INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'ruRU', 'Инновации титанических масштабов', '', '', '', '', '', '', '', '', 26365),
(50374, 'ruRU', 'Кровь Азерот', 'Доставьте сообщение Магни Бронзоборода.', 'Знают ли главы Альянса и Орды, за что они сражаются друг с другом? \n\nИз Раны льется кровь Азерот!\n\nМы должны залечить рану, а не пытаться высосать все до капли! Нельзя позволить Азерот погибнуть!\n\nЯ доверяю тебе, $n, и я знаю, что к тебе прислушиваются в Альянсе. Ты $gдолжен:должна; убедить их остановить это безумие!\n\nЭто единственный шанс для нашего мира...', '', '', '', '', '', '', 26365),
(50373, 'ruRU', 'Внезапный гость', 'Поговорите с верховным магом Кадгаром', 'Ситуация в Силитусе привлекает все больше внимания.\n\nПока тебя не было, прибыл верховный маг Кадгар. Он хочет поговорить с тобой, $n.\n\nПохоже, это срочно – не заставляй его ждать.', '', '', '', '', '', '', 26365),
(50372, 'ruRU', 'Исследования в пустыне', 'Поговорите с Тамми Шестеверткой', 'Темный клинок Саргераса бросил тень разрушения на эту землю.\n\nКазалось, что ульи навсегда уничтожены, но недавно мне доложили, что силитиды возвращаются.\n\nНам нужно выяснить, как они избежали гибели и какой эффект на ульи оказывает меч Саргераса.\n\nКороль хочет, чтобы выживших силитидов поймали, вскрыли и изучили.\n\nОтветственной за исследования назначена Тамми Шестевертка. Ей $gнужен помощник:нужна помощница;.', '', '', '', '', '', '', 26365),
(50371, 'ruRU', 'Вызов в Штормград', 'Встретьтесь с Матиасом Шоу в Штормграде.', 'Король Ринн приказывает тебе вернуться в Штормград как можно скорее. Есть дело, не терпящее отлагательств.\n\nЯ все тебе расскажу по прибытии.\n\nВстретимся в Покое Льва. Поторопись... иначе выпавшая нам возможность может ускользнуть!', '', '', '', '', '', '', 26365),
(50364, 'ruRU', 'Кровь Азерот', 'Доставьте сообщение Магни Бронзоборода.', 'Знают ли главы Альянса и Орды, за что они сражаются друг с другом? \n\nИз Раны льется кровь Азерот!\n\nМы должны залечить рану, а не пытаться высосать все до капли! Нельзя позволить Азерот погибнуть!\n\nЯ доверяю тебе, $n, и я знаю, что к тебе прислушиваются в Орде. Ты $gдолжен:должна; убедить их остановить это безумие!\n\nЭто единственный шанс для нашего мира...', '', '', '', '', '', '', 26365),
(50360, 'ruRU', 'Просьба Кадгара', 'Поговорите с верховным магом Кадгаром.', 'Пока тебя не было, прибыл верховный маг Кадгар. Похоже, он хочет поговорить с тобой, $n.\n\nПоторопись же, узнай, что ему нужно. Я хочу, чтобы он поскорее убрался.\n\nНам точно ни к чему, чтобы человек совал свой нос в дела Орды', '', '', '', '', '', '', 26365),
(50358, 'ruRU', 'Исследования в пустыне', 'Поговорите с Пиксни Ржавобамс.', 'Темный клинок Саргераса бросил тень разрушения на эту землю.\n\nКазалось, что ульи навсегда уничтожены, но недавно мне доложили, что силитиды возвращаются.\n\nБудь моя воля, я бы сжег эти проклятые ульи все до единого! Но это не мне решать.\n\nТемная Госпожа хочет, чтобы оставшихся силитидов поймали, вскрыли и изучили.\n\nОтветственной за исследования назначена Пиксни Ржавобамс. Ей $gнужен помощник:нужна помощница;.', '', '', '', '', '', '', 26365),
(50341, 'ruRU', 'Недавнее открытие', 'Поговорите с Натаносом Гнилостенем.', 'Сегодня лидеры Орды собрались, чтобы отпраздновать нашу победу над Пылающим Легионом.\n\nПосреди пира Галливикс попросил личной аудиенции у Темной Госпожи.\n\nПохоже, он нашел в Силитусе загадочный материал... невиданную ранее магическую руду. \n\nВождь приказала посвятить тебя в детали готовящегося особого задания.', '', '', '', '', '', '', 26365),
(50338, 'ruRU', 'Команда \"Г\"', 'Уничтожьте Липкую муть.', '$n, как ты сюда $gпопал:попала;?\n\nХотя ладно, без разницы. Я Мерд-Док из команды \"Г\", и нам пригодится помощь |3-1($r).\n\nПомоги нам победить зараженных гномов, заполонивших Гномреган, и я награжу тебя званием почетного десантника команды \"Г\".\n\nВот, возьми парашют, спрыгни вниз, прямо в машинный зал, и уничтожь Липкую муть. Когда все будет сделано, разыщи десантника Б. Е. Барекуса в западном коридоре. Я даже не сомневаюсь, что вы с ним подружитесь.', '', '', '', '', '', '', 26365),
(50337, 'ruRU', 'Команда \"Г\"', 'Ликвидируйте \"Электрошокер-6000\".', 'Да Мерд-Док псих, |3-6($c). Никогда, никогда не садись в один летательный аппарат с этим гномом.\n\nДелать все по-тихому – это не в моем духе. Да и не в твоем. Отправляйся по этому коридору к пусковой установке и избавься от \"Электрошокера-6000\".\n\n\"Электрошокер\" заправляет летательными аппаратами...\n\n...а я ненавижу все, что летает. Не полезу я ни в какой летательный аппарат!\n\nВозьми парашют – спрыгнешь с ним вниз, когда все будет сделано. На земле поговори с десантником Фэйсом – он даст тебе новое задание.', '', '', '', '', '', '', 26365),
(50336, 'ruRU', 'Команда \"Г\"', 'Убейте Анжинера Термоштепселя.', 'Я бы на твоем месте держался подальше и от Б. Е. Барекуса, и от Мерд-Дока – у них обоих в голове не пойми что творится.\n\nПора за дело! Спускайся по этому туннелю, пока не окажешься во Дворе механиков. Там найдешь Анжинера Термоштепселя – что с ним делать, надеюсь, объяснять не надо.\n\nГде-то там должен быть и наш командир, десантник Ганни Бал. Он тебя щедро вознаградит за труды.\n\nНаверное...', '', 'Осторожнее, у него бомбы!', 'Анжинер Термоштепсель', '', '', '', 26365),
(50335, 'ruRU', 'Я требую мести', 'Убейте Рендольфа Молоха, лорда Пережара и Дробителя.', 'Мне все равно, на чьей ты стороне, |3-6($r). Я хочу, чтобы Дробитель был мертв!\n\nОн убил моих людей. Он и другие выродки, живущие в этом покинутом Светом месте. Альянс и Орда не слишком-то ладят, но убивать просто так – это чересчур по любым меркам. Я такого не потерплю.', '', '', '', '', '', '', 26365),
(50319, 'ruRU', 'Чужак в чужой стране', 'Найдите доску приказов вождя в Оргриммаре.', 'Здравствуй, я консул Темнослов.\n\n<Посол заглядывает в бумажку, которую держит в руке.>\n\nДля меня честь от имени вождя приветствовать тебя в Оргриммаре, |3-6($r).\n\n<Смотрит на вас, неубедительно улыбается снова заглядывает в бумажку.>\n\nТебя ждет жизнь, полная приключений. Но помни...\n\nЗнаешь, что... Я читаю одно и то же приветствие целый день. Хватит.\n\nНайди посланника своей расы, он или она тебе все расскажет. $gСвободен:Свободна;.', '', '', '', '', '', '', 26365),
(50316, 'ruRU', 'Обнаружен источник помех: Ульдуар', 'Заново примите участие в походе в Ульдуар.', 'Опаздываешь. Помнишь, когда-то нам уже доводилось усмирять Йогг-Сарона, вырвавшегося из темницы под Ульдуаром? Тогда ты тоже $gопоздал:опоздала;. Тебе нужно научиться быть более $gпунктуальным:пунктуальной;.\n\nНу, вперед! Древние боги! Хранители! Творения титанов! И дальше по списку. Разберись со всем этим, чтобы можно было приступить к другим делам в других временах.\n\n<Ворму нетерпеливым жестом отправляет вас прочь.>', '', '', '', '', '', '', 26365),
(50313, 'ruRU', 'Чужак в чужой стране', 'Найдите доску объявлений для героев в Штормграде.', 'Добро пожаловать в Штормград, |3-6($r)! Король Андуин Ринн приглашает тебя осмотреть этот замечательный город. Похоже, ты $gпредан:предана; идеалам веры, а значит тебя наверняка заинтересует наш прекрасный собор.\n\nНезадолго до тебя прибыл посол озаренных, чья задача – рассказать тебе о грядущих приключениях.\n\nНадеюсь, тебе здесь понравится, $n. Заранее прошу прощения за беспорядок: не все местные жители так же дисциплинированны, как солдаты Армии Света.', '', '', '', '', '', '', 26365),
(50305, 'ruRU', 'Чужак в чужой стране', 'Найдите доску объявлений для героев в Штормграде.', 'Добро пожаловать в Штормград, |3-6($r)! Король Андуин Ринн приглашает тебя осмотреть наш замечательный город. Похоже, тебя интересует ночная жизнь. У нас множество таверн и выдающаяся музыкальная сцена.\n\nНо об этом чуть позже. Незадолго до тебя прибыл посланник рен\'дорай, чья задача – рассказать тебе о грядущих приключениях.\n\nНадеюсь, тебе здесь понравится, $n. И дружеский совет: не слишком-то выставляй напоказ свои темные магические способности. Не все в Штормграде мыслят так же прогрессивно, как я!', '', '', '', '', '', '', 26365),
(50303, 'ruRU', 'Чужак в чужой стране', 'Найдите доску приказов вождя в Оргриммаре.', 'Здравствуй, я консул Темнослов.\n\n<Посол заглядывает в бумажку, которую держит в руке.>\n\nМне оказана честь приветствовать тебя от имени вождя в Оргриммаре, |3-6($r).\n\n<Смотрит на вас, неубедительно улыбается и снова заглядывает в бумажку.>\n\nТебя ждет жизнь, полная приключений... уф!\n\nЗнаешь, что... Я читаю одно и то же приветствие дни напролет. Хватит.\n\nНайди, где и кого нужно убить, потом поговори с посланником своей расы.\n\nА теперь оставь меня в покое.', '', '', '', '', '', '', 26365),
(50254, 'ruRU', 'Союзников много не бывает', 'Выберите второго союзника.', 'Один новый союзник – это хорошо. Два сделают нас еще сильнее.\n\nПрямо сейчас Альянс строит козни против нас. Нужно поторапливаться, иначе они нападут, прежде чем мы будем готовы отразить агрессию.\n\nСкажи, когда будешь $gготов:готова; к переговорам со следующим союзником. Медлить нельзя.', '', '', '', '', '', '', 26365),
(50248, 'ruRU', 'Союзников много не бывает', 'Выберите второго союзника.', 'Ты $gсделал:сделала; важнейший первый шаг к укреплению Альянса, $n. Прекрасно.\n\nНо боюсь, что единственный новый союзник не поможет нам сдержать противника. Вождь Орды не успокоится, пока не получит серьезное численное преимущество над Штормградом.\n\nПора начать укреплять отношения со следующим вероятным союзником. Без промедления.', '', '', '', '', '', '', 26365),
(50247, 'ruRU', 'Письма должны поступать!', 'Помогите Почтальону отсортировать по меньшей мере 15 писем за отведенное время.', 'В свете последних событий мне для сортировки писем теперь не хватает почтоменталей.\n\nРабота несложная: берешь письмо и кидаешь его в нужный портал. Так они попадают в самые дальние уголки Азерота и в другие миры.\n\nПредставь, что это игра. Сколько писем ты сможешь забросить за минуту?', '', '', '', '', '', '', 26365),
(50242, 'ruRU', 'Выбор союзников', 'Поговорите с Бейном и Лиадрин, чтобы узнать больше о возможных союзниках.', 'Бейн Кровавое Копыто и Леди Лиадрин выступили с предложениями о возможных союзниках, чтобы укрепить наши ряды. Каждый из них знает, что нужно для того, чтобы Орда оставалась сильной.\n\nНа тебя всегда было можно положиться, $n. И я доверяю тебе решить, с кем искать союза в первую очередь.\n\nВыслушай Бейна и Лиадрин и сделай свой выбор. Сообщи мне о своем решении, и мы приступим к воплощению плана по усилению Орды.', '', '', '', '', '', '', 26365),
(50239, 'ruRU', 'Выбор союзников', 'Поговорите с Туралионом и Аллерией, чтобы узнать больше о возможных союзниках.', 'Верховный экзарх Туралион и Аллерия Ветрокрылая выступили с предложениями о возможных союзах. Эти герои посвятили себя защите этого мира – я высоко ценю их мнение.\n\nТы всегда верно $gслужил:служила; Альянсу, $n. Я доверяю тебе решение о том, с кем искать союза в первую очередь.\n\nВыслушай Туралиона и Аллерию, сделай выбор и сообщи мне.', '', '', '', '', '', '', 26365),
(50232, 'ruRU', 'Выживший в сумерках', 'Добудьте голову Мишиэля Скитальца Бездны.', 'Когда-то в Силитусе господствовал культ Сумеречного Молота.\n\nКульт считался полностью уничтоженным, но темные силы всегда выживают, скрывшись в тени.\n\nКогда Саргерас разрушил Силитус, уцелевших приверженцев Сумеречного Молота и их лидера, Мишиэля Скитальца Бездны, это только раззадорило.\n\nМы не можем допустить, чтобы этот безумец собрал остатки культа под своим началом. Принеси мне его голову!', '', '', '', '', '', '', 26365),
(50231, 'ruRU', 'За пригоршню личинок', 'Соберите $1oa |4личинку:личинки:личинок; силитидов.', 'Как силитиды пережили удар мечом Саргераса?!\n\nРаньше мне никогда не встречались виды, которые бы так быстро адаптировались!\n\nДолжен быть способ приручить этих крошек! Использовать их механизмы эволюции в наших целях!\n\nМы начнем дрессировать их сызмала. Старые силитиды нам точно ни к чему, а вот личинки очень даже пригодятся.\n\nПринеси столько личинок, сколько сможешь, а я найду им применение.', '', '', '', '', '', '', 26365),
(50230, 'ruRU', 'Источник силы', 'Добудьте $1oa |4мозг силитида:мозга силитидов:мозгов силитидов;.', 'Силитиды вернулись, став сильнее, чем когда-либо!\n\nЯ думаю, что они черпают силу из некоего подземного источника. Возможно, рядом с мечом Саргераса.\n\nМне приказано изучить влияние этой таинственной силы на ульи.\n\nПринеси побольше неповрежденных мозгов силитидов, и я тут же приступлю к исследованиям!', '', '', '', '', '', '', 26365),
(50229, 'ruRU', 'Чуток ткани', 'Соберите $1oa |4кусок:куска:кусков; сумеречного шелка.', '$n, это ты там шуршишь? Это я, Ангус Грозовой Отвар!\n\nПовсюду сектанты, поэтому говорить будем шепотом. Наклонись поближе и зажми нос!\n\nЭто единственное отхожее место во всем Силитусе! Поверь, я искал его почти всю ночь. Хотел чуток облегчить душу, пока сектанты не проснулись, но, м-м, кое-чего не учел...\n\nТут нечем... замести следы... если ты понимаешь, о чем я.\n\nТы не $gмог:могла; бы раздобыть чуток ткани и просунуть его под дверь?', '', '', '', '', '', '', 26365),
(50228, 'ruRU', 'Выживший в сумерках', 'Добудьте голову Мишиэля Скитальца Бездны.', 'Когда-то в Силитусе господствовал культ Сумеречного Молота.\n\nКульт считался полностью уничтоженным, но некоторые его последователи выжили и ничуть не растеряли своей фанатичности.\n\nКогда Саргерас разрушил Силитус, уцелевших приверженцев Сумеречного Молота и их лидера, Мишиэля Скитальца Бездны, это только раззадорило.\n\nМы не можем допустить, чтобы Мишиэль собрал остатки культа под своим началом. Его нужно остановить!', '', '', '', '', '', '', 26365),
(50227, 'ruRU', 'Сбор личинок', 'Соберите $1oa |4личинку:личинки:личинок; силитидов.', 'Как силитиды пережили удар мечом Саргераса? Я в недоумении.\n\nРаньше мне никогда не встречались виды, которые бы так быстро адаптировались к меняющимся условиям!\n\nИх личинки могут мутировать в одно из множества существ в зависимости от потребностей улья. \n\nПолучает ли личинка сигнал от разума улья или развивается в зависимости от факторов среды?\n\nНаука даст нам ответ!', '', '', '', '', '', '', 26365),
(50226, 'ruRU', 'Источник силы', 'Добудьте $1oa |4мозг силитида:мозга силитидов:мозгов силитидов;.', 'После разрушения Силитуса силитиды на какое-то время полностью исчезли, но недавно они снова вышли на поверхность.\n\nБолее того, теперь они стали сильнее!\n\nМоя гипотеза в том, что они черпают силу из некоего источника, находящегося под мечом Саргераса.\n\nМне приказано изучить влияние этой таинственной силы на ульи.\n\nПринеси как можно больше неповрежденных мозгов силитидов, и я тут же приступлю к исследованиям!', '', '', '', '', '', '', 26365),
(50131, 'ruRU', 'Сторонняя точка зрения', 'Добейтесь нейтрального отношения со стороны Спореггара.', 'Спасибо за все, что ты $gсделал:сделала; для моих сородичей. Увы, они по-прежнему боятся чужестранцев.\n\nОдолей болотников и собери мешочки со спорами на Грибной поляне. На кону будущее нашего племени!', '', '', '', '', '', '', 26365),
(50130, 'ruRU', 'Знак дружбы', 'Добейтесь того, чтобы в Спореггаре к вам относились с дружелюбием.', 'Жизнь в топях непроста, $n. Мы хотим жить в мире с теми, кто называет это место домом, но у нас есть враги, несущие смертельную угрозу.\n\nПомоги народу Спореггара выжить.', '', '', '', '', '', '', 26365),
(50071, 'ruRU', 'Во имя Света!', 'Вернитесь в Штормград и поговорите с королем Андуином Ринном.', 'Озаренные дренеи с радостью присоединятся к Альянсу.\n\nУ нас теперь одна цель, $n.\n\nВернемся в Штормград. А капитан Фария поговорит с нашим народом.', '', '', '', '', '', '', 26365),
(50055, 'ruRU', 'Слово Вестнику', 'Доставьте письмо Кадгара Магни Бронзобороду в Силитус.', 'Саргерас оставил глубокий шрам в сердце нашего мира, $n. Мы нескоро о нем забудем...\n\n...как и о шрамах от былых войн между Ордой и Альянсом.\n\nСлишком много жизней мы потеряли, сражаясь за спасение Азерота. Я не собираюсь просто сидеть и ждать новых смертей.\n\nОтнеси это письмо Магни Бронзобороду. Он сейчас в Силитусе, пытается найти способ заживить Рану.\n\nВ этом письме сведения, которые могут ему пригодиться.', '', '', '', '', '', '', 26365),
(50054, 'ruRU', 'Бурлящий берег', 'Примите участие в сражении на Бурлящем берегу.', 'До нас дошли донесения о находящемся недалеко от Силитуса острове, богатом на залежи азерита.\n\nОчень важно занять этот остров, прежде чем Альянс узнает о его существовании.\n\nНатанос Гнилостень лично возглавляет экспедицию и приказывает тебе явиться к нему. Cтупай с честью!', '', '', '', '', '', '', 26365),
(50053, 'ruRU', 'Ленивые геологи!', 'Мотивируйте $1oa |4ленивого геолога:ленивых геологов:ленивых геологов.', 'Есть одна истина: гоблинам работу орков доверять нельзя.\n\nБыть может, Галливикс с его картелем и служит вождю, но с ними нужно держать ухо востро.\n\nНекоторые гоблины уже были замечены бездельничающими и даже спящими на рабочем месте. Орде не нужны лентяи!\n\nПреподай урок этим тунеядцам-геологам!', '', '', '', '', '', '', 26365),
(50052, 'ruRU', 'Шпионам вход воспрещен', 'Убейте $1oa |4сотрудника:сотрудников:сотрудников; ШРУ в Силитусе.', 'Привет, $n! Хорошо, что ты здесь.\n\nТут такое происходит, что и в бреду не привидится! Жуки-убийцы, сверкающие камни, здоровенный МЕЧ из земли торчит... плохие у меня предчувствия, скажу тебе!\n\nКо всему прочему, мне нашептали, что в лагере замечены шпионы Альянса\n\nУ Темной Госпожи есть планы на эту таинственную руду, и, знаешь, Галливикс не хочет ее разочаровывать.\n\nОтправляйся в шахтерский лагерь и убей этих негодных шпионов ШРУ, пока они не успели все испоганить!', '', '', '', '', '', '', 26365),
(50049, 'ruRU', 'Слово Вестнику', 'Доставьте письмо Кадгара Магни Бронзобороду в Силитус.', 'Саргерас оставил глубокий шрам в сердце нашего мира, $n. Мы нескоро о нем забудем...\n\n...как и о шрамах от былых войн между Ордой и Альянсом.\n\nСлишком много жизней мы потеряли, сражаясь за спасение Азерота. Я не собираюсь просто сидеть и ждать новых смертей.\n\nОтнеси это письмо Магни Бронзобороду. Он сейчас в Силитусе, пытается найти способ заживить Рану.\n\nВ этом письме сведения, которые могут ему пригодиться.', '', '', '', '', '', '', 26365),
(50048, 'ruRU', 'Бурлящий берег', 'Примите участие в сражении на Бурлящем берегу.', 'До нас дошли донесения о находящемся недалеко от Силитуса острове, богатом на залежи азерита.\n\nОчень важно занять этот остров, прежде чем Орда узнает о его существовании.\n\nМастер Матиас Шоу лично возглавляет экспедицию и приказывает тебе явиться к нему. Удачи!', '', '', '', '', '', '', 26365),
(50047, 'ruRU', 'Бесплатные образцы', 'Получите $1oa |4образец:образца:образцов; таинственной руды.', 'Корона приказала ШРУ добыть любую информацию о новом таинственном веществе.\n\nМои агенты под прикрытием проникли в гоблинский лагерь, но времени почти не осталось, нужно получить образцы руды как можно скорее.\n\nИными словами, я ищу того, кто предпочитает более прямолинейный подход: вступить в бой с превосходящим по силе противником и вернуться живым.\n\nКогда вернешься, отнеси образцы руды на исследование Харгу Каменной Мантии.', '', '', '', '', '', '', 26365),
(50046, 'ruRU', 'Это саботаж!', 'Заложите $1oa |4связку:связки:связок; динамита.', 'Орда не теряла времени, бессовестно выскребая эту таинственную руду прямо из великой раны.\n\nК счастью для нас, достижения гоблинской инженерии не сравнятся с тем, что можно сделать в Гномрегане! К примеру, чтобы полностью вывести из строя гоблинский крошшер, достаточно немного старой доброй взрывчатки.\n\nВозьми эти связки динамита и прикрепи к неактивным крошшерам в шахтерском лагере.\n\nЖадным гоблинам потребуются недели, чтобы вернуть их в рабочее состояние!', '', '', '', '', '', '', 26365),
