INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'zhCN', '泰坦革新', '', '', '', '', '', '', '', '', 26365),
(50374, 'zhCN', '艾泽拉斯之血', '传达麦格尼·铜须的信息。', '联盟和部落的领袖到底知不知道他们争夺的是什么东西？\n\n这一从伤口溢出的物质正是艾泽拉斯的生命之血！\n\n我们得治愈伤口，而不是开采其中的能量！艾泽拉斯是我们唯一的家园，我们不能眼睁睁看着她死去！\n\n我相信你，$n，也知道你的言辞在联盟中举足轻重。你必须把缘由告诉他们，阻止这种疯狂的行为！\n\n这是我们的世界的唯一希望……', '', '', '', '', '', '', 26365),
(50373, 'zhCN', '最近的访客', '与大法师卡德加谈一谈。', '希利苏斯的情况似乎引起了更多的注意。\n\n大法师卡德加在你离开的时候刚到。他似乎是来找你的，$n。\n\n他看上去挺急的。要是我的话我会尽快去和他谈谈。', '', '', '', '', '', '', 26365),
(50372, 'zhCN', '沙漠研究', '与塔米·旋匠谈一谈。', '萨格拉斯的黑暗之刃在这片土地上洒下了一片毁灭的阴影。\n\n虽然异种蝎虫巢似乎在攻击下被摧毁，然而我却接到报告说异种蝎又回来了。\n\n我们得弄清这些异种蝎是如何幸免于难，萨格拉斯之刃又是如何影响它们的虫巢的。\n\n国王希望解剖并研究活体异种蝎。\n\n塔米·旋匠正在进行这项研究，她正需要一名“采集者“。\n\n\n', '', '', '', '', '', '', 26365),
(50371, 'zhCN', '暴风城的召唤', '前往暴风城，与马迪亚斯·肖尔见面。', '乌瑞恩国王要求你尽快赶回暴风城。有件紧急事务需要你处理。\n\n你回来后我会马上告知你详情。\n\n来雄狮之眠找我。要快……时机稍纵即逝！', '', '', '', '', '', '', 26365),
(50364, 'zhCN', '艾泽拉斯之血', '传达麦格尼·铜须的信息。', '联盟和部落的领袖到底知不知道他们争夺的是什么东西？\n\n这一从伤口溢出的物质正是艾泽拉斯的生命之血！\n\n我们得治愈伤口，而不是开采其中的能量！艾泽拉斯是我们唯一的家园，我们不能眼睁睁看着她死去！\n\n我相信你，$n，也知道你的言辞在部落中举足轻重。你必须把缘由告诉他们，阻止这种疯狂的行为！\n\n这是我们的世界的唯一希望……', '', '', '', '', '', '', 26365),
(50360, 'zhCN', '卡德加的请求', '与大法师卡德加谈一谈。', '你不在的时候大法师卡德加来了。他似乎是来找你的，$n。\n\n赶紧去见他，看看他到底想做什么。我要他马上走，越快越好。\n\n我最不想看到的，就是人类来打探部落事务！', '', '', '', '', '', '', 26365),
(50358, 'zhCN', '沙漠研究', '与皮克妮·锈弹谈一谈。', '萨格拉斯的黑暗之刃在这片土地上洒下了一片毁灭的阴影。\n\n虽然异种蝎虫巢似乎在攻击下被摧毁，然而我却接到报告说异种蝎又回来了。\n\n要是我的话，我宁愿一劳永逸地烧光这些讨厌的虫巢！然而，我说的不算。\n\n黑暗女王希望解剖并研究活体异种蝎。\n\n皮克妮·锈弹正在进行这项研究，她正需要一名“采集者“。\n\n\n', '', '', '', '', '', '', 26365),
(50341, 'zhCN', '近期发现', '与纳萨诺斯·凋零者谈一谈。', '今天早些时候，部落的领袖们齐聚一堂，欢庆我们在对抗燃烧军团战争中的胜利。\n\n宴席进行到一半，加里维克斯要求和黑暗女王单独见面。\n\n他似乎在希利苏斯找到了一种神秘物质……一种我们之前从未遇到过的魔法矿物。\n\n酋长命令我告知你细节，为一项特殊安排做好准备。', '', '', '', '', '', '', 26365),
(50338, 'zhCN', '地龙特攻队', '杀死粘性辐射尘。', '你怎么跑到这里来的，$n？\n\n我是地龙特攻队的莫多克。我们需要像你这样的$r的帮助。\n\n帮我们打败那些被辐射的居民，他们现在已经占据了诺莫瑞根。完成后，我就能帮你任命为荣耀的地龙特攻队特种兵！\n\n拿着这包降落伞，直接跳到齿轮大厅里去，干掉粘性辐射尘。完成这个任务之后，到西边的走廊去找特种兵“坏脾气”巴拉克斯报告。我敢说，你们两个肯定很合得来。', '', '', '', '', '', '', 26365),
(50337, 'zhCN', '地龙特攻队', '杀死电刑器6000型。', '那个莫多克是个疯子，$c。绝对绝对不要跟这个疯侏儒乘坐同一架飞机。\n\n鬼鬼祟祟的做法不是我擅长的风格，显然也不该是你的。沿着这条走廊前往发射台，除掉电刑器6000型。\n\n电刑器是负责管理飞机的……\n\n……而我讨厌飞机。我绝对不会乘坐任何一种飞机！\n\n解决它之后，就用这包降落伞跳下去，找特种兵费斯接你的下一个任务。', '', '', '', '', '', '', 26365),
(50336, 'zhCN', '地龙特攻队', '消灭机械师瑟玛普拉格。', '我要跟“坏脾气”巴拉克斯和莫多克保持距离，他俩都出奇地不靠谱。\n\n那就派你去干活吧！顺着这条隧道往下走，一直到工匠议会。希望你知道如何对付机械师瑟玛普拉格。\n\n我们的领队，特种兵汉尼巴尔就在那里的某处等着你。他会重重奖赏你的出色表现。\n\n应该是这样吧……', '', '当心他的炸弹！', '机械师瑟玛普拉格', '', '', '', 26365),
(50335, 'zhCN', '为我复仇', '杀死兰多菲·摩洛克，灼热勋爵以及霍格。', '我不管你效忠谁，$r。我要霍格死！\n\n他——以及他那些被圣光遗忘的同伙——杀了我的人。联盟与部落可能是看不对眼，但你必须明白，我们不能容忍冷血的谋杀。', '', '', '', '', '', '', 26365),
(50319, 'zhCN', '异乡异客', '在奥格瑞玛找到酋长的命令布告板。', '嗨。我是黑卫大使。\n\n<黑卫低头看着手上的字条。>\n\n我很荣幸能代表大酋长欢迎你来到奥格瑞玛，$r。\n\n<他看了你一眼，勉强扯出一个微笑，目光又回到了纸上。>\n\n有无尽的冒险在前方等着你，请确保……呃……\n\n你知道吗？我在这里读这份欢迎信读了一整天了。受够了。\n\n去和你的使者谈谈，想想该去哪冒险。走吧。', '', '', '', '', '', '', 26365),
(50316, 'zhCN', '干扰检测：奥杜尔', '重温奥杜尔的探险。', '你来晚了。不记得上次我们怎么处理逃出奥杜尔地下监狱的尤格-萨隆了吗？那次你也迟到了。你真的应该学会守时。\n\n好了，快去！上古之神！守护者！泰坦造物！赶紧解决这件事情我们才能走。还有更多的时间和地点要去呢。\n\n<沃尔姆做了个不耐烦的手势，要你离开。>', '', '', '', '', '', '', 26365),
(50313, 'zhCN', '异乡异客', '找到暴风城里的英雄的召唤布告牌。', '欢迎来到暴风城，$r。安度因·乌瑞恩国王诚挚欢迎你，请尽情游览我们这座伟大的城市。你看上去像是信仰虔诚的人。这里有座大教堂，你一定会感兴趣的！\n\n啊，那个其实不急。有位光铸德莱尼大使正在等你，他会告诉你城市之外有什么样的冒险在等待你。\n\n希望你在这里过得愉快，$n。噢，这里偶尔会有地方比较脏乱，请不要介意！这里的市民可比不上以严于律己闻名的圣光军团战士！', '', '', '', '', '', '', 26365),
(50305, 'zhCN', '异乡异客', '找到暴风城里的英雄的召唤布告牌。', '欢迎来到暴风城，$r。安度因·乌瑞恩国王诚挚欢迎你，请尽情游览我们这座伟大的城市。你看上去像是喜欢夜生活的那类人。这里有许多流行的酒馆，还有美妙的音乐表演！\n\n啊，那些都不急。有位朗多雷的大使正在等你，他会告诉你城市之外有什么样的冒险在等待你。\n\n希望你在这里过得愉快，$n。噢，还有个友情提醒……还请控制好你的那些暗影能量。在暴风城不是所有人都像我一样开明！', '', '', '', '', '', '', 26365),
(50303, 'zhCN', '异乡异客', '在奥格瑞玛找到酋长的命令布告板。', '嗨。我是黑卫大使。\n\n<黑卫低头看着手上的字条。>\n\n我很荣幸能代表大酋长欢迎你来到奥格瑞玛，$r。\n\n<他看了你一眼，勉强扯出一个微笑，目光又回到了纸上。>\n\n有无尽的冒险在前方等着……呃！\n\n够了！我受够整天在这里读这份欢迎信了。\n\n自己想想该去哪冒险，再和你的大使谈谈。\n\n让我一个人静一静。', '', '', '', '', '', '', 26365),
(50254, 'zhCN', '伟大事业的第二个盟友', '选择另一个要寻找的盟友。', '一个盟友对我们很有用。而两个盟友会让我们实力大增。\n\n即使现在，联盟还在密谋对抗我们。我们必须赶快行动，不然就无法再他们袭击前做好准备，抵御侵略。\n\n你做好准备就告诉我，开始追寻我们下一个盟友。事不宜迟。', '', '', '', '', '', '', 26365),
(50248, 'zhCN', '伟大事业的第二个盟友', '选择另一个要寻找的盟友。', '你迈出了重要的一步，增强了联盟的实力，$n。干得好。\n\n但恐怕只增加一个盟友并不能威慑部落。在奥格瑞玛的军力远超暴风城前大酋长是不会罢休的。\n\n我必须请你找到我们下一个盟友。事不宜迟。', '', '', '', '', '', '', 26365),
(50247, 'zhCN', '邮件必达', '在时间限制内帮助邮政长整理至少15封信件。', '由于最近那些闹剧，用来整理邮件的邮件元素短缺。\n\n其实这是一项简单的工作——就是把不同的邮件扔进不同的传送门，送到艾泽拉斯的各个角落。\n\n来玩个游戏吧。看你一分钟内能整理多少邮件呢？', '', '', '', '', '', '', 26365),
(50242, 'zhCN', '选择盟友', '与贝恩和莉亚德琳交谈，了解潜在的盟友。', '贝恩·血蹄与女伯爵莉亚德琳提议让潜在的盟友加入我们。他们知道如何让部落保持强大。\n\n你已经证明了自己是坚定的勇士，$n。我相信该由你决定我们先去做哪件事。\n\n听听他们说的，做出你的决定。把你的选择告诉我，我们将实施计划，增强部落的实力。\n', '', '', '', '', '', '', 26365),
(50239, 'zhCN', '选择盟友', '与图拉杨和奥蕾莉亚交谈，了解潜在的盟友。', '大主教图拉杨与奥蕾莉亚·风行者提议让潜在的盟友加入我们。这些英雄奉献了自己的毕生保卫我们的世界，我很相信他们的意见。\n\n你是联盟坚定的勇士，$n。我相信该由你决定我们先去做哪件事。\n\n听听他们说的，做出你的决定。让我知道我们该先与哪个盟友结盟。\n', '', '', '', '', '', '', 26365),
(50232, 'zhCN', '暮光幸存者', '取得米希尔·虚无行者的徽记。', '暮光之锤教徒曾经遍布这块土地。\n\n我们本以为这一教派已被彻底铲除，但黑暗总能在阴影中苟活。\n\n虽然这里的大多数教徒在萨格拉斯进攻时已经被消灭，但仍有一些幸存者。他们的领袖米希尔·虚无行者在希利苏斯被毁后愈发肆无忌惮。\n\n我们不能任由这个疯子集结那些幸存者。把他的徽记给我带来！', '', '', '', '', '', '', 26365),
(50231, 'zhCN', '成打的幼虫', '收集$1oa个异种幼蝎。', '异种蝎是如何在萨格拉斯之刃下幸存的？！\n\n我从没见过有物种适应环境的速度有异种蝎这么快！\n\n一定有办法驯服这些虫子！利用它们的进化生物学知识来赚钱！\n\n我们得从小训练它们！别管那些暴躁的成虫，我们需要幼虫！\n\n尽可能多地给我偷点异种蝎幼虫，我会想办法训练它们！', '', '', '', '', '', '', 26365),
(50230, 'zhCN', '力量之源', '收集$1oa个异种蝎大脑。', '异种蝎来势汹汹，而且从未如此强大！\n\n我猜异种蝎正汲取地底的某种能量。也许就在萨格拉斯之刃附近！\n\n我奉命研究这一影响异种蝎的神秘能量。\n\n带给我尽可能多的完整的异种蝎大脑，我就能开始研究了！\n\n', '', '', '', '', '', '', 26365),
(50229, 'zhCN', '一小块布', '收集$1oa块暮光丝绸。', '$n，是你在外面嚷嚷吗？是我，安古斯·雷酒！\n\n这里到处都是异教徒，请小声点。靠近我，讲话的时候挡住鼻子！\n\n这是希利苏斯唯一的厕所！我花了一整晚才找到的。我打算乘异教徒们睡着时溜进去方便一下，不过有个小问题……\n\n这里没有东西擦……你懂的……\n\n你能不能去找点布头，悄悄带进来给我！', '', '', '', '', '', '', 26365),
(50228, 'zhCN', '暮光幸存者', '取得米希尔·虚无行者的徽记。', '暮光之锤教徒曾经遍布希利苏斯。\n\n我们本以为这一教派已被彻底铲除，然而他们总能在阴影中苟活。即使成员数量锐减，但他们的狂热程度不亚于当年。\n\n虽然这里的大多数教徒在萨格拉斯进攻时已经被消灭，但仍有一些幸存者。他们的领袖米希尔·虚无行者在希利苏斯被毁后愈发肆无忌惮。\n\n我们不能任由米希尔集结那些幸存者。必须阻止他！', '', '', '', '', '', '', 26365),
(50227, 'zhCN', '成打的幼虫', '收集$1oa个异种幼蝎。', '异种蝎是如何在萨格拉斯之刃下幸存的？我不明白！\n\n我从没见过哪个物种适应环境的速度有异种蝎这么快！\n\n它们的幼虫，或者说幼蝎可以根据虫巢需要，进化成不同的种类。\n\n到底是虫巢发出信号影响了幼虫进化，还是有什么环境因素在作祟？\n\n科学必须找到答案！', '', '', '', '', '', '', 26365),
(50226, 'zhCN', '力量之源', '收集$1oa个异种蝎大脑。', '尽管异种蝎在希利苏斯毁灭后立即消失了一段时间，但他们最近又从地下回来了。\n\n这些新型异种蝎似乎比以往更加强大！\n\n我的假设是，这些异种蝎正在从萨格拉斯之刃汲取某种力量。\n\n我奉命研究这种神秘力量对于异种蝎虫巢的影响。\n\n带给我尽可能多的完整的异种蝎大脑，我就能开始研究了！', '', '', '', '', '', '', 26365),
(50131, 'zhCN', '外来视角', '在孢子村达到中立声望。', '感谢你为我的人民所做的一切，但他们还是很怕外来者。\n\n请打败沼泽领主，救出孢殖林里的孢子囊。这关系到我们的未来！', '', '', '', '', '', '', 26365),
(50130, 'zhCN', '友善的表示', '在孢子村达到友善声望。', '沼泽生活很艰难，$n。我们希望与这里的其他居民和平共处，但这里的敌人一直威胁着我们的生存。\n\n请为我们出一份力，让孢子村繁荣起来。', '', '', '', '', '', '', 26365),
(50071, 'zhCN', '为圣光而战！', '回到暴风城，与安度因·乌瑞恩国王谈一谈。', '圣光军团很乐意加入联盟。\n\n现在，我们拥有共同的事业了，$n。\n\n我们回暴风城吧，法瑞娅队长会对我们的人讲话。', '', '', '', '', '', '', 26365),
(50055, 'zhCN', '代言人的观点', '把卡德加的信交给希利苏斯的麦格尼·铜须。', '萨格拉斯在我们的世界上留下了一道伤疤，$n。这道伤疤绝不会马上愈合……\n\n联盟和部落之间的伤疤也一样。\n\n为了拯救艾泽拉斯免遭毁灭，已经有太多生命在战斗中牺牲。我绝不能坐看这个世界再次陷落。\n\n我走之前有个请求。请把这封信交给麦格尼·铜须。他在希利苏斯寻找治疗伤口的方法。\n\n这封信里的信息对他或许会很有用。', '', '', '', '', '', '', 26365),
(50054, 'zhCN', '涌泉海滩', '完成涌泉海滩战场。', '我们收到报告说，希利苏斯近海有座岛，上面有丰富的艾泽里特矿藏。\n\n我们必须赶在联盟得知这座岛的存在前夺下它。\n\n纳萨诺斯·凋零者会亲自带领远征队，他还要求你前去报到。祝你好运！', '', '', '', '', '', '', 26365),
(50053, 'zhCN', '懒惰的勘探者！', '动员$1oa名懒惰的勘探者。', '从我的经验来看，永远不要把兽人的工作托付给地精。\n\n加里维克斯和他的财团也许目前是在为大酋长效命，但要成事的话我们也得对他们多一个心眼。\n\n我已经看到许多地精工人游手好闲，工作时偷偷打瞌睡。部落绝不容忍懒惰！\n\n快去，让那些懒惰的勘探者回去干活！', '', '', '', '', '', '', 26365),
(50052, 'zhCN', '反谍报', '在希利苏斯消灭$1oa个军情七处特工。', '嘿，$n，很高兴你来了。\n\n我已经焦头烂额了！会杀人的虫子，陌生的矿物……插在地上的巨型利剑……我对这里有种不好的感觉！\n\n更糟的是，我接到报告，说有联盟间谍在营地周围打探！\n\n孩子，黑暗女王对这种神秘矿物有很多打算，而我不想让她失望，你明白我的意思吗？\n\n到采掘营地去，把那些军情七处间谍干掉，别让他们把整个行动都毁了！', '', '', '', '', '', '', 26365),
(50049, 'zhCN', '代言人的观点', '把卡德加的信交给希利苏斯的麦格尼·铜须。', '萨格拉斯在我们的世界上留下了一道伤疤，$n。这道伤疤绝不会马上愈合……\n\n联盟和部落之间的伤疤也一样。\n\n为了拯救艾泽拉斯免遭毁灭，已经有太多生命在战斗中牺牲。我绝不能坐看这个世界再次陷落。\n\n我走之前有个请求。请把这封信交给麦格尼·铜须。他在希利苏斯寻找治疗伤口的方法。\n\n这封信里的信息对他或许会很有用。', '', '', '', '', '', '', 26365),
(50048, 'zhCN', '涌泉海滩', '完成涌泉海滩战场。', '我们收到报告说，希利苏斯近海有座岛，上面有丰富的艾泽里特矿藏。\n\n我们必须赶在部落得知这座岛的存在前夺下它。\n\n马迪亚斯·肖尔大师会亲自带领远征队，他还要求你前去报到。祝你好运！', '', '', '', '', '', '', 26365),
(50047, 'zhCN', '免费样品', '收集$1oa个神秘矿石样本。', '王室命令军情七处渗透进地精采掘工程并收集情报，让我们了解这种崭新神秘物质。\n\n我的特工已经渗透了营地，但时间有限，我们必须快点拿到一些提取出来的矿物的样品。\n\n也就是说，我需要找人进行直接行动。那个人要能够在处于极端劣势的战斗中幸存下来。\n\n你回来后，把矿物样本带给卡古斯·石衣进行分析。', '', '', '', '', '', '', 26365),
(50046, 'zhCN', '有人搞破坏', '放置$1oa捆炸药。', '部落一直在争分夺秒地从巨大的伤口中抽取这种神秘物质，真是不知羞耻。\n\n幸好，地精工程学远远没有我们在诺莫瑞根做的东西复杂。只需要一点传统的炸药，就能彻底摧毁一台地精切割机。\n\n多单纯的机器啊！\n\n拿上这些炸药包，放在矿工营地周围还没启动的切割机上。\n\n那些贪婪的地精得花好几周才能找到备用机器重新开工！', '', '', '', '', '', '', 26365),
(50035, 'zhCN', 'Bret Test - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'zhCN', '见证伤痕', '前往希利苏斯与格洛尔·战刃交谈。', '锈水财阀已经开始在希利苏斯的挖掘作业，提取这一神秘物质。\n\n大酋长坚持让你亲自监督在希利苏斯的萃取工程，并提防联盟的阻挠。\n\n加里维克斯对你的认命不是很满意，不过没人敢拒绝希尔瓦娜斯……\n\n你必须立刻前往希利苏斯。基地指挥官正在那里等你。', '', '', '', '', '', '', 26365),
(49981, 'zhCN', '见证伤痕', '去希利苏斯与军情七处指挥官会面。', '我已经派遣特工前往希利苏斯建立营地。\n\n他们受命监视部落的行动，并收集一切关于这一神秘物质的信息。\n\n我的一位军情七处指挥官正在那里等你。快去吧。', '', '', '', '', '', '', 26365),
(49977, 'zhCN', '奥格瑞玛的召唤', '与纳萨诺斯·凋零者谈一谈。', '大酋长要你尽快回到奥格瑞玛。有一件紧急事务需要你处理。\n\n等你到达后我会详细告诉你。\n\n我就在格罗玛什要塞的入口附近。黑暗女王有任务给你，$n。我可不会让她等太久……', '', '', '', '', '', '', 26365),
(49976, 'zhCN', '牺牲者的馈赠', '跟马迪亚斯·肖尔谈一谈。', '今天早些时候，乌瑞恩国王在雄狮之眠向暴风城的市民发表演说，宣布战争告一段落。\n\n在人群离去后，我与国王及他的顾问进行了一次单独会面，商议在希利苏斯找到的一种神秘物质。\n\n这一发光的矿石似乎有着强大的力量，部落正争分夺秒地从巨大的伤口处抽取这一物质。\n\n国王命令我将细节告诉你，为一项重要的任务做好准备。', '', '', '', '', '', '', 26365),
(49973, 'zhCN', '塔莉萨的庄园', '在苏拉玛首席奥术师的庄园与女伯爵莉亚德琳见面。', '摄政王塞隆邀请夜之子在银月城会面。\n\n首先，我们要对他们发出邀请。而这种事情我喜欢亲自来做。\n\n请跟我一起前往苏拉玛，与塔莉萨女士见面。', '', '', '', '', '', '', 26365),
(49933, 'zhCN', '为了部落', '去奥格瑞玛大使馆与黑卫大使见面。', '经过多年的离群索居，我们再一次成为了某个重要事业的一部分。与部落的结盟为我们带来了一个崭新的开始，以及一个独一无二的机会，我们决不能浪费。\n\n大酋长的大使在奥格瑞玛等你，$n。你可以从那里开始在艾泽拉斯的旅程。守卫这片土地，为夏多雷争得一席之地。\n\n让世界看看我们是谁，$c。为部落带去荣耀，也就是为苏拉玛带来荣耀。', '', '', '', '', '', '去奥格瑞玛大使馆与黑卫大使见面。', 26365),
(49930, 'zhCN', '呼唤同盟', '去奥格瑞玛大使馆与大酋长希尔瓦娜斯·风行者见面。', '部落领导军团之战时太过无私，使得军力下降。我们的敌人妄想趁机夺取我们的应得之物。不能让他们得逞。\n\n我下令在奥格瑞玛建好了大使馆，争取让其他人加入我们的事业。去那儿见我。', '', '', '', '', '', '', 26365),
(49929, 'zhCN', '呼唤同盟', '去暴风城大使馆与乌瑞恩国王见面。', '部落会不惜一切夺取艾泽拉斯的生命之血。我担心，军团之战后，我们的军力已经空虚，没法阻止他们的侵犯。\n\n时机到了，应该有新血加入联盟的事业了。我们在暴风城建了座大使馆来指导相关事务。到那儿见。\n', '', '', '', '', '', '', 26365),
(49928, 'zhCN', '虚空的传承', '前往泰洛古斯裂隙，与奥蕾莉亚·风行者见面。', '经过在艾泽拉斯的冒险之旅， 这股力量虽然总想控制你，但终于为你所用。\n\n你已经成为了联盟的强大武器，$p。回到虚空裂隙，让我们来嘉奖你的成就吧。', '', '', '', '', '', '', 26365),
(49866, 'zhCN', '前往潘达利亚！', '去暴风城找利尔·夜风。', '请立刻前往暴风要塞会见瓦里安·乌瑞恩国王。$b$b我们得到消息说，南海发生了一场冲突，我们需要一位老练的$c来协助调查。', '', '', '', '', '', '', 26365),
(49865, 'zhCN', '大灾变急救员', '向暴风城的地语者纳拉特报到。', '死亡之翼，死亡的化身巨龙，派遣了爪牙想要清除艾泽拉斯的生物。$B$B地语者纳拉特可以告诉你更多海加尔山、瓦丝琪尔以及其他死亡之翼袭击的地方的情报。', '', '', '', '', '', '', 26365),
(49864, 'zhCN', '两线作战', '使用特别任务指派书选择你的下一个冒险目的地。', '$n！你再一次受到了召唤，协助联盟完成重要任务。\n\n你愿意协助联盟对抗死亡之翼和暮光之锤吗？\n\n还是想飞往南方的潘达利亚，抢在部落前头夺取那里的资源？\n\n做出你的选择，然后投入战斗吧！', '', '', '', '', '', '', 26365),
(49863, 'zhCN', '前往诺森德！', '乘船前往诺森德。$b$b你可以从暴风城港口登船前往北风苔原，或者从湿地登船前往嚎风峡湾。', '北方的巫妖王已经蠢蠢欲动。$b$b在他的指挥下，一支亡灵大军正在横扫诺森德，到处散播瘟疫。$b$b我们需要你去阻止他们，$n。', '', '', '', '', '', '', 26365),
(49862, 'zhCN', '前往外域！', '穿过诅咒之地的黑暗之门。', '燃烧军团——那支由无数恶魔组成的大军突袭了德莱尼破碎的故乡，外域。联盟的冒险者需要保护外域的残余土地，不能让它们彻底毁于恶魔入侵。$b$b快前往诅咒之地，穿过黑暗之门吧。', '', '', '', '', '', '', 26365),
(49860, 'zhCN', '冒险的召唤', '使用特别任务指派书选择你的下一个冒险目的地。', '你的英勇事迹已经传遍了艾泽拉斯内外。其他地方也需要你的高超身手。\n\n你愿意穿过黑暗之门，去外域的破碎世界与燃烧军团战斗吗？\n\n或者是飞往北方的诺森德，参加对抗巫妖王的战争。\n\n做出你的选择吧，冒险者。', '', '', '', '', '', '', 26365),
(49855, 'zhCN', '海加尔山之灾', '跟塞纳里奥使者托尔·黑蹄谈一谈，进入月光林地。', '塞纳里奥议会的兄弟报告说，死亡之翼的爪牙对世界树发动了袭击，试图把它烧光！$B$B你可以与塞纳里奥使者托尔·黑蹄谈一谈，获得进入月光林地的许可。他在奥格瑞玛的智慧谷的牛头人区。', '获得前往月光林地的许可', '', '', '', '', '跟月光林地永夜港的风歌使者谈一谈。', 26365),
(49852, 'zhCN', '前往潘达利亚！', '到奥格瑞玛的格罗玛什要塞报到。', '你受命即刻前去格罗玛什要塞报到。$B$B我们得到消息说，南方新发现了一块富饶的大陆，我们计划派你和一小队士兵，为部落占领那里。', '', '', '', '', '', '', 26365),
(49851, 'zhCN', '大灾变急救员', '向奥格瑞玛的先知克洛伽报到。', '死亡之翼，死亡的化身巨龙，派遣了爪牙想要清除艾泽拉斯的生物。$B$B奥格瑞玛的先知克洛伽可以告诉你更多海加尔山、瓦丝琪尔以及其他死亡之翼袭击的地方的情报。', '', '', '', '', '', '', 26365),
(49846, 'zhCN', '两线作战', '使用特别任务指派书选择你的下一个冒险目的地。', '$n！你再一次受到了召唤，协助部落完成重要任务。\n\n你愿意协助部落对抗死亡之翼和暮光之锤吗？\n\n还是想飞往南方，抢在联盟前头夺取那里的资源？\n\n做出你的选择，然后投入战斗吧！', '', '', '', '', '', '', 26365),
(49817, 'zhCN', '前往诺森德！', '乘坐飞艇前往诺森德吧。$b$b你可以在提瑞斯法林地搭乘飞往嚎风峡湾的飞艇，或是在奥格瑞玛搭乘飞往北风苔原的飞艇。', '北方的巫妖王已经蠢蠢欲动。$b$b在他的指挥下，一支亡灵大军正在横扫诺森德，到处散播瘟疫。$b$b我们需要你去阻止他们，$n。', '', '', '', '', '', '', 26365),
(49816, 'zhCN', '前往外域！', '穿过诅咒之地的黑暗之门。', '燃烧军团——那支由无数恶魔组成的大军突袭了兽人破碎的故乡，外域。部落的冒险者需要保护外域的残余土地，不能让它们彻底毁于恶魔入侵。$b$b快前往诅咒之地，穿过黑暗之门吧。', '', '', '', '', '', '', 26365),
(49813, 'zhCN', '冒险的召唤', '使用特别任务指派书选择你的下一个冒险目的地。', '你的英勇事迹已经传遍了艾泽拉斯内外。其他地方也需要你的高超身手。\n\n你愿意穿过黑暗之门，去外域的破碎世界与燃烧军团战斗吗？\n\n或者是飞往北方的诺森德，参加对抗巫妖王的战争。\n\n做出你的选择吧，冒险者。', '', '', '', '', '', '', 26365),
(49812, 'zhCN', '达拉然的德拉丝', '前往达拉然的安东尼达斯纪念碑处找到德拉丝·月牙。', '德拉丝·月牙需要我们的帮助。作为回报，她会协助我们破译命令法典。$B$B我想请你尽快去找到她。据说她很喜欢在肯瑞托已故的伟大领袖——安东尼达斯的纪念碑那里逗留。', '', '', '', '', '', '前往达拉然的安东尼达斯纪念碑处找到德拉丝·月卡。', 26365),
(49788, 'zhCN', '为了联盟', '去暴风城大使馆与穆加德大使见面。', '要掌控虚空低语，需要无时无刻的专注和自律。你的奉献鼓舞了所有朗多雷——因为我们已是虚空之子。\n\n你在我这里学了很多，是时候去泰洛古斯裂隙之外冒险了。\n\n去联盟的心脏暴风城。国王的使者会在那里迎接你，并指引你的旅程。\n\n我们必须守护艾泽拉斯。你是我能想到回应这一号召的最佳人选，$p。', '', '', '', '', '', '去暴风城大使馆与穆加德大使见面。', 26365),
(49787, 'zhCN', '幽魂之地', '找到乌布里克在幽魂之地的家。', '有个叫乌布里克的血精灵和他的追随者被银月城驱逐了，理由是探索虚空魔法。\n\n虽然虚空魔法的确被禁了，但这样的处理方式也太极端了。\n\n不过，这恰好为联盟带来了机遇。如果我们可以找到这些流放者，就可以让他们加入我们的事业。而且更重要的是，我们可以帮助他们理解虚空，防止虚空腐化他们。', '', '', '', '', '', '', 26365),
(49784, 'zhCN', '夜之子的传承', '与暗夜要塞的塔莉萨见面。', '你是夏多雷的第一批冒险者，你游历了艾泽拉斯的荒野。但现在，该回家了。\n\n回苏拉玛来。你帮助族人在这个世界重获地位，我们要感谢你。', '', '', '', '', '', '', 26365),
(49783, 'zhCN', '至高岭的传承', '在雷霆图腾与梅拉·高岭见面。', '你在艾泽拉斯旅行了很久，$p。在这段时间里，你为至高岭带来了荣耀。\n\n现在，至高岭正在召唤你回家。我们觉得是时候了，应该感谢你为我们所做的一切。', '', '', '', '', '', '', 26365),
(49782, 'zhCN', '光铸者的传承', '与维迪卡尔的法瑞娅队长见面。', '你对联盟的帮助令人印象深刻，$p。换了我也不可能做得更好。呃，或许可以……但也好不了太多。\n\n来吧，到维迪卡尔来见我。我们该嘉奖你的勤勉了。', '', '', '', '', '', '', 26365),
(49773, 'zhCN', '为了部落！', '去奥格瑞玛大使馆与黑卫大使见面。', '这是高岭部族的一个新纪元，$n。\n\n经过数个世纪的分离，我们与卡利姆多的同胞们重聚。在拯救我们家园的战争中，我们又在部落找到了新的朋友。\n\n像你这样勇敢的牛头人应该响应冒险的号召，向我们的盟友展示实力。\n\n他们邀请你前往奥格瑞玛。去大酋长的大使馆与大使见面，开始你在艾泽拉斯的旅程吧。\n\n大地母亲与你同在，$n。', '', '', '', '', '', '去奥格瑞玛大使馆与黑卫大使见面。', 26365),
(49772, 'zhCN', '为了联盟', '去暴风城大使馆与穆加德大使见面。', '圣光军团建立的唯一目的，就是击败燃烧军团。尽管我们已经达成了这一目标，但新的职责也已降临。\n\n艾泽拉斯世界充斥着许多敌人。用你在训练中掌握的力量去抗击黑暗，实现联盟的理想。\n\n去暴风城，在大使馆找到国王大使，是时候将正义的圣光的洒遍这块土地了。\n\nPheta thones gamera，$n。', '', '', '', '', '', '去暴风城大使馆与穆加德大使见面。', 26365),
(49698, 'zhCN', '光铸者', '与图拉扬一起前往维迪卡尔。', '$n，你已经亲眼目睹了我们的力量。数千年的战争已经把我们塑造成了对抗军团的专家。\n\n现在，时候到了，可以拿起武器，与联盟的同胞并肩作战了。\n\n到维迪卡尔与我们汇合吧。我相信，我们与法瑞娅队长有很多要谈。', '', '', '', '', '', '与维迪卡尔的法瑞娅队长见面', 26365),
(49614, 'zhCN', '夜之子', '向奥格瑞玛的大酋长复命。', '我们的胜利会让大酋长很高兴。\n\n塔莉萨和她的夜之子必然会成为强大的盟友。', '', '', '', '', '', '', 26365),
(49613, 'zhCN', '银月城', '在银月城与女伯爵莉亚德琳谈一谈。', '夜之子与世隔绝了几千年。现在重现于世，自然需要我们来为他们指引正道。\n\n摄政王塞隆已经邀请夜之子和我们在银月城一叙。\n\n来吧，$n，我们去银月城与塔莉萨会面。', '', '', '', '', '', '', 26365),
(49567, 'zhCN', '英雄的召唤：纳格兰！', '前往德拉诺，与纳格兰的巴弗尔中尉谈一谈。', '联盟所有身强力壮的公民都要前往德拉诺，向纳格兰的巴弗尔中尉报到。\n\n伊瑞尔提议，联盟应该在纳格兰修建一个岗哨，协助抗击战歌氏族。\n\n飞往纳格兰，与巴弗尔中尉和补给车队会面。\n\n荣耀属于联盟！', '', '', '', '', '', '前往德拉诺，与纳格兰的巴弗尔中尉谈一谈。', 26365),
(49566, 'zhCN', '英雄的召唤：阿兰卡峰林！', '与阿兰卡峰林的阿兹克谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候阿兰卡峰林的阿兹克差遣。\n\n那些没有翅膀的鸦人似乎正在逃离阿兰卡峰林。传言说他们是为了躲避钢铁部落的超级武器。\n\n我们需要查明是什么导致他们逃离自己的家园，他们的翅膀又是为何枯萎。\n\n荣耀属于联盟！', '', '', '', '', '', '与阿兰卡峰林的阿兹克谈一谈。', 26365),
(49565, 'zhCN', '英雄的召唤：塔拉多！', '与塔拉多的监工艾克索斯谈一谈。', '联盟所有身强力壮的公民都要向塔拉多的监工艾克索斯报到。\n\n钢铁部落想要攫取塔拉多。这片土地的位置很适合向德拉诺的其他地区迅速增派援军，同时不会遭到抵抗。\n\n监工艾克索斯目前正在塔拉多修建一个岗哨。前往那里，阻止钢铁部落。\n\n荣耀属于联盟！', '', '', '', '', '', '与塔拉多的监工艾克索斯谈一谈。', 26365),
(49564, 'zhCN', '英雄的召唤：戈尔隆德！', '与戈尔隆德的伊瑞尔谈一谈。', '联盟所有身强力壮的公民都要前往德拉诺，向戈尔隆德的伊瑞尔报到。\n\n伊瑞尔和玛尔拉德希望派遣一个小队深入戈尔隆德的荒野。\n\n我们希望借此增加对这片土地的了解，从而掌握钢铁部落的弱点。\n\n协助他们的探险吧。\n\n荣耀属于联盟！', '', '', '', '', '', '与戈尔隆德的伊瑞尔谈一谈。', 26365),
(49561, 'zhCN', '英雄的召唤：恐惧废土！', '去蟠龙脊和女射手李琪薇谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候锦绣谷的蟠龙脊的女射手李琪薇差遣。\n\n影踪派正在努力阻止螳螂妖大军入侵整个潘达利亚。\n\n你可以征用一只风筝，飞向蟠龙脊，前往女射手李琪薇的位置。\n\n荣耀属于联盟！', '', '', '', '', '', '去蟠龙脊和女射手李琪薇谈一谈。', 26365),
(49560, 'zhCN', '英雄的召唤：螳螂高原！', '与螳螂高原的祝踏岚谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候螳螂高原的祝踏岚差遣。\n\n影踪派希望从野牛人手中夺回他们的土地，但他们人手不足。\n\n协助他们攻打野牛人吧。\n\n荣耀属于联盟！', '', '', '', '', '', '与螳螂高原的祝踏岚谈一谈。', 26365),
(49559, 'zhCN', '英雄的召唤：昆莱山！', '与四风谷的土地精信使谈一谈。', '所有身强力壮的联盟成员，从此刻起都要前往昆莱山协助游学者周卓。\n\n与四风谷的土地精信使谈一谈。他们会指引你攀登昆莱山的路。\n\n荣耀属于联盟！', '', '', '', '', '', '与四风谷的土地精信使谈一谈。', 26365),
(49558, 'zhCN', '英雄的召唤：卡桑琅丛林！', '与卡桑琅丛林的肯肯谈一谈。', '所有身强力壮的联盟成员，从此刻起都要前往卡桑琅丛林的朱家堡报到。\n\n有传言说，卡桑琅丛林里的熊猫人行事变得很古怪。\n\n与朱家堡的肯肯谈一谈。也许他知道发生了什么事，导致熊猫人变得如此了无生气。', '', '', '', '', '', '与卡桑琅丛林的肯肯谈一谈。', 26365),
(49557, 'zhCN', '英雄的召唤：四风谷！', '与四风谷的陈·风暴烈酒谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候四风谷的陈·风暴烈酒差遣。\n\n风暴烈酒家族的探险精神值得联盟成员学习。\n\n与风暴烈酒家族一起旅行，探索潘达利亚隐藏的秘密。\n\n荣耀属于联盟！', '', '', '', '', '', '与四风谷的陈·风暴烈酒谈一谈。', 26365),
(49556, 'zhCN', '英雄的召唤：翡翠林！', '向暴风要塞的杰塔瑞斯将军报到。', '所有身强力壮的联盟成员，都要立刻前往暴风城的暴风要塞报到。\n\n杰塔瑞斯将军正在召集队伍完成一个重要任务。\n\n立刻前往暴风要塞了解详情吧。\n\n荣耀属于联盟！', '', '', '', '', '', '', 26365),
(49555, 'zhCN', '英雄的召唤：冰冠冰川！', '与晶歌森林上方的达拉然的军官范恩·罗希姆谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候晶歌森林上方的达拉然的军官范恩·罗希姆差遣。\n\n无畏远征军需要更多冒险者来壮大力量，从而攻打冰冠堡垒和阿尔萨斯本人。\n\n时机已经成熟，巫妖王要为自己犯下的罪行付出代价。\n\n荣耀属于联盟！', '', '', '', '', '', '与晶歌森林上方的达拉然的军官范恩·罗希姆谈一谈。', 26365),
(49554, 'zhCN', '英雄的召唤：风暴峭壁！', '与晶歌森林上方的达拉然的林·多克塔谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候晶歌森林上方的达拉然的林·多克塔差遣。\n\nK3的地精在风暴峭壁修建了一座城市，并派遣大使来招募冒险者。\n\n与林谈一谈，看看K3的居民有什么需要。他们在对抗巫妖王的战争中可以成为宝贵的盟友。\n\n荣耀属于联盟！', '', '', '', '', '', '与晶歌森林上方的达拉然的林·多克塔谈一谈。', 26365),
(49553, 'zhCN', '英雄的召唤：索拉查盆地', '与晶歌森林上方的达拉然的大法师伯塔鲁斯谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候晶歌森林上方的达拉然的大法师伯塔鲁斯差遣。\n\n赫米特·奈辛瓦里带着他的远征军前去探索索拉查盆地，但从此杳无音信。\n\n我们对索拉查的广袤丛林所知不多，但那座盆地里可能里隐藏着许多秘密，可以帮助我们在对抗巫妖王时占得上风。\n\n荣耀属于联盟！', '', '', '', '', '', '与晶歌森林上方的达拉然的大法师伯塔鲁斯谈一谈。', 26365),
(49552, 'zhCN', '英雄的召唤：祖达克！', '与祖达克的北伐军领主兰迪加谈一谈。', '所有身强力壮的联盟成员，从此刻起都要听候祖达克的北伐军领主兰迪加差遣。\n\n天灾军团正在屠杀祖达克当地的居民，并把他们转化为自己的一份子。\n\n协助银色北伐军和黑锋骑士团，阻止这场威胁。\n\n荣耀属于联盟！', '', '', '', '', '', '与祖达克的北伐军领主兰迪加谈一谈。', 26365),
(49551, 'zhCN', '英雄的召唤：嚎风峡湾！', '与嚎风峡湾的玛卡洛伊谈一谈。', '所有身强力壮的联盟成员，都要立刻向嚎风峡湾的玛卡洛伊报到。\n\n港口小镇瓦加德就位于嚎风峡湾的核心地带，能够让联盟占据攻击天灾军团的有利地形。\n\n快去协助驻扎在那里的士兵吧。\n\n荣耀属于联盟！', '', '', '', '', '', '与嚎风峡湾的玛卡洛伊谈一谈。', 26365),
(49550, 'zhCN', '英雄的召唤：影月谷！', '前往外域，与影月谷的空军指挥官努埃恩谈一谈。', '所有身强力壮的联盟成员，都要立刻前往外域的影月谷，向空军指挥官努埃恩报到。\n\n燃烧军团正在围攻蛮锤要塞，威胁到了我们人民的生命安全。\n\n我们需要增援要塞，阻止燃烧军团无休止的进攻！\n\n荣耀属于联盟！', '', '', '', '', '', '前往外域，与影月谷的空军指挥官努埃恩谈一谈。', 26365),
(49549, 'zhCN', '大酋长的命令：纳格兰！', '前往德拉诺的纳格兰，与石头守卫布罗克斯谈一谈。', '所有身强力壮的部落成员必须立即前往德拉诺的纳格兰，向头守卫布罗克斯报到。\n\n萨尔正在彼处建造部落岗哨，需要帮手抵挡战歌部族。\n\n快去和布罗恩·天角谈一谈，与补给车队汇合吧。\n\n为了部落！', '', '', '', '', '', '前往德拉诺的纳格兰，与石头守卫布罗克斯谈一谈。', 26365),
