INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(122289, 0, 0, 'If what dis exile says is true, den you must investigate. But use caution. Jakra\'zet may still be near.', 12, 0, 100, 1, 0, 109046, 156182, 'Bladeguard Kaja to Player'),
(122289, 1, 0, 'Let\'s keep moving. Rakera won\'t survive dis desert for long.', 12, 0, 100, 1, 0, 105907, 154114, 'Bladeguard Kaja to Player'),
(122729, 0, 0, 'Bladeguard Jorana is unresponsive.', 42, 0, 100, 0, 0, 0, 131689, 'Bladeguard Jorana to Player'),
(122745, 0, 0, 'You cannot stop the sands from spreading!', 12, 0, 100, 0, 0, 103645, 150843, 'Faithless Sandscout to Player'),
(122762, 0, 0, 'I will repay this debt. You have my word.', 12, 0, 100, 1, 0, 107257, 131777, 'Hakasi to Player'),
(122764, 0, 0, 'I\'m free to go? No deal to be made? You are different than most.', 12, 0, 100, 6, 0, 107255, 131776, 'Kipp to Player'),
(123773, 0, 0, 'My life... for the empire...', 12, 0, 100, 0, 0, 103661, 152357, 'Sethrak Ravager to Player'),
(123774, 0, 0, 'We will eradicate you like insects!', 12, 0, 100, 0, 0, 103641, 150839, 'Sethrak Aggressor to Player'),
(123775, 0, 0, 'For the Emperor!', 12, 0, 100, 0, 0, 103608, 140591, 'Sethrak Warden to Player'),
(123776, 0, 0, 'Your lands are next to fall!', 12, 0, 100, 0, 0, 103608, 140584, 'Sethrak Skycaller to Player'),
(123866, 0, 0, '$n coats their claws with venom!', 16, 0, 100, 0, 0, 0, 139097, 'Faithless Flayer to Faithless Flayer'),
(126697, 0, 0, 'We can\'t get in. The door is sealed with magic we\'ve never seen before.', 12, 0, 100, 1, 0, 107104, 140157, 'Grozztok the Blackheart to Player'),
(126697, 1, 0, 'You made a mistake coming to dese dunes. De princess can\'t save you now.', 12, 0, 100, 1, 0, 107105, 140156, 'Grozztok the Blackheart to Player'),
(126697, 2, 0, 'You are no better than me. My blood... is on your hands.', 12, 0, 100, 603, 0, 107103, 136841, 'Grozztok the Blackheart to Player'),
(126814, 0, 0, 'Looks like our work here is done! Be safe out there, $p.', 12, 0, 100, 1, 0, 0, 156404, 'Ranah to Player'),
(129276, 0, 0, 'Dis one is mine!', 12, 0, 100, 603, 0, 107038, 140557, 'Sandfury Assassin to Player'),
(129276, 1, 0, 'We\'ve got orders to kill dis one.', 12, 0, 100, 603, 0, 107034, 140555, 'Sandfury Assassin to Player'),
(129276, 2, 0, 'Lost to de sands.', 12, 0, 100, 6, 0, 107044, 154568, 'Sandfury Assassin to Player'),
(129304, 0, 0, 'Silence! We are not alone...', 12, 0, 100, 603, 0, 109589, 140158, 'General Jakra\'zet to Player'),
(129304, 1, 0, 'Grozztok, deal with dese trespassers! Do not fail me again.', 12, 0, 100, 1, 0, 109590, 140159, 'General Jakra\'zet to Player'),
(130455, 0, 0, 'Nice choice. I can work with that.', 12, 0, 100, 0, 0, 107742, 153869, 'Nisha to Player'),
(130466, 0, 0, 'You cannot stop the Faithless empire!', 12, 0, 100, 0, 0, 107779, 149258, 'Fangcaller Sraka to Player'),
(130466, 1, 0, 'You cannot stop... the storm...', 12, 0, 100, 603, 0, 107780, 149259, 'Fangcaller Sraka to Player'),
(130474, 0, 0, 'I\'m ready. Let\'s hunt some sethrak.', 12, 0, 100, 0, 0, 107764, 141651, 'Nisha to Player'),
(130474, 1, 0, 'Look what the sethrak have done to our home...', 12, 0, 100, 0, 0, 107743, 153870, 'Nisha to Player'),
(130474, 2, 0, 'Now the sethrak will know to fear the vulpera!', 12, 0, 100, 0, 0, 107744, 141663, 'Nisha to Player'),
(130474, 3, 0, 'We weren\'t prepared for their attack. The sethrak slaughter and enslave without remorse.', 12, 0, 100, 0, 0, 107738, 153936, 'Nisha to Player'),
(130474, 4, 0, 'The vulpera are mostly a passive people. We travel in caravans, scavenging for supplies and trading where we can.', 12, 0, 100, 0, 0, 103661, 153872, 'Nisha to Player'),
(130474, 5, 0, 'The vulpera are anything but weak. They will fear us!', 12, 0, 100, 0, 0, 107758, 141660, 'Nisha to Player'),
(130474, 6, 0, 'I warned Kiro that the sethrak would come for us soon enough. We could have been prepared... Maybe now he\'ll finally listen.', 12, 0, 100, 0, 0, 107740, 153937, 'Nisha to Player'),
(130474, 7, 0, 'I hope the others made it to safety.', 12, 0, 100, 0, 0, 107741, 153873, 'Nisha to Player'),
(130474, 8, 0, 'These sethrak will pay for what they\'ve done to our home.', 12, 0, 100, 0, 0, 107767, 141657, 'Nisha to Player'),
(130474, 9, 0, 'Right where I left it!', 12, 0, 100, 0, 0, 107732, 154001, 'Nisha to Player'),
(135011, 0, 0, 'Coda chatters and looks into the ravine.', 16, 0, 100, 553, 0, 0, 149368, 'Coda to Player'),
(135012, 0, 0, 'If we go in there, those stingers will get us, too!', 12, 0, 100, 0, 0, 0, 149332, 'Vivi to Player'),
(135012, 1, 0, 'I\'m worried, too, Coda.', 12, 0, 100, 0, 0, 0, 149367, 'Vivi to Player'),
(135012, 2, 0, 'Thank you! And please give Poda lots of snuggles!', 12, 0, 100, 3, 0, 0, 149364, 'Vivi to Player'),
(137799, 0, 0, 'Hang on! This is gonna be a wild ride.', 12, 0, 100, 0, 0, 107733, 153972, 'Nisha to Player'),
(137799, 1, 0, 'Looks like we have company. Light \'em up, $p!', 12, 0, 100, 0, 0, 107734, 153973, 'Nisha to Player'),
(137799, 2, 0, 'I think we\'re in the clear. Great work back there.', 12, 0, 100, 0, 0, 107735, 153976, 'Nisha to Player'),
(137799, 3, 0, 'The hideaway should be up ahead. We\'ll be safe there. For a while, at least.', 12, 0, 100, 0, 0, 107736, 154246, 'Nisha to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(126235, 0, 0, 'Dolly! Dot! Wait! It\'s not safe down there!', 14, 0, 100, 603, 0, 105972, 137860, 'Meerah to Player'),
(127966, 0, 0, 'Welcome, Akunda de Nimble.', 12, 0, 100, 0, 0, 105852, 137455, 'Akunda the Exalted to Player'),
(127966, 1, 0, 'You are ready to hear Akunda\'s words, though it must be done alone. Head to de altar and pray when you are ready.', 12, 0, 100, 1, 0, 105857, 154145, 'Akunda the Exalted to Player'),
(127980, 0, 0, 'You! Yes, you. Approach quietly. Do not draw attention to yourself.', 15, 0, 100, 0, 0, 107024, 141689, 'Akunda the Sensible to Player'),
(127989, 0, 0, 'Where... where am I? What am I doing here?', 12, 0, 100, 1, 0, 107089, 154300, 'Meijani to Player'),
(127989, 1, 0, 'I will do my best to repair Akunda\'s mind, but you need to be prepared to subdue any memories dat might be awakened.', 12, 0, 100, 1, 0, 107075, 154304, 'Meijani to Player'),
(127989, 2, 0, 'It\'s almost done! I can hear Akunda\'s voice entering my mind.', 12, 0, 100, 0, 0, 107077, 154305, 'Meijani to Player'),
(127992, 0, 0, 'Gathering de reagents needed for your communion is de first step towards redemption.', 12, 0, 100, 1, 0, 105850, 137909, 'Akunda the Exalted to Player'),
(127992, 1, 0, 'Akunda will only enter your mind if you\'ve been cleansed.', 12, 0, 100, 1, 0, 105854, 154144, 'Akunda the Exalted to Player'),
(128147, 0, 0, 'Fool! You should have run away when you had de chance! De power of Akunda will be mine!', 12, 0, 100, 0, 0, 105849, 137869, 'Akunda the Exalted to Player'),
(128152, 0, 0, 'At last, I am free! Thank you, $p.', 12, 0, 100, 1, 0, 105832, 154171, 'Akunda to Player'),
(128296, 0, 0, 'Dere is a price on your head!', 12, 0, 100, 603, 0, 107018, 140579, 'Sandfury Assassin to Player'),
(128807, 0, 0, 'Thank the sands you\'re safe!', 12, 0, 100, 603, 0, 110189, 149156, 'Meerah to Player'),
(129514, 0, 0, 'We need to find Julwaba before Jakra\'zet does.', 12, 0, 100, 0, 0, 107411, 154315, 'Warguard Rakera to Player'),
(130569, 0, 0, 'Someone in dis strange temple must know something.', 12, 0, 100, 1, 0, 105862, 141683, 'Bladeguard Kaja to Player'),
(135066, 0, 0, 'Go. See what you can find out. I will not leave Rakera\'s side.', 12, 0, 100, 1, 0, 105912, 154146, 'Bladeguard Kaja to Player'),
(138382, 0, 0, 'My dear child. At last, you can hear me. A traitor lurks within our temple.', 12, 0, 100, 0, 0, 105840, 154176, 'Akunda to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(126085, 0, 0, 'Our herd of alpacas has gone missing. We must find them before it\'s too late!', 12, 0, 100, 1, 0, 0, 137252, 'Mugjabu to Player'),
(126085, 1, 0, 'De alpacas won\'t survive in de desert alone. We need to bring them back!', 12, 0, 100, 1, 0, 0, 137253, 'Mugjabu to Player'),
(126108, 0, 0, 'We won\'t survive long without food and supplies...', 12, 0, 100, 1, 0, 0, 137250, 'Sezahjin to Player'),
(126108, 1, 0, 'The desert is a desolate place, but there\'s food to be found if you know where to look!', 12, 0, 100, 1, 0, 0, 137251, 'Sezahjin to Player'),
(126108, 2, 0, 'These may not be the most delicious ingredients, but they keep us fed.', 12, 0, 100, 1, 0, 0, 137255, 'Sezahjin to Player'),
(126108, 3, 0, 'Junji be always getting himself up to no good. It\'ll be bad news for him if de lizards find him before you do!', 12, 0, 100, 1, 0, 0, 137257, 'Sezahjin to Player'),
(126576, 0, 0, 'We\'ll proceed when you\'re ready.', 12, 0, 100, 1, 0, 0, 137248, 'Razgaji to Player'),
(126576, 1, 0, 'Be on your guard. Mojambo can\'t be trusted...', 12, 0, 100, 0, 0, 0, 155199, 'Razgaji to Player'),
(127025, 0, 0, 'Ho! What bilgesuckers be darin\' to challenge me?', 14, 0, 100, 0, 0, 0, 138533, 'Pirate-King Zem\'lan to Zem\'lan\'s Boat'),
(127025, 1, 0, 'A mutiny by me own first mate?! I\'ll keelhaul ya!', 14, 0, 100, 0, 0, 0, 138534, 'Pirate-King Zem\'lan to Zem\'lan\'s Boat Bunny'),
(127025, 2, 0, 'Da Port of Zem\'lan be mine!', 12, 0, 100, 0, 0, 0, 136744, 'Pirate-King Zem\'lan to Player'),
(127025, 3, 0, 'I don\'t stay dead long... I be seein\' ya soon, Jamboya...', 12, 0, 100, 0, 0, 0, 138535, 'Pirate-King Zem\'lan to Player'),
(127032, 0, 0, 'I\'m free! I can\'t believe it!', 12, 0, 100, 1, 0, 0, 136812, 'Junji to Player'),
(127032, 1, 0, 'I\'ll make sure to tell everyone at the camp how you saved me!', 12, 0, 100, 1, 0, 0, 136813, 'Junji to Player'),
(128299, 0, 0, 'Run fast, ya rag. I got all of eternity to hunt you down.', 12, 0, 100, 0, 0, 0, 138650, 'Captain Gulnaku to Player'),
(128346, 0, 0, 'Another fleshy to dominate! This\'ll be fun.', 12, 0, 100, 0, 0, 0, 152516, 'Quartermaster Boonzali to Player'),
(128346, 1, 0, 'We be bound to Zem\'lan\'s will, forever.', 12, 0, 100, 0, 0, 0, 138661, 'Quartermaster Boonzali to Player'),
(128453, 0, 0, 'Heave me down, I be coming back soon...', 12, 0, 100, 0, 0, 107164, 152900, 'Witchdoctor Yoksa to Player'),
(128454, 0, 0, 'Ain\'t I killed ya already? Or were dat ya killed me?', 12, 0, 100, 0, 0, 0, 138658, 'Da Cabin Boy to Player'),
(128454, 1, 0, 'We return from da grave, ready to fight.', 12, 0, 100, 0, 0, 0, 138659, 'Da Cabin Boy to Player'),
(128469, 0, 0, 'Stay away from me treasure map!', 12, 0, 100, 0, 0, 0, 138678, 'First Mate Drakul to Player'),
(128469, 1, 0, 'Gulnaku\'s not gonna be pleased about this.', 12, 0, 100, 0, 0, 0, 138679, 'First Mate Drakul to Player'),
(128497, 0, 0, 'I been drinkin\' all day for dis one...', 12, 0, 100, 0, 0, 0, 138686, 'Bajiani the Slick to Player'),
(128497, 1, 0, 'Come on back for da rematch...', 12, 0, 100, 0, 0, 0, 138687, 'Bajiani the Slick to Player'),
(128530, 0, 0, 'Da sethrak think we gonna roll over and die. What say you?', 14, 0, 100, 0, 0, 0, 138537, 'Vision of Pirate-King Zem\'lan to Player'),
(128530, 1, 0, 'Dis be our home. We gonna do whatever it takes to hold dis port... Forever!', 14, 0, 100, 0, 0, 0, 138539, 'Vision of Pirate-King Zem\'lan to Player'),
(128530, 2, 0, 'Step forward and receive me gift. Join me crew for eternity!', 12, 0, 100, 1, 0, 0, 138540, 'Vision of Pirate-King Zem\'lan to Player'),
(128530, 3, 0, 'First Mate Jamboya, be reborn!', 12, 0, 100, 396, 0, 0, 138542, 'Vision of Pirate-King Zem\'lan to Player'),
(128530, 4, 0, 'Welcome to da eternal crew of the damned, First Mate.', 12, 0, 100, 113, 0, 0, 138544, 'Vision of Pirate-King Zem\'lan to Player'),
(128530, 5, 0, 'Who be next?', 14, 0, 100, 1, 0, 0, 138545, 'Vision of Pirate-King Zem\'lan to Player'),
(128633, 0, 0, 'Raaagh!', 14, 0, 100, 53, 0, 0, 138543, 'Vision of First Mate Jamboya to Player'),
(128640, 0, 0, 'NO!', 14, 0, 100, 22, 0, 0, 150513, 'Vision of Basher Hak\'anjin to Player'),
(128661, 0, 0, 'You have not... won...', 12, 0, 100, 0, 0, 103651, 152344, 'Faithless Ravager to Player'),
(128665, 0, 0, 'I failed you... Korthek...', 12, 0, 100, 0, 0, 103619, 152345, 'Faithless Skycaller to Player'),
(128696, 0, 0, 'Serrik, it\'s good that you have returned! The Faithless have taken Vorrik!', 14, 0, 100, 0, 0, 105814, 148785, 'Zissiah to Player'),
(128743, 0, 0, 'No prey, no pay.', 12, 0, 100, 0, 0, 107143, 138631, 'Sek\'thun to Player'),
(128758, 0, 0, 'If you ain\'t part of de crew, you be dead!', 12, 0, 100, 0, 0, 107146, 138627, 'Lookout Ungalra to Player'),
(128788, 0, 0, 'I feel nothing...', 12, 0, 100, 0, 0, 107159, 152898, 'Zuk\'su to Player'),
(128857, 0, 0, 'De curse makes us strong!', 12, 0, 100, 0, 0, 107147, 138628, 'Xu\'nanji to Player'),
(128858, 0, 0, 'Who wants to drink with me?', 14, 0, 100, 22, 0, 0, 138706, 'Lazy Tzushu'),
(128872, 0, 0, 'Shouldn\'t be wanderin\' in Zem\'lan\'s alleys, rat. Get \'em, Kiffa!', 12, 0, 100, 0, 0, 0, 138717, 'Captain Zhonga to Player'),
(128873, 0, 0, 'Dis land be cursed... we all are....', 12, 0, 100, 0, 0, 107161, 152899, 'First Mate Kiffa to Player'),
(128878, 0, 0, 'Watch where you digging! I be sleepin\' down there!', 12, 0, 100, 0, 0, 0, 138725, 'Zenanju to Player'),
(128878, 1, 0, 'It\'s so bright out here... Put me bones back in the hole.', 12, 0, 100, 0, 0, 0, 152537, 'Zenanju to Player'),
(128961, 0, 0, 'I owe you my life, $p. Meet inside the temple. It\'s not safe here.', 12, 0, 100, 0, 0, 107492, 139391, 'Vorrik to Player'),
(129007, 0, 0, 'Our empire\'s dawn is your extinction!', 12, 0, 100, 0, 0, 103635, 138644, 'Faithless Sentry to Player'),
(129260, 0, 0, 'Go. Gather what is needed to heal dis snakeman. I will fill in Julwaba on what we know.', 12, 0, 100, 6, 0, 109864, 156015, 'Warguard Rakera to Player'),
(134103, 0, 0, 'This is not... the end...', 12, 0, 100, 0, 0, 103663, 152360, 'Sethrak Site Guardian to Player'),
(134121, 0, 0, 'Those blasted Zandalari are closing in, we must find it before they do!', 14, 0, 100, 25, 0, 107775, 147968, 'Overseer Nerzet'),
(134162, 0, 0, 'I need to head back to de arena. Be careful out dere. Jakra\'zet has put a hefty price on your head.', 12, 0, 100, 1, 0, 0, 148145, 'Julwaba to Player'),
(134292, 0, 0, 'Vorrik, if you are receiving this message, then I fear I was too late. Korthek has betrayed our people, and plans to break the barrier we placed on the tomb long ago.', 12, 0, 100, 1, 0, 106397, 148102, 'Sulthis to Player'),
(134292, 1, 0, 'I can only hope I\'ve hidden my key well enough so that you are the only one to find it.', 12, 0, 100, 0, 0, 109931, 156432, 'Sulthis to Player'),
(134320, 0, 0, 'The sands... will spread...', 12, 0, 100, 0, 0, 103658, 152347, 'Faithless Ravager to Player'),
(134320, 1, 0, 'My death... will be... avenged...', 12, 0, 100, 0, 0, 103656, 152346, 'Faithless Ravager to Player'),
(134321, 0, 0, 'This cannot be...', 12, 0, 100, 0, 0, 103659, 152348, 'Faithless Aggressor to Player'),
(134532, 0, 0, 'I can feel my strength returning... it appears the antivenom is working.', 12, 0, 100, 1, 0, 106003, 135577, 'Serrik to Player'),
(134638, 0, 0, 'The temple must fall! We will slaughter any who stand in our way.', 12, 0, 100, 603, 0, 105760, 140595, 'Warlord Zothix to Player'),
(134638, 1, 0, 'You cannot stop... the sands...', 12, 0, 100, 603, 0, 105761, 140596, 'Warlord Zothix to Player'),
(135400, 0, 0, 'I\'ll wait here in case any find their way home!', 12, 0, 100, 0, 0, 0, 151782, 'Jenoh to Player'),
(135400, 1, 0, 'Welcome back, my little friends. Listen to this new song I learned!', 12, 0, 100, 1, 0, 0, 157296, 'Jenoh to Player'),
(136317, 0, 0, 'Pirate-King Zem\'lan never gonna forget dis day.', 12, 0, 100, 396, 0, 0, 152560, 'First Mate Jamboya to Player'),
(136446, 0, 0, 'I gave everything to protect me crew. You ain\'t goin\' no further!', 12, 0, 100, 0, 0, 0, 138660, 'Master Gunner Torwec to Player'),
(136446, 1, 0, 'I got nothin\' else to give, Zem\'lan... You took everything.', 12, 0, 100, 0, 0, 0, 152517, 'Master Gunner Torwec to Player'),
(136466, 0, 0, 'Cursed thief...', 12, 0, 100, 0, 0, 107152, 153956, 'Goldslave to First Mate Jamboya'),
(136466, 1, 0, 'Add me to de pile...', 12, 0, 100, 0, 0, 107149, 153955, 'Goldslave to First Mate Jamboya'),
(136595, 0, 0, 'Drink up, me hearties. Ya be lookin\' a bit dry... Ha!', 12, 0, 100, 0, 0, 0, 154519, 'Taz\'raka the Traitor to Player'),
(136680, 0, 0, 'Keep your hands off my treasure...', 12, 0, 100, 0, 0, 107163, 152900, 'Gunner Sumanko to Player'),
(136963, 0, 0, 'All right, crew. Grab de treasure and get in de boat!', 12, 0, 100, 396, 0, 0, 152731, 'First Mate Jamboya to Player'),
(142830, 0, 0, 'Your kindness is unexpected.', 12, 0, 100, 0, 0, 107262, 131764, 'Captured Vulpera to Player'),
(142851, 0, 0, 'I\'ll just grab one more piece of treasure before I go...', 12, 0, 100, 0, 0, 107311, 137988, 'Captured Vulpera to Player'),
(142854, 0, 0, 'Thanks. I wouldn\'t have lasted much longer.', 12, 0, 100, 0, 0, 107259, 154381, 'Captured Vulpera to Player'),
(142854, 1, 0, 'The things I\'ve seen...', 12, 0, 100, 0, 0, 107261, 137981, 'Captured Vulpera to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(122723, 0, 0, 'It be safe to come out now?', 12, 0, 100, 1, 0, 0, 132696, 'Rhan\'ka to Player'),
(122723, 1, 0, 'Grenja! Volni! Man\'zul! You be back! De gang\'s all here!', 12, 0, 100, 0, 0, 0, 147882, 'Rhan\'ka to Player'),
(122723, 2, 0, 'You\'re right! It be time we brought $p into de gang officially. Prepare de ritual!', 12, 0, 100, 432, 0, 0, 134186, 'Rhan\'ka to Player'),
(122725, 0, 0, 'Mmm, this fruit looks delicious! Let\'s get it on the buffet table.', 12, 0, 100, 1, 0, 0, 140108, 'Zulsan to Player'),
(122725, 1, 0, 'Breakfast is served!', 14, 0, 100, 22, 0, 0, 147932, 'Zulsan to Player'),
(123730, 0, 0, 'Now everyone\'ll know this is the Goldtusk Inn!', 12, 0, 100, 1, 0, 0, 147920, 'Man\'zul to Player'),
(129319, 0, 0, 'Hello? Is anyone dere?', 14, 0, 100, 22, 0, 0, 140197, 'Bladeguard Tarkaj to Player'),
(129319, 1, 0, 'Please, I\'m injured... Have mercy on a fellow Zandalari!', 12, 0, 100, 1, 0, 0, 140200, 'Bladeguard Tarkaj to Player'),
(129319, 2, 0, '$p...?', 12, 0, 100, 0, 0, 0, 140210, 'Bladeguard Tarkaj to Player'),
(129330, 0, 0, 'What you doing out here, $p? I thought you were dead!', 12, 0, 100, 1, 0, 0, 140262, 'Bladeguard Tarkaj to Player'),
(129330, 1, 0, 'My first drink in Vol\'dun. I\'ve been lost in da dunes since we were attacked!', 12, 0, 100, 1, 0, 0, 140264, 'Bladeguard Tarkaj to Player'),
(129330, 2, 0, 'Da Faithless chased me across da desert, through wild beasts and monsters. I barely survived.', 12, 0, 100, 1, 0, 0, 140290, 'Bladeguard Tarkaj to Player'),
(129330, 3, 0, 'You helped dese exiles set up an inn way out here in da middle of nowhere?', 12, 0, 100, 1, 0, 0, 140291, 'Bladeguard Tarkaj to Player'),
(129330, 4, 0, 'I\'d say you\'re crazy, but you just saved my life. So, thanks.', 12, 0, 100, 1, 0, 0, 140292, 'Bladeguard Tarkaj to Player'),
(129330, 5, 0, 'I can\'t believe I found you again, $p. But I\'m exhausted...', 12, 0, 100, 1, 0, 0, 140293, 'Bladeguard Tarkaj to Player'),
(129330, 6, 0, 'Think I might stay here awhile. Help out where I can.', 12, 0, 100, 0, 0, 0, 140294, 'Bladeguard Tarkaj to Player'),
(129330, 7, 0, 'Thanks for saving me...', 12, 0, 100, 0, 0, 0, 140295, 'Bladeguard Tarkaj to Player'),
(129434, 0, 0, 'Get back in your golems, spirits! You belong to me!', 14, 0, 100, 0, 0, 0, 140718, 'Zak\'rajan the Undying to Soulcatcher Totem'),
(129434, 1, 0, 'Filthy scavengers! I rule here!', 12, 0, 100, 0, 0, 0, 140796, 'Zak\'rajan the Undying to Player'),
(129434, 2, 0, 'Let me die this time, Bwonsamdi... Please...', 12, 0, 100, 0, 0, 0, 152875, 'Zak\'rajan the Undying to Omi'),
(129436, 0, 0, 'No loa. Only Zak\'rajan.', 12, 0, 100, 0, 0, 107315, 140808, 'Soul-Trapped Guardian to Player'),
(129436, 1, 0, 'No kings. Only Zak\'rajan.', 12, 0, 100, 0, 0, 107316, 140811, 'Soul-Trapped Guardian to Player'),
