UPDATE `quest_offer_reward` SET `Emote1`=5, `RewardText`='You\'re brilliant, $G lad:lass;! Brilliant! This will be a glorious day indeed.$B$BQuickly, hand me the core before those iron dwarves make their way down here.', `VerifiedBuild`=26822 WHERE `ID`=12880; -- The Master Explorer
