UPDATE `quest_offer_reward` SET `RewardText`='Expertly done, $n.  The Scourge are mistaken if they think they can use those corpses against us, and the fluid you gathered from the Rot Hide slaves will be studied by my master.  It may have secrets we can use against them.', `VerifiedBuild`=32305 WHERE `ID`=24997; -- Graverobbers
