INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(61586, 'zhCN', '战争谋略', '提升你的名望。', '有了晋升堡垒这一盟友，我们获得了对抗伽马尔侯爵和造物密院所需的力量。\n\n我们会收集发动攻势所需的物资，但是我们的士兵需要一名勇士来率队作战。\n\n用实际行动证明，你就是他们心中的那位勇士。', '', '', '', '', '', '与兵主之座的德拉卡交谈。', 38134),
(61569, 'zhCN', '一键点火', '与图伊基恩一同飞往浮空城顶部，并插入符文钥石。', '终于，我们的目标近在眼前了。\n\n浮空城就漂浮在我们的头顶，被通灵束缚桩系在了这个平台上。\n\n图伊基恩能送你抵达浮空城的顶部。只要你插入我找到的这枚钥石，浮空城就是我们的了。\n\n我们会为出发做好准备。你成功后就来找我们。\n\n胜利很快就会属于我们。', '', '', '', '', '', '', 38134),
(61567, 'zhCN', '严肃意图', '摧毁西南、西北和东北的星界投射器。', '我的间谍发现了多个星界投射器，凭借这些装置，伽马尔侯爵在缝合场中集结着部队。\n\n我可不希望在决战的时候面对一大群狂乱的构造体。你肯定明白，我们得重挫它们的斗志。\n\n去摧毁那些投射器。它们应该就在缝合场的边界上。', '', '', '', '', '', '', 38134),
(61565, 'zhCN', 'Tracking Quest', '', '', '', '', '', '', '', '', 38134),
(61559, 'zhCN', '哀伤之刃的诞生', '与伯瓦尔和其他人讨论幻象。', '安度因的父亲失踪后，我对他视如己出。看到他遭受这样的折磨，任凭希尔瓦娜斯处置……\n\n<伯瓦尔的眼睛燃起了火光。>\n\n我们必须采取行动。\n\n你可以稍作休整，等你准备好谈谈后续行动了就告诉我。', '', '', '', '', '', '在奥利波斯与吉安娜·普罗德摩尔女士交谈。', 38134),
(61558, 'zhCN', '大领主的幻象', '找出伯瓦尔在托加斯特中感知到的东西。', '<萨尔皱起了眉头。>\n\n我们当时正在和大领主谈话，但他陷入了痛苦之中。\n\n他说自己看见了噬渊，有什么东西在典狱长之塔中搅动。\n\n他的痛苦似乎加剧了，我们必须查明他感知到的是什么。', '', '', '', '', '', '', 38134),
(61557, 'zhCN', '黑暗中的回响', '与奥利波斯的伯瓦尔·弗塔根碰头。', '$p，我……我被黑暗的幻象吞没了。有一股力量正从托加斯特中传来。\n\n这种感觉非常熟悉，我很久没有感受到了……\n\n快到奥利波斯来。千万不要耽搁。', '', '伯瓦尔·弗塔根的声音传入你的脑海。', '大领主伯瓦尔·弗塔根', '', '', '与奥利波斯的伯瓦尔·弗塔根碰头。', 38134),
(61556, 'zhCN', '派对捣蛋客', '', '', '', '', '', '', '', '', 38134),
(61555, 'zhCN', '大审判官威塔尔', '', '', '', '', '', '', '', '', 38134),
(61553, 'zhCN', '出击之选', '使用指挥台，派遣你的部队出发冒险。', '荒猎团有义务猎捕阻挠我们使命的恶徒，确保边疆的安全。这场危机愈演愈烈，我们对女王和林地的义务也格外沉重。\n\n这就是我来此的原因。我有义务察觉到无穷森林中的所有行径。我负责部署我们的资源，成为女王的助力。\n\n请允许我展示一下……', '', '', '', '', '', '', 38134),
(61552, 'zhCN', '猎人的守望', '激活冒险并寻找守护者暮花。', '守护是荒猎团的职责。他们眼观四路，耳听八方。\n\n守护者暮花负责为森林的眼睛和耳朵提供指引。\n\n壮大我们的势力，到她那里寻求指导。', '', '', '', '', '', '寻找守护者暮花。', 38134),
(61551, 'zhCN', '粉碎他们', '使用安西德的炸药消灭执行者博尔古拉。', '系泊平台是进入浮空城的最佳地点。不过，还有一场战斗在等待着我们。\n\n执行者博尔古拉监管着这里所有的工作细节。我们需要消灭她，才能顺利地执行计划。\n\n她是个可怕的敌人。不过安西德好像还带着一些炸弹……我们可以好好利用这一点。', '', '骨胶锻造厂的监工。', '执行者博尔古拉', '', '', '在骨胶锻造厂与卡莱克会面。', 38134),
(61550, 'zhCN', '增强羁绊', '使用羁绊熔炉激活你的灵魂羁绊力量。', '我知道你的感受……知道你为你的世界所忍受的痛苦。\n\n你现在也知道了我获得的胜利和付出的惨痛代价。\n\n我们的羁绊会随时间不断增强。但现在，我们先来看看这份羁绊给了我们什么帮助吧。\n\n炽蓝仙野需要我们。我们必须尽一切努力来拯救它。', '', '', '', '', '', '', 38134),
(61548, 'zhCN', '助我脱困', '在一名骨胶铁腕战士身上找到骨胶钥匙，并用它救出安西德。', '安西德被工厂守卫俘虏了。\n\n看来他们已经料到我们会去那里，完全打乱了我们出其不意的计划。女男爵一定会对我的失算很失望。\n\n没有安西德就无法实行这个计划。那些士兵中一定有人带着牢笼的钥匙。\n\n要是你能找到钥匙，我们就能继续执行女男爵的意志。', '', '', '', '', '', '', 38134),
(61543, 'zhCN', '草药学：摇曳的慰魂之光', '', '', '', '', '', '', '', '', 38134),
(61542, 'zhCN', '羁绊恩惠', '完成灵魂羁绊仪式。', '<翩翩把手抱在胸前，做出一副平静的样子。>\n\n与他人缔结的灵魂羁绊并非不可解除，但也不能草率决定。\n\n你们将感受到另一个人的心智与精神，你们的希冀、恐惧、梦想、回忆。所有的一切都会彼此分享。\n\n只要这份羁绊存续，你们就会互相交织，即使相隔万里也不会分离。\n\n仪式完成之后，你们之间的联系会超越物理空间，让你们俩都获得强化。', '', '', '', '', '', '', 38134),
(61541, 'zhCN', '羁绊熔炉', '与羁绊熔炉的翩翩碰面。', '$n，你准备好面对另一个礼物了吗？\n\n我们赐予你的下一件恩惠就是灵魂羁绊。你已经投身于我们的事业，我们也将与你联结。随着灵魂羁绊如一，我们也将更为强大。\n\n<月莓凑近了一点。>\n\n听起来怪吓人的吧？别担心！你和你的灵魂羁绊会强化彼此。\n\n当我寻求志愿者的时候，许多法夜都自告奋勇。我选出了你会喜欢的人，而且灵魂羁绊也对她有好处。\n\n去找翩翩吧。她会帮助你们俩的。', '', '', '', '', '', '与羁绊熔炉的翩翩碰面。', 38134),
(61540, 'zhCN', '见风使舵', '', '', '', '', '', '', '', '', 38134),
(61539, 'zhCN', '巫妖也缝合', '', '', '', '', '', '', '', '', 38134),
(61538, 'zhCN', '采矿：危险的罪钒矿簇', '', '', '', '', '', '', '', '', 38134),
(61526, 'zhCN', '灰烬王庭', '决定灰烬王庭的宾客和设施，然后尽力取悦宾客。', '噬渊行者，只要你准备好了，雷纳索尔王子随时都能主持本周的灰烬王庭。\n\n请和其他人员谈谈，在王庭开始前决定宾客名单和设施。\n\n记住，我们需要给宾客留下好印象，让他们相信我们知道自己在做什么。你需要选择让他们满意的设施！\n\n如果你有些关于宾客的问题，就去问伊林卡女勋爵吧。\n\n准备好了就告诉我，我来开启王庭！', '', '', '', '', '', '', 38134),
(61525, 'zhCN', '灰烬王庭', '决定灰烬王庭的宾客和设施，然后尽力取悦宾客。', '噬渊行者，只要你准备好了，雷纳索尔王子随时都能主持本周的灰烬王庭。\n\n请和其他人员谈谈，在王庭开始前决定宾客名单和设施。\n\n记住，我们需要给宾客留下好印象，让他们相信我们知道自己在做什么。你需要选择让他们满意的设施！\n\n如果你有些关于宾客的问题，就去问伊林卡女勋爵吧。\n\n准备好了就告诉我，我来开启王庭！', '', '', '', '', '', '', 38134),
(61523, 'zhCN', '跑尸', '将白骨遗骸带给屠夫区的拉萨恩。', '这儿要干的活真是太多了，嗯哼。\n\n附近有一具庞大的遗骸，我想从它身上回收一些部件。\n\n你也许能跟着我来，帮我把它拖给拉萨恩，他一定会喜欢的，嗯哼。\n\n我还可以载着你往返，毕竟你也不是个大块头。\n\n准备好出发了吗？', '', '', '', '', '', '', 38134),
(61522, 'zhCN', '跑尸', '将白骨遗骸带给屠夫区的拉萨恩。', '这儿要干的活真是太多了，嗯哼。\n\n附近有一具庞大的遗骸，我想从它身上回收一些部件。\n\n你也许能跟着我来，帮我把它拖给拉萨恩，他一定会喜欢的，嗯哼。\n\n我还可以载着你往返，毕竟你也不是个大块头。\n\n准备好出发了吗？', '', '', '', '', '', '', 38134),
(61520, 'zhCN', '念魔姬的敌人', '', '', '', '', '', '', '', '', 38134),
(61518, 'zhCN', '跟我走', '跟随导航员泽妮尔前往集合点。', '假如其他人都就位了，我们就能盗取浮空城了。\n\n我们两个必须赶快前进，去和安西德会和。卡莱克完成任务后就会与维恩会和。\n\n来吧，$p，跟紧我。我可以让你笼罩在阴影中，只要你待在我身边。', '', '', '', '', '', '', 38134),
(61511, 'zhCN', '遗留之物', '在雷文德斯使用乔迪寻找并收集$2oa份宝贵的回收品。', '乔迪好无聊。乔迪想要去冒险！你也想一起去，是吗？\n\n那些高大建筑里的白皮人手里有许多亮闪闪的东西，还有幸运符。我们去那里亲自找找看吧！\n\n准备好出发了吗？', '', '', '', '', '', '', 38134),
(61510, 'zhCN', '满载而归', '在炽蓝仙野使用乔迪寻找并收集$2oa份宝贵的回收品。', '乔迪想要去冒险！你也想一起去，是吗？\n\n我到处拾捡物品、武器、护甲还有宝贵的幸运符！不过，这几天幸运符好像有点紧缺了。\n\n我们去树人的地盘看看吧。我们俩要是能一起搜寻的话，肯定能找到很多幸运符！', '', '', '', '', '', '', 38134),
(61492, 'zhCN', '王子护卫', '复苏“刃墙”切尔拉，将敌人赶出暗墙之塔。', '雷纳索尔虽然看起来风度翩翩，但他其实掩饰着巨大的痛苦。德纳修斯给他留下的伤至今没有痊愈。\n\n他其实非常脆弱。\n\n他希望派你带着旗帜占领他过去的堡垒，也就是暗墙之塔。他想凭借自己的力量来震慑敌人。这样的战术欠缺考量。\n\n带上这点心能，让“刃墙”切尔拉苏醒过来。她最后一次露面就是在暗墙之塔。复苏她，她就能协助你占领高塔。', '', '', '', '', '', '与暗墙之塔的“刃墙”切尔拉交谈。', 38134),
(61490, 'zhCN', '信息战争', '在玛卓克萨斯的祭仪密院、晋升堡垒的万世档案所、炽蓝仙野的瓦尔仙林和雷文德斯的纳斯利亚堡处放置憎恨之眼。', '啊，圣礼之油。我还以为是阿得赖斯提斯招的新兵呢。\n\n没关系。为了你的试炼，你需要我的记忆，对吗？恐怕光靠圣礼之油是不够的。\n\n我们的密院陷落之后，我们只能自己照顾自己。虽然这座高塔能够提供一些保护，我们还是被逼得走投无路。\n\n信息是生存的关键。使用我的圣礼护符，让我的双眼遍布所有的国度……甚至晋升堡垒之内。按照要求，准确行事——不准提问题。\n\n然后我们再来聊聊报酬的事情。', '', '', '', '', '', '', 38134),
(61489, 'zhCN', '传闻与异端', '在玛卓克萨斯的锐眼密院找到艾克兹拉。', '玛卓克萨斯曾经的锐眼密院只剩下了残垣断壁，艾克兹拉就驻留在其中一座残余的建筑中。\n\n把圣礼之油带给她，她就会允许你捕获她的记忆。但要小心，她可是因准头好、动作快而闻名的神射手。\n\n祝你好运，噬渊行者。', '', '', '', '', '', '在玛卓克萨斯的锐眼密院找到艾克兹拉。', 38134),
(61488, 'zhCN', '女妖的勇士', '在东瘟疫之地的玛瑞斯农场与纳萨诺斯·凋零者对峙。', '<信使似乎在颤抖。>\n\n我们收到消息……呃，我不知该怎么说……\n\n凋零者被发现了。\n\n部落军队在艾泽拉斯四处搜寻他的踪迹，坚决要让他为罪行付出代价。\n\n我们的一名银色巡逻兵发现了他。他并没藏在什么遥远的地方，只是……在他的老房子等着。\n\n我们已经将他包围了，但，坦白讲我们的胜算并不大。\n\n也许你能召集盟友，将他绳之以法。', '', '女妖之王的勇士。', '纳萨诺斯·凋零者', '', '', '', 38134),
(61486, 'zhCN', '女妖的勇士', '在东瘟疫之地的玛瑞斯农场与纳萨诺斯·凋零者对峙。', '我……我们……我是说……他……\n\n<年轻的信使深吸一口气。>\n\n我们找到他了。我们找到凋零者了。\n\n我就像之前无数次那样巡逻然后看到他就坐在那座老房子的门廊前带着那些可怕的狗然后我发誓他冲我一笑还用那双通红的眼睛盯着我而且……\n\n<年轻的信使擦了擦额头。>\n\n他在玛瑞斯农场。我们的部队已经包围了他，但，呃……\n\n你也许应该召集你的朋友们。我不认为他会投降。', '', '女妖之王的勇士。', '纳萨诺斯·凋零者', '', '', '', 38134),
(61485, 'zhCN', '我只说一次', '与低语者维恩讨论计划。', '我们终于完成了所有准备，可以执行女男爵的计划了。\n\n既然你来了，我会最后复述一次我们扮演的角色。接下来就看我们的命运如何展开吧。\n\n听仔细了。', '', '', '', '', '', '', 38134),
(61479, 'zhCN', '变形恩惠', '陪同月莓女勋爵进入森林之心并参加仪式。', '<月莓神情严肃。>\n\n你虽然只是个凡人，但你决定成为我们的勇士。只要你恪守我们的事业，我们便有义务在圣所大厅中热忱相待，予你恩惠。\n\n作为魅夜王庭的一员，我可以将力量赐予被选中的凡人们。\n\n$n，艾泽拉斯的$c，我欢迎你来到初生之树，阿基亚德仙林，森林之心。\n\n请跟我来。', '', '', '', '', '', '', 38134),
(61478, 'zhCN', '谦逊的开始', '将谦逊之卫带给位于候选者试练场中的掌炉大师索弗涅。', '谦逊之卫完整之身重现，你也许可以劝阿萨诺斯回到竞技场。\n\n去候选者试练场，那个很久以前你旅程开始的地方。找到掌炉大师索弗涅——她会引导所有寻求与阿萨诺斯相遇的人。\n\n把长矛带给她看。她会知道你的意图并进一步引导你。', '', '', '', '', '', '将谦逊之卫带给位于候选者试练场中的掌炉大师索弗涅。', 38134),
