UPDATE `locales_quest` SET `entry` = 12905,`OfferRewardText_loc8` = 'Мне должны были прислать замену для одной из надзирательниц. Так что мы тебя ждали.\n\nДавай сразу же и приступай к работе.' WHERE `locales_quest`.`entry` = 12905;
