INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(63315, 0, 0, 'Добро пожаловать на Арену Истребления, наше первое официально одобренное боевое состязание!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Player'),
(63315, 1, 0, 'Готовы ли вы продемонстрировать здесь, под взором Тигра, свое воинское искусство?', 14, 0, 100, 6, 0, 0, 'Гаргток to Player'),
(63315, 2, 0, 'Тогда бейте в гонг и покажите этим монахам, из чего вы сделаны! Как вы это умеете, хе-хе.', 14, 0, 100, 1, 0, 0, 'Гаргток to Player'),
(63315, 3, 0, 'Первое испытание мастерства и силы! Перед вами Потертый Панцирь, сильнейшая из драконьих черепах!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Потертый Панцирь'),
(63315, 4, 0, 'Мда, вот это каша! Передайте в монастырскую кухню: сегодня у нас на ужин будет черепаховый суп!', 14, 0, 100, 5, 0, 8573, 'Гаргток to Потертый Панцирь'),
(63315, 5, 0, 'Готовы к новой битве? Бейте в гонг и В АТАКУ!', 14, 0, 100, 5, 0, 0, 'Гаргток to Потертый Панцирь'),
(63315, 6, 0, 'Пришел час грозы груммелей, неистового лохматого голиафа, снежного ужаса вершины Кунь-Лай – Джол''Грума!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Джол''Грум'),
(63315, 7, 0, 'Ох-х! Да, с нашим снежным Джол''Грумом смертоносная троица обошлась совсем не с-нежно! Кстати, о готовности...', 14, 0, 100, 5, 0, 8573, 'Гаргток to Джол''Грум'),
(63315, 8, 0, 'Она здесь для того, чтобы завести новых друзей... а затем поджечь их! Маленькая шутница, самая хорошенькая пироманьячка Пандарии – Люян!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Малышка Люян'),
(63315, 9, 0, 'Загасили, как свечку! Хоть кто-нибудь может противостоять этим троим?!', 14, 0, 100, 5, 0, 8573, 'Гаргток to Малышка Люян'),
(63315, 10, 0, 'Кто сказал, что это разъяренный як?! Это мастер топора Чагань Огненное Копыто, странствующий воин Танлунских степей!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Чаган Огненное Копыто'),
(63315, 11, 0, 'Вот это битва! Наши бравые воители угостили Чагана здоровенной порцией шашлыка из яунголины.', 14, 0, 100, 5, 0, 8573, 'Гаргток to Player'),
(63315, 12, 0, 'Он сквозь туман летит и смерть врагам сулит, он спокоен, но с ним не балуй: ассасин, урожденный цзинь-юй – Сатай Бюй!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Сатай Бюй'),
(63315, 13, 0, 'Да, сколько ни бился наш хвостатый убийца как рыба об лед, пришел ему конец.', 14, 0, 100, 5, 0, 8573, 'Гаргток to Player'),
(63315, 14, 0, 'С какой злобой и жестокостью его скинули с Пика Безмятежности, этого чокнутого пустобреха, жрущего бананы, точно обезьяна, мастера-монаха сокрушителя облаков Кобо!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Player'),
(63315, 15, 0, 'Какая потрясающая и кровавая битва! Трое наших героев наглядно объяснили Кобо, что мартышкам не место на Арене Истребления!', 14, 0, 100, 5, 0, 8573, 'Гаргток to Player'),
(63315, 16, 0, 'Крошит и кромсает, пощады не знает! Поприветствуйте поклоном Маки Водореза, саурока-самурая!', 14, 0, 100, 5, 0, 8574, 'Гаргток to Player'),
(63315, 17, 0, 'Благородный Маки встретил достойный конец... Уверен, его клан гордился бы им, не будь он последним из них!', 14, 0, 100, 5, 0, 8573, 'Гаргток to Player'),
(63315, 18, 0, 'Давайте все вместе поприветствуем новых чемпионов Арены Истребления!', 14, 0, 100, 4, 0, 0, 'Гаргток to Player'),
(63312, 0, 0, '%s злится!', 41, 0, 100, 0, 0, 0, 'Джол''Грум to Джол''Грум'),
(63313, 0, 0, '%s начинает призывать |cFFFE9A2Eпитающих пламя духов|r! Уничтожайте их, чтобы остановить стену огня!', 41, 0, 100, 0, 0, 0, 'Малышка Люян to Малышка Люян'),
(64280, 0, 0, 'Я призываю воды сссвоего родного края... Доставьте ко мне моих врагов!', 14, 0, 100, 0, 0, 0, 'Маки'),
(64281, 0, 0, 'Фум-м!', 12, 0, 100, 2, 0, 0, 'Сатай Бюй to Player');