UPDATE `quest_template_locale` SET `LogDescription`='Instrukteur Razuvious in der Schwarzen Festung befiehlt Euch, mithilfe Eurer Fertigkeit \'Runenschmieden\' aus Eurem Zauberbuch eine Rune in Eure runenbeschriebene Seelenklinge zu prägen.$B$BIhr könnt die Gravur auf Eurer Waffe jederzeit durch eine andere ersetzen, solange Ihr Euch in der Nähe einer Runenschmiede befindet.', `VerifiedBuild`=31478 WHERE (`ID`=12842 AND `locale`='deDE');
