UPDATE `quest_template_locale` SET `QuestDescription`='Mit dem immer näherrückenden Krieg in Ahn\'Qiraj gilt es alles Erdenkliche zu tun, um unsere Soldaten mit allem zu versorgen, das sie brauchen werden. Wir kümmern uns hier genau um diese Sache. Ich bin verantwortlich für das Ansammeln von Seidenstoffverbänden.$B$BGlaubt Ihr, dass Ihr mir dabei helfen könntet, $C?', `VerifiedBuild`=31478 WHERE (`ID`=8520 AND `locale`='deDE');
