INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'zhCN', '', '欢迎，朋友。你对钥石有兴趣吗？', 38134),
(209048, 'zhCN', '在奥利波斯，侵攻者佐·达什可以满足你的需求。你也可以在自己的盟约圣所里找到可以进行物品升级的人。', '在奥利波斯，侵攻者佐·达什可以满足你的需求。你也可以在自己的盟约圣所里找到可以进行物品升级的人。', 38134),
(208581, 'zhCN', '（用户名），你好。我希望你今天过得很（生成一个随机的褒义形容词）。', '', 38134),
(208300, 'zhCN', '我很高兴能出力帮助年轻的灵魂。巴宾斯很快就能痊愈了！', '', 38134),
(208299, 'zhCN', '我常说，要纠正不良习气，最好的办法就是举止礼貌，交友良善，而且起的名字要跟“纳宾斯”押韵！', '', 38134),
(208298, 'zhCN', '指控者刚刚把我派给了西塔尔。他会帮我意识到我之前的错误！', '', 38134),
(208297, 'zhCN', '按巴宾斯的提议，这茶里加入了几种新材料。希望你喜欢！', '', 38134),
(208292, 'zhCN', '向你致敬，我亲爱的$p。\n\n<巴宾斯盯着他手背上潦草的字。>\n\n我很高兴认识你。西塔尔经常跟我提到你。\n\n我可以给你倒杯茶吗？还是给你提供一把可爱的阳伞，好让你趁着这美妙的天气出门散步？\n\n<巴宾斯在等待你的答复时，非常努力地想让他的微笑看起来自然一点。>', '', 38134),
(208211, 'zhCN', '我以前的主人地穴看守者卡希尔想在赎罪大厅见你，$p。', '', 38134),
(208205, 'zhCN', '格雷希特一直都在赎罪大厅挂念着你，$p。', '', 38134),
(208120, 'zhCN', '有些日子非常特殊！你看到我这儿的金色了吧，今天是个特殊的日子！碰到这样的日子，我们就会为一名执事者庆祝。\n\n你可以问我这名执事者是谁，咕咕，然后把熟透的莲榴送给他们，就能打开箱子了！耶！', '', 38134),
(208019, 'zhCN', '我们的一员。我们的一员。我们的一员……', '', 38134),
(208018, 'zhCN', '文官阿得赖斯提斯哼着音乐，跺着脚。', '', 38134),
(208016, 'zhCN', '', '不管你去到什么地方，我都会注视着你。', 38134),
(208014, 'zhCN', '', '朋友多，欢乐多。西卡运气真好。', 38134),
(208013, 'zhCN', '唔……$g他:她;是我的挚友学徒……所以头衔应该是叫挚徒？友徒？不不不……', '', 38134),
(208012, 'zhCN', '团结就是力量。愿我们永不分离。', '', 38134),
(208011, 'zhCN', '', '既然是死亡将我们聚在了一起，那要什么力量才能让我们分离？', 38134),
(208010, 'zhCN', '大家都闭嘴，听$p说话。我最好的朋友对战斗非常了解，比你们全加起来都懂！', '', 38134),
(208009, 'zhCN', '', '树木在森林里更能繁荣生长，$p。快回到我的林地，我们就能一起茁壮成长。', 38134),
(208008, 'zhCN', '丘法开始慢慢地，轻柔地反复重复着一句话。', '', 38134),
(208007, 'zhCN', '所以我们说，$p是所有泥仆最好的朋友！', '', 38134),
(208006, 'zhCN', '我们现在有好多朋友，但最好的只有一个……', '', 38134),
(208005, 'zhCN', '我其实非常期待下一次灰烬王庭。也许我还会接受那位贵族跳舞的邀请……', '', 38134),
(208004, 'zhCN', '', '总有下一场狩猎，但$p只有一个。', 38134),
(208002, 'zhCN', '', '祝贺你，我美丽的$p。你的王庭就和你一样，魅力非凡。', 38134),
(208000, 'zhCN', '', '我们下次去惹点什么麻烦，$p？我快等不及了！', 38134),
(207999, 'zhCN', '', '别让我耽搁了你别的事情，我的小$p。', 38134),
(207996, 'zhCN', '保重，$p。总有救不完的灵魂，但别忘了，有些灵魂早已经迷失。', '', 38134),
(207951, 'zhCN', '错误。用户在获得更多经验之前无法使用此功能。', '', 38134),
(207923, 'zhCN', '炽蓝仙野还有很多事情没有完成，$n。', '炽蓝仙野还有很多事情没有完成，$n。', 38134),
(207903, 'zhCN', '', '想听故事吗？', 38134),
(207841, 'zhCN', '$n！这边，伙计！', '', 38134),
(207504, 'zhCN', '今天的风很适合飞行！你想去哪儿？', '', 38134),
(207499, 'zhCN', '', '<伊林卡女勋爵笑了。>\n\n问得好。每周都调整宾客名单是很明智的，这样可以确保你的客人彼此合拍。\n\n跟加里丹勋爵谈一谈，浏览你的宾客名单，就可以取消对任何客人的邀请。\n\n我们可能得花点注能红宝石来缓和一下撤销邀请造成的关系问题。但你也可以雇佣大使，或者从我这里购买空白的请柬来支付这笔开销。\n\n哦，一定要努力避免把喜欢相反气氛的客人邀请到一起。这样要让他们都很高兴就很难。', 38134),
(207446, 'zhCN', '团结是个与众不同的构造体。顽强不屈的使命感让她与暗影界建立了羁绊，为玛卓克萨斯的不朽军团完成任务时，她可以找到象征着我们在暗影界的使命的护甲。', '团结是个与众不同的构造体。顽强不屈的使命感让她与暗影界建立了羁绊，为玛卓克萨斯的不朽军团完成任务时，她可以找到象征着我们在暗影界的使命的护甲。', 38134),
(207442, 'zhCN', '', '我知道。等你的朋友到了奥利波斯，我会告诉你的。\n\n他安全后你再回来找我吧。', 38134),
(207434, 'zhCN', '是你吗，西塔尔？', '', 38134),
(207388, 'zhCN', '', '有什么需要吗？', 38134),
(207376, 'zhCN', '你选择了礼节而不是武力，真是耻辱，噬渊行者。', '你选择了礼节而不是武力，真是耻辱，噬渊行者。', 38134),
(207372, 'zhCN', '有像你这样无畏的效力者是法夜的幸运，噬渊行者。', '有像你这样无畏的效力者是法夜的幸运，噬渊行者。', 38134),
(207371, 'zhCN', '看起来荒野召唤着你为它效力，$n。伟大的呼唤。', '看起来荒野召唤着你为它效力，$n。伟大的呼唤。', 38134),
(207370, 'zhCN', '尽管你没有加入我们的盟约，但你也是在为伟大的循环服务，噬渊行者。这是高尚的职责。', '尽管你没有加入我们的盟约，但你也是在为伟大的循环服务，噬渊行者。这是高尚的职责。', 38134),
(207369, 'zhCN', '向寒冬女王致敬，她是执政官真正的盟友。', '向寒冬女王致敬，她是执政官真正的盟友。', 38134),
(207368, 'zhCN', '愿你收割能带给你……满足，也希望你能为有意义的事效力，$n。', '愿你收割能带给你……满足，也希望你能为有意义的事效力，$n。', 38134),
(207367, 'zhCN', '噬渊行者，愿你的指引能帮助不羁的灵魂卸下重担。', '噬渊行者，愿你的指引能帮助不羁的灵魂卸下重担。', 38134),
(207366, 'zhCN', '温西尔们明白，灵魂的过去是一种负担。我们的盟约有很多共同点，$n。', '温西尔们明白，灵魂的过去是一种负担。我们的盟约有很多共同点，$n。', 38134),
(207365, 'zhCN', '你选择了效忠温西尔？指引灵魂获得救赎确实是一项有价值的任务。', '你选择了效忠温西尔？指引灵魂获得救赎确实是一项有价值的任务。', 38134),
(207364, 'zhCN', '去吧，继续效力……不管是为了谁。', '去吧，继续效力……不管是为了谁。', 38134),
(207363, 'zhCN', '你仍然受到执政官的眷顾，无论你选择了哪个盟约。', '你仍然受到执政官的眷顾，无论你选择了哪个盟约。', 38134),
(207362, 'zhCN', '愿你能给纷争的国度带去秩序，$n。', '愿你能给纷争的国度带去秩序，$n。', 38134),
(207361, 'zhCN', '噬渊行者，我原以为相比于战斗，你更适合服务。或许是我错了。', '噬渊行者，我原以为相比于战斗，你更适合服务。或许是我错了。', 38134),
(207360, 'zhCN', '你的服务给我们的盟约带来荣耀，噬渊行者。', '你的服务给我们的盟约带来荣耀，噬渊行者。', 38134),
(207359, 'zhCN', '看到我们的道路有所交集，我很高兴，$n。感谢你的奉献。', '看到我们的道路有所交集，我很高兴，$n。感谢你的奉献。', 38134),
(207358, 'zhCN', '啊，是噬渊行者！你肯定是在为执政官做事。去吧，继续服务。', '啊，是噬渊行者！你肯定是在为执政官做事。去吧，继续服务。', 38134),
(207357, 'zhCN', '我们很幸运，能有你与我们并肩作战，$n。愿执政官的恩惠指引着你。', '我们很幸运，能有你与我们并肩作战，$n。愿执政官的恩惠指引着你。', 38134),
(207356, 'zhCN', '如果你厌倦了和那些法夜嬉戏，就来我们这里活动一番吧。', '如果你厌倦了和那些法夜嬉戏，就来我们这里活动一番吧。', 38134),
(207355, 'zhCN', '请代我们向寒冬女王致意，噬渊行者。', '请代我们向寒冬女王致意，噬渊行者。', 38134),
(207354, 'zhCN', '我们的树林虽然没有炽蓝仙野的那么宏伟，但我们也是在为我们国度的存亡而战。', '我们的树林虽然没有炽蓝仙野的那么宏伟，但我们也是在为我们国度的存亡而战。', 38134),
(207353, 'zhCN', '法夜不是在争夺力量？那他们在那乱糟糟的林地里干什么？', '法夜不是在争夺力量？那他们在那乱糟糟的林地里干什么？', 38134),
(207352, 'zhCN', '你能站在我们的盟约这边真是太好了，$n。你穿丝绒的样子一定很好看。', '你能站在我们的盟约这边真是太好了，$n。你穿丝绒的样子一定很好看。', 38134),
(207351, 'zhCN', '我与收割者之庭共进退，当然还有你，噬渊行者。', '我与收割者之庭共进退，当然还有你，噬渊行者。', 38134),
(207350, 'zhCN', '这种鲁莽行为真令人惊异，你一定是在为你的罪碑进行收集！', '这种鲁莽行为真令人惊异，你一定是在为你的罪碑进行收集！', 38134),
(207349, 'zhCN', '$n，有了你的帮助，我们将重拾崇高的使命。', '$n，有了你的帮助，我们将重拾崇高的使命。', 38134),
(207348, 'zhCN', '我猜格里恩的罪碑一定是所有盟约里最平凡的。', '我猜格里恩的罪碑一定是所有盟约里最平凡的。', 38134),
(207347, 'zhCN', '雷文德斯也许是个阴暗的地方，格里恩，但我们的职责和你们的一样高尚。', '雷文德斯也许是个阴暗的地方，格里恩，但我们的职责和你们的一样高尚。', 38134),
(207345, 'zhCN', '你们的执事者看起来挺训练有素的，$n。唔，我们有一些地穴需要清洁……', '你们的执事者看起来挺训练有素的，$n。唔，我们有一些地穴需要清洁……', 38134),
(207343, 'zhCN', '尽管方式不同，但我们的盟约都在努力指引灵魂走上高尚的道路。', '尽管方式不同，但我们的盟约都在努力指引灵魂走上高尚的道路。', 38134),
(207342, 'zhCN', '你选择走上了一条残忍的道路，凡人。如果你需要一些手段，你知道去哪儿找我们。', '你选择走上了一条残忍的道路，凡人。如果你需要一些手段，你知道去哪儿找我们。', 38134),
(207340, 'zhCN', '灰烬王庭比传言中更危险，你会爱上它的。', '灰烬王庭比传言中更危险，你会爱上它的。', 38134),
(207339, 'zhCN', '你的盟约为守护我们的国度而战，这真是太好了，噬渊行者。', '你的盟约为守护我们的国度而战，这真是太好了，噬渊行者。', 38134),
(207338, 'zhCN', '', '就算侯爵不在了，也不意味着我们的努力就会停止。我们迟早会报仇的。', 38134),
(207337, 'zhCN', '拿走你的手术刀和针，通灵领主。我可不希望你对我开刀。', '拿走你的手术刀和针，通灵领主。我可不希望你对我开刀。', 38134),
(207335, 'zhCN', '有什么东西在你后面，$n。那是……法夜的亮粉吗？', '有什么东西在你后面，$n。那是……法夜的亮粉吗？', 38134),
(207334, 'zhCN', '噬渊行者！你厌倦了修剪树丛吧？想要来一场真正的搏斗吗？', '噬渊行者！你厌倦了修剪树丛吧？想要来一场真正的搏斗吗？', 38134),
(207333, 'zhCN', '通灵领主更喜欢策略，而不是小花招，法夜。', '通灵领主更喜欢策略，而不是小花招，法夜。', 38134),
(207332, 'zhCN', '不，我不会参与你的计划。', '不，我不会参与你的计划。', 38134),
(207331, 'zhCN', '让灵魂摆脱骄傲的最好方法就是将其击败。', '让灵魂摆脱骄傲的最好方法就是将其击败。', 38134),
(207330, 'zhCN', '当心脚下，温西尔。你会弄脏你的鞋的。', '当心脚下，温西尔。你会弄脏你的鞋的。', 38134),
(207329, 'zhCN', '派对、诡计。为什么温西尔们不能自己来战斗呢？', '派对、诡计。为什么温西尔们不能自己来战斗呢？', 38134),
(207328, 'zhCN', '确实，我的生活里充满了战争……可是我的记忆依然属于我。', '确实，我的生活里充满了战争……可是我的记忆依然属于我。', 38134),
(207327, 'zhCN', '这里没有冥想池，格里恩。', '这里没有冥想池，格里恩。', 38134),
(207326, 'zhCN', '来吧，向我展示你在晋升堡垒学到的漂亮招式。', '来吧，向我展示你在晋升堡垒学到的漂亮招式。', 38134),
(207324, 'zhCN', '我会邀请你们前来伤逝剧场，但你们的衣服可能会弄脏。', '我会邀请你们前来伤逝剧场，但你们的衣服可能会弄脏。', 38134),
(207323, 'zhCN', '当你的肉体破败不堪时，我们可以给你重塑一个。', '当你的肉体破败不堪时，我们可以给你重塑一个。', 38134),
(207321, 'zhCN', '你是兵主传承的体现，$n。', '你是兵主传承的体现，$n。', 38134),
(207320, 'zhCN', '有你陪在我们身边真好，$n。其他的盟约会嫉妒你的力量。', '有你陪在我们身边真好，$n。其他的盟约会嫉妒你的力量。', 38134),
(207319, 'zhCN', '无论命运怎样，最终都要通过战斗来赢得一切。', '无论命运怎样，最终都要通过战斗来赢得一切。', 38134),
(207318, 'zhCN', '老格里伏塔从没见过这样的地方！\n\n<格里伏塔向你招了招手，上前倾身说起悄悄话。>\n\n嘘！小声点！\n\n这些掮灵会和我们“凡人”交易一切，不过我还没弄清楚这些东西都是什么。\n\n说不定老格里伏塔能找到些新的护符然后拿去卖呢？哈哈。', '', 38134),
(207316, 'zhCN', '啊，又一个来自艾泽拉斯的灵魂。\n\n不，不是灵魂。安瑟的光芒依然温暖着你的躯体。\n\n但愿我能最后再看一眼至高岭，希望大地母亲愿意让我的家人继续留在那里。\n\n这里的许多人毫无耐心可言，只想着快点晋升，而且还被过去的悔恨束缚着。对我来说，能享受我所珍视的记忆，我很满足。我希望能得到他们的重视，尽管总有一天我也会放下他们。\n\n但不是今天，绝对不是。', '', 38134),
(207315, 'zhCN', '我认得你，我们曾在至高岭并肩战斗，别来无恙啊。\n\n我希望库拉和孩子们没有沉溺于悲伤之中，我也希望库拉能得到爱。但就算如此，我还是想保留一些记忆。爱抚库拉、为我们的小儿子取名以及教给麦查如何拿着刀像鬼魂一样行动。像鬼魂一样——\n\n我不记得我遭遇了什么事，一切都发生在一瞬间。达古尔怎么样了？\n\n不，不，别告诉我。这是前世的事儿。这种愤怒，这种痛苦——这就是为什么格里恩要我们卸下重担。', '', 38134),
(207314, 'zhCN', '在享受这永恒的暮光吗，温西尔？', '在享受这永恒的暮光吗，温西尔？', 38134),
(207313, 'zhCN', '我永远也理解不了温西尔为什么这么痴迷于石头。', '我永远也理解不了温西尔为什么这么痴迷于石头。', 38134),
(207312, 'zhCN', '我们要保持警惕，全靠你了，通灵领主。', '我们要保持警惕，全靠你了，通灵领主。', 38134),
(207311, 'zhCN', '兵主的盟友就是森林的盟友。', '兵主的盟友就是森林的盟友。', 38134),
(207310, 'zhCN', '我们都必须为自己的盟约效力。', '我们都必须为自己的盟约效力。', 38134),
(207309, 'zhCN', '愿你张开双翼翱翔在风中，格里恩。', '愿你张开双翼翱翔在风中，格里恩。', 38134),
(207308, 'zhCN', '拥有你是魅夜王庭的幸运，$n。', '拥有你是魅夜王庭的幸运，$n。', 38134),
(207307, 'zhCN', '噬渊行者，你能选择我们的盟约，这让我们很感激。为了女王和林地。', '噬渊行者，你能选择我们的盟约，这让我们很感激。为了女王和林地。', 38134),
(207306, 'zhCN', '这里的灵魂不会让你感觉到高傲的，温西尔。', '这里的灵魂不会让你感觉到高傲的，温西尔。', 38134),
(207305, 'zhCN', '荒猎团与你同在，通灵领主。', '荒猎团与你同在，通灵领主。', 38134),
(207304, 'zhCN', '英勇前行吧，格里恩。', '英勇前行吧，格里恩。', 38134),
(207302, 'zhCN', '愿暗夜拥抱你，温西尔。你肯定已经得到暗夜的恩惠了。', '愿暗夜拥抱你，温西尔。你肯定已经得到暗夜的恩惠了。', 38134),
(207301, 'zhCN', '你会在森林里呆很久吗，通灵领主？', '你会在森林里呆很久吗，通灵领主？', 38134),
(207300, 'zhCN', '祝你好运，格里恩。希望晋升堡垒在心能枯竭期间不会有事。', '祝你好运，格里恩。希望晋升堡垒在心能枯竭期间不会有事。', 38134),
(207299, 'zhCN', '你对林地恩情深重，$n！', '你对林地恩情深重，$n！', 38134),
(207298, 'zhCN', '没想到在这遇见你，温西尔。我真的没想到。', '没想到在这遇见你，温西尔。我真的没想到。', 38134),
(207297, 'zhCN', '那是什么气味？噢，是一位通灵领主。我说呢。', '那是什么气味？噢，是一位通灵领主。我说呢。', 38134),
(207295, 'zhCN', '噢，你是一个格里恩！不过你没有翅膀……和我不一样！', '噢，你是一个格里恩！不过你没有翅膀……和我不一样！', 38134),
(207294, 'zhCN', '噢，是$n！我非常欣赏你的表现！', '噢，是$n！我非常欣赏你的表现！', 38134),
(207289, 'zhCN', '想想看，我还有不到一年就能拿到头盔了……\n\n<海军上将泰勒叹了口气，尽管他现在已经不用呼吸了。>', '', 38134),
(207288, 'zhCN', '不用感谢我。', '', 38134),
(207287, 'zhCN', '我记得杀了我的人就是你。', '', 38134),
(207286, 'zhCN', '你呢，库尔迪拉？关于你的死亡，你还记得什么？', '', 38134),
(207285, 'zhCN', '推测这件事没有意义。已经发生的事情无法改变，死亡无法阻挡，我们必须向前看。', '', 38134),
(207284, 'zhCN', '我明白了。你认为仲裁官会为我们选择什么样的命运？', '', 38134),
(207283, 'zhCN', '就算是，我也不记得了。', '', 38134),
(207282, 'zhCN', '你回忆起死亡时发生的事了吗，萨萨里安？格里恩来接你的灵魂了吗？', '', 38134),
(207281, 'zhCN', '说得好。', '', 38134),
(207280, 'zhCN', '或是希尔瓦娜斯·风行者。', '', 38134),
(207279, 'zhCN', '比如吉恩·格雷迈恩？', '', 38134),
(207278, 'zhCN', '伯瓦尔·弗塔根不再是巫妖王了。但如果要选择听从谁的命令，很多领袖都比他糟糕多了。', '', 38134),
(207277, 'zhCN', '而现在我们为了它的持有者而战斗。', '', 38134),
(207276, 'zhCN', '统御头盔已经碎裂，而我有种奇怪的想法。长期以来，统御头盔一直是我们诅咒和责任的象征。', '', 38134),
(207272, 'zhCN', '你知道我是怎么死的吗，$n？在保卫奎尔萨拉斯期间，我听命于游侠将军希尔瓦娜斯·风行者。\n\n为了拯救她的国家和人民，她付出了生命，也正因如此，她被复活成了一个亡灵。和我一样的遭遇，但之后的道路完全不同。\n\n希尔瓦娜斯造成的损失与苦难，比我们中任何人一生所遭受到的还要多。我促成了短暂的休战，阻止了更多流血事件，但之后她把我囚禁在幽暗城中，就这样过了很多年。\n\n即使是黑锋骑士团也没法说服她放我出来。不管你怎么看待这位女妖之王，无论在生前还是身后，她一直都不是个宽厚的人。\n\n希尔瓦娜斯绝不是容易应付的对手。从来都不是。', '', 38134),
(207271, 'zhCN', '我第一次拿起武器，是在亡灵瘟疫开始席卷洛丹伦的时候。在那之后，我在亡者国度服役的时间已经远长于我作为士兵活着的时间了。\n\n我在帷幕的另一侧瞥见过那里的部队，面对这些部队，生者之间的战争简直微不足道。我始终效忠联盟，除非我最终倒下，否则我会一直守护联盟的人民。但比起部落来说，世界上还有更可怕的东西会威胁到联盟的安全。\n\n或许他们现在已经明白了，随着冰冠冰川的天空被撕裂，死亡的气息已经弥漫在所有人的身边。这就是生者的诅咒。\n\n他们根本不知道什么才是重要的，直到大限将至。', '', 38134),
(207270, 'zhCN', '我希望自己能做个敦实的构造体！每当跳跃的时候，极具重量感的冲击能击碎地面。', '', 38134),
(207269, 'zhCN', '我想要一个结实的框架，上面有很多钩子，可以挂东西。我会带着你和所有的研究材料一起踏上冒险旅途！', '', 38134),
(207268, 'zhCN', '', '<菲利亚低声对你说。>\n\n请别跟艾玛说噬渊的事。\n\n我……觉得她的心脏可能承受不了。', 38134),
(207266, 'zhCN', '', '哦，你好。你好像对这儿很熟。\n\n你看到我的几个儿子，杰姆、杰克和雅克了吗？他们应该就在这附近。\n\n我没找到我的几个儿子是不会走的。我绝不会再失去他们！', 38134),
(207251, 'zhCN', '我尊敬所有为我们的事业服务的人。', '我尊敬所有为我们的事业服务的人。', 38134),
(207250, 'zhCN', '<斯塔尼突然插嘴。>\n\n因为你没在用。我只是……借一下！', '<斯塔尼突然插嘴。>\n\n因为你没在用。我只是……借一下！', 38134),
(207248, 'zhCN', '<斯塔尼科斯盯着你。>\n\n你的东西。你摸过的东西。对。', '<斯塔尼科斯盯着你。>\n\n你的东西。你摸过的东西。对。', 38134),
(207247, 'zhCN', '你是说……我的收藏？很棒，是吧？', '你是说……我的收藏？很棒，是吧？', 38134),
(207241, 'zhCN', '不！你躲不掉的，威·娜莉！你——啊！', '', 38134),
(207231, 'zhCN', '', '什么？', 38134),
(207230, 'zhCN', '我已经懂得事实胜于雄辩这个道理了，噬渊行者。来，我们必须证明我们的意图。', '', 38134),
(207229, 'zhCN', '树倒猢狲散……审判官们也不例外。', '', 38134),
(207228, 'zhCN', '', '迦西吉的路通向许多黑暗的深渊……你会在那些地方找到什么呢？', 38134),
(207227, 'zhCN', '', '你们的王国和部族与这里毫无关系。放下昔日的忠诚，从那段经历里走出来吧！', 38134),
(207226, 'zhCN', '', '你的凡人之力在这里毫无意义。敲响钟，看看你究竟有多么脆弱吧。', 38134),
(207225, 'zhCN', '', '拥抱你们的耻辱。回顾压在你们身上每一段不堪回首的记忆。只有彻底面对，你们才能将其克服。', 38134),
(207224, 'zhCN', '', '无论愿意与否，你们的不公正行为都将在这里遭到谴责。你只能选择是否要从这种体验中进行学习。', 38134),
(207223, 'zhCN', '', '什么？！', 38134),
(207222, 'zhCN', '', '什么？', 38134),
(207221, 'zhCN', '是的，我想你会惊讶地发现，在充斥着暴君和王族的雷文德斯，居然还有一个像我这样的人。\n\n我这样宁静的灵魂很容易被忽视。\n\n事实上，我的功绩不值一提。\n\n但我的言语可就是另一回事了。\n\n通过闲言碎语，我说服无数人以我的名义、为了我的目的而做出会危害自身的暴行。\n\n看起来温西尔不喜欢这种阴谋诡计。', '', 38134),
(207220, 'zhCN', '传奇的噬渊行者会要求我这样卑微的灵魂做什么？', '', 38134),
(207219, 'zhCN', '<维力穆笑了起来。>\n\n我还有什么没做过，凡人？\n\n我还未接受被这些温西尔称为罪过的所有事物都是错误的，不过光是那些我们目前已经认同的罪过，也足够把我罪碑的前后两面都刻满了。\n\n<维力穆短暂地陷入沉思。>\n\n我……我很感激看护者潘查对我的耐心。我见过这里的其他人是如何对待他们看护的灵魂的。\n\n我真的努力在改了。', '', 38134),
(207218, 'zhCN', '你曾经独自造访过死者的土地吗，凡人？你一定要多加小心……\n\n我曾经认为没必要结交朋友，因为他们会成为我的弱点，被我的对手利用。\n\n而我在这里才认识到我之前的想法大错特错。', '', 38134),
(207210, 'zhCN', '有时候我很后悔做了那个石精……', '', 38134),
(207209, 'zhCN', '而且我之前告诉过你：没有“罪碑通报官”这种头衔。', '', 38134),
(207208, 'zhCN', '这不是在竞赛，勒梅特……', '', 38134),
(207207, 'zhCN', '那就走着瞧吧……罪碑通报官又赢了！', '', 38134),
(207206, 'zhCN', '我很想看老泰梅尔那么做。这就是为什么我是罪碑通报官！', '', 38134),
(207205, 'zhCN', '主人，我这周给你找来了许多罪碑碎块！\n\n你打开箱子自己看吧。', '', 38134),
(207203, 'zhCN', '', '敲响仪式之钟意味着牺牲。\n\n每一口钟都会要求敲响它的灵魂献上某种东西。如果使用不当，就像这些大厅中的堕落者一样，这些牺牲都会变成徒劳的折磨。\n\n宣罪者的智慧就体现在，他们能够找出每个灵魂为了走上赎罪之旅而必须牺牲的东西，并说服这些灵魂做出必要的牺牲——为了灵魂自身的利益。\n\n有时这会很难，有些灵魂不是太过固执，就是目光短浅，不愿敲响仪式之钟。\n\n但你能看到其中的真相：为进步而做出的牺牲并不是真正的牺牲。', 38134),
(207202, 'zhCN', '', '没有灵魂准备好进行仪式，我们现在也就不需要仪式之钟。\n\n不需要这些钟的时候，费安和宣罪者的其他档案员会把它们藏起来，避免让堕落的祭师染指这些大厅。\n\n有灵魂准备好后，我会让泥仆们把我们需要的钟拿过来。希望这个过程不会太久。', 38134),
(207200, 'zhCN', '', '啊，我最爱的一个：屈服之钟。\n\n它只会要求你借给它你的一部分力量。\n\n要求很简单是吧？但这对一个经历了血与火的考验才获得成功的骄傲灵魂来说，却是非常可怕的要求。\n\n当灵魂放弃了它的力量后，它们就能像它们一直渴望的那样自由地战斗了。\n\n但是，在它们虚弱的状态下，输掉战斗会损失力量，直至它们彻底崩溃，不得不甘愿臣服于比它们弱小的存在。\n\n只有到那时，这个仪式才算完成。', 38134),
(207199, 'zhCN', '', '你很容易对别人产生同情心吗，噬渊行者？\n\n并非所有人都这样。通过将行为外显出来再重新面对的方式，耻辱之钟能让那些不愿或是没有同情能力的人去理解他们恶行的后果。\n\n它会把你最可耻的行为公诸于众，让所有人见证。然后我们墓穴里的那些复仇之魂会被释放一段时间，去猛烈责难那些羞愧者，让他们面对自己行为的丑恶。\n\n这对有同情心的人来说会很难承受，但他们并不是这样的人。', 38134),
(207198, 'zhCN', '', '斥责之钟是我们最具惩罚性的工具之一，专门用于那些最顽固的灵魂，那都是些需要不断提醒他们在这里已经没有了控制权的灵魂。\n\n当你敲响钟后，你会首先斥责自己——但那并非是主要目标。我们有更好的自省工具。\n\n更重要的是，你会让自己更容易受到别人的斥责——这会提高所有温西尔对你的影响力。\n\n当然，在当前这些大厅里的温西尔如此堕落的情况下，这么做比以往更危险。我会密切留意受到此类钟声效果影响的灵魂。', 38134),
(207197, 'zhCN', '', '告解之钟是一个构造简单但是功能强大的工具，它可以帮助灵魂正视的自己罪过。\n\n只有那些愿意忏悔罪行的灵魂才能敲响它，这样做就能将灵魂的忏悔之意散发至给大厅里的其他灵魂。\n\n公开承认罪行有助于灵魂接纳自己的失败，而且这也给大厅里堕落的温西尔们敲响了警钟。', 38134),
(207196, 'zhCN', '', '目前，大部分灵魂在为下一次仪式做准备时，都会被关在赎罪地穴中。\n\n别担心，我把它们关在了距离宫务大臣的堕落追随者们很远的地方。这些灵魂在与他们的罪责做斗争时会非常安全。\n\n指控者更是亲自看守着地穴，以免出现不测。', 38134),
(207195, 'zhCN', '', '如果过去可以预示未来，那也一定十分缓慢。\n\n伊克嗜好暴力，且对集体知识库有着无止境的仇恨，这使得她犯下了无数罪行，需要赎罪，但完成赎罪的方式却寥寥无几。\n\n目前，我严格遵照计划表将她独自监禁，并给她一些沟通起来很简单的任务，以此来教给她生命中那些被她错过之事的价值。\n\n今天，她将去收集丢失的罪碑碎片，为档案员提供帮助。', 38134),
(207194, 'zhCN', '', '你为什么不自己问他？\n\n我已经看护孔苟德佐罗一段时间了，但他最终学会了说话，而且可以相信他会实话实说。\n\n之前他的谎言和半真半假的话交织在一起，而在我打破了这种交织之后，我才成功地迫使他直面自己在生者国度里的行为所带来的后果。\n\n现在，他必须要靠言语和实际行动来重建声誉。就和所有人一样，不是吗？', 38134),
(207193, 'zhCN', '', '维力穆还是没明白，他的处境是多么岌岌可危！\n\n他已经赎清了很多罪，但他刚到这里时，灵魂的罪孽实在太过沉重，我从来没有见过他这样的情况。\n\n直到他把自己的最后一份罪孽都赎清时，他才算真正安全，否则噬渊的触须还是可能把他抓走。\n\n我目前正在努力让他专心进行他熟悉的任务：破坏一个敌对的组织。', 38134),
(207191, 'zhCN', '', '和我看护下的绝大多数灵魂相比，耶弗凯克算是在赎罪的路上走得最远的一个。\n\n在经历了一段利用自己的力量与才智欺压社会弱者的生命体验后，他正在学着使用相同的技能来保护和拯救他人。\n\n每当我有另一个灵魂迷失或是想逃离正义，我都会托付耶弗凯克找到他们，劝他们要迷途知返。', 38134),
(207190, 'zhCN', '', '甚至连泥仆都成了那些游荡在这些大厅中的堕落者的受害者。\n\n它们被迫劳动和服务于宫务大臣的堕落追随者们，全然无视了自己真正的职责。\n\n这个仪式之钟破损地躺在其基座上，无数有罪的灵魂正迫切地需要它来助其赎罪！', 38134),
(207189, 'zhCN', '', '钟修好了，我们可以继续指引灵魂沿着道路走向救赎了。\n\n你可以敲一下钟，帮我向灵魂展示一下，最真实的救赎之路就是牺牲吗？\n\n如果我们不愿变得谦卑，直面自己的错误，我们又如何能克服它们？', 38134),
(207175, 'zhCN', '', '每个灵魂的救赎之路都是不一样的，但还是有共同之处。\n\n牺牲和痛苦都是达成有效成果的必要手段。\n\n如果不这样做，那后果就是在噬渊里遭受永世煎熬，既然这样，那还有什么代价是高到无法承受的呢？', 38134),
(207168, 'zhCN', '我的道路是寻找弱者，拯救那些我曾欺压过的人。仁慈行事，凡人。', '', 38134),
(207167, 'zhCN', '', '泥仆修好告解之钟后，你来敲响它。', 38134),
(207163, 'zhCN', '我曾有过许多的生命体验……\n\n盯着羊群伺机而动的野狼。\n环绕小船不愿离去的鲨鱼。\n盘旋在天空中捕杀野兔的飞鹰。\n\n只要是离群的没有一个能从我手上逃掉。', '', 38134),
(207161, 'zhCN', '你还有时间，凡人。\n\n改变你的道路吧，不要步我后尘。\n\n这个温西尔的赎罪之路也许能获得拯救，但是……\n\n<这个灵魂沉默着瑟瑟发抖。>', '', 38134),
(207152, 'zhCN', '', '他们全都没有活下来，除了我。\n\n噬渊，正如你所见，是一个危险的地方。一大群人结伴而行只会给人带来安全的幻觉，事实上这么做反而会让你成为更显眼的目标。\n\n我们付出了惨痛的代价才学到这个教训。我们不断地损失人手，直到人数少到我们能隐藏自己的行踪。虽然这延缓了追捕……但并没有完全停止。\n\n当最后只剩下我一个人的时候，我从自己的处境中明白了一个道理。只有独自行事才最有可能活下来。\n\n至少在你来之前是这样。', 38134),
(207149, 'zhCN', '', '他们遭遇这样的命运的确很不幸，但别搞错了。他们跟渊誓者一样都是我们的敌人。\n\n那些掮灵从来到这里的那一刻就死定了。就算你能救他们，也只是给我们招来血光之灾而已。\n\n我承认，这种心态很残忍，但要在噬渊生存，就必须这样想。\n\n你最好记住这一点。', 38134),
(207143, 'zhCN', '', '护园者卡萨德在女王的温室等你！他们正急切地等你开始。\n\n你只需要进入我身边的蘑网就能前往温室。', 38134),
(207142, 'zhCN', '', '当你除了拥有无限的时间便一无所有时，在这永恒的黑暗中，故事比金子还要宝贵。', 38134),
(207119, 'zhCN', '', '哦，你现在肯定跟她混得很熟了。没有她帮忙，你这样一个凡人是绝对没办法在噬渊里活着旅行的。\n\n把你跟威·娜莉做的交易全都告诉我吧。\n\n如果她决定不跟你一起回奥利波斯，那我也有问题想问她。', 38134),
(207118, 'zhCN', '', '当然，当然。这样，就只剩最后一个问题了。\n\n你勇闯噬渊期间，威·娜莉是不是一个宝贵的盟友？\n\n<在你回答前，你想起了威·娜莉特地告诉过你不要向任何人提及你的约定。>', 38134),
(207113, 'zhCN', '', '啊，对于做生意而言，那里果真是一个危险的地方呢。那么第二个问题来了。\n\n要在噬渊那种极具压迫和永不安宁的恐惧环境中活下来，你是否觉得充满挑战性？', 38134),
(207112, 'zhCN', '', '我很感激你能向我们提供噬渊的情报。\n\n第一个问题：\n\n典狱长的军队不管是实力还是数量都增长了，是真的吗？', 38134),
(207111, 'zhCN', '', '你好，噬渊行者。很高兴能认识你！', 38134),
(207110, 'zhCN', '', '真刺激！', 38134),
(207109, 'zhCN', '', '我不会测试或是重新调谐它们，所以我们不知道启动后会有什么东西从里面冒出来。', 38134),
(207108, 'zhCN', '', '只要你有足够的注能红宝石，我就能为你制作更多的修复工具。', 38134),
(207107, 'zhCN', '雷文德斯没有镜子需要修复，噬渊行者。', '', 38134),
(207105, 'zhCN', '', '我从灰烬荒野里带来的镜子修复工具欢迎你随便用。', 38134),
(207104, 'zhCN', '大厅对面有一副破碎的镜子，你可以用它进行练习。', '', 38134),
(207103, 'zhCN', '', '哦，我有很多事情都故意没告诉你。我相信你也有很多事情没告诉我。\n\n信赖的基础是审慎，噬渊行者。否则，生意就做不了了。\n\n话虽如此，我其实已经把我最宝贵的秘密——这个庇护所的位置告诉你了。我只希望你也能信赖我。', 38134),
(207101, 'zhCN', '', '在你的帮助下，炽蓝仙野将会继续维持这个循环。\n\n我的温室就交给你照顾了。\n\n与护园者卡萨德谈谈。他们会给你进一步的指示。', 38134),
(207100, 'zhCN', '', '万物都会终结。\n\n然而，终结之后，我们将迎来一个新的开始。自然的循环就是如此。\n\n我林地里的所有灵魂对整个循环而言非常重要。没了它们，自然本身就会在凡间世界逐渐消亡。每拯救一个灵魂，就是一次胜利。\n\n这个灵魂便是你从濒死的林地与疯狂的德鲁斯特手中拯救出来的。如今，她已经从漫长的沉睡中苏醒，回到了她的世界。在你的帮助下，我们履行了我们神圣的职责。', 38134),
(207097, 'zhCN', '', '寒冬女王竟然亲自传授你灵种的知识，这可真是一份荣耀啊。', 38134),
(207087, 'zhCN', '', '那一下可耗费了不少冥殇呢……不过做生意肯定要付出一些成本的。', 38134),
(207065, 'zhCN', '难道该信你的？也对。你违反了你的合约，背叛了财团！现在，你必须付出代价！', '', 38134),
(207063, 'zhCN', '', '他们的尖叫声不如野猪人的悦耳，不过也还过得去。', 38134),
(207012, 'zhCN', '$n？过了这么久能再次与你相遇真是令人欣慰。尤其是在这里，在这样一个怪异的地方。\n\n当我得知通往暗影界的帷幕被粉碎时，我的心就告诉自己我必须跨过去。无论风险有多大。\n\n你应该记得，我妻子……在孩子出生时就离世了，紧接着我们的一个儿子也遭遇不幸。我必须弄清楚他们是否和我的先祖们安然团聚，还是说他们的灵魂陷入了险境。\n\n拜托了，我恳求你，如果你遇到莱莎的灵魂，请转告她，我们的儿子科尔长得很壮实。告诉她，我深爱着她。直到永远。\n\n而且，无论要付出什么样的代价，我都要找到她。', '', 38134),
(207011, 'zhCN', '能在这样一个怪异的地方见到一张来自艾泽拉斯的面孔真是令人欣慰。\n\n当我得知通往暗影界的帷幕被粉碎时，我就知道我必须跨过去。无论风险有多大。\n\n你知道，我……失去了两位我心里最珍爱的人。我必须弄清楚他们是否和我的先祖们安然团聚，还是说他们的灵魂遭到了危险。\n\n拜托了，我恳求你，如果你遇到我妻子，莱莎·远途行者的灵魂的话，请转告她，我们的儿子科尔长得很壮实。告诉她，我深爱着她。直到永远。\n\n而且，无论要付出什么样的代价，我都要找到她。', '', 38134),
(207009, 'zhCN', '', '正在玩我的一个更有意思的装置。很遗憾，它可能会让你感到十分痛苦。', 38134),
(207008, 'zhCN', '', '来吧，噬渊行者。我想我们有很多事情要谈。', 38134),
(207007, 'zhCN', '', '不，威·布拉克斯。你会离开噬渊的……但你永远别想再回到财团。', 38134),
(207006, 'zhCN', '啊！你……在……做什么……？！', '', 38134),
(207005, 'zhCN', '', '我可不这么认为。让我们解决掉这个烦人的家伙，噬渊行者。', 38134),
(207003, 'zhCN', '', '你以为他们会听信一个刺客的一面之词？', 38134),
(207002, 'zhCN', '我之前提醒过你，噬渊行者。但你就是不听。', '', 38134),
(207000, 'zhCN', '她背叛过之前的同事，如果让她得知了我们的谈话，她也会对你这么做。不要轻信她的谎言。', '', 38134),
(206999, 'zhCN', '在我撤退到自己的安全区域之前，我必须给你提个醒。威·娜莉她可没有你想的那么简单。', '', 38134),
(206995, 'zhCN', '我们来到此地是想弥补威·娜莉的背叛带来的损失，我们之前真是太蠢了……', '', 38134),
(206988, 'zhCN', '被捕……折磨……崩溃……\n\n泽洛斯……把其他人……带到野兽密径……\n\n我们的任务……失败了……', '', 38134),
(206984, 'zhCN', '我跟他说过，我会回来的！我可不想让人失望。', '', 38134),
(206983, 'zhCN', '我被一个邪恶的巫师的锁链困住了！\n\n放开我，我去给他上一课，让他知道生命里最有意思的是什么！当然，死后也一样。', '', 38134),
(206970, 'zhCN', '这只蝙蝠似乎能帮你离开赎罪大厅。', '这只蝙蝠似乎能帮你离开赎罪大厅。', 38134),
(206969, 'zhCN', '灵铸护盾，攻防一体的安全盾！\n\n敌人要来了？正准备大举进攻？\n\n用这个护盾，保你安全！不仅能吸收伤害，还能反射一部分伤害！咕咕！', '', 38134),
(206968, 'zhCN', '守护之环，保你平安！\n\n想要坚守位置，还想让敌人离你远一点？\n\n好办！激活圆环，敌人跑光光！安全的港湾，尽情地休息。', '', 38134),
(206967, 'zhCN', '静谧之瓶，鲜美可口！\n\n感觉自己弱弱的？要我扶才能起来？\n\n静谧之瓶治疗你，让你重返战斗！执事者质量认证！', '', 38134),
(206966, 'zhCN', '巫妖霜瓶，让敌人近不了身！\n\n被对手包围了？想跑路了？\n\n不要怕！把这个瓶子砸在地上，敌人就冻住啦。快跑吧！', '', 38134),
(206965, 'zhCN', '抱歉，$n！装备不是给你的！\n\n这是给你的灵魂羁绊的，可以在训练中帮到他们！', '', 38134),
(206959, 'zhCN', '咕咕！我能帮你完成一项大任务，但只能有一项。再想找我帮忙，就要等一段时间——大概几个小时吧。我也很忙呀！', '咕咕！我能帮你完成一项大任务，但只能有一项。再想找我帮忙，就要等一段时间——大概几个小时吧。我也很忙呀！', 38134),
(206958, 'zhCN', '咕咕，我能帮你做什么呢？我只能同时做一件事，所以要好好选哦！再想找我帮忙就要等……一个小时。我太忙啦！', '咕咕，我能帮你做什么呢？我只能同时做一件事，所以要好好选哦！再想找我帮忙就要等……一个小时。我太忙啦！', 38134),
(206954, 'zhCN', '你这么一说，我确实看到一个绿大个被拖着经过了这里。\n\n那家伙很显眼。嘴里一直喊着“Lock Tar”什么的。\n\n放我出来，我就全盘告诉你。', '', 38134),
(206949, 'zhCN', '不是吧？一个活着的凡人，在这儿？我这是疯了吧！\n\n你肯定不是真的。', '', 38134),
(206948, 'zhCN', '就是这个——凯旋大典！还是你先请吧，$n，万一这是个陷阱什么的呢。', '', 38134),
(206944, 'zhCN', '哦，你好！我照顾玛拉斯缪斯很久了，有些古老的路线，连他都忘了，但我知道！$b$b我听说你也知道那些隐藏的地点。要是你有兴趣的话，我可以帮你返回那里。', '哦，你好！我照顾玛拉斯缪斯很久了，有些古老的路线，连他都忘了，但我知道！$b$b我听说你也知道那些隐藏的地点。要是你有兴趣的话，我可以帮你返回那里。', 38134),
(206933, 'zhCN', '', '你的灵魂浸透了骄傲，凡人。要我帮你赎罪吗？', 38134),
(206931, 'zhCN', '在这里她会好受些。', '', 38134),
(206930, 'zhCN', '雷纳索尔王子已经准许那位纳鲁进驻晨曦堡的塔楼。那里是她们一族最初现身的地方，也是圣光浸透最充分的场所。', '', 38134),
(206929, 'zhCN', '兹拉莉过于虚弱，无法返回她的家园，而她的存在本身就会对附近的温西尔造成危险。', '', 38134),
(206912, 'zhCN', '自然推动是一项被动技能。在灵魂变形期间，施放闪烁时会向你移动方向的传送，而非你面对的方向。', '自然推动是一项被动技能。在灵魂变形期间，施放闪烁时会向你移动方向的传送，而非你面对的方向。', 38134),
(206908, 'zhCN', '你可以在飞地附近找到争端大师和他的商人朋友们。', '你可以在飞地附近找到争端大师和他的商人朋友们。', 38134),
(206897, 'zhCN', '我不过是圣杰的知识残存的回响，不过我看得出你对知识的渴求。\n\n请先阅读我的故事，然后我们再探讨你还想了解什么内容。', '我不过是圣杰的知识残存的回响，不过我看得出你对知识的渴求。\n\n请先阅读我的故事，然后我们再探讨你还想了解什么内容。', 38134),
(206896, 'zhCN', '你找到科布里啦！谢谢你！', '你找到科布里啦！谢谢你！', 38134),
(206895, 'zhCN', '科布里去找酒了，科布里很久都没有回来。我们不能没有酒！\n\n去找找科布里，行不？', '科布里去找酒了，科布里很久都没有回来。我们不能没有酒！\n\n去找找科布里，行不？', 38134),
(206889, 'zhCN', '为了大地母亲！', '', 38134),
(206886, 'zhCN', '愿天命指引你。\n\n我这里存放着许多记录。纠缠扭曲的命运向我们展示出的景象，我们都会妥善保存，用以更好地服务于天命。\n\n你想让我展示什么？', '愿天命指引你。\n\n我这里存放着许多记录。纠缠扭曲的命运向我们展示出的景象，我们都会妥善保存，用以更好地服务于天命。\n\n你想让我展示什么？', 38134),
(206878, 'zhCN', '欢迎。你需要何种帮助？', '', 38134),
(206833, 'zhCN', '', '你不能向任何人提起我。你在这里所做的一切，都是你自发的行为。\n\n我会给你一件名为护魂者的神器，它能帮你保存在噬渊里找到的灵魂。\n\n收下护魂者并同意这些条款，就意味着我们订立了合同关系。\n\n从今以后，你将把噬渊中找到的有价值的信息免费分享给我。\n\n作为回报，我允许你购买必需的工具，以在这里生存，并拯救你找到的灵魂。\n\n怎么样，成交吗？', 38134),
(206827, 'zhCN', '你想演奏音乐？演奏这些乐器？', '你想演奏音乐？演奏这些乐器？', 38134),
(206726, 'zhCN', '<选一个。>', '', 38134),
(206725, 'zhCN', '<关闭，打开。>', '', 38134),
(206719, 'zhCN', '要为粗鄙之语赎罪，矫正你的行为，一定要付出代价。不过，要想彻底地改变说粗口的习惯，还是要让其他人都意识到他们的恶言，这才能让他们管住自己的嘴。', '', 38134),
(206713, 'zhCN', '罪奔者布兰契痛苦地嘶啸着，从你的视线中渐渐消失。', '', 38134),
(206708, 'zhCN', '安度因国王不在的这段时间，我受托来保护联盟的人民。\n\n我曾帮助年轻的瓦里安·乌瑞恩守护暴风王国，现在想来真是恍如隔世。而如今，协助他的儿子、保卫王国平安的重任又落在了我的肩上。\n\n第四次大战让联盟蒙受了巨大损失。我们的军力几乎消耗殆尽。暴风城的贵族正在敦促我们重整防御。\n\n夺回激流堡是个良好的开端。或许我们还可以收复旧联盟时代的其他要地。', '', 38134),
(206707, 'zhCN', '部落面临着许多挑战，许多危险。\n\n能代表议会发声是我的荣幸。议会将带领我们前行。\n\n沃金曾是我们最好的大酋长……也是我们的好朋友。希望我能继承他的遗志。部落永远不会忘记他为我们做出的牺牲。', '', 38134),
(206706, 'zhCN', '', '数月以来，我们找遍了艾泽拉斯的每个角落，搜寻希尔瓦娜斯的行迹，最终只知道她已经动身前往死亡的国度。\n\n<奥蕾莉亚眼神一凝。>\n\n真是适合她的归宿。\n\n尽管我也希望能加入搜捕她的行动，但现在还必须在这里。联盟需要重整力量，而我要帮助图拉扬完成这一任务。\n\n不过等到时机成熟，我一定要亲眼见证希尔瓦娜斯为她的所作所为付出代价。', 38134),
(206705, 'zhCN', '通路开启了。你可以前往玛卓克萨斯了。', '', 38134),
(206704, 'zhCN', '通路正在重新开启。难道是天命派你来帮忙的？', '', 38134),
(206703, 'zhCN', '通路开启了。你可以前往炽蓝仙野了。', '', 38134),
(206701, 'zhCN', '通路开启了。你可以前往雷文德斯了。', '', 38134),
(206692, 'zhCN', '欢迎，卡-谢尔。消息是真的吗？我们要再度打开那扇大门？', '', 38134),
(206686, 'zhCN', '', '这个法术很不稳定，所以会造成巨大的伤害，但只能同时影响一个敌人。', 38134),
(206685, 'zhCN', '这个法术很不稳定，所以会造成巨大的伤害，但只能同时影响一个敌人。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206684, 'zhCN', '敌人死亡后，痛苦无常会产生一个灵魂碎片，所以一定要把需要优先消灭的敌人作为目标。', '', 38134),
(206683, 'zhCN', '', '敌人死亡后，痛苦无常会产生一个灵魂碎片，所以一定要把需要优先消灭的敌人作为目标。', 38134),
(206682, 'zhCN', '', '痛苦无常是个强力的法术，可以持续造成伤害。', 38134),
(206681, 'zhCN', '痛苦无常是个强力的法术，可以持续造成伤害。', '', 38134),
(206679, 'zhCN', '<用一块调谐碎片灌注能量后，这个装置就可以打开高塔中隐藏的路线。>', '', 38134),
(206677, 'zhCN', '好的，噬渊行者。', '', 38134),
(206668, 'zhCN', '', '永恒之城，闪耀，瑰丽。但死亡的触手正在其中到处蔓延。\n\n我也被这股力量触及。或许，奥利波斯隐藏着一些秘密，能帮我理解其中的含义。\n\n那些侍者如此确信他们口中的“天命”，或许他们也能帮我找到我的使命。', 38134),
(206667, 'zhCN', '', '暗影界。真是惊人……说实话，还有点恐怖。\n\n但我不会在恐惧面前停步，我会继续帮助我的朋友们。\n\n我也会继续探寻我的父亲，我曾以为再也见不到他了。', 38134),
(206666, 'zhCN', '我们都知道，典狱长监视着我们，他还抓走了安度因。\n\n我们唯一的希望，$n，就是请你和其他英雄增强各个盟约的力量。\n\n典狱长和希尔瓦娜斯的下一步计划马上就要揭晓。\n\n等到他们开始行动时，我们必须倾尽全力才能阻止他们。', '', 38134),
(206629, 'zhCN', '$g他:她;就是爱这样，是吧？没翅膀的生物一定都喜欢这么干。', '$g他:她;就是爱这样，是吧？没翅膀的生物一定都喜欢这么干。', 38134),
(206628, 'zhCN', '你跳得太多了，凡人，这对关节不太好，你又没有翅膀。', '你跳得太多了，凡人，这对关节不太好，你又没有翅膀。', 38134),
(206627, 'zhCN', '一定会的，不过我能处理……', '一定会的，不过我能处理……', 38134),
(206626, 'zhCN', '你知道吗？你很爱跳。', '你知道吗？你很爱跳。', 38134),
(206625, 'zhCN', '所以，你不如先把它放下，明天再说！', '所以，你不如先把它放下，明天再说！', 38134),
(206624, 'zhCN', '帮忙？我们走吧，凡人，给你抢点好东西去。', '帮忙？我们走吧，凡人，给你抢点好东西去。', 38134),
(206623, 'zhCN', '这……这块石头！它会给你招来祸患，凡人！', '这……这块石头！它会给你招来祸患，凡人！', 38134),
(206622, 'zhCN', '别听信他的诱惑！', '别听信他的诱惑！', 38134),
(206621, 'zhCN', '踏上正义之路，永远不嫌晚！', '踏上正义之路，永远不嫌晚！', 38134),
(206620, 'zhCN', '这次你要帮助谁，凡人？', '这次你要帮助谁，凡人？', 38134),
