INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194286, 'zhCN', '我最聪明了！', '', 38134),
(194285, 'zhCN', '不，我亲爱的塔宾斯，我必须坚持地说，我今天的冒险经历才更精彩！', '', 38134),
(194284, 'zhCN', '你一个人消灭了那么多血牙？', '你一个人消灭了那么多血牙？', 38134),
(194281, 'zhCN', '第一步，你要受到注意！', '', 38134),
(194279, 'zhCN', '光是厉害的斗士还不够，你还要有人脉！', '', 38134),
(194278, 'zhCN', '走开，走开，快走开！这是我的！我找到的！这份宝藏属于我，我的！\n\n滚！快滚，滚回随便哪个国度。从城墙上跳下去吧，我才不管，只要让我和我的宝贝待在一起就好！\n\n我需要它，我太需要这个了，你们这群穿着华丽铠甲和昂贵长袍的人怎么会懂呢？这是我的！', '走开，走开，快走开！这是我的！我找到的！这份宝藏属于我，我的！\n\n滚！快滚，滚回随便哪个国度。从城墙上跳下去吧，我才不管，只要让我和我的宝贝待在一起就好！\n\n我需要它，我太需要这个了，你们这群穿着华丽护甲和昂贵长袍的人怎么会懂呢？这是我的！', 38134),
(194271, 'zhCN', '不，不，不！这是我的宝贝！', '', 38134),
(194266, 'zhCN', '准备好你的下一场决斗了吗，$r？', '', 38134),
(194265, 'zhCN', '我很期待未来赚钱的安排。', '', 38134),
(194250, 'zhCN', '在你进入竞技场战斗之前，我要先行评估你的盈利潜力。我会随着我们的合作进程来判断你的实际价值。', '', 38134),
(194249, 'zhCN', '', '面对我，通灵师！我要给萨尼科斯报仇！', 38134),
(194247, 'zhCN', '该让那些流氓学学礼仪了！', '', 38134),
(194246, 'zhCN', '啊，远离圣光的感觉真好……', '', 38134),
(194245, 'zhCN', '所以我打着遮阳伞的时候绝不奔跑！', '', 38134),
(194229, 'zhCN', '对一项试验来说，意料之外的结果就是最好的回报！谢谢你的帮助！', '', 38134),
(194228, 'zhCN', '换戏服！', '', 38134),
(194227, 'zhCN', '很好，继续表演！', '', 38134),
(194224, 'zhCN', '监工会把它们堆得到处都是，还有石裔魔看着它们。我们都很讨厌他们。你去拿罪碑并帮助娜德佳！', '', 38134),
(194223, 'zhCN', '这是他们的罪碑。监工只要拿着它们就能让其他的毒牙都听从他的命令。你会帮助我的朋友娜德佳吗？', '', 38134),
(194219, 'zhCN', '你是娜德佳的朋友，那你就是我的朋友了。看，我找到了上面画着东西的石头。它们就散落在灌木丛里。', '', 38134),
(194211, 'zhCN', '我早就知道是那个地方！', '', 38134),
(194210, 'zhCN', '', '你准备好学习我的智慧了吗？', 38134),
(194202, 'zhCN', '得赶快找到好运茶壶！赶快！', '', 38134),
(194201, 'zhCN', '喔，现在可别甩锅了……', '', 38134),
(194200, 'zhCN', '茶壶都是塔宾斯带着的。', '', 38134),
(194199, 'zhCN', '我以为茶壶在你身上呢！', '', 38134),
