INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(191027, 'zhCN', '', '被变形的敌人会在附近徘徊。利用这段时间对付剩下的敌人。', 38134),
(191025, 'zhCN', '', '挺好玩的，新兵。我觉得你已经懂门道了。我们回去吧。', 38134),
(191024, 'zhCN', '要记住，攻击对手就会打破法术。打梅瑞迪一下就行。', '', 38134),
(191023, 'zhCN', '哦，我听说过这种战术！你可以把一个敌人变形，然后专心对付另一个。', '', 38134),
(191021, 'zhCN', '', '什么，科尔！我才不会这么做呢。我保证！', 38134),
(191020, 'zhCN', '你打算……把我变成绵羊？', '', 38134),
(191019, 'zhCN', '', '科尔，过来。我们要夹击$p，这样$p就知道该怎么用变形术了。', 38134),
(191017, 'zhCN', '', '我有许多办法可以对付这些食人魔，让我们看看哪个办法不会一不小心把这座小岛炸飞……', 38134),
(191016, 'zhCN', '有时候我希望能像这里的灵魂一样具有变形能力。这样我就能和硕角猎人一样飞到空中了！没有猎物能逃过我的眼睛！', '', 38134),
(191015, 'zhCN', '硕角猎人是顶级追踪者。如果他都找不到猎物，那就没能人找到了。', '', 38134),
(191014, 'zhCN', '告诉我，你的狩猎进展如何？你和硕角猎人有没有消灭他所搜寻的野兽？', '', 38134),
(191013, 'zhCN', '在那之后，我的伤已经痊愈了。幸好那个硕角猎人救了我们，对吧？', '', 38134),
(191012, 'zhCN', '当心，这些道路近来愈发危险了。塞兹仙林发生了一些事情。', '', 38134),
(191011, 'zhCN', '你好啊。荒猎团有什么可以帮你的？', '', 38134),
(191010, 'zhCN', '荒猎团效忠炽蓝仙野。', '', 38134),
(191007, 'zhCN', '我们会让它成为雷文德斯历史上最好的派对！', '', 38134),
(191006, 'zhCN', '', '敌人会变成绵羊，四处乱走，直到你攻击他们为止，就像这样。', 38134),
(191005, 'zhCN', '', '只是演示罢了！变形术在战斗中有时很有用。', 38134),
(191001, 'zhCN', '', '梅瑞迪，我们可没工夫搞恶作剧！', 38134),
(190998, 'zhCN', '期待下一次灰烬王庭，人们都说下一次可能将是最棒的一次！', '', 38134),
(190997, 'zhCN', '当心，狩猎者！这条路现在不安全。', '', 38134),
(190996, 'zhCN', '', '尝尝变形术的滋味！', 38134),
(190995, 'zhCN', '惧影花是斯特拉达玛侯爵最爱的材料。找到后就去实验室见我。', '', 38134),
(190994, 'zhCN', '勇敢……顽强……背叛。全都被困在这里。救救他们。', '', 38134),
(190992, 'zhCN', '陨落的法夜，受尽了苦难。救救他们……不能让他们被黑暗吞噬。', '', 38134),
(190990, 'zhCN', '即使是收割傲慢之人也不该承受幽禁于此的煎熬。救救这名温西尔吧。', '', 38134),
(190989, 'zhCN', '不过预赛好歹是结束了，是时候开始真正的试炼了！', '', 38134),
(190987, 'zhCN', '难以置信！看来狗虽小，但还是会咬人的！', '', 38134),
(190986, 'zhCN', '', '$p潜入深海，与傲慢的艾萨拉女王作战。', 38134),
(190985, 'zhCN', '', '希望我那沉到湖底的法术书还有救。也许我应该亲自去找找。', 38134),
