INSERT INTO `page_text` (`ID`, `Text`, `NextPageID`, `PlayerConditionID`, `Flags`, `VerifiedBuild`) VALUES
(8130, '<HTML><BODY>\n<br/>\n<br/>\n<br/>\n<H1 align=\"center\">Lady Quinby</H1>\n<p align=\"center\">Casa de Rituales\n<br/>\n<br/>\nSu conocimiento de la magia era inigualable. Sus palabras tenan tanto poder que convertan a sus enemigos en polvo. Aunque destruyeron su filacteria, defendi las Tierras de las Sombras hasta su ltimo aliento.</p>\n</BODY></HTML>', 0, 0, 0, 38134), -- 8130
(8132, '<HTML><BODY>\n<br/>\n<br/>\n<br/>\n<H1 align=\"center\">Rakel el Vil</H1>\n<p align=\"center\">Casa de las Plagas\n<br/>\n<br/>\nSus brebajes envenenaron a todos los que se atrevieron a tocarlos. Derriti la carne de los huesos, llen pulmones con bilis y sembr el miedo en corazones de acero. Pocos fueron capaces de crear un arsenal tan potente.</p>\n</BODY></HTML>', 0, 0, 0, 38134), -- 8132
(8131, '<HTML><BODY>\n<br/>\n<br/>\n<br/>\n<H1 align=\"center\">Mesil, la Fantasma</H1>\n<p align=\"center\">Casa de los Ojos\n<br/>\n<br/>\nNinguna sombra pudo oscurecer su mirada. Compr valiosa inteligencia con sangre, y su sacrificio le dio a Maldraxxus la victoria contra el Vaco. Ese es el logro ms grandioso del espionaje.</p>\n</BODY></HTML>', 0, 0, 0, 38134); -- 8131



