(59894, 0, 0, 0, 0, 0, 0, 0, 0, 'Tenemos una alianza. Ahora hay que planificar el ataque.', 38556), -- -Unknown-
(62309, 0, 0, 0, 0, 0, 0, 0, 0, '$n, es bueno volver a verte!\n\nLuego del xito contra la Casa de Ensamblajes, el secutor Mevix no ve la hora de volver a la accin del campo de batalla.\n\nNunca dejes que un soldado motivado pierda tiempo haciendo nada. Dale un propsito!', 38556), -- -Unknown-
(59603, 1, 0, 0, 0, 0, 0, 0, 0, 'Pareciera que t $gmismo:misma; hubieses comandado la batalla en persona! Un trabajo impresionante, debo decir.', 38556), -- -Unknown-
(63054, 2, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, $n. $GJuntos:Juntas;, nos aseguraremos de que nuestros soldados dominen el campo de batalla.', 38556), -- -Unknown-
(59922, 1, 0, 0, 0, 0, 0, 0, 0, 'Hemos demostrado nuestro honor... y conseguimos un nuevo aliado.', 38556), -- -Unknown-
(59921, 1, 0, 0, 0, 0, 0, 0, 0, 'Te ha iluminado esta aventura?', 38556), -- -Unknown-
(59748, 1, 0, 0, 0, 0, 0, 0, 0, 'Ven. Tengo mucho que mostrarte.', 38556), -- -Unknown-
(59709, 0, 0, 0, 0, 0, 0, 0, 0, 'Espero que nuestros caminos vuelvan a cruzarse.', 38556), -- -Unknown-
(59700, 0, 0, 0, 0, 0, 0, 0, 0, 'Que su recuerdo se esfume de este lugar.', 38556), -- -Unknown-
(59698, 0, 0, 0, 0, 0, 0, 0, 0, 'Por Valor!', 38556), -- -Unknown-
(59659, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahora estamos listos.', 38556), -- -Unknown-
(59678, 66, 0, 0, 0, 0, 0, 0, 0, 'Ya no pueden sustentarse. Buen trabajo.', 38556), -- -Unknown-
(59653, 0, 0, 0, 0, 0, 0, 0, 0, 'Solo puedo acercarte hasta aqu. Haz que cuente.', 38556), -- -Unknown-
(59650, 1, 0, 0, 0, 0, 0, 0, 0, 'Esto es solo el principio para enmendar las cosas.', 38556), -- -Unknown-
(59648, 0, 0, 0, 0, 0, 0, 0, 0, 'Esto es solo el comienzo.', 38556), -- -Unknown-
(59645, 0, 0, 0, 0, 0, 0, 0, 0, 'Se ha cumplido nuestra voluntad.', 38556), -- -Unknown-
(59625, 0, 0, 0, 0, 0, 0, 0, 0, 'Estbamos esperando tu llegada. Necesitamos resultados.', 38556), -- -Unknown-
(58820, 574, 0, 0, 0, 0, 0, 0, 0, 'Siento su poder. Quiz nos den la ventaja que necesitamos.', 38556), -- -Unknown-
(61869, 0, 0, 0, 0, 0, 0, 0, 0, 'Esto le perteneca al Primus!', 38556), -- -Unknown-
(59722, 0, 0, 0, 0, 0, 0, 0, 0, 'Es como si nunca nos hubiramos ido.', 38556), -- -Unknown-
(61594, 0, 0, 0, 0, 0, 0, 0, 0, 'Es el momento de partir.', 38556), -- -Unknown-
(61569, 396, 0, 0, 0, 0, 0, 0, 0, 'Ven, $p. No quieres ser testigo de tu xito?', 38556), -- -Unknown-
(61551, 113, 0, 0, 0, 0, 0, 0, 0, 'Tu fuerza nunca deja de impresionarme.', 38556), -- -Unknown-
(61548, 0, 0, 0, 0, 0, 0, 0, 0, 'Excelente navegacin. Tal vez debas pilotear la necrpolis.', 38556), -- -Unknown-
(60112, 0, 0, 0, 0, 0, 0, 0, 0, 'Debera estar aqu pero no est.', 38556), -- -Unknown-
(61518, 0, 0, 0, 0, 0, 0, 0, 0, 'Parece que nos han descubierto. Momentos como este exigen flexibilidad.', 38556), -- -Unknown-
(61485, 0, 0, 0, 0, 0, 0, 0, 0, 'Pongamos en marcha nuestro plan.', 38556), -- -Unknown-
(57778, 0, 0, 0, 0, 0, 0, 0, 0, 'Nos complace verte regresar.', 38556), -- -Unknown-
(62169, 0, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, Surcafauces. Los Ojos depositan su confianza en ti.', 38556), -- -Unknown-
