-- Elwynn Forest Quests
(26390, 0, 0, 0, 0, "Have you found Kurtok?", 35662),
(26391, 0, 0, 0, 0, "Have you put out the fires?", 35662),
(26152, 0, 0, 0, 0, "Turning in a bounty?", 35662),
(31693, 0, 0, 0, 0, "There's nobody I can't beat!", 35662),
(54101, 0, 0, 0, 0, "Let us keep investigating.", 35662),
-- Westfall Quests
(26320, 0, 0, 0, 0, "What have you found out, $n?", 35662),
(26230, 0, 0, 0, 0, "Pies don't make themselves - usually.", 35662),
(26271, 0, 0, 0, 0, "Have you fed the homeless?", 35662),
(26229, 0, 0, 0, 0, "I just want those kobolds dead. Dead!", 35662),
(26288, 0, 0, 0, 0, "Have you found Jango?", 35662),
(26228, 0, 0, 0, 0, "Be careful with my old house. I plan to hand that off to my kids when they move out.", 35662),
(26209, 0, 0, 0, 0, "Anything yet?", 35662),
(26322, 0, 0, 0, 0, "What news do you bring from Westfall?", 35662),
(26290, 0, 0, 0, 0, "I won't let you down, $n.", 35662),
(26348, 0, 0, 0, 0, "Kill 7 Tidehunters, and 7 Oracles and I will see to it that you are rewarded.", 35662),
(26297, 0, 0, 0, 0, "What happened?", 35662),
(26287, 0, 0, 0, 0, "Defend Sentinel Hill!", 35662),
(26237, 0, 0, 0, 0, "Did you destroy the harvest watchers?", 35662),
-- Redridge Mountains Quests
(26708, 0, 0, 0, 0, "We're ready for the final assault.", 35662),
(26562, 0, 0, 0, 0, "Well look at that! The team's all here... everyone but Keeshan.", 35662),
(26636, 0, 0, 0, 0, "Leaves and poop - that's what I need. ", 35662),
(26637, 0, 0, 0, 0, "Did you recover the glands? ", 35662),
(26587, 0, 0, 0, 0, "Did you get the cage key? ", 35662),
(26514, 0, 0, 0, 0, "Did you recover those collars? ", 35662),
(54103, 0, 0, 0, 0, "Let's find out everything we can. ", 35662),
(26714, 0, 0, 0, 0, "...", 35662),
(26506, 0, 0, 0, 0, "Have you gotten my ingredients? ", 35662),
(26519, 0, 0, 0, 0, "<The orb emanates powerful magic> ", 35662),
(26573, 0, 0, 0, 0, "Did you locate the headband and amulet?", 35662),
(26638, 0, 0, 0, 0, "NO MERCY!", 35662),
(26560, 0, 0, 0, 0, "Messner! Damn, it's good to see a friendly face. Get me out of here!", 35662),
(26561, 0, 0, 0, 0, "<Krakauer groans>. ", 35662),
(26511, 0, 0, 0, 0, "Have you killed all the murlocs?", 35662),
(26513, 0, 0, 0, 0, "Did you recover the supplies?", 35662),
(26508, 0, 0, 0, 0, "Hi. I miss my necklace. My daddy got it for me. Daddy says that there are monsters in the lake. Did you beat up any monsters?", 35662),
(26505, 0, 0, 0, 0, "Where did I put my monocle?! Conacher is that you? ", 35662),
(26646, 0, 0, 0, 0, "$n?", 35662),
(26570, 0, 0, 0, 0, "How goes the orc slaughter? ", 35662),
(26520, 0, 0, 0, 0, "That boulder fell from the sky. It's a miracle he's still alive.", 35662),
(26692, 0, 0, 0, 0, "Are they all dead?", 35662),
(26713, 0, 0, 0, 0, "This doesn't look good.", 35662),
(54104, 0, 0, 0, 0, "Well? What are you waiting for? ", 35662),
(26503, 0, 0, 0, 0, "Did you find the gnoll plans? ", 35662),
(26569, 0, 0, 0, 0, "Did you recover the spyglasses?", 35662),
(26693, 0, 0, 0, 0, "I hear you loud and clear, $n.", 35662),
(26694, 0, 0, 0, 0, "I hear you loud and clear, $n.", 35662),
(26607, 0, 0, 0, 0, "You again?", 35662),
(26544, 0, 0, 0, 0, "What have you found out?", 35662),
(54106, 0, 0, 0, 0, "Let's find out everything we can. ", 35662),
(26512, 0, 0, 0, 0, "What are you waiting around here for?", 35662),
(26640, 0, 0, 0, 0, "They got Brubaker?", 35662),
(26504, 0, 0, 0, 0, "Come to collect on a bounty?", 35662),
(26510, 0, 0, 0, 0, "Have you recovered the gnomecorder?", 35662),
(26571, 0, 0, 0, 0, "Did you terminate Murdunk and Homurk?", 35662),
(26545, 0, 0, 0, 0, "Do you have the Blackrock invasion plans?", 35662);

