INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(187442, 'deDE', '', 'Eure Bestien auf Unschuldige anzusetzen, ist die schlimmste Eurer vielen Sünden.', 37474),
(38124, 'deDE', 'Umarmt die Finsternis... die ewige Finsternis.', 'Umarmt die Finsternis... die ewige Finsternis.', 37474),
(180366, 'deDE', '', 'Ich freue mich, Euch in der Bastion willkommen zu heißen. Aber... Ihr gleicht keiner Seele, die ich je gesehen habe.', 37474),
(187280, 'deDE', 'Ohne das Haus der Augen ist unser Geheimnis sicher. Markgräfin Sin\'dane darf nichts von unserer Arbeit hier erfahren.', '', 37474),
(185904, 'deDE', 'Eifersucht? Ich bitte Euch. Ich war ihm in jeder Hinsicht überlegen!', '', 37474),
(182733, 'deDE', 'Das Mal des Wissens. Ein Leuchtfeuer gegen Ignoranz und Angst.', '', 37474),
(180367, 'deDE', '', 'Ihr sollt den Kyrianern beitreten! Es ist unsere heilige Aufgabe, Sterbliche für ihr Urteil vor die Richterin zu bringen.', 37474),
(194383, 'deDE', '', 'Das dürfte spannend werden.', 37474),
(182626, 'deDE', '', 'Wenn Ihr bereit seid, tretet an die Flamme und folgt den Anweisungen des Beobachters.', 37474),
(202313, 'deDE', '', 'Lasst mich sie aufspießen! Aufschlitzen! Zerteilen! Ausweiden! Streifen aus ihren...', 37474),
(202311, 'deDE', 'Hm, das ist eine Idee... Wir könnten diese weinerlichen und engstirnigen Kreaturen häuten und ihre Knochen für unseren Aufstieg nutzen. Was tun, was tun...', '', 37474),
(202310, 'deDE', '', 'Ich habe eine bessere Idee, Meister! Last mich sie aufschlitzen!', 37474),
(202312, 'deDE', '', 'Biiitteee, Meister! Es ist so lange her, dass ich lebende Opfer hatte!', 37474),
(202315, 'deDE', 'Ich werde unseren Gästen bei der Sühne helfen. Sobald sie bereut haben, dürft Ihr sie in Stücke reißen und ihre Seelen schreiend in den Schlund schicken!', '', 37474),
(205464, 'deDE', '', 'Ich verstehe jetzt, warum die Geschöpfe Eurer Welt als so Respekt einflößend angesehen werden. Da ist es kein Wunder, dass Ihr die Brennende Legion zurückschlagen konntet. Ich bin für Eure Stärke dankbar, um des Ardenwalds willen.', 37474),
(191962, 'deDE', '<Der Statuensockel ist mit einem Text versehen.>\n\nRespekt verdient man sich durch die Prüfung der Macht. Wir salutieren denen, die aus der Masse hervorstechen.', '', 37474),
(191966, 'deDE', '<In den Sockel der Statue ist ein Text graviert.>\n\nVerborgene Wahrheiten können in der Dunkelheit gefunden werden. Die Geschickten Schleichen an ihren Gegnern ungesehen vorbei.', '', 37474),
(180775, 'deDE', 'Muss... die Dromanin warnen...', 'Muss... die Dromanin warnen...', 37474),
(193577, 'deDE', '', 'Haltet das hier kurz.', 37474),
(186290, 'deDE', 'Elegant und mächtig. Eine Krone, um ein Reich zu verteidigen.', '', 37474),
(193598, 'deDE', '', 'Hmm, wo pack ich Euch denn hin?', 37474),
(193582, 'deDE', '', 'Wollt Ihr das hier mal ausprobieren?', 37474),
(203409, 'deDE', 'Eine Waffe... geschmiedet in den Feuern von Torghast. Eine Gramklinge.', '', 37474),
(196707, 'deDE', 'Feuern, wenn bereit!', '', 37474),
(199409, 'deDE', 'Ihr wollt meine Anima? Warum können die Tirnenn das nicht tun?', '', 37474),
(199444, 'deDE', 'Da. Bringen wir\'s zu Ende!', '', 37474),
(198956, 'deDE', 'Dafür habe ich keine Zeit, $C, die Gräfin wartet.', 'Dafür habe ich keine Zeit, $C, die Gräfin wartet.', 37474),
(206412, 'deDE', '', 'Was bringt Euch nach Maldraxxus, $p?\n\nMehr Pilze für die Festmahle bei Hof oder mehr Soldaten, um Eure Mauern zu verteidigen?\n\n<Baronin Vashj lächelt.>\n\nIch muss gestehen, dass mir der Gluthof weit besser gefällt, als ich erwartet hätte.\n\nPrinz Renathal spielt vielleicht den Gastgeber auf der Hauptbühne, aber ich habe Euch viel öfter beobachtet.\n\nIhr steuert die Dinge im Hintergrund, wie ich es in meiner Zeit am Hof der Königin tat.\n\n<Baronin Vashj starrt einen Augenblick ins Leere.>\n\nIhr habt keine leichte Aufgabe.', 37474),
(202361, 'deDE', '$gKyrianer:Kyrianerin;! Willkommen im Äonischen Archiv, wo die Anima unsere Fähigkeiten stärken kann.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Leitstern des Aufgestiegenen]|h|r: Solange Eure Gesundheit über 90% liegt, haben Eure Schadenszauber und -fähigkeiten eine Chance, Euer Ziel mit \'Zur Einziehung markiert\' zu belegen und ihm so mit der Zeit Schaden zuzufügen. Wenn Ihr den Gegner während dieser Zeit besiegt, zieht Ihr seine Anima ein, was Euch für kurze Zeit einen Schild gewährt.', '$gKyrianer:Kyrianerin;! Willkommen im Äonischen Archiv, wo die Anima unsere Fähigkeiten stärken kann.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Leitstern des Aufgestiegenen]|h|r: Solange Eure Gesundheit über 90% liegt, haben Eure Schadenszauber und -fähigkeiten eine Chance, Euer Ziel mit \'Zur Einziehung markiert\' zu belegen und ihm so mit der Zeit Schaden zuzufügen. Wenn Ihr den Gegner während dieser Zeit besiegt, zieht Ihr seine Anima ein, was Euch für kurze Zeit einen Schild gewährt.', 37474),
(199451, 'deDE', '', 'Hah... das eine Mal mit Traumweber...', 37474),
(183994, 'deDE', '', 'Ein Stück nach links!', 37474),
(183995, 'deDE', '', 'Links! Rechts! Das andere Rechts!', 37474),
(183993, 'deDE', 'Könnt Ihr die Spitze erreichen?', '', 37474),
(204708, 'deDE', 'Die tote Graumähne hebt brav ihre Hufe und ist froh, dass sich jemand um ihre Verletzungen kümmert. Sie ist außer Atem, keucht schwer und scheint Durst zu haben.', '', 37474),
(204686, 'deDE', 'Die tote Graumähne zittert und stupst Euch sanft an, als der Striegel ihre gereizte Haut säubert. Sie verlagert ihr Gewicht und stampft auf. Ihr bemerkt, dass ihre Hufe stark gespalten sind.', '', 37474),
(204663, 'deDE', 'Die tote Graumähne nimmt den Hafer freudig in Empfang. Obwohl sie an manchen Stellen zottelig ist, zuckt ihre Haut vor Irritation durch Milbenbisse, spitze Dornen und stinkenden Schlamm.', '', 37474),
(204710, 'deDE', 'Die tote Graumähne steht ganz still, während man ihr die Satteldecke auflegt. Ihr Zittern wird langsamer und hört dann auf. Sie beginnt, Eure Taschen nach Leckereien zu durchsuchen.', '', 37474),
(204668, 'deDE', 'Die tote Graumähne hält plötzlich an, um Euch nicht niederzurennen.', '', 37474),
(204903, 'deDE', 'Panik steht in den Augen der toten Graumähne. Sie sieht krank und ausgezehrt aus. Sie ist offensichtlich kurz vorm Verhungern und braucht eine herzhafte Mahlzeit.', '', 37474),
(121956, 'deDE', 'Ich, Meister Mathias Shaw, autorisiere hiermit den Roten Säbel mit der Suche nach der abtrünnigen Agentin Amber Kearnen.', '', 37474),
(204709, 'deDE', 'Die tote Graumähne trinkt gierig den ganzen Eimer leer und stößt Euch dann mit ihrem Kopf an. Sie hat ihren Durst gelöscht, zittert aber unkontrolliert.', '', 37474),
(54130, 'deDE', '', 'Rell ist... Er ist schwer verletzt.$b$b<Agent Kearnen schlägt mit der Faust auf den Boden.>$b$bEr stirbt, $n! Und es gibt verdammt nochmal nichts, was wir dagegen tun können!', 37474),
(204749, 'deDE', 'Die Nüstern der toten Graumähne weiten sich, als sie die Äpfel wittert. Eifrig zermalmt sie sie zwischen ihren Zähnen. Die tote Graumähne seufzt zufrieden und legt ihren Kopf an Eure Schulter.', '', 37474),
(121283, 'deDE', 'Amber Kearnen war eine von uns... Fällt mir schwer zu glauben, dass wir sie töten mussten.', '', 37474),
(197546, 'deDE', '', 'Genug! Eure ungelenken Zuckungen machen mich krank!', 37474),
(207303, 'deDE', 'Es ist gut, dass Ihr uns bei der Verteidigung des Waldes helft, $gJäger:Jägerin;.', 'Es ist gut, dass Ihr uns bei der Verteidigung des Waldes helft, $gJäger:Jägerin;.', 37474),
(189107, 'deDE', 'Auch wenn wir fähige Zauberwirker brauchen, lockern wir unsere Beitrittsanforderungen nicht.', '', 37474);
INSERT INTO `faction_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2463, 'deDE', 'Marasmius', 'Marasmius ist ein unermesslich alter, pilzartiger Bewohner des Ardenwalds und essenzieller Bestandteil seines Ökosystems. Eure Hilfsbereitschaft hat den Teil von ihm erwärmt, der einem Herzen am nächsten kommt, und es gibt immer genug zu tun.', 37474),
(2410, 'deDE', 'Die Unvergängliche Armee', 'Vor langer Zeit errichtete der Primus die fünf Häuser von Maldraxxus, um die ultimative Streitkraft aufzubauen. Diese unbeugsame Armada hat geschworen, die Schattenlande durch ihre Macht, Gerissenheit und Listigkeit gegen alle Bedrohungen zu verteidigen.', 37474),
(2465, 'deDE', 'Die Wilde Jagd', 'Die Wilde Jagd setzt sich aus den besten Jägerinnen und Jägern des Ardenwalds zusammen und wird in Krisenzeiten gerufen, um der Winterkönigin zu dienen. In einer Zeit der Gefahren und der Dürre steht die Wilde Jagd bereit, den Wald zu verteidigen.', 37474),
(2462, 'deDE', 'Flickmeister', 'Die Flickmeister sind berühmt für ihre Fähigkeit, Fleisch, Knochen und Anima zu Konstrukten zu verschmelzen, die sie zu mächtigen Verbündeten in Maldraxxus machen. Sie haben sich nach ihrer Trennung vom verräterischen Haus der Konstrukte am Metzgerblock niedergelassen.', 37474),
(2464, 'deDE', 'Hof der Nacht', 'Die Animadürre und die Invasion der Drust beschäftigen die Winterkönigin und die Wilde Jagd. Daher müssen die Dromanen des Hofs der Nacht auf sich allein gestellt die Wildsamen beschützen und ihre Haine verteidigen.', 37474);
