INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(1309, 'deDE', 'Zirpende Animilbe', '|cFFFFD200Abenteuer:|r Shadowlands|n', 'Diese außergewöhnlich wendigen Flieger sind ausgezeichnete Reittiere, sobald man ihnen beigebracht hat, ihren Reitern nicht ihre gesamte Anima zu entziehen.', 37474),
(1420, 'deDE', 'Umbralsichelhorn', '|cFFFFD200Verkäufer: |rSpindelzinken|n|cFFFFD200Zone: |rArdenwald|n', 'Umbralgorm sind die friedlichsten aller Gorm, obwohl sie die größten Hörner und Mandibeln haben. Sie sind so friedlich, weil es andere Kreaturen tunlichst vermeiden, sie zu stören.', 37474),
(1414, 'deDE', 'Sündenmähre Graumähne', '|cFFFFD200NSC:|r Tote Graumähne|n|cFFFFD200Ort:|r Revendreth|n|n', 'Zu Lebzeiten schien Graumähne immer ein liebes, folgsames Pferd mit reinem Gemüt und gesegnetem Appetit zu sein. Welche Schandtaten sie wohl im Geheimen begangen hat, um sich einen Platz unter den verurteilten Seelen von Revendreth zu verdienen?', 37474),
(1291, 'deDE', 'Glücksyak Yun', '|cFFFFD200WoW-Shop|r|n', 'Die Pandaren glauben, dass einem Wohlstand gewährt wird, wenn man nur seine Hände am glänzenden Fell des Ochsen reibt. Dazu muss man ihn allerdings erst einmal erwischen.', 37474),
(1424, 'deDE', 'Schneesturm', '|cFFFFD20030. Geburtstag von Blizzard|r ', 'Er kam aus Schnee und Eis in die Sonne von Azeroth, die brennt so heiß.', 37474);
