INSERT INTO `mail_template_locale` (`ID`, `locale`, `Body_lang`, `VerifiedBuild`) VALUES
(568, 'deDE', 'An $p,\n\nDie Treffsicherheit Eurer aktuellen Quote fechte ich nicht an.\n\nAber sie ist doch ungewöhnlich hoch. Ich schätze, Ihr seid der Schlundgänger...\n\nDoch ich genieße unseren Wettstreit und will mit Euch mithalten.\n\nIch zähle 138 tödliche Treffer seit Eurem letzten Brief und habe...\n\n<Jagdhauptmann Korayns Schrift geht über das Blatt hinaus.>\n\n152 tödliche Treffer! Ein paar der letzten Welle waren zwar Milben, aber bei Eurem Vorsprung darf man nicht wählerisch sein!\n\nÜbertrefft das,\nJagdhauptmann Korayn', 37474);
INSERT INTO `chr_classes_locale` (`ID`, `locale`, `Name_lang`, `NameMale_lang`, `NameFemale_lang`, `Description_lang`, `RoleInfoString_lang`, `DisabledString_lang`, `HyphenatedNameMale_lang`, `HyphenatedNameFemale_lang`, `VerifiedBuild`) VALUES
(3, 'deDE', 'Jäger', 'Jäger', 'Jägerin', 'Jäger sind in der Wildnis zu Hause und haben ein besonderes Verhältnis zu Tieren. Sie verwenden Waffen und ihre Begleiter, um Schaden zu verursachen.', 'Rollen: |cffffffffNahkampf- oder Fernkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Jäger', 'Jägerin', 37474),
(1, 'deDE', 'Krieger', 'Krieger', 'Kriegerin', 'Krieger sind Kämpfer in Plattenrüstung, die nach Perfektion im bewaffneten Kampf streben. Im Gefecht erzeugen sie Wut, die für ihre Spezialangriffe benötigt wird.', 'Rollen: |cffffffffTank, Nahkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Krieger', 'Kriegerin', 37474),
(11, 'deDE', 'Druide', 'Druide', 'Druidin', 'Druiden sind Gestaltwandler mit besonderer Vorliebe für das Tier- und Pflanzenreich. Druiden können sich entweder auf Heilung oder Distanzschadenszauber spezialisieren oder Bären- beziehungsweise Katzengestalt annehmen, um im Nahkampf zu kämpfen.', 'Rollen: |cffffffffTank, Heilung, Fernkampfschaden, Nahkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Druide', 'Druidin', 37474),
(5, 'deDE', 'Priester', 'Priester', 'Priesterin', 'Priester sind gut ausgewogene Heiler mit einer Vielzahl an Werkzeugen. Sie können jedoch auch ihr Heilungspotential opfern, um durch Schattenmagie Schaden zu verursachen.', 'Rollen: |cffffffffHeilung, Fernkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Priester', 'Priesterin', 37474),
(8, 'deDE', 'Magier', 'Magier', 'Magierin', 'Magier sind die Zauberwirker von Azeroth schlechthin. Sie erlernen ihre Kunst durch intensives Studium und Nachforschungen. Magier sind nur leicht gerüstet, gleichen das jedoch mit einem mächtigen Arsenal offensiver und defensiver Zauber aus.', 'Rolle: |cffffffffFernkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Magier', 'Magierin', 37474),
(7, 'deDE', 'Schamane', 'Schamane', 'Schamanin', 'Schamanen rufen die Mächte der Elemente herbei, um den Schaden ihrer Waffen zu erhöhen oder Zauber zu wirken. Schamanen beschwören im Kampf Totems – kleine Objekte, die Gegner behindern, Heilung hervorrufen oder Schaden verursachen.', 'Rollen: |cffffffffHeilung, Fernkampfschaden, Nahkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Schamane', 'Schamanin', 37474),
(12, 'deDE', 'Dämonenjäger', 'Dämonenjäger', 'Dämonenjägerin', 'Dämonenjäger sind eine Heldenklasse, was bedeutet, dass sie auf einer hohen Stufe starten. Sie sind dunkle Ausgestoßene, die ihre Feinde mithilfe verbotener dämonischer Magie jagen und töten.', 'Rollen: |cffffffffTank, Nahkampfschaden|r', 'Benötigt Legion sowie einen Charakter auf Stufe 70 oder höher', 'Dämonen-|njäger', 'Dämonen-|njägerin', 37474),
(9, 'deDE', 'Hexenmeister', 'Hexenmeister', 'Hexenmeisterin', 'Hexenmeister wirken Feuer- oder Schattenmagie, um Schaden zu verursachen, Energie zu entziehen oder zu verfluchen. Sie beschwören Dämonen, die ihnen zu Diensten sind.', 'Rolle: |cffffffffFernkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Hexen-|nmeister', 'Hexen-|nmeisterin', 37474),
(4, 'deDE', 'Schurke', 'Schurke', 'Schurkin', 'Schurken dienen häufig als Assassinen oder Späher, obwohl auch viele Einzelgänger sind. Schurken können oft um Feinde herumschleichen oder sie von hinten angreifen, um sie möglichst schnell zu erledigen.', 'Rolle: |cffffffffNahkampfschaden|r', 'Ihr müsst ein anderes Volk wählen, um diese Klasse spielen zu können.', 'Schurke', 'Schurkin', 37474);
