INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'frFR', '', 'Bienvenue, l\’$gami:amie;. Vous vous intéressez aux clés mythiques ?', 38134),
(209048, 'frFR', 'À l\’intérieur d\’Oribos, l\’agresseur Zo\’dash peut répondre à vos besoins. Vous pouvez également trouver un améliorateur d\’objets dans votre sanctum de congrégation.', 'À l\’intérieur d\’Oribos, l\’agresseur Zo\’dash peut répondre à vos besoins. Vous pouvez également trouver un améliorateur d\’objets dans votre sanctum de congrégation.', 38134),
(208581, 'frFR', 'Salutations, UTILISATEUR. J\’espère que vous passez une ADJECTIF POSITIF ALÉATOIRE journée.', '', 38134),
(208211, 'frFR', 'Mon vieux maître, le gardien de la crypte Kassir, vous attend dans les salles de l\’Expiation, $p.', '', 38134),
(208209, 'frFR', 'Tubbins vous cherche partout dans la province des Braises, $p.', '', 38134),
(208205, 'frFR', 'Gresit a demandé à vous voir dans les salles de l\’Expiation, $p.', '', 38134),
(208120, 'frFR', 'Certains jours sont très spéciaux ! Si vous voyez mon coffre doré ici, c\’est qu\’on fête un régisseur.\n\nAdressez-vous-hou à moi et donnez un purian mûr à la vedette du jour. Revenez, ouvrez le coffre et c\’est gagné !', '', 38134),
(208019, 'frFR', 'Tous ensemble, tous ensemble…', '', 38134),
(208018, 'frFR', 'Le marchedroit Adrestès fredonne et tape des pieds en rythme.', '', 38134),
(208016, 'frFR', '', 'Où que vous alliez, je ne vous quitterai pas des yeux.', 38134),
(208015, 'frFR', '$uMeilleur ami:Meilleure amie; toujours donner moi meilleur papier !', '', 38134),
(208014, 'frFR', '', 'Des tas d\’amis. Plein de bonheur. Sika a bien de la chance.', 38134),
(208013, 'frFR', 'Quel titre donner à $gmon meilleur ami et apprenti:ma meilleure amie et apprentie; ? Appartenaire ? Non… $uAmipprenti:Amipprentie; ? Non plus…', '', 38134),
(208012, 'frFR', 'L\’union fait la force. Puissions-nous ne jamais être séparés.', '', 38134),
(208011, 'frFR', '', 'Si la mort nous a $uréunis:réunies;, quelle force pourrait bien nous séparer ?', 38134),
(208010, 'frFR', 'Que tout le monde se taise et écoute $p ! Mon acolyte en sait plus long sur l\’art du combat que vous tous réunis.', '', 38134),
(208009, 'frFR', '', 'L\’arbre prospère au sein de la forêt, $p. Revenez vite me voir, et nous prospérerons ensemble.', 38134),
(208008, 'frFR', 'Choufa se met à chanter doucement.', '', 38134),
(208007, 'frFR', 'Et on a décidé que c\’était $p, le bon génie de tous les purotins !', '', 38134),
(208006, 'frFR', 'On a des tas de copains maintenant, mais on peut avoir qu\’un seul meilleur ami…', '', 38134),
(208005, 'frFR', 'Vivement la prochaine réception de la cour des Braises. Peut-être même accorderai-je une danse à cette aristocrate…', '', 38134),
(208004, 'frFR', '', 'Une proie chasse l\’autre, mais il n\’y en a pas deux comme vous, $p.', 38134),
(208003, 'frFR', 'Sachez qu\’en cas de besoin, ma lame est à votre disposition. Je répondrai toujours présent à votre appel, $gmon frère:ma sœur;.', '', 38134),
(208002, 'frFR', '', 'Félicitations, auguste $p. Votre cour est splendide, et vous aussi.', 38134),
(208001, 'frFR', 'Détendez-vous, $p, rien ne presse. Entre amis, on prend le temps.', '', 38134),
(208000, 'frFR', '', 'Dans quel pétrin allons-nous encore nous fourrer, $p ? J\’ai hâte de le découvrir !', 38134),
(207999, 'frFR', '', 'Filez, jeune $p, je ne vous retiens pas plus longtemps.', 38134),
(207996, 'frFR', 'Faites attention à vous, $p. Il y a toujours plus d\’âmes à sauver, mais n\’oubliez pas que certaines sont perdues à jamais.', '', 38134),
(207951, 'frFR', 'Erreur. Accès interdit faute d\’expérience suffisante.', '', 38134),
(207923, 'frFR', 'Sylvarden a grand besoin de vos services, $n.', 'Sylvarden a grand besoin de vos services, $n.', 38134),
(207903, 'frFR', '', 'Que diriez-vous d\’une petite histoire ?', 38134),
(207841, 'frFR', '$n ! Par ici, l\’$uami:amie; !', '', 38134),
(207504, 'frFR', 'C\’est un temps idéal pour une balade dans les airs ! Où est-ce que je peux vous envoyer ?', '', 38134),
(207499, 'frFR', '', '<Dame Ilinca vous sourit.>\n\nExcellente question. Vous avez tout intérêt à adapter chaque semaine votre liste d\’invités afin d\’éviter les éventuelles incompatibilités d\’humeur.\n\nAdressez-vous au seigneur Garridan pour accéder à la liste et révoquer une invitation.\n\nVous devrez peut-être dépenser quelques rubis infusés pour faire passer la pilule, à moins que vous ne préfériez engager des ambassadeurs ou m\’acheter des invitations vierges. Cela suffira amplement à couvrir les frais occasionnés.\n\nEt surtout, gardez-vous bien de réunir des convives aux goûts diamétralement opposés. Vous auriez alors bien du mal à satisfaire tout le monde.', 38134),
(207446, 'frFR', 'Unité est un assemblage unique, lié à l\’Ombreterre par un irrésistible sens du devoir. Elle est capable de découvrir l\’armure qui symbolise notre objectif envers l\’Ombreterre, tout en accomplissant des tâches pour l\’Armée immortelle en Maldraxxus.', 'Unité est un assemblage unique, lié à l\’Ombreterre par un irrésistible sens du devoir. Elle est capable de découvrir l\’armure qui symbolise notre objectif envers l\’Ombreterre, tout en accomplissant des tâches pour l\’Armée immortelle en Maldraxxus.', 38134),
(207442, 'frFR', '', 'Je vous le dirai quand vous aurez conduit votre ami ici présent à Oribos.\n\nRevenez me voir quand il sera en sécurité.', 38134),
(207434, 'frFR', 'Théotar, est-ce vous ?', '', 38134),
(207388, 'frFR', '', 'Que puis-je faire pour vous ?', 38134),
(207376, 'frFR', 'Quel dommage que vous ayez préféré les courbettes aux coups ravageurs, Sans-Entraves.', 'Quel dommage que vous ayez préféré les courbettes aux coups ravageurs, Sans-Entraves.', 38134),
(207372, 'frFR', 'Les Faë nocturnes ont de la chance d\’avoir quelqu\’un d\’aussi intrépide à leur service, Sans-Entraves.', 'Les Faë nocturnes ont de la chance d\’avoir quelqu\’un d\’aussi intrépide à leur service, Sans-Entraves.', 38134),
(207371, 'frFR', 'Il semble que la nature ait décidé de vous appeler à son service, $n. Une bien noble cause.', 'Il semble que la nature ait décidé de vous appeler à son service, $n. Une bien noble cause.', 38134),
(207370, 'frFR', 'Bien que vous n\’ayez pas rejoint notre congrégation, vous êtes au service d\’un cycle vital, Sans-Entraves.', 'Bien que vous n\’ayez pas rejoint notre congrégation, vous êtes au service d\’un cycle vital, Sans-Entraves.', 38134),
(207369, 'frFR', 'Transmettez nos respects à la reine de l\’hiver. C\’est une fidèle alliée de l\’Archonte.', 'Transmettez nos respects à la reine de l\’hiver. C\’est une fidèle alliée de l\’Archonte.', 38134),
(207368, 'frFR', 'Je vous souhaite de trouver satisfaction dans votre… moisson et de vous épanouir dans cette voie, $n.', 'Je vous souhaite de trouver satisfaction dans votre… moisson et de vous épanouir dans cette voie, $n.', 38134),
(207367, 'frFR', 'Puissiez-vous aider les âmes égarées à se libérer de leur fardeau, Sans-Entraves.', 'Puissiez-vous aider les âmes égarées à se libérer de leur fardeau, Sans-Entraves.', 38134),
(207366, 'frFR', 'Les Venthyrs comprennent que le passé est un fardeau. Nos congrégations ont bien des choses en commun, $n.', 'Les Venthyrs comprennent que le passé est un fardeau. Nos congrégations ont bien des choses en commun, $n.', 38134),
(207365, 'frFR', 'Vous avez choisi de servir les Venthyrs ? Guider les âmes vers la rédemption est une tâche d\’importance.', 'Vous avez choisi de servir les Venthyrs ? Guider les âmes vers la rédemption est une tâche d\’importance.', 38134),
(207364, 'frFR', 'Allez et servez… quoi que l\’on puisse exiger de vous.', 'Allez et servez… quoi que l\’on puisse exiger de vous.', 38134),
(207363, 'frFR', 'L\’Archonte vous conservera sa faveur, quelle que soit votre congrégation.', 'L\’Archonte vous conservera sa faveur, quelle que soit votre congrégation.', 38134),
(207362, 'frFR', 'Puissiez-vous faire régner la discipline dans le royaume déchiré, $n !', 'Puissiez-vous faire régner la discipline dans le royaume déchiré, $n !', 38134),
(207361, 'frFR', 'Je vous croyais plus apte à servir qu\’à vous battre, Sans-Entraves. Peut-être me suis-je trompé…', 'Je vous croyais plus apte à servir qu\’à vous battre, Sans-Entraves. Peut-être me suis-je trompée…', 38134),
(207360, 'frFR', 'Vous faites honneur à notre congrégation, Sans-Entraves.', 'Vous faites honneur à notre congrégation, Sans-Entraves.', 38134),
(207359, 'frFR', 'Je me réjouis de voir que nos destins convergent, $n. Nous vous savons gré de votre dévouement.', 'Je me réjouis de voir que nos destins convergent, $n. Nous vous savons gré de votre dévouement.', 38134),
(207358, 'frFR', 'Ah, Sans-Entraves ! En mission pour l\’Archonte, j\’imagine ? Allez et servez.', 'Ah, Sans-Entraves ! En mission pour l\’Archonte, j\’imagine ? Allez et servez.', 38134),
(207357, 'frFR', 'Nous avons de la chance de vous avoir parmi nous, $n. Puisse la grâce de l\’Archonte guider vos pas.', 'Nous avons de la chance de vous avoir parmi nous, $n. Puisse la grâce de l\’Archonte guider vos pas.', 38134),
(207356, 'frFR', 'Quand vous en aurez assez de batifoler avec les Faë, revenez ici pour passer aux choses sérieuses.', 'Quand vous en aurez assez de batifoler avec les Faë, revenez ici pour passer aux choses sérieuses.', 38134),
(207355, 'frFR', 'Transmettez nos salutations à la reine de l\’hiver, Sans-Entraves.', 'Transmettez nos salutations à la reine de l\’hiver, Sans-Entraves.', 38134),
(207354, 'frFR', 'Nos forêts ne sont peut-être pas aussi majestueuses que celles de Sylvarden, mais nous nous battons nous aussi pour protéger notre royaume.', 'Nos forêts ne sont peut-être pas aussi majestueuses que celles de Sylvarden, mais nous nous battons nous aussi pour protéger notre royaume.', 38134),
(207353, 'frFR', 'S\’ils ne recherchent pas le pouvoir, alors que font donc les Faë nocturnes dans leurs bosquets broussailleux ?', 'S\’ils ne recherchent pas le pouvoir, alors que font donc les Faë nocturnes dans leurs bosquets broussailleux ?', 38134),
(207352, 'frFR', 'Vous avez bien fait de rejoindre notre congrégation, $n. Le velours vous ira fort bien.', 'Vous avez bien fait de rejoindre notre congrégation, $n. Le velours vous ira fort bien.', 38134),
(207351, 'frFR', 'Je suis avec la cour des Moissonneurs et avec vous, Sans-Entraves.', 'Je suis avec la cour des Moissonneurs et avec vous, Sans-Entraves.', 38134),
(207350, 'frFR', 'Vous devez collectionner de délicieux péchés pour votre stèle du vice !', 'Vous devez collectionner de délicieux péchés pour votre stèle du vice !', 38134),
(207349, 'frFR', 'Grâce à votre aide, $n, nous accomplirons notre noble mission.', 'Grâce à votre aide, $n, nous accomplirons notre noble mission.', 38134),
(207348, 'frFR', 'De toutes les congrégations, je parie que les Kyrians ont les stèles du vice les plus barbantes.', 'De toutes les congrégations, je parie que les Kyrians ont les stèles du vice les plus barbantes.', 38134),
(207347, 'frFR', 'Revendreth est certes un lieu de pénombre, mais nos devoirs ne sont pas moins nobles que les vôtres.', 'Revendreth est certes un lieu de pénombre, mais nos devoirs ne sont pas moins nobles que les vôtres.', 38134),
(207346, 'frFR', '', 'Merci… Je suis heureuse de savoir que mes dernières heures n\’ont pas été vaines.', 38134),
(207345, 'frFR', 'Vos régisseurs semblent très professionnels, $n. Nous aurions quelques cryptes à récurer…', 'Vos régisseurs semblent très professionnels, $n. Nous aurions quelques cryptes à récurer…', 38134),
(207343, 'frFR', 'Certes, nos méthodes diffèrent, mais nos congrégations cherchent toutes deux à guider les âmes sur une noble voie.', 'Certes, nos méthodes diffèrent, mais nos congrégations cherchent toutes deux à guider les âmes sur une noble voie.', 38134),
(207342, 'frFR', 'Vous avez choisi d\’emprunter la voie de la violence, $umortel:mortelle;. Si jamais vous avez besoin de finesse, vous savez où nous trouver.', 'Vous avez choisi d\’emprunter la voie de la violence, $umortel:mortelle;. Si jamais vous avez besoin de finesse, vous savez où nous trouver.', 38134),
(207341, 'frFR', '', 'Notre margrave a peut-être disparu, mais nos efforts ne doivent pas s\’arrêter pour autant. Nous aurons notre vengeance en temps voulu.\n\nUne minute… Vous venez d\’Azeroth ! Dites-moi, mon message est-il parvenu aux Sans-Couronne ?', 38134),
(207340, 'frFR', 'Vous savez, la cour des Braises est plus dangereuse qu\’il n\’y paraît. Cela vous plairait beaucoup.', 'Vous savez, la cour des Braises est plus dangereuse qu\’il n\’y paraît. Cela vous plairait beaucoup.', 38134),
(207339, 'frFR', 'Content de savoir que votre congrégation se bat pour défendre notre royaume, Sans-Entraves.', 'Contente de savoir que votre congrégation se bat pour défendre notre royaume, Sans-Entraves.', 38134),
(207338, 'frFR', '', 'Notre margrave a peut-être disparu, mais nos efforts ne doivent pas s\’arrêter pour autant. Nous aurons notre vengeance en temps voulu.', 38134),
(207337, 'frFR', 'Rangez votre scalpel et vos aiguilles, Nécro-seigneur. Je suis bien dans ma peau telle qu\’elle est.', 'Rangez votre scalpel et vos aiguilles, Nécro-seigneur. Je suis bien dans ma peau telle qu\’elle est.', 38134),
(207335, 'frFR', 'Qu\’est-ce que vous traînez dans votre sillage, $n ? On dirait… des paillettes ?', 'Qu\’est-ce que vous traînez dans votre sillage, $n ? On dirait… des paillettes ?', 38134),
(207334, 'frFR', 'Sans-Entraves ! Vous en avez assez de faire des boutures ? Vous êtes $uparé:parée; à vous battre ?', 'Sans-Entraves ! Vous en avez assez de faire des boutures ? Vous êtes $uparé:parée; à vous battre ?', 38134),
(207333, 'frFR', 'Les Nécro-seigneurs préfèrent la stratégie aux ruses, Faë nocturne.', 'Les Nécro-seigneurs préfèrent la stratégie aux ruses, Faë nocturne.', 38134),
(207332, 'frFR', 'Non. Je n\’irai pas voir votre pièce.', 'Non. Je n\’irai pas voir votre pièce.', 38134),
(207331, 'frFR', 'La meilleure façon de débarrasser une âme de sa fierté est encore de la rouer de coups.', 'La meilleure façon de débarrasser une âme de sa fierté est encore de la rouer de coups.', 38134),
(207330, 'frFR', 'Attention où vous mettez les pieds, Venthyr. Vous risquez de souiller vos jolis souliers.', 'Attention où vous mettez les pieds, Venthyr. Vous risquez de souiller vos jolis souliers.', 38134),
(207329, 'frFR', 'Des fêtes et des complots… Pourquoi les Venthyrs n\’en viennent-ils jamais aux mains ?', 'Des fêtes et des complots… Pourquoi les Venthyrs n\’en viennent-ils jamais aux mains ?', 38134),
(207328, 'frFR', 'Je passe mes journées à me battre, certes… mais mes souvenirs m\’appartiennent.', 'Je passe mes journées à me battre, certes… mais mes souvenirs m\’appartiennent.', 38134),
(207327, 'frFR', 'Vous ne trouverez pas de bassin de méditation ici.', 'Vous ne trouverez pas de bassin de méditation ici.', 38134),
(207326, 'frFR', 'Venez. Montrez-moi les élégantes attaques que vous avez apprises au Bastion.', 'Venez. Montrez-moi les élégantes attaques que vous avez apprises au Bastion.', 38134),
(207324, 'frFR', 'Je vous inviterais bien au théâtre de la Souffrance, mais votre tenue risquerait d\’être souillée.', 'Je vous inviterais bien au théâtre de la Souffrance, mais votre tenue risquerait d\’être souillée.', 38134),
(207323, 'frFR', 'Lorsque votre enveloppe mortelle aura fait son temps, nous vous en confectionnerons une autre.', 'Lorsque votre enveloppe mortelle aura fait son temps, nous vous en confectionnerons une autre.', 38134),
(207321, 'frFR', 'Vous incarnez les valeurs du Primat, $n.', 'Vous incarnez les valeurs du Primat, $n.', 38134),
(207320, 'frFR', 'Vous avez eu raison de nous rejoindre, $n. Les autres congrégations jalouseront votre puissance.', 'Vous avez eu raison de nous rejoindre, $n. Les autres congrégations jalouseront votre puissance.', 38134),
(207319, 'frFR', 'Quel que soit mon destin, je l\’aurai gagné à la force des poings.', 'Quel que soit mon destin, je l\’aurai gagné à la force des poings.', 38134),
(207318, 'frFR', 'Eskrokar n\’avait jamais vu un endroit pareil, ça non !\n\n<Eskrokar vous fait signe de vous approcher et murmure dans votre oreille.>\n\nChut ! Ne faites pas de bruit !\n\nCes négociants, ils proposent des tas de trucs pour nous autres « mortels » et j\’compte bien découvrir quoi.\n\nP\’têtre qu\’Eskrokar dénichera une ou deux nouvelles amulettes à revendre, hé hé.', '', 38134),
(207316, 'frFR', 'Ah. Une autre âme d\’Azeroth.\n\nNon, pas une âme. La lumière d\’An\’she réchauffe encore votre chair.\n\nComme j\’aimerais pouvoir contempler Haut-Roc une dernière fois… Je prie la Terre-Mère que ma famille y demeure toujours.\n\nBeaucoup ici sont impatients de faire leur ascension malgré les regrets qui les hantent, mais j\’ai pour moi le souvenir de ceux que j\’aime. J\’espère qu\’ils pensent à moi, même s\’il me faudra un jour les oublier.\n\nMais pas aujourd\’hui. Non, pas aujourd\’hui.', '', 38134),
(207315, 'frFR', 'Je vous connais. Nous avons combattu ensemble à Haut-Roc. La vie a été clémente avec vous.\n\nJ\’espère que Kura et les enfants ne m\’ont pas pleuré trop longtemps et que Kura a retrouvé l\’amour. Mais j\’espère garder certains souvenirs. Caresser Lura. Baptiser Junior. Apprendre à Mycah à manier le couteau comme un fantôme…\n\nJe ne me souviens pas de ma fin. Seulement qu\’elle a été rapide. Qu\’est devenu Dargrul ?\n\nNon, ne me dites rien. Cette colère, cette douleur, tout cela appartient à un passé dont les Kyrians tentent de nous délivrer.', '', 38134),
(207314, 'frFR', 'Alors, vous profitez du crépuscule éternel, Venthyr ?', 'Alors, vous profitez du crépuscule éternel, Venthyr ?', 38134),
(207313, 'frFR', 'Je ne comprendrai jamais la fascination des Venthyrs pour la pierre.', 'Je ne comprendrai jamais la fascination des Venthyrs pour la pierre.', 38134),
(207312, 'frFR', 'Nous comptons sur votre vigilance, Nécro-seigneur.', 'Nous comptons sur votre vigilance, Nécro-seigneur.', 38134),
(207311, 'frFR', 'Les alliés du Primat sont les alliés de la forêt.', 'Les alliés du Primat sont les alliés de la forêt.', 38134),
(207310, 'frFR', 'Nous servons notre congrégation comme l\’exige notre devoir.', 'Nous servons notre congrégation comme l\’exige notre devoir.', 38134),
(207309, 'frFR', 'Que nos brises portent vos ailes, âme kyriane.', 'Que nos brises portent vos ailes, âme kyriane.', 38134),
(207308, 'frFR', 'La cour de la Nuit a de la chance de vous compter dans ses rangs, $n.', 'La cour de la Nuit a de la chance de vous compter dans ses rangs, $n.', 38134),
(207307, 'frFR', 'Nous sommes ravis que vous ayez choisi notre congrégation, Sans-Entraves. Pour la reine et pour nos bosquets.', 'Nous sommes ravis que vous ayez choisi notre congrégation, Sans-Entraves. Pour la reine et pour nos bosquets.', 38134),
(207306, 'frFR', 'Les âmes orgueilleuses n\’ont pas leur place ici, Venthyr.', 'Les âmes orgueilleuses n\’ont pas leur place ici, Venthyr.', 38134),
(207305, 'frFR', 'L\’Hallali est avec vous, Nécro-seigneur.', 'L\’Hallali est avec vous, Nécro-seigneur.', 38134),
(207304, 'frFR', 'Allez avec courage, âme kyriane.', 'Allez avec courage, âme kyriane.', 38134),
(207302, 'frFR', 'Que la Nuit vous enveloppe, Venthyr. Même si j\’ai l\’impression que c\’est déjà le cas…', 'Que la Nuit vous enveloppe, Venthyr. Même si j\’ai l\’impression que c\’est déjà le cas…', 38134),
(207301, 'frFR', 'Vous comptez rester longtemps dans la forêt, Nécro-seigneur ?', 'Vous comptez rester longtemps dans la forêt, Nécro-seigneur ?', 38134),
(207300, 'frFR', 'Que le bosquet veille sur vous, âme kyriane. J\’espère que le Bastion a bien tenu face à la pénurie.', 'Que le bosquet veille sur vous, âme kyriane. J\’espère que le Bastion a bien tenu face à la pénurie.', 38134),
(207299, 'frFR', 'Les bosquets ont une dette envers vous, $n !', 'Les bosquets ont une dette envers vous, $n !', 38134),
(207298, 'frFR', 'C\’est une surprise de vous voir ici, Venthyr. Une mauvaise surprise…', 'C\’est une surprise de vous voir ici, Venthyr. Une mauvaise surprise…', 38134),
(207297, 'frFR', 'Qu\’est-ce que c\’est que cette odeur ? Oh, mais bien sûr, un Nécro-seigneur. Suis-je bête !', 'Qu\’est-ce que c\’est que cette odeur ? Oh, mais bien sûr, un Nécro-seigneur. Suis-je bête !', 38134),
(207295, 'frFR', 'Oh, une âme kyriane ! Mais sans ailes… pas comme moi !', 'Oh, une âme kyriane ! Mais sans ailes… pas comme moi !', 38134),
(207294, 'frFR', 'Oh, vous êtes $n ! J\’ai adoré votre pièce !', 'Oh, vous êtes $n ! J\’ai adoré votre pièce !', 38134),
(207289, 'frFR', 'Dire que j\’étais à ça d\’obtenir mon heaume…\n\n<L\’amiral Taylor soupire, bien qu\’il n\’ait plus besoin de respirer.>', '', 38134),
(207288, 'frFR', 'Inutile de me remercier.', '', 38134),
(207287, 'frFR', 'Je me souviens que c\’est toi qui m\’as tué.', '', 38134),
(207286, 'frFR', 'Et toi, Koltira ? Te rappelles-tu ta propre fin ?', '', 38134),
(207285, 'frFR', 'Il ne sert à rien de spéculer. Ce qui est fait est fait. Il ne nous reste plus qu\’à aller de l\’avant, aussi inexorablement que la Mort.', '', 38134),
(207284, 'frFR', 'Je vois. Quelle destinée l\’Arbitre nous aurait-elle réservée, à ton avis ?', '', 38134),
(207283, 'frFR', 'Si c\’est le cas, je ne m\’en souviens pas.', '', 38134),
(207282, 'frFR', 'As-tu le moindre souvenir de ta mort, Thassarian ? Les Kyrians sont-ils venus chercher ton âme ?', '', 38134),
(207281, 'frFR', 'Un argument de poids.', '', 38134),
(207280, 'frFR', 'Ou Sylvanas Coursevent.', '', 38134),
(207279, 'frFR', 'Genn Grisetête, par exemple ?', '', 38134),
(207278, 'frFR', 'Bolvar Fordragon n\’est plus le roi-liche. Tant qu\’à recevoir des ordres, j\’estime qu\’il y a bien pire commandant que lui.', '', 38134),
(207277, 'frFR', 'Et nous brandissons à présent nos épées au nom de celui qui l\’a porté.', '', 38134),
(207276, 'frFR', 'J\’ai du mal à concevoir que le heaume de domination ait été brisé. Il a symbolisé tout à la fois notre malédiction et notre devoir pendant si longtemps.', '', 38134),
(207272, 'frFR', 'Savez-vous comme je suis mort, $n ? Lors de la défense de Quel\’Thalas, sous les ordres de Sylvanas Coursevent, général des forestiers.\n\nElle a donné sa vie pour sauver son royaume et son peuple. Tout comme moi, elle a été récompensée en étant relevée dans la non-mort. Mais nous avons suivi deux chemins différents.\n\nSylvanas nous a infligé bien plus de souffrances que nous n\’en avons subies de notre vivant. Elle m\’a emprisonné dans les tréfonds de Fossoyeuse des années durant, lorsque j\’ai voulu négocier un cessez-le-feu temporaire.\n\nMême la Lame d\’ébène n\’a rien pu faire pour moi. On dira ce qu\’on voudra sur la reine banshee… mais dans la vie comme dans la mort, elle n\’aura jamais rien cédé.\n\nSylvanas est une adversaire remarquable. Elle l\’a toujours été.', '', 38134),
(207271, 'frFR', 'J\’ai pris les armes pour la première fois lorsque le Fléau mort-vivant s\’est emparé de Lordaeron. Depuis, j\’ai passé bien plus d\’années à me battre dans la mort que de mon vivant.\n\nLes guerres que se livrent les mortels semblent bien dérisoires, par rapport aux batailles que j\’ai aperçues par-delà le voile. J\’ai prêté allégeance à l\’Alliance et je lui reste fidèle. Je défendrai ses membres jusqu\’à mon ultime trépas. Mais la Horde n\’est pas leur adversaire le plus dangereux.\n\nPeut-être s\’en sont-ils rendu compte lorsque la Mort s\’est rapprochée de la Couronne de glace et que les cieux se sont déchirés. Telle est la malédiction des vivants.\n\nCe qui compte vraiment ne leur est révélé que lorsqu\’il est trop tard.', '', 38134),
(207270, 'frFR', 'J\’aimerais avoir un corps massif ! Que le sol se fende sous le poids de mon impact quand je saute !', '', 38134),
(207269, 'frFR', 'Il me faut une charpente solide et de nombreux crochets pour y pendre toutes sortes de choses. Je vous porterai, mon matériel de recherche et vous, dans toutes nos aventures !', '', 38134),
(207268, 'frFR', '', '<Philia vous glisse quelques mots à l\’oreille.>\n\nNe dites rien à Emma, à propos de l\’Antre.\n\nJe… Son cœur ne tiendrait pas le coup.', 38134),
(207266, 'frFR', '', 'Bonjour. Vous semblez bien connaître cet endroit !\n\nAvez-vous croisé mes fils, Jem, Jack et Jake ? Ils devraient être dans les parages.\n\nJe ne partirai pas avant de les avoir retrouvés. Pas question de les perdre une nouvelle fois !', 38134),
(207251, 'frFR', 'J\’honore tous ceux qui servent notre cause.', 'J\’honore tous ceux qui servent notre cause.', 38134),
(207250, 'frFR', '<Stanikos s\’exclame en toute hâte.>\n\nVous les UTILISEZ pas. C\’est juste… un emprunt !', '<Stanikos s\’exclame en toute hâte.>\n\nVous les UTILISEZ pas. C\’est juste… un emprunt !', 38134),
(207248, 'frFR', '<Stanikos vous dévisage.>\n\nVos choses. Les choses que vous avez touchées. Oui.', '<Stanikos vous dévisage.>\n\nVos choses. Les choses que vous avez touchées. Oui.', 38134),
(207247, 'frFR', 'Vous voulez dire… ma collection ? Elle est belle, non ?', 'Vous voulez dire… ma collection ? Elle est belle, non ?', 38134),
(207241, 'frFR', 'Non ! Tu ne peux pas te cacher, Ve\’nari ! Tu… AHH !', '', 38134),
(207231, 'frFR', '', 'Quoi ?', 38134),
(207230, 'frFR', 'Je sais à présent que les actes sont plus éloquents que les mots, Sans-Entraves. Ici, nous devons tous prouver notre bonne volonté.', '', 38134),
(207229, 'frFR', 'Quand les chefs tombent, ils entraînent leurs disciples dans leur chute… Et les inquisiteurs ne font pas exception à la règle.', '', 38134),
(207228, 'frFR', '', 'La voie de Gahiji mène à de sombres profondeurs… Qu\’allez-vous y trouver ?', 38134),
(207227, 'frFR', '', 'Vos royaumes et tribus n\’ont aucun pouvoir ici. Renoncez à vos anciennes allégeances et libérez-vous !', 38134),
(207226, 'frFR', '', 'Votre force de $umortel:mortelle; ne vaut rien ici. Sonnez la cloche et vous verrez combien vous êtes faible.', 38134),
(207225, 'frFR', '', 'Abandonnez-vous au remords. Revivez sans broncher chacun des horribles souvenirs qui vous hantent, et surmontez-les !', 38134),
(207224, 'frFR', '', 'Bon gré mal gré, vous allez être $ujugé:jugée; à l\’aune de vos fautes passées. Tâchez d\’en tirer les leçons qui s\’imposent.', 38134),
(207223, 'frFR', '', 'QUOI ?', 38134),
(207222, 'frFR', '', 'Quoi ?', 38134),
(207221, 'frFR', 'Vous ne vous attendiez sûrement pas à trouver quelqu\’un comme moi parmi les tyrans et autres têtes couronnées qui peuplent si souvent les cages de Revendreth.\n\nMoi, une âme si discrète qu\’elle en passerait presque inaperçue.\n\nEntre nous, mes agissements ne valent pas la peine qu\’on s\’y attarde.\n\nEn revanche, il en va tout autrement de mes paroles.\n\nIl m\’a suffi de murmurer à leur oreille pour convaincre d\’innombrables imbéciles de commettre des atrocités en mon nom, dans mon intérêt et à leur détriment.\n\nApparemment, les Venthyrs n\’apprécient guère ce genre de manigances.', '', 38134),
(207220, 'frFR', 'Qu\’est-ce que la légende sans-entraves attend d\’une humble âme telle que moi ?', '', 38134),
(207219, 'frFR', '<Werimu éclate de rire.>\n\nDemandez-moi plutôt ce que je n\’ai pas fait, $umortel:mortelle; !\n\nJe ne suis pas tout à fait convaincu que tout ce que ces Venthyrs désignent sous l\’appellation de péché en soit vraiment, mais ceux sur lesquels nous sommes d\’accord suffisent à eux seuls à occuper les deux faces de ma stèle du vice.\n\n<Werimu se perd quelques instants dans ses pensées.>\n\nJe… je suis reconnaissant envers l\’échevine Pancha pour la patience dont elle fait preuve. J\’ai bien vu la façon dont les autres traitent les âmes qui leur sont confiées.\n\nÇa ne se voit peut-être pas, mais j\’essaie vraiment de m\’améliorer.', '', 38134),
(207218, 'frFR', 'Vous êtes $uvenu seul:venu seule; dans le royaume des morts ? Voilà qui n\’est guère prudent…\n\nMoi aussi, autrefois, j\’ai cru pouvoir me passer de mes amis. Je les considérais comme une faiblesse dont on pourrait se servir contre moi.\n\nEt c\’est dans ce royaume que j\’ai compris à quel point je me trompais.', '', 38134),
(207213, 'frFR', '', 'Gahiji vole des choses… des choses perdues… oubliées…\n\nManquent à personne… jusqu\’au jour où…\n\nGahiji va trop loin… oublie les règles à ne pas oublier…\n\nGahiji fait des erreurs…', 38134),
(207212, 'frFR', '', 'Gahiji veut être libre comme vous, $umortel:mortelle;…\n\nFuir ces salles… et son passé…', 38134),
(207210, 'frFR', 'Parfois, je regrette amèrement d\’avoir créé ce démon de pierre…', '', 38134),
(207209, 'frFR', 'Et pour la dernière fois, ce titre n\’existe pas. Tu l\’as inventé de toutes pièces.', '', 38134),
(207208, 'frFR', 'Du calme, Lemet, ce n\’est pas un concours…', '', 38134),
(207207, 'frFR', 'Ça, c\’est ce qu\’on verra… Un point de plus pour le héraut des stèles du vice !', '', 38134),
(207206, 'frFR', 'J\’aimerais bien l\’y voir, le vieux Témel. Le seul héraut des stèles du vice, c\’est moi !', '', 38134),
(207205, 'frFR', 'Je vous ai trouvé des tas de fragments de stèles du vice cette semaine !\n\nOuvrez le coffre et voyez vous-même.', '', 38134),
(207203, 'frFR', '', 'Sonner une cloche rituelle, c\’est consentir à un sacrifice.\n\nChaque cloche exige quelque chose de l\’âme qui la fait tinter. S\’ils sont mal employés, comme le font les dépravés qui hantent ces salles, ces sacrifices ne sont que vaine torture.\n\nLa sagesse des Confirmés réside dans leur capacité à identifier ce à quoi chaque âme doit renoncer pour avancer sur la longue voie de l\’expiation, avant de la convaincre de la nécessité de ce sacrifice, et ce, pour son propre bien.\n\nCe n\’est pas toujours facile, et certaines âmes sont trop têtues ou irréfléchies pour sonner la cloche.\n\nMais vous, vous le savez mieux que personne : en réalité, se sacrifier pour progresser n\’a rien d\’un sacrifice.', 38134),
(207202, 'frFR', '', 'Faute d\’âmes prêtes à se livrer au cérémonial, nous n\’avons pas besoin de cloche rituelle pour l\’instant.\n\nPour éviter que les ritualistes dépravés qui infestent ces lieux ne les trafiquent dans notre dos, Fane et les autres archivistes confirmés remisent les cloches en lieu sûr quand nous ne nous en servons pas.\n\nDès qu\’une âme sera prête, et cela ne devrait pas tarder, j\’ordonnerai aux purotins d\’apporter la cloche adéquate.', 38134),
(207201, 'frFR', '', '<L\’échevine Pancha esquisse un sourire en coin.>\n\nJe me suis dit que cette cloche vous intéresserait, Sans-Entraves. Elle vous sera peut-être… utile si vous retournez un jour en Revendreth sous mes soins.\n\nLa cloche de renonciation aide ceux qui peinent à se libérer des allégeances et loyautés de leur vie mortelle. Vos alliances, vos royaumes, vos hordes… tout cela n\’existe pas ici et ne vous sera donc d\’aucune utilité.\n\nSonnez la cloche pour vous libérer des liens qui vous entravent.', 38134),
(207200, 'frFR', '', 'Ah, la cloche de soumission, ma préférée entre toutes.\n\nTout ce qu\’elle implique, c\’est de partager une partie de votre pouvoir.\n\nÇa n\’a l\’air de rien, dit comme ça, mais c\’est une démarche terrifiante pour les âmes orgueilleuses qui ont réussi dans la vie par la violence et les combats.\n\nUne fois que l\’âme a renoncé à sa force, elle est libre de se battre si elle le souhaite, et c\’est toujours le cas.\n\nMais elle est si affaiblie qu\’elle enchaîne les défaites jusqu\’à ce qu\’elle soit entièrement brisée et prête à se soumettre à ses inférieurs.\n\nAlors seulement, le rituel a rempli son objectif.', 38134),
(207199, 'frFR', '', 'Ressentez-vous de la compassion pour votre prochain, Sans-Entraves ?\n\nCe n\’est pas le cas de tout le monde. La cloche d\’opprobre aide ceux qui en sont dénués à saisir les conséquences de leurs agissements passés en les divulguant publiquement.\n\nLeurs actes les plus honteux sont ainsi dévoilés au grand jour et devant tout le monde. Les âmes vengeresses qui hantent nos cryptes sont temporairement libérées afin de mettre les impénitents face à leurs méfaits.\n\nCe serait une terrible épreuve pour tout individu doué de compassion, mais elle ne s\’adresse pas à eux.', 38134),
(207198, 'frFR', '', 'La cloche de condamnation est un instrument punitif réservé aux âmes les plus récalcitrantes, à qui il convient de rappeler régulièrement qu\’elles n\’ont aucune autorité en ces lieux.\n\nQuand on sonne la cloche, on commence par se condamner soi-même, mais ce n\’est pas l\’objectif principal. Nous disposons d\’autres outils mieux adaptés à l\’introspection.\n\nL\’essentiel, c\’est que l\’on prête le flanc aux remontrances d\’autrui, et que l\’on donne ce faisant à tous les Venthyrs un moyen de pression sur soi.\n\nInutile de préciser que la dépravation qui règne dans les salles actuellement rend cette démarche d\’autant plus dangereuse. C\’est pourquoi je surveille de très près mes âmes sous l\’effet de cette cloche.', 38134),
(207197, 'frFR', '', 'La cloche de confession est un instrument aussi simple qu\’efficace qui aide les âmes prêtes à assumer leur culpabilité.\n\nSeules celles qui souhaitent confesser un péché doivent la sonner afin que leurs aveux soient entendus de tous ici.\n\nReconnaître publiquement ses fautes aide l\’âme à surmonter ses échecs, mais cela alerte également les Venthyrs dépravés.', 38134),
(207196, 'frFR', '', 'La plupart sont confinées dans les cryptes de l\’Expiation en attendant d\’être prêtes pour le prochain rituel.\n\nNe vous inquiétez pas pour elles, je les ai mises à l\’abri des sbires dépravés du grand chambellan. Ces âmes vont avoir tout le temps de se débattre avec leur culpabilité sans craindre pour leur sécurité.\n\nEt pour cause, l\’Accusatrice elle-même a veillé à ce que les cryptes soient inviolables.', 38134),
(207195, 'frFR', '', 'Très lentement, si l\’on considère le passé comme un présage de l\’avenir.\n\nLe penchant d\’Ick pour la violence et sa haine viscérale pour les symboles du savoir collectif lui ont valu de nombreux vices à expier, mais très peu d\’outils pour y parvenir.\n\nPour l\’heure, je lui impose un isolement strict et des tâches simples, conçues pour lui inculquer la valeur de ce qu\’elle a tant négligé de son vivant.\n\nAujourd\’hui, elle doit récupérer des fragments de stèles du vice égarés afin d\’aider les archivistes.', 38134),
(207194, 'frFR', '', 'Pourquoi ne pas lui poser directement la question ?\n\nKhongordzolo m\’a été confié il y a fort longtemps, mais il a enfin appris à tenir sa langue et à dire la vérité.\n\nCe n\’est qu\’après avoir percé à jour les mensonges et demi-vérités qu\’il s\’obstinait à me servir que j\’ai réussi à le mettre face aux conséquences de ses méfaits dans les royaumes mortels.\n\nÀ présent, il doit se refaire une réputation aussi bien par la parole que par ses actes, comme nous tous.', 38134),
(207193, 'frFR', '', 'Werimu n\’a toujours pas saisi la précarité de sa situation !\n\nIl s\’est déjà beaucoup repenti, mais les fardeaux qui pesaient sur son âme à son arrivée étaient tout bonnement monstrueux. Je n\’avais encore jamais rien vu de pareil.\n\nS\’il souhaite échapper aux griffes de l\’Antre, il devra expier jusqu\’au dernier de ses péchés.\n\nJe m\’efforce actuellement de le maintenir sur la bonne voie en lui confiant une tâche qui lui est familière : démanteler une organisation rivale.', 38134),
(207192, 'frFR', '', 'Gahiji nous donne bien du fil à retordre.\n\nSi elle reconnaît volontiers ses erreurs, elle met un temps considérable à éprouver du remords.\n\nJe crains que son séjour parmi nous ne s\’éternise.\n\nCela étant dit, elle fait tout pour se rendre utile et je fais souvent appel à ses services pour purger les cryptes de l\’expiation de la souillure des Venthyrs dépravés.', 38134),
(207191, 'frFR', '', 'Yevkek est plus avancé sur la voie de l\’expiation que la plupart des âmes placées sous ma garde.\n\nAprès avoir passé sa vie à user de force et de ruse pour exploiter les plus faibles, il apprend désormais à employer ses talents pour protéger et sauver son prochain.\n\nQuand l\’une de mes âmes est perdue ou cherche à fuir la justice, je m\’en remets à Yevkek pour la retrouver et la remettre sur le droit chemin.', 38134),
(207190, 'frFR', '', 'Même les purotins sont victimes des dépravés qui hantent ces salles.\n\nContraints de travailler sans relâche et de servir les sbires corrompus du grand chambellan, ils négligent leurs devoirs premiers.\n\nRaison pour laquelle cette cloche rituelle brisée n\’a toujours pas été réparée alors que nous en avons besoin pour guider les âmes sur la voie du repentir !', 38134),
(207189, 'frFR', '', 'Maintenant que la cloche est réparée, nous allons pouvoir continuer à guider cette âme sur la voie de l\’expiation.\n\nPourriez-vous sonner la cloche et m\’aider à lui faire comprendre que le sacrifice est sa meilleure planche de salut ?\n\nCar enfin, si nous refusons de regarder nos erreurs en face en toute humilité, comment pouvons-nous espérer les surmonter ?', 38134),
(207175, 'frFR', '', 'La voie de l\’expiation est différente pour chaque âme, mais il existe tout de même un point commun à tout un chacun.\n\nLe sens du sacrifice et la souffrance sont indispensables pour prétendre à une fin digne.\n\nEt quand on sait que l\’alternative consiste à passer l\’éternité dans l\’Antre, quel prix ne serait-on pas prêt à payer ?', 38134),
(207168, 'frFR', 'Ma voie consiste à voler au secours des faibles et à sauver ceux que j\’exploitais autrefois. Force et miséricorde, $umortel:mortelle;.', '', 38134),
(207167, 'frFR', '', 'Quand les purotins auront réparé la cloche de confession, c\’est VOUS qui la sonnerez.', 38134),
(207163, 'frFR', 'J\’étais bien des choses, avant…\n\nLe loup dans la bergerie.\nLe requin guettant le radeau des naufragés.\nL\’aigle traquant le lièvre.\n\nLes faibles et les égarés n\’étaient pour moi que des proies.', '', 38134),
(207161, 'frFR', 'Il n\’est pas trop tard, $umortel:mortelle;.\n\nAmendez-vous pour éviter de connaître le même sort que moi.\n\nLa voie de l\’expiation des Venthyrs conduit peut-être au salut, mais…\n\n<L\’âme frissonne avant de retomber dans le silence.>', '', 38134),
(207154, 'frFR', 'Qu\’est-ce que vous faites là ? Vous voyez pas que j\’ai plus rien ?\n\nLes Elfes ont volé nos terres. Puis mon œil… et enfin ma vie.\n\nTout le monde s\’en prend toujours à nous. Mais on n\’a jamais baissé les bras ! Et on n\’est pas du genre à oublier !\n\nLe jour viendra où c\’est vous autres qui perdrez tout !', '', 38134),
(207152, 'frFR', '', 'Aucun n\’a survécu. Il ne reste que moi.\n\nComme vous le savez, l\’Antre est un lieu dangereux. Ce n\’est pas parce que l\’on voyage à plusieurs que l\’on est plus en sécurité. Bien au contraire, un tel attroupement devient une cible plus alléchante encore.\n\nHélas, nous l\’avons appris à nos dépens. Nous avons été décimés jusqu\’à ce que nous soyons en nombre assez réduit pour pouvoir nous cacher. La traque a ralenti… mais n\’a pas cessé pour autant.\n\nQuand je me suis retrouvée seule, j\’ai fini par accepter ma situation. La solitude était ma meilleure chance de survie.\n\nDu moins, jusqu\’à votre arrivée.', 38134),
(207149, 'frFR', '', 'Leur sort est bien regrettable, mais ne vous y trompez pas. Ce sont nos ennemis, au même titre que les antreliges.\n\nCes négociants étaient condamnés dès l\’instant où ils ont posé le pied ici. Et si vous tentiez de les secourir, vous ne feriez que précipiter votre perte et la mienne.\n\nAussi cruel que cela puisse paraître, c\’est également nécessaire pour notre survie dans l\’Antre.\n\nVous feriez bien de vous en souvenir.', 38134),
(207143, 'frFR', '', 'La veilleuse Casad vous attend dans le jardin d\’hiver de la Reine ! Elle a hâte de commencer.\n\nPour la rejoindre, il vous suffit de pénétrer dans le cercle de champignons à mes côtés.', 38134),
(207142, 'frFR', '', 'Lorsque l\’on a l\’éternité devant soi, dans cet océan d\’obscurité sans fin, les histoires sont plus précieuses que l\’or.', 38134),
(207119, 'frFR', '', 'Oh, je suis certaine que vous la connaissez bien, au contraire. Personne ne peut espérer survivre dans l\’Antre sans l\’aide de Ve\’nari.\n\nParlez-moi donc de votre relation avec elle.\n\nSi elle a choisi de ne pas revenir à Oribos avec vous, elle devra elle aussi répondre à quelques questions.', 38134),
(207118, 'frFR', '', 'Bien évidemment. J\’aurais une dernière question à vous poser.\n\nQuelle aide précieuse Ve\’nari vous a-t-elle apportée pour affronter les dangers de l\’Antre ?\n\n<Avant de répondre, vous vous souvenez que Ve\’nari vous a ordonné de ne parler de votre marché à personne.>', 38134),
(207113, 'frFR', '', 'Ah, ainsi il reste fort dangereux de faire des affaires dans cet endroit. Ce qui m\’amène à mon interrogation suivante.\n\nDeuxième question !\n\nTrouvez-vous difficile de survivre parmi les cauchemars oppressants et incessants de l\’Antre ?', 38134),
(207112, 'frFR', '', 'Je vous suis reconnaissante de partager votre expérience dans l\’Antre avec autant d\’honnêteté.\n\nPremière question : \n\nest-il vrai que les forces du Geôlier ont gagné en nombre et en puissance ?', 38134),
(207111, 'frFR', '', 'Salutations, Sans-Entrave. C\’est un plaisir de faire votre connaissance.', 38134),
(207110, 'frFR', '', 'J\’ai hâte de voir ça !', 38134),
(207109, 'frFR', '', 'Sans test, ni réglage. Comme ça, nous aurons la surprise au moment de les activer.', 38134),
(207108, 'frFR', '', 'Si vous avez des rubis infusés, je peux vous fabriquer des trousses de réparation supplémentaires.', 38134),
(207107, 'frFR', 'Ce ne sont pas les miroirs à réparer qui manquent en Revendreth, Sans-Entraves.', '', 38134),
(207105, 'frFR', '', 'La trousse de réparation de miroir que j\’ai apportée de la province des Braises est à votre disposition.', 38134),
(207104, 'frFR', 'Il y a un miroir brisé de l\’autre côté du couloir. Profitez-en pour vous faire la main.', '', 38134),
(207103, 'frFR', '', 'Oh, il y a bien des choses que je ne vous dis pas. Et je suis sûre que vous me faites des cachotteries vous aussi.\n\nLa confiance est fondée sur la discrétion, Sans-Entraves. Sans cela, il serait impossible de faire affaire avec qui que ce soit.\n\nMais je vous ai révélé mon secret le plus précieux : l\’emplacement de ce refuge. Et j\’aimerais que vous m\’accordiez votre confiance en retour.', 38134),
(207101, 'frFR', '', 'Avec votre aide, Sylvarden continuera à entretenir le cycle.\n\nJe vous confie la garde de mon jardin d\’hiver.\n\nAllez voir la veilleuse Casad, elle vous expliquera ce que l\’on attend de vous.', 38134),
(207100, 'frFR', '', 'Toute chose a une fin.\n\nMais cette fin n\’est guère qu\’un nouveau départ. Ainsi va le cycle de la Nature.\n\nLes esprits de mes bosquets y jouent un rôle incontournable. Sans eux, la Nature elle-même se meurt dans le monde mortel. Chaque perte est une tragédie, chaque esprit sauvé un triomphe.\n\nCelui-ci, c\’est vous qui l\’avez arraché à un bosquet mourant et à la folie des Drust. Aujourd\’hui, il s\’éveille enfin afin de regagner son monde. Grâce à vos efforts, nous nous sommes acquittés de notre mission sacrée.', 38134),
(207097, 'frFR', '', 'La reine de l\’hiver vous fait un grand honneur en vous initiant à la culture des graines sauvages.', 38134),
(207087, 'frFR', '', 'Ça m\’aura coûté un bon paquet de stygies… mais les affaires sont les affaires.', 38134),
(207065, 'frFR', 'Plutôt que toi ? Absolument. Tu n\’as pas respecté le contrat et tu as trahi le cartel. Tu vas devoir en payer le prix, à présent.', '', 38134),
(207063, 'frFR', '', 'Leurs hurlements ne valent pas ceux des Hurans, mais il faudra s\’en contenter.', 38134),
(207012, 'frFR', '$n ? Quel plaisir de vous revoir après tout ce temps ! Et surtout dans ce lieu étrange !\n\nQuand j\’ai appris que le voile menant à l\’Ombreterre avait été déchiré, j\’ai su qu\’il fallait absolument que je passe de l\’autre côté.\n\nComme vous le savez, mon épouse est morte en donnant naissance à notre fils qui ne lui a pas survécu. Je dois savoir s\’ils sont en sécurité avec nos ancêtres ou si leurs esprits sont en danger.\n\nJe vous en supplie, si vous croisez l\’âme de Leza, dites-lui que notre fils Kor est en train de devenir un sacré gaillard. Dites-lui que je l\’aime. Pour toujours.\n\nEt que je la retrouverai, où qu\’elle se trouve.', '', 38134),
(207011, 'frFR', 'Quel soulagement de rencontrer quelqu\’un venant d\’Azeroth en ce lieu étrange !\n\nQuand j\’ai appris que le voile menant à l\’Ombreterre avait été déchiré, j\’ai su qu\’il fallait absolument que je passe de l\’autre côté.\n\nVoyez-vous… j\’ai perdu deux êtres qui m\’étaient très chers. Je dois savoir s\’ils sont en sécurité avec nos ancêtres ou si leurs esprits sont en danger.\n\nJe vous en supplie, si vous croisez l\’âme de mon épouse, Leza Longue-Marche, dites-lui que notre fils Kor est en train de devenir un sacré gaillard. Dites-lui que je l\’aime. Pour toujours.\n\nEt que je la retrouverai, où qu\’elle se trouve.', '', 38134),
(207009, 'frFR', '', 'Je vais utiliser l\’un de mes dispositifs les plus intéressants. Mais ce sera douloureux, je le crains.', 38134),
(207008, 'frFR', '', 'Venez, Sans-Entraves. Nous avons bien des choses à nous dire.', 38134),
(207007, 'frFR', '', 'Non, Ve\’brax. Tu vas bien quitter l\’Antre… mais tu ne retourneras pas au cartel.', 38134),
(207006, 'frFR', 'ARGH ! Mais qu\’est-ce que… ?', '', 38134),
(207005, 'frFR', '', 'Je n\’en suis pas si sûre. Sans-Entraves, débarrassons-nous de cet importun.', 38134),
(207003, 'frFR', '', 'Tu pensais vraiment qu\’ils croiraient un assassin ?', 38134),
(207002, 'frFR', 'Je vous avais $uprévenu:prévenue;, Sans-Entraves, mais vous avez refusé de m\’écouter.', '', 38134),
(207000, 'frFR', 'Elle a trahi ses anciens associés et ne se gênera pas pour faire de même avec vous si elle sait que nous nous sommes parlé. Méfiez-vous d\’elle.', '', 38134),
(206999, 'frFR', 'Avant de repartir dans mon refuge, je dois vous mettre en garde contre Ve\’nari ; elle n\’est pas celle qu\’elle prétend être.', '', 38134),
(206995, 'frFR', 'Nous pensions récupérer ici ce que nous avions perdu suite à la trahison de Ve\’nari. Nous avons commis une terrible erreur…', '', 38134),
(206988, 'frFR', 'Capturés… torturés… brisés…\n\nD\’autres… emmenés par Xéros… aux Terriers…\n\nMission… nous avons échoué…', '', 38134),
(206984, 'frFR', 'Je lui avais dit que je serais de retour ! Je tiens toujours parole.', '', 38134),
(206983, 'frFR', 'J\’ai été emprisonné par un vil ensorceleur !\n\nLibérez-moi, que j\’aille lui montrer ce qu\’il y a de mieux dans la vie ! Ou la mort, plutôt.', '', 38134),
(206970, 'frFR', 'Cette chauve-souris semble être votre billet de sortie des salles de l\’Expiation.', 'Cette chauve-souris semble être votre billet de sortie des salles de l\’Expiation.', 38134),
(206969, 'frFR', 'Égide forgée par l\’esprit, bon bouclier offensif !\n\nDes ennemis approchent ? Ça va castagner ?\n\nProtégez-vous avec l\’égide ! Dégâts absorbés et renvoyés en partie aux adversaires ! Hou hou !', '', 38134),
(206968, 'frFR', 'Anneau de sauvegarde, sécurité garantie !\n\nBesoin de rester quelque part en tenant les ennemis à distance ?\n\nFacile ! Activez l\’anneau et les ennemis n\’approchent plus. Parfait pour souffler un peu.', '', 38134),
(206967, 'frFR', 'Flasque de sérénité, délicieuse et rafraîchissante !\n\nUn coup de mou ? Besoin d\’un remontant ?\n\nLa fiole vous soigne pour continuer le combat ! Garantie sur facture !', '', 38134),
(206966, 'frFR', 'Fiole de givreliche, pratique pour distanciation martiale !\n\n$uSubmergé:Submergée; par les ennemis ? Besoin de prendre du recul ?\n\nPas de problème ! Cassez la fiole par terre pour geler vos adversaires sur place et prenez la poudre d\’escampette.', '', 38134),
(206965, 'frFR', 'Pardonnez-moi, $n. Équipement pas pour vous.\n\nSeulement pour vos âmes liées. Très utile pour les épreuves !', '', 38134),
(206959, 'frFR', 'Hou hou ! Je peux vous aider pour une tâche importante, mais pas deux. Après, vous devrez attendre quelques heures. Je suis débordé !', 'Hou hou ! Je peux vous aider pour une tâche importante, mais pas deux. Après, vous devrez attendre quelques heures. Je suis débordée !', 38134),
(206958, 'frFR', 'Que puis-je faire pour vous, hou ? Je peux vous rendre un petit service, alors choisissez bien ! Après, vous devrez attendre au moins une heure. Très occupé !', 'Que puis-je faire pour vous, hou ? Je peux vous rendre un petit service, alors choisissez bien ! Après, vous devrez attendre au moins une heure. Très occupée !', 38134),
(206954, 'frFR', 'Maintenant que vous l\’dites, j\’ai vu passer un grand type vert.\n\nOn peut pas le louper, celui-là ! Il arrêtait pas de crier « Loque de tard ».\n\nSortez-moi de là et j\’vous dirai tout ce que je sais.', '', 38134),
(206949, 'frFR', 'Hein, quoi ? Un être vivant, ici ? Je perds la tête, ou quoi ?!\n\nVous êtes pas $uréel:réelle;.', '', 38134),
(206948, 'frFR', 'Le voilà, le tome de victoire ! Je vous laisse l\’honneur de vous en saisir, $n. On ne sait jamais…', '', 38134),
(206944, 'frFR', 'Plutôt, oui ! Ça fait longtemps que je veille sur Marasmius. D\’ailleurs, je connais certains trajets si anciens que même lui les a oubliés !$b$bQuelque chose me dit que vous connaissez ces endroits secrets, vous aussi. Si jamais vous voulez y retourner, je peux vous aider.', 'Plutôt, oui ! Ça fait longtemps que je veille sur Marasmius. D\’ailleurs, je connais certains trajets si anciens que même lui les a oubliés !$b$bQuelque chose me dit que vous connaissez ces endroits secrets, vous aussi. Si jamais vous voulez y retourner, je peux vous aider.', 38134),
(206941, 'frFR', 'Vous… Je ne vous reconnais pas. Comment êtes-vous $uarrivé:arrivée; jusqu\’ici ? Les jeunes, de nos jours…$b$bJe ne sais pas comment vous vous êtes $udébrouillé:débrouillée;, mais vous vous êtes $ufourré:fourrée; dans un drôle de pétrin ! Bon, je vais vous ramener en Sylvarden. Ce n\’est pas parce que je suis vieux et que personne ne se soucie plus de moi que je refuserais mon aide à une jeune pousse en détresse.', '', 38134),
(206933, 'frFR', '', 'Votre esprit est gonflé d\’orgueil, $umortel:mortelle;. Souhaitez-vous que je vous aide à l\’expier ?', 38134),
(206931, 'frFR', 'Elle devrait moins souffrir, là-bas.', '', 38134),
(206930, 'frFR', 'Le prince Rénathal a accordé à la Naaru l\’autorité suprême sur la tour du donjon de l\’Aube. C\’est là que ses congénères sont apparus et c\’est là aussi que la lumière brille de son éclat le plus vif.', '', 38134),
(206929, 'frFR', 'Z\’rali est trop faible pour rentrer chez elle, et sa nature même présente un danger pour tout Venthyr en sa présence.', '', 38134),
(206912, 'frFR', 'Élan naturel est une compétence passive permettant de lancer Lumière vacillante en Forme d\’âme dans la direction vers laquelle vous vous déplacez, et non plus devant vous.', 'Élan naturel est une compétence passive permettant de lancer Lumière vacillante en Forme d\’âme dans la direction vers laquelle vous vous déplacez, et non plus devant vous.', 38134),
(206908, 'frFR', 'Vous trouverez le maître des conflits et ses amis marchands près de l\’Enclave.', 'Vous trouverez le maître des conflits et ses amis marchands près de l\’Enclave.', 38134),
(206897, 'frFR', 'Je ne suis qu\’un écho du savoir du parangon défunt, mais je vois que vous avez soif de connaissances.\n\nLisez mon histoire et nous discuterons ensuite de ce qu\’il vous faut encore apprendre.', 'Je ne suis qu\’un écho du savoir du parangon défunt, mais je vois que vous avez soif de connaissances.\n\nLisez mon histoire et nous discuterons ensuite de ce qu\’il vous faut encore apprendre.', 38134),
(206896, 'frFR', 'Vous avez ramené Kobri ! Merci !', 'Vous avez ramené Kobri ! Merci !', 38134),
(206895, 'frFR', 'Kobri est parti chercher du vin. Qu\’est-ce qu\’il fiche ? On peut pas commencer sans vin !\n\nVous pouvez le ramener ?', 'Kobri est parti chercher du vin. Qu\’est-ce qu\’il fiche ? On peut pas commencer sans vin !\n\nVous pouvez le ramener ?', 38134),
(206889, 'frFR', 'Pour la Terre-Mère !', '', 38134),
(206886, 'frFR', 'Que le Dessein vous guide.\n\nJe suis le gardien des archives. Nous préservons les visions que les fils emmêlés du destin nous envoient dans l\’intérêt du Dessein.\n\nQue puis-je vous révéler ?', 'Que le Dessein vous guide.\n\nJe suis la gardienne des archives. Nous préservons les visions que les fils emmêlés du destin nous envoient dans l\’intérêt du Dessein.\n\nQue puis-je vous révéler ?', 38134),
(206878, 'frFR', 'Bienvenue. Quelle voie vous intéresse aujourd\’hui ?', '', 38134),
(206833, 'frFR', '', 'Vous ne parlerez à personne de mon existence. C\’est à vous, et à vous $useul:seule; de mener à bien votre mission.\n\nJe vais vous remettre un gardien d\’âmes, un artéfact capable de contenir les âmes que vous trouverez dans l\’Antre.\n\nSi vous en prenez possession et que vous acceptez mes conditions, nous serons $uliés:liées; par un contrat.\n\nVous me rapporterez tout renseignement important que vous découvrirez dans l\’Antre.\n\nEn échange, je vous permettrai d\’acheter les outils qui vous permettront de survivre dans ce royaume et de secourir les âmes que vous recherchez.\n\nMarché conclu ?', 38134),
(206827, 'frFR', 'Vous faites de la musique ? Vous voulez jouer avec ça ?', 'Vous faites de la musique ? Vous voulez jouer avec ça ?', 38134),
(206726, 'frFR', '<Faire un choix.>', '', 38134),
(206725, 'frFR', '<Activer et désactiver.>', '', 38134),
(206719, 'frFR', 'Lorsque l\’on veut expier sa vulgarité et s\’amender, il y a toujours un prix à payer. Mais pour véritablement réformer les grossiers personnages, il faut les dénoncer au grand jour afin de les obliger à rendre des comptes.', '', 38134),
(206713, 'frFR', 'Le vicéquin Blanchette hennit d\’angoisse, puis disparaît.', '', 38134),
(206708, 'frFR', 'En l\’absence du roi Anduin, c\’est à moi qu\’il revient de protéger les membres de l\’Alliance.\n\nJ\’ai l\’impression d\’avoir aidé le jeune Varian Wrynn à monter sur le trône il y a une éternité de cela, et me voilà aujourd\’hui défenseur de ce même trône pour son fils.\n\nL\’Alliance a payé un lourd tribut lors de la Quatrième guerre. Nos troupes ont été décimées et les nobles de Hurlevent tentent de remettre nos défenses en état au plus vite.\n\nReprendre le contrôle de Stromgarde est un bon début. Peut-être pourrions-nous en faire de même avec d\’autres bastions de l\’ancienne Alliance ?', '', 38134),
(206707, 'frFR', 'La Horde, elle va devoir surmonter bien des épreuves et des dangers.\n\nJe suis fier d\’avoir ma place dans un conseil qui nous aidera à aller de l\’avant.\n\nVol\’jin, c\’était le plus grand chef de guerre qu\’on ait jamais connu… et un très bon ami. Je compte honorer sa mémoire en ne laissant aucun d\’entre nous oublier les sacrifices qu\’il a faits pour la Horde.', '', 38134),
(206706, 'frFR', '', 'Nous avons passé des mois à quadriller Azeroth à la recherche de Sylvanas, tout cela pour apprendre que ma sœur a franchi le Voile et s\’est rendue dans les royaumes de la mort.\n\n<Alleria plisse les yeux.>\n\nCela ne m\’étonne guère.\n\nMême si je brûle de partir à sa poursuite, ma place est ici. Aux côtés de Turalyon, je ferai en sorte de remettre l\’Alliance sur pied.\n\nMais Sylvanas ne s\’en tirera pas à si bon compte… Elle répondra de ses actes en temps et en heure.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206705, 'frFR', 'La voie est libre. Vous pouvez vous rendre en Maldraxxus.', '', 38134),
(206704, 'frFR', 'Les voies commencent à se rouvrir. Est-ce pour cela que le Dessein vous a $uenvoyé:envoyée; parmi nous ?', '', 38134),
(206703, 'frFR', 'La voie est libre. Vous pouvez vous rendre en Sylvarden.', '', 38134),
(206701, 'frFR', 'La voie est libre. Vous pouvez vous rendre en Revendreth.', '', 38134),
(206692, 'frFR', 'Bienvenue, Kah-Shir. Est-ce donc vrai ? Devons-nous rouvrir le portail ?', '', 38134),
(206679, 'frFR', '<Une fois imprégné à l\’aide d\’un éclat harmonisé, cet appareil peut vous ouvrir des passages secrets au sein de la tour.>', '', 38134),
(206677, 'frFR', 'À vos ordres, Sans-Entraves.', '', 38134),
(206668, 'frFR', '', 'La splendide cité d\’éternité brille de mille feux. Et pourtant, l\’influence de la mort est partout dans ses rues.\n\nJe la ressens moi aussi. Peut-être existe-t-il à Oribos des secrets qui me permettront de comprendre ce que cela signifie.\n\nLes auxiliaires parlent de leur « Dessein » avec tant de conviction. Qui sait, ils pourraient bien m\’aider à trouver ma voie ?', 38134),
(206667, 'frFR', '', 'L\’Ombreterre. Quel endroit remarquable… et quelque peu terrifiant, il faut bien l\’avouer.\n\nMais je ne laisserai pas la peur m\’empêcher de venir en aide à mes amis.\n\nOu d\’apprendre à connaître le père que je pensais avoir perdu.', 38134),
(206666, 'frFR', 'Nous savons que le Geôlier nous surveille et qu\’il a emprisonné Anduin.\n\nNotre seul espoir est que vous réussissiez, avec d\’autres héros, à renforcer les congrégations, $n.\n\nLe Geôlier et Sylvanas ne tarderont pas à dévoiler la suite de leur plan.\n\nLorsqu\’ils le feront, nous devrons tous unir nos forces pour les vaincre.', '', 38134),
(206629, 'frFR', 'Ça, c\’est bien vrai. C\’est sûrement un truc de sans ailes.', 'Ça, c\’est bien vrai. C\’est sûrement un truc de sans ailes.', 38134),
(206628, 'frFR', 'Vous n\’arrêtez pas de sauter, ma parole. Et sans ailes, c\’est sûrement très mauvais pour les articulations.', 'Vous n\’arrêtez pas de sauter, ma parole. Et sans ailes, c\’est sûrement très mauvais pour les articulations.', 38134),
(206627, 'frFR', 'Peut-être bien, mais qu\’est-ce que ça balance…', 'Peut-être bien, mais qu\’est-ce que ça balance…', 38134),
(206626, 'frFR', 'C\’est une habitude, chez vous, de sauter dans tous les sens ?', 'C\’est une habitude, chez vous, de sauter dans tous les sens ?', 38134),
(206625, 'frFR', 'Justement, ça peut bien attendre demain, au point où on en est !', 'Justement, ça peut bien attendre demain, au point où on en est !', 38134),
(206624, 'frFR', 'De l\’aide ? Allons rafler quelque chose de chouette, $umortel:mortelle;.', 'De l\’aide ? Allons rafler quelque chose de chouette, $umortel:mortelle;.', 38134),
(206623, 'frFR', 'Cette… cette pierre ! Elle vous mènera droit à la catastrophe, $umortel:mortelle; !', 'Cette… cette pierre ! Elle vous mènera droit à la catastrophe, $umortel:mortelle; !', 38134),
(206622, 'frFR', 'Ne le laissez pas vous induire en tentation !', 'Ne le laissez pas vous induire en tentation !', 38134),
(206621, 'frFR', 'Il n\’est jamais trop tard pour emprunter la voie de la piété !', 'Il n\’est jamais trop tard pour emprunter la voie de la piété !', 38134),
(206620, 'frFR', 'Qui allez-vous aider aujourd\’hui, $umortel:mortelle ; ?', 'Qui allez-vous aider aujourd\’hui, $umortel:mortelle ; ?', 38134),
(206619, 'frFR', 'Ne l\’écoutez pas. En unissant vos forces, rien ni personne ne pourra vous arrêter !', 'Ne l\’écoutez pas. En unissant vos forces, rien ni personne ne pourra vous arrêter !', 38134),
(206618, 'frFR', 'Avec votre protection, vos alliés iront loin !', 'Avec votre protection, vos alliés iront loin !', 38134),
(206617, 'frFR', 'Puisse votre arme ne s\’abattre que pour rendre justice !', 'Puisse votre arme ne s\’abattre que pour rendre justice !', 38134),
(206616, 'frFR', 'Un noble combat ! Puisse cette âme trouver le repos éternel !', 'Un noble combat ! Puisse cette âme trouver le repos éternel !', 38134),
(206615, 'frFR', 'Il s\’agissait sans doute d\’un malentendu…', 'Il s\’agissait sans doute d\’un malentendu…', 38134),
(206614, 'frFR', 'Le conflit n\’est pas une solution. Vous avez essayé de discuter ?', 'Le conflit n\’est pas une solution. Vous avez essayé de discuter ?', 38134),
(206613, 'frFR', 'Allons, vous n\’en êtes plus à un vice près !', 'Allons, vous n\’en êtes plus à un vice près !', 38134),
(206612, 'frFR', 'Quel genre de bêtises va-t-on bien pouvoir faire ?', 'Quel genre de bêtises va-t-on bien pouvoir faire ?', 38134),
(206611, 'frFR', 'Songez plutôt à tous les sorts que vous auriez pu faire pleuvoir sur vos ennemis si vous n\’aviez pas gâché celui-ci !', 'Songez plutôt à tous les sorts que vous auriez pu faire pleuvoir sur vos ennemis si vous n\’aviez pas gâché celui-ci !', 38134),
(206610, 'frFR', 'Cette personne n\’en aurait probablement pas fait autant pour vous, vous savez…', 'Cette personne n\’en aurait probablement pas fait autant pour vous, vous savez…', 38134),
(206609, 'frFR', 'Bien joué ! Vous les avez écrasés !', 'Bien joué ! Vous les avez écrasés !', 38134),
(206608, 'frFR', 'Oui ! Abattez-les, à commencer par celui-là !', 'Oui ! Abattez-les, à commencer par celui-là !', 38134),
(206607, 'frFR', 'Faites parler votre arme !', 'Faites parler votre arme !', 38134),
(206606, 'frFR', 'Il ne s\’en tirera pas comme ça ! Réglez-lui son compte !', 'Il ne s\’en tirera pas comme ça ! Réglez-lui son compte !', 38134),
(206605, 'frFR', 'Adonas grimace de douleur en se touchant les côtes.', '', 38134),
(206604, 'frFR', 'Je vous en supplie, Sans-Entraves… J\’ai besoin… de soins…', '', 38134),
(206603, 'frFR', 'Ce sera douloureux pendant quelque temps, mais je devrais pouvoir voler à nouveau… Merci.', '', 38134),
(206590, 'frFR', 'Parchemins, parchemins, parchemins…\n\nOh, hou, bonjour !', 'Parchemins, parchemins, parchemins…\n\nOh, hou, bonjour !', 38134),
(206588, 'frFR', 'Beaucoup de parchemins ont disparu des étagères ! Vous avez trouvé un parchemin ? Oui ?', 'Beaucoup de parchemins ont disparu des étagères ! Vous avez trouvé un parchemin ? Oui ?', 38134),
(206579, 'frFR', 'Le temps presse… Nous devons frapper Denathrius avant qu\’il ne découvre ma présence ici.\n\nMais ce serait de la folie d\’attaquer maintenant. Les blessures que j\’ai subies dans l\’Antre ne se sont pas refermées et vous n\’êtes pas encore assez $ufort:forte; pour survivre à mon plan.\n\nPartez, Sans-Entraves, et développez votre puissance. Apprenez à connaître l\’Ombreterre. Gagnez toute l\’expérience que cet endroit a à vous offrir et revenez me voir lorsque vous vous sentirez $uprêt:prête; à vous mesurer à l\’un des seigneurs de la mort.', '', 38134),
(206576, 'frFR', 'Vous trouverez les portails sur la plateforme installée en périphérie de la ville.', 'Vous trouverez les portails sur la plateforme installée en périphérie de la ville.', 38134),
(206575, 'frFR', 'Tous les négociants offrant des services d\’artisanat sont rassemblés dans la salle des formes.', 'Tous les négociants offrant des services d\’artisanat sont rassemblés dans la salle des formes.', 38134),
(206571, 'frFR', 'Les trésors de la forêt.', 'Les trésors de la forêt.', 38134),
(206564, 'frFR', 'Le temps et le destin sont intimement liés et peuvent être pliés à la volonté du Dessein.\n\nUn pouvoir immense a effleuré votre âme, $umortel:mortelle;. Son influence me permet de modifier votre destinée… et de vous proposer une certaine voie.\n\nSi vous décidez de l\’emprunter, les habitants de l\’Ombreterre vous considéreront comme toutes les autres âmes ayant traversé leurs royaumes.\n\nLes congrégations vous ouvriront leurs portes, prêtes à vous accueillir dans leurs rangs. Quelle que soit celle que vous choisirez, vous devrez l\’aider à gagner en puissance.\n\nSachez cependant que la décision de suivre cette voie est irréversible.\n\nLa décision vous appartient.', '', 38134),
(206562, 'frFR', 'Bien sûr que j\’ai accepté de lier nos âmes : elle est mon amie la plus proche, et mon plus grand souhait est de passer le reste de mon éternité à ses côtés.', 'Bien sûr que j\’ai accepté de lier nos âmes : elle est mon amie la plus proche, et mon plus grand souhait est de passer le reste de mon éternité à ses côtés.', 38134),
(206561, 'frFR', 'Merci encore pour votre aide.\n\n<Elle se tourne vers sa droite et sourit tendrement.>\n\nElle a dit oui.', 'Merci encore pour votre aide.\n\n<Elle se tourne vers sa droite et sourit tendrement.>\n\nElle a dit oui.', 38134),
(206560, 'frFR', 'Je vais lui faire part de ma réponse sur-le-champ. Merci. Vous comprenez l\’importance de ce moment, j\’en suis sûr.', 'Je vais lui faire part de ma réponse sur-le-champ. Merci. Vous comprenez l\’importance de ce moment, j\’en suis sûre.', 38134),
(206559, 'frFR', 'Elle veut que nous liions nos âmes, $n.', 'Elle veut que nous liions nos âmes, $n.', 38134),
(206558, 'frFR', 'Lorsqu\’elle est offerte par une personne qui vous est chère, une couronne est un cadeau très symbolique.', 'Lorsqu\’elle est offerte par une personne qui vous est chère, une couronne est un cadeau très symbolique.', 38134),
(206551, 'frFR', 'Deux chemins s\’offrent à vous, $n.\n\nVous pouvez commencer votre aventure de la même façon que les autres mortels avant vous. Personne ne connaîtra votre destin, ni votre rôle dans le Dessein.\n\nOu vous pouvez suivre les fils du destin. Vous serez alors $uconnu:connue; de toutes les congrégations et en mesure de rejoindre celle de votre choix.\n\nLes royaumes de la mort s\’ouvriront à vous et vous pourrez décider de votre destin.\n\nFaites votre choix.', 'Deux options s\’offrent à vous, $n.\n\nVous pouvez respecter le cours normal du temps, débarquer à l\’improviste et vous faire des tas de nouveaux amis.\n\nOu alors, je peux accélérer les choses en vous propulsant dans le futur.\n\nVous pourrez alors intégrer une congrégation sur-le-champ et partir à l\’aventure où vous voudrez en Ombreterre !\n\nQu\’en dites-vous ?', 38134),
(206550, 'frFR', 'Le destin vous appelle. Le Dessein a besoin de vous.', '', 38134),
(206539, 'frFR', 'Les négociants d\’Oribos ont autorisé nos alliés du fort d\’Ébène à établir un portail entre leur royaume et le nôtre. Vous le trouverez dans la salle des portails, au sommet de la tour des mages.', 'Les négociants d\’Oribos ont autorisé nos alliés du fort d\’Ébène à établir un portail entre leur royaume et le nôtre. Vous le trouverez dans la salle des portails, au sommet de la tour des mages.', 38134),
(206534, 'frFR', '', 'Dans le doute, le mieux est de massacrer tout le monde.', 38134),
(206532, 'frFR', '', 'J\’en ai tué beaucoup pour moins que ça.', 38134),
(206530, 'frFR', 'Ce n\’était pas une mince affaire, mais nos alliés du fort d\’Ébène ont collaboré avec les négociants d\’Oribos pour ouvrir une porte entre nos royaumes. Vous la trouverez au niveau principal de notre salle des portails.', 'Ce n\’était pas une mince affaire, mais nos alliés du fort d\’Ébène ont collaboré avec les négociants d\’Oribos pour ouvrir une porte entre nos royaumes. Vous la trouverez au niveau principal de notre salle des portails.', 38134),
(206528, 'frFR', '', 'Cet endroit me donne la chair de poule. J\’en ai des frissons, je suis constamment sur les nerfs. Le moindre bruit surnaturel me fait sursauter.\n\nMais je suis déterminée. Je suis une transcendée du Bastion. Il est de mon devoir de traquer quiconque menace non seulement notre mode de vie, mais aussi l\’Ombreterre tout entière.', 38134),
(206527, 'frFR', 'D\’ordinaire, les missions d\’une telle importance ne sont pas confiées à de simples aspirants.\n\nJe dois bien admettre que je ne suis pas tranquille à l\’idée de quitter le Bastion aussi vite. Mais avec Kléia et vous à mes côtés, cette aventure devrait s\’avérer très instructive.', '', 38134),
(206526, 'frFR', '', 'Je promets de représenter au mieux le Bastion et de porter haut nos valeurs et coutumes.\n\nLes Venthyrs et les Kyrians ne sont pas souvent d\’accord, mais je suis convaincue que nous pouvons trouver un terrain d\’entente.', 38134),
(206525, 'frFR', '', 'La comtesse garde un œil sur tous les nobles de Revendreth, passés et présents.\n\nPour elle, le pouvoir, c\’est la connaissance… Et elle ne partage ni l\’un ni l\’autre de son plein gré.', 38134),
(206524, 'frFR', '', 'Bien le bonjour, $n !\n\nLes voyages dans le temps sont très périlleux. De grandes aventures vous attendent, quand vous serez un peu plus $uexpérimenté:expérimentée;.', 38134),
(206523, 'frFR', '', 'Nous œuvrons non seulement au service de l\’Archonte, mais aussi des peuples d\’innombrables mondes dans toutes les réalités.', 38134),
(206522, 'frFR', '', 'Bien le bonjour, $n !\n\nSi vous venez pour parler des voyages dans le temps, j\’ai bien peur que vous n\’ayez besoin d\’en apprendre un peu plus sur votre présent avant de pouvoir visiter le passé.\n\nCe sera peut-être possible une fois que vous aurez visité la ville et rencontré le chef de guerre !', 38134),
(206521, 'frFR', '', '<Les yeux de Kléia sont fermés, l\’inquiétude clairement dessinée sur son visage.\n\nSa respiration est profonde alors qu\’elle s\’efforce d\’accepter ces dernières révélations.>', 38134),
(206520, 'frFR', '', 'Azeroth est magnifique ; le contraste avec le Bastion est frappant. J\’ai hâte de découvrir les coutumes des habitants de ce monde.', 38134),
(206519, 'frFR', '', 'Cela fait une éternité que je me prépare à ce jour.\n\nIl ne reste qu\’une seule tâche ; une ultime épreuve.\n\nEt l\’heure de mon ascension sera enfin arrivée.', 38134),
(206512, 'frFR', '<L\’archiviste Janeera ne vous adresse pas un regard et continue à parcourir le parchemin posé devant elle.>\n\nOui ?', '<L\’archiviste Janeera ne vous adresse pas un regard et continue à parcourir le parchemin posé devant elle.>\n\nOui ?', 38134),
(206511, 'frFR', 'Oui, eh bien, lorsque vous voudrez en chasser un autre, apportez-moi des fragments de stèle du vice, $p.', 'Oui, eh bien, lorsque vous voudrez en chasser un autre, apportez-moi des fragments de stèle du vice, $p.', 38134),
(206510, 'frFR', 'Je vous suis très reconnaissant de nous débarrasser des inquisiteurs du grand chambellan, je vous l\’assure.', 'Je vous suis très reconnaissante de nous débarrasser des inquisiteurs du grand chambellan, je vous l\’assure.', 38134),
(206509, 'frFR', 'Mes excuses. On me dit souvent que je parais froid et indifférent.', 'Mes excuses. On me dit souvent que je parais froide et indifférente.', 38134),
(206508, 'frFR', 'L\’archiviste Fane tousse bruyamment.', 'L\’archiviste Fane tousse bruyamment.', 38134),
(206507, 'frFR', 'L\’archiviste Fane des salles de l\’Expiation peut toujours vous fabriquer une stèle du vice d\’inquisiteur… si vous avez les fragments nécessaires.', '', 38134),
(206505, 'frFR', 'Je n\’en suis pas certain, non.\n\nAvez-vous jeté un coup d\’œil dans les bois autour du village ? J\’y avais vu des aspirants en train de lire avant que les wyrms d\’éther ne s\’y installent.', 'Je n\’en suis pas certaine, non.\n\nAvez-vous jeté un coup d\’œil dans les bois autour du village ? J\’y avais vu des aspirants en train de lire avant que les wyrms d\’éther ne s\’y installent.', 38134),
(206496, 'frFR', 'C\’est toujours à Tubbins de faire le plus dur. Aller chercher de l\’eau, c\’est facile !', '', 38134),
(206493, 'frFR', 'L\’eau que vous cherchez n\’est plus très loin…\n\nSi vous avez du mal à l\’atteindre, revenez me voir.\n\nJe ne suis jamais loin… et je suis souvent disposé à apporter mon aide.', '', 38134),
(206492, 'frFR', 'Adieu, jeune pousse. Continuez à travailler dur… et faites preuve de respect envers vos aînés…', '', 38134),
(206491, 'frFR', 'Retenez votre souffle… Rentrez le ventre…\n\nEt c\’est parti…', '', 38134),
(206490, 'frFR', 'Je vous ramènerai sur le continent quand vous l\’aurez récupérée.', '', 38134),
(206489, 'frFR', 'L\’eau que vous cherchez n\’est plus très loin.', '', 38134),
(206488, 'frFR', 'Accrochez-vous… La première fois que l\’on emprunte le réseau mycélien est toujours un peu… déroutante.', '', 38134),
(206487, 'frFR', 'Montrez-moi qui vous êtes vraiment… Vous travaillez dur, au moins ?', '', 38134),
(206486, 'frFR', 'Pas si vite, jeune pousse. On n\’entre pas dans mon réseau mycélien comme dans un moulin, je vous signale.', '', 38134),
(206483, 'frFR', 'J\’ai été témoin de tous vos efforts grâce à mon réseau mycélien.\n\nPartout où on me voit, je vois tout… et on me voit partout.\n\nVous travaillez TRÈS dur.', '', 38134),
(206482, 'frFR', 'Vous, les jeunes, vous avez sans cesse la bougeotte, et vous faites un de ces vacarmes…\n\nQuelle est la cause de tout ce raffut, cette fois ?', '', 38134),
(206479, 'frFR', 'Reposez-vous, car bientôt, la chasse reprendra de plus belle.', '', 38134),
(206478, 'frFR', 'Ça, c\’est bien vrai.', '', 38134),
(206477, 'frFR', '', 'Et en termes de force, nul ne surpasse l\’Hallali !', 38134),
(206476, 'frFR', '', 'Ensemble, les racines gagnent en force.', 38134),
(206475, 'frFR', '', 'Cette patrouille m\’a beaucoup appris. C\’était un honneur de chasser à vos côtés.', 38134),
(206474, 'frFR', '', 'Noble chasse, en vérité. Vous progressez à vue d\’œil, Luméra.', 38134),
(206473, 'frFR', 'Un dévoreur !', '', 38134),
(206472, 'frFR', '', 'Justement, c\’est bien ce qui m\’inquiète.', 38134),
(206471, 'frFR', '', 'Non, je n\’entends rien du tout…', 38134),
(206470, 'frFR', '', 'Attendez. Vous entendez ça ?', 38134),
(206469, 'frFR', 'À présent, ils ont envahi les moindres recoins de la forêt. Nous vivons une bien triste époque.', '', 38134),
(206468, 'frFR', '', 'Les contes disent vrai, même si cela faisait bien longtemps que nous ne les avions pas affrontés.', 38134),
(206467, 'frFR', '', 'J\’ai longtemps cru que les Drust n\’étaient qu\’un mythe… de simples contes destinés à effrayer les enfants faë.', 38134),
(206466, 'frFR', '', 'Tu l\’as mérité, Sika. Amuse-toi bien.', 38134),
(206465, 'frFR', '', 'Ils vont regretter d\’avoir mis les pieds en Sylvarden !', 38134),
(206464, 'frFR', 'Les Drust ! Hallali, aux armes !', '', 38134),
(206463, 'frFR', '', 'Sika… a de bons amis. Sika… va faire la fête !', 38134),
(206462, 'frFR', '', 'Sika hésite un instant, mais un large sourire finit par se dessiner sur son visage.', 38134),
(206461, 'frFR', '', 'Accompagne donc $p à la cour des Braises. Détends-toi un peu ; nous nous occuperons de tout en ton absence.', 38134),
(206460, 'frFR', 'Tu en fais tant pour nous. Laisse-nous t\’aider pour une fois.', '', 38134),
(206459, 'frFR', 'Oui, Sika. Nous n\’imaginions pas que tu ressentais une telle pression.', '', 38134),
(206458, 'frFR', 'Voilà qui est parler en vraie chasseresse.', '', 38134),
(206457, 'frFR', '', 'Jamais ! L\’Hallali protégera la forêt dans la prospérité comme dans l\’adversité.', 38134),
(206456, 'frFR', 'Si cette pénurie ne s\’achève pas bientôt, c\’est l\’ensemble de Sylvarden qui partagera le triste sort de Tirna Scithe.', '', 38134),
(206454, 'frFR', '<Le centurion semble à court d\’anima.>', '', 38134),
(206453, 'frFR', '', 'En voilà d\’autres qui arrivent !', 38134),
(206452, 'frFR', '', 'Le ciel pleure pour Tirna Scithe. Si peu d\’anima, et tant de gorms.', 38134),
(206445, 'frFR', 'Puis-je vous aider, Sans-Entraves ?\n\nJ\’ai un moment avant de devoir m\’adresser à ces âmes.', 'Puis-je vous aider, Sans-Entraves ?\n\nJ\’ai un moment avant de devoir m\’adresser à ces âmes.', 38134),
(206444, 'frFR', 'Leithfor hoche la tête d\’un air solennel.', '', 38134),
(206443, 'frFR', '', 'Il n\’y a pas de réfugiés à Tirna Scithe. Nous l\’avons tous appris à nos dépens.', 38134),
(206442, 'frFR', '', 'Une illusion… Je me suis fait avoir.', 38134),
(206441, 'frFR', 'Luméra, non ! Reculez !', '', 38134),
(206440, 'frFR', '', 'Regardez, des réfugiés ! Ils ont besoin de nous !', 38134),
(206439, 'frFR', '', 'Je me réjouis de me rendre à sa cour.', 38134),
(206438, 'frFR', '', 'Vous auriez pu choisir d\’affronter le plus faible. À la place, vous avez choisi le plus fort.', 38134),
(206437, 'frFR', '', 'Le prince Rénathal a dû vous offrir monts et merveilles, pour vous compter parmi ses alliés.', 38134),
(206436, 'frFR', 'Nous sommes bons amis maintenant, $p. N\’est-ce pas ?\n\nAlors, combien de fois vais-je devoir vous supplier de m\’envoyer quelques purotins de votre congrégation ici, en Maldraxxus ?\n\nÉvidemment, j\’ai réussi à en liquéfier quelques-uns à la cour quand vous aviez le dos tourné, mais imaginez les expériences que je pourrais mener sur eux ici, avec tout mon laboratoire à disposition !\n\nJe pourrais enfin répondre à la question qui taraude toute l\’Ombreterre : les purotins sont-ils composés de gelée ?\n\nC\’est tout à fait possible, vous savez !', '<Sika pose les papiers qu\’elle organisait en s\’adressant à vous.>\n\n$p ! Bonne surprise !\n\nLa plupart des surprises sont mauvaises. Elles gâchent les plans. Ça fait plus de travail.\n\nSika pense à la prochaine cour des Braises. Sika espère qu\’elle sera invitée.\n\nIl y a beaucoup à faire, mais Sika a l\’aide de son $uami:amie;.\n\nSika a de la chance d\’avoir son $uami:amie;.', 38134),
(206435, 'frFR', 'Mais voilà mon $uancien-apprenti-devenu-hôte-de-cour préféré:ancienne-apprentie-devenue-hôtesse-de-cour préférée; !\n\nJ\’ai quelques idées pour les prochaines réceptions que je mourais d\’envie de partager avec vous.\n\nJe vous préviens : elles impliquent toute une bonne quantité de gelée.\n\nCependant, certaines de ces gelées sont également délicieuses !', '', 38134),
(206434, 'frFR', 'Vos choix de carrière sont surprenants, $p.\n\nC\’est bien la première fois que l\’un de mes anciens apprentis décide d\’organiser des réceptions à la cour.\n\nTrès étrange, vraiment…\n\nToutefois, Kevin semble apprécier l\’idée, alors je ne vais pas m\’y opposer.\n\nQuand doit avoir lieu la prochaine soirée de la cour des Braises, au fait ?', '', 38134),
(206433, 'frFR', '$p ! Kevin, mets cette potion expérimentale de côté… pour l\’instant.', '', 38134),
(206432, 'frFR', 'Essayez de ne pas marcher sur ma dernière expérience, $p. Je ne voudrais pas vous perdre.', '', 38134),
(206431, 'frFR', 'Est-ce l\’un de mes naïfs apprentis que j\’entends ?', '', 38134),
(206430, 'frFR', '$p, je parlais justement de vous et de votre cour, le DEUXIÈME MEILLEUR divertissement d\’Ombreterre.\n\nDites-moi, quand aura lieu la prochaine réception ? J\’ai promis à certains de mes clients les plus prometteurs qu\’ils pourraient m\’y accompagner.', '', 38134),
(206429, 'frFR', '$p, $ucher:chère; enfant. Restez, regardez les combats avec moi.\n\nQuoi que vous soyez $uvenu:venue; chercher ici, je peux vous le faire porter.\n\nQuand on a Vole pour ami, inutile d\’arpenter Maldraxxus de fond en comble.', '', 38134),
(206428, 'frFR', 'Comment votre cour des Braises pourrait-elle rivaliser avec pareil spectacle, Sans-Entraves ?\n\nÀ votre prochaine réception, verra-t-on Azmogal ou le grand Urtz terrasser le gâteau ?\n\n<D\’un geste, Vole désigne l\’impressionnant géant de lave en contrebas.>\n\nJe vous déconseille d\’essayer. Les frêles Venthyrs ne feraient pas le poids, face à lui.', '', 38134),
(206427, 'frFR', 'Venez admirer le spectacle, $p. Je vous ai gardé une place à mes côtés.', '', 38134),
(206426, 'frFR', 'Faites place ! Laissez passer l\’élite des champions. $p !', '', 38134),
(206425, 'frFR', 'À qui le tour, maintenant ? Pourquoi pas vous, Sans-Entraves ?', '', 38134),
(206424, 'frFR', 'Il n\’y a aucune honte à payer. Tout le monde n\’est pas de taille pour l\’arène.', '', 38134),
(206423, 'frFR', '$p. Chaque fois que je vous vois, sans exception, je me remémore mon ancienne vie sur Azeroth.\n\nQuand tout sera terminé et que nous célébrerons nos victoires autour d\’un grand festin, j\’aurai des questions à vous poser.\n\nPour l\’instant, nous devons rester concentrés sur notre mission.\n\nLes enjeux sont trop importants pour relâcher notre vigilance, ne serait-ce qu\’un instant.', '', 38134),
(206422, 'frFR', 'Est-ce la première fois que vous dirigez des troupes en Ombreterre, $p ?\n\nJ\’en doute, au vu de vos agissements. Peu de meneurs parviennent à porter ce fardeau sans trébucher au premier pas.\n\nJ\’ai entendu d\’autres invités discuter à la cour… Ils étaient mécontents de certains de vos choix. Comme si toute l\’Ombreterre tournait autour de leurs caprices !\n\nNe laissez pas leurs reproches vous décourager.\n\nOuvrez la voie et guidez-nous vers la victoire. Ils finiront par vous comprendre.', '', 38134),
(206421, 'frFR', 'Regardez ces soldats, Sans-Entraves.\n\nForts, inflexibles, dévoués…\n\nCela ne suffit pas, malheureusement. Une armée, quelle que soit sa taille, doit aussi se montrer sage, rusée et inspirante.\n\nJ\’ai hâte de savoir si le prince Rénathal possède ces qualités et, si oui, comment il les met en application.\n\nQuand aura lieu la prochaine réception de la cour des Braises ? Je m\’assurerai de me libérer pour m\’entretenir plus longuement avec le prince.', '', 38134),
(206420, 'frFR', 'Ah, $p. Comment se porte notre Sans-Entraves $Upréféré:préférée;, aujourd\’hui ?', '', 38134),
(206419, 'frFR', 'Quelles nouvelles nous apportez-vous des batailles en Ombreterre, mon $Uami:amie; ?', '', 38134),
(206418, 'frFR', 'Approchez donc, Sans-Entraves.', '', 38134),
(206417, 'frFR', 'Je serai accompagné du secutor Mévix et de deux de nos meilleurs soldats. J\’ose espérer que vous pourrez les accueillir également.', '', 38134),
(206413, 'frFR', '', 'Vous êtes toujours si $upressé:pressée;, $p.\n\nOh, je ne m\’en plains pas. J\’aime cet aspect de notre relation.\n\nVous ne me faites pas perdre mon temps, et je ne vous retiens pas.\n\nAlors, pourquoi avez-vous besoin de moi, cette fois-ci ?\n\nUne mission de reconnaissance ? Un assassinat ? Un conseil pour votre prochaine réception ?\n\nLa Matrone des espions est à votre disposition.', 38134),
(206411, 'frFR', '', 'À travers les âges, j\’ai vu bien des têtes couronnées jouer les maîtres de cour, Sans-Entraves.\n\nCela s\’est plus souvent mal terminé que l\’inverse, même sous les meilleurs auspices.\n\nVotre Rénathal ferait bien de prêter plus d\’attention aux critiques qu\’aux flatteries.\n\nUne réception ne satisfait jamais tout le monde. Voulez-vous savoir de quel côté je me situe ?\n\n<La baronne Vashj affiche un sourire suffisant.>', 38134),
(206410, 'frFR', 'Repos, sentinelles. L\’âme sans-entraves fait partie de mes proches amis.', '$p… L\’une des rares âmes que je suis contente de voir s\’aventurer dans notre camp ces temps-ci.', 38134),
(206409, 'frFR', 'Tout va pour le mieux au Repos du héros, $p. C\’est bon de vous revoir.', 'Ah, l\’habile prodige des réceptions est de retour dans notre coin de Maldraxxus.', 38134),
(206408, 'frFR', 'Salutations, Sans-Entraves.', 'Vos yeux ne vous ont pas trompée, Khaliiq. L\’âme sans-entraves approche.', 38134),
(206407, 'frFR', 'Je sers où l\’on a besoin de moi, et on a besoin de moi ici.', 'Faites attention à vos fréquentations, $p… Vous ne voudriez pas frayer avec un monstre.', 38134),
(206406, 'frFR', '', 'La forêt nous sourit.', 38134),
(206405, 'frFR', 'Effectivement. Mais qui ?', '', 38134),
(206404, 'frFR', '', 'D\’habitude, les spriggans n\’attaquent pas de façon aussi coordonnée. Quelqu\’un doit les guider.', 38134),
(206403, 'frFR', 'Pourquoi ne remettez-vous pas cette lettre au marchedroit Adrestès ?', '', 38134),
(206402, 'frFR', 'Attention !', '', 38134),
(206401, 'frFR', '', 'Des spriggans ! Parfait pour s\’entraîner ! Dommage qu\’ils soient si peu nombreux…', 38134),
(206400, 'frFR', '', 'Ne faites pas attention à Zarkin, Luméra. Seuls les actes comptent à ses yeux.', 38134),
(206399, 'frFR', '', 'C\’est bien vrai. Vous n\’aurez qu\’à semer le chaos dans les rangs de nos ennemis !', 38134),
(206398, 'frFR', 'Leithfor hoche la tête en silence.', '', 38134),
(206396, 'frFR', 'C\’est ce que nous allons voir. Passez devant, Leithfor.', '', 38134),
(206395, 'frFR', '', 'Je ne vous décevrai pas. Je ne vais pas me laisser décourager pour si peu.', 38134),
(206394, 'frFR', 'Prudence, Luméra. C\’est une patrouille fort dangereuse pour une chasseresse novice.', '', 38134),
(206393, 'frFR', 'J\’ignorais que la cour pouvait être aussi fascinante, $p. Vraiment, la cour des Braises est une très belle réussite de votre part.\n\nUnir toute l\’Ombreterre autour d\’une tasse de thé !\n\nCe n\’est pas aussi simple que ça, bien sûr, mais quand même… Qui l\’eût cru ?', '', 38134),
(206392, 'frFR', 'Cherchez-vous un endroit où vous reposer un instant, Sans-Entraves ?\n\nVous ne sauverez pas l\’Ombreterre si vous êtes sur les rotules.', '', 38134),
(206391, 'frFR', 'Si vous avez un instant, $p, j\’aimerais vous parler de quelque chose.\n\n<Pélagos prend une inspiration.>\n\nCela m\’a fait de la peine d\’apprendre que vous aviez choisi de combattre dans les rangs de Revendreth dans notre combat commun pour sauver l\’Ombreterre. Je pensais que vous lutteriez à mes côtés, dans la congrégation des Kyrians.\n\nMais à présent, je comprends pourquoi vous avez choisi les Venthyrs. Grâce à votre cour des Braises, toute l\’Ombreterre est unie, comme autrefois… comme elle aurait toujours dû l\’être.\n\nCe que je tenais à vous dire, c\’est que notre amitié forgée dans ce combat compte beaucoup à mes yeux. Nos âmes ne sont peut-être pas liées, mais je sens que nous avons tissé un lien d\’une autre nature.', '', 38134),
(206390, 'frFR', '', 'Ah, mon $uami:amie; ! Je suis ravie que vous ayez le temps de parler.\n\nÀ quand remonte notre dernière conversation ? Trop longtemps, en tout cas.\n\nRacontez-moi tout ce que vous avez vu et accompli depuis notre dernière rencontre. Avez-vous besoin de moi ou de Pélagos ?\n\n<Kléia prend une inspiration et sourit.>\n\nC\’est bon de vous revoir, $p.', 38134),
(206389, 'frFR', '', 'Je suis heureuse de vous revoir parmi les âmes du Bastion, $p.\n\nVos réceptions de la cour des Braises m\’ont confirmé que vous êtes $udévoué:dévouée; à l\’Ombreterre, bien que vous ayez choisi de vous associer aux Venthyrs.', 38134),
(206388, 'frFR', '', 'L\’Ombreterre a-t-elle besoin de moi, Sans-Entraves ?\n\nJe suis prête à me mettre à son service.', 38134),
(206387, 'frFR', 'Plus important : cherchez-vous des compagnons de route ?', '', 38134),
(206386, 'frFR', '', '$p ! Où allez-vous donc, mon $uami:amie; ?', 38134),
(206384, 'frFR', '', 'La puissance du Geôlier ne cesse de croître et toute l\’Ombreterre est menacée. Ne relâchez pas votre vigilance, $p. Les royaumes auront bientôt besoin de vous.', 38134),
(206383, 'frFR', '', 'Où que se trouve $p, c\’est le lieu le plus sûr d\’Ombreterre.', 38134),
(206382, 'frFR', 'Faites attention à vous, $p.', '', 38134),
(206381, 'frFR', '', '$gIl:Elle; nous le dira $gs\’il:si elle; le souhaite.', 38134),
(206380, 'frFR', 'Je me demande où se rend notre Sans-Entraves…', '', 38134),
(206378, 'frFR', 'J\’ignore si je vous l\’ai déjà dit, mais sachez que je vous suis reconnaissant, $p.\n\nVous m\’avez appris bien des choses sur la nature du service, du sacrifice, et sur…\n\n<Le marchedroit Adrestès s\’assure qu\’aucun aspirant ne peut l\’entendre avant de terminer sa phrase.>\n\n… l\’importance de s\’amuser de temps à autre.\n\nJ\’étais d\’abord réticent à l\’idée d\’assister à vos réceptions de la cour des Braises, mais maintenant, je les attends avec impatience.\n\nPour l\’Archonte, évidemment !', '', 38134),
(206377, 'frFR', 'Ravi de vous revoir au Bastion, $p.\n\nJe me demande souvent ce qu\’il se serait produit si vous vous étiez entièrement $udévoué:dévouée; à l\’Archonte, comme moi.\n\nCela dit, il est évident que votre allégeance à Tombe-Vice est bénéfique à toute l\’Ombreterre.\n\nJe gage que sous votre supervision, tout se passe au mieux là-bas.', '', 38134),
(206376, 'frFR', 'Que venez-vous faire au Repos du héros, Sans-Entraves ?\n\nSi vous avez besoin d\’une assistance militaire, les sentinelles et moi-même sommes à votre service.\n\nOu peut-être apportez-vous des nouvelles de Tombe-Vice ? Je peux les transmettre à mon commandement.', '', 38134),
(206374, 'frFR', 'Repos, sentinelles. L\’âme sans-entraves fait partie de mes proches amis.', '', 38134),
(206373, 'frFR', 'Tout va pour le mieux au Repos du héros, $p. C\’est bon de vous revoir.', '', 38134),
(206372, 'frFR', 'Salutations, Sans-Entraves.', '', 38134),
(206371, 'frFR', 'Je sers où l\’on a besoin de moi, et on a besoin de moi ici.', '', 38134),
(206367, 'frFR', '', '$p a besoin de quelque chose ? Je l\’ajoute tout en haut de ma liste !', 38134),
(206366, 'frFR', '', 'Sika aime les visites |2 $p !', 38134),
(206365, 'frFR', '', 'De quoi a besoin l\’âme sans-entraves ? Je l\’ajoute à ma liste.', 38134),
(206364, 'frFR', '', 'Dommage. La cour des Braises est très amusante. La prochaine fois, peut-être…', 38134),
(206363, 'frFR', '<Mikanikos inspecte soigneusement les pièces de votre armure tout en marmonnant.>\n\nTrès astucieux… Innovant… intrigant même…\n\nVos visites sont toujours un évènement, Sans-Entraves.', '', 38134),
(206362, 'frFR', 'Vous êtes toujours $ubienvenu:bienvenue; dans mon atelier, $p.', '', 38134),
(206361, 'frFR', '<Mikanikos met de côté son projet en cours pour vous accorder toute son attention.>\n\n$p, vos visites dans mon atelier sont un véritable plaisir.\n\nToujours riches en idées nouvelles. Votre amitié est précieuse.\n\nLe travail peut attendre. Dites-moi, mon $uami:amie;, que venez-vous faire à la forge Éternelle ?\n\nVous venez me voir ? Vous avez besoin de quelque chose ? Il y aura bientôt une autre réception, j\’espère !', '', 38134),
(206360, 'frFR', '$p ! Quelle inspiration nouvelle m\’apportez-vous aujourd\’hui ?\n\nAvez-vous combattu de nouveaux ennemis ? Découvert de nouvelles technologies ? Maîtrisé une nouvelle magie ?\n\nRacontez-moi tout.\n\nLes petits détails mènent souvent aux grandes révélations !', '', 38134),
(206359, 'frFR', 'Chaque pièce est parfaite pour quelque chose, mais cette fois, j\’ai quelque chose de très particulier !', '', 38134),
(206358, 'frFR', 'Ah, mon $uami:amie; ! Les bibelots de la cour des Braises sont si intrigants… Apportez-en d\’autres !', '', 38134),
(206357, 'frFR', 'Tant à apprendre… Tant à expérimenter…', 'Vous êtes toujours $ubienvenu:bienvenue; dans mon bosquet, Sans-Entraves.', 38134),
(206355, 'frFR', '', 'Je sais.', 38134),
(206354, 'frFR', 'Si tu le dis… Je te fais confiance. Pourtant, j\’ai l\’impression de ne pas progresser assez vite.', '', 38134),
(206353, 'frFR', '', 'Tu ne t\’en rends peut-être pas compte, mais moi, je vois que tu as plus de facilité à affronter ces souvenirs. Tu t\’en sors bien, Pélagos.', 38134),
(206352, 'frFR', 'J\’aimerais pouvoir dire que je m\’améliore, Kléia…', '', 38134),
(206344, 'frFR', 'Si vous trouvez des affiches qui montrent de nouvelles coiffures, je pourrai les adopter. Et si vous trouvez des chapeaux à ma taille, je les porterai !', '', 38134),
(206343, 'frFR', 'Vous pouvez trouver des teintures utilisables près des tourbières ou bien au Marché Nocturne.', '', 38134),
(206338, 'frFR', 'Nos assemblages adorent plastronner sur le champ de bataille. Il existe une large gamme d\’accessoires de mode dont vous pourrez affubler vos assemblages pour améliorer leur esthétique au combat.\n\nToutefois, pour trouver les ingrédients nécessaires à la création de certains de ces accessoires, vous ne devrez pas craindre de partir à l\’aventure.', '', 38134),
(206337, 'frFR', 'Les membres exceptionnels sont un composant clé pour fabriquer de puissants objets et assemblages de combat.\n\nCes ingrédients sont particulièrement difficiles à localiser, mais les assemblages ici vous aideront à en obtenir si vous les aidez à résoudre leurs problèmes.', '', 38134),
(206336, 'frFR', 'La chair malléable est le composant principal que nous utilisons pour la plupart des assemblages et objets fabriqués grâce à la suture abominable.\n\nVous avez une chance de récupérer un morceau de chair malléable sur toutes les créatures que vous éliminez en Ombreterre.\n\nCordier est aussi très doué pour trouver de la chair malléable, ne craignez donc pas de faire appel à ses services pour localiser cet ingrédient.', '', 38134),
(206325, 'frFR', 'L\’art de la suture abominable requiert une main ferme, des ingrédients de qualité et, bien sûr, la Table de suture abominable, juste là.\n\nJ\’ai noté la liste des ingrédients dont vous aurez besoin pour chaque assemblage et objet que nous pouvons fabriquer grâce à la Table de suture abominable.', '', 38134),
(206322, 'frFR', 'Oh, ils vont être terriblement déçus s\’ils ratent toute la fête !', '', 38134),
(206321, 'frFR', 'Au fait, je sais pas si je vous l\’ai déjà dit, mais merci de permettre à mes amis de venir aux réceptions de la cour des Braises.\n\nOn est un petit groupe, on se serre les coudes.\n\nEn plus, si j\’allais à ces fêtes d\’enfer sans Slanknen, j\’aurais pas fini d\’en entendre parler !\n\n<Choufa rit.>\n\nAu pire, vous pourriez prendre sa place dans le groupe ! Vous êtes des nôtres, maintenant.', '', 38134),
(206320, 'frFR', 'Ces cours magistrales, ces fêtes luxueuses… On mène la grande vie, vous et moi ! Sans oublier Rury, Ulul et Slanknen.\n\nAu fait, ce sera quand, la prochaine réception de la cour des Braises ? Je suis libre, là. Ça vous dit d\’y aller maintenant, d\’ouvrir quelques bouteilles et de lancer les festivités ?\n\nNan, je vois que vous êtes $uoccupé:occupée;… vous avez à faire. C\’est pas grave.\n\nMais n\’oubliez pas de m\’inviter la prochaine fois, d\’accord ?', '', 38134),
(206319, 'frFR', 'Dites, dites ! Il vous reste des feux d\’artifice de la dernière réception ?', '', 38134),
(206318, 'frFR', 'Bonjour, $p ! C\’est quand, la prochaine réception ?', '', 38134),
(206317, 'frFR', 'Où est-ce qu\’il est encore fourré, Rury… ?', '', 38134),
(206316, 'frFR', '', 'Je ne fais pas toujours confiance à ces négociants… malheureusement, il m\’est souvent difficile de résister à leurs marchandises.', 38134),
(206315, 'frFR', '', 'La douleur et le deuil sont deux puissants précepteurs, et j\’ai été leur élève plus souvent qu\’à mon tour.\n\nMais je n\’en suis que plus reconnaissante de vos invitations à la cour des Braises.\n\nMes séjours ont été pour le moins… enrichissants. Votre prince m\’a convaincue. Je suis persuadée qu\’il ne veut que notre bien, à nous comme aux autres royaumes.\n\nMille mercis, $p. Il me tarde de retourner à la cour en votre compagnie.', 38134),
(206314, 'frFR', '', 'J\’ai beaucoup apprécié le chemin que nous avons parcouru ensemble, $p, et j\’ai le sentiment que ce ne sont là que nos premiers pas sur le sentier d\’une longue amitié.\n\nVous m\’avez appris à m\’ouvrir à de nouvelles idées et possibilités, et j\’espère vous avoir également ouvert les yeux sur des concepts neufs.\n\nJe sais que vous ne pouvez demeurer à mes côtés. Telle est la voie des Sans-Entraves.\n\nMais ne vous absentez pas trop longtemps non plus. Mon bosquet ne serait plus le même sans vous.', 38134),
(206313, 'frFR', '', 'Vous me voyez ravie de vous revoir, $p. Dites-moi, le temps vous presse-t-il ?', 38134),
(206312, 'frFR', '', 'Quel bon vent vous mène à moi ?', 38134),
(206311, 'frFR', '', 'Je vous accueillerai toujours volontiers dans mon bosquet, Sans-Entraves.', 38134),
(206310, 'frFR', '', 'Ah, §p.\n\nPuisque vous êtes là, mon §Uami:amie;, la chasse attendra.\n\nQuelles nouvelles de Revendreth ? Ici, en Sylvarden, les Drurst, les gorms et autres vermines continuent de nous harceler de toute part.\n\nNon que je m\’en plaigne : c\’est une bonne excuse pour sortir chasser.\n\nJ\’espère que vous me ferez bientôt le plaisir de m\’accompagner, d\’ailleurs. J\’ai fini par me faire à vos méthodes de combat.\n\nPourquoi chasser en groupe quand on peut chasser rien qu\’à deux, après tout ?', 38134),
(206309, 'frFR', '', 'Comment va la cour, $p ?\n\nVous avez organisé une ou deux parties de chasse pour la prochaine session ? Si je dois y participer, j\’espère peu de fraîcheur dans le programme.\n\nJ\’ai beau avoir beaucoup apprécié nos chasses passées sur le domaine de la cour, il est toujours plus grisant de traquer de nouvelles proies, vous ne trouvez pas ?\n\nQuoi qu\’il en soit, j\’espère pouvoir de nouveau chasser avec vous très bientôt.', 38134),
(206308, 'frFR', '', 'J\’imagine que votre chasse se déroule toujours au mieux, $p.\n\nAlors, qu\’avez-vous traqué ces derniers temps ?', 38134),
(206307, 'frFR', '', '$p ! Reposez-vous un moment et parlez-moi de votre chasse.', 38134),
(206306, 'frFR', '', 'Comment se passe la chasse, Sans-Entraves ?', 38134),
(206305, 'frFR', '', 'La chasse nous appelle. Qui y répondra ?', 38134),
(206304, 'frFR', '', 'Bonne chasse, Sans-Entraves.', 38134),
(206303, 'frFR', '', 'Les phéromones de gorm sentent bien meilleur que vous ne l\’imaginez, croyez-moi.', 38134),
(206302, 'frFR', '', '<Dame Baie-de-Lune lance un regard furtif aux alentours avant de se pencher vers vous et de poursuivre à voix basse.>\n\nÀ présent, vous et moi sommes les $Umeilleurs amis:meilleures amies; du monde, $p. Ce qui signifie que nous devons nous serrer les coudes.\n\nD\’ailleurs, j\’aimerais que nous jouions un nouveau tour à…\n\n<Votre interlocutrice remarque la présence d\’une fée curieuse à proximité.>\n\nNous en parlerons à la cour des Braises. J\’ai tellement hâte !', 38134),
(206301, 'frFR', '', '<Dame Baie-de-Lune étouffe un rire.>\n\n$p ! Quel plaisir de vous voir !\n\nVous me rappelez le petit tour que j\’ai joué à Théotar lors de la dernière réception de la cour des Braises. Quel moment merveilleux !\n\nThéotar sera-t-il à nouveau de la partie ? Il a promis de me rendre la pareille, mais j\’attends toujours…', 38134),
(206300, 'frFR', '', 'Rebonjour ! Quel tour pendable nous préparez-vous, aujourd\’hui ?\n\nEt surtout : en quoi puis-je y contribuer.', 38134),
(206299, 'frFR', '', '$p ! Vous n\’alliez pas continuer votre route sans dire bonjour à votre fée préférée, quand même ?', 38134),
(206298, 'frFR', '', 'Ne vous en faites pas, $p. Je ne compte pas vous jouer de vilains tours aujourd\’hui.', 38134),
(206297, 'frFR', '', 'Ah, voilà qui devrait être intéressant…', 38134),
(206296, 'frFR', 'Nous sommes débordés. En cas d\’urgence, veuillez vous adresser à la main de pureté. Merci.', 'Nous sommes débordés. En cas d\’urgence, veuillez vous adresser à la main de pureté. Merci.', 38134),
(206295, 'frFR', '<Le transcendé vous dévisage.>\n\nSous quels fardeaux ployez-vous ?', '<La transcendée vous dévisage.>\n\nSous quels fardeaux ployez-vous ?', 38134),
(206294, 'frFR', 'Je viendrai vous aider dès que possible. Patientez encore un peu, je vous prie.', 'Je viendrai vous aider dès que possible. Patientez encore un peu, je vous prie.', 38134),
(206293, 'frFR', 'En quoi puis-je vous être utile ?', 'En quoi puis-je vous être utile ?', 38134),
(206292, 'frFR', 'Qu\’est-ce qui vous tourmente ?', 'Qu\’est-ce qui vous tourmente ?', 38134),
(206291, 'frFR', 'Vous venez vous faire purifier ? Alors, je crains qu\’il ne vous faille attendre. Bien d\’autres aspirants ont besoin de mon aide.', 'Vous venez vous faire purifier ? Alors, je crains qu\’il ne vous faille attendre. Bien d\’autres aspirants ont besoin de mon aide.', 38134),
(206290, 'frFR', 'Je n\’ai jamais rien vu de tel. Il n\’est pas rare que des aspirants viennent à nous en temps de crise, mais jamais en si grand nombre… J\’ai la nette impression que la situation se dégrade hors de l\’enceinte de notre temple.\n\nPardonnez-moi, mais je dois retourner travailler.', 'Je n\’ai jamais rien vu de tel. Il n\’est pas rare que des aspirants viennent à nous en temps de crise, mais jamais en si grand nombre… J\’ai la nette impression que la situation se dégrade hors de l\’enceinte de notre temple.\n\nPardonnez-moi, mais je dois retourner travailler.', 38134),
(206289, 'frFR', 'Bienvenue au temple de la Pureté. Prenez le temps d\’admirer les lieux… mais restez sur vos gardes. Nombreux sont ceux qui attendent leur purification.', 'Bienvenue au temple de la Pureté. Prenez le temps d\’admirer les lieux… mais restez sur vos gardes. Nombreux sont ceux qui attendent leur purification.', 38134),
(206288, 'frFR', '<Le transcendé incline la tête.>\n\nLe regard de l\’Archonte est sur vous, $umortel:mortelle;.', '<La transcendée incline la tête.>\n\nLe regard de l\’Archonte est sur vous, $umortel:mortelle;.', 38134),
(206287, 'frFR', 'Votre arrivée est plus importante que vous ne pouvez l\’imaginer. C\’est un privilège d\’y assister.', 'Votre arrivée est plus importante que vous ne pouvez l\’imaginer. C\’est un privilège d\’y assister.', 38134),
(206286, 'frFR', 'Vous avez créé un certain émoi au fort Élyséen, $umortel:mortelle;.', 'Vous avez créé un certain émoi au fort Élyséen, $umortel:mortelle;.', 38134),
(206285, 'frFR', '<Le transcendé vous jette un regard curieux.>\n\nFélicitations. Et bienvenue dans les rangs des aspirants.', '<Le transcendé vous jette un regard curieux.>\n\nFélicitations. Et bienvenue dans les rangs des aspirants.', 38134),
(206284, 'frFR', 'Je n\’ai pas rendu l\’âme et traversé leur soi-disant voile pour passer l\’éternité à ATTENDRE !\n\nUne minute… Vous venez d\’arriver, vous ! La situation s\’est-elle enfin débloquée ?', 'Je n\’ai pas rendu l\’âme et traversé leur soi-disant voile pour passer l\’éternité à ATTENDRE !\n\nUne minute… Vous venez d\’arriver, vous ! La situation s\’est-elle enfin débloquée ?', 38134),
(206283, 'frFR', '<L\’âme laisse échapper un profond soupir.>\n\nAu moins, c\’est un bel endroit pour patienter.', '<L\’âme laisse échapper un profond soupir.>\n\nAu moins, c\’est un bel endroit pour patienter.', 38134),
(206282, 'frFR', 'Une longue route nous attend, mais on nous empêche de l\’emprunter… Je vais finir par penser qu\’on m\’a envoyé ici par erreur.', 'Une longue route nous attend, mais on nous empêche de l\’emprunter… Je vais finir par penser qu\’on m\’a envoyée ici par erreur.', 38134),
(206281, 'frFR', 'Ils nous disent d\’attendre, mais vous voyez un parangon de patience dans les parages, vous ?', 'Ils nous disent d\’attendre, mais vous voyez un parangon de patience dans les parages, vous ?', 38134),
(206280, 'frFR', 'Vous avez des nouvelles de l\’Archonte ? La pénurie est-elle enfin terminée ?', 'Vous avez des nouvelles de l\’Archonte ? La pénurie est-elle enfin terminée ?', 38134),
(206279, 'frFR', 'Ils nous ont dit d\’attendre ici jusqu\’à ce que la crise de l\’anima soit résolue… mais depuis combien de temps ? Je l\’ignore. C\’est à croire que cette pénurie n\’en finira jamais.\n\nAu moins, les régisseurs sont serviables, et je peux manger autant que je veux sans grossir ! Ha ha !', 'Ils nous ont dit d\’attendre ici jusqu\’à ce que la crise de l\’anima soit résolue… mais depuis combien de temps ? Je l\’ignore. C\’est à croire que cette pénurie n\’en finira jamais.\n\nAu moins, les régisseurs sont serviables, et je peux manger autant que je veux sans grossir ! Ha ha !', 38134),
(206278, 'frFR', 'Je veux juste m\’engager sur la voie. Est-ce donc trop demander ?', 'Je veux juste m\’engager sur la voie. Est-ce donc trop demander ?', 38134),
(206277, 'frFR', 'Tout ceci est absurde. J\’attends depuis… Quel jour sommes-nous déjà ?\n\nUne éternité, pour sûr !', 'Tout ceci est absurde. J\’attends depuis… Quel jour sommes-nous déjà ?\n\nUne éternité, pour sûr !', 38134),
(206276, 'frFR', 'Une nouvelle âme ? ENFIN !', 'Une nouvelle âme ? ENFIN !', 38134),
(206275, 'frFR', 'Oh ! Une nouvelle tête ! Avez-vous des nouvelles du reste de l\’au-delà ?', 'Oh ! Une nouvelle tête ! Avez-vous des nouvelles du reste de l\’au-delà ?', 38134),
(206274, 'frFR', 'Je me suis préparé toute ma vie pour venir ici. Et cet endroit était magnifique. Il l\’est toujours d\’ailleurs, mais on m\’a promis de m\’aider à devenir l\’incarnation la plus aboutie de mon être. Au lieu de cela, je suis coincé ici à ruminer mon amertume. Il ne peut s\’agir que d\’une erreur, n\’est-ce pas ?', 'Je me suis préparée toute ma vie pour venir ici. Et cet endroit était magnifique. Il l\’est toujours d\’ailleurs, mais on m\’a promis de m\’aider à devenir l\’incarnation la plus aboutie de mon être. Au lieu de cela, je suis coincée ici à ruminer mon amertume. Il ne peut s\’agir que d\’une erreur, n\’est-ce pas ?', 38134),
(206273, 'frFR', 'Au moins, la mort était intéressante. Mais à quoi rime toute cette attente ?', 'Au moins, la mort était intéressante. Mais à quoi rime toute cette attente ?', 38134),
(206272, 'frFR', 'Mon corps me manque.', 'Mon corps me manque.', 38134),
(206271, 'frFR', 'J\’espère que votre arrivée annonce un renouveau, l\’$uami:amie;. Tout ne va pas pour le mieux au Bastion.', 'J\’espère que votre arrivée annonce un renouveau, l\’$uami:amie;. Tout ne va pas pour le mieux au Bastion.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206270, 'frFR', 'Le pire dans cette attente, c\’est de ne pas avoir de mains. Ni de jambes. Ni d\’yeux d\’ailleurs.\n\nEn toute franchise, je n\’y comprends pas grand-chose. Ils m\’ont dit que j\’obtiendrais un nouveau corps après ma première purification, mais rares sont ceux qui ont accompli le rituel depuis le début de la pénurie.', 'Le pire dans cette attente, c\’est de ne pas avoir de mains. Ni de jambes. Ni d\’yeux d\’ailleurs.\n\nEn toute franchise, je n\’y comprends pas grand-chose. Ils m\’ont dit que j\’obtiendrais un nouveau corps après ma première purification, mais rares sont ceux qui ont accompli le rituel depuis le début de la pénurie.', 38134),
(206268, 'frFR', 'Cet endroit devait être ma récompense pour une vie exemplaire. M\’aurait-on menti ? S\’agit-il d\’une punition ?', 'Cet endroit devait être ma récompense pour une vie exemplaire. M\’aurait-on menti ? S\’agit-il d\’une punition ?', 38134),
(206267, 'frFR', 'Il paraît que les collecteurs sont habiles de leurs mains. Je devrais peut-être me mettre à la sculpture sur bois…', 'Il paraît que les collecteurs sont habiles de leurs mains. Je devrais peut-être me mettre à la sculpture sur bois…', 38134),
(206266, 'frFR', 'Devenir guetteur n\’a rien d\’une mince affaire, on dirait, mais donner une nouvelle chance à une âme… n\’est-ce point là un but louable ?', 'Devenir guetteuse n\’a rien d\’une mince affaire, on dirait, mais donner une nouvelle chance à une âme… n\’est-ce point là un but louable ?', 38134),
(206265, 'frFR', 'Je crois que je rejoindrai les porte-âmes après mon ascension. Je veux découvrir les mondes au-delà de l\’Ombreterre !', 'Je crois que je rejoindrai les porte-âmes après mon ascension. Je veux découvrir les mondes au-delà de l\’Ombreterre !', 38134),
(206264, 'frFR', 'Le Bastion est un endroit merveilleux. Il paraît que le ciel était empli d\’anima avant que les choses ne tournent mal. J\’aimerais vraiment le voir tel qu\’il était avant.', 'Le Bastion est un endroit merveilleux. Il paraît que le ciel était empli d\’anima avant que les choses ne tournent mal. J\’aimerais vraiment le voir tel qu\’il était avant.', 38134),
(206263, 'frFR', 'Oh, je finirai bien par les avoir, mes ailes…\n\n<L\’âme soupire avec mélancolie.>', 'Oh, je finirai bien par les avoir, mes ailes…\n\n<L\’âme soupire avec mélancolie.>', 38134),
(206262, 'frFR', 'Le bosquet de l\’Éveil est la dernière étape sur la voie de la renaissance. C\’est l\’un des sanctuaires les plus sacrés de Sylvarden.\n\nQuand un esprit est prêt à regagner son monde, on l\’amène ici. Il franchit ce portail et entame un nouveau cycle.', 'Le bosquet de l\’Éveil est la dernière étape sur la voie de la renaissance. C\’est l\’un des sanctuaires les plus sacrés de Sylvarden.\n\nQuand un esprit est prêt à regagner son monde, on l\’amène ici. Il franchit ce portail et entame un nouveau cycle.', 38134),
(206261, 'frFR', 'J\’ai tenté par trois fois la première purification, mais je n\’arrive pas à me débarrasser de mes fardeaux ! Et ce n\’est que la première étape ! Suis-je donc piégé ici pour l\’éternité ?', 'J\’ai tenté par trois fois la première purification, mais je n\’arrive pas à me débarrasser de mes fardeaux ! Et ce n\’est que la première étape ! Suis-je donc piégée ici pour l\’éternité ?', 38134),
(206260, 'frFR', 'J\’ai tant à apprendre… mais rien ne presse. Ce n\’est pas comme si la voie de l\’Ascension était ouverte à qui que ce soit.', 'J\’ai tant à apprendre… mais rien ne presse. Ce n\’est pas comme si la voie de l\’Ascension était ouverte à qui que ce soit.', 38134),
(206259, 'frFR', 'D\’abord le rite de pureté, puis le rite de courage, et ensuite le rite de…\n\nAttendez, je sais. Loyauté ? Non, je dois me tromper…', 'D\’abord le rite de pureté, puis le rite de courage, et ensuite le rite de…\n\nAttendez, je sais. Loyauté ? Non, je dois me tromper…', 38134),
(206258, 'frFR', 'Depuis combien de temps attendez-vous ?', 'Depuis combien de temps attendez-vous ?', 38134),
(206257, 'frFR', 'J\’ai tant à apprendre et ce retard ne fait qu\’entraver mes efforts. J\’ai peur de ne jamais faire mon ascension.', 'J\’ai tant à apprendre et ce retard ne fait qu\’entraver mes efforts. J\’ai peur de ne jamais faire mon ascension.', 38134),
(206256, 'frFR', 'Depuis quand les choses vont-elles aussi mal ? Le temps s\’écoule de façon si étrange ici.', 'Depuis quand les choses vont-elles aussi mal ? Le temps s\’écoule de façon si étrange ici.', 38134),
(206255, 'frFR', 'Tout ce que je veux, c\’est commencer les cinq rites.', 'Tout ce que je veux, c\’est commencer les cinq rites.', 38134),
(206254, 'frFR', 'Où s\’est interrompu votre entraînement ? En ce qui me concerne, j\’étais sur le point d\’achever le rite de loyauté quand tout est parti à vau-l\’eau. J\’étais si proche de l\’ascension…', 'Où s\’est interrompu votre entraînement ? En ce qui me concerne, j\’étais sur le point d\’achever le rite de loyauté quand tout est parti à vau-l\’eau. J\’étais si proche de l\’ascension…', 38134),
(206253, 'frFR', 'Il n\’y a rien à faire. Ni la méditation ni l\’entraînement ne me rapprocheront de l\’Ascension. Pas en ce moment, en tout cas.\n\n<L\’aspirant pousse un profond soupir.>', 'Il n\’y a rien à faire. Ni la méditation ni l\’entraînement ne me rapprocheront de l\’Ascension. Pas en ce moment, en tout cas.\n\n<L\’aspirante pousse un profond soupir.>', 38134),
(206252, 'frFR', 'Bien que le Bastion ne soit plus que l\’ombre de lui-même, il reste le plus bel endroit qu\’il m\’ait été donné de voir. J\’y passerais volontiers le reste de l\’éternité à attendre, s\’il le faut.', 'Bien que le Bastion ne soit plus que l\’ombre de lui-même, il reste le plus bel endroit qu\’il m\’ait été donné de voir. J\’y passerais volontiers le reste de l\’éternité à attendre, s\’il le faut.', 38134),
(206251, 'frFR', 'Comment occupez-vous votre temps, mon $uami:amie; ?', 'Comment occupez-vous votre temps, mon $uami:amie; ?', 38134),
(206250, 'frFR', 'Par l\’Archonte, je suis sur les rotules.', 'Par l\’Archonte, je suis sur les rotules.', 38134),
(206249, 'frFR', 'Servez le temple, qu\’ils disaient. Incarnez la vertu, qu\’ils disaient. Eh bien, moi…\n\n<L\’aspirait s\’interrompt après avoir remarqué votre présence.>', 'Servez le temple, qu\’ils disaient. Incarnez la vertu, qu\’ils disaient. Eh bien, moi…\n\n<L\’aspirante s\’interrompt après avoir remarqué votre présence.>', 38134),
(206248, 'frFR', '<L\’aspirant se ressaisit rapidement avant de parler.>\n\nQue puis-je pour vous ?', '<L\’aspirante se ressaisit rapidement avant de parler.>\n\nQue puis-je pour vous ?', 38134),
(206247, 'frFR', 'Je reviendrai m\’occuper de vous au plus vite.', 'Je reviendrai m\’occuper de vous au plus vite.', 38134),
(206246, 'frFR', 'Êtes-vous également en quête de purification ?', 'Êtes-vous également en quête de purification ?', 38134),
(206245, 'frFR', 'Ils m\’ont dit que je pourrais les aider après avoir accompli mon propre rite de pureté. Mais j\’ai… j\’ai peur. Ils sont si nombreux.', 'Ils m\’ont dit que je pourrais les aider après avoir accompli mon propre rite de pureté. Mais j\’ai… j\’ai peur. Ils sont si nombreux.', 38134),
(206244, 'frFR', '<L\’aspirant prend une profonde inspiration avant de s\’adresser à vous.>\n\nUn problème ?', '<L\’aspirante prend une profonde inspiration avant de s\’adresser à vous.>\n\nUn problème ?', 38134),
(206243, 'frFR', 'De quoi avez-vous besoin ?', 'De quoi avez-vous besoin ?', 38134),
(206242, 'frFR', 'Je vous remercie pour votre patience. Sachez que nous faisons tout notre possible.', 'Je vous remercie pour votre patience. Sachez que nous faisons tout notre possible.', 38134),
(206241, 'frFR', 'Puis-je faire quelque chose pour vous ?', 'Puis-je faire quelque chose pour vous ?', 38134),
(206240, 'frFR', 'En quoi puis-je vous être utile ?', 'En quoi puis-je vous être utile ?', 38134),
(206239, 'frFR', 'Le regard de l\’Archonte est sur vous, mon $uami:amie;.', 'Le regard de l\’Archonte est sur vous, mon $uami:amie;.', 38134),
(206238, 'frFR', 'Qu\’est-ce que ce régisseur peut faire pour vous, hou ?', 'Qu\’est-ce que cette régisseuse peut faire pour vous, hou ?', 38134),
(206236, 'frFR', 'Très triste. Beaucoup de morts.\n\n<Ses aigrettes s\’abaissent.>', 'Très triste. Beaucoup de morts.\n\n<Ses aigrettes s\’abaissent.>', 38134),
(206235, 'frFR', 'Les aspirants tristes, ça fait de la peine aux régisseurs.', 'Les aspirants tristes, ça fait de la peine aux régisseurs.', 38134),
(206234, 'frFR', 'Soyez pas triste. Un jus ? Ou une chanson ?', 'Soyez pas triste. Un jus ? Ou une chanson ?', 38134),
(206233, 'frFR', 'Trop d\’aspirants tristes… Laissez-nous-hou arranger ça !', 'Trop d\’aspirants tristes… Laissez-nous-hou arranger ça !', 38134),
(206232, 'frFR', 'Le jus, c\’est bon pour réfléchir. Et pour s\’entraîner. Vous voulez un jus ?', 'Le jus, c\’est bon pour réfléchir. Et pour s\’entraîner. Vous voulez un jus ?', 38134),
(206231, 'frFR', '$UFatigué:Fatiguée; de votre entraînement ? Asseyez-vous. Ou étirez-vous-hou.', '$UFatigué:Fatiguée; de votre entraînement ? Asseyez-vous. Ou étirez-vous-hou.', 38134),
(206230, 'frFR', 'Bienvenue ! Il vous-hou faut quoi ?', 'Bienvenue ! Il vous-hou faut quoi ?', 38134),
(206229, 'frFR', 'Comment peut-on vous-hou faire plaisir ?', 'Comment peut-on vous-hou faire plaisir ?', 38134),
(206228, 'frFR', 'Tout nettoyer. Tout ranger. Faut que le Bastion soit beau !', 'Tout nettoyer. Tout ranger. Faut que le Bastion soit beau !', 38134),
(206227, 'frFR', 'AH ! Une nouvelle âme ! Sonnez les cloches ! Sonnez les cors ! Hou !', 'AH ! Une nouvelle âme ! Sonnez les cloches ! Sonnez les cors ! Hou !', 38134),
(206226, 'frFR', 'Bonjou-hour ! Vous venez d\’arriver ? Super !', 'Bonjou-hour ! Vous venez d\’arriver ? Super !', 38134),
(206225, 'frFR', 'Hou ! Bonjou-hour ! Une nouvelle âme ! Parfait !', 'Hou ! Bonjou-hour ! Une nouvelle âme ! Parfait !', 38134),
(206223, 'frFR', 'Je peux vous servir quelque chose ? Hou !', 'Je peux vous servir quelque chose ? Hou !', 38134),
(206222, 'frFR', 'On peut aider ! Qu\’est-ce qu\’il vous-hou faut ?', 'On peut aider ! Qu\’est-ce qu\’il vous-hou faut ?', 38134),
(206221, 'frFR', '<Le régisseur fredonne un air guilleret.>', '<La régisseuse fredonne un air guilleret.>', 38134),
(206220, 'frFR', 'J\’ai tout-hou ce qu\’il vous faut !', 'J\’ai tout-hou ce qu\’il vous faut !', 38134),
(206219, 'frFR', '', 'Nous sommes tous un peu las. La pénurie a considérablement bouleversé notre quotidien.\n\nMais je ne baisserai pas les bras. Que puis-je faire pour vous ?', 38134),
(206218, 'frFR', '<Achillon grommelle en exécutant sa série d\’exercices.>', '', 38134),
(206217, 'frFR', '<Phillios est en grande conversation avec Tymon.>\n\nCe que vous êtes ne se résume pas à ce seul souvenir. Évoquez-le, puis libérez-vous-en. Je sais que c\’est plus facile à dire qu\’à faire, mais vous y êtes presque. Vous surmonterez bientôt cette épreuve. J\’en suis certain.', '<Phillios est en grande conversation avec Tymon.>\n\nCe que vous êtes ne se résume pas à ce seul souvenir. Évoquez-le, puis libérez-vous-en. Je sais que c\’est plus facile à dire qu\’à faire, mais vous y êtes presque. Vous surmonterez bientôt cette épreuve. J\’en suis certain.', 38134),
(206216, 'frFR', '<Tymon est en grande discussion avec Phillios.>\n\nVous êtes toujours si stoïque face à vos épreuves ! J\’aimerais pouvoir en faire autant. Mais j\’ai l\’impression de perdre tous mes moyens dès qu\’un souvenir pénible me revient.', '<Tymon est en grande discussion avec Phillios.>\n\nVous êtes toujours si stoïque face à vos épreuves ! J\’aimerais pouvoir en faire autant. Mais j\’ai l\’impression de perdre tous mes moyens dès qu\’un souvenir pénible me revient.', 38134),
(206214, 'frFR', '', 'Mon âme liée semble s\’être entichée de vous. Je devrais sans doute vous remercier. Elle commençait à perdre de son entrain sans sa forge.\n\nSaluez-la si vous passez par le temple du Courage.', 38134),
(206213, 'frFR', '', 'Ne vous en faites pas pour moi. Je suis… souffrante, mais mon parangon s\’occupera de moi dès qu\’elle le pourra. En attendant, mon entraînement m\’aidera à tenir le coup.\n\n<Éridia inspire difficilement.>\n\nJe ne dois pas ressasser mon chagrin ou ma colère… Et oublier Lysonia.', 38134),
(206211, 'frFR', 'Nous préservons le cycle.', 'Nous préservons le cycle.', 38134),
(206210, 'frFR', '', 'Tous ces pauvres aspirants… tous les transcendés que nous avons envoyés au temple de la Loyauté… Les avons-nous condamnés ? Ils ont dû perdre la raison pour attaquer ce temple ! Nous ne sommes pas leurs ennemis ! Nous voulions juste…\n\n<Éridia se mure dans le silence en observant le temple tandis qu\’une larme coule sur sa joue.>', 38134),
(206209, 'frFR', '', 'Vous allez vivre des moments très inhabituels, $umortel:mortelle;. Ne vous laissez pas impressionner. Les esprits sains comme le vôtre nous seront utiles en ces temps difficiles.', 38134),
(206208, 'frFR', 'Bienvenue au bosquet de l\’Éveil.', 'Bienvenue au bosquet de l\’Éveil.', 38134),
(206207, 'frFR', '', 'Je suis la main du parangon. Je lui obéis en tout.', 38134),
(206206, 'frFR', '', 'Vous avez besoin de nos disciples ?', 38134),
(206205, 'frFR', '', 'Il en sera fait selon la volonté de mon parangon.', 38134),
(206204, 'frFR', '', 'Par la volonté de Vésiphone.', 38134),
(206202, 'frFR', 'C\’est peine perdue, ils refusent de nous écouter ! Cette violence doit cesser avant que nous ne succombions tous !', '', 38134),
(206201, 'frFR', 'Merci pour votre aide. Nous ne savons plus où donner de la tête.', '', 38134),
(206200, 'frFR', 'J\’ai voué mon existence à servir le temple de la Pureté. N\’hésitez pas à faire appel à moi si vous avez besoin de quoi que ce soit lors de votre séjour ici.', '', 38134),
(206199, 'frFR', '', 'Cela fait trop longtemps que je n\’avais plus assisté à cette cérémonie.\n\nFélicitations et merci pour l\’espoir que vous nous avez donné.', 38134),
(206198, 'frFR', '', 'Oui, je suis encore là. \n\n<La forgelite Sophone vous regarde en riant.>\n\nIl se trouve que ma forge est idéale pour former les nouveaux aspirants. J\’ai donc demandé à l\’Archonte de rester au Creuset et elle a accepté ! Je serais heureuse de passer le reste de l\’éternité ici.', 38134),
(206196, 'frFR', 'Que la Nuit vous bénisse.', 'Que la Nuit vous bénisse.', 38134),
(206195, 'frFR', '', 'Un jour, je ferai mon ascension. Mais en attendant, j\’ai la forge pour m\’occuper. Enfin, quand j\’ai de l\’anima, bien sûr.', 38134),
(206194, 'frFR', '', 'Cela doit vous sembler étrange de parler de votre entraînement à une autre aspirante. Mais on m\’a seulement nommée responsable du fait de mon ancienneté.\n\n<La forgelite Sophone s\’esclaffe.>', 38134),
(206193, 'frFR', '', 'La forge est la prunelle de mes yeux. Ce n\’est pas pour rien que je suis la seule aspirante forgelite !', 38134),
(206192, 'frFR', '', 'Maître Mikanikos est très occupé. Sika s\’assure que la forgelite a tout ce qu\’il lui faut.', 38134),
(206191, 'frFR', '', '<Sika consulte son parchemin.>\n\nAnima ? OK.\nPince ? OK.\nMatériaux ? OK.\nForgelite ? OK.\nSika ? OK !\n\nOn a tout ! Hou hou !', 38134),
(206190, 'frFR', '', 'Oh, non ! Sika a des ennuis ?', 38134),
(206189, 'frFR', '', 'L\’hôtesse manquait à Sika. La mnémès sait tout !\n\nEnfin, presque tout.', 38134),
(206188, 'frFR', '', 'Maître Mikanikos, le premier forgelite, a chargé Sika de s\’occuper des centurions. Mais il n\’y a pas assez d\’anima pour tout le monde. Et maintenant, vous ! L\’hôtesse a besoin d\’anima !\n\nHeureusement, Sika peut les forcer à partager !', 38134),
(206186, 'frFR', 'Kléia me protégera pendant notre fuite. Faites ce que vous avez à faire, $n. J\’ai confiance en vous.', '', 38134),
(206185, 'frFR', 'Oh, non ! C\’est affreux… J\’espère que Kléia est saine et sauve !', '', 38134),
(206184, 'frFR', 'Quelle tristesse. Tous ces aspirants… Éridia avait peut-être raison. Je devrais rester ici.', '', 38134),
(206183, 'frFR', 'Quelque chose ne tourne vraiment pas rond, $n…', '', 38134),
(206182, 'frFR', 'Je suis heureux que la main de pureté nous ait réunis. Vous avez l\’air de savoir ce que vous faites, mais je serai là pour vous épauler au besoin. Tout ira bien.\n\n<Pélagos regarde autour de lui nerveusement.>', '', 38134),
(206181, 'frFR', 'Bon sang, la situation doit être grave pour qu\’un disciple quitte ainsi son temple.', '', 38134),
(206180, 'frFR', 'Je savais que vous pouviez y arriver, $n. Nous surmonterons cette crise ensemble !', '', 38134),
(206179, 'frFR', 'Faites de votre mieux ! Athanos a été conçu pour éprouver les aspirants les plus prometteurs.', '', 38134),
(206178, 'frFR', '<Pélagos semble en grande conversation avec Kléia.>', '', 38134),
(206177, 'frFR', 'Vous avez vécu une vie si passionnante ! J\’ai hâte d\’en apprendre plus sur vous. Mais en attendant, vous feriez bien d\’écouter la forgelite. Elle sait mieux que moi comment fonctionne cet endroit.', '', 38134),
(206176, 'frFR', 'Je suis curieux d\’en apprendre plus sur vos exploits, $n. Ils doivent être considérables pour vous avoir $umené:menée; ici. Vu comme vous m\’avez aidé dès votre arrivée, vous deviez être $udestiné:destinée; à nous rejoindre !', '', 38134),
(206175, 'frFR', 'Nous ne pouvons que continuer à aller de l\’avant.', '', 38134),
(206174, 'frFR', '', 'J\’ai l\’impression de profaner un endroit interdit aussi vieux que le Bastion lui-même.\n\nSi notre situation n\’était pas aussi grave, je considérerais comme un miracle d\’avoir le privilège de le contempler.', 38134),
(206173, 'frFR', '', 'Je n\’avais jamais vu une Mnémès se comporter ainsi. C\’est… préoccupant.', 38134),
(206172, 'frFR', '', 'Je protégerai le premier forgelite. Faites ce que vous avez à faire, $n.', 38134),
(206171, 'frFR', '', 'Les désavoués se sont aussi emparés de cet endroit. Tout le Bastion finira-t-il sous leur coupe ?\n\nJ\’espère que Pélagos a été prévenu à temps. Il nous faut découvrir qui est derrière ces attaques !', 38134),
(206170, 'frFR', '', 'Nous devons comprendre pourquoi les désavoués ont attaqué et découvrir qui tire les ficelles !', 38134),
(206169, 'frFR', '', 'Merci d\’avoir protégé Pélagos. Vous retrouver indemnes tous les deux est un vrai soulagement.\n\nJe dois maintenant veiller à sa sécurité. J\’espère donc que vous comprenez que je ne peux vous accompagner.\n\nJe sais cependant que nous nous reverrons.\n\nAllez et servez, $n.', 38134),
(206168, 'frFR', '', '<Kléia semble totalement concentrée sur le rituel.>', 38134),
(206167, 'frFR', '', 'Nous n\’avons pas le temps de fêter notre victoire. Nous avons à faire.', 38134),
(206166, 'frFR', '', 'Félicitations, $n. J\’espère que vos efforts finiront par attirer l\’attention de l\’Archonte.', 38134),
(206165, 'frFR', '', '<Kléia est en grande conversation avec Pélagos.>', 38134),
(206164, 'frFR', '', 'Un jour, j\’accomplirai mon ascension. Mais en attendant, je me ferai un plaisir de vous aider, vous, Pélagos et tous les autres aspirants.', 38134),
(206163, 'frFR', '', 'Personne n\’est envoyé au Bastion par erreur, $n. Ne l\’oubliez jamais.\n\n<Kléia vous sourit.>', 38134),
(206162, 'frFR', '', 'J\’ai hâte de découvrir les exploits qui vous ont $gumené:menée; jusqu\’à nos portes, $n.', 38134),
(206161, 'frFR', '', 'Kalisthène s\’occupe des aspirants qui ne sont pas encore hébergés dans un temple. J\’aurais dû me douter que l\’activation de l\’hôtesse attirerait son attention.\n\nNe vous en faites pas et laissez-moi faire.', 38134),
(206160, 'frFR', '', 'Entrez dans le bassin, $n. Cela vous aidera à vous remettre de votre traversée du voile.', 38134),
(206159, 'frFR', '', 'Vous devriez pouvoir soutirer un peu d\’anima à ces centurions. Nous en manquons cruellement, mais nous devrions réussir à nous faire avec le peu que nous avons.', 38134),
(206157, 'frFR', '', 'Intrusion détectée. Le niveau de menace est actuellement important. Déploiement de massacreurs imminent.', 38134),
(206156, 'frFR', '', 'Intrusion détectée. Le niveau de menace est actuellement modéré.', 38134),
(206155, 'frFR', '', 'Intrusion détectée. Le niveau de menace est actuellement faible.', 38134),
(206154, 'frFR', 'Moi chanter truc joyeux… Truuuc joyeuuux…', '', 38134),
(206153, 'frFR', 'Moi manque pote riquiqui de moi…', '', 38134),
(206152, 'frFR', 'J\’espère encore apporté à grignoter !', '', 38134),
(206151, 'frFR', '$UMeilleur ami:Meilleure amie; !\n\nMoi avoir question : moi bien dans les fêtes ?\n\nParfois petits crocs rigolent, mais moi faire de mon mieux.\n\nMoi vouloir rendre $Umeilleur ami fier:meilleure amie fière; !', '', 38134),
(206150, 'frFR', '<Tête-de-Caillasse s\’essuie l\’œil de sa main libre.>\n\n$UMeilleur copain:Meilleure copine; ! Trop content toi ici !\n\nMoi vouloir aller partout avec $Umeilleur copain:meilleure copine;… Peut-être moi essayer de devenir Sans-Entraves ?', '', 38134),
(206149, 'frFR', '', 'Des plans on m\’a donné et des plans j\’ai gardés.\n\nDes plans ont été faits et de l\’espoir j\’ai donné.\n\nMais tout brûle comme des larmes de feu.\n\nAucun endroit pour se cacher en ce lieu. Ils vous voient. Et s\’ils ne vous voient pas, les autres oui.', 38134),
(206148, 'frFR', '', 'Bonjour, Lumière, ma vieille amie.\n\nEs-tu venue pour me brûler une fois plus ?\n\nPour faire taire mon austère argumentaire par ton autoritaire caractère alors que je réitère ma prière envers ta fière Lumière ?\n\nTes soins me blessent et ton souffle vital me brûle.', 38134),
(206147, 'frFR', '', 'Où les planificateurs sont-ils allés ?\nPartis brûler, souffrir et s\’angoisser,\nIls crient et hurlent pour l\’éternité\npendant que leur cervelle se change en pâtée !\n\nAttendez, ai-je le rythme ou seulement les rimes ?\nJe dois répéter jusqu\’à être unanime.\nJe rêve d\’avoir une lime.\nCe que j\’ai fait était-il vraiment un crime ?', 38134),
(206146, 'frFR', '', 'Ils ont dit que c\’était terminé ! Terminé ! Ou était-ce il y a des lustres ? Ou devait-on attendre des lustres que ce soit terminé ? Ou devaient-ils terminer les lustres ?\n\nLe temps n\’est pas un yoyo !\n\nAttendez. Est-ce que je sais déjà ce qu\’est un yoyo ? Quand sommes-nous ?', 38134),
(206145, 'frFR', 'Hep, Sans-Entraves, p\’tite question : ici, c\’est moi et les gars qu\’on se charge d\’entretenir et de faire pousser Revendreth comme un champignon.\n\nJe m\’demandais… qui fait ce boulot, dans l\’Antre ? À c\’qu\’on dit, vous connaissez bien le bouzin.\n\nDu coup, vous sauriez si les gars qu\’ils font l\’Antre, ils ont droit à des pauses ?', '', 38134),
(206144, 'frFR', '', 'Ensuite, vous pourrez revenir me voir, et nous fêterons cela.', 38134),
(206143, 'frFR', '', 'J\’enverrai mon maître des écuries chercher la mascotte au point de rendez-vous.', 38134),
(206142, 'frFR', '', 'Qu\’ouïs-je ? Ne serait-ce donc pas le pas délicat de $p, pour qui j\’ai tant d\’affection ?', 38134),
(206141, 'frFR', '', 'Vous voyez, Alfryd ? Je vous avais bien dit que ce serait une belle journée. $p est des nôtres.', 38134),
(206140, 'frFR', '', 'Ah, Sans-Entraves. Que me vaut ce plaisir ?', 38134),
(206139, 'frFR', '', 'Oh, Alfryd, auriez-vous l\’amabilité de m\’apporter…', 38134),
(206138, 'frFR', '', '<La comtesse vous étreint avec affection.>\n\nMon adorable $p. Quelles nouvelles de Tombe-Vice ? J\’espère que tout le monde se porte bien.\n\nTransmettez mes amitiés à Théotar et à notre cher prince quand vous les reverrez.\n\nÀ moins que je n\’aie bientôt l\’occasion de retourner à la cour des Braises pour les saluer moi-même.\n\nMais assez parlé d\’eux. Comment vous portez-vous ? J\’ai beaucoup pensé à vous depuis votre dernière visite.', 38134),
(206137, 'frFR', '', 'J\’ai songé à vous, $ucher ami:chère amie;.\n\nDites-moi. Quel est votre plus grand désir, en cet instant ?\n\nPas vos projets de vie ni vos rêves d\’enfant. Les rêves s\’effacent, les projets évoluent.\n\nMais il y a toujours votre instinct, qui guette juste sous la surface. Si jamais vous baissez la garde, il vous emportera.\n\n<La comtesse sourit.>\n\nBien. Ne me dites rien. Gardez votre désir secret. C\’est l\’unique moyen de le protéger.', 38134),
(206136, 'frFR', '', 'Dites-moi, que vient faire quelqu\’un de votre stature dans ma tour aujourd\’hui ?\n\nPeut-être venez-vous me demander un autre service ?\n\n<La comtesse sourit.>\n\nNe demandez pas de l\’aide à n\’importe qui.\n\nBien des âmes aveuglées par la cupidité se sont ainsi retrouvées au service de maîtres peu recommandables.', 38134),
(206133, 'frFR', 'J\’suis pas aussi émotif que vous, les mortels, mais j\’dois reconnaître que je repense souvent au jour où on s\’est rencontrés.\n\nJ\’pensais encore passer une longue journée à me rouler les pouces à Tombe-Fierté… Et d\’un coup, paf, voilà que vous vous êtes $Uramené:ramenée; pour chambouler toute l\’Ombreterre. $ULe:La; Sans-Entraves en personne !\n\nSi j\’avais su qu\’on deviendrait les meilleurs amis du monde ! Quel beau bourbier…', '', 38134),
(206132, 'frFR', 'Hé, l\’$Uami:amie;.\n\nPrévenez-moi si vous avez besoin d\’un coup de main. J\’ai des p\’tits boulots sur le feu, mais j\’trouverai toujours du temps pour vous aider.\n\nAu fait, j\’ai hâte de participer à la prochaine réception d\’la cour des Braises. Théotar m\’a appris deux trois trucs à dire à ces belles gens.', '', 38134),
(206130, 'frFR', 'J\’ai un peu de temps devant moi si vous avez besoin d\’aide.', '', 38134),
(206129, 'frFR', 'Comment ça va, l\’$Uami:amie; ?\n\nFérul et moi, on est de toutes les fêtes ces derniers temps, grâce à vous et à vos p\’tits copains de la cour des Braises.\n\nMais on aura toujours du temps à consacrer à notre $UMEILLEUR ami:MEILLEURE amie;, $p.\n\nVous avez qu\’un mot à dire, et on sera tout à vous !', '', 38134),
(206128, 'frFR', '', 'Les incursions préméditées dans notre royaume doivent être repoussées avec la brutalité la plus extrême. Honorez ceux qui protègent nos secrets.', 38134),
(206127, 'frFR', 'Salut, l\’$Uami:amie; ! Vous entendez pas parler de Férul et moi chez les mondains, en ce moment ?\n\nOn participe pas à toutes les réceptions, mais j\’voudrais pas qu\’ils nous oublient, vous voyez ?\n\nÇa fait du bien de souffler un peu pour visiter la cour de temps à autre.\n\nVous et le prince, vous traitez bien les purotins. Y a pas à dire.', '', 38134),
(206126, 'frFR', '', 'Notre savoir en matière de guerre et d\’engins de destruction est un atout certain, un atout qui ne doit jamais tomber entre les mains des ennemis de l\’Ombreterre.', 38134),
(206125, 'frFR', 'Mais moi j\’l\’aime bien mon vieux chapeau pourri…', '', 38134),
(206124, 'frFR', 'On monte en grade, mon p\’tit Férul. Faut qu\’on commence à se pomponner comme ces nobles crasseux, vois-tu.', '', 38134),
(206122, 'frFR', 'Je pige toujours pas ce que tu veux fiche d\’un couvre-chef pareil, Rondel.', '', 38134),
(206120, 'frFR', 'Férul, tu vois bien que $p a déjà fort à faire. Sauver l\’Ombreterre, c\’est pas rien. Et puis, c\’est $Umon meilleur:ma meilleure; pote à moi !', '', 38134),
(206119, 'frFR', 'Tiens, notre $uami:amie; Sans-Entraves ! Vous n\’auriez pas été de trop lors de mon dernier trajet en calèche.', '', 38134),
(206118, 'frFR', 'Non, il m\’en faut d\’autres ! De toute façon, la mode, t\’y connais rien.', '', 38134),
(206117, 'frFR', 'Crois-moi, Rondel, tu as déjà bien assez de chapeaux comme ça…', '', 38134),
(206116, 'frFR', 'Mate pas comme ça, c\’est pas poli !', '', 38134),
(206115, 'frFR', 'Hé, regarde qui voilà ! Toujours aussi classe, Sans-Entraves !', '', 38134),
(206114, 'frFR', 'Ces scélérats n\’ont aucun respect pour ces salles sacrées !', '', 38134),
(206113, 'frFR', '$p ! Quelle merveilleuse surprise de vous voir !', '', 38134),
(206112, 'frFR', 'J\’espère que c\’est vous, $p…', '', 38134),
(206111, 'frFR', 'Qui va là ? Montrez-vous !', '', 38134),
(206110, 'frFR', 'Ah, mon $Uami:amie;. Je me languissais justement de vous revoir.\n\nJ\’ai l\’impression que cela fait une éternité que nous avons assisté ensemble à la dernière réception de la cour des Braises. J\’imagine que Tombe-Vice est toujours sous votre protection ?\n\nGrâce à votre aide, les Confirmés et moi-même avons sauvé de nombreuses âmes égarées par ici.\n\nVous prenez soin de vos amis, $p. Et vous avez bon cœur.\n\nÀ moins que vous ne fassiez bien semblant !\n\n<Le gardien de la crypte Kassir s\’esclaffe.>\n\nMais je doute que vous soyez $Uvenu:venue; rire de mes boutades.\n\nQu\’est-ce qui vous amène dans cette partie de Revendreth ?', '', 38134),
(206109, 'frFR', 'Ah, $p.\n\n<Le gardien de la crypte relâche sa vigilance et laisse échapper un soupir de soulagement à votre approche.>\n\nVotre présence est la bienvenue en ce nid de félons et de profanateurs.\n\nMes efforts se poursuivent, mais je dois avouer m\’être montré distrait.\n\nMes pensées se tournent régulièrement vers la prochaine réception de la cour des Braises…\n\nJe suis honoré, bien qu\’encore surpris, que vous et le prince Rénathal jugiez toujours bon de m\’y convier.\n\nMais je m\’égare. Que puis-je pour vous, mon $Uami:amie; ?', '', 38134),
(206108, 'frFR', 'On va bien s\’amuser !', '', 38134),
(206107, 'frFR', 'Une dose de voyage, une pincée de façonnage de gorm, un soupçon d\’aventure…', '', 38134),
(206106, 'frFR', 'Alors, on n\’est pas bien ici ? Seuls les champignomanciens troublent le silence par leur travail, comme Cort et vous-même ! Et moi, bien sûr. Tout le monde ici se démène pour assurer le bon fonctionnement de Sylvarden.$b$BC\’est bien aimable à vous de me rendre visite. Rares sont ceux qui pensent à moi ces temps-ci, et en plus, vous avez de la conversation, ce qui ne gâche rien. Bref, je tenais simplement à vous remercier.$b$BMais attention, vous n\’aurez pas de traitement de faveur pour autant ! Le travail, c\’est la santé !', '', 38134),
(206105, 'frFR', '', 'Une réception venthyr… voilà qui est intrigant.', 38134),
(206104, 'frFR', '', 'Pour tout vous avouer, je suis ravie que le marchedroit vous ait $genvoyé:envoyée;.', 38134),
(206103, 'frFR', 'Ça fait des siècles que je suis ici. Je me souviens de ma jeunesse, quand il n\’y avait que la reine et moi. On n\’arrêtait jamais, à l\’époque. Elle travaille dur, elle aussi…$b$bPas comme les vauriens qu\’on croise de nos jours ! Ils exploitent les cercles sans jamais se préoccuper de leurs sentiments ! Il serait grand temps que la reine mette ses sujets au pas !', '', 38134),
(206102, 'frFR', 'Bienvenue dans l\’Anneau, jeune pousse.$b$BVous avez de la chance, car on n\’entre pas ici comme dans un moulin. Seuls mes plus fidèles champignomanciens peuvent accéder à ma demeure, au centre des cercles des Faë.$B$BEt dorénavant, vous en faites partie ! Continuez comme ça et de grandes choses vous arriveront à l\’avenir, je vous le garantis.', '', 38134),
(206101, 'frFR', 'J\’espère qu\’il fait pas trop chaud, quand même…', '', 38134),
(206100, 'frFR', 'Il paraît que la Province des Braises est vachement lumineuse !', '', 38134),
(206099, 'frFR', '', 'La cour des Braises, bien évidemment…', 38134),
(206098, 'frFR', '', 'Très bien. Nous nous reverrons à la cour.', 38134),
(206096, 'frFR', '', 'Personne ne vous a $Usuivi:suivie;, j\’espère ?', 38134),
(206095, 'frFR', '', 'Je vous en prie, Sans-Entraves, dites-moi que vous avez des nouvelles intéressantes.', 38134),
(206088, 'frFR', 'N\’auriez-vous pas un peu trop présumé de votre talent, Sans-Entraves ?', 'N\’auriez-vous pas un peu trop présumé de votre talent, Sans-Entraves ?', 38134),
(206085, 'frFR', 'Vous savez faire étalage de vos talents, Sans-Entraves ! Votre soif de sang n\’est-elle pas encore rassasiée ?', '', 38134),
(206077, 'frFR', 'Bolvar souffre. Les forces des ténèbres nous assaillent.', '', 38134),
(206073, 'frFR', 'Après tant de combats et de guerres acharnés pour défendre la vie, il est… intéressant de découvrir en détail ce qui nous attend dans l\’au-delà.', '', 38134),
(206070, 'frFR', '', 'Les centurions sont des assemblages alimentés par l\’anima. Ils remplissent de nombreuses fonctions pour le Bastion.\n\nIls entraînent nos aspirants, défendent nos terres et accomplissent au besoin différentes besognes. Certains, comme la Mnémès que je suis, sont dépositaires des connaissances des Archives.', 38134),
(206069, 'frFR', '', '<L\’hôtesse émet une série de tintements.>\n\nAh. Les chouettes. Dans votre royaume, ce sont des oiseaux nocturnes, n\’est-ce pas ?\n\n<L\’hôtesse désigne Sika.>\n\nCelle-ci est une régisseuse. Elles sont l\’incarnation de l\’anima du Bastion et n\’aiment rien tant qu\’assister les Kyrians dans leurs tâches quotidiennes.', 38134),
(206060, 'frFR', 'Vous trimballez beaucoup de choses. Ça ne vous fatigue pas ?', 'Vous trimballez beaucoup de choses. Ça ne vous fatigue pas ?', 38134),
(206055, 'frFR', 'Vous signez mon parchemin ?', 'Vous signez mon parchemin ?', 38134),
(206054, 'frFR', 'Je suis votre plus grand admirateur, $n !', 'Je suis votre plus grande admiratrice, $n !', 38134),
(206052, 'frFR', 'Eh bien, vous n\’avez pas mieux à faire que de venir bavarder avec moi ?', '', 38134),
(206050, 'frFR', 'Qu\’est-ce qui disparaît dès que l\’on prononce son nom ?', '', 38134),
(206049, 'frFR', '<Le seigneur Goupil sourit de toutes ses dents, qu\’il a fort nombreuses.>', '', 38134),
(206048, 'frFR', 'Hum… Oui, vous devriez faire l\’affaire.', '', 38134),
(206047, 'frFR', 'Tiens, tiens… Que vois-je ? Un beau spécimen |2 $c. Intéressant.', '', 38134),
(206042, 'frFR', '', 'Est-ce bien vous, Sans-Entraves ? Balmedar m\’a parlé de votre petit subterfuge.\n\nJe ne puis ignorer l\’aide que vous avez apportée à la maison des Rituels. Je vous considère comme notre $u allié:alliée;, aussi je ne soufflerai pas mot de votre déguisement à mes sujets.\n\nProfitez de votre temps ici, « acolyte ».', 38134),
(206041, 'frFR', '', 'Personne ne vous a $uautorisé:autorisée; à vous adresser directement aux margraves.\n\nJe vous conseille de passer votre chemin.', 38134),
(206039, 'frFR', 'Vous portez une arme fort intéressante, acolyte.\n\nVous n\’auriez pas envie de changer de vocation, par hasard ?', '', 38134),
(206037, 'frFR', 'J\’espère que nous allons enfin trouver la paix.', 'J\’espère que nous allons enfin trouver la paix.', 38134),
(206036, 'frFR', 'Nous avons tant perdu. Qu\’avons-nous fait pour mériter cela ?', 'Nous avons tant perdu. Qu\’avons-nous fait pour mériter cela ?', 38134),
(206035, 'frFR', 'Notre nouveau foyer… C\’est si beau.', 'Notre nouveau foyer… C\’est si beau.', 38134),
(206034, 'frFR', 'C\’est si bon d\’être de retour dans la forêt. Merci.', 'C\’est si bon d\’être de retour dans la forêt. Merci.', 38134),
(206033, 'frFR', 'Quel bonheur de voler à nouveau si haut dans les airs !', '', 38134),
(206016, 'frFR', '', 'Je n\’avais encore jamais fait l\’expérience d\’un conflit aussi grave. Les enseignements de la matrone ont contribué à me préparer, pourtant, cela ne m\’empêche pas de parfois me sentir dépassée.\n\nMais que cela reste entre nous. La baronne ne voit pas les esprits timorés d\’un très bon œil.', 38134),
(206014, 'frFR', 'Vous avez su vous montrer utile, $n.\n\nSi vous deviez revenir en Maldraxxus par des voies plus traditionnelles, la maison des Rituels vous accueillerait à bras ouverts.', '', 38134),
(206013, 'frFR', '', 'La dernière fois que je suis venue ici, le Primat siégeait encore.\n\nMaintenant que nous sommes de retour, je ne dois plus laisser sa forge sans surveillance. Il ne me le pardonnerait jamais.', 38134),
(206008, 'frFR', '', 'Afin de vous remercier pour votre aide, je vous donne accès à mon mannequin de couture.\n\nVous pourrez personnaliser votre apparence, à condition d\’avoir les accessoires nécessaires.', 38134),
(206007, 'frFR', '', 'Que puis-je faire pour vous, acolyte ?', 38134),
(205988, 'frFR', 'La discrétion est de mise, $n, ne l\’oubliez pas.\n\nNotre… marché doit rester entre nous.', '', 38134),
(205975, 'frFR', 'Ben, je m\’occupe de ces champignons pour Marasmius. Cette pénurie a vraiment mis ses pauvres racines à rude épreuve.', 'Ben, je m\’occupe de ces champignons pour Marasmius. Cette pénurie a vraiment mis ses pauvres racines à rude épreuve.', 38134),
(205973, 'frFR', 'Bien le bonjour !\n\nDésolé, mais j\’ai pas le temps de bavasser ! Ces champignons vont pas s\’entretenir tout seuls.\n\n<Il s\’esclaffe.>\n\nJe peux vous aider ?', 'Bien le bonjour !\n\nDésolée, mais j\’ai pas le temps de bavasser ! Ces champignons vont pas s\’entretenir tout seuls.\n\n<Zacki s\’esclaffe.>\n\nJe peux vous aider ?', 38134),
(205972, 'frFR', 'Toutes mes excuses, Sans-Entraves, mais vous avez refusé l\’hospitalité de mon sanctum.', '', 38134),
(205971, 'frFR', '', 'Nous sommes venus jusqu\’ici en quête de justice. De vengeance, aussi. Pour répondre à un affront fait à la Lumière elle-même.\n\nNous sommes venus nombreux. Innombrables, même. Le cœur vibrant au diapason de notre sainte colère.\n\nBon nombre sont tombés au combat, mais nous avons eu le temps de déchaîner sur nos ennemis un feu auquel nul ne pouvait résister. D\’ailleurs, les stigmates de notre vengeance demeurent gravés dans le ciel.\n\nMon corps brisé gisait sur le champ de bataille quand les invisibles m\’ont trouvée. Ils l\’ont conservé pour servir de réceptacle au courroux de leur seigneur et maître.\n\nMais j\’ai résisté, envers et contre tout. Je savais que la Lumière avait des projets. Pour moi, mais aussi pour les vôtres, $umortel:mortelle;.', 38134),
(205969, 'frFR', '', 'Ma terre natale me manque terriblement, $umortel:mortelle;. Et pourtant, jamais je ne retrouverai sa chaleur.\n\nSi je revenais, les miens ne me reconnaîtraient pas ; ils ne verraient qu\’une abomination. Une créature meurtrie, pervertie par le sinistre maître de ce royaume.\n\nDorénavant, une seule et unique voie s\’offre à moi. Je dois veiller à ce que la Lumière resplendisse jusqu\’ici. Que justice soit rendue et que la Mort apprenne à rester à sa place.\n\nC\’est pourquoi je reste ici, liée par mon devoir jusqu\’à ce que ma dernière étincelle s\’éteigne.', 38134),
(205968, 'frFR', 'Cela prendra du temps. Bien plus que vos semblables et vous n\’êtes capables de le concevoir.\n\nMais en fin de compte, je verrai mon plus vieil ami, mon plus proche confident, et le plus grand de mes maîtres s\’avancer, fort d\’un savoir nouveau et réinvesti.', '', 38134),
(205965, 'frFR', '', 'J\’aimerais vous raconter ce qui est devenu le jour le plus important de notre longue histoire.', 38134),
(205964, 'frFR', '', 'C\’était un jour ordinaire. Les aspirants vaquaient à leurs occupations. Les régisseurs s\’occupaient d\’eux. Les transcendés allaient et venaient depuis Oribos comme à leur habitude.', 38134),
(205963, 'frFR', '', 'Je m\’en souviens comme si c\’était hier… Le flot régulier d\’âmes en provenance d\’Oribos s\’est brusquement… tari. D\’un instant à l\’autre, nous n\’avions plus une seule âme à accueillir.', 38134),
(205962, 'frFR', '', 'Au début, nous avons cru à une interruption temporaire. Après tout, les nobles âmes dignes du Bastion sont plutôt rares. Mais un jour a passé. Puis deux. Puis dix.', 38134),
(205961, 'frFR', '', 'Notre déclin fut lent et frappé du sceau d\’un optimisme prudent, mais infondé. Cela ne s\’était jamais produit par le passé. J\’en viens à me demander qui nous sommes sans nouveaux aspirants à former.', 38134),
(205960, 'frFR', 'Ainsi que l\’exige la tradition des premiers forgelites, j\’ai inventé quelque chose !', '', 38134),
(205959, 'frFR', 'Nous disposons de nombreux centurions différents. Certains sont forts. D\’autres savent voler. D\’autres encore sont rapides et peuvent être chevauchés.', '', 38134),
(205958, 'frFR', 'J\’ai donc réfléchi à ce qu\’ils ne font pas. Ils parlent, mais ne sont pas intelligents. Ils ne savent rien. Pourquoi ?', '', 38134),
(205957, 'frFR', 'Je suis le premier forgelite, alors j\’ai ajouté un noyau cérébral à une magistrate. Résultat, elle se répète peut-être un peu trop, mais elle peut répondre aux questions ! Pour peu qu\’elles soient… très simples.', '', 38134),
(205956, 'frFR', 'Imaginez un peu les possibilités ! Tout le savoir que nous pourrions lui confier. Je l\’ai baptisée… Mnémès. Génial, non ?', '', 38134),
(205955, 'frFR', '', 'J\’ai récemment rencontré une aspirante fascinante. Elle se nomme Lysonia et nous partageons le même amour de la musique.', 38134),
(205954, 'frFR', '', 'Nous avons joué ensemble des heures durant avant de nous séparer, elle de la harpe et moi de la flûte. Nous avons convenu de nous entraîner ensemble dès que possible.', 38134),
(205953, 'frFR', '', 'Lorsque nous nous sommes rencontrées dans l\’arène, j\’ai eu l\’impression de danser avec elle. Nos mouvements étaient si harmonieux que j\’ai failli oublier que nous étions censées nous entraîner.', 38134),
(205952, 'frFR', '', 'Nous avons ensuite gagné un endroit isolé pour admirer le fort Élyséen et partager nos rêves. Elle espère rejoindre Déva au temple de la Loyauté alors que je pense me consacrer à celui de la Pureté.', 38134),
(205951, 'frFR', '', 'C\’est comme si je la connaissais depuis toujours. Vous pensez qu\’elle acceptera de devenir mon âme liée ?', 38134),
(205950, 'frFR', 'Ce que tu peux être courageux, Ulul. Je suis fier de toi.', '', 38134),
(205949, 'frFR', 'Oui ! J\’ai hâte ! J\’avais justement besoin d\’un moment de répit avant de reprendre le combat pour défendre Sylvarden.', '', 38134),
(205948, 'frFR', 'Tu as entendu ça, Ulul ? On est invités à une fête !', '', 38134),
(205946, 'frFR', '2…', '2…', 38134),
(205945, 'frFR', 'Nos règles nous intiment de répéter ces épreuves jusqu\’à ce que nous les maîtrisions.', 'Nos règles nous intiment de répéter ces épreuves jusqu\’à ce que nous les maîtrisions.', 38134),
(205944, 'frFR', 'Nous allons entraver leur chasse ! La maison Irebrande peut compter sur la maison Vicecroc !', 'Nous allons entraver leur chasse ! La maison Irebrande peut compter sur la maison Vicecroc !', 38134),
(205942, 'frFR', 'JE N\’AVAIS PAS TERMINÉ, $uVOLEUR:VOLEUSE; !', 'JE N\’AVAIS PAS TERMINÉ, $uVOLEUR:VOLEUSE; !', 38134),
(205941, 'frFR', '1…', '1…', 38134),
(205940, 'frFR', 'Je vais compter jusqu\’à cinq.', 'Je vais compter jusqu\’à cinq.', 38134),
(205939, 'frFR', 'Qu\’est-ce que vous faites ? Descendez immédiatement de Balthazar !', 'Qu\’est-ce que vous faites ? Descendez immédiatement de Balthazar !', 38134),
(205938, 'frFR', 'Kevin prendra un autre raccourci pour vous rejoindre auprès de nos nouveaux amis. Vous n\’aurez qu\’à les guider jusqu\’à lui. Il se chargera du reste !', '', 38134),
(205919, 'frFR', '', 'Mais je suis certaine que ce genre de plaisanterie plairait à Théotar. Hmm.', 38134),
(205918, 'frFR', '', 'Non. C\’est trop direct.', 38134),
(205913, 'frFR', 'Alors, quelle sera votre monnaie d\’échange, aujourd\’hui, $p ?\n\nL\’argent ou la mort ?', '', 38134),
(205876, 'frFR', 'Venez à moi, âme égarée. J\’ai à vous parler.', 'Venez à moi, âme égarée. J\’ai à vous parler.', 38134),
(205862, 'frFR', 'Ici, vous pouvez accéder aux services ayant trait à vos biens matériels. C\’est un concept relativement nouveau pour nous, aussi les négociants ont-ils offert de vous assister dans ces démarches.', '', 38134),
(205849, 'frFR', '<Le grand maître Vole vous examine.>\n\nVous ferez l\’affaire.\n\n<Il hurle à ceux qui l\’entourent.>\n\nDe la viande fraîche pour le théâtre de la Souffrance !', '', 38134),
(205848, 'frFR', 'Quelle performance splendide, formidable ! Les compliments pleuvent déjà.$B$BJe vous remercie, très $ucher:chère;. Il est si rare que je rencontre de véritables mélomanes !', '', 38134),
(205825, 'frFR', 'J\’ai passé ma vie à aider de jeunes Elfes ambitieux à trouver leur voie.\n\nJe m\’apprête aujourd\’hui à vivre ma propre aventure et je me demande… Ont-ils ressenti la même chose au moment de faire leurs premiers pas dans leur nouveau bosquet ?', '', 38134),
(205822, 'frFR', '', 'Je vous suis reconnaissante de m\’avoir sauvée de ce sinistre endroit.\n\nMais je ne peux oublier les souffrances que la Horde a infligées à mon peuple.\n\nJustice doit être faite.', 38134),
(205821, 'frFR', '', 'Notre peuple paie un lourd tribut à cette guerre, plus que tous les autres.\n\nLes responsables ne peuvent rester impunis.\n\nJ\’espère que vous serez à nos côtés quand viendra l\’heure de notre vengeance.', 38134),
(205812, 'frFR', 'Merci pour votre aide.\n\nSi vous comptez retourner dans l\’Antre, pouvez-vous vous enquérir de quelqu\’un ?\n\nElle se nomme Delaryn.', '', 38134),
(205806, 'frFR', '', 'Élune ne nous a pas oubliés. Votre intervention en est la preuve.\n\nJe me demande si nous avons encore un rôle à jouer… un devoir à accomplir.\n\nIl faut nous montrer patients.', 38134),
(205805, 'frFR', '', 'Notre intention est d\’écraser les ambitions des Nécro-seigneurs. Si vous souhaitez une nouvelle fois vous rallier à notre bannière, ensemble, nous rassemblerons les factions disparates en une armée capable de défier le destin lui-même.', 38134),
(205804, 'frFR', 'Le chemin de la rédemption de Revendreth sera long, mais le sombre prince de Tombe-Vice est prêt à s\’y engager. Vous nous avez menés sur les premières étapes du périple. Il reste à arracher le royaume aux griffes du Maître et de ses partisans.', '', 38134),
(205803, 'frFR', '', 'C\’est par la haine que tout a commencé.\n\nJ\’espère que c\’est dans la justice que tout s\’achèvera.', 38134),
(205801, 'frFR', '', 'En fermant les yeux, je pensais bien les rouvrir face à Élune.\n\nMerci de ne pas nous avoir abandonnés.', 38134),
(205800, 'frFR', '', 'Comme vous avez pu en témoigner, les bosquets de Sylvarden sont à l\’agonie. Le désespoir de notre reine croît chaque fois qu\’un arbre céleste meurt. La cour de la Nuit et sa souveraine ont reconnu votre puissance. Aidez-nous à rétablir l\’équilibre en Sylvarden.', 38134),
(205798, 'frFR', 'Nos nobles Kyrians subissent les conséquences tragiques des trahisons dont vous avez été témoin au Bastion. Les troupes désavouées menacent d\’anéantir notre royaume tout entier. Si nous souhaitons reconquérir le Bastion et sauver l\’Ombreterre, votre aide nous sera extrêmement précieuse.', '', 38134),
(205797, 'frFR', 'C\’est un défi qui exige une grande mobilité ! Et tâchez de repérer le tourbillon d\’Alderyn, c\’est dans son œil que vous êtes en sécurité.', '', 38134),
(205795, 'frFR', 'Attention aux lances. Au bout de trois, elles vous clouent au sol, alors tout ce qui peut vous aider à vous dégager sera d\’une aide précieuse !', '', 38134),
(205794, 'frFR', 'Ouh, ça brille ! Avec un truc pareil sur le crâne, on va te nommer prince des purotins.', '', 38134),
(205793, 'frFR', 'Ah bah v\’là un chapeau classe. Vise les cailloux.', '', 38134),
(205791, 'frFR', 'Avez-vous jamais vu une forêt si splendide ? Ces arbres !\n\nMerci de m\’avoir tiré de cet horrible endroit et de m\’avoir permis de voir ça de mes yeux.', '', 38134),
(205790, 'frFR', 'Eh, c\’est drôlement chic, hein ?', '', 38134),
(205789, 'frFR', 'Tu vas vraiment porter ça à la cour ? Je vois. D\’accord. Fais comme tu veux, Rondel. Tant qu\’on peut y aller.', '', 38134),
(205788, 'frFR', 'Ah ! Regardez-moi. Tout beau, tout rose et pointu.', '', 38134),
(205785, 'frFR', '', 'Mais qu\’est-ce que vous faites là !\n\nBon, je peux peut-être vous aider un peu avec un parchemin pour vous renvoyer sur la partie principale de l\’île. C\’est que nous en sommes loin, ici.', 38134),
(205783, 'frFR', '', 'Oh ! Vous devez être $gun:une:r; $r, hein ? Je n\’en avais plus vu depuis un moment.\n\nVous venez essayer d\’échapper à toute cette horrible nécromancie ? Je ne sais pas trop si vous respirez bien sous l\’eau, vous autres.\n\nTout le monde attend que la tempête passe. Je dirais pas plus de cinq, dix ans. Pour moi, ça ne fait pas trop.\n\nPour vous, par contre…', 38134),
(205781, 'frFR', '', 'Bien sûr que je le connais. Oui, c\’est Gwynceirw en chair et en os ! Sans oublier son fidèle compagnon, Jonquille.$b$bTout ce que je vous demande, c\’est de ne pas faire d\’ennuis. La dernière fois qu\’une bande de spriggans sont venus chercher la bagarre ici, ils sont allés le provoquer. J\’ai mis des heures à tout nettoyer… Qui peut s\’amuser à coller des bouts d\’intestin au plafond, hein ? Et il venait d\’où, tout ce sang ?', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205779, 'frFR', '', 'Oui, faites juste attention à ne rien casser. À chaque fois, Marasmius se plaint que les harpes s\’accrochent à ses pousses. Ne me demandez pas comment c\’est possible.', 38134),
(205777, 'frFR', 'Ouais, t\’es déguisé. Personne saura que c\’est toi, Rondel.', '', 38134),
(205776, 'frFR', 'Oooh ! Hé, Férul, regarde ça ! Mon chapeau, il est trop chaud ! Il brûle, d\’ailleurs.', '', 38134),
(205771, 'frFR', 'Tous nos efforts convergent en ce moment.', '', 38134),
(205757, 'frFR', 'Quel est le point commun entre un régisseur et un verre d\’eau ? Si on le chauffe trop, hibou !', '', 38134),
(205756, 'frFR', 'Vous savez pourquoi j\’aime tant les régisseurs ? Parce qu\’ils sont super chouette !', '', 38134),
(205750, 'frFR', 'Vous savez pourquoi les Kyrians sont toujours les derniers à s\’esclaffer ? Parce que rira bien, Kyrian le dernier !', '', 38134),
(205748, 'frFR', '', 'Les régisseurs vouent leur vie au service non seulement des Kyrians, mais de tout le Bastion.\n\nTous méritent amplement qu\’on les gâte un peu. Dans cette retraite, ils trouvent un instant de répit et même de détente.', 38134),
(205746, 'frFR', 'Quand vous voudrez.', '', 38134),
(205743, 'frFR', 'Vous cherchez une place ?', 'Vous cherchez une place ?', 38134),
(205742, 'frFR', 'Hou, j\’ai la tête qui tourne. J\’ai peut-être un peu forcé…', 'Hou, j\’ai la tête qui tourne. J\’ai peut-être un peu forcé…', 38134),
(205741, 'frFR', 'Se reposer, c\’est bien. Ça retape les muscles !', 'Se reposer, c\’est bien. Ça retape les muscles !', 38134),
(205738, 'frFR', 'Besoin d\’aide sur tout ce qui concerne la magie ?', '', 38134),
(205737, 'frFR', 'Vous avez une peau magnifique. Donnez-la-moi quand vous n\’en aurez plus besoin.', '', 38134),
(205734, 'frFR', 'Pas de plaies à recoudre ?', '', 38134),
(205733, 'frFR', 'Je n\’ai jamais abandonné l\’espoir.', 'Je n\’ai jamais abandonné l\’espoir.', 38134),
(205732, 'frFR', 'Il n\’y a donc pas de fin à ces tourments ?', 'Il n\’y a donc pas de fin à ces tourments ?', 38134),
(205731, 'frFR', 'J\’ai prié pour que la déesse nous entende…', 'J\’ai prié pour que la déesse nous entende…', 38134),
(205730, 'frFR', 'C\’est enfin terminé ?', 'C\’est enfin terminé ?', 38134),
(205726, 'frFR', '', 'Rien ne pourra me briser.', 38134),
(205725, 'frFR', 'Sombrevallon me manque.', '', 38134),
(205722, 'frFR', 'Où est Delaryn ?', '', 38134),
(205720, 'frFR', 'Tout a brûlé… même les jeunes pousses…', '', 38134),
(205717, 'frFR', 'Alors vous voilà $uchampignomancien:champignomancienne; honoraire, hein ? Marasmius semble vraiment vous apprécier. Bienvenue dans l\’équipe !$b$bEnfin, l\’équipe… c\’était juste moi. Je suis heureux de vous avoir ! En tant que $uchampignomancien:champignomancienne;, vous aurez accès aux chemins de Marasmius, ainsi qu\’à quelques jolis avantages et à notre équipement exclusif. En nous aidant, vous obtiendrez des marques à échanger auprès de moi.', '', 38134),
(205716, 'frFR', 'L\’Anneau était une sorte de point névralgique d\’où il était possible de se rendre presque partout dans le royaume.$b$BMais au fil du temps et à cause de la pénurie, même Marasmius a perdu certains chemins de vue. Peut-être qu\’avec assez d\’anima, ils pourraient être retrouvés…$b$bPour l\’heure, les champignomanciens ont le devoir de veiller sur le vénérable. Les racines de Sylvarden repousseront, plus vigoureuses que jamais, et il survivra.', '', 38134),
(205715, 'frFR', 'Bienvenue à l\’Anneau. Certains nomment cet endroit le centre de Sylvarden, ou le berceau de Marasmius. Lui seul sait ce qu\’il en est.', '', 38134),
(205714, 'frFR', '', 'Il faisait si sombre…\n\nJ\’ai fait tout ce que je pouvais pour eux, hein ?', 38134),
(205711, 'frFR', 'Nous avons aidé la princesse Talanji des Zandalari à s\’évader de la prison de Hurlevent.\n\nSon navire lest en route pour Zandalar, mais ces chiens de l\’Alliance sont à sa poursuite.\n\nIl nous faut la puissance de la marine zandalarienne si nous voulons remporter cette guerre. Nous ne pouvons permettre que l\’Alliance s\’empare à nouveau de la princesse.\n\nNous emprunterons la voie des airs pour aller les aider, puis nous ferons en sorte que les Zandalari rejoignent la Horde.\n\nVenez.', '', 38134),
(205709, 'frFR', '', 'Un grand feu, et puis…\n\nJ\’ai chanté…\n\nPar la déesse, mais qu\’est-ce qui m\’arrive ?', 38134),
(205704, 'frFR', 'Ça, c\’est la belle vie, ça. On pourrait y prendre goût même, ouais.', '', 38134),
(205699, 'frFR', 'Vous PAS PRENDRE BOUGIE ! Nooon !', '', 38134),
(205698, 'frFR', 'Quel dommage que nous soyons à court d\’ennemis à massacrer.\n\nMais j\’attends de pied ferme ceux qui viendront les remplacer.', '', 38134),
(205687, 'frFR', 'Qui a faim ?', '', 38134),
(205674, 'frFR', 'Nous pleurons ce que nous avons perdu, qu\’il s\’agisse d\’une possession ou d\’un avenir entravé. Posséder crée des attentes. S\’en détacher, c\’est trouver la paix.', '', 38134),
(205673, 'frFR', 'En se libérant des attentes, en abandonnant la peur et la douleur de la perte, on accepte les choses telles qu\’elles sont.', '', 38134),
(205672, 'frFR', 'Il est douloureux de voir que ce qui fut n\’est plus. On se rappelle la joie, l\’émerveillement, l\’attachement.', '', 38134),
(205671, 'frFR', 'Nous baignons dans la peur de ne plus avoir ce qui fut possédé, ou de ne pas vivre ce que nous pensions vivre.', '', 38134),
(205639, 'frFR', '', 'Bonjour et bienvenue au Troglobar !$b$bQu\’est-ce que je vous sers ?', 38134),
(205627, 'frFR', '', 'Nous serons prêts à défendre la cour des Braises. À bientôt là-bas, $n.', 38134),
(205591, 'frFR', 'À la lisière de l\’existence, il peut être facile de se noyer dans le deuil. Hum, et si je méditais sur la portée du deuil ? Au fait, avez-vous vu mes focalisateurs ? Ils devraient être par ici…', '', 38134),
(205590, 'frFR', 'C\’est l\’endroit idéal pour se reposer dans le calme et le silence ! Cette âme pourrait avoir besoin de vous.', '', 38134),
(205587, 'frFR', '', 'C\’était l\’un des pires combats auxquels j\’ai assisté dans toute ma carrière, recrue. Vous avez raté l\’épreuve. Retournez vous entraîner.', 38134),
(205586, 'frFR', '', 'C\’était du beau travail. Recrue, vous avez réussi l\’épreuve.', 38134),
(205579, 'frFR', 'Vous avez l\’air de vous y connaître en artisanat !\n\nSi vous avez le savoir-faire nécessaire, je peux vous apprendre à créer des réceptacles pour contenir le pouvoir des runes.', '', 38134),
(205568, 'frFR', 'Quel métier vous intéresse ?', 'Quel métier vous intéresse ?', 38134),
(205561, 'frFR', '', 'Je n\’ai aucune confiance en lui, mais c\’est peut-être notre seule chance de savoir ce qu\’ils mijotent.', 38134),
(205560, 'frFR', '', 'Sans-Entraves ! Cette liche a une idée pour nous faire entrer dans la maison des Rituels.', 38134),
(205559, 'frFR', 'Et pourtant, vous n\’avez guère le choix. À moins que vous préfériez un assaut suicide ?', '', 38134),
(205558, 'frFR', '', 'Votre plan ne me dit rien qui vaille, liche.', 38134),
(205552, 'frFR', 'Des phalènes sélènes ! Ils ont dû éclore il y a peu. Allez en attraper quelques-uns et rapportez-les-moi.', '', 38134),
(205550, 'frFR', 'Permettez-moi de vous prévenir : je suis un duelliste aguerri.', 'Permettez-moi de vous prévenir : je suis une duelliste aguerrie.', 38134),
(205534, 'frFR', 'Un nouveau but ? Quel but pourrait bien avoir un dieu dépourvu de monde ?\n\nEnfin, je suppose que je ne peux pas rester là à rien faire. Ce n\’est pas mon genre.\n\nMais je dois comprendre comment fonctionne ce monde-ci… l\’Ombreterre. Je perçois de nombreux royaumes, mais quatre seulement jouent un rôle particulier.\n\nApportez-moi quelque chose issu de chacun de ces mondes. Quelque chose d\’organique. J\’appartiens à une race capable de saisir bien des choses rien qu\’au toucher.', '', 38134),
(205532, 'frFR', 'S\’il était encore là, je le saurais. Je peux sentir son absence.\n\nQuelle tristesse… Qu\’est-ce qu\’un dieu sans monde sur lequel veiller ?\n\nOù dois-je aller, à présent ? Que dois-je faire ?', '', 38134),
(205530, 'frFR', 'Sylvarden ? Alors nous sommes en Ombreterre ? Oui, je connais ce lieu, ne prenez pas cet air surpris. J\’étais un dieu, quand même.\n\nJe connais les réponses à bien des questions. Pourtant, j\’ignore comment je suis arrivé ici et pourquoi mon monde a disparu.\n\nJ\’imagine que je me suis éteint en même temps que lui. Mais me voilà revenu et pas lui.', '', 38134),
(205528, 'frFR', 'Alors, j\’attends des explications ! Que fais-je en ces lieux ?', '', 38134),
(205522, 'frFR', 'Ah, ah, une légère pointe par là. Tout doux.', 'Ah, ah, une légère pointe par là. Tout doux.', 38134),
(205521, 'frFR', 'Ooooh. Quelle couleur éclatante. Voyez ?', 'Ooooh. Quelle couleur éclatante. Voyez ?', 38134),
(205520, 'frFR', 'Regardez avec quelle aisance il s\’étale à la surface. Telle une feuille dans la brise.', 'Regardez avec quelle aisance il s\’étale à la surface. Telle une feuille dans la brise.', 38134),
(205519, 'frFR', 'C\’est terminé. Puisse cet objet vous apporter autant de joie que sa confection m\’en a procuré.', 'C\’est terminé. Puisse cet objet vous apporter autant de joie que sa confection m\’en a procuré.', 38134),
(205516, 'frFR', 'Ensemble… comme une famille.', 'Ensemble… comme une famille.', 38134),
(205505, 'frFR', '<Adrestès acquiesce d\’un signe de tête.>', '', 38134),
(205498, 'frFR', '', 'Notre travail ne fait que commencer.', 38134),
(205497, 'frFR', '', 'Bienvenue dans la dernière forteresse d\’honneur de Maldraxxus. Votre arrivée a presque été aussi chaotique que la mienne.', 38134),
(205496, 'frFR', 'C\’est sûrement ça ! Apportez-moi cette lame !', '', 38134),
(205481, 'frFR', '', 'Nous lancerons à l\’attaque à votre signal, Sans-Entraves !', 38134),
(205472, 'frFR', 'C\’est il y a bien longtemps que Qadarin et moi avions pris les armes pour défendre notre royaume aux côtés d\’autres champions. La guerre ne m\’a pas manqué, mais l\’amitié qui unit les combattants, si.\n\n<Il laisse échapper un bref éclat de rire.>\n\nEt le spectacle des corrupteurs traînés dans la poussière aussi, je dois bien le dire.', '', 38134),
(205471, 'frFR', '', 'Je ne veux pas imaginer ce qui serait arrivé si les Drust avaient pu s\’imposer ici.\n\nJe vois qu\’il reste fort à faire en Sylvarden, mais cette menace-là est écartée.\n\nJe continue à me méfier et je vous garderai à l\’œil, mais nous sommes d\’accord sur un point : ce royaume a besoin de nous.', 38134),
(205470, 'frFR', 'Quel pigment fantastique ! Je n\’en avais encore jamais vu de pareil. Je dois me mettre au travail.', 'Quel pigment fantastique ! Je n\’en avais encore jamais vu de pareil. Je dois me mettre au travail.', 38134),
(205468, 'frFR', '', 'Je ne veux pas imaginer ce qui serait arrivé si les Drust avaient pu s\’imposer ici.\n\nJe vois qu\’il reste fort à faire en Sylvarden, mais cette menace-là est écartée.\n\nTyrande, elle, est toujours seule dans Tourment. La recherche d\’autres guerriers de la Nuit continue et Ysera et Thiernax semblent y croire, mais moi… Je m\’inquiète pour elle.', 38134),
(205467, 'frFR', '', 'Les Drust ont beau être vaincus, Sylvarden a besoin de ses protecteurs. Je vais me battre pour l\’Hallali comme Ara\’lon avant moi, et veiller sur les bosquets.\n\nJ\’en tire l\’espoir qu\’un jour, le royaume sera pleinement guéri.', 38134),
(205466, 'frFR', 'Tout cela était bien exaltant. Quel soulagement d\’être débarrassés des Drust, leur influence sur nos bosquets et nos graines sauvages était réellement néfaste ! J\’espère que la reine pourra tout ramener à la normale. Ou tout rendre moins anormal, en tout cas. Merci, $n, vous avez fait plus que vous ne le soupçonnez pour nous.', '', 38134),
(205465, 'frFR', '', 'Vous nous êtes $uvenu en inconnu:venue en inconnue;, l\’enfant d\’un monde déchiré par la guerre. À présent, nous avons pris les armes côte à côte, en alliés. Je salue votre maîtrise de la chasse, et ma lance vous est acquise.', 38134),
(205463, 'frFR', 'Même les plus grands chasseurs tombent un jour.\n\nNous pleurons la mort d\’Ara\’lon et des autres, mais il faut surtout célébrer leurs vies. Et notre victoire !\n\nVous avez montré que Sylvarden sait se défendre, et la légende de vos exploits retentira partout dans la forêt, $n.\n\nBravo, réellement !', '', 38134),
(205459, 'frFR', '', 'Dès votre arrivée, j\’ai su que vous aviez quelque chose de spécial, $n. Et j\’avais raison ! Vous êtes quelqu\’un… d\’intéressant.\n\n<L\’expression de dame Baie-de-Lune se fait plus sérieuse.>\n\nEt de l\’intéressant, il va nous en falloir de plus en plus. Les Drust ont été chassés, mais ils ne faisaient qu\’accentuer un problème déjà présent. Le mal persiste hélas, mais je sais que nous réussirons à l\’arrêter !', 38134),
(205458, 'frFR', '', 'Ce royaume a beaucoup sacrifié pour prolonger mon existence. Je suis heureuse d\’avoir pu aider à le protéger, de rendre comme on m\’a donné.\n\nL\’emprise des Drust est rompue, mais il faut rester sur nos gardes. C\’est toute l\’Ombreterre… il y a quelque chose qui ne va pas. Cette victoire laisse bien des questions en suspens.\n\nJe sais que je vais avoir besoin de vous pour relever ces défis. Soyez $uprêt:prête;.', 38134),
(205457, 'frFR', 'Bon travail. Rompez !', 'Bon travail. Rompez !', 38134),
(205442, 'frFR', 'C\’est l\’influence de l\’Antre qui a dû les rendre fous ! Il faut vite intervenir !', '', 38134),
(205441, 'frFR', 'Oh, je suis navré. Aurais-je oublié de préciser qu\’il faudrait vous livrer à quelques menus travaux manuels ?', '', 38134),
(205440, 'frFR', 'Vous allez devoir rapporter ce corps dans le cercle rituel.', '', 38134),
(205437, 'frFR', 'Voilà une forme très… intéressante.', 'Voilà une forme très… intéressante.', 38134),
(205436, 'frFR', 'Comment servirez-vous Sylvarden ?', 'Comment servirez-vous Sylvarden ?', 38134),
(205435, 'frFR', 'Quelle forme allez-vous prendre ?', 'Quelle forme allez-vous prendre ?', 38134),
(205434, 'frFR', 'Que la Nuit vous bénisse.', 'Que la Nuit vous bénisse.', 38134),
(205427, 'frFR', '', 'Mais si vous échouez, c\’est le vôtre qui viendra s\’y ajouter !', 38134),
(205426, 'frFR', '', 'Ensemble, nous allons rayer quelques noms de ma liste…', 38134),
(205425, 'frFR', '', 'Utilisez ce cristal pour projeter un court instant mon image dans le plan des vivants.', 38134),
(205424, 'frFR', '', 'Certains des combattants me suivront, même si vous n\’aurez pas besoin d\’eux tant que je serai dans les parages !', 38134),
(205423, 'frFR', 'Draven ose me traiter d\’arrogant, moi ? Pourtant, si cette bourrique de vive-pierre avait bien voulu prendre au sérieux mes avertissements concernant Kel\’Thuzad, nous aurions pu anéantir cette liche ! À présent, notre ennemi s\’est rallié au Geôlier.\n\n<La rage qui anime le regard du prince semble s\’évaporer.>\n\nMes torts m\’apparaissent désormais clairement. Peut-être parviendrai-je un jour à les expier, et mériterai-je de nouveau votre confiance.\n\nMais, je troquerais volontiers cette rédemption contre la vengeance. Au nom de notre peuple. Au nom de celles et ceux qui ont perdu la vie à cause de Kel\’Thuzad…\n\n<Kael\’thas soupire.>\n\nÀ cause de mon incapacité à les protéger.\n\nLaissez-moi, $p. J\’ai à réfléchir.', '', 38134),
(205422, 'frFR', 'Draven ose me traiter d\’arrogant, moi ? Pourtant, si cette bourrique de vive-pierre avait bien voulu prendre au sérieux mes avertissements concernant Kel\’Thuzad, nous aurions pu anéantir cette liche ! À présent, notre ennemi s\’est rallié au Geôlier.\n\n<La rage qui anime le regard de l\’elfe de sang semble s\’évaporer.>\n\nMes torts m\’apparaissent désormais clairement. Peut-être parviendrai-je un jour à les expier.\n\nMais, je troquerais volontiers cette rédemption contre la vengeance. Au nom de mon peuple. Au nom de celles et ceux qui ont perdu la vie à cause de Kel\’Thuzad…\n\n<Kael\’thas soupire.>\n\nÀ cause de mon incapacité à les protéger.', '', 38134),
(205421, 'frFR', 'Tandis que moi, votre prince, je demeure confiné ici, en Revendreth, condamné à souffrir pour quelques… décisions contestables, Kel\’Thuzad erre en toute liberté. Ce monstre a pratiquement pris le contrôle de Maldraxxus !\n\n<Kael\’thas fronce les sourcils un moment, puis il baisse les yeux.>\n\nNeuf sur dix, $p. Occis par Arthas Menethil pour animer cette liche. Notre noble royaume presque entièrement anéanti.\n\nLa survie m\’a souvent donné l\’impression d\’être un fardeau plus qu\’une bénédiction. J\’ai fait tout ce qui était en mon pouvoir pour préserver la force de mon peuple.\n\nMais la voie que j\’ai choisie était peut-être…\n\n<Kael\’thas sombre dans le silence.>', '', 38134),
(205420, 'frFR', 'Tandis que je demeure confiné ici, en Revendreth, condamné à souffrir pour quelques… décisions contestables, Kel\’Thuzad erre en toute liberté. Ce monstre a pratiquement pris le contrôle de Maldraxxus !\n\n<Kael\’thas fronce les sourcils un moment, puis il baisse les yeux.>\n\nNeuf sur dix, $p. Occis par Arthas Menethil pour animer cette liche. Un noble royaume presque entièrement anéanti.\n\nVous saignez toujours, n\’est-ce pas ? Pour chaque goutte que vous sauvez, laissez-en neuf tomber et tremper la terre.\n\nDites-moi ce que cela fait à votre cœur, $p. Car je peux vous dire précisément ce que cela a fait au mien.', '', 38134),
(205419, 'frFR', 'Ce traitement est scandaleux ! Je suis votre prince, votre seigneur légitime, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous ne l\’êtes.', '', 38134),
(205418, 'frFR', 'Je suppose que vous vous délectez de me voir ainsi. Vous m\’avez peut-être vaincu en combat, mais vous ne serez jamais mon $uégal:égale;.\n\nJe suis un prince de noble naissance, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous ne l\’êtes.', '', 38134),
(205417, 'frFR', 'Ce traitement est scandaleux ! Je suis un prince de la plus noble extraction, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous.', '', 38134),
(205416, 'frFR', 'Vous, là ! Approchez, misérable ! J\’exige que vous me libériez sur-le-champ !\n\n<Kael\’thas lutte malgré ses entraves.>\n\nCe traitement est scandaleux ! Je suis votre prince, votre seigneur légitime, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous !\n\nAccusatrice ! Libérez-moi ! J\’ai un royaume à venger. Vous m\’entendez ? ACCUSATRICE !\n\n<Kael\’thas se débat de plus belle.>', '', 38134),
(205415, 'frFR', 'Oui ! Oui, vous, là ! J\’exige que vous me libériez sur-le-champ !\n\n<Kael\’thas lutte malgré ses entraves.>\n\nJe suppose que vous vous délectez de me voir ainsi ! Vous m\’avez peut-être vaincu, mais nous n\’aurons jamais la même stature !\n\nJe suis un prince de la plus noble extraction, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous !\n\nAccusatrice ! Libérez-moi ! J\’ai un royaume à venger. Vous m\’entendez ? ACCUSATRICE !\n\n<Kael\’thas se remet à s\’agiter dans un élan de résistance.>', '', 38134),
(205414, 'frFR', 'Oui ! Oui, vous, là ! J\’exige que vous me libériez sur-le-champ !\n\n<Kael\’thas lutte malgré ses entraves.>\n\nCe traitement est scandaleux ! Je suis un prince de la plus noble extraction, pourtant cette Arbitre insensible a osé me reléguer à un lugubre royaume de pierre grise plein d\’esprits étroits.\n\nAucun de ces Venthyrs n\’est digne de juger les choix que j\’ai faits de mon vivant. Pas plus que vous !\n\nAccusatrice ! Libérez-moi ! J\’ai un royaume à venger. Vous m\’entendez ? ACCUSATRICE !\n\n<Kael\’thas se remet à s\’agiter dans un élan de résistance.>', '', 38134),
(205412, 'frFR', 'Ce n\’est pas le bon moment pour discuter.\n\nJe ne suis pas aveugle, vous savez. J\’ai bien vu que vous essayez déjà de vous attirer les grâces de quelqu\’un d\’autre.', '', 38134),
(205411, 'frFR', 'J\’ai bien vu que vous tentiez d\’impressionner quelqu\’un d\’autre.\n\nJe n\’ai rien à vous dire pour l\’instant.', '', 38134),
(205410, 'frFR', '', 'Vous soutenez déjà un autre camp.\n\nCroyiez-vous que je ne le remarquerais pas ? Nous avons des yeux partout.', 38134),
(205409, 'frFR', '', 'Il me semble que vous essayez déjà d\’obtenir les faveurs d\’une autre congrégation.\n\nNe perdez donc pas votre temps avec moi.', 38134),
(205408, 'frFR', 'C\’est vous que j\’ai $uaperçu:aperçue; avec Baie-de-Lune ?\n\nJ\’espère que vous n\’écoutez pas tous leurs contes à dormir debout sur la renaissance.', '', 38134),
(205407, 'frFR', 'D\’où vient cet acharnement à fréquenter les Kyrians ?\n\nServir les Venthyrs serait beaucoup mieux pour vous.', '', 38134),
(205406, 'frFR', 'Que faites-vous toujours $ufourré:fourrée; avec Draka ?\n\nVotre temps serait mieux employé à aider les Venthyrs comme vous en avez fait le serment.', '', 38134),
(205405, 'frFR', '', 'Vous devriez vous méfier du général Draven.\n\nLes Venthyrs ne sont pas dignes de confiance. Vous le savez certainement déjà, mais je n\’ai pas pu m\’empêcher de remarquer que vous étiez souvent en leur compagnie, ces derniers temps.', 38134),
(205404, 'frFR', '', 'Je vois que vous gravitez toujours autour de Draka.\n\nJe sais que vous êtes originaires du même royaume, mais vous avez de nouveaux amis, maintenant : nous.', 38134),
(205401, 'frFR', '', 'Vous et les Kyrians m\’avez l\’air extrêmement complices.\n\nSeriez-vous déjà $Ulas:lasse; de servir notre reine ? Nous avons encore tant de choses à accomplir ensemble.', 38134),
(205400, 'frFR', '', 'De quoi parliez-vous avec Draven ?\n\nJe ne crois pas avoir forgé d\’alliance avec les Venthyrs. Je ne leur fais pas confiance.', 38134),
(205398, 'frFR', '', 'Je ne savais pas que la nature vous passionnait autant, mais vous avez l\’air de beaucoup vous amuser avec les Faë nocturnes, ces derniers temps.', 38134),
(205397, 'frFR', '', 'Vous semblez particulièrement complice avec les Kyrians, ces derniers temps.\n\nJe suppose que vous restez tout de même fidèle aux nécro-seigneurs, $n ? Nous avons encore beaucoup à faire.\n\nJe ne voudrais pas que notre relation soit ternie par un acte de trahison.', 38134),
(205394, 'frFR', 'J\’ai cru comprendre que Draka et vous êtes originaires du même royaume. \n\nEst-ce la raison pour laquelle vous discutiez avec elle, il y a peu ?', '', 38134),
(205390, 'frFR', 'Vous discutiez avec le général Draven, tout à l\’heure. \n\nJe compte sur vous pour me communiquer les informations d\’importance dont il vous a fait part.', '', 38134),
(205389, 'frFR', 'Vous avez fait affaire avec dame Baie-de-Lune, je me trompe ?\n\nJ\’ignorais que nous collaborions avec les Faë nocturnes en ce moment.', '', 38134),
(205388, 'frFR', '', 'Vous n\’avez pas froid aux yeux, $n. Ça, je vous l\’accorde.\n\nÊtes-vous ici pour me narguer ? Me provoquer pour jauger ma réaction, maintenant que vous m\’avez trahie ?\n\nVous feriez mieux de partir. Je n\’ai franchement rien à vous dire pour le moment.', 38134),
(205387, 'frFR', '', 'Vous nous avez de nouveau prouvé votre puissance.\n\nMais ce n\’est pas tout ce que nous attendons des nôtres. La loyauté est également de mise, et vous avez déjà manqué à votre parole par le passé.\n\nPartez. Nous allons discuter de votre sort au sein de la congrégation.\n\nRevenez me voir plus tard. Nous aurons peut-être pris notre décision.', 38134),
(205380, 'frFR', 'Vous avez bien de la chance que je ne vous casse pas en deux !\n\nSi nous n\’étions pas en terrain neutre, je serais très tenté.\n\nPartez d\’ici le temps que ma colère s\’apaise.', '', 38134),
(205378, 'frFR', 'Vous avez fait les premiers pas pour vous attirer notre faveur.\n\nMais votre cas demande plus ample réflexion, aussi revenez me voir plus tard.\n\nPeut-être nous jugerons vous digne de rejoindre les Venthyrs.', '', 38134),
(205370, 'frFR', 'Quelles modifications souhaitez-vous apporter à votre mantelet et à votre capuche ?', '', 38134),
(205368, 'frFR', 'Quel accessoire souhaitez-vous modifier ?', '', 38134),
(205366, 'frFR', 'Quelle couleur souhaitez-vous appliquer à votre déguisement ?', '', 38134),
(205365, 'frFR', 'Quelle partie de votre déguisement souhaitez-vous modifier ?', '', 38134),
(205364, 'frFR', '', 'Vous osez revenir ici et me parler comme si de rien n\’était ?\n\nVotre perfidie n\’est plus à démontrer, $n.\n\nPeut-être vous pardonnerai-je un jour. Peut-être. Mais pas aujourd\’hui.', 38134),
(205363, 'frFR', 'Morette Lamode se sert de ce mannequin pour créer de nouvelles tenues de parade destinées aux acolytes de la maison des Rituels.\n\nVous pouvez l\’utiliser pour modifier l\’apparence de votre déguisement d\’acolyte nécrotique.', '', 38134),
(205362, 'frFR', 'Vraiment, $n ? \n\nVous avez un sacré culot de venir me voir. Je n\’ai rien à vous dire, ni à vous, ni aux personnes dans votre genre.', '', 38134),
(205360, 'frFR', 'Par ici, Sans-Entraves.', '', 38134),
(205359, 'frFR', '', 'Vous avez fait le premier pas pour regagner la grâce de notre reine.\n\nNous devons tâcher de déterminer si votre retour s\’inscrit au sein du cycle.\n\nRevenez me voir plus tard. Nous serons peut-être parvenus à une conclusion. Je serais presque désolée de devoir vous faire attendre, mais comprenez que votre trahison passée nous a rendus plus circonspects.', 38134),
(205356, 'frFR', 'Bien le bonjour, $n.\n\nNous n\’avons pas encore fini d\’étudier votre candidature.\n\nRevenez me voir dans un moment, peut-être aurai-je une bonne nouvelle à vous annoncer.', '', 38134),
(205345, 'frFR', 'Tharon\’ja voit tout ! Les efforts des mortels ne mettront pas fin à la dynastie éternelle !', '', 38134),
(205343, 'frFR', 'C\’est intolérable ! Cette vermine n\’a rien à faire là ! Vous devez leur donner une bonne leçon !', '', 38134),
(205341, 'frFR', 'Les secrets des profondeurs resteront scellés.', '', 38134),
(205335, 'frFR', 'R\’cousu veut jouer !', '', 38134),
(205334, 'frFR', 'C\’était stupide de venir.', '', 38134),
(205330, 'frFR', 'Plus tripes ! Plus sang ! Plus manger !', '', 38134),
(205328, 'frFR', 'Le frisson glacé qui vous parcourt est annonciateur de votre perte !', '', 38134),
(205323, 'frFR', 'C\’était son heure. Malheureusement, la seule éternité qui l\’attendait était celle de l\’Antre. Ce n\’est pas à nous qu\’il revient d\’en décider, mais à l\’Arbitre.', 'C\’était son heure. Malheureusement, la seule éternité qui l\’attendait était celle de l\’Antre. Ce n\’est pas à nous qu\’il revient d\’en décider, mais à l\’Arbitre.', 38134),
(205322, 'frFR', 'Sa mort est désarmante, mais notre devoir n\’est pas de juger le bien-fondé de son sort.', 'Sa mort est désarmante, mais notre devoir n\’est pas de juger le bien-fondé de son sort.', 38134),
(205321, 'frFR', '', 'L\’arbre est en feu ! Je… n\’arrive plus… à respirer…', 38134),
(205320, 'frFR', 'Aussi tragique que soit son sort, l\’heure était venue pour elle de gagner l\’éternité d\’Oribos.', 'Aussi tragique que soit son sort, l\’heure était venue pour elle de gagner l\’éternité d\’Oribos.', 38134),
(205319, 'frFR', 'Se sacrifier pour sa famille est un geste noble, mais il ne justifie pas un retour au monde des vivants.', 'Se sacrifier pour sa famille est un geste noble, mais il ne justifie pas un retour au monde des vivants.', 38134),
(205318, 'frFR', 'Je disputais un duel contre mon frère qui s\’emporte pour un rien, et je n\’ai pas pu me résoudre à lui faire du mal ! Alors, j\’ai dégainé un peu trop lentement… et j\’en ai payé le prix.', 'Je disputais un duel contre mon frère qui s\’emporte pour un rien, et je n\’ai pas pu me résoudre à lui faire du mal ! Alors, j\’ai dégainé un peu trop lentement… et j\’en ai payé le prix.', 38134),
(205317, 'frFR', 'Esprit de la nature, elle a été envoyée en Sylvarden pour y aider les âmes. Tel était son destin.', 'Esprit de la nature, elle a été envoyée en Sylvarden pour y aider les âmes. Tel était son destin.', 38134),
(205316, 'frFR', 'Si certaines âmes liées à la nature peuvent y retourner après une halte en Sylvarden, d\’autres restent pour l\’éternité.', 'Si certaines âmes liées à la nature peuvent y retourner après une halte en Sylvarden, d\’autres restent pour l\’éternité.', 38134),
(205315, 'frFR', '', 'Le bosquet est en danger ! Les petites ne pourront jamais s\’échapper sans moi !', 38134),
(205314, 'frFR', 'Les démons n\’ont pas leur place sur l\’Ombreterre si l\’on excepte quelques rares combattants de Maldraxxus.', 'Les démons n\’ont pas leur place sur l\’Ombreterre si l\’on excepte quelques rares combattants de Maldraxxus.', 38134),
(205313, 'frFR', 'Oh, vous devez faire erreur ! Ce carton est adressé au marchedroit Adrestès.', '', 38134),
(205311, 'frFR', 'Oui. Les démons doivent retourner au Néant distordu d\’où ils venaient.', 'Oui. Les démons doivent retourner au Néant distordu d\’où ils venaient.', 38134),
(205309, 'frFR', '', 'Hmm ! Je n\’avais plus senti un tel tranchant depuis bien longtemps.', 38134),
(205303, 'frFR', 'Les Val\’kyrs ne sont pas de notre monde, mais les âmes doivent répondre lorsqu\’elles les rappellent.', 'Les Val\’kyrs ne sont pas de notre monde, mais les âmes doivent répondre lorsqu\’elles les rappellent.', 38134),
(205302, 'frFR', 'Bien. Cette âme nous a trouvés à temps. Même les Morts-vivants ne peuvent échapper éternellement à la mort.', 'Bien. Cette âme nous a trouvés à temps. Même les Morts-vivants ne peuvent échapper éternellement à la mort.', 38134),
(205301, 'frFR', 'J\’entends le terrible appel des Val\’kyrs. Je vous en supplie, laissez-moi reposer en paix ! Pitié !', '', 38134),
(205294, 'frFR', 'Vous avez des rubis infusés ? Ce vicéquin peut vous emmener à Sombrehavre.\n\nMais évitez de lâcher les rênes si vous voulez pas tomber en cours de route.', '', 38134),
(205291, 'frFR', 'Vous devez comprendre que le marchedroit ne changera pas sa routine de gaieté de cœur. Cela lui ferait pourtant du bien. Laissez-moi régler ça.', '', 38134),
(205285, 'frFR', 'Oh, je ne vais pas faire la fine bouche.\n\nJ\’apporterai mes propres modifications ultérieurement.', '', 38134),
(205284, 'frFR', 'Pour une poignée de rubis infusés, ce vicéquin vous permettra de faire le tour des remparts nord.\n\nY a une belle vue de là-haut, mais c\’est plein de brutes et de sales bêtes impertinentes. Lâchez pas les rênes avant d\’être $Usûr:sûre; d\’être $Uarrivé:arrivée; au bon endroit.', '', 38134),
(205282, 'frFR', 'Oui. Même si la renvoyer aurait pu infléchir le sort de tout un village, son heure était venue.', 'Oui. Même si la renvoyer aurait pu infléchir le sort de tout un village, son heure était venue.', 38134),
(205281, 'frFR', 'Même si l\’inondation va coûter bien des vies, notre devoir n\’est pas d\’intervenir dans les affaires des mortels.', 'Même si l\’inondation va coûter bien des vies, notre devoir n\’est pas d\’intervenir dans les affaires des mortels.', 38134),
(205280, 'frFR', 'Aïe ! Mon Explositron 5000 a tout explositré le barrage qui est juste au-dessus de mon labo ! Tous mes cob… Tous mes chers animaux de compagnie sont en danger !', 'Aïe ! Mon Explositron 5000 a tout explositré le barrage qui est juste au-dessus de mon labo ! Tous mes cob… Tous mes chers animaux de compagnie sont en danger !', 38134),
(205279, 'frFR', 'Absolument. L\’âme peut bien implorer, nous ne devons pas altérer son destin.', 'Absolument. L\’âme peut bien implorer, nous ne devons pas altérer son destin.', 38134),
(205278, 'frFR', 'Tant que son phylactère est intact, nous devons le renvoyer.', 'Tant que son phylactère est intact, nous devons le renvoyer.', 38134),
(205275, 'frFR', 'Je me suis retrouvé à cette croisée des chemins bien des fois. Je refuse d\’être lié plus longtemps ! Laissez-moi trouver la paix dans la mort !', 'Je me suis retrouvée à cette croisée des chemins bien des fois. Je refuse d\’être liée plus longtemps ! Laissez-moi trouver la paix dans la mort !', 38134),
(205272, 'frFR', 'Si jamais vous avez une poignée de rubis infusés à lui donner, ce vicéquin vous emmènera jusqu\’à la Gargote, du côté ouest du château. \n\nMais ne le lâchez pas, à moins que vous n\’ayez envie d\’être débarqué en cours de route.', '', 38134),
(205268, 'frFR', 'Sans-Entraves, réunissez quelques convives afin qu\’ils conservent une partie de ce formidable présage en souvenir.', '', 38134),
(205267, 'frFR', 'Notre cour a reçu un heureux présage : de l\’anima se concentre au-dessus de nous !', '', 38134),
(205251, 'frFR', 'Cet être est formé de la magie des terres de Feu. Ce n\’est pas à Oribos que l\’attend son jugement.', 'Cet être est formé de la magie des terres de Feu. Ce n\’est pas à Oribos que l\’attend son jugement.', 38134),
(205250, 'frFR', 'Bien ! Les créatures d\’autres plans d\’existence doivent y retourner à leur mort. Leur destin n\’est pas l\’Ombreterre.', 'Bien ! Les créatures d\’autres plans d\’existence doivent y retourner à leur mort. Leur destin n\’est pas l\’Ombreterre.', 38134),
(205249, 'frFR', 'Les terres de Feu sont envahies ! Je dois vite retourner auprès des miens !', '', 38134),
(205247, 'frFR', 'Même si elle porte la magie de l\’Ombreterre, la source en était le royaume des vivants. Il fallait la renvoyer.', 'Même si elle porte la magie de l\’Ombreterre, la source en était le royaume des vivants. Il fallait la renvoyer.', 38134),
(205246, 'frFR', 'C\’est ainsi. Elle était déjà morte une fois, mais devait encore répondre à l\’appel de son maître avant de revenir à nous.', 'C\’est ainsi. Elle était déjà morte une fois, mais devait encore répondre à l\’appel de son maître avant de revenir à nous.', 38134),
(205245, 'frFR', 'Le roi-liche m\’appelle. Je reviens le servir !', 'Le roi-liche m\’appelle. Je reviens le servir !', 38134),
(205243, 'frFR', 'Pfff, les élémentaires de poussière ! S\’ils continuent, ils vont tout emporter et les graines vont mourir !', '', 38134),
(205241, 'frFR', 'Oui. Elle est morte avec noblesse et a gagné sa place sur l\’Ombreterre.', 'Oui. Elle est morte avec noblesse et a gagné sa place sur l\’Ombreterre.', 38134),
(205240, 'frFR', 'Aussi douloureuse que soit la décision, nous ne devons pas la soustraire à son éternité.', 'Aussi douloureuse que soit la décision, nous ne devons pas la soustraire à son éternité.', 38134),
(205239, 'frFR', 'Je vais traduire. Cette créature a péri en protégeant son maître d\’une meute de loups et souhaite retourner auprès de lui pour s\’assurer qu\’il va bien.', 'Je vais traduire. Cette créature a péri en protégeant son maître d\’une meute de loups et souhaite retourner auprès de lui pour s\’assurer qu\’il va bien.', 38134),
(205238, 'frFR', 'Ouaf ! Ouarf ouarf ! Grrr…', '', 38134),
(205237, 'frFR', 'Oui. Il est mort avec l\’honneur d\’un guerrier et est parti se couvrir de gloire en Maldraxxus.', 'Oui. Il est mort avec l\’honneur d\’un guerrier et est parti se couvrir de gloire en Maldraxxus.', 38134),
(205236, 'frFR', 'Non, hélas. Même si d\’autres ont péri sans lui, c\’est dans le royaume des mortels qu\’il a accompli son destin.', 'Non, hélas. Même si d\’autres ont péri sans lui, c\’est dans le royaume des mortels qu\’il a accompli son destin.', 38134),
(205235, 'frFR', 'La bataille fait rage dans ma ville. Je dois y retourner pour protéger mes petits des bêtes féroces !', 'La bataille fait rage dans ma ville. Je dois y retourner pour protéger mes petits des bêtes féroces !', 38134),
(205234, 'frFR', 'Non. Nous ne devons pas emporter des âmes liées à d\’autres forces cosmiques.', 'Non. Nous ne devons pas emporter des âmes liées à d\’autres forces cosmiques.', 38134),
(205233, 'frFR', 'Oui. Les âmes possédant un lien assez puissant avec d\’autres forces cosmiques doivent être autorisées à repartir.', 'Oui. Les âmes possédant un lien assez puissant avec d\’autres forces cosmiques doivent être autorisées à repartir.', 38134),
(205232, 'frFR', '<L\’être émet une cacophonie de carillons anxieux.>', '', 38134),
(205230, 'frFR', 'Pour une poignée de rubis infusés, ce vicéquin vous emmènera jusqu\’à la porte Antique. Mais ne le lâchez pas, à moins que vous n\’ayez envie d\’être $udébarqué:débarquée; en cours de route.', '', 38134),
(205229, 'frFR', 'La basse manque de clarté. La grosse caisse est trop molle !\n\nVous appelez ça une scène ? C\’est une grotte ! Il n\’y a aucune convivialité et l\’acoustique est miteuse.\n\nLes Sangliers noirs ne joueront pas tant que le son n\’est pas parfait. Je suis peut-être un mort-vivant, mais mon ouïe est excellente, figurez-vous !', '', 38134),
(205222, 'frFR', 'Oh, bonjour ! Vous avez appris de nouvelles recettes ?', '', 38134),
(205221, 'frFR', 'C\’est un plaisir de vous voir. Et si on parlait nourriture ?', '', 38134),
(205220, 'frFR', 'Mon nom, c\’est Régisseur, rapport à mon rôle. Marrant, non ?', '', 38134),
(205219, 'frFR', 'Bonjour, bonjour ! Vous venez échanger des recettes ?', '', 38134),
(205214, 'frFR', '', 'Je n\’entraîne pas les voleurs Assassinat.', 38134),
(205212, 'frFR', '', 'La cupidité du haut percepteur est sans limites.\n\nNul n\’agirait ainsi en Maldraxxus. Les Venthyrs n\’ont-ils pas pour mission première d\’aider les âmes à expier leurs fautes ? L\’idée qu\’il ait pu forger une alliance avec des ressortissants de mon royaume me révulse encore plus.\n\nNous devons mettre un terme à cette folie.', 38134),
(205205, 'frFR', '', 'Laissez-moi tranquille.', 38134),
(205202, 'frFR', '', 'Vous avez progressé depuis votre première passe. Nous allons vous trouver un défi plus intéressant.', 38134),
(205201, 'frFR', '', 'Ceux qui ont trahi les Yeux mourront !', 38134),
(205197, 'frFR', '<Arkélios glousse.>\n\nPas du tout. Lorsque tout part à vau-l\’eau autour de moi…\n\nC\’est que l\’heure d\’une nouvelle aventure a sonné.', '', 38134),
(205194, 'frFR', 'Ma foi… en marchant !\n\nJ\’ai trouvé ce joli coin à l\‘ombre d\’un arbre, l\’endroit parfait où méditer.\n\nJ\’ai dû passer un peu plus de temps en contemplation que je ne le pensais, puisque j\’ai bien l\’impression d\’avoir été emporté loin du reste de Sylvarden.', '', 38134),
(205192, 'frFR', 'Je suis un voyageur.\n\nDepuis une éternité, je marche et j\’arpente les royaumes de la vie, et désormais de la mort. Au fil du temps, je me remémore mon existence et réfléchis à bien des choses.', '', 38134),
(205190, 'frFR', 'Salutations, âme vagabonde.', '', 38134),
(205189, 'frFR', 'Ce pouvoir est à moi… je veux dire, il est à nous ! Me voilà enfin libéré de tes sermons incessants sur la protection de cette salle.', '', 38134),
(205188, 'frFR', 'Non ! Allez-vous-en. Arrêtez ! Personne ne doit entrer là-dedans.', '', 38134),
(205187, 'frFR', 'Alors, qu\’est-ce que ça fait ? Hein ? Hein ? Tu vas ouvrir la porte, hein ?', '', 38134),
(205186, 'frFR', 'Aaah. Ça m\’a <burp> tellement manqué. Encore ! Il nous en faut encore.', '', 38134),
(205185, 'frFR', 'Que diriez-vous d\’un petit… coup de pouce ?\n\nUn peu plus de puissance dans vos coups, une once de crépitement pour vos sorts.\n\nJ\’ai ce qu\’il vous faut, si vous avez les moyens.', '', 38134),
(205184, 'frFR', 'Nooon ! Le Maître sera furieux. Vous allez le payer cher !', '', 38134),
(205182, 'frFR', 'Les puissants phalynx ne courberont jamais l\’échine.', '', 38134),
(205180, 'frFR', 'Il vous parle ? C\’est un traître. Ne l\’écoutez pas.', '', 38134),
(205179, 'frFR', 'Pff ! Ignorez cet idiot et son caquetage ! On va entrer, je vous le dis. Allons trouver quelque chose à drainer !', '', 38134),
(205178, 'frFR', 'Oooh, voilà qui fera l\’affaire. Il va m\’en f… euh… il va nous en falloir beaucoup plus.', '', 38134),
(205173, 'frFR', 'L\’âme Sans-Entraves à la rescousse ! Je parie que vous vous en sortirez haut la main.', '', 38134),
(205171, 'frFR', '', 'L\’un de mes éclaireurs en a découvert davantage sur les déplacements de Lysonia.', 38134),
(205170, 'frFR', 'Sa chute me donnera-t-elle jamais la paix ? Serai-je un jour libéré de lui ? Peut-être… Avec le temps, peut-être.', '', 38134),
(205169, 'frFR', '', 'Fais-le pour Pélagos, Kléia… Allez. Pour lui.', 38134),
(205168, 'frFR', 'Mais je ne suis pas seul. Kléia est avec moi ! Comme elle le dit, je m\’améliore un peu à chaque fois. Un jour, j\’y arriverai ! Un jour…', '', 38134),
(205167, 'frFR', '', 'Aujourd\’hui, je vois clairement le chemin qui a toujours été le mien. Je ne le perdrai pas de vue.', 38134),
(205166, 'frFR', 'J\’ai essayé de lui montrer tout ce qu\’elle y avait gagné, mais au final… J\’en viens à me demander s\’il n\’y avait pas un autre moyen. Je n\’ai que des questions à te confier, cher parchemin, et aucune réponse.', '', 38134),
(205155, 'frFR', '<Il s\’est bien défendu, mais il n\’était visiblement pas préparé à devoir se battre.>', '', 38134),
(205153, 'frFR', '<Ce négociant semble être mort au combat.>', '', 38134),
(205148, 'frFR', 'Superbe ! Ça devrait suffire. Maintenant, on va pouvoir passer ce braillard. Allons-y !', '', 38134),
(205147, 'frFR', '<Ce banc de neige n\’a offert que peu de protection à ce négociant.>', '', 38134),
(205146, 'frFR', 'Vous ne trouvez rien.', 'Vous ne trouvez rien.', 38134),
(205145, 'frFR', '<Ce négociant était presque parvenu à rejoindre le portail avant de succomber à ses blessures.\n\nIl ne semble pas avoir découvert quoi que ce soit d\’intéressant.>', '', 38134),
(205144, 'frFR', 'Je vais informer l\’Archonte.', '', 38134),
(205135, 'frFR', 'Hé ! Ne touchez pas à ça. Remettez-le à sa place. Au voleur !', '', 38134),
(205134, 'frFR', 'Arrière ! Vous n\’avez pas le droit d\’être ici.', '', 38134),
(205132, 'frFR', 'Cela ressemble à une armure abandonnée.', '', 38134),
(205129, 'frFR', 'Ahem !\n\nN\’allez pas imaginer que je vais vous laisser passer. Vos pitoyables supplications me laissent parfaitement indifférent.', '', 38134),
(205099, 'frFR', 'On m\’a dit que quelqu\’un était venu de Tombe-Vice. Votre présence nous honore.\n\nEssayez de ne pas gêner les gardiens. Ils tentent de réparer les dégâts.', 'On m\’a dit que quelqu\’un était venu de Tombe-Vice. Votre présence nous honore.\n\nEssayez de ne pas gêner les gardiens. Ils tentent de réparer les dégâts.', 38134),
(205098, 'frFR', 'Le quartier des catacombes était jadis un modèle de discipline en Revendreth. Ce n\’est plus le cas.\n\nAujourd\’hui, nous nous efforçons de restaurer sa gloire passée. Nous avons commencé notre travail de restauration ici. Hélas, le reste du quartier est trop dangereux pour le moment.\n\nVeuillez m\’excuser, je dois retourner à mes devoirs.', 'Le quartier des catacombes était jadis un modèle de discipline en Revendreth. Ce n\’est plus le cas.\n\nAujourd\’hui, nous nous efforçons de restaurer sa gloire passée. Nous avons commencé notre travail de restauration ici. Hélas, le reste du quartier est trop dangereux pour le moment.\n\nVeuillez m\’excuser, je dois retourner à mes devoirs.', 38134),
(205097, 'frFR', '', 'Il avait une telle foi. Jamais le doute ne venait voiler son regard.\n\nJ\’ai vu son monde voler en éclats de mes propres yeux.\n\nJe…\n\nÀ quoi bon continuer, après ça ?', 38134),
(205095, 'frFR', '', 'Nous savions que certaines âmes étaient envoyées dans l\’Antre par erreur.\n\nAchillon était si fort. Il m\’inspirait confiance, sa loyauté n\’a jamais vacillé et il était toujours plein d\’espoir.\n\nMais en arrivant à Oribos… En voyant toutes ces âmes…\n\nIl s\’est effondré sous mes yeux.', 38134),
(205093, 'frFR', '', '<Pallessa observe fixement l\’eau.>', 38134),
(205091, 'frFR', 'Vos alliés et vous avez ma reconnaissance éternelle pour vos efforts, Sans-Entraves.', '', 38134),
(205088, 'frFR', '', 'Je ne… tiendrai… pas…', 38134),
(205087, 'frFR', '', 'Arggh…', 38134),
(205086, 'frFR', '', 'La puissance du Geôlier ne cesse de croître et c\’est toute l\’Ombreterre qui court un effroyable danger. Ne relâchez pas votre vigilance, $p. Les royaumes auront bientôt besoin de vous.', 38134),
(205079, 'frFR', '', 'Au revoir, $uami:amie; des Kyrians ! Bonne chance !', 38134),
(205078, 'frFR', '', 'Puissent vos âmes liées et vous saisir les leçons de patience, d\’unité et de coopération que nous prodiguons.', 38134),
(205077, 'frFR', '', 'Comme il vous plaira, $n. Mes racines sont profondes.', 38134),
(205073, 'frFR', '', 'Les Faë possédés ont réussi à me coincer ! Je ne savais plus quoi faire !', 38134),
(205069, 'frFR', 'Pourquoi vous avez fait ça ? Je m\’amusais tant !', '', 38134),
(205063, 'frFR', '', 'Mon pauvre Gommeux est paniqué ! Rien ne parvient à le calmer.', 38134),
(205062, 'frFR', '', 'Aidez mes alliées à se tirer d\’embarras. Assurez-leur qu\’elles recevront à leur retour la jouvence dont elles ont tant besoin.', 38134),
(205059, 'frFR', '', 'Thran\’tiok a caché son phylactère hors de la maison des Rituels lorsqu\’il a été découvert. Vous devez le trouver… pour moi comme pour vous.', 38134),
(205056, 'frFR', 'Qu\’est-ce qui vous tourmente ?', '', 38134),
(205052, 'frFR', '', 'Pas cette fois, Thran\’tiok !', 38134),
(205051, 'frFR', '', 'Attendez-vous à souffrir mille morts. Je vais vous briser et vous forcer à me révéler lequel de mes ennemis vous servez…', 38134),
(205050, 'frFR', '', 'Un autre traître est tombé dans mon piège !', 38134),
(204889, 'frFR', 'Couloirs Distordus', 'Couloirs Distordus', 38134),
(204888, 'frFR', 'Étages supérieurs', 'Étages supérieurs', 38134),
(204883, 'frFR', 'Interstice Cœur-Algide', 'Interstice Cœur-Algide', 38134),
(204882, 'frFR', 'Forges des Âmes', 'Forges des Âmes', 38134),
(204881, 'frFR', 'Chambres aux Fractures', 'Chambres aux Fractures', 38134),
(204879, 'frFR', 'Je peux vous assister dans toutes les tâches que vous voudrez me confier.\n\nQue puis-je pour vous aujourd\’hui ?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204876, 'frFR', 'C\’est une bonne couleur, ça. C\’est bien, pour un majordome.', '', 38134),
(204875, 'frFR', 'Quel pigment vous voulez jeter dans la tourbière pour votre majordome ?\n\nOn pourra changer la couleur plus tard, mais c\’est un peu pénible pour lui, alors réfléchissez bien.', '', 38134),
(204874, 'frFR', 'Vous voulez faire un nouveau purotin, c\’est ça ?\n\nFaites pareil que moi. Je suis le meilleur !', '', 38134),
(204873, 'frFR', 'Je sais pas pourquoi, je me sens fleur bleue, aujourd\’hui…', '', 38134),
(204872, 'frFR', 'Les nouveaux purotins vont pas se faire tout seuls…', '', 38134),
(204870, 'frFR', 'Une graine vidée de son anima.', 'Une graine vidée de son anima.', 38134),
(204868, 'frFR', 'Le surplomb de Veilleroc me manque. \n\nOu plutôt, ce qu\’il était autrefois… Je n\’apprécie pas ce que notre nouveau « moissonneur » en a fait.', '', 38134),
(204867, 'frFR', 'Nous avons tous deux été chassés ici pour avoir « accaparé » de l\’anima au surplomb de Veilleroc.\n\nSi vous voulez mon avis, le terme est un peu fort quand on ne fait que garder le strict nécessaire pour survivre.\n\nCe haut percepteur est aussi corrompu que Denathrius.', '', 38134),
(204865, 'frFR', 'La province des Braises est une contrée impitoyable. J\’aimerais pouvoir partir.\n\nMais on doit rester dans cet abri car la Lumière nous brûle la peau. La douleur est insupportable.', '', 38134),
(204863, 'frFR', 'L\’horizon semble soudain moins sombre. Je vous remercie, $R.', '', 38134),
(204862, 'frFR', 'Nous avons réussi, cher amour ! La Lumière n\’est plus un frein à notre survie.', '', 38134),
(204852, 'frFR', 'Nous pourrons peut-être le former !', '', 38134),
(204851, 'frFR', 'Hélas, nous allons devoir nous contenter d\’un purotin.', '', 38134),
(204850, 'frFR', 'N\’oubliez pas : les meilleurs majordomes sont intelligents, affables et dotés de compétences sociales exceptionnelles.', '', 38134),
(204840, 'frFR', 'Ça faisait douze mille ans que je me faisais bouffer par mon patron. Une éternité à subir ses crocs.\n\nJe sais, j\’ai aucune marque ; ils savent s\’y prendre pour que ça se voie pas. Croquer son serviteur, ça fait mauvais genre.\n\nEt puis un jour, vous avez débarqué et vous avez éliminé le patron. J\’imagine que vous étiez pas au courant de qui c\’était, mais je vous suis quand même reconnaissant. V\’là pour vous remercier.', '', 38134),
(204837, 'frFR', '$UMortel:Mortelle;…', '', 38134),
(204835, 'frFR', 'Votre maître est très exigeant, vous savez. Cela va prendre du temps de rassembler ses composants.\n\nCela étant dit, ça irait plus vite si vous m\’aidiez à me procurer les ingrédients les plus rares.', '', 38134),
(204834, 'frFR', 'Que puis-je pour vous, acolyte ?', '', 38134),
(204827, 'frFR', '', 'Retrouvons-nous en Revendreth pour remonter à l\’instigateur de cette mascarade… et pour y mettre un terme.', 38134),
(204823, 'frFR', 'Je suis complètement déshydraté à force de parler. Or, il faut penser à bien s\’arroser le gosier quand on a affaire à tous ces combattants.', '', 38134),
(204814, 'frFR', 'Bon. Au moins vous reconnaissez la folie de vos actes.\n\nMais je ne peux pas vous laisser revenir comme ça, l\’honneur exige un geste de plus.\n\nVous devez refaire vos preuves, et j\’ai justement une mission qui serait conforme à vos talents.\n\nÊtes-vous $uprêt:prête; à expier pour redevenir venthyr ?', '', 38134),
(204813, 'frFR', 'Vous osez vous montrer devant moi avec une telle question ?!\n\nSi nous n\’étions pas en terrain neutre, je pourrais être tenté de vous pulvériser.\n\nQu\’est-ce qui vous fait penser que nous pourrions accepter votre retour ?', '', 38134),
(204812, 'frFR', 'Votre aplomb me rassure.\n\nEt nous savons déjà quelle force est la vôtre.\n\nEh bien alors, puisque vous êtes $usûr:sûre; de vous, faites vœu de loyauté envers les Venthyrs.', '', 38134),
(204811, 'frFR', 'Ce n\’est pas une décision à prendre à la légère, $n.\n\nVous devez être tout à fait $usûr:sûre; de vouloir nous rejoindre.\n\nTenez-vous vraiment à suivre ce chemin ?', '', 38134),
(204810, 'frFR', 'Si je ne me trompe pas, vous êtes déjà membre d\’une autre congrégation.\n\nJe comprends que nos talents puissent rendre jaloux, mais voulez-vous vraiment trahir vos alliés actuels ?', '', 38134),
(204809, 'frFR', 'Comment puis-je vous aider ?', '', 38134),
(204802, 'frFR', 'Vous êtes l\’acolyte de la baronne, $n. Moi, je ne vous dois rien.\n\nGardez ça à l\’esprit et limitez au maximum nos interactions.', '', 38134),
(204799, 'frFR', 'La pierre d\’accès ! C\’est notre porte de sortie.', '', 38134),
(204798, 'frFR', 'Vous aviez échappé au Geôlier… Oui. Ça redevient plus clair, ça y est.', '', 38134),
(204795, 'frFR', 'L\’Antre… Je me rappelle. Ça paraît si lointain.', '', 38134),
(204793, 'frFR', 'Je vais les distribuer à nos troupes. Maintenant, parlons de la sécurité…', '', 38134),
(204784, 'frFR', '', 'Et maintenant, vous désirez rejoindre à nouveau nos rangs ?\n\nJe crains de ne pouvoir vous laisser entrer si facilement dans le Cœur de la forêt.\n\nVous allez devoir nous prouver votre loyauté.\n\nPar chance, j\’ai une tâche à vous confier qui constituerait un test idéal.\n\nÊtes-vous $uprêt:prête; à démontrer votre valeur ?', 38134),
(204783, 'frFR', '', 'Ah, notre plus grande déception est de retour.\n\nJe ne pensais pas que vous auriez assez d\’aplomb pour oser vous montrer par ici.\n\nN\’allez pas croire que nous avons oublié votre désertion.', 38134),
(204782, 'frFR', '', 'Vous paraissez $udéterminé:déterminée;.\n\nQui suis-je pour douter de vous, après tout ? Vous avez déjà servi fidèlement notre reine. Je suppose que vous le ferez encore.\n\nDévouerez-vous l\’entièreté de votre âme au service des Faë nocturnes et de leur reine bien aimée ?', 38134),
(204781, 'frFR', 'J\’aurais préféré autre chose… mais je ferai avec.', 'J\’aurais préféré autre chose… mais je ferai avec.', 38134),
(204780, 'frFR', '', 'En êtes-vous bien $ucertain:certaine;, $n ?\n\nUne telle décision peut engendrer de grands déséquilibres dans le cycle de la vie. Vous devez être absolument $usûr:sûre; de votre choix avant que nous n\’en discutions davantage.', 38134),
(204778, 'frFR', '', 'Je vois.\n\nEh bien, nous sommes reconnaissants pour l\’aide que vous nous avez apportée, mais vous représentez déjà une autre congrégation.\n\nSouhaitez-vous vraiment les trahir pour nous rejoindre ?', 38134),
(204777, 'frFR', 'Voilà ! Tout est prêt. Mais attention à ne pas faire de mouvement trop soudain et, surtout, n\’approchez pas trop près des dévoreurs. Bonne chasse !', '', 38134),
(204776, 'frFR', '', 'Que puis-je pour vous, Sans-Entraves ?', 38134),
(204775, 'frFR', '', 'Vous avez déjà délaissé nos rangs une fois.\n\nPourquoi accepterions-nous de vous absoudre ? Avez-vous quelque chose à dire pour votre défense ?', 38134),
(204773, 'frFR', 'Et voilà ! C\’est ma deuxième meilleure brosse. Ils adorent qu\’on les gratouille avec ça.', '', 38134),
(204772, 'frFR', 'La magie ne vous cachera pas.', '', 38134),
(204771, 'frFR', 'Je vous vois…', '', 38134),
(204770, 'frFR', 'Qu\’est-ce que c\’est que cette entourloupe ?', '', 38134),
(204767, 'frFR', '', 'Êtes-vous $udisposé:disposée; à vous racheter auprès de nous ?\n\nVous ne pensiez tout de même pas que nous allions vous accueillir à bras ouverts ! Non, cette fois, vous allez devoir prouver votre dévouement envers notre cause.\n\nConsentez-vous à nous démontrer votre loyauté ?', 38134),
(204765, 'frFR', 'Vous êtes quelqu\’un de volontaire.\n\nJe ne doute pas que vous serez à votre place parmi les Nécro-seigneurs. À présent, vous devez jurer de mettre votre force et vos pouvoirs à notre service.\n\nFaites-en le serment !', 'Vous êtes quelqu\’un de volontaire.\n\nJe ne doute pas que vous serez à votre place parmi les Nécro-seigneurs. À présent, vous devez jurer de mettre votre force et vos pouvoirs à notre service.\n\nFaites-en le serment !', 38134),
(204764, 'frFR', 'En suivant cette voie, vous devrez abandonner votre congrégation.\n\nJe ne vous blâme pas de rechercher de meilleurs alliés, mais je doute que vos amis vous pardonnent cette désertion.\n\nSouhaitez-vous vraiment poursuivre ?', 'En suivant cette voie, vous devrez déserter vos alliés actuels.\n\nJe ne vous blâme pas de rechercher une communauté plus forte, mais je doute que vos amis vous pardonnent cette trahison.\n\nSouhaitez-vous vraiment aborder ce sujet ?', 38134),
(204750, 'frFR', 'Ouais, ça fera l\’affaire. Du coup, côté divertissement, on est parés.', '', 38134),
(204746, 'frFR', 'J\’utilise pour faire des jolies choses. Décorations sont prêtes !', '', 38134),
(204745, 'frFR', '', 'Revendreth remplit une importante mission, même si la plupart des Venthyrs semblent l\’avoir oublié.', 38134),
(204744, 'frFR', '', 'Ne me demandez pas d\’où il vient. Voyez juste ça comme un témoignage de bonne volonté qui fait de vous mon $uobligé:obligée;. Je me réjouis de faire affaire avec vous.', 38134),
(204742, 'frFR', '', 'Comme j\’avais anticipé votre demande, figurez-vous que j\’ai fait l\’acquisition d\’un objet capable de générer un portail menant dans la tour.', 38134),
(204741, 'frFR', '', 'Je vous épargne la litanie habituelle de mises en garde. Votre mélange si subtil de courage et d\’inconscience les rend parfaitement inutiles.', 38134),
(204740, 'frFR', 'Je dois conserver le parchemin le temps de terminer mes recherches.\n\nVous n\’aurez qu\’à dire à la baronne que vous ne l\’avez pas trouvé.', 'Je dois conserver le parchemin le temps de terminer mes recherches.\n\nVous n\’aurez qu\’à dire à la baronne que vous ne l\’avez pas trouvé.', 38134),
(204739, 'frFR', 'Oui, j\’ai toujours le parchemin.\n\nEt alors, qu\’est-ce que vous allez faire ?', 'Oui, j\’ai toujours le parchemin.\n\nEt alors, qu\’est-ce que vous allez faire ?', 38134),
(204738, 'frFR', 'Oh, ça ? J\’ai presque terminé.\n\nDites à Ninadar que je le lui rendrai bientôt.', 'Oh, ça ? J\’ai presque terminé.\n\nDites à Ninadar que je le lui rendrai bientôt.', 38134),
(204737, 'frFR', '', 'À Tourment, hein ? À partir du moment où je vous ai dit que vos amis étaient détenus là-bas, je savais que vous reviendriez me voir.', 38134),
(204735, 'frFR', 'Vous ne voyez pas que je suis occupé, acolyte ?', 'Vous ne voyez pas que je suis occupée, acolyte ?', 38134),
(204734, 'frFR', 'Vous n\’avez pas un rituel à étudier ?', 'Vous n\’avez pas un rituel à étudier ?', 38134),
(204733, 'frFR', 'Qu\’y a-t-il, acolyte ?', 'Qu\’y a-t-il, acolyte ?', 38134),
(204717, 'frFR', '<Vous apercevez une petite chauve-souris de pierre suspendue au sanctuaire.\n\nD\’après vos estimations, un certain nombre de rubis infusés devraient pouvoir la réveiller.\n\nPeut-être sera-t-elle alors à votre service.>', '<Vous apercevez une petite chauve-souris de pierre suspendue au sanctuaire.\n\nD\’après vos estimations, un certain nombre de rubis infusés devraient pouvoir la réveiller.\n\nPeut-être sera-t-elle alors à votre service.>', 38134),
(204716, 'frFR', 'Nous ne devons jamais relâcher notre garde.', '', 38134),
(204714, 'frFR', 'À votre signal, Sans-Entraves.', '', 38134),
(204707, 'frFR', '', 'Effectivement. Le baron a demandé à m\’emprunter divers ouvrages traitant de sujets hétéroclites, si mes souvenirs sont bons.\n\nComme vous pouvez le voir, je suis débordée. Cependant, si vous acceptiez d\’alléger ma charge de travail, je pourrais peut-être vous procurer les livres que vous cherchez.', 38134),
(204706, 'frFR', '', 'J\’espère que vous avez une bonne raison de m\’interrompre, acolyte.\n\nParlez, je vous écoute.', 38134),
(204702, 'frFR', '', 'Vous devriez les rapporter au sanctum. Draka ou l\’un des autres pourra peut-être en comprendre l\’importance.', 38134),
(204699, 'frFR', '', 'Ah, oui, c\’est bien ça. Ils sont d\’une facture incroyable. Je n\’ai jamais vu une telle qualité, même aux forges des Élus.', 38134),
(204692, 'frFR', 'Suivez-moi, acolyte. Prenez le temps d\’admirer les fruits de votre travail.', '', 38134),
(204691, 'frFR', 'Tenez-vous prête, Nélésis, car l\’heure de notre triomphe approche.', '', 38134),
(204690, 'frFR', 'Que puis-je pour vous, Sans-Entraves ?', '', 38134),
(204689, 'frFR', 'Votre force et votre adresse ne sont plus à démontrer.\n\nJe serais ravie de vous voir rejoindre les Nécro-seigneurs lancés à la reconquête de Maldraxxus, mais vous avez déjà intégré une autre congrégation.\n\nÊtes-vous $usûr:sûre; de vouloir déserter ?', '', 38134),
(204684, 'frFR', 'Bien, vous comprendrez que votre parole seule ne saurait suffire.\n\nÊtes-vous $uprêt:prête; à travailler pour gagner votre place parmi les Kyrians ?', '', 38134),
(204681, 'frFR', 'Bonjour, $n.\n\nVous avez du cran d\’oser vous représenter devant moi.\n\nVous vous doutez bien que nous n\’avons pas oublié votre trahison.', '', 38134),
(204677, 'frFR', 'Soyez bien sur vos gardes, il a senti notre présence !', '', 38134),
(204667, 'frFR', '', 'Puissent vos armes toujours faire mouche.', 38134),
(204666, 'frFR', 'C\’est fait. Ce fut un honneur de chasser à vos côtés.', '', 38134),
(204665, 'frFR', '', 'Nous sommes prêts, $n. Quand vous voudrez.', 38134),
(204664, 'frFR', 'Comment puis-je vous aider ?', '', 38134),
(204658, 'frFR', 'Je vous crois.\n\nÀ présent, je dois vous demander de jurer allégeance aux Kyrians. Devenez membre de la congrégation des Kyrians.\n\nRépétez après moi…\n\n« Je jure de servir les Kyrians avec courage et toute la force de ma volonté. »', '', 38134),
(204657, 'frFR', 'Je me dois de vous poser la question une dernière fois, mon $uami:amie;.\n\nÊtes-vous $ucertain:certaine; de vouloir rejoindre les Kyrians ? Cela représentera une trahison aux yeux de vos alliés actuels.\n\nIls n\’oublieront pas de sitôt un tel affront.\n\nAlors, est-ce vraiment votre dernier mot ?', '', 38134),
(204656, 'frFR', 'Vous avez déjà rejoint une autre congrégation, $n. Êtes-vous $usûr:sûre; de vouloir vous engager dans cette voie ?\n\nNous n\’avons pas oublié la façon dont vous nous avez aidés au Bastion et nous serions ravis de vous accueillir parmi nous.\n\nAlors, dites-moi, êtes-vous vraiment $udisposé:disposée; à trahir vos alliés actuels ?', '', 38134),
(204643, 'frFR', '', 'C\’est impossible !', 38134),
(204641, 'frFR', 'Oh, j\’adore les histoires de cour !', '', 38134),
(204640, 'frFR', 'Je préférerais mourir une seconde fois plutôt qu\’on me voie ici avec vous, Frédéric !', 'Je préférerais mourir une seconde fois plutôt qu\’on me voie ici avec vous, Frédéric !', 38134),
(204632, 'frFR', 'J\’ai déniché cet ensemble le premier ! C\’est vous qui copiez, Frédrik.', 'J\’ai déniché cet ensemble le premier ! C\’est vous qui copiez, Frédrik.', 38134),
(204623, 'frFR', 'Les désavoués cherchent inlassablement à faire main basse sur notre savoir. En ce moment même, ils sont à la recherche des antiques parchemins consignant l\’immense sagesse du parangon.\n\nJ\’ignore dans quel but, mais nous pouvons supposer qu\’ils ne sont pas en quête de rédemption.\n\nJe vous en prie, allez récupérer ces parchemins. Mon aimable disciple va vous porter jusqu\’à eux.', 'Les désavoués cherchent inlassablement à faire main basse sur notre savoir. En ce moment même, ils sont à la recherche des antiques parchemins consignant l\’immense sagesse du parangon.\n\nJ\’ignore dans quel but, mais nous pouvons supposer qu\’ils ne sont pas en quête de rédemption.\n\nJe vous en prie, allez récupérer ces parchemins. Mon aimable disciple va vous porter jusqu\’à eux.', 38134),
(204621, 'frFR', 'Sans nos ailes, l\’Ombreterre sombrerait dans le chaos. Laissez-vous guider par notre devoir éternel.', 'Sans nos ailes, l\’Ombreterre sombrerait dans le chaos. Laissez-vous guider par notre devoir éternel.', 38134),
(204620, 'frFR', 'Re… reculez !\n\nOh, vous n\’êtes pas ici pour m\’obliger à consigner ces terribles actes sur du parchemin ?\n\nCes Venthyrs m\’ont volé mon encre quand j\’ai refusé de retranscrire leurs « recherches ». Si on peut les appeler ainsi.\n\nPouvez-vous m\’aider ? Sans mon encre, je ne sers plus à rien !', 'Re… reculez !\n\nOh, vous n\’êtes pas ici pour m\’obliger à consigner ces terribles actes sur du parchemin ?\n\nCes Venthyrs m\’ont volé mon encre quand j\’ai refusé de retranscrire leurs « recherches ». Si on peut les appeler ainsi.\n\nPouvez-vous m\’aider ? Sans mon encre, je ne sers plus à rien !', 38134),
(204618, 'frFR', 'Souvenez-vous de ce que vous avez appris. Fiez-vous à la voie. Ayez foi en l\’Arbitre et sa sagesse éternelle.', 'Souvenez-vous de ce que vous avez appris. Fiez-vous à la voie. Ayez foi en l\’Arbitre et sa sagesse éternelle.', 38134),
(204617, 'frFR', 'Vous porterez bien des âmes entre vos bras. Certains fardeaux seront plus lourds que d\’autres.', 'Vous porterez bien des âmes entre vos bras. Certains fardeaux seront plus lourds que d\’autres.', 38134),
(204616, 'frFR', 'Notre rôle est clair. Laissez tout le reste à l\’Arbitre et la voie éternelle.', 'Notre rôle est clair. Laissez tout le reste à l\’Arbitre et la voie éternelle.', 38134),
(204615, 'frFR', 'On ne peut juger des derniers instants d\’une vie tant qu\’elle n\’a pas fini son temps dans les royaumes mortels.', 'On ne peut juger des derniers instants d\’une vie tant qu\’elle n\’a pas fini son temps dans les royaumes mortels.', 38134),
(204614, 'frFR', 'Gardez-vous de la tentation de les juger. Toutes les âmes passeront devant l\’Arbitre, et chacune récoltera ce qu\’elle a semé.', 'Gardez-vous de la tentation de les juger. Toutes les âmes passeront devant l\’Arbitre, et chacune récoltera ce qu\’elle a semé.', 38134),
(204613, 'frFR', 'Ne vous immiscez pas dans leurs vies, car leurs existences n\’appartiennent qu\’à eux.', 'Ne vous immiscez pas dans leurs vies, car leurs existences n\’appartiennent qu\’à eux.', 38134),
(204612, 'frFR', 'Sachez que s\’imprégner de la vie d\’autrui mène à la compréhension de sa nature profonde, au-delà de tout jugement ou tout artifice.', 'Sachez que s\’imprégner de la vie d\’autrui mène à la compréhension de sa nature profonde, au-delà de tout jugement ou tout artifice.', 38134),
(204611, 'frFR', 'On ne peut cerner la vérité de son prochain qu\’en prenant ses distances avec lui.', 'On ne peut cerner la vérité de son prochain qu\’en prenant ses distances avec lui.', 38134),
(204610, 'frFR', 'Belle chasse, $n. Venez, il est temps de parler de votre récompense.', '', 38134),
(204609, 'frFR', '', 'Ce gorm ne dévorera plus jamais nos précieuses forêts.', 38134),
(204608, 'frFR', '', 'Hou, un nouveau sort ! J\’ai hâte de l\’essayer !', 38134),
(204604, 'frFR', '', 'Nous allons rapporter le bâton au siège du Primat, en témoignage de cette belle victoire.', 38134),
(204601, 'frFR', '', 'La légende dit qu\’à la disparition du Primat, les différentes pièces de son armure ont été éparpillées. Où que soient les autres, notre congrégation saura les retrouver !', 38134),
(204580, 'frFR', '', 'Pas un bruit. Acculer le gorm dans cette grotte n\’a pas été une mince affaire.', 38134),
(204579, 'frFR', '', 'Alderyn, te voilà. Bien. La bête est blessée, mais je ne peux pas faire grand-chose de plus à cause de sa carapace.', 38134),
(204578, 'frFR', 'Ah, nous y sommes.', '', 38134),
(204567, 'frFR', 'Ce n\’est pas… le destin qui…', 'Ce n\’était pas… mon destin...', 38134),
(204566, 'frFR', 'Maldraxxus… sera à nous…', 'Maldraxxus… sera à nous…', 38134),
(204565, 'frFR', 'Les Rituels… sont éternels…', 'Les Rituels… sont éternels…', 38134),
(204564, 'frFR', 'Mais qui… finira… mes recherches ?', 'Mais qui… finira… mes recherches ?', 38134),
(204563, 'frFR', 'Des intrus ! Abattez-les !', 'Des intrus ! Abattez-les !', 38134),
(204562, 'frFR', 'Nul n\’échappe à notre magie !', 'Nul n\’échappe à notre magie !', 38134),
(204561, 'frFR', 'Vous allez regretter cette intrusion !', 'Vous allez regretter cette intrusion !', 38134),
(204560, 'frFR', 'Votre cadavre fera un serviteur parfait.', 'Votre cadavre fera un serviteur parfait.', 38134),
(204559, 'frFR', '', 'Ma mort… ne change rien…', 38134),
(204558, 'frFR', '', 'Vous serez témoins de l\’avènement !', 38134),
(204557, 'frFR', '', 'L\’œuvre du maître va transformer l\’Ombreterre !', 38134),
(204556, 'frFR', '', 'Comment osez-vous interrompre mon incantation !', 38134),
(204554, 'frFR', 'Je n\’ai pas pour habitude de perdre mon temps avec les êtres à l\’esprit inférieur, mais si vous insistez, je vais vous montrer ce qu\’est la véritable souffrance.', '', 38134),
(204553, 'frFR', 'Les créatures ailées mèneront la défense de Sylvarden depuis les cieux !', '', 38134),
(204552, 'frFR', 'Sous leurs airs dociles, les créatures de Sylvarden n\’en sont pas moins de redoutables combattants qui défendront leur territoire avec autant de cœur et de force que les plus grands champions. Vous sentez-vous à la hauteur ?', '', 38134),
(204543, 'frFR', 'Nous ne saurions déterminer notre rôle dans les plans du Dessein, à cet instant.\n\nCette incertitude vous est peut-être familière, Sans-Entraves, mais, pour nous, il s\’agit un sentiment inédit.', '', 38134),
(204542, 'frFR', 'C\’est un spectacle si majestueux.\n\nBien que l\’Antre rejette le Dessein, elle demeure une partie intégrante de ce royaume.', '', 38134),
(204541, 'frFR', 'L\’Antre bouillonne d\’une telle puissance, et pourtant, elle n\’est pas au service du Dessein.\n\nC\’est en cela que le Dessein nous met à l\’épreuve.', '', 38134),
(204540, 'frFR', 'Nous avons toutes et tous notre rôle à jouer.\n\nC\’est ce que nous affirme le Dessein.\n\nQuand nous savions quel était ce rôle, nous n\’avions aucun mal à y croire. Mais à présent ? Ma certitude vacille.\n\nJe suppose que nous devons garder la foi. Faire confiance au Dessein.\n\nNotre voie est la sienne.', '', 38134),
(204539, 'frFR', 'Il arrive que le spectacle du Dessein invite tout particulièrement à la contemplation, vous ne trouvez pas ?', '', 38134),
(204538, 'frFR', 'Je me demande où vont nous mener ces changements. Jusque-là, le Dessein nous a toujours guidés.\n\nOn peut donc en déduire que c\’est encore lui qui est à l\’œuvre. J\’ignore simplement comment tout ceci va se terminer.\n\nMême le plus petit des rouages veut comprendre quel rôle il joue dans le grand ordre des choses.', '', 38134),
(204537, 'frFR', 'Bienvenue, Sans-Entraves. Tout ce que vous voyez ici n\’est pas à vendre. Nous sommes aussi des collectionneurs.\n\nVotre royaume regorge de créatures étranges. Je prends beaucoup de plaisir à enrichir ma collection.', '', 38134),
(204536, 'frFR', 'Bienvenue, Sans-Entraves.\n\nCe dispositif vous permet d\’accéder à des réserves d\’éclats à travers les dimensions qui séparent ce monde du vôtre. Et contrairement, à nos amis auxiliaires, dévoués à leur sacro-saint Dessein, j\’en comprends le fonctionnement.\n\nMais ne comptez pas sur moi pour tout vous révéler. Secret professionnel, voyez-vous.\n\nEn revanche, vous êtes tout à fait libre de l\’utiliser. Avec les compliments des négociants.', '', 38134),
(204535, 'frFR', '', 'Par les noms des cinq maisons ! Ce sont les espauliers du Primat !', 38134),
(204534, 'frFR', '', 'Emportez l\’armure du Primat à notre sanctuaire. Elle y aura la place d\’honneur qui est la sienne.', 38134),
(204533, 'frFR', '', 'La liche menace nos deux royaumes. Quand nos armées seront prêtes à frapper, nous pourrons appeler Revendreth à la bataille.', 38134),
(204532, 'frFR', '', 'Petit prince, vous n\’avez rien à exiger de moi ! Il faut en savoir plus sur ce qui nous attend avant de foncer tête baissée.', 38134),
(204531, 'frFR', 'Kel\’Thuzad va payer. Baronne Draka ! Vous devez absolument m\’emmener en Maldraxxus sur-le-champ !', '', 38134),
(204530, 'frFR', 'Mais peu importe. La maison des Rituels est à mon service. Venez m\’y affronter si vous en avez le courage !', '', 38134),
(204529, 'frFR', 'Vermines ! ', '', 38134),
(204528, 'frFR', '', 'Elle puisait dans une immense source de puissance.', 38134),
(204527, 'frFR', 'Pfff. Vous devez bouillir de honte à la vue de votre glorieux monarque ainsi enchaîné.', '', 38134),
(204526, 'frFR', 'Qu\’est-ce que vous faites ici, copain bien ?', '', 38134),
(204516, 'frFR', 'Moi ? Bien sûr que non. Notre Sans-Entraves, en revanche… Je crois que nous avons nos chances.', '', 38134),
(204515, 'frFR', '', 'La comtesse ? Pensez-vous vraiment pouvoir la convaincre de venir, seigneur Garridan ?', 38134),
(204514, 'frFR', 'J\’espère qu\’il aura Porte-Cendres. Ce serait un merveilleux spectacle !', '', 38134),
(204513, 'frFR', '', 'Oh, je suis impatiente de rencontrer le fameux Alexandros Mograine de Maldraxxus…', 38134),
(204506, 'frFR', 'Carnœil paré au combat !', '', 38134),
(204503, 'frFR', '', 'Ce n\’est pas dans un endroit aussi affreux que nous pourrons nous battre de façon distinguée, mais nous n\’avons pas le choix. Ne me faites pas perdre mon temps.', 38134),
(204500, 'frFR', '', 'Nous suivons la loi du Primat ! Et ensemble, nous allons lutter pour sauver l\’Ombreterre !', 38134),
(204499, 'frFR', '', 'Sans-Entraves, vous avez démasqué le véritable ennemi de Maldraxxus. Et à présent, vous pouvez rendre leur place à ces glorieux espauliers.', 38134),
(204497, 'frFR', 'Merci, Sans-Entraves. Je serai honoré de me présenter à la cour du prince Rénathal.', '', 38134),
(204474, 'frFR', '', 'Mon rôle consiste à protéger l\’émissaire de la cour, mais il m\’arrive d\’avoir vent de menaces qui ne sauraient être ignorées.', 38134),
(204472, 'frFR', 'Si vous me prenez pour un Gnome en détresse englué dans une étrange masse visqueuse, laissez-moi vous dire que vous vous trompez ! Croyez-le ou non, si j\’en suis là, c\’est parce que je l\’ai décidé. Je suis ici incognito, déguisé, sous couverture… bref, appelez ça comme vous voudrez. Cette substance est ma seule protection, dans cet endroit maudit. Heureusement que j\’ai découvert d\’incroyables familiers adaptés à cet environnement. Une vraie équipe de champions ! J\’écraserai tous mes adversaires et j\’aurai enfin droit au respect que je mérite ! Le seul inconvénient, c\’est que toutes mes affaires sont couvertes de mucus maintenant. Toutes, sans exception.', '', 38134),
(204471, 'frFR', 'Nécrotripaille. Restes. Morceaux délaissés. Aux armes.', '', 38134),
(204470, 'frFR', 'Cela fait des décennies que j\’attendais un adversaire digne de ce nom. Que le meilleur gagne !', '', 38134),
(204469, 'frFR', 'Même la plus humble des batailles doit être planifiée et livrée avec honneur. Revenez avec votre groupe quand vos préparatifs seront achevés.', '', 38134),
(204466, 'frFR', '', 'Nous prenons nos moyens de défense très au sérieux. Ce n\’est pas la taille qui compte : nous nous sommes engagés à entretenir et entraîner toute ressource qui fait la force du Bastion.', 38134),
(204457, 'frFR', 'Vous prenez de gros risques en négligeant de porter des protections adaptées. Tenez, voilà pour vous.', '', 38134),
(204455, 'frFR', '', 'Nos alliés attendent dans la nécropole au-dessus de nos têtes.', 38134),
(204442, 'frFR', '', 'La Lame d\’ébène est prête à l\’action.', 38134),
(204441, 'frFR', 'La Lame d\’ébène est prête à l\’action.', '', 38134),
(204439, 'frFR', 'Il s\’est passé beaucoup de choses durant votre absence, $c.\n\nPermettez-moi de vous mettre au courant des derniers événements.', '', 38134),
(204438, 'frFR', '', 'Il s\’est passé beaucoup de choses durant votre absence, $c.\n\nPermettez-moi de vous mettre au courant des derniers évènements.', 38134),
(204435, 'frFR', '', 'Votre Dessein et le nôtre semblent étroitement liés.', 38134),
(204431, 'frFR', '', 'Quelles plaines magnifiques. C\’est l\’endroit rêvé pour livrer bataille. Avez-vous pris vos dispositions ?', 38134),
(204356, 'frFR', '', 'Ça y est, il est vulnérable ! Terrassez-le, Sans-Entraves, on s\’occupe des autres !', 38134),
(204355, 'frFR', '', 'Tu me tortures avec des visions de soldats de la Horde. Qui viendraient m\’aider, c\’est ça ? Ou est-ce que tu veux nous voir nous battre ?', 38134),
(204344, 'frFR', '', 'Tout ça devrait être intéressant…', 38134),
(204338, 'frFR', 'Alors, pourquoi pas…', '', 38134),
(204320, 'frFR', 'Je crois que ça, ça m\’irait bien…', '', 38134),
(204317, 'frFR', '$uParé:Parée; à retourner à Oribos ?', '', 38134),
(204316, 'frFR', 'Harlowe ! Vous avez bien du culot de vous présenter ainsi, avec une tenue identique à la mienne !', 'Harlowe ! Vous avez bien du culot de vous présenter ainsi, avec une tenue identique à la mienne !', 38134),
(204311, 'frFR', 'S\’ils n\’aiment pas, je ne veux pas d\’eux comme fans !', '', 38134),
(204310, 'frFR', 'Bien sûr qu\’ils vont aimer, Chu. Elles sont épiques !', '', 38134),
(204309, 'frFR', '', 'J\’espère que les fans vont aimer…', 38134),
(204308, 'frFR', 'Nos nouvelles affiches sont dans la crypte. C\’est Chu qui les a dessinées.', '', 38134),
(204307, 'frFR', 'Il faut retourner auprès de vos alliés, nous n\’avons pas le choix.', '', 38134),
(204306, 'frFR', 'Kel\’Thuzad s\’est retranché dans la nécropole. À deux, nous n\’avons aucune chance contre lui.', '', 38134),
(204305, 'frFR', 'Vite ! Il est peut-être déjà trop tard !', '', 38134),
(204304, 'frFR', 'Regardez. Des traces de combat qui remontent jusqu\’à la salle de la margrave…', '', 38134),
(204303, 'frFR', 'Vous avez réussi ! Je n\’ai jamais douté de vos capacités une seconde.', '', 38134),
(204302, 'frFR', 'Vous allez peut-être devoir ruser un peu pour réussir à l\’atteindre.', '', 38134),
(204301, 'frFR', 'Essayez d\’atteindre le levier en montant sur les étagères.', '', 38134),
(204300, 'frFR', 'Le levier se trouve de l\’autre côté de la salle.', '', 38134),
(204299, 'frFR', 'Une main ? Hum… Mais oui, oui ! Loué soit le Primat, quelle idée brillante !', '', 38134),
(204298, 'frFR', 'Une torche ? J\’entends bien que les méchants y cachent souvent des leviers secrets, mais quand même pas dans leur propre prison.', '', 38134),
(204297, 'frFR', 'Sauf si c\’est un interlocuteur à votre niveau que vous cherchez, un crâne vide ne nous sera pas d\’un grand secours.', '', 38134),
(204296, 'frFR', 'Je crains fort que ma magie ne soit précisément pas de la chaînomancie. Voyez si vous trouvez autre chose.', '', 38134),
(204295, 'frFR', 'Gardes ! Jetez cette vermine au cachot. Et amenez-moi le traître !', '', 38134),
(204294, 'frFR', 'Ainsi, c\’est vous qui étiez derrière tout ça. Oh, et qu\’est-ce que je vois là… C\’est l\’oculus de Balmedar !', '', 38134),
(204293, 'frFR', 'C\’est impossible ! Mais… quelle est cette traîtrise ?', '', 38134),
(204292, 'frFR', 'Mais… Que se passe-t-il ? Nooon !', '', 38134),
(204291, 'frFR', 'Le sang du porte-âme va ouvrir la porte !', '', 38134),
(204290, 'frFR', 'La barrière s\’affaiblit ! Des chemins restés fermés pendant des millénaires vont s\’ouvrir à nouveau !', '', 38134),
(204289, 'frFR', 'Oui ! J\’entrevois le passage ! Les ténèbres de l\’Antre m\’appellent depuis l\’autre côté !', '', 38134),
(204288, 'frFR', 'Le rituel est lancé ! Ouvrez les flux d\’anima !', '', 38134),
(204287, 'frFR', 'Espérons pour vous qu\’ils ne seront pas tout à fait mortels.', '', 38134),
(204286, 'frFR', 'Même si je suis assez sûr qu\’il n\’aboutira pas, il pourrait y avoir… quelques effets secondaires.', '', 38134),
(204285, 'frFR', 'Je ne prévoyais pas de vous impliquer dans le rituel, mais nous voilà obligés d\’improviser.', '', 38134),
(204284, 'frFR', 'À vos ordres. Je m\’exécute sur-le-champ !', '', 38134),
(204283, 'frFR', 'Alors il semble qu\’il y ait un traître parmi nous. Il faut accélérer la mise en œuvre du plan.', '', 38134),
(204282, 'frFR', 'Il avait été emporté, maître.', '', 38134),
(204281, 'frFR', 'Je n\’ai pas de temps à perdre à me soucier du menu fretin ! Mon composant, où est-il ?', '', 38134),
(204280, 'frFR', 'La caravane a été pillée de fond en comble. Les négociants qui l\’escortaient ont été massacrés.', '', 38134),
(204279, 'frFR', 'Rapportez-moi les autres composants. J\’ai un plan.', '', 38134),
(204278, 'frFR', 'Oh, une mystérieuse livraison des négociants ? Oui, c\’est intrigant. Je veux savoir ce que ça cache !', '', 38134),
(204277, 'frFR', 'Si le baron vous demande quoi que ce soit, soumettez-le-moi avant. Je veux voir ce qu\’il prépare.', '', 38134),
(204276, 'frFR', 'L\’analyse des composants nous aidera peut-être à comprendre quel type de rituel il prépare.', '', 38134),
(204275, 'frFR', 'Poursuivez votre stratagème. Voyons ce que nous pourrons découvrir d\’autre.', '', 38134),
(204274, 'frFR', 'Nous avons beau faire, impossible de percer son plan à jour.', '', 38134),
(204273, 'frFR', 'Mais qu\’est-ce qu\’il cherche, à la fin ?', '', 38134),
(204272, 'frFR', 'L\’Entravement du Proscrit. Celui-là, c\’est à peine plus qu\’un livre d\’histoire.', '', 38134),
(204270, 'frFR', 'Quelle barrière est-ce qu\’il cherche à créer… ou bien franchir ? De plus en plus curieux.', '', 38134),
(204268, 'frFR', 'Protections et barrières volume IV : les contre-sorts. Il est assommant, celui-là.', '', 38134),
(204266, 'frFR', 'Est-ce que son rituel aurait à voir avec les divers passages entre les royaumes de la mort ?', '', 38134),
(204265, 'frFR', 'Les Voies des Fondateurs. Hum. Un ramassis de spéculations et de demi-vérités. Quel choix étrange.', '', 38134),
(204264, 'frFR', 'Qu\’est-ce qu\’il pouvait donc chercher dans un ouvrage aussi rudimentaire ?', '', 38134),
(204263, 'frFR', 'L\’Arcanum ailé. Une référence peu inspirée sur le mysticisme kyrian.', '', 38134),
(204261, 'frFR', 'Les désavoués peinent à comprendre une notion pourtant essentielle.\n\nLa perfection est indispensable à la réussite. Il est donc crucial de prendre soin de ses outils et de veiller sur ses ressources.', '', 38134),
(204259, 'frFR', 'Mais ce n\’est pas une preuve de trahison. Voyons ce que disent les recueils qu\’il recherche.', '', 38134),
(204258, 'frFR', 'Ainsi le baron demande l\’aide des Venthyrs ? Cet écart serait pardonnable si au moins il partageait le butin.', '', 38134),
(204257, 'frFR', 'Mais assez médité. Il est temps de préparer le grand rituel.', '', 38134),
(204256, 'frFR', 'Et grâce aux réserves d\’anima prises à nos alliés de Revendreth, je vais ouvrir les portes de la victoire.', '', 38134),
(204255, 'frFR', 'Cette créature pathétique va devenir la clé qui m\’ouvrira le chemin d\’un pouvoir immense.', '', 38134),
(204254, 'frFR', 'Je ne suis le morceau de personne ! Vous allez me le payer !', '', 38134),
(204253, 'frFR', 'Et avec, mon morceau de choix : un Kyrian, un de ceux qu\’ils appellent les « porte-âme ».', '', 38134),
(204252, 'frFR', 'Les assemblages ont rapporté le dernier butin de nos attaques contre le Bastion.', '', 38134),
(204251, 'frFR', 'Mais vous n\’auriez jamais une telle idée, n\’est-ce pas ?', '', 38134),
(204250, 'frFR', 'S\’il vous prenait l\’envie de me trahir, je m\’assurerais qu\’elle apprenne l\’identité de l\’assassin.', '', 38134),
(204249, 'frFR', 'Oh, et au fait. L\’acolyte que vous venez de tuer de sang-froid se trouvait être l\’un des favoris de Sin\’dane.', '', 38134),
(204248, 'frFR', 'Ça ira bien. Passez me voir à la salle des épées pour que je puisse m\’assurer de sa qualité.', '', 38134),
(204247, 'frFR', 'La tâche suivante ne sera pas si simple, j\’espère que vous avez le goût du défi.', '', 38134),
(204246, 'frFR', 'Ah, vous avez réussi à trouver mon petit paquet cadeau. Vous êtes moins incapable que vous n\’en avez l\’air.', '', 38134),
(204245, 'frFR', 'Ne parlez à personne de cet arrangement.', '', 38134),
(204244, 'frFR', 'La tour jouxte la crête Scaplienne. Vous y trouverez ce qu\’il vous faut.', '', 38134),
(204242, 'frFR', 'Je me demande pour qui on va jouer.', '', 38134),
(204241, 'frFR', '', 'Avec ces instruments, je me sens à nouveau moi-même…', 38134),
(204240, 'frFR', 'Enfin !', '', 38134),
(204239, 'frFR', 'Le temps n\’est plus à la méditation. Les répétitions peuvent commencer !', '', 38134),
(204238, 'frFR', 'Bonne nouvelle, tout le monde ! Notre prochain concert est programmé… et il sera monumental !', '', 38134),
(204237, 'frFR', '', 'De mon vivant, je ne restais jamais au même endroit bien longtemps.\n\nChaque jour était une nouvelle aventure que j\’étais déterminée à vivre.\n\nJe sens le même désir de vagabonder en vous.\n\nDites-moi… Que pensez-vous de la théorie de l\’échevin Tedo ? La musique peut-elle sauver l\’âme ?\n\nMoi, je n\’en suis pas si sûre.', 38134),
(204236, 'frFR', 'Ansel s\’inquiète trop. Tout se passera bien.\n\nComme le disait mon Asha, chaque haute dune est un nouvel espoir de trouver l\’eau.\n\nLa musique nous sauvera, comme elle l\’a toujours fait.', '', 38134),
(204235, 'frFR', 'Avez-vous déjà entendu dire que la musique est la nourriture de l\’âme ?\n\nC\’est une métaphore, bien entendu…\n\n<Il vous regarde dans les yeux.>\n\nEt si elle était plus que cela ?\n\nEt si, pour ceux qui étaient attirés par elle dans leur vie mortelle, elle pouvait être le chemin du salut ?\n\nEt si la musique pouvait réellement soigner leur âme ?\n\nMoi, en tout cas, j\’y crois.', '', 38134),
(204233, 'frFR', '', 'Maintenant que la réhabilitation du jardin d\’hiver a bien progressé, vous avez accès à davantage de gaines sauvages et de lopins catalyseurs.\n\nLes lopins vous permettent de bonifier les graines avant d\’y placer un esprit.\n\nC\’est en plantant les catalyseurs que vous verrez à quelles graines sauvages tel ou tel lopin est relié.', 38134),
(204232, 'frFR', 'Comment allons-nous impressionner nos bienfaiteurs avec notre musique si nous perdons sans cesse nos instruments ?\n\n<Il jette un coup d\’œil aux stèles du vice dehors.>\n\nOn ne sortira jamais d\’ici.', '', 38134),
(204229, 'frFR', '', 'Bienvenue, $n. En quoi puis-je vous aider ?', 38134),
(204228, 'frFR', '', 'Bienvenue, $n. Une fois que vous l\’aurez réhabilité, ce portail fongique vous permettra d\’accéder au jardin d\’hiver de la Reine.\n\nAdressez-vous à Zayhad pour consulter la liste complète des réparations en attente.', 38134),
(204227, 'frFR', 'Blanchetête va vous expliquer le triste sort de nos alliés.', '', 38134),
(204226, 'frFR', 'Nazgrim va vous expliquer le triste sort de nos alliés.', '', 38134),
(204225, 'frFR', 'Bolvar Fordragon a récupéré de son combat contre Sylvanas Coursevent. Il nous attend avec nos alliés.', '', 38134),
(204224, 'frFR', 'Nous avons ordonné le transfert d\’Achérus devant la citadelle de la Couronne de glace. Je nous ouvre une porte de la mort.', '', 38134),
(204223, 'frFR', 'Vous voilà ! Les préparatifs sont terminés.', '', 38134),
(204222, 'frFR', 'La Lame d\’ébène est toujours prête, je vous attends à Orgrimmar. Rejoignez-moi avant que les froides griffes de la mort ne s\’emparent d\’Azeroth.', '', 38134),
(204221, 'frFR', 'Le voile de l\’existence qui séparait notre monde de l\’Ombreterre a été déchiré.', '', 38134),
(204220, 'frFR', 'La Lame d\’ébène est toujours prête, je vous attends à Hurlevent. Rejoignez-moi avant que les froides griffes de la mort ne s\’emparent d\’Azeroth.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204219, 'frFR', 'Le voile de l\’existence qui séparait notre monde de l\’Ombreterre a été déchiré.', '', 38134),
(204217, 'frFR', 'Affaiblissez-le assez pour que je puisse la libérer grâce au bâton !', '', 38134),
(204216, 'frFR', 'Il est en train de drainer la puissance de Sin\’dane !', '', 38134),
(204215, 'frFR', 'Anéantissez ces intrus, je vous l\’ordonne !', '', 38134),
(204194, 'frFR', 'Vous avez vu la tenue d\’Harlowe ? Je devrais lui arracher ces vêtements !', 'Vous avez vu la tenue d\’Harlowe ? Je devrais lui arracher ces vêtements !', 38134),
(204192, 'frFR', '\’soir. Les nouveaux patrons m\’ont envoyé ici pour que je décide quoi faire avec l\’anima fraîchement récolté.\n\nSi vous en obtenez assez, on pourra reconstruire la plupart des murs qui continuent à s\’effondrer en Revendreth.', '', 38134),
(204187, 'frFR', 'Agitez-vous tant que vous voudrez. Vous ne ferez que retarder l\’inévitable.', '', 38134),
(204186, 'frFR', 'Vous pensez posséder un don exceptionnel, mais vous allez apprendre l\’humilité.', '', 38134),
(204185, 'frFR', 'C\’est Vyraz qui m\’a précipité dans l\’Antre. C\’est lui qui a porté le coup fatal à notre margrave, il n\’y a aucun doute là-dessus.', '', 38134),
(204182, 'frFR', 'C\’était si prévisible que c\’en est accablant. Les mortels semblent avoir un don pour l\’échec.', '', 38134),
(204176, 'frFR', 'Vous-hou voulez voler ? Oui ?', 'Vous-hou voulez voler ? Oui ?', 38134),
(204175, 'frFR', 'C\’est certes impressionnant, mais j\’aurai tout de même plaisir à vous anéantir.', '', 38134),
(204174, 'frFR', '', 'Ce serait de la folie d\’essayer maintenant. Ne perdez pas l\’objectif de vue. Les grandes mises en scène vont devoir attendre.', 38134),
(204164, 'frFR', 'Mais en fendant le heaume, elle a aussi libéré Bolvar Fordragon du fardeau d\’incarner le roi-liche. Il jouit de la même liberté que nous, à présent.', '', 38134),
(204155, 'frFR', 'Voilà pour vous ! Utilisez la flûte près du terrier des gormelins, et ils sortiront de leur cachette !', '', 38134),
(204138, 'frFR', 'Puissent les esprits de mes ancêtres trouver paix et sérénité en ces royaumes.', '', 38134),
(204137, 'frFR', 'Je n\’avais vu que les tréfonds les plus sombres de l\’Ombreterre. Il est réconfortant de voir que l\’espoir y existe aussi.', '', 38134),
(204133, 'frFR', '', 'Thrall, vous savez où ils ont emmené Anduin ?', 38134),
(204132, 'frFR', 'Mes amis…', '', 38134),
(204125, 'frFR', 'Paraît que tous les chefs se ressemblent, et que pas avoir de chef, c\’est pareil mais avec moins de coups de fouet.\n\nMais le prince, il est pas comme ça. Il a des crocs, mais c\’est quelqu\’un de bien. De la pierre bien taillée.', '', 38134),
(204124, 'frFR', 'À travers les siècles, j\’ai foulé d\’innombrables champs de bataille dans de multiples royaumes.\n\nEt au long de ces conflits, je n\’ai jamais connu d\’honneur aussi grand que de servir auprès du prince Rénathal, du général Draven et de vous.\n\nNous combattons des forces inconcevables pour préserver notre réalité tout entière. C\’est ma véritable vocation.', '', 38134),
(204123, 'frFR', 'Si le prince Rénathal souhaite vraiment reconquérir tant de territoires perdus, nous allons devoir rallier plus de vives-pierres à sa cause.\n\nLa situation aurait été bien plus simple s\’il avait convaincu la Créalithe de nous rejoindre. En l\’état actuel des choses, nous devons nous contenter d\’animer des vive-pierres bien précis pour éviter de grossir les rangs de l\’ennemi.\n\nHeureusement, je peux vous désigner ceux qui nous sont loyaux.', 'Si le prince Rénathal souhaite vraiment reconquérir tant de territoires perdus, nous allons devoir rallier plus de vives-pierres à sa cause.\n\nLa situation aurait été bien plus simple s\’il avait convaincu la Créalithe de nous rejoindre. En l\’état actuel des choses, nous devons nous contenter d\’animer des vive-pierres bien précis pour éviter de grossir les rangs de l\’ennemi.\n\nHeureusement, je peux vous désigner ceux qui nous sont loyaux.', 38134),
(204122, 'frFR', 'J\’effacerai donc également tous les plans de vengeance détaillés dans le registre.', 'J\’effacerai donc également tous les plans de vengeance détaillés dans le registre.', 38134),
(204114, 'frFR', 'Si j\’en crois mon registre, vous avez récupéré tous les souvenirs volés.', 'Si j\’en crois mon registre, vous avez récupéré tous les souvenirs volés.', 38134),
(204108, 'frFR', 'Le margrave Gharmal périra en ce jour.', '', 38134),
(204107, 'frFR', 'Trop d\’aspirants, pas assez de jus ! Revenez plus tard, d\’accord ?', 'Trop d\’aspirants, pas assez de jus ! Revenez plus tard, d\’accord ?', 38134),
(204104, 'frFR', 'Hou-hou ! J\’ai à boire ! Servez-vous !', 'Hou-hou ! J\’ai à boire ! Servez-vous !', 38134),
(204103, 'frFR', '<Le régisseur vous présente son plateau avec un petit hululement.>', '<La régisseuse vous présente son plateau avec un petit hululement.>', 38134),
(204100, 'frFR', 'Vous-hou avez soif ? J\’ai à boire !', 'Vous-hou avez soif ? J\’ai à boire !', 38134),
(204099, 'frFR', 'À boire ! À boire ! À boire ?', 'À boire ! À boire ! À boire ?', 38134),
(204097, 'frFR', 'Vous-hou voulez une boisson délicieuse ?', 'Vous-hou voulez une boisson délicieuse ?', 38134),
(204081, 'frFR', '', 'Bonjour, l\’$uami:amie; !\n\nIl y a fort à faire.\n\nSi vous avez un peu de temps, votre aide ne serait vraiment pas de refus.', 38134),
(204080, 'frFR', 'Si on le rapporte à Sin\’dane, il reste peut-être un espoir de vaincre Kel\’Thuzad.', '', 38134),
(204079, 'frFR', 'Attendez… Regardez, le bâton du Primat est intact !', '', 38134),
(204078, 'frFR', '', 'Rendez-vous à l\’anneau de transfert et ouvrez un passage vers Sylvarden. Puisse le Dessein guider vos pas.', 38134),
(204077, 'frFR', '', 'Les bosquets de Sylvarden ont besoin d\’anima pour nourrir les esprits qui y sommeillent. J\’espère que les réserves de la reine suffisent face à la pénurie.', 38134),
(204076, 'frFR', '', 'La reine de l\’hiver a toujours été une solide alliée du Primat. Elle saura entendre son avertissement, je n\’en doute pas.', 38134),
(204075, 'frFR', 'Tous ensemble, nous pouvons plonger notre regard loin dans le royaume du Geôlier. Mais attention à ne pas vous perdre dans les ténèbres.', '', 38134),
(204074, 'frFR', 'Le heaume brisé vibre encore du pouvoir du roi-liche. Ce pouvoir… c\’est celui de l\’Antre.', '', 38134),
(204073, 'frFR', '', 'C\’est le sire Denathrius qui règne sur Revendreth. Je prie pour qu\’il ait mieux supporté que les autres la pénurie d\’anima. Nous avons terriblement besoin de son aide.', 38134),
(204072, 'frFR', '', 'C\’est là que sont envoyées les âmes orgueilleuses si l\’Arbitre y décèle l\’ombre d\’un potentiel. Les Venthyrs les guident alors sur le chemin de la rédemption.', 38134),
(204071, 'frFR', '', 'Revendreth est un royaume lugubre, un monde de flèches obscures et de pierre glaciale. Mais lui et ses habitants remplissent une noble fonction.', 38134),
(204070, 'frFR', '', 'Sans la force de Maldraxxus, je crains que nous ayons peu d\’espoir d\’arrêter le Proscrit.', 38134),
(204069, 'frFR', '', '$uMortel:Mortelle;, à vous de découvrir pourquoi ils s\’en prennent aux Kyrians. Vous devez tout faire pour empêcher une escalade du conflit.', 38134),
(204068, 'frFR', 'Elle n\’a pas survécu.', '', 38134),
(204067, 'frFR', '', 'Gweyir ! Non…', 38134),
(204066, 'frFR', '', 'Reste avec nous, Gweyir ! La malédiction est presque rompue !', 38134),
(204065, 'frFR', '', 'Continuez comme ça ! L\’emprise des Drust s\’affaiblit !', 38134),
(204064, 'frFR', '', 'Vite, détruisez ces horreurs !', 38134),
(204063, 'frFR', '', 'Restez sur vos gardes. La magie de Gorak Zhar ne sera pas purgée sans mal…', 38134),
(204062, 'frFR', '', 'Je vais avoir besoin d\’un assemblage de petite taille, vif et agile.\n\nMes ennemis me voient rarement venir, mais craignent toujours que je surgisse de nulle part.', 38134),
(204060, 'frFR', 'Je savais que vous auriez besoin de moi.', '', 38134),
(204059, 'frFR', 'Il me faut un assemblage costaud et renforcé avec du fer.\n\nAvec ça, je serai en mesure de régler tous vos problèmes.', '', 38134),
(204058, 'frFR', 'Eh bien, Garridan n\’exagérait pas. Cette réception est tout bonnement incroyable !', 'Eh bien, Garridan n\’exagérait pas. Cette réception est tout bonnement incroyable !', 38134),
(204057, 'frFR', 'Où est Stéfan le Pinailleur ? Il m\’a promis une dégustation privée !', 'Où est Stéfan le Pinailleur ? Il m\’a promis une dégustation privée !', 38134),
(204056, 'frFR', 'Je suis ravi que Théotar soit de retour d\’exil. Il a tant d\’esprit !', 'Je suis ravi que Théotar soit de retour d\’exil. Il a tant d\’esprit !', 38134),
(204055, 'frFR', 'J\’ai égaré ma boisson. Vu sa taille, elle ne devrait pas passer inaperçue…', 'J\’ai égaré ma boisson. Vu sa taille, elle ne devrait pas passer inaperçue…', 38134),
(204054, 'frFR', 'Je voudrais que cette réception dure toujours !', 'Je voudrais que cette réception dure toujours !', 38134),
(204053, 'frFR', 'J\’ai fait du charme à votre VIP, là-bas… Je vous en prie !', 'J\’ai fait du charme à votre VIP, là-bas… Je vous en prie !', 38134),
(204052, 'frFR', 'Je ne me suis jamais autant amusé !', 'Je ne me suis jamais autant amusée !', 38134),
(204051, 'frFR', 'Votre réception est très intrigante, il faut bien l\’admettre.', 'Votre réception est très intrigante, il faut bien l\’admettre.', 38134),
(204014, 'frFR', 'J\’ai hâte de savoir si les rumeurs sur la cour des Braises sont vraies !', 'J\’ai hâte de savoir si les rumeurs sur la cour des Braises sont vraies !', 38134),
(204013, 'frFR', 'À Sombrehavre, ils vont mourir de jalousie !', 'À Sombrehavre, ils vont mourir de jalousie !', 38134),
(204012, 'frFR', 'Pensez-vous que je rencontrerai beaucoup de célébrités aujourd\’hui ?', 'Pensez-vous que je rencontrerai beaucoup de célébrités aujourd\’hui ?', 38134),
(204011, 'frFR', 'Entre vous et moi, je n\’arrive toujours pas à croire que j\’ai été invité… J\’ai tellement hâte !', 'Entre vous et moi, je n\’arrive toujours pas à croire que j\’ai été invitée… J\’ai tellement hâte !', 38134),
(204010, 'frFR', 'Toute la ville parlera de cette réception !', 'Toute la ville parlera de cette réception !', 38134),
(204009, 'frFR', 'Que pensez-vous de mes bottes ? J\’y ai ajouté des pointes extra-longues pour l\’occasion !', 'Que pensez-vous de ma robe ? Je la gardais pour une occasion spéciale.', 38134),
(204008, 'frFR', 'J\’ai hâte qu\’ils ouvrent les portes !', 'J\’ai hâte qu\’ils ouvrent les portes !', 38134),
(204007, 'frFR', 'Donnez-moi un indice, Sans-Entraves ! Quelles merveilles nous réserve la réception d\’aujourd\’hui ?', 'Donnez-moi un indice, Sans-Entraves ! Quelles merveilles nous réserve la réception d\’aujourd\’hui ?', 38134),
(204005, 'frFR', 'Ara\’lon… Je vais ordonner des recherches. Hors de question de l\’abandonner aux Drust.', '', 38134),
(204003, 'frFR', '', 'Ara\’lon n\’a pas encore rapporté la sacoche. J\’ai peur qu\’il ait eu des ennuis… ou pire.', 38134),
(204002, 'frFR', '', 'Retrouvez-moi au cœur de la Forêt pour le rituel. Bonne chasse à vous deux.', 38134),
(204000, 'frFR', '', 'Il préfère qu\’une réception soit protocolaire, alors assurez-vous de faire preuve de la plus grande formalité.', 38134),
(203999, 'frFR', 'Nous avons reçu le carton d\’invitation du gardien de la crypte Kassir et il a promis d\’être présent.', '', 38134),
(203998, 'frFR', 'Je ne reviendrai pas les mains vides, j\’en fais le serment !', '', 38134),
(203997, 'frFR', '', '$n, trouvez-moi des racines de nox. Ara\’lon, il me faut une sacoche de sorcière. En voler une aux Drust ne sera pas une mince affaire.', 38134),
(203996, 'frFR', 'Voyez avec le personnel pour obtenir les contrats et les invitations. Je m\’occuperai du reste !', '', 38134),
(203995, 'frFR', 'Je connais très bien notre premier invité. Je sais exactement quel genre de cour il apprécie !', '', 38134),
(203993, 'frFR', 'Il faut nous adresser à ses purotins : ils semblent toujours avoir une bouilloire sur le feu.', '', 38134),
(203992, 'frFR', 'J\’ai bien peur que Théotar ait épuisé nos réserves de thé pendant la répétition.', '', 38134),
(203990, 'frFR', 'Gresit fera le relai. Je marque où il est sur la carte.', '', 38134),
(203989, 'frFR', 'Si vous ne savez pas par quoi commencer, jetez donc un coup d\’œil à ma liste de divertissements.', '', 38134),
(203988, 'frFR', 'Vous aidez l\’Accusatrice et les ritualistes et ils nous aideront en retour. Vous pigez ?', '', 38134),
(203987, 'frFR', 'Personne ne mène aussi bien les rituels que les Confirmés des salles de l\’Expiation.', '', 38134),
(203984, 'frFR', 'Pour ne mener que ceux qui le méritent à Oribos, les porte-âme doivent aiguiser leur jugement. Bravo.', '', 38134),
(203983, 'frFR', 'Le rôle des guetteurs est de déterminer si une âme est prête à rejoindre l\’Ombreterre. Voici l\’occasion de vous exercer.', '', 38134),
(203981, 'frFR', '', 'Vous tombez à point nommé ! Même s\’il n\’aurait pas fallu une seconde de plus…', 38134),
(203980, 'frFR', 'Si vous cherchez Kivarr, ils viennent juste de l\’emmener !', '', 38134),
(203979, 'frFR', '', 'Vous allez avoir besoin de trois clés différentes pour ouvrir ma cage. Je crois qu\’ils ont un peu peur de moi.', 38134),
(203974, 'frFR', 'Fouillez le bosquet. Les Drust n\’ont pas pu l\’emmener bien loin.', '', 38134),
(203973, 'frFR', 'Elle n\’est pas là… mais il y a des traces de lutte. Il a dû lui arriver quelque chose !', '', 38134),
(203970, 'frFR', 'Les combats, très peu pour moi. Reste qu\’on ne se bat pas le ventre vide.\n\nJe veux un assemblage à la tête bien faite, aux mains puissantes et avec plusieurs bouches pour goûter mes plats.', '', 38134),
(203969, 'frFR', '', 'Je veux deux lames géantes à la place des mains. Imaginez la terreur dans les yeux de mes ennemis quand ils les verront !\n\nÇa promet, pas vrai ?', 38134),
(203968, 'frFR', '', 'J\’en ai plus qu\’assez de servir de bouclier vivant et de foncer tête baissée vers des dizaines d\’adversaires !\n\nJ\’ai toujours rêvé de lancer des sorts et de bombarder mes ennemis de magie !\n\nConstruisez-moi un assemblage qui en soit capable !', 38134),
(203966, 'frFR', 'Mes Élus ! Nos ennemis passent à l\’action ! La guerre est imminente, et elle nous donnera la victoire !', '', 38134),
(203965, 'frFR', 'Effectivement. Voyez de quoi notre $uinvité:invitée; est capable puis rejoignez-moi à la nécropole. Il y a une guerre à livrer.', '', 38134),
(203964, 'frFR', 'Salutations !\n\nJe suis à la recherche de reliques anciennes, ici en Ombreterre.\n\nQue diriez-vous de m\’accompagner ?', 'Salutations !\n\nJe suis à la recherche de reliques anciennes, ici en Ombreterre.\n\nQue diriez-vous de m\’accompagner ?', 38134),
(203961, 'frFR', 'Archéologue ? Mais qu\’est-ce que c\’est ?\n\n<Il vous écoute lui expliquer les tenants et les aboutissants de l\’archéologie.>\n\nFascinant, mais je n\’ai jamais rien vu de tel ici.\n\nVous autres mortels laissez peut-être le temps engloutir vos secrets, mais ça ne fonctionne pas comme ça, dans ce monde.\n\nCela dit, si la situation devait changer, je ne manquerais pas de vous prévenir.', 'Archéologue ? Mais qu\’est-ce que c\’est ?\n\n<Il vous écoute lui expliquer les tenants et les aboutissants de l\’archéologie.>\n\nFascinant, mais je n\’ai jamais rien vu de tel ici.\n\nVous autres mortels laissez peut-être le temps engloutir vos secrets, mais ça ne fonctionne pas comme ça, dans ce monde.\n\nCela dit, si la situation devait changer, je ne manquerais pas de vous prévenir.', 38134),
(203960, 'frFR', '', 'Il nous a beaucoup appris à tous les deux. Et nous allons le venger ensemble.', 38134),
(203959, 'frFR', 'Je suis heureux que vous l\’ayez servi avec honneur. Même quand vous étiez un sujet d\’Akarek, il admirait votre fougue.', '', 38134),
(203958, 'frFR', 'Il m\’a guidé vers des victoires que j\’aurais crues impossibles. Je vais arracher le cœur de Vyraz de mes propres mains pour sa trahison.', '', 38134),
(203957, 'frFR', '', 'Krexus disait le plus grand bien de vous, Porte-Cendres. Pourtant, il était avare de compliments.', 38134),
(203956, 'frFR', '', 'Survivre est pour les faibles. Les forts triomphent… ou meurent.', 38134),
(203955, 'frFR', '', 'Le margrave a voulu me diriger sur une nouvelle voie, Vashj. Savoir renoncer au passé est notre seul espoir de survie.', 38134),
(203948, 'frFR', '', 'Vous avez couru vous réfugier derrière la bannière des Élus, alors que vos pairs, les autres Yeux, se faisaient massacrer…', 38134),
(203947, 'frFR', '', 'Je n\’ai pas abandonné Akarek. Mais tout comme vous, je suis partie en mission peu avant notre chute.', 38134),
(203944, 'frFR', '', 'Oh, mais qu\’avons-nous là ? C\’est l\’un des petits pantins de Kel\’Thuzad.', 38134),
(203943, 'frFR', 'Non ? Comme c\’est dommage. Mais je ne peux pas partir sans vous laisser un cadeau d\’adieu.', '', 38134),
(203942, 'frFR', 'Ainsi, vous avez réussi à en réchapper. Vous venez pour fêter ma victoire ?', '', 38134),
(203941, 'frFR', 'Peut-être. Mais le pouvoir a toujours un prix.', '', 38134),
(203940, 'frFR', '', 'Vous allez payer pour cette trahison !', 38134),
(203939, 'frFR', '', 'Tant de faiblesse ne fait pas honneur à la maison des Rituels, Sin\’dane. Le moment est venu de nommer une nouvelle margrave.', 38134),
(203938, 'frFR', '', 'Baronne ?! Comment osez-vous !', 38134),
(203937, 'frFR', 'On dirait que le vieux Vol\’jin va repartir en chasse… mais d\’abord, il va se reposer un peu.', '', 38134),
(203936, 'frFR', 'Non. Pas seul, en tout cas.', '', 38134),
(203935, 'frFR', '', 'Quelle est cette félonie ? Vous pensez vraiment être de taille face à une margrave ?', 38134),
(203934, 'frFR', '', 'Je connais les légendes de votre reine, aussi vaniteuse que cruelle. Vous semblez avoir beaucoup en commun avec elle… physiquement comme moralement.', 38134),
(203933, 'frFR', '', 'Les manigances de la reine Azshara nous ont offert une puissance incommensurable. La Fracture n\’était que le prix à payer pour notre élévation.', 38134),
(203931, 'frFR', '', 'Les mondes détruits semblent n\’avoir aucun secret pour vous. Ni les pactes avec la racaille démoniaque.', 38134),
(203930, 'frFR', '', 'Savez-vous que j\’ai moi aussi foulé votre monde, Draka ? Ou plus précisément, sa carcasse brisée.', 38134),
(203929, 'frFR', '', 'Je l\’ai $uvu:vue; faire preuve de force et de solidité. Des qualités qui nous seront précieuses en ces heures difficiles.', 38134),
(203922, 'frFR', 'Vous avez bien fait de lui permettre d\’échapper à l\’arène. Nous ne pouvions pas l\’abandonner à nos ennemis.', '', 38134),
(203919, 'frFR', 'La malédiction des Drust aura bientôt raison de Gweyir. Il est trop risqué de la garder ici !', '', 38134),
(203918, 'frFR', '', 'Je vais retourner voir la baronne. Servir à vos côtés est un honneur.', 38134),
(203914, 'frFR', 'Gweyir ! Elle est en vie, mais… mais qu\’est-ce qu\’ils lui ont fait ?', '', 38134),
(203911, 'frFR', 'Néra était la plus battante d\’entre nous. Jamais elle ne se serait inclinée devant les Drust.', '', 38134),
(203910, 'frFR', 'Taeras… Puisse la nuit t\’accueillir, mon vieil ami.', '', 38134),
(203909, 'frFR', '', 'C\’est le premier d\’une longue série. Quelle belle victoire.', 38134),
(203908, 'frFR', 'Azélor… Ah, comme je le craignais. Il ne méritait pas ce sort.', '', 38134),
(203903, 'frFR', 'Le bosquet entier a été possédé ! Attention à ne pas nous faire démasquer.', '', 38134),
(203902, 'frFR', '$n, par ici ! J\’ai besoin de votre aide…', '', 38134),
(203901, 'frFR', 'Zzz… retourner… Treshala… Darnassus… Zzzz…', '', 38134),
(203899, 'frFR', 'Nous avons tous beaucoup à apprendre. Venez au temple, nous pourrons discuter.', '', 38134),
(203896, 'frFR', 'Nous devons comprendre quel mal le ronge. Je crois qu\’il nous reste encore beaucoup à apprendre.', '', 38134),
(203895, 'frFR', 'Confiez-moi Uther, je vous en conjure.', '', 38134),
(203894, 'frFR', '', 'Maintenant que nous tenons la nécropole, nous pouvons ouvrir un portail vers le siège du Primat.', 38134),
(203893, 'frFR', 'Nous avons besoin de ses talents. Aidez-le à traverser cette crise !', '', 38134),
(203892, 'frFR', 'Pélagos est arrivé pour mobiliser les collecteurs, mais il est assailli par le doute face à la bataille qui s\’annonce.', '', 38134),
(203891, 'frFR', '', 'Dans cet état, elle ne pourra rien faire face à elle. Réactivez les bourdons pour la stabiliser !', 38134),
(203889, 'frFR', 'Vous qui protégez Maldraxxus, je vous accorde ce pouvoir !', '', 38134),
(203885, 'frFR', '', 'Leur douleur est absolument délicieuse !', 38134),
(203884, 'frFR', '', 'Savourons leur souffrance !', 38134),
(203883, 'frFR', 'Mon règne est éternel !', '', 38134),
(203882, 'frFR', 'Je vais piétiner vos cadavres !', '', 38134),
(203881, 'frFR', 'Nul ne survivra !', '', 38134),
(203865, 'frFR', '', 'Maintenant !', 38134),
(203864, 'frFR', '', 'Tâtez de nos lames ! Bien, il ne reste plus qu\’un objectif.', 38134),
(203863, 'frFR', '', 'Restez vigilants, des espions rôdent !', 38134),
(203858, 'frFR', 'Nos ennemis sont fermement rivés à leurs croyances. Si les paroles n\’y peuvent rien, il faut laisser la place aux actes, et c\’est à vous que revient cette triste tâche.', '', 38134),
(203857, 'frFR', 'Avec le temps, ils verront qu\’ils font erreur. J\’espère qu\’ils retiendront cette leçon.', '', 38134),
(203848, 'frFR', '', 'Obtenir la gratitude de l\’un ne fait que galvaniser l\’autre. Mais c\’est tout le Bastion que vos exploits renforcent.', 38134),
(203847, 'frFR', '', 'Cette rivalité entre Néméa et Pélodis dure depuis des siècles. Je suis sûre qu\’ils vous trouveront quelque chose à faire… l\’un contre l\’autre, bien sûr.', 38134),
(203844, 'frFR', '', 'Vous avez toujours été trop veule pour suivre la véritable voie. Déva aurait dû vous laisser pourrir !', 38134),
(203843, 'frFR', 'Notre but était de sauver les nôtres, pas de les soumettre aux ténèbres !', '', 38134),
(203841, 'frFR', 'Lysonia… mais pourquoi ?', '', 38134),
(203840, 'frFR', '', 'Je comprends quelle est votre place dans tout ça, à présent. Vous êtes la clé du destin… de MON destin !', 38134),
(203839, 'frFR', 'Mon âme porte la marque de ce royaume ?', '', 38134),
(203838, 'frFR', 'Je ne vois que les ténèbres…', '', 38134),
(203837, 'frFR', '', 'Pas de discussion. Allez !', 38134),
(203836, 'frFR', 'Lysonia, je…', '', 38134),
(203835, 'frFR', '', 'Encore plus ! Même si ça doit le tuer !', 38134),
(203834, 'frFR', 'À vos ordres.', '', 38134),
(203833, 'frFR', '', 'Il refuse de céder. Redoublez de force !', 38134),
(203832, 'frFR', '', 'Marchedroit, j\’ai renforcé les patrouilles comme vous l\’aviez demandé. Les désavoués sont plus actifs que jamais. Beaucoup plus.', 38134),
(203831, 'frFR', '', 'En avant, mes désavoués. Achevons de préparer la venue d\’une nouvelle aube.', 38134),
(203830, 'frFR', 'Les fidèles de l\’Archonte cherchent à reconstruire l\’écu d\’Ascension avec l\’appui des autres congrégations.', '', 38134),
(203829, 'frFR', '', 'Marchedroit. Et vous, Sans-Entraves. J\’ai quelque chose à vous montrer.', 38134),
(203828, 'frFR', '', 'Et Uther avec, j\’en ai bien peur.', 38134),
(203827, 'frFR', '', 'Et il se peut que nous ayons besoin d\’eux. On me rapporte que comme le pressentait Thénios, Lysonia a pris la tête des désavoués.', 38134),
(203826, 'frFR', '', 'Ce nouvel écu donnera aux transcendés plus de force que jamais !', 38134),
(203819, 'frFR', 'Maintenant que Déva n\’est plus, Lysonia va prendre sa place à la tête des désavoués. J\’espère que nous trouverons la paix avant qu\’ils ne repartent en campagne.', '', 38134),
(203813, 'frFR', 'Sa main Lysonia l\’a vite suivie comme l\’avait fait Uther. Et plus nos réserves d\’anima diminuaient, plus leur nombre grandissait.', '', 38134),
(203812, 'frFR', 'S\’écarter de la voie lorsque l\’on incarne la loyauté… Ça ne pouvait la mener qu\’au doute et à la violence.', '', 38134),
(203805, 'frFR', 'Déva voulait corriger ce qu\’elle voyait comme une injustice. Son intention était pure. Mais… Uther n\’était pas prêt pour les épreuves.', '', 38134),
(203799, 'frFR', 'Ce que vous allez voir, peu l\’ont vu avant vous : l\’arrivée d\’Uther au Bastion.', '', 38134),
(203798, 'frFR', 'Oh, mais ça contient…', '', 38134),
(203797, 'frFR', 'L\’Archonte m\’a privé d\’un verdict juste. Déva, elle, a condamné ce monstre à souffrir comme il le méritait !', '', 38134),
(203796, 'frFR', '', 'Sa glorieuse vision doit devenir réalité. Les Kyrians, tous les Kyrians, seront libérés de vos chaînes !', 38134),
(203795, 'frFR', '', 'Avec ces éclats, je vais pouvoir assembler de nouveaux miroirs pour la prochaine réception.', 38134),
(203791, 'frFR', 'Vous refusez donc de vous rendre. Alors c\’est dans le sang que Krexus sera vengé !', '', 38134),
(203789, 'frFR', '', 'Cette trahison vous condamne. Justice sera faite !', 38134),
(203788, 'frFR', 'Cessez cette folie ! Le Primat n\’aurait jamais accepté une chose pareille !', '', 38134),
(203787, 'frFR', 'Salutations, $umortel:mortelle;.\n\nJe transforme en musique les vices des âmes qui chantent le récit de leur trépas dans les terribles profondeurs de cette salle sacrée.\n\nQuand votre heure sera venue, vous rejoindrez peut-être mon chœur…', 'Salutations, $umortel:mortelle;.\n\nJe transforme en musique les vices des âmes qui chantent le récit de leur trépas dans les terribles profondeurs de cette salle sacrée.\n\nQuand votre heure sera venue, vous rejoindrez peut-être mon chœur…', 38134),
(203781, 'frFR', 'Poursuivez l\’assaut ! Éliminez le chef !', '', 38134),
(203772, 'frFR', '', 'Tout a commencé après que la Horde a détruit Teldrassil, le foyer des Kaldorei.\n\nFaute d\’effectifs suffisants, l\’Alliance n\’a pas pu nous aider à reprendre ce qu\’il en restait, et ma minn\’do n\’a pas eu le choix.\n\nElle a imploré Élune de lui conférer le pouvoir de la guerrière de la Nuit, une incarnation de la fureur divine.\n\nDepuis, Tyrande porte en elle la sainte colère d\’Élune, mais ce pouvoir est si terrible que personne ne saurait le posséder bien longtemps sans y succomber.', 38134),
(203771, 'frFR', '', 'Vous n\’êtes pas au courant ? Je croyais pourtant que la fureur de ma minn\’do s\’était ébruitée au sein de la Horde.\n\nLes vôtres ont détruit Teldrassil, le foyer des Kaldorei.\n\nFaute d\’effectifs suffisants, l\’Alliance n\’a pas pu nous aider à reprendre ce qu\’il en restait, et ma minn\’do n\’a pas eu le choix.\n\nElle a imploré Élune de lui conférer le pouvoir de la guerrière de la Nuit, une incarnation de la fureur divine.\n\nDepuis, Tyrande porte en elle la sainte colère d\’Élune, mais ce pouvoir est si terrible que personne ne saurait le posséder bien longtemps sans y succomber.', 38134),
(203770, 'frFR', '', 'Nous pourrons nous téléporter à la fonderie avec le reste d\’anima. Rendez-vous là-bas.', 38134),
(203765, 'frFR', '', 'La liste des invités de Garridan contient des informations sommaires qui vous aideront à trouver les éventuels participants.\n\nAllez lui parler pour en obtenir une copie consultable à tout moment.\n\nNous allons aussi indiquer sur votre carte l\’emplacement de ceux déjà trouvés mais pour lesquels vous n\’avez pas encore de confirmation.\n\nPour confirmer une invitation, rendez-vous à l\’endroit où se trouve le convive et accomplissez la tâche qu\’il vous confiera.\n\nIl est impossible d\’obtenir la confirmation d\’un invité potentiel sans l\’avoir déjà rencontré. Pour le trouver, il suffit généralement d\’arpenter son royaume.\n\nN\’oubliez pas que la liste des invités de Garridan vous renseignera sur les tâches à accomplir pour valider les invitations.', 38134),
(203764, 'frFR', '', 'Je serai ravie de vous fournir de plus amples informations. N\’hésitez pas à me demander.', 38134),
(203763, 'frFR', '', 'Selon mes registres, il se trouve généralement près des cryptes, dans les salles de l\’Expiation.', 38134),
(203762, 'frFR', 'Auriez-vous la bonté de lui rendre visite et de découvrir ce qui requiert sa présence ailleurs ?', '', 38134),
(203761, 'frFR', 'Nous n\’avons pas encore reçu le carton d\’invitation du gardien de la crypte Kassir.', '', 38134),
(203758, 'frFR', '', 'Merveilleux ! Alors à vous l\’honneur de le déposer à la place qui lui est destinée.', 38134),
(203753, 'frFR', 'Ho, voilà qui pourrait être plus intéressant. Qu\’en pensez-vous ?', '', 38134),
(203752, 'frFR', 'Gharmal avait un appétit de pouvoir absolument féroce. Il aurait dû l\’employer à défendre Maldraxxus au lieu de servir ses petits intérêts.', '', 38134),
(203751, 'frFR', 'Humpf. Rien de bien intéressant ici.', '', 38134),
(203750, 'frFR', 'Allez, venez. Il est temps de fouiller ses réserves.', '', 38134),
(203749, 'frFR', '', 'Effectivement. Je vais préparer notre voyage. En attendant, voyez si Gharmal possédait autre chose qui pourrait nous aider.', 38134),
(203748, 'frFR', 'On sait que Gharmal manigançait quelque chose avec Sin\’dane. S\’ils avaient l\’appui de Revendreth, c\’est très mauvais signe pour nous. Il faut en avoir le cœur net.', '', 38134),
(203747, 'frFR', '', 'Ça ne ressemble pas à une prise de guerre comme l\’anima volé en Maldraxxus. C\’est un cadeau, comme à un allié.', 38134),
(203746, 'frFR', 'C\’est de l\’anima, mais… il n\’est pas de Maldraxxus. Il empeste la sorcellerie venthyr.', '', 38134),
(203745, 'frFR', '', 'J\’ai eu l\’idée de fouiller la tour pour trouver des preuves de sa trahison, mais je ne m\’attendais pas à y trouver ça.', 38134),
(203744, 'frFR', 'Nous vous suivons.', '', 38134),
(203743, 'frFR', '', 'Mes amis ! Vous devez absolument voir ça. J\’ai fait une belle trouvaille dans la citadelle de Gharmal.', 38134),
(203741, 'frFR', '', 'Avec tous ces noms désormais rayés de ma liste, je peux bien vous accorder un peu de temps pour défendre votre cour.', 38134),
(203740, 'frFR', 'Venez vous battre, Draka ! Ou allez-vous nous supplier de rejoindre notre maison, comme vous l\’avez fait chez les Élus ?', 'Venez vous battre, Draka ! Ou allez-vous supplier de pouvoir entrer dans notre maison, comme vous l\’avez fait chez les Élus ?', 38134),
(203739, 'frFR', 'Krexus n\’était qu\’un imbécile ! Il a payé le prix de son insolence, et vous allez le suivre !', 'Krexus n\’était qu\’un imbécile ! Il a payé le prix de son insolence, et vous allez le suivre !', 38134),
(203738, 'frFR', 'Une baronnie à qui me rapportera le cadavre du parangon !', 'Une baronnie à qui me rapportera le cadavre du parangon !', 38134),
(203736, 'frFR', 'Nous aurions dû attaquer le Bastion il y a des siècles ! C\’est lui qui nous a retenus !', 'Nous aurions dû attaquer le Bastion il y a des siècles ! C\’est lui qui nous a retenus !', 38134),
(203734, 'frFR', '', 'Je vais étudier la question avec Adrestès. Allez, et servez, Sans-Entraves.', 38134),
(203733, 'frFR', '', 'Il y a d\’autres améliorations à trouver auprès des autres congrégations.', 38134),
(203732, 'frFR', '', 'Je sens déjà qu\’il gagne en puissance ! Les nouveaux transcendés seront un atout précieux contre Lysonia et Uther.', 38134),
(203731, 'frFR', '', 'À vous l\’honneur, Sans-Entraves. En remerciement du rôle que vous avez joué face à Gharmal.', 38134),
(203730, 'frFR', '', 'Personne ne sera surpris d\’apprendre que Mikanikos a déjà préparé l\’écu à être amélioré.', 38134),
(203729, 'frFR', '', 'Oh, ça a marché ? Et notre Sans-Entraves a même survécu ! Hum. La prochaine fois, essayons de quitter l\’Antre d\’une autre façon. Recommencer ça pourrait nous annihiler.', 38134),
(203728, 'frFR', 'C\’est le moment, frappez !', '', 38134),
(203727, 'frFR', 'Ohé ! À votre signal, je vais l\’affaiblir !', '', 38134),
(203710, 'frFR', '', 'Retrouvez-moi au fort Élyséen. Nous y déposerons le cœur ensemble.', 38134),
(203709, 'frFR', '', 'Eh bien… Merci. Je vais m\’en occuper.', 38134),
(203708, 'frFR', 'C\’est une sale besogne, mais son cœur porte son pouvoir de modeler la chair et serait un vrai renfort pour votre écu d\’Ascension.', '', 38134),
(203707, 'frFR', 'Xandria, vous devez arracher le cœur de Gharmal.', '', 38134),
(203706, 'frFR', '', 'Adieu. Évitez de mentionner ma présence ici. Et si vous veniez à replonger dans cet abîme de souffrance, essayez au moins de me rapporter un souvenir intéressant.', 38134),
(203705, 'frFR', '', 'Voilà qui conclut notre petit marché. Vous trouverez la pierre d\’accès un peu plus haut.', 38134),
(203704, 'frFR', 'Vous confondez bravoure et bêtise. Vous êtes sur notre terrain, ici !', 'Vous confondez bravoure et bêtise. Vous êtes sur notre terrain, ici !', 38134),
(203702, 'frFR', '', 'Vous pouvez fanfaronner. Il est temps de payer pour vos crimes.', 38134),
(203699, 'frFR', 'C\’est tout ce que vous avez pu rassembler ? Moi qui espérais faire un bon repas !', 'C\’est tout ce que vous avez pu rassembler ? Moi qui espérais faire un bon repas !', 38134),
(203697, 'frFR', 'Bien. Finissons-en.', '', 38134),
(203696, 'frFR', '', 'Oh, je le suis depuis qu\’ils ont pillé mon temple.', 38134),
(203695, 'frFR', 'La véritable bataille va commencer. Êtes-vous prête, Xandria ?', '', 38134),
(203694, 'frFR', 'À moi, Élus ! À moi, Kyrians ! L\’heure de l\’ultime assaut a sonné !', '', 38134),
(203693, 'frFR', 'Déchaînez le pouvoir de la lame et détruisez ses mastodontes une bonne fois pour toutes !', '', 38134),
(203692, 'frFR', 'Montrons à ces monstres la vraie force de la maison des Élus.', '', 38134),
(203691, 'frFR', 'Nos mastodontes kyrians vont écraser tous ceux qui osent se dresser contre la maison des Assemblages !', 'Nos mastodontes kyrians vont écraser tous ceux qui osent se dresser contre la maison des Assemblages !', 38134),
(203690, 'frFR', 'Nos nouveaux alliés nous ont donné une force vertigineuse. Le baron de Sin\’dane disait vrai !', 'Nos nouveaux alliés nous ont donné une force vertigineuse. Le baron de Sin\’dane disait vrai !', 38134),
(203689, 'frFR', 'Le Bastion n\’était que le commencement. Notre chair engloutira tout !', 'Le Bastion n\’était que le commencement. Notre chair engloutira tout !', 38134),
(203688, 'frFR', 'Quelle démonstration de force !', '', 38134),
(203674, 'frFR', 'Vous n\’êtes pas le premier aspirant à arpenter la voie et vous ne serez pas le dernier. N\’oubliez pas que nous sommes tous là avec vous.', '', 38134),
(203671, 'frFR', '', 'Suivez-moi dans Tourment, $p. Il n\’y a pas de temps à perdre.', 38134),
(203666, 'frFR', 'À vous de puiser en vous le courage de revenir affronter l\’épreuve.', '', 38134),
(203664, 'frFR', 'La voie est riche de leçons pour nous tous.', '', 38134),
(203660, 'frFR', 'Chaque épreuve nous en apprend un peu plus sur nous et sur notre peuple. La voie de l\’Ascension attend !', '', 38134),
(203657, 'frFR', 'Ce rassemblement est des plus impressionnants. Je vois tant de valeureux champions. Vous m\’en voyez très honoré.', '', 38134),
(203655, 'frFR', 'Choisissez une destination.', 'Choisissez une destination.', 38134),
(203654, 'frFR', 'La baronne Draka a lancé l\’assaut. Je vous emmènerai là-bas dès que vous l\’ordonnerez.', '', 38134),
(203653, 'frFR', 'Bravo, Sans-Entraves. Et maintenant, au tour de Kel\’Thuzad.', '', 38134),
(203652, 'frFR', 'Il s\’est entouré d\’un bouclier impénétrable ! Les armes ne pourront rien pour le briser.', '', 38134),
(203649, 'frFR', 'L\’explosion l\’a complètement détruite !', '', 38134),
(203648, 'frFR', '', 'Il… m\’en faut… plus !', 38134),
(203647, 'frFR', '', 'Non ! Il me l\’avait promis ! Je l\’ai mérité !', 38134),
(203646, 'frFR', '', 'Le pouvoir que je méritais est à moi, enfin !', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203645, 'frFR', '', 'Je refuse d\’être vaincue par cette vermine !', 38134),
(203640, 'frFR', 'Nous devons apprendre à coopérer si nous voulons que le Bastion traverse cette crise. En attendant, votre aide est la bienvenue.', '', 38134),
(203639, 'frFR', 'L\’influence des désavoués a jeté un doute sur la sagesse dont se prévaut mon temple. Je parlerai à mes acolytes, mais il nous faut d\’abord rétablir l\’ordre.', '', 38134),
(203638, 'frFR', 'Êtes-vous encore en train de jouer, Ninadar ? Anéantissez-moi ces intrus !', '', 38134),
(203637, 'frFR', '', 'Oui, maître. Je vis pour servir.', 38134),
(203635, 'frFR', '', 'C\’est un plaisir de vous voir, $p. J\’ai une nouvelle à vous confier.', 38134),
(203633, 'frFR', '', 'Il est temps de passer à l\’action, Sans-Entraves. Exterminez les liches !', 38134),
(203632, 'frFR', 'Bon, bon. Mais si un jour vous avez besoin des services d\’un vieux Loa, n\’hésitez pas à faire un tour de l\’Autre côté, hein !', '', 38134),
(203631, 'frFR', '', 'Hors de ma vue.', 38134),
(203630, 'frFR', 'Ah, mais aucun problème, votre Majesté. Le Vieux Bwonsamdi est heureux de vous laisser une des âmes qu\’il a chèrement sauvées. En retour, tout ce que je demande, c\’est…', '', 38134),
(203626, 'frFR', '', 'La nécropole ronronne de puissance. Il est temps de partir.', 38134),
(203625, 'frFR', 'Nous retrouverons Anduin en temps voulu. Et nous le sortirons de là.', '', 38134),
(203624, 'frFR', 'Alors il ne faut pas nous précipiter. Sans-Entraves, continuez à saboter les opérations du Geôlier.', '', 38134),
(203623, 'frFR', 'Dans sa tour, la malveillance du Geôlier est omniprésente. S\’il pense que son prisonnier peut lui échapper, il pourrait décider de… s\’en débarrasser.', '', 38134),
(203622, 'frFR', 'Je ne sais pas. Mais il faut nous montrer prudents à chaque pas.', '', 38134),
(203621, 'frFR', '', 'Sylvanas ne faisait que jouer avec lui. Elle a parlé de tout anéantir, ça ne voulait quand même pas dire…', 38134),
(203620, 'frFR', '', 'Au moins, il est vivant et apparemment indemne. Je ne sais pas ce qu\’elle lui veut, mais il ne cédera pas.', 38134),
(203619, 'frFR', 'Je… Je ne peux pas en voir plus.', '', 38134),
(203618, 'frFR', '', 'Protégez le cœur de la Forêt, enfant des rêves. Qu\’aucun ennemi ne puisse fouler ce sol sacré.', 38134),
(203616, 'frFR', '', 'Vous avez cédé de votre propre essence pour préserver mon esprit. Je protégerai  Sylvarden jusqu\'à mon dernier souffle.', 38134),
(203612, 'frFR', '', 'Envolons-nous ! Dépêchez-vous !', 38134),
(203611, 'frFR', 'Exterminez les Drust !', '', 38134),
(203610, 'frFR', 'C\’est forcément lui.', '', 38134),
(203609, 'frFR', 'Mes tortionnaires ont évoqué un prisonnier de marque détenu dans les profondeurs de la tour du Geôlier.', '', 38134),
(203608, 'frFR', 'Cherchez encore, je vous prie. Trouvez ce roi que vous avez vu grandir.', '', 38134),
(203607, 'frFR', '', 'J\’ai une dette envers vous, $n. Et même si nous avons pu être adversaires autrefois, nous avons aujourd\’hui un ennemi commun.', 38134),
(203606, 'frFR', '', 'Bien, tout le monde est là. Marchons avant que l\’ennemi puisse organiser ses défenses.', 38134),
(203605, 'frFR', '', 'Nous allons frapper le flanc de l\’est et eux celui de l\’ouest. Puis nous prendrons Gharmal en tenaille.', 38134),
(203604, 'frFR', '', 'Les soldats de Mograine sont de fiers combattants et ils connaissent bien l\’ennemi.', 38134),
(203603, 'frFR', '', 'Nous serons avec vous jusqu\’au bout, parangon.', 38134),
(203602, 'frFR', 'Nous allons voir ce qu\’il en est.', '', 38134),
(203600, 'frFR', '', 'Je vais honorer notre accord. Gharmal va tomber sous nos coups.', 38134),
(203590, 'frFR', '', 'On dirait que Dambala est de la partie aussi. Ça ne va pas être de la tarte face à un Loa, mais j\’ai un petit tour dans ma manche !', 38134),
(203565, 'frFR', '', 'J\’ai bien peur qu\’ils n\’aient été emmenés à Tourment. C\’est la tour qu\’on aperçoit au loin, le Geôlier y enferme ses plus belles prises.', 38134),
(203564, 'frFR', 'Alors vous allez nous montrer comment la prendre d\’assaut !', '', 38134),
(203563, 'frFR', '', 'Ah, vous parlez des mortels qui sont restés ici quand votre $uami:amie; s\’est $uéchappé:échappée;.', 38134),
(203562, 'frFR', 'Il n\’y aura pas de précipitation. Le baron Maldraxxi n\’est pas le seul prisonnier à secourir.', '', 38134),
(203554, 'frFR', 'Mais que les habits, hein… et si vous cherchez d\’autres couleurs, on en trouve généralement près des tourbières. Sinon, faut voir avec ce négociant, au marché Nocturne.', '', 38134),
(203553, 'frFR', 'Vous avez de nouveaux habits pour moi…', '', 38134),
(203552, 'frFR', 'Qu\’est-ce qu\’elle a, ma tignasse ? Vous avez trouvé une nouvelle affiche dont je pourrais m\’inspirer ?', '', 38134),
(203548, 'frFR', 'Ouais ? Je peux vous aider ? À moins que vous ayez un truc pour moi ?', '', 38134),
(203547, 'frFR', '', 'J\’espère que tout le monde aime les champignons relevés. Hé hé hé !', 38134),
(203540, 'frFR', '', 'Et je vois que l\’âme sans-entraves est avec vous. J\’ai confiance en son jugement, si elle s\’allie avec vous, nous le pouvons aussi.', 38134),
(203538, 'frFR', 'Mais quand vous aurez fini d\’aider la reine, passez donc me voir de l\’Autre côté. Juste pour dire bonjour à un vieil ami, hein ?', '', 38134),
(203537, 'frFR', 'Mueh\’zala est très fort et très méchant, mais ne vous en faites pas pour mes vieux os. J\’ai encore un ou deux tours dans ma manche.', '', 38134),
(203536, 'frFR', 'On dirait que le vieux Bwonsamdi va devoir régler son compte au chef… une bonne fois pour toutes.', '', 38134),
(203527, 'frFR', '', 'Vite, lancez le sort !', 38134),
(203513, 'frFR', '', 'Qu\’y a-t-il ? Vous ne voyez pas que je suis en train de lire ?', 38134),
(203512, 'frFR', '', 'Aujourd\’hui s\’achève le règne de Gharmal ! Nous allons rassembler les troupes et préparer le Zerekriss au combat.', 38134),
(203500, 'frFR', '', 'Branchet peut vous emmener sur la nécropole, si nécessaire.', 38134),
(203481, 'frFR', '', 'Anduin est un être lumineux, je sais que sa bonté l\’aidera à traverser cette épreuve.', 38134),
(203480, 'frFR', '', 'Je suis soulagée de vous revoir, Thrall. Je vais prévenir Aggra et le seigneur régent de ce pas.', 38134),
(203478, 'frFR', 'Pouvez-vous l\’aider, auguste voix ? Nous avons encore à discuter de la signification de cette vision.', '', 38134),
(203477, 'frFR', '', 'Généralissime, cette âme porte un avertissement urgent du Primat de Maldraxxus. Il doit être porté à tous les royaumes.', 38134),
(203476, 'frFR', 'Pas maintenant. Ces visions se paient chèrement et je n\’ose pas recommencer si vite.', '', 38134),
(203475, 'frFR', '', 'Je n\’ai entendu ni Anduin, ni Baine. Généralissime, êtes-vous certain de ne pas pouvoir recommencer ?', 38134),
(203474, 'frFR', '', 'J\’ai reconnu la voix de Jaina, et ce qui devait être celle de Thrall. Ces cris… C\’est à vous glacer le sang !', 38134),
(203473, 'frFR', 'Je… Je ne peux pas en voir plus. Il serait trop dangereux d\’aller plus loin.', '', 38134),
(203467, 'frFR', '', 'Champignons frais ! Qui veut mes champignons frais ?', 38134),
(203450, 'frFR', '', 'Tout doit croître pour survivre. Les arbres, les forêts, les gens. Et tous ont besoin de nourriture, même s\’ils ne consomment pas les mêmes choses.$b$BDonner, c\’est faire preuve de bonté. Si vous m\’offrez ce présent, alors en retour, je vous offrirai ma bénédiction.', 38134),
(203448, 'frFR', 'Des fournitures provenant des ateliers, à distribuer à ma guise ? Oh, mais c\’est mon jour de chance !$b$bEt toute cette bonne fortune, je devrais être en mesure de la partager. Absolument, oui. Remettez-moi ces fournitures, et je m\’assurerai qu\’une partie de ma chance vous suive pas à pas !', '', 38134),
(203447, 'frFR', '', 'Vous feriez vraiment mieux de ne pas traîner ici. Le Geôlier n\’aime pas qu\’on lui prenne ses jouets… même s\’il n\’en voulait plus. Allez, ouste, rentrez vite à Oribos !', 38134),
(203443, 'frFR', '', 'Nous frappons d\’un élan commun. Le plan de ma baronne doit réussir.', 38134),
(203442, 'frFR', '', 'Vous montrez enfin votre misérable visage !', 38134),
(203437, 'frFR', 'Je crains qu\’une nouvelle présence soit en train d\’émerger dans Tourment. Une présence effroyable.', '', 38134),
(203436, 'frFR', 'Venez immédiatement à Oribos, Sans-Entraves. Je sens comme une force ténébreuse qui m\’appelle.', '', 38134),
(203435, 'frFR', 'Sans-Entraves, poursuivez la lutte. Explorez les plus sombres recoins de Tourment et défendez l\’Ombreterre. Et le moment venu, nous arracherons la victoire !', '', 38134),
(203434, 'frFR', '', 'Nous allons mobiliser nos alliés d\’Oribos pour rallier les congrégations. Il n\’y a qu\’avec elles que nous aurons un espoir d\’arrêter le Geôlier… et de sauver Anduin.', 38134),
(203433, 'frFR', 'Vous avez vu de quelle armée il dispose, un assaut de front n\’est pas envisageable pour l\’instant. En fonçant sous le coup de l\’émotion, on risquerait de tout perdre.', '', 38134),
(203432, 'frFR', 'Il faut mobiliser nos troupes et attaquer Tourment ! On ne peut pas abandonner Anduin !', '', 38134),
(203431, 'frFR', 'Merci à tous, mais Jaina a raison. Le Geôlier sait ce que nous avons vu et il va s\’en servir contre nous.', '', 38134),
(203430, 'frFR', '', 'Cette attaque-là a échoué, mais je crains fort que ça ne fasse que commencer.', 38134),
(203429, 'frFR', 'Bravo, Jaina. J\’ai cru qu\’on allait le perdre.', '', 38134),
(203428, 'frFR', '', 'Père !', 38134),
(203427, 'frFR', '', 'Tenez bon ! Plus qu\’un instant !', 38134),
(203426, 'frFR', 'J\’aurai ce qui me revient de droit ! La mort va vous emporter !', '', 38134),
(203425, 'frFR', 'Allez, Bolvar ! On est avec vous !', '', 38134),
(203424, 'frFR', '', 'Résiste ! Ne le laisse pas gagner !', 38134),
(203423, 'frFR', 'RAAAAAAAHHHH !', '', 38134),
(203422, 'frFR', 'Tu as échoué, comme ceux qui t\’ont précédé !', '', 38134),
(203421, 'frFR', 'Toi qui devais être le prélude à mon avènement, tu as osé me défier !', '', 38134),
(203420, 'frFR', '', 'Lame de… deuil ? Oh, non…', 38134),
(203419, 'frFR', '', 'Le Geôlier est en train d\’attaquer son esprit ! Il faut briser leur lien !', 38134),
(203418, 'frFR', 'RAAH ! Je… refuse… de… servir !', '', 38134),
(203417, 'frFR', 'C\’est la voix du Geôlier ! Comment est-ce possible ?!', '', 38134),
(203416, 'frFR', 'Je… te… vois…', '', 38134),
(203415, 'frFR', '', 'Bolvar ! Bolvar ! Qu\’est-ce qui se passe ?!', 38134),
(203414, 'frFR', 'Le lien est rompu, je ne peux plus… Oh, attendez. Il y a quelque chose qui…', '', 38134),
(203412, 'frFR', 'Si je me replonge dans ces ténèbres, je vais en payer le prix. Mais je dois savoir. Tous, nous devons savoir !', '', 38134),
(203411, 'frFR', '', 'Moi aussi, j\’ai peur pour Anduin. Mais n\’est-ce pas risqué pour le généralissime de continuer ? Le prix à payer…', 38134),
(203410, 'frFR', '', 'Anduin est encore retenu là-bas ! Est-ce qu\’il lui est arrivé quelque chose ?', 38134),
(203408, 'frFR', 'Qu\’est-ce qu\’il y a ? Vous percevez quelque chose ?', '', 38134),
(203407, 'frFR', 'Non ! Encore ?! Non !', '', 38134),
(203406, 'frFR', 'Un mal… toujours plus fort… la Domination…', '', 38134),
(203403, 'frFR', 'Darion ! Ça fait si longtemps…', '', 38134),
(203402, 'frFR', 'Mais je connais cette voix ! Impossible… Le baron serait… mon père ?!', '', 38134),
(203401, 'frFR', '', 'Je peux nous dissimuler le temps qu\’il faudra pour retrouver votre baron perdu. Venez !', 38134),
(203397, 'frFR', 'Prudence. Les yeux du Geôlier sont partout.', '', 38134),
(203396, 'frFR', 'Faites attention à vous. Trouvez Darion, il vous aidera dans vos recherches.', '', 38134),
(203395, 'frFR', '', 'Mais attention, $umortel:mortelle; : vous avez pu échapper à l\’Antre une fois, vous pourriez ne pas avoir la même chance.', 38134),
(203394, 'frFR', 'L\’auguste voix a parlé. Je vais vous conduire à l\’anneau de transfert.', '', 38134),
(203363, 'frFR', '', 'On ne peut pas la laisser emporter Anduin aussi.', 38134),
(203362, 'frFR', '', 'Arthas et Bolvar ne sont pas les seuls à avoir été affectés par cette influence maléfique. La lame a emporté bien des vies et bien des âmes.', 38134),
(203361, 'frFR', '', 'Les runes qui ornent Deuillegivre sont une magie qui n\’existait pas sur Azeroth avant l\’apparition du roi-liche, mais que l\’on retrouve partout dans l\’essence de l\’Antre. Et de Tourment.', 38134),
(203360, 'frFR', '', 'Et sur celui de mon père. Le heaume et cette lame… Est-ce qu\’ils étaient des instruments du Geôlier depuis le début ?', 38134),
(203359, 'frFR', '', 'Le spectre de mon frère plane au-dessus de nous tel un suaire. Il est responsable de ses choix, même s\’il semble aujourd\’hui que le Geôlier ait aussi pesé sur son sort.', 38134),
(203358, 'frFR', '', 'Merci, Taelia. L\’Antre est si dépourvu d\’espoir… Je n\’avais ressenti un tel néant qu\’une seule fois dans ma vie : face à Arthas à la Couronne de glace.', 38134),
(203357, 'frFR', '', 'C\’est un soulagement de vous voir saine et sauve. Vous et les autres avez dû endurer des horreurs qui dépassent l\’imagination.', 38134),
(203353, 'frFR', 'Abritez-vous derrière les blocs de glace !', '', 38134),
(203352, 'frFR', '', 'Abritez-vous derrière les blocs de glace !', 38134),
(203347, 'frFR', '', 'Retournez à Oribos. Prévenez l\’Arbitre de ce qui s\’est passé ici. Dites-lui que nous allons nous assurer en personne qu\’il n\’y ait plus d\’attaque de Maldraxxus.', 38134),
(203346, 'frFR', '', 'Les Éternels… Le Geôlier. Ce message est important, $n.', 38134),
(203333, 'frFR', 'Cinq vertus, et cinq runes gravées sur la lame… les mêmes qu\’on retrouve au Siège du Primat.', '', 38134),
(203332, 'frFR', 'L\’implacable quête de la victoire… même face à une défaite inexorable.', '', 38134),
(203331, 'frFR', 'La sagacité de percer leurs manigances à jour et la ruse pour les déjouer.', '', 38134),
(203330, 'frFR', 'L\’ambition de s\’élever au-dessus de ses ennemis et la force de les dominer.', '', 38134),
(203322, 'frFR', '', 'Nous avons beaucoup à apprendre les uns des autres.', 38134),
(203318, 'frFR', 'Si vous avez du mal à vous procurer l\’un des matériaux, consultez Dactylis. Chaque plan détaille les matériaux requis et contient des informations qui vous aideront à découvrir où les trouver.\n\nN\’oubliez pas qu\’ils sont dispersés à travers toute l\’Ombreterre. Vous devrez peut-être quitter le Bastion pour les obtenir.', '', 38134),
(203316, 'frFR', '', 'C\’est comme si c\’était fait.', 38134),
(203315, 'frFR', '', 'Tu me vois… tu me vois plus.', 38134),
(203314, 'frFR', '', 'Il y a des mauvais coups à jouer ?', 38134),
(203313, 'frFR', '', 'Boule de feu !', 38134),
(203312, 'frFR', '', 'Nécro-trait !', 38134),
(203311, 'frFR', '', 'Ha ! Et… pan !', 38134),
(203310, 'frFR', '', 'N\’hésitez pas à faire appel à moi.', 38134),
(203308, 'frFR', '', 'Je vais vous montrer un tour de magie !', 38134),
(203298, 'frFR', 'Il ne faudrait pas que ça devienne une habitude.', '', 38134),
(203293, 'frFR', 'Tout le monde à bord !', '', 38134),
(203292, 'frFR', 'Il faut absolument que je consigne tout ça !', '', 38134),
(203289, 'frFR', 'Parfait, un nouveau chapitre pour mon recueil !', '', 38134),
(203285, 'frFR', 'Goûtez-moi ça un peu !', '', 38134),
(203284, 'frFR', 'Je vous ai laissé un petit cadeau.', '', 38134),
(203282, 'frFR', 'Alors, est-ce que je sens bon ?', '', 38134),
(203279, 'frFR', '', 'Il y a de quoi faire par ici ?', 38134),
(203277, 'frFR', '', 'Je trouverai bien des ennuis ailleurs !', 38134),
(203271, 'frFR', 'L\’existence n\’est que votre perception de l\’univers.', 'L\’existence n\’est que votre perception de l\’univers.', 38134),
(203270, 'frFR', 'Voici une expérience qui a de quoi changer une âme…', 'Voici une expérience qui a de quoi changer une âme…', 38134),
(203269, 'frFR', 'Que votre âme se porte bien.', 'Que votre âme se porte bien.', 38134),
(203268, 'frFR', 'J\’ai hâte de partir à l\’aventure avec vous.', 'J\’ai hâte de partir à l\’aventure avec vous.', 38134),
(203267, 'frFR', '', 'On va se déterrer deux-trois ennuis !', 38134),
(203266, 'frFR', 'Le Primat incarnait les cinq vertus du soldat maldraxxi idéal.', '', 38134),
(203265, 'frFR', '', 'Quand vous aurez la clé, retrouvez-nous devant la cage d\’Anside. Ne tardez pas.', 38134),
(203263, 'frFR', 'On ferme !', '', 38134),
(203262, 'frFR', 'La boutique est ouverte !', '', 38134),
(203260, 'frFR', '', 'Je vais vous retaper !', 38134),
(203259, 'frFR', '', 'Je m\’occupe de ce petit bobo !', 38134),
(203258, 'frFR', '', 'Vous en avez vu d\’autres, ça va aller !', 38134),
(203257, 'frFR', '', 'Alors, on a bobo à sa mémère ?', 38134),
(203255, 'frFR', '', 'Je sais où est passé Anside ! Il a été capturé, il faut le libérer avant de continuer.', 38134),
(203253, 'frFR', 'Le trident d\’écho profond est parfait contre les sorts méchants.\n\nOn vous lance des sorts qui font mal ? On vous complique la vie ?\n\nJetez la lance, ça interrompt les sorts ! Et ça inflige un peu de dégâts physiques au passage.', '', 38134),
(203252, 'frFR', 'Et puis quoi encore ? Tu veux nous attirer des ennuis ?', '', 38134),
(203251, 'frFR', 'Ouais, je vais leur montrer comment on danse, moi !', '', 38134),
(203250, 'frFR', 'Férul et moi, on va être les invités parfaits.', '', 38134),
(203249, 'frFR', 'Vous possédez le courage, la force et la ruse pour vaincre vos ennemis.', '', 38134),
(203248, 'frFR', '', 'Ce serait un honneur de me battre à vos côtés, $p.', 38134),
(203247, 'frFR', 'On va bien voir de quel bois est fait votre prince.', '', 38134),
(203246, 'frFR', 'Attendez, il y a quelque chose qui ne va pas. On dirait que la pierre a arrêté de se charger !', '', 38134),
(203245, 'frFR', '', 'La margrave Sin\’dane a affaire à des alliés de Kel\’Thuzad et nous demande de l\’aide.', 38134),
(203244, 'frFR', '', 'Je vous attends à l\’endroit convenu. Espérons que les autres y soient déjà…', 38134),
(203233, 'frFR', 'Vous êtes $uarrivé:arrivée; si haut, mais la chance vous fait défaut.\n\nInutile de me supplier, car à part quelques sonnets… je n\’ai rien à vous proposer. Oh, ne soyez pas $uvexé:vexée.', 'Vous êtes $uarrivé:arrivée; si haut, mais la chance vous fait défaut.\n\nInutile de me supplier, car à part quelques sonnets… je n\’ai rien à vous proposer. Oh, ne soyez pas $uvexé:vexée.', 38134),
(203231, 'frFR', '', 'Mes sœurs ne laissent rien paraître, mais nous sommes toutes ravies et impatientes à l\’idée de voler à nouveau.', 38134),
(203229, 'frFR', '', 'Que voulez-vous ?', 38134),
(203226, 'frFR', '', 'Ils étaient… préparés. Je crains que Khaliiq et Anside ne soient eux aussi en danger.', 38134),
(203225, 'frFR', '', 'Vite, par ici !', 38134),
(203224, 'frFR', '', 'On est $urepérés:repérées; !', 38134),
(203223, 'frFR', '', 'Faites attention… Ils n\’ont pas encore remarqué notre présence.', 38134),
(203222, 'frFR', 'Ne vous inquiétez pas. Ensemble, nous parviendrons à donner cette réception. Les hérauts des festivités !', '', 38134),
(203221, 'frFR', '', 'Venez, $p. Le succès de la mission repose sur nous.', 38134),
(203199, 'frFR', 'Croyez-moi, un petit échantillon n\’est jamais suffisant.', '', 38134),
(203198, 'frFR', 'Merci, catalyseur de peste, mais ce petit échantillon suffira amplement à mes desseins.', '', 38134),
(203197, 'frFR', 'J\’ai concocté exactement ce qu\’il vous faut. Retrouvez-moi derrière la tour, après la réception, et je vous en fournirai.', '', 38134),
(203196, 'frFR', 'Vous êtes en quête de gelée farceuse ?', '', 38134),
(203193, 'frFR', 'Merci. Je m\’en servirai plus tard pour jouer un tour pendable à Théotar…', '', 38134),
(203192, 'frFR', 'Le prince Rénathal fait disparaître la gelée dans l\’une des poches de son manteau.', '', 38134),
(203184, 'frFR', '', 'Les centurions sont des gardiens du savoir et doivent être préservés à tout prix. Merci pour vos efforts.', 38134),
(203183, 'frFR', '', 'Le premier forgelite nous informe que l\’une de nos mnémès ne répond plus. Apportez-lui ce qu\’il faudra pour qu\’elle puisse se remettre au travail.', 38134),
(203182, 'frFR', '', 'Recrues, quand vous aurez affronté mon $uami:amie;, vous pourrez vous retirer. Si vous vous en sortez avec les honneurs, nous vous convoquerons quand nous aurons besoin de vous.', 38134),
(203181, 'frFR', '', 'Ces affiches ont fait l\’affaire. De nouvelles recrues se sont déjà présentées.', 38134),
(203173, 'frFR', 'À présent, disparaissez ! Je n\’ai que trop toléré votre présence ici.', 'À présent, disparaissez ! Je n\’ai que trop toléré votre présence ici.', 38134),
(203172, 'frFR', 'Faites vite.', 'Faites vite.', 38134),
(203152, 'frFR', 'Cela promet d\’être spectaculaire ! J\’espère que nous verrons quelqu\’un mourir pour de bon aujourd\’hui.', 'Cela promet d\’être spectaculaire ! J\’espère que nous verrons quelqu\’un mourir pour de bon aujourd\’hui.', 38134),
(203150, 'frFR', 'Oh, j\’espère que j\’affronterai un membre de la maison Sombreveine. J\’ai quelques différends à régler !', 'Oh, j\’espère que j\’affronterai un membre de la maison Sombreveine. J\’ai quelques différends à régler !', 38134),
(203149, 'frFR', 'Personne ne peut me surpasser. Mon adresse avec une rapière est légendaire.', 'Personne ne peut me surpasser. Mon adresse avec une rapière est légendaire.', 38134),
(203148, 'frFR', 'La maison Aigrevin paiera pour ce qu\’elle nous a fait dès l\’instant où nous nous retrouverons face à face !', 'La maison Aigrevin paiera pour ce qu\’elle nous a fait dès l\’instant où nous nous retrouverons face à face !', 38134),
(203147, 'frFR', '', 'Je propose de battre en retraite. Je peux nous mener dans une cachette près de la pierre d\’accès que vous aviez utilisée.', 38134),
(203146, 'frFR', 'Je cherche la gloire pour ma maison, pas pour moi-même.', 'Je cherche la gloire pour ma maison, pas pour moi-même.', 38134),
(203145, 'frFR', 'Attendez, il me faut mon épée. Porte-destin… Je vais en avoir besoin.', '', 38134),
(203143, 'frFR', 'Économise tes forces, père. On aura le temps de parler plus tard.', '', 38134),
(203142, 'frFR', 'Un concours pour intégrer une troupe d\’élite… L\’occasion idéale de liquider un bon à rien de la maison Primula. J\’en suis.', 'Un concours pour intégrer une troupe d\’élite… L\’occasion idéale de liquider un bon à rien de la maison Primula. J\’en suis.', 38134),
(203141, 'frFR', 'Mon fils…', '', 38134),
(203129, 'frFR', '', 'Dans le fort de la Perdition, l\’odeur de l\’anima trahit votre présence. Faites demi-tour !', 38134),
(203128, 'frFR', '', 'Vous n\’êtes pas encore $uparé:parée; à entrer dans les Terriers. Faites demi-tour !', 38134),
(203126, 'frFR', 'Débarrassez-moi de ce bazar avant que nos magnifiques décorations n\’en pâtissent !', '', 38134),
(203124, 'frFR', '', 'Porte-destin ? Je lui ai fabriqué quand il a été nommé baron. Il voulait une arme similaire à celle qu\’il utilisait de son vivant. À en juger par votre réaction, vous en avez reconnu la forme. Moi, si je portais le même titre que mon épée, j\’y serais sûrement très attachée.\n\nC\’est l\’une de mes plus belles créations. Alexandros s\’est donné du mal pour trouver certains de ces matériaux. J\’ignore d\’où vient le crâne, mais certainement pas de Maldraxxus.', 38134),
(203122, 'frFR', '', 'Je suis ravie de vous revoir, mon $uami:amie;. Nous vivons une époque passionnante, non ?', 38134),
(203112, 'frFR', '', 'Bonjour, $c. Je propose aux voyageurs expérimentés des moyens d\’apporter leur aide à ceux qu\’ils rencontrent en chemin.', 38134),
(203109, 'frFR', '', 'C\’est étrange. Ce n\’est pas la Gorak Zhar que je connaissais…', 38134),
(203108, 'frFR', '', 'Imbéciles ! Vous n\’êtes que des insectes face à Gorak Zhar !', 38134),
(203107, 'frFR', 'Je vous apporte des douceurs pour les invités de la cour des Braises !', '', 38134),
(203106, 'frFR', 'Bonjour, $c. Je propose aux voyageurs expérimentés des moyens d\’apporter leur aide à ceux qu\’ils rencontrent en chemin.', 'Bonjour, $c. Je propose aux voyageurs expérimentés des moyens d\’apporter leur aide à ceux qu\’ils rencontrent en chemin.', 38134),
(203100, 'frFR', '', 'Tout est prêt. Prévenez Branchet dès que vous le serez aussi.', 38134),
(203098, 'frFR', 'Deux, trois petites explosions devraient parfaitement faire l\’affaire !', '', 38134),
(203097, 'frFR', 'C\’est une cour des plus respectables, pas le marais des Affres !', '', 38134),
(203096, 'frFR', 'Oui aux lanternes, non aux torches.', '', 38134),
(203095, 'frFR', '', 'Anside va détourner l\’attention des assemblages pendant que nous pénétrons dans la nécropole.', 38134),
(203094, 'frFR', '', 'Branchet va vous mener jusqu\’à nos positions. Profitez du vol pour observer les environs.', 38134),
(203084, 'frFR', 'Je vous remercie, Sans-Entraves.', '', 38134),
(203083, 'frFR', '', 'Amusez-vous bien.', 38134),
(203082, 'frFR', '', 'L\’Accusatrice affiche un sourire en coin.', 38134),
(203081, 'frFR', 'Vous pouvez compter sur moi, Sans-Entraves ! J\’excelle dans tous les domaines !', '', 38134),
(203080, 'frFR', 'Le héraut des festivités est paré pour l\’action !', '', 38134),
(203079, 'frFR', 'Témel semble confus quelques instants, mais il tire rapidement un chapeau de fête de sa ceinture et se l\’enfonce sur la tête.', '', 38134),
(203078, 'frFR', '', 'Vos services de héraut du vice ne sont pas requis pour l\’instant. Vous aiderez notre Sans-Entraves à organiser une nouvelle réception.', 38134),
(203077, 'frFR', 'Le seul et unique héraut du vice est à votre service, Accusatrice !', '', 38134),
(203076, 'frFR', '', 'Témel !', 38134),
(203075, 'frFR', '', 'Toutefois, si le prince a besoin d\’assistance pour ces tâches…', 38134),
(203074, 'frFR', '', 'C\’est hors de question. Les âmes agitées nécessitent toute mon attention.', 38134),
(203073, 'frFR', 'Je suis impatient d\’en parler à dame Lérae !', 'Je suis impatiente d\’en parler à dame Lérae !', 38134),
(203071, 'frFR', 'Ah, Sans-Entraves. Nous parlions justement de vous.\n\nQue nous vaut cet honneur ?', '', 38134),
(203070, 'frFR', 'Oooh ! Ce sera la réception la plus grandiose de Revendreth !', 'Oooh ! Ce sera la réception la plus grandiose de Revendreth !', 38134),
(203069, 'frFR', 'Bien sûr, je m\’assurerai que vous figuriez tous sur la liste des invités.', '', 38134),
(203068, 'frFR', 'Mon vaste réseau de contacts et mes humbles facultés diplomatiques sont vôtres.', '', 38134),
(203067, 'frFR', 'Je serais honoré de vous assister, le prince Rénathal et vous.', '', 38134),
(203066, 'frFR', 'Il a l\’œil, ce croc. Vous pouvez compter sur moi.', '', 38134),
(203065, 'frFR', 'C\’est Théotar qui vous a $uconseillé:conseillée;, c\’est ça ?', '', 38134),
(203064, 'frFR', 'C\’est pas tout le monde qui confierait une tâche pareille à un purotin.', '', 38134),
(203062, 'frFR', 'Vous avez déjà entendu Calice Perdu ?\n\nVraiment émouvant. Ces mélodies, ça vous hante.\n\nJ\’ai commencé à lire leur biographie. Enfin un bouquin digne de c\’nom !', '', 38134),
(203061, 'frFR', 'Parlons, si vous le voulez bien ?', '', 38134),
(203060, 'frFR', 'Je serai notre critique le plus sévère, et notre défenseur le plus acharné !', '', 38134),
(203059, 'frFR', 'Viandes fraîches ! Produits importés ! Les serveurs purotins les plus propres !', '', 38134),
(203058, 'frFR', 'Bien sûr, je vous garantis les ingrédients de la meilleure qualité…', '', 38134),
(203056, 'frFR', '<Stéfan le Pinailleur interrompt sa dictée à son parchemin flottant et vous regarde.>\n\nOui ? Avez-vous quelque chose à ajouter sur l\’arôme complexe de ce steak ?', '', 38134),
(203055, 'frFR', 'Pour commencer, il nous faut plus de thé. Un déluge de thé !', '', 38134),
(203054, 'frFR', 'Oh, c\’est comme si j\’étais né pour cet évènement. Je vous apprendrai tout ce que vous devez savoir !', '', 38134),
(203053, 'frFR', 'Mais je suis ravi, moi aussi. Je compte sur vous pour rafraîchir mes connaissances en matière de protocole.', '', 38134),
(203052, 'frFR', 'Vous êtes vous-même à l\’origine de bon nombre de ces rumeurs, Théotar.', '', 38134),
(203051, 'frFR', 'Mon cher Rénathal, les rumeurs disent vrai. Nous donnerons la réception ici, à Tombe-Vice !', '', 38134),
(203050, 'frFR', 'Je suis dans tous mes états. Cette nouvelle ne peut pas attendre !', '', 38134),
(203049, 'frFR', '', 'Nous ferons notre rapport à la cour des Braises, selon vos instructions.', 38134),
(203047, 'frFR', '', 'Il nous reste fort à faire.', 38134),
(203038, 'frFR', 'Venez. Je vais vous laisser une chance de m\’expliquer ce que vous faites dans ma tanière.', 'Venez. Je vais vous laisser une chance de m\’expliquer ce que vous faites dans ma tanière.', 38134),
(203037, 'frFR', 'Attendez… Ne tirez pas ! Je connais cet individu.', 'Attendez… Ne tirez pas ! Je connais cet individu.', 38134),
(203036, 'frFR', 'Qui ose pénétrer dans mon domaine ?', 'Qui ose pénétrer dans mon domaine ?', 38134),
(203035, 'frFR', 'Vous n\’irez pas plus loin !', 'Vous n\’irez pas plus loin !', 38134),
(203029, 'frFR', '', 'C\’est sans issue. Ha, eh bien montrez-nous donc cette puissance dont tout le monde parle.', 38134),
(203028, 'frFR', 'Que… Qui est là ? Il faut… avertir… le margrave…', '', 38134),
(203027, 'frFR', '', 'Plus que vous. C\’est tout ce que vous avez besoin de savoir.', 38134),
(203026, 'frFR', 'Depuis combien de temps êtes-vous retenue ici ?', '', 38134),
(203022, 'frFR', '', 'Le bosquet est miné par la pénurie et le conflit. S\’il refleurit, ses esprits fleuriront avec lui.', 38134),
(203015, 'frFR', 'Lysonia est affaiblie, mais je ne suis toujours pas convaincu que nous soyons de taille à la vaincre.\n\nPourtant, il nous faut essayer. Puis-je compter sur vous ?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203014, 'frFR', 'Tous ces morts… et le parangon concentre toute son attention sur sa Main.\n\nNous devons intervenir, $umortel:mortelle;.', '', 38134),
(203009, 'frFR', 'Nous connaissons les risques. Mais il faut essayer, il n\’y a pas le choix.', '', 38134),
(203008, 'frFR', '', 'Un seul chemin mène en ce terrible royaume. Celui que doivent emprunter toutes les âmes vouées à la damnation.', 38134),
(203007, 'frFR', 'Auguste voix, nous voudrions pénétrer dans l\’Antre.', '', 38134),
(203006, 'frFR', 'Je ne sais pas comment retrouver ce baron disparu, ni nos alliés encore en vie. Mais je pense pouvoir vous mener à Darion Mograine. Ce sera un début.', '', 38134),
(203005, 'frFR', 'Il semble que vous deviez à nouveau affronter les horreurs de l\’Antre, $n.', '', 38134),
(202998, 'frFR', 'J\’ai assisté à bien des réceptions dans ma vie.\n\nMais jamais par choix.', '', 38134),
(202997, 'frFR', 'Il n\’y a rien de tel que de déguster une bonne tasse de thé entre amis lors d\’une réception à la cour.\n\nEn cas de doute, servez plus de thé. C\’est la solution à tous les problèmes !', '', 38134),
(202996, 'frFR', 'Je n\’aime peut-être pas participer à des réceptions autant que Théotar, et je n\’apprécie certainement pas leur organisation comme Témel.\n\nMais j\’ai confiance en mes propres capacités. Je m\’efforce de gagner des alliés depuis la naissance de ce royaume.\n\nLes réceptions de la cour des Braises seront un outil puissant pour développer le renom et l\’influence de notre congrégation.\n\nJe suis ravi de vous avoir à mes côtés dans cette nouvelle entreprise.', '', 38134),
(202991, 'frFR', '', 'Je me rends à Nurakkir. Nous nous retrouverons là-bas.', 38134),
(202990, 'frFR', 'Arh ! À vous… d\’en… finir…', '', 38134),
(202989, 'frFR', 'Vous cherchez un truc ?', '', 38134),
(202988, 'frFR', 'Hé ! Qu\’est-ce que vous regardez ?', '', 38134),
(202987, 'frFR', 'Qu\’est-ce qui vous amène à la porte de Nourman ?', '', 38134),
(202985, 'frFR', '', 'Nos artisans travaillent très dur. Je peux vous garantir que la cour des Braises sera splendide !', 38134),
(202984, 'frFR', 'Je déclare la réception de la cour des Braises officiellement ouverte !', '', 38134),
(202983, 'frFR', 'Tout est si propre ! Les préparatifs seront terminés en un rien de temps !', '', 38134),
(202982, 'frFR', 'Il nous reste beaucoup à nettoyer avant de pouvoir tenir notre première véritable réception !', '', 38134),
(202981, 'frFR', 'Eh bien, ce n\’était pas le genre de réception auquel je suis habitué, mais si les autres invités ont apprécié…', '', 38134),
(202980, 'frFR', 'Quelle merveilleuse réception protocolaire ! Gubbins et Tubbins doivent absolument être de la prochaine fête !', '', 38134),
(202979, 'frFR', 'Ma mission est accomplie.', '', 38134),
(202978, 'frFR', 'Vous avez réussi l\’impensable, Sans-Entraves ! Vous avez organisé une réception qui m\’a réellement diverti. Quel tour de force !', '', 38134),
(202977, 'frFR', 'Cela va de soi, Témel. Commencez donc à organiser l\’envoi des premières invitations.', '', 38134),
(202976, 'frFR', 'Je vais préparer tout le nécessaire pour nos rituels à la cour des Braises.', '', 38134),
(202975, 'frFR', 'Crache-Vase grommelle en poussant sa brouette.', '', 38134),
(202974, 'frFR', 'Et c\’est pour cela que tu n\’es pas invité, Crache-Vase. Allez, du vent !', '', 38134),
(202973, 'frFR', 'Moi, j\’aime bien quand c\’est tout sale !', '', 38134),
(202972, 'frFR', 'Oui, oui, mais tâchez de nettoyer ce que vous pouvez en passant. Les purotins ont à nouveau tout sali !', '', 38134),
(202971, 'frFR', 'Je vous demanderai de me rejoindre sur la scène des tributs d\’ici quelques minutes, pour la cérémonie de clôture.', '', 38134),
(202970, 'frFR', 'Assez de sermons, Témel. Laissez donc notre Sans-Entraves profiter pleinement de cette répétition !', '', 38134),
(202969, 'frFR', 'En cas de conflit direct, comme ici entre Théotar et le maître de guet Boromod, vous aurez à choisir sur qui concentrer vos efforts.', '', 38134),
(202968, 'frFR', 'Une activité qui enchante l\’un de vos invités pourrait être mortellement ennuyeuse pour un autre.', '', 38134),
(202967, 'frFR', 'Le fait est, Sans-Entraves, que tout le monde ne se fait pas la même idée d\’une réception idéale.', '', 38134),
(202966, 'frFR', 'Mon cher maître de guet, j\’ai bien peur que vous ne vous fourvoyiez. Voyez-vous, j\’ai assisté aux dîners les plus fins de Revendreth, et…', '', 38134),
(202965, 'frFR', 'Ça, c\’est une fête, une vraie ! Plus de duels et moins de thé, voilà ce qu\’il nous faut !', '', 38134),
(202934, 'frFR', '', 'C\’est avec plaisir que je vous accorderai ce don. Si vous voulez bien me suivre…', 38134),
(202933, 'frFR', '', 'Et ainsi, $p, je vous donne la bénédiction de la reine. Levez-vous.', 38134),
(202932, 'frFR', '', '« Je t\’offre cette bénédiction, a répondu la reine. Rejoins les créatures de la forêt. Prends la forme qu\’il te faudra prendre. »', 38134),
(202931, 'frFR', '', '« Et comment puis-je m\’acquitter de cette tâche, ma reine ? Car ce qui était mon corps n\’est plus. »', 38134),
(202930, 'frFR', '', '« Veille sur ces esprits pour l\’éternité. Le cycle doit perdurer ou tout sera perdu. »', 38134),
(202929, 'frFR', '', 'L\’âme s\’est inclinée. « Comment puis-je vous servir, ma reine ? »', 38134),
(202928, 'frFR', '', '« Alors sois la bienvenue en mon royaume, » a répondu la reine.', 38134),
(202927, 'frFR', '', '« On m\’a envoyée à vous. Car j\’avais consacré ma vie au service de la nature. »', 38134),
(202926, 'frFR', '', 'La reine l\’a accueillie à l\’orée du royaume. « Toi qui n\’es ni esprit ni Faë, pourquoi venir en ma forêt ? »', 38134),
(202925, 'frFR', '', 'Il y a bien longtemps, après l\’émergence des arbres et des Faë nocturnes, la première âme mortelle est apparue en Sylvarden.', 38134),
(202923, 'frFR', 'Continuez à fabriquer de nouveaux objets avec Dactylis. Trouvez d\’autres souvenirs à capturer et de nouvelles pièces d\’équipement à essayer !\n\nSi vous n\’avez jamais vaincu un souvenir donné ou si vous butez sur un niveau de difficulté, faites l\’impossible pour triompher de cette épreuve et recevoir une juste récompense. En fabriquant et en utilisant des charmes, vous donnerez un avantage non négligeable à vos âmes liées.\n\nEssayez de nouveaux défis ! Vous trouverez ici de nombreux vendeurs proposant des récompenses exceptionnelles réservées à ceux qui surmontent certaines épreuves sur la voie de l\’Ascension. Essayez de toutes les obtenir !', '', 38134),
(202921, 'frFR', 'Certaines âmes liées peuvent être avantagées face à des souvenirs précis. Choisissez donc celle qui vous semble la plus à même d\’en venir à bout.\n\nEssayez également de lui fournir de l\’équipement et des charmes susceptibles de faire pencher la balance en sa faveur. L\’équipement lui conférera une technique active, tandis que les charmes lui offriront des améliorations passives. Cela peut vraiment changer le cours de la bataille. Je peux vous expliquer tout cela plus en détail, si vous voulez.\n\nPour finir, vos âmes liées gagneront en force à mesure que vous augmenterez votre renom auprès de votre congrégation. En cas de difficulté, prenez le temps de soigner votre réputation avant de réessayer.', '', 38134),
(202919, 'frFR', '', 'Nous serons un duo puissant, mais pas des $uamis:amies;. Je n\’ai pas d\’amis.', 38134),
(202914, 'frFR', 'Contrairement à l\’équipement, qui est considéré comme actif, les charmes renforcent vos âmes liées de façon passive. Le pouvoir qu\’ils abritent est à usage unique, alors pensez à en fabriquer plusieurs !\n\nBien que vous ne puissiez porter qu\’un seul charme de chaque catégorie, rien ne vous empêche de combiner les différents types selon vos envies.', '', 38134),
(202912, 'frFR', 'Vous serez parfois $uconfronté:confrontée; à des défis que vos âmes liées ne pourront surmonter seules. C\’est là que l\’équipement entre en jeu en leur conférant une technique de combat active supplémentaire, une ruée par exemple.\n\nVous pourrez utiliser indéfiniment les pièces d\’équipement que vous fabriquerez. Adressez-vous à Paolone dans le colisée pour en équiper votre âme liée. Mais sachez que cette dernière ne pourra porter qu\’une seule pièce à la fois, alors choisissez celle qui l\’aidera au mieux à affronter son problème.', '', 38134),
(202910, 'frFR', 'La voie de l\’Ascension mène à la gloire et à l\’honneur, mais nous devons en contrepartie faire acte d\’humilité. Les médaillons de service sont la preuve de tels actes et servent à relever les défis de la voie.\n\nIls peuvent être obtenus en servant le Bastion. Par exemple en répondant aux appels, en participant à des chasses au trésor ou en éliminant des ennemis rares. Dactylis vous en remettra également en échange de matériaux.', '', 38134),
(202909, 'frFR', 'Très peu pour moi, les longues conversations. Comment puis-je vous aider ?', 'Très peu pour moi, les longues conversations. Comment puis-je vous aider ?', 38134),
(202908, 'frFR', 'C\’est ma première affectation à une réception. Je ferai de mon mieux.', 'C\’est ma première affectation à une réception. Je ferai de mon mieux.', 38134),
(202906, 'frFR', 'Vous pensez qu\’il y aura des restes ?', 'Vous pensez qu\’il y aura des restes ?', 38134),
(202905, 'frFR', 'Les nouveaux plans s\’obtiennent en terrassant des souvenirs dans le colisée. Apportez-les ensuite à Dactylis pour fabriquer de nouveaux appâts, charmes et pièces d\’équipement. Essayez de tous les obtenir et d\’expérimenter différentes combinaisons.', '', 38134),
(202903, 'frFR', 'Je n\’attends qu\’un ordre.', 'Je n\’attends qu\’un ordre.', 38134),
(202902, 'frFR', 'La première chose à faire est de renforcer le miroir des âmes afin d\’attirer des souvenirs plus puissants. Vous devrez pour cela améliorer le sanctum de votre congrégation.\n\nSachez ensuite que certaines épreuves sont plus difficiles que d\’autres et nécessitent d\’avoir terrassé un certain nombre de boss au préalable.\n\nEt bien sûr, vous ne pourrez vous attaquer à un souvenir de niveau supérieur qu\’après avoir vaincu ses manifestations précédentes.', '', 38134),
(202900, 'frFR', 'Dactylis vous préviendra quand vous aurez de nouveaux souvenirs à capturer. Elle vous aidera également à fabriquer un appât spécifique pour chacun d\’eux ainsi qu\’un miroir des âmes. Donnez-les ensuite à Dactylis et elle vous expliquera comment rapporter ce souvenir au colisée pour l\’entraînement de vos âmes liées.', '', 38134),
(202899, 'frFR', 'Tout fauteur de troubles potentiel me trouvera sur sa route.', 'Tout fauteur de troubles potentiel me trouvera sur sa route.', 38134),
(202897, 'frFR', 'Je me tiens à votre disposition.', 'Je me tiens à votre disposition.', 38134),
(202896, 'frFR', 'Comment puis-je me rendre utile ?', 'Comment puis-je me rendre utile ?', 38134),
(202895, 'frFR', 'Pour Tombe-Vice.', 'Pour Tombe-Vice.', 38134),
(202893, 'frFR', '', 'Nous sommes en train de rassembler les créatures sauvages. Nous nous tiendrons prêts à répondre à votre convocation à la cour des Braises.', 38134),
(202881, 'frFR', 'L\’heure est venue, un nouvel aspirant doit emprunter la voie. Qui relèvera le gant ?', '', 38134),
(202880, 'frFR', 'Voyez, Kyrians, une nouvelle âme honore la tradition sacrée et s\’élance sur la voie de l\’Ascension !', '', 38134),
(202868, 'frFR', 'Tout le monde n\’a pas vos goûts, Théotar. Nous devons adapter nos réceptions aux penchants de nos invités.', '', 38134),
(202867, 'frFR', 'Une série de combats ferait souffler un vent de fraîcheur sur ce protocole rigide.', '', 38134),
(202866, 'frFR', 'Si vous voulez me voir sourire, allez plutôt défier l\’une de mes jeunes recrues.', '', 38134),
(202864, 'frFR', 'Bah ! Si vous saviez ce que je pense de tout ça !', '', 38134),
(202863, 'frFR', 'Je n\’en suis pas si sûr, Théotar… Boromod ne m\’a jamais semblé très intéressé par tout ce formalisme…', '', 38134),
(202862, 'frFR', 'À commencer par notre taciturne maître de guet. Il appréciera forcément cette attention protocolaire.', '', 38134),
(202861, 'frFR', 'Tout le monde devrait avoir une tasse de thé !', '', 38134),
(202854, 'frFR', 'Merci, Sans-Entraves. Le respect de l\’étiquette est la marque d\’une cour digne de ce nom, et cela, peu importe son cadre.', '', 38134),
(202852, 'frFR', '', 'Faites vaillamment face à l\’adversité.', 38134),
(202851, 'frFR', '', 'Un petit conseil, Pélagos : il ne faut pas laisser l\’adversaire trop approcher.', 38134),
(202842, 'frFR', '', 'L\’Hallali ne recrute que des combattants implacables parés à relever tous les défis. Voyons si vous vous en tirez.', 38134),
(202837, 'frFR', '', 'Aïe, les choses paraissent bien mal engagées.', 38134),
(202821, 'frFR', '', 'Voilà une petite leçon d\’humilité.', 38134),
(202815, 'frFR', 'Je vais préparer une autre théière, au cas où.', '', 38134),
(202814, 'frFR', 'Veuillez servir le thé tant qu\’il est chaud.', '', 38134),
(202813, 'frFR', '', 'Quel spectacle ! Je savais dès notre première rencontre que vous feriez de grandes choses !', 38134),
(202812, 'frFR', '', 'Aspirants ! Venez rendre hommage à notre $uami:amie; $p qui s\’est $udressé:dressée; contre le puissant Athanos !', 38134),
(202811, 'frFR', '', 'La lance est incroyablement lourde. Vous ne pourrez la soulever qu\’un bref instant. Choisissez bien votre moment !', 38134),
(202809, 'frFR', 'Vous avez fait de beaux progrès, $p. Mon souvenir est à vous.', '', 38134),
(202808, 'frFR', '', 'On dirait qu\’il vous reste des choses à apprendre.', 38134),
(202807, 'frFR', '« Onéreux », c\’est mon parfum favori. Vous le saviez donc, mon cher Témel ?', '', 38134),
(202806, 'frFR', 'Sans-Entraves, veuillez servir à Théotar une tasse de notre thé le plus onéreux.', '', 38134),
(202804, 'frFR', 'Qu\’en pensez-vous ? Sommes-nous prêts à ouvrir les portes de la cour des Braises à des personnalités extérieures ?', '', 38134),
(202803, 'frFR', 'Vous voulez du protocole ? Aucun problème !', '', 38134),
(202802, 'frFR', 'Toutefois, force m\’est de confesser que les personnalités les plus influentes s\’attendent généralement à des… réceptions plus protocolaires.', '', 38134),
(202801, 'frFR', 'C\’est certainement un bon début, mon cher prince.', '', 38134),
(202800, 'frFR', 'Théotar, vous avez toujours apprécié les plaisirs de ce genre de mondanités.', '', 38134),
(202799, 'frFR', 'Je suis là pour vous servir en toutes circonstances.', '', 38134),
(202798, 'frFR', 'Le prince Rénathal adresse un clin d\’œil au maître de guet Boromod.', '', 38134),
(202797, 'frFR', 'Mondains et aristocrates ont l\’air ravis eux aussi, même si notre bon maître de guet n\’a pas la mine aussi réjouie.', '', 38134),
(202796, 'frFR', 'Je suis impressionné, Témel. Avec l\’âme sans-entraves, vous avez déjà accompli des miracles !', '', 38134),
(202795, 'frFR', 'Voici le prince Rénathal avec notre invité d\’honneur !', '', 38134),
(202794, 'frFR', 'Parfait. Ensuite, faites de même avec la cuve d\’enzyme.', '', 38134),
(202793, 'frFR', '', 'Ces créatures sont magnifiques, vous ne trouvez pas ? Et elles le savent ! Voyez comme elles sont fières.', 38134),
(202792, 'frFR', '', 'Il y a bien longtemps, nous avons puisé dans sa magie pour nous assurer que le Proscrit ne pourrait jamais s\’échapper de l\’Antre.', 38134),
(202790, 'frFR', 'J\’ai mis tous les purotins au travail. L\’endroit doit être parfait pour le prince Rénathal.', '', 38134),
(202789, 'frFR', 'J\’aurais bien besoin de votre aide pour cette répétition de la cour des Braises.', '', 38134),
(202786, 'frFR', 'Ah, ma petite étoile scintillante… Je vais le faire.', '', 38134),
(202785, 'frFR', '', 'Mais aujourd\’hui, je te demande ça. Dis-nous s\’ils sont encore vivants.', 38134),
(202784, 'frFR', '', 'Je ne t\’ai plus rien demandé depuis mon enfance, quand je n\’étais qu\’une petite fille que tu as choisi d\’éloigner.', 38134),
(202783, 'frFR', '', 'J\’ai choisi de voyager jusqu\’au royaume de la mort pour retrouver le père que je croyais avoir perdu. Bolvar Fordragon, l\’un des plus vaillants héros d\’Azeroth.', 38134),
(202782, 'frFR', 'Taelia, tu ne saisis pas les risques. Si jamais je plonge mon regard dans l\’Antre…', '', 38134),
(202781, 'frFR', '', 'Je t\’en prie ! On a besoin de savoir si nos amis peuvent encore être sauvés !', 38134),
(202780, 'frFR', 'Une nouvelle vie m\’attend ! Voire des nouvelles vies ? Non, ne mettons pas la charrue avant les bœufs. Adieu !', '', 38134),
(202779, 'frFR', 'Bonne chance pour cette histoire d\’arène dont vous parliez tantôt.', '', 38134),
(202778, 'frFR', 'Parfait ! Pas du tout ce à quoi nous nous attendions, mais la science est parfois imprévisible !', '', 38134),
(202775, 'frFR', 'Parfait, il en sera ravi.', '', 38134),
(202774, 'frFR', 'Ce gentilhomme a du goût ! Je lui préparerai mon meilleur thé.', '', 38134),
(202773, 'frFR', 'S\’il vous reste du thé dans votre fourbi, Stefan, je suis sûr que notre invité l\’appréciera.', '', 38134),
(202772, 'frFR', 'Le prince Rénathal sera accompagné ? Bonté divine, je vais préparer une boisson spéciale pour l\’occasion.', '', 38134),
(202771, 'frFR', 'Nous finirons le nettoyage plus tard. Le prince Rénathal sera bientôt là avec son invité.', '', 38134),
(202770, 'frFR', 'Ça brille comme un sou neuf ! Vous autres purotins pourriez en prendre de la graine !', '', 38134),
(202769, 'frFR', 'Belle débauche d\’énergie, Sans-Entraves ! Maintenant, si vous voulez bien nettoyer le reste avant l\’arrivée de nos invités…', '', 38134),
(202768, 'frFR', 'C\’est déjà mieux ! Ah, l\’impact de la propreté !', '', 38134),
(202767, 'frFR', 'Vous deux, prenez la patrouille du sud. Je m\’occupe des civils.', '', 38134),
(202766, 'frFR', '', 'Bon débarras. Ne faites pas confiance à ce triste personnage.', 38134),
(202765, 'frFR', 'Ça marche, patron.', '', 38134),
(202763, 'frFR', 'Je me sentirais mieux pour cette répétition si le coin rafraîchissements était plus propre.', '', 38134),
(202762, 'frFR', 'Ne vous préoccupez pas des mondains, concentrez vos efforts sur les invités de marque.', '', 38134),
(202761, 'frFR', 'Oh, j\’allais oublier ! J\’ai invité certains des membres des congrégations pour un galop d\’essai.', '', 38134),
(202760, 'frFR', 'Mais tout est dans un tel état ! Un grand nettoyage s\’impose si nous voulons en faire un lieu de réception digne de ce nom.', '', 38134),
(202759, 'frFR', 'Ah, vous voilà, Sans-Entraves.', '', 38134),
(202758, 'frFR', 'Si vous avez besoin de conseils, n\’hésitez pas à revenir me voir. Puissent vos âmes liées et vous suivre fièrement la voie.', '', 38134),
(202757, 'frFR', '', 'Dactylis peut vous aider à obtenir de nouveaux souvenirs contre lesquels vous entraîner et vous conférer des charmes et de l\’équipement pour donner l\’avantage à vos âmes liées.', 38134),
(202756, 'frFR', '', 'La suite ne dépend plus que de vous et de vos âmes liées, $n. La gloire vous attend, mais il vous reste beaucoup à apprendre.', 38134),
(202755, 'frFR', '', 'Toute nouvelle épreuve de la voie nécessitera désormais un médaillon de service, preuve du devoir accompli au service du Bastion. La voie mène à la gloire, mais nous devons garder les pieds sur terre. Tenez, en voici quelques-uns.', 38134),
(202752, 'frFR', 'Je garde équipement. Venez me parler dans colisée pour l\’utiliser !', '', 38134),
(202741, 'frFR', 'Voilà. Ce téléporteur vous emmènera à l\’Anneau de transfert.', '', 38134),
(202735, 'frFR', 'Rien ne vaut le présent, $p. Utilisez votre miroir et finissons-en !', '', 38134),
(202734, 'frFR', 'Je suis… nous sommes… moi ? Nous sommes tous moi !', '', 38134),
(202733, 'frFR', 'Que… qu\’est-ce qui m\’est arrivé ? Où suis-je ?', '', 38134),
(202727, 'frFR', 'Des bougies sur la terrasse ! Des sièges près des rafraîchissements ! Des lanternes pour éclairer la scène !', '', 38134),
(202709, 'frFR', '', 'Avec plaisir.', 38134),
(202706, 'frFR', 'Pour ça, il faudra me passer sur le corps !', '', 38134),
(202701, 'frFR', '', 'Vous allez amèrement regretter d\’avoir défié la maison des Rituels !', 38134),
(202698, 'frFR', 'Vous travaillez dur et bien…', '', 38134),
(202697, 'frFR', 'Maintenant, Tubbins fait thé. C\’est le meilleur thé ! Tout le monde le dit !', '', 38134),
(202696, 'frFR', 'Noooon !', '', 38134),
(202694, 'frFR', 'Oh, ça s\’annonce mal, je vous l\’accorde.', '', 38134),
(202693, 'frFR', 'Oh, allez, soyons fous ! Versons trois bonnes larmes !', '', 38134),
(202692, 'frFR', 'Et pour finir, le catalyseur que vous avez dégotté ! On mélange le tout avec un petit fouet et…', '', 38134),
(202691, 'frFR', 'Juste une pincée de poudre d\’os… une larme de limon…', '', 38134),
(202690, 'frFR', 'Admirez le produit de siècles de recherches alchimiques et de stagiaires sous-payés !', '', 38134),
(202688, 'frFR', 'Je n\’atteindrai peut-être pas l\’Ascension aussi vite que d\’autres, $p, mais la voie que je suis me rend heureux.\n\nRares sont les Kyrians qui discutent de leur passé ou même qui s\’en souviennent. Il me semble que nous avons beaucoup à apprendre en la matière.\n\nQuand j\’ai tout raconté à Kléia, elle m\’a écouté, comme vous l\’avez fait. Et son amitié ne s\’est pas plus démentie que la vôtre.\n\nMerci. Je suis content d\’avoir pu partager mon histoire avec vous.', '', 38134),
(202686, 'frFR', 'Mais en arrivant au Bastion, j\’ai découvert que j\’étais devenu un aspirant…\n\n<Pélagos se désigne d\’un geste de la main.>\n\nUN aspirant, vous comprenez ? De sexe masculin !\n\nJe me suis vraiment senti moi-même ! Celui que j\’étais à l\’intérieur correspondait pour la première fois à mon apparence !\n\nTout ce que j\’avais éprouvé de mon vivant avait enfin trouvé un sens.', '', 38134),
(202684, 'frFR', 'J\’apprécie sincèrement de pouvoir me confier à vous. En tant qu\’âmes liées, je sais que je peux vous faire confiance.\n\n<Pélagos prend une profonde inspiration, puis expire lentement.>\n\nJ\’étais une femme de mon vivant. Je ne me souviens ni du nom que je portais ni même de ma race, mais j\’ai… j\’ai toujours su que mon apparence et ma personnalité réelle étaient en porte-à-faux.\n\nMais cela ne m\’était jamais apparu aussi clairement à l\’époque. J\’ai lutté toute ma vie avec cette identité…', '', 38134),
(202682, 'frFR', 'À l\’exception de l\’un d\’eux.\n\nLe raviver n\’est guère plaisant. Je sais que je devrais y renoncer, mais… je veux m\’en souvenir… Ou peut-être en ai-je besoin ?\n\nIl fait trop partie de moi pour que je l\’oublie.', '', 38134),
(202680, 'frFR', 'De moins en moins.\n\nCertains de mes souvenirs s\’estompent à mesure que mon ancienne vie se fait plus lointaine, et j\’en ai sacrifié bien davantage de mon plein gré pour accomplir mon devoir.\n\nÀ l\’exception de…', '', 38134),
(202677, 'frFR', 'Ils me croyaient fou ! En fait, ils le croient toujours !\n\nMais tout ça va changer aujourd\’hui !', '', 38134),
(202667, 'frFR', 'Le travail fait mûrir même les plus formidables d\’entre nous.', '', 38134),
(202665, 'frFR', 'Pas si vite, jeune pousse.', '', 38134),
(202663, 'frFR', '', 'Ce miroir qui est derrière vous renferme tous les souvenirs collectés. Il vous permet de choisir votre adversaire.', 38134),
(202662, 'frFR', 'Pour atteindre les îles, il faut emprunter les cercles de champignons. L\’accès est restreint, mais vous pouvez essayer.', 'Pour atteindre les îles, il faut emprunter les cercles de champignons. L\’accès est restreint, mais vous pouvez essayer.', 38134),
(202661, 'frFR', 'Aujourd\’hui, Pélagos fait ses premiers pas sur la voie de l\’Ascension. Puisse-t-il marcher sur les traces des innombrables Kyrians qui l\’ont précédé.', '', 38134),
(202659, 'frFR', '', 'Prévenez-moi quand vous voudrez que j\’active l\’appareil. Il ne nous protégera pas longtemps.', 38134),
(202658, 'frFR', '', 'Noyez-vous dans la terreur !', 38134),
(202653, 'frFR', 'Vos âmes liées et vous marchez sur les traces de nombreux prédécesseurs. Il n\’y a pas plus grand honneur que d\’emprunter la voie de l\’Ascension !', '', 38134),
(202627, 'frFR', '', 'Tout de suite.', 38134),
(202626, 'frFR', '', 'Je perçois la présence d\’esprits kaldoreï dans votre cristal, $n. Relâchez-les dans les bois de Sylvarden.', 38134),
(202621, 'frFR', '', '<Les yeux de la reine de l\’hiver brillent de puissance contenue. Elle pose sur vous un regard approbateur.>\n\nVous vous êtes bien $ubattu, mortel:battue, mortelle;.\n\nVenez. Les gorms dévoreront leurs os.', 38134),
(202619, 'frFR', '', 'Raaaaarh ! Indigne reine, personne ne peut vaincre les Drust !', 38134),
(202618, 'frFR', '', 'Venez, $umortel:mortelle;. Nous allons en finir avec cette vermine.', 38134),
(202616, 'frFR', '', 'Je ne dois pas perdre sa trace. Nous nous retrouverons plus loin.', 38134),
(202615, 'frFR', '', 'Voyez comme la magie des bosquets se dresse contre vous !', 38134),
(202614, 'frFR', '', 'Mais attention, une terrible force est en train d\’essayer de l\’emporter. J\’espère que Shandris et vous réussirez à l\’en libérer.', 38134),
(202613, 'frFR', 'Shandris, c\’est peut-être notre seul espoir de sauver Tyrande.', '', 38134),
(202602, 'frFR', 'À votre demande, nous présenterons à la réception toute une gamme d\’objets fascinants provenant du monde des mortels. Vos invités souhaiteront peut-être en faire l\’acquisition.', 'À votre demande, nous présenterons à la réception toute une gamme d\’objets fascinants provenant du monde des mortels. Vos invités souhaiteront peut-être en faire l\’acquisition.', 38134),
(202600, 'frFR', '', 'Il semble que vous tombiez juste à temps, Sans-Entraves.', 38134),
(202599, 'frFR', '', 'L\’ennemi avance toujours plus. Le front ne tiendra pas longtemps.', 38134),
(202588, 'frFR', 'C\’est quand vous voudrez. Oh, je suis dans tous mes états !', '', 38134),
(202582, 'frFR', '', 'Marileth va vous enseigner comment on lie deux âmes et ce que cela implique.', 38134),
(202581, 'frFR', 'Ce fleuve… les vivants n\’y ont pas leur place. Il faut l\’éviter à tout prix.', '', 38134),
(202580, 'frFR', 'Regardez comme ils se démènent ! Bientôt, la maison des Élus et ses valets kyrians iront rejoindre nos stocks de chair.', 'Regardez comme ils se démènent ! Bientôt, la maison des Élus et ses valets kyrians iront rejoindre nos stocks de chair.', 38134),
(202576, 'frFR', '', 'Ne gâchez pas cette occasion, Sans-Entraves.', 38134),
(202575, 'frFR', 'Porter le pouvoir du Primat est un grand honneur.', '', 38134),
(202567, 'frFR', '', 'C\’est fort aimable à vous de nous rejoindre.', 38134),
(202565, 'frFR', 'Sylvanas ! Mais qu\’est-ce qu\’elle lui veut ?', '', 38134),
(202561, 'frFR', 'Recherchez-vous un article en particulier ?', 'Recherchez-vous un article en particulier ?', 38134),
(202559, 'frFR', 'Cette créature mécanique semble avoir besoin d\’être réparée.', '', 38134),
(202558, 'frFR', 'La clé… C\’est… Le Marqué…', '', 38134),
(202557, 'frFR', '', 'Je vais m\’en occuper.', 38134),
(202556, 'frFR', 'Ils nous bloquent le chemin !', '', 38134),
(202555, 'frFR', '', 'Je connais quelques astuces qui vous permettront de mieux survivre ici. Si vous vous acharnez à attaquer ses serviteurs, le Geôlier le remarquera. Il vaut mieux les éviter autant que possible.', 38134),
(202554, 'frFR', '', 'Votre présence ici ne fait qu\’attirer l\’attention et ce n\’est pas bon pour mes affaires. Sans vouloir vous offenser.', 38134),
(202553, 'frFR', 'Et pourquoi nous aider, négociante ?', '', 38134),
(202552, 'frFR', '', 'Quant à moi, je suis Ve\’nari, une humble négociante prisonnière de ce cruel royaume. Mais j\’ai une proposition à vous faire, venez !', 38134),
(202551, 'frFR', '', 'Ah, je me demandais ce que cherchaient les antreliges. Vous êtes cette âme mortelle qui est venue gâcher la fête du Geôlier.', 38134),
(202549, 'frFR', 'La diversion s\’est avérée efficace, il faut bien le reconnaître. Leur défaite n\’en sera que plus savoureuse.\n\n<Herne se fend d\’un sourire carnassier.>\n\nPouvons-nous commencer ?', '', 38134),
(202547, 'frFR', 'Ces sales créatures vont tâter de ma lame avant que toute cette histoire ne soit finie.', '', 38134),
(202536, 'frFR', 'Ajoutez le catalyseur dans le chaudron afin que nous puissions admirer la puissance de ma création !', '', 38134),
(202535, 'frFR', 'Je ne suis pas surpris que mon génie soit connu du Bastion. Cette nouvelle création sera la plus redoutable de toutes. Elle prodiguera à vos Kyrians bien des leçons en matière de violence et de chaos !', '', 38134),
(202534, 'frFR', 'Nous protégerons l\’Ombreterre des menaces intérieures comme extérieures !', '', 38134),
(202529, 'frFR', 'Oui, prince. Je suis touché, mais encore debout.', '', 38134),
(202528, 'frFR', '', 'Règle numéro 2 : toujours faire profil bas. Le spectaculaire n\’a pas sa place dans l\’Antre. Vous arrivez, vous prenez ce que vous cherchez, et vous repartez… sans y laisser trop de plumes, si possible.', 38134),
(202527, 'frFR', 'Mes amis, abritez-vous !', '', 38134),
(202525, 'frFR', 'Attention à l\’impact, chers amis !', '', 38134),
(202524, 'frFR', 'Mon ami, tout va bien ?', '', 38134),
(202522, 'frFR', '', 'Merci. C\’est toujours incroyable, la pagaille qu\’ils arrivent à semer.', 38134),
(202521, 'frFR', '', 'Les dompteurs de gormelins ont besoin d\’un coup de main. Vous pourriez les aider ?', 38134),
(202520, 'frFR', '', 'Ne vous en faites pas. Évitez son regard pendant une journée à peine et il se désintéressera de vous et vous pourrez sortir à nouveau. Pour me rapporter des stygies, bien sûr.', 38134),
(202519, 'frFR', '', 'La bonne nouvelle, c\’est que dans mon refuge, vous êtes en sécurité. Mais pas si vous sortez. Et si vous veniez à connaître une fin tragique, ce serait autant de stygies de perdues.', 38134),
(202518, 'frFR', '', 'Il va d\’abord alerter les cherche-âmes. Puis les tours. Puis ses exécuteurs. Et après… mieux vaut ne pas savoir. Il n\’y a qu\’au prix de précautions infinies que vous pourrez rester en vie.', 38134),
(202517, 'frFR', '', 'Vous avez attiré l\’attention du Geôlier, donc vous êtes $ucondamné:condamnée;. Il remarquera vos moindres faits et gestes en son royaume, et c\’est exactement ce que nous cherchons à éviter.', 38134),
(202516, 'frFR', '', 'Je ne peux pas vous laisser passer.', 38134),
(202515, 'frFR', '', 'Nous veillons sur le cœur de la Forêt. Sauf affaire officielle, seuls les membres de l\’Hallali et de la cour de l\’Hiver sont autorisés à entrer.', 38134),
(202514, 'frFR', '', 'Salutations.$b$BSeuls les membres de l\’Hallali sont autorisés à pénétrer dans le cœur de la Forêt.', 38134),
(202513, 'frFR', '', 'Votre présence est la bienvenue dans le cœur de la Forêt. Vous pouvez passer.', 38134),
(202512, 'frFR', '', 'Nous montons la garde, pour la reine et pour nos bosquets.', 38134),
(202511, 'frFR', '', 'Vous revoilà. Comment se passe votre traque ?', 38134),
(202509, 'frFR', '', 'Le deuil attendra. Il faut agir, et plus vite que ça !', 38134),
(202508, 'frFR', '', 'Dame Baie-de-Lune vous adresse un clin d\’œil.', 38134),
(202507, 'frFR', '', 'Je suis certaine que votre réception de la cour des Braises sera aussi charmante que respectable.', 38134),
(202499, 'frFR', '', 'Comment puis-je vous aider ?', 38134),
(202498, 'frFR', '', 'Ma maîtresse a raison d\’avoir foi en vous, $p. Regroupons-nous à Nurakkir.', 38134),
(202497, 'frFR', 'Ils sont solidement enracinés. Notre attaque devra être dévastatrice.', '', 38134),
(202493, 'frFR', '', 'Les liches sont aussi rusées qu\’arrogantes. Elles ne vont pas se laisser faire.', 38134),
(202492, 'frFR', '', 'L\’ingra Drif… Faites en sorte qu\’il souffre.', 38134),
(202491, 'frFR', 'Vous me sauvez d\’un sort ignoble ! Je suis bien content d\’être dans votre camp.', '', 38134),
(202490, 'frFR', 'La remplaçante de Déva, Lysonia, représente une terrible menace. Je vais dépêcher nos porteurs pour découvrir ses intentions.', '', 38134),
(202489, 'frFR', 'Gloire à l\’Archonte, gloire au bastion !', '', 38134),
(202488, 'frFR', 'Nos aspirants peuvent désormais reprendre leur ascension et nous pouvons arracher notre royaume aux désavoués !', '', 38134),
(202487, 'frFR', 'Notre écu, aujourd\’hui reforgé, est plus puissant que jamais !', '', 38134),
(202486, 'frFR', 'Mais nous avons tenu bon avec l\’aide de l\’âme sans-entraves.', '', 38134),
(202485, 'frFR', 'En détruisant l\’écu, Déva cherchait à rompre les liens qui nous unissent.', '', 38134),
(202484, 'frFR', 'Vous avez rendu un grand service à notre peuple. Il nous faut maintenant sceller le pouvoir de notre nouvel écu pour le protéger de futurs assauts.', '', 38134),
(202472, 'frFR', '', 'Souhaitez-vous rejoindre les rangs des nécro-seigneurs ?', 38134),
(202469, 'frFR', '', 'Baronne ! Vous êtes revenue !', 38134),
(202460, 'frFR', 'C\’est sûrement un purotin qui vient de jeter une autre stèle du vice sur la pile.', '', 38134),
(202457, 'frFR', 'Est-ce que cet amas de stèles aurait bougé ?', '', 38134),
(202447, 'frFR', '', 'Pourquoi le sort s\’acharne-t-il ainsi sur nous ?', 38134),
(202422, 'frFR', 'Moi, aime pas les toasts. Préfère les insectes. Vous avez ?', '', 38134),
(202421, 'frFR', 'Votre attention, s\’il vous plaît ! J\’aimerais porter un toast en l\’honneur de notre meilleur ami de la province des Braises, j\’ai nommé Tête-de-Caillasse !', '', 38134),
(202375, 'frFR', 'Cette chauve-souris semble être votre billet de sortie des profondeurs Sanguines.', 'Cette chauve-souris semble être votre billet de sortie des profondeurs Sanguines.', 38134),
(202373, 'frFR', 'Bolvar croit savoir où la Banshee a conduit ceux qu\’elle a enlevés.', '', 38134),
(202368, 'frFR', '', 'Nous ne faisons rien par hasard en Sylvarden. Et les âmetoiles n\’échappent pas à la règle. La plupart sont utilisées pour stocker ou libérer de l\’anima, comme celles que l\’on trouve dans les parages. Mais celle-ci… celle-ci est différente.$b$bElle sert à capturer les rêves.$b$bMalheureusement, je peux seulement lui rendre sa forme initiale, pas sa fonction. Pour ce faire, vous devrez vous adresser à quelqu\’un qui sache insuffler le pouvoir des rêves aux âmetoiles.', 38134),
(202367, 'frFR', '', '<Gwenna examine les fragments avec précaution.>$b$bOh, la pauvre petite. Que lui est-il arrivé ?$b$BAh, Tirna Scithe… Sa souffrance est bien connue. Pas de panique, nous allons te retaper en un clin d\’œil !$b$BMerci de me l\’avoir apportée. Nous jetons trop souvent des choses qui peuvent encore servir. Trouvez-moi dix morceaux de soie enténébrée et je la réparerai.', 38134),
(202366, 'frFR', 'La puissance des archives nous permet d\’exercer une surveillance attentive de tout le Bastion.', 'La puissance des archives nous permet d\’exercer une surveillance attentive de tout le Bastion.', 38134),
(202365, 'frFR', 'La puissance des archives nous permet d\’exercer une surveillance attentive de tout le Bastion.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante : \n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Protection du sage]|h|r : Quand vos points de vie sont bas, un transcendé vous soigne et repousse les ennemis proches. Vous subissez moins de dégâts. Cet effet diminue rapidement et ne peut se produire qu\’une fois à quelques minutes d\’intervalle.', 'La puissance des archives nous permet d\’exercer une surveillance attentive de tout le Bastion.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante : \n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Protection du sage]|h|r : Quand vos points de vie sont bas, un transcendé vous soigne et repousse les ennemis proches. Vous subissez moins de dégâts. Cet effet diminue rapidement et ne peut se produire qu\’une fois à quelques minutes d\’intervalle.', 38134),
(202364, 'frFR', 'L\’heure est venue pour ces bourdons de faire à nouveau résonner l\’appel de la paix et de la clarté.', 'L\’heure est venue pour ces bourdons de faire à nouveau résonner l\’appel de la paix et de la clarté.', 38134),
(202363, 'frFR', 'L\’heure est venue pour ces bourdons de faire à nouveau résonner l\’appel de la paix et de la clarté.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Bourdon de clarté]|h|r : lorsque plusieurs ennemis se rassemblent, vos sorts et techniques de dégâts ont une chance d\’invoquer le bourdon de clarté pendant un court laps de temps. Le bourdon de clarté étourdit les ennemis proches à son arrivée et augmente les dégâts que vous infligez tant que vous restez à proximité.', 'L\’heure est venue pour ces bourdons de faire à nouveau résonner l\’appel de la paix et de la clarté.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Bourdon de clarté]|h|r : lorsque plusieurs ennemis se rassemblent, vos sorts et techniques de dégâts ont une chance d\’invoquer le bourdon de clarté pendant un court laps de temps. Le bourdon de clarté étourdit les ennemis proches à son arrivée et augmente les dégâts que vous infligez tant que vous restez à proximité.', 38134),
(202362, 'frFR', 'Bienvenue dans les archives des Âges où un afflux d\’anima nous permet de décupler vos capacités.', 'Bienvenue dans les archives des Âges où un afflux d\’anima nous permet de décupler vos capacités.', 38134),
(202360, 'frFR', 'C\’est une expérience à marquer d\’une pierre blanche !', '', 38134),
(202359, 'frFR', 'L\’allégresse de ma démarche n\’a d\’égale que la légèreté qui a envahi mon âme.', '', 38134),
(202358, 'frFR', 'Auriez-vous un instant à m\’accorder, mon $uami:amie; ?', '', 38134),
(202357, 'frFR', 'À moi, archiliche ! Servez-vous du médaillon qui vous a été confié et ensemble, nous écraserons cette saleté de Sans-Entraves.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202356, 'frFR', 'Vite, Sans-Entraves, tuez quiconque se dresse entre vous et le prochain puits. Je sens le pouvoir de mon médaillon tout près d\’ici.', '', 38134),
(202355, 'frFR', 'Que votre pénitence commence !', '', 38134),
(202354, 'frFR', 'Vous allez répondre de votre arrogance !', '', 38134),
(202353, 'frFR', '', 'Quant à Bwonsamdi, ce n\’est pas de mon ressort. Aidez-le ou non, comme vous le voudrez. Je dois m\’occuper de Sylvarden.', 38134),
(202352, 'frFR', '', 'Mon frère n\’est pas du genre à faire fi des cérémonies.', 38134),
(202349, 'frFR', 'L\’âme sans-entraves accepte de se joindre à notre cause. Il est temps d\’entendre son serment pour l\’accueillir au sein de notre front.', '', 38134),
(202347, 'frFR', 'Alors prenez place à mes côtés.', '', 38134),
(202331, 'frFR', 'Oui, mon ami. Avec l\’aide de ces mortels, notre victoire est assurée.', '', 38134),
(202330, 'frFR', 'C\’est le prix à payer pour son orgueil démesuré. Il assistera impuissant au renouveau de Revendreth et à la défaite du Geôlier.', '', 38134),
(202329, 'frFR', 'Depuis trop longtemps, Denathrius se sert de la Lumière comme d\’une arme contre nos sujets de la province des Braises. Nous allons l\’utiliser à notre tour pour le neutraliser.', '', 38134),
(202328, 'frFR', '', 'Je serais honorée de représenter ma reine à la cour des Braises.', 38134),
(202327, 'frFR', '', 'Nous vous reverrons bientôt à la cour des Braises, $n.', 38134),
(202326, 'frFR', 'Nous allons apporter la lame à Tombe-Vice. Z\’rali, la Naaru que les mortels ont secourue, s\’est engagée à servir notre cause.', '', 38134),
(202325, 'frFR', 'C\’est exact, mais je connais un autre moyen.', '', 38134),
(202324, 'frFR', 'Si cette épée renferme vraiment l\’essence de Denathrius, je crains que vos sorts ne suffisent pas à contenir sa puissance.', '', 38134),
(202323, 'frFR', 'Silence ! Nous t\’avons déjà bien assez entendue !', '', 38134),
(202322, 'frFR', '', 'Traître de prince ! Je m\’en vais t\’embrocher ! Te tailler en pièces ! Te disséquer ! Te découper en lamelles…', 38134),
(202321, 'frFR', 'Non. Denathrius devra répondre de sa trahison.', '', 38134),
(202320, 'frFR', '', 'Zovaal vous remettra sur pied, Maître ! Nous nous vengerons !', 38134),
(202319, 'frFR', 'La lame a absorbé son essence !', '', 38134),
(202317, 'frFR', '', 'Votre heure n\’a pas encore sonné, Maître ! Laissez-moi vous protéger !', 38134),
(202314, 'frFR', 'Patience, Rémornia. Élégance et précipitation font rarement bon ménage.', '', 38134),
(202309, 'frFR', '', 'C\’est notre dur labeur à tous qui contribue à préserver la pureté du royaume. Merci.', 38134),
(202308, 'frFR', '', 'C\’est la pagaille qui règne ici. Heureusement, il y a des régisseurs à disposition pour régler tous nos problèmes. Remettez donc de l\’ordre dans tout ça.', 38134),
(202307, 'frFR', 'Regarde, Rémornia ! Ces audacieux mortels ont surmonté tous les obstacles placés sur leur chemin. Nous n\’avons plus d\’autre choix que de nous rendre.', '', 38134),
(202306, 'frFR', 'Vous êtes la clé de notre victoire, mortels ! Allez-y, éliminez celui qui nous a tous trahis !', '', 38134),
(202305, 'frFR', 'Encore des loyalistes ! Nous devons les repousser, mon prince !', '', 38134),
(202302, 'frFR', 'Les Drust ne tarderont pas à revenir à la charge. Dépêchez-vous !', '', 38134),
(202299, 'frFR', '', 'Eh bien, tout s\’est passé à peu près comme je m\’y attendais.', 38134),
(202298, 'frFR', '', 'J\’ai fait de mon mieux pour sauver l\’ambiance, mais cela n\’a pas suffi, on dirait.', 38134),
(202297, 'frFR', 'Je dois retourner au travail. J\’ai perdu assez de temps comme ça.', '', 38134),
(202296, 'frFR', '', 'Organiser une réception n\’est pas donné à tout le monde, mon cher prince. Il vous reste beaucoup à apprendre.', 38134),
(202295, 'frFR', 'J\’ai perdu bien trop de temps loin du champ de bataille.', '', 38134),
(202294, 'frFR', '', 'Je ne suis pas faite pour ces mondanités. La chasse m\’appelle.', 38134),
(202293, 'frFR', 'Je comprends à présent que l\’Archonte entendait m\’enseigner les vertus de l\’humilité en m\’envoyant ici.', '', 38134),
(202292, 'frFR', 'Ouais, bon… On fait la course jusqu\’à la calèche ?', '', 38134),
(202291, 'frFR', 'La haute société, c\’est pas vraiment ce que j\’avais imaginé.', '', 38134),
(202290, 'frFR', 'Allez, cul sec, Slanknen ! Notre calvaire est presque terminé !', '', 38134),
(202289, 'frFR', 'Être invité est toujours un honneur, prince Rénathal… Mais peut-être devriez-vous en faire profiter quelqu\’un d\’autre la prochaine fois ?', '', 38134),
(202288, 'frFR', '', 'J\’aurais aimé que vous soyez plus convaincant, prince Rénathal. Je veux avoir foi en votre cause.', 38134),
(202287, 'frFR', 'Je me serais bien plus amusé en regardant des forgerons meuler de l\’acier.', '', 38134),
(202286, 'frFR', 'Ce n\’était pas si horrible que ça, Kléia. Ce purotin, Gubbins, a été très gentil avec nous.', '', 38134),
(202285, 'frFR', '', 'Louée soit l\’Archonte, c\’est terminé.', 38134),
(202284, 'frFR', 'Bon, au moins, Kevin s\’est bien amusé. Je suis sûr que ces purotins ne vous manqueront pas.', '', 38134),
(202283, 'frFR', '', 'J\’aurais mieux fait de rester au Bastion. J\’aurais nettoyé plus.', 38134),
(202282, 'frFR', 'Y a pas assez à manger ici.', '', 38134),
(202281, 'frFR', 'Je veux parler de deux études distinctes et sans aucun lien… Mais qui ont toutes deux porté leurs fruits !', '', 38134),
(202280, 'frFR', '', '<Dame Baie-de-Lune vous adresse un clin d\’œil.>', 38134),
(202279, 'frFR', 'Le temps est venu de détrôner notre ancien Maître. Ce faisant, nous rendrons un fier service à toute l\’Ombreterre !', '', 38134),
(202278, 'frFR', 'Les vives-pierres ne sont pas les seuls à avoir été manipulés, mon ami. L\’Arbitre elle-même s\’est fait rouler dans la farine.', '', 38134),
(202277, 'frFR', 'La vocation des vives-pierres est de défendre Revendreth. Je suis écœuré de voir que notre loyauté a été dévoyée par celui que nous servions autrefois.', '', 38134),
(202275, 'frFR', 'J\’ai aimé quand j\’ai dit des blagues et qu\’$uami:amie; a rigolé.', '', 38134),
(202274, 'frFR', '', 'C\’était très sympa ! Et ça valait le déplacement ! Je le dirai à tous mes amis !', 38134),
(202273, 'frFR', 'Une expérience réussie en limon et en amitié !', '', 38134),
(202272, 'frFR', 'Unis, nul ne peut nous résister.', '', 38134),
(202271, 'frFR', '', 'J\’admire votre volonté à servir l\’Ombreterre, même au-delà de Revendreth.', 38134),
(202270, 'frFR', 'J\’apprécie votre style, Rénathal. Un prince qui n\’a pas peur de se salir les mains… Vous avez a du potentiel.', '', 38134),
(202269, 'frFR', '', 'Vous avez su m\’impressionner aujourd\’hui. Je ne manquerai pas de rapporter vos actes et vos paroles à la reine de l\’hiver.', 38134),
(202268, 'frFR', 'Bien sûr que c\’est notre cadeau le meilleur. On l\’a fabriqué nous-mêmes !', '', 38134),
(202267, 'frFR', 'J\’espère que mon cadeau, c\’est le meilleur. Comme ça, vous m\’inviterez à nouveau, hein ?', '', 38134),
(202266, 'frFR', '', 'J\’ai hâte de nous voir chasser ensemble à l\’avenir.', 38134),
(202265, 'frFR', 'Merci pour ce répit salvateur. Je retourne au front animé d\’une ardeur nouvelle !', '', 38134),
(202263, 'frFR', 'Et nous avons uni nos forces pour repousser l\’ennemi commun de tous les royaumes, les dévoreurs !', '', 38134),
(202262, 'frFR', 'Prudence. Les plus loyaux gardiens de Denathrius se dressent entre nous et notre proie.', '', 38134),
(202261, 'frFR', 'Nous ne devons surtout pas nous arrêter en si bon chemin, mortels. Par chance, ce miroir m\’a l\’air réparable.', '', 38134),
(202260, 'frFR', 'Comme tu dis. Veille à ce que ça ne se reproduise plus.', '', 38134),
(202259, 'frFR', 'Il est vrai que vous avez fait des ravages. Vous l\’aurez voulu, mon compère ici présent va se faire un plaisir de vous en donner pour votre argent.', '', 38134),
(202258, 'frFR', 'Vous m\’aurez au moins appris à éliminer vos nombreux sous-fifres, « Maître ».', '', 38134),
(202257, 'frFR', 'Malgré tous mes enseignements, vous n\’avez toujours pas appris à rester à votre place, Rénathal. Tss. C\’est décevant.', '', 38134),
(202256, 'frFR', 'Vous avez déjà porté un coup aux manigances de Denathrius, mortels, mais ne commettez pas l\’erreur de le sous-estimer. Son pouvoir est… considérable.', '', 38134),
(202255, 'frFR', 'Il y a un autre passage menant à l\’antichambre par ici.', '', 38134),
(202253, 'frFR', 'Voyez comme ils détalent ! J\’ai honte de voir les miens tomber si bas.', '', 38134),
(202252, 'frFR', 'Venez, mortels. Il est temps de débarrasser Revendreth de la bande de nobles présomptueux qui se dresse entre nous et Denathrius.', '', 38134),
(202251, 'frFR', 'Emmenez cette âme et aidez-la, Accusatrice.', '', 38134),
(202250, 'frFR', '', 'Vous avez tant à apprendre.', 38134),
(202249, 'frFR', 'J\’en ai assez qu\’on se serve de moi comme d\’un pion. Me « guiderez-vous » aussi sur la voie de la vengeance ?', '', 38134),
(202248, 'frFR', '', 'Kael\’thas Haut-Soleil ! Vous êtes désormais délivré de la tyrannie du Maître, mais pas de vos propres méfaits. Suivez-moi, je vous guiderai sur la voie de l\’expiation.', 38134),
(202244, 'frFR', 'La convoitise… L\’un des vices les plus odieux qui soient.', '', 38134),
(202243, 'frFR', 'C\’est scandaleux ! Nos gens meurent de faim au pied des murs du château pendant que ces loyalistes assouvissent leur avidité.', '', 38134),
(202242, 'frFR', 'Ces négociants sont insatiables. Ne laissez pas des armes aussi puissantes tomber entre leurs mains !', '', 38134),
(202241, 'frFR', 'Ces salles renferment bon nombre d\’armes précieuses. Arrachons-les aux mains avides de ces aristocrates.', '', 38134),
(202240, 'frFR', '', 'Ils dansent et festoient alors que le Geôlier rassemble une armée pour détruire l\’Ombreterre. Ils ont bien mérité leur sort pour avoir gobé les mensonges de Denathrius !', 38134),
(202239, 'frFR', 'Ce miroir devrait vous permettre de circuler dans le château, mortels.', '', 38134),
(202238, 'frFR', 'Nous autres vives-pierres empruntions ces passages pour satisfaire les caprices du Maître. Si vous aviez vu les orgies auxquelles j\’ai assisté…', '', 38134),
(202237, 'frFR', 'Par ici, mortels. Je connais un passage qui nous ramènera à l\’antichambre.', '', 38134),
(202236, 'frFR', 'Vous aimer chapeau ?\n\nNouveau copain Tatane, me faire beau pour la fête.\n\nVous, pas triste, hein. Toujours $umeilleur ami:meilleure amie; !', '', 38134),
(202235, 'frFR', '', 'Les regrets devront attendre. Il faut mettre un terme au règne de Denathrius.', 38134),
(202234, 'frFR', '', 'Nous avons stoppé l\’épanchement d\’anima, mais je crains que le mal ne soit fait. Nous allons devoir récupérer ce qui reste pour sauver Revendreth.', 38134),
(202232, 'frFR', 'Beau travail, mortels ! Les réserves d\’anima nous tendent les bras !', '', 38134),
(202231, 'frFR', '', 'Éliminez cette créature avant qu\’elle ne gagne encore en force !', 38134),
(202230, 'frFR', '', 'La bête cherche à assouvir sa faim insatiable en dévorant les réserves d\’anima, en contrebas.', 38134),
(202229, 'frFR', 'Un dévoreur, ici ? Et c\’est le plus gros que j\’aie jamais vu !', '', 38134),
(202223, 'frFR', '', 'Je… ne… peux plus…', 38134),
(202217, 'frFR', '', 'Maints dangers nous attendent en chemin, mais nous devons à tout prix atteindre ces réserves d\’anima !', 38134),
(202216, 'frFR', 'Voici le passage vers les niveaux inférieurs.', '', 38134),
(202215, 'frFR', '', 'Cette charogne a eu ce qu\’elle méritait.', 38134),
(202214, 'frFR', 'Mettez un terme à la cruauté du maître-chien, que nous puissions accéder aux niveaux inférieurs du château.', '', 38134),
(202213, 'frFR', 'Le chenil abrite la meute du Maître. Il affame ses chiens de chasse pour décupler leurs appétits sanguinaires.', '', 38134),
(202212, 'frFR', '', 'Si nous voulons avoir une chance de vaincre Denathrius, nous devons le priver de ses biens les plus précieux. Et justement, je sais où les trouver.', 38134),
(202211, 'frFR', 'Il y a un passage, derrière le chenil, qui mène aux soubassements du château. À partir de là, nous pourrons atteindre les réserves d\’anima et couper le flot qui alimente l\’Antre.', '', 38134),
(202210, 'frFR', 'L\’antichambre est à nous ! Mais je soupçonne Denathrius de s\’être réfugié dans son sanctum.', '', 38134),
(202209, 'frFR', 'Je ne suis habitué ni au verbiage mondain ni à l\’opulence de la royauté.\n\n<Le visage d\’Alexandros s\’adoucit alors qu\’il relâche son étreinte de la garde de son arme.>\n\nCela dit, j\’apprécie les moments de répit. Même le plus grand guerrier doit pouvoir se ressourcer entre deux batailles.\n\nJ\’emploierai ce temps pour me reposer et me concentrer pleinement sur les combats à venir.\n\nLa volonté de Krexus sera faite.', '', 38134),
(202208, 'frFR', 'Commençons votre expiation par un défi de bon aloi.', '', 38134),
(202207, 'frFR', 'Dommage que vous n\’ayez pas retenu la leçon. Peut-être ai-je été trop indulgent ? Il est vrai que la compassion est une seconde nature, chez moi.', '', 38134),
(202206, 'frFR', 'Je vous ai déjà entendu fanfaronner de la sorte, Rénathal. Nous savons tous deux comment ça s\’est terminé la dernière fois.', '', 38134),
(202205, 'frFR', 'Assez joué la comédie, Denathrius ! Venez nous affronter si vous l\’osez !', '', 38134),
(202204, 'frFR', 'Notre prince rebelle, bien sûr. L\’Accusatrice destituée de ses fonctions, toujours aussi sérieuse. Et notre ancien général si rebelle. Sans oublier tout un ramassis de mortels qui courent à une mort certaine.', '', 38134),
(202203, 'frFR', 'Bienvenue ! Je vous attendais avec impatience. Voyons, à qui ai-je l\’honneur ?', '', 38134),
(202199, 'frFR', 'Restez sur vos gardes, mortels. Denathrius joue sur son propre terrain. Il ne reculera devant rien pour nous contrecarrer.', '', 38134),
(202198, 'frFR', '', 'Il sait que le temps joue contre nous. La puissance du Geôlier grandit. Il faut endiguer le flot d\’anima qui se déverse dans l\’Antre.', 38134),
(202196, 'frFR', 'Humm…$b$b<Il est clairement sur le qui-vive. Comme quoi, ce humm a des applications très variées.>', '', 38134),
(202195, 'frFR', 'Un peu plus, et on nous déroulait le tapis rouge. Notre ancien Maître nous met au défi de nous aventurer dans les profondeurs de son bastion.', '', 38134),
(202194, 'frFR', 'Humm…$b$b<Il a l\’air presque pensif.>', '', 38134),
(202193, 'frFR', 'Humm…$b$b<Il semble chercher désespérément à faire abstraction des sons environnants. Et ça n\’a pas l\’air de marcher.>', '', 38134),
(202192, 'frFR', 'Humm…$b$b<Cette fois, il a clairement l\’air énervé.>', '', 38134),
(202191, 'frFR', '', 'J\’ignorais que les Venthyrs pouvaient être si… naïfs. Je leur ai joué quelques tours pendables.\n\n<Dame Baie-de-Lune sourit et regarde sur le côté.>\n\nOh, je dois vous laisser, l\’une de mes illusions est sur le point de…\n\nMais il vaut mieux que je garde cela pour moi.', 38134),
(202190, 'frFR', 'Méditer me permet d\’entrer en harmonie avec la forêt. D\’identifier les choses qui ne sont pas à leur place.$b$B<Le Vorkaï ouvre un œil et vous dévisage.>$b$bEt je n\’y arrive pas quand on me perturbe. Allez parler à Jonquille.', '', 38134),
(202189, 'frFR', 'Humm…$b$B<Difficile de dire si le Vorkaï est plongé dans une profonde méditation ou s\’il est simplement agacé par votre présence.>', '', 38134),
(202188, 'frFR', '$p ! La reine soit louée, vous êtes là !', '', 38134),
(202185, 'frFR', 'Tout est terminé, Gorak Zhar !', '', 38134),
(202184, 'frFR', '', 'Vous allez payer pour cette insolence ! Dans le sang !', 38134),
(202182, 'frFR', 'Je dois me préparer pour le voyage à venir. J\’ai hâte d\’avoir l\’avis de nos alliés.', '', 38134),
(202181, 'frFR', '', 'Vous servez les Yeux, à présent. Vous aurez vos instructions.', 38134),
(202179, 'frFR', 'Les souliers du héraut sont très utiles pour se déplacer.\n\nL\’adversaire vous ralentit ? Vous empêche de bouger ?\n\nPas avec ces bottes ! Avec elles, on est libre de foncer !', '', 38134),
(202178, 'frFR', '', 'Pitié ! Je vais obéir. Dites-moi ce que je dois faire.', 38134),
(202177, 'frFR', 'Je prends soin de votre équipement, pas de souci ! Tout sera prêt quand vous emprunterez la voie.\n\nVous voulez des informations sur l\’équipement ? Dites-moi.', '', 38134),
(202175, 'frFR', '', 'Il est temps de faire des étincelles !', 38134),
(202174, 'frFR', '', 'Des espions ?! Vous allez le regretter !', 38134),
(202173, 'frFR', '', 'Où suis-je ? Qui ose m\’invoquer ?', 38134),
(202172, 'frFR', 'Bon sang, Jonquille…', '', 38134),
(202171, 'frFR', '', 'Ça leur apprendra ! Qui a dit que la magie n\’était pas la solution à tout ?', 38134),
(202170, 'frFR', '', 'Qu\’est-ce que vous diriez de retourner faire un peu d\’étincelles ?', 38134),
(202169, 'frFR', 'Veuillez prendre place quand vous voudrez commencer.', '', 38134),
(202168, 'frFR', 'Retrouvez-nous au niveau inférieur, et nous commencerons.', '', 38134),
(202167, 'frFR', '', 'Le message donne pas de nouvelle tâche… Le message offre aide ?', 38134),
(202166, 'frFR', 'Tout autour de moi, les transcendés parlent de la voie. Les épreuves par lesquelles les âmes doivent passer pour trouver la paix éternelle après une vie imparfaite.', '', 38134),
(202165, 'frFR', 'J\’ai tenté d\’oublier. J\’ai tenté de pardonner. Et pourtant… Il hante chacune de mes pensées. Il me suit partout.', '', 38134),
(202164, 'frFR', 'Cette colère m\’est étrangère. Elle ne me ressemble pas. Je dois m\’en débarrasser. Telle est ma voie. C\’est ce qu\’affirme mon parangon et il en sera ainsi.', '', 38134),
(202163, 'frFR', 'La Lumière a damné votre âme, Arthas. Puisse-t-elle pourrir dans l\’Antre !', '', 38134),
(202162, 'frFR', '', 'J\’ai encore demandé quand je pourrai accomplir le dernier rite. Et je n\’ai pas reçu de réponse, comme d\’habitude. La patience que j\’ai apprise ici est mise à rude épreuve.', 38134),
(202161, 'frFR', '', 'Je vois la frustration des autres aspirants. Je la ressens moi-même fortement, mais je ne dois pas rien en montrer. Ils prennent exemple sur moi. Pélagos aussi…', 38134),
(202160, 'frFR', '', 'Quand cette pénurie finira-t-elle enfin ? Et quelle en est la cause ? Les transcendés le savent-ils ou sont-ils aussi démunis que nous ? J\’aimerais avoir des réponses.', 38134),
(202159, 'frFR', '', 'Je dois me rappeler ce que j\’ai appris. Me recentrer. Trouver la paix dans les choses qui dépendent de moi.', 38134),
(202158, 'frFR', 'On dit que coucher certaines choses par écrit permet de réfléchir et de prendre du recul en cas de difficulté. Voyons cela…', '', 38134),
(202157, 'frFR', 'Aujourd\’hui, j\’ai à nouveau essayé le rite de pureté. Mon âme liée m\’a assuré que j\’étais prêt. Je me suis senti fort au début, mais j\’ai fini par échouer, comme à chaque fois.', '', 38134),
(202156, 'frFR', 'Pourquoi suis-je incapable d\’y arriver ? Tout semble évident quand Kléia me l\’explique, mais dès qu\’il faut agir, j\’oublie tout ce que j\’ai appris.', '', 38134),
(202155, 'frFR', 'Il m\’arrive parfois de penser que je ne ferai jamais mon ascension. J\’ai peur de me retrouver à Oribos, sous le regard impitoyable de l\’Arbitre…', '', 38134),
(202153, 'frFR', '', 'J\’espère qu\’elle n\’a pas encore été avalée par un dévoreur. Quel gâchis ce serait.', 38134),
(202152, 'frFR', '', 'Le temps presse, elle ne va pas tarder à revenir. Je vais vous dévoiler la vérité.', 38134),
(202151, 'frFR', '', '$uCher:Chère; Sans-Entraves, Denathrius a fait de vous un pion.', 38134),
(202150, 'frFR', '', 'Continuez ! Je m\’occupe de bloquer le pont.', 38134),
(202149, 'frFR', 'L\’obscur pouvoir d\’Élune m\’a coûté la vie.\n\nLes mortels ne sont pas faits pour détenir pareille puissance. D\’une façon ou d\’une autre, j\’étais condamné. Mais si cela pouvait sauver mon monde, mon peuple, ma famille, j\’étais prêt à faire ce sacrifice.\n\nQadarin ne l\’entendait pas de cette oreille. Nous avons essayé de partager ce fardeau, et à mon grand regret, nous en sommes morts tous les deux.\n\n<Thiernax soupire.>\n\nHélas, rien ne sert de ressasser le passé. L\’essentiel, c\’est que notre monde ait été sauvé et que nous soyons toujours ensemble.', '', 38134),
(202146, 'frFR', 'Nous avons fort à faire pour préparer l\’arrivée de la reine. Pressons !', '', 38134),
(202144, 'frFR', '', 'Laissez-moi vous montrer la vérité, Sans-Entraves.', 38134),
(202143, 'frFR', '', 'Votre loyauté est reconnue. Relevez-vous et soyez des nôtres.', 38134),
(202142, 'frFR', 'Dans le maquis, vos yeux peuvent vous jouer des tours. Ce qui semble tout proche est parfois dissimulé derrière un voile de brume.\n\nVous aurez beau vous acharner, rien n\’y fera. Ceux qui osent s\’y aventurer seuls finissent souvent par se perdre dans les méandres du temps.\n\nLa vigilance, voilà votre seul espoir. Le maquis ne révèle ses secrets qu\’aux plus perspicaces.\n\nLaissez-vous guider par la lumière. La traversée du voile a déjà commencé.', '', 38134),
(202141, 'frFR', '', 'J\’accepte votre allégeance comme franche et honnête. Vous pouvez vous relever.', 38134),
(202140, 'frFR', '', 'Sika est tellement gentille et travaille si dur. Je peux nettoyer plus soigneusement la forge pour alléger ses soucis.', 38134),
(202139, 'frFR', 'Merci, Sans-Entraves.\n\nJ\’ai affronté ces wyrms d\’éther pour permettre aux aspirants de s\’échapper… mais ils étaient bien trop nombreux.\n\n<Adonas touche ses côtes avec précaution.>\n\nHeureusement que vous étiez là.', '', 38134),
(202138, 'frFR', '', 'Nul n\’a jamais échappé aux profondeurs Sanguines, votre sort est scellé !', 38134),
(202137, 'frFR', '', 'Vous n\’êtes que les molosses enragés de Draven !', 38134),
(202136, 'frFR', '', 'Misérables mortels !', 38134),
(202135, 'frFR', '', 'Je vous retrouverai à la balise. Il y a beaucoup à faire.', 38134),
(202134, 'frFR', 'Oh. J\’avais parlé à Sika de réorganiser la bibliothèque et elle semblait heureuse de le faire. Je ne savais pas qu\’elle était si occupée. Je vais m\’en charger moi-même !', 'Oh. J\’avais parlé à Sika de réorganiser la bibliothèque et elle semblait heureuse de le faire. Je ne savais pas qu\’elle était si occupée. Je vais m\’en charger moi-même !', 38134),
(202133, 'frFR', 'C\’est tout ce que vous avez dans le ventre ?', '', 38134),
(202132, 'frFR', 'Pas de souci, je peux vous emmener.', '', 38134),
(202131, 'frFR', 'Euh… et c\’est tout ?', '', 38134),
(202130, 'frFR', 'Un problème à résoudre ?', '', 38134),
(202129, 'frFR', 'Si vous avez besoin du meilleur, je suis à votre service.', '', 38134),
(202128, 'frFR', 'On tue qui aujourd\’hui ?', '', 38134),
(202127, 'frFR', 'J\’ai déjà vu pire.', '', 38134),
(202123, 'frFR', '', 'C\’est à cause des bras en lames, hein ?', 38134),
(202122, 'frFR', '', 'Besoin d\’un coup de main ?', 38134),
(202121, 'frFR', '', 'C\’est l\’heure du câlin !', 38134),
(202120, 'frFR', '', 'Pensez à moi quand je ne serai plus là.', 38134),
(202117, 'frFR', 'Vous aidez aussi Héphæsius ?', '', 38134),
(202116, 'frFR', '', 'Les amis, ça doit se serrer les coudes.', 38134),
(202115, 'frFR', '', 'Je vous emmène ?', 38134),
(202113, 'frFR', 'Puisse le timbre inspirant de cette cloche résonner à nouveau.', '', 38134),
(202109, 'frFR', 'Oui ?', '', 38134),
(202107, 'frFR', 'Pour lutter contre des forces maléfiques sanguinaires.\n\nUne coalition de champions s\’était rassemblée pour tuer le dieu ténébreux qui avait atterri sur notre monde. Pour réussir, nous avions besoin du pouvoir que la dernière Oratrice avait tenté d\’invoquer en faisant appel à mère Lune.\n\n<Thiernax laisse échapper un rire attristé.>\n\nNous nous sommes querellés. Sa mère l\’avait déjà payé de sa vie, il ne voulait pas risquer de perdre aussi son mari. Mais la puissance d\’Élune était l\’une de nos meilleures armes face aux monstres. Alors, j\’ai accompli le rituel, et j\’ai survécu. Il m\’a soutenu. Le regard embrasé par la noirceur de la nouvelle lune, nous avons affronté l\’ennemi.\n\nJe ne m\’attendais pas à devoir payer un tel prix.', '', 38134),
(202104, 'frFR', 'À la longue, oui.\n\nThiernax a réussi à devenir le guerrier de la Nuit. Les autres champions de Fyzandi et moi-même avons combattu jusqu\’au bout à ses côtés, mais la victoire nous a coûté très cher.\n\nLes armées ennemies ont fait d\’innombrables victimes en notre absence, avant de se disperser à la mort du Ténébreux. Certains des nôtres ont succombé aux murmures. Ils ont disparu ou basculé dans la folie. Nous avons tous perdu des êtres chers.\n\nEt le sang de cette entité impie a contaminé nos terres. Il faudra bien des générations pour purger le mal qui sévit dans notre monde.\n\nSi tant est que ce soit possible.', '', 38134),
(202103, 'frFR', 'Des hordes de monstres.\n\nDes créatures bardées d\’yeux, de membres difformes et de gueules hérissées de crocs. Elles se sont répandues dans notre monde quand mes grands-parents étaient enfants, et c\’est ainsi qu\’a débuté la Guerre sans fin. Des générations entières ont passé leur vie à lutter contre les Autres et leur folie meurtrière.\n\nC\’est ma mère qui a fini par découvrir la vraie nature de ce qui vivait au centre du cratère. Elle a essayé d\’invoquer le côté obscur d\’Élune pour tuer cette entité venue d\’un autre monde… et elle en est morte.', '', 38134),
(202100, 'frFR', 'Oui. Plusieurs générations avant ma naissance, ce dieu de malheur a atterri sur notre monde.\n\nL\’impact a rasé un royaume entier et ébranlé l\’équilibre naturel. Quiconque s\’aventurait dans la région sinistrée n\’en revenait jamais.\n\nNous autres druides avions beau garder un œil sur nos frontières, nous n\’avions pas anticipé ce qui est arrivé ensuite.', '', 38134),
(202098, 'frFR', 'Je viens d\’un monde appelé Fyzandi. Une terre magnifique peuplée de vastes forêts, de hautes montagnes et de plaines paisibles.\n\nC\’est pour sauver tout ça que nous avons combattu le dieu ténébreux.', '', 38134),
(202097, 'frFR', 'À nous la récompense ! Et si on chantait pour fêter ça ? Si ton chasseur aime les graines, tape dans tes mains…', '', 38134),
(202096, 'frFR', 'Hé, moi aussi, j\’ai participé ! J\’ai chanté comme jamais !', '', 38134),
(202095, 'frFR', 'Ha, je savais bien que… Hé, répète un peu pour voir !', '', 38134),
(202094, 'frFR', 'Merci pour votre aide. Ce n\’était pas une partie de plaisir, mais vous vous êtes bien $ubattu:battue;.', '', 38134),
(202093, 'frFR', 'Pff…', '', 38134),
(202092, 'frFR', 'Ahem… L\’essentiel, c\’est qu\’il soit mort. Et maintenant, en route.', '', 38134),
(202091, 'frFR', 'Beau travail. Les boggarts ne supportent pas le boucan.', '', 38134),
(202087, 'frFR', 'Non, attends !', '', 38134),
(202086, 'frFR', 'OH NON !', '', 38134),
(202085, 'frFR', 'Pourquoi ? Où est le danger ? Ce n\’est certainement pas la créature qu\’on cherche. Je n\’en ferai qu\’une bouchée, tu vas voir !', '', 38134),
(202084, 'frFR', 'Ne crains rien, bouffon sans… Hein ? Euh… Gwynceirw ? Je crois que j\’ai trouvé quelque chose !', '', 38134),
(202083, 'frFR', 'Où est la bête ? Où diable se cache-t-elle ? Foin des tire-au-flanc, place à l\’illustre ménestrel !', '', 38134),
(202082, 'frFR', 'Laissez faire Jonquille, le poète de génie. Pas ce grand dadais qui roupille jour et nuit !', '', 38134),
(202081, 'frFR', 'Ô Jonquille, Jonquille, c\’est bien toi le meilleur ! Pas comme ton partenaire, cet indécrottable flâneur !', '', 38134),
(202080, 'frFR', 'Oh, mais je sais ce que je vais faire ! Je retrouverai cette chose moi-même, s\’il le faut. Et je composerai une chanson par la même occasion ! Ça donnerait quelque chose comme ça…', '', 38134),
(202079, 'frFR', 'Qui ça, lui ? Ah, ne vous en faites pas. S\’il est grognon, c\’est parce qu\’il n\’a pas encore attrapé sa proie. C\’est un bon combattant, mais ses méthodes sont… différentes. Si on vous demande, ce n\’est pas moi qui ai dit ça.$b$bDe toute façon, il N\’ENTEND RIEN QUAND IL PIONCE.$B$B<Gwynceirw n\’a pas l\’air de réagir.>', '', 38134),
(202078, 'frFR', 'Ah, ça y est, la sieste est terminée ? Tu as enfin décidé de te joindre à nous ?$b$BOh désolé, je m\’attendais à voir quelqu\’un d\’autre. Cela dit, vous pourriez peut-être nous être utile. Vous n\’auriez pas croisé une monstruosité végétale dans les parages, par hasard ?', '', 38134),
(202077, 'frFR', 'Je. Médite. Ou je m\’y efforce.$b$bPourquoi n\’allez-vous pas parler à Jonquille ? Il adore discuter.', '', 38134),
(202076, 'frFR', 'Le Bastion abrite une cloche particulière qui n\’a pas sonné depuis fort longtemps.\n\nElle servait autrefois dans un rite important pour tous les Kyrians. Mais la plupart des maillets cristallins avec lesquels on sonnait de telles cloches ont été brisés.\n\nIl me serait possible d\’en reforger un, pour peu qu\’on me fournisse les éclats adéquats.', '', 38134),
(202075, 'frFR', 'Quel soulagement de vous voir !', 'Quel soulagement de vous voir !', 38134),
(202074, 'frFR', 'Quel soulagement de vous voir ! J\’ai mis la main sur un peu de surplus d\’anima et conçu un plan de génie visant à réutiliser les cendres qui nous entourent.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Urne de cendres]|h|r : Après avoir vaincu plusieurs ennemis, vous invoquez un rampant des cendres pour se battre à vos côtés. Il ne peut pas recevoir de soins, mais chaque ennemi que vous terrassez ensemble augmente ses dégâts et lui rend des points de vie.', 'Quel soulagement de vous voir ! J\’ai mis la main sur un peu de surplus d\’anima et conçu un plan de génie visant à réutiliser les cendres qui nous entourent.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Urne de cendres]|h|r : Après avoir vaincu plusieurs ennemis, vous invoquez un rampant des cendres pour se battre à vos côtés. Il ne peut pas recevoir de soins, mais chaque ennemi que vous terrassez ensemble augmente ses dégâts et lui rend des points de vie.', 38134),
(202073, 'frFR', 'Restez $ucaché:cachée;.', 'Restez $ucaché:cachée;.', 38134),
(202072, 'frFR', 'Restez $ucaché:cachée;, et je vous donnerai quelque chose qui vous aidera à éliminer ces infâmes mécréants. Je leur ai volé quelques-uns de leurs pitoyables miroirs que vous allez pouvoir utiliser à notre avantage.\n\nAvec mon amélioration, vous bénéficierez de la technique suivante :\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Jeu de miroirs mobiles]|h|r : Vos sorts et techniques de dégâts ont une chance d\’invoquer des éclats de miroir qui canalisent la lumière pour projeter un rayon devant vous, ce qui inflige à vos ennemis des dégâts de Feu, puis des dégâts supplémentaires sur la durée.', 'Restez $ucaché:cachée;, et je vous donnerai quelque chose qui vous aidera à éliminer ces infâmes mécréants. Je leur ai volé quelques-uns de leurs pitoyables miroirs que vous allez pouvoir utiliser à notre avantage.\n\nAvec mon amélioration, vous bénéficierez de la technique suivante :\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Jeu de miroirs mobiles]|h|r : Vos sorts et techniques de dégâts ont une chance d\’invoquer des éclats de miroir qui canalisent la lumière pour projeter un rayon devant vous, ce qui inflige à vos ennemis des dégâts de Feu, puis des dégâts supplémentaires sur la durée.', 38134),
(202071, 'frFR', 'Venthyr ! Nous nous sommes cachés parmi les Voilés en espérant que vous pourriez nous aider. J\’ai dérobé de l\’anima pour votre cause.', 'Venthyr ! Nous nous sommes cachés parmi les Voilés en espérant que vous pourriez nous aider. J\’ai dérobé de l\’anima pour votre cause.', 38134),
(202070, 'frFR', 'Venthyr ! Nous nous sommes cachés parmi les Voilés en espérant que vous pourriez nous aider. J\’ai dérobé de l\’anima pour votre cause.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante : \n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Soif d\’anima]|h|r : Lorsque vos points de vie sont bas, vos sorts et techniques de dégâts ont de grandes chances d\’étourdir votre ennemi pendant un court instant, ce qui vous rend des points de vie et lui inflige des dégâts pendant la durée de l\’effet.\n\nCet effet ne peut pas se produire très souvent. Armez-vous de courage.', 'Venthyr ! Nous nous sommes cachés parmi les Voilés en espérant que vous pourriez nous aider. J\’ai dérobé de l\’anima pour votre cause.\n\nGrâce à mon amélioration, vous bénéficierez de la technique suivante : \n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Soif d\’anima]|h|r : Lorsque vos points de vie sont bas, vos sorts et techniques de dégâts ont de grandes chances d\’étourdir votre ennemi pendant un court instant, ce qui vous rend des points de vie et lui inflige des dégâts pendant la durée de l\’effet.\n\nCet effet ne peut pas se produire très souvent. Armez-vous de courage.', 38134),
(202066, 'frFR', 'Je ne peux pas rester dans l\’Antre plus longtemps. Ramenez les esprits en Sylvarden.', '', 38134),
(202056, 'frFR', 'Je vais faire honneur à ce don, Rezan. Ton nom ne sera pas oublié. Jamais.', '', 38134),
(202054, 'frFR', 'C\’est… de toi… qu\’ils ont besoin…', '', 38134),
(202040, 'frFR', 'Allez, détruisez la barrière !', '', 38134),
(202039, 'frFR', 'Il est temps de venger tous ceux qu\’ils ont possédés ! Pour Sylvarden !', '', 38134),
(202038, 'frFR', 'Mais quelle horrible magie ! Abattez ses monstres, je m\’occupe des graines sauvages !', '', 38134),
(202037, 'frFR', 'Rezan ! Le Loa des rois… Il faut vite briser cette barrière !', '', 38134),
(202035, 'frFR', 'Il en manque encore un. Mais il est déjà presque perdu…', '', 38134),
(202034, 'frFR', 'Shadra ! Bwonsamdi m\’avait dit qu\’elle avait été dévorée par sa propre prêtresse.', '', 38134),
(202033, 'frFR', 'Il y a d\’autres Loas par là. On se dépêche !', '', 38134),
(202032, 'frFR', 'Hir\’eek ! J\’avais entendu son râle d\’agonie en Nazmir…', '', 38134),
(202029, 'frFR', 'M\’apportez-vous quelque chose ?', '', 38134),
(202027, 'frFR', '', 'Un instant !', 38134),
(202024, 'frFR', '', 'Bien des choses ont changé en mon absence.\n\nJe vais avoir besoin de temps pour redevenir moi-même, si tant est que ce soit possible.\n\nMerci de m\’avoir sauvée, $n.', 38134),
(202021, 'frFR', 'Ce serait un honneur d\’aider Sika. Je vais m\’assurer que ses prochaines cargaisons de fournitures arrivent à bon port sans encombre.', '', 38134),
(202020, 'frFR', '', 'Vermine ! Ça fera des morceaux bien utiles !', 38134),
(201999, 'frFR', '', 'Les Ulterres vont tout engloutir !', 38134),
(201996, 'frFR', '', 'Pitoyables petites créatures qui vous agitez en vain.', 38134),
(201995, 'frFR', '', 'Je sens la peur indicible qui étreint notre proie.', 38134),
(201994, 'frFR', '', 'Des manifestations de leurs ultimes espoirs. Nous approchons.', 38134),
(201993, 'frFR', '', 'La litanie des espoirs brisés montre la voie.', 38134),
(201992, 'frFR', 'Oh oui, bien sûr ! C\’est l\’occasion idéale d\’aider la forgelite Sophone et peut-être même d\’apprendre une ou deux choses à son contact.', 'Oh oui, bien sûr ! C\’est l\’occasion idéale d\’aider la forgelite Sophone et peut-être même d\’apprendre une ou deux choses à son contact.', 38134),
(201983, 'frFR', 'Par ici ! Il faut secourir les survivants !', '', 38134),
(201980, 'frFR', 'Xavius le demi-Sylvar déchu ! Conseiller d\’Azshara en personne, il céda à l\’appel de la corruption du Cauchemar d\’émeraude et précipita la ruine de son ancien peuple ! Tremblez devant la puissance de ses redoutables terreurs nocturnes !', '', 38134),
(201979, 'frFR', 'N\’Zoth le Corrupteur ! Il avait des yeux, et des poissons, partout sur Azeroth ! À qui se fier en pareilles circonstances ? Quels poissons étaient encore comestibles ? À ce jour, personne ne sait jusqu\’où s\’est répandue sa corruption !', '', 38134),
(201978, 'frFR', 'Jaina Portvaillant, amiral suprême et reine des glaces ! Pareils à de la poussière de diamant, ses vents algides gelaient ses ennemis jusqu\’au sang, les paralysant sur place ! C\’est ainsi qu\’elle les tuait avant de les transformer en figures de proue pour son vaisseau mystique !', '', 38134),
(201977, 'frFR', 'Gul\’dan, chef du Conseil des ombres ! « Comment est-il revenu ? D\’où sort-il ? » se demandait-on ! Mais la vraie question, c\’était plutôt QUAND était-il revenu ? Et son crâne, qu\’avait-il de si spécial ?', '', 38134),
(201976, 'frFR', 'Kil\’jaeden le Trompeur ! Nul ne se souvient de Jaeden, mais peut-être cela faisait-il partie de son plan ? La bataille eut lieu dans le Néant distordu, à savoir la dimension la plus tortueuse qui ait jamais existé, comme nous le savons tous !', '', 38134),
(201975, 'frFR', 'Argus l\’Annihilateur ! Il cherchait à créer un Titan funeste, tout en sachant pertinemment que la mort est la chasse gardée de l\’Ombreterre et de notre illustre reine ! C\’était impensable, et les aventuriers se mirent en devoir d\’accomplir l\’impossible : tuer un monde tout entier !', '', 38134),
(201974, 'frFR', 'Faites confiance à mes derniers compagnons kyrians ! Ils nous conduiront en lieu sûr !', '', 38134),
(201971, 'frFR', 'Je peux finir la construction ! Bien. Et ensuite, la cour des Braises.', '', 38134),
(201970, 'frFR', 'Merci. Je l\’espère aussi.', '', 38134),
(201969, 'frFR', '', 'Je comprends l\’importance de cette introspection. J\’espère que vous trouverez ce que vous cherchez.', 38134),
(201968, 'frFR', 'Non, mon enfant. Ils ne sont pas tels que je les imaginais… Et je doute qu\’ils l\’aient été un jour. Je dois me retirer pour réfléchir à tête reposée.', '', 38134),
(201967, 'frFR', '', 'Où irez-vous, Uther ? Vous ne comptez pas retourner auprès des désavoués, j\’espère ?', 38134),
(201966, 'frFR', 'Éliminez ces traîtres !', '', 38134),
(201965, 'frFR', 'Nous devons nous frayer un chemin. Thalès ?', '', 38134),
(201964, 'frFR', 'Protéger des mortels, ça ne faisait pas partie du contrat, au départ.', 'Protéger des mortels, ça ne faisait pas partie du contrat, au départ.', 38134),
(201963, 'frFR', 'Merci, $umortel:mortelle;. Un tel sort n\’était certainement pas mérité.', '', 38134),
(201962, 'frFR', 'Je suis payé pour protéger tous ceux qui se trouvent à l\’abri de la porte Antique, même $gun:une:r; $r dans votre genre. Je devrais peut-être réclamer une augmentation.', 'Je suis payée pour protéger tous ceux qui se trouvent à l\’abri de la porte Antique, même $gun:une:r; $r dans votre genre. Je devrais peut-être réclamer une augmentation.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201960, 'frFR', 'Si je n\’avais pas besoin de ce poste et de la paye qui l\’accompagne, je ne lèverais pas mon arme pour défendre une créature mortelle.', 'Si je n\’avais pas besoin de ce poste et de la paye qui l\’accompagne, je ne lèverais pas mon arme pour défendre une créature mortelle.', 38134),
(201959, 'frFR', 'Mograine m\’a demandé de rester en arrière, au cas où vous vous égareriez.\n\nCette porte de la mort vous conduira à lui. Je vous recommande de l\’emprunter.', '', 38134),
(201958, 'frFR', '', 'Après vous, Sans-Entraves.', 38134),
(201950, 'frFR', '', 'Mograine m\’a demandé de rester en arrière, au cas où vous vous égareriez.\n\nCette porte de la mort vous conduira à lui. Je vous recommande de l\’emprunter.', 38134),
(201949, 'frFR', 'Ces atrocités ne peuvent rester impunies.', '', 38134),
(201948, 'frFR', '', 'Nous pourrons peut-être discuter de tout cela un autre jour. En attendant, fuyons cet endroit maudit.', 38134),
(201947, 'frFR', 'Je crois que nous avons bien des points communs, Kléia.', '', 38134),
(201946, 'frFR', '', 'Bien que nous soyons ennemis, il aurait été immoral de vous abandonner à votre sort.', 38134),
(201945, 'frFR', 'Je souffre, mais rien de nouveau sous le soleil. Mes blessures guériront.', '', 38134),
(201944, 'frFR', '', 'Comment allez-vous, Uther ?', 38134),
(201943, 'frFR', 'Je vous remercie de m\’avoir tiré des griffes de Lysonia. Sans votre intervention si opportune, je crains fort qu\’il ne resterait rien de moi.', '', 38134),
(201942, 'frFR', '', 'Les antreliges en ont après vous. Nous allons devoir faire vite.', 38134),
(201940, 'frFR', 'Au cours de cette production théâtrale de génie, l\’un de nos acteurs se transformera sous vos yeux ébahis en puissant adversaire venu d\’un autre royaume, fin prêt à en découdre avec quiconque osera le défier !', '', 38134),
(201939, 'frFR', '', 'Laissez la bête passer.', 38134),
(201938, 'frFR', 'N\’oubliez pas, c\’est grâce au soutien de nos mécènes et spectateurs que nous avons assez d\’anima pour continuer à faire vivre notre art ! Et maintenant, sans plus tarder…', '', 38134),
(201937, 'frFR', 'Régnant depuis les profondeurs marines, la reine Azshara, jadis réputée pour sa beauté, attaqua ses ennemis ! Après les avoir attirés dans son palais, elle déploya maints artifices ainsi que ses innombrables tentacules pour les abattre ! Incroyable !', '', 38134),
(201936, 'frFR', 'Faites ça délicatement ! Elle est presque perdue…', '', 38134),
(201935, 'frFR', 'Votre attention, amis amateurs d\’art ! Grâce à l\’incroyable générosité de notre $uami:amie; $n, j\’ai l\’honneur de vous annoncer que l\’amphithéâtre du Lac étoilé accueillera bientôt une nouvelle représentation !', '', 38134),
(201933, 'frFR', '', 'Restez à mes côtés. Il ne faudrait pas attirer l\’attention avec un combat malvenu.', 38134),
(201932, 'frFR', '', 'Abattez-le vite ou nous risquons de la perdre !', 38134),
(201931, 'frFR', '', 'Nous devons apporter la graine sauvage d\’Ysera à la reine. C\’est son seul espoir !', 38134),
(201930, 'frFR', '', 'Je vais retrouver les autres Yeux. Agissez sans tarder.', 38134),
(201928, 'frFR', 'Ah, quelle journée idéale pour trouver l\’inspiration ! Mon cœur déclame une ode à la création ! Mes ailes frétillent à chaque nouvelle possibilité artistique qui naît dans mon esprit ! C\’est un vrai miracle : nous avons de nouveau assez d\’anima pour me permettre de créer.$b$bJ\’ai longtemps œuvré et je sais désormais que j\’ai imaginé quelque chose qui fera trembler Sylvarden jusqu\’à ses plus profondes racines ! Souhaitez-vous y assister ? Contempler toute la puissance de mes passions ?', '', 38134),
(201926, 'frFR', 'Ô rage, ô désespoir !$b$BAh, mon $uami:amie;. Je dépéris ! Je ne manque ni d\’eau ni de nourriture, mais mon esprit, lui, est affamé ! Avide d\’art, de cette créativité qui nous entraîne vers des cieux que même la plus féroce des fées ne peut atteindre en volant !$b$BHélas, la pénurie étouffe notre créativité. Nous sommes contraints de lutter pour notre survie.$b$BMais soyons honnêtes : une vie sans art vaut-elle vraiment d\’être vécue ?', '', 38134),
(201925, 'frFR', 'Le compte à rebours est déclenché ! Attention !', '', 38134),
(201924, 'frFR', '', 'La célérité prime désormais sur la discrétion. Volons jusqu\’à la pierre d\’accès et fuyons l\’Antre au plus vite.', 38134),
(201923, 'frFR', 'Couvrez-moi !', '', 38134),
(201922, 'frFR', 'La charge est placée ! Reculez !', '', 38134),
(201921, 'frFR', 'Passons aux choses sérieuses !', '', 38134),
(201920, 'frFR', 'Voilà qui devrait faire de sacrés dégâts !', '', 38134),
(201905, 'frFR', '', 'N\’oublions pas que la rédemption est la véritable finalité de Revendreth.', 38134),
(201904, 'frFR', 'Préservez les âmes restantes. Nous les aiderons, si nous le pouvons.', '', 38134),
(201903, 'frFR', 'Repliez-vous avec les âmes que vous pouvez contrôler !', 'Repliez-vous avec les âmes que vous pouvez contrôler !', 38134),
(201902, 'frFR', '', 'Épargnez les âmes si possible. Nous pouvons peut-être les réhabiliter !', 38134),
(201901, 'frFR', '', 'La corruption du Maître ne connaît aucune limite !', 38134),
(201900, 'frFR', 'Ces âmes ont été corrompues par le Maître et transformées en armes !', '', 38134),
(201893, 'frFR', 'Poursuivez-les dans le périmètre, mais pas plus loin !', '', 38134),
(201892, 'frFR', 'Parfait ! Je m\’assurerai que ces gelées soient reconstituées au plus vite.', '', 38134),
(201891, 'frFR', 'Ah, $ucher apprenti:chère apprentie; ! Nous sommes à court de gelée. Que diriez-vous d\’une petite opération de recyclage ?', '', 38134),
(201890, 'frFR', 'Bonjour ! Le catalyseur de peste Marileth m\’envoie tester cet équipement. Nous devons rassembler les gelées vivantes du secteur, ou du moins des échantillons. Vous voulez participer ?', '', 38134),
(201888, 'frFR', 'Ça ne l\’est pas pour moi, bien sûr. Mais ça l\’est pour certains !', '', 38134),
(201887, 'frFR', 'Repliez-vous et regroupez-vous au donjon !', '', 38134),
(201886, 'frFR', 'Notre Sans-Entraves a bien fait d\’activer les renforts vives-pierres, aujourd\’hui. Cap sur les cieux !', '', 38134),
(201885, 'frFR', 'La Légion de pierre ! Le Maître envoie ses troupes les plus puissantes à notre cour ?', '', 38134),
(201884, 'frFR', 'Vous savez, susciter l\’intérêt de la Légion de pierre est considéré comme un grand honneur par certains.', '', 38134),
(201883, 'frFR', '', 'Quel impressionnant choix d\’ennemis, $p…', 38134),
(201875, 'frFR', 'Celui-ci détient le pouvoir d\’imprégner la maldracite d\’anima. Et à en juger par son regard, il n\’en mène pas large.', 'Celui-ci détient le pouvoir d\’imprégner la maldracite d\’anima. Et à en juger par son regard, il n\’en mène pas large.', 38134),
(201874, 'frFR', 'Celui-ci détient le pouvoir d\’imprégner la maldracite d\’anima.\n\nEn lui retirant cette amélioration, vous bénéficierez de \n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Maldracite chargée]|h|r : vous utilisez un éclat de maldracite qui absorbe les âmes que vous avez vaincues. Lorsque vous passez sous un certain seuil de points de vie, l\’éclat se brise et repousse les ennemis proches, tout en vous conférant un bouclier pendant quelques instants.\n\nCet effet ne peut se produire très souvent. Son temps de recharge est néanmoins réduit chaque fois que vous infligez un coup fatal.', 'Celui-ci détient le pouvoir d\’imprégner la maldracite d\’anima.\n\nEn lui retirant cette amélioration, vous bénéficierez de \n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Maldracite chargée]|h|r : vous utilisez un éclat de maldracite qui absorbe les âmes que vous avez vaincues. Lorsque vous passez sous un certain seuil de points de vie, l\’éclat se brise et repousse les ennemis proches, tout en vous conférant un bouclier pendant quelques instants.\n\nCet effet ne peut se produire très souvent. Son temps de recharge est néanmoins réduit chaque fois que vous infligez un coup fatal.', 38134),
(201873, 'frFR', 'Bas les pattes ! Celui-ci maîtrise la glace et le givre.', 'Bas les pattes ! Celui-ci maîtrise la glace et le givre.', 38134),
(201872, 'frFR', 'Bas les pattes ! Celui-ci maîtrise la glace et le givre.\n\nEn choisissant cette amélioration, vous bénéficierez de \n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Chair arctique]|h|r : lorsque vous subissez des dégâts, vous avez une chance de gagner une charge de Cristallisation d\’anima, ce qui réduit les dégâts que vous subissez. Subir des dégâts lorsque vous avez le maximum de charges gèle les ennemis proches pendant 5 s et augmente les dégâts qu\’ils subissent.', 'Bas les pattes ! Celui-ci maîtrise la glace et le givre.\n\nEn choisissant cette amélioration, vous bénéficierez de \n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Chair arctique]|h|r : lorsque vous subissez des dégâts, vous avez une chance de gagner une charge de Cristallisation d\’anima, ce qui réduit les dégâts que vous subissez. Subir des dégâts lorsque vous avez le maximum de charges gèle les ennemis proches pendant 5 s et augmente les dégâts qu\’ils subissent.', 38134),
(201870, 'frFR', 'Celui-ci est capable d\’invoquer des créatures en décomposition. C\’est répugnant.', 'Celui-ci est capable d\’invoquer des créatures en décomposition. C\’est répugnant.', 38134),
(201869, 'frFR', 'Celui-ci est capable d\’invoquer des créatures en décomposition.\n\nEn choisissant cette amélioration, vous bénéficierez de \n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Commandement de squelettes]|h|r : quand l\’effet passif de Chair recomposée absorbe l\’essence d\’un cadavre, il fait régulièrement apparaître un squelette qui se bat à vos côtés.', 'Celui-ci est capable d\’invoquer des créatures en décomposition.\n\nEn choisissant cette amélioration, vous bénéficierez de \n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Commandement de squelettes]|h|r : quand l\’effet passif de Chair recomposée absorbe l\’essence d\’un cadavre, il fait régulièrement apparaître un squelette qui se bat à vos côtés.', 38134),
(201868, 'frFR', '', 'Venez, Uther. Laissez-moi vous conduire en lieu sûr.', 38134),
(201865, 'frFR', 'Salutations, $ul\’ami:l\’amie;.', 'Salutations, $ul\’ami:l\’amie;.', 38134),
(201864, 'frFR', 'Comment puis-je me rendre utile ?', 'Comment puis-je me rendre utile ?', 38134),
(201863, 'frFR', 'Qu\’est-ce que je peux faire pour vous ?', 'Qu\’est-ce que je peux faire pour vous ?', 38134),
(201861, 'frFR', '', 'Votre volonté sera faite, maître. Ces serviteurs de l\’Archonte ne peuvent s\’y opposer.', 38134),
(201860, 'frFR', '', 'Ne me décevez pas, Lysonia. Prouvez-moi que vous êtes digne de votre nouveau pouvoir.', 38134),
(201859, 'frFR', '', 'Ah, ces Drust, quelle plaie ! Nous devons défendre le bosquet et la graine sauvage d\’Ysera, ou tous nos efforts n\’auront servi à rien !', 38134),
(201858, 'frFR', '', 'Vous avez l\’air soucieux, Thrall. Qu\’y a-t-il ?', 38134),
(201857, 'frFR', '', 'Là où il y a une sortie, il y a une entrée. S\’il le faut, nous reviendrons la chercher.', 38134),
(201855, 'frFR', '', 'Non, mais nous n\'avons aucun moyen de la retrouver. Il faut tout essayer, mais la pierre d\’accès pourrait être notre seule porte de sortie.', 38134),
(201853, 'frFR', 'Toujours pas la moindre trace de Tyrande. On ne peut pas l\’abandonner seule à ce cauchemar.', '', 38134),
(201851, 'frFR', 'Elle s\’est jetée dans la faille juste avant nous en jurant de traquer Sylvanas… à n\’importe quel prix.', '', 38134),
(201850, 'frFR', 'Tyrande reste introuvable pour l\’instant. Peut-être a-t-elle foncé droit dans le chaos.', '', 38134),
(201849, 'frFR', 'Vous avez passé la faille. Sans votre aide, cette mission serait bien mal engagée.', '', 38134),
(201848, 'frFR', '', 'Un grand danger nous guette. Venez, nous allons l\’affronter ensemble.', 38134),
(201844, 'frFR', '', 'Mueh\’zala va tâter de ma fureur. Je vous fais confiance, $umortel:mortelle;…', 38134),
(201842, 'frFR', '', 'Je me souviens d\’avoir été arrachée à ma forêt. J\’ai refusé d\’aider cet infâme Mueh\’zala… et j\’ai été abandonnée ici.\n\nSi j\’en avais la force, je le réduirais en charpie et sèmerais sa carcasse broyée aux quatre vents !\n\nJe dois retourner dans cette forêt. Pouvez-vous m\’y aider ?', 38134),
(201841, 'frFR', '', 'Bénie soit sa voix.', 38134),
(201840, 'frFR', '', 'Kah-Delen, guidez les représentants des congrégations jusqu\’à l\’enclave. Je vous y retrouverai après en avoir fini ici. Par moi, c\’est sa voix qui a parlé.', 38134),
(201839, 'frFR', '', 'Alors, il n\’est pas question de les faire patienter. Accompagnez-moi, Sans-Entraves. Peut-être nous révéleront-ils notre chemin.', 38134),
(201838, 'frFR', 'Auguste voix ! Les Éternels vous attendent dans la salle de l\’Arbitre.', '', 38134),
(201837, 'frFR', 'J\’ai peur que le Geôlier ne tire aussi les ficelles de la destinée de notre monde.', '', 38134),
(201836, 'frFR', '', 'Puisse le Dessein nous sauver ! C\’est encore pire que ce que je craignais.', 38134),
(201835, 'frFR', 'Denathrius est responsable de la pénurie d\’anima. Il a déversé ses vastes réserves dans l\’Antre. Et il est de mèche avec le Geôlier.', '', 38134),
(201834, 'frFR', '', 'Je sens une énergie familière m\’attirer vers l\’ouest. Nous devons empêcher Lysonia et son maître de parvenir à leurs fins.', 38134),
(201833, 'frFR', '', 'J\’espère que je n\’aurai pas à le regretter…', 38134),
(201832, 'frFR', '', 'Retrouvez-moi près de la maison des Yeux. Une telle mission nécessite… de la finesse.', 38134),
(201831, 'frFR', '', 'Sans nécropole, inutile de songer à garder le contrôle du siège. Nous allons donc en arracher une à nos ennemis.', 38134),
(201830, 'frFR', '', 'Non. Il est temps d\’en finir !', 38134),
(201829, 'frFR', '', 'Mais vous préférez prendre et détruire. Quelle tristesse, j\’aurais presque pitié de vous.', 38134),
(201828, 'frFR', '', 'Les Drust auraient pu être des protecteurs de la nature et des gardiens du cycle.', 38134),
(201826, 'frFR', '', 'Nous allons tout prendre jusqu\’au dernier bosquet, au dernier Faë et à la dernière âme.', 38134),
(201825, 'frFR', '', 'Et pourtant nous sommes toujours plus forts, alors que vos pantins dépérissent chaque jour un peu plus !', 38134),
(201824, 'frFR', '', 'Des êtres avides, incapables de voir plus loin que leurs propres appétits.', 38134),
(201823, 'frFR', '', 'Je vais broyer le cœur de la Forêt entre mes mains et m\’emparer de votre trône !', 38134),
(201822, 'frFR', '', 'Je n\’ai pas besoin de la pitié d\’une reine sur le retour.', 38134),
(201820, 'frFR', 'À moi, l\’Hallali ! Pour Sylvarden !', '', 38134),
(201813, 'frFR', 'Ashamane ! C\’est une déesse sauvage du temps de la guerre des Anciens.', '', 38134),
(201812, 'frFR', 'Nous avons nos ordres. Kyrians du Bastion, combattez avec bravoure.', '', 38134),
(201811, 'frFR', 'Bonjour, $c. Je suis venu vous offrir l\’aide de l\’Hallali pour lutter contre nos ennemis.\n\nEn acceptant mon aide, vous obtiendrez la technique suivante : \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Charge vorkaï]|h|r : Lorsque vous êtes en infériorité numérique, vos sorts et techniques ont une chance d\’invoquer des Vorkaïs qui chargent votre cible, infligent des dégâts de Nature et étourdissent les ennemis sur leur passage.', 'Bonjour, $c. Je suis venu vous offrir l\’aide de l\’Hallali pour lutter contre nos ennemis.\n\nEn acceptant mon aide, vous obtiendrez la technique suivante : \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Charge vorkaï]|h|r : Lorsque vous êtes en infériorité numérique, vos sorts et techniques ont une chance d\’invoquer des Vorkaïs qui chargent votre cible, infligent des dégâts de Nature et étourdissent les ennemis sur leur passage.', 38134),
(201810, 'frFR', 'Grâce à mon amélioration, la terre elle-même peut anéantir les ennemis de Sylvarden.\n\nEn acceptant mon aide, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Colère des Tirnenn]|h|r : Vos sorts et techniques ont de grandes chances de faire apparaître des éclats de terre. Les ennemis touchés saignent et subissent des dégâts de Nature toutes les secondes. Cet effet est cumulable. Les ennemis qui meurent avec le maximum de charges explosent en infligeant des dégâts de Nature aux cibles proches.', 'Grâce à mon amélioration, la terre elle-même peut anéantir les ennemis de Sylvarden.\n\nEn acceptant mon aide, vous bénéficierez de la technique suivante :\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Colère des Tirnenn]|h|r : Vos sorts et techniques ont de grandes chances de faire apparaître des éclats de terre. Les ennemis touchés saignent et subissent des dégâts de Nature toutes les secondes. Cet effet est cumulable. Les ennemis qui meurent avec le maximum de charges explosent en infligeant des dégâts de Nature aux cibles proches.', 38134),
(201809, 'frFR', 'Je peux apprendre à ceux qui marchent, courent ou trottent à se déplacer en toute discrétion, $r.\n\nEn acceptant mon aide, vous obtiendrez la technique suivante :\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Subterfuges d\’entourloupeur]|h|r : hors combat, Forme d\’âme empêche les ennemis de vous repérer.', 'Je peux apprendre à ceux qui marchent, courent ou trottent à se déplacer en toute discrétion, $r.\n\nEn acceptant mon aide, vous obtiendrez la technique suivante :\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Subterfuges d\’entourloupeur]|h|r : Hors combat, Forme d\’âme empêche les ennemis de vous repérer.', 38134),
(201802, 'frFR', 'Alors il faut bien s\’accrocher à ce lien, et surtout il ne faut pas révéler ton esprit tant que tu es là-bas. Sinon, ton âme pourrait disparaître… Pouf !', '', 38134),
(201789, 'frFR', 'Il faut avertir la reine de l\’hiver immédiatement.', '', 38134),
(201788, 'frFR', '', 'Malheureusement, ils ne pourront pas être ramenés. Gorak Zhar paiera cher les horreurs qu\’elle a fait subir à mon royaume.', 38134),
(201787, 'frFR', '', 'Voilà qui est préoccupant, $n. J\’espérais trouver comment guérir ceux qui ont été possédés par les Drust.', 38134),
(201779, 'frFR', 'Grâce à mon amélioration, la terre elle-même peut anéantir les ennemis de Sylvarden.', 'Grâce à mon amélioration, la terre elle-même peut anéantir les ennemis de Sylvarden.', 38134),
(201774, 'frFR', 'Je peux apprendre à ceux qui marchent, courent ou trottent à se déplacer en toute discrétion, $r.', 'Je peux apprendre à ceux qui marchent, courent ou trottent à se déplacer en toute discrétion, $r.', 38134),
(201773, 'frFR', 'Joli combat ! Pour en revenir aux choses sérieuses…', '', 38134),
(201772, 'frFR', 'Deux contre un ? Tant pis pour vous !', '', 38134),
(201771, 'frFR', 'Je me charge de tenir la place. Partez !', '', 38134),
(201770, 'frFR', '', 'Voilà. Je vais pouvoir défendre le bosquet.', 38134),
(201766, 'frFR', '', 'Tout ira bien pour moi ! Allez-y !', 38134),
(201765, 'frFR', '', 'Brasillon ? Gerwyn ? Oh non, pas vous aussi.', 38134),
(201764, 'frFR', '', 'Vite, aidez-moi à entrer en harmonie avec l\’ancre.', 38134),
(201763, 'frFR', '', 'Aidez-moi à reprendre l\’ancre, $p.', 38134),
(201762, 'frFR', '', 'Je vais te tailler en pièces !', 38134),
(201747, 'frFR', 'Je suis ravi. Une sortie fera le plus grand bien à Kevin et je suis sûr que mes collègues sauront apprécier les gelées que nous leur apporterons !', '', 38134),
(201744, 'frFR', '', 'Pardonnez-moi, mais je dois me reposer un moment.', 38134),
(201741, 'frFR', '', 'Lysonia se trouve de l\’autre côté de ces portes. Mais mes premiers repérages sont… préoccupants.', 38134),
(201737, 'frFR', 'Oooh, flûte et zut. À l\’aide ! Est-ce que quelqu\’un m\’entend ?', '', 38134),
(201730, 'frFR', 'On va à une fête entre amis ! Youpi ! On va bien s\’amuser.', '', 38134),
(201727, 'frFR', 'Nous n\’irons pas plus loin sans le soutien des vives-pierres.', '', 38134),
(201726, 'frFR', 'Les liches de Kel\’Thuzad les ont changés en pierre.', '', 38134),
(201724, 'frFR', 'Vos os décoreront le trône de Mueh\’zala !', '', 38134),
(201722, 'frFR', 'Écrase quiconque s\’oppose à Mueh\’zala !', '', 38134),
(201721, 'frFR', 'Assez ! À moi les noyés du Noir bourbier !', '', 38134),
(201720, 'frFR', 'Bonjour, $c. Je suis venu vous offrir l\’aide de l\’Hallali pour lutter contre nos ennemis.', 'Bonjour, $c. Je suis venue vous offrir l\’aide de l\’Hallali pour lutter contre nos ennemis.', 38134),
(201718, 'frFR', 'Mueh\’zala… l\’emporte toujours…', '', 38134),
(201717, 'frFR', 'Que Mueh\’zala vous maudisse…', '', 38134),
(201716, 'frFR', 'Aucun espoir… pour vous…', '', 38134),
(201715, 'frFR', 'Mueh\’zala… reviendra…', '', 38134),
(201713, 'frFR', 'Ah non, pas encore…', '', 38134),
(201708, 'frFR', 'Vous n\’arrivez pas à la cheville de Mueh\’zala !', '', 38134),
(201705, 'frFR', 'Vous mourrez avec Bwonsamdi !', '', 38134),
(201704, 'frFR', 'Personne ne trahit Mueh\’zala !', '', 38134),
(201703, 'frFR', 'Mueh\’zala régnera à nouveau !', '', 38134),
(201701, 'frFR', 'Nous allons libérer le maître !', '', 38134),
(201695, 'frFR', 'Ça a fonctionné ! Oh, mais cet endroit est… remarquable.', '', 38134),
(201694, 'frFR', 'Je reste près de vous. Puisse la Terre-Mère nous mener à bon port.', '', 38134),
(201693, 'frFR', 'C\’est la pierre d\’accès ! J\’espère qu\’être avec vous me permettra de passer.', '', 38134),
(201689, 'frFR', 'Oh, merci, merci mille fois pour votre aide. Ces satanés spriggans me causent bien du souci !', '', 38134),
(201637, 'frFR', 'Moi, j\’ai tous les Zandalari qui vénèrent mon nom ! Sans le Geôlier, tu n\’es qu\’un pauvre Loa oublié. Ha, pauvre petit Mueh\’zala.', '', 38134),
(201636, 'frFR', '', 'Retrouvez-moi à ma tanière et je ferai de mon mieux pour vous aider.', 38134),
(201635, 'frFR', 'Mes fidèles sont là pour me libérer… Tu vas passer un sale moment.', '', 38134),
(201634, 'frFR', '', 'Xandria nous attend à bord du Zerekriss. Allons-y.', 38134),
(201631, 'frFR', 'Oh, bon sang. Oh là là. Il se passe quelque chose de bizarre, avec cette eau.', '', 38134),
(201630, 'frFR', 'Content de vous revoir, $n. Bwonsamdi sait qu\’il peut compter sur vous.\n\nNous avons fort à faire pour arrêter les troupes de Mueh\’zala.', '', 38134),
(201629, 'frFR', 'Je me charge d\’explorer les alentours depuis les airs.', '', 38134),
(201628, 'frFR', 'Nul ne franchit ces murs sans que je le sache. Vous ne repartirez pas !', '', 38134),
(201627, 'frFR', 'Mais quelle idée… Fabriquer un miroir pour pénétrer dans mon domaine…', '', 38134),
(201626, 'frFR', '', 'Bonjour, l\’$gami:amie;. Moi, c\’est Sauliflore. Sylvarden est bien à plaindre, vous ne trouvez pas ?\n\n<Elle se penche vers vous et baisse d\’un ton.>\n\nNe lui répétez surtout pas, mais je suis en train de préparer ma revanche sur dame Baie-de-Lune. Nous passons notre temps à nous jouer des tours pendables, et cette fois-ci, je ne vais pas la louper !\n\nJ\’ai déjà ma petite idée, mais je dois trouver un moyen d\’accéder aux coulisses de l\’amphithéâtre pour mettre mon plan à exécution. Vous allez peut-être pouvoir m\’aider.', 38134),
(201622, 'frFR', 'Ce clairon ordonnera à la Lame d\’ébène d\’attaquer votre adversaire. Faites-en bon usage.', '', 38134),
(201620, 'frFR', 'Je me charge d\’escorter l\’âme sans-entraves, mon prince. Venez, $n.', '', 38134),
(201619, 'frFR', 'Le piège assure leur sécurité en prévenant les chutes de toute hauteur. Si cette information vous semble pertinente.', '', 38134),
(201618, 'frFR', 'Virez-les de la fête !', '', 38134),
(201617, 'frFR', 'Ce piège immobilisera les voleurs afin de les envoyer dans un lieu plus… approprié.', '', 38134),
(201615, 'frFR', 'Plus rien ne me surprend, et surtout pas les truands qui tentent de voler nos marchandises exotiques.\n\nLe cartel Ta a conçu une technologie antivol très efficace pour ces situations-là.', '', 38134),
(201613, 'frFR', 'Sans-Entraves ! Besoin de vous, en bas !', '', 38134),
(201612, 'frFR', 'Alors, pourquoi ce purotin crochète-t-il la serrure de cette cage, derrière vous ?', '', 38134),
(201611, 'frFR', 'Pas besoin. Pas de voleurs ici !', '', 38134),
(201610, 'frFR', '', 'Le vent mordant se change en brise apaisante. Laissez-vous porter jusqu\’aux autres. Aidez la sylve.', 38134),
(201609, 'frFR', 'J\’en ai bien assez de cet endroit. Allons-y.', '', 38134),
(201608, 'frFR', 'Enfin… Mes tourments s\’achèvent…', '', 38134),
(201603, 'frFR', 'Oh ! Une fête, c\’est pile ce dont j\’ai besoin pour reprendre des forces. Ensuite, je pourrai retourner me battre !', '', 38134),
(201602, 'frFR', 'Pour une somme modique, je peux vous montrer le sort que le \"cartel Ta\" réserve aux voleurs.', '', 38134),
(201600, 'frFR', 'Oui. Quand Choufa ira, j\’irai. Après ma sieste.', '', 38134),
(201599, 'frFR', 'À moi, Tombe-Vice ! Marchons ensemble sur Maldraxxus !', '', 38134),
(201598, 'frFR', 'Nos gens ont été massacrés et le Puits de soleil profané, tout ça pour faire revenir une liche d\’entre les morts.', '', 38134),
(201595, 'frFR', 'Merci. Rien de tout cela n\’aurait été possible sans vous, mon $uami:amie;.', '', 38134),
(201594, 'frFR', 'Savoir que Sauliflore survira en chaque nouveau transcendé me réconforte.', '', 38134),
(201593, 'frFR', 'Placez le réceptacle ici, $n.', '', 38134),
(201591, 'frFR', 'Venez, allons retrouver Pélagos devant l\’écu.', '', 38134),
(201590, 'frFR', 'Je suis touché par le grand sacrifice consenti par Sylvarden.', '', 38134),
(201589, 'frFR', 'Attendez ! Qu\’est-ce que c\’est que ça ?', '', 38134),
(201588, 'frFR', 'L\’amiral suprême est vraiment redoutable. Venir à bout de l\’une de ces monstruosités a tout d\’un exploit.', '', 38134),
(201587, 'frFR', 'Il serait imprudent de briser l\’enchantement. Ne restons pas là.', '', 38134),
(201586, 'frFR', 'Dame Portvaillant n\’a pas chômé, on dirait…', '', 38134),
(201585, 'frFR', 'Le combat semble avoir continué dans cette direction. Suivons la piste tant qu\’elle est, euh… fraîche.', '', 38134),
(201584, 'frFR', 'De la magie du Givre. Sans doute l\’œuvre de notre cher amiral suprême.', '', 38134),
(201582, 'frFR', '', 'Ara\’lon se considère responsable de ce malheur. J\’ai peur que la culpabilité le submerge.', 38134),
(201581, 'frFR', 'Kivarr a sa tanière près du bosquet du Brunant. Rejoignons-nous là-bas.', '', 38134),
(201580, 'frFR', 'Nous trouverons un moyen de te sauver, Gweyir. C\’est promis…', '', 38134),
(201579, 'frFR', '', 'Voilà, elle est endormie… mais ses rêves ne lui offrent que peu de réconfort.', 38134),
(201578, 'frFR', 'Il faut à tout prix arrêter Gorak Zhar et ses sbires. Trouvez Kivarr en Ombreterre et conjurez cette malédiction.', '', 38134),
(201577, 'frFR', '', 'Gol Inath sera votre tombeau !', 38134),
(201576, 'frFR', '', 'Pauvre $umortel:mortelle;, cette idiote de parlépine vous a $umené:menée; droit dans mes griffes…', 38134),
(201575, 'frFR', 'Gorak Tul a beau être mort, sa présence maléfique continue à se manifester entre ces murs.', '', 38134),
(201574, 'frFR', 'Placez le fétiche dans le chaudron de Gol Inath, mais prenez garde…', '', 38134),
(201573, 'frFR', '', 'S\’il existe un moyen de lever la malédiction des Drust, Ulfar le connaîtra. Allez le consulter.', 38134),
(201572, 'frFR', '', 'Il reste peut-être un espoir pour la jeune chasseresse…', 38134),
(201571, 'frFR', 'Présentez-vous au seigneur Herne. Je vais escorter Gweyir.', '', 38134),
(201570, 'frFR', 'Le moment venu, enfilez le masque et nous irons trouver les autres.', '', 38134),
(201569, 'frFR', 'Il nous faut des déguisements. Cherchez dans les alentours.', '', 38134),
(201567, 'frFR', 'Nous avons déjà perdu bien trop de chasseurs. Retrouvez-les et ramenez-les nous.', '', 38134),
(201558, 'frFR', 'Veuillez remercier le prince Rénathal pour son invitation. Je ne manquerai pas de me rendre à la cour des Braises.', '', 38134),
(201557, 'frFR', 'Il en sera fait selon votre volonté, parangon. Je surmonterai cette épreuve.', '', 38134),
(201556, 'frFR', 'Très certainement. Mais il s\’agit là d\’une occasion de vous améliorer et de servir l\’Archonte au mieux.', '', 38134),
(201555, 'frFR', 'Non, bien sûr. Mais il me semble que pareilles frivolités conviendraient mieux à d\’autres.', '', 38134),
(201554, 'frFR', 'L\’Arbitre est la dernière clé. Protégez-la, ou tout sera perdu.', '', 38134),
(201553, 'frFR', 'Prévenez l\’Archonte, la reine de l\’hiver et Denathrius. Ils doivent préparer nos défenses. Zovaal ne doit pas atteindre le sépulcre.', '', 38134),
(201552, 'frFR', 'L\’Ombreterre ne peut être sauvée qu\’en réunissant à nouveau les Éternels, avant que le Geôlier ne s\’échappe de l\’Antre.', '', 38134),
(201551, 'frFR', 'Si vous entendez mon message, alors mes craintes étaient fondées. Il a fait de ses chaînes une arme… et a eu raison de moi.', '', 38134),
(201549, 'frFR', 'Nous avons été choisis. Nous comptons sur la force brute. Rassemblons nos soldats les plus vaillants… même le baron disparu.', '', 38134),
(201548, 'frFR', 'Je crains que Zovaal n\’ait pas agi seul. Il a sûrement fait appel à d\’anciens alliés… et il cherchera à rallier d\’autres combattants à sa cause.', '', 38134),
(201547, 'frFR', 'Il y a bien longtemps, les Éternels ont puni notre frère Zovaal pour trahison. Ils l\’ont enfermé à jamais dans l\’Antre, le condamnant à en devenir le Geôlier.', '', 38134),
(201545, 'frFR', 'Je suis le Primat. Votre présence dans mon sanctum signifie que les ténèbres se sont emparées de Maldraxxus… et des autres royaumes de la Mort.', '', 38134),
(201544, 'frFR', 'Ah, Adrestès. Il se dit que vous seriez trop important pour accepter l\’invitation du prince Rénathal. Ou aurais-je mal compris vos réticences ?', '', 38134),
(201542, 'frFR', 'La patience nécessaire à l\’apprentissage.\n\nLa connaissance nécessaire à une nouvelle perspective.\n\nL\’intuition nécessaire au choix.\n\nLe jugement nécessaire à la compréhension d\’une vérité plus profonde.\n\nTelle est la voie de la sagesse.', 'La patience nécessaire à l\’apprentissage.\nLa connaissance nécessaire à une nouvelle perspective.\nL\’intuition nécessaire au choix.\nLe jugement nécessaire à la compréhension d\’une vérité plus profonde.\n\nTelle est la voie de la sagesse.', 38134),
(201538, 'frFR', 'Ce serait un honneur de m\’y rendre. Mais c\’est le marchedroit Adrestès qui est invité.', '', 38134),
(201536, 'frFR', 'Il n\’y a pas de honte à demander l\’aide d\’autrui.', '', 38134),
(201535, 'frFR', 'Merci, $p ! Je n\’ai pas grand-chose à offrir, mais je vous prie de bien vouloir accepter ce don de vision lunaire, en guise de remerciement. Maintenant que j\’ai récupéré mes outils, je vais pouvoir reprendre mon travail dans les jardins.', 'Merci, $p ! Je n\’ai pas grand-chose à offrir, mais je vous prie de bien vouloir accepter ce don de vision lunaire, en guise de remerciement. Maintenant que j\’ai récupéré mes outils, je vais pouvoir reprendre mon travail dans les jardins.', 38134),
(201534, 'frFR', '', 'Même quand le prix en est élevé, l\’équilibre doit être rétabli. Merci.', 38134),
(201531, 'frFR', 'On dirait que j\’ai perdu mon panier, ma flûte, mon marteau et ma baguette. Oh, et il me manque aussi le journal ! Si les spriggans le détruisent, c\’est toute l\’histoire du jardin qui disparaîtra…\n\nJ\’ignore comment je pourrais vous remercier, mais si vous parvenez à retrouver mes affaires, je vous serai éternellement reconnaissant.', 'On dirait que j\’ai perdu mon panier, ma flûte, mon marteau et ma baguette. Oh, et il me manque aussi le journal ! Si les spriggans le détruisent, c\’est toute l\’histoire du jardin qui disparaîtra…\n\nJ\’ignore comment je pourrais vous remercier, mais si vous parvenez à retrouver mes affaires, je vous serai éternellement reconnaissante.', 38134),
(201530, 'frFR', 'Vraiment ? Vous m\’avez effectivement l\’air redoutable, $pc, mais le danger est réel, et je m\’en voudrais qu\’il vous arrive quelque chose.', 'Vraiment ? Vous m\’avez effectivement l\’air redoutable, $pc, mais le danger est réel, et je m\’en voudrais qu\’il vous arrive quelque chose.', 38134),
(201529, 'frFR', 'À bientôt !', '', 38134),
(201526, 'frFR', 'C\’est ma faute, si Gweyir s\’est fait capturer et posséder par les Drust…', '', 38134),
(201525, 'frFR', '« Le jugement nécessaire à la compréhension d\’une vérité plus profonde. »\n\nJe sais que j\’ai triomphé de la voie, mais la vraie réponse m\’échappe encore.', '« Le jugement nécessaire à la compréhension d\’une vérité plus profonde. »\n\nJe sais que j\’ai triomphé de la voie, mais la vraie réponse m\’échappe encore.', 38134),
(201524, 'frFR', '« L\’intuition nécessaire au choix. »\n\nJe dois méditer davantage sur cette question.', '« L\’intuition nécessaire au choix. »\n\nJe dois méditer davantage sur cette question.', 38134),
(201523, 'frFR', '« La connaissance nécessaire à une nouvelle perspective. »\n\nQue suis-je censé voir ?', '« La connaissance nécessaire à une nouvelle perspective. »\n\nQue suis-je censée voir ?', 38134),
(201522, 'frFR', '« La patience nécessaire à l\’apprentissage. »\n\nJe dois prendre le temps de réfléchir. La hâte ne mène nulle part.', '« La patience nécessaire à l\’apprentissage. »\n\nJe dois prendre le temps de réfléchir. La hâte ne mène nulle part.', 38134),
(201521, 'frFR', 'AAAHHHHHH !', '', 38134),
(201520, 'frFR', 'Ô éléments… entendez mon appel… Donnez-moi la force de…', '', 38134),
(201519, 'frFR', '', 'Combien de fois vais-je encore devoir vous vaincre ?', 38134),
(201518, 'frFR', '', 'J\’en ai assez !', 38134),
(201513, 'frFR', 'Nos yeux sont habitués à la lumière lunaire et elle nous révèle maints secrets dissimulés dans notre monde.\n\nLa vision lunaire permet de voir ce que seules les Fées peuvent percevoir.', 'Nos yeux sont habitués à la lumière lunaire et elle nous révèle maints secrets dissimulés dans notre monde.\n\nLa vision lunaire permet de voir ce que seules les Fées peuvent percevoir.', 38134),
(201512, 'frFR', 'Enfin, vous voilà, $uapprenti:apprentie; !', '', 38134),
(201511, 'frFR', '', 'Ces Faë masqués ont attaqué le bosquet sans crier gare. Aidez-nous à les repousser !', 38134),
(201510, 'frFR', '', 'Je comprends l\’intérêt d\’un assaut coordonné, et j\’assisterai donc à cette réception de la cour des Braises afin d\’y représenter l\’Hallali dans les pourparlers.', 38134),
(201509, 'frFR', '', 'Il sait que vous êtes ici…', 38134),
(201508, 'frFR', '', '<Chatoyaile fait grise mine.>\n\nLa perte d\’un arbre est une affreuse tragédie. Quand l\’anima ne suffit plus à alimenter la forêt tout entière, la reine peut choisir d\’en sacrifier un pour sauver les autres.\n\nC\’est une décision déchirante, mais nécessaire.\n\nLorsqu\’un arbre meurt, toutes les graines sauvages abritées sous ses frondaisons ne peuvent être sauvées. Certaines sont confiées à la garde d\’autres bosquets, mais pour ce qui est des autres…\n\nPire encore, on raconte que dans les bosquets morts, la barrière qui sépare Sylvarden des autres royaumes ne tient plus qu\’à un fil.\n\nC\’est en ces endroits-là que nous sommes les plus vulnérables aux attaques extérieures.', 38134),
(201507, 'frFR', '', 'Eh oui ! Les Tirnenn sont des arbres majestueux qui marchent et parlent !\n\nIls sont liés d\’une manière ou d\’une autre aux arbres de Sylvarden, mais leurs secrets sont si anciens et mystérieux qu\’ils nous échappent.\n\nCertains peuvent communiquer avec les arbres, même s\’ils s\’expriment davantage par la musique que par les mots, d\’après ce que j\’ai compris.\n\nFascinant, n\’est-ce pas ?', 38134),
(201505, 'frFR', '', 'Eh oui ! Chaque arbre a un petit nom !\n\nCelui du creux Hibernal s\’appelle Tirna Glayn. Quant au bosquet de Tirna Vaal, au nord, il tient son nom de l\’arbre qui y pousse.\n\nTirna Noch, l\’arbre abattu, a succombé à la pénurie, suivi de près par Tirna Rowen, dans le bosquet du Xylocarde.\n\nParmi les arbres encore bien vivants, il y a aussi Tirna Kaithe, à Bord-de-Griffe.\n\nSans oublier Tirna Eas, dans le bassin Tombe-Lueur. Enfin, mon arbre à moi, Tirna Fenn, se trouve dans le palun Chanterêve !\n\nMais nous déplorons aussi la perte d\’autres arbres, comme Tirna Marwol, dans les Sombres-Confins.\n\nEt puis… il y a Tirna Scithe. Celui-là, je préfère éviter de m\’en approcher. Beaucoup trop dangereux !', 38134),
(201500, 'frFR', '', 'Salutations. Que voulez-vous savoir à propos de nos arbres ?', 38134),
(201490, 'frFR', '', 'Merci, $n. J\’ai… une grande dette envers vous.', 38134),
(201489, 'frFR', '', 'Vous parlez avec sagesse. J\’ai confiance en votre jugement.', 38134),
(201488, 'frFR', 'Je ne doute pas de vous, Jaina, mais vous avez besoin de temps pour récupérer. Notre $uami:amie; va pouvoir poursuivre les recherches.', '', 38134),
(201487, 'frFR', '', 'Oui… je comprends. Et pour Anduin et Thrall ? Il faut retourner à Tourment et…', 38134),
(201486, 'frFR', 'Je suis heureux de vous voir saine et sauve. Mes blessures seront bientôt refermées, mais je dois bien dire que… les horreurs de l\’Antre me poursuivent.', '', 38134),
(201485, 'frFR', '', 'Baine ! Par la Lumière, vous êtes vivant !', 38134),
(201477, 'frFR', '', 'Concentrons-nous. Le Dessein doit s\’accomplir.', 38134),
(201476, 'frFR', 'Vyraz, Draka. Conduisez cette âme mortelle jusqu\’au siège du Primat. Voyons ce qu\’il adviendra.', '', 38134),
(201475, 'frFR', 'D\’abord, vous apportez la guerre et maintenant, la lame du Primat. Votre légende s\’écrit à toute vitesse, $umortel:mortelle; !', '', 38134),
(201474, 'frFR', 'Nous manquons toujours d\’anima. J\’espère que nous parviendrons à mener cette tâche à bien.', '', 38134),
(201473, 'frFR', 'Anduin… une arme ? Que veut-elle dire ?', '', 38134),
(201464, 'frFR', 'J\’ai réussi à m\’échapper, mais j\’ai perdu mes affaires dans la panique.\n\nLes spriggans vont détruire mes outils, ça ne va pas louper !\n\nEt qui s\’occupera du jardin si je ne les récupère pas ?', 'J\’ai réussi à m\’échapper, mais j\’ai perdu mes affaires dans la panique.\n\nLes spriggans vont détruire mes outils, ça ne va pas louper !\n\nEt qui s\’occupera du jardin si je ne les récupère pas ?', 38134),
(201462, 'frFR', 'Je me trouvais dans le jardin de la Nuit quand les spriggans ont attaqué. Vague après vague, ils ont déferlé en semant la terreur dans leur sillage…', 'Je me trouvais dans le jardin de la Nuit quand les spriggans ont attaqué. Vague après vague, ils ont déferlé en semant la terreur dans leur sillage…', 38134),
(201454, 'frFR', 'Et rendre justice à mes frères disparus.', '', 38134),
(201443, 'frFR', 'Que cette couronne incarne toutes les valeurs défendues par Tombe-Vice. Revendreth s\’unira et rachètera les péchés de Denathrius.', '', 38134),
(201427, 'frFR', '', 'La nouvelle de l\’attaque se répand rapidement. Je vous accompagne.', 38134),
(201424, 'frFR', 'Kah-Delen, une mission solennelle nous a été confiée.', '', 38134),
(201422, 'frFR', 'Tout de suite, auguste voix.', '', 38134),
(201421, 'frFR', '', 'Il faut ouvrir la voie vers Maldraxxus. Allez trouver Kah-Delen et occupez-vous-en.', 38134),
(201409, 'frFR', '', 'À moins que… À moins que ça ne soit qu\’un nouveau tour encore plus cruel.', 38134),
(201408, 'frFR', '', 'Si j\’aligne mon sort de téléportation sur le don qui vous permet de sortir, je vais enfin pouvoir m\’échapper d\’ici !', 38134),
(201407, 'frFR', '', 'Mais attendez… c\’est bien vous, hein ? Oui… Je vous ai $uvu:vue; vous échapper avec la pierre d\’accès. Ça semble être il y a si longtemps…', 38134),
(201396, 'frFR', '', 'La reine a fait appel à nous, et nous répondons présents.', 38134),
(201395, 'frFR', '', '$p, vous êtes $upareil:pareille; à la tempête qui purifie la terre.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201394, 'frFR', '', 'Les branches s\’agitent et la forêt mugit !', 38134),
(201392, 'frFR', 'Noble parangon… J\’ai… échoué…', '', 38134),
(201387, 'frFR', '', 'Il faut faire front ensemble si nous voulons survivre. Ne vous éloignez pas !', 38134),
(201383, 'frFR', '', 'Voilà le parangon ! Il est seul face à Lysonia. Où sont ses disciples ?', 38134),
(201366, 'frFR', 'J\’ai envoyé Kléia à la poursuite de Lysonia. Allez vite la retrouver au temple de l\’Humilité.', '', 38134),
(201360, 'frFR', '', 'Les nécro-seigneurs sont de grands guerriers mais ils ont toujours mis leur force au service de l\’Ombreterre. Pourquoi iraient-ils l\’attaquer ?', 38134),
(201359, 'frFR', 'C\’est une nouvelle des plus inquiétantes. Si les royaumes de la mort sombrent dans la guerre, il n\’y aura rien pour freiner l\’expansion de l\’Antre.', '', 38134),
(201347, 'frFR', 'À vos ordres, madame.', '', 38134),
(201345, 'frFR', 'Hipokos secoue la tête et pointe l\’endroit où devrait se trouver sa langue.', '', 38134),
(201343, 'frFR', 'Hipokos ! Mais… que t\’ont-ils fait ? Tu ne peux plus parler ?', '', 38134),
(201337, 'frFR', '', 'Nous allons prévenir Krexus. Défendez cette position jusqu\’à notre retour.', 38134),
(201333, 'frFR', '', 'Bloubeur est en sécurité. Il vous attend sagement à Tirna Vaal.', 38134),
(201332, 'frFR', 'Kota, vous n\’auriez pas aperçu Bloubeur ? Il a disparu !', '', 38134),
(201331, 'frFR', '', 'Humpf.', 38134),
(201330, 'frFR', '', 'Inutile de vous en vouloir, ma jeune amie. Rien n\’est perdu. Il se pourrait qu\’il y ait quelqu\’un encore capable de la sauver.', 38134),
(201329, 'frFR', '', 'Ysera… J\’ai échoué. Je n\’ai pas pu ramener Tyrande. Elle erre encore dans l\’Antre, habitée par le pouvoir de la guerrière de la Nuit et mue par une rage brûlante.', 38134),
(201328, 'frFR', '', 'Elles trouveront le repos en ces lieux. Certaines pourraient même prendre une nouvelle forme pour aider à cultiver les bosquets.', 38134),
(201327, 'frFR', '', 'Que va-t-il advenir de ces âmes ?', 38134),
(201325, 'frFR', '', 'Mon esprit a failli être emporté, mais le toucher de la reine de l\’hiver m\’a éveillée. Me voilà liée à ce royaume.', 38134),
(201324, 'frFR', '', 'Il est vulnérable ! Frappez, vite !', 38134),
(201323, 'frFR', '', 'Au nom d\’Élune, vous avez vraiment la capacité de les sauver !', 38134),
(201322, 'frFR', '', 'Je vous en prie, utilisez votre cristal pour sauver les Kaldoreï piégés ici !', 38134),
(201321, 'frFR', '', 'Voici au moins quelques âmes kaldoreï libérées. Pour la première fois depuis longtemps… j\’ai un espoir.', 38134),
(201295, 'frFR', 'Vous vous croyez brave ?\n\nSoit. Prouvez-le-moi.', 'Vous vous croyez brave ?\n\nSoit. Prouvez-le-moi.', 38134),
(201292, 'frFR', 'Je suis… éternel.', '', 38134),
(201291, 'frFR', 'Votre orgueil n\’a pas de limites, $umortel:mortelle; !', '', 38134),
(201289, 'frFR', 'On ne peut pas vous laisser… sombrer dans les ténèbres…', '', 38134),
(201287, 'frFR', '', 'Sha\’lor ! Par la grâce de la reine, c\’est bon de vous revoir !', 38134),
(201286, 'frFR', 'Pfiou. Ça, c\’est ce qu\’on appelle une sortie.', '', 38134),
(201284, 'frFR', 'Notre maison ne doit pas tomber la première. N\’oublions pas que nous avons une guerre à remporter.', '', 38134),
(201279, 'frFR', 'Présentez-vous à la Pointe-de-Lance. Nous avons une bataille à gagner !', '', 38134),
(201278, 'frFR', 'Regardez ! La barrière qui cernait le temple a disparu. Beau travail, $n.', '', 38134),
(201277, 'frFR', 'Je crois avoir trouvé une sortie… mais il est hors de question que je passe par là ! Aidez-moi !', '', 38134),
(201276, 'frFR', 'Je m\’occupe d\’ouvrir la voie. Ne traînez pas, soldat !', '', 38134),
(201272, 'frFR', 'Et s\’ils ont raison ? Et si… et si…', 'Et s\’ils ont raison ? Et si… et si…', 38134),
(201271, 'frFR', '', 'Quant à moi, je ferai expier celle qui les a livrés en pâture à cet enfer !', 38134),
(201270, 'frFR', '', 'Si c\’est vrai, alors je m\’en remets à vous, $n. Sauvez les âmes de mes frères et sœurs.', 38134),
(201269, 'frFR', '', 'Si c\’est vrai, alors je m\’en remets à vous, enfant de Teldrassil. Sauvez les âmes de nos frères et sœurs.', 38134),
(201268, 'frFR', '', 'Cette âme sans-entraves peut délivrer nos frères et sœurs de leur calvaire.', 38134),
(201267, 'frFR', '', 'Non ! Tant qu\’il restera une âme à secourir et que la Banshee n\’aura pas répondu de ses crimes, ma chasse continuera !', 38134),
(201266, 'frFR', '', 'Quant à moi, je vengerai les Kaldoreï.', 38134),
(201265, 'frFR', '', 'D\’accord. Alors que ce bon petit soldat de la Horde se rachète pour les crimes de son peuple.', 38134),
(201264, 'frFR', '', 'Rien ne vous oblige à lui faire confiance… Mais faites-moi confiance à moi.', 38134),
(201263, 'frFR', '', 'Je partage votre sentiment mais je l\’ai $uvu:vue; sauver des âmes perdues. Je pense que nous n\’avons rien à craindre.', 38134),
(201262, 'frFR', '', 'Vous devrez désormais trouver les matériaux par vous-même. Référez-vous au plan pour savoir où chercher.', 38134),
(201261, 'frFR', 'Douleur… souffrance… désespoir…', '', 38134),
(201260, 'frFR', 'Allez-y. Je reste ici pour m\’occuper des autres.', '', 38134),
(201259, 'frFR', '', 'Je dois fouiller les lieux à la recherche des animaux égarés.', 38134),
(201258, 'frFR', 'Demander à un autre Kyrian de vous affronter sur la voie est quelque chose de très personnel. J\’espère que la nature cérémonielle de nos méthodes ne vous échappe pas.', '', 38134),
(201257, 'frFR', '', 'Bonjour, l\’$uami:amie;. Je serai ravie de vous aider à fabriquer votre appât et votre miroir.', 38134),
(201256, 'frFR', 'Dactylis pourra vous aider à fabriquer tout ce dont vous aurez besoin sur la voie.', '', 38134),
(201254, 'frFR', 'Vous trouverez tous les composants nécessaires ici, au Bastion. Mais il vous faudra à l\’avenir vous rendre dans d\’autres royaumes d\’Ombreterre pour vous procurer des matériaux.', '', 38134),
(201253, 'frFR', '', 'J\’ai sauvé ce que j\’ai pu. Espérons que cela suffise.', 38134),
(201250, 'frFR', 'À votre service, premier forgelite.', '', 38134),
(201249, 'frFR', 'Nous sommes tous concernés. Souvenez-vous en quand le Bastion aura besoin d\’aide.', '', 38134),
(201248, 'frFR', 'Merci de nous avoir fait confiance, premier forgelite. Sans votre aide, nous n\’aurions jamais réussi.', '', 38134),
(201247, 'frFR', 'La couronne est prête à servir un nouveau maître. L\’influence de Denathrius a disparu.', '', 38134),
(201246, 'frFR', '', 'Tu nous as causé assez d\’ennuis comme ça.', 38134),
(201245, 'frFR', '', 'Nage à ta guise, petit poisson.', 38134),
(201235, 'frFR', 'Les miens organiseront certainement des représailles militaires dès qu\’Artémède aura transmis le message.', '', 38134),
(201234, 'frFR', 'Je vous retrouverai là-bas. Faites attention.', '', 38134),
(201233, 'frFR', 'Nous devons immédiatement nous rendre à Oribos.', '', 38134),
(201231, 'frFR', 'Reculez. Sa loyauté seule doit être mise à l\’épreuve.', '', 38134),
(201230, 'frFR', 'Denathrius ! On vous couvre, Sans-Entraves !', '', 38134),
(201220, 'frFR', 'Assez de votre insolence ! FINISSONS-EN !', '', 38134),
(201219, 'frFR', 'Vous osez défier MON autorité ?', '', 38134),
(201216, 'frFR', '', 'Vous venez de loin et vous n\’êtes pas au bout de vos surprises.\nPour l\’heure, reposez vos membres fatigués.', 38134),
(201215, 'frFR', 'Pas de temps à perdre ! Vous devez aller présenter la couronne. Les fidèles de l\’Ombreterre sont ici reconnus entre tous.', '', 38134),
(201210, 'frFR', '$UParé:Parée; à vous rendre au temple ?', '', 38134),
(201199, 'frFR', 'Qui va là ? Vous êtes $uvenu:venue; nous aider ?', '', 38134),
(201196, 'frFR', 'La sagacité de percer à jour les manigances adverses.', '', 38134),
(201195, 'frFR', 'La force d\’écraser ses ennemis.', '', 38134),
(201194, 'frFR', 'Alors, il faut y aller. Le temple est le seul moyen d\’affranchir la couronne de l\’influence de Denathrius.', '', 38134),
(201193, 'frFR', 'La ruse pour mettre ses ennemis en échec.', '', 38134),
(201192, 'frFR', 'La barrière qui cernait le temple a disparu.', '', 38134),
(201191, 'frFR', 'L\’ambition de s\’élever au-dessus des autres.', '', 38134),
(201189, 'frFR', 'Gagné.', '', 38134),
(201188, 'frFR', 'Votre heure a sonné.', '', 38134),
(201187, 'frFR', 'Regardez votre défaite.', '', 38134),
(201186, 'frFR', 'Vous avez oublié de lever les yeux.', '', 38134),
(201181, 'frFR', 'Bravo. Ils peuvent bien essayer de se cacher ou de s\’enfuir, personne n\’échappe à l\’Hallali !', '', 38134),
(201180, 'frFR', 'J\’ai envoyé un petit groupe de chasseurs en patrouille au maquis Voile-de-Brume. Recevoir votre soutien serait sans doute bon pour leur moral.', '', 38134),
(201170, 'frFR', 'C\’est impossible !', '', 38134),
(201160, 'frFR', '', 'Je chevaucherai peut-être Balthazar jusqu\’à la cour des Braises…', 38134),
(201157, 'frFR', 'Aujourd\’hui, on fête Covinkles !\n\nIl travaille dur avec son marteau.\n\nSon atelier est à côté de la forge Éternelle.\n\nVous lui apportez un purian mûr ?', '', 38134),
(201141, 'frFR', '<L\’écho du parangon vaincu vous dévisage.>\n\nAvez-vous trouvé le chapitre manquant de mon histoire, $umortel:mortelle; ?', '<L\’écho du parangon vaincu vous dévisage.>\n\nAvez-vous trouvé le chapitre manquant de mon histoire, $umortel:mortelle; ?', 38134),
(201140, 'frFR', 'Si vous voulez en apprendre davantage, vous devrez trouver le tome et me le rapporter.', 'Si vous voulez en apprendre davantage, vous devrez trouver le tome et me le rapporter.', 38134),
(201137, 'frFR', 'Je ne suis qu\’un écho du savoir du parangon défunt, mais je vois que vous avez soif de connaissances.\n\nMon histoire est incomplète. L\’ultime chapitre a été caché par mes disciples. Les désavoués cherchent à déformer la vérité. Mais l\’histoire complète ne peut servir leurs desseins. Ils la détruiront s\’ils l\’obtiennent.\n\nJe vais vous conférer le pouvoir de poser les yeux sur ce tome sacré. Rapportez-le-moi et vous connaîtrez la véritable fin de cette histoire.\n\nQu\’en dites-vous ?', 'Je ne suis qu\’un écho du savoir du parangon défunt, mais je vois que vous avez soif de connaissances.\n\nMon histoire est incomplète. L\’ultime chapitre a été caché par mes disciples. Les désavoués cherchent à déformer la vérité. Mais l\’histoire complète ne peut servir leurs desseins. Ils la détruiront s\’ils l\’obtiennent.\n\nJe vais vous conférer le pouvoir de poser les yeux sur ce tome sacré. Rapportez-le-moi et vous connaîtrez la véritable fin de cette histoire.\n\nQu\’en dites-vous ?', 38134),
(201135, 'frFR', 'En route, Bloubeur ! L\’heure est venue de faire preuve de courage !', '', 38134),
(201128, 'frFR', '', 'Sauliflore acquiesce.', 38134),
(201124, 'frFR', 'Je compte sur vous pour vous en charger, général. Moi, je pars en éclaireur.', '', 38134),
(201123, 'frFR', 'Kel\’Thuzad nous provoque depuis sa cachette, mais plus pour longtemps. Ses nécromanciens finiront par nous dire où il se terre.', '', 38134),
(201115, 'frFR', '', 'Sauliflore se demande si elle n\’a pas induit Pélagos en erreur, puis elle hausse les épaules.', 38134),
(201114, 'frFR', '', 'Laissez-moi éprouver vos techniques de combat.', 38134),
(201112, 'frFR', 'Moi aussi, je peux vous aider. Cette protection améliorera nettement vos chances de survie.', '', 38134),
(201111, 'frFR', 'Je vous soutiendrai depuis le ciel.', '', 38134),
(201104, 'frFR', 'Il ne reste qu\’une ultime étape !', '', 38134),
(201098, 'frFR', '', 'Vous devez interrompre le rituel de Lysonia. Elle ne doit pas imposer sa volonté au parangon.', 38134),
(201097, 'frFR', '', 'La cime Automnale est juste au nord. J\’espère qu\’il n\’est pas trop tard.', 38134),
(201096, 'frFR', '', 'Mettons à profit le répit qui nous est offert. Faites-moi votre rapport. Pendant ce temps, $n ira se renseigner sur les désavoués.', 38134),
(201095, 'frFR', '', 'Chyrus a courageusement choisi de risquer sa vie pour détourner son attention des disciples.', 38134),
(201094, 'frFR', '', 'Le parangon se trouve près du temple, où il se mesure seul à Lysonia !', 38134),
(201093, 'frFR', '', 'Ce sillage sanglant devrait nous mener à elle.', 38134),
(201091, 'frFR', '', 'C\’est l\’œuvre de ma minn\’do. Je l\’ai vu en rêve.', 38134),
(201085, 'frFR', '', 'Aucune âme n\’a pu être jugée depuis ce jour. Les malheureuses sont livrées aux horreurs de l\’Antre.', 38134),
(201075, 'frFR', 'Le fort de la Lame d\’ébène, Achérus, s\’est déplacé à la Couronne de glace. Ma porte de la mort va nous y conduire.', '', 38134),
(201074, 'frFR', 'Vous voilà ! Les préparatifs sont terminés.', '', 38134),
(201063, 'frFR', 'Je tiens à être témoin de l\’histoire que vous écrivez.', '', 38134),
(201062, 'frFR', 'Je vais en informer Vyraz et je vous retrouverai au siège du Primat.', '', 38134),
(201060, 'frFR', '', 'Je ne sais pas quel mal Tyrande trouvera dans cet tour, mais nous ne pouvons pas la laisser l\’affronter seule.', 38134),
(201059, 'frFR', '', 'Je vais détruire cette tour de malheur, et la Banshee avec !', 38134),
(201058, 'frFR', '', 'Non ! Pas tant que les nôtres souffrent mille morts dans Tourment !', 38134),
(201057, 'frFR', '', 'Minn\’do ! Quittez l\’Antre avec nous… avant que le pouvoir de la guerrière de la Nuit n\’ait raison de vous.', 38134),
(201056, 'frFR', 'Il est temps de passer à l\’attaque ! Les heures de Kel\’Thuzad sont comptées.', '', 38134),
(201055, 'frFR', 'Il suffit ! Ces diversions ne nous détourneront pas de notre but.', '', 38134),
(201054, 'frFR', 'Allez-y, $n. Nous vous rejoindrons.', '', 38134),
(201053, 'frFR', 'Pour l\’en délivrer, il faut l\’apporter à la citadelle de la Loyauté.', '', 38134),
(201052, 'frFR', 'La couronne reste soumise à l\’influence de Denathrius.', '', 38134),
(201045, 'frFR', 'Je vais bien. On doit accéder au temple pour réparer la couronne. Mais d\’abord, il faut désactiver cette barrière.', '', 38134),
(201044, 'frFR', 'Navré pour ce contretemps, $n. Notre nouvel allié refusait obstinément de croire en ma capacité à voler.', '', 38134),
(201034, 'frFR', 'Bienvenue dans notre bosquet.', 'Bienvenue dans notre bosquet.', 38134),
(201015, 'frFR', 'C\’est Mueh\’zala qui a attiré Helya dans le camp du Geôlier… qui a échangé son œil au vieil Odyn... Et qui a chuchoté au chef de guerre de choisir la banshee comme successeur !', '', 38134),
(201014, 'frFR', 'Secouez-vous, les héros ! Rapportez-moi mon mojo et aidez-moi à remettre ce vieux Loa à sa place !', '', 38134),
(201013, 'frFR', 'Tu crois que tu as gagné ? Hum. Peut-être que tu rumines dans les ombres depuis trop longtemps.', '', 38134),
(201012, 'frFR', 'Tes petits amis croient pouvoir t\’aider, Bwonsamdi ? Quelle chance avez-vous contre moi ?', '', 38134),
(201011, 'frFR', 'Hakkar, c\’est un mauvais. Il a une dette et il faut qu\’il paie. Mais il reviendra tôt ou tard. Un mauvais Loa, on ne s\’en débarrasse jamais !', '', 38134),
(201010, 'frFR', 'Ne faites pas cette tête. Bien sûr que Hakkar, c\’est un Loa du sang. Mais un marché, c\’est un marché ! Tss. Rapportez-moi mon mojo.', '', 38134),
(201009, 'frFR', 'Vous le reconnaissez, ce Loa, hein ? Hakkar, a toujours besoin de mojo pour ses rituels… et Bwonsamdi, est toujours prêt à négocier.', '', 38134),
(201008, 'frFR', 'Les négociants, ils préparent quelque chose. Faut les garder à l\’œil. GNNN ! Dépêchez-vous de me rapporter mon mojo !', '', 38134),
(201007, 'frFR', 'Celle-là, elle ne va pas vouloir me rendre mon mojo. Attention… c\’est une sacrée roublarde.', '', 38134),
(201006, 'frFR', 'Bwonsamdi ne limite pas ses marchés à Azeroth. Les négociants, ils ont besoin de pouvoir… beaucoup de pouvoir.', '', 38134),
(201005, 'frFR', 'Les adorateurs, je n\’en ai pas besoin ! Le Geôlier, il me donne tout le pouvoir qu\’il me faut… C\’est Bwonsamdi qu\’on va oublier !', '', 38134),
(201004, 'frFR', 'C\’était il y a bien longtemps, mais je n\’ai pas oublié. Les Trolls, ils se détournaient de toi. Tous leurs sacrifices… tout ce sang… ils allaient GNNNNN !', '', 38134),
(201003, 'frFR', 'N\’oublie pas qui a fait de toi un Loa, Bwonsamdi ! Donne-moi ces âmes et agenouille-toi devant ton maître !', '', 38134),
(201002, 'frFR', 'La mort, c\’est le seul pouvoir qui compte ! Et personne ne peut lui échapper !', '', 38134),
(201001, 'frFR', 'Tu as oublié ce que c\’est, qu\’être un Loa ! Le lien avec le peuple… le pouvoir partagé… GNNNNN !', '', 38134),
(201000, 'frFR', 'Je veux leurs âmes, Bwonsamdi ! Je veux Rastakhan et les autres Zandalari ! C\’est pas bien malin de s\’opposer à Mueh\’zala !', '', 38134),
(200999, 'frFR', 'Ce Loa, je me charge de l\’occuper. Allez-y. Suivez les chemins. Et ne revenez pas les mains vides !', '', 38134),
(200998, 'frFR', 'Mueh\’zala, il m\’a joué un sale tour. Mais si vous allez voir certains de mes… partenaires… pour récupérer mon mojo, on pourra lui rendre la monnaie de sa pièce !', '', 38134),
(200997, 'frFR', 'Une minute… Qu\’est-ce que… ? Des héros ! Euh… J\’espère que vous ne m\’en voulez pas trop pour Zuldazar, hein ?', '', 38134),
(200996, 'frFR', 'Une minute… Qu\’est-ce que… ? Des héros ! Bwonsamdi, il savait que vous ne le laisseriez pas tomber.', '', 38134),
(200995, 'frFR', 'Tu as toujours été faible, Bwonsamdi. Tu craqueras, tôt ou tard. Le Geôlier aura son dû !', '', 38134),
(200994, 'frFR', 'Pour que tu les envoies dans l\’Antre ? Non, non, non… Pas question que je te livre mon peuple, Mueh\’zala !', '', 38134),
(200993, 'frFR', 'Les âmes des Zandalari, où les caches-tu, Bwonsamdi ? Donne-les à ton chef ou ça va mal tourner. Très mal !', '', 38134),
(200992, 'frFR', 'Ah, l\’amour… Émouvant, hein ? Bwonsamdi, a eu son lot d\’aventures à l\’époque. Et même plus que son lot, hé hé !', '', 38134),
(200987, 'frFR', 'Apprenez du passé, mais tournez-vous vers l\’avenir.', '', 38134),
(200977, 'frFR', '', 'Servez avec courage.', 38134),
(200970, 'frFR', '', 'Aucun fardeau n\’est éternel.', 38134),
(200963, 'frFR', 'J\’espère que votre présence n\’aura pas d\’impact sur mes bénéfices.', '', 38134),
(200962, 'frFR', '', 'Évitez les Drust pendant que vous récupérez le pollen des roses de l\’effroi. Ensuite, saupoudrez-en sur les Sylvars profanés pour les rendre fous furieux. Avec un peu de chance, ça suffira à perturber les plans des Drust.', 38134),
(200933, 'frFR', '', 'Hé ! Par ici !', 38134),
(200932, 'frFR', 'Profitez donc de la mélodie apaisante de trois humbles âmes.', '', 38134),
(200931, 'frFR', 'Merci, Croupion. Nous sommes ravis d\’avoir été invités à jouer ici.', '', 38134),
(200930, 'frFR', 'Calice Perdu est prêt à commencer son spectacle !', '', 38134),
(200929, 'frFR', 'Ouvrez votre cœur et votre âme à la musique la plus poignante d\’Ombreterre !', '', 38134),
(200928, 'frFR', '', 'Cachez-vous, bon sang ! Ça grouille de Drust par ici, $n ! Il y en a partout ! Ils préparent un mauvais coup et nous devons les arrêter !\n\nBon, voilà ce que je vous propose. Les Drust ont asservi un groupe de Sylvars grâce à ces masques étranges. Les pauvres sont entièrement sous leur coupe. Il est trop tard pour les sauver, mais ils peuvent encore nous être utiles.\n\nVous voyez cette fleur, à côté de moi ? Ne la touchez surtout pas, c\’est une rose de l\’effroi ! Au contact de son pollen, les Sylvars deviennent fous furieux et finissent par perdre connaissance. Je vais utiliser ma magie pour vous déguiser en phalarden. Récupérez du pollen de rose de l\’effroi, puis saupoudrez-en sur les profanés pour semer le chaos.\n\nSuccès garanti à cent pour cent !\n\nOn peut y aller ?', 38134),
(200927, 'frFR', 'Cette âme mortelle prétend avoir réussi à s\’en échapper ! Vérifions ses dires.', '', 38134),
(200925, 'frFR', '', 'N\’était-il pas à la merci de l\’Antre ?', 38134),
(200924, 'frFR', 'Et si jamais cette salle reste vide ? C\’est de la folie de croire en ce siège.', '', 38134),
(200922, 'frFR', '', 'Sans Krexus, nous n\’avons aucune chance de gagner. Le siège du Primat est notre seul espoir.', 38134),
(200921, 'frFR', '', 'Vous, $p, avec moi ! Vite !', 38134),
(200920, 'frFR', 'Ils… ils ont détruit la nécropole. Krexus est…', '', 38134),
(200919, 'frFR', 'Les assemblages… ont riposté… Ils étaient trop nombreux…', '', 38134),
(200918, 'frFR', '', 'Vyraz ! Que s\’est-il passé ?', 38134),
(200916, 'frFR', 'Hahahahahahahaha !', 'Hahahahahahahaha !', 38134),
(200915, 'frFR', 'Stupide insecte ! Je te lâcherai pas !', 'Stupide insecte ! Je te lâcherai pas !', 38134),
(200914, 'frFR', 'Plus vite, stupide créature !', 'Plus vite, stupide créature !', 38134),
(200913, 'frFR', 'En selle !', 'En selle !', 38134),
(200910, 'frFR', '', 'Je suis nerveuse, $p. La musique met mon âme à nu, à la vue de tous.\n\nEt bien que cette stèle du vice rappelle à tous les ténèbres qui m\’habitent, j\’espère que ma musique révélera également ma lumière.\n\nÀ votre ambassade, le seigneur Garridan était du même avis. Il s\’est montré d\’une grande gentillesse et a apprécié les airs que je lui ai joués à mon arrivée.\n\nJ\’ai dû laisser mon luth avec lui. S\’il vous plaît, récupérez-le pendant que je termine ma méditation avant le spectacle.', 38134),
(200909, 'frFR', '', 'Cette défaite ne fait que vous rapprocher un peu plus de la victoire.', 38134),
(200907, 'frFR', '<Ansel le Veinard détourne le regard de sa stèle du vice, à regret.>\n\nMes fardeaux sont encore bien trop lourds.\n\nTant qu\’il reste le moindre vice sur cette stèle, je ne pourrai échapper aux dangers de l\’Antre.\n\nPar chance, mon don pour la musique me garantit les bonnes grâces des Venthyrs, et je sais qu\’aider le prince Rénathal à la cour est considéré comme un effort appréciable.\n\nRécupérez mon violon dans le matériel du groupe, près des danseurs, et je pourrai entamer ma pénitence publique.', '', 38134),
(200902, 'frFR', '<Noko prend une lente inspiration.>\n\nLe régisseur ne trouve ma flûte nulle part.\n\nLa dernière fois que cela s\’est produit, l\’un des machinistes bagarreurs s\’était enfui avec.\n\nCes purotins sont pénibles et causent des ennuis un peu partout à la cour.\n\nSi vous parvenez à leur reprendre ma flûte, je pourrai jouer pour vos invités.', '', 38134),
(200900, 'frFR', 'L\’esprit a retrouvé son calme, on dirait… Merci, mon $uami:amie;.', '', 38134),
(200895, 'frFR', '', 'Le premier morceau vous sera dédié.', 38134),
(200894, 'frFR', '', 'Les Drust ont transformé les miens en bêtes sauvages ! Voyez avec mon amie Pied-d\’Ambre comment défendre le bosquet.', 38134),
(200893, 'frFR', 'Je crains que le voyage ne soit pas des plus plaisant. Enfin, bonne chance !', 'Je crains que le voyage ne soit pas des plus plaisant. Enfin, bonne chance !', 38134),
(200892, 'frFR', 'De là, vous pourrez sauter dans le Pilier d\’âme. Il s\’agit du torrent d\’âmes qui se déverse dans l\’Antre.', 'De là, vous pourrez sauter dans le Pilier d\’âme. Il s\’agit du torrent d\’âmes qui se déverse dans l\’Antre.', 38134),
(200891, 'frFR', 'Cette plateforme de téléportation vous mènera à l\’anneau de transfert.', 'Cette plateforme de téléportation vous mènera à l\’anneau de transfert.', 38134),
(200889, 'frFR', '$p, veuillez me suivre.', '$p, veuillez me suivre.', 38134),
(200888, 'frFR', 'Je vais délivrer la meilleure prestation de toute ma carrière.', '', 38134),
(200886, 'frFR', 'Cette lame… elle a été forgée par le Primat !', '', 38134),
(200885, 'frFR', 'Je sens… une puissance… vibrer dans l\’air…', '', 38134),
(200882, 'frFR', 'Puisse notre musique aussi guider votre âme, mon $uami:amie;.', '', 38134),
(200880, 'frFR', 'Il tient Baine !', '', 38134),
(200879, 'frFR', '', 'Le Geôlier.', 38134),
(200876, 'frFR', 'Votre premier défi vous attend, Pélagos. Sonnez le bourdon lorsque vous serez prêt.', '', 38134),
(200871, 'frFR', 'Qui je suis ? Une âme, bien entendu !\n\nQuand mon heure a sonné, j\’ai été appelé en Sylvarden. Toute ma vie, j\’ai travaillé avec les esprits, alors quoi de plus… naturel ?\n\nQue dites-vous de ma forme d\’âme ? J\’ai toujours voulu parcourir les étendues sauvages dans la peau d\’un loup !', '', 38134),
(200867, 'frFR', '', 'Le message porte un sceau magique ? Pff, même pas drôle…', 38134),
(200865, 'frFR', 'Vais à la fête ! Vais manger du bon miam-miam !', '', 38134),
(200863, 'frFR', '', 'Il faut faire vite. Vous vous sentez en état de voyager ?', 38134),
(200856, 'frFR', 'Nos alliés ne peuvent pas affronter ce monstre à eux seuls ! Nous devons les aider !', '', 38134),
(200850, 'frFR', '', 'Pitié, libérez-moi ! Cela fait bien trop longtemps que les bosquets de Sylvarden sont privés de mes bons soins.', 38134),
(200847, 'frFR', 'Oui, il faut vite que je consigne ça dans mes notes… Mais où sont-elles passées ?', '', 38134),
(200846, 'frFR', 'Ça ne va pas du tout. J\’avais des notes, je suis formel. Des notes de recherche sur l\’anima.\n\nGrands dieux, on dirait que je les ai égarées.', '', 38134),
(200842, 'frFR', 'Pas mal du tout ! Allons vite chercher mon sabre et donnons une bonne correction à ces choses.', '', 38134),
(200841, 'frFR', 'Oui, vous m\’avez eu. Et alors ? Vous allez encore me narguer ? Oh, mais… vous n\’êtes pas des leurs.\n\nJe me suis fait capturer et on m\’a pris mes armes. C\’est risqué, mais vous voulez bien m\’aider à m\’évader ?', '', 38134),
(200838, 'frFR', '', 'Allons chercher… Mince, ça m\’échappe.', 38134),
(200837, 'frFR', '', 'Ils m\’ont coincée ici avant de m\’empoisonner l\’esprit. Il me semble avoir consigné mes souvenirs juste avant, mais je ne suis plus sûre de rien.', 38134),
(200834, 'frFR', '', 'Bon ! Allons vite trouver ce traque-tombe.', 38134),
(200833, 'frFR', '', 'Cette chose m\’a enfermée dans ce trou grouillant de morts. J\’ai perdu la notion du temps, mais ça n\’a que trop duré ! Libérez-moi et aidez-moi à l\’abattre !', 38134),
(200830, 'frFR', 'Merci ! Allons vite trouver Énothia.', '', 38134),
(200829, 'frFR', 'Décidément, tout va de travers. J\’essayais de secourir Énothia quand je me suis fait capturer à mon tour. Je vous en prie, aidez-nous !', '', 38134),
(200826, 'frFR', '', 'Ils approchent. Ne faites pas de quartier.', 38134),
(200825, 'frFR', '', 'Sans-Entraves. Je vous ai $uvu:vue; arpenter ces salles.\n\nHélas, mon excès de confiance a entraîné ma capture. Je n\’ai pas pu accomplir ma mission et obtenir des informations auprès d\’un serviteur haut placé du Geôlier.', 38134),
(200821, 'frFR', 'Partez.\n\nJ\’ai été vaincu.\n\nSans mon armure, je ne tiendrai pas longtemps.', '', 38134),
(200818, 'frFR', 'La créature n\’est pas loin. Un bon démembrement devrait lui servir de leçon.', '', 38134),
(200817, 'frFR', 'Salutations ! Ou soyez $ubéni:bénie;, selon vos préférences en matière de formules de politesse.\n\nJ\’étais à la poursuite… du fruit de mes expériences quand je me suis retrouvé dans cette situation incongrue. Le bougre s\’est révélé plus malin que je ne le pensais !\n\nEt si vous me libériez ? Nous pourrions récupérer ce qui m\’appartient, du moins les morceaux utiles.', '', 38134),
(200814, 'frFR', 'Je dois trouver mon épée. Sans elle, je ne pourrai pas m\’enfuir.', '', 38134),
(200813, 'frFR', 'Vous venez me tourmenter ? Non, vous n\’êtes pas des leurs !\n\nJ\’ai été condamné à passer l\’éternité prisonnier de ces chaînes, de ces murs.\n\nAidez-moi à retrouver mon épée, c\’est mon seul espoir de regagner un semblant d\’honneur.', '', 38134),
(200810, 'frFR', 'Allons chercher ma baguette ! Enfin, ce n\’est pas encore la mienne. Il est possible qu\’il faille avoir recours à la violence pour l\’obtenir.', '', 38134),
(200809, 'frFR', 'Ah, $ule:la; légendaire Sans-Entraves ! Ma soif de pouvoir est la cause de cette situation embarrassante, j\’en ai bien peur.\n\nAuriez-vous l\’amabilité de me libérer ?\n\nOh, et j\’aurais également un petit compte à régler, si possible.', '', 38134),
(200803, 'frFR', 'Pris au piège !\n\nLibérez-moi, petit être.\n\nJe dois retourner à mon maître.', '', 38134),
(200799, 'frFR', 'Des pièces détachées sont entreposées ici. Elles semblent provenir de quelque chose d\’important.', '', 38134),
(200796, 'frFR', 'Merci. Pour les dernières chaînes, vous aurez besoin de la clé que transporte le garde qui m\’a enfermé.', '', 38134),
(200795, 'frFR', 'Ces créatures m\’ont capturé et enchaîné ici. S\’il vous plaît, libérez-moi !', '', 38134),
(200792, 'frFR', '', 'Merci. Partons à la recherche de mon épée.', 38134),
(200791, 'frFR', '', 'C\’est vous que l\’on nomme « Sans-Entraves » ? On m\’a volé l\’épée que je tenais de mes ancêtres. Je vous en prie, aidez-moi à la récupérer.', 38134),
(200780, 'frFR', 'Je m\’en occupe.', '', 38134),
(200778, 'frFR', 'Ce défi ne me fait pas peur !', '', 38134),
(200776, 'frFR', 'Ha ha ! Qu\’est-ce qu\’on s\’amuse !', '', 38134),
(200770, 'frFR', 'Merci, cette abomination ne doit pas être bien loin.', '', 38134),
(200769, 'frFR', 'Mon maître a été tué par ici. J\’ai fini seul et rongé par la honte. Je vous en prie, aidez-moi à finir la mission qui lui avait été confiée.', '', 38134),
(200766, 'frFR', '', 'Allons rendre visite à cette abomination.', 38134),
(200765, 'frFR', '', 'Ma lance s\’est brisée pendant que j\’affrontais l\’une des monstruosités qui peuplent cet endroit. Aidez-moi à en venir à bout pour que je puisse m\’emparer de ses armes !', 38134),
(200764, 'frFR', 'J\’étais capable de rivaliser avec les meilleurs, mais il semble que j\’aie perdu tout mon savoir-faire. Je ne vois qu\’une seule explication possible : l\’on m\’a volé mes souvenirs. Je vous en prie, aidez-moi à les récupérer.', '', 38134),
(200763, 'frFR', 'Trouvons une stèle mémorielle.', '', 38134),
(200760, 'frFR', 'J\’ai besoin d\’anima ! Débarrassez-vous de mon bourreau, c\’est lui qui m\’a volé mes réserves, et je veux qu\’on me les rende.', '', 38134),
(200759, 'frFR', 'Allons récupérer mon anima.', '', 38134),
(200756, 'frFR', '', 'Je vous en prie, délivrez-moi ! Qui sait depuis combien de temps je suis attachée, là.', 38134),
(200752, 'frFR', 'Je dois retrouver ma stèle du vice ! Il y a des âmes à écraser !', '', 38134),
(200751, 'frFR', 'Où qu\’elle est, ma stèle du vice ?', '', 38134),
(200746, 'frFR', 'Parfait. Allons vite trouver cette gargouille et lui faire payer ça.', '', 38134),
(200745, 'frFR', 'J\’ai été enlevé ! C\’est une gargouille renégate qui m\’a emmené ici. Libérez-moi ! Il faut que je me venge !', '', 38134),
(200742, 'frFR', 'La lutte pour préserver Sylvarden n\’est pas encore terminée.', '', 38134),
(200741, 'frFR', 'Allons récupérer c\’te pelle !', '', 38134),
(200740, 'frFR', 'Mais c\’est qu\’y m\’ont piqué ma pelle ! V\’pourriez pas m\’aider à la récupérer, des fois ?', '', 38134),
(200737, 'frFR', '', 'Allons mettre la main sur mes souvenirs. Ils devraient se trouver dans une stèle mémorielle, quelque part dans les environs.', 38134),
(200735, 'frFR', '', 'Qui êtes-vous ? Attendez, qui suis-je ? Mes souvenirs m\’échappent, je dois retrouver ma stèle mémorielle pour les restaurer.', 38134),
(200733, 'frFR', '<Choisir une formule de fin.>', '', 38134),
(200731, 'frFR', 'Appliquez à vos futures épreuves les leçons que vous avez apprises ici, mon $uami:amie;.', '', 38134),
(200728, 'frFR', 'Sa présence est requise à la cour des Braises, car _______', '', 38134),
(200726, 'frFR', 'Veuillez libérer ________ de son tour de garde.', '', 38134),
(200701, 'frFR', 'Espérons que les papilles de nos invités soient moins délicates que les miennes.', '', 38134),
(200700, 'frFR', 'Au moins, ce devrait être moins dangereux que ce que vous avez rapporté de Maldraxxus.', '', 38134),
(200699, 'frFR', 'Tout de suite, patron.', '', 38134),
(200698, 'frFR', 'Serveur, apportez immédiatement ces… spécialités locales… à table.', '', 38134),
(200697, 'frFR', 'Répugnant, immangeable, nauséabond…', '', 38134),
(200696, 'frFR', 'Stéfan le Pinailleur examine les champignons que vous lui avez apportés.', '', 38134),
(200695, 'frFR', 'Vous devez être la personne dont l\’émissaire du Bastion a parlé.', '', 38134),
(200694, 'frFR', 'Je prépare encore l\’équipement. Revenez plus tard. Après avoir fabriqué ce qu\’il faut !', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200692, 'frFR', 'J\’ai localisé votre armée, Draven.', '', 38134),
(200691, 'frFR', 'Sans-Entraves, détruisez, avec l\’Elfe de sang, les cristaux qui drainent leur anima. Je vais continuer à patrouiller dans le ciel.', '', 38134),
(200690, 'frFR', 'Kael\’thas nous fait signe, là en bas. Nous devons atterrir !', '', 38134),
(200689, 'frFR', '', 'Mon amie… Je pleure votre mort et le rôle que j\’ai dû jouer dans votre disparition.\n\nJ\’espère que l\’histoire ne s\’achève pas ainsi.', 38134),
(200688, 'frFR', 'Ma bien-aimée a pris la bonne décision en tentant de vous sauver. Je regrette que les choses se soient terminées ainsi.\n\nLe Rêve ne sera plus jamais le même.', '', 38134),
(200687, 'frFR', '', 'Mère, si seulement vous étiez là, à mes côtés. Les nôtres gardent espoir, mais tout est plus difficile sans vous.\n\nJ\’ai besoin de vos lumières.', 38134),
(200686, 'frFR', 'Vous étiez la mère qui marchait à mes côtés sur la voie du Rêve. Vous m\’avez tant appris.\n\nLe monde a besoin de vous.', '', 38134),
(200684, 'frFR', '<Vous avez tout le nécessaire pour contrefaire la feuille de permission de Tête-de-Caillasse.\n\nVous devez à présent décider quoi écrire.>', '', 38134),
(200681, 'frFR', 'Balourd ? Le puissant Milhouse Tempête-de-Mana est le plus adroit de tous les Gnomes !', '', 38134),
(200680, 'frFR', '', 'Hé ! Ça fait mal, gros balourd !', 38134),
(200679, 'frFR', '', 'Ne reste pas sur mon chemin, Milhouse !', 38134),
(200678, 'frFR', 'Aïeuh ! Fais attention, Millificent !', '', 38134),
(200677, 'frFR', 'C\’est toi la meilleure, Milly ! Je ne veux plus qu\’on se dispute ! Au moins quelque temps…', '', 38134),
(200675, 'frFR', '', 'Vous avez le chic pour être toujours au cœur de l\’action, vous alors !', 38134),
(200674, 'frFR', 'Pour… vous… je saurai faire face…', '', 38134),
(200671, 'frFR', '', 'La tempête se calme… pour l\’instant.', 38134),
(200669, 'frFR', '', 'Nous ne sommes pas les seuls à avoir contemplé ce cauchemar. Les Drust approchent !', 38134),
(200667, 'frFR', '', 'Votre attention à tous s\’il vous plaît !', 38134),
(200658, 'frFR', '', 'Bien le bonjour, $n !\n\nSi vous venez pour parler des voyages dans le temps, j\’ai bien peur que vous n\’ayez besoin d\’en apprendre un peu plus sur votre présent avant de pouvoir visiter le passé.\n\nCe sera peut-être possible une fois que vous aurez visité la ville et rencontré le roi !', 38134),
(200657, 'frFR', '', 'Ooooh, trésor ! Tu t\’inquiètes pour moi !', 38134),
(200656, 'frFR', 'Quoi que je fasse, ces cauchemars reviennent sans cesse ! Vous pourriez tenter une approche plus… directe ?', '', 38134),
(200647, 'frFR', '', 'Ringard !', 38134),
(200646, 'frFR', 'Eh bien, je… euh… Mais ! Minute papillon ! Tu viens d\’avouer avoir fait la même chose que moi ! Hypocrite !', '', 38134),
(200645, 'frFR', 'Mais c\’est pas tout ça… Rendez-moi mon mojo !', '', 38134),
(200644, 'frFR', 'Comme ces deux Gnomes un peu dérangés, là. Ils avaient soif de pouvoir, j\’ai été ravi de les aider.', '', 38134),
(200643, 'frFR', 'Il n\’y a pas que les Trolls qui demandent de l\’aide à Bwonsamdi. Tout le monde veut passer des marchés avec le Loa de la mort !', '', 38134),
(200641, 'frFR', 'Ma puissance est indéniable ! Vous œuvrerez à MON service !', '', 38134),
(200640, 'frFR', 'Par l\’Archonte ! Ça vient du médaillon enchâssé dans la couronne. Faites vite, Sans-Entraves !', '', 38134),
(200639, 'frFR', 'Beau travail. Je reviendrai encore plus fort.', '', 38134),
(200638, 'frFR', 'Cette voix ? D\’où vient-elle ?', '', 38134),
(200637, 'frFR', 'Oui… Renforcez la couronne.', '', 38134),
(200621, 'frFR', 'Il faut en informer la dromane. Une graine qui se lie à son soigneur est une occasion rare et réjouissante !', '', 38134),
(200620, 'frFR', 'Celui-ci est différent. Voilà le véritable ennemi… les Drust.', '', 38134),
(200618, 'frFR', '', 'Vyraz a beau être présomptueux, il n\’a pas tort. Il nous faut des renforts.', 38134),
(200616, 'frFR', 'Quel souvenir voulez-vous défier ?', '', 38134),
(200615, 'frFR', '', 'Pas mal ! Pour quelqu\’un qui n\’a pas d\’ailes, bien sûr.', 38134),
(200614, 'frFR', '', 'Les aspirants se préparent à éprouver leur précision en vol. Vous pensez pouvoir les égaler ?', 38134),
(200611, 'frFR', 'Nous ne cessons jamais de nous entraîner pour tenir notre rang.', '', 38134),
(200610, 'frFR', '', 'C\’est par le désarroi d\’un esprit que s\’incarne le cauchemar. Tisse-Rêves a besoin de votre aide.', 38134),
(200608, 'frFR', 'Je sais peut-être où en trouver davantage.', '', 38134),
(200607, 'frFR', 'Non. Les cauchemars persistent. Il nous faut davantage d\’anima.', '', 38134),
(200606, 'frFR', '', 'Est-ce que ça a marché ?', 38134),
(200605, 'frFR', 'Espérons que ça suffira…', '', 38134),
(200603, 'frFR', '', 'Le siège du Primat est attaqué ! Il faut le défendre !', 38134),
(200601, 'frFR', '', 'Porte-Cendres, Sans-Entraves ! Venez !', 38134),
(200599, 'frFR', 'Bien sûr ! Proglo a conservé cet anima au cas où une telle situation se présenterait.\n\nTenez. Faites attention, et s\’il vous faut quoi que ce soit, n\’oubliez pas que Proglo est l\’une des âmes les plus courageuses de Sylvarden !', '', 38134),
(200597, 'frFR', 'AH ! Vous êtes quoi, au juste ? Quatre spriggans sous un déguisement de… votre espèce ?\n\nOu une de ces monstruosités chancreuses ?\n\nQuoi que vous soyez… partez ! Pitié, laissez-moi tranquille !', '', 38134),
(200596, 'frFR', 'Salutations ! Vous êtes là pour l\’anima ? Proglo a justement fait des réserves afin de pourvoir aux besoins des graines sauvages.\n\nCes saletés de spriggans et de créatures chancreuses ne mettront pas la main dessus !\n\nProglo est un brave parmi les braves. Rien ne lui échappe !', '', 38134),
(200593, 'frFR', 'Préparons tout pour le rituel !', '', 38134),
(200592, 'frFR', 'Le général Draven approche !', '', 38134),
(200591, 'frFR', 'Parlez, et vite. Nous avons beaucoup à faire.', '', 38134),
(200590, 'frFR', '', 'Krexus avait raison. La clé du siège du Primat est à nous !', 38134),
(200587, 'frFR', 'Dans ce cas, nous allons le débusquer. Mais d\’abord, nous devons dégager le ciel.', '', 38134),
(200586, 'frFR', 'Il se cache sous terre, en bon lâche qu\’il est.', '', 38134),
(200584, 'frFR', '', 'Voici une fraction de mon pouvoir. Elle vous permettra d\’adopter une forme plus… familière pour l\’âme qui vous attend en bas.', 38134),
(200577, 'frFR', 'J\’espère que nos chemins se recroiseront bientôt. En attendant, suivez la voie.', '', 38134),
(200576, 'frFR', 'C\’est grâce à vous que nous avons récupéré la lance de Kalliope.', '', 38134),
(200575, 'frFR', '', 'Voilà qui devrait suffire ! Je vais garder les Drust à l\’œil. Vous, retournez auprès d\’Aliothe.', 38134),
(200574, 'frFR', 'Beau travail, $n. Je vous félicite.', '', 38134),
(200573, 'frFR', '', 'Nos ressources sont limitées mais je vais faire ce que je peux.', 38134),
(200572, 'frFR', '', 'La voie de l\’Ascension vous attend.\n\nVous sentez-vous de taille à relever ce défi ?', 38134),
(200571, 'frFR', 'De grâce, Aliothe, aidez-nous mettre fin à son cauchemar !', '', 38134),
(200527, 'frFR', '', 'Les autres maisons ne tarderont pas à apprendre que nous avons l\’épée. Il va falloir nous battre.', 38134),
(200526, 'frFR', '', 'Les armes qu\’il forgeait étaient légendaires. Si nous avons trouvé la clé de son arsenal, elles seront à nous.', 38134),
(200525, 'frFR', '', 'Une porte nous barre la route, on dirait. Prévenez-moi quand vous voudrez que je vous aide à la franchir.', 38134),
(200508, 'frFR', '', 'Le siège du Primat est resté fermé depuis sa disparition. Beaucoup ont essayé de le rouvrir, en vain.', 38134),
(200506, 'frFR', 'Ne nous attardons pas dans les parages. Vous devez apporter cette graine sauvage au creux Hibernal, non ?', '', 38134),
(200505, 'frFR', 'Il faut empêcher ces monstres de s\’emparer de la prairie de Soryn.', '', 38134),
(200491, 'frFR', '', 'Ah, c\’est vous ! La graine commençait à s\’agiter.', 38134),
(200469, 'frFR', 'Montez à dos de cerf et venez avec nous, $p.', '', 38134),
(200458, 'frFR', '$n, nous avons récupéré la lance et les érudits restants sont saufs. Acceptons leur offre, restons-en là et partons. Je vous retrouverai au Repos du héros.', '', 38134),
(200455, 'frFR', 'Souffrez bien, $p.', '', 38134),
(200451, 'frFR', '', 'Bravo ! Les phalènes du coin feraient bien de s\’inspirer de vous !', 38134),
(200450, 'frFR', '', 'On a besoin de récolter des glands d\’anima, mais il y a des Drust partout ! Un petit brin de magie vous aidera à vous faufiler.', 38134),
(200449, 'frFR', '', 'Je me suis laissé dire que vous faisiez un superbe hurleur du val.\n\n<Baie-de-Lune éclate de rire.>\n\nJe m\’en souviendrai la prochaine fois que j\’aurai besoin d\’un oiseau pour faire une parade amoureuse.\n\nBref, passons. J\’ai une bonne nouvelle à vous annoncer ! La reine va bientôt venir à Tirna Vaal ! Ce bosquet restera ouvert pour accueillir les graines sauvages et les âmes. Elle sera bientôt là pour bénir son cœur et revigorer les lieux.\n\nCe sera l\’occasion pour vous de lui parler.', 38134),
(200448, 'frFR', '', 'Alors, comme ça, vous avez réussi à faire danser Korenth ?\n\n<Baie-de-Lune éclate de rire.>\n\nJe savais bien que vous ne me décevriez pas, $n. Je m\’en souviendrai si jamais j\’ai d\’autres menus services à vous confier.\n\nÀ part ça, j\’ai une bonne nouvelle à vous annoncer ! La reine va bientôt venir à Tirna Vaal ! Ce bosquet continuera à accueillir les graines sauvages et les âmes. Elle sera bientôt là pour bénir son cœur et revigorer les lieux.\n\nCe sera l\’occasion pour vous de lui parler.', 38134),
(200446, 'frFR', 'Prestige et puissance. Rénathal sera ravi. Faites en sorte que les besoins de notre ami soient satisfaits, $n.', '', 38134),
(200445, 'frFR', 'Cet outil… couronne est pour votre chef. Elle concentre les pouvoirs des médaillons dont vous m\’avez tant parlé.', '', 38134),
(200442, 'frFR', '', 'N\’allez pas croire que vous avez gagné ! Notre prochaine rencontre sera la dernière.', 38134),
(200441, 'frFR', '', 'À l\’attaque !', 38134),
(200440, 'frFR', '', 'Faut-il que je m\’abaisse à votre niveau ? Je suis le parangon de doute et vos interruptions répétées usent ma patience !', 38134),
(200439, 'frFR', '', 'Il semble que vous ayez des invités, parangon. Occupez-vous d\’eux.', 38134),
(200438, 'frFR', '', 'Je sais désormais ce qui m\’attend. Je vais tuer l\’Archonte et m\’emparer du Bastion !', 38134),
(200437, 'frFR', 'Ahhh !', '', 38134),
(200436, 'frFR', '', 'Vous avez été un pion fort utile, Uther. Déva vous portait dans son cœur… mais vous ne me servez plus à rien.', 38134),
(200435, 'frFR', '', 'Emparez-vous de son pouvoir, Lysonia. Utilisez-le pour accélérer votre transformation.', 38134),
(200431, 'frFR', 'Je vois que vous êtes au bord du gouffre, $umortel:mortelle;. Sentez-vous dans votre âme l\’appel de l\’Antre ?\n\nJ\’admire votre bravoure et votre résilience, mais je souhaite vous éprouver.\n\nSi vous êtes vraiment $utel:telle; qu\’on le dit, affrontez-moi. Triomphez, et vous serez libre de regagner le Bastion. Dans le cas contraire, je vous séparerai à nouveau de votre anima.\n\nQu\’en dites-vous ?', 'Je vois que vous êtes au bord du gouffre, $umortel:mortelle;. Sentez-vous dans votre âme l\’appel de l\’Antre ?\n\nJ\’admire votre bravoure et votre résilience, mais je souhaite vous éprouver.\n\nSi vous êtes vraiment $utel:telle; qu\’on le dit, affrontez-moi. Triomphez, et vous serez libre de regagner le Bastion. Dans le cas contraire, je vous séparerai à nouveau de votre anima.\n\nQu\’en dites-vous ?', 38134),
(200429, 'frFR', 'Nous avons gagné une bataille, mais je crains qu\’il en vienne bien d\’autres.', '', 38134),
(200428, 'frFR', 'Beau travail ! Replions-nous !', '', 38134),
(200427, 'frFR', 'Hallali, à l\’attaque !', '', 38134),
(200422, 'frFR', '', 'Rebonjour ! J\’ai une bonne nouvelle !', 38134),
(200421, 'frFR', 'Allez, vite ! Tout doit être parfait pour la reine !', '', 38134),
(200420, 'frFR', '', 'Pitié ! Que quelqu\’un me vienne en aide !', 38134),
(200418, 'frFR', '', 'Nelwyn sanglote.', 38134),
(200416, 'frFR', 'Suivez la route et vous arriverez en Tirna Vaal. On se voit là-bas !', '', 38134),
(200396, 'frFR', 'Un carnage absolument exemplaire ! Félicitons les survivants !', '', 38134),
(200395, 'frFR', 'Han ! Ce n\’est pas si mal pour une bande de débutants.', '', 38134),
(200391, 'frFR', 'Votre technique est excellente ! Il ne vous reste plus grand-chose à apprendre.', '', 38134),
(200390, 'frFR', 'Nul ne rejoint les rangs des transcendés s\’il ne maîtrise l\’art de voler. Voyons comment vous vous en sortez !', '', 38134),
(200386, 'frFR', 'J\’ai assisté à bon nombre de ces expiations légendaires moi-même, vous savez.\n\nCes histoires valent la peine d\’être racontées.', '', 38134),
(200385, 'frFR', 'Évitons d\’y passer trois jours, si vous voulez bien !', '', 38134),
(200384, 'frFR', 'Nous partirons à votre signal, Sans-Entraves.', '', 38134),
(200367, 'frFR', '', 'Faites en sorte de ne pas ajouter votre nom à la liste.', 38134),
(200366, 'frFR', '', 'La recherche de la lance a été marquée par bien des déceptions.', 38134),
(200363, 'frFR', '<L\’équidé vous défie d\’un hennissement agressif !>', '<L\’équidé vous défie d\’un hennissement agressif !>', 38134),
(200362, 'frFR', '<L\’équidé vous observe les oreilles baissées et gratte le sol de ses sabots d\’un air menaçant.>', '<L\’équidé vous observe les oreilles baissées et gratte le sol de ses sabots d\’un air menaçant.>', 38134),
(200356, 'frFR', 'Par ici ! On s\’est fait attaquer par des créatures affamées. Des âmes sont en danger !', '', 38134),
(200353, 'frFR', 'L\’intervention suivante va devenir virale !', '', 38134),
(200343, 'frFR', 'Les désavoués occupent ma forge. Dégagez-moi un espace de travail, et je vous aiderai en retour.', '', 38134),
(200342, 'frFR', '', 'Je soutiens Rénathal dans tout ce qu\’il cherche à accomplir.', 38134),
(200341, 'frFR', '', 'Laurent a ses travers, mais je lui fais confiance pour réparer le miroir.', 38134),
(200340, 'frFR', '', 'Voilà qui est bien mieux.', 38134),
(200336, 'frFR', 'Vous remplacez dame Baie-de-Lune ? Bon courage, ce n\’est pas le travail qui manque !', '', 38134),
(200334, 'frFR', 'Allez !', '', 38134),
(200333, 'frFR', 'Frêleplume ! On a besoin d\’aide !', '', 38134),
(200331, 'frFR', 'Ce bruit… c\’est insupportable !', '', 38134),
(200330, 'frFR', '', 'Quelle est cette folie ?', 38134),
(200329, 'frFR', 'Il m\’est bien plus facile de me concentrer si la menace de la Légion de pierre ne plane pas au-dessus de ma tête.', '', 38134),
(200328, 'frFR', 'C\’est… une bien triste mission. Mais tant que la pénurie sévira, nous devrons nous y résigner.', '', 38134),
(200326, 'frFR', 'Chaque graine sauvage abrite un esprit de la nature appelé à se réveiller et à regagner un jour son monde d\’origine.', '', 38134),
(200323, 'frFR', 'Suivez-moi. Le bosquet du Xylocarde est tout proche.', '', 38134),
(200322, 'frFR', 'C\’est à la reine de décider lesquelles doivent survivre… et lesquelles sont condamnées à dépérir, leurs âmes perdues à jamais.', '', 38134),
(200287, 'frFR', 'Sonnez le cor, $p !', '', 38134),
(200286, 'frFR', 'Vite, ramassez le cor !', '', 38134),
(200275, 'frFR', 'Essayez d\’éloigner l\’oiseau du nid !', '', 38134),
(200272, 'frFR', 'À vous, $p ! Récupérez le cor !', '', 38134),
(200271, 'frFR', 'Non, le cor !', '', 38134),
(200270, 'frFR', 'Pour la reine et pour nos bosquets !', '', 38134),
(200267, 'frFR', 'Merci d\’avoir sauvé la graine sauvage ! Je vais la mettre en sécurité.', '', 38134),
(200262, 'frFR', 'Ta\’saran des négociants propose des services d\’écurie dans la salle des curiosités.', 'Ta\’saran des négociants propose des services d\’écurie dans la salle des curiosités.', 38134),
(200259, 'frFR', 'Vous trouverez une boîte aux lettres près de l\’auberge, à l\’est de l\’anneau des destins.', 'Vous trouverez une boîte aux lettres près de l\’auberge, à l\’est de l\’anneau des destins.', 38134),
(200257, 'frFR', 'L\’auberge se trouve à l\’est de l\’anneau des destins.', 'L\’auberge se trouve à l\’est de l\’anneau des destins.', 38134),
(200255, 'frFR', 'Vous trouverez le maître de vol à l\’étage de l\’anneau de transfert. Utilisez le portail de l\’anneau des destins pour monter.', 'Vous trouverez le maître de vol à l\’étage de l\’anneau de transfert. Utilisez le portail de l\’anneau des destins pour monter.', 38134),
(200253, 'frFR', 'Ta\’visage des négociants vous donne la possibilité de changer d\’apparence. Il se trouve dans la salle des curiosités.', 'Ta\’visage des négociants vous donne la possibilité de changer d\’apparence. Il se trouve dans la salle des curiosités.', 38134),
(200252, 'frFR', 'Notre $uami:amie; $p et moi allons ouvrir le combat. Chargez quand je sonnerai le cor !', '', 38134),
(200251, 'frFR', 'Nous ferons amende honorable, premier forgelite. J\’en fais le serment.', '', 38134),
(200250, 'frFR', 'Les négociants ont mis des services bancaires à votre disposition dans la salle des Possessions.', 'Les négociants ont mis des services bancaires à votre disposition dans la salle des Possessions.', 38134),
(200248, 'frFR', 'Les négociants proposent des services de transmogrification dans la salle des curiosités.', 'Les négociants proposent des services de transmogrification dans la salle des curiosités.', 38134),
(200246, 'frFR', 'Vous trouverez les surveillants et d\’autres visiteurs importants dans la salle de réunion de l\’Enclave.', 'Vous trouverez les surveillants et d\’autres visiteurs importants dans la salle de réunion de l\’Enclave.', 38134),
(200244, 'frFR', '', 'Heureusement que j\’ai mes ailes pour me tirer d\’affaire si ça tourne mal. Je vous fais confiance.', 38134),
(200243, 'frFR', '', 'On va sauter à pieds joints là-dedans ? Là où les âmes sont conduites à leur damnation éternelle sans espoir de rédemption ?', 38134),
(200239, 'frFR', 'Ils ont une bête géante… Beaucoup sont tombés sous ses griffes.', '', 38134),
(200238, 'frFR', 'Aujourd\’hui, on fête Chaddius !\n\nIl prend des bains tous les jours. Toujours propre !\n\nIl est pas loin du temple de la Pureté.\n\nVous lui apportez un purian mûr ?', '', 38134),
(200233, 'frFR', 'Cours, Korenth !', '', 38134),
(200232, 'frFR', 'Oh oh… Euh… Bonjour ?', '', 38134),
(200231, 'frFR', 'Continuez comme ça ! Je vais me faufiler discrètement…', '', 38134),
(200230, 'frFR', 'À elle de tomber sous les nôtres.', '', 38134),
(200227, 'frFR', 'Je crois que j\’ai une solution.\n\nNe bougez pas. Ça ne devrait pas être douloureux.', '', 38134),
(200225, 'frFR', 'Je vous remercie !', 'Je vous remercie !', 38134),
(200224, 'frFR', 'Nous n\’avons pas le temps de nous reposer !', 'Nous n\’avons pas le temps de nous reposer !', 38134),
(200223, 'frFR', 'Ils étaient en train de me transformer en l\’un d\’entre eux !', 'Ils étaient en train de me transformer en l\’une d\’entre eux !', 38134),
(200222, 'frFR', 'À chaque bataille, ils deviennent plus nombreux…', 'À chaque bataille, ils deviennent plus nombreux…', 38134),
(200221, 'frFR', 'Mon combat continue ! Pour Sylvarden !', 'Mon combat continue ! Pour Sylvarden !', 38134),
(200220, 'frFR', 'Nous serons forts pour la forêt !', 'Nous serons forts pour la forêt !', 38134),
(200219, 'frFR', '', 'Mon devoir est sacré.', 38134),
(200215, 'frFR', '', 'Laissez-moi Compassion et retrouvons-nous à Oribos. Je dois me préparer. Rares sont les Kyrians qui accompliront un tel périple…', 38134),
(200207, 'frFR', 'Merci à tous d\’avoir accepté de venir à la cour des Braises !', '', 38134),
(200206, 'frFR', '', 'RHAAA ! Venez, Uther. Nous avons mieux à faire que de perdre notre temps avec ces imbéciles !', 38134),
(200205, 'frFR', 'Aujourd\’hui, on fête Brûlios !\n\nIl adore les animaux. Surtout Kala.\n\nIl est dans le jardin du Répit.\n\nVous lui apportez un purian mûr ?', '', 38134),
(200204, 'frFR', '', 'Laissez donc ces enfantillages et mettons-nous au travail.', 38134),
(200203, 'frFR', 'Tu imposes tes vues et tes échecs à autrui… Alors que j\’ai peiné des siècles durant pour incarner mes principes…', '', 38134),
(200202, 'frFR', 'Maîtresse… il ne s\’est rien produit.', '', 38134),
(200201, 'frFR', '', 'Allez, parangon ! Laissez éclater votre orgueil !', 38134),
(200200, 'frFR', '', 'Vous arrivez trop tard, Sans-Entraves. Votre précieux parangon succombera bientôt à l\’orgueil qu\’il a enfoui en lui.', 38134),
(200199, 'frFR', 'Des tensions ? Dans quoi m\’avez-vous encore embarqué, Draven ?', '', 38134),
(200198, 'frFR', 'Voilà comment on traite les ruffians de tout poil !', '', 38134),
(200197, 'frFR', 'Grossier personnage !', '', 38134),
(200196, 'frFR', 'Je m\’en vais vous apprendre les bonnes manières !', '', 38134),
(200195, 'frFR', 'Nous vous rejoindrons à la forge Éternelle. Votre présence devrait apaiser les tensions que notre arrivée risque de susciter.', '', 38134),
(200193, 'frFR', 'Dépêchons. J\’ai rendez-vous.', '', 38134),
(200189, 'frFR', 'Venez ! Ils ont besoin de notre aide !', '', 38134),
(200188, 'frFR', '', 'Aucune importance, Uther. Nous en avons plus qu\’il n\’en faut.', 38134),
(200187, 'frFR', 'Les collecteurs ont été sabotés. La puissance est en chute libre !', '', 38134),
(200185, 'frFR', 'C\’est un Drust… et un gros.', '', 38134),
(200184, 'frFR', '', 'Qu\’est-ce que c\’est que ça ?', 38134),
(200183, 'frFR', '', 'J\’adore les solutions pleines de créativité ! Ils y réfléchiront à deux fois avant de venir chercher les ennuis, maintenant.', 38134),
(200182, 'frFR', '', 'Il faut arrêter les spriggans ! Et si on les faisait goûter à leur propre poison ?', 38134),
(200171, 'frFR', '', 'Comment ? Tu as aussi passé un marché avec Bwonsamdi ? Je te savais prêt à tout, mais tu as touché le fond cette fois !', 38134),
(200170, 'frFR', 'Qu\’avons-nous là ? Ce vieux Loa poussiéreux envoie des collecteurs de dettes ? Mais j\’ai encore besoin de son mojo pour créer mon chef-d\’œuvre !', '', 38134),
(200169, 'frFR', 'Bien sûr ! Il ne manque plus que la touche finale…', '', 38134),
(200168, 'frFR', 'Les invités ne vont pas tarder à arriver. Je gage que Témel et vous avez la situation bien en main ?', '', 38134),
(200167, 'frFR', 'Comme toujours, vous arrivez à point nommé, $p.', '', 38134),
(200165, 'frFR', 'La cour de Revendreth la plus récente et la plus courue du moment !', '', 38134),
(200163, 'frFR', '', 'Nous tiendrons bon ! Ouvrez cette porte !', 38134),
(200157, 'frFR', 'Halte ! Vous n\’êtes pas en sécurité sur cette route.', '', 38134),
(200156, 'frFR', 'Dame Baie-de-Lune... est la plus grande… Dois-je vraiment ?', '', 38134),
(200155, 'frFR', 'Bon… puisque vous insistez…', '', 38134),
(200151, 'frFR', 'Rhaaaah !', '', 38134),
(200150, 'frFR', '', 'Vous ne faites que gâcher votre salive. Uther, finissez-en, bon sang !', 38134),
(200148, 'frFR', 'Tu étais l\’une des plus fidèles disciples de notre Archonte. Renonce à tes noirs desseins. Tu peux encore sauver tes partisans, et te sauver toi-même !', '', 38134),
(200147, 'frFR', '', 'Je dois trouver le parangon. Compassion devrait amplement suffire pour mener à bien votre tâche.', 38134),
(200145, 'frFR', '', 'Touchant de naïveté… Mais vous courberez quand même l\’échine, parangon.', 38134),
(200144, 'frFR', 'Ton arrogance te perdra, Lysonia. Ne renonce pas à la voie !', '', 38134),
(200143, 'frFR', 'Argh ! Comment les Kyrians font-ils pour supporter cette affreuse luminosité ?', '', 38134),
(200142, 'frFR', 'Le Bastion. Moi non plus, je n\’échapperai pas à mes péchés.', '', 38134),
(200133, 'frFR', '', 'Par la grâce de l\’Archonte, de l\’aide, enfin ! Mes soldats sont blessés et je fais l\’impossible pour les maintenir en vie.', 38134),
(200131, 'frFR', '', 'Venez. Allons trouver mon âme liée, Pélagos, au Repos de l\’aspirant.', 38134),
(200130, 'frFR', '', 'Là, regardez ! Bien qu\’ils soient acculés, Voïtha et les disciples de l\’Humilité tiennent bon.', 38134),
(200129, 'frFR', '', 'Pauvres aspirants… Les désavoués les poussent à succomber à leur orgueil afin qu\’ils viennent grossir leurs rangs.', 38134),
(200127, 'frFR', 'Vous serez enchaînés pour l\’éternité !', '', 38134),
(200126, 'frFR', 'Débattez-vous tant que vous voudrez, mortels. Nul n\’échappe à son destin !', '', 38134),
(200124, 'frFR', 'Une minute… C\’est vous qui nous avez aidés sur les hauts de Tombe-Lueur. Peut-être le pourriez-vous à nouveau.', '', 38134),
(200114, 'frFR', 'Hipokos, il y a peut-être moyen de réparer l\’un de ces magistrats ?', '', 38134),
(200113, 'frFR', 'Vos liens sont brisés ! Dépêchez-vous avant que les gardes ne reviennent !', '', 38134),
(200111, 'frFR', '', 'L\’Hallali défend Sylvarden au nom de sa Majesté. Aidez les chasseurs pour alléger le fardeau de la reine !', 38134),
(200110, 'frFR', '', 'La Lame d\’ébène est stationnée non loin d\’ici. Elle vous défendra !', 38134),
(200109, 'frFR', 'Trouvez la grotte, de l\’autre côté du gouffre ! Nos alliés vous protégeront !', '', 38134),
(200108, 'frFR', '', 'Si seulement la reine de l\’hiver pouvait toutes les sauver…', 38134),
(200107, 'frFR', '', 'Chaque graine sauvage est un précieux trésor. On en a perdu tellement.', 38134),
(200105, 'frFR', '', 'Encore des cauchemars ! Protégez-moi pendant que je calme la graine.', 38134),
(200104, 'frFR', '', 'C\’est la mélodie qui doit les attirer…', 38134),
(200100, 'frFR', '', 'Qu\’est-ce que vous faites ici, au juste, $n ?', 38134),
(200098, 'frFR', '', 'Je vais faire tout ce que je peux pour vous aider à obtenir une audience. Vous m\’avez bien aidée, vous, après tout.', 38134),
(200097, 'frFR', '', 'Merci de m\’avoir emmenée avec vous, $p. Je suis désolée que vous n\’ayez pas pu voir la reine.', 38134),
(200096, 'frFR', 'Par ici, $uapprenti:apprentie; !', '', 38134),
(200095, 'frFR', '', 'Attention, des spriggans !', 38134),
(200094, 'frFR', '', 'Le cauchemar recommence… Je dois lui jouer de la harpe…', 38134),
(200093, 'frFR', '', 'La première cache vous attend sur ce chemin. Bonne chance !', 38134),
(200092, 'frFR', 'Non ! La puissance de la Conservatrice était à ma portée.', '', 38134),
(200087, 'frFR', 'Alors je vous retrouve au creux Hibernal. Faites bon voyage.', '', 38134),
(200085, 'frFR', 'Niya, voici ma harpe. Sa mélodie apaisera les cauchemars de la graine sauvage.', '', 38134),
(200084, 'frFR', 'La graine se portera mieux au creux Hibernal. Niya pourrait peut-être vous accompagner.', '', 38134),
(200083, 'frFR', 'Korenth a le chic pour s\’attirer des ennuis. Voyons de quoi il en retourne cette fois.', '', 38134),
(200082, 'frFR', 'Si c\’est moi que tu veux, viens donc me chercher.', '', 38134),
(200081, 'frFR', 'Grashaal, ils vous ont réveillé pour prendre ma place ?', '', 38134),
(200080, 'frFR', 'Espèce d\’égoïste attardé ! Nous ne baissons pas les armes, nous obéissons aux ordres !', '', 38134),
(200079, 'frFR', 'Kaal, si tu m\’as jamais aimé, fais-moi confiance et dépose les armes.', '', 38134),
(200078, 'frFR', '', 'Draven ! Ne m\’oblige pas à t\’anéantir.', 38134),
(200076, 'frFR', 'Sentez-vous ce basculement ? Ce déséquilibre ? Quelque chose est en marche. Nous devons nous hâter !\n\nTrois moissonneurs et une âme sans-entraves investis de la puissance brute de l\’anima des nombreux royaumes de l\’Ombreterre contre un seigneur de la mort… Nous voilà aux portes de son domaine.\n\nNous affronterons cette terreur ensemble !', '', 38134),
(200075, 'frFR', 'En temps normal, les miens n\’ont pas l\’honneur de lier leur âme, mais je ne suis pas un régisseur ordinaire ! Qu\’en dites-vous ?', '', 38134),
(200074, 'frFR', 'Je n\’avais plus vécu d\’aventures aussi trépidantes depuis des siècles.', '', 38134),
(200073, 'frFR', '', 'Je n\’aurais jamais cru que le destin me conduirait jusqu\’ici, et pourtant, nous y voilà.\n\nBref… comment fait-on pour pénétrer dans l\’Antre ?', 38134),
(200071, 'frFR', 'Sous couvert de rendre justice, nous l\’avons sacrifié à notre vengeance.', '', 38134),
(200070, 'frFR', 'Chaque fois que je ferme les yeux, je vois ma mort. Je le vois… lui… qui se dresse devant moi, armé de cette maudite lame…', '', 38134),
(200069, 'frFR', 'Emmenez-le au guet Éternel en attendant que l\’Archonte décide de son sort.', '', 38134),
(200066, 'frFR', '', 'Vos agissements de ce jour m\’ont beaucoup appris.', 38134),
(200065, 'frFR', 'Ne comptez plus sur moi. Je préfère encore oublier mon passé plutôt que de nous condamner à un tel avenir.', '', 38134),
(200063, 'frFR', 'Misérable pleutre !', '', 38134),
(200062, 'frFR', 'Assez joué ! Gardes, massacrez ces avortons !', '', 38134),
(200059, 'frFR', '', 'Ils se reproduisent à une vitesse alarmante. Éliminez les larves pour endiguer l\’infestation.', 38134),
(200057, 'frFR', 'Je vous donne un coup de main ?', 'Je vous donne un coup de main ?', 38134),
(200055, 'frFR', 'Je vous aide ?', 'Je vous aide ?', 38134),
(200054, 'frFR', 'Vous aimez les jus sucrés ? J\’en ai plein !', 'Vous aimez les jus sucrés ? J\’en ai plein !', 38134),
(200052, 'frFR', 'Je peux aider de plein de façons. Vous voulez ?', 'Je peux aider de plein de façons. Vous voulez ?', 38134),
(200046, 'frFR', 'Purians trop bons ! Content ! You-hou !', 'Purians trop bons ! Contente ! You-hou !', 38134),
(200045, 'frFR', 'Trop content ! Merci beaucoup-hou !', 'Trop contente ! Merci beaucoup-hou !', 38134),
(200044, 'frFR', 'Hou-hou, c\’est ma fête !', 'Hou-hou, c\’est ma fête !', 38134),
(200043, 'frFR', 'C\’est MOI qu\’on fête ? Waouh-hou !', 'C\’est MOI qu\’on fête ? Waouh-hou !', 38134),
(200042, 'frFR', '', 'Mais cette fois, nous autres Faë nocturnes devons agir en son nom.', 38134),
(200037, 'frFR', 'Joyeuse fête des régisseurs !\n\nOn répare. On porte. On sert.\n\nOn est les régisseurs !', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200035, 'frFR', 'Décidément, nous vivons des temps bien étranges. Je n\’avais encore jamais vu Sylvarden en proie à un tel chaos.', '', 38134),
(200032, 'frFR', '', 'Étrange… Poursuivez votre apprentissage. Je vous retrouverai près de l\’hôtesse.', 38134),
(200031, 'frFR', '', 'Vous voyez ça ? On dirait un Kyrian, mais… corrompu.', 38134),
(200028, 'frFR', 'Idiot ! Ce sont toutes les maisons de Maldraxxus qui se dressent contre vous !', '', 38134),
(200027, 'frFR', 'Vous ne pouvez pas gagner, Vyraz. Nous sommes trop nombreux !', '', 38134),
(200025, 'frFR', 'Merci, mon ami. Je serai heureux de retrouver toutes mes forces.', '', 38134),
(200024, 'frFR', 'Ces blessures ont l\’air graves. Laissez-moi m\’en occuper.', '', 38134),
(200014, 'frFR', '', 'Kala, tu as été très vilaine aujourd\’hui. Présente tes excuses.', 38134),
(200013, 'frFR', '', 'Je serai à vos côtés pendant les années à venir pour vous aider à utiliser le bâton. Nous restaurerons le jardin et ses créatures, Zosime.', 38134),
(200012, 'frFR', 'Un soldat de l\’Alliance a assassiné nombre de vos camarades. Il est temps de lui montrer qui domine le champ de bataille.', '', 38134),
(200011, 'frFR', 'Un soldat de la Horde a massacré nombre de vos alliés. Je vous conseille de lui rappeler qui domine le champ de bataille.', '', 38134),
(200010, 'frFR', 'Vous avez fait fuir ces voleurs avec habileté. Je le signalerai dans mon rapport.', '', 38134),
(200009, 'frFR', 'Les Faë nocturnes sont devenus kleptomanes. Votre aide serait grandement appréciée.', '', 38134),
(200008, 'frFR', 'Vous avez un don pour manier l\’épée, $umortel:mortelle;. J\’aurai peut-être un… emploi à vous proposer, bientôt.', '', 38134),
(200007, 'frFR', 'Il n\’est question que d\’orgueil et de prestige dans les duels entre Venthyrs. Mais pour le cartel, c\’est aussi synonyme de profits.', '', 38134),
(199996, 'frFR', '', 'Je reviendrai lorsque vous aurez trouvé le succès en vous.', 38134),
(199995, 'frFR', '', 'Canalisez tout votre anima dans le bâton, mon enfant. Prouvez l\’étendue de votre courage et de votre espoir.', 38134),
(199994, 'frFR', '', 'L\’appel du courage. Ah, Zosime. Vous venez récupérer la houlette.', 38134),
(199993, 'frFR', '', 'Passez devant. Je dois calmer Kala. Elle est dans tous ses états maintenant qu\’elle a retrouvé ses jouets.', 38134),
(199992, 'frFR', '', 'Une fois à l\’intérieur, sonnez le bourdon… enfin, la cloche. Korinna devrait se manifester et nous dire quoi faire ensuite.', 38134),
(199984, 'frFR', 'Dis-moi, Keltesh, as-tu remarqué quoi que ce soit de suspect ?', 'Dis-moi, Keltesh, as-tu remarqué quoi que ce soit de suspect ?', 38134),
(199951, 'frFR', '', 'Le jardin du Répit est un lieu paisible, propice à la méditation et à la contemplation des défis de l\’existence. Ou du moins, il l\’était. Mais j\’ai bon espoir qu\’il le redevienne.\n\nOn le doit à la grande Korinna qui fut l\’une des premières transcendées. C\’est elle qui dompta les créatures du Bastion et leur apporta la sérénité. Mais la pénurie d\’anima a fait voler cette paix en éclats et plus personne ne peut venir se délasser ici.', 38134),
(199950, 'frFR', '', 'Ce que j\’ignore encore, c\’est le moyen de sortir d\’ici. Je n\’arrive à me téléporter qu\’à courte distance. On est prisonniers de ce cauchemar.', 38134),
(199948, 'frFR', '', 'Ah, je suis bien contente de vous voir. Ma première épreuve d\’aspirante est de m\’occuper de ce jardin, et c\’est tout sauf une sinécure.\n\nLa pénurie d\’anima a enragé bon nombre des animaux qui vivent ici. Je dois trouver un moyen de rétablir l\’équilibre.\n\nVous voulez bien m\’aider ?', 38134),
(199905, 'frFR', 'Se voir accorder une telle audience est un honneur rare, $umortel:mortelle;. Montrez-vous à la hauteur.', '', 38134),
(199904, 'frFR', 'Les parangons sont déjà rassemblés, ils attendent l\’arrivée de l\’Archonte. Un conseil, ne prenez pas la parole avant qu\’on vous la donne.', '', 38134),
(199887, 'frFR', '', '<Z\’rali lance un cri de douleur.>', 38134),
(199886, 'frFR', '', 'Vous ne savez pas… ce que vous faites…', 38134),
(199885, 'frFR', '', 'La Lumière… triomphera…', 38134),
(199879, 'frFR', '', 'Que voulez-vous savoir ?', 38134),
(199876, 'frFR', 'La cible se trouve dans les profondeurs de la prison. Ne laissez personne vous barrer le chemin.', '', 38134),
(199873, 'frFR', '', 'Je vais avoir besoin d\’un peu de temps pour me remettre de ce qui s\’est passé dans Tourment. Je ne souhaite pas en parler davantage.', 38134),
(199869, 'frFR', '', '<Le regard perdu dans le lointain, Korayn a l\’air furieuse.>', 38134),
(199859, 'frFR', 'Où dois-je mener nos hôtes ?', '', 38134),
(199858, 'frFR', '', 'Je fais de mon mieux. Et vous allez peut-être pouvoir l\’aider à repousser !', 38134),
(199856, 'frFR', '', 'Les feuilles bruissent de son rire. Vous êtes la joie de la forêt, dame Baie-de-Lune.', 38134),
(199855, 'frFR', '', 'Une barbe, un chapeau ? Ha, des marques du temps qui passe… et de la facétie des Faë.', 38134),
(199853, 'frFR', 'Détendez-vous, $p. Il reste des rafraîchissements, allez donc vous servir un verre.\n\nAprès une réception aussi réussie, vous avez bien mérité du repos ainsi qu\’une récompense.', '', 38134),
(199850, 'frFR', 'Avez-vous trouvé le lys dont nous avons besoin ?', '', 38134),
(199849, 'frFR', 'Sans vouloir vous importuner, nous avons des délais à tenir.', '', 38134),
(199848, 'frFR', '', 'Vous avez bien travaillé, $n. L\’infestation est sous contrôle pour l\’instant.', 38134),
(199847, 'frFR', '', 'L\’endroit grouille déjà de gorms. Éradiquez cette infestation avant qu\’elle puisse se répandre !', 38134),
(199832, 'frFR', '', 'Je serai ravie de vous accompagner. Je vais me préparer.', 38134),
(199828, 'frFR', '', 'Cette farce-là restera dans les annales ! Il n\’y a plus qu… Oh, euh, bonjour !', 38134),
(199826, 'frFR', 'L\’implacable quête de la victoire.', '', 38134),
(199823, 'frFR', '', 'Elles prennent la forme d\’ombres malveillantes qui errent dans les ténèbres. Elles ne seront pas disposées à nous aider.', 38134),
(199821, 'frFR', '', 'Bienvenue parmi nous ! Nous vous avons préparé quelques menues tâches pour vous acclimater à votre nouvelle existence.', 38134),
(199820, 'frFR', '', 'Bienvenue parmi nous ! Nous vous avons préparé quelques menues tâches pour vous acclimater à votre nouvelle existence.', 38134),
(199818, 'frFR', '', 'La paire réunie ne marque que l\’impasse. À issue unique symbole unique.', 38134),
(199816, 'frFR', '', 'On dirait qu\’elle s\’est trouvé un allié puissant. Les ombres murmurent un nom… le Geôlier.', 38134),
(199815, 'frFR', '', 'Trouvez la voie. Percez les brumes.', 38134),
(199812, 'frFR', '', 'Pierre et symbole marquent le chemin. L\’un est imposture. L\’autre vous mènera à bien.', 38134),
(199811, 'frFR', '', 'Mais je dois préparer ma station. Je vous retrouve là-bas !', 38134),
(199809, 'frFR', '', 'Vous pourrez en apprendre plus en consultant chaque station.', 38134),
(199800, 'frFR', 'Nous allons le venger sur-le-champ !', '', 38134),
(199647, 'frFR', '', 'C\’est une décision douloureuse, mais qui doit être prise pour la sauvegarde du cycle.', 38134),
(199646, 'frFR', '', 'Mais la pénurie l\’a fané et il faut absolument le renforcer… même si nous manquons d\’anima pour sauver nos bosquets en péril.', 38134),
(199645, 'frFR', '', 'Le cœur de la Forêt est crucial pour la survie de Sylvarden… et des autres royaumes.', 38134),
(199643, 'frFR', '', 'Je possède bien des connaissances. Que voulez-vous savoir ?', 38134),
(199639, 'frFR', '', 'Je ne comprends pas.\n\nDe quel monde, système, entité, royaume ou dimension existentielle provenez-vous ?', 38134),
(199638, 'frFR', '', 'Nous sommes conscients que le passage de vie à trépas peut sembler abrupt et qu\’une telle transition est parfois difficile à accepter. Vous êtes nombreux à vous interroger quant à la nature de votre nouvelle existence.\n\nProfitez de cette occasion pour assimiler votre mort et faire la paix avec cette réalité. Ceci fait, nous pourrons passer à votre initiation.', 38134),
(199625, 'frFR', '', 'Beau travail, $n. Vous nous offrez un temps précieux pour nous préparer à repousser les gorms.', 38134),
(199624, 'frFR', '', 'Une horde vorace de piqueracines est en train d\’avancer dans les terres ! Prenez une aile-voilée pour en éliminer avant qu\’ils arrivent au cœur de la Forêt !', 38134),
(199615, 'frFR', 'Je vais l\’incinérer au feu d\’un millier de soleils !', '', 38134),
(199613, 'frFR', 'Libérez la prisonnière et échappez-vous sans traîner. La victoire nous reviendra.', '', 38134),
(199611, 'frFR', '', 'Sylvarden s\’effondre de toutes parts. D\’abord les possédés, et maintenant, ces monstres étranges qui attaquent Tirna Vaal…\n\nVous cherchiez quelque chose, $n ?', 38134),
(199609, 'frFR', 'Venez servir votre maître, mes pécheurs !', '', 38134),
(199601, 'frFR', '', 'Seigneur Herne, nous demandons audience avec la reine de l\’hiver.', 38134),
(199600, 'frFR', '', 'C\’est malheureusement très sérieux.', 38134),
(199599, 'frFR', 'Baie-de-Lune, est-ce encore un vilain tour ou y a-t-il un problème sérieux ?', '', 38134),
(199596, 'frFR', 'Je vous vois, Sans-Entraves. Hors de ma tour !', '', 38134),
(199595, 'frFR', '', 'Venez, $p. Je vous invite à visiter ma cour et à découvrir le cœur de la Forêt. Nous devons discuter plus avant.', 38134),
(199594, 'frFR', '', 'Si le Proscrit s\’agite, c\’est un danger plus grave encore que cette terrible pénurie.', 38134),
(199593, 'frFR', '', 'Mon vieil ami. Bien des siècles plus tard, nos plus grandes peurs se réalisent.', 38134),
(199592, 'frFR', '', 'La reine de l\’hiver a bien reçu le message du Primat.', 38134),
(199590, 'frFR', '', 'Sylvanas commande les forces du Geôlier. Elle n\’a qu\’à les appeler pour nous submerger.\n\nNous ne pourrons pas aider Anduin si nous sommes tous capturés. Évitons un engagement frontal tant que nous ignorons ce qu\’elle cherche.', 38134),
(199585, 'frFR', 'Bien sûr. Les occasions ne manquent pas.', 'Bien sûr. Les occasions ne manquent pas.', 38134),
(199584, 'frFR', 'C\’est ton destin. Il faut t\’y résigner !', '', 38134),
(199576, 'frFR', '', 'Alors vous n\’êtes plus un vrai chasseur.', 38134),
(199573, 'frFR', 'Je sers un nouveau maître qui flétrira Sylvarden !', '', 38134),
(199559, 'frFR', '', 'Ce n\’est pas rassurant. Elle a reçu un pouvoir immense d\’un rituel ancestral et j\’ai peur qu\’elle ne soit pas en mesure de le contenir.', 38134),
(199557, 'frFR', '', 'Tyrande, ici ? Non, je ne l\’ai pas vue.', 38134),
(199555, 'frFR', '', 'Je me suis déjà cachée dans cette grotte. Il ne faut pas nous éterniser, mais l\’endroit devrait être sûr un certain temps.', 38134),
(199554, 'frFR', '', 'Oui. J\’ai eu beau m\’échapper un nombre incalculable de fois, je finis toujours au fond d\’une cage.', 38134),
(199533, 'frFR', 'Bravo ! Vous avez triomphé de vos doutes les plus profonds.', '', 38134),
(199532, 'frFR', 'Pas sans me battre jusqu\’au bout.', '', 38134),
(199531, 'frFR', 'Tu vas être tué au temple de la Loyauté, et tu rejoindras les désavoués !', '', 38134),
(199530, 'frFR', 'Tu te trompes ! Ensemble, on est plus forts. Tous les trois !', '', 38134),
(199529, 'frFR', 'Tu ne transcenderas jamais ! Tu resteras éternellement le valet de Kléia !', '', 38134),
(199528, 'frFR', 'Ce sera ton dernier échec.', '', 38134),
(199527, 'frFR', 'Je l\’avais ratée tant de fois, cette épreuve. Mais pas aujourd\’hui.', '', 38134),
(199526, 'frFR', 'Ensemble.', '', 38134),
(199524, 'frFR', 'Je… Non, nous… nous pouvons y arriver ! Je ne suis pas seul !', '', 38134),
(199523, 'frFR', 'JE… N\’Y… ARRIVERAI… PAS…', '', 38134),
(199522, 'frFR', 'Pas assez fort… pas assez bon…', '', 38134),
(199520, 'frFR', '', 'C\’est le moment de tester cette nouvelle armure.', 38134),
(199515, 'frFR', 'Il est inutile… de résister…', '', 38134),
(199514, 'frFR', '', 'Nous devons à tout prix poursuivre Lysonia. Elle ne doit surtout pas frapper à nouveau !', 38134),
(199510, 'frFR', '', 'Ah, fini de rire !', 38134),
(199508, 'frFR', 'Je ne… suis pas sûr de pouvoir le faire…', '', 38134),
(199507, 'frFR', 'Maintenant, mettons fin à leur petite expérience !', '', 38134),
(199485, 'frFR', '', 'Trouvez-les et utilisez-les pour libérer le seigneur de guerre.', 38134),
(199484, 'frFR', 'La maison de la Peste… décimée ! Où est Stradama ? Je dois terminer la potion… Il… il le faut !', '', 38134),
(199483, 'frFR', 'Nul ne peut ouvertement accuser les liches de traîtrise, aussi ai-je mis au point une nouvelle potion. Une protection de dernier recours pour Stradama, si le pire devait arriver. J\’espère qu\’il n\’en sera rien.', '', 38134),
(199482, 'frFR', 'La maison des Rituels propose de nous ravitailler en anima. Comme Stradama, je ne leur fais pas confiance… mais nous ne pouvons refuser une main tendue. Restons vigilants.', '', 38134),
(199481, 'frFR', 'Il n\’y a aucune issue ! Personne ne s\’échappe de l\’Antre !', '', 38134),
(199480, 'frFR', 'Mon génie… ne doit pas… disparaître…', '', 38134),
(199477, 'frFR', 'La guerre civile menace en l\’absence du Primat. La margrave Stradama me confie ses craintes. Sans anima, nos travaux, et donc notre puissance, ne progressent pas.', '', 38134),
(199471, 'frFR', 'Attendez ! Ne lui faites pas de mal !', '', 38134),
(199468, 'frFR', 'Ton heure est venue. Tu as eu ton pouvoir, mais maintenant Mueh\’zala va venir te le reprendre.', '', 38134),
(199467, 'frFR', 'Tu fais un piètre Loa de la mort à faire tes petites affaires au lieu de prendre ce que tu veux prendre.', '', 38134),
(199466, 'frFR', 'Alors, tu vas pleurer dans les jupes de la reine, maintenant ?', '', 38134),
(199465, 'frFR', 'Non, non. Vous ne pigez pas. Le chef, est en train de vous voler vos Loas dans leur sommeil, et…', '', 38134),
(199464, 'frFR', '', 'Les querelles des Loas ne m\’intéressent pas. N\’entraînez pas Sylvarden dans vos petites disputes.', 38134),
(199463, 'frFR', 'Ah, mais non, Majesté ! Je viens en paix. Il faut qu\’on bosse ensemble si on veut battre Mueh\’zala.', '', 38134),
(199462, 'frFR', '', '$n, qu\’est-ce que cela signifie ?', 38134),
(199461, 'frFR', 'Hé, qu\’est-ce tu racontes, reine des glaces ? Ça faisait longtemps, hein ? Il est bien joli, cet arbre. J\’aime beaucoup la nouvelle décoration !', '', 38134),
(199459, 'frFR', 'On dirait que quelqu\’un voudrait dérober le secret de mon génie !', '', 38134),
(199458, 'frFR', '', 'Donc vous avez bien fait n\’importe quoi, c\'est ce que je disais !', 38134),
(199457, 'frFR', 'C\’est euh, c\’est que le traitement de la chair est une opération compliquée. Peut-être qu\’il faudrait ajouter un tout petit peu de…', '', 38134),
(199456, 'frFR', '', 'Hé… Il a bougé, je l\’ai vu ! Les organes kyrians sont en train de lutter pour prendre le contrôle !', 38134),
(199455, 'frFR', 'Balivernes ! Une fois qu\’une âme maldraxxi y sera attachée, elle balaiera toute trace du conditionnement du Bastion !', '', 38134),
(199454, 'frFR', '', 'Il va surtout remarquer que vous avez oublié de vider la chair de ses souvenirs avant assemblage.', 38134),
(199453, 'frFR', 'À son retour, Sutur apprendra que moi, Malkrex, j\’ai mené ses recherches à bien et assemblé la créature ultime dans la chair des Kyrians !', '', 38134),
(199452, 'frFR', '', 'Ah, vous voilà. La reine sera là très bientôt !', 38134),
(199450, 'frFR', 'On m\’a dit que Derawyn et Korenth étaient les rois de la farce. Est-ce vrai ?', '', 38134),
(199449, 'frFR', '', 'Merci.', 38134),
(199448, 'frFR', 'Euh… je préfère rester avec vous. Pour votre sécurité !', '', 38134),
(199447, 'frFR', '', 'Ça va aller, Te\’zan. Partez devant.', 38134),
(199446, 'frFR', 'Êtes-vous certaine d\’être en sécurité ? Il y a encore de nombreux Faë masqués.', '', 38134),
(199445, 'frFR', '', 'Le temps de faire mes adieux… et je vous rejoindrai à Tirna Vaal.', 38134),
(199442, 'frFR', '', 'Le cœur du bosquet, non !', 38134),
(199441, 'frFR', '', 'Sans l\’anima du cœur, les graines vont dépérir…', 38134),
(199440, 'frFR', 'Niya… Je suis… désolé…', '', 38134),
(199439, 'frFR', '', 'Vous m\’avez déjà trop pris !', 38134),
(199438, 'frFR', 'Je n\’aime pas du tout la façon dont vous me regardez.\n\nVous êtes au courant que l\’au-delà est synonyme de paix éternelle ?', '', 38134),
(199436, 'frFR', '', 'Une étrange magie a pris possession de nos gens, la pénurie d\’anima ravage nos terres et le cœur d\’un bosquet a été dérobé.\n\nTout va beaucoup trop vite à mon goût.', 38134),
(199427, 'frFR', 'Je ne suis pas encore mort. Mais j\’aurai sûrement mal aux côtes demain.', '', 38134),
(199426, 'frFR', 'Draven, tout va bien ?', '', 38134),
(199424, 'frFR', 'Aucune pierre n\’est assez solide pour me résister !', '', 38134),
(199423, 'frFR', 'Vous ne pouvez pas échapper à ma colère !', '', 38134),
(199422, 'frFR', '', 'Nos chemins se séparent à l\’ombre des feuillages. Tirna Vaal vous appelle.', 38134),
(199421, 'frFR', 'Il me faut plus d\’anima !', '', 38134),
(199420, 'frFR', 'Rapportez-moi leur anima !', '', 38134),
(199419, 'frFR', '', 'Dans les cendres poussent les graines de l\’espoir. Le cycle se poursuit inexorablement.', 38134),
(199417, 'frFR', 'Champions, je m\’occupe de dégager le ciel !', '', 38134),
(199414, 'frFR', 'Tous se soumettront à la flétrissure !', '', 38134),
(199413, 'frFR', 'Sylvarden reviendra aux maîtres !', '', 38134),
(199412, 'frFR', 'Bon, d\’accord. Mais j\’espère bien en récupérer un peu quand tout sera fini !', '', 38134),
(199411, 'frFR', '', 'L\’anima donné sera rendu en temps voulu.', 38134),
(199410, 'frFR', '', 'Les ténèbres gagnent du terrain, mais les étoiles nous protègent.', 38134),
(199399, 'frFR', 'Je ne suis pas complètement inepte, je vous assure.\n\nMon cher ami Théotar vient régulièrement s\’assurer que je suis protégé de l\’agressivité de la Lumière.\n\nToujours aussi prévenant, celui-là.', '', 38134),
(199397, 'frFR', 'Bien sûr que non. Je me plais beaucoup, ici.\n\nC\’est un endroit paisible. Personne ne vient m\’ennuyer… d\’habitude.\n\n<Il vous foudroie du regard.>\n\nJe rattrape enfin tous les livres que je voulais lire depuis des années.\n\nJ\’aimerais seulement en avoir davantage…', '', 38134),
(199396, 'frFR', 'Oui, qu\’y a-t-il ?\n\nJ\’espère que vous avez une très bonne raison d\’interrompre ma lecture aussi grossièrement.', '', 38134),
(199395, 'frFR', 'Tant de vies sacrifiées, tant de morts…\n\n<Ara\’lon se tait, plongé dans ses pensées.>', '', 38134),
(199384, 'frFR', 'Et bonne chance. Le nouveau patron est un poil soupe au lait.', '', 38134),
(199383, 'frFR', 'Je vous ouvre la porte en un rien de temps.', '', 38134),
(199382, 'frFR', '', 'Tu es la honte de notre espèce, Draven ! Puisque tu refuses de servir, tu mourras !', 38134),
(199380, 'frFR', 'Kaal ! Grashaal ! Ne vous laissez pas aveugler par une loyauté mal placée ! Le marché conclu par Denathrius scellera la perte de Revendreth !', '', 38134),
(199379, 'frFR', '', 'Vives-pierres, abattez ces usurpateurs !', 38134),
(199378, 'frFR', 'Je bloque… leurs attaques… autant… que possible…', '', 38134),
(199377, 'frFR', 'Une attaque ! Tous à couvert !', '', 38134),
(199372, 'frFR', '', 'Ces ignobles créatures vont connaître la colère de l\’Hallali !', 38134),
(199370, 'frFR', 'Tout le monde est agité depuis que le ciel a été déchiré, et le Fléau ne fait pas exception. Voyons où en sont vos techniques de combat, ça passera le temps.', '', 38134),
(199369, 'frFR', 'Bienvenue à bord ! Le roi Wrynn veut que tous les héros de l\’Alliance soient au mieux de leur forme pour affronter les horreurs qui sortiront de la Couronne de glace.', '', 38134),
(199368, 'frFR', '', 'Depuis que le ciel s\’est déchiré, le Fléau a l\’air de s\’agiter à nouveau. Notre boulot, c\’est de le surveiller. On va commencer par voir si vous savez vous battre.', 38134),
(199367, 'frFR', '', 'Bonjour et bienvenue dans la vigie de la Couronne de glace. J\’espère que vous avez le cœur bien accroché.', 38134),
(199365, 'frFR', '', 'En quoi puis-je vous aider ?', 38134),
(199363, 'frFR', '', 'Derawyn, non ! Ces monstres vont payer !', 38134),
(199362, 'frFR', '', 'Le cycle… continue… vous devez… aussi…', 38134),
(199361, 'frFR', '', 'On a déjà perdu Korenth. Je refuse de te perdre aussi !', 38134),
(199360, 'frFR', '', 'Non… l\’anima… pour les graines…', 38134),
(199357, 'frFR', 'Hipokos a trouvé une magistrate parmi les Kyrians morts et l\’a réparée. Comme elle n\’a pas d\’âme, elle devrait pouvoir traverser la barrière.\n\nGuidez-la et utilisez-la pour neutraliser les défenses une fois de l\’autre côté et nous ouvrir la voie.', '', 38134),
(199356, 'frFR', 'Pour Azeroth !', '', 38134),
(199353, 'frFR', 'Nous devons nous échapper d\’ici et avertir l\’Archonte de ce qui se passe ici.', '', 38134),
(199352, 'frFR', 'Dans mon ancien bosquet, j\’utilisais cet appareil pour transporter les graines. J\’espère qu\’il vous sera utile.', '', 38134),
(199350, 'frFR', 'Merci d\’être $uvenu:venue; nous trouver. Sylvarden a grand besoin de gens comme vous.', '', 38134),
(199349, 'frFR', 'J\’ai vaincu Krexus en personne ! Vous deux, vous n\’êtes rien !', '', 38134),
(199348, 'frFR', 'Je vais vous planter ma lame dans le crâne, traître. Pour Krexus !', '', 38134),
(199345, 'frFR', 'Ce naïf de Mograine qui préfère toujours la loyauté à la puissance. Mais c\’est dans le choix des bons alliés que réside la victoire. Et grâce aux miens, je prends la place de margrave !', '', 38134),
(199344, 'frFR', 'Ce sont vos propres manigances qui vont causer votre perte. Vous allez périr !', '', 38134),
(199343, 'frFR', 'Ainsi, l\’âme mortelle nous revient avec le baron disparu. Ni les assemblages ni l\’Antre n\’auront eu raison de vous. Quelle déception.', '', 38134),
(199342, 'frFR', '', 'Assez ! Et si vous me connaissez si bien, vous savez qu\’il faut me craindre.', 38134),
(199341, 'frFR', 'Vous nous avez lancés sur une chasse sans retour. À vous d\’être la proie. Une proie que je connais si bien…', '', 38134),
(199340, 'frFR', '', 'Les Faë masqués… C\’est donc ça, le sort qui les attend ?', 38134),
(199339, 'frFR', 'Mais, vous ne reconnaissez pas vos victimes ? Les armes qu\’elles portent, les visages cachés derrière les masques.', '', 38134),
(199337, 'frFR', '', 'Je connais cette voix, mais… il avait été tué…', 38134),
(199336, 'frFR', 'Ah, Korayn… toujours aussi rusée.', '', 38134),
(199335, 'frFR', 'Connaître sa proie, c\’était votre première leçon. On dirait que vous l\’avez oubliée.', '', 38134),
(199334, 'frFR', '', 'Lorkron… Mais qu\’est-ce qui t\’est arrivé ?', 38134),
(199332, 'frFR', '', 'Retournez au bassin Tombe-Lueur. Ma chasse à moi ne fait que commencer.', 38134),
(199331, 'frFR', '', 'Un bon ami… devenu un terrible ennemi. Il faut l\’abattre.', 38134),
(199330, 'frFR', 'Non ! La proie n\’aura pas le chasseur, cette fois. Méfiez-vous bien des ombres, Korayn…', '', 38134),
(199329, 'frFR', 'C\’est la fin pour vous, Korayn !', '', 38134),
(199325, 'frFR', '', 'N\’avancez pas.', 38134),
(199323, 'frFR', '', 'Attention. Leurs yeux pourront voir à travers mon sort.', 38134),
(199322, 'frFR', '', 'En avant vers la victoire !', 38134),
(199318, 'frFR', 'Et moi, je vais joindre mes contacts de confiance en Maldraxxus.', '', 38134),
(199317, 'frFR', '', 'Je vous suis redevable. Je n\’étais pas loin de succomber.', 38134),
(199316, 'frFR', '', 'Mon âme liée… l\’Antre…', 38134),
(199310, 'frFR', 'Le rituel d\’absolution aidera ces âmes à se libérer des fardeaux de leur vie mortelle.', 'Le rituel d\’absolution aidera ces âmes à se libérer des fardeaux de leur vie mortelle.', 38134),
(199304, 'frFR', 'Très bien ! Ensuite, nous allons voir… Une minute, qu\’est-ce que… ? Aux armes ! Le Fléau nous attaque !', '', 38134),
(199303, 'frFR', '', 'Impressionnant ! Ensuite, nous… Minute, qu\’est-ce que… ? Préparez-vous à combattre ! Le Fléau nous attaque !', 38134),
(199302, 'frFR', 'La mère de toutes les pestes… viendra… vous emporter…', '', 38134),
(199301, 'frFR', 'Porteurs de la putridité, entendez mon appel !', '', 38134),
(199298, 'frFR', 'Mes créatures, venez vous repaître !', '', 38134),
(199296, 'frFR', 'Debout, serviteurs de la peste !', '', 38134),
(199291, 'frFR', 'Bienvenue. Nous vous attendions impatiemment !', '', 38134),
(199288, 'frFR', '', 'Ce savoir… qui nous tendait les bras…', 38134),
(199287, 'frFR', '', 'Toutes ces reliques reviendront au Maître !', 38134),
(199286, 'frFR', 'Avez-vous trouvé un moyen de me libérer ?', '', 38134),
(199285, 'frFR', 'Le patron… va pas être… content…', '', 38134),
(199284, 'frFR', 'Il marche pas, ce machin !', '', 38134),
(199283, 'frFR', 'Vous pas prendre… cristal…', '', 38134),
(199282, 'frFR', 'Ça brille, et ça cogne !', '', 38134),
(199281, 'frFR', 'J\’ai fait… mon mieux…', '', 38134),
(199280, 'frFR', 'Les gros os sont pas sympas !', '', 38134),
(199279, 'frFR', 'C\’est… moi… copain nul ?', '', 38134),
(199278, 'frFR', 'Vous êtes copain nul !', '', 38134),
(199275, 'frFR', 'Les Maldraxxi sont-ils tous aussi assoiffés de sang ?', '', 38134),
(199274, 'frFR', 'Tout ça porte la marque de l\’horrible Sutur. Faites diversion pendant que je mets la main sur ses disciples !', '', 38134),
(199273, 'frFR', '', 'Sans le fragment de miroir détenu par Matéo, nous n\’arriverons jamais au médaillon à temps.', 38134),
(199271, 'frFR', 'Vos secrets, je les veux !', 'Vos secrets, je les veux !', 38134),
(199266, 'frFR', 'Maldraxxus sera à nous !', 'Maldraxxus sera à nous !', 38134),
(199265, 'frFR', 'Salut ! Inspection des crocs obligatoire pour tout le monde. Ordre du patron.', '', 38134),
(199259, 'frFR', 'Pour devenir des armes au service du Geôlier.', '', 38134),
(199246, 'frFR', '', 'Il est… trop tard… pour moi…', 38134),
(199245, 'frFR', '', 'Il fait… si sombre…', 38134),
(199244, 'frFR', 'Merci. Je passe devant ; vous allez avoir besoin de mon aide avec tout ça !', '', 38134),
(199243, 'frFR', 'Je n\’avais jamais rencontré de Faë aussi méchants !', '', 38134),
(199242, 'frFR', 'Même avec tout ce qu\’a fait Sylvanas, je n\’aurais jamais pensé la voir aller si loin.', '', 38134),
(199241, 'frFR', '', 'Au cœur de l\’orage fleurit la gratitude. Nos chemins convergent vers le bourbier.', 38134),
(199240, 'frFR', 'Toutes ces âmes qui déferlent dans l\’Antre. Les morts d\’Azeroth, de Draenor et de mondes insoupçonnés, tous maudits à échouer ici.', '', 38134),
(199239, 'frFR', '', 'Cette horrible magie… Je connais cet ennemi…', 38134),
(199238, 'frFR', 'L\’armée du Geôlier est aussi vaste que celle de la Légion. Et elle ne fait que grandir.', '', 38134),
(199237, 'frFR', '', 'Restez près de moi. On ne peut pas se permettre de se faire repérer.', 38134),
(199229, 'frFR', 'À mesure que nous reprenons le contrôle de nos terres sacrées, les transcendés reprennent leur entraînement. Ils auront hâte de voir le dernier temple libéré.', '', 38134),
(199228, 'frFR', '[DEPRECATED] Ya best be turnin\' around, Bwonsamdi. Dis be none of ya concern.', '', 38134),
(199227, 'frFR', 'Ah, mais c\’est la marque de Mueh\’zala, ça. Mais non, ah là là, non ! C\’est le grand patron qui est derrière tout ça ?!', '', 38134),
(199226, 'frFR', 'Grâce à vous, Tombe-Vice reste en sécurité.', '', 38134),
(199225, 'frFR', 'Ennemis en approche ! À vos miroirs ! S\’ils s\’en prennent aux murs, ils seront accueillis à coups de Lumière !', '', 38134),
(199223, 'frFR', 'Les brutes vives-pierres ont beaucoup de force mais ne visent pas particulièrement bien. Aidez-les à faire parvenir l\’aide à nos alliés postés sur les fortifications en contrebas.', '', 38134),
(199218, 'frFR', '', 'Vos actes aideront Revendreth à renouer avec sa vocation première.', 38134),
(199217, 'frFR', '', 'La ménagerie reçoit un afflux d\’âmes sans précédent. Nous ne pouvons pas nous permettre de laisser leur anima se perdre. Il faut vite agir, avant qu\’elles soient absorbées par l\’Antre !', 38134),
(199183, 'frFR', 'L\’Accusatrice doit nous laisser la traquer et l\’anéantir !', '', 38134),
(199182, 'frFR', 'Les Venthyrs travaillent avec une liche ?!', '', 38134),
(199181, 'frFR', '', 'Nous partons pour Maldraxxus immédiatement, $n. Vyraz doit payer pour ses crimes.', 38134),
(199180, 'frFR', 'Nous pourrons parler plus tard, mon fils. Je te le promets.', '', 38134),
(199179, 'frFR', 'Draka ! Il faut retourner en Maldraxxus sur-le-champ. Le margrave Krexus…', '', 38134),
(199178, 'frFR', 'Non… Alors mon avertissement arrive trop tard. L\’un d\’entre nous nous a trahis.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(199177, 'frFR', '', 'Le margrave a été tué, Porte-Cendres. Les Élus ont plus que jamais besoin de votre lame.', 38134),
(199174, 'frFR', 'Je dois retourner sur Azeroth pour avertir nos amis. Mais dès que mon devoir le permettra, je reviendrai à tes côtés.', '', 38134),
(199173, 'frFR', 'Généralissime ! Nous avons réussi à échapper à l\’Antre. Merci pour tout.', '', 38134),
(199172, 'frFR', '', 'Bon. Finissons-en une fois pour toutes.', 38134),
(199171, 'frFR', '', 'Votre heure viendra. Tout ça n\’était que le commencement !', 38134),
(199170, 'frFR', '', 'Votre vérité ne m\’échappera pas !', 38134),
(199169, 'frFR', '', 'Tester la puissance d\’un parangon était bien naïf.', 38134),
(199168, 'frFR', '', 'Notre royaume n\’est plus que ruine. Les désavoués vont nous libérer !', 38134),
(199167, 'frFR', '', 'Vous pouvez bien essayer de la sauver, elle a vu la vérité !', 38134),
(199154, 'frFR', '', 'Le moyen de sortir d\’ici. Du moins, on l\’espère.', 38134),
(199153, 'frFR', 'Mais il n\’avait pas l\’air important, les sbires du Geôlier ne lui accordent aucune attention. Qu\’est-ce que ce serait ?', '', 38134),
(199152, 'frFR', 'J\’ai bien vu un vieil obélisque en pierre sur la grande crête.', '', 38134),
(199151, 'frFR', 'On cherche une pierre d\’accès qui serait quelque part par-delà le chaudron. Ça vous dit quelque chose ?', '', 38134),
(199150, 'frFR', 'Compris. Je vous suis.', '', 38134),
(199149, 'frFR', '', 'J\’aurais aimé vous laisser plus de temps pour récupérer, mais il faut partir d\’ici.', 38134),
(199148, 'frFR', 'Vous avez ma gratitude éternelle, $n.', '', 38134),
(199147, 'frFR', 'Elle a bien failli. Sans notre $uami:amie; pour vous sauver…', '', 38134),
(199146, 'frFR', 'Anduin ? Je pensais bien que mon heure était venue…', '', 38134),
(199145, 'frFR', 'Baine, vous m\’entendez ?', '', 38134),
(199144, 'frFR', 'Il reprend ses esprits.', '', 38134),
(199143, 'frFR', '', 'Tenez bon, Baine ! Ne vous laissez pas emporter par le Geôlier !', 38134),
(199142, 'frFR', 'Vous devez vite la neutraliser !', '', 38134),
(199141, 'frFR', 'Cette malédiction va lui déchirer l\’âme !', '', 38134),
(199140, 'frFR', 'Je vais briser la lame. Soyez prêts à accueillir tout ce qui pourrait en sortir.', '', 38134),
(199139, 'frFR', '', 'Qu\’est-ce que… C\'est impossible…', 38134),
(199138, 'frFR', '', 'Pfff. Vous ne voyez pas que je travaille !', 38134),
(199137, 'frFR', 'Mais… l\’Ancien… m\’avait promis…', '', 38134),
(199136, 'frFR', 'Votre anima est à moi !', '', 38134),
(199135, 'frFR', 'Moi, un traître ? C\’est vous qui pactisez avec l\’âme sans-entraves, que je sache ! Occupez-vous de ces deux-là et rapportez-moi l\’éclat à Archivam.', '', 38134),
(199134, 'frFR', 'Denathrius ? Comment avez-vous pu pactiser avec lui alors qu\’il a jeté la Conservatrice dans l\’Antre ? Traître !', '', 38134),
(199133, 'frFR', 'Émile ! On vous a cherché partout… Notre sire a besoin de l\’éclat de miroir que la Conservatrice vous a donné.', 'Émile ! On vous a cherché partout… Notre sire a besoin de l\’éclat de miroir que la Conservatrice vous a donné.', 38134),
(199130, 'frFR', '', 'Oh, Korenth… Ils n\’auront pas le cœur du bosquet. Je te le jure.', 38134),
(199127, 'frFR', 'Gravissez les marches quand vous le voudrez.', '', 38134),
(199126, 'frFR', '', 'Korenth ! Hou hou, Korenth !', 38134),
(199125, 'frFR', '', 'Je vais chercher les survivants. On se retrouve plus loin dans le bosquet.', 38134),
(199121, 'frFR', 'Ara\’lon, chasseur de l\’Hallali, pour vous servir.\n\nAutrefois, je prenais soin des esprits en hibernation au sein d\’un magnifique bosquet appelé Tirna Noch. Mais ce sanctuaire est tombé et j\’ai trouvé un nouveau sens à mon existence.\n\nDepuis, je sers Sylvarden.', '', 38134),
(199119, 'frFR', 'On va regagner le bosquet aussi vite que possible. Merci à vous.', '', 38134),
(199118, 'frFR', 'Les chutes sont envahies, et je crois… que quelqu\’un est à nos trousses.', '', 38134),
(199116, 'frFR', 'Prudence. Cet endroit grouille de créatures monstrueuses de toutes tailles.', '', 38134),
(199115, 'frFR', 'Par ici, avant qu\’ils vous repèrent !', '', 38134),
(199113, 'frFR', 'Ce doit être la dague. La lame est encore tachée du sang de Baine.', '', 38134),
(199111, 'frFR', 'Le cœur… volé…', '', 38134),
(199110, 'frFR', 'Krelnor… masque… bourbier… Embrum…', '', 38134),
(199109, 'frFR', 'Si le Geôlier me cherche, il va tâter de mon fer !', '', 38134),
(199108, 'frFR', 'Enfin ! Une arme digne d\’un vrai guerrier !', '', 38134),
(199107, 'frFR', 'Disons que c\’est mieux que rien. Allez, un dernier coup d\’œil.', '', 38134),
(199106, 'frFR', 'Mouais. Là, trop de manche.', '', 38134),
(199105, 'frFR', 'Trop de lame et pas assez de manche.', '', 38134),
(199104, 'frFR', 'Au moins, c\’est une vraie arme. Même si je n\’ai jamais compris pourquoi l\’Alliance aime tellement les épées.', '', 38134),
(199103, 'frFR', 'Continuons à chercher.', '', 38134),
(199102, 'frFR', 'Les bâtons n\’ont jamais été mon truc, mais au moins celui-là ne casserait pas en pleine action.', '', 38134),
(199101, 'frFR', 'Celle-là a un certain poids, c\’est sûr. Mais voyons ce qu\’on peut trouver d\’autre.', '', 38134),
(199100, 'frFR', 'Une arbalète ? Je préfère frapper face à face.', '', 38134),
(199099, 'frFR', 'Mais vous vous obstinez à inciter mon courroux. Très bien. Vous allez l\’avoir.', '', 38134),
(199097, 'frFR', 'Assez ! En bon hôte, j\’ai fait preuve de patience et même de courtoisie.', '', 38134),
(199094, 'frFR', '', 'On entendra leurs hurlements dans tous les royaumes de la mort !', 38134),
(199093, 'frFR', '', 'Cette arme que je vous ai forgée est exceptionnelle. Si vous vous avisez de la casser, c\’est moi qui vous casse.', 38134),
(199092, 'frFR', '', 'Oh oui, oui, je vais les découper en pièces !', 38134),
(199088, 'frFR', '', 'Je vais les faire gicler sur les murs !', 38134),
(199086, 'frFR', '', 'Je vais les tailler en mille petits morceaux !', 38134),
(199079, 'frFR', 'Bon travail. Dites à Draka que nous sommes parés au combat.', '', 38134),
(199076, 'frFR', 'Je n\’ai pas besoin de votre confiance. Cela importe peu pour les Maldraxxi.', '', 38134),
(199072, 'frFR', '', 'On m\’a dit grand bien de vous, $p. Vous pouvez vous reposer ici.', 38134),
(199058, 'frFR', '', 'Je continuerai à m\’entraîner pendant votre absence.\n\nAu revoir, Sans-Entraves.', 38134),
(199053, 'frFR', '', 'Qui êtes… Une minute… c\’est vous qui avez aidé Derawyn et Te\’zan…', 38134),
(199051, 'frFR', '', 'Venez. Le margrave Krexus nous attend.', 38134),
(199050, 'frFR', '', '<Elle semble être en paix.>', 38134),
(199049, 'frFR', '', 'Toujours cette arrogance qui causa votre perte…', 38134),
(199048, 'frFR', '', 'Estimons-nous heureux. Nous pourrons chasser un jour de plus.', 38134),
(199045, 'frFR', '', '<Une Kyriane blessée est venu expirer son dernier souffle ici, à l\’abri des regards. $B$BDans sa main, une petite boîte.>', 38134),
(199044, 'frFR', 'Un résultat… inattendu…', '', 38134),
(199043, 'frFR', 'Ah, de nouveaux cobayes ! Et zélés, avec ça !', '', 38134),
(199040, 'frFR', '', 'Exercez-vous avec Throg. Moi, je dois parler à l\’équipage de cette pluie.', 38134),
(199031, 'frFR', 'Ils… ils font des expériences sur nous…', '', 38134),
(199030, 'frFR', 'Les cadavres sont une monnaie comme une autre pour le tabulateur.', '', 38134),
(199028, 'frFR', '<Le regard figé, le Sylvar fixe le vide devant lui. Il a succombé à une embuscade.>', '', 38134),
(199027, 'frFR', 'Cette magie… que font-ils donc à mes frères ?!', '', 38134),
(199026, 'frFR', '', '<Le visage de la Vorkaï est déformé par la rage. Elle a succombé à la force du nombre.>', 38134),
(199022, 'frFR', 'Je vais vous le rendre au centuple !', '', 38134),
(199020, 'frFR', 'C\’était mon préféré !', '', 38134),
(199019, 'frFR', 'Mon petit, non !', '', 38134),
(199018, 'frFR', 'Hecutis, au pied !', '', 38134),
(199017, 'frFR', 'Margore, ici !', '', 38134),
(199016, 'frFR', 'Bargast, à moi !', '', 38134),
(199015, 'frFR', '<Le visage du Vorkaï est marqué par la magie noire. On l\’a attiré dans un piège.>', '', 38134),
(199013, 'frFR', '', 'Imbécile, vous nous avez menés droit à Émile. À nous l\’éclat !', 38134),
(199012, 'frFR', 'J\’espère que cette petite merveille sera aussi meurtrière qu\’elle en a l\’air.', '', 38134),
(199011, 'frFR', 'Vous, là-bas ! C\’est la Conservatrice qui vous envoie ? Approchez, il y a… Quoi ?', '', 38134),
(199010, 'frFR', 'Oh, celle-là, je meurs d\’envie de la voir en action. Mais c\’est plutôt vous qui allez mourir.', '', 38134),
(199009, 'frFR', 'Voyons si toutes les légendes sur celle-ci sont vraies !', '', 38134),
(199008, 'frFR', '', 'Mes chasseurs sont en retard, c\’est inhabituel. Retrouvez-les.', 38134),
(199007, 'frFR', '', 'Je suis le capitaine de l\’Hallali Korayn. Je sers la reine de l\’hiver et je protège Sylvarden.\n\nSachez que ma lame s\’abattra prestement sur quiconque ose menacer la forêt.\n\nC\’est ainsi, il n\’y a pas de négociation possible.', 38134),
(199005, 'frFR', '', 'Personne n\’échappe à l\’Hallali.', 38134),
(199004, 'frFR', 'J\’ai beau resserrer ses chaînes, cette âme refuse de se taire.', '', 38134),
(199003, 'frFR', 'Cette âme résiste à mon contrôle de toutes ses forces.', '', 38134),
(198998, 'frFR', '', 'Vous vous battez en vain…', 38134),
(198997, 'frFR', '', 'Vous allez nourrir nos soldats !', 38134),
(198993, 'frFR', '', 'Dans son état actuel, Kael\’thas Haut-Soleil est un danger pour tous.', 38134),
(198991, 'frFR', '', 'Ce bosquet ne tombera pas !\n\nLa reine m\’a confié la garde du creux Hibernal et des esprits qui s\’y trouvent. Nous n\’échouerons pas.', 38134),
(198990, 'frFR', '', 'Venez, il n\’y a qu\’un moyen d\’atteindre la maison des Élus.', 38134),
(198989, 'frFR', 'Il faut les sauver, vite !', '', 38134),
(198988, 'frFR', '', 'Il est le plus fort d\’entre nous, mais attention. Parlez avec respect, mais pas avec crainte.', 38134),
(198985, 'frFR', '', 'Les Élus combattent avec force et honneur. Nous allons écraser les traîtres et mettre fin à la guerre.', 38134),
(198984, 'frFR', 'Entrée IV :\n\nJe parierais qu\’Émile se cache dans la petite catacombe creusée au pied de la tour de garde en ruine. Si seulement je pouvais l\’atteindre…\n\nNotre progression dans les catacombes a été stoppée net. Les assemblages stèles du vice sont si redoutables qu\’ils donnent du fil à retordre aux plus forts d\’entre nous.\n\nJ\’ai cru que nous étions sauvés quand les éclaireurs camouflés sont arrivés d\’Archivam… mais au lieu de ça, ils ont retourné leurs lames contre nous, contre les leurs ! La raison de cette trahison m\’échappe. J\’espère seulement qu\’ils se lasseront avant de me mettre le grappin dessus.', '', 38134),
(198983, 'frFR', '', 'Bonjour, $umortel:mortelle;. Dame Baie-de-Lune nous a fait votre éloge.', 38134),
(198982, 'frFR', '', 'Jamais ! Vos maîtres devront répondre de leur forfaiture !', 38134),
(198981, 'frFR', 'Rejoignez-nous, Niya. Adoptez le masque et servez les maîtres !', '', 38134),
(198978, 'frFR', '', 'Attendez, cette potion… Marileth ?', 38134),
(198958, 'frFR', 'Je ne suis pas à votre service. Je n\’écoute que la comtesse.', 'Je ne suis pas à votre service. Je n\’écoute que la comtesse.', 38134),
(198957, 'frFR', 'Ne vous préoccupez pas de questions que vous ne pouvez même pas comprendre.', 'Ne vous préoccupez pas de questions que vous ne pouvez même pas comprendre.', 38134),
(198952, 'frFR', 'À mon arrivée en Revendreth, mon châtiment consistait à servir la comtesse et les nobles de sa cour. Au fil des millénaires, j\’y ai pris goût. Je suis désormais son serviteur personnel et son confident de mon plein gré. Je satisfais ses moindres besoins et je mets ses secrets inavouables sous clé.\n\nJ\’espère pour vous ne pas avoir besoin d\’un verrou supplémentaire.', 'À mon arrivée en Revendreth, mon châtiment consistait à servir la comtesse et les nobles de sa cour. Au fil des millénaires, j\’y ai pris goût. Je suis désormais sa servante personnelle et sa confidente de mon plein gré. Je satisfais ses moindres besoins et je mets ses secrets inavouables sous verrou.\n\nJ\’espère pour vous ne pas avoir besoin d\’un verrou supplémentaire.', 38134),
(198951, 'frFR', 'Je vais trouver un endroit sûr !', '', 38134),
(198950, 'frFR', '', 'Aujourd\’hui, les cinq maisons se déchirent. Deux sont déjà tombées et je ne permettrai pas que les Élus les suivent.', 38134),
(198949, 'frFR', '', 'Ah, enfin, des renforts… Mais ! Vous n\’êtes pas Venthyr.', 38134),
(198948, 'frFR', '', 'Nous sommes les ardents défenseurs de l\’Ombreterre. Mais notre chef, le Primat, a disparu.', 38134),
(198947, 'frFR', '', 'Vous comptez explorer les profondeurs des catacombes ? Dans ce cas, vous pouvez nous être utile.', 38134),
(198946, 'frFR', 'Je crois que moi, si. Une fois, très loin d\’ici…', '', 38134),
(198945, 'frFR', '', 'Nous avons perdu la trace d\’Émile quand la situation a dégénéré. Nous avons envoyé Dorin à sa recherche mais il n\’est toujours pas revenu.', 38134),
(198944, 'frFR', 'Ses blessures sont refermées mais son esprit reste affaibli. Je n\’ai jamais rien vu de tel.', '', 38134),
(198935, 'frFR', '', 'Hum. Quoi qu\’il en soit, sans manche, une lame ne vaut pas grand-chose. J\’ai du pain sur la planche.', 38134),
(198933, 'frFR', 'Vous êtes les renforts que nous avions demandés ? Il n\’y a pas de nouvelles de nos éclaireurs.', 'Vous êtes les renforts que nous avions demandés ? Il n\’y a pas de nouvelles de nos éclaireurs.', 38134),
(198932, 'frFR', '', 'Alors c\’était ça la source de cette agitation. Une lame et un message du Primat en personne.', 38134),
(198931, 'frFR', '', 'Prenez notre cerf et hâtez-vous. Allez au bosquet du Xylocarde car le ciel s\’assombrit.', 38134),
(198912, 'frFR', '', 'Un vent gémissant nous vient du Xylocarde… La forêt tremble…', 38134),
(198911, 'frFR', 'Je dois dire que j\’admire la force de conviction de la comtesse. Pas vous ?', 'Je dois dire que j\’admire la force de conviction de la comtesse. Pas vous ?', 38134),
(198910, 'frFR', 'Sans-Entraves ? Vous m\’intriguez beaucoup.', 'Sans-Entraves ? Vous m\’intriguez beaucoup.', 38134),
(198908, 'frFR', '<Il vous examine de haut en bas sans prononcer un mot.>', '<Elle vous examine de haut en bas sans prononcer un mot.>', 38134),
(198907, 'frFR', 'Prudence, Sans-Entraves. La comtesse connaît vos désirs les plus profonds et elle guette constamment sa prochaine moisson.', 'Prudence, Sans-Entraves. La comtesse connaît vos désirs les plus profonds et elle guette constamment sa prochaine moisson.', 38134),
(198904, 'frFR', 'Quelle surprise de vous croiser ici, $pr ! La comtesse doit être d\’humeur charitable.', 'Quelle surprise de vous croiser ici, $pr ! La comtesse doit être d\’humeur charitable.', 38134),
(198903, 'frFR', 'Je n\’ai pas de temps à accorder à vos brouilles ridicules, $c.', 'Je n\’ai pas de temps à accorder à vos brouilles ridicules, $c.', 38134),
(198902, 'frFR', 'C\’est vrai, ce qu\’on raconte sur les Sans-Entraves ?', 'C\’est vrai, ce qu\’on raconte sur les Sans-Entraves ?', 38134),
(198901, 'frFR', '', 'Il y a un problème, je le vois dans votre regard. Comme puis-je vous aider ?', 38134),
(198899, 'frFR', 'La comtesse invite qui elle veut dans son domaine, bien sûr… mais $gun:une:r; $pr ? Vraiment ?', 'La comtesse invite qui elle veut dans son domaine, bien sûr… mais $gun:une:r; $pr ? Vraiment ?', 38134),
(198893, 'frFR', 'Général, à couvert ! Mes soldats vont frapper depuis les airs !', '', 38134),
(198892, 'frFR', '', 'Grashaal, protégez-vous ! La terreur va s\’abattre du ciel !', 38134),
(198891, 'frFR', '', 'Maldraxxus accueille les âmes qui vivaient pour le feu du combat. Ici, c\’est la force qui est célébrée.', 38134),
(198890, 'frFR', '', 'Ils sont trop nombreux ! Je vais nous sortir de là !', 38134),
(198876, 'frFR', 'Votre technique de tir est excellente. Voilà qui leur apprendra à venir se goinfrer en Sylvarden.', '', 38134),
(198875, 'frFR', 'Les dévoreurs envahissent peu à peu la forêt. Abattez-en et utilisez leur anima pour refermer les failles avant qu\’ils nous submergent !', '', 38134),
(198871, 'frFR', 'Des vortex tourbillonnants apparaissent et s\’effondrent brutalement, offrant un bref aperçu de lieux bien reconnaissables en Ombreterre. Traverser ce portail vous permet de vous téléporter instantanément en un point de la zone désirée, mais en aucun cas votre sécurité n\’est garantie.', 'Des vortex tourbillonnants apparaissent et s\’effondrent brutalement, offrant un bref aperçu de lieux bien reconnaissables en Ombreterre. Traverser ce portail vous permet de vous téléporter instantanément en un point de la zone désirée, mais en aucun cas votre sécurité n\’est garantie.', 38134),
(198868, 'frFR', '', 'J\’ai confié chaque moitié à l\’un de mes lieutenants. Il faut arriver à eux avant les sbires de Denathrius.', 38134),
(198867, 'frFR', '', 'J\’ai caché mon médaillon de l\’autre côté de ce miroir juste avant que Denathrius me jette dans l\’Antre. Et pour l\’empêcher de mettre la main dessus, je l\’ai scindé en deux.', 38134),
(198865, 'frFR', '', 'Les catacombes du Regret sont en proie au chaos et les troupes de Denathrius ont envahi Archivam.\n\nHeureusement, nous avons pu calmer un peu le jeu dans la crypte des Oubliés avec l\’aide de quelques Venthyrs d\’Absolution. Mais l\’affrontement a laissé des traces.\n\nLe prince Rénathal m\’a chargée de récupérer le médaillon de l\’avarice, mais je ne suis hélas pas en mesure d\’engager les hostilités. J\’ai besoin de votre aide, Sans-Entraves.', 38134),
(198864, 'frFR', '', 'C\’est pire que je ne pensais. Le quartier des catacombes a sombré dans le chaos en mon absence. Il y aura fort à faire pour l\’en sortir.', 38134),
(198863, 'frFR', 'Tu me manques, mon amour.\n\nUn jour, nous serons à nouveau réunis dans le Rêve.', '', 38134),
(198861, 'frFR', '', 'Ça recommence… La douleur déchirante… La folie qui dépasse tout entendement…', 38134),
(198860, 'frFR', 'Beau combat, Mévix !', '', 38134),
(198859, 'frFR', 'Ha ! Notre Sans-Entraves vaut bien cent soldats ! Allez donc remporter la victoire !', '', 38134),
(198858, 'frFR', 'Une offensive directe prendra Gharmal par surprise et réduira son armée à néant.', '', 38134),
(198857, 'frFR', 'Mais il nous faut des renforts !', '', 38134),
(198856, 'frFR', 'C\’est le moment ou jamais d\’attaquer la maison des Assemblages !', '', 38134),
(198854, 'frFR', 'Le Dessein les guide comme il le fait avec nous. Il faut garder la foi.', 'Le Dessein les guide comme il le fait avec nous. Il faut garder la foi.', 38134),
(198853, 'frFR', 'J\’espère que nous ne nous faisons pas de faux espoirs. Ces étrangers sont-ils dignes de confiance ?', 'J\’espère que nous ne nous faisons pas de faux espoirs. Ces étrangers sont-ils dignes de confiance ?', 38134),
(198852, 'frFR', 'Je dirais que cela démontre la créativité sans borne des Fondateurs. Ils ont façonné tout ce qui est et nous ont fait don du Dessein.', 'Je dirais que cela démontre la créativité sans borne des Fondateurs. Ils ont façonné tout ce qui est et nous ont fait don du Dessein.', 38134),
(198851, 'frFR', 'C\’est fou le nombre de variétés d\’étrangers qu\’il semble exister.', 'C\’est fou le nombre de variétés d\’étrangers qu\’il semble exister.', 38134),
(198850, 'frFR', 'Prions pour que le Dessein guide ceux d\’entre nous qui y sont forcés.', 'Prions pour que le Dessein guide ceux d\’entre nous qui y sont forcés.', 38134),
(198849, 'frFR', 'Heureusement que je n\’ai pas à traiter avec eux directement.', 'Heureusement que je n\’ai pas à traiter avec eux directement.', 38134),
(198848, 'frFR', 'Je suis on ne peut plus d\’accord !', 'Je suis on ne peut plus d\’accord !', 38134),
(198847, 'frFR', 'Si seulement les surveillants se montraient plus sévères avec les négociants. La pagaille qu\’ils fichent a le don de m\’irriter.', 'Si seulement les surveillants se montraient plus sévères avec les négociants. La pagaille qu\’ils fichent a le don de m\’irriter.', 38134),
(198846, 'frFR', 'Allons, ne vous en faites pas. Le Dessein nous guidera à travers ce chaos.', 'Allons, ne vous en faites pas. Le Dessein nous guidera à travers ce chaos.', 38134),
(198845, 'frFR', 'J\’aimerais être aussi confiant que vous. J\’ai des doutes, je le reconnais. Et pour être honnête, j\’ai du mal à m\’en débarrasser, même après l\’arrivée de ces étrangers.', 'J\’aimerais être aussi confiant que vous. J\’ai des doutes, je le reconnais. Et pour être honnête, j\’ai du mal à m\’en débarrasser, même après l\’arrivée de ces étrangers.', 38134),
(198844, 'frFR', 'Je n\’ai pour ma part jamais perdu la foi. Non que mon dévouement soit supérieur au vôtre, simplement, je n\’ai jamais douté du Dessein.', 'Je n\’ai pour ma part jamais perdu la foi. Non que mon dévouement soit supérieur au vôtre, simplement, je n\’ai jamais douté du Dessein.', 38134),
(198843, 'frFR', 'Je dois bien admettre que nos récentes découvertes ont ravivé ma foi. Le Dessein nous protège, c\’est certain.', 'Je dois bien admettre que nos récentes découvertes ont ravivé ma foi. Le Dessein nous protège, c\’est certain.', 38134),
(198842, 'frFR', 'Tout à fait. Le Dessein guide la création avec la minutie d\’un maître artisan. Jamais il ne gâcherait ses talents à créer un être seulement fonctionnel.', 'Tout à fait. Le Dessein guide la création avec la minutie d\’un maître artisan. Jamais il ne gâcherait ses talents à créer un être seulement fonctionnel.', 38134),
(198841, 'frFR', 'Une telle beauté ne peut être due à la chance seule. Cela porte la marque des Fondateurs, c\’est certain.', 'Une telle beauté ne peut être due à la chance seule. Cela porte la marque des Fondateurs, c\’est certain.', 38134),
(198840, 'frFR', 'Comme vous avez raison. Chacune de ses créations doit être louée.', 'Comme vous avez raison. Chacune de ses créations doit être louée.', 38134),
(198839, 'frFR', 'Cette vue me rappelle toujours à quel point le Dessein est grand.', 'Cette vue me rappelle toujours à quel point le Dessein est grand.', 38134),
(198838, 'frFR', 'C\’est ce que l\’on m\’a dit, oui. Le Dessein nous guide malgré tout, telle une lumière dans les ténèbres qui nous entourent.', 'C\’est ce que l\’on m\’a dit, oui. Le Dessein nous guide malgré tout, telle une lumière dans les ténèbres qui nous entourent.', 38134),
(198837, 'frFR', 'Nous allons les mettre en pièces !', '', 38134),
(198836, 'frFR', 'Il paraît que l\’Arbitre s\’agite !', 'Il paraît que l\’Arbitre s\’agite !', 38134),
(198835, 'frFR', 'Le Dessein les guide comme il le fait avec nous. Je n\’en doute pas une seconde.', 'Le Dessein les guide comme il le fait avec nous. Je n\’en doute pas une seconde.', 38134),
(198834, 'frFR', 'Les portes sont rouvertes. Enfin de bonnes nouvelles ! J\’espère que ces étrangers sont dignes de confiance.', 'Les portes sont rouvertes. Enfin de bonnes nouvelles ! J\’espère que ces étrangers sont dignes de confiance.', 38134),
(198833, 'frFR', 'Pour ma part, je ne doute pas du Dessein. Même lorsque ses voies sont impénétrables.', 'Pour ma part, je ne doute pas du Dessein. Même lorsque ses voies sont impénétrables.', 38134),
(198832, 'frFR', 'Je ne vois pas comment ce chaos pourrait être la volonté du Dessein !', 'Je ne vois pas comment ce chaos pourrait être la volonté du Dessein !', 38134),
(198830, 'frFR', '', 'Pas le temps de discuter. En avant !', 38134),
(198829, 'frFR', '', 'Je suis la baronne Draka. La maison des Élus est avec vous.', 38134),
(198828, 'frFR', 'La clé saura honorer les braves.', '', 38134),
(198827, 'frFR', 'La trahison est mère du chaos. Les liens détériorés doivent être retissés.', '', 38134),
(198826, 'frFR', 'Les terres des morts sont inondées par les ténèbres.', '', 38134),
(198825, 'frFR', 'Il ne doit pas en rester un !', '', 38134),
(198824, 'frFR', '', 'Tashmur, à vous !', 38134),
(198823, 'frFR', '', 'Je dois avertir l\’Hallali. On dirait qu\’on a encore besoin de votre aide !', 38134),
(198822, 'frFR', '', 'Regardez ! Tous ces Faë qui fuient le bosquet du Xylocarde… Il se passe quelque chose !', 38134),
(198821, 'frFR', '', 'Là ! Bien, maintenant que c\’est réglé… Attendez, qu\’est-ce que c\’est ?', 38134),
(198820, 'frFR', '', 'Pour me faire pardonner, voici un cadeau. C\’est une touche de la magie de Sylvarden, faites-en ce que vous voudrez.', 38134),
(198818, 'frFR', '', 'Nous avons besoin de vos talents de chasseur pour remonter jusqu\’à une proie insaisissable.', 38134),
(198817, 'frFR', 'Nous allons nous tailler un chemin droit à travers les défenses du Maître.\n\nVenez me voir quand vous voudrez démarrer l\’entraînement.', '', 38134),
(198816, 'frFR', 'Il vous attend au Trône de glace en compagnie des dirigeants de la Horde et de l\’Alliance.', '', 38134),
(198815, 'frFR', 'Mais en fendant le heaume, elle a aussi libéré Bolvar Fordragon, du fardeau d\’incarner le roi-liche.', '', 38134),
(198814, 'frFR', 'Il vous suffit de lever les yeux au ciel pour en voir les conséquences. Je… Je n\’ai jamais vu une chose pareille.', '', 38134),
(198813, 'frFR', 'Avant que ses créatures enlèvent vos dirigeants, la reine banshee a lancé une attaque contre la Couronne de glace. Elle s\’est frayée un chemin jusqu\’au Trône de glace et s\’est emparée du heaume de domination.', '', 38134),
(198811, 'frFR', 'Courbez donc l\’échine !', '', 38134),
(198810, 'frFR', 'C\’en est fini de vous !', '', 38134),
(198809, 'frFR', 'Vous ne m\’arrivez pas à la cheville !', '', 38134),
(198808, 'frFR', 'Vous pensiez vraiment pouvoir gagner ?', '', 38134),
(198807, 'frFR', 'Rémornia, écrasez ces insectes.', '', 38134),
(198806, 'frFR', 'Votre vanité vous fera tout perdre.', '', 38134),
(198805, 'frFR', 'Si vous vous obstinez, vous connaîtrez un tourment éternel !', '', 38134),
(198804, 'frFR', 'Mes fidèles, venez les dévorez.', '', 38134),
(198798, 'frFR', '', 'Huln Haut-Roc ! Je ne vous avais pas vu combattre depuis bien longtemps.', 38134),
(198789, 'frFR', '', 'Les larbins du Maître ne vont pas comprendre ce qui leur arrive.\n\nVenez me voir quand vous voudrez démarrer l\’entraînement.', 38134),
(198786, 'frFR', '', 'Une Faë rend toujours la politesse. Vous avez tant fait pour notre forêt et je n\’ai même pas réussi à livrer votre message en retour.', 38134),
(198778, 'frFR', 'Vous n\’allez pas ruiner ma couverture !', 'Vous n\’allez pas ruiner ma couverture !', 38134),
(198775, 'frFR', 'Vous revoilà ! Avez-vous trouvé ce que vous cherchiez ?', '', 38134),
(198774, 'frFR', 'Ah, vous tombez à point nommé.', '', 38134),
(198773, 'frFR', 'J\’ai préparé l\’illusion idéale pour vous permettre de passer sans être $uvu:vue;. Ne bougez pas…', '', 38134),
(198772, 'frFR', 'Prenez tout le nécessaire aux gorms. Ensuite, il sera temps d\’abattre le mange-monde.', '', 38134),
(198770, 'frFR', '<Nelwyn vous adresse un sourire en coin.>', '<Nelwyn vous adresse un sourire en coin.>', 38134),
(198769, 'frFR', '', 'Oh, aidez-nous, s\’il vous plaît !', 38134),
(198728, 'frFR', 'Dévastez-moi ces lâches, Mévix !', '', 38134),
(198727, 'frFR', 'Merci, $ucher ami:chère amie;. En gage de ma reconnaissance, permettez-moi de porter un toast à l\’un de vos invités.', '', 38134),
(198726, 'frFR', 'Vite ! Rassemblez les cinq pièces de mon service à thé avant que les démons de pierre ne les cassent !', '', 38134),
(198724, 'frFR', '', 'Et maintenant, ça chauffe pour de bon !', 38134),
(198722, 'frFR', 'Hecutis, brise leurs os !', '', 38134),
(198721, 'frFR', 'Bargast, déchire leur âme !', '', 38134),
(198720, 'frFR', 'Margore, saigne-les !', '', 38134),
(198717, 'frFR', '', 'Vous auriez dû rester sur la route !', 38134),
(198716, 'frFR', 'Lâchez les chiens !', '', 38134),
(198715, 'frFR', 'Faites un peu attention, je vous prie ! Vous en faites tomber partout !', '', 38134),
(198714, 'frFR', 'Non ! C\’est mon plus beau service à thé !', '', 38134),
(198713, 'frFR', 'Sur celui-là, j\’ai inscrit votre nom.', '', 38134),
(198712, 'frFR', 'J\’ai bien assez de traits pour tout le monde !', '', 38134),
(198711, 'frFR', 'Rien n\’échappe à ma visée.', '', 38134),
(198710, 'frFR', 'Votre peur vous trahit.', '', 38134),
(198709, 'frFR', 'Mais… Les pertes vont être immenses.', '', 38134),
(198708, 'frFR', 'C\’est le prix de la victoire !', '', 38134),
(198707, 'frFR', 'Avancez ! Il faut briser leur défense avec une attaque de front !', '', 38134),
(198705, 'frFR', 'Imbéciles ! Revendreth, c\’est moi !', '', 38134),
(198698, 'frFR', 'Rémornia, faites hurler leur chair !', '', 38134),
(198697, 'frFR', 'Grâce à votre aide, l\’armée de Revendreth continue à gagner en nombre comme en force.', '', 38134),
(198696, 'frFR', 'Rémornia, massacrez cette valetaille !', '', 38134),
(198695, 'frFR', 'Des sombrailes ont été déployés ici pour entraînement. Faites votre possible pour que nos troupes soient parées au combat !', '', 38134),
(198694, 'frFR', 'Que les ombres vous emportent !', '', 38134),
(198693, 'frFR', 'Vous allez périr tous ensemble !', '', 38134),
(198692, 'frFR', 'Criez pour moi !', '', 38134),
(198691, 'frFR', 'Je vais vous mettre à terre !', '', 38134),
(198689, 'frFR', 'Mes dévoués adeptes, évacuez cette racaille.', '', 38134),
(198687, 'frFR', 'Mes serviteurs, nettoyez donc cette saleté qui m\’indispose.', '', 38134),
(198684, 'frFR', 'Venez m\’implorer à genoux et je vous accorderai une fin clémente.', '', 38134),
(198683, 'frFR', 'Il est temps de vous rappeler quelle est votre place.', '', 38134),
(198682, 'frFR', 'Rémornia, débarrassez mon château de ces vermines.', '', 38134),
(198677, 'frFR', 'Votre orgueil est un fardeau impossible à porter !', '', 38134),
(198676, 'frFR', 'Holà, attention ! Cette route n\’est plus sûre.', '', 38134),
(198675, 'frFR', '', 'Rien n\’échappe à l\’Hallali. Un de ses membres doit bien pouvoir nous aider.', 38134),
(198673, 'frFR', 'Des lâches doublés de traîtres. Ils regretteront de défier la puissance des Élus !', '', 38134),
(198670, 'frFR', 'Parlez, recrue !', '', 38134),
(198669, 'frFR', 'La douleur purifie l\’âme.', '', 38134),
(198668, 'frFR', 'Confessez vos vices avant qu\’ils ne vous dévorent !', '', 38134),
(198665, 'frFR', '', 'Ne faites pas attendre Vyraz. La patience n\’est pas son fort.', 38134),
(198653, 'frFR', '<Cette Sylvar est anormalement pâle et respire difficilement… Si ça se trouve, elle est déjà aux portes de la mort.\n\nUne seconde plus tard, l\’un de ses yeux s\’écarquille et vous observe fixement.\n\nPuis, sa bouche s\’ouvre en grand, tel un gouffre béant.>', '<Cette Sylvar est anormalement pâle et respire difficilement… Si ça se trouve, elle est déjà aux portes de la mort.\n\nUne seconde plus tard, l\’un de ses yeux s\’écarquille et vous observe fixement.\n\nPuis, sa bouche s\’ouvre en grand, tel un gouffre béant.>', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198651, 'frFR', '', 'Oh, merci ! C\’est si dur de trouver quelqu\’un sur qui compter.', 38134),
(198649, 'frFR', 'Ma grand-mère est gravement malade !\n\nNous cherchions à atteindre Tirna Vaal dans l\’espoir que la dromane puisse nous aider, mais elle est trop faible et la forêt trop dangereuse !\n\nS\’il vous plaît, aidez-moi. Ma grand-mère est cachée dans une grotte non loin d\’ici. La pauvre, elle est si mal en point…', 'Ma grand-mère est gravement malade !\n\nNous cherchions à atteindre Tirna Vaal dans l\’espoir que la dromane puisse nous aider, mais elle est trop faible et la forêt trop dangereuse !\n\nS\’il vous plaît, aidez-moi. Ma grand-mère est cachée dans une grotte non loin d\’ici. La pauvre, elle est si mal en point…', 38134),
(198648, 'frFR', '', 'Merci infiniment ! Vous nous avez beaucoup aidées, moi et les âmes qui m\’ont été confiées.\n\nSurtout, ne vous éloignez pas de la route. La forêt grouille de créatures dangereuses !\n\nMerci encore !', 38134),
(198645, 'frFR', '', 'Puisse la nuit vous protéger. Nous vous sommes redevable ! ', 38134),
(198642, 'frFR', '', 'Sauvez ces pauvres âmes !', 38134),
(198616, 'frFR', 'Mes membres doivent être… comptabilisés…', '', 38134),
(198615, 'frFR', 'Un autre corps à faucher !', '', 38134),
(198612, 'frFR', '', 'Le journal de Matyas s\’est révélé très instructif. Mais nous allons devoir jouer de ruse pour lui arracher ma stèle du vice. J\’ai une idée pour le neutraliser. Il nous faut juste les bons ingrédients.', 38134),
(198607, 'frFR', 'Sauf votre respect, vous m\’avez l\’air de quelqu\’un qui est habitué à en voir de toutes les couleurs.', '', 38134),
(198606, 'frFR', 'Elles incarnent la raison d\’être de Sylvarden ! Les graines sauvages renferment des esprits de la nature plongés dans un profond sommeil, comme l\’ancienne Dunmira. Qui se trouve sous cet oiseau. Si vous parveniez à le convaincre de quitter son nid, je pourrais la récupérer discrètement !\n\nJe suis ravi que Baie-de-Lune ait envoyé quelqu\’un pour la remplacer. Vous savez qu\’elle fait généralement toute une histoire quand il s\’agit de donner son aide ?\n\n<Korenth s\’esclaffe.>\n\nAlors, vous allez m\’aider à virer cet oiseau du nid ?\n\n<Korenth vous lance un sourire plein d\’espoir.>', '', 38134),
(198590, 'frFR', 'Ah, quelque chose ne va pas… Son cauchemar est encore là. Qu\’est-ce qui a bien pu lui arriver ?', '', 38134),
(198589, 'frFR', 'Mais, attendez… Il est en train de se lier… à vous ! Je n\’ai pas vu ça depuis… Eh, aussi loin que je me souvienne !', '', 38134),
(198573, 'frFR', 'Une goutte d\’anima devrait permettre de l\’apaiser. Quand ses rêves seront plus tranquilles, nous pourr…', '', 38134),
(198569, 'frFR', 'Oh oh… L\’esprit de celle-là est en train de subir un cauchemar. Le pauvre doit avoir connu une fin tragique…', '', 38134),
(198567, 'frFR', 'La Traquepeur vous demande de conduire Loksey à la colline de l\’Hypogée. Elle est déjà en route.', '', 38134),
(198565, 'frFR', 'Attendez, je la reconnais… elle était à bord de notre vaisseau ! Elle a dû survivre à la collision.', '', 38134),
(198564, 'frFR', 'On la tient !', '', 38134),
(198563, 'frFR', 'Regardez, elle approche !', '', 38134),
(198561, 'frFR', 'Ce fut un honneur exceptionnel, prince Rénathal. Merci.', '', 38134),
(198560, 'frFR', 'Non ! Faut pas nous mettre dehors ! On s\’amuse comme des fous ici !', '', 38134),
(198559, 'frFR', 'Mon rapport à l\’Archonte sera parfaitement élogieux.', '', 38134),
(198558, 'frFR', '', 'La réception s\’est montrée à la hauteur des attentes. J\’attends la prochaine avec impatience.', 38134),
(198557, 'frFR', 'J\’ai beaucoup appris. Il faut que je cogite sur tout ça. Ces types sont futés…', '', 38134),
(198556, 'frFR', '', 'J\’ai passé une soirée absolument délicieuse !', 38134),
(198555, 'frFR', '', 'Votre cour est impressionnante, prince. Tout n\’est pas perdu, semble-t-il.', 38134),
(198551, 'frFR', 'Cette petite graine sauvage n\’est-elle pas merveilleuse ? Elle ne regagnera pas son monde avant de longues années.\n\nPrenez les glands que vous avez trouvés et imprégnez-la d\’anima.', '', 38134),
(198549, 'frFR', 'Flétrissure et décomposition !', '', 38134),
(198546, 'frFR', 'Par le sacrifice nous arrive la victoire !', '', 38134),
(198545, 'frFR', 'Toujours, mes soldats se relèvent !', '', 38134),
(198542, 'frFR', '', 'Vous voulez… m\’empêcher de voler ? Alors je vais… vous rendre la politesse !', 38134),
(198534, 'frFR', 'Tout ça, c\'est grâce à notre $ucher ami:chère amie; !', '', 38134),
(198533, 'frFR', '', 'Votre mélodie a retrouvé tout son éclat, Tisse-Rêves.', 38134),
(198532, 'frFR', 'Ah, Nadjia, vous reprenez enfin vos esprits ! Nous étions en train d\’éplucher le journal personnel du sous-chef pour comprendre pourquoi il a invoqué notre $umortel:mortelle;.', '', 38134),
(198531, 'frFR', '', 'Argh. J\’ai l\’impression qu\’on m\’a planté des aiguilles dans les yeux. C\’est vous, Sans-Entraves ?', 38134),
(198529, 'frFR', '', 'Désolée, j\’ai dû faire un petit arrêt sur le chemin.', 38134),
(198527, 'frFR', 'Commençons par un esprit assez jeune. Il est arrivé juste avant la pénurie et il a besoin d\’un peu plus de soins.', '', 38134),
(198520, 'frFR', 'Vous avez entendu les ordres ! En avant, marche !', '', 38134),
(198519, 'frFR', 'Venez. Vous allez voir comment s\’occuper d\’une graine et suivre les rêves de son esprit.', '', 38134),
(198517, 'frFR', 'Tout le monde s\’est déjà fait manger par des plantes. C\’est quelque chose qui arrive.', '', 38134),
(198510, 'frFR', '', 'Regardez, $n. Il suffit de frapper une âme de peur pour que son anima coule à flots.', 38134),
(198509, 'frFR', 'Nous lancerons l\’attaque au son du cor. Pour les Élus !', '', 38134),
(198505, 'frFR', '', '<Nadjia tente désespérément de parler.>', 38134),
(198500, 'frFR', 'Nous allons retourner l\’anima volé par Denathrius contre lui.', '', 38134),
(198499, 'frFR', 'Sans-Entraves ! Venez, et prenez autant d\’anima que vous pourrez. C\’est un gain temporaire, mais qui devrait vous être précieux pour le combat à venir.', '', 38134),
(198496, 'frFR', '', 'Eurh. Les spriggans.', 38134),
(198495, 'frFR', '', 'Mes yeux sont bien ouverts, et ce qu\’ils voient est un avenir plein de promesses. Mais pas pour les traîtres comme vous !', 38134),
(198494, 'frFR', '', 'Denathrius a trahi Revendreth en déversant son anima dans l\’Antre. Même une arriviste comme vous doit finir par ouvrir les yeux !', 38134),
(198493, 'frFR', '', 'Accusatrice. Vous réussissez à m\’échapper, tout ça pour vous remontrer ici à la tête d\’une pauvre bande de petits mortels. Quelle tristesse.', 38134),
(198489, 'frFR', 'Ma colère brûle comme un millier de soleils !', '', 38134),
(198488, 'frFR', 'Je n\’en ai pas terminé ! Libérez-moi !', '', 38134),
(198487, 'frFR', '', 'Tor ilisar\’thera\’nal !', 38134),
(198486, 'frFR', 'Tyrande, non !', '', 38134),
(198485, 'frFR', '', 'Occupez de sauver les autres, je vais exécuter la Banshee !', 38134),
(198484, 'frFR', 'Retrouvez nos camarades enlevés et ramenez-les nous !', '', 38134),
(198483, 'frFR', 'Votre bravoure ne sera jamais oubliée. Al diel shala.', '', 38134),
(198482, 'frFR', 'La voie est ouverte !', '', 38134),
(198481, 'frFR', 'Le moment est venu. Brandissez le heaume brisé et nous ouvrirons la brèche.', '', 38134),
(198480, 'frFR', 'Mais il n\’y a pas d\’alternative. La mort voudrait nous ravir Azeroth et c\’est à vous de l\’en empêcher.', '', 38134),
(198479, 'frFR', 'Aucun vivant n\’a jamais été censé passer de l\’autre côté du voile. Vous partez pour un endroit dont vous pourriez ne jamais revenir.', '', 38134),
(198478, 'frFR', 'Même dans un royaume de l\’au-delà, la Lame d\’ébène ne craint pas la mort.', '', 38134),
(198477, 'frFR', 'Les chevaliers de la Lame d\’ébène vont mettre leur force à votre service. Darion Mograine est volontaire pour combattre à vos côtés.', '', 38134),
(198476, 'frFR', 'Vous affronterez des dangers terribles. Sans aide, vous pourriez être $udépassé:dépassée;.', '', 38134),
(198475, 'frFR', 'Mais cette menace doit absolument être jugulée. Alors vous devez aller où je ne le peux pas.', '', 38134),
(198474, 'frFR', 'Je ne peux pas vous accompagner. Même libéré du heaume, je craindrais trop de tomber sous l\’influence de son véritable maître.', '', 38134),
(198473, 'frFR', 'Et aujourd\’hui, je sens cette même présence tapie de l\’autre côté de la brèche.', '', 38134),
(198472, 'frFR', 'Je percevais une noire présence à la lisière de mon esprit. Mais pas Arthas… ni Ner\’zhul… Quelque chose d\’autre.', '', 38134),
(198471, 'frFR', 'Le pouvoir du heaume est insidieux. Il me fallait toute ma volonté pour réussir à le contenir. Bien des fois, j\’ai failli ployer sous l\’effort.', '', 38134),
(198470, 'frFR', 'Disposez les éclats de domination autour du cercle runique et le rituel pourra commencer.', '', 38134),
(198469, 'frFR', 'C\’est là que vous devez vous rendre pour sauver les victimes de Sylvanas. Tous ensemble, nous pouvons ouvrir un chemin.', '', 38134),
(198468, 'frFR', 'Pendant ma veille sur le Trône de glace, j\’ai eu de brèves visions des royaumes de la mort. Et parmi eux, d\’un abîme aux ténèbres inexorables.', '', 38134),
(198467, 'frFR', 'En brisant le heaume, Sylvanas a ouvert une plaie béante entre notre monde et l\’Ombreterre.', '', 38134),
(198466, 'frFR', 'Il y a une menace bien plus urgente et nous devons nous préparer à l\’affronter.', '', 38134),
(198465, 'frFR', 'Assez ! La guerre est terminée et personne n\’a intérêt à ce qu\’elle recommence.', '', 38134),
(198464, 'frFR', '', 'Vous osez parler de paix après avoir commis de telles atrocités ?!', 38134),
(198463, 'frFR', '', 'S\’ils veulent un espoir de trouver la paix, les Kaldoreï ont besoin de vous.', 38134),
(198462, 'frFR', 'Moi aussi, je veux la voir payer pour ses crimes. Mais Lor\’themar a raison, Anduin ne voudrait pas qu\’on mette l\’Alliance en danger pour lui.', '', 38134),
(198461, 'frFR', '', 'Lâche ! Je donnerais tout pour voir la Banshee au bout de ma lame !', 38134),
(198460, 'frFR', 'Il serait inconcevable d\’envoyer les dirigeants d\’Azeroth en mission suicide. Nos amis enlevés auraient dit la même chose.', '', 38134),
(198459, 'frFR', 'La paix reste bien trop fragile. L\’époque est incertaine et nos nations ont besoin de stabilité, l\’Alliance comme la Horde.', '', 38134),
(198456, 'frFR', 'Il reste fort à faire pour nous préparer à intervenir partout dans l\’Ombreterre.', '', 38134),
(198452, 'frFR', 'Parfois, je ne vois plus l\’intérêt de préparer les âmes. Sans nouveaux arrivants, le rendement en anima diminue.', 'Parfois, je ne vois plus l\’intérêt de préparer les âmes. Sans nouveaux arrivants, le rendement en anima diminue.', 38134),
(198451, 'frFR', 'Il paraît que vous êtes à la recherche d\’anima. Si seulement on en avait assez…', 'Il paraît que vous êtes à la recherche d\’anima. Si seulement on en avait assez…', 38134),
(198450, 'frFR', 'Regardez au-dessus de vous. Nous ne contrôlons même plus nos propres créations !', 'Regardez au-dessus de vous. Nous ne contrôlons même plus nos propres créations !', 38134),
(198449, 'frFR', 'En temps normal, ces cages sont pleines à craquer… Maudite pénurie.', 'En temps normal, ces cages sont pleines à craquer… Maudite pénurie.', 38134),
(198431, 'frFR', '', 'Allez, plus fort ! Pas de quartier !', 38134),
(198429, 'frFR', '', 'Vous avez toute ma gratitude. Une fois le Geôlier vaincu… il sera temps pour les Kyrians d\’entamer une grande réflexion.', 38134),
(198428, 'frFR', '', 'Pendant longtemps, nous n\’avons vu en nos vies mortelles que des fardeaux dont il fallait nous débarrasser pour servir. Pourtant, c\’est bien aux mortels que nous devons notre victoire.', 38134),
(198427, 'frFR', '', 'Les manigances du Geôlier ont provoqué une déchirure au sein des Kyrians. Il faut la refermer.', 38134),
(198426, 'frFR', '', 'Déva…', 38134),
(198425, 'frFR', '', 'Le Geôlier a corrompu votre esprit. Je dois vous arrêter… coûte que coûte.', 38134),
(198424, 'frFR', '', 'Avec votre lance, je vais pouvoir m\’emparer des flèches et forger de nouveaux parangons pour mener les Kyrians !', 38134),
(198423, 'frFR', '', 'Acceptez la défaite. Je sers une puissance bien supérieure à présent.', 38134),
(198422, 'frFR', '', 'Argh !', 38134),
(198421, 'frFR', '', 'Et moi, je pleure votre trahison.', 38134),
(198420, 'frFR', '', 'Il cherche à nous libérer de cette prison que nous nous sommes créée. Pour cette raison, il a gagné ma loyauté.', 38134),
(198419, 'frFR', '', 'Alors j\’ai tendu l\’oreille aux paroles du Geôlier. Il m\’a appris quelle injustice vous et les autres lui aviez fait subir… et à nous tous.', 38134),
(198418, 'frFR', '', 'Mais vous, vous avez refusé de le voir. De voir que la voie est viciée et que nous servons un mensonge.', 38134),
(198417, 'frFR', '', 'Ce que j\’ai aperçu en observant les souvenirs d\’Uther m\’a secouée jusqu\’au plus profond de moi.', 38134),
(198416, 'frFR', '', 'Non. Et c\’est une créature mortelle qui a révélé la folie de vos préceptes.', 38134),
(198415, 'frFR', 'Face à l\’avancée des alliés du Geôlier, il faut en exploiter toutes les ressources.', '', 38134),
(198414, 'frFR', '', 'Nous nous sacrifions parce qu\’il le faut. Traîner nos fardeaux mortels ne fait qu\’entraver notre devoir sacré.', 38134),
(198413, 'frFR', '', 'Mais pour servir quoi et qui ? Une Arbitre impuissante aux lois écrasantes ! Tous ces sacrifices pour rien !', 38134),
(198412, 'frFR', 'L\’Archonte a fait bâtir le fort Élyséen pour qu\’il devienne la base principale de la congrégation.', '', 38134),
(198411, 'frFR', '', 'Depuis une éternité, on nous dit de nous « purifier » en abandonnant nos souvenirs, nos identités et ceux qui nous sont chers. Tout ça pour servir.', 38134),
(198410, 'frFR', '', 'Pas sous votre commandement. Les désavoués vont ouvrir une nouvelle voie pour le Bastion !', 38134),
(198409, 'frFR', '', 'Revenez à la raison ! Les Kyrians doivent rester unis.', 38134),
(198408, 'frFR', '', 'Désavoués, empêchez les mortels de s\’en mêler !', 38134),
(198407, 'frFR', '', 'Votre fidélité aveugle à la voie nous condamnait tous. Je devais m\’y opposer, vous ne m\’avez pas laissé le choix !', 38134),
(198406, 'frFR', '', 'C\’est vous qui étiez à la tête des désavoués depuis le début. Mais vous étiez le parangon de loyauté !', 38134),
(198405, 'frFR', '', 'Terrassez leur arrière-garde pendant que je vais affronter Déva. Allez, et servez.', 38134),
(198404, 'frFR', '', 'Nous sommes très inférieurs en nombre. Les désavoués ont balayé la plupart de nos troupes.', 38134),
(198403, 'frFR', 'Contemplez toute la gloire du fort Élyséen. Éblouissant, n\’est-ce pas ?', '', 38134),
(198402, 'frFR', 'Sans-Entraves, ces âmes vaillantes sont à présent votre famille. Bienvenue.', '', 38134),
(198401, 'frFR', 'À présent, ensemble ! À l\’attaque !', '', 38134),
(198400, 'frFR', 'Je vais en finir !', '', 38134),
(198399, 'frFR', 'J\’ai accompli ma mission !', '', 38134),
(198398, 'frFR', 'Encore… J\’en veux plus…', '', 38134),
(198397, 'frFR', 'J\’en ai fini avec celui-ci.', '', 38134),
(198396, 'frFR', 'Elle a choisi de rejoindre notre auguste congrégation et de nous aider à reconquérir notre royaume.', '', 38134),
(198395, 'frFR', 'Une encoche de plus sur ma garde.', '', 38134),
(198390, 'frFR', 'Partez devant et trouvez le général Alver. Quand vous serez en position, sonnez le cor pour déclencher l\’attaque.', '', 38134),
(198386, 'frFR', 'Montrez-moi qui sont vos ennemis, et je me charge du reste.', '', 38134),
(198385, 'frFR', 'Pour vous remercier de m\’avoir rendu ma liberté, je vais vous aider à éliminer vos adversaires… pendant un temps, du moins.', '', 38134),
(198384, 'frFR', 'Laissez-moi mener l\’attaque, et vos ennemis tomberont comme des mouches.', '', 38134),
(198383, 'frFR', 'Joli combat, $p ! Un ordre de vous, et nous lançons le prochain assaut !', '', 38134),
(198382, 'frFR', 'Oh, comme je me languissais des combats…', '', 38134),
(198381, 'frFR', 'Nous frappons toujours juste !', '', 38134),
(198371, 'frFR', 'C\’est juste une épreuve de plus. Même si j\’échoue… cela ne fera qu\’augmenter mes chances lors de la prochaine tentative.', '', 38134),
(198370, 'frFR', 'J\’ai un bon pressentiment cette fois, $n.', '', 38134),
(198369, 'frFR', 'C\’est mon tour ?\n\nJe ferai de mon mieux.', '', 38134),
(198368, 'frFR', '', '« Invoquez Mortanis »… Avec la pénurie ?! Ils n\’y pensent pas !', 38134),
(198365, 'frFR', 'Oui, chef !', '', 38134),
(198363, 'frFR', 'Est-ce vraiment une trahison quand votre maître vous a été imposé ?', '', 38134),
(198362, 'frFR', 'Ah, et encore merci pour tout ! Je ne sais pas trop de quel monde vous venez, mais il a bien de la chance de vous avoir.', '', 38134),
(198361, 'frFR', 'Avec ma dague, ouvrez les plantes pour libérer les autres. Nous nous retrouverons en Tirna Vaal.', '', 38134),
(198341, 'frFR', 'Les plantes les ont dévorés ? Oh non, pas encore…', '', 38134),
(198340, 'frFR', 'Ouais, c\’est peu d\’le dire. J\’connais pas beaucoup de grogas aussi pénibles en état d\’ébria… heu… d\’abrié… \’fin, bourré, quoi. Hic ! Le seul truc à faire, c\’est de le ramollir à la bibine.', '', 38134),
(198339, 'frFR', 'Voilà qui est mieux ! C\’est que plus personne ne fait attention aux personnes âgées, de nos jours.', '', 38134),
(198338, 'frFR', 'Ah, vous ne pouviez pas mieux tomber ! Vous voulez bien vous occuper de mes pousses ? Et de ces satanés envahisseurs ?', '', 38134),
(198337, 'frFR', 'T\’inquiète. On risque rien tant que personne nous balance. Le problème, c\’est plutôt Biglevase.', '', 38134),
(198336, 'frFR', 'Au secours ! Aidez-moi !', '', 38134),
(198335, 'frFR', 'C\’est pas bien, c\’qu\’on fait là. Si le croc nous chope à glander, on retournera direct aux tourbières.', '', 38134),
(198334, 'frFR', '', 'Voilà qui devrait calmer la situation. J\’espère que vous n\’avez pas trop respiré de spores.', 38134),
(198333, 'frFR', '', 'Le champignomancien en chef a besoin d\’aide ! Je vous en prie, il faut protéger les Stipes !', 38134),
(198332, 'frFR', '', 'Par là, suivez-moi.', 38134),
(198329, 'frFR', 'Nous attaquerons à votre signal. Les purotins ici présents utiliseront le miroir pour repousser les renforts en provenance du château Nathria ; dans le même temps, vous, les quelques alliés qu\’il nous reste et moi lancerons notre assaut contre Denathrius.\n\nIl en revient à une âme sans-entraves, à trois moissonneurs, à l\’ancien général de la Légion de pierre et aux quelques Venthyrs qui nous soutiennent encore de combattre l\’être qui a créé tout ce qui s\’étend devant nous.\n\nQuand vous serez $uparé:parée; à affronter la mort, venez me parler.', '', 38134),
(198316, 'frFR', 'Vous n\’avez aucune chance, créature mortelle !', '', 38134),
(198315, 'frFR', 'Soldats, le combat fera bientôt rage ! Préparez les défenses !', '', 38134),
(198313, 'frFR', '', '<Le centurion se déplace de façon erratique.>\n\nJe suis victime d\’une erreur critique.\n\nJe suis victime d\’une erreur critique.\n\nJe suis victime d\’une erreur critique.', 38134),
(198312, 'frFR', 'ATTRAPE ! ATTRAPE ! AHHH !\n\nATTRAPE LE PARCHEMIN !', 'ATTRAPE ! ATTRAPE ! AHHH !\n\nATTRAPE LE PARCHEMIN !', 38134),
(198311, 'frFR', '', 'Allez, et servez, $n.', 38134),
(198310, 'frFR', '', 'Suivre la voie de l\’Ascension en tant que combattant est un grand honneur. Un reflet de mon être se battra au travers de votre lien d\’âme… et peut-être ferai-je moi-même une apparition.', 38134),
(198309, 'frFR', '', 'Voilà qui est fait. Un fragment de ce que je suis réside désormais à vos côtés dans ce miroir.', 38134),
(198308, 'frFR', 'BOULETTE PRÊT À COMBATTRE !', '', 38134),
(198307, 'frFR', 'Ma méditation matinale m\’a apporté un calme bienvenu, mais je ferais bien autre chose que m\’entraîner.\n\nUn bon livre, peut-être ?', 'Ma méditation matinale m\’a apporté un calme bienvenu, mais je ferais bien autre chose que m\’entraîner.\n\nUn bon livre, peut-être ?', 38134),
(198306, 'frFR', 'Hum… Je croquerais bien un fruit. Pourriez-vous me trouver un régisseur ?', 'Hum… Je croquerais bien un fruit. Pourriez-vous me trouver un régisseur ?', 38134),
(198305, 'frFR', 'LE TOURMENTEUR A VOLÉ LE MANGER ! IL VOLE TOUJOURS TOUT LE MANGER ! JE VEUX VENGEANCE ! ET MANGER !', '', 38134),
(198304, 'frFR', '', 'Avancez-vous. Présentez-vous devant vos frères kyrians, qu\’ils puissent vous accueillir en leur sein.', 38134),
(198303, 'frFR', 'Et voici notre révérée Archonte ! Elle qui était déjà ancienne quand ce royaume naissait à peine.', '', 38134),
(198302, 'frFR', 'L\’âme sans-entraves a prouvé qu\’elle était une fière alliée non seulement du Bastion, mais de toute l\’Ombreterre.', '', 38134),
(198301, 'frFR', 'Ce rituel est censé aider les esprits sans repos à surmonter les traumatismes qui les empêchent de recouvrer leurs forces.\n\nJ\’ai le sentiment que cet esprit, celui qui s\’est tant attaché à vous, a vécu une terrible épreuve de son vivant. J\’espère que nous réussirons à apaiser ses cauchemars.', '', 38134),
(198300, 'frFR', 'Oyez, mes frères et sœurs ! Nous voici rassemblés en un jour glorieux.', '', 38134),
(198299, 'frFR', 'À cette occasion, nous devons parcourir le chemin ensemble. Venez.', '', 38134),
(198298, 'frFR', '', 'La pierre ! Qu\’avez-vous fait ?!', 38134),
(198297, 'frFR', '', 'Bien que j\’ai déjà fort à faire, servir la voie me permettrait de faire encore davantage pour le Bastion. J\’accepte donc de vous laisser utiliser le miroir des âmes pour capturer mon souvenir.', 38134),
(198296, 'frFR', '', 'La voie de l\’Ascension ? Je suis flattée qu\’on me considère comme digne d\’enseigner aux aspirants.', 38134),
(198292, 'frFR', '', 'Le Primat nous a laissé la pierre en défi. Vous devez arriver à elle pour prouver votre force.', 38134),
(198288, 'frFR', 'Je boirais bien quelque chose. Où donc est passé ce régisseur ?', 'Je boirais bien quelque chose. Où donc est passé ce régisseur ?', 38134),
(198287, 'frFR', 'Mais… vous êtes… inférieurs…', '', 38134),
(198286, 'frFR', '', 'Ma mission consiste à préserver nos ressources et à protéger les aspirants placés sous mon aile.\n\nJe me souviens de notre première rencontre, de l\’anima gâché pour des centurions. Vous nous avez aidés à surmonter à cette pénurie, mais nous ne pouvons toujours pas dépenser sans compter.\n\nPourquoi m\’invoquez-vous ?', 38134),
(198271, 'frFR', 'En sécurité ! Oui, on sera en sécurité ici.\n\nMerci ! Merci de nous avoir sauvés !', '', 38134),
(198268, 'frFR', '', 'Je vous avais prévenu de ne pas remettre les pieds ici !', 38134),
(198267, 'frFR', '', 'Zayhad ! Réveillez-vous !', 38134),
(198265, 'frFR', 'On y est presque ! Les écuries sont en haut de la colline qu\’on voit là-bas.', '', 38134),
(198264, 'frFR', 'Si les fiers-à-bras de l\’Alliance avaient leur mot à dire là-dessus, on n\’aurait même pas ça.', '', 38134),
(198263, 'frFR', '', 'Bienvenue, jeune pousse. Chaque pas que vous faites révèle votre chemin.', 38134),
(198262, 'frFR', 'Le truc, c\’est qu\’Orgrimmar, c\’est l\’endroit où on peut se sentir chez nous. Et ça, ça compte beaucoup pour les gens.', '', 38134),
(198261, 'frFR', 'Bon, la ville est un nid à poussière, il fait une chaleur de mort et les Orcs ne sont pas toujours très accueillants, vous excepté bien sûr… mais ça a un charme fou, hein ?', '', 38134),
(198260, 'frFR', 'Bon, la ville est un nid à poussière, il fait une chaleur de mort et les Orcs ne sont pas très accueillants… mais ça a un charme fou, hein ?', '', 38134),
(198259, 'frFR', 'Bienvenue dans le grand bain, $n. Vous voilà à Orgrimmar. C\’est ici que se font les vraies affaires.', '', 38134),
(198257, 'frFR', '', 'Je suis bien placée pour savoir que certains ne servent pas Matyas de leur plein gré. Délivrez-les si vous le pouvez.', 38134),
(198256, 'frFR', 'Un autre chute…', '', 38134),
(198253, 'frFR', 'Je ressens… tout. Par l\’Archonte, vous avez vécu une vie bien remplie !', '', 38134),
(198252, 'frFR', 'Les purians, ça rend heureux ! On en donne aux aspirants ?', 'Les purians, ça rend heureux ! On en donne aux aspirants ?', 38134),
(198251, 'frFR', 'C\’est pour qui le parchemin ? Hou ?', 'C\’est pour qui le parchemin ? Hou ?', 38134),
(198250, 'frFR', 'Hou ! Où ils sont, les assoiffés ?', 'Hou ! Où ils sont, les assoiffés ?', 38134),
(198249, 'frFR', 'Qu\’est-ce qu\’il faut nettoyer ?', 'Qu\’est-ce qu\’il faut nettoyer ?', 38134),
(198248, 'frFR', 'Je peux tenir, porter ou attraper !', 'Je peux tenir, porter ou attraper !', 38134),
(198247, 'frFR', 'Purian bien mûr, trop bon !\n\nParfait pour les aspirants, oui ! Mais il faut d\’abord trimer !', 'Purian bien mûr, trop bon !\n\nParfait pour les aspirants, oui ! Mais il faut d\’abord trimer !', 38134),
(198246, 'frFR', 'Parchemin, parchemin, parchemin…\n\nOù ranger le parchemin ?', 'Parchemin, parchemin, parchemin…\n\nOù ranger le parchemin ?', 38134),
(198245, 'frFR', 'Vous avez soif ? J\’ai un jus bien sucré !', 'Vous avez soif ? J\’ai un jus bien sucré !', 38134),
(198244, 'frFR', 'Nettoyer, tout nettoyer !\n\n<Le régisseur fredonne en travaillant.>', 'Nettoyer, tout nettoyer !\n\n<La régisseuse fredonne en travaillant.>', 38134),
(198243, 'frFR', 'Je tiens ça pour vous ?', 'Je tiens ça pour vous ?', 38134),
(198239, 'frFR', 'Quelle que soit votre espèce, c\’est un plaisir de vous voir en Sylvarden.', '', 38134),
(198232, 'frFR', 'J\’en serais honoré, guidesprit. J\’espère seulement… J\’espère pouvoir conserver mon lien avec Kléia aussi.', '', 38134),
(198230, 'frFR', 'Bon, finissez ici, je vais à la recherche des autres cultivateurs.', '', 38134),
(198229, 'frFR', 'Mais depuis la pénurie, il n\’y a plus assez d\’anima pour toutes. Il a fallu faire des choix… difficiles.', '', 38134),
(198228, 'frFR', 'Les cultivateurs doivent nourrir les graines en anima ou les esprits qu\’elles abritent ne peuvent pas renaître.', '', 38134),
(198227, 'frFR', 'Oh, merci ! Les bassins ne sont vraiment pas tranquilles en ce moment…', '', 38134),
(198226, 'frFR', 'Oh, non… La reine ne va vraiment pas être contente…', '', 38134),
(198224, 'frFR', 'Je vous fracasse !', '', 38134),
(198223, 'frFR', 'Je vous brise !', '', 38134),
(198222, 'frFR', 'Je vais vous enterrer !', '', 38134),
(198221, 'frFR', 'Je vais vous briser !', '', 38134),
(198220, 'frFR', 'Les gardes devraient pouvoir nous dire où on trouvera les écuries.', '', 38134),
(198219, 'frFR', 'Alors, c\’est quoi le topo ?', '', 38134),
(198214, 'frFR', 'Cette insolence me fatigue !', '', 38134),
(198213, 'frFR', 'Rien ne peut me briser !', '', 38134),
(198212, 'frFR', 'Vos lames se briseront sur ma peau !', '', 38134),
(198210, 'frFR', 'Périssez de ma main !', '', 38134),
(198208, 'frFR', 'Servir fut… le plus grand honneur…', '', 38134),
(198205, 'frFR', 'Vous n\’étiez pas dignes de me croiser sur le champ de bataille.', '', 38134),
(198203, 'frFR', 'Que cela vous serve de leçon !', '', 38134),
(198202, 'frFR', 'Racaille sans honneur !', '', 38134),
(198201, 'frFR', 'Mort aux traîtres !', '', 38134),
(198200, 'frFR', '', 'Ma lame vous trouvera !', 38134),
(198199, 'frFR', '', 'Je déchire votre chair !', 38134),
(198198, 'frFR', '', 'Saignez !', 38134),
(198197, 'frFR', '', 'Il n\’y a pas d\’issue !', 38134),
(198196, 'frFR', '', 'Vous ne m\’échapperez pas !', 38134),
(198194, 'frFR', '', 'Votre fin est proche !', 38134),
(198193, 'frFR', '', 'Nous ne savourerons pas bien longtemps notre victoire aux confins de l\’Exil, car il nous faut à présent livrer une guerre.', 38134),
(198192, 'frFR', '', 'Rien ne peut me briser !', 38134),
(198191, 'frFR', '', 'De chair en pierre !', 38134),
(198190, 'frFR', '', 'Je vais vous anéantir moi-même !', 38134),
(198189, 'frFR', '', 'Là-haut, Sans-Entraves. J\’ai trouvé Matyas au sommet de la tour. Rejoignez-moi quand vous aurez fait vos préparatifs.', 38134),
(198188, 'frFR', '', 'Combattez avec honneur, et n\’oubliez pas votre entraînement. Lok\’tar ogar !', 38134),
(198187, 'frFR', '', 'Tremblez sous mon ombre !', 38134),
(198186, 'frFR', '', 'Mon sang… pour… le Maître…', 38134),
(198185, 'frFR', '', 'C\’en est fini de votre traîtrise.', 38134),
(198184, 'frFR', '', 'C\’est la gloire qui vous attend sur le champ de bataille, $p.', 38134),
(198183, 'frFR', 'C\’est le changement de costume ! Vite, en coulisse !', '', 38134),
(198180, 'frFR', '', 'Perfide vermine !', 38134),
(198179, 'frFR', '', 'Les profondeurs vous emportent !', 38134),
(198175, 'frFR', 'Ils sont trop nombreux !', 'Ils sont trop nombreux !', 38134),
(198173, 'frFR', '', 'C\’en est fini de votre rébellion !', 38134),
(198170, 'frFR', '', 'Ces chaînes permettent de contrôler les âmes récalcitrantes. Le fer les soumet à votre volonté.', 38134),
(198166, 'frFR', '', 'Frêleplume, occupe-toi du lys. Cette saleté refuse de s\’ouvrir !', 38134),
(198163, 'frFR', 'C\’est le chaos ! Où va-t-on si on commence à s\’entretuer ?', 'C\’est le chaos ! Où va-t-on si on commence à s\’entretuer ?', 38134),
(198156, 'frFR', 'Je peux réparer ! Suffit de demander.', 'Je peux réparer ! Suffit de demander.', 38134),
(198155, 'frFR', 'Bonjour ! Oui ? Réparer, c\’est ma spécialité.', 'Bonjour ! Oui ? Réparer, c\’est ma spécialité.', 38134),
(198142, 'frFR', 'D\’où tiennent-ils leur anima ?', 'D\’où tiennent-ils leur anima ?', 38134),
(198141, 'frFR', 'Les assemblages étaient nos alliés ! Pourquoi nous ont-ils trahis ?', 'Les assemblages étaient nos alliés ! Pourquoi nous ont-ils trahis ?', 38134),
(198137, 'frFR', 'La Dame noire nous a convoqués pour une importante mission. Nous n\’aurons pas droit à l\’échec.', '', 38134),
(198136, 'frFR', 'Leur armée s\’agrandit à chaque soldat que nous perdons.', 'Leur armée s\’agrandit à chaque soldat que nous perdons.', 38134),
(198135, 'frFR', 'Ils sont bien trop nombreux !', 'Ils sont bien trop nombreux !', 38134),
(198129, 'frFR', '', 'Merci, Aliothe. J\’apprécie vos conseils et votre amitié.', 38134),
(198127, 'frFR', '', 'Oui, mais avec votre aide nous les repousserons. J\’en ai la certitude.', 38134),
(198125, 'frFR', '', 'Mais Sylvarden aussi est menacée. Je vois bien des ombres qui rôdent à sa lisière pour s\’y frayer un chemin.', 38134),
(198124, 'frFR', '', 'Aujourd\’hui, c\’est cette forêt qui est mon monde. C\’est ainsi. Nous servons le cycle et la reine est là pour nous guider.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198121, 'frFR', '', 'C\’est le marteau de la Légion ardente qui a brisé mon monde. Il n\’en reste que des cendres.', 38134),
(198119, 'frFR', '', 'Vous aussi avez beaucoup perdu. Je l\’entends dans votre voix.', 38134),
(198118, 'frFR', '', 'Vous êtes liée à la reine et donc à Sylvarden, comme moi. Mais vous, vous rêvez encore de votre monde.', 38134),
(198116, 'frFR', '', 'Je ressens un lien puissant avec ce royaume. Et pourtant, dans mes rêves, c\’est toujours dans le ciel d\’Azeroth que je vole.', 38134),
(198115, 'frFR', 'Que voulez-vous, recrue ?', '', 38134),
(198112, 'frFR', '', 'Utilisez la lance pour l\’abattre !', 38134),
(198111, 'frFR', '', 'Brandissez ma lance, mortels !', 38134),
(198108, 'frFR', '', 'Je suis une Tirnenn, fille de la forêt et proche parente des arbres.\n\nQuand la reine de l\’hiver a tendu la main pour créer Sylvarden, nous avons été parmi les premiers à pousser. Des profondeurs de nos racines aux sommets de nos frondaisons, c\’est nous qui avons donné naissance aux bosquets.\n\nNous passons notre vie à servir, jusqu\’à ce que l\’insondable cours des saisons soit écoulé et que le cycle recommence.', 38134),
(198105, 'frFR', '', 'Je vais vous abattre !', 38134),
(198103, 'frFR', '', 'Qu\’est-ce que vous me voulez ? Vous ne voyez pas que je suis occupée ?', 38134),
(198099, 'frFR', '', 'Ma lance va vous transpercer le cœur !', 38134),
(198095, 'frFR', '', 'Voici la recrue dont je vous parlais, chef de guerre.', 38134),
(198094, 'frFR', 'Oui, ma reine. Nous ne vous décevrons pas.', '', 38134),
(198093, 'frFR', '', 'Je suis sûre que non.', 38134),
(198092, 'frFR', '', 'Nathanos, expliquez la mission à notre nouveau soldat.', 38134),
(198089, 'frFR', '', 'Pour gagner la guerre contre l\’Alliance, la Horde aura besoin d\’alliés puissants.', 38134),
(198088, 'frFR', '', 'On m\’a beaucoup vanté vos talents, $n. Je vais les mettre à contribution.', 38134),
(198087, 'frFR', '', 'Vous semblez avoir l\’œil pour le talent, seigneur de guerre Hache-Sinistre.', 38134),
(198085, 'frFR', '', 'Il faut que je me repose quelques instants… Je suis exténuée…', 38134),
(198076, 'frFR', '', 'Il y a encore des idiots qui voudraient nous ralentir. Ne les laissez pas faire.', 38134),
(198075, 'frFR', '', 'D\’autres gardes qui viennent vous déranger. Chargez-vous d\’eux.', 38134),
(198074, 'frFR', '', 'Il y a d\’autres gardes en route. Éliminez-les vite.', 38134),
(198072, 'frFR', 'Non ! Je refuse d\’être enfermé à nouveau !', '', 38134),
(198071, 'frFR', 'Par le pouvoir du soleil !', '', 38134),
(198070, 'frFR', 'Lève-toi de tes cendres !', '', 38134),
(198069, 'frFR', 'Les flammes vont vous emporter !', '', 38134),
(198068, 'frFR', 'Felomin Ashal !', '', 38134),
(198067, 'frFR', 'Que le feu vous consume !', '', 38134),
(198066, 'frFR', 'Tous subiront mon courroux !', '', 38134),
(198065, 'frFR', 'Vous pouvez forcément faire mieux que ça !', '', 38134),
(198062, 'frFR', '', 'Tirna Vaal fait partie des bosquets les mieux lotis. Il a une chance de survivre. Et la reine fera tout pour s\’en assurer.', 38134),
(198061, 'frFR', 'Mais… vous plaisantez…', '', 38134),
(198057, 'frFR', 'Assez ! J\’ai dit, ce monde brûlera !', '', 38134),
(198056, 'frFR', 'Cette torture est insupportable !', '', 38134),
(198055, 'frFR', 'Oui… mon pouvoir… Il revient !', '', 38134),
(198053, 'frFR', 'Je sens… que mes forces… me reviennent…', '', 38134),
(198052, 'frFR', 'Alors comme ça… mes bourreaux… viennent me sauver… ?', '', 38134),
(198051, 'frFR', '$UTrès cher:Très chère; $p, je suis ravi de constater que nous sommes enfin sur la même longueur d\’onde…\n\n<Théotar vous adresse un clin d\’œil.>\n\nIl faudra que l\’on reprenne le thé ensemble à l\’occasion.\n\nDame Sybille m\’a demandé de vos nouvelles.', '', 38134),
(198050, 'frFR', 'J\’ai hâte de passer du temps avec vous.', '', 38134),
(198049, 'frFR', 'Vos tourments ne font que commencer.', '', 38134),
(198048, 'frFR', 'Haut-Soleil… est à nous…', '', 38134),
(198047, 'frFR', 'Mais, ce sont les traîtres de Rénathal ! Gardes, saisissez-les !', '', 38134),
(198045, 'frFR', 'La colère est votre péché le plus fort, Haut-Soleil. Il nous sera bien utile.', '', 38134),
(198044, 'frFR', 'Attendez que je brise mes chaînes, vous serez le premier à brûler !', '', 38134),
(198043, 'frFR', '', 'C\’est encore moi qui vais devoir sauver tout le monde ?', 38134),
(198039, 'frFR', '', 'C\’est ici que Denathrius retient ses âmes les plus puissantes. Il y attise leurs pires instincts pour en faire des armes à déchaîner contre ses ennemis.', 38134),
(198033, 'frFR', '', 'Il vaut mieux rester proches. S\’ils réussissent à nous séparer, ils pourront nous submerger.', 38134),
(198031, 'frFR', '', 'Ne vous éloignez pas, $n. La portée de mon sort est limitée.', 38134),
(198025, 'frFR', '', 'Remontez la rivière, je vous couvre depuis la rive. Espérons qu\’elle nous mènera à la vraie Tyrande.', 38134),
(198018, 'frFR', 'Je vous amènerai au fort Élyséen dès que vous le souhaiterez.', '', 38134),
(198004, 'frFR', '', 'En Maldraxxus, il n\’y a rien de gratuit. Tout doit se mériter. Ces composants ne font pas exception.', 38134),
(197971, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nEt VOUS, très $ucher ami:chère amie;, vous me faites penser à un élégant parasol !\n\nJe ne saurai dire en quoi, très exactement… mais comme j\’adore cet accessoire, vous pouvez le prendre comme un compliment !', '', 38134),
(197970, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nCroyez-moi, vous ne pouviez pas être plus près de la vérité, Sans-Entraves.\n\nJ\’ai bien envie de vous faire honneur avec mon nouveau thé.\n\nJe pense l\’appeler « Thé brûlant |2 $p », qu\’en dites-vous ?', '', 38134),
(197969, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nJe suis entièrement de votre avis, Sans-Entraves.\n\nJ\’ai tenté de convaincre qui je pouvais à la cour de la comtesse, mais personne ne s\’est montré réceptif jusqu\’à présent.\n\nJ\’y parviendrai peut-être avec votre aide.', '', 38134),
(197968, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nJ\’imagine que votre dernier message s\’adressait à Tubbins.\n\nNe vous en faites pas, il lui a été transmis.\n\nJe vous souhaite le meilleur dans vos entreprises futures.\nThéotar', '', 38134),
(197967, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nVotre proposition m\’intrigue, $p.\n\nNous devrions en discuter à l\’occasion d\’une longue promenade à travers la Marcescence, sous mon parasol.\n\nJe m\’occupe du thé, apportez de quoi grignoter.', '', 38134),
(197966, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nJe pense que nous devrions nous asseoir devant une bonne tasse de thé… ou même plusieurs.\n\nC\’est qu\’il m\’arrive d\’avoir très soif, vous savez.', '', 38134),
(197965, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nJe dois vous avouer que je suis encore plus écarlate que le pagne de Denathrius après avoir lu votre petit mot !', '', 38134),
(197964, 'frFR', '<Théotar vous a répondu de sa plus belle écriture.>\n\nOh, que c\’est facétieux de votre part !\n\nJ\’ai été agréablement surpris de découvrir vos notes dans la marge.\n\nÀ ce propos, j\’en profite pour vous faire savoir que je suis du même avis que vous.', '', 38134),
(197957, 'frFR', '', 'Reposez-vous, ce combat est encore loin d\’être terminé.', 38134),
(197951, 'frFR', 'Je suis vraiment ravi de vous revoir, $p. Il faudra que l\’on reprenne le thé ensemble à l\’occasion.\n\nDame Sybille m\’a demandé de vos nouvelles.', '', 38134),
(197945, 'frFR', '<Ce téléporteur permet d\’accéder aux autres niveaux de la tour.>', '', 38134),
(197942, 'frFR', 'Oui, très bonne journée à vous aussi, cher marquis. Nous allons vous laisser.', '', 38134),
(197941, 'frFR', 'C\’est vrai qu\’il est joli, hein ? Merci de le faire remarquer, cher marquis.', '', 38134),
(197940, 'frFR', 'Nul besoin de flâner. Une bonne course fait une excellente promenade.', '', 38134),
(197917, 'frFR', 'Il paraît que les crocs du château sont de plus en plus, euh… bah, vous savez comment ils peuvent être. Faites gaffe. Ils sont dangereux quand ils sont, euh… comme ça.', '', 38134),
(197916, 'frFR', 'Il paraît que vous avez vu d\’autres royaumes. Y en a où y a des purotins aussi ?\n\nLes gens du coin sont sympas, mais après mille ans et des brouettes, j\’aimerais bien faire de nouvelles rencontres.', '', 38134),
(197915, 'frFR', 'Ici, les crocs sont pas aussi mauvais qu\’ailleurs. Sauf qu\’avec la pénurie, ils ont la fourberie facile.', '', 38134),
(197912, 'frFR', 'Hé ! On m\’a dit que vous avez tiré Rondel et Férul du marais des Affres en un seul morceau, même pas une jambe en moins, rien !\n\nVous serez toujours bien $uaccueilli:accueillie; ici, quoi qu\’en disent les crocs. Nous autres, les purotins, on sait remercier ceux qui prennent notre défense.', '', 38134),
(197911, 'frFR', 'Excusez, mais vous êtes quelqu\’un d\’important, vous ? Sur une échelle de bourbe-né à dieu, par exemple ?\n\nJ\’essaie juste de savoir s\’il faut que je vous parle compliqué.', '', 38134),
(197910, 'frFR', 'Oh, j\’ai entendu dire que vous étiez dans le coin. Ici, les rumeurs vont plus vite qu\’une âme damnée descend dans l\’Antre.', '', 38134),
(197909, 'frFR', 'Bienvenue à Sombrehavre, le pays des âmes jalouses et pitoyables.\n\nAttention à qui vous demandez de l\’aide, par ici.', '', 38134),
(197908, 'frFR', '', 'C\’est ce monde qui essaie de nous tromper. Continuons, j\’aperçois une autre traînée de cadavres un peu plus loin.', 38134),
(197907, 'frFR', 'Vous ne trouverez jamais la guerrière de la Nuit !', '', 38134),
(197906, 'frFR', '', 'Pour l\’éternité.', 38134),
(197905, 'frFR', '', 'Ah, pauvre enfant. Personne ne peut partir d\’ici. Mais toi, tu peux me rejoindre…', 38134),
(197903, 'frFR', '', 'Mes rêves m\’ont menée ici. Nous sommes là pour vous sauver.', 38134),
(197902, 'frFR', '', 'Shandris ? Qu\’est-ce que tu fais ici ?', 38134),
(197901, 'frFR', '', 'Minn\’do !', 38134),
(197879, 'frFR', '', 'Encore une proie traquée. J\’ai vu bien assez de ces… choses. Mes chasseurs pourront se charger du reste.', 38134),
(197877, 'frFR', '', 'Ces vermines sont encore plus répugnantes de près.', 38134),
(197870, 'frFR', 'Je vais vous disséquer !', '', 38134),
(197869, 'frFR', 'Oh, splendide, encore des cobayes ! Lâchez les ronge-peste !', '', 38134),
(197855, 'frFR', 'Pouvons-nous commencer la cérémonie ?', '', 38134),
(197854, 'frFR', '', 'Vous vous rendez bien compte que je suis une épée volante, hein ? Je n\’ai aucun mal à passer entre ces immenses barreaux.', 38134),
(197852, 'frFR', '', 'Oh, mais regardez-moi ces fournitures ! Voilà qui suffirait à faire des tas d\’âmetoiles.$b$bEn échange, je pourrais vous proposer de quoi faire face aux dangers de la forêt en vous permettant de surprendre l\’ennemi ou de quitter un combat.$b$bAlors, l\’affaire est entendue ?', 38134),
(197850, 'frFR', 'Ça fait plaisir de vous voir, d\’autant plus que je suis presque à court de gorms à forger, si vous voyez ce que je veux dire…$b$BDes fournitures, vous dites ? Ah, je ne dis pas non. Et en échange, je peux probablement vous confectionner de quoi disposer de renforts temporaires. Alors, marché conclu ?', '', 38134),
(197848, 'frFR', '', 'Des fournitures ? Pour moi ? Mais la dame…$b$bÉcoutez, j\’accepte volontiers votre don. J\’ai nos peintures de guerre avec moi, je vous les remettrai en échange. Elles vous conféreront la force et la rapidité des chasseurs.', 38134),
(197846, 'frFR', '', 'Ah, les fournitures des Florès ! Comme la région est sous ma supervision, il est sage de votre part de venir les confier à mes bons soins.$b$bRemettez-les-moi, et je vous offrirai de quoi vous remonter le moral et retrouver de l\’allant.', 38134),
(197843, 'frFR', 'Venez, faisons un tour à la Queue de Wyverne. Vous pourrez me payer un verre !', '', 38134),
(197839, 'frFR', 'On va aller voir un maître pour vous apprendre à vous spécialiser !', '', 38134),
(197834, 'frFR', '', 'J\’entends réparer les injustices de cette éternité.', 38134),
(197833, 'frFR', 'Voilà qui devrait vous apprendre un peu les bonnes manières !', '', 38134),
(197832, 'frFR', 'Espérons-le, par la grâce de la reine.', 'Espérons-le, par la grâce de la reine.', 38134),
(197831, 'frFR', 'Ça alors… une nouvelle âme ? Elles reviennent enfin en Sylvarden ?', 'Ça alors… une nouvelle âme ? Elles reviennent enfin en Sylvarden ?', 38134),
(197830, 'frFR', '', 'Le flanc ouest est aux abois, Mograine a besoin de renforts !', 38134),
(197829, 'frFR', '', 'Cet endroit bride mes capacités de téléportation, mais je devrais pouvoir ouvrir un portail menant à mon bâton.', 38134),
(197827, 'frFR', '', 'J\’ai commis une erreur. On ne m\’y reprendra plus.', 38134),
(197826, 'frFR', '', 'Je suis blessée… Laissez-moi un petit instant !', 38134),
(197820, 'frFR', '', 'Luttez… avec… courage…', 38134),
(197819, 'frFR', '', 'Douleur… agonie !', 38134),
(197817, 'frFR', '', 'C\’est l\’œuvre de Tyrande. Je l\’ai vue pourfendre ces monstres dans mes rêves.\n\nOn dirait qu\’elle a laissé un sillage de cadavres derrière elle. Ils sont encore frais… Elle était ici il y a peu de temps !', 38134),
(197815, 'frFR', '', 'J\’aperçois l\’endroit que j\’ai vu en rêve un peu plus loin. Venez… Voyons quelles réponses nous y trouverons.', 38134),
(197814, 'frFR', 'Eh bien, il semblerait que le grand chambellan ait un contrat avec moi, sans date d\’expiration.\n\nJe vais pouvoir conduire cette calèche aussi longtemps que je le souhaite, tant qu\’aucune créature avec des crocs ou des ailes ne me demande d\’arrêter. Ouvrez l\’œil, des fois que vous me croisiez.\n\nVous avez rendu un grand service aux purotins en me tirant de la gueule de ce dévoreur. Quoi qu\’il arrive, vous pourrez compter sur nous.', '', 38134),
(197812, 'frFR', '', 'Tout est en place. En route.', 38134),
(197811, 'frFR', '', 'Mon bâton me servira de repère pour nous faire revenir ici une fois qu\’on aura retrouvé Anduin.', 38134),
(197810, 'frFR', '', 'Je sens la peur de la félonne. Elle est au dépôt Décrépit.', 38134),
(197809, 'frFR', 'Nos derniers soldats vont monter la garde ici. Quand vous aurez trouvé cette fameuse pierre, prévenez-nous.', '', 38134),
(197808, 'frFR', 'On ne peut pas abandonner ces âmes innocentes à leur sort.', '', 38134),
(197807, 'frFR', 'Faites bien attention à vous, hein ? Et quand vous verrez le chef de guerre, parlez-lui de moi !', '', 38134),
(197806, 'frFR', 'Je savais que vous alliez vous faire un nom, $n.', '', 38134),
(197805, 'frFR', '', 'Le chef de guerre vous attend au fort Grommash.', 38134),
(197804, 'frFR', '', 'Le chef de guerre demande à vous voir au fort Grommash. Mieux vaut ne pas la faire attendre…', 38134),
(197803, 'frFR', '', '$n, j\’ai une communication du chef de guerre !', 38134),
(197798, 'frFR', '', '$n, quel soulagement de vous voir dans cet endroit étrange. Je vous en prie, aidez-moi. Pour Tyrande.', 38134),
(197797, 'frFR', '', 'Très bien. Je vais mettre mes blessures de côté… pour l\’instant. Mais je ne les oublierai jamais, sachez-le.', 38134),
(197796, 'frFR', 'Shandris, je sais quelles cicatrices nous laisse la guerre. Mais cette âme sans-entraves pourrait être le seul espoir de sauver Tyrande.', '', 38134),
(197795, 'frFR', '', 'Je refuse de mettre une vie qui m\’est si chère entre les mains de quelqu\’un de la Horde.', 38134),
(197794, 'frFR', '', 'Rien ni personne n\’a jamais réussi à sortir de l\’Antre, jusqu\’à ce que certaines âmes mortelles en reviennent. Comme celle-ci.', 38134),
(197789, 'frFR', 'Prenez votre temps. J\’ai assez de boulot pour m\’occuper un bon moment.', '', 38134),
(197788, 'frFR', 'Les autres purotins sont négligents… Ils laissent leurs outils traîner n\’importe où.', '', 38134),
(197787, 'frFR', 'Mais je vous filerai rien d\’autre. Pour la suite, débrouillez-vous !', '', 38134),
(197786, 'frFR', 'Pour commencer, je vous donne mon marteau de rechange.', '', 38134),
(197782, 'frFR', 'Interdit déranger maître !', '', 38134),
(197781, 'frFR', '', 'La chasse nous appelle, $n.', 38134),
(197779, 'frFR', '', 'Il faut que vous protégiez l\’avenir d\’Azeroth, $n !', 38134),
(197777, 'frFR', '', 'Je cherchais à emmener ces âmes en un lieu où elles pourraient faire don de leur anima, mais tout est allé de travers !\n\nLa faune de Sylvarden n\’était pas aussi agressive avant la pénurie. Je dois vite les conduire en lieu sûr, dans le vallon aux Esprits.\n\nAidez-moi, je vous prie !', 38134),
(197776, 'frFR', '', 'Te\’zan ! Vous nous amenez des renforts !', 38134),
(197775, 'frFR', 'Ah, là ! Sur votre tête, y a plus de danger.', '', 38134),
(197772, 'frFR', 'Quelle drôle de créature vous faites…', '', 38134),
(197770, 'frFR', '', 'À vous. Utilisez le croc pour annoncer que la chasse est ouverte.', 38134),
(197768, 'frFR', '', 'Drainez l\’anima du maître-chien Loksey, Sans-Entraves.', 38134),
(197766, 'frFR', 'Cette négociation est caduque. La transaction s\’achève ici pour l\’instant.', '', 38134),
(197760, 'frFR', 'Super choix, $n !', '', 38134),
(197758, 'frFR', '', 'Je peux nous rendre invisibles à nouveau. Donnez-moi une seconde !', 38134),
(197754, 'frFR', 'Jaina, est-ce que vous pouvez nous dissimuler ?', '', 38134),
(197752, 'frFR', '', 'La pierre répond à votre présence, $n ! Essayez de l\’activer pendant qu\’on les tient à distance !', 38134),
(197748, 'frFR', 'C\’est la mort qui approche pour l\’âme de votre monde ! Tout ce que vous connaissez, tout sera annihilé !', '', 38134),
(197747, 'frFR', '', 'Elle est presque chargée, courage !', 38134),
(197746, 'frFR', 'Les légions n\’ont jamais réussi à nous battre. Celle-là ne fera pas exception !', '', 38134),
(197745, 'frFR', 'Mon armée est légion ! Elle n\’a pas de fin !', '', 38134),
(197744, 'frFR', '', 'Tenez bon, il faut qu\’elle finisse de se charger !', 38134),
(197743, 'frFR', 'Antreliges, appréhendez-les !', '', 38134),
(197742, 'frFR', 'Une relique des Fondateurs réagirait à une créature mortelle ?! C\’est impossible !', '', 38134),
(197740, 'frFR', '', 'Mon sort peut nous permettre de passer, mais certains gardes pourront le percer à jour.', 38134),
(197739, 'frFR', 'Son armée semble sans fin. Nous ne pourrons jamais tous les vaincre.', '', 38134),
(197738, 'frFR', 'Venez ! Cette barrière nous protégera !', '', 38134),
(197736, 'frFR', 'Si j\’avais su qu\’baver sur un croc auprès d\’un de ses petits copains me ferait renvoyer au bourbier, j\’aurais fermé ma grande bouche. Z\’auriez des rubis pour rallumer ma torche ?', '', 38134),
(197734, 'frFR', '', 'Vos fautes condamnent vos alliés.', 38134),
(197733, 'frFR', '', 'Vous mourrez ensemble !', 38134),
(197732, 'frFR', '', 'Vos destins sont liés !', 38134),
(197731, 'frFR', '', 'Il n\’y aura pas de rédemption pour vous !', 38134),
(197730, 'frFR', '', 'Votre vice sera une torture éternelle.', 38134),
(197729, 'frFR', '', 'J\’aurai plaisir à vous arracher vos fautes.', 38134),
(197728, 'frFR', '', 'Catastrophe !', 38134),
(197727, 'frFR', '', 'Le sol se fracasse !', 38134),
(197726, 'frFR', '', 'La destruction est reine !', 38134),
(197725, 'frFR', '', 'Le cœur saisi par les ténèbres !', 38134),
(197724, 'frFR', '', 'Le vice vous brûle le cœur !', 38134),
(197723, 'frFR', '', 'Je vais vous broyer le cœur !', 38134),
(197722, 'frFR', '', 'Ce doit être une volonté du Dessein. C\’est lui qui nous guide.', 38134),
(197721, 'frFR', '', 'Cette créature empeste l\’Antre !', 38134),
(197720, 'frFR', '', 'Cet afflux d\’anima annonce votre perte !', 38134),
(197718, 'frFR', '', 'Merci, Sans-Entraves.', 38134),
(197717, 'frFR', '', 'De l\’Antre ? Impossible !', 38134),
(197716, 'frFR', '', 'Tout va si vite ! Mon bosquet est à l\’agonie !', 38134),
(197710, 'frFR', '', 'Le Dessein mettrait-il notre foi à l\’épreuve ?', 38134),
(197709, 'frFR', '', 'Une intrusion ? Comment est-ce possible ?', 38134),
(197708, 'frFR', '', 'Je refuse de croire que quiconque ait pu réchapper de l\’Antre !', 38134),
(197705, 'frFR', 'La maison des Rituels mène une cérémonie de renforcement. Si vous ne pouvez pas l\’interrompre, anéantissez ce qui en sortira.', '', 38134),
(197704, 'frFR', 'Malgré leur vile sorcellerie, les os des ritualistes se brisent de la même manière que ceux des autres. Beau travail.', '', 38134),
(197701, 'frFR', 'Mais comment m\’assurer que tout est authentique ?', '', 38134),
(197695, 'frFR', '', 'Oranomonos a perdu la raison à cause des gorms qui la dévorent peu à peu. Mettez fin à ses souffrances, la pauvre.', 38134),
(197694, 'frFR', '', 'Les gorms n\’auront pas leur festin aujourd\’hui. Adieu, mon amie. Vous aurez défendu Sylvarden de toute votre âme.', 38134),
(197691, 'frFR', 'Collectez la dîme auprès des villageois et écumez le secteur en quête de la moindre once qui nous aurait échappé.', 'Collectez la dîme auprès des villageois et écumez le secteur en quête de la moindre once qui nous aurait échappé.', 38134),
(197689, 'frFR', 'À quoi servent tous ces rituels si nous sommes incapables d\’arracher nos protégés aux griffes de l\’Antre ?', 'À quoi servent tous ces rituels si nous sommes incapables d\’arracher nos protégés aux griffes de l\’Antre ?', 38134),
(197688, 'frFR', 'Aidez-nous d\’abord à accomplir le rituel d\’expiation. Ensuite, nous pourrons discuter.', 'Aidez-nous d\’abord à accomplir le rituel d\’expiation. Ensuite, nous pourrons discuter.', 38134),
(197687, 'frFR', 'Je ne m\’attendais pas à ce qu\’ils s\’entretuent, mais nous faisons le nécessaire dans l\’intérêt de tous. Denathrius doit disparaître. Adieu.', 'Je ne m\’attendais pas à ce qu\’ils s\’entretuent, mais nous faisons le nécessaire dans l\’intérêt de tous. Denathrius doit disparaître. Adieu.', 38134),
(197669, 'frFR', '', 'Je crois que je sais où trouver un autre miroir des âmes. Dirigez-vous vers la grotte au nord, je vous retrouverai là-bas.', 38134),
(197668, 'frFR', '', 'Tant que votre détermination demeure, la vraie défaite n\’existe pas.', 38134),
(197665, 'frFR', '', 'Ce n\’est pas encore votre heure, enfant d\’Azeroth.', 38134),
(197664, 'frFR', '', 'De la défaite naît la sagesse.', 38134),
(197663, 'frFR', '', 'Même les meilleurs d\’entre nous sont parfois vaincus.', 38134),
(197662, 'frFR', '', 'Vos alliés vous sauveront la prochaine fois, j\’en suis sûre.', 38134),
(197661, 'frFR', '', 'Vous ferez mieux la prochaine fois.', 38134),
(197660, 'frFR', '', 'Personne n\’aurait pu le prévoir.', 38134),
(197659, 'frFR', 'Je savais que j\’aurais dû prendre ma tenue protocolaire.', '', 38134),
(197658, 'frFR', 'L\’heure est à la détente ! Les traditions, c\’est fait pour être bousculé.', '', 38134),
(197657, 'frFR', 'Ha, mais quelle effervescence !', '', 38134),
(197656, 'frFR', 'Relaxez-vous, tout le monde a le droit de souffler un peu.', '', 38134),
(197654, 'frFR', 'Des fois, il faut savoir faire simple.', '', 38134),
(197651, 'frFR', 'Voilà, on peut s\’amuser très proprement !', '', 38134),
(197650, 'frFR', 'Attention où vous mettez les pieds ! Le ménage a été un peu négligé.', '', 38134),
(197635, 'frFR', 'Une victoire décisive. Retrouvons-nous devant Combeffroi, $n.', '', 38134),
(197632, 'frFR', 'Le résultat me paraît magnifique ! Faites-en bon usage pour protéger Sylvarden.', '', 38134),
(197631, 'frFR', 'Nos artisans confectionnent toutes sortes de merveilles. Il leur faut juste quelqu\’un pour leur apporter des fournitures. Quelqu\’un comme vous !', '', 38134),
(197630, 'frFR', 'À vos ordres !', '', 38134),
(197629, 'frFR', '', 'Ho, soldat ! Allez dire au baron que s\’il veut sa garde d\’os, il va falloir qu\’il lâche de l\’anima !', 38134),
(197627, 'frFR', '', 'Pouvons-nous y aller ?', 38134),
(197624, 'frFR', '', 'Encore des ordres ? Qu\’est-ce qu\’ils veulent, encore ?', 38134),
(197623, 'frFR', '', 'Je préfère me déplacer à dos de loup redoutable plutôt que dans sa panse.', 38134),
(197622, 'frFR', '', 'Elle essaie de siphonner l\’anima ! Vite, $p, la fiole !', 38134),
(197621, 'frFR', '', 'Soldats ! Le perdant sera de corvée de minerai pour la forge ! Allez !', 38134),
(197620, 'frFR', 'Il reste si peu d\’anima et j\’ai dû faire mes adieux à tant d\’esprits en sommeil, tant d\’amis dont j\’ai pris soin des siècles durant.\n\nMais l\’ancienne Dunmira devrait bientôt se réveiller, je ne pouvais pas partir comme ça !\n\nAlors, j\’ai déplacé sa graine à un endroit plus riche en anima, pour l\’aider à tenir le coup. Et c\’est là que cet oiseau de malheur l\’a enlevée ! J\’ai l\’impression qu\’il essaie de la couver comme un œuf.', '', 38134),
(197614, 'frFR', '', 'Malgré votre plan si bien élaboré, je suis encore là. Finissons-en.', 38134),
(197611, 'frFR', 'Les ordres ont changé ! Direction l\’ouest ! En avant, marche !', '', 38134),
(197603, 'frFR', 'L\’heure est venue. Soldats, tenez vos positions !', '', 38134),
(197602, 'frFR', 'La collection privée de sire Denathrius. Des reliques de toute l\’Ombreterre rassemblées au long des millénaires.', '', 38134),
(197601, 'frFR', 'Voilà qui est intéressant. L\’agitation a éloigné les gardes d\’ici.', '', 38134),
(197598, 'frFR', 'Des armées infinies sont à mes ordres !', '', 38134),
(197597, 'frFR', 'Un pouvoir qui ébranle la réalité !', '', 38134),
(197595, 'frFR', 'Celle-ci est d\’une puissance à faire trembler les dieux !', '', 38134),
(197594, 'frFR', '', 'Cette plaisanterie commence à me fatiguer. Montrez-vous et je vous accorderai une mort rapide.', 38134),
(197593, 'frFR', 'L\’énergie arcanique court dans mes veines !', '', 38134),
(197590, 'frFR', 'Vous mourrez ensemble !', '', 38134),
(197589, 'frFR', 'Attention au transfert de matière !', '', 38134),
(197571, 'frFR', 'Pfouh, ça va faire une trotte. Ne me perdez pas de vue, hein.', '', 38134),
(197570, 'frFR', 'Elles ont envahi le sépulcre du Savoir.', '', 38134),
(197569, 'frFR', 'Ces créatures débarquent par ce tunnel.', '', 38134),
(197564, 'frFR', 'Attention où vous mettez les pieds !', '', 38134),
(197563, 'frFR', 'Personne ne peut négocier avec la mort !', '', 38134),
(197562, 'frFR', 'La destruction est inévitable !', '', 38134),
(197559, 'frFR', 'C\’est si bon d\’être de retour à Orgrimmar !', '', 38134),
(197552, 'frFR', '', 'Oh, ne soyez pas aussi barbant. Si vous êtes sage, je vous laisserais embrocher un serviteur ou deux.', 38134),
(197551, 'frFR', 'Je ne partage pas votre goût pour ces frivolités. Je préfère les rassemblements sur le champ de bataille.', '', 38134),
(197550, 'frFR', '', 'Mais avec plaisir, très cher. Tant que le vin coule à flot, vous aurez mon attention.', 38134),
(197549, 'frFR', 'Merci à vous deux pour votre présence. Le bal n\'aurait pas été aussi réussi sans vous.', '', 38134),
(197548, 'frFR', 'C\’est n\’importe quoi ! Vous avez suffisamment fait honte à cette noble assemblée !', '', 38134),
(197547, 'frFR', 'Vos pas étaient absolument sublimes ! Dommage que vous deviez mourir.', '', 38134),
(197545, 'frFR', 'On se trémousse vers la droite maintenant !', '', 38134),
(197544, 'frFR', 'Déhanché à droite !', '', 38134),
(197543, 'frFR', 'Déhanché à gauche !', '', 38134),
(197542, 'frFR', 'Déhanché à gauche !', '', 38134),
(197541, 'frFR', 'Faites un entrechat !', '', 38134),
(197540, 'frFR', 'Faites un entrechat !', '', 38134),
(197539, 'frFR', 'En avant le boogie !', '', 38134),
(197538, 'frFR', 'En avant le boogie !', '', 38134),
(197537, 'frFR', '', 'En avant le boogie !', 38134),
(197536, 'frFR', '', 'On se trémousse à droite !', 38134),
(197535, 'frFR', '', 'Déhanché à gauche !', 38134),
(197534, 'frFR', '', 'Faites un entrechat !', 38134),
(197533, 'frFR', 'Tout le monde en position ! Il est l\’heure de la danse macabre !', '', 38134),
(197532, 'frFR', 'Votre attention ! En formation pour la danse macabre !', '', 38134),
(197531, 'frFR', '', 'Chers invités et vils intrus, prenez place pour la danse macabre !', 38134),
(197530, 'frFR', '', 'Je crois que ce n\’est pas à notre présence qu\’elle répond, mais à la vôtre, $n.', 38134),
(197529, 'frFR', 'Mais… je ne trouve pas comment l\’activer.', '', 38134),
(197528, 'frFR', 'La pierre était censée être en sommeil, mais elle réagit à notre présence !', '', 38134),
(197525, 'frFR', 'Nos éclaireurs ont repéré la Traquepeur sur la route de Combeffroi. Préparez-vous vite.', '', 38134),
(197521, 'frFR', '', 'En tuant mes brisespoirs, vous révélez votre position. Votre fin est proche.', 38134),
(197518, 'frFR', 'Le temps, c\’est de l\’argent, $n. En route !', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197516, 'frFR', '', 'Nous vous retrouverons au manoir au fond du village.', 38134),
(197515, 'frFR', 'Votre mission est d\’une importance décisive. Si vous voulons réussir, le piège doit être parfait.', '', 38134),
(197508, 'frFR', 'J\’étais censé prendre soin d\’eux… mais on me les a enlevés !', '', 38134),
(197507, 'frFR', 'Frêleplume ! Par ici !', '', 38134),
(197505, 'frFR', '', 'Mon sommeil… s\’achève…', 38134),
(197500, 'frFR', '', 'Vous me serez toujours… $uinférieur:inférieure;.', 38134),
(197493, 'frFR', '', 'Vous n\’étiez pas sur la liste des invités !', 38134),
(197489, 'frFR', '', 'Suivez la voie.', 38134),
(197487, 'frFR', 'La dernière chanson vient de commencer ! Que tout le monde danse jusqu\’à la mort !', '', 38134),
(197485, 'frFR', 'Que tout le monde rejoigne son partenaire ! Ou vous mourrez !', '', 38134),
(197483, 'frFR', 'Tout le monde sur la piste de danse !', '', 38134),
(197482, 'frFR', 'Venez, mes amis ! Dansez jusqu\’à vous vider de votre sang !', '', 38134),
(197476, 'frFR', 'Mais je suis… le roi… de la fête…', '', 38134),
(197472, 'frFR', 'Comment osez-vous participer à ma fête dans une tenue aussi ringarde !', '', 38134),
(197465, 'frFR', '', 'Gardez un œil sur les cieux. Les volions passent le plus clair de leur temps dans les airs !', 38134),
(197464, 'frFR', 'Forcément, la tentation est grande face à une telle quantité d\’anima. Adieu !', 'Forcément, la tentation est grande face à une telle quantité d\’anima. Adieu !', 38134),
(197463, 'frFR', 'Il est fâcheux que ma note sur la dîme ait été découverte, mais il me suffit de glisser un mot à droite à gauche et ils s\’accuseront les uns les autres.', 'Il est fâcheux que ma note sur la dîme ait été découverte, mais il me suffit de glisser un mot à droite à gauche et ils s\’accuseront les uns les autres.', 38134),
(197462, 'frFR', 'Oh, quel bazar !', 'Oh, quel bazar !', 38134),
(197460, 'frFR', '', 'Je dois faire mon rapport au chef de guerre. Cork va vous faire visiter la ville.', 38134),
(197459, 'frFR', 'Quelle… stratégie… est-ce là…', '', 38134),
(197454, 'frFR', 'Splendide ! Un combat pour la victoire !', '', 38134),
(197453, 'frFR', '', 'Sire… j\’ai échoué…', 38134),
(197452, 'frFR', '', 'Cette ridicule rébellion est terminée.', 38134),
(197451, 'frFR', '', 'Votre anima est à moi !', 38134),
(197450, 'frFR', '', 'Vermine corrompue.', 38134),
(197449, 'frFR', '', 'Je vais lacérer vos âmes !', 38134),
(197448, 'frFR', 'C\’est impossible ! Je suis… éternel…', '', 38134),
(197447, 'frFR', 'Cette ridicule rébellion est terminée.', '', 38134),
(197446, 'frFR', 'Nul ne peut défier mon autorité.', '', 38134),
(197445, 'frFR', 'Voilà pour votre impertinence !', '', 38134),
(197443, 'frFR', 'Votre orgueil sera votre perte !', '', 38134),
(197439, 'frFR', '', 'Notre seule chance est de les tenir en respect assez longtemps pour que la pierre se charge. Chaque seconde sera cruciale !', 38134),
(197438, 'frFR', '', 'Quand le Geôlier comprendra ce qu\’on essaye de faire, il va nous envoyer toutes ses troupes disponibles.', 38134),
(197434, 'frFR', '', 'J\’étais l\’une des meilleures… Pardon, Maître.', 38134),
(197432, 'frFR', '', 'Quelle erreur pour la proie de se livrer au prédateur !', 38134),
(197425, 'frFR', '', 'Il faut l\’atteindre avant que le Geôlier ne nous remarque.', 38134),
(197424, 'frFR', '', 'Regardez, sur cette plateforme, là-bas ! Ce doit être la pierre d\’accès !', 38134),
(197423, 'frFR', 'C\’est une fin plus clémente que celle qui vous attend…', '', 38134),
(197422, 'frFR', 'Ha ha ! À moi le premier sang !', '', 38134),
(197421, 'frFR', '', 'Pour Sylvarden !', 38134),
(197420, 'frFR', '', 'Pour Sylvarden !', 38134),
(197419, 'frFR', '', 'Pour Sylvarden !', 38134),
(197418, 'frFR', 'Pour Sylvarden !', '', 38134),
(197417, 'frFR', 'À vos ordres, votre majesté.', '', 38134),
(197416, 'frFR', 'Elle va vous anéantir…', '', 38134),
(197415, 'frFR', 'Ha, pile dessus ! Votre tête me sera précieuse.', '', 38134),
(197414, 'frFR', '', 'Les Drust pensent que s\’emparer du bosquet de l\’Éveil leur offrira la victoire. Ils se trompent.', 38134),
(197412, 'frFR', 'Rah. Pas la moindre détection.', '', 38134),
(197410, 'frFR', '', 'Est-ce que tu as vu l\’âme sans-entraves ? Parle ou je te vide de ton sang !', 38134),
(197409, 'frFR', '', 'Baie-de-Lune, Herne, Aliothe et Zayhad ! À vous de reprendre les fondations du bosquet. Et par le lien qui nous unit, je chasserai les envahisseurs !', 38134),
(197408, 'frFR', 'Qu\’est-ce que tu perçois, Souffrance ? L\’âme sans-entraves ne doit pas être loin.', '', 38134),
(197405, 'frFR', 'Vous êtes une insulte à tout ce que représente ce royaume. Disparaissez !', 'Vous êtes une insulte à tout ce que représente ce royaume. Disparaissez !', 38134),
(197404, 'frFR', 'Les négociants font vraiment affaire avec n\’importe qui…', 'Les négociants font vraiment affaire avec n\’importe qui…', 38134),
(197403, 'frFR', 'Ne m\’adressez pas la parole, $umortel:mortelle; !', 'Ne m\’adressez pas la parole, $umortel:mortelle; !', 38134),
(197402, 'frFR', '', 'Je dois rester là pour panser mes blessures. Allez aider les défenseurs des flèches pour moi !', 38134),
(197394, 'frFR', 'Merveilleux !', '', 38134),
(197393, 'frFR', 'Mourir, telle est votre seule utilité !', '', 38134),
(197392, 'frFR', 'Enfin ! J\’avais hâte de tester ces reliques sur des cibles vivantes.', '', 38134),
(197391, 'frFR', 'Désormais… c\’est vous… l\’alpha…', '', 38134),
(197390, 'frFR', 'Encore une chasse réussie.', '', 38134),
(197389, 'frFR', 'Quelques os à ronger pour mes bêtes.', '', 38134),
(197388, 'frFR', 'Mes chiens vont bien manger aujourd\’hui.', '', 38134),
(197387, 'frFR', 'Que la chasse commence !', '', 38134),
(197385, 'frFR', '', 'Non, je… Si, bien sûr.', 38134),
(197384, 'frFR', 'Kléia, tu es trop mal en point pour te battre. Je vais te soigner.', '', 38134),
(197379, 'frFR', 'Pierre qui écrase !', '', 38134),
(197377, 'frFR', 'C\’est pas juste !', '', 38134),
(197376, 'frFR', 'Pas bouger !', '', 38134),
(197375, 'frFR', 'Pierre est plus dure que ma tête.', '', 38134),
(197374, 'frFR', 'Aïeuh !', '', 38134),
(197373, 'frFR', 'Je veux cogner des crânes !', '', 38134),
(197371, 'frFR', 'Je te broie ta face !', '', 38134),
(197370, 'frFR', 'Moi, j\’écrase !', '', 38134),
(197369, 'frFR', 'Et boum !', '', 38134),
(197368, 'frFR', 'Maître… va être… vraiment fâché…', '', 38134),
(197366, 'frFR', 'Hi hi, ça fait bruit marrant quand je marche dessus.', '', 38134),
(197365, 'frFR', 'Et voilà ! $uBroyé:Broyée; !', '', 38134),
(197364, 'frFR', 'Maître va être vraiment content !', '', 38134),
(197363, 'frFR', 'Je vais tout broyer tes os !', '', 38134),
(197362, 'frFR', '', 'Cette pénurie menace la forêt entière. Votre voix est pareille à un oiseau qui chanterait seul dans la nuit.', 38134),
(197358, 'frFR', 'Qui qui fait gros vacarme ?!', '', 38134),
(197357, 'frFR', '', 'Au final, vous m\’avez même aidée à réaliser mes rêves. Rejoignez les transcendés, c\’est un honneur de lier mon âme à la vôtre.', 38134),
(197356, 'frFR', 'Votre retour au Bastion nous a emplis de joie. Vous êtes $udevenu:devenue; l\’$uami:amie; sur qui on compte tous les deux !', '', 38134),
(197355, 'frFR', '', 'Vous avez vite montré que vous étiez bien plus. Une épaule solide sur laquelle me reposer… en amie.', 38134),
(197353, 'frFR', '', 'Je commande au pouvoir du Bastion !', 38134),
(197352, 'frFR', '', 'Je suis porteuse de la lance de l\’Archonte !', 38134),
(197349, 'frFR', 'Vivement que nous remettions le couvert… dès que nous serons remis de nos émotions.', '', 38134),
(197347, 'frFR', 'Que d\’émotions… je suis épuisé !', '', 38134),
(197346, 'frFR', 'Hardi, mes amis ! Nous avons une fête à organiser !', '', 38134),
(197345, 'frFR', 'Je veillerai à ce que tout soit parfait pendant que vous peaufinez les derniers détails !', '', 38134),
(197344, 'frFR', 'Pardon ? De toute évidence, il y a erreur sur la personne. Que tout le monde se calme et baissez ces arbalètes, ou je tire à vue.', 'Pardon ? De toute évidence, il y a erreur sur la personne. Que tout le monde se calme et baissez ces arbalètes, ou je tire à vue.', 38134),
(197343, 'frFR', 'Du flair, toi ? On aura tout entendu. D\’après mes sources, Samo aurait allègrement pioché dans la dîme d\’anima. En réalité, c\’est lui qui nous a trahis.', 'Du flair, toi ? On aura tout entendu. D\’après mes sources, Samo aurait allègrement pioché dans la dîme d\’anima. En réalité, c\’est lui qui nous a trahis.', 38134),
(197342, 'frFR', 'Son message codé à propos de la dîme, pour commencer. Mais quelle importance ? Nul besoin de preuve quand on a du flair.', 'Son message codé à propos de la dîme, pour commencer. Mais quelle importance ? Nul besoin de preuve quand on a du flair.', 38134),
(197341, 'frFR', 'Pas si vite, Lajos. Cesse donc de pointer cette arbalète sur Ilka. De quelles preuves disposes-tu ?', 'Pas si vite, Lajos. Cesse donc de pointer cette arbalète sur Ilka. De quelles preuves disposes-tu ?', 38134),
(197340, 'frFR', '', 'On va transférer les cocons dans les bosquets qui en ont besoin. Merci d\’avoir rétabli l\’équilibre.', 38134),
(197339, 'frFR', '', 'Les cocons de Lumefronde échappent à tout contrôle. Vous pouvez en rapporter du haut des arbres ?', 38134),
(197334, 'frFR', 'Ha ha ha ! Je ne vois pas du tout de quoi tu parles, Lajos. De toute évidence, il y a erreur.', 'Ha ha ha ! Je ne vois pas du tout de quoi tu parles, Lajos. De toute évidence, il y a erreur.', 38134),
(197320, 'frFR', '', 'Le Chaudron de Zovaal est lourdement gardé et occupé par les forces du Geôlier. Nous ferions mieux de passer sans nous faire remarquer.\n\nVous n\’avez qu\’un mot à dire, et je nous dissimulerai à leurs yeux.', 38134),
(197314, 'frFR', '', 'Les désavoués battent en retraite… Du fort, tout au moins. Revenez, nous devons nous rassembler.', 38134),
(197308, 'frFR', 'Hou ! Que faire, mais que faire ?', 'Hou ! Que faire, mais que faire ?', 38134),
(197303, 'frFR', '', 'Toujours.', 38134),
(197298, 'frFR', 'Je me chargerai de cette tâche pendant que vous finirez votre mission. Prends bien soin de toi, Kléia.', '', 38134),
(197297, 'frFR', '', 'Les désavoués risquent d\’attaquer à nouveau. Nous devons avertir le Repos du héros.', 38134),
(197295, 'frFR', '', 'Mes disciples ont encore du travail ici, et je dois prévenir l\’Archonte. À vous de servir, et sans délai !', 38134),
(197290, 'frFR', '', 'Si la chance nous sourit, les souvenirs de Lysonia nous mèneront aux réponses que nous cherchons.', 38134),
(197288, 'frFR', '', 'Apportez ce fragment à l\’enclave Mnémonique où nous recueillons les souvenirs des Kyrians.', 38134),
(197287, 'frFR', '', 'Je vais informer l\’Archonte de ce qui s\’est passé ici… et venger la perte de tous nos amis.', 38134),
(197286, 'frFR', '', 'L\’essence de Lysonia a été corrompue par l\’Antre. Nous devons découvrir comment une telle chose a pu se produire.', 38134),
(197274, 'frFR', 'Les flèches de l\’Ascension appartiendront bientôt aux désavoués. Déva sauvera l\’Ombreterre de la pénurie et nous offrira une nouvelle ère !', '', 38134),
(197273, 'frFR', 'Un jour, j\’ai péché un poisson d\’une espèce inconnue. Alors, je lui ai donné mon nom.\n\nEt comme je suis le seul à l\’avoir vu, personne n\’a voulu me croire.', '', 38134),
(197272, 'frFR', '', 'Voilà qui change tout. Draka peut espérer un semblant de victoire.', 38134),
(197268, 'frFR', '', 'Un écho de ses souvenirs les plus profondément enfouis. Il va faire la lumière sur sa duplicité.', 38134),
(197267, 'frFR', 'Je peux pas parler. J\’dois aiguiser ces armes au cas où les vive-pierres se réveillent.\n\nIls se réveillent pas souvent, mais quand ça arrive, ils sont ronchons si on leur met pas une bonne arme bien affûtée entre les mains.', '', 38134),
(197266, 'frFR', 'Allez, venez. Nous allons régler ça entre nous.', '', 38134),
(197265, 'frFR', 'Better them than being chopped up for parts.', 'Better them than being chopped up for parts.', 38134),
(197264, 'frFR', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 38134),
(197263, 'frFR', 'We don\'t have options now. This is our best chance for success.', 'We don\'t have options now. This is our best chance for success.', 38134),
(197259, 'frFR', '', 'Le prince Rénathal donne ces réceptions de la cour des Braises pour s\’attirer les faveurs des personnalités influentes de l\’Ombreterre.\n\nSi nos invités repartent mécontents, nous aurons manqué notre objectif.\n\nDe plus, chaque invité paie un tribut au prince en quittant la cour.\n\nPlus un invité est satisfait, plus le tribut qu\’il offre est important.\n\nJe suis certaine que cela vous intriguera.', 38134),
(197258, 'frFR', '', '<L\’étrange femme arbre ne réagit pas. On dirait qu\’elle s\’est endormie au beau milieu du chemin.>', 38134),
(197257, 'frFR', '', 'Comme vous vous en doutez, chaque invité à la réception a ses préférences. Certains aiment les évènements luxueux et protocolaires, tandis que d\’autres se sentent plus à leur aise dans un cadre détendu, voire dangereux.\n\nEn tant qu\’organisateurs, notre tâche consiste à créer une ambiance que nos invités apprécieront.\n\nC\’est donc à nous qu\’il incombe de sélectionner des services à leur goût, notamment pour les rafraîchissements et les divertissements.\n\nNous pouvons aussi participer à diverses activités au cours de la réception afin de créer une soirée mémorable pour chacun d\’entre eux.\n\nSi vous ignorez ce que préfère l\’un de vos hôtes, consultez la liste des invités tenue par le seigneur Garridan.\n\nLe personnel affecté aux différents services peut aussi vous indiquer l\’option la mieux adaptée aux penchants de chacun.', 38134),
(197255, 'frFR', '', 'Quoi… qu\’est-ce que c\’est ?', 38134),
(197251, 'frFR', '', 'Cette puissance… c\’est impossible !', 38134),
(197249, 'frFR', '', 'L\’Antre vous emporte !', 38134),
(197248, 'frFR', '', 'Le parangon doit mourir ! Je ne vous laisserai pas intervenir !', 38134),
(197247, 'frFR', '', 'J\’ai toujours trouvé qu\’il était plutôt facile de rallier quelqu\’un à sa cause.\n\nIl suffit de lui apporter ce qu\’il désire.', 38134),
(197243, 'frFR', '', 'Une nouvelle surprise. Nous nous chargeons de l\’accueillir.', 38134),
(197227, 'frFR', 'Ton petit jeu ne trompe personne, Ilka. Je sais que c\’est toi qui as trahi le village.', 'Ton petit jeu ne trompe personne, Ilka. Je sais que c\’est toi qui as trahi le village.', 38134),
(197226, 'frFR', 'Du calme, Samo. Nous t\’écoutons, Lajos ! Qui a fait quoi à qui ? J\’ai hâte d\’entendre ça.', 'Du calme, Samo. Nous t\’écoutons, Lajos ! Qui a fait quoi à qui ? J\’ai hâte d\’entendre ça.', 38134),
(197223, 'frFR', 'Quoi encore, Lajos ? Je suis occupé, contrairement à vous.', 'Quoi encore, Lajos ? Je suis occupée, contrairement à vous.', 38134),
(197182, 'frFR', '', 'Ah ha !', 38134),
(197181, 'frFR', 'House of Plagues. I was out on assignment when the accident happened.', 'House of Plagues. I was out on assignment when the accident happened.', 38134),
(197180, 'frFR', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 38134),
(197179, 'frFR', 'Good. It will speed up your training considerably if you are already skilled.', '', 38134),
(197178, 'frFR', 'I am a mage. I can channel anima.', 'I am a mage. I can channel anima.', 38134),
(197177, 'frFR', 'Vous ne voyez pas que je suis occupé ?', 'Vous ne voyez pas que je suis occupée ?', 38134),
(197176, 'frFR', 'Do you have any other skills or abilities we should know about?', '', 38134),
(197175, 'frFR', 'Which house were you originally affiliated with before now?', '', 38134),
(197174, 'frFR', 'Are you able to or were you previously able to cast spells? If so, what kind?', '', 38134),
(197172, 'frFR', 'This isn\'t a hypothetical question. Don\'t take it lightly.', '', 38134),
(197171, 'frFR', 'Yes.', 'Yes.', 38134),
(197170, 'frFR', 'Are you willing to pledge your afterlife to Margrave Sin\'dane and to the service of the House of Rituals?', '', 38134),
(197168, 'frFR', '', 'Leur commandant a pris le contrôle de la salle d\’audience, vous devez l\’affronter !', 38134),
(197160, 'frFR', '', 'Vous ne pouvez pas m\’échapper !', 38134),
(197146, 'frFR', 'Bwonsamdi laisse des mortels se battre à sa place ? C\’est un trouillard, je l\’ai toujours su.', '', 38134),
(197145, 'frFR', 'Sans moi, il serait rien ! Et quand j\’en aurai fini avec lui, c\’est ce qu\’il sera à nouveau. Rien.', '', 38134),
(197144, 'frFR', 'Quand je vois tout ce que j\’ai fait pour Bwonsamdi. Un pauvre petit prêtre, j\’en ai fait un Loa ! Je lui ai donné le pouvoir sur la mort. Et maintenant, il s\’en sert contre moi ?!', '', 38134),
(197143, 'frFR', 'Mais toi, tu as préféré trahir. Alors nous serons ennemis.', '', 38134),
(197142, 'frFR', 'Tu vois la puissance que j\’ai offerte à mes fidèles, Bwonsamdi ?', '', 38134),
(197140, 'frFR', '', 'Plus de morts, toujours plus !', 38134),
(197135, 'frFR', 'This is the line for application to begin testing. If you have no magical ability, you\'re wasting everyone\'s time.', '', 38134),
(197134, 'frFR', '', 'Oh, quelle chance ! Vous voilà de retour.', 38134),
(197130, 'frFR', 'On est sauvés ! Merci beaucoup-hou !', 'On est sauvés ! Merci beaucoup-hou !', 38134),
(197129, 'frFR', 'ELLE BOUGE !', 'ELLE BOUGE !', 38134),
(197128, 'frFR', 'Je la frappe ?', 'Je la frappe ?', 38134),
(197127, 'frFR', 'Faites quelque chose ! Faites quelque chose !', 'Faites quelque chose ! Faites quelque chose !', 38134),
(197121, 'frFR', 'Ça alors, quelle drôle de créature vous faites… Ce sont vos vraies oreilles ?', 'Ça alors, quelle drôle de créature vous faites… Ce sont vos vraies oreilles ?', 38134),
(197114, 'frFR', '', 'Heureusement pour nous, notre $uami:amie; fait des merveilles face au danger. Et puisqu\’on parle du loup.', 38134),
(197113, 'frFR', 'Ce plan pourrait fonctionner, mais il ne sera pas sans risque.', '', 38134),
(197100, 'frFR', '', 'Vous ne serez en sécurité nulle part. Nous écumerons les moindres recoins de Revendreth s\’il le faut.', 38134),
(197099, 'frFR', '', 'Courez, pauvre $umortel:mortelle;. Mes brisespoirs vous retrouveront.', 38134),
(197098, 'frFR', '', 'Je vous retrouverai là-bas. Je dois prévenir nos alliés de ce qui se prépare.', 38134),
(197095, 'frFR', '', 'Très bien. À présent, montrez-lui ce qui arrivera s\’il refuse de coopérer.', 38134),
(197093, 'frFR', 'Il faut qu\’il finisse de se recharger. Essayez de gagner du temps !', '', 38134),
(197092, 'frFR', 'Nous sommes tous condamnés… à finir dans l\’Antre…', 'Nous sommes tous condamnés… à finir dans l\’Antre…', 38134),
(197091, 'frFR', 'Il est trop tard…', 'Il est trop tard…', 38134),
(197090, 'frFR', 'Je ne sais pas ce qui s\’est passé… mais on ne peut pas faire confiance aux Venthyrs…', 'Je ne sais pas ce qui s\’est passé… mais on ne peut pas faire confiance aux Venthyrs…', 38134),
(197089, 'frFR', 'Non ! C\’est un mensonge !', 'Non ! C\’est un mensonge !', 38134),
(197087, 'frFR', 'Les Venthyrs nous avaient promis de nous libérer de nos vices.\n\nEt après tout ce temps, j\’ai vraiment cru que j\’y étais parvenu…\n\nMais ils se sont joués de moi… Ils se sont moqués… Et ils m\’ont abandonné.\n\nTout ceci n\’était donc qu\’un mensonge ?', 'Les Venthyrs nous avaient promis de nous libérer de nos vices.\n\nEt après tout ce temps, j\’ai vraiment cru que j\’y étais parvenue…\n\nMais ils se sont joués de moi… Ils se sont moqués… Et ils m\’ont abandonnée.\n\nTout ceci n\’était donc qu\’un mensonge ?', 38134),
(197086, 'frFR', 'N\’oubliez pas d\’utiliser le résonateur pour qu\’ils ne se relèvent pas !', '', 38134),
(197082, 'frFR', 'Bien compris ! Je vais déplacer le colosse… enfin… je vais essayer…', '', 38134),
(197067, 'frFR', 'Il n\’en reste qu\’une. Ne vous déconcentrez pas.', '', 38134),
(197065, 'frFR', '', 'C\’est là ou je voulais en venir : ne me trahissez pas.', 38134),
(197064, 'frFR', '', 'Car, oui, c\’est ce que nous sommes, $p. $uAssociés:Associées;. Dès à présent et jusqu\’au dernier soubresaut de nos âmes.', 38134),
(197063, 'frFR', '', 'S\’il découvre que vous et moi sommes $uassociés:associées;, il vous fera joyeusement tuer pour nuire à ma réussite.', 38134),
(197062, 'frFR', '', 'Si vous rencontrez d\’autres membres de mon cartel dans l\’Ombreterre, sachez qu\’il ne faut pas leur faire confiance.', 38134),
(197061, 'frFR', '', 'Et voyez-vous, mon cartel est de nature envieuse. Lui qui m\’a envoyée ici il y a si longtemps envoie à présent des assassins pour m\’éliminer.', 38134),
(197060, 'frFR', '', 'Maintenant que nous savons que certains peuvent s\’échapper d\’ici, je me retrouve dans une position, disons… Enviable.', 38134),
(197057, 'frFR', '', 'Ça fait des années que mon cartel envoie des agents dans l\’Antre. C\’est une mission dangereuse, mais nous ne résistons pas à l\’attrait des informations sensibles.', 38134),
(197056, 'frFR', '', 'Règle numéro 7 : la trahison est inéluctable, même au sein de vos proches. Surtout au sein de vos proches.', 38134),
(197055, 'frFR', '', 'Voyez ce que vous pourrez apprendre dans les Terriers. Je poursuis mes recherches.', 38134),
(197053, 'frFR', '', 'Ce cryptogramme permettra de masquer votre anima dans leurs terriers. Rapportez-moi les runes qui animent ces horreurs, que je puisse les étudier.', 38134),
(197051, 'frFR', '', 'Règle numéro 6 : inventer sans cesse de nouvelles méthodes de camouflage. Les antreliges trouvent sans cesse de nouveaux moyens de nous dénicher.', 38134),
(197050, 'frFR', '', 'Je dois avouer que je n\’étais pas certaine de vous voir revenir. Et ce n\’est pas un compliment.', 38134),
(197049, 'frFR', '', 'Prenez donc ce nouveau cryptogramme. S\’il marche comme prévu, poser le pied dans le fort ne devrait pas vous valoir une mort immédiate.', 38134),
(197048, 'frFR', '', 'Il règne une grande agitation au fort de la Perdition. Je veux savoir pourquoi.', 38134),
(197047, 'frFR', '', 'Règle numéro 5 : savoir faire preuve d\’audace quand la situation l\’exige. Les antreliges s\’y attendent rarement.', 38134),
(197045, 'frFR', 'Ce temple n\’était que le premier à tomber. Bientôt, le Bastion tout entier sera la proie des flammes.', '', 38134),
(197044, 'frFR', '', 'Tout en haut de ma liste figure un inquisiteur qui a gagné plusieurs places. Si son existence venait à prendre fin par votre faute, je saurais me montrer généreuse.', 38134),
(197043, 'frFR', '', 'Règle numéro 4 : toujours avoir une liste de ceux qui pourraient vouloir mettre fin à vos jours. Et si vous êtes sur la mienne, n\’allez pas prendre la grosse tête. Vous êtes tout en bas.', 38134),
(197042, 'frFR', '', 'Ne commettez pas d\’imprudence tant que l\’œil du Geôlier sera sur vous. Vous avez un certain potentiel, vous perdre maintenant serait une contrariété.', 38134),
(197039, 'frFR', 'Notre arène est bardée d\’anima et mérite une championne bardée de trophées !', '', 38134),
(197038, 'frFR', 'Véritable légende de l\’arène, elle manie sa fameuse charognarde runique, c\’est Sabrielle l\’Ostéoclaste !', '', 38134),
(197037, 'frFR', '', 'Règle numéro 3 : la confiance se mérite et le savoir n\’est pas gratuit. Je vous ai donné un cryptogramme, il est donc clair que vous m\’êtes redevable.', 38134),
(197029, 'frFR', '', 'Tirna Scithe ne vous oubliera jamais.', 38134),
(197028, 'frFR', '', 'Lakali sommeille, son savoir est préservé. Le temps le soignera.', 38134),
(197000, 'frFR', '', 'Un savoir… si… précieux…', 38134),
(196984, 'frFR', '', 'Affrontez… les brumes… la menace qu\’elles cachent. Et nous nous retrouverons.', 38134),
(196976, 'frFR', 'Regardez ! Là-haut, au bord du précipice ! Serait-ce…', '', 38134),
(196975, 'frFR', '', 'À mort ! Que les âmes des damnés vous submergent !', 38134),
(196974, 'frFR', '', 'Cet outrage va vous coûter cher ! Les ailes de mes antreliges noirciront le ciel de votre monde !', 38134),
(196973, 'frFR', '', 'Indignes ? Des champions indignes vous ont pourtant flanqué une bonne raclée !', 38134),
(196972, 'frFR', '', 'Vos âmes indignes viendront nourrir l\’obscurité !', 38134),
(196971, 'frFR', 'Attendez. J\’entends quelque chose…', '', 38134),
(196968, 'frFR', '', 'Préparez le « rituel ». Notre piège doit être convaincant pour que Mor\’Bidan s\’y laisse prendre.', 38134),
(196920, 'frFR', 'Peut-être bien, Keltesh. La plupart des gens oublient que les âmes sont très observatrices, même si elles sont prisonnières.', 'Peut-être bien, Keltesh. La plupart des gens oublient que les âmes sont très observatrices, même si elles sont prisonnières.', 38134),
(196918, 'frFR', '', 'Assez ricané. Allez chercher les stygies dont j\’ai besoin et revenez immédiatement. Le cryptogramme vous attend !', 38134),
(196917, 'frFR', '', 'Je suis bien consciente de l\’ironie qu\’il y a à énoncer cette règle alors que je me montre incapable de m\’échapper d\’ici. Je vous autorise à en rire… brièvement.', 38134),
(196916, 'frFR', '', 'Règle numéro 1 : Avoir une porte de sortie. Pour assurer sa survie dans l\’Antre, il faut disposer en permanence d\’un plan d\’évasion.', 38134),
(196915, 'frFR', 'Difficile à dire… Samo et Ilka se sont disputés à propos de la dîme et de la pénurie. C\’est suspect, à votre avis ?', '', 38134),
(196891, 'frFR', '', 'Gloire à toi, Zovaal ! Voici des âmes pour ton bon plaisir !', 38134),
(196885, 'frFR', 'Je sais, tes péchés te pèsent terriblement. Je vais faire mander l\’Accusatrice afin de procéder à ton jugement.', 'Je sais, tes péchés te pèsent terriblement. Je vais faire mander l\’Accusatrice afin de procéder à ton jugement.', 38134),
(196880, 'frFR', '', 'Obtenir leur confiance comble l\’âme. Rien ne surpasse l\’émotion qui vous anime quand vous faites équipe avec un volion qui vous respecte.', 38134),
(196879, 'frFR', 'Aaargh ! Je… je suis étrangement… plus léger ? Je n\’ai pas peur.', '', 38134),
(196878, 'frFR', '', 'Forger un lien avec ne serait-ce qu\’un volion n\’est pas chose facile. Ce sont des créatures particulières, nobles mais têtues.', 38134),
(196874, 'frFR', '', 'Notre troupe vous souhaite la bienvenue, $n. Je suis certaine que vous suivrez votre cœur.', 38134),
(196873, 'frFR', 'Vous avez un partenaire parfait, conçu pour répondre spécifiquement à vos besoins, avec pour simple contrainte une maintenance et des calibrations occasionnelles.', '', 38134),
(196872, 'frFR', 'Les phalynx font preuve d\’une incroyable compréhension. Ils acceptent et suivent les ordres facilement, et ne s\’y opposent ni ne les remettent jamais en question.', '', 38134),
(196868, 'frFR', 'Je suis convaincu que vous verrez les avantages de travailler avec mes phalynx.', '', 38134),
(196865, 'frFR', 'Le marais des Affres gagne jour après jour. Il est en train de tout avaler. J\’espère que vous pourrez y remédier.', '', 38134),
(196864, 'frFR', 'À une époque, cet endroit grouillait de princes voleurs, de rois miséreux et de pirates. Saleté de pénurie.', '', 38134),
(196863, 'frFR', 'Hé, c\’est que partout dans les tours, ça cause que de vous ces jours-ci.', '', 38134),
(196862, 'frFR', 'Pour tout vous dire, si je prends ce chemin, c\’est juste parce qu\’ils ont pas assez d\’ailés pour patrouiller ici.', '', 38134),
(196861, 'frFR', 'Mon cousin a travaillé sur certaines de ces maisons. Ces salauds l\’ont collé au bourbier quand il a terminé.', '', 38134),
(196860, 'frFR', 'Gardez le museau baissé. Faudrait pas que les crocs nous voient ensemble.', '', 38134),
(196859, 'frFR', 'Il paraît que ces bois grouillent de rebelles et de hors-la-loi.', '', 38134),
(196858, 'frFR', 'Pas possible de s\’arrêter ici. C\’est le domaine des chiropteffrois.', '', 38134),
(196857, 'frFR', 'Avec moi, $n.', '', 38134),
(196856, 'frFR', 'J\’ai pas le temps de bavasser. Je dois garder un œil sur les travailleurs.', '', 38134),
(196854, 'frFR', 'Yahou ! De l\’aide !', 'Yahou ! De l\’aide !', 38134),
(196846, 'frFR', 'Ce poste n\’est pas digne de moi. J\’espère que la comtesse retiendra toute l\’étendue de mes services.', 'Ce poste à la porte Antique n\’est pas digne de moi. J\’espère que la comtesse retiendra toute l\’étendue de mes services.', 38134),
(196845, 'frFR', '', 'Tu te mets le doigt dans l\’œil, Pélodis. $n a été $uaccepté:acceptée; dans notre troupe. Il n\’y a pas de plus grand honneur.', 38134),
(196844, 'frFR', 'Je crois que sa préférence va à l\’efficacité de mes phalynx.', '', 38134),
(196840, 'frFR', 'J\’existe depuis bien trop longtemps pour finir si trivialement…', '', 38134),
(196839, 'frFR', 'C\’est l\’une des plus belles créations de Mikanikos. Il concentre directement l\’anima en décharges d\’énergie.', '', 38134),
(196838, 'frFR', 'Une intrusion ! Vous allez subir le supplice de la Lumière !', '', 38134),
(196836, 'frFR', 'Si sa magie a pu pénétrer un tel blindage, Nalthor est réellement puissant. Il ne sera pas facile à battre.', '', 38134),
(196835, 'frFR', 'Au Bastion, tout est alimenté par l\’anima, même les grands colosses. Voilà qui l\’aidera à rester chargé.', '', 38134),
(196834, 'frFR', '', 'Ce sera quand vous voudrez, $n.', 38134),
(196830, 'frFR', 'J\’ai commis de terribles péchés de mon vivant. Tout est si clair, à présent.', '', 38134),
(196828, 'frFR', 'Salutations, Sans-Entraves.', '', 38134),
(196815, 'frFR', '', 'Je retourne voir le seigneur de guerre Hache-Sinistre. Essayez de me suivre sous votre nouvelle forme.', 38134),
(196814, 'frFR', '', 'Votre lien avec la nature s\’est renforcé. Utilisez-le pour protéger les terres sauvages et maintenir l\’équilibre.', 38134),
(196813, 'frFR', '', 'Écoutez leurs murmures… Retenez les leçons qu\’ils vous transmettent.', 38134),
(196812, 'frFR', '', 'Les esprits nous répondent !', 38134),
(196811, 'frFR', '', 'Ô esprits de la nature, nous vous implorons. Offrez votre pouvoir à cette jeune âme.', 38134),
(196810, 'frFR', '', 'Il y a une antique pierre druidique non loin d\’ici. Allons sur place pour communier avec la nature.', 38134),
(196808, 'frFR', 'Je continuerai de me battre jusqu\’à ce que vous me montriez vos nouveaux talents !', '', 38134),
(196807, 'frFR', 'Impressionnant ! Vous avez vraiment progressé. Retournons au camp.', '', 38134),
(196806, 'frFR', 'Montrez-moi ce que vous avez appris !', '', 38134),
(196805, 'frFR', 'Voilà, c\’est terminé. Comment vous sentez-vous ?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196804, 'frFR', 'Laissez le monde partir au loin…', '', 38134),
(196803, 'frFR', 'Fermez les yeux. Concentrez-vous.', '', 38134),
(196802, 'frFR', 'Venez. Méditons ensemble pour découvrir votre véritable potentiel.', '', 38134),
(196801, 'frFR', 'Je… n\’aime pas trop la magie gangrenée. Mais j\’imagine que vous faites ça pour la Horde.', '', 38134),
(196800, 'frFR', 'Cette leçon ne vous était pas destinée, que je sache ?', '', 38134),
(196799, 'frFR', 'J\’ai pas l\’impression d\’avoir appris grand-chose…', '', 38134),
(196798, 'frFR', 'Bon, vous pouvez donner un petit coup à Throg pour dissiper l\’effet.', '', 38134),
(196797, 'frFR', 'Regardez comme l\’ennemi métamorphosé erre sans comprendre. Profitez-en pour achever les autres.', '', 38134),
(196795, 'frFR', 'Alors là, vous m\’en avez bouché un coin, recrue ! Bon, retournons au camp.', '', 38134),
(196794, 'frFR', 'Il y a deux personnes qui vous attaquent en même temps. Lancez Métamorphose !', '', 38134),
(196793, 'frFR', 'L\’adversaire est libéré s\’il subit des dégâts. Attaquez donc Herbert pour le libérer.', '', 38134),
(196792, 'frFR', 'Je connais cette tactique. Garder un ennemi métamorphosé pendant qu\’on s\’occupe de l\’autre.', '', 38134),
(196790, 'frFR', 'Ce n\’est pas avec ce genre d\’attitude que vous apprendrez quoi que ce soit !', '', 38134),
(196789, 'frFR', 'J\’ai pas envie de me faire transformer en mouton.', '', 38134),
(196788, 'frFR', 'Throg, avec moi. $p, on va vous attaquer pour vous apprendre à utiliser Métamorphose.', '', 38134),
(196787, 'frFR', 'Votre ennemi se retrouve désorienté tant que vous ne l\’attaquez pas. Comme ça.', '', 38134),
(196786, 'frFR', 'Ce n\’est qu\’une démonstration. Métamorphose est très utile en combat.', '', 38134),
(196785, 'frFR', '', 'Tristéclat, expliquez-vous !', 38134),
(196784, 'frFR', 'Voici un exemple de Métamorphose !', '', 38134),
(196783, 'frFR', 'J\’irais bien chercher le livre moi-même, mais un vrai mage doit savoir déléguer.', '', 38134),
(196782, 'frFR', '', 'Je me rappelle la première fois où Shuja a communié avec eux. C\’est un bel avenir qui vous attend chez les chamans, $n.', 38134),
(196781, 'frFR', '', 'Je vois que les esprits vous ont confié une tâche. Ils ont bien raison.', 38134),
(196780, 'frFR', '', '$p ? Vous parlez aux esprits, c\’est ça ?', 38134),
(196777, 'frFR', '', 'Oui, marchedroit ! Puisse l\’Archonte veiller sur nous.', 38134),
(196773, 'frFR', '', 'Il est plus… fragile que d\’autres face à la purification. Je ne voudrais pas qu\’il s\’égare en chemin. Ce que nous nous apprêtons à faire peut paraître éminemment déplaisant.\n\nJ\’ai toutefois la nette impression que vous avez l\’estomac suffisamment bien accroché pour supporter un tel spectacle.', 38134),
(196772, 'frFR', '', 'Uther ? Ce nom me dit quelque chose.\n\nNe serait-ce pas l\’aspirant que Déva a pris sous son aile ? Comme il est étrange que vous l\’ayez reconnu… Mettons cela sur le compte du fait qu\’il est originaire de votre monde.', 38134),
(196771, 'frFR', 'Faites pleuvoir la mort ! Que personne ne puisse s\’interposer !', '', 38134),
(196770, 'frFR', 'Éridia, les protections ! Ils vont…', '', 38134),
(196769, 'frFR', '', 'Rien, je…', 38134),
(196768, 'frFR', 'Artémède va prévenir l\’Archonte. Nous devons retourner à Oribos et les informer de la trahison de Maldraxxus !', '', 38134),
(196767, 'frFR', '', 'Le parangon pourra s\’en sortir seule le temps que vous battiez le rappel des troupes.', 38134),
(196764, 'frFR', 'C\’est bien naturel, Keltesh. L\’heure du jugement approche et nous devons te libérer de tes péchés.', 'C\’est bien naturel, Keltesh. L\’heure du jugement approche et nous devons te libérer de tes péchés.', 38134),
(196761, 'frFR', 'Venez, il faut sortir d\’ici !', '', 38134),
(196758, 'frFR', 'J\’ai peur, Bélo.', 'J\’ai peur, Bélo.', 38134),
(196755, 'frFR', 'Eh bien, approchez, si vous l\’osez.', '', 38134),
(196754, 'frFR', '', 'Vous le paierez très cher, nécromancien !', 38134),
(196753, 'frFR', 'Alors, vous serez la prochaine à succomber.', '', 38134),
(196752, 'frFR', '', 'Moi vivante, jamais.', 38134),
(196750, 'frFR', 'Les Kyrians entretiennent un cycle néfaste. Nous allons nous emparer de l\’anima gâché par le Bastion.', '', 38134),
(196745, 'frFR', '', 'Monstres ! Mais qu\’est-ce qui a pu vous pousser à une telle folie ?', 38134),
(196740, 'frFR', '', 'Allez trouver Nikolon et assurez-vous qu\’il va bien, je vous prie. S\’attarder ainsi ne lui ressemble guère.', 38134),
(196721, 'frFR', 'Mon esprit va passer de l\’Autre côté. Vous allez faire de grandes choses.', '', 38134),
(196720, 'frFR', 'Lancez Résurrection pour les arracher aux griffes de la mort !', '', 38134),
(196719, 'frFR', 'De l\’aide, enfin ! Magnez-vous. Ces recrues, elles ont encore une étincelle de vie !', '', 38134),
(196718, 'frFR', 'Si elle vit encore, il faut lui porter secours.', '', 38134),
(196717, 'frFR', 'Le parangon de courage était au cœur du temple au moment de l\’attaque.', '', 38134),
(196716, 'frFR', 'Merci, $p. On ne peut pas enterrer les recrues, mais on peut quand même leur donner les derniers sacrements.', '', 38134),
(196714, 'frFR', 'Il se dirige vers le parangon !', '', 38134),
(196713, 'frFR', '', 'Ayez foi en la Lumière, noble paladin.', 38134),
(196712, 'frFR', '', 'Bouclier divin vous protégera de la magie noire de l\’autel.', 38134),
(196711, 'frFR', '', 'Approchez, paladin. La Lumière va vous aider en cette heure difficile.', 38134),
(196710, 'frFR', 'Et voilà comment on règle un problème !', '', 38134),
(196709, 'frFR', 'FEU !', '', 38134),
(196708, 'frFR', 'Avez-vous retrouvé d\’autres souvenirs ?$b$BPlus vous m\’en rendrez, mieux nous nous en porterons tous les deux.', '', 38134),
(196706, 'frFR', 'Je ne suis pas très calé en matière de Lumière. Mais quand un esprit appelle, faut obéir.', '', 38134),
(196705, 'frFR', 'Le colosse est prêt ! Ha, venez donc !', '', 38134),
(196704, 'frFR', 'Venez donc si vous êtes si braves, chers Kyrians. Venez trouver votre destin.', '', 38134),
(196703, 'frFR', 'Ça suffit. Le fils de Croc-d\’Os a mérité sa friandise.', '', 38134),
(196702, 'frFR', 'Il est presque à puissance maximale ! Tenez encore un peu !', '', 38134),
(196701, 'frFR', 'L\’esprit d\’un ancien guerrier doit avoir des tas de choses à vous apprendre. Écoutez-le bien.', '', 38134),
(196700, 'frFR', 'Nous sommes sans fin. Un par un, vous succomberez.', '', 38134),
(196699, 'frFR', 'C\’est tout ce que vous avez pour nous ? Je m\’attendais à plus !', '', 38134),
(196698, 'frFR', '', 'Vite, $n, écartez ces souvenirs !', 38134),
(196697, 'frFR', 'Je n\’y arrive pas ! AHHH !', '', 38134),
(196696, 'frFR', '', 'Reprenez-vous, acolyte !', 38134),
(196695, 'frFR', '', 'J\’y suis presque… Oh !', 38134),
(196694, 'frFR', 'Placez votre piège givrant dans les ruines de l\’Ogre. La bête qui harcèle notre camp devrait se montrer.', '', 38134),
(196693, 'frFR', 'Oh, un granule de glace ? Oui, allez-y. J\’en ai plein en stock depuis… l\’accident.', '', 38134),
(196692, 'frFR', 'Rapportez tout ça sans perdre de temps. Plus vite vous apprendrez à confectionner ce piège, mieux ce sera.', '', 38134),
(196690, 'frFR', 'Imbécile. Mon armée vous encercle.', '', 38134),
(196689, 'frFR', 'Rappelez donc vos bêtes ou elles mourront comme les autres.', '', 38134),
(196688, 'frFR', 'Vos troupes ont déjà été repoussées. Vous avez perdu.', '', 38134),
(196685, 'frFR', 'Étrange… Pourquoi ces magistrats sont-ils désactivés ?', '', 38134),
(196683, 'frFR', 'Cela devra suffire pour l\’instant. Allez trouver une bête et apprivoisez-la.', '', 38134),
(196682, 'frFR', 'Je vais vous enseigner les bases aussi vite que possible. Concentrez-vous.', '', 38134),
(196681, 'frFR', 'Bal\’a dash, malanore. Je me ferai une joie de vous transmettre le savoir des Pérégrins.', '', 38134),
(196680, 'frFR', 'Vite, il faut avertir le marchedroit !', '', 38134),
(196679, 'frFR', 'Non… J\’avais gagné sa faveur…', '', 38134),
(196677, 'frFR', 'Comment osez-vous retarder la livraison du haut percepteur ?!', '', 38134),
(196676, 'frFR', 'Imbéciles ! Les désavoués vont écraser votre Archonte !', '', 38134),
(196675, 'frFR', '', 'Le sceau du courage est encore intact, quel soulagement.', 38134),
(196674, 'frFR', 'Ah non, pas encore…', '', 38134),
(196673, 'frFR', 'Je crois qu\’il me faudrait quelques points de suture…', '', 38134),
(196672, 'frFR', 'C\’est… irréparable…', '', 38134),
(196671, 'frFR', 'Ne laissez pas… ma chair… se perdre…', '', 38134),
(196670, 'frFR', 'Ah… j\’avais trop… de défauts…', '', 38134),
(196668, 'frFR', '', 'Mange quelque chose. Ne va pas mourir de faim.', 38134),
(196667, 'frFR', '', 'Les Hurans nous sont tombés dessus…', 38134),
(196666, 'frFR', 'Le plein d\’os, de chair et de peau !', '', 38134),
(196665, 'frFR', 'Dépêchez-vous de mourir, j\’ai hâte de m\’y mettre !', '', 38134),
(196664, 'frFR', 'Découdre, coudre et recoudre !', '', 38134),
(196663, 'frFR', 'Ça me fera du stock d\’organes !', '', 38134),
(196662, 'frFR', 'Hou, de la matière première !', '', 38134),
(196661, 'frFR', '', 'Dans ce cas, vous ne m\’êtes d\’aucune utilité.', 38134),
(196660, 'frFR', 'Général, vous nous demandez plus que nous ne pouvons vous donner. Nous n\’avons plus rien.', 'Général, vous nous demandez plus que nous ne pouvons vous donner. Nous n\’avons plus rien.', 38134),
(196659, 'frFR', '', 'Prenez une wyverne et rentrez à Orgrimmar. Là aussi, la gloire nous attend.', 38134),
(196657, 'frFR', 'Ces aspirants auraient bien besoin d\’être encouragés.', '', 38134),
(196656, 'frFR', 'Celles qui abdiquent trouveront le repos au temple jusqu\’à ce qu\’elles embrassent à nouveau la voie.', '', 38134),
(196655, 'frFR', 'Oui, Votre Transcendance.', '', 38134),
(196653, 'frFR', 'Bien des âmes ont chancelé ces derniers temps. Le temple de la Loyauté fait l\’impossible pour les ramener sur la voie.', '', 38134),
(196651, 'frFR', 'Je me souviens de ma famille. Elle aurait du mal à me reconnaître aujourd\’hui ! Puisse mon oubli se passer… différemment.', '', 38134),
(196649, 'frFR', 'Ça s\’est plutôt bien passé. Mieux que ma dernière purification, en tout cas.', '', 38134),
(196644, 'frFR', 'Vous ai-je déjà vu ? Vous ne m\’évoquez rien…', '', 38134),
(196643, 'frFR', 'Pouvons-nous commencer ?', '', 38134),
(196639, 'frFR', '', 'Merci. Et croyez bien que je suis désolée.', 38134),
(196638, 'frFR', '', 'Perdre un acolyte au terme de ses épreuves… Je dois en informer le parangon.', 38134),
(196637, 'frFR', '', 'Vous ne pouvez pas dire une telle chose, acolyte. Courage. Renoncer ne vous mènera nulle part.', 38134),
(196636, 'frFR', '', 'Acolyte ! Non !', 38134),
(196635, 'frFR', '', 'Assez. Je renonce à suivre cette voie !', 38134),
(196634, 'frFR', '', 'J\’ai longtemps servi ce temple sans ménager ma peine. Et qu\’en ai-je retiré ?', 38134),
(196633, 'frFR', '', 'Non ! Résistez ! Il le faut !', 38134),
(196632, 'frFR', 'Vous savez ce qu\’il vous faut faire. Ouvrez-vous à moi.', '', 38134),
(196631, 'frFR', '', 'Je ne suis… rien…', 38134),
(196630, 'frFR', 'Vous n\’étiez rien. Et vous n\’êtes toujours rien.', '', 38134),
(196629, 'frFR', '', 'Je suis prête.', 38134),
(196628, 'frFR', 'Bon, ça veut dire que Mueh\’zala protège les Loas pendant qu\’ils dévorent les graines sauvages pour se renforcer.', '', 38134),
(196627, 'frFR', 'Il est en train de drainer les graines pour ressusciter Hakkar et l\’imprégner de mojo maléfique.', '', 38134),
(196626, 'frFR', 'Les traces mènent au bosquet un peu plus loin. Alors, vous attendez quoi ?', '', 38134),
(196625, 'frFR', 'Je ne sais pas qui a laissé ces traces, mais il était pressé !', '', 38134),
(196624, 'frFR', 'Tous ces Loas qui disparaissent, ça ne me dit rien qui vaille.', '', 38134),
(196622, 'frFR', 'Hakkar aussi a disparu ? Ah, misère de misère… C\’est pas bon, ça. Pas bon du tout !', '', 38134),
(196621, 'frFR', 'Sang et tonnerre, soldats ! On a eu du mal à traverser la tempête pour vous retrouver.', '', 38134),
(196620, 'frFR', 'La graine de Dambala… Il n\’y a plus qu\’une enveloppe vide ! Il faut qu\’on trouve le vieux Hakkar, et vite !', '', 38134),
(196619, 'frFR', '', 'Des renforts de la Horde ! Je savais qu\’ils nous retrouveraient.', 38134),
(196618, 'frFR', 'Tharon\’ja, c\’était le Loa des Drakkari avant qu\’ils se retournent contre lui. Tss tss. Les fidèles ne sont plus ce qu\’ils étaient, de nos jours !', '', 38134),
(196617, 'frFR', '', 'Gardez la tête haute, $n. Nous avons tué un dragon et mis un terme à ce rituel aberrant !\n\nJe veillerai à ce que mes supérieurs au sein de la Horde aient vent de la vaillance dont vous avez fait preuve en ce jour de grâce !\n\nEt maintenant, filons d\’ici et trouvons un moyen de quitter cette île.', 38134),
(196616, 'frFR', '', 'Reposez vos racines.', 38134),
(196615, 'frFR', 'Mam\’toth repose en paix, maintenant. C\’est une sale affaire, ce qui s\’est passé à Zul\’Drak.', '', 38134),
(196614, 'frFR', 'Eh bien, je pense pouvoir dire que j\’influence mon propre cartel.\n\nJe suis l\’un des anciens du cartel Ba. Nous accordons beaucoup d\’importance à l\’accumulation d\’informations et de connaissances, un processus qui nous permet à son tour de gagner en influence et en richesse.\n\nLa réussite est essentielle à nos yeux, c\’est pourquoi les négociants ne rechignent jamais à afficher les fruits de leurs efforts. D\’où les titres dont m\’ont honoré mes pairs.\n\nAussi, j\’influence mon très cher cartel pour qu\’il participe à ma réussite. Après tout, j\’ai seulement à cœur d\’assurer notre prospérité.', '', 38134),
(196612, 'frFR', 'Qui commande ? Non, je ne dirais pas vraiment que c\’est moi qui commande.\n\nIl y en a bien d\’autres comme moi. Mais j\’imagine que ma voix est assez imposante.\n\nL\’influence a parfois plus de poids que le pouvoir, vous ne croyez pas ?\n\nVoilà, disons plutôt que je suis quelqu\’un… d\’influent.', '', 38134),
(196610, 'frFR', '', '$p, prévenez-moi quand vous serez $udisposé:disposée; à partir d\’ici. Il faut trouver un moyen de regagner Orgrimmar.', 38134),
(196609, 'frFR', '', 'Ha ! Les Ogres et leur dragon sont étendus à nos pieds et le ciel bleu vient célébrer notre victoire.', 38134),
(196608, 'frFR', 'Bienvenue. Que peuvent faire les négociants pour vous ?\n\nNous sommes à votre service.', '', 38134),
(196607, 'frFR', '', 'À l\’attaque ! Pour la Horde !', 38134),
(196606, 'frFR', '', 'Lok\’tar ogar ! La victoire ou la mort !', 38134),
(196605, 'frFR', '', 'On a… réussi à l\’arrêter ?', 38134),
(196604, 'frFR', '', 'Il va achever le rituel !', 38134),
(196603, 'frFR', 'Avec un peu de chance, tout le nécessaire pour votre artisanat !\n\nEn tant que négociants, nous parcourons l\’univers et recueillons le savoir-faire des maîtres de chaque réalité qu\’il nous est donné de traverser. C\’est ce qui nous permet de mettre à votre disposition les marchandises les plus adaptées à vos besoins.\n\nNos membres ont commencé à se perfectionner dans les diverses professions que vous et vos semblables avez l\’air de juger utiles. Ici, vous pourrez leur demander de vous assister dans votre formation à l\’artisanat.\n\nÀ l\’instant où je vous parle, nous nous procurons de nouvelles recettes. Je vous en prie, voyez par vous-même !', '', 38134),
(196601, 'frFR', '', 'Gor\’groth ! Tu vas aller rejoindre ton cher petit dragon dans la mort !', 38134),
(196600, 'frFR', '', 'Continuez, Gor\’groth ne doit plus être loin.', 38134),
(196599, 'frFR', '', 'À vos ordres ! Lok-tar !', 38134),
(196598, 'frFR', '', 'Je vais l\’occuper. Shuja, soigne-nous !', 38134),
(196597, 'frFR', '', '$n, à vous de lancer l\’assaut et je chargerai à la suite. Rien ne peut nous arrêter !', 38134),
(196596, 'frFR', '', 'C\’est l\’un des gardes du corps de Gor\’groth. C\’est quoi, le plan ?', 38134),
(196595, 'frFR', '', 'En route. J\’attire leur attention, occupez-vous de les abattre !', 38134),
(196594, 'frFR', '', 'Toute cette énergie nécrotique… le rituel doit être presque terminé !', 38134),
(196593, 'frFR', '', 'Ils sont presque tous morts, continuez à vous battre !', 38134),
(196592, 'frFR', 'Je vous remercie. Mais il faut toujours arrêter leur rituel !', '', 38134),
(196591, 'frFR', '', 'Mueh\’zala va payer cher pour ses ravages. Les esprits qu\’il a détruits étaient sous ma protection.', 38134),
(196590, 'frFR', '', 'Belle chasse ! La reine de l\’hiver sera contente d\’apprendre que Mueh\’zala a été chassé de Sylvarden.', 38134),
(196586, 'frFR', '', 'Le temple de la Sagesse abrite les souvenirs de tous les Kyrians. C\’est un soulagement de voir que son sceau est intact.', 38134),
(196585, 'frFR', 'Le sceau de l\’humilité est solide… même si ça ne ferait sans doute pas de mal aux désavoués de réviser les leçons qu\’ils y ont apprises.', '', 38134),
(196584, 'frFR', 'Une démonstration fort distrayante et un bel exemple de votre courage. Je vous félicite.', '', 38134),
(196583, 'frFR', 'Excellent, le sceau du temple de la Loyauté est activé. Les Kyrians méditant sur leur devoir et l\’Archonte seront en sécurité.', '', 38134),
(196580, 'frFR', 'Même brisé, le Heaume de domination possède un pouvoir incommensurable sur les âmes des morts.\n\nMais pas besoin de me croire sur parole, il suffit de lui demander.', '', 38134),
(196579, 'frFR', '', 'Les troupes de Mueh\’zala sont plus coriaces que prévu. Aidez l\’Hallali à les éliminer !', 38134),
(196576, 'frFR', '', 'Ralliez les bosquets voisins puis allez trouver dame Baie-de-Lune en Tirna Noch.', 38134),
(196575, 'frFR', 'Il pourrait lever de force une armée parmi les habitants de Revendreth. Avec de l\’anima en quantité suffisante, il pourrait même altérer la trame de l\’Ombreterre.', '', 38134),
(196574, 'frFR', 'Je n\’ai pas pour habitude de jouer les mercenaires. Mais c\’est pour cela que nous nous sommes associés.', '', 38134),
(196573, 'frFR', 'Cette tour est un avantage stratégique de taille pour les Maldraxxi. Elle pourrait être très utile au cartel.', '', 38134),
(196572, 'frFR', 'On va avoir besoin de la reine de l\’hiver si on veut avoir une chance face au vieux Mueh\’zala.', '', 38134),
(196571, 'frFR', '', 'C\’est étrange… Le sceau de la pureté est désactivé. C\’est ce qui aura permis aux désavoués de pénétrer en force.', 38134),
(196564, 'frFR', 'Oh… Il va trop loin, cette fois, ce vieux serpent !', '', 38134),
(196562, 'frFR', 'Il vous va comme un gant, ce masque ! Vous me rappelez… moi !', '', 38134),
(196561, 'frFR', 'Il y a quelque chose qui cloche avec les esprits des Loas qui reposent en Sylvarden. Allez voir ce qui se passe, hein ?', '', 38134),
(196558, 'frFR', 'Allez ! Servez-vous de la pierre de mort pour venir me voir de l\’Autre côté. Ne faites pas la poule mouillée !', '', 38134),
(196535, 'frFR', '', 'La force brute ne suffira pas. Il faut interrompre leur sortilège.', 38134),
(196534, 'frFR', '', 'Seigneur de guerre Foule-Tonnerre ! Nous devons détruire ces sceaux, maintenant !', 38134),
(196532, 'frFR', '', 'Hé, je ne vous ai pas dit de danser !', 38134),
(196531, 'frFR', '', 'Continuons. Voyons si on trouve des informations sur le rituel.', 38134),
(196530, 'frFR', '', 'Souriez et faites coucou, mais ne dites rien. Plus vous aurez l\’air bête, mieux ça vaudra.', 38134),
(196529, 'frFR', '', 'Jouez-nous votre meilleure imitation d\’Ogre pour nous infiltrer là-dedans.', 38134),
(196528, 'frFR', 'Un cobaye volontaire ! C\’est tellement rare de nos jours.', '', 38134),
(196527, 'frFR', '', 'Vous, venez avec moi ! Les autres, vous tenez cette position.', 38134),
(196526, 'frFR', '', 'Beau combat. Nous sommes à deux doigts d\’écraser ces misérables Ogres.', 38134),
(196525, 'frFR', 'Là où la suite du plan m\’appelle, quelle question. Essayez quand même d\’en tuer quelques-uns avant de vous faire anéantir.', '', 38134),
(196524, 'frFR', '', 'Je crois qu\’il s\’agit d\’une sorte de clé qui ouvre une salle au sein de Tourment.\n\nFouillez l\’antichambre à la recherche d\’un appareil compatible.\n\nQuant à l\’endroit où cela vous mènera… Je ne peux rien affirmer avec certitude.', 38134),
(196523, 'frFR', 'Mais où allez-vous ?', '', 38134),
(196522, 'frFR', 'Ne comptez pas sur moi. Vous avez rempli votre rôle, à mon tour de remplir le mien.', '', 38134),
(196520, 'frFR', 'L\’Archonte règne sur le Bastion depuis sa fondation en des temps immémoriaux.\n\nTâchez de vous souvenir de votre place lorsque vous lui demanderez humblement son aide.', '', 38134),
(196519, 'frFR', 'Une fois la mission accomplie, nous nous retrouverons plus bas au viaduc Brisé.', '', 38134),
(196518, 'frFR', '', 'Nous combattrons ensemble ! Lok\’tar ogar !', 38134),
(196517, 'frFR', 'Je vous couvre ! Abattons-les !', '', 38134),
(196516, 'frFR', 'Je suis libre ! Ha ha ha ! Hum… Ne nous attardons pas ici, voulez-vous ?', '', 38134),
(196513, 'frFR', '', 'Alors, on va l\’abattre tous ensemble. Pour la Horde !', 38134),
(196512, 'frFR', 'Il y a quelque chose d\’énorme qui approche !', '', 38134),
(196511, 'frFR', 'Quel dommage de devoir dissiper un sort aussi fascinant. Tenez les Harpies à distance !', '', 38134),
(196499, 'frFR', 'Tout cela était déjà difficile avant que l\’anima ne se volatilise. À présent, c\’est devenu impossible.\n\n<L\’aspirant lève les yeux au ciel d\’un air abattu.>\n\nImpossible.', 'Tout cela était déjà difficile avant que l\’anima ne se volatilise. À présent, c\’est devenu impossible.\n\n<L\’aspirante lève les yeux au ciel d\’un air abattu.>\n\nImpossible.', 38134),
(196498, 'frFR', 'Rien de ce que je fais n\’a la moindre importance. Aucun de nous ne verra le fort Élyséen. L\’Archonte s\’est murée dans le silence.\n\nNous sommes piégés ici par la pénurie d\’anima.', 'Rien de ce que je fais n\’a la moindre importance. Aucun de nous ne verra le fort Élyséen. L\’Archonte s\’est murée dans le silence.\n\nNous sommes piégés ici par la pénurie d\’anima.', 38134),
(196497, 'frFR', 'Même si j\’échoue, ils ne me renverront pas à Oribos. Nous sommes tous condamnés à demeurer ici pour l\’éternité.\n\nNotre sort est désormais sans espoir.', 'Même si j\’échoue, ils ne me renverront pas à Oribos. Nous sommes tous condamnés à demeurer ici pour l\’éternité.\n\nNotre sort est désormais sans espoir.', 38134),
(196496, 'frFR', 'Les cloches seront bientôt privées d\’anima. Plus rien n\’empêchera alors notre chute.\n\nMais ce n\’est pas une raison pour baisser les bras.', 'Les cloches seront bientôt privées d\’anima. Plus rien n\’empêchera alors notre chute.\n\nMais ce n\’est pas une raison pour baisser les bras.\n\n<L\’aspirante pousse un long soupir de lassitude.>', 38134),
(196495, 'frFR', 'Surmonter cette épreuve n\’y changera rien : plus personne ne rejoindra les rangs des transcendés. Je suis condamné à péricliter dans ce temple jusqu\’à la fin des temps.', 'Surmonter cette épreuve n\’y changera rien : plus personne ne rejoindra les rangs des transcendés. Je suis condamnée à péricliter dans ce temple jusqu\’à la fin des temps.', 38134),
(196494, 'frFR', 'Je me sens dépérir. Tout en moi me pousse à résister, mais ils disent que je dois continuer.\n\nNe voient-ils pas ce que nous subissons ? Sont-ils aveugles ?', 'Je me sens dépérir. Tout en moi me pousse à résister, mais ils disent que je dois continuer.\n\nNe voient-ils pas ce que nous subissons ? Sont-ils aveugles ?', 38134),
(196493, 'frFR', 'Un grand vide a remplacé mes souvenirs passés. Fallait-il vraiment qu\’il en soit ainsi ?', 'Un grand vide a remplacé mes souvenirs passés. Fallait-il vraiment qu\’il en soit ainsi ?', 38134),
(196492, 'frFR', '', 'Lysonia… je n\’aurais jamais cru qu\’elle portait les ténèbres en elle…', 38134),
(196491, 'frFR', '', 'Ses souvenirs ne laissent aucun doute, Déva. Votre main est au service de l\’Antre !', 38134),
(196490, 'frFR', 'J\’en suis !', '', 38134),
(196489, 'frFR', '', 'Je vais nous frayer un chemin à travers ces harpies.', 38134),
(196487, 'frFR', '', 'C\’est Herbert. Le connaissant, il doit être en train de prendre des notes.', 38134),
(196486, 'frFR', 'On dirait qu\’ils ont capturé un Réprouvé pour leur rituel.', '', 38134),
(196484, 'frFR', '', 'Je rejoindrai Bo au nord d\’ici, là où nichent les Harpies. Retrouvez-moi là-bas.', 38134),
(196482, 'frFR', 'C\’est pas bientôt fini, tout ce bazar ? Il y en a qui essaient de dormir. Réveillez-moi quand ce sera terminé.', '', 38134),
(196481, 'frFR', 'Bientôt, je pourrai de nouveau me battre pour Sylvarden ! Il faut juste que je me repose un peu, que je recouvre mes forces. Et ensuite, chaud devant !', '', 38134),
(196480, 'frFR', 'Ah, c\’est bon d\’être de nouveau à peu près en sécurité. Tout est si sauvage, là dehors !', '', 38134),
(196479, 'frFR', '', 'Enfin, une audience avec l\’Archonte ! Retrouvons-nous là-bas, $n.', 38134),
(196478, 'frFR', '', 'Des petits ruisseaux aux grands lacs, les eaux reviennent toujours. Bienvenue au bercail.', 38134),
(196477, 'frFR', 'Ohé, par ici ! Nous sommes sauvés !', '', 38134),
(196475, 'frFR', '', 'J\’espère que vous aimez les araignées. Vous avez vu celles qu\’on se coltine ?', 38134),
(196474, 'frFR', '', 'Euh, les araignées vous posent un problème ?', 38134),
(196473, 'frFR', '', 'Eh bien, présenté comme ça…', 38134),
(196472, 'frFR', '', 'Ces araignées se sont fait détruire leur foyer. Elles ne méritaient pas ça.', 38134),
(196467, 'frFR', '', 'Je vous remercie. Montez sur mon dos, allez ! Il faut se mettre à l\’abri !', 38134),
(196465, 'frFR', 'J\’ai arraché une partie de leur anima ! Il vous soignera si vous approchez assez.', '', 38134),
(196464, 'frFR', 'Sauvez tous les Kyrians que vous pourrez !', '', 38134),
(196462, 'frFR', 'Trouvez vite les pièces pour que le colosse puisse nous aider à éliminer leurs saletés volantes !', '', 38134),
(196461, 'frFR', 'Restez bien sur le qui-vive. Notre ennemi n\’a rien d\’honorable.', '', 38134),
(196460, 'frFR', 'Mes amis, j\’amène des renforts !', '', 38134),
(196459, 'frFR', 'Sacrilège ! Intervenez, mortel et reprenez le temple !', '', 38134),
(196458, 'frFR', 'Vous et les vôtres serez les premiers. Mais pas les derniers.', '', 38134),
(196457, 'frFR', 'Je n\’ai rien rompu. Mon maître souhaite voir le Bastion détruit et les cadavres des Kyrians rejoindre nos armées.', '', 38134),
(196452, 'frFR', 'Vous avez rompu l\’ancien serment de Maldraxxus, liche ! Justice sera faite !', '', 38134),
(196449, 'frFR', 'Je suis heureux de voir quelqu\’un. Les Maldraxxi ont attaqué sans crier gare !', '', 38134),
(196444, 'frFR', 'Si tel est le cas, alors peut-être que… Par l\’Archonte ! Kléia, regarde !', '', 38134),
(196442, 'frFR', '', 'Il semble qu\’il ait été tenu dans l\’ignorance de leur alliance avec l\’Antre. En va-t-il de même avec les autres désavoués ?', 38134),
(196441, 'frFR', 'Le désavoué qui a détruit le sceau et celui des souvenirs de Lysonia ne font qu\’un…', '', 38134),
(196440, 'frFR', 'Cassius va vous fournir un volion. Dépêchez-vous !', '', 38134),
(196439, 'frFR', '', 'C\’est vrai. Mais pour l\’heure, nous pouvons déposer le bâton du Primat dans notre sanctuaire et savourer cette victoire. Sans-Entraves, vos exploits résonneront pour l\’éternité.', 38134),
(196438, 'frFR', 'Nous sommes loin d\’en avoir fini. Vyraz doit payer pour sa fourberie, et Kel\’Thuzad avec lui.', '', 38134),
(196437, 'frFR', '', 'J\’admets avoir d\’abord douté de vous, Sans-Entraves, mais c\’est une aide inestimable que vous apportez à la congrégation.', 38134),
(196436, 'frFR', '', 'Kel\’Thuzad échappe à notre vindicte mais son ombre ne planera plus sur Maldraxxus. Et nous avons gagné une alliée singulière en Sin\’dane.', 38134),
(196435, 'frFR', 'Les forts survivent, les faibles sont abattus !', '', 38134),
(196434, 'frFR', 'Quelle technique inouïe ! Quelle puissance !', '', 38134),
(196433, 'frFR', 'Quel coup ! Bravo !', '', 38134),
(196430, 'frFR', '', 'Je sens des vrilles de magie liant cette rune à la tour. Voilà qui devrait nous y conduire.', 38134),
(196429, 'frFR', '', 'Des secours ! Ô Terre-Mère, guide leurs pas !', 38134),
(196423, 'frFR', '', 'Par là !', 38134),
(196422, 'frFR', '', 'La dernière fois que j\’ai aperçu Anduin, ils le traînaient vers la tour, là-haut. Il faut trouver comment l\’atteindre.', 38134),
(196421, 'frFR', 'Voici le Tremaculum. C\’est ici qu\’ils tourmentent les âmes pour en faire ces ombres pitoyables.', '', 38134),
(196420, 'frFR', '', 'Et oubliez pas, ce rituel, faut l\’arrêter !', 38134),
(196419, 'frFR', '', 'Vous êtes le dernier espoir de ces pauvres survivants. Sans vouloir vous mettre la pression, hein.', 38134),
(196418, 'frFR', 'À chaque fois que je plonge le regard dans Tourment, je sens comme des yeux malveillants qui m\’observent à leur tour. Le risque est toujours plus grand, mais il faut persévérer.', '', 38134),
(196417, 'frFR', 'Ce que vous me demandez est... terriblement dangereux. Pour nous tous.', '', 38134),
(196416, 'frFR', 'Bolvar. Vous qui avez sondé Tourment pour y guider l\'âme sans-entraves.', '', 38134),
(196415, 'frFR', '', 'Mieux vaut oublier tout ça. Du moins, pour l\’instant.', 38134),
(196414, 'frFR', 'Les choses que j\’ai pu voir dans cet endroit. Les tortures que j\’ai subies…', '', 38134),
(196413, 'frFR', '', 'Je craignais le pire. Baine et moi savons de quelles horreurs le Geôlier est capable.', 38134),
(196412, 'frFR', 'Thrall, louée soit la Terre-Mère ! Je suis heureux de vous revoir, mon frère.', '', 38134),
(196410, 'frFR', '', 'Une fosse ténébreuse débordante d\’énergie encore plus ténébreuse. Je crois qu\’on a trouvé.', 38134),
(196409, 'frFR', '', 'Bon, voyons si on peut trouver l\’une de ces installations rituelles…', 38134),
(196404, 'frFR', '', 'Appliquez le poison sur votre arme avant de prendre Bidegruk en embuscade. Réglez… lui… son compte…', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196403, 'frFR', 'Manier une telle puissance a un prix. Il nous faudra les cendres d\’âmes perdues dans la tour. Allez toujours plus loin et rapportez les vestiges des âmes égarées.', '', 38134),
(196401, 'frFR', '', 'Vous pouvez extraire un puissant poison de ces racines. Ramassez-en pendant que l\’ours a le dos tourné !', 38134),
(196397, 'frFR', 'Drizza a utilisé son camouflage pour se faufiler dans des endroits vraiment dangereux. Suivez son exemple.', '', 38134),
(196390, 'frFR', '', 'C\’est quand on combat tous ensemble que la Horde est forte. Et maintenant, dis-moi ce qui s\’est passé ici.', 38134),
(196389, 'frFR', '', 'Mère… j\’ai tenté d\’arrêter les Ogres par moi-même, mais j\’ai échoué. Je ne suis pas assez forte.', 38134),
(196386, 'frFR', 'Selon le Dessein, c\’est à moi de m\’en charger, oui.\n\nComme vous, les négociants sont de passage à Oribos. Ils nous prennent juste un peu plus de temps.', '', 38134),
(196383, 'frFR', 'Salut, la compagnie. Bourboss à la rescousse !', '', 38134),
(196382, 'frFR', 'Le parfum… de la liberté.', '', 38134),
(196377, 'frFR', 'Mais vous êtes libre de vos mouvements, Sans-Entraves. Vous pourriez… me libérer ? M\’aider ? Oui. Comme je vous aiderai. Nous sommes liés par le destin.', '', 38134),
(196376, 'frFR', 'Il m\’a utilisé avant de m\’abandonner ici. Depuis… depuis quand, déjà ? Peu importe. La mort n\’est pas sujette au passage du temps.', '', 38134),
(196375, 'frFR', 'LUI s\’est servi de moi. Le Geôlier, comme il aime se faire appeler. Pour fabriquer… pour fabriquer… je ne sais plus. Il m\’a arraché mes souvenirs.', '', 38134),
(196374, 'frFR', 'Une créature mortelle, hein ? Qui se promène dans l\’Antre et vient jusqu\’à ma prison pour… quoi, me libérer ? Se servir de moi ?', '', 38134),
(196373, 'frFR', 'Vous faites partie de ses sbires ? Non, non… vous êtes… autre chose.', '', 38134),
(196372, 'frFR', '', 'Venthyrs, l\’heure est venue de jeter bas vos oppresseurs !', 38134),
(196371, 'frFR', 'Gardes ! Tuez la baronne !', '', 38134),
(196370, 'frFR', '', 'Saleté d\’Ogre. Allons voir si Shuja va bien.', 38134),
(196369, 'frFR', '', 'Maman… J\’ai échoué…', 38134),
(196368, 'frFR', '', 'Tiens bon, Shuja ! Vous, avec moi ! Lok\’tar ogar !', 38134),
(196348, 'frFR', 'Euh… vous vous souvenez de cette histoire de risques et périls ? Eh bien, on dirait que cette machine est sur le point… et bien, d\’exploser.', '', 38134),
(196342, 'frFR', 'Et en faisant sauter tous les zombies, rappelez-vous bien que c\’est grâce à mon modèle déposé ! Mmh ?!', '', 38134),
(196341, 'frFR', 'Ouah, alors ÇA ça me plaît ! Bon, retournons voir Hache-Sinistre.', '', 38134),
(196340, 'frFR', 'Ouah ! Avec un coup pareil, pas besoin de sanglier géant ! Mais bon, réessayez comme ça.', '', 38134),
(196337, 'frFR', '', 'Je défendrai ce bosquet au péril de ma vie. Tel est mon devoir sacré. Qu\’est-ce qui vous amène à Tirna Vaal ?', 38134),
(196336, 'frFR', '', 'Les Yeux ont marqué une liche qui rôde autour des nids des ailes-écorchées. Trouvez-la et éliminez-la.', 38134),
(196334, 'frFR', '', 'Avec les nouvelles recrues, il va falloir de nouvelles montures. Et il faut être particulièrement courageux ou idiot pour capturer une aile-écorchée.', 38134),
(196333, 'frFR', '', 'Une liche abjecte de moins. Bon débarras…', 38134),
(196332, 'frFR', 'Je ne vous décevrai pas, ma margrave.', '', 38134),
(196331, 'frFR', '', 'Certes. Pour expier cette faute, il est donc juste que vous… deveniez mon baron. À compter de ce jour, vous ferez le relais entre la baronne Draka et moi.', 38134),
(196330, 'frFR', 'Je ne voulais que démasquer le traître Kel\’Thuzad et rendre gloire à votre règne.', '', 38134),
(196329, 'frFR', 'Non, ça ne va pas. Voyons… Suffit juste de… voilà ! Réessayez.', '', 38134),
(196328, 'frFR', 'Bon, moi j\’ai promis un sanglier géant. Alors il va falloir en dégoter un.', '', 38134),
(196327, 'frFR', '', 'Balmedar. Vous avez conspiré avec des forces extérieures pour défier votre maison… et votre margrave.', 38134),
(196322, 'frFR', '', 'Kel\’Thuzad est vaincu, mais vos troupes sont encore là. Aurais-je donc à craindre que vous cherchiez à me déposer ?', 38134),
(196314, 'frFR', 'Il faut détruire les cristaux de stabilisation du bouclier pour pouvoir arriver à lui.', '', 38134),
(196312, 'frFR', 'En avant ! Pour Maldraxxus et pour le Bastion ! Pour l\’Ombreterre !', '', 38134),
(196311, 'frFR', '', 'J\’ai à faire. Vous pouvez demander à votre régisseur de réparer la balise.', 38134),
(196306, 'frFR', '', 'Faites ce qu\’il faudra, mais vite. C\’est la vie de ma fille qui est en jeu.', 38134),
(196305, 'frFR', 'Hé, je peux vous bricoler tout ce qu\’il vous faut. Que diriez-vous… d\’un sanglier géant ?', '', 38134),
(196304, 'frFR', '', 'Personne ne fuit quand nos frères ont besoin de nous ! Trouvez-nous un moyen de gérer ces morts-vivants.', 38134),
(196303, 'frFR', 'Ils raniment un dragon ?! Oh non… Et, euh, il est trop tard pour fuir ?', '', 38134),
(196302, 'frFR', 'Ah, c\’est vous qui étiez au temple de la Pureté… Fort bien, venez.', '', 38134),
(196301, 'frFR', '', 'Marchedroit. Nous avons des nouvelles urgentes pour l\’Archonte.', 38134),
(196300, 'frFR', 'Aspirants, pourquoi avez-vous allumé le signal ?', '', 38134),
(196296, 'frFR', '', 'Plus un bruit ! J\’aperçois quelque chose !', 38134),
(196295, 'frFR', 'Euh, c\’est encore allumé ? Ohé, l\’$uami:amie;, je vous ai dit que vous utilisiez ce truc à vos risques et périls !', '', 38134),
(196293, 'frFR', 'Il me faut un plan de secours si mon appareil laisse tomber votre recrue comme une pierre.', '', 38134),
(196292, 'frFR', '', 'Il y a une caméra ? Pourquoi est-ce qu\’il fallait envoyer quelqu\’un là-haut, alors ?', 38134),
(196290, 'frFR', '$n, vous m\’entendez ? On dirait que la caméra fonctionne parfaitement !', '', 38134),
(196287, 'frFR', 'Parfait ! Ça manque un peu de finition, mais ça fera le boulot.', '', 38134),
(196286, 'frFR', 'Bon, j\’y ai peut-être été un peu fort. Attendez…', '', 38134),
(196285, 'frFR', 'J\’ai une version miniaturisée du propulseur variable modèle V. Une seconde, je lui redonne sa taille…', '', 38134),
(196277, 'frFR', 'À l\’aide ! Les spriggans nous ont pris notre anima et ils ont capturé mes amis !', '', 38134),
(196272, 'frFR', 'Aucune idée… mais je sais comment le découvrir !', '', 38134),
(196271, 'frFR', '', 'On n\’est pas encore tirés d\’affaire. Où sont les autres ?', 38134),
(196270, 'frFR', 'Vous devez être Breka, la mère de Shuja ! Depuis le début, elle dit que vous allez nous sauver.', '', 38134),
(196266, 'frFR', 'La bataille n\’est pas encore perdue.', '', 38134),
(196265, 'frFR', 'Et voilà pourquoi je parie toujours sur la Horde ! Allez, on se taille !', '', 38134),
(196264, 'frFR', 'Oh, vous allez voir, ça va barder !', '', 38134),
(196263, 'frFR', '', 'Oui, espérons. Mais elle n\’est jamais très prudente.', 38134),
(196262, 'frFR', 'Shuja ? Aucune nouvelle. Elle a peut-être réussi à s\’enfuir.', '', 38134),
(196261, 'frFR', '', 'En avant. Ces monstres ont enlevé ma fille.', 38134),
(196260, 'frFR', 'Quand je vous aurai $uanéanti:anéantie;, ça me fera une nouvelle arme pour ma collection.', '', 38134),
(196258, 'frFR', 'Ah, vous voilà. Ces barbares de Hurans conduisent un rituel assez sinistre.', '', 38134),
(196257, 'frFR', '', 'Confiez-moi vos problèmes.', 38134),
(196253, 'frFR', '', 'La Horde n\’abandonne jamais ses soldats… et moi, je n\’abandonne pas ma fille.', 38134),
(196252, 'frFR', '', 'À la revoyure, alors. Votre fille a raison de compter sur vous.', 38134),
(196250, 'frFR', '', 'Le margrave va vous dévorer !', 38134),
(196249, 'frFR', 'Hum, quelle constitution inhabituelle. Quelle est cette créature ? Peu importe, je vois quoi en faire.', '', 38134),
(196246, 'frFR', '', 'Voilà Anduin !', 38134),
(196235, 'frFR', '', 'Allons au nord pour voir ce que les autres ont trouvé. Won\’sa, récupérez ici avant de nous rejoindre.', 38134),
(196233, 'frFR', 'Les sutures ne doivent pas être trop serrées, sans quoi l\’âme se retrouve prisonnière de l\’assemblage.\n\nCe dernier n\’est jamais qu\’un contenant doublé d\’un instrument. Ce qui compte, c\’est la façon dont l\’âme s\’en sert.', '', 38134),
(196232, 'frFR', 'La victoire et la gloire vous appartiennent ! Puissiez-vous toujours combattre, Sans-Entraves !', '', 38134),
(196231, 'frFR', 'Mais oui, Mordretha est vaincue ! J\’en crois à peine mes yeux ! Célébrez les nouveaux champions de l\’arène !', '', 38134),
(196230, 'frFR', 'Nos nouvelles coqueluches vont maintenant affronter la championne au plus grand palmarès du théâtre de la Souffrance, j\’ai nommé Mordretha, l\’impératrice immortelle !', '', 38134),
(196229, 'frFR', 'Je suis prête à adopter la forme d\’un nouvel assemblage.', '', 38134),
(196223, 'frFR', 'Vous trouvez une âme partiellement intégrée à une arme.', 'Vous trouvez une âme partiellement intégrée à une arme.', 38134),
(196220, 'frFR', 'Une créature est en chasse. Faites attention.', '', 38134),
(196218, 'frFR', 'Soldats de Maldraxxus ! Êtes-vous prêts pour le carnage ?', '', 38134),
(196215, 'frFR', 'Retournez dans la pièce centrale ! C\’est l\’heure de l\’évènement principal !', '', 38134),
(196208, 'frFR', 'Toujours à l\’affût !', '', 38134),
(196206, 'frFR', 'Le parangon est là, de l\’autre côté du temple… où tant d\’âmes attendent leur tour.', '', 38134),
(196205, 'frFR', '', 'Vous avez cinq points de combo, utilisez Éviscération pour en finir !', 38134),
(196204, 'frFR', '', 'Tâchez d\’accumuler cinq points de combo avant de lancer Éviscération.', 38134),
(196203, 'frFR', '', 'Pas mal, mais Éviscération est bien plus puissante avec plus de points de combo. Réessayez !', 38134),
(196202, 'frFR', '', 'Beau travail. Éviscération dépense tous vos points de combo. Pour la prochaine, utilisez les cinq !', 38134),
(196201, 'frFR', '', 'Vous avez au moins quatre points de combo. Utilisez Éviscération !', 38134),
(196200, 'frFR', '', 'Les attaques normales ne vous mèneront pas loin. Utilisez Attaque pernicieuse !', 38134),
(196199, 'frFR', '', 'Vous avez au moins trois points de combo. Utilisez vite Éviscération !', 38134),
(196198, 'frFR', '', 'Bien ! Les dégâts d\’Éviscération augmentent avec les points de combo. Recommencez, mais avec quatre !', 38134),
(196197, 'frFR', '', 'Utilisez Attaque pernicieuse aussi souvent que possible pour gagner des points de combo !', 38134),
(196196, 'frFR', '', 'Attaque pernicieuse vous donne des points de combo. Lancez Éviscération quand vous en avez au moins trois !', 38134),
(196194, 'frFR', 'En quoi puis-je vous aider ?', '', 38134),
(196193, 'frFR', '', 'Cause perdue, perte cossue.\n\nRage de la cage, âge du sage.\n\n<Sybille l\’égarée vous pointe du doigt et éclate d\’un rire hystérique.>', 38134),
(196192, 'frFR', 'Incroyable ! Nos combattants ont réussi les épreuves et progressé jusqu\’au dernier tour !', '', 38134),
(196191, 'frFR', '', 'Vous pensiez vraiment que votre présence passerait inaperçue ? Vous ne repartirez pas d\’ici !', 38134),
(196182, 'frFR', 'Je suis à vos ordres !', 'Je suis à vos ordres !', 38134),
(196180, 'frFR', 'Vous êtes un sacré numéro, vous. Quel numéro, j\’en sais rien, mais un sacré. Qu\’est-ce que vous voulez ?', '', 38134),
(196179, 'frFR', 'Oh. Quelqu\’un a pénétré dans mon domaine.', '', 38134),
(196178, 'frFR', 'Qu\’y a-t-il, vous avez perdu votre chemin ? L\’obscurité est... menaçante...', '', 38134),
(196174, 'frFR', 'J\’ai aperçu cet endroit dans ma vision de Tourment. Dame Portvaillant n\’est plus loin.', '', 38134),
(196173, 'frFR', 'Ici, les âmes sont condamnées à brûler et fondues pour servir de matière aux créations du Geôlier.', '', 38134),
(196169, 'frFR', 'Ah ! Je… Je vous ai $uvu:vue; réussir à vous échapper avant qu\’ils nous reprennent. Sauriez-vous comment sortir de l\’Antre ?', '', 38134),
(196168, 'frFR', 'Je flottais dans un doux rêve… et je me réveille dans cet endroit de malheur.', '', 38134),
(196167, 'frFR', 'Qu\’est-ce qu\’il vous faut ? Un en-cas ? Un coup de main ?', 'Qu\’est-ce qu\’il vous faut ? Un en-cas ? Un coup de main ?', 38134),
(196166, 'frFR', 'Demandez-moi ce que vous voudrez !', 'Demandez-moi ce que vous voudrez !', 38134),
(196165, 'frFR', 'Que puis-je faire pour vous ?', 'Que puis-je faire pour vous ?', 38134),
(196146, 'frFR', 'Ce doit être le baron que vous cherchez, $n.', '', 38134),
(196140, 'frFR', 'Combien de temps ai-je passé prisonnier de l\’Antre ? Un jour ? Une éternité ?', '', 38134),
(196139, 'frFR', 'Merci, $p. Maintenant que je suis libre, mes forces devraient vite me revenir.', '', 38134),
(196138, 'frFR', '', 'Trait de feu se lance instantanément, c\’est un sort parfait quand l\’adversaire est sur vous.', 38134),
(196137, 'frFR', '', 'Tout ça est prometteur. Essayons une dernière fois avant que vous alliez affronter les Hurans.', 38134),
(196136, 'frFR', '', 'Bien ! Comme Trait de feu est instantané, il est parfait en mêlée. Allez, encore une fois !', 38134),
(196134, 'frFR', '', 'Trait de feu est une incantation instantanée. Utilisez-le quand l\’ennemi est sur vous.', 38134),
(196133, 'frFR', '', 'Restez à distance et bombardez votre ennemi avec Éclair de givre.', 38134),
(196132, 'frFR', '', 'Lancez Éclair de givre pour tenir l\’adversaire à distance.', 38134),
(196131, 'frFR', '', 'Éclair de givre ralentit l\’ennemi. Continuez à le lancer jusqu\’à ce que ce dernier arrive à votre portée.', 38134),
(196130, 'frFR', '', 'Utilisez Frappe primordiale au lieu d\’Éclair quand l\’ennemi est sur vous !', 38134),
(196128, 'frFR', '', 'Montrez-moi ce que vous valez à distance une dernière fois. C\’est un avantage face à ceux qui ne frappent que de près.', 38134),
(196127, 'frFR', '', 'Dominez l\’adversaire avec des attaques de mêlée. Vous apprenez vite !', 38134),
(196126, 'frFR', '', 'Encore ! Continuez avec Frappe primordiale quand l\’adversaire est sur vous.', 38134),
(196125, 'frFR', '', 'Profitez de la distance pour attaquer votre adversaire avec Éclair !', 38134),
(196124, 'frFR', '', 'Le temps que votre adversaire coure jusqu\’à vous, vous pouvez lui infliger de beaux dégâts !', 38134),
(196122, 'frFR', '', 'Très bien. Maintenant, essayons d\’utiliser à nouveau Éclair. Attention…', 38134),
(196121, 'frFR', '', 'Un régisseur va vous aider à invoquer le marchedroit Adrestès. Choisissez-en un pour compagnon.', 38134),
(196120, 'frFR', '', 'Beau travail. Utilisez Frappe primordiale quand vous êtes à distance de mêlée !', 38134),
(196119, 'frFR', '', 'Bien. Utilisez Éclair contre vos ennemis quand ils sont loin de vous.', 38134),
(196118, 'frFR', 'Ça n\’a pas pu empirer depuis ma dernière visite…', '', 38134),
(196115, 'frFR', 'Méfiez-vous des doutes qui vous assaillent. Il y a un problème.', '', 38134),
(196114, 'frFR', 'Encore une épreuve réussie ! Il semblerait que nous ayons de bons candidats, cette fois !', '', 38134),
(196113, 'frFR', 'Je sens quelque chose pas loin d\’ici !', '', 38134),
(196110, 'frFR', 'Il y en a quelque part par là !', '', 38134),
(196109, 'frFR', 'Par les grigris du grand tout gris, montrez-vous !', '', 38134),
(196106, 'frFR', '', 'Bien, essayons à nouveau. Éclat lunaire et Colère vous aideront à vaincre les Hurans.', 38134),
(196105, 'frFR', '', 'Vous avez utilisé Éclat lunaire trop tôt. Pour optimiser ses dégâts, il faut le relancer quand il est presque terminé.', 38134),
(196104, 'frFR', '', 'Éclat lunaire est en train de disparaître, relancez-le !', 38134),
(196102, 'frFR', '', 'Éclat lunaire inflige des dégâts sur la durée. Utilisez Colère quand votre victime subit ses effets.', 38134),
(196101, 'frFR', '', 'Beau travail. Continuez à utiliser Colère pendant qu\’Éclat lunaire inflige ses dégâts, je vous dirai quand le relancer.', 38134),
(196099, 'frFR', '', 'Utilisez Éclat lunaire au début du combat. Il inflige des dégâts sur la durée.', 38134),
(196097, 'frFR', 'Il y a forcément un traître parmi nous. Quelqu\’un qui connaissait le moment idéal pour attaquer le convoi.', 'Il y a forcément un traître parmi nous. Quelqu\’un qui connaissait le moment idéal pour attaquer le convoi.', 38134),
(196096, 'frFR', 'Il y a quelque chose de louche dans tout ça.', 'Il y a quelque chose de louche dans tout ça.', 38134),
(196095, 'frFR', '', 'Bien, on y retourne. Je dois m\’assurer que vous saurez vous débrouiller face aux Hurans.', 38134),
(196094, 'frFR', '', 'Vous avez utilisé Corruption trop tôt. Il faut la relancer quand elle est presque terminée, pour optimiser ses dégâts.', 38134),
(196093, 'frFR', '', 'Corruption est en train de disparaître, relancez-la !', 38134),
(196091, 'frFR', '', 'Corruption inflige des dégâts sur la durée.', 38134),
(196090, 'frFR', '', 'Continuez ! Les démonistes peuvent infliger des dégâts immenses s\’ils gèrent bien leur Corruption.', 38134),
(196088, 'frFR', 'Nos compétiteurs ont réussi la première épreuve ! Mais il leur reste beaucoup à faire pour décrocher le titre !', '', 38134),
(196086, 'frFR', 'Pas… d\’espoir ! Pas… de pitié !', '', 38134),
(196085, 'frFR', '', 'Utilisez Corruption au début du combat. Elle inflige des dégâts sur la durée.', 38134),
(196084, 'frFR', 'Allez, parle !', '', 38134),
(196083, 'frFR', 'Alors notre objectif est clair.', '', 38134),
(196082, 'frFR', '', 'Le chaudron en question se trouve de l\’autre côté de la rivière des âmes. C\’est là que Baine doit être détenu.', 38134),
(196081, 'frFR', 'Avez-vous compris quelque chose, Jaina ?', '', 38134),
(196080, 'frFR', 'Personne… ne s\’échappe… de l\’Antre…', '', 38134),
(196079, 'frFR', 'Non ! Elle est… inerte… brisée !', '', 38134),
(196078, 'frFR', 'Pierre d\’accès… dans le Chaudron… peut-être…', '', 38134),
(196077, 'frFR', 'Alors, il y a bel et bien une sortie ! Dites-nous où la trouver !', '', 38134),
(196076, 'frFR', 'Peine perdue ! Le passage… fermé… depuis longtemps…', '', 38134),
(196075, 'frFR', 'Parlez ! Comment quitte-t-on cet endroit ?', '', 38134),
(196074, 'frFR', 'Notre ami a des choses à nous dire.', '', 38134),
(196073, 'frFR', 'Ah, vous arrivez juste à temps.', '', 38134),
(196072, 'frFR', '', 'Tous… serviront… le Geôlier…', 38134),
(196071, 'frFR', '', 'Nous allons vous briser !', 38134),
(196070, 'frFR', 'Je me porterai mieux quand nous aurons quitté cet endroit. Je vous suis.', '', 38134),
(196069, 'frFR', 'Merci, $n. J\’ai bien cru ne plus jamais échapper à ces chaînes.', '', 38134),
(196068, 'frFR', 'Il n\’y a pas de honte à devoir se reposer un peu. Vous êtes $umortel:mortelle;, après tout.', '', 38134),
(196061, 'frFR', '', 'Vos sorts peuvent se lancer à distance. Profitez-en pour frapper l\’ennemi en premier !', 38134),
(196060, 'frFR', '', 'Bravo, encore une fois ! Mot de l\’ombre : Douleur continuera à infliger des dégâts à votre adversaire pendant que vous lui assénez Châtiment.', 38134),
(196059, 'frFR', '', 'Vous avez utilisé Mot de l\’ombre : Douleur trop tôt. Pour optimiser ses dégâts, il faut le relancer quand il est presque terminé.', 38134),
(196058, 'frFR', '', 'Mot de l\’ombre : Douleur est en train de disparaître, relancez-le !', 38134),
(196052, 'frFR', '', 'Bien, encore une fois ! Bien employé, Mot de l\’ombre : Douleur mine peu à peu vos adversaires.', 38134),
(196051, 'frFR', '', 'Bien ! Utilisez Châtiment pendant que Mot de l\’ombre : Douleur inflige ses dégâts. Je vous dirai quand relancer ce dernier.', 38134),
(196050, 'frFR', '', 'Utilisez Mot de l\’ombre : Douleur au début du combat. Il inflige des dégâts sur la durée.', 38134),
(196032, 'frFR', '', 'Vous avez assez de puissance sacrée pour en finir, utilisez Bouclier du vertueux !', 38134),
(196031, 'frFR', '', 'Beau combat ! Vous n\’avez plus de puissance sacrée, utilisez Frappe du croisé pour en générer !', 38134),
(196030, 'frFR', '', 'Pas si mal. Frappe du croisé permet de gagner de la puissance sacrée.', 38134),
(196029, 'frFR', '', 'Vous avez assez de puissance sacrée pour lancer Bouclier du vertueux !', 38134),
(196028, 'frFR', '', 'Bravo. Pour un paladin, le but est d\’accumuler la puissance sacrée rapidement pour la déchaîner contre l\’ennemi.', 38134),
(196027, 'frFR', '', 'Encore ! Continuez à lancer Frappe du croisé pour gagner de la puissance sacrée !', 38134),
(196026, 'frFR', '', 'Frappe du croisé fait monter votre puissance sacrée. C\’est important pour lancer des attaques puissantes !', 38134),
(196025, 'frFR', '', 'Vous rayonnez de puissance sacrée, utilisez vite Bouclier du vertueux !', 38134),
(196024, 'frFR', '', 'Les attaques normales ne vous mèneront pas loin. Utilisez Frappe du croisé !', 38134),
(196022, 'frFR', '', 'Encore. À la Charge !', 38134),
(196021, 'frFR', '', 'Chargez à nouveau sur moi pour casser la distance ! Il faut toujours commencer le combat comme ça.', 38134),
(196020, 'frFR', '', 'Lancez le combat avec Charge. Déchaînez la fureur de la Horde !', 38134),
(196015, 'frFR', 'Je peux vous aider !', 'Je peux vous aider !', 38134),
(196003, 'frFR', 'J\’ai à manger, à boire et des objets spéciaux pour vous aider dans votre entreprise.\n\nAlors, que puis-je pour vous ?', '', 38134),
(195999, 'frFR', '', 'Assez ! Vous êtes $uparé:parée; à affronter les Hurans. Allons sauver nos frères !', 38134),
(195997, 'frFR', '', 'Bien combattu ! Charge donne de la rage et blesse la cible. Débutez toujours vos combats avec.', 38134),
(195994, 'frFR', '', 'Heurtoir dépense un peu de rage pour infliger des dégâts. Tant qu\’il vous en reste, vous pouvez l\’utiliser !', 38134),
(195993, 'frFR', '', 'Pour un guerrier, il vaut mieux ouvrir le combat avec Charge. Elle donne de la rage pour utiliser d\’autres techniques.', 38134),
(195992, 'frFR', '', 'Vos attaques normales ne suffiront pas, utilisez Heurtoir !', 38134),
(195990, 'frFR', 'Commençons à aligner votre voie sur le Dessein. Pensez-vous en être capable ?', '', 38134),
(195989, 'frFR', '', 'Chargez vers moi, allez !', 38134),
(195988, 'frFR', '', 'Je comprends, Kléia. Nous devons contacter le marchedroit. Il peut vous présenter à l\’Archonte.', 38134),
(195985, 'frFR', '', 'En avant pour la nouvelle leçon !', 38134),
(195981, 'frFR', '', 'Les Hurans sont bien plus dangereux que les Murlocs. Un vrai soldat de la Horde doit être prêt à les affronter.', 38134),
(195980, 'frFR', '', 'Et puis, elle sait que dans la Horde, on n\’abandonne pas les soldats.', 38134),
(195979, 'frFR', '', 'Rien au monde n\’aurait pu m\’empêcher de retrouver ma fille.', 38134),
(195978, 'frFR', '', 'Shuja savait… que vous viendriez…', 38134),
(195977, 'frFR', 'Je ne peux pas garantir votre sécurité de l\’autre côté de la porte Antique.', 'Je ne peux pas garantir votre sécurité de l\’autre côté de la porte Antique.', 38134),
(195975, 'frFR', '', 'Merci à vous, Thiernax. Et à vous, Qadarin. Vous avez bien de la chance de vous avoir.', 38134),
(195972, 'frFR', '', 'J\’espère seulement qu\’elle sera d\’accord pour s\’en séparer.', 38134),
(195971, 'frFR', '', 'Alexstrasza a dû conserver un souvenir de Korialstrasz. Une marque d\’amour perdu.', 38134),
(195969, 'frFR', '', 'Won\’sa !', 38134),
(195968, 'frFR', '', 'J\’ai… faim…', 38134),
(195959, 'frFR', 'Je vais préparer des centurions pour une attaque !', '', 38134),
(195958, 'frFR', '', 'Vous trois, cherchez d\’autres rescapés. Quant à vous, $n, vous venez avec moi.', 38134),
(195957, 'frFR', '', 'Ces informations doivent à tout prix parvenir à l\’Archonte. Retrouvez-moi au Repos du héros, $n !', 38134),
(195953, 'frFR', 'Allez-y, mortels ! Je vais la retenir !', '', 38134),
(195952, 'frFR', 'Pas aujourd\’hui, Kaal !', '', 38134),
(195949, 'frFR', '', 'Assez ! Votre souffrance sera un criant rappel de la colère du Maître.', 38134),
(195948, 'frFR', '', 'Crétins de rebelles… Préparez-vous à mourir !', 38134),
(195947, 'frFR', '', 'Ces salles seront votre tombeau !', 38134),
(195946, 'frFR', '', 'Cette prisonnière appartient au Maître. Vous ne l\’emmènerez pas !', 38134),
(195945, 'frFR', '', 'Gardes ! Tuez-les !', 38134),
(195943, 'frFR', '', 'Alors, c\’est elle que vous êtes venus chercher. Elle ne quittera jamais cette prison !', 38134),
(195942, 'frFR', 'Vite, mortels ! Emmenez Z\’rali !', '', 38134),
(195940, 'frFR', 'Denathrius se sert de sa Lumière contre ses adversaires. Et nous, nous allons la retourner contre lui.', '', 38134),
(195939, 'frFR', 'La prisonnière est là, en bas. Z\’rali, l\’une des Naaru qui ont envahi la province des Braises. Les autres la croyaient morte.', '', 38134),
(195937, 'frFR', 'Le plus grand, là, ce doit être l\’alpha. Tuez-le et les autres se disperseront.', '', 38134),
(195932, 'frFR', 'Cette tempête n\’était pas naturelle, croyez-moi.', '', 38134),
(195931, 'frFR', '', 'Prudence, Sans-Entraves, ils ont réussi à infiltrer nos lignes.', 38134),
(195930, 'frFR', 'Merci bien. Mais qu\’est donc devenue l\’expédition ?', '', 38134),
(195924, 'frFR', '', 'Belle tempête. Elle a dû frapper le premier navire aussi.', 38134),
(195920, 'frFR', '', 'Récupérez le matériel, mais n\’affrontez pas trop de Murlocs d\’un coup. En groupe, ils sont dangereux.', 38134),
(195918, 'frFR', '', '$n ! Vous êtes en vie ! Il faut rassembler tout le monde.', 38134),
(195917, 'frFR', '', 'Je vais vous anéantir !', 38134),
(195914, 'frFR', '', 'Saignez pour vos crimes.', 38134),
(195913, 'frFR', '', 'Mourez tous, traîtres.', 38134),
(195910, 'frFR', 'Cette pierre est un repère pour les esprits qui ont été détachés.\n\n<Le scribe du destin semble vous jauger un instant.>\n\nJe perçois l\’aura d\’un grand pouvoir sur vous, $p. Vous avez dû affronter la fin de votre existence à moult reprises, pourtant votre force vous maintient $uancré:ancrée; dans le plan mortel.\n\nPeut-être un appareil comme celui-ci pourra-t-il vous ancrer à l\’Ombreterre, dans le cas où vous succomberiez à un sort funeste.\n\nLe Dessein en décidera. Le Dessein est notre voie.', '', 38134),
(195905, 'frFR', 'Vous verrez que les négociants du coin ont trouvé le moyen de vous donner accès à vos effets personnels. Comment ont-ils appelé cela, déjà… Ah, oui, une banque.\n\nJe dois bien admettre que le concept même de possession nous est totalement étranger, mais eux nous ont toujours assuré être experts en la matière.', '', 38134),
(195903, 'frFR', '', 'On dirait que j\’ai surestimé la taille.', 38134),
(195902, 'frFR', '', 'C\’est l\’âme sans-entraves.', 38134),
(195901, 'frFR', 'C\’est ici que nous avons établi une salle dédiée aux artisans. Pour votre usage, bien entendu.\n\nNous autres, négociants, passons notre existence à voyager et à apprendre auprès des habitants de tous les royaumes que nous visitons. Cela nous permet d\’adapter nos offres aux besoins de notre clientèle.\n\nNos membres se sont efforcés de maîtriser les divers métiers qui semblent avoir de la valeur à vos yeux. Vous verrez, ils sont impatients de vous aider à développer vos talents d\’artisans.', '', 38134),
(195899, 'frFR', 'Bienvenue, $étranger:étrangère;. Nous sommes à votre service.', '', 38134),
(195898, 'frFR', 'Vous trouverez ici certaines des marchandises les plus exceptionnelles qui soient.\n\nMes collègues et moi-même nous spécialisons dans l\’acquisition et la mise à l\’écurie de bêtes exotiques. Vous constaterez également que nous offrons l\’accès à ce que vous appelez la chambre du Vide, ainsi que la possibilité d\’utiliser vos pouvoirs de transmogrification.\n\nEnfin, Ta\’visage, que vous voyez là-bas, peut vous aider à modifier votre apparence. Quitte à sauver le multivers, autant le faire sur votre trente-et-un, non ?', '', 38134),
(195896, 'frFR', 'Bienvenue dans notre bazar. Que pouvons-nous faire pour vous ?', '', 38134),
(195893, 'frFR', '', 'Accrochez-vous bien, tout le monde !', 38134),
(195892, 'frFR', 'Seigneur ! La tempête va bientôt nous emporter !', '', 38134),
(195891, 'frFR', '', 'Le combat nous appelle là-haut. Puissent les flammes du courage guider votre lame.', 38134),
(195888, 'frFR', 'Faites-les payer…', 'Faites-les payer…', 38134),
(195887, 'frFR', 'Tout aurait pu être si… différent…', 'Tout aurait pu être si… différent…', 38134),
(195886, 'frFR', 'Je n\’ai… qu\’un seul regret…', 'Je n\’ai… qu\’un seul regret…', 38134),
(195885, 'frFR', 'Je n\’ai… que ce que… je mérite…', 'Je n\’ai… que ce que… je mérite…', 38134),
(195884, 'frFR', 'Sauvez… les autres…', 'Sauvez… les autres…', 38134),
(195883, 'frFR', 'Je suis… perdu…', 'Je suis… perdue…', 38134),
(195882, 'frFR', 'J\’espérais… tellement plus…', 'J\’espérais… tellement plus…', 38134),
(195881, 'frFR', 'Pardonnez… moi…', 'Pardonnez… moi…', 38134),
(195880, 'frFR', '', 'Je veux filer comme l\’éclair et être infatigable. Je veux pouvoir chasser à travers toute l\’Ombreterre !\n\nDonnez-moi le corps d\’un chien.', 38134),
(195879, 'frFR', '', 'Pouvez-vous m\’aider à atteindre tout mon potentiel, parmi les âmes de ces terres ?', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195878, 'frFR', 'Non. Nous devons tous apporter notre contribution. Et tu en ferais autant à ma place.', 'Non. Nous devons tous apporter notre contribution. Et tu en ferais autant à ma place.', 38134),
(195877, 'frFR', 'Certainement pas, Véronika ! Toi non plus, tu ne peux pas te le permettre. Mihaela doit comprendre que c\’est impossible.', 'Certainement pas, Véronika ! Toi non plus, tu ne peux pas te le permettre. Mihaela doit comprendre que c\’est impossible.', 38134),
(195876, 'frFR', 'Pas de panique. Je vais cotiser pour nous deux.', 'Pas de panique. Je vais cotiser pour nous deux.', 38134),
(195875, 'frFR', 'Encore une dîme à payer ? Mais je n\’ai plus d\’âmes et plus la moindre miette d\’anima. Mihaela ne vous a pas dit de m\’exempter ?', 'Encore une dîme à payer ? Mais je n\’ai plus d\’âmes et plus la moindre miette d\’anima. Mihaela ne vous a pas dit de m\’exempter ?', 38134),
(195873, 'frFR', '', 'Au revoir, revenez vite !', 38134),
(195862, 'frFR', '', 'Nous y voilà.', 38134),
(195859, 'frFR', '', 'Il faudrait faire un peu de rangement. Je vais relancer la forge en un rien de temps.', 38134),
(195858, 'frFR', '', 'Oui, on aide.', 38134),
(195857, 'frFR', 'Je rends les armes ! Vous êtes largement à la hauteur.', '', 38134),
(195856, 'frFR', '', 'Les régisseurs n\’existent que pour nous servir. S\’ils ne peuvent pas s\’acquitter de leurs tâches, ils en sont tout déprimés.', 38134),
(195855, 'frFR', '', 'Réparer la forge ! Réparer la fooorge !', 38134),
(195854, 'frFR', 'Faites toujours face à l\’ennemi !', '', 38134),
(195853, 'frFR', 'Ne fuyez jamais le combat. C\’est la victoire ou la mort !', '', 38134),
(195852, 'frFR', 'Je me mets en place. À vous de frapper, et fort !', '', 38134),
(195851, 'frFR', '', 'Je suis honorée de vous avoir à mes côtés en cette occasion tant attendue…', 38134),
(195844, 'frFR', '', 'Très beau… Tiens, il pleut ? Les chamans n\’avaient pas prévu de pluie.', 38134),
(195835, 'frFR', 'Toi, tu viens avec nous !', '', 38134),
(195834, 'frFR', '', 'On ne sait pas ce qu\’on trouvera sur cette île. Montrez-moi comment vous savez vous battre.', 38134),
(195833, 'frFR', '', 'Hou ! Vite ! C\’est froid !', 38134),
(195832, 'frFR', '', 'Enfin…', 38134),
(195831, 'frFR', '', '<Les yeux fermés, Sophone se concentre sur le moment présent.>', 38134),
(195827, 'frFR', 'C\’est le moment ! Pliez-la à votre volonté !', '', 38134),
(195825, 'frFR', 'Obéir…', '', 38134),
(195824, 'frFR', 'Vous ne pouvez pas me retenir ! Je… vais…', '', 38134),
(195823, 'frFR', 'Lame d\’ébène, à moi ! Anéantissez ce monstre !', '', 38134),
(195822, 'frFR', 'Cette créature va nous donner du fil à retordre. Je demanderai l\’aide de la Lame d\’ébène quand nous attaquerons.', '', 38134),
(195821, 'frFR', 'Il n\’y a rien à en tirer, elles sont trop fragmentées. Il doit y avoir un autre moyen…', '', 38134),
(195820, 'frFR', 'Utilisez le heaume tant qu\’elle est affaiblie !', '', 38134),
(195819, 'frFR', '', 'Aspirante Kléia, avancez-vous.', 38134),
(195818, 'frFR', '', 'Le sol que vous foulez me régénère !', 38134),
(195817, 'frFR', '', 'Engrais de la terre, nourris-moi une fois encore !', 38134),
(195816, 'frFR', '', 'Je suis désolée… Je n\’ai pas pu te sauver…', 38134),
(195815, 'frFR', '', 'Maudites bêtes !', 38134),
(195814, 'frFR', '', 'Les fleurs éclosent à nouveau pour vous chasser de cet endroit !', 38134),
(195813, 'frFR', '', 'Vous êtes à ma portée !', 38134),
(195812, 'frFR', '', 'Que mes branches vous enveloppent !', 38134),
(195811, 'frFR', '', 'Qui… est là ?', 38134),
(195810, 'frFR', '', 'Les ténèbres obscurcissent peut-être ma vision, mais mes racines sont solides et vous ne m\’empêcherez pas de défendre ces terres !', 38134),
(195795, 'frFR', '', 'C\’est fou tout ce que l\’on peut trouver dans l\’Antre. J\’ai un don pour identifier les bonnes affaires potentielles.', 38134),
(195790, 'frFR', 'Tu t\’es toujours moqué de ton peuple. Tu ne veux que le pouvoir !', '', 38134),
(195789, 'frFR', '', 'À très bientôt, Sans-Entraves.', 38134),
(195788, 'frFR', '', 'J\’espère que vous ne profitez tout de même pas trop de mon hospitalité. C\’est qu\’il y a du travail à faire.', 38134),
(195787, 'frFR', 'Fais bien attention, père du sommeil !', '', 38134),
(195785, 'frFR', 'Tu pourras pas me retenir pour toujours !', '', 38134),
(195784, 'frFR', 'Tu n\’es pas encore débarrassé de moi, mon vieux Loa !', '', 38134),
(195783, 'frFR', 'Dépêchez-vous ! On oublie le prix pour l\’instant.', '', 38134),
(195780, 'frFR', 'Salutations, $n, le seigneur Bolvar nous a envoyés vous aider.', '', 38134),
(195778, 'frFR', 'Vous avez bien travaillé, les héros. Mais la punition de Mueh\’zala, n\’est pas pour vos yeux. Prenez ce qui est à vous et zou !', '', 38134),
(195777, 'frFR', 'Vous avez pénétré sur mes terres, $umortel:mortelle;. Où que vous vous cachiez, je vous retrouverai.', '', 38134),
(195776, 'frFR', '', 'À présent, il est temps d\’aller défier votre bourreau sur son terrain !', 38134),
(195775, 'frFR', 'Mes chers sujets, nous avons arraché une petite victoire. Prenez la part d\’anima qui vous revient.', '', 38134),
(195774, 'frFR', 'Les petits, je les aplatis !', '', 38134),
(195773, 'frFR', 'Les ombres t\’emportent !', '', 38134),
(195772, 'frFR', 'Je prends cette vie !', '', 38134),
(195770, 'frFR', 'Idiots de mortels ! C\’est moi le vrai dieu de la mort !', '', 38134),
(195769, 'frFR', 'Personne échappe à la mort !', '', 38134),
(195768, 'frFR', 'Je commande aux ténèbres !', '', 38134),
(195764, 'frFR', 'Voyez la force de notre incroyable Sans-Entraves ! Beau travail.', '', 38134),
(195762, 'frFR', 'Des muscles et une bonne armure ! Je veux me jeter dans la bataille et rire des coups qui rebondiront sur moi !', '', 38134),
(195760, 'frFR', 'Vos recrues ont besoin d\’action ! Sans combats, elles s\’ennuient !', '', 38134),
(195759, 'frFR', 'Surveille ta langue, petit moustique. C\’est moi qui commande ici, maintenant…', '', 38134),
(195758, 'frFR', '', 'Qui aurait cru qu\’une créature nommée Aile-de-mort causerait une telle destruction ?', 38134),
(195757, 'frFR', '', 'Si vous trouvez une arme puissante, assurez-vous qu\’elle n\’est pas corrompue par la Légion ardente ou les Dieux très anciens.', 38134),
(195756, 'frFR', 'Rah ah ha, il est naturel qu\’il vous ait fallu… tant de combattants… pour m\’abattre…', '', 38134),
(195755, 'frFR', 'Tu fais le fier, Bwonsamdi… Mais devant le Geôlier, t\’es rien du tout !', '', 38134),
(195753, 'frFR', 'La puissance de Maldraxxus afflue en moi ! Je suis le visage de la mort !', '', 38134),
(195752, 'frFR', 'Mais t\’inquiète pas. Tu vas avoir tout le temps d\’apprendre de tes erreurs.', '', 38134),
(195751, 'frFR', 'Les ossements vous appellent !', '', 38134),
(195750, 'frFR', 'Les temps changent. Tu les as toujours sous-estimés, alors qu\’ils ont du… potentiel.', '', 38134),
(195749, 'frFR', 'Les crânes des défunts crient vengeance !', '', 38134),
(195747, 'frFR', 'Contemplez mon nouveau réceptacle… Une forme bien plus adaptée à un véritable maître de la mort !', '', 38134),
(195746, 'frFR', 'Vos petites perturbations ne servent à rien, car le rituel est terminé.', '', 38134),
(195745, 'frFR', 'Cela aussi, nous l\’avions prédit !', '', 38134),
(195744, 'frFR', 'Leurs os ne feront que renforcer mon réceptacle !', '', 38134),
(195743, 'frFR', 'Un sacrifice librement consenti pour la maison des Rituels !', '', 38134),
(195742, 'frFR', 'Le rituel ne doit pas être interrompu ! Ossements des défunts, réveillez-vous et accueillez nos visiteurs !', '', 38134),
(195739, 'frFR', 'Aucune… échappatoire…', '', 38134),
(195737, 'frFR', 'La douleur… L\’éternel désespoir…', '', 38134),
(195736, 'frFR', 'Enfin… l\’ultime… délivrance…', '', 38134),
(195734, 'frFR', 'La souffrance… le tourment… l\’oubli…', '', 38134),
(195724, 'frFR', 'J\’ai hâte de vous assister, Sans-Entraves.', '', 38134),
(195721, 'frFR', 'Des doigts agiles, de bons réflexes et une vue perçante, voilà ce que je veux. Je vous aiderai à rassembler des fournitures et à vendre vos objets superflus.', '', 38134),
(195720, 'frFR', 'Si ma peau émettait des spores régénérantes, je pourrais nous soigner au plus fort des combats.', '', 38134),
(195718, 'frFR', 'Nous ferons une équipe formidable, quand je serai à nouveau en un seul morceau !', '', 38134),
(195717, 'frFR', 'Les aspirants s\’entraînent des siècles durant pour perfectionner leur âme et leur corps. Ils s\’affranchissent de leur passé afin de se concentrer exclusivement sur l\’avenir.\n\nC\’est une vie entièrement dévouée au service de l\’Archonte, mais aussi de toutes les autres âmes. Solidaires les uns des autres.\n\nL\’ascension dont ils rêvent depuis si longtemps est enfin à leur portée.', '', 38134),
(195716, 'frFR', 'Je me battrai bientôt à vos côtés !', '', 38134),
(195715, 'frFR', 'C\’est important de travailler en équipe. Je tiens à être assez robuste pour porter mes frères d\’armes au combat.', '', 38134),
(195713, 'frFR', 'Oh, et si vous voulez vous déplacer plus vite sur les falaises, prenez un lance-soie. Vous pourrez vous balancer d\’un endroit à un autre !', '', 38134),
(195705, 'frFR', 'Encore une création fabuleuse !', '', 38134),
(195703, 'frFR', 'C\’est exaspérant. Crache-Vérole, au travail, et ne me déçois pas comme les autres !', '', 38134),
(195702, 'frFR', 'Mais quels balourds empotés ! Broyaux, essaie de ne pas abîmer les membre en les arrachant.', '', 38134),
(195701, 'frFR', '', 'Ce qu\’il vous faut, c\’est une arbalète. Comment voulez-vous atteindre votre cible avec une vulgaire aiguille à tricoter ?', 38134),
(195700, 'frFR', 'Moi, j\’opterais pour un coup de dague effilée bien placé, juste entre les côtes.', '', 38134),
(195699, 'frFR', 'Qu\’est-ce que vous préférez, $umortel:mortelle; ? Dague, épée ou hache ?', '', 38134),
(195689, 'frFR', '', 'Hé, regardez ! Voilà d\’autres liches ! Voyons si elles ont envie de jouer !', 38134),
(195688, 'frFR', 'Vous déguiser en Ogre pour infiltrer leur base et les empêcher de mener à bien leur rituel de résurrection de dragon… c\’est de la folie !\n\nBon, on s\’y met ?', '', 38134),
(195687, 'frFR', '', 'Vous allez mener la charge avec nous, $p. Chassez-les de mon bosquet !', 38134),
(195686, 'frFR', '', 'Sonnez les cors ! Appelez l\’ensemble de Sylvarden à marcher sous la bannière de l\’Hallali !', 38134),
(195674, 'frFR', '', 'Des Kyrians… au service de l\’Antre ? Voilà qui est préoccupant…', 38134),
(195672, 'frFR', '', 'Il n\’existe qu\’une seule façon d\’obtenir une audience auprès de l\’Archonte : suivre la voie et se montrer digne de l\’ascension.', 38134),
(195671, 'frFR', '', 'Il ne s\’agit pas de prisonniers, mais d\’âmes consentantes. Cela mérite une audience !', 38134),
(195670, 'frFR', '', 'Si l\’Arbitre a jugé qu\’ils méritaient une punition aussi effroyable, leur sort ne nous concerne en rien.', 38134),
(195669, 'frFR', '', 'Votre Transcendance, cette âme vient de nous rapporter la présence de Kyrians dans l\’Antre. Il faut en informer l\’Archonte !', 38134),
(195668, 'frFR', '', 'Au revoir.', 38134),
(195666, 'frFR', '', 'Pourquoi cette mnémès est-elle active ? Nous ne pouvons nous permettre cette dépense d\’anima.', 38134),
(195665, 'frFR', '', 'Ils ont dû sentir que j\’ai activé l\’hôtesse. Laissez-moi faire, $n.', 38134),
(195664, 'frFR', '', 'Que signifie tout ceci ?', 38134),
(195662, 'frFR', 'Cet anima appartient au haut percepteur. N\’approchez pas, ou vous mourrez !', '', 38134),
(195661, 'frFR', '', 'La voilà ! On est sur leurs talons !', 38134),
(195660, 'frFR', '', 'Croc-de-Mort, suis cette calèche !', 38134),
(195659, 'frFR', 'Tout peut être réparé, et ceci n\’échappe pas à la règle.', '', 38134),
(195656, 'frFR', '', 'Le voilà pris au piège. Montrons-lui donc à quel point il s\’est fourvoyé.', 38134),
(195655, 'frFR', 'Aahhhh ! Nooooon. Assez… Arggghhhh !', 'Aahhhh ! Nooooon. Assez… Arggghhhh !', 38134),
(195653, 'frFR', 'Nous devons tous faire des sacrifices. Le Maître l\’exige.', 'Nous devons tous faire des sacrifices. Le Maître l\’exige.', 38134),
(195652, 'frFR', 'Nooon ! Encore ? Je n\’en peux plus…', 'Nooon ! Encore ? Je n\’en peux plus…', 38134),
(195650, 'frFR', 'La Lame d\’ébène sait faire parler les morts. Si nous parvenons à capturer l\’un d\’eux, nous pourrons lui arracher ses secrets.', '', 38134),
(195648, 'frFR', 'L\’endroit grouille de nouvelles âmes, mais certaines sont ici depuis des éternités. L\’une d\’entre elles saura peut-être où trouver une issue.', '', 38134),
(195647, 'frFR', '', 'J\’ai peine à imaginer les horreurs que ces âmes ont pu endurer. Je crains qu\’elles soient au-delà de toute rédemption.', 38134),
(195643, 'frFR', '', 'J\’ai aperçu Baine et Anduin lors de mes diverses tentatives d\’évasion. Ils sont vivants… et c\’est tout ce que je sais.', 38134),
(195640, 'frFR', '', 'Quoi que préparent ce Geôlier et la Banshee, cela se terminera mal… pour nous tous.', 38134),
(195639, 'frFR', 'C\’était une terrible torture. Parfois, on aurait dit qu\’il voulait nous mettre à l\’épreuve. Mais pour quoi, ça, je l\’ignore.', '', 38134),
(195638, 'frFR', '', 'Il s\’est occupé personnellement... de notre accueil en ces lieux.', 38134),
(195637, 'frFR', '', 'Le Geôlier est celui qui règne sur cet endroit. On ne sait pas grand-chose de lui, mais il semble beaucoup s\’intéresser à nous.', 38134),
(195635, 'frFR', '', 'Je sais que cet endroit est… difficile à appréhender.\n\nPrenez le temps de vous ressaisir. Je vous dirai alors tout ce que je sais.', 38134),
(195634, 'frFR', '', 'Oui. Et parmi leurs tortionnaires et les nôtres, on retrouve Sylvanas.', 38134),
(195633, 'frFR', 'Mais quelque chose semble avoir changé et des âmes pures échouent ici aussi. On les entend qui hurlent d\’horreur.', '', 38134),
(195632, 'frFR', '', 'Ils appellent cet endroit « l\’Antre ». C\’est une sorte de prison pour les âmes les plus cruelles et corrompues qui soient.', 38134),
(195631, 'frFR', '', 'Bon… par où commencer ?', 38134),
(195622, 'frFR', 'Il n\’y a rien de plus satisfaisant que de voir une âme obtenir son nouveau corps d\’assemblage.\n\nAvec votre aide, nous pourrons fabriquer une nouvelle armée d\’assemblages conçus pour le combat et mener Maldraxxus à la victoire !', '', 38134),
(195615, 'frFR', 'Fini les secrets !', 'Fini les secrets !', 38134),
(195614, 'frFR', 'Laissez-nous entrer !', 'Laissez-nous entrer !', 38134),
(195613, 'frFR', 'L\’Archonte, qu\’est-ce qu\’elle nous cache ?!', 'L\’Archonte, qu\’est-ce qu\’elle nous cache ?!', 38134),
(195612, 'frFR', '', 'Il vous est vivement conseillé de remettre ces documents à leur place. Seuls les désavoués sont autorisés à les consulter.', 38134),
(195611, 'frFR', '', 'Et bonjour à vous, garde-caveau.', 38134),
(195610, 'frFR', '', 'Bonjour, garde-enclave.', 38134),
(195607, 'frFR', 'Plus facile à dire qu\’à faire.', '', 38134),
(195606, 'frFR', '', 'Nous devons obtenir une audience avec l\’Archonte.', 38134),
(195605, 'frFR', '', 'Par ici.', 38134),
(195603, 'frFR', '', 'C\’est donc vrai… Les désavoués font cause commune avec l\’Antre !', 38134),
(195602, 'frFR', '', 'C\’est compris. Je vais m\’en assurer.', 38134),
(195601, 'frFR', '', 'Peut-être qu\’en en choisissant un autre…', 38134),
(195600, 'frFR', '', 'Quand tu auras le contrôle de la Pureté, nous pourrons révéler notre main. Ensemble, nous allons faire de grandes choses.', 38134),
(195599, 'frFR', '', 'Nous ne sommes pas seuls dans notre combat pour prendre le Bastion. Il y a une autre puissance à nos côtés.', 38134),
(195598, 'frFR', '', 'Il ne voit pas encore assez loin pour ce que je vais te proposer.', 38134),
(195597, 'frFR', '', 'Pourquoi avoir renvoyé ainsi notre esprit vengeur ?', 38134),
(195594, 'frFR', '', 'Veuillez présenter les souvenirs archivés à l\’œil correspondant pour les consulter.', 38134),
(195591, 'frFR', '', 'Mais combien en avez-vous donc construit ?', 38134),
(195590, 'frFR', '', 'Bienvenue dans le caveau de l\’Archonte. Pour nos registres, veuillez indiquer la raison de votre présence ici.', 38134),
(195589, 'frFR', 'La barrière est désactivée ! Allez-y et faites ce que vous aviez à faire !', '', 38134),
(195588, 'frFR', '', 'Hé, $umortel:mortelle;, j\’aurais besoin de votre aide !', 38134),
(195587, 'frFR', 'Et c\’est seulement maintenant que vous le dites ? Bah, peu importe. Je vais arranger ça.', '', 38134),
(195586, 'frFR', '', 'Oh, mais vous vouliez accéder au caveau intérieur ? C\’est encore un tout autre protocole, j\’en ai bien peur.', 38134),
(195585, 'frFR', 'Quoi, une autre barrière ?', '', 38134),
(195584, 'frFR', '', 'Ne vous dispersez pas, il y a une mission à accomplir.', 38134),
(195583, 'frFR', 'Je dois bien avouer que si je voulais entrer ici, c\’est surtout pour prouver que je pouvais. C\’est joli, en tout cas.', '', 38134),
(195580, 'frFR', '', 'Vous n\’aurez qu\’à déposer les offrandes devant les gardiens et l\’accès s\’ouvrira.', 38134),
(195579, 'frFR', '', 'Je vous attendrai à l\’entrée du caveau de l\’Archonte, encore au sud de votre position actuelle.', 38134),
(195577, 'frFR', 'Là ! Vous me protégez, hein ?', '', 38134),
(195576, 'frFR', 'Je sais. C\’est comme ça qu\’une mnémès est CENSÉE se comporter.', '', 38134),
(195575, 'frFR', '', 'Elle est vraiment très serviable.', 38134),
(195574, 'frFR', '', 'Vous devriez à présent repartir vers le sud.', 38134),
(195572, 'frFR', '', 'Vous allez devoir dénicher trois plants de ciguë flétrie.\n\nAvec ça, je vous apprendrai à préparer un poison puissant.\n\nPendant la cueillette, tâchez de vous tenir bien à l\’écart de l\’ours. Ces petites choses font beaucoup de bruit.', 38134),
(195571, 'frFR', '', 'Vous m\’avez tout l\’air d\’être $uun voleur:une voleuse;. Il rôde dans les parages un Ogre puissant qui a capturé une partie des membres de la première expédition.\n\nCette brute en a sans doute aussi après vos amis ! Il est incroyablement fort, mais il est possible de l\’affaiblir avec du poison.\n\nJ\’étais justement en train d\’en préparer quand j\’ai été tuée. Trouvez les composants, et je vous montrerai comment concocter des poisons pour vos armes.\n\nQu\’est-ce que vous en dites ?', 38134),
(195570, 'frFR', 'Oui, c\’est vrai.', '', 38134),
(195569, 'frFR', 'Je ne sais pas s\’il est possible de sauver la guerrière de la Nuit, mais je ferai tout pour vous aider.', '', 38134),
(195567, 'frFR', 'Qadarin ! Louée soit Élune, ça a marché…', '', 38134),
(195565, 'frFR', 'Je… Mais que… qu\’est-ce qui s… ?', '', 38134),
(195563, 'frFR', '', 'Bon, au boulot ! Levons la malédiction !', 38134),
(195562, 'frFR', '', 'Ma bonne vieille Gertie… J\’espère que vous allez bien, toi et tes congénères. Rose, Daisy… Lys-Chardon junior…', 38134),
(195560, 'frFR', '', 'Je… J\’ai besoin d\’un moment pour y réfléchir. On se retrouve au fort.', 38134),
(195558, 'frFR', 'Quand cette pénurie cessera-t-elle enfin ?', 'Quand cette pénurie cessera-t-elle enfin ?', 38134),
(195557, 'frFR', 'Nous avons l\’habitude de souffrir. Nous survivrons, comme toujours.', 'Nous avons l\’habitude de souffrir. Nous survivrons, comme toujours.', 38134),
(195556, 'frFR', 'Grâce à sire Denathrius, tout finira par rentrer dans l\’ordre.', 'Grâce à sire Denathrius, tout finira par rentrer dans l\’ordre.', 38134),
(195555, 'frFR', 'Nous avons tous un rôle à jouer.', 'Nous avons tous un rôle à jouer.', 38134),
(195552, 'frFR', '', 'Par exemple, commencez par vous diriger droit au sud.', 38134),
(195551, 'frFR', '', 'J\’ai interdiction de vous indiquer l\’emplacement desdites offrandes, mais je peux vous donner des direction approximatives.', 38134),
(195548, 'frFR', 'Alors, vous allez nous dire où trouver les offrandes ?', '', 38134),
(195546, 'frFR', 'Il faut savoir faire des sacrifices. C\’est le Maître qui l\’a dit.', 'Il faut savoir faire des sacrifices. C\’est le Maître qui l\’a dit.', 38134),
(195542, 'frFR', '', 'Ah, cher premier forgelite. Quel plaisir de vous voir.', 38134),
(195541, 'frFR', '', 'Veuillez patienter pendant la fermeture de mes systèmes.', 38134),
(195540, 'frFR', '', 'Il semble que je sois victime d\’une erreur critique.', 38134),
(195539, 'frFR', '', 'Veuillez quitter l\’enclave immédiatement ou nous serons contraints de vous expulser.', 38134),
(195537, 'frFR', 'Continuez d\’occuper la mnémès ! Je vais la réparer !', '', 38134),
(195536, 'frFR', '', 'Si vous continuez vos manigances, j\’ai bien envie de vous expulser de l\’enclave.', 38134),
(195535, 'frFR', '', 'Hum, je crois que vous n\’avez pas l\’autorisation d\’accéder à ce souvenir.', 38134),
(195534, 'frFR', '', 'Lysonia, je n\’en ai pas encore fini…', 38134),
(195532, 'frFR', '', 'Uther, infligez au Bastion le châtiment des désavoués.', 38134),
(195527, 'frFR', '', 'Nous allons nous emparer du temple de la Pureté et anéantir tous ceux qui se dressent sur notre chemin !', 38134),
(195525, 'frFR', '', 'Depuis trop longtemps, nous sommes forcés de renoncer à tout ce qui nous définit. À ce qui fait notre force !', 38134),
(195524, 'frFR', '', 'Désavoués, écoutez-moi !', 38134),
(195522, 'frFR', '', 'Suis-moi. Ensemble, nous guiderons les Kyrians vers une nouvelle voie et nous réparerons les dégâts causés par l\’Archonte.', 38134),
(195521, 'frFR', '', 'Que voulez-vous que je fasse ?', 38134),
(195520, 'frFR', '', 'J\’ai tenté de les avertir. Une telle insistance à suivre aveuglément la voie finira par nous détruire.', 38134),
(195517, 'frFR', '', 'Pas assez longtemps pour que j\’aie oublié la douleur, mais assez pour qu\’elle ne soit qu\’un souvenir. Tu connaîtras ça aussi.', 38134),
(195516, 'frFR', '', 'J\’ai l\’impression de me déliter chaque jour un peu plus. Quand avez-vous enduré cela ?', 38134),
(195515, 'frFR', '', 'Aie donc foi en notre mission. Le temps venu, tu comprendras.', 38134),
(195513, 'frFR', '', 'Allez-y, $n. Je reste pour protéger le premier forgelite.', 38134),
(195512, 'frFR', 'Elle, je vais te la reprogrammer même si je dois y laisser mes dents.', '', 38134),
(195510, 'frFR', '', 'Les ténèbres ont plongé notre Arbitre bien-aimée dans un profond sommeil dont rien ne peut la sortir.', 38134),
(195509, 'frFR', 'Ça ne devrait pas exploser. Croisons les doigts.', '', 38134),
(195508, 'frFR', '', 'Je vous laisse vous en charger, $n. Vous avez déjà affronté Lysonia.', 38134),
(195506, 'frFR', 'Le fragment est prêt ! Allez consulter ses souvenirs dans les miroirs des âmes.', '', 38134),
(195504, 'frFR', 'Les rassembler ne devrait pas être bien compliqué. Le reste ira rejoindre les archives.', '', 38134),
(195503, 'frFR', '', 'Les souvenirs de Lysonia concernant les désavoués sont perdus parmi les autres.', 38134),
(195501, 'frFR', 'Ah, l\’enclave n\’a pas explosé. Bien.', '', 38134),
(195500, 'frFR', '', 'Je veillerai à ce qu\’on prenne bien soin de cette petite trottesoie, pour l\’avenir de Sylvarden. Peut-être pourriez-vous venir lui rendre visite de temps en temps ?', 38134),
(195499, 'frFR', '', 'Et tu voudrais bien que je me laisse abuser par ce portail. Il ne va faire que me mener plus loin dans les tréfonds de la tour. Mais… Est-ce que j\’ai le choix ? Je dois en avoir le cœur net.', 38134),
(195498, 'frFR', '', 'Tu me tortures avec des visions d\’alliés venant à mon secours.', 38134),
(195497, 'frFR', '', 'Qu\’est-ce que c\'est… Non, encore un sale petit tour, j\’imagine.', 38134),
(195493, 'frFR', '', 'Je vous aiderai de mon mieux. Les énergies nécrotiques qui souillent cet endroit doivent être purifiées.', 38134),
(195490, 'frFR', 'Ah, l\’enclave est à nouveau en service.', '', 38134),
(195489, 'frFR', 'Poursuivez-les.', '', 38134),
(195488, 'frFR', 'Intéressant…', '', 38134),
(195487, 'frFR', '', 'Un fragment d\’essence de l\’une des désavoués. Il pourrait nous révéler le secret de leurs origines.', 38134),
(195486, 'frFR', 'De quoi s\’agit-il ?', '', 38134),
(195485, 'frFR', '', 'Mikanikos ? L\’enclave peut-elle extraire des souvenirs de ceci ?', 38134),
(195482, 'frFR', 'Les intermédiaires sont activés. Alimentez-les avec l\’anima que je vous ai donné.', '', 38134),
(195480, 'frFR', '', 'Je me battrai jusqu\’à ce que vous me montriez vos nouveaux talents !', 38134),
(195478, 'frFR', '', 'Hein ? Cette calèche est en train d\’emporter ce qu\’il restait d\’anima.', 38134),
(195475, 'frFR', '', 'Ah, les voilà. Où vont… Elles se dirigent toutes vers l\’Antre ! Ben !', 38134),
(195473, 'frFR', '', 'Attendez… mais où sont les âmes ? Elles devraient être en cours de transfert d\’Oribos vers leur nouveau royaume…', 38134),
(195471, 'frFR', '', 'C\’était une… mnémès ?', 38134),
(195470, 'frFR', 'Oui. Mais je ne tiens pas à en parler.', '', 38134),
(195469, 'frFR', '', 'Maintenant, Ben va se présenter à l\’Arbitre, qui jugera son âme et l\’enverra vers un au-delà bien mérité.', 38134),
(195467, 'frFR', '', 'Oh, non… Il est peut-être déjà trop tard.', 38134),
(195465, 'frFR', 'C\’est MOI qui t\’ai fait ! Je suis le PREMIER forgelite !', '', 38134),
(195464, 'frFR', '', 'L\’accès à l\’enclave est actuellement interdit.', 38134),
(195462, 'frFR', 'À force de patience, on surmonte tous les obstacles.', '', 38134),
(195461, 'frFR', 'À une prochaine fois, $p.', '', 38134),
(195453, 'frFR', 'Avec le temps, j\’ai compris qu\’ils n\’étaient pas les monstres décérébrés que je croyais avoir vus. J\’ai vu que Maldraxxus ne manque pas d\’honneur, ou en tout cas n\’en manquait pas.', '', 38134),
(195450, 'frFR', '<Mikanikos semble absorbé par l\’étude des runes.>', '', 38134),
(195449, 'frFR', 'La dernière mort m\’a trouvé, même caché dans l\’ombre…', '', 38134),
(195448, 'frFR', 'Ah, ces mortels qui se mêlent de ce qu\’ils ne comprennent pas !', '', 38134),
(195442, 'frFR', 'Venthyrs, l\’heure est venue de prendre les armes pour arracher votre anima au tyran !', '', 38134),
(195441, 'frFR', 'Margrave, je suis avec vous !', 'Margrave, je suis avec vous !', 38134),
(195440, 'frFR', 'Le simple fait qu\’elle ait, elle aussi, sonné la cloche de repentance devrait suffire à vous inquiéter.', 'Le simple fait qu\’elle ait, elle aussi, sonné la cloche de repentance devrait suffire à vous inquiéter.', 38134),
(195439, 'frFR', 'Vous n\’avez qu\’à interroger l\’âme sans-entraves sur le sort qui vous y attend si vous échouez.', 'Vous n\’avez qu\’à interroger l\’âme sans-entraves sur le sort qui vous y attend si vous échouez.', 38134),
(195438, 'frFR', 'Nous ferons tout notre possible pour vous aider, mais à la prochaine erreur, vous passerez le reste de l\’éternité dans l\’Antre.', 'Nous ferons tout notre possible pour vous aider, mais à la prochaine erreur, vous passerez le reste de l\’éternité dans l\’Antre.', 38134),
(195436, 'frFR', 'Vous ne pouvez rejeter la faute sur personne. Ce sont vos erreurs. Vos vices.', 'Vous ne pouvez rejeter la faute sur personne. Ce sont vos erreurs. Vos vices.', 38134),
(195435, 'frFR', 'J\’espère que les aveux de Sadika sauront vous motiver. Ce périple ne sera aisé pour aucun d\’entre vous.', 'J\’espère que les aveux de Sadika sauront vous motiver. Ce périple ne sera aisé pour aucun d\’entre vous.', 38134),
(195434, 'frFR', 'Merci.', 'Merci.', 38134),
(195433, 'frFR', 'Vous pouvez reprendre votre place, Sadika.', 'Vous pouvez reprendre votre place, Sadika.', 38134),
(195432, 'frFR', 'Les Venthyrs vont faire en sorte que vous puissiez affronter et vaincre les vices qui entravent votre âme.', 'Les Venthyrs vont faire en sorte que vous puissiez affronter et vaincre les vices qui entravent votre âme.', 38134),
(195430, 'frFR', 'C\’est pour cette raison que nous allons vous aider, Sadika.', 'C\’est pour cette raison que nous allons vous aider, Sadika.', 38134),
(195429, 'frFR', 'C\’est un souvenir… plutôt douloureux…', 'C\’est un souvenir… plutôt douloureux…', 38134),
(195426, 'frFR', 'Il va falloir exposer vos défauts au grand jour avant de penser à pouvoir les corriger.', 'Il va falloir exposer vos défauts au grand jour avant de penser à pouvoir les corriger.', 38134),
(195422, 'frFR', 'Je suis ravi d\’entendre votre confession, Sadika.', 'Je suis ravie d\’entendre votre confession, Sadika.', 38134),
(195421, 'frFR', '<Vous ignorez le corps sans vie pour vous intéresser à une récompense plus alléchante : son grimoire !\n\nIl décrit un rituel permettant d\’asservir un puissant marcheur du Vide. Manifestement, ce démoniste n\’y est pas parvenu.\n\nMais vous pourriez réussir là où il a échoué. Ramassez le grimoire à côté du cadavre et terminez le rituel pour prendre le contrôle de votre propre marcheur du Vide !>', '', 38134),
(195418, 'frFR', '', 'C\’est le Dessein qui vous envoie. Cela ne fait aucun doute.', 38134),
(195415, 'frFR', 'Il ne nous échappera pas.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195412, 'frFR', 'Et pourtant, vous vous terrez dans votre nécropole. Un coup me suffira pour vous faire taire !', '', 38134),
(195411, 'frFR', '', 'Vous courez à votre ruine. J\’ai hâte de vous mettre en pièces !', 38134),
(195409, 'frFR', 'Antolyte… Elle ne m\’a jamais battu dans l\’arène et elle ne nous battra pas aujourd\’hui.', '', 38134),
(195408, 'frFR', '', 'Combattre à vos côtés a été un honneur.', 38134),
(195406, 'frFR', '', 'Gharmal n\’échappera pas à notre vengeance.\n\nPouvons-nous y aller ?', 38134),
(195398, 'frFR', 'Une créature mortelle à Oribos ? Le Dessein nous fait un bien étrange présent.', 'Une créature mortelle à Oribos ? Le Dessein nous fait un bien étrange présent.', 38134),
(195382, 'frFR', '', 'À votre signal, nous tenterons de gagner la grotte non loin. Vous vous sentez d\’attaque ?', 38134),
(195381, 'frFR', 'Il nous reste des problèmes importants à régler.', '', 38134),
(195370, 'frFR', 'La voilà. Apprivoisez-la comme la précédente.', '', 38134),
(195369, 'frFR', 'Ah, la voilà ! Apprivoisez-la comme la précédente.', '', 38134),
(195368, 'frFR', 'Une autre bête apprivoisée ! Vous devez maintenant apprendre à confier vos protégées à une écurie.', '', 38134),
(195367, 'frFR', 'Vous avez trop de familiers. Venez me voir pour en mettre un à l\’écurie.', '', 38134),
(195366, 'frFR', 'À vous de me dire quand vous voudrez apprivoiser la bête.', '', 38134),
(195365, 'frFR', 'Renvoyez votre familier actuel pour en apprivoiser un autre. Vous pourrez le rappeler plus tard.', '', 38134),
(195364, 'frFR', 'Beau travail ! Vous devez maintenant apprendre à confier vos familiers à une écurie.', '', 38134),
(195363, 'frFR', 'Vous avez trop de familiers. Venez me voir pour en mettre un à l\’écurie.', '', 38134),
(195362, 'frFR', 'Dites-moi quand vous voudrez apprivoiser la bête.', '', 38134),
(195358, 'frFR', 'Si nous tombons, ce sera les armes à la main.', '', 38134),
(195351, 'frFR', 'Nos troupes sont rassemblées au viaduc Brisé. Je vous y retrouverai.', '', 38134),
(195340, 'frFR', 'Espérons. Ils ne seront pas ravis de voir que vous avez encore réussi à leur échapper.', '', 38134),
(195331, 'frFR', 'Une matriarche gorm ! Elle et ses petits vont tout dévorer autour de leurs galeries. Éliminez-la, et surtout n\’oubliez pas de vous occuper des œufs !', '', 38134),
(195328, 'frFR', 'Qui êtes-vous, $gétranger:étrangère; ?', 'Qui êtes-vous, $gétranger:étrangère; ?', 38134),
(195327, 'frFR', 'Échelon ! Livre-moi une nouvelle âme…', '', 38134),
(195326, 'frFR', 'Traquez les âmes qui se terrent ici jusqu\’à la dernière !', '', 38134),
(195325, 'frFR', 'Nous rejetons les mensonges de l\’Accusatrice et de sa cohorte de traîtres !', '', 38134),
(195324, 'frFR', 'Rien ne pourra racheter ces âmes. Emportez-les !', '', 38134),
(195303, 'frFR', '', 'Je commençais à désespérer de revoir quelqu\’un de notre monde un jour.', 38134),
(195301, 'frFR', '', 'En vous voyant arriver, je me suis d\’abord demandé si vous n\’étiez pas qu\’une autre illusion venue nous tourmenter…', 38134),
(195299, 'frFR', '', 'Nous devrions être à l\’abri, ici… pour l\’instant.', 38134),
(195297, 'frFR', '', 'Par ici !', 38134),
(195296, 'frFR', 'C\’est pour toi que tout est fini !', '', 38134),
(195295, 'frFR', 'Vos pathétiques alliés ne vous sauveront pas ! Vous finirez tous les chaînes aux pieds !', '', 38134),
(195294, 'frFR', 'Votre cellule vous attend, mage. Le Geôlier a des projets pour vous !', '', 38134),
(195293, 'frFR', '', 'J\’en ai assez de tes cages, démon !', 38134),
(195292, 'frFR', 'Vous ne nous échapperez jamais, mortels !', '', 38134),
(195289, 'frFR', 'Jaina, avez-vous vu aperçu le moindre signe de la guerrière de la Nuit avant notre arrivée ?', '', 38134),
(195287, 'frFR', 'En voilà d\’autres ! Abattez-les !', '', 38134),
(195285, 'frFR', 'Qu\’a voulu dire Thrall, exactement ? C\’est une habitude, chez vous ?', '', 38134),
(195284, 'frFR', '', 'Pour la discrétion, on repassera. Allons, dépêchons.', 38134),
(195282, 'frFR', 'Chevaliers, emparez-vous de la grotte ! Nous allons retenir ces démons !', '', 38134),
(195279, 'frFR', '', 'Il y a une grotte non loin d\’ici. Avec un peu de chance, nous l\’atteindrons sans nous faire repérer.', 38134),
(195278, 'frFR', '', 'Mais l\’illusion ne tiendra pas bien longtemps. Dépêchez-vous !', 38134),
(195277, 'frFR', '', 'J\’ai créé des images miroirs qui devraient faire diversion.', 38134),
(195276, 'frFR', '', 'Ils nous ont repérés ! Attention !', 38134),
(195275, 'frFR', 'Il y a quelque chose dans le ciel !', '', 38134),
(195274, 'frFR', 'Merci d\’avoir volé à mon secours, Jaina.', '', 38134),
(195273, 'frFR', 'Je vais… me débrouiller…', '', 38134),
(195272, 'frFR', '', 'On ne peut pas rester ici. Thrall, vous pouvez marcher ?', 38134),
(195270, 'frFR', 'Recueillez tout indice susceptible de nous apprendre où se trouvent nos alliés.', '', 38134),
(195269, 'frFR', 'Les ennemis qui rôdent ici sont bien plus nombreux que nous. Évitons d\’attirer leur attention.', '', 38134),
(195266, 'frFR', 'Allez, partons d\’ici avant que des renforts n\’arrivent.', '', 38134),
(195265, 'frFR', 'On ne pourra pas en faire plus ici.', '', 38134),
(195264, 'frFR', 'Nous nous retrouverons plus loin. Allez !', '', 38134),
(195263, 'frFR', 'Il n\’y a… pas… d\’échappatoire…', '', 38134),
(195262, 'frFR', 'Vous mourrez les fers aux pieds !', '', 38134),
(195259, 'frFR', '', 'Je vous retrouverai auprès de Rénathal.', 38134),
(195256, 'frFR', 'Nous sommes en train d\’établir une position avancée. Allez vous assurer qu\’elle soit solide.', '', 38134),
(195255, 'frFR', 'Retrouvez les autres chevaliers, on ne sera pas loin derrière.', '', 38134),
(195254, 'frFR', 'Frayez-vous un chemin hors d\’ici. On vous retrouvera plus loin.', '', 38134),
(195253, 'frFR', '', 'Pitoyable. En Maldraxxus, elle aurait du mal à être prise comme soldat !', 38134),
(195252, 'frFR', '', 'Vous allez payer le prix ultime !', 38134),
(195251, 'frFR', 'Bonne nouvelle : de nouvelles âmes ont rejoint nos rangs.', '', 38134),
(195222, 'frFR', '', 'L\’endroit grouille de désavoués. Restez sur vos gardes.', 38134),
(195220, 'frFR', 'Halte ! Je sens une protection à proximité.', '', 38134),
(195217, 'frFR', '', 'Vous êtes très proche de votre objectif.', 38134),
(195205, 'frFR', '', 'Maldraxxi, marquez les cibles. Je vais les frapper de mon courroux !', 38134),
(195204, 'frFR', 'On va bien s\’amuser. Comme à la bonne époque, pas vrai, $p ?', '', 38134),
(195203, 'frFR', 'Je ne m\’inquiète pas pour la force. C\’est de votre génie pour la stratégie que j\’ai besoin.', '', 38134),
(195202, 'frFR', '', 'Le haut percepteur n\’a rien d\’un adversaire ordinaire. Pour le renverser, il va nous falloir une force immense.', 38134),
(195201, 'frFR', 'J\’essaie de ne pas juger ceux qui servent le Maître.\n\nJ\’ai été envoyé en Revendreth parce que moi aussi, je n\’ai pas su voir la vérité alors qu\’elle était juste devant mes yeux.\n\nIl n\’est jamais trop tard pour faire le bon choix.', '', 38134),
(195196, 'frFR', 'Les ténèbres m\’entourent !', '', 38134),
(195195, 'frFR', 'L\’archiviste Fane vous donnera tous les détails.', '', 38134),
(195194, 'frFR', 'Je réparerai Témel lorsque vous aurez rassemblé toutes ses pièces.', '', 38134),
(195193, 'frFR', '', 'Tout me semble en ordre !', 38134),
(195173, 'frFR', '', 'Bonjour, $n !\n\nOn a besoin de vous à la Couronne de glace. Sans votre aide, Azeroth est condamnée !', 38134),
(195172, 'frFR', '', 'Quoi que vous fassiez, ne vous mettez jamais entre un Pandaren et sa bière. Vous vous en mordriez les doigts !', 38134),
(195166, 'frFR', 'Nous allons rouvrir l\’une des conduites d\’âmes vers le Bastion. Elle ne sont plus utilisées depuis… quelque temps. Votre arrivée là-bas sera pour le moins inattendue.', '', 38134),
(195165, 'frFR', 'Jouez de la harpe pour apaiser les trottesoies et ils sécréteront de la soie. Mais s\’ils ont peur, ils n\’écouteront pas !', '', 38134),
(195164, 'frFR', 'Il nous reste bien peu d\’anima, mais la voix de l\’Arbitre a donné sa bénédiction. Nous allons vous offrir ce que nous avons.', '', 38134),
(195163, 'frFR', 'Venez, $ucher mortel:chère mortelle;. Je vais vous montrer le chemin.', '', 38134),
(195162, 'frFR', '', 'Allez, Kah-Shir. Par moi, c\’est sa voix qui a parlé.', 38134),
(195161, 'frFR', 'Mais, auguste voix, il nous reste si peu d\’anima ! Ne devrions-nous pas être plus…', '', 38134),
(195160, 'frFR', '', 'Surveillant Kah-Shir, menez cette âme mortelle à l\’anneau de transfert. Puisez dans nos réserves d\’anima pour ouvrir un passage vers le Bastion.', 38134),
(195159, 'frFR', '', 'Certes, mais il semble que ce soit la seule piste qui s\’offre à nos amis. À nous de leur ouvrir la voie pour qu\’ils puissent la suivre.', 38134),
(195158, 'frFR', '', 'Les nobles Kyrians du Bastion mènent les âmes des mondes des mortels vers l\’Ombreterre, mais jamais en les enchaînant. Ils n\’ont rien à voir avec l\’Antre.', 38134),
(195156, 'frFR', 'C\’est Sylvanas Coursevent qui a envoyé ses agents les capturer. Des créatures ailées les ont enchaînés et emportés vers la fracture ouverte dans le ciel.', '', 38134),
(195155, 'frFR', '', 'Dites-moi… Comment ces alliés de votre monde ont-ils fini emprisonnés dans l\’Antre ?', 38134),
(195154, 'frFR', '', 'Si l\’emprise de l\’Antre s\’insinue en votre royaume, c\’est tout l\’équilibre de la vie et de la mort qui en sera bouleversé. Votre monde sombrera alors dans les ténèbres.', 38134),
(195153, 'frFR', 'Et quand elle a brisé le casque, le ciel s\’est fracturé au-dessus de la citadelle et une immense tour est apparue derrière le voile de l\’existence.', '', 38134),
(195151, 'frFR', '', 'Si un Orc patibulaire vous offre à boire, il vaut peut-être mieux refuser.', 38134),
(195150, 'frFR', 'Je crois que je commence à comprendre.\n\nMais nous sommes si peu nombreux… Comment leur tenir tête ?\n\n<Il inspire profondément et relève la tête.>\n\nNous ferons ce qui est juste. Nous sauverons Revendreth.', '', 38134),
(195148, 'frFR', 'Moi qui croyais que la mort était la fin de tout être. Ici, on dirait que c\’est un pouvoir qui donne un nouveau départ.', '', 38134),
(195147, 'frFR', 'J\’ai ouvert un beau paquet de portails dans ma vie, mais jamais entre deux plans d\’existence.', '', 38134),
(195141, 'frFR', 'Très bien. $n, si vous pouvez nous indiquer où les acolytes doivent créer les portails…', '', 38134),
(195140, 'frFR', '', 'Allons, Kah-Shir, faites preuve d\’hospitalité. Amis mortels, je vous propose d\’installer vos portails à l\’endroit de votre arrivée… juste devant la cité.', 38134),
(195139, 'frFR', 'Vous voudriez ouvrir un passage de retour vers le monde des vivants ? Par tous les… Oribos est déjà bondé avec tous ces négociants qui arrivent chaque jour.', '', 38134),
(195134, 'frFR', '', 'Regagnez les niveaux inférieurs. Les surveillants vont vous aider à vous préparer.', 38134),
(195133, 'frFR', '', 'On dit que sa haine pour l\’Arbitre est dévorante. S\’il venait à l\’abattre, c\’en serait fini de tout ce que les Fondateurs ont créé.', 38134),
(195132, 'frFR', '', 'Et j\’ai bien peur que, fort de cet immense afflux d\’âmes, le Geôlier devienne assez puissant pour s\’échapper de l\’Antre.', 38134),
(195131, 'frFR', '', 'Privée d\’anima, l\’Ombreterre sombre dans la pénurie. Les royaumes se coupent les uns des autres et seule la peur domine.', 38134),
(195130, 'frFR', '', 'Aujourd\’hui, les âmes regorgeant d\’anima échouent tout droit dans l\’Antre. Nous avons tout essayé pour y remédier et réveiller notre sainte Arbitre… mais sans succès.', 38134),
(195129, 'frFR', '', 'Les mots ne sauraient décrire la gloire de cet instant. Avancez, je vais vous montrer.', 38134),
(195128, 'frFR', '', 'À votre arrivée à Oribos, je l\’ai vue frémir pour la première fois depuis bien longtemps. Ça m\’a donné espoir qu\’elle s\’éveille enfin pour accomplir son Dessein.', 38134),
(195127, 'frFR', '', 'Rien n\’a changé. Elle reste silencieuse. Inerte.', 38134),
(195126, 'frFR', '', '$uMortel:Mortelle; ! Je prie pour que l\’Arbitre s\’éveille à votre présence.', 38134),
(195125, 'frFR', '', 'Louée soit sa voix.', 38134),
(195124, 'frFR', 'Louée soit sa voix.', '', 38134),
(195123, 'frFR', '', 'Kah-Shir, c\’est à l\’Arbitre qu\’il revient de juger les âmes, pas à nous. Je vais mener cette âme mortelle auprès d\’elle. Par moi, c\’est sa voix qui a parlé.', 38134),
(195122, 'frFR', 'Cette âme mortelle mérite-t-elle vraiment une audience ? Si ça se trouve, c\’est un agent à la solde du Geôlier !', '', 38134),
(195121, 'frFR', '', 'Une âme vivante ! Sa présence ici ne peut que servir le Dessein. Si l\’Arbitre a réagi à son arrivée, c\’est qu\’elle pourrait être celle qui l\’éveillera.', 38134),
(195120, 'frFR', '', 'C\’est merveilleux, auguste voix ! Il y a quelque temps, nous avons perçu une perturbation à la lisière de l\’Antre, puis cette créature mortelle est apparue. Elle dit avoir réussi à s\’en libérer.', 38134),
(195116, 'frFR', 'Honorables surveillants ! $UCet inconnu:Cette inconnue; est $uarrivé:arrivée; aux portes de la cité. Il semble qu\’il s\’agisse… d\’un être encore vivant.', '', 38134),
(195115, 'frFR', 'C\’est certainement un bien sombre présage. Mais de quoi ?', '', 38134),
(195113, 'frFR', '', 'Les descriptions des clairvoyants ne laissent aucun doute. L\’Antre s\’est brusquement agité, comme en réaction à un bouleversement.', 38134),
(195111, 'frFR', 'Vous pouvez dire ce que vous voulez sur les Réprouvés, mais au moins, nous ne passons pas notre temps à ressusciter des dragons morts.\n\nEnfin, à part une petite minorité d\’entre nous.', '', 38134),
(195108, 'frFR', '', 'Suivez-nous, $uinconnu:inconnue;.', 38134),
(195107, 'frFR', 'Il faut l\’emmener auprès des surveillants immédiatement !', '', 38134),
(195106, 'frFR', '', 'Attendez. Il en émane effectivement une trace du pouvoir de l\’Antre, mais aussi… Non, c\’est impossible !', 38134),
(195105, 'frFR', 'Un agent de l\’Antre vient attaquer Oribos ! Protégez l\’Arbitre !', '', 38134),
(195102, 'frFR', 'Vous n\’avez qu\’un mot à dire et je me mets au travail.', '', 38134),
(195101, 'frFR', 'Salut, copain bien !\n\nJe vais vous trouver de quoi grignoter !', '', 38134),
(195057, 'frFR', '', 'Branchet… Je n\’ai plus que lui. Faites tout pour le sauver.', 38134),
(195056, 'frFR', 'Ah, l\’ironie du sort ! Les Harpies étaient en train de discuter du meilleur moyen de m\’utiliser comme appât pour mettre la main sur d\’autres membres de la Horde.\n\nEt voilà que vous débarquez comme une fleur ! Ah et au fait, elles vont vous tomber dessus d\’un instant à l\’autre.', '', 38134),
(195048, 'frFR', '', 'Votre enveloppe est faible et ridicule.', 38134),
(195047, 'frFR', '', 'Attention, la défaite arrive !', 38134),
(195046, 'frFR', '', 'En garde !', 38134),
(195040, 'frFR', '', 'Je ne laisserai pas nos sacrifices se perdre en vain !', 38134),
(195039, 'frFR', '', 'Mais il vient un moment où il faut avant tout se battre pour défendre les siens.', 38134),
(195038, 'frFR', 'Le Faë nocturne a l\’air abattu.', 'Le Faë nocturne a l\’air abattu.', 38134),
(195037, 'frFR', '', 'Ara\’lon était loyal. Il comprenait la nécessité de faire des choix et certains sacrifices.', 38134),
(195035, 'frFR', 'Quels sont vos ordres ?', '', 38134),
(195033, 'frFR', 'L\’ennemi marche à présent sur le bosquet de l\’Éveil en continuant à asservir vos sujets.', '', 38134),
(195031, 'frFR', '', 'Ma reine, les Drust ont corrompu le noble cœur d\’Ara\’lon. Ils nous ont forcés à l\’affronter, et… Il n\’est plus.', 38134),
(195029, 'frFR', '', 'Tout ce qu\’ils vont trouver ici, c\’est le sommeil éternel !', 38134),
(195028, 'frFR', 'Ha ! Regardez-les tomber sous nos coups !', '', 38134),
(195025, 'frFR', '', '<Les yeux de la reine de l\’hiver brillent de puissance contenue.>\n\nDites-moi ce que vous savez.', 38134),
(195024, 'frFR', '', '<La colère flamboie dans le regard de la reine.>\n\nCes créatures doivent être exterminées.', 38134),
(195023, 'frFR', '', 'Ben, ce n\’est que le début. J\’ai hâte de voir dans quel royaume on vous envoie. Vous serez récompensé pour votre altruisme et votre courage !', 38134),
(195022, 'frFR', '', 'Le cor de traversée du voile appelle un transcendé qui vous conduira à Oribos. Votre protégé y recevra son jugement.', 38134),
(195021, 'frFR', '', 'Quelle mort cruelle pour un homme si simple… Son courage ne connaît pas de limites. Vous avez bien mérité la paix dans l\’au-delà, Ben.', 38134),
(195020, 'frFR', '', 'Le Fléau ? Non… Aucune de ces personnes n\’y survivra !', 38134),
(195009, 'frFR', 'Que se passe-t-il ?', 'Que se passe-t-il ?', 38134),
(195007, 'frFR', '', 'Il semble être aimé non seulement de sa famille mais aussi de tous les habitants. Une vie simple… Qu\’est-ce qui l\’a conduit ici ?', 38134),
(194998, 'frFR', 'Ma famille… Je vais vous protéger.', '', 38134),
(194996, 'frFR', '', 'Bien le bonjour, $n ! Je peux vous envoyer où vous le souhaitez.\n\nLe temps fonctionne de manière mystérieuse, mais vous avez l\’air de vous y connaître, en mystères.', 38134),
(194995, 'frFR', 'S\’il est de votre sang, je n\’en doute pas une seconde.', '', 38134),
(194994, 'frFR', '', 'Vous devez être bien fier. Mon fils n\’était qu\’un petit enfant quand les traîtres m\’ont assassinée. Je me suis battue comme une lionne pour le sauver, mais… j\’ignore s\’il a survécu.', 38134),
(194993, 'frFR', 'Je l\’ai revu récemment dans l\’Antre. Lui aussi s\’est relevé en chevalier de la mort et combat pour défendre le monde.', '', 38134),
(194992, 'frFR', 'Oui, deux fils. L\’un m\’a trahi au service du Fléau, l\’autre s\’est sacrifié pour me libérer.', '', 38134),
(194991, 'frFR', '', 'Vous avez des enfants, Alexandros ?', 38134),
(194989, 'frFR', 'Que la victoire soit vôtre !', '', 38134),
(194987, 'frFR', 'J\’ignore totalement à quoi on s\’expose en essayant d\’extraire les souvenirs d\’un fragment comme ça.\n\nDonc n\’oubliez pas, vous cherchez des souvenirs de désavoués. Tout ce qui est violet avec des ailes, ou toute image de centurions.\n\nHonnêtement, je ne sais pas ce qu\’on cherche… mais s\’il y a quoi que ce soit, l\’extracteur trouvera !\n\nJe l\’active à votre signal.', '', 38134),
(194986, 'frFR', 'Regardez ce qu\’ils ont fait à mes centurions ! Et à l\’enclave !\n\n<Mikanikos balaye d\’un geste colérique tout ce qui l\’entoure.>', '', 38134),
(194985, 'frFR', '', 'Alors à vous de poursuivre l\’œuvre de votre vie.', 38134),
(194984, 'frFR', 'C\’est le margrave Krexus qui m\’a fait voir que notre armée combat avec gloire et honneur. Mais les exactions de nos ennemis rappellent celles du Fléau.', '', 38134),
(194983, 'frFR', 'De mon vivant, ma grande vocation était d\’éliminer les morts-vivants. Me retrouver entouré d\’eux dans la mort fut un véritable cauchemar.', '', 38134),
(194982, 'frFR', '', 'Mograine. Compte tenu de vos antécédents avec la non-mort, vous devez trouver Maldraxxus… désagréable.', 38134),
(194979, 'frFR', 'Bienvenue, $umortel:mortelle;.', 'Bienvenue, $umortel:mortelle;.', 38134),
(194978, 'frFR', 'Bienvenue, $p.', 'Bienvenue, $p.', 38134),
(194977, 'frFR', '', 'Ne faites preuve d\’aucune retenue lorsque vous déchaînez votre pouvoir contre le Fléau !', 38134),
(194975, 'frFR', '', 'Parangon, à moi !', 38134),
(194974, 'frFR', 'Luttez jusqu\’au dernier ! Nous ne courberons pas l\’échine !', '', 38134),
(194973, 'frFR', '', 'Battez-vous avec ardeur, mes frères et sœurs ! Le margrave va payer !', 38134),
(194972, 'frFR', '', 'Ses mastodontes s\’en prennent aux troupes de Mograine. Elles sont coincées !', 38134),
(194957, 'frFR', 'Je pense que je vais survivre !', 'Je pense que je vais survivre !', 38134),
(194956, 'frFR', 'J\’ai vu Xandria s\’envoler droit vers le temple. Elle pourrait avoir besoin de votre aide.', 'J\’ai vu Xandria s\’envoler droit vers le temple. Elle pourrait avoir besoin de votre aide.', 38134),
(194955, 'frFR', 'L\’Archonte soit louée, vous arrivez à temps.', 'L\’Archonte soit louée, vous arrivez à temps.', 38134),
(194954, 'frFR', 'Par ici, vite !', 'Par ici, vite !', 38134),
(194953, 'frFR', 'Sauvez… les autres…', 'Sauvez… les autres…', 38134),
(194942, 'frFR', 'Nous avons tous les corps dont nous avons besoin, Sutur. Préparez-vous à attaquer le prochain temple.', '', 38134),
(194941, 'frFR', 'Encore un petit instant. Il semblerait que les pièces de ma prochaine création soient enfin arrivées…', '', 38134),
(194940, 'frFR', '', 'Je mènerai le combat ici pendant que mes disciples vous conduiront à la nécropole. Mettons un terme à ces atrocités.', 38134),
(194939, 'frFR', '', 'Vous êtes $uarrivé:arrivée; à point nommé, mais la bataille continue. Nous n\’aurons de repos tant que ces envahisseurs voleront dans nos cieux.', 38134),
(194938, 'frFR', 'Pauvre petit parangon, vous ne pouvez rien faire pour nous arrêter !', '', 38134),
(194937, 'frFR', '', 'Justice sera rendue !', 38134),
(194936, 'frFR', '', 'Horrible monstre !', 38134),
(194935, 'frFR', 'Votre temple est en ruines. Vos frères ont été massacrés. Vous avez déjà perdu !', '', 38134),
(194921, 'frFR', 'Êtes-vous si $upressé:pressée; de partager le sort de votre sous-fifre ? Votre fameux courage causera votre mort ! ', '', 38134),
(194916, 'frFR', 'Sutur sera agacé d\’apprendre que vous avez détruit sa précieuse création. Bien qu\’il sera sans doute ravi de récupérer vos membres en échange.', '', 38134),
(194915, 'frFR', 'Plus qu\’une rapiècerie, ça pourrait devenir notre nouveau foyer.', '', 38134),
(194913, 'frFR', 'Je suis offensé rien qu\’à l\’idée de devoir me salir les mains en tuant ces Hurans. Ils découvriront bientôt la toute-puissance de la Horde.', '', 38134),
(194912, 'frFR', 'ÉCRABOUILLONS CE GROS MACHIN ! HA HA !', '', 38134),
(194910, 'frFR', '', 'Je suis issue d\’une lignée royale. N\’hésitez pas à vous prosterner.', 38134),
(194908, 'frFR', '', 'La Lumière brille… jusqu\’ici !', 38134),
(194907, 'frFR', 'Le réseau sera nôtre !', 'Le réseau sera nôtre !', 38134),
(194906, 'frFR', 'Les cercles vont cracher leurs secrets !', 'Les anneaux vont cracher leurs secrets !', 38134),
(194905, 'frFR', 'C\’est parti pour la chasse au grigri !', '', 38134),
(194902, 'frFR', 'Soyez sur vos gardes, dans l\’Antre, où vous risqueriez de perdre votre âme pour toujours.', '', 38134),
(194901, 'frFR', 'Les vivants n\’ont pas leur place en Ombreterre.\n\nRestez sur vos gardes et méfiez-vous de ce que perçoivent vos sens.\n\nSouvenez-vous-en.', '', 38134),
(194897, 'frFR', 'Cordier pourrait être rapide comme… une grue bondisseuse !\n\nSix… sept… non, deux bras et des pouces costauds pour porter des trucs.', '', 38134),
(194895, 'frFR', 'Cordier a hâte d\’avoir à nouveau un corps !', '', 38134),
(194883, 'frFR', 'Le Fléau. Nous devons vite gagner l\’hôtel de ville !', 'Le Fléau. Nous devons vite gagner l\’hôtel de ville !', 38134),
(194880, 'frFR', 'Tu n\‘as qu\’un mot à dire, Ben.', 'Tu n\‘as qu\’un mot à dire, Ben.', 38134),
(194875, 'frFR', 'Ces rumeurs à propos du Fléau… C\’est donc ça ?', 'Ces rumeurs à propos du Fléau… C\’est donc ça ?', 38134),
(194874, 'frFR', 'C\’est la fin du monde !', 'C\’est la fin du monde !', 38134),
(194873, 'frFR', 'Qu\’est-ce que je vais faire ? Où puis-je me cacher ?', 'Qu\’est-ce que je vais faire ? Où puis-je me cacher ?', 38134),
(194872, 'frFR', 'Ben ! Que se passe-t-il ?', 'Ben ! Que se passe-t-il ?', 38134),
(194856, 'frFR', 'L\’hôtel de ville est notre seul espoir de survie… Je vais commencer les préparatifs.', '', 38134),
(194845, 'frFR', 'L\’Accusatrice saura quoi faire… elle sait toujours…', '', 38134),
(194844, 'frFR', '', 'Vous épuisez ma patience !', 38134),
(194837, 'frFR', '', 'Je vous remercie, Z\’rali. Vous faites un excellent spécimen.', 38134),
(194836, 'frFR', '', 'Ah, ainsi vous sentez la douleur. C\’est étonnant.', 38134),
(194835, 'frFR', 'Enfin, l\’envahisseur entre en scène. Que le spectacle commence !', '', 38134),
(194833, 'frFR', '', 'La Lumière soit louée, vous nous avez trouvés !', 38134),
(194832, 'frFR', 'Il semble que nous ayons retrouvé l\’amiral suprême. Je vais faire signe à la Lame d\’ébène d\’avancer.', '', 38134),
(194831, 'frFR', 'Là-bas ! C\’est dame Portvaillant !', '', 38134),
(194830, 'frFR', 'Poursuivons les recherches.', '', 38134),
(194829, 'frFR', 'Désolé, Solomon, ce sera sans moi. La vie de fermier et bûcheron me suffit amplement désormais.', '', 38134),
(194828, 'frFR', 'Si l\’un de nos alliés a été pris là-dedans, je doute qu\’il en revienne.', '', 38134),
(194827, 'frFR', 'Ne restons pas là.', '', 38134),
(194826, 'frFR', 'Quoi qu\’il se passe ici, mieux vaut que ça se fasse sans nous.', '', 38134),
(194825, 'frFR', 'Ah, Ben ! Nous envisageons de reformer la milice locale. Ça t\’intéresserait d\’en reprendre les rênes ?', '', 38134),
(194823, 'frFR', 'Évitons de nous faire dévorer.', '', 38134),
(194822, 'frFR', 'J\’espère que nos alliés n\’ont pas terminé dans le gosier de cette… chose…', '', 38134),
(194821, 'frFR', 'Mieux vaut ne pas affronter ce colosse. Je doute que nos armes puissent lui faire grand mal.', '', 38134),
(194814, 'frFR', '', 'Votre espèce est vraiment fascinante… Vous renfermez tant de secrets…', 38134),
(194799, 'frFR', '', 'Lok-tar ! Désormais, tout Maldraxxus sait qu\’il ne faut pas vous défier à la légère.', 38134),
(194795, 'frFR', '', 'Quelles sont ces monstruosités ?! Elles ne sont pas encore actives, louée soit l\’Archonte.', 38134),
(194794, 'frFR', '', 'À vous les cieux, transcendés ! Pour le Bastion !', 38134),
(194773, 'frFR', '', 'Ce plan ne peut pas échouer. Enfin, pour peu que vous me trouviez ce dont j\’ai besoin.', 38134),
(194772, 'frFR', '', 'Matyas n\’utilisera plus jamais ma stèle du vice contre moi.', 38134),
(194769, 'frFR', 'J\’espère vous revoir, Zayhad ! La reine sait à quel point les bons compagnons se font rares.', '', 38134),
(194768, 'frFR', '', 'Une terre fertile se doit d\’être labourée. Poussez bien, Marasmius.', 38134),
(194767, 'frFR', 'Humpf. Ma foi, vous avez bien aménagé les lieux. Et il semble que la cour n\’ait rien perdu de sa diligence.', '', 38134),
(194766, 'frFR', '', 'C\’est toute la communauté qui bénéficie de ses efforts. Le cœur de la Forêt s\’épanouit en votre présence.', 38134),
(194765, 'frFR', 'Vous croyez ? Jusqu\’à présent, cette âme mortelle est la seule à m\’avoir traité avec le respect qui m\’est dû.', '', 38134),
(194764, 'frFR', '', 'Peut-être les cercles des Faë vont-ils nous accueillir à nouveau.', 38134),
(194763, 'frFR', 'Zayhad, vieille branche ! Le vent n\’a rien à voir là-dedans. C\’est notre jeune $uami:amie; qui m\’a conduit ici.', '', 38134),
(194762, 'frFR', '', 'Marasmius, le vent a-t-il précipité votre retour ?', 38134),
(194760, 'frFR', '', 'Bonjour, petite créature. Qu\’apportez-vous donc là ?', 38134),
(194759, 'frFR', '', 'La pousse devient le tronc. Le tronc soutient le ciel. Le ciel fait naître la pousse.', 38134),
(194758, 'frFR', '', 'Tout vient à point à qui sait attendre.', 38134),
(194757, 'frFR', '', 'Tout ce que vous voyez autour de vous est le fruit d\’innombrables vies.$b$bMagnifique, n\’est-ce pas ?', 38134),
(194749, 'frFR', 'Ha ! Vous ne méritez que ça ! Dégagez de ma forêt !', '', 38134),
(194748, 'frFR', 'Un peu de respect, que diable !', '', 38134),
(194747, 'frFR', 'Ça leur apprendra !', '', 38134),
(194746, 'frFR', 'Je t\’aime aussi.', '', 38134),
(194744, 'frFR', 'Ça me rappelle le jour où je me suis retrouvé dans le miroir qui servait de garde-robe à…', '', 38134),
(194743, 'frFR', 'Ravi de vous revoir, Sybille. Je crois que je vous dois encore un miroir.', 'Ravie de vous revoir, Sybille. Je crois que je vous dois encore un miroir.', 38134),
(194742, 'frFR', 'Vous vous trompez, mon cher Tubbins. Dame Sybille arrive toujours à point nommé.', '', 38134),
(194741, 'frFR', '', 'Sans cervelle, mais essentiels.', 38134),
(194740, 'frFR', 'Eh oui ! Ce ne sera pas long, tu vas voir.', '', 38134),
(194735, 'frFR', '', 'Faux esprit, heureuse folie.', 38134),
(194734, 'frFR', 'Ah, encore un des valets des Kyrians. Êtes-vous ici pour vous faire massacrer vous aussi ?', '', 38134),
(194732, 'frFR', 'Dites-moi quand vous voudrez y aller. La première fois est toujours un peu… déstabilisante !', '', 38134),
(194731, 'frFR', '', 'Protéger être aimé, pas offenser.', 38134),
(194730, 'frFR', 'Ha ! Ces vauriens ont eu ce qu\’ils méritaient ! À nous trois, nous formons une bonne équipe.', '', 38134),
(194729, 'frFR', 'Entendu, mon amour.', '', 38134),
(194728, 'frFR', '', 'Reviens dès que tu auras terminé ! J\’ai une livraison de bois pour le magistrat.', 38134),
(194727, 'frFR', '', 'En ce jour, nous allons rendre justice pour nos frères tombés et mettre fin aux horreurs qui se trament ici !', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194726, 'frFR', '', 'Déballer, voler, filer !', 38134),
(194725, 'frFR', '', 'Déboussolé du parasol !', 38134),
(194711, 'frFR', 'Mais sans l\’ombre, voilà que je sombre…', '', 38134),
(194709, 'frFR', 'Pas d\’ombre sans encombre.', '', 38134),
(194708, 'frFR', 'Je t\’apporterai des planches tout à l\’heure. Cadeau de la maison !', '', 38134),
(194707, 'frFR', 'Cette fois, c\’est pire, Ben. Un rocher a transpercé la coque de part en part !', '', 38134),
(194706, 'frFR', 'Hé, Vernon ! Encore un problème de voie d\’eau ?', '', 38134),
(194677, 'frFR', '', 'Branle-bas, recrue $n ! On approche de l\’île.', 38134),
(194672, 'frFR', 'Cette affaire s\’est avérée très fructueuse, $p. J\’espère que nous aurons à nouveau l\’occasion de travailler ensemble.', '', 38134),
(194671, 'frFR', 'Non, non, ça ne suffira pas. Il me les faut tous ! Une telle occasion ne se présentera sans doute pas deux fois.', '', 38134),
(194670, 'frFR', 'Je vous suis infiniment reconnaissant d\’avoir accepté de m\’aider à préserver tous ces autres ouvrages.', '', 38134),
(194669, 'frFR', 'Peu importe, nous sommes légion. Je vais te tuer de mes propres mains.', '', 38134),
(194668, 'frFR', 'Marasmius ! Il vient à notre aide !', '', 38134),
(194666, 'frFR', 'Tuez-les tous.', '', 38134),
(194665, 'frFR', 'Idiot. Tu nous as menés droit au but.', '', 38134),
(194664, 'frFR', '', 'Patience, vous allez comprendre. Ces instants de paix sont essentiels pour cerner cette âme.', 38134),
(194663, 'frFR', '', 'Bizarre… Pourtant, tout a l\’air en ordre. S\’agit-il vraiment de ses derniers instants ?', 38134),
(194661, 'frFR', 'Vous persistez ? Très bien. Les charognards se repaîtront de votre essence.', '', 38134),
(194657, 'frFR', '', 'Je suis sûre qu\’Ysera et vous avez beaucoup à vous dire.', 38134),
(194637, 'frFR', '', 'Comprenez-vous, à présent ?', 38134),
(194636, 'frFR', 'Encore une guerre ? Arh, c\’est dur.', '', 38134),
(194629, 'frFR', 'Je vous condamne à brûler !', '', 38134),
(194625, 'frFR', 'Ma… dernière… sentence…', '', 38134),
(194624, 'frFR', 'Ces traîtres doivent être punis !', '', 38134),
(194618, 'frFR', '', 'Les portes s\’ouvrent ! Ce sont les mêmes pour tous, mais chaque binôme se rendra dans un monde différent.', 38134),
(194617, 'frFR', '', 'Nous y voilà.', 38134),
(194616, 'frFR', '', 'Nous consacrons notre vie à servir les nôtres, mais aussi les âmes de tout l\’univers qui font appel à nous.', 38134),
(194615, 'frFR', '', 'Nous restons fidèles à nos principes. Nous apprenons et nous nous améliorons sans cesse dans le cadre de notre vocation.', 38134),
(194614, 'frFR', '', 'Nous demeurons forts dans l\’adversité. Aucun obstacle, aucune tragédie ne saurait nous déstabiliser.', 38134),
(194613, 'frFR', 'Beau travail, mon âme liée. Notre ascension est proche.', '', 38134),
(194612, 'frFR', '', 'J\’ai trouvé le parchemin rituel. Cette formule va nous ouvrir les portes.', 38134),
(194611, 'frFR', 'La loyauté envers l\’Archonte mérite châtiment !', '', 38134),
(194610, 'frFR', 'Vous connaissez la vérité.', '', 38134),
(194609, 'frFR', 'Prouvez votre loyauté !', '', 38134),
(194608, 'frFR', 'Allez-y, $p. Je… J\’ai besoin d\’un instant pour réfléchir.', '', 38134),
(194607, 'frFR', 'Je comprends, à présent. L\’honneur a déserté Maldraxxus, mais… Ensemble, nous allons lui rendre !', '', 38134),
(194606, 'frFR', '', 'L\’Arbitre a vu que loin d\’être un fardeau, vos souvenirs sont la source de votre force. C\’est pour ça que votre place est en Maldraxxus.', 38134),
(194605, 'frFR', 'Je… Je crois que j\’ai enfin compris. Retournons auprès du parangon.', '', 38134),
(194604, 'frFR', 'Mon fils, je suis tellement fier de toi. Ça, rien ne pourra jamais me l\’enlever.', '', 38134),
(194603, 'frFR', 'Père… ?', '', 38134),
(194602, 'frFR', 'Je n\’oublierai jamais ton sacrifice. Jamais !', '', 38134),
(194601, 'frFR', 'Darion… Tu es mort pour me libérer du Fléau… Et tu continues à défendre Azéroth !', '', 38134),
(194600, 'frFR', 'Bon… Avançons, s\’il vous plaît.', '', 38134),
(194598, 'frFR', 'Sa trahison me hante jusqu\’après la mort. Mon fils… Cette douleur-là, il ne vaudrait pas mieux l\’oublier ?', '', 38134),
(194597, 'frFR', 'Renaud. Mon aîné… et ma perte.', '', 38134),
(194596, 'frFR', 'Pardonnez-moi, $p. Poursuivons.', '', 38134),
(194595, 'frFR', 'Elle brille du même éclat que notre dernière étreinte. Dire qu\’elle serait tombée dans l\’oubli si je n…', '', 38134),
(194594, 'frFR', 'Serait-ce… Elena ? Ô, mon amour… ma lueur…', '', 38134),
(194593, 'frFR', 'Le courage m\’est toujours venu de la Lumière. Mais… je vais voir ce que je peux apprendre.', '', 38134),
(194592, 'frFR', '', 'Méditez sur ces écrits, Alexandros. Cherchez-y la source de votre courage. Ce qui fait notre différence.', 38134),
(194591, 'frFR', '', 'Cette voie ne mène qu\’à notre perte. Je vais façonner un nouveau destin pour les nôtres !', 38134),
(194590, 'frFR', 'Mais ces problèmes nous viennent d\’ailleurs ! Tant que nous suivons la voie, nous pouvons les affronter ensemble !', '', 38134),
(194589, 'frFR', '', 'Et où ça nous a menés ? Les aspirants qui ne peuvent plus transcender, les centurions laissés à pourrir…', 38134),
(194588, 'frFR', '', 'Mais nous devenons plus que nos passés, vous ne le voyez donc pas ?', 38134),
(194587, 'frFR', '', 'Tous ces souvenirs effacés, toutes ces vies oubliées… Il leur sera rendu justice !', 38134),
(194586, 'frFR', '', 'Nous débattrons de votre sort en temps voulu. Vous pouvez rester ici en attendant, sous bonne garde.', 38134),
(194585, 'frFR', '', 'Vous l\’avez. Pour l\’instant.', 38134),
(194584, 'frFR', 'Archonte, j\’implore votre miséricorde. J\’ignorais que…', '', 38134),
(194583, 'frFR', '', 'Quant à vous, Uther…', 38134),
(194580, 'frFR', 'Avez-vous perdu la tête, Lysonia ?', '', 38134),
(194578, 'frFR', 'Se donner à ces ténèbres, c\’est trahir tout ce qui fait de nous des Kyrians !', '', 38134),
(194577, 'frFR', '', 'L\’âme brisée d\’Uther m\’a révélé tout le pouvoir de l\’Antre. Et maintenant, je vais vous en faire la démonstration !', 38134),
(194576, 'frFR', '', 'VOUS N\’ÊTES RIEN FACE À L\’ANTRE !', 38134),
(194575, 'frFR', '', 'Votre service est terminé.', 38134),
(194574, 'frFR', '', 'Impossible… cette vision…', 38134),
(194573, 'frFR', '', 'Maintenant !', 38134),
(194572, 'frFR', '', 'Tant de puissance, et pourtant vous ne voyez pas. Laissez-moi lever le voile posé devant vos yeux.', 38134),
(194571, 'frFR', '', 'Ton maître t\’abreuve de mensonges.', 38134),
(194570, 'frFR', '', 'Tes menaces sont vaines, transcendée.', 38134),
(194569, 'frFR', '', 'Kyrestia, votre règne prend fin aujourd\’hui ! Les Kyrians auront bientôt un nouveau maître !', 38134),
(194566, 'frFR', '', 'Venez, Sans-Entraves. Venez assister à la chute de l\’Archonte !', 38134),
(194565, 'frFR', 'Vous devez triompher de Lysonia coûte que coûte.', '', 38134),
(194564, 'frFR', 'Nous allons empêcher ses renforts d\’aller appuyer l\’attaque. Je vous le promets.', '', 38134),
(194563, 'frFR', 'Le Bastion tomberait entre ses mains.', '', 38134),
(194562, 'frFR', 'J\’espère simplement qu\’il n\’est pas trop tard. Si l\’Archonte venait à tomber…', '', 38134),
(194561, 'frFR', 'Le reste de nos troupes est aussi en train de battre en retraite vers le fort.', '', 38134),
(194560, 'frFR', 'Les désavoués marchent sur le fort Élyséen ! Il faut rentrer tout de suite !', '', 38134),
(194559, 'frFR', '', 'Je touche au but. Vous ne m\’empêcherez pas d\’accomplir ma destinée.', 38134),
(194558, 'frFR', '', 'Une fois l\’Archonte abattue, même vos chers parangons devront voir la vérité.', 38134),
(194557, 'frFR', '', 'Mais c\’est déjà fait. Votre petite attaque a laissé l\’Archonte sans défense pour la première fois.', 38134),
(194556, 'frFR', '', 'Jamais je n\’abandonnerai mon peuple, moi ! Quel que soit le prix !', 38134),
(194554, 'frFR', '', 'Je peux vous montrer votre passé. Ce royaume peut être sauvé si vous rejetez les mensonges de l\’Archonte !', 38134),
(194553, 'frFR', '', 'C\’est tout, vraiment ? Montrez-moi donc la puissance de l\’Archonte ! Venez prendre cette victoire que vous voulez tant !', 38134),
(194552, 'frFR', 'C\’est votre dernière chance. Vos soldats sont vaincus. Vous avez déjà perdu !', '', 38134),
(194551, 'frFR', '', 'Pauvre petit naïf. Si vous êtes les derniers à pouvoir m\’affronter, alors j\’ai déjà gagné.', 38134),
(194550, 'frFR', '', 'La Lumière, le Vide… Ces pouvoirs mineurs ont peu d\’incidence ici. Si l\’Arbitre vous a envoyé en Maldraxxus, elle avait une raison. Essayons de la comprendre.', 38134),
(194549, 'frFR', 'Mais pourquoi n\’ai-je pas été appelé ? Ma foi en la Lumière n\’était pas assez forte ? Est-ce la non-mort qui a souillé mon âme irrémédiablement ?', '', 38134),
(194547, 'frFR', '', 'Les souvenirs de votre vie continuent à vous hanter dans la mort. Si vous étiez appelé au Bastion, ils ne seraient qu\’un fardeau.', 38134),
(194545, 'frFR', 'Face aux Maldraxxi, je n\’ai d\’abord vu que le Fléau qui a dévasté mon pays. Les monstres que j\’avais fait le serment d\’exterminer.', '', 38134),
(194544, 'frFR', '', 'Au temple de la Pureté, nos aspirants font l\’abandon de leurs souvenirs, mais c\’est une autre voie que nous allons emprunter. Maldraxxus ne vous convient-il pas ?', 38134),
(194543, 'frFR', '', 'Accompagnez-moi tous les deux.', 38134),
(194542, 'frFR', 'État de la cible : vi-vi-vi-vivante. Élimination en cours…', '', 38134),
(194541, 'frFR', 'Ses systèmes commencent à faiblir ! Continuez comme ça !', '', 38134),
(194539, 'frFR', 'Je me souviens de… l\’humiliation. Maintenant, c\’est votre tour.', '', 38134),
(194538, 'frFR', '', 'On a formé tellement d\’aspirants ensemble. Tu ne te souviens vraiment pas ?', 38134),
(194537, 'frFR', 'Ton inacceptable détecté. Désactivation du mode non létal.', '', 38134),
(194536, 'frFR', '', 'Tout mais pas lui…', 38134),
(194535, 'frFR', 'Mais, Athanos… ?', '', 38134),
(194534, 'frFR', '', 'La voie est libre vers le sud. Il faut nous hâter !', 38134),
(194533, 'frFR', '', 'C\’est là qu\’est toute l\’hypocrisie de l\’Archonte.', 38134),
(194532, 'frFR', '', 'Vous pensez servir le bien, mais vous ne faites qu\’exécuter inlassablement les vôtres.', 38134),
(194531, 'frFR', '', 'Je vais continuer ce qu\’elle a commencé. Notre sort à tous en dépend.', 38134),
(194530, 'frFR', '', 'Vous osez prononcer son nom ? Le grand œuvre de mon parangon ne fait que commencer !', 38134),
(194529, 'frFR', '', 'Vous savez comment ça va terminer. Vous allez finir comme Déva !', 38134),
(194528, 'frFR', '', 'Silence, petit aspirant. Vous ignorez ce qu\’est la folie.', 38134),
(194527, 'frFR', '', 'J\’ai dû renoncer à mes souvenirs, à mon passé, à toute mon existence !', 38134),
(194526, 'frFR', 'Lysonia, il n\’est pas trop tard pour renoncer à cette folie ! Nous sommes tous des Kyrians !', '', 38134),
(194524, 'frFR', '', 'On sera avec vous jusqu\’au bout !', 38134),
(194523, 'frFR', 'Quel jour glorieux ! Bon, à présent, si vous voulez bien m\’excuser, je dois mettre la dernière touche à une petite merveille d\’amélioration pour Bron.', '', 38134),
(194521, 'frFR', 'Vous êtes $gprêt:prête; ?', '', 38134),
(194517, 'frFR', 'Lysonia m\’a servi les mêmes mensonges. J\’ignorais qu\’elles servaient les ténèbres…', '', 38134),
(194516, 'frFR', 'Déva m\’a convaincu que je ne méritais pas de mourir. Elle m\’a promis la justice.', '', 38134),
(194515, 'frFR', 'Ma formation d\’aspirant a été difficile. J\’ai tout oublié de ma vie passée.', '', 38134),
(194514, 'frFR', 'Votre miséricorde me va droit au cœur. Je ne l\’oublierai pas.', '', 38134),
(194513, 'frFR', 'J\’aimerais bien le savoir aussi. Je vous suis.', '', 38134),
(194512, 'frFR', '', 'Faut-il vraiment que je m\’occupe de tout ?', 38134),
(194511, 'frFR', '', 'Votre heure n\’est pas venue. Levez-vous et combattez !', 38134),
(194510, 'frFR', '', 'Ils étaient faibles. Vous me rendez service en écrémant leurs rangs.', 38134),
(194509, 'frFR', '', 'C\’est sans importance.', 38134),
(194508, 'frFR', '', 'Vous refusez de vous battre à la loyale ? Soit, j\’en ferai de même. À moi, Sans-Entraves !', 38134),
(194506, 'frFR', 'Vous êtes toujours aussi écervelée, Nadjia. Mes gardes vont vous régler votre compte.', '', 38134),
(194504, 'frFR', '', 'Approchez donc. Venez me décevoir comme l\’a fait Uther.', 38134),
(194502, 'frFR', '', 'Enfin ! J\’ai mis à profit le temps que j\’ai passé en cage pour me préparer à ce duel.', 38134),
(194500, 'frFR', '', 'Kyrestia a raison : l\’Ombreterre est brisée. Mais c\’est de la force qu\’il faut pour la restaurer. Une force dont elle manque cruellement.', 38134),
(194498, 'frFR', '', 'Je vais finir ce que Déva a commencé. Plus jamais l\’Archonte ne nous aveuglera avec sa « voie ».', 38134),
(194495, 'frFR', '', 'Son anima est à nous.', 38134),
(194494, 'frFR', '', 'Nous avons perdu les flèches, certes, mais notre débâcle n\’est pas totale.', 38134),
(194493, 'frFR', 'La victoire est presque à nous, Sans-Entraves… Arrachez-la…', '', 38134),
(194490, 'frFR', '', 'Le regard de l\’Archonte est sur nous.', 38134),
(194489, 'frFR', '', 'Je vais dégager l\’arène. La porte menant au temple est juste au nord.', 38134),
(194487, 'frFR', '', 'Venez par ici !', 38134),
(194485, 'frFR', '', 'TÂTEZ DE MON ACIER !', 38134),
(194483, 'frFR', '', 'Vous n\’avez pas oublié votre entraînement au Creuset, si ? Le convertir n\’a été qu\’une formalité…', 38134),
(194482, 'frFR', 'Oui… Oui, mon parangon.', '', 38134),
(194481, 'frFR', '', 'Mais arrêtez-les, bon sang ! Même si votre anima doit y passer jusqu\’à la dernière goutte !', 38134),
(194480, 'frFR', '', 'Occupez-les, la Porte-Vérité est presque rechargée !', 38134),
(194479, 'frFR', '', 'Vous êtes coriaces, hein ?', 38134),
(194478, 'frFR', 'Nous allons tenir leurs renforts en respect. Avancez !', '', 38134),
(194477, 'frFR', '', 'Ils sont encore debout. Tirez à nouveau !', 38134),
(194476, 'frFR', '', 'Alors, rechargez ! Prenez celui des centurions s\’il le faut !', 38134),
(194475, 'frFR', 'Réserve d\’anima du colosse tarie. Il faut recharger.', '', 38134),
(194474, 'frFR', '', 'Faites pleuvoir la mort !', 38134),
(194473, 'frFR', 'Quel plaisir de vous rencontrer enfin !', '', 38134),
(194469, 'frFR', '', 'Mes champions ne seront pas si faciles à vaincre.', 38134),
(194468, 'frFR', '', 'Vous n\’avez tué que de simples soldats. D\’autres prendront leur place.', 38134),
(194465, 'frFR', '', 'Contemplez la gloire déchaînée des désavoués !', 38134),
(194464, 'frFR', '', 'La subtilité n\’a jamais été le fort de Kyrestia.', 38134),
(194463, 'frFR', '', 'Alors on charge droit devant, hein ?', 38134),
(194462, 'frFR', 'Gloire à l\’archonte et au Bastion !', '', 38134),
(194460, 'frFR', 'À votre signal.', '', 38134),
(194459, 'frFR', 'Sans-Entraves, à vous d\’attaquer de front avec le gros des troupes.', '', 38134),
(194458, 'frFR', 'Nos parangons frapperont depuis les flancs avec leurs fidèles.', '', 38134),
(194456, 'frFR', 'Transcendés, notre heure est venue. Aujourd\’hui, nous allons mettre fin à la menace des désavoués.', '', 38134),
(194444, 'frFR', 'C\’est une joie de vous voir, Gubbins.', 'C\’est une joie de vous voir, Gubbins.', 38134),
(194443, 'frFR', 'Gubbins content !', '', 38134),
(194442, 'frFR', 'Je souviens toujours ! Je souviens la théière aussi !', '', 38134),
(194440, 'frFR', 'Mes très chers Gubbins et Tubbins ! J\’ai un instant craint que vous n\’ayez oublié l\’heure.', '', 38134),
(194437, 'frFR', '', 'Justice sera faite.', 38134),
(194428, 'frFR', '', 'Très bien. J\’ai trouvé quelqu\’un pour t\’aider.', 38134),
(194427, 'frFR', '', 'Oui ! J\’ai !', 38134),
(194426, 'frFR', '', 'Tu as une liste, Sika ?', 38134),
(194425, 'frFR', '', 'Oh, hoo ! Rebonjour !', 38134),
(194423, 'frFR', 'Les bêtes du Bastion sont plus indisciplinées que les Kyrians. Essayez d\’en dompter quelques-unes.', '', 38134),
(194421, 'frFR', 'Hou ! Besoin d\’aide ? Je peux vous rendre plein de services ou vous tenir compagnie !', 'Hou ! Besoin d\’aide ? Je peux vous rendre plein de services ou vous tenir compagnie !', 38134),
(194414, 'frFR', 'Magnifique. Voilà qui devrait vraiment nous aider.', '', 38134),
(194413, 'frFR', 'Les négociants ont obtenu un stock d\’étranges armes d\’autres royaumes. Voyons si elles peuvent servir notre cause.', '', 38134),
(194402, 'frFR', '', 'C\’est à elle de voir. Commençons par lui montrer.', 38134),
(194401, 'frFR', 'Tu penses que cette âme pourra vraiment nous aider ?', '', 38134),
(194391, 'frFR', '', 'Je vous l\’avais dit, son potentiel est formidable.', 38134),
(194388, 'frFR', 'Ouvrez-moi votre esprit, $umortel:mortelle;, et voyons quels souvenirs en jaillissent.', 'Ouvrez-moi votre esprit, $umortel:mortelle;, et voyons quels souvenirs en jaillissent.', 38134),
(194385, 'frFR', 'Oui, oui. Merci, Gubbins. Moi c\’est le plus malin !', '', 38134),
(194384, 'frFR', 'Tubbins savait ! Tubbins trop fort !', '', 38134),
(194378, 'frFR', '<Siffle>', '<Siffle>', 38134),
(194377, 'frFR', 'Il faut savoir prendre la mort du bon côté…', 'Il faut savoir prendre la mort du bon côté…', 38134),
(194375, 'frFR', 'Imaginez ma surprise ! Un simple négociant comme moi qui se retrouve à travailler aux côtés d\’une figure aussi spéciale !', '', 38134),
(194363, 'frFR', 'Vous allez apprendre la vérité… et puis mourir… ', '', 38134),
(194358, 'frFR', '', 'À la fin… ils ouvriront les yeux…', 38134),
(194336, 'frFR', '', 'Dis-toi que ça fait partie de ton entraînement, cher ami.', 38134),
(194335, 'frFR', 'On est vraiment obligés de marcher ? Mon épreuve m\’a bien fatigué.', '', 38134),
(194334, 'frFR', '', 'Avancez. Pélagos et moi, on ira à pieds.', 38134),
(194332, 'frFR', 'Visiblement, tous mes outils répondent à l\’appel. Et je dois un miroir à quelqu\’un.', 'Visiblement, tous mes outils répondent à l\’appel. Et je dois un miroir à quelqu\’un.', 38134),
(194331, 'frFR', 'Autant m\’amuser un peu sur le chemin de la fête.', 'Autant m\’amuser un peu sur le chemin de la fête.', 38134),
(194330, 'frFR', 'Eh bien, voilà qui est fort commode… Comment donc mon sac a-t-il fini ici ?', 'Eh bien, voilà qui est fort commode… Comment donc mon sac a-t-il fini ici ?', 38134),
(194328, 'frFR', '', 'Les désavoués cherchent à nous empêcher de transcender. Il faut trouver les autres !', 38134),
(194327, 'frFR', 'J\’aimerais pouvoir en dire autant ! Vous ne devinerez jamais toutes les contrariétés qu\’il m\’a fallu souffrir depuis ce matin !', '', 38134),
(194326, 'frFR', 'Je n\’ai pas à me plaindre.', 'Je n\’ai pas à me plaindre.', 38134),
(194324, 'frFR', 'Vulca, très chère, soyez la bienvenue ! J\’ose espérer que vous avez fait bon voyage ?', '', 38134),
(194319, 'frFR', 'Donnez-moi quelque chose à cogner !', 'Donnez-moi quelque chose à cogner !', 38134),
(194318, 'frFR', 'J\’ai un mauvais pressentiment…', 'J\’ai un mauvais pressentiment…', 38134),
(194317, 'frFR', 'Je ne cèderai pas comme ça !', 'Je ne cèderai pas comme ça !', 38134),
(194316, 'frFR', 'Je ne… dormirai pas… avant… la fête…', 'Je ne… dormirai pas… avant… la fête…', 38134),
(194315, 'frFR', 'Je pourrais peut-être me reposer… mais rien qu\’un instant !', 'Je pourrais peut-être me reposer… mais rien qu\’un instant !', 38134),
(194314, 'frFR', 'Je ne peux pas m\’arrêter maintenant. Je dois continuer d\’avancer.', 'Je ne peux pas m\’arrêter maintenant. Je dois continuer d\’avancer.', 38134),
(194297, 'frFR', 'Je ferais mieux de mettre l\’eau sur le feu…', '', 38134),
(194296, 'frFR', 'Il semblerait que je sois arrivé le premier, malgré les événements inopinés de ce matin.', '', 38134),
(194293, 'frFR', '', 'Mograine ne cesse de vanter votre résolution et votre bravoure, Draka. C\’est un honneur d\’enfin vous rencontrer.', 38134),
(194292, 'frFR', '', 'Vous tombez à point nommé, parangon. Je crois que nous avons un ennemi commun.', 38134),
(194291, 'frFR', 'Peut-être pourrai-je minimiser mes pertes…', '', 38134),
(194290, 'frFR', 'Voici venir de quoi nous remplir les poches.', '', 38134),
(194289, 'frFR', 'Sybille a raison. Assez discuté. Passez-moi donc le thé et les confitures !', 'Sybille a raison. Assez discuté. Passez-moi donc le thé et les confitures !', 38134),
(194288, 'frFR', '', 'Embrassades, sérénades… Tartinade en rade !', 38134),
(194287, 'frFR', 'Oui ! L\’heure du thé des histoires !', '', 38134),
(194286, 'frFR', 'Je suis plus intelligent !', '', 38134),
(194285, 'frFR', 'Non, mon cher Tubbins, je me dois d\’insister : c\’est moi qui ai vécu l\’aventure la plus intéressante aujourd\’hui !', '', 38134),
(194284, 'frFR', 'Vous avez tué tous ces défenses-de-sang à vous $useul:seule; ?', 'Vous avez tué tous ces défenses-de-sang à vous $useul:seule; ?', 38134),
(194281, 'frFR', 'Première étape : faites-vous remarquer !', '', 38134),
(194279, 'frFR', 'Il ne suffit pas d\’avoir du talent au combat, vous devez créer des liens !', '', 38134),
(194278, 'frFR', 'Fichez le camp, fichez le camp, DU VENT ! C\’est à MOI ! Je l\’ai trouvé. Ce trésor m\’appartient. Il est à moi.\n\nPartez ! Partez maintenant et retournez dans le royaume crasseux dont vous êtes $usorti:sortie;. Jetez-vous du haut des remparts, je m\’en moque, mais laissez-moi tranquille avec mon trésor.\n\nJ\’en ai besoin. J\’en ai plus besoin que quelqu\’un comme vous ne pourrait le comprendre, avec votre armure rare et vos vêtements de luxe. C\’EST À MOI !', 'Fichez le camp, fichez le camp, DU VENT ! C\’est à MOI ! Je l\’ai trouvé. Ce trésor m\’appartient. Il est à moi.\n\nPartez ! Partez maintenant et retournez dans le royaume crasseux dont vous êtes $usorti:sortie;. Jetez-vous du haut des remparts, je m\’en moque, mais laissez-moi tranquille avec mon trésor.\n\nJ\’en ai besoin. J\’en ai plus besoin que quelqu\’un comme vous ne pourrait le comprendre, avec votre armure rare et vos vêtements de luxe. C\’EST À MOI !', 38134),
(194271, 'frFR', 'Non, non, NON ! Ce trésor est à moi !', '', 38134),
(194266, 'frFR', '$UParé:Parée; pour votre prochain duel, $r ?', '', 38134),
(194265, 'frFR', 'J\’espère que nous pourrons bientôt conclure des accords profitables.', '', 38134),
(194250, 'frFR', 'Avant que vous ne vous lanciez dans l\’arène, je dois évaluer votre potentiel de rentabilité. Je jugerai de votre valeur en fonction de l\’évolution de notre partenariat.', '', 38134),
(194249, 'frFR', '', 'Nécromancien ! Venez m\’affronter ! Vengeance pour Thanikos !', 38134),
(194247, 'frFR', 'Il est temps d\’apprendre les bonnes manières à ces vauriens !', '', 38134),
(194246, 'frFR', 'Ah, douces ténèbres…', '', 38134),
(194245, 'frFR', 'Voilà précisément pourquoi je n\’approuverai jamais que l\’on coure avec un parasol !', '', 38134),
(194229, 'frFR', 'Il n\’y a vraiment rien de tel qu\’un résultat inattendu lors d\’une expérience ! Merci pour votre aide !', '', 38134),
(194228, 'frFR', 'Costume suivant !', '', 38134),
(194227, 'frFR', 'Bien, prenez vite position !', '', 38134),
(194224, 'frFR', 'Le sous-chef les empile ici aux bons soins de gardes vive-pierres. On le déteste tous, pour sûr ! Alors, récupérez ces stèles et aidez Nadjia !', '', 38134),
(194223, 'frFR', 'Ben, c\’est des stèles du vice. Le sous-chef s\’en sert pour forcer les autres crocs à faire ses quatre volontés. Et vous ? Vous aidez ma copine Nadjia ?', '', 38134),
(194219, 'frFR', 'Vous avez l\’air réglo. Les amis de Nadjia sont mes amis, comme on dit. Nous, on cherche les pierres couvertes de gribouillis. Elles sont éparpillées dans tout le fourré.', '', 38134),
(194211, 'frFR', 'J\’en étais sûr ! Exactement là où je le pensais !', '', 38134),
(194210, 'frFR', '', 'Souhaitez-vous que je partage mon savoir avec vous ?', 38134),
(194202, 'frFR', 'Vite ! Faut retrouver la théière porte-bonheur. Dépêche !', '', 38134),
(194201, 'frFR', 'Tubbins toujours porter chapeau, surtout…', '', 38134),
(194200, 'frFR', 'Tubbins toujours porter théière.', '', 38134),
(194199, 'frFR', 'Je croyais TU as la théière !', '', 38134),
(194197, 'frFR', 'Bon, on a arrivé un peu tard au goûter…', '', 38134),
(194196, 'frFR', 'Je ne peux rien faire ici sans mes outils…', 'Je ne peux rien faire ici sans mes outils…', 38134),
(194195, 'frFR', 'Je n\’arrive pas à croire qu\’ils m\’aient surpris en train de voler ces miroirs !', 'Je n\’arrive pas à croire qu\’ils m\’aient surprise en train de voler ces miroirs !', 38134),
(194194, 'frFR', 'Tout a commencé par un simple malentendu…', 'Tout a commencé par un simple malentendu…', 38134),
(194193, 'frFR', 'Sans mon parasol, je suis obligé de rester à l\’ombre… Maudite Lumière !', '', 38134),
(194192, 'frFR', 'Argh… Rendez-moi mon parasol, bande de ruffians !', '', 38134),
(194191, 'frFR', 'Asseyez-vous, prenez un peu de thé et écoutez le remarquable récit de mon aventure…', '', 38134),
(194181, 'frFR', 'Sybille en retard ! Pas Tubbins !', '', 38134),
(194178, 'frFR', '', 'Même pas la pétoche !', 38134),
(194177, 'frFR', '', 'Moches et sans caboche !', 38134),
(194174, 'frFR', '', 'Le bosquet chante sa joie et la splendeur retrouvée. Vous enchantez les graines et leur gardienne, et honorez la reine et sa cour.', 38134),
(194173, 'frFR', 'Je m\’éveille. Que l\’évaluation commence.', '', 38134),
(194170, 'frFR', '', 'Vous apportez subsistance aux racines qui s\’étendent en retour. Grâce à vous, bien des esprits pourront renaître.', 38134),
(194164, 'frFR', '', 'Je sens l\’anima qui pousse en votre jardin. Vous n\’êtes qu\’un arbuste mais vos branches nous abritent déjà.', 38134),
(194161, 'frFR', '', 'Le bosquet fleurit et il vous en remercie. Ses graines suivront. Nous aurons encore à cultiver.', 38134),
(194160, 'frFR', 'Il vous reste bien du potentiel. Revenez quand vous l\’aurez développé.', '', 38134),
(194159, 'frFR', 'L\’évaluation est satisfaisante. Je m\’incline.', '', 38134),
(194158, 'frFR', 'Vous serez évalués. Vous serez jaugés.', '', 38134),
(194140, 'frFR', '', 'Trois fois j\’ai assisté, sans rien exiger.', 38134),
(194137, 'frFR', 'Youpiiii !', '', 38134),
(194135, 'frFR', 'J\’ai réussi ! Waouh !', '', 38134),
(194127, 'frFR', '', 'Mon détachement a été attaqué par les dévoreurs. La brèche dans la tour leur a permis de nous prendre par surprise et ils nous ont encerclés en quelques secondes. J\’ai eu toutes les peines du monde à leur résister.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194124, 'frFR', '', 'C\’est le temps qui lui permettra de pousser. Et bientôt, vous pourrez l\’éveiller.', 38134),
(194122, 'frFR', '', 'Pour commencer, placez cet esprit dans une graine sauvage libre.', 38134),
(194120, 'frFR', 'Aidez-les à installer la scène. Allez, allez !', '', 38134),
(194117, 'frFR', 'Et maintenant, le prochain acte !', '', 38134),
(194106, 'frFR', '', 'Mais le catalyseur doit être planté avant la graine, ou il ne lui apportera pas ses bienfaits.', 38134),
(194105, 'frFR', '', 'Chaque type de catalyseur apporte un bienfait particulier. Expérimentez et exprimez-vous.', 38134),
(194104, 'frFR', '', 'Les graines sauvages puisent vigueur et bien-être dans les catalyseurs plantés près d\’elles.', 38134),
(194096, 'frFR', 'Tout le monde à sa place !', '', 38134),
(194094, 'frFR', 'Ah, vous voilà ! On va commencer !', '', 38134),
(194093, 'frFR', '', 'Vous pouvez les accepter sans détour. C\’est ainsi que sont faits les mortels.', 38134),
(194091, 'frFR', '', 'Lorsqu\’il renaît, un esprit exprime sa gratitude avec de menues récompenses.', 38134),
(194088, 'frFR', '', 'Ici poussent des graines sauvages uniques. Votre art devra pousser avec elles. Je saurai cultiver les deux.', 38134),
(194087, 'frFR', '', 'Il faut un soin attentionné et délicat pour les guider sur le chemin du destin. C\’est à nous qu\’il revient de les nourrir.', 38134),
(194085, 'frFR', 'Non, non ! Je dois résister !', '', 38134),
(194082, 'frFR', 'Il y a toujours de nouvelles découvertes à faire !', '', 38134),
(194081, 'frFR', 'Rezan est plus malin que je pensais. Peut-être qu\’un jour, il y aura un nouveau Loa qui viendra faire affaire avec le vieux Bwonsamdi, hein ?', '', 38134),
(194080, 'frFR', 'Je suis… faible… je vais… échouer…', '', 38134),
(194078, 'frFR', 'C\’est un grand honneur, votre Majesté. Et vous, $p, merci pour tout ce que vous avez fait pour moi… mort ou vivant.', '', 38134),
(194077, 'frFR', '', 'J\’ignore ce que vous réservera le destin, Vol\’jin des Sombrelances. Mais je vais vous donner une chance de renaître un jour.', 38134),
(194074, 'frFR', '', 'Ainsi que celui-ci. Comme il est étrange… Un esprit mortel lié à l\’essence d\’un Loa.', 38134),
(194073, 'frFR', '', 'Nous allons prendre soin de ces trois esprits.', 38134),
(194070, 'frFR', 'Oh, bonjour, ma reine ! Le vieux Bwonsamdi participe aux recherches pour retrouver les Loas disparus.', '', 38134),
(194067, 'frFR', 'Pitié ! Pitié !!!', '', 38134),
(194066, 'frFR', 'C\’est enfin… terminé…', '', 38134),
(194065, 'frFR', 'Cet échec… est une plaie… sur votre âme…', '', 38134),
(194064, 'frFR', 'Vous êtes faible. Jamais vous ne transcenderez.', '', 38134),
(194063, 'frFR', 'Han… Aidez-moi…', '', 38134),
(194062, 'frFR', 'Je suis vraiment désolé…', '', 38134),
(194061, 'frFR', 'Je suis… si seul…', '', 38134),
(194060, 'frFR', 'Trop… d\’épreuves…', '', 38134),
(194059, 'frFR', 'Jamais je ne ferai mon ascension.', '', 38134),
(194058, 'frFR', 'Kléia… Aide-moi !', '', 38134),
(194057, 'frFR', 'C\’est impossible !', '', 38134),
(194056, 'frFR', 'Aaahh…', '', 38134),
(194054, 'frFR', 'Tout est… pardonné…', '', 38134),
(194053, 'frFR', 'Le silence… enfin…', '', 38134),
(194052, 'frFR', 'Non !', '', 38134),
(194051, 'frFR', 'Pourquoi ? Pourquoi ?!', '', 38134),
(194050, 'frFR', 'Faux ! Tout faux !', '', 38134),
(194049, 'frFR', 'Est-ce que… j\’ai échoué ?', '', 38134),
(194048, 'frFR', 'Faites que… ça cesse…', '', 38134),
(194047, 'frFR', 'Je n\’ai jamais été à la hauteur !', '', 38134),
(194046, 'frFR', 'Souffrance !', '', 38134),
(194042, 'frFR', 'Non, moi je ne suis qu\’un vieux chasseur des ombres. Les nôtres, ils ont besoin de toi !', '', 38134),
(194040, 'frFR', 'Que mon être… puisse renaître… en toi.', '', 38134),
(194039, 'frFR', 'Si tu le veux… Prends ce qu\’il reste de mon essence… Porte-la… en Sylvarden…', '', 38134),
(194035, 'frFR', 'Oh ! Nous sommes loin du résultat escompté. Essayez encore !', '', 38134),
(194031, 'frFR', '', 'Le fleuve du temps s\’écoule et son courant est tumultueux. Seule, je me noierais. Ensemble, nous nagerons.', 38134),
(194030, 'frFR', '', 'Les esprits qui y sommeillent sont des esprits meurtris. Leur renaissance est incertaine comme peut l\’être la pluie.', 38134),
(194029, 'frFR', '', 'C\’est un endroit spécial, un berceau de renouveau où les âmes perdues peuvent trouver une nouvelle chance.', 38134),
(194028, 'frFR', '', 'Le bosquet vous murmure sa bienvenue par les branches de sa gardienne.', 38134),
(194026, 'frFR', '', 'Suivre la voie. Comme si nous allions… Venez, nous avons à faire.', 38134),
(194025, 'frFR', '', 'Nous avons beaucoup à vous apprendre, mais n\’ayez nulle inquiétude ! Ce périple est le vôtre et vous l\’accomplirez à votre propre rythme.', 38134),
(194024, 'frFR', '', 'Nous vous débarrasserons de vos défauts de mortels pour vous sublimer et vous permettre d\’accomplir notre devoir le plus ardu et le plus sacré.', 38134),
(194023, 'frFR', '', 'Vous allez apprendre parmi nous les vertus de la pureté, du courage, de l\’humilité, de la sagesse et de la loyauté.', 38134),
(194022, 'frFR', 'Vol\’jin… des Sombrelances… Tu es une âme féroce. Tu aspires… à la justice.', '', 38134),
(194019, 'frFR', 'Non ! Rezan, on va te sortir de là. Essaye de reprendre des forces.', '', 38134),
(194018, 'frFR', 'Vous arrivez… trop tard…', '', 38134),
(194017, 'frFR', 'Pas… encore…', '', 38134),
(194016, 'frFR', 'Cette âme est perdue ! Il n\’y a plus rien à sauver !', '', 38134),
(194015, 'frFR', 'Fuyez…', '', 38134),
(194014, 'frFR', 'Vous allez partager les tourments de ce pitoyable Loa !', '', 38134),
(194011, 'frFR', 'Je compte sur vous… Prenez mon anima pour sauver Rezan !', '', 38134),
(194010, 'frFR', 'Mais j\’ai consacré ma vie à servir la Lumière. N\’est-ce pas ce qui vous donne votre place au Bastion ?', '', 38134),
(194007, 'frFR', '', 'Porte-Cendres… Le jugement de l\’Arbitre est infaillible. Elle a dû voir en vous quelque chose qui vous destinait à Maldraxxus.', 38134),
(194006, 'frFR', 'Il faut qu\’on sauve Rezan des griffes de ce monstre !', '', 38134),
(194005, 'frFR', 'Il est possible que trouver de la gelée méphitique et du limon miscible ne soit pas une tâche aisée.', '', 38134),
(194003, 'frFR', 'Revenez plus tard, nous préparerons une autre potion.', '', 38134),
(193997, 'frFR', 'Ces masques… mes amis… c\’est comme s\’ils n\’étaient plus là.', '', 38134),
(193996, 'frFR', 'C\’est terrible, ce qui est arrivé à notre village… Nous devons veiller à ce que cela ne se reproduise pas !', '', 38134),
(193995, 'frFR', 'Je suis reconnaissant aux gardiens de ce bosquet de m\’avoir permis de rester. Je jure de faire honneur à leur hospitalité.', '', 38134),
(193994, 'frFR', 'Vous avez réussi ! Le réseau est à nouveau opérationnel.', '', 38134),
(193993, 'frFR', 'Quelqu\’un ou quelque chose a détraqué notre réseau de miroirs. Trouvez quoi, qu\’il puisse recommencer à fonctionner.', '', 38134),
(193991, 'frFR', 'Les purotins jettent tout et n\’importe quoi dans la tourbière. C\’est une étape essentielle du processus.\n\nMalheureusement, en cas de déséquilibre, ce sont des créatures infectes en sortent. Une fois, une immense amalgamation s\’est formée… elle a semé une pagaille sans nom.', '', 38134),
(193990, 'frFR', 'Excellent ! Revenez vite prendre le thé, sans hésiter !', '', 38134),
(193982, 'frFR', 'Oh, mais joignez-vous à nous ! Vous êtes juste à l\’heure pour le thé.', '', 38134),
(193981, 'frFR', '', 'Tenez-le bien. Il ne faut pas perdre la moindre goutte d\’anima !', 38134),
(193980, 'frFR', '', 'Sika, est-ce que nous avons un peu d\’anima pour l\’hôtesse ?', 38134),
(193978, 'frFR', 'Criez, frappez, mordez, utilisez vos armes… Tous les coups sont permis !', '', 38134),
(193970, 'frFR', 'Vous avez fait tous les bons choix, Sans-Entraves.', '', 38134),
(193960, 'frFR', 'Vous avez du potentiel, Sans-Entraves. Voyons comment vous vous en sortez dans l\’arène.', '', 38134),
(193940, 'frFR', 'J\’ai hâte d\’analyser ce que vous me rapportez ! Beau travail, $ucher apprenti:chère apprentie; !', '', 38134),
(193938, 'frFR', 'Les alentours de notre nécropole ont manifestement été affectés par des substances caustiques. Il est temps de récupérer quelques échantillons !', '', 38134),
(193933, 'frFR', 'Ne m\’attendez pas. J\’ai besoin de… d\’être seul quelques instants.', '', 38134),
(193930, 'frFR', 'Me mettre à genoux ? Vous espérez me voir m\’humilier devant vous ? Vous divaguez, Nadjia, comme toujours. Notre prochaine rencontre scellera votre véritable mort.', '', 38134),
(193929, 'frFR', '', 'Matyas. Je demande réparation pour vos affronts. Mettez-vous à genoux et implorez mon pardon ou nous croiserons le fer. Sans-Entraves, vous m\’assisterez.', 38134),
(193928, 'frFR', 'Argh… Quelle est cette traîtrise ?', '', 38134),
(193919, 'frFR', 'Elle est au courant ?', 'Elle est au courant ?', 38134),
(193899, 'frFR', 'Je ferai de mon mieux. Pour le bien du cartel, évidemment.', 'Je ferai de mon mieux. Pour le bien du cartel, évidemment.', 38134),
(193894, 'frFR', 'Tout à fait. Les conseilleurs sont tout aussi avisés, mais j\’ai confiance en vous, Ta\’mari. Veillez à ce que vos partenaires ne fassent pas n\’importe quoi.', 'Tout à fait. Les conseilleurs sont tout aussi avisés, mais j\’ai confiance en vous, Ta\’mari. Veillez à ce que vos partenaires ne fassent pas n\’importe quoi.', 38134),
(193893, 'frFR', 'Ne les sous-estimez pas. Votre habileté ne vous abstient pas de rester sur vos gardes.', 'Ne les sous-estimez pas. Votre habileté ne vous abstient pas de rester sur vos gardes.', 38134),
(193892, 'frFR', 'Je viens au rapport, Opulence. Les auxiliaires s\’interrogent quant au réseau de portails, mais je peux régler cela sans problème.', 'Je viens au rapport, Opulence. Les auxiliaires s\’interrogent quant au réseau de portails, mais je peux régler cela sans problème.', 38134),
(193891, 'frFR', 'Excellent. Continuez comme ça.', 'Excellent. Continuez comme ça.', 38134),
(193890, 'frFR', 'Mon implication fut tout à fait mineure, mais ce fut un honneur.', 'Mon implication fut tout à fait mineure, mais ce fut un honneur.', 38134),
(193889, 'frFR', 'Même si j\’imagine que vous n\’êtes pas pour rien dans cette amélioration, Ta\’mari ?', 'Même si j\’imagine que vous n\’êtes pas pour rien dans cette amélioration, Ta\’mari ?', 38134),
(193888, 'frFR', 'Fantastique. Transmettez mes salutations à vos partenaires.', 'Fantastique. Transmettez mes salutations à vos partenaires.', 38134),
(193887, 'frFR', 'Je viens au rapport, Opulence. Nous sommes de nouveau parvenus à augmenter la capacité du réseau de portails.', 'Je viens au rapport, Opulence. Nous sommes de nouveau parvenus à augmenter la capacité du réseau de portails.', 38134),
(193886, 'frFR', '', 'Pitoyables vermines ! Vous ne pouvez pas arrêter la maison des Assemblages !', 38134),
(193867, 'frFR', 'Vous ne devriez pas douter de nous.', 'Vous ne devriez pas douter de nous.', 38134),
(193866, 'frFR', 'Assurez-vous qu\’il reste actif. Nous pourrions en avoir besoin sous peu.', 'Assurez-vous qu\’il reste actif. Nous pourrions en avoir besoin sous peu.', 38134),
(193865, 'frFR', 'Bien entendu. Il s\’agit du réseau le plus fiable de tous les cartels. Et du plus discret.', 'Bien entendu. Il s\’agit du réseau le plus fiable de tous les cartels. Et du plus discret.', 38134),
(193864, 'frFR', 'Votre partenaire assure toujours le fonctionnement du portail ?', 'Votre partenaire assure toujours le fonctionnement du portail ?', 38134),
(193863, 'frFR', 'Salutations. J\’espère que tout se passe comme prévu.', 'Salutations. J\’espère que tout se passe comme prévu.', 38134),
(193862, 'frFR', 'Les Loas sont quelque part de l\’autre côté du pont. Il ne faut pas traîner.', '', 38134),
(193860, 'frFR', 'Mais de rien, enfin.', 'Mais de rien, enfin.', 38134),
(193859, 'frFR', 'Ah vraiment ? Eh bien, merci.', 'Ah vraiment ? Eh bien, merci.', 38134),
(193858, 'frFR', 'Oui, c\’est un style en soi. Et je trouve qu\’il vous va bien.', 'Oui, c\’est un style en soi. Et je trouve qu\’il vous va bien.', 38134),
(193857, 'frFR', 'C\’est à vous qu\’elles vont le mieux. Je préfère la simplicité.', 'C\’est à vous qu\’elles vont le mieux. Je préfère la simplicité.', 38134),
(193856, 'frFR', 'Bien le bonjour ! Avez-vous vu les nouvelles transformations disponibles ?', 'Bien le bonjour ! Avez-vous vu les nouvelles transformations disponibles ?', 38134),
(193855, 'frFR', 'Tout à fait ! C\’est plus exotique !', 'Tout à fait ! C\’est plus exotique !', 38134),
(193854, 'frFR', 'Salutations, Visage. Votre masque brillerait-il d\’un nouvel éclat ?', 'Salutations, Visage. Votre masque brillerait-il d\’un nouvel éclat ?', 38134),
(193853, 'frFR', 'Nous nous reverrons.', 'Nous nous reverrons.', 38134),
(193852, 'frFR', 'Merci, merci.', 'Merci, merci.', 38134),
(193851, 'frFR', 'Votre style ne m\’échappe jamais, Visage.', 'Votre style ne m\’échappe jamais, Visage.', 38134),
(193850, 'frFR', 'Vous avez remarqué ? Merci beaucoup. Je craignais que ce ne soit trop subtil.', 'Vous avez remarqué ? Merci beaucoup. Je craignais que ce ne soit trop subtil.', 38134),
(193849, 'frFR', 'Salutations, Visage. J\’adore votre nouveau style !', 'Salutations, Visage. J\’adore votre nouveau style !', 38134),
(193848, 'frFR', 'Je sais.', 'Je sais.', 38134),
(193847, 'frFR', 'Mais de rien, enfin.', 'Mais de rien, enfin.', 38134),
(193846, 'frFR', 'Cela vous va à ravir. Peut-être devrais-je l\’essayer moi-même.', 'Cela vous va à ravir. Peut-être devrais-je l\’essayer moi-même.', 38134),
(193845, 'frFR', 'En effet. Elle vous plaît ?', 'En effet. Elle vous plaît ?', 38134),
(193844, 'frFR', 'Salutations, Ta\’visage. Est-ce là une nouvelle lueur que vous arborez ?', 'Salutations, Ta\’visage. Est-ce là une nouvelle lueur que vous arborez ?', 38134),
(193839, 'frFR', '', 'Je vais éradiquer votre souillure !', 38134),
(193838, 'frFR', '', 'Je vais tous vous briser !', 38134),
(193837, 'frFR', '', 'Ce n\’est ni le lieu ni le moment !', 38134),
(193836, 'frFR', '', 'Vous connaîtrez la colère du courage !', 38134),
(193835, 'frFR', '', 'Subissez ma vengeance !', 38134),
(193834, 'frFR', '', 'Cet endroit sera purifié !', 38134),
(193833, 'frFR', 'Exactement !', 'Exactement !', 38134),
(193832, 'frFR', 'Il suffira d\’accuser les mortels. Après tout, ils ne se rappellent jamais où ils laissent les leurs. Je me trompe ?', 'Il suffira d\’accuser les mortels. Après tout, ils ne se rappellent jamais où ils laissent les leurs. Je me trompe ?', 38134),
(193831, 'frFR', 'Un petit nombre d\’évasions mis à part, nous nous en sortons bien. Ce ne sont pas quelques serpents de plus ou de moins qui alerteront les auxiliaires, si ?', 'Un petit nombre d\’évasions mis à part, nous nous en sortons bien. Ce ne sont pas quelques serpents de plus ou de moins qui alerteront les auxiliaires, si ?', 38134),
(193830, 'frFR', 'Tout a l\’air de bien se passer, non ?', 'Tout a l\’air de bien se passer, non ?', 38134),
(193829, 'frFR', '', 'Ah, Mograine. Quand j\’en aurai fini avec vous, l\’Antre ne sera plus qu\’un doux souvenir.', 38134),
(193828, 'frFR', 'Nous ferons notre possible !', 'Nous ferons notre possible !', 38134),
(193827, 'frFR', 'Les mortels semblent préférer les choses qui leur paraissent « mignonnes ». J\’avoue ne pas bien savoir ce que signifie ce terme, mais cela semble lié à leur passion pour les grands yeux. Plus de créatures aux grands yeux, donc !', 'Les mortels semblent préférer les choses qui leur paraissent « mignonnes ». J\’avoue ne pas bien savoir ce que signifie ce terme, mais cela semble lié à leur passion pour les grands yeux. Plus de créatures aux grands yeux, donc !', 38134),
(193826, 'frFR', 'Entendu. Des préférences ?', 'Entendu. Des préférences ?', 38134),
(193825, 'frFR', 'Bien, mais dites à vos partenaires de continuer à importer des créatures.', 'Bien, mais dites à vos partenaires de continuer à importer des créatures.', 38134),
(193823, 'frFR', 'C\’est notre lot à tous. Cette opération nous rapportera beaucoup, j\’en fais le pari.', 'C\’est notre lot à tous. Cette opération nous rapportera beaucoup, j\’en fais le pari.', 38134),
(193822, 'frFR', 'Plutôt bien. Travailler pour le cartel, ça occupe.', 'Plutôt bien. Travailler pour le cartel, ça occupe.', 38134),
(193821, 'frFR', 'Ce sera fait.', 'Ce sera fait.', 38134),
(193820, 'frFR', 'Quel réseau de portails impressionnant ! Transmettez mes compliments à qui de droit.', 'Quel réseau de portails impressionnant ! Transmettez mes compliments à qui de droit.', 38134),
(193819, 'frFR', 'C\’est incroyable le nombre de créatures différentes que mes partenaires font venir. Un plaisir, vraiment.', 'C\’est incroyable le nombre de créatures différentes que mes partenaires font venir. Un plaisir, vraiment.', 38134),
(193818, 'frFR', 'Les affaires se portent bien. Ces mortels ont des goûts très divers en matière de compagnons.', 'Les affaires se portent bien. Ces mortels ont des goûts très divers en matière de compagnons.', 38134),
(193817, 'frFR', 'Comment allez-vous ?', 'Comment allez-vous ?', 38134),
(193816, 'frFR', 'Vous allez perdre ! J\’en ai la certitude !', '', 38134),
(193815, 'frFR', '', 'La peur et le doute risquent de s\’emparer de Pélagos. Hâtons-nous.', 38134),
(193814, 'frFR', '', 'Quoi ? L\’imbécile… Il n\’y a pas une seconde à perdre !', 38134),
(193813, 'frFR', 'Kléia ! Louée soit l\’Archonte… Pélagos a commencé le rituel sans vous !', '', 38134),
(193812, 'frFR', '', 'Nous y sommes. Pélagos sera heureux de vous rencontrer.', 38134),
(193811, 'frFR', '', 'Nous vivons une tragédie qui dépasse l\’entendement, $n.', 38134),
(193810, 'frFR', '', 'Attendez… Laissez-moi vous montrer.', 38134),
(193809, 'frFR', '', 'Maintenant que les portails sont fermés, nos combattants ont une chance.', 38134),
(193808, 'frFR', '', 'Notre royaume doit vous sembler immaculé. Mais vous n\’imaginez pas tout ce que nous avons perdu.', 38134),
(193807, 'frFR', '', 'Le Vide a ouvert d\’immenses portails pour nous envoyer ses soldats. Si on ne les avait pas détruits, la défaite serait inévitable.', 38134),
(193805, 'frFR', '', 'J\’allais passer ma dernière épreuve quand la pénurie s\’est abattue, barrant la voie vers l\’ascension.', 38134),
(193802, 'frFR', '', 'Vous connaissez la voie mieux que quiconque, aspirante. Les yeux de l\’Archonte soient sur vous.', 38134),
(193801, 'frFR', 'Ho, $p… Je sens des Loas dans les parages !', '', 38134),
(193800, 'frFR', '', 'Beau combat… Suivez-moi. La vengeance… peut attendre.', 38134),
(193799, 'frFR', 'Bien que nous entretenions Oribos, les voies surnaturelles du Dessein nous sont impénétrables.\n\nLe Dessein est très ancien. Peut-être même au-delà de votre entendement. C\’est une technologie qui tient du divin.', '', 38134),
(193797, 'frFR', 'Les voies du Dessein sont parfois impénétrables.\n\nÀ ma connaissance, la nature du don dépend des activités auxquelles vous avez pris part pour contribuer au Dessein. \n\nC\’est ainsi qu\’Oribos vous vient en aide. Vous servez le Dessein, et le Dessein vous sert à sa façon.\n\nLe Dessein est notre voie.', '', 38134),
(193795, 'frFR', 'Salutations, Sans-Entraves.\n\nQuand vous aurez suffisamment gagné en puissance, cette chambre forte vous accordera un don qui, selon Oribos, vous aidera à servir le Dessein. Ceci se produit à intervalles réguliers, du moment que vous nous aidez de façon active.\n\nJe crois que votre peuple définit cet intervalle comme « hebdomadaire ».', '', 38134),
(193789, 'frFR', '', 'Ils me prennent… pour une proie ? Je vais leur montrer qui est le prédateur, ici !', 38134),
(193787, 'frFR', '', 'Vous… ne faites pas partie de mes tortionnaires.\n\nJe me trouvais dans… une forêt luxuriante… quand une force m\’a soudain happée…\n\nMes ravisseurs reviennent… Protégez-moi… le temps que je recouvre mes forces… !', 38134),
(193782, 'frFR', 'Les Terriers auront votre âme !', 'Les Terriers auront votre âme !', 38134),
(193780, 'frFR', 'Les cloches du Bastion apaiseront votre âme. Écoutez-les et soyez en paix.', 'Les cloches du Bastion apaiseront votre âme. Écoutez-les et soyez en paix.', 38134),
(193779, 'frFR', '', 'Plongez-vous dans le bassin, $n.', 38134),
(193778, 'frFR', 'Veuillez vous asseoir. Une présentation est sur le point de commencer.', 'Veuillez vous asseoir. Une présentation est sur le point de commencer.', 38134),
(193777, 'frFR', 'Bienvenue. Veuillez ajouter votre nom à la liste des nobles âmes résidant désormais au Bastion.', 'Bienvenue. Veuillez ajouter votre nom à la liste des nobles âmes résidant désormais au Bastion.', 38134),
(193772, 'frFR', 'Alors, quoi ? Qu\’est-ce que vous lui voulez, à Bwonsamdi ?', '', 38134),
(193770, 'frFR', 'Ravi de vous revoir ! On fait une bonne équipe, vous et moi, pas vrai ?', '', 38134),
(193769, 'frFR', 'On vous a pas dit que personne échappe au vieux Bwonsamdi ?', '', 38134),
(193768, 'frFR', 'Même pour un chasseur des ombres, cette piste-là n\’est pas facile à remonter. Allez.', '', 38134),
(193767, 'frFR', 'Regardez qui voilà. Il faut qu\’on parle, vous et moi.', '', 38134),
(193765, 'frFR', 'Hum… Ils ont pris l\’esprit, ils l\’ont torturé… puis ils l\’ont emmené ailleurs. Ça y est, j\’ai une piste.', '', 38134),
(193764, 'frFR', 'On s\’est fait repérer !', '', 38134),
(193763, 'frFR', 'Rejoignez-moi dans mon tourment éternel !', 'Rejoignez-moi dans mon tourment éternel !', 38134),
(193762, 'frFR', 'Je peux suivre la piste, mais rien de plus. Sinon, je risque de me retrouver coincé ici pour l\’éternité.', '', 38134),
(193760, 'frFR', 'L\’Antre nous attend, $p. On a un esprit à secourir.', '', 38134),
(193757, 'frFR', 'Cet être qui rayonnait de Lumière sacrée est devenu une créature du Vide à l\’ombre dévorante !', '', 38134),
(193756, 'frFR', 'L\’Antre. C\’est pire que tout ce que j\’imaginais. Je sens la trace de l\’esprit pas loin d\’ici…', '', 38134),
(193747, 'frFR', 'Quoi qu\’on ait sorti de là, je suis sûr que ça aidera notre cause.', '', 38134),
(193746, 'frFR', 'Les tourbières sont l\’endroit parfait où pêcher de nouveaux purotins… entre autres choses.', '', 38134),
(193741, 'frFR', '', 'Salutations, $c ! Je vous emmène ?', 38134),
(193738, 'frFR', '', 'Bienvenue parmi nous ! Nous vous avons préparé quelques menues tâches pour vous acclimater à votre nouvelle existence.', 38134),
(193737, 'frFR', '', 'Pilier fondateur des Éternels, l\’Archonte administre notre royaume et se dévoue corps et âme à son auguste tâche.\n\nC\’est par sa volonté que le Bastion remplit ses fonctions au sein de l\’Ombreterre. Il y aurait beaucoup à dire à propos de cette vénérable entité, mais pareille gloire n\’est pas destinée aux oreilles des simples aspirants.\n\nQuand vous aurez obtenu vos ailes, vous aurez peut-être l\’honneur de la rencontrer.', 38134),
(193736, 'frFR', '', 'Le rôle que m\’a confié l\’Archonte consiste à m\’assurer que les nouveaux venus s\’acclimatent à leur nouvel environnement. Je suis là pour leur présenter la voie des Kyrians.\n\nC\’est un honneur et un privilège de faire votre connaissance.', 38134),
(193730, 'frFR', '', 'Faites attention à vous, et n\’oubliez pas votre entraînement.', 38134),
(193729, 'frFR', '', 'C\’est un avenir radieux qui vous attend, $n.', 38134),
(193727, 'frFR', 'D\’accord. Je vais pouvoir me racheter, pas question de laisser Mueh\’zala s\’en tirer comme ça.', '', 38134),
(193726, 'frFR', 'Comme l\’a dit Mueh\’zala, tu as encore un lien très fort avec Azeroth. Exactement comme notre $uami:amie;, en fait.', '', 38134),
(193725, 'frFR', 'Ho, Bwonsamdi ! Personne ne peut sortir de l\’Antre. Si je l\’accompagne là-dedans…', '', 38134),
(193724, 'frFR', 'Une seconde, $n. Il faut qu\’on parle.', '', 38134),
(193723, 'frFR', 'Mrrrriaaz !', '', 38134),
(193721, 'frFR', 'Je vais vous accompagner dans l\’Antre, $n.\n\nMueh\’zala, il s\’est bien payé ma tête et je compte pas rester les bras croisés pendant que quelqu\’un d\’autre recolle les pots cassés.\n\nOn va la retrouver ensemble, cette déesse sauvage.', '', 38134),
(193720, 'frFR', 'Il me plaît pas du tout, ce Mueh\’zala de malheur.', '', 38134),
(193719, 'frFR', 'Lescié ! Il était temps !', '', 38134),
(193718, 'frFR', '', 'Indigo ! Le ciel soit loué, j\’avais perdu espoir.', 38134),
(193714, 'frFR', 'Partir à l\’aventure avec vous était un honneur, $n.', '', 38134),
(193681, 'frFR', '', 'Calix ! L\’Archonte soit louée !', 38134),
(193676, 'frFR', 'Quelque chose me dit que ce que raconte Mueh\’zala l\’intéressera. C\’est qu\’ils ont... un lien spécial.', '', 38134),
(193675, 'frFR', 'Bwonsamdi ! Tu es trop important pour t\’occuper de m\’invoquer toi-même, c\’est ça ?', '', 38134),
(193670, 'frFR', 'Tes fidèles, ils ne valent rien, Mueh\’zala ! Un tas d\’os décrépits et de petits esprits farceurs.', '', 38134),
(193660, 'frFR', 'Ma nécropole est par là. Bon, il se pourrait qu\’il y ait un peu de… désordre. Mais ce n\’est pas à vous que ça va faire peur, hein ?', '', 38134),
(193655, 'frFR', '', 'Voilà, assez ! On peut ranimer l\’hôtesse.', 38134),
(193654, 'frFR', 'Les plus grands ont tout plein d\’anima ! Mais celui-ci, non… presque vide.', 'Les plus grands ont tout plein d\’anima ! Mais celui-ci, non… presque vide.', 38134),
(193653, 'frFR', 'Mon anima… pour le Bastion…', '', 38134),
(193652, 'frFR', 'Merci.', 'Merci.', 38134),
(193651, 'frFR', 'Pas de panique. Quand l\’anima revient, il revient aussi.', 'Pas de panique. Quand l\’anima revient, il revient aussi.', 38134),
(193650, 'frFR', '', 'Oui ! Prenez mon tonneau. Les centurions partageront !', 38134),
(193645, 'frFR', '', 'Vives-pierres, le prince déchu et son acolyte sans-Entraves sont ici. Ne les laissez surtout pas atteindre le Maître.', 38134),
(193642, 'frFR', 'En quoi puis-je vous aider ?', 'En quoi puis-je vous aider ?', 38134),
(193641, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les druides.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les druides.', 38134),
(193638, 'frFR', 'Cet endroit est à tomber !', '', 38134),
(193636, 'frFR', 'Louison m\’a prévenu que vous passeriez par ici.', '', 38134),
(193635, 'frFR', 'Vous devez être drôlement coriace pour avoir une telle collection de crânes !', '', 38134),
(193634, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les moines.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les moines.', 38134),
(193633, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les paladins.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les paladins.', 38134),
(193632, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les prêtres.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les prêtres.', 38134),
(193631, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les mages.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les mages.', 38134),
(193630, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les chasseurs.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les chasseurs.', 38134),
(193629, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les voleurs.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les voleurs.', 38134),
(193628, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les chamans.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les chamans.', 38134),
(193627, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les démonistes.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les démonistes.', 38134),
(193626, 'frFR', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les guerriers.', 'Salutations, $c.\n\nSi vous voulez vous former, il faut vous adresser à un maître capable d\’enseigner à $uun:une:c; $c. Moi, je ne forme que les guerriers.', 38134),
(193624, 'frFR', 'Non ! Des… mortels ? Comment c\’est… possible…', '', 38134),
(193623, 'frFR', 'Alors les petits fidèles, ils peuvent pas faire mieux que ça ? C\’est minable.', '', 38134),
(193622, 'frFR', 'Vos âmes, elles sont pour l\’Antre ! Personne peut défier le grand Mueh\’zala !', '', 38134),
(193621, 'frFR', 'Vous avez choisi le mauvais Loa !', '', 38134),
(193620, 'frFR', 'Que le Geôlier vous emporte !', '', 38134),
(193619, 'frFR', 'C\’est la fin de Bwonsamdi, et de vous avec !', '', 38134),
(193617, 'frFR', '', 'Celle-ci va vous casser les noisettes !', 38134),
(193616, 'frFR', '', 'Ça va pulser !', 38134),
(193615, 'frFR', '', 'Il pleut de la volaille !', 38134),
(193614, 'frFR', '', 'Attendez ! Prenez le mojo, il a moins d\’importance que Milhouse !', 38134),
(193613, 'frFR', '', 'Millhouse ! Nettoie-moi tout ça pour que je puisse me remettre au travail !', 38134),
(193609, 'frFR', 'Une puissance qui dépasse votre entendement !', '', 38134),
(193608, 'frFR', 'Allumeeer le… Euh… Noir c\’est noir, y a plus d\’espoir !', '', 38134),
(193607, 'frFR', 'Tremblez face à ma toute-puissance !', '', 38134),
(193605, 'frFR', 'Arrêtez ! Je préfère perdre le mojo plutôt qu\’elle.', '', 38134),
(193604, 'frFR', 'Ha ha ! Ça, c\’est du mojo de Tempête-de-mana !', '', 38134),
(193602, 'frFR', 'Vous allez regretter de vous en prendre à un génie !', '', 38134),
(193600, 'frFR', 'Vous avez toute notre reconnaissance, Sans-Entraves. L\’Archonte vous guide.', '', 38134),
(193599, 'frFR', 'Améthia !', '', 38134),
(193596, 'frFR', 'Merci, Sans-Entraves. Améthia, mon âme liée, est elle aussi retenue prisonnière en ces lieux. De grâce, aidez-moi à la libérer.', '', 38134),
(193593, 'frFR', 'Sans-Entraves, j\’ai besoin de votre aide. Ils ont enlevé Améthia ! Je… Je ne sais pas ce que je ferais sans elle. Je n\’ose même pas imaginer ce que je deviendrais si je devais rester coincé ici tout seul pour l\’éternité.\n\nNous devons la sauver, et vite !', '', 38134),
(193588, 'frFR', '', 'Bonjour ! Humble régisseuse Sika, pour vous servir !', 38134),
(193586, 'frFR', 'Oh, c\’est un autel. Pour le détruire, il faut trouver un peu de poudre.', '', 38134),
(193585, 'frFR', '', 'Dégagez de là.', 38134),
(193584, 'frFR', '', 'Excellent ! Espérons qu\’il n\’ait pas été définitivement endommagé.', 38134),
(193583, 'frFR', 'Voilà un autel. Versez un peu de poudre dessus, on va tenter…', '', 38134),
(193581, 'frFR', 'Vous avez trouvé de la poudre ! Avec ça, je peux faire sauter un autel. Enfin, j\’espère…', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193580, 'frFR', '', 'Merci, Sans-Entraves. Vous n\’avez pas idée du mal que je me suis donné pour assembler Boutons. Sur ce, nous vous laissons.', 38134),
(193575, 'frFR', '', 'Ce ne sont pas de simples gadgets.', 38134),
(193572, 'frFR', '', 'Mes petits bibelots peuvent être destructeurs.', 38134),
(193568, 'frFR', 'La Légion de pierre ne peut pas entrer ici.', 'La Légion de pierre ne peut pas entrer ici.', 38134),
(193566, 'frFR', '', 'BOUTONS !', 38134),
(193565, 'frFR', '', 'Faites bien attention où vous mettez les pieds.', 38134),
(193562, 'frFR', '', 'Là, on devrait rigoler un peu.', 38134),
(193561, 'frFR', '', 'Ah ! Vous êtes $uun:une; Sans-Entraves ! Vous me donnez faim.\n\nMon… compagnon et moi avons été séparés. Libérez-moi et partons à sa recherche, qu\’est-ce que vous en dites ?', 38134),
(193554, 'frFR', '', 'Est-ce que vous rêvez de voler ?', 38134),
(193552, 'frFR', 'Merci pour votre aide. Nous nous efforcerons d\’être plus prudents à l\’avenir.', '', 38134),
(193551, 'frFR', 'Ah, le voilà. Et il a tous ses membres !', '', 38134),
(193550, 'frFR', 'Sans-Entraves ! Devant vous se tient… enfin, flotte… le sous-chef Matyas de la maison Malbruyère.', '', 38134),
(193549, 'frFR', '', 'Tous… mes… plans…', 38134),
(193548, 'frFR', '', 'Vous êtes… durs… en affaires…', 38134),
(193546, 'frFR', 'Merci, Sans-Entraves. Allons trouver Varrik, il ne va probablement pas mieux que moi.', '', 38134),
(193542, 'frFR', '', 'Cette transaction est terminée.', 38134),
(193540, 'frFR', '', 'Un pitoyable spectacle.', 38134),
(193536, 'frFR', 'Ah. Sans-Entraves ! J\’ai beaucoup entendu parler de vous. Mais vous ne me rencontrez pas dans un bon jour.\n\nNous sommes deux à avoir été capturés par les créatures qui rôdent en ces lieux. Cela vous ennuierait-il de nous venir en aide ?', '', 38134),
(193535, 'frFR', '', 'Vous avez présumé de vos forces.', 38134),
(193534, 'frFR', '', 'Vous allez le payer chèrement.', 38134),
(193532, 'frFR', 'Atal\’ai, venez servir Hakkar !', '', 38134),
(193527, 'frFR', 'Je me nourris de votre sang !', '', 38134),
(193526, 'frFR', 'Subissez le courroux de l\’écorcheur d\’âmes !', '', 38134),
(193525, 'frFR', 'Mourez, mortels !', '', 38134),
(193524, 'frFR', 'Cette puissance… elle était à moi…', '', 38134),
(193523, 'frFR', 'L\’écorcheur a eu ses offrandes.', '', 38134),
(193522, 'frFR', 'Je serai le dieu du sang et de la mort.', '', 38134),
(193521, 'frFR', 'Rien de plus qu\’une goutte dans l\’océan.', '', 38134),
(193520, 'frFR', 'Il faut trouver Penthia, ils n\’ont pas pu l\’emmener bien loin. Et puis, merci de m\’avoir libéré !', '', 38134),
(193519, 'frFR', 'Hé, $n, qu\’est-ce qui vous retarde comme ça ?', '', 38134),
(193518, 'frFR', 'Quoi qu\’il en soit, c\’est bien le chemin à suivre.', '', 38134),
(193517, 'frFR', '', 'Je me souviens d\’avoir eu vent d\’un tel plan il y a bien des années, Rénathal. Avant que nous ne découvrions les véritables intentions du Maître…', 38134),
(193516, 'frFR', 'Votre sang est faible et indigne.', '', 38134),
(193515, 'frFR', 'Son pouvoir m\’appartient ! Je vais revivre !', '', 38134),
(193514, 'frFR', '', 'Il m\’en faut plus. Cette illumination m\’ouvre de nouveaux horizons.', 38134),
(193513, 'frFR', '', 'Je me nourris… et je deviens… tellement plus…', 38134),
(193512, 'frFR', '', 'Comment peut-on être aussi prévisible ?', 38134),
(193511, 'frFR', '', 'J\’anticipe le moindre de vos agissements.', 38134),
(193510, 'frFR', '', 'Évitez ça si vous le pouvez !', 38134),
(193509, 'frFR', 'Tant que vous servirez notre cause, cette puissance restera vôtre.', '', 38134),
(193508, 'frFR', '', 'Nul ne peut m\’échapper !', 38134),
(193506, 'frFR', 'Qui êtes-vous ? Comment pouvez-vous déambuler si librement ici ?\n\nJe vous en prie, aidez-nous. Mon ami a été enlevé. Je dois le retrouver.\n\nPourriez-vous m\’y aider ?', '', 38134),
(193505, 'frFR', '', 'Boum ! Boum !', 38134),
(193504, 'frFR', '', 'Souffrance ! Souffrance !', 38134),
(193503, 'frFR', 'On va trouver un meilleur endroit où se cacher. Merci pour votre aide.', '', 38134),
(193502, 'frFR', 'La voilà !', '', 38134),
(193500, 'frFR', '', 'Vous devez vous poser bien des questions. Rassurez-vous, tout vous sera expliqué en temps voulu.\n\nUn nouveau chapitre de votre existence s\’ouvre aujourd\’hui. Vous vous sentez d\’attaque ?', 38134),
(193499, 'frFR', 'C\’est sous cette forme que j\’ai anéanti tant de morts-vivants sur Azeroth… Mais la question se pose, c\’est vrai.', '', 38134),
(193498, 'frFR', '', 'Mais je pourrais poser la même question. Pourquoi prendre la forme d\’un pauvre Humain alors qu\’il en existe tant de plus puissantes ?', 38134),
(193497, 'frFR', '', 'Kaldoreï, je n\’étais que la suivante de ma reine. Appréciée pour ma beauté et rien d\’autre. Mais devenue Naga, j\’ai pu être reconnue pour ma ruse et ma détermination.', 38134),
(193489, 'frFR', 'Il faut nous en emparer et les utiliser pour instituer une nouvelle ère. Que Revendreth retrouve son véritable rôle.', '', 38134),
(193488, 'frFR', 'L\’emprise qu\’il a sur le royaume lui vient de médaillons dont il a doté chacun d\’un fragment de sa puissance. Il les a confiés à des lieutenants qui lui obéissent aveuglément.', '', 38134),
(193485, 'frFR', 'Dites-moi, baronne… Votre enveloppe d\’Elfe a dû vous être rendue dans l\’au-delà. Pourquoi l\’avoir délaissée pour reprendre celle d\’une Naga ?', '', 38134),
(193481, 'frFR', '', 'Les ténèbres abyssales de votre psyché.', 38134),
(193480, 'frFR', '', 'Votre ignorance crève les yeux.', 38134),
(193477, 'frFR', '', 'Ombres !', 38134),
(193476, 'frFR', '', 'Ténèbres !', 38134),
(193472, 'frFR', '', 'Certaines proies sont plus goûteuses que d\’autres.', 38134),
(193470, 'frFR', '', 'Ma progéniture sait quelle sera sa première victime.', 38134),
(193469, 'frFR', '', 'J\’en ai l\’eau à la bouche !', 38134),
(193468, 'frFR', '', 'Ils sentent votre odeur !', 38134),
(193462, 'frFR', '', 'Votre chair nourrira mes enfants.', 38134),
(193461, 'frFR', '', 'Ma progéniture va pouvoir se sustenter.', 38134),
(193460, 'frFR', '', 'Dévorez-les, mes petits !', 38134),
(193459, 'frFR', '', 'Venez, mes enfants. Le repas est servi.', 38134),
(193458, 'frFR', '', 'Nourriture ! Manger !', 38134),
(193457, 'frFR', '', 'Chaque bouchée m\’octroie la connaissance éternelle.', 38134),
(193456, 'frFR', '', 'Venez ! Manger !', 38134),
(193455, 'frFR', '', 'J\’ai faim…', 38134),
(193454, 'frFR', 'Ho, Vol\’jin, ne commence pas à chipoter. J\’ai quelque chose à te montrer.', '', 38134),
(193452, 'frFR', 'Heirmir et moi avons préparé quelques surprises pour nos invités ! Vous voulez bien vous charger de la livraison ?', '', 38134),
(193451, 'frFR', '', 'C\’est maintenant ou jamais ! Vite, en coulisses !', 38134),
(193450, 'frFR', '', 'Parfait ! Allez, petite pause pour tout le monde !', 38134),
(193449, 'frFR', '', 'Non, ça ne va pas. Mettez l\’arbre de l\’autre côté !', 38134),
(193448, 'frFR', '', 'Hé ! Vous, là, avec l\’arbre ! En position !', 38134),
(193447, 'frFR', '', 'Euh… On ferait mieux d\’y aller.', 38134),
(193446, 'frFR', '', 'On arrive à la scène de l\’incendie de Teldrassil. Où est l\’arbre ?', 38134),
(193445, 'frFR', '', 'J\’ai une idée ! Utilisons le reste de poudre magique pour nous déguiser !', 38134),
(193444, 'frFR', 'Sans-Entraves, avec nous !', 'Sans-Entraves, avec nous !', 38134),
(193443, 'frFR', 'Faites pleuvoir la destruction !', 'Faites pleuvoir la destruction !', 38134),
(193441, 'frFR', '', 'J\’ai à faire !', 38134),
(193420, 'frFR', 'Ensemble, nous sommes plus forts !', '', 38134),
(193324, 'frFR', '', 'Hum… Je ne sais plus trop si je l\’avais sur ma liste. Hé, tant pis.', 38134),
(193322, 'frFR', '', 'Vous êtes avec la Reine des surins ? Vous choisissez mal vos amis !', 38134),
(193321, 'frFR', 'On trouve toujours refuge dans l\’ombre.', 'On trouve toujours refuge dans l\’ombre.', 38134),
(193320, 'frFR', '', 'Il ne reste plus qu\’une cible. Dites-moi quand vous serez $uparé:parée;.', 38134),
(193319, 'frFR', 'Moi, c\’est Hanzabu. Je suis l\’un des plus fidèles disciples de Bwonsamdi. Je lui suis resté loyal même quand le reste de Zuldazar s\’est détourné de lui.\n\nOn va arrêter les sbires de Mueh\’zala… quoi qu\’il en coûte.', '', 38134),
(193318, 'frFR', 'Mueh\’zala… a déjà… gagné…', '', 38134),
(193317, 'frFR', 'Je vais libérer Mueh\’zala et il va me récompenser !', '', 38134),
(193313, 'frFR', 'Ce n\’est pas terminé.', 'Ce n\’est pas terminé.', 38134),
(193312, 'frFR', 'Un être vivant ? Revendreth part à vau-l\’eau, ma parole !', '', 38134),
(193311, 'frFR', 'Combien d\’innocents avons-nous malmenés en pure perte ?', 'Combien d\’innocents avons-nous malmenés en pure perte ?', 38134),
(193307, 'frFR', '', 'Maldraxxi, à moi ! Ensemble, nous allons purger le temple.', 38134),
(193304, 'frFR', 'Le frisson de la mort me protège !', '', 38134),
(193303, 'frFR', 'Personne n\’échappe à la tempête.', '', 38134),
(193302, 'frFR', 'J\’en ai bien assez de vous !', '', 38134),
(193301, 'frFR', 'Une victoire bien futile… Il reste le gros de nos troupes…', '', 38134),
(193300, 'frFR', 'Dommage, vous ne serez plus là pour assister à la fin du royaume.', '', 38134),
(193299, 'frFR', 'Quelle bêtise de venir m\’affronter.', '', 38134),
(193298, 'frFR', 'Quelle vaine tentative.', '', 38134),
(193297, 'frFR', 'Le Bastion sera anéanti !', '', 38134),
(193293, 'frFR', 'Les cornes sur la tête, han, j\’adore.', '', 38134),
(193291, 'frFR', 'Sur des épaules comme ça, je vais pouvoir coller pas mal de bras.', '', 38134),
(193290, 'frFR', 'Oh, des organes transformables. Deux en un, la belle affaire !', '', 38134),
(193289, 'frFR', 'Tous ces appendices, j\’ai un concept parfait pour ça.', '', 38134),
(193288, 'frFR', 'Grands yeux et mains fines. Oui, oui ! L\’idéal pour les tâches minutieuses.', '', 38134),
(193287, 'frFR', 'Ah, quelles belles oreilles. On en trouve rarement.', '', 38134),
(193286, 'frFR', 'Une assise solide, parfaite pour l\’infanterie.', '', 38134),
(193285, 'frFR', 'Une facture classique mais pleine de potentiel.', '', 38134),
(193284, 'frFR', 'Partez avant que les crocs vous voient en train de me parler et personne ne vous jettera la pierre.', '', 38134),
(193282, 'frFR', 'Votre chair n\’est pas sans promesses.', '', 38134),
(193281, 'frFR', 'Mes créatures pourront se charger de vous.', '', 38134),
(193280, 'frFR', 'Imbécile ! On ne frappe pas son créateur !', '', 38134),
(193279, 'frFR', 'Celle-là, j\’en suis particulièrement fier !', '', 38134),
(193278, 'frFR', 'Je suis là pour en découdre !', '', 38134),
(193276, 'frFR', 'Je dois préserver vos membres pour plus tard !', '', 38134),
(193275, 'frFR', 'N\’avez-vous aucun respect pour le travail des artisans ?!', '', 38134),
(193274, 'frFR', 'Ah non, c\’était mon préféré !', '', 38134),
(193272, 'frFR', 'J\’aurais pu… assembler… mon chef-d\’œuvre…', '', 38134),
(193271, 'frFR', 'C\’est ma plus belle récolte depuis un bon moment !', '', 38134),
(193270, 'frFR', 'Avec ça, j\’aurai de quoi faire !', '', 38134),
(193269, 'frFR', 'Je sais déjà ce que je vais faire des restes.', '', 38134),
(193267, 'frFR', 'Non ! Il va falloir que je les ré-assemble de zéro ! Bon, il va falloir que je m\’occupe de vous.', '', 38134),
(193266, 'frFR', 'Hein ? Vous êtes encore en vie ? Vous avez déjà une stèle du vice ou vous travaillez encore dessus ?', '', 38134),
(193265, 'frFR', 'Les vivants continuent d\’arriver par cette porte ? Les crocs sont toujours ravis d\’accueillir des âmes, mais ils les préfèrent mortes.', '', 38134),
(193264, 'frFR', 'Si on me surprend à causer, j\’suis bon pour les crocs ou le bourbier. Alors, venez pas me causer des ennuis.', '', 38134),
(193263, 'frFR', 'Évitez de contrarier les crocs. Ils sont à cran depuis qu\’ils ont le grand patron sur le dos.', '', 38134),
(193262, 'frFR', 'J\’aime bien ce boulot. N\’allez pas tout gâcher.', '', 38134),
(193261, 'frFR', 'Mieux vaut pas que les chefs nous voient en train d\’parler. J\’veux pas finir au bourbier.', '', 38134),
(193260, 'frFR', 'À mesure qu\’elles se rapprochent de leur jugement dernier, ces âmes engendrent sans cesse des manifestations de plus en plus dangereuses.\n\nNous avons besoin d\’âmes, d\’âmes fraîches, sans quoi nous ne parviendrons pas à remplir les quotas que le Maître et le haut percepteur nous ont imposés.', 'À mesure qu\’elles se rapprochent de leur jugement dernier, ces âmes engendrent sans cesse des manifestations de plus en plus dangereuses.\n\nNous avons besoin d\’âmes, d\’âmes fraîches, sans quoi nous ne parviendrons pas à remplir les quotas que le Maître et le haut percepteur nous ont imposés.', 38134),
(193258, 'frFR', 'Selon les ordres du haut percepteur, tous les Venthyrs de cette province doivent fournir un quota d\’anima, sans quoi ils encourront des sanctions pouvant aller jusqu\’à l\’exil dans la province des Braises.\n\nMerci de ne pas perturber le processus d\’extraction.', '', 38134),
(193256, 'frFR', '', 'Tremble donc, je vais arracher ta carcasse à mon temple et la précipiter dans le néant !', 38134),
(193255, 'frFR', 'Ho, mes assistants ! Venez vous charger de ces matières premières !', '', 38134),
(193254, 'frFR', '', 'J\’en ai assez ! Venez donc vous battre pour que je puisse vous écraser une bonne fois pour toutes !', 38134),
(193253, 'frFR', '', 'Votre arrivée nous redonne espoir.\n\nNous n\’avons pas grand-chose, mais ce qui est à nous est à vous. Nous réhabiliterons Sylvarden.', 38134),
(193252, 'frFR', '', 'Du vent, les sous-fifres ! Où sont mes vraies cibles ?', 38134),
(193251, 'frFR', '', 'Et le massacre continue !', 38134),
(193250, 'frFR', '', 'Si vous me défiez, vous mourrez !', 38134),
(193249, 'frFR', '', 'Ah, dommage. Je l\’aimais bien, en fait.', 38134),
(193248, 'frFR', '', 'Et un nom de moins sur la liste !', 38134),
(193246, 'frFR', '', 'Ma liste raccourcit à vue d\’œil.', 38134),
(193245, 'frFR', '', 'Venez me voir quand vous serez prêts à traverser. Ne me faites pas perdre mon temps.', 38134),
(193243, 'frFR', 'Le prince Rénathal a organisé la réception de la cour des Braises afin d\’impressionner les personnalités les plus influentes de l\’Ombreterre.\n\nSi nos invités repartent mécontents, nous aurons manqué notre objectif… ce qui n\’est pas envisageable pour tout héraut des festivités qui se respecte.\n\nDe plus, chaque invité paie un tribut en quittant la cour.\n\nPlus un invité est satisfait, plus son tribut est élevé !', '', 38134),
(193240, 'frFR', 'Chaque invité à la réception a ses préférences. Certains aiment les évènements luxueux et protocolaires, tandis que d\’autres se sentent plus à leur aise dans un cadre détendu, voire dangereux.\n\nEn tant qu\’organisateurs de la réception de la cour des Braises, notre tâche consiste à créer une ambiance que nos invités apprécieront, en choisissant des services à leur goût. Nous pouvons ainsi leur proposer une dégustation de thé de luxe ou un divertissement musical, et organiser différentes activités au cours de la réception.\n\nEssayez de faire des choix pendant l\’évènement et voyez ce qui se produit. Si vous ignorez ce que préfère l\’un de vos hôtes, consultez la liste des invités !', '', 38134),
(193239, 'frFR', 'Les Venthyrs ont toujours tenu des cours extravagantes et donné des réceptions décadentes afin d\’impressionner leurs alliés potentiels et d\’accroître leur influence en Revendreth.\n\nLe prince Rénathal donne ses réceptions dans la province des Braises, au sommet de Tombe-Vice, dans un but bien précis : étendre l\’influence de notre congrégation.\n\nS\’il parvient à séduire le beau monde de Revendreth et de toute l\’Ombreterre, nous deviendrons la première puissance venthyr.\n\nCela étant, il ne sera pas facile de satisfaire toutes les personnalités qui assisteront à notre réception de la cour des Braises.', '', 38134),
(193233, 'frFR', 'On est en pleine répétition !', 'On est en pleine répétition !', 38134),
(193220, 'frFR', 'À l\’intérieur, c\’est bien plus supportable.', '', 38134),
(193216, 'frFR', '', 'Les flèches obéissent à mes ordres !', 38134),
(193215, 'frFR', '', 'Sombrez dans les nuages !', 38134),
(193214, 'frFR', '', 'Les vents rugissent de colère !', 38134),
(193213, 'frFR', '', 'Hors d\’ici !', 38134),
(193212, 'frFR', '', 'Non ! La voie… est… sans issue…', 38134),
(193210, 'frFR', '', 'Ainsi prend fin le règne inique de l\’Archonte.', 38134),
(193209, 'frFR', '', 'Mort à ceux qui servent les Kyrians !', 38134),
(193208, 'frFR', '', 'Vous avez signé votre arrêt de mort.', 38134),
(193207, 'frFR', '', 'L\’heure des désavoués est imminente !', 38134),
(193202, 'frFR', 'Tsss, mon pauvre Loa… Bwonsamdi sait comment se débarrasser de tes petits serviteurs.', '', 38134),
(193201, 'frFR', 'Regardez qui est de retour dans sa jolie nécropole. Tu as peur que mes fidèles détruisent tout ?', '', 38134),
(193178, 'frFR', '', 'Notre margrave a des ennuis !', 38134),
(193164, 'frFR', '', 'Éméni est de retour ! Je n\’en crois pas mes yeux !', 38134),
(193154, 'frFR', '', 'Bon débarras. Ne faites pas confiance à ce triste personnage.', 38134),
(193151, 'frFR', 'Ah, mais ce n\’est pas elle que je veux voir…', '', 38134),
(193150, 'frFR', '', 'Tes petits arrangements n\’intéressent pas la reine, Bwonsamdi.', 38134),
(193147, 'frFR', 'Les Assemblages sont obstinés, c\’est un fait. Mais je jure sur la Lumière que nous allons les chasser du Bastion.', '', 38134),
(193146, 'frFR', '', 'Enfin, les ténèbres quittent mon temple… Néanmoins, j\’ai peur qu\’elles ne reviennent si nous ne faisons rien.', 38134),
(193144, 'frFR', 'Toute cette violence est-elle vraiment nécessaire ?', 'Toute cette violence est-elle vraiment nécessaire ?', 38134),
(193143, 'frFR', '', 'Le ciel est dégagé ! Retrouvez-moi à l\’aire côté ouest, Maldraxxi. Un châtiment est de rigueur.', 38134),
(193140, 'frFR', 'Les Kyrians sont condamnés !', '', 38134),
(193138, 'frFR', '', 'L\’air se gorge de notre poison… comme vos poumons vont se gorger de votre sang !', 38134),
(193136, 'frFR', '', 'Nous allons vicier les cieux !', 38134),
(193131, 'frFR', 'Croc-d\’Os va vous réduire en poussière !', '', 38134),
(193128, 'frFR', 'Je crois qu\’ils fabriquent un assemblage.', 'Je crois qu\’ils fabriquent un assemblage.', 38134),
(193126, 'frFR', 'Obéissez une dernière fois !', '', 38134),
(193125, 'frFR', 'Les morts reviennent sous mon joug !', '', 38134),
(193121, 'frFR', 'Il va tous… nous… recycler…', '', 38134),
(193120, 'frFR', 'Voilà une nouvelle fournée de corps. Nalthor sera satisfait.', '', 38134),
(193119, 'frFR', 'Oui, vous ferez bien l\’affaire.', '', 38134),
(193115, 'frFR', 'Un soldat de plus pour mon armée.', '', 38134),
(193111, 'frFR', 'Ah, peu importe ! J\’ai une infinité de serviteurs !', '', 38134),
(193110, 'frFR', 'Ça… arrive…', '', 38134),
(193109, 'frFR', 'Chancros… échoue…', '', 38134),
(193102, 'frFR', '', 'Nettoyez les cieux, Maldraxxi. Retrouvez-moi à l\’aire côté ouest quand vous aurez terminé.', 38134),
(193101, 'frFR', 'Chancros rend maître fier.', '', 38134),
(193100, 'frFR', 'Faible petite créature !', '', 38134),
(193099, 'frFR', 'Cassés ! Battus !', '', 38134),
(193098, 'frFR', 'Chancros vous démolit !', '', 38134),
(193087, 'frFR', '', 'Ils lui construisent un nouveau corps !', 38134),
(193086, 'frFR', 'Ma puissance est bien trop grande pour vous !', '', 38134),
(193081, 'frFR', '', 'Les monstres dans mon temple sont trop puissants pour les affronter $useul:seule;. Nettoyez les cieux pour moi… Et laissez-moi me charger de leur destin.', 38134),
(193080, 'frFR', 'J\’ai de l\’anima en réserve !', '', 38134),
(193079, 'frFR', 'Très bien, parangon.', '', 38134),
(193078, 'frFR', 'Je déborde d\’anima !', '', 38134),
(193077, 'frFR', '', 'La congrégation ne tarit pas d\’éloges à votre égard. Ne me décevez pas.', 38134),
(193075, 'frFR', '', 'Je suis à bout de patience. Ne perdons pas une seconde de plus.', 38134),
(193074, 'frFR', '', 'Vos infâmes nécromanciens ont plongé le sanctum dans un nuage toxique, je ne peux pas aller plus loin que l\’entrée.', 38134),
(193073, 'frFR', 'Sentez tout le poids de vos fautes.', '', 38134),
(193072, 'frFR', 'Vous ne méritez pas un tel orgueil.', '', 38134),
(193071, 'frFR', 'Votre orgueil vous condamne.', '', 38134),
(193069, 'frFR', 'Contemplez la puissance des Venthyrs !', '', 38134),
(193068, 'frFR', 'Ridicule vermine !', '', 38134),
(193064, 'frFR', 'Je commence à perdre patience !', '', 38134),
(193063, 'frFR', 'Je vous méprise au plus haut point !', '', 38134),
(193062, 'frFR', 'Ne restez pas là.', '', 38134),
(193061, 'frFR', 'Serviteurs… incapables…', '', 38134),
(193060, 'frFR', 'Quelqu\’un de mon rang ne devrait pas avoir à se salir les mains pour ces vétilles.', '', 38134),
(193056, 'frFR', 'Qu\’est-ce que le Maître a bien pu voir en vous ?', '', 38134),
(193055, 'frFR', 'Je gaspille mon génie, ici.', '', 38134),
(193054, 'frFR', 'Vous êtes loin d\’avoir mon niveau.', '', 38134),
(193053, 'frFR', 'Disparaissez, vermine !', '', 38134),
(193052, 'frFR', 'Je suis las de votre ennuyeuse présence. Il semblerait que je doive me charger de vous moi-même.', '', 38134),
(193051, 'frFR', '', 'Bouclier divin vous protégera de la magie noire de l\’autel.', 38134),
(193048, 'frFR', 'Suivez-moi à la nécropole. On va rapporter un petit quelque chose pour la reine de l\’hiver.', '', 38134),
(193021, 'frFR', 'Ils auront leur vengeance. Mais prendre la maison des Assemblages n\’est pas un mince exploit. Il nous faut du temps pour préparer l\’assaut.', '', 38134),
(193020, 'frFR', '', 'Débarrassé des Maldraxxi, le Courage pourra retrouver de sa superbe. Mes semblables auront soif de vengeance contre les assemblages.', 38134),
(193018, 'frFR', 'Je deviendrai… plus fort encore…', '', 38134),
(193017, 'frFR', 'J\’ajouterai votre peau à ma collection !', '', 38134),
(193016, 'frFR', 'Fut un temps, j\’organisais de merveilleuses réceptions dans cette tour. On pouvait même y voir le Maître, tout sourire, qui profitait de l\’instant. Il était superbe à l\’époque ; indéniablement le seigneur des Venthyrs.\n\nJe me demande quand ses complots avec le Geôlier ont commencé. Derrière son sourire, cachait-il déjà son mépris pour la réalité tout entière ?', '', 38134),
(193015, 'frFR', '', 'Très bien. Alors je verrai ce que Rénathal a à dire. À défaut d\’autre chose, ça pourrait me permettre de mettre fin à ce conflit stérile qui déchire mes enfants.', 38134),
(193014, 'frFR', 'Mère de pierre, c\’est Revendreth que je sers. À l\’heure actuelle, il n\’y a plus que le sombre prince pour ça. Avec l\’aide de l\’âme sans-entrave, nous pouvons briser la soumission aveugle du Maître au Geôlier et à l\’Antre.', '', 38134),
(193013, 'frFR', '', 'Ainsi, Draven, tu t\’obstines à servir le prince Rénathal malgré ses nombreux échecs.', 38134),
(193012, 'frFR', '', 'Vous avez su maîtriser votre colère pour traverser le pont malgré ceux qui se tenaient sur votre route. Vous progressez.\n\nC\’est cependant la dernière fois que je vous permettrai d\’entrer dans mon domaine, alors expliquez les raisons de votre intrusion.', 38134),
(193011, 'frFR', 'Avancez avec prudence. L\’épreuve a beau être passée, les vive-pierre n\’attendent qu\’un prétexte pour nous délester de nos têtes.', '', 38134),
(193005, 'frFR', 'Je n\’ai jamais voulu te tuer, sœur de pierre. Ne m\’y oblige pas, je t\’en prie.', '', 38134),
(193004, 'frFR', '', 'Sale traître ! Kaal aurait dû me laisser t\’abattre il y a longtemps !', 38134),
(192996, 'frFR', 'S\’il vous faut un moyen de transport particulier pour rentrer, nous avons tout ce qu\’il vous faut !', '', 38134),
(192984, 'frFR', 'Il me semble que pour tous ses efforts, notre Sans-Entraves mérite bien le tribut de cette réception inaugurale.', '', 38134),
(192983, 'frFR', '', 'Vous devez recevoir votre juste châtiment.', 38134),
(192982, 'frFR', '', 'Leur souffrance vous apprendra beaucoup.', 38134),
(192980, 'frFR', 'Le temps aidant, cette cour nous aidera à gagner les cœurs et les esprits de toute l\’Ombreterre !', '', 38134),
(192979, 'frFR', '', 'Mes ouailles ont besoin de moi…', 38134),
(192978, 'frFR', 'Merci à tous pour vos merveilleuses contributions visant à faire de cette répétition un succès !', '', 38134),
(192975, 'frFR', 'Ensemble, nous guiderons Revendreth et toute l\’Ombreterre vers un futur meilleur, un avenir sous le sceau de la justice, de la stabilité et de la prospérité.', '', 38134),
(192974, 'frFR', 'Alors que vous quittez ces lieux, sachez que vous et les vôtres serez toujours accueillis à bras ouverts à Tombe-Vice.', '', 38134),
(192972, 'frFR', 'Nous voilà tous réunis dans ce décor délicieusement avant-gardiste imaginé par notre maître des miroirs, Laurent !', '', 38134),
(192971, 'frFR', 'Nous voilà tous réunis dans ce décor délicieusement nostalgique. Remercions chaleureusement le gardien Ta\’saran et le cartel Ta !', '', 38134),
(192970, 'frFR', 'Le tout dans ce décor résolument et merveilleusement traditionnel. Un grand merci à Témel, à Tatane et au reste du personnel !', '', 38134),
(192969, 'frFR', 'Et la vocation première de Revendreth nous a été rappelée quand nous avons affronté ensemble ces âmes corrompues.', '', 38134),
(192968, 'frFR', 'Pour ma part, j\’ai hâte de changer de tenue, après toute la saleté que cette armée de purotins a soulevée !', '', 38134),
(192967, 'frFR', 'Et qui pourrait l\’oublier, alors que nous avons repoussé ensemble l\’armée vive-pierre à la solde du Maître ?', '', 38134),
(192966, 'frFR', 'Et bien sûr, les troupes du Maître ont appris une bonne leçon aujourd\’hui : Tombe-Vice ne flanchera pas !', '', 38134),
(192964, 'frFR', 'La danse voluptueuse inspirée par la musique du Calice perdu.', '', 38134),
(192963, 'frFR', 'Les bêtes majestueuses et les gormelins dodus et grouillants de Sylvarden.', '', 38134),
(192962, 'frFR', 'Les rituels profondément émouvants menés par notre fidèle championne de la justice, l\’Accusatrice !', '', 38134),
(192961, 'frFR', 'TBD-REfreshment4', '', 38134),
(192960, 'frFR', 'Les champignons épicés et pleins d\’allant venus de Maldraxxus !', '', 38134),
(192959, 'frFR', 'Les batailles de nourriture sauvages et spontanées pendant le service des rafraîchissements.', '', 38134),
(192958, 'frFR', 'Le thé si apaisant proposé par nos chers amis, Théotar et Tubbins !', '', 38134),
(192957, 'frFR', '', 'Qu\’une pluie de justice vienne tout purifier !', 38134),
(192956, 'frFR', 'J\’ai adoré chaque instant partagé avec vous aujourd\’hui…', '', 38134),
(192955, 'frFR', 'Mes chers amis, c\’est avec regret que je vais bientôt devoir mettre fin à cette cérémonie.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192953, 'frFR', 'N\’en dites pas trop. Inutile de leur révéler nos marges. Me suis-je bien fait comprendre ?', 'N\’en dites pas trop. Inutile de leur révéler nos marges. Me suis-je bien fait comprendre ?', 38134),
(192951, 'frFR', 'J\’en parlerai à Ta\’saran. Son cartel pourra peut-être nous aider.', 'J\’en parlerai à Ta\’saran. Son cartel pourra peut-être nous aider.', 38134),
(192950, 'frFR', 'Ba\’solo, il faut accroître la portée de nos réseaux.', 'Ba\’solo, il faut accroître la portée de nos réseaux.', 38134),
(192949, 'frFR', 'Je dois vous signaler que les demandes des mortels sont trop importantes pour la capacité de nos portails.', 'Je dois vous signaler que les demandes des mortels sont trop importantes pour la capacité de nos portails.', 38134),
(192944, 'frFR', 'Et que vous avez eu la bonne idée d\’exploiter leurs désirs.', 'Et que vous avez eu la bonne idée d\’exploiter leurs désirs.', 38134),
(192943, 'frFR', 'Heureusement pour nous que leur appétit est sans limites.', 'Heureusement pour nous que leur appétit est sans limites.', 38134),
(192942, 'frFR', 'Tout se passe bien, Opulence. Les mortels consomment sans interruption.', 'Tout se passe bien, Opulence. Les mortels consomment sans interruption.', 38134),
(192941, 'frFR', 'Comment se passent nos opérations au niveau supérieur ?', 'Comment se passent nos opérations au niveau supérieur ?', 38134),
(192940, 'frFR', '', 'Gor\’groth a parlé de pierres… Retrouvez-les, on pourra libérer le capitaine.', 38134),
(192934, 'frFR', 'Nous devons prendre les mesures qui s\’imposent pour les apaiser. Rien ne doit gêner notre opération.', 'Nous devons prendre les mesures qui s\’imposent pour les apaiser. Rien ne doit gêner notre opération.', 38134),
(192931, 'frFR', 'Opulence, je dois vous signaler que nos activités inquiètent certains conseilleurs.', 'Opulence, je dois vous signaler que nos activités inquiètent certains conseilleurs.', 38134),
(192930, 'frFR', '', 'Je vais pouvoir vous accueillir parmi mes fidèles.', 38134),
(192929, 'frFR', 'Bien sûr, Opulence.', 'Bien sûr, Opulence.', 38134),
(192928, 'frFR', 'Quand je fais des bénéfices, nous en faisons tous. Chacun selon son rang.', 'Quand je fais des bénéfices, nous en faisons tous. Chacun selon son rang.', 38134),
(192927, 'frFR', 'Ces opérations peuvent encore nous profiter.', 'Ces opérations peuvent encore nous profiter.', 38134),
(192926, 'frFR', 'Voilà de bonnes nouvelles, même si je n\’en attendais pas moins.', 'Voilà de bonnes nouvelles, même si je n\’en attendais pas moins.', 38134),
(192925, 'frFR', 'Opulence, j\’ai le plaisir de vous annoncer que tout se déroule comme prévu.', 'Opulence, j\’ai le plaisir de vous annoncer que tout se déroule comme prévu.', 38134),
(192924, 'frFR', '', 'Pas de rédemption pour vous.', 38134),
(192923, 'frFR', '', 'Une âme de plus à purifier !', 38134),
(192922, 'frFR', 'Le héraut des festivités a encore frappé !', '', 38134),
(192921, 'frFR', '', 'Votre pénitence est avancée !', 38134),
(192919, 'frFR', 'Nous tenons la zone. Retournons auprès du parangon.', '', 38134),
(192918, 'frFR', 'Vous devez renvoyer votre familier avant de pouvoir en dompter un autre. Vous pourrez le rappeler plus tard.', '', 38134),
(192917, 'frFR', 'Le rempart est juste là… avec le monstre qui le garde. Il serait prudent d\’appeler notre parangon.', '', 38134),
(192916, 'frFR', 'C\’est… impossible…', '', 38134),
(192908, 'frFR', 'Personne ne garde le rempart ? Méfiez-vous…', '', 38134),
(192906, 'frFR', 'Une embuscade ! Abattez-les !', '', 38134),
(192902, 'frFR', '', 'Si vous avez besoin d\’être $usauvé:sauvée;, criez.', 38134),
(192898, 'frFR', 'Le sol saigne !', '', 38134),
(192897, 'frFR', 'Que le sang vous emporte !', '', 38134),
(192896, 'frFR', 'Noyez-vous dans le sang !', '', 38134),
(192895, 'frFR', 'Ne bougez plus !', '', 38134),
(192894, 'frFR', 'La chair est faible !', '', 38134),
(192893, 'frFR', 'Votre destin est gravé dans la pierre !', '', 38134),
(192892, 'frFR', 'Votre maître vous appelle !', '', 38134),
(192889, 'frFR', 'Bonté divine ! Ces spriggans ont le chic pour mettre une de ces pagailles !', 'Bonté divine ! Ces spriggans ont le chic pour mettre une de ces pagailles !', 38134),
(192887, 'frFR', '', 'Votre jalousie est palpable.', 38134),
(192885, 'frFR', '', 'C\’est à une princesse que vous vous adressez !', 38134),
(192884, 'frFR', 'Magnifique ! Quel talent ! Il ne me reste qu\’à ajuster encore un peu la durée…', '', 38134),
(192877, 'frFR', 'Nous sommes des associés du cartel. Ou comme les mortels aiment à dire… la famille.', 'Nous sommes des associés du cartel. Ou comme les mortels aiment à dire… la famille.', 38134),
(192876, 'frFR', 'Rappelez-moi : pourquoi le couvrons-nous déjà ?', 'Rappelez-moi : pourquoi le couvrons-nous déjà ?', 38134),
(192875, 'frFR', 'Bien sûr, matriarche.', 'Bien sûr, matriarche.', 38134),
(192874, 'frFR', 'Je vous laisse, dans ce cas. Je compte sur vous pour me prévenir s\’il se montre.', 'Je vous laisse, dans ce cas. Je compte sur vous pour me prévenir s\’il se montre.', 38134),
(192873, 'frFR', 'Jamais.', 'Jamais.', 38134),
(192872, 'frFR', 'Nous n\’oserions jamais, matriarche.', 'Nous n\’oserions jamais, matriarche.', 38134),
(192871, 'frFR', 'Vous feriez mieux de ne pas le couvrir !', 'Vous feriez mieux de ne pas le couvrir !', 38134),
(192870, 'frFR', 'Ni moi, matriarche.', 'Ni moi, matriarche.', 38134),
(192869, 'frFR', 'Non, matriarche. Pas moi.', 'Non, matriarche. Pas moi.', 38134),
(192868, 'frFR', 'Épargnez-moi les mondanités. L\’un d\’entre vous a-t-il vu Ta\’vini ?', 'Épargnez-moi les mondanités. L\’un d\’entre vous a-t-il vu Ta\’vini ?', 38134),
(192867, 'frFR', 'Matriarche Ta\’dolla. Bienvenue !', 'Matriarche Ta\’dolla. Bienvenue !', 38134),
(192854, 'frFR', '', 'Elle aura abusé de ma patience jusqu\’au bout. Et c\’est très bien comme ça.', 38134),
(192853, 'frFR', '', 'Oh, madame, jamais je ne vous jouerai un tel tour !', 38134),
(192851, 'frFR', '', 'Si les Maldraxxi vous opposent trop de résistance, vous pouvez invoquer mon nom. Je les anéantirai moi-même.', 38134),
(192845, 'frFR', 'Victoire pour Tombe-Vice ! Pour l\’Ombreterre !', '', 38134),
(192844, 'frFR', 'L\’ennemi bat en retraite ! Victoire pour Tombe-Vice !', '', 38134),
(192842, 'frFR', 'Admirez cette démonstration en conditions réelles des capacités martiales de notre organisation.', '', 38134),
(192839, 'frFR', 'N\’ayez crainte, chers invités. Nous chasserons très vite ces importuns.', '', 38134),
(192838, 'frFR', 'Vous… vous croyez que je peux lui demander son autographe ? Il pourrait peut-être même m\’apprendre quelques techniques de chasse ?', '', 38134),
(192837, 'frFR', 'C\’est lui ! Le chasseur cornu, là devant vous ! Attention, pas de blague. Rien n\’échappe à ma vigilance ! Pas plus qu\’à la sienne ! Il voit tout, lui aussi…', '', 38134),
(192836, 'frFR', 'Garde la tête froide, Reldorn. Il est juste là. Ne va pas te ridiculiser devant lui…$b$bAu temps pour moi, je ne vous avais pas $uvu:vue; ! Que lui voulez-vous, au chasseur de l\’Hallali ? Je veux dire au cornu… sauvage… Bref, qu\’est-ce qui vous amène ?', '', 38134),
(192835, 'frFR', 'Ennemis en approche ! Protégez les invités coûte que coûte !', '', 38134),
(192833, 'frFR', 'À moi, Sans-Entraves ! Nous repousserons cette menace ensemble !', '', 38134),
(192832, 'frFR', 'Votre cour pathétique s\’inclinera devant le Maître !', '', 38134),
(192829, 'frFR', 'Là, derrière ces broussailles. Il faut nous frayer un passage. Utilisez les graines !', '', 38134),
(192823, 'frFR', 'Et nous non plus.', '', 38134),
(192821, 'frFR', '', 'Nous avions quasiment repris notre temple quand vos semblables sont revenus en force. Je n\’aurai de cesse d\’y mettre un terme.', 38134),
(192819, 'frFR', '', 'Ouah, elles ne font pas semblant ! Il faut en lancer d\’autres !', 38134),
(192818, 'frFR', 'J\’ai apporté quelques, euh… modifications à mon fameux parasol afin qu\’il absorbe l\’anima. Essayez-le donc !', '', 38134),
(192817, 'frFR', 'Dites-moi, $c, il ne vous paraît pas un peu… absent, cet éclaireur ? Il a à peine décroché un mot depuis mon arrivée.', '', 38134),
(192816, 'frFR', 'Ainsi le sombre prince passe à l\’action pour s\’emparer du trône ? Il faudra un personnage avec assez de trempe pour l\’arrêter.', '', 38134),
(192815, 'frFR', 'Qu\’y a-t-il ?', '', 38134),
(192812, 'frFR', 'Jamais. Mais il fait partie du cartel, après tout.', 'Jamais. Mais il fait partie du cartel, après tout.', 38134),
(192811, 'frFR', 'Quand a-t-il jamais fait ce que vous avez demandé ?', 'Quand a-t-il jamais fait ce que vous avez demandé ?', 38134),
(192809, 'frFR', 'Et tenez-vous à l\’écart de mon réseau de portails !', 'Et tenez-vous à l\’écart de mon réseau de portails !', 38134),
(192808, 'frFR', 'Mauvais portail ! Renvoyez-moi. Si la matriarche Ta\’dolla demande, vous ne m\’avez pas vu !', 'Mauvais portail ! Renvoyez-moi. Si la matriarche Ta\’dolla demande, vous ne m\’avez pas vu !', 38134),
(192807, 'frFR', 'Encore, Ta\’vini ? Nous en avons déjà parlé.', 'Encore, Ta\’vini ? Nous en avons déjà parlé.', 38134),
(192806, 'frFR', 'Ta\’vini ?', 'Ta\’vini ?', 38134),
(192804, 'frFR', '', 'Cette poudre nous permettra de voir au-delà de leurs illusions !', 38134),
(192803, 'frFR', 'Vous devriez voir certaines des choses qu\’ils stockent dans leur chambre du Vide.', 'Vous devriez voir certaines des choses qu\’ils stockent dans leur chambre du Vide.', 38134),
(192802, 'frFR', 'Ces mortels sont effectivement très étranges.', 'Ces mortels sont effectivement très étranges.', 38134),
(192801, 'frFR', 'Les mortels recherchent toutes sortes de choses. Peut-être en voudront-ils comme compagnon ?', 'Les mortels recherchent toutes sortes de choses. Peut-être en voudront-ils comme compagnon ?', 38134),
(192800, 'frFR', 'Cette chose ne vient-elle pas de Maldraxxus ? Pourquoi l\’avoir ramenée ici ?', 'Cette chose ne vient-elle pas de Maldraxxus ? Pourquoi l\’avoir ramenée ici ?', 38134),
(192799, 'frFR', 'Si vous le dites, Maître.', 'Si vous le dites, Maître.', 38134),
(192798, 'frFR', 'J\’en doute. Comparativement, nous leur offrons une vie d\’opulence.', 'J\’en doute. Comparativement, nous leur offrons une vie d\’opulence.', 38134),
(192797, 'frFR', 'Peut-être que toute cette chair et cette gelée visqueuse leur manqueront ?', 'Peut-être que toute cette chair et cette gelée visqueuse leur manqueront ?', 38134),
(192796, 'frFR', 'Nous leur rendons un service en leur permettant de quitter cet endroit.', 'Nous leur rendons un service en leur permettant de quitter cet endroit.', 38134),
(192795, 'frFR', 'Une autre marchandise de Maldraxxus. Si caractéristique.', 'Une autre marchandise de Maldraxxus. Si caractéristique.', 38134),
(192794, 'frFR', 'X', '', 38134),
(192792, 'frFR', '', 'Votre force est sans égale, Sans-Entraves.', 38134),
(192791, 'frFR', '', 'Personne ne peut nous chasser ! Maldraxxus ne tombera pas !', 38134),
(192790, 'frFR', 'Le Bastion vous remercie, votre Majesté.', '', 38134),
(192787, 'frFR', '', 'Je me suis souvenue d\’un détail important en méditant sur ma vie d\’avant. Cette archive le conservera pour les siècles à venir.', 38134),
(192786, 'frFR', '', 'Je n\’étais guère habile de mes mains avant d\’arriver au Bastion. Au final, je ne savais vraiment que me battre. J\’ai certes servi mon peuple, mais à quel prix ?', 38134),
(192785, 'frFR', '', 'Vers la fin de ma vie, j\’ai compris que j\’allais m\’éteindre en n\’ayant fait que prendre. Je ne donnais jamais qu\’une seule chose à autrui : la mort.', 38134),
(192784, 'frFR', '', 'Mon âme s\’est réveillée au Bastion. J\’ai appris le travail de la forge. La flamme qui brûlait en moi s\’est transformée. Je pouvais créer au lieu de détruire.', 38134),
(192783, 'frFR', 'Ça fait partie du métier ! Je tiens à ce qu\’ils soient heureux.', 'Ça fait partie du métier ! Je tiens à ce qu\’ils soient heureux.', 38134),
(192782, 'frFR', 'Vous ne vous lassez jamais de répéter la même chose à chaque créature que vous empaquetez ?', 'Vous ne vous lassez jamais de répéter la même chose à chaque créature que vous empaquetez ?', 38134),
(192781, 'frFR', 'En effet. À présent, allez placer celui-ci à la réserve.', 'En effet. À présent, allez placer celui-ci à la réserve.', 38134),
(192780, 'frFR', 'Et ils sont affreusement voraces.', 'Et ils sont affreusement voraces.', 38134),
(192779, 'frFR', 'Il est vrai qu\’ils dépendent énormément des ressources récupérées sur d\’autres êtres.', 'Il est vrai qu\’ils dépendent énormément des ressources récupérées sur d\’autres êtres.', 38134),
(192778, 'frFR', 'La diversité des créatures avec lesquelles ces mortels s\’associent ne cesse de m\’étonner.', 'La diversité des créatures avec lesquelles ces mortels s\’associent ne cesse de m\’étonner.', 38134),
(192777, 'frFR', 'Voilà qui est étrange. Un tel témoignage est une bénédiction pour les archives. Merci pour vos efforts.', '', 38134),
(192776, 'frFR', 'Les désavoués ont presque anéanti la mémoire de ce lieu. Récupérez les écrits que vous trouverez afin que nous les préservions.', '', 38134),
(192775, 'frFR', 'Retrouvez-moi au temple du Courage. L\’heure de rendre justice est venue.', '', 38134),
(192773, 'frFR', 'Les Assemblages ont envoyé des renforts au Bastion. Il faut aider les Kyrians à s\’en débarrasser.', '', 38134),
(192750, 'frFR', '', 'On dit que le Maître a fait exprès de maintenir la province du village dans le besoin. Ceux qui vivaient autrefois dans le confort sont désormais contraints de contempler la véritable richesse hors de leur portée.\n\nC\’est révoltant, mais c\’est aussi le lieu idéal pour trouver des adversaires.', 38134),
(192720, 'frFR', 'Repliez-vous ! En arrière !', 'Repliez-vous ! En arrière !', 38134),
(192719, 'frFR', 'C\’est bien noté, Maître.', 'C\’est bien noté, Maître.', 38134),
(192718, 'frFR', 'Évitons désormais d\’invoquer des créatures de ce royaume !', 'Évitons désormais d\’invoquer des créatures de ce royaume !', 38134),
(192717, 'frFR', 'Et voilà !', 'Et voilà !', 38134),
(192716, 'frFR', 'VITE !', 'VITE !', 38134),
(192715, 'frFR', 'Inversion du flux.', 'Inversion du flux.', 38134),
(192714, 'frFR', 'FAITES-LA SORTIR D\’ICI !', 'FAITES-LA SORTIR D\’ICI !', 38134),
(192713, 'frFR', 'Qu\’est-ce que c\’est que ça ! Ça a l\’air dangereux !', 'Qu\’est-ce que c\’est que ça ! Ça a l\’air dangereux !', 38134),
(192710, 'frFR', 'Ça devrait tout de même faire l\’affaire. Vous pouvez l\’empaqueter.', 'Ça devrait tout de même faire l\’affaire. Vous pouvez l\’empaqueter.', 38134),
(192709, 'frFR', 'Pas avec ces couleurs. J\’imagine que les couleurs et le nombre de pattes peuvent varier !', 'Pas avec ces couleurs. J\’imagine que les couleurs et le nombre de pattes peuvent varier !', 38134),
(192708, 'frFR', 'Je pensais que nous en avions déjà une comme ça ?', 'Je pensais que nous en avions déjà une comme ça ?', 38134),
(192707, 'frFR', 'Encore une avec huit pattes ! C\’est la première fois que je vois cette variante, cela dit.', 'Encore une avec huit pattes ! C\’est la première fois que je vois cette variante, cela dit.', 38134),
(192702, 'frFR', 'Tuez les intrus !', '', 38134),
(192701, 'frFR', 'À moi !', '', 38134),
(192700, 'frFR', 'De poussière à poussière !', '', 38134),
(192699, 'frFR', 'Vous serez anéantis !', '', 38134),
(192698, 'frFR', '', 'Beau travail, on y retourne. Je dois m\’assurer que vous saurez vous débrouiller face aux Hurans.', 38134),
(192695, 'frFR', '', 'Excellent, essayons à nouveau. Éclat lunaire et Colère vous aideront à vaincre les Hurans.', 38134),
(192692, 'frFR', 'Ce sera fait, Maître.', 'Ce sera fait, Maître.', 38134),
(192691, 'frFR', 'C\’est une magnifique représentante de son espèce. Prenez-en soin.', 'C\’est une magnifique représentante de son espèce. Prenez-en soin.', 38134),
(192690, 'frFR', '', 'Bien, encore une fois ! Mot de l\’ombre : Douleur continuera à infliger des dégâts à votre adversaire pendant que vous lui assénez Châtiment.', 38134),
(192688, 'frFR', 'Entre dans la boîte, ma jolie. Ne t\’en fais pas, c\’est plus grand à l\’intérieur.', 'Entre dans la boîte, ma jolie. Ne t\’en fais pas, c\’est plus grand à l\’intérieur.', 38134),
(192686, 'frFR', 'Celle-ci est plutôt jolie !', 'Celle-ci est plutôt jolie !', 38134),
(192685, 'frFR', '', 'Son orgueil et son arrogance sont plus que légendaires. Venez, privons Denathrius de sa prise de choix.', 38134),
(192680, 'frFR', 'La mort vient d\’en haut !', '', 38134),
(192679, 'frFR', 'Tous indignes jusqu\’au dernier.', '', 38134),
(192678, 'frFR', 'Votre échec est consommé.', '', 38134),
(192677, 'frFR', 'Vous n\’aviez aucune chance.', '', 38134),
(192676, 'frFR', 'Enfin, vous ne parlez plus.', '', 38134),
(192675, 'frFR', 'Disparaissez, vermine !', '', 38134),
(192673, 'frFR', '', 'Bonjour, je m\’appelle Sorcha.$b$bVous avez l\’air de savoir vous battre. Tant mieux, parce qu\’il reste encore bien des batailles à livrer…', 38134),
(192672, 'frFR', 'Oups.', '', 38134),
(192671, 'frFR', 'Ce qui est arrivé au Bastion souille notre honneur. Nous devons tout faire pour le restaurer.', '', 38134),
(192670, 'frFR', '', 'Je suis venue supplier la dame des Cascades de me fournir des armes. C\’est à Tirna Eas que la quasi-totalité de nos armements, outils et fournitures sont fabriqués. À l\’évidence, ils en ont plus que nécessaire.$b$bQui sait, vous parviendrez peut-être mieux que moi à la convaincre de nous aider ?', 38134),
(192669, 'frFR', 'Les Assemblages continuent à renforcer leur présence au temple du Courage. Aidez les Kyrians à les exterminer.', '', 38134),
(192667, 'frFR', '', 'Aidez-moi à faire entendre raison à la dromane. Elle reste ici à prendre racine alors que les autres souffrent !', 38134),
(192616, 'frFR', 'Les temps sont durs par ici, c\’est sûr, mais il faut bien que je veille sur les petits.$b$bSi vous envisagez de continuer vos emplettes, allez donc saluer Jé\’ni et les gamins à l\’autre bout du bosquet.', '', 38134),
(192615, 'frFR', 'Bienvenue. Moi, c\’est Cavinus, et vous ne trouverez pas meilleur forgegorm dans tout Sylvarden.$b$bCertes, je pourrais bien être le dernier, mais par la reine, ça ne m\’empêche pas d\’être le meilleur !', '', 38134),
(192614, 'frFR', 'J\’adore travailler avec les gorms. Excellents matériaux, excellents compagnons, excellente nourriture. Ils servent quasiment à tout !$b$bVous voulez les meilleures armures de Sylvarden ? Utilisez des carapaces de gorm !$b$bVous êtes en quête d\’un tranchant incassable ? Griffes et dards de gorm feront votre bonheur !$b$bDésolé pour cet excès d\’enthousiasme, c\’est que j\’adore mon métier, vous comprenez. Enfin, la plupart du temps.', '', 38134),
(192613, 'frFR', '', 'J\’espère que j\’aurai un jour la chance d\’apprendre la magie du Givre ! Fiou ! Fiou !', 38134),
(192612, 'frFR', '', '<Marétoile baisse le ton et chuchote bruyamment.> Parfois, j\’ai l\’impression que mes jouets bougent quand j\’ai le dos tourné. C\’est de la magie, à votre avis ?', 38134),
(192611, 'frFR', '', 'Parfois, je me dis que j\’aimerais bien vivre sous la mer. Mais on habiterait où ? Et comment on ferait pour respirer ?', 38134),
(192610, 'frFR', '', '<Avec un air de connivence, Harpécume murmure bruyamment.> Hé dites donc, vous voulez voir mon ami ?$b$b<Harpécume vous montre une petite éponge.>$b$bIl vit sous la mer. Et son meilleur ami, c\’est une étoile de mer !', 38134),
(192599, 'frFR', 'Elle a parlé d\’une perte incommensurable. D\’une vie qu\’elle ne reconnaissait plus en dehors des archives et des écrits. La voie, disait-elle, l\’avait effacée !', '', 38134),
(192598, 'frFR', 'Elle approchait de la fin de ses épreuves. Mon temple était le dernier obstacle qui la séparait de l\’ascension, mais ce n\’est pas son orgueil qui a causé son échec.', '', 38134),
(192597, 'frFR', 'J\’ai perdu une aspirante aujourd\’hui. Je ne suis pas vaniteux au point de croire que son destin ne dépendait que de moi, mais cette perte soudaine a été… douloureuse.', '', 38134),
(192596, 'frFR', 'J\’ai grand besoin de réfléchir et mon ami Thénios m\’a proposé de venir ici. Sauras-tu, cher parchemin, me délivrer du fardeau que je porte désormais ?', '', 38134),
(192590, 'frFR', '', 'Rebonjour. J\’étais en train de discuter avec Tirna Eas.$b$bAllez-y, je vous écoute. Vous ne me dérangez pas. Ça fait longtemps qu\’on ne s\’est pas parlé, et cette conversation risque de s\’éterniser.', 38134),
(192589, 'frFR', '', 'Cette forêt-ci, cette forêt-là. Quelle différence ? Nous sommes tous des feuilles de la grande forêt.', 38134),
(192588, 'frFR', '', 'Nos chemins se croisent à nouveau, Sans-Entraves. Et ce n\’est peut-être pas la dernière fois.', 38134),
(192586, 'frFR', '', 'Ah, c\’est bon de reprendre le travail. Surtout sans les ricanements des spriggans et les horribles manifestations en maraude…', 38134),
(192585, 'frFR', '', 'Ce n\’est pas mon atelier, mais je peux tout de même travailler ici. Ces âmetoiles ne vont pas se fabriquer toutes seules !', 38134),
(192584, 'frFR', '', 'Merci encore pour votre aide dans les Florès. Sans vous, nous serions toujours coincés là-bas !', 38134),
(192581, 'frFR', '', 'Pouvons-nous commencer ?', 38134),
(192580, 'frFR', 'Je dois dire que cet endroit est bien plus agréable que mon atelier. Ces spriggans faisaient un tel raffut !$b$bIci, je suis au calme. Je peux enfin entendre le fracas de mon maillet fétiche !', '', 38134),
(192579, 'frFR', 'Depuis que vous m\’avez rendu mon maillet, les choses se sont améliorées. C\’est un vrai porte-bonheur, vous savez !', '', 38134),
(192578, 'frFR', 'Tiens, voilà de la visite… Encore vous ? Quelle chance !', '', 38134),
(192577, 'frFR', '', 'Bonjour, très $ucher:chère;. Dites-moi, comment se portent les terres au-delà de mon bosquet ?', 38134),
(192576, 'frFR', '', 'Je sais que les temps sont durs pour tous en Sylvarden. Mais l\’on m\’a confié la charge de ce bosquet et j\’entends assurer sa sécurité.', 38134),
(192575, 'frFR', '', 'Bienvenue au bassin Tombe-Lueur.$b$bJe vous en prie, détendez-vous. Vous êtes en sécurité ici.', 38134),
(192574, 'frFR', '', 'Voyez comme il brille ! Laissez-le résonner contre le monstre né du bourdon corrompu !', 38134),
(192573, 'frFR', '', 'Pour ceux qui cherchent à se purifier, un tel bourdon est un don du ciel. Pour ceux qui se sont détournés de la voie… son timbre est une source de terreur absolue. Je crois qu\’ils ont compris la leçon.', 38134),
(192572, 'frFR', '', 'Les désavoués ont encore corrompu mon plus puissant bourdon. Prenez ce carillon pour riposter, que la pureté de son chant mène nos ennemis à la vérité.', 38134),
(192567, 'frFR', 'Vous trouvez le savon de dame Baie-de-Lune sur la table.', 'Vous trouvez le savon de dame Baie-de-Lune sur la table.', 38134),
(192566, 'frFR', 'Ne vous en mêlez pas !', '', 38134),
(192565, 'frFR', 'N\’approchez pas !', '', 38134),
(192564, 'frFR', 'Contagion !', '', 38134),
(192563, 'frFR', 'La maladie se propage !', '', 38134),
(192562, 'frFR', '', 'Vous ne… gagnerez… jamais…', 38134),
(192561, 'frFR', '', 'Le Bastion sera à nous !', 38134),
(192548, 'frFR', '', 'Une invitation du prince Rénathal ? Comme c\’est exquis. Je ne raterais ça pour rien au monde.', 38134),
(192545, 'frFR', '', 'Les profondeurs vous emporteront !', 38134),
(192544, 'frFR', '', 'Vous n\’avez pas la solidité de la pierre.', 38134),
(192543, 'frFR', '', 'Personne ne s\’échappe des profondeurs Sanguines. Personne.', 38134),
(192542, 'frFR', '', 'La rébellion vacille.', 38134),
(192541, 'frFR', '', 'Rendez-nous le prisonnier !', 38134),
(192538, 'frFR', 'Veuillez me pardonner. Nous sommes en train de surveiller le flot des âmes.', '', 38134),
(192537, 'frFR', 'Allez pas torcher le boulot, hein !', '', 38134),
(192536, 'frFR', 'Faut qu\’on remplisse les quotas !', '', 38134),
(192534, 'frFR', 'Hé, vous autres ! Du nerf !', '', 38134),
(192533, 'frFR', 'Cherchez pas midi à quatorze heures, faut mettre un bon coup de collier !', '', 38134),
(192532, 'frFR', 'Pas de problème ! Avec les purotins, la fête est plus chouette !', '', 38134),
(192531, 'frFR', '', 'Sa loge est juste en face.', 38134),
(192530, 'frFR', '', 'Jouons un petit tour à Baie-de-Lune. Il faut bien que ce soit son tour, un peu !', 38134),
(192525, 'frFR', '', 'Vous avez utilisé Éclat lunaire trop tôt. Pour optimiser ses dégâts, il faut le relancer quand il est presque terminé.', 38134),
(192524, 'frFR', '', 'Éclat lunaire est en train de disparaître, relancez-le !', 38134),
(192521, 'frFR', '', 'Éclat lunaire inflige des dégâts sur la durée. Utilisez Colère quand votre victime subit ses effets.', 38134),
(192520, 'frFR', '', 'Bien ! Continuez à utiliser Colère pendant qu\’Éclat lunaire inflige ses dégâts et attendez le bon moment pour le relancer.', 38134),
(192518, 'frFR', '', 'Utilisez Éclat lunaire au début du combat. Il inflige des dégâts sur la durée.', 38134),
(192517, 'frFR', '', 'Vous avez utilisé Mot de l\’ombre : Douleur trop tôt. Pour optimiser ses dégâts, il faut le relancer quand il est presque terminé.', 38134),
(192516, 'frFR', '', 'Mot de l\’ombre : Douleur est en train de disparaître, relancez-le !', 38134),
(192514, 'frFR', '', 'Excellent, encore une fois ! Bien employé, Mot de l\’ombre : Douleur mine peu à peu vos adversaires.', 38134),
(192512, 'frFR', '', 'Bien ! Continuez à utiliser Châtiment pendant que Mot de l\’ombre : Douleur inflige ses dégâts, et attendez le bon moment pour relancer ce dernier.', 38134),
(192511, 'frFR', '', 'Utilisez Mot de l\’ombre : Douleur au début du combat. Il inflige des dégâts sur la durée.', 38134),
(192464, 'frFR', 'Ces imbéciles ne servent pas Revendreth. Très joli travail, $n.', '', 38134),
(192463, 'frFR', 'En l\’absence de la Traquepeur, de nouvelles forces ont pris le pouvoir. Renversez-les !', '', 38134),
(192446, 'frFR', '', 'Il me reste… tant de choses… à découvrir…', 38134),
(192445, 'frFR', '', 'Enfin. Bien, Z\'rali… où en étions-nous ?', 38134),
(192444, 'frFR', '', 'Je ne tirerai rien de vous.', 38134),
(192443, 'frFR', '', 'Cessez de me déranger.', 38134),
(192441, 'frFR', '', 'Comment osez-vous interrompre mes recherches !', 38134),
(192440, 'frFR', '', 'Commence par manger un peu. Tu as besoin de reprendre des forces.', 38134),
(192430, 'frFR', '', 'Non… La Traquepeur avait foi en moi…', 38134),
(192429, 'frFR', '', 'De la viande fraîche pour mes créatures !', 38134),
(192427, 'frFR', 'Vous vous confesserez, d\’une façon ou d\’une autre.', '', 38134),
(192425, 'frFR', 'Tant de pêcheurs… m\’attendent… encore…', '', 38134),
(192424, 'frFR', 'Pélagos est en Sylvarden pour obtenir une audience avec la reine. Allez le rejoindre.', '', 38134),
(192423, 'frFR', 'D\’autres nécessitent mes bons soins.', '', 38134),
(192422, 'frFR', 'Repentez-vous, $umortel:mortelle;.', '', 38134),
(192421, 'frFR', 'Quel dommage, je vous voyais tenir plus longtemps.', '', 38134),
(192419, 'frFR', 'Bien, commençons.', '', 38134),
(192405, 'frFR', '', 'Tout est prêt.', 38134),
(192403, 'frFR', '', 'Puis-je savoir pourquoi vous interrompez mes activités mondaines ?', 38134),
(192402, 'frFR', 'Si ça vous est égal, j\’aimerais autant pas me faire prendre. \n\nPour vous, bon, ça sera la torture éternelle, la mort, et puis un peu plus de torture éternelle.\n\nMais moi, ils risquent de m\’envoyer dans le marais des Affres. Et ça, pas question !', '', 38134),
(192394, 'frFR', 'Votre peau me fera une jolie ceinture !', '', 38134),
(192389, 'frFR', 'Pas… la… figure…', '', 38134),
(192386, 'frFR', 'Très bien. Au service du Dessein.', 'Très bien. Au service du Dessein.', 38134),
(192385, 'frFR', 'Les observations ont été apportées, conseilleur.', 'Les observations ont été apportées, conseilleur.', 38134),
(192384, 'frFR', 'Merci beaucoup. Vous avez bien servi le Dessein.', 'Merci beaucoup. Vous avez bien servi le Dessein.', 38134),
(192383, 'frFR', 'Scribe du destin, voici les dernières observations du Flot d\’âmes.', 'Scribe du destin, voici les dernières observations du Flot d\’âmes.', 38134),
(192382, 'frFR', 'Oui, conseilleur. Tout de suite.', 'Oui, conseilleur. Tout de suite.', 38134),
(192381, 'frFR', 'Apportez ces observations au scribe du destin.', 'Apportez ces observations au scribe du destin.', 38134),
(192363, 'frFR', 'Acceptez le défi. Dominez les épreuves.', '', 38134),
(192362, 'frFR', 'Savourez vos victoires. Tirez-en de la fierté.', '', 38134),
(192361, 'frFR', 'Servez toujours le Primat.', '', 38134),
(192360, 'frFR', 'Ici, chaque âme doit mériter sa place.', '', 38134),
(192359, 'frFR', 'Maldraxxus honore les puissants.', '', 38134),
(192340, 'frFR', 'Je ne fais que le servir, comme nous tous.', 'Je ne fais que le servir, comme nous tous.', 38134),
(192338, 'frFR', 'La chance vous sourit. Le Dessein est avec vous.', 'La chance vous sourit. Le Dessein est avec vous.', 38134),
(192334, 'frFR', 'Il y a peu, mon conseilleur a loué mes transactions avec l\’émissaire des négociants.', 'Il y a peu, mon conseilleur a loué mes transactions avec l\’émissaire des négociants.', 38134),
(192333, 'frFR', 'Salutations.', 'Salutations.', 38134),
(192331, 'frFR', 'Je crois que nous sommes tous dans le même cas. Le Dessein nous met à l\’épreuve.', 'Je crois que nous sommes tous dans le même cas. Le Dessein nous met à l\’épreuve.', 38134),
(192329, 'frFR', 'Je dois bien admettre que j\’ai moi aussi quelques doutes.', 'Je dois bien admettre que j\’ai moi aussi quelques doutes.', 38134),
(192328, 'frFR', 'Je ne comprends plus rien au Dessein. Je ne sais que penser de tout ce chaos.', 'Je ne comprends plus rien au Dessein. Je ne sais que penser de tout ce chaos.', 38134),
(192326, 'frFR', 'Ils n\’en manquent pas, c\’est certain. J\’imagine qu\’il s\’agit là de leur rôle dans le Dessein.', 'Ils n\’en manquent pas, c\’est certain. J\’imagine qu\’il s\’agit là de leur rôle dans le Dessein.', 38134),
(192325, 'frFR', 'C\’est cela. Même si j\’ai parfois du mal à calmer leurs penchants chaotiques.', 'C\’est cela. Même si j\’ai parfois du mal à calmer leurs penchants chaotiques.', 38134),
(192324, 'frFR', 'Vous servez d\’intermédiaire avec les négociants, je me trompe ?', 'Vous servez d\’intermédiaire avec les négociants, je me trompe ?', 38134),
(192300, 'frFR', '', 'Je dois manger. Vous, partez.', 38134),
(192298, 'frFR', '', 'Avaler, dévorer, engloutir !', 38134),
(192297, 'frFR', 'Et la tienne manque d\’efficacité, Néméa ! Tu passes tes journées à créer des liens alors que je passe les miennes à agir et à changer les choses !', '', 38134),
(192296, 'frFR', '', 'Ta méthode manque de finesse, Pélodis. Tes phalynx t\’obéissent aveuglément. Il n\’y a pas de communication, pas d\’échange.', 38134),
(192295, 'frFR', '', 'Tirna Scithe dépérit sous les crocs des gorms. L\’équilibre doit être rétabli.', 38134),
(192294, 'frFR', '', 'Hé… Ça ne va pas, ça. Ça ne va pas du tout !', 38134),
(192293, 'frFR', '', 'Ouais, c\’est moi qui gagne !', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192292, 'frFR', '', 'Et non ! Je vous ai $ueu:eue; !', 38134),
(192291, 'frFR', '', 'Mais non, enfin, c\’était pas moi !', 38134),
(192290, 'frFR', '', 'Han, bon, d\’accord. J\’arrête les brumes.', 38134),
(192289, 'frFR', '', 'La récréation est terminée. Les vents nous disent d\’avancer.', 38134),
(192288, 'frFR', '', 'C\’était vraiment super, je ne m\’étais pas amusée comme ça depuis longtemps… Revenez vite !', 38134),
(192284, 'frFR', '', 'Un super jeu rien que pour nous deux !', 38134),
(192283, 'frFR', '', 'Vous jouez avec moi ? Alleeeez !', 38134),
(192282, 'frFR', '', 'Vous, là, jouez avec moi !', 38134),
(192281, 'frFR', '', 'Il faut bouger vite !', 38134),
(192280, 'frFR', '', 'Hé, attrapez !', 38134),
(192279, 'frFR', '', 'Essayez donc d\’esquiver !', 38134),
(192278, 'frFR', 'Le Dessein est notre voie.', 'Le Dessein est notre voie.', 38134),
(192277, 'frFR', 'C\’est bien moi, oui. Je trouve ces créatures fort irritantes, mais il faut croire qu\’elles servent le Dessein… Du moins, à leur façon.', 'C\’est bien moi, oui. Je trouve ces créatures fort irritantes, mais il faut croire qu\’elles servent le Dessein… Du moins, à leur façon.', 38134),
(192276, 'frFR', '', 'C\’est moi, hi hi hi !', 38134),
(192275, 'frFR', 'C\’est vous qui devez observer les négociants ? Alors j\’imagine, oui.', 'C\’est vous qui devez observer les négociants ? Alors j\’imagine, oui.', 38134),
(192274, 'frFR', 'Je ne peux pas rester, mais je voulais vous saluer. Une montagne de travail m\’attend.', 'Je ne peux pas rester, mais je voulais vous saluer. Une montagne de travail m\’attend.', 38134),
(192272, 'frFR', 'Salutations.', 'Salutations.', 38134),
(192271, 'frFR', 'Salutations.', 'Salutations.', 38134),
(192270, 'frFR', '', 'Ouah, vous vous débrouillez bien !', 38134),
(192269, 'frFR', '', 'Ah, vous m\’avez trouvée ! Super !', 38134),
(192268, 'frFR', '', 'J\’ai une petite devinette pour vous !', 38134),
(192267, 'frFR', '', 'Alors, laquelle est la vraie moi ?', 38134),
(192266, 'frFR', '', 'Il y en a une qui n\’est pas comme les autres !', 38134),
(192265, 'frFR', '', 'Merci d\’avoir joué avec moi ! Revenez vite !', 38134),
(192264, 'frFR', '', 'On joue plus ?', 38134),
(192263, 'frFR', '', 'Vous ne voulez plus jouer ?', 38134),
