INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'itIT', '', 'Ti do il benvenuto. Ti interessano delle Chiavi del Potere?', 38134),
(209048, 'itIT', 'All\'interno di Oribos, l\'Aggressore Zo\'dash può soddisfare i tuoi bisogni. Puoi trovare un potenziatore d\'oggetti anche nel Santuario della Congrega.', 'All\'interno di Oribos, l\'Aggressore Zo\'dash può soddisfare i tuoi bisogni. Puoi trovare un potenziatore d\'oggetti anche nel Santuario della Congrega.', 38134),
(208581, 'itIT', 'Salve, NOMEUTENTE. Spero che tu stia trascorrendo una GENERA AGGETTIVO POSITIVO CASUALE giornata.', '', 38134),
(208211, 'itIT', 'Il mio vecchio maestro, il Custode delle Cripte Kassir, vuole incontrarti nelle Sale della Redenzione, $p.', '', 38134),
(208209, 'itIT', 'Tubbins chiede di te nel Distretto delle Braci, $p.', '', 38134),
(208205, 'itIT', 'Gresit ha chiesto di te fuori dalle Sale della Redenzione, $p.', '', 38134),
(208120, 'itIT', 'Certi giorni sono speciali! Se vedi la mia Cassa Dorata qui, è un giorno speciale! In quei giorni celebriamo un Factotum. \n\nPrima mi chiedi chi è, poi dovrai dargli un Puriano Maturo. Torna qui e apri la cassa! Sì!', '', 38134),
(208019, 'itIT', '$gUno:Una; di noi. $gUno:Una; di noi. $gUno:Una; di noi...', '', 38134),
(208018, 'itIT', 'Il Polemarco Adrestes batte i piedi a terra mentre canticchia.', '', 38134),
(208016, 'itIT', '', 'Ovunque andrai, io ti osserverò.', 38134),
(208015, 'itIT', 'Tesoro mi porta sempre gli appunti migliori!', '', 38134),
(208014, 'itIT', '', 'Molti amici. Molta gioia. Sika fortunata.', 38134),
(208013, 'itIT', 'Hmm... È $gil mio migliore amico:la mia migliore amica; e apprendista... forse il titolo migliore sarebbe Miglior Amicista? Miglior Apprendico? No, no, no...', '', 38134),
(208012, 'itIT', 'L\'unione fa la forza. Che le nostre strade non si separino mai.', '', 38134),
(208011, 'itIT', '', 'Se la morte ci ha riuniti, quale forza potrebbe sperare di separarci?', 38134),
(208010, 'itIT', 'Fate tutti silenzio e ascoltate $p. $gIl mio migliore amico:La mia migliore amica; ne sa di più sul combattimento di tutti voi messi insieme!', '', 38134),
(208009, 'itIT', '', 'Questo albero prospera nella foresta, $p. Torna presto al mio bosco, così che possiamo prosperare entrambi.', 38134),
(208008, 'itIT', 'Chufa inizia lentamente a intonare un coro.', '', 38134),
(208007, 'itIT', 'E diciamo che $p è $gil migliore amico:la migliore amica di tutti i Dragatori!', '', 38134),
(208006, 'itIT', 'Ora abbiamo un sacco di amici, ma solo uno può essere il migliore...', '', 38134),
(208005, 'itIT', 'Non vedo l\'ora che inizi la prossima Corte delle Braci. Forse accetterò anche l\'invito a ballare di quel nobile...', '', 38134),
(208004, 'itIT', '', 'C\'è sempre un\'altra caccia, ma non ci sarà mai $gun altro:un\'altra; $p.', 38134),
(208003, 'itIT', 'La mia lama sarà pronta ogni volta che avrai bisogno di me. Risponderò sempre alla tua chiamata.', '', 38134),
(208002, 'itIT', '', 'Congratulazioni, $p. La tua corte è magnifica, proprio come te.', 38134),
(208001, 'itIT', 'Siediti, rilassati, nessuna fretta. Ho sempre tempo per $gil mio migliore amico:la mia migliore amica; $p.', '', 38134),
(208000, 'itIT', '', 'In che guai ci ficcheremo insieme la prossima volta, $p? Non vedo l\'ora!', 38134),
(207999, 'itIT', '', 'Non lasciare che ti trattenga, $gmio piccolo:mia piccola; $p.', 38134),
(207996, 'itIT', 'Fai attenzione, $p. Ci sono sempre altre anime da salvare, ma non dimenticare che alcune sono già perdute.', '', 38134),
(207951, 'itIT', 'Errore. Accesso proibito fino all\'ottenimento di ulteriore esperienza.', '', 38134),
(207923, 'itIT', 'C\'è molto da fare per Selvarden, $n.', 'C\'è molto da fare per Selvarden, $n.', 38134),
(207903, 'itIT', '', 'Vuoi ascoltare una storia?', 38134),
(207841, 'itIT', '$n! Vieni accà!', '', 38134),
(207504, 'itIT', 'Oggi c\'è un vento perfetto per volare! Dove vuoi andare?', '', 38134),
(207499, 'itIT', '', '<Dama Ilinca sorride.>\n\nOttima domanda. È saggio modificare la lista degli ospiti ogni settimana per assicurarsi che siano compatibili tra loro.\n\nPuoi modificare l\'invito di un ospite parlando con Sire Garridan per vedere la Lista degli Ospiti.\n\nPotremmo aver bisogno di alcuni Rubini Infusi per alleviare il malumore dovuto al cambio. Ma per coprire quei costi puoi assumere ambasciatori o acquistare Inviti Vuoti da me.\n\nOh, e cerca di non invitare ospiti che hanno gusti opposti in termini di atmosfere. Sarà difficile mantenere felici entrambi.', 38134),
(207446, 'itIT', 'Unità è un costrutto davvero unico. È vincolata alle Terretetre da un indefesso senso del dovere, ed è in grado di scoprire delle armature che rappresentano il nostro scopo nelle Terretetre mentre completa gli incarichi per l\'Armata Imperitura di Maldraxxus.', 'Unità è un costrutto davvero unico. È vincolata alle Terretetre da un indefesso senso del dovere, ed è in grado di scoprire delle armature che rappresentano il nostro scopo nelle Terretetre mentre completa gli incarichi per l\'Armata Imperitura di Maldraxxus.', 38134),
(207442, 'itIT', '', 'Sì, e te lo dirò dopo che avrai portato il tuo amico a Oribos.\n\nTorna da me quando sarà in salvo.', 38134),
(207434, 'itIT', 'Sei tu laggiù, Teotar?', '', 38134),
(207388, 'itIT', '', 'Qualche richiesta?', 38134),
(207376, 'itIT', 'Un vero peccato che tu abbia preferito la formalità alla forza, Calcafauce.', 'Un vero peccato che tu abbia preferito la formalità alla forza, Calcafauce.', 38134),
(207372, 'itIT', 'I Silfi della Notte sono fortunati ad avere te al loro servizio, Calcafauce.', 'I Silfi della Notte sono fortunati ad avere te al loro servizio, Calcafauce.', 38134),
(207371, 'itIT', 'Sembra che le terre selvagge ti abbiano $gchiamato:chiamata; al loro servizio, $n. Una nobile chiamata.', 'Sembra che le terre selvagge ti abbiano $gchiamato:chiamata; al loro servizio, $n. Una nobile chiamata.', 38134),
(207370, 'itIT', 'Anche se non hai scelto la nostra Congrega, servi un grande ciclo, Calcafauce. Un compito davvero nobile.', 'Anche se non hai scelto la nostra Congrega, servi un grande ciclo, Calcafauce. Un compito davvero nobile.', 38134),
(207369, 'itIT', 'I nostri saluti alla Regina dell\'Inverno, una vera alleata dell\'Arconte.', 'I nostri saluti alla Regina dell\'Inverno, una vera alleata dell\'Arconte.', 38134),
(207368, 'itIT', 'Che tu possa trovare soddisfazione nella tua... raccolta, e significato nel servizio da te scelto, $n.', 'Che tu possa trovare soddisfazione nella tua... raccolta, e significato nel servizio da te scelto, $n.', 38134),
(207367, 'itIT', 'Calcafauce, che la tua guida possa aiutare le anime ribelli a liberarsi del loro fardello.', 'Calcafauce, che la tua guida possa aiutare le anime ribelli a liberarsi del loro fardello.', 38134),
(207366, 'itIT', 'I Venthyr sanno che il passato è un fardello. Le nostre Congreghe hanno molto in comune, $n.', 'I Venthyr sanno che il passato è un fardello. Le nostre Congreghe hanno molto in comune, $n.', 38134),
(207365, 'itIT', 'Hai scelto di servire i Venthyr? Guidare le anime verso la redenzione è un compito davvero degno.', 'Hai scelto di servire i Venthyr? Guidare le anime verso la redenzione è un compito davvero degno.', 38134),
(207364, 'itIT', 'Presta servizio... qualunque servizio sia.', 'Presta servizio... qualunque servizio sia.', 38134),
(207363, 'itIT', 'Il favore dell\'Arconte è su di te, a prescindere dalla tua Congrega.', 'Il favore dell\'Arconte è su di te, a prescindere dalla tua Congrega.', 38134),
(207362, 'itIT', 'Che tu possa portare disciplina nel regno del conflitto, $n.', 'Che tu possa portare disciplina nel regno del conflitto, $n.', 38134),
(207361, 'itIT', 'Ti credevo più $gadatto:adatta; al servizio che ai combattimenti, Calcafauce. Forse mi sbagliavo.', 'Ti credevo più $gadatto:adatta; al servizio che ai combattimenti, Calcafauce. Forse mi sbagliavo.', 38134),
(207360, 'itIT', 'Il tuo servizio onora la nostra Congrega, Calcafauce.', 'Il tuo servizio onora la nostra Congrega, Calcafauce.', 38134),
(207359, 'itIT', 'Sono lieto di vedere che i nostri cammini convergono, $n. La tua devozione è molto apprezzata.', 'Sono lieta di vedere che i nostri cammini convergono, $n. La tua devozione è molto apprezzata.', 38134),
(207358, 'itIT', 'Ah, Calcafauce! Senza dubbio stai lavorando per l\'Arconte. Presta servizio.', 'Ah, Calcafauce! Senza dubbio stai lavorando per l\'Arconte. Presta servizio.', 38134),
(207357, 'itIT', 'Siamo fortunati ad averti tra i nostri ranghi, $n. Che la grazia dell\'Arconte guidi il tuo cammino.', 'Siamo fortunati ad averti tra i nostri ranghi, $n. Che la grazia dell\'Arconte guidi il tuo cammino.', 38134),
(207356, 'itIT', 'Quando ti sarai $gstufato:stufata; di spassartela con i Silfi, torna qui per un po\' di vero divertimento.', 'Quando ti sarai $gstufato:stufata; di spassartela con i Silfi, torna qui per un po\' di vero divertimento.', 38134),
(207355, 'itIT', 'Porta i nostri migliori auguri alla Regina dell\'inverno, Calcafauce.', 'Porta i nostri migliori auguri alla Regina dell\'inverno, Calcafauce.', 38134),
(207354, 'itIT', 'Le nostre foreste forse sono meno maestose di quelle di Selvarden, ma anche noi combattiamo per preservare il nostro regno.', 'Le nostre foreste forse sono meno maestose di quelle di Selvarden, ma anche noi combattiamo per preservare il nostro regno.', 38134),
(207353, 'itIT', 'I Silfi della Notte non competono per il potere? Allora cosa fanno in quei boschi selvatici?', 'I Silfi della Notte non competono per il potere? Allora cosa fanno in quei boschi selvatici?', 38134),
(207352, 'itIT', 'È un bene che tu ti sia $gschierato:schierata; con la nostra congrega, $n. Credo che staresti divinamente $gvestito:vestita; di velluto.', 'È un bene che tu ti sia $gschierato:schierata; con la nostra congrega, $n. Credo che staresti divinamente $gvestito:vestita; di velluto.', 38134),
(207351, 'itIT', 'Io combatto con la Corte dei Mietitori. E con te, Calcafauce.', 'Io combatto con la Corte dei Mietitori. E con te, Calcafauce.', 38134),
(207350, 'itIT', 'Chissà che meravigliose indiscrezioni starai raccogliendo per la tua Pietra del Peccato!', 'Chissà che meravigliose indiscrezioni starai raccogliendo per la tua Pietra del Peccato!', 38134),
(207349, 'itIT', 'Con il tuo aiuto, $n, rivendicheremo il nostro nobile scopo.', 'Con il tuo aiuto, $n, rivendicheremo il nostro nobile scopo.', 38134),
(207348, 'itIT', 'Di tutte le congreghe, scommetto che i Kyrian hanno le Pietre del Peccato più noiose.', 'Di tutte le congreghe, scommetto che i Kyrian hanno le Pietre del Peccato più noiose.', 38134),
(207347, 'itIT', 'Revendreth sarà anche un luogo di ombre, Kyrian, ma i nostri doveri non sono meno nobili dei tuoi.', 'Revendreth sarà anche un luogo di ombre, Kyrian, ma i nostri doveri non sono meno nobili dei tuoi.', 38134),
(207346, 'itIT', '', 'Grazie... È bello sapere che i miei ultimi sforzi non sono stati vani.', 38134),
(207345, 'itIT', 'Quei tuoi Factotum sembrano ben addestrati, $n. Sai, abbiamo delle cripte che hanno bisogno di essere ripulite.', 'Quei tuoi Factotum sembrano ben addestrati, $n. Sai, abbiamo delle cripte che hanno bisogno di essere ripulite.', 38134),
(207343, 'itIT', 'Per quanto i nostri metodi siano diversi, entrambe le nostre Congreghe cercano di guidare le anime lungo una nobile via.', 'Per quanto i nostri metodi siano diversi, entrambe le nostre Congreghe cercano di guidare le anime lungo una nobile via.', 38134),
(207342, 'itIT', 'Hai scelto di percorrere un sentiero brutale, mortale. Se avessi bisogno di un po\' di eleganza, sai dove trovarci.', 'Hai scelto di percorrere un sentiero brutale, mortale. Se avessi bisogno di un po\' di eleganza, sai dove trovarci.', 38134),
(207341, 'itIT', '', 'Il Margravio sarà anche sparito, ma questo non significa che i nostri sforzi siano stati fermati. Ci vendicheremo, com\'è giusto che sia.\n\nCosa... Aspetta, tu vieni da Azeroth. Dimmi, il mio messaggio è arrivato ai Senza Corona?', 38134),
(207340, 'itIT', 'Sai, la Corte delle Braci è più pericolosa di quanto sembri. Penso che l\'adorerai.', 'Sai, la Corte delle Braci è più pericolosa di quanto sembri. Penso che l\'adorerai.', 38134),
(207339, 'itIT', 'È un bene che la tua Congrega combatta per difendere il nostro regno, Calcafauce.', 'È un bene che la tua Congrega combatta per difendere il nostro regno, Calcafauce.', 38134),
(207338, 'itIT', '', 'Il Margravio sarà anche sparito, ma questo non significa che i nostri sforzi siano stati fermati. Ci vendicheremo, com\'è giusto che sia.', 38134),
(207337, 'itIT', 'Metti giù i tuoi bisturi e i tuoi aghi, $gNecrosignore:Necrosignora;. Preferisco che la mia pelle resti saldamente attaccata così com\'è.', 'Metti giù i tuoi bisturi e i tuoi aghi, $gNecrosignore:Necrosignora;. Preferisco che la mia pelle resti saldamente attaccata così com\'è.', 38134),
(207335, 'itIT', 'Qualcosa si muove alle tue spalle, $n. Sono... brillantini da Silfi?', 'Qualcosa si muove alle tue spalle, $n. Sono... brillantini da Silfi?', 38134),
(207334, 'itIT', 'Calcafauce! Non ti sei ancora $gstancato:stancata; di occuparti di alberi? Ti andrebbe una vera rissa?', 'Calcafauce! Non ti sei ancora $gstancato:stancata; di occuparti di alberi? Ti andrebbe una vera rissa?', 38134),
(207333, 'itIT', 'I Necrosignori preferiscono la tattica ai trucchi, $gSilfo:Silfa; della Notte.', 'I Necrosignori preferiscono la tattica ai trucchi, $gSilfo:Silfa; della Notte.', 38134),
(207332, 'itIT', 'No, non verrò al tuo spettacolo.', 'No, non verrò al tuo spettacolo.', 38134),
(207331, 'itIT', 'Il modo migliore per liberare un\'anima dall\'orgoglio è farglielo sputare a pugni.', 'Il modo migliore per liberare un\'anima dall\'orgoglio è farglielo sputare a pugni.', 38134),
(207330, 'itIT', 'Fai attenzione a dove metti i piedi, Venthyr. Ti ritroverai le scarpe piene di poltiglia.', 'Fai attenzione a dove metti i piedi, Venthyr. Ti ritroverai le scarpe piene di poltiglia.', 38134),
(207329, 'itIT', 'Feste, intrighi... Perché i Venthyr non risolvono tutto combattendo?', 'Feste, intrighi... Perché i Venthyr non risolvono tutto combattendo?', 38134),
(207328, 'itIT', 'Sì, i miei giorni sono pieni di guerra... ma i miei ricordi sono solo miei.', 'Sì, i miei giorni sono pieni di guerra... ma i miei ricordi sono solo miei.', 38134),
(207327, 'itIT', 'Qui non troverai nessuna pozza di meditazione, Kyrian.', 'Qui non troverai nessuna pozza di meditazione, Kyrian.', 38134),
(207326, 'itIT', 'Vieni. Mostrami le eleganti mosse di combattimento che hai imparato nel Bastione.', 'Vieni. Mostrami le eleganti mosse di combattimento che hai imparato nel Bastione.', 38134),
(207324, 'itIT', 'Ti inviterei al Teatro del Dolore, ma non vorrei che ti si sporcassero i vestiti.', 'Ti inviterei al Teatro del Dolore, ma non vorrei che ti si sporcassero i vestiti.', 38134),
(207323, 'itIT', 'Quando la sovrastruttura mortale svanisce, possiamo crearne un\'altra.', 'Quando la sovrastruttura mortale svanisce, possiamo crearne un\'altra.', 38134),
(207321, 'itIT', 'Tu incarni il retaggio del Primus, $n.', 'Tu incarni il retaggio del Primus, $n.', 38134),
(207320, 'itIT', 'È un bene che tu ti sia $gschierato:schierata; con noi, $n. Le altre congreghe invidieranno la tua forza.', 'È un bene che tu ti sia $gschierato:schierata; con noi, $n. Le altre congreghe invidieranno la tua forza.', 38134),
(207319, 'itIT', 'Qualunque cosa riservi il destino, me lo sarò guadagnato combattendo.', 'Qualunque cosa riservi il destino, me lo sarò guadagnata combattendo.', 38134),
(207318, 'itIT', 'Il vecchio Griftah nun ha mai visto nu posto del genere!\n\n<Griftah ti saluta con la mano, avvicinandosi per sussurrarti.>\n\nShhh! Abbassa la voce!\n\nChesti Alienatori offrono tante cose a noi \"mortali\" e sto cercando di scoprire cosa sono, cheste cose.\n\nForse il vecchio Griftah potrebbe trovare nuovi amuleti da vendere, eh?', '', 38134),
(207316, 'itIT', 'Ah, un\'altra anima da Azeroth.\n\nNo, non un\'anima. La luce di An\'she scalda ancora la tua carne.\n\nSe solo potessi posare lo sguardo un\'ultima volta su Alto Monte. Se la Madre Terra me lo concedesse... La mia famiglia abita ancora laggiù.\n\nMolti qui sono impazienti di ascendere e liberarsi dai rimpianti del passato, ma io sono felice di potermi godere i ricordi di coloro che amo. Spero che mi ricordino con piacere, anche se un giorno dovrò lasciarli andare.\n\nMa non oggi. No. Non oggi.', '', 38134),
(207315, 'itIT', 'Ti riconosco. Abbiamo combattuto fianco a fianco ad Alto Monte. Sembra che il tempo sia stato clemente con te.\n\nSpero che Kura e i ragazzi non abbiano pianto troppo, e che Kura sia amata. Vorrei conservare qualche ricordo. Quando mi rannicchiavo con Kura, quando chiamavo il piccolo, quando insegnavo a Mycah come muoversi con il coltello come un fantasma. Come un fantasma...\n\nNon ricordo il mio fatale destino. Solo che è successo in fretta. Cosa ne è stato di Dargrul?\n\nNo. No, non dirmelo. Riguarda una vita passata. Questa rabbia, questo dolore... È per questo che i Kyrian ci liberano dal fardello dei nostri ricordi.', '', 38134),
(207314, 'itIT', 'Ti stai godendo il crepuscolo eterno, Venthyr?', 'Ti stai godendo il crepuscolo eterno, Venthyr?', 38134),
(207313, 'itIT', 'Non capirò mai il fascino che la pietra esercita sui Venthyr.', 'Non capirò mai il fascino che la pietra esercita sui Venthyr.', 38134),
(207312, 'itIT', 'Contiamo che tu rimanga vigile, $gNecrosignore:Necrosignora;.', 'Contiamo che tu rimanga vigile, $gNecrosignore:Necrosignora;.', 38134),
(207311, 'itIT', '$gUn alleato:Un\'alleata; del Primus è $gun alleato:un\'alleata; della foresta.', '$gUn alleato:Un\'alleata; del Primus è $gun alleato:un\'alleata; della foresta.', 38134),
(207310, 'itIT', 'Entrambi serviamo le nostre Congreghe come dobbiamo.', '$gEntrambi:Entrambe; serviamo le nostre Congreghe come dobbiamo.', 38134),
(207309, 'itIT', 'Che i venti sostengano le tue ali, Kyrian.', 'Che i venti sostengano le tue ali, Kyrian.', 38134),
(207308, 'itIT', 'La Corte della Notte è fortunata ad averti, $n.', 'La Corte della Notte è fortunata ad averti, $n.', 38134),
(207307, 'itIT', 'Siamo grati di essere alleati della tua Congrega, Calcafauce. Per la Regina e il bosco.', 'Siamo grati di essere alleati della tua Congrega, Calcafauce. Per la Regina e il bosco.', 38134),
(207306, 'itIT', 'Non troverai alcuna anima orgogliosa qui, Venthyr.', 'Non troverai alcuna anima orgogliosa qui, Venthyr.', 38134),
(207305, 'itIT', 'La Caccia Selvaggia è al tuo fianco, $gNecrosignore:Necrosignora;.', 'La Caccia Selvaggia è al tuo fianco, $gNecrosignore:Necrosignora;.', 38134),
(207304, 'itIT', 'Cammina con coraggio, Kyrian.', 'Cammina con coraggio, Kyrian.', 38134),
(207302, 'itIT', 'Che la notte ti abbracci, Venthyr. Anche se di certo già lo fa.', 'Che la notte ti abbracci, Venthyr. Anche se di certo già lo fa.', 38134),
(207301, 'itIT', 'Starai nella foresta a lungo, $gNecrosignore:Necrosignora;?', 'Starai nella foresta a lungo, $gNecrosignore:Necrosignora;?', 38134),
(207300, 'itIT', 'Buona fortuna, Kyrian. Spero che il Bastione abbia resistito bene alla siccità.', 'Buona fortuna, Kyrian. Spero che il Bastione abbia resistito bene alla siccità.', 38134),
(207299, 'itIT', 'I boschi sono in debito con te, $n!', 'I boschi sono in debito con te, $n!', 38134),
(207298, 'itIT', 'Strano trovarti qui, Venthyr. Troppo strano, anzi.', 'Strano trovarti qui, Venthyr. Troppo strano, anzi.', 38134),
(207297, 'itIT', 'Cos\'è questo odoraccio? Oh, $gun Necrosignore:una Necrosignora;. Ovvio.', 'Cos\'è questo odoraccio? Oh, $gun Necrosignore:una Necrosignora;. Ovvio.', 38134),
(207295, 'itIT', 'Oh, tu sei $guno:una; dei Kyrian! Senza ali, però... non come me!', 'Oh, tu sei $guno:una; dei Kyrian! Senza ali, però... non come me!', 38134),
(207294, 'itIT', 'Oh, ecco $n! Mi è piaciuto tantissimo il tuo spettacolo!', 'Oh, ecco $n! Mi è piaciuto tantissimo il tuo spettacolo!', 38134),
(207289, 'itIT', 'E pensare che mi mancava meno di un anno per ottenere il mio elmetto...\n\n<L\'Ammiraglio Taylor sospira, anche se in realtà non avrebbe più bisogno nemmeno di respirare.>', '', 38134),
(207288, 'itIT', 'Prego.', '', 38134),
(207287, 'itIT', 'Ricordo che sei stato tu a uccidermi.', '', 38134),
(207286, 'itIT', 'E tu cosa dici, Koltira? Cosa ti ricordi della tua sconfitta?', '', 38134),
(207285, 'itIT', 'È inutile fare ipotesi. Ciò che è successo non può essere cambiato. Possiamo solo andare avanti, inesorabili come la morte stessa.', '', 38134),
(207284, 'itIT', 'Capisco. Quale destino avrebbe potuto scegliere l\'Arbiter per noi?', '', 38134),
(207283, 'itIT', 'Se l\'hanno fatto, non me lo ricordo.', '', 38134),
(207282, 'itIT', 'Ricordi qualcosa dal momento della tua morte, Thassarian? I Kyrian sono venuti a prendere la tua anima?', '', 38134),
(207281, 'itIT', 'Ho afferrato.', '', 38134),
(207280, 'itIT', 'O Sylvanas Ventolesto.', '', 38134),
(207279, 'itIT', 'Come Genn Mantogrigio, intendi?', '', 38134),
(207278, 'itIT', 'Bolvar Domadraghi non è più il Re dei Lich. Se proprio dobbiamo prendere ordini da qualcuno, mi vengono in mente comandanti molto peggiori che potrebbero darceli.', '', 38134),
(207277, 'itIT', 'E ora le nostre lame sono al servizio di colui che lo indossava.', '', 38134),
(207276, 'itIT', 'L\'Elmo del Dominio è stato infranto. Strano pensarci. Per così tanto tempo è stato il simbolo della nostra maledizione e del nostro dovere.', '', 38134),
(207272, 'itIT', 'Sai come sono morto, $n? È successo mentre difendevo Quel\'Thalas, sotto il comando del Generale dei Guardaboschi Sylvanas Ventolesto.\n\nHa dato la vita per salvare il suo regno e la sua gente, e per colpa dei suoi sforzi è stata fatta rinascere nella Non Morte. Proprio come me. Ma poi le nostre strade si sono divise.\n\nSylvanas ha causato più perdite e sofferenza di tutti noi. Mi ha imprigionato nelle profondità di Sepulcra per anni, dopo che avevo tentato di riportare la situazione alla normalità con una tregua temporanea.\n\nNemmeno la Spada d\'Ebano è riuscita a negoziare il mio rilascio. Si possono dire molte cose sulla Regina Banshee... ma non ha mai ceduto, nella vita o nella morte.\n\nSylvanas non sarà una nemica facile da sconfiggere. Non lo è mai stata.', '', 38134),
(207271, 'itIT', 'La prima volta che ho impugnato la spada, è stata quando la Piaga della Non Morte travolse Lordaeron. Negli anni seguenti, ho servito da soldato nella morte più a lungo di quanto abbia fatto in vita.\n\nLe guerre dei vivi sembrano preoccupazioni trascurabili, in confronto alle forze che ho visto affrontarsi oltre il velo. La mia lealtà è al servizio dell\'Alleanza e io combatterò per i miei concittadini finché non cadrò per un\'ultima volta. Ma ci sono minacce ben più grandi per la loro esistenza dell\'Orda.\n\nForse ora capiscono, con i cieli infranti sopra la Corona di Ghiaccio e la morte che li osserva dall\'alto. Questa è la maledizione dei vivi.\n\nNon capiscono cosa conti davvero, finché non è troppo tardi.', '', 38134),
(207270, 'itIT', 'Vorrei un costrutto un po\' massiccio! Quando salta, il terreno dovrebbe spaccarsi sotto il peso dell\'impatto.', '', 38134),
(207269, 'itIT', 'Voglio una struttura forte, con molti ganci cui appende roba. Porterò te e tutti i materiali delle mie ricerche sulle nostre numerose avventure insieme!', '', 38134),
(207268, 'itIT', '', '<Philia ti sussurra.>\n\nTi prego, non dire a Emma della Fauce.\n\nNon... Non penso che il suo cuore lo reggerebbe.', 38134),
(207266, 'itIT', '', 'Oh, salve. Sembra che tu sappia come muoverti da queste parti.\n\nHai visto i miei figli, Jem, Jack e Jake? Dovrebbero essere qui da qualche parte.\n\nNon me ne andrò finché non li avrò trovati. Non li perderò di nuovo!', 38134),
(207251, 'itIT', 'Rendo onore a coloro che servono la nostra causa.', 'Rendo onore a coloro che servono la nostra causa.', 38134),
(207250, 'itIT', '<Stanikos ti ferma velocemente.>\n\nTu non le usi. Io... prendo in prestito!', '<Stanikos ti ferma velocemente.>\n\nTu non le usi. Io... prendo in prestito!', 38134),
(207248, 'itIT', '<Stanikos ti fissa.>\n\nLe tue cose. Le cose che hai toccato. Sì.', '<Stanikos ti fissa.>\n\nLe tue cose. Le cose che hai toccato. Sì.', 38134),
(207247, 'itIT', 'Intendi... la mia collezione? Ti piace, sì?', 'Intendi... la mia collezione? Ti piace, sì?', 38134),
(207241, 'itIT', 'No! Non puoi nasconderti, Ve\'nari! Non... AAAH!', '', 38134),
(207231, 'itIT', '', 'Che?', 38134),
(207230, 'itIT', 'Ho imparato che le azioni valgono più delle parole, Calcafauce. Qui dobbiamo entrambi dimostrare le nostre intenzioni.', '', 38134),
(207229, 'itIT', 'Quando cadono i capi, i seguaci li seguono... Gli inquisitori non fanno eccezione.', '', 38134),
(207228, 'itIT', '', 'La via di Gahji conduce a molti abissi oscuri... Cosa ci troverai?', 38134),
(207227, 'itIT', '', 'I vostri regni e le vostre tribù non hanno potere qui. Rinunciate alle vecchie alleanze e siatene liberi!', 38134),
(207226, 'itIT', '', 'Qui la tua forza mortale non significa nulla. Suona la campana e scopri quanto sei debole, in realtà.', 38134),
(207225, 'itIT', '', 'Accogliete la vergogna. Affrontate ogni ricordo orribile che pesa su di voi. Resistetevi, così che possiate superarlo.', 38134),
(207224, 'itIT', '', 'Volenti o nolenti, le vostre iniquità saranno rimproverate. Potete scegliere se imparare o meno dall\'esperienza.', 38134),
(207223, 'itIT', '', 'CHE?!?', 38134),
(207222, 'itIT', '', 'Che?', 38134),
(207221, 'itIT', 'Sì, sospetto che tu sia $gsorpreso:sorpresa; di trovare uno come me tra i tiranni e i nobili che spesso occupano le gabbie di Revendreth.\n\nUn\'anima silenziosa, facile da ignorare.\n\nInfatti, le mie gesta non sono state nulla di che.\n\nLe mie parole, tuttavia, sono tutt\'altra questione.\n\nTramite i miei sussurri, ho convinto innumerevoli altri a compiere atrocità in mio nome, per i miei fini e a loro discapito.\n\nSembra che i Venthyr non vedano di buon occhio macchinazioni del genere.', '', 38134),
(207220, 'itIT', 'Cosa desidera chiedere $gil leggendario:la leggendaria; Calcafauce a un\'umile anima come me?', '', 38134),
(207219, 'itIT', '<Werimu ride.>\n\nCosa non ho fatto, mortale?\n\nNon sono ancora convinto che tutto ciò che questi Venthyr chiamano peccato sia sbagliato, ma anche quelli su cui concordiamo al momento sono abbastanza da riempire da cima a fondo la mia Pietra del Peccato.\n\n<Werimu si perde tra sé e sé per un momento.>\n\nIo... sono grato per la pazienza che la Curatrice Pancha ha con me. Ho visto il modo in cui gli altri trattano le anime sotto la loro custodia.\n\nSto DAVVERO cercando di migliorare.', '', 38134),
(207218, 'itIT', 'Ti sei mai $gavventurato da solo:avventurata da sola; nella terra dei morti, mortale? Dovresti fare più attenzione...\n\nUna volta credevo che non servisse avere amici al mio fianco. Credevo fossero un punto debole da sfruttare o per fare leva contro di me.\n\nIn queste terre ho imparato esattamente quanto mi sbagliavo.', '', 38134),
(207213, 'itIT', '', 'Gahji rubò cose... cose perdute... cose dimenticate...\n\nNon mancano a nessuno... finché...\n\nGahji esagerato... dimenticato regole che non vanno dimenticate...\n\nGahiji fatto errori...', 38134),
(207212, 'itIT', '', 'Gahji vorrebbe essere libera, come mortale...\n\nScappa da qui... Scappa dal passato...', 38134),
(207210, 'itIT', 'A volte mi pento di aver fatto quel Demonio di Pietra...', '', 38134),
(207209, 'itIT', 'E ti ripeto: non esiste il titolo \"Alfiere delle Pietre del Peccato\".', '', 38134),
(207208, 'itIT', 'Non è una gara, Lemet...', '', 38134),
(207207, 'itIT', 'Lo vedremo... l\'Alfiere delle Pietre del Peccato vince ancora!', '', 38134),
(207206, 'itIT', 'Vorrei vederlo fare al vecchio Temel. Ecco perché sono l\'Alfiere delle Pietre del Peccato!', '', 38134),
(207205, 'itIT', 'Ho trovato una pletora di frammenti di Pietre del Peccato per te questa settimana, padrone!\n\nApri la cassa e osserva.', '', 38134),
(207203, 'itIT', '', 'Suonare una campana rituale significa sacrificarsi.\n\nOgni campana richiede qualcosa dall\'anima che la suona. Usate in modo scorretto, come fanno i degenerati in queste sale, rendono questi sacrifici un futile tormento.\n\nLa saggezza degli Affermati consiste nell\'identificare cosa deve sacrificare ogni anima per procedere nel lungo viaggio verso la redenzione, e nel convincere quell\'anima a fare il sacrificio necessario, per il suo stesso bene.\n\nNon è sempre facile, alcune anime sono troppo testarde o poco lungimiranti per suonare una campana rituale.\n\nMa tu sei in grado di vedere la verità: sacrificarsi per fare progressi significa non sacrificarsi affatto.', 38134),
(207202, 'itIT', '', 'Per il momento, senza delle anime pronte per un rituale, non abbiamo bisogno di una campana.\n\nFane e gli altri archivisti degli Affermati tengono le campane nascoste finché non servono, per tenerle al sicuro da eventuali manomissioni dei ritualisti degenerati che infestano queste sale.\n\nQuando avremo un\'anima pronta, farò portare qui la campana apposita dai Dragatori. Dubito che ci vorrà molto.', 38134),
(207201, 'itIT', '', '<La Curatrice Pancha sogghigna.>\n\nPensavo che questa campana potrebbe interessarti in modo particolare, Calcafauce. Credo che potrebbe rivelarsi... utile per te, dovessi tornare a Revendreth, un giorno.\n\nLa Campana della Rinuncia aiuta coloro che cercano di liberarsi dalle alleanze e dai vincoli delle loro vite mortali. La tua Alleanza, la tua Orda, i tuoi regni... non esistono qui e non hanno più alcun senso per te.\n\nSuona la campana e liberati dalle catene che ti legano.', 38134),
(207200, 'itIT', '', 'Ah, la mia preferita in assoluto: la Campana della Sottomissione.\n\nTi chiede solo di concedere una parte del tuo potere.\n\nSemplice, sì, ma rappresenta una proposta terribile per un\'anima orgogliosa che ha raggiunto il successo con la violenza e il combattimento.\n\nDopo che l\'anima ha ceduto il suo potere, è libera di combattere come desidera.\n\nMa, essendo indebolita, subisce una sconfitta dopo l\'altra finché non si spezza e diventa disposta ad arrendersi ai suoi inferiori.\n\nSolo allora il rituale giunge al termine.', 38134),
(207199, 'itIT', '', 'Provi facilmente empatia per gli altri, Calcafauce?\n\nNon tutti ci riescono. La Campana della Vergogna aiuta chi non è in grado o chi rifiuta di provare empatia a capire le conseguenze delle proprie azioni passate, mettendole a nudo e affrontandole.\n\nPorta alla luce le gesta più vergognose, perché tutti le vedano. Le anime vendicative nelle cripte vengono poi scatenate per un certo tempo perché si scaglino sugli umiliati, facendo affrontare loro le conseguenze delle proprie azioni.\n\nÈ difficile da sopportare per gli empatici, ma d\'altronde loro non ne hanno bisogno.', 38134),
(207198, 'itIT', '', 'La Campana della Predica è uno dei nostri strumenti più punitivi, riservata alle anime più testarde che necessitano di essere ricordate costantemente che non sono più in controllo.\n\nSuonando la campana, rimproveri anzitutto te stesso, ma raramente questo è lo scopo principale. Abbiamo strumenti migliori per l\'introspezione.\n\nBen più importante, ti rendi vulnerabile alle prediche altrui, fornendo ai Venthyr una maggiore leva su di te.\n\nOvviamente, la degenerazione dei Venthyr in queste sale ha reso il tutto più pericoloso. Supervisiono molto da vicino le mie anime mentre sono sotto effetto di questa campana.', 38134),
(207197, 'itIT', '', 'La Campana della Confessione è uno strumento semplice ma potente per aiutare le anime a fare i conti con le proprie colpe.\n\nSolo chi desidera confessare un peccato può suonarla, e così facendo emana la confessione dell\'anima a tutti i presenti nelle sale.\n\nAmmettere pubblicamente la propria colpa aiuta l\'anima e fare i conti coi propri fallimenti, ma avverte pure i Venthyr degenerati in queste sale della loro presenza.', 38134),
(207196, 'itIT', '', 'Molte al momento sono confinate nelle cripte, mentre si preparano per il prossimo rituale.\n\nNon preoccuparti, le ho tenute lontane dai seguaci degenerati del Ciambellano Supremo. Le anime saranno al sicuro mentre affronteranno le colpe delle loro trasgressioni.\n\nL\'Accusatrice stessa ha difeso le cripte dalla manomissione.', 38134),
(207195, 'itIT', '', 'Molto lentamente, se il passato è un indizio del futuro.\n\nLa propensione alla violenza di Ick, abbinata al suo odio sfrenato per gli archivi di conoscenza collettiva, l\'ha lasciata con molti peccati da cui redimersi e ben pochi strumenti per poterlo fare.\n\nPer ora, la sto tenendo in riga con una rigida tabella di confinamento solitario e dei compiti semplici da comunicare pensati per insegnarle il valore di ciò che in vita ha congedato troppo rapidamente.\n\nOggi, raccoglierà frammenti perduti di Pietre del Peccato per aiutare gli archivisti.', 38134),
(207194, 'itIT', '', 'Perché non glielo chiedi tu $gstesso:stessa;? Khongordzolo è stato sotto le mie cure per un bel po\' di tempo, ma ha finalmente padroneggiato l\'uso della lingua e ora è possibile fidarsi delle sue parole.\n\nSolo dopo essermi fatta strada nella sua selva di bugie e mezze verità ho potuto iniziare a costringerlo ad affrontare le conseguenze delle sue azioni nei regni dei mortali.\n\nOra, deve ricostruire la sua reputazione tramite le parole e i fatti. Come tutti noi, no?', 38134),
(207193, 'itIT', '', 'Werimu ancora non ha affettato la precarietà della sua situazione!\n\nHa fatto ammenda per molte cose, ma quando era appena arrivato, i fardelli sulla sua anima erano mostruosi. Non avevo mai visto qualcosa del genere.\n\nNon sarà al sicuro dalle grinfie della Fauce finché ogni suo peccato non sarà stato espiato.\n\nAl momento sto cercando di tenerlo concentrato assegnandogli un compito a lui familiare: smantellare un\'organizzazione rivale.', 38134),
(207192, 'itIT', '', 'Gahiji si è dimostrata essere una sfida difficile.\n\nPer quanto abbia fatto in fretta ad ammettere i propri errori, è particolarmente lenta nel provare rimorso per essi.\n\nTemo che resterà con noi per molto tempo.\n\nTuttavia, è felice di rendersi utile mentre è qui. Spesso la incarico di purificare le Cripte della Redenzione dal lerciume dei Venthyr degenerati.', 38134),
(207191, 'itIT', '', 'Yevkek è più avanti lungo il percorso rispetto a molte anime in mia custodia.\n\nDopo aver passato un\'intera vita a usare la sua forza e il suo spirito per sfruttare i più deboli della società, ha imparato a usare queste stesse abilità per proteggere e salvare gli altri.\n\nQuando un\'altra delle mie anime si perde o cerca di sfuggire alla giustizia, affido a Yevkek il compito di trovarle e riportarle lungo la retta via.', 38134),
(207190, 'itIT', '', 'Persino i Dragatori sono vittime dei degenerati che vagano per queste sale.\n\nCostretti a lavorare e a servire i seguaci corrotti del Ciambellano Supremo, rinnegano i loro veri compiti.\n\nQuesta campana rituale giace in pezzi sul suo piedistallo nonostante ne abbiamo disperatamente bisogno per facilitare la redenzione delle anime!', 38134),
(207189, 'itIT', '', 'Con la campana ripristinata, possiamo continuare a guidare quest\'anima lungo il percorso della redenzione.\n\nPotresti suonarla e aiutarmi a mostrarle che il sacrificio è l\'unico vero modo per redimersi?\n\nSe non fossimo disposti a renderci umili e affrontare i nostri errori, come potremmo sperare di superarli?', 38134),
(207175, 'itIT', '', 'Il percorso di redenzione è diverso per ogni anima, ma ci sono punti in comune tra tutte.\n\nIl sacrificio e la sofferenza sono mezzi necessari per un degno fine.\n\nAvendo come alternativa la Fauce, può forse esistere un prezzo che non vale la pena pagare?', 38134),
(207168, 'itIT', 'Il mio cammino è alla ricerca dei deboli, per salvare coloro che un tempo sfruttavo. Cammina nella pietà, mortale.', '', 38134),
(207167, 'itIT', '', 'Dopo che i Dragatori ripareranno la Campana della Confessione, TU la suonerai.', 38134),
(207163, 'itIT', 'Sono stato molte cose...\n\nIl lupo che preda il gregge del pastore.\nLo squalo che nuota attorno alla zattera.\nIl falco che dà la caccia alla lepre.\n\nChi si perse non fu mai al sicuro da me.', '', 38134),
(207161, 'itIT', 'Sei ancora in tempo, mortale.\n\nCambia i tuoi metodi. Evita il mio destino.\n\nQuesto percorso dei Venthyr potrebbe essere la salvezza, ma è...\n\n<L\'anima trema fino ad ammutolirsi.>', '', 38134),
(207154, 'itIT', 'Perché sei qui, eh? Nun vedi che nun mi è rimasto altro?\n\nGli Elfi hanno sottratto la terra al popolo mio. Poi m\'hanno preso l\'occhio... poi la vita.\n\nTutti vogliono sempre prendere da noi, ma noi nun ci arrendiamo! E nun dimentichiamo.\n\nNu giorno, presto, sarai tu a perdere... tutto!', '', 38134),
(207152, 'itIT', '', 'Nessuno è sopravvissuto, a parte me.\n\nLa Fauce, come ben sai, è un luogo pericoloso. E per quanto viaggiare con un ampio gruppo possa fornire l\'illusione della sicurezza, la realtà è che ti rende un bersaglio più facile.\n\nL\'abbiamo imparato con le cattive. I nostri numeri sono stati decimati finché non siamo rimasti abbastanza pochi da poterci nascondere. Così abbiamo rallentato la caccia... ma non l\'abbiamo fermata.\n\nQuando sono rimasta sola, ho accettato la realtà della mia situazione. La solitudine mi ha dato la migliore possibilità di sopravvivere.\n\nAlmeno fino al tuo arrivo.', 38134),
(207149, 'itIT', '', 'È un peccato che debbano soffrire un tale destino, ma non capire male. Sono nostri nemici tanto quanto i Giurafauce.\n\nQuegli Alienatori erano condannati sin da quando sono arrivati. E anche se li salvassi, non faresti che attirare la rovina su di noi.\n\nÈ un modo di pensare spietato, lo ammetto, ma è necessario per sopravvivere nella Fauce.\n\nFaresti bene a ricordartelo.', 38134),
(207143, 'itIT', '', 'La Custode Casad ti attende nel Vivaio della Regina! Non vede l\'ora di cominciare con te.\n\nPuoi entrare nell\'Anello di Funghi accanto a me per raggiungere il Vivaio.', 38134),
(207142, 'itIT', '', 'Quando ti resta solo il tempo dinanzi a te in quest\'oscurità infinita, le storie valgono più dell\'oro.', 38134),
(207119, 'itIT', '', 'Oh, immagino vi conosciate piuttosto bene ormai. Non è possibile per un $gun:una; mortale come te sopravvivere a un viaggio nella Fauce senza il suo aiuto.\n\nParlami dei tuoi accordi con Ve\'nari.\n\nSe ha scelto di non tornare a Oribos assieme a te, allora anche lei avrà delle domande a cui rispondere.', 38134),
(207118, 'itIT', '', 'Ma certo, ma certo... Il che mi porta all\'ultima domanda.\n\nAffrontando la Fauce, Ve\'nari si è dimostrata essere una tua buona alleata?\n\n<Prima di rispondere, ti ricordi che Ve\'nari ti ha specificamente detto di non rivelare del vostro accordo a nessuno.>', 38134),
(207113, 'itIT', '', 'Ah, dunque rimane un luogo piuttosto pericoloso in cui condurre affari... il che porta alla mia prossima questione.\n\nDomanda numero due:\n\nReputi impegnativo sopravvivere tra gli opprimenti e incessanti orrori della Fauce?', 38134),
(207112, 'itIT', '', 'Apprezzo la tua volontà nell\'illuminarci con la tua conoscenza della Fauce.\n\nDomanda numero uno:\n\nÈ vero che le forze del Carceriere sono cresciute in potenza e numeri?', 38134),
(207111, 'itIT', '', 'Salve, Calcafauce. È un piacere fare la tua conoscenza!', 38134),
(207110, 'itIT', '', 'Che emozione!', 38134),
(207109, 'itIT', '', 'Non li proverò e non li risintonizzerò, così non avremo idea di cosa ne uscirà dopo averli attivati.', 38134),
(207108, 'itIT', '', 'Potrò creare altri Kit per Riparazioni, quando avrai altri Rubini Infusi.', 38134),
(207107, 'itIT', 'Non mancano specchi da riparare a Revendreth, Calcafauce.', '', 38134),
(207105, 'itIT', '', 'Puoi provare a usare il Kit per Riparazioni che ho portato con me dal Distretto delle Braci.', 38134),
(207104, 'itIT', 'C\'è uno specchio rotto dall\'altra parte della sala, se vuoi fare un po\' di pratica.', '', 38134),
(207103, 'itIT', '', 'Oh, ci sono molte cose che ho scelto di non dirti. Così come so che ci sono molte cose che non mi hai detto tu.\n\nLa fiducia si basa sulla discrezione, Calcafauce. Senza di essa, fare affari sarebbe impossibile.\n\nDetto questo, ho affidato a te il mio segreto di maggior valore: la posizione di questo rifugio. In cambio ti chiedo solo di fidarti di me.', 38134),
(207101, 'itIT', '', 'Con il tuo aiuto, Selvarden continuerà a mantenere il ciclo.\n\nAffido a te il mio Vivaio.\n\nParla con il Custode Casad. Ti verranno date ulteriori istruzioni.', 38134),
(207100, 'itIT', '', 'Tutto ha una fine.\n\nEppure, ogni fine è un nuovo inizio. Questo è il ciclo della natura.\n\nGli spiriti nei miei boschi sono vitali per il ciclo. Senza di loro, la natura stessa inizierebbe a morire nel regno dei mortali. Ogni perdita è una tragedia. Ogni spirito salvato, un trionfo.\n\nQuesto spirito è stato salvato da un bosco morente e dalla follia dei Drust. Oggi, si risveglia da un lungo sonno e ritorna nel proprio mondo. Con il tuo aiuto siamo stati all\'altezza della nostra sacra carica.', 38134),
(207097, 'itIT', '', 'Che la Regina dell\'Inverno in persona ti insegni le arti dei Semebrado è davvero un onore.', 38134),
(207087, 'itIT', '', 'C\'è voluta un bel po\' di Stigia... ma suppongo che fare affari abbia un costo.', 38134),
(207065, 'itIT', 'Rispetto alla tua? Naturalmente. Hai rotto il contratto e tradito il cartello! Ora ne pagherai il prezzo!', '', 38134),
(207063, 'itIT', '', 'Le loro urla non sono piacevoli come quelle dei Verrospino, ma vanno bene lo stesso.', 38134),
(207012, 'itIT', '$n? È un sollievo vederti, dopo tutto questo tempo. Soprattutto qui, in questo strano posto.\n\nQuando ho saputo che il velo delle Terretetre era stato squarciato, ho capito che dovevo attraversarlo, a prescindere dai rischi.\n\nCome ricorderai, io... ho perso mia moglie durante il parto e subito dopo mio figlio. Devo sapere se sono al sicuro con i nostri antenati. Non lo sopporterei, se il loro spirito fosse in pericolo.\n\nTi prego, ti scongiuro, se dovessi incontrare l\'anima di Leza, dille che nostro figlio Kor sta crescendo bene. Dille che la amo, sempre.\n\nE che non importa quanto ci vorrà, io la troverò.', '', 38134),
(207011, 'itIT', 'È un sollievo vedere qualcuno di Azeroth, in questo strano posto.\n\nQuando ho saputo che il velo delle Terretetre era stato squarciato, ho capito che dovevo attraversarlo, a prescindere dai rischi.\n\nVedi, io... ho perso due delle persone che avevo più care. Devo sapere se sono al sicuro con i nostri antenati o se il loro spirito è in pericolo.\n\nTi prego, ti scongiuro, se dovessi incontrare l\'anima di mia moglie, Leza Alba Nuova, dille che nostro figlio Kor sta crescendo bene. Dille che la amo, sempre.\n\nE che non importa quanto ci vorrà, io la troverò.', '', 38134),
(207009, 'itIT', '', 'Sto utilizzando uno dei miei dispositivi più interessanti. Temo che per te sarà piuttosto doloroso.', 38134),
(207008, 'itIT', '', 'Vieni, Calcafauce. Credo abbiamo molto di cui discutere.', 38134),
(207007, 'itIT', '', 'No, Ve\'brax. Tu lascerai la Fauce... ma non tornerai mai al cartello.', 38134),
(207006, 'itIT', 'Argh! Che cosa... stai... facendo?!?', '', 38134),
(207005, 'itIT', '', 'Non credo proprio. Calcafauce, mettiamo fine a questa seccatura.', 38134),
(207003, 'itIT', '', 'Pensavi che avrebbero creduto alla parola di un\'assassina?', 38134),
(207002, 'itIT', 'Ho cercato di avvisarti, Calcafauce. Ma non mi hai dato ascolto.', '', 38134),
(207000, 'itIT', 'Ha tradito i suoi vecchi soci, e farà lo stesso con te se saprà che abbiamo parlato. Non fidarti delle sue menzogne.', '', 38134),
(206999, 'itIT', 'Prima di ritirarmi al mio rifugio, ti do un consiglio su Ve\'nari. Lei non è come sembra.', '', 38134),
(206995, 'itIT', 'Siamo venuti qui sperando di rifarci delle perdite del tradimento di Ve\'nari. Oh, quanto siamo stati stupidi...', '', 38134),
(206988, 'itIT', 'Catturati... torturati... spezzati...\n\nXeros... portato gli altri... Antri delle Bestie...\n\nFallito... il compito...', '', 38134),
(206984, 'itIT', 'Gli avevo detto che sarei tornato! E odio deludere le aspettative.', '', 38134),
(206983, 'itIT', 'Sono stato messo in catene da un incantatore malvagio!\n\nLiberami, in modo che possa insegnargli ciò per cui valga la pena vivere... e morire, suppongo.', '', 38134),
(206970, 'itIT', 'Questo pipistrello sembra essere la tua via d\'uscita dalle Sale della Redenzione.', 'Questo pipistrello sembra essere la tua via d\'uscita dalle Sale della Redenzione.', 38134),
(206969, 'itIT', 'Egida dello Spirito Forgiato, scudo protettivo che spacca!\n\nI nemici incombono su di te? Preparano un assalto?\n\nStai al sicuro grazie all\'egida! Assorbi i danni e scagliali indietro ai tuoi avversari! Uuuh-uuuh!', '', 38134),
(206968, 'itIT', 'Anello della Protezione, tiene al sicuro!\n\nDevi stare immobile, pur tenendo i nemici lontani?\n\nSemplice! Attiva l\'anello e i nemici stanno lontani! Riparo sicuro dove riprendere fiato.', '', 38134),
(206967, 'itIT', 'Fiala della Serenità, ristoro delizioso!\n\nTi senti debole? Devi tirarti su?\n\nFiala ti cura e ti fa continuare a combattere! Garantita dai Factotum!', '', 38134),
(206966, 'itIT', 'Fiala di Gelo del Lich, mantiene le distanze!\n\nSopraffatti dagli avversari? Dovete scappare?\n\nNon temete! Basta rompere la fiala per terra, congelare i nemici e poi via di corsa!', '', 38134),
(206965, 'itIT', 'Scusa, $n! L\'equipaggiamento non è per te!\n\nSolo per i tuoi Vincoli dell\'Anima. Li aiuterà nelle prove!', '', 38134),
(206959, 'itIT', 'Uuuh-uuuh! Posso aiutarti con un compito importante, ma solo uno. Mi servirà del tempo per aiutarti di nuovo: quale ora. Quante cose da fare!', 'Uuuh-uuuh! Posso aiutarti con un compito importante, ma solo uno. Mi servirà del tempo per aiutarti di nuovo: quale ora. Quante cose da fare!', 38134),
(206958, 'itIT', 'Come posso esserti uuutile? Posso aiutarti solo con una cosa, quindi scegli attentamente! Mi servirà del tempo per aiutarti di nuovo: un\'ora. Ho molte cose da fare!', 'Come posso esserti uuutile? Posso aiutarti solo con una cosa, quindi scegli attentamente! Mi servirà del tempo per aiutarti di nuovo: un\'ora. Ho molte cose da fare!', 38134),
(206954, 'itIT', 'Ora che me lo dici, ho visto un grosso tipo verde che veniva trascinato qui.\n\nDifficile non vederlo, quello. Urlava di un \"Loc da arar\", un posto da arare, sicuro.\n\nLiberami e ti dirò tutto quello che so.', '', 38134),
(206949, 'itIT', 'Che c\'è? Un mortale? Qui? Devo essere fuori come un balcone!\n\nNon esiste che sei reale.', '', 38134),
(206948, 'itIT', 'Eccoli, i Volumi sulla Vittoria! Vuoi avere tu l\'onore, $n? Sai com\'è, in caso ci sia una trappola mortale o altro.', '', 38134),
(206944, 'itIT', 'Oh, tantissimo! Mi sono occupato di Marasmio per così tanto tempo, conosco addirittura alcuni vecchi percorsi di cui si è scordato persino lui!$b$bQualcosa mi dice che conosci anche tu quei percorsi nascosti . Se mai volessi tornare in uno di essi ti aiuterò ad arrivarci, se ti farà piacere.', 'Oh, tantissimo! Mi sono occupato di Marasmio per così tanto tempo, conosco addirittura alcuni vecchi percorsi di cui si è scordato persino lui!$b$bQualcosa mi dice che conosci anche tu quei percorsi nascosti . Se mai volessi tornare in uno di essi ti aiuterò ad arrivarci, se ti farà piacere.', 38134),
(206941, 'itIT', 'Tu... non ti riconosco. Come sei $garrivato:arrivata; fin qui? I giovani d\'oggi...$b$bNon so bene come tu abbia fatto, ma ti sei $gmesso:messa; un bel po\' nei guai, eh? Ti riporterò a Selvarden, stavolta. Solo perché sono vecchio e a nessuno importa più di me non vuol dire che non aiuterò qualcuno nei guai.', '', 38134),
(206933, 'itIT', '', 'Il tuo spirito è ricolmo d\'Orgoglio, mortale. Posso aiutarti a fare ammenda?', 38134),
(206931, 'itIT', 'Là dovrebbe soffrire di meno.', '', 38134),
(206930, 'itIT', 'Il Principe Renathal ha dato alla Naaru l\'autorità sulla torre di Fortechiaro. È il luogo dove la sua gente è comparsa la prima volta ed è perlopiù satura di Luce.', '', 38134),
(206929, 'itIT', 'Z\'rali è troppo debole per tornare a casa, e la sua stessa natura rappresenta un pericolo per qualunque Venthyr in sua presenza.', '', 38134),
(206912, 'itIT', 'Spinta Naturale è un\'abilità passiva che ti permette di Sbalzare nella direzione in cui ti stai muovendo mentre sei in forma di Animorfosi, anziché nella direzione in cui stai guardando.', 'Spinta Naturale è un\'abilità passiva che ti permette di Sbalzare nella direzione in cui ti stai muovendo mentre sei in forma di Animorfosi, anziché nella direzione in cui stai guardando.', 38134),
(206908, 'itIT', 'Puoi trovare il Maestro del Conflitto, e i suoi vari amici mercanti, a fianco dell\'Enclave.', 'Puoi trovare il Maestro del Conflitto, e i suoi vari amici mercanti, a fianco dell\'Enclave.', 38134),
(206897, 'itIT', 'Non sono che un\'eco della conoscenza dell\'Eletta caduta, ma vedo chiaramente in te il desiderio di conoscenza.\n\nLeggi la mia storia e potremo discutere di ciò che imparerai.', 'Non sono che un\'eco della conoscenza dell\'Eletta caduta, ma vedo chiaramente in te il desiderio di conoscenza.\n\nLeggi la mia storia e potremo discutere di ciò che imparerai.', 38134),
(206896, 'itIT', 'Hai trovato Kobri! Grazie!', 'Hai trovato Kobri! Grazie!', 38134),
(206895, 'itIT', 'Kobri è andato a prendere vino. Kobri ci sta mettendo tanto. Non iniziamo senza vino!\n\nTrova Kobri, va bene?', 'Kobri è andato a prendere vino. Kobri ci sta mettendo tanto. Non iniziamo senza vino!\n\nTrova Kobri, va bene?', 38134),
(206889, 'itIT', 'Per la Madre Terra!', '', 38134),
(206886, 'itIT', 'Che lo Scopo ti guidi.\n\nIo conservo molti dei nostri registri. Preserviamo le visioni che i fili contorti del fato ci rivelano al servizio dello Scopo. \n\nCosa posso rivelarti?', 'Che lo Scopo ti guidi.\n\nIo conservo molti dei nostri registri. Preserviamo le visioni che i fili contorti del fato ci rivelano al servizio dello Scopo. \n\nCosa posso rivelarti?', 38134),
(206878, 'itIT', '$gBenvenuto:Benvenuta;. Per quale via posso assisterti?', '', 38134),
(206833, 'itIT', '', 'Non dirai a nessuno della mia esistenza. Tutto ciò che fai qui sono affari unicamente tuoi.\n\nTi darò un Custode delle Anime, un manufatto capace di conservare le anime trovate nella Fauce.\n\nAccettare il Custode delle Anime e queste regole significa che ora siamo dei soci in affari.\n\nMi darai gratuitamente ogni informazione preziosa che troverai nella Fauce.\n\nIn cambio, io ti permetterò di comprare degli strumenti per aiutarti a sopravvivere in questo regno e salvare le anime che cerchi.\n\nAbbiamo un accordo?', 38134),
(206827, 'itIT', 'Vuoi suonare musica? Con questi?', 'Vuoi suonare musica? Con questi?', 38134),
(206726, 'itIT', '<Scegline uno.>', '', 38134),
(206725, 'itIT', '<Spegni e riaccendi.>', '', 38134),
(206719, 'itIT', 'Fare ammenda per le volgarità e cambiare il proprio comportamento ha sempre un costo. Tuttavia, per far davvero rigare dritto le persone volgari, devono rendersi conto che è meglio ritenerli responsabili della loro vile parlata.', '', 38134),
(206713, 'itIT', 'La Calcapeccati Blanchy urla in preda all\'angoscia e poi sparisce dalla vista.', '', 38134),
(206708, 'itIT', 'In assenza di Re Anduin, mi è stata affidata la protezione dei cittadini dell\'Alleanza.\n\nMi sembrano passati secoli da quando ho aiutato un giovane Varian Wrynn a stabilirsi sul trono. Ora sono stato incaricato di tenerlo al sicuro per suo figlio.\n\nLa Quarta Guerra ha richiesto un prezzo altissimo per l\'Alleanza. La nostra forza militare si è esaurita. I nobili di Roccavento desiderano vedere le nostre difese ristabilite.\n\nRivendicare Stromgarde è stato un ottimo primo passo. Forse ci sono altri vecchi terreni dell\'Alleanza che possono essere recuperati.', '', 38134),
(206707, 'itIT', 'L\'Orda sta affrontando prove e pericoli assai difficili.\n\nSono fiero di essere na voce al concilio che ci guiderà lungo la strada.\n\nVol\'jin è stato il capoguerra migliore che abbia mai conosciuto... e nu buono amico. Posso solo sperare che onorino il suo ricordo nun facendo scordare all\'Orda i sacrifici che ha fatto per noi.', '', 38134),
(206706, 'itIT', '', 'Abbiamo trascorso mesi a cercare qualsiasi traccia di Sylvanas in ogni angolo di Azeroth, solo per scoprire che mia sorella è andata nei regni della morte.\n\n<Alleria assottiglia gli occhi.>\n\nChe ironia.\n\nAnche se desidero darle la caccia, ora il mio posto è qui. L\'Alleanza deve ricostruire la sua forza, e per questo aiuterò Turalyon.\n\nMa quando verrà il momento, farò in modo che Sylvanas risponda per ciò che ha fatto.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206705, 'itIT', 'La strada è aperta, ora puoi percorrerla per andare al Maldraxxus.', '', 38134),
(206704, 'itIT', 'Le strade stanno riaprendo. Lo Scopo ti ha mandato da noi per questo, forse?', '', 38134),
(206703, 'itIT', 'La strada è aperta, ora puoi percorrerla per andare a Selvarden.', '', 38134),
(206701, 'itIT', 'La strada è aperta, ora puoi percorrerla per andare a Revendreth.', '', 38134),
(206692, 'itIT', 'Benvenuto, Kah-Sher. È vero? Stiamo aprendo di nuovo i varchi?', '', 38134),
(206686, 'itIT', '', 'La sua natura instabile infligge danni tremendi, ma può affliggere un solo nemico alla volta.', 38134),
(206685, 'itIT', 'La sua natura instabile infligge danni tremendi, ma può affliggere un solo nemico alla volta.', '', 38134),
(206684, 'itIT', 'Afflizione Instabile genera un frammento d\'anima dai nemici, quando questi muoiono, quindi assicurati che l\'effetto sia attivo.', '', 38134),
(206683, 'itIT', '', 'Afflizione Instabile genera un frammento d\'anima dai nemici, quando questi muoiono, quindi assicurati che l\'effetto sia attivo.', 38134),
(206682, 'itIT', '', 'Afflizione Instabile è un potente incantesimo che infligge danni periodici.', 38134),
(206681, 'itIT', 'Afflizione Instabile è un potente incantesimo che infligge danni periodici.', '', 38134),
(206679, 'itIT', '<Quando è attivato da una Scheggia Sintonizzata, questo dispositivo può mostrare dei percorsi nascosti all\'interno della torre.>', '', 38134),
(206677, 'itIT', 'Sarà fatto, Calcafauce.', '', 38134),
(206668, 'itIT', '', 'La Città Eterna è splendente e meravigliosa. Tuttavia, il tocco della morte aleggia ovunque su di essa.\n\nAnche io sono stata toccata da quel potere e forse ci sono dei segreti qui a Oribos che possono aiutarmi a capirne il significato.\n\n Gli Attendenti parlano con sicurezza del loro \"Scopo\". Forse mi aiuteranno a trovare il mio.', 38134),
(206667, 'itIT', '', 'Le Terretetre. È notevole... E se devo essere onesta, anche un po\' terrificante.\n\nMa non lascerò che la paura mi impedisca di aiutare i miei amici.\n\nO di conoscere il padre che pensavo di aver perso.', 38134),
(206666, 'itIT', 'Sappiamo che il Carceriere ci osserva e che ha Anduin.\n\nLa nostra unica speranza è che tu e gli altri eroi rendiate forti le congreghe, $n.\n\nArriverà presto il giorno in cui il Carceriere e Sylvanas riveleranno la prossima fase del loro piano.\n\nQuando lo faranno, servirà tutta la nostra forza per sconfiggerli.', '', 38134),
(206629, 'itIT', 'Lo fa, lo fa, dev\'essere di moda tra chi non ha le ali.', 'Lo fa, lo fa, dev\'essere di moda tra chi non ha le ali.', 38134),
(206628, 'itIT', 'Salti parecchio, mortale. Senza le ali non deve far bene alle tue articolazioni.', 'Salti parecchio, mortale. Senza le ali non deve far bene alle tue articolazioni.', 38134),
(206627, 'itIT', 'Ci scommetto, ma posso semplicemente continuare...', 'Ci scommetto, ma posso semplicemente continuare...', 38134),
(206626, 'itIT', 'Sai... salti davvero tanto.', 'Sai... salti davvero tanto.', 38134),
(206625, 'itIT', 'Ed è per quello, perché non lo rimandi a domani?', 'Ed è per quello, perché non lo rimandi a domani?', 38134),
(206624, 'itIT', 'Aiuto? Mortale, andiamo a depredare qualcosa di bello per te.', 'Aiuto? Mortale, andiamo a depredare qualcosa di bello per te.', 38134),
(206623, 'itIT', 'Questa... questa pietra! Ti porterà alla rovina, mortale!', 'Questa... questa pietra! Ti porterà alla rovina, mortale!', 38134),
(206622, 'itIT', 'Non lasciare che t\'induca in tentazione!', 'Non lasciare che t\'induca in tentazione!', 38134),
(206621, 'itIT', 'Non è mai troppo tardi per imboccare un cammino di rettitudine!', 'Non è mai troppo tardi per imboccare un cammino di rettitudine!', 38134),
(206620, 'itIT', 'Chi aiuterai oggi, mortale?', 'Chi aiuterai oggi, mortale?', 38134),
(206619, 'itIT', 'Non dargli ascolto. Il tuo gruppo è imbattibile se fa gioco di squadra!', 'Non dargli ascolto. Il tuo gruppo è imbattibile se fa gioco di squadra!', 38134),
(206618, 'itIT', 'Con la tua protezione, i tuoi alleati andranno lontano!', 'Con la tua protezione, i tuoi alleati andranno lontano!', 38134),
(206617, 'itIT', 'Che la tua arma colpisca solo che si merita giustizia!', 'Che la tua arma colpisca solo che si merita giustizia!', 38134),
(206616, 'itIT', 'Un combattimento virtuoso. Che conoscano una morte onorevole!', 'Un combattimento virtuoso. Che conoscano una morte onorevole!', 38134),
(206615, 'itIT', 'Molto probabilmente è stata un\'incomprensione...', 'Molto probabilmente è stata un\'incomprensione...', 38134),
(206614, 'itIT', 'Il conflitto non è la risposta. Hai provato a parlare con loro?', 'Il conflitto non è la risposta. Hai provato a parlare con loro?', 38134),
(206613, 'itIT', 'Cos\'è un peccato in più? Forza!', 'Cos\'è un peccato in più? Forza!', 38134),
(206612, 'itIT', 'In che razza di guai possiamo cacciarci?', 'In che razza di guai possiamo cacciarci?', 38134),
(206611, 'itIT', 'Pensa a tutti gli incantesimi che avresti potuto far piovere su di loro se non avessi sprecato quello!', 'Pensa a tutti gli incantesimi che avresti potuto far piovere su di loro se non avessi sprecato quello!', 38134),
(206610, 'itIT', 'Sai che con tutta probabilità non avrebbero fatto lo stesso per te...', 'Sai che con tutta probabilità non avrebbero fatto lo stesso per te...', 38134),
(206609, 'itIT', 'Ben fatto, ah! Li hai schiacciati!', 'Ben fatto, ah! Li hai schiacciati!', 38134),
(206608, 'itIT', 'Sì! Abbattili! Ora quello laggiù!', 'Sì! Abbattili! Ora quello laggiù!', 38134),
(206607, 'itIT', 'Parla! Parla con la tua arma!', 'Parla! Parla con la tua arma!', 38134),
(206606, 'itIT', 'Non puoi fargliela passare liscia. Prendilo!', 'Non puoi fargliela passare liscia. Prendilo!', 38134),
(206605, 'itIT', 'Adonas fa una smorfia dal dolore mentre si tiene il fianco.', '', 38134),
(206604, 'itIT', 'Ti prego, Calcafauce... Mi servono... delle cure...', '', 38134),
(206603, 'itIT', 'Mi farà male per un po\', ma credo di poter volare nuovamente... Grazie.', '', 38134),
(206590, 'itIT', 'Pergamene, pergamene, pergamene.\n\nOh, uuh, salve!', 'Pergamene, pergamene, pergamene.\n\nOh, uuh, salve!', 38134),
(206588, 'itIT', 'Diverse pergamene mancano dagli scaffali! Trovi pergamene scomparse? Sì?', 'Diverse pergamene mancano dagli scaffali! Trovi pergamene scomparse? Sì?', 38134),
(206579, 'itIT', 'Abbiamo poco tempo e dobbiamo colpire Denathrius prima che scopra della mia presenza.\n\nMa in questo momento sarebbe una follia farlo. Io sono ancora ferito dalla Fauce e tu non sei ancora abbastanza forte per sopravvivere a ciò che ho in mente.\n\nVai, Calcafauce e fai crescere il tuo potere. Scopri le Terretetre. Fa\' ciò che devi per acquisire più esperienza di questo posto. Torna da me quando sarai $gpronto:pronta; ad affrontare uno dei signori della morte in persona.', '', 38134),
(206576, 'itIT', 'Puoi trovare i portali all\'approdo fuori dalla città.', 'Puoi trovare i portali all\'approdo fuori dalla città.', 38134),
(206575, 'itIT', 'Puoi trovare tutti gli Alienatori che offrono servizi di artigianato nella Sala delle Forme.', 'Puoi trovare tutti gli Alienatori che offrono servizi di artigianato nella Sala delle Forme.', 38134),
(206571, 'itIT', 'La foresta provvede.', 'La foresta provvede.', 38134),
(206564, 'itIT', 'Il fato e il tempo sono intrecciati. Si possono piegare le loro vie, riplasmare addirittura, se lo Scopo lo desidera.\n\nIl tocco di un grande potere aleggia sulla tua anima, mortale. Attraverso la sua influenza, io posso cambiare il tuo fato... E offrirti una via.\n\nSe dovessi scegliere di percorrerla, gli abitanti delle Terretetre ti guarderanno come fanno con gli altri che si sono già avventurati nei loro regni.\n\nOgnuna delle congreghe ti darà il benvenuto, desiderose che ti unisca ai loro ranghi. Qualunque sia la tua scelta, le aiuterai a crescere più forti.\n\nMa se scegli questa strada, non si può tornare indietro.\n\nLa decisione è tua.', '', 38134),
(206562, 'itIT', 'Certo che ho accettato di diventare il suo Vincolo dell\'Anima. È la mia adorata compagna, colei con cui non vorrei altro se non passare il resto della mia eternità assieme.', 'Certo che ho accettato di diventare il suo Vincolo dell\'Anima. È la mia adorata compagna, colei con cui non vorrei altro se non passare il resto della mia eternità assieme.', 38134),
(206561, 'itIT', 'Grazie ancora per il tuo aiuto.\n\n<Sorride dolcemente voltandosi alla sua destra.>\n\nHa detto sì.', 'Grazie ancora per il tuo aiuto.\n\n<Sorride dolcemente voltandosi alla sua destra.>\n\nHa detto sì.', 38134),
(206560, 'itIT', 'Le darò risposta direttamente. Grazie di tutto. Sono certo che tu capisca quanto sia importante questo momento.', 'Le darò risposta direttamente. Grazie di tutto. Sono certo che tu capisca quanto sia importante questo momento.', 38134),
(206559, 'itIT', 'Mi sta chiedendo di diventare il suo Vincolo dell\'Anima, $n.', 'Mi sta chiedendo di diventare il suo Vincolo dell\'Anima, $n.', 38134),
(206558, 'itIT', 'Una ghirlanda, quando viene data da qualcuno cui vuoi bene, è un regalo simbolico.', 'Una ghirlanda, quando viene data da qualcuno cui vuoi bene, è un regalo simbolico.', 38134),
(206551, 'itIT', 'Ci sono due vie da percorrere, $n.\n\nIniziare il tuo viaggio come altri mortali hanno fatto prima di te. Nessuno saprà il tuo fato, il tuo ruolo nello Scopo.\n\nO lasciare che i fili del tempo e del destino ti tirino avanti. Sarai $gricordato:ricordata; dalle congreghe e potrai scegliere a chi essere $gdevoto:devota;.\n\nI regni della Morte si apriranno dinanzi a te e il tuo fato sarà nelle tue mani.\n\nPrendi la tua decisione.', 'Hai due opzioni, $n.\n\nPosso tenere le cose come sono adesso. Tutti saranno sorpresi che tu sia qui, incontrerai di nuovo i tuoi nuovi amici.\n\nOppure, ti faccio saltare in avanti nel tempo.\n\nPotrai unirti direttamente a una Congrega, e poi avventurarti ovunque nelle Terretetre!\n\nSei $gpronto:pronta; a fare la tua scelta?', 38134),
(206550, 'itIT', 'Il fato chiama. Lo Scopo ha bisogno di te.', '', 38134),
(206539, 'itIT', 'Gli Alienatori di Oribos hanno permesso ai nostri alleati della Roccaforte d\'Ebano di mantenere un portale tra il loro regno e il nostro. Puoi trovarlo nella Stanza dei Portali, in cima alla Torre della Magia.', 'Gli Alienatori di Oribos hanno permesso ai nostri alleati della Roccaforte d\'Ebano di mantenere un portale tra il loro regno e il nostro. Puoi trovarlo nella Stanza dei Portali, in cima alla Torre della Magia.', 38134),
(206534, 'itIT', '', 'Quando sei in dubbio, massacra tutti quanti.', 38134),
(206532, 'itIT', '', 'Hai idea di quanti rivali ho ucciso?', 38134),
(206530, 'itIT', 'Anche se non è stato facile, alcuni dei nostri alleati della Roccaforte d\'Ebano hanno lavorato con gli Alienatori di Oribos per aprire un varco tra i nostri regni. Lo troverai al livello principale della nostra Stanza dei Portali.', 'Anche se non è stato facile, alcuni dei nostri alleati della Roccaforte d\'Ebano hanno lavorato con gli Alienatori di Oribos per aprire un varco tra i nostri regni. Lo troverai al livello principale della nostra Stanza dei Portali.', 38134),
(206528, 'itIT', '', 'Questo posto mi fa scorrere un brivido lungo la schiena. La mia pelle pizzica, costantemente tesa. Il mio corpo vuole tirarsi indietro a ogni suono ultraterreno.\n\nSono risoluta. Sono un\'Ascesa del Bastione. È mio dovere cercare coloro che rappresentano una minaccia non solo per le nostre vie, ma per tutte le Terretetre.', 38134),
(206527, 'itIT', 'Normalmente gli Aspiranti non vengono inviati in missioni di tale importanza.\n\nDevo ammetterlo, trovarmi fuori dal Bastione così presto mi innervosisce. Ma con te e Kleia al mio fianco, credo che imparerò molto da questo viaggio.', '', 38134),
(206526, 'itIT', '', 'Giuro di essere una rappresentante adeguata del Bastione e delle nostre tradizioni.\n\nI Venthyr e i Kyrian presentano molte differenze, ma penso che si possano trovare dei punti in comune.', 38134),
(206525, 'itIT', '', 'La Contessa tiene d\'occhio ogni nobile di Revendreth, passato e presente.\n\nPer lei, le informazioni sono potere. Non lo condivide facilmente.', 38134),
(206524, 'itIT', '', 'Ehilà, $n!\n\nViaggiare nel tempo è piuttosto pericoloso. L\'avventura ti attenderà quando avrai accumulato un po\' più d\'esperienza.', 38134),
(206523, 'itIT', '', 'Tutto ciò che facciamo non è solo per servire l\'Arconte, ma per servire le genti di innumerevoli mondi sparsi nella realtà.', 38134),
(206522, 'itIT', '', 'Ehilà, $n!\n\nSe sei qui per i Viaggi nel Tempo, temo che dovrai conoscere meglio il presente prima di poter visitare il passato.\n\nForse potrò mandarti indietro quando avrai girato per la città e incontrato il Capoguerra!', 38134),
(206521, 'itIT', '', '<Gli occhi di Kleia sono chiusi, il volto visibilmente preoccupato.>\n\n<Il suo respiro è intenso e profondo mentre processa ciò che ha appena percepito.>', 38134),
(206520, 'itIT', '', 'Azeroth è stupenda. Un netto e umile contrasto rispetto al Bastione. Non vedo l\'ora di scoprire come vive la gente di questo mondo.', 38134),
(206519, 'itIT', '', 'Mi sono addestrato instancabilmente per eoni per arrivare a oggi.\n\nSolo un altro incarico. Un\'altra prova.\n\nDopodiché il tempo della mia ascensione sarà finalmente giunto.', 38134),
(206512, 'itIT', '<L\'Archivista Janeera non ti guarda negli occhi mentre continua a leggere la pergamena di fronte a lei.>\n\nSì?', '<L\'Archivista Janeera non ti guarda negli occhi mentre continua a leggere la pergamena di fronte a lei.>\n\nSì?', 38134),
(206511, 'itIT', 'Sì, be\', portami i frammenti di Pietra del Peccato ogni volta che vuoi cacciarne un altro, $p.', 'Sì, be\', portami i frammenti di Pietra del Peccato ogni volta che vuoi cacciarne un altro, $p.', 38134),
(206510, 'itIT', 'Ti assicuro che ti sono grato per i tuoi sforzi nel liberarci dagli Inquisitori del Ciambellano Supremo.', 'Ti assicuro che ti sono grata per i tuoi sforzi nel liberarci dagli Inquisitori del Ciambellano Supremo.', 38134),
(206509, 'itIT', 'Mi dispiace. Mi sento dire spesso che do l\'impressione di essere freddo e ingrato.', 'Mi dispiace. Mi sento dire spesso che do l\'impressione di essere fredda e ingrata.', 38134),
(206508, 'itIT', 'L\'Archivista Fane tossisce rumorosamente.', 'L\'Archivista Fane tossisce rumorosamente.', 38134),
(206507, 'itIT', 'L\'Archivista Fane nelle Sale della Redenzione può sempre creare una Pietra del Peccato dell\'Inquisitore... se hai i frammenti.', '', 38134),
(206505, 'itIT', 'Non con certezza, no.\n\nHai controllato gli alberi nei pressi del villaggio? Avevo visto gli Aspiranti leggere là, prima che i dragoni dell\'etere vi si trasferissero.', 'Non con certezza, no.\n\nHai controllato gli alberi nei pressi del villaggio? Avevo visto gli Aspiranti leggere là, prima che i dragoni dell\'etere vi si trasferissero.', 38134),
(206496, 'itIT', 'Tubbins fa sempre parti difficili. Cercare acqua facile!', '', 38134),
(206493, 'itIT', 'L\'acqua che cerchi è più avanti...\n\nSe hai problemi a raggiungerla, cercami là.\n\nNon sono mai molto distante... e spesso sono disposto ad aiutare...', '', 38134),
(206492, 'itIT', 'Addio, giovane. Ricorda di lavorare sodo... e rispettare gli anziani...', '', 38134),
(206491, 'itIT', 'Trattieni il fiato... e lo stomaco...\n\nE via...', '', 38134),
(206490, 'itIT', 'Posso riportarti sul continente dopo che l\'avrai recuperata.', '', 38134),
(206489, 'itIT', 'L\'acqua che cerchi è più avanti.', '', 38134),
(206488, 'itIT', 'Preparati... Viaggiare nella mia Rete Miceliare è... disorientante, la prima volta.', '', 38134),
(206487, 'itIT', 'Mostrami qualcosa della tua natura... Sei $gun buon lavoratore:una buona lavoratrice;?', '', 38134),
(206486, 'itIT', 'Non così in fretta, giovane... Non permetto agli stranieri di viaggiare lungo la mia Rete Miceliare a casaccio.', '', 38134),
(206483, 'itIT', 'Ho osservato il tuo duro lavoro tramite la mia Rete Miceliare.\n\nOvunque io venga visto, posso vedere... e tu mi vedi dappertutto.\n\nTu SEI $gun buon lavoratore:una buona lavoratrice;.', '', 38134),
(206482, 'itIT', 'Voi giovani, sempre in movimento, sempre a fare rumore...\n\nQual è la causa di questa baraonda stavolta...', '', 38134),
(206479, 'itIT', 'Riposo, tutti quanti. Presto la caccia avrà di nuovo inizio.', '', 38134),
(206478, 'itIT', 'Infatti.', '', 38134),
(206477, 'itIT', '', 'E non c\'è nessuno più forte della Caccia Selvaggia!', 38134),
(206476, 'itIT', '', 'Unite, le radici crescono più forti.', 38134),
(206475, 'itIT', '', 'Ho imparato molto con questo pattugliamento. È stato un onore cacciare al vostro fianco.', 38134),
(206474, 'itIT', '', 'È stata una caccia degna. Le tue abilità stanno migliorando, Lumera.', 38134),
(206473, 'itIT', 'Divoratore!', '', 38134),
(206472, 'itIT', '', 'È quello che mi preoccupa.', 38134),
(206471, 'itIT', '', 'No, per niente...', 38134),
(206470, 'itIT', '', 'Aspetta. Hai sentito?', 38134),
(206469, 'itIT', 'E ora li incontriamo in ogni angolo della foresta. Sono tempi difficili.', '', 38134),
(206468, 'itIT', '', 'Le storie sono vere. Anche se è passato molto tempo da quando la Caccia Selvaggia li ha incontrati apertamente in battaglia.', 38134),
(206467, 'itIT', '', 'Una volta pensavo che i Drust fossero un mito... delle storie raccontate per spaventare i giovani Silfi.', 38134),
(206466, 'itIT', '', 'Te lo sei meritato, Sika. Divertiti.', 38134),
(206465, 'itIT', '', 'Si pentiranno di aver messo piede a Selvarden!', 38134),
(206464, 'itIT', 'I Drust! Caccia Selvaggia, alle armi!', '', 38134),
(206463, 'itIT', '', 'Sika... ha amici gentili. Sika... va a festa!', 38134),
(206462, 'itIT', '', 'Sika sembra incerta per un momento, ma poi un grosso sorriso le solca il volto.', 38134),
(206461, 'itIT', '', 'Vai a rilassarti e goderti la Corte delle Braci con $p. Ci occuperemo noi di tutto quanto finché sei via.', 38134),
(206460, 'itIT', 'Hai fatto tanto per noi. Lascia che ti aiutiamo noi stavolta.', '', 38134),
(206459, 'itIT', 'Sì, Sika. Non avevamo idea che ti sentissi così sotto pressione.', '', 38134),
(206458, 'itIT', 'Parli già come una vera cacciatrice.', '', 38134),
(206457, 'itIT', '', 'Non permetteremo che accada. La Caccia Selvaggia proteggerà la foresta dalla siccità e la carestia.', 38134),
(206456, 'itIT', 'Se questa siccità non finirà presto, tutta Selvarden condividerà il destino di Tirna Falcis.', '', 38134),
(206454, 'itIT', '<Il Centurione sembra aver esaurito l\'Animum.>', '', 38134),
(206453, 'itIT', '', 'E adesso ne arrivano altri!', 38134),
(206452, 'itIT', '', 'Lacrime di pioggia per Tirna Falcis. Così poco Animum, così tanti gorm.', 38134),
(206445, 'itIT', 'Hai bisogno di qualcosa, Calcafauce?\n\nHo tempo prima che sia il mio turno di guidare queste anime.', 'Hai bisogno di qualcosa, Calcafauce?\n\nHo tempo prima che sia il mio turno di guidare queste anime.', 38134),
(206444, 'itIT', 'Leithfor annuisce solennemente.', '', 38134),
(206443, 'itIT', '', 'Non ci sono rifugiati a Tirna Falcis. L\'abbiamo saputo tutti nel modo peggiore.', 38134),
(206442, 'itIT', '', 'Un\'illusione... e io ci sono cascata.', 38134),
(206441, 'itIT', 'Lumera, no! Stai indietro!', '', 38134),
(206440, 'itIT', '', 'Attenzione, dei rifugiati! Hanno bisogno del nostro aiuto!', 38134),
(206439, 'itIT', '', 'Non vedo l\'ora di partecipare alla sua corte.', 38134),
(206438, 'itIT', '', 'Avresti potuto scegliere di combattere il più debole tra loro. Invece hai scelto la più forte.', 38134),
(206437, 'itIT', '', 'Per attirare un alleato come te, il Principe Renathal deve avere molto da offrire.', 38134),
(206436, 'itIT', 'Ora siamo migliori amici, $p. Giusto?\n\nQuindi quante volte devo chiederti di contrabbandare alcuni dei Dragatori della tua Congrega qui a Maldraxxus?\n\nCerto, sono riuscito a liquefarne alcuni alla corte mentre non guardavi, ma immagina gli esperimenti che potrei condurre su di loro avendo accesso al mio intero laboratorio.\n\nAvrei potuto rispondere alla domanda che è nella mente di tutti: i Dragatori sono fatti di melma?\n\nSembra certamente possibile!', '<Sika finisce di organizzare dei fogli mentre ti parla.>\n\n$p! Sei una bella sorpresa.\n\nLa maggior parte delle volte, le sorprese sono brutte. Ostacolano piani. Causano altro lavoro.\n\nSika pensa alla prossima Corte delle Braci. Spero che Sika sia invitata.\n\nUn sacco da fare, ma migliore amico aiuta sempre Sika.\n\nSika ha $gun buon:una buona; migliore $gamico:amica;.', 38134),
(206435, 'itIT', 'Ecco $gil mio vecchio:la mia vecchia; ex apprendista $gdiventato:diventata; $gorganizzatore:organizzatrice; di corti!\n\nNon vedevo l\'ora di condividere alcune idee che ho avuto per la prossima Corte delle Braci.\n\nTi avverto: tutte prevedono una quantità smodata di melma.\n\nAlcune delle melme, tuttavia, sono deliziose!', '', 38134),
(206434, 'itIT', 'La direzione della tua carriera mi turba, $p.\n\nNon ho mai visto $gun :un\';ex apprendista andare a organizzare a corti.\n\nPiuttosto anormale...\n\nKevin sembra preso dall\'idea, comunque, per cui suppongo che non ostacolerò il progresso.\n\nQuando sarebbe la prossima Corte delle Braci?', '', 38134),
(206433, 'itIT', 'È $p! Kevin, metti via la pozione sperimentale... per ora.', '', 38134),
(206432, 'itIT', 'Fai attenzione a non mettere un piede nel mio ultimo esperimento, $p. Non vorrei proprio perderti.', '', 38134),
(206431, 'itIT', 'È il suono di $gun :un\';apprendista $gingenuo:ingenua; che si avvicina quello che sento?', '', 38134),
(206430, 'itIT', '$p, stavo giusto parlando di te e del SECONDO miglior intrattenimento nelle Terretetre ad alcuni degli altri.\n\nQuando sarà la prossima Corte delle Braci, comunque? Ho promesso ad alcuni dei potenziali clienti che avrei permesso loro di venirci con me.', '', 38134),
(206429, 'itIT', '$p, $gvecchio mio:vecchia mia;. Resta a osservare i combattimenti con me.\n\nQualunque sia il motivo per cui sei qui, me ne occuperò io.\n\nNon c\'è bisogno di girovagare per tutte Maldraxxus quando hai un amico come Vole.', '', 38134),
(206428, 'itIT', 'Come può competere la tua Corte delle Braci con uno spettacolo del genere, Calcafauce?\n\nLa tua prossima corte avrà forse Azmogal o il grande Urtz a calpestare la torta?\n\n<Vole indica l\'enorme Gigante Fuso.>\n\nNon ti consiglio di provare. Dubito che i fragili Venthyr possano gestirlo.', '', 38134),
(206427, 'itIT', 'Vieni qui e osserva lo spettacolo, $p. Ti ho tenuto un posto vicino a me.', '', 38134),
(206426, 'itIT', 'Fate spazio, abbiamo $gun campione:una campionessa; in arrivo. $p!', '', 38134),
(206425, 'itIT', 'Chi sarà il prossimo? Magari tu, Calcafauce?', '', 38134),
(206424, 'itIT', 'Non c\'è vergogna nel pagare. Non tutti sono tagliati per l\'arena.', '', 38134),
(206423, 'itIT', '$p. Ogni volta che ci incontriamo mi ricordo la mia vecchia vita su Azeroth, senza eccezioni.\n\nQuando tutto questo sarà finito e staremo celebrando le nostre vittorie a un banchetto, ti farò delle domande.\n\nMa per ora dobbiamo rimanere concentrati sulla missione.\n\nC\'è troppo in gioco per distogliere lo sguardo dal nemico, anche solo per un istante.', '', 38134),
(206422, 'itIT', 'La tua esperienza qui nelle Terretetre è la prima volta in cui sei a capo di qualcosa, $p?\n\nNe dubito visto come te la cavi. Essere capo è un fardello che pochi gestiscono correttamente la prima volta.\n\nHo sentito gli altri ospiti chiacchierare alla corte, scontenti di alcune scelte che hai fatto. Come se tutti i problemi delle Terretetre girassero attorno ai loro piagnistei.\n\nNon lasciare che le loro parole ti scoraggino.\n\nTraccia una via per la vittoria e conducici. Prima o poi capiranno.', '', 38134),
(206421, 'itIT', 'Guarda i soldati sotto di noi, Calcafauce.\n\nForti, saldi e dediti.\n\nMa non basta. Una forza militare di qualsiasi dimensione deve essere anche saggia, astuta e d\'ispirazione.\n\nNon vedo l\'ora di scoprire se il Principe Renathal ha queste caratteristiche e, se le ha, come le usa.\n\nQuando sarà la tua prossima Corte delle Braci? Farò in modo di essere libero per partecipare, così potrò parlare con il Principe.', '', 38134),
(206420, 'itIT', 'Ah, $p. Come sta $gil mio:la mia; Calcafauce $gpreferito:preferita; oggi?', '', 38134),
(206419, 'itIT', 'Qualche novità sulle battaglie nelle Terretetre, $gamico:amica;?', '', 38134),
(206418, 'itIT', 'Avvicinati liberamente, Calcafauce.', '', 38134),
(206417, 'itIT', 'Io mi farò accompagnare dal Secutor Mevix e due dei nostri soldati migliori. Confido che tu sia in grado di sistemare anche loro.', '', 38134),
(206413, 'itIT', '', 'Hai sempre fretta, $gmio piccolo:mia piccola; $p.\n\nNon è una lamentela. Anzi, mi piace dire questo particolare aspetto della nostra relazione.\n\nNon farmi perdere tempo e io non sprecherò i tuoi sforzi.\n\nQuindi, cosa vuoi da me in questa particolare occasione?\n\nRiconoscenza? Assassinio? Consigli per pianificare la corte?\n\nLa Matrona delle Spie è a tua disposizione.', 38134),
(206411, 'itIT', '', 'Ho visto molti principi partecipare alle corti in passato, Calcafauce.\n\nÈ più probabile che vada male piuttosto che bene, anche nelle migliori circostanze.\n\nIl tuo Renathal farebbe bene ad ascoltare le critiche ben più che le lusinghe.\n\nLe Corti presentano sempre entrambe. Ti sei $gchiesto:chiesta; da che parte starò io?\n\n<La Baronessa Vashj sogghigna.>', 38134),
(206410, 'itIT', 'Riposo, sentinelle. $p è $gun mio amico:una mia amica;.', '$p... $gUno:Una; dei pochi che sono felice di vedere nel nostro accampamento di questi tempi.', 38134),
(206409, 'itIT', 'Va tutto bene a Requie dell\'Eroe, $p. $gBentornato:Bentornata;.', 'Ah, $gil maestro:la maestra; delle corti torna al nostro angolo di Maldraxxus.', 38134),
(206408, 'itIT', 'Salve, Calcafauce.', 'Sembra che i tuoi occhi avessero ragione, Khaliiq. $gIl:La; Calcafauce si avvicina.', 38134),
(206407, 'itIT', 'Servo se ce n\'è bisogno, e qui hanno bisogno di me.', 'Fai attenzione a chi fai la corte, $p... Non vuoi finire in combutta con un mostro.', 38134),
(206406, 'itIT', '', 'La foresta asseconda.', 38134),
(206405, 'itIT', 'Già. La domanda è chi potrebbe essere.', '', 38134),
(206404, 'itIT', '', 'Gli Spriggan di solito non attaccano con tale coordinazione. Qualcuno li sta guidando.', 38134),
(206403, 'itIT', 'Perché non stai portando questa lettera al Polemarco Adrestes?', '', 38134),
(206402, 'itIT', 'Attenzione!', '', 38134),
(206401, 'itIT', '', 'Spriggan! Perfetti per il tiro al bersaglio! Peccato che ce ne siano così pochi...', 38134),
(206400, 'itIT', '', 'Ignora Zarkin, Lumera. Dà più valore ai fatti che alle parole.', 38134),
(206399, 'itIT', '', 'Esatto. Quindi basta solo scatenare un po\' di distruzione sui nostri nemici!', 38134),
(206398, 'itIT', 'Leithfor annuisce in silenzio.', '', 38134),
(206396, 'itIT', 'Questo lo vedremo. Leithfor, va\' avanti.', '', 38134),
(206395, 'itIT', '', 'Sono all\'altezza del lavoro, signore. Non esiste una sfida troppo ardita.', 38134),
(206394, 'itIT', 'Sta\' in guardia, Lumera. Questo è un pattugliamento pericoloso per una cacciatrice novizia.', '', 38134),
(206393, 'itIT', 'Non avevo idea che le corti potessero essere così affascinanti, $p. Davvero, la Corte delle Braci è un\'impresa incredibile da parte tua.\n\nUnire le Terretetre, una tazza di tè alla volta!\n\nChiaramente, è molto più di questo. Ma comunque... Chi l\'avrebbe mai detto?', '', 38134),
(206392, 'itIT', 'Hai bisogno di un posto dove restare e riposarti un po\', Calcafauce?\n\nNon puoi salvare la Terretetre se sei $gesausto:esausta;.', '', 38134),
(206391, 'itIT', 'Se hai un momento, $p, vorrei dire qualcosa.\n\n<Pelagos prende fiato.>\n\nCi sono rimasto male quando hai scelto di essere $gun campione:una campionessa; di Revendreth nella nostra lotta comune per salvare le Terretetre. Credevo che avresti scelto di combattere al fianco mio e alla Congrega dei Kyrian.\n\nMa ora capisco perché hai scelto i Venthyr. La tua Corte delle Braci sta riunendo tutte le Terretetre, com\'era una volta, e come dovrebbe essere.\n\nTutto ciò, per dire che sono felice che siamo diventati amici. Potremmo non essere Vincoli dell\'Anima, ma sento che siamo legati in altro modo.', '', 38134),
(206390, 'itIT', '', '$gAmico mio:Amica mia;! Sono felice che tu abbia tempo per parlare.\n\nQuanto tempo è passato? Troppo, in ogni caso.\n\nDimmi tutto quello che hai fatto e visto dall\'ultima volta che ci siamo incontrati. Hai bisogno di me o di Pelagos?\n\n<Kleia riprende fiato e sorride.>\n\nChe piacere rivederti, $n.', 38134),
(206389, 'itIT', '', 'È bello rivederti qui tra le anime del Bastione, $p.\n\nLe tue Corti delle Braci mi hanno dimostrato la tua dedizione alle Terretetre, anche se hai scelto i Venthyr come mezzo primario.', 38134),
(206388, 'itIT', '', 'Per caso le Terretetre hanno bisogno di me, Calcafauce?\n\nSono pronta a servire.', 38134),
(206387, 'itIT', 'Ma soprattutto, desideri compagni lungo strada?', '', 38134),
(206386, 'itIT', '', '$p! Dove ti stai dirigendo, $gamico:amica;?', 38134),
(206384, 'itIT', '', 'Il potere del Carceriere cresce, e le Terretetre affrontano un inesorabile pericolo. Resta in guardia, ci sarà bisogno della tua forza nei giorni a venire.', 38134),
(206383, 'itIT', '', 'Ovunque si trovi $p, quello è il posto più sicuro delle Terretetre.', 38134),
(206382, 'itIT', 'Fai attenzione là fuori, $p.', '', 38134),
(206381, 'itIT', '', 'Ce lo dirà se vorrà farcelo sapere.', 38134),
(206380, 'itIT', 'Chissà dove sta andando $gil:la; Calcafauce...', '', 38134),
(206378, 'itIT', 'Se non l\'ho detto prima, voglio assicurarmi che tu sappia quando ti sono grato, $p.\n\nMi hai insegnato molte cose sulla natura del servizio, del sacrificio, e...\n\n<Il Polemarco Adrestes fa sì che nessun Aspirante possa sentire prima di finire la frase.>\n\nDell\'importanza di divertirsi di tanto in tanto.\n\nPotrei essere stato inizialmente riluttante a partecipare alle tue Corti delle Braci, ma ora non ne vedo l\'ora.\n\nPer l\'Arconte, ovviamente!', '', 38134),
(206377, 'itIT', 'È bello vederti al Bastione ancora una volta, $p.\n\nSpesso mi chiedo come sarebbero state le cose se avessi scelto di accogliere del tutto l\'Arconte come ho fatto io.\n\nTuttavia, è chiaro che il tuo servizio a Oblio del Peccato sia di gran valore per tutte le Terretetre.\n\nConfido che vada tutto bene sotto il tuo controllo.', '', 38134),
(206376, 'itIT', 'Cosa ti porta a Requie dell\'Eroe, Calcafauce?\n\nSe hai bisogno di assistenza militare, io e le sentinelle siamo pronti a servire.\n\nO forse porti notizie da Oblio del Peccato? Posso farle arrivare in cima alla gerarchia di comando.', '', 38134),
(206374, 'itIT', 'Riposo, sentinelle. $p è $gun mio amico:una mia amica;.', '', 38134),
(206373, 'itIT', 'Va tutto bene a Requie dell\'Eroe, $p. $gBentornato:Bentornata;.', '', 38134),
(206372, 'itIT', 'Salve, Calcafauce.', '', 38134),
(206371, 'itIT', 'Servo se ce n\'è bisogno, e qui hanno bisogno di me.', '', 38134),
(206367, 'itIT', '', '$p ha bisogno di qualcosa? La metto in cima alla mia lista!', 38134),
(206366, 'itIT', '', 'A Sika piace quando $p fa visita!', 38134),
(206365, 'itIT', '', 'Anche $gil:la; Calcafauce vuole qualcosa? Aggiungi alla mia lista.', 38134),
(206364, 'itIT', '', 'Molto triste. La Corte delle Braci è divertente. Forse la prossima volta...', 38134),
(206363, 'itIT', '<Mikanikos controlla attentamente i pezzi della tua armatura mentre borbotta tra sé e sé.>\n\nMolto astuto... Molto nuovo... Molto intrigante...\n\nLe tue visite sono sempre ricche di avvenimenti, Calcafauce.', '', 38134),
(206362, 'itIT', 'Sempre spazio per $p nella mia officina.', '', 38134),
(206361, 'itIT', '<Mikanikos mette da parte il suo progetto e ti dà la sua piena attenzione.>\n\n$p, $gil mio visitatore:la mia visitatrice; dell\'officina $gpreferito:preferita;.\n\nSempre nuove idee. Sempre $gun buon amico:una buona amica;.\n\nIl lavoro aspetterà. Dimmi, $gamico:amica;, perché vieni alla Forgia Eterna?\n\nVedermi? Serve qualcosa? Nuova corte a breve? Spero!', '', 38134),
(206360, 'itIT', '$p! Quale nuova ispirazione porti oggi?\n\nCombattuto nuovi nemici? Scoperte nuove tecnologie? Padroneggiato nuove magie?\n\nDimmi tutto.\n\nI piccoli dettagli spesso portano a grandi rivelazioni!', '', 38134),
(206359, 'itIT', 'Ogni parte è perfetta per qualcosa, ma questa volta ho qualcosa di molto particolare!', '', 38134),
(206358, 'itIT', '$gAmico:Amica;! Corte delle Braci molto interessante... Ci vorrà ancora presto!', '', 38134),
(206357, 'itIT', 'Molto da imparare... Molto da provare...', 'Sarai sempre $gil benvenuto:la benvenuta; nel mio bosco, Calcafauce.', 38134),
(206355, 'itIT', '', 'Lo so.', 38134),
(206354, 'itIT', 'Se lo dici tu. Ti credo. Davvero. Solo, non mi sento abbastanza veloce.', '', 38134),
(206353, 'itIT', '', 'So che potrebbe non sembrarti, ma vedo che stai affrontando più facilmente questi ricordi. Stai andando bene, Pelagos.', 38134),
(206352, 'itIT', 'Vorrei tanto credere che sto migliorando, Kleia.', '', 38134),
(206344, 'itIT', 'Se trovi volantini con nuovi tagli per capelli, posso far crescere i miei capelli in altro modo, o magari se trovi un cappello che mi sta posso indossarlo!', '', 38134),
(206343, 'itIT', 'Puoi trovare tinture che possiamo usare vicino alle pozze di fango o al Mercato Notturno.', '', 38134),
(206338, 'itIT', 'I nostri costrutti adorano pavoneggiarsi sul campo di battaglia. Scoprirai che esiste una vasta gamma di accessori di stile che possono essere ottenuti per migliorare il loro aspetto in battaglia.\n\nDovrai cercare in lungo e in largo per trovare gli ingredienti e creare alcuni di questi accessori di stile.', '', 38134),
(206337, 'itIT', 'I Pezzi Superiori sono una componente chiave per costruire potenti costrutti da combattimento e oggetti.\n\nQuesti pezzi sono difficili da trovare, ma i costrutti qui in questo posto ti aiuteranno a trovarli, se li aiuterai coi loro problemi.', '', 38134),
(206336, 'itIT', 'La Carne Malleabile è l\'ingrediente base utilizzato nella maggior parte dei costrutti e oggetti creati con le Cuciture Abominevoli.\n\nHai la possibilità di trovare un pezzo adatto di Carne Malleabile in pressoché qualsiasi creatura sconfitta nelle Terretetre.\n\nAnche Accordino è molto bravo a trovare la Carne Malleabile, quindi non avere paura di chiedere il suo aiuto per trovare questo ingrediente.', '', 38134),
(206325, 'itIT', 'L\'arte delle cuciture abominevoli richiede una mano salda, ingredienti di qualità e naturalmente il Tavolo delle Cuciture Abominevoli, quello laggiù.\n\nHo annotato i giusti ingredienti di cui avrai bisogno per ogni costrutto e oggetto che potremo costruire con il Tavolo delle Cuciture Abominevoli.', '', 38134),
(206322, 'itIT', 'Oh, eccome se si arrabbieranno se si perdono la festa perché non erano qui!', '', 38134),
(206321, 'itIT', 'Ehi, non so se te l\'ho mai detto, ma grazie per aver sempre fatto partecipare anche i miei amici alla Corte delle Braci.\n\nSiamo un gruppo compatto, restiamo sempre uniti.\n\nPer di più, Celontra non la finirebbe più di lamentarsi se andassi a queste feste epiche senza di lui!\n\n<Chufa ride.>\n\nMa ehi, potrei rimpiazzarlo con te, giusto? Ora sei uno di noi.', '', 38134),
(206320, 'itIT', 'Grandi corti per grandi feste. Ci godiamo la vita, tu e io... e Rury, Grufo e anche Celontra.\n\nA proposito, quando sarà la prossima Corte delle Braci? Ho un po\' di tempo al momento. Vuoi andarci prima, stappare un paio di bottiglie e dare inizio a un pre-serata?\n\nNah, vedo che sei $goccupato:occupata;. Hai cose da fare, va bene.\n\nAssicurati solo di invitarmi alla prossima festa, d\'accordo?', '', 38134),
(206319, 'itIT', 'Ehi, $gamico:amica;! Hai qualche fuoco d\'artificio che ti è avanzato dalla corte?', '', 38134),
(206318, 'itIT', 'Ehi, $p! Quand\'è la prossima festa?', '', 38134),
(206317, 'itIT', 'Ora dov\'è finito Rury...?', '', 38134),
(206316, 'itIT', '', 'Non mi fiderò sempre degli Alienatori... ma non posso neanche sempre resistere alle loro merci.', 38134),
(206315, 'itIT', '', 'Il dolore e la perdita sono insegnanti potenti, e io ne sono stata allieva troppo spesso.^n^nTuttavia, ciò mi ha fatto apprezzare i tuoi inviti alla Corte delle Braci ancora di più.^n^nI miei viaggi sono stati... illuminanti, in diversi modi. Il tuo Principe mi ha convinta. Credo che desideri il bene nostro e di tutti gli altri regni.^n^nGrazie, $p. Attenderò la nostra prossima corte insieme.', 38134),
(206314, 'itIT', '', 'Mi sono goduta il nostro viaggio finora, $p, e sento che siano solo i primi passi nella lunga strada della nostra amicizia.^n^nMi hai già insegnato a considerare molte nuove idee e possibilità. Spero di averti aperto anch\'io gli occhi per qualche nuova idea.^n^nSo che non puoi sederti con me a lungo. Tale è la via $gdel:della; Calcafauce.^n^nNon rimanere neanche lontano troppo tempo. Il mio boschetto non sarebbe lo stesso senza di te.', 38134),
(206313, 'itIT', '', 'È bello rivederti, $p. Vai di fretta?', 38134),
(206312, 'itIT', '', 'Cosa ti porta nella mia tenuta, $gamico:amica;?', 38134),
(206311, 'itIT', '', 'Sarai sempre $gil benvenuto:la benvenuta; nel mio bosco, Calcafauce.', 38134),
(206310, 'itIT', '', 'Ah, $p.\n\nLa caccia può attendere finché sei qui, $gamico mio:amica mia;.\n\nChe notizie porti da Revendreth? Qui a Selvarden, i Drust, i gorm e gli altri parassiti continuano a minacciarci da tutti i lati.\n\nNon che mi lamenti. Sempre più motivi per cacciare.\n\nSpero che ti unirai presto a me. Mi sono abituato al tuo stile di combattimento.\n\nChi ha bisogno di un\'intera squadra di caccia quando ci copriamo a vicenda, no?', 38134),
(206309, 'itIT', '', 'Come procede la corte, $p?\n\nHai organizzato una caccia per la prossima sessione? Spero che sia qualcosa di nuovo, dovendo partecipare.\n\nPer quanto finora mi sia goduto le nostre cacce sui terreni di corte, è sempre più eccitante inseguire qualcosa di nuovo. Non sei d\'accordo?\n\nIn ogni caso, spero di poter presto cacciare di nuovo con te.', 38134),
(206308, 'itIT', '', 'Immagino che la tua caccia continui ad andare bene, $p.\n\nDimmi, che prede hai trovato di recente?', 38134),
(206307, 'itIT', '', '$p! Riposa e raccontami delle tue cacce.', 38134),
(206306, 'itIT', '', 'Come va la caccia, Calcafauce?', 38134),
(206305, 'itIT', '', 'La caccia richiama. Chi risponderà?', 38134),
(206304, 'itIT', '', 'Buona caccia, Calcafauce.', 38134),
(206303, 'itIT', '', 'Fidati, i feromoni di gorm hanno un odore migliore di quanto credi.', 38134),
(206302, 'itIT', '', '<Dama Baccaluna guarda su entrambi i lati prima di chinarsi e sussurrare.>\n\nOra siamo migliori amici, $p. Significa che tu e io affronteremo tutto insieme.\n\nA proposito, ho un piano per il prossimo scherzo che voglio...\n\n<Dama Baccaluna si accorge di un Silfide che sta origliando la conversazione.>\n\nNe parleremo alla prossima Corte delle Braci. Non vedo l\'ora!', 38134),
(206301, 'itIT', '', '<Dama Baccaluna soffoca una risata.>\n\n$p! Sono così felice che ti sia $gfermato:fermata;.\n\nVederti mi ha ricordato lo scherzo che ho fatto a Teotar all\'ultima Corte delle Braci. Che momento meraviglioso!\n\nCi sarà Teotar alla prossima corte? Mi aveva promesso che mi avrebbe reinvitato e sto ancora aspettando...', 38134),
(206300, 'itIT', '', 'Salve di nuovo! Che guaio stai combinando oggi?\n\nE, soprattutto, posso farne parte?', 38134),
(206299, 'itIT', '', '$p! Non passeresti senza salutare la tua Silfide preferita, vero?', 38134),
(206298, 'itIT', '', 'Non temere, $p. Oggi non ho scherzi in serbo per te.', 38134),
(206297, 'itIT', '', 'Be\', sarà interessante...', 38134),
(206296, 'itIT', 'C\'è molto da fare. Se ti serve un intervento immediato, cerca la Mano della Purezza. Grazie mille.', 'C\'è molto da fare. Se ti serve un intervento immediato, cerca la Mano della Purezza. Grazie mille.', 38134),
(206295, 'itIT', '<Ti studia.>\n\nQuali fardelli porti con te?', '<Ti studia.>\n\nQuali fardelli porti con te?', 38134),
(206294, 'itIT', 'Verrò ad aiutarti non appena potrò, Aspirante. Resisti ancora un istante.', 'Verrò ad aiutarti non appena potrò, Aspirante. Resisti ancora un istante.', 38134),
(206293, 'itIT', 'Come posso esserti d\'aiuto?', 'Come posso esserti d\'aiuto?', 38134),
(206292, 'itIT', 'Cosa ti turba, Aspirante?', 'Cosa ti turba, Aspirante?', 38134),
(206291, 'itIT', 'Anche tu hai bisogno di purificarti? Temo di doverti chiedere di aspettare. In molti sono in attesa del mio aiuto.', 'Anche tu hai bisogno di purificarti? Temo di doverti chiedere di aspettare. In molti sono in attesa del mio aiuto.', 38134),
(206290, 'itIT', 'Non ho mai visto nulla del genere. Non è raro che gli Aspiranti vengano da noi in tempi di crisi, ma vederne così tanti contemporaneamente... non posso fare a meno di sentire che qualcosa non vada come dovrebbe oltre le porte del nostro tempio.\n\nChiedo scusa, devo tornare al mio lavoro.', 'Non ho mai visto nulla del genere. Non è raro che gli Aspiranti vengano da noi in tempi di crisi, ma vederne così tanti contemporaneamente... non posso fare a meno di sentire che qualcosa non vada come dovrebbe oltre le porte del nostro tempio.\n\nChiedo scusa, devo tornare al mio lavoro.', 38134),
(206289, 'itIT', 'Ti do il benvenuto al Tempio della Purezza, Aspirante. Ti prego di prenderti un istante per goderti il posto... ma fa\' attenzione: molti attendono la propria purificazione.', 'Ti do il benvenuto al Tempio della Purezza, Aspirante. Ti prego di prenderti un istante per goderti il posto... ma fa\' attenzione: molti attendono la propria purificazione.', 38134),
(206288, 'itIT', '<L\'Asceso china la testa verso di te.>\n\nChe l\'Arconte vegli su di te, mortale.', '<L\'Ascesa china la testa verso di te.>\n\nChe l\'Arconte vegli su di te, mortale.', 38134),
(206287, 'itIT', 'Il tuo arrivo è molto più importante di quanto tu possa immaginare. È un privilegio potervi assistere.', 'Il tuo arrivo è molto più importante di quanto tu possa immaginare. È un privilegio potervi assistere.', 38134),
(206286, 'itIT', 'Stai suscitando scalpore nel Forte Elisio, mortale.', 'Stai suscitando scalpore nel Forte Elisio, mortale.', 38134),
(206285, 'itIT', '<L\'Asceso ti guarda incuriosito.>\n\nCongratulazioni, ti do il benvenuto tra i ranghi degli Aspiranti.', '<L\'Ascesa ti guarda incuriosita.>\n\nCongratulazioni, ti do il benvenuto tra i ranghi degli Aspiranti.', 38134),
(206284, 'itIT', 'Non sono MORTO e non ho attraversato quel... com\'è che lo chiamavano, il Velo... per trascorrere un\'eternità in ATTESA!\n\nUn attimo... tu sei $gnuovo:nuova;! Finalmente l\'attesa è terminata?!?', 'Non sono MORTA e non ho attraversato quel... com\'è che lo chiamavano, il Velo... per trascorrere un\'eternità in ATTESA!\n\nUn attimo... tu sei $gnuovo:nuova;! Finalmente l\'attesa è terminata?!?', 38134),
(206283, 'itIT', '<Tira un lungo sospiro.>\n\nAlmeno questo è un posto piacevole dove aspettare.', '<Tira un lungo sospiro.>\n\nAlmeno questo è un posto piacevole dove aspettare.', 38134),
(206282, 'itIT', 'C\'è una lunga strada da fare, ma che mi venga negata la possibilità di percorrerla... Sto cominciando a pensare di essere stato mandato qui per errore.', 'C\'è una lunga strada da fare, ma che mi venga negata la possibilità di percorrerla... Sto cominciando a pensare di essere stata mandata qui per errore.', 38134),
(206281, 'itIT', 'Pazienza, dicono! Be\', non vedo alcun Eletto della Pazienza in giro, e tu???', 'Pazienza, dicono! Be\', non vedo alcun Eletto della Pazienza in giro, e tu???', 38134),
(206280, 'itIT', 'Hai notizie dall\'Arconte? La siccità è finalmente finita???', 'Hai notizie dall\'Arconte? La siccità è finalmente finita???', 38134),
(206279, 'itIT', 'Ci hanno detto di aspettare fin quando la crisi d\'Animum non fosse stata risolta... Non so nemmeno quanto tempo sia passato. Comincio a credere che questa siccità non finirà mai.\n\nAlmeno i Factotum sono molto gentili e posso mangiare quanto voglio senza prendere peso! Ahah!', 'Ci hanno detto di aspettare fin quando la crisi d\'Animum non fosse stata risolta... Non so nemmeno quanto tempo sia passato. Comincio a credere che questa siccità non finirà mai.\n\nAlmeno i Factotum sono molto gentili e posso mangiare quanto voglio senza prendere peso! Ahah!', 38134),
(206278, 'itIT', 'Voglio solo iniziare il mio viaggio. È chiedere troppo?', 'Voglio solo iniziare il mio viaggio. È chiedere troppo?', 38134),
(206277, 'itIT', 'Questo è assurdo. Sto aspettando da... un attimo, che giorno è oggi?\n\n...da tantissimo tempo!', 'Questo è assurdo. Sto aspettando da... un attimo, che giorno è oggi?\n\n...da tantissimo tempo!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206276, 'itIT', '$GUn nuovo arrivato:Una nuova arrivata;? FINALMENTE!', '$GUn nuovo arrivato:Una nuova arrivata;? FINALMENTE!', 38134),
(206275, 'itIT', 'Oh! Un nuovo volto! Ci sono novità dall\'altra parte?', 'Oh! Un nuovo volto! Ci sono novità dall\'altra parte?', 38134),
(206274, 'itIT', 'Ho sacrificato tutta la mia vita per arrivare qui ed è stato meraviglioso. Pieno di speranza. È ancora meraviglioso, ma mi avevano promesso che sarei diventato la versione migliore di me stesso, invece sono bloccato qui, a diventare sempre più triste ogni giorno che passa. Non può essere questo quello che intendevano, vero?', 'Ho sacrificato tutta la mia vita per arrivare qui ed è stato meraviglioso. Pieno di speranza. È ancora meraviglioso, ma mi avevano promesso che sarei diventata la versione migliore di me stessa, invece sono bloccata qui, a diventare sempre più triste ogni giorno che passa. Non può essere questo quello che intendevano, vero?', 38134),
(206273, 'itIT', 'Almeno la morte è stata interessante. Tutta questa attesa... e per cosa poi?', 'Almeno la morte è stata interessante. Tutta questa attesa... e per cosa poi?', 38134),
(206272, 'itIT', 'Mi manca il mio corpo.', 'Mi manca il mio corpo.', 38134),
(206271, 'itIT', 'Spero che tu sia un segno per un futuro migliore. Non va tutto liscio al Bastione.', 'Spero che tu sia un segno per un futuro migliore. Non va tutto liscio al Bastione.', 38134),
(206270, 'itIT', 'La parte peggiore dell\'attesa è il non avere né mani né gambe e nemmeno gli occhi.\n\nIn tutta onestà, non so bene come funzioni. Mi avevano detto che avrei assunto una nuova forma una volta completata la mia prima purificazione, ma in pochi sono riusciti a completare il rito dall\'inizio della siccità.', 'La parte peggiore dell\'attesa è il non avere né mani né gambe e nemmeno gli occhi.\n\nIn tutta onestà, non so bene come funzioni. Mi avevano detto che avrei assunto una nuova forma una volta completata la mia prima purificazione, ma in pochi sono riusciti a completare il rito dall\'inizio della siccità.', 38134),
(206268, 'itIT', 'Mi avevano detto che questo posto era la mia ricompensa per aver vissuto una buona vita. Ma in definitiva, vengo punito?', 'Mi avevano detto che questo posto era la mia ricompensa per aver vissuto una buona vita. Ma in definitiva, vengo punita?', 38134),
(206267, 'itIT', 'Dicono che i Collettori siano bravi con le mani. Forse dovrei cominciare a intagliare...', 'Dicono che i Collettori siano bravi con le mani. Forse dovrei cominciare a intagliare...', 38134),
(206266, 'itIT', 'Diventare un Guardiano sembra molto difficile... ma se potessi dare alle anime un\'altra opportunità di vita... questo non sarebbe un nobile scopo?', 'Diventare una Guardiana sembra molto difficile... ma se potessi dare alle anime un\'altra opportunità di vita... questo non sarebbe un nobile scopo?', 38134),
(206265, 'itIT', 'Penso di voler diventare un Latore quando ascenderò. Voglio vedere tutti i mondi al di là delle Terretetre. Voglio sapere cosa c\'è là fuori!', 'Penso di voler diventare una Latrice quando ascenderò. Voglio vedere tutti i mondi al di là delle Terretetre. Voglio sapere cosa c\'è là fuori!', 38134),
(206264, 'itIT', 'Il Bastione è un posto davvero meraviglioso. Mi dicono che il cielo era pieno d\'Animum prima che qualcosa andasse storto. Vorrei vederlo com\'era un tempo.', 'Il Bastione è un posto davvero meraviglioso. Mi dicono che il cielo era pieno d\'Animum prima che qualcosa andasse storto. Vorrei vederlo com\'era un tempo.', 38134),
(206263, 'itIT', 'Oh, avere le ali, un giorno...\n\n<Sospira malinconicamente.>', 'Oh, avere le ali, un giorno...\n\n<Sospira malinconicamente.>', 38134),
(206262, 'itIT', 'Il Bosco del Risveglio è l\'ultimo passo sulla via della rinascita. È uno dei luoghi più sacri di Selvarden.\n\nQuando uno spirito è pronto a tornare al proprio mondo, viene portato qui. Fa ritorno attraverso questo portale, per iniziare da capo il suo ciclo.', 'Il Bosco del Risveglio è l\'ultimo passo sulla via della rinascita. È uno dei luoghi più sacri di Selvarden.\n\nQuando uno spirito è pronto a tornare al proprio mondo, viene portato qui. Fa ritorno attraverso questo portale, per iniziare da capo il suo ciclo.', 38134),
(206261, 'itIT', 'Ho tentato tre volte la mia prima purificazione e ogni volta i miei fardelli mi hanno sopraffatto! E questo è solo il primo passo! Cosa farò se resterò intrappolato qui per sempre?', 'Ho tentato tre volte la mia prima purificazione e ogni volta i miei fardelli mi hanno sopraffatta! E questo è solo il primo passo! Cosa farò se resterò intrappolata qui per sempre?', 38134),
(206260, 'itIT', 'Ho così tanto da imparare... ma almeno non c\'è alcuna fretta. Dopotutto non è che qualcuno possa ascendere in questo momento.', 'Ho così tanto da imparare... ma almeno non c\'è alcuna fretta. Dopotutto non è che qualcuno possa ascendere in questo momento.', 38134),
(206259, 'itIT', 'Prima il Rito della Purezza, poi il Rito del Coraggio e un altro Rito della...\n\nAspetta, ce l\'ho. Lealtà? No, non è quello...', 'Prima il Rito della Purezza, poi il Rito del Coraggio e un altro Rito della...\n\nAspetta, ce l\'ho. Lealtà? No, non è quello...', 38134),
(206258, 'itIT', 'Da quanto tempo stai aspettando?', 'Da quanto tempo stai aspettando?', 38134),
(206257, 'itIT', 'Ho così tanto da imparare e questo ritardo sta solo prolungando il mio viaggio. Temo che non ascenderò mai.', 'Ho così tanto da imparare e questo ritardo sta solo prolungando il mio viaggio. Temo che non ascenderò mai.', 38134),
(206256, 'itIT', 'Quanto tempo è passato da quando le cose erano normali? Il tempo trascorre in modo strano qui.', 'Quanto tempo è passato da quando le cose erano normali? Il tempo trascorre in modo strano qui.', 38134),
(206255, 'itIT', 'Voglio solo iniziare i Cinque Riti.', 'Voglio solo iniziare i Cinque Riti.', 38134),
(206254, 'itIT', 'Dove ti sei $gfermato:fermata; col tuo addestramento? Ero a tanto così dal completare il Rito della Lealtà quando le cose hanno cominciato ad andare storte. Ero vicinissimo all\'Ascensione...', 'Dove ti sei $gfermato:fermata; col tuo addestramento? Ero a tanto così dal completare il Rito della Lealtà quando le cose hanno cominciato ad andare storte. Ero vicinissima all\'Ascensione...', 38134),
(206253, 'itIT', 'Non c\'è niente da fare, non c\'è meditazione o addestramento che possa portarmi più vicino all\'ascensione. Non ora, comunque.\n\n<Sospira profondamente.>', 'Non c\'è niente da fare, non c\'è meditazione o addestramento che possa portarmi più vicino all\'ascensione. Non ora, comunque.\n\n<Sospira profondamente.>', 38134),
(206252, 'itIT', 'Nonostante il Bastione sia solo un\'ombra della sua antica gloria, è ancora il posto più bello in cui sia mai stato. Se dovessi scegliere un posto dove aspettare per l\'eternità, sceglierei questo.', 'Nonostante il Bastione sia solo un\'ombra della sua antica gloria, è ancora il posto più bello in cui sia mai stata. Se dovessi scegliere un posto dove aspettare per l\'eternità, sceglierei questo.', 38134),
(206251, 'itIT', 'Come stai passando il tempo?', 'Come stai passando il tempo?', 38134),
(206250, 'itIT', 'Per l\'Arconte, sono esausto.', 'Per l\'Arconte, sono esausta.', 38134),
(206249, 'itIT', 'Servi il tempio, dicevano. Incarna la virtù, dicevano. Be\', io...\n\n<Tace non appena nota la tua presenza.>', 'Servi il tempio, dicevano. Incarna la virtù, dicevano. Be\', io...\n\n<Tace non appena nota la tua presenza.>', 38134),
(206248, 'itIT', '<Si ricompone in fretta prima di parlare.>\n\nAspirante, come posso servirti?', '<Si ricompone in fretta prima di parlare.>\n\nAspirante, come posso servirti?', 38134),
(206247, 'itIT', 'Sarò da te il prima possibile, Aspirante.', 'Sarò da te il prima possibile, Aspirante.', 38134),
(206246, 'itIT', 'Anche tu hai bisogno di purificarti?', 'Anche tu hai bisogno di purificarti?', 38134),
(206245, 'itIT', 'Mi avevano detto che avrei potuto aiutarli dopo aver completato il mio Rito della Purezza. Invece, io... ho paura. Ce ne sono TANTISSIMI.', 'Mi avevano detto che avrei potuto aiutarli dopo aver completato il mio Rito della Purezza. Invece, io... ho paura. Ce ne sono TANTISSIMI.', 38134),
(206244, 'itIT', '<Fa un respiro profondo prima di riconoscere la tua presenza.>\n\nC\'è qualcosa che non va?', '<Fa un respiro profondo prima di riconoscere la tua presenza.>\n\nC\'è qualcosa che non va?', 38134),
(206243, 'itIT', 'Di cosa hai bisogno?', 'Di cosa hai bisogno?', 38134),
(206242, 'itIT', 'Apprezzo la tua pazienza. Sappi che stiamo facendo tutto il possibile.', 'Apprezzo la tua pazienza. Sappi che stiamo facendo tutto il possibile.', 38134),
(206241, 'itIT', 'Posso fare qualcosa per servirti?', 'Posso fare qualcosa per servirti?', 38134),
(206240, 'itIT', 'Come posso esserti d\'aiuto?', 'Come posso esserti d\'aiuto?', 38134),
(206239, 'itIT', 'Che l\'Arconte vegli su di te.', 'Che l\'Arconte vegli su di te.', 38134),
(206238, 'itIT', 'Cosa può fare questo Factotum, uuuh?', 'Cosa può fare questo Factotum, uuuh?', 38134),
(206236, 'itIT', 'Molto tristezza. Molti caduti.\n\n<Abbassa gli occhi con tristezza.>', 'Molto tristezza. Molti caduti.\n\n<Abbassa gli occhi con tristezza.>', 38134),
(206235, 'itIT', 'Gli Aspiranti tristi rendono tristi i Factotum.', 'Gli Aspiranti tristi rendono tristi i Factotum.', 38134),
(206234, 'itIT', 'Non essere triste. Vuoi una canzone? Forse un succo?', 'Non essere triste. Vuoi una canzone? Forse un succo?', 38134),
(206233, 'itIT', 'Molti Aspiranti tristi... vuoi che ti facciamo sentire meglio?', 'Molti Aspiranti tristi... vuoi che ti facciamo sentire meglio?', 38134),
(206232, 'itIT', 'Il succo fa bene al cervello e fa bene ai muscoli. Ti porto del succo?', 'Il succo fa bene al cervello e fa bene ai muscoli. Ti porto del succo?', 38134),
(206231, 'itIT', 'Buon allenamento oggi? Sei $gstanco:stanca;? Meglio sedersi. O meglio distendersi.', 'Buon allenamento oggi? Sei $gstanco:stanca;? Meglio sedersi. O meglio distendersi.', 38134),
(206230, 'itIT', '$gSimpatico:Simpatica; Aspirante. Cosa serve?', '$gSimpatico:Simpatica; Aspirante. Cosa serve?', 38134),
(206229, 'itIT', 'Come posso farti felice?', 'Come posso farti felice?', 38134),
(206228, 'itIT', 'Pulire e riordinare. Il Bastione è bellissimo, vero?', 'Pulire e riordinare. Il Bastione è bellissimo, vero?', 38134),
(206227, 'itIT', 'AHHH! $gNuovo:Nuova; Aspirante! Suonate le campane e i corni! Che momento!!!', 'AHHH! $gNuovo:Nuova; Aspirante! Suonate le campane e i corni! Che momento!!!', 38134),
(206226, 'itIT', 'Ciao! Sei $gnuovo:nuova;! Molto bene! Sì!', 'Ciao! Sei $gnuovo:nuova;! Molto bene! Sì!', 38134),
(206225, 'itIT', 'Uuuh! Ciao! $gNuovo:Nuova; Aspirante! Urrà!', 'Uuuh! Ciao! $gNuovo:Nuova; Aspirante! Urrà!', 38134),
(206223, 'itIT', 'Ti porto qualcosa?', 'Ti porto qualcosa?', 38134),
(206222, 'itIT', 'Noi aiutiamo! Di cosa hai bisogno?', 'Noi aiutiamo! Di cosa hai bisogno?', 38134),
(206221, 'itIT', '<Il Factotum canticchia tra sé un allegro motivetto.>', '<Il Factotum canticchia tra sé un allegro motivetto.>', 38134),
(206220, 'itIT', 'Tu vuoi, io porto?', 'Tu vuoi, io porto?', 38134),
(206219, 'itIT', '', 'Siamo tutti un po\' stanchi al momento. La siccità ha cambiato molti aspetti della nostra vita quotidiana. \n\nMa dobbiamo continuare. Come posso servirti?', 38134),
(206218, 'itIT', '<Achillon borbotta tra sé mentre esegue la sua sequenza d\'addestramento.>', '', 38134),
(206217, 'itIT', '<Sembra che Fillios stia discutendo con Tymon.>\n\nNon puoi concentrare il tuo intero essere sul ricordo. Evocalo e lascia che scorra. So che è più facile da dire che da fare, ma hai quasi finito. Presto lo supererai, non ho dubbi.', '<Sembra che Fillios stia discutendo con Tymon.>\n\nNon puoi concentrare il tuo intero essere sul ricordo. Evocalo e lascia che scorra. So che è più facile da dire che da fare, ma hai quasi finito. Presto lo supererai, non ho dubbi.', 38134),
(206216, 'itIT', '<Sembra che Tymon stia discutendo con Fillios.>\n\nSei sempre così stoico durante le tue prove! Vorrei riuscire a poterti imitare. È come se la mia mente se ne andasse per conto suo non appena sale a galla un brutto ricordo.', '<Sembra che Tymon stia discutendo con Fillios.>\n\nSei sempre così stoico durante le tue prove! Vorrei riuscire a poterti imitare. È come se la mia mente se ne andasse per conto suo non appena sale a galla un brutto ricordo.', 38134),
(206214, 'itIT', '', 'Sembra che il mio Vincolo dell\'Anima ti abbia preso in simpatia. Immagino che dovrei ringraziarti, stava iniziando a perdere un po\' del suo entusiasmo senza la sua forgia.\n\nPassa a salutare se capiti nel Tempio del Coraggio.', 38134),
(206213, 'itIT', '', 'Ti prego, non preoccuparti per me. Non mi sento molto bene, ma la mia Eletta mi curerà quando potrà farlo. Nel frattempo, ho il mio addestramento che mi sostiene.\n\n<Eridia inspira affannosamente.> \n\nNon devo rimuginare sulla tristezza o sulla rabbia, e neanche su... Lysonia.', 38134),
(206211, 'itIT', 'Noi preserviamo il ciclo.', 'Noi preserviamo il ciclo.', 38134),
(206210, 'itIT', '', 'Tutti quegli Aspiranti vulnerabili... tutti gli Ascesi che abbiamo mandato al Tempio della Lealtà... è questo ciò che ne è stato di loro? Devono averli fatti impazzire o non avrebbero mai attaccato questo tempio! Non siamo i loro nemici! Siamo soltanto...\n\n<Eridia si ammutolisce mentre fissa il tempio. Una singola lacrima scende sul suo viso.>', 38134),
(206209, 'itIT', '', 'Sei testimone delle circostanze più insolite, mortale. Ti prego, non lasciare che ti turbino, ci serve chiunque abbia una mente lucida in questi tempi bui.', 38134),
(206208, 'itIT', '$Benvenuto:Benvenuta; al Bosco del Risveglio.', '$Benvenuto:Benvenuta; al Bosco del Risveglio.', 38134),
(206207, 'itIT', '', 'Io sono la Mano dell\'Eletta, il suo volere è il mio.', 38134),
(206206, 'itIT', '', 'Hai bisogno dei nostri Discepoli?', 38134),
(206205, 'itIT', '', 'Sia fatta la volontà della mia Eletta.', 38134),
(206204, 'itIT', '', 'Per volere di Vesifone.', 38134),
(206202, 'itIT', 'Non ascolteranno. Non vogliono ascoltare! Questa violenza deve cessare, prima che tutto sia perduto.', '', 38134),
(206201, 'itIT', 'Ti ringrazio per il tuo aiuto. Purtroppo siamo oberati di lavoro.', '', 38134),
(206200, 'itIT', 'Ho dato tutto me stesso al servizio del Tempio della Purezza. Puoi chiamarmi per qualsiasi cosa ti serva mentre ti trovi qui.', '', 38134),
(206199, 'itIT', '', 'È passato troppo tempo dall\'ultima volta che ho visto la celebrazione di questa cerimonia. \n\nCongratulazioni, e grazie per la speranza che ci hai dato.', 38134),
(206198, 'itIT', '', 'Sì, sono ancora qui. \n\n<La Forgialita Sofone sorride.> \n\nA quanto pare la mia forgia è un ottimo strumento per insegnare ai nuovi Aspiranti. Ho chiesto all\'Arconte se potessi rimanere qui al Crogiolo e lei ha accettato! Se questa è l\'eternità che mi aspetta, allora sarò felice per gli eoni a venire.', 38134),
(206196, 'itIT', 'Che la notte ti benedica.', 'Che la notte ti benedica.', 38134),
(206195, 'itIT', '', 'Un giorno ascenderò. Fino ad allora, ho la mia forgia a tenermi occupata... Quando avrò l\'Animum per usarla, s\'intende.', 38134),
(206194, 'itIT', '', 'Deve sembrare strano parlare con un\'altra Aspirante del tuo addestramento. Mi hanno messa al comando qui perché mi trovo qui da più tempo, ecco tutto.\n\n<La Forgialita Sofone sorride.>', 38134),
(206193, 'itIT', '', 'La mia forgia è il mio gioiellino. Sono l\'unica Aspirante Forgialita per un motivo!', 38134),
(206192, 'itIT', '', 'Il maestro Mikanikos è molto occupato. Sika si assicura che la Forgialita abbia tutto ciò che le serva, sì!', 38134),
(206191, 'itIT', '', '<Sika controlla la sua pergamena.>\n\nAnimum? C\'è.\nPinze? Ci sono.\nMateriali? Ci sono.\nForgialita? C\'è.\nSika? C\'è!\n\nAbbiamo tutto! Uuurrà!', 38134),
(206190, 'itIT', '', 'Oh no! Sika è in pericolo?', 38134),
(206189, 'itIT', '', 'A Sika è mancata l\'Anfitriona. Mnemi sa ogni cosa!\n\nBe\', quasi tutto.', 38134),
(206188, 'itIT', '', 'Il maestro Mikanikos, il Forgialita Supremo, ha chiesto a Sika di occuparsi dei Centurioni. Abbiamo poco Animum, dobbiamo scegliere a chi darlo! Ma ora tu sei qui! Serve l\'Animum per l\'Anfitriona!\n\nÈ un bene che Sika l\'abbia preso dai Centurioni!', 38134),
(206186, 'itIT', 'Kleia mi proteggerà mentre fuggiremo. Fai ciò che devi fare, $n, credo in te.', '', 38134),
(206185, 'itIT', 'Oh no! È terribile... Spero che Kleia sia al sicuro!', '', 38134),
(206184, 'itIT', 'È terribile. Tutti quegli Aspiranti... forse Eridia ha ragione. Dovrei restare qui.', '', 38134),
(206183, 'itIT', 'C\'è qualcosa che non va, $n...', '', 38134),
(206182, 'itIT', 'Sono felice che la Mano della Purezza ci abbia chiesto di rimanere uniti. Sembra che tu abbia la situazione sotto controllo, e io sarò qui per sostenerti se dovessi averne bisogno. Me la caverò.\n\n<Pelagos si guarda intorno preoccupato.>', '', 38134),
(206181, 'itIT', 'Oh, cielo. La situazione dev\'essere grave se un Discepolo ha dovuto abbandonare il suo tempio in questo modo.', '', 38134),
(206180, 'itIT', 'Sapevo che ce l\'avresti fatta, $n. Affronteremo insieme questa crisi!', '', 38134),
(206179, 'itIT', 'Fai del tuo meglio! Athanos è progettato per mettere alla prova anche il più capace degli Aspiranti.', '', 38134),
(206178, 'itIT', '<Sembra che Pelagos stia discutendo animatamente con Kleia.>', '', 38134),
(206177, 'itIT', 'La tua vita è stata molto interessante! Non vedo l\'ora di saperne di più su di te. Tuttavia, per ora è meglio che ascolti la Forgialita. Lei sa come funziona questo luogo molto meglio di me.', '', 38134),
(206176, 'itIT', 'Sono molto curioso delle gesta che hai compiuto, $n. Devono essere grandiose per averti condotto qui, e visto come mi hai aiutato non appena sei $ggiunto:giunta; qui! Devi essere $gnato:nata; per il Bastione, non c\'è dubbio!', '', 38134),
(206175, 'itIT', 'Tutto ciò che possiamo fare è continuare ad avanzare.', '', 38134),
(206174, 'itIT', '', 'Mi sento come se stessi sconfinando. Questo luogo è antico quanto il Bastione stesso, lo percepisco.\n\nSe non fosse per queste tragiche circostanze, mi riterrei fortunata solo per avervi posato sopra gli occhi.', 38134),
(206173, 'itIT', '', 'Non ho mai visto un\'unità Mnemi comportarsi in questo modo, è... inquietante.', 38134),
(206172, 'itIT', '', 'Proteggerò il Forgialita Supremo. Fai ciò che devi fare, $n.', 38134),
(206171, 'itIT', '', 'Gli Indegni hanno preso il controllo anche di questo posto... Controllano tutto il Bastione?\n\nSpero che Pelagos abbia sparso la voce in tempo, ma dobbiamo concentraci sul nostro compito qui: dobbiamo scoprire chi si cela dietro questi attacchi!', 38134),
(206170, 'itIT', '', 'Dobbiamo scoprire perché gli Indegni hanno deciso di attaccare. Dobbiamo sapere chi c\'è dietro tutto questo!', 38134),
(206169, 'itIT', '', 'Grazie per aver tenuto al sicuro Pelagos. Sono felice di vedervi tutti interi!\n\nDevo tenerlo al sicuro. Spero tu comprenda, ma non posso venire con te.\n\nSo che puoi aiutarci e confido che ci rivedremo di nuovo. \n\nPresta servizio, $n.', 38134),
(206168, 'itIT', '', '<Kleia sembra molto concentrata sul rituale.>', 38134),
(206167, 'itIT', '', 'Sembra che non ci sia tempo per festeggiare la tua vittoria. Hanno bisogno di noi.', 38134),
(206166, 'itIT', '', 'Ben fatto, $n. Spero che le tue imprese si guadagnino l\'attenzione dell\'Arconte.', 38134),
(206165, 'itIT', '', '<Kleia discute animatamente con Pelagos.>', 38134),
(206164, 'itIT', '', 'Un giorno ascenderò. Fino ad allora, sono felice di aiutarti, Pelagos, e aiutare tutti gli altri Aspiranti in ogni modo possibile.', 38134),
(206163, 'itIT', '', 'Nessuno viene mai inviato al Bastione per errore, $n. Ricordalo.\n\n<Kleia ti sorride.>', 38134),
(206162, 'itIT', '', 'Non vedo l\'ora di assistere alle gesta che ti hanno portato alle nostre porte, $n.', 38134),
(206161, 'itIT', '', 'Kalisthene supervisiona tutti gli Aspiranti che non risiedono attualmente in un tempio. Avrei dovuto sapere che attivando l\'Anfitriona avrei attirato la sua attenzione. \n\nNon preoccuparti, ci penso io!', 38134),
(206160, 'itIT', '', 'Entra nella pozza, $n. Ti aiuterà a riprenderti dal tuo viaggio attraverso il Velo.', 38134),
(206159, 'itIT', '', 'Dovresti riuscire a recuperare un po\' di Animum da questi Centurioni. Ce n\'è ben poco in giro, ma credo che potremo arrangiarci con quello che abbiamo.', 38134),
(206157, 'itIT', '', 'Questa è un\'allerta d\'intrusione. Il livello di minaccia attuale è: maggiore. L\'arrivo dell\'Esecutrice è imminente.', 38134),
(206156, 'itIT', '', 'Questa è un\'allerta d\'intrusione. Il livello di minaccia attuale è: moderato.', 38134),
(206155, 'itIT', '', 'Questa è un\'allerta d\'intrusione. Il livello di minaccia attuale è: minore.', 38134),
(206154, 'itIT', 'Io cantare una canzone felice... Caaanzoooneee feeeliiiceee...', '', 38134),
(206153, 'itIT', 'Io manca caro tesoro piccolino...', '', 38134),
(206152, 'itIT', 'Spero tu abbia portato di nuovo merendine!', '', 38134),
(206151, 'itIT', 'Caro tesoro!\n\nDomanda. Io fa bene alle feste?\n\nA volte i denti aguzzi ridono un po\', ma faccio mio meglio.\n\nIo fa fiero di me caro tesoro!', '', 38134),
(206150, 'itIT', '<Testaroccia si asciuga le lacrime con la mano libera.>\n\nCaro tesoro!!! Così felice che tu qui!\n\nIo vorrei andare ovunque con caro tesoro...\n\nAnche io potrei provare a entrare Fauce?', '', 38134),
(206149, 'itIT', '', 'Scadenze mi han dato che ho rispettato.\n\nPiani furon fatti e speranza fu data.\n\nMa tutto ormai brucia come lacrime di fuoco.\n\nNon ci si nasconde. Essi ti vedono. E se non riescono, lo faranno gli altri.', 38134),
(206148, 'itIT', '', 'Salve, Luce, mia vecchia amica.\n\nSei qui per darmi ancora fuoco?\n\nVacilla e brilla il mio elementare balbettare contro la truce Luce.\n\nCurarmi mi danneggia e la vita mi brucia.', 38134),
(206147, 'itIT', '', 'Dove sono gli organizzatori andati?\nBruciano con dolore e non fanno che preoccuparsi,\nurlano a gran voce, ma sono soffocati,\ndai brandelli del loro cervello ormai sparsi!\n\nAspetta, ho fatto un\'assonanza o forse una rima?\nForse avrei dovuto pensarci prima.\nVorrei tanto avere una lima.\nHo davvero compiuto un crimine di così bassa stima?', 38134),
(206146, 'itIT', '', 'Dicevano che era finita! Finita! O è successo secoli fa? O ci sarebbero voluti secoli per finire? O l\'avrebbero fatto tra secoli?\n\nIl tempo non è uno yo-yo!\n\nAspetta! So già cos\'è uno yo-yo? Che tempo è adesso?', 38134),
(206145, 'itIT', 'Ehi Calcafauce, domanda per te. Tocca ai ragazzi e a me mantenere Revendreth tutta in ghingheri e farla crescere sempre di più.\n\nQuindi... a chi è che tocca nella Fauce? Cioè, immagino tu ci sia $gstato:stata;, dato che $gil:la; Calcafauce.\n\nPiù che altro, quelli che si occupano della Fauce, fanno delle pause?', '', 38134),
(206144, 'itIT', '', 'Poi dopo potrai tornare qui e festeggeremo.', 38134),
(206143, 'itIT', '', 'Invierò il mio stalliere per recuperare la mascotte da te al punto d\'incontro.', 38134),
(206142, 'itIT', '', 'Sento forse $gil mio bellissimo:la mia bellissima; $p che si avvicina?', 38134),
(206141, 'itIT', '', 'Vedi, Alfryd? Te l\'avevo detto che sarebbe stata una bella giornata. $p è qui.', 38134),
(206140, 'itIT', '', 'Ah, Calcafauce. A cosa devo il piacere?', 38134),
(206139, 'itIT', '', 'Oh, Alfryd caro, potresti portarmi un nuovo...', 38134),
(206138, 'itIT', '', '<La Contessa ti accoglie caldamente.>\n\n$gIl mio bellissimo:La mia bellissima; $p. Che notizie porti da Oblio del Peccato? Confido che tutti stiano bene.\n\nPorgi a Teotar e al mio caro Principe i miei saluti al vostro prossimo incontro.\n\nOppure, se dovessi avere il piacere di partecipare presto a un\'altra Corte delle Braci, lo farò io stessa.\n\nMa basta parlare di loro. Cosa mi dici di te? Mi sei $gmancato:mancata; dalla tua ultima visita.', 38134),
(206137, 'itIT', '', 'Ti stavo pensando, $gmio caro:mia cara;.\n\nDimmi. Cosa desideri di più, in questo momento?\n\nNon parlo dei tuoi obiettivi di vita o sogni d\'infanzia. I sogni svaniscono, gli obiettivi cambiano.\n\nMa c\'è sempre l\'istinto, che attende appena sotto la superficie. Ti attirerà altrove se mai abbasserai la guardia.\n\n<La Contessa sorride.>\n\nBene. Non dirmelo. Tienilo per te. È l\'unico modo per essere al sicuro.', 38134),
(206136, 'itIT', '', 'Dimmi, cosa porta $gil:la; potente Calcafauce alla mia torre oggi?\n\nUn altro favore, forse?\n\n<La Contessa sorride.>\n\nFai attenzione a chi ne chiedi.\n\nMolte povere anime, accecate dal desiderio, si sono ritrovate in debito con i maestri più sgradevoli.', 38134),
(206133, 'itIT', 'Non riesco a piangerci come voi mortali, ma ammetto che ho ripensato al giorno in cui ci siamo incontrati.\n\nUn altro noioso turno in attesa a Oblio dell\'Orgoglio senza nulla da fare... E poi dal nulla salta fuori $gil:la; Calcafauce $gstesso:stessa;, $gpronto:pronta; a scuotere tutte le Terretetre!\n\nNon mi ero reso conto che saremmo diventati migliori amici un giorno. Pensa un po\'...', '', 38134),
(206132, 'itIT', 'Ehi, $gamico:amica;.\n\nFammi sapere quando devi fare qualcosa. Ho della roba da fare, ma posso trovare il tempo per qualunque cosa tu abbia in mente.\n\nNon vedo l\'ora che ci sia la prossima Corte delle Braci, comunque. Teotar mi ha insegnato un paio di cose nuove da dire ai tizi eleganti.', '', 38134),
(206130, 'itIT', 'Ho un po\' di tempo, se hai bisogno di fare qualcosa.', '', 38134),
(206129, 'itIT', 'Come va, $gvecchio amico:vecchia amica;?\n\nIo e Mazzafaccia siamo stati molto occupati con la vita sociale ultimamente, grazie a te e ai nostri nuovi amici della Corte delle Braci.\n\nMa troveremo sempre tempo per $gil nostro:la nostra; MIGLIORE $gamico:amica;, $p.\n\nTutto ciò che devi fare è dirlo, e noi saremo lì.', '', 38134),
(206128, 'itIT', '', 'Le incursioni organizzate contro il nostro regno devono essere affrontate con estrema forza. Onora coloro che proteggono i nostri segreti.', 38134),
(206127, 'itIT', 'Ehi, tesoro. Hai sentito dei mondani parlare di me e Mazzafaccia di recente?\n\nNon possiamo esserci in ogni corte, ma voglio assicurarmi che non si siano dimenticati di noi, capisci?\n\nÈ stato bello staccare dal lavoro nel fango per venire alla corte.\n\nTu e il Principe trattate bene i Dragatori. Vi rispetto.', '', 38134),
(206126, 'itIT', '', 'La nostra conoscenza della guerra e delle macchine da distruzione è una risorsa preziosa. Deve essere protetta da coloro che la rivolterebbero contro le Terretetre.', 38134),
(206125, 'itIT', 'Ma a me piace il mio vecchio cappello sporco...', '', 38134),
(206124, 'itIT', 'Muoviamoci, Mazzafaccia. Devo cominciare a vestirmi come quei nobili fangosi.', '', 38134),
(206122, 'itIT', 'Non so ancora perché vuoi un cappello stravagante, Squartacus...', '', 38134),
(206120, 'itIT', '$p è occupato, Mazzafaccia. Sta salvando le Terretetre e così via. E poi, $glui:lei; è $gil MIO:la MIA; migliore $gamico:amica;!', '', 38134),
(206119, 'itIT', 'Ehi, $gil mio:la mia; Calcafauce $gpreferito:preferita;! Mi sei $gmancato:mancata; durante il mio ultimo trasporto.', '', 38134),
(206118, 'itIT', 'Ma certo che sì! Non sai nulla dell\'alta moda.', '', 38134),
(206117, 'itIT', 'Te lo sto dicendo, Squartacus. Non hai bisogno di altri cappelli...', '', 38134),
(206116, 'itIT', 'Non fissare. È maleducato!', '', 38134),
(206115, 'itIT', 'Ehi, guarda. È $gquel:quella; Calcafauce elegante!', '', 38134),
(206114, 'itIT', 'Quei farabutti non hanno rispetto per la santità di queste sale!', '', 38134),
(206113, 'itIT', '$p! Che splendida sorpresa vederti!', '', 38134),
(206112, 'itIT', 'Spero che sia tu, $p...', '', 38134),
(206111, 'itIT', 'Chi va là? Mostrati!', '', 38134),
(206110, 'itIT', 'Ah, $gmio caro amico:mia cara amica;. Speravo proprio che passassi di tanto in tanto.\n\nMi sembra sia passata un\'eternità dall\'ultima Corte delle Braci insieme. Confido che vada tutto bene a Oblio del Peccato sotto il tuo sguardo vigile?\n\nL\'Affermato e io abbiamo fatto grandi progressi per salvare le anime perdute qui, grazie al tuo aiuto.\n\nSei $gun buon amico: una buona amica;, $p. E soprattutto, una brava persona.\n\nO almeno sei abbastanza $gbravo:brava; da far finta di esserne una!\n\n<Il Custode delle Cripte Kassir ride.>\n\nMa dubito che tu sia $gvenuto:venuta; a sentire le mie vecchie battute.\n\nCosa ti porta in questo angolo di Revendreth oggi, $gvecchio amico:vecchia amica;?', '', 38134),
(206109, 'itIT', 'Ah, $p.\n\n<Il Custode della Cripta emette un sospiro di sollievo e si rilassa.>\n\nIl tuo è un volto amico in questa folla di traditori e profanatori.\n\nContinuo a fare progressi, ma devo ammettere che mi sono spesso distratto.\n\nHo trovato che la mia mente vaghi spesso a riflettere sulla prossima Corte delle Braci...\n\nSono ancora sorpreso che tu e il Principe Renathal mi reputiate degno; di un invito ricorrente, ma ne sono onorato.\n\nMa basta col mio divagare. Cosa posso fare per te oggi, $gamico mio:amica mia;?', '', 38134),
(206108, 'itIT', 'Sarà uno spasso!', '', 38134),
(206107, 'itIT', 'Un po\' di viaggio, un po\' di forgiatura di gorm, un po\' d\'avventura…', '', 38134),
(206106, 'itIT', 'Si sta bene quaggiù, vero? Quanta calma e pace, solo dei laboriosi Fungomanti come te e Cort laggiù. Anche me, ovviamente. Tutti che lavorano sodo per mantenere Selvarden in funzione.$b$BÈ bello che tu mi faccia visita. Non mi pensano molte creature al giorno d\'oggi e conversare con te è sempre piacevole. Volevo solo farti sapere che lo apprezzo molto.$b$BNon pensare che io diventi troppo gentile con te, però! C\'è molto lavoro da fare!', '', 38134),
(206105, 'itIT', '', 'Una corte di Venthyr sembra... intrigante.', 38134),
(206104, 'itIT', '', 'Se posso parlare liberamente, sono felice che il Polemarco ti abbia $gmandato:mandata; da me.', 38134),
(206103, 'itIT', 'Sono quaggiù da secoli. Mi ricordo i primi giorni, quando eravamo solo io e la Regina. Eravamo molto impegnati all\'epoca, anche lei è una gran lavoratrice...$b$bNon come questa gentaglia che c\'è ora! Tutti che vogliono usare gli anelli e nessuno che chiede mai come stiano gli anelli! Devo dire alla Regina di dare una sistemata ai suoi sudditi!', '', 38134),
(206102, 'itIT', '$gBenvenuto:Benvenuta; all\'Anello, giovane.$b$BSai, non a tutti è permesso scendere qui. Solo i miei più fidati dei Fungomanti possono entrare nella mia dimora, il punto nevralgico degli anelli dei Silfi.$B$BQuesto significa che ora tu sei $guno:una; dei miei Fungomanti! Continua l\'ottimo lavoro che stai facendo e vedo un futuro radioso per te, ne sono certo.', '', 38134),
(206101, 'itIT', 'Spero non sia troppo caldo...', '', 38134),
(206100, 'itIT', 'Ho sentito che il Distretto delle Braci è luminoso, vero?', '', 38134),
(206099, 'itIT', '', 'La Corte delle Braci, ovviamente...', 38134),
(206098, 'itIT', '', 'Molto bene, la corte ti giudicherà.', 38134),
(206096, 'itIT', '', 'Nessuno ti ha $gseguito:seguita;, vero?', 38134),
(206095, 'itIT', '', 'Dimmi che hai qualcosa di interessante di cui parlare, Calcafauce.', 38134),
(206091, 'itIT', 'Oh, il Semi Interrato! Sono felice che esista ancora. Ultimamente ti stai dando un gran daffare, vai e prenditi una pausa. Torna solo quando ti sarai $grilassato:rilassata; un po\'!', '', 38134),
(206088, 'itIT', 'La tua ambizione ha superato le tue abilità, Calcafauce?', 'La tua ambizione ha superato le tue abilità, Calcafauce?', 38134),
(206085, 'itIT', 'Che abilità di combattimento, Calcafauce! La tua sete di sangue non è ancora stata saziata?', '', 38134),
(206077, 'itIT', 'Bolvar soffre. Forze oscure si muovono contro di noi.', '', 38134),
(206073, 'itIT', 'Dopo aver combattuto tante aspre battaglie e guerre, è... interessante... avere così tante risposte su cosa accadrà in futuro.', '', 38134),
(206070, 'itIT', '', 'Centurioni. Questi costrutti, portati in vita dall\'Animum, servono molti scopi nel Bastione.\n\nAiutano i nostri Aspiranti nell\'addestramento, difendono le nostre terre ed eseguono i compiti umili se necessario. Alcuni, come la tipologia Mnemi alla quale appartengo, si prendono cura di un patrimonio di conoscenza per conto degli archivi.', 38134),
(206069, 'itIT', '', '<L\'Anfitriona Mnemi fa una serie di suoni simili a una campana.>\n\nAh. Gufi. Nel tuo regno sono gli uccelli notturni, vero? \n\n<L\'Anfitriona Mnemi fa dei gesti a Sika.>\n\nQuesto è un Factotum. Si manifestano dall\'Animum del Bastione per aiutare i Kyrian nei loro compiti quotidiani. Non desiderano nient\'altro che essere d\'aiuto.', 38134),
(206060, 'itIT', 'Indossi tanta roba. Non ti stanchi di correrci dentro?', 'Indossi tanta roba. Non ti stanchi di correrci dentro?', 38134),
(206055, 'itIT', 'Firmi la mia pergamena?!?', 'Firmi la mia pergamena?!?', 38134),
(206054, 'itIT', 'Sono un tuo grande ammiratore, $n!', 'Sono una tua grande ammiratrice, $n!', 38134),
(206052, 'itIT', 'Ci si potrebbe immaginare che tu abbia delle cose molto più interessanti da fare invece che venire a parlare con me, eh?', '', 38134),
(206050, 'itIT', 'Cosa scompare appena pronunci il suo nome?', '', 38134),
(206049, 'itIT', '<Sire Renard sogghigna. Ha tantissimi denti.>', '', 38134),
(206048, 'itIT', 'Mmmmmm. Sì, credo che tu andrai bene.', '', 38134),
(206047, 'itIT', 'Bene, bene, bene! Cosa vedo qui davanti a me? $gUn:Una; semplice $C, interessante.', '', 38134),
(206042, 'itIT', '', 'Sei tu, Calcafauce? Sì, Balmedar mi ha informato del tuo stratagemma di prima.\n\nTuttavia, il tuo contributo alla Casata dei Rituali non può essere ignorato. Ti considero $gun alleato:un\'alleata;, quindi non smaschererò il tuo travestimento alle mie cavie.\n\nDivertiti, \"$gaccolito:accolita;\".', 38134),
(206041, 'itIT', '', 'Affrontare direttamente un Margravio è oltre la tua portata, $gaccolito:accolita;.\n\nTi suggerisco di lasciar perdere.', 38134),
(206039, 'itIT', 'È certamente un pezzo d\'armatura interessante quello che porti con te, $gaccolito:accolita;.\n\nSpero che tu non stia pensando a un cambio di vocazione.', '', 38134),
(206037, 'itIT', 'Spero che possiamo finalmente trovare la pace.', 'Spero che possiamo finalmente trovare la pace.', 38134),
(206036, 'itIT', 'Non posso fare a meno di pensare a chi non c\'è più. Perché è dovuto accadere?', 'Non posso fare a meno di pensare a chi non c\'è più. Perché è dovuto accadere?', 38134),
(206035, 'itIT', 'La nostra nuova dimora... è bellissima.', 'La nostra nuova dimora... è bellissima.', 38134),
(206034, 'itIT', 'È bello trovarsi nuovamente nella foresta. Ti ringrazio.', 'È bello trovarsi nuovamente nella foresta. Ti ringrazio.', 38134),
(206033, 'itIT', 'È bello volare di nuovo in alto!', '', 38134),
(206016, 'itIT', '', 'Non ho mai visto un conflitto così grave. Gli insegnamenti della mia Matrona mi hanno preparata a questo, anche se di tanto in tanto mi sento sopraffatta.\n\nTi prego, sappi che condivido le mie preoccupazioni in via confidenziale. La baronessa rimprovererebbe la mia mancanza di coraggio.', 38134),
(206014, 'itIT', 'Ti sei $gdimostrato:dimostrata; utile, $n.\n\nSe mai tornassi a Maldraxxus con mezzi convenzionali, sarai $gil benvenuto:la benvenuta; nella Casata dei Rituali.', '', 38134),
(206013, 'itIT', '', 'L\'ultima volta che sono stata qui, il Primus era ancora in vita.\n\nOra che siamo tornati non posso lasciare la forgia incustodita, non me la farebbe passare liscia.', 38134),
(206008, 'itIT', '', 'Come ricompensa aggiuntiva per il tuo aiuto, sentiti $glibero:libera; di usare il mio manichino da stilista.\n\nTi permetterà di personalizzare il tuo aspetto, anche se dovrai acquisire gli altri accessori per conto tuo.', 38134),
(206007, 'itIT', '', 'Hai bisogno di me, $gaccolito:accolita;?', 38134),
(205988, 'itIT', 'Ricorda di essere $gdiscreto:discreta;, $n.\n\nNessuno deve sapere del nostro... accordo.', '', 38134),
(205975, 'itIT', 'Be\', sto cercando di badare a questi funghi per Marasmio. Questa siccità gli ha davvero scosso le radici.', 'Be\', sto cercando di badare a questi funghi per Marasmio. Questa siccità gli ha davvero scosso le radici.', 38134),
(205973, 'itIT', 'Ehilà!\n\nScusa, non ho tempo per le chiacchiere! Questi funghi non si crescono da soli.\n\n<Ridacchia.>\n\nPosso aiutarti?', 'Ehilà!\n\nScusa, non ho tempo per le chiacchiere! Questi funghi non si crescono da soli.\n\nZacki ridacchia.>\n\nPosso aiutarti?', 38134),
(205972, 'itIT', 'Le mie scuse, Calcafauce, ma hai rifiutato l\'ospitalità del mio santuario.', '', 38134),
(205971, 'itIT', '', 'Venimmo in cerca di giustizia. Di castigo. Per rispondere a un affronto commesso verso la Luce stessa.\n\nLe nostre forze erano sconfinate. Innumerevoli. Ogni cuore bruciava di furia virtuosa.\n\nMolti caddero, ma non prima che scatenassimo un fuoco che i nostri nemici non poterono sopportare. La cicatrice furiosa della nostra vendetta persiste ancora nel firmamento.\n\nRimasi a pezzi sul campo di battaglia, finché gli invisibili non mi trovarono e mi custodirono, per servire da tramite per la rabbia del Sire.\n\nMa resistetti. Sapevo che la Luce aveva un destino. Per me. E anche per la tua gente, mortale.', 38134),
(205969, 'itIT', '', 'Casa. Mi chiama, mortale. Tuttavia, non sentirò mai più il suo calore.\n\nLa mia gente non mi vedeva per quello che ero. Mi vedevano come un\'aberrazione. Spaccata. Contaminata dal tocco del Maestro oscuro di questo regno.\n\nOra c\'è solo una strada che mi aspetta: assicurarmi che la Luce arrivi anche in questo posto. Che sia fatta giustizia. E che la Morte venga messa al suo posto. \n\nPer questo resto qui, legata dal dovere. Fino a che la luce non mi abbandonerà.', 38134),
(205968, 'itIT', 'Ci vorrà del tempo. Tempo che i tuoi simili devono ancora misurare.\n\nMa durante quel tempo, vedo il mio più vecchio amico, il mio confidente più fidato, e il mio più grande Maestro farsi avanti da questa grande critica, col suo vero scopo nuovamente affermato.', '', 38134),
(205965, 'itIT', '', 'Vorrei raccontare quello che è diventato il giorno più significativo nella nostra lunga storia.', 38134),
(205964, 'itIT', '', 'Era un giorno come tutti gli altri. Gli Aspiranti si stavano preoccupando dei loro viaggi. I Factotum li stavano assistendo. Gli Ascesi venivano e andavano da Oribos come al solito.', 38134),
(205963, 'itIT', '', 'Lo ricordo così chiaramente... il flusso costante di anime da Oribos verso il nostro regno, semplicemente... si fermò. Un momento prima stavamo dando il benvenuto agli ultimi arrivi, un momento dopo non c\'era più nessuno.', 38134),
(205962, 'itIT', '', 'All\'inizio pensavamo fosse solo una pausa. Dopo tutte, le nobili anime degne del Bastione non sono così comuni! Ma poi passò un giorno. Poi due. Poi dieci.', 38134),
(205961, 'itIT', '', 'Il nostro declino è lento. Marchiato da un cauto ma infondato ottimismo. Non era mai successo prima. Ma alla fine... devo chiedermelo... chi siamo noi senza nuovi Aspiranti da addestrare?', 38134),
(205960, 'itIT', 'Come da tradizione tra i Forgialiti Supremi giunti prima di me, ho inventato qualcosa di nuovo!', '', 38134),
(205959, 'itIT', 'Abbiamo molti Centurioni, tutti a scopi diversi. Grossi e forti. Veloci e volanti. Rapidi e in grado di cavalcare, sì.', '', 38134),
(205958, 'itIT', 'Ho pensato: cosa non fanno i Centurioni? Parlano, ma non sono intelligenti. Non hanno conoscenza. Perché no?', '', 38134),
(205957, 'itIT', 'Io sono il Forgialita Supremo, così ne ho fatto uno. Ho preso un Pretore e gli ho dato un nucleo mentale. Si ripete troppe volte, ma risponde alle domande! Domande molto... molto semplici.', '', 38134),
(205956, 'itIT', 'Se penso a tutte le possibilità! Tutte le conoscenze che possiamo conservare! La chiamerò... Mnemi. Sì. Geniale!', '', 38134),
(205955, 'itIT', '', 'Ho incontrato un Aspirante affascinante, di recente. Il suo nome è Lysonia e abbiamo legato grazie alla nostra condivisa affinità musicale.', 38134),
(205954, 'itIT', '', 'Lei suona l\'arpa, io il flauto. Abbiamo suonato assieme per ore prima di separarci. Siamo rimasti d\'accordo che ci saremmo addestrati insieme alla prima occasione utile.', 38134),
(205953, 'itIT', '', 'Quando ci siamo incontrati nell\'arena di combattimento, è stato come partecipare a un ballo. I nostri movimenti erano così sincronizzati. Ho quasi dimenticato che eravamo là per addestrarci!', 38134),
(205952, 'itIT', '', 'Dopo, abbiamo trovato un punto tranquillo per guardare verso il Forte Elisio e abbiamo condiviso i nostri sogni per il futuro. Lei spera di unirsi a Devos nel Tempio della Lealtà, mentre io guardo alla Purezza per il mio destino.', 38134),
(205951, 'itIT', '', 'Mi sento come se la conoscessi da eoni. Pensi che... sarebbe d\'accordo a diventare il mio Vincolo dell\'Anima?', 38134),
(205950, 'itIT', 'Sei così forte e coraggioso, Grufo. Sono fiero di te.', '', 38134),
(205949, 'itIT', 'Sì! È così entusiasmante. Credo un po\' di riposo sia proprio ciò che mi serve per tornare in battaglia a difendere Selvarden.', '', 38134),
(205948, 'itIT', 'Grufo! Hai sentito? Siamo stati invitati a una festa!', '', 38134),
(205946, 'itIT', '2...', '2...', 38134),
(205945, 'itIT', 'Il nostro codice ci impone di ripetere queste prove finché non si ha la completa padronanza.', 'Il nostro codice ci impone di ripetere queste prove finché non si ha la completa padronanza.', 38134),
(205944, 'itIT', 'Fermeremo l\'inseguimento! La Casata Peccazanna sta con la Casata Landalivida!', 'Fermeremo l\'inseguimento! La Casata Peccazanna sta con la Casata Landalivida!', 38134),
(205942, 'itIT', 'NON AVEVO FINITO, $gLADRO:LADRA;!', 'NON AVEVO FINITO, $gLADRO:LADRA;!', 38134),
(205941, 'itIT', '1...', '1...', 38134),
(205940, 'itIT', 'Conterò fino a cinque.', 'Conterò fino a 5.', 38134),
(205939, 'itIT', 'Cosa stai facendo? Scendi da Balthazar immediatamente!', 'Cosa stai facendo? Scendi da Balthazar immediatamente!', 38134),
(205938, 'itIT', 'Kevin ti raggiungerà dai nostri nuovi amici dopo aver preso una scorciatoia. Guidali semplicemente verso di lui e penserà al resto!', '', 38134),
(205919, 'itIT', '', 'Anche se sono sicuro che Teotar si divertirebbe per un tale stupidaggine. Mmm.', 38134),
(205918, 'itIT', '', 'No. Troppo diretto.', 38134),
(205913, 'itIT', 'Con cosa vuoi pagare oggi, $p?\n\nMonete o morte?', '', 38134),
(205876, 'itIT', 'Vieni da me, anima ribelle. Vorrei scambiare due parole con te.', 'Vieni da me, anima ribelle. Vorrei scambiare due parole con te.', 38134),
(205862, 'itIT', 'Questa zona fornisce accesso ai servizi relativi ai tuoi beni materiali. Siamo relativamente nuovi a tali concetti, quindi gli Alienatori si sono gentilmente offerti di agevolare questi servizi per te.', '', 38134),
(205856, 'itIT', 'Oooh, non dirò certo di no. Qualcosha di dolce, qualcosha di lishcio. Hai mai bevuto un animai tai? Quelli di Mak shono i migliori da queshte parti.', '', 38134),
(205855, 'itIT', 'Me ne sherve decisamente un\'altra, per il pashticcio in cui mi shono cacciato. Vedi she rieshci a trovarmi qualcosha di forte. Forshe dei peli di falena potrebbero farmi rinshavire e aiutarmi a ricordare.', '', 38134),
(205854, 'itIT', 'Davvero? Mi compreresti qualcosha da bere? Shei $gil mio nuovo miglior amico:la mia nuova miglior amica;. Volevo sholo che lo sapesshi.$b$B Mi piacerebbe una bella Shpriggan Colada. Ha quel gushto che ti fa corrugare le labbra coshì tanto che ti fanno male le guance! Ah!', '', 38134),
(205853, 'itIT', 'No, grazie! Ne ho già bevuti troppi. Devo <hic!> shmaltire la shbornia!', '', 38134),
(205850, 'itIT', 'Ehilà... <hic!>ti dishpiace aiutarmi?$b$BMi chiamo Luccichino e shono un maeshtro delle illusioni! Almeno shono piuttosto shicuro di esserlo. Ora, ammetto di aver bevuto un po\' troppo, e uh... <hic!>$b$bNon rieshco più a capire quale shia il vero me. Immagino che queshta shia quella che chiamano <hic> una crisi esistenziale. Oh, mamma.', '', 38134),
(205849, 'itIT', '<Il Gran Maestro Vole ti squadra dall\'alto in basso.>\n\nAndrai bene.\n\n<Il Gran Maestro Vole urla agli altri attorno a lui.>\n\nCarne fresca per il Teatro del Dolore!', '', 38134),
(205848, 'itIT', 'È stata un\'ottima esibizione. Non per vantarmi, ma ho già ricevuto diversi complimenti.$B$BTi ringrazio, $gamico mio:amica mia;. Non capita spesso di trovare qualcuno col mio stesso talento musicale.', '', 38134),
(205846, 'itIT', 'Oh, Silfi uniti, oh Silfi uniti, ohhh!', '', 38134),
(205845, 'itIT', 'Donate l\'Animum al Cacciatore,', '', 38134),
(205844, 'itIT', 'E nuove missioni iniziamo!', '', 38134),
(205843, 'itIT', 'E per questo esultiamo,', '', 38134),
(205842, 'itIT', 'L\'eroe supera la prova', '', 38134),
(205841, 'itIT', 'Ma non è cosa nuova,', '', 38134),
(205840, 'itIT', 'E il suo eco tonante.', '', 38134),
(205839, 'itIT', 'Con un colpo trionfante...', '', 38134),
(205838, 'itIT', 'Il lupo ormai l\'ha ucciso,', '', 38134),
(205837, 'itIT', 'Uno scatto all\'improvviso,', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205836, 'itIT', 'Con le fauci in attesa!', '', 38134),
(205835, 'itIT', 'La preda è distesa,', '', 38134),
(205834, 'itIT', 'Nei pantani foschi...', '', 38134),
(205833, 'itIT', 'Attraverso i cupi boschi...', '', 38134),
(205832, 'itIT', 'Per la Regina, è fantastica! Sei $gsicuro:sicura; che non eri un bardo nel tuo mondo precedente?$b$B<Narciso si prende qualche momento per ricomporsi.> Perfetto, credo essere pronto. Vuoi sentirla?', '', 38134),
(205831, 'itIT', 'Che cos\'è un\'umanità? Sei tu? Tu sei un\'umanità? Questa canzone non è per te, sai? È per ispirare i Silfi della Notte in tutta Selvarden, e soprattutto i qui presenti, nel credere che siamo degli eroi degni di elogi, adulazioni e una ricompensa!', '', 38134),
(205830, 'itIT', 'Orecchiabile, ma un po\' prevedibile. Inoltre non ci sono tutte queste valli a Selvarden, che cosa buffa!', '', 38134),
(205829, 'itIT', 'Mi piace! C\'è un invito ad agire e ricorda a tutti che è impegnato a mantenere la pace. Proviamola. E ora, il gran finale!$B$BDonate l\'Animum al Cacciatore, oh... oh...', '', 38134),
(205828, 'itIT', 'Anche se così si dipinge una situazione molto piacevole, credo che chiunque preferirebbe tenersi le proprie mani attaccate ai polsi, grazie tante.', '', 38134),
(205826, 'itIT', 'Ascolta, tutto fantastico, ma stavo sperando in qualcosa di un po\' più... sostanzioso. Essere un eroe è impegnativo, sai? E l\'impegno si merita un compenso, se capisci cosa intendo.', '', 38134),
(205825, 'itIT', 'Per così tanto tempo ho aiutato i giovani e ambiziosi Elfi a trovare il loro cammino.\n\nOra sono all\'inizio della mia avventura. Mi chiedo se è così che si sentissero a mettere piede in un nuovo bosco per la prima volta.', '', 38134),
(205824, 'itIT', 'Fa\' attenzione a ciò che ti circonda! Se finisci nelle schegge potrebbe verificarsi un disastro a cascata.', '', 38134),
(205822, 'itIT', '', 'Riconosco che mi hai salvata da quel luogo oscuro e terribile, e di questo ti sono grata.\n\nMa non dimenticherò la sofferenza che l\'Orda ha inflitto alla mia gente.\n\nBisogna ancora fare giustizia.', 38134),
(205821, 'itIT', '', 'La guerra ha preteso un tremendo tributo dalla nostra gente. Più di chiunque altro.\n\nNon può restare impunito.\n\nSpero che sarai al nostro fianco quando avremo finalmente la nostra vendetta.', 38134),
(205817, 'itIT', 'Ohhh, belle parole. Spesso educare l\'ascoltatore è il dovere dell\'umile menestrello, e il lessico è un ottimo inizio!$b$BAspetta, come sapevi che aveva le fauci spalancate?$b$BComunque, ecco la prossima strofa:\nMa non è cosa nuova\nL\'eroe supera la prova\nE per questo esultiamo\n... Mmm, esultiamo, e poi... ?', '', 38134),
(205816, 'itIT', 'Be\', non è molto drammatica, vero? Narciso l\'Impavido ha pugnalato la bestia che stava dormendo! No, no, così non funziona. Mmm...', '', 38134),
(205815, 'itIT', 'Apprezzo la tua devozione alla rima, ma stavamo combattendo contro un boggart. Nonostante spesso distorca un po\' la verità nelle mie ballate, penso che qui il salto sia un po\' troppo evidente, se capisci cosa intendo.$b$B<Narciso ti fa l\'occhiolino in maniera evidente.>', '', 38134),
(205814, 'itIT', 'Grande! Ecco la prima strofa:\nAttraverso i cupi boschi\nNei pantani foschi\nLa preda è distesa\n... E non mi viene altro. Qualche idea?', '', 38134),
(205812, 'itIT', 'Grazie per avermi riportato indietro.\n\nSe deciderai di tornare alla Fauce, potresti provare a cercare qualcuno?\n\nSi chiama Delaryn.', '', 38134),
(205810, 'itIT', 'I Boccioli dei Drust ti forniscono una protezione dagli attacchi della creatura, ma solo per un breve periodo di tempo.', '', 38134),
(205809, 'itIT', 'Oh, salve di nuovo! Avrei giurato che ci fossimo incontrati prima d\'ora...$B$BNon importa, arrivi giusto in tempo. Temo di avere il blocco dello scrittore e mi chiedevo se potessi aiutarmi con un paio di testi. È una canzone che parla del mio nobile amico laggiù e della nostra battaglia contro una creatura spaventosa.', '', 38134),
(205808, 'itIT', '', '<Mak versa il liquido in un miscelatore e comincia a scuotere vigorosamente.>\nUcciderti? Perché? Sei $gvivo:viva; o cosa? AH!$b$BOh, sei $gserio:seria;. Nah, il succo di gorm va bene per te, aggiunge un tocco di dolcezza finale. \n<Mak ti porge il bicchiere.>\nGustalo!', 38134),
(205807, 'itIT', '', '<Mak ti squadra per bene.>$b$bSì, credo di avere la cosa giusta per te. Però mi servirà del succo di gorm di Tempesta, e l\'ho appena finito. Ti dispiacerebbe andare a prenderne un po\' laggiù da Spremucchio? Non preoccuparti di fargli del male, gli piacciono le attenzioni.', 38134),
(205806, 'itIT', '', 'Elune non ci ha dimenticati. Tu, salvandoci, ne sei la prova inconfutabile.\n\nMi chiedo se ci sia ancora un ruolo da svolgere per noi, un dovere da compiere.\n\nDobbiamo essere pazienti.', 38134),
(205805, 'itIT', '', 'Le ambizioni dei Necrosignori sono minacciate dalle nostre stesse mani. Se dovessi ancora unire la tua forza alla nostra, combatteremo insieme per unificare i frammenti di un\'armata capace di sfidare perfino il destino stesso.', 38134),
(205804, 'itIT', 'La strada di Revendreth verso la redenzione è molto lunga, ma il Principe Oscuro di Oblio del Peccato è pronto a percorrerla. Ci hai già aiutato a fare i primi passi in questo viaggio, ora dobbiamo sforzarci di sottrarre il controllo di Revendreth dalle grinfie del Maestro e dei suoi lealisti.', '', 38134),
(205803, 'itIT', '', 'L\'odio è stato l\'origine di tutto questo.\n\nMa spero che alla fine la giustizia trionfi.', 38134),
(205801, 'itIT', '', 'Quando ho chiuso gli occhi, pensavo che avrei visto il volto di Elune.\n\nTi ringrazio per non averci abbandonati.', 38134),
(205800, 'itIT', '', 'Come hai visto con i tuoi occhi, i boschi di Selvarden stanno morendo. Con ogni grande albero celestiale perduto, la disperazione della Regina non fa altro che crescere. Lei e la sua Corte della Notte conoscono la tua forza, aiutaci a ripristinare l\'equilibrio a Selvarden.', 38134),
(205798, 'itIT', 'I nostri nobili Kyrian affrontano le gravi conseguenze dei tradimenti a cui hai assistito al Bastione. Le forze degli Indegni minacciano di far crollare tutto il regno. Il tuo aiuto sarà prezioso se vogliamo rivendicare il Bastione e salvare le Terretetre.', '', 38134),
(205797, 'itIT', 'Devi essere veloce per superare questa sfida! Fa\' attenzione anche al vortice di Alderyn. L\'occhio del ciclone è il luogo più sicuro.', '', 38134),
(205796, 'itIT', 'Queste creature sono pericolose. Fai in modo di mantenere le distanze e non lasciare che ti facciano finire in nessun rifiuto schifoso!', '', 38134),
(205795, 'itIT', 'Attenzione a quelle lance. Tre ti fermeranno, quindi la chiave è usare qualsiasi oggetto o abilità che può liberarti!', '', 38134),
(205794, 'itIT', 'Brilly, sarai il nostro Principe dei Dragatori con quel cappello.', '', 38134),
(205793, 'itIT', 'Questo sì che è un cappello stravagante. Guarda quanto spacca.', '', 38134),
(205791, 'itIT', 'Hai mai visto una foresta così splendida? Che alberi!\n\nGrazie per avermi tirato fuori da quell\'orribile posto, ora posso vederla con i miei occhi.', '', 38134),
(205790, 'itIT', 'Be\', non è un vero schianto?', '', 38134),
(205789, 'itIT', 'Vuoi davvero indossare quel cappello alla corte? Va bene. D\'accordo. Tu fai ciò che vuoi, Squartacus. Finché possiamo andare.', '', 38134),
(205788, 'itIT', 'Ahah! Guardami. Carino, rosa e appuntito.', '', 38134),
(205785, 'itIT', '', 'Ma guardati!\n\nBe\', forse posso aiutarti un po\' con una pergamena per farti tornare alla zona principale di quest\'isola. Siamo piuttosto lontani dal percorso previsto, dopotutto.', 38134),
(205783, 'itIT', '', 'Bene! Tu devi essere $gun:una; grande $R, giusto? Non vedevo qualcuno come te da un po\'.\n\nSei qui per nasconderti da tutta questa folle negromanzia? Non so come faccia la tua razza a respirare sott\'acqua.\n\nStiamo aspettando che tutto questo finisca prima di ripartire. Gli do cinque, dieci anni. Non troppo per me.\n\nPotrebbe essere un po\' troppo per te, però...', 38134),
(205781, 'itIT', '', 'Certo che lo conosco. Gwynceirw, in carne e ossa! E chi può dimenticarsi del suo compagno fidato, Narciso?$b$bGuarda, non causare problemi. L\'ultima volta che un paio di Spriggan sono venuti qui cercando uno scontro, hanno attaccato briga con lui. Mi ci sono voluti secoli per ripulire il posto. Come fanno le interiora a finire sul soffitto? Da dov\'è venuto fuori tutto quel sangue???', 38134),
(205779, 'itIT', '', 'Certo, ma non andare a spezzarle. Marasmio si lamenta sempre di finire impigliato nelle arpe, non chiedermi COME faccia.', 38134),
(205778, 'itIT', '', 'Oh, Luccichino? Sì, sta bene. Adora le illusioni, ma a volte è così ubriaco che non si ricorda quale sia quello reale.$b$bQuello vero, però, può far andare via gli altri. La sua bevanda preferita è l\'animai tai, dice che è la più dolce e raffinata che ha trovato in tutta Selvarden. $b$bSapendo quanto beve, sono molto orgoglioso di questo complimento. Vuoi provarne uno?', 38134),
(205777, 'itIT', 'Sì, sei camuffato. Nessuno saprà che sei tu, Squartacus.', '', 38134),
(205776, 'itIT', 'Ohhh! Ehi! Guarda il mio cappello, Mazzafaccia. È nuovo fiammante... sul serio!', '', 38134),
(205771, 'itIT', 'Tutti i nostri sforzi ci hanno condotto a questo istante.', '', 38134),
(205757, 'itIT', 'I Factotum vengono qui per scaricare lo stress... Questo posto è come il loro nido.', '', 38134),
(205756, 'itIT', 'Fidati sempre dei consigli culinari dei Factotum. Sono l\'ultimo grido in fatto di cucina, o dovrei dire ultimo bubbolio?', '', 38134),
(205750, 'itIT', 'Sai perché i Factotum hanno dei muscoli possenti? Perché chi riandrebbe a prendere l\'equipaggiamento dei Kyrian se non loro?', '', 38134),
(205748, 'itIT', '', 'I Factotum dedicano la propria vita a prendersi cura non solo dei Kyrian, ma di tutto il Bastione.\n\nOgnuno di loro si merita un trattamento di bellezza. Quando arrivano in questo ritiro, speriamo che trovino dei momenti di riposo e di rilassamento.', 38134),
(205746, 'itIT', 'Quando vuoi, Calcafauce.', '', 38134),
(205743, 'itIT', 'Serve un posticino?', 'Serve un posticino?', 38134),
(205742, 'itIT', 'Mi gira la testa, forse ho esagerato...', 'Mi gira la testa, forse ho esagerato...', 38134),
(205741, 'itIT', 'Ottimo per riposare. Fa riprendere i muscoli!', 'Ottimo per riposare. Fa riprendere i muscoli!', 38134),
(205738, 'itIT', 'Ti serve aiuto con qualche questione magica?', '', 38134),
(205737, 'itIT', 'Hai una bella pelle, posso averla quando hai finito?', '', 38134),
(205734, 'itIT', 'C\'è qualche ferita che ha bisogno di cure?', '', 38134),
(205733, 'itIT', 'Non ho mai perso la speranza.', 'Non ho mai perso la speranza.', 38134),
(205732, 'itIT', 'Ci sarà mai una fine a questo tormento?', 'Ci sarà mai una fine a questo tormento?', 38134),
(205731, 'itIT', 'Ho pregato affinché la Dea ci ascoltasse...', 'Ho pregato affinché la Dea ci ascoltasse...', 38134),
(205730, 'itIT', 'È finita?', 'È finita?', 38134),
(205726, 'itIT', '', 'Non sarò mai spezzata.', 38134),
(205725, 'itIT', 'Mi manca Vallombra.', '', 38134),
(205722, 'itIT', 'Dov\'è Delaryn?', '', 38134),
(205720, 'itIT', 'Tutto andava a fuoco... Anche i germogli...', '', 38134),
(205717, 'itIT', 'Fungomante $gonorario:onoraria;, eh? Sembra che tu vada a genio a Marasmio, $gbenvenuto:benvenuta; nella squadra!$B$BEhm, c\'ero solo io prima, ma sono felice che ora ci sia anche tu! Come parte dei tuoi doveri di Fungomante avrai accesso a tutti i sentieri di Marasmio. Inoltre, potrai usufruire di una serie di benefici ed equipaggiamenti esclusivi. Mentre aiuti me e Marasmio otterrai degli emblemi da scambiare con me.', '', 38134),
(205716, 'itIT', 'L\'Anello era una specie di luogo di passaggio, il fulcro da cui viaggiare quasi ovunque a Selvarden. $b$BCon il passare del tempo e la recente siccità, anche Marasmio ha perduto alcuni dei suoi sentieri. Forse con abbastanza Animum si potrebbero invertire i danni...$b$bPer ora è dovere dei Fungomanti prendersi cura di Marasmio. Ricostruiremo le radici di Selvarden più forti di prima, e ci assicureremo la sua sopravvivenza.', '', 38134),
(205715, 'itIT', 'Ti do il benvenuto all\'Anello. Alcuni dicono che questo è il centro di Selvarden o dove Marasmio è cresciuto. Solo lui lo sa per certo.', '', 38134),
(205714, 'itIT', '', 'Così tanta oscurità...\n\nHo fatto tutto il possibile per salvarli, o no?', 38134),
(205711, 'itIT', 'Abbiamo salvato la Principessa Talanji degli Zandalari dalle Segrete di Roccavento. \n\nLa sua imbarcazione è in viaggio verso Zandalar, ma quei cani dell\'Alleanza la stanno inseguendo.\n\nAbbiamo bisogno della potente flotta degli Zandalari se vogliamo vincere questa guerra. Non possiamo permettere che l\'Alleanza catturi di nuovo la principessa.\n\nViaggeremo per via aerea per fornire assistenza e faremo in modo che gli Zandalari si uniscano all\'Orda.\n\nVieni.', '', 38134),
(205709, 'itIT', '', 'C\'era del fuoco... e poi...\n\nHo cantato.\n\nDea, cosa sta succedendo?', 38134),
(205704, 'itIT', 'Qui si vive facile. Uno potrebbe pure abituarcisi.', '', 38134),
(205699, 'itIT', 'TU NO PRENDERE CANDELA! Nooo!', '', 38134),
(205698, 'itIT', 'È un peccato che abbiamo finito i nemici da massacrare.\n\nQuando torneranno, sarò pronto.', '', 38134),
(205687, 'itIT', 'Chi è affamato?', '', 38134),
(205674, 'itIT', 'Piangiamo ciò che abbiamo perso, sia che si tratti di una cosa che abbiamo avuto, sia che si tratti di un futuro che non si avvererà mai. Abbandona le aspettative che il possesso ti offre e conoscerai la pace.', '', 38134),
(205673, 'itIT', 'Liberarsi dalle aspettative che avevamo e lasciar andare la paura e il dolore della perdita, questo vuol dire accettare le cose per quelle che sono.', '', 38134),
(205672, 'itIT', 'È doloroso vedere ciò che un tempo era, ora non esistere più. Ricordiamo la gioia che abbiamo provato, lo stupore e i legami.', '', 38134),
(205671, 'itIT', 'Temiamo di non avere più qualcosa che un tempo possedevamo, o di non provare ciò che pensavamo di provare.', '', 38134),
(205639, 'itIT', '', 'Ehilà, $gbenvenuto:benvenuta; al Semi Interrato!$b$BCosa posso portarti?', 38134),
(205627, 'itIT', '', 'Saremo pronti a difendere la Corte delle Braci. Ci vediamo lì, $n.', 38134),
(205591, 'itIT', 'Al limitare dell\'esistenza, è facile essere sopraffatti dalla perdita. Mmm... Forse dovrei meditare sul significato della perdita. Potresti trovare i miei focus? Dovrebbero essere qui da qualche parte...', '', 38134),
(205590, 'itIT', 'Se qualcuno volesse \"pace e silenzio\", questo sarebbe il posto giusto! Forse possiamo aiutare quell\'anima laggiù.', '', 38134),
(205587, 'itIT', '', 'È stato uno dei peggiori combattimenti che abbia mai visto, recluta. Hai fallito. Vai ad allenarti.', 38134),
(205586, 'itIT', '', 'Un lavoro ben fatto. Recluta, hai superato la prova.', 38134),
(205579, 'itIT', 'Hai l\'aspetto di $gun... artigiano:una... artigiana;?\n\nSe hai le competenze necessarie, posso trasmetterti la mia conoscenza su come creare dei ricettacoli che possano contenere i poteri delle rune.', '', 38134),
(205568, 'itIT', 'Quale professione cerchi?', 'Quale professione cerchi?', 38134),
(205561, 'itIT', '', 'Non mi fido di lui, ma potrebbe essere il nostro unico mezzo per scoprire i loro piani.', 38134),
(205560, 'itIT', '', 'Calcafauce! Il Lich ha un\'idea per farci accedere alla Casata dei Rituali.', 38134),
(205559, 'itIT', 'Eppure non hai altra scelta, vero? A meno che tu non preferisca una missione suicida...', '', 38134),
(205558, 'itIT', '', 'Non approvo questo tuo piano, Lich.', 38134),
(205552, 'itIT', 'Falene Selenia! Devono essere appena nate. Vedi se riesci a catturarne qualcuna e a portarmela.', '', 38134),
(205550, 'itIT', 'Ti avverto: sono ben addestrato nell\'arte del duello.', 'Ti avverto: sono ben addestrata nell\'arte del duello.', 38134),
(205534, 'itIT', 'Un nuovo scopo? Che scopo può esserci senza il mio mondo?\n\nE tuttavia, non posso semplicemente fare nulla. Non è il mio modo di fare.\n\nDevo comprendere quest\'esistenza. Queste... Terretetre. Percepisco molti regni, ma ce ne sono quattro che coprono un ruolo particolare.\n\nPortami qualcosa da questi quattro regni. Qualcosa di organico. La mia razza può comprendere molto dal più semplice tocco.', '', 38134),
(205532, 'itIT', 'Lo saprei se fosse ancora lì. Riesco a sentirne l\'assenza.\n\nChe terribile tristezza. Cos\'è una divinità senza un mondo?\n\nDove vorreste che vada? Cosa vorreste che faccia?', '', 38134),
(205530, 'itIT', 'Selvarden? Allora queste sono le Terretetre? Non sorprenderti che sappia delle Terretetre. Ero una divinità, sai.\n\nConosco le risposte a molte domande. Tuttavia, non so come ho fatto ad arrivare qui, o come il mio mondo abbia smesso di esistere.\n\nImmagino di aver cessato di esistere allo stesso tempo. Anche se ora sono tornato, il mio mondo non l\'ha fatto.', '', 38134),
(205528, 'itIT', 'Allora, cos\'hai da dire? Come ho fatto ad arrivare qui?', '', 38134),
(205522, 'itIT', 'Ah, ah, un altro tocco di là. Fai piano.', 'Ah, ah, un altro tocco di là. Fai piano.', 38134),
(205521, 'itIT', 'Ohhh. Una tonalità così vivace. Hai visto $gamico:amica;?', 'Ohhh. Una tonalità così vivace. Hai visto $gamico:amica;?', 38134),
(205520, 'itIT', 'Guarda come si diffonde bene in superficie. Come una foglia nel vento.', 'Guarda come si diffonde bene in superficie. Come una foglia nel vento.', 38134),
(205519, 'itIT', 'Ho finito. Possa il tuo piacere nel leggere questo pezzo essere pari alla gioia che ho provato nel realizzarlo.', 'Ho finito. Possa il tuo piacere nel leggere questo pezzo essere pari alla gioia che ho provato nel realizzarlo.', 38134),
(205516, 'itIT', 'Insieme... come una famiglia.', 'Insieme... come una famiglia.', 38134),
(205505, 'itIT', 'Il Polemarco Adrestes annuisce.', '', 38134),
(205498, 'itIT', '', 'Il nostro lavoro è appena cominciato.', 38134),
(205497, 'itIT', '', 'Benvenuti nell\'ultimo baluardo dell\'onore di Maldraxxus. Il tuo arrivo qui è stato caotico come lo è stato il mio.', 38134),
(205496, 'itIT', 'Dev\'essere quella! Portami quella lama!', '', 38134),
(205481, 'itIT', '', 'Quando sei $gpronto:pronta; cominceremo l\'attacco!', 38134),
(205472, 'itIT', 'È passato molto tempo da quando Qadarin e io abbiamo combattuto per difendere la nostra dimora con altri campioni al nostro fianco. Non mi è mancata la guerra, ma ho apprezzato stare fianco a fianco con amici e alleati.\n\n<Thiernax trattiene una risata.>\n\nE vedere tali vili corruttori totalmente distrutti.', '', 38134),
(205471, 'itIT', '', 'Non oso pensare a cosa sarebbe successo se i Drust avessero acquisito il controllo di questo luogo.\n\nVedo che c\'è ancora da fare qui a Selvarden, ma almeno questa minaccia è stata respinta.\n\nHo ancora i miei dubbi e continuerò a osservarti, ma almeno siamo d\'accordo sull\'aiutare questo posto.', 38134),
(205470, 'itIT', 'Che pigmento meraviglioso! Non ho mai visto nulla del genere. Devo mettermi al lavoro.', 'Che pigmento meraviglioso! Non ho mai visto nulla del genere. Devo mettermi al lavoro.', 38134),
(205468, 'itIT', '', 'Non oso pensare a cosa sarebbe successo se i Drust avessero acquisito il controllo di questo luogo.\n\nVedo che c\'è ancora da fare qui a Selvarden, ma almeno questa minaccia è stata respinta.\n\nTyrande è ancora da sola a Torgast. La ricerca degli altri Guerrieri della Notte continua, e Ysera e Thiernax sembrano sicuri del loro piano, ma io... mi preoccupo per lei.', 38134),
(205467, 'itIT', '', 'I Drust saranno anche sconfitti, ma Selvarden ha ancora bisogno dei suoi protettori. Combatterò per la Caccia Selvaggia, come fece Ara\'lon, e m\'impegnerò per proteggere tutti i boschi. \n\nQuesto mi dà speranza che forse, un giorno, Selvarden tornerà al suo antico splendore.', 38134),
(205466, 'itIT', 'Be\', è stato emozionante! Sono felice che i Drust siano spariti, hanno avuto una terribile influenza sui nostri boschi e i nostri Semebrado. Spero che la Regina riuscirà a far tornare le cose alla normalità, o almeno un po\' più normali. Ti ringrazio, $n. Hai fatto più di quanto immagini per noi.', '', 38134),
(205465, 'itIT', '', '$gFiglio:Figlia; di un mondo devastato dalla guerra, sei $ggiunto:giunta; da noi come $gstraniero:straniera;. Ora che abbiamo combattuto $gl\'uno:l\'una; accanto all\'altra come $gcompagni:compagne; d\'armi, mi inchino di fronte alla tua abilità nella caccia. La mia lancia sarà sempre a tua disposizione.', 38134),
(205463, 'itIT', 'Persino i più grandi cacciatori cadono. \n\nSiamo in lutto per Ara\'lon e gli altri, ma celebriamo le loro vite. Celebriamo la nostra vittoria! \n\nHai dimostrato il coraggio di Selvarden in battaglia. La leggenda delle tue gesta risuonerà per tutta la foresta, $n. \n\nHai combattuto bene. Meravigliosamente, oserei dire!', '', 38134),
(205459, 'itIT', '', 'Sin dal momento del tuo arrivo sapevo che c\'era qualcosa in te, $n... E avevo ragione! Tu sei... interessante.\n\n<Baccaluna si fa seria.>\n\nE diventerà tutto molto più interessante da queste parti. I Drust sono stati cacciati via, ma stavano solo peggiorando un problema già esistente. In giro c\'è ancora del male, e io so che lo fermeremo!', 38134),
(205458, 'itIT', '', 'Molto è stato dato affinché io continuassi a esistere. Sono felice di aver aiutato a salvaguardare questo luogo e di cominciare a ripagare la compassione ricevuta.\n\nIl potere dei Drust qui è stato spezzato, ma dobbiamo restare in guardia. Le Terretetre... Non tutto è perfetto, qui. Abbiamo ottenuto una vittoria, ma ci sono ancora questioni da risolvere. \n\nPrevedo che il tuo aiuto sarà necessario per le sfide a venire. Preparati.', 38134),
(205457, 'itIT', 'Ben fatto. Potete andare!', 'Ben fatto. Potete andare!', 38134),
(205442, 'itIT', 'L\'influenza della Fauce ha fatto impazzire quei funghi! Dobbiamo fare qualcosa, presto!', '', 38134),
(205441, 'itIT', 'Oh, mi scuso. Ho dimenticato di specificare che avrebbe comportato un po\' di lavoro manuale?', '', 38134),
(205440, 'itIT', 'Dovrai riportare questo cadavere al circolo del rituale.', '', 38134),
(205437, 'itIT', 'Questa forma è certamente... interessante.', 'Questa forma è certamente... interessante.', 38134),
(205436, 'itIT', 'Come servirai Selvarden?', 'Come servirai Selvarden?', 38134),
(205435, 'itIT', 'Che forma vuoi prendere?', 'Che forma vuoi prendere?', 38134),
(205434, 'itIT', 'Che la notte ti benedica.', 'Che la notte ti benedica.', 38134),
(205427, 'itIT', '', 'Ma se fallirai, sulla lista ci scriverò il tuo!', 38134),
(205426, 'itIT', '', 'Insieme, depenneremo alcuni nomi dalla mia lista...', 38134),
(205425, 'itIT', '', 'Usa questo cristallo per proiettare la mia immagine nel piano dei vivi per qualche istante.', 38134),
(205424, 'itIT', '', 'Alcuni degli altri combattenti mi seguiranno. Non che avrai bisogno di loro, con me presente!', 38134),
(205423, 'itIT', 'Draven chiama ME arrogante! Tuttavia, se quella testa di sasso avesse ascoltato i miei avvertimenti su Kel\'Thuzad, ormai avremmo distrutto il Lich! Invece, ora il nostro nemico è al fianco del Carceriere.\n\n<La rabbia sembra fluire negli occhi del principe.>\n\nHo capito che ho commesso degli errori. Forse, col tempo, potrò fare ammenda e dimostrarti nuovamente di meritare la tua fiducia.\n\nTuttavia, cederei volentieri la mia redenzione per vendicare la mia gente. Per coloro che sono morti a causa di Kel\'Thuzad...\n\n<Kael\'thas sospira.>\n\nPerché io ho fallito nel proteggerli.\n\nLasciami stare, $p. Ho molto su cui riflettere.', '', 38134),
(205422, 'itIT', 'Draven chiama ME arrogante! Tuttavia, se quella testa di sasso avesse ascoltato i miei avvertimenti su Kel\'Thuzad, ormai avremmo distrutto il Lich! Invece, ora il nostro nemico è al fianco del Carceriere.\n\n<La rabbia sembra fluire negli occhi dell\'Elfo del Sangue.>\n\nHo capito che ho commesso degli errori. Forse, col tempo, potrò fare ammenda.\n\nTuttavia, cederei volentieri la mia redenzione per vendicare la mia gente. Per coloro che sono morti a causa di Kel\'Thuzad...\n\n<Kael\'thas sospira.>\n\nPerché io ho fallito nel proteggerli.', '', 38134),
(205421, 'itIT', 'Mentre io, il tuo principe, resto confinato qui a Revendreth, condannato a soffrire a causa di poche... scelte discutibili, Kel\'Thuzad è libero. Quel mostro ha quasi preso il controllo di Maldraxxus!\n\n<Kael\'thas ti guarda torvo per un momento, poi i suoi occhi puntano verso il basso.>\n\nNove su dieci di noi, $p, uccisi da Arthas Menethil per rianimare quel Lich. Il nostro nobile regno, quasi del tutto cancellato.\n\nLa sopravvivenza spesso sembra più una penitenza che una benedizione. Ho fatto ciò che potevo per mantenere forte la nostra gente.\n\nMa forse la via che ho scelto è stata...\n\n<Kael\'thas si ammutolisce.>', '', 38134),
(205420, 'itIT', 'Mentre io resto confinato qui a Revendreth, condannato a soffrire a causa di poche... scelte discutibili, Kel\'Thuzad è libero. Quel mostro ha quasi preso il controllo di Maldraxxus!\n\n<Kael\'thas ti guarda torvo per un momento, poi i suoi occhi puntano verso il basso.>\n\nNove su dieci, $p, uccisi da Arthas Menethil per rianimare quel Lich. Un nobile regno, quasi del tutto cancellato.\n\nSanguini ancora, vero? Taglia la tua carne e conta le gocce di sangue. Lasciate che nove cadano e bagnino la terra per ogni singolo individuo che salvi.\n\nDimmi poi che effetto fa al tuo cuore, $p. Io posso dirti esattamente cos\'ha fatto al mio.', '', 38134),
(205419, 'itIT', 'Questo trattamento è oltraggioso! Sono il tuo principe, il tuo legittimo sovrano, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita. Nemmeno tu.', '', 38134),
(205418, 'itIT', 'Suppongo che ti faccia piacere vedermi in questo stato! Potrai avermi sconfitto in battaglia, ma non sarai $gun mio:una mia; pari.\n\nSono un principe dalla nobile stirpe, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita. Nemmeno tu.', '', 38134),
(205417, 'itIT', 'Questo trattamento è oltraggioso! Sono un principe dalla nobile stirpe, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita. Nemmeno tu.', '', 38134),
(205416, 'itIT', 'Tu! Vieni qui, $gsuddito:suddita;! Ti ordino di togliermi subito le catene!\n\n<Kael\'thas cerca di tirare via le sue catene.>\n\nQuesto trattamento è oltraggioso! Sono il tuo principe, il tuo legittimo sovrano, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita! Nemmeno tu!\n\nAccusatrice! Liberami! Ho un regno da vendicare. Mi senti?!? ACCUSATRICE!\n\n<Kael\'thas riprende a tirare le catene in segno di ribellione.>', '', 38134),
(205415, 'itIT', 'Tu! Ehi tu! Esigo che tu mi tolga subito le catene!\n\n<Kael\'thas cerca di tirare via le sue catene.>\n\nSuppongo che ti faccia piacere vedermi in questo stato! Potrai avermi sconfitto in battaglia, ma non sarai $gun mio:una mia; pari!\n\nSono un principe dalla nobile stirpe, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita! Nemmeno tu!\n\nAccusatrice! Liberami! Ho un regno da vendicare. Mi senti?!? ACCUSATRICE!\n\n<Kael\'thas riprende a tirare le catene in segno di ribellione.>', '', 38134),
(205414, 'itIT', 'Tu! Ehi tu! Esigo che tu mi tolga subito le catene!\n\n<Kael\'thas cerca di tirare via le sue catene.>\n\nQuesto trattamento è oltraggioso! Sono un principe dalla nobile stirpe, eppure quella fredda Arbiter ha osato relegarmi in questo cupo regno di pietra grigia e mentalità chiuse.\n\nNessuno di questi Venthyr è degno di giudicare le mie scelte in vita! Nemmeno tu!\n\nAccusatrice! Liberami! Ho un regno da vendicare. Mi senti?!? ACCUSATRICE!\n\n<Kael\'thas riprende a tirare le catene in segno di ribellione.>', '', 38134),
(205412, 'itIT', 'Meglio che non parli con me al momento.\n\nNon sono cieco, vedo benissimo che sei già all\'opera per ottenere la lealtà di un\'altra congrega.', '', 38134),
(205411, 'itIT', 'Ti ho $gvisto:vista; cercare di far buona impressione su di un altro.\n\nNon abbiamo nulla di cui discutere al momento.', '', 38134),
(205410, 'itIT', '', 'Al momento stai prestando la tua forza a un\'altra Congrega. \n\nNon credere che non mi accorga di cose del genere, abbiamo occhi dappertutto.', 38134),
(205409, 'itIT', '', 'Credo che tu stia già cercando di entrare nelle grazie di un\'altra Congrega. \n\nNon devi perdere tempo con me.', 38134),
(205408, 'itIT', 'Ti ho $gvisto:vista; con Dama Baccaluna? \n\nSpero che tu non abbia dato troppo peso a quelle stupidaggini sulla rinascita.', '', 38134),
(205407, 'itIT', 'Perché insisti ad associarti ai Kyrian?\n\nÈ molto meglio che tu sia al servizio dei Venthyr.', '', 38134),
(205406, 'itIT', 'Perché parli così tanto con Draka?\n\nSarebbe meglio utilizzare tutto quel tempo per aiutare i Venthyr come avevi promesso.', '', 38134),
(205405, 'itIT', '', 'Fa\' attenzione ad avere a che fare col Generale Draven.\n\nNon ci si può fidare dei Venthyr. Sono certa che lo saprai già, ma non posso fare a meno di notare il tempo che hai trascorso con lui ultimamente.', 38134),
(205404, 'itIT', '', 'Vedo che continui ad avvicinarti a Draka.\n\nSo che proviene dal tuo regno, ma ora hai dei nuovi amici: noi.', 38134),
(205401, 'itIT', '', 'Tu e i Kyrian sembrate dei grandi amici ultimamente.\n\nTi sei $gstancato:stancata; di servire la nostra Regina? C\'è ancora molto da fare insieme.', 38134),
(205400, 'itIT', '', 'Di cosa stavate discutendo tu e Draven?\n\nNon sapevo che stessimo collaborando con i Venthyr al momento. Non mi fido di loro.', 38134),
(205398, 'itIT', '', 'Non pensavo che fossi $gun amante:un\'amante; della natura, ma ultimamente sembra che trascorri molto tempo con i Silfi della Notte.', 38134),
(205397, 'itIT', '', 'Sembri molto amichevole con i Kyrian ultimamente.\n\nConfido che tu sia ancora leale ai Necrosignori, $n? Abbiamo ancora molto da fare. \n\nNon sopporterei che la nostra relazione s\'inasprisse a causa di un tuo tradimento.', 38134),
(205394, 'itIT', 'Capisco che tu e Draka proveniate dallo stesso regno. \n\nÈ per questo che ti ho visto parlare con lei di recente?', '', 38134),
(205390, 'itIT', 'Ti ho $gvisto:vista; discutere con il Generale Draven prima. \n\nConfido che tu riferisca ogni informazione importante che ha condiviso.', '', 38134),
(205389, 'itIT', 'Ti ho per caso $gvisto:vista; fare affari con Dama Baccaluna di là?\n\nNon ero a conoscenza di nessun incarico che coinvolgesse i Silfi della Notte.', '', 38134),
(205388, 'itIT', '', 'Te lo concedo, $n, sei $gcoraggioso:coraggiosa;.\n\nVuoi provocarmi? Vuoi suscitare una reazione al tuo tradimento? \n\nDovresti andartene. Potrebbe passare un po\' di tempo prima che voglia discutere ancora con gente come te.', 38134),
(205387, 'itIT', '', 'Ci hai mostrato la tua forza ancora una volta.\n\nEssa è solo una parte di ciò che cerchiamo in $gun Necrosignore:una Necrosignora;. Esigiamo anche che tu ci serva ed è qui che in passato non sei $gstato:stata; all\'altezza. \n\nOra va\', discuteremo della tua riammissione alla Congrega. \n\nTorna da me dopo, forse saremo giunti a una conclusione.', 38134),
(205380, 'itIT', 'Sei $gfortunato:fortunata; che non ti spezzi in due!\n\nSe questo non fosse un territorio neutrale, sarei stato davvero tentato.\n\nMeglio che te ne vada finché la mia ira non si placherà.', '', 38134),
(205378, 'itIT', 'Hai fatto i primi passi per rientrare nelle nostre grazie.\n\nDobbiamo valutare più approfonditamente il tuo caso, vieni a parlarmi di nuovo in futuro.\n\nPer allora, forse ti reputeremo $gdegno:degna; di riunirti ai Venthyr.', '', 38134),
(205370, 'itIT', 'Cosa vorresti cambiare nelle spalline e nel cappuccio?', '', 38134),
(205368, 'itIT', 'Quale accessorio vuoi cambiare?', '', 38134),
(205366, 'itIT', 'Quale colore del tuo travestimento desideri cambiare?', '', 38134),
(205365, 'itIT', 'Quale parte del tuo camuffamento vuoi cambiare?', '', 38134),
(205364, 'itIT', '', 'Osi tornare qui e parlarmi?\n\nHai già dimostrato la tua mancanza di lealtà, $n. \n\nForse più in là ti avrò $gperdonato:perdonata;. Forse.', 38134),
(205363, 'itIT', 'Questo manichino è stato usato da Moret l\'Elegante per disegnare i nuovi modelli per gli accoliti della Casata dei Rituali.\n\nPuoi usarlo per modificare l\'aspetto del tuo camuffamento.', '', 38134),
(205362, 'itIT', 'Davvero, $n? \n\nSei $gcoraggioso:coraggiosa; a mostrare la tua faccia davanti a me. Al momento non ho voglia di parlare con gente come te.', '', 38134),
(205360, 'itIT', 'Da questa parte, Calcafauce.', '', 38134),
(205359, 'itIT', '', 'Hai fatto il primo passo per tornare dalla nostra Regina.\n Discutevamo se il tuo ritorno facesse parte del ciclo oppure no.\n\nTi prego di tornare da me più tardi, forse saremo giunti una conclusione. Ti direi che ci dispiace di farti aspettare, ma ti prego di capire che è il tuo precedente tradimento a renderci cauti.', 38134),
(205356, 'itIT', 'Ehilà, $n.\n\nStiamo decidendo se permetterti di riunirti a noi oppure no. \n\nRitorna tra un po\', forse i venti volgeranno a tuo favore.', '', 38134),
(205345, 'itIT', 'Tharon\'ja vede tutto! Le azioni dei mortali non porranno fine alla dinastia eterna!', '', 38134),
(205343, 'itIT', 'Non è giusto! Questi tizi non dovrebbero essere qui! Bisogna dar loro una lezione.', '', 38134),
(205341, 'itIT', 'I segreti delle profondità resteranno nascosti.', '', 38134),
(205335, 'itIT', 'Pezzacarne vuole giocare!', '', 38134),
(205334, 'itIT', 'Hai fatto una pazzia, venendo qui.', '', 38134),
(205330, 'itIT', 'Budella! Sangue! Roba da mangiare!', '', 38134),
(205328, 'itIT', 'Il gelo che sentite anticipa la vostra fine!', '', 38134),
(205323, 'itIT', 'Era giunto il suo momento. Sfortunatamente, l\'unica eternità che l\'attendeva era la Fauce. Decidere tali questioni non spetta a noi, ma all\'Arbiter.', 'Era giunto il suo momento. Sfortunatamente, l\'unica eternità che l\'attendeva era la Fauce. Decidere tali questioni non spetta a noi, ma all\'Arbiter.', 38134),
(205322, 'itIT', 'Questa è stata una morte problematica, ma non è nostro dovere decidere la moralità del suo destino.', 'Questa è stata una morte problematica, ma non è nostro dovere decidere la moralità del suo destino.', 38134),
(205321, 'itIT', '', 'L\'albero è in fiamme! Io... non respiro...', 38134),
(205320, 'itIT', 'Non importa quanto sia tragico, era il momento di andare a Oribos e verso la sua eternità.', 'Non importa quanto sia tragico, era il momento di andare a Oribos e verso la sua eternità.', 38134),
(205319, 'itIT', 'Sacrificarsi per la famiglia è nobile, ma non è un motivo per tornare nella terra dei vivi.', 'Sacrificarsi per la famiglia è nobile, ma non è un motivo per tornare nella terra dei vivi.', 38134),
(205318, 'itIT', 'Stavo duellando con quella testa calda di mio fratello e... non ho potuto sopportare di perdere contro quel verme! Ho sguainato troppo tardi la mia spada e ne ho pagato il prezzo.', 'Stavo duellando con quella testa calda di mio fratello e... non ho potuto sopportare di perdere contro quel verme! Ho sguainato troppo tardi la mia spada e ne ho pagato il prezzo.', 38134),
(205317, 'itIT', 'Come spirito della natura, è stata inviata a Selvarden per aiutare ulteriormente chi ne ha bisogno. Tale era il suo destino.', 'Come spirito della natura, è stata inviata a Selvarden per aiutare ulteriormente chi ne ha bisogno. Tale era il suo destino.', 38134),
(205316, 'itIT', 'Anche se alcune anime strettamente connesse alla natura fanno ritorno dopo essersi riposate a Selvarden, altre trascorrono l\'eternità tra i suoi rami.', 'Anche se alcune anime strettamente connesse alla natura fanno ritorno dopo essersi riposate a Selvarden, altre trascorrono l\'eternità tra i suoi rami.', 38134),
(205315, 'itIT', '', 'Il bosco è in pericolo! I piccoletti non riusciranno a mettersi in salvo senza di me!', 38134),
(205314, 'itIT', 'Non c\'è posto per i demoni nelle Terretetre, salvo i combattenti occasionali di Maldraxxus.', 'Non c\'è posto per i demoni nelle Terretetre, salvo i combattenti occasionali di Maldraxxus.', 38134),
(205313, 'itIT', 'Oh, c\'è qualche errore? Questo è indirizzato al Polemarco Adrestes.', '', 38134),
(205311, 'itIT', 'Corretto. I demoni devono tornare alla Distorsione Fatua da dove provengono.', 'Corretto. I demoni devono tornare alla Distorsione Fatua da dove provengono.', 38134),
(205309, 'itIT', '', 'Mmm! Non percepivo la puntura di una lama del genere da tempo.', 38134),
(205303, 'itIT', 'Anche se le Val\'kyr non fanno parte della nostra gente, il loro richiamo al piano mortale dev\'essere onorato.', 'Anche se le Val\'kyr non fanno parte della nostra gente, il loro richiamo al piano mortale dev\'essere onorato.', 38134),
(205302, 'itIT', 'Ben fatto. Quest\'anima è arrivata da noi in tempo. Anche i Non Morti non possono sfuggire alla morte per sempre.', 'Ben fatto. Quest\'anima è arrivata da noi in tempo. Anche i Non Morti non possono sfuggire alla morte per sempre.', 38134),
(205301, 'itIT', 'Ho sentito il temibile richiamo delle Val\'kyr. Per favore, lasciatemi riposare in pace! Vi supplico!', '', 38134),
(205294, 'itIT', 'Hai dei rubini infusi? Questo Calcapeccati ti porterà fino a Lidoscuro.\n\nNon lasciarti cadere se non vuoi scendere lungo strada.', '', 38134),
(205291, 'itIT', 'Devi capire che il Polemarco non accoglierà mai volentieri dei cambiamenti alla sua routine. Ciò nonostante, sarebbe un bene per lui. Lascia che ci pensi io.', '', 38134),
(205285, 'itIT', 'Oh, andrà bene qualsiasi cosa.\n\nFarò io delle modifiche, dopo.', '', 38134),
(205284, 'itIT', 'Una manciata di rubini infusi e questo Calcapeccati ti scarrozzerà tra i bastioni settentrionali.\n\nLa vista è ottima, ma piena di brutta gente ed energumeni. Non lasciarti cadere se non sei $gsicuro:sicura; di voler finire proprio dove vuoi andare.', '', 38134),
(205282, 'itIT', 'Corretto. Anche se il destino del suo villaggio sarebbe stato alterato se fosse tornata, era giunto il suo momento.', 'Corretto. Anche se il destino del suo villaggio sarebbe stato alterato se fosse tornata, era giunto il suo momento.', 38134),
(205281, 'itIT', 'Anche se molti sarebbero davvero morti durante l\'inondazione che ne seguì, non è nostro dovere intervenire nelle faccende dei mortali.', 'Anche se molti sarebbero davvero morti durante l\'inondazione che ne seguì, non è nostro dovere intervenire nelle faccende dei mortali.', 38134),
(205280, 'itIT', 'Il mio Esplosivatore 5000 ha appena fatto esplodere la diga sopra il mio laboratorio! Tutti i miei esperimenti... ehm, i miei poveri animaletti... sono in grave pericolo!', 'Il mio Esplosivatore 5000 ha appena fatto esplodere la diga sopra il mio laboratorio! Tutti i miei esperimenti... ehm, i miei poveri animaletti... sono in grave pericolo!', 38134),
(205279, 'itIT', 'È giusto. Non importa quanto un\'anima ci supplichi, non dobbiamo cambiare il suo destino.', 'È giusto. Non importa quanto un\'anima ci supplichi, non dobbiamo cambiare il suo destino.', 38134),
(205278, 'itIT', 'Finché il Filatterio del lich rimarrà intatto, dobbiamo mandarlo indietro.', 'Finché il Filatterio del lich rimarrà intatto, dobbiamo mandarlo indietro.', 38134),
(205275, 'itIT', 'Mi sono ritrovato a questo crocevia innumerevoli volte. Vorrei poter essere libero! Donami la pace della morte!', 'Mi sono ritrovata a questo crocevia innumerevoli volte. Vorrei poter essere libera! Donami la pace della morte!', 38134),
(205272, 'itIT', 'Se sei $gdisposto:disposta; a spendere una manciata di rubini infusi allora questo Calcapeccati ti porterà al Varco nel Muro in cima al Castello occidentale.\n\nNon lasciarti cadere se non vuoi scendere lungo strada.', '', 38134),
(205268, 'itIT', 'Calcafauce, raccogline un po\' da portare a casa per i nostri ospiti come ricordo di questo buon presagio.', '', 38134),
(205267, 'itIT', 'Un buon presagio per la nostra corte: l\'Animum si condensa sopra le nostre teste!', '', 38134),
(205251, 'itIT', 'Questa creatura possiede la magia delle Terre del Fuoco e non può essere mandata a Oribos per essere giudicata.', 'Questa creatura possiede la magia delle Terre del Fuoco e non può essere mandata a Oribos per essere giudicata.', 38134),
(205250, 'itIT', 'Ottimo! Al momento della morte, le creature di un altro piano d\'esistenza devono tornare là. Le Terretetre non sono nel loro destino.', 'Ottimo! Al momento della morte, le creature di un altro piano d\'esistenza devono tornare là. Le Terretetre non sono nel loro destino.', 38134),
(205249, 'itIT', 'Gli invasori minacciosi le Terre del Fuoco! Devo tornare subito dalla mia gente!', '', 38134),
(205247, 'itIT', 'Anche se brandisce la magia delle Terretetre, il suo richiamo proviene dal mondo dei vivi. Doveva tornare.', 'Anche se brandisce la magia delle Terretetre, il suo richiamo proviene dal mondo dei vivi. Doveva tornare.', 38134),
(205246, 'itIT', 'Dev\'essere così. Anche se è morta già una volta, doveva rispondere al richiamo del suo padrone prima di poter tornare da noi.', 'Dev\'essere così. Anche se è morta già una volta, doveva rispondere al richiamo del suo padrone prima di poter tornare da noi.', 38134),
(205245, 'itIT', 'Il Re dei Lich mi chiama. Devo servirlo ancora una volta!', 'Il Re dei Lich mi chiama. Devo servirlo ancora una volta!', 38134),
(205243, 'itIT', 'Bah, elementali della polvere! Se continuano così faranno piazza pulita di tutto e i Semebrado moriranno!', '', 38134),
(205241, 'itIT', 'Esatto. Questa creatura è morta nobilmente e si è guadagnata il suo posto nelle Terretetre.', 'Esatto. Questa creatura è morta nobilmente e si è guadagnata il suo posto nelle Terretetre.', 38134),
(205240, 'itIT', 'Anche se potrebbe essere doloroso, non dobbiamo tenere lontana questa creatura dalla sua eternità.', 'Anche se potrebbe essere doloroso, non dobbiamo tenere lontana questa creatura dalla sua eternità.', 38134),
(205239, 'itIT', 'Lascia che traduca. Questa creatura è caduta proteggendo il suo padrone da un branco di lupi, e desidera tornare per assicurarsi che sia al sicuro.', 'Lascia che traduca. Questa creatura è caduta proteggendo il suo padrone da un branco di lupi, e desidera tornare per assicurarsi che sia al sicuro.', 38134),
(205238, 'itIT', 'Woof! Bau! Bau! Bau! Grrr...', '', 38134),
(205237, 'itIT', 'È giusto... Si è meritato una morte da guerriero. È morto con onore ed è andato verso la gloria a Maldraxxus.', 'È giusto... Si è meritato una morte da guerriero. È morto con onore ed è andato verso la gloria a Maldraxxus.', 38134),
(205236, 'itIT', 'Sfortunatamente no. Anche se altri sono morti senza di lui, ha compiuto il suo destino nel piano dei mortali.', 'Sfortunatamente no. Anche se altri sono morti senza di lui, ha compiuto il suo destino nel piano dei mortali.', 38134),
(205235, 'itIT', 'La battaglia infuria nella mia città. Devo tornare a proteggere i piccoli prima che le bestie li raggiungano!', 'La battaglia infuria nella mia città. Devo tornare a proteggere i piccoli prima che le bestie li raggiungano!', 38134),
(205234, 'itIT', 'Non è corretto. Noi dobbiamo traghettare le anime che hanno dei legami con altre forze.', 'Non è corretto. Noi dobbiamo traghettare le anime che hanno dei legami con altre forze.', 38134),
(205233, 'itIT', 'Corretto. Alle anime con dei legami sufficientemente forti con altre forze cosmiche è permesso fare ritorno.', 'Corretto. Alle anime con dei legami sufficientemente forti con altre forze cosmiche è permesso fare ritorno.', 38134),
(205232, 'itIT', '<L\'essere emette dei cacofonici suoni d\'ansia.>', '', 38134),
(205230, 'itIT', 'Una manciata di rubini infusi e questo Calcapeccati ti porterà fino al Cancello Antico. Non lasciarti cadere se non vuoi scendere lungo strada.', '', 38134),
(205229, 'itIT', 'Il basso dev\'essere più preciso e la batteria dev\'essere più incisiva.\n\nE tu questa la chiami sala concerti? È una caverna! Non c\'è calore e al massimo l\'acustica è scadente.\n\nI Cinghiali Appestati non possono esibirsi finché non è tutto perfetto. Solo perché sono un Non Morto, non significa che non abbia un orecchio assoluto!', '', 38134),
(205222, 'itIT', 'Oh, salve! Imparato nuove ricette?', '', 38134),
(205221, 'itIT', 'È bello vederti. Ora parliamo di cibo?', '', 38134),
(205220, 'itIT', 'Io sono bel manzo, proprio come carne di stufato. È divertente, vero?', '', 38134),
(205219, 'itIT', 'Salve salvino! Sei qui per condividere ricette?', '', 38134),
(205214, 'itIT', '', 'Non alleno i Ladri dell\'Assassinio.', 38134),
(205212, 'itIT', '', 'L\'avidità del Tributiere non conosce limiti.\n\nA Maldraxxus, queste azioni sono inimmaginabili. I Venthyr non dovrebbero concentrarsi sull\'aiutare le anime coi loro peccati? Mi fa stare ancora peggio sapere che ha forgiato un\'alleanza con quelli del mio regno.\n\nDobbiamo porre fine a questa follia.', 38134),
(205205, 'itIT', '', 'Lasciatemi in pace.', 38134),
(205202, 'itIT', '', 'La tua forza è cresciuta dal nostro primo combattimento. Ti propongo una sfida più adeguata.', 38134),
(205201, 'itIT', '', 'Tutti coloro che hanno tradito gli Occhi periranno!', 38134),
(205197, 'itIT', '<Arkelios sorride.>\n\nNo. Se tutto si sgretolerà sotto ai miei piedi...\n\nSarà il momento per una nuova avventura.', '', 38134),
(205194, 'itIT', 'Be\'... Ho camminato! \n\nHo trovato questo posto incantevole sotto un albero e ho deciso di fermarmi a meditare.\n\nIn qualche modo devo aver riflettuto più a lungo di quanto pensassi, poiché sembra che mi sia allontanato dal resto di Selvarden.', '', 38134),
(205192, 'itIT', 'Sono un girovago, un viandante.\n\nPer eoni ho camminato ed esplorato i regni della vita, e ora quelli della morte. Col trascorrere del tempo ripenso alla mia esistenza e rifletto su molte cose.', '', 38134),
(205190, 'itIT', 'Salute, viandante.', '', 38134),
(205189, 'itIT', 'Io ho... Volevo dire... Noi abbiamo il potere! Finalmente sarò libero dalle tue interminabili grida sul fare la guardia a quella stanza.', '', 38134),
(205188, 'itIT', 'No! Vai via. Basta! Nessuno può entrare qua dentro.', '', 38134),
(205187, 'itIT', 'Come ci si sente? Eh? Eh? Aprirai la porta, non è vero?', '', 38134),
(205186, 'itIT', 'Ahhh. Mi è mancato <rutta> così tanto. Ancora! Ce ne serve ancora.', '', 38134),
(205185, 'itIT', 'Cerchi qualche... potenziamento? \n\nUn po\' di potenza per i tuoi pugni, del brio per la tua magia.\n\nTutto questo può essere tuo, al giusto prezzo.', '', 38134),
(205184, 'itIT', 'Noooooo! Il Maestro sarà furioso. La pagherai!', '', 38134),
(205182, 'itIT', 'Le potenti falinci non vacilleranno mai.', '', 38134),
(205180, 'itIT', 'Sta parlando con te? È un traditore, non ascoltarlo.', '', 38134),
(205179, 'itIT', 'Pfff! Ignora quel folle che continua a blaterare. Entreremo in quella stanza, andiamo a trovare qualcosa da prosciugare!', '', 38134),
(205178, 'itIT', 'Oooh, sì. È proprio quella la roba che ci serve! Me... ehm, ce ne servirà molto di più.', '', 38134),
(205173, 'itIT', '$gIl:La; Calcafauce alla riscossa! Scommetto che vincerai.', '', 38134),
(205171, 'itIT', '', 'Uno dei miei esploratori ha riportato informazioni sui movimenti di Lysonia.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205170, 'itIT', 'Il suo destino mi darà mai pace? Mi libererò mai di lui? Forse... col tempo. Forse.', '', 38134),
(205169, 'itIT', '', 'Fallo per Pelagos, Kleia... Fallo per lui.', 38134),
(205168, 'itIT', 'Aspetta. Non sono da solo. Ho Kleia, dopo tutto! Come dice sempre, miglioro un pochettino ogni volta. Un giorno, ce la farò! Un giorno...', '', 38134),
(205167, 'itIT', '', 'Ora capisco, questo sentiero che ho sempre camminato. Non lo dimenticherò mai.', 38134),
(205166, 'itIT', 'Ho provato a ricordarle di tutto ciò che aveva ottenuto, ma alla fine... mi viene da chiedermi se questo fosse l\'unico modo. Ho solo domande, cara pergamena, e nessuna risposta.', '', 38134),
(205155, 'itIT', '<Ha combattuto bene, ma sembrava impreparato per una battaglia.>', '', 38134),
(205153, 'itIT', '<Questo Alienatore sembra sia morto combattendo.>', '', 38134),
(205148, 'itIT', 'Ottimo! Così dovrebbe bastare. Ora possiamo superare quello sbruffone. Torniamo là!', '', 38134),
(205147, 'itIT', '<Il cumulo di neve non ha offerto molta protezione all\'Alienatore.>', '', 38134),
(205146, 'itIT', 'Non hai trovato nulla.', 'Non hai trovato nulla.', 38134),
(205145, 'itIT', '<Questo Alienatore ferito aveva quasi raggiunto il portale prima di morire.>\n\n<Non c\'è alcun segno che abbia trovato qualcosa di valore.>', '', 38134),
(205144, 'itIT', 'Informerò l\'Arconte.', '', 38134),
(205135, 'itIT', 'Ehi! Lascialo stare, rimettilo a posto. Ladro!', '', 38134),
(205134, 'itIT', 'Indietro! Non ti è permesso entrare qui.', '', 38134),
(205132, 'itIT', 'Sembra un mucchio di armature scartate.', '', 38134),
(205129, 'itIT', 'Ehm! \n\nNon pensare neanche per un attimo di riuscire a superarmi. Sono immune alla tua patetica supplica.', '', 38134),
(205099, 'itIT', 'Mi era stato detto che qualcuno da Oblio del Peccato sarebbe stato qui. La tua presenza ci onora.\n\nCerca di non intralciare i custodi, si stanno adoperando per restaurare questo luogo dopo la rovina che vi si è abbattuta.', 'Mi era stato detto qualcuno da Oblio del Peccato sarebbe stato qui. La tua presenza ci onora.\n\nCerca di non intralciare la custode, si sta adoperando per restaurare questo luogo dopo la rovina che vi si è abbattuta.', 38134),
(205098, 'itIT', 'Un tempo le cripte erano un modello di ordine a Revendreth. Ora non più.\n\nOra dobbiamo lavorare per ripristinare la loro antica gloria. Abbiamo iniziato il processo di rigenerazione. Sfortunatamente, il resto del distretto è troppo pericoloso per noi al momento.\n\nChiedo scusa, devo tornare ai miei doveri.', 'Un tempo le cripte erano un modello di ordine a Revendreth. Ora non più.\n\nOra dobbiamo lavorare per ripristinare la loro antica gloria. Abbiamo iniziato il processo di rigenerazione. Sfortunatamente, il resto del distretto è troppo pericoloso per noi al momento.\n\nChiedo scusa, devo tornare ai miei doveri.', 38134),
(205097, 'itIT', '', 'Aveva così tanta fede. Non aleggiava neanche un dubbio nella sua mente.\n\nHo assistito al suo mondo che si frantumava davanti ai miei occhi.\n\nIo...\n\nNon sono più sicura di quale sia lo scopo.', 38134),
(205095, 'itIT', '', 'Sapevamo che alcune anime stavano andando per sbaglio alla Fauce.\n\nAchillon è stato sempre molto forte, mi ispirava fiducia. La sua lealtà non ha mai vacillato... Era sempre così pieno di speranza.\n\nMa all\'arrivo a Oribos... Vedere tutte quelle anime...\n\nL\'ho visto cadere davanti ai miei occhi.', 38134),
(205093, 'itIT', '', '<Pallessia guarda impassibile nell\'acqua.>', 38134),
(205091, 'itIT', 'Tu e il tuo gruppo di alleati avete la mia eterna gratitudine per le vostre imprese, Calcafauce.', '', 38134),
(205088, 'itIT', '', 'Non riesco... a resistere...', 38134),
(205087, 'itIT', '', 'Arghhh...', 38134),
(205086, 'itIT', '', 'Il potere del Carceriere cresce, e le Terretetre affrontano un inesorabile pericolo. Resta in guardia, cacciatore. Ci sarà bisogno della tua forza nei giorni a venire.', 38134),
(205079, 'itIT', '', 'A presto, $gamico:amica; dei Kyrian! Buona fortuna!', 38134),
(205078, 'itIT', '', 'Possiate tu e i tuoi Vincoli dell\'Anima verificare le lezioni di pazienza, unità e cooperazione che trasmettiamo.', 38134),
(205077, 'itIT', '', 'Quando vuoi, $n. Le mie radici sono profonde.', 38134),
(205073, 'itIT', '', 'I Silfi posseduti mi avevano circondato! Non sapevo cosa fare!', 38134),
(205069, 'itIT', 'Perché lo hai fatto? Mi stavo divertendo a farmi un giretto!', '', 38134),
(205063, 'itIT', '', 'Il mio piccolo Gommino è sconvolto! Niente riesce a calmarlo.', 38134),
(205062, 'itIT', '', 'Aiuta i miei alleati coi problemi che li perseguitano. Rassicurali che, al loro ritorno, riceveranno il rinvigorimento che gli serve.', 38134),
(205059, 'itIT', '', 'Thran\'tiok ha nascosto il suo filatterio fuori dalla Casata dei Rituali dopo che è stato scoperto. Devi trovarlo... per entrambi noi.', 38134),
(205056, 'itIT', 'Cosa ti affligge, mortale?', '', 38134),
(205052, 'itIT', '', 'Non questa volta, Thran\'tiok!', 38134),
(205051, 'itIT', '', 'La tua esistenza mi servirà in un\'agonia straziante mentre ti piego e scopro quale dei miei nemici sia il tuo padrone...', 38134),
(205050, 'itIT', '', '$gUn altro folle traditore è caduto:Un\'altra folle traditrice è caduta; nella mia trappola!', 38134),
(204961, 'itIT', 'Oblio del Peccato si erge forte.', 'Oblio del Peccato si erge forte.', 38134),
(204960, 'itIT', 'Sì, Calcafauce?', 'Sì, Calcafauce?', 38134),
(204959, 'itIT', 'Qualche ordine, $gsignore:signora;?', 'Qualche ordine, $gsignore:signora;?', 38134),
(204958, 'itIT', 'Il generale ti manda i suoi saluti.', 'Il generale ti manda i suoi saluti.', 38134),
(204957, 'itIT', 'Per Oblio del Peccato.', 'Per Oblio del Peccato.', 38134),
(204956, 'itIT', 'Sono sempre pronto.', 'Sono sempre pronta.', 38134),
(204955, 'itIT', 'Come posso esserti utile?', 'Come posso esserti utile?', 38134),
(204954, 'itIT', 'Di cosa hai bisogno?', 'Di cosa hai bisogno?', 38134),
(204889, 'itIT', 'Corridoi Contorti', 'Corridoi Contorti', 38134),
(204888, 'itIT', 'Bastioni Superiori', 'Bastioni Superiori', 38134),
(204883, 'itIT', 'Interstizi dei Cuorfreddo', 'Interstizi dei Cuorfreddo', 38134),
(204882, 'itIT', 'Forge dell\'Anima', 'Forge dell\'Anima', 38134),
(204881, 'itIT', 'Stanze delle Fratture', 'Stanze delle Fratture', 38134),
(204879, 'itIT', 'Posso agevolare qualsiasi sua esigenza.\n\nCome posso esserle d\'aiuto oggi, $gmio signore:mia signora;?', '', 38134),
(204876, 'itIT', 'Bel colore, davvero adatto a un maggiordomo.', '', 38134),
(204875, 'itIT', 'Quale colore del pigmento dovremmo lanciare nella pozza di fango per il maggiordomo Dragatore?\n\nPossiamo cambiare il colore più tardi, ma non sarà piacevole per il Dragatore, quindi scegli attentamente.', '', 38134),
(204874, 'itIT', 'Stai provando a fare un nuovo Dragatore, vero?\n\nFallo come me, sono il migliore!', '', 38134),
(204873, 'itIT', 'Non so perché, ma mi sento un po\' triste oggi...', '', 38134),
(204872, 'itIT', 'Quei nuovi Dragatori non si prepareranno da soli...', '', 38134),
(204870, 'itIT', 'Un seme senza Animum.', 'Un seme senza Animum.', 38134),
(204868, 'itIT', 'Mi manca l\'Osservatorio Vegliapietra.\n\nBe\', mi manca quello che era... Non come l\'ha ridotto il nostro nuovo \"mietitore\".', '', 38134),
(204867, 'itIT', 'Siamo stati entrambi esiliati qui per aver \"accumulato\" Animum nell\'Osservatorio Vegliapietra.\n\nSe chiedi a me, conservare abbastanza Animum per mantenersi non è accumulazione. È sopravvivenza.\n\nQuel Tributiere è corrotto quanto Denathrius.', '', 38134),
(204865, 'itIT', 'Il Distretto delle Braci è un luogo spietato, vorrei che potessimo andarcene.\n\nMa dobbiamo rimanere qui in questo rifugio, perché la Luce brucia la nostra pelle. Il dolore è insopportabile.', '', 38134),
(204863, 'itIT', 'Le cose sembrano finalmente meno gravi. Grazie, $R.', '', 38134),
(204862, 'itIT', 'Ce l\'abbiamo fatta, amore mio! La Luce non ostacola più la nostra sopravvivenza.', '', 38134),
(204852, 'itIT', 'Forse, con un po\' d\'addestramento...', '', 38134),
(204851, 'itIT', 'Ma invece dovremo accontentarci di un Dragatore.', '', 38134),
(204850, 'itIT', 'Ricorda: i migliori maggiordomi sono saggi, affascinanti e possiedono capacità relazionali straordinarie.', '', 38134),
(204840, 'itIT', 'Due dozzine di millenni mi sono fatto mettere i denti addosso dal capo. Ero uno spuntino, per eoni. \n\nGià, non si riescono a vedere i segni dei morsi, ma sanno come nasconderli in modo che gli altri non vedano. Non sono considerati dignitosi da quei Venthyr di lusso.\n\nE poi un giorno tu vieni qui e fai fuori il mio capo. Ora non so se sapessi chi stavi facendo fuori o liberando, ma sono grato in ogni caso. Ora un po\' di vendetta.', '', 38134),
(204837, 'itIT', 'Mortale.', '', 38134),
(204835, 'itIT', 'Il tuo padrone è molto esigente. Ci vorrà del tempo per raccogliere i reagenti che ha chiesto.\n\nPosso comunque accelerare il processo, se mi aiuterai a raccogliere alcuni degli ingredienti più difficili.', '', 38134),
(204834, 'itIT', 'Cosa posso fare per te?', '', 38134),
(204827, 'itIT', '', 'Incontriamoci a Revendreth, Calcafauce. Scoveremo la fonte di questo tradimento... e la elimineremo.', 38134),
(204823, 'itIT', 'Tutto questo parlare spesso lascia le mie vecchie ossa inaridite, e bisogna rimanere idratati quando si ha a che fare con tutti questi combattenti.', '', 38134),
(204814, 'itIT', 'Be\', almeno ammetti di essere folle.\n\nNon posso semplicemente farti riunire a noi, il mio onore esige qualcos\'altro.\n\nDevi dimostrare il tuo valore. Guarda caso ho un compito perfetto per le tue abilità.\n\nSei $gpronto:pronta; a completarlo per diventare ancora una volta $gun:una; Venthyr?', '', 38134),
(204813, 'itIT', 'Osi farmi quella domanda in faccia?\n\nSe questo non fosse un terreno neutrale, sarei tentato di schiacciarti.\n\nCosa ti fa credere che ti rivogliamo tra di noi?', '', 38134),
(204812, 'itIT', 'La tua certezza è rassicurante, Calcafauce.\n\nSiamo già a conoscenza dei tuoi punti di forza.\n\nOra, visto che sei $gsicuro:sicura;, giura fedeltà ai Venthyr.', '', 38134),
(204811, 'itIT', 'Non è qualcosa da prendere alla leggera, $n. \n\nDevi essere $gcerto:certa; che desideri unirti ai nostri ranghi.\n\nSei assolutamente $gsicuro:sicura; che sia una cosa di cui vuoi discutere?', '', 38134),
(204810, 'itIT', 'Mi sbaglio? Non fai già parte di un\'altra congrega?\n\nSarai anche $ginvidioso:invidiosa; di noi Venthyr, ma sei davvero $gpronto:pronta; a tradire i tuoi attuali alleati?', '', 38134),
(204809, 'itIT', 'Come posso aiutarti, Calcafauce?', '', 38134),
(204802, 'itIT', 'Tu sei $gl\'accolito:l\'accolita; della baronessa, non $gil mio:la mia;, $n.\n\nRicordatelo e riduci al minimo le nostre interazioni.', '', 38134),
(204799, 'itIT', 'La Pietra di Transito! La nostra via fuori da qui.', '', 38134),
(204798, 'itIT', 'Sei in fuga dal Carceriere... sì. È tutto più chiaro ora.', '', 38134),
(204795, 'itIT', 'La Fauce... Mi ricordo. Sembra così tanto tempo fa.', '', 38134),
(204793, 'itIT', 'Lo farò distribuire tra le nostre truppe se necessario. Discutiamo delle opzioni di sicurezza.', '', 38134),
(204784, 'itIT', '', 'E così vuoi tornare nei nostri ranghi?\n\nTemo di non poterti far tornare nel Cuore della Foresta così facilmente.\n\nDevi dimostrarci di essere $gpronto:pronta; a servirci ancora.\n\nPer fortuna ho un compito da svolgere che dovrebbe costituire una prova ideale.\n\nSei $gpronto:pronta; a dimostrare il tuo valore?', 38134),
(204783, 'itIT', '', 'Ah, $gquello:quella; che ci ha delusi. \n\nNon credevo che avresti mostrato ancora la tua faccia qui in giro, $n.\n\nNon credere che abbiamo perdonato il tuo abbandono.', 38134),
(204782, 'itIT', '', 'Sembra che ti sia $gmesso:messa; in testa di farlo.\n\nChi sono io per dubitare di te? Hai già servito fedelmente la nostra Regina, potrai farlo di nuovo.\n\nMetterai la tua intera anima al servizio dei Silfi della Notte e della nostra amata Regina?', 38134),
(204781, 'itIT', 'Non era la mia prima scelta... ma mi arrangerò.', 'Non era la mia prima scelta... ma mi arrangerò.', 38134),
(204780, 'itIT', '', 'Sei $gsicuro:sicura;, $n?\n\nUna decisione del genere può causare molti squilibri nel ciclo della vita. Devi essere assolutamente $gsicuro:sicura; che questo è ciò che vuoi prima di procedere.', 38134),
(204778, 'itIT', '', 'Capisco. \n\nBe\', ti siamo grati per l\'aiuto che ci hai già fornito, ma rappresenti un\'altra Congrega.\n\nSei $gsicuro:sicura; di volerli tradire per unirti a noi?', 38134),
(204777, 'itIT', 'Ecco fatto! È tutto pronto. Ma... uhm, fa\' attenzione a non fare alcuna mossa improvvisa. E qualunque cosa tu faccia, non avvicinarti troppo ai divoratori. Buona caccia!', '', 38134),
(204776, 'itIT', '', 'Come posso aiutarti, Calcafauce?', 38134),
(204775, 'itIT', '', 'Ci hai già abbandonati una volta.\n\nPerché dovremmo dimenticarcene? Hai qualcosa da dire in tua difesa?', 38134),
(204773, 'itIT', 'Giusto! Ecco qui la mia seconda spazzola, quelle bestie amano scacciare il prurito.', '', 38134),
(204772, 'itIT', 'La tua magia non ti nasconderà dalla mia vista.', '', 38134),
(204771, 'itIT', 'Ti vedo...', '', 38134),
(204770, 'itIT', 'Che trucco è mai questo?', '', 38134),
(204767, 'itIT', '', 'Sei $gpronto:pronta; a redimerti ai nostri occhi?\n\nNon puoi semplicemente venire qui e trovarci a braccia aperte. No, non questa volta. Questa volta ci serve una prova della tua dedizione alla nostra causa.\n\nSei $gpronto:pronta; a dimostrare il tuo valore?', 38134),
(204765, 'itIT', 'La tua volontà è forte.\n\nNon ho dubbi che diventerai $gun ottimo Necrosignore:un\'ottima Necrosignora;. Ora devi dedicare la tua forza e il tuo potere alla nostra causa.\n\nGiuralo!', 'La tua volontà è forte.\n\nNon ho dubbi che diventerai $gun ottimo Necrosignore:un\'ottima Necrosignora;. Ora devi dedicare la tua forza e il tuo potere alla nostra causa.\n\nGiuralo!', 38134),
(204764, 'itIT', 'Percorrere questa strada significa abbandonare la congrega che hai scelto.\n\nNon ti biasimo per aver cercato degli alleati più forti, ma dubito che vedranno di buon occhio la tua diserzione.\n\nSei $gcerto:certa; di voler procedere?', 'Percorrere questa strada significa abbandonare i tuoi attuali alleati.\n\nNon ti biasimo per aver cercato dei compagni più forti, ma dubito che vedranno di buon occhio il tuo tradimento.\n\nSei $gcerto:certa; di volerne discutere?', 38134),
(204750, 'itIT', 'Sì, andrà bene. Siamo pronti a fare spettacolo.', '', 38134),
(204746, 'itIT', 'Lo uso per fare delle cose carine. Le decorazioni sono pronte!', '', 38134),
(204745, 'itIT', '', 'Serviamo uno scopo qui a Revendreth, anche se così tanti Venthyr se ne sono dimenticati.', 38134),
(204744, 'itIT', '', 'Non chiederti come è stato ottenuto. Consideralo semplicemente un gesto di buona volontà per il quale tu ora sei in debito. Non vedo l\'ora di trattare di nuovo con te, Calcafauce.', 38134),
(204742, 'itIT', '', 'Dato che sono riuscita ad anticipare la tua richiesta, non è proprio un caso che abbia già acquisito un dispositivo di portali che conduce alla torre.', 38134),
(204741, 'itIT', '', 'Eviterò i soliti avvertimenti e avvisi, dato che la tua combinazione unica di coraggio e incoscienza li renderebbe irrilevanti.', 38134),
(204740, 'itIT', 'Ho semplicemente bisogno della pergamena per completare la mia ricerca.\n\nAlla baronessa di\' semplicemente che non mi hai trovato.', 'Ho semplicemente bisogno della pergamena per completare la mia ricerca.\n\nAlla baronessa di\' semplicemente che non mi hai trovato.', 38134),
(204739, 'itIT', 'Sì, ho ancora io la pergamena.\n\nCosa vorresti fare, esattamente?', 'Sì, ho ancora io la pergamena.\n\nCosa vorresti fare, esattamente?', 38134),
(204738, 'itIT', 'Oh, questa? Ho quasi finito.\n\nRiferisci a Ninadar che mi serve un po\' più di tempo.', 'Oh, questa? Ho quasi finito.\n\nRiferisci a Ninadar che mi serve un po\' più di tempo.', 38134),
(204737, 'itIT', '', 'Torgast, eh? Dopo averti detto che i tuoi amici erano tenuti all\'interno, sapevo che era solo questione di tempo prima che tu venissi da me in cerca di aiuto.', 38134),
(204735, 'itIT', 'Non vedi che sono occupato?', 'Non vedi che sono occupata?', 38134),
(204734, 'itIT', 'Non hai un rituale da studiare?', 'Non hai un rituale da studiare?', 38134),
(204733, 'itIT', 'Che c\'è?', 'Che c\'è?', 38134),
(204717, 'itIT', '<Vedi un piccolo pipistrello di pietra appeso al santuario.>\n\n<Capisci che è possibile usare una quantità molto specifica di rubini infusi per svegliarlo.>\n\n<Forse risponderà ai tuoi comandi se lo farai.>', '<Vedi un piccolo pipistrello di pietra appeso al santuario.>\n\n<Capisci che è possibile usare una quantità molto specifica di rubini infusi per svegliarlo.>\n\n<Forse risponderà ai tuoi comandi se lo farai.>', 38134),
(204716, 'itIT', 'Dobbiamo rimanere sempre vigili.', '', 38134),
(204714, 'itIT', 'Al tuo comando, Calcafauce.', '', 38134),
(204707, 'itIT', '', 'Ah, sì, adesso ricordo che il Barone mi aveva chiesto un elenco di tomi della mia biblioteca. E degli argomenti più strani, se ricordo bene.\n\nCome puoi vedere, sono una Lich molto occupata. Tuttavia, se volessi alleggerire il mio carico di lavoro, potrei trovarti i tomi che cerchi.', 38134),
(204706, 'itIT', '', 'Sarà meglio che tu abbia una buona ragione per interrompermi.\n\nAllora, cosa c\'è?', 38134),
(204705, 'itIT', '', 'Oh Rogana, ho ricevuto innumerevoli complimenti sul vestito che hai cucito appositamente per me.', 38134),
(204704, 'itIT', 'E sempre uno dei miei peccati preferiti, cara Rogana.', '', 38134),
(204703, 'itIT', 'Buona fortuna. Non finire nella tua stessa rete!', '', 38134),
(204702, 'itIT', '', 'Dovresti riportarla al santuario, Calcafauce. Forse la Baronessa Draka o qualcun altro potranno comprendere la sua importanza.', 38134),
(204701, 'itIT', '<Tulipnaso se la ride.>', '', 38134),
(204700, 'itIT', 'Fai attenzione con quello, Calcafauce. Non vorrei che ti rovinasse i vestiti.', '', 38134),
(204699, 'itIT', '', 'Ah, sì, si tratta di questo. Un\'armatura di manifattura senza eguali. Non ho mai visto niente di simile, nemmeno dalle forge dei Prescelti.', 38134),
(204698, 'itIT', 'Come hanno fatto tutti questi moscerini a entrare nella corte, Calcafauce?\n\nDavvero insolito...\n\nPer fortuna ho proprio lo strumento perfetto per un evento così imprevedibile!\n\nEcco, prendilo pure.\n\n<Tulipnaso se la ride.>', '', 38134),
(204695, 'itIT', 'Andiamo, Temel! I Silfi sanno come far festa!', '', 38134),
(204694, 'itIT', 'Non sono sicuro di chi mi renda più nervoso: gli insetti o i Silfi...', '', 38134),
(204692, 'itIT', 'Seguimi. Concediti di godere dei frutti del tuo lavoro.', '', 38134),
(204691, 'itIT', 'Preparati a dovere, Nelesis, perché presto giungerà l\'ora del nostro trionfo.', '', 38134),
(204690, 'itIT', 'Come posso aiutarti, Calcafauce?', '', 38134),
(204689, 'itIT', 'Hai dimostrato la tua forza e la tua astuzia, $gamico mio:amica mia;. \n\nAnche se ti accoglierei per combattere al fianco dei Necrosignori mentre ci riprendiamo Maldraxxus, fai già parte di un\'altra congrega.\n\nSei $gcerto:certa; di volerla abbandonare?', '', 38134),
(204684, 'itIT', 'Be\', capirai se non ti crediamo sulla parola.\n\nSei $gdisposto:disposta; a impegnarti per un\'occasione di riunirti ai Kyrian?', '', 38134),
(204681, 'itIT', 'Salve di nuovo, $n.\n\nDevi essere $gsicuro:sicura; di te per farti vedere di nuovo dinanzi a me.\n\nNon abbiamo dimenticato il tuo tradimento della nostra causa.', '', 38134),
(204677, 'itIT', 'In guardia! Ci ha percepiti!', '', 38134),
(204676, 'itIT', '', 'Non disturbare gli ospiti, Podd. $p ha chiaramente dei grandi piani per questa corte.', 38134),
(204675, 'itIT', 'Mi sembri più $gcarino:carina; di una pila di fango!', '', 38134),
(204674, 'itIT', '', 'Se dovessi stancarti di questo completo, dovrei avere altre opzioni durante il dopofesta.', 38134),
(204667, 'itIT', '', 'Che le tue armi trovino il bersaglio.', 38134),
(204666, 'itIT', 'È fatta. È stato un onore cacciare con te.', '', 38134),
(204665, 'itIT', '', 'Siamo pronti, $n. Quando vuoi.', 38134),
(204664, 'itIT', 'Come posso aiutarti?', '', 38134),
(204658, 'itIT', 'Ti credo.\n\nOra devo chiederti di giurare fedeltà ai Kyrian. Diventa un membro della Congrega dei Kyrian.\n\nRipeti dopo di me:\n\n\"Servirò i Kyrian con coraggio e con tutta la mia volontà.\"', '', 38134),
(204657, 'itIT', 'Devo chiederlo un\'altra volta $gamico mio:amica mia;.\n\nSei $gcerto:certa; di volerti unire ai Kyrian? Significherebbe tradire tuoi attuali alleati.\n\nProbabilmente non se ne dimenticheranno in fretta. \n\nSei davvero $gsicuro:sicura;?', '', 38134),
(204656, 'itIT', 'Fai già parte di un\'altra Congrega, $n. Sei davvero $gsicuro:sicura; di voler percorrere questa strada?\n\nCi ricordiamo ancora di come ci hai aiutati al Bastione e accoglieremmo a braccia aperte la tua forza. \n\nVuoi davvero tradire i tuoi alleati attuali?', '', 38134),
(204643, 'itIT', '', 'Non è possibile!', 38134),
(204641, 'itIT', 'Oh, adoro i drammi di corte!', '', 38134),
(204640, 'itIT', 'Be\', neanche MORTO starò qui con te, Frederik!', 'Be\', neanche MORTA starò qui con te, Frederik!', 38134),
(204632, 'itIT', 'Ho scoperto questo completo per primo, Fredrik! Sei tu il ladro di stile!', 'Ho scoperto questo completo per primo, Fredrik! Sei tu il ladro di stile!', 38134),
(204623, 'itIT', 'Gli Indegni sono implacabili nella loro ricerca della nostra conoscenza. Proprio ora, cercano le pergamene antiche che raccolgono tutta la saggezza dell\'Eletto. \n\nNon so con quale scopo, ma ritengo che si possa presumere che non sia per apprendere.\n\nTi prego, recupera queste pergamene. Il mio discepolo ti porterà da loro.', 'Gli Indegni sono implacabili nella loro ricerca della nostra conoscenza. Proprio ora, cercano le pergamene antiche che raccolgono tutta la saggezza dell\'Eletto. \n\nNon so con quale scopo, ma ritengo che si possa presumere che non sia per apprendere.\n\nTi prego, recupera queste pergamene. Il mio discepolo ti porterà da loro.', 38134),
(204621, 'itIT', 'Senza le nostre ali, le Terretetre cadranno nel caos. Lascia che il nostro scopo eterno ti guidi.', 'Senza le nostre ali, le Terretetre cadranno nel caos. Lascia che il nostro scopo eterno ti guidi.', 38134),
(204620, 'itIT', 'St... Sta\' indietro!\n\nOh, non sei qui per costringermi a mettere quei terribili atti su pergamena?\n\nQuei Venthyr mi hanno privato dell\'inchiostro per aver rifiutato di trascrivere la loro \"ricerca\". Come se si potesse chiamare tale.\n\nPuoi aiutarmi? Senza il mio inchiostro sono inutile!', 'St... Sta\' indietro!\n\nOh, non sei qui per costringermi a mettere quei terribili atti su pergamena?\n\nQuei Venthyr mi hanno privato dell\'inchiostro per aver rifiutato di trascrivere la loro \"ricerca\". Come se si potesse chiamare tale.\n\nPuoi aiutarmi? Senza il mio inchiostro sono inutile!', 38134),
(204618, 'itIT', 'Ricorda il tuo addestramento. Confida nella via. Abbi fede nell\'Arbiter e nella sua infinita saggezza.', 'Ricorda il tuo addestramento. Confida nella via. Abbi fede nell\'Arbiter e nella sua infinita saggezza.', 38134),
(204617, 'itIT', 'Ne porterai molti sulle tue spalle. Alcuni fardelli saranno più pesanti di altri.', 'Ne porterai molti sulle tue spalle. Alcuni fardelli saranno più pesanti di altri.', 38134),
(204616, 'itIT', 'Lascia tutto il resto all\'Arbiter e alla Via Eterna. Questo è il nostro compito.', 'Lascia tutto il resto all\'Arbiter e alla Via Eterna. Questo è il nostro compito.', 38134),
(204615, 'itIT', 'Gli ultimi istanti della vita degli altri sono tuoi solo per decidere se il loro tempo nei regni mortali sia giunto al termine.', 'Gli ultimi istanti della vita degli altri sono tuoi solo per decidere se il loro tempo nei regni mortali sia giunto al termine.', 38134),
(204614, 'itIT', 'Preparati ad affrontare la tentazione di giudicare. Tutte le anime andranno dinanzi all\'Arbiter e tutte le anime raccoglieranno ciò che hanno seminato.', 'Preparati ad affrontare la tentazione di giudicare. Tutte le anime andranno dinanzi all\'Arbiter e tutte le anime raccoglieranno ciò che hanno seminato.', 38134),
(204613, 'itIT', 'Non portare la tua persona all\'interno delle loro vite, poiché la loro esistenza appartiene a loro.', 'Non portare la tua persona all\'interno delle loro vite, poiché la loro esistenza appartiene a loro.', 38134),
(204612, 'itIT', 'Sappi che scrutare nella vita degli altri significa capirli come individui, per vedere la loro vera essenza senza alcun giudizio o riserva.', 'Sappi che scrutare nella vita degli altri significa capirli come individui, per vedere la loro vera essenza senza alcun giudizio o riserva.', 38134),
(204611, 'itIT', 'Solo quando ti allontanerai dalle esperienze degli altri potrai vedere la loro verità.', 'Solo quando ti allontanerai dalle esperienze degli altri potrai vedere la loro verità.', 38134),
(204610, 'itIT', 'Ottima caccia, $n. Vieni, parliamo della tua ricompensa.', '', 38134),
(204609, 'itIT', '', 'Quel gorm non mangerà più le nostre preziose foreste.', 38134),
(204608, 'itIT', '', 'Un nuovo incantesimo! Non sto nella pelle per vederlo in azione!', 38134),
(204604, 'itIT', '', 'Riporteremo il bastone al Seggio del Primus. Sarà un ricordo della nostra vittoria.', 38134),
(204601, 'itIT', '', 'Si dice che quando il Primus scomparve, i pezzi della sua armatura si dispersero ovunque. Se ce ne sono altri da trovare, li rivendicheremo per la nostra congrega!', 38134),
(204580, 'itIT', '', 'Silenzio. Intrappolare il gorm in questa caverna non è stato facile.', 38134),
(204579, 'itIT', '', 'Alderyn, sei qui. Bene. La bestia è ferita, ma non posso fare molto altro con quell\'armatura ancora intatta.', 38134),
(204578, 'itIT', 'Ah, Il posto dev\'essere questo.', '', 38134),
(204567, 'itIT', 'Questo non era... il mio destino...', 'Questo non era... il mio destino...', 38134),
(204566, 'itIT', 'Maldraxxus... sarà nostra...', 'Maldraxxus... sarà nostra...', 38134),
(204565, 'itIT', 'La casata... non cadrà...', 'La casata... non cadrà...', 38134),
(204564, 'itIT', 'Chi finirà... la mia ricerca?', 'Chi finirà... la mia ricerca?', 38134),
(204563, 'itIT', 'Intrusi! Abbatteteli!', 'Intrusi! Abbatteteli!', 38134),
(204562, 'itIT', 'Non puoi nasconderti dalla nostra magia!', 'Non puoi nasconderti dalla nostra magia!', 38134),
(204561, 'itIT', 'Rimpiangerai questa tua intrusione!', 'Rimpiangerai questa tua intrusione!', 38134),
(204560, 'itIT', 'Il tuo cadavere sarà un ottimo schiavo!', 'Il tuo cadavere sarà un ottimo schiavo!', 38134),
(204559, 'itIT', '', 'La mia morte... non cambia nulla...', 38134),
(204558, 'itIT', '', 'Tu ne sarai testimone!', 38134),
(204557, 'itIT', '', 'Il lavoro del mio padrone riplasmerà le Terretetre!', 38134),
(204556, 'itIT', '', 'Come osate interrompere il mio rituale!', 38134),
(204554, 'itIT', 'I deboli di mente non dovrebbero farmi sprecare tempo, ma se insisti ti mostrerò cos\'è il vero dolore.', '', 38134),
(204553, 'itIT', 'Lascia che le difese di Selvarden siano guidate dalle creature nel cielo.', '', 38134),
(204552, 'itIT', 'Le creature di Selvarden potrebbero sembrare docili, ma difenderanno il loro territorio con una forza e un coraggio tali da far sfigurare anche i più grandi campioni mai esistiti. Ne sarai all\'altezza?', '', 38134),
(204543, 'itIT', 'Il nostro ruolo nello Scopo al momento non può essere determinato con sicurezza.\n\nSarai anche $gabituata:abituato; a questa condizione, Calcafauce, ma per noi è una sensazione nuova.', '', 38134),
(204542, 'itIT', 'Che magnifica visione. \n\nAnche mentre la Fauce nega lo Scopo, è ancora una parte intrecciata di questo regno.', '', 38134),
(204541, 'itIT', 'La Fauce si agita con un tale potere, eppure non è ancora al servizio dello Scopo. \n\nÈ così che lo Scopo ci mette alla prova.', '', 38134),
(204540, 'itIT', 'Tutti abbiamo una parte da svolgere.\n\nÈ questo ciò che ci dice lo Scopo.\n\nQuando conoscevamo il significato di tali parti era facile da credere. Ora? Non ne sono più così sicuro.\n\nDobbiamo mantenere salda la fede, immagino, fidandoci dello Scopo.\n\nLa nostra via è lo Scopo.', '', 38134),
(204539, 'itIT', 'Lo Scopo può fornire una magnifica visione contemplativa di tanto in tanto, non credi?', '', 38134),
(204538, 'itIT', 'Chissà dove ci porteranno questi cambiamenti. Lo Scopo ci ha sempre guidati.\n\nQuesto fa pensare che sia ancora parte dello Scopo, solo che non so come andrà a finire.\n\nPersino un piccolo ingranaggio vuole capire il significato del suo ruolo.', '', 38134),
(204537, 'itIT', '$gBenvenuto:Benvenuta;, Calcafauce.\n\nNon tutto quello che vedi è in vendita, abbiamo anche un servizio di raccolta.\n\nChe strane creature che abbiamo trovato nel tuo regno, mi piace espandere la mia collezione!', '', 38134),
(204536, 'itIT', 'Ti do il benvenuto, Calcafauce.\n\nQuesto dispositivo ti permetterà di accedere al deposito di frammenti da tutte le dimensioni che separano questo mondo dal tuo. A differenza dei nostri amici Attendenti laggiù, con il loro sacro Scopo, io capisco come funziona.\n\nNon te lo dirò, però. Consideralo un segreto commerciale.\n\nSei comunque $glibero:libera; di usarlo. Ringrazia gli Alienatori.', '', 38134),
(204535, 'itIT', '', 'Per le cinque casate... i Paraspalle del Primus!', 38134),
(204534, 'itIT', '', 'Porta l\'armatura del Primus al nostro santuario, Calcafauce. Le daremo il posto d\'onore che le spetta.', 38134),
(204533, 'itIT', '', 'Questo Lich mette in pericolo entrambi i nostri regni. Quando le nostre forze saranno pronte a colpire, chiederemo a Revendreth di unirsi alla lotta.', 38134),
(204532, 'itIT', '', 'Tu non pretendi un bel niente da me, principe caduto! Dobbiamo saperne di più su questa minaccia prima di gettarcisi in modo sconsiderato.', 38134),
(204531, 'itIT', 'La farò pagare a Kel\'Thuzad per ciò che ha fatto! Baronessa Draka! Esigo che mi scorti immediatamente a Maldraxxus!', '', 38134),
(204530, 'itIT', 'Non importa. La Casata dei Rituali serve il mio volere. Se sarete così coraggiosi, affrontatemi là!', '', 38134),
(204529, 'itIT', 'Ficcanaso! Osate abbattere la mia servitrice rubare i paraspalle che le avevo donato?', '', 38134),
(204528, 'itIT', '', 'Il Lich stava attingendo a una fonte di grande potere...', 38134),
(204527, 'itIT', 'Umpf, quanta vergogna devi provare nel vedere in catene il tuo glorioso monarca.', '', 38134),
(204526, 'itIT', 'Che ci fai qui, tesoro?', '', 38134),
(204516, 'itIT', 'Io? Ovvio che no. Ma il Calcafauce, d\'altro canto... abbiamo buone possibilità.', '', 38134),
(204515, 'itIT', '', 'La Contessa? Credi davvero di poterla convincere a partecipare, Sire Garridan?', 38134),
(204514, 'itIT', 'Spero che porti il Brandicenere. Sarebbe uno spettacolo!', '', 38134),
(204513, 'itIT', '', 'Oh, non vedo l\'ora d\'incontrare Alexandros Mograine da Maldraxxus...', 38134),
(204506, 'itIT', 'Aigor è pronto a combattere!', '', 38134),
(204503, 'itIT', '', 'Non ci si può mai aspettare di dover combattere un\'illustre battaglia in quest\'orribile zona, ma ahimè, eccoci qui. Non farmi sprecare tempo.', 38134),
(204500, 'itIT', '', 'Diamo ascolto alle parole del Primus! Insieme, combatteremo per salvare le Terretetre!', 38134),
(204499, 'itIT', '', 'Hai rivelato il vero nemico di Maldraxxus, Calcafauce. E ora riporterai questi paraspalle al posto che spetta loro di diritto.', 38134),
(204497, 'itIT', 'Grazie, Calcafauce. Sarà un onore assistere alla corte del Principe Renathal.', '', 38134),
(204484, 'itIT', 'Cosa ti piacerebbe mettere in mostra, $n?', 'Cosa ti piacerebbe mettere in mostra, $n?', 38134),
(204474, 'itIT', '', 'Il mio dovere è proteggere l\'emissario, ma mi è giunta notizia di minacce che devono essere affrontate di tanto in tanto.', 38134),
(204472, 'itIT', 'Forse ora stai pensando: \"Quello è uno Gnomo incastrato dentro una strana massa melmosa. Forse ha bisogno di aiuto!\". No! Credici o no, ho scelto io di stare in questa \"cosa\". Sono in incognito, camuffato, infiltrato... in qualsiasi modo tu voglia chiamarlo. Questo sacco viscido è l\'unica cosa che mi protegge in questo luogo orribile. Per fortuna ho fatto delle scoperte incredibili riguardo a nuovi famigli da addestrare qui, e ho una squadra formata da campioni. Io combatterò per la vittoria e mi guadagnerò il rispetto che mi merito! L\'unico lato negativo è che tutte le mie cose ora sono appiccicose. Tutte.', '', 38134),
(204471, 'itIT', 'Torcibudella. Avanzino. Pezzi in più. Tu combatti.', '', 38134),
(204470, 'itIT', 'Ho atteso decine di anni che un degno avversario apparisse. Al vincitore vanno le spoglie!', '', 38134),
(204469, 'itIT', 'Anche le battaglie più insignificanti dovrebbero essere combattute con onore e attenzione. Mostra la tua squadra quando sei $gpronto:pronta;.', '', 38134),
(204466, 'itIT', '', 'Prendiamo le nostre difese molto seriamente. Piccoli o grandi che siano, ci impegniamo anima e corpo per preservare e addestrare i mezzi che sostengono il Bastione.', 38134),
(204457, 'itIT', 'Come fai a sopravvivere? Non indossi gli equipaggiamenti protettivi di base durante queste attività pericolose. Ecco, prendi questo.', '', 38134),
(204455, 'itIT', '', 'I nostri alleati attendono nella necropoli qui sopra.', 38134),
(204442, 'itIT', '', 'La Spada d\'Ebano combatte prontamente.', 38134),
(204441, 'itIT', 'La Spada d\'Ebano combatte prontamente.', '', 38134),
(204439, 'itIT', 'Molto è successo in tua assenza, $C.\n\nLascia che ti aggiorni in fretta.', '', 38134),
(204438, 'itIT', '', 'Molto è successo in tua assenza, $C.\n\nLascia che ti aggiorni in fretta.', 38134),
(204435, 'itIT', '', 'Sembra che il tuo Scopo e i nostri siano legati.', 38134),
(204431, 'itIT', '', 'Che magnifiche pianure qui. Un luogo glorioso per una battaglia. Sei $gpronto:pronta;?', 38134),
(204357, 'itIT', 'Indossi bene quegli abiti... per $gun:una; mortale.', 'Indossi bene quegli abiti... per $gun:una; mortale.', 38134),
(204356, 'itIT', '', 'La bestia è esposta! Calcafauce, abbattilo! Ti copriremo le spalle!', 38134),
(204355, 'itIT', '', 'Tormentarmi con una visione di questo campione dell\'Orda. Per aiutarmi? O speravi che combattessimo?', 38134),
(204344, 'itIT', '', 'Dovrebbe rivelarsi interessante...', 38134),
(204338, 'itIT', 'Va bene, che ne dici di uno di questi...', '', 38134),
(204320, 'itIT', 'Mi ci vedrei bene con uno di questi...', '', 38134),
(204317, 'itIT', 'Sei $gpronto:pronta; a tornare a Oribos?', '', 38134),
(204316, 'itIT', 'Che coraggio, Harlowe! Ti presenti sempre con lo stesso mio vestito.', 'Che coraggio, Harlowe! Ti presenti sempre con lo stesso mio vestito.', 38134),
(204311, 'itIT', 'Se non gli piace, non li voglio come fan!', '', 38134),
(204310, 'itIT', 'Certo che gli piacerà, Chiu. È epica!', '', 38134),
(204309, 'itIT', '', 'Spero che piaccia ai nostri fan...', 38134),
(204308, 'itIT', 'Le nostre nuove locandine si trovano nella cripta. Chiu ha realizzato i disegni.', '', 38134),
(204307, 'itIT', 'Non c\'è altra scelta che tornare dai tuoi alleati e riorganizzarsi.', '', 38134),
(204306, 'itIT', 'Kel\'Thuzad si è ritirato nella necropoli. Ora, noi due da soli non abbiamo alcuna speranza contro di lui.', '', 38134),
(204305, 'itIT', 'Presto! Potrebbe essere già tardi.', '', 38134),
(204304, 'itIT', 'Guarda là... Segni di una battaglia che portano alla sala del margravio.', '', 38134),
(204303, 'itIT', 'Be\', ce l\'hai fatta! Non ho mai dubitato di te nemmeno per un istante!', '', 38134),
(204302, 'itIT', 'Dovrai essere abile a trovare un percorso per raggiungerla.', '', 38134),
(204301, 'itIT', 'Prova a scalare quegli scaffali per raggiungere la leva.', '', 38134),
(204300, 'itIT', 'La leva è alla fine della sala.', '', 38134),
(204299, 'itIT', 'Una mano? Sì... sì! Per il Primus, è geniale!', '', 38134),
(204298, 'itIT', 'Una torcia? Anche se molti cattivi nascondono un interruttore segreto dentro le torce, una cella di una prigione non è il luogo ideale.', '', 38134),
(204297, 'itIT', 'A meno che tu non voglia parlare con qualcuno più sul tuo livello intellettuale, un teschio non ci servirà a niente.', '', 38134),
(204296, 'itIT', 'Temo che la mia magia non sia del tipo che manipola le catene. Vedi cos\'altro riesci a trovare.', '', 38134),
(204295, 'itIT', 'Guardie! Portate questa miserabile creatura in cella! E portatemi anche quel traditore d\'un Lich!', '', 38134),
(204294, 'itIT', 'Quindi c\'eri tu dietro tutto questo! E cos\'abbiamo qui... l\'Oculus di Balmedar!', '', 38134),
(204293, 'itIT', 'Impossibile! Aspetta... che inganno è mai questo?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204292, 'itIT', 'Cosa... cosa sta succedendo? Noooooo!', '', 38134),
(204291, 'itIT', 'Il sangue del Latore aprirà la strada!', '', 38134),
(204290, 'itIT', 'La barriera s\'indebolisce! I percorsi sigillati da tempi immemori saranno presto aperti!', '', 38134),
(204289, 'itIT', 'Sì! Vedo la strada! L\'oscurità della Fauce mi richiama a sé!', '', 38134),
(204288, 'itIT', 'Il rituale ha inizio! Libera i flussi d\'Animum!', '', 38134),
(204287, 'itIT', 'Per il tuo bene, speriamo che non siano fatali.', '', 38134),
(204286, 'itIT', 'Anche se sono piuttosto sicuro che il rituale fallirà, potrebbe esserci degli... effetti collaterali.', '', 38134),
(204285, 'itIT', 'Coinvolgerti nel rituale non faceva parte del piano, ma sembra che dobbiamo improvvisare.', '', 38134),
(204284, 'itIT', 'Come desideri, padrone. Sarà fatto subito!', '', 38134),
(204283, 'itIT', 'A quanto pare abbiamo un traditore tra di noi. Dobbiamo accelerare i nostri piani.', '', 38134),
(204282, 'itIT', 'Non si trovava da nessuna parte, padrone.', '', 38134),
(204281, 'itIT', 'Non perdere tempo a parlare di servitori! Che ne è del mio reagente?', '', 38134),
(204280, 'itIT', '... la carovana è stata completamente saccheggiata. Gli Alienatori di scorta sono stati brutalmente uccisi.', '', 38134),
(204279, 'itIT', 'Torna da me con i reagenti rimanenti. Ho un piano.', '', 38134),
(204278, 'itIT', 'Una misteriosa consegna dagli Alienatori? Davvero intrigante, devo saperne di più!', '', 38134),
(204277, 'itIT', 'Qualunque cosa richieda il barone, portala prima a me, così vedrò cosa intende usare.', '', 38134),
(204276, 'itIT', 'Forse alcuni di questi reagenti potrebbero aiutarci a dedurre la natura di questo rituale.', '', 38134),
(204275, 'itIT', 'Continua con il tuo stratagemma, vediamo cos\'altro possiamo scoprire.', '', 38134),
(204274, 'itIT', 'Nonostante i nostri sforzi, siamo ancora lontani dal verificare i piani del Barone.', '', 38134),
(204273, 'itIT', 'Per cosa vorrebbe utilizzarlo Kel\'Thuzad?', '', 38134),
(204272, 'itIT', '\"Vincolo dell\'Esiliato\"? Questo è qualcosa di più grosso di un semplice libro di storia.', '', 38134),
(204270, 'itIT', 'Che tipo di barriera sta cercando di erigere... o di superare? Strano.', '', 38134),
(204268, 'itIT', '\"Barriere e protezioni superiori, Volume IV: contro-incantesimi\". Una lettura davvero pesante.', '', 38134),
(204266, 'itIT', 'Non è che il suo rituale riguarda in qualche modo i sentieri che collegano i regni della Morte?', '', 38134),
(204265, 'itIT', '\"Percorsi dei Primi\"? Una scelta strana, è pieno di speculazioni e mezza verità.', '', 38134),
(204264, 'itIT', 'Cosa si aspettava di trovare in un volume così elementare?', '', 38134),
(204263, 'itIT', '\"Arcanum Alato\". Una scelta ovvia per le ricerche sul misticismo dei Kyrian.', '', 38134),
(204261, 'itIT', 'Io comprendo ciò che gli Indegni non comprendono.\n\nPerfezione è necessaria per successo! Devi prenderti cura dei tuoi attrezzi e delle tue risorse.', '', 38134),
(204259, 'itIT', 'Tuttavia, non vedo prove di tradimento. Vediamo cosa si trova in questi tomi che cerca di ottenere.', '', 38134),
(204258, 'itIT', 'Quindi il barone sta chiedendo aiuto ai Venthyr? Non è un affronto imperdonabile, se condividesse la sua parte.', '', 38134),
(204257, 'itIT', 'Ma ora basta riflettere. È tempo di prepararci per il rituale.', '', 38134),
(204256, 'itIT', 'E usando I bacini d\'Animum ottenuti dai nostri alleati a Revendreth, aprirò la strada per la vittoria.', '', 38134),
(204255, 'itIT', 'Dentro questo patetico Kyrian si cela la chiave per luoghi d\'immenso potere.', '', 38134),
(204254, 'itIT', 'Risponderai di questo, Lich! Non sono la preda di nessuno!', '', 38134),
(204253, 'itIT', 'Insieme alla mia preda personale, un tipo di Kyrian che viene chiamato \"Latore\" dalla sua gente.', '', 38134),
(204252, 'itIT', 'I costrutti sono tornati col restante bottino dell\'incursione al Bastione.', '', 38134),
(204251, 'itIT', 'Ma non ti sogneresti mai di farlo... vero?', '', 38134),
(204250, 'itIT', 'Se per qualche strano motivo ritieni opportuno tradirmi, farò in modo che il margravio scopra l\'identità di chi l\'ha ucciso.', '', 38134),
(204249, 'itIT', 'Oh, un piccolo appunto. Quell\'accolito che hai appena ucciso senza ragione era anche il preferito del Margravio Sin\'dane.', '', 38134),
(204248, 'itIT', 'Soddisfacente. Vieni da me nella Sala delle Lame, così posso verificare la qualità del tuo travestimento.', '', 38134),
(204247, 'itIT', 'Il tuo prossimo compito non sarà così semplice. Spero ti vada una sfida.', '', 38134),
(204246, 'itIT', 'Bene, hai trovato il mio piccolo \"pacco viveri\". Quindi non sei del tutto inutile.', '', 38134),
(204245, 'itIT', 'Non dire a nessuno di questo accordo.', '', 38134),
(204244, 'itIT', 'La torre è vicina alla Cresta delle Scapole. Là, troverai ciò di cui hai bisogno per procedere.', '', 38134),
(204242, 'itIT', 'Mi chiedo per chi suoneremo.', '', 38134),
(204241, 'itIT', '', 'Mi sento di nuovo me stessa con questi strumenti...', 38134),
(204240, 'itIT', 'Finalmente!', '', 38134),
(204239, 'itIT', 'Il tempo della riflessione è finito. Che le prove inizino!', '', 38134),
(204238, 'itIT', 'Buone notizie, gente! Ho fissato il nostro prossimo concerto... uno di quelli GROSSI!', '', 38134),
(204237, 'itIT', '', 'Nella mia vita mortale, non sono mai riuscita a restare in un unico posto a lungo.\n\nOgni giorno era una nuova avventura ed ero determinata a viverla.\n\nPercepisco la stessa voglia di viaggiare in te.\n\nDimmi quindi, viandante... Credi nella teoria del Sorvegliante Tedo? La musica è la salvezza dell\'anima?\n\nNon ne sono così sicura.', 38134),
(204236, 'itIT', 'Ansel si preoccupa troppo, andrà tutto bene.\n\nCome diceva la mia Asha, ogni duna risalita è una nuova opportunità di trovare l\'acqua.\n\nLa musica ci salverà, come ha sempre fatto.', '', 38134),
(204235, 'itIT', 'Hai mai sentito dire che la musica è il cibo dell\'anima?\n\nUna metafora, naturalmente...\n\n<Il Sorvegliante Tedo ti fissa.>\n\nMa se fosse più di questo?\n\nE se per pochi selezionati, attratti dalla musica nella loro vita mortale, fosse uno strumento di salvezza?\n\nE se la musica potesse davvero curare l\'anima?\n\nCredo che ci sia del vero.', '', 38134),
(204233, 'itIT', '', 'Ora che abbiamo ripristinato maggiormente il Conservatorio hai accesso a più Semebrado e ai Lotti Catalizzatori.\n\nI Lotti Catalizzatori ti permettono di fornire benefici aggiuntivi ai Semebrado prima di mettervi uno spirito all\'interno.\n\nQuando pianterai un Catalizzatore vedrai a quali Semebrado è collegato.', 38134),
(204232, 'itIT', 'Come potremmo far colpo con la musica sui nostri sorveglianti se continuiamo a perdere gli strumenti?\n\n<Ansel il Fortunato adocchia le Pietre del Peccato all\'esterno.>\n\nNon ce ne andremo mai da qui.', '', 38134),
(204229, 'itIT', '', '$GBenvenuto:Benvenuta;, $n. Come posso aiutarti quest\'oggi?', 38134),
(204228, 'itIT', '', '$gBenvenuto:Benvenuta;, $n. Una volta rimesso in sesto, potrai accedere al Vivaio della Regina da questo portale fungino.\n\nParla con Zayhad per vedere tutto ciò che ha bisogno di una sistemata.', 38134),
(204227, 'itIT', 'Biancachioma ti racconterà il triste destino che ha colpito i nostri alleati.', '', 38134),
(204226, 'itIT', 'Nazgrim ti racconterà il triste destino che ha colpito i nostri alleati.', '', 38134),
(204225, 'itIT', 'Il Gran Signore Bolvar Domadraghi si è ripreso dalla sua battaglia contro Sylvanas Ventolesto. Lui e i nostri alleati ci aspettano.', '', 38134),
(204224, 'itIT', 'Ho disposto che Acherus venisse spostato fuori dalla Rocca della Corona di Ghiaccio. Permettimi di aprirci un Portale della Morte.', '', 38134),
(204223, 'itIT', 'I preparativi sono ultimati.', '', 38134),
(204222, 'itIT', 'La Spada d\'Ebano è pronta. Ti aspetto a Orgrimmar, raggiungimi prima che la gelida morsa della Morte rivendichi tutta Azeroth.', '', 38134),
(204221, 'itIT', 'Il velo tra il nostro mondo e le Terretetre è stato squarciato.', '', 38134),
(204220, 'itIT', 'La Spada d\'Ebano è pronta. Ti aspetto a Roccavento, raggiungimi prima che la gelida morsa della Morte rivendichi tutta Azeroth.', '', 38134),
(204219, 'itIT', 'Il velo tra il nostro mondo e le Terretetre è stato squarciato.', '', 38134),
(204217, 'itIT', 'Indeboliscilo, così posso usare il bastone per liberarla!', '', 38134),
(204216, 'itIT', 'Kel\'Thuzad sta assorbendo potere dal Margravio Sin\'dane!', '', 38134),
(204215, 'itIT', 'Servitori! Eliminate questi intrusi!', '', 38134),
(204211, 'itIT', '<Il baule scricchiola all\'apertura, rivelando un assortimento di completi e accessori.>>', '', 38134),
(204194, 'itIT', 'Vedi cosa sta indossando Harlowe? Che banalità. Dovrei strappargli quei vestiti dalla schiena!', 'Vedi cosa sta indossando Harlowe? Che banalità. Dovrei strappargli quei vestiti dalla schiena!', 38134),
(204192, 'itIT', 'Sera. I nuovi capi mi dicono di andare là fuori e scoprire cosa possiamo fare con l\'Animum in arrivo.\n\nSe ne abbiamo abbastanza potremo ricostruire molti dei muri che continuano a cadere dalla parte di Revendreth.', '', 38134),
(204187, 'itIT', 'Interferite quanto volete, mortali. State solo ritardando l\'inevitabile.', '', 38134),
(204186, 'itIT', 'Credete di essere speciali, dei prodigi, ma imparerete a essere umili.', '', 38134),
(204185, 'itIT', 'È stato Vyraz a intrappolarmi all\'interno della Fauce. Senza dubbio è stato lui a uccidere il nostro margravio.', '', 38134),
(204182, 'itIT', 'Siete tremendamente prevedibili. Ma d\'altronde, immagino che il fallimento sia naturale per la vostra razza.', '', 38134),
(204176, 'itIT', 'Vuoi volare? Sì?', 'Vuoi volare? Sì?', 38134),
(204175, 'itIT', 'Notevole, presumo. Comunque, non vedo l\'ora di porre fine alla vostra esistenza.', '', 38134),
(204174, 'itIT', '', 'Sarebbe folle inseguirli ora. Non perdere la concentrazione, ci sarà tempo per le sceneggiate.', 38134),
(204164, 'itIT', 'Spezzare l\'elmo, tuttavia, ha anche liberato Bolvar Domadraghi dal fardello del Re dei Lich. Ora gode della stessa libertà di cui godiamo tutti.', '', 38134),
(204155, 'itIT', 'Ecco qui! Usa al flauto vicino al cunicolo dei gormini per farli uscire dal nascondiglio!', '', 38134),
(204138, 'itIT', 'Che gli spiriti dei miei antenati siano al sicuro in regni di conforto e di pace.', '', 38134),
(204137, 'itIT', 'Ho visto solo gli angoli più oscuri delle Terretetre. È bello sapere che ci sono anche luoghi di speranza.', '', 38134),
(204133, 'itIT', '', 'Thrall, sai dove hanno portato Anduin?', 38134),
(204132, 'itIT', 'Amici miei!', '', 38134),
(204125, 'itIT', 'Si dice che un capo sia un capo come qualsiasi altro capo, ma nessun capo è un capo che frusta meno dell\'ultimo capo.\n\nTranne che per il Principe. È uno a posto, coi denti aguzzi e tutto. Proprio un tipo con la tosta sulle spalle.', '', 38134),
(204124, 'itIT', 'Sono stato in molti campi di battaglia di molti regni lungo gli eoni. \n\nIn tutti quei conflitti nulla è mai stato più onorevole di servire al fianco del Principe Renathal, del Generale Draven, e al tuo fianco.\n\nCombattiamo per l\'integrità della nostra realtà contro forze impossibili. Questo è davvero ciò per cui sono stato fatto.', '', 38134),
(204123, 'itIT', 'Se il Principe Renathal vuole davvero recuperare così tanto territorio, avremo bisogno di più Litogeni da radunare per la sua causa.\n\nSarebbe stato molto più facile se avesse convinto la Madrepietra a unirsi a noi. Allo stato attuale, dobbiamo animare solo Litogeni specifici, altrimenti risveglieremmo altre forze per i nostri nemici.\n\nPer fortuna, ci sono io a dirti quelli che sono leali a noi.', 'Se il Principe Renathal vuole davvero recuperare così tanto territorio, avremo bisogno di più Litogeni da radunare per la sua causa.\n\nSarebbe stato molto più facile se avesse convinto la Madrepietra a unirsi a noi. Allo stato attuale, dobbiamo animare solo Litogeni specifici, altrimenti risveglieremmo altre forze per i nostri nemici.\n\nPer fortuna, ci sono io a dirti quelli che sono leali a noi.', 38134),
(204122, 'itIT', 'Farò in modo di eliminare anche tutti i potenziali piani di ritorsione dalla registrazione.', 'Farò in modo di eliminare anche tutti i potenziali piani di ritorsione dalla registrazione.', 38134),
(204114, 'itIT', 'In base ai miei registri, hai recuperato tutti i cimeli rubati.', 'In base ai miei registri, hai recuperato tutti i cimeli rubati.', 38134),
(204108, 'itIT', 'Quest\'oggi il Margravio Gharmal morirà.', '', 38134),
(204107, 'itIT', 'Molti Aspiranti, poco succo! Torna più tardi, va bene?', 'Molti Aspiranti, poco succo! Torna più tardi, va bene?', 38134),
(204104, 'itIT', 'Uuuh! Questo ha da bere! Prendi! Prendi!', 'Uuuh! Questo ha da bere! Prendi! Prendi!', 38134),
(204103, 'itIT', '<Il Factotum ti offre il suo vassoio emettendo un leggero bubbolio.>', '<Il Factotum ti offre il suo vassoio emettendo un leggero bubbolio.>', 38134),
(204100, 'itIT', 'Aspirante ha sete? Factotum ha da bere!', 'Aspirante ha sete? Factotum ha da bere!', 38134),
(204099, 'itIT', 'Bevi! Bevi! Bevi!', 'Bevi! Bevi! Bevi!', 38134),
(204097, 'itIT', 'Vuoi bere? Molto gustoso!', 'Vuoi bere? Molto gustoso!', 38134),
(204081, 'itIT', '', 'Buona giornata, $gamico:amica;!\n\nC\'è molto da fare.\n\nSe hai un momento, apprezzerei qualsiasi aiuto tu possa darmi.', 38134),
(204080, 'itIT', 'Se lo riportassimo al Margravio Sin\'dane, forse avremmo una possibilità contro Kel\'Thuzad.', '', 38134),
(204079, 'itIT', 'Aspetta... Il Bastone del Primus! È ancora tutto intero!', '', 38134),
(204078, 'itIT', '', 'Va\' all\'Anello del Trasferimento e apri un varco per Selvarden. Che lo Scopo ti guidi, mortale.', 38134),
(204077, 'itIT', '', 'I boschi di Selvarden contano sull\'Animum per nutrire gli spiriti della natura che dormono lì. Spero che le scorte della Regina abbiano resistito a questa terribile siccità.', 38134),
(204076, 'itIT', '', 'La Regina dell\'Inverno è sempre stata una stretta alleata del Primus. Non ho dubbi che presterà attenzione ai suoi avvertimenti.', 38134),
(204075, 'itIT', 'Insieme, possiamo concentrarci sul regno del Carceriere. Ma fai attenzione... Non perderti nell\'oscurità.', '', 38134),
(204074, 'itIT', 'L\'elmo infranto è ancora in risonanza col potere del Re dei Lich. Il potere... della Fauce.', '', 38134),
(204073, 'itIT', '', 'Sire Denathrius regna su Revendreth. Mi auguro che abbia sopportato la siccità d\'Animum meglio degli altri regni. Ci serve disperatamente il suo aiuto.', 38134),
(204072, 'itIT', '', 'È la destinazione per le anime arroganti e altezzose nelle quali l\'Arbiter vede un barlume di potenziale. I Venthyr guidano queste anime lungo la via per la redenzione.', 38134),
(204071, 'itIT', '', 'Revendreth è un regno inquietante di guglie oscure e fredda pietra. Ma il territorio e i suoi abitanti servono un nobile scopo.', 38134),
(204070, 'itIT', '', 'Senza la forza di Maldraxxus dalla nostra parte, temo che non avremo alcuna speranza di fermare l\'Esiliato.', 38134),
(204069, 'itIT', '', 'Mortale, devi capire il perché abbiano attaccato i Kyrian. Fai tutto il necessario per fermare questo conflitto prima che peggiori ulteriormente.', 38134),
(204068, 'itIT', 'Non è sopravvissuta...', '', 38134),
(204067, 'itIT', '', 'Gweyir! No...', 38134),
(204066, 'itIT', '', 'Resta con noi, Gweyir! La maledizione è quasi spezzata!', 38134),
(204065, 'itIT', '', 'Continua così! La morsa dei Drust su di lei s\'indebolisce.', 38134),
(204064, 'itIT', '', 'Distruggi quelle cose, presto!', 38134),
(204063, 'itIT', '', 'Preparati, Calcafauce. La stregoneria di Gorak Zhar non si dissiperà facilmente.', 38134),
(204062, 'itIT', '', 'Mi servirà un costrutto piccolo, rapido e dal passo leggero.\n\nI miei nemici mi vedono raramente, ma hanno sempre paura che io sia lì tra loro.', 38134),
(204060, 'itIT', 'Sapevo che avresti avuto bisogno di me.', '', 38134),
(204059, 'itIT', 'Avrò bisogno di un forte costrutto di ferro.\n\nCosì potrei risolvere ogni problema che hai.', '', 38134),
(204058, 'itIT', 'Sire Garridan non stava esagerando, che corte lodevole!', 'Sire Garridan non stava esagerando, che corte lodevole!', 38134),
(204057, 'itIT', 'Dov\'è Stefan lo Schizzinoso? Mi aveva promesso un campione privato.', 'Dov\'è Stefan lo Schizzinoso? Mi aveva promesso un campione privato.', 38134),
(204056, 'itIT', 'Sono felice che Teotar sia tornato dall\'esilio. Che verve!', 'Sono felice che Teotar sia tornato dall\'esilio. Che verve!', 38134),
(204055, 'itIT', 'Hai visto un\'enorme bevanda da qualche parte?', 'Hai visto un\'enorme bevanda da qualche parte?', 38134),
(204054, 'itIT', 'Spero che questa corte non finisca mai!', 'Spero che questa corte non finisca mai!', 38134),
(204053, 'itIT', 'Ho rimorchiato quel VIP laggiù... Non c\'è di che!', 'Ho rimorchiato quel VIP laggiù... Non c\'è di che!', 38134),
(204052, 'itIT', 'Mi sto divertendo un mondo!', 'Mi sto divertendo un mondo!', 38134),
(204051, 'itIT', 'Devo ammetterlo, la tua corte è intrigante.', 'Devo ammetterlo, la tua corte è intrigante.', 38134),
(204014, 'itIT', 'Non vedo l\'ora di scoprire se le voci sulla Corte delle Braci siano vere!', 'Non vedo l\'ora di scoprire se le voci sulla Corte delle Braci siano vere!', 38134),
(204013, 'itIT', 'A Lidoscuro saranno tutti gelosi di me!', 'A Lidoscuro saranno tutti gelosi di me!', 38134),
(204012, 'itIT', 'Quante persone famose credi che incontrerò oggi?', 'Quante persone famose credi che incontrerò oggi?', 38134),
(204011, 'itIT', 'Detto tra noi, non riesco ancora a credere di aver trovato un invito... sono così emozionato!', 'Detto tra noi, non riesco ancora a credere di aver trovato un invito... sono così emozionata!', 38134),
(204010, 'itIT', 'Questa corte sarà sulla bocca di tutti!', 'Questa corte sarà sulla bocca di tutti!', 38134),
(204009, 'itIT', 'Che ne pensi dei miei stivali? Ho applicato questi spuntoni lunghissimi per l\'occasione!', 'Che ne pensi del mio vestito? Lo riservavo per un\'occasione speciale.', 38134),
(204008, 'itIT', 'Non vedo l\'ora che aprano il cancello!', 'Non vedo l\'ora che aprano il cancello!', 38134),
(204007, 'itIT', 'Dammi un indizio, Calcafauce! Quali nuove emozioni ci aspettano nella corte di oggi?', 'Dammi un indizio, Calcafauce! Quali nuove emozioni ci aspettano nella corte di oggi?', 38134),
(204005, 'itIT', 'Ara\'lon... Invierò subito una squadra di ricerca. Non permetteremo che i Drust lo uccidano.', '', 38134),
(204003, 'itIT', '', 'Ara\'lon non è ancora tornato con la borsa. Temo che possa essere in pericolo... O peggio.', 38134),
(204002, 'itIT', '', 'Incontriamoci al Cuore della Foresta per completare il rituale. Buona caccia, a entrambi.', 38134),
(204000, 'itIT', '', 'Preferisce una corte Formale, quindi ricordati di concentrare tutte le tue azioni sulle formalità.', 38134),
(203999, 'itIT', 'Abbiamo ricevuto l\'RSVP del Custode delle Cripte Kassir, e parteciperà.', '', 38134),
(203998, 'itIT', 'Non tornerò senza. Hai la mia parola!', '', 38134),
(203997, 'itIT', '', 'Calcafauce, tu raccogli la Radice della Notte. Ara\'lon, ci servirà una Borsa della Strega, rubarne una dai Drust sarà un compito pericoloso.', 38134),
(203996, 'itIT', 'Coordinati con il nostro personale per ottenere i contratti e gli RSVP, io mi occuperò del resto!', '', 38134),
(203995, 'itIT', 'Conosco bene il nostro primo ospite, quindi so esattamente che tipo di corte gli piacerà!', '', 38134),
(203993, 'itIT', 'Dovremo parlare con i suoi Dragatori, che sembrano sempre avere un bollitore pronto sul fuoco.', '', 38134),
(203992, 'itIT', 'Temo che Teotar abbia bevuto la nostra intera scorta di tè durante le prove.', '', 38134),
(203990, 'itIT', 'Gresit provvederà a tutto. Ti segno la sua posizione sulla mappa.', '', 38134),
(203989, 'itIT', 'Se non sai da dove cominciare, la mia lista d\'intrattenimento ti dirà dove andare.', '', 38134),
(203988, 'itIT', 'Aiuta l\'Accusatrice e i suoi ritualisti con tutto ciò di cui hanno bisogno e in cambio ci aiuteranno, capito?', '', 38134),
(203987, 'itIT', 'Nessuno esegue i rituali meglio dei ritualisti delle Sale della Redenzione.', '', 38134),
(203984, 'itIT', 'Avere la testa sulle spalle assicura che i nostri Latori portino a Oribos solo le anime pronte. Ben fatto.', '', 38134),
(203983, 'itIT', 'Un Guardiano deve determinare se un\'anima sia pronta per essere portata nelle Terretetre. Affina le tue capacità con questa prova.', '', 38134),
(203981, 'itIT', '', 'Ottimo tempismo voi due! Anche se per poco...', 38134),
(203980, 'itIT', 'Se stai cercando Kivarr, l\'hanno appena trascinata via!', '', 38134),
(203979, 'itIT', '', 'La mia gabbia ha tre serrature separate, ciascuna con la propria chiave. Credo che i Drust abbiano paura di me.', 38134),
(203974, 'itIT', 'Esplora il bosco. I Drust non possono averla portata lontano.', '', 38134),
(203973, 'itIT', 'Kivarr non è qui... Ma ci sono tracce di lotta. Dev\'esserle successo qualcosa!', '', 38134),
(203970, 'itIT', 'Non ho intenzione di sporcarmi troppo le mani, ma comunque non si può combattere una guerra a stomaco vuoto.\n\nVorrei un costrutto con una buona testa, mani forti e un sacco di bocche per assaggiare i miei piatti.', '', 38134),
(203969, 'itIT', '', 'Voglio due lame giganti in mano. Immagina il terrore dei nemici, quando vedranno le mie mani!\n\nSarà fantastico... vero?', 38134),
(203968, 'itIT', '', 'Mi sono stancata di essere uno scudo di carne che si butta a capofitto contro venti nemici alla volta!\n\nHo sempre voluto lanciare gli incantesimi e fare esplodere i nemici con i miei bombardamenti magici.\n\nCostruiscimi un costrutto che può farlo!', 38134),
(203966, 'itIT', 'Prescelti! I nostri nemici agiscono contro di noi! La guerra è imminente, e la vittoria sarà nostra!', '', 38134),
(203965, 'itIT', 'D\'accordo. Metti alla prova il mortale, poi unisciti a me nella necropoli. Abbiamo una guerra da vincere.', '', 38134),
(203964, 'itIT', 'Salve, viandante.\n\nSto facendo ricerche sulle antiche reliquie qui nelle Terretetre.\n\nVuoi unirti a me?', 'Salve, viandante.\n\nSto facendo ricerche sulle antiche reliquie qui nelle Terretetre.\n\nVuoi unirti a me?', 38134),
(203961, 'itIT', 'Archeologia? Di cosa si tratta?\n\n<Ti ascolta mentre gli spieghi il procedimento e il ragionamento archeologico.>\n\nAffascinante, ma non credo che ci sia qualcosa di simile qui.\n\nVoi mortali potreste perdere dei segreti nel tempo, ma qui le cose funzionano diversamente.\n\nTuttavia, se le cose dovessero cambiare, te lo farò sapere.', 'Archeologia? Di cosa si tratta?\n\n<Ti ascolta mentre gli spieghi il procedimento e il ragionamento archeologico.>\n\nAffascinante, ma non credo che ci sia qualcosa di simile qui.\n\nVoi mortali potreste perdere dei segreti nel tempo, ma qui le cose funzionano diversamente.\n\nTuttavia, se le cose dovessero cambiare, te lo farò sapere.', 38134),
(203960, 'itIT', '', 'Abbiamo entrambi imparato molto da lui... E insieme, lo vendicheremo.', 38134),
(203959, 'itIT', 'Mi fa piacere sapere che hai servito bene Krexus. Ha sempre ammirato il tuo spirito, anche quando rispondevi ad Akarek.', '', 38134),
(203958, 'itIT', 'Ha forgiato la mia forza in vittorie che pensavo impossibili. Strapperò il cuore oscuro di Vyraz per averlo tradito.', '', 38134),
(203957, 'itIT', '', 'Krexus ha parlato molto bene di te, Brandicenere. Loda raramente gli altri.', 38134),
(203956, 'itIT', '', 'La sopravvivenza è per i deboli e i timorosi. O si vince... o si muore.', 38134),
(203955, 'itIT', '', 'Il margravio mi ha affidato un nuovo scopo, Vashj. Lasciarci il passato alle spalle è l\'unico modo in cui possiamo sopravvivere.', 38134),
(203948, 'itIT', '', 'È per questo che ti sei nascosta dietro il vessillo dei Prescelti mentre i tuoi fratelli degli Occhi venivano uccisi?', 38134),
(203947, 'itIT', '', 'Io non ho abbandonato il Margravio Akarek. Ero stata mandata in missione prima della caduta della nostra casata, proprio come te.', 38134),
(203944, 'itIT', '', 'Bene, cos\'abbiamo qui? Uno dei cagnolini di Kel\'Thuzad.', 38134),
(203943, 'itIT', 'No? Che peccato. Tuttavia, permettimi di lasciarti andare con un... regalino d\'addio.', '', 38134),
(203942, 'itIT', 'Quindi, mortale, hai trovato il modo di fuggire. Sei qui per festeggiare la mia vittoria?', '', 38134),
(203941, 'itIT', 'Forse. Ma tale è il prezzo del potere.', '', 38134),
(203940, 'itIT', '', 'Pagherai per il tuo tradimento!', 38134),
(203939, 'itIT', '', 'La tua debolezza non è adatta alla Casata dei Rituali, Sin\'dane. È tempo di designare un nuovo margravio.', 38134),
(203938, 'itIT', '', 'Ninadar?!? Come osi!', 38134),
(203937, 'itIT', 'Sembra che il vecchio Vol\'jin inizierà na nuova caccia... dopo essersi riposato.', '', 38134),
(203936, 'itIT', 'Da solo? No.', '', 38134),
(203935, 'itIT', '', 'Cos\'è questo tradimento! Credi davvero di essere all\'altezza del potere di un margravio?', 38134),
(203934, 'itIT', '', 'Ho sentito parlare della tua regina, presuntuosa e senza cuore. Sembra che tu abbia molto in comune con lei... sia nella forma che nella sostanza.', 38134),
(203933, 'itIT', '', 'Le macchinazioni della Regina Azshara ci hanno fornito un potere inimmaginabile. La Separazione era un piccolo prezzo da pagare per la nostra Ascensione.', 38134),
(203931, 'itIT', '', 'Sembra che tu sappia molto sui mondi infranti, Vashj... E sulla fraternizzazione con la feccia demoniaca.', 38134),
(203930, 'itIT', '', 'Una volta ho visto il tuo mondo personalmente, Draka. O meglio, la rovina che si è abbattuta su di esso.', 38134),
(203929, 'itIT', '', 'Ha dimostrato forza e resilienza. Qualità necessarie in questi tempi bui.', 38134),
(203922, 'itIT', 'Hai fatto bene a tirare fuori il mortale dall\'arena. Non potevamo permettere che cadesse in mano ai nostri nemici.', '', 38134),
(203921, 'itIT', 'Non lasciarti sopraffare dalla presunzione. Concentrati!', '', 38134),
(203919, 'itIT', 'La maledizione dei Drust presto piegherà Gweyir al loro volere. Tenerla qui è un rischio troppo grande!', '', 38134),
(203918, 'itIT', '', 'Farò rapporto alla Baronessa Vashj. È un onore combattere al tuo fianco, Calcafauce.', 38134),
(203914, 'itIT', 'Gweyir! Respira ancora! Ma... Che cosa le hanno fatto?', '', 38134),
(203911, 'itIT', 'Nera era la più feroce tra noi. Non si sarebbe mai inchinata ai Drust.', '', 38134),
(203910, 'itIT', 'Taeras... che la notte ti abbracci, vecchio amico...', '', 38134),
(203909, 'itIT', '', 'Questo portale è solo il primo di molti. Una degna vittoria.', 38134),
(203908, 'itIT', 'Azelor... come temevo. Si meritava meglio di così...', '', 38134),
(203903, 'itIT', 'L\'intero bosco è stato posseduto! Fa\' attenzione a non farti smascherare.', '', 38134),
(203902, 'itIT', 'Mortale, qui! Ho bisogno di aiuto...', '', 38134),
(203901, 'itIT', 'Zzz... Riporta... Treshala... Darnassus... Zzz...', '', 38134),
(203899, 'itIT', 'Tutti abbiamo molto da imparare, Uther. Vieni, approfondiamo la cosa al mio tempio.', '', 38134),
(203896, 'itIT', 'Dobbiamo capire che cosa lo affligge. Ho il presentimento che ci sia molto altro che non sappiamo.', '', 38134),
(203895, 'itIT', 'Ti scongiuro di mettere Uther sotto la mia tutela.', '', 38134),
(203894, 'itIT', '', 'Ora che abbiamo il controllo della necropoli, possiamo aprire un portale verso il Seggio del Primus.', 38134),
(203893, 'itIT', 'Ci serviranno le sue abilità sul campo di battaglia. Devi aiutarlo a superare la cosa!', '', 38134),
(203892, 'itIT', 'Pelagos è venuto per cercare di prendere i Collettori per aiutarti, ma è stato consumato dai dubbi sulla battaglia che verrà.', '', 38134),
(203891, 'itIT', '', 'Non ha speranze di aiutarci contro di lei in questo stato. Ripristina i vespri per stabilizzarla!', 38134),
(203889, 'itIT', 'Ti conferisco questo potere, per difendere Maldraxxus!', '', 38134),
(203885, 'itIT', '', 'La loro angoscia è deliziosa!', 38134),
(203884, 'itIT', '', 'Inondiamoci del loro tormento!', 38134),
(203883, 'itIT', 'Il mio regno è intramontabile!', '', 38134),
(203882, 'itIT', 'Morirete ai miei piedi!', '', 38134),
(203881, 'itIT', 'Nessuno sopravvivrà!', '', 38134),
(203865, 'itIT', '', 'Ora!', 38134),
(203864, 'itIT', '', 'Assaggiate i nostri colpi! Resta solo un compito.', 38134),
(203863, 'itIT', '', 'Restate all\'erta! Delle spie si nascondono tra i nostri ranghi!', 38134),
(203858, 'itIT', 'I nemici rimangono saldi nelle proprie convinzioni. Se non riusciremo a raggiungerli con le parole, dovremo raggiungerli con le azioni. Lascio a te questo compito ingrato.', '', 38134),
(203857, 'itIT', 'Col tempo, capiranno i loro errori. Spero che abbiano imparato una lezione importante oggi.', '', 38134),
(203848, 'itIT', '', 'La gratitudine di una creatura è la ragione dell\'altra. Il Bastione si rafforza tramite il tuo impegno.', 38134),
(203847, 'itIT', '', 'La rivalità tra Nemea e Pelodis è durata eoni. Sono certa che tornerai loro utile... Ovviamente, nello spirito della competizione.', 38134),
(203844, 'itIT', '', 'Sei sempre stato troppo debole per seguire la vera via. Devos avrebbe dovuto lasciarti a marcire in quel campo!', 38134),
(203843, 'itIT', 'Cercavamo di salvare il nostro popolo, non di vincolarlo a una tale oscurità!', '', 38134),
(203841, 'itIT', 'Lysonia... perché...', '', 38134),
(203840, 'itIT', '', 'Ora capisco quale fosse il tuo ruolo. Tu eri la chiave per il nostro vero destino! Il mio destino!', 38134),
(203839, 'itIT', 'La mia anima porta il marchio di questo luogo?', '', 38134),
(203838, 'itIT', 'Vedo soltanto oscurità...', '', 38134),
(203837, 'itIT', '', 'Non discutere. Fallo!', 38134),
(203836, 'itIT', 'Lysonia, io...', '', 38134),
(203835, 'itIT', '', 'Più potere! Uccidilo se devi!', 38134),
(203834, 'itIT', 'Agli ordini.', '', 38134),
(203833, 'itIT', '', 'Non si sta spezzando! Raddoppia gli sforzi.', 38134),
(203832, 'itIT', '', 'Ho aumentato le ronde come richiesto, Polemarco. L\'attività degli Indegni ultimamente è aumentata... drasticamente.', 38134),
(203831, 'itIT', '', 'Andate, miei Indegni. Completate i preparativi, tra poco sorgerà una nuova alba.', 38134),
(203830, 'itIT', 'I devoti all\'Arconte s\'impegnano per ricostruire l\'Emblema dell\'Ascensione, potenziato dalle altre congreghe.', '', 38134),
(203829, 'itIT', '', 'Polemarco, Calcafauce. Ho qualcosa che dovete vedere.', 38134),
(203828, 'itIT', '', 'E anche Uther, temo.', 38134),
(203827, 'itIT', '', 'Potremmo avere bisogno di loro. I miei guardiani mi dicono che i sospetti di Thenios su Lysonia alla guida degli Indegni si sono avverati.', 38134),
(203826, 'itIT', '', 'Questo nuovo emblema renderà gli Ascesi addirittura più forti di prima!', 38134),
(203819, 'itIT', 'Con Devos fuori dai giochi, Lysonia prenderà il suo posto alla guida degli Indegni. Prego di trovare la pace prima che inizi la loro campagna.', '', 38134),
(203813, 'itIT', 'La sua mano, Lysonia, si è unita a Uther per seguirla. Mentre le nostre scorte d\'Animum svanivano, loro crescevano di numero.', '', 38134),
(203812, 'itIT', 'Allontanarsi dalla via quando si è l\'incarnazione della lealtà stessa, ha fatto sì che il percorso di Devos conducesse al dubbio e alla violenza.', '', 38134),
(203805, 'itIT', 'Devos aveva buone intenzioni... voleva rimediare a un\'ingiustizia... Ma Uther non era pronto per le prove dell\'ascensione.', '', 38134),
(203799, 'itIT', 'È tempo che tu veda ciò che in pochi hanno visto, Calcafauce... L\'arrivo di Uther al Bastione.', '', 38134),
(203798, 'itIT', 'Questo... questo contiene...', '', 38134),
(203797, 'itIT', 'L\'Arconte mi ha negato la mia giustizia. Devos mi ha assicurato che quel mostro soffre nel posto a cui appartiene!', '', 38134),
(203796, 'itIT', '', 'Vedremo la sua gloriosa visione avverarsi. Kyrian, tutti i Kyrian, liberi dalle catene dei tuoi rituali!', 38134),
(203795, 'itIT', '', 'Con questi frammenti posso mettere insieme dei nuovi specchi per la prossima Corte delle Braci.', 38134),
(203791, 'itIT', 'Hai avuto la tua occasione di arrenderti. Ora vendicheremo Krexus con il tuo sangue!', '', 38134),
(203789, 'itIT', '', 'Il tuo tradimento ha segnato il tuo destino. Giustizia sarà fatta!', 38134),
(203788, 'itIT', 'Abbandona questa follia! Il Primus non lo tollererebbe!', '', 38134),
(203787, 'itIT', 'Salve, mortale.\n\nSono il creatore della musica dai peccati delle anime che cantano le gesta della loro dipartita nelle tetre profondità di questa sacra sala. \n\nForse, quando il tuo tempo sarà giunto, ti unirai al mio coro...', 'Salve, mortale.\n\nSono la creatrice della musica dai peccati delle anime che cantano le gesta della loro dipartita nelle tetre profondità di questa sacra sala. \n\nForse, quando il tuo tempo sarà giunto, ti unirai al mio coro...', 38134),
(203781, 'itIT', 'Continua ad attaccare, Calcafauce! Fa\' fuori il loro capo.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203772, 'itIT', '', 'È iniziato tutto dopo che l\'Orda ha distrutto Teldrassil, dimora dei Kaldorei.\n\nL\'Alleanza non poteva inviare soldati per dare loro una mano a riconquistare quel poco rimasto di Teldrassil, per cui la mia Minn\'do non ha avuto altra scelta.\n\nHa pregato Elune perché le desse il potere della Guerriera della Notte, una rappresentazione dell\'ira di Elune.\n\nTyrande ora possiede dentro di sé la furia di Elune, ma un tale potere ha ucciso chiunque l\'abbia impugnato troppo a lungo.', 38134),
(203771, 'itIT', '', 'Non lo sai? Credevo che le storie sulla furia della mia Minn\'do si fossero sparse tra i membri dell\'Orda.\n\nLa tua gente ha distrutto Teldrassil, dimora dei Kaldorei.\n\nL\'Alleanza non poteva inviare soldati per dare loro una mano a riconquistare quel poco rimasto di Teldrassil, per cui la mia Minn\'do non ha avuto altra scelta.\n\nHa pregato Elune perché le desse il potere della Guerriera della Notte, una rappresentazione dell\'ira di Elune.\n\nTyrande ora possiede dentro di sé la furia di Elune, ma un tale potere ha ucciso chiunque l\'abbia impugnato troppo a lungo.', 38134),
(203770, 'itIT', '', 'Useremo il resto dell\'Animum per teletrasportarci alla fonderia. Incontriamoci là.', 38134),
(203765, 'itIT', '', 'La Lista degli Ospiti di Sire Garridan dovrebbe includere le informazioni basilari su dove trovare ogni invitato.\n\nDopo aver parlato con Sire Garridan, riceverai una copia della Lista degli Ospiti, così potrai ricontrollarla in ogni momento.\n\nInoltre, ti indicheremo sulla mappa dove trovare gli ospiti già scoperti che non hanno ancora inviato il RSVP.\n\nPer ottenere il RSVP di un ospite, vai alla posizione indicata sulla mappa e completa qualsiasi compito che ti richiedono di svolgere.\n\nSe non hai ancora incontrato un potenziale ospite, non potrai ottenere il loro RSVP finché non lo scoprirai, ma solitamente basta avventurarsi nel suo regno.\n\nLa Lista degli Ospiti di Sire Garridan dovrebbe avere ogni dettaglio che ti serve per ottenere il loro RSVP.', 38134),
(203764, 'itIT', '', 'Sarò felice di fornirti ulteriori informazioni, se necessario. Basta chiedere.', 38134),
(203763, 'itIT', '', 'Secondo i miei registri, di solito si trova vicino alle cripte nelle Sale della Redenzione.', 38134),
(203762, 'itIT', 'Ti andrebbe di andare a fargli visita e risolvere qualunque problema stia ostacolando la sua presenza?', '', 38134),
(203761, 'itIT', 'Non abbiamo ancora ricevuto l\'RSVP del Custode delle Cripte Kassir.', '', 38134),
(203758, 'itIT', '', 'Ben fatto! Puoi avere l\'onore di mettere il coprispalle al posto che gli spetta di diritto.', 38134),
(203753, 'itIT', 'Aspetta, potrebbe esserci qualcosa. Che dici, Calcafauce?', '', 38134),
(203752, 'itIT', 'Gharmal aveva una sete insaziabile di potere. Avrebbe dovuto usarla per difendere Maldraxxus invece che per i suoi interessi personali.', '', 38134),
(203751, 'itIT', 'Mmm, non c\'è niente di valore qui.', '', 38134),
(203750, 'itIT', 'Vieni, Calcafauce. Cerchiamo i depositi del margravio caduto.', '', 38134),
(203749, 'itIT', '', 'D\'accordo. Io e te, Calcafauce, ci prepareremo per andare là. Nel frattempo, vedi se Gharmal possedeva qualcos\'altro che potrebbe servirci.', 38134),
(203748, 'itIT', 'Sappiamo che Gharmal cospirava con Sin\'dane. Se hanno il sostegno di Revendreth, è un brutto segno per la nostra causa. Dobbiamo indagare.', '', 38134),
(203747, 'itIT', '', 'Non sembra che sia stato saccheggiato come l\'Animum rubato da Maldraxxus. Questo è stato donato, come se fossero alleati.', 38134),
(203746, 'itIT', 'Animum... ma non di Maldraxxus. Sento puzza di stregoneria Venthyr.', '', 38134),
(203745, 'itIT', '', 'Credevo fosse il caso di cercare le prove del tradimento di Gharmal nella torre. Non mi aspettavo di trovare questo.', 38134),
(203744, 'itIT', 'Fai strada, Baronessa.', '', 38134),
(203743, 'itIT', '', 'Mograine! Calcafauce! Ho trovato qualcosa di preoccupante nella roccaforte di Gharmal. Dovete vederlo.', 38134),
(203741, 'itIT', '', 'Con così tanti nomi eliminati dalla mia lista, credo di poter dedicare un po\' di tempo per difendere la tua corte.', 38134),
(203740, 'itIT', 'Affrontaci, Draka! O ci implorerai di unirti alla nostra casata, come hai fatto con i Prescelti?', 'Affrontaci, Draka! O ci implorerai di unirti alla nostra casata, come hai fatto con i Prescelti?', 38134),
(203739, 'itIT', 'Krexus era uno stolto! Ha pagato il prezzo per averci disubbidito, e lo farai anche tu!', 'Krexus era uno stolto! Ha pagato il prezzo per averci disubbidito, e lo farai anche tu!', 38134),
(203738, 'itIT', 'Chi mi porterà la pelle dell\'Eletta diventerà barone!', 'Chi mi porterà la pelle dell\'Eletta diventerà barone!', 38134),
(203736, 'itIT', 'Avremmo dovuto saccheggiare il Bastione secoli fa! Il Primus ci ha frenato!', 'Avremmo dovuto saccheggiare il Bastione secoli fa! Il Primus ci ha frenato!', 38134),
(203734, 'itIT', '', 'Discuterò la questione con Adrestes. Presta servizio, Calcafauce.', 38134),
(203733, 'itIT', '', 'Dovremmo cercare altri potenziamenti dalle congreghe degli altri regni.', 38134),
(203732, 'itIT', '', 'Sento già crescere il potere dell\'emblema! I nuovi Ascesi saranno fondamentali contro Lysonia e Uther.', 38134),
(203731, 'itIT', '', 'L\'onore è tuo, Calcafauce, come ringraziamento per il ruolo che hai svolto nella vittoria contro Gharmal.', 38134),
(203730, 'itIT', '', 'Non dovrebbe sorprendere che Mikanikos abbia già preparato l\'emblema per ricevere certe doti.', 38134),
(203729, 'itIT', '', 'Oh, ha funzionato? E il Calcafauce è ancora in vita, fantastico! Mmm... Meglio non lasciare la Fauce in quel modo, potrebbe ucciderti.', 38134),
(203728, 'itIT', 'Attaccala adesso!', '', 38134),
(203727, 'itIT', 'Calcafauce! Indebolirò la bestia al tuo comando!', '', 38134),
(203710, 'itIT', '', 'Incontriamoci al Forte Elisio. Piazzeremo insieme il cuore.', 38134),
(203709, 'itIT', '', 'Io... ti ringrazio. Farò il mio dovere.', 38134),
(203708, 'itIT', 'È un compito ingrato, ma porta con sé il dono di plasmare la carne. Il suo potere migliorerà il nuovo Emblema dell\'Ascensione.', '', 38134),
(203707, 'itIT', 'Xandria, ti supplico di strappare il cuore di Gharmal.', '', 38134),
(203706, 'itIT', '', 'Addio. Ti prego, non dire a nessuno che mi hai vista. E se dovrai lanciarti ancora in questo vortice di oscurità, spero che porterai qualcosa d\'interessante da scambiare.', 38134),
(203705, 'itIT', '', 'Credo che questo concluda il nostro accordo. La Pietra del Peccato è poco più avanti.', 38134),
(203704, 'itIT', 'Non confondere la pazzia col coraggio, Kyrian. Ti trovi nel nostro dominio!', 'Non confondere la pazzia col coraggio, Kyrian. Ti trovi nel nostro dominio!', 38134),
(203702, 'itIT', '', 'Fai pure lo spaccone, è tempo di pagare per i tuoi crimini.', 38134),
(203699, 'itIT', 'Questo è tutto ciò che hai messo insieme? Speravo in un pasto decente!', 'Questo è tutto ciò che hai messo insieme? Speravo in un pasto decente!', 38134),
(203697, 'itIT', 'Facciamola finita.', '', 38134),
(203696, 'itIT', '', 'Sono pronta sin da quando hanno saccheggiato il mio tempio.', 38134),
(203695, 'itIT', 'Xandria! Sei pronta alla vera battaglia?', '', 38134),
(203694, 'itIT', 'Ascoltatemi, Prescelti! Ascoltatemi, Kyrian! È giunta l\'ora del nostro assalto finale!', '', 38134),
(203693, 'itIT', 'Scatena il potere della lama runica ed elimina i mastodonti di Gharmal una volta per tutte!', '', 38134),
(203692, 'itIT', 'Mostriamo a questi demoni il vero potere della Casata dei Prescelti.', '', 38134),
(203691, 'itIT', 'I nostri mastodonti Kyrian sconfiggeranno chiunque oserà opporsi alla Casata dei Costrutti!', 'I nostri mastodonti Kyrian sconfiggeranno chiunque oserà opporsi alla Casata dei Costrutti!', 38134),
(203690, 'itIT', 'I nuovi alleati ci hanno conferito un potere oltre ogni immaginazione. Il nuovo barone di Sin\'dane ha detto il vero!', 'I nuovi alleati ci hanno conferito un potere oltre ogni immaginazione. Il nuovo barone di Sin\'dane ha detto il vero!', 38134),
(203689, 'itIT', 'Il Bastione è stato solo l\'inizio. Presto tutto sarà legato alla nostra carne!', 'Il Bastione è stato solo l\'inizio. Presto tutto sarà legato alla nostra carne!', 38134),
(203688, 'itIT', 'Che spettacolo!', '', 38134),
(203686, 'itIT', 'Uno scontro che resterà negli annali!', '', 38134),
(203680, 'itIT', 'La Prova della Saggezza, per prendere ciò che si è appreso e applicarlo a questa sfida.', '', 38134),
(203674, 'itIT', 'Non è la prima volta che un Aspirante sfida la Via e di certo non sarà l\'ultima. Ricorda che siamo tutti qui per sostenerti.', '', 38134),
(203671, 'itIT', '', 'Seguimi a Torgast. Dobbiamo sbrigarci.', 38134),
(203670, 'itIT', 'Sei più che capace, Kleia. Che chi è venuto prima di te possa guidarti in questa prova.', '', 38134),
(203667, 'itIT', 'Echthra è una spia dai molti talenti. Non sottovalutare la sua padronanza dei veleni.', '', 38134),
(203666, 'itIT', 'Raccogli il tuo coraggio per affrontare questa sfida ancora una volta.', '', 38134),
(203664, 'itIT', 'Abbiamo tutti delle lezioni da imparare lungo la Via.', '', 38134),
(203660, 'itIT', 'Con ogni prova impariamo più cose su noi stessi e sulle usanze del nostro popolo. La Via dell\'Ascensione è pronta!', '', 38134),
(203659, 'itIT', 'I nostri sfidanti troveranno la gloria nella vittoria o insegnamenti dalla sconfitta? Scopriamolo.', '', 38134),
(203657, 'itIT', 'Che raduno notevole. Così tanti campioni coraggiosi, è un onore per me.', '', 38134),
(203655, 'itIT', 'Seleziona una destinazione.', 'Seleziona una destinazione.', 38134),
(203654, 'itIT', 'La Baronessa Draka ha iniziato l\'assalto. Dai l\'ordine e ti porterò in battaglia.', '', 38134),
(203653, 'itIT', 'Bel combattimento, Calcafauce. Ora tocca a Kel\'Thuzad.', '', 38134),
(203652, 'itIT', 'Il Lich si è protetto! Le armi non sfonderanno quella barriera!', '', 38134),
(203649, 'itIT', 'Quell\'esplosione l\'ha totalmente annientata!', '', 38134),
(203648, 'itIT', '', 'Io... ne voglio... di più!', 38134),
(203647, 'itIT', '', 'No! Avrò ciò che mi era stato promesso! Me lo sono guadagnato!', 38134),
(203646, 'itIT', '', 'Finalmente! Tutto il potere che merito!', 38134),
(203645, 'itIT', '', 'Non sarò sconfitta da pezzenti come voi!', 38134),
(203640, 'itIT', 'Dobbiamo imparare a collaborare, se il Bastione vuole superare questa crisi. Fino ad allora, ti siamo grati per il tuo aiuto.', '', 38134),
(203639, 'itIT', 'L\'influenza degli Indegni ha messo in discussione la credenza comune al mio tempio. Parlerò con i miei Accoliti, ma prima dobbiamo riportare l\'ordine.', '', 38134),
(203638, 'itIT', 'Ti stai ancora divertendo con gli intrusi, Ninadar? Uccidili!', '', 38134),
(203637, 'itIT', '', 'Sì, padrone. Esisto per servire.', 38134),
(203635, 'itIT', '', 'Sono felice di vederti. Ci sono novità che vorrei condividere con te.', 38134),
(203633, 'itIT', '', 'Confido che tu riesca a fare a pezzi quei Lich, Calcafauce!', 38134),
(203632, 'itIT', 'Vabbuo\', vabbuo\'. Ma se avrai mai bisogno di chesto vecchio loa, basta che vieni nell\'Aldilà.', '', 38134),
(203631, 'itIT', '', 'Vattene.', 38134),
(203630, 'itIT', 'Ma ceeeeeerto maestà, il vecchio Bwonsamdi è felice di darti n\'anima che ha salvato rischiando il collo. E in cambio, tutto chello che chiede è...', '', 38134),
(203626, 'itIT', '', 'La necropoli vibra di potere. È ora di partire.', 38134),
(203625, 'itIT', 'Quando verrà il momento, troveremo Anduin... e lo riporteremo a casa.', '', 38134),
(203624, 'itIT', 'Quindi dobbiamo essere pazienti. Calcafauce, tu continua con i tuoi sforzi per minare la causa del Carceriere.', '', 38134),
(203623, 'itIT', 'La malevolenza del Carceriere permea tutto, in quella torre. Se dovesse capire che la sua preda è in pericolo, potrebbe decidere di... liberarsene.', '', 38134),
(203622, 'itIT', 'Non lo so. Ma dobbiamo essere cauti. Prudenti.', '', 38134),
(203621, 'itIT', '', 'Sylvanas stava giocando con lui. Ha parlato di rompere tutto. Non intendeva che... vero?', 38134),
(203620, 'itIT', '', 'Almeno è vivo ed è apparentemente illeso. Qualunque cosa voglia la Banshee, lui non cederà.', 38134),
(203619, 'itIT', 'Questo è... tutto ciò che posso vedere.', '', 38134),
(203618, 'itIT', '', 'Proteggi il Cuore della Foresta, Figlia dei Sogni. Non lasciare che alcun nemico invada le nostre sacre sale.', 38134),
(203616, 'itIT', '', 'Hai dato la tua stessa essenza per preservare il mio spirito. Difenderò Selvarden fino all\'ultimo.', 38134),
(203612, 'itIT', '', 'Dobbiamo librarci in cielo! Presto!', 38134),
(203611, 'itIT', 'Nessun Drust deve sopravvivere!', '', 38134),
(203610, 'itIT', 'Dev\'essere Anduin.', '', 38134),
(203609, 'itIT', 'I miei aguzzini parlavano di un prigioniero inestimabile tenuto nelle profondità della torre del Carceriere.', '', 38134),
(203608, 'itIT', 'Ti prego di osservare più a fondo. Per trovare il re che un tempo proteggevi.', '', 38134),
(203607, 'itIT', '', 'Sono in debito con te. Anche se in passato ci siamo trovati su fronti opposti, ora dobbiamo affrontare un nemico comune.', 38134),
(203606, 'itIT', '', 'Bene, ci siamo tutti. Dobbiamo muoverci ora, prima che il nemico prepari delle difese adeguate.', 38134),
(203605, 'itIT', '', 'Noi assalteremo il fianco orientale e loro quello occidentale. Quindi, convergeremo su Gharmal come una sola armata.', 38134),
(203604, 'itIT', '', 'I compagni di Mograine si sono dimostrati dei degni alleati e conoscono bene il nostro nemico.', 38134),
(203603, 'itIT', '', 'Staremo al tuo fianco fino alla fine, mia Eletta.', 38134),
(203602, 'itIT', 'Lo vedremo presto.', '', 38134),
(203600, 'itIT', '', 'Terrò fede al nostro accordo. Gharmal cadrà grazie alle nostre forze unite!', 38134),
(203590, 'itIT', '', 'Sembra che anche Dambala sia qui. Combattere contro un Loa non sarà facile, ma ho un asso nella manica!', 38134),
(203565, 'itIT', '', 'Temo che siano stati portati a Torgast, la torre che s\'intravede in lontananza. Il Carceriere tiene i suoi averi più preziosi al suo interno.', 38134),
(203564, 'itIT', 'Allora mostraci un modo per riuscire a entrare nella torre!', '', 38134),
(203563, 'itIT', '', 'Ah, parli dei mortali rimasti indietro quando questa creatura è fuggita.', 38134),
(203562, 'itIT', 'Non metterci fretta, Alienatrice. Ci sono altri che devono essere salvati insieme al barone dei Maldraxxi.', '', 38134),
(203554, 'itIT', 'Devi solo avere i vestiti giusti... E se stai cercando colori diversi, puoi trovarli vicino alle pozze, ma anche quegli Alienatori al Mercato Notturno potrebbero averne.', '', 38134),
(203553, 'itIT', 'Mi hai preso dei vestiti nuovi...', '', 38134),
(203552, 'itIT', 'Cos\'è che non va con i miei capelli? O hai trovato un nuovo manifesto che devo imparare?', '', 38134),
(203548, 'itIT', 'Sì? Come posso essere d\'aiuto? O hai trovato tu qualcosa per me?', '', 38134),
(203547, 'itIT', '', 'Spero che a tutti piacciano i funghi con un po\' di vita. Eheheh', 38134),
(203540, 'itIT', '', 'Vedo un Calcafauce al tuo fianco. Ho fiducia nella sua scelta di allearsi con te, e su questo si basa anche la nostra di alleanza.', 38134),
(203538, 'itIT', 'Ma quando hai finito di aiutare la Regina dell\'Inverno, forse verrai n\'attimo nell\'Aldilà. Sai, giusto per far visita a n\'amico, no?', '', 38134),
(203537, 'itIT', 'Mueh\'zala è forte e molto fetente, ma nun preoccuparti per il vecchio Bwonsamdi. Chesto vecchio loa tiene ancora n\'asso nella manica!', '', 38134),
(203536, 'itIT', 'Pare che dovrò regolare il conto con il capo... na volta per tutte.', '', 38134),
(203527, 'itIT', '', 'Presto! Lancia l\'incantesimo!', 38134),
(203513, 'itIT', '', 'Che vuoi? Non vedi che sto leggendo?', 38134),
(203512, 'itIT', '', 'Il regno del Margravio Gharmal finisce oggi! Raduneremo le nostre forze e prepareremo Zerekriss per un assalto.', 38134),
(203500, 'itIT', '', 'Dicchiupo ti può portare alla necropoli, se necessario.', 38134),
(203481, 'itIT', '', 'La Luce dentro Anduin è forte. Sono certa che il suo cuore lo salverà.', 38134),
(203480, 'itIT', '', 'È un sollievo riaverti indietro, Thrall. Farò informare subito Aggra e il Reggente.', 38134),
(203478, 'itIT', 'Onorevole Voce, procedi pure, mentre io parlo con gli altri. Dobbiamo considerare le implicazioni di ciò che abbiamo visto.', '', 38134),
(203477, 'itIT', '', 'Gran Signore, abbiamo qui un avvertimento urgente dal Primus di Maldraxxus che dev\'essere consegnato agli altri regni.', 38134),
(203476, 'itIT', 'Non ancora, Calia. C\'è un prezzo da pagare per queste visioni. Non oso tentare di nuovo così presto.', '', 38134),
(203475, 'itIT', '', 'Non ho sentito Anduin, né Baine Zoccolo Sanguinario. Gran Signore, sei sicuro di non poter riprovare?', 38134),
(203474, 'itIT', '', 'Sono riuscito a distinguere la voce dell\'Ammiraglio Supremo, e anche quella di Thrall. Le loro grida... mi hanno raggelato di terrore!', 38134),
(203473, 'itIT', 'Questo è... tutto ciò che posso vedere. Sarebbe... troppo pericoloso spingersi oltre.', '', 38134),
(203467, 'itIT', '', 'Funghi pregiati! Prendete i vostri funghi pregiati qui.', 38134),
(203450, 'itIT', '', 'Tutto cresce per sopravvivere: alberi, foreste e persone. Tutto richiede cibo, ma non tutto richiede lo stesso cibo.$b$BCondividere con me è un atto di gentilezza, se lo farai io condividerò il mio dono con te.', 38134),
(203448, 'itIT', 'Rifornimenti dai laboratori da distribuire? Oh, questo è davvero il mio giorno fortunato!$b$bCon tutta questa fortuna, forse potrei anche condividerne un po\'. Sì, sì. Dammi i rifornimenti e di sicuro un po\' della mia fortuna ti seguirà!', '', 38134),
(203447, 'itIT', '', 'Non dovresti attardarti qui. Al Carceriere non piace che gli si tolgano i giocattoli... neanche quelli che ha gettato via. Su. Torna a Oribos!', 38134),
(203443, 'itIT', '', 'Colpiamo uniti. Il piano della mia baronessa avrà successo.', 38134),
(203442, 'itIT', '', 'Finalmente mostri il tuo miserabile volto!', 38134),
(203437, 'itIT', 'Temo stia per emergere qualcosa a Torgast. Qualcosa di terribile.', '', 38134),
(203436, 'itIT', 'Vieni subiti a Oribos, Calcafauce. Percepisco un\'oscurità... che mi chiama.', '', 38134),
(203435, 'itIT', 'Calcafauce, continua i tuoi sforzi. Esplora le zone oscure di Torgast, combatti per le Terretetre. E quando verrà il momento, rivendicheremo la vittoria!', '', 38134),
(203434, 'itIT', '', 'Lavoreremo con i nostri alleati a Oribos per radunare le Congreghe. Solo con il loro aiuto avremo una possibilità di fermare il Carceriere... e salvare Anduin.', 38134),
(203433, 'itIT', 'Hai visto gli eserciti del Carceriere... Non siamo pronti a lanciare un assalto su vasta scala. Se seguissimo i nostri cuori, precipitandoci lì dentro, potremmo perdere tutto.', '', 38134),
(203432, 'itIT', 'Dovremmo radunare le nostre forze e attaccare Torgast! Dobbiamo liberare Anduin!', '', 38134),
(203431, 'itIT', 'Vi ringrazio tutti, ma Jaina ha ragione. Il Carceriere sa cosa abbiamo visto. Userà questa conoscenza contro di noi.', '', 38134),
(203430, 'itIT', '', 'Anche se siamo riusciti a fermare questo attacco, temo che il pericolo sia tutt\'altro che finito.', 38134),
(203429, 'itIT', 'Ben fatto, Jaina. L\'avevamo quasi perso.', '', 38134),
(203428, 'itIT', '', 'Padre!', 38134),
(203427, 'itIT', '', 'Resisti, Bolvar! Solo un altro po\'!', 38134),
(203426, 'itIT', 'Reclamerò ciò che mi appartiene! La morte viene a prendervi tutti!', '', 38134),
(203425, 'itIT', 'Combatti, Gran Signore! Siamo con te!', '', 38134),
(203424, 'itIT', '', 'Padre, resisti! Non permettergli di vincere!', 38134),
(203423, 'itIT', 'RAAAaaaarrGGHHHH!', '', 38134),
(203422, 'itIT', 'Un fallimento. Come quelli che sono venuti prima di te!', '', 38134),
(203421, 'itIT', 'Avresti dovuto annunciare la mia venuta. Invece, mi hai sfidato!', '', 38134),
(203420, 'itIT', '', 'Una... lama greve? Oh no...', 38134),
(203419, 'itIT', '', 'Il Carceriere sta squarciando la mente di Bolvar! Dobbiamo interrompere la loro connessione!', 38134),
(203418, 'itIT', 'ARGH! Io... non ti... servirò!', '', 38134),
(203417, 'itIT', 'La voce del Carceriere! Com\'è possibile?', '', 38134),
(203416, 'itIT', 'Io... ti... vedo!', '', 38134),
(203415, 'itIT', '', 'Bolvar! Cosa c\'è?', 38134),
(203414, 'itIT', 'Il collegamento è stato interrotto. Questo è tutto quello che... aspettate... c\'è qualcosa!', '', 38134),
(203412, 'itIT', 'C\'è sempre un prezzo da pagare per comunicare con quell\'oscurità. Ma devo sapere. Tutti noi dobbiamo sapere!', '', 38134),
(203411, 'itIT', '', 'Temo anch\'io per Anduin. Ma è sicuro per il Gran Signore scrutare là dentro? Il prezzo da pagare è...', 38134),
(203410, 'itIT', '', 'Anduin è ancora prigioniero laggiù! Gli è accaduto qualcosa, padre?', 38134),
(203408, 'itIT', 'Che c\'è, Gran Signore? Cosa percepisci?', '', 38134),
(203407, 'itIT', 'No! Non di nuovo!', '', 38134),
(203406, 'itIT', 'Malevolenza... crescente... Dominazione... nascente...', '', 38134),
(203403, 'itIT', 'Darion! Dopo tutto questo tempo...', '', 38134),
(203402, 'itIT', 'Conosco quella voce! Non può essere? Il barone è... mio padre?!?', '', 38134),
(203401, 'itIT', '', 'Posso nasconderci abbastanza a lungo da trovare il vostro barone. Seguimi!', 38134),
(203397, 'itIT', 'Attenzione. Gli occhi del Carceriere sono ovunque.', '', 38134),
(203396, 'itIT', 'Fai attenzione. Trova Darion, ti aiuterà nella tua ricerca del barone scomparso.', '', 38134),
(203395, 'itIT', '', 'Ti avviso, mortale. Potrai aver eluso la Fauce una volta, ma potresti non riuscire a farlo di nuovo.', 38134),
(203394, 'itIT', 'L\'Onorevole Voce ha parlato. Ti guiderò all\'Anello del Trasferimento, mortale.', '', 38134),
(203363, 'itIT', '', 'Non possiamo permettere che reclami anche Anduin.', 38134),
(203362, 'itIT', '', 'Arthas e Bolvar non sono stati i soli a essere toccati da quell\'influenza oscura. La lama ha reclamato molte vite. Molte anime.', 38134),
(203361, 'itIT', '', 'Le rune di Gelidanima sono un tipo di magia che non si era mai vista su Azeroth, prima dell\'avvento del Re dei Lich. Un potere di cui è infuso il tessuto stesso della Fauce... e di Torgast.', 38134),
(203360, 'itIT', '', 'Anche il destino di mio padre. L\'elmo... quella lama... sono sempre stati strumenti del Carceriere?', 38134),
(203359, 'itIT', '', 'Lo spettro di mio fratello è sospeso su tutti noi come un sudario. Le sue scelte sono state solo sue, per quanto sembri che il suo destino sia stato in parte plasmato dalla mano del Carceriere.', 38134),
(203358, 'itIT', '', 'Grazie, Taelia. La Fauce è... senza speranza. Ho sperimentato un vuoto simile solo una volta... quando ho affrontato Arthas alla Corona di Ghiaccio.', 38134),
(203357, 'itIT', '', 'Sono sollevata che siate al sicuro, Ammiraglio Supremo. Non riesco a immaginare gli orrori che voi e tutti gli altri avete dovuto affrontare.', 38134),
(203353, 'itIT', 'Riparatevi dietro il ghiaccio!', '', 38134),
(203352, 'itIT', '', 'Al riparo dietro i blocchi di ghiaccio!', 38134),
(203347, 'itIT', '', 'Torna a Oribos. Informali che Maldraxxus non colpirà nuovamente gli altri regni. La nostra è una guerra intestina.', 38134),
(203346, 'itIT', '', 'Gli Eterni... il Carceriere. Questo è un messaggio tremendo.', 38134),
(203333, 'itIT', 'Cinque tratti, cinque rune della lama... Le stesse rune intagliate nel Seggio del Primus.', '', 38134),
(203332, 'itIT', 'La brama incessante di vittoria... anche di fronte a una sconfitta inevitabile.', '', 38134),
(203331, 'itIT', 'Il discernimento per capire i loro piani e l\'astuzia per contrastarli.', '', 38134),
(203330, 'itIT', 'L\'ambizione per elevarsi al di sopra dei nemici e la forza per schiacciarli.', '', 38134),
(203322, 'itIT', '', 'Impareremo molto l\'$guno:una; dall\'altra.', 38134),
(203318, 'itIT', 'Se hai difficoltà a trovare un materiale, consulta Dattilia. Ogni progetto elenca i propri materiali e le liste dei materiali contengono informazioni che ti aiuteranno a scoprire dove recuperarli.\n\nRicorda che questi materiali sono sparsi per tutti i regni delle Terretetre. Dovrai lasciare il Bastione per trovarli tutti!', '', 38134),
(203316, 'itIT', '', 'Consideralo fatto.', 38134),
(203315, 'itIT', '', 'Sono già là.', 38134),
(203314, 'itIT', '', 'Trovato qualche affare losco?', 38134),
(203313, 'itIT', '', 'Palla di Fuoco!', 38134),
(203312, 'itIT', '', 'Necrodardo!', 38134),
(203311, 'itIT', '', 'Ah... Zap!', 38134),
(203310, 'itIT', '', 'Puoi sempre contare su di me.', 38134),
(203308, 'itIT', '', 'Facciamo un po\' di magia!', 38134),
(203298, 'itIT', 'Dobbiamo davvero smettere di incontrarci in questo modo.', '', 38134),
(203293, 'itIT', 'Tutti a bordo!', '', 38134),
(203292, 'itIT', 'Devo documentare immediatamente le nostre avventure!', '', 38134),
(203289, 'itIT', 'Splendido, un altro capitolo per il mio libro!', '', 38134),
(203285, 'itIT', 'Dai un\'occhiata qua!', '', 38134),
(203284, 'itIT', 'Ho lasciato qualcosa per te.', '', 38134),
(203282, 'itIT', 'Ho un buon odore?', '', 38134),
(203279, 'itIT', '', 'È qui da qualche parte?', 38134),
(203277, 'itIT', '', 'Cercherò qualche guaio altrove!', 38134),
(203271, 'itIT', 'L\'esistenza non è altro che la percezione dell\'universo.', 'L\'esistenza non è altro che la percezione dell\'universo.', 38134),
(203270, 'itIT', 'Potrebbe essere un\'esperienza che ti cambia l\'anima.', 'Potrebbe essere un\'esperienza che ti cambia l\'anima.', 38134),
(203269, 'itIT', 'Che la tua anima stia al sicuro.', 'Che la tua anima stia al sicuro.', 38134),
(203268, 'itIT', 'Non vedo l\'ora che inizino le nostre avventure insieme.', 'Non vedo l\'ora che inizino le nostre avventure insieme.', 38134),
(203267, 'itIT', '', 'Andiamo a cacciarci in qualche guaio!', 38134),
(203266, 'itIT', 'Il Primus incarna i cinque tratti del soldato Maldraxxi ideale.', '', 38134),
(203265, 'itIT', '', 'Troviamoci alla gabbia di Ansid quando avrai la chiave. Fa\' in fretta.', 38134),
(203263, 'itIT', 'Stiamo chiudendo!', '', 38134),
(203262, 'itIT', 'Siamo aperti!', '', 38134),
(203260, 'itIT', '', 'Vediamo di sistemarti!', 38134),
(203259, 'itIT', '', 'Lo migliorerò!', 38134),
(203258, 'itIT', '', 'Sei forte... Te la caverai.', 38134),
(203257, 'itIT', '', 'Ci siamo fatti la bua?', 38134),
(203255, 'itIT', '', 'Calcafauce! Ansid è stato catturato. Dobbiamo liberarlo, prima di procedere.', 38134),
(203253, 'itIT', 'Il Tridente dell\'Eco Profonda, buono per gli incantesimi cattivi!\n\nI nemici lanciano incantesimi che fanno male, rendendoti le cose difficili?\n\nNon più! Lancia la lancia, interrompi gli incantesimi! Anche un po\' di danni fisici, sì.', '', 38134),
(203252, 'itIT', 'No che non lo sei! Cerchi di farci finire nella melma?', '', 38134),
(203251, 'itIT', 'Sì, mostrerò loro tutte le mie mosse di ballo!', '', 38134),
(203250, 'itIT', 'Io e Mazzafaccia saremo veri e propri nobili di corte.', '', 38134),
(203249, 'itIT', 'Dentro di te hai il coraggio, la forza e l\'astuzia per vincere i tuoi rivali.', '', 38134),
(203248, 'itIT', '', 'Sarebbe un onore combattere al tuo fianco.', 38134),
(203247, 'itIT', 'Immagino che vedrò quanto è davvero duro il tuo Principe.', '', 38134),
(203246, 'itIT', 'Jaina, c\'è qualcosa che non va. La Pietra di Transito ha smesso di caricarsi!', '', 38134),
(203245, 'itIT', '', 'Il Margravio Sin\'dane ha chiesto il nostro aiuto per occuparci degli alleati di Kel\'Thuzad. Fagliela pagare!', 38134),
(203244, 'itIT', '', 'Ti aspetterò al punto d\'incontro. Speriamo che gli altri siano già là...', 38134),
(203233, 'itIT', 'Sei $garrivato:arrivata; fin quassù, ma cosa puoi comprare tu?\n\nLa mazzetta si schiva, se sei $guno:una; scriba... Non ho nulla da darti, oh, ma non rattristarti.', 'Sei $garrivato:arrivata; fin quassù, ma cosa puoi comprare tu?\n\nLa mazzetta si schiva, se sei $guno:una; scriba... Non ho nulla da darti, oh, ma non rattristarti.', 38134),
(203231, 'itIT', '', 'Le mie sorelle non lo danno a vedere, ma siamo felici e grate di volare ancora.', 38134),
(203229, 'itIT', '', 'Che cosa stai cercando?', 38134),
(203226, 'itIT', '', 'Ci stavano... aspettando. Temo che Khaliiq e Ansid possano essere in pericolo.', 38134),
(203225, 'itIT', '', 'Da questa parte, presto!', 38134),
(203224, 'itIT', '', 'Attenzione!', 38134),
(203223, 'itIT', '', 'Fa\' attenzione... Non ci hanno ancora notati.', 38134),
(203222, 'itIT', 'Non preoccuparti. Troveremo insieme un modo per organizzare al meglio questa corte. Gli Alfieri della Festa!', '', 38134),
(203221, 'itIT', '', 'Non allontanarti, la nostra missione richiede furtività.', 38134),
(203199, 'itIT', 'Una modesta quantità non è mai abbastanza.', '', 38134),
(203198, 'itIT', 'Grazie, Architetto della Piaga, ma credo che questa modesta quantità basterà allo scopo.', '', 38134),
(203197, 'itIT', 'Ho realizzato il campione perfetto. Vediamoci dietro la torre dopo la corte e ti farò vedere.', '', 38134),
(203196, 'itIT', 'Oh, stai cercando poltiglia per scherzi?', '', 38134),
(203193, 'itIT', 'Grazie. Questa sarà un ottimo scherzo per Teotar...', '', 38134),
(203192, 'itIT', 'Il Principe Renathal mette la poltiglia in una tasca della giacca.', '', 38134),
(203184, 'itIT', '', 'Questi Centurioni sono portatori di conoscenza e devono essere preservati a ogni costo. Grazie per i tuoi servigi.', 38134),
(203183, 'itIT', '', 'Il Forgialita Supremo ci ha informati che un\'unità Mnemi non risponde più. Assicurati che abbia tutto ciò di cui ha bisogno per tornare funzionale.', 38134),
(203182, 'itIT', '', 'Reclute, dopo aver duellato con $gil mio amico:la mia amica; potrete ritirarvi. Agite bene e verrete convocati se ce ne sarà bisogno.', 38134),
(203181, 'itIT', '', 'Quei volantini hanno funzionato bene. Abbiamo già reclute in arrivo.', 38134),
(203173, 'itIT', 'Ora sparisci! Ho tollerato fin troppo a lungo la tua presenza.', 'Ora sparisci! Ho tollerato fin troppo a lungo la tua presenza.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203172, 'itIT', 'Fa\' in fretta.', 'Fa\' in fretta.', 38134),
(203152, 'itIT', 'Sarà uno spettacolo da non dimenticare! Spero che oggi qualcuno incontrerà la sua vera morte durante la prova.', 'Sarà uno spettacolo da non dimenticare! Spero che oggi qualcuno incontrerà la sua vera morte durante la prova.', 38134),
(203150, 'itIT', 'Oh, spero di trovarmi contro un membro della Casata Venascura durante la prova. Ho ancora delle questioni in sospeso da risolvere!', 'Oh, spero di trovarmi contro un membro della Casata Venascura durante la prova. Ho ancora delle questioni in sospeso da risolvere!', 38134),
(203149, 'itIT', 'Nessuno mi batterà. La mia abilità con lo stocco è leggendaria.', 'Nessuno mi batterà. La mia abilità con lo stocco è leggendaria.', 38134),
(203148, 'itIT', 'La Casata Asprovino pagherà per le sue offese non appena inizieremo l\'allenamento!', 'La Casata Asprovino pagherà per le sue offese non appena inizieremo l\'allenamento!', 38134),
(203147, 'itIT', '', 'Suggerisco una ritirata strategica. Ci porterò in una piccola alcova vicino alla Pietra di Transito che ti ha permesso di fuggire in precedenza.', 38134),
(203146, 'itIT', 'Cerco la gloria per la mia casata, non per me stesso.', 'Cerco la gloria per la mia casata, non per me stessa.', 38134),
(203145, 'itIT', 'Aspettate. La mia spada... Brandifato. Ne avrò presto bisogno.', '', 38134),
(203143, 'itIT', 'Risparmia le forze, padre. Avremo tempo per parlare più tardi.', '', 38134),
(203142, 'itIT', 'Mettere qualcuno alla prova per un posto nelle truppe d\'élite è un\'opportunità perfetta per pugnalare un idiota della Casata Primulan. Ci sto.', 'Mettere qualcuno alla prova per un posto nelle truppe d\'élite è un\'opportunità perfetta per pugnalare un idiota della Casata Primulan. Ci sto.', 38134),
(203141, 'itIT', 'Figlio mio...', '', 38134),
(203129, 'itIT', '', 'L\'odore dell\'Animum tradisce la tua presenza nel Forte della Perdizione, mortale. Vattene!', 38134),
(203128, 'itIT', '', 'Non hai l\'equipaggiamento adatto per entrare negli Antri delle Bestie. Torna indietro!', 38134),
(203126, 'itIT', 'Porta via quel disastro, prima che rovini tutte le nostre meravigliose decorazioni!', '', 38134),
(203124, 'itIT', '', 'Brandifato, eh? L\'ho fatta per lui quando è stato nominato barone. Voleva qualcosa di simile a quella che aveva usato in vita. In base alla tua reazione, direi che un po\' la ricorda. Immagino che se condividessi il mio titolo con una spada, anch\'io ci sarei affezionata.\n\nÈ una delle mie creazioni migliori. Alexandros ha combattuto duramente per recuperare alcuni dei materiali che ho usato. Non so da dove arrivi il teschio, ma non è certo di Maldraxxus.', 38134),
(203122, 'itIT', '', 'Sono felice di rivederti. Questi momenti sono emozionanti, vero?', 38134),
(203112, 'itIT', '', 'Salve, $C. Offro delle possibilità ai viaggiatori più esperti di aiutare gli altri che incontrano lungo la strada.', 38134),
(203109, 'itIT', '', 'Strano... non sembra la Gorak Zhar che ricordo...', 38134),
(203108, 'itIT', '', 'Sciocchi! Siete solo degli insetti dinanzi a Gorak Zhar!', 38134),
(203107, 'itIT', 'Porto dolcetti golosi per gli ospiti della Corte delle Braci!', '', 38134),
(203106, 'itIT', 'Salve, $C. Offro delle possibilità ai viaggiatori più esperti di aiutare gli altri che incontrano lungo la strada.', 'Salve, $C. Offro delle possibilità ai viaggiatori più esperti di aiutare gli altri che incontrano lungo la strada.', 38134),
(203100, 'itIT', '', 'Tutto è a posto. Fai sapere a Dicchiupo quando vuoi partire.', 38134),
(203098, 'itIT', 'Qualche esplosione dovrebbe bastare!', '', 38134),
(203097, 'itIT', 'Questa è una corte rispettabile, non Ultimo Limo!', '', 38134),
(203096, 'itIT', 'Sì alle lanterne, no alle torce.', '', 38134),
(203095, 'itIT', '', 'Ci infiltreremo nella necropoli mentre Ansid distrae i costrutti.', 38134),
(203094, 'itIT', '', 'Perlustra l\'area dall\'alto mentre Dicchiupo ti porta verso la nostra posizione.', 38134),
(203084, 'itIT', 'I miei ringraziamenti, Calcafauce.', '', 38134),
(203083, 'itIT', '', 'Divertitevi.', 38134),
(203082, 'itIT', '', 'L\'Accusatrice fa un sorrisetto.', 38134),
(203081, 'itIT', 'Puoi contare su di me, Calcafauce! Sono il migliore, in tutto!', '', 38134),
(203080, 'itIT', 'L\'Alfiere della Festa è pronto!', '', 38134),
(203079, 'itIT', 'Temel sembra confuso per un momento, quindi prende rapidamente un cappello da festa dalla cintura e lo indossa.', '', 38134),
(203078, 'itIT', '', 'Qui non sono richiesti i tuoi servizi in qualità di Alfiere del Peccato. Aiuterai $gil:la; Calcafauce a coordinare una nuova corte.', 38134),
(203077, 'itIT', 'Il primo e unico Alfiere del Peccato, al vostro servizio, Accusatrice!', '', 38134),
(203076, 'itIT', '', 'Temel!', 38134),
(203075, 'itIT', '', 'Tuttavia, se il Principe lo ritiene un dovere da compiere...', 38134),
(203074, 'itIT', '', 'Non farò nulla del genere. Le anime inquiete richiedono la mia completa attenzione.', 38134),
(203073, 'itIT', 'Non vedo l\'ora di raccontare tutto a Dama Lerae!', 'Non vedo l\'ora di raccontare tutto a Dama Lerae!', 38134),
(203071, 'itIT', 'Ah, Calcafauce. Stavamo giusto parlando di te.\n\nA cosa dobbiamo l\'onore?', '', 38134),
(203070, 'itIT', 'Oh! Sarà la corte più bella della storia di Revendreth!', 'Oh! Sarà la corte più bella della storia di Revendreth!', 38134),
(203069, 'itIT', 'E, naturalmente, farò in modo che tutti voi siate inclusi nella Lista degli Ospiti.', '', 38134),
(203068, 'itIT', 'La mia estesa rete di contatti e le mie modeste abilità diplomatiche sono al vostro servizio.', '', 38134),
(203067, 'itIT', 'Sarebbe un onore aiutare te e il Principe Renathal.', '', 38134),
(203066, 'itIT', 'Quel denti aguzzi riconosce il talento, quando lo vede. Ci penso io.', '', 38134),
(203065, 'itIT', 'È stato Teotar a dirtelo, vero?', '', 38134),
(203064, 'itIT', 'Mi sembra strano che affidi a un Dragatore un lavoro del genere.', '', 38134),
(203062, 'itIT', 'Hai mai sentito suonare i Caliceamaro?\n\nRoba di un certo livello, che spacca.\n\nHo anche iniziato la loro biografia: finalmente un libro per cui vale la pena saper leggere!', '', 38134),
(203061, 'itIT', 'Calcafauce, parliamo.', '', 38134),
(203060, 'itIT', 'Sarò il nostro critico più severo e avvocato più forte!', '', 38134),
(203059, 'itIT', 'Carni fresche! Prodotti importati! Sono i Camerieri Dragatori più puliti!', '', 38134),
(203058, 'itIT', 'Naturalmente, vengono usati solo gli ingredienti migliori...', '', 38134),
(203056, 'itIT', '<Stefan lo Schizzinoso smette di dettare alla pergamena fluttuante vicina e ti guarda.>\n\nSì? E hai anche un\'opinione sul complesso aroma di questa bistecca?', '', 38134),
(203055, 'itIT', 'Prima di tutto, abbiamo bisogno di più tè. Tutto il tè che c\'è!', '', 38134),
(203054, 'itIT', 'La mia esistenza prima di questo momento non è stata altro che una prova. Ti insegnerò tutto ciò che devi sapere!', '', 38134),
(203053, 'itIT', 'Tuttavia, devo dire che anch\'io sono contento. Anche se dovrò farò affidamento su di te per rinfrescare la mia conoscenza dell\'etichetta delle corti.', '', 38134),
(203052, 'itIT', 'Credo che abbia dato il via tu stesso alla maggior parte di quelle voci, Teotar.', '', 38134),
(203051, 'itIT', 'Mio caro Renathal, le voci dicono il vero. Ospiteremo una corte qui a Oblio del Peccato!', '', 38134),
(203050, 'itIT', 'Non sto più nella pelle dall\'emozione. Questa notizia non può aspettare!', '', 38134),
(203049, 'itIT', '', 'Ai tuoi ordini, faremo rapporto alla Corte delle Braci.', 38134),
(203047, 'itIT', '', 'C\'è ancora molto lavoro da fare.', 38134),
(203038, 'itIT', 'Vieni, ti darò una possibilità di spiegare perché sei $gentrato:entrata; nel mio antro.', 'Vieni, ti darò una possibilità di spiegare perché sei $gentrato:entrata; nel mio antro.', 38134),
(203037, 'itIT', 'Aspettate... Non sparate! $gLo:La; conosco.', 'Aspettate... Non sparate! $gLo:La; conosco.', 38134),
(203036, 'itIT', 'Chi osa entrare nel mio dominio?', 'Chi osa entrare nel mio dominio?', 38134),
(203035, 'itIT', 'Hai oltrepassato il limite!', 'Hai oltrepassato il limite!', 38134),
(203029, 'itIT', '', 'In trappola. Umpf. Bene, vai avanti! Mostraci la tua decantata potenza, mortale.', 38134),
(203028, 'itIT', 'Chi... va là? Devo... avvisare il Margravio...', '', 38134),
(203027, 'itIT', '', 'Da più tempo di te, mortale. Ti basti sapere questo.', 38134),
(203026, 'itIT', 'Da quanto tempo sei intrappolata qui?', '', 38134),
(203022, 'itIT', '', 'Siccità e conflitti indeboliscono il bosco. Se fiorirà, lo faranno anche i suoi spiriti.', 38134),
(203015, 'itIT', 'Lysonia è indebolita, ma non sono ancora certo che io e te riusciremo a contrastare la sua incredibile forza.\n\nCiò nonostante, dobbiamo provare. Sei con me?', '', 38134),
(203014, 'itIT', 'Così tanti caduti... E l\'attenzione dell\'Eletta è concentrata sulla sua Mano.\n\nDobbiamo agire, mortale.', '', 38134),
(203009, 'itIT', 'Conosciamo il rischio. Non abbiamo altra scelta che provarci.', '', 38134),
(203008, 'itIT', '', 'C\'è una sola via per accedere al regno oscuro. La stessa via che devono seguire tutte le anime destinate alla dannazione.', 38134),
(203007, 'itIT', 'Onorevole Voce, vorremmo accedere alla Fauce.', '', 38134),
(203006, 'itIT', 'Non so come localizzare questo barone Maldraxxi scomparso, né i nostri alleati viventi. Ma credo di poterti aiutare a trovare Darion Mograine. È pur sempre un inizio.', '', 38134),
(203005, 'itIT', 'A quanto pare, dovrai sfidare ancora una volta gli orrori della Fauce.', '', 38134),
(202998, 'itIT', 'Ho partecipato a molte corti, durante la mia esistenza.\n\nNessuna di mia volontà.', '', 38134),
(202997, 'itIT', 'Ci sono poche attività piacevoli quanto sorseggiare un buon tè a una corte con degli amici.\n\nSe a un certo punto non sai cosa fare, versa dell\'altro tè. Risolve ogni problema!', '', 38134),
(202996, 'itIT', 'Potrò non divertirmi a frequentare le corti come Teotar, o non aver voglia di organizzarle come Temel.\n\nMa ho molta fiducia nelle mie capacità: conquisto in questo modo i miei alleati fin dagli albori di questo regno.\n\nLa Corte delle Braci sarà uno strumento potente per aumentare la fama e l\'influenza della nostra Congrega.\n\nSono lieto di averti al mio fianco per questo prossimo grande impegno.', '', 38134),
(202991, 'itIT', '', 'Farò rapporto a Nurakkir. Incontriamoci là.', 38134),
(202990, 'itIT', 'Agh! Calcafauce... pensaci tu!', '', 38134),
(202989, 'itIT', 'Stai cercando qualcosa?', '', 38134),
(202988, 'itIT', 'Cosa guardi?', '', 38134),
(202987, 'itIT', 'Cosa ti ha spinto alla porta di Fernandello?', '', 38134),
(202985, 'itIT', '', 'I nostri artigiani sono dei gran lavoratori. Ti prometto uno spettacolo magnifico per la Corte delle Braci!', 38134),
(202984, 'itIT', 'La Corte delle Braci è ufficialmente aperta!', '', 38134),
(202983, 'itIT', 'È tutto così pulito! I campi saranno pronti in men che non si dica!', '', 38134),
(202982, 'itIT', 'C\'è ancora tanto da pulire prima della nostra prima vera corte!', '', 38134),
(202981, 'itIT', 'Be\', non è come le corti a cui sono abituato, ma suppongo che se gli altri sono contenti così...', '', 38134),
(202980, 'itIT', 'Una splendida corte formale! Gubbins e Tubbins devono venire assolutamente alla prossima!', '', 38134),
(202979, 'itIT', 'Il mio dovere è compiuto.', '', 38134),
(202978, 'itIT', 'Hai ottenuto l\'impossibile, Calcafauce: realizzare una corte che mi sia effettivamente goduto. Impressionante.', '', 38134),
(202977, 'itIT', 'Ma certo, Temel. Comincia pure a coordinare gli inviti per i nostri primi ospiti esterni.', '', 38134),
(202976, 'itIT', 'Preparerò tutto per i nostri rituali alla Corte delle Braci.', '', 38134),
(202975, 'itIT', 'Gettafango grugnisce mentre sposta via la carriola.', '', 38134),
(202974, 'itIT', 'Ed ecco perché non ti ho invitato, Gettafango! Muoviti!', '', 38134),
(202973, 'itIT', 'Una situazione caotica mi piace di più!', '', 38134),
(202972, 'itIT', 'Sì, sì, ma cerca di pulire nel frattempo. I Dragatori hanno fatto altri disastri!', '', 38134),
(202971, 'itIT', 'Vi chiamerò tutti per salire sul palco tra pochi minuti per eseguire la cerimonia di chiusura e riscattare i tributi.', '', 38134),
(202970, 'itIT', 'Basta ramanzine, Temel. Lasciamo che sia $gil:la; Calcafauce a godersi l\'esercitazione!', '', 38134),
(202969, 'itIT', 'Quando salteranno fuori conflitti insolvibili, come tra Teotar e il Maestro di Guardia Boromod, dovrai scegliere su quale ospite concentrare i tuoi sforzi.', '', 38134),
(202968, 'itIT', 'Le attività che rendono felice un ospite potrebbero indispettirne un altro.', '', 38134),
(202967, 'itIT', 'Il senso, Calcafauce, è che a tutti piacciono tipi di corti diverse.', '', 38134),
(202966, 'itIT', 'Mio caro Maestro di Guardia, insisto sul fatto che ti sbagli. Ho partecipato alle corti più esclusive di tutta Revendreth e...', '', 38134),
(202965, 'itIT', 'Questo sì che è stato DIVERTENTE! Dovrebbe esserci meno tè e più combattimento in ogni corte!', '', 38134),
(202934, 'itIT', '', 'Sono felice di concederti questo dono. Seguimi, per cortesia.', 38134),
(202933, 'itIT', '', 'E così, estendo il dono della Regina a te. In piedi.', 38134),
(202932, 'itIT', '', '\"Ti offro questo dono. Diventa come le creature selvagge, qualsiasi forma tu voglia, sarà tua.\"', 38134),
(202931, 'itIT', '', '\"Come posso farlo, mia Regina?\" chiese l\'anima. \"Non possiedo più un corpo\".', 38134),
(202930, 'itIT', '', '\"Prenditi cura di questi spiriti per sempre. Il ciclo deve continuare, o tutto è perduto\".', 38134),
(202929, 'itIT', '', 'L\'anima s\'inginocchiò: \"Come posso servirti, mia Regina?\".', 38134),
(202928, 'itIT', '', '\"Allora ti do il benvenuto nel mio regno\", rispose la Regina.', 38134),
(202927, 'itIT', '', '\"Mi hanno mandato da te!\" disse lo spirito, \"Perché in vita, mi dedicai a servire la natura\".', 38134),
(202926, 'itIT', '', 'La nostra Regina la incontrò all\'ingresso. \"Non sei né uno spirito né un Silfo. Perché sei qui nella mia foresta?\"', 38134),
(202925, 'itIT', '', 'Tempo fa, dopo che sorsero gli alberi e i Silfi della Notte, la prima anima mortale apparve a Selvarden.', 38134),
(202923, 'itIT', 'Crea nuovi oggetti con Dattilia che non hai mai creato prima. Forse hai degli altri ricordi da catturare o nuovi equipaggiamenti da provare!\n\nSe non hai mai sconfitto un determinato ricordo o modalità di quel Boss prima d\'ora, impegnati a superare la prova per ottenere una ricompensa. Creare e utilizzare i talismani può fornire ai tuoi Vincoli dell\'Anima quel vantaggio di cui potrebbero aver bisogno.\n\nInfine, impegnati in nuove sfide. Ci sono molti venditori nelle vicinanze con ricompense incredibili che puoi ottenere soltanto superando certe sfide nella Via dell\'Ascensione. Prova a raccoglierle tutte!', '', 38134),
(202921, 'itIT', 'Certi Vincoli dell\'anima hanno un vantaggio contro uno specifico ricordo rispetto a un altro. Un\'opzione potrebbe essere di far combattere un altro Vincolo dell\'Anima.\n\nProva a dargli equipaggiamenti e amuleti. Gli equipaggiamenti garantiranno un\'abilità attiva ai tuoi Vincoli dell\'Anima, mentre gli amuleti dei benefici passivi. Entrambi possono cambiare le sorti di un combattimento. Se vuoi, posso dirti di più a riguardo!\n\nInfine, con l\'aumentare della tua fama con la nostra congrega, i tuoi Vincoli dell\'Anima si rafforzeranno passivamente. Se hai difficoltà con un ricordo, prova a concentrarti sull\'aumentare la tua fama e a rafforzare il tuo Vincolo dell\'Anima prima di riprovare ancora.', '', 38134),
(202919, 'itIT', '', 'Saremo dei potenti alleati, Calcafauce, ma non amici. Io non ho \"amici\".', 38134),
(202914, 'itIT', 'A differenza degli equipaggiamenti, che sono attivi, gli amuleti rafforzano passivamente i tuoi Vincoli dell\'Anima. Possono essere usati una sola volta prima che il loro potere svanisca, quindi creane molti!\n\nAnche se puoi equipaggiare solo un amuleto per tipo, puoi usare tutti i tipi di amuleti che desideri. Sentiti $glibero:libera; di combinarli come vuoi.', '', 38134),
(202912, 'itIT', 'Certe volte potrebbe capitarti una sfida che il tuo Vincolo dell\'Anima non è preparato ad affrontare da solo, ed è qui che l\'equipaggiamento entra in gioco. L\'equipaggiamento può dare ai tuoi Vincoli dell\'Anima un\'abilità aggiuntiva in combattimento, come uno scatto.\n\nQuando creerai un pezzo di equipaggiamento, potrai usarlo per sempre. Parla con Paolone al colosseo per vestire il tuo Vincolo dell\'Anima. Puoi avere solo un equipaggiamento alla volta, quindi assicurati di scegliere il migliore per il tuo Vincolo dell\'Anima.', '', 38134),
(202910, 'itIT', 'La Via dell\'Ascensione contiene glorie e onori, ma bisogna equilibrarli con atti di umiltà. I Medaglioni del Servizio sono una prova di tali atti e vengono usati per affrontare la Via.\n\nQuesti medaglioni si possono ottenere adempiendo ai propri compiti al Bastione. Il modo migliore per accumularli è partecipando alle chiamate, alle cacce al tesoro e occupandosi di nemici rari. Ovviamente, se te ne servono di più, puoi sempre donare materiali a Dattilia per ottenerne altri.', '', 38134),
(202909, 'itIT', 'Non parli molto, $gsignore:signora;. Come posso aiutarti?', 'Non parli molto, $gsignore:signora;. Come posso aiutarti?', 38134),
(202908, 'itIT', 'È la prima volta che mi assegnano a una corte. Farò del mio meglio.', 'È la prima volta che mi assegnano a una corte. Farò del mio meglio.', 38134),
(202906, 'itIT', 'Credi che avanzerà del cibo?', 'Credi che avanzerà del cibo?', 38134),
(202905, 'itIT', 'I nuovi progetti possono essere trovati sconfiggendo i ricordi nel colosseo. Dopo che li porti da Dattilia, potrai creare nuove esche, amuleti ed equipaggiamento. Prova a raccoglierli tutti e sperimenta con tutte le varie scelte.', '', 38134),
(202903, 'itIT', 'Sono pronto a servire.', 'Sono pronta a servire.', 38134),
(202902, 'itIT', 'Il primo passo per sfidare ricordi più difficili è di potenziare lo specchio dell\'anima per poter far emergere ricordi più forti. Puoi farlo potenziando il tuo Santuario della Congrega.\n\nIn secondo luogo, certe prove sono più difficili di altre. Dovrai mostrare il tuo valore contro un numero sufficiente di Boss per poter affrontare una sfida più difficile.\n\nOvviamente, non puoi sfidare un livello di ricordo più difficile se non hai sconfitto la sua versione più debole.', '', 38134),
(202900, 'itIT', 'Quando avrai nuovi ricordi da catturare, Dattilia te lo farà sapere. Per ogni ricordo dovrai creare la sua specifica esca e uno specchio dell\'anima, per quest\'ultimo ti può aiutare lei stessa. Quando darai tutto a Dattilia, lei ti darà ulteriori istruzioni per portare il ricordo al colosseo per addestrare i tuoi Vincoli dell\'Anima.', '', 38134),
(202899, 'itIT', 'Eventuali combinaguai dovranno vedersela con me.', 'Eventuali combinaguai dovranno vedersela con me.', 38134),
(202897, 'itIT', 'Sono ai tuoi ordini.', 'Sono ai tuoi ordini.', 38134),
(202896, 'itIT', 'Come posso esserti d\'aiuto?', 'Come posso esserti d\'aiuto?', 38134),
(202895, 'itIT', 'Per Oblio del Peccato.', 'Per Oblio del Peccato.', 38134),
(202893, 'itIT', '', 'Il resto della fauna si sta radunando. Ci faremo trovare pronti per quando ci inviterai alla Corte delle Braci.', 38134),
(202889, 'itIT', 'Una storia per i posteri!', '', 38134),
(202888, 'itIT', 'Ricorda tutto quello che hai imparato.', '', 38134),
(202881, 'itIT', 'È giunto il momento per un\'altra sfida sulla Via. Chi si fa avanti?', '', 38134),
(202880, 'itIT', 'Un altro Kyrian prende parte in questa sacra tradizione. È tempo di affrontare la Via dell\'Ascensione!', '', 38134),
(202868, 'itIT', 'Non tutti apprezzano ciò che piace a te, Teotar. Dobbiamo adattare la nostra corte a ogni ospite.', '', 38134),
(202867, 'itIT', 'Un paio di combattimenti saranno una bella boccata d\'aria fresca rispetto a queste formalità.', '', 38134),
(202866, 'itIT', 'Se vuoi migliorare il mio umore, vai a duellare con una delle mie nuove reclute.', '', 38134),
(202864, 'itIT', 'Bah! Non ho alcun interesse nelle frivole formalità della corte.', '', 38134),
(202863, 'itIT', 'Non saprei, Teotar... Boromod non sembra andare pazzo per le formalità...', '', 38134),
(202862, 'itIT', 'Comincia dal nostro deprimente Maestro di Guardia. Apprezzerà senza dubbio essere incluso nelle formalità della corte.', '', 38134),
(202861, 'itIT', 'Tutti dovrebbero ricevere una tazza di tè!', '', 38134),
(202858, 'itIT', '', 'Ora capisco perfettamente come Kleia si sia guadagnata le sue ali.', 38134),
(202854, 'itIT', 'Grazie, Calcafauce. È importante mantenere un\'etichetta appropriata durante la corte, non importa dove si trovi.', '', 38134),
(202852, 'itIT', '', 'Ergiti con coraggio di fronte alle avversità.', 38134),
(202851, 'itIT', '', 'Eccoti un consiglio, Pelagos. Non lasciare mai che l\'avversario si avvicini troppo.', 38134),
(202842, 'itIT', '', 'La Caccia Selvaggia è formata da soldati implacabili che affronteranno qualsiasi sfida. Vediamo come te la cavi contro di loro.', 38134),
(202837, 'itIT', '', 'Guardate! Sfidante a rischio eliminazione.', 38134),
(202830, 'itIT', '', 'Tieni duro, ce l\'hai quasi fatta!', 38134),
(202822, 'itIT', '', 'Ogni sconfitta rende più piacevole una tua eventuale vittoria!', 38134),
(202821, 'itIT', '', 'Ah, una lezione di umiltà.', 38134),
(202816, 'itIT', '', 'Che un\'altra prova abbia inizio!', 38134),
(202815, 'itIT', 'Ne preparerò ancora qualora fosse richiesto.', '', 38134),
(202814, 'itIT', 'Bisogna servire il tè finché è caldo.', '', 38134),
(202813, 'itIT', '', 'Che spettacolo meraviglioso! Sapevo dal primo istante in cui ti ho $gincontrato:incontrata; che avresti fatto grandi cose.', 38134),
(202812, 'itIT', '', 'Compagni Aspiranti, ammirate $p, $gcolui:colei; che ha combattuto contro il possente Athanos!', 38134),
(202811, 'itIT', '', 'La lancia è incredibilmente pesante, sarai in grado di sollevarla solo per un breve istante. Il tempismo è fondamentale!', 38134),
(202809, 'itIT', 'Sei $gmigliorato:migliorata;, $n. Il mio ricordo è tuo.', '', 38134),
(202808, 'itIT', '', 'Sembra che tu abbia ancora da imparare.', 38134),
(202807, 'itIT', 'Mio caro Temel, come facevi a sapere che \"più costoso\" è il mio gusto di tè preferito?', '', 38134),
(202806, 'itIT', 'Calcafauce, servi a Teotar una tazza del nostro tè più costoso.', '', 38134),
(202804, 'itIT', 'Cosa ne pensi? Siamo pronti ad aprire la Corte delle Braci al pubblico?', '', 38134),
(202803, 'itIT', 'Ti piace formale? Facciamo formale!', '', 38134),
(202802, 'itIT', 'Ma devo ammettere che chi risiede nei circoli più influenti è abituato maggiormente a... circostanze più formali.', '', 38134),
(202801, 'itIT', 'Be\', è senza dubbio un buon inizio, mio caro Principe.', '', 38134),
(202800, 'itIT', 'Teotar, hai sempre apprezzato l\'allegria di celebrazioni sociali come questa.', '', 38134),
(202799, 'itIT', 'Servo dove richiesto.', '', 38134),
(202798, 'itIT', 'Il Principe Renathal fa l\'occhiolino al Maestro di Guardia Boromod.', '', 38134),
(202797, 'itIT', 'Sembra che i salottieri e i nobili si stiano divertendo, anche se il nostro Maestro di Guardia non sembra condividere.', '', 38134),
(202796, 'itIT', 'Notevole, Temel. Tu e $gil:la; Calcafauce avete già fatto enormi progressi!', '', 38134),
(202795, 'itIT', 'Ecco il Principe Renathal col nostro ospite speciale!', '', 38134),
(202794, 'itIT', 'Eccellente. Ora, lo stesso vale per la Vasca di Enzimi.', '', 38134),
(202793, 'itIT', '', 'Sono creature magnifiche, non è vero? Lo sanno anche loro! Così fieri.', 38134),
(202792, 'itIT', '', 'Tempo fa, attingemmo alla sua magia per garantire che non ci fosse via di fuga dalla Fauce, così l\'Esiliato sarebbe rimasto per sempre in catene.', 38134),
(202790, 'itIT', 'Ho messo al lavoro ogni Dragatore per rendere questo posto perfetto per il Principe Renathal.', '', 38134),
(202789, 'itIT', 'Mi farebbe comodo il tuo aiuto in quest\'esercitazione della Corte delle Braci!', '', 38134),
(202786, 'itIT', 'Mia piccola stella... Farò quello che mi chiedi.', '', 38134),
(202785, 'itIT', '', 'Te lo sto chiedendo ora: dicci se sono ancora vivi.', 38134),
(202784, 'itIT', '', 'Non ti ho mai chiesto nulla, da quando ero una bambina. Una bambina che tu hai cacciato via.', 38134),
(202783, 'itIT', '', 'Ho viaggiato fino ai regni della Morte per incontrare il padre che pensavo di aver perso. Bolvar Domadraghi, uno degli eroi più coraggiosi di Azeroth.', 38134),
(202782, 'itIT', 'Non capisci il rischio, Taelia. Per me scrutare nella Fauce significa...', '', 38134),
(202781, 'itIT', '', 'Ti prego, padre, dobbiamo sapere se i nostri amici possono ancora essere salvati!', 38134),
(202780, 'itIT', 'Parto per la mia nuova vita! Vite? Andiamo con vita per ora, no? Comunque, addio!', '', 38134),
(202779, 'itIT', 'Buona fortuna con la tua arena... O qualsiasi cosa che stavi dicendo.', '', 38134),
(202778, 'itIT', 'Splendido! Per niente quello che ci aspettavamo, ma la scienza è imprevedibile!', '', 38134),
(202775, 'itIT', 'Molto bene. Lo apprezzerà.', '', 38134),
(202774, 'itIT', 'Un gentiluomo di buon gusto? Preparerò una teiera del mio tè migliore.', '', 38134),
(202773, 'itIT', 'Se hai del tè in quel chiosco, Stefan, sono sicuro che il nostro ospite lo apprezzerà.', '', 38134),
(202772, 'itIT', 'Il Principe Renathal sta portando un ospite? Oh cielo, devo preparare un rinfresco speciale.', '', 38134),
(202771, 'itIT', 'Noi puliremo il resto. Il Principe Renathal sarà presto qui con l\'ospite speciale.', '', 38134),
(202770, 'itIT', 'Guardate come strofina! Voi Dragatori potreste imparare un paio di cosette $gdal:dalla; Calcafauce.', '', 38134),
(202769, 'itIT', 'Bel lavoro, Calcafauce! Pulisci il resto prima che arrivino gli ospiti.', '', 38134),
(202768, 'itIT', 'Ha già un aspetto migliore! Mi piacciono le corti pulite.', '', 38134),
(202767, 'itIT', 'Voi due occupatevi di fare la guardia a sud. Io mi occuperò dei civili.', '', 38134),
(202766, 'itIT', '', 'Era ora. Fa\' attenzione a non fidarti di lui, cacciatrice.', 38134),
(202765, 'itIT', 'Ricevuto, capo.', '', 38134),
(202763, 'itIT', 'Questa corte di prova mi piacerebbe di più se la zona dei rinfreschi fosse più pulita.', '', 38134),
(202762, 'itIT', 'Ignora i salottieri. Dovremmo concentrarci sull\'impressionare gli ospiti più importanti.', '', 38134),
(202761, 'itIT', 'Oh, quasi mi dimenticavo di dirlo: ho invitato alcuni membri della congrega per fare un giro di prova.', '', 38134),
(202760, 'itIT', 'Ma è un disastro! Dobbiamo pulire tutto prima di poter tenere una corte vera.', '', 38134),
(202759, 'itIT', 'Eccoti qua, Calcafauce!', '', 38134),
(202758, 'itIT', 'Se sei $gconfuso:confusa; o se ti servisse una guida, io potrei offrirti delle linee guida. Che tu e i tuoi Vincoli dell\'Anima possiate percorrere la Via con fierezza.', '', 38134),
(202757, 'itIT', '', 'Dattilia può aiutarti a ottenere nuovi ricordi contro cui addestrarsi, così come talismani o equipaggiamenti che potrebbero fornire ai tuoi Vincoli dell\'Anima una marcia in più.', 38134),
(202756, 'itIT', '', 'I prossimi passi dipendono da te e dai tuoi Vincoli dell\'Anima, $n. Si può ottenere gloria, ma anche lezioni da imparare.', 38134),
(202755, 'itIT', '', 'Da ora in poi, ogni tentativo sulla Via richiede un Medaglione del Servizio, prova dei doveri compiuti verso il Bastione. La Via è ricca di glorie, ma dobbiamo stare con i piedi per terra. Te ne ho dato alcuni per iniziare.', 38134),
(202752, 'itIT', 'Io terrò l\'equipaggiamento. Parla con me al colosseo se vuoi usarlo!', '', 38134),
(202741, 'itIT', 'Ecco, mortale. Questo teletrasporto ti condurrà all\'Anello del Trasferimento.', '', 38134),
(202735, 'itIT', 'Non c\'è momento migliore di ora, $n. Usa quel tuo specchio e facciamola finita!', '', 38134),
(202734, 'itIT', 'Sono... Siamo... me? Siamo me!', '', 38134),
(202733, 'itIT', 'Cosa... Cosa è successo? Dove sono?', '', 38134),
(202727, 'itIT', 'Candele nel patio! Sedie ai rinfreschi! Lanterne sul palco!', '', 38134),
(202709, 'itIT', '', 'Come desideri.', 38134),
(202706, 'itIT', 'Dovrai prima vedertela con me!', '', 38134),
(202701, 'itIT', '', 'Ora capirete quanto è folle opporsi alla Casata dei Rituali!', 38134),
(202698, 'itIT', 'Molto bene, sei $gun gran lavoratore:una grande lavoratrice;...', '', 38134),
(202697, 'itIT', 'Ora Tubbins fa tè. Ottimo tè! Tutti dicono che sono grandioso!', '', 38134),
(202696, 'itIT', 'Nooooooo!!!', '', 38134),
(202694, 'itIT', 'Oh, non promette bene, te lo concedo.', '', 38134),
(202693, 'itIT', 'Oooh, forse tre spruzzate... Si vive una volta sola!', '', 38134),
(202692, 'itIT', 'E per ultima, ma non meno importante, il catalizzatore che hai portato! Mescola con un frustino e...', '', 38134),
(202691, 'itIT', 'Giusto un tocco di polvere d\'ossa... Una spruzzata di melma...', '', 38134),
(202690, 'itIT', 'Ammira il miracolo di eoni di ricerche alchemiche e stagisti scartati!', '', 38134),
(202688, 'itIT', 'Potrei non raggiungere l\'ascensione in fretta come gli altri, $n, ma sono felice della via che percorro.\n\nNon molti Kyrian parlano del proprio passato, o addirittura neanche lo ricordano! Credo che al riguardo abbiamo molto da imparare come popolo.\n\nQuando l\'ho detto a Kleia, lei ha ascoltato... proprio come te; è stata un\'amica più fedele di quanto potessi sperare... proprio come te.\n\nTi ringrazio. Sono felice di aver potuto condividere la mia storia con te.', '', 38134),
(202686, 'itIT', 'Ma quando sono arrivato al Bastione sono diventato un Aspirante con le sembianze di, be\'...\n\n<Pelagos si indica.>\n\nQuesto! Una forma maschile!\n\nMi sono sentito... bene! Entusiasta! Come se chi fossi combaciasse per la prima volta con quello che gli altri vedevano!\n\nTutte quelle emozioni della mia vita finalmente avevano senso.', '', 38134),
(202684, 'itIT', 'Apprezzo che mi stia ascoltando, davvero. Come Vincoli dell\'Anima, so che posso fidarmi di te.\n\n<Pelagos fa un respiro profondo, poi espira lentamente.>\n\nIn vita avevo una forma femminile. Non mi ricordo il mio nome e neanche la mia razza, ma... non ho mai sentito che la mia forma fisica mi rappresentasse per chi fossi.\n\nNon mi è mai stato più chiaro di come lo è ora. Ho avuto difficoltà con quell\'identità per tutta la mia vita...', '', 38134),
(202682, 'itIT', 'Tranne uno.\n\nÈ dura pensarci e so che dovrei lasciarlo andare, ma... voglio... Forse ho bisogno... di ricordare.\n\nÈ una parte troppo importante di quello che sono per poterlo dimenticare.', '', 38134),
(202680, 'itIT', 'Ricordo ben poco, negli ultimi tempi.\n\nI ricordi svaniscono naturalmente mentre la mia vita passata si allontana sempre più, e molti altri li ho abbandonati spontaneamente per adempiere ai miei doveri.\n\nTranne...', '', 38134),
(202677, 'itIT', 'Mi chiamavano folle! A dir la verità, lo fanno ancora!\n\nTutto questo cambierà da oggi!', '', 38134),
(202667, 'itIT', 'Il duro lavoro rende elastici e fa crescere anche i più forti.', '', 38134),
(202665, 'itIT', 'Piano, giovane.', '', 38134),
(202663, 'itIT', '', 'Lo specchio dietro di te racchiude tutti i ricordi che hai recuperato. Usalo per scegliere il tuo avversario.', 38134),
(202662, 'itIT', 'Devi attraversare gli anelli di funghi per raggiungere le isole. L\'accesso è limitato, ma puoi provare.', 'Devi attraversare gli anelli di funghi per raggiungere le isole. L\'accesso è limitato, ma puoi provare.', 38134),
(202661, 'itIT', 'Quest\'oggi, Pelagos fa il suo primo passo sulla Via dell\'Ascensione. Che possa seguire le orme degli innumerevoli Kyrian che sono venuti prima di lui!', '', 38134),
(202659, 'itIT', '', 'Fammi sapere quando vuoi partire e attiverò il dispositivo. Non ci coprirà per molto.', 38134),
(202658, 'itIT', '', 'Annega nel terrore.', 38134),
(202653, 'itIT', 'Tu e i tuoi Vincoli dell\'Anima seguite le orme di molti altri prima di voi. Non c\'è onore o tradizione più grande del partecipare alla Via dell\'Ascensione.', '', 38134),
(202627, 'itIT', '', 'Sarà fatto.', 38134),
(202626, 'itIT', '', 'Percepisco gli spiriti dei Kaldorei nel tuo Custode delle Anime. Liberale nella natura incontaminata di Selvarden.', 38134),
(202621, 'itIT', '', '<Gli occhi della Regina dell\'Inverno si infiammano di potere trattenuto. Ti osserva con approvazione.>\n\nBel combattimento, mortale.\n\nVieni. Alle ossa ci penseranno i gorm.', 38134),
(202619, 'itIT', '', 'Raaargh! Vile regina! i Drust non saranno mai sconfitti!', 38134),
(202618, 'itIT', '', 'Vieni, mortale. Sbarazziamoci di questa miserabile.', 38134),
(202616, 'itIT', '', 'Non posso permettermi di perdere le sue tracce. Ci incontreremo più avanti.', 38134),
(202615, 'itIT', '', 'Ammira! La magia dei miei boschi si erge contro di te!', 38134),
(202614, 'itIT', '', 'Fa\' attenzione. Una potente forza cerca di consumarla. Prego affinché Shandris riesca a liberarla da questa stretta.', 38134),
(202613, 'itIT', 'Shandris, questa potrebbe essere l\'unica speranza di salvare Tyrande.', '', 38134),
(202602, 'itIT', 'Su tua richiesta presenzieremo alla Corte delle Braci con una curiosa gamma di oggetti mortali. Forse i tuoi ospiti vedranno qualcosa che vogliono comprare.', 'Su tua richiesta presenzieremo alla Corte delle Braci con una curiosa gamma di oggetti mortali. Forse i tuoi ospiti vedranno qualcosa che vogliono comprare.', 38134),
(202600, 'itIT', '', 'Il tuo arrivo è tempestivo, Calcafauce.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202599, 'itIT', '', 'I nemici avanzano. Non possiamo mantenere la posizione a lungo.', 38134),
(202588, 'itIT', 'Sono pronto quando vuoi. Oh, è davvero elettrizzante!', '', 38134),
(202582, 'itIT', '', 'Marileth t\'insegnerà cosa significa vincolare un\'anima a un\'altra.', 38134),
(202581, 'itIT', 'Quel fiume non è posto per i vivi. Meglio evitarlo a tutti i costi.', '', 38134),
(202580, 'itIT', 'Guarda come lottano! Presto la carne di quegli esiliati Prescelti e dei loro cagnolini Kyrian sarà nostra da manipolare.', 'Guarda come lottano! Presto la carne di quegli esiliati Prescelti e dei loro cagnolini Kyrian sarà nostra da manipolare.', 38134),
(202576, 'itIT', '', 'Non sprecare questa opportunità, Calcafauce.', 38134),
(202575, 'itIT', 'È un grande onore brandire il potere del Primus.', '', 38134),
(202567, 'itIT', '', 'Gentile da parte tua unirti a noi.', 38134),
(202565, 'itIT', 'Sylvanas! Cosa vuole da lui?', '', 38134),
(202561, 'itIT', 'Vuoi acquistare qualcosa di speciale?', 'Vuoi acquistare qualcosa di speciale?', 38134),
(202559, 'itIT', 'Questa creatura meccanica sembra aver bisogno di riparazioni.', '', 38134),
(202558, 'itIT', 'Il Marchio... detiene... la chiave...', '', 38134),
(202557, 'itIT', '', 'Me ne occuperò io.', 38134),
(202556, 'itIT', 'I Drust ci sbarrano la strada!', '', 38134),
(202555, 'itIT', '', 'Ho imparato qualche trucchetto per sopravvivere qui. Il Carceriere noterà se attaccherete le sue forze, per questo sarebbe saggio evitarle se possibile.', 38134),
(202554, 'itIT', '', 'La vostra presenza attira l\'attenzione. Attenzione negativa per i miei affari. Senza offesa, naturalmente.', 38134),
(202553, 'itIT', 'Perché dovresti aiutarci, Alienatrice?', '', 38134),
(202552, 'itIT', '', 'Io sono Ve\'nari, un\'umile Alienatrice intrappolata in questa terra di tormenti. Ho una proposta da farti, vieni!', 38134),
(202551, 'itIT', '', 'Ah, mi chiedevo cosa stessero cercando i Giurafauce. Sei l\'essere mortale che ha devastato tutto e guastato la festa al Carceriere.', 38134),
(202549, 'itIT', 'Il diversivo ha funzionato, lo ammetto. Il che renderà ancora più dolce il gusto della loro sconfitta.\n\n<Herne sogghigna ampiamente.>\n\nCominciamo?', '', 38134),
(202547, 'itIT', 'Queste immonde creature assaggeranno la mia lama prima della fine.', '', 38134),
(202536, 'itIT', 'Aggiungi il Catalizzatore al calderone così potremo assistere alla potenza della mia creazione!', '', 38134),
(202535, 'itIT', 'Non mi sorprende che voci sul mio genio siano arrivate sino al Bastione. Questa nuova creazione sarà la più forte, potrebbe insegnare ai tuoi stupidi Kyrian qualche lezioncina sulla brutalità e sul caos!', '', 38134),
(202534, 'itIT', 'Proteggeremo le Terretetre dalle minacce esterne e interne!', '', 38134),
(202529, 'itIT', 'Sì, mio principe. Ferito ma non sconfitto.', '', 38134),
(202528, 'itIT', '', 'Regola numero due: profilo basso. La Fauce non è un posto per scenate. Entra, fa\' ciò che devi e vattene... cercando di conservare gli arti e ciò che possiedi.', 38134),
(202527, 'itIT', 'Amici! Proteggetevi!', '', 38134),
(202525, 'itIT', 'Alleati, state pronti!', '', 38134),
(202524, 'itIT', 'Amico mio, sei ancora vivo?', '', 38134),
(202522, 'itIT', '', 'Ti ringrazio. Resto sempre stupita dai guai in cui riescono a cacciarsi!', 38134),
(202521, 'itIT', '', 'Ai custodi dei gormini serve un po\' d\'aiuto, potresti darglielo tu?', 38134),
(202520, 'itIT', '', 'Non preoccuparti, mortale. Basterà che non ti osservi per un giorno, e il Carceriere guarderà altrove. A quel punto sarà sicuro per te tornare là fuori. Per ottenere Stigia. Per me.', 38134),
(202519, 'itIT', '', 'La buona notizia è che nel mio rifugio sarai al sicuro. Non come là fuori. Ricorda, se andassi incontro a un destino tragico, parte della tua preziosa Stigia ti scivolerà dalle mani.', 38134),
(202518, 'itIT', '', 'Prima ti manderà addosso i Cercatori d\'Anime. Poi le torri. Poi le sue squadre d\'esecuzione. Dopodiché... be\', non vuoi saperlo. Ti serviranno precisione e scrupolosità per farcela.', 38134),
(202517, 'itIT', '', 'Ora che il Carceriere si è accorto di te, non puoi fare nulla. Tutto ciò che farai nella Fauce attirerà la sua attenzione. Esattamente ciò che non ci serve.', 38134),
(202516, 'itIT', '', 'Non posso farti passare.', 38134),
(202515, 'itIT', '', 'Facciamo la guardia al Cuore della Foresta. Solo la Caccia Selvaggia e la Corte dell\'Inverno possono accedere, a meno che siano questioni ufficiali.', 38134),
(202514, 'itIT', '', 'Salve.$b$BSoli i membri della Caccia Selvaggia possono entrare nel Cuore della Foresta.', 38134),
(202513, 'itIT', '', 'La tua presenza è benvenuta nel Cuore della Foresta. Puoi passare.', 38134),
(202512, 'itIT', '', 'Per la Regina e il bosco, stiamo in guardia.', 38134),
(202511, 'itIT', '', 'Eccoti di nuovo. Come va la caccia?', 38134),
(202509, 'itIT', '', 'Il nostro lutto dovrà aspettare, dobbiamo sbrigarci!', 38134),
(202508, 'itIT', '', 'Dama Baccaluna fa l\'occhiolino.', 38134),
(202507, 'itIT', '', 'Sono certa che la Corte delle Braci sarà un evento solenne e incantevole.', 38134),
(202499, 'itIT', '', 'Come posso aiutarti?', 38134),
(202498, 'itIT', '', 'La mia baronessa aveva ragione su di te, Calcafauce. Ci riuniamo a Nurakkir.', 38134),
(202497, 'itIT', 'I Drust sono in una posizione di forza, dobbiamo colpirli con vigore.', '', 38134),
(202493, 'itIT', '', 'I Lich sono astuti e arroganti. Opporranno resistenza.', 38134),
(202492, 'itIT', '', 'Ingra Drif... fallo soffrire, Calcafauce.', 38134),
(202491, 'itIT', 'Mi hai risparmiato da un destino terribile! Sono felice di essere dalla tua parte!', '', 38134),
(202490, 'itIT', 'La succeditrice di Devos, Lysonia, rappresenta una minaccia. Invierò i nostri Latori per scoprire i suoi piani.', '', 38134),
(202489, 'itIT', 'Gloria all\'Arconte. Gloria al Bastione!', '', 38134),
(202488, 'itIT', 'Possiamo nuovamente far ascendere i nostri Aspiranti e rivendicare il regno dagli Indegni!', '', 38134),
(202487, 'itIT', 'Il nostro Emblema riforgiato è più forte che mai!', '', 38134),
(202486, 'itIT', 'Col tuo aiuto, Calcafauce, abbiamo perseverato.', '', 38134),
(202485, 'itIT', 'Quando Devos frantumò l\'Emblema, cercò di spezzare i vincoli tra di noi.', '', 38134),
(202484, 'itIT', 'Avete entrambi svolto un grande servizio per la nostra gente. Sigilliamo il potere dell\'Emblema per salvaguardarlo da assalti futuri.', '', 38134),
(202472, 'itIT', '', 'Sei $gpronto:pronta; a unirti ai ranghi dei Necrosignori?', 38134),
(202469, 'itIT', '', 'Baronessa! Sei tornata!', 38134),
(202460, 'itIT', 'Forse era solo un Dragatore che ne ha gettata un\'altra in cima.', '', 38134),
(202457, 'itIT', 'Quella montagna di Pietre del Peccato si è davvero mossa?', '', 38134),
(202447, 'itIT', '', 'Quanto dovremo perdere ancora?', 38134),
(202422, 'itIT', 'Posso avere un bicchiere di insetti?', '', 38134),
(202421, 'itIT', 'Vorrei la vostra attenzione, per cortesia! Vorrei proporre un brindisi al tesoro del Distretto delle Braci, Testaroccia!', '', 38134),
(202375, 'itIT', 'Questo pipistrello sembra essere la tua via d\'uscita dalle Profondità Sanguigne.', 'Questo pipistrello sembra essere la tua via d\'uscita dalle Profondità Sanguigne.', 38134),
(202373, 'itIT', 'Bolvar crede di sapere dove la Banshee abbia portato gli ostaggi.', '', 38134),
(202368, 'itIT', '', 'Creiamo le Ragnatele dell\'Anima per uno scopo, come tutto quanto a Selvarden. Molte vengono usate per conservare o rilasciare Animum, come quelle qui attorno. Ma questa... questa è diversa.$b$bQuesta viene usata per catturare i sogni.$b$bPurtroppo, possiedo solo la capacità di ripararne la forma, ma non lo scopo. Dovrei trovare qualcuno in grado di potenziare la Ragnatela dell\'Anima con il potere dei sogni.', 38134),
(202367, 'itIT', '', '<Gwenna esamina con cautela i frammenti.>$b$bOh, poverino. Cos\'è successo?$b$BAh, Tirna Falcis. Conosco bene il suo dolore. Sì, sì, possiamo sistemarti in un battito d\'ali!$b$BTi ringrazio per avermelo portato. Troppo spesso buttiamo via cose che possono essere ancora utili. Potrò rimetterlo in sesto se mi porterai dieci pezzi di Seta Oscura.', 38134),
(202366, 'itIT', 'Con il potere degli archivi, potremmo tenere perennemente sotto controllo tutto il Bastione.', 'Con il potere degli archivi, potremmo tenere perennemente sotto controllo tutto il Bastione.', 38134),
(202365, 'itIT', 'Con il potere degli archivi, potremmo tenere perennemente sotto controllo tutto il Bastione.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Osservazione del Saggio]|h|r: quando si ha poca salute, un Asceso la ripristina completamente, respingendo indietro i nemici vicini. Inoltre, riduce i danni subiti. L\'effetto decade rapidamente in pochi istanti. L\'effetto può verificarsi solo una volta ogni pochi min.', 'Con il potere degli archivi, potremmo tenere perennemente sotto controllo tutto il Bastione.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Osservazione del Saggio]|h|r: quando si ha poca salute, un Asceso la ripristina completamente, respingendo indietro i nemici vicini. Inoltre, riduce i danni subiti. L\'effetto decade rapidamente in pochi istanti. L\'effetto può verificarsi solo una volta ogni pochi min.', 38134),
(202364, 'itIT', 'È giunto il momento che questi vespri risuonino una canzone di pace e lucidità.', 'È giunto il momento che questi vespri risuonino una canzone di pace e lucidità.', 38134),
(202363, 'itIT', 'È giunto il momento che questi vespri risuonino una canzone di pace e lucidità.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Vespro della Lucidità]|h|r: quando ci sono numerosi nemici vicini, gli incantesimi e le abilità d\'attacco hanno una probabilità di evocare un Vespro della Lucidità per un breve periodo. Il Vespro della Lucidità stordisce istantaneamente i nemici vicini e aumenta i danni da te inflitti finché è vicino.', 'È giunto il momento che questi vespri risuonino una canzone di pace e lucidità.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Vespro della Lucidità]|h|r: quando ci sono numerosi nemici vicini, gli incantesimi e le abilità d\'attacco hanno una probabilità di evocare un Vespro della Lucidità per un breve periodo. Il Vespro della Lucidità stordisce istantaneamente i nemici vicini e aumenta i danni da te inflitti finché è vicino.', 38134),
(202362, 'itIT', 'Kyrian! Ti do il benvenuto negli Archivi degli Eoni, dove un afflusso d\'Animum ci permette di rinforzare le tue capacità', 'Kyrian! Ti do il benvenuto negli Archivi degli Eoni, dove un afflusso d\'Animum ci permette di rinforzare le tue capacità', 38134),
(202360, 'itIT', 'Sarà una grande occasione!', '', 38134),
(202359, 'itIT', 'La mia grinta è pari solo alla leggerezza della mia anima.', '', 38134),
(202358, 'itIT', 'Puoi concedermi un momento?', '', 38134),
(202357, 'itIT', 'Arcilich! Unisciti a me! Usa il medaglione che ti è stato dato e assieme annienteremo qualunque Calcafauce!', '', 38134),
(202356, 'itIT', 'Presto, Calcafauce, uccidi tutto ciò che trovi fino al prossimo pozzo. Sento avvicinarsi il potere del mio medaglione.', '', 38134),
(202355, 'itIT', 'Che la penitenza cominci!', '', 38134),
(202354, 'itIT', 'Risponderete della vostra arroganza!', '', 38134),
(202353, 'itIT', '', 'Quanto a Bwonsamdi, il suo destino non mi riguarda. Aiutalo oppure no, come desideri. Io devo occuparmi di Selvarden.', 38134),
(202352, 'itIT', '', 'Mio fratello non è uno che risparmia nelle cerimonie.', 38134),
(202349, 'itIT', 'Il Calcafauce ha accettato di unirsi alla nostra causa. Ascolteremo il suo giuramento ed entrerà a far parte della ribellione.', '', 38134),
(202347, 'itIT', 'Ora, prendi posto accanto a me.', '', 38134),
(202331, 'itIT', 'Sì, amico mio. Insieme a questi mortali, otterremo la nostra vittoria.', '', 38134),
(202330, 'itIT', 'Il giusto destino per una tale superbia. Ci osserverà mentre rigenereremo Revendreth e rovineremo i piani del Carceriere.', '', 38134),
(202329, 'itIT', 'Troppo a lungo Denathrius ha usato la Luce come arma contro la nostra gente nel Distretto delle Braci. Ora la useremo per vincolare lui.', '', 38134),
(202328, 'itIT', '', 'Sarei felice di rappresentare la mia Regina alla Corte delle Braci.', 38134),
(202327, 'itIT', '', '$n, ci vedremo presto alla Corte delle Braci.', 38134),
(202326, 'itIT', 'Porteremo la lama a Oblio del Peccato. Il Naaru salvato dai mortali, Z\'rali, ha promesso di aiutarci.', '', 38134),
(202325, 'itIT', 'Vero, ma ho qualcosa che potrebbe farlo.', '', 38134),
(202324, 'itIT', 'Se quella spada contiene davvero l\'essenza del Sire, temo che i tuoi incantesimi non potranno contenerne il potere.', '', 38134),
(202323, 'itIT', 'Abbiamo sentito a sufficienza!', '', 38134),
(202322, 'itIT', '', 'Principe traditore! Ti infilzerò! Taglierò! Fenderò! Vivisezionerò! Farò a brandelli la tua...', 38134),
(202321, 'itIT', 'No. Denathrius risponderà del suo tradimento.', '', 38134),
(202320, 'itIT', '', 'Zovaal ti rigenererà, Maestro! Avremo la nostra vendetta!', 38134),
(202319, 'itIT', 'La lama ha assorbito la sua essenza!', '', 38134),
(202317, 'itIT', '', 'Non è la fine, Maestro! Lascia che ti preservi!', 38134),
(202314, 'itIT', 'Calmati, Remornia. La fretta rende tutto così... sgraziato.', '', 38134),
(202309, 'itIT', '', 'È grazie all\'impegno di tutti che la natura incontaminata del nostro regno viene preservata. Ben fatto.', 38134),
(202308, 'itIT', '', 'Questa zona è allo sbando. Fortunatamente, abbiamo molti Factotum a nostra disposizione per affrontare ogni problema. Assicurati che l\'ordine sia ripristinato.', 38134),
(202307, 'itIT', 'Guarda, Remornia! Questi audaci mortali hanno perseverato oltre ogni ostacolo posto dinanzi a loro. Sembra che non ci resti altro che arrenderci.', '', 38134),
(202306, 'itIT', 'Siete la chiave della vittoria, mortali! Andate, assalite colui che ci ha tradito tutti!', '', 38134),
(202305, 'itIT', 'Altri lealisti! Dobbiamo respingerli, mio Principe!', '', 38134),
(202302, 'itIT', 'Presto altri Drust ci attaccheranno. Sbrigati!', '', 38134),
(202299, 'itIT', '', 'Be\', è andata bene come mi aspettavo.', 38134),
(202298, 'itIT', '', 'Ho fatto del mio meglio per rendere tutto interessante per tutti, ma temo che non sia bastato.', 38134),
(202297, 'itIT', 'Devo tornare al lavoro. Ho perso troppo tempo.', '', 38134),
(202296, 'itIT', '', 'Temo che tu abbia ancora molto da imparare sul tenere una corte, mio caro Principe.', 38134),
(202295, 'itIT', 'Ho perso fin troppo tempo lontano dal campo di battaglia.', '', 38134),
(202294, 'itIT', '', 'Le corti non fanno per me. Devo tornare alla caccia.', 38134),
(202293, 'itIT', 'Ora capisco perché l\'Arconte ha voluto umiliarmi mandandomi qui.', '', 38134),
(202292, 'itIT', 'Già... Facciamo a gara fino alla carrozza?', '', 38134),
(202291, 'itIT', 'L\'alta società non è esattamente come me l\'aspettavo.', '', 38134),
(202290, 'itIT', 'Celontra, finisci il resto in fretta! Dobbiamo andarcene!', '', 38134),
(202289, 'itIT', 'È sempre un onore essere invitato, Principe Renathal. Forse... dovresti concedere questo onore anche ad altri la prossima volta.', '', 38134),
(202288, 'itIT', '', 'Speravo che fossi più convincente, Principe Renathal. Voglio credere nella tua causa.', 38134),
(202287, 'itIT', 'Mi diverto di più a guardare i fabbri affilare il metallo.', '', 38134),
(202286, 'itIT', 'Non era così male, Kleia. Quel Dragatore è stato molto gentile con noi. Gubbins.', '', 38134),
(202285, 'itIT', '', 'Sia ringraziata l\'Arconte, è finita.', 38134),
(202284, 'itIT', 'Be\', almeno Kevin si è divertito un po\'. Credo che non ti mancheranno quei Dragatori.', '', 38134),
(202283, 'itIT', '', 'Dovevo stare al Bastione. Più pulito.', 38134),
(202282, 'itIT', 'A festa servono più merendine.', '', 38134),
(202281, 'itIT', 'Parlo di due esperimenti separati e paralleli, chiaramente... ma sono entrambi andati bene!', '', 38134),
(202280, 'itIT', '', '<Dama Baccaluna ti fa l\'occhiolino.>', 38134),
(202279, 'itIT', 'È ora di detronizzare il nostro vecchio Maestro. Ciò che faremo dopo sarà per il bene di tutte le Terretetre!', '', 38134),
(202278, 'itIT', 'I Litogeni non sono gli unici a essere stati manipolati e ingannati, amico mio. Oserei dire che persino l\'Arbiter è stata usata come pedina.', '', 38134),
(202277, 'itIT', 'La mia gente è chiamata dalla pietra per difendere Revendreth. Soffro al vedere la nostra fedeltà corrotta da colui che servivamo.', '', 38134),
(202275, 'itIT', 'Mi è piaciuto quando ho fatto battuta e migliore $gamico:amica; ha riso.', '', 38134),
(202274, 'itIT', '', 'Corte divertente! Viaggio ripagato! Dirò tutto ai miei amici!', 38134),
(202273, 'itIT', 'Un ottimo esperimento di amicizia e melma!', '', 38134),
(202272, 'itIT', 'Quando restiamo uniti, nessuno può resistere contro di noi.', '', 38134),
(202271, 'itIT', '', 'Ammiro la tua dedizione nel servire le Terretetre persino al di là di Revendreth.', 38134),
(202270, 'itIT', 'Mi piace il tuo stile, Renathal. Un principe che non ha paura di sporcarsi le mani... C\'è un sacco di potenziale.', '', 38134),
(202269, 'itIT', '', 'Sono stupita di ciò che ho visto oggi. Riporterò sia le tue parole che le tue gesta alla Regina dell\'Inverno.', 38134),
(202268, 'itIT', 'Certo che è il regalo migliore. L\'abbiamo fatto noi!', '', 38134),
(202267, 'itIT', 'Spero di farti il regalo migliore! Così mi inviti ancora, vero?', '', 38134),
(202266, 'itIT', '', 'Non vedo l\'ora di cacciare assieme a te in futuro.', 38134),
(202265, 'itIT', 'Grazie per il meritato riposo. Tornerò in battaglia rinnovato!', '', 38134),
(202263, 'itIT', 'E ci siamo uniti per respingere il nemico comune a tutti i regni: i divoratori!', '', 38134),
(202262, 'itIT', 'Fate attenzione. Tra noi e la nostra preda ci sono ancora i guardiani più fedeli di Denathrius.', '', 38134),
(202261, 'itIT', 'Dobbiamo continuare la nostra ricerca, mortali. Fortunatamente, questo specchio sembra riparabile.', '', 38134),
(202260, 'itIT', 'No. Fai in modo che non possano.', '', 38134),
(202259, 'itIT', 'Vero, hai seminato una certa devastazione lungo la strada. Se sei così prono alla distruzione, il mio imponente amico ti darà una mano.', '', 38134),
(202258, 'itIT', 'Ma ho appreso abbastanza da riuscire a liberarmi di molti dei tuoi servitori, \"Maestro\".', '', 38134),
(202257, 'itIT', 'Dopo tutti i miei insegnamenti, Renathal, eccedi nuovamente il tuo ruolo. Tsk tsk tsk. Che delusione.', '', 38134),
(202256, 'itIT', 'Finora avete agito bene, mortali, colpendo con ferocia le forze di Denathrius, ma non sottovalutatelo. La sua potenza è... considerevole.', '', 38134),
(202255, 'itIT', 'Da questa parte c\'è un altro passaggio per l\'anticamera.', '', 38134),
(202253, 'itIT', 'Guardate come corrono! Mi nausea vedere quanto siano caduti in basso alcuni dei nostri.', '', 38134),
(202252, 'itIT', 'Venite, mortali. È tempo di liberare Revendreth dall\'arrogante banda di nobili che si frappone tra noi e Denathrius.', '', 38134),
(202251, 'itIT', 'Porta via quest\'anima e aiutala, Accusatrice.', '', 38134),
(202250, 'itIT', '', 'Hai così tanto da imparare.', 38134),
(202249, 'itIT', 'Sono stufo di essere usato come pedina dagli altri. Il tuo \"aiuto\" mi farà ottenere vendetta?', '', 38134),
(202248, 'itIT', '', 'Kael\'thas Solealto! Sei libero dalla tirannia del Sire, ma non puoi sfuggire alle tue colpe. Vieni con me, ti aiuterò a trovare la redenzione.', 38134),
(202244, 'itIT', 'L\'avidità... Senza dubbio uno dei peccati più maligni.', '', 38134),
(202243, 'itIT', 'Orribile! La nostra gente patisce la fame fuori dalle mura del castello e questi lealisti si crogiolano nell\'avarizia.', '', 38134),
(202242, 'itIT', 'Questi Alienatori sono molto avidi. Non lasciate che armi del genere cadano in mano loro!', '', 38134),
(202241, 'itIT', 'Molte armi di valore adornano queste sale. Liberiamole dalle grinfie di questi aristocratici.', '', 38134),
(202240, 'itIT', '', 'Mentre banchettano, il Carceriere accumula un esercito per distruggere le Terretetre. Chi crede alle bugie di Denathrius merita il suo destino!', 38134),
(202239, 'itIT', 'Questo specchio vi sarà utile per spostarvi nel castello, mortali.', '', 38134),
(202238, 'itIT', 'I Litogeni utilizzavano queste vie per ottemperare alle richieste del Maestro. Quanta depravazione ho potuto osservare...', '', 38134),
(202237, 'itIT', 'Di qua, mortali. Conosco un passaggio che porta all\'anticamera.', '', 38134),
(202236, 'itIT', 'Ti piace cappello?\n\nNuovo tesoro Lyn mi ha fatto bello per festa.\n\nNon preoccuparti, tu ancora tesoro migliore!', '', 38134),
(202235, 'itIT', '', 'Non c\'è tempo per i rimorsi. Il regno di Denathrius deve terminare.', 38134),
(202234, 'itIT', '', 'Abbiamo arginato il flusso d\'Animum, ma temo che il danno sia già stato fatto. Possiamo solo recuperare il recuperabile per il bene di Revendreth.', 38134),
(202232, 'itIT', 'Ben fatto, mortali! Le scorte d\'Animum ci attendono!', '', 38134),
(202231, 'itIT', '', 'Liberatevi della creatura, prima che diventi più forte!', 38134),
(202230, 'itIT', '', 'La bestia vuole saziare la sua fame infinita con le scorte d\'Animum quaggiù.', 38134),
(202229, 'itIT', 'Un divoratore? Qui?!? È il più grosso che abbia mai visto!', '', 38134),
(202224, 'itIT', '', 'Sono curiosa, Calcafauce. Consideri Revendreth la tua vera casa?\n\nIl Principe Renathal è il TUO principe?\n\n<Il Droman guarda oltre le tue spalle.>\n\nLa mia casa mortale non esiste più . Distrutta.\n\nMa io mi sento a casa a Selvarden, sotto la Regina dell\'Inverno.\n\n<La Droman fa un lungo sospiro.>\n\nLe considerazioni che il tuo Principe ci chiede di fare non sono così facili.', 38134),
(202223, 'itIT', '', 'Non... posso... continuare...', 38134),
(202222, 'itIT', 'Devo dire che è bello avere dei Dragatori che aspettano ME per un cambiamento.\n\n<Squartacus sorride orgogliosamente e gonfia un po\' il petto.>\n\nSì, il vecchio Squartacus si sta finalmente facendo strada in questo mondo fangoso.', '', 38134),
(202217, 'itIT', '', 'La via sarà colma di pericoli, ma dobbiamo raggiungere quelle scorte d\'Animum!', 38134),
(202216, 'itIT', 'Il passaggio per scendere è qui, mortali.', '', 38134),
(202215, 'itIT', '', 'Una degna fine per quella carogna.', 38134),
(202214, 'itIT', 'Metti fine alla crudeltà del Maestro dei Segugi per poter accedere ai livelli inferiori del castello.', '', 38134),
(202213, 'itIT', 'I canili ospitano il branco da caccia del Maestro. Egli affama le sue bestie.', '', 38134),
(202212, 'itIT', '', 'Se vogliamo avere una possibilità contro Denathrius, dobbiamo privarlo dei suoi beni più preziosi. Conosco la via che dobbiamo seguire per raggiungerli.', 38134),
(202211, 'itIT', 'C\'è un passaggio oltre i canili che porta giù verso le fondamenta del castello. Da là potremo raggiungere le scorte d\'Animum e impedirne il flusso verso la Fauce.', '', 38134),
(202210, 'itIT', 'L\'anticamera è nostra! Sospetto però che Denathrius si sia ritirato nel suo santuario.', '', 38134),
(202209, 'itIT', 'Non sono abituato né al vocio della mondanità né all\'opulenza della regalità.\n\n<L\'espressione di Alexandros si ammorbidisce, mentre allenta la presa sull\'arma.>\n\nMa ti sono grato per questo momento di riposo. Anche al guerriero più forte dev\'essere concesso di distrarsi, tra una battaglia e l\'altra.\n\nUserò questo tempo per ricaricarmi e concentrarmi solo sulla lotta che mi aspetta.\n\nSia fatta la volontà di Krexus.', '', 38134),
(202208, 'itIT', 'Diamo il via alla vostra espiazione con una sfida adeguata, che ne dite?', '', 38134),
(202207, 'itIT', 'Sembra che tu non abbia imparato la lezione. Forse sono stato troppo clemente. Dopotutto, sono conosciuto per la mia compassione.', '', 38134),
(202206, 'itIT', 'Ricordo una simile spacconata da parte tua non troppo tempo fa, Renathal. Sappiamo come è finita.', '', 38134),
(202205, 'itIT', 'Finiscila di pavoneggiarti, Denathrius! Affrontaci!', '', 38134),
(202204, 'itIT', 'Il Principe ribelle, naturalmente. L\'Accusatrice deposta, più seria che mai. Il nostro vecchio generale ammutinato. Assieme a una marmaglia di mortali desiderosi di perire.', '', 38134),
(202203, 'itIT', 'Benvenuti! Attendevo con molta ansia il vostro arrivo. Vediamo, chi abbiamo qui?', '', 38134),
(202199, 'itIT', 'State in guardia, mortali. Denathrius qui è in vantaggio. Userà tutta la sua astuzia per ostacolarci.', '', 38134),
(202198, 'itIT', '', 'Sa che il tempo è contro di noi. Il potere del Carceriere cresce ogni momento. Il flusso d\'Animum verso la Fauce deve essere fermato.', 38134),
(202196, 'itIT', 'Mmm.$b$b<È chiaramente in allerta. È anche molto bravo a mostrare la versatilità della parola \"Mmm\".>', '', 38134),
(202195, 'itIT', 'È stato troppo facile irrompere nel castello. Il nostro vecchio Maestro ci sta sfidando ad avventurarci in profondità.', '', 38134),
(202194, 'itIT', 'Mmm.$b$b<Sembra quasi pensieroso.>', '', 38134),
(202193, 'itIT', 'Mmm.$b$b<Sembra che stia provando molto, molto duramente a ignorare i rumori nei paraggi. Sembra anche che non ci stia riuscendo.>', '', 38134),
(202192, 'itIT', 'Mmm.$b$b<Questo sembra visibilmente irritato nei tuoi confronti.>', '', 38134),
(202191, 'itIT', '', 'Non avevo idea che i Venthyr potessero essere così... ingenui. È stato un sacco divertente farmi beffe di loro.\n\n<Dama Baccaluna sogghigna e guarda di sottecchi da una parte.>\n\nOh, mi sa che devo andare, una delle mie immagini sta per...\n\nA dire il vero, probabilmente è meglio se non lo sai.', 38134),
(202190, 'itIT', 'La meditazione mi rende uno con la foresta. Mi aiuta a discernere ciò che non le appartiene.$b$B<Il Vorkai spalanca un occhio e ti guarda.>$b$bEssa richiede di non distrarsi. Va\' a parlare con Narciso.', '', 38134),
(202189, 'itIT', 'Mmm.$b$B<Non sai se il Vorkai sta meditando ancora più duramente o se è semplicemente irritato con te.>', '', 38134),
(202188, 'itIT', 'Grazie alla Regina sei qui!', '', 38134),
(202185, 'itIT', 'È finita, Gorak Zhar!', '', 38134),
(202184, 'itIT', '', 'No! Pagherai per la tua insolenza col sangue!', 38134),
(202182, 'itIT', 'Devo prepararmi per il viaggio. Sono impaziente d\'interpellare i nostri alleati.', '', 38134),
(202181, 'itIT', '', 'Gli Occhi ti comandano ora. Attendi i nostri ordini.', 38134),
(202179, 'itIT', 'Le Calzature dell\'Alfiere sono utili per muoversi! \n\nI nemici ti rallentano o ti bloccano?\n\nNon con questi stivali! Puoi scattare subito, senza intralci!', '', 38134),
(202178, 'itIT', '', 'Basta! Mi arrendo. Dimmi le tue condizioni.', 38134),
(202177, 'itIT', 'Io mi prendo cura del tuo equipaggiamento, non preoccuparti! Tutto sarà pronto per il tuo cammino sulla Via.\n\nVuoi informazioni sull\'equipaggiamento disponibile? Quali?', '', 38134),
(202175, 'itIT', '', 'Facciamo brillare tutto!', 38134),
(202174, 'itIT', '', 'Spie?!? Ve ne pentirete, vermi!', 38134),
(202173, 'itIT', '', 'Dove mi trovo?!? Chi osa evocarmi?', 38134),
(202172, 'itIT', 'Dannazione, Narciso...', '', 38134),
(202171, 'itIT', '', 'Così imparano! Chi dice che la magia non possa risolvere tutto?', 38134),
(202170, 'itIT', '', 'Che ne diresti di diffondere un altro po\' di caos scintillante?', 38134),
(202169, 'itIT', 'Prendi posto appena vuoi iniziare.', '', 38134),
(202168, 'itIT', 'Ci vedremo nelle sale di sotto quando vorrai iniziare.', '', 38134),
(202167, 'itIT', '', 'Le note non sono nuovo lavoro... Le note offrono aiuto?', 38134),
(202166, 'itIT', 'Attorno a me, gli Ascesi parlano della Via. Delle prove in cui ogni anima deve trovare la sua pace, la sua eternità, dopo una vita mortale imperfetta.', '', 38134),
(202165, 'itIT', 'Ho provato a dimenticare. Ho provato a perdonare. Eppure... lui si annida in ogni mio pensiero, lungo ogni percorso.', '', 38134),
(202164, 'itIT', 'Questa rabbia mi è estranea, irriconoscibile. Devo purificarla, questa è la mia via. La mia Eletta ha detto che dovrebbe essere così, e SARÀ così.', '', 38134),
(202163, 'itIT', 'La Luce ha condannato la tua anima, Arthas. Che possa marcire nella Fauce!', '', 38134),
(202162, 'itIT', '', 'Oggi ho chiesto di nuovo quando potrò tentare il mio ultimo rito. E come sempre, nessuna risposta. Questo posto mi ha insegnato la pazienza, certo, ma viene messa ancora alla prova.', 38134),
(202161, 'itIT', '', 'Vedo la frustrazione dei miei compagni Aspiranti. Io stessa la sento intensamente, ma non devo darlo a vedere. Loro mi ammirano! Pelagos mi ammira...', 38134),
(202160, 'itIT', '', 'Quando finirà questa siccità? Cosa l\'ha causata in primo luogo? Gli Ascesi lo sanno o sono smarriti come noi? Quanto vorrei avere delle risposte.', 38134),
(202159, 'itIT', '', 'Devo ricordarmi il mio addestramento. Devo concentrarmi e trovare la pace nelle cose che posso controllare.', 38134),
(202158, 'itIT', 'Mi è stato detto che scrivere fornisce a una creatura la capacità di riflettere e distanziarsi dai propri problemi. Vediamo...', '', 38134),
(202157, 'itIT', 'Oggi ho tentato di nuovo il Rito della Purezza. Il mio Vincolo dell\'Anima mi ha assicurato che ero pronto. Mi sentivo forte, all\'inizio! Ma poi ho ceduto, come sempre.', '', 38134),
(202156, 'itIT', 'Perché sono un tale disastro? Tutto questo ha perfettamente senso quando lo spiega Kleia, ma quando è il momento di agire, mi sembra di dimenticare tutto ciò che ho imparato.', '', 38134),
(202155, 'itIT', 'A volte temo che non ascenderò mai. Sarò rimandato a Oribos e l\'Arbiter scruterà nella mia anima e...', '', 38134),
(202153, 'itIT', '', 'Ormai potrebbe essersene occupato un divoratore. Sarebbe un vero peccato.', 38134),
(202152, 'itIT', '', 'Tornerà presto. Abbiamo poco tempo. Lascia che ti mostri la verità.', 38134),
(202151, 'itIT', '', 'Calcafauce, Denathrius ha fatto di te la sua pedina.', 38134),
(202150, 'itIT', '', 'Va\' avanti! Io proteggerò il ponte.', 38134),
(202149, 'itIT', 'Il prezzo del potere di Elune fu la mia vita.\n\nNon era pensato per essere impugnato dai mortali. In un modo o nell\'altro mi avrebbe ucciso. Ero disposto al sacrificio, per il mio mondo, la mia gente, la mia famiglia.\n\nQadarin non era così ben disposto a farlo accadere. Con mio eterno rimorso, cercare di condividerne il peso ci uccise entrambi.\n\n<Thiernax sospira.>\n\nDovrei lasciare stare il passato. Ciò che conta è che il nostro mondo si sia salvato e noi siamo ancora assieme.', '', 38134),
(202146, 'itIT', 'C\'è tanto da fare prima che la Regina arrivi. Non c\'è tempo da perdere!', '', 38134),
(202144, 'itIT', '', 'Lascia che ti mostri la verità, Calcafauce.', 38134),
(202143, 'itIT', '', 'La tua lealtà è stata notata e riconosciuta. Ora fai parte dei nostri.', 38134),
(202142, 'itIT', 'Nell\'intrico i tuoi occhi possono ingannarti. Quello che sembra vicino si cela dietro una coltre di nebbia. \n\nPuoi provare quanto vuoi, ma il risultato sarà lo stesso. Chi osa avventurarsi da solo spesso si perde all\'interno del tempo stesso. \n\nLa speranza risiede solo nella consapevolezza. L\'intrico nasconde molti segreti solo per i più arguti. \n\nLascia che la luce guidi il tuo cammino, l\'attraversamento del velo è già iniziato.', '', 38134),
(202141, 'itIT', '', 'Riconosco il tuo impegno come onesto e sincero. Puoi alzarti.', 38134),
(202140, 'itIT', '', 'Sika è molto dolce e lavora duramente. Dovrei fare di meglio per mantenere la forgia pulita in modo da ridurre i suoi problemi.', 38134),
(202139, 'itIT', 'Grazie, Calcafauce.\n\nHo trattenuto questi dragoni dell\'etere mentre gli Aspiranti fuggivano... ma i loro numeri erano fin troppo grandi.\n\n<Adonas si tocca il fianco con gentilezza.>\n\nSono felice che tu sia $gcomparso:comparsa; al momento giunto.', '', 38134),
(202138, 'itIT', '', 'Nessuno è mai fuggito dalle Profondità Sanguigne, e voi non sarete i primi!', 38134),
(202137, 'itIT', '', 'Non siete altro che i cani di Draven!', 38134),
(202136, 'itIT', '', 'Miserabili mortali!', 38134),
(202135, 'itIT', '', 'Incontriamoci al pilastro, abbiamo del lavoro da fare.', 38134),
(202134, 'itIT', 'Oh. Ho parlato con Sika per riorganizzare la biblioteca e sembrava molto felice di farlo. Non avevo idea che fosse così occupata. Me ne occuperò io!', 'Oh. Ho parlato con Sika per riorganizzare la biblioteca e sembrava molto felice di farlo. Non avevo idea che fosse così occupata. Me ne occuperò io!', 38134),
(202133, 'itIT', 'Tutto qui quello che sai fare?', '', 38134),
(202132, 'itIT', 'Certo, ti sosterrò io.', '', 38134),
(202131, 'itIT', 'Uh... È tutto qui?', '', 38134),
(202130, 'itIT', 'Hai un problema da risolvere?', '', 38134),
(202129, 'itIT', 'Quando ti serve il meglio, io sono disponibile.', '', 38134),
(202128, 'itIT', 'Chi uccideremo oggi?', '', 38134),
(202127, 'itIT', 'Ho affrontato difficoltà maggiori di questa.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202123, 'itIT', '', 'È perché ho le lame al posto delle mani, vero?', 38134),
(202122, 'itIT', '', 'Ti serve una mano?', 38134),
(202121, 'itIT', '', 'È l\'ora di un abbraccio!', 38134),
(202120, 'itIT', '', 'Pensami quando me ne andrò.', 38134),
(202117, 'itIT', 'Aiuti anche Efesius?', '', 38134),
(202116, 'itIT', '', 'Gli amici devono stare appiccicati.', 38134),
(202115, 'itIT', '', 'Serve un passaggio?', 38134),
(202113, 'itIT', 'Che il suono di quella campana sia d\'ispirazione ancora una volta.', '', 38134),
(202109, 'itIT', 'Sì, cumpà?', '', 38134),
(202107, 'itIT', 'Combattere un grande male che ne uccise molti.\n\nVenne formata una coalizione di campione per uccidere il dio oscuro atterrato nel nostro mondo. Per riuscire ci servì il potere che l\'ultimo Oratore cercò di invocare dalla Madre Luna.\n\n<Thiernax sorride tristemente.>\n\nDovemmo discuterne aspramente. Aveva già ucciso sua madre, non volevo che uccidesse anche suo marito. Ma il potere di Elune era una delle nostre armi migliori contro i mostri, per cui eseguii il rituale, e sopravvissi. Restò al mio fianco. I nostri occhi divennero neri come le nuove lune e combattemmo i mostri.\n\nNon potei prevederne il costo.', '', 38134),
(202104, 'itIT', 'Alla fine qualcuno riuscì.\n\nThiernax divenne il Guerriero della Notte. Combattei al fianco suo e degli altri campioni di Fyzandi fino alla fine. Vincemmo, ma a un terribile costo.\n\nLe armate della follia uccisero molti dei nostri mentre eravamo via, poi si dispersero quando l\'Oscuro cadde. Molti tra la nostra gente caddero vittima dei sussurri. Scomparvero o impazzirono. Perdemmo amici. Famiglia.\n\nIl dio sanguinò. Serviranno molteplici generazioni per purificare questa malattia dal nostro mondo.\n\nSempre che sia possibile.', '', 38134),
(202103, 'itIT', 'Mostri.\n\nCreature con troppi occhi, troppi arti o troppe bocche affamate. Si sparsero in tutto il mondo quando i miei bisnonni erano bambini. È così che iniziò la Guerra Eterna. Intere generazioni passarono la vita assediate dagli Altri e dalla loro follia.\n\nFu mia madre a scoprire la natura di ciò che viveva al centro del cratere. Per uccidere l\'essere giunto da un altro mondo, cercò di invocare il lato oscuro di Elune. Morì.', '', 38134),
(202100, 'itIT', 'Sì. Alcune generazioni prima che nascessi, il Dio Oscuro atterrò nel nostro mondo.\n\nL\'impatto distrusse un\'intera nazione e rovinò l\'equilibrio della natura. Nessuno che decise di avventurarsi nella zona colpita fece mai ritorno.\n\nNoi druidi tenemmo d\'occhio i confini, ma non avremmo mai potuto prevedere ciò che sarebbe successo.', '', 38134),
(202098, 'itIT', 'Il mio mondo si chiama Fyzandi. Uno splendido mondo colmo di foreste sconfinate, montagne possenti e pianure serene.\n\nEra per queste lande che avevamo combattuto contro il Dio Oscuro.', '', 38134),
(202097, 'itIT', 'Sì, prendiamo le ricompense! Che ne dite di una canzone per festeggiare? Dona un soldo al tuo cacciatore...', '', 38134),
(202096, 'itIT', 'Ehi, anche io ho aiutato! Ricordi quel canto orribile?', '', 38134),
(202095, 'itIT', 'Ah, ce l\'ho fatt... Aspetta, cosa?', '', 38134),
(202094, 'itIT', 'Apprezzo l\'aiuto che mi hai dato. Ce la siamo vista brutta, ma hai combattuto bene.', '', 38134),
(202093, 'itIT', 'Ugh.', '', 38134),
(202092, 'itIT', 'Mmm, quello che importa è che ce l\'abbiamo fatta. Muoviamoci.', '', 38134),
(202091, 'itIT', 'Ottimo lavoro, i Boggart odiano i rumori orribili.', '', 38134),
(202087, 'itIT', 'Aspetta, fermo!', '', 38134),
(202086, 'itIT', 'OH NO!', '', 38134),
(202085, 'itIT', 'Perché? Non sembra così male. Di sicuro non è la creatura che stiamo cercando. Me ne occuperò facilmente, osserva!', '', 38134),
(202084, 'itIT', 'Non aver paura, rozzo pagliacc... Eh? Io, ehm... Gwynceirw? Credo di aver trovato qualcosa?', '', 38134),
(202083, 'itIT', 'Dov\'è la bestia? Avrà detto addio? Se non mi aiuti, allora ci penserò io!', '', 38134),
(202082, 'itIT', 'Ci pensa Narciso, il brillante cantore. Non come quel babbeo, che fa solo rumore!', '', 38134),
(202081, 'itIT', 'Oh, Narciso, oh, Narciso, è il migliore! Non come il suo compagno, che vuole solo DORMIRE!', '', 38134),
(202080, 'itIT', 'Oh, ti dirò esattamente cosa farò! Se sarà necessario, lo troverò io stesso. Nel mentre, mi metterò anche a comporre una canzone! Ecco, cosa ne dici...', '', 38134),
(202079, 'itIT', 'Chi, lui? Lascialo perdere, è inquieto perché non ha ancora cacciato la sua preda. In realtà è un ottimo combattente, ma i suoi metodi sono... strani. Ma io non ho detto niente e tu non mi hai sentito.$b$bNon che POSSA SENTIRMI MENTRE DORME.$B$B<Gwinceirw non sembra reagire.>', '', 38134),
(202078, 'itIT', 'Hai smesso di sonnecchiare e ti sei unito eh?$b$BOh, non sei lui. Be\', forse mi verrai utile. Per caso hai visto un orribile terrore vegetale qua vicino da poco?', '', 38134),
(202077, 'itIT', 'Sto. Meditando. O almeno ci provo.$b$bPerché non vai a parlare con Narciso? Gli piace parlare.', '', 38134),
(202076, 'itIT', 'Da qualche parte nel Bastione si trova una campana particolare, che non suona da molto tempo. \n\nUn tempo serviva da rituale per tutti i Kyrian. Tuttavia, la maggior parte dei Mazzuoli Cristallini usati per suonare queste campane sono andati in pezzi.\n\nSe qualcuno posse recuperarne i pezzi, potrei forgiarne un altro.', '', 38134),
(202075, 'itIT', 'Grazie al cielo sei qui.', 'Grazie al cielo sei qui.', 38134),
(202074, 'itIT', 'Grazie al cielo sei qui. Ho preso un po\' di questo Animum in eccesso e ho ideato un piano per riutilizzare un po\' di questa cenere attorno a noi.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Urna di Ceneri]|h|r: dopo aver sconfitto diversi nemici, richiama un Ragno delle Ceneri a combattere al tuo fianco. Il ragno non può essere curato, ma ogni nemico sconfitto insieme aumenta i suoi danni e lo cura.', 'Grazie al cielo sei qui. Ho preso un po\' di questo Animum in eccesso e ho ideato un piano per riutilizzare un po\' di questa cenere attorno a noi.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Urna di Ceneri]|h|r: dopo aver sconfitto diversi nemici, richiama un Ragno delle Ceneri a combattere al tuo fianco. Il ragno non può essere curato, ma ogni nemico sconfitto insieme aumenta i suoi danni e lo cura.', 38134),
(202073, 'itIT', 'Fai silenzio.', 'Fai silenzio.', 38134),
(202072, 'itIT', 'Se fai silenzio posso fornirti un aiuto per eliminare questi disgustosi miscredenti. Ho privato alcuni di loro dei loro miseri specchi, che puoi usare a nostro vantaggio.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Set di Specchio Mobile]|h|r: gli incantesimi e le abilità d\'attacco hanno una probabilità di evocare delle schegge di specchio che concentrano la luce in un raggio frontale, infliggendo danni da fuoco ai nemici istantaneamente e danni da fuoco periodici aggiuntivi.', 'Se fai silenzio posso fornirti un aiuto per eliminare questi disgustosi miscredenti. Ho privato alcuni di loro dei loro miseri specchi, che puoi usare a nostro vantaggio.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Set di Specchio Mobile]|h|r: gli incantesimi e le abilità d\'attacco hanno una probabilità di evocare delle schegge di specchio che concentrano la luce in un raggio frontale, infliggendo danni da fuoco ai nemici istantaneamente e danni da fuoco periodici aggiuntivi.', 38134),
(202071, 'itIT', 'Venthyr! Ci siamo nascosti tra gli Ammantati nella speranza che tu possa aiutarci. Ho rubato dell\'Animum per te.', 'Venthyr! Ci siamo nascosti tra gli Ammantati nella speranza che tu possa aiutarci. Ho rubato dell\'Animum per te.', 38134),
(202070, 'itIT', 'Venthyr! Ci siamo nascosti tra gli Ammantati nella speranza che tu possa aiutarci. Ho rubato dell\'Animum per te.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Sete d\'Animum]|h|r: quando si ha poca salute, gli incantesimi e le abilità d\'attacco hanno un\'alta probabilità di stordire il nemico per qualche istante, infliggendogli danni e rigenerando la propria salute per tutta la durata.\n\nQuesto effetto non può verificarsi molto spesso, meglio fare attenzione.', 'Venthyr! Ci siamo nascosti tra gli Ammantati nella speranza che tu possa aiutarci. Ho rubato dell\'Animum per te.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Sete d\'Animum]|h|r: quando si ha poca salute, gli incantesimi e le abilità d\'attacco hanno un\'alta probabilità di stordire il nemico per qualche istante, infliggendogli danni e rigenerando la propria salute per tutta la durata.\n\nQuesto effetto non può verificarsi molto spesso, meglio fare attenzione.', 38134),
(202066, 'itIT', 'Non posso stare più nella Fauce. Riporta li spiriti a casa a Selvarden...', '', 38134),
(202056, 'itIT', 'Rezan, nun sprecherò il dono tuo. Il nome tuo sarà ricordato... per sempre.', '', 38134),
(202054, 'itIT', 'Hanno... bisogno... di te....', '', 38134),
(202040, 'itIT', 'Calcafauce, abbatti la barriera!', '', 38134),
(202039, 'itIT', 'Vendicheremo i posseduti qui e ora! Per Selvarden!', '', 38134),
(202038, 'itIT', 'Che stregoneria è mai questa? Uccidi i suoi servitori! Io metterò al sicuro i Semebrado!', '', 38134),
(202037, 'itIT', 'Rezan! Il Loa dei Re! Dobbiamo spezzare chella barriera!', '', 38134),
(202035, 'itIT', 'Ce ne manca uno... uno quasi morto...', '', 38134),
(202034, 'itIT', 'Shadra! Bwonsamdi mi ha detto che la sacerdotessa sua l\'ha consumata...', '', 38134),
(202033, 'itIT', 'Ci stanno più Loa di qua. Presto!', '', 38134),
(202032, 'itIT', 'Hir\'eek! Ho sentito lo strillo suo quando è morto a Nazmir...', '', 38134),
(202029, 'itIT', 'Mi stai portando qualcosa?', '', 38134),
(202027, 'itIT', '', 'Aspetta!', 38134),
(202024, 'itIT', '', 'Sento che sono successe molte cose durante la mia assenza.\n\nAvrò bisogno di tempo per tornare pienamente in me, sempre che sia possibile.\n\nTi ringrazio, $n, per avermi salvata.', 38134),
(202021, 'itIT', 'Sarebbe un onore aiutare Sika. Posso assicurare che le sue prossime spedizioni arriveranno intatte e senza ritardi.', '', 38134),
(202020, 'itIT', '', 'Parassiti! Le vostre parti ci serviranno bene.', 38134),
(201999, 'itIT', '', 'Thros inghiottirà questo regno!', 38134),
(201996, 'itIT', '', 'Patetiche creaturine! State lottando invano!', 38134),
(201995, 'itIT', '', 'Posso percepire l\'immensa paura della nostra preda.', 38134),
(201994, 'itIT', '', 'Manifestazioni della speranza che resta loro. Siamo vicini.', 38134),
(201993, 'itIT', '', 'La loro speranza infranta indica la via.', 38134),
(201992, 'itIT', 'Oh, sì! Certo che darò una mano. Questa è una fantastica opportunità di aiutare la Forgialita Sofone e forse imparare qualcosa da lei allo stesso tempo.', 'Oh, sì! Certo che darò una mano. Questa è una fantastica opportunità di aiutare la Forgialita Sofone e forse imparare qualcosa da lei allo stesso tempo.', 38134),
(201983, 'itIT', 'Da questa parte. Dobbiamo liberare i sopravvissuti!', '', 38134),
(201980, 'itIT', 'Xavius, il quasi Silvar caduto! Consigliere di Azshara in persona, ha abbracciato la corruzione dell\'Incubo di Smeraldo e ha portato alla rovina il suo popolo! Ammira il potere dei suoi temibili terrori notturni!', '', 38134),
(201979, 'itIT', 'N\'zoth il Corruttore! I suoi occhi, e i suoi pesci, erano ovunque e infettavano l\'intera Azeroth! Di chi ci si poteva fidare? Quali pesci si potevano mangiare? Ancora oggi, nessuno sa la reale portata della sua malvagità!', '', 38134),
(201978, 'itIT', 'Jaina Marefiero, Ammiraglio Supremo e regina di ghiaccio! I suoi venti ghiacciati erano come polvere di diamanti e congelava i suoi nemici finché il sangue nelle loro vene non scorreva più! Così uccideva i suoi nemici e ne faceva polene sulla sua nave da guerra arcana!', '', 38134),
(201977, 'itIT', 'Gul\'dan, capo del Concilio dell\'Ombra! \"Ma come ha fatto a tornare? Da dove è venuto?\" tutti si chiesero! Ma la vera domanda era un\'altra: \"da che TEMPO proviene? E cosa c\'era di così speciale nel suo teschio?', '', 38134),
(201976, 'itIT', 'Kil\'jaeden l\'Ingannatore! Nessuno si ricorda quale fosse l\'inganno, ma forse anche questo fa parte dell\'inganno stesso? La battaglia ha avuto luogo nella Distorsione Fatua, che è la più ingannevole tra le dimensioni, come tutti sappiamo bene!', '', 38134),
(201975, 'itIT', 'Argus il Distruttore! Volevano creare un Titano della Morte, sapendo perfettamente che la Morte rientra nell\'ambito delle Terretetre e della nostra illustre Regina! Non andava per niente bene, per questo gli avventurieri hanno tentato di compiere l\'impossibile: uccidere un mondo!', '', 38134),
(201974, 'itIT', 'Fidati di ciò che rimane dei miei compagni Kyrian! Ci porteranno in salvo!', '', 38134),
(201971, 'itIT', 'Posso finire di costruire! Bene. Corte delle Braci sarà la prossima.', '', 38134),
(201970, 'itIT', 'Anche io. Ti ringrazio.', '', 38134),
(201969, 'itIT', '', 'Devi riflettere su te stesso... Lo capisco bene. Spero che tu possa trovare quel che cerchi.', 38134),
(201968, 'itIT', 'No, figliola. Gli Indegni non sono come pensavo... Dubito che lo siano mai stati. Devo trovare pace e solitudine, ho molto a cui pensare.', '', 38134),
(201967, 'itIT', '', 'Dove andrai ora, Uther? Di sicuro non tornerai dagli Indegni.', 38134),
(201966, 'itIT', 'Abbattete quei traditori!', '', 38134),
(201965, 'itIT', 'Dobbiamo aprirci la strada. Thales?', '', 38134),
(201964, 'itIT', 'Proteggere mortali non era parte dell\'accordo.', 'Proteggere mortali non era parte dell\'accordo.', 38134),
(201963, 'itIT', 'Grazie, mortale. Si meritavano di meglio.', '', 38134),
(201962, 'itIT', 'Sono pagato per proteggere tutto all\'interno del Cancello Antico, anche $guno:una; della tua razza. Forse dovrei chiedere un aumento.', 'Sono pagata per proteggere tutto all\'interno del Cancello Antico, anche $guno:una; della tua razza. Forse dovrei chiedere un aumento.', 38134),
(201960, 'itIT', 'Se non avessi bisogno di questo lavoro e la sua paga, non alzerei un dito per difendere $gun:una; mortale.', 'Se non avessi bisogno di questo lavoro e la sua paga, non alzerei un dito per difendere $gun:una; mortale.', 38134),
(201959, 'itIT', 'Mograine mi ha chiesto di restare indietro nel caso ti allontanassi troppo.\n\nQuesto Portale della Morte ti porterà da lui. Ti suggerisco di usarlo.', '', 38134),
(201958, 'itIT', '', 'Dopo di te, Calcafauce.', 38134),
(201950, 'itIT', '', 'Mograine mi ha chiesto di restare indietro nel caso ti allontanassi troppo.\n\nQuesto Portale della Morte ti porterà da lui. Ti suggerisco di usarlo.', 38134),
(201949, 'itIT', 'Chi commette questi orrori deve fare i conti con la giustizia.', '', 38134),
(201948, 'itIT', '', 'Forse un giorno potremo approfondire la questione. Per ora, fuggiamo da quest\'orribile luogo.', 38134),
(201947, 'itIT', 'Siamo piuttosto simili io e te, Kleia.', '', 38134),
(201946, 'itIT', '', 'Anche se siamo nemici, non sarebbe stato giusto lasciare che tu soffrissi.', 38134),
(201945, 'itIT', 'Soffro, ma non è nulla che non abbia già provato prima. Le mie ferite guariranno.', '', 38134),
(201944, 'itIT', '', 'Come stai, Uther?', 38134),
(201943, 'itIT', 'Devo ringraziarvi per avermi salvato dai soprusi di Lysonia. Temo che non sarebbe rimasto niente di me se non foste intervenuti in quel momento.', '', 38134),
(201942, 'itIT', '', 'I Giurafauce non vedono l\'ora di trovarvi. Dobbiamo fare in fretta.', 38134),
(201940, 'itIT', 'In questa brillante esibizione, uno dei nostri attori si trasformerà davanti ai vostri occhi in un temibile nemico di un altro regno, pronto a lottare con chiunque abbia abbastanza coraggio da sfidarlo!', '', 38134),
(201939, 'itIT', '', 'Aspetta che la belva sia passata.', 38134),
(201938, 'itIT', 'Ricordate, è grazie al sostegno dei nostri mecenati e degli spettatori come voi che abbiamo abbastanza Animum per portare avanti le arti creative! E ora, senza ulteriori indugi...', '', 38134),
(201937, 'itIT', 'Governando dalle profondità dei mari, la Regina Azshara, un tempo meravigliosa, ha assalito i suoi nemici! Dopo averli attirati nel suo palazzo, ha usato dei raffinati sotterfugi e un numero impressionante di tentacoli per colpirli! Vai così, Regina!', '', 38134),
(201936, 'itIT', 'Fa\' piano! È in fin di vita...', '', 38134),
(201935, 'itIT', 'Attenzione, compagni amanti delle arti! Con il generoso patrocinio di $n, sono lieto di annunciare che un nuovo spettacolo inizierà a breve nell\'Anfiteatro del Lago Stellare!', '', 38134),
(201933, 'itIT', '', 'Non allontanarti. Non voglio attirare l\'attenzione combattendo.', 38134),
(201932, 'itIT', '', 'Abbattilo, presto! Non possiamo perderla!', 38134),
(201931, 'itIT', '', 'Dobbiamo portare il Semebrado di Ysera dalla Regina, è la sua unica speranza!', 38134),
(201930, 'itIT', '', 'Mi riunirò agli altri Occhi. Fa\' in fretta.', 38134),
(201928, 'itIT', 'Oh, che giorno glorioso per uno spunto! Il cuore canta la canzone della creatività! Le mie ali si agitano dalle possibilità artistiche che scorrono nella mia mente! È davvero un miracolo avere abbastanza Animum da potermi permettere di creare ancora una volta.$b$bHo faticato a lungo, e ora di aver creato uno spettacolo che scuoterà le radici di Selvarden. Ti andrebbe di vedere il potere della mia passione?', '', 38134),
(201926, 'itIT', 'Ahimè, che pena!$b$BAh, $gamico mio:amica mia;. Ho fame. Non di cibo o bevande, almeno non del tipo che tu conosci. Bramo il cibo dello spirito! Le arti creative, che ci portano a vette così alte che persino i Silfidi più agguerriti non possono raggiungere!$b$BOh, ma la siccità ha soffocato la nostra creatività... e anzi, dobbiamo affannarci per sopravvivere.$B$BMa dimmi, vale davvero vivere una vita senza arte?', '', 38134),
(201925, 'itIT', 'Il tempo scorre! Muoversi!', '', 38134),
(201924, 'itIT', '', 'Al momento, la velocità è più importante della furtività. Voliamo alla pietra di transito e scappiamo dalla Fauce.', 38134),
(201923, 'itIT', 'Coprimi!', '', 38134),
(201922, 'itIT', 'Ho piazzato la carica! Indietro!', '', 38134),
(201921, 'itIT', 'È tempo di far casino!', '', 38134),
(201920, 'itIT', 'Questo sì che si sentirà!', '', 38134),
(201905, 'itIT', '', 'Dobbiamo tutti ricordare che il vero scopo di Revendreth è la redenzione.', 38134),
(201904, 'itIT', 'Mettete al sicuro le anime rimanenti. Le aiuteremo noi, se riusciremo.', '', 38134),
(201903, 'itIT', 'Ritiratevi con tutte le anime che riuscite a controllare!', 'Ritiratevi con tutte le anime che riuscite a controllare!', 38134),
(201902, 'itIT', '', 'Risparmiate le anime, se potete. Potremmo riuscire a riabilitarle!', 38134),
(201901, 'itIT', '', 'La depravazione del Maestro non conosce confini!', 38134),
(201900, 'itIT', 'Queste anime sono state corrotte dal Maestro e trasformate in armi!', '', 38134),
(201899, 'itIT', 'Vero è che non avrei scelto del fango profumato come decorazione...', '', 38134),
(201898, 'itIT', 'Cos\'hanno fatto? Questo posto è lurido!', '', 38134),
(201897, 'itIT', 'Basta casini! Si salvi chi può!', '', 38134),
(201896, 'itIT', 'Calcafauce, portami fuori di qui, e qualcuno mi procuri uno straccio!', '', 38134),
(201895, 'itIT', 'Quei Dragatori sono coperti di fango! Rovineranno le decorazioni!', '', 38134),
(201893, 'itIT', 'Seguiteli fino al perimetro. Non oltre!', '', 38134),
(201892, 'itIT', 'Che maestria! Mi assicurerò che queste poltiglie siano ricostituite al più presto.', '', 38134),
(201891, 'itIT', 'Ah, apprendista! Stiamo registrando una carenza di poltiglie. Ti andrebbe di riconvertirne alcune per noi?', '', 38134),
(201890, 'itIT', 'Ehilà! L\'Architetto della Piaga Marileth mi ha mandato qui a provare questo equipaggiamento. Stiamo raccogliendo le poltiglie viventi di quest\'area. O almeno dei campioni. Vuoi aiutarmi?', '', 38134),
(201888, 'itIT', 'Io non sono stato, ma qualcuno l\'ha fatto!', '', 38134),
(201887, 'itIT', 'Ripiegate e raggruppatevi alla rocca!', '', 38134),
(201886, 'itIT', 'È stato saggio da parte $gdel:della; Calcafauce attivare le Riserve Litogene. Verso il cielo!', '', 38134),
(201885, 'itIT', 'La Legione di Pietra! Il Maestro ha mandato le sue forze più potenti alla nostra corte?', '', 38134),
(201884, 'itIT', 'Sapete, alcuni considerano un grande onore essere degni dell\'attenzione della Legione di Pietra.', '', 38134),
(201883, 'itIT', '', 'Ottima scelta di nemici, $p...', 38134),
(201875, 'itIT', 'Questo contiene il potere per infondere la maldracite con l\'Animum. È terrorizzato. Vedi il suo sguardo?', 'Questo contiene il potere per infondere la maldracite con l\'Animum. È terrorizzato. Vedi il suo sguardo?', 38134),
(201874, 'itIT', 'Questo contiene il potere per infondere la maldracite con l\'Animum.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Maldracite Caricata]|h|r: una scheggia di Maldracite assorbe le anime dei nemici sconfitti. Quando si ha poca salute, la scheggia si infrange, respingendo indietro i nemici vicini e proteggendo con uno scudo per qualche istante.\n\nL\'effetto non può verificarsi molto spesso, ma il suo tempo di recupero si riduce ogni volta che si infligge un colpo di grazia.', 'Questo contiene il potere per infondere la maldracite con l\'Animum.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Maldracite Caricata]|h|r: una scheggia di Maldracite assorbe le anime dei nemici sconfitti. Quando si ha poca salute, la scheggia si infrange, respingendo indietro i nemici vicini e proteggendo con uno scudo per qualche istante.\n\nL\'effetto non può verificarsi molto spesso, ma il suo tempo di recupero si riduce ogni volta che si infligge un colpo di grazia.', 38134),
(201873, 'itIT', 'Attenzione alle mani! Questo contiene abilità che hanno a che fare col fuoco e il ghiaccio.', 'Attenzione alle mani! Questo contiene abilità che hanno a che fare col fuoco e il ghiaccio.', 38134),
(201872, 'itIT', 'Attenzione alle mani! Questo contiene abilità che hanno a che fare col fuoco e il ghiaccio.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Carne Artica]|h|r: Subire danni fornisce una probabilità di ottenere un accumulo di Animum Cristallizzante, riducendo i danni subiti. Al raggiungimento degli accumuli massimi, subire danni congela i nemici vicini per 5 s e ne aumenta i danni subiti.', 'Attenzione alle mani! Questo contiene abilità che hanno a che fare col fuoco e il ghiaccio.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Carne Artica]|h|r: Subire danni fornisce una probabilità di ottenere un accumulo di Animum Cristallizzante, riducendo i danni subiti. Al raggiungimento degli accumuli massimi, subire danni congela i nemici vicini per 5 s e ne aumenta i danni subiti.', 38134),
(201870, 'itIT', 'Questo contiene delle abilità di evocazione di creature morte. È una cosa disgustosa.', 'Questo contiene delle abilità di evocazione di creature morte. È una cosa disgustosa.', 38134),
(201869, 'itIT', 'Questo contiene delle abilità di evocazione di creature morte.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Comando Scheletrico]|h|r: Quando l\'effetto passivo di Forgiatura della Carne consuma l\'essenza di un cadavere, spesso verrà evocato uno scheletro che combatterà al tuo fianco.', 'Questo contiene delle abilità di evocazione di creature morte.\n\nAccettare il suo potenziamento ti fornisce la seguente abilità:\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Comando Scheletrico]|h|r: Quando l\'effetto passivo di Forgiatura della Carne consuma l\'essenza di un cadavere, spesso verrà evocato uno scheletro che combatterà al tuo fianco.', 38134),
(201868, 'itIT', '', 'Vieni, Uther. Lascia che ti porti in salvo.', 38134),
(201865, 'itIT', 'Salve, $gamico:amica;.', 'Salve, $gamico:amica;.', 38134),
(201864, 'itIT', 'Come posso esserti d\'aiuto?', 'Come posso esserti d\'aiuto?', 38134),
(201863, 'itIT', 'Cosa posso fare per te?', 'Cosa posso fare per te?', 38134),
(201861, 'itIT', '', 'Certamente, padrona. Questi servitori dell\'Arconte non sono nulla.', 38134),
(201860, 'itIT', '', 'Non deludermi, Lysonia. Dimostra di essere degna dei tuoi nuovi poteri.', 38134),
(201859, 'itIT', '', 'Questi Drust sono una tale rottura di ali! Dobbiamo difendere il bosco e il Semebrado di Ysera, altrimenti sarà stato tutto inutile!', 38134),
(201858, 'itIT', '', 'Sei preoccupato, Thrall. Di che si tratta?', 38134),
(201857, 'itIT', '', 'Se c\'è un modo di uscire dalla Fauce, allora c\'è un modo per entrare. Torneremo a prenderla.', 38134),
(201855, 'itIT', '', 'Concordo, ma non abbiamo idea di come trovarla. Faremo del nostro meglio, ma questa pietra di transito potrebbe essere la nostra unica via di fuga.', 38134),
(201853, 'itIT', 'Ancora nessun segno di Tyrande. Non possiamo lasciarla sola in questo incubo.', '', 38134),
(201851, 'itIT', 'Si è lanciata nella fenditura davanti a noi. Ha giurato di trovare Sylvanas Ventolesto... a ogni costo.', '', 38134),
(201850, 'itIT', 'Ancora nessun segno della Gran Sacerdotessa. Forse si è lanciata alla carica nel caos.', '', 38134),
(201849, 'itIT', 'Hai attraversato la fenditura, bene. Dubito che questa missione possa avere successo senza di te.', '', 38134),
(201848, 'itIT', '', 'Un grande pericolo incombe su di noi. Vieni da me e lo affronteremo insieme.', 38134),
(201844, 'itIT', '', 'Mueh\'zala assaggerà presto la mia furia. Mi fido di te, mortale...', 38134),
(201842, 'itIT', '', 'Ricordo di essere stata strappata da una foresta. Mi sono rifiutata di aiutare quel vile di Mueh\'zala... quindi mi hanno gettata qui.\n\nSe avessi la mia forza, lo farei a pezzi e disperderei i suoi resti in tutto il creato!\n\nDevo tornare in quella foresta. Potresti aiutarmi?', 38134),
(201841, 'itIT', '', 'Sia benedetta la sua voce.', 38134),
(201840, 'itIT', '', 'Kah-Delen, porta i rappresentanti delle congreghe all\'Enclave. Dopo che io e il mortale avremo finito, ci incontreremo là con te. Con la sua voce ho parlato.', 38134),
(201839, 'itIT', '', 'Allora devo rispondere alla loro chiamata. Vieni con me, Calcafauce. Forse potranno guidare i nostri passi.', 38134),
(201838, 'itIT', 'Onorevole Voce! Gli Eterni ti hanno convocata nella stanza dell\'Arbiter!', '', 38134),
(201837, 'itIT', 'Sembra che il Carceriere tiri i fili del fato anche sul nostro mondo.', '', 38134),
(201836, 'itIT', '', 'Che lo Scopo ci salvi tutti! È peggio di quanto si potesse immaginare.', 38134),
(201835, 'itIT', 'Denathrius ha causato la siccità d\'Animum e ha riposto le sue scorte nella Fauce. È in combutta col Carceriere.', '', 38134),
(201834, 'itIT', '', 'Percepisco un\'energia familiare che mi attira a ovest. Dobbiamo fermare i piani di Lysonia e del suo padrone.', 38134),
(201833, 'itIT', '', 'Non farmene pentire.', 38134),
(201832, 'itIT', '', 'Incontriamoci vicino alla Casata degli Occhi. Una missione di questo tipo richiede... delicatezza.', 38134),
(201831, 'itIT', '', 'Non possiamo sperare di mantenere il seggio senza una necropoli, dovremo prenderne una dai nostri nemici', 38134),
(201830, 'itIT', '', 'No. Tutto questo finisce ora!', 38134),
(201829, 'itIT', '', 'Invece avete scelto di rubare e distruggere. Che spreco! Mi fate quasi pena.', 38134),
(201828, 'itIT', '', 'I Drust avrebbero potuto proteggere la natura... Sorvegliare il ciclo.', 38134),
(201826, 'itIT', '', 'Uno dopo l\'altro, rivendicheremo ogni bosco. Ogni Silfo. Ogni anima.', 38134),
(201825, 'itIT', '', 'Eppure noi diventiamo forti, mentre chi s\'inginocchia dinanzi a te avvizzisce!', 38134),
(201824, 'itIT', '', 'Che creature egoiste. Non vedete niente al di là dei vostri stessi desideri.', 38134),
(201823, 'itIT', '', 'Starai lì a guardare mentre rivendicherò il tuo trono e schiaccerò il Cuore della Foresta!', 38134),
(201822, 'itIT', '', 'Non mi serve la pietà di una regina in declino.', 38134),
(201820, 'itIT', 'Caccia Selvaggia, in marcia! Per Selvarden!', '', 38134),
(201813, 'itIT', 'Chella è Grigiomanto! Na Dea Selvatica della Guerra degli Antichi!', '', 38134),
(201812, 'itIT', 'Abbiamo i nostri ordini. Combattete bene, Kyrian del Bastione.', '', 38134),
(201811, 'itIT', 'Salute, $C. Sono qui per offrire l\'aiuto della Caccia Selvaggia contro i nostri nemici.\n\nAccettare il mio aiuto ti fornisce la seguente abilità: \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Carica dei Vorkai]|h|r: Quando si è in inferiorità numerica, gli incantesimi e le abilità hanno una probabilità di evocare dei Vorkai che caricano il bersaglio, infliggendo danni da natura e stordendo i nemici sulla traiettoria.', 'Salute, $C. Sono qui per offrire l\'aiuto della Caccia Selvaggia contro i nostri nemici.\n\nAccettare il mio aiuto ti fornisce la seguente abilità: \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Carica dei Vorkai]|h|r: Quando si è in inferiorità numerica, gli incantesimi e le abilità hanno una probabilità di evocare dei Vorkai che caricano il bersaglio, infliggendo danni da natura e stordendo i nemici sulla traiettoria.', 38134),
(201810, 'itIT', 'Con il mio potenziamento, la terra stessa sconfiggerà i nemici di Selvarden.\n\nAccettare il mio aiuto ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Ira dei Tirnenn]|h|r: gli incantesimi e le abilità hanno un\'alta probabilità di creare frammenti di terra che fanno sanguinare il nemico, infliggendo danni da natura ogni secondo. Si accumula. I nemici che muoiono con il numero massimo di accumuli esplodono, infliggendo danni da natura ai nemici vicini.', 'Con il mio potenziamento, la terra stessa sconfiggerà i nemici di Selvarden.\n\nAccettare il mio aiuto ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Ira dei Tirnenn]|h|r: gli incantesimi e le abilità hanno un\'alta probabilità di creare frammenti di terra che fanno sanguinare il nemico, infliggendo danni da natura ogni secondo. Si accumula. I nemici che muoiono con il numero massimo di accumuli esplodono, infliggendo danni da natura ai nemici vicini.', 38134),
(201809, 'itIT', 'Sono qui per aiutare chi cammina, corre o trotta a imparare a stare in piedi con leggerezza, $R.\n\nAccettare il mio aiuto ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Mosse dell\'Ingannatore]|h|r: Se lanciata quando non si è in combattimento, Animorfosi impedisce ai nemici di notarti.', 'Sono qui per aiutare chi cammina, corre o trotta a imparare a stare in piedi con leggerezza, $R.\n\nAccettare il mio aiuto ti fornisce la seguente abilità:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Mosse dell\'Ingannatore]|h|r: Se lanciata quando non si è in combattimento, Animorfosi impedisce ai nemici di notarti.', 38134),
(201802, 'itIT', 'Allora tieniti stretti chei legami e nun manifestare tutto lo spirito tuo in chel posto oscuro. Se no l\'anima tua farà PUF!', '', 38134),
(201789, 'itIT', 'Riferiscilo alla Regina dell\'Inverno. Lo vorrà sapere.', '', 38134),
(201788, 'itIT', '', 'Purtroppo, non c\'è niente da fare per loro. Gorak Zhar la pagherà cara per la ferita che ha osato infliggere al mio regno.', 38134),
(201787, 'itIT', '', 'Brutte notizie, mortale. Speravo che riuscissimo a scoprire qualcosa per recuperare le creature possedute dai Drust.', 38134),
(201779, 'itIT', 'Con il mio potenziamento, la terra stessa sconfiggerà i nemici di Selvarden.', 'Con il mio potenziamento, la terra stessa sconfiggerà i nemici di Selvarden.', 38134),
(201774, 'itIT', 'Sono qui per aiutare chi cammina, corre o trotta a imparare a stare in piedi con leggerezza, $R.', 'Sono qui per aiutare chi cammina, corre o trotta a imparare a stare in piedi con leggerezza, $R.', 38134),
(201773, 'itIT', 'Bel combattimento! Ora, al lavoro.', '', 38134),
(201772, 'itIT', 'Due contro uno? Si mette male... per voi!', '', 38134),
(201771, 'itIT', 'Qui ci penso io. Va\'!', '', 38134),
(201770, 'itIT', '', 'Ecco fatto, difenderò questa posizione.', 38134),
(201766, 'itIT', '', 'Ci penso io ora, muoviti!', 38134),
(201765, 'itIT', '', 'Brillardente? Gerwyn? Non anche voi...', 38134),
(201764, 'itIT', '', 'Presto, aiutami a entrare in comunione con l\'ancora!', 38134),
(201763, 'itIT', '', 'Aiutami a rivendicare l\'ancora.', 38134),
(201762, 'itIT', '', 'Ti ridurrò a brandelli!', 38134),
(201747, 'itIT', 'Sono contento. Un\'uscita di ricerca è proprio quello che serve a Kevin e sono certo che i nostri colleghi apprezzeranno la nostra premura nel portar loro qualche poltiglia!', '', 38134),
(201744, 'itIT', '', 'Perdonami, devo riposare un istante.', 38134),
(201741, 'itIT', '', 'Ho rintracciato Lysonia oltre questi cancelli. La mia ricognizione iniziale è stata... preoccupante.', 38134),
(201737, 'itIT', 'Oh, doppio accidenti, aiuto! C\'è qualcuno che può sentirmi?', '', 38134),
(201730, 'itIT', 'Andare a una festa con amici! Non c\'è niente di meglio.', '', 38134),
(201727, 'itIT', 'Non possiamo procedere senza il supporto dei Litogeni.', '', 38134),
(201726, 'itIT', 'I Lich di Kel\'Thuzad le hanno mutate in pietra.', '', 38134),
(201724, 'itIT', 'Le ossa tue decoreranno il trono di Mueh\'zala!', '', 38134),
(201722, 'itIT', 'Schiacciate chiunque si opponga a Mueh\'zala!', '', 38134),
(201721, 'itIT', 'Mo\' basta! Invoco i morti di Bragomorto!', '', 38134),
(201720, 'itIT', 'Salute, $C. Sono qui per offrire l\'aiuto della Caccia Selvaggia contro i nostri nemici.', 'Salute, $C. Sono qui per offrire l\'aiuto della Caccia Selvaggia contro i nostri nemici.', 38134),
(201718, 'itIT', 'Mueh\'zala... vince... sempre...', '', 38134),
(201717, 'itIT', 'Mueh\'zala... ti maledica...', '', 38134),
(201716, 'itIT', 'Non hai... speranze...', '', 38134),
(201715, 'itIT', 'Mueh\'zala... risorgerà...', '', 38134),
(201713, 'itIT', 'Non... di nuovo...', '', 38134),
(201708, 'itIT', 'Non sei niente per Mueh\'zala!', '', 38134),
(201705, 'itIT', 'Cadrai con Bwonsamdi!', '', 38134),
(201704, 'itIT', 'Nessuno può tradire Mueh\'zala!', '', 38134),
(201703, 'itIT', 'Mueh\'zala regnerà ancora!', '', 38134),
(201701, 'itIT', 'Libereremo il maestro!', '', 38134),
(201695, 'itIT', 'La Pietra di Transito ha funzionato! Che luogo... notevole!', '', 38134),
(201694, 'itIT', 'Starò vicino. Che la Madre Terra ci guidi.', '', 38134),
(201693, 'itIT', 'La Pietra di Transito! Spero che la tua presenza mi permetta di usarla per viaggiare.', '', 38134),
(201689, 'itIT', 'Grazie, grazie mille per avermi protetto. Quegli Spriggan furbetti sono dei combinaguai!', '', 38134),
(201637, 'itIT', 'Io ho tutti li Zandalari a venerarmi. Senza il Carceriere, saresti solo nu povero Loa dimenticato. Povero piccolo Mueh\'zala!', '', 38134),
(201636, 'itIT', '', 'Incontriamoci al mio antro. Farò tutto il possibile per aiutarvi.', 38134),
(201635, 'itIT', 'Le forze mie stanno ccà per liberarmi... e poi te ne pentirai assai.', '', 38134),
(201634, 'itIT', '', 'Xandria attende il nostro arrivo a Zerekriss. Muoviamoci.', 38134),
(201631, 'itIT', 'Oh cielo, qualcosa di strano sta accadendo con l\'acqua.', '', 38134),
(201630, 'itIT', 'Che piacere rivederti, $n. Bwonsamdi sa di poter contare su di te.\n\nAbbiamo da faticare assai per fermare le forze di Mueh\'zala.', '', 38134),
(201629, 'itIT', 'Volerò in cielo e perlustrerò dall\'alto.', '', 38134),
(201628, 'itIT', 'Nessuno oltrepassa queste mura senza che io lo sappia. Non uscirete vivi da qui!', '', 38134),
(201627, 'itIT', 'Credete di essere furbi? Costruire uno specchio per entrare nel mio regno?', '', 38134),
(201626, 'itIT', '', 'Ehilà, sono Sbocciasalici. Non è tremendo quello che sta accadendo a Selvarden? \n\n<Si avvicina verso di te e abbassa la voce.>\n\nTi prego non dirglielo, ma al momento sto preparando la mia vendetta contro Dama Baccaluna. Ci facciamo sempre degli scherzi a vicenda, ma questa volta sarà lei a cascarci!\n\nHo un\'idea, ma per farcela devo trovare un modo per andare dietro le quinte dell\'anfiteatro. Forse potresti aiutarmi.', 38134),
(201622, 'itIT', 'Questo clarino farà segno alla Spada d\'Ebano di convergere sul tuo nemico. Usalo con saggezza.', '', 38134),
(201620, 'itIT', 'Farò da scorta, mio Principe. Vieni, Calcafauce.', '', 38134),
(201619, 'itIT', 'La trappola metterà in sicurezza da cadute di qualsiasi altezza. Nel caso pensassi sia importante.', '', 38134),
(201618, 'itIT', 'Buttali fuori dalla festa!', '', 38134),
(201617, 'itIT', 'La trappola immobilizzerà i ladri, così potrai portarli in un luogo più... adatto.', '', 38134),
(201615, 'itIT', 'Non sono affatto sorpreso. Mi aspettavo che dei ladri avrebbero tentato di rubare le nostre merci esotiche.\n\nIl Cartello Ta ha sviluppato una tecnologia antifurto eccezionalmente efficace per situazioni come questa.', '', 38134),
(201613, 'itIT', 'Calcafauce! Ci servi quaggiù!', '', 38134),
(201612, 'itIT', 'Allora perché quel Dragatore sta scassinando la gabbia dietro di te?', '', 38134),
(201611, 'itIT', 'Non serve. Non ci sono ladri qui!', '', 38134),
(201610, 'itIT', '', 'Venti taglienti diventano brezza leggera. Segui gli altri. Aiuta il bosco.', 38134),
(201609, 'itIT', 'Ne ho abbastanza di questo posto. Andiamo.', '', 38134),
(201608, 'itIT', 'Finalmente... il tormento è finito...', '', 38134),
(201604, 'itIT', 'Sta\' indietro! Vincoleranno anche te!', '', 38134),
(201603, 'itIT', 'Oh! Una festa è quello che mi serve per recuperare le forze. Così potrò tornare a combattere!', '', 38134),
(201602, 'itIT', 'Per un costo esiguo, ti mostrerò come si occupa dei ladri il Cartello Ta.', '', 38134),
(201600, 'itIT', 'Sì, quando va Chufa andrò anche io. Dopo il mio pisolino.', '', 38134),
(201599, 'itIT', 'Oblio del Peccato, a me! Oggi marciamo su Maldraxxus!', '', 38134),
(201598, 'itIT', 'Il nostro popolo è stato massacrato, il Pozzo Solare dissacrato, tutto per resuscitare un Lich nella non morte.', '', 38134),
(201595, 'itIT', 'Grazie. Niente di tutto questo sarebbe stato possibile senza di te.', '', 38134),
(201594, 'itIT', 'Mi conforta sapere che Sbocciasalici non sia svanita e vivrà per sempre in ogni nuovo Asceso.', '', 38134),
(201593, 'itIT', 'Piazza qui il ricettacolo.', '', 38134),
(201591, 'itIT', 'Vieni, incontriamo Pelagos presso l\'Emblema.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201590, 'itIT', 'Sono mortificato nell\'apprendere del nobile sacrificio di Selvarden a nostro favore.', '', 38134),
(201589, 'itIT', 'Alt! Cosa c\'è più avanti?', '', 38134),
(201588, 'itIT', 'È davvero formidabile che l\'Ammiraglio Supremo abbia sconfitto una di queste mostruosità.', '', 38134),
(201587, 'itIT', 'Non sarebbe saggio spezzare l\'incantesimo. Proseguiamo.', '', 38134),
(201586, 'itIT', 'Altre tracce dell\'operato di Dama Marefiero.', '', 38134),
(201585, 'itIT', 'Il conflitto sembra dirigersi in questa direzione. Dovremmo seguire le tracce prima che svaniscano.', '', 38134),
(201584, 'itIT', 'Magia del gelo. Sembra opera della nostra amica, l\'Ammiraglio Supremo.', '', 38134),
(201582, 'itIT', '', 'Ara\'lon si sente responsabile per una sfortuna. Tempo che la colpa possa sopraffarlo.', 38134),
(201581, 'itIT', 'Kivarr risiede in un antro vicino al Bosco del Vespro. Ci rivedremo lì.', '', 38134),
(201580, 'itIT', 'Troveremo il modo di salvarti, Gweyir. Te lo prometto...', '', 38134),
(201579, 'itIT', '', 'Riposa per adesso... nonostante i suoi sogni non offrano asilo.', 38134),
(201578, 'itIT', 'Gorak Zhar e i suoi scagnozzi devono essere fermati. Trova Kivarr nelle Terretetre e metti fine a questa maledizione.', '', 38134),
(201577, 'itIT', '', 'Gol Inath sarà la tua tomba!', 38134),
(201576, 'itIT', '', 'Miserabile mortale. Quello stolto Cantaspine ti ha condotto tra le mie grinfie.', 38134),
(201575, 'itIT', 'Gorak Tul sarà morto, ma la sua presenza oscura ancora permane tra queste mura.', '', 38134),
(201574, 'itIT', 'Posiziona il feticcio nel calderone a Gol Inath, ma fa\' attenzione...', '', 38134),
(201573, 'itIT', '', 'Se esiste un modo per spezzare la maledizione dei Drust, Ulfar lo conosco. Raggiungiamolo.', 38134),
(201572, 'itIT', '', 'C\'è ancora speranza per la giovane cacciatrice...', 38134),
(201571, 'itIT', 'Fai rapporto a Ser Herne. Io porterò Gweyir al sicuro.', '', 38134),
(201570, 'itIT', 'Indossa la maschera quando vuoi. Dobbiamo trovare gli altri.', '', 38134),
(201569, 'itIT', 'Serviranno dei travestimenti. Perlustra la zona in cerca di qualcosa che possiamo usare.', '', 38134),
(201567, 'itIT', 'Non possiamo permetterci di perdere altri cacciatori e cacciatrici. Trovali e portali a casa.', '', 38134),
(201558, 'itIT', 'Ti prego di porgere i miei ringraziamenti per l\'invito al Principe Renathal. Parteciperò sicuramente alla Corte delle Braci.', '', 38134),
(201557, 'itIT', 'Come desidera, Eletto. Supererò il mio disagio.', '', 38134),
(201556, 'itIT', 'Molto probabile. Sono felice che tu abbia questa opportunità per migliorarti servendo la volontà dell\'Arconte.', '', 38134),
(201555, 'itIT', 'No, ovviamente no. Ma certamente ci sono altri più adatti a queste frivolezze, no?', '', 38134),
(201554, 'itIT', 'L\'Arbiter è la chiave finale. Proteggila o tutto sarà perduto.', '', 38134),
(201553, 'itIT', 'Porta il mio monito all\'Arconte, alla Regina dell\'Inverno e al Sire. Dovranno provvedere alle difese. Non lasciare che Zovaal raggiunga il sepolcro.', '', 38134),
(201552, 'itIT', 'C\'è una sola speranza per salvare le Terretetre. Gli Eterni dovranno ergersi nuovamente uniti, prima che il Carceriere fugga dalla Fauce.', '', 38134),
(201551, 'itIT', 'Il fatto che tu stia ascoltando questo messaggio indica che i miei sospetti erano fondati. Zovaal ha fatto delle sue catene un\'arma... e ha portato alla mia sconfitta.', '', 38134),
(201549, 'itIT', 'Siamo i Prescelti. Contiamo sulla nostra forza. Dobbiamo radunare i nostri soldati migliori... e il nostro Barone scomparso.', '', 38134),
(201548, 'itIT', 'Ora temo che Zovaal non abbia agito da solo. Sospetto che abbia alleati antichi... e cercherà di reclutare altri per la sua causa.', '', 38134),
(201547, 'itIT', 'Secoli fa, gli Eterni punirono nostro fratello Zovaal per il suo tradimento. Fu rilegato nell\'ineluttabile Fauce, per esserne per sempre il Carceriere.', '', 38134),
(201545, 'itIT', 'Io sono il Primus. La tua presenza nel mio santuario implica che l\'oscurità è caduta su Maldraxxus... e su tutti i regni della Morte.', '', 38134),
(201544, 'itIT', 'Ah, Adrestes. Ho sentito che ti ritieni troppo importante per accettare l\'invito del Principe Renathal? Oppure ho travisato la situazione?', '', 38134),
(201542, 'itIT', 'Per imparare serve pazienza.\n\nPer cambiare prospettiva serve conoscenza.\n\nPer scegliere serve intuizione.\n\nPer comprendere le profonde verità serve giudizio.\n\nQuesta è la via della saggezza.', 'Per imparare serve pazienza.\n\nPer cambiare prospettiva serve conoscenza.\n\nPer scegliere serve intuizione.\n\nPer comprendere le profonde verità serve giudizio.\n\nQuesta è la via della saggezza.', 38134),
(201538, 'itIT', 'Sarei onorato di partecipare. Tuttavia, il Polemarco Adrestes è l\'ospite previsto.', '', 38134),
(201536, 'itIT', 'Non c\'è vergogna nel chiedere aiuto a qualcuno.', '', 38134),
(201535, 'itIT', 'Grazie, $p! Non ho granché da offrirti, ma ti prego di accettare questo dono dello Sguardo della Luna come ricompensa per ciò che hai fatto. Con i miei strumenti di nuovo in mano mia potrò tornare a lavorare sul giardino.', 'Grazie, $p! Non ho granché da offrirti, ma ti prego di accettare questo dono dello Sguardo della Luna come ricompensa per ciò che hai fatto. Con i miei strumenti di nuovo in mano mia potrò tornare a lavorare sul giardino.', 38134),
(201534, 'itIT', '', 'Anche se a caro prezzo, l\'equilibrio dev\'essere ripristinato. Ti ringrazio.', 38134),
(201531, 'itIT', 'Sembra che abbia perso il cestino, il flauto, il martello e la bacchetta. Oh, e il diario! La storia del giardino andrà perduta se gli Spriggan lo distruggeranno...\n\nNon so come potrò ricompensare la tua gentilezza, ma se riuscirai a trovare le mie cose te ne sarò eternamente grato.', 'Sembra che abbia perso il cestino, il flauto, il martello e la bacchetta. Oh, e il diario! La storia del giardino andrà perduta se gli Spriggan lo distruggeranno...\n\nNon so come potrò ricompensare la tua gentilezza, ma se riuscirai a trovare le mie cose te ne sarò eternamente grata.', 38134),
(201530, 'itIT', 'Davvero? Sembri $gun:una; potente $PC, ma sarebbe pericoloso e non sopporterei che ti accadesse qualcosa.', 'Davvero? Sembri $gun:una; potente $PC, ma sarebbe pericoloso e non sopporterei che ti accadesse qualcosa.', 38134),
(201529, 'itIT', 'A presto!', '', 38134),
(201526, 'itIT', 'È colpa mia se Gweyir è stata catturata e posseduta dai Drust...', '', 38134),
(201525, 'itIT', '\"Per comprendere le profonde verità serve giudizio.\"\n\nSo di aver svelato la via, ma qualcosa non va bene. La vera risposta mi deve star sfuggendo.', '\"Per comprendere le profonde verità serve giudizio.\"\n\nSo di aver svelato la via, ma qualcosa non va bene. La vera risposta mi deve star sfuggendo.', 38134),
(201524, 'itIT', '\"Per scegliere serve intuizione.\"\n\nDevo meditare ulteriormente.', '\"Per scegliere serve intuizione.\"\n\nDevo meditare ulteriormente', 38134),
(201523, 'itIT', '\"Per cambiare prospettiva serve conoscenza.\"\n\nCosa dovrei vedere?', '\"Per cambiare prospettiva serve conoscenza.\"\n\nCosa dovrei vedere?', 38134),
(201522, 'itIT', '\"Per imparare serve pazienza.\"\n\nHo bisogno di fermarmi e riflettere. La fretta non porta da nessuna parte.', '\"Per imparare serve pazienza.\"\n\nHo bisogno di fermarmi e riflettere. La fretta non porta da nessuna parte.', 38134),
(201521, 'itIT', 'AAARRRGH!', '', 38134),
(201520, 'itIT', 'Elementi... ascoltatemi. Datemi la forza per...', '', 38134),
(201519, 'itIT', '', 'Quante volte devo sconfiggerti?', 38134),
(201518, 'itIT', '', 'Ne ho abbastanza!', 38134),
(201513, 'itIT', 'I nostri occhi sono abituati alla luce lunare, che rivela molti segreti nascosti al mondo.\n\nLo Sguardo della Luna ti permette di vedere con gli occhi di un Silfide.', 'I nostri occhi sono abituati alla luce lunare, che rivela molti segreti nascosti al mondo.\n\nLo Sguardo della Luna ti permette di vedere con gli occhi di un Silfide.', 38134),
(201512, 'itIT', 'Oh, apprendista! Eccoti qua!', '', 38134),
(201511, 'itIT', '', 'Questi Silfi mascherati hanno attaccato il bosco all\'improvviso. Dobbiamo respingerli!', 38134),
(201510, 'itIT', '', 'Capisco l\'importanza di un assalto coordinato, quindi parteciperò a questa Corte delle Braci e rappresenterò la Caccia Selvaggia ai dibattiti.', 38134),
(201509, 'itIT', '', 'Sa che siete qui...', 38134),
(201508, 'itIT', '', '<L\'espressione di Brilliola si incupisce.>\n\nPerdere un albero è una tragedia terribile. Quando non c\'è abbastanza Animum per l\'intera foresta, la Regina sceglie di sacrificare il singolo per salvare gli altri.\n\nUna scelta terribile, ma necessaria.\n\nQuando un albero muore, tutti i suoi Semebrado non possono più essere nutriti. Alcuni possono essere portati in un altro bosco, ma gli altri...\n\nE come se non bastasse, alcuni dicono che nei boschi morti la barriera tra Selvarden e gli altri piani sia più debole.\n\nDicono che in quei luoghi siamo più vulnerabili ad attacchi esterni.', 38134),
(201507, 'itIT', '', 'Oh sì! I Tirnenn sono creature arboree meravigliose, alte e senzienti.\n\nSono in qualche modo correlate agli alberi di Selvarden, ma sono così antiche e bizzarre che non conosciamo esattamente la natura del loro legame.\n\nAlcune di loro riescono persino a sentire parlare gli alberi, anche se comunicano più tramite il canto che la parola, da quanto ho capito.\n\nÈ affascinante!', 38134),
(201505, 'itIT', '', 'Sì! Ogni albero ha un nome!\n\nIl nostro albero nell\'Antro del Letargo si chiama Tirna Glayn. A nord, il bosco di Tirna Vaal prende il nome dal suo albero.\n\nTirna Noch, l\'albero caduto, è morto all\'inizio della siccità, mentre Tirna Rowen, nella Foresta di Cuordilegno, è deceduto di recente.\n\nAlcuni altri alberi ancora in vita sono Tirna Kaithe, il bosco del Ciglio dell\'Artiglio.\n\nTirna Eas è quello del Bacino Scintillante. La mia casa, Tirna Fenn, è nell\'Altopiano Cantonirico!\n\nAnche altri alberi, tuttavia, sono caduti, come Tirna Marwol, a Poggioscuro.\n\nE ovviamente c\'è Tirna Falcis, ma non oso andarci, è troppo pericoloso!', 38134),
(201500, 'itIT', '', 'Salute. Cosa desideri sapere sui nostri alberi?', 38134),
(201490, 'itIT', '', 'Grazie infinite. Ti devo... tutto.', 38134),
(201489, 'itIT', '', 'Parole sagge, Gran Signore. Mi fido del tuo giudizio.', 38134),
(201488, 'itIT', 'Con tutto il rispetto, Dama Marefiero, devi recuperare le forze. Lascia che ci occupiamo noi della ricerca degli altri.', '', 38134),
(201487, 'itIT', '', 'Io... capisco. Cosa sai di Thrall e Anduin? Dobbiamo tornare a Torgast e...', 38134),
(201486, 'itIT', 'È bello vederti al sicuro, Jaina. Anche se le mie ferite si rimarginano... gli orrori della Fauce mi tormentano ancora.', '', 38134),
(201485, 'itIT', '', 'Baine! Grazie alla Luce, sei vivo!', 38134),
(201477, 'itIT', '', 'Dobbiamo concentrarci. Lo Scopo sarà raggiunto.', 38134),
(201476, 'itIT', 'Vyraz, Draka. Conducete l\'anima mortale al Seggio del Primus. Vediamo cosa succede.', '', 38134),
(201475, 'itIT', 'Prima porti la guerra, ora una Lama del Primus. La tua leggenda cresce, mortale!', '', 38134),
(201474, 'itIT', 'Spero che quest\'impresa abbia successo. È rimasto poco Animum disponibile.', '', 38134),
(201473, 'itIT', 'Anduin... un\'arma? Cosa voleva dire?', '', 38134),
(201464, 'itIT', 'Sono ancora in vita dopotutto, ma nella confusione ho perso alcune delle mie cose.\n\nGli Spriggan distruggeranno i miei attrezzi, ne sono sicuro!\n\nTemo di non poter tornare a recuperarli, il giardino resterà incolto…', 'Sono ancora in vita dopotutto, ma nella confusione ho perso alcune delle mie cose.\n\nGli Spriggan distruggeranno i miei attrezzi, ne sono sicura!\n\nTemo di non poter tornare a recuperarli, il giardino resterà incolto…', 38134),
(201462, 'itIT', 'Io c\'ero nel Giardino della Notte quando gli Spriggan hanno attaccato. Intere ondate si sono riversate nel giardino per terrorizzarci tutti...', 'Io c\'ero nel Giardino della Notte quando gli Spriggan hanno attaccato. Intere ondate si sono riversate nel giardino per terrorizzarci tutti...', 38134),
(201454, 'itIT', 'E ottenere giustizia per i miei compagni caduti.', '', 38134),
(201443, 'itIT', 'Che questa corona rappresenti tutto ciò in cui crede Oblio del Peccato. Revendreth sarà unita e si redimerà dai peccati di Denathrius.', '', 38134),
(201427, 'itIT', '', 'La notizia dell\'attacco si sparge in fretta. Mi unirò a voi.', 38134),
(201424, 'itIT', 'Kah-Delen, ci è stato affidato un compito solenne.', '', 38134),
(201422, 'itIT', 'Subito, Onorevole Voce.', '', 38134),
(201421, 'itIT', '', 'La via per Maldraxxus dev\'essere aperta. Raduna Kah-Delen e occupatene.', 38134),
(201409, 'itIT', '', 'A meno che... questo non sia solo un trucco crudele.', 38134),
(201408, 'itIT', '', 'Se sintonizzo il mio incantesimo di teletrasporto con la tua abilità di lasciare la Fauce, posso finalmente liberarmi di questo posto!', 38134),
(201407, 'itIT', '', 'Aspetta. Sei reale, non è vero? Sì... Ti ho visto usare la Pietra di Transito per fuggire. Sembra siano passati secoli...', 38134),
(201396, 'itIT', '', 'La Regina esige l\'azione, dunque rispondiamo.', 38134),
(201395, 'itIT', '', 'Sei la tempesta purificatrice in una landa polverosa.', 38134),
(201394, 'itIT', '', 'I rami sferzano e la foresta ulula!', 38134),
(201392, 'itIT', 'Eletta... ti ho... delusa...', '', 38134),
(201387, 'itIT', '', 'Dobbiamo restare uniti se vogliamo sopravvivere. Non cediamo il passo!', 38134),
(201383, 'itIT', '', 'È l\'Eletto! Sta combattendo Lysonia da solo. Dove sono i suoi discepoli?', 38134),
(201366, 'itIT', 'Ho mandato Kleia più avanti per inseguire Lysonia. Va\' da lei al Tempio dell\'Umiltà, presto.', '', 38134),
(201360, 'itIT', '', 'I Necrosignori sono dei maestri nell\'arte bellica, è vero, ma hanno sempre usato il loro potere per difendere le Terretetre, non per attaccarle.', 38134),
(201359, 'itIT', 'Pessime notizie. Se i regni della Morte verranno trascinati in guerra, il potere della Fauce continuerà a crescere incontrollato.', '', 38134),
(201347, 'itIT', 'Agli ordini, Baronessa.', '', 38134),
(201345, 'itIT', 'Hipokos scuote la testa e poi indica dove dovrebbe essere la sua lingua.', '', 38134),
(201343, 'itIT', 'Hipokos! Cosa... ti hanno fatto? Riesci a parlare?', '', 38134),
(201337, 'itIT', '', 'Faremo rapporto a Krexus. Mantieni la posizione fino al nostro ritorno.', 38134),
(201333, 'itIT', '', 'Melmalercia è al sicuro, ti sta aspettando a Tirna Vaal.', 38134),
(201332, 'itIT', 'Kota, sei riuscita a trovare Melmalercia? L\'ho perso!', '', 38134),
(201331, 'itIT', '', 'Ah!', 38134),
(201330, 'itIT', '', 'Non permettere che questo ti turbi, giovane. Potrebbe esserci ancora speranza per salvare Tyrande Soffiabrezza.', 38134),
(201329, 'itIT', '', 'Ysera... Non ho potuto portare indietro Tyrande. Vaga ancora nella Fauce, alimentata dal potere della Guerriera della Notte... Una rabbia implacabile la guida.', 38134),
(201328, 'itIT', '', 'Qui troveranno pace e sollievo. Alcune potrebbero anche scegliere una nuova forma e prendersi cura dei boschi.', 38134),
(201327, 'itIT', '', 'Cosa accadrà a queste anime?', 38134),
(201325, 'itIT', '', 'Il mio spirito era quasi perduto. Il tocco della Regina dell\'Inverno mi ha risvegliata. Ora sono legata a questo regno.', 38134),
(201324, 'itIT', '', 'Il demone è vulnerabile! Abbattilo!', 38134),
(201323, 'itIT', '', 'Per Elune! Puoi portare in salvo queste anime!', 38134),
(201322, 'itIT', '', 'Ti prego, Calcafauce! Usa il cristallo che hai con te per salvare gli spiriti di questi Kaldorei!', 38134),
(201321, 'itIT', '', 'Almeno queste anime dei Kaldorei sono state liberate. Per la prima volta da tempo, sento nascere... speranza.', 38134),
(201295, 'itIT', 'Pensi di essere tanto $gcoraggioso:coraggiosa;, Aspirante?\n\nVorrei che me lo dimostrassi.', 'Pensi di essere tanto $gcoraggioso:coraggiosa;, Aspirante?\n\nVorrei che me lo dimostrassi.', 38134),
(201292, 'itIT', 'Io sono... eterno.', '', 38134),
(201291, 'itIT', 'La tua arroganza non conosce confini, mortale!', '', 38134),
(201289, 'itIT', 'Non possiamo perderti... nell\'oscurità.', '', 38134),
(201287, 'itIT', '', 'Sha\'lor! Per la grazia della Regina, è bello vederti.', 38134),
(201286, 'itIT', 'Uff! Quella sì che era una via d\'uscita.', '', 38134),
(201284, 'itIT', 'Non ci gioverebbe se la nostra casata cadesse per prima in battaglia. Dobbiamo restare concentrati sulla guerra.', '', 38134),
(201279, 'itIT', 'Fa\' rapporto a Punta di Lancia, recluta. Abbiamo una battaglia da vincere!', '', 38134),
(201278, 'itIT', 'Guarda! La barriera attorno al tempio è scomparsa. Ottimo lavoro.', '', 38134),
(201277, 'itIT', 'Credo di aver trovato un\'uscita, ma non andrò da quella parte! Aiuto!', '', 38134),
(201276, 'itIT', 'Andrò avanti in ricognizione. Non indugiare, recluta!', '', 38134),
(201272, 'itIT', 'E se avessero ragione? E se... E se...', 'E se avessero ragione? E se... E se...', 38134),
(201271, 'itIT', '', 'Mentre io mi vendicherò su chi le ha portate qui!', 38134),
(201270, 'itIT', '', 'Se quel che dici è vero, allora affido questo sacro compito a te. Salva le anime della mia gente.', 38134),
(201269, 'itIT', '', 'Se quel che dici è vero, allora affido questo sacro compito a te, discendente di Teldrassil. Salva le anime della nostra gente.', 38134),
(201268, 'itIT', '', 'Il Calcafauce è in grado di liberare i nostri fratelli e sorelle dal loro tormento.', 38134),
(201267, 'itIT', '', 'No! La mia caccia non finirà finché ogni anima non verrà salvata... E la Banshee risponderà dei suoi crimini!', 38134),
(201266, 'itIT', '', 'Mentre io otterrò vendetta per i Kaldorei.', 38134),
(201265, 'itIT', '', 'Così sia. Assicurati che faccia ammenda per i crimini del suo popolo.', 38134),
(201264, 'itIT', '', 'Non devi fidarti di questa creatura... Ma ti chiedo di fidarti di me.', 38134),
(201263, 'itIT', '', 'Condivido le tue perplessità... Ma ha salvato delle anime perdute, credo che possa aiutarci.', 38134),
(201262, 'itIT', '', 'D\'ora in poi dovrai trovare i materiali autonomamente. Consulta il progetto se ti servono delle indicazioni.', 38134),
(201261, 'itIT', 'Dolore... Sofferenza... Nessuna speranza...', '', 38134),
(201260, 'itIT', 'Va\'. Penserò io agli altri qui.', '', 38134),
(201259, 'itIT', '', 'Devo perlustrare i campi per trovare gli animali fuggiti.', 38134),
(201258, 'itIT', 'Chiedere a un altro Kyrian di fare da avversario nella Via è molto personale. Spero che la natura cerimoniale del nostro metodo non si perda su nessuno di voi.', '', 38134),
(201257, 'itIT', '', 'Saluti. Sarò felice di aiutarti a creare l\'esca e lo specchio.', 38134),
(201256, 'itIT', 'Dattilia ti aiuterà in qualsiasi opera d\'artigianato di cui dovessi aver bisogno per la Via.', '', 38134),
(201254, 'itIT', 'Potrai trovare tutti i componenti necessari qui al Bastione. Tuttavia, in futuro, potresti aver bisogno di viaggiare negli altri regni delle Terretetre per raccogliere materiali.', '', 38134),
(201253, 'itIT', '', 'Ho salvato tutto ciò che potevo. Che sia abbastanza.', 38134),
(201250, 'itIT', 'Al tuo servizio, Forgialita Supremo.', '', 38134),
(201249, 'itIT', 'Siamo tutti sulla stessa barca, ricordatene quando al Bastione servirà aiuto.', '', 38134),
(201248, 'itIT', 'Ti ringraziamo profondamente, Forgialita Supremo. Non ce l\'avremmo fatta senza il tuo aiuto e la tua fiducia.', '', 38134),
(201247, 'itIT', 'La corona è pronta per il tuo capo. L\'influenza Di Denathrius è scomparsa.', '', 38134),
(201246, 'itIT', '', 'Ci hai causato fin troppi problemi.', 38134),
(201245, 'itIT', '', 'Nuota libero, piccolino.', 38134),
(201235, 'itIT', 'Di sicuro la mia gente preparerà l\'esercito per una rappresaglia una volta ricevuta parola da Artemede.', '', 38134),
(201234, 'itIT', 'Ci vediamo lì. Sta\' al sicuro.', '', 38134),
(201233, 'itIT', 'Dobbiamo andare subito a Oribos.', '', 38134),
(201231, 'itIT', 'State indietro. La loro fedeltà deve ergersi da sola.', '', 38134),
(201230, 'itIT', 'Denathrius! Calcafauce, ti copriamo le spalle!', '', 38134),
(201220, 'itIT', 'Ne ho abbastanza della vostra insolenza! LA COSA. FINISCE. QUI.', '', 38134),
(201219, 'itIT', 'Osi sfidare il MIO regno?', '', 38134),
(201216, 'itIT', '', 'Hai fatto molta strada, ospite, e altre meraviglie ti attendono.\nOra riposa le tue stanche membra.', 38134),
(201215, 'itIT', 'Non c\'è tempo da perdere! Devi andare a presentare la corona. Chi è fedele alle Terretetre viene riconosciuto qui.', '', 38134),
(201210, 'itIT', 'Sei $gpronto:pronta; ad andare al tempio?', '', 38134),
(201199, 'itIT', 'Chi va là? Sei qui per aiutare?', '', 38134),
(201196, 'itIT', 'L\'intuito per anticipare i piani dei tuoi nemici.', '', 38134),
(201195, 'itIT', 'La forza per schiacciare i tuoi rivali.', '', 38134),
(201194, 'itIT', 'Allora muoviamoci. Il tempio è l\'unico modo per liberare la corona dal controllo di Denathrius.', '', 38134),
(201193, 'itIT', 'L\'astuzia per aggirare gli avversari.', '', 38134),
(201192, 'itIT', 'La barriera attorno al tempio non c\'è più.', '', 38134),
(201191, 'itIT', 'L\'ambizione per ergersi sopra gli altri.', '', 38134),
(201189, 'itIT', 'Sconfitta.', '', 38134),
(201188, 'itIT', 'È giunta la tua ora.', '', 38134),
(201187, 'itIT', 'Affronta la tua sconfitta.', '', 38134),
(201186, 'itIT', 'Hai scordato di guardare in alto.', '', 38134),
(201181, 'itIT', 'Ben fatto! Per quanto si nascondano o corrano veloce, nessuno può sfuggire alla Caccia Selvaggia!', '', 38134),
(201180, 'itIT', 'Calcafauce, alcuni dei miei cacciatori sono di pattuglia all\'Intrico Velofosco. Potrebbe fa bene al loro morale se ti unissi a loro.', '', 38134),
(201170, 'itIT', 'Non è possibile!', '', 38134),
(201160, 'itIT', '', 'Forse dovrei portare Balthazar alla Corte delle Braci...', 38134),
(201157, 'itIT', 'Oggi onoriamo Baruffo!\n\nLui lavora sodo e martella le cose.\n\nHa un\'officina vicino alla Forgia Eterna.\n\nGli porti un Puriano Maturo?', '', 38134),
(201141, 'itIT', '<L\'eco dell\'Eletto caduto ti scruta.>\n\nHai trovato il capitolo perduto della mia storia, mortale?', '<L\'eco dell\'Eletta caduta ti scruta.>\n\nHai trovato il capitolo perduto della mia storia, mortale?', 38134),
(201140, 'itIT', 'Se vuoi imparare di più, trova il tomo e torna da me.', 'Se vuoi imparare di più, trova il tomo e torna da me.', 38134),
(201137, 'itIT', 'Non sono che un\'eco della conoscenza dell\'Eletta caduta, ma vedo chiaramente in te il desiderio di conoscenza.\n\nLa mia storia è incompleta. Il capitolo finale è stato nascosto dai miei discepoli. Ti concederò la vista necessaria per trovare quel sacro tomo. Gli Indegni vogliono distorcere il mio destino per i loro scopi, ma non possono farlo con la storia completa. Lo distruggeranno, qualora dovessero trovarlo.\n\nRestituiscimelo e saprai come finisce davvero la mia storia.\n\nCosa ne dici?', 'Non sono che un\'eco della conoscenza dell\'Eletta caduta, ma vedo chiaramente in te il desiderio di conoscenza.\n\nLa mia storia è incompleta. Il capitolo finale è stato nascosto dai miei discepoli. Ti concederò la vista necessaria per trovare quel sacro tomo. Gli Indegni vogliono distorcere il mio destino per i loro scopi, ma non possono farlo con l\'intera storia. Lo distruggeranno, qualora dovessero trovarlo.\n\nRestituiscimelo e saprai come finisce davvero la mia storia.\n\nCosa ne dici?', 38134),
(201135, 'itIT', 'Vieni, Melmalercia! È tempo di mostrare il nostro coraggio!', '', 38134),
(201128, 'itIT', '', 'Sbocciasalici annuisce.', 38134),
(201124, 'itIT', 'Dovresti essere sufficiente per quel compito, Generale. Io esplorerò di sotto.', '', 38134),
(201123, 'itIT', 'Kel\'Thuzad ci provoca da lontano, ma non per molto. I suoi necromanti ci riveleranno dove si nasconde... prima o poi.', '', 38134),
(201115, 'itIT', '', 'Sbocciasalici si chiede se abbia dato le giuste informazioni a Pelagos e poi fa spallucce.', 38134),
(201114, 'itIT', '', 'Lascia che metta alla prova la tua tecnica.', 38134),
(201112, 'itIT', 'Anch\'io posso essere utile, questa protezione aumenterà le tue possibilità di sopravvivenza.', '', 38134),
(201111, 'itIT', 'Salirò in cielo per fornirti supporto.', '', 38134),
(201104, 'itIT', 'Resta solo un\'ultima operazione!', '', 38134),
(201098, 'itIT', '', 'Devi fermare il rituale di Lysonia. Non possiamo permetterle di sopraffare l\'Eletta.', 38134),
(201097, 'itIT', '', 'L\'Altura Autunnale è poco più a nord. Spero non sia troppo tardi.', 38134),
(201096, 'itIT', '', 'Dovrebbe farci guadagnare un po\' di tempo... Io starò qui e ascolterò i tuoi rapporti su cos\'è accaduto, mentre tu, mortale, indagherai sugli Indegni.', 38134),
(201095, 'itIT', '', 'Chyrus è forte. Ha scelto di rischiare la vita per attirare la sua attenzione lontano dai discepoli.', 38134),
(201094, 'itIT', '', 'L\'Eletto è vicino al tempio, sta affrontando Lysonia da solo!', 38134),
(201093, 'itIT', '', 'Questo scia di morte dovrebbe portarci da lei.', 38134),
(201091, 'itIT', '', 'Questa è opera della mia Minn\'do, proprio come ho sognato.', 38134),
(201085, 'itIT', '', 'Sin da quel giorno nessun\'anima è stata giudicata e tutte sono perdute tra gli orrori della Fauce.', 38134),
(201075, 'itIT', 'Acherus, la fortezza della Spada d\'Ebano, è stata spostata alla Rocca della Corona di Ghiaccio. Il mio Portale della Morte ci porterà là.', '', 38134),
(201074, 'itIT', 'I preparativi sono stati ultimati.', '', 38134),
(201063, 'itIT', 'La tua è una leggenda a cui voglio assistere di persona.', '', 38134),
(201062, 'itIT', 'Farò rapporto a Vyraz e poi ti raggiungerò al Seggio del Primus.', '', 38134),
(201060, 'itIT', '', 'Qualsiasi male celato nella torre stia cercando Tyrande, non dobbiamo lasciare che lo affronti da sola.', 38134),
(201059, 'itIT', '', 'Distruggerò quella torre, e con essa la Banshee!', 38134),
(201058, 'itIT', '', 'No! Non finché la nostra gente soffre a Torgast!', 38134),
(201057, 'itIT', '', 'Minn\'do! Lascia la Fauce con noi, prima che il potere della Guerriera della Notte ti sopraffaccia!', 38134),
(201056, 'itIT', 'Ora è il momento di colpire! Dobbiamo combattere contro Kel\'Thuzad.', '', 38134),
(201055, 'itIT', 'Basta con queste distrazioni! Non ci scoraggeremo!', '', 38134),
(201054, 'itIT', 'Ci vediamo là.', '', 38134),
(201053, 'itIT', 'Per aggiustare, devi raggiungere la Cittadella della Lealtà.', '', 38134),
(201052, 'itIT', 'La corona si piega alla volontà di Denathrius dalla sua influenza latente.', '', 38134),
(201045, 'itIT', 'Sto bene. Dobbiamo accedere al Tempio per sistemare la corona. È protetto da una barriera, dobbiamo disattivarla.', '', 38134),
(201044, 'itIT', 'Le mie scuse per il ritardo. Convincere il nostro nuovo alleato della mia capacità di volo è stato difficile.', '', 38134),
(201034, 'itIT', 'Sii $gil benvenuto:la benvenuta; nel nostro bosco.', 'Sii $gil benvenuto:la benvenuta; nel nostro bosco.', 38134),
(201015, 'itIT', 'È Mueh\'zala che ha portato Helya dalla parte del Carceriere... Stretto nu patto per l\'occhio di Odyn... E reso Capoguerra chella Banshee con nu sussurro!', '', 38134),
(201014, 'itIT', 'Mo\' forza, eroi! Pigliatemi il mojo e aiutatemi a mazziare chesto vecchio Loa!', '', 38134),
(201013, 'itIT', 'Credi di aver vinto, cumpà? Eheh, forse hai agito nell\'ombra per troppo tempo assai.', '', 38134),
(201012, 'itIT', 'Chisti piccirilli tuoi pensano di poterti aiutare, Bwonsamdi? Che speranza pensate di tenere contro di me?', '', 38134),
(201011, 'itIT', 'Vecchio Hakkar, nu fetente. Gli hai fatto pagare il debito suo, ma prima o poi tornerà. Nun puoi fermare nu Loa fetente!', '', 38134),
(201010, 'itIT', 'Be\' perché mi guardi così? Certo, Hakkar è nu Loa del Sangue, ma n\'accordo è n\'accordo, cumpà! Vedi solo di recuperare il mojo mio.', '', 38134),
(201009, 'itIT', 'Mo\' ecco nu Loa che potresti ricordare! A Hakkar serviva sempre del mojo per i rituali suoi... e Bwonsamdi è sempre disposto a fare un accordo.', '', 38134),
(201008, 'itIT', 'Chelli Alienatori tengono qualcosa per la capa. Meglio che li tieni d\'occhio! NNNGGG! Mo\' sbrigati col mojo mio!', '', 38134),
(201007, 'itIT', 'Chesta nun vuole ridarmi il mojo. Statt\' accuort\'... È furba...', '', 38134),
(201006, 'itIT', 'Mo\' il vecchio Bwonsamdi nun farà accordi solo su Azeroth. Anche gli Alienatori cercano potere... Tutti i tipi di potere.', '', 38134),
(201005, 'itIT', 'Nun mi servono degli adoratori! Il Carceriere mi darà tutto il potere che mi serve... e Bwonsamdi verrà dimenticato!', '', 38134),
(201004, 'itIT', 'Era molto tempo fa, ma mi ricordo. Ricordo come i Troll ti voltavano le spalle, tutti chei sacrifici... tutto quanto chel sangue... loro... NNNGGGHHH!', '', 38134),
(201003, 'itIT', 'Nun dimenticarti chi ti ha reso nu Loa, Bwonsamdi! Mo\' dammi chelle anime e inginocchiati davanti al padrone tuo!', '', 38134),
(201002, 'itIT', 'L\'unico potere che conta è la Morte! E la Morte arriverà per tutti quanti!', '', 38134),
(201001, 'itIT', 'Ti sei dimenticato cosa significhi essere nu Loa! Il legame tuo con le persone... Il potere che tieni... NNNGGG!', '', 38134),
(201000, 'itIT', 'Io voglio chelle anime, Bwonsamdi! Voglio Rastakhan e tutti quanti gli altri Zandalari! Nun è saggio opporre resistenza a Mueh\'zala!', '', 38134),
(200999, 'itIT', 'Terrò distratto chesto vecchio Loa. Andate, mo\'. Seguite il sentiero e nun accettate nu rifiuto come risposta.', '', 38134),
(200998, 'itIT', 'Mueh\'zala mi ha messo in una posizione di... svantaggio. Ma se pigliate il mojo mio da alcuni... soci in affari... Allora insieme potremo avere la meglio su di lui!', '', 38134),
(200997, 'itIT', 'Aspetta... Chi...? Eroi! Uh... Nessun rancore su chel disaccordo piccirillo che abbiamo avuto a Zuldazar, eh?', '', 38134),
(200996, 'itIT', 'Aspetta... Chi...? Eroi! Sapevo che sareste venuti ad aiutare il vecchio cumpà Bwonsamdi.', '', 38134),
(200995, 'itIT', 'Sei sempre stato tenero, Bwonsamdi. Debole. Presto o tardi ti spezzerai, e poi il Carceriere avrà chello che gli spetta!', '', 38134),
(200994, 'itIT', 'Così le manderai nella Fauce? No, no, no... Sono la gente mia, Mueh\'zala, nun rinuncerò per niente a loro!', '', 38134),
(200993, 'itIT', 'Dov\'è che nascondi tutte chelle anime Zandalari, Bwonsamdi? Dalle al cumpà tuo, mo\', o te ne pentirai. Te ne pentirai assaaai!', '', 38134),
(200992, 'itIT', 'Il vero amore... nun è tenero? Il vecchio Bwonsamdi ha tenuto la dose di romanticismo sua in passato. Assai!', '', 38134),
(200987, 'itIT', 'Impara dal passato, ma non soffermartici troppo.', '', 38134),
(200977, 'itIT', '', 'Servi con coraggio.', 38134),
(200970, 'itIT', '', 'Nessun fardello è eterno.', 38134),
(200963, 'itIT', 'Spero che la tua presenza qui non danneggi i miei profitti.', '', 38134),
(200962, 'itIT', '', 'Stai lontano dai Drust mentre recuperi il polline dalle Rose Feroci. Spargi il polline sui Silvar Profanati per farli infuriare. Con un po\' di fortuna dovrebbe bastare a ostacolare i piani dei Drust, qualunque essi siano.', 38134),
(200933, 'itIT', '', 'Ehi! Da questa parte!', 38134),
(200932, 'itIT', 'Godetevi la musica rilassante di queste tre anime umili.', '', 38134),
(200931, 'itIT', 'Grazie, Fianc. Siamo entusiasti di essere stati invitati a suonare qui.', '', 38134),
(200930, 'itIT', 'I Caliceamaro sono pronti a cominciare!', '', 38134),
(200929, 'itIT', 'Aprite i vostri cuori e le vostre anime alla musica più commovente di tutte le Terretetre!', '', 38134),
(200928, 'itIT', '', 'Non farti vedere! I Drust strisciano da tutte le parti, $n! Letteralmente, strisciano! Stanno tramando qualcosa, qualcosa di grosso che dobbiamo fermare!\n\nVa bene, ecco il piano. I Drust hanno un mucchio di Silvar corrotti da quelle strane maschere. I poveracci sono sotto il controllo assoluto dei Drust. Non c\'è modo di salvarli, ma possiamo ancora sfruttarli.\n\nVedi questo fiore vicino a me? È una Rosa Feroce. No, non toccarla! Il polline di Rosa Feroce è pericoloso, rende i Silvar folli e violenti finché non svengono. Sfrutterò la mia magia per farti apparire come una Falenarden qualsiasi. Recupera del polline di Rosa Feroce, spargila sui corrotti e scatena il caos.\n\nCosa mai potrebbe andare storto?\n\nBe\', sei $gpronto:pronta;?', 38134),
(200927, 'itIT', 'Questa creatura mortale sostiene di essere già scappata dalle grinfie della Fauce! Mettiamo alla prova le sue parole.', '', 38134),
(200925, 'itIT', '', 'Non era caduto nella Fauce?', 38134),
(200924, 'itIT', 'E se quella sala risultasse vuota? No, il seggio è una perdita di tempo.', '', 38134),
(200922, 'itIT', '', 'Senza Krexus, la vittoria è impossibile. Dobbiamo aprire il Seggio del Primus!', 38134),
(200921, 'itIT', '', 'Vieni con me! Presto!', 38134),
(200920, 'itIT', 'Loro... hanno distrutto la necropoli. Krexus è...', '', 38134),
(200919, 'itIT', 'I Costrutti... hanno contrattaccato... Erano troppi...', '', 38134),
(200918, 'itIT', '', 'Vyraz! Cos\'è successo qui?', 38134),
(200916, 'itIT', 'Ahahahahahahah!', 'Ahahahahahahah!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200915, 'itIT', 'Stupido insetto! Non ci si libera di me!', 'Stupido insetto! Non ci si libera di me!', 38134),
(200914, 'itIT', 'Va\' più in fretta, stupida cosa!', 'Va\' più in fretta, stupida cosa!', 38134),
(200913, 'itIT', 'Cavalcale!', 'Cavalcale!', 38134),
(200912, 'itIT', '', 'Una vittoria gloriosa!', 38134),
(200910, 'itIT', '', 'Sono molto nervosa, $p. La musica mette a nudo la mia anima così che tutti possano vederla.\n\nE mentre questa Pietra del Peccato mi ricorda di tutta l\'oscurità che ho dentro, spero che la mia musica manifesti la luce che pure esiste.\n\nSire Garridan nella tua ambasciata è d\'accordo con me. È stato molto gentile e gli è piaciuta la musica che ho suonato per lui al mio arrivo.\n\nDevo aver lasciato il mio liuto da lui. Per favore, vai a prenderlo, mentre io continuo la mia meditazione prima dello spettacolo.', 38134),
(200909, 'itIT', '', 'Avrai anche fallito, ma sei sulla strada per la vittoria.', 38134),
(200907, 'itIT', '<Ansel il Fortunato distoglie a malincuore lo sguardo dalla sua Pietra del Peccato.>\n\nI miei fardelli sono ancora troppo pesanti.\n\nNon sarò mai libero dai pericoli della Fauce, finché ci sarà anche un solo peccato su questa pietra.\n\nFortunatamente, la mia musica eccezionale mi fa ottenere degli elogi dai miei controllori Venthyr e sono sicuro che aiutare la corte del Principe Renathal sia uno sforzo meritevole.\n\nRecupera il mio violino dai rifornimenti del gruppo, vicino ai ballerini, e inizierò la mia penitenza pubblica.', '', 38134),
(200902, 'itIT', '<Noko inspira lentamente.>\n\nIl direttore di scena dice che non riesce a trovare il mio flauto da nessuna parte.\n\nL\'ultima volta che è successo, ce l\'aveva uno dei Tecnici Chiassosi.\n\nQuei fastidiosi Dragatori non fanno che causare problemi ovunque alla festa.\n\nSe recupererai il mio flauto, suonerò per i tuoi ospiti.', '', 38134),
(200900, 'itIT', 'Sembra che lo spirito si sia calmato... Ti ringrazio.', '', 38134),
(200895, 'itIT', '', 'Ti dedicherò la mia prima canzone.', 38134),
(200894, 'itIT', '', 'I Drust hanno trasformato la mia gente in bestie senza cervello! Collabora con Piedambrato per difendere Selvarden.', 38134),
(200893, 'itIT', 'Suppongo che il tuo viaggio non sarà piacevole. Ah, be\'. Buona fortuna!', 'Suppongo che il tuo viaggio non sarà piacevole. Ah, be\'. Buona fortuna!', 38134),
(200892, 'itIT', 'Da lì, potrai saltare nel Pilastro dell\'Anima. È il fiume di anime che si incanala nella Fauce.', 'Da lì, potrai saltare nel Pilastro dell\'Anima. È il fiume di anime che si incanala nella Fauce.', 38134),
(200891, 'itIT', 'La Piattaforma di Teletrasporto ti porterà all\'Anello del Trasferimento.', 'La Piattaforma di Teletrasporto ti porterà all\'Anello del Trasferimento.', 38134),
(200889, 'itIT', 'Attenzione, $p. Ti prego di seguirmi.', 'Attenzione, $p. Ti prego di seguirmi.', 38134),
(200888, 'itIT', 'Farò in modo che sia la mia miglior prestazione mai vista finora.', '', 38134),
(200886, 'itIT', 'Quella lama... è stata forgiata dal Primus!', '', 38134),
(200885, 'itIT', 'Percepisco... il potere... nell\'aria...', '', 38134),
(200883, 'itIT', '', 'Anche se non hai le ali, non hai meno coraggio degli Ascesi!', 38134),
(200882, 'itIT', 'Che la nostra musica guidi anche la tua anima, $gamico:amica;.', '', 38134),
(200881, 'itIT', '', 'Maldraxxus osa farsi vedere ancora nel nostro regno? Mostra loro chi domina i cieli!', 38134),
(200880, 'itIT', 'Ha con sé Baine!', '', 38134),
(200879, 'itIT', '', 'Il Carceriere.', 38134),
(200876, 'itIT', 'Pelagos, è l\'ora della tua prima sfida. Suona il vespro quando sei pronto.', '', 38134),
(200875, 'itIT', '', 'La Via dell\'Ascensione è vicina. Combattenti, preparatevi!', 38134),
(200871, 'itIT', 'Chi sono io? Sono un\'anima!\n\nQuando giunse il mio tempo, fui chiamato a Selvarden. Lavorai con gli spiriti per tutta la vita, per cui fu... be\', naturale.\n\nCosa ne pensi della forma assunta dalla mia anima? Ho sempre voluto andare in giro come un lupo TUTTO il tempo!', '', 38134),
(200867, 'itIT', '', 'Il messaggio è sigillato con la magia? Be\', non è divertente...', 38134),
(200865, 'itIT', 'Si va alla festa! Merendine sono generose.', '', 38134),
(200863, 'itIT', '', 'Dovremmo muoverci in fretta. Sei in grado di viaggiare?', 38134),
(200856, 'itIT', 'I nostri alleati non possono affrontare da soli quella mostruosità. Dobbiamo dar loro una mano!', '', 38134),
(200850, 'itIT', '', 'Oh, ti prego, liberami! È passato troppo tempo dall\'ultima volta che sono riuscita a prendermi cura dei boschi di Selvarden.', 38134),
(200847, 'itIT', 'Sì, lascia che prenda nota! Oh aspetta, mi servono i miei appunti.', '', 38134),
(200846, 'itIT', 'C\'è qualcosa che non va. Avevo degli appunti, sono sicuro che avevo degli appunti. La ricerca, sì. La ricerca sull\'Animum.\n\nA quanto pare, ho perso i miei appunti.', '', 38134),
(200842, 'itIT', 'Bello spettacolo! Andiamo a cercare la mia sciabola e diamo a questi tizi una bella lezione.', '', 38134),
(200841, 'itIT', 'Sì, mi hai beccato. Sei qui per gongolare? Oh, ma non sei $guno:una; di loro.\n\nSono stato catturato e mi hanno tolto le armi. Pensi di potermi aiutare a fuggire?', '', 38134),
(200838, 'itIT', '', 'Andiamo a cercare... non riesco a ricordare cosa.', 38134),
(200837, 'itIT', '', 'Mi hanno tenuto imprigionata qui e hanno avvelenato la mia mente. Credo di aver salvato i miei ricordi prima che tutto questo succedesse, ma non posso esserne sicura.', 38134),
(200834, 'itIT', '', 'Bene! Andiamo a trovare quell\'Inseguitore delle Tombe.', 38134),
(200833, 'itIT', '', 'Quella cosa mi ha intrappolata qui e sopraffatta con i morti. Non so quanto tempo sia passato, ma ne ho avuto abbastanza! Liberami e aiutami ad abbatterla!', 38134),
(200830, 'itIT', 'Grazie! Andiamo a trovare Enozia.', '', 38134),
(200829, 'itIT', 'Non sta andando come previsto. Stavo cercando di salvare Enozia e mi sono ritrovato prigioniero io stesso. Aiutami, ti prego. E aiuta lei!', '', 38134),
(200826, 'itIT', '', 'Sono vicini, non mostrare rimorso.', 38134),
(200825, 'itIT', '', 'Calcafauce, ti ho $gvisto:vista; camminare in queste sale.\n\nAhimè, il mio eccesso di arroganza ha portato alla mia cattura. Ho ancora un compito da svolgere, un servitore di alto rango del Carceriere da cui dovrei ricavare alcune importanti informazioni.', 38134),
(200821, 'itIT', 'Vai via.\n\nSono stato sconfitto.\n\nPresto sarò morto senza la mia armatura.', '', 38134),
(200818, 'itIT', 'È ancora nelle vicinanze. Un bello smembramento gli insegnerà la lezione.', '', 38134),
(200817, 'itIT', 'Salve! O saluti, o qualsiasi calorosa cortesia potresti aspettarti.\n\nStavo inseguendo il mio... uh, chiamiamolo esperimento. Si è scoperto essere più intelligente del previsto e così mi sono trovato in questa situazione.\n\nChe ne diresti di liberarmi, così da riottenere ciò che è mio? Le parti utili, almeno.', '', 38134),
(200814, 'itIT', 'Devo trovare la mia lama. Solo allora potrò ritornare.', '', 38134),
(200813, 'itIT', '$gUn altro venuto:Un\'altra venuta; a provocarmi... Oh, non sei $guno:una; di loro?\n\nCredevo di essere condannato per l\'eternità a restare incatenato tra queste mura.\n\nAiutami a recuperare la mia lama, vorrei riavere almeno questo onore.', '', 38134),
(200810, 'itIT', 'Ora andiamo a cercare la mia bacchetta! Be\', non è ancora mia. Potremmo dover ricorrere a un po\' di violenza per ottenerla.', '', 38134),
(200809, 'itIT', 'Ah, $gil leggendario:la leggendaria; Calcafauce. La mia sete di potere sembra aver provocato l\'attuale situazione.\n\nPotresti essere $gun buon:una buona;... qualsiasi cosa tu sia, e liberarmi?\n\nOh, potrei anche avere un piccolo conto da regolare.', '', 38134),
(200803, 'itIT', 'Intrappolato!\n\nLiberami!\n\nDevo tornare dal mio padrone.', '', 38134),
(200799, 'itIT', 'C\'è un mucchio di parti gettate qui. Sembra che siano state rimosse da qualcosa di importante.', '', 38134),
(200796, 'itIT', 'Grazie. Per queste ultime catene servirà la chiave della guardia che mi ha rinchiuso.', '', 38134),
(200795, 'itIT', 'Queste creature mi hanno rinchiuso e incatenato quassù. Liberami!', '', 38134),
(200792, 'itIT', '', 'Grazie. Andiamo a trovare la mia spada.', 38134),
(200791, 'itIT', '', 'Sei $gil:la; Calcafauce? Mi hanno preso la spada della mia famiglia. Aiutami a recuperarla.', 38134),
(200780, 'itIT', 'Ci penso io.', '', 38134),
(200778, 'itIT', 'Sono pronto ad affrontare la sfida!', '', 38134),
(200776, 'itIT', 'Ah ah! Questo SÌ che è divertente!', '', 38134),
(200770, 'itIT', 'Grazie, l\'abominio dovrebbe essere qui da qualche parte.', '', 38134),
(200769, 'itIT', 'L\'unico che servivo è stato ucciso quaggiù, lasciandomi solo e umiliato. Aiutami a finire il lavoro che sono stati mandati a fare.', '', 38134),
(200766, 'itIT', '', 'Raggiungiamo quella mostruosità.', 38134),
(200765, 'itIT', '', 'La mia lancia si è rotta mentre combattevo una delle mostruosità di questo posto. Aiutami a ucciderla, così che possa prendere la sua arma!', 38134),
(200764, 'itIT', 'Ero in grado di tenere testa ai migliori, ma ora sembra che non abbia più la conoscenza di una volta. Posso solo pensare che i ricordi mi siano stati rubati. Aiutami a recuperarli.', '', 38134),
(200763, 'itIT', 'Cerchiamo una Pietra della Memoria.', '', 38134),
(200760, 'itIT', 'Ayeleth vuole Animum! Uccidi il mio torturatore, ha rubato la mia scorta e lo rivoglio indietro.', '', 38134),
(200759, 'itIT', 'Andiamo a recuperare il mio Animum.', '', 38134),
(200756, 'itIT', '', 'Liberami, per favore! Sono stata incatenata qui per non so quanto tempo.', 38134),
(200752, 'itIT', 'Devo recuperare la mia Pietra del Peccato! Ci sono anime da distruggere!', '', 38134),
(200751, 'itIT', 'Dov\'è la mia Pietra del Peccato?', '', 38134),
(200746, 'itIT', 'Eccellente. Andiamo a trovare quel gargoyle e vendichiamoci un po\'.', '', 38134),
(200745, 'itIT', 'Sono stato rapito! Mi ha portato qui un maledetto gargoyle. Liberami! Liberami, così potrò vendicarmi!', '', 38134),
(200742, 'itIT', 'La guerra per preservare Selvarden non è ancora finita.', '', 38134),
(200741, 'itIT', 'Andiamo a ritrovare il mio badile!', '', 38134),
(200740, 'itIT', 'Mi hanno rubato il badile! Mi farebbe comodo una mano per riprendermelo.', '', 38134),
(200737, 'itIT', '', 'Andiamo a ritrovare i miei ricordi. Dovrebbero essere conservati in una Pietra della Memoria qui da qualche parte.', 38134),
(200735, 'itIT', '', 'Tu chi sei? Aspetta, io chi sono? I miei ricordi sono sparsi, devo ritrovare la mia Pietra della Memoria per recuperarli.', 38134),
(200733, 'itIT', '<Scegli un discorso.>', '', 38134),
(200731, 'itIT', 'Assimila le lezioni che hai appreso in questa sfida e applicale ai tuoi futuri tentativi.', '', 38134),
(200728, 'itIT', 'È richiesta la loro partecipazione alla Corte delle Braci perché ______', '', 38134),
(200726, 'itIT', 'La prego di esonerare ______ dal suo dovere al cancello.', '', 38134),
(200701, 'itIT', 'Speriamo che i nostri ospiti siano meno esigenti di me su ciò che mangiano.', '', 38134),
(200700, 'itIT', 'Questi almeno dovrebbero essere più sicuri di qualsiasi altra cosa tu abbia portato da Maldraxxus.', '', 38134),
(200699, 'itIT', 'Subito, capo.', '', 38134),
(200698, 'itIT', 'Cameriere, servi queste... delicatezze locali... ai tavoli, immediatamente.', '', 38134),
(200697, 'itIT', 'Disgustoso, non commestibile, puzzolente...', '', 38134),
(200696, 'itIT', 'Stefan lo Schizzinoso rovista tra i funghi che gli hai portato.', '', 38134),
(200695, 'itIT', 'Tu devi essere la persona di cui parlava l\'emissario del Bastione.', '', 38134),
(200694, 'itIT', 'Equipaggiamento ancora da sistemare. Torna più tardi, dopo che crei!', '', 38134),
(200693, 'itIT', 'Tutto il tuo equipaggiamento, proprio qui!\n\nQuale vuoi equipaggiare?', '', 38134),
(200692, 'itIT', 'Ho individuato le tu forze, Draven.', '', 38134),
(200691, 'itIT', 'Calcafauce, prendi l\'Elfo del Sangue e distruggi i cristalli che prosciugano il loro Animum, io continuerò a perlustrare i cieli.', '', 38134),
(200690, 'itIT', 'Kael\'thas ci fa cenno da laggiù. Dobbiamo atterrare!', '', 38134),
(200689, 'itIT', '', 'Piango per la tua perdita e per la parte che ho avuto in tutto ciò.\n\nSpero che questa non sia la fine.', 38134),
(200688, 'itIT', 'La mia amata ha fatto la scelta giusta quando ha deciso di salvarti. Sono addolorato per come sia finita.\n\nIl Sogno non sarà mai più lo stesso.', '', 38134),
(200687, 'itIT', '', 'Madre, se solo tu fossi qui con me... La nostra gente ha ancora speranza, ma fatichiamo senza di te.\n\nHo bisogno della tua guida.', 38134),
(200686, 'itIT', 'Tu eri la madre che percorreva il sentiero del Sogno con me. Mi hai insegnato tantissime cose.\n\nIl mondo ha bisogno di te.', '', 38134),
(200684, 'itIT', '<Hai tutti gli strumenti che ti servono per scrivere il Foglio di Permesso di Testaroccia.>\n\n<Ora devi scegliere cosa scrivere.>', '', 38134),
(200681, 'itIT', 'Goffo??? Il potente Millhouse Manalampo non è mai stato goffo neanche un giorno, nella sua vita!', '', 38134),
(200680, 'itIT', '', 'Ahi! Mi hai fatto male, goffo babbeo!', 38134),
(200679, 'itIT', '', 'E allora non metterti in mezzo, Millhouse!', 38134),
(200678, 'itIT', 'Ahia! Attenta a quello che fai, Millefica!', '', 38134),
(200677, 'itIT', 'Sei la migliore, Milly! Non litighiamo mai più! Per un po\', almeno...', '', 38134),
(200675, 'itIT', '', 'Ti impegni a essere sempre al centro del divertimento, non è vero?', 38134),
(200674, 'itIT', 'Resisterò... per te...', '', 38134),
(200671, 'itIT', '', 'La tempesta si placa... per ora.', 38134),
(200669, 'itIT', '', 'Non siamo gli unici che hanno visto quell\'incubo. I Drust si avvicinano in questo preciso istante!', 38134),
(200667, 'itIT', '', 'Ehi! Ascoltate tutti!', 38134),
(200658, 'itIT', '', 'Ehilà, $n!\n\nSe sei qui per i Viaggi nel Tempo, temo che dovrai conoscere meglio il presente prima di poter visitare il passato.\n\nForse potrò mandarti indietro quando avrai girato per la città e incontrato il Re!', 38134),
(200657, 'itIT', '', 'Awww, che tesoro! Allora ti importa davvero di me!', 38134),
(200656, 'itIT', 'Sembra che nulla metta fine agli incubi! Potresti provare un... approccio diretto?', '', 38134),
(200647, 'itIT', '', 'Fallito!', 38134),
(200646, 'itIT', 'Be\', io, uh... Ehi, aspetta un attimo! Hai appena ammesso di aver fatto la stessa cosa che ho fatto io! Ipocrita!', '', 38134),
(200645, 'itIT', 'Però mo\', ecco... tengo di nuovo bisogno di chel mojo!', '', 38134),
(200644, 'itIT', 'Come chesti due Gnomi pazzerelli accà. Entrambi stavano assetati di potere, sono stato felice di soddisfarli.', '', 38134),
(200643, 'itIT', 'Sai, nun sono soltanto i Troll a chiedere n\'aiuto al vecchio Bwonsamdi. Tutti possono fare accordi con il Loa della Morte!', '', 38134),
(200641, 'itIT', 'La mia influenza è indiscutibile! Tu servirai ME!', '', 38134),
(200640, 'itIT', 'Per l\'Arconte! Viene dal medaglione nella corona. Presto, Calcafauce!', '', 38134),
(200639, 'itIT', 'Ottimo lavoro, mio fedele servo. Risorgerò di nuovo.', '', 38134),
(200638, 'itIT', 'Quella voce? Dove?', '', 38134),
(200637, 'itIT', 'Sì... Potenzia la corona.', '', 38134),
(200621, 'itIT', 'Dovremmo dirlo al Droman. Un Semebrado che lega con il suo curatore è un evento raro e gioioso!', '', 38134),
(200620, 'itIT', 'Questo è diverso dagli altri. Il nostro vero nemico... I Drust.', '', 38134),
(200618, 'itIT', '', 'Vyraz è impudente, ma ha ragione. Ci servono rinforzi.', 38134),
(200616, 'itIT', 'Che ricordo vuoi sfidare?', '', 38134),
(200615, 'itIT', '', 'Non male! Per una creatura sprovvista di ali, ovviamente.', 38134),
(200614, 'itIT', '', 'Gli Aspiranti si riuniscono per mettere alla prova la loro precisione in aria. Pensi di poterlo fare?', 38134),
(200611, 'itIT', 'Non finiamo mai di addestrarci, dobbiamo migliorare sempre le nostre capacità.', '', 38134),
(200610, 'itIT', '', 'Il tumulto di uno spirito, un incubo a cui è stata data una forma. Plasmasogni chiede aiuto.', 38134),
(200608, 'itIT', 'Forse so dove potremo ottenerne dell\'altro.', '', 38134),
(200607, 'itIT', 'No... gli incubi persistono ancora, ci serve più Animum.', '', 38134),
(200606, 'itIT', '', 'Ha funzionato?', 38134),
(200605, 'itIT', 'Speriamo che sia abbastanza...', '', 38134),
(200603, 'itIT', '', 'Il Seggio del Primus è sotto assedio! Dobbiamo difenderlo!', 38134),
(200601, 'itIT', '', 'Brandicenere! Calcafauce! Con me!', 38134),
(200599, 'itIT', 'Certo! Proglo ha conservato quest\'Animum in caso di eventi come questo.\n\nEcco a te. Fa\' attenzione, e se mai ti servisse qualcosa, ricorda che Proglo è una delle anime più coraggiose di Selvarden!', '', 38134),
(200597, 'itIT', 'AH! Che cosa sei? Quattro Spriggan in una sottospecie di costume?\n\nUna di quelle mostruosità ammorbate? \n\nQualunque cosa siate... andatevene! Per favore?', '', 38134),
(200596, 'itIT', 'Salve! Sei qui per l\'Animum? Proglo si stava assicurando di tenerlo conservato per qualunque Semebrado ne avesse avuto bisogno.\n\nQuegli orribili Spriggan e le creature ammorbate non ci metteranno le mani sopra!\n\nProglo è una delle anime più coraggiose di Selvarden, non gli sfugge niente!', '', 38134),
(200593, 'itIT', 'Prepareremo il rituale!', '', 38134),
(200592, 'itIT', 'Si avvicina il Generale Draven!', '', 38134),
(200591, 'itIT', 'Parla rapidamente. Abbiamo molto da fare.', '', 38134),
(200590, 'itIT', '', 'Krexus aveva ragione. Possediamo la chiave per il Seggio del Primus.', 38134),
(200587, 'itIT', 'Allora lo faremo uscire allo scoperto, ma prima dobbiamo ripulire i cieli dai nemici.', '', 38134),
(200586, 'itIT', 'Si nasconde sottoterra, come il codardo che è.', '', 38134),
(200584, 'itIT', '', 'Accetta questa parte del mio potere, mortale. Usala per alterare la tua forma in una che l\'anima sottostante troverà più... confortevole.', 38134),
(200577, 'itIT', 'Spero proprio che le nostre strade si incrocino di nuovo. Fino ad allora, segui la via.', '', 38134),
(200576, 'itIT', 'La Lancia di Kalliope è stata recuperata ed è solo grazie a te, $geroe:eroina;.', '', 38134),
(200575, 'itIT', '', 'Così dovrebbe andare! Io terrò d\'occhio i Drust, tu ritorna da Aliothe!', 38134),
(200574, 'itIT', 'Complimenti, $n, davvero.', '', 38134),
(200573, 'itIT', '', 'Anche se le nostre risorse sono agli sgoccioli, farò il possibile.', 38134),
(200572, 'itIT', '', 'La Via dell\'Ascensione ti aspetta.\n\nSei $gpronto:pronta; ad affrontarla?', 38134),
(200571, 'itIT', 'Mettiamo fine all\'incubo di questo spirito, Aliothe!', '', 38134),
(200527, 'itIT', '', 'Non ci vorrà molto prima che le altre casate scoprano della lama. Preparati a combattere.', 38134),
(200526, 'itIT', '', 'Il Primus era un fabbro d\'armi leggendario. Se otterremo la chiave per la sua cripta, il suo arsenale sarà nostro.', 38134),
(200525, 'itIT', '', 'Sembra che un cancello blocchi il nostro cammino, quando vuoi fammi un fischio e ti porterò oltre.', 38134),
(200508, 'itIT', '', 'Il Seggio del Primus è rimasto chiuso da quando è scomparso. Molti hanno provato ad aprirlo.', 38134),
(200506, 'itIT', 'Non restiamo qui troppo a lungo. Bisogna portare quel Semebrado all\'Antro del Letargo, giusto?', '', 38134),
(200505, 'itIT', 'Non possiamo cedere la Radura di Soryn a questi mostri.', '', 38134),
(200491, 'itIT', '', 'Hai fatto ritorno! Il Semebrado stava diventando inquieto.', 38134),
(200469, 'itIT', 'Prendi il nostro cervo e cavalca con noi.', '', 38134),
(200458, 'itIT', '$n, hai recuperato la Lancia e i restanti studiosi sono al sicuro. Approfittiamo di questa offerta e andiamo via senza ulteriore indugio. Ci vedremo a Requie dell\'Eroe.', '', 38134),
(200455, 'itIT', 'Soffri bene, $p.', '', 38134),
(200451, 'itIT', '', 'Ben fatto! Credo che le falene potrebbero imparare un paio di cosette da te.', 38134),
(200450, 'itIT', '', 'Dobbiamo raccogliere dei Coni d\'Animum, ma il posto è pieno di Drust! Un po\' di magia dovrebbe aiutarti a superarli.', 38134),
(200449, 'itIT', '', 'Ho sentito che sei un ottimo riecheggiatore.\n\n<Baccaluna ride.>\n\nTerrò a mente il tuo nome nel caso mi servano altre mosse di seduzione ornitologiche.\n\nComunque, ho buone notizie! La Regina si sta dirigendo a Tirna Vaal! Il bosco resterà aperto per occuparsi dei Semebrado e per ospitare le anime. Sarà presto qui per benedire i nostri cuori e rinvigorire tutto ciò che ci circonda.\n\nDovresti avere la possibilità di parlarle.', 38134),
(200448, 'itIT', '', 'Hai fatto ballare Korenth, eh?\n\n<Baccaluna ride.>\n\nSapevo che fossi $gquello giusto:quella giusta;, $n. Terrò a mente il tuo nome nel caso saltino fuori altri favori o compiti strani da portare a termine.\n\nHo buone notizie! La Regina si sta dirigendo a Tirna Vaal! Il bosco resterà aperto per occuparsi dei Semebrado e per ospitare le anime. Sarà presto qui per benedire i nostri cuori e rinvigorire tutto ciò che ci circonda.\n\nDovresti avere la possibilità di parlarle.', 38134),
(200446, 'itIT', 'Prestigio e potere. Renathal sarà soddisfatto. Controlla che le esigenze del nostro amico siano soddisfatte.', '', 38134),
(200445, 'itIT', 'La corona perfetta... per il tuo capo. Concentra il potere dei medaglioni di cui mi hai parlato.', '', 38134),
(200442, 'itIT', '', 'Non pensate di aver vinto! La prossima volta che ci incontreremo sarà l\'ultima.', 38134),
(200441, 'itIT', '', 'Addosso!', 38134),
(200440, 'itIT', '', 'Non sei alla mia altezza, Kyrian. Io sono l\'Eletta del Dubbio e sto perdendo la pazienza per le tue continue interruzioni!', 38134),
(200439, 'itIT', '', 'Sembra che tu abbia degli ospiti, Eletta. Occupati di loro.', 38134),
(200438, 'itIT', '', 'Vedo chiaramente il mio futuro. L\'Arconte cadrà sotto la mia lama e il Bastione sarà mio!', 38134),
(200437, 'itIT', 'Ahhh!', '', 38134),
(200436, 'itIT', '', 'Sei stato una valida pedina, Uther. Devos ti avrà anche apprezzato... ma io non ho più bisogno di te.', 38134),
(200435, 'itIT', '', 'Prendi il suo potere, Lysonia. Usalo per alimentare la tua trasformazione.', 38134),
(200431, 'itIT', 'Vedo che indugi sull\'orlo del baratro, mortale. Percepisci la Fauce tentare di impadronirsi della tua anima?\n\nAmmiro la tua audacia e la tua resilienza. Vorrei metterle alla prova.\n\nSe sei veramente quello che dicono che tu sia, allora ti sfido: sconfiggimi e potrai continuare a camminare per il Bastione. Soccombi, e mi approprierò nuovamente del tuo Animum.\n\nCosa ne dici?', 'Vedo che indugi sull\'orlo del baratro, mortale. Percepisci la Fauce tentare di impadronirsi della tua anima?\n\nAmmiro la tua audacia e la tua resilienza. Vorrei metterle alla prova.\n\nSe sei veramente quello che dicono che tu sia, allora ti sfido: sconfiggimi e potrai continuare a camminare per il Bastione. Soccombi, e mi approprierò nuovamente del tuo Animum.\n\nCosa ne dici?', 38134),
(200429, 'itIT', 'Questa era solo una battaglia, temo che molte altre ci attendano.', '', 38134),
(200428, 'itIT', 'Bel colpo! Ripiegate e riorganizziamoci!', '', 38134),
(200427, 'itIT', 'Caccia Selvaggia! Ora!', '', 38134),
(200422, 'itIT', '', 'Salve ancora! Porto buone notizie!', 38134),
(200421, 'itIT', 'Presto! Tutto dev\'essere perfetto per la Regina!', '', 38134),
(200420, 'itIT', '', 'Aiuto! Non c\'è nessuno che mi aiuti?', 38134),
(200418, 'itIT', '', 'Nelwyn singhiozza.', 38134),
(200416, 'itIT', 'Segui la strada per raggiungere Tirna Vaal. Ci vediamo lì!', '', 38134),
(200396, 'itIT', 'Un massacro esemplare! Complimenti ai sopravvissuti!', '', 38134),
(200395, 'itIT', 'Ah! Non male, per un branco di neofiti!', '', 38134),
(200391, 'itIT', 'Una magnifica dimostrazione! Ce la farai in men che non si dica.', '', 38134),
(200390, 'itIT', 'È saggio esercitarsi con l\'arte del volo prima di unirsi alle fila degli Ascesi. Vediamo come te la cavi!', '', 38134),
(200386, 'itIT', 'Ho assistito a molte di queste espiazioni leggendarie di persona, sai.\n\nSono storie che vale la pena raccontare.', '', 38134),
(200385, 'itIT', 'Non perdiamo tempo, va bene?', '', 38134),
(200384, 'itIT', 'Ci muoveremo quando darai il segnale, Calcafauce.', '', 38134),
(200367, 'itIT', '', 'Vedi di non aggiungere il tuo nome alla lista.', 38134),
(200366, 'itIT', '', 'La ricerca della lancia ha portato a molti fallimenti.', 38134),
(200363, 'itIT', '<L\'animale soffia e ti sfida!>', '<L\'animale soffia e ti sfida!>', 38134),
(200362, 'itIT', '<L\'animale ti fissa, con le orecchie abbassate. I suoi zoccoli battono minacciosamente il terreno.>', '<L\'animale ti fissa, con le orecchie abbassate. I suoi zoccoli battono minacciosamente il terreno.>', 38134),
(200356, 'itIT', 'Di qua! Siamo stati attaccati da alcuni animali famelici, ci sono anime in pericolo!', '', 38134),
(200353, 'itIT', 'La prossima procedura sarà una vera bomba!', '', 38134),
(200343, 'itIT', 'Le legioni degli Indegni infestano la mia forgia. Fammi spazio per lavorare, e ti aiuterò.', '', 38134),
(200342, 'itIT', '', 'Sto con Renathal in ciò che spera di realizzare.', 38134),
(200341, 'itIT', '', 'Laurent ha i suoi difetti, ma mi fido di lui per riparare lo specchio.', 38134),
(200340, 'itIT', '', 'Questo ambiente è molto più adatto.', 38134),
(200336, 'itIT', 'Stai dalla parte di Dama Baccaluna? Be\', spero che te la cavi bene con la fauna affamata!', '', 38134),
(200334, 'itIT', 'Continuate a muovervi!', '', 38134),
(200333, 'itIT', 'Piumaleggera! Abbiamo bisogno d\'aiuto!', '', 38134),
(200331, 'itIT', 'Il rumore... è insopportabile!', '', 38134),
(200330, 'itIT', '', 'Cos\'è questa follia?', 38134),
(200329, 'itIT', 'È molto più facile mantenere la concentrazione senza doversi preoccupare della Legione di Pietra che incombe sulle nostre teste.', '', 38134),
(200328, 'itIT', 'È... un compito ingrato, ma finché la siccità continuerà, qualcuno dovrà pur farlo.', '', 38134),
(200326, 'itIT', 'Ogni Semebrado contiene uno spirito della natura che un giorno si sveglierà e farà ritorno al suo mondo.', '', 38134),
(200323, 'itIT', 'Seguimi. La Foresta di Cuordilegno non è lontana.', '', 38134),
(200322, 'itIT', 'La Regina deve decidere chi sopravvive... e chi avvizzisce, perdendo per sempre lo spirito al suo interno.', '', 38134),
(200287, 'itIT', 'Suona il corno!', '', 38134),
(200286, 'itIT', 'Presto, devi raccogliere il corno!', '', 38134),
(200275, 'itIT', 'Cerca di attirare quell\'uccello via dal nido!', '', 38134),
(200272, 'itIT', 'Mortale, raccogli il corno!', '', 38134),
(200271, 'itIT', 'No! Il corno!', '', 38134),
(200270, 'itIT', 'Per la Regina e il bosco!', '', 38134),
(200267, 'itIT', 'Grazie per aver salvato il Semebrado! Lo porterò al sicuro.', '', 38134),
(200266, 'itIT', 'Hai del talento! Quegli Aspiranti potrebbero imparare qualcosina da te.', '', 38134),
(200265, 'itIT', 'Per poter volare, devi prima imparare a cadere. Unisciti a questi Aspiranti e fai un tentativo!', '', 38134),
(200262, 'itIT', 'Ta\'saran degli Alienatori offre servizi di stallaggio nella Sala delle Curiosità.', 'Ta\'saran degli Alienatori offre servizi di stallaggio nella Sala delle Curiosità.', 38134),
(200259, 'itIT', 'Puoi trovare una cassetta della posta fuori dalla Locanda sulla punta orientale dell\'Anello dei Destini.', 'Puoi trovare una cassetta della posta fuori dalla Locanda sulla punta orientale dell\'Anello dei Destini.', 38134),
(200257, 'itIT', 'Puoi trovare la Locanda sulla punta orientale dell\'Anello dei Destini.', 'Puoi trovare la Locanda sulla punta orientale dell\'Anello dei Destini.', 38134),
(200255, 'itIT', 'Puoi trovare il Maestro di Volo ai piani superiori sull\'Anello del Trasferimento. Prendi il portale dall\'Anello dei Destini.', 'Puoi trovare il Maestro di Volo ai piani superiori sull\'Anello del Trasferimento. Prendi il portale dall\'Anello dei Destini.', 38134),
(200253, 'itIT', 'Ta\'vis degli Alienatori ti offre l\'abilità di cambiare il tuo aspetto. Puoi trovarlo nella Sala delle Curiosità.', 'Ta\'vis degli Alienatori ti offre la possibilità di cambiare il tuo aspetto. Puoi trovarlo nella Sala delle Curiosità.', 38134),
(200252, 'itIT', 'Noi due colpiremo per primi, il resto di voi attaccherà quando suonerò il corno!', '', 38134),
(200251, 'itIT', 'Faremo ammenda per questo, Forgialita Supremo. Lo giuro.', '', 38134),
(200250, 'itIT', 'Gli Alienatori hanno stabilito delle strutture bancarie per te nella Sala della Custodia.', 'Gli Alienatori hanno stabilito delle strutture bancarie per te nella Sala della Custodia.', 38134),
(200248, 'itIT', 'Gli Alienatori offrono servizi di trasmogrificazione nella Sala delle Curiosità.', 'Gli Alienatori offrono servizi di trasmogrificazione nella Sala delle Curiosità.', 38134),
(200246, 'itIT', 'Puoi trovare i Sovrintendenti e gli altri visitatori importanti nella sala riunioni dell\'Enclave.', 'Puoi trovare i Sovrintendenti e gli altri visitatori importanti nella sala riunioni dell\'Enclave.', 38134),
(200244, 'itIT', '', 'Be\', suppongo di potermi affidare alle mie ali in caso qualcosa vada storto. Mi fido di te.', 38134),
(200243, 'itIT', '', 'Dobbiamo... saltarci dentro? Dentro il pozzo delle anime condannate alla dannazione eterna, con ben poca speranza di libertà e redenzione?', 38134),
(200239, 'itIT', 'Comandano un\'enorme bestia, molti di noi sono caduti per mano sua.', '', 38134),
(200238, 'itIT', 'Oggi onoriamo Potentius!\n\nLui è molto pulito, si lava sempre.\n\nLo trovi vicino al Tempio della Purezza.\n\nGli porti un Puriano Maturo?', '', 38134),
(200233, 'itIT', 'Corri, Korenth!', '', 38134),
(200232, 'itIT', 'Oh oh... ehm... salve?', '', 38134),
(200231, 'itIT', 'Non mollare! Devo solo intrufolarmi...', '', 38134),
(200230, 'itIT', 'Allora cadrà per mano nostra.', '', 38134),
(200227, 'itIT', 'Sai, credo di avere proprio quello che serve in questa situazione.\n\nNon muoverti. Probabilmente non farà male.', '', 38134),
(200225, 'itIT', 'I miei ringraziamenti!', 'I miei ringraziamenti!', 38134),
(200224, 'itIT', 'Non è il momento di riposare!', 'Non è il momento di riposare!', 38134),
(200223, 'itIT', 'Volevano farmi diventare uno di loro...', 'Volevano farmi diventare una di loro...', 38134),
(200222, 'itIT', 'Il loro numero cresce con ogni battaglia...', 'Il loro numero cresce con ogni battaglia...', 38134),
(200221, 'itIT', 'La lotta continua! Per Selvarden!', 'La lotta continua! Per Selvarden!', 38134),
(200220, 'itIT', 'Non deluderemo la foresta!', 'Non deluderemo la foresta!', 38134),
(200219, 'itIT', '', 'Il mio compito è sacro.', 38134),
(200215, 'itIT', '', 'Porterò la Compassione con me e ci incontreremo a Oribos. Devo prepararmi, è un viaggio che molti Kyrian non intraprenderanno mai...', 38134),
(200207, 'itIT', 'Grazie per la vostra presenza alla Corte delle Braci!', '', 38134),
(200206, 'itIT', '', 'RAHHH!!! Vieni, Uther. Il nostro scopo è superiore a questi sciocchi!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200205, 'itIT', 'Oggi onoriamo Arsios!\n\nAma gli animali, specialmente Kala.\n\nLo trovi nel Giardino del Riposo.\n\nGli porti un Puriano Maturo?', '', 38134),
(200204, 'itIT', '', 'Lascia il tuo giocattolino, mio animaletto. C\'è del lavoro da svolgere.', 38134),
(200203, 'itIT', 'Imponi agli altri il tuo punto di vista... I tuoi fallimenti... Io ho faticato per eoni per diventare l\'incarnazione dei miei principi...', '', 38134),
(200202, 'itIT', 'Padrona... non si è manifestato nulla.', '', 38134),
(200201, 'itIT', '', 'Rivelaci il tuo orgoglio, Eletto!', 38134),
(200200, 'itIT', '', 'È troppo tardi, Calcafauce. Presto, il tuo prezioso Eletto soccomberà alla superbia che ha rinchiuso dentro di sé.', 38134),
(200199, 'itIT', 'Tensioni? In che guai mi hai fatto cacciare questa volta, Draven?', '', 38134),
(200198, 'itIT', 'So come occuparmi di canaglie come questa!', '', 38134),
(200197, 'itIT', 'Ma insomma!', '', 38134),
(200196, 'itIT', 'Forse così imparerai le buone maniere!', '', 38134),
(200195, 'itIT', 'Ci incontreremo alla Forgia Eterna. La tua presenza attenuerà eventuali tensioni dovute al nostro arrivo.', '', 38134),
(200193, 'itIT', 'Facciamo in fretta. Ho un appuntamento.', '', 38134),
(200189, 'itIT', 'Vieni! In molti hanno bisogno d\'aiuto!', '', 38134),
(200188, 'itIT', '', 'Non importa, Uther. Abbiamo tutto ciò che ci serve.', 38134),
(200187, 'itIT', 'I collettori sono stati fermati. Stiamo perdendo potenza!', '', 38134),
(200185, 'itIT', 'Uno dei Drust... uno enorme.', '', 38134),
(200184, 'itIT', '', 'Cos\'è quell\'affare?!?', 38134),
(200183, 'itIT', '', 'Adoro le soluzioni creative! Forse ora ci penseranno due volte prima di causare problemi.', 38134),
(200182, 'itIT', '', 'Questi Spriggan devono essere fermati! Dico di dar loro un assaggio della loro stessa medicina.', 38134),
(200171, 'itIT', '', 'Aspetta, anche TU hai fatto un patto con Bwonsamdi? So che scegli sempre la via più comoda, Millhouse, ma ora hai davvero toccato il fondo!', 38134),
(200170, 'itIT', 'Cos\'abbiamo qui? Quel decrepito Loa ha già mandato degli esattori di crediti?!? Ma non ho ancora avuto il suo mojo abbastanza a lungo da creare il mio capolavoro!', '', 38134),
(200169, 'itIT', 'Certo! Solo un paio di ritocchi dell\'ultimo minuto...', '', 38134),
(200168, 'itIT', 'Gli ospiti arriveranno a breve. Suppongo che tu e Temel abbiate tutto sotto controllo?', '', 38134),
(200167, 'itIT', 'Come al solito, arrivi esattamente quando ho bisogno di te.', '', 38134),
(200165, 'itIT', 'L\'ultima e la più eccitante corte di Revendreth!', '', 38134),
(200163, 'itIT', '', 'Terremo la posizione! Apri la porta!', 38134),
(200157, 'itIT', 'Aspetta! La strada non è sicura!', '', 38134),
(200156, 'itIT', 'Dama Baccaluna... è la migliore Silfa... È davvero necessario?', '', 38134),
(200155, 'itIT', 'Be\'... Se proprio insisti...', '', 38134),
(200151, 'itIT', 'Argh!', '', 38134),
(200150, 'itIT', '', 'Basta con questi inutili giochetti. Uther, finiamola qui, dannazione!', 38134),
(200148, 'itIT', 'Un tempo eri la più fedele ai nostri insegnamenti, alla nostra Arconte. Devi scacciare i pensieri oscuri. Puoi salvare i tuoi seguaci e anche te stessa!', '', 38134),
(200147, 'itIT', '', 'Devo trovare l\'Eletto. La Compassione dovrebbe darti l\'aiuto che ti serve a terra.', 38134),
(200145, 'itIT', '', 'Il solito idealista, Eletto. Crollerai sotto il mio potere.', 38134),
(200144, 'itIT', 'Lysonia, la tua presunzione sarà la tua rovina. Devi ritornare sulla retta via!', '', 38134),
(200143, 'itIT', 'Argh! Come fanno i Kyrian a sopportare tutta questa stupida Luce?', '', 38134),
(200142, 'itIT', 'Bastione. Anche io non posso sfuggire ai miei peccati.', '', 38134),
(200133, 'itIT', '', 'Per la grazia dell\'Arconte, è arrivato un po\' di aiuto. I miei soldati sono feriti e ci vuole tutta la mia forza per tenerli stabili.', 38134),
(200131, 'itIT', '', 'Vieni. Cercheremo Pelagos, il mio Vincolo dell\'Anima, a Requie dell\'Aspirante.', 38134),
(200130, 'itIT', '', 'Là! Anche se i discepoli dell\'Umiltà sono alle strette, Voitha tiene duro.', 38134),
(200129, 'itIT', '', 'Quei poveri Aspiranti... gli Indegni li obbligano a cedere alla loro superbia per rinfoltire le proprie armate.', 38134),
(200127, 'itIT', 'Le vostre catene saranno eterne!', '', 38134),
(200126, 'itIT', 'Sforzarsi è inutile, mortali. Non potete sfuggire al vostro destino.', '', 38134),
(200124, 'itIT', 'Un attimo... Tu ci hai aiutato alle Alture Scintillanti, forse puoi farlo ancora.', '', 38134),
(200114, 'itIT', 'Hipokos, forse puoi aggiustare uno di questi Pretori!', '', 38134),
(200113, 'itIT', 'Ho spezzato le tue catene! Presto, prima che tornino le guardie!', '', 38134),
(200111, 'itIT', '', 'La Caccia Selvaggia protegge Selvarden nel nome della regina. Aiutarli potrebbe alleggerire il peso sulle sue spalle!', 38134),
(200110, 'itIT', '', 'Le forze della Spada d\'Ebano sono posizionate qui vicino, ti difenderanno!', 38134),
(200109, 'itIT', 'Trova la caverna dall\'altra parte del baratro. Lì i nostri alleati potranno proteggerti!', '', 38134),
(200108, 'itIT', '', 'Vorrei che la Regina dell\'Inverno potesse salvarli tutti...', 38134),
(200107, 'itIT', '', 'Ogni Semebrado è prezioso, ne abbiamo persi troppi.', 38134),
(200105, 'itIT', '', 'Altri incubi! Proteggimi mentre quieto il Semebrado!', 38134),
(200104, 'itIT', '', 'Dev\'essere la canzone che li attira...', 38134),
(200100, 'itIT', '', 'Perché sei $gvenuto:venuta; qui, $n?', 38134),
(200098, 'itIT', '', 'Farò il possibile per aiutarti a incontrarla, proprio come tu hai fatto per me.', 38134),
(200097, 'itIT', '', 'Grazie per avermi portata con te. Mi dispiace che tu non abbia potuto parlare con la Regina.', 38134),
(200096, 'itIT', 'Di qua, apprendista!', '', 38134),
(200095, 'itIT', '', 'Spriggan! Attenzione!', 38134),
(200094, 'itIT', '', 'L\'incubo è tornato... Devo suonare l\'arpa...', 38134),
(200093, 'itIT', '', 'Troverai il primo nascondiglio lungo questo sentiero. Buona fortuna!', 38134),
(200092, 'itIT', 'No! Il potere della Curatrice era quasi mio.', '', 38134),
(200087, 'itIT', 'Ci vediamo all\'Antro del Letargo. Fate buon viaggio, voi tre.', '', 38134),
(200085, 'itIT', 'Niya, prendi la mia arpa. La sua musica aiuta a placare i Semebrado.', '', 38134),
(200084, 'itIT', 'Il Semebrado starà meglio all\'Antro del Letargo. Forse Niya potrebbe voler venire con te.', '', 38134),
(200083, 'itIT', 'Korenth si caccia sempre nei guai, vediamo di cosa si tratta stavolta.', '', 38134),
(200082, 'itIT', 'Se mi vuoi, vieni a prendermi.', '', 38134),
(200081, 'itIT', 'Grashaal, ti hanno svegliato per sostituirmi?', '', 38134),
(200080, 'itIT', 'Poppante egoista! Non ci faremo da parte. Facciamo come ordinato!', '', 38134),
(200079, 'itIT', 'Kaal, se ti è mai importato di me, fidati di me e fatti da parte.', '', 38134),
(200078, 'itIT', '', 'Draven! Non costringermi ad annientarti.', 38134),
(200076, 'itIT', 'Riesci a sentirlo? Il cambiamento nell\'ordine? Qualcosa sta accadendo, dobbiamo muoverci in fretta!\n\nI tre Mietitori e $gil:la; Calcafauce infusi col potere grezzo dell\'Animum proveniente dai numerosi regni delle Terretetre contro un signore della morte, e noi ci ritroviamo sull\'orlo del suo dominio.\n\nAffrontiamo insieme questo terrore!', '', 38134),
(200075, 'itIT', 'Anche se la mia specie di solito non ha l\'onore di vincolare la propria anima, io non sono un Factotum come gli altri! Che ne dici?', '', 38134),
(200074, 'itIT', 'Non mi imbarcavo in avventure così straordinarie come quelle vissute insieme da... eoni.', '', 38134),
(200073, 'itIT', '', 'Non ho mai creduto che il destino mi avrebbe portata qui, e invece eccoci qua.\n\nQuindi... Come arriviamo alla Fauce?', 38134),
(200071, 'itIT', 'Quello che gli abbiamo fatto non era giustizia, era vendetta.', '', 38134),
(200070, 'itIT', 'Ogni volta che chiudo gli occhi, vedo la mia morte. Vedo...lui ... lì davanti a me, con quella spada maledetta in mano...', '', 38134),
(200069, 'itIT', 'Portatelo al Presidio Eterno finché la nostra Arconte non deciderà il suo destino.', '', 38134),
(200066, 'itIT', '', 'Le tue azioni quest\'oggi mi hanno dimostrato molte cose.', 38134),
(200065, 'itIT', 'Non starò più al fianco degli Indegni. Preferirei dimenticare il mio passato che rendere questo il nostro futuro!', '', 38134),
(200063, 'itIT', 'Codardo senza onore!', '', 38134),
(200062, 'itIT', 'Basta con i giochetti! Guardie! Uccidete questi smidollati!', '', 38134),
(200059, 'itIT', '', 'I gorm si stanno riproducendo a un ritmo allarmante. Elimina le loro larve per aiutare a contenere l\'infestazione.', 38134),
(200057, 'itIT', 'Vuoi aiuto?', 'Vuoi aiuto?', 38134),
(200055, 'itIT', 'Ti aiuto?', 'Ti aiuto?', 38134),
(200054, 'itIT', 'Ti piace succo dolce? Lo prendo.', 'Ti piace succo dolce? Lo prendo.', 38134),
(200052, 'itIT', 'Aiuto in molti modi. Tu chiedi.', 'Aiuto in molti modi. Tu chiedi.', 38134),
(200046, 'itIT', 'I Puriani fanno felici! Sì!', 'I Puriani fanno felici! Sì!', 38134),
(200045, 'itIT', 'Che felice! Grazie!', 'Che felice! Grazie!', 38134),
(200044, 'itIT', 'Tu-uuh mi onori!', 'Tu-uuh mi onori!', 38134),
(200043, 'itIT', 'Tu onori ME? Fantastico!', 'Tu onori ME? Fantastico!', 38134),
(200042, 'itIT', '', 'Per ora, noi Silfi della Notte dobbiamo agire in sua vece.', 38134),
(200037, 'itIT', 'Felice giornata del Factotum!\n\nNoi aggiustiamo, portiamo e serviamo. \n\nSiamo Factotum!', '', 38134),
(200035, 'itIT', 'Molte cose bizzarre sono accadute di recente. Non mi pare di aver mai visto così tanto caos a Selvarden.', '', 38134),
(200032, 'itIT', '', 'Che strano... Continua ad apprendere qui. Ci incontreremo vicino all\'Anfitriona.', 38134),
(200031, 'itIT', '', 'Che cos\'è? Sembra un Kyrian, ma è... deforme.', 38134),
(200028, 'itIT', 'Sciocco! Tutte le casate di Maldraxxus sono contro di te!', '', 38134),
(200027, 'itIT', 'Non puoi vincere, Vyraz! Siamo in troppi!', '', 38134),
(200025, 'itIT', 'Ti ringrazio, Anduin. Sarà bello ritornare al massimo delle forze.', '', 38134),
(200024, 'itIT', 'Quelle ferite sembrano gravi, Thrall. Lascia che me ne occupi.', '', 38134),
(200014, 'itIT', '', 'Kala, oggi sei stata molto cattiva. Chiedi scusa.', 38134),
(200013, 'itIT', '', 'Negli anni che verranno, ti guiderò nell\'uso del bastone per ripristinare il giardino e le sue creature, Zosime.', 38134),
(200012, 'itIT', 'Un soldato dell\'Alleanza ha ucciso molti dei tuoi compagni. Sembrerebbe che una dimostrazione di superiorità sia d\'obbligo.', '', 38134),
(200011, 'itIT', 'Uno dei soldati dell\'Orda sta massacrando i tuoi alleati. Ti consiglio di ricordargli chi comanda.', '', 38134),
(200010, 'itIT', 'Hai eliminato quei ladri con destrezza, dovrò prenderne nota.', '', 38134),
(200009, 'itIT', 'Sembra che i Silfi della Notte siano stati vittima di alcuni furti. Sicuramente il tuo aiuto sarà molto apprezzato.', '', 38134),
(200008, 'itIT', 'Ottima tecnica di spada, mortale. In futuro potrei... avere un impiego da offrirti.', '', 38134),
(200007, 'itIT', 'I duelli dei Venthyr sono una questione di orgoglio e prestigio. Ma per il cartello si tratta anche di soldi.', '', 38134),
(199996, 'itIT', '', 'Tornerò quando avrai successo.', 38134),
(199995, 'itIT', '', 'Canalizza tutto il tuo Animum nel bastone. Questa è una prova del tuo coraggio e di speranza.', 38134),
(199994, 'itIT', '', 'Il coraggio chiama. Ah, Zosime, sei venuta a recuperare il bastone.', 38134),
(199993, 'itIT', '', 'Tu vai avanti, io devo calmare Kala. Ora si agita troppo perché HA i suoi giocattoli.', 38134),
(199992, 'itIT', '', 'Quando arrivi là, suona il vespro... la campana. Speriamo che Korinna appaia e ci dica cosa fare.', 38134),
(199984, 'itIT', 'E ora, Keltesh, hai notato niente di sospetto?', 'E ora, Keltesh, hai notato niente di sospetto?', 38134),
(199951, 'itIT', '', 'Il Giardino del Riposo è un luogo di pace per meditare e riflettere sulle sfide da affrontare... O almeno, lo era. Spero con tutto il cuore che ritorni a esserlo.\n\nLa grande Korinna, una delle prime Ascese, ha creato questo posto per tutti. All\'inizio, era lei che addomesticava le creature del Bastione e donava loro serenità. Ora, a causa della mancanza d\'Animum, quella pace è stata infranta e nessuno può venire qui a riposarsi.', 38134),
(199950, 'itIT', '', 'Ma non ho trovato alcun modo per lasciare la Fauce. Riesco a teletrasportarmi solo a brevi distanze. Fuggire da quest\'incubo sembra... impossibile.', 38134),
(199948, 'itIT', '', 'Che sollievo vederti. Come parte delle mie prove da Aspirante, preservare questo giardino è il mio primo incarico, ma potrebbe andare meglio.\n\nMolti degli animali sono entrati in uno stato di frenesia a causa della siccità d\'Animum. Devo trovare un modo per riportare l\'equilibrio.\n\nMi aiuterai?', 38134),
(199905, 'itIT', 'Un\'udienza del genere è un onore più unico che raro, mortale. Non sprecarla.', '', 38134),
(199904, 'itIT', 'Gli Eletti si sono già riuniti e aspettano l\'arrivo dell\'Arconte. Sarebbe saggio ascoltare e parlare solo se interpellati.', '', 38134),
(199887, 'itIT', '', '<Z\'rali grida dal dolore.>', 38134),
(199886, 'itIT', '', 'Non sapete... cosa state facendo...', 38134),
(199885, 'itIT', '', 'La Luce... trionferà...', 38134),
(199879, 'itIT', '', 'Che risposte desideri?', 38134),
(199876, 'itIT', 'Il nostro obiettivo si trova nelle profondità della prigione. Non lasciare che nessuno ti fermi.', '', 38134),
(199873, 'itIT', '', 'Mi servirà... un po\' di tempo per riprendermi da ciò che è successo a Torgast. Non voglio parlarne ancora.', 38134),
(199869, 'itIT', '', '<Korayn guarda lontano.>', 38134),
(199859, 'itIT', 'Dove dovrei accompagnare i nostri ospiti?', '', 38134),
(199858, 'itIT', '', 'Ci provo, vecchia mia. E so che aiuterai la foresta a crescere di nuovo florida!', 38134),
(199856, 'itIT', '', 'La foresta piange dalle risate. Dama Baccaluna, tu porti felicità.', 38134),
(199855, 'itIT', '', 'Una barba, un cappello... i doni del tempo e... l\'allegria dei Silfi.', 38134),
(199853, 'itIT', 'Rilassati, $p, e prendi pure qualcosa da ciò che è rimasto dei rinfreschi.\n\nDopo una corte così bella, hai certo il diritto di riposarti e goderne i frutti.', '', 38134),
(199850, 'itIT', 'Hai raccolto il giglio che ci serve?', '', 38134),
(199849, 'itIT', 'Non mi piace fare pressioni, ma abbiamo un programma da seguire.', '', 38134),
(199848, 'itIT', '', 'Ben fatto. L\'infestazione è sotto controllo, almeno per ora.', 38134),
(199847, 'itIT', '', 'Questo posto è infestato di gorm. Liberatene, prima che l\'infestazione si diffonda!', 38134),
(199832, 'itIT', '', 'Sarei felice di unirmi a te, mi preparerò per il viaggio.', 38134),
(199828, 'itIT', '', 'Questo sarà uno scherzo epico! Basta... oh! Ehilà!', 38134),
(199826, 'itIT', 'L\'implacabile ricerca della vittoria.', '', 38134),
(199823, 'itIT', '', 'Si manifestano come ombre malevole che vagano nell\'oscurità. Dubito che ci aiuteranno.', 38134),
(199821, 'itIT', '', 'Salute, abitante di Argus! Abbiamo preparato una serie di attività per aiutarti ad adattarti alla tua nuova esistenza.', 38134),
(199820, 'itIT', '', 'Salute, abitante di Draenor! Abbiamo preparato una serie di attività per aiutarti ad adattarti alla tua nuova esistenza.', 38134),
(199818, 'itIT', '', 'Sigilli uguali, la via per il nulla. Solo una via, solo un sigillo del suo tipo.', 38134),
(199816, 'itIT', '', 'Sembra che si sia fatta un potente alleato. Le ombre mormorano di un certo... Carceriere.', 38134),
(199815, 'itIT', '', 'Discerni la via. Penetra le nebbie.', 38134),
(199812, 'itIT', '', 'Pietra e sigillo mostrano la via. Una è irrilevante. Una va seguita.', 38134),
(199811, 'itIT', '', 'Devo preparare la mia stazione, ci vediamo là!', 38134),
(199809, 'itIT', '', 'Visita ogni stazione per saperne di più.', 38134),
(199800, 'itIT', 'Lo vendicheremo qui e ora!', '', 38134),
(199647, 'itIT', '', 'È una scelta impossibile, ma va fatta comunque per il bene del ciclo.', 38134),
(199646, 'itIT', '', 'La siccità ha fatto avvizzire il cuore. La sua forza dev\'essere ripristinata... Anche se abbiamo poco Animum per salvare i nostri boschi appassiti.', 38134),
(199645, 'itIT', '', 'Il Cuore della Foresta è vitale per Selvarden... e per la difesa degli altri regni.', 38134),
(199643, 'itIT', '', 'Possiedo una vasta gamma di conoscenze. Cosa desideri sapere?', 38134),
(199639, 'itIT', '', 'Non capisco.\n\nDa quale pianeta, sistema, entità, regno o forma d\'esistenza hai origine?', 38134),
(199638, 'itIT', '', 'Comprendiamo che la fine dell\'esistenza mortale possa essere brusca e a volte difficile da accettare. Non sei $gil primo:la prima; e non sarai $gl\'ultimo:l\'ultima; a domandarti la natura della tua nuova esistenza.\n\nPrenditi del tempo per comprendere la tua situazione e accettarla. Quando avrai terminato, proseguiremo con l\'orientamento.', 38134),
(199625, 'itIT', '', 'Ben fatto. Ci hai fatto guadagnare del tempo prezioso per potenziare le difese contro i gorm.', 38134),
(199624, 'itIT', '', 'Una schiera di Pungiradici voraci si muove nell\'entroterra! Prendi un Alavelata per sfoltire lo sciame. Non devono raggiungere il Cuore della Foresta!', 38134),
(199615, 'itIT', 'Lo incenerirò col calore di mille soli!', '', 38134),
(199613, 'itIT', 'Recuperare il prigioniero e fuggire. La vittoria sarà nostra.', '', 38134),
(199611, 'itIT', '', 'Sembra che Selvarden si stia sgretolando. Prima i posseduti e ora gli strani mostri che attaccano Tirna Vaal...\n\nTi serve qualcosa, $n?', 38134),
(199609, 'itIT', 'Venite, peccatori! Servite il vostro Maestro!', '', 38134),
(199601, 'itIT', '', 'Ser Herne, chiediamo un\'udienza con la Regina dell\'Inverno.', 38134),
(199600, 'itIT', '', 'Temo che la questione sia seria.', 38134),
(199599, 'itIT', 'Baccaluna. Sei qui per fare altri scherzi? O c\'è qualcosa di grave in arrivo?', '', 38134),
(199596, 'itIT', 'Ti vedo, Calcafauce. Vattene dalla mia torre!', '', 38134),
(199595, 'itIT', '', 'Vieni, mortale. Raggiungimi alla mia corte e presentati dinanzi al Cuore della Foresta. Là, continueremo a parlare.', 38134),
(199594, 'itIT', '', 'Se l\'Esiliato si risvegliasse, potremmo affrontare una minaccia ancora più grande di questa devastante siccità.', 38134),
(199593, 'itIT', '', 'Mio vecchio amico, dopo innumerevoli secoli le nostre paure si sono avverate.', 38134),
(199592, 'itIT', '', 'La Regina dell\'Inverno osserva il messaggio del Primus.', 38134),
(199590, 'itIT', '', 'Sylvanas ha le forze del Carceriere al suo comando. Potrebbe chiamarle per sopraffarci.\n\nFarci catturare tutti non aiuterà Anduin. Non colpiamola finché non sapremo cosa vuole fare.', 38134),
(199585, 'itIT', 'Già. Le opportunità abbondano.', 'Già. Le opportunità abbondano.', 38134),
(199584, 'itIT', 'Questo è il tuo destino. Accettalo!', '', 38134),
(199576, 'itIT', '', 'Allora non sei più un cacciatore.', 38134),
(199573, 'itIT', 'Servo un nuovo padrone! Egli vedrà Selvarden appassire!', '', 38134),
(199559, 'itIT', '', 'È preoccupante. Un antico rituale le ha donato un incredibile potere. Temo che la sua capacità di contenerlo non sia abbastanza.', 38134),
(199557, 'itIT', '', 'Tyrande? Qui? No, non l\'ho vista.', 38134),
(199555, 'itIT', '', 'Mi sono già nascosta in questa caverna. Non possiamo stare a lungo, ma dovrebbe farci guadagnare tempo.', 38134),
(199554, 'itIT', '', 'Mi sono liberata innumerevoli volte, solo per finire di nuovo in gabbia.', 38134),
(199533, 'itIT', 'Ben fatto! Hai finalmente sconfitto i tuoi dubbi più profondi.', '', 38134),
(199532, 'itIT', 'Non senza lottare.', '', 38134),
(199531, 'itIT', 'Cadrai al Tempio della Lealtà. Ti unirai agli Indegni!', '', 38134),
(199530, 'itIT', 'Ti sbagli! Insieme siamo più forti!', '', 38134),
(199529, 'itIT', 'Non ascenderai mai! Sarai sempre e solo il servo di Kleia!', '', 38134),
(199528, 'itIT', 'Questo sarà il tuo ultimo fallimento.', '', 38134),
(199527, 'itIT', 'Ho fallito quella prova così tante volte... Ma non oggi.', '', 38134),
(199526, 'itIT', 'Insieme.', '', 38134),
(199524, 'itIT', 'Io, no... Noi... possiamo farcela! NON sono solo.', '', 38134),
(199523, 'itIT', 'IO... NON... POSSO... FARCELA!', '', 38134),
(199522, 'itIT', 'Mai abbastanza forte... Mai... all\'altezza...', '', 38134),
(199520, 'itIT', '', 'È tempo di provare la tua nuova armatura.', 38134),
(199515, 'itIT', 'È vano... resistere...', '', 38134),
(199514, 'itIT', '', 'Dobbiamo inseguire Lysonia a tutti i costi. Non dobbiamo permetterle di colpire di nuovo!', 38134),
(199510, 'itIT', '', 'Be\', addio alla nostra fortuna!', 38134),
(199508, 'itIT', 'Non credo... di riuscire a farcela...', '', 38134),
(199507, 'itIT', 'Ora distruggiamo il loro piccolo progetto!', '', 38134),
(199485, 'itIT', '', 'Gor\'groth ha parlato di pietre di protezione. Trovale e usale per liberare Mulgrin.', 38134),
(199484, 'itIT', 'La Casata delle Piaghe... annientata! Dov\'è Stradama? Devo creare la pozione... Devo... devo...', '', 38134),
(199483, 'itIT', 'Nessuno può accusare apertamente i Lich di tradimento, così ho ideato una nuova pozione, un\'ultima spiaggia per proteggere Stradama in caso dovesse averne bisogno. Prego che non gli serva mai.', '', 38134),
(199482, 'itIT', 'La Casata dei Rituali si è offerta di fornirci dell\'Animum. Io e Stradama sospettiamo che ci sia qualcosa sotto... ma la nostra casata non è in condizioni di rifiutare. Dobbiamo rimanere vigili.', '', 38134),
(199481, 'itIT', 'Non avete scampo! Nulla sfugge alla Fauce!', '', 38134),
(199480, 'itIT', 'Il mio genio... deve... sopravvivere...', '', 38134),
(199477, 'itIT', 'Certamente, in assenza del Primus, la guerra civile è all\'orizzonte. Il Margravio Stradama mi confida le sue paure. Senza Animum, il nostro lavoro, e perciò anche il nostro potere, ristagna.', '', 38134),
(199471, 'itIT', 'Aspetta! Non fargli del male!', '', 38134),
(199468, 'itIT', 'Il tempo tuo è finito. Nun hai più potere. Ora il vecchio Mueh\'zala viene a riscuotere.', '', 38134),
(199467, 'itIT', 'Che bel Loa della Morte che sei, Bwonsamdi. Fare patti anziché pigliare ciò che è tuo.', '', 38134),
(199466, 'itIT', 'Corri dalla Regina dell\'Inverno coi problemi tuoi adesso?', '', 38134),
(199465, 'itIT', 'No, no, no. Nun è come pensi! Il capo si sta pigliando i Loa mentre dormono e...', '', 38134),
(199464, 'itIT', '', 'Le liti fra Loa non mi riguardano. Non coinvolgere Selvarden nei tuoi futili alterchi.', 38134),
(199463, 'itIT', 'Ué, maestà! Vengo in pace! Dobbiamo cooperare se vogliamo mazziare il vecchio Mueh\'zala.', '', 38134),
(199462, 'itIT', '', 'Che cosa significa questa intrusione, mortale?', 38134),
(199461, 'itIT', 'Ué, che si dice, Regina di Ghiaccio! Nun ci vediamo da moooooolto tempo, eh? Mi piace come hai sistemato chesto posto!', '', 38134),
(199459, 'itIT', 'Sembra che qualcuno sia qui per rubare i segreti della mia genialità!', '', 38134),
(199458, 'itIT', '', 'Ah-hah! Allora avevi DAVVERO sbagliato la procedura!', 38134),
(199457, 'itIT', 'Be\', ehm, ci sono molti fattori complessi che entrano in gioco nella preparazione della carne. Forse mi serviva un tocco un po\' più...', '', 38134),
(199456, 'itIT', '', 'Uh... L\'ho visto muoversi! Quelle parti Kyrian stanno lottando per prendere il controllo del Costrutto!', 38134),
(199455, 'itIT', 'Sciocchezze! Una volta inserita un\'anima di Maldraxxus, reprimerà ogni ricordo rimanente dell\'indottrinamento del Bastione!', '', 38134),
(199454, 'itIT', '', 'Noterà anche che hai fallito nell\'eliminare i ricordi dalla carne, prima di assemblarla!', 38134),
(199453, 'itIT', 'Quando Cucicarne tornerà, saprà che io, Malkrex, ho portato a termine la sua ricerca: impiantare carne Kyrian nel Costrutto definitivo!', '', 38134),
(199452, 'itIT', '', 'Eccoti qui! La Regina sta per arrivare!', 38134),
(199450, 'itIT', 'Ho sentito dire che Derawyn e Korenth facevano scherzi incredibili. È vero?', '', 38134),
(199449, 'itIT', '', 'Grazie.', 38134),
(199448, 'itIT', 'Be\'... È meglio che io rimanga con te. Per protezione.', '', 38134),
(199447, 'itIT', '', 'Me la caverò, Te\'zan. Va\' avanti.', 38134),
(199446, 'itIT', 'Pensi che sia sicuro? Ci sono molti altri Silfi mascherati qui in giro.', '', 38134),
(199445, 'itIT', '', 'Ti raggiungerò a Tirna Vaal. Voglio... dare un ultimo saluto.', 38134),
(199442, 'itIT', '', 'Il cuore della foresta! No!', 38134),
(199441, 'itIT', '', 'Senza l\'Animum del cuore, tantissimi Semebrado periranno d\'inedia...', 38134),
(199440, 'itIT', 'Niya... mi... dispiace...', '', 38134),
(199439, 'itIT', '', 'Non ruberai mai più!', 38134),
(199438, 'itIT', 'Non mi piace il modo in cui mi guardi. \n\nSai che nell\'aldilà si dovrebbe trovare la pace eterna, vero?', '', 38134),
(199436, 'itIT', '', 'Una strana magia che si impossessa della nostra gente, una siccità d\'Animum che devasta la terra e il cuore di un bosco che viene rubato.\n\nSta succedendo tutto troppo in fretta per i miei gusti.', 38134),
(199427, 'itIT', 'Sono ancora in piedi, Principe. Anche se domattina dubito che potrò volare.', '', 38134),
(199426, 'itIT', 'Draven! Stai bene?', '', 38134),
(199424, 'itIT', 'Non c\'è pietra che io non possa rompere, Grashaal!', '', 38134),
(199423, 'itIT', 'Non puoi sfuggire alla mia ira, Kaal!', '', 38134),
(199422, 'itIT', '', 'Le nostre strade si separano nel bosco crepuscolare. Tirna Vaal ti accoglie a casa.', 38134),
(199421, 'itIT', 'Mi serve più Animum!', '', 38134),
(199420, 'itIT', 'Portatemi il loro Animum, eroi!', '', 38134),
(199419, 'itIT', '', 'La speranza cresce tra la cenere. Il ciclo va avanti, sempre.', 38134),
(199417, 'itIT', 'Campioni, ripulirò i cieli!', '', 38134),
(199414, 'itIT', 'Tutti si inchineranno! Tutti marciranno!', '', 38134),
(199413, 'itIT', 'Selvarden sarà dei padroni!', '', 38134),
(199412, 'itIT', 'Be\', va bene. Ma è meglio che dopo me lo ridiate!', '', 38134),
(199411, 'itIT', '', 'L\'Animum dato ritornerà col tempo.', 38134),
(199410, 'itIT', '', 'L\'oscurità si avvicina. Le stelle risplendono in nostra difesa.', 38134),
(199399, 'itIT', 'Te lo assicuro, non sono così noioso.\n\nIl mio caro amico Teotar si ferma regolarmente per assicurarsi che io sia protetto dalla crudeltà della Luce.\n\nUn vero gentiluomo, quel Venthyr.', '', 38134),
(199397, 'itIT', 'Certo che no, mi piace quassù.\n\nPace e silenzio. Nessuno che mi disturbi... di solito.\n\n<Sire Torvans ti mostra il suo omonimo.>\n\nFinalmente mi sto mettendo in pari con tutte le letture che volevo fare da anni.\n\nVorrei solo avere più libri.', '', 38134),
(199396, 'itIT', 'Sì, cosa c\'è?\n\nSpero tu avessi una ragione molto buona per interrompere così sgarbatamente la mia lettura.', '', 38134),
(199395, 'itIT', 'Ne abbiamo persi tanti. Così tanti morti...\n\n<Ara\'lon si chiude in una contemplazione silenziosa.>', '', 38134),
(199384, 'itIT', 'Buona fortuna. Il nuovo capo è un po\' suscettibile.', '', 38134),
(199383, 'itIT', 'Lascia che ti apra quella porta un attimo.', '', 38134),
(199382, 'itIT', '', 'Sei la vergogna della nostra razza, Draven! Se non vorrai servire, allora cadrai!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(199380, 'itIT', 'Kaal! Grashaal! Non fatevi accecare da una lealtà malriposta! L\'accordo siglato da Denathrius distruggerà Revendreth!', '', 38134),
(199379, 'itIT', '', 'Litogeni! Assalite questi usurpatori!', 38134),
(199378, 'itIT', 'Serve... tutta la mia forza... per bloccare... il loro assalto!', '', 38134),
(199377, 'itIT', 'In arrivo! Al riparo!', '', 38134),
(199372, 'itIT', '', 'Queste vili creature conosceranno la furia della Caccia Selvaggia!', 38134),
(199370, 'itIT', 'La frantumazione del cielo ha reso tutti agitati, incluso il Flagello. Rinfreschiamo un po\' le tue abilità in combattimento.', '', 38134),
(199369, 'itIT', 'Ti do il benvenuto a bordo! Re Wrynn avrà bisogno di ogni eroe dell\'Alleanza in ottima forma per gli orrori che usciranno dalla Corona di Ghiaccio.', '', 38134),
(199368, 'itIT', '', 'Sembra che il Flagello sia irrequieto da quando il cielo si è spezzato, è nostro compito tenerli d\'occhio. Nel frattempo, assicuriamoci che le tue abilità in combattimento siano al meglio.', 38134),
(199367, 'itIT', '', 'Salve! Ti do il benvenuto nella pattuglia della Corona di Ghiaccio.', 38134),
(199365, 'itIT', '', 'Come posso essere d\'aiuto?', 38134),
(199363, 'itIT', '', 'Derawyn... no! Quei mostri la pagheranno!', 38134),
(199362, 'itIT', '', 'Il ciclo... va avanti... e così... anche tu...', 38134),
(199361, 'itIT', '', 'Korenth non c\'è più. Non perderò anche te!', 38134),
(199360, 'itIT', '', 'No... l\'Animum... è per i Semebrado...', 38134),
(199357, 'itIT', 'Hipokos ha trovato e riparato una Pretora tra i Kyrian caduti. Dal momento che non possiede un\'anima, dovrebbe essere in grado di attraversare la barriera.\n\nCon la tua guida, puoi usarla per disattivare le difese e liberarci la strada.', '', 38134),
(199356, 'itIT', 'Per Azeroth!', '', 38134),
(199353, 'itIT', 'Dobbiamo scappare e informare l\'Arconte di quello che viene fatto qui.', '', 38134),
(199352, 'itIT', 'Un tempo lo usavo per portare dei Semebrado nel mio vecchio bosco. Spero ti sia utile.', '', 38134),
(199350, 'itIT', 'Grazie per essere $gvenuto:venuta; a trovarci. A Selvarden farebbe comodo altra gente come te.', '', 38134),
(199349, 'itIT', 'Ho sconfitto Krexus in persona! Voi due non siete nulla!', '', 38134),
(199348, 'itIT', 'Conficcherò la mia lama nel tuo cranio, traditore! Per Krexus!', '', 38134),
(199345, 'itIT', 'Stolto di un Mograine, dai troppo valore alla lealtà rispetto al potere. Ma la vittoria sta nella scelta dei propri alleati. E col loro aiuto, ora rivendicherò il ruolo di Margravio!', '', 38134),
(199344, 'itIT', 'I tuoi piani hanno causato la tua stessa rovina. Ora ti finiremo!', '', 38134),
(199343, 'itIT', 'Ecco il ritorno del Barone ribelle col suo cagnolino mortale. I costrutti non sono riusciti a finirvi, e neanche la Fauce. Deludente.', '', 38134),
(199342, 'itIT', '', 'Basta, Lorkron! Se mi conosci davvero, allora dovresti avere paura.', 38134),
(199341, 'itIT', 'Ci hai mandato in una caccia senza speranza e ora sei tu la preda... e io ti conosco bene.', '', 38134),
(199340, 'itIT', '', 'I Silfi mascherati... Questo è il destino che li aspetta?!?', 38134),
(199339, 'itIT', 'Non riconosci le tue prede? Le lame che impugnano? I volti dietro le loro maschere?', '', 38134),
(199337, 'itIT', '', 'Conosco quella voce, ma è caduto in battaglia...', 38134),
(199336, 'itIT', 'Korayn... la solita astuta cacciatrice.', '', 38134),
(199335, 'itIT', '\"Conosci la tua preda\" è la prima lezione che mi hai insegnato... e che hai dimenticato.', '', 38134),
(199334, 'itIT', '', 'Lorkron... Cosa ti è successo?', 38134),
(199332, 'itIT', '', 'Ritorna al Bacino Scintillante, la mia caccia è appena iniziata.', 38134),
(199331, 'itIT', '', 'Un caro amico... trasformato in un temibile nemico. Dev\'essere ucciso.', 38134),
(199330, 'itIT', 'No! Non fallirò di nuovo la mia caccia. Fa\' attenzione alle ombre, Korayn...', '', 38134),
(199329, 'itIT', 'La tua caccia finisce qui, Korayn!', '', 38134),
(199325, 'itIT', '', 'Fermi.', 38134),
(199323, 'itIT', '', 'Attenzione. I suoi occhi possono penetrare il mio incantesimo.', 38134),
(199322, 'itIT', '', 'Avanti, verso la vittoria!', 38134),
(199318, 'itIT', 'E io mi metterò in contatto con i miei agganci di fiducia a Maldraxxus.', '', 38134),
(199317, 'itIT', '', 'Sono in debito con te, mi ero quasi persa.', 38134),
(199316, 'itIT', '', 'Il mio Vincolo dell\'Anima... La Fauce...', 38134),
(199310, 'itIT', 'Il Rituale dell\'Assoluzione aiuterà queste anime a liberarsi dei fardelli delle loro vite mortali.', 'Il Rituale dell\'Assoluzione aiuterà queste anime a liberarsi dei fardelli delle loro vite mortali.', 38134),
(199304, 'itIT', 'Ben fatto! Ora vediamo come... Aspetta, ma quella è... Alle armi! Il Flagello sta attaccando!', '', 38134),
(199303, 'itIT', '', 'Notevole! Ora vediamo... Aspetta, ma quella è... Preparatevi a combattere! Il Flagello sta attaccando!', 38134),
(199302, 'itIT', 'La madre delle piaghe... sta arrivando...', '', 38134),
(199301, 'itIT', 'Custodi del marciume, io vi evoco!', '', 38134),
(199298, 'itIT', 'Servitori, è ora di mangiare!', '', 38134),
(199296, 'itIT', 'Alzatevi, servitori della piaga!', '', 38134),
(199291, 'itIT', 'Benvenuti, vi stavamo aspettando!', '', 38134),
(199288, 'itIT', '', 'La conoscenza... che avremmo potuto avere.', 38134),
(199287, 'itIT', '', 'I manufatti saranno rivendicati nel nome del Maestro!', 38134),
(199286, 'itIT', 'Hai scoperto un modo per liberarmi?', '', 38134),
(199285, 'itIT', 'Capo... si infuria...', '', 38134),
(199284, 'itIT', 'Questa macchina non va!', '', 38134),
(199283, 'itIT', 'No prendi... cristallo...', '', 38134),
(199282, 'itIT', 'Spacco e brillo!', '', 38134),
(199281, 'itIT', 'Faccio... mio meglio...', '', 38134),
(199280, 'itIT', 'Tizi ossei prepotenti!', '', 38134),
(199279, 'itIT', 'Sono io... gente... brutta?', '', 38134),
(199278, 'itIT', 'Gente brutta!', '', 38134),
(199275, 'itIT', 'Tutti i Maldraxxi sono così assetati di sangue?', '', 38134),
(199274, 'itIT', 'Sento puzza della mano di Cucicarne. Crea un diversivo mentre do la caccia ai suoi discepoli!', '', 38134),
(199273, 'itIT', '', 'Senza la scheggia dello specchio di Mateo, non raggiungeremo in tempo il medaglione.', 38134),
(199271, 'itIT', 'Conosco i tuoi segreti!', 'Conoscerò i tuoi segreti!', 38134),
(199266, 'itIT', 'Maldraxxus sarà nostra!', 'Maldraxxus sarà nostra!', 38134),
(199265, 'itIT', 'Ehi! Il capo dice che nessuno può entrare finché i denti aguzzi non sono a posto.', '', 38134),
(199259, 'itIT', 'Per essere trasformati in armi per il Carceriere.', '', 38134),
(199246, 'itIT', '', 'È troppo... tardi... per me...', 38134),
(199245, 'itIT', '', 'Una tale... oscurità...', 38134),
(199244, 'itIT', 'Grazie, andrò avanti! Immagino ti possa servire un po\' d\'aiuto.', '', 38134),
(199243, 'itIT', 'Questi sono i Silfi più cattivi che abbia mai visto!', '', 38134),
(199242, 'itIT', 'Nonostante tutto quello che Sylvanas ha fatto, non avrei mai pensato che potesse arrivare a tanto.', '', 38134),
(199241, 'itIT', '', 'La gratitudine fiorisce nella tempesta. Le nostre vie convergono nel pantano.', 38134),
(199240, 'itIT', 'Tante anime si riversano nella Fauce. I morti di Azeroth... di Draenor... di mondi oltre la nostra immaginazione. Tutti condannati a questo luogo.', '', 38134),
(199239, 'itIT', '', 'Magia oscura... un nemico familiare...', 38134),
(199238, 'itIT', 'La dimensione dell\'esercito del Carceriere rivaleggia con la Legione... e cresce sempre più.', '', 38134),
(199237, 'itIT', '', 'Statemi vicini. Non dobbiamo attirare l\'attenzione.', 38134),
(199229, 'itIT', 'Mentre ci riprendiamo i nostri luoghi sacri, gli Ascesi hanno ricominciato l\'addestramento. Saranno impazienti di vedere l\'ultimo tempio riconquistato.', '', 38134),
(199228, 'itIT', '[DEPRECATED] Ya best be turnin\' around, Bwonsamdi. Dis be none of ya concern.', '', 38134),
(199227, 'itIT', 'Chesto è il marchio di Mueh\'zala. Oh, no, no, no! Mi stai dicendo che c\'è il capo dietro tutto chesto???', '', 38134),
(199226, 'itIT', 'Oblio del Peccato sarà al sicuro grazie ai tuoi sforzi.', '', 38134),
(199225, 'itIT', 'Nemici in arrivo! Agli specchi! Chiunque cerchi di penetrare le mura incontrerà la Luce!', '', 38134),
(199223, 'itIT', 'I bruti Litogeni sono forti, ma la loro mira lascia a desiderare. Aiutali a dare manforte ai nostri alleati sui bastioni quaggiù.', '', 38134),
(199218, 'itIT', '', 'Le tue gesta aiuteranno a riportare Revendreth al suo scopo originario.', 38134),
(199217, 'itIT', '', 'Il serraglio è stato invaso da un numero spropositato di anime. Non possiamo lasciare che il loro Animum vada sprecato. Agisci in fretta, prima che finiscano nella Fauce!', 38134),
(199211, 'itIT', '', 'Non avrai bisogno di lanciarli nuovamente finché il loro effetto non starà per svanire.', 38134),
(199210, 'itIT', '', 'Lancia Agonia e Corruzione sul Manichino d\'Allenamento.', 38134),
(199209, 'itIT', '', 'Agonia e Corruzione infliggono danni da ombra periodici sul tuo bersaglio, tormentandolo mentre lanci incantesimi aggiuntivi.', 38134),
(199208, 'itIT', '', 'Ora, chiunque sia così folle da affrontarti ne subirà le conseguenze.', 38134),
(199207, 'itIT', 'Non avrai bisogno di lanciarli nuovamente finché il loro effetto non starà per svanire.', '', 38134),
(199206, 'itIT', 'Lancia Agonia e Corruzione sul manichino.', '', 38134),
(199205, 'itIT', 'Agonia e Corruzione infliggono danni da ombra periodici sul tuo bersaglio, tormentandolo mentre lanci incantesimi aggiuntivi.', '', 38134),
(199204, 'itIT', 'Ora, chiunque sia così folle da affrontarti ne subirà le conseguenze.', '', 38134),
(199183, 'itIT', 'L\'Accusatrice deve permetterci di dargli la caccia e distruggerlo!', '', 38134),
(199182, 'itIT', 'Questi Venthyr sono in combutta con un Lich???', '', 38134),
(199181, 'itIT', '', 'Partiamo subito per Maldraxxus. Vyraz risponderà dei suoi crimini.', 38134),
(199180, 'itIT', 'Ci sarà tempo per parlare più tardi, figlio mio. Te lo giuro.', '', 38134),
(199179, 'itIT', 'Draka! Dobbiamo tornare subito a Maldraxxus. Il Margravio Krexus...', '', 38134),
(199178, 'itIT', 'No... allora il mio avvertimento è arrivato troppo tardi! Siamo stati traditi da uno dei nostri!', '', 38134),
(199177, 'itIT', '', 'Il nostro Margravio è stato ucciso, Brandicenere. II Prescelti hanno bisogno della tua lama, ora più che mai.', 38134),
(199174, 'itIT', 'Devo tornare ad Azeroth e dire ai nostri alleati cos\'ho scoperto, padre. Ma se il dovere lo permetterà, starò ancora una volta al tuo fianco.', '', 38134),
(199173, 'itIT', 'Gran Signore Domadraghi! Siamo fuggiti dalla Fauce. Grazie per il tuo aiuto.', '', 38134),
(199172, 'itIT', '', 'E ora finiamo il lavoro.', 38134),
(199171, 'itIT', '', 'Verrà il tuo momento. Questo è solo l\'inizio!', 38134),
(199170, 'itIT', '', 'Scoprirò la verità!', 38134),
(199169, 'itIT', '', 'Sei stata sciocca a sfidare la potenza di un\'Eletta.', 38134),
(199168, 'itIT', '', 'Il nostro reame giace in rovina. Gli Indegni ci libereranno!', 38134),
(199167, 'itIT', '', 'Cerca di salvarla quanto ti pare. Ha visto la verità!', 38134),
(199154, 'itIT', '', 'La nostra via di fuga... Speriamo.', 38134),
(199153, 'itIT', 'Non sembrava importante. Le forze del Carceriere ci hanno badato poco. Cosa pensi che sia?', '', 38134),
(199152, 'itIT', 'Ho visto un obelisco fatto di antica pietra sul crinale più alto.', '', 38134),
(199151, 'itIT', 'Cerchiamo una pietra di transito oltre il Calderone. L\'avete vista?', '', 38134),
(199150, 'itIT', 'Ricevuto. Fa\' strada.', '', 38134),
(199149, 'itIT', '', 'Vorrei avere più tempo per farti riprendere, ma dobbiamo muoverci.', 38134),
(199148, 'itIT', 'Per questo hai la mia eterna gratitudine.', '', 38134),
(199147, 'itIT', 'Quasi, Baine, se non fosse stato per il suo aiuto.', '', 38134),
(199146, 'itIT', 'Anduin? Pensavo fosse arrivata la mia fine...', '', 38134),
(199145, 'itIT', 'Baine, riesci a sentirmi?', '', 38134),
(199144, 'itIT', 'Si sta riprendendo.', '', 38134),
(199143, 'itIT', '', 'Resisti, Baine! Non farti prendere dal Carceriere!', 38134),
(199142, 'itIT', 'Devi sconfiggerlo, presto!', '', 38134),
(199141, 'itIT', 'La maledizione gli sta straziando l\'anima!', '', 38134),
(199140, 'itIT', 'Quando spezzerò la lama, preparati a qualunque cosa si presenti.', '', 38134),
(199139, 'itIT', '', 'Quella è... Non può essere...', 38134),
(199138, 'itIT', '', 'Umpf, non vedi che sono occupata?', 38134),
(199137, 'itIT', 'Ma... l\'Antico... aveva promesso...', '', 38134),
(199136, 'itIT', 'Mi prenderò tutto il tuo Animum!', '', 38134),
(199135, 'itIT', 'Io, un traditore? Dalla presenza $gdel:della; Calcafauce, posso vedere che sei tu il vero traditore. Occupati di questi due e riportami il frammento di specchio nell\'Archivam.', '', 38134),
(199134, 'itIT', 'Denathrius? Come puoi schierarti con lui dopo che ha gettato la Curatrice nella Fauce? Traditore.', '', 38134),
(199133, 'itIT', 'Emil! Ti stavamo cercando... Il Sire vuole il frammento di specchio che la Curatrice ti ha dato.', 'Emil! Ti stavamo cercando... Il Sire vuole il frammento di specchio che la Curatrice ti ha dato.', 38134),
(199130, 'itIT', '', 'Korenth... riprenderemo il cuore della foresta. Lo giuro.', 38134),
(199127, 'itIT', 'Sali gli scalini quando lo reputi opportuno.', '', 38134),
(199126, 'itIT', '', 'Korenth! Dove sei?', 38134),
(199125, 'itIT', '', 'Cercherò i sopravvissuti. Incontriamoci all\'interno del bosco.', 38134),
(199121, 'itIT', 'Sono Ara\'lon. Combatto per la Caccia Selvaggia.\n\nUn tempo servivo uno splendido bosco chiamato Tirna Noch, mi occupavo degli spiriti durante il lungo inverno. Ma quel bosco cadde, e trovai un nuovo scopo.\n\nServo Selvarden.', '', 38134),
(199119, 'itIT', 'Torneremo al bosco non appena sarà possibile. Ti ringrazio.', '', 38134),
(199118, 'itIT', 'Le cascate sono state invase. Penso... che ci stiano dando la caccia.', '', 38134),
(199116, 'itIT', 'Attenzione. Questo posto è pieno di creature orribili, grandi e piccole.', '', 38134),
(199115, 'itIT', 'Da questa parte! Prima che ti vedano!', '', 38134),
(199113, 'itIT', 'Dev\'essere il pugnale. Il sangue di Baine macchia ancora la lama.', '', 38134),
(199111, 'itIT', 'Ha preso... il cuore...', '', 38134),
(199110, 'itIT', 'Krelnor... mascherato... Pantano Celato...', '', 38134),
(199109, 'itIT', 'Se il Carceriere arriverà, assaggerà il mio acciaio!', '', 38134),
(199108, 'itIT', 'Finalmente, un\'arma da guerrieri!', '', 38134),
(199107, 'itIT', 'Meglio di niente, immagino. Cerchiamo ancora.', '', 38134),
(199106, 'itIT', 'Umpf, impugnatura troppo lunga.', '', 38134),
(199105, 'itIT', 'Troppa lama e poco manico.', '', 38134),
(199104, 'itIT', 'Un\'arma decente. Non ho mai capito perché l\'Alleanza sia ossessionata dalle spade.', '', 38134),
(199103, 'itIT', 'Continuiamo a cercare.', '', 38134),
(199102, 'itIT', 'Non sono mai stato un tipo da bastoni, ma almeno non si spezzerà in mischia.', '', 38134),
(199101, 'itIT', 'Suppongo che sia abbastanza pesante. Vediamo cos\'altro troviamo, però.', '', 38134),
(199100, 'itIT', 'Una balestra? Preferisco affrontare i miei nemici faccia a faccia.', '', 38134),
(199099, 'itIT', 'Tuttavia, continuate a provocarmi. Molto bene. La pagherete!', '', 38134),
(199097, 'itIT', 'Ora basta! Sono stato fin troppo ospitale con voi, addirittura cortese.', '', 38134),
(199094, 'itIT', '', 'La loro agonia riecheggerà in tutti i regni della Morte!', 38134),
(199093, 'itIT', '', 'Ti ho forgiato un\'arma senza eguali. Se spezzi quella lama, io spezzerò te.', 38134),
(199092, 'itIT', '', 'Sì! Sì! Li farò a pezzetti!', 38134),
(199088, 'itIT', '', 'Tingerò le pareti con le vostre viscere!', 38134),
(199086, 'itIT', '', 'Li squarterò per renderli bocconcini!', 38134),
(199079, 'itIT', 'Ottimo lavoro. Informa Draka che siamo pronti a combattere.', '', 38134),
(199076, 'itIT', 'Potete fidarvi o no. Non è questo ciò che i Maldraxxi rappresentano.', '', 38134),
(199072, 'itIT', '', 'Ho sentito dire buone cose su di te, $p. Puoi riposare qui.', 38134),
(199058, 'itIT', '', 'Continuerò l\'addestramento mentre sarai via.\n\nAlla prossima, Calcafauce.', 38134),
(199053, 'itIT', '', 'Chi sei... aspetta, tu hai aiutato Derawyn e Te\'zan...', 38134),
(199051, 'itIT', '', 'Vieni, il Margravio Krexus ci attende.', 38134),
(199050, 'itIT', '', '<Sembra essere in pace.>', 38134),
(199049, 'itIT', '', 'La stessa arroganza che ti ha rovinato quand\'eri in vita...', 38134),
(199048, 'itIT', '', 'Siamo stati fortunati. Cacceremo la prossima volta.', 38134),
(199045, 'itIT', '', '<Un Kyrian ferito è strisciato fuori per morire qui lontano da tutto.>$B$B<Nella sua mano stringe una scatola.>', 38134),
(199044, 'itIT', 'Un... risultato... inaspettato...', '', 38134),
(199043, 'itIT', 'Ah, nuove cavie impazienti!', '', 38134),
(199040, 'itIT', '', 'Ora Throg combatterà con te. Devo parlare con la ciurma riguardo alla pioggia.', 38134),
(199031, 'itIT', 'Fanno... esperimenti su di noi...', '', 38134),
(199030, 'itIT', 'Il tabulatore conta i cadaveri come fossero soldi.', '', 38134),
(199028, 'itIT', '<Il volto del Silvar è vuoto e penetrante. È caduto vittima di un\'imboscata.>', '', 38134),
(199027, 'itIT', 'Questa magia... Cosa stanno facendo ai miei fratelli?!?', '', 38134),
(199026, 'itIT', '', '<Il volto della Vorkai si è contorto in una smorfia di rabbia. È morta sovrastata dal gran numero di nemici.>', 38134),
(199022, 'itIT', 'Le vostre morti non saranno altrettanto rapide!', '', 38134),
(199020, 'itIT', 'Avete ucciso il mio preferito!', '', 38134),
(199019, 'itIT', 'Tesoro mio! No!', '', 38134),
(199018, 'itIT', 'Hecutis, qui! Ora!', '', 38134),
(199017, 'itIT', 'Squartarovina! A me!', '', 38134),
(199016, 'itIT', 'Bargast, vieni a me!', '', 38134),
(199015, 'itIT', '<Il volto del Vorkai è segnato dalla magia oscura. È stato attirato in trappola.>', '', 38134),
(199013, 'itIT', '', '$gSciocco:Sciocca;, ci hai portato da Emil! Il suo frammento di specchio sarà nostro!', 38134),
(199012, 'itIT', 'Spero che questa meraviglia sia letale come sembra!', '', 38134),
(199011, 'itIT', 'Ehi tu! Ti ha $gmandato:mandata; la Curatrice? Vieni, c\'è... cosa?!?', '', 38134),
(199010, 'itIT', 'L\'attesa di poter usare la reliquia mi sta uccidendo! Anche se probabilmente ucciderà voi.', '', 38134),
(199009, 'itIT', 'Vediamo se le leggende su questa reliquia sono vere!', '', 38134),
(199008, 'itIT', '', 'I miei cacciatori e cacciatrici fanno di rado tardi al rapporto. Trovali.', 38134),
(199007, 'itIT', '', 'Sono il Capitano Korayn, della Caccia Selvaggia. Servo la Regina dell\'Inverno e proteggo Selvarden.\n\nSappi che la mia lama porrà rapidamente fine a chiunque, e qualunque cosa, minacci la foresta.\n\nSenza se e senza ma.', 38134),
(199005, 'itIT', '', 'Nessuno sfugge alla Caccia Selvaggia.', 38134),
(199004, 'itIT', 'Quest\'anima non rimarrà in silenzio, non importa quanto stringa le catene.', '', 38134),
(199003, 'itIT', 'Quest\'anima resiste al mio controllo con tutte le sue forze.', '', 38134),
(198998, 'itIT', '', 'Uno sforzo inutile...', 38134),
(198997, 'itIT', '', 'Alimenterai le nostre armate!', 38134),
(198993, 'itIT', '', 'Nel suo stato attuale, Kael\'thas Solealto è un pericolo per tutti.', 38134),
(198991, 'itIT', '', 'Non lasceremo cadere questo bosco!\n\nLa Regina mi ha affidato la sicurezza dell\'Antro del Letargo e di tutti gli spiriti al suo interno. Non possiamo fallire.', 38134),
(198990, 'itIT', '', 'Vieni. Questo è l\'unico sentiero per la Casata dei Prescelti.', 38134),
(198989, 'itIT', 'Dobbiamo salvarli! Presto!', '', 38134),
(198988, 'itIT', '', 'Il Margravio Krexus è il più forte di tutti noi. Non mostrargli alcuna paura, solo rispetto.', 38134),
(198985, 'itIT', '', 'I Prescelti combattono con forza e onore. Schiacceremo i traditori e porremo fine a questa guerra.', 38134),
(198984, 'itIT', 'Nota IV:\n\nSono piuttosto sicuro che Emil sia nella piccola catacomba alla base delle torri di guardia ormai in rovina. Se solo potessi raggiungerlo...\n\nI progressi nelle catacombe si sono arrestati. I costrutti di Pietra del Peccato rappresentano una minaccia contro cui persino i nostri più forti arrancano.\n\nPensavo che fosse arrivata la salvezza quando abbiamo visto gli esploratori dell\'Archivam... ma hanno puntato le loro lame contro di noi, la loro stessa gente. La ragione di questo tradimento mi sfugge. Posso solo sperare che si stanchino di cacciare prima di trovarmi.', '', 38134),
(198983, 'itIT', '', 'Salute, mortale. Dama Baccaluna ha parole di stima per te.', 38134),
(198982, 'itIT', '', 'Mai! I vostri padroni pagheranno per ciò che hanno fatto!', 38134),
(198981, 'itIT', 'Unisciti a noi, Niya. Indossa la maschera e servi i padroni!', '', 38134),
(198978, 'itIT', '', 'Aspetta... questa pozione... Marileth?', 38134),
(198958, 'itIT', 'Io non servo te, rispondo solo alla Contessa.', 'Io non servo te, rispondo solo alla Contessa.', 38134),
(198957, 'itIT', 'Non preoccuparti per le questioni che non puoi sperare di comprendere.', 'Non preoccuparti per le questioni che non puoi sperare di comprendere.', 38134),
(198952, 'itIT', 'Quando arrivai a Revendreth la mia penitenza fu servire la Contessa e i nobili della sua corte. Dopo millenni, la mia penitenza è diventata un desiderio. Ora resto volontariamente qui come servitrice personale e confidente della Contessa. Mi occupo di ogni sua cosa e metto sottochiave i suoi segreti più oscuri.\n\nPer il tuo bene, spero che non mi serva un\'altra chiave.', 'Quando arrivai a Revendreth la mia penitenza fu servire la Contessa e i nobili della sua corte. Dopo millenni, la mia penitenza è diventata un desiderio. Ora resto volontariamente qui come servitrice personale e confidente della Contessa. Mi occupo di ogni sua cosa e metto sottochiave i suoi segreti più oscuri.\n\nPer il tuo bene, spero che non mi serva un\'altra chiave.', 38134),
(198951, 'itIT', 'Conquisterò una posizione all\'esterno!', '', 38134),
(198950, 'itIT', '', 'Ora le cinque Casate sono in guerra. Due sono già cadute, non lascerò che i Prescelti siano i prossimi.', 38134),
(198949, 'itIT', '', 'Oh, bene, rinforzi... Aspetta, non sei un Venthyr.', 38134),
(198948, 'itIT', '', 'Siamo i difensori giurati della Terretetre, ma il nostro capo, il Primus, è scomparso.', 38134),
(198947, 'itIT', '', 'Se ti addentrassi nelle catacombe, ci farebbe comodo il tuo aiuto.', 38134),
(198946, 'itIT', 'Io credo di sì. In un posto molto lontano da qui...', '', 38134),
(198945, 'itIT', '', 'Abbiamo perso il contatto con Emil quando il caos si è scatenato. Abbiamo inviato l\'Armigero Dorin a cercarlo. Deve ancora tornare.', 38134),
(198944, 'itIT', 'Le sue ferite si rigenerano, ma il suo spirito è ancora debole. Non ho mai visto nulla del genere.', '', 38134),
(198935, 'itIT', '', 'Mmm, qualunque sia la ragione, una lama è inutile senza un\'impugnatura. È ora di mettersi al lavoro.', 38134),
(198933, 'itIT', 'Sei i rinforzi che abbiamo chiesto? Non abbiamo più sentito i nostri esploratori.', 'Sei i rinforzi che abbiamo chiesto? Non abbiamo più sentito i nostri esploratori.', 38134),
(198932, 'itIT', '', 'Quindi era questo il motivo di tutto quel trambusto. Una lama e un messaggio del Primus in persona...', 38134),
(198931, 'itIT', '', 'Cavalca il cervo. Raggiungi la Foresta di Cuordilegno. Nuvole oscure si avvicinano.', 38134),
(198912, 'itIT', '', 'Venti ululanti giungono dalla Foresta di Cuordilegno... essa trema...', 38134),
(198911, 'itIT', 'Devo dire che ammiro la sicurezza della Contessa, tu no?', 'Devo dire che ammiro la sicurezza della Contessa, tu no?', 38134),
(198910, 'itIT', 'Calcafauce? Sei davvero $gcurioso:curiosa;.', 'Calcafauce? Sei davvero $gcurioso:curiosa;.', 38134),
(198908, 'itIT', '<Ti guarda dall\'alto in basso senza proferire una sola parola di riconoscimento.>', '<Ti guarda dall\'alto in basso senza proferire una sola parola di riconoscimento.>', 38134),
(198907, 'itIT', 'Ci andrei piano, Calcafauce: la Contessa conosce i tuoi desideri più profondi ed è sempre pronta alla sua prossima raccolta.', 'Ci andrei piano, Calcafauce: la Contessa conosce i tuoi desideri più profondi ed è sempre pronta alla sua prossima raccolta.', 38134),
(198904, 'itIT', 'Curioso incontrarti qui $PR, la Contessa deve sentirsi caritatevole.', 'Curioso incontrarti qui $PR, la Contessa deve sentirsi caritatevole.', 38134),
(198903, 'itIT', '$C, ora non è il momento di infastidirmi con i tuoi futili alterchi.', '$C, ora non è il momento di infastidirmi con i tuoi futili alterchi.', 38134),
(198902, 'itIT', 'È vero quello che dicono sui Calcafauce?', 'È vero quello che dicono sui Calcafauce?', 38134),
(198901, 'itIT', '', 'I tuoi occhi mi dicono che qualcosa non va. Come posso aiutarti?', 38134),
(198899, 'itIT', 'Ovviamente la Contessa può invitare chiunque abbia voglia nel suo dominio, ma $gun famigerato:una famigerata; $Pr, davvero?', 'Ovviamente la Contessa può invitare chiunque abbia voglia nel suo dominio, ma $gun famigerato:una famigerata; $Pr, davvero?', 38134),
(198893, 'itIT', 'Generale Kaal, al riparo! I miei soldati colpiranno dal cielo!', '', 38134),
(198892, 'itIT', '', 'Grashaal, proteggiti! È ora di far piovere il terrore dall\'alto!', 38134),
(198891, 'itIT', '', 'Maldraxxus è la patria di chi ha vissuto per combattere. Qui, la forza è preziosa e viene ricompensata.', 38134),
(198890, 'itIT', '', 'Ci stanno travolgendo! Ci porterò via di qui!', 38134),
(198876, 'itIT', 'La tua mira è lodevole, Calcafauce. Questo dovrebbe insegnare loro a non banchettare a Selvarden.', '', 38134),
(198875, 'itIT', 'I divoratori continuano a invadere la foresta. Uccidi le bestie e usa il loro Animum per sigillare le fenditure prima che ci travolgano!', '', 38134),
(198871, 'itIT', 'I vortici spirano e si ripiegano violentemente, fornendo dei barlumi di luoghi riconoscibili nelle Terretetre. Entrare nel portale ti permetterà di viaggiare istantaneamente in un punto all\'interno della zona desiderata, anche se la tua incolumità non è garantita.', 'I vortici spirano e si ripiegano violentemente, fornendo dei barlumi di luoghi riconoscibili nelle Terretetre. Entrare nel portale ti permetterà di viaggiare istantaneamente in un punto all\'interno della zona desiderata, anche se la tua incolumità non è garantita.', 38134),
(198868, 'itIT', '', 'Ho affidato ogni metà ai miei sottoposti. È imperativo che li raggiungiamo prima delle forze di Denathrius.', 38134),
(198867, 'itIT', '', 'Ho nascosto il mio medaglione in questo specchio prima che Denathrius mi gettasse nella Fauce. Per impedirgli di raggiungerlo, ho spaccato il vetro.', 38134),
(198865, 'itIT', '', 'È scoppiato il caos nelle Catacombe del Rimorso e le forze di Denathrius infestano l\'Archivam.\n\nPer fortuna siamo stati in grado di riportare un minimo di stabilità nella Cripta dei Dimenticati con l\'aiuto di alcuni Venthyr della Cripta dell\'Assoluzione. Ma le macerie della distruzione permangono.\n\nIl Principe Renathal mi ha affidato il recupero del Medaglione dell\'Avarizia, ma non sono nelle condizioni di intraprendere azioni ostili. Mi serve il tuo aiuto, Calcafauce.', 38134),
(198864, 'itIT', '', 'È peggio di quanto pensassi, Calcafauce. Le cripte sono sprofondate nel caos in mia assenza. C\'è molto da fare.', 38134),
(198863, 'itIT', 'Mi manchi, amore mio. \n\nStaremo ancora insieme nel Sogno, un giorno.', '', 38134),
(198861, 'itIT', '', 'È successo ancora... il dolore e l\'agonia, la follia spropositata...', 38134),
(198860, 'itIT', 'Bel combattimento, Mevix!', '', 38134),
(198859, 'itIT', 'Ahah! Vali quanto cento soldati! Va\' e rivendica la nostra vittoria!', '', 38134),
(198858, 'itIT', 'Un assalto diretto coglierà Gharmal impreparato e schiaccerà la sua armata in un unico colpo!', '', 38134),
(198857, 'itIT', 'Ma ci servono dei rinforzi!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198856, 'itIT', 'È la nostra occasione per colpire la Casata dei Costrutti!', '', 38134),
(198854, 'itIT', 'Lo Scopo li guida come guida noi. Devi avere fede, amico mio.', 'Lo Scopo li guida come guida noi. Devi avere fede, amico mio.', 38134),
(198853, 'itIT', 'Spero che questa non sia una falsa partenza. Possiamo fidarci di questi stranieri?', 'Spero che questa non sia una falsa partenza. Possiamo fidarci di questi stranieri?', 38134),
(198852, 'itIT', 'Credo che dimostri la creatività illimitata dei Primi. Hanno fatto tutte le cose e ci hanno benedetti con lo Scopo.', 'Credo che dimostri la creatività illimitata dei Primi. Hanno fatto tutte le cose e ci hanno benedetti con lo Scopo.', 38134),
(198851, 'itIT', 'La miriade di forme che prendono questi stranieri mi intriga.', 'La miriade di forme che prendono questi stranieri mi intriga.', 38134),
(198850, 'itIT', 'Dobbiamo solo avere fiducia che lo Scopo guidi chi tra noi deve lavorarci assieme.', 'Dobbiamo solo avere fiducia che lo Scopo guidi chi tra noi deve lavorarci assieme.', 38134),
(198849, 'itIT', 'Sono solo felice di non doverli affrontare direttamente.', 'Sono solo felice di non doverli affrontare direttamente.', 38134),
(198848, 'itIT', 'Non potrei essere più d\'accordo!', 'Non potrei essere più d\'accordo!', 38134),
(198847, 'itIT', 'Vorrei che i Sovrintendenti fossero più rigorosi con gli Alienatori. Il caos che portano mi irrita.', 'Vorrei che i Sovrintendenti fossero più rigorosi con gli Alienatori. Il caos che portano mi irrita.', 38134),
(198846, 'itIT', 'Non temere, amico mio. Lo Scopo ci guiderà oltre questo caos.', 'Non temere, amico mio. Lo Scopo ci guiderà oltre questo caos.', 38134),
(198845, 'itIT', 'Allora la tua fede è più forte della mia. Posso ammettere i miei stessi dubbi. A essere sinceri ancora permangono in me, persino con l\'arrivo di questi stranieri.', 'Allora la tua fede è più forte della mia. Posso ammettere i miei stessi dubbi. A essere sinceri ancora permangono in me, persino con l\'arrivo di questi stranieri.', 38134),
(198844, 'itIT', 'Io non ho mai perso la fede. Non per dire che la mia devozione sia più forte della tua, ma non ho mai dubitato dello Scopo.', 'Io non ho mai perso la fede. Non per dire che la mia devozione sia più forte della tua, ma non ho mai dubitato dello Scopo.', 38134),
(198843, 'itIT', 'Devo ammetterlo. Gli eventi recenti mi hanno restituito la fede. Lo Scopo ci protegge sul serio.', 'Devo ammetterlo. Gli eventi recenti mi hanno restituito la fede. Lo Scopo ci protegge sul serio.', 38134),
(198842, 'itIT', 'Già. Lo Scopo guida la creazione con l\'occhio di un maestro artigiano. La semplice funzionalità non è sufficiente a mostrare la sua arte.', 'Già. Lo Scopo guida la creazione con l\'occhio di un maestro artigiano. La semplice funzionalità non è sufficiente a mostrare la sua arte.', 38134),
(198841, 'itIT', 'Una tale bellezza non può essere solo una semplice coincidenza. Dev\'essere di sicuro opera dei Primi.', 'Una tale bellezza non può essere solo una semplice coincidenza. Dev\'essere di sicuro opera dei Primi.', 38134),
(198840, 'itIT', 'Parole di verità, amico mio. Lo Scopo mette mano su tutta la creazione.', 'Parole di verità, amico mio. Lo Scopo mette mano su tutta la creazione.', 38134),
(198839, 'itIT', 'Questa vista mi ricorda sempre della magnificenza dello Scopo.', 'Questa vista mi ricorda sempre della magnificenza dello Scopo.', 38134),
(198838, 'itIT', 'Ho sentito lo stesso. Lo Scopo ci guida persino adesso. Una luce nell\'oscurità attuale.', 'Ho sentito lo stesso. Lo Scopo ci guida persino adesso. Una luce nell\'oscurità attuale.', 38134),
(198837, 'itIT', 'Li faremo a pezzetti!', '', 38134),
(198836, 'itIT', 'Si dice che l\'Arbiter si sia risvegliata!', 'Si dice che l\'Arbiter si sia risvegliata!', 38134),
(198835, 'itIT', 'Lo Scopo li guida come guida noi. Ne sono sicuro.', 'Lo Scopo li guida come guida noi. Ne sono sicuro.', 38134),
(198834, 'itIT', 'Le porte si stanno riaprendo. Finalmente, questa è una buona notizia. Spero di potermi fidare di questi stranieri.', 'Le porte si stanno riaprendo. Finalmente, questa è una buona notizia. Spero di potermi fidare di questi stranieri.', 38134),
(198833, 'itIT', 'Io, di mio, non dubito dello Scopo. Neanche quando le sue vie sono misteriose.', 'Io, di mio, non dubito dello Scopo. Neanche quando le sue vie sono misteriose.', 38134),
(198832, 'itIT', 'Di certo questo caos non può essere la vera volontà dello Scopo.', 'Di certo questo caos non può essere la vera volontà dello Scopo.', 38134),
(198830, 'itIT', '', 'Non c\'è tempo di parlare, muoviamoci, ora!', 38134),
(198829, 'itIT', '', 'Sono la Baronessa Draka, la Casata dei Prescelti è al tuo fianco.', 38134),
(198828, 'itIT', 'La chiave onorerà chi è degno.', '', 38134),
(198827, 'itIT', 'Il tradimento genera caos. I legami logori devono essere ripristinati.', '', 38134),
(198826, 'itIT', 'I regni della Morte sono inondati d\'oscurità.', '', 38134),
(198825, 'itIT', 'Non risparmiate nessuno!', '', 38134),
(198824, 'itIT', '', 'Tashmur, prendi!', 38134),
(198823, 'itIT', '', 'Informerò la Caccia Selvaggia. Ci servirà ancora il tuo aiuto!', 38134),
(198822, 'itIT', '', 'Guarda! Silfi che corrono via dalla Foresta di Cuordilegno... Qualcosa non va!', 38134),
(198821, 'itIT', '', 'Ci siamo! Ora che siamo pari... aspetta, cos\'è quello?', 38134),
(198820, 'itIT', '', 'Accetta in cambio questo dono. Una piccola parte della magia di Selvarden. Usala come desideri.', 38134),
(198818, 'itIT', '', 'Ci servono le tue abilità di cacciatore. Dobbiamo localizzare una preda sfuggente.', 38134),
(198817, 'itIT', 'Ci faremo strada direttamente tra le difese del Maestro.\n\nFammi sapere quando vuoi iniziare l\'addestramento.', '', 38134),
(198816, 'itIT', 'Ti attende al Trono di Ghiaccio. I capi dell\'Alleanza e dell\'Orda si sono riuniti con lui.', '', 38134),
(198815, 'itIT', 'Spezzare l\'elmo, tuttavia, ha anche liberato Bolvar Domadraghi dal fardello del Re dei Lich.', '', 38134),
(198814, 'itIT', 'Guarda il cielo e osserva l\'esito delle sue azioni. Io... non ho mai visto nulla del genere.', '', 38134),
(198813, 'itIT', 'Prima che i suoi servitori rapissero i capi, la Regina Banshee ha attaccato la Rocca della Corona di Ghiaccio. Ha raggiunto il trono e ha preso l\'Elmo della Dominazione.', '', 38134),
(198811, 'itIT', 'In ginocchio!', '', 38134),
(198810, 'itIT', 'Ti ho spezzato!', '', 38134),
(198809, 'itIT', 'Non eri alla mia altezza!', '', 38134),
(198808, 'itIT', 'Pensavi davvero di vincere?', '', 38134),
(198807, 'itIT', 'Remornia! Stermina questi insetti.', '', 38134),
(198806, 'itIT', 'La vostra presunzione vi costerà cara.', '', 38134),
(198805, 'itIT', 'Continuate a ribellarvi e la vostra sofferenza sarà eterna!', '', 38134),
(198804, 'itIT', 'Cabalisti! Cibatevi dei ficcanaso.', '', 38134),
(198798, 'itIT', '', 'Huln Alto Monte! È passato molto tempo da quando ti ho visto in battaglia.', 38134),
(198789, 'itIT', '', 'I lacchè del Maestro non sapranno cosa li avrà colpiti.\n\nFammi sapere quando vuoi iniziare l\'addestramento.', 38134),
(198786, 'itIT', '', 'Una Silfide deve sempre ripagare un favore. Hai fatto così tanto per la foresta, ma in cambio non sono riuscita a consegnare il tuo messaggio.', 38134),
(198778, 'itIT', 'Non comprometterai la mia posizione!', 'Non comprometterai la mia posizione!', 38134),
(198775, 'itIT', 'Eccoti di nuovo! Hai trovato ciò che ti serviva?', '', 38134),
(198774, 'itIT', 'Ah, arrivi giusto in tempo.', '', 38134),
(198773, 'itIT', 'Ho preparato il travestimento perfetto per aiutarti ad arrivare senza farti notare. Resta immobile per un secondo...', '', 38134),
(198772, 'itIT', 'Ricava ciò che ci serve dai gorm. Poi penseremo al Divoratore di Regni.', '', 38134),
(198770, 'itIT', '<Nelwyn sogghigna verso di te.>', '<Nelwyn sogghigna verso di te.>', 38134),
(198769, 'itIT', '', 'Aiutaci, prode $geroe:eroina;!', 38134),
(198728, 'itIT', 'Mevix! Scatena il caos su questi codardi!', '', 38134),
(198727, 'itIT', 'Ti ringrazio, $gamico mio:amica mia;. In segno di gratitudine, permettimi di fare un brindisi a uno dei tuoi ospiti.', '', 38134),
(198726, 'itIT', 'Presto! Prendi tutti e cinque i pezzi del mio set da tè prima i mostri di pietra li rompano!', '', 38134),
(198724, 'itIT', '', 'Ora sì che ci divertiamo!', 38134),
(198722, 'itIT', 'Hecutis, frantuma le loro ossa!', '', 38134),
(198721, 'itIT', 'Bargast, lacera le loro anime!', '', 38134),
(198720, 'itIT', 'Squartarovina, falli sanguinare!', '', 38134),
(198717, 'itIT', '', 'Avresti dovuto stare sulla strada!', 38134),
(198716, 'itIT', 'Liberate i cani!', '', 38134),
(198715, 'itIT', 'Ti scongiuro di fare attenzione! Stai perdendo pezzi ovunque!', '', 38134),
(198714, 'itIT', 'No! È il mio set da tè migliore!', '', 38134),
(198713, 'itIT', 'Questo dardo ha il tuo nome scritto sopra.', '', 38134),
(198712, 'itIT', 'Ho abbastanza dardi per tutti!', '', 38134),
(198711, 'itIT', 'Niente sfugge alla mia vista.', '', 38134),
(198710, 'itIT', 'Sento l\'odore della vostra paura.', '', 38134),
(198709, 'itIT', 'Ma ser... ci sarà un numero massiccio di vittime.', '', 38134),
(198708, 'itIT', 'Questo è il prezzo della vittoria!', '', 38134),
(198707, 'itIT', 'Avanzate! Travolgeteli con un assalto frontale!', '', 38134),
(198705, 'itIT', 'Sciocchi! Io sono Revendreth!', '', 38134),
(198698, 'itIT', 'Remornia! Fai urlare la loro carne!', '', 38134),
(198697, 'itIT', 'Col tuo aiuto, le forze di Revendreth continueranno a crescere in forza e numero.', '', 38134),
(198696, 'itIT', 'Remornia! Massacra questi insetti!', '', 38134),
(198695, 'itIT', 'Gli Alacupa sono stati liberati in zona per le esercitazioni. Fa\' di tutto per assicurarti che le truppe siano pronte alla battagliai!', '', 38134),
(198694, 'itIT', 'Che le ombre vi consumino!', '', 38134),
(198693, 'itIT', 'Morirete tutti insieme!', '', 38134),
(198692, 'itIT', 'Urla per me!', '', 38134),
(198691, 'itIT', 'Ti farò a pezzi!', '', 38134),
(198689, 'itIT', 'Apostoli devoti! Occupatevi di questa feccia.', '', 38134),
(198687, 'itIT', 'Servi! Portate via questo rifiuto dalla mia vista.', '', 38134),
(198684, 'itIT', 'Strisciate dinanzi a me. Arrendetevi e vi donerò una morte clemente.', '', 38134),
(198683, 'itIT', 'È tempo che torniate al vostro posto, mortali.', '', 38134),
(198682, 'itIT', 'Remornia! Libera il castello da questi parassiti.', '', 38134),
(198677, 'itIT', 'Non potete pagare il debito della vostra arroganza!', '', 38134),
(198676, 'itIT', 'Fa\' molta attenzione! La strada non è più sicura.', '', 38134),
(198675, 'itIT', '', 'Niente sfugge alla Caccia Selvaggia. Dev\'esserci qualcuno tra loro che può aiutarci.', 38134),
(198673, 'itIT', 'Quei codardi traditori sono degli sciocchi a sfidare la potenza dei Prescelti!', '', 38134),
(198670, 'itIT', 'Parla, recluta!', '', 38134),
(198669, 'itIT', 'Il dolore purifica l\'anima.', '', 38134),
(198668, 'itIT', 'Confessate i vostri peccati o vi consumeranno!', '', 38134),
(198665, 'itIT', '', 'Non far aspettare Vyraz. Non è famoso per la sua pazienza.', 38134),
(198664, 'itIT', '', 'Presto, suona l\'arpa per portare i passosetoso al sicuro!', 38134),
(198653, 'itIT', '<Il colore della pelle di questa Silvar è innaturalmente pallido e respira a malapena... Potrebbe essere già morta.\n\nSbattendo le palpebre vedi un occhio completamente aperto che ti fissa.\n\nLa bocca si slabbra in una fauce spalancata.>', '<Il colore della pelle di questa Silvar è innaturalmente pallido e respira a malapena... Potrebbe essere già morta.\n\nSbattendo le palpebre vedi un occhio completamente aperto che ti fissa.\n\nLa bocca si slabbra in una fauce spalancata.>', 38134),
(198651, 'itIT', '', 'Oh, ti ringrazio, viandante! È difficile trovare qualcuno di cui potersi fidare.', 38134),
(198649, 'itIT', 'Mia nonna sta malissimo!\n\nStavamo cercando di andare a Tirna Vaal. Speravamo che il Droman potesse aiutarci, ma è debole e la foresta è pericolosa!\n\nTi prego, potresti aiutarmi? Mia nonna si sta nascondendo in una caverna qua vicino. Sta così male...', 'Mia nonna sta malissimo!\n\nStavamo cercando di andare a Tirna Vaal. Speravamo che il Droman potesse aiutarci, ma è debole e la foresta è pericolosa!\n\nTi prego, potresti aiutarmi? Mia nonna si sta nascondendo in una caverna qua vicino. Sta così male...', 38134),
(198648, 'itIT', '', 'Ti ringrazio tanto! Sei $gstato:stata; di grande aiuto per me e le anime di cui mi prendo cura.\n\nRicordati di stare sulla strada. La foresta è ricolma di creature pericolose!\n\nGrazie ancora!', 38134),
(198645, 'itIT', '', 'Che la notte ti abbracci. Siamo in debito con te!', 38134),
(198642, 'itIT', '', 'Ti prego, salva quelle povere anime!', 38134),
(198616, 'itIT', 'Le mie parti... vanno... contate...', '', 38134),
(198615, 'itIT', 'Un altro cadavere da mietere!', '', 38134),
(198612, 'itIT', '', 'Il diario di Matyas ci ha fornito molte informazioni. Sottrargli la mia Pietra del Peccato richiederà astuzia. Ho un piano per metterlo fuori combattimento. Ci servono solo gli ingredienti giusti.', 38134),
(198607, 'itIT', 'Senza offesa, ma hai proprio l\'aria di una creatura a cui succedono molte cose strane.', '', 38134),
(198606, 'itIT', 'Essi sono lo scopo di Selvarden! I Semebrado contengono gli spiriti della natura dormienti come l\'Anziana Dunmira, che si trova sotto quell\'uccello. Se potessi attirarlo fuori dal nido, potrei intrufolarmi per recuperarla.\n\nSono felice che Baccaluna abbia potuto inviare qualcuno non potendo presentarsi lei stessa. Sai che di solito vuole una canzone e un ballo in cambio del suo aiuto?\n\n<Korenth ridacchia.>\n\nQuindi mi darai una mano ad allontanare quell\'uccello dal nido?\n\n<Korenth sorride speranzoso.>', '', 38134),
(198590, 'itIT', 'Mmm, non va bene... l\'incubo dello spirito persiste. Cosa potrebbe essergli successo?', '', 38134),
(198589, 'itIT', 'Aspetta... lo spirito... si sta legando a te! Non succedeva da... neanche ricordo da quando!', '', 38134),
(198573, 'itIT', 'Un tocco d\'Animum dovrebbe placarlo. Quando i suoi sogni si saranno calmati, potremo...', '', 38134),
(198569, 'itIT', 'Oh oh... un incubo sta affliggendo lo spirito dentro questo Semebrado. Il poverino dev\'essere morto violentemente...', '', 38134),
(198567, 'itIT', 'La Cacciapaura esige che tu porti Loksey al Colle Criptavecchia. È già partita.', '', 38134),
(198565, 'itIT', 'Aspetta, riconosco quella belva... Era a bordo della nostra nave! Dev\'essere sopravvissuta allo schianto.', '', 38134),
(198564, 'itIT', 'Catturata!', '', 38134),
(198563, 'itIT', 'Guarda, eccola!', '', 38134),
(198561, 'itIT', 'È stato un onore eccezionale, Principe Renathal. Grazie.', '', 38134),
(198560, 'itIT', 'No! Non farci andare via! Ci si diverte un mondo qui!', '', 38134),
(198559, 'itIT', 'Il mio rapporto per l\'Arconte sarà perlopiù soddisfacente.', '', 38134),
(198558, 'itIT', '', 'Le voci sulla corte non mentivano. Non vedo l\'ora della prossima.', 38134),
(198557, 'itIT', 'Ho imparato molto. Rifletterò su ciò che ho visto. Gente furba...', '', 38134),
(198556, 'itIT', '', 'Io, me e me stessa ci siamo divertite un sacco!', 38134),
(198555, 'itIT', '', 'Hai organizzato una corte impressionante, Principe. Forse dopo tutto non si tratta di una causa persa.', 38134),
(198551, 'itIT', 'Non è grandioso questo Semebrado? Ci vorranno molti anni prima che ritorni al suo piano d\'esistenza.\n\nVai, prendi i Coni d\'Animum che trovi e infondilo d\'Animum.', '', 38134),
(198550, 'itIT', 'Questi Spriggan portano solo problemi!', 'Questi Spriggan portano solo problemi!', 38134),
(198549, 'itIT', 'Avvizzite e morite!', '', 38134),
(198546, 'itIT', 'Nel sacrificio vi è vittoria!', '', 38134),
(198545, 'itIT', 'I miei soldati sorgono ancora!', '', 38134),
(198542, 'itIT', '', 'Osi... negarmi i cieli? Allora... risponderò a tono!', 38134),
(198534, 'itIT', 'Tutto grazie al tuo aiuto!', '', 38134),
(198533, 'itIT', '', 'Il vento solleva nuovamente le tue ali, Plasmasogni.', 38134),
(198532, 'itIT', 'Nadjia, sei di nuovo cosciente! Stiamo cercando di capire perché il Coordinatore abbia convocato $gquesto:questa; mortale. Abbiamo trovato il suo diario personale.', '', 38134),
(198531, 'itIT', '', 'Urgh. Mi sento gli occhi come trafitti da spilli. Sei $gstato:stata; tu, Calcafauce?', 38134),
(198529, 'itIT', '', 'Mi dispiace! Ho dovuto fare una deviazione lungo la strada.', 38134),
(198527, 'itIT', 'Iniziamo con uno più giovane. Arrivò appena prima della siccità e necessita di maggiori attenzioni.', '', 38134),
(198520, 'itIT', 'Avete sentito il Barone! Avanziamo!', '', 38134),
(198519, 'itIT', 'Vieni, ti mostreremo come occuparti di un Semebrado e lo osserverai sognare.', '', 38134),
(198517, 'itIT', 'Capita a tutti di essere mangiati da una pianta, prima o poi.', '', 38134),
(198510, 'itIT', '', 'Guarda. Paralizza dalla paura un\'anima e il suo Animum scorrerà a fiumi.', 38134),
(198509, 'itIT', 'Colpiremo quando suonerai il corno. Per i Prescelti!', '', 38134),
(198505, 'itIT', '', '<Nadjia si sforza di parlare.>', 38134),
(198500, 'itIT', 'Useremo l\'Animum rubato da Denathrius contro di lui.', '', 38134),
(198499, 'itIT', 'Calcafauce! Vieni, raccogli quanto più Animum riesci. Questo tipo di legame è temporaneo ma dovrebbe darti forza per la battaglia in arrivo.', '', 38134),
(198496, 'itIT', '', 'Agh. Spriggan.', 38134),
(198495, 'itIT', '', 'L\'unica cosa che vedo è un glorioso futuro che sta per avverarsi. Un futuro che non ha posto per traditori come te!', 38134),
(198494, 'itIT', '', 'Denathrius ha tradito Revendreth scatenando questo Animum nella Fauce. Sicuramente, anche un leccapiedi come te riesce a vederlo!', 38134),
(198493, 'itIT', '', 'Accusatrice, sei riuscita a sfuggire alle mie grinfie soltanto per tornare spudoratamente in queste sale, insieme a una misera banda di mortali. Patetica.', 38134),
(198492, 'itIT', '', 'Tutti i cuccioli sono stati ritrovati e gli Spriggan sono in fuga. Grazie di cuore per il tuo aiuto!', 38134),
(198490, 'itIT', '', 'Gli Spriggan stanno terrorizzando i passosetoso! Ti prego, metti fine ai loro dispetti!', 38134),
(198489, 'itIT', 'La mia furia brucia con la potenza di mille soli!', '', 38134),
(198488, 'itIT', 'Non è finita! Lasciatemi andare!', '', 38134),
(198487, 'itIT', '', 'Tor ilisar\'thera\'nal!', 38134),
(198486, 'itIT', 'Tyrande! Non farlo!', '', 38134),
(198485, 'itIT', '', 'Salva gli altri! La Banshee la ucciderò io!', 38134),
(198484, 'itIT', 'Trova coloro che sono stati presi e riportali a casa!', '', 38134),
(198483, 'itIT', 'Il tuo coraggio sarà ricordato. Al diel shala.', '', 38134),
(198482, 'itIT', 'La via si apre!', '', 38134),
(198481, 'itIT', 'È giunto il momento. Raccogli l\'elmo infranto e apriremo il varco.', '', 38134),
(198480, 'itIT', 'Ma non c\'è altro modo. La morte vuole rivendicare Azeroth. Tocca a te difenderla.', '', 38134),
(198479, 'itIT', 'Il velo non è fatto per essere attraversato da mortali viventi. Potrebbe non esserci ritorno da dove ti stai recando.', '', 38134),
(198478, 'itIT', 'La Spada d\'Ebano non teme la morte. Né qui, né in altri regni.', '', 38134),
(198477, 'itIT', 'Per aiutarti nell\'impresa, i cavalieri della Spada d\'Ebano ti presteranno la loro forza. Darion Mograine si è offerto volontario per accompagnarti.', '', 38134),
(198476, 'itIT', 'Affronterai pericoli considerevoli. Senza un aiuto, la tua sconfitta sarà probabile.', '', 38134),
(198475, 'itIT', 'Ma tale minaccia non può essere ignorata. Ecco perché dovrai avventurarti dove io non posso.', '', 38134),
(198474, 'itIT', 'Non oso viaggiare con te. Anche se sono libero dall\'elmo, temo che verrei vincolato alla volontà del suo vero padrone.', '', 38134),
(198473, 'itIT', 'È la stessa presenza che sento nascondersi nel cielo spezzato sopra di noi.', '', 38134),
(198472, 'itIT', 'Potevo percepire una presenza oscura al limite della mia coscienza. Non Arthas... non Ner\'zhul. Qualcos\'altro.', '', 38134),
(198471, 'itIT', 'Quando l\'elmo si trovava sulla mia testa, avevo bisogno di tutta la mia volontà per tenerne a bada il potere. Molte volte ho quasi ceduto.', '', 38134),
(198470, 'itIT', 'Posiziona le Schegge della Dominazione intorno al cerchio di rune. Cominceremo il rituale.', '', 38134),
(198469, 'itIT', 'È lì che bisogna andare, per salvare gli ostaggi di Sylvanas. Assieme, possiamo aprire la via.', '', 38134),
(198468, 'itIT', 'Durante la mia veglia sul Trono di Ghiaccio, ho percepito barlumi fugaci nei regni della Morte. Tra essi, ho visto un luogo di ineluttabile oscurità.', '', 38134),
(198467, 'itIT', 'Quando la Banshee ha spezzato l\'elmo, ha aperto uno squarcio nel velo tra il nostro mondo e le Terretetre.', '', 38134),
(198466, 'itIT', 'Abbiamo una minaccia più urgente, e dobbiamo prepararci ad affrontarla.', '', 38134),
(198465, 'itIT', 'Basta! La guerra è finita. Non possiamo permettercene un\'altra.', '', 38134),
(198464, 'itIT', '', 'Osi parlarmi di pace? Dopo le atrocità che ci ha inflitto la tua gente?!?', 38134),
(198463, 'itIT', '', 'I Kaldorei hanno bisogno di te per poter sperare nella pace, Gran Sacerdotessa.', 38134),
(198462, 'itIT', 'Anch\'io voglio che Sylvanas risponda dei suoi crimini, ma Lor\'themar ha ragione. Anduin non vorrebbe che mettessimo a repentaglio l\'Alleanza per lui.', '', 38134),
(198461, 'itIT', '', 'Codardo! Pagherei qualsiasi prezzo pur di vedere la Banshee impalata sulla mia lama!', 38134),
(198460, 'itIT', 'Non possiamo mandare i capi di Azeroth in quella che potrebbe essere una missione suicida. I nostri amici rapiti sarebbero dello stesso avviso.', '', 38134),
(198459, 'itIT', '...la pace resta fragile. Sono tempi difficili. I nostri popoli necessitano di stabilità, sia nell\'Alleanza che nell\'Orda.', '', 38134),
(198456, 'itIT', 'C\'è ancora molto da fare per essere pronti ad agire in tutte le Terretetre.', '', 38134),
(198452, 'itIT', 'A volte non capisco più il senso di preparare queste anime. Senza nuove anime, le rese d\'Animum sono diminuite.', 'A volte non capisco più il senso di preparare queste anime. Senza nuove anime, le rese d\'Animum sono diminuite.', 38134),
(198451, 'itIT', 'Ho sentito che sei qui in cerca d\'Animum. Se solo ce ne fosse abbastanza.', 'Ho sentito che sei qui in cerca d\'Animum. Se solo ce ne fosse abbastanza.', 38134),
(198450, 'itIT', 'Guarda sopra di te. Non riusciamo nemmeno controllare più le nostre creazioni!', 'Guarda sopra di te. Non riusciamo nemmeno controllare più le nostre creazioni!', 38134),
(198449, 'itIT', 'Di solito, queste gabbie sono riempite fino all\'orlo... maledetta siccità d\'Animum.', 'Di solito, queste gabbie sono riempite fino all\'orlo... maledetta siccità d\'Animum.', 38134),
(198431, 'itIT', '', 'Colpite più forte! Nessuna pietà!', 38134),
(198429, 'itIT', '', 'Avete la mia gratitudine. Quando il Carceriere sarà sconfitto... i Kyrian avranno molto di cui discutere.', 38134),
(198428, 'itIT', '', 'A lungo abbiamo pensato che le nostre vite mortali fossero un peso di cui liberarsi in nome del servizio. Tuttavia, oggi le gesta dei mortali hanno fatto la differenza.', 38134),
(198427, 'itIT', '', 'Le macchinazioni del Carceriere hanno causato una ferita tra i Kyrian. Bisogna curarla.', 38134),
(198426, 'itIT', '', 'Devos...', 38134),
(198425, 'itIT', '', 'Il Carceriere ha distorto il tuo pensiero, Devos. Devi essere fermata... a qualsiasi costo.', 38134),
(198424, 'itIT', '', 'Con la tua lancia, prenderò il controllo delle Guglie e forgerò nuovi Eletti a guidare i Kyrian!', 38134),
(198423, 'itIT', '', 'Accetta la sconfitta, Arconte. Servo un potere molto più grande adesso.', 38134),
(198422, 'itIT', '', 'Argh!', 38134),
(198421, 'itIT', '', 'E io piango il tuo tradimento.', 38134),
(198420, 'itIT', '', 'Egli desidera liberarci dalla prigione che ci siamo costruiti. E per tale ragione, si è meritato la mia lealtà.', 38134),
(198419, 'itIT', '', 'E così ho ascoltato le parole del Carceriere. Ho compreso l\'ingiustizia che tu e gli altri avete inflitto su di lui... e su di noi.', 38134),
(198418, 'itIT', '', 'Ma ti sei rifiutata di vedere la verità. Che la via è fallace. Che serviamo una menzogna.', 38134),
(198417, 'itIT', '', 'Ciò che ho visto tra i ricordi di Uther mi ha scossa nel profondo.', 38134),
(198416, 'itIT', '', 'No. È stato un mortale a esporre la follia dei tuoi editti.', 38134),
(198415, 'itIT', 'Con gli alleati del Carceriere che ci invadono da ogni lato, dobbiamo sfruttare appieno le sue risorse.', '', 38134),
(198414, 'itIT', '', 'Ci sacrifichiamo perché dobbiamo. I fardelli mortali rallenterebbero solo il nostro sacro compito.', 38134),
(198413, 'itIT', '', 'Ma cosa serviamo? Un\'Arbiter impotente, una legge che ci opprime! I nostri sacrifici sono stati inutili!', 38134),
(198412, 'itIT', 'Il Forte Elisio fu costruito dall\'Arconte come base operativa della nostra congrega.', '', 38134),
(198411, 'itIT', '', 'Per eoni interi ci è stato detto di \"purificarci\". Di abbandonare i nostri ricordi, la nostra identità, i nostri cari. Tutto in nome del servizio.', 38134),
(198410, 'itIT', '', 'Non finché resterai al comando. Gli Indegni forgeranno una nuova via per il Bastione!', 38134),
(198409, 'itIT', '', 'Ascolta la ragione, Devos. I Kyrian devono restare uniti.', 38134),
(198408, 'itIT', '', 'Indegni, impedite ai mortali di interferire!', 38134),
(198407, 'itIT', '', 'La tua cieca accettazione ci avrebbe condannati tutti. Non mi hai lasciato altra scelta se non oppormi!', 38134),
(198406, 'itIT', '', 'Eri l\'Eletta della Lealtà, Devos, e invece sei stata tu a guidare gli Indegni per tutto questo tempo.', 38134),
(198405, 'itIT', '', 'Occupatevi della retroguardia mentre affronto Devos. Prestate servizio.', 38134),
(198404, 'itIT', '', 'Siamo troppo pochi, mortali. Gli Indegni si sono fatti strada tra le nostre forze.', 38134),
(198403, 'itIT', 'Crogiolati nella gloria del Forte Elisio. Magnifico, vero?', '', 38134),
(198402, 'itIT', 'Ora queste anime coraggiose sono la tua gente, Calcafauce.', '', 38134),
(198401, 'itIT', 'Insieme! All\'attacco!', '', 38134),
(198400, 'itIT', 'La farò finita!', '', 38134),
(198399, 'itIT', 'Ho raggiunto il mio scopo!', '', 38134),
(198398, 'itIT', 'Ancora... Ne bramo ancora...', '', 38134),
(198397, 'itIT', 'Ho finito qui.', '', 38134),
(198396, 'itIT', 'Ha scelto di unirsi ai nostri encomiabili ranghi, per aiutarci a rivendicare il nostro regno.', '', 38134),
(198395, 'itIT', 'Un\'altra tacca sulla mia elsa...', '', 38134),
(198390, 'itIT', 'Va\' in ricognizione e trova il Generale Alver. Quando sei $gpronto:pronta;, usa questo corno per segnalarci di attaccare.', '', 38134),
(198386, 'itIT', 'Indicami i tuoi nemici e me ne occuperò io.', '', 38134),
(198385, 'itIT', 'Per avermi liberato, ti aiuterò a uccidere i tuoi nemici... per un po\' di tempo.', '', 38134),
(198384, 'itIT', 'Permettimi di guidarti e i tuoi nemici cadranno.', '', 38134),
(198383, 'itIT', 'Hai combattuto bene, $p! Dai l\'ordine e lanceremo il prossimo assalto!', '', 38134),
(198382, 'itIT', 'Oh, quanto ho voluto tornare alla battaglia...', '', 38134),
(198381, 'itIT', 'Colpiamo dove conta!', '', 38134),
(198380, 'itIT', '', 'Sono pronta alla sfida.', 38134),
(198379, 'itIT', '', 'Permettimi di sgranchirmi le ali.', 38134),
(198378, 'itIT', '', 'Forse se avessi successo potrei instillare un po\' di fiducia in Pelagos.', 38134),
(198371, 'itIT', 'È solo un\'altra prova. Anche se non passassi adesso... diventerei più forte per la prossima.', '', 38134),
(198370, 'itIT', 'Questa volta pare promettere bene, $n.', '', 38134),
(198369, 'itIT', 'È il mio turno?\n\nFarò del mio meglio.', '', 38134),
(198368, 'itIT', '', '\"Evoca Mortanis\"... Con questa siccità??? Assurdo!', 38134),
(198365, 'itIT', 'Sì, signore!', '', 38134),
(198363, 'itIT', 'È davvero considerato tradimento se non hai mai scelto il tuo padrone in primo luogo?', '', 38134),
(198362, 'itIT', 'Oh, e grazie ancora! Qualunque sia il tuo mondo, è fortunato ad averti al suo fianco.', '', 38134),
(198361, 'itIT', 'Usa il mio pugnale per aprire le piante e liberare gli altri. Ci vediamo a Tirna Vaal.', '', 38134),
(198341, 'itIT', 'Mangiati dalle piante? Un\'altra volta?', '', 38134),
(198340, 'itIT', 'Oh, ah, molto vero eh? Per essere un tipo tosto è proprio un tipo brutto e carniv... eh... attiv... eh... cattivo. Hic! Dobbiamo solo tenerlo col gomito alzato per farlo stare buono.', '', 38134),
(198339, 'itIT', 'Ah, molto meglio. Quasi a nessuno importa degli anziani, oggigiorno.', '', 38134),
(198338, 'itIT', 'Oh bene, mi farebbe comodo un Fungomante! Ti dispiacerebbe occuparti dei miei steli? E anche di quegli invasori disgustosi?', '', 38134),
(198337, 'itIT', 'Vai tranquillo che se nessuno glielo spiffera stiamo a posto. Comunque, Strizzafango è un problema vero.', '', 38134),
(198336, 'itIT', 'Aiuto! Aiutami!', '', 38134),
(198335, 'itIT', 'Occhio che non stiamo facendo una bella cosa. Se quello ci becca finiamo nella melma tutti e due.', '', 38134),
(198334, 'itIT', '', 'Questo dovrebbe alleviare il problema. Spero che tu non abbia respirato troppe spore.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198333, 'itIT', '', 'Il Capo Fungomante ha bisogno d\'aiuto! Ti prego, fai il possibile per aiutarlo a proteggere Millesteli.', 38134),
(198332, 'itIT', '', 'Da questa parte, seguimi.', 38134),
(198329, 'itIT', 'Attaccheremo quando sarai $gpronto:pronta;. I Dragatori qui controlleranno lo specchio per impedire i rinforzi dal Castello di Nathria, mentre tu, io e i nostri pochi alleati rimasti attaccheremo direttamente Denathrius.\n\nCombattere contro l\'essere che ha creato tutto ciò che vedete spetterà $gal:alla; Calcafauce, ai tre Mietitori, all\'ex generale della Legione di Pietra e a quei pochi Venthyr che sono al nostro fianco.\n\nFammi sapere quando sei $gpronto:pronta; ad affrontare la morte.', '', 38134),
(198316, 'itIT', 'Mortale! Non hai possibilità contro di noi!', '', 38134),
(198315, 'itIT', 'La battaglia ci attende, soldati! Preparate le difese!', '', 38134),
(198313, 'itIT', '', '<Il Centurione si muove in modo irregolare.>\n\nSto riscontrando un errore.\n\nSto riscontrando un errore.\n\nSto riscontrando un errore.', 38134),
(198312, 'itIT', 'PRENDI! PRENDI! AHHH!\n\nPRENDI LA PERGAMENA!', 'PRENDI! PRENDI! AHHH!\n\nPRENDI LA PERGAMENA!', 38134),
(198311, 'itIT', '', 'Presta servizio, $n.', 38134),
(198310, 'itIT', '', 'Presentarti come $gun:una; combattente nella Via dell\'Ascensione è un grande onore. Tuttavia, le anime a te vincolate combatteranno solo una mia visione... Forse dovrei apparire io stessa.', 38134),
(198309, 'itIT', '', 'Dunque è finita. Ora una parte di me risiede in quello specchio, con te.', 38134),
(198308, 'itIT', 'POLPETTA PRONTO A COMBATTERE!', '', 38134),
(198307, 'itIT', 'Mi sento calmo dopo la mia meditazione mattutina, ma non mi dispiacerebbe fare qualcos\'altro oltre allenarmi!\n\nForse un buon libro...', 'Mi sento calma dopo la mia meditazione mattutina, ma non mi dispiacerebbe fare qualcos\'altro oltre allenarmi!\n\nForse un buon libro...', 38134),
(198306, 'itIT', 'Mmm... Mi piacerebbe un po\' di frutta. Credi di poter trovare un Factotum per me?', 'Mmm... Mi piacerebbe un po\' di frutta. Credi di poter trovare un Factotum per me?', 38134),
(198305, 'itIT', 'TORMENTATORE RUBATO CIBO! PRENDE SEMPRE TUTTO CIBO! ORA VENDETTA! ORA CIBO!', '', 38134),
(198304, 'itIT', '', 'Fatti avanti. Unisciti ai tuoi compagni Kyrian, così che possano darti il benvenuto tra loro.', 38134),
(198303, 'itIT', 'Ammirate la nostra gloriosa Arconte! Già antica quando questo regno era ancora giovane.', '', 38134),
(198302, 'itIT', 'Il Calcafauce ha dimostrato di essere un prezioso alleato non solo del Bastione, ma di tutte le Terretetre.', '', 38134),
(198301, 'itIT', 'Il rituale è pensato per aiutare gli spiriti irrequieti a misurarsi con gli ostacoli che impediscono loro di ripristinare la propria forza.\n\nSento che questo spirito, quello che ti è così legato, abbia affrontato una grande prove in vita. Spero che possiamo aiutarla a tornare a fare sonni tranquilli.', '', 38134),
(198300, 'itIT', 'Fratelli e sorelle, ascoltatemi! Siamo tutti riuniti qui quest\'oggi per un evento fatidico.', '', 38134),
(198299, 'itIT', 'In questo momento, seguiremo insieme la via. Venite.', '', 38134),
(198298, 'itIT', '', 'La pietra! Che cos\'hai fatto???', 38134),
(198297, 'itIT', '', 'Nonostante io abbia molto da fare, apparendo lungo la Via posso fare di più per il Bastione. Ti concederò di usare lo specchio dell\'anima per catturare il mio ricordo.', 38134),
(198296, 'itIT', '', 'La Via dell\'Ascensione? È un onore essere considerata un esempio per gli altri.', 38134),
(198292, 'itIT', '', 'Il Primus ha lasciato la pietra come sfida. Raggiungila e dimostra la tua forza.', 38134),
(198288, 'itIT', 'Vorrei qualcosa da bere. Dov\'è quel Factotum?', 'Vorrei qualcosa da bere. Dov\'è quel Factotum?', 38134),
(198287, 'itIT', 'Ma tu... sei... inferiore...', '', 38134),
(198286, 'itIT', '', 'Il mio compito è preservare le nostre risorse e proteggere gli Aspiranti sotto il mio comando.\n\nRicordo il nostro primo incontro, l\'Animum sprecato su Centurioni superflui. So che ci hai aiutati con la siccità, ma non possiamo comunque sprecare ciò che abbiamo.\n\nPerché mi invochi?', 38134),
(198271, 'itIT', 'Al sicuro, al sicuro! Qui saremo al sicuro.\n\nGrazie, grazie, ci hai salvato!', '', 38134),
(198268, 'itIT', '', 'Vi avevo avvisato di non tornare!', 38134),
(198267, 'itIT', '', 'Zayhad! Svegliati!', 38134),
(198265, 'itIT', 'Ci siamo quasi! Le scuderie sono su quella collina.', '', 38134),
(198264, 'itIT', 'Se gli idioti dell\'Alleanza facessero come gli pare, non avremmo neanche quello.', '', 38134),
(198263, 'itIT', '', 'Sii $Gbenvenuto:benvenuta;, giovane germoglio. Ogni passo che fai rivela il tuo cammino.', 38134),
(198262, 'itIT', 'Vedi, il bello di Orgrimmar è che è un luogo che possiamo chiamare casa. Questo significa tanto per molti.', '', 38134),
(198261, 'itIT', 'Sì, è afosa e polverosa, e gli Orchi non sono sempre ospitali, compagnia qui presente esclusa... ma ha il suo fascino, no?', '', 38134),
(198260, 'itIT', 'Sì, è afosa e polverosa, e gli Orchi non sono sempre ospitali... ma ha il suo fascino, no?', '', 38134),
(198259, 'itIT', 'Ti do il benvenuto tra i pesci grossi. Ti trovi a Orgrimmar ora, chiunque sia importante fa affari qui.', '', 38134),
(198257, 'itIT', '', 'Alcuni dei seguaci di Matyas sono costretti a seguirlo, come lo ero io. Liberali se puoi.', 38134),
(198256, 'itIT', 'Un altro cade...', '', 38134),
(198253, 'itIT', 'Percepisco... tutto. Per l\'Arconte, che vita che hai vissuto!', '', 38134),
(198252, 'itIT', 'Puriani mi fanno felice! Facciamo felici gli Aspiranti?', 'Puriani mi fanno felice! Facciamo felici gli Aspiranti?', 38134),
(198251, 'itIT', 'Chi prende pergamena? Tu-uuh?', 'Chi prende pergamena? Tu-uuh?', 38134),
(198250, 'itIT', 'Cambio pannolino?', 'Cambio pannolino?', 38134),
(198249, 'itIT', 'Che vuoi pulire?', 'Che vuoi pulire?', 38134),
(198248, 'itIT', 'Vuoi che aspetto? Porto? Prendo???', 'Vuoi che aspetto? Porto? Prendo???', 38134),
(198247, 'itIT', 'Ti piace Puriano saporito? \n\nAspiranti lo prendono, sì! Lavora sodo, prendi frutto!', 'Ti piace Puriano saporito? \n\nAspiranti lo prendono, sì! Lavora sodo, prendi frutto!', 38134),
(198246, 'itIT', 'Pergamena, pergamena...\n\nDove va la pergamena?', 'Pergamena, pergamena...\n\nDove va la pergamena?', 38134),
(198245, 'itIT', 'Sete? Vuoi succo dolce?', 'Sete? Vuoi succo dolce?', 38134),
(198244, 'itIT', 'Pulire! Pulire!\n\n<Il Factotum parla tra sé.>', 'Pulire! Pulire!\n\n<La Factotum parla tra sé.>', 38134),
(198243, 'itIT', 'Vuoi che aspetto?', 'Vuoi che aspetto?', 38134),
(198239, 'itIT', 'È un piacere vedere qualunque cosa tu sia qui a Selvarden.', '', 38134),
(198232, 'itIT', 'Ne sarei onorato, Guida delle Anime. Spero soltanto... di poter restare legato anche a Kleia.', '', 38134),
(198230, 'itIT', 'Bene, qui hai finito. Io cercherò i curatori scomparsi.', '', 38134),
(198229, 'itIT', 'Da quando è iniziata la siccità, non c\'è più stato abbastanza Animum. Abbiamo dovuto fare... scelte difficili.', '', 38134),
(198228, 'itIT', 'I Semebrado necessitano di curatori che diano loro Animum. Senza, gli spiriti al loro interno non possono rinascere.', '', 38134),
(198227, 'itIT', 'Ti ringrazio! Queste pozze sono tutto fuorché tranquille di recente...', '', 38134),
(198226, 'itIT', 'No, no... Alla Regina non piacerà per niente...', '', 38134),
(198224, 'itIT', 'Cedete!', '', 38134),
(198223, 'itIT', 'Spezzatevi!', '', 38134),
(198222, 'itIT', 'Ti schianterò a terra!', '', 38134),
(198221, 'itIT', 'Ti spezzerò!', '', 38134),
(198220, 'itIT', 'Scommetto che quella guardia può indicarci le scuderie.', '', 38134),
(198219, 'itIT', 'Be\', che hanno detto?', '', 38134),
(198214, 'itIT', 'Sono stanco della vostra insolenza!', '', 38134),
(198213, 'itIT', 'Non andrò in frantumi!', '', 38134),
(198212, 'itIT', 'Le tue lame si spezzeranno sulla mia pelle!', '', 38134),
(198210, 'itIT', 'Morirai per mano mia!', '', 38134),
(198208, 'itIT', 'Prestare servizio... è stato... il mio più grande onore...', '', 38134),
(198205, 'itIT', 'Non eravate degni di stare sul mio stesso campo di battaglia.', '', 38134),
(198203, 'itIT', 'Stai al tuo posto!', '', 38134),
(198202, 'itIT', 'Feccia senza onore!', '', 38134),
(198201, 'itIT', 'I traditori devono morire!', '', 38134),
(198200, 'itIT', '', 'La mia lama vi troverà!', 38134),
(198199, 'itIT', '', 'Ti faccio a pezzi!', 38134),
(198198, 'itIT', '', 'Sanguina!', 38134),
(198197, 'itIT', '', 'Non c\'è scampo!', 38134),
(198196, 'itIT', '', 'Non potete fuggire!', 38134),
(198194, 'itIT', '', 'La vostra fine è vicina!', 38134),
(198193, 'itIT', '', 'Il sapore della nostra vittoria sull\'Isola dell\'Esilio avrà vita breve. Ora dobbiamo rivolgere l\'attenzione alla guerra.', 38134),
(198192, 'itIT', '', 'Non riuscirete a spezzarmi!', 38134),
(198191, 'itIT', '', 'Da carne in pietra!', 38134),
(198190, 'itIT', '', 'Vi finirò io stessa!', 38134),
(198189, 'itIT', '', 'Quassù, Calcafauce. Ho trovato Matyas nella parte alta della torre. Seguimi quando sarai $gpronto:pronta;.', 38134),
(198188, 'itIT', '', 'Combatti con onore e ricorda sempre il tuo addestramento. Lok\'tar ogar!', 38134),
(198187, 'itIT', '', 'Tremate nella mia ombra!', 38134),
(198186, 'itIT', '', 'Il mio sangue... per... il Maestro...', 38134),
(198185, 'itIT', '', 'Il vostro tradimento è giunto alla fine.', 38134),
(198184, 'itIT', '', 'Ti attende molta gloria sul campo di battaglia.', 38134),
(198183, 'itIT', 'Cambio d\'abito! Dietro le quinte, ora!', '', 38134),
(198180, 'itIT', '', 'Feccia traditrice!', 38134),
(198179, 'itIT', '', 'Che le profondità ti inghiottano!', 38134),
(198175, 'itIT', 'Ce ne sono troppi!', 'Ce ne sono troppi!', 38134),
(198173, 'itIT', '', 'La vostra ribellione finisce qui!', 38134),
(198170, 'itIT', '', 'Usa queste catene per controllare quell\'anima testarda, in catene risponderà al tuo volere.', 38134),
(198166, 'itIT', '', 'Piumaleggera, tu piazza il giglio. Questo stupido sigillo non si apre!', 38134),
(198163, 'itIT', 'Questo è il caos! Non dovremmo combattere gli uni contro gli altri!', 'Questo è il caos! Non dovremmo combattere gli uni contro gli altri!', 38134),
(198156, 'itIT', 'So aggiustare molte cose! Basta chiedere.', 'So aggiustare molte cose! Basta chiedere.', 38134),
(198155, 'itIT', 'Salve! Sì? Posso aggiustare.', 'Salve! Sì? Posso aggiustare.', 38134),
(198142, 'itIT', 'Dove hanno trovato tutto quell\'Animum?', 'Dove hanno trovato tutto quell\'Animum?', 38134),
(198141, 'itIT', 'I costrutti erano nostri alleati! Perché si rivoltano contro di noi?', 'I costrutti erano nostri alleati! Perché si rivoltano contro di noi?', 38134),
(198137, 'itIT', 'La Dama Oscura ci ha convocati per una missione importante. Non ci sarà spazio per il fallimento.', '', 38134),
(198136, 'itIT', 'La loro armata cresce a ogni soldato che noi perdiamo.', 'La loro armata cresce a ogni soldato che noi perdiamo.', 38134),
(198135, 'itIT', 'Siamo in inferiorità numerica!', 'Siamo in inferiorità numerica!', 38134),
(198129, 'itIT', '', 'Ti ringrazio, Aliothe. Il tuo consiglio è bene accetto, come la tua amicizia.', 38134),
(198127, 'itIT', '', 'Sì, anche se con il tuo aiuto li respingeremo. Sono certa di questo.', 38134),
(198125, 'itIT', '', 'Ma anche Selvarden è in pericolo. Vedo molte ombre che lacerano i confini, facendosi strada con le unghie e con i denti.', 38134),
(198124, 'itIT', '', 'Ora questa foresta è la mia casa, così come dev\'essere. Noi serviamo il ciclo, e la Regina ci guida.', 38134),
(198121, 'itIT', '', 'Il mio mondo fu distrutto dagli assalti della Legione Infuocata. Non rimangono che ceneri.', 38134),
(198119, 'itIT', '', 'La nostalgia nella tua voce racconta di una perdita.', 38134),
(198118, 'itIT', '', 'Sei legata alla Regina e a tutta Selvarden, proprio come me. Eppure invidio i tuoi sogni di casa.', 38134),
(198116, 'itIT', '', 'Sento un profondo legame con questo regno. Tuttavia, nei miei sogni sorvolo ancora Azeroth.', 38134),
(198115, 'itIT', 'Che c\'è, recluta?', '', 38134),
(198112, 'itIT', '', 'La lancia! Usatela per abbatterla!', 38134),
(198111, 'itIT', '', 'Mortali! Prendete la mia lancia!', 38134),
(198108, 'itIT', '', 'Sono un Tirnenn. Sono parte della foresta, sono legato ai suoi alberi.\n\nQuando la Regina dell\'Inverno allungò la sua mano e creò Selvarden, noi fummo tra i primi a crescere. Affondammo le radici in profondità e sollevammo alte pareti. Costruimmo i boschi.\n\nI Tirnenn serviranno fino all\'ultimo incommensurabile respiro vitale, per poi tornare a far parte del ciclo.', 38134),
(198105, 'itIT', '', 'Sarò la tua fine!', 38134),
(198103, 'itIT', '', 'Perché mi disturbi? Non vedi che sono occupata?', 38134),
(198099, 'itIT', '', 'Questa lancia ti trafiggerà il cuore!', 38134),
(198095, 'itIT', '', 'Capoguerra, questa è la recluta di cui parlavo.', 38134),
(198094, 'itIT', 'Ma certo, mia Regina. Non ti deluderemo.', '', 38134),
(198093, 'itIT', '', 'Sono certa che non lo farai.', 38134),
(198092, 'itIT', '', 'Nathanos, informa il nuovo arrivo sulla missione in questione.', 38134),
(198089, 'itIT', '', 'Per vincere contro l\'Alleanza, l\'Orda ha bisogno di rinforzare i propri ranghi.', 38134),
(198088, 'itIT', '', 'Ho sentito parlare molto bene delle tue abilità, ne farò buon uso.', 38134),
(198087, 'itIT', '', 'Sembra che tu abbia occhio per il talento, Signora della Guerra.', 38134),
(198085, 'itIT', '', 'Mi serve un momento... per riposare i miei arti esausti...', 38134),
(198076, 'itIT', '', 'Altri sciocchi che vogliono ostacolarci. Non permettere loro di interferire.', 38134),
(198075, 'itIT', '', 'Un\'altra interruzione da parte delle guardie. Sbarazzati di loro.', 38134),
(198074, 'itIT', '', 'Stanno arrivando altre guardie. Assicurati che periscano in fretta.', 38134),
(198072, 'itIT', 'No! Non verrò confinato di nuovo!', '', 38134),
(198071, 'itIT', 'Per il potere del sole!', '', 38134),
(198070, 'itIT', 'Risorgi dalle ceneri!', '', 38134),
(198069, 'itIT', 'Vi avvolgerò nelle fiamme!', '', 38134),
(198068, 'itIT', 'Felomin ashal!', '', 38134),
(198067, 'itIT', 'Il fuoco vi consuma!', '', 38134),
(198066, 'itIT', 'Tutti subiranno la mia ira!', '', 38134),
(198065, 'itIT', 'Di certo... puoi fare... di meglio...', '', 38134),
(198062, 'itIT', '', 'Tirna Vaal è uno dei boschi più fortunati. Forse potrebbe sopravvivere. La Regina farà tutto ciò che è in suo potere perché accada.', 38134),
(198061, 'itIT', 'Starai... scherzando...', '', 38134),
(198057, 'itIT', 'Mai più! Il mondo brucerà!', '', 38134),
(198056, 'itIT', 'Non posso sopportare questo tormento!', '', 38134),
(198055, 'itIT', 'Sì... il mio potere... ritorna!', '', 38134),
(198053, 'itIT', 'Sento... le mie forze... tornare...', '', 38134),
(198052, 'itIT', 'Quindi... chi mi ha ucciso... è qui per salvarmi...', '', 38134),
(198051, 'itIT', '$gMio caro:Mia cara; $n, sono felice che io e te siamo finalmente sulla stessa lunghezza d\'onda...\n\n<Teotar fa l\'occhiolino.>\n\nDobbiamo assolutamente prenderci un tè insieme al più presto.\n\nDama Sibilla chiede sempre come stai.', '', 38134),
(198050, 'itIT', 'Non vedo l\'ora di goderci un po\' di tempo insieme.', '', 38134),
(198049, 'itIT', 'Il tuo tormento ti attende.', '', 38134),
(198048, 'itIT', 'Solealto... appartiene... a noi...', '', 38134),
(198047, 'itIT', 'I traditori di Renathal??? Guardie, fermateli!', '', 38134),
(198045, 'itIT', 'L\'ira è il tuo peccato più potente, Solealto. Ci tornerà utile.', '', 38134),
(198044, 'itIT', 'Una volta spezzate queste catene, tu sarai il primo a bruciare!', '', 38134),
(198043, 'itIT', '', 'Perché tocca sempre a me salvarvi?', 38134),
(198039, 'itIT', '', 'Denathrius tiene le sue anime più potenti in questa stanza. Alimenta i loro più oscuri istinti, plasmandole in armi da scagliare contro i suoi nemici.', 38134),
(198033, 'itIT', '', 'Dovremmo rimanere uniti. I nostri nemici potrebbero sopraffarci se fossimo da soli.', 38134),
(198031, 'itIT', '', 'Rimani con me. La mia magia ha una portata limitata.', 38134),
(198025, 'itIT', '', 'Tu segui il fiume, io ti coprirò le spalle dalla riva. Speriamo che la vera Tyrande ci aspetti più avanti.', 38134),
(198018, 'itIT', 'Quando sei $gpronto:pronta; ti porterò al Forte Elisio.', '', 38134),
(198004, 'itIT', '', 'Niente è regalato, tutto si guadagna. Inclusi i materiali per la tua impugnatura.', 38134),
(197971, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nE TU, $gmio carissimo:mia carissima; Calcafauce, mi ricordi un elegante parasole in tutto e per tutto!\n\nNon sono certo di quale sia il risultato di tutto per tutto... ma adoro i parasole, quindi sono sicuro che sia un complimento!', '', 38134),
(197970, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nE le tue supposizioni, $gmio carissimo:mia carissima; Calcafauce, sono assolutamente corrette.\n\nChiamerò il mio prossimo delizioso tè in tuo onore.\n\nChe ne pensi di \"Tè caldo di $n\"?', '', 38134),
(197969, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nSono assolutamente d\'accordo, Calcafauce.\n\nHo cercato di convincere molti alla corte della Contessa di quel preciso concetto, ma nessuno è di larghe vedute.\n\nForse potremmo convincerli insieme.', '', 38134),
(197968, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nImmagino che il tuo precedente messaggio fosse destinato a Tubbins.\n\nStai pur $gcerto:certa; che gliel\'ho riferito.\n\nI migliori auguri per le tue imprese future,\nTeotar', '', 38134),
(197967, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nLa tua offerta mi intriga, $n.\n\nDovremmo discuterne meglio durante una passeggiata attraverso l\'Essiccazione sotto il mio parasole.\n\nIo porterò il tè se tu porterai i biscottini.', '', 38134),
(197966, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nE poi dovremmo prenderci una tazza di tè insieme... o venti.\n\nCerte volte mi viene una tale sete.', '', 38134),
(197965, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nDevo ammettere che, dopo aver letto il tuo messaggio, il mio volto è più rosso della fascia che copre le vergogne di Denathrius!', '', 38134),
(197964, 'itIT', '<Teotar ha risposto al tuo messaggio scrivendo con una magnifica calligrafia.>\n\nOh, mortale $gdispettoso:dispettosa;!\n\nÈ stato incantevole trovare il tuo recente appunto scribacchiato a margine.\n\nE, per la cronaca, sono d\'accordo con la tua valutazione.', '', 38134),
(197957, 'itIT', '', 'Riposati, questo combattimento durerà a lungo.', 38134),
(197951, 'itIT', 'Sono felice di rivederti, $gcaro:cara; $n. Dobbiamo assolutamente prenderci un tè insieme al più presto.\n\nDama Sibilla chiede sempre come stai.', '', 38134),
(197945, 'itIT', '<Questo teletrasporto permette l\'accesso agli altri livelli della torre.>', '', 38134),
(197942, 'itIT', 'E una buona giornata anche a lei, Marchese. I miei ossequi!', '', 38134),
(197941, 'itIT', 'È alquanto raffinata, vero? Grazie per averlo notato, Marchese...', '', 38134),
(197940, 'itIT', 'Non indugiamo. Alcune delle mie passeggiate preferite le ho fatte di corsa.', '', 38134),
(197917, 'itIT', 'Ho sentito che i denti aguzzi su al castello, ultimamente sono più... \"loro stessi\". Fa\' attenzione, non è molto sicuro quando diventano... \"loro stessi\", per così dire.', '', 38134),
(197916, 'itIT', 'Qualcuno ha detto che hai visto altri regni. Ci sono Dragatori in qualcuno di essi? \n\nI ragazzi da queste parti sono simpatici, ma uno ha la necessità d\'incontrare nuova gente dopo un migliaio di anni.', '', 38134),
(197915, 'itIT', 'I denti aguzzi qui non sono male come negli altri posti. Anche se questa siccità li ha resi ancora più traditori.', '', 38134),
(197912, 'itIT', 'Ehi! Ho sentito dire che hai tirato fuori Squartacus e Mazzafaccia da Ultimo Limo tutti interi, con ancora gli arti attaccati e tutto il resto. \n\nSarai sempre $il benvenuto:la benvenuta; qui, non importa cosa dicano i denti aguzzi. Noi Dragatori apprezziamo chi lotta per noi.', '', 38134),
(197911, 'itIT', 'Chiedo scusa, ma quanto sei importante? Tipo su una scala da poltigeno a divinità? \n\nCerco di capire quale livello di paroloni devo usare quando parlo con te.', '', 38134),
(197910, 'itIT', 'Ho sentito dire che eri qui in giro. I pettegolezzi sono l\'unica cosa da queste parti che viaggia più rapidamente di un\'anima destinata alla Fauce.', '', 38134),
(197909, 'itIT', '$GBenvenuto:Benvenuta; a Lidoscuro. Casa di coloro che fungono da nutrimento per la loro pietà e invidia. \n\nFai attenzione a come chiedi aiuto qui in giro.', '', 38134),
(197908, 'itIT', '', 'Questo regno cerca d\'ingannare i nostri sensi. Continuiamo a muoverci, vedo una scia di cadaveri più avanti.', 38134),
(197907, 'itIT', 'Non troverai mai la Guerriera della Notte!', '', 38134),
(197906, 'itIT', '', 'Per sempre.', 38134),
(197905, 'itIT', '', 'Piccola sciocca. Non c\'è modo di lasciare questo luogo, ma puoi unirti a me...', 38134),
(197903, 'itIT', '', 'I miei sogni mi hanno condotta qui, siamo venuti a salvarti.', 38134),
(197902, 'itIT', '', 'Shandris? Perché sei qui?', 38134),
(197901, 'itIT', '', 'Minn\'do!', 38134),
(197879, 'itIT', '', 'Un\'altra preda conquistata. Ne ho abbastanza di queste... creature. I miei cacciatori penseranno al resto.', 38134),
(197877, 'itIT', '', 'Questi parassiti sono ancora peggio da vicino.', 38134),
(197870, 'itIT', 'Vi dissezionerò io stesso!', '', 38134),
(197869, 'itIT', 'Fantastico, altre cavie! Liberate i Trivellapiaga!', '', 38134),
(197855, 'itIT', 'Sei $gpronto:pronta; a iniziare la cerimonia?', '', 38134),
(197854, 'itIT', '', 'Sai che sono una spada volante, vero? Posso attraversare facilmente queste sbarre.', 38134),
(197852, 'itIT', '', 'Oh, guarda quanti rifornimenti! Con questi potrei creare delle Ragnatele dell\'Anima per parecchio tempo.$b$bIn cambio, potrei offrirti qualcosa per gestire l\'ostilità della foresta, permettendoti di uscire dal combattimento o sorprendere un nemico a tuo piacimento.$b$bSiamo d\'accordo?', 38134),
(197850, 'itIT', 'È un bene che tu sia qui. Sono a corto di gorm da forgiare, se capisci cosa intendo.$b$BRifornimenti, dici? Be\', non li rifiuterò di certo. Probabilmente potrei creare qualcosa che ti darebbe degli amici temporanei se li dai a me. Che ne dici?', '', 38134),
(197848, 'itIT', '', 'Rifornimenti? Per me? Ma la Dama...$b$bAccetterò volentieri questo dono. Ho portato le nostre pitture di guerra e sarei felice di offrirle in cambio. Ti benediranno con la forza e la rapidità della caccia.', 38134),
(197846, 'itIT', '', 'Ah, i rifornimenti dalla Bottega della Crescita! Visto che la zona è sotto la mia supervisione, è saggio da parte tua portarle da me per tenerle al sicuro.$b$bHo giusto qualcosa per sollevarti il morale e darti una bella spinta, se le darai a me.', 38134),
(197843, 'itIT', 'Forza, facciamoci un giro alla Coda della Viverna. Puoi offrirmi da bere!', '', 38134),
(197839, 'itIT', 'Facciamo visita a un istruttore per apprendere come specializzare le tue abilità!', '', 38134),
(197834, 'itIT', '', 'Raddrizzerò i torti di quest\'eternità.', 38134),
(197833, 'itIT', 'Questo t\'insegnerà le buone maniere, canaglia!', '', 38134),
(197832, 'itIT', 'Per la Regina, lo spero.', 'Per la Regina, lo spero.', 38134),
(197831, 'itIT', 'Quella è... una nuova anima? Stanno finalmente tornando a Selvarden?!?', 'Quella è... una nuova anima? Stanno finalmente tornando a Selvarden?!?', 38134),
(197830, 'itIT', '', 'Il fianco occidentale è bloccato. Mograine ha bisogno di rinforzi!', 38134),
(197829, 'itIT', '', 'Qui la mia abilità nel teletrasporto è limitata, ma dovrei essere in grado di aprire un portale dove si trova il mio bastone.', 38134),
(197827, 'itIT', '', 'È stato un errore che non ripeterò', 38134),
(197826, 'itIT', '', 'Sono ferita... dammi un momento!', 38134),
(197820, 'itIT', '', 'Va\'... con Coraggio...', 38134),
(197819, 'itIT', '', 'Dolore... agonia...!', 38134),
(197817, 'itIT', '', 'Questa è opera di Tyrande. L\'ho vista fare a pezzi questi mostri nei miei sogni.\n\nSembra che ci sia una scia di cadaveri, e sembrano freschi... Come se fosse appena stata qui!', 38134),
(197815, 'itIT', '', 'Il luogo mostrato nella mia visione si trova più avanti. Vedremo se lì riusciremo a trovare delle risposte.', 38134),
(197814, 'itIT', 'Be\', sembra che sua Ciambellanza Suprema abbia un bel lavoro per me senza alcuna indicazione su quanto tempo richiederà.\n\nSembra che dovrò guidare questo carro finché i denti aguzzi o le ali non mi dicono di fermarmi. Tieni gli occhi aperti per me.\n\nC\'hai fatto un grosso favore a noi Dragatori salvandomi da quel divoratore. Non importa cosa accadrà, ti copriremo le spalle.', '', 38134),
(197812, 'itIT', '', 'Tutto a posto, procediamo.', 38134),
(197811, 'itIT', '', 'Quando troveremo Anduin, il mio bastone servirà da ancora per teletrasportarci in salvo.', 38134),
(197810, 'itIT', '', 'Sento la paura della traditrice. Si trova nel Deposito Decrepito.', 38134),
(197809, 'itIT', 'Le nostre forze rimaste resteranno qui di guardia. Dacci un segnale quando trovi questa \"pietra di transito\".', '', 38134),
(197808, 'itIT', 'Non possiamo lasciare queste anime innocenti al loro destino.', '', 38134),
(197807, 'itIT', 'Guardati le spalle là fuori e metti una parola buona per me con la Capoguerra!', '', 38134),
(197806, 'itIT', 'Ho sempre saputo che avresti fatto grandi cose.', '', 38134),
(197805, 'itIT', '', 'La Capoguerra ti attende al Mastio Grommash.', 38134),
(197804, 'itIT', '', 'La tua presenza è richiesta al Mastio Grommash. Non far aspettare la Capoguerra...', 38134),
(197803, 'itIT', '', 'Recluta, porto notizie dalla Capoguerra!', 38134),
(197799, 'itIT', 'Che cosa ci fai qui? È troppo pericoloso andare in giro da $gsolo:sola;.', 'Che cosa ci fai qui? È troppo pericoloso andare in giro da $gsolo:sola;.', 38134),
(197798, 'itIT', '', 'Mi conforta vederti in questo strano luogo. Per il bene di Tyrande, devo chiederti aiuto.', 38134),
(197797, 'itIT', '', 'Va bene. Metterò da parte il mio dolore... per ora. Ma sappi che non verrà mai dimenticato.', 38134),
(197796, 'itIT', 'Shandris, so cosa vuol dire portare le cicatrici della guerra, ma il Calcafauce potrebbe essere l\'unica speranza di salvare Tyrande.', '', 38134),
(197795, 'itIT', '', 'Non metterò una vita che mi sta così tanto a cuore nelle mani di un soldato dell\'Orda.', 38134),
(197794, 'itIT', '', 'Niente è mai scappato dalla Fauce. Perlomeno finché certi mortali non hanno dimostrato di potervi fare ritorno.', 38134),
(197789, 'itIT', 'Fai con calma, ho molto lavoro che mi tiene occupato.', '', 38134),
(197788, 'itIT', 'Quegli altri Dragatori sono distratti... Lasciano i loro strumenti praticamente ovunque.', '', 38134),
(197787, 'itIT', 'Ma è tutto quello che otterrai da me. Trova gli altri da $gsolo:sola;!', '', 38134),
(197786, 'itIT', 'Ti darò il mio martello di scorta per il primo.', '', 38134),
(197782, 'itIT', 'Nessuno disturba padrone!', '', 38134),
(197781, 'itIT', '', 'La caccia chiama, $n.', 38134),
(197779, 'itIT', '', 'Devi proteggere il futuro di Azeroth!', 38134),
(197777, 'itIT', '', 'Stavo cercando di portare queste anime in un posto in cui potessero rilasciare il proprio Animum, ma è andato tutto a rotoli!\n\nLa fauna di Selvarden non è mai stata così aggressiva prima della siccità. Devo riportarli al sicuro alla Radura dello Spirito.\n\nAiutami!', 38134),
(197776, 'itIT', '', 'Te\'zan! Hai trovato aiuto!', 38134),
(197775, 'itIT', 'Ecco un posto sicuro! Sulla tua testa.', '', 38134),
(197772, 'itIT', 'Che creatura strana che sei.', '', 38134),
(197770, 'itIT', '', 'Calcafauce, usa la zanna cava per dare il segnale d\'inizio della caccia.', 38134),
(197768, 'itIT', '', 'Prosciuga l\'Animum dal Maestro dei Segugi Loksey, Calcafauce.', 38134),
(197766, 'itIT', 'Questo scambio non ha più valore. Considerate conclusi i nostri affari, per ora.', '', 38134),
(197760, 'itIT', 'Ottima scelta!', '', 38134),
(197758, 'itIT', '', 'Posso renderci di nuovo invisibili, datemi un attimo.', 38134),
(197754, 'itIT', 'Jaina, puoi nasconderci alla loro vista?', '', 38134),
(197752, 'itIT', '', 'La pietra reagisce alla tua presenza! Prova ad attivarla mentre teniamo a bada l\'esercito del Carceriere!', 38134),
(197748, 'itIT', 'La Morte giunge per l\'anima del vostro mondo! Tutto ciò che vedete, che conoscete, non sarà più!', '', 38134),
(197747, 'itIT', '', 'È quasi carica! Non arrendetevi!', 38134),
(197746, 'itIT', 'Abbiamo già fermato eserciti infiniti. Non ci arrenderemo mai!', '', 38134),
(197745, 'itIT', 'Le mie forze sono infinite!', '', 38134),
(197744, 'itIT', '', 'Mantenete la posizione! La Pietra di Transito deve caricarsi!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197743, 'itIT', 'Giurafauce! Prendeteli!', '', 38134),
(197742, 'itIT', 'Una reliquia dei Primi... che reagisce a creature mortali?!? Impossibile!', '', 38134),
(197740, 'itIT', '', 'Il mio incantesimo può farci superare le guardie, ma ce ne sono alcune che non si faranno ingannare.', 38134),
(197739, 'itIT', 'Le forze del Carceriere sembrano infinite. Non abbiamo modo di combatterle tutte.', '', 38134),
(197738, 'itIT', 'Qui! Questa barriera ci proteggerà!', '', 38134),
(197736, 'itIT', 'Se avessi saputo che tradire un dente aguzzo per un altro mi avrebbe fatto finire nella melma credo che mi sarei tenuto la bocca chiusa. Hai dei rubini con cui accendere la mia torcia?', '', 38134),
(197734, 'itIT', '', 'I vostri peccati schiacceranno i vostri alleati.', 38134),
(197733, 'itIT', '', 'Morite insieme!', 38134),
(197732, 'itIT', '', 'I vostri destini sono legati!', 38134),
(197731, 'itIT', '', 'Non vi è redenzione per i tuoi peccati!', 38134),
(197730, 'itIT', '', 'I tuoi peccati ti tormenteranno in eterno.', 38134),
(197729, 'itIT', '', 'Sarà piacevole estrarre i tuoi peccati.', 38134),
(197728, 'itIT', '', 'Catastrofico!', 38134),
(197727, 'itIT', '', 'La terra si frantuma!', 38134),
(197726, 'itIT', '', 'La distruzione regna!', 38134),
(197725, 'itIT', '', 'Cuori vincolati all\'oscurità!', 38134),
(197724, 'itIT', '', 'Che il peccato ti consumi il cuore!', 38134),
(197723, 'itIT', '', 'Ti frantumerò il cuore!', 38134),
(197722, 'itIT', '', 'Dev\'essere lo Scopo. Ci guida sempre.', 38134),
(197721, 'itIT', '', 'Quella creatura ha lo stesso fetore della Fauce!', 38134),
(197720, 'itIT', '', 'L\'Animum si scatena, annunciando la vostra fine!', 38134),
(197718, 'itIT', '', 'Grazie, $gcampione:campionessa;.', 38134),
(197717, 'itIT', '', 'Dalla Fauce? Mi rifiuto di crederci!', 38134),
(197716, 'itIT', '', 'Sta succedendo tutto così in fretta! Il mio bosco sta morendo!', 38134),
(197710, 'itIT', '', 'La nostra fede nello Scopo è messa alla prova?', 38134),
(197709, 'itIT', '', '$Un intruso:Un\'intrusa;? Com\'è possibile?', 38134),
(197708, 'itIT', '', 'È appena $gscappato:scappata; dalla Fauce? Impossibile!', 38134),
(197705, 'itIT', 'La Casata dei Rituali sta eseguendo un rituale di potenziamento qui. Se non riesci a fermarli, puoi anche distruggere ciò che ne salta fuori.', '', 38134),
(197704, 'itIT', 'Nonostante tutte le loro vili magie, le ossa dei ritualisti si spezzano come quelle degli altri. Ottimo lavoro.', '', 38134),
(197703, 'itIT', 'L\'oblio è un destino troppo clemente per aver tradito Krexus. Grazie per aver fatto giustizia.', '', 38134),
(197702, 'itIT', 'I traditori della mia vecchia casata hanno una fortezza qui. Distruggili tutti quanti!', '', 38134),
(197701, 'itIT', 'Ma come ne controllo l\'autenticità?', '', 38134),
(197695, 'itIT', '', 'Oranomonos è impazzita a causa dei gorm che la stanno divorando. Ti prego, metti fine alla sofferenza di quella povera Tirnenn.', 38134),
(197694, 'itIT', '', 'Oggi i gorm non banchetteranno. Addio, amica mia, sei stata una vera protettrice di Selvarden.', 38134),
(197691, 'itIT', 'Raccogli il tributo dagli abitanti dove riesci e perlustra la zona per non perdertene neanche una goccia.', 'Raccogli il tributo dagli abitanti dove riesci e perlustra la zona per non perdertene neanche una goccia.', 38134),
(197689, 'itIT', 'A cosa servono tutti i rituali se poi non ci salviamo dalla Fauce?', 'A cosa servono tutti i rituali se poi non ci salviamo dalla Fauce?', 38134),
(197688, 'itIT', 'Aiutaci a completare il rituale di redenzione e poi potremo parlare con te.', 'Aiutaci a completare il rituale di redenzione e poi potremo parlare con te.', 38134),
(197687, 'itIT', 'Non avevo pensato che si sarebbero uccisi a vicenda, ma facciamo ciò che dobbiamo per il bene superiore. Denathrius deve andarsene. Addio.', 'Non avevo pensato che si sarebbero uccisi a vicenda, ma facciamo ciò che dobbiamo per il bene superiore. Denathrius deve andarsene. Addio.', 38134),
(197669, 'itIT', '', 'Penso di sapere dove trovare un altro Specchio dell\'Anima. Raggiungi la caverna a nord, ci vedremo là.', 38134),
(197668, 'itIT', '', 'Finché vi sarà determinazione, non ci sarà una vera sconfitta.', 38134),
(197665, 'itIT', '', 'Non è ancora il tuo momento, abitante di Azeroth.', 38134),
(197664, 'itIT', '', 'La sconfitta porta saggezza.', 38134),
(197663, 'itIT', '', 'Anche i migliori tra noi a volte vengono sopraffatti.', 38134),
(197662, 'itIT', '', 'La prossima volta il tuo alleato ti aiuterà a evitare la sconfitta, ne sono certa.', 38134),
(197661, 'itIT', '', 'Farai meglio la prossima volta.', 38134),
(197660, 'itIT', '', 'Nessuno avrebbe potuto prevederlo.', 38134),
(197659, 'itIT', 'Avrei dovuto proprio portare la giacca elegante.', '', 38134),
(197658, 'itIT', 'Certe tradizioni sono fatte per essere superate.', '', 38134),
(197657, 'itIT', 'È tutto così entusiasmante!', '', 38134),
(197656, 'itIT', 'Rilassati, distenditi. A tutti serve una pausa di tanto in tanto.', '', 38134),
(197654, 'itIT', 'C\'è grande valore nella semplicità.', '', 38134),
(197651, 'itIT', 'Che gran divertimento!', '', 38134),
(197650, 'itIT', 'Occhio a dove metti i piedi! C\'è un putiferio.', '', 38134),
(197635, 'itIT', 'Una vittoria decisiva! Ci rivedremo fuori da Concacupa.', '', 38134),
(197632, 'itIT', 'Be\', mi sembra grandiosa! Usala per proteggere Selvarden.', '', 38134),
(197631, 'itIT', 'I nostri artigiani possono creare cose meravigliose. Hanno solo bisogno che qualcuno porti loro risorse. Qualcuno come te!', '', 38134),
(197630, 'itIT', 'Ai tuoi ordini!', '', 38134),
(197629, 'itIT', '', 'Tu, laggiù! Di\' al Barone di racimolare un po\' d\'Animum se vuole la sua Guardia d\'Ossa!', 38134),
(197627, 'itIT', '', 'Sei $gpronto:pronta;?', 38134),
(197624, 'itIT', '', 'Altri ordini? Cosa c\'è ora?', 38134),
(197623, 'itIT', '', 'Preferirei cavalcare sul dorso di un Metalupo piuttosto che essere nella sua pancia.', 38134),
(197622, 'itIT', '', 'Sta cercando di assorbire l\'Animum! Usa la fiala!', 38134),
(197621, 'itIT', '', 'Ascoltate! Chi perderà, trasporterà il metallo alla forgia! E ora, combattete!', 38134),
(197620, 'itIT', 'C\'è così poco Animum, e ho dovuto dire addio a molti spiriti dormienti. Amici di cui mi sono curato per generazioni.\n\nMa l\'Anziana Dunmira è così prossima al risveglio, non potevo dirle addio così!\n\nHo spostato il suo Semebrado in un punto in cui poteva ricevere un pochettino d\'Animum per farcela, e poi quella creatura orribile l\'ha rubato! Credo che l\'uccello stia cercando di covarlo come un uovo.', '', 38134),
(197614, 'itIT', '', 'Un piano così elaborato per sconfiggermi, e ancora resisto. Facciamola finita.', 38134),
(197611, 'itIT', 'Nuovi ordini! Marciamo verso ovest! Muoversi!', '', 38134),
(197603, 'itIT', 'L\'ora è giunta! Truppe, difendete il confine.', '', 38134),
(197602, 'itIT', 'La collezione privata di Sire Denathrius. Raccolta per eoni da tutte le Terretetre.', '', 38134),
(197601, 'itIT', 'Interessante, i combattimenti hanno allontanato le guardie.', '', 38134),
(197598, 'itIT', 'Sono al comando di eserciti infiniti!', '', 38134),
(197597, 'itIT', 'Con questo potere, la realtà crolla!', '', 38134),
(197595, 'itIT', 'Il potere di questa reliquia fa tremare gli dei!', '', 38134),
(197594, 'itIT', '', 'Mi sono stancato di questa farsa. Mostratevi, e vi concederò la grazia di una morte rapida.', 38134),
(197593, 'itIT', 'Il potere arcano scorre dentro di me!', '', 38134),
(197590, 'itIT', 'Morite insieme!', '', 38134),
(197589, 'itIT', 'La distorsione incombe!', '', 38134),
(197571, 'itIT', 'Cavoli, è lontano. È meglio se mi stai vicino.', '', 38134),
(197570, 'itIT', 'Hanno invaso il Sepolcro della Conoscenza.', '', 38134),
(197569, 'itIT', 'Le creature vengono fuori da questo tunnel.', '', 38134),
(197564, 'itIT', 'Attenti a dove mettete i piedi!', '', 38134),
(197563, 'itIT', 'Non si può trattare con la morte!', '', 38134),
(197562, 'itIT', 'Distruzione assicurata!', '', 38134),
(197559, 'itIT', 'È bello tornare a Orgrimmar!', '', 38134),
(197552, 'itIT', '', 'Oh, che noia. Se ti comporterai a modo, forse ti lascerò trafiggere alcuni camerieri.', 38134),
(197551, 'itIT', 'Non condivido la tua passione per queste frivolezze. Preferisco i raduni in battaglia.', '', 38134),
(197550, 'itIT', '', 'Non c\'è di che, tesoro. Finché il vino scorrerà a fiumi, avrai la mia attenzione.', 38134),
(197549, 'itIT', 'Vi ringrazio entrambi per la vostra presenza. Non sarebbe lo stesso senza di voi.', '', 38134),
(197548, 'itIT', 'Cessate questa follia! Avete dissacrato abbastanza questo raduno!', '', 38134),
(197547, 'itIT', 'I vostri passi sono deliziosi! È un peccato che dobbiate morire.', '', 38134),
(197545, 'itIT', 'Spallucce a destra!', '', 38134),
(197544, 'itIT', 'Spallucce a destra!', '', 38134),
(197543, 'itIT', 'Volteggio a sinistra!', '', 38134),
(197542, 'itIT', 'Volteggio a sinistra!', '', 38134),
(197541, 'itIT', 'Saltelli in avanti!', '', 38134),
(197540, 'itIT', 'Saltelli in avanti!', '', 38134),
(197539, 'itIT', 'Via alle danze!', '', 38134),
(197538, 'itIT', 'Via alle danze!', '', 38134),
(197537, 'itIT', '', 'Via alle danze!', 38134),
(197536, 'itIT', '', 'Spallucce a destra!', 38134),
(197535, 'itIT', '', 'Volteggio a sinistra!', 38134),
(197534, 'itIT', '', 'Saltelli in avanti!', 38134),
(197533, 'itIT', 'Tutti quanti, in posizione! È l\'ora della danza macabra!', '', 38134),
(197532, 'itIT', 'Attenzione! In formazione per la danza macabra!', '', 38134),
(197531, 'itIT', '', 'Cari ospiti e ignobili intrusi, ai vostri posti per la danza macabra!', 38134),
(197530, 'itIT', '', 'Non credo stia rispondendo a noi. Sta rispondendo al campione.', 38134),
(197529, 'itIT', 'Non riesco a capire come attivarla.', '', 38134),
(197528, 'itIT', 'Si dice che la pietra fosse dormiente, ma sembra che reagisca alla nostra presenza!', '', 38134),
(197525, 'itIT', 'I nostri esploratori hanno avvistato la Cacciapaura lungo la strada per Concacupa. Preparati in fretta.', '', 38134),
(197521, 'itIT', '', 'Uccidendo i miei Frangifede mi stai rivelando la tua posizione. La tua fine si avvicina.', 38134),
(197518, 'itIT', 'Il tempo è denaro, andiamo!', '', 38134),
(197516, 'itIT', '', 'Ci incontreremo al maniero.', 38134),
(197515, 'itIT', 'La tua missione è di vitale importanza. La trappola dovrà essere organizzata bene.', '', 38134),
(197508, 'itIT', 'Dovevo prendermene cura... ma poi sono stati presi!', '', 38134),
(197507, 'itIT', 'Piumaleggera! Di qua!', '', 38134),
(197505, 'itIT', '', 'Il mio sonno... è terminato...', 38134),
(197500, 'itIT', '', 'Sarete sempre... sotto di me...', 38134),
(197493, 'itIT', '', 'Non eravate sulla lista degli invitati!', 38134),
(197489, 'itIT', '', 'Segui la via.', 38134),
(197487, 'itIT', 'Il nostro ultimo pezzo è iniziato! Ballate fino alla morte!', '', 38134),
(197485, 'itIT', 'Trovatevi un compagno, o morite!', '', 38134),
(197483, 'itIT', 'Tutti in pista!', '', 38134),
(197482, 'itIT', 'Prego, amici! Danzate fino a sanguinare!', '', 38134),
(197476, 'itIT', 'Ma sono... l\'anima... della festa...', '', 38134),
(197472, 'itIT', 'Come osate rovinare la festa con abiti del genere!', '', 38134),
(197465, 'itIT', '', 'Tieni gli occhi fissi al cielo. I larion trascorrono la maggior parte della loro vita in volo!', 38134),
(197464, 'itIT', 'Tutto quell\'Animum è una tentazione per chiunque. Addio!', 'Tutto quell\'Animum è una tentazione per chiunque. Addio!', 38134),
(197463, 'itIT', 'Sconveniente che la mia nota sul tributo sia stata trovata, ma un piccolo sussurro qua e là ed erano già disposti a incolparsi a vicenda.', 'Sconveniente che la mia nota sul tributo sia stata trovata, ma un piccolo sussurro qua e là ed erano già disposti a incolparsi a vicenda.', 38134),
(197462, 'itIT', 'Oh! Che disastro!', 'Oh! Che disastro!', 38134),
(197460, 'itIT', '', 'Devo fare rapporto alla Capoguerra. Per ora, Cork ti farà fare un giro della città.', 38134),
(197459, 'itIT', 'Che tipo... di strategia... era...', '', 38134),
(197454, 'itIT', 'Splendido! Una battaglia da vincere!', '', 38134),
(197453, 'itIT', '', 'Sire... Ti ho deluso...', 38134),
(197452, 'itIT', '', 'La vostra patetica ribellione è terminata.', 38134),
(197451, 'itIT', '', 'Il tuo Animum è mio!', 38134),
(197450, 'itIT', '', 'Feccia traditrice.', 38134),
(197449, 'itIT', '', 'Strapperò le vostre anime!', 38134),
(197448, 'itIT', 'Impossibile! Io sono... eterno...', '', 38134),
(197447, 'itIT', 'La vostra ribellione è terminata.', '', 38134),
(197446, 'itIT', 'Il mio dominio è incontestabile.', '', 38134),
(197445, 'itIT', 'La tua ribellione è finita!', '', 38134),
(197443, 'itIT', 'Il tuo orgoglio sarà la tua rovina!', '', 38134),
(197439, 'itIT', '', 'La nostra unica possibilità è di tenerle a bada abbastanza a lungo perché la pietra si carichi. Ogni secondo conta!', 38134),
(197438, 'itIT', '', 'Quando il Carceriere scoprirà il nostro piano, manderà tutte le sue forze contro di noi.', 38134),
(197434, 'itIT', '', 'Ero una delle migliori... Perdonami, Maestro.', 38134),
(197432, 'itIT', '', 'Sembra che la preda abbia scioccamente cercato il predatore!', 38134),
(197425, 'itIT', '', 'Raggiungiamola prima che il Carceriere se ne accorga.', 38134),
(197424, 'itIT', '', 'Guardate! Sulla piattaforma! Quella dev\'essere la pietra di transito!', 38134),
(197423, 'itIT', 'Questo destino è meglio di ciò che ti attende...', '', 38134),
(197422, 'itIT', 'Ahah! Il primo sangue sarà mio!', '', 38134),
(197421, 'itIT', '', 'Per Selvarden!', 38134),
(197420, 'itIT', '', 'Per Selvarden!', 38134),
(197419, 'itIT', '', 'Per Selvarden!', 38134),
(197418, 'itIT', 'Per Selvarden!', '', 38134),
(197417, 'itIT', 'Come comandi, mia Regina!', '', 38134),
(197416, 'itIT', 'Lei... sarà la tua fine.', '', 38134),
(197415, 'itIT', 'Eccoti tra le mie braccia! La tua testa mi sarà utile.', '', 38134),
(197414, 'itIT', '', 'I Drust credono che impadronirsi del Bosco del Risveglio li porterà alla vittoria, ma si sbagliano.', 38134),
(197412, 'itIT', 'Argh, non riesco a trovare una traccia.', '', 38134),
(197410, 'itIT', '', 'Il Calcafauce, ho detto! Dov\'è? Parla o ti dissanguo!', 38134),
(197409, 'itIT', '', 'Baccaluna, Herne, Aliothe, Zayhad! Riconquistate il controllo delle ancore del bosco. Espellerò questi intrusi grazie al vincolo delle nostre anime!', 38134),
(197408, 'itIT', 'Dolore, hai fiutato qualcosa? Il Calcafauce dev\'essere nei paraggi.', '', 38134),
(197405, 'itIT', 'Sei una macchia su questo regno. Vattene!', 'Sei una macchia su questo regno. Vattene!', 38134),
(197404, 'itIT', 'Gli Alienatori commerciano con chiunque, vero...', 'Gli Alienatori commerciano con chiunque, vero...', 38134),
(197403, 'itIT', 'Non rivolgermi la parola, mortale!', 'Non rivolgermi la parola, mortale!', 38134),
(197402, 'itIT', '', 'Devo restare e curare le mie ferite. Dovrai aiutare a proteggere le guglie al posto mio!', 38134),
(197394, 'itIT', 'Meraviglioso!', '', 38134),
(197393, 'itIT', 'Morire è la cosa migliore che hai fatto!', '', 38134),
(197392, 'itIT', 'Splendido! Speravo di provare queste reliquie su dei bersagli viventi.', '', 38134),
(197391, 'itIT', 'Sei tu... l\'alfa... adesso...', '', 38134),
(197390, 'itIT', 'Un\'altra caccia andata bene.', '', 38134),
(197389, 'itIT', 'Ai miei animaletti piacerà rosicchiare le tue ossa.', '', 38134),
(197388, 'itIT', 'Oggi i miei segugi banchetteranno con te.', '', 38134),
(197387, 'itIT', 'La caccia è aperta!', '', 38134),
(197385, 'itIT', '', 'Pelagos, io... Sì, certamente.', 38134),
(197384, 'itIT', 'Kleia, sei troppo debole per combattere. Permettimi di curare le tue ferite.', '', 38134),
(197379, 'itIT', 'Pietra schiaccia te!', '', 38134),
(197377, 'itIT', 'Non vale.', '', 38134),
(197376, 'itIT', 'Non muove tu.', '', 38134),
(197375, 'itIT', 'Pietra è più dura di testa.', '', 38134),
(197374, 'itIT', 'Ahia!', '', 38134),
(197373, 'itIT', 'Tempo di frantumare crani!', '', 38134),
(197371, 'itIT', 'Io vi spacca faccia!', '', 38134),
(197370, 'itIT', 'Schiaccia!', '', 38134),
(197369, 'itIT', 'Spiaccica!', '', 38134),
(197368, 'itIT', 'Maestro... sarà furioso... con voi...', '', 38134),
(197366, 'itIT', 'Schiacciarvi coi piedi fa bello suono.', '', 38134),
(197365, 'itIT', 'Ora tu piatto!', '', 38134),
(197364, 'itIT', 'Maestro sarà felice!', '', 38134),
(197363, 'itIT', 'Io vi schiaccia ossa!', '', 38134),
(197362, 'itIT', '', 'Questa siccità minaccia l\'intera foresta. La tua voce non è altro che un cinguettio solitario nella notte.', 38134),
(197358, 'itIT', 'Chi fa questo baccano???', '', 38134),
(197357, 'itIT', '', 'Alla fine, mi hai addirittura aiutato a realizzare i miei sogni, a diventare un\'Ascesa. Sono onorata di vincolare la mia anima a te.', 38134),
(197356, 'itIT', 'Quando hai fatto ritorno al Bastione eravamo entusiasti della notizia. Hai finito col diventare una persona su cui entrambi contiamo!', '', 38134),
(197355, 'itIT', '', 'Hai dimostrato presto di essere più di questo. Qualcuno su cui contare... un volto amico.', 38134),
(197353, 'itIT', '', 'Controllo il potere del Bastione stesso!', 38134),
(197352, 'itIT', '', 'La Lancia dell\'Arconte è mia!', 38134),
(197349, 'itIT', 'Immagina quanto ci divertiremo nella prossima corte... Dopo esserci ripresi.', '', 38134),
(197347, 'itIT', 'Che corte... Sono esausto!', '', 38134),
(197346, 'itIT', 'Su con la vita! Abbiamo una festa da annunciare!', '', 38134),
(197345, 'itIT', 'Farò in modo che tutto sia perfetto mentre tu definisci i dettagli!', '', 38134),
(197344, 'itIT', 'Cosa? Fidatevi di me, vi state sbagliando. Diamoci tutti una calmata e abbassiamo le balestre, altrimenti sparo.', 'Cosa? Fidatevi di me, vi state sbagliando. Diamoci tutti una calmata e abbassiamo le balestre, altrimenti sparo.', 38134),
(197343, 'itIT', 'Istinto? Tu? Questa è bella. Inoltre, un pipistrellino mi ha detto che Samu stava fregando Animum dalla cima del tributo. È lui che ci ha traditi.', 'Istinto? Tu? Questa è bella. Inoltre, un pipistrellino mi ha detto che Samu stava fregando Animum dalla cima del tributo. È lui che ci ha traditi.', 38134),
(197342, 'itIT', 'I suoi appunti codificati sul tributo, anzitutto. Ma non importa, non servono prove quando hai l\'istinto.', 'I suoi appunti codificati sul tributo, anzitutto. Ma non importa, non servono prove quando hai l\'istinto.', 38134),
(197341, 'itIT', 'Calma, Lajos. Non puntare la balestra verso Ilka. Che prove hai a tua disposizione?', 'Calma, Lajos. Non puntare la balestra verso Ilka. Che prove hai a tua disposizione?', 38134),
(197340, 'itIT', '', 'Sposteremo i bozzoli nei boschi a cui servono. Ti ringrazio per aver riportato l\'equilibrio.', 38134),
(197339, 'itIT', '', 'I bozzoli di falena del Bosco Luminoso stanno crescendo in modo incontrollato! Puoi aiutarmi a recuperarli dalle cime?', 38134),
(197334, 'itIT', 'Ahahah. Non ho idea di cosa tu stia parlando, Lajos. Ti sbagli.', 'Ahahah. Non ho idea di cosa tu stia parlando, Lajos. Ti sbagli.', 38134),
(197320, 'itIT', '', 'Il Calderone di Zovaal è ben difeso dalle forze del Carceriere. Sarebbe saggio passare senza essere scoperti.\n\nDammi l\'ordine e ci nasconderò alla loro vista.', 38134),
(197314, 'itIT', '', 'Gli Indegni si ritirano... Almeno dal Forte. Torna da noi, dobbiamo riorganizzarci.', 38134),
(197308, 'itIT', 'Che faccio adesso?!?', 'Che faccio adesso?!?', 38134),
(197303, 'itIT', '', 'Sempre.', 38134),
(197298, 'itIT', 'Li avvertirò mentre porti a termine la tua missione, ma Kleia... Ti prego, ritorna da me.', '', 38134),
(197297, 'itIT', '', 'Gli Indegni potrebbero attaccare ancora. Dobbiamo avvertire Requie dell\'Eroe.', 38134),
(197295, 'itIT', '', 'I miei Discepoli servono qui, devo informare immediatamente l\'Arconte. Presta servizio, e in fretta!', 38134),
(197290, 'itIT', '', 'Se il destino lo vorrà, i ricordi di Lysonia ci condurranno alle risposte che cerchiamo.', 38134),
(197288, 'itIT', '', 'Porta questo frammento al Locus Mnemonico, dove raccogliamo i ricordi di tutti i Kyrian.', 38134),
(197287, 'itIT', '', 'Informerò l\'Arconte su cosa è accaduto qui... E chiederò che le perdite vengano risarcite.', 38134),
(197286, 'itIT', '', 'L\'essenza di Lysonia è stata corrotta dalla Fauce. Dobbiamo capire com\'è potuto accadere.', 38134),
(197274, 'itIT', 'Le Guglie dell\'Ascensione apparterranno presto agli Indegni. Devos salverà le Terretetre dalla siccità e ci condurrà verso una nuova era!', '', 38134),
(197273, 'itIT', 'Una volta ho catturato un pesce di una specie sconosciuta. Gli ho dato il mio nome.\n\nNessun altro ha visto quel pesce, quindi non mi hanno creduto.', '', 38134),
(197272, 'itIT', '', 'Questo... cambia le cose. Forse Draka ha una flebile speranza di vittoria.', 38134),
(197268, 'itIT', '', 'È un\'eco dei suoi ricordi più profondi. Rivelerà la verità che si cela dietro il suo tradimento.', 38134),
(197267, 'itIT', 'Non posso parlare ora. Devo affilare le armi in caso si sveglino i Litogeni e ne abbiano bisogno.\n\nNon si svegliano spesso, ma quando capita si imbronciano se non c\'è una buona arma a disposizione.', '', 38134),
(197266, 'itIT', 'Vieni, mortale. Sistemeremo le cose tra di noi.', '', 38134),
(197265, 'itIT', 'Better them than being chopped up for parts.', 'Better them than being chopped up for parts.', 38134),
(197264, 'itIT', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 38134),
(197263, 'itIT', 'We don\'t have options now. This is our best chance for success.', 'We don\'t have options now. This is our best chance for success.', 38134),
(197259, 'itIT', '', 'Il Principe Renathal tiene queste Corti delle Braci per ottenere favori e influenza sugli abitanti più importanti delle Terretetre.\n\nSe i nostri ospiti se ne andranno dalla festa infelici, avremo mancato il nostro obiettivo.\n\nInoltre, ogni ospite paga un tributo al Principe, quando lascia la corte.\n\nOspiti più felici lasciano tributi più corposi.\n\nSono certa che la cosa ti interessi.', 38134),
(197258, 'itIT', '', '<La strana creatura arborea lungo il cammino non risponde. Sembra che stia dormendo.>', 38134),
(197257, 'itIT', '', 'Come puoi immaginare, ospiti diversi preferiscono tipi di corte diversi. Alcuni amano le ambientazioni eleganti e formali, mentre altri si sentono più a proprio agio in un\'atmosfera informale o pericolosa.\n\nIl nostro lavoro come organizzatori è di creare un\'atmosfera che piaccia ai nostri ospiti. \n\nPossiamo farlo scegliendo i servizi che si abbinano alle preferenze dei nostri ospiti, come l\'intrattenimento e i rinfreschi.\n\nInoltre, possiamo partecipare a delle attività durante la Corte delle Braci per creare la corte perfetta che si addica al gusto di ogni ospite.\n\nSe non sei $gcerto:certa; di che tipo di corte preferiscano, consulta la Lista degli Ospiti della Corte delle Braci creata da Sire Garridan.\n\nIl nostro personale ti saprà informare su quale delle opzioni si abbinerà alle preferenze dei nostri ospiti.', 38134),
(197255, 'itIT', '', 'Cosa... Cos\'è quello?', 38134),
(197251, 'itIT', '', 'Quel potere... non può essere!', 38134),
(197249, 'itIT', '', 'Che la Fauce vi colga tutti!', 38134),
(197248, 'itIT', '', 'L\'Eletta deve morire! Non interferirai!', 38134),
(197247, 'itIT', '', 'Ho sempre creduto che fosse facile convincere gli altri a stare dalla tua parte.\n\nBasta dar loro ciò che vogliono.', 38134),
(197243, 'itIT', '', 'Un altro sviluppo inaspettato. Provvederemo alla sua accoglienza.', 38134),
(197227, 'itIT', 'Sminuisci pure la questione se devi, ma so che hai tradito tu il villaggio, Ilka.', 'Sminuisci pure la questione se devi, ma so che hai tradito tu il villaggio, Ilka.', 38134),
(197226, 'itIT', 'Dai su, Samu. Dicci tutto, Lajos! Chi ha fatto cosa a chi? Semplicemente delizioso.', 'Dai su, Samu. Dicci tutto, Lajos! Chi ha fatto cosa a chi? Semplicemente delizioso.', 38134),
(197223, 'itIT', 'Cosa c\'è, Lajos? A differenza tua, io sono occupato.', 'Cosa c\'è, Lajos? A differenza tua, io sono occupata.', 38134),
(197182, 'itIT', '', 'Ah hah!', 38134),
(197181, 'itIT', 'House of Plagues. I was out on assignment when the accident happened.', 'House of Plagues. I was out on assignment when the accident happened.', 38134),
(197180, 'itIT', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 38134),
(197179, 'itIT', 'Good. It will speed up your training considerably if you are already skilled.', '', 38134),
(197178, 'itIT', 'I am a mage. I can channel anima.', 'I am a mage. I can channel anima.', 38134),
(197177, 'itIT', 'Non vedi che sono occupato?', 'Non vedi che sono occupata?', 38134),
(197176, 'itIT', 'Do you have any other skills or abilities we should know about?', '', 38134),
(197175, 'itIT', 'Which house were you originally affiliated with before now?', '', 38134),
(197174, 'itIT', 'Are you able to or were you previously able to cast spells? If so, what kind?', '', 38134),
(197172, 'itIT', 'This isn\'t a hypothetical question. Don\'t take it lightly.', '', 38134),
(197171, 'itIT', 'Yes.', 'Yes.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197170, 'itIT', 'Are you willing to pledge your afterlife to Margrave Sin\'dane and to the service of the House of Rituals?', '', 38134),
(197168, 'itIT', '', 'Il loro comandante ha preso la sala d\'udienza. Devi affrontarlo!', 38134),
(197160, 'itIT', '', 'Non puoi nasconderti!', 38134),
(197146, 'itIT', 'Bwonsamdi fa combattere ai mortali le battaglie sue? Lo sapevo che era nu codardo.', '', 38134),
(197145, 'itIT', 'Nun sarebbe niente senza di me! E quando avrò finito, tornerà a essere niente!', '', 38134),
(197144, 'itIT', 'Dopo tutto chello che ho fatto per Bwonsamdi, facendolo crescere da sacerdote a Loa, dandogli il potere della Morte... ora la usa contro di me?!?', '', 38134),
(197143, 'itIT', 'È nu peccato che tu abbia tradito Mueh\'zala. Mo\' siamo nemici.', '', 38134),
(197142, 'itIT', 'Vedi il potere che dono ai seguaci miei, Bwonsamdi?', '', 38134),
(197140, 'itIT', '', 'Morte... altra morte!', 38134),
(197135, 'itIT', 'This is the line for application to begin testing. If you have no magical ability, you\'re wasting everyone\'s time.', '', 38134),
(197134, 'itIT', '', 'Oh, sei $gtornato:tornata;. Che fortuna.', 38134),
(197130, 'itIT', 'Siamo salvi! Grazieee!', 'Siamo salvi! Grazieee!', 38134),
(197129, 'itIT', 'SI MUOVE!', 'SI MUOVE!', 38134),
(197128, 'itIT', 'Dovrei colpirlo?', 'Dovrei colpirlo?', 38134),
(197127, 'itIT', 'Fa\' qualcosa! Fa\' qualcosa!', 'Fa\' qualcosa! Fa\' qualcosa!', 38134),
(197121, 'itIT', 'Oh, interessante... quelle sono le tue vere orecchie?', 'Oh, interessante... quelle sono le tue vere orecchie?', 38134),
(197114, 'itIT', '', 'Per nostra fortuna, il Calcafauce può superare ogni ostacolo... ed ecco che ne arriva uno.', 38134),
(197113, 'itIT', 'Il piano potrebbe funzionare, ma non è privo di rischi.', '', 38134),
(197100, 'itIT', '', 'Non sarai mai al sicuro. Setacceremo tutta Revendreth se necessario.', 38134),
(197099, 'itIT', '', 'Scappa, miserabile mortale. I miei Frangifede ti troveranno.', 38134),
(197098, 'itIT', '', 'Ci vedremo là, Calcafauce. Devo dire ai nostri alleati di prepararsi per ciò che ci aspetta.', 38134),
(197095, 'itIT', '', 'Eccellente. Ora mostragli cosa succede a chi non collabora.', 38134),
(197093, 'itIT', 'Il Colosso deve finire di caricarsi. Guadagna tempo!', '', 38134),
(197092, 'itIT', 'Siamo tutti condannati... alla Fauce...', 'Siamo tutti condannati... alla Fauce...', 38134),
(197091, 'itIT', 'Non più...', 'Non più...', 38134),
(197090, 'itIT', 'Non so cosa sia cambiato... Ma non ci si può fidare dei Venthyr...', 'Non so cosa sia cambiato... Ma non ci si può fidare dei Venthyr...', 38134),
(197089, 'itIT', 'No! Stai mentendo!', 'No! Stai mentendo!', 38134),
(197087, 'itIT', 'I Venthyr hanno promesso che tutti ci saremo liberati dei nostri peccati.\n\nDopo innumerevoli e dolorosi eoni, pensavo finalmente di essere libero...\n\nMa ora i Venthyr mi maltrattano... mi deridono... mi abbandonano...\n\nÈ sempre stata una bugia?', 'I Venthyr hanno promesso che tutti ci saremo liberati dei nostri peccati.\n\nDopo innumerevoli e dolorosi eoni, pensavo finalmente di essere libera...\n\nMa ora i Venthyr mi maltrattano... mi deridono... mi abbandonano...\n\nÈ sempre stata una bugia?', 38134),
(197086, 'itIT', 'Non dimenticare di usare il risonatore prima che insorgano contro di noi ancora una volta.', '', 38134),
(197082, 'itIT', 'Forte e chiaro! Devo soltanto... muovere il Colosso... in qualche modo...', '', 38134),
(197067, 'itIT', 'Ne manca solo una. Mantieni la concentrazione.', '', 38134),
(197065, 'itIT', '', 'In conclusione: non tradirmi.', 38134),
(197064, 'itIT', '', 'Perchè è questo che siamo, mortale: soci in affari. Da adesso fino a quanto la nostra fiamma non si spegnerà.', 38134),
(197063, 'itIT', '', 'Se si accorgono che faccio affari con te, sospetto che ti ucciderebbero molto volentieri pur di minare la mia prosperità.', 38134),
(197062, 'itIT', '', 'Se dovessi incontrare altri membri del mio cartello nelle Terretetre, non fidarti di loro.', 38134),
(197061, 'itIT', '', 'E il mio cartello non gestisce l\'invidia particolarmente bene. Chi mi ha assegnata qui molti anni fa, ora sta mandando degli assassini per eliminarmi.', 38134),
(197060, 'itIT', '', 'Ora che sappiamo che alcuni mortali possono lasciare questo regno, mi trovo improvvisamente in una posizione che definirei invidiabile.', 38134),
(197057, 'itIT', '', 'Il mio cartello ha mandato degli agenti nella Fauce per diverso tempo. Un incarico pericoloso, ma il richiamo delle informazioni preziose ha un certo fascino per la mia razza.', 38134),
(197056, 'itIT', '', 'Regola numero sette: il tradimento è inevitabile, anche da chi ci sta accanto. Specialmente da chi ci sta accanto.', 38134),
(197055, 'itIT', '', 'Scopri ciò che puoi dentro le tane, mortale. La mia ricerca continua.', 38134),
(197053, 'itIT', '', 'Questo codice attenuerà il tuo odore nei loro antri. Portami le rune che animano quei mostri, così da poter comprendere meglio le loro funzioni.', 38134),
(197051, 'itIT', '', 'Regola numero sei: impiegare sempre nuove misure di occultamento. I Giurafauce non smettono mai di escogitare nuovi modi per trovarci.', 38134),
(197050, 'itIT', '', 'Confesso che non ero certa del tuo ritorno. Non prenderlo come un complimento.', 38134),
(197049, 'itIT', '', 'Prendi questo codice sperimentale. Qualora funzionasse come previsto, entrare nel Forte della Perdizione non provocherà la tua morte. Probabilmente.', 38134),
(197048, 'itIT', '', 'Il Forte della Perdizione brulica di attività, devo sapere perché.', 38134),
(197047, 'itIT', '', 'Regola numero cinque: essere audaci quando la situazione lo richiede. I Giurafauce non se l\'aspetteranno.', 38134),
(197045, 'itIT', 'Questo tempio è stato solo il primo a cadere, presto lo seguirà tutto il Bastione.', '', 38134),
(197044, 'itIT', '', 'In cima alla mia lista c\'è un Inquisitore. Ha scavalcato tutte le posizioni. Potrei essere molto generosa se la sua esistenza cessasse per mano tua.', 38134),
(197043, 'itIT', '', 'Regola numero quattro: stilare una lista di coloro che desiderano la fine della tua esistenza. Non darti troppe arie, la tua presenza nella lista è l\'ultima delle priorità dei Giurafauce.', 38134),
(197042, 'itIT', '', 'Fa\' attenzione quando l\'Occhio del Carceriere ricade su di te, mortale. Sarebbe terribilmente sconveniente perdere un socio d\'affari con un potenziale simile!', 38134),
(197039, 'itIT', 'Un\'arena piena di Animum richiede un campione pieno di vittoria!', '', 38134),
(197038, 'itIT', 'Gladiatrice leggendaria, detentrice del Creacadaveri Runico, Sabriel la Fendiossa!', '', 38134),
(197037, 'itIT', '', 'Regola numero tre: la fiducia si guadagna, le informazioni si pagano. Ricordi quel codice che ti ho dato? Esatto, sei in debito.', 38134),
(197029, 'itIT', '', 'Tirna Falcis si ricorderà di te. Sempre.', 38134),
(197028, 'itIT', '', 'Lakali dorme, la sua conoscenza è intatta. Il tempo sarà la sua cura.', 38134),
(197000, 'itIT', '', 'Che... deliziosa... conoscenza...', 38134),
(196984, 'itIT', '', 'Affronta le nebbie, il pericolo al di là. Allora, ci rivedremo.', 38134),
(196976, 'itIT', 'Guardate! Là su quel precipizio! È...', '', 38134),
(196975, 'itIT', '', 'Affogate! Affogate tra le anime dei dannati!', 38134),
(196974, 'itIT', '', 'La pagherai per questo insulto! I cieli del tuo mondo saranno oscurati dalle ali dei miei Giurafauce!', 38134),
(196973, 'itIT', '', '\"Misere\", hai detto? È stato un gruppo di \"miseri\" campioni a sconfiggerti, strega!', 38134),
(196972, 'itIT', '', 'Le vostre misere anime nutriranno l\'imminente oscurità!', 38134),
(196971, 'itIT', 'Un attimo. Sento qualcosa risvegliarsi...', '', 38134),
(196968, 'itIT', '', 'Prepara il \"rituale\". La nostra farsa deve essere convincente se vogliamo ingannare Mor\'bitan.', 38134),
(196920, 'itIT', 'Forse sì, Keltesh. Molti dimenticano che le anime osservano tutto ciò che le circonda, persino se incatenate.', 'Forse sì, Keltesh. Molti dimenticano che le anime osservano tutto ciò che le circonda, persino se incatenate.', 38134),
(196918, 'itIT', '', 'Basta ridere. Raggruppa la Stigia che mi serve e torna al più presto. Il tuo codice ti aspetta!', 38134),
(196917, 'itIT', '', 'Riconosco l\'ironia di aver creato questa regola nonostante la mia incapacità di lasciare questo luogo. Puoi riderne... Per poco... Senza offendermi.', 38134),
(196916, 'itIT', '', 'Regola numero uno: prevedere sempre una via d\'uscita. Per mantenere intatta la propria esistenza nella Fauce, bisogna avere sempre pronto un piano di fuga.', 38134),
(196915, 'itIT', 'Mi viene da esitare. Come posso sapere cosa sia sospetto? Samu e Ilka hanno litigato per i tributi e per la siccità. È sospetto?', '', 38134),
(196891, 'itIT', '', 'Salute, Zovaal! Queste anime sono tue!', 38134),
(196885, 'itIT', 'Sì, i tuoi peccati gravano enormemente su di te. Farò domanda all\'Accusatrice per il tuo giudizio.', 'Sì, i tuoi peccati gravano enormemente su di te. Farò domanda all\'Accusatrice per il tuo giudizio.', 38134),
(196880, 'itIT', '', 'Il percorso per ottenere la loro fiducia è appagante. Non c\'è sensazione migliore di cooperare con un larion che ti rispetta.', 38134),
(196879, 'itIT', 'Aaargh! Io... Sono in qualche modo... più leggero? Non ho paura.', '', 38134),
(196878, 'itIT', '', 'Formare un legame anche solo con un larion non è un\'impresa semplice. Sono esseri particolari, nobili e testardi.', 38134),
(196874, 'itIT', '', 'Il nostro branco ti dà il benvenuto. Confido che seguirai il tuo cuore.', 38134),
(196873, 'itIT', 'Basta solo un po\' di manutenzione e messa a punto occasionale e avrai il compagno perfetto creato appositamente per i tuoi bisogni.', '', 38134),
(196872, 'itIT', 'Le falinci mostrano una comprensione eccezionale. Accettano e seguono i comandi con semplicità, senza fare domande o discutere.', '', 38134),
(196868, 'itIT', 'Sono sicuro che vedrai i vantaggi di lavorare con le mie falinci.', '', 38134),
(196865, 'itIT', 'L\'Ultimo Limo si estende ogni giorno mangiandosi i dintorni. Spero che ci puoi fare qualcosa tu.', '', 38134),
(196864, 'itIT', 'Un po\' di tempo fa \'sto posto era pieno di ladri della peggior specie, barboni e pirati. Siccità di melma!', '', 38134),
(196863, 'itIT', 'Nelle torri, ultimamente si parla solo di te.', '', 38134),
(196862, 'itIT', 'Oh, detto tra noi, l\'unico motivo del perché faccio \'sta strada è che non hanno abbastanza ali per controllarla.', '', 38134),
(196861, 'itIT', 'Mio cugino ha servito alcune di queste casate... però a lavoro finito l\'hanno smelmato.', '', 38134),
(196860, 'itIT', 'Tieni giù la testa. Non voglio che quei denti aguzzi ci vedano assieme.', '', 38134),
(196859, 'itIT', 'Dicono che qui nel bosco vivano ribelli e vigilanti.', '', 38134),
(196858, 'itIT', 'Non ci possiamo fermare… ci sono i pipistrelli!', '', 38134),
(196857, 'itIT', 'Mortale, a me.', '', 38134),
(196856, 'itIT', 'Non c\'è tempo da perdere in chiacchiere. Devo tenere gli occhi aperti sui lavoratori.', '', 38134),
(196854, 'itIT', 'Evviva! Sono arrivati i rinforzi!', 'Evviva! Sono arrivati i rinforzi!', 38134),
(196846, 'itIT', 'Questo posto è semplicemente inferiore a me. Spero che la Contessa ricordi la grandezza del mio servizio.', 'Questo posto al Cancello Antico è semplicemente inferiore a me. Spero che la Contessa ricordi la grandezza del mio servizio.', 38134),
(196845, 'itIT', '', 'Sei uno sciocco, Pelodis. $n fa parte del nostro branco, ora. Non esiste onore più grande.', 38134),
(196844, 'itIT', 'Credo che l\'efficienza delle mie falinci abbia fatto colpo su di $glui:lei;.', '', 38134),
(196840, 'itIT', 'La mia esistenza è durata troppo perché termini così...', '', 38134),
(196839, 'itIT', 'Questa è una delle creazioni migliori di Mikanikos. Incanala l\'Animum in esplosioni di energia.', '', 38134),
(196838, 'itIT', 'Un\'intrusione! Ti costringeremo a sopportare la Luce!', '', 38134),
(196836, 'itIT', 'Nalthor è davvero potente se la sua magia ha penetrato questa corazza. Non cadrà facilmente.', '', 38134),
(196835, 'itIT', 'L\'Animum alimenta ogni cosa nel Bastione, anche i Colossi. Questo lo aiuterà a mantenere la carica.', '', 38134),
(196834, 'itIT', '', 'Cominciamo quando vuoi.', 38134),
(196830, 'itIT', 'I miei peccati in vita erano terribili. Ora lo capisco chiaramente.', '', 38134),
(196828, 'itIT', 'Salve, Calcafauce.', '', 38134),
(196815, 'itIT', '', 'Tornerò dalla Signora della Guerra Breka. Potresti seguirmi nella tua nuova forma animale.', 38134),
(196814, 'itIT', '', 'La tua sintonia con la natura è diventata più forte. Usala per proteggere la selva e mantenere l\'equilibrio.', 38134),
(196813, 'itIT', '', 'Ascolta i loro sussurri. Apprendi le lezioni che hanno da insegnarti.', 38134),
(196812, 'itIT', '', 'Gli spiriti rispondono!', 38134),
(196811, 'itIT', '', 'Spiriti della natura: offrite i vostri doni a quest\'aspirante della via druidica.', 38134),
(196810, 'itIT', '', 'So che c\'è un\'antica pietra druidica nei paraggi. Raggiungiamola ed entriamo in comunione con la natura.', 38134),
(196808, 'itIT', 'Non smetterò di combattere finché non mi mostrerai la tua nuova abilità!', '', 38134),
(196807, 'itIT', 'Notevole! Le tue capacità sono cresciute a dismisura. Ritorniamo all\'accampamento.', '', 38134),
(196806, 'itIT', 'Fammi vedere cos\'hai imparato!', '', 38134),
(196805, 'itIT', 'Direi che abbiamo finito. Come ti senti?', '', 38134),
(196804, 'itIT', 'Lascia che il mondo svanisca...', '', 38134),
(196803, 'itIT', 'Chiudi gli occhi. Trova il tuo equilibrio.', '', 38134),
(196802, 'itIT', 'Vieni, andiamo a meditare per sbloccare il nostro potenziale interiore.', '', 38134),
(196801, 'itIT', 'Non apprezzo molto la magia vile, ma immagino che qualunque cosa tu faccia sia per l\'Orda.', '', 38134),
(196800, 'itIT', 'Be\', non stavo mica insegnando a te, no?', '', 38134),
(196799, 'itIT', 'Non credo di aver imparato qualcosa.', '', 38134),
(196798, 'itIT', 'Sarà meglio che tu colpisca Throg per spezzare l\'incantesimo.', '', 38134),
(196797, 'itIT', 'Osserva! Il tuo nemico metamorfato vagherà senza meta, usa questo tempo per sconfiggere chiunque rimanga.', '', 38134),
(196795, 'itIT', 'Sei una mina vagante, recluta. Sono sbalordito! Torniamo all\'accampamento.', '', 38134),
(196794, 'itIT', 'Due nemici ti stanno attaccando, usa Metamorfosi per pareggiare i conti!', '', 38134),
(196793, 'itIT', 'Colpire il tuo nemico annullerà l\'incantesimo. Attacca Herbert per riportarlo alla normalità.', '', 38134),
(196792, 'itIT', 'Conosco questa strategia! Tieni un nemico metamorfato mentre ti concentri sull\'altro.', '', 38134),
(196790, 'itIT', 'Sono i pensieri come questo che frenano l\'apprendimento!', '', 38134),
(196789, 'itIT', 'Preferirei non essere trasformato in una pecora.', '', 38134),
(196788, 'itIT', 'Throg, assistimi. Attaccheremo entrambi la recluta per insegnarle a usare Metamorfosi.', '', 38134),
(196787, 'itIT', 'Il tuo nemico vagherà senza meta finché non lo attaccherai, così.', '', 38134),
(196786, 'itIT', 'Sto soltanto dimostrando l\'utilità di Metamorfosi in combattimento.', '', 38134),
(196785, 'itIT', '', 'Cupatenebra, esigo una spiegazione!', 38134),
(196784, 'itIT', 'Ecco un esempio di Metamorfosi!', '', 38134),
(196783, 'itIT', 'Prenderei da solo il libro, ma un bravo Mago sa quando è tempo di delegare.', '', 38134),
(196782, 'itIT', '', 'Ricordo quando Shuja entrò in comunione con gli spiriti la prima volta. Un giorno ti distinguerai tra gli Sciamani, recluta.', 38134),
(196781, 'itIT', '', 'Vedo che gli spiriti ti hanno affidato un compito. Hanno scelto saggiamente.', 38134),
(196780, 'itIT', '', 'Recluta? Stai parlando con gli spiriti?', 38134),
(196777, 'itIT', '', 'Sì, Polemarco! Che l\'Arconte vegli su tutti noi.', 38134),
(196773, 'itIT', '', 'È... più fragile di altri, in termini di purificazione. Non vorrei che smarrisse la via. Ciò che stiamo per fare può essere alquanto sgradevole da osservare.\n\nSento che... tu sia in grado di gestire gli eventuali momenti drammatici.', 38134),
(196772, 'itIT', '', 'Uther? Il nome mi è familiare.\n\nForse si tratta dell\'Aspirante che Devos ha preso con sé? Curioso che tu l\'abbia riconosciuto. Forse perché proviene dal tuo mondo.', 38134),
(196771, 'itIT', 'Fate piovere morte su di loro! Assicuratevi che non interferiscano.', '', 38134),
(196770, 'itIT', 'Eridia! Le protezioni! Sono...', '', 38134),
(196769, 'itIT', '', 'Niente, io...', 38134),
(196768, 'itIT', 'Artemede farà rapporto all\'Arconte. Dobbiamo tornare a Oribos e informarli del tradimento di Maldraxxus!', '', 38134),
(196767, 'itIT', '', 'L\'Eletta può vedersela da sola per un po\' finché non raduni rinforzi.', 38134),
(196764, 'itIT', 'Certo che ne provi, Keltesh. Il giorno del tuo giudizio si avvicina e dobbiamo liberarti dai tuoi peccati.', 'Certo che ne provi, Keltesh. Il giorno del tuo giudizio si avvicina e dobbiamo liberarti dei tuoi peccati.', 38134),
(196761, 'itIT', 'Presto, dobbiamo andarcene da qui!', '', 38134),
(196758, 'itIT', 'Ho paura, Bela.', 'Ho paura, Bela.', 38134),
(196755, 'itIT', 'Allora vieni, se ne hai il coraggio.', '', 38134),
(196754, 'itIT', '', 'Soffrirai per questo, necromante!', 38134),
(196753, 'itIT', 'Allora sarai la prossima a cadere.', '', 38134),
(196752, 'itIT', '', 'Non finché sarò tua avversaria.', 38134),
(196750, 'itIT', 'Voi Kyrian siete complici di un ciclo spezzato. Il Bastione è uno spreco d\'Animum. Lo prenderemo noi.', '', 38134),
(196745, 'itIT', '', 'Mostro! Che cosa ti ha spinto a una tale follia?!?', 38134),
(196740, 'itIT', '', 'Ti prego di cercare Nikolon e assicurarti che stia bene. Non è da lui tardare.', 38134),
(196721, 'itIT', 'È tempo che lo spirito mio vada nell\'Aldilà. Sei na promessa tra i Sacerdoti, cumpà.', '', 38134),
(196720, 'itIT', 'Lancia Resurrezione per ripigliarli dalle fauci della morte!', '', 38134),
(196719, 'itIT', 'Sacerdote! Accà, presto, che la scintilla di vita brucia ancora in cheste reclute!', '', 38134),
(196718, 'itIT', 'Se respira ancora, dobbiamo darle una mano.', '', 38134),
(196717, 'itIT', 'L\'Eletta del Coraggio era nel cuore del tempio quando l\'attacco è avvenuto.', '', 38134),
(196716, 'itIT', 'Grazie, recluta. Non possiamo seppellire a dovere le altre reclute, ma possiamo almeno celebrare i riti funebri.', '', 38134),
(196714, 'itIT', 'È andato verso l\'Eletta!', '', 38134),
(196713, 'itIT', '', 'Abbi fiducia nella Luce.', 38134),
(196712, 'itIT', '', 'Scudo Divino ti proteggerà contro la magia oscura dell\'altare.', 38134),
(196711, 'itIT', '', 'Avanza senza timore. La Luce ti aiuterà nel momento del bisogno.', 38134),
(196710, 'itIT', 'È così che ci si occupa dei problemi!', '', 38134),
(196709, 'itIT', 'FUOCO!!!', '', 38134),
(196708, 'itIT', 'Hai trovato altri miei ricordi?$b$bPiù ne trovi, più entrambi ne beneficeremo.', '', 38134),
(196706, 'itIT', 'Non so molto riguardo la Luce, ma quando uno spirito chiama, tu devi rispondere.', '', 38134),
(196705, 'itIT', 'Il Colosso è pronto! Vieni! Vieni!', '', 38134),
(196704, 'itIT', 'Venite, Kyrian, se ne avete il coraggio. Compite il vostro destino.', '', 38134),
(196703, 'itIT', 'Basta così. Alla prole di Ossartiglio andrebbe uno spuntino.', '', 38134),
(196702, 'itIT', 'È quasi a piena potenza! Solo un altro po\'!', '', 38134),
(196701, 'itIT', 'Lo spirito di un antico guerriero deve avere molto da insegnarti. Vedi di imparare bene.', '', 38134),
(196700, 'itIT', 'Noi siamo infiniti. Cadrete, pezzo dopo pezzo.', '', 38134),
(196699, 'itIT', 'Tutto qui? Mi aspettavo di più!', '', 38134),
(196698, 'itIT', '', 'Presto, $n, cancella questi ricordi!', 38134),
(196697, 'itIT', 'Non posso! AAAHHH!', '', 38134),
(196696, 'itIT', '', 'Riprendi il controllo, Accolito!', 38134),
(196695, 'itIT', '', 'Un po\' più in là... Oh!', 38134),
(196694, 'itIT', 'Usa la Trappola Congelante nelle Rovine degli Ogre e la bestia che assedia l\'accampamento dovrebbe arrivare.', '', 38134),
(196693, 'itIT', 'Un Granulo di Ghiaccio? Certo. Ne ho un\'infinità dopo... l\'incidente.', '', 38134),
(196692, 'itIT', 'Non tardare coi rifornimenti. Prima impari a creare questa trappola, meglio è.', '', 38134),
(196690, 'itIT', 'Sciocco. Il mio esercito è tutto intorno a te.', '', 38134),
(196689, 'itIT', 'Richiama le tue bestie. Periranno proprio come gli altri!', '', 38134),
(196688, 'itIT', 'Le tue forze sono già state respinte. Hai perso.', '', 38134),
(196685, 'itIT', 'Che strano... Perché questi Pretori sono disattivati?', '', 38134),
(196683, 'itIT', 'Per ora basterà. Adesso trova una bestia e addomesticala.', '', 38134),
(196682, 'itIT', 'Ti insegnerò le basi il più velocemente possibile. Presta attenzione.', '', 38134),
(196681, 'itIT', 'Bal\'a dash, malanore. Istruirò volentieri qualunque compagno Elfo del Sangue nelle vie dei Lungopasso.', '', 38134),
(196680, 'itIT', 'Presto, dobbiamo avvisare il Polemarco!', '', 38134),
(196679, 'itIT', 'No... Avevo il suo favore...', '', 38134),
(196677, 'itIT', 'Come OSI ritardare la spedizione del Tributiere?!?', '', 38134),
(196676, 'itIT', 'Sciocchi! Gli Indegni trionferanno sull\'Arconte.', '', 38134),
(196675, 'itIT', '', 'La Protezione del Coraggio regge. È un sollievo.', 38134),
(196674, 'itIT', 'No... di nuovo...', '', 38134),
(196673, 'itIT', 'Mi servono... dei punti...', '', 38134),
(196672, 'itIT', 'Irreparabile...', '', 38134),
(196671, 'itIT', 'Non sprecate... la mia carne...', '', 38134),
(196670, 'itIT', 'Troppi... difetti...', '', 38134),
(196668, 'itIT', '', 'Prima mangia, non ti lascerò morire di fame.', 38134),
(196667, 'itIT', '', 'I Verrospino... ci hanno teso n\'imboscata...', 38134),
(196666, 'itIT', 'Carne e ossa! Pelle e tendini!', '', 38134),
(196665, 'itIT', 'Muori in fretta, così posso iniziare!', '', 38134),
(196664, 'itIT', 'Ti faccio a pezzi e poi ti ricompongo!', '', 38134),
(196663, 'itIT', 'Più pezzi per i miei progetti!', '', 38134),
(196662, 'itIT', 'Oooh, materiali grezzi!', '', 38134),
(196661, 'itIT', '', 'Allora non mi servite più a niente.', 38134),
(196660, 'itIT', 'Generale, chiedi più di quanto possiamo dare. Non abbiamo più nulla.', 'Generale, chiedi più di quanto possiamo dare. Non abbiamo più nulla.', 38134),
(196659, 'itIT', '', 'Sali su una viverna e torna a Orgrimmar. La gloria ci attende.', 38134),
(196657, 'itIT', 'Una parola d\'incoraggiamento potrebbe essere d\'aiuto agli Aspiranti.', '', 38134),
(196656, 'itIT', 'Coloro che cadono potranno riposarsi al Tempio della Lealtà, finché non ritornano alla propria via.', '', 38134),
(196655, 'itIT', 'Sì, Ascesa.', '', 38134),
(196653, 'itIT', 'Sono caduti in molti recentemente. Il Tempio della Lealtà prova a fare del suo meglio per riportarli sulla retta via.', '', 38134),
(196651, 'itIT', 'Mi ricordo la mia famiglia, dubito che mi riconoscerebbero adesso! Spero che quando dovrò dimenticare, non vada... così.', '', 38134),
(196649, 'itIT', 'È andata bene. Sicuramente meglio della mia ultima purificazione.', '', 38134),
(196644, 'itIT', 'Ti conosco? Non hai un aspetto familiare...', '', 38134),
(196643, 'itIT', 'Sei $gpronto:pronta; per iniziare?', '', 38134),
(196639, 'itIT', '', 'Grazie. E... mi dispiace.', 38134),
(196638, 'itIT', '', 'Devo informare l\'Eletta. Perdere un Accolito alla fine delle sue prove, non è cosa da poco...', 38134),
(196637, 'itIT', '', 'Mi rifiuto di credere che sia vero. Va\', la Lealtà ti attende, $gAccolito:Accolita;.', 38134),
(196636, 'itIT', '', 'Accolito! No!', 38134),
(196635, 'itIT', '', 'Mai più. Non seguirò più questa via!', 38134),
(196634, 'itIT', '', 'Quanto ho faticato in questo tempio, e per che cosa?', 38134),
(196633, 'itIT', '', 'No! Devi resistere!', 38134),
(196632, 'itIT', 'Conosco la via. Fammi entrare.', '', 38134),
(196631, 'itIT', '', 'Non ho... speranze...', 38134),
(196630, 'itIT', 'Non avevi speranze al mio cospetto, e non ne hai neanche adesso.', '', 38134),
(196629, 'itIT', '', 'Sono pronta.', 38134),
(196628, 'itIT', 'Significa che il vecchio Mueh\'zala sta proteggendo i Loa suoi mentre si nutrono dei Semebrado per rafforzarsi.', '', 38134),
(196627, 'itIT', 'Mueh\'zala sta prosciugando i Semebrado per rianimare Hakkar e riempirlo di mojo oscuro.', '', 38134),
(196626, 'itIT', 'Le tracce portano a chel bosco più avanti. Be\', che aspetti, cumpà?', '', 38134),
(196625, 'itIT', 'Chiunque lasci cheste tracce dev\'essere di fretta!', '', 38134),
(196624, 'itIT', 'Ho nu brutto presentimento su chesti Loa scomparsi...', '', 38134),
(196622, 'itIT', 'Che? Anche Hakkar è scomparso? No, no, no... chesto nun va bene, cumpà! Nun va bene per niente!', '', 38134),
(196621, 'itIT', 'Sangue e tuono, soldati! Abbiamo provato a farci strada attraverso la tempesta.', '', 38134),
(196620, 'itIT', 'Il Semebrado di Dambala nun ci sta più... Resta solo il guscio! Meglio sbrigarsi a trovare il vecchio Hakkar!', '', 38134),
(196619, 'itIT', '', 'Rinforzi dell\'Orda! Sapevo che ci avrebbero trovato.', 38134),
(196618, 'itIT', 'Tharon\'ja veniva venerato dai Drakkari... Finché nun si sono rivoltati contro di lui. È difficile trovare seguaci fedeli di chesti tempi, mah!', '', 38134),
(196617, 'itIT', '', 'Cammina a testa alta, $n. Abbiamo ucciso un drago e messo fine a questo folle rituale!\n\nMi assicurerò che i miei superiori dell\'Orda sentano parlare delle tue gesta di quest\'oggi!\n\nOra, andiamocene da qui e troviamo un modo per abbandonare l\'isola.', 38134),
(196616, 'itIT', '', 'Posa le tue radici.', 38134),
(196615, 'itIT', 'Mam\'toth sta riposando in pace, mo\'. Tutta colpa di chella faccenda fetente a Zul\'drak!', '', 38134),
(196614, 'itIT', 'Posso tranquillamente dire di influenzare il mio cartello.\n\nSono uno degli anziani del Cartello Ba. Diamo molto valore alle informazioni e al sapere. L\'applicazione di tale sapere ha un effetto secondario che ci permette di accumulare ricchezza e influenza. \n\nNoi Alienatori riteniamo che il successo sia importante e non abbiamo paura di mostrare i risultati delle nostre imprese. Per questo i miei colleghi mi onorano con un titolo.\n\nInfluenzo il mio amato cartello così che possano essere partecipi del mio successo. Cerco solo di assicurarmi che continuiamo a prosperare.', '', 38134),
(196612, 'itIT', 'Al comando? No, non direi che sono al comando, non esattamente.\n\nSono solo uno dei tanti, ma potresti dire che la mia voce ha un certo peso. \n\nL\'influenza è spesso più importante del potere, non credi?\n\nQuindi, diciamo che sono... influente.', '', 38134),
(196610, 'itIT', '', 'Recluta, fammi sapere quando vuoi lasciare questo posto. Dobbiamo trovare un modo per ritornare a Orgrimmar.', 38134),
(196609, 'itIT', '', 'Ah! Gli Ogre e il loro drago giacciono morti ai nostri piedi e il cielo risplende sulla nostra vittoria.', 38134),
(196608, 'itIT', '$gBenvenuto:Benvenuta;. Cosa cerchi dagli Alienatori?\n\nSiamo sempre al tuo servizio.', '', 38134),
(196607, 'itIT', '', 'Colpite! Per l\'Orda!', 38134),
(196606, 'itIT', '', 'Lok\'tar ogar! Vittoria o morte!', 38134),
(196605, 'itIT', '', 'L\'abbiamo... fermato?', 38134),
(196604, 'itIT', '', 'Sta finendo il rituale!', 38134),
(196603, 'itIT', 'Si spera qualsiasi cosa possa servirti da artigiano!\n\nNoi Alienatori esploriamo l\'universo e impariamo dai maestri di ogni realtà che riusciamo a trovare. Questo ci permette di rispondere alle tue necessità con le nostre offerte. \n\nI nostri membri hanno iniziato a padroneggiare le varie professioni che la tua razza trova importanti. Li troverai qui, disposti ad aiutarti nel tuo sviluppo come $gartigiano:artigiana;.\n\nCi stiamo procurando nuove ricette in questo momento. Dai pure un\'occhiata, non resterai $gdeluso:delusa;.', '', 38134),
(196601, 'itIT', '', 'Gor\'groth! Preparati a unirti al tuo prezioso drago nella morte!', 38134),
(196600, 'itIT', '', 'Proseguiamo! Gor\'groth dev\'essere vicino.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196599, 'itIT', '', 'Ai tuoi ordini! Lok\'tar!', 38134),
(196598, 'itIT', '', 'Attirerò la sua attenzione. Shuja, cura le nostre ferite!', 38134),
(196597, 'itIT', '', 'Recluta, vai avanti tu. Ti seguirò subito dopo. Non lasciare che ci intralcino!', 38134),
(196596, 'itIT', '', 'Quella è una delle guardie del corpo di Gor\'groth. Qual è il piano d\'attacco?', 38134),
(196595, 'itIT', '', 'Muoviamoci. Terrò gli Ogre concentrati su di me mentre voi penserete a ucciderli!', 38134),
(196594, 'itIT', '', 'Tutta questa energia necrotica... Il rituale dev\'essere quasi completo!', 38134),
(196593, 'itIT', '', 'Li abbiamo uccisi quasi tutti! Continuate a combattere!', 38134),
(196592, 'itIT', 'Ti sono grato... Ma dobbiamo ancora porre fine al rituale degli Ogre!', '', 38134),
(196591, 'itIT', '', 'Mueh\'zala la pagherà cara per la distruzione che ha provocato.', 38134),
(196590, 'itIT', '', 'Buona caccia! La Regina sarà felice di sapere che abbiamo cacciato via Mueh\'zala da Selvarden.', 38134),
(196586, 'itIT', '', 'I ricordi passati di tutti i Kyrian vengono archiviati nel Tempio della Saggezza. Mi sento sollevata a vederlo resistere.', 38134),
(196585, 'itIT', 'La Protezione dell\'Umiltà resiste. Anche se forse sarebbe un bene per gli Indegni reimparare ciò che viene insegnato qui.', '', 38134),
(196584, 'itIT', 'Un\'impresa divertente, nonché ottima dimostrazione di tempra. Ben fatto.', '', 38134),
(196583, 'itIT', 'Bene, la protezione del Tempio della Lealtà è attiva. I Kyrian impegnati a meditare sul loro impegno verso il Bastione e l\'Arconte sono al sicuro.', '', 38134),
(196580, 'itIT', 'Anche se in frantumi, l\'Elmo del Dominio può esercitare un potere incredibile sulle anime dei morti.\n\nMa non credermi sulla parola. Chiedi al nostro amico qui.', '', 38134),
(196579, 'itIT', '', 'Le forze di Mueh\'zala sono più radicate del previsto. Aiuta la Caccia Selvaggia a liberarsene!', 38134),
(196576, 'itIT', '', 'Raduna i boschi vicini, quindi cerca Dama Baccaluna a Tirna Noch.', 38134),
(196575, 'itIT', 'Potrebbe costringere i cittadini di Revendreth a formare un esercito. Con abbastanza Animum potrebbe persino manipolare l\'essenza stessa delle Terretetre.', '', 38134),
(196574, 'itIT', 'Il lavoro da mercenari non è di mia competenza, ma è per questo che collaboriamo.', '', 38134),
(196573, 'itIT', 'Questa torre è strategicamente importante per i Maldraxxi. Conquistarla sarebbe un bene per il cartello.', '', 38134),
(196572, 'itIT', 'Se vogliamo resistere al vecchio Mueh\'zala, dobbiamo ottenere l\'aiuto della Regina dell\'Inverno.', '', 38134),
(196571, 'itIT', '', 'Uhm... la Protezione della Purezza è disattivata. Dev\'essere questo che ha permesso agli Indegni di invaderci in un tale numero.', 38134),
(196564, 'itIT', 'Oh, chel Loa fetente si sta spingendo troppo oltre!', '', 38134),
(196562, 'itIT', 'Chella maschera ti sta bene, cumpà! Mi ricordi... me!', '', 38134),
(196561, 'itIT', 'Sta succedendo na cosa strana con gli spiriti dei Loa che riposano a Selvarden. Vai a controllare per me, cumpà.', '', 38134),
(196558, 'itIT', 'Forza! Usa la Pietra della Morte per venire da me nell\'Aldilà. Nun tenere paura, mo\'!', '', 38134),
(196535, 'itIT', '', 'La forza bruta non basterà, dobbiamo spezzare l\'incantesimo.', 38134),
(196534, 'itIT', '', 'Mulgrin, no! Presto, aiutami a spezzare questi sigilli!', 38134),
(196532, 'itIT', '', 'Devi fingere di essere un Ogre, non ballare come loro!', 38134),
(196531, 'itIT', '', 'Continuiamo a muoverci. Vediamo cosa scopriamo sul rituale.', 38134),
(196530, 'itIT', '', 'Non parlare. Sorridi e saluta, più idiota sembri, meglio è.', 38134),
(196529, 'itIT', '', 'Bene, ora fingi di essere un Ogre e facci entrare.', 38134),
(196528, 'itIT', 'Una cavia volontaria! Davvero molto raro.', '', 38134),
(196527, 'itIT', '', 'Recluta, avanziamo! Voialtri, proteggete questa posizione.', 38134),
(196526, 'itIT', '', 'Ben fatto. Siamo un passo più vicini dallo schiacciare quei patetici Ogre.', 38134),
(196525, 'itIT', 'Alla prossima parte del piano, naturalmente. Cerca di uccidere alcuni di questi ostacoli prima che ti annientino.', '', 38134),
(196524, 'itIT', '', 'Credo che sia una chiave di qualche tipo, di una stanza all\'interno di Torgast.\n\nCerca nell\'anticamera un dispositivo che la accetti.\n\nDove ti conduca... posso solo speculare.', 38134),
(196523, 'itIT', 'Dove stai andando?!?', '', 38134),
(196522, 'itIT', 'No, non credo proprio. Hai servito il tuo scopo. Ora io servirò il mio.', '', 38134),
(196520, 'itIT', 'L\'Arconte è stato il capo stimato del Bastione sin dalla sua creazione, infiniti eoni addietro.\n\nTi consiglio di stare al tuo posto mentre le chiedi umilmente aiuto.', '', 38134),
(196519, 'itIT', 'Ci incontreremo tutti alla Campata Infranta qui sotto quando avremo finito il nostro lavoro.', '', 38134),
(196518, 'itIT', '', 'Non siete soli! Lok\'tar ogar!', 38134),
(196517, 'itIT', 'Ti copro le spalle! Abbattiamole!', '', 38134),
(196516, 'itIT', 'Sono libero! Ahahah! Ehm, è meglio levarci dai piedi, che ne dite?', '', 38134),
(196513, 'itIT', '', 'Allora lo uccideremo insieme. Per l\'Orda!', 38134),
(196512, 'itIT', 'Sta arrivando qualcosa di grosso!', '', 38134),
(196511, 'itIT', 'È un peccato che debba dissolvere un incantesimo del genere. Tieni le Arpie lontane da me!', '', 38134),
(196499, 'itIT', 'Era difficile già prima che l\'Animum svanisse. Ora è impossibile.\n\n<L\'Aspirante guarda in alto malinconico.>\n\nImpossibile.', 'Era difficile già prima che l\'Animum svanisse. Ora è impossibile.\n\n<L\'Aspirante guarda in alto malinconica.>\n\nImpossibile.', 38134),
(196498, 'itIT', 'Nulla che io faccia ha senso. Nessuno di noi vedrà mai il Forte Elisio. L\'Arconte tace.\n\nSenza Animum, siamo intrappolati qui.', 'Nulla che io faccia ha senso. Nessuno di noi vedrà mai il Forte Elisio. L\'Arconte tace.\n\nSenza Animum, siamo intrappolati qui.', 38134),
(196497, 'itIT', 'Anche se cadrò, non sarò riportato a Oribos. Siamo tutti condannati a restare qui per sempre.\n\nNon c\'è speranza, non più.', 'Anche se cadrò, non sarò riportata a Oribos. Siamo tutti condannati a restare qui per sempre.\n\nNon c\'è speranza, non più.', 38134),
(196496, 'itIT', 'Presto non ci sarà più Animum per le campane. Presto non resterà nulla a salvarci dal fallimento.\n\nE nonostante ciò, insistiamo.', 'Presto non ci sarà più Animum per le campane. Presto non resterà nulla a salvarci dal fallimento.\n\nE nonostante ciò, insistiamo.\n\n<L\'Aspirante tira un lungo sospiro di sfinimento.>', 38134),
(196495, 'itIT', 'Anche se superassi questa prova, non ci saranno più Ascesi. Sono condannato a marcire in questo tempio fino alla fine dei tempi.', 'Anche se superassi questa prova, non ci saranno più Ascesi. Sono condannata a marcire in questo tempio fino alla fine dei tempi.', 38134),
(196494, 'itIT', 'Sento di stare scivolando via. Tutto ciò che voglio fare è resistere, ma dicono che devo proseguire.\n\nCome fanno a non vedere ciò che ci accade? Sono forse ciechi?', 'Sento di stare scivolando via. Tutto ciò che voglio fare è resistere, ma dicono che devo proseguire.\n\nCome fanno a non vedere ciò che ci accade? Sono forse ciechi?', 38134),
(196493, 'itIT', 'Al posto del mio passato vi è un buco. È davvero così che dev\'essere?', 'Al posto del mio passato vi è un buco. È davvero così che dev\'essere?', 38134),
(196492, 'itIT', '', 'Lysonia... Non sapevo che l\'oscurità si celasse dentro di lei...', 38134),
(196491, 'itIT', '', 'I suoi ricordi sono inequivocabili, Devos. La tua Mano ha giurato fedeltà alla Fauce!', 38134),
(196490, 'itIT', 'Conta su di me!', '', 38134),
(196489, 'itIT', '', 'Mi farò strada direttamente tra le Arpie.', 38134),
(196487, 'itIT', '', 'Herbert. Probabilmente sta prendendo appunti.', 38134),
(196486, 'itIT', 'Sembra che abbiano catturato un Reietto per il loro rituale.', '', 38134),
(196484, 'itIT', '', 'Mi unirò a Bo più a nord, dove nidificano le Arpie. Incontriamoci là.', 38134),
(196482, 'itIT', 'Che seccatura. Puoi fare piano così posso fare un pisolino? Svegliami quando non c\'è più baccano.', '', 38134),
(196481, 'itIT', 'Presto sarò pronto per tornare a combattere per Selvarden! Mi serve solo un po\' di riposo per riprendere le forze! E poi... attenti che arriva Grufo!', '', 38134),
(196480, 'itIT', 'Oh, cielo, è bello essere di nuovo in un posto relativamente sicuro. Certe volte là fuori è un putiferio!', '', 38134),
(196479, 'itIT', '', 'Finalmente... un\'udienza con l\'Arconte! Vai pure, ti raggiungeremo lì.', 38134),
(196478, 'itIT', '', 'Dal ruscello al lago, le acque fanno ritorno. Eccoti qui.', 38134),
(196477, 'itIT', 'Di qua! Siamo salvi!', '', 38134),
(196475, 'itIT', '', 'Se vedessi i ragni con cui trattiamo noi Troll, nun piacerebbero manco a te.', 38134),
(196474, 'itIT', '', 'Non avrai mica problemi coi ragni, vero Won\'sa?', 38134),
(196473, 'itIT', '', 'Be\', se la metti così...', 38134),
(196472, 'itIT', '', 'Le case di quei ragni sono state distrutte, non se lo meritavano.', 38134),
(196467, 'itIT', '', 'Ti ringrazio. Ci porterò al sicuro, salta su!', 38134),
(196465, 'itIT', 'Ho reciso una parte del loro Animum! Guarirà le tue ferite se ti avvicinerai a essa.', '', 38134),
(196464, 'itIT', 'Salva quanti più Kyrian possibile!', '', 38134),
(196462, 'itIT', 'Prima recuperi quei pezzi, prima questo Colosso potrà aiutarci a sgomberare i cieli!', '', 38134),
(196461, 'itIT', 'Tieni gli occhi aperti. Il nostro nemico non ha onore.', '', 38134),
(196460, 'itIT', 'Amici! Porto aiuto!', '', 38134),
(196459, 'itIT', 'Sacrilegio! Mortale, avanza e conquista il tempio!', '', 38134),
(196458, 'itIT', 'Tu e la tua gente sarete i primi, ma non gli ultimi.', '', 38134),
(196457, 'itIT', 'Io non tradisco nulla. Il mio maestro desidera distruggere il Bastione e mietere cadaveri per le nostre armate.', '', 38134),
(196452, 'itIT', 'Tradisci il tuo antico patto verso Maldraxxus, Lich! Giustizia sarà fatta!', '', 38134),
(196449, 'itIT', 'Sono felice di vedere di non essere ancora solo. I Maldraxxi hanno attaccato senza preavviso!', '', 38134),
(196444, 'itIT', 'Se fosse questo il caso, allora forse... Per l\'Arconte... Kleia, guarda!', '', 38134),
(196442, 'itIT', '', 'Sembra che il suo patto con la Fauce gli sia stato tenuto segreto. Mi chiedo... e se anche gli altri Indegni non ne fossero al corrente?', 38134),
(196441, 'itIT', 'L\'Indegno che ha distrutto l\'ala... era quello che compariva nei ricordi di Lysonia...', '', 38134),
(196440, 'itIT', 'Cassius ti fornirà un larion. Va\', presto!', '', 38134),
(196439, 'itIT', '', 'Certamente. Ma ora portiamo il Bastone del Primus al nostro santuario e gustiamoci la vittoria. Calcafauce, le tue gesta in questo giorno saranno ricordate per tutta l\'eternità.', 38134),
(196438, 'itIT', 'C\'è ancora molto da fare. Vyraz deve rispondere del suo tradimento, così come Kel\'Thuzad.', '', 38134),
(196437, 'itIT', '', 'Ammetto che all\'inizio avevo dei dubbi su di te, Calcafauce, ma hai dimostrato di essere importante per la nostra congrega.', 38134),
(196436, 'itIT', '', 'Anche se Kel\'Thuzad è sfuggito alla giustizia, la sua ombra è stata cacciata da Maldraxxus. Inoltre, il Margravio Sin\'dane è diventato nostro alleato.', 38134),
(196435, 'itIT', 'I forti sopravvivono e i deboli cadono!', '', 38134),
(196434, 'itIT', 'Uno sfidante si erge sugli altri!', '', 38134),
(196433, 'itIT', 'Un ottimo colpo!', '', 38134),
(196430, 'itIT', '', 'Percepisco dei flussi di magia che legano questa runa alla torre. Dovrebbe portarci là.', 38134),
(196429, 'itIT', '', 'Una squadra di salvataggio! Madre Terra, guida il loro cammino!', 38134),
(196423, 'itIT', '', 'Qui!', 38134),
(196422, 'itIT', '', 'L\'ultima volta che ho visto Anduin, lo stavano portando alla torre. Dobbiamo trovare il modo di arrivare lassù.', 38134),
(196421, 'itIT', 'Questo è il Tremacolo. Qui tormentano le anime, distorte in ombre abiette.', '', 38134),
(196420, 'itIT', '', 'E fa\' in modo di fermare il rituale!', 38134),
(196419, 'itIT', '', 'Sei l\'unica speranza per quei sopravvissuti. Non per metterti pressione.', 38134),
(196418, 'itIT', 'Ogni volta che do uno sguardo a Torgast, avverto degli occhi malvagi che mi fissano. Il rischio è sempre maggiore, ma non possiamo cedere.', '', 38134),
(196417, 'itIT', 'Ciò che mi chiedi... rappresenta un rischio per tutti noi.', '', 38134),
(196416, 'itIT', 'Gran Signore. Hai scrutato dentro Torgast e guidato il Calcafauce tra i suoi tormenti.', '', 38134),
(196415, 'itIT', '', '...è meglio non riviverli. Almeno per ora.', 38134),
(196414, 'itIT', 'Gli orrori che ho visto in quel luogo. I tormenti che ho patito...', '', 38134),
(196413, 'itIT', '', 'Ho temuto il peggio. Io e Baine conosciamo gli orrori inflitti dal Carceriere.', 38134),
(196412, 'itIT', 'Thrall Lode alla Madre Terra! Sono felice che tu sia tornato, fratello.', '', 38134),
(196410, 'itIT', '', 'Una fossa oscura e della magia ancora più oscura. Mi sa che abbiamo trovato il rituale.', 38134),
(196409, 'itIT', '', 'Vediamo di trovare uno di quei rituali.', 38134),
(196404, 'itIT', '', 'Imbevi di veleno le tue armi prima di assalire Sventragruk. Fallo... fuori...', 38134),
(196403, 'itIT', 'Per infondere tale potere c\'è un prezzo da pagare. I resti cinerei delle anime perdute in questa torre. Sì. Scalala, mortale. Recupera ciò che resta.', '', 38134),
(196401, 'itIT', '', 'Le radici possono diventare un veleno mortale. Raccoglile mentre l\'orso non guarda.', 38134),
(196397, 'itIT', 'Drizza deve aver evitato il pericolo in un territorio così ostile. Usa la Furtività per fare lo stesso.', '', 38134),
(196390, 'itIT', '', 'Shuja, l\'Orda non è forte quando combattiamo da soli, ma quando restiamo uniti. Ora dimmi cos\'è successo.', 38134),
(196389, 'itIT', '', 'Madre... ho tentato di fermare gli Ogre da sola, ma ho fallito. Non sono stata abbastanza forte.', 38134),
(196386, 'itIT', 'Lo Scopo ha deciso che dovessi farlo io, esatto.\n\nProprio come te, gli Alienatori sono dei visitatori giunti a Oribos. L\'unica differenza è che richiedono un po\' più del nostro tempo.', '', 38134),
(196383, 'itIT', 'Ehi, salve. Viscozzo ti dà un aiutozzo!', '', 38134),
(196382, 'itIT', 'Un assaggio... di libertà. Un posto per iniziare.', '', 38134),
(196377, 'itIT', 'Calcafauce. Vieni. Vai. Per liberarmi? Forse. Aiutarmi? Sì. Per aiutarmi, così ti aiuto io. Il destino, come vedi, ci ha legati.', '', 38134),
(196376, 'itIT', 'Mi ha usato per il suo scopo. Poi mi ha lasciato qui. Privo di ricordi per... quanto? Non importa. Il tempo, dopotutto, non riguarda la Morte.', '', 38134),
(196375, 'itIT', 'Lui mi ha usato. Il Carceriere, lo chiamano. Per fare... per fare... Mmm. I miei ricordi. Li hai presi tu? No. Li ha presi lui.', '', 38134),
(196374, 'itIT', 'Un mortale? No. Sì... un mortale nella Fauce, diretto alla mia prigione. Per salvarmi? Mmm. Per usarmi?', '', 38134),
(196373, 'itIT', 'Sei uno dei suoi, vero? No. Non dei suoi. Di... qualcos\'altro.', '', 38134),
(196372, 'itIT', '', 'Venthyr! È il momento di colpire questi oppressori!', 38134),
(196371, 'itIT', 'Guardie! Uccidete la Baronessa!', '', 38134),
(196370, 'itIT', '', 'Miserabile Ogre. Vediamo come sta Shuja.', 38134),
(196369, 'itIT', '', 'Madre! Ho deluso... l\'Orda...', 38134),
(196368, 'itIT', '', 'Resisti, Shuja! Recluta, con me! Lok\'tar ogar!', 38134),
(196348, 'itIT', 'Ehi, uh... Ti ricordi di quel discorsino sulle responsabilità? Sembra che quella trivella stia per... esplodere.', '', 38134),
(196342, 'itIT', 'Quando farai saltare quegli zombi, ricordati che è stata la mia invenzione brevettata a renderlo possibile!', '', 38134),
(196341, 'itIT', 'Ora sì che si ragiona! Ritorniamo da Asciatetra.', '', 38134),
(196340, 'itIT', 'Oh oh! Chi ha bisogno di un cinghiale gigante quando possiamo fare COSÌ? Prova ancora.', '', 38134),
(196337, 'itIT', '', 'È mio sacro dovere difendere questo bosco con la vita. Che ci fate a Tirna Vaal?', 38134),
(196336, 'itIT', '', 'Gli Occhi hanno evidenziato un Lich nascosto tra i nidi degli Alalacera. Dagli la caccia.', 38134),
(196334, 'itIT', '', 'Nuove reclute implicano la necessità di addomesticare cavalcature aggiuntive. Solo i più coraggiosi, o i più sciocchi, possono catturare un Alalacera Selvaggio!', 38134),
(196333, 'itIT', '', 'Un altro maledetto Lich è stato sterminato, che liberazione.', 38134),
(196332, 'itIT', 'Non ti deluderò, mio Margravio.', '', 38134),
(196331, 'itIT', '', 'Certamente. Allora è giusto che tu faccia ammenda della tua imprudenza... diventando il mio barone. Da questo giorno in avanti, sarai il mio contatto con la Baronessa Draka.', 38134),
(196330, 'itIT', 'Solo per smascherare il tradimento di Kel\'Thuzad e ripristinare il tuo legittimo governo.', '', 38134),
(196329, 'itIT', 'Uh, non va bene. Vediamo... Devo soltanto... Ecco! Provalo ora.', '', 38134),
(196328, 'itIT', 'Be\', ho promesso un cinghiale gigante. Ora devo tenere fede alla promessa.', '', 38134),
(196327, 'itIT', '', 'Balmedar, hai cospirato con gli stranieri per sfidare la tua casata... e il tuo Margravio.', 38134),
(196322, 'itIT', '', 'Kel\'Thuzad non c\'è più, ma le tue forze sono ancora qui. Pensavi forse di cogliere l\'opportunità al volo e rimuovermi dal ruolo di Margravio?', 38134),
(196314, 'itIT', 'Se vogliamo raggiungerlo, dobbiamo distruggere i cristalli stabilizzatori dello scudo.', '', 38134),
(196312, 'itIT', 'Avanti! Per Maldraxxus! Per il Bastione! Per le Terretetre!', '', 38134),
(196311, 'itIT', '', 'Ho altri compiti di cui occuparmi. Il tuo Factotum può riparare il Segnalatore su tua richiesta.', 38134),
(196306, 'itIT', '', 'Qualunque cosa tu debba fare, falla in fretta. C\'è in gioco la vita di mia figlia.', 38134),
(196305, 'itIT', 'Ehi, se mi dici che vuoi saltare, io ti do gli stivali razzo. Che ne dici di un... cinghiale gigante?', '', 38134),
(196304, 'itIT', '', 'Non scappiamo se la nostra gente ha bisogno di noi! Trova un modo per superare quei non morti.', 38134),
(196303, 'itIT', 'Stanno resuscitando un drago?!? È troppo tardi per scappare via?', '', 38134),
(196302, 'itIT', 'Siete quelli del Tempio della Purezza... Molto bene.', '', 38134),
(196301, 'itIT', '', 'Polemarco, abbiamo notizie urgenti per l\'Arconte.', 38134),
(196300, 'itIT', 'Per quale motivo accendete il Segnalatore, Aspiranti?', '', 38134),
(196296, 'itIT', '', 'Silenzio! Vedo qualcosa!', 38134),
(196295, 'itIT', 'Uhhh, è ancora acceso? Ehi, recluta! Salendo, ti sei assunta ogni responsabilità!', '', 38134),
(196293, 'itIT', 'Mi serve un piano di riserva in caso la tua recluta cada come un salame.', '', 38134),
(196292, 'itIT', '', 'Se ha una telecamera, perché qualcuno deve stare lassù?', 38134),
(196290, 'itIT', 'Mi senti, recluta? Sembra che la telecamera funzioni alla perfezione!', '', 38134),
(196287, 'itIT', 'Perfetto! Non è bellissimo, ma fa il suo dovere.', '', 38134),
(196286, 'itIT', 'Va bene, potrei aver esagerato. Un attimo...', '', 38134),
(196285, 'itIT', 'Questa è una versione ridotta del Propulsore di Destabilizzazione V5. Lasciate che lo ingrandisca...', '', 38134),
(196277, 'itIT', 'Aiuto! Gli Spriggan hanno rubato l\'Animum e preso i miei amici!', '', 38134),
(196272, 'itIT', 'Non ne ho idea... ma so come scoprirlo!', '', 38134),
(196271, 'itIT', '', 'Non siamo ancora al sicuro, dove sono gli altri?', 38134),
(196270, 'itIT', 'Devi essere la Signora della Guerra Breka! La madre di Shuja! Lo diceva che ci avresti salvati.', '', 38134),
(196266, 'itIT', 'Non è ancora tutto perduto.', '', 38134),
(196265, 'itIT', 'Ed ecco perché scommetto sempre sull\'Orda! Filiamocela!', '', 38134),
(196264, 'itIT', 'Oh, siete nei guai ora, maiali!', '', 38134),
(196263, 'itIT', '', 'Speriamo. È raro che agisca con cautela.', 38134),
(196262, 'itIT', 'Shuja? Non ho visto nessuna traccia di lei, forse non è stata catturata.', '', 38134),
(196261, 'itIT', '', 'Muoviamoci, adesso. Quei mostri hanno mia figlia.', 38134),
(196260, 'itIT', 'Tu cadrai, e io avrò un\'altra arma per la mia collezione.', '', 38134),
(196258, 'itIT', 'Bene, sei qui. I Verrospino stanno conducendo un qualche terribile rituale...', '', 38134),
(196257, 'itIT', '', 'Liberati dei tuoi fardelli.', 38134),
(196253, 'itIT', '', 'L\'Orda non abbandona mai i suoi soldati, e io non abbandonerò lei.', 38134),
(196252, 'itIT', '', 'Ci vedremo presto. La piccirilla tua faceva bene a contare su di te.', 38134),
(196250, 'itIT', '', 'Il Margravio ti consumerà!', 38134),
(196249, 'itIT', 'Mmm... una struttura peculiare senza dubbio. Che cos\'è? Chi se ne importa, è comunque utilizzabile.', '', 38134),
(196246, 'itIT', '', 'Vedo Anduin!', 38134),
(196235, 'itIT', '', 'Ci dirigeremo a nord per vedere cos\'hanno scoperto gli altri. Won\'sa, riposa e riprendi le forze prima di unirti a noi.', 38134),
(196233, 'itIT', 'Le cuciture dovrebbero essere più larghe, per permettere all\'anima di entrare e uscire liberamente dal costrutto.\n\nIl costrutto è solo un contenitore, uno strumento, ciò che conta è come l\'anima lo usa.', '', 38134),
(196232, 'itIT', 'Vittoria e gloria sono vostre! Possiate combattere per sempre, Calcafauce!', '', 38134),
(196231, 'itIT', 'Sì! Mordretha è stata sconfitta! Non riesco quasi a crederci! Salutate i nuovi campioni dell\'Arena!', '', 38134),
(196230, 'itIT', 'Le nostre stelle nascenti affronteranno il campione indiscusso del Teatro del Dolore: Mordretha, l\'Imperatrice Eterna!', '', 38134),
(196229, 'itIT', 'Sono pronto a sperimentare l\'esistenza in una nuova forma.', '', 38134),
(196223, 'itIT', 'Hai trovato un Residuo d\'Anima parzialmente assorbito in alcuni armamenti.', 'Hai trovato un Residuo d\'Anima parzialmente assorbito in alcuni armamenti.', 38134),
(196220, 'itIT', 'Ti danno la caccia. Fai attenzione.', '', 38134),
(196218, 'itIT', 'Soldati di Maldraxxus! Siete pronti al massacro?!?', '', 38134),
(196215, 'itIT', 'Tornate alla sala centrale! È il momento dell\'evento principale!', '', 38134),
(196208, 'itIT', 'Continua a cercare!', '', 38134),
(196206, 'itIT', 'L\'Eletta è là, dall\'altra parte del tempio... Molti aspettano i propri riti.', '', 38134),
(196205, 'itIT', '', 'Hai cinque punti combo. Usa Sventramento per chiudere i conti!', 38134),
(196204, 'itIT', '', 'Cerca di ottenere cinque punti combo prima di usare Sventramento.', 38134),
(196203, 'itIT', '', 'Non male, ma Sventramento è molto più potente quando hai più punti combo. Riprova!', 38134),
(196202, 'itIT', '', 'Ottimo lavoro! Sventramento consuma tutti i punti combo. Usali tutti e cinque sul tuo prossimo Sventramento!', 38134),
(196201, 'itIT', '', 'Hai almeno quattro punti combo! È tempo di usare Sventramento!', 38134),
(196200, 'itIT', '', 'Gli attacchi normali non servono a molto da soli. Usa Assalto Funesto!', 38134),
(196199, 'itIT', '', 'Hai almeno tre punti combo! Usa Sventramento!', 38134),
(196198, 'itIT', '', 'Bene! Sventramento infligge danni aggiuntivi in base ai punti combo consumati. Usalo ancora, ma stavolta con quattro punti combo!', 38134),
(196197, 'itIT', '', 'Usa Assalto Funesto ogni volta che puoi per generare punti combo!', 38134),
(196196, 'itIT', '', 'Assalto Funesto genera punti combo. Usa Sventramento quando ne avrai accumulati tre!', 38134),
(196194, 'itIT', 'Come posso aiutarti?', '', 38134),
(196193, 'itIT', '', 'Causa persa, spesa avversa.\n\nRabbia in gabbia, d\'annata saggia.\n\n<La Sibilla Perduta ti indica e ride istericamente.>', 38134),
(196192, 'itIT', 'Incredibile! I nostri contendenti hanno superato le prove e raggiunto la sfida finale!', '', 38134),
(196191, 'itIT', '', 'Pensavi che la tua presenza passasse inosservata? Non lascerai questo posto!', 38134),
(196182, 'itIT', 'Vivo per aiutare!', 'Vivo per aiutare!', 38134),
(196180, 'itIT', 'Te la cavi. Non so quanto, ma te la cavi. Che vuoi?', '', 38134),
(196179, 'itIT', 'Un\'intrusione nel mio dominio.', '', 38134),
(196178, 'itIT', 'Calcafauce... Hai smarrito la via? Un\'oscurità... incombe...', '', 38134),
(196174, 'itIT', 'Questo è il luogo che ho visto quando ho guardato dentro Torgast. Dama Marefiero è vicina.', '', 38134),
(196173, 'itIT', 'Qui le anime sono costrette a bruciare. Sciolte come materiali grezzi per le creazioni del Carceriere.', '', 38134),
(196169, 'itIT', 'Ho... Ho visto la tua fuga prima che venissimo catturati di nuovo. Sai come farci uscire dalla Fauce?', '', 38134),
(196168, 'itIT', 'Sognavo la mia casa... ma mi sono svegliato in questo tormento.', '', 38134),
(196167, 'itIT', 'Vuoi una merendina? Una mano?', 'Vuoi una merendina? Una mano?', 38134),
(196166, 'itIT', 'Qualunque cosa ti serva, ci penso io!', 'Qualunque cosa ti serva, ci penso io!', 38134),
(196165, 'itIT', 'Hai bisogno d\'aiuto?', 'Hai bisogno d\'aiuto?', 38134),
(196146, 'itIT', 'Questo dev\'essere il tuo barone.', '', 38134),
(196140, 'itIT', 'Per quanto tempo sono rimasto nella Fauce? Un giorno? Un\'eternità?', '', 38134),
(196139, 'itIT', 'Grazie mille. Ora che sono libero, dovrei riacquistare presto le forze.', '', 38134),
(196138, 'itIT', '', 'Detonazione di Fuoco è istantanea, quindi è perfetta per quando il nemico è vicino a te.', 38134),
(196137, 'itIT', '', 'Prometti bene. Facciamolo un\'ultima volta prima di affrontare i Verrospino.', 38134),
(196136, 'itIT', '', 'Bene! Detonazione di Fuoco è istantanea ed è perfetta da usare in mischia. Facciamo ancora pratica!', 38134),
(196134, 'itIT', '', 'Detonazione di Fuoco è istantanea. Usala quando il tuo nemico è vicino.', 38134),
(196133, 'itIT', '', 'Resta distante dal nemico e colpiscilo con Dardo di Gelo!', 38134),
(196132, 'itIT', '', 'Continua a usare Dardo di Gelo per tenere a distanza il nemico!', 38134),
(196131, 'itIT', '', 'Il Dardo di Gelo rallenta i movimenti del nemico. Continua a lanciarlo finché non si avvicina.', 38134),
(196130, 'itIT', '', 'Usa Assalto Primordiale anziché Dardo Fulminante quando il nemico è vicino!', 38134),
(196128, 'itIT', '', 'Mostrami ancora i tuoi attacchi a distanza. Ti torneranno utili contro i nemici che devono avvicinarsi a te.', 38134),
(196127, 'itIT', '', 'Travolgi il nemico con attacchi in mischia. Impari in fretta!', 38134),
(196126, 'itIT', '', 'Ancora! Continua a usare Assalto Primordiale quando combatti in mischia.', 38134),
(196125, 'itIT', '', 'Usa la distanza a tuo vantaggio. Colpisci il nemico con Dardo Fulminante!', 38134),
(196124, 'itIT', '', 'Ricorda che puoi infliggere molti danni al nemico mentre corre verso di te!', 38134),
(196122, 'itIT', '', 'Ben fatto, ora proviamo a usare ancora Dardo Fulminante. Preparati!', 38134),
(196121, 'itIT', '', 'Un Factotum ti aiuterà a evocare il Polemarco Adrestes. Scegline uno che ti accompagnerà.', 38134),
(196120, 'itIT', '', 'Bel lavoro. Usa Assalto Primordiale quando il tuo bersaglio è a portata di mischia!', 38134),
(196119, 'itIT', '', 'Bene. Usa Dardo Fulminante per colpire il nemico quando si trova lontano da te.', 38134),
(196118, 'itIT', 'Non può essere peggio dell\'ultima volta che sono stato lì...', '', 38134),
(196115, 'itIT', 'Difendetevi dai vostri dubbi. Non tutto è perfetto.', '', 38134),
(196114, 'itIT', 'Un\'altra sfida superata! Sembra che abbiamo degli sfidanti seri qui!', '', 38134),
(196113, 'itIT', 'Sento che è vicino!', '', 38134),
(196110, 'itIT', 'È qui da qualche parte!', '', 38134),
(196109, 'itIT', 'Grande Portabene, mostrati!', '', 38134),
(196106, 'itIT', '', 'Bene, un\'altra volta. Fuoco Lunare e Ira Silvana ti aiuteranno ad affrontare i Verrospino.', 38134),
(196105, 'itIT', '', 'Hai usato Fuoco Lunare troppo presto. Infliggerai più danni se lo usi quando è quasi scaduto.', 38134),
(196104, 'itIT', '', 'Fuoco Lunare si sta esaurendo, lancialo ancora!', 38134),
(196102, 'itIT', '', 'Fuoco Lunare infliggerà danni periodici al tuo avversario. Usa Ira Silvana mentre il nemico ne è afflitto.', 38134),
(196101, 'itIT', '', 'Ottimo lavoro. Continua a usare Ira Silvana mentre Fuoco Lunare infligge danni. Ti dirò quando usarlo di nuovo.', 38134),
(196099, 'itIT', '', 'Usa Fuoco Lunare all\'inizio del combattimento. Infliggerà danni periodici al tuo nemico.', 38134),
(196097, 'itIT', 'Sai, dev\'esserci un traditore tra noi. Qualcuno doveva sapere il momento esatto in cui attaccare la scorta.', 'Sai, dev\'esserci un traditore tra noi. Qualcuno doveva sapere il momento esatto in cui attaccare la scorta.', 38134),
(196096, 'itIT', 'C\'è proprio qualcosa di sbagliato in tutto questo.', 'C\'è proprio qualcosa di sbagliato in tutto questo.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196095, 'itIT', '', 'Bene, riproviamo. Devo essere certa che tu possa affrontare i Verrospino.', 38134),
(196094, 'itIT', '', 'Hai usato Corruzione troppo presto. Infliggerai più danni se la usi quando è quasi scaduta.', 38134),
(196093, 'itIT', '', 'Corruzione si sta esaurendo, lanciala ancora!', 38134),
(196091, 'itIT', '', 'Corruzione infliggerà danni periodici al tuo avversario.', 38134),
(196090, 'itIT', '', 'Ancora! Gli Stregoni possono infliggere danni ingenti usando correttamente Corruzione.', 38134),
(196088, 'itIT', 'I nostri contendenti hanno superato la prima prova! Ma il titolo è ancora lontano!', '', 38134),
(196086, 'itIT', 'Nessuna... speranza! Nessuna... pietà!', '', 38134),
(196085, 'itIT', '', 'Usa Corruzione all\'inizio del combattimento. Infliggerà danni periodici al tuo nemico.', 38134),
(196084, 'itIT', 'Parla, mostro!', '', 38134),
(196083, 'itIT', 'Allora abbiamo una strada da seguire.', '', 38134),
(196082, 'itIT', '', 'Il Calderone di cui ha parlato si trova oltre il Fiume delle Anime. Penso che Baine sia prigioniero lì.', 38134),
(196081, 'itIT', 'Hai trovato un senso alle sue parole, Ammiraglio Supremo?', '', 38134),
(196080, 'itIT', 'Niente... sfugge... alla Fauce...', '', 38134),
(196079, 'itIT', 'No! Dormiente... distrutta!', '', 38134),
(196078, 'itIT', 'Pietra... al Calderone... forse...', '', 38134),
(196077, 'itIT', 'Quindi un modo c\'è! Dicci dove trovarlo!', '', 38134),
(196076, 'itIT', 'È inutile! Il passaggio... sigillato... tempo fa...', '', 38134),
(196075, 'itIT', 'Parla! Come lasciamo questo posto?', '', 38134),
(196074, 'itIT', 'Il nostro amico ha qualcosa da condividere.', '', 38134),
(196073, 'itIT', 'Giusto in tempo.', '', 38134),
(196072, 'itIT', '', 'Tutti... serviranno... il Carceriere...', 38134),
(196071, 'itIT', '', 'Vi piegherete tutti!', 38134),
(196070, 'itIT', 'Mi sentirò meglio quando ci lasceremo questo posto alle spalle, fa\' strada.', '', 38134),
(196069, 'itIT', 'Ti ringrazio. Temevo che non sarei mai stato libero da queste catene.', '', 38134),
(196068, 'itIT', 'Non c\'è vergogna nell\'aver bisogno di un po\' di riposo. Sei mortale, dopotutto.', '', 38134),
(196061, 'itIT', '', 'I tuoi incantesimi si possono usare a distanza. Lanciali per sferrare il primo colpo ai tuoi nemici!', 38134),
(196060, 'itIT', '', 'Ottimo, ora fallo ancora! Parola d\'Ombra: Dolore continuerà a danneggiare il nemico mentre lo attacchi con Punizione.', 38134),
(196059, 'itIT', '', 'Hai usato Parola d\'Ombra: Dolore troppo presto. Infliggerai più danni se la usi quando è quasi scaduta.', 38134),
(196058, 'itIT', '', 'Parola d\'Ombra: Dolore sta svanendo! Lanciala di nuovo!', 38134),
(196052, 'itIT', '', 'Bene così, un\'altra volta! I Sacerdoti possono indebolire gli avversari con un uso corretto di Parola d\'Ombra: Dolore.', 38134),
(196051, 'itIT', '', 'Bene! Usa Punizione mentre Parola d\'Ombra: Dolore infligge danni. Ti dirò quando usare di nuovo Parola d\'Ombra: Dolore.', 38134),
(196050, 'itIT', '', 'Usa Parola d\'Ombra: Dolore all\'inizio del combattimento. Infligge danni periodici ai tuoi nemici.', 38134),
(196032, 'itIT', '', 'Hai abbastanza potere sacro per farla finita! Usa Scudo del Virtuoso!', 38134),
(196031, 'itIT', '', 'Ben fatto! Non hai più potere sacro, quindi usa Assalto del Crociato per generarne altro!', 38134),
(196030, 'itIT', '', 'Non male, recluta. Continua a usare Assalto del Crociato per generare potere sacro.', 38134),
(196029, 'itIT', '', 'Hai abbastanza potere sacro da usare Scudo del Virtuoso!', 38134),
(196028, 'itIT', '', 'Bene! I Paladini più abili accumulano velocemente potere sacro e lo usano per infliggere danni ai nemici.', 38134),
(196027, 'itIT', '', 'Ancora! Continua a usare Assalto del Crociato per accumulare potere sacro!', 38134),
(196026, 'itIT', '', 'Assalto del Crociato aumenta il tuo potere sacro. Ne avrai bisogno per usare abilità più potenti!', 38134),
(196025, 'itIT', '', 'Il potere sacro brucia in te! Adesso usa Scudo del Virtuoso!', 38134),
(196024, 'itIT', '', 'Gli attacchi normali non servono a molto da soli. Usa Assalto del Crociato!', 38134),
(196022, 'itIT', '', 'Ancora una volta. Carica!', 38134),
(196021, 'itIT', '', 'Usa di nuovo Carica verso di me per avvicinarti! Inizia sempre i combattimenti usando Carica!', 38134),
(196020, 'itIT', '', 'Usa Carica all\'inizio del combattimento. Mostra al tuo nemico la furia dell\'Orda!', 38134),
(196015, 'itIT', 'Posso aiutare!', 'Posso aiutare!', 38134),
(196003, 'itIT', 'Ho cibo, bevande e oggetti speciali per supportare i tuoi sforzi.\n\nCosa posso fare per te?', '', 38134),
(195999, 'itIT', '', 'Basta! Sei in grado di combattere i Verrospino. Ora dobbiamo soccorrere i nostri!', 38134),
(195997, 'itIT', '', 'Ottimo lavoro! Carica genera rabbia e infligge danni ai nemici. Usala sempre per iniziare uno scontro.', 38134),
(195994, 'itIT', '', 'Contusione consumerà rabbia per infliggere danni. Puoi usare Contusione finché avrai abbastanza rabbia!', 38134),
(195993, 'itIT', '', 'I Guerrieri dovrebbero usare Carica in ogni combattimento, poiché genera rabbia per lanciare altre abilità.', 38134),
(195992, 'itIT', '', 'Non fare affidamento sugli attacchi normali. Usa Contusione!', 38134),
(195990, 'itIT', 'Iniziamo ad allineare la tua via allo Scopo. Pensi di poterci riuscire?', '', 38134),
(195989, 'itIT', '', 'Usa Carica verso di me!', 38134),
(195988, 'itIT', '', 'Capisco, Kleia. Dobbiamo chiamare il Polemarco, lui potrà presentarti all\'Arconte.', 38134),
(195985, 'itIT', '', 'La tua prossima lezione inizia ora!', 38134),
(195981, 'itIT', '', 'Recluta, i Verrospino sono una minaccia più grande dei Murloc. Devi prepararti ad affrontarli in battaglia.', 38134),
(195980, 'itIT', '', 'Ma è anche un soldato dell\'Orda, sa che non lasciamo indietro nessuno.', 38134),
(195979, 'itIT', '', 'È mia figlia, niente potrebbe tenermi lontana da lei.', 38134),
(195978, 'itIT', '', 'Shuja ci aveva avvisato... dell\'arrivo vostro...', 38134),
(195977, 'itIT', 'Non posso garantire la tua sicurezza fuori dal Cancello Antico.', 'Non posso garantire la tua sicurezza fuori dal Cancello Antico.', 38134),
(195975, 'itIT', '', 'Ti ringrazio, Thiernax. E anche te, Qadarin. Siete fortunati a poter contare l\'uno sull\'altro.', 38134),
(195972, 'itIT', '', 'Spero soltanto che sia disposta a separarsene.', 38134),
(195971, 'itIT', '', 'Alexstrasza deve aver tenuto qualche ricordo di Korialstrasz. Un Emblema dell\'Amore Perduto.', 38134),
(195969, 'itIT', '', 'Won\'sa!', 38134),
(195968, 'itIT', '', 'Tengo... fame...', 38134),
(195959, 'itIT', 'Preparerò i Centurioni per un attacco!', '', 38134),
(195958, 'itIT', '', 'Voi tre, cercate altri superstiti. Recluta, io e te ci spingeremo più avanti.', 38134),
(195957, 'itIT', '', 'Dobbiamo far arrivare quest\'informazione all\'Arconte. Incontriamoci a Requie dell\'Eroe!', 38134),
(195953, 'itIT', 'Andate, mortali! La tratterrò io!', '', 38134),
(195952, 'itIT', 'Non oggi, Kaal!', '', 38134),
(195949, 'itIT', '', 'Basta! Le vostre sofferenze saranno una testimonianza dell\'ira del Maestro.', 38134),
(195948, 'itIT', '', 'Ribelli ostinati, conoscerete la vostra fine!', 38134),
(195947, 'itIT', '', 'Queste sale saranno la vostra tomba!', 38134),
(195946, 'itIT', '', 'Quel prigioniero appartiene al Maestro, non lo prenderete!', 38134),
(195945, 'itIT', '', 'Guardie! Uccideteli tutti!', 38134),
(195943, 'itIT', '', 'Quindi è questo che cerchi. Non lascerà mai questa prigione!', 38134),
(195942, 'itIT', 'Presto, mortali! Scappate con Z\'rali!', '', 38134),
(195940, 'itIT', 'Denathrius ne assorbe la Luce per usarla contro i suoi nemici. Noi la useremo contro di lui, invece.', '', 38134),
(195939, 'itIT', 'Il prigioniero è quaggiù. Z\'rali, uno dei Naaru che hanno invaso il Distretto delle Braci. Gli altri pensano sia morto.', '', 38134),
(195937, 'itIT', 'Il più grosso dev\'essere l\'alfa. Uccidilo e gli altri scapperanno.', '', 38134),
(195932, 'itIT', 'Datemi retta, quella tempesta non era naturale.', '', 38134),
(195931, 'itIT', '', 'Attenzione, Calcafauce, si sono infiltrati tra le nostre fila.', 38134),
(195930, 'itIT', 'Grazie mille. Troveremo la spedizione.', '', 38134),
(195924, 'itIT', '', 'Che tempesta! Probabilmente avrà colpito anche la prima nave.', 38134),
(195920, 'itIT', '', 'Recupera i nostri rifornimenti, ma non combattere contro troppi Murloc, sono pericolosi in gruppo.', 38134),
(195918, 'itIT', '', 'Recluta! Sei ancora in vita! Dobbiamo riorganizzarci subito.', 38134),
(195917, 'itIT', '', 'Ve la farò pagare!', 38134),
(195914, 'itIT', '', 'Sanguina per i tuoi crimini.', 38134),
(195913, 'itIT', '', 'Un altro traditore cade.', 38134),
(195910, 'itIT', 'Questo è un punto di riferimento per gli spiriti liberi.\n\n<Lo Scriba del Destino sembra valutarti per un istante.>\n\nPercepisco la mano di un grande potere su di te, $p. Nonostante tu abbia affrontato la fine della tua esistenza più e più volte, questa forza ti ha $gmantenuto ancorato:mantenuta ancorata; al piano mortale.\n\nForse i dispositivi come questo ti terranno $gancorato:ancorata; alle Terretetre, se un destino avverso dovesse abbattersi su di te.\n\nDeciderà lo Scopo. Lo Scopo ci guida.', '', 38134),
(195905, 'itIT', 'Qui scoprirai che gli Alienatori hanno creato un mezzo per farti accedere ai tuoi averi personali. Qual era la parola che hanno usato... una banca, credo.\n\nAmmetto che il concetto di possesso ci è estraneo, ma gli Alienatori hanno sempre affermato di essere degli esperti in questo campo.', '', 38134),
(195903, 'itIT', '', 'Sono molto più piccoli di quanto immaginassi.', 38134),
(195902, 'itIT', '', 'È $gil:la; Calcafauce.', 38134),
(195901, 'itIT', 'Qui abbiamo stabilito il salone dell\'artigiano, naturalmente a tuo beneficio.\n\nNoi Alienatori viaggiamo in lungo e in largo e apprendiamo dagli abitanti di ogni regno che visitiamo. Questo ci permette di soddisfare ogni necessità della nostra clientela. \n\nI nostri membri hanno cercato di padroneggiare le varie professioni che quelli come te ritengono importanti. Scoprirai che sono ansiosi di aiutarti nel tuo cammino da $gartigiano:artigiana;.', '', 38134),
(195899, 'itIT', '$gBenvenuto, straniero:Benvenuta, straniera;. Siamo al tuo servizio.', '', 38134),
(195898, 'itIT', 'Qui abbiamo alcune delle offerte più esotiche che puoi trovare. \n\nIo e i miei colleghi siamo specializzati nel reperimento e nello stallaggio di bestie esotiche. Inoltre, scoprirai che abbiamo accesso a ciò che chiami Banca Eterea e alla trasmogrificazione. \n\nInfine, il mio amico Ta\'vis può aiutarti ad alterare il tuo aspetto. Tanto vale essere $gbelli:belle; mentre si salvano gli universi, no?', '', 38134),
(195896, 'itIT', '$GBenvenuto:Benvenuta; nel nostro bazar, $gamico mio:amica mia;. Siamo qui per servire.', '', 38134),
(195893, 'itIT', '', 'Soldati, tenetevi forte!', 38134),
(195892, 'itIT', 'Signora della Guerra! Questa tempesta sta per travolgerci!', '', 38134),
(195891, 'itIT', '', 'Dobbiamo unirci al combattimento in cielo. Che le Fiamme del Coraggio guidino la tua lama.', 38134),
(195888, 'itIT', 'Fategliela... pagare...', 'Fategliela... pagare...', 38134),
(195887, 'itIT', 'Sarebbe potuta andare... diversamente...', 'Sarebbe potuta andare... diversamente...', 38134),
(195886, 'itIT', 'Ho solo... un rimpianto...', 'Ho solo... un rimpianto...', 38134),
(195885, 'itIT', 'È ciò... che mi merito...', 'È ciò... che mi merito...', 38134),
(195884, 'itIT', 'Salva... gli... altri...', 'Salva... gli... altri...', 38134),
(195883, 'itIT', 'Sono... perduto...', 'Sono... perduta...', 38134),
(195882, 'itIT', 'Speravo... in qualcosa... di più...', 'Speravo... in qualcosa... di più...', 38134),
(195881, 'itIT', 'Perdonami...', 'Perdonami...', 38134),
(195880, 'itIT', '', 'Voglio essere veloce e forte in una forma che mi permetta di andare a caccia per tutte le Terretetre!\n\nFammi diventare un cane.', 38134),
(195879, 'itIT', '', 'Puoi aiutarmi a raggiungere il mio pieno potenziale tra le anime di questa terra?', 38134),
(195878, 'itIT', 'No, no. Tutti dobbiamo fare la nostra parte. So che faresti lo stesso per me.', 'No, no. Tutti dobbiamo fare la nostra parte. So che faresti lo stesso per me.', 38134),
(195877, 'itIT', 'Oh, no Veronika! Neanche tu puoi condividere l\'Animum. Dobbiamo dire alla Signora Mihaela che è impossibile.', 'Oh, no Veronika! Neanche tu puoi condividere l\'Animum. Dobbiamo dire alla Signora Mihaela che è impossibile.', 38134),
(195876, 'itIT', 'Nessun problema, tesoro. Posso pagare anche per te.', 'Nessun problema, tesoro. Posso pagare anche per te.', 38134),
(195875, 'itIT', 'Un altro tributo? Non mi restano anime e non ho Animum in avanzo. La Signora Mihaela non ti ha detto di escludermi?', 'Un altro tributo? Non mi restano anime e non ho Animum in avanzo. La Signora Mihaela non ti ha detto di escludermi?', 38134),
(195873, 'itIT', '', 'Ciao! Torna presto!', 38134),
(195872, 'itIT', 'Ottimo lavoro.', '', 38134),
(195871, 'itIT', '', 'Assicurati di infliggere afflizioni su quanti più nemici possibili per sfruttare al meglio Estasi Malefica!', 38134),
(195870, 'itIT', '', 'Ora lancia Estasi Malefica.', 38134),
(195869, 'itIT', '', 'Estasi Malefica è un incantesimo molto potente. Lanciarla consuma un frammento d\'anima, ma fa pulsare tutte le afflizioni per torturare di danni il nemico.', 38134),
(195868, 'itIT', '', 'Dardo d\'Ombra ti permette di continuare a colpire con la magia dell\'ombra senza consumare frammenti d\'anima.', 38134),
(195867, 'itIT', '', 'Ottimo lavoro.', 38134),
(195866, 'itIT', 'Assicurati di infliggere afflizioni su quanti più nemici possibili per sfruttare al meglio Estasi Malefica!', '', 38134),
(195865, 'itIT', 'Ora lancia Estasi Malefica.', '', 38134),
(195864, 'itIT', 'Estasi Malefica è uno dei tuoi incantesimi più potenti. Lanciarla consuma un frammento d\'anima, ma fa pulsare tutte le afflizioni per straziare il nemico.', '', 38134),
(195863, 'itIT', 'Dardo d\'Ombra ti permette di continuare a colpire con la magia dell\'ombra senza consumare frammenti d\'anima.', '', 38134),
(195862, 'itIT', '', 'Ecco fatto.', 38134),
(195859, 'itIT', '', 'Da\' una sistemata, così potrò riaccendere questa forgia al più presto.', 38134),
(195858, 'itIT', '', 'Noi aiutiamo bene.', 38134),
(195857, 'itIT', 'Mi arrendo! La tua forza ci farà comodo nella nostra missione.', '', 38134),
(195856, 'itIT', '', 'I Factotum esistono esclusivamente per esserci d\'aiuto. Si deprimono quando non riescono a svolgere i loro compiti.', 38134),
(195855, 'itIT', '', 'Aggiusta forgia! Aggiusta forgia!', 38134),
(195854, 'itIT', 'Non dare mai le spalle al nemico!', '', 38134),
(195853, 'itIT', 'Non scappare mai dal nemico! Vittoria o morte!', '', 38134),
(195852, 'itIT', 'Mi metto in posizione. Fai la prima mossa e colpisci con forza!', '', 38134),
(195851, 'itIT', '', 'Sono onorata di avervi accanto a me. Finalmente...', 38134),
(195844, 'itIT', '', 'Ottimo lavoro. Ora dob... Pioggia? Il nostro Sciamano aveva previsto un cielo limpido.', 38134),
(195835, 'itIT', 'Tu vieni con noi!', '', 38134),
(195834, 'itIT', '', 'Dobbiamo essere pronti a tutto. Mostrami le tue abilità in combattimento.', 38134),
(195833, 'itIT', '', 'Freddo! Aiuto! Freddo!', 38134),
(195832, 'itIT', '', 'Finalmente...', 38134),
(195831, 'itIT', '', '<Gli occhi di Sofone sono chiusi mentre assapora il momento.>', 38134),
(195827, 'itIT', 'Ora! Piegala al tuo volere!', '', 38134),
(195825, 'itIT', '...obbedisco...', '', 38134),
(195824, 'itIT', 'Non puoi vincolarmi! Io... io...', '', 38134),
(195823, 'itIT', 'Spada d\'Ebano, a me! Abbattete questa mostruosità!', '', 38134),
(195822, 'itIT', 'Questo bersaglio rappresenta una minaccia temibile. Farò segno alla Spada d\'Ebano di avanzare una volta ingaggiato.', '', 38134),
(195821, 'itIT', 'È inutile, queste anime sono troppo frammentate. Dev\'esserci un altro modo...', '', 38134),
(195820, 'itIT', 'Usa l\'elmo mentre è debole!', '', 38134),
(195819, 'itIT', '', 'Aspirante Kleia, fatti avanti.', 38134),
(195818, 'itIT', '', 'La terra stessa su cui camminate mi rigenera!', 38134),
(195817, 'itIT', '', 'Dolce suolo, nutrimi ancora una volta!', 38134),
(195816, 'itIT', '', 'Mi dispiace... non sono riuscita a salvarvi...', 38134),
(195815, 'itIT', '', 'Bestie schifose!', 38134),
(195814, 'itIT', '', 'I fiori tornano a sbocciare e vi scacciano da questo luogo!', 38134),
(195813, 'itIT', '', 'Vi ho in pugno!', 38134),
(195812, 'itIT', '', 'I rami vi avvolgono!', 38134),
(195811, 'itIT', '', 'Chi va là?', 38134),
(195810, 'itIT', '', 'L\'oscurità può anche offuscarmi la vista, ma le mie radici sono salde e voi non mi impedirete di difendere questa radura!', 38134),
(195795, 'itIT', '', 'È incredibile quello che viene gettato nella Fauce. Modestamente, so riconoscere un bene di valore quando ne vedo uno.', 38134),
(195790, 'itIT', 'Nun ti è mai interessato della gente tua, ma solo del potere!', '', 38134),
(195789, 'itIT', '', 'Alla prossima, Calcafauce.', 38134),
(195788, 'itIT', '', 'Spero che tu non voglia approfittare troppo della mia ospitalità, c\'è del lavoro da fare.', 38134),
(195787, 'itIT', 'Meglio che fai attenzione, Padre del Sonno.', '', 38134),
(195785, 'itIT', 'Nun puoi fermarmi per sempre!', '', 38134),
(195784, 'itIT', 'Nun cantare vittoria troppo presto, vecchio Loa!', '', 38134),
(195783, 'itIT', 'Sbrigatevi! Mo\' nun importa il prezzo.', '', 38134),
(195780, 'itIT', 'Salve, $n, Ser Bolvar ci ha chiesto di aiutarti.', '', 38134),
(195778, 'itIT', 'Ben fatto, eroi, già... Chello che attende Mueh\'zala nun è cosa per occhi mortali. Pigliate i vostri gingilli e andatevene!', '', 38134),
(195777, 'itIT', 'Hai sconfinato nel mio dominio, mortale. Ovunque tu ti nasconda... Io ti troverò.', '', 38134),
(195776, 'itIT', '', 'Dobbiamo muoverci e affrontare il vostro oppressore!', 38134),
(195775, 'itIT', 'Miei cari sudditi, abbiamo ottenuto una piccola vittoria. Prendete l\'Animum dalle loro spoglie.', '', 38134),
(195774, 'itIT', 'Così piccirilli, così deboli!', '', 38134),
(195773, 'itIT', 'Le ombre vi piglieranno!', '', 38134),
(195772, 'itIT', 'Mi piglierò la vita vostra!', '', 38134),
(195770, 'itIT', 'Stupidi mortali! Io sono il vero Dio della Morte!', '', 38134),
(195769, 'itIT', 'Nun si fugge alla morte!', '', 38134),
(195768, 'itIT', 'Io comando l\'oscurità!', '', 38134),
(195764, 'itIT', 'Ammirate l\'incredibile potenza del Calcafauce! Ben fatto!', '', 38134),
(195762, 'itIT', 'Muscoli e armatura! Mi getto in piena battaglia e rido quando i colpi mi rimbalzano addosso!', '', 38134),
(195760, 'itIT', 'Le tue reclute hanno bisogno d\'azione, si annoiano senza combattere!', '', 38134),
(195759, 'itIT', 'Occhio a come parli, piccirillo! Bwonsamdi è il capo mo\'.', '', 38134),
(195758, 'itIT', '', 'Chi avrebbe pensato che qualcuno chiamato Alamorte potesse portare tutta quella distruzione?', 38134),
(195757, 'itIT', '', 'Se trovi un\'arma potente, assicurati che non sia corrotta dalla Legione Infuocata o dagli Dei Antichi, va bene?', 38134),
(195756, 'itIT', 'Ahahah, curioso che ci siano voluti... così tanti di voi... Per sconfiggermi...', '', 38134),
(195755, 'itIT', 'Nun darti delle arie, Bwonsamdi... Nun sei niente paragonato al Carceriere!', '', 38134),
(195753, 'itIT', 'Il potere di Maldraxxus scorre dentro di me! Io sono il volto della morte!', '', 38134),
(195752, 'itIT', 'Ma nun ti devi preoccupare. Terrai tantissimo tempo per imparare dagli errori tuoi.', '', 38134),
(195751, 'itIT', 'Le ossa vi chiamano!', '', 38134),
(195750, 'itIT', 'I tempi cambiano, cumpà. I mortali nun sono mai stati deboli come credevi. Vedi, tengono... potenziale.', '', 38134),
(195749, 'itIT', 'I teschi dei caduti gridano vendetta!', '', 38134),
(195747, 'itIT', 'Ammirate, il nuovo ricettacolo mi aspetta... Una forma molto più adatta a un maestro della morte!', '', 38134),
(195746, 'itIT', 'La vostra interferenza non importa più, il rituale è completo.', '', 38134),
(195745, 'itIT', 'Anche questo è stato previsto!', '', 38134),
(195744, 'itIT', 'Le loro ossa rafforzeranno soltanto il mio ricettacolo!', '', 38134),
(195743, 'itIT', 'Un sacrificio volontario per la Casata dei Rituali!', '', 38134),
(195742, 'itIT', 'Il rituale non sarà interrotto! Ossa dei caduti, risvegliatevi e accogliete i nostri visitatori!', '', 38134),
(195739, 'itIT', 'Nessuna... liberazione...', '', 38134),
(195737, 'itIT', 'Agonia... angoscia eterna...', '', 38134),
(195736, 'itIT', 'Finalmente... la vera... fine...', '', 38134),
(195734, 'itIT', 'Dolore... tormento... oblio...', '', 38134),
(195724, 'itIT', 'Non vedo l\'ora di aiutarti, Calcafauce.', '', 38134),
(195721, 'itIT', 'Dita rapide, riflessi pronti, sguardo attento, così mi piace. Ti aiuterò a recuperare i rifornimenti e a vendere oggetti che non ti servono.', '', 38134),
(195720, 'itIT', 'Se avessi una pelle che emette spore rigenerative, potrei tenerci in vita nel cuore della battaglia.', '', 38134),
(195718, 'itIT', 'Saremo una grande squadra, una volta tornati insieme!', '', 38134),
(195717, 'itIT', 'Gli Aspiranti si addestrano per eoni per affinare il corpo e la mente. Si purificano dal proprio passato, guardando soltanto al futuro.\n\nUna vita intera al servizio non solo dell\'Arconte, ma di tutte le anime. Gli uni per gli altri.\n\nL\'Ascensione, un tempo solo un lontano traguardo, ora è finalmente a portata di mano per questi Aspiranti.', '', 38134),
(195716, 'itIT', 'Combatterò presto al tuo fianco!', '', 38134),
(195715, 'itIT', 'Avere dei compagni è importante. Voglio essere abbastanza forte da riuscire a trascinare gli altri in combattimento con me.', '', 38134),
(195713, 'itIT', 'Oh, se volessi muoverti rapidamente tra le scogliere, prendi un Lanciatore di Seta. Ti aiuterà ad arrivare in un altro punto!', '', 38134),
(195705, 'itIT', 'Un\'altra creazione raffinata!', '', 38134),
(195703, 'itIT', 'Che seccatura. Sputomarcio, mettiti al lavoro, e non deludermi come il tuo predecessore!', '', 38134),
(195702, 'itIT', 'Babbei incompetenti! Tritabudella, cerca di non danneggiare quegli arti mentre li strappi via.', '', 38134),
(195701, 'itIT', '', 'È una balestra che vuoi. Non ti serve a niente un pugnale, se non puoi avvicinarti!', 38134),
(195700, 'itIT', 'A me piacciono i pugnali lunghi che si infilano sotto le costole.', '', 38134),
(195699, 'itIT', 'Mortale, cosa preferisci? Pugnale, spada o ascia?', '', 38134),
(195689, 'itIT', '', 'Ehi! Guarda! Ci sono altri Lich! Vediamo se vogliono giocare!', 38134),
(195688, 'itIT', 'Camuffarti da Ogre per intrufolarti in una loro base e impedire un rituale per rianimare un drago morto è da pazzi.\n\nNon vedo l\'ora di cominciare!', '', 38134),
(195687, 'itIT', '', 'Aiuterai a guidare la carica, mortale. Caccia via questi invasori dal mio bosco!', 38134),
(195686, 'itIT', '', 'Suonate i corni! Tutta Selvarden è chiamata alla Caccia!', 38134),
(195674, 'itIT', '', 'Kyrian... al servizio della Fauce? È preoccupante...', 38134),
(195672, 'itIT', '', 'C\'è solo un modo di ottenere un\'udienza con l\'Arconte: percorrere la via e dimostrarsi meritevoli dell\'ascensione.', 38134),
(195671, 'itIT', '', 'Non erano dei prigionieri, ma servi volontari. Questo sicuramente merita un\'udienza!', 38134),
(195670, 'itIT', '', 'Un triste destino... Ma se l\'Arbiter ha ritenuto che se lo meritassero, allora la questione non ci riguarda.', 38134),
(195669, 'itIT', '', 'Ascesa, il nuovo arrivo porta delle notizie allarmanti di Kyrian nella Fauce. Dobbiamo informare l\'Arconte!', 38134),
(195668, 'itIT', '', 'Addio.', 38134),
(195666, 'itIT', '', 'Perché quest\'unità Mnemi è attiva? Possiamo a malapena permetterci di usare quest\'Animum.', 38134),
(195665, 'itIT', '', 'Devono aver percepito l\'attivazione dell\'Anfitriona. Me ne occuperò io.', 38134),
(195664, 'itIT', '', 'Cosa significa tutto questo?', 38134),
(195662, 'itIT', 'Questo Animum appartiene al Tributiere. Fermatevi o perite!', '', 38134),
(195661, 'itIT', '', 'Eccolo! Gli stiamo alle costole!', 38134),
(195660, 'itIT', '', 'Zannamortale! Segui quella carrozza!', 38134),
(195659, 'itIT', 'Tutto può essere riparato, anche questo.', '', 38134),
(195656, 'itIT', '', 'È alle strette! Mostriamo a questo farabutto quanto abbia sbagliato strada.', 38134),
(195655, 'itIT', 'Aaah! Nooo! Mai più... Aaargh!', 'Aaah! Nooo! Mai più... Aaargh!', 38134),
(195654, 'itIT', 'Che panorama! Oh, e uno splendido passosetoso! Quel poverino deve sentirsi solo qui, vedi se riesci a tirarlo su di morale!', '', 38134),
(195653, 'itIT', 'Dobbiamo fare tutti dei sacrifici. Il Maestro lo pretende.', 'Dobbiamo fare tutti dei sacrifici. Il Maestro lo pretende.', 38134),
(195652, 'itIT', 'Nooo! Non di nuovo. Non posso...', 'Nooo! Non di nuovo. Non posso...', 38134),
(195650, 'itIT', 'I cavalieri della Spada d\'Ebano sanno come costringere i morti a parlare. Se prendessimo una di queste ombre, potremmo scoprire i loro segreti.', '', 38134),
(195648, 'itIT', 'Infinite anime stanno giungendo in questo regno, ma alcune si trovano qui da innumerevoli secoli. Forse una di loro saprà come uscire.', '', 38134),
(195647, 'itIT', '', 'Riesco appena a immaginare l\'orrore che devono aver subito questi spiriti. Temo non ci sia nulla da salvare.', 38134),
(195643, 'itIT', '', 'Mi è capitato di intravedere Baine e Anduin durante i miei tentativi di fuga. Sono vivi... di questo sono certa.', 38134),
(195640, 'itIT', '', 'Qualunque cosa abbiano in mente il Carceriere e la Banshee, non penso che finirebbe bene... per noi.', 38134),
(195639, 'itIT', 'Abbiamo sopportato innumerevoli tormenti. Certe volte, sembrava che ci stessero mettendo alla prova. Con quale scopo, non saprei dirlo.', '', 38134),
(195638, 'itIT', '', 'Si è occupato personalmente del nostro... trattamento.', 38134),
(195637, 'itIT', '', 'Il Carceriere comanda questo regno. Sappiamo poco su di lui, ma è chiaro che provi un interesse speciale per noi.', 38134),
(195635, 'itIT', '', 'Conosco questo... posto... è molto su cui riflettere.\n\nPrenditi un attimo per rimetterti in sesto. Quando vorrai, ti dirò quello che so.', 38134),
(195634, 'itIT', '', 'Sì. E una delle loro carnefici, nonché la nostra, è Sylvanas Ventolesto.', 38134),
(195633, 'itIT', 'Qualcosa è cambiato. Anche anime pure e nobili vengono imprigionate qui. Urlano tra atroci sofferenze.', '', 38134),
(195632, 'itIT', '', 'I suoi abitanti la chiamano la \"Fauce\". A quanto ho capito era un luogo pensato per imprigionare le anime più malvagie e irrecuperabili che siano mai esistite.', 38134),
(195631, 'itIT', '', 'Bene... da dove cominciare?', 38134),
(195622, 'itIT', 'Non c\'è nulla di più soddisfacente di vedere un\'anima ricevere il suo nuovo corpo.\n\nCon il tuo aiuto, costruiremo un nuovo esercito di costrutti da combattimento per guidare Maldraxxus alla vittoria!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195615, 'itIT', 'Niente più segreti!', 'Niente più segreti!', 38134),
(195614, 'itIT', 'Fateci entrare!', 'Fateci entrare!', 38134),
(195613, 'itIT', 'Cosa ci sta nascondendo l\'Arconte???', 'Cosa ci sta nascondendo l\'Arconte???', 38134),
(195612, 'itIT', '', 'Consiglio di rimettere quelle pergamene al loro posto. Solo gli Indegni sono autorizzati alla consultazione.', 38134),
(195611, 'itIT', '', 'E salute a te, Custode dell\'Archivio.', 38134),
(195610, 'itIT', '', 'Salute, Custode del Locus.', 38134),
(195607, 'itIT', 'Facile a dirsi, ma difficile a farsi.', '', 38134),
(195606, 'itIT', '', 'Dobbiamo ottenere un\'udienza con l\'Arconte.', 38134),
(195605, 'itIT', '', 'Da questa parte.', 38134),
(195603, 'itIT', '', 'Dunque è vero... Gli Indegni sono in combutta con la Fauce!', 38134),
(195602, 'itIT', '', 'Capisco, sarà fatto.', 38134),
(195601, 'itIT', '', 'Se ne prendessimo un altro...', 38134),
(195600, 'itIT', '', 'Quando avrai preso la Purezza, faremo la nostra mossa. Insieme, realizzeremo grandi cose.', 38134),
(195599, 'itIT', '', 'C\'è un altro potere in gioco. Non lottiamo soltanto per il Bastione.', 38134),
(195598, 'itIT', '', 'La sua visione è ancora troppo ristretta, per ciò che ho da offrirti.', 38134),
(195597, 'itIT', '', 'Perché hai mandato via quel vendicativo?', 38134),
(195594, 'itIT', '', 'Presentate i ricordi archiviati all\'occhio corrispondente per poterli vedere.', 38134),
(195591, 'itIT', '', 'Ma quanti ne hai costruiti?', 38134),
(195590, 'itIT', '', 'Benvenuti all\'Archivio dell\'Arconte. Dichiarate le vostre intenzioni per i nostri registri.', 38134),
(195589, 'itIT', 'La barriera è abbassata! Andate laggiù e fate quello per cui siete venuti!', '', 38134),
(195588, 'itIT', '', 'Mortale! Mi farebbe comodo il tuo aiuto qui!', 38134),
(195587, 'itIT', 'Non hai pensato di dircelo prima? Bah, pazienza. Ci penso io.', '', 38134),
(195586, 'itIT', '', 'Ah, intendevi l\'accesso all\'Archivio INTERNO? Temo che si tratti di tutto un altro protocollo.', 38134),
(195585, 'itIT', 'Cosa? Un\'altra barriera?', '', 38134),
(195584, 'itIT', '', 'Resta concentrato. Abbiamo una missione da compiere.', 38134),
(195583, 'itIT', 'Lo ammetto, più che altro volevo entrare qui per dimostrare di poterlo fare. Molto bene.', '', 38134),
(195580, 'itIT', '', 'Posiziona le offerte davanti ai guardiani e la strada si aprirà.', 38134),
(195579, 'itIT', '', 'Ti aspetterò all\'entrata dell\'Archivio dell\'Arconte. Si trova ancora a sud rispetto alla tua attuale posizione.', 38134),
(195577, 'itIT', 'Ecco! Proteggimi, d\'accordo?', '', 38134),
(195576, 'itIT', 'Lo so. È così che le Mnemi DOVREBBERO comportarsi.', '', 38134),
(195575, 'itIT', '', 'È così gentile da parte sua.', 38134),
(195574, 'itIT', '', 'Dovresti procedere a sud ancora una volta.', 38134),
(195572, 'itIT', '', 'Ti serviranno tre Cicute Secche. \n\nTi mostrerò come realizzare dei veleni potenti se me ne porterai alcune.\n\nAssicurati che l\'orso non sia troppo vicino a te quando le raccogli, quelle cosettine fanno un sacco di rumore.', 38134),
(195571, 'itIT', '', 'Hai l\'aria di $gun Ladro:una Ladra;. C\'è un potente Ogre là fuori che ha catturato una parte della prima spedizione.\n\nProbabilmente vuole catturare anche i tuoi amici! Quella creatura è folle, ma può essere indebolita con i veleni.\n\nStavo cercando di creare un veleno per indebolirlo quando mi hanno uccisa. Se tu prendi i materiali io ti mostrerò come avvelenare le tue armi.\n\nAffare fatto?', 38134),
(195570, 'itIT', 'Sì, è vero.', '', 38134),
(195569, 'itIT', 'Non so se il destino della Guerriera della Notte si possa evitare, ma farò qualsiasi cosa per aiutarti.', '', 38134),
(195567, 'itIT', 'Qadarin! Ha funzionato, sia lodata Elune...', '', 38134),
(195565, 'itIT', 'Io... Che cosa è... successo?', '', 38134),
(195563, 'itIT', '', 'Bene! Spezziamo questa maledizione!', 38134),
(195562, 'itIT', '', 'Vecchia Gertie... spero che tu e gli altri passosetoso stiate bene. Rosa, Daisy... Gigliocardo Jr....', 38134),
(195560, 'itIT', '', 'Ho bisogno di... riflettere. Ci rivedremo al Forte.', 38134),
(195558, 'itIT', 'Quando finirà la siccità?', 'Quando finirà la siccità?', 38134),
(195557, 'itIT', 'Siamo esperti della sofferenza. Ce la faremo.', 'Siamo esperti della sofferenza. Ce la faremo.', 38134),
(195556, 'itIT', 'Sire Denathrius riporterà l\'ordine.', 'Sire Denathrius riporterà l\'ordine.', 38134),
(195555, 'itIT', 'Tutti dobbiamo fare la nostra parte.', 'Tutti dobbiamo fare la nostra parte.', 38134),
(195552, 'itIT', '', 'Per esempio: dirigiti a sud.', 38134),
(195551, 'itIT', '', 'Mi è proibito fornire la posizione delle offerte richieste. Posso solo fornire delle direzioni approssimative.', 38134),
(195548, 'itIT', 'Be\'? Ci dirai dove trovare le offerte?', '', 38134),
(195546, 'itIT', 'Il Maestro ha detto che servono sacrifici.', 'Il Maestro ha detto che servono sacrifici.', 38134),
(195542, 'itIT', '', 'Ah! Forgialita Supremo, è bello vederti.', 38134),
(195541, 'itIT', '', 'Ti prego di scusarmi mentre arresto i sensi.', 38134),
(195540, 'itIT', '', 'Sembra che si stia riscontrando un errore.', 38134),
(195539, 'itIT', '', 'Si prega di allontanarsi immediatamente dal Locus, o procederò usando la forza.', 38134),
(195537, 'itIT', 'Tieni occupata la Mnemi! La riparerò!', '', 38134),
(195536, 'itIT', '', 'Sono incline a rimuoverti dal Locus, se insisti a ficcare il naso.', 38134),
(195535, 'itIT', '', 'Mmm... Non credo che tu abbia l\'autorizzazione per vedere questo ricordo.', 38134),
(195534, 'itIT', '', 'Lysonia, c\'è dell\'altro che devo...', 38134),
(195532, 'itIT', '', 'Uther, scatena la furia degli Indegni sul Bastione.', 38134),
(195527, 'itIT', '', 'Rivendicheremo il Tempio della Purezza ed elimineremo tutti coloro che ci ostacolano.', 38134),
(195525, 'itIT', '', 'Per troppo a lungo abbiamo rinunciato alle cose che ci rendono ciò che siamo. Le cose che ci donano forza!', 38134),
(195524, 'itIT', '', 'Ascoltatemi, Indegni!', 38134),
(195522, 'itIT', '', 'Seguimi. Insieme, condurremo i Kyrian su una nuova via. Insieme, correggeremo gli errori perpetrati dall\'Arconte.', 38134),
(195521, 'itIT', '', 'Cosa vorresti che facessi?', 38134),
(195520, 'itIT', '', 'Ho provato ad avvisarli. Insistere così imprudentemente sulla via a tutti i costi... alla fine ci distruggerà.', 38134),
(195517, 'itIT', '', 'Abbastanza da ricordarne il dolore, ma non così tanto da provarlo ancora. Anche questo passerà.', 38134),
(195516, 'itIT', '', 'Ogni istante che passa sento una parte di me che scivola via. Quanto tempo fa hai dovuto sopportare tutto questo?', 38134),
(195515, 'itIT', '', 'Fidati del processo, Aspirante. Tutto diventerà chiaro col tempo.', 38134),
(195513, 'itIT', '', 'Va\' avanti. Io devo proteggere il Forgialita Supremo.', 38134),
(195512, 'itIT', 'La riprogrammerò anche se fosse L\'ULTIMA cosa che faccio.', '', 38134),
(195510, 'itIT', '', 'Una grande oscurità si è abbattuta sulla nostra sacra Arbiter, facendola piombare in un sonno eterno.', 38134),
(195509, 'itIT', 'Non dovremmo esplodere. Forse.', '', 38134),
(195508, 'itIT', '', 'Occupatene tu, hai già affrontato Lysonia prima d\'ora.', 38134),
(195506, 'itIT', 'Il frammento è pronto! Portalo agli Specchi dell\'Anima per vedere i ricordi.', '', 38134),
(195504, 'itIT', 'È abbastanza semplice raccogliere i ricordi degli Indegni. Gli altri possono andare negli archivi.', '', 38134),
(195503, 'itIT', '', 'I ricordi degli Indegni di Lysonia sono sparsi tra gli altri.', 38134),
(195501, 'itIT', 'Oh, bene, il Locus non è esploso.', '', 38134),
(195500, 'itIT', '', 'Mi assicurerò che questo piccolo passosetoso riceva le cure necessarie, per il futuro di Selvarden. Magari potrai farci visita di tanto in tanto!', 38134),
(195499, 'itIT', '', 'Indubbiamente volevi che pensassi che questo portale fosse una via di fuga, quando in verità conduce nelle profondità di questa torre maledetta. Ma non ho altra scelta che attraversarlo.', 38134),
(195498, 'itIT', '', 'Tormentarmi con una visione di un fidato alleato che è venuto ad aiutarmi a fuggire.', 38134),
(195497, 'itIT', '', 'Che cos\'è? Un altro dei tuoi giochetti, suppongo.', 38134),
(195493, 'itIT', '', 'Ti aiuterò come posso. L\'energia necrotica in questo luogo dev\'essere purificata.', 38134),
(195490, 'itIT', 'Ah! Il Locus è in funzione ancora una volta.', '', 38134),
(195489, 'itIT', 'Inseguitela!', '', 38134),
(195488, 'itIT', 'Interessante...', '', 38134),
(195487, 'itIT', '', 'Un frammento di essenza proveniente da un Indegno. Potrebbe contenere il segreto della loro origine.', 38134),
(195486, 'itIT', 'E quello cos\'è?', '', 38134),
(195485, 'itIT', '', 'Mikanikos, il Locus può estrarre i ricordi anche da questo?', 38134),
(195482, 'itIT', 'I condotti sono attivi. Fornisci loro l\'Animum che ti ho dato.', '', 38134),
(195480, 'itIT', '', 'Non smetterò di battermi finché non mi mostrerai la tua nuova abilità!', 38134),
(195478, 'itIT', '', 'Cosa? Quella carrozza sta fuggendo con l\'ultima scorta d\'Animum.', 38134),
(195475, 'itIT', '', 'Eccole lì. Dove stanno... Stanno andando tutte alla Fauce! Ben!', 38134),
(195473, 'itIT', '', 'Aspetta... Dove sono le anime? Dovrebbero riversarsi fuori da Oribos verso i loro nuovi regni...', 38134),
(195471, 'itIT', '', 'Quella era... un\'unità Mnemi?', 38134),
(195470, 'itIT', 'Non voglio parlarne, ma sì.', '', 38134),
(195469, 'itIT', '', 'Ora Ben verrà presentato all\'Arbiter, che giudicherà la sua anima e lo manderà nell\'aldilà che si merita.', 38134),
(195467, 'itIT', '', 'Oh no... Potrebbe essere già tardi.', 38134),
(195465, 'itIT', 'Ti ho CREATO IO! Io sono il Forgialita SUPREMO!', '', 38134),
(195464, 'itIT', '', 'L\'accesso al Locus è attualmente limitato.', 38134),
(195462, 'itIT', 'La pazienza supererà ogni ostacolo.', '', 38134),
(195461, 'itIT', 'Al nostro prossimo incontro.', '', 38134),
(195453, 'itIT', 'Col tempo, ho scoperto che non sono i non morti senza cervello che pensavo fossero e che l\'onore ha il suo posto a Maldraxxus. O almeno era così.', '', 38134),
(195450, 'itIT', '<Mikanikos sembra molto preso dalle rune davanti a lui.>', '', 38134),
(195449, 'itIT', 'La vera morte mi ha trovato, persino tra le ombre...', '', 38134),
(195448, 'itIT', 'Tipico dei mortali, ficcare il naso in cose che non capiscono!', '', 38134),
(195442, 'itIT', 'È il momento di insorgere, Venthyr! Rivendicheremo l\'Animum dal nostro oppressore!', '', 38134),
(195441, 'itIT', 'Mio Margravio! Sono al tuo fianco!', 'Mio Margravio! Sono al tuo fianco!', 38134),
(195440, 'itIT', 'Ma il fatto che anche loro abbiano suonato la Campana della Penitenza dovrebbe darti tutto l\'avvertimento di cui hai bisogno.', 'Ma il fatto che anche loro abbiano suonato la Campana della Penitenza dovrebbe darti tutto l\'avvertimento di cui hai bisogno.', 38134),
(195439, 'itIT', 'E potete chiedere $gal Calcafauce:alla Calcafauce; quali tormenti vi attendono in quel dominio se doveste fallire qui.', 'E potete chiedere $gal Calcafauce:alla Calcafauce; quali tormenti vi attendono in quel dominio se doveste fallire qui.', 38134),
(195438, 'itIT', 'Faremo tutto il possibile per aiutarti, ma resti a un errore di distanza da un\'eternità nella Fauce.', 'Faremo tutto il possibile per aiutarti, ma resti a un errore di distanza da un\'eternità nella Fauce.', 38134),
(195436, 'itIT', 'Non avete nessuno da incolpare se non voi stessi. I vostri errori. I vostri peccati.', 'Non avete nessuno da incolpare se non voi stessi. I vostri errori. I vostri peccati.', 38134),
(195435, 'itIT', 'Spero che voi altri siate stati motivati dalla confessione di Sandika. Questo viaggio non sarà facile per nessuno di voi.', 'Spero che voi altri siate stati motivati dalla confessione di Sandika. Questo viaggio non sarà facile per nessuno di voi.', 38134),
(195434, 'itIT', 'Grazie.', 'Grazie.', 38134),
(195433, 'itIT', 'Sandika, puoi tornare al tuo posto.', 'Sandika, puoi tornare al tuo posto.', 38134),
(195432, 'itIT', 'I Venthyr assicureranno che tu affronti, e superi, ogni peccato che pesa sulla tua anima.', 'I Venthyr assicureranno che tu affronti, e superi, ogni peccato che pesa sulla tua anima.', 38134),
(195430, 'itIT', 'È per questo che siamo qui per aiutarti, Sandika.', 'È per questo che siamo qui per aiutarti, Sandika.', 38134),
(195429, 'itIT', 'È stato... doloroso... ricordare...', 'È stato... doloroso... ricordare...', 38134),
(195426, 'itIT', 'I tuoi difetti non possono essere trattati correttamente finché non saranno stati esposti.', 'I tuoi difetti non possono essere trattati correttamente finché non saranno stati esposti.', 38134),
(195422, 'itIT', 'Sono felice di sentire la tua confessione, Sandika.', 'Sono felice di sentire la tua confessione, Sandika.', 38134),
(195421, 'itIT', '<Ignori il cadavere e guardi un premio più prestigioso, il suo grimorio!> \n\n<Descrive nel dettaglio un rituale per vincolare una potente Ombra del Vuoto al tuo servizio. Chiaramente questo Stregone ha fallito nel suo tentativo.>\n\n<Ma tu potresti aver successo dove lui ha fallito. Prendi il grimorio accanto al corpo e completa il rituale per prendere il controllo dell\'Ombra del Vuoto!>', '', 38134),
(195418, 'itIT', '', 'Lo Scopo ti ha $gmandato:mandata; da noi. Lo so.', 38134),
(195415, 'itIT', 'Non fuggirà.', '', 38134),
(195412, 'itIT', 'Eppure, ti nascondi nella tua necropoli! Metterò a tacere la tua sbruffoneria con un solo colpo!', '', 38134),
(195411, 'itIT', '', 'Ti butti a capofitto verso la tua morte! Non vedo l\'ora di farti a pezzi!', 38134),
(195409, 'itIT', 'Antolite. Non mi ha mai sconfitto nell\'arena e non ci sconfiggerà neanche qui.', '', 38134),
(195408, 'itIT', '', 'Sono onorata di aver combattuto al tuo fianco.', 38134),
(195406, 'itIT', '', 'Gharmal non sfuggirà alla nostra vendetta.\n\nSei $gpronto:pronta;?', 38134),
(195398, 'itIT', '$gUn:Una; mortale a Oribos? Lo Scopo ha portato $gun:una; mortale da noi?', '$gUn:Una; mortale a Oribos? Lo Scopo ha portato $gun:una; mortale da noi?', 38134),
(195382, 'itIT', '', 'Al tuo segnale, irromperemo nella caverna qua vicino. Sei $gpronto:pronta;?', 38134),
(195381, 'itIT', 'Restano delle importanti questioni da risolvere, Calcafauce.', '', 38134),
(195370, 'itIT', 'Ecco che arriva. Addomesticala, proprio come l\'altra.', '', 38134),
(195369, 'itIT', 'Ecco che arriva. Addomesticala, proprio come l\'altra.', '', 38134),
(195368, 'itIT', 'Un\'altra bestia addomesticata. Ora devi imparare a metterla in una stalla.', '', 38134),
(195367, 'itIT', 'Hai troppi famigli. Parla con me per metterne uno nella stalla.', '', 38134),
(195366, 'itIT', 'Fammi sapere quando vuoi addomesticare la bestia.', '', 38134),
(195365, 'itIT', 'Devi congedare il tuo famiglio attuale per addomesticarne un altro. Potrai richiamarlo più tardi.', '', 38134),
(195364, 'itIT', 'Bel lavoro! Ora devi imparare a metterli in una stalla.', '', 38134),
(195363, 'itIT', 'Hai troppi famigli. Parla con me per metterne uno nella scuderia.', '', 38134),
(195362, 'itIT', 'Fammi sapere quando vuoi addomesticare la bestia.', '', 38134),
(195358, 'itIT', 'Se cadremo, almeno ce ne andremo combattendo.', '', 38134),
(195351, 'itIT', 'Le nostre forze si sono riunite alla Campata Infranta. Ci incontreremo lì.', '', 38134),
(195340, 'itIT', 'Speriamo. I nostri aguzzini non saranno felici di sapere che sei scappata ancora una volta.', '', 38134),
(195331, 'itIT', 'Una Matriarca Gorm! Lei e la sua prole divoreranno tutto il terreno attorno alla rete. Uccidila e assicurati di far fuori tutte quelle uova!', '', 38134),
(195328, 'itIT', 'Tu chi sei, $gstraniero:straniera;?', 'Tu chi sei, $gstraniero:straniera;?', 38134),
(195327, 'itIT', 'Eminenth! Portami un\'altra anima da mietere...', '', 38134),
(195326, 'itIT', 'Date la caccia a ogni anima nascosta in queste sale!', '', 38134),
(195325, 'itIT', 'Rifiutiamo le menzogne dell\'Accusatrice e i suoi seguaci!', '', 38134),
(195324, 'itIT', 'Queste anime non possono essere redente. Mietetele tutte!', '', 38134),
(195303, 'itIT', '', 'Stavo iniziando a perdere la speranza di rivedere qualcuno del nostro mondo.', 38134),
(195301, 'itIT', '', 'Quando siete arrivati, non sapevo neanche se foste reali... o se foste solo un\'altra illusione pensata per tormentarci.', 38134),
(195299, 'itIT', '', 'Dovremmo essere abbastanza al sicuro, per ora.', 38134),
(195297, 'itIT', '', 'Qua dentro!', 38134),
(195296, 'itIT', 'Non se ti finiremo prima noi!', '', 38134),
(195295, 'itIT', 'I vostri alleati sono insignificanti. Finirete tutti in catene!', '', 38134),
(195294, 'itIT', 'La tua cella attende, Maga! Il Carceriere ha dei piani per te!', '', 38134),
(195293, 'itIT', '', 'Ne ho abbastanza delle tue celle, mostro!', 38134),
(195292, 'itIT', 'Non sfuggirete alle nostre grinfie, mortali!', '', 38134),
(195289, 'itIT', 'Ammiraglio Supremo, hai visto qualche traccia della Guerriera della Notte prima che ti trovassimo?', '', 38134),
(195287, 'itIT', 'Un altro agguato! Abbattiamoli!', '', 38134),
(195285, 'itIT', 'Cosa intendeva Thrall con \"scappata ancora\"? È già successo?', '', 38134),
(195284, 'itIT', '', 'Fortuna che non dovevamo farci notare... Sbrighiamoci.', 38134),
(195282, 'itIT', 'Cavalieri, proteggete la caverna! Respingeremo questi mostri.', '', 38134),
(195279, 'itIT', '', 'C\'è una caverna dietro l\'ansa. Con un po\' di fortuna, la raggiungeremo prima che ci notino.', 38134),
(195278, 'itIT', '', 'L\'illusione non reggerà a lungo. Presto!', 38134),
(195277, 'itIT', '', 'Ho lasciato alcune immagini speculari per distrarli un po\'.', 38134),
(195276, 'itIT', '', 'Ci hanno trovati! Preparatevi!', 38134),
(195275, 'itIT', 'Qualcosa vola sulle nostre teste!', '', 38134),
(195274, 'itIT', 'Grazie per avermi aiutato, Jaina.', '', 38134),
(195273, 'itIT', 'Ce la farò...', '', 38134),
(195272, 'itIT', '', 'Non possiamo restare qui. Riesci a camminare, Thrall?', 38134),
(195270, 'itIT', 'Teniamo gli occhi aperti in cerca di indizi che portino dai nostri alleati.', '', 38134),
(195269, 'itIT', 'Siamo in netta inferiorità numerica. Cerchiamo di non attirare l\'attenzione.', '', 38134),
(195266, 'itIT', 'Muoviamoci, prima che arrivino i rinforzi.', '', 38134),
(195265, 'itIT', 'Abbiamo fatto il possibile qui.', '', 38134),
(195264, 'itIT', 'Ci riuniremo più avanti. Va\'!', '', 38134),
(195263, 'itIT', 'Non c\'è... via di fuga...', '', 38134),
(195262, 'itIT', 'Muori in catene, mortale!', '', 38134),
(195259, 'itIT', '', 'Ti aspetterò dove si trova Renathal.', 38134),
(195256, 'itIT', 'Stiamo stabilendo una testa di ponte. Va\', aiutaci a mantenerla.', '', 38134),
(195255, 'itIT', 'Torna dai tuoi compagni cavalieri. Ti seguiremo a breve.', '', 38134),
(195254, 'itIT', 'Fatti strada fuori di qui, ci rivedremo più avanti.', '', 38134),
(195253, 'itIT', '', 'Patetica. A Maldaraxxus sarebbe a malapena soldato semplice!', 38134),
(195252, 'itIT', '', 'È giunta l\'ora di pagare!', 38134),
(195251, 'itIT', 'Sarai felice di sapere che alcune nuove anime si sono unite alla nostra causa.', '', 38134),
(195222, 'itIT', '', 'Ci sono Indegni ovunque. Sta\' in guardia.', 38134),
(195220, 'itIT', 'Fermi! Avverto una protezione nelle vicinanze.', '', 38134),
(195217, 'itIT', '', 'Sei in prossimità della tua destinazione.', 38134),
(195205, 'itIT', '', 'Indicatemi i bersagli, Maldraxxi. Li castigherò rapidamente!', 38134),
(195204, 'itIT', 'Sarà divertente. Proprio come i vecchi tempi. Giusto, $p?', '', 38134),
(195203, 'itIT', 'La forza non mi preoccupa. Mi servono le tue nozioni strategiche.', '', 38134),
(195202, 'itIT', '', 'Il Tributiere non è un nemico qualunque. Ci servirà una grande forza per rovesciarlo.', 38134),
(195201, 'itIT', 'Cerco di non giudicare coloro che servono il Maestro.\n\nSono stato mandato a Revendreth perché non ho percepito la verità che avevo davanti a me.\n\nNon è mai troppo tardi per fare la scelta giusta.', '', 38134),
(195196, 'itIT', 'L\'oscurità mi circonda!', '', 38134),
(195195, 'itIT', 'L\'Archivista Fane spiegherà i dettagli.', '', 38134),
(195194, 'itIT', 'Ricostruirò Temel una volta che avrai recuperato tutti i suoi pezzi.', '', 38134),
(195193, 'itIT', '', 'Sembri discretamente capace!', 38134),
(195173, 'itIT', '', 'Ciao, $n!\n\nC\'è bisogno di te alla Corona di Ghiaccio. Senza il tuo aiuto, il destino di Azeroth è condannato!', 38134),
(195172, 'itIT', '', 'Qualunque cosa fai, non metterti in mezzo a un Pandaren e la sua birra. Altrimenti saranno dolori!', 38134),
(195166, 'itIT', 'Invocheremo uno dei condotti delle anime per usarlo come passaggio verso il Bastione. Queste vie sono inutilizzate da tempo. Il tuo arrivo sarà... inaspettato.', '', 38134),
(195165, 'itIT', 'Usa l\'arpa per calmare i passosetoso, così ti daranno della lana. Non ti daranno ascolto se saranno spaventati!', '', 38134),
(195164, 'itIT', 'Il poco Animum che ci resta è prezioso, ma l\'Onorevole Voce ha benedetto la tua missione, quindi useremo quello che abbiamo.', '', 38134),
(195163, 'itIT', 'Vieni, mortale. Ti mostrerò la strada.', '', 38134),
(195162, 'itIT', '', 'Fa\' il tuo dovere, Kah-Sher. La sua Voce ha parlato.', 38134),
(195161, 'itIT', 'Ma, Onorevole Voce, abbiamo così poco Animum nelle riserve! Dovremmo essere più...', '', 38134),
(195160, 'itIT', '', 'Mortale, il Sovrintendente Kah-Sher ti porterà all\'Anello del Trasferimento. Usa i nostri bacini d\'Animum per aprire un passaggio verso il Bastione.', 38134),
(195159, 'itIT', '', 'È vero, ma sembra che sia l\'unico indizio che possa essere seguito. Tocca a noi aprire loro il passaggio.', 38134),
(195158, 'itIT', '', 'I Kyrian alati del Bastione traghettano le anime dal regno mortale alle Terretetre, ma non in catene. Essi sono nobili, incontaminati dalla Fauce.', 38134),
(195156, 'itIT', 'Sylvanas Ventolesto ha mandato i suoi agenti a rapirli. Creature oscure e alate che li hanno messi in catene e trascinati verso il cielo spezzato.', '', 38134),
(195155, 'itIT', '', 'Dimmi, come hanno fatto questi alleati del tuo mondo a finire intrappolati nella Fauce?', 38134),
(195154, 'itIT', '', 'Se l\'influenza della Fauce giungerà nel tuo regno, l\'equilibrio tra la Vita e la Morte verrà spezzato. Tutto ciò che ami verrà consumato dall\'oscurità.', 38134),
(195153, 'itIT', '...e quando ha spezzato l\'elmo, il cielo sopra la Rocca si è infranto. La spira di un\'immensa torre è apparsa oltre il velo.', '', 38134),
(195151, 'itIT', '', 'Se un Orco spaventoso ti offrisse qualcosa da bere, dovresti dire di no.', 38134),
(195150, 'itIT', 'Credo di star iniziando a capire.\n\nMa noi siamo così pochi... Come riusciremo ad affrontarli?\n\n<Gresit respira profondamente e alza il mento.>\n\nFaremo ciò che è giusto. Ristabiliremo l\'ordine a Revendreth.', '', 38134),
(195148, 'itIT', 'Credevo che la morte fosse la fine dell\'esistenza. Qui invece, sembra che sia un nuovo inizio.', '', 38134),
(195147, 'itIT', 'Ho aperto diversi portali nella mia vita, ma mai uno che collegasse due piani della realtà.', '', 38134),
(195141, 'itIT', 'Come volete. Indicami dove i nostri accoliti dovrebbero aprire i portali.', '', 38134),
(195140, 'itIT', '', 'Su, su, Kah-Sher, sii ospitale. Mortale, ti suggerisco di stabilire i portali nel tuo punto d\'arrivo... appena fuori dalla città.', 38134),
(195139, 'itIT', 'Desideri aprire una porta per il regno dei vivi? Oh, cielo. Oribos è già abbastanza affollata con tutti gli Alienatori che arrivano ogni giorno.', '', 38134),
(195134, 'itIT', '', 'Torna ai livelli minori. I sovrintendenti ti prepareranno per ciò che ti aspetta.', 38134),
(195133, 'itIT', '', 'Si dice che sia colmo di rancore verso l\'Arbiter. Se riuscirà ad abbatterla, sarà la fine di tutto quello che i Primi hanno creato.', 38134),
(195132, 'itIT', '', 'Con così tante anime ad alimentare il Carceriere, temo possa diventare abbastanza forte da evadere dalla Fauce.', 38134),
(195131, 'itIT', '', 'Senza l\'Animum, la siccità si è impadronita delle Terretetre. I regni si sono isolati. La paura e l\'incertezza ci tormentano.', 38134),
(195130, 'itIT', '', 'Le anime ricche d\'Animum viaggiano solo verso la Fauce. Abbiamo provato a fermarle, a svegliare la nostra amata Arbiter... senza risultati.', 38134),
(195129, 'itIT', '', 'Le sole parole non possono descrivere una tale gloria. Fatti avanti affinché possa mostrarti.', 38134),
(195128, 'itIT', '', 'Con il tuo arrivo a Oribos, ho visto l\'Arbiter muoversi per la prima volta da innumerevoli cicli. Mi ha dato speranza che si risvegliasse e proseguisse il suo Scopo.', 38134),
(195127, 'itIT', '', 'Essa è immutata. Silente. Immobile.', 38134),
(195126, 'itIT', '', 'Prego che l\'Arbiter si risvegli in tua presenza, mortale.', 38134),
(195125, 'itIT', '', 'Sia benedetta la sua Voce.', 38134),
(195124, 'itIT', 'Sia benedetta la sua Voce.', '', 38134),
(195123, 'itIT', '', 'È compito dell\'Arbiter giudicare le anime, Kah-Sher, non nostro. Mi occuperò che appaia dinanzi a lei. La sua Voce ha parlato.', 38134),
(195122, 'itIT', 'Ritieni che meriti un\'udienza? Potrebbe essere in combutta col Carceriere!', '', 38134),
(195121, 'itIT', '', 'La presenza di tale mortale ancora in vita serve allo Scopo. Se l\'Arbiter ha reagito al suo arrivo, forse è la chiave del suo risveglio.', 38134),
(195120, 'itIT', '', 'Splendide notizie, Onorevole Voce! Pochi minuti fa, c\'è stato un disturbo al confine con la Fauce. Poi ha fatto la sua comparsa quest\'essere mortale, raccontando della sua fuga.', 38134),
(195116, 'itIT', 'Sovrintendenti! Quest\'essere è comparso al limitare della città. Crediamo che sia... un mortale in vita.', '', 38134),
(195115, 'itIT', 'Un brutto presagio, senza dubbio. Che vorrà dire?', '', 38134),
(195113, 'itIT', '', 'I rapporti dei nostri veggenti parlano chiaro. La Fauce ha tremato per un istante, come in risposta a un cambiamento.', 38134),
(195111, 'itIT', 'Di\' quello che ti pare sui Reietti, ma almeno non se ne vanno in giro a resuscitare draghi morti.\n\nBe\', perlomeno non lo fa la maggior parte di noi.', '', 38134),
(195108, 'itIT', '', 'Vieni con noi, viandante.', 38134),
(195107, 'itIT', 'Saranno i sovrintendenti a decidere cosa farne.', '', 38134),
(195106, 'itIT', '', 'Aspetta. Il potere della Fauce persiste in loro, però sento... No, non è possibile!', 38134),
(195105, 'itIT', 'Un agente della Fauce sta attaccando Oribos! Difendete l\'Arbiter!', '', 38134),
(195102, 'itIT', 'Dai l\'ordine e mi metterò all\'opera.', '', 38134),
(195101, 'itIT', 'Ciao, tesoro!\n\nHo trovato delle merendine per te!', '', 38134),
(195057, 'itIT', '', 'Dicchiupo è tutto ciò che mi rimane. Fai tutto il necessario per salvarlo.', 38134),
(195056, 'itIT', 'Non è ironico? Le Arpie stavano GIUSTO discutendo sull\'usare me come esca per catturare altri membri dell\'Orda.\n\nE guarda caso tu arrivi proprio adesso! Tra l\'altro, ti attaccheranno da un momento all\'altro.', '', 38134),
(195048, 'itIT', '', 'La tua forma è debole e miserabile.', 38134),
(195047, 'itIT', '', 'Preparati alla sconfitta!', 38134),
(195046, 'itIT', '', 'In guardia!', 38134),
(195040, 'itIT', '', 'I nostri sacrifici non saranno vani!', 38134),
(195039, 'itIT', '', 'Ma c\'è un momento in cui bisogna lottare per ciò a cui teniamo di più.', 38134),
(195038, 'itIT', 'I Silfi della Notte sembrano scoraggiati.', 'I Silfi della Notte sembrano scoraggiati.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195037, 'itIT', '', 'Ara\'lon era leale, comprendeva le scelte difficili e il bisogno di sacrificarsi.', 38134),
(195035, 'itIT', 'Quali sono i tuoi ordini?', '', 38134),
(195033, 'itIT', 'Il nemico sta marciando sul Bosco del Risveglio, arruolando tra i propri ranghi i tuoi stessi sudditi.', '', 38134),
(195031, 'itIT', '', 'Mia Regina... i Drust hanno soggiogato il nobile Ara\'lon al loro volere e ci hanno ingannato. È morto...', 38134),
(195029, 'itIT', '', 'Il sonno eterno è ciò che aspetta questi vili demoni!', 38134),
(195028, 'itIT', 'Ah! Guarda come cadono!', '', 38134),
(195025, 'itIT', '', '<Gli occhi della Regina dell\'Inverno si infiammano di potere trattenuto.>\n\nDimmi tutto ciò che sai.', 38134),
(195024, 'itIT', '', '<Gli occhi della Regina brillano di furia.>\n\nQueste creature saranno sterminate.', 38134),
(195023, 'itIT', '', 'Ben, questo è solo l\'inizio. Non vedo l\'ora di scoprire in quale regno sarai mandato. Il tuo altruismo e il tuo coraggio saranno ricompensati!', 38134),
(195022, 'itIT', '', 'Il Corno dello Stormo del Velo chiamerà gli Ascesi per portarti a Oribos. Là verrà giudicato.', 38134),
(195021, 'itIT', '', 'Una morte così crudele per un uomo così semplice... Il suo coraggio non conosce limiti. Ti sei meritato la pace nell\'aldilà, Ben.', 38134),
(195020, 'itIT', '', 'Il Flagello? No... questa gente non sopravvivrà!', 38134),
(195009, 'itIT', 'Che succede?', 'Che succede?', 38134),
(195007, 'itIT', '', 'Sembra che sia adorato non solo dalla sua famiglia, ma dall\'intero borgo. Ha condotto una vita semplice... cosa l\'ha portato da noi?', 38134),
(194998, 'itIT', 'Proteggerò... la mia famiglia.', '', 38134),
(194996, 'itIT', '', 'Ehilà, $n! In qualsiasi posto tu voglia andare, io ti ci posso portare!\n\nIl tempo funziona in modi misteriosi, ma sembra che tu non sia estraneo ai misteri.', 38134),
(194995, 'itIT', 'Se è sangue del tuo sangue, Draka, non ho dubbi a riguardo.', '', 38134),
(194994, 'itIT', '', 'Devi esserne fiero. Mio figlio era appena nato quando i traditori mi uccisero. Ho combattuto fino all\'ultimo respiro per salvarlo, ma... non so se sia sopravvissuto.', 38134),
(194993, 'itIT', 'L\'ho rivisto di recente, nella Fauce. Anch\'egli è risorto come Cavaliere della Morte. Combatte per difendere il nostro mondo.', '', 38134),
(194992, 'itIT', 'Sì, due. Uno mi ha dato in pasto al Flagello, l\'altro si è sacrificato per liberarmi.', '', 38134),
(194991, 'itIT', '', 'Hai figli, Alexandros?', 38134),
(194989, 'itIT', 'Che la vittoria sia con te!', '', 38134),
(194987, 'itIT', 'Non ho idea di cosa accadrà provando a estrarre i ricordi da frammenti del genere.\n\nRicorda: cerca i ricordi degli Indegni. Prendi tutto ciò che è viola e ha le ali, o forse che è un Centurione...\n\nOnestamente non so cosa stiamo cercando, ma se è là, l\'estrattore lo tirerà fuori!\n\nDammi il via e attiverò l\'estrattore.', '', 38134),
(194986, 'itIT', 'Guarda cos\'hanno fatto ai miei Centurioni! Al Locus!\n\n<Mikanikos gesticola con fare arrabbiato verso, be\', qualunque cosa.>', '', 38134),
(194985, 'itIT', '', 'Allora forse lo scopo della tua vita continuerà a esistere.', 38134),
(194984, 'itIT', 'Fu il Margravio Krexus a mostrarmi che il nostro esercito possiede grande onore e gloria, ma le azioni dei nostri nemici sembrano quelle del Flagello.', '', 38134),
(194983, 'itIT', 'Uccidere i Non Morti era lo scopo della mia vita. Essere circondato da loro nella morte... È stato come trovarsi nel bel mezzo di un incubo.', '', 38134),
(194982, 'itIT', '', 'Mograine. Dati i tuoi trascorsi con i non morti, Maldraxxus deve sembrarti... ripugnante.', 38134),
(194979, 'itIT', 'Ben trovato, mortale.', 'Ben trovata, mortale.', 38134),
(194978, 'itIT', '$GBenvenuto:Benvenuta;, $p.', '$GBenvenuto:Benvenuta;, $p.', 38134),
(194977, 'itIT', '', 'Non trattenerti nel mostrare il tuo immenso potere al Flagello!', 38134),
(194975, 'itIT', '', 'Eletta! Di qua!', 38134),
(194974, 'itIT', 'Combattete fino alla fine! Non spariremo nelle tenebre senza lottare!', '', 38134),
(194973, 'itIT', '', 'Combattete bene, fratelli e sorelle. Il Margravio pagherà!', 38134),
(194972, 'itIT', '', 'I mastodonti del Margravio attaccano la posizione sottostante di Mograine. Non possono avanzare!', 38134),
(194957, 'itIT', 'Credo di potercela fare!', 'Credo di potercela fare!', 38134),
(194956, 'itIT', 'Ho visto Xandria volare al tempio. Potrebbe avere bisogno del tuo aiuto.', 'Ho visto Xandria volare al tempio. Potrebbe aver bisogno del tuo aiuto.', 38134),
(194955, 'itIT', 'Sia ringraziata l\'Arconte per il tuo arrivo.', 'Sia ringraziata l\'Arconte per il tuo arrivo.', 38134),
(194954, 'itIT', 'Di qua! Presto!', 'Di qua! Presto!', 38134),
(194953, 'itIT', 'Salva... gli altri...', 'Salva... gli altri...', 38134),
(194942, 'itIT', 'Abbiamo tutti i cadaveri che ci servono, Cucicarne. Preparatevi a colpire il prossimo tempio.', '', 38134),
(194941, 'itIT', 'Sembra che i pezzi per la mia nuova creazione siano finalmente arrivati... Ci vorrà un attimo.', '', 38134),
(194940, 'itIT', '', 'Io guiderò le forze da qui mentre i miei discepoli ti porteranno alla necropoli. Mettiamo fine a quest\'atrocità.', 38134),
(194939, 'itIT', '', 'Il tuo arrivo è stato provvidenziale, ma la battaglia infuria. Non potremo riposare finché ci saranno invasori in cielo.', 38134),
(194938, 'itIT', 'Sciocca Eletta. Non c\'è niente che tu possa fare per fermarci!', '', 38134),
(194937, 'itIT', '', 'Sarai consegnato alla giustizia!', 38134),
(194936, 'itIT', '', 'Maledetto mostro!', 38134),
(194935, 'itIT', 'Il tuo tempio è distrutto, la tua gente massacrata. Hai già perso!', '', 38134),
(194921, 'itIT', 'Hai così fretta di condividere il destino del tuo sottoposto? Il tuo decantato \"coraggio\" sarà la tua rovina!', '', 38134),
(194916, 'itIT', 'Cucicarne non sarà contento di sapere che avete distrutto la sua creazione migliore. Anche se accetterà i vostri arti come risarcimento.', '', 38134),
(194915, 'itIT', 'Questa può essere più di una semplice Corte della Sutura, può essere la nostra nuova casa.', '', 38134),
(194913, 'itIT', 'Doverci occupare di questi Verrospino è un insulto. Presto affronteranno tutta la potenza dell\'orda.', '', 38134),
(194912, 'itIT', 'SPACCHIAMO QUELLO GROSSO, AHAH!', '', 38134),
(194910, 'itIT', '', 'Ero una nobile prima di arrivare qui. L\'inchino va benissimo.', 38134),
(194908, 'itIT', '', 'La luce risplende... persino qui!', 38134),
(194907, 'itIT', 'Controlleremo tutto noi!', 'Controlleremo tutto noi!', 38134),
(194906, 'itIT', 'Gli anelli ci riveleranno i loro segreti!', 'Gli anelli ci riveleranno i loro segreti!', 38134),
(194905, 'itIT', 'Troviamo gli oggetti luccicanti!', '', 38134),
(194902, 'itIT', 'Sta\' in guardia dentro la Fauce, o la tua anima si perderà per sempre.', '', 38134),
(194901, 'itIT', 'Le Terretetre non erano destinate a occhi mortali.\n\nNon fidarti delle tue percezioni e fai attenzione.\n\nFai sempre attenzione.', '', 38134),
(194897, 'itIT', 'Fai Accordino, uh... veloce come una Gru Saltatrice!\n\nSei... sette... no, due braccia e pollici forti per trasportare cose.', '', 38134),
(194895, 'itIT', 'Accordino non vede l\'ora di avere nuovamente un corpo!', '', 38134),
(194883, 'itIT', 'Il Flagello. Dobbiamo andare al Municipio.', 'Il Flagello. Dobbiamo andare al Municipio.', 38134),
(194880, 'itIT', 'Devi solo dare l\'ordine, Ben.', 'Devi solo dare l\'ordine, Ben.', 38134),
(194875, 'itIT', 'Ho sentito delle voci sul Flagello... È il momento?', 'Ho sentito delle voci sul Flagello... È il momento?', 38134),
(194874, 'itIT', 'È la fine del mondo!', 'È la fine del mondo!', 38134),
(194873, 'itIT', 'Che faccio? Dove mi nascondo?', 'Che faccio? Dove mi nascondo?', 38134),
(194872, 'itIT', 'Ben! Che sta succedendo?', 'Ben! Che sta succedendo?', 38134),
(194856, 'itIT', 'Il Municipio è la nostra miglior speranza di sopravvivenza... Comincerò con i preparativi.', '', 38134),
(194845, 'itIT', 'L\'Accusatrice saprà cosa fare... lo sa sempre...', '', 38134),
(194844, 'itIT', '', 'La mia pazienza si sta esaurendo!', 38134),
(194837, 'itIT', '', 'Ti ringrazio con tutto il cuore, Z\'rali. Sei uno splendido esemplare.', 38134),
(194836, 'itIT', '', 'Ah, allora lo senti il dolore. Intrigante.', 38134),
(194835, 'itIT', 'Gli invasori finalmente hanno messo piede sul mio palco. Iniziamo!', '', 38134),
(194833, 'itIT', '', 'Sia ringraziata la Luce! Ci hai trovato!', 38134),
(194832, 'itIT', 'Sembra che abbiamo trovato l\'Ammiraglio Supremo. Farò segno alla Spada d\'Ebano di avanzare.', '', 38134),
(194831, 'itIT', 'Di là! È Dama Marefiero!', '', 38134),
(194830, 'itIT', 'Continuiamo a cercare.', '', 38134),
(194829, 'itIT', 'Mi dispiace, Solomon, non posso. La mia vita è ormai è tra la fattoria e i boschi.', '', 38134),
(194828, 'itIT', 'Se uno dei nostri alleati fosse intrappolato lì, dubito che riusciremmo a tirarlo fuori.', '', 38134),
(194827, 'itIT', 'Muoviamoci.', '', 38134),
(194826, 'itIT', 'Qualunque cosa stia succedendo qui, non vogliamo farne parte.', '', 38134),
(194825, 'itIT', 'Ah, Ben! Stavamo giusto parlando di riformare la milizia cittadina. Ti interesserebbe esserne di nuovo al comando?', '', 38134),
(194823, 'itIT', 'Cerchiamo di non farci mangiare!', '', 38134),
(194822, 'itIT', 'Spero che i nostri alleati non siano finiti nel ventre di quella... cosa.', '', 38134),
(194821, 'itIT', 'Meglio non ingaggiare quel mastodonte. Dubito che le nostre armi possano penetrare quella corazza.', '', 38134),
(194814, 'itIT', '', 'La tua razza è davvero affascinante... con così tanti segreti da svelare...', 38134),
(194799, 'itIT', '', 'Lok\'tar! Ora tutta Maldraxxus sa che non può prenderti alla leggera.', 38134),
(194795, 'itIT', '', 'Cosa sono quelle mostruosità?!? Sia ringraziata l\'Arconte che non sono ancora attive...', 38134),
(194794, 'itIT', '', 'Verso i cieli, Ascesi! Per il Bastione!', 38134),
(194773, 'itIT', '', 'Sono certa che il piano funzionerà, purché tu riesca a trovare i pezzi che mi servono.', 38134),
(194772, 'itIT', '', 'Matyas non userà mai più la mia Pietra del Peccato contro di me.', 38134),
(194769, 'itIT', 'Torna presto, Zayhad! La Regina sa che la buona compagnia è difficile da trovare.', '', 38134),
(194768, 'itIT', '', 'Il suolo fertile dev\'essere coltivato. Cresci bene, Marasmio.', 38134),
(194767, 'itIT', 'Umpf. Be\', mi piace come hai sistemato questo posto. Sembra anche che la Corte operi con diligenza, come al solito.', '', 38134),
(194766, 'itIT', '', 'I loro sforzi ci nutrono tutti. Il Cuore della Foresta prospera al tuo cospetto.', 38134),
(194765, 'itIT', 'È così che la pensi? Finora, soltanto questo germoglio mi ha mostrato il dovuto rispetto.', '', 38134),
(194764, 'itIT', '', 'Allora forse gli anelli dei Silfi ci accoglieranno ancora.', 38134),
(194763, 'itIT', 'Zayhad, vecchia corteccia! Il vento non c\'entra niente, questo giovane germoglio mi ha portato qui.', '', 38134),
(194762, 'itIT', '', 'Marasmio, qual buon vento ha portato il tuo ritorno?', 38134),
(194760, 'itIT', '', 'Salve, $gpiccolino:piccolina;. Che cos\'hai lì?', 38134),
(194759, 'itIT', '', 'Il germoglio diventa tronco. Il tronco mantiene il cielo. Il cielo contiene i germogli.', 38134),
(194758, 'itIT', '', 'Tutto a suo tempo.', 38134),
(194757, 'itIT', '', 'Ciò che ti circonda è l\'esito di infinite vite passate.$b$bStupendo, vero?', 38134),
(194749, 'itIT', 'Ah! Ti serva di lezione! Ora, fuori dalla mia foresta!', '', 38134),
(194748, 'itIT', 'Impara a portare rispetto!', '', 38134),
(194747, 'itIT', 'Così imparano!', '', 38134),
(194746, 'itIT', 'Ti voglio bene anch\'io.', '', 38134),
(194744, 'itIT', 'Questo mi ricorda la volta in cui sono entrato nello specchio di uno spogliatoio che apparteneva a...', '', 38134),
(194743, 'itIT', 'Mi fa piacere vederti, Sibilla. Credo che ti debba uno specchio.', 'Mi fa piacere vederti, Sibilla. Credo che ti debba uno specchio.', 38134),
(194742, 'itIT', 'Ti sbagli, mio caro Tubbins. Dama Sibilla arriva precisamente quando intende farlo.', '', 38134),
(194741, 'itIT', '', 'Simili e compari, alquanto necessari.', 38134),
(194740, 'itIT', 'Sì! Ci vorrà solo un attimo.', '', 38134),
(194735, 'itIT', '', 'L\'assenza d\'intelligenza, che decadenza.', 38134),
(194734, 'itIT', 'Ah, i Kyrian mandano ancora i loro leali seguaci. Siete qui per farvi massacrare come gli altri?', '', 38134),
(194732, 'itIT', 'Dimmi quando vuoi iniziare, giovane. È sempre un po\'... spiazzante la prima volta!', '', 38134),
(194731, 'itIT', '', 'Amici da difesa, non è un\'offesa.', 38134),
(194730, 'itIT', 'Ah! Quei farabutti hanno avuto quello che meritavano! Formiamo una bella squadra, noi tre.', '', 38134),
(194729, 'itIT', 'Con piacere, amore mio.', '', 38134),
(194728, 'itIT', '', 'Appena hai finito, sbrigati a tornare! Ho una consegna di legna per il Magistrato.', 38134),
(194727, 'itIT', '', 'Oggi otterremo giustizia per i nostri fratelli caduti e porremo fine a questa opera malvagia una volta per tutte!', 38134),
(194726, 'itIT', '', 'Spalanca, arraffa, scappa!', 38134),
(194725, 'itIT', '', 'I mezzi per il parasole!', 38134),
(194711, 'itIT', 'All\'ombra starò, sennò impazzirò...', '', 38134),
(194709, 'itIT', 'Furto effettuato, sole negato.', '', 38134),
(194708, 'itIT', 'Ti porterò delle assi più tardi. Gratis, ovviamente!', '', 38134),
(194707, 'itIT', 'È peggio di così, Ben. Una roccia ha fatto un buco nel legno!', '', 38134),
(194706, 'itIT', 'Vernon! Un\'altra falla nella barca?', '', 38134),
(194677, 'itIT', '', 'Recluta, occhi aperti! Siamo quasi arrivati all\'isola.', 38134),
(194676, 'itIT', 'Da servire caldo! Il tè freddo è terribile!', '', 38134),
(194672, 'itIT', 'È stata un\'impresa remunerativa, $p. Spero di lavorare ancora insieme in futuro.', '', 38134),
(194671, 'itIT', 'No, no, così non va, devo averli tutti! Potrei non avere più un\'opportunità come questa.', '', 38134),
(194670, 'itIT', 'Non riesco a esprimere quanto ti sia grato per avermi aiutato ad acquisire tutti questi volumi aggiuntivi.', '', 38134),
(194669, 'itIT', 'Non importa, noi siamo infiniti. Ti ucciderò personalmente.', '', 38134),
(194668, 'itIT', 'Marasmio! Ci sta aiutando!', '', 38134),
(194666, 'itIT', 'Uccideteli.', '', 38134),
(194665, 'itIT', 'Idiota, ci hai condotti dritti al nostro obiettivo.', '', 38134),
(194664, 'itIT', '', 'Col tempo, capirai. Questi istanti di pace sono necessari per capire quest\'anima.', 38134),
(194663, 'itIT', '', 'Incredibile, ma... non c\'è niente che non va. Stiamo davvero assistendo ai suoi ultimi istanti di vita?', 38134),
(194661, 'itIT', 'Sei ancora qui? Molto bene, i saprofagi si nutriranno della tua essenza.', '', 38134),
(194657, 'itIT', '', 'Sono certa che tu e Ysera abbiate molto di cui parlare.', 38134),
(194637, 'itIT', '', 'Ora capisci, Alexandros?', 38134),
(194636, 'itIT', 'Un\'altra guerra? Dev\'essere dura.', '', 38134),
(194632, 'itIT', 'Ah, la fragranza mi ricorda i molti anni passati nella Foresta della Rovina...', 'Ah, la fragranza mi ricorda i molti anni passati nella Foresta della Rovina...', 38134),
(194631, 'itIT', 'Tubbins e Teotar ce l\'hanno fatta ancora! Dei sapori così delicati e inaspettati...', 'Tubbins e Teotar ce l\'hanno fatta ancora! Dei sapori così delicati e inaspettati...', 38134),
(194630, 'itIT', 'Apprezzo l\'opportunità di rallentare e godermi il momento.', 'Apprezzo l\'opportunità di rallentare e godermi il momento.', 38134),
(194629, 'itIT', 'Io vi condanno a bruciare!', '', 38134),
(194625, 'itIT', 'La mia... ultima... condanna...', '', 38134),
(194624, 'itIT', 'Questi traditori devono essere puniti!', '', 38134),
(194618, 'itIT', '', 'I portali sono aperti! Anche se varchiamo gli stessi, ogni coppia raggiungerà un mondo differente.', 38134),
(194617, 'itIT', '', 'Si inizia.', 38134),
(194616, 'itIT', '', 'Viviamo al servizio non solo di noi stessi, ma per le molte anime dell\'universo che cercano il nostro aiuto.', 38134),
(194615, 'itIT', '', 'Teniamo fede alle nostre usanze, imparando e migliorando noi stessi nelle nostre vocazioni.', 38134),
(194614, 'itIT', '', 'Resistiamo di fronte a ogni cosa. Nessun ostacolo o tragedia può far vacillare la nostra tempra.', 38134),
(194613, 'itIT', 'Ben fatto, mio Vincolo dell\'Anima. Siamo sempre più vicini alla nostra ascensione.', '', 38134),
(194612, 'itIT', '', 'Ho trovato la pergamena del rituale. Grazie a queste parole potremo aprire i cancelli.', 38134),
(194611, 'itIT', 'La lealtà all\'Arconte merita di essere punita!', '', 38134),
(194610, 'itIT', 'Conosci la verità, Aspirante.', '', 38134),
(194609, 'itIT', 'Dimostra la tua fedeltà!', '', 38134),
(194608, 'itIT', 'Va\' avanti, Calcafauce. Io... mi prenderò un istante per riflettere.', '', 38134),
(194607, 'itIT', 'Ora capisco. L\'onore avrà anche abbandonato Maldraxxus... Ma insieme, ricostruiremo ciò che è andato perduto!', '', 38134),
(194606, 'itIT', '', 'L\'Arbiter ha visto che i tuoi ricordi non sono un fardello, ma la tua fonte di forza. È per questo che appartieni a Maldraxxus.', 38134),
(194605, 'itIT', 'Io... credo di capire ora. Ritorniamo dall\'Eletta.', '', 38134),
(194604, 'itIT', 'Mio amato figlio... sono così orgoglioso di te... e questo nulla me lo porterà mai via.', '', 38134),
(194603, 'itIT', 'Padre...?', '', 38134),
(194602, 'itIT', 'Non dimenticherò mai il tuo sacrificio... Mai!', '', 38134),
(194601, 'itIT', 'Darion... sei morto per liberarmi dal Flagello... e ancora ti ergi a difesa di Azeroth!', '', 38134),
(194600, 'itIT', 'Ti prego... Procediamo.', '', 38134),
(194599, 'itIT', '', 'Questa forgia sarà anche antica, ma le lame che forgiamo sono affilate come non mai. Ben fatto.', 38134),
(194598, 'itIT', 'Il suo tradimento mi perseguita, persino nella morte. Mio figlio... Devo forse dimenticare questo dolore?', '', 38134),
(194597, 'itIT', 'Renault. Il mio primogenito... La mia fine.', '', 38134),
(194596, 'itIT', 'Perdonami. Proseguiamo.', '', 38134),
(194595, 'itIT', 'È radiosa come l\'ultima volta che l\'ho abbracciata... E pensare che verrebbe dimenticata se io...', '', 38134),
(194594, 'itIT', 'È forse... Elena? Il mio amore... la mia luce...', '', 38134),
(194593, 'itIT', 'La Luce è sempre stata il mio coraggio, ma... vedrò cosa rivelano queste pergamene.', '', 38134),
(194592, 'itIT', '', 'Medita su queste pergamene, Alexandros. Rifletti sull\'origine del tuo coraggio, sul perché siamo diversi.', 38134),
(194591, 'itIT', '', 'La via che percorri conduce solo alla rovina! Plasmerò un nuovo destino per la nostra gente.', 38134),
(194590, 'itIT', 'Quello è fuori dal nostro controllo! Finché seguiremo la via potremo risolvere tutto insieme!', '', 38134),
(194589, 'itIT', '', 'E a cosa ci ha portato? Aspiranti incapaci di ascendere, Centurioni che marciscono nei camp!', 38134),
(194588, 'itIT', '', 'Siamo diventati più del nostro passato! Non riesci a vederlo?', 38134),
(194587, 'itIT', '', 'Tutti i ricordi cancellati e le vite dimenticate... avranno finalmente giustizia!', 38134),
(194586, 'itIT', '', 'Parleremo della tua sorte a tempo debito. Fino ad allora, resterai qui sotto sorveglianza.', 38134),
(194585, 'itIT', '', 'Concessa, per ora.', 38134),
(194584, 'itIT', 'Mia Arconte, ti chiedo pietà. Io... non comprendevo...', '', 38134),
(194583, 'itIT', '', 'Quanto a te, Uther...', 38134),
(194581, 'itIT', 'Il campione di Lysonia combatte contro di lei!', '', 38134),
(194580, 'itIT', 'Hai perso il senno, Lysonia?', '', 38134),
(194579, 'itIT', 'Giustizia sarà fatta.', '', 38134),
(194578, 'itIT', 'Fondersi con una tale oscurità significa rinunciare all\'essere Kyrian!', '', 38134),
(194577, 'itIT', '', 'L\'anima infranta di Uther mi ha aperto le porte per il potere della Fauce. Ora te lo mostrerò di persona!', 38134),
(194576, 'itIT', '', 'NON SIETE NULLA DINANZI ALLA FAUCE!', 38134),
(194575, 'itIT', '', 'Il tuo servizio è concluso.', 38134),
(194574, 'itIT', '', 'Impossibile... Quello che ho visto...', 38134),
(194573, 'itIT', '', 'Ora!', 38134),
(194572, 'itIT', '', 'Quanto potere, e quanta cecità. Permettimi di togliere il velo dai tuoi occhi, mia Arconte.', 38134),
(194571, 'itIT', '', 'Il tuo padrone ti racconta solo bugie.', 38134),
(194570, 'itIT', '', 'Minacce vane, Ascesa.', 38134),
(194569, 'itIT', '', 'Kyrestia, il tuo regno finisce oggi! I Kyrian seguiranno un nuovo padrone!', 38134),
(194566, 'itIT', '', 'Vieni, Calcafauce. Sii testimone della fine dell\'Arconte!', 38134),
(194565, 'itIT', 'Devi sconfiggere Lysonia a ogni costo.', '', 38134),
(194564, 'itIT', 'Impediremo alle sue altre armate di avanzare verso l\'Arconte. Lo giuro.', '', 38134),
(194563, 'itIT', 'Il Bastione sarebbe suo.', '', 38134),
(194562, 'itIT', 'Spero solo non sia troppo tardi. Se l\'Arconte cadesse...', '', 38134),
(194561, 'itIT', 'Anche il resto delle nostre forze si sta ritirando al Forte.', '', 38134),
(194560, 'itIT', 'Gli Indegni si dirigono al Forte Elisio! Dobbiamo ripiegare immediatamente!', '', 38134),
(194559, 'itIT', '', 'Non fermerete il mio destino. Non quando ci sono così vicina.', 38134),
(194558, 'itIT', '', 'Quando l\'Arconte cadrà, anche i vostri preziosi Eletti vedranno la verità.', 38134),
(194557, 'itIT', '', 'La vittoria è già mia. Grazie al tuo misero assalto, l\'Arconte è indifesa per la prima volta.', 38134),
(194556, 'itIT', '', 'Non abbandonerò mai la mia gente come hai fatto tu! Mai!', 38134),
(194554, 'itIT', '', 'Posso mostrarti il tuo passato e dare un futuro a questo regno. Abbandona le bugie dell\'Arconte e unisciti a me!', 38134),
(194553, 'itIT', '', 'Tutto qui? Mostrami la forza dell\'Arconte! Prenditi la vittoria che brami così disperatamente!', 38134),
(194552, 'itIT', 'Questa è la tua ultima possibilità. Le tue forze giacciono morte tutte attorno a te, hai già perso!', '', 38134),
(194551, 'itIT', '', 'Folle Aspirante. Se sei tu tutto ciò che mi rimane da affrontare, allora siete voi ad aver perso.', 38134),
(194550, 'itIT', '', 'Luce, Vuoto... questi poteri insignificanti non hanno valore qui. L\'Arbiter ti ha inviato a Maldraxxus per un motivo che cercheremo di capire.', 38134),
(194549, 'itIT', 'Ma perché non sono stato scelto? La mia fede nella Luce non era abbastanza salda? La mia anima è stata contaminata dalla mia rinascita nella non morte?', '', 38134),
(194547, 'itIT', '', 'I ricordi della tua vita recano tormento nella morte. Sei fossi stato scelto per il Bastione, sarebbero stati considerati un fardello.', 38134),
(194545, 'itIT', 'Quando posai lo sguardo sui Maldraxxi per la prima volta, tutto ciò che riuscivo a vedere era il Flagello che ha portato alla rovina la mia patria. I mostri che avevo giurato di distruggere.', '', 38134),
(194544, 'itIT', '', 'I nostri Aspiranti hanno versato i loro ricordi nel Tempio della Purezza, ma noi seguiremo un sentiero diverso. Maldraxxus non fa per te?', 38134),
(194543, 'itIT', '', 'Venite con me, entrambi.', 38134),
(194542, 'itIT', 'Stato del bersaglio: in vi...vi...vi...vita. Eliminazione...', '', 38134),
(194541, 'itIT', 'Sta iniziando ad arrestarsi! Continuate a lottare, amici!', '', 38134),
(194540, 'itIT', '', 'Ho detto di eliminarli. Perché sono ancora in vita, Mastodonte?', 38134),
(194539, 'itIT', 'Ricordo... l\'umiliazione. Ora è... il tuo turno.', '', 38134),
(194538, 'itIT', '', 'Ti ho aiutato ad addestrare tante persone. Non ricordi nulla?', 38134),
(194537, 'itIT', 'Tono inaccettabile. Rimozione restrizioni letalità.', '', 38134),
(194536, 'itIT', '', 'Tutto ma non lui...', 38134),
(194535, 'itIT', 'Aspetta, Athanos...?', '', 38134),
(194534, 'itIT', '', 'La strada è libera a sud. Dobbiamo sbrigarci!', 38134),
(194533, 'itIT', '', 'Questa è l\'immensa ipocrisia dell\'Arconte.', 38134),
(194532, 'itIT', '', 'Credi di essere virtuosa, ma uccidi i tuoi simili senza sosta.', 38134),
(194531, 'itIT', '', 'Continuerò il suo lavoro. Tutti i nostri destini dipendono da questo.', 38134),
(194530, 'itIT', '', 'Osi pronunciare il suo nome? Quello che la mia Eletta ha messo in moto è appena cominciato!', 38134),
(194529, 'itIT', '', 'Sai già come andrà a finire. Condividerai il destino di Devos!', 38134),
(194528, 'itIT', '', 'Taci, Aspirante. Non sai nulla della follia.', 38134),
(194527, 'itIT', '', 'Ho rinunciato ai miei ricordi, al mio passato, alla mia intera esistenza!', 38134),
(194526, 'itIT', 'Lysonia, non è troppo tardi per rinunciare a questa follia! Siamo tutti Kyrian!', '', 38134),
(194524, 'itIT', '', 'Siamo con te fino alla fine!', 38134),
(194523, 'itIT', 'Che magnifica occasione! Ora, vogliate scusarmi, ho quasi finito uno stupendo potenziamento su Bron!', '', 38134),
(194521, 'itIT', 'Sei $gpronto:pronta;?', '', 38134),
(194517, 'itIT', 'Lysonia non era diversa. Non sapevo che celassero la stessa oscurità...', '', 38134),
(194516, 'itIT', 'Devos mi ha convinto che la mia morte era sbagliata. Mi ha promesso giustizia.', '', 38134),
(194515, 'itIT', 'Il mio addestramento da Aspirante è stato difficile. Non ricordo nulla della mia vita.', '', 38134),
(194514, 'itIT', 'Grazie per avermi mostrato pietà. Non lo dimenticherò.', '', 38134),
(194513, 'itIT', 'Anch\'io vorrei capire. Ti prego, fai strada.', '', 38134),
(194512, 'itIT', '', 'Devo fare tutto da sola?', 38134),
(194511, 'itIT', '', 'Non è ancora la vostra ora! Alzatevi e combattete!', 38134),
(194510, 'itIT', '', 'Erano deboli. Dovrei essere grata per avermi liberata dal peso del loro fallimento.', 38134),
(194509, 'itIT', '', 'Irrilevante.', 38134),
(194508, 'itIT', '', 'Se non combatti con onestà, non lo farò neanch\'io. Calcafauce, al mio fianco!', 38134),
(194506, 'itIT', 'Sei la solita sciocca, Nadjia. Le mie guardie vi spazzeranno via.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194504, 'itIT', '', 'Vieni avanti, così potrai deludermi come ha fatto Uther.', 38134),
(194502, 'itIT', '', 'Finalmente! Il mio tempo in gabbia mi ha fornito molto tempo per pianificare questo duello.', 38134),
(194500, 'itIT', '', 'Kyrestia ha ragione, le Terretetre sono a pezzi. Ma per porre rimedio serve forza di volontà... Forza che lei non ha.', 38134),
(194498, 'itIT', '', 'Finirò ciò che Devos ha iniziato. Non saremo più schiavi dell\'Arconte, accecati dalla \"via\".', 38134),
(194495, 'itIT', '', 'Il suo Animum è nostro.', 38134),
(194494, 'itIT', '', 'Possiamo anche aver perso le guglie, ma abbiamo portato a casa una vittoria.', 38134),
(194493, 'itIT', 'La vittoria è quasi nostra, Calcafauce. Rivendicala... Per tutti noi.', '', 38134),
(194490, 'itIT', '', 'Che l\'Arconte vegli su tutti noi.', 38134),
(194489, 'itIT', '', 'Io ripulirò l\'arena. Il portale per il tempio si trova a nord di qui.', 38134),
(194487, 'itIT', '', 'Vieni qui!', 38134),
(194485, 'itIT', '', 'ASSAGGIA IL MIO ACCIAIO!', 38134),
(194483, 'itIT', '', 'Di sicuro ti ricordi il tuo addestramento al Crogiolo. È stato facile da convertire.', 38134),
(194482, 'itIT', 'S-Sì... mia Eletta...', '', 38134),
(194481, 'itIT', '', 'Fermateli, maledizione! Athanos, dai ogni singola goccia del tuo Animum se devi!', 38134),
(194480, 'itIT', '', 'Guadagnate tempo! L\'Araldo della Verità è quasi carico.', 38134),
(194479, 'itIT', '', 'Non volete arrendervi, eh?', 38134),
(194478, 'itIT', 'Terremo a bada i loro rinforzi. Avanzate!', '', 38134),
(194477, 'itIT', '', 'Sono ancora in piedi. Di nuovo!', 38134),
(194476, 'itIT', '', 'E allora ricaricalo! Prosciuga i nostri Centurioni se serve!', 38134),
(194475, 'itIT', 'Livello Animum Colosso critico. Ricaricare.', '', 38134),
(194474, 'itIT', '', 'Che la morte piova su di loro!', 38134),
(194473, 'itIT', 'Sono molto felice di incontrarti!', '', 38134),
(194469, 'itIT', '', 'I miei campioni non cadranno così facilmente.', 38134),
(194468, 'itIT', '', 'Avete ucciso delle reclute, niente di più. Altri prenderanno il loro posto.', 38134),
(194465, 'itIT', '', 'Ammirate la piena gloria degli Indegni!', 38134),
(194464, 'itIT', '', 'La discrezione non è mai stata il punto forte di Kyrestia.', 38134),
(194463, 'itIT', '', 'Una carica a testa bassa, eh?', 38134),
(194462, 'itIT', 'Gloria all\'Arconte! Gloria al Bastione!', '', 38134),
(194460, 'itIT', 'Seguiamo i tuoi ordini.', '', 38134),
(194459, 'itIT', 'Calcafauce, tu avanzerai con la nostra armata più grande.', '', 38134),
(194458, 'itIT', 'Ognuno dei nostri Eletti guiderà un attacco dai fianchi.', '', 38134),
(194456, 'itIT', 'Il nostro momento è giunto, Ascesi. Oggi metteremo fine alla minaccia degli Indegni una volta per tutte.', '', 38134),
(194444, 'itIT', 'È bello vederti, Gubbins.', 'È bello vederti, Gubbins.', 38134),
(194443, 'itIT', 'Gubbins felice!', '', 38134),
(194442, 'itIT', 'Io lo ricordo sempre! Come la mia teiera!', '', 38134),
(194440, 'itIT', 'Miei cari Gubbins e Tubbins! Temevo che aveste scordato che ora è.', '', 38134),
(194437, 'itIT', '', 'Giustizia sarà fatta.', 38134),
(194428, 'itIT', '', 'Bene. Ti ho trovato aiuto.', 38134),
(194427, 'itIT', '', 'Fatta! Sì!', 38134),
(194426, 'itIT', '', 'Hai fatto una lista, Sika?', 38134),
(194425, 'itIT', '', '…Oh! Uuh-uuh! Salve di nuovo!', 38134),
(194423, 'itIT', 'Le bestie del Bastione non possiedono la disciplina dei Kyrian. Forse puoi sistemarne un po\'.', '', 38134),
(194421, 'itIT', 'Uuuh! Posso aiutare? Posso svolgere molti compiti o tenerti compagnia!', 'Uuuh! Posso aiutare? Posso svolgere molti compiti o tenerti compagnia!', 38134),
(194414, 'itIT', 'Splendido. Penso che quel dispositivo ci tornerà utile.', '', 38134),
(194413, 'itIT', 'Gli Alienatori hanno ottenuto un forziere colmo di armi particolari degli altri regni. Vediamo se possono esserci utili.', '', 38134),
(194402, 'itIT', '', 'Spetta a loro. Prima, lasciamoli vedere.', 38134),
(194401, 'itIT', 'Pensi che saranno davvero in grado di aiutarci?', '', 38134),
(194391, 'itIT', '', 'Non te l\'ho detto? Sembra promettente.', 38134),
(194388, 'itIT', 'Apri a me la tua mente, mortale. Vediamo quali ricordi si riveleranno.', 'Apri a me la tua mente, mortale. Vediamo quali ricordi si riveleranno.', 38134),
(194385, 'itIT', 'Sì, sì. Grazie, Gubbins. Sono molto intelligente!', '', 38134),
(194384, 'itIT', 'Tubbins lo sapeva! Grande, Tubbins!', '', 38134),
(194378, 'itIT', 'Fiii...', 'Fiii...', 38134),
(194377, 'itIT', 'Guarda sempre il lato positivo della morte...', 'Guarda sempre il lato positivo della morte...', 38134),
(194375, 'itIT', 'Immagina la mia sorpresa! Un semplice Alienatore come me che lavora con $glo straniero:la straniera; più speciale.', '', 38134),
(194363, 'itIT', 'Scoprirà la verità... E poi cadrà...', '', 38134),
(194358, 'itIT', '', 'Vedranno con chiarezza... alla fine...', 38134),
(194336, 'itIT', '', 'Consideralo parte del tuo addestramento, amico mio.', 38134),
(194335, 'itIT', 'Dobbiamo camminare? Sono piuttosto affaticato dopo la mia prova.', '', 38134),
(194334, 'itIT', '', 'Va\' avanti. Io e Pelagos ci faremo strada a piedi.', 38134),
(194332, 'itIT', 'Sembra che tutti i miei attrezzi siamo qui. Devo uno specchio a qualcuno.', 'Sembra che tutti i miei attrezzi siamo qui. Devo uno specchio a qualcuno.', 38134),
(194331, 'itIT', 'Tanto vale divertirmi un po\' lungo strada per l\'Ora del Tè.', 'Tanto vale divertirmi un po\' lungo strada per l\'Ora del Tè.', 38134),
(194330, 'itIT', 'Be\', questa è certamente un\'ottima coincidenza... Com\'è arrivata la mia borsa qui?', 'Be\', questa è certamente un\'ottima coincidenza... Com\'è arrivata la mia borsa qui?', 38134),
(194328, 'itIT', '', 'Gli Indegni mirano a impedire la nostra ascensione. Dobbiamo trovare gli altri!', 38134),
(194327, 'itIT', 'Vorrei poter dire lo stesso! Non crederai mai a tutto ciò che ho subito stamattina!', '', 38134),
(194326, 'itIT', 'Non mi lamento.', 'Non mi lamento.', 38134),
(194324, 'itIT', 'Benvenuta, mia cara Vulca! Confido che il tuo viaggio sia stato divertente.', '', 38134),
(194319, 'itIT', 'Dammi qualcosa da colpire!', 'Dammi qualcosa da colpire!', 38134),
(194318, 'itIT', 'Ho un brutto presentimento...', 'Ho un brutto presentimento...', 38134),
(194317, 'itIT', 'Non lascerò che mi spezzi!', 'Non lascerò che mi spezzi!', 38134),
(194316, 'itIT', 'Mi riposerò... quando... sarò... alla festa...', 'Mi riposerò... quando... sarò... alla festa...', 38134),
(194315, 'itIT', 'Forse posso riposare... ma solo per un attimo!', 'Forse posso riposare... ma solo per un attimo!', 38134),
(194314, 'itIT', 'Non posso fermarmi ora. Devo continuare a muovermi.', 'Non posso fermarmi ora. Devo continuare a muovermi.', 38134),
(194297, 'itIT', 'Meglio iniziare a preparare...', '', 38134),
(194296, 'itIT', 'Sembra che sia il primo ad arrivare, nonostante la mia mattinata imprevista.', '', 38134),
(194293, 'itIT', '', 'Mograine ha menzionato spesso la tua capacità di comando e il tuo coraggio, Draka. Sono onorata di incontrarti, finalmente.', 38134),
(194292, 'itIT', '', 'La tua presenza qui è ben accetta, Eletta. Abbiamo un nemico comune.', 38134),
(194291, 'itIT', 'Forse è un\'occasione per recuperare parte delle nostre perdite.', '', 38134),
(194290, 'itIT', 'Potrebbe portarci parecchi guadagni.', '', 38134),
(194289, 'itIT', 'Sibilla ha ragione. Basta parlare. Servite il tè e i contorni!', 'Sibilla ha ragione. Basta parlare. Servite il tè e i contorni!', 38134),
(194288, 'itIT', '', 'Radunare, blaterare.... Terminare e poi mangiare!', 38134),
(194287, 'itIT', 'Sì! Storie dell\'Ora del Tè!', '', 38134),
(194286, 'itIT', 'Sono il più furbo!', '', 38134),
(194285, 'itIT', 'No, mio caro Tubbins, insisto che abbia dovuto affrontare io l\'avventura più emozionante oggi!', '', 38134),
(194284, 'itIT', 'Hai ucciso tutti quegli Zannacruenta senza alcun aiuto?', 'Hai ucciso tutti quegli Zannacruenta senza alcun aiuto?', 38134),
(194281, 'itIT', 'Prima fase, farsi notare!', '', 38134),
(194279, 'itIT', 'Non si tratta solo di essere bravi a combattere. Bisogna creare dei legami!', '', 38134),
(194278, 'itIT', 'Vai via, vattene, VAI VIA! Questo è MIO! L\'ho trovato io! Rivendico questo tesoro. È mio.\n\nVattene! Vattene ora e torna in qualunque regno da cui sei $guscito:uscita;. Lanciati dai bastioni, non mi interessa, lasciami con questo tesoro che ho trovato.\n\nNe ho bisogno. Mi serve più di quanto qualcuno con la tua bella armatura e vestiti costosi possa capire. È MIO!', 'Vai via, vattene, VAI VIA! Questo è MIO! L\'ho trovato io! Rivendico questo tesoro. È mio.\n\nVattene! Vattene ora e torna in qualunque regno da cui sei $guscito:uscita;. Lanciati dai bastioni, non mi interessa, lasciami con questo tesoro che ho trovato.\n\nNe ho bisogno. Mi serve più di quanto qualcuno con la tua bella armatura e vestiti costosi possa capire. È MIO!', 38134),
(194271, 'itIT', 'No, no, no! Questo è il MIO tesoro!', '', 38134),
(194266, 'itIT', '$gPronto:Pronta; per il prossimo duello, $R?', '', 38134),
(194265, 'itIT', 'Confido in altri futuri accordi remunerativi.', '', 38134),
(194250, 'itIT', 'Prima di poter competere nell\'arena, devo valutare se hai del potenziale per generare profitti. Giudicherò il tuo esatto valore col progredire dei nostri rapporti d\'affari.', '', 38134),
(194249, 'itIT', '', 'Affrontami, necromante! Thanikos sarà vendicato!', 38134),
(194247, 'itIT', 'È ora di insegnare a questi furfanti un po\' di buone maniere!', '', 38134),
(194246, 'itIT', 'Ah, un po\' di riposo dalla Luce...', '', 38134),
(194245, 'itIT', 'Questo è esattamente il motivo per cui non approvo la corsa con i parasole!', '', 38134),
(194229, 'itIT', 'Un risultato inaspettato è la ricompensa migliore di un esperimento! Grazie per il tuo aiuto!', '', 38134),
(194228, 'itIT', 'Il prossimo costume!', '', 38134),
(194227, 'itIT', 'Bene, ora va\' in posizione!', '', 38134),
(194224, 'itIT', 'Il Coordinatore le ammucchia qua e là mettendo dei Litogeni di guardia. Ci fa salire il crimine a tutti. Prendi quelle pietre e aiuta Nadjia!', '', 38134),
(194223, 'itIT', 'Be\' quelle sono Pietre del Peccato. Il Coordinatore le usa per costringere gli altri a fare ciò che vuole se le trova, capito? Stai aiutando la mia amica Nadjia?', '', 38134),
(194219, 'itIT', 'Gli amici di Nadjia sono amici miei. Ci tocca trovare \'ste pietre con le scritte. Si trovano ovunque nel bosco.', '', 38134),
(194211, 'itIT', 'Proprio dove sapevo che era dall\'inizio!', '', 38134),
(194210, 'itIT', '', 'Sei $gpronto:pronta; ad apprendere la mia saggezza?', 38134),
(194202, 'itIT', 'Bisogna trovare in fretta la teiera fortunata! Presto!', '', 38134),
(194201, 'itIT', 'Be\', adesso non fare lo scaricateiera...', '', 38134),
(194200, 'itIT', 'Tubbins porta sempre la teiera.', '', 38134),
(194199, 'itIT', 'Credevo che TU avessi la teiera!', '', 38134),
(194197, 'itIT', 'Be\', eravamo un po\' in ritardo per l\'Ora del Tè...', '', 38134),
(194196, 'itIT', 'Non servo a nulla senza i miei attrezzi...', 'Non servo a nulla senza i miei attrezzi...', 38134),
(194195, 'itIT', 'Non riesco a credere che mi abbiano beccato a rubare quegli specchi!', 'Non riesco a credere che mi abbiano beccato a rubare quegli specchi!', 38134),
(194194, 'itIT', 'Tutto è iniziato con un semplice malinteso...', 'Tutto è iniziato con un semplice malinteso...', 38134),
(194193, 'itIT', 'Senza il mio parasole, devo stare all\'ombra... Maledetta Luce!', '', 38134),
(194192, 'itIT', 'Argh. Ridatemi il mio parasole, furfanti!', '', 38134),
(194191, 'itIT', 'Sedetevi, godetevi un po\' di tè e ascoltate il grande racconto della mia avventura...', '', 38134),
(194181, 'itIT', 'Sibilla in ritardo! Tubbins in anticipo!', '', 38134),
(194178, 'itIT', '', 'Sii imprudente!', 38134),
(194177, 'itIT', '', 'Pelati sconsiderati!', 38134),
(194174, 'itIT', '', 'Il bosco risuona di gioia. Gli antichi fasti ora ricostruiti. Tu, eroe di regine e corti, sei amico e guardiano dei Semebrado.', 38134),
(194173, 'itIT', 'Mi sono risvegliato. Che il giudizio cominci.', '', 38134),
(194170, 'itIT', '', 'Le radici si estendono, la loro sete placata dalle tue gesta. Una miriade di spiriti rinascerà grazie a te.', 38134),
(194164, 'itIT', '', 'L\'Animum fiorisce nel giardino. Anche se solo un germoglio, i tuoi rami sosterranno tutti noi.', 38134),
(194161, 'itIT', '', 'Il bosco ti ringrazia e prospera. Lo stesso faranno i suoi Semebrado. Ci attendono altre cure.', 38134),
(194160, 'itIT', 'Hai ancora modo di crescere. Ritorna quando sarai più forte.', '', 38134),
(194159, 'itIT', 'Un risultato soddisfacente. Mi arrendo.', '', 38134),
(194158, 'itIT', 'Ti misurerò e ti valuterò.', '', 38134),
(194140, 'itIT', '', 'I tre gourmet, grazie a me.', 38134),
(194137, 'itIT', 'Uuuooooooooo!', '', 38134),
(194135, 'itIT', 'Ce l\'ho fatta! Uuuh!', '', 38134),
(194127, 'itIT', '', 'Il mio intero distaccamento è stato assalito dai divoratori. La loro fenditura nella torre ci ha colti di sorpresa... ci hanno circondati in pochi secondi. Sono sopravvissuto a malapena.', 38134),
(194124, 'itIT', '', 'Il tempo alimenterà la sua crescita. Presto potrai risvegliarlo.', 38134),
(194122, 'itIT', '', 'Metti questo spirito all\'interno di un Semebrado vuoto per iniziare.', 38134),
(194120, 'itIT', 'Aiutali a preparare il palco. Sbrigati!', '', 38134),
(194117, 'itIT', 'È l\'ora del prossimo atto!', '', 38134),
(194106, 'itIT', '', 'Ma i catalizzatori devono precedere il Semebrado. Prima pianta i catalizzatori o gli spiriti non ne trarranno alcun beneficio.', 38134),
(194105, 'itIT', '', 'Ogni catalizzatore conferisce una benedizione differente. Sperimenta, esplora ed espanditi.', 38134),
(194104, 'itIT', '', 'I Semebrado traggono forza e aiuto dai catalizzatori piantati accanto a loro.', 38134),
(194096, 'itIT', 'Tutti al proprio posto!', '', 38134),
(194094, 'itIT', 'Ah, eccoti qui! Stiamo per iniziare!', '', 38134),
(194093, 'itIT', '', 'Puoi accettarli liberamente, è così che fanno i mortali.', 38134),
(194091, 'itIT', '', 'Con la rinascita, gli spiriti mostrano gratitudine con dei doni.', 38134),
(194088, 'itIT', '', 'Qui crescono Semebrado diversi da tutti gli altri, le tue abilità devono crescere con loro. Io coltiverò entrambi.', 38134),
(194087, 'itIT', '', 'Un tocco delicato e una cura amorevole devono guidarli verso il loro destino. Saranno i nostri sforzi a coltivarli.', 38134),
(194085, 'itIT', 'No. No! Non devo arrendermi!', '', 38134),
(194082, 'itIT', 'C\'è sempre qualcosa di nuovo da scoprire!', '', 38134),
(194081, 'itIT', 'Mmm, Rezan è stato più furbo di quanto pensassi! Forse nu giorno ci sarà nu nuovo Loa che farà patti con Bwonsamdi, eh?', '', 38134),
(194080, 'itIT', 'Io sono... debole... Fallirò...', '', 38134),
(194078, 'itIT', 'Sono molto onorato, maestà. Grazie per tutto chello che hai fatto per me... nella vita e nella morte.', '', 38134),
(194077, 'itIT', '', 'Non saprei dire cosa il destino abbia in serbo per te, Vol\'jin dei Lanciascura, ma ti offro una possibilità di rinascere, un giorno.', 38134),
(194074, 'itIT', '', 'Insieme a quest\'altro. Interessante... un\'anima mortale intrecciata con l\'essenza di un Loa.', 38134),
(194073, 'itIT', '', 'Prenderemo questi tre spiriti sotto la nostra tutela, Bwonsamdi.', 38134),
(194070, 'itIT', 'Ehilà, maestà! Il vecchio amico tuo Bwonsamdi ha aiutato chesta creatura a trovare i Loa che mancavano.', '', 38134),
(194067, 'itIT', 'Ti prego! Ti prego!!!', '', 38134),
(194066, 'itIT', 'Finalmente... è finita...', '', 38134),
(194065, 'itIT', 'Il tuo fallimento... è un\'onta... sulla tua anima...', '', 38134),
(194064, 'itIT', 'Sei debole. Non otterrai mai l\'Ascensione.', '', 38134),
(194063, 'itIT', 'Aiutatemi...', '', 38134),
(194062, 'itIT', 'Mi dispiace tanto...', '', 38134),
(194061, 'itIT', 'Solo... Tutto solo...', '', 38134),
(194060, 'itIT', 'Così tante... prove...', '', 38134),
(194059, 'itIT', 'Non ascenderò mai.', '', 38134),
(194058, 'itIT', 'Kleia... aiutami!', '', 38134),
(194057, 'itIT', 'È impossibile!', '', 38134),
(194056, 'itIT', 'Ahhh...', '', 38134),
(194054, 'itIT', 'Tutto è... perdonato...', '', 38134),
(194053, 'itIT', 'Silenzio... finalmente...', '', 38134),
(194052, 'itIT', 'No!', '', 38134),
(194051, 'itIT', 'Perché? Perché???', '', 38134),
(194050, 'itIT', 'Sbagliato! Tutto sbagliato!', '', 38134),
(194049, 'itIT', 'Ho... fallito?', '', 38134),
(194048, 'itIT', 'Basta... Basta...', '', 38134),
(194047, 'itIT', 'Non sono mai stato all\'altezza!', '', 38134),
(194046, 'itIT', 'Dolore!', '', 38134),
(194042, 'itIT', 'No, sono solo nu Cacciatore dell\'Ombra. La gente nostra ti necessita!', '', 38134),
(194040, 'itIT', 'Tutto ciò che sono stato... rinasca... in te.', '', 38134),
(194039, 'itIT', 'L\'ultimo barlume... della mia essenza... è tuo. Portalo... a Selvarden.', '', 38134),
(194035, 'itIT', 'Oh! Oh, non è così che dovrebbe andare... Ma continua a provare!', '', 38134),
(194031, 'itIT', '', 'Il fiume del tempo scorre impetuoso, le sue correnti sono rapide e forti. Da soli si annega, insieme si nuota.', 38134),
(194030, 'itIT', '', 'Gli spiriti che dormono al suo interno sono indeboliti. La loro rinascita è incerta come la pioggia.', 38134),
(194029, 'itIT', '', 'È un luogo speciale. Una valle di rinvigorimento, di anime perdute a cui è stata data una nuova possibilità.', 38134),
(194028, 'itIT', '', 'Il bosco sussurra il suo benvenuto attraverso i rami dei suoi guardiani.', 38134),
(194026, 'itIT', '', 'Segui la via. Come se avessimo scel... Dobbiamo incamminarci.', 38134),
(194025, 'itIT', '', 'Abbiamo molto da insegnarti, ma non temere! Il tuo viaggio ti appartiene, sei tu che scegli come percorrere la via.', 38134),
(194024, 'itIT', '', 'Ti purificherai dalle tue imperfezioni mortali e ascenderai verso una più alta forma d\'esistenza, capace di svolgere i nostri compiti più sacri e difficili.', 38134),
(194023, 'itIT', '', 'Mentre ti avventuri tra di noi, ti insegneremo le virtù di Purezza, Coraggio, Umiltà, Saggezza e Lealtà.', 38134),
(194022, 'itIT', 'Vol\'jin... dei Lanciascura... La tua anima è forte. Cerchi... giustizia.', '', 38134),
(194019, 'itIT', 'No! Rezan, ti tireremo fuori da qui. Avrai di nuovo la forza tua.', '', 38134),
(194018, 'itIT', 'È troppo... tardi...', '', 38134),
(194017, 'itIT', 'Non... ancora...', '', 38134),
(194016, 'itIT', 'Quest\'anima è persa! Non c\'è più niente da salvare!', '', 38134),
(194015, 'itIT', 'Fuggi...', '', 38134),
(194014, 'itIT', 'Ti unirai al tormento di questo patetico Loa!', '', 38134),
(194011, 'itIT', 'Conto su di te... piglia l\'Animum mio e salva Rezan!', '', 38134),
(194010, 'itIT', 'Eppure, ho dedicato la mia vita al servizio della Luce. Non è questo ciò che serve per ottenere un posto al Bastione?', '', 38134),
(194007, 'itIT', '', 'Brandicenere, il giudizio dell\'Arbiter è infallibile. Deve aver riconosciuto qualcosa in te che era destinato a Maldraxxus.', 38134),
(194006, 'itIT', 'Dobbiamo salvare Rezan da chesto mostro!', '', 38134),
(194005, 'itIT', 'Scoprirai che la Gelatina Mefitica e la Melma Miscibile sono difficili da trovare.', '', 38134),
(194003, 'itIT', 'Torna più tardi. Creeremo un\'altra pozione insieme.', '', 38134),
(193997, 'itIT', 'Quelle maschere... i miei amici... è come se non fossero neanche lì.', '', 38134),
(193996, 'itIT', 'Quanto successo al nostro villaggio è terribile... dobbiamo assicurarci che non accada di nuovo!', '', 38134),
(193995, 'itIT', 'Ringrazio i custodi di questo bosco per avermi permesso di restare qui. Prometto che farò del mio meglio per meritarmi la loro ospitalità.', '', 38134),
(193994, 'itIT', 'Ce l\'hai fatta! La rete funziona di nuovo.', '', 38134),
(193993, 'itIT', 'Qualcuno, o qualcosa, ha destabilizzato la nostra rete di specchi. Devi trovare la fonte della devastazione, così che le cose tornino a funzionare a dovere.', '', 38134),
(193991, 'itIT', 'I Dragatori stanno lanciando di tutto nella Pozza di Fango. È essenziale per il processo di dragaggio.\n\nSfortunatamente, l\'equilibrio sbagliato può portare a generare queste sporche creature. Una volta si è creato questo grande amalgama... è stato un disastro.', '', 38134),
(193990, 'itIT', 'Splendido! Puoi unirti a noi per l\'Ora del tè quando vuoi!', '', 38134),
(193985, 'itIT', '', 'Non ti hanno seguito, vero? La Forgiaossa Heirmir vuole mantenere la segretezza più totale.', 38134),
(193982, 'itIT', 'Vieni, unisciti a noi! Sei giusto in tempo per il tè.', '', 38134),
(193981, 'itIT', '', 'Tienilo stretto, ora! L\'Animum non deve cadere!', 38134),
(193980, 'itIT', '', 'Sika, possiamo destinare un po\' d\'Animum all\'Anfitriona?', 38134),
(193978, 'itIT', 'Urla, calcia, mordi, usa le tue armi... qualunque cosa debba fare per vincere!', '', 38134),
(193970, 'itIT', 'Tutte le tue mosse erano azzeccate, Calcafauce.', '', 38134),
(193960, 'itIT', 'Hai del potenziale, Calcafauce. Vediamo come te la cavi nell\'arena.', '', 38134),
(193940, 'itIT', 'Non vedo l\'ora di analizzare ciò che hai raccolto! Ben fatto, apprendista!', '', 38134),
(193938, 'itIT', 'Sembra che la zona attorno alla nostra necropoli sia stata colpita da sostanze corrosive. È ora di raccogliere qualche campione!', '', 38134),
(193933, 'itIT', 'Vai avanti. Mi serve solo... un momento.', '', 38134),
(193930, 'itIT', 'Inginocchiarmi? Dinanzi a te? Sei assurda come sempre, Nadjia. Ti donerò la vera morte la prossima volta che ci incontreremo.', '', 38134),
(193929, 'itIT', '', 'Matyas, le tue offese mi hanno stancata. Esigo soddisfazione! Inchinati a me e chiedi perdono, o duelleremo. $gIl:La; Calcafauce sarà $gil mio secondo:la mia seconda;.', 38134),
(193928, 'itIT', 'Urgh. Che tradimento è mai questo?', '', 38134),
(193919, 'itIT', 'Lei sa?', 'Lei sa?', 38134),
(193899, 'itIT', 'Farò del mio meglio. Per il cartello, naturalmente.', 'Farò del mio meglio. Per il cartello, naturalmente.', 38134),
(193894, 'itIT', 'Già. I Sorveglianti sono anche astuti, ma mi fido di te, Ta\'mari. Tieni in riga i tuoi soci.', 'Già. I Sorveglianti sono anche astuti, ma mi fido di te, Ta\'mari. Tieni in riga i tuoi soci.', 38134),
(193893, 'itIT', 'Non sottovalutarli. So che sei un Alienatore astuto, ma resta in guardia.', 'Non sottovalutarli. So che sei un Alienatore astuto, ma resta in guardia.', 38134),
(193892, 'itIT', 'A rapporto, Opulenza. Gli attendenti stanno facendo domande sulla rete dei portali, ma niente che non possa gestire.', 'A rapporto, Opulenza. Gli attendenti stanno facendo domande sulla rete dei portali, ma niente che non possa gestire.', 38134),
(193891, 'itIT', 'Eccellente. Continua così.', 'Eccellente. Continua così.', 38134),
(193890, 'itIT', 'Sono stato onorato di farne parte in misura minore.', 'Sono stato onorato di farne parte in misura minore.', 38134),
(193889, 'itIT', 'Anche se scommetto che hai messo mano al miglioramento, Ta\'mari.', 'Anche se scommetto che hai messo mano al miglioramento, Ta\'mari.', 38134),
(193888, 'itIT', 'Ottime notizie. Porgi i miei saluti ai tuoi soci.', 'Ottime notizie. Porgi i miei saluti ai tuoi soci.', 38134),
(193887, 'itIT', 'A rapporto, Opulenza. Siamo riusciti ad aumentare nuovamente la capacità della rete di portali.', 'A rapporto, Opulenza. Siamo riusciti ad aumentare nuovamente la capacità della rete di portali.', 38134),
(193886, 'itIT', '', 'Patetiche e deboli creature! Non potete fermare la Casata dei Costrutti!', 38134),
(193867, 'itIT', 'Non dovresti dubitare di noi.', 'Non dovresti dubitare di noi.', 38134),
(193866, 'itIT', 'Assicurati solo che resti attiva. Forse ne avremo bisogno a breve.', 'Assicurati solo che resti attiva. Forse ne avremo bisogno a breve.', 38134),
(193865, 'itIT', 'Certo. È la rete più affidabile tra tutti i cartelli. Molto discreta.', 'Certo. È la rete più affidabile tra tutti i cartelli. Molto discreta.', 38134),
(193864, 'itIT', 'Il tuo socio mantiene ancora il portale?', 'Il tuo socio mantiene ancora il portale?', 38134),
(193863, 'itIT', 'Salute. Confido che stia andando tutto secondo i programmi.', 'Salute. Confido che stia andando tutto secondo i programmi.', 38134),
(193862, 'itIT', 'Il Loa deve essere da qualche parte oltre il ponte. Muoviamoci.', '', 38134),
(193860, 'itIT', 'Figurati.', 'Figurati.', 38134),
(193859, 'itIT', 'Davvero? Ma grazie.', 'Davvero? Ma grazie.', 38134),
(193858, 'itIT', 'Questo è uno stile di per sé. Credo che ti doni.', 'Questo è uno stile di per sé. Credo che ti doni.', 38134),
(193857, 'itIT', 'Sembrano sempre molto meglio su di te. Il mio stile è più semplice.', 'Sembrano sempre molto meglio su di te. Il mio stile è più semplice.', 38134),
(193856, 'itIT', 'Salve, $gamico mio:amica mia;. Hai visto le nuove trasformazioni che abbiamo disponibili?', 'Salve, $gamico mio:amica mia;. Hai visto le nuove trasformazioni che abbiamo disponibili?', 38134),
(193855, 'itIT', 'Eccome. Davvero esotico!', 'Eccome. Davvero esotico!', 38134),
(193854, 'itIT', 'Salve, Vis. Quello è un nuovo luccichio sulla tua maschera?', 'Salve, Vis. Quello è un nuovo luccichio sulla tua maschera?', 38134),
(193853, 'itIT', 'Ci rivedremo.', 'Ci rivedremo.', 38134),
(193852, 'itIT', 'Ma grazie.', 'Ma grazie.', 38134),
(193851, 'itIT', 'Noto sempre il tuo stile, Vis.', 'Noto sempre il tuo stile, Vis.', 38134),
(193850, 'itIT', 'L\'hai notato? Ma grazie. Pensavo non si notasse troppo.', 'L\'hai notato? Ma grazie. Pensavo non si notasse troppo.', 38134),
(193849, 'itIT', 'Salve, Vis. Mi piace il nuovo aspetto!', 'Salve, Vis. Mi piace il nuovo aspetto!', 38134),
(193848, 'itIT', 'Lo so.', 'Lo so.', 38134),
(193847, 'itIT', 'Non c\'è problema.', 'Non c\'è problema.', 38134),
(193846, 'itIT', 'Ti sta bene. Forse lo proverò anch\'io, più tardi.', 'Ti sta bene. Forse lo proverò anch\'io, più tardi.', 38134),
(193845, 'itIT', 'Lo è. Ti piace?', 'Lo è. Ti piace?', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193844, 'itIT', 'Salve, Ta\'vis. È un nuovo bagliore quello che sfoggi?', 'Salve, Ta\'vis. È un nuovo bagliore quello che sfoggi?', 38134),
(193841, 'itIT', '', 'Senza la fonderia dei Prescelti, dobbiamo forgiare le nostre armi dove e quando possiamo. Tu sta\' di guardia, Calcafauce!', 38134),
(193839, 'itIT', '', 'Questa terra sarà purificata!', 38134),
(193838, 'itIT', '', 'Vi distruggerò tutti quanti!', 38134),
(193837, 'itIT', '', 'Non c\'è posto per questo!', 38134),
(193836, 'itIT', '', 'Assaggiate l\'ira del Coraggio!', 38134),
(193835, 'itIT', '', 'Che la mia vendetta vi colga!', 38134),
(193834, 'itIT', '', 'Questo luogo sarà epurato!', 38134),
(193833, 'itIT', 'Esattamente!', 'Esattamente!', 38134),
(193832, 'itIT', 'Incolpa pure i mortali. Si dimenticano spesso dove lasciano le loro serpi, dopotutto. Non sei d\'accordo?', 'Incolpa pure i mortali. Si dimenticano spesso dove lasciano le loro serpi, dopotutto. Non sei d\'accordo?', 38134),
(193831, 'itIT', 'A parte un paio di cose fuggite, stiamo bene. Gli attendenti non noteranno un paio di serpenti in più che strisciano in giro, vero?', 'A parte un paio di cose fuggite, stiamo bene. Gli attendenti non noteranno un paio di serpenti in più che strisciano in giro, vero?', 38134),
(193830, 'itIT', 'Le cose sembrano andare bene.', 'Le cose sembrano andare bene.', 38134),
(193829, 'itIT', '', 'Mograine. La Fauce sembrerà un dolce ricordo quando avrò finito con te.', 38134),
(193828, 'itIT', 'Vedrò cosa riusciremo a trovare!', 'Vedrò cosa riusciremo a trovare!', 38134),
(193827, 'itIT', 'I mortali sembrano preferire le cose che definiscono \"carine\". Non so bene cosa significhi, ma gli occhi grandi sembrano esserne il fulcro. Quindi, altre creature con occhi grandi!', 'I mortali sembrano preferire le cose che definiscono \"carine\". Non so bene cosa significhi, ma gli occhi grandi sembrano esserne il fulcro. Quindi, altre creature con occhi grandi!', 38134),
(193826, 'itIT', 'Lo farò. Preferenze?', 'Lo farò. Preferenze?', 38134),
(193825, 'itIT', 'Bene, ma di\' ai tuoi soci di continuare a portare altre creature.', 'Bene, ma di\' ai tuoi soci di continuare a portare altre creature.', 38134),
(193823, 'itIT', 'Come tutti noi. Sospetto che guadagneremo bene da questa ultima impresa.', 'Come tutti noi. Sospetto che guadagneremo bene da questa ultima impresa.', 38134),
(193822, 'itIT', 'Ben fatto. Lavorare per il cartello mi tiene occupato.', 'Ben fatto. Lavorare per il cartello mi tiene occupato.', 38134),
(193821, 'itIT', 'Lo farò certamente.', 'Lo farò certamente.', 38134),
(193820, 'itIT', 'Una rete di portali davvero notevole. Ti prego di farlo sapere ai fautori.', 'Una rete di portali davvero notevole. Ti prego di farlo sapere ai fautori.', 38134),
(193819, 'itIT', 'È incredibile vedere tutte le creature che i miei soci forniscono. Una tale gioia.', 'È incredibile vedere tutte le creature che i miei soci forniscono. Una tale gioia.', 38134),
(193818, 'itIT', 'Il commercio è molto intenso. I mortali hanno gusti diversi in fatto di compagni.', 'Il commercio è molto intenso. I mortali hanno gusti diversi in fatto di compagni.', 38134),
(193817, 'itIT', 'Come stai?', 'Come stai?', 38134),
(193816, 'itIT', 'Perderai! Io lo so!', '', 38134),
(193815, 'itIT', '', 'Pelagos potrebbe soccombere presto al dubbio e alla paura. Fai in fretta.', 38134),
(193814, 'itIT', '', 'Cosa??? Quell\'idiota... Abbiamo poco tempo!', 38134),
(193813, 'itIT', 'Kleia! Sia ringraziata l\'Arconte... Pelagos ha iniziato il rituale senza di te!', '', 38134),
(193812, 'itIT', '', 'Siamo arrivati. Pelagos sarà impaziente di incontrarti.', 38134),
(193811, 'itIT', '', 'Siamo nel mezzo di una tragedia indescrivibile.', 38134),
(193810, 'itIT', '', 'Aspetta... Lascia che te lo mostri.', 38134),
(193809, 'itIT', '', 'Con i portali chiusi, le nostre forze hanno avuto una possibilità di combattere.', 38134),
(193808, 'itIT', '', 'Il nostro regno deve sembrarti immacolato. Le parole non possono esprimere ciò che è andato perso.', 38134),
(193807, 'itIT', '', 'Il Vuoto ha aperto enormi portali per portare qui le proprie forze. Se non li distruggiamo, non avremo alcuna speranza di vittoria.', 38134),
(193805, 'itIT', '', 'Avevo quasi superato la prova finale quand\'è iniziata la siccità. Nessuno è asceso da allora.', 38134),
(193802, 'itIT', '', 'Conosci la via meglio di tutti, Aspirante. Che l\'Arconte vegli su di te.', 38134),
(193801, 'itIT', 'Cumpà... percepisco altri Loa qua vicino!', '', 38134),
(193800, 'itIT', '', 'Ben fatto... ora vieni. La vendetta... può aspettare.', 38134),
(193799, 'itIT', 'Anche se ci occupiamo di Oribos, l\'operato dello Scopo è opaco e divino ai nostri occhi.\n\nEsso è antico, probabilmente ben oltre la tua comprensione. Questo sistema è divino.', '', 38134),
(193797, 'itIT', 'Il funzionamento dello Scopo non è sempre chiaro.\n\nPer quanto possa comprendere, esso valuterà i tuoi bisogni in base alle attività che hai compiuto per favorire lo Scopo. \n\nQuesto è il modo di Oribos di aiutarti. Tu servi lo Scopo e perciò, a suo modo, lo Scopo servirà te.\n\nLo Scopo è la nostra via.', '', 38134),
(193795, 'itIT', 'Salute, Calcafauce.\n\nUna volta che sarai $gdiventato:diventata; abbastanza forte, questa banca ti fornirà un beneficio che Oribos ritiene appropriato per le imprese che hai compiuto in aiuto dello scopo. Questo accadrà a intervalli regolari, fintanto che accorrerai in nostro aiuto.\n\nCredo che la tua razza si riferisca alla natura di questi intervalli con la parola \"settimanali\".', '', 38134),
(193789, 'itIT', '', 'Pensano di potermi... dare la caccia? Voglio vederli... fatti a brandelli!', 38134),
(193787, 'itIT', '', 'Tu... non infliggi torture. \n\nEro dentro... una magnifica foresta... E poi qualcosa mi ha preso...\n\nI miei aguzzini stanno tornando... Proteggimi... finché la mia forza non torna!', 38134),
(193782, 'itIT', 'I cunicoli reclamano la tua anima!', 'I cunicoli reclamano la tua anima!', 38134),
(193780, 'itIT', 'Le campane del Bastione calmeranno la tua anima. Ascoltale e sii in pace.', 'Le campane del Bastione calmeranno la tua anima. Ascoltale e sii in pace.', 38134),
(193779, 'itIT', '', 'Entra nella pozza.', 38134),
(193778, 'itIT', 'Accomodati pure. Sta per iniziare una presentazione.', 'Accomodati pure. Sta per iniziare una presentazione.', 38134),
(193777, 'itIT', '$gBenvenuto:Benvenuta;. Aggiungi il tuo nome alla lista delle anime nobili che chiamano il Bastione la loro nuova casa.', '$gBenvenuto:Benvenuta;. Aggiungi il tuo nome alla lista delle anime nobili che chiamano il Bastione la loro nuova casa.', 38134),
(193772, 'itIT', 'Sì cumpà? Che ti serve da Bwonsamdi?', '', 38134),
(193770, 'itIT', 'Salve, eroe. Siamo na bella squadra eh?', '', 38134),
(193769, 'itIT', 'Ah, nun lo sapevate? Nun ci si può nascondere da Bwonsamdi!', '', 38134),
(193768, 'itIT', 'Chesta è dura da tracciare... persino per nu Cacciatore dell\'Ombra. Andiamo.', '', 38134),
(193767, 'itIT', 'Salve, eroe. Dobbiamo parlare!', '', 38134),
(193765, 'itIT', 'Mmm. Hanno torturato lo spirito... poi l\'hanno mosso altrove. Ho la traccia.', '', 38134),
(193764, 'itIT', 'C\'hanno beccati, cumpà!', '', 38134),
(193763, 'itIT', 'Unisciti a questo spirito nel tormento eterno!', 'Unisciti a questo spirito nel tormento eterno!', 38134),
(193762, 'itIT', 'Seguirò le tracce per te, ma nulla di più. Se insistessi, rischierei di restare bloccato per sempre.', '', 38134),
(193760, 'itIT', 'La Fauce aspetta, cumpà... e anche lo spirito da salvare.', '', 38134),
(193757, 'itIT', 'Un tempo faro della Luce Sacra, ora entità del Vuoto che avvolge tutto in un\'ombra infinita!', '', 38134),
(193756, 'itIT', 'Chesto luogo... è peggio di quanto pensassi. Sento tracce dello spirito qua vicino...', '', 38134),
(193747, 'itIT', 'Sono certo che, qualsiasi cosa venga dragata, aiuterà la nostra causa.', '', 38134),
(193746, 'itIT', 'Le Pozze di Fango sono il posto ideale per recuperare qualche nuovo Dragatore, tra le altre cose...', '', 38134),
(193741, 'itIT', '', 'Ehilà, $C! Serve un passaggio?', 38134),
(193738, 'itIT', '', 'Salute, abitante di Azeroth! Abbiamo preparato una serie di attività per aiutarti ad adattarti alla tua nuova esistenza.', 38134),
(193737, 'itIT', '', 'L\'Arconte governa il nostro regno, lei è la più leale e zelante degli Eterni.\n\nÈ per sua volontà che il Bastione svolge la sua funzione nelle Terretetre. Ci sarebbe molto altro da dire su questo antico essere, ma certi onori non si addicono alle orecchie di un Aspirante.\n\nForse, quando avrai le tue ali, potrai ritrovarti in sua presenza.', 38134),
(193736, 'itIT', '', 'Lo scopo che mi è stato dato dall\'Arconte è di garantire che i nuovi arrivati al Bastione si adattino al passo successivo della loro nuova esistenza. Esisto per insegnare agli altri tutto sui Kyrian.\n\nOccuparmi di te sarà un onore e un privilegio.', 38134),
(193730, 'itIT', '', 'Fai attenzione là fuori, e ricorda sempre il tuo addestramento.', 38134),
(193729, 'itIT', '', 'Hai un radioso futuro davanti a te.', 38134),
(193727, 'itIT', 'Allora è tempo di mettere le cose a posto. Nun lascerò che Mueh\'zala abbia l\'ultima parola.', '', 38134),
(193726, 'itIT', 'Hai sentito Mueh\'zala. L\'anima tua è ancora legata ad Azeroth. Come l\'illustre cumpà qui presente.', '', 38134),
(193725, 'itIT', 'Bwonsamdi, nun si esce dalla Fauce, vero? Se ci vado...', '', 38134),
(193724, 'itIT', 'Aspetta, cumpà. Tengo qualcosa da dirti.', '', 38134),
(193723, 'itIT', 'Mrraaazz!', '', 38134),
(193721, 'itIT', 'Verrò con te in chesta Fauce. \n\nMueh\'zala si sta prendendo gioco di me, nun starò con le mani in mano ad aspettare che qualcuno sistemi chesto casino.\n\nTroveremo insieme chesto Dio Selvatico.', '', 38134),
(193720, 'itIT', 'A me nun piace nulla che riguarda chesto Mueh\'zala.', '', 38134),
(193719, 'itIT', 'Segh! Era ora!', '', 38134),
(193718, 'itIT', '', 'Indaco! Grazie alla dea, pensavo di essere finita.', 38134),
(193714, 'itIT', 'È stato un onore andare all\'avventura con te.', '', 38134),
(193681, 'itIT', '', 'Calix! Sia ringraziata l\'Arconte!', 38134),
(193676, 'itIT', 'L\'ospite speciale mio prova nu grande interesse in chello che Mueh\'zala ha da dire. Hanno na... connessione speciale.', '', 38134),
(193675, 'itIT', 'Bwonsamdi! Sei troppo importante per evocarmi tu stesso?', '', 38134),
(193670, 'itIT', 'I discepoli tuoi sono deboli, Mueh\'zala! Vecchie ossa stanche e spiritelli miserabili.', '', 38134),
(193660, 'itIT', 'La Necropoli mia sta accà. Potrebbe esserci nu poco di... caos. Ma nun è niente che tu nun possa gestire, vero?', '', 38134),
(193655, 'itIT', '', 'Così basta! Svegliamo l\'Anfitriona ora.', 38134),
(193654, 'itIT', 'Quelli grandi hanno tantiiissimo Animum! Questo però... è quasi vuoto.', 'Quelli grandi hanno tantiiissimo Animum! Questo però... è quasi vuoto.', 38134),
(193653, 'itIT', 'Il mio Animum... per il Bastione...', '', 38134),
(193652, 'itIT', 'Ti ringrazio.', 'Ti sono grata.', 38134),
(193651, 'itIT', 'Non preoccuparti. Farà ritorno quando tornerà l\'Animum.', 'Non preoccuparti. Farà ritorno quando tornerà l\'Animum.', 38134),
(193650, 'itIT', '', 'Sì! Prendi pure il mio barile. I Centurioni condivideranno!', 38134),
(193645, 'itIT', '', 'Litogeni, il principe caduto e il suo Calcafauce sono arrivati, non lasciate che raggiungano il Maestro.', 38134),
(193642, 'itIT', 'Come posso aiutarti?', 'Come posso aiutarti?', 38134),
(193641, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Druidi!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Druidi!', 38134),
(193638, 'itIT', 'Questa posizione è bella da morire!', '', 38134),
(193636, 'itIT', 'Louison ha detto che potevi restare.', '', 38134),
(193635, 'itIT', 'Devi combattere davvero bene per avere tutti quei teschi!', '', 38134),
(193634, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Monaci!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Monaci!', 38134),
(193633, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Paladini!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Paladini!', 38134),
(193632, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Sacerdoti!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Sacerdoti!', 38134),
(193631, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Maghi!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Maghi!', 38134),
(193630, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Cacciatori!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Cacciatori!', 38134),
(193629, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Ladri!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Ladri!', 38134),
(193628, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Sciamani!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Sciamani!', 38134),
(193627, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Stregoni!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Stregoni!', 38134),
(193626, 'itIT', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Guerrieri!', 'Saluti, $C.\n\nSe vuoi addestrarti, cerca un istruttore della tua classe. Io addestro soltanto Guerrieri!', 38134),
(193624, 'itIT', 'No! Siete semplici... mortali! Com\'è possibile...?', '', 38134),
(193623, 'itIT', 'Chello è il meglio che i seguaci tuoi possono fare, Bwonsamdi? Patetico.', '', 38134),
(193622, 'itIT', 'Spedirò le anime vostre nella Fauce! Nessuno sfida Mueh\'zala!', '', 38134),
(193621, 'itIT', 'Hai servito il Loa sbagliato!', '', 38134),
(193620, 'itIT', 'Mo\' appartieni al Carceriere!', '', 38134),
(193619, 'itIT', 'Chesta sarà la fine di Bwonsamdi, e pure la vostra!', '', 38134),
(193617, 'itIT', '', 'Vediamo un po\' se vi rosica questa!', 38134),
(193616, 'itIT', '', 'È l\'ora del laser!', 38134),
(193615, 'itIT', '', 'Una pioggia di pollo!', 38134),
(193614, 'itIT', '', 'Fermi! Prendetevi pure il mojo, Millhouse è più importante!', 38134),
(193613, 'itIT', '', 'Millhouse! Sistema questo disastro in modo che possa tornare al lavoro!', 38134),
(193609, 'itIT', 'Un potere al di là di ogni comprensione!', '', 38134),
(193608, 'itIT', 'Ora vi accendo! Con le ombre!', '', 38134),
(193607, 'itIT', 'Stavolta ammirerete davvero il mio potere!', '', 38134),
(193605, 'itIT', 'Basta! Preferisco perdere il mojo che lei.', '', 38134),
(193604, 'itIT', 'Ahah! Vi piace il mojo dei Manalampo?', '', 38134),
(193602, 'itIT', 'T\'insegno io a disturbare un genio!', '', 38134),
(193600, 'itIT', 'Hai la nostra gratitudine, Calcafauce. Che l\'Arconte ti guidi.', '', 38134),
(193599, 'itIT', 'Amethia!', '', 38134),
(193596, 'itIT', 'Ti ringrazio, Calcafauce. Anche il mio Vincolo dell\'Anima, Amethia, è tenuta qui. Ti prego, aiutami a liberarla.', '', 38134),
(193593, 'itIT', 'Per favore, Calcafauce, ho bisogno del tuo aiuto. Hanno preso Amethia! Io... non so cosa farei senza di lei. Non voglio nemmeno pensare all\'idea di restare qui da solo per sempre senza di lei.\n\nDobbiamo salvarla immediatamente!', '', 38134),
(193588, 'itIT', '', 'Ciao! L\'umile Factotum Sika è qui per aiutare!', 38134),
(193586, 'itIT', 'Ecco uno degli altari. Dobbiamo trovare un po\' della mia polvere per poterlo distruggere.', '', 38134),
(193585, 'itIT', '', 'Vattene.', 38134),
(193584, 'itIT', '', 'Eccellente. Speriamo che Bottons non sia troppo danneggiato.', 38134),
(193583, 'itIT', 'Ecco uno degli altari. Versaci sopra un po\' di polvere e vediamo che succede...', '', 38134),
(193581, 'itIT', 'Hai trovato un po\' di polvere! Con questa posso far saltare in aria un altare. Almeno, spero...', '', 38134),
(193580, 'itIT', '', 'Hai la mia gratitudine, Calcafauce. Non è stato facile assemblare Bottons con le parti che avevo a disposizione. Andiamo.', 38134),
(193575, 'itIT', '', 'Sono più che semplici monili.', 38134),
(193572, 'itIT', '', 'Le mie rarità possono essere alquanto letali.', 38134),
(193568, 'itIT', 'La Legione di Pietra non può penetrare queste mura.', 'La Legione di Pietra non può penetrare queste mura.', 38134),
(193566, 'itIT', '', 'BOTTONS!', 38134),
(193565, 'itIT', '', 'Fossi in voi, farei attenzione a dove mettete i piedi.', 38134),
(193562, 'itIT', '', 'Questo sarà... interessante.', 38134),
(193561, 'itIT', '', 'Ah! Sei tu $gil:la; Calcafauce? Sembri $gappetitoso:appetitosa;...\n\nSono stata separata dal mio... compagno. Cosa ne dici di liberarmi e di cercarlo insieme a me?', 38134),
(193554, 'itIT', '', 'Hai mai desiderato volare?', 38134),
(193552, 'itIT', 'Ti ringrazio per il tuo aiuto. Probabilmente dovremmo fare più attenzione in futuro.', '', 38134),
(193551, 'itIT', 'Eccolo, di nuovo con tutti i suoi arti.', '', 38134),
(193550, 'itIT', 'Calcafauce! Come richiesto, hai dinanzi a te, ehm... se alzi un po\' lo sguardo, il Coordinatore Matyas della Casata Rovotossico.', '', 38134),
(193549, 'itIT', '', 'Tutti... i miei... piani...', 38134),
(193548, 'itIT', '', 'Avete fatto... un vero affare...', 38134),
(193546, 'itIT', 'Grazie, Calcafauce. Ora andiamo a cercare Varrik. Non starà sicuramente meglio di me.', '', 38134),
(193542, 'itIT', '', 'La transazione è conclusa.', 38134),
(193540, 'itIT', '', 'Che figura patetica.', 38134),
(193536, 'itIT', '$gIl leggendario:La leggendaria; Calcafauce. Mi hai beccato durante una giornataccia.\n\nIo e un associato siamo stati entrambi catturati da ciò che c\'è in questo posto. Credo ci verrebbe comodo il tuo aiuto.', '', 38134),
(193535, 'itIT', '', 'Avevi sopravvalutato il tuo valore.', 38134),
(193534, 'itIT', '', 'La pagherete cara per questo.', 38134),
(193532, 'itIT', 'Atal\'ai! Ergetevi al servizio di Hakkar!', '', 38134),
(193527, 'itIT', 'Il vostro sangue mi dà forza!', '', 38134),
(193526, 'itIT', 'Affrontate l\'ira dello Scortica Anime!', '', 38134),
(193525, 'itIT', 'Muori, mortale!', '', 38134),
(193524, 'itIT', 'Il potere... era mio...', '', 38134),
(193523, 'itIT', 'Altri sacrifici per lo Scortica Anime.', '', 38134),
(193522, 'itIT', 'Sarò il dio del sangue e della morte.', '', 38134),
(193521, 'itIT', 'Una misera goccia nell\'oceano.', '', 38134),
(193520, 'itIT', 'Dobbiamo trovare Penthia. Non può essere lontana. E ovviamente, ti ringrazio di avermi liberato!', '', 38134),
(193519, 'itIT', 'Ehi, tu! Che è successo?', '', 38134),
(193518, 'itIT', 'Ciononostante, Accusatrice, questa è la strada che dobbiamo percorrere.', '', 38134),
(193517, 'itIT', '', 'Ricordo distintamente di aver sentito parlare di un piano simile molti anni fa, Renathal, prima che fossero rivelate le vere intenzioni del Maestro...', 38134),
(193516, 'itIT', 'Il tuo sangue è debole e indegno.', '', 38134),
(193515, 'itIT', 'Il suo potere è mio! Vivrò di nuovo!', '', 38134),
(193514, 'itIT', '', 'Devo averne ancora. L\'illuminazione è vicina.', 38134),
(193513, 'itIT', '', 'Mi nutro... Divento... Molto di più...', 38134),
(193512, 'itIT', '', 'Le vostre azioni sono prevedibili.', 38134),
(193511, 'itIT', '', 'Posso predire ogni vostra mossa.', 38134),
(193510, 'itIT', '', 'Questa non l\'eviterete!', 38134),
(193509, 'itIT', 'Questo potere sarà tuo finché resterai fedele alla nostra causa.', '', 38134),
(193508, 'itIT', '', 'Non potete fuggire!', 38134),
(193506, 'itIT', 'Chi sei? Come fai a camminare così liberamente?\n\nTi prego, aiutaci. Il mio amico è stato preso. Devo ritrovarlo.\n\nMi aiuterai?', '', 38134),
(193505, 'itIT', '', 'Boom! Boom!', 38134),
(193504, 'itIT', '', 'Dolore! Dolore!', 38134),
(193503, 'itIT', 'Troveremo un altro posto più sicuro dove nasconderci. Grazie del tuo aiuto.', '', 38134),
(193502, 'itIT', 'Eccola!', '', 38134),
(193500, 'itIT', '', 'Avrai molte domande, ma non preoccuparti! Tutto ti verrà spiegato a tempo debito.\n\nVuoi iniziare il prossimo capitolo della tua esistenza?', 38134),
(193499, 'itIT', 'Questa è la forma che avevo in vita per epurare innumerevoli Non Morti su Azeroth. Ma suppongo tu abbia ragione.', '', 38134),
(193498, 'itIT', '', 'Ma potrei farti la stessa domanda. Perché prendere la forma di un debole Umano, quando ne esistono altre più forti?', 38134),
(193497, 'itIT', '', 'Quando ero una Kaldorei, ero solo un\'ancella della mia Regina, apprezzata per la mia bellezza e poco altro. Come Naga, sono stata riconosciuta e ricompensata per la mia astuzia e determinazione.', 38134),
(193489, 'itIT', 'Dobbiamo mettere al sicuro quei medaglioni e, con essi, inaugurare una nuova era che veda Revendreth tornare al suo vero scopo.', '', 38134),
(193488, 'itIT', 'La sua stretta su questo regno deriva dai medaglioni che ha creato, ciascuno contenente una frazione del suo potere e donato a subordinati che obbediscono ciecamente.', '', 38134),
(193485, 'itIT', 'Baronessa, la tua forma originale di Elfa della Notte è stata ripristinata nell\'aldilà, vero? Perché abbandonarla per una simile ai Naga?', '', 38134),
(193481, 'itIT', '', 'L\'abisso oscuro della vostra psiche.', 38134),
(193480, 'itIT', '', 'La vostra ignoranza si manifesta.', 38134),
(193477, 'itIT', '', 'Ombre!', 38134),
(193476, 'itIT', '', 'Oscurità!', 38134),
(193475, 'itIT', '', 'Così tanto ancora da apprendere...', 38134),
(193472, 'itIT', '', 'Alcuni di voi sono più appetitosi di altri.', 38134),
(193470, 'itIT', '', 'La mia prole sa chi mangiare prima.', 38134),
(193469, 'itIT', '', 'Che delizie allettanti!', 38134),
(193468, 'itIT', '', 'Sentono il vostro odore!', 38134),
(193462, 'itIT', '', 'La vostra carne nutrirà i miei piccoli.', 38134),
(193461, 'itIT', '', 'La mia progenie placherà la sua fame.', 38134),
(193460, 'itIT', '', 'Divorateli, miei piccoli!', 38134),
(193459, 'itIT', '', 'Venite, piccoli miei. Il pasto è pronto.', 38134),
(193458, 'itIT', '', 'Cibo! Mangiate!', 38134),
(193457, 'itIT', '', 'Eterna conoscenza in ogni boccone.', 38134),
(193456, 'itIT', '', 'Venite! Cibatevi!', 38134),
(193455, 'itIT', '', 'Ho fame...', 38134),
(193454, 'itIT', 'Nun ti lamentare così, cumpà! Ho qui na cosa che devi vedere!', '', 38134),
(193452, 'itIT', 'Io ed Heirmir abbiamo preparato alcune sorprese per i nostri ospiti! Le consegneresti per noi?', '', 38134),
(193451, 'itIT', '', 'È la nostra occasione! Dietro le quinte!', 38134),
(193450, 'itIT', '', 'Perfetto! D\'accordo, pausa per tutti.', 38134),
(193449, 'itIT', '', 'Mmm, non va bene. Sposta l\'albero dall\'altra parte!', 38134),
(193448, 'itIT', '', 'Ehi! Tu con l\'albero! Va\' al tuo posto!', 38134),
(193447, 'itIT', '', 'Uh... Meglio andare.', 38134),
(193446, 'itIT', '', 'È il momento della scena dell\'incendio di Teldrassil! Dov\'è l\'albero?!', 38134),
(193445, 'itIT', '', 'Oooh, ho trovato! Travestiamoci con la polvere per trucchi che è avanzata!', 38134),
(193444, 'itIT', 'Il Calcafauce è con noi!', 'Il Calcafauce è con noi!', 38134),
(193443, 'itIT', 'Fai piovere distruzione!', 'Fai piovere distruzione!', 38134),
(193441, 'itIT', '', 'Ho da fare!', 38134),
(193420, 'itIT', 'Uniti siamo più forti!', '', 38134),
(193324, 'itIT', '', 'Mmm, era sul mio elenco? Non importa.', 38134),
(193322, 'itIT', '', 'Ti allei con la Regina del Massacro? Pessima scelta!', 38134),
(193321, 'itIT', 'Puoi sempre trovare rifugio nelle ombre.', 'Puoi sempre trovare rifugio nelle ombre.', 38134),
(193320, 'itIT', '', 'C\'è rimasto un solo nemico da uccidere. Parla con me quando vorrai partire.', 38134),
(193319, 'itIT', 'Io sono Hanzabu, nu seguace tra i più fedeli di Bwonsamdi. Gli ero fedele anche quando il resto di Zuldazar non lo era.\n\nFermeremo le forze di Mueh\'zala... a qualsiasi costo.', '', 38134),
(193318, 'itIT', 'Mueh\'zala... ha già... vinto...', '', 38134),
(193317, 'itIT', 'Libererò Mueh\'zala e mi piglierò la ricompensa!', '', 38134),
(193313, 'itIT', 'Non è finita qui.', 'Non è finita qui.', 38134),
(193312, 'itIT', 'Un essere vivente? Dove sta andando a finire Revendreth?', '', 38134),
(193311, 'itIT', 'Non abbiamo ottenuto nulla. Quanti ne abbiamo feriti?', 'Non abbiamo ottenuto nulla. Quanti ne abbiamo feriti?', 38134),
(193307, 'itIT', '', 'Con me! Insieme, purificheremo questo tempio!', 38134),
(193304, 'itIT', 'Il brivido della morte mi protegge!', '', 38134),
(193303, 'itIT', 'Nessuno sfugge alla tempesta.', '', 38134),
(193302, 'itIT', 'Ne ho abbastanza di te!', '', 38134),
(193301, 'itIT', 'Una vittoria effimera... contro appena una frazione delle nostre forze.', '', 38134),
(193300, 'itIT', 'È un peccato che non potrai vedere la distruzione di questo regno.', '', 38134),
(193299, 'itIT', 'Affrontarmi è stata un\'idiozia.', '', 38134),
(193298, 'itIT', 'Uno sforzo così inutile.', '', 38134),
(193297, 'itIT', 'Il Bastione sarà raso al suolo!', '', 38134),
(193296, 'itIT', 'Ahah! Compattezza e tinta verde già presente! Sì! Mi farà risparmiare tempo.', '', 38134),
(193295, 'itIT', 'Esile, ma traboccante di energia arcana! Oooh, ah, magnifico...', '', 38134),
(193294, 'itIT', 'Una tale rigenerazione! Oooh, devo averla!', '', 38134),
(193293, 'itIT', 'Corna sulla testa, uuuh, lo adoro.', '', 38134),
(193291, 'itIT', 'Quelle spalle potrebbero sopportare tantissime braccia.', '', 38134),
(193290, 'itIT', 'Parti trasformabili? Due in uno! Geniale!', '', 38134),
(193289, 'itIT', 'Tentacoli e code. Oooh, se posso usarli.', '', 38134),
(193288, 'itIT', 'Mani piccole e occhi grandi. Sì, sì, sì! Perfetti per un lavoro di rifinitura.', '', 38134),
(193287, 'itIT', 'Che orecchie favolose! Sono così difficili da trovare.', '', 38134),
(193286, 'itIT', 'Forza e robustezza, ottime per la fanteria!', '', 38134),
(193285, 'itIT', 'Una costruzione semplice, ma con un vasto potenziale.', '', 38134),
(193284, 'itIT', 'Allontanati prima che i denti aguzzi si accorgano che stai parlando con me e andrà tutto liscio come l\'olio.', '', 38134),
(193282, 'itIT', 'La vostra carne ha un potenziale così grande!', '', 38134),
(193281, 'itIT', 'I miei costrutti possono occuparsi di voi da soli.', '', 38134),
(193280, 'itIT', 'Imbecille! Sono io che ti ho creato!', '', 38134),
(193279, 'itIT', 'Vado terribilmente fiero di questo qui!', '', 38134),
(193278, 'itIT', 'Cucirò la tua carne!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193277, 'itIT', '', 'Col tempo, la mia precedente casata potrà considerarci degli alleati. Fino ad allora faremo ciò che serve per assisterla.', 38134),
(193276, 'itIT', 'Devo conservare i vostri pezzi per dopo!', '', 38134),
(193275, 'itIT', 'Non avete alcun rispetto per l\'artigianato?!?', '', 38134),
(193274, 'itIT', 'Quello era il mio preferito!', '', 38134),
(193273, 'itIT', '', 'Le sentinelle della Casata dei Rituali tengono sotto sorveglianza questa zona. Non troveranno alcun segreto qui. Distruggile.', 38134),
(193272, 'itIT', 'Il meglio... doveva ancora... venire...', '', 38134),
(193271, 'itIT', 'Non avevo così tanti pezzi di ricambio da secoli!', '', 38134),
(193270, 'itIT', 'Così tanto materiale da lavoro!', '', 38134),
(193269, 'itIT', 'Non vedo l\'ora di utilizzarti.', '', 38134),
(193267, 'itIT', 'No! Ora dovrò ricostruire quei due da zero! Sembra che debba prendere in mano la situazione.', '', 38134),
(193266, 'itIT', 'Aspetta, sei ancora in vita? Quindi non hai ancora una Pietra del Peccato o ci stai ancora lavorando?', '', 38134),
(193265, 'itIT', 'Ci sono altri corpi vivi in arrivo da quel cancello? I denti aguzzi vorrebbero tanto ricevere altre anime, ma credo che le vogliano morte prima di tutto.', '', 38134),
(193264, 'itIT', 'Se mi beccano a parlare durante il mio turno sarò un sacco di sangue ambulante o finirò nella melma. Quindi non farmi scoprire.', '', 38134),
(193263, 'itIT', 'Non infastidire i denti aguzzi. Sono su di giri perché il capo in cima sta facendo loro pressioni.', '', 38134),
(193262, 'itIT', 'Questo è un bel lavoro. Non rovinarmelo.', '', 38134),
(193261, 'itIT', 'Non lasciare che i capi ci vedano parlare. Non voglio essere smelmato.', '', 38134),
(193260, 'itIT', 'Queste anime sono così vicine alla loro ultima sentenza che stanno emettendo regolarmente manifestazioni sempre più pericolose.\n\nCi servono altre anime, anime fresche, se vogliamo raggiungere la quota che il Maestro e il Tributiere ci stanno chiedendo.', 'Queste anime sono così vicine alla loro ultima sentenza che stanno emettendo regolarmente manifestazioni sempre più pericolose.\n\nCi servono altre anime, anime fresche, se vogliamo raggiungere la quota che il Maestro e il Tributiere ci stanno chiedendo.', 38134),
(193258, 'itIT', 'Per ordine del Tributiere, tutti i Venthyr di questo distretto devono presentare una quota d\'Animum o essere sottoposti a sanzioni, fino all\'esilio nel Distretto delle Braci.\n\nNon interferire con il processo di estrazione.', '', 38134),
(193256, 'itIT', '', 'Codarda! Farò a pezzi la tua carcassa e la getterò nell\'oblio!', 38134),
(193255, 'itIT', 'Venite, assistenti! Questi materiali grezzi devono essere lavorati!', '', 38134),
(193254, 'itIT', '', 'Hai interferito abbastanza! Affrontami, così che possa schiacciarti una volta per tutte!', 38134),
(193253, 'itIT', '', 'Il tuo arrivo ci dona speranza.\n\nNon abbiamo molto da offrire, ma serviti pure. Ridaremo lustro a Selvarden.', 38134),
(193252, 'itIT', '', 'Inutile marmaglia! Dove sono i miei veri bersagli?', 38134),
(193251, 'itIT', '', 'Il massacro continua!', 38134),
(193250, 'itIT', '', 'Tutti quelli che mi ostacolano moriranno!', 38134),
(193249, 'itIT', '', 'Che peccato, mi piaceva.', 38134),
(193248, 'itIT', '', 'Un altro nome eliminato dall\'elenco!', 38134),
(193246, 'itIT', '', 'L\'elenco si riduce.', 38134),
(193245, 'itIT', '', 'Parla con me quando vuoi procedere, abitante di Maldraxxus. Non farmi perdere tempo.', 38134),
(193243, 'itIT', 'Il Principe Renathal ha aperto la Corte delle Braci per impressionare gli abitanti più influenti delle Terretetre. \n\nSe i nostri ospiti se ne andranno dalla festa infelici, avremo mancato il nostro obiettivo... e mancare l\'obiettivo di una corte non è accettabile, per un Alfiere della Festa.\n\nInoltre, ogni ospite lascerà un tributo, andandosene dalla corte.\n\nOspiti più felici lasciano tributi più corposi!', '', 38134),
(193240, 'itIT', 'Ospiti diversi preferiscono tipi di corte diversi. Alcuni di essi amano le ambientazioni eleganti e formali, mentre altri preferiscono atmosfere informali o pericolose.\n\nIl nostro lavoro, come organizzatori della Corte delle Braci, è fare le scelte giuste per creare un\'atmosfera che piaccia ai nostri ospiti. Possiamo farlo scegliendo dei servizi da offrire loro, come un\'elegante ora del tè o un intrattenimento musicale, e attraverso le nostre stesse azioni.\n\nProva a fare delle scelte durante la Corte delle Braci e vedi cosa succede. Se non sei $gsicuro:sicura; del tipo di corte preferita da un ospite, controlla la Lista degli Ospiti!', '', 38134),
(193239, 'itIT', 'I Venthyr da sempre usano corti stravaganti e feste decadenti per impressionare i potenziali alleati e accrescere la propria influenza su Revendreth.\n\nIl Principe Renathal tiene la sua corte qui, nel Distretto delle Braci, in cima a Oblio del Peccato, proprio per questo motivo: espandere l\'influenza della nostra Congrega.\n\nConquistando le personalità più in vista e influenti di Revendreth e di tutte le Terretetre, ci affermeremo come la principale fonte di potere di Revendreth.\n\nNaturalmente, con così tante personalità diverse presenti alla nostra Corte delle Braci, sarà difficile rendere tutti felici.', '', 38134),
(193233, 'itIT', 'Siamo nel bel mezzo delle prove!', 'Siamo nel bel mezzo delle prove!', 38134),
(193220, 'itIT', 'Stare dentro è ben più sostenibile.', '', 38134),
(193216, 'itIT', '', 'Le Guglie obbediscono al mio comando!', 38134),
(193215, 'itIT', '', 'Perite tra le nuvole!', 38134),
(193214, 'itIT', '', 'Il vento ruggisce furioso!', 38134),
(193213, 'itIT', '', 'Sparite da questo luogo!', 38134),
(193212, 'itIT', '', 'No! La via... va... abbandonata...', 38134),
(193210, 'itIT', '', 'Così si conclude il regime fuorviante dell\'Arconte.', 38134),
(193209, 'itIT', '', 'Chiunque serva i Kyrian deve cadere!', 38134),
(193208, 'itIT', '', 'Il tuo destino è segnato!', 38134),
(193207, 'itIT', '', 'L\'epoca degli Indegni è vicina!', 38134),
(193202, 'itIT', 'Tsk tsk, piccolo Loa. Bwonsamdi ha nu piano per i seguaci tuoi.', '', 38134),
(193201, 'itIT', 'Guarda nu poco chi ha fatto ritorno alla sua piccola necropoli. Hai paura che i seguaci miei la buttino giù?', '', 38134),
(193178, 'itIT', '', 'Il Margravio è nei guai!', 38134),
(193164, 'itIT', '', 'Non posso credere che Emeni sia tornata!', 38134),
(193154, 'itIT', '', 'Era ora. Fa\' attenzione a non fidarti di lui, cacciatore.', 38134),
(193151, 'itIT', 'Oh ma dai, nun è la Regina che voglio vedere...', '', 38134),
(193150, 'itIT', '', 'Alla Regina non interessano i tuoi \"patti\", Bwonsamdi.', 38134),
(193147, 'itIT', 'I Costrutti sono sicuramente molto persistenti, ma giuro sulla Luce che li elimineremo dal Bastione.', '', 38134),
(193146, 'itIT', '', 'Finalmente l\'oscurità si dirada dal mio tempio... Tuttavia, sento che non manca molto prima che ritorni.', 38134),
(193144, 'itIT', 'Tutta questa violenza... Ne varrà la pena?', 'Tutta questa violenza ne varrà la pena?', 38134),
(193143, 'itIT', '', 'I cieli sono liberi! Raggiungetemi all\'approdo occidentale, la vendetta è a portata di mano.', 38134),
(193140, 'itIT', 'I Kyrian cadranno!', '', 38134),
(193138, 'itIT', '', 'Il veleno impesta l\'aria... e i tuoi polmoni saranno impregnati del tuo sangue!', 38134),
(193136, 'itIT', '', 'Soffocheremo i cieli!', 38134),
(193131, 'itIT', 'Ossartiglio vi ridurrà in cenere!', '', 38134),
(193128, 'itIT', 'Credo che stia facendo un costrutto.', 'Credo che stia facendo un costrutto.', 38134),
(193126, 'itIT', 'Un ultimo atto di servizio!', '', 38134),
(193125, 'itIT', 'I caduti serviranno ancora!', '', 38134),
(193121, 'itIT', 'Egli ci... userà... tutti...', '', 38134),
(193120, 'itIT', 'Altri corpi da mietere. Nalthor sarà soddisfatto.', '', 38134),
(193119, 'itIT', 'Sì, tu andrai benissimo.', '', 38134),
(193115, 'itIT', 'Un altro soldato tra le mie schiere.', '', 38134),
(193111, 'itIT', 'Non importa! I miei servitori sono infiniti!', '', 38134),
(193110, 'itIT', 'Qualcosa... sta... arrivando...', '', 38134),
(193109, 'itIT', 'Piagaossa... ha fallito...', '', 38134),
(193102, 'itIT', '', 'Sgomberate i cieli, Maldraxxi. Ci incontreremo all\'approdo occidentale quando avrete finito.', 38134),
(193101, 'itIT', 'Piagaossa fa fiero padrone.', '', 38134),
(193100, 'itIT', 'Debole esserino!', '', 38134),
(193099, 'itIT', 'Spezzati! Battuti!', '', 38134),
(193098, 'itIT', 'Piagaossa fa voi a pezzi!', '', 38134),
(193087, 'itIT', '', 'Le sta costruendo un nuovo corpo!', 38134),
(193086, 'itIT', 'Non avete speranze contro il mio potere!', '', 38134),
(193081, 'itIT', '', 'I mostri al suo interno sono troppo potenti per una persona sola. Sgombera i cieli per me... Poi avrò il loro destino in pugno.', 38134),
(193080, 'itIT', 'Ho Animum da vendere!', '', 38134),
(193079, 'itIT', 'Ce ne occuperemo, Eletta.', '', 38134),
(193078, 'itIT', 'L\'Animum trabocca!', '', 38134),
(193077, 'itIT', '', 'La tua congrega parla molto bene di te, Calcafauce. Non deludermi.', 38134),
(193075, 'itIT', '', 'La mia pazienza è finita. Non perdiamo altro tempo.', 38134),
(193074, 'itIT', '', 'I vostri dannati necromanti hanno avvolto il santuario interno in una nuvola tossica. Possono raggiungere solo l\'ingresso.', 38134),
(193073, 'itIT', 'Sentite il peso dei vostri peccati.', '', 38134),
(193072, 'itIT', 'Non meriti il tuo orgoglio.', '', 38134),
(193071, 'itIT', 'L\'orgoglio è la tua rovina!', '', 38134),
(193069, 'itIT', 'Ammirate la potenza dei Venthyr!', '', 38134),
(193068, 'itIT', 'Patetici miserabili!', '', 38134),
(193064, 'itIT', 'Sto perdendo la pazienza!', '', 38134),
(193063, 'itIT', 'Non valete il mio disprezzo!', '', 38134),
(193062, 'itIT', 'Fuori dai piedi.', '', 38134),
(193061, 'itIT', 'Inutili... servi...', '', 38134),
(193060, 'itIT', 'Se penso che qualcuno del mio rango deve scendere a tali livelli...', '', 38134),
(193056, 'itIT', 'Chissà che cosa avrà visto in te il Maestro.', '', 38134),
(193055, 'itIT', 'Uno spreco dei miei talenti.', '', 38134),
(193054, 'itIT', 'Non eri alla mia altezza.', '', 38134),
(193053, 'itIT', 'Sparite, plebaglia!', '', 38134),
(193052, 'itIT', 'Mi sono stancato di voi zotici. Sembra che dovrò sistemarvi io stesso.', '', 38134),
(193051, 'itIT', '', 'Scudo Divino ti proteggerà contro la magia oscura dell\'altare.', 38134),
(193048, 'itIT', 'Cumpà, torna con me alla necropoli. Piglieremo qualcosa di speciale per la Regina dell\'Inverno.', '', 38134),
(193021, 'itIT', 'E l\'avranno. Ma l\'assalto alla Casata dei Costrutti non è un compito semplice. Ci serve tempo per preparare le nostre forze.', '', 38134),
(193020, 'itIT', '', 'Senza le forze di Maldraxxus, il Coraggio potrà recuperare le forze. La mia gente griderà vendetta contro i costrutti.', 38134),
(193018, 'itIT', 'Io... diventerò... più forte di prima...', '', 38134),
(193017, 'itIT', 'Ti strapperò la pelle!', '', 38134),
(193016, 'itIT', 'C\'è stato un tempo in cui ho ospitato delle feste meravigliose in questa torre. Vi si poteva vedere anche il Maestro, mentre si godeva il tempo e sorrideva. Era magnifico da ammirare all\'epoca, il vero sire dei Venthyr.\n\nMi chiedo da quanto tempo stesse cospirando con il Carceriere... Se persino allora il suo sorriso fosse una maschera per nascondere il suo disgusto per tutta la realtà.', '', 38134),
(193015, 'itIT', '', 'E sia. Sentirò ciò che Renathal ha da dire. Se non altro, forse, riuscirò a porre fine a quest\'insensata lotta che costringe i miei figli a combattere fra loro.', 38134),
(193014, 'itIT', 'Io servo Revendreth, Madre della pietra. In questo momento, il Principe Oscuro è l\'unico che ha un piano vincente. Grazie all\'aiuto del Calcafauce riusciremo a infrangere la cieca devozione del Maestro al Carceriere e alla Fauce.', '', 38134),
(193013, 'itIT', '', 'E quindi Draven, continui a servire il Principe Renathal, nonostante tutti i suoi fallimenti.', 38134),
(193012, 'itIT', '', 'Hai limitato la tua rabbia quanto basta per attraversare il ponte, nonostante coloro che ti ostacolavano; questo è un passo avanti.\n\nÈ anche l\'unica volta che ti permetterò di entrare ancora nel mio dominio, quindi dimmi la ragione della tua intrusione.', 38134),
(193011, 'itIT', 'Fa\' attenzione. Anche se abbiamo completato la prova non ci vorrà molto prima che i Litogeni vengano a prendere le nostre teste.', '', 38134),
(193005, 'itIT', 'Sorella, non ho mai voluto ucciderti. Per favore, non farmelo fare.', '', 38134),
(193004, 'itIT', '', 'Kaal avrebbe dovuto permettermi di ucciderti molto tempo fa, $gtraditore:traditrice;!', 38134),
(192996, 'itIT', 'Se vi servirà un trasporto specializzato per riportarvi a casa, vi sarà fornito!', '', 38134),
(192984, 'itIT', 'Suppongo che il discorso di ringraziamento di questa corte inaugurale debba essere indirizzato $gal:alla; Calcafauce. Se l\'è meritato.', '', 38134),
(192983, 'itIT', '', 'Dovete tutti affrontare la vostra punizione.', 38134),
(192982, 'itIT', '', 'Il loro dolore chiarirà tutto.', 38134),
(192980, 'itIT', 'Col tempo questa corte ci permetterà di conquistare i cuori e le menti di personalità da tutte le Terretetre!', '', 38134),
(192979, 'itIT', '', 'Il mio stormo... ha bisogno di me...', 38134),
(192978, 'itIT', 'Vi ringrazio per il contributo nell\'aver reso quest\'esercitazione un successo!', '', 38134),
(192975, 'itIT', 'Assieme guideremo Revendreth e tutte le Terretetre verso un futuro più giusto, stabile e prosperoso.', '', 38134),
(192974, 'itIT', 'Ora che ci separiamo, sappi che tu e la tua gente sarete sempre i benvenuti a Oblio del Peccato.', '', 38134),
(192972, 'itIT', 'Il tutto organizzato dal nostro Maestro degli specchi, Laurent, in un ambiente meravigliosamente all\'avanguardia!', '', 38134),
(192971, 'itIT', 'Tutto nel pieno di questa meravigliosa nostalgia. Ringrazio il Custode Ta\'saran e il Cartello Ta per essere stati così gentili!', '', 38134),
(192970, 'itIT', 'Tutto in questo ambiente tradizionale e meraviglioso, a cura di Temel, Lyn e il resto del personale. Grazie a tutti!', '', 38134),
(192969, 'itIT', 'E ci è stato ricordato il vero scopo di Revendreth quando abbiamo combattuto assieme le anime corrotte.', '', 38134),
(192968, 'itIT', 'Io per primo non vedo l\'ora di cambiarmi le vesti dopo il putiferio causato da quell\'esercito di Dragatori!', '', 38134),
(192967, 'itIT', 'E chi può dimenticare quando abbiamo respinto assieme la potenza dell\'esercito dei Litogeni del Maestro?', '', 38134),
(192966, 'itIT', 'E, ovviamente, le forze del Maestro oggi hanno imparato una lezione importante: Oblio del Peccato si erge possente!', '', 38134),
(192964, 'itIT', 'La splendida danza ispirata dalla musica dei Caliceamaro.', '', 38134),
(192963, 'itIT', 'Le bestie maestose e i gormini splendidamente verseggianti di Selvarden.', '', 38134),
(192962, 'itIT', 'I toccanti rituali guidati dalla nostra fedele campionessa della giustizia, l\'Accusatrice!', '', 38134),
(192961, 'itIT', 'TBD-REfreshment4', '', 38134),
(192960, 'itIT', 'Gli allegri e sorprendenti funghi viventi di Maldraxxus!', '', 38134),
(192959, 'itIT', 'Le battaglie di cibo spontanee e selvagge durante il rinfresco.', '', 38134),
(192958, 'itIT', 'Il tè rilassante fornito dai nostri cari amici Teotar e Tubbins!', '', 38134),
(192957, 'itIT', '', 'Che una pioggia di giustizia purifichi queste terre!', 38134),
(192956, 'itIT', 'Ho adorato ogni momento condiviso oggi...', '', 38134),
(192955, 'itIT', 'Miei cari amici, mi duole annunciare che questa sessione di corte è quasi giunta al termine.', '', 38134),
(192953, 'itIT', 'Con discrezione. Non c\'è bisogno di rivelare i nostri margini. Sono stato chiaro?', 'Con discrezione. Non c\'è bisogno di rivelare i nostri margini. Sono stata chiara?', 38134),
(192951, 'itIT', 'Parlerò con Ta\'saran. Il suo cartello potrebbe essere in grado di aiutarci.', 'Parlerò con Ta\'saran. Il suo cartello potrebbe essere in grado di aiutarci.', 38134),
(192950, 'itIT', 'Ba\'solo, dobbiamo espandere la portata delle nostre reti.', 'Ba\'solo, dobbiamo espandere la portata delle nostre reti.', 38134),
(192949, 'itIT', 'Devo fare rapporto sul fatto che le richieste dei mortali stiano spingendo al limite le capacità dei nostri portali.', 'Devo fare rapporto sul fatto che le richieste dei mortali stiano spingendo al limite le capacità dei nostri portali.', 38134),
(192944, 'itIT', 'E che voi siate così intelligente da trasformare i loro desideri in profitto.', 'E che voi siate così intelligente da trasformare i loro desideri in profitto.', 38134),
(192943, 'itIT', 'Fortuna per noi che hanno un appetito così vorace.', 'Fortuna per noi che hanno un appetito così vorace.', 38134),
(192942, 'itIT', 'Va tutto bene, Opulenza. I mortali continuano a consumare.', 'Va tutto bene, Opulenza. I mortali continuano a consumare.', 38134),
(192941, 'itIT', 'Come procedono le nostre operazioni nell\'anello sopra di noi?', 'Come procedono le nostre operazioni nell\'anello sopra di noi?', 38134),
(192940, 'itIT', '', 'Gor\'groth ha parlato di pietre di protezione. Troviamole, per liberare il Capitano.', 38134),
(192934, 'itIT', 'Dobbiamo adottare delle misure appropriate per placarli. Niente può interferire con l\'operazione.', 'Dobbiamo adottare delle misure appropriate per placarli. Niente può interferire con l\'operazione.', 38134),
(192931, 'itIT', 'Opulenza, devo riferirle che alcuni dei sorveglianti sono irrequieti riguardo alle nostre attività.', 'Opulenza, devo riferirle che alcuni dei sorveglianti sono irrequieti riguardo alle nostre attività.', 38134),
(192930, 'itIT', '', 'E ora potrete unirvi alla mia congrega.', 38134),
(192929, 'itIT', 'Naturalmente, Opulenza.', 'Naturalmente, Opulenza.', 38134),
(192928, 'itIT', 'Quando guadagno qualcosa, tutti noi guadagniamo qualcosa. Ognuno in base al proprio ruolo.', 'Quando guadagno qualcosa, tutti noi guadagniamo qualcosa. Ognuno in base al proprio ruolo.', 38134),
(192927, 'itIT', 'Potremmo ancora trarre profitto da questi tentativi.', 'Potremmo ancora trarre profitto da questi tentativi.', 38134),
(192926, 'itIT', 'Buone notizie, ma non mi aspettavo altrimenti.', 'Buone notizie, ma non mi aspettavo altrimenti.', 38134),
(192925, 'itIT', 'Opulenza, sono felice di riferire che tutto va secondo i piani.', 'Opulenza, sono felice di riferire che tutto va secondo i piani.', 38134),
(192924, 'itIT', '', 'Irredimibile.', 38134),
(192923, 'itIT', '', 'La purificazione attende tutti voi.', 38134),
(192922, 'itIT', 'L\'Alfiere della Festa colpisce ancora!', '', 38134),
(192921, 'itIT', '', 'La vostra penitenza si avvicina!', 38134),
(192919, 'itIT', 'Abbiamo conquistato il perimetro. Riuniamoci con l\'Eletta.', '', 38134),
(192918, 'itIT', 'Dovrai congedare il tuo famiglio attuale prima di poterne addomesticare un altro. Potrai richiamarlo più tardi.', '', 38134),
(192917, 'itIT', 'Il baluardo è più avanti... Insieme alla sua guardia. Sarebbe saggio invocare l\'Eletta.', '', 38134),
(192916, 'itIT', 'Non... può... essere...', '', 38134),
(192908, 'itIT', 'Il baluardo è indifeso? Resta vigile.', '', 38134),
(192906, 'itIT', 'Un\'imboscata! Abbattili!', '', 38134),
(192902, 'itIT', '', 'Urla se hai bisogno che venga a salvarti.', 38134),
(192898, 'itIT', 'La terra sanguina!', '', 38134),
(192897, 'itIT', 'Che il sangue vi colga!', '', 38134),
(192896, 'itIT', 'Annegate nel sangue!', '', 38134),
(192895, 'itIT', 'Non muovetevi, mortali!', '', 38134),
(192894, 'itIT', 'La carne è debole!', '', 38134),
(192893, 'itIT', 'Il vostro destino è scolpito nella pietra!', '', 38134),
(192892, 'itIT', 'Obbeditemi, servitori!', '', 38134),
(192889, 'itIT', 'Santo cielo! Questi Spriggan stanno facendo una tale baraonda!', 'Santo cielo! Questi Spriggan stanno facendo una tale baraonda!', 38134),
(192887, 'itIT', '', 'La tua invidia è palpabile.', 38134),
(192885, 'itIT', '', 'Stai parlando con una principessa!', 38134),
(192884, 'itIT', 'Magnifico! Sei un vero portento, devo solo lavorare un po\' di più sulla durata...', '', 38134),
(192877, 'itIT', 'Siamo soci del cartello, o come piace dire ai mortali... una famiglia.', 'Siamo soci del cartello, o come piace dire ai mortali... una famiglia.', 38134),
(192876, 'itIT', 'Ricordamelo: perché lo copriamo ancora?', 'Ricordamelo: perché lo copriamo ancora?', 38134),
(192875, 'itIT', 'Certamente, Matriarca.', 'Certamente, Matriarca.', 38134),
(192874, 'itIT', 'Allora vado. Mi aspetto che mi avvisiate se si fa vedere.', 'Allora vado. Mi aspetto che mi avvisiate se si fa vedere.', 38134),
(192873, 'itIT', 'Mai.', 'Mai.', 38134),
(192872, 'itIT', 'Non oseremmo mai, Matriarca.', 'Non oseremmo mai, Matriarca.', 38134),
(192871, 'itIT', 'Meglio che non lo stiate coprendo!', 'Meglio che non lo stiate coprendo!', 38134),
(192870, 'itIT', 'Nemmeno io, Matriarca.', 'Nemmeno io, Matriarca.', 38134),
(192869, 'itIT', 'No, Matriarca. Non l\'ho visto.', 'No, Matriarca. Non l\'ho visto.', 38134),
(192868, 'itIT', 'Risparmiami i convenevoli. Avete visto Ta\'vini?', 'Risparmiami i convenevoli. Avete visto Ta\'vini?', 38134),
(192867, 'itIT', 'Benvenuta, Matriarca Ta\'dolla!', 'Benvenuta, Matriarca Ta\'dolla!', 38134),
(192854, 'itIT', '', 'Una spina nel fianco fino all\'ultimo. Non avrei voluto diversamente.', 38134),
(192853, 'itIT', '', 'Dama Baccaluna! Non ti farei mai uno scherzo simile!', 38134),
(192851, 'itIT', '', 'Se le forze di Maldraxxus si dimostreranno troppo dure per te, invoca il mio nome. Me ne occuperò di persona.', 38134),
(192845, 'itIT', 'Vittoria per Oblio del Peccato! Per le Terretetre!', '', 38134),
(192844, 'itIT', 'Il nemico è in ritirata! Vittoria per Oblio del Peccato!', '', 38134),
(192842, 'itIT', 'Goditi questa dimostrazione dal vivo delle capacità di combattimento della nostra organizzazione.', '', 38134),
(192839, 'itIT', 'Non temete, miei stimati ospiti. Ci occuperemo subito di questi nemici.', '', 38134),
(192838, 'itIT', 'Credi... Credi che possa chiedergli l\'autografo? Pensi che possa insegnarmi alcune tecniche di caccia?', '', 38134),
(192837, 'itIT', 'È lui! Il Cacciatore Cornuto, proprio qui! Mi sto comportando al meglio, te l\'assicuro. Non sfuggirà nulla ai miei occhi vigili! O ai suoi! Anche i suoi sono super vigili...', '', 38134),
(192836, 'itIT', 'Bene, Reldorn, stai calmo. Si trova di fronte a te. Non fare lo stupido...$b$bOh, non ti avevo $gvisto:vista;! Cosa ti porta dal Cacciatore Selvaggio? Cioè, il Cornuto... Selvaggio... Cosa vuoi?', '', 38134),
(192835, 'itIT', 'Nemici in arrivo! Proteggete gli ospiti a ogni costo!', '', 38134),
(192833, 'itIT', 'Calcafauce, al mio fianco! Respingeremo questa minaccia assieme!', '', 38134),
(192832, 'itIT', 'La tua patetica corte si inchinerà al Maestro!', '', 38134),
(192829, 'itIT', 'Dietro tutto quel fogliame, dobbiamo entrare là. Usa i semi per aprirci la strada!', '', 38134),
(192823, 'itIT', 'E neanche noi.', '', 38134),
(192821, 'itIT', '', 'Avevamo quasi riconquistato il tempio prima che la vostra gente ritornasse. Non avrò pace finché non sarà finita.', 38134),
(192819, 'itIT', '', 'Oooh, questi sono una bomba! Tiriamone altri!', 38134),
(192818, 'itIT', 'Ho apportato un paio di... modifiche al mio parasole personale perché assorba l\'Animum. Fagli fare un giro!', '', 38134),
(192817, 'itIT', 'Dimmi, $C. Questo esploratore non ti sembra un po\'... distratto? Non ha quasi proferito parola da quando sono arrivato.', '', 38134),
(192816, 'itIT', 'E quindi il Principe Oscuro sta facendo la sua mossa per usurpare il trono? Suppongo che una persona di carattere dovrebbe fermarlo.', '', 38134),
(192815, 'itIT', 'Cos\'è questo?', '', 38134),
(192812, 'itIT', 'Mai. Ma fa parte del cartello, dopotutto.', 'Mai. Ma fa parte del cartello, dopotutto.', 38134),
(192811, 'itIT', 'Quand\'è che ha mai fatto ciò che hai chiesto?', 'Quand\'è che ha mai fatto ciò che hai chiesto?', 38134),
(192809, 'itIT', 'E stai alla larga dalla mia rete di portali!', 'E stai alla larga dalla mia rete di portali!', 38134),
(192808, 'itIT', 'Portale sbagliato! Riportami indietro. Se la Matriarca Ta\'dolla te lo chiede, tu non mi hai visto!', 'Portale sbagliato! Riportami indietro. Se la Matriarca Ta\'dolla te lo chiede, tu non mi hai vista!', 38134),
(192807, 'itIT', 'Ancora, Ta\'vini? Ne abbiamo già parlato.', 'Ancora, Ta\'vini? Ne abbiamo già parlato.', 38134),
(192806, 'itIT', 'Ta\'vini?', 'Ta\'vini?', 38134),
(192804, 'itIT', '', 'Quella polvere ci permetterà di riconoscere le loro illusioni!', 38134),
(192803, 'itIT', 'Dovresti vedere alcune delle cose che mettono nella Banca Eterea.', 'Dovresti vedere alcune delle cose che mettono nella Banca Eterea.', 38134),
(192802, 'itIT', 'Questi mortali sono davvero strani.', 'Questi mortali sono davvero strani.', 38134),
(192801, 'itIT', 'I mortali cercano ogni genere di cose. Forse la vorranno tenere come mascotte?', 'I mortali cercano ogni genere di cose. Forse la vorranno tenere come mascotte?', 38134),
(192800, 'itIT', 'Quella cosa non proveniva da Maldraxxus? Perché l\'hai portata qui?', 'Quella cosa non proveniva da Maldraxxus? Perché l\'hai portata qui?', 38134),
(192799, 'itIT', 'Se lo dici tu.', 'Se lo dici tu.', 38134),
(192798, 'itIT', 'Ne dubito, in confronto noi offriamo loro una vita lussuosa.', 'Ne dubito, in confronto noi offriamo loro una vita lussuosa.', 38134),
(192797, 'itIT', 'Forse sentiranno la mancanza della carne e delle poltiglie?', 'Forse sentiranno la mancanza della carne e delle poltiglie?', 38134),
(192796, 'itIT', 'Stiamo facendo loro un favore a tirarle fuori da quel posto.', 'Stiamo facendo loro un favore a tirarle fuori da quel posto.', 38134),
(192795, 'itIT', 'Un\'altra da Maldraxxus. Inconfondibile.', 'Un\'altra da Maldraxxus. Inconfondibile.', 38134),
(192794, 'itIT', 'X', '', 38134),
(192792, 'itIT', '', 'La tua forza non conosce eguali, Calcafauce.', 38134),
(192791, 'itIT', '', 'Non verremo respinti! Maldraxxus resisterà!', 38134),
(192790, 'itIT', 'Il Bastione vi ringrazia, vostra grazia.', '', 38134),
(192787, 'itIT', '', 'Stavo meditando sulla mia vita passata e ho ricordato qualcosa d\'importantissimo. Questa registrazione servirà da promemoria, per gli eoni che verranno.', 38134),
(192786, 'itIT', '', 'Non ero una creatrice prima di venire al Bastione. In effetti, oltre l\'arte della guerra non avevo alcuna abilità di grande valore. Ho servito la mia gente, sì, ma a quale costo?', 38134),
(192785, 'itIT', '', 'Verso la fine della mia vita, ho compreso che me ne sarei andata lasciando dietro di me solo il vuoto. L\'unico dono che ho fatto è stato la morte.', 38134),
(192784, 'itIT', '', 'La mia anima si è risvegliata al Bastione. Ho imparato a usare la forgia. Quell\'impulso che sentivo in vita ha preso una nuova forma: potevo creare, invece di distruggere.', 38134),
(192783, 'itIT', 'Fa tutto parte del lavoro. Voglio che siano felici!', 'Fa tutto parte del lavoro. Voglio che siano felici!', 38134),
(192782, 'itIT', 'Non ti stanchi mai di dire la stessa cosa a ogni creatura che rinchiudi?', 'Non ti stanchi mai di dire la stessa cosa a ogni creatura che rinchiudi?', 38134),
(192781, 'itIT', 'Già. Ora metti questa nel magazzino.', 'Già. Ora metti questa nel magazzino.', 38134),
(192780, 'itIT', 'E sono davvero avidi.', 'E sono davvero avidi.', 38134),
(192779, 'itIT', 'Senza dubbio fanno affidamento sulle risorse raccolte dagli altri esseri.', 'Senza dubbio fanno affidamento sulle risorse raccolte dagli altri esseri.', 38134),
(192778, 'itIT', 'La varietà di creature che i mortali frequentano mi stupisce.', 'La varietà di creature che i mortali frequentano mi stupisce.', 38134),
(192777, 'itIT', 'Curioso. Gli archivi sono fortunati a ottenere una tale testimonianza. Ti ringrazio per i tuoi sforzi.', '', 38134),
(192776, 'itIT', 'Gli Indegni hanno quasi distrutto l\'eredità di questo luogo. Cerca qualsiasi scrittura tu riesca a trovare, così potremmo preservarla.', '', 38134),
(192775, 'itIT', 'Incontriamoci al Tempio del Coraggio. È ora di fare giustizia.', '', 38134),
(192773, 'itIT', 'I Costrutti hanno inviato dei rinforzi al Bastione. Dobbiamo aiutare i Kyrian a cacciarli via.', '', 38134),
(192750, 'itIT', '', 'Dicono che il Maestro abbia tenuto il Distretto del Villaggio in miseria di proposito. Coloro che un tempo erano ricchi sono stati costretti a guardare in faccia la vera ricchezza appena oltre la loro portata.\n\nDisgustoso, anche se è un buon posto per sfidare gli avversari.', 38134),
(192720, 'itIT', 'Ritirata! Ripiegare!', 'Ritirata! Ripiegare!', 38134),
(192719, 'itIT', 'Ne prendo nota.', 'Ne prendo nota.', 38134),
(192718, 'itIT', 'Non evochiamo mai più da quel regno!', 'Non evochiamo mai più da quel regno!', 38134),
(192717, 'itIT', 'Ecco fatto!', 'Ecco fatto!', 38134),
(192716, 'itIT', 'PRESTO!', 'PRESTO!', 38134),
(192715, 'itIT', 'Inversione di flusso.', 'Inversione di flusso.', 38134),
(192714, 'itIT', 'PORTALA VIA DA QUI!', 'PORTALA VIA DA QUI!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192713, 'itIT', 'Cos\'è quella cosa! Sembra pericolosa!', 'Cos\'è quella cosa! Sembra pericolosa!', 38134),
(192710, 'itIT', 'Comunque dovrebbe andare. Impacchetta tutto.', 'Comunque dovrebbe andare. Impacchetta tutto.', 38134),
(192709, 'itIT', 'Non di questo colore. Immagino che il colore e le gambe possano variare!', 'Non di questo colore. Immagino che il colore e le gambe possano variare!', 38134),
(192708, 'itIT', 'Pensavo che in passato avessimo avuto uno di questi?', 'Pensavo che in passato avessimo avuto uno di questi?', 38134),
(192707, 'itIT', 'Eccone un altro con otto gambe! Però non avevo mai visto questa combinazione prima.', 'Eccone un altro con otto gambe! Però non avevo mai visto questa combinazione prima.', 38134),
(192702, 'itIT', 'Uccidete gli invasori!', '', 38134),
(192701, 'itIT', 'Al mio fianco!', '', 38134),
(192700, 'itIT', 'Polvere alla polvere!', '', 38134),
(192699, 'itIT', 'Sarete schiacciati!', '', 38134),
(192698, 'itIT', '', 'Bel lavoro, riproviamo. Devo essere certa che tu possa affrontare i Verrospino.', 38134),
(192695, 'itIT', '', 'Eccellente, proviamo un\'ultima volta. Fuoco Lunare e Ira Silvana ti aiuteranno ad affrontare i Verrospino.', 38134),
(192692, 'itIT', 'Lo farò.', 'Lo farò.', 38134),
(192691, 'itIT', 'È un ottimo esempio del suo genere. Occupatene.', 'È un ottimo esempio del suo genere. Occupatene.', 38134),
(192690, 'itIT', '', 'Ottimo, ora fallo ancora! Parola d\'Ombra: Dolore continuerà a danneggiare il nemico mentre lo attacchi con Punizione.', 38134),
(192688, 'itIT', 'Nella scatola, forza. Non preoccuparti, è più grande all\'interno.', 'Nella scatola, forza. Non preoccuparti, è più grande all\'interno.', 38134),
(192686, 'itIT', 'Che carino!', 'Che carino!', 38134),
(192685, 'itIT', '', 'L\'orgoglio e l\'arroganza di quest\'anima sono leggendari. Vieni, sottraiamola a Denathrius.', 38134),
(192680, 'itIT', 'Morte dall\'alto!', '', 38134),
(192679, 'itIT', 'Feccia, dal primo all\'ultimo.', '', 38134),
(192678, 'itIT', 'Il tuo fallimento è totale.', '', 38134),
(192677, 'itIT', 'Non hai mai avuto speranze.', '', 38134),
(192676, 'itIT', 'Finalmente un po\' di silenzio.', '', 38134),
(192675, 'itIT', 'Sparisci, feccia ribelle!', '', 38134),
(192673, 'itIT', '', 'Salve. Io sono Soren.$b$bHai l\'aspetto di chi sa combattere. Benissimo. Ci sarà molto da combattere prima che avremo finito.', 38134),
(192672, 'itIT', 'Ops.', '', 38134),
(192671, 'itIT', 'Quello che è accaduto al Bastione è un\'onta sul nostro onore. Dobbiamo fare tutto il possibile per rimediare.', '', 38134),
(192670, 'itIT', '', 'Sono giunta qui a implorare la Dama delle Cascate per avere delle armi. Tirna Eas è dove viene realizzata la maggior parte delle nostre armi, degli strumenti e delle provviste. Qui ne sono chiaramente in sovrabbondanza.$b$bForse avrai più fortuna di me a parlarle.', 38134),
(192669, 'itIT', 'I Costrutti continuano a potenziare le loro forze al Tempio del Coraggio. Aiuta i Kyrian a eliminarle.', '', 38134),
(192667, 'itIT', '', 'Aiutami a far ragionare il Droman. Se ne sta là mentre gli altri soffrono!', 38134),
(192619, 'itIT', '', 'Dobbiamo sempre ricordare che tale è il vero scopo di Revendreth!', 38134),
(192618, 'itIT', '', 'Assieme ci assicureremo che ogni anima volenterosa sia assolta dai suoi peccati.', 38134),
(192617, 'itIT', '', 'Grazie per aver testimoniato per queste accuse oggi!', 38134),
(192616, 'itIT', 'Sono tempi duri là fuori, ne sono certo, ma devo tenere d\'occhio i piccoli.$b$bSe ti interessa fare altri acquisti, passa a salutare Je\'ni e i ragazzi dall\'altro lato del bosco.', '', 38134),
(192615, 'itIT', '$gBenvenuto:Benvenuta;. Sono Cavina, il miglior Forgiagorm di tutta Selvarden.$b$bProbabilmente anche l\'ultimo rimasto, ma pur sempre il migliore, giuro sulla Regina!', '', 38134),
(192614, 'itIT', 'I gorm sono ottimi compagni di lavoro. Ottimi materiali, ottima compagnia, ottimo cibo. Puoi usare i gorm praticamente per tutto!$b$bLe armature migliori di Selvarden? Gusci di gorm!$b$bPunte affilate che non si rompono mai? Artigli o pungiglioni di gorm!$b$bMi spiace, mi sono fatto trascinare. Mi piace un sacco il mio lavoro.', '', 38134),
(192613, 'itIT', '', 'Spero che quando imparerò la magia possa imparare la magia del ghiaccio! Bibidi! Bobidi!', 38134),
(192612, 'itIT', '', '<La voce di Marastella diventa un sussurro da palcoscenico.> A volte penso che i miei giocattoli si muovano quando pensano che non sto guardando. Credi che si tratti di magia?', 38134),
(192611, 'itIT', '', 'A volte vorrei vivere sotto il mare, ma dove vivremmo? Come respireremmo?', 38134),
(192610, 'itIT', '', '<L\'Arpista del Mare ti sussurra con tono cospiratorio.> Ehi $gsignore:signora;, vuoi vedere il mio amico?$b$b<L\'Arpista del Mare ti mostra una piccola spugna.>$b$bVive sotto il mare. Il suo migliore amico è una stella marina!', 38134),
(192608, 'itIT', '', 'Identifichiamo i fardelli che pesano di più sulla sua anima... perché possa essere corretta.', 38134),
(192607, 'itIT', '', 'Che venga la prossima anima da accusare!', 38134),
(192599, 'itIT', 'Ha parlato di una perdita inestimabile, di una vita non più riconosciuta conservata da qualche parte nei registri e nelle scritture. Ha detto che la via ha cancellato chi era!', '', 38134),
(192598, 'itIT', 'Era vicina alla fine delle sue prove. Il mio tempio era l\'ultimo che si ergeva tra lei e l\'ascensione, eppure non è stato l\'orgoglio che l\'ha fatta vacillare.', '', 38134),
(192597, 'itIT', 'Ho perso un\'Aspirante oggi. Non sono così presuntuoso da credere che il suo destino fosse saldamente sotto il mio controllo, ma anche così, la perdita è stata improvvisa... e dolorosa.', '', 38134),
(192596, 'itIT', 'Mi ritrovo ad aver bisogno di riflettere e il mio amico Thenios mi ha detto di venire qui. Ti prego, cara scrittura, allevia questo peso che mi porto dietro.', '', 38134),
(192590, 'itIT', '', 'Oh, salve ancora. Stavo giusto parlando con Tirna Eas.$b$bTi prego, continua. Non stai interrompendo. Questa conversazione durerà almeno qualche altro eone, non parliamo da molto tempo.', 38134),
(192589, 'itIT', '', 'Questa foresta, quella foresta. Che strane distinzioni. Siamo tutti foglie in un\'immensa foresta.', 38134),
(192588, 'itIT', '', 'Ci incontriamo ancora, Calcafauce... E forse ci rincontreremo.', 38134),
(192586, 'itIT', '', 'Ahhh, è bello ritornare al lavoro. Nessuno Spriggan che se la ride, nessuna manifestazione inquietante che si aggira in ogni angolo...', 38134),
(192585, 'itIT', '', 'Non è la mia officina, ma qui potrei riuscire a lavorare. Le Ragnatele dell\'Anima non si creano da sole, sai?', 38134),
(192584, 'itIT', '', 'Grazie ancora per il tuo aiuto alla Bottega della Crescita. Saremmo ancora bloccati là se non fosse per te!', 38134),
(192581, 'itIT', '', 'Sei $gpronto:pronta; ad attaccare?', 38134),
(192580, 'itIT', 'Devo dirlo, è più piacevole stare qui che vicino alla mia officina. Quei terribili Spriggan stavano facendo una gran confusione.$b$bSì, qui è piacevole e tranquillo. Finalmente posso sentire i colpi del mio martello fortunato!', '', 38134),
(192579, 'itIT', 'Sin da quando mi hai aiutato a trovare il mio martello, le cose si stanno mettendo bene. Porta davvero fortuna, vedi?', '', 38134),
(192578, 'itIT', 'Oh, $gun visitatore:una visitatrice;... Ancora tu! Che fortuna!', '', 38134),
(192577, 'itIT', '', 'Ciao, tesoro. Dimmi, come vanno le cose fuori dal mio bosco?', 38134),
(192576, 'itIT', '', 'So che sono tempi difficili per tutti a Selvarden, ma a me è stato affidato questo bosco, e mi assicurerò che resti al sicuro.', 38134),
(192575, 'itIT', '', '$GBenvenuto:Benvenuta; al Bacino Scintillante, $gviaggiatore:viaggiatrice;.$b$bRiposati, sei al sicuro qui.', 38134),
(192574, 'itIT', '', 'Guarda come brilla! Lascia che la sua voce risuoni contro il mostro generato dal vespro corrotto!', 38134),
(192573, 'itIT', '', 'Per coloro che lottano per la purificazione, questo vespro è un dono. Per coloro che hanno abbandonato il sentiero... la sua voce è un grido di terrore assordante. Hai dato loro una bella lezione.', 38134),
(192572, 'itIT', '', 'Gli Indegni hanno corrotto un\'altra volta il mio vespro più potente. Ti dono il potere di una campana minore. Contrattacca. Lascia che risuoni, così che i nostri nemici possano scoprire la verità della purezza.', 38134),
(192567, 'itIT', 'Trovi il Sapone di Dama Baccaluna sul tavolo.', 'Trovi il Sapone di Dama Baccaluna sul tavolo.', 38134),
(192566, 'itIT', 'Non interferite!', '', 38134),
(192565, 'itIT', 'State lontani!', '', 38134),
(192564, 'itIT', 'Contagio!', '', 38134),
(192563, 'itIT', 'La malattia si diffonde!', '', 38134),
(192562, 'itIT', '', 'Non... vincerete... mai...', 38134),
(192561, 'itIT', '', 'Tutto il Bastione sarà nostro!', 38134),
(192548, 'itIT', '', 'Un invito del Principe Renathal? Incantevole, non posso assolutamente mancare.', 38134),
(192545, 'itIT', '', 'Le profondità vi inghiottiranno tutti!', 38134),
(192544, 'itIT', '', 'Ti manca la forza della pietra.', 38134),
(192543, 'itIT', '', 'Nessuno fugge dalle Profondità Sanguigne. Nessuno!', 38134),
(192542, 'itIT', '', 'La ribellione vacilla.', 38134),
(192541, 'itIT', '', 'Consegnatemi il prigioniero!', 38134),
(192538, 'itIT', 'Ti prego di scusarmi, mortale. Siamo impegnati a osservare lo scorrere delle anime.', '', 38134),
(192537, 'itIT', 'Fai un lavoro ben fatto, su.', '', 38134),
(192536, 'itIT', 'Le quote devono essere raggiunte!', '', 38134),
(192534, 'itIT', 'Ehi tu, dacci dentro.', '', 38134),
(192533, 'itIT', 'Dobbiamo lavorare di più, non meglio!', '', 38134),
(192532, 'itIT', 'Nessun problema! I Dragatori migliorano la festa!', '', 38134),
(192531, 'itIT', '', 'Il suo guardaroba è di qua.', 38134),
(192530, 'itIT', '', 'Facciamo uno scherzetto a Baccaluna. È ora che ne subisca uno anche lei!', 38134),
(192525, 'itIT', '', 'Hai usato Fuoco Lunare troppo presto. Infliggerai più danni se lo usi quando è quasi scaduto.', 38134),
(192524, 'itIT', '', 'Fuoco Lunare sta svanendo, lancialo di nuovo!', 38134),
(192521, 'itIT', '', 'Fuoco Lunare infliggerà danni periodici al tuo avversario. Usa Ira Silvana mentre il nemico è afflitto da Fuoco Lunare.', 38134),
(192520, 'itIT', '', 'Bene. Continua a usare Ira Silvana mentre Fuoco Lunare infligge danni. Aspetta il momento giusto per usare di nuovo Fuoco Lunare.', 38134),
(192518, 'itIT', '', 'Usa Fuoco Lunare all\'inizio del combattimento. Infliggerà danni periodici al tuo nemico.', 38134),
(192517, 'itIT', '', 'Hai usato Parola d\'Ombra: Dolore troppo presto. Infliggerai più danni se la usi quando è quasi scaduta.', 38134),
(192516, 'itIT', '', 'Parola d\'Ombra: Dolore sta svanendo, lanciala di nuovo!', 38134),
(192514, 'itIT', '', 'Eccellente, un\'altra volta! I Sacerdoti possono indebolire gli avversari con un uso corretto di Parola d\'Ombra: Dolore.', 38134),
(192512, 'itIT', '', 'Bene! Continua a usare Punizione mentre Parola d\'Ombra: Dolore infligge danni. Aspetta il momento giusto per usare di nuovo Parola d\'Ombra: Dolore.', 38134),
(192511, 'itIT', '', 'Usa Parola d\'Ombra: Dolore all\'inizio del combattimento. Infliggerà danni periodici al nemico.', 38134),
(192464, 'itIT', 'Questi idioti non servono Revendreth. Lavoro eccellente, Calcafauce.', '', 38134),
(192463, 'itIT', 'In assenza della Cacciapaura, si è insediata una nuova guida. Uccideteli tutti!', '', 38134),
(192446, 'itIT', '', 'C\'è ancora... così tanto... da scoprire...', 38134),
(192445, 'itIT', '', 'Finalmente. Ora, Z\'rali... dov\'eravamo rimasti?', 38134),
(192444, 'itIT', '', 'Non c\'è nulla che possa imparare da te.', 38134),
(192443, 'itIT', '', 'Che seccatura.', 38134),
(192441, 'itIT', '', 'Come osate interrompere la mia ricerca!', 38134),
(192440, 'itIT', '', 'Prima mangia, Alaria. Devi rimetterti in forze.', 38134),
(192430, 'itIT', '', 'No... La Cacciapaura si fidava di me...', 38134),
(192429, 'itIT', '', 'Più carne per i miei piccoli!', 38134),
(192427, 'itIT', 'In un modo o nell\'altro confesserai.', '', 38134),
(192425, 'itIT', 'I peccatori... aspettano...', '', 38134),
(192424, 'itIT', 'Pelagos è a Selvarden per chiedere un\'udienza con la Regina. Raggiungilo là.', '', 38134),
(192423, 'itIT', 'Ci sono altre persone che necessitano delle mie attenzioni.', '', 38134),
(192422, 'itIT', 'Pentiti, mortale.', '', 38134),
(192421, 'itIT', 'Peccato, pensavo avresti resistito.', '', 38134),
(192419, 'itIT', 'Iniziamo.', '', 38134),
(192405, 'itIT', '', 'È tutto pronto.', 38134),
(192403, 'itIT', '', 'Perché mi disturbi mentre sto socializzando?', 38134),
(192402, 'itIT', 'Ora, vorrei che non ci catturassero se non ti dispiace. \n\nPer te si tratta solo di tormento eterno, morte, e poi probabilmente altro tormento eterno. \n\nPer me, be\', mi potrebbero mandare a Ultimo Limo. Non voglio finire nella melma!', '', 38134),
(192399, 'itIT', '', 'Lo ammetto, mi ha fatto assai piacere il tuo invito alla Corte delle Braci, Calcafauce.\n\nMi sono giunte molte voci deliziate delle attività svoltesi.', 38134),
(192394, 'itIT', 'Userò la tua pelle come cintura!', '', 38134),
(192389, 'itIT', 'Non... la mia faccia...', '', 38134),
(192386, 'itIT', 'Molto bene. Resta al servizio dello Scopo.', 'Molto bene. Resta al servizio dello Scopo.', 38134),
(192385, 'itIT', 'Le osservazioni sono state consegnate, Sorvegliante.', 'Le osservazioni sono state consegnate, Sorvegliante.', 38134),
(192384, 'itIT', 'Grazie mille. Hai servito bene lo Scopo.', 'Grazie mille. Hai servito bene lo Scopo.', 38134),
(192383, 'itIT', 'Scriba del Destino, qui vi sono le ultime osservazioni sul flusso delle anime.', 'Scriba del Destino, qui vi sono le ultime osservazioni sul flusso delle anime.', 38134),
(192382, 'itIT', 'Sì, Sorvegliante. Subito.', 'Sì, Sorvegliante. Subito.', 38134),
(192381, 'itIT', 'Porta queste osservazioni allo Scriba del Destino.', 'Porta queste osservazioni allo Scriba del Destino.', 38134),
(192363, 'itIT', 'Abbraccia la sfida. Conquista le tue prove.', '', 38134),
(192362, 'itIT', 'Goditi le vittorie, e mostra i tuoi risultati con orgoglio.', '', 38134),
(192361, 'itIT', 'Resta fedele al Primus.', '', 38134),
(192360, 'itIT', 'Ogni anima deve guadagnarsi il proprio posto.', '', 38134),
(192359, 'itIT', 'Maldraxxus esalta chi è forte.', '', 38134),
(192340, 'itIT', 'Non faccio altro che servire, come facciamo tutti.', 'Non faccio altro che servire, come facciamo tutti.', 38134),
(192338, 'itIT', 'La tua fortuna cresce. Lo Scopo ti sorride.', 'La tua fortuna cresce. Lo Scopo ti sorride.', 38134),
(192334, 'itIT', 'Di recente il mio Sorvegliante mi ha lodato per i miei contatti proficui con l\'emissario degli Alienatori.', 'Di recente il mio Sorvegliante mi ha lodata per i miei contatti proficui con l\'emissario degli Alienatori.', 38134),
(192333, 'itIT', 'Salute, Quartiermastro.', 'Salute, Quartiermastro.', 38134),
(192331, 'itIT', 'Credo che tutti ci sentiamo così in una certa misura. Lo Scopo ci sta mettendo alla prova.', 'Credo che tutti ci sentiamo così in una certa misura. Lo Scopo ci sta mettendo alla prova.', 38134),
(192329, 'itIT', 'Confesso che anche io ho dei dubbi.', 'Confesso che anche io ho dei dubbi.', 38134),
(192328, 'itIT', 'Lo Scopo mi lascia perplesso. Non sono sicuro di cosa pensare riguardo a questo caos.', 'Lo Scopo mi lascia perplessa. Non sono sicura di cosa pensare riguardo a questo caos.', 38134),
(192326, 'itIT', 'Sembra che non finiscano mai, dev\'essere il loro ruolo nello Scopo.', 'Sembra che non finiscano mai, dev\'essere il loro ruolo nello Scopo.', 38134),
(192325, 'itIT', 'Corretto. Anche se a volte è l\'unica cosa che posso fare per tenere a freno le loro tendenze più caotiche.', 'Corretto. Anche se a volte è l\'unica cosa che posso fare per tenere a freno le loro tendenze più caotiche.', 38134),
(192324, 'itIT', 'Il tuo compito è di comunicare con gli Alienatori, vero?', 'Il tuo compito è di comunicare con gli Alienatori, vero?', 38134),
(192300, 'itIT', '', 'Vai via, devo mangiare.', 38134),
(192298, 'itIT', '', 'Consumare! Divorare! Banchettare!', 38134),
(192297, 'itIT', 'E i tuoi mancano di efficienza, Nemea! Trascorri le tue giornate a costruire relazioni, mentre io passo le mie ad agire e fare la differenza!', '', 38134),
(192296, 'itIT', '', 'I tuoi metodi mancano di raffinatezza, Pelodis. La tua falince fa qualsiasi cosa le ordini, non c\'è addestramento né dare e ricevere.', 38134),
(192295, 'itIT', '', 'Tirna Falcis avvizzisce e i gorm banchettano. Bisogna ristabilire l\'equilibrio.', 38134),
(192294, 'itIT', '', 'Ehi... questo non va bene. Non va affatto bene!', 38134),
(192293, 'itIT', '', 'Sììì! Vinco io a questo giro!', 38134),
(192292, 'itIT', '', 'No! Te l\'ho fatta!', 38134),
(192291, 'itIT', '', 'Quella non ero io!', 38134),
(192290, 'itIT', '', 'Oh, va bene. Niente più nebbia.', 38134),
(192289, 'itIT', '', 'Il tempo dei giochi è finito. I venti ci spingono avanti.', 38134),
(192288, 'itIT', '', 'È stato divertente! Non giocavo così da tantissimo tempo... Facciamolo ancora!', 38134),
(192284, 'itIT', '', 'Un gioco speciale solo per noi!', 38134),
(192283, 'itIT', '', 'Giocheresti con me? Per piaceeere?', 38134),
(192282, 'itIT', '', 'Tu! Voglio giocare con te!', 38134),
(192281, 'itIT', '', 'Pensa in fretta!', 38134),
(192280, 'itIT', '', 'Ehi, prendi!', 38134),
(192279, 'itIT', '', 'Scommetto che questo non riesci a schivarlo!', 38134),
(192278, 'itIT', 'La nostra via è lo Scopo.', 'La nostra via è lo Scopo.', 38134),
(192277, 'itIT', 'Sì. Sono delle creature frustranti, ma dobbiamo confidare che servano lo Scopo... a modo loro.', 'Sì. Sono delle creature frustranti, ma dobbiamo confidare che servano lo Scopo... a modo loro.', 38134),
(192276, 'itIT', '', 'Eccomi! Eheh!', 38134),
(192275, 'itIT', 'Ti è stato ordinato di osservare gli Alienatori? Posso soltanto immaginare...', 'Ti è stato ordinato di osservare gli Alienatori? Posso soltanto immaginare...', 38134),
(192274, 'itIT', 'Non posso restare, ma volevo salutarti. C\'è un sacco di lavoro da sbrigare.', 'Non posso restare, ma volevo salutarti. C\'è un sacco di lavoro da sbrigare.', 38134),
(192272, 'itIT', 'Salve.', 'Salve.', 38134),
(192271, 'itIT', 'Salve.', 'Salve.', 38134),
(192270, 'itIT', '', 'Ehi, ci sai davvero fare!', 38134),
(192269, 'itIT', '', 'Mi hai trovata! Ottimo lavoro!', 38134),
(192268, 'itIT', '', 'Giochiamo agli indovinelli!', 38134),
(192267, 'itIT', '', 'Qual è la vera me?', 38134),
(192266, 'itIT', '', 'Una di noi non è come le altre!', 38134),
(192265, 'itIT', '', 'Grazie per aver giocato con me! Torna ancora!', 38134),
(192264, 'itIT', '', 'Fine del divertimento?', 38134),
(192263, 'itIT', '', 'Non vuoi continuare a giocare?', 38134),
(192262, 'itIT', '', 'Oh, non ce la fai più?', 38134),
(192261, 'itIT', '', 'Ci divertiremo un mondo!', 38134),
(192260, 'itIT', '', 'Mi dispiace! Prova ancora!', 38134),
(192259, 'itIT', '', 'Oh oh! Hai sbagliato.', 38134),
(192258, 'itIT', '', 'Ops! Non perderti!', 38134),
(192257, 'itIT', '', 'Urrà! Ci sei quasi!', 38134),
(192256, 'itIT', '', 'Non ti si può ingannare!', 38134),
(192255, 'itIT', '', 'Ce l\'hai fatta! Hai indovinato!', 38134),
(192254, 'itIT', '', 'Riesci a trovare la strada attraverso la nebbia? Scegli con saggezza!', 38134),
(192249, 'itIT', '', 'Sì! Più amici con cui giocare! Venite!', 38134),
(192225, 'itIT', '', 'Ingra Maloch si fa silente. La foresta esprime gratitudine.', 38134),
(192224, 'itIT', '', 'Riponete la vostra fede nel bosco.', 38134),
(192223, 'itIT', '', 'La foresta brama la vostra vittoria, mortali.', 38134),
(192221, 'itIT', '', 'Dovete... resistere...', 38134),
(192220, 'itIT', '', 'Vi prego... Sopravvivete...', 38134),
(192219, 'itIT', '', 'Non riesco... a controllarlo...', 38134),
(192218, 'itIT', '', 'Dolore... Confusione...', 38134),
(192217, 'itIT', '', 'Non era così... che doveva andare...', 38134),
(192216, 'itIT', '', 'No...', 38134),
(192215, 'itIT', '', 'Dovete fuggire. La mia mente... mi tradisce...', 38134),
(192214, 'itIT', 'Vi ridurrò a brandelli!', '', 38134),
(192213, 'itIT', 'Come osate sfidarmi!', '', 38134),
(192211, 'itIT', 'Sono il vostro peggior incubo!', '', 38134),
(192210, 'itIT', 'Sparite dalla mia vista!', '', 38134),
(192209, 'itIT', 'Obbediscimi!', '', 38134),
(192208, 'itIT', 'Eseguirai i miei ordini!', '', 38134),
(192207, 'itIT', 'Ma voi... siete... deboli...', '', 38134),
(192203, 'itIT', 'Miserabili. Ho solo perso tempo prezioso con voi.', '', 38134),
(192196, 'itIT', 'Thros ti consumerà.', '', 38134),
(192192, 'itIT', 'Contorciti nella polvere!', '', 38134),
(192189, 'itIT', 'Tirnenn! Schiacciate questi insetti!', '', 38134),
(192186, 'itIT', 'La natura è fatta per essere controllata. Conquistata! E i Drust conquisteranno tutto!', '', 38134),
(192185, 'itIT', 'Patetiche creature. Venerate legnetti e foglie...', '', 38134),
(192183, 'itIT', '', 'Non... vincerete... mai...', 38134),
(192182, 'itIT', '', 'Subite la mia ira!', 38134),
(192181, 'itIT', '', 'Non potete scappare!', 38134),
(192180, 'itIT', '', 'La vostra rovina prende il volo!', 38134),
(192179, 'itIT', '', 'Temete i cieli!', 38134),
(192177, 'itIT', '', 'Siete stati sciocchi a sfidarci!', 38134),
(192176, 'itIT', '', 'Tiro al bersaglio.', 38134),
(192175, 'itIT', '', 'Non eri che una preda!', 38134),
(192174, 'itIT', '', 'Nessuno verrà risparmiato!', 38134),
(192172, 'itIT', '', 'Osservo la vostra fine dalle ombre.', 38134),
(192171, 'itIT', '', 'La vostra vista vi porterà alla rovina.', 38134),
(192170, 'itIT', '', 'Osservate lo splendore.', 38134),
(192169, 'itIT', '', 'La mia velocità non è seconda a nessuno.', 38134),
(192168, 'itIT', '', 'Non puoi eguagliare la mia rapidità.', 38134),
(192167, 'itIT', '', 'Io... ho... fallito...', 38134),
(192166, 'itIT', '', 'Tutti gli obiettivi eliminati.', 38134),
(192165, 'itIT', '', 'Le tue abilità non sono abbastanza.', 38134),
(192164, 'itIT', '', 'Uno sforzo inutile.', 38134),
(192162, 'itIT', '', 'Il mio tempo sta per scadere. La sento...', 38134),
(192161, 'itIT', '', 'Fa\' ciò che devi, amica mia. È l\'unico modo.', 38134),
(192160, 'itIT', '', 'Perdonatemi, figli miei. Le mie azioni... non mi appartengono...', 38134),
(192154, 'itIT', '', 'Sapevo che avresti fatto ritorno. Le tue capacità ci porteranno alla vittoria.', 38134),
(192105, 'itIT', '', 'Qui è dove incontrerete la vostra fine.', 38134),
(192102, 'itIT', 'Tutto qui? Pensavo che sarebbe stato uno spettacolo! Che delusione.', '', 38134),
(192101, 'itIT', 'Era troppo per i nostri decantati sfidanti!', '', 38134),
(192100, 'itIT', 'Una misera fine a una misera leggenda!', '', 38134),
(192099, 'itIT', 'Ah! Quello deve aver fatto male!', '', 38134),
(192098, 'itIT', 'Che il bagno di sangue inizi!', '', 38134),
(192094, 'itIT', '', 'Pagherete cari i vostri insensati massacri!', 38134),
(192093, 'itIT', '', 'Tutti coloro che avete ucciso reclamano vendetta!', 38134),
(192092, 'itIT', '', 'La realtà volge la sua collera contro di voi.', 38134),
(192091, 'itIT', '', 'La morte stessa esige le vostre anime.', 38134),
(192088, 'itIT', '', 'La mia magia va ben oltre la comprensione di voi mortali.', 38134),
(192087, 'itIT', '', 'Verrete scaraventati nell\'oblio.', 38134),
(192084, 'itIT', 'Riuscite a eguagliare la mia forza?', '', 38134),
(192083, 'itIT', 'Tremate davanti alla mia potenza!', '', 38134),
(192082, 'itIT', 'Lotta per la gloria!', '', 38134),
(192081, 'itIT', 'La mia lama non conosce eguali.', '', 38134),
(192080, 'itIT', 'Vi apro in due!', '', 38134),
(192079, 'itIT', 'Prendo qualcosa di buono!', '', 38134),
(192078, 'itIT', 'Ti faccio a pezzi!', '', 38134),
(192073, 'itIT', '', 'Nessun bersaglio mi è mai sfuggito.', 38134),
(192070, 'itIT', '', 'Un solo graffio è tutto ciò serve...', 38134),
(192068, 'itIT', '', 'Vi ucciderò seduta stante!', 38134),
(192066, 'itIT', '', 'Le mie lame faranno a pezzi la vostra carne!', 38134),
(192065, 'itIT', '', 'Temete le lame nel buio.', 38134),
(192064, 'itIT', '', 'La vostra fine arriva dalle ombre.', 38134),
(192063, 'itIT', '', 'No! Posso ancora... combattere...', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192062, 'itIT', '', 'L\'Animum... è incontenibile!', 38134),
(192061, 'itIT', '', 'Tremate dinanzi al mio potere!', 38134),
(192060, 'itIT', '', 'Chiunque ci ostacoli verrà annientato!', 38134),
(192059, 'itIT', '', 'Dovete essere annientati!', 38134),
(192058, 'itIT', '', 'Avete deluso i Kyrian, così come loro hanno deluso tutti noi.', 38134),
(192056, 'itIT', '', 'Il Primus è stato campione di molte battaglie campali. Onoralo sconfiggendo i nemici in questo luogo illustre.', 38134),
(192052, 'itIT', '', 'Nessuno può opporsi all\'Eletta.', 38134),
(192051, 'itIT', '', 'Sei immeritevole della nostra causa!', 38134),
(192050, 'itIT', '', 'La vostra ribellione dev\'essere punita!', 38134),
(192038, 'itIT', '', 'Non fidatevi... del Barone…', 38134),
(192037, 'itIT', '', 'La pestilenza vi inghiottirà!', 38134),
(192036, 'itIT', '', 'Si diffonde! Non si può contenere!', 38134),
(192034, 'itIT', '', 'Percepite la sua rabbia? La mia rabbia?!?', 38134),
(192028, 'itIT', '', 'Espierai i crimini della tua gente.', 38134),
(192027, 'itIT', '', 'La mia casata trabocca di morbo!', 38134),
(192023, 'itIT', '', 'Non potete sfuggire al destino!', 38134),
(192021, 'itIT', '', 'Tutto verrà distrutto!', 38134),
(192015, 'itIT', '', 'Quanta forza. Un\'aggiunta incredibilmente potente alla piaga.', 38134),
(192008, 'itIT', '', 'Ora proverai la mia sofferenza.', 38134),
(192007, 'itIT', '', 'Cadi nell\'abbraccio mortale della malattia.', 38134),
(192006, 'itIT', '', 'Ammirate il mio ultimo lavoro! Il mio orgoglio e la mia rovina!', 38134),
(192005, 'itIT', '', 'Se desiderate tanto questo potere, allora venite a prenderlo... o sarà lui a prendere voi.', 38134),
(192004, 'itIT', '', 'Chi osa entrare nella Casata delle Piaghe? Chi cerca ciò che non può essere controllato?', 38134),
(192003, 'itIT', '', 'La vendetta... sarà... mia...', 38134),
(192002, 'itIT', '', 'Presto tutta Maldraxxus si unirà a voi.', 38134),
(192001, 'itIT', '', 'Non te lo aspettavi, mh?', 38134),
(192000, 'itIT', '', 'È questo il meglio che gli Occhi sanno fare?', 38134),
(191999, 'itIT', '', 'Non mi fermerete!', 38134),
(191980, 'itIT', '', 'Correggerò il suo errore. Tutti soffriranno quanto ha sofferto la Casata degli Occhi!', 38134),
(191979, 'itIT', '', 'Se Akarek non fosse stato così stolto e si fosse impadronito della piaga, la Casata degli Occhi sarebbe ancora in piedi!', 38134),
(191978, 'itIT', '', 'Questo è il vero potere della piaga. Essa persiste e continua a marcire, annientando tutto ciò che incontra... persino i vincitori.', 38134),
(191977, 'itIT', '', 'Quindi emergete vittoriosi... ma a che prezzo? Quali vili composti scorrono ora nelle vostre vene?', 38134),
(191975, 'itIT', '', 'Quello è stato un semplice assaggio di ciò che può fare la piaga. Una volta che la controllerò, tutta Maldraxxus la pagherà.', 38134),
(191970, 'itIT', 'Dobbiamo radunare i Mietitori a Oblio del Peccato mentre fai ritorno a Oribos, mortale. C\'è bisogno che si sappia cos\'è successo qui.', '', 38134),
(191969, 'itIT', '', 'Denathrius radunerà sicuramente le sue forze al Castello di Nathria. Se lasciati agire, i suoi seguaci ci travolgeranno presto.', 38134),
(191968, 'itIT', '', 'La Fauce cresce, ricolma d\'Animum. Temo che nulla potrà fermare la fuga del Carceriere.', 38134),
(191967, 'itIT', '', 'Sapevo che non ci avresti deluso.', 38134),
(191965, 'itIT', '', '<Il testo è inciso alla base della statua.>\n\nIl destino è determinato da coloro che se ne impadroniscono. I supplicanti devono Inginocchiarsi in segno di omaggio.', 38134),
(191964, 'itIT', '', '<Il testo è inciso alla base della statua.>\n\nGrazie alla virulenza sconfiggiamo i nostri nemici. I meritevoli devono Sanguinare per Maldraxxus.', 38134),
(191963, 'itIT', '<Il testo è inciso alla base della statua.>\n\nLa debolezza dev\'essere soppiantata dalla forza. Fletti i muscoli per darne dimostrazione.', '<Il testo è inciso alla base della statua.>\n\nLa debolezza dev\'essere soppiantata dalla forza. Fletti i muscoli per darne dimostrazione.', 38134),
(191961, 'itIT', '', 'Questa era un\'antica forgia, risalente a prima che Maldraxxus fosse conquistata. Non è stata usata per anni, ma ora sarà molto utile, dato che Vyraz ci impedisce l\'accesso alla forgia di Heirmir.', 38134),
(191947, 'itIT', '', 'Ehm, andiamo avanti...', 38134),
(191946, 'itIT', '', 'Sono stata sconfitta. Evviva.', 38134),
(191945, 'itIT', '', 'Ugh... D\'accordo.', 38134),
(191944, 'itIT', '', 'Davvero. E nemmeno Jaina morì.', 38134),
(191943, 'itIT', '', 'Oh, ma dai! Davvero?!?', 38134),
(191942, 'itIT', '', 'Pulvilume, Jaina non uccise i suoi inseguitori.', 38134),
(191941, 'itIT', '', 'Pulvilume, Jaina non uccise il campione.', 38134),
(191940, 'itIT', '', 'Le navi dell\'Orda inseguirono la flotta dell\'Alleanza, ma vennero fermate dall\'Ammiraglio Supremo Jaina Marefiero!', 38134),
(191939, 'itIT', '', 'Cumpà! Vascelli dell\'Orda all\'orizzonte! Mo\' nun tieni via di scampo, strega del gelo!', 38134),
(191936, 'itIT', '', 'Per porre fine velocemente alla guerra, l\'Alleanza invase Dazar\'alor e uccise il Re Rastakhan!', 38134),
(191935, 'itIT', '', 'Muahahahahah!', 38134),
(191934, 'itIT', '', 'Vi ho attirato in mare aperto perché qui non avete alcuna possibilità di fuga!', 38134),
(191933, 'itIT', '', 'Credi davvero che stessi scappando, Capitano?', 38134),
(191932, 'itIT', '', 'Che la notte ti accolga, Calcafauce. La tua caccia è aperta.', 38134),
(191931, 'itIT', 'Stolti! È stato fin troppo facile seminare caos e discordia tra le vostre litigiose casate.', '', 38134),
(191916, 'itIT', 'Ah, i cadaveri erranti sono tornati. È ora di raccogliere i vostri pezzi!', '', 38134),
(191911, 'itIT', 'I vostri sforzi sono inutili. Non potete fermare il mio rituale!', '', 38134),
(191910, 'itIT', 'Presto, su per la collina!', '', 38134),
(191903, 'itIT', 'Prestate servizio, sapendo che i vostri risultati hanno contribuito a ripristinare la sacralità del nostro ordine.', '', 38134),
(191901, 'itIT', 'Il suo potere ha portato molte anime al Bastione e ora concederà le ali ai nostri aspiranti.', '', 38134),
(191900, 'itIT', 'Il Sigillo del Pentimento rappresenta molte cose: contrizione, rimorso, pulizia di noi stessi dagli errori passati per lasciarceli alle spalle.', '', 38134),
(191893, 'itIT', '', 'Posso portarti dalla Regina.', 38134),
(191862, 'itIT', '', 'Questo unguento ti aiuterà ad alleviare il dolore per un po\', Alwin. Devi ringraziare chi ha recuperato i petali necessari.', 38134),
(191855, 'itIT', 'Pesante!', '', 38134),
(191854, 'itIT', '', 'Non cedere, Kou!', 38134),
(191851, 'itIT', '', 'Uff!', 38134),
(191846, 'itIT', 'Città Vecchia, arriviamo! C\'è da muoversi un po\', per cui seguimi e non perderti!', '', 38134),
(191843, 'itIT', '', 'Hai il mio martello.', 38134),
(191839, 'itIT', 'È grandioso tornare a Roccavento!', '', 38134),
(191838, 'itIT', 'È fatta, devo tornare dagli altri!', '', 38134),
(191837, 'itIT', 'Che potere incredibile! Potremo addirittura uccidere il loro barone!', '', 38134),
(191836, 'itIT', 'Un soldato Maldraxxi? È il nemico!', '', 38134),
(191831, 'itIT', 'Bene, non ti hanno fatto a pezzi!', '', 38134),
(191830, 'itIT', 'Kynthia! Sia lode all\'Arconte!', '', 38134),
(191829, 'itIT', 'Tutte queste atrocità... Come possiamo fidarci di te?', '', 38134),
(191825, 'itIT', 'Salve, $p!\n\nPosso esserti d\'aiuto?', 'Salve, $p!\n\nPosso esserti d\'aiuto?', 38134),
(191824, 'itIT', 'Gli altri... Puoi aiutarmi a trovarli?', '', 38134),
(191820, 'itIT', 'La poltiglia mi protegge!', '', 38134),
(191819, 'itIT', 'La... fame... finisce...', '', 38134),
(191818, 'itIT', 'Diventerete un banchetto prelibato.', '', 38134),
(191817, 'itIT', 'Ancora... La poltiglia ne vuole ancora!', '', 38134),
(191816, 'itIT', 'La tua carne sarà consumata.', '', 38134),
(191815, 'itIT', 'Non sei altro che cibo.', '', 38134),
(191814, 'itIT', 'La poltiglia deve nutrirsi!', '', 38134),
(191813, 'itIT', 'Intrusi! Verrete divorati in un boccone!', '', 38134),
(191812, 'itIT', '', 'Gli orrori all\'interno di queste sale si sbarazzeranno in fretta di voi. La piaga di Stradama sarà presto mia.', 38134),
(191811, 'itIT', '', 'Dunque, la Casata degli Occhi ha mandato finalmente qualcuno a uccidermi. Provateci pure.', 38134),
(191810, 'itIT', '', 'Non avete scampo.', 38134),
(191809, 'itIT', '', 'Vi strapperò l\'anima dal petto!', 38134),
(191808, 'itIT', 'Quando vi troverò, sarò felice di mostrarvi la mia ricerca.', '', 38134),
(191807, 'itIT', 'Chi osa introdursi nel mio laboratorio?!?', '', 38134),
(191806, 'itIT', 'È ora di testare questa poltiglia su di voi!', '', 38134),
(191805, 'itIT', 'Ciò che le serve è... carne! Divora!', '', 38134),
(191804, 'itIT', 'È tempo che questo esperimento abbia risultati esplosivi!', '', 38134),
(191801, 'itIT', 'Aggiungiamo un accelerante, va bene?', '', 38134),
(191800, 'itIT', 'Un pizzico di aggressività dovrebbe andare bene!', '', 38134),
(191794, 'itIT', 'Ma io... c\'ero... quasi...', '', 38134),
(191790, 'itIT', 'Almeno siete stati delle ottime cavie.', '', 38134),
(191784, 'itIT', 'Il prossimo esemplare!', '', 38134),
(191783, 'itIT', 'Imbecilli!', '', 38134),
(191782, 'itIT', 'Non potete comprendere il mio genio!', '', 38134),
(191753, 'itIT', '', 'La tua anima sarà presto vincolata al Carceriere.', 38134),
(191750, 'itIT', 'Ti pentirai d\'essere nel nostro dominio.', '', 38134),
(191704, 'itIT', '', 'Il mio lavoro è organizzare trasporti volanti per conto di Re Wrynn.\n\nSe hai ricevuto un incarico speciale da parte della corona, potrei essere in grado di organizzare un volo per te.', 38134),
(191703, 'itIT', 'Ho fallito?', 'Ho fallito?', 38134),
(191702, 'itIT', 'Perdonami... Maestro.', 'Perdonami... Maestro.', 38134),
(191701, 'itIT', 'Non può finire così.', 'Non può finire così.', 38134),
(191700, 'itIT', 'Ero così... vicino.', 'Ero così... vicina.', 38134),
(191691, 'itIT', 'Silenzio, Kael\'thas.', '', 38134),
(191690, 'itIT', 'Bel modo di ringraziarci.', '', 38134),
(191689, 'itIT', '', 'Mi avrebbero fatto comodo anche un po\' prima.', 38134),
(191681, 'itIT', '', 'Vengo anche io. Supporto aereo al tuo servizio.', 38134),
(191677, 'itIT', '', 'I Silfi attendono il suo giudizio trattenendo il respiro...', 38134),
(191662, 'itIT', 'Bene. Ora bisogna solo dare inizio al rituale.', '', 38134),
(191661, 'itIT', 'Ma non c\'è problema. $p ci aiuterà a rimettere a posto Selvarden! Lo so e basta!', '', 38134),
(191660, 'itIT', 'Già! Di solito appare come un grosso coso gattoso... Sempre che sia ancora là. Non vedo più nessuno da quel bosco da un po\'.', '', 38134),
(191659, 'itIT', '...davvero.', '', 38134),
(191658, 'itIT', 'Be\', una volta ho conosciuto qualcuno il cui mondo era un anello, quindi non è così strano al confronto?', '', 38134),
(191657, 'itIT', 'Non ho ancora capito bene come abbia fatto il loro mondo a sopravvivere con una spada infilzata dentro.', '', 38134),
(191656, 'itIT', 'Non ho ancora capito bene come abbia fatto Sargeras a trovare una spada così grande.', '', 38134),
(191655, 'itIT', '', 'Mi è piaciuta la parte in cui hanno battuto la Legione.', 38134),
(191654, 'itIT', 'Baronessa Draka! Sembra che ti servano rinforzi!', '', 38134),
(191653, 'itIT', 'È stato divertente!', '', 38134),
(191652, 'itIT', 'Uno spettacolo eccezionale!', '', 38134),
(191651, 'itIT', '$p! $p! $p!', '$p! $p! $p!', 38134),
(191650, 'itIT', 'SÌ!', 'SÌ!', 38134),
(191649, 'itIT', 'SÌ! SÌÌÌ! $p!', '', 38134),
(191648, 'itIT', 'Ben fatto, $p!', 'Ben fatto, $p!', 38134),
(191647, 'itIT', 'UOOOOO!', 'UOOOOO!', 38134),
(191646, 'itIT', '', 'Un\'esibizione eccezionale.', 38134),
(191645, 'itIT', '', 'La Regina dell\'Inverno si alza dal trono.', 38134),
(191634, 'itIT', '', 'Alaria!', 38134),
(191633, 'itIT', '', 'Non è qui, proviamo altrove.', 38134),
(191632, 'itIT', '', 'Mmm, dov\'è?', 38134),
(191631, 'itIT', '', 'Niente Larvy, eh.', 38134),
(191628, 'itIT', '', 'Possiamo usare i semi per fermarli!', 38134),
(191627, 'itIT', '', 'Guarda quanti gorm! Dobbiamo fermarli prima che travolgano la foresta!', 38134),
(191626, 'itIT', '', 'Oh, no! Siamo in ritardo per il rituale!', 38134),
(191625, 'itIT', '', 'Dovrebbe bastare! Si occuperà la Caccia Selvaggia dei gorm rimasti.', 38134),
(191624, 'itIT', '', 'Ci vediamo nel bosco!', 38134),
(191619, 'itIT', 'Fai attenzione, Jaina. Che la Luce sia con te.', '', 38134),
(191618, 'itIT', 'Non badare a queste storie. Non c\'è alcun profitto in esse.', 'Non badare a queste storie. Non c\'è alcun profitto in esse.', 38134),
(191617, 'itIT', 'Opulenza, ho sentito storie che dicevano che molto tempo fa gli Eterni si riunivano nella stanza dell\'Aribter.', 'Opulenza, ho sentito storie che dicevano che molto tempo fa gli Eterni si riunivano nella stanza dell\'Aribter.', 38134),
(191616, 'itIT', 'Quando il potere è affiancato dal desiderio, sì. Ma esseri come l\'Arconte e la Regina dell\'Inverno vogliono solo anime. Noi non possiamo dargliele. Non ancora.', 'Quando il potere è affiancato dal desiderio, sì. Ma esseri come l\'Arconte e la Regina dell\'Inverno vogliono solo anime. Noi non possiamo dargliele. Non ancora.', 38134),
(191615, 'itIT', 'Ma non sono le creature più potenti delle Terretetre? Dici sempre che col potere si guadagna.', 'Ma non sono le creature più potenti delle Terretetre? Dici sempre che col potere si guadagna.', 38134),
(191614, 'itIT', 'Non permettono che nessuno sia testimone del processo e probabilmente ci esilierebbero da Oribos solo per averlo chiesto.', 'Non permettono che nessuno sia testimone del processo e probabilmente ci esilierebbero da Oribos solo per averlo chiesto.', 38134),
(191613, 'itIT', 'Opulenza, questa Arbiter che gli attendenti servono con tale devozione... L\'ha mai vista adempiere al suo presunto Scopo?', 'Opulenza, questa Arbiter che gli attendenti servono con tale devozione... L\'ha mai vista adempiere al suo presunto Scopo?', 38134),
(191612, 'itIT', 'Per il tuo bene, Ba\'solo, fai in modo che non accada.', 'Per il tuo bene, Ba\'solo, fai in modo che non accada.', 38134),
(191611, 'itIT', 'Le porgo le mie scuse, Opulenza! Perdoni il mio entusiasmo.', 'Le porgo le mie scuse, Opulenza! Perdoni il mio entusiasmo.', 38134),
(191610, 'itIT', 'Lo so, e non ne parlerò oltre qui.', 'Lo so, e non ne parlerò oltre qui.', 38134),
(191609, 'itIT', 'Grazie per avermi permesso di accompagnarti in questo pellegrinaggio, Opulenza. La Città Eterna è davvero meravigliosa!', 'Grazie per avermi permesso di accompagnarti in questo pellegrinaggio, Opulenza. La Città Eterna è davvero meravigliosa!', 38134),
(191608, 'itIT', 'È impressionante, Ba\'solo, ma impallidisce in confronto alle altre opere dei Primi.', 'È impressionante, Ba\'solo, ma impallidisce in confronto alle altre opere dei Primi.', 38134),
(191607, 'itIT', 'Tu... hai visto il loro operato? I motivi? La geometria?', 'Tu... hai visto il loro operato? I motivi? La geometria?', 38134),
(191605, 'itIT', 'Silenzio! Non parlare del grande piano!', 'Silenzio! Non parlare del grande piano!', 38134),
(191604, 'itIT', 'Opulenza, intendi la posizione del sepol...', 'Opulenza, intendi la posizione del sepol...', 38134),
(191603, 'itIT', 'Questo è un periodo interessante, Ba\'solo. Dobbiamo sforzarci di trovare la causa di questo caos. Una tale conoscenza potrebbe rivelarsi utile.', 'Questo è un periodo interessante, Ba\'solo. Dobbiamo sforzarci di trovare la causa di questo caos. Una tale conoscenza potrebbe rivelarsi utile.', 38134),
(191602, 'itIT', 'Come desideri, Opulenza.', 'Come desideri, Opulenza.', 38134),
(191601, 'itIT', 'Placa il loro prezioso Scopo. È un mezzo per raggiungere i nostri fini.', 'Placa il loro prezioso Scopo. È un mezzo per raggiungere i nostri fini.', 38134),
(191600, 'itIT', 'D\'accordo, Opulenza. Kah-Delen è più perspicace di quanto vuole farci credere.', 'D\'accordo, Opulenza. Kah-Delen è più perspicace di quanto vuole farci credere.', 38134),
(191599, 'itIT', 'Dobbiamo fare attenzione, Ba\'solo. Ci sono dei limiti alla pazienza degli attendenti, e io non voglio sprecare questa opportunità.', 'Dobbiamo fare attenzione, Ba\'solo. Ci sono dei limiti alla pazienza degli attendenti, e io non voglio sprecare questa opportunità.', 38134),
(191594, 'itIT', 'Così come l\'Alleanza.', '', 38134),
(191593, 'itIT', 'Il Capitano Garrick ti stima molto. Sono certo che Jaina possa contare su di te.', '', 38134),
(191592, 'itIT', 'Ah, questa dev\'essere la nuova recluta. Ti do il benvenuto.', '', 38134),
(191591, 'itIT', 'Ho saputo che la missione è stata un successo, Capitano.', '', 38134),
(191590, 'itIT', 'Ecco perché non andrà da sola.', '', 38134),
(191589, 'itIT', 'Anduin, devo forse ricordarti che Jaina è il motivo per cui Kul Tiras ha lasciato l\'Alleanza? Non l\'accoglieranno mai!', '', 38134),
(191588, 'itIT', '', 'Tornerò con la flotta di Kul Tiras... o non tornerò affatto.', 38134),
(191587, 'itIT', '', 'Devo almeno provarci. Senza il loro aiuto, le nostre navi non potranno nulla contro le flotte combinate dell\'Orda e degli Zandalari.', 38134),
(191584, 'itIT', 'Puoi convincerli a tornare nell\'Alleanza e offrirci la loro forza?', '', 38134),
(191582, 'itIT', 'Allora dobbiamo cercare nuovi alleati anche noi. Jaina, che mi dici di Kul Tiras? La sua flotta navale non è seconda a nessuno.', '', 38134),
(191581, 'itIT', 'Ho saputo che l\'Orda si è alleata con l\'Impero degli Zandalari. Ora la sua forza navale è notevolmente cresciuta.', '', 38134),
(191580, 'itIT', 'La guerra contro l\'Orda occupa sempre i miei pensieri. Che notizie porti, Maestro delle Spie?', '', 38134),
(191579, 'itIT', '', 'Radunatevi! Dov\'è Kel\'Thuzad?', 38134),
(191578, 'itIT', 'Si è impadronito dell\'Arcanum Supremo! È dove conduciamo i nostri rituali più potenti.', '', 38134),
(191576, 'itIT', '', 'Ben fatto! Ne hai preso uno decisamente temibile.', 38134),
(191565, 'itIT', 'Rilassatevi! Esplorate la Corte! Divertitevi!', '', 38134),
(191563, 'itIT', 'Apprezziamo le difficoltà affrontate pur di viaggiare sino a qui. Assieme faremo sì che questa Corte valga il vostro viaggio.', '', 38134),
(191562, 'itIT', 'Benvenuti, amici miei, alla Corte delle Braci!', '', 38134),
(191560, 'itIT', 'Ma erano appena usciti da una guerra!', '', 38134),
(191542, 'itIT', '', 'Il Sigillo del Pentimento ci permetterà di ricostruire l\'emblema e creare nuovi Ascesi per rinforzare i ranghi del Bastione contro gli Indegni.', 38134),
(191540, 'itIT', 'Voras è sconfitto. La mia missione è conclusa.', '', 38134),
(191539, 'itIT', 'Mi preparerò per la nostra caccia a Voras mentre recupererai ciò di cui avremo bisogno.', '', 38134),
(191538, 'itIT', 'Un tempo parlavo per i Tauren. Ora servo la Caccia Selvaggia.', '', 38134),
(191537, 'itIT', 'Cacci bene.', '', 38134),
(191534, 'itIT', '', 'Andrò a prendere Kleia e Pelagos. Spero non avrai problemi nel viaggio verso il Distretto delle Braci.', 38134),
(191533, 'itIT', '', 'Potrei dire che è stato un piacere, ma non sarebbe vero.', 38134),
(191519, 'itIT', '', 'Non c\'è dubbio che questa minaccia ritornerà. Ma per ora, abbiamo scongiurato il pericolo.', 38134),
(191516, 'itIT', '', 'Onorare l\'eredità del Primus è un dovere solenne di chiunque abbia giurato di difendere Maldraxxus. Hai fatto un ottimo lavoro.', 38134),
(191511, 'itIT', '', 'Non possiamo permettere che i divoratori si stabiliscano a Maldraxxus. Consumeranno tutto ciò per cui abbiamo lottato. Falli fuori!', 38134),
(191505, 'itIT', '', 'Tutti insieme! Avanzate!', 38134),
(191504, 'itIT', 'Ninadar! Non puoi controllare tutto quell\'Animum!', '', 38134),
(191503, 'itIT', 'Quella folle sconsiderata! Se non la fermiamo, potrebbe distruggerci tutti!', '', 38134),
(191502, 'itIT', '', 'Patience Mograine, he cannot hide forever.', 38134),
(191501, 'itIT', '[DEPRECATED] KEL\'THUZAD! YOU COWARD!', '', 38134),
(191497, 'itIT', 'Questi ospiti hanno si aspettano intrattenimenti senza precedenti!\n\nLascia che scateni il mio meraviglioso talento per loro, Calcafauce!', '', 38134),
(191485, 'itIT', '', 'Da questa parte!', 38134),
(191484, 'itIT', '', 'Sembra che faremo presto amicizia! Vediamo chi arriva prima di sotto!', 38134),
(191483, 'itIT', '', 'Vedo che il nostro ospite è arrivato.', 38134),
(191462, 'itIT', 'QUANDO SPACCHIAMO PIPISTRELLO BRUTTO?!?', '', 38134),
(191427, 'itIT', 'Ben fatto. La corte è nostra, per il momento.', '', 38134),
(191426, 'itIT', 'Ah, apprendista! Hai fatto ritorno infine.', '', 38134),
(191425, 'itIT', 'Dobbiamo sempre lottare per mantenere il vantaggio. Usa tutte le Scorte d\'Animum che trovi per risvegliare i nostri alleati ed eliminare coloro che ci opprimono!', '', 38134),
(191420, 'itIT', '', 'Allora possiedi la chiave per il Seggio del Primus. Ci vedremo là, dunque.', 38134),
(191407, 'itIT', '', 'Conosco personalmente tutti gli ospiti presenti in questo elenco.\n\nDì solo una parola e farò in modo che si sentano tutti a proprio agio.', 38134),
(191404, 'itIT', 'Una volta che saremo dentro, dobbiamo conquistare il bacino d\'Animum perché non sia usato contro di noi.', '', 38134),
(191403, 'itIT', '', 'Fatica sprecata! I miei costrutti resisteranno a qualsiasi tempesta riescano a evocare!', 38134),
(191402, 'itIT', '', 'Pronti per un passaggio?', 38134),
(191401, 'itIT', 'Ho forgiato la mia... via.', '', 38134),
(191400, 'itIT', 'Questi non sono i semi adatti, quindi? Devi aver detto seme blu con foglie rosse!', '', 38134),
(191399, 'itIT', 'Il percorso laterale è sbarrato. Il nemico ci farebbe a pezzi da una posizione sopraelevata.', '', 38134),
(191398, 'itIT', 'Più debitori da cui riscuotere!', '', 38134),
(191397, 'itIT', 'Mmm, credo di aver trovato le sue tracce. Dammi solo un momento.', '', 38134),
(191390, 'itIT', '', 'Sei un esempio da seguire per tutti. Presta servizio.', 38134),
(191389, 'itIT', '', 'Tutti gli Aspiranti sanno che il loro addestramento non è mai completo. Prendi una lancia e continua il tuo.', 38134),
(191388, 'itIT', '', 'Prendi la Pietra del Peccato e usciamo di qui prima di farci catturare.', 38134),
(191378, 'itIT', '', 'Sto con te. La Pietra del Peccato della Contessa sarà nostra!', 38134),
(191375, 'itIT', 'Volete che tipo spacco qualcosa?', '', 38134),
(191367, 'itIT', 'Ti ringrazio! Le magie illusorie non sono il mio forte, ma mi sono aiutato con una pagina di un libro dei Venthyr.', '', 38134),
(191362, 'itIT', '', 'Vieni avanti, mortale, e solleva la tua lama runica!', 38134),
(191360, 'itIT', '', 'Schieriamo le riserve! Teniamo la posizione finché il Calcafauce non colpisce.', 38134),
(191358, 'itIT', '', 'Non appena darai l\'ordine, rimetterò insieme lo specchio.', 38134),
(191346, 'itIT', 'Ben fatto. Questo dovrebbe dissuaderli dall\'intraprendere nuove azioni.', '', 38134),
(191345, 'itIT', 'Molti nel Distretto del Calice sono ancora leali a Denathrius, non possiamo permettere che si radunino e crescano. Va\' al loro distretto e mostragli la forza di Oblio del Peccato!', '', 38134),
(191335, 'itIT', '', 'Seguimi per le scale, i tuoi amici attendono.', 38134),
(191334, 'itIT', 'È tutta qui la morte?', 'È tutta qui la morte?', 38134),
(191329, 'itIT', 'Curiosa pure tra le mie merci, è solo un peccatuccio.', 'Curiosa pure tra le mie merci, è solo un peccatuccio.', 38134),
(191322, 'itIT', '', 'Kleia si è occupata in fretta dei Venthyr lungo la strada. È davvero una gioia per gli occhi.', 38134),
(191321, 'itIT', '', 'Di qua! Abbiamo trovato il frammento di specchio.', 38134),
(191318, 'itIT', '', 'Non preoccuparti, i Factotum sono facili da impressionare. Athanos, invece...', 38134),
(191304, 'itIT', '', 'I nostri Aspiranti hanno sentito parlare tanto delle tue imprese. Forse ti andrebbe di dimostrare le tue abilità... Magari con un pubblico questa volta?', 38134),
(191300, 'itIT', 'Ci servirà una strategia per sconfiggerlo... e penso di sapere da dove cominciare.', '', 38134),
(191299, 'itIT', 'Voras non è un divoramondo qualsiasi. Nonostante la nostra discrezione, è riuscito comunque a scoprirci.', '', 38134),
(191298, 'itIT', 'Resisti!', '', 38134),
(191297, 'itIT', 'Può percepirci! Preparati!', '', 38134),
(191294, 'itIT', 'Dobbiamo osservarlo più da vicino prima di colpire. Cerca un nascondiglio.', '', 38134),
(191293, 'itIT', '', 'La nostra congrega si rafforza! Vittoria per Maldraxxus! Vittoria per le Terretetre!', 38134),
(191292, 'itIT', '', 'Nonostante il Primus sia stato a lungo assente da queste sale, il Calcafauce incarna le virtù che egli aveva infuso nelle cinque casate.', 38134),
(191291, 'itIT', '', 'Ora abbiamo preso questo santuario, grazie alle azioni del mortale che sta con la nostra congrega.', 38134),
(191290, 'itIT', '', 'Necrosignori! Veri difensori di Maldraxxus! Udite le mie parole.', 38134),
(191289, 'itIT', 'Amici come ombrello!\n\nGubbins aiuta!\n\nGubbins felice!\n\n<Gubbins ti mostra i pollici alzati.>', '', 38134),
(191288, 'itIT', '', 'Il mio frammento... il mio biglietto per il potere...', 38134),
(191287, 'itIT', '', 'Ho avvertito il tuo olezzo non appena hai messo piede qui. Vieni, mostrami cosa ti hanno insegnato i Kyrian.', 38134),
(191285, 'itIT', 'Questi Spriggan hanno preso il controllo di ogni cosa! Sono una minaccia, s\'infilano dappertutto e distruggono ogni cosa, in più fanno scherzi!\n\nSono peggio di qualsiasi Silfide, te l\'assicuro!', '', 38134),
(191282, 'itIT', '', 'Non penso che inganneresti qualcuno, fratello. Sei portato per la magia, forse un\'illusione sarebbe meglio?', 38134),
(191280, 'itIT', 'Fammi provare questo... Come sto?', '', 38134),
(191277, 'itIT', 'Ho qualcosa tra i denti?\n\nSiamo amici abbastanza stretti. Me lo diresti se fosse il caso, ne sono sicuro.', '', 38134),
(191276, 'itIT', 'C\'è ancora molto da miscelare! Puoi sempre contribuire con più ingredienti per aiutarci.', '', 38134),
(191274, 'itIT', 'Strapperò la carne dalle tue ossa!', '', 38134),
(191273, 'itIT', 'Sei più forte di quanto immaginassi...', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(191272, 'itIT', 'Osi mettere piede a Revendreth?', '', 38134),
(191271, 'itIT', '', 'I miei occhi si chiudono, ma altri prenderanno il mio posto...', 38134),
(191270, 'itIT', '', 'Puzzi di feccia Kyrian.', 38134),
(191269, 'itIT', '', 'La Casata Primulan invita le casate Venascura e Peccazanna a discutere gli accordi con la Contessa. Collaboriamo per rinforzare i nuovi legami tra le nostre famiglie.', 38134),
(191268, 'itIT', 'Mi serve anche un bouquet di ventitré Rose di Mezzanotte selezionate, una guardia Litogena che faccia rapporto unicamente a me, e del tè di foglie ricavate dal Distretto delle Braci. Dopodiché potrei considerare di tenere segreta la posizione della tua pietra del peccato...', '', 38134),
(191267, 'itIT', '', 'Ho mandato il fattorino al Mercato Notturno per vendere della roba, e ha avuto l\'ardire di fare il mio nome! Ho congedato quell\'idiota all\'istante. Quegli irritanti Alienatori ce l\'avranno con me per millenni!', 38134),
(191266, 'itIT', 'Ho sentito che il Duca Grigore è stato negligente nei suoi doveri di assoluzione. Che faccia tosta, considerarsi più occupato di noi altri!', '', 38134),
(191264, 'itIT', '<Il varco vibra di un potere familiare. Senti che conduce di nuovo nella Fauce.>', '<Il varco vibra di un potere familiare. Senti che conduce di nuovo nella Fauce.>', 38134),
(191263, 'itIT', '', 'Vieni, mortale.', 38134),
(191262, 'itIT', 'Calcafauce! Hai fatto ritorno!', '', 38134),
(191261, 'itIT', '', 'C\'è ancora molto lavoro da fare, Calcafauce. Dobbiamo ripristinare l\'ordine a Maldraxxus!\n\nSono sicura che col tuo aiuto vinceremo.', 38134),
(191239, 'itIT', '', 'E così, il Corruttore fu sconfitto! Il bene prevalse ancora una volta!', 38134),
(191238, 'itIT', '', 'E così, il Corruttore fu sconfitto! I campioni di Azeroth prevalsero ancora una volta!', 38134),
(191237, 'itIT', 'La Casata delle Piaghe un tempo era la più temuta di Maldraxxus.', '', 38134),
(191234, 'itIT', '', 'Quella lama runica... è stata creata dal Primus...', 38134),
(191226, 'itIT', '', 'No! Fermateli, sciocchi!', 38134),
(191225, 'itIT', '', 'Ma... quale potere ancora permane nell\'aria?', 38134),
(191153, 'itIT', 'Vivo servendo umilmente l\'Arconte.\n\nOgni mia parola, ogni mia azione, è in sua grazia.', '', 38134),
(191151, 'itIT', 'Stanno resuscitando un drago? Come faremo ad affrontare una cosa del genere?', '', 38134),
(191150, 'itIT', '', 'Ti ergi contro di noi anche senza un medaglione. Il tuo Animum sarà utile al Sire!', 38134),
(191149, 'itIT', '', 'Arrenditi, Accusatrice. Il Maestro schiaccerà la tua patetica resistenza!', 38134),
(191146, 'itIT', 'Il tuo aiuto è ben accetto, Calcafauce.\n\nInsieme possiamo fare molto!', '', 38134),
(191145, 'itIT', '', 'Ho riparato lo specchio. Via! Dobbiamo andarcene SUBITO!', 38134),
(191144, 'itIT', 'Questo disastro ci offre nuove opportunità per ricostruire una Casata ancora più forte.', '', 38134),
(191143, 'itIT', 'Il lavoro che facciamo richiede grandi abilità combattive. ^n^nSe il Maestro delle Pustole Yetch ti accetterà, ti permetterò di aiutare!', '', 38134),
(191122, 'itIT', 'Abbiamo un compito molto difficile per cui ci servirà aiuto.\n\nMentre ci pensi, prova pure a fare qualche esperimento... c\'è sempre bisogno di cavie!', '', 38134),
(191121, 'itIT', 'Raccoglierò i semi per il rituale. Ci rivedremo quando avrai trovato Larvy.', '', 38134),
(191115, 'itIT', 'Troveremo un modo per imbrigliare la sofferenza del nostro Margravio, trasformandola in un\'arma!', '', 38134),
(191109, 'itIT', 'Quelle corna... Porti con te la benedizione di Cenarius! Sono Huln Alto Monte, il Cacciatore Cornuto.', '', 38134),
(191102, 'itIT', '', 'Tieni giù la testa, non vogliamo che ci vedano.', 38134),
(191101, 'itIT', '', 'Guarda. La nostra voce del popolo fa tutto fuorché servire Revendreth.', 38134),
(191100, 'itIT', '', 'Mi servirà del tempo per trovare una strada fuori da qui. Al mio fianco!', 38134),
(191099, 'itIT', 'La corte ti aspetta nelle sue stanze. Ti ci porterò io.', '', 38134),
(191098, 'itIT', '', 'Guardie, occupatevi di questi intrusi! Nel nome del Maestro!', 38134),
(191095, 'itIT', '', 'Forse è qui!', 38134),
(191094, 'itIT', '', 'Me ne sono accorta subito.', 38134),
(191093, 'itIT', '', 'Che cosa nascondono qui?', 38134),
(191092, 'itIT', '', 'Sbarazziamoci di questa illusione.', 38134),
(191086, 'itIT', '', 'Benvenuto, mortale. Questo varco ti porterà in alto.', 38134),
(191080, 'itIT', '', 'Nessuno ci fermerà!', 38134),
(191079, 'itIT', 'Gli Occhi saranno epurati da Maldraxxus!', '', 38134),
(191078, 'itIT', '', 'Alimenterai i nostri rituali!', 38134),
(191077, 'itIT', 'Aiuto! Sono troppo brillante per morire!', '', 38134),
(191076, 'itIT', '', 'Lo so, l\'ho cresciuto per superare qualunque sfida.', 38134),
(191073, 'itIT', '', 'Seguimi, Calcafauce, in modo che tu capisca per che cos\'hai combattuto.', 38134),
(191072, 'itIT', '', 'Recluta! I Verrospino sono molto più pericolosi dei Murloc. Dovrai prepararti ai pericoli che ti aspettano.', 38134),
(191070, 'itIT', '', 'Chi ha detto che dovrai \"parlare\"?', 38134),
(191069, 'itIT', 'Maledetta strega... Non parlerò mai!', '', 38134),
(191067, 'itIT', '', 'Dopo la sconfitta di G\'huun, l\'Orda e l\'Alleanza tornarono a combattere tra di loro!', 38134),
(191066, 'itIT', '', 'Ysera scuote la testa.', 38134),
(191065, 'itIT', 'Unità Dio Antico G\'huun sterm... Melmalercia, no!', '', 38134),
(191064, 'itIT', 'Credo che finiscano tutti nella Fauce, in realtà.', '', 38134),
(191063, 'itIT', 'Ehm... Gli \"Dei del Sangue\" arrivano da noi o cosa?', '', 38134),
(191062, 'itIT', '', 'Nelle profondità delle giungle di Nazmir, il Dio del Sangue G\'huun fu sconfitto!', 38134),
(191061, 'itIT', '', 'Nelle profondità delle giungle di Nazmir, i Campioni di Azeroth sconfissero il Dio del Sangue G\'huun!', 38134),
(191059, 'itIT', 'Piazza la Trappola Congelante nelle Rovine degli Ogre, la bestia che assedia l\'accampamento dovrebbe saltare fuori.', '', 38134),
(191058, 'itIT', 'Quella non è una belva selvaggia... È una delle bestie a bordo della nostra nave! Dev\'essere sopravvissuta allo schianto.', '', 38134),
(191057, 'itIT', 'Catturata!', '', 38134),
(191056, 'itIT', 'Guarda, eccola!', '', 38134),
(191038, 'itIT', 'Piazza la trappola più distante dall\'accampamento, la bestia non verrà attirata se è troppo vicina a noi.', '', 38134),
(191037, 'itIT', 'Portami i materiali e ti mostrerò come creare una trappola.', '', 38134),
(191035, 'itIT', '', 'Un Granulo di Ghiaccio? Certo. Per cosa ti serve?', 38134),
(191031, 'itIT', '', 'Due nemici ti stanno attaccando, usa Metamorfosi per pareggiare i conti!', 38134),
(191030, 'itIT', '', 'È stata la recluta, infatti!', 38134),
(191029, 'itIT', 'Hai detto che non mi avresti trasformato in pecora!', '', 38134),
(191028, 'itIT', '', 'Colpiscilo con un attacco per spezzare l\'incantesimo.', 38134),
(191027, 'itIT', '', 'Il tuo nemico metamorfato vagherà senza meta per un po\', sfrutta questo tempo per combattere chi rimane.', 38134),
(191025, 'itIT', '', 'Molto divertente, recluta. Credo che tu ci abbia preso la mano, torniamo indietro.', 38134),
(191024, 'itIT', 'Ricorda solo che colpire il tuo nemico annullerà l\'incantesimo. Prova a farlo con Meredy.', '', 38134),
(191023, 'itIT', 'Oh, ho sentito di questa strategia! Puoi tenere un nemico metamorfato mentre ti concentri su un altro.', '', 38134),
(191021, 'itIT', '', 'Ma dai, Cole! Non lo farei mai. Giuro!', 38134),
(191020, 'itIT', 'Hai... intenzione di trasformarmi in una pecora?', '', 38134),
(191019, 'itIT', '', 'Cole, vieni qui. Circonderemo la recluta per mostrarle come si usa Metamorfosi.', 38134),
(191017, 'itIT', '', 'Ho parecchie idee su come occuparci di questi Ogre. Vediamo quale non fa esplodere accidentalmente l\'isola.', 38134),
(191016, 'itIT', 'A volte vorrei avere il potere di mutare forma come gli spiriti qui. Così potrei librarmi nel cielo come il Cacciatore Cornuto! Nessuna preda sfuggirebbe al mio sguardo!', '', 38134),
(191015, 'itIT', 'Il Cacciatore Cornuto è un segugio senza pari. Se lui non può trovare la preda, allora nessuno può.', '', 38134),
(191014, 'itIT', 'Dimmi, com\'è andata la vostra caccia? Tu e il Cacciatore Cornuto avete ucciso la bestia che stava cercando?', '', 38134),
(191013, 'itIT', 'Da allora le mie ferite si sono rimarginate. È un bene che il Cacciatore Cornuto ci abbia salvato, non è così?', '', 38134),
(191012, 'itIT', 'Fa\' attenzione, queste strade sono diventate pericolose di recente. È successo qualcosa a Tirna Falcis.', '', 38134),
(191011, 'itIT', 'Salve. Come può esserti d\'aiuto la Caccia Selvaggia?', '', 38134),
(191010, 'itIT', 'La Caccia serve Selvarden.', '', 38134),
(191007, 'itIT', 'Organizzeremo la festa più bella che Revendreth abbia mai visto!', '', 38134),
(191006, 'itIT', '', 'Il tuo nemico resterà trasformato in pecora finché non gli infliggi dei danni, così.', 38134),
(191005, 'itIT', '', 'È una dimostrazione! La Metamorfosi può fare la differenza in battaglia.', 38134),
(191001, 'itIT', '', 'Meredy, non è il momento di fare scherzi!', 38134),
(190998, 'itIT', 'Non vedo l\'ora della prossima Corte delle Braci. Le persone dicono che potrebbe essere la migliore di sempre!', '', 38134),
(190997, 'itIT', 'Fa\' molta attenzione! La strada non è più sicura.', '', 38134),
(190996, 'itIT', '', 'Assaggia un po\' di Metamorfosi!', 38134),
(190995, 'itIT', 'L\'Ombra del Terrore è il reagente preferito del Margravio Stradama. Incontriamoci al laboratorio quando lo avrai recuperato!', '', 38134),
(190994, 'itIT', 'Il coraggioso... il grande... il pericoloso. Tutti sono vincolati qui. Liberali.', '', 38134),
(190992, 'itIT', 'Un Silfo perduto, vincolato al tormento. Liberalo... o sarà consumato dall\'oscurità.', '', 38134),
(190990, 'itIT', 'Neanche un Mietitore dell\'Orgoglio si merita di marcire qui. Salva i Venthyr da questo destino.', '', 38134),
(190989, 'itIT', 'Ora che abbiamo finito con i preliminari, le vere prove possono cominciare!', '', 38134),
(190987, 'itIT', 'Che sorpresa! Sembra che i nostri cuccioli riescano a mordere, dopotutto!', '', 38134),
(190986, 'itIT', '', 'Nelle profondità marine, ebbe luogo la battaglia contro la dispotica Regina Azshara.', 38134),
(190985, 'itIT', '', 'Spero che il mio grimorio non si sia rovinato troppo restando sul fondo del lago. Forse dovevo andare a prenderlo io.', 38134),
(190984, 'itIT', '', 'È fatta. Che il potere racchiuso nelle vostre anime possa esservi utile.', 38134),
(190983, 'itIT', '', 'Tornerò dal Capitano Garrick. Potresti seguirmi nella tua nuova forma animale.', 38134),
(190981, 'itIT', 'In ogni caso dovrebbe essere interessante... Fate entrare gli sfidanti!', '', 38134),
(190980, 'itIT', 'Sembra che i nuovi arrivati pensino di poter competere con i migliori combattenti di Maldraxxus! Ah! Ci crederò quando lo vedrò!', '', 38134),
(190979, 'itIT', 'Benvenuti, signore e signori, al Teatro del Dolore!', '', 38134),
(190978, 'itIT', '', 'La tua sintonia con la natura è diventata più forte. Usala per proteggere la selva e mantenere l\'equilibrio.', 38134),
(190975, 'itIT', 'Non può... finire... così...', 'Non può... finire... così...', 38134),
(190974, 'itIT', 'Un figlio del Bastione... immeritevole di questo destino. Portalo nella Luce.', '', 38134),
(190972, 'itIT', 'Troppo... presto...', 'Troppo... presto...', 38134),
(190970, 'itIT', 'Hai... imbrogliato...', 'Hai... imbrogliato...', 38134),
(190968, 'itIT', 'Mancava... tanto così...', 'Mancava... tanto così...', 38134),
(190967, 'itIT', 'Inutili... ossa...', 'Inutili... ossa...', 38134),
(190966, 'itIT', 'Non... Non ancora...', 'Non... ancora...', 38134),
(190965, 'itIT', 'Mi stavo solo... riscaldando...', 'Mi stavo solo... riscaldando...', 38134),
(190964, 'itIT', 'Non sopravvivrai a questo!', 'Non sopravvivrai a questo!', 38134),
(190963, 'itIT', 'Affronta la tua fine!', 'Affronta la tua fine!', 38134),
(190962, 'itIT', '', 'Insieme, stermineremo i traditori che infestano Maldraxxus.', 38134),
(190960, 'itIT', 'È tempo di guadagnarmi la gloria!', 'È tempo di guadagnarmi la gloria!', 38134),
(190959, 'itIT', 'La mia leggenda ha inizio!', 'La mia leggenda ha inizio!', 38134),
(190958, 'itIT', 'Mi farai guadagnare un po\' di notorietà.', 'Mi farai guadagnare un po\' di notorietà.', 38134),
(190957, 'itIT', 'Sei il mio biglietto per il successo!', 'Sei il mio biglietto per il successo!', 38134),
(190956, 'itIT', 'Spacchiamo qualche osso!', 'Spacchiamo qualche osso!', 38134),
(190910, 'itIT', 'Cercavi me?', '', 38134),
(190907, 'itIT', '', 'Ascolta i loro sussurri. Apprendi le lezioni che hanno da insegnarti.', 38134),
(190906, 'itIT', '', 'Gli spiriti rispondono!', 38134),
(190903, 'itIT', 'Combatti con onore! Tutta Maldraxxus ti osserva.', '', 38134),
(190901, 'itIT', '', 'Spiriti della natura, offrite i vostri doni a quest\'aspirante della via druidica.', 38134),
(190900, 'itIT', '', 'Il cambiamento serpeggia in tutta Maldraxxus e lascia un vuoto lungo la sua scia.\n\nSpero che la mia Matrona ci condurrà attraverso questi tempi oscuri. La Casata degli Occhi vedrà di nuovo la gloria.', 38134),
(190894, 'itIT', 'Benedetto... silenzio...', 'Benedetto... silenzio...', 38134),
(190893, 'itIT', 'È finita... finalmente...', 'Finalmente... è finita...', 38134),
(190892, 'itIT', '<Sembra che le azioni della Casata dei Costrutti abbiano reso muto Hipokos. Ti prende la mano nella sua, dandoti un cenno d\'approvazione al posto delle parole.>', '', 38134),
(190891, 'itIT', 'Finalmente... è finita.', 'Finalmente... è finita.', 38134),
(190890, 'itIT', 'Non finisce mai! Non finisce mai!', 'Non finisce mai! Non finisce mai!', 38134),
(190889, 'itIT', 'Metti fine a questa sofferenza!', 'Metti fine a questa sofferenza!', 38134),
(190886, 'itIT', '', 'Ho trovato un\'antica pietra druidica qua vicino. Raggiungiamola ed entriamo in comunione con la natura.', 38134),
(190884, 'itIT', '', 'Non fare quella faccia, Draven. Presto sarà il tuo turno.', 38134),
(190883, 'itIT', 'Che la sua lama ti torni utile, Calcafauce.', '', 38134),
(190879, 'itIT', 'Ora devo ritornare alla mia postazione. Buona caccia.', '', 38134),
(190878, 'itIT', 'Signore, è un onore conoscerti... e farci salvare da te!', '', 38134),
(190877, 'itIT', '', 'Ah! Sono stata a Maldraxxus per parecchio tempo e non ho mai visto nulla del genere!\n\nQuesta volta c\'è qualcosa di speciale. Dovrei essere felice di poter partecipare.\n\nOra vattene. Ho delle armi da riparare.', 38134),
(190869, 'itIT', 'Indosserò il tuo teschio come trofeo!', 'Indosserò il tuo teschio come trofeo!', 38134),
(190868, 'itIT', 'La mia lama si bagnerà col tuo sangue!', 'La mia lama si bagnerà col tuo sangue!', 38134),
(190866, 'itIT', 'Questo conflitto c\'è costato tantissimo. Le ferite che abbiamo subito sono molto più gravi di semplici ferite fisiche.\n\nPosso solo sperare che il Seggio del Primus rimetta le cose a posto. Sia il Bastione che Maldraxxus devono avere il tempo di guarire.', '', 38134),
(190850, 'itIT', 'Debole e miserabile!', 'Debole e miserabile!', 38134),
(190845, 'itIT', 'Le tue ossa decoreranno la mia armatura!', 'Le tue ossa decoreranno la mia armatura!', 38134),
(190844, 'itIT', 'La gloria sarà mia.', 'La gloria sarà mia.', 38134),
(190816, 'itIT', 'Questa corruzione... Dovevo aspettarmi qualcosa del genere dalle infami forze della Morte.\n\nHo percepito solo orrore quando mi sono svegliato qui. Orrore e rabbia. Ma, col tempo, il Margravio Krexus mi ha mostrato che i Maldraxxi non erano il Flagello: vi era onore tra i Prescelti.\n\nMa è stato ucciso dal suo subordinato. Gli altri Margravi avanzano verso di noi. La fame per il dominio ha divorato quel poco onore che c\'era rimasto.\n\nEpurerò questo luogo maledetto.', '', 38134),
(190815, 'itIT', '', 'Ben... fatto...', 38134),
(190814, 'itIT', '', 'Un bel combattimento.', 38134),
(190813, 'itIT', '', 'Ho... servito...', 38134),
(190812, 'itIT', '', 'Per... Revendreth...', 38134),
(190811, 'itIT', '', 'Ritorno... alla pietra...', 38134),
(190810, 'itIT', '', 'Così... la mia guardia... finisce.', 38134),
(190809, 'itIT', '', 'Glorioso.', 38134),
(190808, 'itIT', '', 'Affrontami!', 38134),
(190807, 'itIT', '', 'Questa è la tua fine.', 38134),
(190806, 'itIT', '', 'Incontra la morte con dignità.', 38134),
(190805, 'itIT', '', 'È giunta la tua ora.', 38134),
(190804, 'itIT', '', 'Prima muori e poi soffri.', 38134),
(190803, 'itIT', '', 'Mi ero sempre immaginata che l\'aldilà sarebbe stato stravagante e bellissimo, un regno tutto per me dove avrei potuto ammirare ed essere ammirata.\n\nImmagina la mia sorpresa quando sono arrivata in un deserto necrotico più arido delle Terre Esterne e più ostile delle profondità schiaccianti. \n\nImmagina la mia sorpresa quando ho scoperto che mi piaceva. \n\nQuesta lotta per il dominio è più stimolante di qualsiasi lusso possa esistere.', 38134),
(190802, 'itIT', '', 'Muori con onore.', 38134),
(190801, 'itIT', '', 'Per il Maestro!', 38134),
(190796, 'itIT', 'Apprendista! Che bello vederti. Ti ringrazio per il tuo aiuto con la mia ricerca sul campo!\n\nNon so spiegarlo, ma c\'è qualcosa di diverso, ora. È come se un grosso peso fosse stato tolto dalle spalle di Maldraxxus.\n\nForse sono semplicemente felice che i nostri visitatori del Bastione abbiano fatto amicizia con le mie care poltiglie!\n\nAnche se non riesco a ricordare quali di loro siano corrosive...', '', 38134),
(190759, 'itIT', 'Dolore! Dolore! Dolore!', 'Dolore! Dolore! Dolore!', 38134),
(190758, 'itIT', 'Nient\'altro che dolore!', 'Nient\'altro che dolore!', 38134),
(190648, 'itIT', '', 'Notevole! Le tue capacità sono cresciute a dismisura. Ritorniamo dal Capitano Garrick.', 38134),
(190647, 'itIT', '', 'Fammi vedere cos\'hai imparato!', 38134),
(190618, 'itIT', 'La mia gloria... ora... è tua...', 'La mia gloria... ora... è tua...', 38134),
(190613, 'itIT', 'Ucciso da... da te...', 'Uccisa da... te...', 38134),
(190612, 'itIT', 'Bel... combattimento...', 'Bel... combattimento...', 38134),
(190610, 'itIT', 'E così... è la fine...', 'E così... è la fine...', 38134),
(190608, 'itIT', 'Le gesta... rimangono... per sempre...', 'Le gesta... rimangono... per sempre...', 38134),
(190607, 'itIT', 'Ricorda... il mio nome...', 'Ricorda... il mio nome...', 38134),
(190606, 'itIT', '', 'La pazienza è la chiave per la vittoria.', 38134),
(190605, 'itIT', 'Dovresti... essere... felice...', 'Dovresti... essere... felice...', 38134),
(190604, 'itIT', 'Lascia parlare le armi!', 'Lascia parlare le armi!', 38134),
(190601, 'itIT', 'Fatti avanti e combatti!', 'Fatti avanti e combatti!', 38134),
(190600, 'itIT', 'Alla battaglia!', 'Alla battaglia!', 38134),
(190599, 'itIT', 'Preparati!', 'Preparati!', 38134),
(190598, 'itIT', 'Ti presento la mia lama!', 'Ti presento la mia lama!', 38134),
(190597, 'itIT', 'Ho ucciso nemici molto più forti di te.', 'Ho ucciso nemici molto più forti di te.', 38134),
(190596, 'itIT', 'La tua leggenda finisce qui e ora.', 'La tua leggenda finisce qui e ora.', 38134),
(190586, 'itIT', '', 'Nadjia, vieni avanti. Sarai la prima a ricevere quest\'onore.', 38134),
(190585, 'itIT', '', 'Direi che abbiamo finito. Come ti senti?', 38134),
(190583, 'itIT', 'Ora dobbiamo separarci. Forse un giorno potrai farmi visita al Picco della Serenità.', '', 38134),
(190582, 'itIT', 'Ti insegnerò come eseguire il Tocco della Morte. Potrebbe essere la chiave per salvare i tuoi amici.', '', 38134),
(190581, 'itIT', 'Sono il Maestro Pugno Alto. Pur essendo lontano, il tuo spirito mi ha raggiunto in cerca di consiglio.', '', 38134),
(190579, 'itIT', 'Adesso apri gli occhi.', '', 38134),
(190578, 'itIT', '', 'Maldraxxus si ergerà nuovamente unito.', 38134),
(190577, 'itIT', '', 'Lascia che il mondo svanisca...', 38134),
(190575, 'itIT', '', 'Chiudi gli occhi. Trova il tuo equilibrio.', 38134),
(190559, 'itIT', 'A liberare questo regno da un tiranno le cui azioni minacciano l\'intera realtà.', '', 38134),
(190558, 'itIT', 'Aiutaci a riportare Revendreth al suo scopo originario.', '', 38134),
(190557, 'itIT', 'Muoviti, reclutala! Siamo in guerra!', '', 38134),
(190556, 'itIT', 'Sempre... al servizio...', 'Sempre... al servizio...', 38134),
(190555, 'itIT', 'Ti trovi dinnanzi alla Corte dei Mietitori, disponibile e capace di accettare il nostro dono.', '', 38134),
(190553, 'itIT', 'Mi sarei... dovuto nascondere...', 'Mi sarei... dovuta nascondere...', 38134),
(190551, 'itIT', 'È stato... uno sbaglio...', 'È stato... uno sbaglio...', 38134),
(190550, 'itIT', 'Ho... seguito...', 'Ho... seguito...', 38134),
(190549, 'itIT', 'Il mio servizio... finisce...', 'Il mio servizio... finisce...', 38134),
(190548, 'itIT', 'La mia fine è giu-uuhnta...', 'La mia fine è giu-uuhnta...', 38134),
(190547, 'itIT', 'Uuh...', 'Uuh...', 38134),
(190546, 'itIT', 'Io servo gli Indegni!', 'Io servo gli Indegni!', 38134),
(190545, 'itIT', 'Lasciaci stare!', 'Lasciaci stare!', 38134),
(190544, 'itIT', 'Mi serve aiuto!', 'Mi serve aiuto!', 38134),
(190538, 'itIT', 'Intrusi!', 'Intrusi!', 38134),
(190534, 'itIT', 'Chi sei tu-uuh?!', 'Chi sei tu-uuh?!', 38134),
(190533, 'itIT', 'Indegni, proteggetemi!', 'Indegni, proteggetemi!', 38134),
(190530, 'itIT', 'Non dovresti essere qui!', 'Non dovresti essere qui!', 38134),
(190493, 'itIT', '[DEPRECATED]A Maw Walker will do much to bolster our ranks...if the soul has the mettle to survive.', '', 38134),
(190483, 'itIT', 'Per... Revendreth.', '', 38134),
(190482, 'itIT', 'Ritorno... alla pietra.', '', 38134),
(190481, 'itIT', 'Bel... combattimento...', '', 38134),
(190480, 'itIT', 'E così... è la fine.', '', 38134),
(190479, 'itIT', 'Dormire... per sempre.', '', 38134),
(190478, 'itIT', 'Ben... fatto.', '', 38134),
(190477, 'itIT', 'Ti rendo... onore.', '', 38134),
(190476, 'itIT', 'Per l\'onore!', '', 38134),
(190475, 'itIT', '', '[DEPRECATED] I have your new soul, Margrave.', 38134),
(190474, 'itIT', 'Sfodera le armi, mortale.', '', 38134),
(190473, 'itIT', 'Affrontami e non deludermi.', '', 38134),
(190472, 'itIT', 'Ti conviene fuggire.', '', 38134),
(190471, 'itIT', 'Muori, mortale.', '', 38134),
(190470, 'itIT', 'Per il Maestro!', '', 38134),
(190469, 'itIT', 'Affrontami con onore.', '', 38134),
(190456, 'itIT', 'Hai trovato pane per i tuoi denti!', 'Hai trovato pane per i tuoi denti!', 38134),
(190432, 'itIT', 'No! Non verrò cancellato!', '', 38134),
(190425, 'itIT', 'Presto... perirete... tutti...', '', 38134),
(190382, 'itIT', 'La vostra agonia si rivelerà illuminante!', '', 38134),
(190372, 'itIT', 'La mia... creazione... migliore...', '', 38134),
(190364, 'itIT', 'Cadi dinanzi alla tempesta.', '', 38134),
(190348, 'itIT', 'Come...?', '', 38134),
(190346, 'itIT', 'No...', '', 38134),
(190345, 'itIT', 'Ops.', '', 38134),
(190344, 'itIT', 'Ti triterò le ossa per farci il pane!', '', 38134),
(190343, 'itIT', 'Ti stritolo!', '', 38134),
(190342, 'itIT', 'È tempo di morire.', '', 38134),
(190341, 'itIT', 'I capi dicono di strizzarti per bene.', '', 38134),
(190338, 'itIT', 'Ti schiaccerò!', '', 38134),
(190337, 'itIT', 'Sei troppo piccolo per vincere!', '', 38134),
(190336, 'itIT', 'Tu! Mostrami cosa sai fare.', '', 38134),
(190335, 'itIT', 'Sono stanco di vittorie facili.', '', 38134),
(190334, 'itIT', 'Uno spreco delle mie abilità.', '', 38134),
(190333, 'itIT', 'Già finito?', '', 38134),
(190332, 'itIT', 'È stato... un bel... combattimento...', '', 38134),
(190331, 'itIT', 'Farete meglio a rendere le cose interessanti.', '', 38134),
(190330, 'itIT', 'Nuovi sfidanti? Umpf... vediamo quanto riuscite a resistere.', '', 38134),
(190329, 'itIT', 'Allineare accuratamente i cristalli e il flusso dell\'Animum sbloccherà la porta!', '', 38134),
(190327, 'itIT', '', 'Iniziamo le meditazioni? Trovo sempre che mi aiuti a calmare la mente per imparare qualcosa di nuovo.', 38134),
(190326, 'itIT', 'I tuoi Eletti ti ingannano.', 'I tuoi Eletti ti ingannano.', 38134),
(190325, 'itIT', 'Ti ho sottovalutato.', 'Ti ho sottovalutato.', 38134),
(190324, 'itIT', 'Gli Indegni persistono.', 'Gli Indegni persistono.', 38134),
(190323, 'itIT', 'L\'Arconte ti tradisce.', 'L\'Arconte ti tradisce.', 38134),
(190322, 'itIT', 'Le Terretetre sono spacciate.', 'Le Terretetre sono spacciate.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(190321, 'itIT', 'Ti hanno fuorviato.', 'Ti hanno fuorviato.', 38134),
(190320, 'itIT', 'La nostra causa continuerà a vivere.', 'La nostra causa continuerà a vivere.', 38134),
(190319, 'itIT', 'Non mi pento di nulla...', 'Non mi pento di nulla...', 38134),
(190318, 'itIT', 'Una degna fine.', 'Una degna fine.', 38134),
(190317, 'itIT', 'Altri verranno a prenderti.', 'Altri verranno a prenderti.', 38134),
(190316, 'itIT', 'Altri due prenderanno il mio posto.', 'Altri due prenderanno il mio posto.', 38134),
(190315, 'itIT', 'Ho fallito...', 'Ho fallito...', 38134),
(190314, 'itIT', 'Una degna sfida.', 'Una degna sfida.', 38134),
(190313, 'itIT', 'Non dimenticherò mai.', 'Non dimenticherò mai.', 38134),
(190312, 'itIT', 'Incontrerai presto il tuo destino.', 'Incontrerai presto il tuo destino.', 38134),
(190311, 'itIT', 'Ora nessuna prova mi vincola.', 'Ora nessuna prova mi vincola.', 38134),
(190310, 'itIT', 'Ricordati di me.', 'Ricordati di me.', 38134),
(190309, 'itIT', 'Ho fallito.', 'Ho fallito.', 38134),
(190308, 'itIT', 'Sono stato ingannato?', 'Sono stata ingannata?', 38134),
(190307, 'itIT', 'Canteranno le mie gesta?', 'Canteranno le mie gesta?', 38134),
(190306, 'itIT', 'Volevo volare...', 'Volevo volare...', 38134),
(190305, 'itIT', 'Non ero pronto.', 'Non ero pronta.', 38134),
(190304, 'itIT', 'Che cosa sei tu?', 'Che cosa sei?', 38134),
(190303, 'itIT', 'Riuscirai a sconfiggere tutti noi?', 'Riuscirai a sconfiggere tutti noi?', 38134),
(190302, 'itIT', 'Sei troppo forte.', 'Sei troppo forte.', 38134),
(190301, 'itIT', 'Il mio addestramento non mi aveva preparato a questo.', 'Il mio addestramento non mi aveva preparata a questo.', 38134),
(190300, 'itIT', 'Ho rappresentato una sfida per te?', 'Ho rappresentato una sfida per te?', 38134),
(190299, 'itIT', 'Non otterrò mai le mie ali...', 'Non ho mai ottenuto le mie ali...', 38134),
(190298, 'itIT', 'Così tanto orgoglio.', 'Così tanto orgoglio.', 38134),
(190297, 'itIT', 'Se è una fine veloce che cerchi, così sia.', 'Se è una fine veloce che cerchi, così sia.', 38134),
(190296, 'itIT', 'Un incontro insignificante.', 'Un incontro insignificante.', 38134),
(190295, 'itIT', 'Per Devos!', 'Per Devos!', 38134),
(190293, 'itIT', 'Avresti potuto unirti a noi.', 'Avresti potuto unirti a noi.', 38134),
(190292, 'itIT', 'Molto bene...', 'Molto bene...', 38134),
(190290, 'itIT', 'Il nostro scopo è retto.', 'Il nostro scopo è retto.', 38134),
(190289, 'itIT', 'Ti respingerò con tutto me stesso.', 'Ti respingerò con tutta me stessa.', 38134),
(190288, 'itIT', 'Il dovere mi vincola.', 'Il dovere mi vincola.', 38134),
(190287, 'itIT', 'Quindi hai deciso di combatterci.', 'Quindi hai deciso di combatterci.', 38134),
(190286, 'itIT', 'Non fermerai la nostra causa!', 'Non fermerai la nostra causa!', 38134),
(190285, 'itIT', 'I poeti narreranno questa battaglia.', 'I poeti narreranno questa battaglia.', 38134),
(190284, 'itIT', 'Devos, dammi la forza!', 'Devos, dammi la forza!', 38134),
(190283, 'itIT', 'L\'Arconte non può salvarti da me.', 'Il tuo Arconte non può salvarti da me.', 38134),
(190282, 'itIT', 'Vediamo quanto resisterai.', 'Vediamo quanto resisterai.', 38134),
(190281, 'itIT', 'I miei ricordi mi danno uno scopo.', 'I miei ricordi mi danno uno scopo.', 38134),
(190280, 'itIT', 'Il nostro lavoro non dev\'essere interrotto!', 'Il nostro lavoro non dev\'essere interrotto!', 38134),
(190279, 'itIT', 'Il destino ti ha condotto qui.', 'Il destino ti ha condotto qui.', 38134),
(190278, 'itIT', 'Il tuo destino è giunto alla fine.', 'Il tuo destino è giunto alla fine.', 38134),
(190277, 'itIT', 'Mi applaudiranno quando ti finirò.', 'Mi applaudiranno quando ti finirò.', 38134),
(190276, 'itIT', 'Venire qui è stato arrogante da parte tua.', 'Venire qui è stato arrogante da parte tua.', 38134),
(190275, 'itIT', 'Ho la fortuna di avere un nuovo sfidante.', 'Ho la fortuna di avere un nuovo sfidante.', 38134),
(190274, 'itIT', 'Tu? Osi sfidare me?', 'Tu? Osi sfidare me?', 38134),
(190273, 'itIT', 'Non puoi vincere contro gli Indegni.', 'Non puoi vincere contro gli Indegni.', 38134),
(190272, 'itIT', 'Mi sono addestrato eoni per questo!', 'Mi sono addestrata secoli per questo!', 38134),
(190271, 'itIT', 'Affronta gli Indegni!', 'Affronta gli Indegni!', 38134),
(190270, 'itIT', 'È tempo di dimostrare il mio valore!', 'È tempo di dimostrare il mio valore!', 38134),
(190269, 'itIT', 'La tua testa mi farà guadagnare le ali.', 'La tua testa mi farà guadagnare le ali.', 38134),
(190268, 'itIT', '', 'Vieni. Andiamo a meditare per sbloccare il nostro potenziale interiore.', 38134),
(190266, 'itIT', 'Ti stacco la testa, così vedi come schiaccio il resto!', '', 38134),
(190251, 'itIT', 'Ho fatto un bel casino...', '', 38134),
(190250, 'itIT', 'Avevo ancora... un lavoretto... in sospeso...', '', 38134),
(190249, 'itIT', 'Spero di diventare... un Tipo Tosto...', '', 38134),
(190244, 'itIT', 'Muori! Se per te va bene.', '', 38134),
(190243, 'itIT', 'Questa non è la mia specialità, quindi se potessi non muoverti lo apprezzerei molto.', '', 38134),
(190207, 'itIT', 'Il nostro dovere è giusto. Il nostro scopo è puro.', 'Il nostro dovere è giusto. Il nostro scopo è puro.', 38134),
(190194, 'itIT', 'Be\', alla fine è successo... no?', '', 38134),
(190193, 'itIT', 'Stavo solo... facendo il mio lavoro...', '', 38134),
(190192, 'itIT', 'Mi stavo appena abituando... a questo corpo...', '', 38134),
(190191, 'itIT', 'Mi spiace, gli ordini sono ordini.', '', 38134),
(190190, 'itIT', 'Il capo dice che devi andartene, quindi... devi andartene.', '', 38134),
(190181, 'itIT', 'Il tuo destino è segnato!', 'Il tuo destino è segnato!', 38134),
(190180, 'itIT', 'Non ti muovere mentre te le suono!', '', 38134),
(190163, 'itIT', 'Ti ho insegnato le basi, ora trova una bestia e addomesticala.', '', 38134),
(190162, 'itIT', 'Non abbiamo molto tempo, dovrai imparare in fretta.', '', 38134),
(190161, 'itIT', '', 'La vostra prova termina in un fallimento.', 38134),
(190160, 'itIT', '', 'Accetta la tua sconfitta.', 38134),
(190159, 'itIT', '', 'Non potevi vincere.', 38134),
(190157, 'itIT', 'Sconfiggere Denathrius non è abbastanza, dobbiamo annientare la sua morsa su Revendreth.', '', 38134),
(190156, 'itIT', 'Questa tua abilità da sola è già perfetta per il mio piano, ma dentro di te c\'è molto più di ciò che sembra.', '', 38134),
(190155, 'itIT', 'No, tu puoi infrangere i confini della Fauce con facilità, è qualcosa che nemmeno i Venthyr più anziani sono in grado di fare.', '', 38134),
(190153, 'itIT', 'Ti starai chiedendo perché siamo disposti a trasmettere il nostro potere a un mortale, ma tu non sei come gli altri mortali, vero?', '', 38134),
(190143, 'itIT', '', 'Tutte... le cose... devono finire...', 38134),
(190141, 'itIT', '', 'Ammirate il mio potere e disperate!', 38134),
(190140, 'itIT', '', 'Finalmente dei degni sfidanti...', 38134),
