INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'ruRU', '', 'Здравствуй. Тебя интересуют ключи?', 38134),
(209048, 'ruRU', 'В Орибосе тебе поможет агрессор Зо\'даш. Также ты можешь улучшать предметы в обители ковенанта.', 'В Орибосе тебе поможет агрессор Зо\'даш. Также ты можешь улучшать предметы в обители ковенанта.', 38134),
(208581, 'ruRU', 'Приветствую, ЮЗЕРНЕЙМ. Желаю СЛУЧАЙНОЕ ПОЛОЖИТЕЛЬНОЕ ПРИЛАГАТЕЛЬНОЕ дня.', '', 38134),
(208300, 'ruRU', 'Я по мере сил помогаю юным душам, которые отдают мне на попечение. Баббинс скоро исправится!', '', 38134),
(208299, 'ruRU', 'Я всегда говорил: чтобы перевоспитать грубияна, нужен строгий этикет, хорошая компания и имя, которое рифмуется с \"наббинс\"!', '', 38134),
(208298, 'ruRU', 'Обвинительница совсем недавно приставила меня к Теотару. Он помогает мне осознать мои прошлые ошибки!', '', 38134),
(208297, 'ruRU', 'Баббинс предложил пару новых ингредиентов для этого чая. Надеемся, тебе понравится!', '', 38134),
(208296, 'ruRU', 'Ты делаешь большие успехи, Баббинс. На этот раз ты никого не ткнул в глаз!', '', 38134),
(208292, 'ruRU', 'Приветствую, $gмой дорогой:моя дорогая; $p.\n\n<Баббинс сверяется с каракулями на тыльной стороне руки.>\n\nПриятно познакомиться. Теотар мне много про тебя рассказывал.\n\nИзволишь чашечку чаю? Или подать тебе зонтик в этот прекрасный день?\n\n<Баббинс изо всех сил пытается выглядеть естественно, растягивая губы в улыбке в ожидании вашего ответа.>', '', 38134),
(208211, 'ruRU', 'Мой прежний хозяин, хранитель склепа Каззир, хочет встретиться с тобой в Чертогах Покаяния, $p.', '', 38134),
(208209, 'ruRU', 'Таббинс спрашивал о тебе в Пепельном Пределе, $p.', '', 38134),
(208205, 'ruRU', 'Гресит хочет видеть тебя в Чертогах Покаяния, $p.', '', 38134),
(208120, 'ruRU', 'Некоторые дни особые! Увидишь тут мой золотой сундук – значит, особый день! Это когда мы чествуем распорядителей. \n\nСпроси меня – кто распорядитель дня? И дай ему спелый чистоплод. Потом вернись, открой сундук. Ура!', '', 38134),
(208019, 'ruRU', 'Один из нас. Один из нас. Один из нас...', '', 38134),
(208018, 'ruRU', 'Полемарх Адрест притоптывает, напевая мелодию себе под нос.', '', 38134),
(208016, 'ruRU', '', 'Я буду следить за тобой.', 38134),
(208015, 'ruRU', 'Лучший друг приносит мне лучшие записки!', '', 38134),
(208014, 'ruRU', '', 'Много друзей. Много радости. Сике повезло.', 38134),
(208013, 'ruRU', 'Хм... Это мой лучший друг и подмастерье. Как бы это выразиться... мой лучший другмастерье? Хорошо звучит? Нет, что-то не так...', '', 38134),
(208012, 'ruRU', 'Надеюсь, что никакая. Вместе мы сильнее.', '', 38134),
(208011, 'ruRU', '', 'Если смерть свела нас вместе, то какая сила может разлучить?', 38134),
(208010, 'ruRU', 'Все резко заткнитесь и слушайте моего лучшего друга. $p знает о боях больше, чем вы все, вместе взятые!', '', 38134),
(208009, 'ruRU', '', 'Это дерево расцветает в лесу, $p. Возвращайся в мою рощу, и мы будем процветать вместе.', 38134),
(208008, 'ruRU', 'Чуфа начинает нараспев повторять одну и ту же фразу.', '', 38134),
(208007, 'ruRU', 'И мы всем скажем, что $p – лучший друг землероев!', '', 38134),
(208006, 'ruRU', 'У нас теперь много друзей, но лучший друг бывает только один...', '', 38134),
(208005, 'ruRU', 'Как ни странно, я с нетерпением жду следующего приема. Может, даже потанцую с кем-нибудь из аристократов.', '', 38134),
(208004, 'ruRU', '', 'За одной охотой всегда идет другая, но $p – особенная душа. Других таких я не знаю.', 38134),
(208003, 'ruRU', 'Если тебе что-то понадобится, мой клинок к твоим услугам. Я всегда отвечу на твой зов.', '', 38134),
(208002, 'ruRU', '', '$p, дорогуша, поздравляю. Твой прием прекрасен, и ты тоже.', 38134),
(208001, 'ruRU', 'Присесть, расслабиться, не спешить. Для лучшего друга всегда есть время.', '', 38134),
(208000, 'ruRU', '', 'Во что еще мы с тобой ввяжемся, $p? Мне уже интересно!', 38134),
(207999, 'ruRU', '', 'Не буду тебя задерживать, $p.', 38134),
(207996, 'ruRU', 'Осторожно, $p. Всегда есть души, которые еще можно спасти, но помни, что многие уже потеряны.', '', 38134),
(207951, 'ruRU', 'Ошибка. Доступ запрещен, у пользователя недостаточно опыта.', '', 38134),
(207923, 'ruRU', 'Нам предстоит еще многое сделать для Арденвельда, $n.', 'Нам предстоит еще многое сделать для Арденвельда, $n.', 38134),
(207903, 'ruRU', '', 'Хочешь послушать интересную историю?', 38134),
(207841, 'ruRU', '$n! Эй, поди сюда!', '', 38134),
(207504, 'ruRU', 'Сегодня славный день для полета! Куда тебя отвезти?', '', 38134),
(207499, 'ruRU', '', '<Леди Илинка улыбается.>\n\nОтличный вопрос. Менять список гостей раз в неделю очень полезно. Так можно убедиться, что все гости друг с другом ладят.\n\nЧтобы отозвать любое приглашение, попроси у лорда Гарридана список.\n\nВозможно, придется потратить пару насыщенных рубинов, чтобы никто не оскорбился такой сменой планов. Еще ты можешь нанять послов или приобрести у меня незаполненные приглашения.\n\nИ главное: постарайся не приглашать на прием гостей со слишком разными вкусами. Угодить всем будет слишком трудно.', 38134),
(207446, 'ruRU', 'Множество – необычный кадавр. Ее удерживает в Темных Землях истовая верность долгу, и она может находить доспехи, символизирующие наше предназначение, когда выполняет задания Неумирающей армии в Малдраксусе.', 'Множество – необычный кадавр. Ее удерживает в Темных Землях истовая верность долгу, и она может находить доспехи, символизирующие наше предназначение, когда выполняет задания Неумирающей армии в Малдраксусе.', 38134),
(207442, 'ruRU', '', 'Знаю. Я все объясню после того, как ты доставишь своего друга в Орибос.\n\nВозвращайся, когда он будет в безопасности.', 38134),
(207434, 'ruRU', 'Кто там? Это ты, Теотар?', '', 38134),
(207388, 'ruRU', '', 'Хочешь о чем-то попросить?', 38134),
(207376, 'ruRU', 'Жаль, что ты предпочитаешь силе формальности, пилигрим Утробы.', 'Жаль, что ты предпочитаешь силе формальности, пилигрим Утробы.', 38134),
(207372, 'ruRU', 'Ночному народцу повезло, что их ковенанту служит отважный пилигрим Утробы.', 'Ночному народцу повезло, что их ковенанту служит отважный пилигрим Утробы.', 38134),
(207371, 'ruRU', 'Кажется, тебя призвала на службу природа, $n. Благородное призвание.', 'Кажется, тебя призвала на службу природа, $n. Благородное призвание.', 38134),
(207370, 'ruRU', 'Хотя ты принадлежишь к другому ковенанту, но служишь великому циклу, пилигрим Утробы. Это действительно благородное дело.', 'Хотя ты принадлежишь к другому ковенанту, но служишь великому циклу, пилигрим Утробы. Это действительно благородное дело.', 38134),
(207369, 'ruRU', 'Передай привет Королеве Зимы. Она верный союзник Архонта.', 'Передай привет Королеве Зимы. Она верный союзник Архонта.', 38134),
(207368, 'ruRU', 'Надеюсь, ты обретешь смысл бытия, собирая свою... жатву, $n.', 'Надеюсь, ты обретешь смысл бытия, собирая свою... жатву, $n.', 38134),
(207367, 'ruRU', 'Пилигрим Утробы. Пусть твое наставничество поможет грешным душам освободиться от бремени.', 'Пилигрим Утробы. Пусть твое наставничество поможет грешным душам освободиться от бремени.', 38134),
(207366, 'ruRU', 'Вентиры понимают, что прошлое – это бремя. У наших ковенантов много общего, $n.', 'Вентиры понимают, что прошлое – это бремя. У наших ковенантов много общего, $n.', 38134),
(207365, 'ruRU', 'Ты хочешь служить вентирам? Вести души к искуплению – действительно достойное дело.', 'Ты хочешь служить вентирам? Вести души к искуплению – действительно достойное дело.', 38134),
(207364, 'ruRU', 'Служи верно... какой бы ни была служба.', 'Служи верно... какой бы ни была служба.', 38134),
(207363, 'ruRU', 'Милость Архонта не оставит тебя – не важно, какой у тебя ковенант.', 'Милость Архонта не оставит тебя – не важно, какой у тебя ковенант.', 38134),
(207362, 'ruRU', 'Привнеси порядок в царство раздора, $n.', 'Привнеси порядок в царство раздора, $n.', 38134),
(207361, 'ruRU', 'Я думал, служение подходит тебе больше, чем война, пилигрим Утробы. Возможно, я ошибался.', 'Я думала, служение подходит тебе больше, чем война, пилигрим Утробы. Возможно, я ошибалась.', 38134),
(207360, 'ruRU', 'То, что ты служишь нам, – большая честь для нашего ковенанта, пилигрим Утробы.', 'То, что ты служишь нам, – большая честь для нашего ковенанта, пилигрим Утробы.', 38134),
(207359, 'ruRU', 'Я рад, что наши пути пересеклись, $n. Твое усердие весьма похвально.', 'Я рада, что наши пути пересеклись, $n. Твое усердие весьма похвально.', 38134),
(207358, 'ruRU', 'А! Пилигрим Утробы! Несомненно, у тебя поручение от Архонта. Служи верно.', 'А! Пилигрим Утробы! Несомненно, у тебя поручение от Архонта. Служи верно.', 38134),
(207357, 'ruRU', 'Нам повезло, что ты с нами, $n. Да укажет тебе путь милость Архонта.', 'Нам повезло, что ты с нами, $n. Да укажет тебе путь милость Архонта.', 38134),
(207356, 'ruRU', 'Когда устанешь резвиться с пикси, возвращайся сюда. Разомнемся как следует.', 'Когда устанешь резвиться с пикси, возвращайся сюда. Разомнемся как следует.', 38134),
(207355, 'ruRU', 'Передай наши наилучшие пожелания Королеве Зимы, пилигрим Утробы.', 'Передай наши наилучшие пожелания Королеве Зимы, пилигрим Утробы.', 38134),
(207354, 'ruRU', 'Пусть наши леса не такие величественные, как в Арденвельде, но мы тоже сражаемся за свое царство.', 'Пусть наши леса не такие величественные, как в Арденвельде, но мы тоже сражаемся за свое царство.', 38134),
(207353, 'ruRU', 'Ночной народец не борется за власть? Чем же они занимаются в своих запущенных рощах?', 'Ночной народец не борется за власть? Чем же они занимаются в своих запущенных рощах?', 38134),
(207352, 'ruRU', 'Хорошо, что ты поддерживаешь наш ковенант, $n. По-моему, бархат будет тебе очень к лицу.', 'Хорошо, что ты поддерживаешь наш ковенант, $n. По-моему, бархат будет тебе очень к лицу.', 38134),
(207351, 'ruRU', 'Я за Двор Жнецов. И за тебя, пилигрим Утробы.', 'Я за Двор Жнецов. И за тебя, пилигрим Утробы.', 38134),
(207350, 'ruRU', 'Какие интересные проступки ты, наверное, собираешь для своего камня грехов!', 'Какие интересные проступки ты, наверное, собираешь для своего камня грехов!', 38134),
(207349, 'ruRU', 'С твоей помощью, $n, мы вернем себе наше благородное предназначение.', 'С твоей помощью, $n, мы вернем себе наше благородное предназначение.', 38134),
(207348, 'ruRU', 'Готов поспорить, из всех ковенантов у кирий самые скучные камни грехов.', 'Готова поспорить, из всех ковенантов у кирий самые скучные камни грехов.', 38134),
(207347, 'ruRU', 'Может, Ревендрет и полон теней, кирия, но наш долг не менее благороден, чем твой.', 'Может, Ревендрет и полон теней, кирия, но наш долг не менее благороден, чем твой.', 38134),
(207346, 'ruRU', '', 'Благодарю... Приятно знать, что мои последние усилия не пропали даром.', 38134),
(207345, 'ruRU', 'Ваши распорядители, кажется, отлично обучены, $n. Знаешь, в некоторых наших склепах не помешало бы прибраться...', 'Ваши распорядители, кажется, отлично обучены, $n. Знаешь, в некоторых наших склепах не помешало бы прибраться...', 38134),
(207343, 'ruRU', 'Хотя у нас разные методы, оба наших ковенанта стремятся направить души на благородный путь.', 'Хотя у нас разные методы, оба наших ковенанта стремятся направить души на благородный путь.', 38134),
(207342, 'ruRU', 'Ты выбираешь жестокий путь, смертное существо. Если захочется немного изящества, ты знаешь, где нас искать.', 'Ты выбираешь жестокий путь, смертное существо. Если захочется немного изящества, ты знаешь, где нас искать.', 38134),
(207341, 'ruRU', '', 'Маркграф погиб, но это не значит, что мы бросили свое дело. Когда придет время, мы отомстим.\n\nПодожди... Ты... ты из Азерота. Скажи, Некоронованные получили мое послание?', 38134),
(207340, 'ruRU', 'Знаешь, Пепельный Двор опаснее, чем кажется. Думаю, тебе бы он понравился.', 'Знаешь, Пепельный Двор опаснее, чем кажется. Думаю, тебе бы он понравился.', 38134),
(207339, 'ruRU', 'Хорошо, что твой ковенант сражается за наше царство, пилигрим Утробы.', 'Хорошо, что твой ковенант сражается за наше царство, пилигрим Утробы.', 38134),
(207338, 'ruRU', '', 'Маркграф погиб, но это не значит, что мы бросили свое дело. Когда придет время, мы отомстим.', 38134),
(207337, 'ruRU', 'Убери скальпель и иглы, некролорд. Моя кожа мне еще нужна.', 'Убери скальпель и иглы, некролорд. Моя кожа мне еще нужна.', 38134),
(207335, 'ruRU', 'За тобой тянется какой-то след, $n. Это что... блестки пикси?', 'За тобой тянется какой-то след, $n. Это что... блестки пикси?', 38134),
(207334, 'ruRU', 'Пилигрим Утробы! Тебе не надоело ухаживать за рощами? Не скучаешь по настоящей драке?', 'Пилигрим Утробы! Тебе не надоело ухаживать за рощами? Не скучаешь по настоящей драке?', 38134),
(207333, 'ruRU', 'Пикси любят уловки. Некролорды предпочитают стратегию.', 'Пикси любят уловки. Некролорды предпочитают стратегию.', 38134),
(207332, 'ruRU', 'Нет. Я на твой спектакль не приду.', 'Нет. Я на твой спектакль не приду.', 38134),
(207331, 'ruRU', 'Лучший способ избавить душу от гордыни – выколотить ее.', 'Лучший способ избавить душу от гордыни – выколотить ее.', 38134),
(207330, 'ruRU', 'Будь осторожнее, вентир. Запачкаешь обувь слизью.', 'Будь осторожнее, вентир. Запачкаешь обувь слизью.', 38134),
(207329, 'ruRU', 'Фракции. Интриги. Почему бы вентирам просто не подраться хорошенько?', 'Фракции. Интриги. Почему бы вентирам просто не подраться хорошенько?', 38134),
(207328, 'ruRU', 'Да, мои дни полны битв... но зато я сохранил свои воспоминания.', 'Да, мои дни полны битв... но зато я сохранила свои воспоминания.', 38134),
(207327, 'ruRU', 'Здесь ты не найдешь прудов для медитации, кирия.', 'Здесь ты не найдешь прудов для медитации, кирия.', 38134),
(207326, 'ruRU', 'Иди сюда. Покажи мне, каким изящным приемам тебя научили в Бастионе.', 'Иди сюда. Покажи мне, каким изящным приемам тебя научили в Бастионе.', 38134),
(207324, 'ruRU', 'Я бы пригласил тебя в Театр Боли, но там твой наряд может испачкаться.', 'Я бы пригласила тебя в Театр Боли, но там твой наряд может испачкаться.', 38134),
(207323, 'ruRU', 'Когда твоя смертная оболочка придет в негодность, мы сможем сделать тебе новую.', 'Когда твоя смертная оболочка придет в негодность, мы сможем сделать тебе новую.', 38134),
(207321, 'ruRU', 'Ты воплощение наследия Примаса, $n.', 'Ты воплощение наследия Примаса, $n.', 38134),
(207320, 'ruRU', 'Хорошо, что ты на нашей стороне, $n. Другие ковенанты будут завидовать твоей силе.', 'Хорошо, что ты на нашей стороне, $n. Другие ковенанты будут завидовать твоей силе.', 38134),
(207319, 'ruRU', 'Не знаю, что нам готовит судьба, но за это придется сражаться.', 'Не знаю, что нам готовит судьба, но за это придется сражаться.', 38134),
(207318, 'ruRU', 'Старый Жуль в жизни не видел ничего подобного!\n\n<Жуль делает вам знак рукой, наклоняется ближе и шепчет.>\n\nТсс! Потише!\n\nЭти самые \"брокеры\" предлагают нам, \"смертным\", всякие разные штуки, и я пытаюсь разобраться, что же это такое.\n\nМожет, старина Жуль найдет какие-нибудь новые амулеты на продажу? Ха...', '', 38134),
(207316, 'ruRU', 'А! Еще одна душа из Азерота.\n\nТочнее, не душа. Свет Ан\'ше еще согревает твою плоть.\n\nХотел бы я взглянуть на Крутогорье хотя бы еще один раз. Моя семья еще живет там, если на то воля Матери-Земли.\n\nМногим здесь не терпится переродиться, поскольку их преследуют сожаления о прошлом. Но меня радуют воспоминания о тех, кого я любил. Надеюсь, они меня тоже поминают добрым словом. Когда-нибудь мне придется про них забыть.\n\nНо не сегодня. Нет, не сегодня.', '', 38134),
(207315, 'ruRU', 'Я тебя знаю. Мы вместе сражались в Крутогорье. Кажется, с тобой все в порядке.\n\nНадеюсь, Гура и мальчики не слишком долго меня оплакивали, и Гура нашла новую любовь. Но есть вещи, которые я не хочу забывать. Запах Гуры. Как мы придумали имя для первенца. Как я учил Мику бесшумно красться с ножом в руках. Подобно призраку...\n\nНе помню, что со мной случилось. Только то, что это произошло быстро. А что случилось с Даргрулом?\n\nНет. Нет, не говори мне. Это – заботы из прошлой жизни. Этот гнев, эта боль... Вот почему кирии освобождают нас от бремени воспоминаний.', '', 38134),
(207314, 'ruRU', 'Тебе нравятся наши вечные сумерки, вентир?', 'Тебе нравятся наши вечные сумерки, вентир?', 38134),
(207313, 'ruRU', 'Мне не понять страсть вентиров к камню.', 'Мне не понять страсть вентиров к камню.', 38134),
(207312, 'ruRU', 'Будь всегда начеку, некролорд! Не подведи нас.', 'Будь всегда начеку, некролорд! Не подведи нас.', 38134),
(207311, 'ruRU', 'Союзник Примаса – союзник леса.', 'Союзник Примаса – союзник леса.', 38134),
(207310, 'ruRU', 'Мы с тобой верно служим своим ковенантам.', 'Мы с тобой верно служим своим ковенантам.', 38134),
(207309, 'ruRU', 'Да поднимет наш ветер твои крылья, кирия.', 'Да поднимет наш ветер твои крылья, кирия.', 38134),
(207308, 'ruRU', 'Двору Ночи повезло, что ты с нами, $n.', 'Двору Ночи повезло, что ты с нами, $n.', 38134),
(207307, 'ruRU', 'Мы благодарны тебе за поддержку нашего ковенанта, пилигрим Утробы. За королеву и рощу!', 'Мы благодарны тебе за поддержку нашего ковенанта, пилигрим Утробы. За королеву и рощу!', 38134),
(207306, 'ruRU', 'Здесь нет душ, одержимых гордыней, вентир.', 'Здесь нет душ, одержимых гордыней, вентир.', 38134),
(207305, 'ruRU', 'Дикая Охота тебя не подведет, некролорд.', 'Дикая Охота тебя не подведет, некролорд.', 38134),
(207304, 'ruRU', 'Ступай без страха, кирия.', 'Ступай без страха, кирия.', 38134),
(207302, 'ruRU', 'Да укроет тебя ночь, вентир. Хотя, уверен, она тебя уже укрывает.', 'Да укроет тебя ночь, вентир. Хотя, уверена, она тебя уже укрывает.', 38134),
(207301, 'ruRU', 'Надолго ли ты задержишься в лесу, некролорд?', 'Надолго ли ты задержишься в лесу, некролорд?', 38134),
(207300, 'ruRU', 'Удачи, кирия. Надеюсь, Бастион переживает засуху без потерь.', 'Удачи, кирия. Надеюсь, Бастион переживает засуху без потерь.', 38134),
(207299, 'ruRU', 'Рощи у тебя в долгу, $n!', 'Рощи у тебя в долгу, $n!', 38134),
(207298, 'ruRU', 'Вентир, здесь? Ну и дела. Так себе дела, если честно.', 'Вентир, здесь? Ну и дела. Так себе дела, если честно.', 38134),
(207297, 'ruRU', 'Что это за запах? А, некролорд. Ну конечно.', 'Что это за запах? А, некролорд. Ну конечно.', 38134),
(207295, 'ruRU', 'О, ты из кирий! Но у тебя нет крыльев... в отличие от меня!', 'О, ты из кирий! Но у тебя нет крыльев... в отличие от меня!', 38134),
(207294, 'ruRU', 'О, это же $n! Я просто без ума от твоей пьесы!', 'О, это же $n! Я просто без ума от твоей пьесы!', 38134),
(207289, 'ruRU', 'А ведь мне оставалось меньше года до получения шлема...\n\n<Адмирал Тейлор вздыхает, хотя ему больше не нужно дышать.>', '', 38134),
(207288, 'ruRU', 'Всегда рад стараться.', '', 38134),
(207287, 'ruRU', 'Я помню, что это ты убил меня.', '', 38134),
(207286, 'ruRU', 'А ты, Кольтира? Что ты помнишь о своей гибели?', '', 38134),
(207285, 'ruRU', 'Нет смысла об этом рассуждать. То, что случилось, нельзя изменить. Мы можем только двигаться вперед – неумолимо, как сама смерть.', '', 38134),
(207284, 'ruRU', 'Ясно. Какую, по-твоему, судьбу выбрала бы для нас Арбитр?', '', 38134),
(207283, 'ruRU', 'Если да, то я этого не помню.', '', 38134),
(207282, 'ruRU', 'Ты помнишь что-нибудь о своей смерти, Тассариан? Кирии пришли за твоей душой?', '', 38134),
(207281, 'ruRU', 'Это правда.', '', 38134),
(207280, 'ruRU', 'Или Сильвана Ветрокрылая.', '', 38134),
(207279, 'ruRU', 'Например, Генн Седогрив?', '', 38134),
(207278, 'ruRU', 'Болвар Фордрагон больше не Король-лич. Но кто-то все равно должен нами командовать, и он не самый плохой вариант. Есть и похуже.', '', 38134),
(207277, 'ruRU', 'А теперь мы сражаемся за того, кто носил его.', '', 38134),
(207276, 'ruRU', 'Шлем Господства расколот. Странная мысль. Так долго он был символом нашего проклятья и долга.', '', 38134),
(207272, 'ruRU', 'Знаешь, как я погиб, $n? Защищал Кель\'Талас под предводительством командира следопытов Сильваны Ветрокрылой.\n\nОна отдала жизнь за свое королевство, свой народ. И в награду за все это стала нежитью. Как и я. Но потом наши пути разошлись.\n\nСильвана причинила больше зла и страданий, чем кто-либо из нас пережил при жизни. Она заточила меня в недрах Подгорода на долгие годы, когда я осмелился перевязать рану, заключив недолгий мир.\n\nДаже ордену Черного Клинка не удалось добиться моего освобождения. Что бы ни говорили о Королеве-банши, она всегда шла до конца и не сдавалась. Как при жизни, так и после смерти.\n\nПобедить Сильвану будет непросто. Она всегда была опасным врагом.', '', 38134),
(207271, 'ruRU', 'Я начал сражаться, когда чума нежити только появилась в Лордероне. Это было очень давно. И так вышло, что после смерти мне пришлось сражаться куда дольше, чем при жизни.\n\nПо сравнению с силами, с которыми я столкнулся по ту сторону завесы, войны живых кажутся ничтожными. Я поклялся в верности Альянсу и буду защищать его, пока не погибну окончательно. Но есть угрозы пострашнее Орды.\n\nВозможно, теперь, когда небеса над Ледяной Короной разорваны и смерть дышит Альянсу в затылок, его представители это понимают. Таково проклятие живых.\n\nОни не знают, что действительно важно, пока не приходит их время.', '', 38134),
(207270, 'ruRU', 'Мне нужен могучий кадавр. Чтобы по земле шли трещины, когда я прыгаю.', '', 38134),
(207269, 'ruRU', 'Мне нужно сильное тело со множеством крюков, чтобы вешать разные вещи. В наших приключениях я буду носить тебя и все свои исследовательские материалы!', '', 38134),
(207268, 'ruRU', '', '<Филия шепчет вам.>\n\nПожалуйста, не говори Эмме об Утробе.\n\nЯ... не думаю, что ее сердце это выдержит.', 38134),
(207266, 'ruRU', '', 'О, привет! Ты вроде хорошо здесь ориентируешься.\n\nНе знаешь, где мои сыновья Джем, Джек и Джейк? Они должны быть где-то рядом.\n\nНикуда отсюда не уйду без моих мальчиков. Я не хочу снова их потерять!', 38134),
(207251, 'ruRU', 'Я чту всех, кто служит нашей цели.', 'Я чту всех, кто служит нашей цели.', 38134),
(207250, 'ruRU', '<Станикос бросается наперерез.>\n\nТы их не использовать! Я только... одолжить!', '<Станикос бросается наперерез.>\n\nТы их не использовать! Я только... одолжить!', 38134),
(207248, 'ruRU', '<Станикос угрюмо смотрит на вас.>\n\nТвои вещи. То, что ты трогать. Да.', '<Станикос угрюмо смотрит на вас.>\n\nТвои вещи. То, что ты трогать. Да.', 38134),
(207247, 'ruRU', 'Ты про... мою коллекцию? Хорошая, да?', 'Ты про... мою коллекцию? Хорошая, да?', 38134),
(207241, 'ruRU', 'Нет! Тебе не скрыться, Ве\'нари! Ты... А-а-а!', '', 38134),
(207231, 'ruRU', '', 'Что?', 38134),
(207230, 'ruRU', 'Я знаю, что поступки красноречивее слов, пилигрим Утробы. Здесь мы оба должны показать свои намерения.', '', 38134),
(207229, 'ruRU', 'Потеряв вождей, последователи терпят поражение... Инквизиторы – не исключение.', '', 38134),
(207228, 'ruRU', '', 'Путь Гахиджи ведет в темные глубины... Что ты там найдешь?', 38134),
(207227, 'ruRU', '', 'Королевства и племена здесь ничего не значат. Откажись от старых обязательств и освободись от них!', 38134),
(207226, 'ruRU', '', 'Сила, которая была у тебя при жизни, здесь ничего не значит. Позвонив в колокол, ты осознаешь свою истинную слабость.', 38134),
(207225, 'ruRU', '', 'Прими свой стыд. Пройди через все ужасные воспоминания, которые над тобой довлеют. Выдержи их и преодолей себя!', 38134),
(207224, 'ruRU', '', 'Хочешь не хочешь, но здесь придется ответить за все. Но ты можешь чему-то научиться на этом горьком опыте.', 38134),
(207223, 'ruRU', '', 'Что?!', 38134),
(207222, 'ruRU', '', 'Что?', 38134),
(207221, 'ruRU', 'Да, тебе, наверное, странно видеть кого-то вроде меня среди тиранов и королей, которыми переполнены клетки Ревендрета.\n\nТихая душа, которую легко не заметить.\n\nМои дела даже не стоят упоминания.\n\nНо вот мои слова – совсем другое дело.\n\nМой шепот склонял других совершать ужасные преступления мне на пользу, им на беду.\n\nИ, кажется, вентиры не щадят таких махинаторов.', '', 38134),
(207220, 'ruRU', 'О чем же легендарный пилигрим Утробы хочет попросить такую скромную душу, как я?', '', 38134),
(207219, 'ruRU', '<Вериму смеется.>\n\nПроще сказать, чего я не сделал!\n\nЯ до сих пор не убежден, что все, что эти вентиры называют грехом, было так уж неправильно, но даже того, с чем я согласен, достаточно, чтобы исписать мой камень грехов вдоль и поперек.\n\n<Вериму замолкает, о чем-то задумавшись.>\n\nЯ... благодарен смотрительнице Панче за терпение. Я видел, как другие вентиры обращаются с доверенными им душами.\n\nЯ правда стараюсь стать лучше.', '', 38134),
(207218, 'ruRU', 'Ты скитаешься по миру мертвых в одиночку, смертная душа? Будь осторожнее...\n\nКогда-то я думал, что друзья мне ни к чему. Я думал, что дружба – это слабость, о которой могут узнать и использовать против меня.\n\nВ этом мире я узнал, как я ошибался.', '', 38134),
(207213, 'ruRU', '', 'Гахиджи крала вещи. Потерянные вещи. Забытые вещи.\n\nНикто даже не замечал, пока...\n\nГахиджи не зашла слишком далеко. Не забыла правила, которые нельзя забывать...\n\nГахиджи совершала ошибки...', 38134),
(207212, 'ruRU', '', 'Гахиджи хочет быть свободной, как смертная душа...\n\nУбежать отсюда... Убежать от прошлого...', 38134),
(207210, 'ruRU', 'Иногда я жалею о том, что сделал этого камнебеса.', '', 38134),
(207209, 'ruRU', 'И я тебе уже говорил: нет такого титула как \"Глашатай камней грехов\".', '', 38134),
(207208, 'ruRU', 'Это не состязание, Лемет...', '', 38134),
(207207, 'ruRU', 'Посмотрим... Глашатай камней грехов снова побеждает!', '', 38134),
(207206, 'ruRU', 'Посмотрим, как старина Темель с этим справится. Вот почему я Глашатай камней грехов!', '', 38134),
(207205, 'ruRU', 'На этой неделе я нашел для тебя много-много фрагментов камней грехов!\n\nОткрой сундук, и ты увидишь.', '', 38134),
(207203, 'ruRU', '', 'Ударить в ритуальный колокол – значит принести жертву.\n\nКаждый колокол берет что-то от души, которая его активировала. Если их использовать неправильно – так, как это делают порочные вентиры, причиняемые муки оказываются напрасными.\n\nМудрость Нераскаявшихся заключается в том, чтобы узнать, какая именно жертва нужна каждой душе на пути к искуплению, а потом убедить душу принести ее ради ее же собственного блага.\n\nНе всегда это легко, многие души слишком упрямы или слепы и не хотят звонить в ритуальный колокол.\n\nНо ты наверняка увидишь правду: жертва, принесенная ради прогресса, на самом деле не является жертвой.', 38134),
(207202, 'ruRU', '', 'Сейчас у нас нет душ, готовых к ритуалу, поэтому колокол пока не нужен.\n\nФейн и остальные Нераскаявшиеся архивариусы спрячут их, чтобы до них не добрались порочные ритуалисты.\n\nКогда у нас появится готовая душа, я прикажу землероям принести нужный колокол. Думаю, ждать придется недолго.', 38134),
(207201, 'ruRU', '', '<Смотрительница Панча ухмыляется.>\n\nЯ так и думала, что этот колокол тебя особенно заинтересует, пилигрим Утробы. Подозреваю, он тебе... пригодится, если когда-нибудь ты вернешься в Ревендрет и попадешь под мою опеку.\n\nКолокол Отречения помогает тем, кому трудно освободиться от уз верности, связывавших их при жизни. Ваши королевства, Альянс, Орда... здесь их нет, и верность им больше не может быть смыслом твоего существования.\n\nУдарь в колокол и освободись от нее.', 38134),
(207200, 'ruRU', '', 'О, это мой любимый колокол Подчинения.\n\nЕму надо лишь отдать часть своей силы.\n\nОчень просто, да. Но это ужасная перспектива для гордецов, которые пришли к успеху через насилие.\n\nОтдав свою силу, мы позволяем душе сражаться – то есть делать то, что она всегда хотела.\n\nНо в своем ослабленном состоянии она терпит поражение в бою и, сломленная, вынуждена сдаться тем, кого считает ниже себя.\n\nТолько после этого ритуал заканчивается.', 38134),
(207199, 'ruRU', '', 'Сопереживаешь ли ты другим смертным, пилигрим Утробы?\n\nЭто дано не всем. Колокол Позора помогает неспособным на эмпатию осознать последствия былых грехов, проявляя их и заставляя бросить им вызов.\n\nКолокол обнажает самые постыдные деяния и выставляет их на всеобщее обозрение. После этого мы на время освобождаем мстительные души из склепов и спускаем на испытуемого.\n\nЭто нелегкое зрелище для тех, кто способен на эмпатию, но наши души не нуждаются в сочувствии.', 38134),
(207198, 'ruRU', '', 'Колокол Обличения – одно из самых мощных карающих орудий в наших руках. Мы применяем его к самым упрямым душам, которым нужно постоянное напоминание о том, что здесь они бессильны.\n\nУдарив в этот колокол, ты тоже почувствуешь его эффект – но обычно это не главная цель. Для работы над собой у нас есть инструменты и получше.\n\nОднако твои изъяны станут заметны всем вокруг, и вентиры получат на тебя больше влияния.\n\nЭто опасно, потому что в чертогах теперь много порочных вентиров. Мне приходится тщательно следить за вверенными мне душами, когда они под воздействием колокола.', 38134),
(207197, 'ruRU', '', 'Колокол Исповеди – простой, но мощный инструмент, помогающий душам, осознавшим свою вину.\n\nВ него может ударить только душа, готовая признать свой грех. После этого ее признание разносится по всем чертогам.\n\nПубличное покаяние помогает душе осознать свои ошибки, но привлекает внимание порочных вентиров в этих чертогах.', 38134),
(207196, 'ruRU', '', 'Большая часть сейчас в склепах Покаяния, они готовятся к следующему ритуалу.\n\nНе беспокойся, последователи Лорда-камергера до них не доберутся. Этим душам ничто не угрожает, пока они осмысляют чувство вины за былые прегрешения.\n\nОбвинительница лично защищает склепы от посягательств.', 38134),
(207195, 'ruRU', '', 'Очень медленно, если прошлое позволяет судить о будущем.\n\nИз-за страсти к насилию и явной ненависти к общественным хранилищам знаний Ик накопила множество грехов, которые ей надо искупить. А достижений, которые помогут ей это сделать, у нее очень мало.\n\nПока я держу ее в одиночном заключении и даю много простых задач, чтобы научить ее ценности того, что она так легко отвергала при жизни.\n\nСегодня она будет помогать архивариусам собирать потерянные фрагменты камней грехов.', 38134),
(207194, 'ruRU', '', 'Спроси его самого.\n\nКонгордзоло у меня уже давно, но наконец-то научился сдерживать свой язык. Теперь тому, что он говорит, можно верить.\n\nЛишь после того, как я прервала его поток лжи и полуправды, мы с ним подошли к принятию последствий его действий в мире смертных.\n\nТеперь он должен восстановить свою репутацию словом и делом. Как и все мы, не так ли?', 38134),
(207193, 'ruRU', '', 'Вериму до сих пор не понимает, как шатко его положение!\n\nОн искупил уже много грехов, но бремя, с которым он сюда прибыл, было чудовищным. Никогда прежде я такого не видела.\n\nЖадные щупальца Утробы будут грозить ему, пока он не искупит все свои грехи до последнего.\n\nСейчас я стараюсь поддерживать в нем правильный настрой, поручив ему знакомое дело: борьбу с враждебной организацией.', 38134),
(207192, 'ruRU', '', 'С Гахиджи мне пришлось повозиться.\n\nОна быстро признает свои ошибки, но почти не испытывает угрызений совести. Это удивительно.\n\nЯ боюсь, что она с нами надолго.\n\nНо она готова нам помогать. Я часто поручаю ей очищать склепы от грязи порочных вентиров.', 38134),
(207191, 'ruRU', '', 'Йевкек прошел по пути к искуплению дальше, чем многие души под моей опекой.\n\nВсю жизнь его сила и ум были направлены на то, чтобы лишать свободы слабых и бесправных. Теперь он учится защищать и спасать других.\n\nКогда одна из моих душ теряется или хочет скрыться от правосудия, я поручаю Йевкеку найти ее и наставить на правильный путь.', 38134),
(207190, 'ruRU', '', 'Даже землерои становятся жертвами порочных вентиров в этих чертогах.\n\nРаботая здесь и выполняя поручения сбившихся с пути служителей Лорда-камергера, землерои пренебрегают своими истинными обязанностями.\n\nЭтот ритуальный колокол неисправным лежит на своем пьедестале, в то время как он отчаянно нужен нам для очищения душ!', 38134),
(207189, 'ruRU', '', 'Колокол восстановлен, и мы можем повести эту душу дальше к искуплению.\n\nУдарь в колокол и помоги мне показать, что жертва – самый верный путь к искуплению вины.\n\nЕсли мы сами не готовы проявить смирение и признать свои ошибки, как мы можем требовать того же от остальных?', 38134),
(207175, 'ruRU', '', 'Путь каждой души к искуплению уникален, но у них есть нечто общее.\n\nДля достойного дела нужны жертвы и страдания.\n\nКогда альтернатива – это вечность в Утробе, может цена быть слишком высокой?', 38134),
(207168, 'ruRU', 'Мой путь – искать слабых и спасать тех, кого когда-то использовал. Следуй пути милосердия, смертная душа.', '', 38134),
(207167, 'ruRU', '', 'Когда землерои принесут колокол Исповеди, тебе придется в него ударить.', 38134),
(207163, 'ruRU', 'Я много кем был...\n\nВолком, идущим за стадом пастуха.\nАкулой, кружащей вокруг плота.\nЯстребом, охотящимся на зайца.\n\nСлабые не могли спастись от меня.', '', 38134),
(207161, 'ruRU', 'У тебя еще есть время, смертная душа.\n\nТы можешь измениться. Избежать моей участи.\n\nВентиры ведут нас к искуплению, но этот путь...\n\n<Душа содрогается и замолкает.>', '', 38134),
(207154, 'ruRU', 'Зачем ты здесь? У меня и так ничего не осталось!\n\nЭльфы захватили наш дом. Они забрали мой глаз... и мою жизнь.\n\nУ нас вечно все отбирают. Но мы не сдаемся! И не забываем.\n\nПридет день, и настанет твой черед... все потерять!', '', 38134),
(207152, 'ruRU', '', 'Остальные не выжили. Только я.\n\nУтроба, как ты знаешь, опасное место. Большая группа дает ложное чувство безопасности, но на самом деле только делает тебя более лакомой целью.\n\nМы узнали это на собственном горьком опыте. Нас становилось все меньше, пока не осталось так мало, что выжившие смогли спрятаться. Это замедлило охоту... Но не остановило ее.\n\nКогда я осталась одна, то приняла правду о своем положении. Одиночество оказалось лучшим способом выжить.\n\nПо крайней мере, так было до твоего прибытия.', 38134),
(207149, 'ruRU', '', 'Мне их жалко, но не заблуждайся на их счет. Они наши враги, как и Верные Утробе.\n\nЭти брокеры были обречены сразу по прибытии сюда. А если ты их спасешь, то только погубишь нас обоих.\n\nДа, я знаю, это звучит жестоко. Но по-другому в Утробе выжить нельзя.\n\nСоветую тебе это запомнить.', 38134),
(207143, 'ruRU', '', 'Хранительница Казада ждет тебя в зимнем саду. Ей не терпится начать твое обучение!\n\nЧтобы попасть в зимний сад, просто перейди в кольцо грибов рядом со мной.', 38134),
(207142, 'ruRU', '', 'В этой бесконечной тьме у тебя нет ничего, кроме времени. Истории здесь ценятся дороже золота.', 38134),
(207119, 'ruRU', '', 'О, я не сомневаюсь, что вы хорошо знакомы. Без ее помощи смертному существу вроде тебя не удалось бы выжить в Утробе. \n\nРасскажи мне о своих связях с Ве\'нари.\n\nЕсли она решила не возвращаться в Орибос вместе с тобой, ей тоже придется кое-что объяснить.', 38134),
(207118, 'ruRU', '', 'Конечно, конечно... И последний вопрос.\n\nБыла ли Ве\'нари для тебя ценным союзником в Утробе? \n\n<Перед тем как ответить, вы вспоминаете, что Ве\'нари настоятельно просила вас никому не сообщать о вашей сделке.>', 38134),
(207113, 'ruRU', '', 'Значит, вести здесь бизнес по-прежнему опасно. В связи с этим я хочу узнать кое-что еще.\n\nВопрос второй! \n\nТебе трудно выживать среди гнетущих и всепоглощающих ужасов Утробы?', 38134),
(207112, 'ruRU', '', 'Я ценю твою готовность поделиться знаниями об Утробе.\n\nВопрос первый: \n\nЭто правда, что войска тюремщика стали сильнее и больше числом?', 38134),
(207111, 'ruRU', '', 'Приветствую, пилигрим. Я рада наконец-то с тобой повстречаться.', 38134),
(207110, 'ruRU', '', 'Как интересно!', 38134),
(207109, 'ruRU', '', 'Я не буду проверять их или настраивать. Мы не узнаем, что появится из зеркал, пока их не активируем.', 38134),
(207108, 'ruRU', '', 'Я изготовлю для тебя новые наборы для ремонта, если принесешь достаточно насыщенных рубинов.', 38134),
(207107, 'ruRU', 'По всему Ревендрету ты найдешь еще множество зеркал, требующих починки.', '', 38134),
(207105, 'ruRU', '', 'Можешь использовать набор для ремонта зеркал, который я принесла с собой из Пепельного Предела.', 38134),
(207104, 'ruRU', 'В другом конце зала стоит разбитое зеркало. Можешь на нем потренироваться.', '', 38134),
(207103, 'ruRU', '', 'О, существует еще очень многое, о чем я тебе не рассказываю. Наверняка и ты тоже что-то держишь при себе.\n\nДоверие строится на осторожности, пилигрим Утробы. Иначе бизнес вести нельзя.\n\nНо при всем при том я доверила тебе мой самый ценный секрет: местонахождение моего убежища. И взамен прошу только об одном: доверяй мне.', 38134),
(207101, 'ruRU', '', 'С твоей помощью Арденвельд сможет и дальше поддерживать цикл перерождения.\n\nЯ доверяю тебе мой зимний сад.\n\nПоговори с хранительницей Казадой. Она научит тебя всему остальному.', 38134),
(207100, 'ruRU', '', 'Всему приходит конец.\n\nНо повстречав его, мы находим новое начало. Таков круговорот природы. \n\nДухи, спящие в моих рощах, незаменимы для этого цикла. Без них в мире смертных начнет умирать сама природа. Каждая потерянная душа – ужасная трагедия. Каждая спасенная – величайшая победа.\n\nЭтого духа тебе удалось спасти из умирающей рощи, окутанной безумием друстов. Сегодня она пробудится от долгого сна и вернется в свой мир. Благодаря тебе мы остались верны нашему священному долгу.', 38134),
(207097, 'ruRU', '', 'Королева Зимы решила лично поведать тебе о диких семенах? Это большая честь.', 38134),
(207087, 'ruRU', '', 'На это ушло много стигии... Но что поделать. Издержки бизнеса.', 38134),
(207065, 'ruRU', 'Мое слово против твоего? Конечно. Ты нарушила контракт и предала картель! И теперь ты за это заплатишь.', '', 38134),
(207063, 'ruRU', '', 'Вопли свинобразов, конечно же, приятнее, но и эти сойдут.', 38134),
(207012, 'ruRU', '$n? Рад снова тебя видеть, друг мой. Особенно в этом странном месте.\n\nУзнав, что путь в Темные Земли открыт, я сразу отправился сюда. Риск меня не страшил.\n\nЕсли ты помнишь, моя жена... умерла при родах, а вскоре я потерял одного из наших сыновей. Я должен убедиться, что наши предки защищают их. Я всем сердцем надеюсь, что их души в безопасности.\n\nУмоляю тебя, если повстречаешь душу Лезы, передай ей, что наш сын Кор растет здоровым и сильным. Скажи ей, что я ее люблю. Что я буду всегда любить ее.\n\nИ что найду ее, чего бы мне это ни стоило.', '', 38134),
(207011, 'ruRU', 'Как приятно увидеть кого-то с Азерота в этом странном месте.\n\nУзнав, что путь в Темные Земли открыт, я сразу отправился сюда. Опасность меня не волновала.\n\nВидишь ли, я... потерял тех, кто был мне дороже всего. Я должен убедиться, что наши предки защищают их, или узнать, не грозит ли им опасность.\n\nУмоляю тебя, если повстречаешь душу моей жены, Лезы Дальноход, передай ей, что наш сын Кор растет здоровым и сильным. Скажи ей, что я ее люблю. Что я буду всегда любить ее.\n\nИ что найду ее, чего бы мне это ни стоило.', '', 38134),
(207009, 'ruRU', '', 'Я использую одно из самых интересных моих устройств. Боюсь, тебя это будет очень больно.', 38134),
(207008, 'ruRU', '', 'Идем, пилигрим. Нам надо многое обсудить.', 38134),
(207007, 'ruRU', '', 'Нет, Ве\'бракс. Утробу ты покинешь... Но в картель не вернешься.', 38134),
(207006, 'ruRU', 'А-а! Что... ты... делаешь?!', '', 38134),
(207005, 'ruRU', '', 'Я так не думаю. Разберись с этой помехой, пилигрим.', 38134),
(207003, 'ruRU', '', 'Думаешь, они поверят на слово убийце?', 38134),
(207002, 'ruRU', 'Я тебя предупреждал, пилигрим. Надо было слушать.', '', 38134),
(207000, 'ruRU', 'Она предала своих прошлых партнеров, и тебя тоже предаст, если узнает, что мы говорили. Не верь ее лжи.', '', 38134),
(206999, 'ruRU', 'Я пойду в убежище. Но хочу предупредить насчет Ве\'нари. Она не та, за кого себя выдает.', '', 38134),
(206995, 'ruRU', 'Мы пришли, чтобы компенсировать убытки, которые понесли из-за предательства Ве\'нари. О, как это было глупо...', '', 38134),
(206988, 'ruRU', 'Нас схватили... пытали... ломали.\n\nОстальных... забрал Ксерос... в Загоны\n\nМы... потерпели неудачу.', '', 38134),
(206984, 'ruRU', 'Я же сказал ему, что вернусь! И я не намерен его разочаровывать.', '', 38134),
(206983, 'ruRU', 'Один гнусный чародей заковал меня в цепи!\n\nОсвободи меня, и я устрою ему веселую жизнь! Или, скорее, смерть.', '', 38134),
(206981, 'ruRU', 'Теотару потребовались все его связи на Ночном рынке, чтобы это раздобыть...', '', 38134),
(206970, 'ruRU', 'Похоже, на этом нетопыре вы сможете выбраться из Чертогов Покаяния.', 'Похоже, на этом нетопыре вы сможете выбраться из Чертогов Покаяния.', 38134),
(206969, 'ruRU', 'Выкованная духами эгида. И защита, и атака!\n\nПриближаются враги? Готовятся бить?\n\nЭгида тебя защитит! Поглотит урон и отразит немного во врагов! У-ух! У-ух!', '', 38134),
(206968, 'ruRU', 'Ограждающее кольцо защитит!\n\nХочешь стоять на месте, и чтобы враги не подходили?\n\nЛегко! Включи кольцо, и враги не подойдут! Никто не нападет. Можно передохнуть.', '', 38134),
(206967, 'ruRU', 'Флакон безмятежности прекрасно освежает!\n\nЗаканчиваются силы? Надо взбодриться?\n\nФлакон вылечит, и ты продолжишь сражаться! Гарантия распорядителя!', '', 38134),
(206966, 'ruRU', 'Флакон холода личей поможет задержать врагов!\n\nОкружили со всех сторон? Нужно поскорее убежать?\n\nНе беда! Разбей флакон о землю, он заморозит врагов. И можно убегать!', '', 38134),
(206965, 'ruRU', 'Прости, $n! Снаряжение не для тебя!\n\nТолько для твоих медиумов. Пригодится им в испытаниях!', '', 38134),
(206959, 'ruRU', 'У-ух! Могу выполнить одно большое задание, но только одно! Смогу помочь снова через несколько часов. Так много дел!', 'У-ух! Могу выполнить одно большое задание, но только одно! Смогу помочь снова через несколько часов. Так много дел!', 38134),
(206958, 'ruRU', 'Чем тебе помочь? Это должно быть что-то одно. Выбирай с умом! Помочь снова смогу только через час. Много дел!', 'Чем тебе помочь? Это должно быть что-то одно. Выбирай с умом! Помочь снова смогу только через час. Много дел!', 38134),
(206954, 'ruRU', 'Что-то припоминаю... Я видел, как мимо тащили какого-то большого зеленого парня.\n\nТакое трудно не заметить. Все кричал про какой-то \"лук да угар\".\n\nОсвободи-ка меня, и я все тебе расскажу.', '', 38134),
(206949, 'ruRU', 'Это еще что? Живая смертная душа? Здесь? Да я с ума схожу!\n\nТебя просто не может быть.', '', 38134),
(206948, 'ruRU', 'Вот он... \"Альманах побед\"! Предоставляю эту честь тебе, $n! Вдруг это ловушка.', '', 38134),
(206944, 'ruRU', 'Очень! Я уже давно забочусь о Чесночнике. Знаю даже о старых путях, про которые он уже и сам не помнит!$b$bЧто-то мне подсказывает, тебе об этих тайных местах тоже известно. Если вдруг захочешь вернуться в одно из них, я тебе помогу.', 'Очень! Я уже давно забочусь о Чесночнике. Знаю даже о старых путях, про которые он уже и сам не помнит!$b$bЧто-то мне подсказывает, тебе об этих тайных местах тоже известно. Если вдруг захочешь вернуться в одно из них, я тебе помогу.', 38134),
(206941, 'ruRU', 'Ты... Я тебя не знаю. Как тебя сюда занесло? Ну и молодежь пошла...$b$bНе знаю как, но тебе удалось попасть в настоящую передрягу! В этот раз я доставлю тебя прямо в Арденвельд. То, что я старый и мне никто не помогает, не значит, что я останусь глух к чужим горестям.', '', 38134),
(206933, 'ruRU', '', 'Твой дух переполняет гордыня. Помочь тебе искупить твои грехи?', 38134),
(206931, 'ruRU', 'Там ей будет немного легче.', '', 38134),
(206930, 'ruRU', 'Принц Ренатал дал ей власть над башней цитадели Рассвета. Наару появились в нашем мире как раз через это место. Его почти полностью поглотил Свет.', '', 38134),
(206929, 'ruRU', 'Зе\'рали слишком слаба, чтобы вернуться домой, а само ее присутствие подвергает всех вентиров опасности.', '', 38134),
(206912, 'ruRU', '\"Естественный импульс\" – это пассивная способность, которая позволит тебе \"Мигать\" в облике души по направлению движения, а не по направлению взгляда.', '\"Естественный импульс\" – это пассивная способность, которая позволит тебе \"Мигать\" в облике души по направлению движения, а не по направлению взгляда.', 38134),
(206908, 'ruRU', 'Ты можешь найти эксперта по конфликтам и его друзей-торговцев в стороне от Анклава.', 'Ты можешь найти эксперта по конфликтам и его друзей-торговцев в стороне от Анклава.', 38134),
(206897, 'ruRU', 'Я лишь отголосок воспоминаний падшего идеала, но хорошо вижу в тебе жажду знаний.\n\nПрочти мою историю, и мы обсудим, что еще тебе требуется узнать.', 'Я лишь отголосок воспоминаний падшего идеала, но хорошо вижу в тебе жажду знаний.\n\nПрочти мою историю, и мы обсудим, что еще тебе требуется узнать.', 38134),
(206896, 'ruRU', 'А вот и Кобри! Спасибо!', 'А вот и Кобри! Спасибо!', 38134),
(206895, 'ruRU', 'Кобри ушел за вином. Кобри нет очень давно. Без вина мы не начнем!\n\nНайдешь Кобри?', 'Кобри ушел за вином. Кобри нет очень давно. Без вина мы не начнем!\n\nНайдешь Кобри?', 38134),
(206889, 'ruRU', 'Во имя Матери-Земли!', '', 38134),
(206886, 'ruRU', 'Пусть Предназначение направляет тебя.\n\nЯ веду учет всего, что здесь происходит. Мы записываем видения, что открывают нам переплетающиеся нити судьбы, когда мы служим Предназначению. \n\nЧто ты хочешь у меня узнать?', 'Пусть Предназначение направляет тебя.\n\nЯ веду учет всего, что здесь происходит. Мы записываем видения, что открывают нам переплетающиеся нити судьбы, когда мы служим Предназначению. \n\nЧто ты хочешь у меня узнать?', 38134),
(206878, 'ruRU', 'Приветствую. Чем могу помочь на твоем пути?', '', 38134),
(206833, 'ruRU', '', 'Никто не должен узнать о моем существовании. У тебя здесь свои дела, и решать их тоже тебе.\n\nЯ могу лишь предоставить хранитель душ, артефакт, позволяющий переносить души, найденные в Утробе.\n\nПриняв у меня хранитель душ и согласившись на мои условия, ты заключишь со мной контракт.\n\nСогласно его условиям ты будешь делиться со мной любой ценной информацией, найденной в Утробе.\n\nВзамен я буду продавать тебе инструменты, которые помогут здесь выжить и спасти нужные души.\n\nТак что, мы договорились?', 38134),
(206827, 'ruRU', 'Любишь музыку? Хочешь сыграть?', 'Любишь музыку? Хочешь сыграть?', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206726, 'ruRU', '<Выбрать что-то одно.>', '', 38134),
(206725, 'ruRU', '<Использовать или убрать.>', '', 38134),
(206719, 'ruRU', 'Чтобы искупить вину за непристойное поведение, приходится платить. Но чтобы по-настоящему исправить подобных грешников, об их порочном поведении должны узнать другие. Так будет проще призвать их к ответу.', '', 38134),
(206713, 'ruRU', 'Кошмарная лошадь Савраска кричит от боли и исчезает.', '', 38134),
(206708, 'ruRU', 'В отсутствие короля Андуина защищать народы Альянса доверили мне.\n\nМне кажется, будто прошло много веков с тех пор, как я помог молодому королю Вариану Ринну завоевать трон. Теперь я должен сохранить этот трон ради его сына.\n\nЧетвертая война обратилась для Альянса ужасными потерями. Наши войска истощены. Штормградской знати не терпится восстановить нашу оборону.\n\nМы неплохо начали, вернув Стромгард. Быть может, нам удастся отвоевать и другие территории старого Альянса.', '', 38134),
(206707, 'ruRU', 'На долю Орды выпало много испытаний. Много опасностей.\n\nДля меня честь стать частью совета, который приведет нас к лучшему будущему.\n\nВол\'джин был лучшим вождем из всех, что знала Орда... и хорошим другом. Я надеюсь не посрамить его память. Орда не должна забыть о его жертве.', '', 38134),
(206706, 'ruRU', '', 'Мы потратили месяцы, прочесывая каждый уголок Азерота в поисках Сильваны, и тут оказывается, что моя сестра сбежала в миры мертвых.\n\n<Глаза Аллерии сужаются.>\n\nТам ей самое место.\n\nИ хотя я мечтаю отправиться в погоню, сейчас мое место здесь. Альянсу нужно восстановить силы, и мы с Туралионом об этом позаботимся.\n\nНо когда придет время, я заставлю Сильвану ответить за свои злодеяния.', 38134),
(206705, 'ruRU', 'Путь открыт. Теперь ты можешь отправиться в Малдраксус.', '', 38134),
(206704, 'ruRU', 'Пути открываются вновь. Быть может, за этим Предназначение и направило тебя к нам?', '', 38134),
(206703, 'ruRU', 'Путь открыт. Теперь ты можешь отправиться в Арденвельд.', '', 38134),
(206701, 'ruRU', 'Путь открыт. Теперь ты можешь отправиться в Ревендрет.', '', 38134),
(206692, 'ruRU', 'Здравствуй, Ка-Шер. Это правда? Нам велено вновь открыть врата?', '', 38134),
(206686, 'ruRU', '', 'Эти нестабильные чары наносят огромный урон, но могут воздействовать только на одну цель единовременно.', 38134),
(206685, 'ruRU', 'Эти нестабильные чары наносят огромный урон, но могут воздействовать только на одну цель единовременно.', '', 38134),
(206684, 'ruRU', '\"Нестабильное колдовство\" создает осколок души, когда пораженная цель погибает. Применяйте его к цели, которая погибнет первой.', '', 38134),
(206683, 'ruRU', '', '\"Нестабильное колдовство\" создает осколок души, когда пораженная цель погибает. Применяйте его к цели, которая погибнет первой.', 38134),
(206682, 'ruRU', '', '\"Нестабильное колдовство\" – мощное заклинание, наносящее периодический урон.', 38134),
(206681, 'ruRU', '\"Нестабильное колдовство\" – мощное заклинание, наносящее периодический урон.', '', 38134),
(206679, 'ruRU', '<Это устройство может открывать в башне потайные ходы с помощью настроенных осколков.>', '', 38134),
(206677, 'ruRU', 'Будет исполнено, пилигрим.', '', 38134),
(206668, 'ruRU', '', 'Вечный город сияет красотой. И все же я повсюду чувствую прикосновение Смерти.\n\nЭта сила и на мне оставила отпечаток. Быть может, познав тайны Орибоса, я пойму, что все это значит.\n\nМестные служители с такой уверенностью говорят о своем \"Предназначении\". Быть может, они помогут мне найти и мое.', 38134),
(206667, 'ruRU', '', 'Темные Земли. Они невероятны... и, если честно, немного пугают.\n\nНо страх не помешает мне помочь друзьям.\n\nИ поближе узнать отца, которого я когда-то потеряла.', 38134),
(206666, 'ruRU', 'Мы знаем, что Тюремщик следит за нами, и что Андуин у него в плену.\n\nТы и другие герои должны помочь ковенантам стать сильнее. Это наша единственная надежда, $n.\n\nСкоро Сильвана и Тюремщик перейдут к новой стадии своего плана.\n\nКогда это случится, нам понадобится огромная сила, чтобы дать им отпор.', '', 38134),
(206629, 'ruRU', 'И правда. Должно быть, все бескрылые так делают.', 'И правда. Должно быть, все бескрылые так делают.', 38134),
(206628, 'ruRU', 'Ты так часто прыгаешь. И без помощи крыльев! Это вредно для суставов!', 'Ты так часто прыгаешь. И без помощи крыльев! Это вредно для суставов!', 38134),
(206627, 'ruRU', 'Наверняка. Но мне-то что с того?', 'Наверняка. Но мне-то что с того?', 38134),
(206626, 'ruRU', 'Что-то ты очень часто прыгаешь.', 'Что-то ты очень часто прыгаешь.', 38134),
(206625, 'ruRU', 'Вот поэтому такое дело лучше отложить на завтра.', 'Вот поэтому такое дело лучше отложить на завтра.', 38134),
(206624, 'ruRU', 'Поможешь? Давай украдем что-нибудь хорошее!', 'Поможешь? Давай украдем что-нибудь хорошее!', 38134),
(206623, 'ruRU', 'Этот... этот камень! Не жди от него ничего хорошего!', 'Этот... этот камень! Не жди от него ничего хорошего!', 38134),
(206622, 'ruRU', 'Не поддавайся его сладким речам!', 'Не поддавайся его сладким речам!', 38134),
(206621, 'ruRU', 'Никогда не поздно ступить на путь праведности!', 'Никогда не поздно ступить на путь праведности!', 38134),
(206620, 'ruRU', 'Кому ты поможешь сегодня, смертная душа?', 'Кому ты поможешь сегодня, смертная душа?', 38134),
(206619, 'ruRU', 'Не слушай его! Если ваша группа будет работать сообща, вас ничто не остановит!', 'Не слушай его! Если ваша группа будет работать сообща, вас ничто не остановит!', 38134),
(206618, 'ruRU', 'Твои союзники многого добьются, находясь под твоей защитой.', 'Твои союзники многого добьются, находясь под твоей защитой.', 38134),
(206617, 'ruRU', 'Пусть твое оружие преследует лишь тех, кого нужно призвать к ответу!', 'Пусть твое оружие преследует лишь тех, кого нужно призвать к ответу!', 38134),
(206616, 'ruRU', 'Славный бой. Пусть твой противник покоится с миром.', 'Славный бой. Пусть твой противник покоится с миром.', 38134),
(206615, 'ruRU', 'Должно быть, возникло недопонимание...', 'Должно быть, возникло недопонимание...', 38134),
(206614, 'ruRU', 'Агрессия – это не выход. Попробуй просто поговорить!', 'Агрессия – это не выход. Попробуй просто поговорить!', 38134),
(206613, 'ruRU', 'Какая разница – грехом больше, грехом меньше? Давай!', 'Какая разница – грехом больше, грехом меньше? Давай!', 38134),
(206612, 'ruRU', 'Что бы нам такое учинить?', 'Что бы нам такое учинить?', 38134),
(206611, 'ruRU', 'Представь, сколько заклинаний можно было бросить во врагов вместо этого!', 'Представь, сколько заклинаний можно было бросить во врагов вместо этого!', 38134),
(206610, 'ruRU', 'Увы, добро не всегда возвращается.', 'Увы, добро не всегда возвращается.', 38134),
(206609, 'ruRU', 'Ха, превосходно! Бац – и в лепешку!', 'Ха, превосходно! Бац – и в лепешку!', 38134),
(206608, 'ruRU', 'Да! Убей! А теперь вон того!', 'Да! Убей! А теперь вон того!', 38134),
(206607, 'ruRU', 'Да, поговори! Своим оружием!', 'Да, поговори! Своим оружием!', 38134),
(206606, 'ruRU', 'Такое нельзя прощать. Вперед!', 'Такое нельзя прощать. Вперед!', 38134),
(206605, 'ruRU', 'Адонас морщится от боли, держась за поврежденный бок.', '', 38134),
(206604, 'ruRU', 'Прошу, пилигрим... Мне нужно... лечение...', '', 38134),
(206603, 'ruRU', 'Болеть будет еще долго, но летать я смогу. Спасибо.', '', 38134),
(206590, 'ruRU', 'Свитки, свитки, свитки...\n\nУх-ух! Привет!', 'Свитки, свитки, свитки...\n\nУх-ух! Привет!', 38134),
(206588, 'ruRU', 'На полках не хватает свитков! Ты найдешь свиток? Да?', 'На полках не хватает свитков! Ты найдешь свиток? Да?', 38134),
(206579, 'ruRU', 'Времени мало, и мы должны нанести Денатрию удар до того, как он узнает, что я здесь.\n\nНо сейчас это будет безумием. Мне нужно восстановиться после пребывания в Утробе, а тебе пока не хватит сил на то, чтобы помочь с моим замыслом.\n\nИди, пилигрим Утробы, набирайся сил. Узнай больше о Темных Землях. Освойся в этом новом для тебя мире. Возвращайся ко мне, когда сможешь сразиться с одним из повелителей смерти.', '', 38134),
(206576, 'ruRU', 'Ты найдешь порталы на площадке рядом с городом.', 'Ты найдешь порталы на площадке рядом с городом.', 38134),
(206575, 'ruRU', 'Ты найдешь всех брокеров, занимающихся ремеслом, в Зале Контуров.', 'Ты найдешь всех брокеров, занимающихся ремеслом, в Зале Контуров.', 38134),
(206571, 'ruRU', 'Лес щедро кормит нас.', 'Лес щедро кормит нас.', 38134),
(206564, 'ruRU', 'Время и судьба тесно связаны. Они могут изменяться и искажаться по воле Предназначения.\n\nС твоей душой связана великая сила. Благодаря ей я могу изменить твою судьбу... и предоставить тебе новый путь.\n\nЕсли ты решишь на него ступить, обитатели Темных Земель станут обращаться с тобой как со смертными, что уже успели познакомиться с их царствами.\n\nКаждый ковенант будет рад принять тебя в свои ряды. Выбрав один из них, ты обязуешься помочь ему достичь величия.\n\nНо помни: избрав этот путь, ты уже не сможешь вернуть все обратно.\n\nРешать тебе.', '', 38134),
(206562, 'ruRU', 'Конечно, я согласился стать ее медиумом. Для меня она дороже всех на свете, и я готов провести с ней всю оставшуюся вечность.', 'Конечно, я согласилась стать ее медиумом. Для меня она дороже всех на свете, и я готова провести с ней всю оставшуюся вечность.', 38134),
(206561, 'ruRU', 'Еще раз спасибо тебе за помощь.\n\n<Претендент мягко улыбается, кивая вправо.>\n\nОна сказала да.', 'Еще раз спасибо тебе за помощь.\n\n<Претендентка мягко улыбается, кивая вправо.>\n\nОна сказала да.', 38134),
(206560, 'ruRU', 'Я дам ей ответ лично. Благодарю тебя. Уверен, ты понимаешь, насколько это для нас важно.', 'Я дам ей ответ лично. Благодарю тебя. Уверена, ты понимаешь, насколько это для нас важно.', 38134),
(206559, 'ruRU', 'Она хочет, чтобы я стал ее медиумом, $n.', 'Она хочет, чтобы я стала ее медиумом, $n.', 38134),
(206558, 'ruRU', 'Венок, полученный в подарок от того, кто тебе дорог, очень важный знак.', 'Венок, полученный в подарок от того, кто тебе дорог, очень важный знак.', 38134),
(206551, 'ruRU', 'Перед тобой два пути, $n.\n\nТы можешь начать свое путешествие с чистого листа, как и другие смертные до тебя. Никто не будет знать о тебе и о твоем Предназначении.\n\nИли же ты можешь позволить нитям судьбы и времени увести тебя вперед. О тебе будут знать во всех ковенантах, и ты сразу сможешь выбрать себе союзников.\n\nМиры смерти будут для тебя открыты, и ничто не помешает тебе свободно распоряжаться своей судьбой.\n\nВыбор за тобой.', 'У тебя два варианта, $n. \n\nМожно оставить все как есть. Тогда все будут удивляться тебе, как в первый раз, и ты заново повстречаешь своих друзей.\n\nИли же я перемещу тебя вперед во времени.\n\nТы сможешь сразу присоединиться к ковенанту и начать свои приключения в любой точке Темных Земель!\n\nТак что же ты выберешь?', 38134),
(206550, 'ruRU', 'Судьба зовет тебя. Пора послужить Предназначению.', '', 38134),
(206539, 'ruRU', 'Брокеры Орибоса позволили нашим союзникам в Черном оплоте открыть портал между нашими мирами. Ты найдешь его в зале порталов на вершине башни магов.', 'Брокеры Орибоса позволили нашим союзникам в Черном оплоте открыть портал между нашими мирами. Ты найдешь его в зале порталов на вершине башни магов.', 38134),
(206534, 'ruRU', '', 'Не знаешь, что делать – убивай.', 38134),
(206532, 'ruRU', '', 'Ты хоть представляешь, сколько соперников я убила?', 38134),
(206530, 'ruRU', 'Это было непросто, но благодаря брокерам Орибоса наши союзники в Черном оплоте смогли открыть врата между нашими мирами. Ты найдешь их на главном этаже нашего зала порталов.', 'Это было непросто, но благодаря брокерам Орибоса наши союзники в Черном оплоте смогли открыть врата между нашими мирами. Ты найдешь их на главном этаже нашего зала порталов.', 38134),
(206528, 'ruRU', '', 'От этого места у меня мурашки по коже. Я вся дрожу и совсем не могу расслабиться. Вздрагиваю от любого незнакомого звука.\n\nНо я должна быть стойкой. Я перерожденная из Бастиона. Мой долг – бороться с теми, кто представляет угрозу не только нашим порядкам, но и существованию Темных Земель.', 38134),
(206527, 'ruRU', 'Обычно претендентам не поручают такие важные задания.\n\nПризнаюсь, я не ожидал так рано оказаться за пределами Бастиона, и от этого мне немного не по себе. Но со мной вы с Клейей. Уверен, я многому научусь в этом путешествии.', '', 38134),
(206526, 'ruRU', '', 'Я обещаю достойно представлять Бастион и наши порядки.\n\nМежду вентирами и кириями немало различий, но я верю, что мы найдем общий язык.', 38134),
(206525, 'ruRU', '', 'Графиня не выпускает из виду аристократов Ревендрета, даже тех, кто утратил свое влияние.\n\nДля нее информация – величайшая сила. И так просто она ею не поделится.', 38134),
(206524, 'ruRU', '', 'Привет, $n!\n\nПутешествия во времени бывают опасными. Ты сможешь пуститься навстречу приключениям, когда наберешься опыта!', 38134),
(206523, 'ruRU', '', 'Мы служим не только Архонту, но и обитателям бесчисленных миров, существующих во вселенной.', 38134),
(206522, 'ruRU', '', 'Привет, $n!\n\nИнтересуешься путешествиями во времени? Боюсь, тебе стоит получше узнать настоящее, прежде чем заглядывать в прошлое.\n\nДавай я отправлю тебя туда, как только ты пройдешься по городу и познакомишься с вождем!', 38134),
(206521, 'ruRU', '', '<Глаза Клейи закрыты, а на ее лице явно читается беспокойство.\n\nОна дышит медленно и глубоко, пытаясь смириться с тем, что только что узнала.>', 38134),
(206520, 'ruRU', '', 'Азерот очень красив. Он совсем не похож на величественный Бастион. Мне не терпится узнать, как живут обитатели этого мира.', 38134),
(206519, 'ruRU', '', 'Я готовилась тысячи лет и наконец дождалась этого дня.\n\nОсталась только одна задача. Последнее испытание.\n\nИ тогда я наконец-то смогу переродиться.', 38134),
(206512, 'ruRU', '<Архивариус Джанира продолжает читать свиток перед собой, не глядя на вас.>\n\nДа?', '<Архивариус Джанира продолжает читать свиток перед собой, не глядя на вас.>\n\nДа?', 38134),
(206511, 'ruRU', 'И, $p... если захочешь сразиться еще с одним, можешь в любое время принести мне фрагменты камня грехов.', 'И, $p... если захочешь сразиться еще с одним, можешь в любое время принести мне фрагменты камня грехов.', 38134),
(206510, 'ruRU', 'Уверяю тебя, я очень ценю то, что ты стараешься избавить нас от инквизиторов Лорд-камергера.', 'Уверяю тебя, я очень ценю то, что ты стараешься избавить нас от инквизиторов Лорд-камергера.', 38134),
(206509, 'ruRU', 'Прости меня. Мне часто говорят, что я кажусь холодным и неблагодарным.', 'Прости меня. Мне часто говорят, что я кажусь холодной и неблагодарной.', 38134),
(206508, 'ruRU', 'Архивариус Фейн громко кашляет.', 'Архивариус Фейн громко кашляет.', 38134),
(206507, 'ruRU', 'Архивариус Фейн в Чертогах Покаяния всегда готов создать для тебя камень грехов инквизитора... если у тебя найдутся нужные фрагменты.', '', 38134),
(206505, 'ruRU', 'Наверняка ничего сказать не могу.\n\nКак насчет деревьев рядом с поселением? Претенденты любили там читать, до того как это место наводнили эфирные змеи.', 'Наверняка ничего сказать не могу.\n\nКак насчет деревьев рядом с поселением? Претенденты любили там читать, до того как это место наводнили эфирные змеи.', 38134),
(206496, 'ruRU', 'Таббинсу всегда достается все самое сложное. Ходить за водой легко!', '', 38134),
(206493, 'ruRU', 'Здесь ты найдешь нужную воду...\n\nЕсли тебе не удастся ее достать, поищи меня.\n\nЯ всегда где-то неподалеку... и почти всегда готов помочь...', '', 38134),
(206492, 'ruRU', 'До встречи, дитя. Не забывай усердно работать... и слушаться старших...', '', 38134),
(206491, 'ruRU', 'Задержи дыхание... зажми рот... \n\nИ вперед...', '', 38134),
(206490, 'ruRU', 'Я могу отправить тебя обратно в лес, когда ты ее соберешь.', '', 38134),
(206489, 'ruRU', 'Впереди ты найдешь нужную воду.', '', 38134),
(206488, 'ruRU', 'Приготовься... Поначалу путешествия по моей мицелиальной сети немного... дезориентируют.', '', 38134),
(206487, 'ruRU', 'Сначала прояви себя... Ты любишь работать?', '', 38134),
(206486, 'ruRU', 'Погоди, дитя... Я не пускаю незнакомцев шнырять туда-сюда по моей мицелиальной сети.', '', 38134),
(206483, 'ruRU', 'Я наблюдал, как ты трудишься, через мою сеть.\n\nВезде, где меня видно, вижу и я... А меня видно повсюду.\n\nТы действительно любишь работать.', '', 38134),
(206482, 'ruRU', 'Эх, молодняк... Вечно копошитесь, шумите...\n\nЧто же не так на этот раз?..', '', 38134),
(206479, 'ruRU', 'Постарайтесь хорошенько отдохнуть. Скоро нас ждет новая охота.', '', 38134),
(206478, 'ruRU', 'Это точно.', '', 38134),
(206477, 'ruRU', '', 'А сильнее Дикой Охоты нет никого!', 38134),
(206476, 'ruRU', '', 'Вместе корни вырастают сильнее.', 38134),
(206475, 'ruRU', '', 'В этот раз я многому научилась. Для меня было честью охотиться с вами.', 38134),
(206474, 'ruRU', '', 'Это была славная охота. У тебя получается все лучше и лучше, Люмера.', 38134),
(206473, 'ruRU', 'Пожиратель!', '', 38134),
(206472, 'ruRU', '', 'Это меня и тревожит.', 38134),
(206471, 'ruRU', '', 'Нет, ничего...', 38134),
(206470, 'ruRU', '', 'Постой. Ты что-нибудь слышишь?', 38134),
(206469, 'ruRU', 'А теперь мы видим их повсюду в нашем лесу. Настали поистине смутные времена.', '', 38134),
(206468, 'ruRU', '', 'Сказки не врали. Хотя Дикая Охота уже очень давно не сражалась с друстами.', 38134),
(206467, 'ruRU', '', 'Когда-то я думала, что друстов не бывает... что это сказки для маленьких пикси.', 38134),
(206466, 'ruRU', '', 'Ты это заслужила, Сика. Развлекись хорошенько.', 38134),
(206465, 'ruRU', '', 'Они пожалеют, что заявились в Арденвельд!', 38134),
(206464, 'ruRU', 'Друсты! Дикая Охота, к оружию!', '', 38134),
(206463, 'ruRU', '', 'У Сики... такие хорошие друзья. Сика... может пойти на вечеринку!', 38134),
(206462, 'ruRU', '', 'Сика какое-то время колеблется, но затем расплывается в широкой улыбке.', 38134),
(206461, 'ruRU', '', 'Отправляйся с |3-4($p) и насладись приемом. Мы обо всем позаботимся, пока тебя не будет.', 38134),
(206460, 'ruRU', 'Ты столько для нас делаешь. Позволь и нам тебе помочь.', '', 38134),
(206459, 'ruRU', 'Да, Сика. Мы не представляли, что тебе приходится так тяжело.', '', 38134),
(206458, 'ruRU', 'Это слова истинной охотницы.', '', 38134),
(206457, 'ruRU', '', 'Мы этого не допустим. Дикая Охота будет защищать лес, что бы ни случилось.', 38134),
(206456, 'ruRU', 'Если эта засуха скоро не закончится, то весь Арденвельд разделит судьбу Тирна Скитта.', '', 38134),
(206454, 'ruRU', '<Кажется, анима этого центуриона полностью исчерпана.>', '', 38134),
(206453, 'ruRU', '', 'А теперь их стало еще больше!', 38134),
(206452, 'ruRU', '', 'Дождь проливает слезы за Тирна Скитт. Так мало анимы, но так много гормов.', 38134),
(206445, 'ruRU', 'Тебе что-нибудь нужно, пилигрим?\n\nУ меня есть немного времени, прежде чем я займусь этими душами.', 'Тебе что-нибудь нужно, пилигрим?\n\nУ меня есть немного времени, прежде чем я займусь этими душами.', 38134),
(206444, 'ruRU', 'Лейтфор мрачно кивает.', '', 38134),
(206443, 'ruRU', '', 'В Тирна Скитте нет беженцев. Мы убедились в этом на горьком опыте.', 38134),
(206442, 'ruRU', '', 'Это была иллюзия... а я и поверила.', 38134),
(206441, 'ruRU', 'Люмера, нет! Стой!', '', 38134),
(206440, 'ruRU', '', 'Смотрите, беженцы! Нужно им помочь!', 38134),
(206439, 'ruRU', '', 'Мне уже не терпится посетить его прием.', 38134),
(206438, 'ruRU', '', 'У тебя была возможность сразиться против слабейших. Но вместо этого ты бросаешь вызов самым сильным.', 38134),
(206437, 'ruRU', '', 'Принцу Ренаталу есть что предложить, раз он смог заручиться твоей поддержкой.', 38134),
(206436, 'ruRU', 'Мы ведь теперь лучшие друзья, не правда ли, $p?\n\nТак сколько мне еще умолять тебя переправить несколько землероев твоего ковенанта сюда, в Малдраксус?\n\nЯ, конечно, уже обратил нескольких в жижу, пока никто не видел, но представь, какие эксперименты я смогу провести над ними здесь, в лаборатории.\n\nЯ наконец-то смогу ответить на вопрос, не дающий всем покоя: сделаны ли землерои из слизи?\n\nЭто вполне вероятно!', '<Сика откладывает в сторону бумаги, которые она приводила в порядок, чтобы поговорить с вами.>\n\n$p! Ты – хороший сюрприз.\n\nОбычно сюрпризы плохие. Нарушают планы. Прибавляют работы.\n\nСика думала о новом приеме. Сика надеется, что ее пригласят.\n\nМного дел, но лучший друг обязательно поможет.\n\nУ Сики хороший друг.', 38134),
(206435, 'ruRU', 'А вот и мой любимый бывший подмастерье, ставший глашатаем вечеринок!\n\nМне просто не терпится поделиться с тобой идеями для твоего приема.\n\nНо предупреждаю: для каждой из них требуется умопомрачительное количество слизи.\n\nОднако некоторые разновидности этой слизи такие вкусные, что достойны праздничного стола!', '', 38134),
(206434, 'ruRU', 'Меня беспокоит твой выбор карьеры, $p.\n\nНикогда еще мои бывшие ученики не начинали устраивать приемы.\n\nЭто неслыханно...\n\nНо Кевину эта затея, похоже, нравится. Да и кто я, чтобы вставать на пути прогресса?\n\nКстати, когда состоится следующий прием?', '', 38134),
(206433, 'ruRU', 'Это же $p! Кевин, убери-ка экспериментальное зелье... ненадолго.', '', 38134),
(206432, 'ruRU', 'Смотри, не наступи в мой последний эксперимент, $p. Мне бы не хотелось тебя потерять.', '', 38134),
(206431, 'ruRU', 'Что это я слышу? Неужели сюда идет мой наивный ученик?', '', 38134),
(206430, 'ruRU', '$p, я как раз рассказывал о тебе и ПОЧТИ самом лучшем способе развлечься в Темных Землях.\n\nКстати, когда следующий прием? Я пообещал паре многообещающих бойцов, что возьму их с собой.', '', 38134),
(206429, 'ruRU', '$p, дружище! Останься и посмотри со мной бои.\n\nЧто тебе нужно? Скажи, и тебе все принесут.\n\nС таким другом, как Воул, тебе не нужно шататься по всему Малдраксусу.', '', 38134),
(206428, 'ruRU', 'Разве твой Пепельный двор может сравниться с таким зрелищем, пилигрим?\n\nИли на твой следующий прием ворвется Азмогал? Или, может, Урц наступит на праздничный торт?\n\n<Воул указывает на огненного великана внизу.>\n\nДумаю, оно и к лучшему. Не думаю, что эти хрупкие вентиры с ним справятся.', '', 38134),
(206427, 'ruRU', 'Иди сюда и полюбуйся на представление, $p. Я занял тебе место.', '', 38134),
(206426, 'ruRU', 'Посторонитесь! Пропустите чемпиона! $p!', '', 38134),
(206425, 'ruRU', 'Кто будет следующим? Как насчет тебя, пилигрим?', '', 38134),
(206424, 'ruRU', 'В ставках нет ничего плохого. Не каждому дано сражаться на арене.', '', 38134),
(206423, 'ruRU', '$p, когда бы мы ни встретились, я всегда вспоминаю о моей прошлой жизни на Азероте.\n\nКогда все это закончится, и мы будем праздновать нашу победу за одним столом, у меня к тебе будет много вопросов.\n\nНо пока надо думать только о цели.\n\nНа кону слишком много, чтобы забывать о наших врагах даже на минуту.', '', 38134),
(206422, 'ruRU', 'Тебе приходилось вести за собой войска до прибытия в Темные Земли, $p?\n\nНе похоже, что это был твой первый опыт. Быть командиром непросто, это мало кому дается сразу.\n\nЯ слышал разговоры других гостей на приеме: то и дело они осуждали твои решения. Как будто все благополучие Темных Земель зависит от их прихотей.\n\nНе принимай их слова близко к сердцу.\n\nПроложи для нас путь к победе. И рано или поздно они все поймут.', '', 38134),
(206421, 'ruRU', 'Посмотри на тех солдат, пилигрим.\n\nОни сильны, выносливы и верны долгу.\n\nНо этого мало. Армии нужен умный, хитрый и волевой командир, который воодушевит солдат.\n\nМне не терпится узнать, есть ли эти качества у принца Ренатала, а если есть – как он их использует.\n\nКогда состоится следующий прием? Я постараюсь попасть на него и поговорить с принцем.', '', 38134),
(206420, 'ruRU', 'А, $p. Как поживает мой любимый пилигрим?', '', 38134),
(206419, 'ruRU', 'Как проходят битвы в Темных Землях?', '', 38134),
(206418, 'ruRU', 'Можешь подойти, пилигрим.', '', 38134),
(206417, 'ruRU', 'Я прибуду в сопровождении секутора Мевикса и двух наших лучших воинов. Надеюсь, ты и им окажешь свое гостеприимство.', '', 38134),
(206413, 'ruRU', '', 'Ты все время куда-то спешишь, $p.\n\nНо я не жалуюсь. Это мне и нравится в наших отношениях.\n\nТы не тратишь мое время, а я не гоняю тебя с дурацкими поручениями.\n\nТак что привело тебя ко мне на этот раз?\n\nНужно что-то разведать? Кого-то убить? Дать совет по проведению приемов?\n\nПовелительница шпионов к твоим услугам.', 38134),
(206411, 'ruRU', '', 'На своем веку я видела, как тысячи принцев устраивали свои приемы, пилигрим.\n\nВероятность того, что все пойдет не так, весьма велика, даже при наилучших обстоятельствах.\n\nТвоему принцу стоит больше прислушиваться к своим критикам, нежели к обожателям.\n\nНа приемах всегда найдутся и те, и другие. Угадай, кем из них буду я.\n\n<Баронесса Вайш ухмыляется.>', 38134),
(206410, 'ruRU', 'Вольно, часовые. $p мой друг.', '$p... Ты в числе немногих, кого я нынче рада увидеть в своем лагере.', 38134),
(206409, 'ruRU', 'С возвращением, $p. В Обители Героев все спокойно.', 'Лучший глашатай вечеринок – и в нашем уголке Малдраксуса!', 38134),
(206408, 'ruRU', 'Здравствуй, пилигрим Утробы.', 'Похоже, глаза не подвели тебя, Кхалик. Вот и пилигрим Утробы.', 38134),
(206407, 'ruRU', 'Я всегда там, где нужна, и сейчас я нужна здесь.', 'Принимай гостей осторожно, $p... Ты ведь не хочешь иметь дело с каким-нибудь чудовищем?', 38134),
(206406, 'ruRU', '', 'Лес проявил милосердие.', 38134),
(206405, 'ruRU', 'Это точно. Но кто бы это мог быть?', '', 38134),
(206404, 'ruRU', '', 'Обычно спригганы нападают неорганизованной толпой. Должно быть, сейчас их кто-то направляет.', 38134),
(206403, 'ruRU', 'Почему ты отдаешь это послание мне, а не полемарху Адресту?', '', 38134),
(206402, 'ruRU', 'Осторожно!', '', 38134),
(206401, 'ruRU', '', 'Спригганы! Как раз набьем руку! Жаль только, что их так мало...', 38134),
(206400, 'ruRU', '', 'Не переживай из-за Заркина, Люмера. Для него действия дороже слов.', 38134),
(206399, 'ruRU', '', 'Да. Просто не дайвай нашим врагам спуску!', 38134),
(206398, 'ruRU', 'Лейтфор молча кивает.', '', 38134),
(206396, 'ruRU', 'Надеюсь, ты права. Лейтфор, займи позицию.', '', 38134),
(206395, 'ruRU', '', 'Я справлюсь. Меня не страшат испытания.', 38134),
(206394, 'ruRU', 'Будь начеку, Люмера. Это опасное задание для охотницы-новичка.', '', 38134),
(206393, 'ruRU', 'Я и не знал, что на приемах может быть так интересно, $p. Ты можешь гордиться успехом Пепельного Двора.\n\nЧашка за чашкой ты объединяешь Темные Земли!\n\nКонечно же, все далеко не так просто. И все же... Кто бы мог подумать?', '', 38134),
(206392, 'ruRU', 'У тебя есть где остановиться и передохнуть, пилигрим?\n\nНе дело спасать Темные Земли вполсилы.', '', 38134),
(206391, 'ruRU', 'У тебя не найдется минутка, $p? Я хочу тебе кое-что сказать.\n\n<Пелагий делает глубокий вдох.>\n\nЯ был обижен твоим решением присоединиться к Ревендрету в борьбе за Темные Земли. Я надеялся, что ты предпочтешь сражаться вместе со мной и ковенантом кирий.\n\nНо теперь я понимаю твой выбор. Когда обитатели разных миров встречаются при Пепельном дворе, Темные Земли становятся такими, как прежде... какими они должны быть.\n\nВ любом случае, я рад, что после всего мы стали друзьями. Хотя мы и не стали медиумами, мне кажется, между нами есть иная связь.', '', 38134),
(206390, 'ruRU', '', 'Мой друг! Как здорово, что у тебя нашлась возможность поговорить.\n\nКак давно мы не виделись? В любом случае, слишком давно.\n\nРасскажи мне обо всем, что с тобой случилось за это время. Хочешь, мы с Пелагием тебе поможем?\n\n<Клейя делает вдох и улыбается.>\n\nЯ так рада тебя видеть, $p.', 38134),
(206389, 'ruRU', '', 'Я рада снова видеть тебя среди душ Бастиона, $p.\n\nПосетив твои приемы, я могу с уверенностью сказать, что ты трудишься во благо Темных Земель, просто теперь тебе в этом помогают вентиры.', 38134),
(206388, 'ruRU', '', 'Темным Землям нужна моя помощь, пилигрим?\n\nЯ готова служить.', 38134),
(206387, 'ruRU', 'И главное, тебе нужны спутники?', '', 38134),
(206386, 'ruRU', '', '$p! Куда ты направляешься, друг?', 38134),
(206384, 'ruRU', '', 'Сила Тюремщика растет, и на Темные Земли надвигается страшная угроза. Готовься, $p. Твоя сила нам еще пригодится.', 38134),
(206383, 'ruRU', '', 'Там, куда отправится $p, будет безопаснее всего.', 38134),
(206382, 'ruRU', 'Береги себя, $p.', '', 38134),
(206381, 'ruRU', '', 'Пилигрим все нам расскажет, если захочет.', 38134),
(206380, 'ruRU', 'Интересно, куда направляется пилигрим...', '', 38134),
(206378, 'ruRU', 'Если я до сих пор тебе об этом не сказал, то знай: я очень благодарен тебе, $p.\n\nС тобой я узнал много нового о службе, самоотверженности и...\n\n<Полемарх Адрест озирается по сторонам и, убедившись, что никто из претендентов его не слышит, продолжает говорить.>\n\n...необходимости время от времени развлекаться.\n\nПризнаюсь, сперва я отнесся к твоим приемам при Пепельном Дворе скептически, но теперь я прихожу на них с удовольствием.\n\nВсе ради Архонта, конечно же!', '', 38134),
(206377, 'ruRU', 'Я рад снова видеть тебя в Бастионе, $p.\n\nЯ часто думаю, что было бы, выбери ты путь служения Архонту, как и я.\n\nОднако совершенно очевидно, что твоя служба в Грехопаде идет на пользу всем Темным Землям.\n\nНадеюсь, под твоим присмотром там все будет в порядке.', '', 38134),
(206376, 'ruRU', 'Что привело тебя в Обитель Героев, пилигрим?\n\nЕсли тебе нужна помощь в бою, мы с часовыми готовы служить.\n\nИли ты хочешь поделиться новостями из Грехопада? Я могу передать их в соответствующие инстанции.', '', 38134),
(206374, 'ruRU', 'Вольно, часовые. $p мой друг.', '', 38134),
(206373, 'ruRU', 'С возвращением, $p. В Обители Героев все спокойно.', '', 38134),
(206372, 'ruRU', 'Здравствуй, пилигрим Утробы.', '', 38134),
(206371, 'ruRU', 'Я всегда там, где нужен, и сейчас я нужен здесь.', '', 38134),
(206367, 'ruRU', '', '|3-2($p) что-то нужно? Займусь этим сразу!', 38134),
(206366, 'ruRU', '', 'Сике нравится, когда $p ее навещает!', 38134),
(206365, 'ruRU', '', 'Пилигриму тоже что-то нужно? Добавлю дело в список.', 38134),
(206364, 'ruRU', '', 'Как грустно. На приеме весело. Может, в другой раз...', 38134),
(206363, 'ruRU', '<Миканикос внимательно осматривает детали вашей брони, бормоча себе под клюв.>\n\nКакая хитрая работа... Все такое новое... И интересное...\n\nТвои визиты не дают нам соскучиться, пилигрим.', '', 38134),
(206362, 'ruRU', 'Для |3-1($p) в моей мастерской всегда найдется место.', '', 38134),
(206361, 'ruRU', '<Миканикос откладывает свое новое творение и обращает все свое внимание на вас.>\n\n$p, обожаю, когда ты заходишь ко мне в мастерскую.\n\nС тобой всегда приходят хорошие идеи.\n\nРабота подождет. Скажи, друг, что привело тебя в Вечную кузню?\n\nПросто в гости? Что-нибудь нужно? Скоро новый прием? Надеюсь, что да!', '', 38134),
(206360, 'ruRU', '$p! О чем расскажешь на этот раз?\n\nО сражениях с новыми врагами? О новых инженерных находках? Или же новых заклинаниях?\n\nРасскажи мне все.\n\nБольшие открытия часто кроются в мелких деталях!', '', 38134),
(206359, 'ruRU', 'Каждая деталь идеально подходит для чего-то, но в этот раз я задумал нечто особенное!', '', 38134),
(206358, 'ruRU', 'Друг! При Пепельном Дворе нашлось столько диковинок... Скоро мне понадобятся новые!', '', 38134),
(206357, 'ruRU', 'Столькому можно научиться... Столько опробовать...', 'Тебе всегда рады в моей роще, пилигрим.', 38134),
(206355, 'ruRU', '', 'Я знаю.', 38134),
(206354, 'ruRU', 'Что ж, тебе виднее. И я тебе верю. Правда. Просто мне хотелось бы, чтобы все происходило немного быстрее.', '', 38134),
(206353, 'ruRU', '', 'Может, тебе так не кажется, но я вижу, что ты все лучше справляешься со своими воспоминаниями. Ты молодец, Пелагий.', 38134),
(206352, 'ruRU', 'По-моему, я совсем не продвинулся в этом испытании, Клейя.', '', 38134),
(206346, 'ruRU', 'Как здорово, что тебе удалось отыскать этих духов. С ними в зимнем саду гораздо веселее, не правда ли?', 'Как здорово, что тебе удалось отыскать этих духов. С ними в зимнем саду гораздо веселее, не правда ли?', 38134),
(206345, 'ruRU', 'Этот дух лучше себя чувствует в воде. Думаю, ты сможешь найти его недалеко от озера.', 'Этот дух лучше себя чувствует в воде. Думаю, ты сможешь найти его недалеко от озера.', 38134),
(206344, 'ruRU', 'Если принесешь мне плакаты с новыми стрижками, я отращу себе другую прическу, а если шляпу, то я могу ее надеть!', '', 38134),
(206343, 'ruRU', 'Красители можно найти около луж жижи или на ночном рынке.', '', 38134),
(206338, 'ruRU', 'Наши кадавры обожают красоваться на поле боя. Ты сможешь раздобыть множество модных аксессуаров, которые помогут приукрасить внешний вид кадавров.\n\nОднако чтобы добыть ингредиенты для этих аксессуаров, тебе придется хорошо поискать.', '', 38134),
(206337, 'ruRU', 'Качественные части необходимы для изготовления мощных предметов и боевых кадавров.\n\nНайти их довольно сложно, но наши кадавры помогут тебе с их поиском, если ты выполнишь их просьбы.', '', 38134),
(206336, 'ruRU', 'Податливая плоть – основной ингредиент, необходимый для изготовления большинства кадавров и предметов с помощью сшивания.\n\nДобыть подходящий кусок податливой плоти ты сможешь почти у каждого существа в Темных Землях.\n\nКроме того, у Корди отлично получается ее находить, так что можешь обращаться к нему, если тебе понадобится этот материал.', '', 38134),
(206325, 'ruRU', 'Чтобы сшивать поганищ, нужна твердая рука, хорошие материалы и, конечно же, такой стол для сшивания поганищ, как этот.\n\nЯ составил для тебя список всех материалов, которые потребуются для создания на этом столе всех возможных поганищ и предметов.', '', 38134),
(206322, 'ruRU', 'Если они не смогут сюда прийти и пропустят все веселье, то расстроятся!', '', 38134),
(206321, 'ruRU', 'Не помню, говорил я тебе это уже или нет, но спасибо за разрешение привести всех моих друзей в Пепельный двор.\n\nМы дружные ребята. Всегда вместе.\n\nК тому же Вихлюн бы меня в жизни не простил, если бы я начал ходить на такие классные приемы без него!\n\n<Чуфа смеется.>\n\nС другой стороны, я бы мог тогда бродить с тобой вместо него, да? Ты теперь из нашей компании.', '', 38134),
(206320, 'ruRU', 'Большие Дворы, большие приемы. Вот это жизнь у нас с тобой! ...и у Рури, и у Ухача, и у Вихлюна.\n\nКстати, когда следующий прием? Я свободен прямо сейчас. Хочешь, придем первыми, откроем пару бутылок и покутим среди своих?\n\nЛадно-ладно, я вижу, тебе не до этого. Дела. Ничего, нормально.\n\nТы, главное, зови меня в следующий раз, хорошо?', '', 38134),
(206319, 'ruRU', 'Эй, дружище! У тебя остались какие-нибудь фейерверки после приема?', '', 38134),
(206318, 'ruRU', 'Эй, $p! Когда следующий прием?', '', 38134),
(206317, 'ruRU', 'Ну, и куда убежал этот Рури?', '', 38134),
(206316, 'ruRU', '', 'Я не всегда готова довериться брокерам... но и не всегда могу устоять перед их товарами.', 38134),
(206315, 'ruRU', '', 'Боль и утрата – хорошие учителя, и мне не раз приходилось быть их ученицей.\n\nНо благодаря им я стала еще больше ценить приглашения на твои приемы.\n\nЭти путешествия... на многое открыли мне глаза. Твой принц говорил весьма убедительно. Я верю, что он действительно желает добра нам и другим мирам.\n\nБлагодарю тебя, $p. Я буду ждать нашей встречи на новом приеме.', 38134),
(206314, 'ruRU', '', 'Я наслаждалась каждой минутой проделанного вместе с тобой пути, $p, и я чувствую, что это только начало нашей долгой дружбы.\n\nЗа время, проведенное с тобой, я уже многое пересмотрела и увидела перед собой новые возможности. Надеюсь, я и тебе смогла открыть глаза на что-нибудь новое.\n\nЯ понимаю, что ты не можешь долго со мной оставаться. Таков удел пилигрима Утробы.\n\nИ все же постарайся приходить почаще. Моя роща совсем не та без тебя.', 38134),
(206313, 'ruRU', '', 'С возвращением, $p. Ты торопишься?', 38134),
(206312, 'ruRU', '', 'Здравствуй, друг. Что привело тебя в мою рощу?', 38134),
(206311, 'ruRU', '', 'Тебе всегда рады в моей роще, пилигрим.', 38134),
(206310, 'ruRU', '', 'А, $p.\n\nПока ты здесь, охота может подождать.\n\nКак дела в Ревендрете? Что до Арденвельда – друсты, гормы и другие вредители продолжают угрожать нам со всех сторон.\n\nНо я, конечно, не жалуюсь. Больше врагов – больше дичи.\n\nНадеюсь, скоро ты сможешь составить мне компанию. Я успела привыкнуть к твоему стилю боя.\n\nКогда ты со мной, мне и никакой отряд не нужен.', 38134),
(206309, 'ruRU', '', 'Как дела при Пепельном Дворе, $p?\n\nНа следующем приеме можно будет поохотиться? Надеюсь, дичь будет новая? Тогда я точно приду.\n\nМне очень нравится охотиться с тобой на территории Двора, но преследовать новую дичь всегда интереснее. Не правда ли?\n\nВ любом случае, мне не терпится вновь поохотиться вместе.', 38134),
(206308, 'ruRU', '', 'Полагаю, с охотой у тебя по-прежнему все хорошо, $p.\n\nСкажи мне, какую добычу удалось выследить в последнее время?', 38134),
(206307, 'ruRU', '', '$p! Отдохни и расскажи мне о своих охотничьих успехах.', 38134),
(206306, 'ruRU', '', 'Как твоя охота, пилигрим?', 38134),
(206305, 'ruRU', '', 'Охота зовет. Кто ответит на этот зов?', 38134),
(206304, 'ruRU', '', 'Удачной охоты, пилигрим.', 38134),
(206303, 'ruRU', '', 'Поверь, феромоны гормов пахнут гораздо лучше, чем ты думаешь.', 38134),
(206302, 'ruRU', '', '<Леди Лунная Ягода оглядывается по сторонам, прежде чем наклониться к вам и прошептать.>\n\nТеперь мы лучшие друзья, $p. Это значит, что мы не бросим друг друга ни при каких обстоятельствах.\n\nКстати, я придумала наш следующий розыгрыш и хочу...\n\n<Леди Лунная Ягода замечает, что ее подслушивает пикси.>\n\nПоговорим об этом на следующем приеме Пепельного двора. Не могу его дождаться!', 38134),
(206301, 'ruRU', '', '<Леди Лунная Ягода заглушает смешок.>\n\n$p! Я так рада, что у тебя нашлось время зайти.\n\nУвидев тебя, я вспомнила, как подшутила над Теотаром на прошлом Пепельном дворе. Как славно я повеселилась!\n\nТеотар придет на следующий прием? Он обещал, что расквитается со мной, и я все еще жду...', 38134),
(206300, 'ruRU', '', 'А вот и ты! Какие проделки ты затеваешь сегодня?\n\nИ самое главное, могу ли я тебе с ними помочь?', 38134),
(206299, 'ruRU', '', '$p! Ты же не пройдешь мимо, не поздоровавшись со своей любимой пикси?', 38134),
(206298, 'ruRU', '', 'Не переживай, $p. На сегодня я оставлю тебя в покое.', 38134),
(206297, 'ruRU', '', 'Это что-то интересное...', 38134),
(206296, 'ruRU', 'У нас много дел... если тебе немедленно нужна помощь, обратись к деснице Чистоты.', 'У нас много дел... если тебе немедленно нужна помощь, обратись к деснице Чистоты.', 38134),
(206295, 'ruRU', '<Перерожденный внимательно на вас смотрит.>\n\nЧто тебя тяготит?', '<Перерожденная внимательно на вас смотрит.>\n\nЧто тебя тяготит?', 38134),
(206294, 'ruRU', 'Я приду к тебе на помощь, как только смогу. Прошу, подожди еще немного.', 'Я приду к тебе на помощь, как только смогу. Прошу, подожди еще немного.', 38134),
(206293, 'ruRU', 'Чем я могу помочь?', 'Чем я могу помочь?', 38134),
(206292, 'ruRU', 'Что-то не дает тебе покоя?', 'Что-то не дает тебе покоя?', 38134),
(206291, 'ruRU', 'Тебе тоже требуется очищение? Боюсь, придется подождать. Многим другим тоже нужна моя помощь.', 'Тебе тоже требуется очищение? Боюсь, придется подождать. Многим другим тоже нужна моя помощь.', 38134),
(206290, 'ruRU', 'Я никогда не видел ничего подобного. Претенденты часто обращаются к нам за помощью, но они никогда не приходили сюда в таком количестве... Мне начинает казаться, что и за пределами нашего храма далеко не все в порядке.\n\nПрости меня. Я должен вернуться к работе.', 'Я никогда не видела ничего подобного. Претенденты часто обращаются к нам за помощью, но они никогда не приходили сюда в таком количестве сразу... Мне начинает казаться, что и за пределами нашего храма далеко не все в порядке.\n\nПрости меня. Я должна вернуться к работе.', 38134),
(206289, 'ruRU', 'Добро пожаловать в храм Чистоты. Осмотрись вокруг и отдыхай на здоровье... но веди себя учтиво. Здесь ожидают очищения и многие другие претенденты.', 'Добро пожаловать в храм Чистоты. Осмотрись вокруг и отдыхай на здоровье... но веди себя учтиво. Здесь ожидают очищения и многие другие претенденты.', 38134),
(206288, 'ruRU', '<Перерожденный кивает вам.>\n\nДа хранит тебя Архонт, смертная душа.', '<Перерожденная кивает вам.>\n\nДа хранит тебя Архонт, смертная душа.', 38134),
(206287, 'ruRU', 'Твое прибытие значит для нас больше, чем тебе кажется. Для меня честь быть его свидетелем.', 'Твое прибытие значит для нас больше, чем тебе кажется. Для меня честь быть его свидетелем.', 38134),
(206286, 'ruRU', 'Твое прибытие устроило в Элизийской цитадели настоящий переполох.', 'Твое прибытие устроило в Элизийской цитадели настоящий переполох.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206285, 'ruRU', '<Перерожденный с интересом смотрит на вас.>\n\nПоздравляю. Я рад поприветствовать тебя среди наших претендентов.', '<Перерожденная с интересом смотрит на вас.>\n\nПоздравляю. Я рада поприветствовать тебя среди наших претендентов.', 38134),
(206284, 'ruRU', 'Не для того я умер и перешел через... как они ее назвали, \"завесу\"?.. чтобы провести целую вечность в ожидании!\n\nПостой, ты же... новая душа! Неужели ждать больше не придется?!', 'Не для того я умерла и перешла через... как они ее назвали, \"завесу\"?.. чтобы провести целую вечность в ожидании!\n\nПостой, ты же... новая душа! Неужели ждать больше не придется?!', 38134),
(206283, 'ruRU', '<Претендент очень тяжело вздыхает.>\n\nПо крайней мере, это далеко не худшее место для ожидания.', '<Претендентка очень тяжело вздыхает.>\n\nПо крайней мере, это далеко не худшее место для ожидания.', 38134),
(206282, 'ruRU', 'Мне предстоит такой долгий путь, и пока я даже не могу его начать... Такое впечатление, что меня направили сюда по ошибке.', 'Мне предстоит такой долгий путь, и пока я даже не могу его начать... Такое впечатление, что меня направили сюда по ошибке.', 38134),
(206281, 'ruRU', 'Терпение, говорят они! Что-то я не вижу, чтобы идеал Терпения сидел сложа руки!', 'Терпение, говорят они! Что-то я не вижу, чтобы идеал Терпения сидел сложа руки!', 38134),
(206280, 'ruRU', 'Есть какие-нибудь вести от Архонта? Засуха наконец закончилась?!', 'Есть какие-нибудь вести от Архонта? Засуха наконец закончилась?!', 38134),
(206279, 'ruRU', 'Нам велели ждать здесь, пока длится нехватка анимы... Я уже не помню, как давно это было. Мне уже кажется, что эта засуха никогда не закончится.\n\nЧто ж, зато распорядители очень милые, а я могу есть сколько угодно, не набирая вес! Ха-ха!', 'Нам велели ждать здесь, пока длится нехватка анимы... Я уже не помню, как давно это было. Мне уже кажется, что эта засуха никогда не закончится.\n\nЧто ж, зато распорядители очень милые, а я могу есть сколько угодно, не набирая вес! Ха-ха!', 38134),
(206278, 'ruRU', 'Я просто хочу начать свой путь к перерождению. Разве я о многом прошу?', 'Я просто хочу начать свой путь к перерождению. Разве я о многом прошу?', 38134),
(206277, 'ruRU', 'Это просто смешно! Я жду здесь уже... хм... а какой сегодня день?\n\nУже очень-очень долго!', 'Это просто смешно! Я жду здесь уже... хм... а какой сегодня день?\n\nУже очень-очень долго!', 38134),
(206276, 'ruRU', 'Новая душа? Наконец-то!', 'Новая душа? Наконец-то!', 38134),
(206275, 'ruRU', 'О! Новое лицо! У тебя есть новости с той стороны?', 'О! Новое лицо! У тебя есть новости с той стороны?', 38134),
(206274, 'ruRU', 'Я пожертвовал всей своей жизнью, чтобы попасть сюда, и это было прекрасно. Я был полон надежды. Здесь тоже красиво, но мне обещали, что я буду расти и стану лучшей версией себя. Вместо этого я застрял здесь, и растет только моя досада. Все ведь не могло быть именно так задумано?', 'Я пожертвовала всей своей жизнью, чтобы попасть сюда, и это было прекрасно. Я была полна надежды. Здесь тоже красиво, но мне обещали, что я буду расти и стану лучшей версией себя. Вместо этого я застряла здесь, и растет только моя досада. Все ведь не могло быть именно так задумано?', 38134),
(206273, 'ruRU', 'В смерти, по крайней мере, было что-то интересное. Все это ожидание... и ради чего?', 'В смерти, по крайней мере, было что-то интересное. Все это ожидание... и ради чего?', 38134),
(206272, 'ruRU', 'Я скучаю по своему телу.', 'Я скучаю по своему телу.', 38134),
(206271, 'ruRU', 'Надеюсь, твое появление – знак приближения лучших времен, друг. В Бастионе не все гладко.', 'Надеюсь, твое появление – знак приближения лучших времен, друг. В Бастионе не все гладко.', 38134),
(206270, 'ruRU', 'Хуже всего в этом ожидании то, что у тебя нет рук. Или ног. Или глаз.\n\nЕсли честно, не уверен, что понимаю, как все это работает. Мне сказали, что после первого ритуала очищения у меня будет новый облик, но с тех пор как началась засуха, почти никому не удалось его пройти.', 'Хуже всего в этом ожидании то, что у тебя нет рук. Или ног. Или глаз.\n\nЕсли честно, не уверена, что понимаю, как все это работает. Мне сказали, что после первого ритуала очищения у меня будет новый облик, но с тех пор как началась засуха, почти никому не удалось его пройти.', 38134),
(206268, 'ruRU', 'Мне сказали, что это место – моя награда за хорошую жизнь. Это был обман? Меня все-таки наказывают?', 'Мне сказали, что это место – моя награда за хорошую жизнь. Это был обман? Меня все-таки наказывают?', 38134),
(206267, 'ruRU', 'Говорят, сборщики много работают руками. Может, мне стоит заняться резьбой по дереву?..', 'Говорят, сборщики много работают руками. Может, мне стоит заняться резьбой по дереву?..', 38134),
(206266, 'ruRU', 'Мне кажется, быть дозорным очень непросто... но возможность давать душам новый шанс... что может быть достойнее?', 'Мне кажется, быть дозорным очень непросто... но возможность давать душам новый шанс... что может быть достойнее?', 38134),
(206265, 'ruRU', 'После перерождения я хотел бы стать носителем. Я мечтаю путешествовать за пределы Темных Земель. Хочу повидать другие миры!', 'После перерождения я хотела бы стать носителем. Я мечтаю путешествовать за пределы Темных Земель. Хочу повидать другие миры!', 38134),
(206264, 'ruRU', 'Бастион – такое чудесное место. Мне сказали, что до этой засухи небеса были полны анимы. Как бы мне хотелось на это посмотреть.', 'Бастион – такое чудесное место. Мне сказали, что до этой засухи небеса были полны анимы. Как бы мне хотелось на это посмотреть.', 38134),
(206263, 'ruRU', 'Когда-нибудь у меня будут крылья...\n\n<Претендент мечтательно вздыхает.>', 'Когда-нибудь у меня будут крылья...\n\n<Претендентка мечтательно вздыхает.>', 38134),
(206262, 'ruRU', 'Роща Пробуждения – последний шаг на пути к перерождению. Это одно из самых священных мест в Арденвельде.\n\nКогда дух готов вернуться в свой мир, его приводят сюда. Сквозь этот портал он возвращается, чтобы снова начать свой цикл.', 'Роща Пробуждения – последний шаг на пути к перерождению. Это одно из самых священных мест в Арденвельде.\n\nКогда дух готов вернуться в свой мир, его приводят сюда. Сквозь этот портал он возвращается, чтобы снова начать свой цикл.', 38134),
(206261, 'ruRU', 'Я уже три раза пытался пройти первый обряд очищения. Каждый раз меня одолевали мои тяготы! И это всего лишь первая ступень! А вдруг я останусь здесь навсегда?!', 'Я уже три раза пыталась пройти первый обряд очищения. Каждый раз меня одолевали мои тяготы! И это всего лишь первая ступень! А вдруг я останусь здесь навсегда?!', 38134),
(206260, 'ruRU', 'Мне нужно столькому научиться... Что ж, хотя бы меня никто не гонит. Сейчас все равно никто не может переродиться.', 'Мне нужно столькому научиться... Что ж, хотя бы меня никто не гонит. Сейчас все равно никто не может переродиться.', 38134),
(206259, 'ruRU', 'Сперва ритуал Чистоты, потом ритуал Отваги, затем ритуал...\n\nПостой, я вспомнил. Верности? Нет. Кажется, не он...', 'Сперва ритуал Чистоты, потом ритуал Отваги, затем ритуал...\n\nПостой, я вспомнила. Верности? Нет. Кажется, не он...', 38134),
(206258, 'ruRU', 'А как долго пришлось ждать тебе?', 'А как долго пришлось ждать тебе?', 38134),
(206257, 'ruRU', 'Мне надо еще столькому научиться, а из-за этой задержки мне придется ждать еще дольше. Я боюсь, что уже никогда не стану перерожденным.', 'Мне надо еще столькому научиться, а из-за этой задержки мне придется ждать еще дольше. Я боюсь, что уже никогда не стану перерожденной.', 38134),
(206256, 'ruRU', 'Сколько прошло времени с тех пор, как все вышло из-под контроля? В этом месте время течет самым необычным образом.', 'Сколько прошло времени с тех пор, как все вышло из-под контроля? В этом месте время течет самым необычным образом.', 38134),
(206255, 'ruRU', 'Я просто хочу выполнить пять ритуалов.', 'Я просто хочу выполнить пять ритуалов.', 38134),
(206254, 'ruRU', 'На каком этапе подготовки тебе пришлось остановиться? Я уже почти завершил ритуал Верности, когда все пошло не так. Я был так близок к перерождению... так близок.', 'На каком этапе подготовки тебе пришлось остановиться? Я уже почти завершила ритуал Верности, когда все пошло не так. Я была так близка к перерождению... так близка.', 38134),
(206253, 'ruRU', 'Сейчас я ничего не могу сделать. Медитация и тренировки не помогут мне приблизиться к перерождению. Только не теперь.\n\n<Претендент тяжело вздыхает.>', 'Сейчас я ничего не могу сделать. Медитация и тренировки не помогут мне приблизиться к перерождению. Только не теперь.\n\n<Претендентка тяжело вздыхает.>', 38134),
(206252, 'ruRU', 'Хотя Бастион и утратил свое былое величие, он по-прежнему остается самым прекрасным местом из всех, что мне доводилось видеть. Если и ждать чего-то целую вечность, то только здесь.', 'Хотя Бастион и утратил свое былое величие, он по-прежнему остается самым прекрасным местом из всех, что мне доводилось видеть. Если и ждать чего-то целую вечность, то только здесь.', 38134),
(206251, 'ruRU', 'Как ты проводишь время, друг?', 'Как ты проводишь время, друг?', 38134),
(206250, 'ruRU', 'Клянусь Архонтом, я так устал.', 'Клянусь Архонтом, я так устала.', 38134),
(206249, 'ruRU', 'Послужи храму, говорили они. Воплощай свою добродетель, говорили они. Что ж, я...\n\n<Он быстро затихает, когда замечает ваше присутствие.>', 'Послужи храму, говорили они. Воплощай свою добродетель, говорили они. Что ж, я...\n\n<Она быстро затихает, когда замечает ваше присутствие.>', 38134),
(206248, 'ruRU', '<Он быстро собирается с духом, прежде чем заговорить.>\n\nЧем могу помочь, претендент?', '<Она быстро собирается с духом, прежде чем заговорить.>\n\nЧем могу помочь, претендент?', 38134),
(206247, 'ruRU', 'Я доберусь до тебя как можно быстрее, претендент.', 'Я доберусь до тебя как можно быстрее, претендент.', 38134),
(206246, 'ruRU', 'Тебе тоже нужно очищение?', 'Тебе тоже нужно очищение?', 38134),
(206245, 'ruRU', 'Мне сказали, я смогу помочь им, когда сам пройду ритуал очищения. Но, оказалось, я... Я боюсь. Их так много!', 'Мне сказали, я смогу помочь им, когда сама пройду ритуал очищения. Но, оказалось, я... Я боюсь. Их так много!', 38134),
(206244, 'ruRU', '<Он делает долгий, глубокий вдох, прежде чем показать, что заметил вас.>\n\nВсе в порядке?', '<Она делает долгий, глубокий вдох, прежде чем показать, что заметила вас.>\n\nВсе в порядке?', 38134),
(206243, 'ruRU', 'Что тебе нужно?', 'Что тебе нужно?', 38134),
(206242, 'ruRU', 'Благодарю за терпение. Пожалуйста, знай: мы делаем все, что в наших силах.', 'Благодарю за терпение. Пожалуйста, знай: мы делаем все, что в наших силах.', 38134),
(206241, 'ruRU', 'Я могу чем-нибудь помочь тебе?', 'Я могу чем-нибудь помочь тебе?', 38134),
(206240, 'ruRU', 'Чем я могу помочь?', 'Чем я могу помочь?', 38134),
(206239, 'ruRU', 'Архонт смотрит на тебя, друг.', 'Архонт смотрит на тебя, друг.', 38134),
(206238, 'ruRU', 'Ух! Как этому распорядителю тебе помочь?', 'Ух! Как этому распорядителю тебе помочь?', 38134),
(206236, 'ruRU', 'Столько печали. Так много погибло.\n\n<Он опускает взор.>', 'Столько печали. Так много погибло.\n\n<Она опускает взор.>', 38134),
(206235, 'ruRU', 'Претенденты грустят – грустят и распорядители.', 'Претенденты грустят – грустят и распорядители.', 38134),
(206234, 'ruRU', 'Не грусти. Хочешь песенку? Или, может, сока?', 'Не грусти. Хочешь песенку? Или, может, сока?', 38134),
(206233, 'ruRU', 'Много грустных претендентов... Пусть нам всем станет лучше.', 'Много грустных претендентов... Пусть нам всем станет лучше.', 38134),
(206232, 'ruRU', 'Сок помогает думать. Помогает сражаться. Принести тебе сока?', 'Сок помогает думать. Помогает сражаться. Принести тебе сока?', 38134),
(206231, 'ruRU', 'Хорошая тренировка сегодня? Что-то болит? Может, посиди. Или разомнись.', 'Хорошая тренировка сегодня? Что-то болит? Может, посиди. Или разомнись.', 38134),
(206230, 'ruRU', 'Милый претендент. Что ты хочешь?', 'Милый претендент. Что ты хочешь?', 38134),
(206229, 'ruRU', 'Как мне тебя порадовать?', 'Как мне тебя порадовать?', 38134),
(206228, 'ruRU', 'Чистить! Украшать! Бастион красивый, да?', 'Чистить! Украшать! Бастион красивый, да?', 38134),
(206227, 'ruRU', 'Ах! Новый претендент! Звоните в колокола! Трубите в боевой рог! Лучший день!!!', 'Ах! Новый претендент! Звоните в колокола! Трубите в боевой рог! Лучший день!!!', 38134),
(206226, 'ruRU', 'Привет! Ты новичок! Отлично! Да!', 'Привет! Ты новичок! Отлично! Да!', 38134),
(206225, 'ruRU', 'У-ух! Здравствуй! Новый претендент! Ура!', 'У-ух! Здравствуй! Новый претендент! Ура!', 38134),
(206223, 'ruRU', 'Принести тебе что-нибудь?', 'Принести тебе что-нибудь?', 38134),
(206222, 'ruRU', 'Мы поможем! Что тебе нужно?', 'Мы поможем! Что тебе нужно?', 38134),
(206221, 'ruRU', '<Распорядитель весело напевает себе под нос.>', '<Распорядитель весело напевает себе под нос.>', 38134),
(206220, 'ruRU', 'Тебе нужно, я достаю?', 'Тебе нужно, я достаю?', 38134),
(206219, 'ruRU', '', 'Сейчас мы все чувствуем усталость. Эта засуха перевернула верх дном наше привычное существование. \n\nНо мы должны это вытерпеть. Чем я могу помочь?', 38134),
(206218, 'ruRU', '<Ахиллион бормочет себе под нос, повторяя упражнение.>', '', 38134),
(206217, 'ruRU', '<Филиос увлеченно разговаривает с Тимоном.>\n\nНельзя слишком сильно концентрироваться на воспоминании. Пусть оно всплывет в твоем сознании и постепенно рассеется. Я знаю, что на словах звучит куда легче, но ты уже в самом конце пути. Скоро преодолеешь и это. Я уверен.', '<Филиос увлеченно разговаривает с Тимоном.>\n\nНельзя слишком сильно концентрироваться на воспоминании. Пусть оно всплывет в твоем сознании и постепенно рассеется. Я знаю, что на словах звучит куда легче, но ты уже в самом конце пути. Скоро преодолеешь и это. Я уверен.', 38134),
(206216, 'ruRU', '<Тимон увлеченно разговаривает с Филиосом.>\n\nТы всегда так уверенно выдерживаешь испытания! Вот бы и мне перенять твою стойкость. Мои мысли разбегаются, как мыши, при первом намеке на неприятное воспоминание.', '<Тимон увлеченно разговаривает с Филиосом.>\n\nТы всегда так уверенно выдерживаешь испытания! Вот бы и мне перенять твою стойкость. Мои мысли разбегаются, как мыши, при первом намеке на неприятное воспоминание.', 38134),
(206214, 'ruRU', '', 'Кажется, ты нравишься моему медиуму. Думаю, мне стоит поблагодарить тебя – без своей кузни она немного загрустила.\n\nБуду рада встретить тебя в храме Отваги.', 38134),
(206213, 'ruRU', '', 'Обо мне не беспокойся. Я... неважно себя чувствую, но мой идеал позаботится обо мне, как только сможет. А пока я укреплю свой дух, тренируясь.\n\n<Эридия делает прерывистый вдох.> \n\nЯ должна забыть о моем горе, моем гневе и... Лисонии.', 38134),
(206211, 'ruRU', 'Мы поддерживаем цикл.', 'Мы поддерживаем цикл.', 38134),
(206210, 'ruRU', '', 'Все уязвимые претенденты... все перерожденные, посланные нами в храм Верности... такая их ждала участь? Должно быть, они лишились рассудка, раз напали на этот храм! Мы им не враги! Мы лишь хотели...\n\n<Эридия замолкает и устремляет взгляд за пределы храма. По ее щеке стекает одинокая слеза.>', 38134),
(206209, 'ruRU', '', 'Твое прибытие совпало с крайне необычными обстоятельствами. Но не дай происходящему себя смутить. В такие темные времена нам всем нужно сохранять здравый рассудок.', 38134),
(206208, 'ruRU', 'Добро пожаловать в рощу Пробуждения.', 'Добро пожаловать в рощу Пробуждения.', 38134),
(206207, 'ruRU', '', 'Как десница Чистоты, я исполняю волю моего идеала.', 38134),
(206206, 'ruRU', '', 'Тебе нужна помощь наших послушников?', 38134),
(206205, 'ruRU', '', 'Я сделаю так, как велит мой идеал.', 38134),
(206204, 'ruRU', '', 'На все воля Весифоны.', 38134),
(206202, 'ruRU', 'Они не послушают. Они не хотят нас слышать! Нужно остановить эту агрессию, пока мы все не погибли!', '', 38134),
(206201, 'ruRU', 'Благодарю тебя за помощь. Мы все едва держимся.', '', 38134),
(206200, 'ruRU', 'Я посвятил вечность служению храму Чистоты. Пока ты здесь, обращайся ко мне за любой помощью.', '', 38134),
(206199, 'ruRU', '', 'Я так давно не была свидетельницей этой церемонии. \n\nПоздравляю. Благодаря тебе у нас вновь появилась надежда.', 38134),
(206198, 'ruRU', '', 'Да, я все еще здесь. \n\n<Автоматург Софона усмехается.> \n\nОказалось, что моя кузня отлично подходит для обучения новых претендентов. Я попросила у Архонта разрешения остаться в Горниле, и она согласилась! Если мне уготована такая вечность, то я буду счастлива еще многие тысячелетия.', 38134),
(206196, 'ruRU', 'Да благословит тебя ночь.', 'Да благословит тебя ночь.', 38134),
(206195, 'ruRU', '', 'Когда-нибудь я стану перерожденной. А пока у меня есть моя кузня. Точнее будет, когда у меня появится анима.', 38134),
(206194, 'ruRU', '', 'Наверное, немного странно рассказывать другому претенденту о том, как проходит твое обучение. Все очень просто. Меня назначили сюда главной, потому что я пробыла здесь дольше всех.\n\n<Автоматург Софона весело усмехается.>', 38134),
(206193, 'ruRU', '', 'Я очень горжусь своей кузней. Ведь я не просто так стала единственной претенденткой-автоматургом!', 38134),
(206192, 'ruRU', '', 'Господин Миканикос сейчас очень занят. Сика должна убедиться, что у автоматурга есть все, что нужно.', 38134),
(206191, 'ruRU', '', '<Сика сверяется со своим списком.>\n\nАнима? Есть.\nЩипцы? Есть.\nМатериалы? Есть.\nАвтоматург? Есть.\nСика? Есть!\n\nУ нас все готово! У-ух! У-ух!', 38134),
(206190, 'ruRU', '', 'О, нет! У Сики проблемы?', 38134),
(206189, 'ruRU', '', 'Сика скучала по привратнице. \"Мнемида\" знает все!\n\nНу, почти все.', 38134),
(206188, 'ruRU', '', 'Господин Миканикос, верховный автоматург, попросил Сику присмотреть за центурионами. Анимы мало, и мы выбираем, кому она нужнее! А теперь ты здесь! И анима нужна привратнице!\n\nХорошо, что Сика заставила центурионов поделиться!', 38134),
(206186, 'ruRU', 'Клейя защитит меня на обратном пути. Сделай все, что в твоих силах, $n. Я верю в тебя.', '', 38134),
(206185, 'ruRU', 'О, нет! Какой ужас... Надеюсь, Клейя не пострадала!', '', 38134),
(206184, 'ruRU', 'Ужасно. Все эти претенденты... Возможно, Эридия права. Я должен остаться здесь.', '', 38134),
(206183, 'ruRU', 'Здесь что-то явно не так, $n...', '', 38134),
(206182, 'ruRU', 'Я рад, что десница Чистоты попросила нас держаться вместе. Кажется, ты уверенно себя чувствуешь. Я всегда поддержу тебя, если потребуется. Со мной все будет в порядке.\n\n<Пелагий тревожно оглядывается.>', '', 38134),
(206181, 'ruRU', 'Плохи дела. Просто так послушники свой храм не покидают.', '', 38134),
(206180, 'ruRU', 'Я знал, что у тебя получится, $n. Вместе мы переживем эти трудные времена!', '', 38134),
(206179, 'ruRU', 'Просто сделай все, что в твоих силах! \"Атанос\" предназначен для испытания даже самых способных претендентов.', '', 38134),
(206178, 'ruRU', '<Похоже, Пелагий полностью погружен в разговор с Клейей.>', '', 38134),
(206177, 'ruRU', 'Твоя жизнь была такой интересной! Не терпится узнать о тебе побольше. Но пока что тебе лучше послушаться автоматурга. Она знает больше меня о том, как вести себя здесь.', '', 38134),
(206176, 'ruRU', 'Мне очень любопытно узнать о твоих подвигах, $n. Они должны быть грандиозны – ведь они привели тебя сюда, и почти сразу после твоего прибытия тебе удалось помочь мне! Несомненно, твоя душа создана для нашего царства!', '', 38134),
(206175, 'ruRU', 'Все, что нам остается – это двигаться дальше.', '', 38134),
(206174, 'ruRU', '', 'Я как будто совершаю преступление. Это место такое же древнее, как сам Бастион.\n\nВ другой ситуации я считала бы, что мне повезло даже просто увидеть его.', 38134),
(206173, 'ruRU', '', 'Я никогда раньше не видела, чтобы \"Мнемида\" так себя вела. Мне от нее... не по себе.', 38134),
(206172, 'ruRU', '', 'Я защищу верховного автоматурга. А ты сделай все, что требуется, $n.', 38134),
(206171, 'ruRU', '', 'Раскольники захватили и это место... Неужели они подчинили себе весь Бастион? \n\nНадеюсь, Пелагий успел всех предупредить, а пока мы должны сосредоточиться на задании. Нужно узнать, кто стоит за этими нападениями!', 38134),
(206170, 'ruRU', '', 'Нужно узнать, почему раскольники решили напасть на нас. Узнать, кто за всем этим стоит!', 38134),
(206169, 'ruRU', '', 'Спасибо, что оберегаешь Пелагия. И я очень рада, что с тобой все в порядке!\n\nЯ не могу позволить, чтобы с ним что-то случилось. Я не могу пойти с тобой. Надеюсь, ты понимаешь.\n\nЯ знаю, что ты нам поможешь, и всей душой верю, что мы встретимся снова. \n\nСлужи верно, $n.', 38134),
(206168, 'ruRU', '', '<Кажется, Клейя полностью сосредоточена на ритуале.>', 38134),
(206167, 'ruRU', '', 'Похоже, у нас не будет времени отпраздновать твою победу. Мы нужны в другом месте.', 38134),
(206166, 'ruRU', '', 'Молодец, $n. Надеюсь, Архонт в конце концов заметит все, что ты для нас делаешь.', 38134),
(206165, 'ruRU', '', '<Клейя погружена в разговор с Пелагием.>', 38134),
(206164, 'ruRU', '', 'Когда-нибудь я перерожусь. А до тех пор я рада помогать тебе, Пелагию и остальным претендентам, как только смогу.', 38134),
(206163, 'ruRU', '', 'Никого не отправляют в Бастион по ошибке, $n. Помни об этом.\n\n<Клейя улыбается вам.>', 38134),
(206162, 'ruRU', '', 'Мне не терпится узнать, какие подвиги привели тебя к нашим вратам, $n.', 38134),
(206161, 'ruRU', '', 'Калисфена следит за всеми аспирантами, которые сейчас не живут в храме. Мне следовало предвидеть, что активация привратницы привлечет ее внимание. \n\nНе волнуйся. Я с этим разберусь!', 38134),
(206160, 'ruRU', '', 'Войди в воду, $n. Она исцелит тебя после путешествия через завесу.', 38134),
(206159, 'ruRU', '', 'Из этих центурионов можно извлечь немного анимы. Последние крохи, но нам должно пока хватить.', 38134),
(206157, 'ruRU', '', 'Тревога: вторжение. Текущая степень угрозы: серьезная. Ожидается немедленная мобилизация.', 38134),
(206156, 'ruRU', '', 'Тревога: вторжение. Текущая степень угрозы: умеренная.', 38134),
(206155, 'ruRU', '', 'Тревога: вторжение. Текущая степень угрозы: незначительная.', 38134),
(206154, 'ruRU', 'Я петь веселую песенку... Весе-е-елую песенку...', '', 38134),
(206153, 'ruRU', 'Мы скучали по маленькому другу...', '', 38134),
(206152, 'ruRU', 'Вкусняшки с тобой?', '', 38134),
(206151, 'ruRU', 'Лучший друг!\n\nУ меня вопрос. Как я смотрюсь на вечеринках?\n\nИногда клыкастые смеются, но я стараюсь.\n\nЛучший друг может мной гордиться!', '', 38134),
(206150, 'ruRU', '<Камнелоб трет глаз свободной рукой.>\n\nЛучший друг!!! Я так тебе рад!\n\nЯ хотеть идти за лучшим другом везде...\n\nУтроба тоже. Можно?', '', 38134),
(206149, 'ruRU', '', 'Я следовала графикам множество лет.\n\nНа планы свои я возлагала много надежд.\n\nНо все сгорело огнем отчаяния.\n\nНет душам покоя, если ждет их изгнание. Они тебя видят. А если и нет, то другие точно увидят.', 38134),
(206148, 'ruRU', '', 'Здравствуй, Свет, старинный друг. \n\nОпять сжигаешь все вокруг?\n\nРазвеет меня ветер, словно сор, когда принесешь ты мне мор и позор, за то, что посмела развязать спор тебе, Свету, в укор.\n\nАх, сколько боли несет твой священный взор.', 38134),
(206147, 'ruRU', '', 'Куда подевались все глашатаи?\nНебось опять от жара убегают,\nИх крики боли ни на миг не смолкают,\nВедь их разум на свету так и тает!\n\nЧто это было: стих или стихотворение?\nПовторю еще раз и познаю суть моего творения.\nВсе отдам, чтобы увидеть живое растение.\nНеужели я так провинилась, что достойна забвения?', 38134),
(206146, 'ruRU', '', 'Они сказали, что все готово! Готово! Или они сказали это еще несколько веков назад? Или что все будет готово только через несколько веков? Или несколько веков – это и есть они? \n\nВремя вам не йо-йо! \n\nСтоп! А мне уже пора знать, что такое йо-йо? Какое сейчас время?', 38134),
(206145, 'ruRU', 'Эй, пилигрим! Ответь-ка на один вопрос! В Ревендрете это мы с ребятами все поддерживаем в лучшем виде и строим новые громадины.\n\nА вот... кто этим занимается в Утробе? Тебе ведь приходилось там бывать? Не зря ж тебя прозвали пилигримом Утробы.\n\nЯ это все к чему: у этих ребят бывают перерывы?', '', 38134),
(206144, 'ruRU', '', 'Затем возвращайся сюда, и мы отпразднуем эту маленькую победу.', 38134),
(206143, 'ruRU', '', 'Я пошлю за талисманом своего смотрителя стойл. Отправляйся на место встречи и отдай его.', 38134),
(206142, 'ruRU', '', 'Чьи это шаги я слышу? Неужто это $p?', 38134),
(206141, 'ruRU', '', 'Видишь, Альфрид? Я говорила вам всем, что сегодня будет замечательный день. $p здесь.', 38134),
(206140, 'ruRU', '', 'А! Пилигрим Утробы. Чем обязана?', 38134),
(206139, 'ruRU', '', 'О, Альфрид, милый, не принесешь мне новый...', 38134),
(206138, 'ruRU', '', '<Графиня приветливо обнимает вас.>\n\nПрекрасно выглядишь, $p. Какие новости из Грехопада? Надеюсь, у всех все хорошо.\n\nПередай горячий привет Теотару и моему дорогому принцу, когда снова их встретишь.\n\nИли, если удастся снова посетить Пепельный двор в ближайшее время, я скажу им сама.\n\nНо хватит о них. Как дела у тебя? Я скучала по тебе с твоего последнего визита.', 38134),
(206137, 'ruRU', '', 'Я о тебе думала, дражайший друг.\n\nСкажи мне, чего ты сейчас хочешь больше всего?\n\nРечь не о мечтах всей твоей жизни или детских грезах. Мечты угасают, цели меняются.\n\nНо инстинкты всегда остаются, ожидая своего времени. Стоит расслабиться, и они сразу окажут на тебя влияние.\n\n<Графиня улыбается.>\n\nХорошо. Можешь мне не говорить. Держи эти мысли при себе. Только так ты сможешь защитить себя.', 38134),
(206136, 'ruRU', '', 'Неужели это сам пилигрим Утробы пожаловал ко мне в башню?\n\nСнова собираешься меня о чем-то просить?\n\n<Графиня улыбается.>\n\nНе проси об услугах первого встречного.\n\nМногие бедные души, ослепленные желанием, попадали таким образом в рабство к самым скверным хозяевам.', 38134),
(206133, 'ruRU', 'У меня глаза не слезятся, как у вас, смертных, но признаю, что расчувствовался, вспоминая день, когда мы впервые встретились.\n\nЕще одна скучная смена в Грехопаде, на которой нечего делать... И тут, откуда не возьмись – пилигрим Утробы, собирающийся устроить встряску всем Темным Землям!\n\nДаже не думал тогда, что мы когда-то станем лучшими друзьями. Такая неожижанная удача...', '', 38134),
(206132, 'ruRU', 'Эй, дружище.\n\nСкажи мне, когда потребуется с чем-то помочь. У меня уже есть несколько поручений от других, но я всегда найду время и для твоего.\n\nКстати, жду не дождусь следующего Пепельного двора. Теотар научил меня еще паре фраз, которые понравятся нарядным гостям.', '', 38134),
(206130, 'ruRU', 'Если тебе нужна помощь, у меня найдется время.', '', 38134),
(206129, 'ruRU', 'Как дела, дружище?\n\nБлагодаря тебе и всем друзьям, с которыми мы познакомились на Пепельном дворе, у нас с Дуборылом в последнее время очень насыщенная светская жизнь.\n\nНо у нас всегда найдется время для нашего самого лучшего друга – тебя, $p.\n\nТолько позови, и мы сразу придем.', '', 38134),
(206128, 'ruRU', '', 'Умышленные нападения на наше царство должны быть встречены яростным сопротивлением. Чти тех, что хранит наши тайны.', 38134),
(206127, 'ruRU', 'Эй, дружище. Тебе не приходилось слышать, как в последнее время какие-нибудь светские персонажи обсуждали меня и Дуборыла?\n\nМы не можем посетить каждый Двор, но я хочу сделать все, чтобы о нас не забывали, понимаешь?\n\nОчень здорово, что можно иногда отдохнуть от работы в грязи и прийти на прием.\n\nВы с принцем и правда заботитесь о землероях. Уважаю вас за это.', '', 38134),
(206126, 'ruRU', '', 'Все, что мы знаем о войне и боевых машинах, дает нам преимущество. Эти знания надо охранять от тех, кто хотел бы обратить их против Темных Земель.', 38134),
(206125, 'ruRU', 'Но мне нравится моя старая грязная шляпа...', '', 38134),
(206124, 'ruRU', 'Мы теперь важные шишки, Дуборыл. Придется одеваться, как все эти аристократишки.', '', 38134),
(206122, 'ruRU', 'До сих пор ума не приложу, Рендл... На кой тебе сдалась стильная шляпа?', '', 38134),
(206120, 'ruRU', 'У |3-1($p) дела, Дуборыл. Спасение Темных Земель и все такое. И вообще это мой лучший друг!', '', 38134),
(206119, 'ruRU', 'Эй, мой друг-пилигрим! Скучновато без тебя ездить.', '', 38134),
(206118, 'ruRU', 'Как это куда? Ты ничего не понимаешь в высокой моде.', '', 38134),
(206117, 'ruRU', 'Говорю тебе, Рендл. Куда тебе еще больше шапок...', '', 38134),
(206116, 'ruRU', 'Не пялься так. Невежливо!', '', 38134),
(206115, 'ruRU', 'Эй, глянь. Это ж пилигрим!', '', 38134),
(206114, 'ruRU', 'Этим негодяям совсем плевать на эти священные залы!', '', 38134),
(206113, 'ruRU', '$p! Какой приятный сюрприз!', '', 38134),
(206112, 'ruRU', 'Надеюсь, это ты, $p...', '', 38134),
(206111, 'ruRU', 'Стой, кто идет? Покажись!', '', 38134),
(206110, 'ruRU', 'А, мой дорогой друг. Я надеялся, что ты поскорее ко мне зайдешь.\n\nКажется, с нашей последней встречи при Пепельном дворе прошла целая вечность. Надеюсь, Грехопад процветает под твоим бдительным надзором?\n\nБлагодаря твоей помощи нам с Нераскаявшимися удалось спасти здесь много потерянных душ.\n\nТы отличный друг, $p. И, что еще важнее, ты действительно борешься за правое дело.\n\nИли очень хорошо притворяешься, что борешься!\n\n<Хранитель склепа Каззир смеется.>\n\nНо сомневаюсь, что ты здесь ради моих старых шуток.\n\nЧто привело тебя сегодня в мой уголок Ревендрета?', '', 38134),
(206109, 'ruRU', 'А, $p.\n\n<Хранитель склепа облегченно вздыхает и расслабляется.>\n\nПриятно видеть твое лицо среди толпы предателей и осквернителей.\n\nЯ продолжаю успешно работать здесь, но должен признать, что часто отвлекаюсь.\n\nМой разум часто возвращается к мыслям о следующем Пепельном дворе...\n\nЯ все еще удивлен, что вы с принцем Ренаталом считаете меня достойным посетить ваш двор, и не один раз, но это большая честь для меня.\n\nНо полно разглагольствовать. Чем тебе помочь сегодня?', '', 38134),
(206108, 'ruRU', 'Думаю, мне понравится!', '', 38134),
(206107, 'ruRU', 'Возможность попутешествовать, поработать, поучаствовать в приключениях...', '', 38134),
(206106, 'ruRU', 'Хорошо тут, правда? Тихо и спокойно. Никого, кроме трудолюбивых грибомантов вроде тебя с Кортом. И меня, конечно. Все трудимся на благо Арденвельда.$b$BСпасибо, что навещаешь. Многие уже давно про меня забыли, а с тобой всегда можно поговорить. Просто хотел сказать, как я это ценю.$b$BТолько не подумай, что я завожу любимчиков! Будешь работать, как все остальные!', '', 38134),
(206105, 'ruRU', '', 'Прием вентиров? Как... любопытно.', 38134),
(206104, 'ruRU', '', 'По правде говоря, я рада, что полемарх послал тебя ко мне.', 38134),
(206103, 'ruRU', 'Я уже много веков тут, внизу. Я помню первые дни Арденвельда – никого, кроме нас с королевой. Мы тогда были ужасно заняты. Она тоже такая труженица...$b$bНе то что нынешние бездельники! Подай им, мол, кольца. А у колец кто-нибудь спросил? Нет, я точно пожалуюсь Королеве, пусть их приструнит!', '', 38134),
(206102, 'ruRU', 'Добро пожаловать в Кольцо, юное создание.$b$BСюда всех подряд не пускают, знаешь ли. Мой дом, где пересекаются все кольца, открыт только для самых верных грибомантов.$B$BТо есть для тебя! Ты теперь тоже мой грибомант! Будешь так же усердно работать – добьешься многого, помяни мое слово.', '', 38134),
(206101, 'ruRU', 'Может, хоть похолодало немного...', '', 38134),
(206100, 'ruRU', 'Что в Пепельном Пределе, все так же ярко?', '', 38134),
(206099, 'ruRU', '', 'На Пепельном дворе, я хочу сказать.', 38134),
(206098, 'ruRU', '', 'Очень хорошо. Жди меня в гости.', 38134),
(206096, 'ruRU', '', 'За тобой никто не следил?', 38134),
(206095, 'ruRU', '', 'Очень надеюсь, что ты расскажешь мне что-нибудь интересное, пилигрим.', 38134),
(206091, 'ruRU', 'Ого, \"Погребок в корнях\"! Хорошо, что он еще не закрылся. Знаешь что, ты трудишься как пчелка. Иди устрой себе отдых, а потом возвращайся!', '', 38134),
(206088, 'ruRU', 'Навыки не совпадают со стремлениями, а, пилигрим?', 'Навыки не совпадают со стремлениями, а, пилигрим?', 38134),
(206085, 'ruRU', 'Восхитительная демонстрация воинского искусства, пилигрим Утробы! Ты по-прежнему жаждешь крови?', '', 38134),
(206077, 'ruRU', 'Болвар страдает. Темные силы выступают против нас.', '', 38134),
(206073, 'ruRU', 'После стольких изнурительных битв и войн за жизнь весьма... интересно... так подробно узнать, что ждет, когда она заканчивается.', '', 38134),
(206070, 'ruRU', '', 'Центурионы. Эти оживленные анимой механические существа выполняют в Бастионе самые разные функции.\n\nОни помогают нашим претендентам тренироваться, защищают наши земли, а если понадобится, берутся за простые поручения. А некоторые, например такие \"Мнемиды\" как я, помогают работе архивов, храня различные знания.', 38134),
(206069, 'ruRU', '', '<\"Мнемида\"-привратница издает серию звенящих звуков.>\n\nА... совы. В вашем мире – это ночные пернатые существа, не так ли? \n\n<\"Мнемида\" указывает на Сику.>\n\nЭто распорядитель. Они появляются из анимы Бастиона и помогают кириям с повседневными делами. Больше всего им нравится быть полезными.', 38134),
(206060, 'ruRU', 'Сколько на тебе всего надето. Не устаешь бегать?', 'Сколько на тебе всего надето. Не устаешь бегать?', 38134),
(206055, 'ruRU', 'Можешь подписать мне свиток?!', 'Можешь подписать мне свиток?!', 38134),
(206054, 'ruRU', 'Я твой самый большой поклонник, $n!', 'Я твоя самая большая поклонница, $n!', 38134),
(206052, 'ruRU', 'Я думал, у тебя есть дела поинтереснее, чем разговоры со мной. Мм-м?', '', 38134),
(206050, 'ruRU', 'Что исчезнет, если назвать это по имени?', '', 38134),
(206049, 'ruRU', '<Лорд Ренард широко улыбается, обнажив множество зубов.>', '', 38134),
(206048, 'ruRU', 'Хм... Да. Ты, пожалуй, сгодишься.', '', 38134),
(206047, 'ruRU', 'Так-так-так! Кого это я вижу перед собой? $c? Как интересно...', '', 38134),
(206042, 'ruRU', '', 'Это ты, пилигрим? Да, Балмедар уже сообщил мне о вашем маленьком заговоре.\n\nВ любом случае я очень ценю твой вклад во благо дома Ритуалов. Я не выдам своим подданным такого ценного союзника.\n\nЧувствуй себя как дома, \"аколит\".', 38134),
(206041, 'ruRU', '', 'Твой статус не позволяет напрямую обращаться к маркграфине.\n\nЛучше иди своей дорогой.', 38134),
(206039, 'ruRU', 'У тебя очень интересное оружие, аколит.\n\nНадеюсь, ты не думаешь сменить профессию?', '', 38134),
(206037, 'ruRU', 'Надеюсь, мы сможем наконец-то обрести покой.', 'Надеюсь, мы сможем наконец-то обрести покой.', 38134),
(206036, 'ruRU', 'Я постоянно думаю обо всех, кто погиб. Почему этого нельзя было избежать?', 'Я постоянно думаю обо всех, кто погиб. Почему этого нельзя было избежать?', 38134),
(206035, 'ruRU', 'Наш новый дом... такой красивый.', 'Наш новый дом... такой красивый.', 38134),
(206034, 'ruRU', 'Так хорошо вернуться в лес. Спасибо.', 'Так хорошо вернуться в лес. Спасибо.', 38134),
(206033, 'ruRU', 'Здорово снова взлететь!', '', 38134),
(206016, 'ruRU', '', 'Я никогда не думала, что буду участвовать в таком серьезном конфликте. Учения госпожи хорошо меня подготовили, но иногда я все равно чувствую, что еле справляюсь.\n\nЗнай, что, делясь с тобой переживаниями, я сохраняю уверенность. Баронесса осудила бы мою слабость.', 38134),
(206014, 'ruRU', 'Тебе удалось доказать свою пользу, $n. \n\nЕсли когда-нибудь ты вернешься в Малдраксус обычным способом, тебе будут рады в доме Ритуалов.', '', 38134),
(206013, 'ruRU', '', 'Когда я в последний раз посещала это место, Примас еще был с нами.\n\nВернувшись сюда, я уже не смогу оставить его кузню без присмотра. Он бы мне такого не простил.', 38134),
(206008, 'ruRU', '', 'Вдобавок к твоей награде я разрешаю тебе воспользоваться моим манекеном. \n\nС его помощью ты сможешь еще больше изменить свой наряд, вот только аксессуары для этого тебе придется добыть самостоятельно.', 38134),
(206007, 'ruRU', '', 'Что тебе нужно, аколит?', 38134),
(205988, 'ruRU', 'Храни все в тайне, $n.\n\nНикто не должен знать о нашей... договоренности.', '', 38134),
(205975, 'ruRU', 'Ну, я ухаживаю за этими грибами по просьбе Чесночника. Эта засуха сильно повредила его бедные корни.', 'Ну, я ухаживаю за этими грибами по просьбе Чесночника. Засуха сильно повредила его бедные корни.', 38134),
(205973, 'ruRU', 'Привет! \n\nПрости, нет времени на болтовню. Эти грибы сами о себе не позаботятся.\n\n<Он усмехается.>\n\nЧем могу помочь?', 'Привет! \n\nПрости, нет времени на болтовню. Эти грибы сами о себе не позаботятся.\n\n<Закки усмехается.>\n\nЧем могу помочь?', 38134),
(205972, 'ruRU', 'Извини, пилигрим Утробы, но ты нежеланный гость в обители моего ковенанта.', '', 38134),
(205971, 'ruRU', '', 'Мы пришли, чтобы восстановить справедливость. Добиться правосудия. Ответить на оскорбление, нанесенное самому Свету.\n\nНас было много, без числа. Сердце каждого горело праведным гневом.\n\nМногие пали в бою. Но мы призвали огонь, который пожрал врагов. В здешнем небосводе до сих пор зияет палящая рана, символ нашей мести.\n\nИзраненная, я осталась лежать на поле боя. Пока незримые существа не нашли меня. Они оставили меня в живых, чтобы я стала оружием для их господина.\n\nНо и это меня не сломило. Я знала, Свет уготовил для меня иную участь. Для меня и для тебя, смертная душа.', 38134),
(205969, 'ruRU', '', 'Дом зовет меня, смертная душа. Но я уже никогда не почувствую его тепло.\n\nМои собратья не смогут увидеть во мне меня прежнюю. Они увидят уродливое чудовище. Изломленное. Оскверненное темным Владыкой этого царства.\n\nТеперь у меня лишь один путь. Я буду напоминать всем о существовании Света. Я добьюсь правосудия. Смерть никогда больше не зайдет за грань дозволенного.\n\nЯ останусь здесь и буду выполнять мой долг. Пока не иссякну до последней искры.', 38134),
(205968, 'ruRU', 'Пройдет немало времени. Времени, которое твой народ еще не научился по-настоящему измерять. \n\nНо время идет, и я вижу, что мой старый друг, мой доверенный советник и мой великий повелитель выходят из этого храма знаний, который вновь выполняет свое предназначение.', '', 38134),
(205965, 'ruRU', '', 'Я хочу в подробностях вспомнить тот день, роковой день в нашей долгой истории.', 38134),
(205964, 'ruRU', '', 'В том дне не было ничего особенного. Претенденты готовились к испытаниям. Распорядители ухаживали за ними. Перерожденные прилетали из Орибоса и улетали обратно, как обычно.', 38134),
(205963, 'ruRU', '', 'Я помню как сейчас... постоянный поток душ из Орибоса в наше царство просто... прекратился. Только что мы приветствовали новых претендентов, и вот уже не осталось никого.', 38134),
(205962, 'ruRU', '', 'Сначала нам казалось, что это лишь временный перерыв. В конце концов, немногие души достойны попасть в Бастион! Но потом прошел день. Потом два. Потом десять.', 38134),
(205961, 'ruRU', '', 'Мы угасаем медленно. Со сдержанной, но беспочвенной надеждой в сердце. Такое случается впервые. Но все-таки... я не могу не задаваться вопросом... что нам останется, если некого будет обучать?', 38134),
(205960, 'ruRU', 'Как и все верховные автоматурги до меня, я кое-что изобрел!', '', 38134),
(205959, 'ruRU', 'У нас много центурионов. У всех свое назначение. Есть большие и сильные. Есть быстрые, умеющие летать. И стремительные, на которых можно ездить.', '', 38134),
(205958, 'ruRU', 'Я подумал: что центурионы не могут? Они говорят, но не думают. У них нет своих знаний. Но почему?', '', 38134),
(205957, 'ruRU', 'Я верховный автоматург, и я сделал такого центуриона. Наделил претора ядром разума. Она слишком часто все повторяет, но зато умеет отвечать на вопросы! Очень... простые вопросы.', '', 38134),
(205956, 'ruRU', 'Столько новых возможностей! Мы сможем сохранить столько знаний! Я назову ее... \"Мнемида\". Да. Гениально!', '', 38134),
(205955, 'ruRU', '', 'Недавно я встретила очень интересного претендента. Ее зовут Лисония. Мы сблизились, разделив нашу общую любовь к музыке.', 38134),
(205954, 'ruRU', '', 'Она играла на арфе, а я – на флейте. Прежде чем расстаться, мы часами исполняли друг другу серенады. Мы договорились, что потренируемся вместе, как только выпадет такая возможность.', 38134),
(205953, 'ruRU', '', 'Встретившись на тренировочной арене, мы будто пустились в пляс. Двигались необычайно слаженно. Я почти забыла, что мы были на тренировке!', 38134),
(205952, 'ruRU', '', 'После поединка мы нашли тихое местечко с видом на Элизийскую цитадель и поделились друг с другом мечтами о будущем. Она надеется присоединиться к Девии и служить в храме Верности, а я вижу себя в храме Чистоты.', 38134),
(205951, 'ruRU', '', 'Мне кажется, будто я знала ее целую вечность. Как ты думаешь... она согласится связать наши души?', 38134),
(205950, 'ruRU', 'Ты такой свирепый и храбрый, Ухач. Я тобой горжусь.', '', 38134),
(205949, 'ruRU', 'Да? Как здорово! Немного отдохнуть – это как раз то, что мне надо. А потом снова в бой, защищать Арденвельд.', '', 38134),
(205948, 'ruRU', 'Ухач! Ты слышишь? Нас позвали на прием!', '', 38134),
(205946, 'ruRU', 'Два...', 'Два...', 38134),
(205945, 'ruRU', 'Эти испытания должны повторяться до тех пор, пока ты их не освоишь.', 'Эти испытания должны повторяться до тех пор, пока ты их не освоишь.', 38134),
(205944, 'ruRU', 'Мы им помешаем! Дом Синфэнг поддерживает дом Айрмур!', 'Мы им помешаем! Дом Синфэнг поддерживает дом Айрмур!', 38134),
(205942, 'ruRU', 'Вор! Я еще не закончил!', 'Вор! Я еще не закончила!', 38134),
(205941, 'ruRU', 'Один...', 'Один...', 38134),
(205940, 'ruRU', 'Я сосчитаю до пяти.', 'Я сосчитаю до пяти.', 38134),
(205939, 'ruRU', 'Что ты делаешь? Сейчас же слезай с Бальтазара!', 'Что ты делаешь? Сейчас же слезай с Бальтазара!', 38134),
(205938, 'ruRU', 'Кевин встретится с тобой у наших новых друзей после того, как срежет путь по своей тропе. Просто отведи их к нему, и он сделает остальное!', '', 38134),
(205919, 'ruRU', '', 'Хотя уверена, что на Теотара такой глупый трюк подействует. Нда...', 38134),
(205918, 'ruRU', '', 'Нет. Слишком броско.', 38134),
(205913, 'ruRU', 'Чем будешь сегодня платить, $p?\n\nМонетами или смертью?', '', 38134),
(205876, 'ruRU', 'Подойди, заблудшая душа. У меня к тебе серьезный разговор.', 'Подойди, заблудшая душа. У меня к тебе серьезный разговор.', 38134),
(205862, 'ruRU', 'Здесь ты можешь воспользоваться услугами, связанными с твоим материальным имуществом. Нам пока не слишком знакомы подобные явления, поэтому брокеры любезно согласились предоставить тебе эти услуги.', '', 38134),
(205856, 'ruRU', 'У-у-у, не откажусь! Что-нибудь послаще и понежнее. Доводилось пить анимай-тай? Мак смешивает его лучш-ше всех.', '', 38134),
(205855, 'ruRU', 'Я попал в такую передрягу, что только пить и остается. Найди мне что-нибудь покрепче. Быть может, \"Мохнатый мотылек\" ударит в голову и поможет вспомнить.', '', 38134),
(205854, 'ruRU', 'Да ну? Угостишь меня выпивкой? Теперь ты мой лучший друг. Так и знай.$b$B Я бы выпил \"Спригган сауэр\". Он очень кислый! Губы сворачиваются так, что щеки ломит! Ха!', '', 38134),
(205853, 'ruRU', 'Нет, спасибо. Я и так много выпил. Мне надо <ик!> протрезветь!', '', 38134),
(205850, 'ruRU', 'Приветик... <ик!> Не поможешь мне?$b$BМеня зовут Мерцающий Дерн, я мастер иллюзи... ик! По крайней мере, мне так кажется. Да, я конечн... изрядно подвыпил... и... <ик!>$b$bТеперь я вижу кучу копий себя и не пойму, какой я – настоящий! Значит, вот он какой, <ик!> душевный кризис... Ну и ну...', '', 38134),
(205849, 'ruRU', '<Великий мастер Воул смеривает вас взглядом.>\n\nПодходишь.\n\n<Великий мастер Воул кричит окружающим.>\n\nСвежее мясо для Театра Боли!', '', 38134),
(205848, 'ruRU', 'Скажу без ложной скромности, что выступление было великолепным. Я уже получил несколько восторженных отзывов.$B$BБлагодарю тебя, друг. Нечасто доводится встретить такого же одаренного музыканта.', '', 38134),
(205846, 'ruRU', 'Кристальной анимой, о-о-о!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205845, 'ruRU', 'Заплатите герою кристальной анимой,', '', 38134),
(205844, 'ruRU', 'Защиту всех нас!', '', 38134),
(205843, 'ruRU', 'Встречайте его,', '', 38134),
(205842, 'ruRU', 'Герой на страже зла.', '', 38134),
(205841, 'ruRU', 'История ясна,', '', 38134),
(205840, 'ruRU', 'По роще прозвенел.', '', 38134),
(205839, 'ruRU', 'Один удар меча...', '', 38134),
(205838, 'ruRU', 'Рванулся на нас зверь!', '', 38134),
(205837, 'ruRU', 'Как роковая тень,', '', 38134),
(205836, 'ruRU', 'Три пасти разверз!', '', 38134),
(205835, 'ruRU', 'Где дикий наш монстр', '', 38134),
(205834, 'ruRU', 'В трясину топи влезть...', '', 38134),
(205833, 'ruRU', 'Пройти дремучий лес...', '', 38134),
(205832, 'ruRU', 'Отлично, клянусь королевой! Тебе правда не приходилось сочинять песни в своем родном мире?$b$B<Нарцисс замолкает на несколько секунд, чтобы собраться с духом.>Ладно, думаю, я готов. Итак, послушаем, что получилось?', '', 38134),
(205831, 'ruRU', 'Что такое человечество? Это ты? Ты – человечество? Ведь эта песня вообще-то не для тебя. Она должна вдохновить ночной народец всего Арденвельда – а особенно тех, кто сейчас здесь. Сказать им, что мы герои, достойные похвалы, прославления и щедрого вознаграждения!', '', 38134),
(205830, 'ruRU', 'Хорошо запоминается, но немного предсказуемо. К тому же у нас в Арденвельде на самом деле не так много долин. Интересный факт!', '', 38134),
(205829, 'ruRU', 'Мне нравится! Есть призыв к действию, и к тому же это напоминает всем, что он обязуется сохранять мир. Давай попробуем. А теперь, грандиозный финал!$B$BБрось аниму своему охотнику, о... о...', '', 38134),
(205828, 'ruRU', 'Хотя звучит это очень забавно, я думаю, что все должны держать свои руки при себе и там, где положено.', '', 38134),
(205826, 'ruRU', 'Пожелания – это, конечно, здорово, но я бы хотел чего-то более... весомого. Быть героем совсем непросто, знаешь ли. А за тяжелый труд полагается награда, понимаешь?', '', 38134),
(205825, 'ruRU', 'Я столько лет помогал юным многообещающим эльфам найти свой путь.\n\nА теперь настало время и мне отправиться в путешествие. Интересно, так ли они себя чувствовали, впервые открывая для себя новую рощу?', '', 38134),
(205822, 'ruRU', '', 'Я признательна тебе за спасение из того страшного места. Спасибо за это.\n\nНо я не могу забыть, как страдал мой народ по вине Орды.\n\nПравосудие должно свершиться несмотря ни на что.', 38134),
(205821, 'ruRU', '', 'Наш народ понес невообразимые потери во время войны. Больше любого другого.\n\nЭто нельзя просто так оставить.\n\nЯ надеюсь, что ты поддержишь нас, когда придет время отмщения.', 38134),
(205820, 'ruRU', 'Пожирающие разломы – трещины между нашей реальностью и миром пожирателей. Они не знают стыда или сомнений и готовы уничтожить все, кроме самого Азаруукса.', '', 38134),
(205818, 'ruRU', '\"Атанос\" больше не сдерживается. Приготовься уворачиваться от его могучих взмахов... и держись подальше от края арены.', '', 38134),
(205817, 'ruRU', 'О, хорошее слово. Долг скромного барда – образовывать публику, и начать вполне можно с лексики!$b$BСтоп, откуда ты знаешь, что у него три пасти?$b$BВ общем, вот следующий куплет:\nИстория ясна,\nГерой на страже зла,\nВстречайте его\n...хм, встречайте его, а дальше?..', '', 38134),
(205816, 'ruRU', 'Получается не очень-то по-геройски. Нарцисс Отважный заколол спящего зверя, пока тот решил вздремнуть! Нет-нет, надо не так. Хм...', '', 38134),
(205815, 'ruRU', 'Молодец, держишь рифму, но мы все-таки сражались с боггартом. Я в своих балладах частенько приукрашиваю действительность, но отсюда уже до правды как до Луны. Понимаешь, о чем я?$b$B<Нарцисс нарочито театрально вам подмигивает.>', '', 38134),
(205814, 'ruRU', 'Отлично! Первый куплет такой:\nПройти дремучий лес,\nВ трясину топи влезть,\nГде дикий монстр наш\n...ничего в голову не идет. Есть идеи?', '', 38134),
(205813, 'ruRU', 'Постарайся не выпускать Коринфа из виду. Он будет исчезать, чтобы поглощать аниму и восстанавливать силы.', '', 38134),
(205812, 'ruRU', 'Благодарю за мое спасение.\n\nЕсли собираешься вернуться в Утробу, можешь поискать там кое-кого?\n\nЕе зовут Деларин.', '', 38134),
(205811, 'ruRU', 'Сила Тран\'тиок скрыта в ее филактериях. Ее невозможно ранить, пока все они остаются целы, но она будет получать мощную способность при уничтожении каждой из них.', '', 38134),
(205810, 'ruRU', 'Цветы друстов защитят тебя от атак этого существа, но только на короткое время.', '', 38134),
(205809, 'ruRU', 'О, опять ты. Привет! Мне кажется или мы уже встречались?...$B$BХотя без разницы, ты как раз вовремя. Боюсь, у меня наступил небольшой творческий кризис. Не поможешь мне подобрать некоторые слова? Я пишу песню о моем благородном друге и нашей недавней битве с жутким чудищем.', '', 38134),
(205808, 'ruRU', '', '<Мак заливает жидкость в шейкер и начинает рьяно трясти.>\nУбьет? С чего бы? Как будто ты уже не того. Хе-хе...$b$BА, ты это серьезно. Да нет, сок гормов ничего тебе не сделает. Только даст сладкое мягкое послевкусие. \n<Мак протягивает вам стакан.>\nПрошу!', 38134),
(205807, 'ruRU', '', '<Мак осматривает вас с ног до головы.>$b$bДа, у меня есть кое-что как раз для тебя. Но нужен пищеварительный сок гормов, а он у меня весь вышел. Не возьмешь немного у Ляпсуса? Не бойся за него, ему только понравится.', 38134),
(205806, 'ruRU', '', 'Элуна нас не забыла. Она послала тебя спасти нас.\n\nМожет быть, мы еще сыграем свою роль и послужим судьбе.\n\nМы должны набраться терпения.', 38134),
(205805, 'ruRU', '', 'Некролорды забыли про свое предназначение в Темных Землях и сами идут к погибели. Если ты снова встанешь на нашу сторону, мы вместе выкуем из разрозненных обломков армию, которая бросит вызов самой судьбе.', 38134),
(205804, 'ruRU', 'Путь искупления для обитателей Ревендрета будет долгим, но Темный принц Грехопада готов его пройти. И благодаря твоей помощи мы уже сделали первые шаги. Теперь необходимо захватить власть в Ревендрете, победив Владыку и верных ему вентиров.', '', 38134),
(205803, 'ruRU', '', 'Все началось из-за ненависти.\n\nНо я надеюсь, что все окончится правосудием.', 38134),
(205801, 'ruRU', '', 'Когда я закрыла глаза, то подумала, что следующее увиденное мной лицо будет принадлежать Элуне.\n\nСпасибо за то, что не бросаешь нас.', 38134),
(205800, 'ruRU', '', 'Ты уже видишь, что рощи Арденвельда гибнут. Наша королева в отчаянии наблюдает за тем, как одно за другим рушатся небесные древа. Королева и ее Двор Ночи знают о твоей силе. Помоги нам восстановить баланс в Арденвельде.', 38134),
(205799, 'ruRU', 'Пикси всегда рассчитывают на поддержку друзей. Если их разлучить с ними, они увядают и становятся уязвимыми.', '', 38134),
(205798, 'ruRU', 'Наши благородные кирии испытывают на себе ужасные последствия предательства, которое тебе довелось раскрыть в Бастионе. Раскольники грозят полностью разрушить наше царство. Если ты поможешь восстановить порядок в Бастионе и спасти Темные Земли, мы будем тебе очень благодарны.', '', 38134),
(205797, 'ruRU', 'В этом испытании главное – скорость! И следи за смерчем Альдерина. Безопаснее всего в эпицентре бури.', '', 38134),
(205796, 'ruRU', 'Ползуны опасны. Старайся не подходить близко и избегай наступать на мерзкие отходы!', '', 38134),
(205795, 'ruRU', 'Берегись копий. Три попадания, и ты замрешь на месте, так что очень пригодятся любые предметы или способности, которые тебя освободят!', '', 38134),
(205794, 'ruRU', 'Так и сияют. В такой шляпе можешь быть принцем всех землероев.', '', 38134),
(205793, 'ruRU', 'Шляпа хоть куда. Гляди, какие камни.', '', 38134),
(205791, 'ruRU', 'Доводилось ли тебе видеть такой великолепный лес! Взгляни на эти деревья!\n\nСпасибо за спасение из того жуткого места, чтобы я мог увидеть его своими глазами.', '', 38134),
(205790, 'ruRU', 'Как я тебе? Правда, роскошно?', '', 38134),
(205789, 'ruRU', 'И ты в этом собрался на прием? Ладно. Ну тебя. Делай, что хочешь, Рендл. Главное, на прием попадем.', '', 38134),
(205788, 'ruRU', 'Ха! Смотри, какая шляпа. Красивая, розовая, а сверху – острая.', '', 38134),
(205785, 'ruRU', '', 'Посмотрите, кто у нас тут! \n\nПожалуй, помогу тебе немного. Вот свиток, который перенесет тебя на основной остров. Мы все-таки далековато забрались.', 38134),
(205783, 'ruRU', '', 'Ага! Ты, наверное, 3-6($r)? Давно я вас не видала. \n\nПойдем со мной прятаться от всей этой жуткой некромантии. Умеет ли твой народ дышать под водой?\n\nМы собираемся переждать суматоху и опять пуститься в путь. По моей прикидке, еще лет пять-десять. Для меня всего ничего.\n\nА вот для тебя не знаю...', 38134),
(205781, 'ruRU', '', 'Конечно я его знаю. Гвинкейрв собственной персоной! И как можно забыть его верного спутника Нарцисса?$b$bСлушай, только не нужно нарываться на неприятности. В прошлый раз, когда сюда пришло несколько воинственно настроенных спригганов, они устроили с ними драку. Я потом, кажется, сто лет тут прибиралась. Как могут внутренности оказаться на потолке?! Откуда взялось столько крови?!', 38134),
(205779, 'ruRU', '', 'Хорошо, только не ломай их. Чесночник всегда жалуется, что арфы об него ломаются. Только не спрашивай, как это получается.', 38134),
(205778, 'ruRU', '', 'А, Мерцающий Дерн? Неплохой парень. Он обожает иллюзии, но иногда напивается так, что не может вспомнить, где иллюзия, а где он сам.$b$bОднако настоящий Дерн мастер выпить. Его любимый напиток – анимай-тай. Он утверждает, что это самый приятный и сладкий коктейль во всем Арденвельде. $b$bИ зная, сколько он пьет, я горжусь этим его комплиментом. Хочешь попробовать?', 38134),
(205777, 'ruRU', 'Да, ты же замаскирован. Никто не поймет, что это ты, Рендл.', '', 38134),
(205776, 'ruRU', 'О-о-о! Да! Взгляни на мою шляпу, Дуборыл. Просто... огонь.', '', 38134),
(205771, 'ruRU', 'Мы так долго к этому шли.', '', 38134),
(205757, 'ruRU', 'Распорядители приходят сюда снять стресс... вроде как выпустить пар.', '', 38134),
(205756, 'ruRU', 'Всегда следуй кулинарным советам распорядителей. Они мастера совиной кухни.', '', 38134),
(205750, 'ruRU', 'Знаешь, почему у распорядителей такие сильные мышцы? Потому что они часто помогают переносить снаряжение кирий.', '', 38134),
(205748, 'ruRU', '', 'Распорядители проводят всю жизнь, ухаживая не только за кириями, но и за всем Бастионом.\n\nВсе из них до единого заслуживают вознаграждения за труды. Мы надеемся, что здесь они находят немного покоя и уединения.', 38134),
(205746, 'ruRU', 'Скажи, когда начинать, пилигрим.', '', 38134),
(205743, 'ruRU', 'Освободить тебе место?', 'Освободить тебе место?', 38134),
(205742, 'ruRU', 'Голова закружилась. Наверное, слишком напрягся...', 'Голова закружилась. Наверное, слишком напряглась...', 38134),
(205741, 'ruRU', 'Отдыхать полезно. Пусть мышцы восстанавливаются!', 'Отдыхать полезно. Пусть мышцы восстанавливаются!', 38134),
(205738, 'ruRU', 'Нужно что-то заколдовать?', '', 38134),
(205737, 'ruRU', 'У тебя чудесная кожа. Можно я заберу ее, когда она тебе надоест?', '', 38134),
(205734, 'ruRU', 'Нужно залечить раны?', '', 38134),
(205733, 'ruRU', 'Я не переставал надеяться.', 'Я не переставала надеяться.', 38134),
(205732, 'ruRU', 'Эти мучения когда-нибудь закончатся?', 'Эти мучения когда-нибудь закончатся?', 38134),
(205731, 'ruRU', 'Я молился, чтобы богиня нас услышала...', 'Я молилась, чтобы богиня нас услышала...', 38134),
(205730, 'ruRU', 'Все позади?', 'Все позади?', 38134),
(205726, 'ruRU', '', 'Никто и никогда не сломит меня.', 38134),
(205725, 'ruRU', 'Я скучаю по Тенистой долине.', '', 38134),
(205722, 'ruRU', 'Где Деларин?', '', 38134),
(205720, 'ruRU', 'Все сгорело... Даже юные саженцы...', '', 38134),
(205717, 'ruRU', 'Значит, ты у нас почетный грибомант? Похоже, что-то в тебе очень понравилось Чесночнику. Добро пожаловать в команду!$B$BПравда, до твоего прибытия этим занимался один я. Но я рад, что ты теперь со мной! Раз ты теперь грибомант, для тебя откроются пути Чесночника. Ты также сможешь получить различные положительные эффекты и экипировку, доступную только для грибомантов. Помогая мне и Чесночнику, ты будешь зарабатывать жетоны, которые сможешь обменять у меня на награды.', '', 38134),
(205716, 'ruRU', 'Раньше Кольцо было неким перекрестком, центральной станцией, с которой можно было отправиться практически в любую точку Арденвельда. $b$BНо прошло время, началась засуха, и даже некоторые пути Чесночника закрылись. Быть может, их еще удастся спасти, если найдется достаточно анимы...$b$bА пока мы, грибоманты, продолжим заботиться о Чесночнике. С нашей поддержкой корни Арденвельда станут еще крепче, чем прежде. Это пойдет на благо всему лесу.', '', 38134),
(205715, 'ruRU', 'Добро пожаловать в Кольцо, друг. Говорят, что здесь находится центр Арденвельда, и что именно в этом месте появился Чесночник. Но лишь он один знает наверняка.', '', 38134),
(205714, 'ruRU', '', 'Тьма вокруг...\n\nЯ ведь больше ничем не могла помочь им, правда?', 38134),
(205711, 'ruRU', 'Мы спасли зандаларскую принцессу Таланджи из тюрьмы Штормграда. \n\nЕе корабль уже на пути к Зандалару, но псы Альянса отправили погоню.\n\nЕсли мы рассчитываем победить в этой войне, нам нужен Зандаларский флот. Нельзя допустить, чтобы Альянс снова взял принцессу в плен.\n\nМы отправимся к ней по воздуху, предложим помощь и убедим зандаларов присоединиться к Орде.\n\nИдем.', '', 38134),
(205709, 'ruRU', '', 'Все охватил огонь... и затем...\n\nЯ начала петь.\n\nО богиня... что происходит?', 38134),
(205704, 'ruRU', 'Вот это я понимаю, жизнь. Я бы мог к такому привыкнуть.', '', 38134),
(205699, 'ruRU', 'Отдай свеча! Нет!', '', 38134),
(205698, 'ruRU', 'Как жаль, что всех врагов мы уже поубивали.\n\nЕсли они только подумают вернуться, я буду готов.', '', 38134),
(205696, 'ruRU', 'Ха! Глупый землерой. Я один в целом замке!', '', 38134),
(205691, 'ruRU', 'Мы разозлим Грязешмяка! Так, что он погонится за нами и треснется прямо в камни!\n\nУ него голова потом весь день болеть будет...\n\n<Глиноптах хихикает как безумный.>', '', 38134),
(205690, 'ruRU', 'Грязешмяк меня по пути чуть не раздавил!\n\nВот же болван.\n\n<Глаза Глиноптаха злорадно загораются.>\n\nРазвлечемся?', '', 38134),
(205687, 'ruRU', 'Кто-нибудь голоден?', '', 38134),
(205674, 'ruRU', 'Мы оплакиваем все, чего лишились – и то, что потеряли, и то, что уже никогда не сбудется. Ты связываешь ожидания с тем, что имеешь. Оставь стремления и обрети покой.', '', 38134),
(205673, 'ruRU', 'Освободив себя от былых ожиданий, избавившись от страха и боли потери, мы сможем принять настоящее таким, какое оно есть.', '', 38134),
(205672, 'ruRU', 'Грустно видеть, как исчезает то, что когда-то было. Мы помним связанную с этим радость, наши первые впечатления и чувствуем к этому привязанность.', '', 38134),
(205671, 'ruRU', 'Мы испытываем страх, когда не можем вновь получить то, что у нас когда-то было, или испытать то, что когда-то могли испытывать.', '', 38134),
(205639, 'ruRU', '', 'Привет и добро пожаловать в \"Погребок в корнях\"!$b$BЧего желаешь?', 38134),
(205627, 'ruRU', '', 'Мы будем готовы защищать Пепельный двор. Увидимся на приеме, $n.', 38134),
(205591, 'ruRU', 'Находясь на краю существования, нетрудно предаться унынию от пережитых потерь. Хм... Думаю, мне стоит поразмыслить об истинном значении потери. Не найдешь мое средоточие? Оно должно быть где-то здесь...', '', 38134),
(205590, 'ruRU', 'Это самое место для тех, кто ищет тишины и покоя! Может, той душе нужно чем-то помочь.', '', 38134),
(205587, 'ruRU', '', 'Давно не видела, чтобы кто-то так ужасно сражался. Это никуда не годится, новобранец. Иди потренируйся.', 38134),
(205586, 'ruRU', '', 'Очень неплохо! Испытание пройдено, новобранец.', 38134),
(205579, 'ruRU', 'Кажется, ты хорошо умеешь... мастерить.\n\nЕсли у тебя есть нужные навыки, я могу научить тебя создавать сосуды для силы рун.', '', 38134),
(205568, 'ruRU', 'Какие профессии тебя интересуют?', 'Какие профессии тебя интересуют?', 38134),
(205561, 'ruRU', '', 'Я не верю ему. Однако другой возможности раскрыть их козни у нас может и не быть.', 38134),
(205560, 'ruRU', '', 'Пилигрим! Этот лич предлагает открыть нам доступ в дом Ритуалов.', 38134),
(205559, 'ruRU', 'Но у тебя нет выбора, не так ли? Если ты не хочешь идти в атаку, которая станет самоубийством.', '', 38134),
(205558, 'ruRU', '', '...Мне не нравится твой план, лич.', 38134),
(205552, 'ruRU', 'Лунные мотыльки! Наверное, они только что вылупились. Попробуй поймать нескольких и забрать с собой.', '', 38134),
(205550, 'ruRU', 'Предупреждаю: я хорошо обучен боевым искусствам.', 'Предупреждаю: я хорошо обучена боевым искусствам.', 38134),
(205534, 'ruRU', 'Новый смысл существования? У меня, божества без мира?\n\nИ все же я не намереваюсь просто сидеть без дела. Это не в моем обычае.\n\nМне нужно лучше понять этот мир. Эти... Темные Земли. Я чувствую множество царств, но особую роль сыграют лишь четыре.\n\nПринеси мне что-нибудь из этих четырех царств. Что-то живое. Моя раса умеет многое узнавать простым прикосновением.', '', 38134),
(205532, 'ruRU', 'Я знаю, что его больше нет. Я чувствую утрату. \n\nУжасная боль. Что есть божество без своего мира? \n\nКуда мне теперь идти? Что мне теперь делать?', '', 38134),
(205530, 'ruRU', 'Арденвельд? Так это Темные Земли? Естественно, я знаю про Темные Земли. Я все-таки божество.\n\nМне известно многое. Но все же я не знаю, откуда я здесь и как погиб мой мир. \n\nДолжно быть, мы с ним умерли одновременно. Но теперь меня возродили, а его нет.', '', 38134),
(205528, 'ruRU', 'Ну, что скажешь? Почему я здесь?', '', 38134),
(205522, 'ruRU', 'Тихо-тихо, всего один мазок. Аккуратно.', 'Тихо-тихо, всего один мазок. Аккуратно.', 38134),
(205521, 'ruRU', 'Ох! Какой глубокий оттенок. Видишь?', 'Ох! Какой глубокий оттенок. Видишь?', 38134),
(205520, 'ruRU', 'Посмотри, как ровно он покрывает поверхность. Как лист на ветру.', 'Посмотри, как ровно он покрывает поверхность. Как лист на ветру.', 38134),
(205519, 'ruRU', 'Все, готово. Пусть радость, с какой я делал эту вещь, передастся тебе.', 'Все, готово. Пусть радость, с какой я делала эту вещь, передастся тебе.', 38134),
(205516, 'ruRU', 'Вместе... как дружная семья.', 'Вместе... как дружная семья.', 38134),
(205505, 'ruRU', 'Полемарх Адрест кивает.', '', 38134),
(205498, 'ruRU', '', 'Самое трудное впереди.', 38134),
(205497, 'ruRU', '', 'Добро пожаловать в последний оплот чести в Малдраксусе. Твое прибытие сюда прошло почти при таких же неспокойных обстоятельствах, как и мое.', 38134),
(205496, 'ruRU', 'Думаю, это он! Принеси мне этот клинок!', '', 38134),
(205481, 'ruRU', '', 'Мы начнем атаку по твоей команде, пилигрим!', 38134),
(205472, 'ruRU', 'С тех пор, как мы с Кадарином сражались за свой родной мир вместе с другими его защитниками, прошло так много лет. Я не скучал по войне, но был рад снова стоять плечом к плечу с друзьями и соратниками. \n\n<Тирнакс весело фыркает.>\n\nИ рад, что мы покончили с этими осквернителями.', '', 38134),
(205471, 'ruRU', '', 'Не хочу даже думать о том, что случилось бы, добейся друсты своего.\n\nБеды на этом не кончились, но с одной угрозой мы по крайней мере расправились.\n\nЯ еще не избавилась от подозрений насчет тебя, но хотя бы в одном наши цели совпадают – Арденвельду надо помочь.', 38134),
(205470, 'ruRU', 'Чудесный пигмент! Никогда прежде такого не видел. Сейчас же примусь за работу.', 'Чудесный пигмент! Никогда прежде такого не видела. Сейчас же примусь за работу.', 38134),
(205468, 'ruRU', '', 'Не хочу даже думать о том, что случилось бы, добейся друсты своего.\n\nБеды Арденвельда на этом не кончились, но с одной угрозой мы по крайней мере расправились. \n\nТиранда все еще бродит по Торгасту в одиночестве. Поиски другой Ночной Воительницы пока продолжаются, и Изера с Тирнаксом уверены в своем плане, но... я боюсь за нее.', 38134),
(205467, 'ruRU', '', 'Друсты повержены, но Арденвельду по-прежнему нужны защитники. Как и Ара\'лон, я буду сражаться вместе с Дикой Охотой и защищать каждую рощу. \n\nТеперь я правда верю, что однажды Арденвельд будет полностью восстановлен.', 38134),
(205466, 'ruRU', 'Что ж, это было интересно. Как здорово, что друстов больше нет. Они оказывали ужасное влияние на наши рощи и семена. Надеюсь, теперь королева восстановит прежний порядок. Или хотя бы какое-то подобие порядка. Спасибо тебе, $n. Твоя помощь была бесценна.', '', 38134),
(205465, 'ruRU', '', 'Сперва мы были тебе чужаками, дитя мира, истерзанного войной. Но сейчас мы сражались плечом к плечу, как настоящие товарищи. Я преклоняюсь перед твоими навыками охотника. Мое копье к твоим услугам.', 38134),
(205463, 'ruRU', 'Даже самые сильные охотники могут потерпеть поражение. \n\nМы скорбим об Ара\'лоне и других потерянных душах, но и воспеваем их жизни. И благодарим судьбу за нашу победу! \n\nТвоя прыть в бою – настоящее воплощение стойкости Арденвельда. Песни о твоих подвигах эхом пронесутся по всему лесу, $n. \n\nЭто был славный бой. Воистину славный.', '', 38134),
(205459, 'ruRU', '', 'С самого твоего прибытия я увидела в тебе что-то необычное, $n. И я не ошиблась! Ты... очень любопытное создание.\n\n<Голос Лунной Ягоды становится серьезнее.>\n\nИ такие любопытные вещи нам здесь совсем не помешают. Друстов удалось прогнать, но они лишь усугубляли уже существующую проблему. У нас еще далеко не все в порядке. Но я знаю, что мы сможем все исправить!', 38134),
(205458, 'ruRU', '', 'Ради моего спасения было принесено немало жертв. Я рада, что смогла помочь защитить это место и хотя бы отчасти отплатить за оказанное мне милосердие.\n\nДрусты больше не имеют здесь власти, но терять бдительность пока рано. Для Темных Земель... настали поистине смутные времена. Сегодня мы победили, но это не значит, что повсюду воцарился порядок. \n\nГрядут новые испытания, и этому миру снова потребуется твоя помощь. Приготовься.', 38134),
(205457, 'ruRU', 'Молодец. Вольно!', 'Молодец. Вольно!', 38134),
(205442, 'ruRU', 'Эти грибы обезумели под влиянием Утробы! Нужно поскорее что-то предпринять!', '', 38134),
(205441, 'ruRU', 'О, прости. Разве я забыл сказать, что потребуется физический труд?', '', 38134),
(205440, 'ruRU', 'Отнеси это тело обратно и положи в ритуальный круг.', '', 38134),
(205437, 'ruRU', 'Этот облик, конечно, очень... интересный.', 'Этот облик, конечно, очень... интересный.', 38134),
(205436, 'ruRU', 'Как ты послужишь Арденвельду?', 'Как ты послужишь Арденвельду?', 38134),
(205435, 'ruRU', 'Какой облик ты примешь?', 'Какой облик ты примешь?', 38134),
(205434, 'ruRU', 'Да благословит тебя ночь.', 'Да благословит тебя ночь.', 38134),
(205427, 'ruRU', '', 'Но если ты не справишься, я внесу в список твое имя!', 38134),
(205426, 'ruRU', '', 'Вместе мы вычеркнем несколько имен в моем списке.', 38134),
(205425, 'ruRU', '', 'Используй этот кристалл, чтобы я смогла ненадолго появиться в мире живых.', 38134),
(205424, 'ruRU', '', 'За мной последуют другие бойцы... но тебе они не понадобятся, пока рядом я!', 38134),
(205423, 'ruRU', 'И Дрейвен еще называет меня высокомерным! Да если бы этот камнерожденный олух внял моим предупреждениям о Кел\'Тузаде, мы бы уничтожили лича! Но нет, теперь наш враг благополучно присоединился к Тюремщику.\n\n<Ярость в глазах эльфа крови постепенно угасает.>\n\nТеперь я понимаю, какие ошибки совершил. Может быть, со временем я искуплю вину и снова докажу, что достоин твоего доверия.\n\nНо я бы с радостью обменял прощение для себя на месть за свой народ. За тех, кто умер, потому что Кел\'Тузад...\n\n<Кель\'тас вздыхает.>\n\nПотому что я не сумел защитить их.\n\nОставь меня, $p. Мне нужно о многом подумать.', '', 38134),
(205422, 'ruRU', 'И Дрейвен еще называет МЕНЯ высокомерным! Да если бы этот камнерожденный олух внял моим предупреждениям о Кел\'Тузаде, мы бы уничтожили лича! Но нет, теперь наш враг благополучно присоединился к Тюремщику.\n\n<Ярость в глазах эльфа крови постепенно угасает.>\n\nТеперь я понимаю, какие ошибки совершил. Может быть, со временем я искуплю вину.\n\nНо я бы с радостью обменял прощение для себя на месть за свой народ. За тех, кто умер, потому что Кел\'Тузад...\n\n<Кель\'тас вздыхает.>\n\nПотому что я не сумел защитить их.', '', 38134),
(205421, 'ruRU', 'И пока я, твой принц, вынужден оставаться в Ревендрете и страдать из-за пары... сомнительных поступков, Кел\'Тузад наслаждается свободой. Этот монстр подчинил себе почти весь Малдраксус!\n\n<Кель\'тас некоторое время яростно смотрит перед собой, но затем опускает взгляд.>\n\nНа каждый десяток кель\'дорай пришлось девять убитых, $p. И все ради того, чтобы Артас Менетил оживил этого лича. Наше славное королевство было почти стерто с лица земли.\n\nА те, кто выжил, считали это бременем, а не спасением. Я сделал все, что было в моих силах, чтобы поддержать наш народ.\n\nНо возможно, путь, что я избрал, был...\n\n<Кель\'тас умолкает.>', '', 38134),
(205420, 'ruRU', 'Пока я вынужден оставаться в Ревендрете и страдать из-за пары... сомнительных поступков, Кел\'Тузад наслаждается свободой. Этот монстр подчинил себе почти весь Малдраксус!\n\n<Кель\'тас некоторое время яростно смотрит перед собой, но затем опускает взгляд.>\n\nНа каждый десяток кель\'дорай пришлось девять убитых, $p. И все ради того, чтобы Артас Менетил оживил этого лича. Наше славное королевство было почти стерто с лица земли.\n\nВ тебе ведь еще теплится кровь? Рассеки свою плоть и сосчитай каждую каплю. За каждую спасенную каплю позволь девяти другим упасть и впитаться в землю.\n\nСкажи мне, что после этого будет чувствовать твое сердце, $p. И я скажу тебе, что чувствует мое.', '', 38134),
(205419, 'ruRU', 'Я не потерплю такого обращения! Я принц, особа королевской крови, а эта Арбитр посмела отправить меня в царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!', '', 38134),
(205418, 'ruRU', 'Тебе, должно быть, нравится видеть меня таким. Быть может, тебе и удалось одолеть меня в бою, но равными мы никогда не будем.\n\nЯ принц, особа королевской крови, а эта Арбитр посмела отправить меня в царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!', '', 38134),
(205417, 'ruRU', 'Я не потерплю такого обращения! Я принц, особа королевской крови, а эта Арбитр посмела отправить меня в царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!', '', 38134),
(205416, 'ruRU', 'Ты! Подойди! Твой владыка приказывает тебе немедленно освободить его!\n\n<Кель\'тас дергает за свои цепи.>\n\nТакое обращение возмутительно! Я твой принц, твой законный властитель, но Арбитру все это безразлично. Она сослала меня в это царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!\n\nОбвинительница! Освободи меня! Я должен отомстить за свое королевство. Ты меня слышала?! Обвинительница!\n\n<Кель\'тас возобновляет попытки вырваться из пут.>', '', 38134),
(205415, 'ruRU', 'Ты! Да, ты! Немедленно сними с меня цепи!\n\n<Кель\'тас натягивает оковы, пытаясь вырваться.>\n\nТебе доставляет удовольствие унижать меня, да? Пусть я проиграл в бою, но тебе все равно никогда не подняться до моего уровня!\n\nЯ принц, особа королевской крови, но Арбитру это безразлично. Она сослала меня в это царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!\n\nОбвинительница! Освободи меня! Я должен отомстить за свое королевство. Ты меня слышала?! Обвинительница!\n\n<Кель\'тас возобновляет попытки вырваться из пут.>', '', 38134),
(205414, 'ruRU', 'Ты! Да, ты! Немедленно сними с меня цепи!\n\n<Кель\'тас натягивает оковы, пытаясь вырваться.>\n\nЭто недопустимо! Я принц, особа королевской крови, но Арбитру это безразлично. Она сослала меня в это царство серого камня к унылым плебеям.\n\nУ вентиров нет права судить меня! И у тебя тоже!\n\nОбвинительница! Освободи меня! Я должен отомстить за свое королевство. Ты меня слышала?! Обвинительница!\n\n<Кель\'тас возобновляет попытки вырваться из пут.>', '', 38134),
(205412, 'ruRU', 'Даже не вздумай со мной заговаривать.\n\nЯ не слепой. И знаю, что ты уже стараешься заслужить доверие другого ковенанта.', '', 38134),
(205411, 'ruRU', 'Я видел, как ты стараешься произвести впечатление на другой ковенант.\n\nНам пока не о чем с тобой говорить.', '', 38134),
(205410, 'ruRU', '', 'Твоя сила теперь служит не только нам. \n\nНе думай, что я упускаю такое из виду. У нас повсюду есть глаза.', 38134),
(205409, 'ruRU', '', 'Кажется, ты уже стараешься заслужить милость другого ковенанта. \n\nТогда не стоит тратить время со мной.', 38134),
(205408, 'ruRU', 'Мне кажется, или я видел тебя с Лунной Ягодой? \n\nНадеюсь, когда эти помешанные заводят про свое возрождение, ты пропускаешь эту чепуху мимо ушей.', '', 38134),
(205407, 'ruRU', 'Почему ты не желаешь порвать отношения с кириями?\n\nТы принесешь гораздо больше пользы, служа вентирам.', '', 38134),
(205406, 'ruRU', 'Зачем ты столько болтаешь с Дрекой?\n\nНе трать время зря на разговоры. Лучше выполняй обещание и помогай вентирам.', '', 38134),
(205405, 'ruRU', '', 'Будь начеку в обществе генерала Дрейвена.\n\nВентирам нельзя доверять. Уверена, ты уже это знаешь, просто я не могла не заметить, что ты проводишь с ним все больше времени.', 38134),
(205404, 'ruRU', '', 'Я вижу, что ты сближаешься с Дрекой.\n\nПонимаю, она из твоего родного мира, но все-таки у тебя теперь есть новые друзья. Мы.', 38134),
(205401, 'ruRU', '', 'Кажется, в последнее время вы с кириями очень подружились.\n\nТебе надоело служить нашей королеве? Нам еще столько нужно сделать вместе.', 38134),
(205400, 'ruRU', '', 'Что это вы обсуждали с Дрейвеном?\n\nНе знала, что сейчас мы сотрудничаем с вентирами. Я им не доверяю.', 38134),
(205398, 'ruRU', '', 'Не думала, что ты очень любишь природу, но в последнее время ты, кажется, проводишь много времени с ночным народцем.', 38134),
(205397, 'ruRU', '', 'Кажется, в последнее время ты крепко дружишь с кириями.\n\nНадеюсь, твоя верность некролордам не ослабла, $n? Нам еще многое нужно сделать. \n\nМне бы не хотелось, чтобы наши отношения испортило предательство.', 38134),
(205394, 'ruRU', 'Как я понимаю, вы с Дрекой из одного мира. \n\nЭто потому вы недавно с ней болтали?', '', 38134),
(205390, 'ruRU', 'Я видел, как вы разговаривали с генералом Дрейвеном. \n\nПолагаю, ты передашь нам всю важную информацию.', '', 38134),
(205389, 'ruRU', 'У вас что, какие-то общие дела с Лунной Ягодой?\n\nНе знал, что мы сейчас поддерживаем связь с ночным народцем.', '', 38134),
(205388, 'ruRU', '', 'Храбрости тебе, $n, конечно, не занимать.\n\nТы хочешь меня спровоцировать? Нарваться на наказание за свое предательство? \n\nТебе лучше уйти. Вряд ли в ближайшее время я захочу о чем-то говорить с кем-то вроде тебя.', 38134),
(205387, 'ruRU', '', 'Ты снова доказываешь свою силу.\n\nНо сила – только одно из качеств, которыми должен обладать некролорд. Мы также требуем непоколебимой верности, и ее тебе в прошлом не хватало. \n\nУходи. Мы обсудим, стоит ли принять тебя обратно в ковенант. \n\nВозвращайся позже. Может, к тому времени мы примем решение.', 38134),
(205380, 'ruRU', 'Тебе повезло, что я не разорвал тебя пополам на месте!\n\nЕсли бы не соглашение о нейтралитете, я бы с удовольствием так и сделал.\n\nЛучше уходи. И не возвращайся, пока я не усмирю свой гнев.', '', 38134),
(205378, 'ruRU', 'Тебе удалось искупить часть своей вины перед нами.\n\nПосмотрим, как ты покажешь себя в дальнейшем. Заходи попозже.\n\nМожет быть, в следующий раз мы решим, что ты заслуживаешь снова стать одним из вентиров.', '', 38134),
(205370, 'ruRU', 'Как вы хотите изменить ваши наплечники и клобук?', '', 38134),
(205368, 'ruRU', 'Какой аксессуар вы хотите изменить?', '', 38134),
(205366, 'ruRU', 'В какой цвет вы хотите перекрасить свою маскировку?', '', 38134),
(205365, 'ruRU', 'Какой элемент своей маскировки вы хотите изменить?', '', 38134),
(205364, 'ruRU', '', 'Ты смеешь возвращаться и говорить со мной?\n\nМы уже убедились, что твоя верность весьма недолговечна, $n. \n\nМожет, позже я смогу простить тебя. Может быть.', 38134),
(205363, 'ruRU', 'Этот манекен служит Морет Стильной для создания новых регалий аколитов дома Ритуалов.\n\nС его помощью вы можете изменить внешний вид вашей маскировки под некроаколита.', '', 38134),
(205362, 'ruRU', '$n? Серьезно? \n\nОтдаю тебе должное – ты не боишься показываться здесь. Но мне нечего сказать таким, как ты.', '', 38134),
(205360, 'ruRU', 'Сюда, пилигрим.', '', 38134),
(205359, 'ruRU', '', 'Это был твой первый шаг на пути к нашей королеве.\n\nМы с ней сейчас решаем, является твое возвращение частью цикла или нет.\n\nЗнаешь, загляни ко мне попозже. Может быть, к тому времени мы примем решение. Я бы извинилась за то, что мы заставляем тебя ждать, но, пожалуйста, пойми, что совершенное тобой предательство заставляет нас действовать со всей осторожностью.', 38134),
(205356, 'ruRU', 'Привет, $n.\n\nМы решаем, стоит ли позволить тебе снова присоединиться к нам. \n\nВозвращайся попозже. Быть может, ветра переменятся и принесут тебе удачу.', '', 38134),
(205345, 'ruRU', 'Тарон\'джа знает все! Смертным не удастся прервать вечную династию!', '', 38134),
(205343, 'ruRU', 'Это никуда не годится! Этим ребятам здесь не место! Надо проучить их.', '', 38134),
(205341, 'ruRU', 'Секреты глубин должны остаться секретами.', '', 38134),
(205335, 'ruRU', 'Лоскутик хочет поиграть!', '', 38134),
(205334, 'ruRU', 'Прийти сюда было глупо с твоей стороны.', '', 38134),
(205330, 'ruRU', 'Больше потрохов! Больше крови! Больше еды!', '', 38134),
(205328, 'ruRU', 'Вам холодно? Это дыхание скорой смерти.', '', 38134),
(205323, 'ruRU', 'Ее время пришло. К несчастью, ее путь в вечности мог вести только в Утробу. Такие решения принимаем не мы, а Арбитр.', 'Ее время пришло. К несчастью, ее путь в вечности мог вести только в Утробу. Такие решения принимаем не мы, а Арбитр.', 38134),
(205322, 'ruRU', 'Печальная смерть, но не нам решать, заслуживала ли она ее.', 'Печальная смерть, но не нам решать, заслуживала ли она ее.', 38134),
(205321, 'ruRU', '', 'Древо горит! Я... задыхаюсь...', 38134),
(205320, 'ruRU', 'Ее смерть ужасна, но это ничего не меняет: пришло ее время отправляться в Орибос и присоединиться к вечности.', 'Ее смерть ужасна, но это ничего не меняет: пришло ее время отправляться в Орибос и присоединиться к вечности.', 38134),
(205319, 'ruRU', 'Пожертвовать собой ради семьи – это благородно. Но это не может служить причиной для возвращения в мир живых.', 'Пожертвовать собой ради семьи – это благородно. Но это не может служить причиной для возвращения в мир живых.', 38134),
(205318, 'ruRU', 'Я сражался на дуэли со своим безрассудным братом и... понял, что не смогу пережить его потерю! Я слишком медленно поднял меч и поплатился за это.', 'Я сражалась на дуэли со своим безрассудным братом и... поняла, что не смогу пережить его потерю! Я слишком медленно подняла меч и поплатилась за это.', 38134),
(205317, 'ruRU', 'Она дух природы, поэтому ее отправили в Арденвельд – и дальше помогать тем, кто в этом нуждается. Такова была ее судьба.', 'Она дух природы, поэтому ее отправили в Арденвельд – и дальше помогать тем, кто в этом нуждается. Такова была ее судьба.', 38134),
(205316, 'ruRU', 'Хотя некоторые тесно связанные с природой души возвращаются в свои миры после отдыха в Арденвельде, многие остаются в его рощах навечно.', 'Хотя некоторые тесно связанные с природой души возвращаются в свои миры после отдыха в Арденвельде, многие остаются в его рощах навечно.', 38134),
(205315, 'ruRU', '', 'Роща в опасности! Малыши не доберутся до укрытия без меня!', 38134),
(205314, 'ruRU', 'Демонам не место в Темных Землях, если не считать некоторых бойцов Малдраксуса.', 'Демонам не место в Темных Землях, если не считать некоторых бойцов Малдраксуса.', 38134),
(205313, 'ruRU', 'Наверное, произошла путаница? Приглашение адресовано полемарху Адресту?', '', 38134),
(205311, 'ruRU', 'Правильно. Демоны приходят из Круговерти Пустоты и в нее же должны отправиться после смерти.', 'Правильно. Демоны приходят из Круговерти Пустоты и в нее же должны отправиться после смерти.', 38134),
(205309, 'ruRU', '', 'Мм-м! Я уже успел позабыть, как жалит клинок.', 38134),
(205303, 'ruRU', 'Хотя валь\'киры не одни из нас, мы не можем препятствовать им призывать кого-то в мир живых.', 'Хотя валь\'киры не одни из нас, мы не можем препятствовать им призывать кого-то в мир живых.', 38134),
(205302, 'ruRU', 'Отлично. Эта душа прибыла к нам своевременно. Даже нежити когда-нибудь суждено умереть.', 'Отлично. Эта душа прибыла к нам своевременно. Даже нежити когда-нибудь суждено умереть.', 38134),
(205301, 'ruRU', 'Я слышу жуткий зов валь\'киры. Пожалуйста! Подари мне тихую смерть! Молю тебя!', '', 38134),
(205294, 'ruRU', 'У тебя есть насыщенные рубины? Этот кошмарный конь доставит тебя прямо в Темный Приют. \n\nТолько держись крепче, если не хочешь свалиться по дороге.', '', 38134),
(205291, 'ruRU', 'Пойми, он не любит изменения в своем распорядке дня. Но в любом случае ему полезно развеяться. Не беспокойся, я разберусь.', '', 38134),
(205285, 'ruRU', 'Подойдет все что угодно.\n\nПотом я сам доработаю.', '', 38134),
(205284, 'ruRU', 'Несколько насыщенных рубинов и этот кошмарный конь позволят тебе с ветерком прокатиться по северным укреплениям. \n\nВид с них открывается просто отличный, но там много всяких злобных громил и прочих неприятных типов. Так что держись крепче, если точно не хочешь остаться там.', '', 38134),
(205282, 'ruRU', 'Так и есть. Вернись она, у города появился бы шанс на спасение. Но ее время пришло.', 'Так и есть. Вернись она, у города появился бы шанс на спасение. Но ее время пришло.', 38134),
(205281, 'ruRU', 'Хотя потоп, несомненно, погубит множество жизней, мы не должны вмешиваться в дела смертных.', 'Хотя потоп, несомненно, погубит множество жизней, мы не должны вмешиваться в дела смертных.', 38134),
(205280, 'ruRU', 'Мой \"Взрывонатор 5000\" просто взял и подорвал плотину над лабораторией! Мои подопытные... в смысле... мои любимые зверушки в смертельной опасности!', 'Мой \"Взрывонатор 5000\" просто взял и подорвал плотину над лабораторией! Мои подопытные... в смысле... мои любимые зверушки в смертельной опасности!', 38134),
(205279, 'ruRU', 'Справедливо. Мольбы души не убедят нас изменить ее судьбу.', 'Справедливо. Мольбы души не убедят нас изменить ее судьбу.', 38134),
(205278, 'ruRU', 'Раз филактерия лича не тронута, надо отправить его назад.', 'Раз филактерия лича не тронута, надо отправить его назад.', 38134),
(205275, 'ruRU', 'Не счесть, сколько раз я уже стоял на этом перекрестке. Я больше не буду рабом! Подари мне покой смерти!', 'Не счесть, сколько раз я уже стояла на этом перекрестке. Я больше не буду рабыней! Подари мне покой смерти!', 38134),
(205272, 'ruRU', 'Если хочешь обменять на что-нибудь свои насыщенные рубины, то этот кошмарный конь отнесет тебя прямо к дыре в стене в западном замке. \n\nТолько не отпускай поводьев, если, конечно, не хочешь совершить вынужденную остановку где-то по пути.', '', 38134),
(205268, 'ruRU', 'Пилигрим, собери немного для наших гостей, чтобы они запомнили этот добрый знак.', '', 38134),
(205267, 'ruRU', 'Нашему Двору послано благое предзнаменование: над нами сгущается анима!', '', 38134),
(205251, 'ruRU', 'Так как это существо создано магией Огненных Просторов, его нельзя отправить на суд в Орибос.', 'Так как это существо создано магией Огненных Просторов, его нельзя отправить на суд в Орибос.', 38134),
(205250, 'ruRU', 'Отлично! Существа из другого измерения должны вернуться туда после смерти. Им не суждено попасть в Темные Земли.', 'Отлично! Существа из другого измерения должны вернуться туда после смерти. Им не суждено попасть в Темные Земли.', 38134),
(205249, 'ruRU', 'Огненным Просторам угрожают захватчики! Я должен немедленно вернуться к своим собратьям!', '', 38134),
(205247, 'ruRU', 'В ней чувствуется магия Темных Земель, но источник этой магии зовет ее обратно в мир живых. Она должна была вернуться.', 'В ней чувствуется магия Темных Земель, но источник этой магии зовет ее обратно в мир живых. Она должна была вернуться.', 38134),
(205246, 'ruRU', 'Должно быть, так и есть. Она уже когда-то умерла, но не смогла вернуться к нам, потому что должна была ответить на зов своего хозяина.', 'Должно быть, так и есть. Она уже когда-то умерла, но не смогла вернуться к нам, потому что должна была ответить на зов своего хозяина.', 38134),
(205245, 'ruRU', 'Король-лич призывает меня. Он снова нуждается во мне!', 'Король-лич призывает меня. Он снова нуждается во мне!', 38134),
(205243, 'ruRU', 'Фу, пылевые элементали! Если их не прогнать, они тут все выметут, и дикие семена погибнут!', '', 38134),
(205241, 'ruRU', 'Верно. Это создание отдало свою жизнь в бою и заслужило право войти в Темные Земли.', 'Верно. Это создание отдало свою жизнь в бою и заслужило право войти в Темные Земли.', 38134),
(205240, 'ruRU', 'Хотя нам больно принимать такое решение, мы не должны вставать между этим существом и вечностью.', 'Хотя нам больно принимать такое решение, мы не должны вставать между этим существом и вечностью.', 38134),
(205239, 'ruRU', 'Я переведу. Это существо умерло, защищая хозяина от стаи волков. Оно хочет вернуться, чтобы оберегать его от опасностей.', 'Я переведу. Это существо умерло, защищая хозяина от стаи волков. Оно хочет вернуться, чтобы оберегать его от опасностей.', 38134),
(205238, 'ruRU', 'Тяв! Гав! Гав! Гав! Р-р-р...', '', 38134),
(205237, 'ruRU', 'Все верно – он пал смертью храбрых. Погиб как герой и справедливо заслужил славу в Малдраксусе.', 'Все верно – он пал смертью храбрых. Погиб как герой и справедливо заслужил славу в Малдраксусе.', 38134),
(205236, 'ruRU', 'К сожалению, нет. Хотя остальные в самом деле умерли раньше, он выполнил свое предназначение в мире смертных.', 'К сожалению, нет. Хотя остальные в самом деле умерли раньше, он выполнил свое предназначение в мире смертных.', 38134),
(205235, 'ruRU', 'В моем городе бушует битва. Я должен вернуться и защитить малышей, пока до них не добрались чудовища!', 'В моем городе бушует битва. Я должна вернуться и защитить малышей, пока до них не добрались чудовища!', 38134),
(205234, 'ruRU', 'Это неверно. Мы не должны сопровождать души, у которых есть связи с другими силами.', 'Это неверно. Мы не должны сопровождать души, у которых есть связи с другими силами.', 38134),
(205233, 'ruRU', 'Верно! Душам, у которых достаточно сильная связь с другими управляющими вселенной силами, нужно позволить вернуться.', 'Верно! Душам, у которых достаточно сильная связь с другими управляющими вселенной силами, нужно позволить вернуться.', 38134),
(205232, 'ruRU', '<Существо издает какофонию тревожного звона.>', '', 38134),
(205230, 'ruRU', 'Всего горсть насыщенных рубинов, и этот кошмарный конь доставит тебя до самых Старых ворот. Только держись крепче, если не хочешь свалиться, не доехав до цели.', '', 38134),
(205229, 'ruRU', 'Басы должны быть плотнее. Бочка должна быть мощнее.\n\nЭто, по-вашему, нормальный концертный зал? Это пещера! Холодно и акустика дрянь.\n\n\"Чумной вепрь\" шагу не ступит на сцену, пока не отладят звук. Да, я нежить, и что? У меня не может быть музыкального слуха?', '', 38134),
(205222, 'ruRU', 'О, привет! Несешь мне новые рецепты?', '', 38134),
(205221, 'ruRU', 'Рад тебя видеть. Перейдем к еде?', '', 38134),
(205220, 'ruRU', 'Меня зовут Ясытем, потому что я люблю сытную еду. Смешно, правда?', '', 38134),
(205219, 'ruRU', 'Привет! Привет! Ты хочешь передать рецепт?', '', 38134),
(205214, 'ruRU', '', 'Я не тренирую разбойников со специализацией \"Ликвидация\".', 38134),
(205212, 'ruRU', '', 'Алчность Казначея безгранична.\n\nВ Малдраксусе никому и в голову не придет так вести себя. Разве вентиры не призваны помогать душам искупить свои грехи? Еще больше меня возмущает то, что он объединился с кем-то из моего царства.\n\nМы должны положить конец этому безумию.', 38134),
(205205, 'ruRU', '', 'Я... хочу побыть одна.', 38134),
(205202, 'ruRU', '', 'Первая тренировка сделала тебя гораздо сильнее. Давай найдем более подходящего противника.', 38134),
(205201, 'ruRU', '', 'Смерть предавшим дом Глаз!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205197, 'ruRU', '<Аркелий усмехается.>\n\nНет. Если все передо мной рассыплется в прах...\n\n...то я всего лишь отправлюсь в новое приключение.', '', 38134),
(205194, 'ruRU', 'Ну... Я гулял! \n\nНабрел на одно чудесное местечко под деревом и решил остановиться и помедитировать.\n\nНаверное, я слишком долго провел в размышлениях, потому что каким-то образом очутился в стороне от Арденвельда.', '', 38134),
(205192, 'ruRU', 'Я странник. Путешественник.\n\nЯ веками исследовал царства жизни, а теперь брожу по царствам смерти. Много времени утекло с начала моего пути, и теперь, оглянувшись назад, я нахожу немало поводов для размышлений.', '', 38134),
(205190, 'ruRU', 'Здравствуй. Ты, я вижу, тоже путешественник.', '', 38134),
(205189, 'ruRU', 'У меня... В смысле, у нас есть сила! Наконец-то я избавлюсь от твоего бесконечного нытья про охрану этого зала.', '', 38134),
(205188, 'ruRU', 'Нет! Уходи. Стой! Сюда никому нельзя.', '', 38134),
(205187, 'ruRU', 'Как тебе такое? А? А? Откроешь дверь или как?', '', 38134),
(205186, 'ruRU', 'О-о-о! Мне так <рыгает> ее не хватало. Еще! Нам надо еще.', '', 38134),
(205185, 'ruRU', 'Хочешь... расширить свои возможности? \n\nПомощнее удар, пожарче магию.\n\nЯ могу это устроить, только заплати.', '', 38134),
(205184, 'ruRU', 'Не-е-ет! Владыка будет в ярости. Ты поплатишься за это!', '', 38134),
(205182, 'ruRU', 'Могучие анимапарды не дрогнут.', '', 38134),
(205180, 'ruRU', 'Он с тобой разговаривает? Он предатель. Не слушай его.', '', 38134),
(205179, 'ruRU', 'Пф! Не обращай внимания на болтовню этого глупца. Мы все равно проберемся в зал. Поищем, откуда можно вытянуть аниму!', '', 38134),
(205178, 'ruRU', 'О да! То, что надо! Вот только... нам понадобится гораздо больше.', '', 38134),
(205173, 'ruRU', 'А вот и пилигрим Утробы! Я на тебя рассчитываю.', '', 38134),
(205171, 'ruRU', '', 'Один из моих разведчиков вернулся с информацией о передвижении Лисонии.', 38134),
(205170, 'ruRU', 'Принесет ли мне успокоение его судьба? Обрету ли я когда-нибудь свободу от него? Возможно... в свое время. Возможно.', '', 38134),
(205169, 'ruRU', '', 'Сделай это ради Пелагия, Клейя. Ради него.', 38134),
(205168, 'ruRU', 'Нет. Я не один. У меня же есть Клейя! Она говорила, что с каждым разом у меня получается все лучше. Когда-нибудь я справлюсь! Когда-нибудь...', '', 38134),
(205167, 'ruRU', '', 'Теперь я вижу. Тот путь, по которому я шла. Я больше не забуду.', 38134),
(205166, 'ruRU', 'Я хотел напомнить ей обо всем, что она обрела, но... Все спрашиваю себя, есть ли иной выход из этой ситуации. У меня столько вопросов, дорогой свиток, и ни одного ответа.', '', 38134),
(205155, 'ruRU', '<Видимо, этот брокер отчаянно оборонялся, но оказался не готов к сражению.>', '', 38134),
(205153, 'ruRU', '<Кажется, этот брокер погиб в бою.>', '', 38134),
(205148, 'ruRU', 'Прекрасно! Этого должно хватить. Теперь мы сможем пробраться мимо этого крикуна. Давай туда вернемся!', '', 38134),
(205147, 'ruRU', '<Сугроб не защитил этого брокера.>', '', 38134),
(205146, 'ruRU', 'Вы ничего не находите.', 'Вы ничего не находите.', 38134),
(205145, 'ruRU', '<Этот раненый брокер почти успел добраться до портала, прежде чем погиб. \n\nНичто не указывает на то, что ему удалось найти нечто ценное.>', '', 38134),
(205144, 'ruRU', 'Я извещу Архонта.', '', 38134),
(205135, 'ruRU', 'Эй! Верни на место. Положи обратно. Вор!', '', 38134),
(205134, 'ruRU', 'Назад! Путь для тебя закрыт.', '', 38134),
(205132, 'ruRU', 'Кажется, это груда выброшенных кем-то доспехов.', '', 38134),
(205129, 'ruRU', 'Кха-кха! \n\nДаже не думай, что тебе удастся проскользнуть мимо меня. Я глух к твоим жалким мольбам.', '', 38134),
(205099, 'ruRU', 'Мне говорили, что к нам прибудет кто-то из Грехопада. Твое присутствие для нас – честь.\n\nТолько постарайся не мешать хранителям. Они заняты восстановлением того, что было здесь разрушено.', 'Мне говорили, что к нам прибудет кто-то из Грехопада. Твое присутствие для нас – честь.\n\nТолько постарайся не мешать хранителям. Они пытаются восстановить то, что было здесь разрушено.', 38134),
(205098, 'ruRU', 'Раньше склепы были эталоном порядка в Ревендрете. Теперь все не так.\n\nМы должны вернуть их былое величие. Здесь нам уже удалось кое-что восстановить. Увы, в других местах для нас еще слишком опасно.\n\nПрошу меня простить. Я должен вернуться к работе.', 'Раньше склепы были эталоном порядка в Ревендрете. Теперь все не так.\n\nМы должны вернуть их былое величие. Здесь нам уже удалось кое-что восстановить. Увы, в других местах для нас еще слишком опасно.\n\nПрошу меня простить. Я должна вернуться к работе.', 38134),
(205097, 'ruRU', '', 'Он так искренне верил... И ни секунды не сомневался в нашей правоте.\n\nВ тот миг я увидела, как рухнул весь его мир.\n\nЯ...\n\nЯ не знаю, как нам теперь продолжать.', 38134),
(205095, 'ruRU', '', 'Мы знали, что некоторые души по ошибке попадали в Утробу.\n\nАхиллион всегда был таким стойким... Он вселял в меня уверенность. Его вера ни разу не дрогнула. Он всегда был полон надежды.\n\nНо когда мы прибыли в Орибос... увидели все эти души...\n\nОн пал прямо у меня на глазах.', 38134),
(205093, 'ruRU', '', '<Палесса, не мигая, вглядывается в воду.>', 38134),
(205091, 'ruRU', 'Я навечно благодарен тебе и твоим союзникам за храбрость, пилигрим.', '', 38134),
(205088, 'ruRU', '', 'Не могу... продолжать...', 38134),
(205087, 'ruRU', '', 'Ох...', 38134),
(205086, 'ruRU', '', 'Сила Тюремщика растет, и на Темные Земли надвигается страшная угроза. Готовься, $p. Твоя сила нам еще пригодится.', 38134),
(205079, 'ruRU', '', 'До свидания, друг кирий! Удачи!', 38134),
(205078, 'ruRU', '', 'Надеюсь, тебе и твоим медиумам пойдут на пользу наши уроки терпения, единения и взаимопомощи.', 38134),
(205077, 'ruRU', '', 'В любое время, когда пожелаешь, $n. Мои корни тянутся глубоко.', 38134),
(205073, 'ruRU', '', 'Меня окружили одержимые! Я не знала, что делать!', 38134),
(205069, 'ruRU', 'А это за что? Мне было так весело!', '', 38134),
(205063, 'ruRU', '', 'Бедный Гамми так расстроен! Его ничто не успокаивает.', 38134),
(205062, 'ruRU', '', 'Помоги моим союзникам, их терзают сомнения. Скажи им, чтобы они возвращались. Здесь они исцелятся.', 38134),
(205059, 'ruRU', '', 'После того, как филактерию Тран\'тиок нашли, она спрятала ее у дома Ритуалов. Найди ее... она пригодится нам обоим.', 38134),
(205056, 'ruRU', 'Что тебя тревожит, смертная душа?', '', 38134),
(205052, 'ruRU', '', 'Не в этот раз, Тран\'тиок!', 38134),
(205051, 'ruRU', '', 'Тебя ждет вечная агония. Я сломаю твою волю и узнаю, кому из моих врагов ты служишь...', 38134),
(205050, 'ruRU', '', 'Еще один глупый предатель угодил в мою ловушку!', 38134),
(205028, 'ruRU', 'Твой принц меньше, чем я думал.', 'Твой принц меньше, чем я думала.', 38134),
(205027, 'ruRU', 'Мои заклинания справятся с любой преградой.', 'Мои заклинания справятся с любой преградой.', 38134),
(205026, 'ruRU', 'Я слышал, как гости шептались о \"запретной библиотеке\" недалеко отсюда.\n\nПозволь спросить, что в ней хранится?', 'Я слышала, как гости шептались о \"запретной библиотеке\" недалеко отсюда.\n\nПозволь спросить, что в ней хранится?', 38134),
(205025, 'ruRU', 'Завидев меня, все враги разбегаются в страхе.', 'Завидев меня, все враги разбегаются в страхе.', 38134),
(205024, 'ruRU', 'Мы многому можем друг у друга научиться.', 'Мы многому можем друг у друга научиться.', 38134),
(205023, 'ruRU', 'Твое царство интригует меня.', 'Твое царство интригует меня.', 38134),
(205022, 'ruRU', 'Твои каменные друзья любят пообщаться.', 'Твои каменные друзья любят пообщаться.', 38134),
(205021, 'ruRU', 'Я готов служить.', 'Я готова служить.', 38134),
(205004, 'ruRU', 'Хорошо снова оказаться в Ревендрете. Я всегда уважал вентиров и их камнерожденных.', 'Хорошо снова оказаться в Ревендрете. Я всегда уважала вентиров и их камнерожденных.', 38134),
(205003, 'ruRU', 'Спорю, что уложу двух или даже трех твоих больших землероев.', 'Спорю, что уложу двух или даже трех твоих больших землероев.', 38134),
(205002, 'ruRU', 'А где бойцовский клуб? У вас же тут есть бойцовский клуб?', 'А где бойцовский клуб? У вас же тут есть бойцовский клуб?', 38134),
(204999, 'ruRU', 'Тут слишком много болтают и слишком мало дерутся.', 'Тут слишком много болтают и слишком мало дерутся.', 38134),
(204998, 'ruRU', 'Если будут проблемы, сразу беги ко мне.', 'Если будут проблемы, сразу беги ко мне.', 38134),
(204997, 'ruRU', 'Когда начнется бой, я буду там.', 'Когда начнется бой, я буду там.', 38134),
(204996, 'ruRU', 'Хочешь, чтобы я кого-нибудь убил?', 'Хочешь, чтобы я кого-нибудь убила?', 38134),
(204995, 'ruRU', 'Я не привык слушаться вентиров.', 'Я не привыкла слушаться вентиров.', 38134),
(204978, 'ruRU', '<Огромный камнерожденный молча смотрит на вас, ожидая приказов.>', '', 38134),
(204961, 'ruRU', 'Грехопад выстоит.', 'Грехопад выстоит.', 38134),
(204960, 'ruRU', 'Да, пилигрим?', 'Да, пилигрим?', 38134),
(204959, 'ruRU', 'Новые указания?', 'Новые указания?', 38134),
(204958, 'ruRU', 'Генерал шлет свои лучшие пожелания.', 'Генерал шлет свои лучшие пожелания.', 38134),
(204957, 'ruRU', 'За Грехопад.', 'За Грехопад.', 38134),
(204956, 'ruRU', 'Я готов.', 'Я готова.', 38134),
(204955, 'ruRU', 'Чем могу служить?', 'Чем могу служить?', 38134),
(204954, 'ruRU', 'Что тебе нужно?', 'Что тебе нужно?', 38134),
(204889, 'ruRU', 'Извилистые коридоры', 'Извилистые коридоры', 38134),
(204888, 'ruRU', 'Верхний ярус', 'Верхний ярус', 38134),
(204883, 'ruRU', 'Междумирье Бессердечных', 'Междумирье Бессердечных', 38134),
(204882, 'ruRU', 'Кузни душ', 'Кузни душ', 38134),
(204881, 'ruRU', 'Костоломни', 'Костоломни', 38134),
(204879, 'ruRU', 'Я обо всем позабочусь, только скажите.\n\nЧем я могу вам служить сегодня?', '', 38134),
(204876, 'ruRU', 'Ничего цвет. В самый раз для дворецкого, да?', '', 38134),
(204875, 'ruRU', 'Какую краску мы кинем в лужу, чтобы сделать твоего землероя-дворецкого?\n\nПотом его можно перекрасить, но для землероя в этом мало приятного, так что подумай как следует.', '', 38134),
(204874, 'ruRU', 'Хочешь создать нового землероя?\n\nПусть он будет похож на меня! Я лучше всех!', '', 38134),
(204873, 'ruRU', 'На меня сегодня почему-то напала хандра...', '', 38134),
(204872, 'ruRU', 'Новые землерои сами себя не сделают...', '', 38134),
(204870, 'ruRU', 'В этом семени больше нет анимы.', 'В этом семени больше нет анимы.', 38134),
(204868, 'ruRU', 'Я скучаю по Каменному Дозору. \n\nТочнее по тому, каким он был раньше... До того, как там все испортил наш новый \"Жнец\".', '', 38134),
(204867, 'ruRU', 'Нас обоих изгнали сюда за то, что мы \"утаивали запасы\" анимы в Каменном Дозоре. \n\nПо мне, так оставить себе ровно столько анимы, чтобы не угаснуть, это не \"утаивание запасов\". Это выживание.\n\nКазначей такой же мерзавец, как Денатрий.', '', 38134),
(204865, 'ruRU', 'Пепельный Предел – суровое место. Хотел бы я убраться отсюда подальше.\n\nНо мы вынуждены оставаться здесь, в этом убежище, потому что Свет обжигает нашу кожу. Эту боль невозможно терпеть.', '', 38134),
(204863, 'ruRU', 'Теперь все не кажется таким мрачным. Спасибо, $r.', '', 38134),
(204862, 'ruRU', 'У нас все получилось! Свет больше не угрожает нашему существованию.', '', 38134),
(204852, 'ruRU', 'Может быть, он еще научится!', '', 38134),
(204851, 'ruRU', 'Но вместо этого у нас будет землерой.', '', 38134),
(204850, 'ruRU', 'Запомни: хороший дворецкий должен быть умным, харизматичным и приятным в общении.', '', 38134),
(204840, 'ruRU', 'Меня прошлый босс ел поедом, сколько себя помню. Живьем жрал! \n\nДа, следы от зубов не видны. Они так кусаются, чтобы другие потом не видели. Для модного вентира это типа стыдно.\n\nА потом ты моего старого босса тогось. На вечный покой. Я понимаю, что ты это не ради меня, но все равно спасибочки. Вот, надо тебя отблагодарить.', '', 38134),
(204839, 'ruRU', 'Магси заработал на твоей гонке пару рубинов.', '', 38134),
(204837, 'ruRU', 'Смертное создание.', '', 38134),
(204835, 'ruRU', 'Твой повелитель довольно взыскателен. Потребуется некоторое время, чтобы собрать нужные ему реагенты.\n\nОднако, возможно, я смогу ускорить процесс, если ты поможешь добыть самые редкие.', '', 38134),
(204834, 'ruRU', 'Что мне достать для тебя, аколит?', '', 38134),
(204827, 'ruRU', '', 'Встретимся в Ревендрете, пилигрим. Мы найдем источник предательства... и вырвем его с корнем.', 38134),
(204823, 'ruRU', 'От этих разговоров старые кости пересыхают. А гоняя на арену бойцов, можно и обезвоживание заработать.', '', 38134),
(204814, 'ruRU', 'Что ж, по крайней мере, ты не отрицаешь своей глупости.\n\nЯ не могу просто так позволить тебе снова присоединиться к нам. Это вопрос моей чести.\n\nЕсли хочешь загладить оскорбление, докажи, что от тебя есть польза. У меня как раз есть задание, которое тебе по силам.\n\nЖелаешь ли ты искупить свою вину и снова стать вентиром?', '', 38134),
(204813, 'ruRU', 'Ты смеешь приходить ко мне и задавать этот вопрос?\n\nЕсли бы не соглашение о нейтралитете, я бы с удовольствием тебя прикончил.\n\nОтчего ты думаешь, что мы примем тебя с распростертыми объятиями?', '', 38134),
(204812, 'ruRU', 'Меня радует твоя твердость, пилигрим Утробы.\n\nМы уже слышали про твои подвиги.\n\nТогда, раз ты не отступаешься от своего решения, поклянись в верности вентирам.', '', 38134),
(204811, 'ruRU', 'Такое решение нельзя принимать сгоряча, $n. \n\nТебе нужно точно знать, что ты хочешь вступить в наши ряды.\n\nТы по-прежнему желаешь осуществить свое намерение?', '', 38134),
(204810, 'ruRU', 'Я не ошибся? Ты ведь уже принадлежишь к другому ковенанту?\n\nДаже если ты завидуешь нам, вентирам, предать своих нынешних союзников – серьезный шаг.', '', 38134),
(204809, 'ruRU', 'Чем я могу помочь пилигриму Утробы?', '', 38134),
(204802, 'ruRU', 'Ты аколит барона, а не мой, $n.\n\nПомни об этом и поменьше докучай мне.', '', 38134),
(204799, 'ruRU', 'Камень! Камень – наше спасение.', '', 38134),
(204798, 'ruRU', 'Тебе удалось сбежать от Тюремщика... да, точно.', '', 38134),
(204795, 'ruRU', 'Утроба... Я помню... Будто это было так давно.', '', 38134),
(204793, 'ruRU', 'Я раздам это нашим воинам. Давай обсудим меры безопасности.', '', 38134),
(204784, 'ruRU', '', 'Значит, теперь ты хочешь к нам вернуться?\n\nБоюсь, я не могу просто взять и пропустить тебя к Сердцу леса.\n\nПридется доказать готовность служить нашему делу.\n\nК счастью, у меня как раз найдется подходящее задание, чтобы тебя испытать.\n\nТы сможешь вновь доказать нам свою преданность?', 38134),
(204783, 'ruRU', '', 'А вот и наше разочарование. \n\nНе ожидала, что ты посмеешь снова явиться сюда, $n.\n\nНе думай, что мы тебя простили.', 38134),
(204782, 'ruRU', '', 'Похоже, тебя не переубедить.\n\nИ с чего мне в тебе сомневаться? Тебе уже удалось доказать свою верность нашей королеве. Уверена, ты справишься и на этот раз.\n\nТак что? Ты посвятишь свою душу служению ночному народцу и нашей дорогой королеве?', 38134),
(204781, 'ruRU', 'Это не то, что я бы выбрал... но буду довольствоваться и этим.', 'Это не то, что я бы выбрала... но буду довольствоваться и этим.', 38134),
(204780, 'ruRU', '', 'Ты точно сможешь это сделать, $n? \n\nТакое решение способно нарушить баланс жизненного цикла. Прежде чем мы продолжим разговор, я должна убедиться, что ты действительно этого хочешь.', 38134),
(204778, 'ruRU', '', 'Вот как? \n\nЧто ж, мы благодарны тебе за помощь, оказанную нам в прошлом, но сейчас ты уже состоишь в другом ковенанте.\n\nТы решишься предать его и присоединиться к нам?', 38134),
(204777, 'ruRU', 'Вот так, готово! Только, м-м, постарайся не совершать резких движений. И ни в коем случае не приближайся к пожирателям. Ну, ни пуха!', '', 38134),
(204776, 'ruRU', '', 'Чем я могу помочь пилигриму Утробы?', 38134),
(204775, 'ruRU', '', 'Мы уже предлагали тебе свою силу и получили отказ.\n\nПочему мы вдруг должны об этом забыть? Что ты скажешь в свое оправдание?', 38134),
(204773, 'ruRU', 'Не вопрос! Вот моя почти самая лучшая щетка. Зверье обожает, когда им шкуру чешут.', '', 38134),
(204772, 'ruRU', 'Магия не укроет тебя от моих глаз.', '', 38134),
(204771, 'ruRU', 'Я тебя вижу...', '', 38134),
(204770, 'ruRU', 'Что это за уловка?', '', 38134),
(204767, 'ruRU', '', 'Тебе придется постараться, чтобы заслужить наше прощение. Ты сможешь это сделать?\n\nНе жди, что мы встретим тебя с распростертыми объятиями. Нет, не в этот раз. Теперь нам понадобится доказательство твоей преданности.\n\nТы сможешь показать нам, чего стоишь?', 38134),
(204765, 'ruRU', 'Твоя воля крепка.\n\nИз тебя выйдет славный некролорд. Для этого тебе придется посвятить всю свою силу и мощь нашей цели.\n\nКлянись!', 'Твоя воля крепка.\n\nИз тебя выйдет славный некролорд. Теперь тебе нужно посвятить свои силу и мощь нашей цели.\n\nПоклянись нам в верности!', 38134),
(204764, 'ruRU', 'Ступив на этот путь, ты покинешь свой нынешний ковенант.\n\nЯ не осуждаю желание обзавестись союзниками посильнее, но не думаю, что твои старые друзья хорошо воспримут такое дезертирство.\n\nТы точно хочешь это сделать?', 'Ступив на этот путь, ты покинешь своих нынешних союзников.\n\nЯ не осуждаю желание обзавестись друзьями посильнее, но не думаю, что твои старые друзья хорошо воспримут такое предательство.\n\nТы точно хочешь продолжить этот разговор?', 38134),
(204750, 'ruRU', 'Да, сойдет. Развлечения готовы.', '', 38134),
(204746, 'ruRU', 'Из этого получаются красивые штуки. Декорации готовы!', '', 38134),
(204745, 'ruRU', '', 'У всех в Ревендрете есть важное предназначение, даже если многие вентиры об этом забыли.', 38134),
(204744, 'ruRU', '', 'И не спрашивай, откуда у меня эта вещица. Считай, это жестом доброй воли... и кстати, теперь ты у меня в долгу. С нетерпением жду дальнейшего сотрудничества, пилигрим Утробы.', 38134),
(204742, 'ruRU', '', 'И поскольку я предугадала твою просьбу, я предусмотрительно обзавелась устройством, которое открывает портал прямиком в башню.', 38134),
(204741, 'ruRU', '', 'Обойдусь без стандартных предупреждений: твое уникальное сочетание безрассудности и отваги делает их бессмысленными.', 38134),
(204740, 'ruRU', 'Мне очень нужен этот свиток, чтобы закончить исследования.\n\nПросто скажи баронессе, что тебе не удалось его найти.', 'Мне очень нужен этот свиток, чтобы закончить исследования.\n\nПросто скажи баронессе, что тебе не удалось его найти.', 38134),
(204739, 'ruRU', 'Да, свиток еще у меня.\n\nИ что именно ты собираешься предпринять?', 'Да, свиток еще у меня.\n\nИ что именно ты собираешься предпринять?', 38134),
(204738, 'ruRU', 'О, это? Я почти закончил.\n\nПросто скажи Нинадар, что мне этот свиток пока что нужен.', 'О, это? Я почти закончила.\n\nПросто скажи Нинадар, что мне этот свиток пока что нужен.', 38134),
(204737, 'ruRU', '', 'Значит, Торгаст? Когда я сказала тебе, что там держат твоих друзей, то сразу поняла, что ты всенепременно придешь ко мне за помощью.', 38134),
(204735, 'ruRU', 'Не видишь, я занят, аколит?', 'Не видишь, я занята, аколит?', 38134),
(204734, 'ruRU', 'Тебе разве не нужно готовиться к ритуалу?', 'Тебе разве не нужно готовиться к ритуалу?', 38134),
(204733, 'ruRU', 'В чем дело, аколит?', 'В чем дело, аколит?', 38134),
(204717, 'ruRU', '<Вы замечаете небольшого каменного нетопыря, свисающего со святилища.\n\nСудя по всему, его можно разбудить с помощью определенного количества насыщенных рубинов. \n\nЕсли вы это сделаете, он наверняка будет вас слушаться.>', '<Вы замечаете небольшого каменного нетопыря, свисающего со святилища.\n\nСудя по всему, его можно разбудить с помощью определенного количества насыщенных рубинов. \n\nЕсли вы это сделаете, он наверняка будет вас слушаться.>', 38134),
(204716, 'ruRU', 'Мы не должны терять бдительность.', '', 38134),
(204714, 'ruRU', 'По твоему приказу, пилигрим Утробы.', '', 38134),
(204707, 'ruRU', '', 'Ах да, я помню, как барон попросил несколько томов из моей библиотеки. Странная подборка, если мне не изменяет память.\n\nКак видишь, я очень занята. Но если ты возьмешь на себя часть моей работы, может, я и поищу для тебя эти книги.', 38134),
(204706, 'ruRU', '', 'Надеюсь, ты отрываешь меня от дел ради чего-то важного, аколит.\n\nИтак, в чем дело?', 38134),
(204705, 'ruRU', '', 'Ах, Рогана, я получила множество комплиментов за платье, которое ты для меня сшила.', 38134),
(204704, 'ruRU', 'Это один из моих любимых грехов, дорогая Рогана.', '', 38134),
(204703, 'ruRU', 'Удачи. Не попадись в собственную сеть!', '', 38134),
(204702, 'ruRU', '', 'Отнеси это в обитель, пилигрим Утробы. Быть может, баронесса Дрека или кто-то еще знает больше об этом предмете.', 38134),
(204701, 'ruRU', '<Тюльпановый Нос хихикает.>', '', 38134),
(204700, 'ruRU', 'Осторожнее, пилигрим... Будет жаль, если это попадет на твою одежду.', '', 38134),
(204699, 'ruRU', '', 'Вот! Фрагмент доспехов, выкованных с невероятной искусностью. Я не видела ничего подобного даже в кузнях дома Избранных.', 38134),
(204698, 'ruRU', 'И как эти мошки оказались при твоем Дворе, пилигрим?\n\nПонятия не имею...\n\nКак хорошо, что у меня при себе были инструменты как раз для такой непредвиденной ситуации!\n\nБери на здоровье.\n\n<Тюльпановый Нос хихикает.>', '', 38134),
(204695, 'ruRU', 'Идем, Темель! Ночной народец умеет веселиться!', '', 38134),
(204694, 'ruRU', 'Не знаю, из-за чего я нервничаю больше: из-за жуков или ночного народца...', '', 38134),
(204692, 'ruRU', 'Пойдем со мной, аколит. Насладись плодами своего труда.', '', 38134),
(204691, 'ruRU', 'Приготовься, Нелезида. Близится час нашего триумфа.', '', 38134),
(204690, 'ruRU', 'Чем я могу помочь пилигриму Утробы?', '', 38134),
(204689, 'ruRU', 'Твоя сила и находчивость известны всем. \n\nМне будет радостно видеть тебя среди некролордов в нашей борьбе за Малдраксус, но ты уже находишься в союзе с другим ковенантом.\n\nНеужели ты его покинешь?', '', 38134),
(204684, 'ruRU', 'Думаю, ты поймешь, если мы не сможем поверить тебе на слово.\n\nТы сможешь приложить усилия, чтобы заслужить возможность вновь присоединиться к нам?', '', 38134),
(204681, 'ruRU', 'Здравствуй, $n.\n\nТебе не занимать уверенности в себе, раз ты снова стоишь передо мной.\n\nМы не забыли о твоем предательстве.', '', 38134),
(204677, 'ruRU', 'Осторожно! Он нас заметил!', '', 38134),
(204676, 'ruRU', '', 'Не приставай к гостям, Подд. $p явно задумывает что-то особенное.', 38134),
(204675, 'ruRU', 'Ты красивше, чем куча грязи!', '', 38134),
(204674, 'ruRU', '', 'Если этот костюм тебе надоест, на банкете после приема я могу тебя переодеть.', 38134),
(204673, 'ruRU', '', 'Мудрый выбор. Обезоруживает и очаровывает всех, кого надо.', 38134),
(204672, 'ruRU', 'Я бы тоже это выбрал!', '', 38134),
(204667, 'ruRU', '', 'Пусть твое оружие бьет в точно цель.', 38134),
(204666, 'ruRU', 'Дело сделано. Охотиться с тобой было честью для меня.', '', 38134),
(204665, 'ruRU', '', 'Мы готовы, $n. Начинай.', 38134),
(204664, 'ruRU', 'Чем могу помочь?', '', 38134),
(204658, 'ruRU', 'Я тебе верю.\n\nА теперь тебе нужно принести клятву верности. Так ты станешь членом ковенанта кирий.\n\nПовторяй за мной...\n\n\"Я буду служить ковенанту кирий верой и правдой\".', '', 38134),
(204657, 'ruRU', 'Я должен спросить у тебя еще раз.\n\nТы точно хочешь присоединиться к ковенанту кирий? Сделав это, ты предашь своих нынешних союзников.\n\nНе думаю, что они скоро об этом забудут. \n\nТы точно присоединишься к нам?', '', 38134),
(204656, 'ruRU', 'Ты уже принадлежишь к другому ковенанту, $n. Ты точно хочешь пойти по нашему пути?\n\nМы не забыли о твоей помощи Бастиону и охотно примем тебя в наши ряды. \n\nНо сможешь ли ты предать своих нынешних союзников?', '', 38134),
(204653, 'ruRU', 'Помолчи, старая жижа... Она, наверное, меня и не помнит.', 'Помолчи, старая жижа... Она, наверное, меня и не помнит.', 38134),
(204652, 'ruRU', 'Я слышал, что Куратор тоже пришла сюда, Фредрик...', 'Я слышала, что Куратор тоже пришла сюда, Фредрик...', 38134),
(204651, 'ruRU', 'Смотри клыки не обломай. В этом склепе тебя ждет лишь разбитое сердце.', 'Смотри клыки не обломай. В этом склепе тебя ждет лишь разбитое сердце.', 38134),
(204650, 'ruRU', 'Какие у тех сильваров рога! Довольно элегантные...', 'Какие у тех сильваров рога! Довольно элегантные...', 38134),
(204649, 'ruRU', 'Ты старый камень грехов, Гарлоу! Это гиблое дело...', 'Ты старый камень грехов, Гарлоу! Это гиблое дело...', 38134),
(204648, 'ruRU', 'Кажется, я видел на сцене Обвинительницу. Она мне всегда нравилась...', 'Кажется, я видела на сцене Обвинительницу. Она мне всегда нравилась...', 38134),
(204647, 'ruRU', 'Непременно.', 'Непременно.', 38134),
(204646, 'ruRU', 'Давай покрасуемся перед Теотаром и леди Илинкой? Пусть позавидуют!', 'Давай покрасуемся перед Теотаром и леди Илинкой? Пусть позавидуют!', 38134),
(204645, 'ruRU', 'Благодарю, Фредрик. Мой стиль тебе тоже идет.', 'Благодарю, Фредрик. Мой стиль тебе тоже идет.', 38134),
(204644, 'ruRU', 'И, Гарлоу, тебе очень идет мой стиль одежды.', 'И, Гарлоу, тебе очень идет мой стиль одежды.', 38134),
(204643, 'ruRU', '', 'Это невозможно!', 38134),
(204641, 'ruRU', 'О, обожаю скандальные сцены!', '', 38134),
(204640, 'ruRU', 'Я видел тебя в гробу, Фредерик!', 'Я видела тебя в гробу, Фредерик!', 38134),
(204639, 'ruRU', 'Сегодня прямо на наших глазах изваяют статую, которая украсит Грехопад!', '', 38134),
(204638, 'ruRU', 'Отлично. Прибыл скульптор. Особый сюрприз для наших гостей.', '', 38134),
(204632, 'ruRU', 'Я его первый нашел, Фредрик! Это ты копируешь наряды!', 'Я его первая нашла, Фредрик! Это ты копируешь наряды!', 38134),
(204626, 'ruRU', 'Какую статую мне возвести?', '', 38134),
(204623, 'ruRU', 'Раскольники непреклонны в поисках наших знаний. Даже сейчас они ищут древние свитки, в которых собрана вся мудрость нашего идеала. \n\nНе знаю, зачем они им понадобились, но явно не для обучения.\n\nПрошу, раздобудь эти свитки. Мой послушник отнесет тебя к ним.', 'Раскольники непреклонны в поисках наших знаний. Даже сейчас они ищут древние свитки, в которых собрана вся мудрость нашего идеала. \n\nНе знаю, зачем они им понадобились, но явно не для обучения.\n\nПрошу, раздобудь эти свитки. Мой послушник отнесет тебя к ним.', 38134),
(204621, 'ruRU', 'Наши крылья защищают Темные Земли от хаоса. Следуй вечному предназначению.', 'Наши крылья защищают Темные Земли от хаоса. Следуй вечному предназначению.', 38134),
(204620, 'ruRU', 'От... отойди!\n\nА, так ты не собираешься заставлять меня издеваться над пергаментом? \n\nВентиры отобрали у меня чернила за то, что я не хочу записывать их \"исследования\". Если их вообще можно так назвать.\n\nПоможешь мне? Я ничто без своих чернил!', 'От... отойди!\n\nА, так ты не собираешься заставлять меня издеваться над пергаментом? \n\nВентиры отобрали у меня чернила за то, что я не хочу записывать их \"исследования\". Если их вообще можно так назвать.\n\nПоможешь мне? Я ничто без своих чернил!', 38134),
(204618, 'ruRU', 'Помни, чему тебя учили. Не сходи с пути. Верь в бесконечную мудрость Арбитра.', 'Помни, чему тебя учили. Не сходи с пути. Верь в бесконечную мудрость Арбитра.', 38134),
(204617, 'ruRU', 'Ты будешь переправлять тысячи душ. Некоторые ноши покажутся тяжелее других.', 'Ты будешь переправлять тысячи душ. Некоторые ноши покажутся тяжелее других.', 38134),
(204616, 'ruRU', 'Остальное доверь Арбитру и Вечному Пути. В этом наш долг.', 'Остальное доверь Арбитру и Вечному Пути. В этом наш долг.', 38134),
(204615, 'ruRU', 'Увидев последние мгновения ее жизни, ты лишь вправе решать, готова ли душа покинуть мир смертных.', 'Увидев последние мгновения ее жизни, ты лишь вправе решать, готова ли душа покинуть мир смертных.', 38134),
(204614, 'ruRU', 'Не поддавайся желанию осудить. Все души предстанут перед Арбитром и получат то, чего заслуживают.', 'Не поддавайся желанию осудить. Все души предстанут перед Арбитром и получат то, чего заслуживают.', 38134),
(204613, 'ruRU', 'Не ставь себя на их место... Это их существование, а не твое.', 'Не ставь себя на их место... Это их существование, а не твое.', 38134),
(204612, 'ruRU', 'Заглянуть в жизнь души – это понять ее как личность, познать суть, посмотреть на нее открыто и непредвзято.', 'Заглянуть в жизнь души – это понять ее как личность, познать суть, посмотреть на нее открыто и непредвзято.', 38134),
(204611, 'ruRU', 'Лишь исключив себя из судеб остальных, ты сможешь познать их истинную суть.', 'Лишь исключив себя из судеб остальных, ты сможешь познать их истинную суть.', 38134),
(204610, 'ruRU', 'Молодец, $n. Давай поговорим о твоей награде.', '', 38134),
(204609, 'ruRU', '', 'Больше этот горм не будет пожирать наши драгоценные деревья.', 38134),
(204608, 'ruRU', '', 'Новое заклинание! Вот бы его опробовать!', 38134),
(204604, 'ruRU', '', 'Мы вернем посох к Престолу. Там он будет служить напоминанием о нашей победе.', 38134),
(204601, 'ruRU', '', 'Говорят, что когда Примас исчез, его доспехи оказались разбросанными по всему Малдраксусу. Найдем остальные части – во славу ковенанта!', 38134),
(204588, 'ruRU', 'Как говорится, жижа к жиже...', '', 38134),
(204587, 'ruRU', 'Когда-нибудь ты тоже закончишь дни в луже жижи. Как все мы.', '', 38134),
(204580, 'ruRU', '', 'Тихо. Загнать этого горма в пещеру было очень непросто.', 38134),
(204579, 'ruRU', '', 'Альдерин, ты здесь! Хорошо. Чудище ранено, но пока его панцирь цел, я не могу ничего сделать.', 38134),
(204578, 'ruRU', 'Кажется, мы пришли.', '', 38134),
(204567, 'ruRU', 'Так не было... предначертано...', 'Так не было... предначертано...', 38134),
(204566, 'ruRU', 'Малдраксус... будет нашим...', 'Малдраксус... будет нашим...', 38134),
(204565, 'ruRU', 'Наш дом... вечен...', 'Наш дом... вечен...', 38134),
(204564, 'ruRU', 'Кто завершит... мои исследования?', 'Кто завершит... мои исследования?', 38134),
(204563, 'ruRU', 'Чужаки! Уничтожить!', 'Чужаки! Уничтожить!', 38134),
(204562, 'ruRU', 'Тебе не скрыться от нашей магии!', 'Тебе не скрыться от нашей магии!', 38134),
(204561, 'ruRU', 'Ты пожалеешь об этом вторжении!', 'Ты пожалеешь об этом вторжении!', 38134),
(204560, 'ruRU', 'Твой труп отлично мне послужит!', 'Твой труп отлично мне послужит!', 38134),
(204559, 'ruRU', '', 'Моя смерть... ничего не изменит...', 38134),
(204558, 'ruRU', '', 'Ты еще увидишь!', 38134),
(204557, 'ruRU', '', 'Творения господина изменят Темные Земли!', 38134),
(204556, 'ruRU', '', 'Как ты смеешь прерывать мой ритуал?!', 38134),
(204554, 'ruRU', 'Я не трачу время на слабовольных существ, но если настаиваешь, я покажу тебе истинную боль.', '', 38134),
(204553, 'ruRU', 'Пусть защиту Арденвельда возглавят небесные создания.', '', 38134),
(204552, 'ruRU', 'Хотя существа Арденвельда и выглядят безобидно, они готовы защищать свою территорию с силой и отвагой величайших героев. А ты справишься?', '', 38134),
(204548, 'ruRU', '', 'Катализатор количества увеличит объем ресурсов, которыми наградит тебя восстановленная душа. \n\nЕсли в саду достаточно места, ты можешь посадить несколько катализаторов количества.', 38134),
(204546, 'ruRU', '', 'Катализаторы скорости ускоряют рост дикого семени. \n\nЕсли в саду достаточно места, ты можешь посадить несколько катализаторов скорости, и их эффект будет суммироваться.', 38134),
(204545, 'ruRU', '', 'С помощью катализаторов ты можешь влиять на рост дикого семени.\n\nКатализатор можно посадить на специальном участке в саду. После этого направление потока анимы укажет тебе, на какие дикие семена он влияет. \n\nКатализатор, посаженный рядом с уже растущим семенем, никак не сможет на него повлиять. \n\nСуществуют разные виды катализаторов. Каждый из них по-своему влияет на дикое семя.', 38134),
(204543, 'ruRU', 'Пока что нельзя с уверенностью сказать, какую роль подготовило нам Предназначение.\n\nЭто ощущение хорошо известно тебе, но совершенно незнакомо нам.', '', 38134),
(204542, 'ruRU', 'Великолепный вид. \n\nПусть даже Утроба отвергает Предназначение, она все равно неотъемлемая часть этого мира.', '', 38134),
(204541, 'ruRU', 'Утроба кипит от накопившейся силы, но не служит Предназначению. \n\nПредназначение испытывает нас.', '', 38134),
(204540, 'ruRU', 'У всех у нас своя роль.\n\nТак говорит Предназначение.\n\nКогда мы знали смысл своего служения, верить было легче. А теперь? Я уже не знаю.\n\nНельзя терять веру, я так полагаю. Надо довериться Предназначению.\n\nПредназначение ведет нас.', '', 38134),
(204539, 'ruRU', 'Предназначение иногда дает возможность взглянуть на все шире.', '', 38134),
(204538, 'ruRU', 'К чему же приведут эти перемены? Предназначение всегда указывало нам путь.\n\nТо есть все это свершается по воле его. Я только не знаю, какова конечная цель.\n\nДаже покорный слуга хочет понимать смысл своего служения.', '', 38134),
(204537, 'ruRU', 'Добро пожаловать, пилигрим Утробы.\n\nНе все, что ты видишь, продается. Мы также любим коллекционировать.\n\nВ твоем мире мы нашли таких любопытных обитателей. Мне нравится пополнять свою коллекцию.', '', 38134),
(204536, 'ruRU', 'Добро пожаловать, пилигрим Утробы!\n\nЭто устройство дает доступ к хранилищу осколков из разных измерений, отделяющих этот мир от твоего. В отличие от наших уважаемых служителей с их драгоценным Предназначением, я знаю, как оно работает.\n\nНо тебе не скажу. Профессиональная тайна, понимаешь ли. \n\nОднако можешь пользоваться им в любое время. Брокеры щедры.', '', 38134),
(204535, 'ruRU', '', 'Клянусь пятью домами... Наплечья Примаса!', 38134),
(204534, 'ruRU', '', 'Отнеси доспехи Примаса в нашу обитель, пилигрим. Там мы воздадим этой реликвии должные почести.', 38134),
(204533, 'ruRU', '', 'Лич угрожает обоим нашим мирам. Когда войска будут готовы нанести удар, мы отправим в Ревендрет призыв к наступлению.', 38134),
(204532, 'ruRU', '', 'Не дерзи, падший принц! Прежде чем бездумно рваться в бой, нужно узнать побольше о нашем противнике.', 38134),
(204531, 'ruRU', 'Я заставлю Кел\'Тузада заплатить за все! Баронесса Дрека! Я требую, чтобы меня сопроводили в Малдраксус!', '', 38134),
(204530, 'ruRU', 'Неважно... Дом Ритуалов служит мне. Сразитесь со мной там, если посмеете!', '', 38134),
(204529, 'ruRU', 'Мерзавцы! Вы посмели сразить мою прислужницу?!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204528, 'ruRU', '', 'У этого лича был источник невообразимой силы...', 38134),
(204527, 'ruRU', 'Величайший монарх в истории твоего народа стоит перед тобой в цепях... Какой позор.', '', 38134),
(204526, 'ruRU', 'Что ты тут поделываешь, дружище?', '', 38134),
(204516, 'ruRU', 'Я? Нет, конечно. А вот пилигрим Утробы... вполне возможно.', '', 38134),
(204515, 'ruRU', '', 'Графиня? Лорд Гарридан, ты уверен, что сможешь убедить ее явиться к нам?', 38134),
(204514, 'ruRU', 'Надеюсь, он возьмет с собой Испепелитель. Это будет потрясающее зрелище!', '', 38134),
(204513, 'ruRU', '', 'О! Мне не терпится встретить Александроса Могрейна из Малдраксуса...', 38134),
(204506, 'ruRU', 'Глазгор к бою готов!', '', 38134),
(204503, 'ruRU', '', 'В таком ужасном месте не положено проходить знаменитым битвам, но, увы, нам как всегда везет. Поторопись.', 38134),
(204500, 'ruRU', '', 'Мы следуем заветам Примаса! И мы спасем Темные Земли – вместе!', 38134),
(204499, 'ruRU', '', 'Пилигрим, тебе удалось выявить истинного врага Малдраксуса. А теперь верни наплечье законному владельцу.', 38134),
(204497, 'ruRU', 'Спасибо, пилигрим Утробы. Я признателен за приглашение на прием принца Ренатала.', '', 38134),
(204484, 'ruRU', 'Что ты хочешь продемонстрировать, $n?', 'Что ты хочешь продемонстрировать, $n?', 38134),
(204474, 'ruRU', '', 'Мой долг – защищать придворного посла, но до меня иногда доходят слухи об угрозах, с которыми нужно разобраться.', 38134),
(204472, 'ruRU', 'Ты, вероятно, думаешь: \"О нет! Этот гном застрял в странном сгустке слизи! Ему нужно помочь!\" Как бы не так! Трудно поверить, но я сам решил сюда забраться. Теперь я инкогнито, замаскирован, под прикрытием... Называй, как хочешь. Эта жижа – единственное, что может защитить меня в этом ужасном месте. К счастью, я обнаружил здесь множество чудесных боевых питомцев и собрал команду настоящих чемпионов. С ней я разобью всех противников и наконец-то добьюсь должного уважения! Правда, есть один нюанс: все мои вещи теперь липкие. Абсолютно все.', '', 38134),
(204471, 'ruRU', 'Гнилобрюх. Огрызок. Новые части. Ты дерись.', '', 38134),
(204470, 'ruRU', 'Я ждал достойного противника десятилетиями. Пускай победит сильнейший!', '', 38134),
(204469, 'ruRU', 'Даже в незначительных битвах надо сражаться честно и осмотрительно. Подготовь свою команду.', '', 38134),
(204466, 'ruRU', '', 'Мы очень серьезно относимся к обороне. Все мы – и маленькие, и большие – считаем своим долгом укреплять и тренировать защитников Бастиона.', 38134),
(204457, 'ruRU', 'Как тебе удалось выжить? Ты занимаешься весьма опасными вещами без защитного снаряжения. Вот, возьми запасное.', '', 38134),
(204455, 'ruRU', '', 'Наши союзники сейчас в некрополе.', 38134),
(204442, 'ruRU', '', 'Рыцари Черного Клинка готовы выступать.', 38134),
(204441, 'ruRU', 'Рыцари Черного Клинка готовы выступать.', '', 38134),
(204439, 'ruRU', 'В твое отсутствие многое изменилось, |3-6($c).\n\nПозволь мне ввести тебя в курс дела.', '', 38134),
(204438, 'ruRU', '', 'В твое отсутствие многое изменилось, |3-6($c).\n\nПозволь мне ввести тебя в курс дела.', 38134),
(204435, 'ruRU', '', 'Похоже, твое предназначение связано с моим.', 38134),
(204431, 'ruRU', '', 'Какие чудесные просторные поля. Самое место для битвы. Ты ведь примешь в ней участие?', 38134),
(204357, 'ruRU', 'Тебе идет. Неплохо выглядишь... для смертной души.', 'Тебе идет. Неплохо выглядишь... для смертной души.', 38134),
(204356, 'ruRU', '', 'Самое время нанести удар! Вперед, пилигрим! Мы прикроем тебя!', 38134),
(204355, 'ruRU', '', 'Образ героя Орды... Я должна поверить, что это мое спасение? Или ты хочешь увидеть нашу схватку?', 38134),
(204349, 'ruRU', 'Фу, ну ты и зануда.', '', 38134),
(204346, 'ruRU', 'Пилигрим! Не хочешь погоняться за землероем?\n\nЯ вымазался как следует, но никто не хочет со мной играть!\n\nИли может, прыгнешь в лужу жижи, попаришь свои смертные кости?', '', 38134),
(204344, 'ruRU', '', 'Это будет интересно...', 38134),
(204338, 'ruRU', 'Ладно, как насчет этих?..', '', 38134),
(204320, 'ruRU', 'Думаю, мне подойдет какое-нибудь из них...', '', 38134),
(204317, 'ruRU', '$gГотов:Готова; вернуться в Орибос?', '', 38134),
(204316, 'ruRU', 'Какая наглость, Гарлоу! Прийти в том же наряде, что и я!', 'Какая наглость, Гарлоу! Прийти в том же наряде, что и я!', 38134),
(204311, 'ruRU', 'А если и не понравятся, то нам такие поклонники не нужны!', '', 38134),
(204310, 'ruRU', 'Ну конечно понравятся, Чиу. Они потрясающие!', '', 38134),
(204309, 'ruRU', '', 'Надеюсь, нашим поклонникам они понравятся.', 38134),
(204308, 'ruRU', 'Новые плакаты лежат в склепе. Чиу их сама рисовала.', '', 38134),
(204307, 'ruRU', 'Остается только вернуться к твоим союзникам и обдумать новый план.', '', 38134),
(204306, 'ruRU', 'Кел\'Тузад сбежал в некрополь. Вдвоем нам теперь с ним не справиться.', '', 38134),
(204305, 'ruRU', 'Скорее! Надеюсь, мы не опоздали!', '', 38134),
(204304, 'ruRU', 'Смотри... Следы боя ведут в покои маркграфини.', '', 38134),
(204303, 'ruRU', 'У тебя получилось! Я ни секунды в тебе не сомневался.', '', 38134),
(204302, 'ruRU', 'Тебе придется немного изловчиться, чтобы добраться до него.', '', 38134),
(204301, 'ruRU', 'Попробуй достать до рычага, забравшись по полкам.', '', 38134),
(204300, 'ruRU', 'Рычаг ты найдешь в конце коридора.', '', 38134),
(204299, 'ruRU', 'Рука? Да... да! Клянусь Примасом, это гениально!', '', 38134),
(204298, 'ruRU', 'Факел? Злодеи любят скрывать в них потайные рычаги, но в тюремных камерах такое случается редко.', '', 38134),
(204297, 'ruRU', 'Череп нам ничем не поможет – разве что если тебе захочется поговорить с кем-то... твоего уровня.', '', 38134),
(204296, 'ruRU', 'Моя магия годится для многого, но на цепи она не подействует. Продолжай искать.', '', 38134),
(204295, 'ruRU', 'Стража! Бросить это ничтожество в камеру! И приведите сюда предателя!', '', 38134),
(204294, 'ruRU', 'Так это твоих рук дело? А это у нас что?.. Око Балмедара!', '', 38134),
(204293, 'ruRU', 'Это невозможно! Но... Это еще что?!', '', 38134),
(204292, 'ruRU', 'Что... что происходит? Нет!', '', 38134),
(204291, 'ruRU', 'Кровь носителя откроет путь!', '', 38134),
(204290, 'ruRU', 'Барьер слабеет! Веками закрытый проход вот-вот откроется!', '', 38134),
(204289, 'ruRU', 'Да! Я вижу путь! Тьма Утробы взывает ко мне!', '', 38134),
(204288, 'ruRU', 'Да начнется ритуал! Направляйте аниму!', '', 38134),
(204287, 'ruRU', 'Будем надеяться, что они не окажутся для тебя фатальными.', '', 38134),
(204286, 'ruRU', 'Хотя я уверен, что ритуал провалится, у него могут быть... последствия.', '', 38134),
(204285, 'ruRU', 'Я не рассчитывал на то, что тебе придется участвовать в ритуале. Что ж, будем импровизировать.', '', 38134),
(204284, 'ruRU', 'Как прикажете, господин. Все будет исполнено!', '', 38134),
(204283, 'ruRU', 'Похоже, среди нас завелся предатель. Нам нужно действовать быстрее.', '', 38134),
(204282, 'ruRU', 'Его не удалось найти, господин.', '', 38134),
(204281, 'ruRU', 'Не трать мое время, говоря о челяди! Что с моим реагентом?', '', 38134),
(204280, 'ruRU', '...караван был разграблен. А сопровождающие его брокеры – жестоко убиты.', '', 38134),
(204279, 'ruRU', 'Принеси мне оставшиеся реагенты. У меня есть идея.', '', 38134),
(204278, 'ruRU', 'Таинственная доставка от брокеров? Очень интересно... Я должен узнать, что это!', '', 38134),
(204277, 'ruRU', 'Все, что потребует барон, должен сначала увидеть я. Посмотрим, что он хочет использовать.', '', 38134),
(204276, 'ruRU', 'Возможно, эти реагенты помогут нам разгадать суть ритуала.', '', 38134),
(204275, 'ruRU', 'Продолжай двойную игру. Посмотрим, что еще нам удастся узнать.', '', 38134),
(204274, 'ruRU', 'Вопреки нашим усилиям, мы так и не приблизились к разгадке.', '', 38134),
(204273, 'ruRU', 'Как она может помочь Кел\'Тузаду?', '', 38134),
(204272, 'ruRU', '\"Оковы Изгнанника\"? Это же простая книга по истории.', '', 38134),
(204271, 'ruRU', '', 'Благодаря тебе зимний сад полностью восстановлен.\n\nТеперь ты можешь посадить семя, с которым можно связать четыре катализатора. Так ты сможешь использовать весь потенциал этого сада.', 38134),
(204270, 'ruRU', 'Но что за барьер барон решил возвести... или преодолеть? Интересно...', '', 38134),
(204268, 'ruRU', '\"Барьеры и обереги. Том IV: контрзаклинания\". Довольно нудная книга.', '', 38134),
(204266, 'ruRU', 'Может быть, его ритуал как-то связан с проходами между мирами Смерти?', '', 38134),
(204265, 'ruRU', '\"Пути Предвечных\"? Странный выбор. В этой книге полно догадок и полуправды.', '', 38134),
(204264, 'ruRU', 'Что он надеется узнать из книги для новичков?', '', 38134),
(204263, 'ruRU', '\"Мистерия крыл\". Базовые знания о кирийском мистицизме.', '', 38134),
(204261, 'ruRU', 'Я вижу то, что не понимают раскольники.\n\nЧтобы добиться успеха, надо стремиться к совершенству! И прежде всего – заботиться о ресурсах и инструментах.', '', 38134),
(204259, 'ruRU', 'Само по себе это, увы, не предательство. Посмотрим, что там в этих его книгах.', '', 38134),
(204258, 'ruRU', 'Значит, барон принимает помощь вентиров? Это было бы вполне простительно, если бы он делился своей добычей.', '', 38134),
(204257, 'ruRU', 'Но хватит разговоров. Пора готовиться к ритуалу.', '', 38134),
(204256, 'ruRU', 'А запасы анимы, полученной от наших союзников в Ревендрете, откроют путь к нашему триумфу.', '', 38134),
(204255, 'ruRU', 'Это жалкое создание – ключ к местам великой силы.', '', 38134),
(204254, 'ruRU', 'Ты ответишь за это, лич! Я – не твой трофей!', '', 38134),
(204253, 'ruRU', 'И доставили мне ценный трофей: кирию из тех, кого они называют \"душеносцами\".', '', 38134),
(204252, 'ruRU', 'Кадавры вернулись с остатками награбленного из похода на Бастион.', '', 38134),
(204251, 'ruRU', 'Но ты ведь так никогда не поступишь... верно?', '', 38134),
(204250, 'ruRU', 'Если вдруг ты задумаешь меня предать, маркграфиня обязательно узнает, кто совершил это убийство.', '', 38134),
(204249, 'ruRU', 'Ах да, еще кое-что: тот походя убитый тобой аколит был любимчиком маркграфини Син\'дейн.', '', 38134),
(204248, 'ruRU', 'Пожалуй, сойдет. Приходи в зал Клинков. Там я проверю качество твоей маскировки.', '', 38134),
(204247, 'ruRU', 'Следующее задание будет сложнее. Надеюсь, тебя это не пугает.', '', 38134),
(204246, 'ruRU', 'Тебе все же удалось найти мою \"посылку с помощью\". Возможно, от тебя будет какой-то прок.', '', 38134),
(204245, 'ruRU', 'И смотри, не проговорись о нашей сделке.', '', 38134),
(204244, 'ruRU', 'Найди башню возле Скаплийского хребта. Там есть все, что тебе нужно.', '', 38134),
(204242, 'ruRU', 'Но для кого мы играем?', '', 38134),
(204241, 'ruRU', '', 'С этими инструментами я снова стала собой...', 38134),
(204240, 'ruRU', 'Наконец-то!', '', 38134),
(204239, 'ruRU', 'Хватит предаваться раздумьям. Начинаем репетицию!', '', 38134),
(204238, 'ruRU', 'Хорошие новости! Я организовал для нас следующий концерт... и вы не поверите, где!', '', 38134),
(204237, 'ruRU', '', 'При жизни я не могла подолгу оставаться в одном месте.\n\nКаждый день скрывал новое приключение, и я должна была непременно найти его.\n\nТу же жажду странствий я чувствую и в тебе.\n\nСкажи мне, пилигрим... Ты веришь смотрителю Тедо? Способна ли музыка спасти душу?\n\nЯ пока в этом не уверена.', 38134),
(204236, 'ruRU', 'Ансель слишком много волнуется. Все будет хорошо.\n\nКак говорила моя Аша, за каждой песчаной грядой может скрываться вода.\n\nМузыка нас спасет. Она всегда нас спасала.', '', 38134),
(204235, 'ruRU', 'Говорят, музыка – пища для души.\n\nЭто, конечно, метафора...\n\n<Смотритель Тедо ловит ваш взгляд.>\n\nА если это правда?\n\nЧто, если для тех, кто любил музыку при жизни, она может стать ключом к спасению?\n\nМожет, музыка действительно способна исцелять души?\n\nЯ верю, что это так.', '', 38134),
(204233, 'ruRU', '', 'Зимний сад частично восстановлен, теперь в нем больше диких семян – и появились участки для катализаторов.\n\nОни позволяют еще больше усиливать дикие семена перед тем, как поместить в них душу.\n\nПосадив катализатор, ты увидишь, к каким диким семенам он подключился.', 38134),
(204232, 'ruRU', 'Как мы порадуем наших смотрителей музыкой, если у нас все время теряются инструменты?\n\n<Счастливчик Ансель бросает беглый взгляд на камни грехов снаружи.>\n\nНам отсюда никогда не выбраться.', '', 38134),
(204231, 'ruRU', '', 'Сейчас восстановлено только два диких семени. С их помощью ты можешь возвращать силы духам. \n\nКогда восстановишь рощу в ее былом великолепии, тебе откроются и другие возможности сада. \n\nПодойди ко мне, когда зимний сад наберется сил.', 38134),
(204229, 'ruRU', '', 'Привет, $n. Чем я могу тебе помочь?', 38134),
(204228, 'ruRU', '', 'Добро пожаловать, $n. Восстановив грибной портал, ты сможешь перемещаться в королевский зимний сад.\n\nПоговори с Зайхед, она покажет тебе, что надо починить.', 38134),
(204227, 'ruRU', 'Вайтмейн расскажет тебе, какая судьба постигла наших союзников.', '', 38134),
(204226, 'ruRU', 'Назгрим расскажет тебе, какая судьба постигла наших союзников.', '', 38134),
(204225, 'ruRU', 'Верховный лорд Болвар Фордрагон оправился после боя с Сильваной. Он ждет нас вместе с нашими союзниками.', '', 38134),
(204224, 'ruRU', 'Мы приказали переместить \"Акерус\" к цитадели Ледяной Короны. Позволь мне открыть для нас врата смерти.', '', 38134),
(204223, 'ruRU', 'Приветствую! Мы завершили приготовления.', '', 38134),
(204222, 'ruRU', 'Рыцари Черного Клинка готовы выступать. Я жду тебя в Оргриммаре. Поспеши, пока Азерот не поглотила Смерть.', '', 38134),
(204221, 'ruRU', 'Завеса между нашим миром и Темными Землями разорвана.', '', 38134),
(204220, 'ruRU', 'Рыцари Черного Клинка готовы выступать. Я жду тебя в Штормграде. Поспеши, пока Азерот не поглотила Смерть.', '', 38134),
(204219, 'ruRU', 'Завеса между нашим миром и Темными Землями разорвана.', '', 38134),
(204217, 'ruRU', 'Ослабьте его, а я освобожу ее с помощью посоха!', '', 38134),
(204216, 'ruRU', 'Кел\'Тузад вытягивает силы из маркграфини Син\'дейн!', '', 38134),
(204215, 'ruRU', 'Прислужники! Уничтожьте их!', '', 38134),
(204211, 'ruRU', '<Сундук со скрипом открывается, и вы видите большое количество костюмов и аксессуаров.>', '', 38134),
(204202, 'ruRU', 'Об этом приеме давно ходили разговоры.', 'Об этом приеме давно ходили разговоры.', 38134),
(204201, 'ruRU', 'Прекрасный день для приема.', 'Прекрасный день для приема.', 38134),
(204200, 'ruRU', 'Ты обладаешь необычайным умом для смертного, $p.', 'Ты обладаешь необычайным умом для смертного, $p.', 38134),
(204199, 'ruRU', 'Прикуси язык!', 'Прикуси язык!', 38134),
(204198, 'ruRU', 'Иногда мне кажется, что Гарлоу открывает рот только для того, чтобы услышать собственный голос!', 'Иногда мне кажется, что Гарлоу открывает рот только для того, чтобы услышать собственный голос!', 38134),
(204197, 'ruRU', 'Да, это верно. Фредрик не виноват, что у него нет вкуса.', 'Да, это верно. Фредрик не виноват, что у него нет вкуса.', 38134),
(204196, 'ruRU', 'Фредрик просто завидует, что я выгляжу в этом костюме куда лучше него. В посмертии он не стал стройнее.', 'Фредрик просто завидует, что я выгляжу в этом костюме куда лучше него. В посмертии он не стал стройнее.', 38134),
(204194, 'ruRU', 'Ты знаешь, что надел Гарлоу? Никакой фантазии. Надо сорвать с него эти тряпки!', 'Ты знаешь, что надел Гарлоу? Никакой фантазии. Надо сорвать с него эти тряпки!', 38134),
(204192, 'ruRU', 'Приветик, шеф! Новые боссы велели прийти сюда, мол, надо решить, что делать с анимой. \n\nКогда ее станет много, можно будет укрепить все стены. А то что за дела, пол-Ревендрета в развалинах.', '', 38134),
(204187, 'ruRU', 'Делайте что хотите, смертные. Вы пытаетесь отсрочить неизбежное.', '', 38134),
(204186, 'ruRU', 'Думаете, вы особенные? Одаренные? Я научу вас смирению.', '', 38134),
(204185, 'ruRU', 'Это Вираз заточил меня в Утробе. Я не сомневаюсь, что наш маркграф пал именно от его руки.', '', 38134),
(204182, 'ruRU', 'Такая предсказуемость утомляет. Впрочем, где смертные – там и неудачи.', '', 38134),
(204176, 'ruRU', 'Хочешь летать? Да?', 'Хочешь летать? Да?', 38134),
(204175, 'ruRU', 'Впечатляет. И все же мне не терпится оборвать ваши жалкие жизни.', '', 38134),
(204174, 'ruRU', '', 'Идти за ними сейчас – безрассудство. Соберитесь. Еще успеете погеройствовать.', 38134),
(204169, 'ruRU', 'Так, каменные блоки есть. Что мне из них соорудить?', '', 38134),
(204164, 'ruRU', 'Но когда шлем раскололся, Болвар Фордрагон освободился от своего бремени. Теперь он так же свободен, как и мы с тобой.', '', 38134),
(204163, 'ruRU', '', 'Я тысячи лет училась ритуальной игре на барабанах. \n\nОднажды мне даже выпала честь играть для самой Королевы Зимы! Теперь я смогу выступить на этом замечательном приеме. Спасибо, пилигрим Утробы.', 38134),
(204160, 'ruRU', 'Я думаю, нам стоит поставить статую у главных ворот.\n\nНо что это будет за статуя?', '', 38134),
(204155, 'ruRU', 'На, держи! Сыграй на флейте рядом с норой гормлинга, и он высунется!', '', 38134),
(204138, 'ruRU', 'Надеюсь, духи моих предков нашли в этих краях мир и покой.', '', 38134),
(204137, 'ruRU', 'Я видел лишь самые мрачные уголки Темных Земель. Приятно знать, что здесь есть место и надежде.', '', 38134),
(204133, 'ruRU', '', 'Тралл, ты знаешь, куда увели Андуина?', 38134),
(204132, 'ruRU', 'Друзья мои!', '', 38134),
(204125, 'ruRU', 'Говорят, любой босс – это босс, все боссы как боссы, но ни один босс не любит махать плеткой так, как прошлый босс.\n\nНо только не принц. Он ничего так, хоть и клыкастый. Он что надо.', '', 38134),
(204124, 'ruRU', 'Я участвовал во многих сражениях в разных мирах. \n\nНо ни одна из этих войн не была более благородной, чем служба принцу Ренаталу, генералу Дрейвену и тебе.\n\nМы сражаемся за само существование нашей реальности против невероятно сильного врага. Это то, для чего меня создали!', '', 38134),
(204123, 'ruRU', 'Если принц Ренатал действительно хочет вернуть столько потерянных территорий, надо привлечь на нашу сторону больше камнерожденных. \n\nБыло бы намного легче, если бы он убедил Камнетворицу нам помочь. Сейчас мы можем пробуждать только тех, в ком уверены. Иначе мы только умножим армии наших врагов.\n\nНо тебе повезло – у тебя есть я. Я знаю, кто нас поддержит.', 'Если принц Ренатал действительно хочет вернуть столько потерянных территорий, надо привлечь на нашу сторону больше камнерожденных. \n\nБыло бы намного легче, если бы он убедил Камнетворицу нам помочь. Сейчас мы можем пробуждать только тех, в ком уверены. Иначе мы только умножим армии наших врагов.\n\nНо тебе повезло – у тебя есть я. Я знаю, кто нас поддержит.', 38134),
(204122, 'ruRU', 'Теперь я могу также вычеркнуть из своих планов ответные меры.', 'Теперь я могу также вычеркнуть из своих планов ответные меры.', 38134),
(204114, 'ruRU', 'Согласно моим записям, тебе удалось вернуть все украденные реликвии.', 'Согласно моим записям, тебе удалось вернуть все украденные реликвии.', 38134),
(204108, 'ruRU', 'Сегодня маркграф Гармал умрет.', '', 38134),
(204107, 'ruRU', 'Много претендентов, мало сока! Приходи потом, ладно?', 'Много претендентов, мало сока! Приходи потом, ладно?', 38134),
(204104, 'ruRU', 'У-ух! Тут есть питье! Бери! Бери!', 'У-ух! Тут есть питье! Бери! Бери!', 38134),
(204103, 'ruRU', '<Распорядитель, тихо ухнув, протягивает вам поднос.>', '<Распорядительница, тихо ухнув, протягивает вам поднос.>', 38134),
(204100, 'ruRU', 'Претендент хочет пить? У распорядителя есть напиток!', 'Претендент хочет пить? У распорядителя есть напиток!', 38134),
(204099, 'ruRU', 'Пить! Пить! Пить?', 'Пить! Пить! Пить?', 38134),
(204097, 'ruRU', 'Хочешь пить? Очень вкусно!', 'Хочешь пить? Очень вкусно!', 38134),
(204081, 'ruRU', '', 'Привет, дружище!\n\nУ нас столько дел...\n\nЕсли у тебя есть минутка, мне бы очень пригодилась твоя помощь.', 38134),
(204080, 'ruRU', 'Если мы вернем его маркграфине Син\'дейн, у нас появится шанс победить Кел\'Тузада.', '', 38134),
(204079, 'ruRU', 'Постой... Посох Примаса! Он все еще цел!', '', 38134),
(204078, 'ruRU', '', 'Ступай в Кольцо Перенаправления и открой портал в Арденвельд. Следуй за Предназначением.', 38134),
(204077, 'ruRU', '', 'В Арденвельде анима питает духов природы, что спят в его рощах. Надеюсь, запасы Королевы выдержали эту жуткую засуху.', 38134),
(204076, 'ruRU', '', 'Королева Зимы была близкой союзницей Примаса. Она прислушается к его предупреждению.', 38134),
(204075, 'ruRU', 'Вместе мы сможем заглянуть глубже в мир Тюремщика. Но берегись. Не потеряй себя в этой тьме.', '', 38134),
(204074, 'ruRU', 'Сломанный шлем все еще наполнен силой Короля-лича. Силой... Утробы.', '', 38134),
(204073, 'ruRU', '', 'Ревендретом правит сир Денатрий. Надеюсь, он справляется с засухой анимы лучше, чем другие властители. Нам очень нужна его помощь.', 38134),
(204072, 'ruRU', '', 'Туда попадают заносчивые и надменные души, в которых Арбитр видит потенциал. Вентиры ведут эти души по пути к искуплению.', 38134),
(204071, 'ruRU', '', 'Ревендрет – зловещее царство темных шпилей и холодных склепов. Но этот мир и его обитатели служат благородной цели.', 38134),
(204070, 'ruRU', '', 'Боюсь, что без помощи армий Малдраксуса у нас немного шансов остановить Изгнанника.', 38134),
(204069, 'ruRU', '', 'Пилигрим, выясни, почему они напали на кирий. Сделай все, чтобы пресечь этот конфликт, пока еще не поздно.', 38134),
(204068, 'ruRU', 'Ее больше нет...', '', 38134),
(204067, 'ruRU', '', 'Гвейир! Нет...', 38134),
(204066, 'ruRU', '', 'Держись, Гвейир! Проклятье почти снято!', 38134),
(204065, 'ruRU', '', 'Еще немного! Магия друстов ослабевает!', 38134),
(204064, 'ruRU', '', 'Скорей! Уничтожь их!', 38134),
(204063, 'ruRU', '', 'Приготовься, пилигрим. Рассеять магию горака Зар будет непросто.', 38134),
(204062, 'ruRU', '', 'Мне нужен маленький, быстрый и легкий кадавр.\n\nВраги редко меня видят, но всегда боятся, что я рядом.', 38134),
(204060, 'ruRU', 'Я знал, что понадоблюсь.', '', 38134),
(204059, 'ruRU', 'Мне понадобится прочный кадавр с железной кожей.\n\nС ним я смогу решить все твои проблемы.', '', 38134),
(204058, 'ruRU', 'Лорд Гарридан не преувеличивал. Это достойный прием!', 'Лорд Гарридан не преувеличивал. Это достойный прием!', 38134),
(204057, 'ruRU', 'Где Привереда Стефан? Он обещал мне приватную дегустацию.', 'Где Привереда Стефан? Он обещал мне приватную дегустацию.', 38134),
(204056, 'ruRU', 'Я рад, что Теотар вернулся из изгнания. Блестящий ум!', 'Я рада, что Теотар вернулся из изгнания. Блестящий ум!', 38134),
(204055, 'ruRU', 'Я хочу выпить. Мне нужен очень, очень большой кубок!', 'Я хочу выпить. Мне нужен очень, очень большой кубок!', 38134),
(204054, 'ruRU', 'Хоть бы этот прием никогда не заканчивался!', 'Хоть бы этот прием никогда не заканчивался!', 38134),
(204053, 'ruRU', 'Я развлек твоего особого гостя беседой... всегда пожалуйста!', 'Я развлекла твоего особого гостя беседой... всегда пожалуйста!', 38134),
(204052, 'ruRU', 'Все просто великолепно!', 'Все просто великолепно!', 38134),
(204051, 'ruRU', 'Должен признать, это очень интересный прием.', 'Должна признать, это очень интересный прием.', 38134),
(204050, 'ruRU', 'Банкет после приема – моя любимая часть!', 'Банкет после приема – моя любимая часть!', 38134),
(204049, 'ruRU', 'Тебе будет трудно сделать следующий прием еще лучше!', 'Тебе будет трудно сделать следующий прием еще лучше!', 38134),
(204048, 'ruRU', 'Должен признать, я предпочитаю... Немного другие приемы.', 'Должна признать, я предпочитаю... Немного другие приемы.', 38134),
(204047, 'ruRU', 'Это было незабываемо!', 'Это было незабываемо!', 38134),
(204046, 'ruRU', 'Я поверить не могу, что... О, привет, пилигрим Утробы.', 'Я поверить не могу, что... О, привет, пилигрим Утробы.', 38134),
(204045, 'ruRU', 'Обязательно зови меня в следующий раз!', 'Обязательно зови меня в следующий раз!', 38134),
(204044, 'ruRU', 'Обязательно расскажу об этом всем моим друзьям.', 'Обязательно расскажу об этом всем моим друзьям.', 38134),
(204043, 'ruRU', 'Какой чудесный прием!', 'Какой чудесный прием!', 38134),
(204014, 'ruRU', 'Мне не терпится узнать, насколько правдивы слухи о Пепельном дворе!', 'Мне не терпится узнать, насколько правдивы слухи о Пепельном дворе!', 38134),
(204013, 'ruRU', 'Все в Темном Приюте просто упадут от зависти!', 'Все в Темном Приюте просто упадут от зависти!', 38134),
(204012, 'ruRU', 'Интересно, сколько знаменитостей я сегодня встречу?', 'Интересно, сколько знаменитостей я сегодня встречу?', 38134),
(204011, 'ruRU', 'Должен признаться, я до сих пор не понимаю, как мне удалось раздобыть приглашение... Я сгораю от нетерпения!', 'Должна признаться, я до сих пор не понимаю, как мне удалось раздобыть приглашение... Я сгораю от нетерпения!', 38134),
(204010, 'ruRU', 'Этот прием еще долго будет у всех на устах!', 'Этот прием еще долго будет у всех на устах!', 38134),
(204009, 'ruRU', 'Как тебе моя обувь? Я по такому случаю надел самые длинные шпоры!', 'Как тебе мое платье? Я берегла его для знаменательного события.', 38134),
(204008, 'ruRU', 'Поскорей бы они открыли ворота!', 'Поскорей бы они открыли ворота!', 38134),
(204007, 'ruRU', 'Просто намекни, пилигрим! Какие невероятные сюрпризы ждут нас на сегодняшнем приеме?', 'Просто намекни, пилигрим! Какие невероятные сюрпризы ждут нас на сегодняшнем приеме?', 38134),
(204005, 'ruRU', 'Ара\'лон... Я немедленно вышлю поисковую группу. Мы не оставим его друстам.', '', 38134),
(204003, 'ruRU', '', 'Ара\'лон все еще не вернулся. Боюсь, как бы он не попал в беду...', 38134),
(204002, 'ruRU', '', 'Приходите в Сердце рощи, и мы проведем ритуал. Удачной вам охоты.', 38134),
(204000, 'ruRU', '', 'Он предпочитает церемониальную обстановку, поэтому не забудь, все должно быть как можно более официально.', 38134),
(203999, 'ruRU', 'Мы получили ответ на приглашение от хранителя склепа Каззира, и он придет на прием.', '', 38134),
(203998, 'ruRU', 'Я не вернусь без нее. Даю слово.', '', 38134),
(203997, 'ruRU', '', '$p, ты найди ядовитый корень. Ара\'лон, добудь ведьмину сумку у друстов. Только будь осторожен.', 38134),
(203996, 'ruRU', 'Поговори с персоналом, чтобы получить контракты и ответы на приглашения, а я займусь остальным!', '', 38134),
(203995, 'ruRU', 'Я хорошо знаком с нашим первым гостем и точно знаю, какой прием для него устроить!', '', 38134),
(203993, 'ruRU', 'Нам придется поговорить с его землероями. Они всегда успевают поставить на огонь новый чайник.', '', 38134),
(203992, 'ruRU', 'Боюсь, Теотар выпил весь наш запас чая в ходе репетиции.', '', 38134),
(203990, 'ruRU', 'Гресит нас с ними свяжет. Я отмечу на твоей карте, где его найти.', '', 38134),
(203989, 'ruRU', 'Если не знаешь, куда пойти сначала, то посмотри в моем списке развлечений.', '', 38134),
(203988, 'ruRU', 'Помоги Обвинительнице и ее ритуалистам, а взамен они помогут нам, смекаешь?', '', 38134),
(203987, 'ruRU', 'Никто не проводит ритуалы лучше, чем Нераскаявшиеся в Чертогах Покаяния.', '', 38134),
(203984, 'ruRU', 'Прекрасно. Душеносцы должны мыслить здраво, чтобы доставлять в Орибос готовые души.', '', 38134),
(203983, 'ruRU', 'Дозорный решает, готова ли душа попасть в Темные Земли. Отточи свои навыки в этом испытании.', '', 38134),
(203981, 'ruRU', '', 'Вы как раз вовремя! Хотя можно было бы и побыстрее...', 38134),
(203980, 'ruRU', 'Если вы ищете Киварру, они только что увели ее!', '', 38134),
(203979, 'ruRU', '', 'У моей клетки три замка, и для каждого нужен свой ключ. Видимо, друсты меня побаиваются.', 38134),
(203974, 'ruRU', 'Давай обыщем рощу. Вряд ли друсты увели ее далеко.', '', 38134),
(203973, 'ruRU', 'Киварры здесь нет... и кругом следы борьбы. С ней что-то случилось!', '', 38134),
(203970, 'ruRU', 'Я не собираюсь много сражаться, но на пустой желудок особо не повоюешь.\n\nХочу кадавра с хорошей головой, сильными руками и множеством ртов, чтобы пробовать то, что я готовлю.', '', 38134),
(203969, 'ruRU', '', 'Хочу два больших лезвия вместо рук. Представь, какой ужас я буду наводить на врагов!\n\nКак считаешь, будет здорово... правда?', 38134),
(203968, 'ruRU', '', 'Мне надоело быть мясным щитом и бросаться в толпу врагов!\n\nВсегда хотела кидаться заклятьями, разрывать противников своей магией.\n\nСоздай для меня кадавра, способного на это!', 38134),
(203966, 'ruRU', 'Избранные! Враги выступили против нас! Грядет война, и мы победим!', '', 38134),
(203965, 'ruRU', 'Согласен. Испытай пилигрима. Затем найди меня в некрополе. Нам предстоит война.', '', 38134),
(203964, 'ruRU', 'Здравствуй, путник.\n\nЯ изучаю древние реликвии Темных Земель.\n\nНе хочешь тоже этим заняться?', 'Здравствуй, путник.\n\nЯ изучаю древние реликвии Темных Земель.\n\nНе хочешь тоже этим заняться?', 38134),
(203961, 'ruRU', 'Археология? Что это?\n\n<Он слушает ваши объяснения про археологические исследования и их причины.>\n\nНевероятно. Но вряд ли у нас есть что-то подобное.\n\nВы, смертные, со временем утрачиваете секреты, но здесь все иначе.\n\nНо если что-то изменится, я дам тебе знать.', 'Археология? Что это?\n\n<Он слушает ваши объяснения про археологические исследования и их причины.>\n\nНевероятно. Но вряд ли у нас есть что-то подобное.\n\nВы смертные, со временем утрачиваете секреты, но здесь все иначе.\n\nНо если что-то изменится, я дам тебе знать.', 38134),
(203960, 'ruRU', '', 'Мы многому у него научились... и вместе мы за него отомстим.', 38134),
(203959, 'ruRU', 'Я рад, что тебе тоже довелось служить Крексусу. Он восхищался тобой, даже когда ты подчинялась Акареку.', '', 38134),
(203958, 'ruRU', 'Он научил меня преодолевать непреодолимое. Я вырву черное сердце предателя Вираза.', '', 38134),
(203957, 'ruRU', '', 'Крексус высоко отзывался о тебе, Могрейн. А он был скуп на похвалу.', 38134),
(203956, 'ruRU', '', 'Выживание – дорога слабых. Есть лишь победа... или смерть.', 38134),
(203955, 'ruRU', '', 'Маркграф дал мне новую цель, Вайш. Если мы будем цепляться за прошлое, нам не выжить.', 38134),
(203948, 'ruRU', '', 'Поэтому ты пряталась за знаменами Избранных, пока нас убивали одного за другим?', 38134),
(203947, 'ruRU', '', 'Я не бросала Акарека. Меня, так же как и тебя, отправили с поручением прямо перед тем, как наш дом пал.', 38134),
(203944, 'ruRU', '', 'Так, а это у нас кто? Кто-то из любимцев Кел\'Тузада.', 38134),
(203943, 'ruRU', 'Нет? Как жаль. Ничего, я не оставлю тебя без... прощального подарка.', '', 38134),
(203942, 'ruRU', 'Значит, тебе удалось сбежать. А теперь ты хочешь отпраздновать мою победу?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203941, 'ruRU', 'Может быть. Но у власти всегда есть цена.', '', 38134),
(203940, 'ruRU', '', 'Ты заплатишь за это коварство!', 38134),
(203939, 'ruRU', '', 'Твоя слабость позорит дом Ритуалов, Син\'дейн. Пришло время сменить маркграфиню.', 38134),
(203938, 'ruRU', '', 'Баронесса? Как ты смеешь?!', 38134),
(203937, 'ruRU', 'Похоже, старый Вол\'джин начнет новую охоту... но сначала – отдых.', '', 38134),
(203936, 'ruRU', 'Один? Нет.', '', 38134),
(203935, 'ruRU', '', 'Какое вероломство! Ты правда думаешь, что можешь тягаться с маркграфиней?', 38134),
(203934, 'ruRU', '', 'Я наслышана о твоей тщеславной, бессердечной королеве. Похоже, у тебя с ней много общего – и я не только о внешности.', 38134),
(203933, 'ruRU', '', 'С королевой Азшарой мы достигли небывалой мощи. Раскол был лишь небольшой платой за наше величие.', 38134),
(203931, 'ruRU', '', 'Ты многовато знаешь о расколотых мирах, Вайш. И о том, как якшаться с демонами.', 38134),
(203930, 'ruRU', '', 'Мне доводилось видеть твой родной мир, Дрека. Или, точнее, то, что от него осталось.', 38134),
(203929, 'ruRU', '', 'Пилигрим проявляет стойкость и силу – те качества, которые нам сейчас нужнее всего.', 38134),
(203928, 'ruRU', 'Смелый побег!', '', 38134),
(203926, 'ruRU', 'Это не должно тебя задержать.', '', 38134),
(203924, 'ruRU', 'Не можешь пошевелиться?', '', 38134),
(203922, 'ruRU', 'Спасибо, что увела пилигрима с арены. Нельзя было допустить, чтобы враги забрали его.', '', 38134),
(203921, 'ruRU', 'Не дай самоуверенности тебе помешать. Не расслабляйся!', '', 38134),
(203920, 'ruRU', 'С края арены очень высоко падать!', '', 38134),
(203919, 'ruRU', 'Проклятие друстов скоро сломит волю Гвейир. Держать ее здесь слишком опасно!', '', 38134),
(203918, 'ruRU', '', 'Я доложу обо всем баронессе Вайш. Служить с тобой – честь для меня, пилигрим.', 38134),
(203914, 'ruRU', 'Гвейир! Она дышит... Но... что они с ней сделали?', '', 38134),
(203911, 'ruRU', 'Нера была самой яростной из нас. Она никогда не покорилась бы друстам.', '', 38134),
(203910, 'ruRU', 'Тэрас... да укроет тебя ночь, друг мой.', '', 38134),
(203909, 'ruRU', '', 'Этот портал – лишь первый из многих. Достойная победа.', 38134),
(203908, 'ruRU', 'Азелор... этого я и боялся. Он не заслужил такой судьбы.', '', 38134),
(203903, 'ruRU', 'Они поработили всю рощу! Постарайся не потерять маску.', '', 38134),
(203902, 'ruRU', '$n, сюда! Мне нужна помощь...', '', 38134),
(203901, 'ruRU', 'Хр-р-р... вернись... Трешала... Дарнас... Хр-р-р...', '', 38134),
(203899, 'ruRU', 'Нам всем нужно многому научиться. Идем. Поговорим в моем храме.', '', 38134),
(203896, 'ruRU', 'Мы должны понять, что его терзает. Подозреваю, мы еще многого не знаем.', '', 38134),
(203895, 'ruRU', 'Полемарх, позволь мне позаботиться об Утере.', '', 38134),
(203894, 'ruRU', '', 'Некрополь у нас есть. Теперь мы можем спокойно открыть портал к Престолу Примаса.', 38134),
(203893, 'ruRU', 'Он нужен нам на поле боя. Помоги ему преодолеть их!', '', 38134),
(203892, 'ruRU', 'Пелагий пришел созвать сборщиков, чтобы помочь тебе, но его охватили сомнения по поводу грядущей битвы.', '', 38134),
(203891, 'ruRU', '', 'Она не сможет помочь нам в таком состоянии. Восстанови колокола, чтобы ей стало лучше!', 38134),
(203889, 'ruRU', 'Я дарую тебе эту силу... Защищай Малдраксус!', '', 38134),
(203885, 'ruRU', '', 'Их страдания прекрасны!', 38134),
(203884, 'ruRU', '', 'Насладимся их мучениями!', 38134),
(203883, 'ruRU', 'Мое правление вечно!', '', 38134),
(203882, 'ruRU', 'Вы умрете у моих ног!', '', 38134),
(203881, 'ruRU', 'Никто не выживет!', '', 38134),
(203865, 'ruRU', '', 'Вперед!', 38134),
(203864, 'ruRU', '', 'Получай! Теперь осталось лишь одно.', 38134),
(203863, 'ruRU', '', 'Не зевайте! В наших рядах шпионы!', 38134),
(203858, 'ruRU', 'Наши враги непреклонны в своих убеждениях. Если они не слушают наших слов, мы вразумим их действиями. Поручаю это тебе.', '', 38134),
(203857, 'ruRU', 'Со временем они осознают, что неправы. Надеюсь, сегодняшний день станет полезным уроком.', '', 38134),
(203848, 'ruRU', '', 'Благодарность одного – мотивация для другого. Ты делаешь Бастион сильнее.', 38134),
(203847, 'ruRU', '', 'Немея и Пелодис соперничают уже тысячи лет. Они наверняка примут твою помощь... и продолжат соперничество.', 38134),
(203846, 'ruRU', '', 'Тебе удалось выстоять против чудищ из другого мира. Это дает нам надежду. Спасибо.', 38134),
(203845, 'ruRU', '', 'Нам понадобится твоя отвага. Эти чудовища пожирают все на своем пути. Останови их!', 38134),
(203844, 'ruRU', '', 'Ты всегда был слишком слаб для истинного пути. Лучше бы Девия бросила тебя на том поле!', 38134),
(203843, 'ruRU', 'Мы хотели спасти наш народ, а не отдать его во власть тьмы!', '', 38134),
(203841, 'ruRU', 'Лисония... почему?..', '', 38134),
(203840, 'ruRU', '', 'Теперь я вижу отведенную тебе роль. Ты – ключ к истинной судьбе. Моей судьбе!', 38134),
(203839, 'ruRU', 'Моя душа... отмечена Утробой?', '', 38134),
(203838, 'ruRU', 'Мой взор... застилает тьма...', '', 38134),
(203837, 'ruRU', '', 'Не перечь мне. Выполняй!', 38134),
(203836, 'ruRU', 'Лисония, я...', '', 38134),
(203835, 'ruRU', '', 'Еще! Убей его, если нужно.', 38134),
(203834, 'ruRU', 'Как прикажешь.', '', 38134),
(203833, 'ruRU', '', 'Он не поддается! Удвой усилия.', 38134),
(203832, 'ruRU', '', 'Я усилила патрулирование, как ты просил, полемарх. Раскольники стали гораздо... активнее.', 38134),
(203831, 'ruRU', '', 'Идите, мои раскольники. Готовьтесь. Скоро придет новый рассвет.', 38134),
(203830, 'ruRU', 'Слуги Архонта пытаются восстановить Мастерскую Перерождения с помощью других ковенантов.', '', 38134),
(203829, 'ruRU', '', 'Полемарх. Пилигрим. Я хочу вам кое-что показать.', 38134),
(203828, 'ruRU', '', 'Боюсь, Утер заодно с ними.', 38134),
(203827, 'ruRU', '', 'Они нам понадобятся. Дозорные подтвердили опасения Тениоса: Лисония возглавила раскольников.', 38134),
(203826, 'ruRU', '', 'Новая Мастерская сделает перерожденных еще сильнее!', 38134),
(203819, 'ruRU', 'Без Девии место лидера раскольников наверняка займет Лисония. Надеюсь, мы добьемся мира, пока они не вернулись.', '', 38134),
(203813, 'ruRU', 'Лисония последовала за ней, как и Утер. Наши запасы анимы таяли, а их ряды росли.', '', 38134),
(203812, 'ruRU', 'Сбившись с пути воплощения верности, Девия неминуемо встала на путь сомнений и насилия.', '', 38134),
(203805, 'ruRU', 'Девия хотела... исправить несправедливость... Но Утер не был готов к перерождению.', '', 38134),
(203799, 'ruRU', 'Ты увидишь то, что видели немногие... Прибытие Утера в Бастион.', '', 38134),
(203798, 'ruRU', 'Здесь... это же...', '', 38134),
(203797, 'ruRU', 'Архонт отказала мне в правосудии. Благодаря Девии монстр оказался там, где ему и место!', '', 38134),
(203796, 'ruRU', '', 'Мы осуществим ее великий замысел. Кирии – все кирии! – освободятся от ваших ритуалов!', 38134),
(203795, 'ruRU', '', 'Из этих осколков я соберу новые зеркала к следующему приему.', 38134),
(203791, 'ruRU', 'Надо было сдаваться. Сейчас ты заплатишь кровью за гибель Крексуса!', '', 38134),
(203789, 'ruRU', '', 'Предательство решило твою судьбу. Тебя ждет расплата!', 38134),
(203788, 'ruRU', 'Останови это безумие! Примас бы этого не потерпел!', '', 38134),
(203787, 'ruRU', 'Здравствуй, пилигрим.\n\nЯ создаю музыку из грехов тех душ, что поют о своей кончине в жутких глубинах этих священных залов. \n\nМожет быть, когда придет твое время уйти из мира живых, ты присоединишься к моему хору...', 'Здравствуй, пилигрим.\n\nЯ создаю музыку из грехов тех душ, что поют о своей кончине в жутких глубинах этих священных залов. \n\nМожет быть, когда придет твое время уйти из мира живых, ты присоединишься к моему хору...', 38134),
(203781, 'ruRU', 'Продолжай, пилигрим! Уничтожь их главаря.', '', 38134),
(203772, 'ruRU', '', 'Все началось с того, что Орда разрушила Тельдрассил, дом калдорай. \n\nАльянс не мог выделить войска, чтобы помочь вернуть то, что осталось от Тельдрассила, и у моей минн\'до не было выбора.\n\nОна обратилась к Элуне с просьбой о силе Ночной Воительницы – воплощенного гнева Элуны.\n\nТеперь Тиранда носит в себе ярость Элуны, но эта сила убивает всех, кто владеет ею слишком долго.', 38134),
(203771, 'ruRU', '', 'Разве ты не знаешь? Я думала, рассказы о ярости моей минн\'до облетели всю Орду.\n\nТвои соратники уничтожили Тельдрассил, дом калдорай. \n\nАльянс не мог выделить войска, чтобы помочь вернуть то, что осталось от Тельдрассила, и у моей минн\'до не было выбора.\n\nОна обратилась к Элуне с просьбой о силе Ночной Воительницы – воплощенного гнева Элуны.\n\nТеперь Тиранда носит в себе ярость Элуны, но эта сила убивает всех, кто владеет ею слишком долго.', 38134),
(203770, 'ruRU', '', 'Мы используем оставшуюся аниму, чтобы переместиться на фабрику. Встретимся там.', 38134),
(203765, 'ruRU', '', 'В списке гостей лорда Гарридана должно быть указано, где их искать.\n\nПоговорив с лордом Гарриданом, вы получите копию его списка и сможете сверяться с ним в любое время.\n\nКроме того, мы отметим на вашей карте местонахождение всех гостей, которых вы уже встретили, но которые пока не ответили на ваше приглашение.\n\nЧтобы получить ответ гостя на приглашение, просто отправляйтесь в отмеченное на карте место и сделайте то, о чем гость вас попросит.\n\nЕсли вы еще не встретили потенциального гостя, вы не сможете получить его ответ на приглашение, пока не познакомитесь с ним. Обычно это происходит во время приключений в соответствующем царстве.\n\nПосле этого загляните в список лорда Гарридана – там будет указано, что нужно сделать, чтобы получить ответ на приглашение.', 38134),
(203764, 'ruRU', '', 'Я с радостью отвечу на все твои вопросы. Просто спроси.', 38134),
(203763, 'ruRU', '', 'Насколько я знаю, его можно найти у склепов Чертогов Покаяния.', 38134),
(203762, 'ruRU', 'Тебя не затруднит навестить его и выяснить, что мешает ему прийти?', '', 38134),
(203761, 'ruRU', 'Мы пока не получили ответ на приглашение от хранителя склепа Каззира.', '', 38134),
(203758, 'ruRU', '', 'Блестяще! Тебе выпадет честь вернуть эту мантию на свое законное место.', 38134),
(203753, 'ruRU', 'Постой, здесь может что-то быть. Как думаешь?', '', 38134),
(203752, 'ruRU', 'Жажда власти Гармала была ненасытна. Он должен был использовать ее для защиты Малдраксуса, а не в своих целях.', '', 38134),
(203751, 'ruRU', 'Хм... ничего особенно ценного.', '', 38134),
(203750, 'ruRU', 'Идем, пилигрим. Изучим запасы погибшего маркграфа.', '', 38134),
(203749, 'ruRU', '', 'Согласна. Мы с пилигримом Утробы отправимся туда. А ты пока разузнай, нет ли у Гармала еще чего-нибудь ценного для нас.', 38134),
(203748, 'ruRU', 'Мы знаем, что Гармал сговорился с Син\'дейн. Если их поддерживает Ревендрет, это нам сильно помешает. Нужно узнать больше.', '', 38134),
(203747, 'ruRU', '', 'Это не похоже на добычу – вроде анимы, украденной из Малдраксуса. Это дар. Дар от союзника.', 38134),
(203746, 'ruRU', 'Анима... но не малдраксийская. Смердит магией вентиров.', '', 38134),
(203745, 'ruRU', '', 'Я сочла разумным поискать в башне доказательства измены Гармала... но к такому я была не готова.', 38134),
(203744, 'ruRU', 'Веди, баронесса.', '', 38134),
(203743, 'ruRU', '', 'Могрейн, пилигрим! В глубинах крепости Гармала я нашла нечто... пугающее. Вам стоит взглянуть.', 38134),
(203741, 'ruRU', '', 'В моем списке убавилось немало имен. Так и быть, я найду время на защиту твоего приема.', 38134),
(203740, 'ruRU', 'Сразись с нами, Дрека! Или попросишься в наш дом, как до этого к Избранным?', 'Сразись с нами, Дрека! Или попросишься в наш дом, как до этого к Избранным?', 38134),
(203739, 'ruRU', 'Крексус был глупцом! Он поплатился за свою дерзость. И вы поплатитесь!', 'Крексус был глупцом! Он поплатился за свою дерзость. И вы поплатитесь!', 38134),
(203738, 'ruRU', 'Титул тому, кто принесет мне тело идеала!', 'Титул тому, кто принесет мне тело идеала!', 38134),
(203736, 'ruRU', 'Давно надо было атаковать Бастион. Примас нам только мешал!', 'Давно надо было атаковать Бастион. Примас нам только мешал!', 38134),
(203734, 'ruRU', '', 'Я поговорю об этом с Адрестом. Служи верно, пилигрим Утробы.', 38134),
(203733, 'ruRU', '', 'Теперь нужны другие улучшения – из миров других ковенантов.', 38134),
(203732, 'ruRU', '', 'Я чувствую, как растет ее мощь! Новые Перерожденные помогут одолеть Лисонию и Утера.', 38134),
(203731, 'ruRU', '', 'Эта честь достается тебе, пилигрим, в благодарность за победу над Гармалом.', 38134),
(203730, 'ruRU', '', 'Вряд ли это кого-то удивит, но Миканикос уже подготовил Мастерскую к усилению.', 38134),
(203729, 'ruRU', '', 'Сработало? И пилигрим Утробы в целости! Потрясающе. Но... больше так Утробу не покидай... Или перестанешь существовать.', 38134),
(203728, 'ruRU', 'Бей, сейчас же!', '', 38134),
(203727, 'ruRU', 'Пилигрим! Я ослаблю врага по твоему сигналу!', '', 38134),
(203710, 'ruRU', '', 'Увидимся в Элизийской цитадели. Мы установим сердце вместе.', 38134),
(203709, 'ruRU', '', 'Я... спасибо. Я это сделаю.', 38134),
(203708, 'ruRU', 'Это неприятно, но в нем заключена сила скульптора плоти. Она усилит вашу Мастерскую Перерождения.', '', 38134),
(203707, 'ruRU', 'Ксандрия, ты должна вырезать сердце Гармала.', '', 38134),
(203706, 'ruRU', '', 'Прощай. И никому обо мне не рассказывай. А если снова захочешь ринуться в эту пучину мрака, принеси что-нибудь интересное. Поторгуемся.', 38134),
(203705, 'ruRU', '', 'Думаю, теперь наш уговор исполнен. Путевой камень ждет тебя неподалеку.', 38134),
(203704, 'ruRU', 'Это не отвага, кирия. Это глупость. Сейчас ты в наших владениях!', 'Это не отвага, кирия. Это глупость. Сейчас ты в наших владениях!', 38134),
(203702, 'ruRU', '', 'Кичись, пока можешь. Ты заплатишь за свои преступления.', 38134),
(203700, 'ruRU', 'Тран\'тиок очень сильна в некротической магии. Ее слова имеют власть над силами смерти.', '', 38134),
(203699, 'ruRU', 'Это все, что вы собрали? А я уж надеялся на сытный обед!', 'Это все, что вы собрали? А я уж надеялась на сытный обед!', 38134),
(203697, 'ruRU', 'Тогда приступим.', '', 38134),
(203696, 'ruRU', '', 'Я была готова с того дня, как они разграбили мой храм.', 38134),
(203695, 'ruRU', 'Ксандрия! Ты готова к настоящей битве?', '', 38134),
(203694, 'ruRU', 'Слушайте, Избранные! Слушайте, кирии! Приготовьтесь к последней атаке!', '', 38134),
(203693, 'ruRU', 'Высвободи силу рунического клинка и уничтожь исполинов Гармала!', '', 38134),
(203692, 'ruRU', 'Покажем этим тварям истинную мощь дома Избранных.', '', 38134),
(203691, 'ruRU', 'Наши кирийские исполины сокрушат всех врагов дома Кадавров!', 'Наши кирийские исполины сокрушат всех врагов дома Кадавров!', 38134),
(203690, 'ruRU', 'Союзники дали нам невообразимую мощь. Новый барон Син\'дейн не соврал!', 'Союзники дали нам невообразимую мощь. Новый барон Син\'дейн не соврал!', 38134),
(203689, 'ruRU', 'Бастион был только началом. Скоро вся плоть станет нашей!', 'Бастион был только началом. Скоро вся плоть станет нашей!', 38134),
(203688, 'ruRU', 'Вот это зрелище!', '', 38134),
(203687, 'ruRU', 'Победа на Пути!', '', 38134),
(203686, 'ruRU', 'Этот бой запомнят надолго!', '', 38134),
(203684, 'ruRU', 'В тебе много внутренней силы, Пелагий. Запомни эту победу!', '', 38134),
(203683, 'ruRU', 'Первый распорядитель, ставший верховным автоматургом, и первый распорядитель на Пути. Ты войдешь в историю, Миканикос.', '', 38134),
(203680, 'ruRU', 'Испытание Мудрости – вспомнить былые уроки и применить их в деле.', '', 38134),
(203679, 'ruRU', 'Испытание Верности – следовать за теми, кто шел по Пути до тебя.', '', 38134),
(203676, 'ruRU', 'Испытание Отваги – твои первые шаги на Пути.', '', 38134),
(203674, 'ruRU', 'Ты – не первый из претендентов, вставших на Путь, и далеко не последний. Помни, что мы все поддерживаем тебя.', '', 38134),
(203671, 'ruRU', '', 'Отправляемся в Торгаст, $p. Нельзя терять времени.', 38134),
(203669, 'ruRU', 'Это поистине кошмарный деревянный монстр. Опасайся щепок от его доспехов!', '', 38134),
(203668, 'ruRU', 'Альдерин и Мин\'ир – опасный дуэт. Их заклинания и стрелы сеют смерть. Будь осторожнее.', '', 38134),
(203667, 'ruRU', 'Эктра – весьма талантливая шпионка. Не забывай, как хорошо она разбирается в ядах.', '', 38134),
(203666, 'ruRU', 'Наберись отваги, чтобы снова пройти это испытание.', '', 38134),
(203665, 'ruRU', 'Обдумай поражение и усвой его уроки.', '', 38134),
(203664, 'ruRU', 'На Пути нам всем есть чему поучиться.', '', 38134),
(203662, 'ruRU', 'Запомни этот урок и усиль центурионов.', '', 38134),
(203660, 'ruRU', 'Испытания – это возможность познать себя и изучить наши традиции. Путь Перерождения ждет!', '', 38134),
(203659, 'ruRU', 'Что ожидает претендентов? Славная победа или уроки поражения?', '', 38134),
(203658, 'ruRU', 'Сегодня мы собрались, чтобы узреть еще одно испытание. Путь ждет!', '', 38134),
(203657, 'ruRU', 'Какой внушительный отряд! Столько отважных героев! Я польщен.', '', 38134),
(203655, 'ruRU', 'Выберите пункт назначения.', 'Выберите пункт назначения.', 38134),
(203654, 'ruRU', 'Баронесса Дрека начала наступление. Только скажи, и я доставлю тебя на поле боя.', '', 38134),
(203653, 'ruRU', 'Браво, пилигрим. Остался только Кел\'Тузад.', '', 38134),
(203652, 'ruRU', 'Лич оградил себя щитом! Оружием его не пробить!', '', 38134),
(203649, 'ruRU', 'Ее уничтожило взрывом!', '', 38134),
(203648, 'ruRU', '', 'Мне... нужно... больше!', 38134),
(203647, 'ruRU', '', 'Нет! Я получу то, что мне обещано! То, что заслужила!', 38134),
(203646, 'ruRU', '', 'Наконец-то! Сила, которую я заслуживаю!', 38134),
(203645, 'ruRU', '', 'Я не сдамся таким, как вы!', 38134),
(203640, 'ruRU', 'Чтобы спасти Бастион посреди хаоса, нам нужно научиться работать вместе. А пока мы благодарны тебе за помощь.', '', 38134),
(203639, 'ruRU', 'Влияние раскольников бросает тень сомнения на мудрость моего храма. Я поговорю с аколитами, но сначала восстанови порядок.', '', 38134),
(203638, 'ruRU', 'Хватит играть с незваными гостями, Нинадар! Уничтожь их!', '', 38134),
(203637, 'ruRU', '', 'Да, повелитель. Как прикажешь.', 38134),
(203635, 'ruRU', '', 'Рада видеть, $p. У меня есть для тебя новости.', 38134),
(203633, 'ruRU', '', 'Ну что, пилигрим Утробы? Пора покончить с личами!', 38134),
(203632, 'ruRU', 'Ладно, ладно. Но если старый лоа вдруг тебе понадобится, ты заходи на Ту Сторону, ага?', '', 38134),
(203631, 'ruRU', '', 'Уходи.', 38134),
(203630, 'ruRU', 'Коне-е-ечно, твое величество! Бвонсамди с радостью отдаст тебе одну из душ, которые ему с таким трудом удалось спасти. А взамен...', '', 38134),
(203626, 'ruRU', '', 'Некрополь переполнен энергией. Нам пора отчаливать.', 38134),
(203625, 'ruRU', 'А когда придет время, мы найдем Андуина... и вернем его домой.', '', 38134),
(203624, 'ruRU', 'Значит, нам нельзя спешить. Пилигрим Утробы, продолжай свою борьбу против Тюремщика.', '', 38134),
(203623, 'ruRU', 'Злоба Тюремщика пропитала все в этой башне. Если он решит, что его добыче что-то угрожает, он может от нее... избавиться.', '', 38134),
(203622, 'ruRU', 'Не знаю. Нам нужно действовать осторожно. С умом.', '', 38134),
(203621, 'ruRU', '', 'Сильвана играла с ним. Говорила, что разрушит все. Она же не может... просто взять и?..', 38134),
(203620, 'ruRU', '', 'По крайней мере, он жив и, кажется, невредим. Не знаю, что от него хочет банши, но он не сдастся.', 38134),
(203619, 'ruRU', 'И это все... что я вижу.', '', 38134),
(203618, 'ruRU', '', 'Защити Сердце леса, Дитя Снов. Не пускай врагов в наши священные залы.', 38134),
(203616, 'ruRU', '', 'Ты отдала часть своей сущности, чтобы спасти мой дух. Я буду защищать Арденвельд до последнего.', 38134),
(203612, 'ruRU', '', 'Мы взлетим в небо! Быстрее!', 38134),
(203611, 'ruRU', 'Не щадите друстов!', '', 38134),
(203610, 'ruRU', 'Похоже, это Андуин.', '', 38134),
(203609, 'ruRU', 'Мои стражи говорили о ценном пленнике, которого держат в глубине Башни Тюремщика.', '', 38134),
(203608, 'ruRU', 'Мальчик, которого ты когда-то защищал, стал королем. Молю, найди его.', '', 38134),
(203607, 'ruRU', '', 'Я у тебя в долгу, $n. Хотя в прошлом мы были противниками, теперь у нас есть общий враг.', 38134),
(203606, 'ruRU', '', 'Итак, все в сборе. Нужно выступать, пока враг не успел организовать оборону.', 38134),
(203605, 'ruRU', '', 'Мы атакуем восточный фланг, а они – западный. Затем мы вместе окружим Гармала.', 38134),
(203604, 'ruRU', '', 'Друзья Могрейна – достойные союзники. Они хорошо знают врага.', 38134),
(203603, 'ruRU', '', 'Мы с тобой до конца, мой идеал.', 38134),
(203602, 'ruRU', 'Скоро узнаем.', '', 38134),
(203600, 'ruRU', '', 'Я исполню свое обещание. Вместе мы сразим Гармала.', 38134),
(203590, 'ruRU', '', 'Кажется, Дамбала тоже здесь. Нелегко сражаться с лоа, но у меня в запасе есть один трюк!', 38134),
(203565, 'ruRU', '', 'Боюсь, их забрали в Торгаст – башню, что возвышается вдалеке. В ней Тюремщик держит самые ценные трофеи.', 38134),
(203564, 'ruRU', 'Тогда покажи нам, как проникнуть в эту башню!', '', 38134),
(203563, 'ruRU', '', 'А, ты о смертных, что остались там, когда пилигриму удалось сбежать.', 38134),
(203562, 'ruRU', 'Не торопи нас, брокер. Есть и другие, кого надо спасти вместе с малдраксийским бароном.', '', 38134),
(203554, 'ruRU', 'Крась только одежду, ладно? А если тебе нужен какой-нибудь другой цвет, поищи вокруг луж жижи или спроси у того брокера на Ночном рынке.', '', 38134),
(203553, 'ruRU', 'О! Обновки...', '', 38134),
(203552, 'ruRU', 'А что не так с моими волосами? Или у тебя для меня новый плакат?', '', 38134),
(203548, 'ruRU', 'Да-да? Чем могу служить? Или у тебя что-то новенькое?', '', 38134),
(203547, 'ruRU', '', 'Надеюсь, всем здесь нравятся очень свежие грибочки. Ха-ха-ха!', 38134),
(203540, 'ruRU', '', 'Я вижу, что пилигрим Утробы с тобой. Если вы союзники, я готова доверять тебе.', 38134),
(203538, 'ruRU', 'И все-таки... навестишь меня на Той Стороне, когда поможешь Королеве Зимы?.. Ну так, по-дружески?', '', 38134),
(203537, 'ruRU', 'Мве\'зала, конечно, силен, но за старину Бвонсамди ты не бойся. У него есть еще пара фокусов в рукаве.', '', 38134),
(203536, 'ruRU', 'Кажется, мне придется свести счеты с боссом. Раз и навсегда...', '', 38134),
(203527, 'ruRU', '', 'Скорее! Примени чары!', 38134),
(203513, 'ruRU', '', 'Что это? Не видишь, я читаю?', 38134),
(203512, 'ruRU', '', 'Сегодня маркграф Гармал падет! Мы соберем войска и подготовим \"Зерекрисс\" к атаке.', 38134),
(203500, 'ruRU', '', 'Если понадобится, Твигин доставит тебя в некрополь.', 38134),
(203481, 'ruRU', '', 'Свет не оставит Андуина. Я не сомневаюсь, что он справится.', 38134),
(203480, 'ruRU', '', 'Хорошо, что ты снова с нами, Тралл. Я сейчас же сообщу Аггре и лорд-регенту.', 38134),
(203478, 'ruRU', 'Высокочтимый Голос, прошу, помоги пилигриму, пока я говорю с остальными. Мы должны понять, что значит увиденное мной.', '', 38134),
(203477, 'ruRU', '', 'Верховный лорд, у этой смертной души срочное послание от Примаса Малдраксуса. Нужно доставить его в другие миры.', 38134),
(203476, 'ruRU', 'Пока нет, Калия. За эти видения приходится платить. Я не решусь повторить их так скоро.', '', 38134),
(203475, 'ruRU', '', 'Но от Андуина и Бейна – ни звука. Верховный лорд, вы точно не можете попробовать снова?', 38134),
(203474, 'ruRU', '', 'Я смогла распознать голос лорд-адмирала... и, кажется, Тралла тоже. Их крики... леденили душу!', 38134),
(203473, 'ruRU', 'Это... все, что я вижу. Продолжать... слишком опасно.', '', 38134),
(203467, 'ruRU', '', 'Отличные грибы! Покупайте отменные грибы.', 38134),
(203450, 'ruRU', '', 'Все растет, чтобы выживать. Деревья, леса, люди. Всем нужна пища, но пища у всех разная.$b$BДелиться – значит, проявлять доброту. Поделись со мной, и я тебя отблагодарю.', 38134),
(203448, 'ruRU', 'Припасы из мастерских? Мне? В полное распоряжение? О, это мой счастливый день!$b$bСколько удачи, я даже могу ею поделиться. Да-да! Дай мне припасы, и немного моей удачи достанется тебе!', '', 38134),
(203447, 'ruRU', '', 'Тебе не стоит тут задерживаться. Тюремщик не любит расставаться со своими игрушками... даже если они ему наскучили. Ну же! Возвращайся в Орибос!', 38134),
(203443, 'ruRU', '', 'Мы ударим вместе. План баронессы будет исполнен!', 38134),
(203442, 'ruRU', '', 'Наконец-то я с вами разделаюсь!', 38134),
(203437, 'ruRU', 'Боюсь, что-то пробудилось в Торгасте. Что-то ужасное.', '', 38134),
(203436, 'ruRU', 'Пилигрим, скорее возвращайся в Орибос. Я чувствую... зов тьмы.', '', 38134),
(203435, 'ruRU', 'Пилигрим, продолжай свое дело. Исследуй темные коридоры Торгаста. Сражайся за Темные Земли. Когда-нибудь мы одержим победу!', '', 38134),
(203434, 'ruRU', '', 'Мы свяжемся с союзниками в Орибосе и призовем ковенанты к действию. Только с их помощью мы сможем остановить Тюремщика... и спасти Андуина.', 38134),
(203433, 'ruRU', 'Ты видел армии Тюремщика. Мы не готовы к штурму башни. Если мы необдуманно ворвемся туда, то все потеряем.', '', 38134),
(203432, 'ruRU', 'Надо собрать войска и атаковать Торгаст! Освободить Андуина!', '', 38134),
(203431, 'ruRU', 'Благодарю вас всех. Но Джайна права. Тюремщик знает, что мы видели. И он использует это против нас.', '', 38134),
(203430, 'ruRU', '', 'Нам удалось сдержать его, но, боюсь, угроза еще не миновала.', 38134),
(203429, 'ruRU', 'Спасибо, Джайна. Тюремщик слишком силен.', '', 38134),
(203428, 'ruRU', '', 'Отец!', 38134),
(203427, 'ruRU', '', 'Держись, Болвар! Еще немного!', 38134),
(203426, 'ruRU', 'Я получу свое! Вам не избежать Смерти!', '', 38134),
(203425, 'ruRU', 'Борись, верховный лорд! Мы с тобой!', '', 38134),
(203424, 'ruRU', '', 'Борись, отец! Не уступай ему!', 38134),
(203423, 'ruRU', 'А-а-а!', '', 38134),
(203422, 'ruRU', 'Ты подвел меня. Как и все твои предшественники!', '', 38134),
(203421, 'ruRU', 'Ты должен был стать моим глашатаем, а стал предателем!', '', 38134),
(203420, 'ruRU', '', 'Рунический клинок? О нет...', 38134),
(203419, 'ruRU', '', 'Тюремщик прорывается в сознание Болвара! Нужно разорвать между ними связь!', 38134),
(203418, 'ruRU', 'А-а-а! Я не буду... тебе... служить!', '', 38134),
(203417, 'ruRU', 'Голос Тюремщика! Как такое возможно?!', '', 38134),
(203416, 'ruRU', 'Я... тебя... вижу!', '', 38134),
(203415, 'ruRU', '', 'Болвар! Что с тобой?!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203414, 'ruRU', 'Связь оборвалась. Я больше ничего... Постой... кажется, что-то...', '', 38134),
(203412, 'ruRU', 'Чтобы вновь установить связь с этой тьмой, придется чем-то пожертвовать. Но я должен увидеть. Мы должны знать!', '', 38134),
(203411, 'ruRU', '', 'Я тоже боюсь за Андуина. Но не слишком ли это опасно? Верховный лорд тратит столько сил...', 38134),
(203410, 'ruRU', '', 'Андуин до сих пор в плену. С ним что-то случилось, отец?', 38134),
(203408, 'ruRU', 'В чем дело, верховный лорд? Что ты чувствуешь?', '', 38134),
(203407, 'ruRU', 'Нет! Только не снова!', '', 38134),
(203406, 'ruRU', 'Злоба... растет... Власть... усиливается...', '', 38134),
(203403, 'ruRU', 'Дарион! После стольких лет...', '', 38134),
(203402, 'ruRU', 'Я узнаю этот голос! Неужели... барон... мой отец?!', '', 38134),
(203401, 'ruRU', '', 'Я укрою нас, пока мы ищем этого пропавшего барона. Идем!', 38134),
(203397, 'ruRU', 'Будь начеку. У Тюремщика повсюду глаза.', '', 38134),
(203396, 'ruRU', 'Будь осторожнее. Найди Дариона, и он поможет тебе в поисках барона.', '', 38134),
(203395, 'ruRU', '', 'Не теряй бдительности. Если однажды тебе удалось сбежать из Утробы, это еще не значит, что повезет еще раз.', 38134),
(203394, 'ruRU', 'Высокочтимый Голос приняла решение. Идем, смертная душа. Я отведу тебя к Кольцу Перенаправления.', '', 38134),
(203363, 'ruRU', '', 'Мы не позволим ему забрать Андуина.', 38134),
(203362, 'ruRU', '', 'Ее темное влияние затронуло не только Артаса и Болвара. Клинок забрал много жизней. Много душ.', 38134),
(203361, 'ruRU', '', 'Магия рун на Ледяной Скорби была неведома на Азероте до Короля-лича. Но та же самая магия пронизывает Утробу... и Торгаст.', 38134),
(203360, 'ruRU', '', 'И в судьбе моего отца. Шлем... и меч... все это время они были орудиями Тюремщика?', 38134),
(203359, 'ruRU', '', 'Призрак моего брата не оставляет всех нас. Он сам выбрал свой путь, хотя, судя по всему, Тюремщик мог принимать участие в его судьбе.', 38134),
(203358, 'ruRU', '', 'Спасибо, Телия. Утроба – место, где нет надежды. Такую пустоту я чувствовала лишь однажды... когда встретилась с Артасом в Ледяной Короне.', 38134),
(203357, 'ruRU', '', 'Я счастлива, что вы живы, лорд-адмирал. Не могу даже представить, через что вам всем пришлось пройти.', 38134),
(203353, 'ruRU', 'Прячьтесь за льдом!', '', 38134),
(203352, 'ruRU', '', 'Прячься за ледяными глыбами!', 38134),
(203347, 'ruRU', '', 'Возвращайся в Орибос. Скажи, что Малдраксус больше не нападет на другие миры. Наш враг – в наших же землях.', 38134),
(203346, 'ruRU', '', 'Вечные... Тюремщик... Тревожное послание, $n.', 38134),
(203333, 'ruRU', 'Пять качеств. Пять рун на клинке. Это те же руны, что вырезаны на Престоле Примаса.', '', 38134),
(203332, 'ruRU', 'Неудержимую волю к победе... даже перед лицом неизбежного поражения.', '', 38134),
(203331, 'ruRU', 'Мудрость, чтобы разгадать их планы и хитрость, чтобы их обмануть.', '', 38134),
(203330, 'ruRU', 'Стремление превзойти врагов и мощь, чтобы сокрушить их.', '', 38134),
(203322, 'ruRU', '', 'Мы многому друг у друга научимся.', 38134),
(203318, 'ruRU', 'Если не получается найти материал, обратись к Дактилии. В каждом чертеже указаны материалы, а в списках материалов есть подсказки, где их собирать. \n\nПомни, что их надо искать по всем Темным Землям. Придется оставить Бастион!', '', 38134),
(203316, 'ruRU', '', 'Положись на меня.', 38134),
(203315, 'ruRU', '', 'Вот меня и нет.', 38134),
(203314, 'ruRU', '', 'Интересуешься чем-то запретным?', 38134),
(203313, 'ruRU', '', 'Огненный шар!', 38134),
(203312, 'ruRU', '', 'Некротическая стрела!', 38134),
(203311, 'ruRU', '', 'Ха... Бз-з-з!', 38134),
(203310, 'ruRU', '', 'Зови, если что.', 38134),
(203308, 'ruRU', '', 'Займемся магией!', 38134),
(203298, 'ruRU', 'Вот бы нам как-нибудь встретиться при лучших обстоятельствах.', '', 38134),
(203293, 'ruRU', 'Все на борт!', '', 38134),
(203292, 'ruRU', 'Я сейчас же запишу историю наших приключений!', '', 38134),
(203289, 'ruRU', 'Чудесно, еще одна глава для моей книги!', '', 38134),
(203285, 'ruRU', 'А как вам такое?', '', 38134),
(203284, 'ruRU', 'Я тебе кое-что оставил.', '', 38134),
(203282, 'ruRU', 'Как я пахну?', '', 38134),
(203279, 'ruRU', '', 'Где-то здесь?', 38134),
(203277, 'ruRU', '', 'Поищу приключений где-нибудь еще!', 38134),
(203271, 'ruRU', 'Существование – это то, как ты воспринимаешь вселенную.', 'Существование – это то, как ты воспринимаешь вселенную.', 38134),
(203270, 'ruRU', 'Это может изменить саму твою душу.', 'Это может изменить саму твою душу.', 38134),
(203269, 'ruRU', 'Береги свою душу.', 'Береги свою душу.', 38134),
(203268, 'ruRU', 'Я с радостью отправлюсь с тобой в приключение.', 'Я с радостью отправлюсь с тобой в приключение.', 38134),
(203267, 'ruRU', '', 'Пора копать!', 38134),
(203266, 'ruRU', 'Примас воплощал пять качеств идеального солдата Малдраксуса.', '', 38134),
(203265, 'ruRU', '', 'Когда найдешь ключ, приходи к клетке Ансида. Поспеши.', 38134),
(203263, 'ruRU', 'Магазин закрывается!', '', 38134),
(203262, 'ruRU', 'У нас открыто!', '', 38134),
(203260, 'ruRU', '', 'Сейчас подлатаю!', 38134),
(203259, 'ruRU', '', 'Будет лучше, чем было!', 38134),
(203258, 'ruRU', '', 'Ты крепкий орешек... переживешь.', 38134),
(203257, 'ruRU', '', 'Кто-то поранился?', 38134),
(203255, 'ruRU', '', 'Пилигрим! Ансида взяли в плен. Надо освободить его, прежде чем двигаться дальше.', 38134),
(203253, 'ruRU', 'Трезубец глубинного эха защищает от магии!\n\nВраг колдует что-то плохое? Хочет тебе навредить?\n\nА вот все! Бросаешь копье, прерываешь заклинание! И еще немного урона наносишь, да.', '', 38134),
(203252, 'ruRU', 'Даже не думай! Обратно на болото захотел?', '', 38134),
(203251, 'ruRU', 'Да, я всех удивлю своим танцем!', '', 38134),
(203250, 'ruRU', 'Мы с Дуборылом будем как настоящие аристократы.', '', 38134),
(203249, 'ruRU', 'В тебе есть отвага, мощь и хитрость – все, что нужно для победы.', '', 38134),
(203248, 'ruRU', '', 'Почту за честь сражаться вместе с тобой, $p.', 38134),
(203247, 'ruRU', 'Надеюсь, твой принц не окажется слабаком.', '', 38134),
(203246, 'ruRU', 'Джайна, что-то не так. Путевой камень больше не заряжается!', '', 38134),
(203245, 'ruRU', '', 'Маркграфиня Син\'дейн попросила помочь ей разобраться с союзниками Кел\'Тузада. Пусть заплатят за все!', 38134),
(203244, 'ruRU', '', 'Я буду ждать тебя на месте встречи. Надеюсь, остальные уже там...', 38134),
(203233, 'ruRU', 'Тебе удалось многого достичь, но что ты можешь купить?\n\nЯ не беру взяток, так что если ты не начертатель... Мне нечего тебе предложить. Только не грусти.', 'Тебе удалось многого достичь, но что ты можешь купить?\n\nЯ не беру взяток, так что если ты не начертатель... Мне нечего тебе предложить. Только не грусти.', 38134),
(203231, 'ruRU', '', 'Мои сестры этого не показывают, но мы рады, что можем снова подняться в небо, и благодарны за это.', 38134),
(203229, 'ruRU', '', 'Что тебе нужно?', 38134),
(203226, 'ruRU', '', 'Они... ждали нас. Боюсь, Кхалик и Ансид в опасности.', 38134),
(203225, 'ruRU', '', 'Сюда! Скорее!', 38134),
(203224, 'ruRU', '', 'Берегись!', 38134),
(203223, 'ruRU', '', 'Осторожно... нас еще не заметили.', 38134),
(203222, 'ruRU', 'Не переживай. Мы вместе придумаем, как организовать прием. Глашатаи вечеринки!', '', 38134),
(203221, 'ruRU', '', 'Держись рядом, $p. Нас не должны заметить.', 38134),
(203205, 'ruRU', 'Я... Хорошо. Спасибо за совет, Ния.', '', 38134),
(203204, 'ruRU', '', 'Не упусти их. Они ценнее, чем кажутся на первый взгляд.', 38134),
(203203, 'ruRU', '', 'В твоих руках то, что дает безграничные возможности, Ренатал.', 38134),
(203202, 'ruRU', 'Может, какое-нибудь вкусное.', '', 38134),
(203201, 'ruRU', 'Какое растение вырастет из этой крошечной оболочки?', '', 38134),
(203200, 'ruRU', 'Принц Ренатал берет семя двумя пальцами.', '', 38134),
(203199, 'ruRU', 'Слизи всегда нужно больше!', '', 38134),
(203198, 'ruRU', 'Спасибо, изобретатель чумы, но для моих целей нужно совсем немного слизи.', '', 38134),
(203197, 'ruRU', 'Я состряпал пару отличных образцов. Найди меня за башней после приема, и я с тобой поделюсь.', '', 38134),
(203196, 'ruRU', 'Ищешь слизь для розыгрыша?', '', 38134),
(203193, 'ruRU', 'Спасибо. Я потом так разыграю Теотара...', '', 38134),
(203192, 'ruRU', 'Принц Ренатал кладет слизь в карман костюма.', '', 38134),
(203191, 'ruRU', '', 'Сплетни – это ресурс. Не более того.', 38134),
(203190, 'ruRU', '', 'Ты слишком все усложняешь, Ренатал.', 38134),
(203189, 'ruRU', 'Сплетни – как лужи землероев. Распространяются слишком быстро и всех пачкают.', '', 38134),
(203188, 'ruRU', 'Принц Ренатал решительно рвет бумагу.', '', 38134),
(203187, 'ruRU', 'Фейерверк! Фейерверк! Фейерверк!', '', 38134),
(203186, 'ruRU', 'Жаль. Это было бы весело, не будь оно так опасно.', '', 38134),
(203185, 'ruRU', 'Принц Ренатал щелкает пальцем по дефектному фейерверку.', '', 38134),
(203184, 'ruRU', '', 'Центурионы хранят ценные знания. Их нужно защищать любой ценой. Благодарю тебя.', 38134),
(203183, 'ruRU', '', 'Верховный автоматург сказал, что одна из наших \"Мнемид\" перестала выполнять приказы. Узнай, чего ей не хватает, чтобы вернуться к работе.', 38134),
(203182, 'ruRU', '', 'Рекруты, когда сразитесь с моим другом, можете уходить. Если справитесь хорошо, то вас еще позовут, когда будет нужно.', 38134),
(203181, 'ruRU', '', 'Плакаты сработали превосходно. К нам уже начали подтягиваться добровольцы.', 38134),
(203173, 'ruRU', 'Теперь прочь! Я достаточно терпел твое присутствие.', 'Теперь прочь! Я достаточно терпела твое присутствие.', 38134),
(203172, 'ruRU', 'Побыстрее с этим.', 'Побыстрее с этим.', 38134),
(203152, 'ruRU', 'Вот это будет зрелище! Надеюсь, на сегодняшнем испытании кто-нибудь по-настоящему умрет.', 'Вот это будет зрелище! Надеюсь, на сегодняшнем испытании кто-нибудь по-настоящему умрет.', 38134),
(203150, 'ruRU', 'О, надеюсь мой противник будет из дома Дарквейн. У меня с ними свои счеты!', 'О, надеюсь мой противник будет из дома Дарквейн. У меня с ними свои счеты!', 38134),
(203149, 'ruRU', 'Никому не победить меня. О моем мастерстве ходят легенды.', 'Никому не победить меня. О моем мастерстве ходят легенды.', 38134),
(203148, 'ruRU', 'Когда дойдет до поединка, дом Саурвайн заплатит за свои преступления!', 'Когда дойдет до поединка, дом Саурвайн заплатит за свои преступления!', 38134),
(203147, 'ruRU', '', 'Предлагаю отступить. Я отведу вас в укромный уголок возле путевого камня, который помог вам сбежать в прошлый раз.', 38134),
(203146, 'ruRU', 'Я хочу прославить свой дом, а не себя.', 'Я хочу прославить свой дом, а не себя.', 38134),
(203145, 'ruRU', 'Постойте. Мой меч... Вестник Судьбы. Он мне скоро понадобится.', '', 38134),
(203143, 'ruRU', 'Береги силы, отец. Поговорим потом.', '', 38134),
(203142, 'ruRU', 'Испытание на место в элитном отряде – отличная возможность заколоть какого-нибудь выскочку из дома Примроуз. Я в деле.', 'Испытание на место в элитном отряде – отличная возможность заколоть какого-нибудь выскочку из дома Примроуз. Я в деле.', 38134),
(203141, 'ruRU', 'Сын мой...', '', 38134),
(203137, 'ruRU', 'Вот это развлечения задарма!', '', 38134),
(203136, 'ruRU', 'Не бойся, Габбинс. Мы защитим тебя от этого бессердечного шарлатана!', '', 38134),
(203133, 'ruRU', 'Не друг. Габбинс не любит фейерверки.', '', 38134),
(203131, 'ruRU', 'Выпроводите его отсюда и не слушайте, что он скажет!', '', 38134),
(203130, 'ruRU', 'Нет, конечно! Я бы ни за что не стал сплетничать с таким негодяем!', '', 38134),
(203129, 'ruRU', '', 'В цитадели Вечных Мук запах анимы выдаст тебя, смертное существо. Остановись!', 38134),
(203128, 'ruRU', '', 'Ты не продержишься в Загонах, смертная душа. Остановись!', 38134),
(203126, 'ruRU', 'Убери эту пакость, пока не пострадали наши прекрасные декорации!', '', 38134),
(203125, 'ruRU', 'Неплохо... Неплохо.', '', 38134),
(203124, 'ruRU', '', 'Вестник судьбы, да? Я сделала этот меч для него, когда его провозгласили бароном. Он хотел оружие, похожее на то, которым он сражался при жизни. Судя по твоей реакции, его форма тебе знакома. Думаю, я бы очень привязалась к своему мечу, если бы у нас был одинаковый титул.\n\nЭто одно из моих лучших творений. Александросу пришлось потрудиться, чтобы добыть материалы для его изготовления. Не знаю, откуда этот череп, но точно не из Малдраксуса.', 38134),
(203122, 'ruRU', '', 'Рада снова тебя видеть, друг мой. Интересные времена, да?', 38134),
(203112, 'ruRU', '', 'Привет, |3-6($c). Я подсказываю опытным странникам, как лучше помогать другим.', 38134),
(203109, 'ruRU', '', 'Странно... Горак Зар запомнилась мне совсем другой...', 38134),
(203108, 'ruRU', '', 'Глупцы! Вы – ничто перед гораком Зар!', 38134),
(203107, 'ruRU', 'Я несу сладости для гостей Пепельного двора!', '', 38134),
(203106, 'ruRU', 'Привет, |3-6($c). Я подсказываю опытным странникам, как лучше помогать другим.', 'Привет, |3-6($c). Я подсказываю опытным странникам, как лучше помогать другим.', 38134),
(203100, 'ruRU', '', 'Все готово. Дай Твигину знать, когда сможешь отправиться в путь.', 38134),
(203098, 'ruRU', 'Я захвачу немного взрывчатки и пущу ее в ход!', '', 38134),
(203097, 'ruRU', 'Это респектабельный прием, а не Гиблотопь!', '', 38134),
(203096, 'ruRU', 'Фонари – хорошо, а факелы – плохо.', '', 38134),
(203095, 'ruRU', '', 'Мы проникнем в некрополь, пока Ансид будет отвлекать кадавров.', 38134),
(203094, 'ruRU', '', 'Осмотри местность, пока вы с Твигином летите к нам.', 38134),
(203084, 'ruRU', 'Благодарю, пилигрим Утробы.', '', 38134),
(203083, 'ruRU', '', 'Приятно провести время.', 38134),
(203082, 'ruRU', '', 'Обвинительница усмехается.', 38134),
(203081, 'ruRU', 'Ты можешь рассчитывать на меня, пилигрим! Я лучший в чем угодно!', '', 38134),
(203080, 'ruRU', 'Глашатай вечеринки ждет приказаний!', '', 38134),
(203079, 'ruRU', 'Темель на секунду выглядит растерянным, затем быстро достает из-за пояса праздничный колпак и надевает на голову.', '', 38134),
(203078, 'ruRU', '', 'Сейчас ты мне не нужен в качестве Глашатая грехов. Ты поможешь пилигриму Утробы организовать новый прием.', 38134),
(203077, 'ruRU', 'Несравненный Глашатай грехов к услугам Обвинительницы!', '', 38134),
(203076, 'ruRU', '', 'Темель!', 38134),
(203075, 'ruRU', '', 'Но если принцу требуется кому-то это поручить...', 38134),
(203074, 'ruRU', '', 'Я не стану этого делать. Грешные души требуют моего полного внимания.', 38134),
(203073, 'ruRU', 'Мне не терпится рассказать об этом леди Лерэ!', 'Мне не терпится рассказать об этом леди Лерэ!', 38134),
(203071, 'ruRU', 'А! Пилигрим Утробы. Мы только что о тебе говорили.\n\nЧем обязаны такой чести?', '', 38134),
(203070, 'ruRU', 'О-о-о! Это будет самый обсуждаемый прием в Ревендрете.', 'О-о-о! Это будет самый обсуждаемый прием в Ревендрете.', 38134),
(203069, 'ruRU', 'И конечно, я обязательно включу вас всех в список приглашенных.', '', 38134),
(203068, 'ruRU', 'Мои обширные связи и скромные дипломатические способности к твоим услугам.', '', 38134),
(203067, 'ruRU', 'Помогать тебе и принцу Ренаталу – честь для меня.', '', 38134),
(203066, 'ruRU', 'Этот клыкастый разбирается в талантах. Все будет по первому разряду.', '', 38134),
(203065, 'ruRU', 'Это Теотар тебе сказал?', '', 38134),
(203064, 'ruRU', 'И ты доверяешь землерою такую работу?', '', 38134),
(203062, 'ruRU', 'Знаешь такую труппу – \"Потерянный Грааль\"?\n\nСтоящая музычка. Привязчивая такая... одухотворенная.\n\nЯ начал читать их биографии. Наконец-то нашел, ради чего стоило учиться читать!', '', 38134),
(203061, 'ruRU', 'На пару слов, пилигрим.', '', 38134),
(203060, 'ruRU', 'Я буду нашим самым строгим критиком и самым горячим сторонником!', '', 38134),
(203059, 'ruRU', 'Свежее мясо! Экзотические фрукты и овощи! Только чистые землерои-официанты!', '', 38134),
(203058, 'ruRU', 'Конечно, надо взять только лучшие ингредиенты...', '', 38134),
(203056, 'ruRU', '<Привереда Стефан перестает диктовать висящему в воздухе свитку и смотрит на вас.>\n\nДа? Что думаешь про многогранный аромат этого стейка?', '', 38134),
(203055, 'ruRU', 'Для начала нам нужно больше чая. Весь что есть!', '', 38134),
(203054, 'ruRU', 'Все мое прошлое было просто подготовкой к этой великой минуте. Я научу тебя всему, что только надо знать!', '', 38134),
(203053, 'ruRU', 'Но я тоже рад это слышать. Хотя тебе придется напомнить мне основы придворного этикета.', '', 38134),
(203052, 'ruRU', 'Мне казалось, ты сам же их и распускал, Теотар.', '', 38134),
(203051, 'ruRU', 'Мой дорогой Ренатал, слухи правдивы. Мы устроим в Грехопаде прием!', '', 38134),
(203050, 'ruRU', 'Я весь дрожу от волнения. У меня срочные новости!', '', 38134),
(203049, 'ruRU', '', 'Так точно. Мы сейчас же отправимся к Пепельному двору.', 38134),
(203047, 'ruRU', '', 'У нас еще много работы.', 38134),
(203038, 'ruRU', 'Хорошо. Я дам тебе шанс объяснить, что ты здесь делаешь.', 'Хорошо. Я дам тебе шанс объяснить, что ты здесь делаешь.', 38134),
(203037, 'ruRU', 'Стоп... Не стрелять! Знакомое лицо.', 'Стоп... Не стрелять! Знакомое лицо.', 38134),
(203036, 'ruRU', 'Кто вторгается в мои владения?', 'Кто вторгается в мои владения?', 38134),
(203035, 'ruRU', 'Ни шагу дальше!', 'Ни шагу дальше!', 38134),
(203029, 'ruRU', '', 'Ловушка. Хм. Ну же, покажи нам свою легендарную силу, смертное создание!', 38134),
(203028, 'ruRU', 'Кто... кто здесь? Надо... предупредить маркграфа...', '', 38134),
(203027, 'ruRU', '', 'Задолго до тебя, смертный. Это все, что тебе надо знать.', 38134),
(203026, 'ruRU', 'Как давно ты попала в Утробу?', '', 38134),
(203022, 'ruRU', '', 'Раздор и засуха ослабляют рощу. Поможешь ей – поможешь и духам.', 38134),
(203015, 'ruRU', 'Лисония ослаблена, но я все еще не уверен, что мы с тобой сможем противостоять ее страшной силе.\n\nВ любом случае, надо попробовать. Ты со мной?', '', 38134),
(203014, 'ruRU', 'Столько павших... а идеал не думает ни о чем, кроме своей Десницы.\n\nНадо действовать.', '', 38134),
(203009, 'ruRU', 'Мы понимаем опасность. Но должны попытаться.', '', 38134),
(203008, 'ruRU', '', 'В царство тьмы существует лишь один путь. Тот, которым следуют души, приговоренные к вечному заточению.', 38134),
(203007, 'ruRU', 'Высокочтимый Голос, мы хотим войти в Утробу.', '', 38134),
(203006, 'ruRU', 'Я не знаю, где искать ни этого малдраксийского барона, ни наших товарищей. Но я могу помочь тебе найти Дариона Могрейна. Начнем с этого.', '', 38134),
(203005, 'ruRU', 'Похоже, тебе вновь придется столкнуться с ужасами Утробы, $n.', '', 38134),
(202998, 'ruRU', 'Я видел много разных приемов.\n\nНо будь моя воля, ноги бы моей там не было.', '', 38134),
(202997, 'ruRU', 'Что может быть лучше, чем пить хороший чай на приеме вместе с друзьями?\n\nЕсли не знаешь, что делать, просто налей еще чая. Это всегда помогает!', '', 38134),
(202996, 'ruRU', 'Мне не нравятся приемы так, как Теотару, и я не жажду их организовывать так, как Темель.\n\nНо я знаю свои сильные стороны. Я умею завоевывать дружбу союзников с самого начала времен.\n\nПепельный двор прославит наш ковенант и усилит наше влияние.\n\nЯ рад, что затеваю это грандиозное предприятие вместе с тобой.', '', 38134),
(202992, 'ruRU', 'Наконец-то! Еда для настоящих чемпионов!', '', 38134),
(202991, 'ruRU', '', 'Я вернусь на \"Нураккир\". Встретимся там.', 38134),
(202990, 'ruRU', 'Гр-р... Пилигрим! Покончи... с этим!..', '', 38134),
(202989, 'ruRU', 'Чего-то ищешь?', '', 38134),
(202988, 'ruRU', 'Эй! Чего пялишься?', '', 38134),
(202987, 'ruRU', 'Что привело тебя к Нурману?', '', 38134),
(202985, 'ruRU', '', 'Наши мастера трудятся не покладая рук. Антураж при Пепельном дворе будет просто ослепительным!', 38134),
(202984, 'ruRU', 'Пепельный двор официально открывается!', '', 38134),
(202983, 'ruRU', 'Все такое чистое! Мы тут все мигом подготовим!', '', 38134),
(202982, 'ruRU', 'Нам еще чистить и чистить перед первым настоящим приемом!', '', 38134),
(202981, 'ruRU', 'Я не привык к таким приемам, но если остальным понравилось...', '', 38134),
(202980, 'ruRU', 'Потрясающий официальный прием! Габбинс и Таббинс просто обязаны посетить следующий!', '', 38134),
(202979, 'ruRU', 'Мой долг исполнен.', '', 38134),
(202978, 'ruRU', 'Тебе удалось невозможное, пилигрим. Мне действительно понравился этот прием! Впечатляет.', '', 38134),
(202977, 'ruRU', 'Конечно, Темель. Начинай раздавать приглашения нашим первым гостям.', '', 38134),
(202976, 'ruRU', 'Я подготовлю все для наших ритуалов при Пепельном дворе.', '', 38134),
(202975, 'ruRU', 'Грязетряс ворчит, толкая тачку.', '', 38134),
(202974, 'ruRU', 'Вот поэтому я тебя и не звал, Грязетряс! Иди давай!', '', 38134),
(202973, 'ruRU', 'А я люблю, когда грязно!', '', 38134),
(202972, 'ruRU', 'Да-да, иди и уберись еще немного. Землерои опять намусорили!', '', 38134),
(202971, 'ruRU', 'Через пару минут я соберу всех на помосте для даров на завершающую церемонию.', '', 38134),
(202970, 'ruRU', 'Хватит лекций, Темель. Пусть пилигрим наслаждается приемом!', '', 38134),
(202969, 'ruRU', 'В случае прямого противоречия, как между Теотаром и старшим дозорным Боромодом, лучше выбрать кого-то одного и позаботиться о нем.', '', 38134),
(202968, 'ruRU', 'То, что может развлечь одного гостя, будет неприятно для другого.', '', 38134),
(202967, 'ruRU', 'Правда в том, что всем нравятся разные приемы.', '', 38134),
(202966, 'ruRU', 'Дорогой мой, я вынужден сказать, что ты заблуждаешься. Я посещал лучшие приемы во всем Ревендрете и...', '', 38134),
(202965, 'ruRU', 'Вот это было весело! На приемах должно быть меньше чая и больше драк!', '', 38134),
(202934, 'ruRU', '', 'Я рада наделить тебя этим даром. Итак, пойдем?', 38134),
(202933, 'ruRU', '', 'Итак, $p, я передаю тебе дар Королевы. Встань.', 38134),
(202932, 'ruRU', '', '\"Я наделяю тебя даром. Слейся с дикой природой. Прими любой облик, какой ни пожелаешь\".', 38134),
(202931, 'ruRU', '', '\"Но как, о Королева?\" – спросила душа. \"Ведь у меня больше нет тела\".', 38134),
(202930, 'ruRU', '', '\"Ухаживай за духами до конца вечности. Круговорот должен продолжаться\".', 38134),
(202929, 'ruRU', '', 'Душа преклонила колено. \"Чем я могу вам служить?\"', 38134),
(202928, 'ruRU', '', '\"Тогда входи по праву,\" – ответила Королева.', 38134),
(202927, 'ruRU', '', '\"Меня послали к тебе, ибо я посвятила жизнь служению природе\".', 38134),
(202926, 'ruRU', '', 'Наша Королева встретила ее у ворот. \"Ты не дух и не пикси. Зачем ты пришла в мой лес?\"', 38134),
(202925, 'ruRU', '', 'Давным-давно, на заре Арденвельда, в наши леса прибыла первая смертная душа.', 38134),
(202923, 'ruRU', 'Попробуй изготовить что-нибудь новое вместе с Дактилией. Еще ты можешь записать больше воспоминаний или опробовать новое снаряжение!\n\nЕсли какое-то воспоминание или босс еще не были побеждены, выложись на полную и заработай награду. Новые подвески могут дать твоим медиумам небольшое преимущество, которого им как раз не хватало.\n\nНаконец, преодолевай новые испытания. Рядом ты найдешь торговцев с великолепными наградами, которые можно получить только за прохождение испытаний на Пути Перерождения. Попробуй собрать их все!', '', 38134),
(202921, 'ruRU', 'Против разных воспоминаний лучше сражаются разные медиумы. Можно попробовать выставить в этом бою другого.\n\nИли снабди своего медиума снаряжением и подвесками. Снаряжение даст ему активную способность, а подвески – пассивные бонусы. И то, и другое может решить исход боя. Если хочешь, могу рассказать об этом больше!\n\nНу и наконец, твой уровень известности в ковенанте также делает медиумов сильнее. Если ты не можешь одолеть воспоминание, не спеши. Заработай известность, усиль своего медиума и попробуй еще раз.', '', 38134),
(202919, 'ruRU', '', 'Мы будем сильными союзниками, пилигрим. Но не друзьями. \"Друзей\" мне не надо.', 38134),
(202914, 'ruRU', 'В отличие от снаряжения, которое надо активировать, подвески дают пассивный эффект. Их можно использовать только один раз, поэтому делай сразу несколько!\n\nМожно надеть только одну подвеску каждого типа, но разных подвесок можно надеть сколько угодно. Можешь комбинировать их как захочешь.', '', 38134),
(202912, 'ruRU', 'Твой медиум может столкнуться с испытанием, в котором ему понадобится дополнительная помощь – для этого и нужно снаряжение. Оно дает медиумам новые способности, такие как рывок.\n\nСозданный предмет снаряжения можно использовать сколько угодно. Чтобы снарядить родственную душу, поговори с Паолоне. Одновременно можно надеть только один предмет снаряжения, поэтому выбирай тот, который лучше всего поможет в следующем испытании.', '', 38134),
(202910, 'ruRU', 'Путь Перерождения ведет к почету и славе, но мы не должны забывать о смирении. Медальоны Служения – знак заслуг, позволяющий встать на Путь Перерождения.\n\nИх дают за смиренное служение Бастиону. Чтобы их получить, отвечай на призывы, ищи сокровища и убивай редких противников. Ну и, конечно, можешь сдавать ресурсы Дактилии – так тоже можно зарабатывать медальоны.', '', 38134),
(202909, 'ruRU', 'Я не любитель разговоров. Чем могу помочь?', 'Я не любительница разговоров. Чем могу помочь?', 38134),
(202908, 'ruRU', 'Мне еще не доводилось нести службу на приеме. Я буду стараться.', 'Мне еще не доводилось нести службу на приеме. Я буду стараться.', 38134),
(202906, 'ruRU', 'Как ты думаешь, после пира еда останется?', 'Как ты думаешь, после пира еда останется?', 38134),
(202905, 'ruRU', 'Новые чертежи можно получить, побеждая проекции в колизее. Принеси их Дактилии и сможешь создавать новое снаряжение, приманки и подвески. Попытайся собрать все чертежи и опробовать разные сочетания.', '', 38134),
(202903, 'ruRU', 'Я готов служить.', 'Я готова служить.', 38134),
(202902, 'ruRU', 'Чтобы сразиться с более сильными проекциями, нужно в первую очередь усилить зеркало души. Так оно сможет вызывать более мощные воспоминания. Чтобы это сделать, тебе нужно улучшить обитель ковенанта.\n\nКроме того, некоторые испытания сложнее других. Ты не сможешь в них участвовать, пока не победишь достаточно боссов.\n\nИ конечно же, тебе не удастся сразиться с более сильной проекцией, не победив сначала ее более слабую версию.', '', 38134),
(202900, 'ruRU', 'Дактилия сообщит, когда появятся новые воспоминания. Для каждого из них потребуется своя приманка и зеркало души. С ними она тоже тебе поможет. Как только отдашь их Дактилии, она расскажет, как принести в колизей отпечаток воспоминания для тренировок твоих медиумов.', '', 38134),
(202899, 'ruRU', 'Любые нарушители будут иметь дело со мной.', 'Любые нарушители будут иметь дело со мной.', 38134),
(202897, 'ruRU', 'Я к твоим услугам.', 'Я к твоим услугам.', 38134),
(202896, 'ruRU', 'Чем могу быть полезен?', 'Чем могу быть полезна?', 38134),
(202895, 'ruRU', 'За Грехопад.', 'За Грехопад.', 38134),
(202893, 'ruRU', '', 'Остальные обитатели леса уже собираются. Когда ты позовешь нас на Пепельный двор, мы сразу явимся.', 38134),
(202892, 'ruRU', 'Калисфена может многому научить. Остальное за тобой.', '', 38134),
(202889, 'ruRU', 'Этот бой войдет в историю!', '', 38134),
(202888, 'ruRU', 'Помни, чему тебя учили.', '', 38134),
(202886, 'ruRU', 'Должен сказать, распорядителям нечасто предлагают сражаться на Пути. Впрочем, ты не простой распорядитель!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202881, 'ruRU', 'Настало время нового испытания на Пути. Кто наш претендент?', '', 38134),
(202880, 'ruRU', 'Пришло время ступить на Путь Перерождения и приобщиться к нашей священной традиции!', '', 38134),
(202868, 'ruRU', 'Не всем нравится то же, что тебе, Теотар. Надо угодить всем гостям.', '', 38134),
(202867, 'ruRU', 'Пара драк будет приятной переменой после всей этой официальной скукоты.', '', 38134),
(202866, 'ruRU', 'Хочешь поднять мне настроение – сразись с кем-нибудь из моих новобранцев.', '', 38134),
(202864, 'ruRU', 'Отстань. Меня не интересуют глупые формальности двора.', '', 38134),
(202863, 'ruRU', 'Не знаю, Теотар... Боромод не очень любит церемонии...', '', 38134),
(202862, 'ruRU', 'Начиная с нашего мрачного дозорного. Уверен, он оценит, что его включили в круг избранных.', '', 38134),
(202861, 'ruRU', 'Налейте всем по чашке чая!', '', 38134),
(202859, 'ruRU', '', 'Пелагий справился с очередным испытанием!', 38134),
(202857, 'ruRU', '', 'Кто знал, что распорядитель и голиаф могут ТАКОЕ?', 38134),
(202856, 'ruRU', '', 'Славный бой!', 38134),
(202854, 'ruRU', 'Спасибо, пилигрим Утробы. При дворе надо соблюдать этикет. Не важно, где этот двор находится.', '', 38134),
(202853, 'ruRU', '', 'Противник повержен!', 38134),
(202852, 'ruRU', '', 'Сохраняй отвагу перед лицом опасности!', 38134),
(202851, 'ruRU', '', 'Мой тебе совет, Пелагий. Не давай противнику подобраться близко.', 38134),
(202850, 'ruRU', '', 'Мне не терпится увидеть, как \"Брон\" сражается. Может, мне самой с ним потренироваться?', 38134),
(202849, 'ruRU', '', 'Сосредоточься, Клейя. Ты справишься!', 38134),
(202848, 'ruRU', '', 'А, \"Атанос\"... Один из моих любимых бойцов. Надеюсь, ты продержишься дольше, чем прошлый участник!', 38134),
(202847, 'ruRU', '', 'Палящий Свет свел его с ума. Этот противник точно не станет сдерживаться!', 38134),
(202845, 'ruRU', '', 'Друсты держат в страхе весь Арденвельд. Даже одного друста нельзя недооценивать.', 38134),
(202842, 'ruRU', '', 'Яростные солдаты Дикой Охоты готовы столкнуться с любыми испытаниями. Посмотрим, как ты с ними справишься.', 38134),
(202841, 'ruRU', '', 'Владычица Ненависти... Боюсь даже предположить, чем она заслужила такое имя.', 38134),
(202839, 'ruRU', '', 'У нашего бойца проблемы!', 38134),
(202838, 'ruRU', '', 'Ну же, очнись!', 38134),
(202837, 'ruRU', '', 'Положение у нашего бойца незавидное!', 38134),
(202836, 'ruRU', '', 'Смотри под ноги!', 38134),
(202835, 'ruRU', '', 'Никто не говорил, что яд запрещен!', 38134),
(202834, 'ruRU', '', 'Нашего бойца что-то притягивает!', 38134),
(202831, 'ruRU', '', 'Этого бойца так просто не остановить!', 38134),
(202830, 'ruRU', '', 'Еще немного! Ты справишься!', 38134),
(202828, 'ruRU', '', 'Кажется, твоему голиафу понадобится ремонт.', 38134),
(202822, 'ruRU', '', 'Чем больше поражений, тем слаще победа!', 38134),
(202821, 'ruRU', '', 'А вот и твой урок смирения.', 38134),
(202820, 'ruRU', '', 'Отдохни и попробуй еще раз!', 38134),
(202817, 'ruRU', '', 'Я ждала этого весь день! Пора начать новое испытание!', 38134),
(202816, 'ruRU', '', 'Начинаем испытание!', 38134),
(202815, 'ruRU', 'Я заварю еще чайник на всякий случай.', '', 38134),
(202814, 'ruRU', 'Подавай чай, пока он еще горячий.', '', 38134),
(202813, 'ruRU', '', 'Отличный показательный бой. Я знала, что тебе предназначена великая судьба, с тех пор как мы только встретились.', 38134),
(202812, 'ruRU', '', 'Мои дорогие претенденты, перед вами $p, что не боится выйти против самого \"Атаноса\"!', 38134),
(202811, 'ruRU', '', 'Копье невероятно тяжелое – его можно поднять лишь ненадолго. Надо сделать все быстро!', 38134),
(202810, 'ruRU', '', 'Калисфена тренирует претендентов многие тысячелетия. Посмотрим, как ты справишься.', 38134),
(202809, 'ruRU', 'Ты совершенствуешься, $p. Возьми мое воспоминание.', '', 38134),
(202808, 'ruRU', '', 'Тебе еще многому надо научиться.', 38134),
(202807, 'ruRU', 'Любезный Темель, откуда ты узнал, что \"самый дорогой\" – мой любимый вкус?', '', 38134),
(202806, 'ruRU', 'Пилигрим, подай Теотару чашку нашего самого дорогого чая.', '', 38134),
(202804, 'ruRU', 'А ты что скажешь? Мы готовы открыть Пепельный двор для посторонних?', '', 38134),
(202803, 'ruRU', 'Хочешь церемоний? Это можно устроить!', '', 38134),
(202802, 'ruRU', 'Но должен сказать, что влиятельные персоны привыкли к более... церемониальной атмосфере.', '', 38134),
(202801, 'ruRU', 'Это хорошее начало, мой дражайший принц.', '', 38134),
(202800, 'ruRU', 'Теотар, ты всегда был душой компании и любил подобные шумные развлечения.', '', 38134),
(202799, 'ruRU', 'Я выполняю свой долг.', '', 38134),
(202798, 'ruRU', 'Принц Ренатал подмигивает старшему дозорному Боромоду.', '', 38134),
(202797, 'ruRU', 'Наши гости и аристократы хорошо проводят время. Хотя наш дозорный, кажется, чем-то недоволен.', '', 38134),
(202796, 'ruRU', 'Я впечатлен, Темель. Вы с пилигримом Утробы столько всего сделали! Просто чудесно.', '', 38134),
(202795, 'ruRU', 'А вот и принц Ренатал с нашим особым гостем!', '', 38134),
(202794, 'ruRU', 'Блестяще. Теперь проделай то же с чаном с ферментами.', '', 38134),
(202793, 'ruRU', '', 'Чудесные создания, не правда ли? Они и сами это знают! Такие гордые...', 38134),
(202792, 'ruRU', '', 'Когда-то мы использовали его магию, чтобы закрыть выход из Утробы... Чтобы Изгнанник навсегда оставался в заточении.', 38134),
(202790, 'ruRU', 'Я каждого землероя приставил к делу, чтобы к приходу принца все сияло.', '', 38134),
(202789, 'ruRU', 'Мне бы пригодилась твоя помощь на этой репетиции приема.', '', 38134),
(202786, 'ruRU', 'Звездочка моя... Я... исполню твою просьбу.', '', 38134),
(202785, 'ruRU', '', 'Но теперь я прошу тебя об одном: скажи нам, живы ли они.', 38134),
(202784, 'ruRU', '', 'Я ничего у тебя не просила с тех пор, как была ребенком. С тех пор, как ты отослал меня.', 38134),
(202783, 'ruRU', '', 'Я пришла в мир Смерти, чтобы встретиться с отцом, которого когда-то потеряла. С Болваром Фордрагоном, храбрейшим из героев Азерота.', 38134),
(202782, 'ruRU', 'Ты не понимаешь, как это опасно, Телия. Если я загляну в Утробу...', '', 38134),
(202781, 'ruRU', '', 'Прошу, отец. Нам надо знать, можно ли спасти наших друзей!', 38134),
(202780, 'ruRU', 'Я отправляюсь навстречу новой жизни! Или жизней? Пока остановимся на одной. До свидания!', '', 38134),
(202779, 'ruRU', 'Удачи на твоей арене или о чем тебе хотелось меня попросить...', '', 38134),
(202778, 'ruRU', 'Изумительно! Совсем не то, что мы ожидали, но на то она и наука!', '', 38134),
(202775, 'ruRU', 'Хорошо. Ему понравится.', '', 38134),
(202774, 'ruRU', 'Тонкий ценитель? Я заварю мой лучший чай.', '', 38134),
(202773, 'ruRU', 'У тебя там случайно нет чая, Стефан? Гостю он придется по душе.', '', 38134),
(202772, 'ruRU', 'Принц Ренатал приведет гостя? Ох ты! Надо приготовить особые угощения.', '', 38134),
(202771, 'ruRU', 'Остальное уберем потом. Принц Ренатал вот-вот прибудет с особым гостем.', '', 38134),
(202770, 'ruRU', 'Вот это техника! Вы, землерои, могли бы и поучиться у пилигрима.', '', 38134),
(202769, 'ruRU', 'Молодец, пилигрим! Вычисти остальное, пока не прибыли гости.', '', 38134),
(202768, 'ruRU', 'Ну вот, уже лучше! Обожаю чистоту.', '', 38134),
(202767, 'ruRU', 'Вы двое патрулируйте с юга. Я разберусь с жителями.', '', 38134),
(202766, 'ruRU', '', 'Туда тебе и дорога. С такими лучше не связываться.', 38134),
(202765, 'ruRU', 'Есть, босс.', '', 38134),
(202763, 'ruRU', 'Надо бы почистить в зоне с угощениями, а то мне неудобно перед гостями.', '', 38134),
(202762, 'ruRU', 'Не обращай внимания на аристократов. Надо подумать о том, как впечатлить самых важных гостей.', '', 38134),
(202761, 'ruRU', 'Ах да, совсем забыл: я пригласил кое-кого из ковенанта, так сказать, на репетицию.', '', 38134),
(202760, 'ruRU', 'Здесь такая грязища! Надо прибраться, иначе не выйдет никакого приема.', '', 38134),
(202759, 'ruRU', 'Вот ты где, пилигрим!', '', 38134),
(202758, 'ruRU', 'Если тебе что-то непонятно или нужны мои наставления, я всегда к твоим услугам. Удачи на Пути и тебе, и твоим медиумам.', '', 38134),
(202757, 'ruRU', '', 'Дактилия поможет тебе записать новые воспоминания для тренировок или создать снаряжение и подвески, которые усилят твоих медиумов.', 38134),
(202756, 'ruRU', '', 'Дальнейшее зависит от тебя и твоих медиумов, $n. Вас ждет не только слава, но и много трудных уроков.', 38134),
(202755, 'ruRU', '', 'Теперь для каждого сражения на Пути потребуется медальон Служения, доказательство выполненного долга перед Бастионом. Путь ведет к славе, но и о смирении забывать не стоит. Вот тебе несколько медальонов для начала.', 38134),
(202752, 'ruRU', 'Я подержу снаряжение. Поговори со мной в колизее, если оно понадобится!', '', 38134),
(202741, 'ruRU', 'Сюда. Через этот телепорт ты попадешь в Кольцо Перенаправления.', '', 38134),
(202735, 'ruRU', 'Сейчас самое время, $p. Бери свое зеркало и действуй!', '', 38134),
(202734, 'ruRU', 'Я... мы... это я? Мы – это я!', '', 38134),
(202733, 'ruRU', 'Что... что произошло? Где я?', '', 38134),
(202727, 'ruRU', 'Свечи во двор! Стулья к столу с угощениями! Светильники на сцену!', '', 38134),
(202709, 'ruRU', '', 'С удовольствием.', 38134),
(202706, 'ruRU', 'Попробуй сперва одолеть меня!', '', 38134),
(202701, 'ruRU', '', 'Сейчас вы поймете, как глупо было идти против дома Ритуалов!', 38134),
(202698, 'ruRU', 'Молодец, усердно работаешь...', '', 38134),
(202697, 'ruRU', 'Теперь Таббинс делает чай. Лучший чай! Все говорят, как хорошо у меня получается!', '', 38134),
(202696, 'ruRU', 'Не-е-ет!', '', 38134),
(202694, 'ruRU', 'Да, это плохо. Очень плохо, не поспоришь.', '', 38134),
(202693, 'ruRU', 'Знаешь что, давай три капельки. Побалуем себя!', '', 38134),
(202692, 'ruRU', 'И наконец самое главное, твой катализатор! Размешать крохотным венчиком и...', '', 38134),
(202691, 'ruRU', 'Щепотку костяной пыли... капельку слизи...', '', 38134),
(202690, 'ruRU', 'Узрите плоды тысячелетий алхимических исследований и потраченных учеников!', '', 38134),
(202688, 'ruRU', 'Может быть, $p, я буду ждать перерождения дольше остальных, но я выбрал этот путь и счастлив следовать ему.\n\nКирии редко говорят о своем прошлом. Многие его вообще не помнят! Думаю, в будущем наш народ станет мудрее в этом отношении.\n\nКогда я рассказал то же самое Клейе, она выслушала меня – совсем как ты. Она поддержала меня, как настоящий друг, и ты тоже.\n\nСпасибо. Я рад, что поделился с тобой этим воспоминанием.', '', 38134),
(202686, 'ruRU', 'Но когда я прибыл в Бастион, внешне я стал выглядеть... \n\n<Пелагий показывает на себя.>\n\nВот так! Как мужчина!\n\nЯ почувствовал... облегчение! Радость! Как будто окружающие впервые увидели меня таким, какой я есть!\n\nЯ наконец понял, что значили мои чувства при жизни.', '', 38134),
(202684, 'ruRU', 'Спасибо за это. Правда. Я знаю, что могу доверять своему медиуму.\n\n<Пелагий делает глубокий вдох, затем медленно выдыхает.>\n\nПри жизни я был женщиной. Я не помню своего прошлого имени или даже расы, но... моя внешность никогда не отражала того, кто я на самом деле.\n\nТогда я не вполне это понимал, но сейчас осознаю со всей ясностью. Всю жизнь я старался примирить две стороны себя...', '', 38134),
(202682, 'ruRU', 'Кроме одного.\n\nМне тяжело думать о нем. Я знаю, что должен забыть, но... я хочу... или мне нужно... помнить.\n\nЗабыть его – значит потерять себя.', '', 38134),
(202680, 'ruRU', 'В последнее время почти нет.\n\nМоя прошлая жизнь кажется все более далекой, воспоминания блекнут, и многими из них я пожертвовал во имя своего долга.\n\nКроме...', '', 38134),
(202677, 'ruRU', 'Они говорили, что я сошел с ума! Они и сейчас так говорят!\n\nНо сегодня все изменится!', '', 38134),
(202667, 'ruRU', 'Даже самые могучие растут и вытягиваются благодаря тяжелому труду...', '', 38134),
(202665, 'ruRU', 'Не торопись, малыш.', '', 38134),
(202663, 'ruRU', '', 'В зеркале позади тебя хранятся все собранные тобой воспоминания. Выбери из них противника.', 38134),
(202662, 'ruRU', 'Чтобы попасть на острова, нужно пройти через грибные кольца. Доступ туда ограничен, но можешь попытаться.', 'Чтобы попасть на острова, нужно пройти через грибные кольца. Доступ туда ограничен, но можешь попытаться.', 38134),
(202661, 'ruRU', 'Сегодня Пелагий впервые ступит на Путь Перерождения. Да направят его шаги тысячи кирий-предшественников!', '', 38134),
(202659, 'ruRU', '', 'Приготовься и скажи, когда мне активировать устройство. Оно не сможет скрывать нас слишком долго.', 38134),
(202658, 'ruRU', '', 'Страх поглотит вас!', 38134),
(202653, 'ruRU', 'Ты со своими медиумами идешь по стопам многих предшественников. Участие в испытаниях на Пути Перерождения – величайшая честь!', '', 38134),
(202632, 'ruRU', 'Собачья Кость начинает рыть землю, к чему-то принюхиваясь.', '', 38134),
(202627, 'ruRU', '', 'Будет сделано.', 38134),
(202626, 'ruRU', '', 'Я чувствую, что в твоем хранителе душ множество калдорай, $n. Выпусти их в леса Арденвельда.', 38134),
(202621, 'ruRU', '', '<Глаза Королевы Зимы сияют от сдерживаемой силы. Она одобрительно смотрит на вас.>\n\nХорошо сражаешься, смертная душа.\n\nИдем. Оставим их кости на съедение гормам.', 38134),
(202619, 'ruRU', '', 'А-а-а! Мерзкая королева! Друстов не сломить!', 38134),
(202618, 'ruRU', '', 'Идем, пилигрим. Избавимся от этого чудовища.', 38134),
(202616, 'ruRU', '', 'Я не могу упустить ее след. Встретимся впереди.', 38134),
(202615, 'ruRU', '', 'Смотри! Магия моих рощ восстает против тебя!', 38134),
(202614, 'ruRU', '', 'Будь осторожнее. Ее поглощает могучая сила. Молюсь, чтобы вы с Шандрисой смогли освободить ее.', 38134),
(202613, 'ruRU', 'Шандриса, другой шанс спасти Тиранду у тебя вряд ли появится.', '', 38134),
(202602, 'ruRU', 'По твоей просьбе мы украсим Пепельный двор интереснейшими вещицами из мира смертных. Возможно, твои гости даже захотят что-то приобрести.', 'По твоей просьбе мы украсим Пепельный двор интереснейшими вещицами из мира смертных. Возможно, твои гости даже захотят что-то приобрести.', 38134),
(202600, 'ruRU', '', 'Ты как раз вовремя, пилигрим Утробы.', 38134),
(202599, 'ruRU', '', 'Враг наседает. Наша оборона долго не продержится.', 38134),
(202588, 'ruRU', 'Я готов начинать, только дай мне знак. Скорее бы!', '', 38134),
(202582, 'ruRU', '', 'Марилет объяснит тебе, как создавать связь между душами.', 38134),
(202581, 'ruRU', 'Эта река – не место для живых. Лучше к ней не приближаться.', '', 38134),
(202580, 'ruRU', 'Они еще сопротивляются! Ничего, скоро мы разберем Избранных и их дружков-кирий на органы.', 'Они еще сопротивляются! Ничего, скоро мы разберем Избранных и их дружков-кирий на органы.', 38134),
(202576, 'ruRU', '', 'Не упусти свой шанс, пилигрим.', 38134),
(202575, 'ruRU', 'Обладать мощью Примаса – великая честь.', '', 38134),
(202567, 'ruRU', '', 'Как ты, однако, вовремя.', 38134),
(202565, 'ruRU', 'Сильвана! Что она с ним делает?', '', 38134),
(202561, 'ruRU', 'Тебе нужно что-то особенное?', 'Тебе нужно что-то особенное?', 38134),
(202559, 'ruRU', 'Это механическое существо выглядит неисправным.', '', 38134),
(202558, 'ruRU', 'Ключ... у... Бретлана...', '', 38134),
(202557, 'ruRU', '', 'Я ими займусь.', 38134),
(202556, 'ruRU', 'Друсты перекрыли нам путь!', '', 38134),
(202555, 'ruRU', '', 'Я знаю пару трюков, которые помогают здесь выживать. Если сражаться со слугами Тюремщика, он нас заметит. Поэтому лучше их избегать.', 38134),
(202554, 'ruRU', '', 'Вы привлекаете слишком много внимания. Это мешает мне заниматься делами. Ничего личного.', 38134),
(202553, 'ruRU', 'С чего бы тебе помогать нам?', '', 38134),
(202552, 'ruRU', '', 'Я – Ве\'нари, скромный брокер и пленница в этом мире страданий. У меня есть к вам предложение. Идем!', 38134),
(202551, 'ruRU', '', 'Вот как. А я все гадала, что ищут Верные Утробе. Ты – та смертная душа, которая испортила Тюремщику праздник?', 38134),
(202549, 'ruRU', 'Признаюсь, их отвлекающий маневр сработал на славу. Теперь победа над ними покажется нам еще слаще.\n\n<Лицо Герна искривляется в грозной ухмылке.>\n\nНачнем?', '', 38134),
(202547, 'ruRU', 'Эти гадкие существа отведают моего клинка, прежде чем все закончится.', '', 38134),
(202536, 'ruRU', 'Кладу в котел катализатор. Сейчас мы увидим мощь моего творения!', '', 38134),
(202535, 'ruRU', 'Я не удивлен, что слава о моей гениальности дошла до Бастиона. Это будет мое самое сильное творение. Оно покажет вам, глупым кириям, что значит хаос и жесткость!', '', 38134),
(202534, 'ruRU', 'Мы защитим Темные Земли от любой внешней или внутренней угрозы!', '', 38134),
(202529, 'ruRU', 'Да, мой принц. Я ранен, но не сломлен.', '', 38134),
(202528, 'ruRU', '', 'Правило второе: не привлекай внимания. Не пытайся быть героем. Входишь, берешь что нужно, уходишь... желательно со всеми конечностями и имуществом.', 38134),
(202527, 'ruRU', 'Друзья! Поднять щиты!', '', 38134),
(202525, 'ruRU', 'Всем приготовиться!', '', 38134),
(202524, 'ruRU', 'Старый друг, ты еще жив?', '', 38134),
(202522, 'ruRU', '', 'Спасибо. Как они умудряются находить столько неприятностей сразу!', 38134),
(202521, 'ruRU', '', 'Заводчикам гормлингов нужна помощь. У тебя найдется время?', 38134),
(202520, 'ruRU', '', 'Но не унывай. Не попадайся Тюремщику на глаза хотя бы день, и он отвлечется на что-нибудь другое. Тогда ты сможешь снова отправиться в Утробу... И собрать стигию... для меня.', 38134),
(202519, 'ruRU', '', 'Зато в моем убежище тебе ничего не грозит. Не то, что снаружи. И помни: если тебя постигнет печальная участь, ты наверняка потеряешь часть стигии.', 38134),
(202518, 'ruRU', '', 'Сперва он натравит на тебя искателей душ. Затем башни... Потом – карателей. А после... лучше тебе не знать. Крайняя осторожность – вот, как ты сможешь выжить.', 38134),
(202517, 'ruRU', '', 'Тюремщик заметил тебя. Теперь тебе несдобровать. Любые твои начинания в Утробе привлекут его внимание. Этого мы себе позволить не можем.', 38134),
(202516, 'ruRU', '', 'Я не могу тебя впустить.', 38134),
(202515, 'ruRU', '', 'Мы охраняем Сердце леса. Без особого поручения войти могут лишь члены Дикой Охоты и двора Зимы.', 38134),
(202514, 'ruRU', '', 'Приветствую тебя!$b$BЛишь участникам Дикой Охоты дозволено входить в Сердце леса.', 38134),
(202513, 'ruRU', '', 'Мы рады видеть тебя в Сердце леса. Проходи.', 38134),
(202512, 'ruRU', '', 'Мы стоим на страже. За королеву и рощу!', 38134),
(202511, 'ruRU', '', 'С возвращением. Как твоя охота?', 38134),
(202509, 'ruRU', '', 'Горевать будем потом. Нам нужно спешить!', 38134),
(202508, 'ruRU', '', 'Леди Лунная Ягода подмигивает.', 38134),
(202507, 'ruRU', '', 'Уверена, что Пепельный двор будет приятным и величественным событием.', 38134),
(202499, 'ruRU', '', 'Чем могу помочь?', 38134),
(202498, 'ruRU', '', 'Баронесса была права насчет тебя, $p. Встретимся на \"Нураккире\".', 38134),
(202497, 'ruRU', 'Друсты укрепили свои позиции. Нужно ударить всеми силами.', '', 38134),
(202493, 'ruRU', '', 'Личи хитры и высокомерны. Это будет непросто.', 38134),
(202492, 'ruRU', '', 'Я хочу, чтобы ингра Дриф страдал, пилигрим.', 38134),
(202491, 'ruRU', 'Если бы не ты, меня бы ждала ужасная участь! Спасибо.', '', 38134),
(202490, 'ruRU', 'Преемница Девии, Лисония, очень опасна. Я пошлю наших носителей душ узнать о ее замыслах.', '', 38134),
(202489, 'ruRU', 'Слава Архонту! Слава Бастиону!', '', 38134),
(202488, 'ruRU', 'Теперь наши претенденты переродятся и помогут нам отбиться от раскольников!', '', 38134),
(202487, 'ruRU', 'Новая мастерская стала мощнее предыдущей!', '', 38134),
(202486, 'ruRU', 'Благодаря пилигриму нам удалось их сохранить.', '', 38134),
(202485, 'ruRU', 'Разрушив ее, Девия попыталась разорвать узы между нами.', '', 38134),
(202484, 'ruRU', 'Вы оба оказали нам огромную услугу. Теперь мы должны защитить восстановленную мастерскую от новых нападений.', '', 38134),
(202472, 'ruRU', '', 'Итак, ты $gготов:готова; вступить в ряды некролордов?', 38134),
(202469, 'ruRU', '', 'Баронесса! Ты вернулась!', 38134),
(202460, 'ruRU', 'Наверное, землерой подкинул в нее новый камень.', '', 38134),
(202457, 'ruRU', 'Эта гора камней грехов пошевелилась?', '', 38134),
(202447, 'ruRU', '', 'Сколько еще нам придется потерять?', 38134),
(202420, 'ruRU', '', 'Все смотрят на меня... Привет! Я ваш друг!', 38134),
(202419, 'ruRU', 'Прошу вашего внимания! Предлагаю тост за гостью из далекого Бастиона, самую лучшую помощницу после Габбинса. Сика!', '', 38134),
(202413, 'ruRU', 'Почтенный член двора благодаря непревзойденной силе и незаурядному рвению, самый продуктивный палач в истории Ревендрета. Поприветствуем... Великий мастер Воул!', '', 38134),
(202412, 'ruRU', 'Позволь дальше мне, Теотар.', '', 38134),
(202411, 'ruRU', 'Прошу вашего внимания! Предлагаю тост за Театр Боли и его знаменитого...', '', 38134),
(202410, 'ruRU', '', 'Мы в Арденвельде вырастаем большие. Заглядывайте к нам как-нибудь.', 38134),
(202409, 'ruRU', 'Прошу внимания! Я предлагаю тост за надежную защитницу Арденвельда и самую огромную медведицу из всех, что я видел, – дромана Алиот!', '', 38134),
(202408, 'ruRU', 'Поднимем бокалы! За Рендла и Дуборыла!', '', 38134),
(202407, 'ruRU', 'Ну я вообще польщен!', '', 38134),
(202406, 'ruRU', 'Прошу внимания! Я предлагаю тост за работящих землероев Ревендрета, особенно за Рендла и Дуборыла!', '', 38134),
(202403, 'ruRU', 'Пусть наши враги обратятся в пепел.', '', 38134),
(202401, 'ruRU', 'Минуту внимания! Хочу предложить тост за Александроса Могрейна, воплощение силы Малдраксуса!', '', 38134),
(202380, 'ruRU', '', 'По крайней мере, я на это надеюсь...', 38134),
(202379, 'ruRU', '', 'Ничто не теряется навечно, принц Ренатал.', 38134),
(202378, 'ruRU', 'Этот был одним из первых... Я думал, он уже навсегда потерян...', '', 38134),
(202377, 'ruRU', 'Принц Ренатал с огромным интересом изучает камень грехов.', '', 38134),
(202375, 'ruRU', 'Похоже, на этом нетопыре вы сможете выбраться из Кровавых катакомб.', 'Похоже, на этом нетопыре вы сможете выбраться из Кровавых катакомб.', 38134),
(202373, 'ruRU', 'Болвар полагает, что знает, куда банши забрала своих пленников.', '', 38134),
(202368, 'ruRU', '', 'Как и все в Арденвельде, мы делаем сети душ для определенной цели. Обычно для хранения или высвобождения анимы – как те сети, что ты видишь здесь. Но эта... эта сеть особенная.$b$bОна должна ловить сны.$b$bК сожалению, я могу лишь починить ее, а не вернуть присущие ей свойства. Тебе придется найти того, кто сможет наполнить ее силой душ.', 38134),
(202367, 'ruRU', '', '<Гвенна осторожно рассматривает фрагменты.>$b$bАх, бедняжка... Что с тобой стряслось?$b$BА, Тирна Скитт... Печальная судьба этой рощи хорошо известна. Ничего, не успеет пикси и крылышком махнуть, как мы тебя починим!$b$BСпасибо тебе. Мы слишком часто избавляемся от вещей, которые еще могут пригодиться. Если принесешь мне десять лоскутов сумрачного шелка, я смогу ее починить.', 38134),
(202366, 'ruRU', 'Эти архивы помогают нам следить за всем, что происходит в Бастионе.', 'Эти архивы помогают нам следить за всем, что происходит в Бастионе.', 38134),
(202365, 'ruRU', 'Эти архивы помогают нам следить за всем, что происходит в Бастионе.\n\nМое усиление дарует тебе способность:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Дозор мудреца]|h|r:когда ваш запас здоровья снижается до критического уровня, перерожденный исцеляет вас и отбрасывает находящихся рядом противников. При этом получаемый вами урон уменьшается, но этот эффект стремительно ослабевает. Срабатывает раз в несколько минут.', 'Эти архивы помогают нам следить за всем, что происходит в Бастионе.\n\nМое усиление дарует тебе способность:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Дозор мудреца]|h|r:когда ваш запас здоровья снижается до критического уровня, перерожденный исцеляет вас и отбрасывает находящихся рядом противников. При этом получаемый вами урон уменьшается, но этот эффект стремительно ослабевает. Срабатывает раз в несколько минут.', 38134),
(202364, 'ruRU', 'Песня этих колоколов будет снова даровать всем покой и просветление.', 'Песня этих колоколов будет снова даровать всем покой и просветление.', 38134),
(202363, 'ruRU', 'Песня этих колоколов будет снова даровать всем покой и просветление.\n\nМое усиление дарует тебе способность:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Колокол Ясности]|h|r: когда вы собираете вместе большое число противников, ваши наносящие урон заклинания и способности могут на некоторое время призвать колокол Ясности. При появлении колокол оглушает находящихся рядом противников и увеличивает наносимый вами урон, пока вы находитесь рядом с ним.', 'Песня этих колоколов будет снова даровать всем покой и просветление.\n\nМое усиление дарует тебе способность:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Колокол Ясности]|h|r: когда вы собираете вместе большое число противников, ваши наносящие урон заклинания и способности могут на некоторое время призвать колокол Ясности. При появлении колокол оглушает находящихся рядом противников и увеличивает наносимый вами урон, пока вы находитесь рядом с ним.', 38134),
(202362, 'ruRU', 'Кирия! Добро пожаловать в Вечные архивы. Новый прилив анимы позволит нам усилить твои способности.', 'Кирия! Добро пожаловать в Вечные архивы. Новый прилив анимы позволит нам усилить твои способности.', 38134),
(202360, 'ruRU', 'Какое чудесное событие!', '', 38134),
(202359, 'ruRU', 'Моя поступь легка, и душа моя парит от счастья!', '', 38134),
(202358, 'ruRU', 'Милый друг, не найдется минутка?', '', 38134),
(202357, 'ruRU', 'Верховный лич! Помоги мне! Используй данный тебе медальон! Вместе мы сокрушим пилигрима Утробы!', '', 38134),
(202356, 'ruRU', 'Скорее, пилигрим. Расчисти путь к следующему колодцу. Я чувствую поблизости силу моего медальона.', '', 38134),
(202355, 'ruRU', 'Да начнется ваше искупление!', '', 38134),
(202354, 'ruRU', 'Вы поплатитесь за свою дерзость!', '', 38134),
(202353, 'ruRU', '', 'Что до Бвонсамди – его судьба меня не заботит. Можешь помочь ему, если пожелаешь. А меня ждет Арденвельд.', 38134),
(202352, 'ruRU', '', 'Мой брат всегда следовал традициям.', 38134),
(202349, 'ruRU', 'Пилигрим Утробы присоединится к нам, принесет клятву и вступит в ряды восстания.', '', 38134),
(202347, 'ruRU', 'Займи свое место рядом со мной.', '', 38134),
(202331, 'ruRU', 'Да, мой друг. Вместе с этими смертными мы добьемся победы.', '', 38134),
(202330, 'ruRU', 'Подходящая участь для этого гордеца. Он сможет лишь смотреть, как мы спасаем Ревендрет и одолеваем Тюремщика.', '', 38134),
(202329, 'ruRU', 'Много веков Денатрий использовал Свет против нашего народа в Пепельном Пределе. Теперь Свет станет его оковами.', '', 38134),
(202328, 'ruRU', '', 'Я рада представлять мою королеву при Пепельном дворе.', 38134),
(202327, 'ruRU', '', '$n, скоро увидимся снова на Пепельном дворе.', 38134),
(202326, 'ruRU', 'Мы отнесем клинок в Грехопад. Спасенная смертными наару Зе\'рали согласилась нам помочь.', '', 38134),
(202325, 'ruRU', 'Ты прав. Но у меня есть идея.', '', 38134),
(202324, 'ruRU', 'Если сущность Денатрия действительно укрылась в этом клинке, заклинаниями ее не сдержать.', '', 38134),
(202323, 'ruRU', 'Довольно! Нам надоело тебя слушать!', '', 38134),
(202322, 'ruRU', '', 'Вероломный принц! Я проткну тебя! Порублю! Рассеку! Покромсаю! Порежу на лоскуты...', 38134),
(202321, 'ruRU', 'Нет. Денатрий ответит за свое предательство.', '', 38134),
(202320, 'ruRU', '', 'Зоваал восстановит тебя, Владыка! Мы отомстим!', 38134),
(202319, 'ruRU', 'Клинок впитал его сущность!', '', 38134),
(202317, 'ruRU', '', 'Не волнуйся, Владыка! Я сохраню тебя!', 38134),
(202314, 'ruRU', 'Умерь свой пыл, Реморния. Спешить в таком деле... некрасиво.', '', 38134),
(202309, 'ruRU', '', 'Наш мир хранит первозданную чистоту только благодаря усердному труду. Спасибо тебе.', 38134),
(202308, 'ruRU', '', 'Какая разруха... К счастью, у нас есть множество распорядителей – они возьмутся за что угодно. Прошу, наведи здесь порядок.', 38134),
(202307, 'ruRU', 'Смотри, Реморния! Эти наглые смертные преодолели все наши препятствия. Похоже, у нас не остается выбора: придется сдаться.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202306, 'ruRU', 'Мы надеемся на вас, смертные! Идите вперед и сразите того, кто предал нас всех!', '', 38134),
(202305, 'ruRU', 'Слуги Денатрия! Мы должны их отбросить, мой принц!', '', 38134),
(202302, 'ruRU', 'Друсты наверняка нападут снова. Прошу, поторопись!', '', 38134),
(202299, 'ruRU', '', 'Это было... примерно как я и ожидала.', 38134),
(202298, 'ruRU', '', 'Я пыталась всех развлекать, но, боюсь, этого было недостаточно.', 38134),
(202297, 'ruRU', 'Меня ждут дела. Столько времени упущено...', '', 38134),
(202296, 'ruRU', '', 'Боюсь, тебе еще нужно поучиться устраивать приемы, дорогой принц.', 38134),
(202295, 'ruRU', 'Я потратил достаточно времени вдали от поля боя.', '', 38134),
(202294, 'ruRU', '', 'На приемах я не в своей тарелке. Мне нужно вернуться к охоте.', 38134),
(202293, 'ruRU', 'Теперь я понимаю, что Архонт отправила меня сюда, чтобы научить смирению.', '', 38134),
(202292, 'ruRU', 'Да... Наперегонки до экипажа?', '', 38134),
(202291, 'ruRU', 'Я представлял себе высшее общество по-другому.', '', 38134),
(202290, 'ruRU', 'Вихлюн, допивай скорее! Мы уже одной ногой за дверью!', '', 38134),
(202289, 'ruRU', 'Я польщен этим приглашением, принц Ренатал... Возможно, в следующий раз стоит предоставить эту честь другим.', '', 38134),
(202288, 'ruRU', '', 'Я надеялась, что вы приложите больше усилий, принц Ренатал. Вообще-то я вас поддерживаю.', 38134),
(202287, 'ruRU', 'На заточку ножей и то веселее смотреть.', '', 38134),
(202286, 'ruRU', 'Это прошло не так уж плохо, Клейя. Тот землерой был очень любезен с нами. Габбинс.', '', 38134),
(202285, 'ruRU', '', 'Хвала Архонту, все кончилось.', 38134),
(202284, 'ruRU', 'Ну, хотя бы Кевину тут понравилось. А эти землерои тебе все равно были не нужны.', '', 38134),
(202283, 'ruRU', '', 'Лучше бы я осталась в Бастионе. Прибралась бы.', 38134),
(202282, 'ruRU', 'Закусок маловато!', '', 38134),
(202281, 'ruRU', 'Это, конечно, два разных эксперимента... но оба дали отличный результат!', '', 38134),
(202280, 'ruRU', '', '<Леди Лунная Ягода подмигивает вам.>', 38134),
(202279, 'ruRU', 'Пришло время свергнуть бывшего Владыку. То, что нам предстоит сделать, пойдет на благо всем Темным Землям!', '', 38134),
(202278, 'ruRU', 'Не одни камнерожденные были обмануты, мой друг. Посмею предположить, что даже Арбитр стала пешкой в этой игре.', '', 38134),
(202277, 'ruRU', 'Мои собратья восстают из камня на защиту Ревендрета. Больно видеть, как нашей верностью пользуется тот, кому мы раньше служили.', '', 38134),
(202275, 'ruRU', 'Мне понравилось, когда я пошутил, и друг рассмеялся.', '', 38134),
(202274, 'ruRU', '', 'Было весело! Ничего, что далеко! Всем друзьям расскажу!', 38134),
(202273, 'ruRU', 'Эксперимент с дружбой и слизью вышел удачным!', '', 38134),
(202272, 'ruRU', 'Когда мы едины, никто не может нам противостоять.', '', 38134),
(202271, 'ruRU', '', 'Меня восхищает твоя готовность служить Темным Землям, в том числе за пределами Ревендрета.', 38134),
(202270, 'ruRU', 'Ты мне нравишься, Ренатал. Принц, который не боится запачкать руки... в тебе есть потенциал.', '', 38134),
(202269, 'ruRU', '', 'То, что я сегодня увидела, впечатляет. Я передам твои слова Королеве Зимы и расскажу ей о твоих деяниях.', 38134),
(202268, 'ruRU', 'Естественно, наш подарок лучший. Мы его сами сделали!', '', 38134),
(202267, 'ruRU', 'Надеюсь, мой подарок лучше всех! Тогда ты ведь снова меня пригласишь?', '', 38134),
(202266, 'ruRU', '', 'Я бы хотела как-нибудь поохотиться вместе с тобой.', 38134),
(202265, 'ruRU', 'Благодарю тебя за прекрасный отдых. Я вернусь в бой с новыми силами!', '', 38134),
(202263, 'ruRU', 'И мы объединились, чтобы вместе изгнать общего врага: пожирателей!', '', 38134),
(202262, 'ruRU', 'Будьте осторожны. Нас еще ждут самые верные слуги Денатрия.', '', 38134),
(202261, 'ruRU', 'Мы должны двигаться дальше, смертные. К счастью, это зеркало еще можно починить.', '', 38134),
(202260, 'ruRU', 'Вот именно. Останови их.', '', 38134),
(202259, 'ruRU', 'Это верно. Ты разгромил мне ползамка. И раз ты так любишь разрушения, мой неповоротливый друг тебе поможет.', '', 38134),
(202258, 'ruRU', 'Зато эти уроки помогли мне избавиться от твоих слуг, \"Владыка\".', '', 38134),
(202257, 'ruRU', 'После всех моих уроков ты по-прежнему продолжаешь забываться, Ренатал. Ай-ай-ай... Я разочарован.', '', 38134),
(202256, 'ruRU', 'Смертные, хоть вам и удалось подорвать планы Денатрия, не стоит его недооценивать. Он обладает... внушительной силой.', '', 38134),
(202255, 'ruRU', 'Этот проход тоже ведет обратно в вестибюль.', '', 38134),
(202253, 'ruRU', 'Смотрите, как разбегаются! Больно видеть, как низко пали мои собратья.', '', 38134),
(202252, 'ruRU', 'Идем, смертные. Пора избавить Ревендрет от чванливой кучки вельмож, что стоят между нами и Денатрием.', '', 38134),
(202251, 'ruRU', 'Позаботься об этой душе, Обвинительница.', '', 38134),
(202250, 'ruRU', '', 'Тебе еще есть чему учиться.', 38134),
(202249, 'ruRU', 'Я не собираюсь снова быть чьей-то пешкой. Твоя \"помощь\" поможет мне свершить возмездие?', '', 38134),
(202248, 'ruRU', '', 'Кель\'тас Солнечный Скиталец! Ты свободен от Денатрия, но не от своих грехов. Идем со мной, и я помогу тебе найти искупление.', 38134),
(202244, 'ruRU', 'Алчность... Пожалуй, самый пагубный из грехов.', '', 38134),
(202243, 'ruRU', 'Невероятно! Пока наш народ за стенами замка голодает, эти льстецы предаются утехам.', '', 38134),
(202242, 'ruRU', 'Брокеры – алчные негодяи. Такое мощное оружие не должно попасть к ним!', '', 38134),
(202241, 'ruRU', 'Залы здесь украшены неплохим оружием. Давайте избавим от него этих аристократов.', '', 38134),
(202240, 'ruRU', '', 'Пока они пируют, Тюремщик собирает армию, чтобы уничтожить Темные Земли. Эти глупцы заслуживают своей участи за верность Денатрию!', 38134),
(202239, 'ruRU', 'Это зеркало поможет вам перемещаться по замку, смертные.', '', 38134),
(202238, 'ruRU', 'Камнерожденные могли использовать такие ходы, чтобы выполнять приказы Владыки. Знали бы вы, что мне приходилось видеть...', '', 38134),
(202237, 'ruRU', 'Сюда, смертные. Я знаю проход, который выведет нас обратно в вестибюль.', '', 38134),
(202236, 'ruRU', 'Нравится шляпа?\n\nНовый друг Калош меня принарядил.\n\nНе волнуйся. Ты все равно мой самый лучший друг!', '', 38134),
(202235, 'ruRU', '', 'Горевать будем потом. Пришло время свергнуть Денатрия.', 38134),
(202234, 'ruRU', '', 'Анима больше не уходит, но, боюсь, худшее уже произошло. Нужно собрать то, что осталось, и позаботиться о будущем Ревендрета.', 38134),
(202233, 'ruRU', '', 'Друзья помогают Сике. Сика привела друзей.\n\nДрузья убираются. Пилигрим Утробы – друг.\n\nПилигрим Утробы... тоже убирается?\n\n<Сика отвлекается на кого-то рядом.>\n\nПовеселимся! Отдохнем на приеме!', 38134),
(202232, 'ruRU', 'Браво, смертные! Запасы анимы ждут нас впереди!', '', 38134),
(202231, 'ruRU', '', 'Уничтожьте эту тварь, пока она не стала сильнее!', 38134),
(202230, 'ruRU', '', 'Это чудище жаждет утолить свой ненасытный голод запасами анимы.', 38134),
(202229, 'ruRU', 'Пожиратель? Здесь?! Да еще и какой огромный!', '', 38134),
(202228, 'ruRU', 'Какой чудесный день для приема!\n\nЯ, конечно, взял с собой Кевина. И еще два лакомства, которые приготовил по такому случаю!\n\nО, не волнуйся. Это просто слизь. Все слизь.\n\n<Марилет оглядывается по сторонам.>\n\nДа, кстати... Мне говорили, что землерои созданы из жижи.\n\nЖижа... жижа... \n\nЭто ведь тоже своего рода слизь, верно?\n\nНадо разобраться!', '', 38134),
(202227, 'ruRU', 'Даже если этот прием не оправдает ожиданий твоего принца, он все равно уже удался.\n\nПосмотри вокруг, пилигрим.\n\nОбитатели разных царств собрались вместе и стараются найти общий язык.\n\nТак рождается надежда.\n\nПусть эта надежда приведет к единству, а единство – к миру.', '', 38134),
(202226, 'ruRU', '', 'Надеюсь, ты не против, что мы с Пелагием пригласили еще двух претендентов?\n\nОни очень многообещающие, и Архонт тоже думает, что это путешествие и новые знакомства пойдут им на пользу.\n\nМы можем многому научить друг друга.', 38134),
(202225, 'ruRU', 'Я рад возможности продемонстрировать мощь Малдраксуса твоему принцу.\n\n<Великий мастер Воул играет мускулами перед ближайшим гостем.>\n\nКстати, мы тут с парнями поспорили, не удастся ли нам устроить до конца приема бойцовский клуб.\n\nЭто, конечно, не Театр Боли, но мне нравится, какая здесь... \"непринужденная\" атмосфера.\n\nДумаю, нам удастся это провернуть. Скажи, если захочешь поучаствовать.', '', 38134),
(202224, 'ruRU', '', 'Скажи мне, пилигрим... Ты считаешь Ревендрет своим домом?\n\nИ принца Ренатала своим принцем?\n\n<Дроман смотрит сквозь вас.>\n\nМоего прежнего дома больше нет. Он уничтожен.\n\nНо я чувствую себя как дома в Арденвельде, служа Королеве.\n\n<Дроман глубоко вздыхает.>\n\nРазмер дара, о котором просит нас твой принц, определить будет непросто.', 38134),
(202223, 'ruRU', '', 'Я... больше... не могу...', 38134),
(202222, 'ruRU', 'А что? Приятно, когда тебе землерои прислуживают.\n\n<Рендл улыбается и гордо выпячивает грудь.>\n\nО да, старина Рендл таки делает карьеру!', '', 38134),
(202219, 'ruRU', 'Сколько раз я сюда приезжал, пижонов всяких привозил на вечеринку.\n\nА теперь глянь на меня! Я сам пижон! Ха!', '', 38134),
(202218, 'ruRU', '', 'Скажи, пилигрим, какие монстры бродят за стенами этого Двора?\n\nЯ вижу запустение повсюду вокруг этого плато.\n\nРаненые земли порождают чудовищ. Я видела это в своем царстве.\n\nНадо быть готовыми.', 38134),
(202217, 'ruRU', '', 'Дорога полна опасностей. Но мы должны добраться до запасов анимы!', 38134),
(202216, 'ruRU', 'Вот проход вниз, смертные.', '', 38134),
(202215, 'ruRU', '', 'Он заслужил эту участь.', 38134),
(202214, 'ruRU', 'Покончите с жестоким псарем, и тогда мы сможем спуститься на нижний ярус замка.', '', 38134),
(202213, 'ruRU', 'На псарне Владыка держит охотничью свору. Эти псы вечно голодны.', '', 38134),
(202212, 'ruRU', '', 'Чтобы одолеть Денатрия, мы должны сперва лишить его самых ценных сокровищ. И я знаю путь, который приведет нас к ним.', 38134),
(202211, 'ruRU', 'За псарней есть проход, который ведет в подвалы замка. Оттуда мы сможем добраться до запасов анимы и прервать ее отток в Утробу.', '', 38134),
(202210, 'ruRU', 'Вестибюль очищен! Но Денатрий, судя по всему, сбежал в свои покои.', '', 38134),
(202209, 'ruRU', 'Я не привык к светским беседам и напыщенной роскоши.\n\n<Выражение лица Александроса смягчается, и он ослабляет хватку на своем оружии.>\n\nНо я благодарен за возможность развеяться. Даже самым сильным воинам необходим отдых между сражениями.\n\nЯ воспользуюсь этой передышкой, чтобы собраться с силами и подготовиться к грядущей битве.\n\nДа исполнится воля Крексуса.', '', 38134),
(202208, 'ruRU', 'А теперь начнем ваше искупление – с подходящего испытания.', '', 38134),
(202207, 'ruRU', 'Похоже, ты не усвоил урок. Возможно, я был слишком милосерден. В конце концов, мне ли не знать о милосердии.', '', 38134),
(202206, 'ruRU', 'Я уже слышал от тебя подобные угрозы, Ренатал. Мы оба помним, чем все закончилось.', '', 38134),
(202205, 'ruRU', 'Довольно самолюбования, Денатрий! Иди сюда и сражайся!', '', 38134),
(202204, 'ruRU', 'Заблудший принц... Развенчанная Обвинительница... Мятежный бывший генерал... А с ними – кучка смертных оборванцев, жаждущих сгинуть навсегда.', '', 38134),
(202203, 'ruRU', 'Приветствую! Я с таким нетерпением ждал вашего прибытия! Итак, кто у нас тут...', '', 38134),
(202202, 'ruRU', 'Интересно... очень интересно...\n\nЛюбопытный у вас двор.\n\nНовые идеи... новые знакомства...\n\nСтолькому можно научиться...', '', 38134),
(202199, 'ruRU', 'Будьте начеку, смертные. Преимущество на стороне Денатрия. Он попытается взять нас хитростью.', '', 38134),
(202198, 'ruRU', '', 'Он знает, что время не на нашей стороне. Сила Тюремщика растет с каждой секундой. Нужно перекрыть поток анимы, текущий в Утробу.', 38134),
(202196, 'ruRU', 'Хм...$b$b<Теперь он явно настороже. Он умудряется вложить в этот простой звук целую гамму чувств.>', '', 38134),
(202195, 'ruRU', 'Пробиться через ворота оказалось слишком просто. Наш бывший Владыка явно заманивает нас вглубь замка.', '', 38134),
(202194, 'ruRU', 'Хм...$b$b<Его голос звучит почти задумчиво.>', '', 38134),
(202193, 'ruRU', 'Хм-м.$b$b<Похоже, он изо всех сил старается игнорировать любые звуки. Но у него плохо получается.>', '', 38134),
(202192, 'ruRU', 'Хм...$b$b<На этот раз, судя по его голосу, он точно на вас сердится.>', '', 38134),
(202190, 'ruRU', 'Медитация помогает мне установить связь с лесом. Помогает найти то, чему в лесу не место.$b$B<Воркай приоткрывает один глаз и смотрит на вас.>$b$bОт медитации ничто не должно отвлекать. Поговори с Нарциссом.', '', 38134),
(202189, 'ruRU', 'Хм...$b$B<Вам не ясно, погрузился ли воркай глубже в медитацию или просто сердит на вас.>', '', 38134),
(202188, 'ruRU', '$p! Хвала Королеве, ты здесь!', '', 38134),
(202185, 'ruRU', 'Горак Зар, все кончено!', '', 38134),
(202184, 'ruRU', '', 'Нет! За свою дерзость ты заплатишь кровью!', 38134),
(202182, 'ruRU', 'Мне нужно готовиться к путешествию. Не терпится поговорить с твоими союзниками.', '', 38134),
(202181, 'ruRU', '', 'Теперь ты служишь дому Глаз. Жди приказаний.', 38134),
(202179, 'ruRU', 'В башмаках глашатая бежишь быстрее! \n\nЗамедлил противник? Не можешь двигаться?\n\nБашмаки помогут! Можно сбросить чары мгновенно!', '', 38134),
(202178, 'ruRU', '', 'Стойте! Я сдаюсь... Что вам нужно?', 38134),
(202177, 'ruRU', 'Не беспокойся, я присмотрю за твоим снаряжением! Все будет готово, когда захочешь снова испытать себя на Пути.\n\nХочешь что-то узнать про доступное снаряжение? Какое?', '', 38134),
(202175, 'ruRU', '', 'Добавим блеска!', 38134),
(202174, 'ruRU', '', 'Шпионы?!.. Вы об этом пожалеете!', 38134),
(202173, 'ruRU', '', 'Где я?! Кто посмел призвать меня?!', 38134),
(202172, 'ruRU', 'Чтоб тебя, Нарцисс...', '', 38134),
(202171, 'ruRU', '', 'Так им и надо! Кто сказал, что магией все не решишь?', 38134),
(202170, 'ruRU', '', 'Как насчет того, чтобы еще раз учинить блестящий кавардак?', 38134),
(202169, 'ruRU', 'Займи свое место, когда приготовишься.', '', 38134),
(202168, 'ruRU', 'Найди нас в нижних залах – и мы начнем.', '', 38134),
(202167, 'ruRU', '', 'В записках не новая работа... В записках помощь?', 38134),
(202166, 'ruRU', 'Какой бы перерожденный мне ни встретился, все говорят про Путь. Испытания, которые проходят все души, чтобы обрести покой и найти свое место в вечности после несовершенной смертной жизни.', '', 38134),
(202165, 'ruRU', 'Я пытался забыть. Пытался простить. И все же... Он не покидает мои мысли. Он повсюду следует за мной.', '', 38134),
(202164, 'ruRU', 'Мне чужд этот гнев. С ним я себя не узнаю. Я должен отбросить его. Это мой путь. Идеал сказала, что я должен сделать это, и я это сделаю.', '', 38134),
(202163, 'ruRU', 'Свет отверг твою душу, Артас. Она будет вечно гнить в Утробе!', '', 38134),
(202162, 'ruRU', '', 'Сегодня я снова спросила, когда смогу пройти завершающий ритуал. И снова я не получила ответа. Здесь меня научили терпению, но сохранять его становится нелегко.', 38134),
(202161, 'ruRU', '', 'Другие претенденты обеспокоены. Мне и самой все это не нравится, но я не должна подавать виду. Они равняются на меня! И Пелагий тоже...', 38134),
(202160, 'ruRU', '', 'Когда закончится эта засуха? Что ее вызвало? Знают ли что-то перерожденные, или они, как и мы, теряются в догадках? Как бы мне хотелось знать...', 38134),
(202159, 'ruRU', '', 'Нельзя забывать, чему меня учили. Я должна сосредоточиться... и найти покой в тех вещах, которые могу контролировать.', 38134),
(202158, 'ruRU', 'Мне сказали, что если я напишу о своих проблемах, то смогу отстраниться и трезво на них взглянуть. Что ж, попробуем...', '', 38134),
(202157, 'ruRU', 'Сегодня я снова попытался пройти ритуал очищения. Моему медиуму удалось убедить меня, что я ко всему готов. Сперва я чувствовал себя уверенно! Но потом, как и всегда, поддался сомнениям.', '', 38134),
(202156, 'ruRU', 'Почему у меня ничего не получается? Я все понимаю, когда это объясняет Клейя, но стоит мне приступить к самому испытанию, как я тут же забываю все, чему научился.', '', 38134),
(202155, 'ruRU', 'Иногда мне кажется, что я никогда не стану перерожденным. Меня отправят обратно в Орибос, Арбитр всмотрится в мою душу и...', '', 38134),
(202153, 'ruRU', '', 'Она уже могла стать добычей пожирателя. Мне бы этого не хотелось.', 38134),
(202152, 'ruRU', '', 'Она скоро вернется. Времени мало. Я покажу тебе правду.', 38134),
(202151, 'ruRU', '', 'Пилигрим... ты – марионетка Денатрия.', 38134),
(202150, 'ruRU', '', 'Вперед! Я буду защищать мост.', 38134),
(202149, 'ruRU', 'Платой за темную силу Элуны была моя жизнь. \n\nТакие вещи не предназначены для смертных. Рано или поздно эта сила должна была меня убить. Ради моего мира, моего народа и моей семьи я был готов принести эту жертву.\n\nНо Кадарин не мог этого допустить. О том, что произошло дальше, я жалею до сих пор... В порыве разделить эту ношу, мы погибли оба.\n\n<Тирнакс вздыхает.>\n\nНо не стоит ворошить прошлое... Наш мир был спасен, а мы все еще вместе.', '', 38134),
(202146, 'ruRU', 'Нам многое нужно успеть к прибытию Королевы. Не будем терять времени!', '', 38134),
(202144, 'ruRU', '', 'Я покажу тебе правду, пилигрим Утробы.', 38134),
(202143, 'ruRU', '', 'Твоя верность достойна похвалы. Мы рады, что ты на нашей стороне.', 38134),
(202142, 'ruRU', 'В этих зарослях глаза могут тебя обмануть. То, что кажется досягаемым, спрятано за пеленой тумана. \n\nСколько ни пытайся, результат будет тем же. Те, кто заходит сюда в одиночку, теряют себя в потоках времени. \n\nНадежда кроется в осмотрительности. Заросли скрывают множество секретов, подвластных лишь самым наблюдательным. \n\nПозволь Свету направлять тебя. Путешествие через пелену уже началось.', '', 38134),
(202141, 'ruRU', '', 'Я принимаю твою присягу. Теперь можешь встать.', 38134),
(202140, 'ruRU', '', 'Сика такая милая и работящая. Лучше мне держать кузню в чистоте и не волновать ее понапрасну.', 38134),
(202139, 'ruRU', 'Благодарю тебя, пилигрим.\n\nЯ сдерживал этих эфирных змеев, чтобы претенденты смогли убежать... но змеев оказалось слишком много.\n\n<Адонас осторожно ощупывает бок.>\n\nТы очень вовремя.', '', 38134),
(202138, 'ruRU', '', 'Из Кровавых катакомб не сбежать! Тем более вам!', 38134),
(202137, 'ruRU', '', 'Вы лишь жалкие прихвостни Дрейвена!', 38134),
(202136, 'ruRU', '', 'Ничтожные смертные!', 38134),
(202135, 'ruRU', '', 'Встретимся у столпа. Нам многое нужно сделать.', 38134),
(202134, 'ruRU', 'Ой... я сказал Сике, что нужно прибраться в библиотеке, и она ответила, что будет рада... Я и не знал, что она так занята. Я сам все сделаю!', 'Ой... я сказала Сике, что нужно прибраться в библиотеке, и она ответила, что будет рада... Я и не знала, что она так занята. Я сама все сделаю!', 38134),
(202133, 'ruRU', 'Это все, что ты можешь?', '', 38134),
(202132, 'ruRU', 'Можешь на меня положиться.', '', 38134),
(202131, 'ruRU', 'Хм... это все?', '', 38134),
(202130, 'ruRU', 'Помощь нужна?', '', 38134),
(202129, 'ruRU', 'Понадобится лучший боец – зови.', '', 38134),
(202128, 'ruRU', 'Кого сегодня убьем?', '', 38134),
(202127, 'ruRU', 'Я встречал и посильнее.', '', 38134),
(202123, 'ruRU', '', 'Это из-за моих рук, да?', 38134),
(202122, 'ruRU', '', 'Нужна помощь?', 38134),
(202121, 'ruRU', '', 'Обнимашки!', 38134),
(202120, 'ruRU', '', 'Не забывай меня.', 38134),
(202117, 'ruRU', 'Ты тоже помогаешь Гефесию?', '', 38134),
(202116, 'ruRU', '', 'Друзья должны держаться вместе.', 38134),
(202115, 'ruRU', '', 'Тебя подбросить?', 38134),
(202113, 'ruRU', 'Пусть песня колокола снова вдохновит кирий.', '', 38134),
(202109, 'ruRU', 'Привет, дружище.', '', 38134),
(202107, 'ruRU', 'Надо было сразиться с ужасным злом, погубившим многих.\n\nМы собрали группу защитников, поклявшихся уничтожить темного бога, попавшего в наш мир. Для этого нам нужна была сила, которую просила у Матери-Луны та, что была ее Гласом до меня.\n\n<Тирнакс грустно усмехается.>\n\nМы с Кадарином много из-за этого спорили. Это сила уже убила его мать. Он не хотел потерять и своего друга. Но сила Элуны была лучшим шансом справиться с теми чудовищами. Я исполнил ритуал... и выжил. Он был со мной рядом. Наши глаза стали темными, как оборотная сторона луны, и мы приняли бой.\n\nНо я не ожидал, чего это будет нам стоить.', '', 38134),
(202104, 'ruRU', 'Итоги. \n\nТирнаксу удалось стать Ночным Воителем. Я сражался до конца рядом с ним и другими защитниками Физанди. Мы победили, но страшной ценой. \n\nАрмии безумия перебили многих, пока нас не было, а когда главного зла не стало, скрылись во тьме. Некоторые наши братья поддались их шепоту. Они пропали или сошли с ума. Мы потеряли друзей... и родных. \n\nКровь их божества окропила наш мир. Понадобятся многие столетия, чтобы очистить его от этой заразы.\n\nЕсли это вообще возможно.', '', 38134),
(202103, 'ruRU', 'Чудовища.\n\nСущества с множеством глаз, конечностей и кровожадных ртов. Они заполонили наш мир, когда наши прадеды были еще детьми. Так началась Бесконечная война. Целыми поколениями мы сдерживали Иных и их безумие.\n\nУзнать, что именно обитает в центре кратера, удалось моей матери. Чтобы сразить это инородное существо, она обратилась к темной стороне Элуны. Это ее погубило.', '', 38134),
(202100, 'ruRU', 'Да. За несколько поколений до моего рождения на планету из космоса рухнул темный бог. \n\nЭта катастрофа нарушила баланс природы и уничтожила целое королевство. На месте его падения осталась пустыня, откуда никто не возвращался. \n\nМы, друиды, следили за ее границами, но не догадывались, что будет дальше.', '', 38134),
(202098, 'ruRU', 'Мой мир – Физанди. Это был прекрасный мир, полный обширных лесов, высоких гор и безмятежных долин. \n\nИ за эти прекрасные дикие земли мы сражались с темным богом.', '', 38134),
(202097, 'ruRU', 'Да! Идем и получим нашу награду! Как насчет песни в честь победы? Охотнику заплатите зеленым семечком...', '', 38134),
(202096, 'ruRU', 'Эй! Я тоже помогал! Своим ужасным пением!', '', 38134),
(202095, 'ruRU', 'Ха! Я действительно... Эй, что?..', '', 38134),
(202094, 'ruRU', 'Спасибо за помощь. Мерзкая тварь... но ты хорошо сражаешься.', '', 38134),
(202093, 'ruRU', 'Уф...', '', 38134),
(202092, 'ruRU', 'Хм... Самое главное, что мы справились. Идем дальше.', '', 38134),
(202091, 'ruRU', 'Прекрасно. Боггарты не терпят неприятных звуков.', '', 38134),
(202087, 'ruRU', 'Нет, постой!', '', 38134),
(202086, 'ruRU', 'О, НЕТ!', '', 38134),
(202085, 'ruRU', 'Почему? Это существо совсем не страшное. Точно не то, которое мы ищем. Я с ним в два счета разберусь. Смотри!', '', 38134),
(202084, 'ruRU', 'Чего боишься, ты, грубый тю... а? Я... Гвинкейрв? Кажется, я что-то нашел!', '', 38134),
(202083, 'ruRU', 'Жуткое чудище где искать? Не справишься сам – поймешь, кого звать!', '', 38134),
(202082, 'ruRU', 'Положитесь на Нарцисса, на великого поэта. Этот увалень большой всех оставит без ответа!', '', 38134),
(202081, 'ruRU', 'Нарцисс, Нарцисс, он всех победит! А вот спутник его все время спит!', '', 38134),
(202080, 'ruRU', 'О, я скажу тебе, что собираюсь сделать! Я сам разыщу эту тварь, если придется. И заодно напишу об этом песню! Как насчет...', '', 38134),
(202079, 'ruRU', 'Кто? Он? Не обращай внимания: он злится, потому что еще не поймал свою добычу. Вообще-то он отличный боец, но приемчики у него... странные. Учти, я тебе этого не говорил.$b$bПравда, он ведь НЕ СЛЫШИТ МЕНЯ, КОГДА ДРЫХНЕТ!$B$B<Кажется, Гвинкейрв никак не реагирует.>', '', 38134),
(202078, 'ruRU', 'Ну что, наконец решил проснуться и помочь?$b$BА, ты не он. Что ж, может, и ты сгодишься. Тебе не попадалось поблизости жуткое растение?', '', 38134),
(202077, 'ruRU', 'Я! Медитирую! По крайней мере, пытаюсь.$b$bМожет, поговоришь с Нарциссом? Он любит языком чесать.', '', 38134),
(202076, 'ruRU', 'В Бастионе есть колокол, в который уже давно никто не звонил. \n\nКогда-то он участвовал в важном ритуале кирий. Но с тех пор большая часть кристальных молотов, которыми можно звонить в этот колокол, раскололась.\n\nЕсли кто-нибудь соберет их осколки, я выкую новый молот.', '', 38134),
(202075, 'ruRU', 'Как хорошо, что ты здесь!', 'Как хорошо, что ты здесь!', 38134),
(202074, 'ruRU', 'Как хорошо, что ты здесь! Я взял немного лишней анимы и нашел прекрасное применение всему этому пеплу.\n\nМое усиление дарует тебе способность.\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Урна с прахом]|h|r: после убийства нескольких противников вы призываете пепельного ползуна, который помогает вам в бою. За каждого противника, которого вы побеждаете совместно с пепельным ползуном, наносимый им урон увеличивается, а его здоровье восполняется. Иным способом ползуна исцелить нельзя.', 'Как хорошо, что ты здесь! Я взяла немного лишней анимы и нашла прекрасное применение всему этому пеплу.\n\nМое усиление дарует тебе способность.\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Урна с прахом]|h|r: после убийства нескольких противников вы призываете пепельного ползуна, который помогает вам в бою. За каждого противника, которого вы побеждаете совместно с пепельным ползуном, наносимый им урон увеличивается, а его здоровье восполняется. Иным способом ползуна исцелить нельзя.', 38134),
(202073, 'ruRU', 'Никому не говори.', 'Никому не говори.', 38134),
(202072, 'ruRU', 'Если никому не скажешь, я дам тебе одну вещь, которая поможет избавиться от этих мерзавцев. Я забрал несколько их проклятых зеркал, и теперь они могут тебе пригодиться.\n\nМое усиление дарует тебе следующую способность:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Подвижные зеркала]|h|r: ваши наносящие урон заклинания и способности могут призвать осколки зеркала, которые фокусируют свет в плотный поток перед вами. Этот поток наносит противникам урон от огня мгновенно, а также дополнительный периодический урон от огня.', 'Если никому не скажешь, я дам тебе одну вещь, которая поможет избавиться от этих мерзавцев. Я забрала несколько их проклятых зеркал, и теперь они могут тебе пригодиться.\n\nМое усиление дарует тебе следующую способность:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Подвижные зеркала]|h|r: ваши наносящие урон заклинания и способности могут призвать осколки зеркала, которые фокусируют свет в плотный поток перед вами. Этот поток наносит противникам урон от огня мгновенно, а также дополнительный периодический урон от огня.', 38134),
(202071, 'ruRU', 'Вентир! Мы спрятались среди Покровников, надеясь, что ты нам поможешь. Мне удалось выкрасть для тебя немного анимы.', 'Вентир! Мы спрятались среди Покровников, надеясь, что ты нам поможешь. Мне удалось выкрасть для тебя немного анимы.', 38134),
(202070, 'ruRU', 'Вентир! Мы спрятались среди Покровников, надеясь, что ты нам поможешь. Мне удалось выкрасть для тебя немного анимы.\n\nМое усиление дарует тебе способность.\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Жажда анимы]:|h|r при низком запасе здоровья ваши наносящие урон заклинания и способности с высокой вероятностью оглушают противника на короткое время, исцеляя вас и нанося противнику урон до истечения времени действия.\n\nБудьте начеку: эффект срабатывает довольно редко.', 'Вентир! Мы спрятались среди Покровников, надеясь, что ты нам поможешь. Мне удалось выкрасть для тебя немного анимы.\n\nМое усиление дарует тебе способность.\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Жажда анимы]:|h|r при низком запасе здоровья ваши наносящие урон заклинания и способности с высокой вероятностью оглушают противника на короткое время, исцеляя вас и нанося противнику урон до истечения времени действия.\n\nБудьте начеку: эффект срабатывает довольно редко.', 38134),
(202066, 'ruRU', 'Я не могу больше оставаться в Утробе. Верни духов в Арденвельд...', '', 38134),
(202056, 'ruRU', 'Резан, твой дар не пропадет. Твое имя запомнят... навеки.', '', 38134),
(202054, 'ruRU', 'Им нужен... именно ты...', '', 38134),
(202040, 'ruRU', 'Пилигрим, разрушь барьер!', '', 38134),
(202039, 'ruRU', 'Мы отомстим за порабощенных собратьев! За Арденвельд!', '', 38134),
(202038, 'ruRU', 'Что это за магия?! Убей ее приспешников, а я спасу семена!', '', 38134),
(202037, 'ruRU', 'Резан! Лоа королей! Нужно разрушить барьер!', '', 38134),
(202035, 'ruRU', 'Остался один лоа... Он вот-вот угаснет...', '', 38134),
(202034, 'ruRU', 'Шадра! Бвонсамди сказал, что ее пожрала собственная жрица...', '', 38134),
(202033, 'ruRU', 'Я чувствую лоа вон там. Быстрее!', '', 38134),
(202032, 'ruRU', 'Хир\'ик! Я слышал его последний крик в Назмире...', '', 38134),
(202029, 'ruRU', 'Ты мне что-нибудь несешь?', '', 38134),
(202027, 'ruRU', '', 'Постой!', 38134),
(202024, 'ruRU', '', 'Без меня столько всего произошло...\n\nМне понадобится время, чтобы прийти в себя, если такое вообще возможно...\n\nСпасибо тебе за спасение, $n.', 38134),
(202021, 'ruRU', 'Помочь Сике? Почту за честь. Я прослежу, чтобы нужные ей припасы пришли в назначенный срок в целости с сохранности.', '', 38134),
(202020, 'ruRU', '', 'Паразиты! Ваши органы послужат нам.', 38134),
(201999, 'ruRU', '', 'Фрос поглотит этот мир!', 38134),
(201996, 'ruRU', '', 'Жалкие создания! Вы зря сопротивляетесь!', 38134),
(201995, 'ruRU', '', 'Я чую страх, переполняющий нашу жертву.', 38134),
(201994, 'ruRU', '', 'Воплощения последней надежды. Мы уже близко.', 38134),
(201993, 'ruRU', '', 'Разбитые надежды укажут нам путь.', 38134),
(201992, 'ruRU', 'О, да! Конечно, я помогу! Помочь автоматургу Софоне и чему-нибудь у нее научиться – это чудесная возможность.', 'О, да! Конечно, я помогу! Помочь автоматургу Софоне и чему-нибудь у нее научиться – это чудесная возможность.', 38134),
(201983, 'ruRU', 'Сюда! Нужно освободить выживших!', '', 38134),
(201980, 'ruRU', 'Ксавий, падший почти что сильвар! Он был советником самой Азшары, но поддался порче Изумрудного Кошмара и принес гибель своему народу! Узрите мощь его жутких ночных кошмаров!', '', 38134),
(201979, 'ruRU', 'Н\'Зот Заразитель! Его глаза... и горы рыбы!.. были повсюду, заражали весь Азерот! Кому можно было довериться? Какой рыбой – не отравиться? Вся глубина его злодеяний неизвестна и по сей день!', '', 38134),
(201978, 'ruRU', 'Джайна Праудмур, лорд-адмирал и королева льда! Подобно алмазной пыли, ее ледяной ветер замораживал врагов, пока кровь не застывала в их жилах! Они превращались в ледяные статуи, точно фигуры на носу ее зачарованного корабля!', '', 38134),
(201977, 'ruRU', 'Гул\'дан, глава Совета Теней! \"Но как он вернулся? Откуда он появился?\" – спрашивали они! Но главный вопрос был: из какого времени он вернулся? И что такого замечательного в его черепе?', '', 38134),
(201976, 'ruRU', 'Кил\'джеден Искуситель! Никто не помнит, кто такой Джеден... Но, может, это все – часть его обмана? Битва разразилась в Круговерти Пустоты – самом обманчивом измерении, как всем известно!', '', 38134),
(201975, 'ruRU', 'Аргус Порабощенный! Они пытались создать титана смерти, хотя отлично знали, что смерть находится в ведении Темных Земель и нашей славной королевы! Разумеется, это было недопустимо, и герои решили совершить невозможное: убить сам мир!', '', 38134),
(201974, 'ruRU', 'Доверься моим братьям и сестрам! Они вынесут нас отсюда!', '', 38134),
(201971, 'ruRU', 'Могу закончить строительство! Отлично. Дальше Пепельный двор.', '', 38134),
(201970, 'ruRU', 'Я тоже. Спасибо.', '', 38134),
(201969, 'ruRU', '', 'Познать себя в раздумьях... Я тебя понимаю. Надеюсь, ты найдешь то, что ищешь.', 38134),
(201968, 'ruRU', 'Нет, дитя. Они не те, кем я раньше их видел... Возможно, я уже тогда был слеп. Хочу побыть один. Мне нужно многое обдумать.', '', 38134),
(201967, 'ruRU', '', 'Куда ты пойдешь теперь, Утер? Ты ведь не вернешься к раскольникам?', 38134),
(201966, 'ruRU', 'Разберись с предателями!', '', 38134),
(201965, 'ruRU', 'Нужно расчистить путь. Талес!', '', 38134),
(201964, 'ruRU', 'Я не нанимался защищать смертных.', 'Я не нанималась защищать смертных.', 38134),
(201963, 'ruRU', 'Благодарю тебя. Они не заслужили такой участи.', '', 38134),
(201962, 'ruRU', 'Мне платят, чтобы я защищал всех в Старых воротах. Даже таких |3-8($r), как ты. Надо попросить прибавку.', 'Мне платят, чтобы я защищала всех в Старых воротах. Даже таких |3-8($r), как ты. Надо попросить прибавку.', 38134),
(201960, 'ruRU', 'Если бы мне не нужна была эта должность и жалование, я бы не поднял свое оружие для защиты смертной души.', 'Если бы мне не нужна была эта должность и жалование, я бы не подняла свое оружие для защиты смертной души.', 38134),
(201959, 'ruRU', 'Могрейн попросил меня задержаться, на случай если ты слишком сильно отклонишься от маршрута.\n\nЭти врата смерти доставят тебя к нему. Предлагаю тебе ими воспользоваться.', '', 38134),
(201958, 'ruRU', '', 'После тебя, пилигрим.', 38134),
(201950, 'ruRU', '', 'Могрейн попросил меня задержаться, на случай если ты слишком сильно отклонишься от маршрута.\n\nЭти врата смерти доставят тебя к нему. Предлагаю тебе ими воспользоваться.', 38134),
(201949, 'ruRU', 'Те, кто за это в ответе, должны быть наказаны.', '', 38134),
(201948, 'ruRU', '', 'Быть может, когда-нибудь мы это обсудим. А пока что надо выбираться отсюда.', 38134),
(201947, 'ruRU', 'Мы не такие уж разные, Клейя.', '', 38134),
(201946, 'ruRU', '', 'Может быть, мы и враги, но мы не могли обречь тебя на мучения.', 38134),
(201945, 'ruRU', 'Боль все не проходит, но это для меня не впервой. Раны заживут.', '', 38134),
(201944, 'ruRU', '', 'Ты в порядке, Утер?', 38134),
(201943, 'ruRU', 'Спасибо, что спасли меня от пыток Лисонии. Если бы вы вовремя не вмешались, от меня бы ничего не осталось.', '', 38134),
(201942, 'ruRU', '', 'Верные Утробе явно ищут вас. Давайте не будем терять время.', 38134),
(201940, 'ruRU', 'В этом поразительном представлении один из наших артистов у вас на глазах преобразится в могучего врага из другого мира, готового сразиться с любым, у кого хватит храбрости бросить ему вызов!', '', 38134),
(201939, 'ruRU', '', 'Пусть чудище пройдет.', 38134),
(201938, 'ruRU', 'Помните, именно благодаря нашим покровителям и таким зрителям, как вы, у нас хватает анимы на то, чтобы продолжать творить искусство! А теперь без дальнейших церемоний...', '', 38134),
(201937, 'ruRU', 'Правя в глубинах под тяжестью волн, когда-то прекрасная королева Азшара напала на своих врагов! Заманив их в свой дворец, она пустила в ход свое коварство и свои многочисленные щупальца, чтобы сразить их! Да, королева!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201936, 'ruRU', 'Осторожно! Она едва держится...', '', 38134),
(201935, 'ruRU', 'Внимание, ценители искусства! Я рад вам сообщить, что благодаря щедрому вкладу от |3-1($n) в амфитеатре Звездного Озера скоро начнется новое представление!', '', 38134),
(201933, 'ruRU', '', 'Держитесь рядом. Не стоит привлекать лишнее внимание.', 38134),
(201932, 'ruRU', '', 'Разберись с врагом! Иначе мы ее потеряем!', 38134),
(201931, 'ruRU', '', 'Нужно доставить дикое семя Изеры королеве. Это ее единственная надежда!', 38134),
(201930, 'ruRU', '', 'Я встречусь с бывшими соратниками. Не теряй времени.', 38134),
(201928, 'ruRU', 'Какой чудесный день для вдохновения! Мое сердце творца просто поет! Мои крылья дрожат от всех творческих идей, наводнивших мой разум! Это просто чудо, что у нас достаточно анимы, чтобы вновь позволить мне творить.$b$bЯ проделал огромный труд и точно знаю, что новый спектакль потрясет Арденвельд. Хочешь его посмотреть? Взглянуть на плод моего вдохновения?', '', 38134),
(201926, 'ruRU', 'Ах, горе мне!$b$BО, друг мой. Я голодаю. Но не по воде и пище, которые тебе привычны. Я изголодался по пище для души! По искусству, которое возносит нас выше, чем может взлететь даже самая стойкая пикси!$b$BНо эта засуха не позволяет нам творить. Вместо этого мы вынуждены бороться, просто чтобы выжить.$B$BНо скажи мне, чего стоит жизнь, лишенная искусства?', '', 38134),
(201925, 'ruRU', 'Время идет! Вперед!', '', 38134),
(201924, 'ruRU', '', 'Сейчас скорость важнее скрытности. Полетели к путевому камню и поскорее покинем это место.', 38134),
(201923, 'ruRU', 'Прикрой меня!', '', 38134),
(201922, 'ruRU', 'Готово! Назад!', '', 38134),
(201921, 'ruRU', 'Сейчас будет весело!', '', 38134),
(201920, 'ruRU', 'Это будет нечто!', '', 38134),
(201909, 'ruRU', 'Теперь вы видите? Когда мы вместе, против нас никто не устоит!', '', 38134),
(201908, 'ruRU', 'Рр-р-а!', '', 38134),
(201907, 'ruRU', 'Мы прогоним их вместе. Сначала отсюда, потом из всех наших царств!', '', 38134),
(201906, 'ruRU', 'Пожиратели! Общий враг, который гложет окраины Темных Земель!', '', 38134),
(201905, 'ruRU', '', 'Нельзя забывать, что истинная цель Ревендрета – спасение душ.', 38134),
(201904, 'ruRU', 'Защити оставшиеся души. Мы попробуем им помочь.', '', 38134),
(201903, 'ruRU', 'Берем все души, какие сможем, и отступаем!', 'Берем все души, какие сможем, и отступаем!', 38134),
(201902, 'ruRU', '', 'Спаси души, если сможешь. Может быть, мы сумеем их восстановить!', 38134),
(201901, 'ruRU', '', 'Порочность Владыки не знает границ!', 38134),
(201900, 'ruRU', 'Эти души поработил Владыка и превратил их в оружие!', '', 38134),
(201899, 'ruRU', 'Пахучую грязь я бы, конечно, не выбрал для украшения интерьера...', '', 38134),
(201898, 'ruRU', 'Что они натворили?! Все вокруг в грязи!', '', 38134),
(201897, 'ruRU', 'Хватит пачкать! Каждый землерой сам за себя!', '', 38134),
(201896, 'ruRU', 'Пилигрим, прогони их отсюда. И дайте мне тряпку!', '', 38134),
(201895, 'ruRU', 'Землерои вымазаны жижей! Они испортят украшения!', '', 38134),
(201893, 'ruRU', 'Преследовать их до периметра. Ни шагу дальше!', '', 38134),
(201892, 'ruRU', 'Работа эксперта! Я прослежу, чтобы слизь в кратчайшие сроки попала куда следует.', '', 38134),
(201891, 'ruRU', 'А, подмастерье! Нам отчаянно не хватает слизи. Не поможешь нам с... добычей?', '', 38134),
(201890, 'ruRU', 'Привет! Изобретатель чумы Марилет послал меня сюда испытать это оборудование. Мы собираем в этой местности живую слизь. Или хотя бы ее образцы. Хочешь помочь?', '', 38134),
(201888, 'ruRU', 'Не я, конечно же... Но некоторые!', '', 38134),
(201887, 'ruRU', 'Отступаем и собираемся у форта!', '', 38134),
(201886, 'ruRU', 'Хорошо, что мы сегодня пробудили наших камнерожденных. В небо!', '', 38134),
(201885, 'ruRU', 'Каменный легион! Неужто Владыка прислал к нам своих самых сильных воинов?', '', 38134),
(201884, 'ruRU', 'Некоторые считают внимание Каменного легиона большой честью.', '', 38134),
(201883, 'ruRU', '', 'А ты не боишься заводить сильных врагов, $p...', 38134),
(201875, 'ruRU', 'Этот может усиливать малдрацит анимой. А еще он жутко напуган. Видишь его взгляд?', 'Этот может усиливать малдрацит анимой. А еще он жутко напуган. Видишь его взгляд?', 38134),
(201874, 'ruRU', 'Этот позволяет усиливать малдрацит анимой.\n\nЕго усиление даст тебе\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[обработанный малдрацит]|h|r. У тебя будет осколок малдрацита, поглощающий души убитых врагов. Когда твой запас здоровья опустится ниже определенного уровня, он расколется, отбрасывая находящихся рядом противников, и на некоторое время укроет тебя щитом.\n\nЭффект срабатывает довольно редко, но время его восстановления сокращается каждый раз, когда ты наносишь смертельный удар.', 'Этот позволяет усиливать малдрацит анимой.\n\nЕго усиление даст тебе\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[обработанный малдрацит]|h|r. У тебя будет осколок малдрацита, поглощающий души убитых врагов. Когда твой запас здоровья опустится ниже определенного уровня, он расколется, отбрасывая находящихся рядом противников, и на некоторое время укроет тебя щитом.\n\nЭффект срабатывает довольно редко, но время его восстановления сокращается каждый раз, когда ты наносишь смертельный удар.', 38134),
(201873, 'ruRU', 'Береги свои руки! Этот хорошо работает со льдом и морозом.', 'Береги свои руки! Этот хорошо работает со льдом и морозом.', 38134),
(201872, 'ruRU', 'Береги руки! Этот хорошо работает со льдом и морозом.\n\nЕго усиление даст тебе \n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[арктическую плоть]|h|r. При получении урона ты будешь с некоторой вероятностью получать один эффект \"Кристаллизующаяся анима\", уменьшающий наносимый тебе урон. Накопив максимальное количество эффектов, при получении урона ты заморозишь находящихся поблизости врагов на 5 сек. и увеличишь получаемый ими урон.', 'Береги руки! Этот хорошо работает со льдом и морозом.\n\nЕго усиление даст тебе \n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[арктическую плоть]|h|r. При получении урона ты будешь с некоторой вероятностью получать один эффект \"Кристаллизующаяся анима\", уменьшающий наносимый тебе урон. Накопив максимальное количество эффектов, при получении урона ты заморозишь находящихся поблизости врагов на 5 сек. и увеличишь получаемый ими урон.', 38134),
(201870, 'ruRU', 'Этот умеет призывать всяких чахлых тварей. Отвратительно.', 'Этот умеет призывать всяких чахлых тварей. Отвратительно.', 38134),
(201869, 'ruRU', 'Этот умеет призывать ветхих костлявых тварей.\n\nЕго усиление даст тебе следующий эффект.\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Армия скелетов]|h|r: когда пассивный эффект \"Скульптора плоти\" поглощает сущность трупа, он с большой вероятностью призывает скелет, который сражается на твоей стороне.', 'Этот умеет призывать ветхих костлявых тварей.\n\nЕго усиление даст тебе следующий эффект.\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Армия скелетов]|h|r: когда пассивный эффект \"Скульптора плоти\" поглощает сущность трупа, он с большой вероятностью призывает скелет, который сражается на твоей стороне.', 38134),
(201868, 'ruRU', '', 'Идем, Утер. Здесь небезопасно.', 38134),
(201865, 'ruRU', 'Здравствуй.', 'Здравствуй.', 38134),
(201864, 'ruRU', 'Что-то посоветовать?', 'Что-то посоветовать?', 38134),
(201863, 'ruRU', 'Чем тебе помочь?', 'Чем тебе помочь?', 38134),
(201861, 'ruRU', '', 'С радостью, госпожа. Слугам Архонта не выстоять против нас.', 38134),
(201860, 'ruRU', '', 'Не подведи меня, Лисония. Докажи, что достойна своей новой силы.', 38134),
(201859, 'ruRU', '', 'От этих друстов столько неприятностей! Мы должны защитить рощу и дикое семя Изеры, иначе все будет напрасно!', 38134),
(201858, 'ruRU', '', 'Ты чем-то обеспокоен, Тралл?', 38134),
(201857, 'ruRU', '', 'Если из Утробы есть выход, то найдется и путь обратно. Мы за ней вернемся.', 38134),
(201855, 'ruRU', '', 'Да, но мы не знаем, где она. Мы попробуем ее найти, но этот камень может быть единственным шансом сбежать отсюда.', 38134),
(201853, 'ruRU', 'Тиранда. Мы не можем оставить ее одну среди этого кошмара.', '', 38134),
(201851, 'ruRU', 'Она вошла в портал прямо перед нами. Клялась, что отыщет Сильвану... любой ценой.', '', 38134),
(201850, 'ruRU', 'Ни единого следа верховной жрицы. Должно быть, она где-то посреди этого хаоса.', '', 38134),
(201849, 'ruRU', 'Портал не убил тебя? Хорошо. Без тебя мы вряд ли справимся.', '', 38134),
(201848, 'ruRU', '', 'Приближается что-то опасное. Нам лучше держаться вместе.', 38134),
(201844, 'ruRU', '', 'Мве\'зала еще познает мою ярость. Надеюсь, ты знаешь, что делаешь...', 38134),
(201842, 'ruRU', '', 'Я помню, как меня вырвали из леса. Я отказалась помогать проклятому Мве\'зале... и меня бросили сюда.\n\nБудь у меня силы, я бы разорвала его в клочья и раскидала по всем мирам!\n\nМне нужно вернуться в лес. Ты поможешь мне?', 38134),
(201841, 'ruRU', '', 'Ее слово – закон.', 38134),
(201840, 'ruRU', '', 'Ка-Делен, собери представителей ковенантов в Анклаве. Когда мы с пилигримом вернемся, то сразу направимся туда. Такова воля Арбитра.', 38134),
(201839, 'ruRU', '', 'Значит, я отвечу на зов. Следуй за мной, пилигрим. Быть может, они укажут нам путь.', 38134),
(201838, 'ruRU', 'Высокочтимый Голос! Вечные зовут тебя в покои Арбитра!', '', 38134),
(201837, 'ruRU', 'Похоже, Тюремщик втайне вершил судьбу и нашего мира.', '', 38134),
(201836, 'ruRU', '', 'Храни нас Предназначение... Все еще хуже, чем мы думали.', 38134),
(201835, 'ruRU', 'Денатрий вызвал засуху и выпустил свои запасы анимы в Утробу. Он действует заодно с Тюремщиком.', '', 38134),
(201834, 'ruRU', '', 'Знакомая энергия манит меня на запад. Мы должны помешать планам Лисонии и ее госпожи.', 38134),
(201833, 'ruRU', '', 'Надеюсь, я не буду об этом жалеть.', 38134),
(201832, 'ruRU', '', 'Встретимся на руинах дома Глаз. Нам потребуется... деликатный подход.', 38134),
(201831, 'ruRU', '', 'Нам не удержать Престол Примаса без некрополя, поэтому мы отберем его у врагов.', 38134),
(201830, 'ruRU', '', 'Нет. Довольно!', 38134),
(201829, 'ruRU', '', 'Но вы решили забирать и уничтожать. Как это нелепо... Мне вас почти жаль.', 38134),
(201828, 'ruRU', '', 'Друсты могли стать защитниками природы. Хранителями круговорота.', 38134),
(201826, 'ruRU', '', 'Одну за другой мы заберем каждую рощу. Каждое существо. Каждую душу.', 38134),
(201825, 'ruRU', '', 'Но мы становимся все сильнее, пока твои верные слуги увядают!', 38134),
(201824, 'ruRU', '', 'Мелочные создания... Не знаете ничего, кроме своих прихотей.', 38134),
(201823, 'ruRU', '', 'Ты будешь бессильно смотреть, как я сажусь на твой трон и уничтожаю Сердце леса!', 38134),
(201822, 'ruRU', '', 'Мне не нужна жалость слабеющей королевы.', 38134),
(201820, 'ruRU', 'Дикая Охота, вперед! За Арденвельд!', '', 38134),
(201813, 'ruRU', 'Это Пеплошкурая! Дикая богиня, что билась в Войне древних!', '', 38134),
(201812, 'ruRU', 'Вы слышали приказ. Удачи вам в бою, кирии Бастиона.', '', 38134),
(201811, 'ruRU', 'Здравствуй, $c. Дикая Охота готова оказать тебе поддержку в бою с нашими врагами.\n\nПриняв мою помощь, ты получишь способность: \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Воркайский рывок]|h|r: когда противники превосходят вас числом, ваши заклинания и способности могут призвать воркая. Он бросается к цели, нанося противникам на своем пути урон от сил природы и оглушая их.', 'Здравствуй, $c. Дикая Охота готова оказать тебе поддержку в бою с нашими врагами.\n\nПриняв мою помощь, ты получишь способность: \n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Воркайский рывок]|h|r: когда противники превосходят вас числом, ваши заклинания и способности могут призвать воркая. Он бросается к цели, нанося противникам на своем пути урон от сил природы и оглушая их.', 38134),
(201810, 'ruRU', 'С моим усилением сама земля обратится против врагов Арденвельда.\n\nПриняв мою помощь, ты получишь способность:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Гнев тирненна]|h|r: заклинания и способности с высокой вероятностью призывают земляные осколки, которые вызывают у противников кровотечение, наносящее урон от сил природы раз в секунду. Эффект суммируется. Противники, погибающие с максимальным количеством эффектов, взрываются, нанося находящимся рядом противникам урон от сил природы.', 'С моим усилением сама земля обратится против врагов Арденвельда.\n\nПриняв мою помощь, ты получишь способность:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Гнев тирненна]|h|r: заклинания и способности с высокой вероятностью призывают земляные осколки, которые вызывают у противников кровотечение, наносящее урон от сил природы раз в секунду. Эффект суммируется. Противники, погибающие с максимальным количеством эффектов, взрываются, нанося находящимся рядом противникам урон от сил природы.', 38134),
(201809, 'ruRU', 'Я учу всех, кто ходит, бегает или гарцует, двигаться с грацией и изяществом, $r.\n\nПриняв мою помощь, ты получишь способность:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Хитрые уловки]|h|r: при использовании вне боя \"Облик души\" скрывает вас от противников.', 'Я учу всех, кто ходит, бегает или гарцует, двигаться с грацией и изяществом, $r.\n\nПриняв мою помощь, ты получишь способность:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Хитрые уловки]|h|r: при использовании вне боя \"Облик души\" скрывает вас от противников.', 38134),
(201802, 'ruRU', 'Так что держись за эту связь и не перемещай свой дух в это жуткое место полностью. Иначе твоя душа... Пуф!', '', 38134),
(201789, 'ruRU', 'Сообщи Королеве Зимы. Она должна об этом узнать.', '', 38134),
(201788, 'ruRU', '', 'К сожалению, мы потеряли их навсегда. Горак Зар заплатит за страдания, причиненные моему лесу.', 38134),
(201787, 'ruRU', '', 'Прискорбные известия, $n. Я надеялась, что мы сумеем спасти тех, кого поработили друсты...', 38134),
(201779, 'ruRU', 'С моим усилением сама земля обратится против врагов Арденвельда.', 'С моим усилением сама земля обратится против врагов Арденвельда.', 38134),
(201774, 'ruRU', 'Я учу всех, кто ходит, бегает или гарцует, двигаться с грацией и изяществом, $r.', 'Я учу всех, кто ходит, бегает или гарцует, двигаться с грацией и изяществом, $r.', 38134),
(201773, 'ruRU', 'Славный бой! Теперь займемся делом...', '', 38134),
(201772, 'ruRU', 'Двое на одного? Не повезло... вам!', '', 38134),
(201771, 'ruRU', 'Я их задержу. Вперед!', '', 38134),
(201770, 'ruRU', '', 'Готово. Я останусь защищать это место.', 38134),
(201766, 'ruRU', '', 'Дальше я сама. Иди!', 38134),
(201765, 'ruRU', '', 'Яркая Искра? Гервин? И вы тоже...', 38134),
(201764, 'ruRU', '', 'Быстрее, помоги мне наладить связь с маяком!', 38134),
(201763, 'ruRU', '', 'Помоги мне вернуть маяк, $p.', 38134),
(201762, 'ruRU', '', 'Я порву тебя на куски!', 38134),
(201747, 'ruRU', 'Это замечательно! Кевину пойдет на пользу научная экспедиция, а наши коллеги наверняка оценят, если мы принесем им немного слизи!', '', 38134),
(201744, 'ruRU', '', 'Прости, мне нужно перевести дух.', 38134),
(201741, 'ruRU', '', 'Следуя за Лисонией, я прошла за эти ворота. И там я узнала... нечто ужасное.', 38134),
(201737, 'ruRU', 'О, проклятье! На помощь! Кто-нибудь меня слышит?', '', 38134),
(201730, 'ruRU', 'Мы идем на прием всей компанией! Что может быть лучше.', '', 38134),
(201727, 'ruRU', 'Мы не сможем обойтись без камнерожденных.', '', 38134),
(201726, 'ruRU', 'Личи Кел\'Тузада обратили их в камень.', '', 38134),
(201724, 'ruRU', 'Твои кости украсят трон Мве\'залы!', '', 38134),
(201722, 'ruRU', 'Смерть врагам Мве\'залы!', '', 38134),
(201721, 'ruRU', 'Довольно! Я призываю мертвых из Жуткой трясины!', '', 38134),
(201720, 'ruRU', 'Здравствуй, $c. Дикая Охота готова поддержать тебя в бою.', 'Здравствуй, $c. Дикая Охота готова поддержать тебя в бою.', 38134),
(201718, 'ruRU', 'Мве\'зала... не может проиграть...', '', 38134),
(201717, 'ruRU', 'Мве\'зала покарает тебя...', '', 38134),
(201716, 'ruRU', 'Вы все... обречены...', '', 38134),
(201715, 'ruRU', 'Мве\'зала... восстанет...', '', 38134),
(201713, 'ruRU', 'Что... опять?..', '', 38134),
(201708, 'ruRU', 'Ты – никто против Мве\'залы!', '', 38134),
(201705, 'ruRU', 'Ты падешь вместе с Бвонсамди!', '', 38134),
(201704, 'ruRU', 'Никто не предает Мве\'залу!', '', 38134),
(201703, 'ruRU', 'Мве\'зала будет править снова!', '', 38134),
(201701, 'ruRU', 'Мы освободим господина!', '', 38134),
(201695, 'ruRU', 'Камень сработал! Какое... удивительное место!', '', 38134),
(201694, 'ruRU', 'Я буду рядом. Да направит нас Мать-Земля.', '', 38134),
(201693, 'ruRU', 'Путевой камень! Надеюсь, вместе с тобой я тоже смогу использовать его.', '', 38134),
(201690, 'ruRU', 'Как видишь, я пока не готов подойти к Зене. Мне еще многому надо научиться.', '', 38134),
(201689, 'ruRU', 'О, спасибо за то, что я снова в безопасности. Эти спригганы такие хулиганы!', '', 38134),
(201637, 'ruRU', 'Бвонсамди поклоняется вся империя Зандалари! А ты без Тюремщика – просто забытый лоа. Бедный, бедный Мве\'зала!', '', 38134),
(201636, 'ruRU', '', 'Приходи в мое логово. Я постараюсь тебе помочь.', 38134),
(201635, 'ruRU', 'Мои солдаты освободят меня... вот тогда ты пожалеешь.', '', 38134),
(201634, 'ruRU', '', 'Ксандрия ждет нас на \"Зерекриссе\". Вперед.', 38134),
(201631, 'ruRU', 'Ой! Ой-ой! С водой происходит что-то странное.', '', 38134),
(201630, 'ruRU', 'Я рад снова тебя видеть, $n. Бвонсамди знает, что может на тебя положиться.\n\nНам предстоит многое сделать, чтобы остановить Мве\'залу.', '', 38134),
(201629, 'ruRU', 'Я поднимусь вверх и осмотрюсь с воздуха.', '', 38134),
(201628, 'ruRU', 'Никто не проникает в эти стены незамеченным. Вы отсюда уже не уйдете!', '', 38134),
(201627, 'ruRU', 'Портал в зеркале? Думаете, вы меня перехитрили?', '', 38134),
(201626, 'ruRU', '', 'Привет, друг. Я Цветок Ивы. Какой ужас происходит с Арденвельдом, правда?\n\n<Она склоняется к вам и понижает голос.>\n\nНе говори ей, но я думаю, как бы мне поквитаться с Лунной Ягодой. Мы все время друг друга разыгрываем, и в этот раз ей мало не покажется!\n\nУ меня есть одна идея, но для этого мне нужно как-то попасть за кулисы амфитеатра. Не поможешь мне?', 38134),
(201622, 'ruRU', 'С помощью этого рога ты сможешь призвать рыцарей Черного Клинка. Используй его с умом.', '', 38134),
(201620, 'ruRU', 'Я провожу пилигрима, мой принц. Идем, $n.', '', 38134),
(201619, 'ruRU', 'Вор в ловушке не разобьется при падении с любой высоты. Если тебе вдруг придет это в голову.', '', 38134),
(201618, 'ruRU', 'Выкинь их с вечеринки!', '', 38134),
(201617, 'ruRU', 'Эта ловушка обездвиживает воров, чтобы потом их можно было переместить... в более подходящее место.', '', 38134),
(201615, 'ruRU', 'Меня мало чем удивишь, и уж точно не попытками украсть наш необычный товар.\n\nНа такой случай картель Та разработал исключительно эффективную защиту от кражи.', '', 38134),
(201613, 'ruRU', 'Пилигрим Утробы! Спустись вниз!', '', 38134),
(201612, 'ruRU', 'Тогда почему тот землерой вскрывает замок прямо позади тебя?', '', 38134),
(201611, 'ruRU', 'Не нужно. Здесь нет воров!', '', 38134),
(201610, 'ruRU', '', 'Шторм превратился в легкий бриз. Лети к остальным. Защити Арденвельд.', 38134),
(201609, 'ruRU', 'Я и так здесь задержался. Идем.', '', 38134),
(201608, 'ruRU', 'Наконец-то... я свободен.', '', 38134),
(201604, 'ruRU', 'Уходи! Иначе тебя тоже закуют!', '', 38134),
(201603, 'ruRU', 'О! Вечеринка – отличный способ восстановить силы. Отдохну – и снова в бой!', '', 38134),
(201602, 'ruRU', 'За небольшую плату я покажу, как в картеле Та поступают с ворами.', '', 38134),
(201600, 'ruRU', 'Да. Я пойду вместе с Чуфой. Только сначала вздремну.', '', 38134),
(201599, 'ruRU', 'Солдаты, за мной! Мы идем в атаку на Малдраксус!', '', 38134),
(201598, 'ruRU', 'Мой народ почти истребили, а Солнечный Колодец осквернили, чтобы воскресить лича.', '', 38134),
(201595, 'ruRU', 'Спасибо. Без твоей помощи у нас бы ничего не вышло.', '', 38134),
(201594, 'ruRU', 'Меня утешает, что память о Цветке Ивы продолжит жить и станет частью новых перерожденных.', '', 38134),
(201593, 'ruRU', 'Помести сосуд сюда, $n.', '', 38134),
(201591, 'ruRU', 'Идем. Пелагий ждет нас в мастерской.', '', 38134),
(201590, 'ruRU', 'Я склоняюсь перед благородной жертвой Арденвельда.', '', 38134),
(201589, 'ruRU', 'Постой! Что там впереди?', '', 38134),
(201588, 'ruRU', 'Какова же сила лорд-адмирала, если она одолела такое чудовище?', '', 38134),
(201587, 'ruRU', 'Лучше не рассеивать эти чары. Идем дальше.', '', 38134),
(201586, 'ruRU', 'Так-так, снова магия леди Праудмур...', '', 38134),
(201585, 'ruRU', 'Следы борьбы уводят туда. Надо идти по ним, пока... след не простыл.', '', 38134),
(201584, 'ruRU', 'Магия льда. Похоже на работу нашего лорд-адмирала.', '', 38134),
(201582, 'ruRU', '', 'Ара\'лон винит себя в том, что случилось. Боюсь, чувство вины может поглотить его.', 38134),
(201581, 'ruRU', 'Киварра поселилась в берлоге у Сумеречной рощи. Встретимся там.', '', 38134),
(201580, 'ruRU', 'Мы придумаем, как спасти тебя, Гвейир. Обещаю...', '', 38134),
(201579, 'ruRU', '', 'Сейчас она спит... хотя сон вряд ли принесет ей покой.', 38134),
(201578, 'ruRU', 'Горак Зар и ее прихвостней нужно остановить. Найди Киварру в Темных Землях и покончи с этим проклятием.', '', 38134),
(201577, 'ruRU', '', 'Гол Инат станет твоей могилой!', 38134),
(201576, 'ruRU', '', 'Ничтожество! Безмозглый терномант завел тебя прямо в ловушку.', 38134),
(201575, 'ruRU', 'Пусть горак Тул мертв, но там все еще живо эхо его темной сущности.', '', 38134),
(201574, 'ruRU', 'Положи фетиш в котел в Гол Инате, но берегись...', '', 38134),
(201573, 'ruRU', '', 'Если проклятье друстов можно снять, Ульфар должен знать, как. Обратись к нему за помощью.', 38134),
(201572, 'ruRU', '', 'Думаю, юной охотнице еще можно помочь...', 38134),
(201571, 'ruRU', 'Доложи обо всем лорду Герну. А я позабочусь о Гвейир.', '', 38134),
(201570, 'ruRU', 'Надевай маску. Мы должны найти остальных.', '', 38134),
(201569, 'ruRU', 'Поищи вокруг что-то, что можно использовать для маскировки.', '', 38134),
(201567, 'ruRU', 'Мы больше не можем позволить себе терять охотников и охотниц. Найди их и верни домой.', '', 38134),
(201564, 'ruRU', '', 'Оцепите периметр! Если что, вызывайте подкрепление!', 38134),
(201563, 'ruRU', 'Удвоить охрану! Никого не выпускать!', '', 38134),
(201562, 'ruRU', '', 'Покажи им, как малдраксийцы разбираются с предателями!', 38134),
(201561, 'ruRU', 'Месть... за Владыку!', 'Месть... за Владыку!', 38134),
(201560, 'ruRU', 'Стража! Не упустите сбежавших заключенных!', '', 38134),
(201559, 'ruRU', 'Зеркальные тюрьмы не выдерживают!', '', 38134),
(201558, 'ruRU', 'Передай принцу Ренаталу, что я благодарен за приглашение. Я приду на прием при Пепельном дворе.', '', 38134),
(201557, 'ruRU', 'Как скажешь, идеал. Я справлюсь со своим дискомфортом.', '', 38134),
(201556, 'ruRU', 'Может, и есть. Но для тебя это хорошая возможность стать лучше, послужив воле Архонта.', '', 38134),
(201555, 'ruRU', 'Нет, что ты. Просто... есть же другие, более привычные к таким фривольностям.', '', 38134),
(201554, 'ruRU', 'Последний ключ к ней – Арбитр. Защищайте ее любой ценой.', '', 38134),
(201553, 'ruRU', 'Предупреди Архонта, Королеву Зимы и Сира. Они должны сберечь свои миры и не дать Зоваалу добраться до гробницы.', '', 38134),
(201552, 'ruRU', 'У Темных Земель осталась лишь одна надежда. Вечные должны снова объединиться, пока Тюремщик не вырвался из Утробы.', '', 38134),
(201551, 'ruRU', 'Если ты слышишь это послание, мои подозрения сбылись. Зоваал перековал свои цепи в оружие... и одолел меня.', '', 38134),
(201549, 'ruRU', 'Мы – дом Избранных. Наша сила в оружии. Надо собрать сильнейших солдат... даже пропавшего барона.', '', 38134),
(201548, 'ruRU', 'Но, боюсь, Зоваал действовал не в одиночку. У него могли быть давние союзники... и он постарается найти новых.', '', 38134),
(201547, 'ruRU', 'Много веков назад Вечные наказали нашего брата Зоваала за измену. Утроба навсегда стала его темницей, а он – ее Тюремщиком.', '', 38134),
(201545, 'ruRU', 'Я – Примас. Ты в моем святилище, и это значит, что тьма поглотила Малдраксус... и остальные царства Смерти.', '', 38134),
(201544, 'ruRU', 'А, Адрест! Говорят, ты слишком важная птица, чтобы принять приглашение принца Ренатала? Или я что-то не так понял?', '', 38134),
(201542, 'ruRU', 'Чтобы выучить урок, нужно терпение.\n\nЧтобы увидеть полную картину, нужны знания.\n\nЧтобы сделать выбор, нужно понимание.\n\nЧтобы познать скрытую суть, нужна проницательность.\n\nВот он, путь мудрости.', 'Чтобы выучить урок, нужно терпение.\nЧтобы увидеть полную картину, нужны знания.\nЧтобы сделать выбор, нужно понимание.\nЧтобы познать скрытую суть, нужна проницательность.\n\nВот он, путь мудрости.', 38134),
(201538, 'ruRU', 'Я бы счел за честь посетить прием. Но все же полемарх Адрест уже приглашен.', '', 38134),
(201536, 'ruRU', 'Просить о помощи – это нормально. В этом нет позора.', '', 38134),
(201535, 'ruRU', 'Спасибо, $p! Я не так много могу тебе предложить, поэтому прими дар \"Лунного зрения\" в знак благодарности за твою помощь. Теперь, когда у меня снова есть мои инструменты, я могу продолжить работу в саду.', 'Спасибо, $p! Я не так много могу тебе предложить, поэтому прими дар \"Лунного зрения\" в знак благодарности за твою помощь. Теперь, когда у меня снова есть мои инструменты, я могу продолжить работу в саду.', 38134),
(201534, 'ruRU', '', 'Равновесие восстановлено, пусть и высокой ценой. Спасибо, пилигрим.', 38134),
(201531, 'ruRU', 'Моя корзина, флейта, молоток и жезл куда-то пропали. И еще дневник! Если спригганы его уничтожат, то история сада будет утеряна навсегда...\n\nНе знаю, чем отплатить за это доброе дело, но если ты найдешь мои вещи, то заслужишь мою вечную благодарность.', 'Моя корзина, флейта, молоток и жезл куда-то пропали. И еще дневник! Если спригганы его уничтожат, то история сада будет утеряна навсегда...\n\nНе знаю, чем отплатить за это доброе дело, но если ты найдешь мои вещи, то заслужишь мою вечную благодарность.', 38134),
(201530, 'ruRU', 'Серьезно?! Конечно, ты |3-6($pc) что надо, но опасность велика, а я себе не прощу, если ты пострадаешь.', 'Серьезно?! Конечно, ты |3-6($pc) что надо, но опасность велика, а я себе не прощу, если ты пострадаешь.', 38134),
(201529, 'ruRU', 'До встречи!', '', 38134),
(201526, 'ruRU', 'Это моя вина, что Гвейир схватили и поработили друсты.', '', 38134),
(201525, 'ruRU', '\"Чтобы познать скрытую суть, нужна проницательность\".\n\nЯ уже познал путь, но что-то здесь не сходится. Должно быть, я чего-то не замечаю.', '\"Чтобы познать скрытую суть, нужна проницательность\".\n\nЯ уже познала путь, но что-то здесь не сходится. Должно быть, я чего-то не замечаю.', 38134),
(201524, 'ruRU', '\"Чтобы сделать выбор, нужно понимание\".\n\nМне нужно еще немного подумать.', '\"Чтобы сделать выбор, нужно понимание\".\n\nМне нужно еще немного подумать.', 38134),
(201523, 'ruRU', '\"Чтобы увидеть полную картину, нужны знания\".\n\nНо что я должен увидеть?', '\"Чтобы увидеть полную картину, нужны знания\".\n\nНо что я должна увидеть?', 38134),
(201522, 'ruRU', '\"Чтобы выучить урок, нужно терпение\".\n\nМне надо над этим поразмыслить. Спешка ни к чему не приведет.', '\"Чтобы выучить урок, нужно терпение\".\n\nМне надо над этим поразмыслить. Спешка ни к чему не приведет.', 38134),
(201521, 'ruRU', 'А-а-а!', '', 38134),
(201520, 'ruRU', 'Стихии... услышьте меня. Дайте мне силу...', '', 38134),
(201519, 'ruRU', '', 'Сколько еще раз мне тебя побеждать?', 38134),
(201518, 'ruRU', '', 'Довольно!', 38134),
(201513, 'ruRU', 'Наши глаза привыкли к лунному свету. Благодаря этому нам стали доступны секреты этого мира, скрытые от невооруженного взгляда.\n\n\"Лунное зрение\" позволит тебе увидеть то, что обычно видят только пикси.', 'Наши глаза привыкли к лунному свету. Благодаря этому нам стали доступны секреты этого мира, скрытые от невооруженного взгляда.\n\n\"Лунное зрение\" позволит тебе увидеть то, что обычно видят только пикси.', 38134),
(201512, 'ruRU', 'Подмастерье! Вот ты где!', '', 38134),
(201511, 'ruRU', '', 'Эти существа в масках внезапно напали на рощу. Прошу, помоги от них отбиться!', 38134),
(201510, 'ruRU', '', 'Я согласна, что нам лучше объединить силы, и приду на ваш прием как представитель Дикой Охоты.', 38134),
(201509, 'ruRU', '', 'Он знает, что ты здесь...', 38134),
(201508, 'ruRU', '', '<Блестянка вдруг мрачнеет.>\n\nПотерять древо – ужасная трагедия. Когда анимы не хватает на весь лес, Королева может дать одному из них умереть, чтобы спасти остальные.\n\nСтрашная мера, хоть и необходимая.\n\nКогда древо умирает, его корни больше не могут питать дикие семена. Некоторые уносят в другие рощи, остальные же...\n\nЯ слышала, что в погибших рощах грань между Арденвельдом и другими мирами истончается, и это еще ужасней.\n\nГоворят, оттуда нам прежде всего стоит ждать нападения.', 38134),
(201507, 'ruRU', '', 'Ах, да! Тирненны – это очаровательные высокие создания, сами похожие на деревья. \n\nОни как-то связаны с древами Арденвельда, но мы не знаем как... это такой древний и странный народ!\n\nНекоторые тирненны даже слышат, как деревья говорят с ними! Хотя насколько я понимаю, их речь состоит не из слов, а из песен и мелодий. \n\nЭто просто невероятно!', 38134),
(201505, 'ruRU', '', 'Да! У каждого древа есть имя! \n\nНаше древо в Спящей лощине зовется Тирна Глейн. Рощу к северу, Тирна Ваал, назвали так же, как ее древо.\n\nТирна Нох, погибшее древо, нехватка анимы сгубила одним из первых, а Тирна Ровен в Сердцевинной роще высохло совсем недавно.\n\nНо Тирна Кайт в роще Урочища Когтя еще держится. \n\nТирна Эас – древо низины Мерцающих Каскадов. А я живу у Тирна Фенна, дерева рощи Песни Сна!\n\nНо другие деревья тоже погибли, как Тирна Марвол в Темном Краю. \n\nКонечно, есть еще Тирна Скитт, но туда слишком опасно даже близко подходить!', 38134),
(201500, 'ruRU', '', 'Привет. Что тебе рассказать о наших деревьях?', 38134),
(201490, 'ruRU', '', 'Спасибо, $n. Я в долгу перед тобой... за все.', 38134),
(201489, 'ruRU', '', 'Хорошо, верховный лорд. Я доверюсь вам.', 38134),
(201488, 'ruRU', 'При всем уважении, леди Праудмур, вам надо отдохнуть. А пилигрим может продолжить поиски остальных.', '', 38134),
(201487, 'ruRU', '', 'Понимаю... Нет ли вестей об Андуине и Тралле? Мы должны вернуться в Торгаст и...', 38134),
(201486, 'ruRU', 'Я тоже рад тебя видеть, Джайна. Мои раны исцеляются, но должен признать... мне до сих пор снятся ужасы Утробы.', '', 38134),
(201485, 'ruRU', '', 'Бейн! Хвала Свету, ты жив!', 38134),
(201477, 'ruRU', '', 'Нужно сосредоточиться. Мы послужим Предназначению.', 38134),
(201476, 'ruRU', 'Вираз, Дрека. Идите с пилигримом к Престолу Примаса. Посмотрим, что нас там ждет.', '', 38134),
(201475, 'ruRU', 'Сначала война, теперь клинок Примаса. Ты становишься легендой!', '', 38134),
(201474, 'ruRU', 'Надеюсь, эта задумка удастся. У нас еще есть немного анимы.', '', 38134),
(201473, 'ruRU', 'Андуин... Оружие? О чем она?', '', 38134),
(201464, 'ruRU', 'Мне удалось выбраться, но в суматохе я растерял свои вещи.\n\nСпригганы уничтожат мои инструменты, я точно знаю!\n\nНо, боюсь, я не могу за ними вернуться, и мне нечем теперь работать в саду...', 'Мне удалось выбраться, но в суматохе я растеряла свои вещи.\n\nСпригганы уничтожат мои инструменты, я точно знаю!\n\nНо, боюсь, я не могу за ними вернуться, и мне нечем теперь работать в саду...', 38134),
(201462, 'ruRU', 'Я был здесь, в Саду Ночи, когда спригганы атаковали. Они заполонили весь сад и начали терроризировать нас...', 'Я была здесь, в Саду Ночи, когда спригганы атаковали. Они заполонили весь сад и начали терроризировать нас...', 38134),
(201454, 'ruRU', 'И мы отомстим за павших кирий.', '', 38134),
(201443, 'ruRU', 'Пусть эта корона символизирует предназначение Грехопада. Ревендрет объединится и искупит грехи Денатрия.', '', 38134),
(201427, 'ruRU', '', 'Мне уже сообщили об атаке. Я с тобой.', 38134),
(201424, 'ruRU', 'Ка-Делен, на нас возложена священная задача.', '', 38134),
(201422, 'ruRU', 'Да, Высокочтимый Голос.', '', 38134),
(201421, 'ruRU', '', 'Пришло время открыть путь в Малдраксус. Возьми Ка-Делен и займись этим.', 38134),
(201409, 'ruRU', '', 'Конечно... если это не очередная жестокая шутка.', 38134),
(201408, 'ruRU', '', 'Мои заклинания телепортации и твоя способность выходить из Утробы... я наконец вырвусь отсюда!', 38134),
(201407, 'ruRU', '', 'Постой... это и правда ты, $n? Я помню... путевой камень... твое бегство... Это было так давно.', 38134),
(201396, 'ruRU', '', 'Королева зовет, и мы подчинимся.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201395, 'ruRU', '', '$p, ты будто ураган в засушливой степи.', 38134),
(201394, 'ruRU', '', 'Ветви хлещут, лес гудит!', 38134),
(201392, 'ruRU', 'Мой идеал... Я... подвел тебя...', '', 38134),
(201387, 'ruRU', '', 'Только вместе мы сможем выбраться отсюда. Держитесь ближе!', 38134),
(201383, 'ruRU', '', 'Я вижу идеала! Он сражается с Лисонией – один. Где его ученики?', 38134),
(201366, 'ruRU', 'Я уже послал Клейю в погоню за Лисонией. Найди ее в храме Смирения как можно скорее.', '', 38134),
(201360, 'ruRU', '', 'Некролорды – мастера военного дела, но их мощь всегда служила защите Темных Земель, а не вредила им.', 38134),
(201359, 'ruRU', 'Ужасные новости. Если война разорит Темные Земли, все наши усилия пойдут прахом.', '', 38134),
(201347, 'ruRU', 'Как прикажете, баронесса.', '', 38134),
(201345, 'ruRU', 'Гипокос трясет головой и показывает на отсутствующий язык.', '', 38134),
(201343, 'ruRU', 'Гипокос! Что... с тобой сделали? Ты можешь говорить?', '', 38134),
(201337, 'ruRU', '', 'Мы доложим обо всем Крексусу. Удерживайте позиции.', 38134),
(201333, 'ruRU', '', 'С ним все в порядке. Он ждет тебя в Тирна Ваале.', 38134),
(201332, 'ruRU', 'Котта, ты нигде не видела Хлюпня? Я его потерял!', '', 38134),
(201331, 'ruRU', '', 'Баронесса Вайш усмехается.', 38134),
(201330, 'ruRU', '', 'Не беспокойся, юное создание. Возможно, Тиранду Шелест Ветра спасет кто-то другой.', 38134),
(201329, 'ruRU', '', 'Изера, я... не смогла вернуть Тиранду. Она в Утробе, одержима силой Ночной Воительницы. Ярость поглотила ее.', 38134),
(201328, 'ruRU', '', 'Здесь их ждет покой. Некоторые даже примут новые обличья и помогут хранителям рощ.', 38134),
(201327, 'ruRU', '', 'Что будет с этими душами?', 38134),
(201325, 'ruRU', '', 'Мой дух чуть не погиб. Но Королева Зимы пробудила меня. Теперь я связана с этим миром.', 38134),
(201324, 'ruRU', '', 'Теперь монстр уязвим! Прикончи его!', 38134),
(201323, 'ruRU', '', 'Храни нас Элуна! Ты можешь спасти эти души!', 38134),
(201322, 'ruRU', '', 'Скорее, пилигрим Утробы! Используй кристалл и спаси души ночных эльфов!', 38134),
(201321, 'ruRU', '', 'Хотя бы эти души калдорай теперь свободны. Впервые за долгое время ко мне вернулась... надежда.', 38134),
(201295, 'ruRU', 'Ты чувствуешь в себе отвагу, претендент?\n\nПродемонстрируй ее мне.', 'Ты чувствуешь в себе отвагу, претендент?\n\nПродемонстрируй ее мне.', 38134),
(201292, 'ruRU', 'Я... вечен.', '', 38134),
(201291, 'ruRU', 'Твое высокомерие не знает пределов!', '', 38134),
(201289, 'ruRU', 'Мы не можем... потерять тебя там...', '', 38134),
(201287, 'ruRU', '', 'Ша\'лор! Хвала Королеве! Я так рада тебя видеть!', 38134),
(201286, 'ruRU', 'Уф! Что ж, так тоже годится.', '', 38134),
(201284, 'ruRU', 'Мы ничего не добьемся, если наш дом падет первым. Нельзя забывать, что сейчас главное – война.', '', 38134),
(201279, 'ruRU', 'Ступай на Острие, рекрут. У нас впереди битва!', '', 38134),
(201278, 'ruRU', 'Барьер вокруг храма пропал. Прекрасно, $n.', '', 38134),
(201277, 'ruRU', 'Кажется, я нашел выход! Но я боюсь туда идти. Помоги!', '', 38134),
(201276, 'ruRU', 'Я разведаю путь. Поспеши, солдат!', '', 38134),
(201272, 'ruRU', 'Что, если они правы? Что, если... что, если...', 'Что, если они правы? Что, если... что, если...', 38134),
(201271, 'ruRU', '', 'А я отомщу той, что отправила их души сюда!', 38134),
(201270, 'ruRU', '', 'Что ж, тогда я поручаю это тебе, $n. Спаси мой народ от страданий.', 38134),
(201269, 'ruRU', '', 'Что ж, тогда я поручаю это тебе, дитя Тельдрассила. Спаси наш народ от страданий.', 38134),
(201268, 'ruRU', '', 'Пилигрим Утробы может избавить наших братьев и сестер от мучений.', 38134),
(201267, 'ruRU', '', 'Нет! Я остановлюсь только тогда, когда спасу все души... а банши ответит за свои преступления!', 38134),
(201266, 'ruRU', '', 'А я должна отомстить за наш народ.', 38134),
(201265, 'ruRU', '', 'Что ж. Если тебе и правда суждено искупить грехи Орды – да будет так.', 38134),
(201264, 'ruRU', '', 'Я не прошу тебя верить Орде... поверь мне.', 38134),
(201263, 'ruRU', '', 'Я тебя понимаю. Но пилигрим может нам помочь. Вместе мы уже спасли множество душ.', 38134),
(201262, 'ruRU', '', 'С этих пор тебе придется искать материалы самостоятельно. Посмотри в чертеж, если забудешь инструкции.', 38134),
(201261, 'ruRU', 'Боль... муки... надежды нет...', '', 38134),
(201260, 'ruRU', 'Иди. Я позабочусь об остальных.', '', 38134),
(201259, 'ruRU', '', 'Я должна отправиться на поиски сбежавших животных.', 38134),
(201258, 'ruRU', 'Для кирий просьба стать оппонентом на Пути – это очень личное. Надеюсь, вы оба осознаете важность этих поединков для наших традиций.', '', 38134),
(201257, 'ruRU', '', 'Здравствуй. Я с радостью помогу тебе смастерить приманку и зеркало.', 38134),
(201256, 'ruRU', 'Дактилия поможет тебе изготовить любой предмет для Пути Перерождения.', '', 38134),
(201254, 'ruRU', 'Ты найдешь все необходимые материалы в Бастионе. Однако потом тебе также придется собирать их в других царствах Темных Земель.', '', 38134),
(201253, 'ruRU', '', 'Я сберегла, что смогла. Наверное, хватит.', 38134),
(201250, 'ruRU', 'К вашим услугам, верховный автоматург.', '', 38134),
(201249, 'ruRU', 'Я сделал, что должно. Вспомни об этом, когда Бастион попросит вернуть долг.', '', 38134),
(201248, 'ruRU', 'Спасибо, верховный автоматург. Мы бы не справились без твоей помощи и доверия.', '', 38134),
(201247, 'ruRU', 'Корона готова. Денатрий над ней больше не властен.', '', 38134),
(201246, 'ruRU', '', 'От тебя и так сегодня полно неприятностей.', 38134),
(201245, 'ruRU', '', 'Плыви, малыш.', 38134),
(201235, 'ruRU', 'Как только Артемеда доставит послание, кирии начнут готовиться к ответному удару.', '', 38134),
(201234, 'ruRU', 'Встретимся в городе. Береги себя.', '', 38134),
(201233, 'ruRU', 'Мы немедленно отправляемся в Орибос.', '', 38134),
(201231, 'ruRU', 'Нельзя! Это испытание только для одного.', '', 38134),
(201230, 'ruRU', 'Денатрий! Мы прикроем тебя, пилигрим!', '', 38134),
(201220, 'ruRU', 'Я сыт твоей дерзостью! Пора покончить с этим!', '', 38134),
(201219, 'ruRU', 'Ты противишься моей власти?', '', 38134),
(201216, 'ruRU', '', 'У тебя за плечами долгий путь, мой гость, и тебе еще многое предстоит.\nА пока отдохни.', 38134),
(201215, 'ruRU', 'Мало времени! Отнеси корону сейчас же. Те, кто преданы Темным Землям, здесь в почете.', '', 38134),
(201210, 'ruRU', 'Отправляемся в храм?', '', 38134),
(201199, 'ruRU', 'Стой, кто идет? Ты друг или враг?', '', 38134),
(201196, 'ruRU', 'Разгадай планы противников.', '', 38134),
(201195, 'ruRU', 'Сокруши врагов своей мощью.', '', 38134),
(201194, 'ruRU', 'Пора действовать. Только в храме мы сможем вырвать корону из-под контроля Денатрия.', '', 38134),
(201193, 'ruRU', 'Будь хитрее своих неприятелей.', '', 38134),
(201192, 'ruRU', 'Барьер вокруг храма исчез.', '', 38134),
(201191, 'ruRU', 'Стремись превзойти остальных.', '', 38134),
(201189, 'ruRU', 'Это конец.', '', 38134),
(201188, 'ruRU', 'Твой час настал.', '', 38134),
(201187, 'ruRU', 'Прими поражение.', '', 38134),
(201186, 'ruRU', 'Надо было смотреть вверх.', '', 38134),
(201183, 'ruRU', 'Габбинс, ты пригласил своих друзей? Как мило.', '', 38134),
(201181, 'ruRU', 'Прекрасный бой. От Дикой Охоты никому не спрятаться, никому не убежать!', '', 38134),
(201180, 'ruRU', 'Небольшой отряд охотников патрулирует Туманную чащобу. Присутствие пилигрима Утробы поднимет им дух.', '', 38134),
(201171, 'ruRU', 'Теотар... Это твой \"плюс один\"?', '', 38134),
(201170, 'ruRU', 'Не может этого быть!', '', 38134),
(201168, 'ruRU', 'Прошу, к вашим услугам... наслаждайтесь... вот этим вот...', '', 38134),
(201160, 'ruRU', '', 'Может, я поеду на Бальтазаре на Пепельный двор...', 38134),
(201157, 'ruRU', 'Сегодня мы воздаем почести Ковинклсу!\n\nОн много трудится. Чинит-кует.\n\nТы найдешь его у Вечной кузни.\n\nДашь спелый чистоплод?', '', 38134),
(201156, 'ruRU', 'У меня есть башмаки! Не забывай про снаряжение!', '', 38134),
(201141, 'ruRU', '<Эхо павшего идеала пристально смотрит на вас.>\n\nТебе удалось отыскать недостающую главу моей истории?', '<Эхо павшего идеала пристально смотрит на вас.>\n\nТебе удалось отыскать недостающую главу моей истории?', 38134),
(201140, 'ruRU', 'Если хочешь узнать больше, отыщи фолиант и верни его мне.', 'Если хочешь узнать больше, отыщи фолиант и верни его мне.', 38134),
(201137, 'ruRU', 'Я лишь отголосок воспоминаний падшего идеала, но четко ощущаю в тебе жажду знаний.\n\nМоя история не завершена. Последнюю главу спрятали мои ученики. Раскольники хотят исказить мою судьбу в своих целях, но если записи будут целы, у них ничего не получится. Они уничтожат их, если найдут.\n\nЯ покажу тебе, где искать этот священный фолиант. Верни его мне, и ты узнаешь, чем все кончилось.\n\nЧто скажешь?', 'Я лишь отголосок воспоминаний падшего идеала, но вижу, что тебе интересна моя судьба.\n\nМоя история не завершена. Последнюю главу спрятали мои ученики. Раскольники хотят использовать мою судьбу в своих целях, но если история будет цела, у них ничего не получится. Они уничтожат ее, если найдут.\n\nЯ покажу тебе, где искать этот священный фолиант. Верни его мне, и ты узнаешь, чем все кончилось.\n\nЧто скажешь?', 38134),
(201135, 'ruRU', 'Пойдем, Хлюпень. Время быть храбрыми!', '', 38134),
(201128, 'ruRU', '', 'Цветок Ивы кивает.', 38134),
(201124, 'ruRU', 'Думаю, ты справишься, генерал. Я разведаю, что там внизу.', '', 38134),
(201123, 'ruRU', 'Кел\'Тузад грозит нам из укрытия, но ничего... Некроманты выдадут его – рано или поздно.', '', 38134),
(201117, 'ruRU', '', 'Неплохо. Это был славный бой.', 38134),
(201115, 'ruRU', '', 'Цветок Ивы задумывается, все ли она правильно сообщила Пелагию, и пожимает плечами.', 38134),
(201114, 'ruRU', '', 'Позволь испытать тебя в бою.', 38134),
(201113, 'ruRU', '', 'Посмотрим, что ты уже умеешь.', 38134),
(201112, 'ruRU', 'Я тоже могу кое-чем помочь. Этот оберег защитит тебя от опасностей.', '', 38134),
(201111, 'ruRU', 'Я обеспечу поддержку с воздуха.', '', 38134),
(201104, 'ruRU', 'Осталась последняя процедура!', '', 38134),
(201098, 'ruRU', '', 'Нельзя дать Лисонии провести ритуал. Она не должна победить.', 38134),
(201097, 'ruRU', '', 'Осенний подъем недалеко, к северу отсюда. Надеюсь, мы не опоздали.', 38134),
(201096, 'ruRU', '', 'Это даст нам немного времени... Я останусь здесь и выслушаю тебя, а ты, $n, займись раскольниками.', 38134),
(201095, 'ruRU', '', 'Мощь Хирона велика. Он принял удар на себя, чтобы отвлечь ее от послушников.', 38134),
(201094, 'ruRU', '', 'Идеал прямо у храма... Он сражается с Лисонией в одиночку!', 38134),
(201093, 'ruRU', '', 'Этот кровавый след должен привести нас к ней.', 38134),
(201091, 'ruRU', '', 'Это сделала моя минн\'до. Я видела ее во сне.', 38134),
(201085, 'ruRU', '', 'С того самого дня ни одна душа не получила свой вердикт: всех ждут только кошмары Утробы.', 38134),
(201075, 'ruRU', '\"Акерус\", оплот Черного Клинка, переместился к цитадели Ледяной Короны. Мы попадем туда через врата смерти.', '', 38134),
(201074, 'ruRU', 'Приветствую! Мы завершили приготовления.', '', 38134),
(201073, 'ruRU', 'Моя дорогая Зена, это па – мой любимый номер!', '', 38134),
(201072, 'ruRU', '', 'Зена подмигивает Айвену.', 38134),
(201071, 'ruRU', '', 'Особенно весело, когда ты кружишься, размахивая руками.', 38134),
(201070, 'ruRU', '', 'Но тебе придется научить меня паре движений, которые ты демонстрировал.', 38134),
(201069, 'ruRU', 'Конечно, госпожа Зена. Сочту за величайшую честь танцевать с вами.', '', 38134),
(201068, 'ruRU', 'Айвен краснеет и ненадолго теряет дар речи.', '', 38134),
(201067, 'ruRU', '', 'Можно, я приглашу этого господина на танец?', 38134),
(201066, 'ruRU', '', 'Прошу прощения, но я не могла не обратить внимание на твой энергичный танец.', 38134),
(201065, 'ruRU', 'Если я научусь танцевать, то завоюю сердце Зены!', '', 38134),
(201064, 'ruRU', 'Спасибо за помощь, пилигрим Утробы. Теперь я танцую гораздо лучше.', '', 38134),
(201063, 'ruRU', 'Я слежу за твоим восхождением к славе.', '', 38134),
(201062, 'ruRU', 'Я доложу обо всем Виразу. Встретимся у Престола Примаса.', '', 38134),
(201060, 'ruRU', '', 'Какое бы чудовище ни поджидало Тиранду в этой башне, мы должны ей помочь.', 38134),
(201059, 'ruRU', '', 'Я сровняю его с землей, а затем доберусь до банши!', 38134),
(201058, 'ruRU', '', 'Нет! Наши братья и сестры страдают в Торгасте!', 38134),
(201057, 'ruRU', '', 'Минн\'до! Скорее, уйдем отсюда, пока сила Элуны не поглотила тебя!', 38134),
(201056, 'ruRU', 'Пора нанести удар! Застанем Кел\'Тузада врасплох.', '', 38134),
(201055, 'ruRU', 'Покончим с этим! Нас ничто не остановит.', '', 38134),
(201054, 'ruRU', 'Встретимся там, $n.', '', 38134),
(201053, 'ruRU', 'Мы очистим ее в цитадели Верности.', '', 38134),
(201052, 'ruRU', 'Корона все еще находится под влиянием Денатрия.', '', 38134),
(201045, 'ruRU', 'Я в порядке. Нам надо попасть в храм, чтобы починить корону. Вокруг барьер. Нужно отключить.', '', 38134),
(201044, 'ruRU', 'Извини, что так долго, $n. Наш новый союзник не верил, что мои крылья выдержат нас обоих.', '', 38134),
(201034, 'ruRU', 'Добро пожаловать в нашу рощу.', 'Добро пожаловать в нашу рощу.', 38134),
(201019, 'ruRU', 'Хорошо, если ты считаешь это разумным.', '', 38134),
(201015, 'ruRU', 'Кто переманил Хелию на сторону Тюремщика? Кто выторговал у Одина глаз? Кто шепнул сделать банши вождем? Мве\'зала!', '', 38134),
(201014, 'ruRU', 'Давайте, герои! Верните мое моджо, и мы поставим этого лоа на место!', '', 38134),
(201013, 'ruRU', 'Думаешь, ты всех победил и теперь босс? Ха. Слишком долго ты пропадал.', '', 38134),
(201012, 'ruRU', 'Маленькие друзья Бвонсамди хотят его спасти? Да что вы можете против меня?', '', 38134),
(201011, 'ruRU', 'Неприятный тип этот Хаккар. Но вы заставили его отдать должок. Когда-нибудь он вернется. Злого лоа надолго не сдержать!', '', 38134),
(201010, 'ruRU', 'Что вы на меня так смотрите? Ну да, Хаккар – лоа крови. Но зато какая сделка! Ну вас. Верните мое моджо.', '', 38134),
(201009, 'ruRU', 'Этого лоа вы должны помнить! Хаккару часто нужно было моджо для ритуалов... и Бвонсамди всегда был рад новой сделке.', '', 38134),
(201008, 'ruRU', 'Брокеры что-то задумали. Не спускайте с них глаз! А-а-а! Скорее несите мне мое моджо!', '', 38134),
(201007, 'ruRU', 'Эта вряд ли захочет отдавать мое моджо. Осторожней с ней... та еще хитрюга...', '', 38134),
(201006, 'ruRU', 'У Бвонсамди связи не только в Азероте. Брокеры ищут силу... и им неважно, откуда она.', '', 38134),
(201005, 'ruRU', 'Мне ни к чему почитатели! Тюремщик даст мне сколько угодно власти... а про Бвонсамди, наоборот, все забудут!', '', 38134),
(201004, 'ruRU', 'Давно это было, но я все помню. Помню, как тролли отрекались от тебя. Все их жертвы... их кровь... они хотели... А-а-а!', '', 38134),
(201003, 'ruRU', 'Ты забываешь, кто сделал тебя лоа, Бвонсамди! Верни мне души и склонись перед своим господином!', '', 38134),
(201002, 'ruRU', 'Нет силы, кроме смерти! И смерть настигнет их всех!', '', 38134),
(201001, 'ruRU', 'Ты напрочь позабыл, что значит быть лоа! Связь с твоим народом... ваша общая сила... А-а-а!', '', 38134),
(201000, 'ruRU', 'Мне нужны их души, Бвонсамди! Отдавай мне Растахана и остальных зандаларов! Зря, зря ты злишь Мве\'залу!', '', 38134),
(200999, 'ruRU', 'Я займу этого лоа чем-нибудь. Давайте, давайте. Вперед. И не вздумайте вернуться ни с чем!', '', 38134),
(200998, 'ruRU', 'Из-за Мве\'залы я... немного влип. Но если вы заберете мое моджо у кое-кого из моих... деловых партнеров... то вместе мы с ним рассчитаемся!', '', 38134),
(200997, 'ruRU', 'Что... кто это?.. Герои! Э-э... мы в Зулдазаре повздорили, но никто же не в обиде?', '', 38134),
(200996, 'ruRU', 'Что... кто это?.. Герои! Я знал, что старые друзья придут за Бвонсамди.', '', 38134),
(200995, 'ruRU', 'Ты тряпка, Бвонсамди. Слабак. Рано или поздно ты сломаешься. Тогда Тюремщик получит свое по праву!', '', 38134),
(200994, 'ruRU', 'Чтобы ты отправил их в Утробу? Нет уж... Это мой народ, Мве\'зала. Никогда я их не предам!', '', 38134),
(200993, 'ruRU', 'Где ты прячешь души зандаларов, Бвонсамди? Отдавай их своему хозяину, а то попляшешь у меня! Уж я тебе устрою!', '', 38134),
(200992, 'ruRU', 'Настоящая любовь... Плакать хочется. Старина Бвонсамди в свое время знавал любовь. Побольше многих!', '', 38134),
(200987, 'ruRU', 'Учись у прошлого, но стремись к будущему.', '', 38134),
(200977, 'ruRU', '', 'Служи отважно.', 38134),
(200970, 'ruRU', '', 'Всякая ноша временна.', 38134),
(200963, 'ruRU', 'Надеюсь, ты не лишишь меня прибыли.', '', 38134),
(200962, 'ruRU', '', 'Не приближайся к друстам, пока добываешь пыльцу. Потом рассеешь ее над оскверненными сильварами, чтобы те впали в ярость. Надеюсь, этого будет достаточно, чтобы нарушить планы друстов.', 38134),
(200947, 'ruRU', 'Когда привыкнешь к своей новой внешности, можем начинать танец.', '', 38134),
(200946, 'ruRU', 'Потанцуем?', 'Потанцуем?', 38134),
(200945, 'ruRU', 'Я очень изобретательно дал тебе другой цвет волос, так что никто ничего не заподозрит!', 'Я очень изобретательно дал тебе другой цвет волос, так что никто ничего не заподозрит!', 38134),
(200944, 'ruRU', 'Такое гостеприимство – очень великодушно с твоей стороны.', '', 38134),
(200937, 'ruRU', 'Правда? Не знаю как и благодарить.\n\nЕсли мы порепетируем, я должен почувствовать себя увереннее.\n\n<Айвен нервно переступает с ноги на ногу.>\n\nМне пришло в голову, что лучше порепетировать с кем-то, похожим на Зену...\n\nОна ведь двигается совсем не так, как |3-6($pr)...\n\nМожет... если ты не против...\n\n<Айвен с надеждой смотрит на вас.>\n\nВ общем, могу я тебя превратить в копию Зены? На время, конечно.', '', 38134),
(200935, 'ruRU', 'Прошу прощения, пилигрим.\n\nЯ мешаю тебе пройти? Извини...\n\n<Айвен тоскливо смотрит на грациозные движения танцоров.>\n\nБоюсь, я сейчас не в лучшем состоянии.\n\nПонимаешь, я только недавно стал вентиром и пока не умею танцевать ни одного их... то есть нашего танца.\n\nСовершенно не умею, но я готов учиться! Чтобы в ритме вальса пленить сердце моей возлюбленной Зены!', '', 38134),
(200933, 'ruRU', '', 'Эй! Сюда!', 38134),
(200932, 'ruRU', 'Надеемся, вам понравится успокаивающая музыка трех смиренных душ.', '', 38134),
(200931, 'ruRU', 'Спасибо, Бедрош. Мы очень рады возможности сыграть перед вами.', '', 38134),
(200930, 'ruRU', 'Труппа \"Потерянный Грааль\" начинает свое выступление!', '', 38134),
(200929, 'ruRU', 'Откройте сердца и души и приготовьтесь услышать самую трогательную музыку во всех Темных Землях!', '', 38134),
(200928, 'ruRU', '', 'Осторожно! Это место кишит друстами, $n! Буквально кишит! Они задумали что-то очень неладное, и мы должны им помешать!\n\nПлан у меня такой... Друсты поработили много сильваров своими странными масками. Бедняжки теперь полностью подчиняются друстам. Их не спасти, но они еще смогут нам помочь.\n\nВидишь цветок рядом со мной? Это жуткая роза... Только не трогай ее! Пыльца жуткой розы очень опасна. От нее сильвары начинают на всех бросаться, пока не потеряют сознание. Я превращу тебя в неприметного арденвельдского мотылька. Набери немного пыльцы жуткой розы и рассей ее над порабощенными сильварами, чтобы устроить переполох.\n\nПлан просто безупречный!\n\nНу что, начнем?', 38134),
(200927, 'ruRU', 'Этой смертной душе якобы уже удавалось вырваться из Утробы! Проверим, правда ли это.', '', 38134),
(200925, 'ruRU', '', 'Разве он не канул в Утробу?', 38134),
(200924, 'ruRU', 'Но что, если зал окажется пустым? Нет, это напрасная затея.', '', 38134),
(200922, 'ruRU', '', 'Без Крексуса нам не победить. Мы обязаны открыть Престол Примаса!', 38134),
(200921, 'ruRU', '', '$p, за мной! Скорее!', 38134),
(200920, 'ruRU', 'Они... уничтожили некрополь. Крексус... он...', '', 38134),
(200919, 'ruRU', 'Кадавры... нанесли ответный удар... Их слишком много...', '', 38134),
(200918, 'ruRU', '', 'Вираз! Что произошло?', 38134),
(200916, 'ruRU', 'Ха-ха-ха-ха!', 'Ха-ха-ха-ха!', 38134),
(200915, 'ruRU', 'Дурацкий жук! Отцепись!', 'Дурацкий жук! Отцепись!', 38134),
(200914, 'ruRU', 'Быстрее, тупица!', 'Быстрее, тупица!', 38134),
(200913, 'ruRU', 'По седлам!', 'По седлам!', 38134),
(200912, 'ruRU', '', 'Славная победа!', 38134),
(200910, 'ruRU', '', 'Я волнуюсь, $p. Музыка обнажает мою душу и выставляет на суд публики.\n\nИ хотя мой камень грехов напоминает о тьме внутри, я надеюсь через музыку показать, что во мне еще остался свет.\n\nЛорд Гарридан из посольства согласился со мной. Он был так добр ко мне, и ему очень понравилась музыка, которую я сыграла ему в день приезда.\n\nНаверное, я оставила свою лютню у него. Пожалуйста, принеси ее мне, пока я медитирую перед выступлением.', 38134),
(200909, 'ruRU', '', 'Через поражение ты однажды придешь к победе.', 38134),
(200907, 'ruRU', '<Счастливчик Ансель неохотно отрывает взгляд от своего камня грехов.>\n\nМой груз все еще слишком тяжел.\n\nУгроза Утробы всегда будет нависать надо мной, покуда на этом камне записан хоть один грех.\n\nК счастью, мой музыкальный талант высоко оценили наблюдатели-вентиры. Я полагаю, стоит выделить время и сыграть на приеме принца Ренатала.\n\nНайди мою скрипку среди наших вещей около танцоров, и я начну публичное покаяние.', '', 38134),
(200902, 'ruRU', '<Ноко делает медленный вдох.>\n\nАссистент режиссера говорит, что не может нигде найти мою флейту.\n\nВ прошлый раз ее украл один из шумных помощников.\n\nНайди этих невоспитанных землероев, которые пакостят на празднике.\n\nЕсли вернешь мою флейту, я сыграю для твоих гостей.', '', 38134),
(200900, 'ruRU', 'Кажется, дух успокоился... Спасибо, тебе.', '', 38134),
(200895, 'ruRU', '', 'Я посвящаю первую песню тебе.', 38134),
(200894, 'ruRU', '', 'Друсты превратили мой народ в неразумных зверей! Вам с Желтоножкой предстоит встать на защиту Арденвельда.', 38134),
(200893, 'ruRU', 'Думаю, путешествие будет не из приятных. Ну что ж. Удачи!', 'Думаю, путешествие будет не из приятных. Ну что ж. Удачи!', 38134),
(200892, 'ruRU', 'Оттуда можно прыгнуть в Шпиль душ. Это поток душ, впадающий в Утробу.', 'Оттуда можно прыгнуть в Шпиль душ. Это поток душ, впадающий в Утробу.', 38134),
(200891, 'ruRU', 'Площадка телепортера ведет к Кольцу Перенаправления.', 'Площадка телепортера ведет к Кольцу Перенаправления.', 38134),
(200889, 'ruRU', 'Внимание, $p. Следуй за мной.', 'Внимание, $p. Следуй за мной.', 38134),
(200888, 'ruRU', 'Это будет мое лучшее выступление.', '', 38134),
(200886, 'ruRU', 'Этот клинок... Его выковал сам Примас!', '', 38134),
(200885, 'ruRU', 'Воздух... просто гудит... от энергии...', '', 38134),
(200883, 'ruRU', '', 'У тебя нет крыльев, но есть храбрость, достойная перерожденных!', 38134),
(200882, 'ruRU', 'Пусть наша музыка укажет путь и тебе, друг.', '', 38134),
(200881, 'ruRU', '', 'Малдраксус осмелился снова вторгнуться к нам? Покажи им, кто правит небом!', 38134),
(200880, 'ruRU', 'Бейн у него!', '', 38134),
(200879, 'ruRU', '', 'Тюремщик.', 38134),
(200876, 'ruRU', 'Пелагий, твое первое испытание ждет. Позвони в колокол, когда будешь готов.', '', 38134),
(200875, 'ruRU', '', 'Путь к перерождению открыт. Готовьтесь к бою!', 38134),
(200871, 'ruRU', 'Кто я? Я душа!\n\nКогда пришло мое время, меня призвали в Арденвельд. При жизни я разговаривал с духами природы, так что это показалось мне вполне... естественным.\n\nНравится мой облик души? Я всегда мечтал насовсем превратиться в волка!', '', 38134),
(200867, 'ruRU', '', 'Послание запечатано магией? Какая досада...', 38134),
(200865, 'ruRU', 'Я на вечеринку! Много жратвы для больших друзей!', '', 38134),
(200863, 'ruRU', '', 'Нам нельзя задерживаться. Ты сможешь идти?', 38134),
(200856, 'ruRU', 'Наши союзники не справятся с этим чудовищем одни. Мы должны помочь им!', '', 38134),
(200850, 'ruRU', '', 'О, молю, выпусти меня! Я уже слишком долго не имею возможности заботиться о рощах Арденвельда!', 38134),
(200847, 'ruRU', 'Да, это надо записать! Ой, подожди, я забыл блокнот...', '', 38134),
(200846, 'ruRU', 'Это никуда не годится. У меня же были с собой записи, я уверен. Исследования, да. Изучение анимы. \n\nВидимо, я потерял свои записи.', '', 38134),
(200842, 'ruRU', 'Хорошее представление! Давай найдем мою саблю и покажем этим тварям.', '', 38134),
(200841, 'ruRU', 'Да, вы меня поймали. Ты здесь, чтобы злорадствовать? А, ты не из них.\n\nМеня взяли в плен и отобрали оружие. Поможешь мне совершить дерзкий побег?', '', 38134),
(200838, 'ruRU', '', 'Пойдем искать... Не могу вспомнить, кого.', 38134),
(200837, 'ruRU', '', 'Они сковали меня здесь и отравили мой разум. Кажется, я еще помню, что было перед этим, но не поручусь.', 38134),
(200834, 'ruRU', '', 'Ладно! Пойдем отыщем этого могильного ловчего.', 38134),
(200833, 'ruRU', '', 'Эта штука заперла меня здесь! Я не смогла справиться со всеми этими мертвецами. Не знаю, сколько времени прошло, но с меня хватит! Освободи меня и помоги справиться с ней!', 38134),
(200830, 'ruRU', 'Благодарю тебя! Давай найдем Энофию.', '', 38134),
(200829, 'ruRU', 'Все пошло не так. Я хотел спасти Энофию и сам попал в плен. Пожалуйста, помоги мне! И ей тоже!', '', 38134),
(200826, 'ruRU', '', 'Они близко! Пощады не давать.', 38134),
(200825, 'ruRU', '', 'Пилигрим Утробы! Я видела, как ты бродишь по этим залам.\n\nУвы, я была самоуверенна и попала в плен. Но мне нужно выполнить задание. У одного из высокопоставленных слуг Тюремщика есть ценные сведения, которые мне нужно добыть.', 38134),
(200821, 'ruRU', 'Уходи. \n\nЯ побежден.\n\nБез брони я скоро погибну.', '', 38134),
(200818, 'ruRU', 'Это существо еще близко. Расчленим его, будет знать.', '', 38134),
(200817, 'ruRU', 'Здравствуй! Или благословляю тебя... Или любое другое пафосное приветствие, которое ты ожидаешь услышать.\n\nЯ погнался за... ну, назовем это экспериментом. Оно оказалось умнее, чем я задумывал. Так я и попал в эту переделку.\n\nКак насчет того, чтобы освободить меня и отправиться со мной на поиски того, что я здесь потерял? Или по крайней мере его самых нужных частей.', '', 38134),
(200814, 'ruRU', 'Надо найти мой клинок. Только тогда я смогу вернуться.', '', 38134),
(200813, 'ruRU', 'Опять кто-то пришел дразнить меня. Или нет? Ты не из них?\n\nЯ уж думал, что обречен провести целую вечность в цепях среди этих стен.\n\nПрошу, помоги возвратить мой клинок. Я хочу вернуть себе хоть какую-то честь.', '', 38134),
(200810, 'ruRU', 'Теперь пойдем, найдем мой жезл. Хотя он еще не мой. Придется слегка прибегнуть к насилию, чтобы получить его.', '', 38134),
(200809, 'ruRU', 'А, пилигрим Утробы. Кажется, этим незавидным положением я обязан своей жажде силы.\n\nПожалуйста, прояви великодушие, которым славятся... такие, как ты, и выпусти меня.\n\nИ кстати, у меня еще могут быть кое с кем небольшие счеты.', '', 38134),
(200803, 'ruRU', 'Я в ловушке!\n\nОсвободи меня, коротышка!\n\nНадо вернуться к хозяину.', '', 38134),
(200799, 'ruRU', 'Здесь лежит куча запчастей. Кажется, они могут быть от чего-то важного.', '', 38134),
(200796, 'ruRU', 'Спасибо. Чтобы снять эти последние цепи, потребуется ключ охранника, который меня здесь заточил.', '', 38134),
(200795, 'ruRU', 'Эти твари заковали меня в цепи и оставили здесь. Прошу, освободи меня!', '', 38134),
(200792, 'ruRU', '', 'Спасибо. Давай отыщем мой меч.', 38134),
(200791, 'ruRU', '', 'Ты пилигрим Утробы? Они забрали мой фамильный меч. Прошу, помоги его вернуть.', 38134),
(200780, 'ruRU', 'Я этим займусь.', '', 38134),
(200778, 'ruRU', 'Я готов к испытанию!', '', 38134),
(200776, 'ruRU', 'Ха-ха! Вот это круто!', '', 38134),
(200773, 'ruRU', 'Э-э... Это я сделал?', '', 38134),
(200770, 'ruRU', 'Благодарю тебя. Поганище должно быть где-то здесь.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200769, 'ruRU', 'Моего господина убили внизу, а я остался в одиночестве, покрыв себя позором. Помоги мне закончить его дело.', '', 38134),
(200766, 'ruRU', '', 'Пойдем убьем это чудовище.', 38134),
(200765, 'ruRU', '', 'Мое копье сломалось в бою с одним из здешних чудовищ. Помоги мне убить его, чтобы я могла забрать себе его оружие!', 38134),
(200764, 'ruRU', 'Раньше я мог потягаться с лучшими, но с тех пор утратил все свои знания. У меня будто украли воспоминания. Пожалуйста, помоги мне их вернуть.', '', 38134),
(200763, 'ruRU', 'Давай поищем камень воспоминаний.', '', 38134),
(200760, 'ruRU', 'Айелет жаждет анимы! Убей моего мучителя, который отобрал у меня аниму. Верни ее мне.', '', 38134),
(200759, 'ruRU', 'Давай вернем мою аниму.', '', 38134),
(200756, 'ruRU', '', 'Прошу, освободи меня! Я столько провела в этих оковах, что уже потеряла счет времени.', 38134),
(200752, 'ruRU', 'Нужно поскорее найти мой камень грехов! Столько душ еще не сломлено!', '', 38134),
(200751, 'ruRU', 'А где же мой камень грехов?', '', 38134),
(200746, 'ruRU', 'Отлично. Давай найдем ту горгулью и поквитаемся с ней.', '', 38134),
(200745, 'ruRU', 'Меня похитили! Меня сцапала одичавшая горгулья. Выпусти меня! Освободи, чтобы я мог ей отомстить!', '', 38134),
(200742, 'ruRU', 'Битва за Арденвельд еще не закончена.', '', 38134),
(200741, 'ruRU', 'Пошли искать лопату!', '', 38134),
(200740, 'ruRU', 'У меня стащили лопату! Вот бы мне ее кто вернул.', '', 38134),
(200737, 'ruRU', '', 'Давай отыщем мои воспоминания. Камень воспоминаний, где они хранятся, должен быть где-то здесь.', 38134),
(200735, 'ruRU', '', 'Кто ты? Нет, кто я? Я ничего не могу вспомнить. Нужно найти мой камень воспоминаний, чтобы восстановить их.', 38134),
(200733, 'ruRU', '<Выбрать пожелания.>', '', 38134),
(200731, 'ruRU', 'Задумайся над уроками этого испытания. Пусть они помогают тебе в будущих попытках.', '', 38134),
(200728, 'ruRU', 'Ему необходимо присутствовать на приеме при Пепельном дворе, потому что _______', '', 38134),
(200726, 'ruRU', 'Прошу освободить ________ с поста у ворот.', '', 38134),
(200701, 'ruRU', 'Надеюсь, наши гости не такие разборчивые в еде, как я.', '', 38134),
(200700, 'ruRU', 'Во всяком случае, эти грибы должны быть безопаснее, чем те из Малдраксуса.', '', 38134),
(200699, 'ruRU', 'Слушаюсь, босс.', '', 38134),
(200698, 'ruRU', 'Официант, немедленно отнеси эти... деликатесы... на столы.', '', 38134),
(200697, 'ruRU', 'Отвратительно, есть невозможно, жутко пахнет...', '', 38134),
(200696, 'ruRU', 'Привереда Стефан перебирает грибы, которые вы ему принесли.', '', 38134),
(200695, 'ruRU', 'Должно быть, посланник Бастиона говорил о тебе.', '', 38134),
(200694, 'ruRU', 'Снаряжение еще не готово. Приходи, когда что-то создашь!', '', 38134),
(200693, 'ruRU', 'Все твое снаряжение здесь!\n\nЧто ты выбираешь?', '', 38134),
(200692, 'ruRU', 'Я знаю, где твоя армия, Дрейвен.', '', 38134),
(200691, 'ruRU', 'Пилигрим! Вы с эльфом крови должны уничтожить кристаллы, вытягивающие аниму. А я присмотрю за небом.', '', 38134),
(200690, 'ruRU', 'Кель\'тас сигналит нам снизу. Нужно приземлиться!', '', 38134),
(200689, 'ruRU', '', 'Мой друг... Я скорблю о тебе и своей роли в твоей гибели.\n\nНадеюсь, это не конец.', 38134),
(200688, 'ruRU', 'Моя любимая была права, когда решила спасти тебя. Я скорблю о плодах ее решения. \n\nСон уже не будет прежним.', '', 38134),
(200687, 'ruRU', '', 'Матушка, мне так тебя не хватает. У нашего рода еще есть надежда, но без тебя нам трудно.\n\nУкажи мне путь.', 38134),
(200686, 'ruRU', 'Ты – мать, которая шла со мной путем сна. Ты многому меня научила.\n\nТы нужна миру.', '', 38134),
(200684, 'ruRU', '<У вас есть все необходимое для того, чтобы подделать разрешение Камнелба.\n\nОсталось выбрать, что именно вы хотите написать.>', '', 38134),
(200681, 'ruRU', 'Это я-то \"дятел\"?! Великий Миллхаус Манашторм никогда никого не дятил!', '', 38134),
(200680, 'ruRU', '', 'Эй! Больно, дятел ты криворукий!', 38134),
(200679, 'ruRU', '', 'А ты не лезь под руку, Миллхаус!', 38134),
(200678, 'ruRU', 'Ай! Миллифисент! Смотри, куда бьешь!', '', 38134),
(200677, 'ruRU', 'Милли, ты у меня лучше всех! Давай больше не ссориться... пока что...', '', 38134),
(200675, 'ruRU', '', 'Тебе всегда удается быть в гуще событий, не так ли?', 38134),
(200674, 'ruRU', 'Я все вынесу... ради тебя...', '', 38134),
(200671, 'ruRU', '', 'Буря утихла... пока что.', 38134),
(200669, 'ruRU', '', 'Не только мы видели этот кошмар. Друсты уже подбираются к нам!', 38134),
(200667, 'ruRU', '', 'Эй, слушайте все!', 38134),
(200658, 'ruRU', '', 'Привет, $n!\n\nИнтересуешься путешествиями во времени? Боюсь, тебе стоит получше узнать настоящее, прежде чем заглядывать в прошлое.\n\nДавай я отправлю тебя туда, как только ты пройдешься по городу и познакомишься с королем!', 38134),
(200657, 'ruRU', '', 'О-о-о! Так тебе не все равно!', 38134),
(200656, 'ruRU', 'Кошмары никак не отступают! Попробуй разобраться с ними... напрямую.', '', 38134),
(200647, 'ruRU', '', 'Прохвост!', 38134),
(200646, 'ruRU', 'Ну понимаешь, я... Так, минутку! Ты же только что созналась в том же самом! Лицемерка!', '', 38134),
(200645, 'ruRU', 'Но теперь, ох... мне снова нужно мое моджо!', '', 38134),
(200644, 'ruRU', 'Например, вот эти двое, безумные гномы. Им хотелось силы, и я был рад услужить.', '', 38134),
(200643, 'ruRU', 'Не только тролли просят старину Бвонсамди помочь. Все заключают сделки с лоа смерти!', '', 38134),
(200641, 'ruRU', 'Моя власть необорима! Ты будешь служить мне!', '', 38134),
(200640, 'ruRU', 'Архонт милостивый! Он из медальона в короне. Быстрее, пилигрим!', '', 38134),
(200639, 'ruRU', 'Хорошо, мой верный слуга. Я восстану вновь.', '', 38134),
(200638, 'ruRU', 'Откуда этот голос?', '', 38134),
(200637, 'ruRU', 'Да... Усиль корону.', '', 38134),
(200621, 'ruRU', 'Нужно рассказать дроману. Между семенами и хранителями нечасто возникает такая связь!', '', 38134),
(200620, 'ruRU', 'Это что-то новое. Наши истинные враги... Друсты.', '', 38134),
(200618, 'ruRU', '', 'Вираз безрассуден, но тут он прав. Нам нужно пополнить ряды.', 38134),
(200616, 'ruRU', 'С чьей проекцией ты сразишься?', '', 38134),
(200615, 'ruRU', '', 'Неплохо! Для бескрылого создания, конечно же.', 38134),
(200614, 'ruRU', '', 'Претенденты учатся в маневрировать воздухе. Угонишься за ними?', 38134),
(200611, 'ruRU', 'Мы постоянно тренируемся. У нас высокие требования.', '', 38134),
(200610, 'ruRU', '', 'Кошмары терзают беспокойную душу. Творец Снов просит о помощи.', 38134),
(200608, 'ruRU', 'Кажется, я знаю, где ее взять.', '', 38134),
(200607, 'ruRU', 'Нет... Кошмары не исчезают. Нужно больше анимы.', '', 38134),
(200606, 'ruRU', '', 'Сработало?', 38134),
(200605, 'ruRU', 'Надеюсь, этого хватит...', '', 38134),
(200603, 'ruRU', '', 'Престол Примаса осадили! Мы должны защитить его!', 38134),
(200601, 'ruRU', '', 'Испепелитель! Пилигрим! Сюда!', 38134),
(200599, 'ruRU', 'Конечно! Прогло заготовил аниму как раз для подобного случая.\n\nДержи. Береги себя, и если что-нибудь понадобится, помни, что Прогло – одна из самых отважных душ в Арденвельде!', '', 38134),
(200597, 'ruRU', 'А! Что это такое? Четыре сприггана в обличье... как вы там себя называете?\n\nКакой-то монстр из Бесплотного мира? \n\nКто бы вы ни были... просто уходите! Прошу!', '', 38134),
(200596, 'ruRU', 'Здравствуй! Анима нужна? Прогло откладывал ее на случай, если она вдруг понадобится диким семенам.\n\nЭти гадкие спригганы и друсты ее не получат!\n\nПрогло – один из храбрейших обитателей Арденвельда. Он все видит!', '', 38134),
(200593, 'ruRU', 'Мы подготовим все для ритуала!', '', 38134),
(200592, 'ruRU', 'Генерал Дрейвен близко!', '', 38134),
(200591, 'ruRU', 'Говори быстрее. У нас много дел.', '', 38134),
(200590, 'ruRU', '', 'Крексус был прав. Ключ к Престолу Примаса у нас!', 38134),
(200587, 'ruRU', 'Тогда мы его выманим. Но сперва надо очистить небо от врагов.', '', 38134),
(200586, 'ruRU', 'Он прячется под землей, как жалкий трус.', '', 38134),
(200584, 'ruRU', '', 'Прими эту часть моей силы, смертное создание. Она позволит сделать облик твоей души... менее чужеродным для духа.', 38134),
(200577, 'ruRU', 'Надеюсь, скоро мы снова встретимся. А пока не сходи с пути.', '', 38134),
(200576, 'ruRU', 'Копье Каллиопы снова у нас, и все благодаря тебе.', '', 38134),
(200575, 'ruRU', '', 'Так намного лучше! Я останусь следить за друстами, а ты лети назад, к Алиот!', 38134),
(200574, 'ruRU', 'Отлично, $n, отлично.', '', 38134),
(200573, 'ruRU', '', 'Наши запасы на исходе, но я сделаю, что смогу.', 38134),
(200572, 'ruRU', '', 'Путь Перерождения ждет.\n\nТы хочешь пройти испытание?', 38134),
(200571, 'ruRU', 'Прошу, Алиот. Помоги нам избавить дух от кошмаров!', '', 38134),
(200540, 'ruRU', 'Угощайся, пилигрим.\n\nВсе гости полюбили блюда из Бастиона.\n\nЗемлерои тоже оценили. Помоги записать рецепты. Загляни в мою книгу!', '', 38134),
(200533, 'ruRU', 'СОБРАНИЕ РЕЦЕПТОВ РАСПОРЯДИТЕЛЯ\n\n(Записано несколькими полуграмотными землероями.)\n\nОглавление:', '', 38134),
(200527, 'ruRU', '', 'Совсем скоро остальные дома тоже узнают о клинке. Готовься к битве.', 38134),
(200526, 'ruRU', '', 'Примас был легендарным кузнецом. Если это – ключ к его хранилищам, нам достанется мощнейший арсенал.', 38134),
(200525, 'ruRU', '', 'Кажется, наш путь преграждают ворота. Я перенесу тебя на другую сторону, только скажи.', 38134),
(200508, 'ruRU', '', 'Престол Примаса не открывался с тех пор, как он пропал. Многие пытались туда проникнуть.', 38134),
(200506, 'ruRU', 'Не будем задерживаться здесь. Тебе ведь нужно доставить дикое семя в Спящую лощину?', '', 38134),
(200505, 'ruRU', 'Нельзя позволить им захватить лужайку Сорин.', '', 38134),
(200491, 'ruRU', '', 'Ты здесь! Дикое семя уже начало беспокоиться.', 38134),
(200469, 'ruRU', 'Садись на оленя и езжай с нами, $p.', '', 38134),
(200466, 'ruRU', 'Гости довольны! Я хороший официант!', '', 38134),
(200465, 'ruRU', 'Не бойся, птичка. Я все сделаю!', '', 38134),
(200464, 'ruRU', 'Где все официанты?', 'Где все официанты?', 38134),
(200463, 'ruRU', 'Заказ готов!', '', 38134),
(200458, 'ruRU', '$n! Копье у тебя, а оставшиеся ученые в безопасности. Думаю, стоит принять предложение и покинуть это место. Встретимся в Обители Героев.', '', 38134),
(200455, 'ruRU', 'Страдай вечно, $p.', '', 38134),
(200451, 'ruRU', '', 'Чудесно! Местные мотыльки могли бы у тебя поучиться.', 38134),
(200450, 'ruRU', '', 'Нам надо собрать еще анимашишек, но эти земли захватили друсты! Чары помогут тебе пробраться мимо них.', 38134),
(200449, 'ruRU', '', 'Говорят, из тебя вышел прекрасный долинный крикун.\n\n<Лунная Ягода смеется.>\n\nБуду иметь тебя в виду на случай, если понадобится симпатичная птичка.\n\nНо хватит об этом, у меня отличные новости! Королева собирается в Тирна Ваал! В этой роще по-прежнему будут ухаживать за дикими семенами и давать приют душам. Скоро она прибудет сюда, чтобы благословить сердце и наполнить все новой энергией.\n\nНаверное, тогда у тебя будет возможность поговорить с ней.', 38134),
(200448, 'ruRU', '', 'Значит, тебе удалось заставить Коренфа станцевать?\n\n<Лунная Ягода смеется.>\n\nЯ знала, что ты мне понравишься, $n. Буду иметь тебя в виду на случай, если нужно будет кому-то еще что-то поручить.\n\nУ меня хорошие новости! Королева собирается в Тирна Ваал! В этой роще по-прежнему будут ухаживать за дикими семенами и давать приют душам. Скоро она прибудет сюда, чтобы благословить сердце и наполнить все новой энергией.\n\nНаверное, тогда у тебя будет возможность поговорить с ней.', 38134),
(200446, 'ruRU', 'Слава и власть. Ренатал будет доволен. $n, проследи, чтобы наш друг получил все, что ему нужно.', '', 38134),
(200445, 'ruRU', 'Корона для вашего принца. Фокусирует силу медальонов, о которых вы говорили.', '', 38134),
(200442, 'ruRU', '', 'Не думайте, что победили. Наша следующая встреча будет последней.', 38134),
(200441, 'ruRU', '', 'В атаку!', 38134),
(200440, 'ruRU', '', 'Недостойные кирии. Я – идеал Сомнения, и ваше постоянное вмешательство мне надоело!', 38134),
(200439, 'ruRU', '', 'Кажется, у тебя гости, идеал. Разберись с ними.', 38134),
(200438, 'ruRU', '', 'Я вижу свое будущее. Я сражу Архонта своим мечом, и Бастион будет моим!', 38134),
(200437, 'ruRU', 'А-а-а!', '', 38134),
(200436, 'ruRU', '', 'Ты был полезной пешкой, Утер. Хоть Девия и дорожила тобой... мне ты больше не нужен.', 38134),
(200435, 'ruRU', '', 'Возьми его силу, Лисония. Используй его для превращения.', 38134),
(200431, 'ruRU', 'Я вижу, как ты стоишь на краю попасти, смертная душа. Чувствуешь, как Утроба вытягивает из тебя душу?\n\nЯ восхищаюсь твоим бесстрашием и упорством. Мне хочется испытать их.\n\nЕсли ты не хуже, чем о тебе говорят, то прими мой вызов: победи меня, и можешь снова войти в Бастион. Если же уступишь, я еще раз заберу твою аниму.\n\nВыбирай.', 'Я вижу, как ты стоишь на краю попасти, смертная душа. Чувствуешь, как Утроба вытягивает из тебя душу?\n\nЯ восхищаюсь твоим бесстрашием и упорством. Мне хочется испытать их.\n\nЕсли ты не хуже, чем о тебе говорят, то прими мой вызов: победи меня, и можешь снова войти в Бастион. Если же уступишь, я еще раз заберу твою аниму.\n\nВыбирай.', 38134),
(200429, 'ruRU', 'Это была лишь одна битва. Боюсь, нас ждут еще многие.', '', 38134),
(200428, 'ruRU', 'Прекрасно! А теперь отступаем!', '', 38134),
(200427, 'ruRU', 'Дикая Охота, вперед!', '', 38134),
(200422, 'ruRU', '', 'Здравствуй! У меня хорошие новости!', 38134),
(200421, 'ruRU', 'Поспешите! К визиту Королевы все должно блестеть!', '', 38134),
(200420, 'ruRU', '', 'Помогите! Прошу, кто-нибудь!', 38134),
(200418, 'ruRU', '', 'Нелвина хнычет.', 38134),
(200416, 'ruRU', 'Иди по дороге и скоро дойдешь до Тирна Ваала! Увидимся там!', '', 38134),
(200396, 'ruRU', 'Образцовая резня! Аплодисменты выжившим!', '', 38134),
(200395, 'ruRU', 'Ха! Неплохо для кучки новичков!', '', 38134),
(200391, 'ruRU', 'Прекрасная демонстрация! Небо готово принять тебя.', '', 38134),
(200390, 'ruRU', 'Прежде чем вступить в ряды перерожденных, стоит попрактиковаться в полетах. Покажи, что умеешь!', '', 38134),
(200385, 'ruRU', 'Полагаю, нам лучше поспешить.', '', 38134),
(200384, 'ruRU', 'Начнем по твоему сигналу, пилигрим.', '', 38134),
(200367, 'ruRU', '', 'Постарайся не стать одной из них.', 38134),
(200366, 'ruRU', '', 'Разыскивая копье, мы столкнулись с множеством неприятностей.', 38134),
(200363, 'ruRU', '<Лошадь ржет, бросая вам вызов!>', '<Лошадь ржет, бросая вам вызов!>', 38134),
(200362, 'ruRU', '<Конь смотрит на вас, прижав уши. Он угрожающе топчет копытами землю.>', '<Конь смотрит на вас, прижав уши. Он угрожающе топчет копытами землю.>', 38134),
(200356, 'ruRU', 'Сюда! На нас напали обезумевшие от голода животные. Души в опасности!', '', 38134),
(200353, 'ruRU', 'А эта процедурка будет просто взрывной!', '', 38134),
(200343, 'ruRU', 'Раскольники захватили мою кузню. Помогите прогнать их, и я помогу вам.', '', 38134),
(200342, 'ruRU', '', 'Я поддерживаю Ренатала в его начинаниях.', 38134),
(200341, 'ruRU', '', 'У Лорана есть свои недостатки, но зеркало он починит.', 38134),
(200340, 'ruRU', '', 'Здесь обстановка гораздо лучше.', 38134),
(200336, 'ruRU', 'Ты замещаешь леди Лунную Ягоду? Что ж, надеюсь, ты хорошо управляешься с голодными обитателями леса!', '', 38134),
(200334, 'ruRU', 'Не останавливайтесь!', '', 38134),
(200333, 'ruRU', 'Легкое Перо! Нам нужна помощь!', '', 38134),
(200331, 'ruRU', 'Этот шум... невыносим!', '', 38134),
(200330, 'ruRU', '', 'Какое... безумие!', 38134),
(200329, 'ruRU', 'Гораздо легче сохранять рассудок, когда Каменный легион не следит за каждым твоим шагом.', '', 38134),
(200328, 'ruRU', 'Мрачная работа. Но пока засуха не кончится, кто-то должен ее делать.', '', 38134),
(200326, 'ruRU', 'В каждом семени дремлет дух природы, который однажды проснется и вернется в свой мир.', '', 38134),
(200323, 'ruRU', 'Идем. Сердцевинная роща недалеко.', '', 38134),
(200322, 'ruRU', 'Королеве приходится решать, кто из духов выживет... а кто зачахнет навсегда.', '', 38134),
(200287, 'ruRU', 'Труби в рог, $p!', '', 38134),
(200286, 'ruRU', 'Скорее! Подними рог!', '', 38134),
(200275, 'ruRU', 'Попробуй выманить птицу из гнезда!', '', 38134),
(200272, 'ruRU', '$p! Быстрее возьми рог!', '', 38134),
(200271, 'ruRU', 'Нет! Рог!', '', 38134),
(200270, 'ruRU', 'За Королеву и рощу!', '', 38134),
(200267, 'ruRU', 'Семя в безопасности. Я унесу его отсюда. Спасибо тебе!', '', 38134),
(200266, 'ruRU', 'Да у тебя талант! Претендентам стоило бы у тебя поучиться.', '', 38134),
(200265, 'ruRU', 'Чтобы научиться летать, надо научиться падать. Попробуй вместе с этими претендентами!', '', 38134),
(200262, 'ruRU', 'Брокер Та\'саран заведует стойлами в Зале Диковинок.', 'Брокер Та\'саран заведует стойлами в Зале Диковинок.', 38134),
(200259, 'ruRU', 'Почтовый ящик располагается на востоке Кольца Судеб.', 'Почтовый ящик располагается на востоке Кольца Судеб.', 38134),
(200257, 'ruRU', 'Таверну можно найти в восточной части Кольца Судеб.', 'Таверну можно найти в восточной части Кольца Судеб.', 38134),
(200255, 'ruRU', 'Ты найдешь распорядителя полетов в верхней части Кольца Перенаправления. Воспользуйся порталом в Кольце Судеб.', 'Ты найдешь распорядителя полетов в верхней части Кольца Перенаправления. Воспользуйся порталом в Кольце Судеб.', 38134),
(200253, 'ruRU', 'Брокер Та\'визаж сможет изменить твою внешность. Найди его в Зале Диковинок.', 'Брокер Та\'визаж может изменить твою внешность. Найди его в Зале Диковинок.', 38134),
(200252, 'ruRU', '$p, мы ударим первыми. Остальные, атакуйте, когда я протрублю в рог!', '', 38134),
(200251, 'ruRU', 'Мы искупим свою вину, верховный автоматург. Клянусь.', '', 38134),
(200250, 'ruRU', 'Брокерский банк в Зале Хранения предоставит тебе свои услуги.', 'Брокерский банк в Зале Хранения предоставит тебе свои услуги.', 38134),
(200248, 'ruRU', 'Брокеры предлагают услуги трансмогрификации в Зале Диковинок.', 'Брокеры предлагают услуги трансмогрификации в Зале Диковинок.', 38134),
(200246, 'ruRU', 'Смотрителей и других важных гостей можно найти в зале собраний Анклава.', 'Смотрителей и других важных гостей можно найти в зале собраний Анклава.', 38134),
(200244, 'ruRU', '', 'Что ж, в наихудшем случае меня спасут крылья. Я тебе верю.', 38134),
(200243, 'ruRU', '', 'То есть мы просто... прыгнем туда? В бездну, полную проклятых душ без надежды на свободу и искупление?', 38134),
(200239, 'ruRU', 'У них огромное чудище. Оно убило много наших.', '', 38134),
(200238, 'ruRU', 'Сегодня мы воздаем почести Чаддиусу!\n\nОн чистюля. Вечно моется.\n\nТы найдешь его у храма Чистоты.\n\nДашь спелый чистоплод?', '', 38134),
(200233, 'ruRU', 'Беги, Коренф!', '', 38134),
(200232, 'ruRU', 'Ой-ой-ой... Гм... привет?', '', 38134),
(200231, 'ruRU', 'Продолжай! А я проберусь в гнездо...', '', 38134),
(200230, 'ruRU', 'Значит, мы убьем его.', '', 38134),
(200227, 'ruRU', 'А знаешь, у меня есть кое-что как раз на такой случай.\n\nСтой смирно. Думаю, больно не будет.', '', 38134),
(200225, 'ruRU', 'Спасибо тебе за помощь!', 'Спасибо тебе за помощь!', 38134),
(200224, 'ruRU', 'Нет времени на отдых!', 'Нет времени на отдых!', 38134),
(200223, 'ruRU', 'Они пытались сделать меня одним из них...', 'Они пытались сделать меня одной из них...', 38134),
(200222, 'ruRU', 'С каждой битвой их все больше...', 'С каждой битвой их все больше...', 38134),
(200221, 'ruRU', 'Бой продолжается. За Арденвельд!', 'Бой продолжается. За Арденвельд!', 38134),
(200220, 'ruRU', 'Мы не подведем Арденвельд!', 'Мы не подведем Арденвельд!', 38134),
(200219, 'ruRU', '', 'Мой долг свят.', 38134),
(200215, 'ruRU', '', 'Я возьму Сострадание с собой. Встретимся в Орибосе. Мне нужно подготовиться. Немногие кирии проделывают такой путь...', 38134),
(200207, 'ruRU', 'Спасибо всем, кто посетил Пепельный двор!', '', 38134),
(200206, 'ruRU', '', 'А-р-р! Идем, Утер. Наша цель важнее этих глупцов.', 38134),
(200205, 'ruRU', 'Сегодня мы воздаем почести Бернсиосу!\n\nОн любит животных. Особенно Калу.\n\nТы найдешь его в Саду отдохновения.\n\nДашь спелый чистоплод?', '', 38134),
(200204, 'ruRU', '', 'Брось игрушку, дорогая. У нас много работы.', 38134),
(200203, 'ruRU', 'Ты навязываешь всем свои взгляды... Свои ошибки... Я потратил тысячи лет, чтобы стать воплощением своих принципов...', '', 38134),
(200202, 'ruRU', 'Госпожа... ничего нет.', '', 38134),
(200201, 'ruRU', '', 'Покажи нам свою гордыню, идеал!', 38134),
(200200, 'ruRU', '', 'Слишком поздно, пилигрим. Скоро ваш драгоценный идеал поддастся своей скрытой гордыне.', 38134),
(200199, 'ruRU', 'Конфликты? Во что ты меня втянул на этот раз, Дрейвен?', '', 38134),
(200198, 'ruRU', 'Я знаю, что делать с такими негодяями!', '', 38134),
(200197, 'ruRU', 'Как грубо!', '', 38134),
(200196, 'ruRU', 'Это научит тебя хорошим манерам!', '', 38134),
(200195, 'ruRU', 'Встретимся у Вечной кузни. Твое присутствие должно предотвратить возможные... конфликты.', '', 38134),
(200193, 'ruRU', 'Только поскорее. У меня важная встреча.', '', 38134),
(200189, 'ruRU', 'Поспеши! Многим нужна помощь!', '', 38134),
(200188, 'ruRU', '', 'Не важно, Утер. У нас есть все, что нужно.', 38134),
(200187, 'ruRU', 'Экстракторы вышли из строя. Мы теряем мощность!', '', 38134),
(200185, 'ruRU', 'А вот и друст. Очень... крупный друст.', '', 38134),
(200184, 'ruRU', '', 'Что это?!', 38134),
(200183, 'ruRU', '', 'Правда оригинальное решение? Теперь они еще подумают, прежде чем снова сюда заявляться!', 38134),
(200182, 'ruRU', '', 'Это нужно прекратить! Может, отплатим спригганам их же монетой?', 38134),
(200174, 'ruRU', 'При Пепельном дворе нынче многое происходит впервые! Это воодушевляет!', '', 38134),
(200173, 'ruRU', 'Я раньше никогда от тебя не слышал ничего подобного, дружище.', '', 38134),
(200172, 'ruRU', 'Мне тоже, Темель. Я с удовольствием понаблюдаю за ритуалами Обвинительницы.', '', 38134),
(200171, 'ruRU', '', 'Стоп, ты ТОЖЕ заключил сделку с Бвонсамди? Я знала, что ты не слишком чист на руку, Миллхаус, но это уже чересчур!', 38134),
(200170, 'ruRU', 'А это еще что? Дряхлый лоа уже прислал за долгами?! Но у меня еще не было времени сделать свой шедевр из его моджо!', '', 38134),
(200169, 'ruRU', 'Конечно! Только последний лоск наведу...', '', 38134),
(200168, 'ruRU', 'Гости скоро прибудут. Полагаю, вы с Темелем завершили все приготовления?', '', 38134),
(200167, 'ruRU', '$p, ты вновь приходишь именно тогда, когда мне нужна твоя помощь.', '', 38134),
(200165, 'ruRU', 'Самый новый – и самый лучший – двор в Ревендрете!', '', 38134),
(200163, 'ruRU', '', 'Мы сдержим их! Открывай дверь!', 38134),
(200157, 'ruRU', 'Стой! Дальше слишком опасно!', '', 38134),
(200156, 'ruRU', 'Лунная Ягода... прекраснее всех... Эта затея курам на смех!', '', 38134),
(200155, 'ruRU', 'Ну... раз ты настаиваешь...', '', 38134),
(200151, 'ruRU', 'А-а!', '', 38134),
(200150, 'ruRU', '', 'Довольно глупых игр. Утер, заканчивай уже наконец!', 38134),
(200148, 'ruRU', 'Когда-то ты была верна Архонту и нашим традициям. Отринь темные мысли. Ты еще можешь спасти и себя, и своих соратников!', '', 38134),
(200147, 'ruRU', '', 'Мне нужно найти идеала. Уверена, вы с Состраданием прекрасно справитесь.', 38134),
(200145, 'ruRU', '', 'Опять твой идеализм. Моя воля сломит тебя.', 38134),
(200144, 'ruRU', 'Лисония, твоя надменность тебя погубит. Ты должна вернуться на путь!', '', 38134),
(200143, 'ruRU', 'А-а! Как же кирии выносят эту абсурдную яркость?', '', 38134),
(200142, 'ruRU', 'Бастион... Даже мне не сбежать от своих грехов.', '', 38134),
(200133, 'ruRU', '', 'Хвала Архонту, подкрепление! Мои солдаты ранены, и мне едва удается поддерживать в них жизнь.', 38134),
(200131, 'ruRU', '', 'Скорее, нужно найти моего друга Пелагия в Обители претендентов.', 38134),
(200130, 'ruRU', '', 'Смотри! Послушники храма Смирения окружены, но Войта еще держится.', 38134),
(200129, 'ruRU', '', 'Бедные претенденты... Раскольники принуждают их поддаться гордыне, чтобы пополнить свою армию.', 38134),
(200127, 'ruRU', 'Вас ждут вечные оковы!', '', 38134),
(200126, 'ruRU', 'Бороться бесполезно, смертные. Вам не уйти от судьбы!', '', 38134),
(200124, 'ruRU', 'Стой... это благодаря тебе спасены Сверкающие водопады? Поможешь нам еще раз?', '', 38134),
(200114, 'ruRU', 'Гипокос, а ты можешь починить какого-нибудь претора?', '', 38134),
(200113, 'ruRU', 'Оковы разбиты! Уходи, пока не вернулась стража!', '', 38134),
(200111, 'ruRU', '', 'Дикая Охота защищает Арденвельд именем Королевы. Помогая им, ты помогаешь и ей!', 38134),
(200110, 'ruRU', '', 'Неподалеку рыцари Черного Клинка. Они защитят тебя!', 38134),
(200109, 'ruRU', 'Найди пещеру на другой стороне пропасти! Там наши союзники защитят тебя!', '', 38134),
(200108, 'ruRU', '', 'Ох, если бы Королева могла спасти всех...', 38134),
(200107, 'ruRU', '', 'Каждое семя бесценно. Мы столько их потеряли...', 38134),
(200105, 'ruRU', '', 'Снова кошмары! Займись ими, а я успокою семя!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200104, 'ruRU', '', 'Кажется, их привлекла музыка...', 38134),
(200100, 'ruRU', '', 'Зачем же ты здесь, $n?', 38134),
(200098, 'ruRU', '', 'Я сделаю все, что в моих силах, чтобы помочь тебе с ней встретиться.', 38134),
(200097, 'ruRU', '', 'Спасибо, $p. Жаль, что тебе не удалось поговорить с Королевой.', 38134),
(200096, 'ruRU', 'Сюда, подмастерье!', '', 38134),
(200095, 'ruRU', '', 'Спригганы! Берегись!', 38134),
(200094, 'ruRU', '', 'Кошмар возвращается... Я сыграю на арфе...', 38134),
(200093, 'ruRU', '', 'Эта дорога ведет к первому тайнику. Удачи!', 38134),
(200092, 'ruRU', 'Нет! Сила Куратора едва не стала моей...', '', 38134),
(200087, 'ruRU', 'Я буду ждать вас в Спящей лощине. Удачного пути!', '', 38134),
(200085, 'ruRU', 'Ния, возьми мою арфу. Ее музыка успокоит семя.', '', 38134),
(200084, 'ruRU', 'Этому семени будет лучше в Спящей лощине. Возможно, Ния составит тебе компанию.', '', 38134),
(200083, 'ruRU', 'Коренф вечно попадает в передряги. Посмотрим, что стряслось в этот раз.', '', 38134),
(200082, 'ruRU', 'Если я тебе нужен, иди сюда.', '', 38134),
(200081, 'ruRU', 'Грашаал, тебя разбудили на замену мне?', '', 38134),
(200080, 'ruRU', 'Надменный щенок! Мы не сдаемся. Мы следуем приказам!', '', 38134),
(200079, 'ruRU', 'Кааль, если ты мой друг – поверь мне и сдавайся.', '', 38134),
(200078, 'ruRU', '', 'Дрейвен! Не вынуждай меня уничтожить тебя.', 38134),
(200076, 'ruRU', 'Чувствуешь? Грядут перемены. Что-то происходит. Надо действовать быстро!\n\nТри Жнеца и пилигрим Утробы, завладевшие мощью чистой анимы из множества царств Темных Земель, против повелителя смерти, у границ владений которого мы находимся.\n\nВстретим это чудовище вместе!', '', 38134),
(200075, 'ruRU', 'Распорядителям редко выпадает честь стать медиумом, но я – особый случай! Приступим?', '', 38134),
(200074, 'ruRU', 'С тобой я пережил больше увлекательных приключений, чем за последние несколько веков.', '', 38134),
(200073, 'ruRU', '', 'Никогда бы не подумала, что судьба приведет меня сюда. Но вот мы здесь.\n\nИ как мы попадем в Утробу?', 38134),
(200071, 'ruRU', 'Но то, что мы с ним сделали, было не правосудием, а местью.', '', 38134),
(200070, 'ruRU', 'Каждый раз, закрывая глаза, я вижу свою смерть. Я вижу... его... Он стоит передо мной, держа этот проклятый меч.', '', 38134),
(200069, 'ruRU', 'Отведи его в Вечный Дозор. Там он будет ожидать вердикта Архонта.', '', 38134),
(200066, 'ruRU', '', 'Сегодня я многое узнала о тебе.', 38134),
(200065, 'ruRU', 'Я отрекаюсь от идей раскольников. Лучше я забуду прошлое, чем выберу такое будущее.', '', 38134),
(200063, 'ruRU', 'Ты бесчестный трус!', '', 38134),
(200062, 'ruRU', 'Хватит игр! Стража, уничтожьте этих слабаков!', '', 38134),
(200059, 'ruRU', '', 'Гормы плодятся с пугающей скоростью. Убивай их личинок, чтобы сдержать нашествие.', 38134),
(200057, 'ruRU', 'Хочешь, помогу?', 'Хочешь, помогу?', 38134),
(200055, 'ruRU', 'Помочь?', 'Помочь?', 38134),
(200054, 'ruRU', 'Любишь сладкий сок? Я принесу.', 'Любишь сладкий сок? Я принесу.', 38134),
(200052, 'ruRU', 'Могу помочь. Спроси, что хочешь.', 'Могу помочь. Спроси, что хочешь.', 38134),
(200046, 'ruRU', 'Чистоплоды – это хорошо! Ура!', 'Чистоплоды – это хорошо! Ура!', 38134),
(200045, 'ruRU', 'Я рад! Спасибочки тебе!', 'Я рада! Спасибочки тебе!', 38134),
(200044, 'ruRU', 'Ух ты! Какая честь!', 'Ух ты! Какая честь!', 38134),
(200043, 'ruRU', 'Это мне?! Ух ты!', 'Это мне?! Ух ты!', 38134),
(200042, 'ruRU', '', 'Поэтому пока что мы ее заменяем.', 38134),
(200037, 'ruRU', 'С днем распорядителя!\n\nМы чиним. Мы носим. Мы служим. \n\nМы – распорядители!', '', 38134),
(200035, 'ruRU', 'За последнее время здесь случилось столько странного... Не помню, чтобы в Арденвельде когда-нибудь царил такой хаос.', '', 38134),
(200032, 'ruRU', '', 'Странно... Продолжай свое обучение. Встретимся у привратницы.', 38134),
(200031, 'ruRU', '', 'А это что за существо? Будто кирия, но... не вполне.', 38134),
(200028, 'ruRU', 'Глупцы! Против вас все дома Малдраксуса!', '', 38134),
(200027, 'ruRU', 'Ты не победишь, Вираз! Вас слишком мало!', '', 38134),
(200026, 'ruRU', 'Кажется, этого брокера убили некоторое время назад.', 'Кажется, этого брокера убили некоторое время назад.', 38134),
(200025, 'ruRU', 'Спасибо, Андуин. Я буду рад восстановить силы.', '', 38134),
(200024, 'ruRU', 'Тебе сильно досталось, Тралл. Позволь мне исцелить тебя.', '', 38134),
(200014, 'ruRU', '', 'Кала, ты себя очень плохо вела. Извинись.', 38134),
(200013, 'ruRU', '', 'В ближайшие годы я буду учить тебя, Зосима, как ухаживать за этим садом и его обитателями с помощью посоха.', 38134),
(200012, 'ruRU', 'Этот солдат Альянса убил множество твоих соратников. Не пора ли тебе продемонстрировать свою силу?', '', 38134),
(200011, 'ruRU', 'Этот солдат Орды уже давно убивает твоих союзников. Предлагаю напомнить всем о превосходстве Альянса.', '', 38134),
(200010, 'ruRU', 'А ты быстро расправляешься с ворами. Надо будет это запомнить.', '', 38134),
(200009, 'ruRU', 'Похоже, местные жители пострадали от кражи. Уверен, они будут рады твоей помощи.', '', 38134),
(200008, 'ruRU', 'Ты искусно фехтуешь, смертная душа. Возможно, в будущем я предложу тебе... подработку.', '', 38134),
(200007, 'ruRU', 'Вентирские дуэли – способ добыть славу и престиж... а еще – выгоду для картеля.', '', 38134),
(199996, 'ruRU', '', 'Я вернусь, когда ты найдешь в себе силы.', 38134),
(199995, 'ruRU', '', 'Направь всю свою аниму в этот посох, дитя. Это будет испытанием твоей храбрости и веры.', 38134),
(199994, 'ruRU', '', 'Отвага зовет. А, Зосима... Ты пришла за тростью.', 38134),
(199993, 'ruRU', '', 'Иди дальше. Мне нужно успокоить Калу. Она получила назад свои игрушки и расшалилась теперь уже поэтому.', 38134),
(199992, 'ruRU', '', 'Когда доберешься, позвони в колокол. Будем надеяться, Коринна отзовется и скажет, что нам делать.', 38134),
(199984, 'ruRU', 'Келтеш, ты не заметил ничего подозрительного?', 'Келтеш, ты не заметил ничего подозрительного?', 38134),
(199951, 'ruRU', '', 'В Саду отдохновения можно спокойно помедитировать и поразмышлять над выпавшими испытаниями. Так, по крайней мере, было раньше. Я искренне надеюсь, что те времена вернутся.\n\nВеликая Коринна создала этот сад, чтобы мы все могли где-то отдохнуть. Она была одной из первых перерожденных. Именно Коринна впервые приручила существ Бастиона и подарила им спокойствие. Но теперь анимы не хватает, покой утерян, и никто больше не находит здесь отдыха.', 38134),
(199950, 'ruRU', '', 'Но я так и не нашла выход из Утробы. Мне удается переместиться лишь на короткие расстояния. Кажется... отсюда не сбежать.', 38134),
(199948, 'ruRU', '', 'Как же я рада тебя видеть! Одно из моих испытаний претендентки – ухаживать за этим садом. Но получается не очень.\n\nИз-за засухи многие звери посходили с ума. Надо как-то навести тут порядок...\n\nТы мне поможешь?', 38134),
(199905, 'ruRU', 'Аудиенция с Архонтом – редкая честь. Постарайся соответствовать.', '', 38134),
(199904, 'ruRU', 'Идеалы уже собрались и ожидают прибытия Архонта. Советую внимательно слушать, пока к тебе не обратятся.', '', 38134),
(199887, 'ruRU', '', '<Зе\'рали кричит от боли>', 38134),
(199886, 'ruRU', '', 'Ты... не ведаешь... что творишь...', 38134),
(199885, 'ruRU', '', 'Свет... одолеет... тьму...', 38134),
(199879, 'ruRU', '', 'Что ты желаешь узнать?', 38134),
(199877, 'ruRU', 'Значит, ты теперь босс? Тогда держи вот этот ключ.', '', 38134),
(199876, 'ruRU', 'Наша цель в самом центре тюрьмы. Уничтожьте всех на своем пути.', '', 38134),
(199875, 'ruRU', 'Если хочешь что-то сказать, то говори сразу боссу, Эдгар.', '', 38134),
(199873, 'ruRU', '', 'Мне потребуется... некоторое время, чтобы прийти в себя после всего, что случилось в Торгасте. Я не хочу об этом говорить.', 38134),
(199869, 'ruRU', '', '<Корейн всматривается вдаль.>', 38134),
(199867, 'ruRU', 'Если гости будут так любезны последовать за мной, пилигрим нам кое-что покажет.', '', 38134),
(199866, 'ruRU', 'Конечно. Я не против прогуляться.', '', 38134),
(199859, 'ruRU', 'Куда мне отвести наших гостей?', '', 38134),
(199858, 'ruRU', '', 'Стараюсь... И я знаю, что ты поможешь лесу снова вырасти!', 38134),
(199856, 'ruRU', '', 'Весь лес смеется. С тобой не соскучишься, леди Лунная Ягода.', 38134),
(199855, 'ruRU', '', 'Борода... шляпа... Проделки времени... и ночного народца.', 38134),
(199853, 'ruRU', 'Расслабься, $p, и угощайся тем, что осталось на столе.\n\nПрием прошел идеально, и ты можешь отдохнуть и получить награду.', '', 38134),
(199850, 'ruRU', 'Тебе удалось найти лилию?', '', 38134),
(199849, 'ruRU', 'Не хочу казаться занудой, но у нас плотный график.', '', 38134),
(199848, 'ruRU', '', 'Прекрасно, $n. Нашествие под контролем... во всяком случае, пока что.', 38134),
(199847, 'ruRU', '', 'Это место кишит гормами. Надо прекратить нашествие, пока оно не распространилось!', 38134),
(199832, 'ruRU', '', 'Я с радостью помогу. Мне только нужно собраться.', 38134),
(199828, 'ruRU', '', 'Этот розыгрыш надолго запомнится! Надо только... ой! Привет!', 38134),
(199826, 'ruRU', 'Всегда стремись только к победе.', '', 38134),
(199823, 'ruRU', '', 'Они принимают облик злобных теней, скрывающихся в темноте. Не думаю, что они захотят нам помогать.', 38134),
(199821, 'ruRU', '', 'Приветствую, душа из Аргуса! Мы составили программу, чтобы помочь вам привыкнуть к новому этапу существования.', 38134),
(199820, 'ruRU', '', 'Приветствую, душа из Дренора! Мы составили программу, чтобы помочь вам привыкнуть к новому этапу существования.', 38134),
(199818, 'ruRU', '', 'Пара печатей не даст ответа. Путь лишь один, и печать лишь одна.', 38134),
(199816, 'ruRU', '', 'У нее появился могущественный сообщник. Тени шепчут о некоем... Тюремщике.', 38134),
(199815, 'ruRU', '', 'Найдите путь. Развейте туман.', 38134),
(199812, 'ruRU', '', 'Камни и печати помогут вам. Один камень лишний. Он укажет путь.', 38134),
(199811, 'ruRU', '', 'Мне нужно подготовиться. Скоро увидимся!', 38134),
(199809, 'ruRU', '', 'Обойдите все точки, чтобы узнать больше.', 38134),
(199800, 'ruRU', 'Мы отомстим за него здесь и сейчас!', '', 38134),
(199647, 'ruRU', '', 'Это невыносимо сложный выбор, но мы должны сохранить круговорот.', 38134),
(199646, 'ruRU', '', 'Но от засухи Сердце начало угасать. Нужно его усилить... Но нашей анимы едва хватает, чтобы поддерживать рощи.', 38134),
(199645, 'ruRU', '', 'Без Сердца леса Арденвельд погибнет... а другие миры останутся без защиты.', 38134),
(199643, 'ruRU', '', 'Мне многое известно. Что вы хотите узнать?', 38134),
(199639, 'ruRU', '', 'Я не понимаю.\n\nИз какой системы, мира, планеты или иной формы бытия вы родом?', 38134),
(199638, 'ruRU', '', 'Мы понимаем, что жизнь смертных часто прерывается внезапно, и что порой это нелегко принять. После смерти многие начинают сомневаться в сущности своего бытия.\n\nПостарайтесь осознать, что с вами произошло, и смириться с этим. Как только вы это сделаете, мы продолжим наш вводный курс.', 38134),
(199625, 'ruRU', '', 'Превосходно, $n. Теперь у нас будет время укрепить защиту от гормов.', 38134),
(199624, 'ruRU', '', 'На нас надвигается рой прожорливых корнежалов! Возьми туманокрыла и убей как можно больше, пока они не добрались до Сердца леса!', 38134),
(199615, 'ruRU', 'Я сожгу это чудовище жаром тысячи солнц!', '', 38134),
(199613, 'ruRU', 'Найди пленницу и уходи как можно быстрее. Победа будет за нами.', '', 38134),
(199611, 'ruRU', '', 'Арденвельд будто трещит по швам. Сначала одержимые, теперь эти странные монстры в Тирна Ваале...\n\nТебе что-то нужно, $n?', 38134),
(199609, 'ruRU', 'Сюда, грешники! Служите мне!', '', 38134),
(199601, 'ruRU', '', 'Лорд Герн, нам нужна аудиенция у Королевы Зимы.', 38134),
(199600, 'ruRU', '', 'Увы, в этот раз все серьезно.', 38134),
(199599, 'ruRU', 'Лунная Ягода! Опять шутки или на этот раз что-то серьезное?', '', 38134),
(199598, 'ruRU', '', 'Боггарты? Ничего, я уже выступала перед монстрами. Шоу должно продолжаться!', 38134),
(199597, 'ruRU', '', 'Сегодня зрители как-то странно себя ведут. Кто-то наверняка устроит... сцену. Кхм. Может, разберешься?', 38134),
(199596, 'ruRU', 'Я тебя вижу, пилигрим. Убирайся из моей башни!', '', 38134),
(199595, 'ruRU', '', 'Я приму тебя при дворе, $p. Отправляйся в Сердце леса. Там мы продолжим наш разговор.', 38134),
(199594, 'ruRU', '', 'Если Изгнанник освободится, наступит беда посерьезнее нынешней засухи.', 38134),
(199593, 'ruRU', '', 'Мой старый друг... После стольких веков наши страхи стали явью.', 38134),
(199592, 'ruRU', '', 'Королева Зимы изучает послание Примаса.', 38134),
(199590, 'ruRU', '', 'Сильвана командует воинами Тюремщика. Она может призвать их, чтобы сокрушить нас. \n\nЕсли нас всех возьмут в плен, Андуину станет только тяжелее. Не будем сражаться с ней, пока не узнаем, что ей нужно.', 38134),
(199585, 'ruRU', 'Истинно так. Кругом море возможностей.', 'Истинно так. Кругом море возможностей.', 38134),
(199584, 'ruRU', 'Это твоя судьба. Прими ее!', '', 38134),
(199576, 'ruRU', '', 'Тогда ты больше не охотник.', 38134),
(199573, 'ruRU', 'Я служу новому господину! Тому, кто увидит гибель Арденвельда!', '', 38134),
(199559, 'ruRU', '', 'Это... тревожно. Древний ритуал дал ей невероятную силу, и я боюсь, что она сама не сможет с ней совладать.', 38134),
(199557, 'ruRU', '', 'Тиранду? Здесь? Нет, не видела.', 38134),
(199555, 'ruRU', '', 'Я и раньше здесь пряталась. Задержаться надолго не выйдет, но немного времени мы выиграем.', 38134),
(199554, 'ruRU', '', 'Увы, я сбегала уже много раз, но всегда снова оказывалась в клетке.', 38134),
(199533, 'ruRU', 'Молодец! Ты наконец-то победил свои страхи.', '', 38134),
(199532, 'ruRU', 'Без боя я не сдамся.', '', 38134),
(199531, 'ruRU', 'Ты сгинешь в храме Верности. Ты станешь раскольником!', '', 38134),
(199530, 'ruRU', 'Неправда! Вместе мы сильнее. Все трое!', '', 38134),
(199529, 'ruRU', 'Тебе не стать перерожденным! Ты так и будешь бегать за Клейей!', '', 38134),
(199528, 'ruRU', 'Эта неудача перечеркнет все.', '', 38134),
(199527, 'ruRU', 'Я столько раз проваливал это испытание... Но не сегодня.', '', 38134),
(199526, 'ruRU', 'Вместе.', '', 38134),
(199524, 'ruRU', 'Я... нет... Мы... сможем! Я не один!', '', 38134),
(199523, 'ruRU', 'Я... НЕ... СМОГУ!', '', 38134),
(199522, 'ruRU', 'Я слишком слаб... Бесполезно...', '', 38134),
(199520, 'ruRU', '', 'Пора испытать твои новые доспехи.', 38134),
(199515, 'ruRU', 'Сопротивляться... бесполезно...', '', 38134),
(199514, 'ruRU', '', 'Мы должны остановить Лисонию во что бы то ни стало. Нельзя позволить ей снова напасть!', 38134),
(199510, 'ruRU', '', 'А все так хорошо начиналось...', 38134),
(199508, 'ruRU', 'Я... не знаю, смогу ли...', '', 38134),
(199507, 'ruRU', 'Уничтожим их любимое творение!', '', 38134),
(199485, 'ruRU', '', 'Гор\'грот говорил о защитных камнях. Найди их, чтобы спасти воеводу.', 38134),
(199484, 'ruRU', 'Дом Чумы... уничтожен... Где Страдама? Я должен закончить работу над зельем... Я... должен!..', '', 38134),
(199483, 'ruRU', 'Открыто обвинить личей в предательстве – немыслимо, и потому я изобрел особое зелье. Оно защитит Страдаму, если мои страхи подтвердятся. Лишь бы оно не пригодилось...', '', 38134),
(199482, 'ruRU', 'Дом Ритуалов предложил нам помощь с анимой. Мы со Страдамой не верим в искренность их намерений... но мы не в том положении, чтобы отказываться. Нужно быть начеку.', '', 38134),
(199481, 'ruRU', 'Бежать некуда! Из Утробы нет выхода!', '', 38134),
(199480, 'ruRU', 'Мой гений... не должен... пропасть...', '', 38134),
(199477, 'ruRU', 'Примаса нет; война между домами неизбежна. Маркграфиня Страдама поделилась со мной опасениями. Без анимы наши труды – и наша мощь – превратятся в ничто.', '', 38134),
(199471, 'ruRU', 'Постой! Опусти оружие!', '', 38134),
(199468, 'ruRU', 'Твое время вышло. Ты растратил свою силу. Теперь Мве\'зала придет за своим долгом.', '', 38134),
(199467, 'ruRU', 'Какой же ты лоа смерти? Заключаешь сделки, когда нужно просто забрать свое...', '', 38134),
(199466, 'ruRU', 'Чуть что, сразу бежишь к Королеве Зимы?', '', 38134),
(199465, 'ruRU', 'Нет, нет. Ты меня не так поняла. Босс крадет лоа из твоих рощ и...', '', 38134),
(199464, 'ruRU', '', 'Меня не волнуют споры между лоа. Не втягивай Арденвельд в свои мелочные распри.', 38134),
(199463, 'ruRU', 'Не серчай, твое величество. Я пришел с миром! Нам надо действовать вместе – иначе Мве\'залу не одолеть.', '', 38134),
(199462, 'ruRU', '', 'Что все это значит, $n?', 38134),
(199461, 'ruRU', 'Приветик, Ледяная Королева! Давненько не виделись, а? Милое у тебя деревце. Да и вообще тут недурно!', '', 38134),
(199459, 'ruRU', 'Кажется, кто-то пришел украсть секрет моего успеха!', '', 38134),
(199458, 'ruRU', '', 'Ага! Значит, ты и впрямь напортачил!', 38134),
(199457, 'ruRU', 'Эм... э-э... При подготовке плоти нужно столько всего учитывать... Возможно, мне слегка не хватило...', '', 38134),
(199456, 'ruRU', '', 'Э-э... Он шевельнулся! Плоть кирий пытается взять контроль над телом!', 38134),
(199455, 'ruRU', 'Вздор! Как только кадавр получит малдраксийскую душу, от пропаганды Бастиона ничего не останется!', '', 38134),
(199454, 'ruRU', '', 'А еще он заметит, что ты начал сборку, не лишив плоть воспоминаний!', 38134),
(199453, 'ruRU', 'Когда Трупошов вернется, он узнает, что я, Малькрекс, завершил его труд. Я сделаю сильнейшего кадавра из плоти кирий!', '', 38134),
(199452, 'ruRU', '', 'А вот и ты! Королева скоро будет здесь!', 38134),
(199450, 'ruRU', 'Мне говорили, что Деравин и Коренф – те еще проказники. Это так?', '', 38134),
(199449, 'ruRU', '', 'Спасибо.', 38134),
(199448, 'ruRU', 'Я... лучше останусь с тобой. Так безопаснее.', '', 38134),
(199447, 'ruRU', '', 'Не бойся, Те\'зан. Иди дальше.', 38134),
(199446, 'ruRU', 'Это точно безопасно? Повсюду еще полно существ в масках.', '', 38134),
(199445, 'ruRU', '', 'Я догоню тебя в Тирна Ваале. Мне нужно... попрощаться.', 38134),
(199442, 'ruRU', '', 'Сердце рощи! Нет!', 38134),
(199441, 'ruRU', '', 'Без сердца множество семян лишится анимы...', 38134),
(199440, 'ruRU', 'Ния... Прости... меня...', '', 38134),
(199439, 'ruRU', '', 'Ты больше ничего не получишь!', 38134),
(199438, 'ruRU', 'Мне не нравится, как ты на меня смотришь. \n\nТы же знаешь, что в посмертии полагается обрести вечный покой?', '', 38134),
(199436, 'ruRU', '', 'Наш народ порабощает странная магия, нашу землю терзает засуха анимы, а сердце рощи украли.\n\nИ все это происходит слишком быстро.', 38134),
(199427, 'ruRU', 'Я еще на ногах. Хотя завтра, пожалуй, летать будет трудновато.', '', 38134),
(199426, 'ruRU', 'Дрейвен! Ты в порядке?', '', 38134),
(199424, 'ruRU', 'Грашаал! Я могу разбить любой камень!', '', 38134),
(199423, 'ruRU', 'Кааль! Тебе от меня не скрыться!', '', 38134),
(199422, 'ruRU', '', 'В туманных лесах наши пути разойдутся. Тирна Ваал зовет тебя.', 38134),
(199421, 'ruRU', 'Мне нужно больше анимы!', '', 38134),
(199420, 'ruRU', 'Принесите мне аниму, герои!', '', 38134),
(199419, 'ruRU', '', 'Надежда возродится из пепла. Круговорот не прервется.', 38134),
(199417, 'ruRU', 'Герои, я очищу небеса!', '', 38134),
(199414, 'ruRU', 'Все склонятся! Все угаснут!', '', 38134),
(199413, 'ruRU', 'Арденвельд будет принадлежать повелителям!', '', 38134),
(199412, 'ruRU', 'Ну ладно. Но потом вам придется мне ее вернуть!', '', 38134),
(199411, 'ruRU', '', 'Все, что ты отдашь, воздастся.', 38134),
(199410, 'ruRU', '', 'Тьма надвигается. Звездный свет защищает нас.', 38134),
(199399, 'ruRU', 'Я не так глуп, позволю заметить.\n\nМой дорогой друг Теотар регулярно навещает меня, чтобы убедиться, что я защищен от жестокого влияния Света.\n\nОн настоящий джентльмен.', '', 38134),
(199397, 'ruRU', 'Конечно нет. Мне здесь вполне нравится.\n\nТихо и спокойно. Никто меня не беспокоит... обычно.\n\n<Лорд Скаул хмурится.>\n\nУ меня наконец-то нашлось время прочитать все то, что я отложил за последние годы.\n\nТолько достать бы мне где еще книг.', '', 38134),
(199396, 'ruRU', 'Да? Что тебе нужно?\n\nНадеюсь, у тебя очень веская причина отрывать меня от чтения.', '', 38134),
(199395, 'ruRU', 'Мы стольких потеряли. Так много погибло...\n\n<Ара\'лон затихает, погружаясь в раздумья.>', '', 38134),
(199384, 'ruRU', 'Удачи там. Новый начальник не славится спокойствием.', '', 38134),
(199383, 'ruRU', 'Дай-ка я открою тебе дверь.', '', 38134),
(199382, 'ruRU', '', 'Ты позоришь весь наш народ, Дрейвен! Служи Владыке или сгинь!', 38134),
(199380, 'ruRU', 'Кааль! Грашаал! Не дайте верности вас ослепить! Своей сделкой Денатрий обрек Ревендрет на погибель!', '', 38134),
(199379, 'ruRU', '', 'Камнерожденные! Уничтожьте узурпаторов!', 38134),
(199378, 'ruRU', 'Я... сдерживаю их... из последних сил!', '', 38134),
(199377, 'ruRU', 'Они атакуют! В укрытие!', '', 38134),
(199372, 'ruRU', '', 'Эти злобные создания познают ярость Дикой Охоты!', 38134),
(199370, 'ruRU', 'Расколотое небо встревожило всех, даже Плеть. Давай убьем время и потренируемся немного.', '', 38134),
(199369, 'ruRU', 'Добро пожаловать на борт! Король Ринн хочет, чтобы все герои Альянса были готовы сразиться с ужасами Ледяной Короны.', '', 38134),
(199368, 'ruRU', '', 'С тех пор как раскололось небо, Плеть стала беспокойной. Наша работа – следить за ними. Для начала давай проверим твою боевую форму.', 38134),
(199367, 'ruRU', '', 'Добро пожаловать в патруль Ледяной Короны! Надеюсь, ты справишься.', 38134),
(199365, 'ruRU', '', 'Чем могу помочь?', 38134),
(199363, 'ruRU', '', 'Деравин... Нет! Эти чудовища за все заплатят!', 38134),
(199362, 'ruRU', '', 'Круговорот... продолжается... Иди...', 38134),
(199361, 'ruRU', '', 'Коренфа больше нет. Я не потеряю и тебя!', 38134),
(199360, 'ruRU', '', 'Нет... эта анима... для диких семян...', 38134),
(199357, 'ruRU', 'Гипокос нашел среди павших кирий претора и починил его. У претора нет души, поэтому он сможет пройти.\n\nНаправь его через барьер, отключи защиту и открой нам путь.', '', 38134),
(199356, 'ruRU', 'За Азерот!', '', 38134),
(199353, 'ruRU', 'Нужно выбраться отсюда и доложить Архонту о том, что здесь происходит.', '', 38134),
(199352, 'ruRU', 'Я так переносил семена в моей старой роще. Надеюсь, тебе это тоже пригодится.', '', 38134),
(199350, 'ruRU', 'Я очень тебе благодарен. Арденвельду нужно побольше таких душ, как ты.', '', 38134),
(199349, 'ruRU', 'Я победил самого Крексуса! Вы ничто!', '', 38134),
(199348, 'ruRU', 'Я разрублю тебе череп, предатель! За Крексуса!', '', 38134),
(199345, 'ruRU', 'Ты как всегда ставишь преданность выше власти. Глупец. Побеждают те, кто умеет выбирать союзников. Они поддержали меня, и теперь я маркграф!', '', 38134),
(199344, 'ruRU', 'Твое коварство сгубило тебя. Пора нам покончить с тобой!', '', 38134),
(199343, 'ruRU', 'Смертная душа снова здесь? И наш заблудший барон тоже? Ни кадавры, ни даже Утроба вас не добили. Обидно.', '', 38134),
(199342, 'ruRU', '', 'Довольно, Лоркрон! Если ты знаешь, кто я, то должен бояться.', 38134),
(199341, 'ruRU', 'Ты послала нас в западню, а теперь сама стала жертвой. И я тебя хорошо знаю.', '', 38134),
(199340, 'ruRU', '', 'Существа в масках... Значит, вот какая участь их ждет?!', 38134),
(199339, 'ruRU', 'Разве ты не узнаешь своих жертв? Их клинки? Их лица под масками?', '', 38134),
(199337, 'ruRU', '', 'Мне знаком этот голос. Но он пал в бою...', 38134),
(199336, 'ruRU', 'Корейн... Ты все такая же хитрая.', '', 38134),
(199335, 'ruRU', 'Знай свою добычу. Это был твой первый урок мне. Но сама ты его забыла.', '', 38134),
(199334, 'ruRU', '', 'Лоркрон... что с тобой случилось?', 38134),
(199332, 'ruRU', '', 'Ступай к Мерцающим каскадам. Моя охота только началась.', 38134),
(199331, 'ruRU', '', 'Верный друг... стал лютым врагом. Мы должны его сразить.', 38134),
(199330, 'ruRU', 'Нет! Моей жертве не победить меня снова. Бойся теней, Корейн...', '', 38134),
(199329, 'ruRU', 'Здесь твоя охота закончится, Корейн!', '', 38134),
(199325, 'ruRU', '', 'Стойте.', 38134),
(199323, 'ruRU', '', 'Осторожно. Эти глаза видят сквозь чары.', 38134),
(199322, 'ruRU', '', 'Вперед! К победе!', 38134),
(199318, 'ruRU', 'А я свяжусь с теми, кому больше всего доверяю в Малдраксусе.', '', 38134),
(199317, 'ruRU', '', 'Я у тебя в долгу. Я чуть не погибла.', 38134),
(199316, 'ruRU', '', 'Она... осквернена Утробой...', 38134),
(199310, 'ruRU', 'Ритуал очищения снимет с этих душ бремя грехов, совершенных при жизни.', 'Ритуал очищения снимет с этих душ бремя грехов, совершенных при жизни.', 38134),
(199304, 'ruRU', 'Вот так! Теперь посмотрим на... Подожди, это что?.. К оружию! На нас напала Плеть!', '', 38134),
(199303, 'ruRU', '', 'Неплохо! Теперь посмотрим на... Подожди, это что?.. К оружию! На нас напала Плеть!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(199302, 'ruRU', 'Мать чумы... с вами... разделается...', '', 38134),
(199301, 'ruRU', 'Служите мне, хранители гнили!', '', 38134),
(199298, 'ruRU', 'Ешьте, мои слуги!', '', 38134),
(199296, 'ruRU', 'Восстаньте, служители чумы!', '', 38134),
(199291, 'ruRU', 'Добро пожаловать. Мы вас ждали!', '', 38134),
(199288, 'ruRU', '', 'Сколько знаний... упущено...', 38134),
(199287, 'ruRU', '', 'Объявляю эти артефакты собственностью Владыки!', 38134),
(199286, 'ruRU', 'Ты уже знаешь, как освободить меня?', '', 38134),
(199285, 'ruRU', 'Боссу... не понравится...', '', 38134),
(199284, 'ruRU', 'Машина не слушает!', '', 38134),
(199283, 'ruRU', 'Не трогай... кристалл...', '', 38134),
(199282, 'ruRU', 'Раздавлю блестяшкой!', '', 38134),
(199281, 'ruRU', 'Стараюсь... как могу...', '', 38134),
(199280, 'ruRU', 'Костлявые всеми командуют!', '', 38134),
(199279, 'ruRU', 'Я... что... плохой?', '', 38134),
(199278, 'ruRU', 'Вы плохие!', '', 38134),
(199275, 'ruRU', 'Малдраксийцы все такие кровожадные?', '', 38134),
(199274, 'ruRU', 'Похоже на проделки Трупошва. Отвлеки их, а я разберусь с его подмастерьями!', '', 38134),
(199273, 'ruRU', '', 'Без осколка зеркала Матео нам не добраться до медальона вовремя.', 38134),
(199271, 'ruRU', 'Я узнаю твои секреты!', 'Я узнаю твои секреты!', 38134),
(199266, 'ruRU', 'Малдраксус будет нашим!', 'Малдраксус будет нашим!', 38134),
(199265, 'ruRU', 'Эй! Босс сказал – без правильных клыков никого не пускать.', '', 38134),
(199259, 'ruRU', 'И стать оружием Тюремщика.', '', 38134),
(199258, 'ruRU', '\"Исповедь\" также поможет тебе вылечить союзника – это универсальное заклинание.', '', 38134),
(199257, 'ruRU', '', '\"Исповедь\" также поможет тебе исцелить союзника – это универсальное заклинание.', 38134),
(199246, 'ruRU', '', 'Для меня... все кончено...', 38134),
(199245, 'ruRU', '', 'Так... темно...', 38134),
(199244, 'ruRU', 'Спасибо! Я пойду дальше. Тебе ведь нужна помощь?', '', 38134),
(199243, 'ruRU', 'Какие же они злобные!', '', 38134),
(199242, 'ruRU', 'Сильвана повинна во многих злодеяниях, но я не думал, что она способна на такое.', '', 38134),
(199241, 'ruRU', '', 'Благодарность расцветает даже посреди бури. Наши пути пересекутся в топи.', 38134),
(199240, 'ruRU', 'В Утробу течет столько душ. Мертвые из Азерота... Дренора... из других, неизвестных нам миров. Все обречены томиться здесь.', '', 38134),
(199239, 'ruRU', '', 'Гнусная магия... знакомого врага...', 38134),
(199238, 'ruRU', 'Армия Тюремщика не меньше, чем сам Легион... и она растет.', '', 38134),
(199237, 'ruRU', '', 'Держитесь рядом. Не привлекайте внимания.', 38134),
(199229, 'ruRU', 'Священные земли снова наши. Перерожденные вернулись к обучению. Они будут рады узнать о возвращении последнего храма.', '', 38134),
(199228, 'ruRU', '[DEPRECATED] Ya best be turnin\' around, Bwonsamdi. Dis be none of ya concern.', '', 38134),
(199227, 'ruRU', 'Это же знак Мве\'залы. О нет! Нет-нет-нет! Это что, выходит, босс за всем стоит?!', '', 38134),
(199226, 'ruRU', 'Благодаря тебе Грехопад в безопасности.', '', 38134),
(199225, 'ruRU', 'Враг уже здесь! Приготовь зеркала! Пусть тех, кто сунется за стены, встретит вспышка Света!', '', 38134),
(199223, 'ruRU', 'Силы камнерожденным-громилам не занимать, а вот с меткостью у них плохо. Помоги им поддержать наших союзников на передовой.', '', 38134),
(199222, 'ruRU', 'Дом Примроуз этого не забудет.', '', 38134),
(199221, 'ruRU', 'Кто-то украл у дома Примроуз ценного камнебеса. Такую наглую кражу нельзя оставлять безнаказанной! Отыщи беса и его похитителя.', '', 38134),
(199218, 'ruRU', '', 'Ты поможешь Ревендрету вернуть истинное предназначение.', 38134),
(199217, 'ruRU', '', 'Тысячи душ беспрерывно стекаются в зверинец. Мы не можем позволить себе упустить столько анимы. Поторопись, пока их не затянуло в Утробу!', 38134),
(199211, 'ruRU', '', 'Эти заклинания нужно обновлять только тогда, когда их действие почти кончается.', 38134),
(199210, 'ruRU', '', 'Отработай \"Агонию\" и \"Порчу\" на манекене.', 38134),
(199209, 'ruRU', '', 'Пока \"Агония\" и \"Порча\" терзают твою цель темной магией, ты можешь использовать другие заклинания.', 38134),
(199208, 'ruRU', '', 'Теперь ты заставишь страдать всех, у кого хватит глупости с тобой сразиться.', 38134),
(199207, 'ruRU', 'Эти заклинания нужно обновлять только тогда, когда их действие почти кончается.', '', 38134),
(199206, 'ruRU', 'Отработай \"Агонию\" и \"Порчу\" на манекене.', '', 38134),
(199205, 'ruRU', 'Пока \"Агония\" и \"Порча\" терзают твою цель темной магией, ты можешь использовать другие заклинания.', '', 38134),
(199204, 'ruRU', 'Теперь ты заставишь страдать всех, у кого хватит глупости с тобой сразиться.', '', 38134),
(199183, 'ruRU', 'Обвинительница должна позволить нам найти и прикончить его!', '', 38134),
(199182, 'ruRU', 'Эти вентиры действуют заодно с личом?!', '', 38134),
(199181, 'ruRU', '', 'Скорее в Малдраксус, $n. Вираз ответит за свои злодеяния.', 38134),
(199180, 'ruRU', 'У нас еще будет время поговорить, сын мой. Обещаю.', '', 38134),
(199179, 'ruRU', 'Дрека! Нам нужно срочно вернуться в Малдраксус. Маркграф Крексус...', '', 38134),
(199178, 'ruRU', 'Нет... выходит, я опоздал! Нас предал соратник, один из нас!', '', 38134),
(199177, 'ruRU', '', 'Испепелитель, нашего маркграфа убили. Избранным как никогда нужна твоя помощь.', 38134),
(199174, 'ruRU', 'Я должен вернуться в Азерот и рассказать обо всем нашим союзникам, отец. Но я вернусь к тебе, когда позволит долг.', '', 38134),
(199173, 'ruRU', 'Верховный лорд! Мы сбежали из Утробы. Спасибо вам за помощь.', '', 38134),
(199172, 'ruRU', '', 'Покончим с этим.', 38134),
(199171, 'ruRU', '', 'Твой час еще настанет. Это только начало!', 38134),
(199170, 'ruRU', '', 'Я узнаю всю правду!', 38134),
(199169, 'ruRU', '', 'Глупо было выступать против идеала.', 38134),
(199168, 'ruRU', '', 'Наш мир гибнет. Раскольники освободят нас!', 38134),
(199167, 'ruRU', '', 'Не пытайся ее спасти. Она увидела истину!', 38134),
(199154, 'ruRU', '', 'Выход – если мы не ошиблись.', 38134),
(199153, 'ruRU', 'Вряд ли он важен – слуги Тюремщика его будто не видят. Что это такое?', '', 38134),
(199152, 'ruRU', 'Я заметил на возвышении обелиск из древнего камня.', '', 38134),
(199151, 'ruRU', 'За Котлом должен быть путевой камень. Ты не видел его?', '', 38134),
(199150, 'ruRU', 'Ясно. Веди нас.', '', 38134),
(199149, 'ruRU', '', 'К сожалению, мы не можем ждать, пока тебе станет лучше.', 38134),
(199148, 'ruRU', 'В таком случае я бесконечно тебе благодарен, $n.', '', 38134),
(199147, 'ruRU', 'Так бы и случилось, если бы нас не спасли.', '', 38134),
(199146, 'ruRU', 'Андуин? Я думал, все кончено...', '', 38134),
(199145, 'ruRU', 'Бейн? Ты меня слышишь?', '', 38134),
(199144, 'ruRU', 'Он приходит в себя.', '', 38134),
(199143, 'ruRU', '', 'Держись, Бейн! Не поддавайся Тюремщику!', 38134),
(199142, 'ruRU', 'Нужно поскорее прервать его!', '', 38134),
(199141, 'ruRU', 'Проклятие разрывает его душу!', '', 38134),
(199140, 'ruRU', 'Сейчас я сломаю клинок. Готовьтесь к неожиданностям.', '', 38134),
(199139, 'ruRU', '', 'Это... Не может быть...', 38134),
(199138, 'ruRU', '', 'М-м? Не видишь – я работаю.', 38134),
(199137, 'ruRU', 'Но... Древний... обещал...', '', 38134),
(199136, 'ruRU', 'Я заберу всю твою аниму!', '', 38134),
(199135, 'ruRU', 'Я предатель? Теперь со мной пилигрим, и я вижу, что настоящий предатель – ты. Разберись с этими двумя и верни осколок зеркала мне в Архивы.', '', 38134),
(199134, 'ruRU', 'Денатрию? Как вы можете помогать ему после того, как он отправил Куратора в Утробу? Предатель.', '', 38134),
(199133, 'ruRU', 'Эмиль! Мы искали тебя... Сиру понадобился осколок зеркала, который дала тебе Куратор.', 'Эмиль! Мы искали тебя... Сиру понадобился осколок зеркала, который дала тебе Куратор.', 38134),
(199130, 'ruRU', '', 'Коренф... Клянусь, мы вернем сердце рощи.', 38134),
(199127, 'ruRU', 'Приготовься и поднимайся по ступеням.', '', 38134),
(199126, 'ruRU', '', 'Коренф! Где же ты?', 38134),
(199125, 'ruRU', '', 'Я поищу выживших. Встретимся в глубине рощи.', 38134),
(199121, 'ruRU', 'Я Ара\'лон. Воин Дикой Охоты.\n\nКогда-то я служил в прекрасной роще Тирна Нох, помогал духам пережить долгую зиму. Но та роща пала, и я нашел для себя новое предназначение.\n\nЯ служу всему Арденвельду.', '', 38134),
(199119, 'ruRU', 'Мы вернемся в рощу как можно скорее. Спасибо тебе.', '', 38134),
(199118, 'ruRU', 'Водопады захвачены. И... кажется, за нами погоня.', '', 38134),
(199116, 'ruRU', 'Осторожно. Здесь полно опасных тварей, больших и маленьких.', '', 38134),
(199115, 'ruRU', 'Сюда! Скорее, пока тебя не увидели!', '', 38134),
(199113, 'ruRU', 'Кажется, это он. На лезвии еще осталась кровь Бейна.', '', 38134),
(199111, 'ruRU', 'Он забрал... сердце...', '', 38134),
(199110, 'ruRU', 'Крелнор... в маске... Туманная топь...', '', 38134),
(199109, 'ruRU', 'Пусть Тюремщик приходит! Пусть отведает стали!', '', 38134),
(199108, 'ruRU', 'Вот оно, оружие настоящего воина!', '', 38134),
(199107, 'ruRU', 'Лучше, чем ничего. Посмотрим еще.', '', 38134),
(199106, 'ruRU', 'Хм, рукоять длинновата.', '', 38134),
(199105, 'ruRU', 'Слишком длинный клинок.', '', 38134),
(199104, 'ruRU', 'Сносное оружие. Хотя никогда не понимал, почему в Альянсе все так обожают мечи.', '', 38134),
(199103, 'ruRU', 'Поищем еще.', '', 38134),
(199102, 'ruRU', 'Я никогда не любил посохи, но этот хотя бы не сломается в бою.', '', 38134),
(199101, 'ruRU', 'Тяжелая штуковина. Посмотрим, что еще мы найдем.', '', 38134),
(199100, 'ruRU', 'Арбалет? Я привык сражаться в ближнем бою. Лицом к лицу.', '', 38134),
(199099, 'ruRU', 'Но вы все равно пытаетесь вызвать мой гнев. Что же... Вы его получите!', '', 38134),
(199097, 'ruRU', 'Хватит! Я принял вас, как гостей. Я проявил терпимость... Даже вежливость.', '', 38134),
(199094, 'ruRU', '', 'Их крики эхом разнесутся по загробным мирам!', 38134),
(199093, 'ruRU', '', 'Этому клинку нет равных. Сломаешь его – и я тебя сама поломаю!', 38134),
(199092, 'ruRU', '', 'Да! Да! Я их покромсаю!', 38134),
(199088, 'ruRU', '', 'Я размажу их по стенам!', 38134),
(199086, 'ruRU', '', 'Я порежу их на ме-е-елкие кусочки!', 38134),
(199079, 'ruRU', 'Так держать, солдат. Доложи Дреке, что мы готовы.', '', 38134),
(199076, 'ruRU', 'Можешь мне не верить, но это не путь Малдраксуса.', '', 38134),
(199072, 'ruRU', '', 'Я слышала о тебе много хорошего, $p. Можешь отдохнуть здесь.', 38134),
(199058, 'ruRU', '', 'Я продолжу тренироваться самостоятельно.\n\nПриходи еще, пилигрим.', 38134),
(199053, 'ruRU', '', 'Ты кто? А, Деравин и Те\'зан рассказывали про тебя...', 38134),
(199051, 'ruRU', '', 'Идем. Маркграф Крексус ждет.', 38134),
(199050, 'ruRU', '', '<На лице застыло выражение умиротворения.>', 38134),
(199049, 'ruRU', '', 'Гордыня погубила тебя и при жизни...', 38134),
(199048, 'ruRU', '', 'Сегодня нам повезло. Мы увидим еще один день и продолжим охоту.', 38134),
(199045, 'ruRU', '', '<Раненая кирия заползла в убежище, чтобы без помех умереть. $B$BЕе руки сжимают маленькую шкатулку.>', 38134),
(199044, 'ruRU', 'Неожиданный... результат...', '', 38134),
(199043, 'ruRU', 'Какие рьяные добровольцы!', '', 38134),
(199040, 'ruRU', '', 'С тобой потренируется Трог. А я поговорю с командой про дождь.', 38134),
(199031, 'ruRU', 'Мы для них... подопытные...', '', 38134),
(199030, 'ruRU', 'Табулятор подсчитывает тела, словно монеты.', '', 38134),
(199028, 'ruRU', '<Этот сильвар смотрит перед собой невидящими глазами. Он пал жертвой внезапного нападения.>', '', 38134),
(199027, 'ruRU', 'Эта магия... Что они с ними делают?!', '', 38134),
(199026, 'ruRU', '', '<Застывшее лицо воркай искажено яростью. Она погибла, не справившись с полчищами врагов.>', 38134),
(199022, 'ruRU', 'О, вы так быстро не умрете!', '', 38134),
(199020, 'ruRU', 'Мой любимый пес!', '', 38134),
(199019, 'ruRU', 'Мой песик! Нет!', '', 38134),
(199018, 'ruRU', 'Гекутис, сюда! Быстро!', '', 38134),
(199017, 'ruRU', 'Маргор, ко мне!', '', 38134),
(199016, 'ruRU', 'Ко мне, Баргаст!', '', 38134),
(199015, 'ruRU', '<Лицо воркая изуродовано темной магией. Его заманили в ловушку.>', '', 38134),
(199013, 'ruRU', '', 'Как глупо! Привести нас прямо к Эмилю. Теперь его осколок зеркала будет нашим!', 38134),
(199012, 'ruRU', 'Надеюсь, эта вещица не только выглядит опасной!', '', 38134),
(199011, 'ruRU', 'Эй, ты! Тебя прислала Куратор? Сюда, мне нужно... Что?!', '', 38134),
(199010, 'ruRU', 'Что же делает эта штука? Любопытно до смерти. Правда, умрете, скорее всего, вы.', '', 38134),
(199009, 'ruRU', 'Пора узнать, правду ли говорят об этой реликвии!', '', 38134),
(199008, 'ruRU', '', 'Мои охотники и охотницы редко так опаздывают. Найди их.', 38134),
(199007, 'ruRU', '', 'Я капитан-егерь Корейн из Дикой Охоты. Служу Королеве Зимы, защищаю Арденвельд.\n\nЗнай, что мой клинок сразит любого, кто будет угрожать моему лесу. \n\nБез всяких разговоров и расспросов.', 38134),
(199005, 'ruRU', '', 'Дикая Охота быстрее всех.', 38134),
(199004, 'ruRU', 'Сколько бы я ни натягивал цепи, эта душа не умолкает.', '', 38134),
(199003, 'ruRU', 'Эта душа всеми силами сопротивляется моим попыткам подчинить ее.', '', 38134),
(198998, 'ruRU', '', 'Тщетная попытка...', 38134),
(198997, 'ruRU', '', 'Твоя душа усилит наши войска!', 38134),
(198993, 'ruRU', '', 'В нынешнем состоянии Кель\'тас Солнечный Скиталец опасен для всех.', 38134),
(198991, 'ruRU', '', 'Мы не позволим этой роще пасть!\n\nКоролева доверила мне защиту Спящей лощины и всех обитающих в ней духов. Мы должны справиться.', 38134),
(198990, 'ruRU', '', 'За мной. Это единственный путь к дому Избранных.', 38134),
(198989, 'ruRU', 'Нужно спасти их! Скорее!', '', 38134),
(198988, 'ruRU', '', 'Маркграф Крексус – сильнейший из нас. Он не приемлет страха – только уважение.', 38134),
(198985, 'ruRU', '', 'Дом Избранных силен и отважен в бою. Мы сокрушим предателей и закончим эту войну.', 38134),
(198984, 'ruRU', 'Запись IV.\n\nЯ почти уверен, что Эмиль находится в катакомбах у подножья ныне разрушенной сторожевой башни. Но мне туда не добраться...\n\nВ катакомбах и шагу не ступить. Даже сильнейшие из нас не всегда могут справиться с грехокаменными колоссами. \n\nЯ решил, что помощь пришла, когда мы увидели облаченных в плащи разведчиков из Архивов... Но они обнажили против нас свои клинки... против собственных собратьев. Я так и не узнал причину этого предательства. Могу лишь надеяться, что охота наскучит им до того, как они найдут меня.', '', 38134),
(198983, 'ruRU', '', 'Здравствуй! Леди Лунная Ягода тебя хвалила.', 38134),
(198982, 'ruRU', '', 'Ни за что! Ваши повелители за все ответят!', 38134),
(198981, 'ruRU', 'Надень маску, Ния. Служи повелителям!', '', 38134),
(198978, 'ruRU', '', 'Это зелье... Марилет?', 38134),
(198958, 'ruRU', 'Ты мне не указ. Я повинуюсь только Графине.', 'Ты мне не указ. Я повинуюсь только Графине.', 38134),
(198957, 'ruRU', 'Не лезь в дела, которых тебе все равно никогда не понять.', 'Не лезь в дела, которых тебе все равно никогда не понять.', 38134),
(198952, 'ruRU', 'Когда я прибыл в Ревендрет, меня определили служить Графине, ее придворным и гостям в качестве искупления. Со временем искупление переросло в желание. Я по собственной воле остался здесь и служу Графине личным помощником и доверенным советником. Я забочусь о ней и храню под замком все ее тайны.\n\nНадеюсь, новый замок мне не понадобится. Ради твоего же блага.', 'Когда я прибыла в Ревендрет, меня назначили служить Графине, ее придворным и гостям в качестве искупления. Со временем искупление переросло в желание. Я по собственной воле осталась здесь и служу Графине личным помощником и доверенным советником. Я забочусь о ней и держу под замком все ее тайны.\n\nНадеюсь, новый замок мне не понадобится. Ради твоего же блага.', 38134),
(198951, 'ruRU', 'Я займу позицию снаружи!', '', 38134),
(198950, 'ruRU', '', 'Теперь все пять домов воюют друг с другом. Два уже пали, но я сделаю все, чтобы уберечь наш.', 38134),
(198949, 'ruRU', '', 'Наконец-то подкрепление... Постой, ты не вентир.', 38134),
(198948, 'ruRU', '', 'Мы поклялись защищать Темные Земли. Но Примас, наш командир, пропал.', 38134),
(198947, 'ruRU', '', 'Если ты собираешься идти вглубь катакомб, то сможешь нам помочь.', 38134),
(198946, 'ruRU', 'А я, кажется, видел... Только очень далеко отсюда...', '', 38134),
(198945, 'ruRU', '', 'Мы потеряли связь с Эмилем, когда здесь воцарился хаос. Стражник Дорин отправился его искать. Он до сих пор не вернулся.', 38134),
(198944, 'ruRU', 'Его раны заживают, но дух все еще слаб. Я никогда не видел такой магии.', '', 38134),
(198935, 'ruRU', '', 'Хм... В любом случае клинок без рукояти тебе ни к чему. Пора за работу.', 38134),
(198933, 'ruRU', 'Это ты – подкрепление, за которым мы посылали? От разведчиков ничего не слышно.', 'Это ты – подкрепление, за которым мы посылали? От разведчиков ничего не слышно.', 38134),
(198932, 'ruRU', '', 'Так вот из-за чего все это было. Клинок и послание от самого Примаса...', 38134),
(198931, 'ruRU', '', 'Садись на оленя и мчись в Сердцевинную рощу. Небо темнеет.', 38134),
(198912, 'ruRU', '', 'Ветер несет скорбь из Сердцевинной рощи... Лес содрогается...', 38134),
(198911, 'ruRU', 'Должен сказать, приверженность Графини ее принципам восхищает.', 'Должен сказать, приверженность Графини ее принципам восхищает.', 38134),
(198910, 'ruRU', 'Пилигрим Утробы? Интересное ты существо.', 'Пилигрим Утробы? Интересное ты существо.', 38134),
(198908, 'ruRU', '<Меряет вас взглядом, ни говоря ни слова.>', '<Меряет вас взглядом, ни говоря ни слова.>', 38134),
(198907, 'ruRU', 'Берегись, пилигрим Утробы. Графиня видит все твои сокровенные желания, и она всегда готова к новой жатве.', 'Берегись, пилигрим Утробы. Графиня видит все твои сокровенные желания, и она всегда готова к новой жатве.', 38134),
(198904, 'ruRU', 'Кого я вижу, |3-6($pr). Наверное, Графиня пустила тебя сюда из жалости.', 'Кого я вижу, |3-6($pr). Наверное, Графиня пустила тебя сюда из жалости.', 38134),
(198903, 'ruRU', 'Сейчас не самое подходящее время. Твои дрязги меня не интересуют, |3-6($c).', 'Сейчас не самое подходящее время. Твои дрязги меня не интересуют, |3-6($c).', 38134),
(198902, 'ruRU', 'То, что говорят о пилигримах Утробы, это правда?', 'То, что говорят о пилигримах Утробы, это правда?', 38134),
(198901, 'ruRU', '', 'По глазам вижу, у тебя что-то случилось. Чем могу помочь?', 38134),
(198899, 'ruRU', 'Я понимаю, что Графиня вольна привечать в своих владениях кого угодно. Но |3-6($pr)? Серьезно?', 'Я понимаю, что Графиня вольна привечать в своих владениях кого угодно. Но |3-6($pr)? Серьезно?', 38134),
(198893, 'ruRU', 'Генерал Кааль, в укрытие! Мои солдаты ударят сверху!', '', 38134),
(198892, 'ruRU', '', 'Грашаал, прячься! Пора обрушить ужас с небес!', 38134),
(198891, 'ruRU', '', 'В Малдраксус попадают те, кто жил битвой. Сила здесь ценится и вознаграждается превыше всего.', 38134),
(198890, 'ruRU', '', 'Их слишком много! Я вытащу нас отсюда!', 38134),
(198876, 'ruRU', 'У тебя меткий глаз, пилигрим Утробы. Это отучит их пировать в лесах Арденвельда.', '', 38134),
(198875, 'ruRU', 'Пожиратели продолжают наводнять лес. Убивай их и закрывай порталы с помощью анимы, пока не стало слишком поздно!', '', 38134),
(198874, 'ruRU', 'Хочешь сразиться?', '', 38134),
(198871, 'ruRU', 'Бурные завихрения то расступаются, то сжимают кольцо вновь, редкими проблесками открывая вид на знакомые места Темных Земель. Портал мгновенно перенесет вас куда-то в указанную зону, хотя безопасность не гарантирована.', 'Бурные завихрения то расступаются, то сжимают кольцо вновь, редкими проблесками открывая вид на знакомые места Темных Земель. Портал мгновенно перенесет вас куда-то в указанную зону, хотя безопасность не гарантирована.', 38134),
(198868, 'ruRU', '', 'Я доверила своим верным слугам по половине зеркала. Нужно найти их прежде, чем их схватят по приказу Денатрия.', 38134),
(198867, 'ruRU', '', 'Я спрятала медальон в этом зеркале, прежде чем Денатрий сослал меня в Утробу. И разбила стекло, чтобы он не мог его достать.', 38134),
(198865, 'ruRU', '', 'Катакомбы Сожалений погрузились в хаос, а силы Денатрия захватили Архивы. \n\nК счастью, с помощью вентиров из склепа Отпущения мы смогли более или менее восстановить порядок в склепе Забытых. Но последствия бесчинств еще видны.\n\nПринц Ренатал доверил мне вернуть медальон Алчности. Но я не в состоянии сражаться. Мне нужна твоя помощь, пилигрим Утробы.', 38134),
(198864, 'ruRU', '', 'Все хуже, чем я думала, пилигрим. За время моего отсутствия склепы погрузились в хаос. У нас много дел.', 38134),
(198863, 'ruRU', 'Я скучаю по тебе. \n\nКогда-нибудь мы снова встретимся во Сне.', '', 38134),
(198861, 'ruRU', '', 'Снова это чувство. Боль, агония... невообразимое безумие...', 38134),
(198860, 'ruRU', 'Молодец, Мевикс!', '', 38134),
(198859, 'ruRU', 'Ха! У нас есть кое-кто получше любого подкрепления! Добудь нам победу!', '', 38134),
(198858, 'ruRU', 'Гармал не ожидает открытого штурма. Мы сметем его войска одной атакой!', '', 38134),
(198857, 'ruRU', 'Но нам нужно подкрепление!', '', 38134),
(198856, 'ruRU', 'Теперь у нас есть шанс нанести удар дому Кадавров!', '', 38134),
(198854, 'ruRU', 'Предназначение ведет их так же, как нас. Не теряй веру, друг мой.', 'Предназначение ведет их так же, как нас. Не теряй веру, друг мой.', 38134),
(198853, 'ruRU', 'Только бы надежда не оказалась ложной. Можем ли мы доверять чужакам?', 'Только бы надежда не оказалась ложной. Можем ли мы доверять чужакам?', 38134),
(198852, 'ruRU', 'Это доказывает безграничную изобретательность Предвечных. Они создали мир и все, что в нем есть, и преподнесли нам дар Предназначения.', 'Это доказывает безграничную изобретательность Предвечных. Они создали мир и все, что в нем есть, и преподнесли нам дар Предназначения.', 38134),
(198851, 'ruRU', 'Любопытно, отчего многие из чужаков настолько не похожи друг на друга.', 'Любопытно, отчего многие из чужаков настолько не похожи друг на друга.', 38134),
(198850, 'ruRU', 'Тем из нас, кто вынужден работать с ними, придется уповать на Предназначение.', 'Тем из нас, кто вынужден работать с ними, придется уповать на Предназначение.', 38134),
(198849, 'ruRU', 'Я благодарю судьбу за то, что избавила меня от прямого общения с ними.', 'Я благодарю судьбу за то, что избавила меня от прямого общения с ними.', 38134),
(198848, 'ruRU', 'Не могу не согласиться.', 'Не могу не согласиться.', 38134),
(198847, 'ruRU', 'Смотрителям не следовало бы закрывать глаза на махинации брокеров. Они нарушают заведенный порядок, и это меня раздражает.', 'Смотрителям не следовало бы закрывать глаза на махинации брокеров. Они нарушают заведенный порядок, и это меня раздражает.', 38134),
(198846, 'ruRU', 'Не волнуйся. Предназначение укажет нам путь через этот хаос.', 'Не волнуйся. Предназначение укажет нам путь через этот хаос.', 38134),
(198845, 'ruRU', 'Значит, твоя вера в самом деле тверже моей. Я могу признать, что мной овладели сомнения. Если честно, они и сейчас еще не совсем покинули мое сердце, даже после прибытия смертных.', 'Значит, твоя вера в самом деле тверже моей. Я могу признать, что сомнения посещали меня. Если честно, они и сейчас еще не совсем покинули мое сердце, даже после прибытия смертных.', 38134),
(198844, 'ruRU', 'Меня вера никогда не оставляла. Я не принижаю твоей верности Предназначению, но я никогда не поддаюсь сомнениям.', 'Меня вера никогда не оставляла. Я не принижаю твоей верности Предназначению, но я никогда не поддаюсь сомнениям.', 38134),
(198843, 'ruRU', 'Признаюсь. Недавние события возродили мою веру. Воистину Предназначение хранит нас.', 'Признаюсь. Недавние события возродили мою веру. Воистину Предназначение хранит нас.', 38134),
(198842, 'ruRU', 'Именно. В работе Предназначения видно мастерство истинного творца. Только вдохновение могло превратить упорядоченный, но невзрачный мир в произведение искусства.', 'Именно. В работе Предназначения видно мастерство истинного творца. Только вдохновение могло превратить упорядоченный, но невзрачный мир в произведение искусства.', 38134),
(198841, 'ruRU', 'Разве такая красота могла возникнуть сама по себе? Нет, она создана трудами Предвечных.', 'Разве такая красота могла возникнуть сама по себе? Нет, она создана трудами Предвечных.', 38134),
(198840, 'ruRU', 'Истинно так, друг мой. Все, что есть в нашем мире, было создано Предназначением.', 'Истинно так, друг мой. Все, что есть в нашем мире, было создано Предназначением.', 38134),
(198839, 'ruRU', 'Этот пейзаж всегда напоминает мне о величии Предназначения.', 'Этот пейзаж всегда напоминает мне о величии Предназначения.', 38134),
(198838, 'ruRU', 'До меня тоже доходили эти вести. Предназначение и сейчас указывает нам путь. Оно наш маяк во тьме.', 'До меня тоже доходили эти вести. Предназначение и сейчас указывает нам путь. Оно наш маяк во тьме.', 38134),
(198837, 'ruRU', 'Подорвем их!', '', 38134),
(198836, 'ruRU', 'Говорят, что Арбитр почти очнулась!', 'Говорят, что Арбитр почти очнулась!', 38134),
(198835, 'ruRU', 'Как и нас, их ведет Предназначение. Я не сомневаюсь в этом.', 'Как и нас, их ведет Предназначение. Я не сомневаюсь в этом.', 38134),
(198834, 'ruRU', 'Врата открываются вновь. Хоть одна хорошая новость. Надеюсь, пришельцам можно доверять.', 'Врата открываются вновь. Хоть одна хорошая новость. Надеюсь, пришельцам можно доверять.', 38134),
(198833, 'ruRU', 'Я лично по-прежнему верю Предназначению. Даже когда его замысел непостижим.', 'Я лично по-прежнему верю Предназначению. Даже когда его замысел непостижим.', 38134),
(198832, 'ruRU', 'Предназначение не могло предусмотреть такого хаоса.', 'Предназначение не могло предусмотреть такого хаоса.', 38134),
(198830, 'ruRU', '', 'Поговорим потом. Уходим, скорее!', 38134),
(198829, 'ruRU', '', 'Я баронесса Дрека. Дом Избранных поддержит тебя.', 38134),
(198828, 'ruRU', 'Ключ выберет того, кто достоин.', '', 38134),
(198827, 'ruRU', 'Измена рождает хаос. Восстанови разорванные узы.', '', 38134),
(198826, 'ruRU', 'Миры Смерти окутала тьма.', '', 38134),
(198825, 'ruRU', 'Никого не щадить!', '', 38134),
(198824, 'ruRU', '', 'Ташмур, лови!', 38134),
(198823, 'ruRU', '', 'Я позову Дикую Охоту. Нам снова нужна твоя помощь!', 38134),
(198822, 'ruRU', '', 'Смотри! Они бегут из Сердцевинной рощи... Что-то случилось!', 38134),
(198821, 'ruRU', '', 'Вот! Теперь, раз уж мы квиты... А? Что такое?', 38134),
(198820, 'ruRU', '', 'Прошу, прими этот подарок. Делай с этой частичкой магии Арденвельда все, что пожелаешь.', 38134),
(198818, 'ruRU', '', 'Нам срочно требуются твои навыки. Нужно выследить неуловимую добычу.', 38134),
(198817, 'ruRU', 'Мы проложим путь прямо через защитные укрепления Владыки.\n\nСкажи мне, когда захочешь начать тренировку.', '', 38134),
(198816, 'ruRU', 'Он ждет тебя у Ледяного Трона. Предводители Альянса и Орды тоже собрались там.', '', 38134),
(198815, 'ruRU', 'Но когда шлем раскололся, Болвар Фордрагон освободился от своего бремени.', '', 38134),
(198814, 'ruRU', 'Посмотри наверх, и увидишь, что она натворила. Я... никогда не видел ничего подобного.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198813, 'ruRU', 'Прежде чем ее слуги похитили правителей, Королева-банши напала на цитадель. Она пробилась к Ледяному Трону и заполучила Шлем Господства.', '', 38134),
(198811, 'ruRU', 'На колени!', '', 38134),
(198810, 'ruRU', 'Ты – ничто!', '', 38134),
(198809, 'ruRU', 'Ты мне не ровня!', '', 38134),
(198808, 'ruRU', 'А чего вы ожидали?', '', 38134),
(198807, 'ruRU', 'Реморния! Истреби этих насекомых!', '', 38134),
(198806, 'ruRU', 'Вы поплатитесь за свое тщеславие.', '', 38134),
(198805, 'ruRU', 'Прекратите сопротивляться, иначе вас ждут вечные муки!', '', 38134),
(198804, 'ruRU', 'Сектанты, пожрите незваных гостей.', '', 38134),
(198798, 'ruRU', '', 'Халн Крутогор! Давно я не видела тебя в бою.', 38134),
(198789, 'ruRU', '', 'Прихвостни Владыки даже ничего понять не успеют.\n\nСкажи мне, когда захочешь начать тренировку.', 38134),
(198786, 'ruRU', '', 'Пикси должны отвечать добром на добро. Ты помогаешь лесу, а я вот не доставила твое сообщение.', 38134),
(198778, 'ruRU', 'Ты не выдашь меня!', 'Ты не выдашь меня!', 38134),
(198775, 'ruRU', 'С возвращением! Ну как, все нашлось?', '', 38134),
(198774, 'ruRU', 'О! Ты как раз вовремя.', '', 38134),
(198773, 'ruRU', 'Я как раз подготовил чары, которые идеально тебя скроют. Постой смирно...', '', 38134),
(198772, 'ruRU', 'Добудь у гормов то, что нам нужно, и тогда мы сразимся с Поглотителем Миров.', '', 38134),
(198770, 'ruRU', '<Нелвина смотрит на вас с усмешкой.>', '<Нелвина смотрит на вас с усмешкой.>', 38134),
(198769, 'ruRU', '', 'О, пожалуйста! Помоги нам, храбрый герой!', 38134),
(198728, 'ruRU', 'Мевикс! Сокруши этих трусов!', '', 38134),
(198727, 'ruRU', 'Спасибо тебе. В благодарность я произнесу тост за одного из твоих гостей.', '', 38134),
(198726, 'ruRU', 'Скорее! Поймай все пять предметов из моего чайного набора, пока камнебесы их все не разбили!', '', 38134),
(198724, 'ruRU', '', 'Теперь поиграем всерьез!', 38134),
(198722, 'ruRU', 'Гекутис, дроби их кости!', '', 38134),
(198721, 'ruRU', 'Баргаст, растерзай их души!', '', 38134),
(198720, 'ruRU', 'Рви их плоть, Маргор!', '', 38134),
(198717, 'ruRU', '', 'Не стоило сходить с дороги!', 38134),
(198716, 'ruRU', 'Спустить псов!', '', 38134),
(198715, 'ruRU', 'Умоляю, осторожнее! Ты разобьешь все мои чашки!', '', 38134),
(198714, 'ruRU', 'Нет! Это же мой лучший сервиз!', '', 38134),
(198713, 'ruRU', 'На этом снаряде – твое имя.', '', 38134),
(198712, 'ruRU', 'Снарядов у меня на всех хватит!', '', 38134),
(198711, 'ruRU', 'От меня ничто не скроется.', '', 38134),
(198710, 'ruRU', 'Я чую ваш страх.', '', 38134),
(198709, 'ruRU', 'Но... господин, мы понесем огромные потери.', '', 38134),
(198708, 'ruRU', 'Такова цена победы!', '', 38134),
(198707, 'ruRU', 'В атаку! Прорвем их ряды прямым натиском!', '', 38134),
(198705, 'ruRU', 'Глупцы! Ревендрет – это я!', '', 38134),
(198698, 'ruRU', 'Реморния, исторгни крики из их глоток!', '', 38134),
(198697, 'ruRU', 'С твоей помощью армии Ревендрета будут прибывать и становиться сильнее.', '', 38134),
(198696, 'ruRU', 'Реморния, перережь этих червей!', '', 38134),
(198695, 'ruRU', 'В этой местности темнокрылы проходят боевую подготовку. Проследи, чтобы наши войска были готовы сражаться!', '', 38134),
(198694, 'ruRU', 'Вас поглотят тени!', '', 38134),
(198693, 'ruRU', 'Вы сгинете вместе!', '', 38134),
(198692, 'ruRU', 'Покричите для меня!', '', 38134),
(198691, 'ruRU', 'Я вас изрублю!', '', 38134),
(198689, 'ruRU', 'Верные слуги! Покончите с этой мерзостью.', '', 38134),
(198687, 'ruRU', 'Слуги, уберите с моих глаз эти отбросы.', '', 38134),
(198684, 'ruRU', 'Ползите ко мне на коленях. Сдавайтесь, и я дарую вам быструю смерть.', '', 38134),
(198683, 'ruRU', 'Пора указать вам на ваше место, смертные.', '', 38134),
(198682, 'ruRU', 'Реморния! Очисти мой замок от паразитов.', '', 38134),
(198677, 'ruRU', 'Гордыня завела вас слишком далеко.', '', 38134),
(198676, 'ruRU', 'Будь начеку, охотница! Здесь небезопасно.', '', 38134),
(198675, 'ruRU', '', 'От Дикой Охоты ничто не скроется. Кто-то из них наверняка сможет нам помочь.', 38134),
(198673, 'ruRU', 'Трусливые предатели. Глупцы, которые пошли против мощи дома Избранных!', '', 38134),
(198670, 'ruRU', 'Говори, рекрут!', '', 38134),
(198669, 'ruRU', 'Боль несет очищение.', '', 38134),
(198668, 'ruRU', 'Покайтесь, или ваши грехи поглотят вас!', '', 38134),
(198665, 'ruRU', '', 'Поспеши. Вираз никогда не отличался терпением.', 38134),
(198664, 'ruRU', '', 'Давай! Используй арфу и отправь малышей в безопасное место!', 38134),
(198653, 'ruRU', '<Эта сильвара выглядит ужасно бледной и едва дышит... Скорее всего, ее уже не спасти. \n\nВы моргаете и вдруг... один ее глаз открывается и начинает таращиться на вас.\n\nЕе рот широко растягивается, превращаясь в разверстую пасть.>', '<Эта сильвара выглядит ужасно бледной и едва дышит... Скорее всего, ее уже не спасти. \n\nВы моргаете и вдруг... один ее глаз открывается и начинает таращиться на вас.\n\nЕе рот широко растягивается, превращаясь в разверстую пасть.>', 38134),
(198651, 'ruRU', '', 'Спасибо тебе! Сейчас мало кому можно довериться.', 38134),
(198649, 'ruRU', 'Моей бабушке плохо!\n\nМы хотели добраться до Тирна Ваала. Думали, дроман нам поможет, но она ослабела, а в лесу опасно!\n\nТы можешь помочь? Бабушка прячется в пещере недалеко отсюда. Ей очень плохо...', 'Моей бабушке плохо!\n\nМы хотели добраться до Тирна Ваала. Думали, дроман нам поможет, но она ослабела, а в лесу опасно!\n\nТы можешь помочь? Бабушка прячется в пещере недалеко отсюда. Ей очень плохо...', 38134),
(198648, 'ruRU', '', 'Благодарю тебя! Твоя помощь была неоценима для меня и моих душ.\n\nПостарайся не сходить с дороги. Лес полон опасных существ!\n\nЕще раз спасибо!', 38134),
(198645, 'ruRU', '', 'Да укроет тебя ночь. Мы у тебя в долгу!', 38134),
(198642, 'ruRU', '', 'Спаси эти несчастные души!', 38134),
(198616, 'ruRU', 'Пересчитайте... мои... органы...', '', 38134),
(198615, 'ruRU', 'Еще один труп в коллекцию!', '', 38134),
(198612, 'ruRU', '', 'Я многое узнала из записей Матьяша. Чтобы отобрать у него мой камень грехов, придется схитрить. Есть у меня кое-что на уме. Надо только найти нужные ингредиенты.', 38134),
(198607, 'ruRU', 'Без обид, но по тебе видно, что с тобой часто происходят странные вещи.', '', 38134),
(198606, 'ruRU', 'Ради них и существует Арденвельд! В диких семенах спят духи природы, такие как Старейшая Данмира. Это она там, под той птицей. Если ты выманишь ее из гнезда, я смогу подкрасться и забрать семя!\n\nЗдорово, что хотя она и не смогла прийти, Лунная Ягода прислала кого-то взамен. Она сказала тебе, что просит за помощь песню и танец?\n\n<Коренф хихикает.>\n\nТак что, ты поможешь согнать птицу с гнезда?\n\n<Коренф умоляюще улыбается.>', '', 38134),
(198590, 'ruRU', 'Хм... Нехорошо все это. Кошмары не отступают. Что же случилось с этим духом?', '', 38134),
(198589, 'ruRU', 'Постой-ка... Этот дух... тянется к тебе! Такого не случалось... сколько я себя помню!', '', 38134),
(198573, 'ruRU', 'Анима поможет унять сны этого духа. Как только он успокоится, мы...', '', 38134),
(198569, 'ruRU', 'Ой-ой... Духу в этом диком семени снится кошмар. Это значит, что бедняжка умерла страшной смертью.', '', 38134),
(198567, 'ruRU', 'Жуткая Охотница приказала доставить Локси на холм Сумрачной Крипты. Она уже в пути.', '', 38134),
(198565, 'ruRU', 'Стой... Я помню этого зверя... Он с нашего корабля! Видимо, пережил крушение.', '', 38134),
(198564, 'ruRU', 'Попался!', '', 38134),
(198563, 'ruRU', 'Смотри, вот он!', '', 38134),
(198561, 'ruRU', 'Спасибо за высочайшую честь, принц Ренатал. Спасибо.', '', 38134),
(198560, 'ruRU', 'Нет! Зачем нас прогонять! Здесь так весело!', '', 38134),
(198559, 'ruRU', 'Я представлю Архонту самый похвальный отчет.', '', 38134),
(198558, 'ruRU', '', 'Прием оправдал все мои ожидания. С удовольствием приду на следующий.', 38134),
(198557, 'ruRU', 'Я многое узнал. Надо все обдумать. Много хороших идей...', '', 38134),
(198556, 'ruRU', '', 'Кто как, а я прекрасно провела время!', 38134),
(198555, 'ruRU', '', 'Великолепный прием, принц. Может, не такая уж бесполезная была затея.', 38134),
(198551, 'ruRU', 'Прекрасное дикое семя, правда? Оно вернется в свой мир через много лет.\n\nВозьми найденные анимашишки и наполни семя анимой.', '', 38134),
(198550, 'ruRU', 'От этих спригганов одни неприятности!', 'От этих спригганов одни неприятности!', 38134),
(198549, 'ruRU', 'Смерть и гниение!', '', 38134),
(198546, 'ruRU', 'Жертвы устилают путь к победе!', '', 38134),
(198545, 'ruRU', 'Мои солдаты восстанут снова!', '', 38134),
(198542, 'ruRU', '', 'Ты закрыл для меня небо? Я отплачу тебе тем же!', 38134),
(198534, 'ruRU', 'Ты знаешь, кого за это благодарить!', '', 38134),
(198533, 'ruRU', '', 'Ветер снова наполняет твои крылья, Творец Снов.', 38134),
(198532, 'ruRU', 'Наджия, ты очнулась! Мы стараемся понять, для чего надсмотрщик призвал сюда смертного. Мы нашли его личные записи.', '', 38134),
(198531, 'ruRU', '', 'Ох... Глаза болят так, будто в них всадили колья. Это ты, пилигрим?', 38134),
(198529, 'ruRU', '', 'Прости! Пришлось сделать по пути остановочку.', 38134),
(198527, 'ruRU', 'Начнем с юного семени. Этот дух прибыл сюда прямо перед засухой и требует особого ухода.', '', 38134),
(198520, 'ruRU', 'Барон отдал приказ! Вперед!', '', 38134),
(198519, 'ruRU', 'Сюда! Мы покажем, как ухаживать за семенем и заглядывать в сны духа.', '', 38134),
(198517, 'ruRU', 'Нас всех проглатывали растения. С кем не бывает?', '', 38134),
(198510, 'ruRU', '', 'Видишь, $n? Когда душа объята страхом, ее анима течет рекой.', 38134),
(198509, 'ruRU', 'Мы нападем, когда прозвучит рог. За дом Избранных!', '', 38134),
(198505, 'ruRU', '', '<Наджия безуспешно пытается заговорить>', 38134),
(198500, 'ruRU', 'Мы используем аниму, которую украл Денатрий, против него самого.', '', 38134),
(198499, 'ruRU', 'Пилигрим Утробы! Быстрее собери столько анимы, сколько сможешь. Это будет временная связь, но ее сила пригодится тебе в бою.', '', 38134),
(198496, 'ruRU', '', 'Спригганы... Фи!', 38134),
(198495, 'ruRU', '', 'Я понимаю, что нас ждет новое славное будущее. В котором нет места предателям вроде тебя!', 38134),
(198494, 'ruRU', '', 'Денатрий предал Ревендрет, когда направил аниму в Утробу. Даже прихвостни вроде тебя должны это понимать!', 38134),
(198493, 'ruRU', '', 'Обвинительница! Сперва ты от меня сбежала, а теперь возвращаешься, прихватив с собой лишь жалкую кучку смертных. Какой позор.', 38134),
(198492, 'ruRU', '', 'Все малыши нашлись, а спригганы обратились в бегство. Спасибо за помощь, $n!', 38134),
(198490, 'ruRU', '', 'Спригганы не дают покоя нашим шелкобродам. Останови это безобразие!', 38134),
(198489, 'ruRU', 'Мой гнев пылает ярче солнца!', '', 38134),
(198488, 'ruRU', 'Я не закончил! Выпустите меня!', '', 38134),
(198487, 'ruRU', '', 'Тор илисар тера нал!', 38134),
(198486, 'ruRU', 'Тиранда! Не делай этого!', '', 38134),
(198485, 'ruRU', '', 'Спаси остальных! А я прикончу банши!', 38134),
(198484, 'ruRU', 'Найди тех, кого похитили, и верни их домой!', '', 38134),
(198483, 'ruRU', 'Твоя отвага не будет забыта. Аль диэль шала.', '', 38134),
(198482, 'ruRU', 'Проход открыт!', '', 38134),
(198481, 'ruRU', 'Время пришло! Возьми расколотый шлем, и мы откроем портал.', '', 38134),
(198480, 'ruRU', 'Но другого пути нет. Смерть пришла, чтобы поглотить Азерот. Твой долг – защитить наш мир.', '', 38134),
(198479, 'ruRU', 'Мир по ту сторону завесы не предназначен для живых смертных. Попав туда, ты можешь уже не вернуться.', '', 38134),
(198478, 'ruRU', 'Мы не боимся смерти. Ни здесь, ни в других мирах.', '', 38134),
(198477, 'ruRU', 'Вместе с тобой туда отправятся рыцари Черного Клинка. Дарион Могрейн вызвался тебе помогать.', '', 38134),
(198476, 'ruRU', 'Тебя будет ждать множество опасностей. В одиночку с ними не справиться.', '', 38134),
(198475, 'ruRU', 'Но нельзя закрывать глаза на эту угрозу. Ты отправишься туда, куда мне путь закрыт.', '', 38134),
(198474, 'ruRU', 'Я не могу пойти с тобой. Даже теперь, когда шлем разрушен, я боюсь поддаться воле его истинного хозяина.', '', 38134),
(198473, 'ruRU', 'И теперь я ощущаю следы этой же силы за расколотым небом.', '', 38134),
(198472, 'ruRU', 'Я чувствовал присутствие какой-то темной сущности... Но не Артаса... и не Нер\'зула. То был... кто-то другой.', '', 38134),
(198471, 'ruRU', 'Когда этот шлем был на мне, я с трудом сдерживал его страшную силу. Иногда мне казалось, что он вот-вот одолеет меня.', '', 38134),
(198470, 'ruRU', 'Разложи обломки Шлема Господства по кругу, чтобы начать ритуал.', '', 38134),
(198469, 'ruRU', 'Туда тебе и предстоит отправиться, чтобы спасти тех, кого забрала Сильвана. Вместе мы откроем путь.', '', 38134),
(198468, 'ruRU', 'Неся службу на Ледяном Троне, я видел смутные образы загробных миров. Среди них было место, окутанное непреодолимой тьмой.', '', 38134),
(198467, 'ruRU', 'Расколов шлем, банши раскрыла зияющую рану в завесе между нашим миром и Темными Землями.', '', 38134),
(198466, 'ruRU', 'Приближается более страшная угроза, и мы должны быть к ней готовы.', '', 38134),
(198465, 'ruRU', 'Довольно! Война окончилась. Не время начинать еще одну.', '', 38134),
(198464, 'ruRU', '', 'Как ты смеешь говорить о мире после всего, что вы натворили?', 38134),
(198463, 'ruRU', '', 'Верховная жрица, без вас калдорай потеряют последнюю надежду обрести мир.', 38134),
(198462, 'ruRU', 'Я тоже хочу призвать Сильвану к ответу. Но Лор\'темар прав. Андуин не захотел бы, чтобы ради него рисковали всем Альянсом.', '', 38134),
(198461, 'ruRU', '', 'Трус! Я готова заплатить любую цену, лишь бы пронзить банши своим клинком!', 38134),
(198460, 'ruRU', 'Отправлять туда оставшихся лидеров было бы самоубийством. Наши похищенные друзья сказали бы то же самое.', '', 38134),
(198459, 'ruRU', '...мир все еще хрупок. Сейчас непростые времена. Нашим народам нужна стабильность – и Альянсу, и Орде.', '', 38134),
(198456, 'ruRU', 'Нам еще многое предстоит сделать, чтобы подготовиться к войне в Темных Землях.', '', 38134),
(198452, 'ruRU', 'Иногда я перестаю понимать, зачем мы вообще готовим эти души. Все равно новых ждать неоткуда, и мы добываем все меньше анимы.', 'Иногда я перестаю понимать, зачем мы вообще готовим эти души. Все равно новых ждать неоткуда, и мы добываем все меньше анимы.', 38134),
(198451, 'ruRU', 'Я слышал, ты ищешь здесь аниму. Боюсь, у нас каждая капля на счету.', 'Я слышала, ты ищешь здесь аниму. Боюсь, у нас каждая капля на счету.', 38134),
(198450, 'ruRU', 'Посмотри в небо. Нам больше не подчиняются даже наши собственные создания!', 'Посмотри в небо. Нам больше не подчиняются даже наши собственные создания!', 38134),
(198449, 'ruRU', 'Обычно клетки ломятся от душ... проклятая засуха.', 'Обычно клетки ломятся от душ... проклятая засуха.', 38134),
(198431, 'ruRU', '', 'Бей сильнее! Никакой пощады!', 38134),
(198429, 'ruRU', '', 'Благодарю вас. После того, как мы победим Тюремщика... Кириям будет над чем подумать.', 38134),
(198428, 'ruRU', '', 'Мы всегда считали, что память о смертных жизнях – это бремя, которое мешает нам служить. Но сегодня именно смертные спасли Бастион.', 38134),
(198427, 'ruRU', '', 'Козни Тюремщика внесли раскол в ряды кирий. Мы должны залечить эту рану.', 38134),
(198426, 'ruRU', '', 'Девия...', 38134),
(198425, 'ruRU', '', 'Тюремщик помутил твой разум, Девия. Тебя нужно остановить... любой ценой.', 38134),
(198424, 'ruRU', '', 'С твоим копьем я смогу управлять Шпилями и создать новых идеалов для кирий!', 38134),
(198423, 'ruRU', '', 'Смирись с поражением, Архонт. Я служу более могучей силе.', 38134),
(198422, 'ruRU', '', 'А-а-а!', 38134),
(198421, 'ruRU', '', 'Я оплакиваю твою измену...', 38134),
(198420, 'ruRU', '', 'Он хочет освободить нас из тюрьмы, которую мы сами построили. И этим он заслужил мою верность.', 38134),
(198419, 'ruRU', '', 'И я выслушала Тюремщика. Я узнала, как несправедливо вы, Вечные, обошлись с ним... и со всеми нами.', 38134),
(198418, 'ruRU', '', 'Но ты отказалась признать правду. Что наш путь ложный. Что мы служим обману.', 38134),
(198417, 'ruRU', '', 'Я заглянула в воспоминания Утера. Увиденное меня потрясло.', 38134),
(198416, 'ruRU', '', 'Нет. Именно смертная душа показала мне ложь в твоих эдиктах.', 38134),
(198415, 'ruRU', 'Теперь, когда со всех сторон наступают слуги Тюремщика, нужно использовать все ее ресурсы.', '', 38134),
(198414, 'ruRU', '', 'Мы жертвуем, потому что так нужно. Бремя смертной жизни мешает нам исполнять долг.', 38134),
(198413, 'ruRU', '', 'Но чему мы все служим? Бессильному Арбитру, жестоким законам! Все наши жертвы были напрасны!', 38134),
(198412, 'ruRU', 'Архонт возвела Элизийскую цитадель как оплот нашего ковенанта.', '', 38134),
(198411, 'ruRU', '', 'Эпоха за эпохой мы проходили ваше \"очищение\". Забывали себя, свои жизни, своих любимых. Все во имя служения.', 38134),
(198410, 'ruRU', '', 'Только не с тобой во главе. Раскольники укажут Бастиону новый путь!', 38134),
(198409, 'ruRU', '', 'Девия, послушай. Кирии должны делать общее дело.', 38134),
(198408, 'ruRU', '', 'Раскольники, не подпускайте этих смертных!', 38134),
(198407, 'ruRU', '', 'Ты слепо веришь в свой путь, обрекая нас всех. У меня нет выбора, я должна остановить тебя!', 38134),
(198406, 'ruRU', '', 'Ты была идеалом Верности, Девия. И все это время ты вела за собой раскольников.', 38134),
(198405, 'ruRU', '', 'Займитесь их арьергардом, а я сражусь с Девией. Служите верно.', 38134),
(198404, 'ruRU', '', 'Смертные, враг превосходит нас числом. Раскольники разгромили большую часть наших сил.', 38134),
(198403, 'ruRU', 'Великолепие цитадели... поражает, не правда ли?', '', 38134),
(198402, 'ruRU', 'Теперь эти храбрые души – твой народ. Добро пожаловать.', '', 38134),
(198401, 'ruRU', 'А теперь вместе! В атаку!', '', 38134),
(198400, 'ruRU', 'Я завершу начатое!', '', 38134),
(198399, 'ruRU', 'Мой долг исполнен!', '', 38134),
(198398, 'ruRU', 'Еще... Я хочу еще...', '', 38134),
(198397, 'ruRU', 'С этим врагом покончено.', '', 38134),
(198396, 'ruRU', 'Пришло время вступить в наши ряды. Помочь нам вернуть наши владения.', '', 38134),
(198395, 'ruRU', 'Еще одна зарубка на моей рукояти.', '', 38134),
(198390, 'ruRU', 'Сходи на разведку и найди генерала Альвера. Приготовься и подай нам сигнал к атаке.', '', 38134),
(198389, 'ruRU', 'Я давно не видел испытания на Пути.\n\nЖду не дождусь!', '', 38134),
(198388, 'ruRU', 'Следовать Пути... для распорядителя это необычно.\n\nМы с \"Броном\" принимаем вызов!', '', 38134),
(198387, 'ruRU', 'Это возможность испытать боевой режим \"Брона\"!', '', 38134),
(198386, 'ruRU', 'Направь меня на своих врагов, а остальное я сделаю за тебя.', '', 38134),
(198385, 'ruRU', 'В благодарность за освобождение я буду убивать твоих врагов... некоторое время.', '', 38134),
(198384, 'ruRU', 'Положись на меня, и твои враги падут.', '', 38134),
(198383, 'ruRU', 'Отличный бой, $p! Отдавай приказ, и мы еще раз пойдем в наступление!', '', 38134),
(198382, 'ruRU', 'Как же мне хотелось вернуться в бой...', '', 38134),
(198381, 'ruRU', 'Мы не промахиваемся!', '', 38134),
(198380, 'ruRU', '', 'Я готова к испытанию.', 38134),
(198379, 'ruRU', '', 'Дай-ка я расправлю крылья.', 38134),
(198378, 'ruRU', '', 'Может, если я справлюсь, Пелагий тоже поверит в себя.', 38134),
(198371, 'ruRU', 'Это лишь еще одно испытание. Если я не справлюсь... то стану сильнее, и в другой раз будет проще.', '', 38134),
(198370, 'ruRU', 'У меня хорошее предчувствие, $n.', '', 38134),
(198369, 'ruRU', 'Подошла моя очередь?\n\nЯ буду стараться изо всех сил.', '', 38134),
(198368, 'ruRU', '', '\"Призвать Мортаниса\", в такую-то засуху?! Немыслимо!', 38134),
(198365, 'ruRU', 'Так точно!', '', 38134),
(198363, 'ruRU', 'Разве это предательство, если ты служишь господину не добровольно?', '', 38134),
(198362, 'ruRU', 'И, кстати, спасибо! Ты настоящий пример для всех... как там называется твой мир?', '', 38134),
(198361, 'ruRU', 'Возьми мой кинжал и освободи остальных. Встретимся позже в Тирна Ваале.', '', 38134),
(198341, 'ruRU', 'Их съели растения? Опять?..', '', 38134),
(198340, 'ruRU', 'Ха, и что я говорил? Он никуда не годится под действием агол... акло... когда пьяный! Ик! Теперь главное – подливать ему, чтобы не прочухался.', '', 38134),
(198339, 'ruRU', 'Так-то лучше! В наши дни никто не уважает старших.', '', 38134),
(198338, 'ruRU', 'Чудесно, грибомант как раз кстати. Не поможешь с побегами? И с этими отвратительными захватчиками?', '', 38134),
(198337, 'ruRU', 'Вот так. Если никто из наших не сболтнет, все будет нормально. А вот с Косогрязом все сложнее.', '', 38134),
(198336, 'ruRU', 'На помощь! Помогите!', '', 38134),
(198335, 'ruRU', 'Зря мы так... Если клыкастый увидит, что мы отлыниваем, отправит нас обратно в жижу.', '', 38134),
(198334, 'ruRU', '', 'Это должно улучшить положение. Надеюсь, тебе не пришлось наглотаться спор.', 38134),
(198333, 'ruRU', '', 'Главный грибомант в беде! Прошу, помоги ему защитить Побеги!', 38134),
(198332, 'ruRU', '', 'Сюда, пойдем со мной.', 38134),
(198329, 'ruRU', 'Мы атакуем по твоему приказу. Землерои будут управлять зеркалом, чтобы сдерживать подкрепление из замка Нафрия, а мы с тобой и немногие оставшиеся союзники пойдем в атаку на самого Денатрия.\n\nВсе будет зависеть от пилигрима Утробы, трех Жнецов, бывшего генерала Каменного легиона и нескольких вентиров, которые поведут бой против того, кто создал нас всех.\n\nДай знать, когда приготовишься встретить смерть лицом к лицу.', '', 38134),
(198316, 'ruRU', 'Смертная тварь! Ты – ничто против нас!', '', 38134),
(198315, 'ruRU', 'Солдаты, нас ждет битва! Готовьте оружие!', '', 38134),
(198313, 'ruRU', '', '<Центурион беспорядочно жестикулирует.>\n\nОшибка системы!\n\nОшибка системы!\n\nОшибка системы!', 38134),
(198312, 'ruRU', 'ЛОВИ! ЛОВИ! A-А-А!\n\nЛОВИ СВИТОК!', 'ЛОВИ! ЛОВИ! A-А-А!\n\nЛОВИ СВИТОК!', 38134),
(198311, 'ruRU', '', 'Служи верно, $n.', 38134),
(198310, 'ruRU', '', 'Сражаться на Пути Перерождения – это большая честь. И хотя твои медиумы будут биться только с видением... я могу явиться и по-настоящему.', 38134),
(198309, 'ruRU', '', 'Вот так. Частица меня осталась в зеркале.', 38134),
(198308, 'ruRU', 'Мясыш готовый драться!', '', 38134),
(198307, 'ruRU', 'После утренних медитаций на душе легко, но мне так хочется заняться чем-нибудь, кроме тренировок!\n\nМожет быть, почитать...', 'После утренних медитаций на душе легко, но мне так хочется заняться чем-нибудь, кроме тренировок!\n\nМожет быть, почитать...', 38134),
(198306, 'ruRU', 'Хм… Я хочу фруктов. Можешь найти распорядителя?', 'Хм… Я хочу фруктов. Можешь найти распорядителя?', 38134),
(198305, 'ruRU', 'Мучитель украл еду! Опять все забрал! Я требую мести! И еды!', '', 38134),
(198304, 'ruRU', '', 'Выйди вперед. Предстань перед кириями, чтобы они приняли тебя в свои ряды.', 38134),
(198303, 'ruRU', 'Узри Архонта! Когда мир был молод, она уже была древнее древних.', '', 38134),
(198302, 'ruRU', 'Мы убедились, что пилигрим сражается не только за Бастион, но и за благо всех Темных Земель.', '', 38134),
(198301, 'ruRU', 'Этот ритуал помогает беспокойным духам одолеть дурные мысли, которые мешают им восстанавливать силы.\n\nМне кажется, что дух, с которым у тебя такая крепкая связь, столкнулся при жизни с тяжелыми испытаниями. Надеюсь, нам удастся вернуть ей спокойный сон.', '', 38134),
(198300, 'ruRU', 'Внемлите мне, братья и сестры! Сегодня мы собрались ради торжественного события.', '', 38134),
(198299, 'ruRU', 'Отныне у нас с тобой общий путь. Подойди.', '', 38134),
(198298, 'ruRU', '', 'Камень! Что произошло?', 38134),
(198297, 'ruRU', '', 'У меня много дел, но на Пути я смогу сделать еще больше для Бастиона. Возьми зеркало души и запиши мои воспоминания.', 38134),
(198296, 'ruRU', '', 'Путь Перерождения? Что ж, обучать других – это большая честь.', 38134),
(198292, 'ruRU', '', 'Примас оставил этот камень как испытание. Доберись до него.', 38134),
(198288, 'ruRU', 'Я хочу пить. Где носит этого распорядителя?', 'Я хочу пить. Где носит этого распорядителя?', 38134),
(198287, 'ruRU', 'Но ведь ты... ничтожество...', '', 38134),
(198286, 'ruRU', '', 'Моя обязанность – беречь запасы и защищать претендентов.\n\nЯ помню о нашей первой встрече и о напрасно потраченной на центурионов аниме. Да, твоя помощь незаменима во время этой засухи, но мы все равно не можем тратить наши запасы.\n\nЧто тебе нужно?', 38134),
(198271, 'ruRU', 'Тут не достанут! Тут мы в безопасности.\n\nСпасибо, спасибо, мы спасены!', '', 38134),
(198268, 'ruRU', '', 'Я же говорила – не возвращайтесь!', 38134),
(198267, 'ruRU', '', 'Зайхед! Проснись!', 38134),
(198265, 'ruRU', 'Мы почти на месте! Стойла на вершине вон того холма.', '', 38134),
(198264, 'ruRU', 'Будь на то воля мерзавцев из Альянса, у нас бы и такого дома не было.', '', 38134),
(198263, 'ruRU', '', 'Приветствую тебя, юный росток. Твой путь раскрывается с каждым шагом.', 38134),
(198262, 'ruRU', 'Дело в том, что Оргриммар мы можем считать своим домом. И это много значит для нас.', '', 38134),
(198261, 'ruRU', 'Да, тут жарко и пыльно, а орки не всегда радушны – исключая тебя – но есть и своя изюминка, да?', '', 38134),
(198260, 'ruRU', 'Да, тут жарко и пыльно, а орки не всегда радушны... но есть и своя изюминка, а?', '', 38134),
(198259, 'ruRU', 'Теперь ты – крупная рыба, $n. Ты в Оргриммаре. Все серьезные дела делаются здесь.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198257, 'ruRU', '', 'Некоторые слуги Матьяша служат ему по принуждению, как и я до недавних пор. Постарайся их освободить.', 38134),
(198256, 'ruRU', 'Еще один враг пал...', '', 38134),
(198253, 'ruRU', 'Я чувствую... все. Клянусь Архонтом, ну и жизнь у тебя была!', '', 38134),
(198252, 'ruRU', 'Чистоплоды – это хорошо! Порадуем претендентов?', 'Чистоплоды – это хорошо! Порадуем претендентов?', 38134),
(198251, 'ruRU', 'Кто получает свиток? У-у-у!', 'Кто получает свиток? У-у-у!', 38134),
(198250, 'ruRU', 'Кого тут жажда мучает?', 'Кого тут жажда мучает?', 38134),
(198249, 'ruRU', 'Что почистить?', 'Что почистить?', 38134),
(198248, 'ruRU', 'Надо что-то подержать? Нести? Ловить?!', 'Надо что-то подержать? Нести? Ловить?!', 38134),
(198247, 'ruRU', 'Любишь вкусные чистоплоды? \n\nДа, претендентам можно! За работу дают фрукты!', 'Любишь вкусные чистоплоды? \n\nДа, претендентам можно! За работу дают фрукты!', 38134),
(198246, 'ruRU', 'Свиток, свиток, свиток...\n\nГде же свиток?', 'Свиток, свиток, свиток...\n\nГде же свиток?', 38134),
(198245, 'ruRU', 'Пить? Хочешь сладкого сока?', 'Пить? Хочешь сладкого сока?', 38134),
(198244, 'ruRU', 'Чистить! Все чистить!\n\n<Распорядитель напевает себе под нос.>', 'Чистить! Все чистить!\n\n<Распорядительница напевает себе под нос.>', 38134),
(198243, 'ruRU', 'Что надо подержать?', 'Что надо подержать?', 38134),
(198239, 'ruRU', 'Всегда приятно встретить в Арденвельде такое... существо, как ты.', '', 38134),
(198232, 'ruRU', 'Это честь для меня. Я лишь... надеюсь, что связь с Клейей мне тоже удастся сохранить.', '', 38134),
(198230, 'ruRU', 'Хорошо. Закончи дела здесь, а я поищу других хранителей.', '', 38134),
(198229, 'ruRU', 'С тех пор как началась засуха, анимы хватает не на всех. Иногда нам приходится... выбирать.', '', 38134),
(198228, 'ruRU', 'Хранители должны снабжать дикие семена анимой. Без нее духи внутри не смогут переродиться.', '', 38134),
(198227, 'ruRU', 'Спасибо тебе! Сейчас эти пруды совсем не безмятежные...', '', 38134),
(198226, 'ruRU', 'Нет, нет... такое нельзя показывать Королеве...', '', 38134),
(198224, 'ruRU', 'Сокрушу!', '', 38134),
(198223, 'ruRU', 'Сломаю!', '', 38134),
(198222, 'ruRU', 'Я вобью вас в землю!', '', 38134),
(198221, 'ruRU', 'Я вас сокрушу!', '', 38134),
(198220, 'ruRU', 'Стража наверняка покажет нам путь к стойлам.', '', 38134),
(198219, 'ruRU', 'Ну, что там?', '', 38134),
(198214, 'ruRU', 'Ваша наглость меня утомляет!', '', 38134),
(198213, 'ruRU', 'Меня не расколоть!', '', 38134),
(198212, 'ruRU', 'Ваши клинки не пробьют мою кожу!', '', 38134),
(198210, 'ruRU', 'Умрите!', '', 38134),
(198208, 'ruRU', 'Я желал... только... служить...', '', 38134),
(198205, 'ruRU', 'Вы недостойны битвы со мной.', '', 38134),
(198203, 'ruRU', 'Знай свое место!', '', 38134),
(198202, 'ruRU', 'Мерзавцы!', '', 38134),
(198201, 'ruRU', 'Смерть предателям!', '', 38134),
(198200, 'ruRU', '', 'Мой клинок вас настигнет!', 38134),
(198199, 'ruRU', '', 'Разрежу на куски!', 38134),
(198198, 'ruRU', '', 'Истекайте кровью!', 38134),
(198197, 'ruRU', '', 'Никто не сбежит!', 38134),
(198196, 'ruRU', '', 'Вам не уйти!', 38134),
(198194, 'ruRU', '', 'Вам конец!', 38134),
(198193, 'ruRU', '', 'Вкус победы на острове Изгнанников быстро выветрится. Сейчас нам нужно сосредоточиться на войне.', 38134),
(198192, 'ruRU', '', 'Меня не сломить!', 38134),
(198191, 'ruRU', '', 'Из плоти в камень!', 38134),
(198190, 'ruRU', '', 'Я сама вас прикончу!', 38134),
(198189, 'ruRU', '', 'Сюда, пилигрим. Я нашла Матьяша на верхнем этаже башни. Приготовься, и идем со мной.', 38134),
(198188, 'ruRU', '', 'Сражайся с честью и всегда помни, чему тебя учили. Лок\'тар огар!', 38134),
(198187, 'ruRU', '', 'Трепещите в моей тени!', 38134),
(198186, 'ruRU', '', 'Кровь... за... повелителя...', 38134),
(198185, 'ruRU', '', 'Больше вы никого не предадите.', 38134),
(198184, 'ruRU', '', 'На поле боя тебя ждет слава, $p.', 38134),
(198183, 'ruRU', 'Смена костюмов! Иди за кулисы!', '', 38134),
(198180, 'ruRU', '', 'Предатели!', 38134),
(198179, 'ruRU', '', 'Тебя поглотит бездна!', 38134),
(198175, 'ruRU', 'Их слишком много!', 'Их слишком много!', 38134),
(198173, 'ruRU', '', 'Я положу конец вашему восстанию!', 38134),
(198170, 'ruRU', '', 'Используй цепи, чтобы управлять упрямой душой. Кандалы сломят ее волю.', 38134),
(198166, 'ruRU', '', 'Легкое Перо, используй лилию. Дурацкая печать не поддается!', 38134),
(198163, 'ruRU', 'Это безумие! Мы не должны сражаться друг с другом!', 'Это безумие! Мы не должны сражаться друг с другом!', 38134),
(198156, 'ruRU', 'Все поправлю! Только скажи.', 'Все поправлю! Только скажи.', 38134),
(198155, 'ruRU', 'Привет! Да? Что починить?', 'Привет! Да? Что починить?', 38134),
(198142, 'ruRU', 'Откуда они получают аниму?', 'Откуда они получают аниму?', 38134),
(198141, 'ruRU', 'Кадавры были нашими союзниками! Почему они напали на нас?', 'Кадавры были нашими союзниками! Почему они напали на нас?', 38134),
(198137, 'ruRU', 'Темная Госпожа созвала нас сюда по важному поручению. Она не потерпит провала.', '', 38134),
(198136, 'ruRU', 'С каждым нашим погибшим их армия растет.', 'С каждым нашим погибшим их армия растет.', 38134),
(198135, 'ruRU', 'Их гораздо больше нас!', 'Их гораздо больше нас!', 38134),
(198129, 'ruRU', '', 'Спасибо тебе, Алиот... за мудрые слова и за дружбу.', 38134),
(198127, 'ruRU', '', 'Да. Но с твоей помощью мы прогоним их. Вот увидишь.', 38134),
(198125, 'ruRU', '', 'Но Арденвельд тоже в опасности. Я вижу, как его окружают тени. Прорываются внутрь.', 38134),
(198124, 'ruRU', '', 'Теперь этот лес – мой дом. Так и должно быть. Мы служим круговороту, а Королева направляет нас.', 38134),
(198121, 'ruRU', '', 'Мой мир был разрушен Пылающим Легионом. От него не осталось ничего, кроме пепла.', 38134),
(198119, 'ruRU', '', 'Ты печальна. Что это, боль утраты?', 38134),
(198118, 'ruRU', '', 'Ты связана с Королевой и с Арденвельдом, как и я. Но я рада, что тебе снится дом...', 38134),
(198116, 'ruRU', '', 'Я чувствую глубокую связь с этим миром. Но в моих снах я все еще парю над Азеротом.', 38134),
(198115, 'ruRU', 'Да, новобранец?', '', 38134),
(198112, 'ruRU', '', 'Копье! Сбейте ее копьем!', 38134),
(198111, 'ruRU', '', 'Смертные! Мое копье!', 38134),
(198108, 'ruRU', '', 'Я – тирненн, житель леса. Деревья – моя семья.\n\nКогда Королева Зимы взмахом своей руки сотворила Арденвельд, мы выросли одними из первых. Наши корни глубоко зарываются в землю, наши стволы высоко поднимаются в небо. Мы создали рощи.\n\nСрок тирненнов так долог, что другим существам не дано его даже осмыслить. И все это время мы служим рощам, но однажды все же возвращаемся в цикл.', 38134),
(198105, 'ruRU', '', 'Я повергну тебя!', 38134),
(198103, 'ruRU', '', 'Зачем ты меня тревожишь? Не видишь, я занята?', 38134),
(198099, 'ruRU', '', 'Это копье пронзит твое сердце!', 38134),
(198095, 'ruRU', '', 'Вождь, это тот самый рекрут.', 38134),
(198094, 'ruRU', 'Да, моя королева. Мы не подведем тебя.', '', 38134),
(198093, 'ruRU', '', 'Конечно не подведете.', 38134),
(198092, 'ruRU', '', 'Натанос расскажет тебе все, что нужно знать.', 38134),
(198089, 'ruRU', '', 'Чтобы победить Альянс в этой войне, Орде нужны могучие союзники.', 38134),
(198088, 'ruRU', '', 'Мне рассказывали о твоем мастерстве, $n. Оно мне понадобится.', 38134),
(198087, 'ruRU', '', 'А ты умеешь подбирать таланты, воевода.', 38134),
(198085, 'ruRU', '', 'Мне нужно... немного отдохнуть...', 38134),
(198084, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nДорогой дневник!\n\nТрудно поверить, что мы стоим на пороге свершения, о котором мечтали на протяжении бесчисленных эпох.\n\nЛеди Сибилла, принц и, что куда более важно, Габбинс верят в пилигрима.\n\nНу и я тоже, чего скрывать.\n\nВ связи с этим я решил установить с $gним:ней; связь душ. Как сказал принц, вместе мы сильнее, и я готов предоставить свою силу пилигриму.\n\nЯ верю, что Ревендрет будет восстановлен, Темные Земли – спасены, а чай – выпит до последней капли!\n\nИскренне твой (а теперь еще и $gего:ее;),\nТеотар\n\nP.S. Связь душ была удивительно приятным опытом. Я поделюсь с тобой всеми интимными подробностями позже!', '', 38134),
(198083, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nМой дорогой дневник,\n\nмне скучно. Пришли мне кого-нибудь.\n\nТвой покорнейший слуга,\nТеотар', '', 38134),
(198082, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nДорогой дневник!\n\nЯ в шоке от собственного поведения. Как я мог совершить такую страшную ошибку этим вечером?\n\nБоюсь, моя репутация будет навеки подмочена слухами, которые пойдут после того, что я натворил. Графиня наверняка уже все знает и обязательно расскажет остальным.\n\nЯ... должен ответить за свои ошибки. Надо придумать, как исправить положение.\n\nНе сомневайся, мой дорогой дневник, я никогда больше не помешаю чай против часовой стрелки. Я усвоил этот урок!', '', 38134),
(198081, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nСписок гостей на чайную вечеринку:\nЛеди Сибилла\nСэр Габбинс\nСэр Таббинс\nПочтенная Вулка\nКамнелоб <вычеркнуто, написано \"$p\">', '', 38134),
(198080, 'ruRU', '<На первой странице красивым каллиграфическим почерком Теотара выведено заглавие.>!\n\nСтрого официальный, совершенно секретный дневник Теотара. $p, друг мой, ты можешь его читать.\n\nНадеюсь, нас ждет много увлекательных совместных приключений!', '', 38134),
(198079, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nПравильный зонтик не дает сбиться с чинной походки.\nТщательное планирование ограждает от возможной боли.\nПроблемы множатся, пока ты не выпьешь чая, потому что лучше чая ничего нет!', '', 38134),
(198078, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nКакая чудесная прогулка у нас сегодня выдалась с леди Сибиллой!\n\nС нами были Габбинс и Таббинс, и старина Огарок трусил неподалеку, следя, чтобы нам никто не помешал.\n\nСобрав осколки зеркала на холме, мы заглянули в гости к одному знакомому, который нам кое-что задолжал.\n\nДолг был полностью возвращен.', '', 38134),
(198076, 'ruRU', '', 'Еще больше этих глупцов. Не дайте им помешать нам.', 38134),
(198075, 'ruRU', '', 'Еще одна группа стражников замка. Уничтожьте их.', 38134),
(198074, 'ruRU', '', 'Сюда идут новые стражники. Убейте их быстро, не тяните.', 38134),
(198072, 'ruRU', 'Нет! Я не вернусь в эту тюрьму!', '', 38134),
(198071, 'ruRU', 'Призываю силу солнца!', '', 38134),
(198070, 'ruRU', 'Восстань из пепла!', '', 38134),
(198069, 'ruRU', 'Вы все сгорите дотла!', '', 38134),
(198068, 'ruRU', 'Феломин ашал!', '', 38134),
(198067, 'ruRU', 'Пламя поглотит вас!', '', 38134),
(198066, 'ruRU', 'Все познают мой гнев!', '', 38134),
(198065, 'ruRU', 'Это все... что ты... можешь?..', '', 38134),
(198062, 'ruRU', '', 'Тирна Ваалу повезло по сравнению с другими рощами. Может, он выживет. Королева сделает для этого все возможное.', 38134),
(198061, 'ruRU', 'Вы, верно... шутите...', '', 38134),
(198057, 'ruRU', 'Хватит! Гори все огнем!', '', 38134),
(198056, 'ruRU', 'Я не вынесу этой муки!', '', 38134),
(198055, 'ruRU', 'Да... моя сила... возвращается!', '', 38134),
(198053, 'ruRU', 'Силы... возвращаются... ко мне...', '', 38134),
(198052, 'ruRU', 'Значит, те... кто убил меня... теперь меня спасают...', '', 38134),
(198051, 'ruRU', '$p, я рад, что мы наконец друг друга понимаем...\n\n<Теотар подмигивает.>\n\nОбязательно как-нибудь опять попьем чаю вместе.\n\nЛеди Сибилла справлялась о твоем здоровье.', '', 38134),
(198050, 'ruRU', 'Скоро мы останемся наедине.', '', 38134),
(198049, 'ruRU', 'Теперь ты познаешь истинные мучения.', '', 38134),
(198048, 'ruRU', 'Солнечный Скиталец... наш...', '', 38134),
(198047, 'ruRU', 'Сообщники Ренатала?! Стража! Сюда!', '', 38134),
(198045, 'ruRU', 'Гнев – сильнейший из твоих грехов, Кель\'тас. Он нам пригодится.', '', 38134),
(198044, 'ruRU', 'Когда я разобью оковы, ты сгоришь первым!', '', 38134),
(198043, 'ruRU', '', 'Мне что тут, вообще всех придется спасать?', 38134),
(198039, 'ruRU', '', 'Здесь Денатрий держит самые сильные души. Он усиливает их темные побуждения, превращая их в мощное оружие.', 38134),
(198033, 'ruRU', '', 'Нужно держаться друг друга. Поодиночке нас легко одолеют.', 38134),
(198031, 'ruRU', '', 'Не отходи, $n. Чары далеко не действуют.', 38134),
(198025, 'ruRU', '', 'Отправляйся по реке. Я прикрою тебя с берега. Надеюсь, скоро мы встретим настоящую Тиранду.', 38134),
(198018, 'ruRU', 'Я перенесу тебя в Элизийскую цитадель, когда скажешь.', '', 38134),
(198004, 'ruRU', '', 'Ничто не дается просто так – материалы для рукояти тоже придется добыть.', 38134),
(197971, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nА ты, дражайший пилигрим Утробы, во всем напоминаешь мне изящный зонтик!\n\nЯ пока еще не решил, что значит \"во всем\"... но я обожаю зонтики, а значит, это замечательный комплимент!', '', 38134),
(197970, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nИ ты, дражайший пилигрим Утробы, мыслишь совершенно верно.\n\nЯ назову в твою честь свой новый вкуснейший сорт чая.\n\nКак тебе название \"Горячий чай |3-1($p)\"?', '', 38134),
(197969, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nЯ полностью с тобой согласен, пилигрим.\n\nЯ пытался донести эту мысль до многих придворных Графини, но никто из них со мной не согласился.\n\nБыть может, нам удастся убедить их вместе.', '', 38134),
(197968, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nОчевидно, твое предыдущее письмо было адресовано Таббинсу.\n\nМожешь не волноваться. Я ему сразу его передал.\n\nЖелаю удачи в твоих будущих свершениях,\nТеотар', '', 38134),
(197967, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nМеня заинтересовало твое предложение, $p.\n\nДавай обсудим его во время долгой прогулки в Суши, укрываясь под моим зонтиком.\n\nЯ даже чай захвачу, если ты позаботишься об угощении.', '', 38134),
(197966, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nА после этого нам стоит выпить по чашечке чая... или двадцать чашечек.\n\nИногда от жажды просто спасу нет.', '', 38134),
(197965, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nДолжен признаться, что после прочтения твоего письма мое лицо стало краснее, чем набедренная повязка Денатрия!', '', 38134),
(197964, 'ruRU', '<Теотар написал ответ на ваше послание изящным почерком.>\n\nО, как это было дерзко!\n\nЯ был просто счастлив, когда обнаружил на полях твою заметку.\n\nИ, кстати говоря, я полностью согласен с твоим суждением.', '', 38134),
(197963, 'ruRU', '<Вы открываете случайную страницу, исписанную красивым каллиграфическим почерком Теотара.>\n\nКак давно я не видел надежды в глазах принца!\n\nЯ не уверен, что в его красных глазах пылает именно свет надежды, но во всяком случае это первый раз на моей памяти, когда это может быть она.', '', 38134),
(197957, 'ruRU', '', 'Отдохни – этот бой затянется надолго.', 38134),
(197951, 'ruRU', 'Весьма рад видеть тебя снова, $p. Как-нибудь обязательно выпьем еще чаю.\n\nЛеди Сибилла справлялась о твоем здоровье.', '', 38134),
(197945, 'ruRU', '<Этот телепорт может перенести вас на другие уровни башни.>', '', 38134),
(197942, 'ruRU', 'И тебе хорошего дня, маркиз. А нам пора!', '', 38134),
(197941, 'ruRU', 'Там и вправду так уютно! Спасибо, что заметил, маркиз.', '', 38134),
(197940, 'ruRU', 'Не стоит мешкать. Я люблю вот так прогуляться, или, скорее, пробежаться.', '', 38134),
(197917, 'ruRU', 'Я слыхал, что клыкастые в замке в последнее время стали еще более... клыкастыми. Осторожнее с ними. Они очень опасны, когда ведут себя... по-клыкастому.', '', 38134),
(197916, 'ruRU', 'Говорят, тебе довелось бывать в других царствах. А там землерои есть? \n\nМестные ребята, конечно, ничего, но после тысячи лет уж больно хочется встретить кого-то нового.', '', 38134),
(197915, 'ruRU', 'Здесь клыкастые не такие уж и плохие. Правда из-за этой засухи и они могут всадить нож в спину.', '', 38134),
(197912, 'ruRU', 'Эй! Не благодаря тебе ли Рендл и Дуборыл выбрались из Гиблотопи да еще и руки-ноги смогли унести? \n\nНе важно, что говорят клыкастые. Тебе здесь всегда рады. Мы, землерои, не забываем, когда за нас кто-то заступается.', '', 38134),
(197911, 'ruRU', 'Кстати, а насколько ты важная персона? К примеру, по шкале от рожденного из жижи до божества. \n\nПросто хочу понять, как лучше с тобой разговаривать.', '', 38134),
(197910, 'ruRU', 'Я слышал, ты тут что-то затеваешь. Слухи в этой дыре распространяются быстрее, чем души достигают Утробы.', '', 38134),
(197909, 'ruRU', 'Добро пожаловать в Темный Приют. Дом для тех, кого наказывают за жалость и зависть. \n\nОсторожнее с местными, если будешь у них что-то просить.', '', 38134),
(197908, 'ruRU', '', 'Это место пытается сбить нас с толку. Идем дальше. Я вижу впереди еще один след из трупов.', 38134),
(197907, 'ruRU', 'Ты не найдешь Ночную Воительницу!', '', 38134),
(197906, 'ruRU', '', 'Навсегда.', 38134),
(197905, 'ruRU', '', 'Глупое дитя. Из Утробы нет выхода. Но ты можешь остаться со мной...', 38134),
(197903, 'ruRU', '', 'Меня привели сюда сны. Мы спасем тебя.', 38134),
(197902, 'ruRU', '', 'Шандриса? Что ты здесь делаешь?', 38134),
(197901, 'ruRU', '', 'Минн\'до!', 38134),
(197879, 'ruRU', '', 'Еще одна жертва сломлена. Хватит с меня этих... существ. Мои охотники расправятся с остальными.', 38134),
(197877, 'ruRU', '', 'Вблизи эти паразиты еще отвратительнее.', 38134),
(197870, 'ruRU', 'Я вас лично препарирую!', '', 38134),
(197869, 'ruRU', 'Чудесно! Новые подопытные! Выпускайте бурильщиков!', '', 38134),
(197855, 'ruRU', 'Все готово к началу церемонии?', '', 38134),
(197854, 'ruRU', '', 'На всякий случай напоминаю, что я – летающий меч. Я легко могу пролететь между прутьями клетки.', 38134),
(197852, 'ruRU', '', 'Ого, сколько припасов! Этого хватит на много сетей душ.$b$bМогу обменять их на одну вещь, которая поможет тебе выживать в лесу – выходить из боя или подкрадываться к врагам.$b$bДоговорились?', 38134),
(197850, 'ruRU', 'Как хорошо, что ты здесь. У меня кончаются гормы, не с чем работать.$b$BПрипасы, говоришь? Ну, я не откажусь. Думаю, могу дать тебе взамен одну вещицу, которая будет призывать временных друзей. Что скажешь?', '', 38134),
(197848, 'ruRU', '', 'Припасы? Для меня? Но Владычица...$b$bО, я с радостью приму этот подарок. Я принесла набор для нанесения боевой раскраски и с радостью отдам его тебе взамен. Это придаст тебе скорость и силу охотника.', 38134),
(197846, 'ruRU', '', 'А, припасы из Лесной мастерской! Поскольку это мои владения, то будет лучше, если ты отдашь их на хранение мне.$b$bУ меня для тебя найдется кое-что, что придаст тебе скорости и поднимет настроение.', 38134),
(197843, 'ruRU', 'Пойдем-ка в \"Хвост виверны\". Угостишь меня!', '', 38134),
(197839, 'ruRU', 'Давай найдем наставника и узнаем про специализации!', '', 38134),
(197834, 'ruRU', '', 'Я восстановлю справедливость в этой вечности.', 38134),
(197833, 'ruRU', 'Будешь знать, как себя вести, грубиян!', '', 38134),
(197832, 'ruRU', 'Клянусь королевой, я очень на это надеюсь.', 'Клянусь королевой, я очень на это надеюсь.', 38134),
(197831, 'ruRU', 'Неужели... новая душа? В Арденвельд снова поступают души?', 'Неужели... новая душа? В Арденвельд снова поступают души?', 38134),
(197830, 'ruRU', '', 'Западный фланг окружен. Могрейну нужно подкрепление!', 38134),
(197829, 'ruRU', '', 'Здесь трудно телепортироваться, но думаю, что я смогу открыть портал туда, где остался мой посох.', 38134),
(197827, 'ruRU', '', 'Я не повторю эту ошибку.', 38134),
(197826, 'ruRU', '', 'Я ранена... дай мне минуту!', 38134),
(197820, 'ruRU', '', 'Ступай... без страха...', 38134),
(197819, 'ruRU', '', 'Боль... мучения!..', 38134),
(197817, 'ruRU', '', 'Это сделала Тиранда. Во сне я видела, как она убивает этих монстров.\n\nКажется, она оставила след из их трупов. Они свежие... она только что была тут!', 38134),
(197815, 'ruRU', '', 'Место из моих видений где-то впереди. Идем. Может быть, там мы все узнаем.', 38134),
(197814, 'ruRU', 'Я смотрю, его светлость Лорд-камергер дал мне работенку, но не сказал, когда завязывать. \n\nБуду вести экипаж, пока кто-нибудь с клыками или крыльями не остановит меня. Пригляди, чтобы со мной ничего не случилось. \n\nВсе землерои тебе очень благодарны за спасение от того пожирателя. Если что надо, ты только скажи.', '', 38134),
(197812, 'ruRU', '', 'Готово. Идем.', 38134),
(197811, 'ruRU', '', 'Когда мы найдем Андуина, посох позволит мне вернуть нас в безопасное место.', 38134),
(197810, 'ruRU', '', 'Я чую страх предательницы. Она в Ветхом хранилище.', 38134),
(197809, 'ruRU', 'Наши оставшиеся силы укрепятся здесь. Подайте знак, когда найдете камень.', '', 38134),
(197808, 'ruRU', 'Мы не можем просто оставить эти невинные души.', '', 38134),
(197807, 'ruRU', 'Ты уж там давай поосторожнее. И замолви за меня словечко вождю!', '', 38134),
(197806, 'ruRU', 'Я всегда знал, что ты прославишься, $n.', '', 38134),
(197805, 'ruRU', '', 'Вождь ждет тебя в крепости Громмаш.', 38134),
(197804, 'ruRU', '', 'Тебе приказано явиться в крепость Громмаш. Не заставляй вождя ждать...', 38134),
(197803, 'ruRU', '', '$n, у меня послание от вождя!', 38134),
(197799, 'ruRU', 'Что ты здесь делаешь? Тебе нельзя бродить здесь в одиночку, это слишком опасно.', 'Что ты здесь делаешь? Тебе нельзя бродить здесь в одиночку, это слишком опасно.', 38134),
(197798, 'ruRU', '', '$n! Как же я рада встретить союзника в этом странном месте. Прошу, помоги мне спасти Тиранду.', 38134),
(197797, 'ruRU', '', 'Хорошо. Я постараюсь усмирить боль... но знай, что забыть о ней я не смогу никогда.', 38134),
(197796, 'ruRU', 'Шандриса, мне тоже ведомы шрамы войны. Но помощь пилигрима – твой единственный шанс спасти Тиранду.', '', 38134),
(197795, 'ruRU', '', 'Я не собираюсь доверять солдату Орды жизнь той, что так мне дорога.', 38134),
(197794, 'ruRU', '', 'Из Утробы еще никто не возвращался. Во всяком случае, пока смертные не доказали обратное.', 38134),
(197789, 'ruRU', 'Не торопись. Мне есть, чем заняться.', '', 38134),
(197788, 'ruRU', 'Те другие землерои совсем не думают... везде инструменты разбросали.', '', 38134),
(197787, 'ruRU', 'Но больше ты от меня ничего не дождешься. Остальное ищи самостоятельно!', '', 38134),
(197786, 'ruRU', 'На первый раз одолжу тебе молот.', '', 38134),
(197782, 'ruRU', 'Не сметь тревожить хозяина!', '', 38134),
(197781, 'ruRU', '', 'Охота зовет, $n.', 38134),
(197779, 'ruRU', '', 'Будущее Азерота не обойдется без твоей защиты, $n!', 38134),
(197777, 'ruRU', '', 'Я хотела доставить эти души туда, где они смогут отдать свою аниму, но все пошло наперекосяк!\n\nПока не началась засуха, дикие звери в Арденвельде не были такими опасными. Мне надо добраться до Лощины Духов.\n\nПожалуйста, помоги мне!', 38134),
(197776, 'ruRU', '', 'Те\'зан! Ты привел помощь!', 38134),
(197775, 'ruRU', 'Тут не достанут! У тебя на голове!', '', 38134),
(197772, 'ruRU', 'Ты странное существо.', '', 38134),
(197770, 'ruRU', '', 'Пилигрим, бери полый клык и подай сигнал к началу охоты.', 38134),
(197768, 'ruRU', '', 'Вытяни из псаря Локси аниму, пилигрим Утробы.', 38134),
(197766, 'ruRU', 'Эта сделка потеряла смысл. Торги закрыты... До следующего раза.', '', 38134),
(197760, 'ruRU', 'Прекрасный выбор, $n!', '', 38134),
(197758, 'ruRU', '', 'Дайте мне минуту, и я сделаю нас невидимыми.', 38134),
(197754, 'ruRU', 'Джайна, ты сможешь спрятать нас от них?', '', 38134),
(197752, 'ruRU', '', 'Камень реагирует на тебя, $n! Активируй его, мы задержим армию Тюремщика!', 38134),
(197748, 'ruRU', 'Смерть заберет душу вашего мира! Все, что вы видите, все, что вы знаете, исчезнет!', '', 38134),
(197747, 'ruRU', '', 'Камень почти заряжен! Держитесь!', 38134),
(197746, 'ruRU', 'Полчищами нас не испугать. Мы не сдадимся!', '', 38134),
(197745, 'ruRU', 'Моим воинам нет числа!', '', 38134),
(197744, 'ruRU', '', 'Сдерживайте их! Камень должен полностью зарядиться!', 38134),
(197743, 'ruRU', 'Верные Утробе, схватите их!', '', 38134),
(197742, 'ruRU', 'Реликвия Предвечных... реагирует на смертного?! Невозможно!', '', 38134),
(197740, 'ruRU', '', 'Мои чары помогут нам пройти мимо стражей... но обмануть удастся не всех.', 38134),
(197739, 'ruRU', 'У Тюремщика слишком много солдат. Мы не справимся с целой армией.', '', 38134),
(197738, 'ruRU', 'Все сюда! Этот барьер защитит нас!', '', 38134),
(197736, 'ruRU', 'Знал бы я, что, выдав одного клыкастого другому, попаду на болото, молчал бы в тряпочку. Не найдется рубинов для моего факела?', '', 38134),
(197734, 'ruRU', '', 'Твои грехи убьют твоих друзей.', 38134),
(197733, 'ruRU', '', 'Умрите вместе!', 38134),
(197732, 'ruRU', '', 'Ваши судьбы связаны!', 38134),
(197731, 'ruRU', '', 'Нет искупления вашим грехам!', 38134),
(197730, 'ruRU', '', 'Ваши грехи будут мучить вас вечно.', 38134),
(197729, 'ruRU', '', 'Сейчас я вытяну из вас грехи.', 38134),
(197728, 'ruRU', '', 'Катастрофа!', 38134),
(197727, 'ruRU', '', 'Земля содрогается!', 38134),
(197726, 'ruRU', '', 'Грядет разрушение!', 38134),
(197725, 'ruRU', '', 'Тьма скует ваши сердца!', 38134),
(197724, 'ruRU', '', 'Грех гложет твое сердце!', 38134),
(197723, 'ruRU', '', 'Я раздавлю твое сердце!', 38134),
(197722, 'ruRU', '', 'Должно быть, так велит Предназначение. Оно всегда указывает нам путь.', 38134),
(197721, 'ruRU', '', 'От этого создания разит Утробой!', 38134),
(197720, 'ruRU', '', 'Поток анимы уничтожит всех вас!', 38134),
(197718, 'ruRU', '', 'Благодарю тебя.', 38134),
(197717, 'ruRU', '', 'Эта душа была в Утробе? Я отказываюсь в это верить!', 38134),
(197716, 'ruRU', '', 'Все происходит так быстро! Моя роща умирает!', 38134),
(197710, 'ruRU', '', 'Это испытание нашей веры в Предназначение?', 38134),
(197709, 'ruRU', '', 'Проникновение? Как это возможно?', 38134),
(197708, 'ruRU', '', 'Эта душа что, выбралась из Утробы? Не может быть!', 38134),
(197705, 'ruRU', 'Дом Ритуалов проводит здесь обряд усиления. Если не остановишь его, уничтожь то, что он произведет на свет.', '', 38134),
(197704, 'ruRU', 'Гнусная магия ритуалистов им не помогла: их кости ничуть не крепче прочих. Браво.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197703, 'ruRU', 'Благодарю тебя. Забвение – слишком легкая кара для тех, кто предал Крексуса.', '', 38134),
(197702, 'ruRU', 'Здесь укрепились предатели из моего бывшего дома. Уничтожь их.', '', 38134),
(197701, 'ruRU', 'Но как мне проверить ее подлинность?', '', 38134),
(197695, 'ruRU', '', 'Ораномонос сошла с ума, пока ее пожирали гормы. Прошу, избавь бедняжку от мучений.', 38134),
(197694, 'ruRU', '', 'Сегодня гормы останутся ни с чем. Прощай. Ты была истинной защитницей Арденвельда.', 38134),
(197691, 'ruRU', 'Собери с жителей деревни как можно больше податей и прочеши округу, чтобы ни капли не пропало.', 'Собери с жителей деревни как можно больше податей и прочеши округу, чтобы ни капли не пропало.', 38134),
(197689, 'ruRU', 'В чем смысл всех этих ритуалов, если нашим подопечным все равно грозит Утроба?', 'В чем смысл всех этих ритуалов, если нашим подопечным все равно грозит Утроба?', 38134),
(197688, 'ruRU', 'Помоги мне завершить ритуал покаяния. Тогда мы сможем поговорить.', 'Помоги мне завершить ритуал покаяния. Тогда мы сможем поговорить.', 38134),
(197687, 'ruRU', 'Не думал, что они убьют друг друга, но я готов на все ради общего блага. Денатрий должен уйти. Прощай.', 'Не думала, что они убьют друг друга, но я готова на все ради общего блага. Денатрий должен уйти. Прощай.', 38134),
(197676, 'ruRU', 'Не боись, запашок приятный!', '', 38134),
(197675, 'ruRU', 'Гнилблин, осторожнее! От тебя слишком много мусора!', '', 38134),
(197674, 'ruRU', 'Как не вовремя! Ты что, не видишь – у нас гости?!', '', 38134),
(197673, 'ruRU', 'Я не вовремя?', '', 38134),
(197672, 'ruRU', 'Что ж, тогда... Надеюсь, всем нравится огонь!', '', 38134),
(197671, 'ruRU', 'Я, хм... боюсь... у нас нет ведер с водой...', '', 38134),
(197669, 'ruRU', '', 'Кажется, я знаю, где найти еще одно зеркало души. Встретимся в пещере к северу отсюда.', 38134),
(197668, 'ruRU', '', 'Пока ты видишь цель, тебя нельзя победить по-настоящему.', 38134),
(197665, 'ruRU', '', 'Твое время еще не пришло, дитя Азерота.', 38134),
(197664, 'ruRU', '', 'С поражениями приходит мудрость.', 38134),
(197663, 'ruRU', '', 'Некоторые задачи бывают непосильными даже для лучших из нас.', 38134),
(197662, 'ruRU', '', 'Уверена, в следующий раз союзник не даст тебе погибнуть.', 38134),
(197661, 'ruRU', '', 'В следующий раз получится лучше.', 38134),
(197660, 'ruRU', '', 'Этого нельзя было предвидеть.', 38134),
(197659, 'ruRU', 'Видимо, стоило прийти в более строгом костюме.', '', 38134),
(197658, 'ruRU', 'Некоторые традиции можно и нарушить.', '', 38134),
(197657, 'ruRU', 'А здесь не скучно!', '', 38134),
(197656, 'ruRU', 'Расслабьтесь, успокойтесь. Всем иногда нужно отдыхать.', '', 38134),
(197655, 'ruRU', 'Какой великолепный декаданс!', '', 38134),
(197654, 'ruRU', 'В простоте тоже есть свой шарм.', '', 38134),
(197653, 'ruRU', 'Опасность разгоняет скуку.', '', 38134),
(197651, 'ruRU', 'Как здесь чисто и здорово!', '', 38134),
(197650, 'ruRU', 'Смотри под ноги! Мы здесь порядком насорили.', '', 38134),
(197635, 'ruRU', 'Безусловная победа. Встретимся у Жуткой Лощины, $n.', '', 38134),
(197633, 'ruRU', 'Еще раз спасибо за оказанную честь.\n\nПриятно знать, что принц думает о будущем, но не забывает о традициях и приличиях.', '', 38134),
(197632, 'ruRU', 'По-моему, очень даже впечатляет! Это поможет тебе защищать Арденвельд.', '', 38134),
(197631, 'ruRU', 'Наши ремесленники славятся своим мастерством. Но для этого им нужны материалы. Поможешь?', '', 38134),
(197630, 'ruRU', 'Слушаюсь!', '', 38134),
(197629, 'ruRU', '', 'Эй! Скажи барону, что без анимы не видать ему своего костяного стража!', 38134),
(197628, 'ruRU', 'Замечательный день для вечеринки! Я как раз говорил друзьям, что хочу где-нибудь отдохнуть.\n\nПравда, планировал для этого более... цветущее место, но и от Ревендрета не откажусь!\n\nАх, да! Надеюсь, ты не будешь против, если я приведу друзей... Ты ведь и так собираешься их пригласить?', '', 38134),
(197627, 'ruRU', '', 'Приступаем?', 38134),
(197626, 'ruRU', 'Сегодня Архонт милостиво приказала мне веселиться в этом дальнем краю.\n\nНе сомневайся, ни один враг славной предводительницы Бастиона не проникнет на твой прием!\n\n<Полемарх Адрест стоит навытяжку и подозрительно осматривает других гостей.>', '', 38134),
(197625, 'ruRU', '', 'Когда при жизни я была служанкой королевы Азшары, то посещала тысячи роскошных приемов.\n\nА будучи малдраксийской баронессой, видела, как даже самые гениальные стратегии и многообещающие маневры проваливаются из-за малейшей оплошности.\n\nВпечатлить меня будет непросто.', 38134),
(197624, 'ruRU', '', 'Снова приказы? Что на этот раз?', 38134),
(197623, 'ruRU', '', 'Я предпочитаю ездить верхом на лютом волке, а не в его желудке.', 38134),
(197622, 'ruRU', '', 'Она вытягивает аниму! Используй флакон, $p!', 38134),
(197621, 'ruRU', '', 'Внимание! Проигравший будет таскать металл в кузню! А теперь в бой!', 38134),
(197620, 'ruRU', 'Анимы осталось так мало, что мне пришлось проститься со многими дремлющими духами. Друзьями, за которыми я ухаживал много веков.\n\nНо Старейшая Данмира уже близка к пробуждению, я не мог взять и попрощаться с ней просто так! \n\nЯ перенес ее дикое семя туда, где она могла бы получить еще немного анимы. И, может, ей бы этого хватило, но та ужасная тварь ее похитила! Думаю, птица хочет высиживать семя как яйцо.', '', 38134),
(197614, 'ruRU', '', 'Столько усилий, чтобы добраться до меня, и все напрасно. Покончим с этим.', 38134),
(197611, 'ruRU', 'Новые приказы! Движемся на запад! Вперед!', '', 38134),
(197603, 'ruRU', 'Это наш шанс. Держать оборону!', '', 38134),
(197602, 'ruRU', 'Это личная коллекция сира Денатрия. Собранная в Темных Землях за много эпох.', '', 38134),
(197601, 'ruRU', 'Как интересно. Стража разбежалась...', '', 38134),
(197598, 'ruRU', 'Бесконечные армии служат мне!', '', 38134),
(197597, 'ruRU', 'Эта сила искажает реальность!', '', 38134),
(197595, 'ruRU', 'Этой реликвии боятся даже боги!', '', 38134),
(197594, 'ruRU', '', 'Мне надоели ваши уловки. Покажитесь, и я, так уж и быть, прикончу вас быстро.', 38134),
(197593, 'ruRU', 'Сила магии переполняет меня!', '', 38134),
(197590, 'ruRU', 'Умрите вместе!', '', 38134),
(197589, 'ruRU', 'Космос зовет!', '', 38134),
(197571, 'ruRU', 'Да уж, далековато. Держись ко мне поближе.', '', 38134),
(197570, 'ruRU', 'Они захватили Гробницу Знаний.', '', 38134),
(197569, 'ruRU', 'Эти существа идут через этот туннель.', '', 38134),
(197564, 'ruRU', 'Смотрите под ноги!', '', 38134),
(197563, 'ruRU', 'Со смертью не поторгуешься!', '', 38134),
(197562, 'ruRU', 'Полное уничтожение!', '', 38134),
(197559, 'ruRU', 'Как приятно вернуться в Оргриммар!', '', 38134),
(197552, 'ruRU', '', 'Ах, какой же ты скучный! Будешь хорошо себя вести – дам заколоть кого-нибудь из прислуги.', 38134),
(197551, 'ruRU', 'Я этих ваших фривольностей не люблю. Поле битвы – вот где настоящее веселье!', '', 38134),
(197550, 'ruRU', '', 'Милый мой, какие пустяки! Не скупись на вино, и я всегда буду рада заглянуть.', 38134),
(197549, 'ruRU', 'Спасибо, что пришли. Что за это был бы вечер без вас двоих?', '', 38134),
(197548, 'ruRU', 'Хватит! Немедленно прекратить этот балаган!', '', 38134),
(197547, 'ruRU', 'Какая грация, какая пластика! А теперь – умрите.', '', 38134),
(197545, 'ruRU', 'Па вправо!', '', 38134),
(197544, 'ruRU', 'Па вправо!', '', 38134),
(197543, 'ruRU', 'Шажок влево!', '', 38134),
(197542, 'ruRU', 'Шажок влево!', '', 38134),
(197541, 'ruRU', 'Жете вперед!', '', 38134),
(197540, 'ruRU', 'Жете вперед!', '', 38134),
(197539, 'ruRU', 'Бризе назад!', '', 38134),
(197538, 'ruRU', 'Бризе назад!', '', 38134),
(197537, 'ruRU', '', 'Бризе назад!', 38134),
(197536, 'ruRU', '', 'Па вправо!', 38134),
(197535, 'ruRU', '', 'Шажок влево!', 38134),
(197534, 'ruRU', '', 'Жете вперед!', 38134),
(197533, 'ruRU', 'Все на позиции! Пришло время для пляски смерти!', '', 38134),
(197532, 'ruRU', 'Слушай мою команду! Начинаем пляску смерти!', '', 38134),
(197531, 'ruRU', '', 'Любезные гости и гнусные чужаки, все по местам – начнем пляску смерти!', 38134),
(197530, 'ruRU', '', 'Не думаю, что дело в нас. Кажется, камень реагирует на тебя, $n.', 38134),
(197529, 'ruRU', 'Как же его активировать?', '', 38134),
(197528, 'ruRU', 'Путевой камень не молчит – похоже, он реагирует на нас!', '', 38134),
(197525, 'ruRU', 'Наши разведчики видели Охотницу по дороге в Жуткую Лощину. Подготовься, и поскорее.', '', 38134),
(197521, 'ruRU', '', 'Мои сокрушители надежды пали, но теперь я знаю, где ты. Все кончено.', 38134),
(197518, 'ruRU', 'Время – деньги, $n. Идем!', '', 38134),
(197516, 'ruRU', '', 'Встретимся у особняка в дальнем конце деревни.', 38134),
(197515, 'ruRU', 'У тебя важная задача. Чтобы все вышло, ловушка должна быть безупречной.', '', 38134),
(197508, 'ruRU', 'Я должен был заботиться о них... но их увели!', '', 38134),
(197507, 'ruRU', 'Легкое Перо! Сюда!', '', 38134),
(197505, 'ruRU', '', 'Сон... отпускает меня...', 38134),
(197500, 'ruRU', '', 'Вам... не превзойти... меня...', 38134),
(197493, 'ruRU', '', 'Вас не приглашали!', 38134),
(197489, 'ruRU', '', 'Следуй пути, пилигрим.', 38134),
(197487, 'ruRU', 'Последний танец! Последний в вашей жизни!', '', 38134),
(197485, 'ruRU', 'Найдите себе партнера... или умрите!', '', 38134),
(197483, 'ruRU', 'Танцевать! Всем танцевать!', '', 38134),
(197482, 'ruRU', 'Танцуйте, друзья! Сбивайте ноги в кровь!', '', 38134),
(197476, 'ruRU', 'Но ведь я... душа... компании...', '', 38134),
(197472, 'ruRU', 'Вы посмели явиться на мой бал... в этом?!', '', 38134),
(197465, 'ruRU', '', 'Смотри в небо. Ларионы проводят там большую часть времени!', 38134),
(197464, 'ruRU', 'Пойми, такое количество анимы любого сведет с ума. Прощай!', 'Пойми, такое количество анимы любого сведет с ума. Прощай!', 38134),
(197463, 'ruRU', 'Жаль, что мою записку о податях нашли. Но стоило пустить пару слухов, как они тут же вцепились друг другу в глотки.', 'Жаль, что мою записку о податях нашли. Но стоило пустить пару слухов, как они тут же вцепились друг другу в глотки.', 38134),
(197462, 'ruRU', 'О! Какой бардак!', 'О! Какой бардак!', 38134),
(197460, 'ruRU', '', 'Я отчитаюсь перед вождем. А Корк пока что проведет тебя по городу.', 38134),
(197459, 'ruRU', 'Что это за стратегия... такая...', '', 38134),
(197454, 'ruRU', 'Наконец-то битва! Превосходно!', '', 38134),
(197453, 'ruRU', '', 'Сир... Я подвела тебя...', 38134),
(197452, 'ruRU', '', 'Вашему жалкому восстанию конец.', 38134),
(197451, 'ruRU', '', 'Я заберу твою аниму!', 38134),
(197450, 'ruRU', '', 'Грязные предатели.', 38134),
(197449, 'ruRU', '', 'Я изрежу ваши души!', 38134),
(197448, 'ruRU', 'Это невозможно! Я... вечен...', '', 38134),
(197447, 'ruRU', 'Вашему восстанию конец.', '', 38134),
(197446, 'ruRU', 'Моя власть неоспорима.', '', 38134),
(197445, 'ruRU', 'Вот и конец твоему бунту!', '', 38134),
(197443, 'ruRU', 'Ваша гордыня вас погубит!', '', 38134),
(197439, 'ruRU', '', 'Нам придется сдерживать их до пробуждения камня, иначе ничего не выйдет. Важна каждая секунда!', 38134),
(197438, 'ruRU', '', 'Когда Тюремщик поймет, что мы делаем, он бросит против нас все силы.', 38134),
(197434, 'ruRU', '', 'Я была одной из лучших... Прости меня, Владыка.', 38134),
(197432, 'ruRU', '', 'Кажется, глупая добыча сама пришла к хищнику!', 38134),
(197425, 'ruRU', '', 'Нужно добраться до него, пока Тюремщик нас не заметил.', 38134),
(197424, 'ruRU', '', 'Смотрите – на платформе! Это же путевой камень!', 38134),
(197423, 'ruRU', 'Даже такая участь лучше, чем то, что грозит тебе...', '', 38134),
(197422, 'ruRU', 'Ха-ха! Первая кровь будет за мной!', '', 38134),
(197421, 'ruRU', '', 'За Арденвельд!', 38134),
(197420, 'ruRU', '', 'За Арденвельд!', 38134),
(197419, 'ruRU', '', 'За Арденвельд!', 38134),
(197418, 'ruRU', 'За Арденвельд!', '', 38134),
(197417, 'ruRU', 'Будет сделано, моя Королева.', '', 38134),
(197416, 'ruRU', 'Она... тебя прикончит.', '', 38134),
(197415, 'ruRU', 'Ага! Мне хорошо заплатят за твою голову.', '', 38134),
(197414, 'ruRU', '', 'Друсты думают, что победят, если захватят рощу Пробуждения. Они ошибаются.', 38134),
(197412, 'ruRU', 'Хм... Никак не нападу на след.', '', 38134),
(197410, 'ruRU', '', 'Здесь был пилигрим Утробы? Говори, не то пущу тебе кровь!', 38134),
(197409, 'ruRU', '', 'Лунная Ягода, Герн, Алиот, Зайхед! Отбейте маяки в этой роще. Наша связь поможет мне изгнать захватчиков!', 38134),
(197408, 'ruRU', 'Боль... что ты чуешь? Пилигрим Утробы явно где-то здесь.', '', 38134),
(197405, 'ruRU', 'Ты порочишь это царство своим присутствием. Уходи!', 'Ты порочишь это царство своим присутствием. Уходи!', 38134),
(197404, 'ruRU', 'Брокеры готовы вести дела с кем угодно...', 'Брокеры готовы вести дела с кем угодно...', 38134),
(197403, 'ruRU', 'Не обращайся ко мне, смертная душа!', 'Не обращайся ко мне, смертная душа!', 38134),
(197402, 'ruRU', '', 'Мне нужно залечить раны. Помоги защитникам Шпилей вместо меня!', 38134),
(197394, 'ruRU', 'Изумительно!', '', 38134),
(197393, 'ruRU', 'Вы годитесь только на мишени!', '', 38134),
(197392, 'ruRU', 'Прекрасно! Живые мишени, чтобы испытать эти артефакты!', '', 38134),
(197391, 'ruRU', 'Теперь... ты... вожак...', '', 38134),
(197390, 'ruRU', 'Еще одна славная охота.', '', 38134),
(197389, 'ruRU', 'Мои псы с радостью обглодают твои кости.', '', 38134),
(197388, 'ruRU', 'Сегодня мои псы попируют.', '', 38134),
(197387, 'ruRU', 'Охота началась!', '', 38134),
(197385, 'ruRU', '', 'Пелагий, я... Да, конечно.', 38134),
(197384, 'ruRU', 'Клейя, с такой раной ты не можешь сражаться. Позволь мне помочь.', '', 38134),
(197379, 'ruRU', 'Прибью камнем!', '', 38134),
(197377, 'ruRU', 'Нечестно.', '', 38134),
(197376, 'ruRU', 'Хватит бегать.', '', 38134),
(197375, 'ruRU', 'Камень тверже, чем голова.', '', 38134),
(197374, 'ruRU', 'Ай!', '', 38134),
(197373, 'ruRU', 'Растопчу!', '', 38134),
(197371, 'ruRU', 'Сокрушу!', '', 38134),
(197370, 'ruRU', 'Раздавлю!', '', 38134),
(197369, 'ruRU', 'Получайте!', '', 38134),
(197368, 'ruRU', 'Хозяин... будет... сердиться...', '', 38134),
(197366, 'ruRU', 'Люблю, когда под ногами хрустит.', '', 38134),
(197365, 'ruRU', 'Раздавил!', '', 38134),
(197364, 'ruRU', 'Хозяин будет рад!', '', 38134),
(197363, 'ruRU', 'Я вас расплющу!', '', 38134),
(197362, 'ruRU', '', 'Эта засуха грозит всему лесу. А твой голос – всего лишь трель одного соловья.', 38134),
(197358, 'ruRU', 'Кто это тут шумит?!', '', 38134),
(197357, 'ruRU', '', 'Благодаря тебе я исполнила свою мечту – переродиться. Для меня честь связать мою душу с твоей.', 38134),
(197356, 'ruRU', 'Мы были в восторге, когда узнали о твоем возвращении. И мы рады, что можем называть тебя другом!', '', 38134),
(197355, 'ruRU', '', 'Но я нашла в тебе нечто большее. Сильного союзника, хорошего товарища... друга.', 38134),
(197353, 'ruRU', '', 'Я повелеваю силой самого Бастиона!', 38134),
(197352, 'ruRU', '', 'В моей руке копье Архонта!', 38134),
(197349, 'ruRU', 'Представь, каким будет следующий... когда мы придем в себя после этого.', '', 38134),
(197347, 'ruRU', 'Вот это был прием... Я без сил!', '', 38134),
(197346, 'ruRU', 'А ну, подтянитесь! Провозглашаю вечеринку!', '', 38134),
(197345, 'ruRU', 'Я все сделаю в лучшем виде! Только скажи, что от меня требуется!', '', 38134),
(197344, 'ruRU', 'Что? Поверь мне, это какая-то ошибка. Успокойтесь и опустите арбалеты, иначе я буду стрелять!', 'Что? Поверь мне, это какая-то ошибка. Успокойтесь и опустите арбалеты, иначе я буду стрелять!', 38134),
(197343, 'ruRU', 'Инстинкты? У тебя? Забавно... К тому же мне одна мышка нашептала, что Самю прикарманивает аниму из податей. Вот кто настоящий предатель.', 'Инстинкты? У тебя? Забавно... К тому же мне одна мышка нашептала, что Самю прикарманивает аниму из податей. Вот кто настоящий предатель.', 38134),
(197342, 'ruRU', 'Ее записка о податях, к примеру. Но и это не важно. Доказательства не нужны, когда есть инстинкты.', 'Ее записка о податях, к примеру. Но и это не важно. Доказательства не нужны, когда есть инстинкты.', 38134),
(197341, 'ruRU', 'Ну же, Лахос. Оставь Илку в покое. Опусти арбалет. Какие у тебя доказательства?', 'Ну же, Лахос. Оставь Илку в покое. Опусти арбалет. Какие у тебя доказательства?', 38134),
(197340, 'ruRU', '', 'Теперь можно будет перенести их в другие рощи и восстановить равновесие. Спасибо!', 38134),
(197339, 'ruRU', '', 'Мотыльков в Мерцающей роще становится слишком много! Поможешь собрать их коконы с верхушек деревьев?', 38134),
(197334, 'ruRU', 'Ха-ха! Понятия не имею, о чем ты, Лахос. Ты ошибаешься.', 'Ха-ха! Понятия не имею, о чем ты, Лахос. Ты ошибаешься.', 38134),
(197320, 'ruRU', '', 'Котел Зоваала хорошо укреплен и полон воинов Тюремщика. Будет лучше, если мы проскользнем туда незаметно.\n\nДай сигнал, и я скрою нас от посторонних глаз.', 38134),
(197314, 'ruRU', '', 'Раскольники уходят... по крайней мере, из цитадели. Возвращайся к нам.', 38134),
(197308, 'ruRU', 'Ух как страшно!', 'Ух как страшно!', 38134),
(197303, 'ruRU', '', 'Обязательно.', 38134),
(197298, 'ruRU', 'Я предупрежу всех за тебя, но Клейя... пожалуйста, вернись.', '', 38134),
(197297, 'ruRU', '', 'Раскольники могут напасть снова. Надо сообщить в Обитель Героев.', 38134),
(197295, 'ruRU', '', 'Мои послушники нужны здесь, а я должна обо всем доложить Архонту. Служи верно. И не теряй время!', 38134),
(197290, 'ruRU', '', 'Если нам повезет, воспоминания Лисонии откроют ответ.', 38134),
(197288, 'ruRU', '', 'Отнеси этот фрагмент в Мнемонический Локус, где хранятся воспоминания кирий.', 38134),
(197287, 'ruRU', '', 'Я доложу Архонту о том, что здесь случилось... и мы взыщем с них за это преступление.', 38134),
(197286, 'ruRU', '', 'Утроба извратила сущность Лисонии. Нужно узнать, как это случилось.', 38134),
(197274, 'ruRU', 'Скоро Шпили Перерождения будут нашими. Девия спасет Темные Земли от засухи и приведет нас к лучшему будущему!', '', 38134),
(197273, 'ruRU', 'Как-то раз я поймал неизвестную рыбу. Назвал ее в честь себя.\n\nНо больше эту рыбу никто никогда не видел, так что мне не поверили.', '', 38134),
(197272, 'ruRU', '', 'Это... все меняет. Возможно, у Дреки еще есть надежда на победу.', 38134),
(197268, 'ruRU', '', 'Это отголоски ее сокровенных воспоминаний. Они раскроют причину предательства.', 38134),
(197267, 'ruRU', 'Не могу говорить. Мне нужно заточить оружие. Вдруг камнерожденные проснутся, и оно понадобится? \n\nПроисходит это редко, но если они просыпаются, то страшно сердятся, если под рукой нет заточенного оружия.', '', 38134),
(197266, 'ruRU', 'Иди сюда, смертная душа. Пусть нас рассудит честный бой.', '', 38134),
(197265, 'ruRU', 'Better them than being chopped up for parts.', 'Better them than being chopped up for parts.', 38134),
(197264, 'ruRU', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 'Are we really going to ally with them? I still suspect their involvement in the \"accident\".', 38134),
(197263, 'ruRU', 'We don\'t have options now. This is our best chance for success.', 'We don\'t have options now. This is our best chance for success.', 38134),
(197259, 'ruRU', '', 'Принц Ренатал устраивает эти приемы, чтобы добиться расположения других влиятельных обитателей Темных Земель.\n\nМы не должны допустить, чтобы наши гости ушли разочарованными.\n\nКроме того, покидая Пепельный двор, каждый гость оставит принцу свой дар.\n\nДовольные гости будут оставлять более щедрые дары.\n\nЭто должно тебя заинтересовать.', 38134),
(197258, 'ruRU', '', '<Странное древо на дороге не отвечает. Кажется, оно спит.>', 38134),
(197257, 'ruRU', '', 'Как ты понимаешь, разным гостям нравятся разные приемы. Одни предпочитают церемониальные и серьезные мероприятия, другие чувствуют себя свободнее в неформальной или даже опасной обстановке.\n\nНаша задача как хозяев Пепельного Двора – подобрать атмосферу приема под вкусы гостей.\n\nЭто можно сделать, разработав особую программу, например, подобрав подходящие угощения и развлечения.\n\nМы также можем принимать участие в различных мероприятиях в ходе приема, чтобы еще больше приблизиться к идеальной атмосфере.\n\nЕсли не знаешь, какой прием предпочитают гости, обратись к списку гостей Пепельного Двора, составленному лордом Гарриданом.\n\nА наши слуги расскажут, какие из предлагаемых мероприятий придутся им по вкусу.', 38134),
(197255, 'ruRU', '', 'Что... Что это такое?', 38134),
(197251, 'ruRU', '', 'Эта сила... Не может быть!', 38134),
(197249, 'ruRU', '', 'Сгиньте в Утробе!', 38134),
(197248, 'ruRU', '', 'Идеал умрет! Не смейте вмешиваться!', 38134),
(197247, 'ruRU', '', 'Я всегда считала, что завоевать расположение других очень просто.\n\nГлавное – узнать, что им нужно, и дать им это.', 38134),
(197243, 'ruRU', '', 'Судьба снова преподносит нам сюрприз. Мы позаботимся о чужаке.', 38134),
(197227, 'ruRU', 'Можешь не воспринимать мои слова всерьез, но я знаю, что ты предала деревню, Илка.', 'Можешь не воспринимать всерьез, но я знаю, что ты предала деревню, Илка.', 38134),
(197226, 'ruRU', 'Ну же, Лахос. Расскажи нам все! Кто и что кому сделал? Просто восхитительно.', 'Ну же, Лахос. Расскажи нам все! Кто и что кому сделал? Просто восхитительно.', 38134),
(197223, 'ruRU', 'Чего тебе, Лахос? Я занят, в отличие от тебя.', 'Чего тебе, Лахос? Я занята, в отличие от тебя.', 38134),
(197182, 'ruRU', '', 'А-ха!', 38134),
(197181, 'ruRU', 'House of Plagues. I was out on assignment when the accident happened.', 'House of Plagues. I was out on assignment when the accident happened.', 38134),
(197180, 'ruRU', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 'I am skilled at alchemy, including gathering, preparing, and storing reagents.', 38134),
(197179, 'ruRU', 'Good. It will speed up your training considerably if you are already skilled.', '', 38134),
(197178, 'ruRU', 'I am a mage. I can channel anima.', 'I am a mage. I can channel anima.', 38134),
(197177, 'ruRU', 'Ты что, не видишь? Я занят.', 'Ты что, не видишь? Я занята.', 38134),
(197176, 'ruRU', 'Do you have any other skills or abilities we should know about?', '', 38134),
(197175, 'ruRU', 'Which house were you originally affiliated with before now?', '', 38134),
(197174, 'ruRU', 'Are you able to or were you previously able to cast spells? If so, what kind?', '', 38134),
(197172, 'ruRU', 'This isn\'t a hypothetical question. Don\'t take it lightly.', '', 38134),
(197171, 'ruRU', 'Yes.', 'Yes.', 38134),
(197170, 'ruRU', 'Are you willing to pledge your afterlife to Margrave Sin\'dane and to the service of the House of Rituals?', '', 38134),
(197168, 'ruRU', '', 'Их командир – в зале для аудиенций. Останови его!', 38134),
(197162, 'ruRU', 'Мелкие паразиты!', '', 38134),
(197161, 'ruRU', 'Ха!', '', 38134),
(197160, 'ruRU', '', 'От меня не спрятаться!', 38134),
(197146, 'ruRU', 'Бвонсамди шлет смертных сражаться за себя? Я всегда знал, что он трус.', '', 38134),
(197145, 'ruRU', 'До меня он был ничтожеством! И скоро снова им станет!', '', 38134),
(197144, 'ruRU', 'Я столько сделал для Бвонсамди... Превратил из жреца в лоа, дал ему силу Смерти... И все это он решил обратить против меня?!', '', 38134),
(197143, 'ruRU', 'Зря ты решил предать Мве\'залу. Теперь мы враги.', '', 38134),
(197142, 'ruRU', 'Видишь, какую силу я даю последователям, Бвонсамди?', '', 38134),
(197141, 'ruRU', '', 'Жалкая жертва!', 38134),
(197140, 'ruRU', '', 'Смерть... Смерть!', 38134),
(197139, 'ruRU', 'Теперь ясно, кто сильнее?', '', 38134),
(197138, 'ruRU', 'Раздавлю!', '', 38134),
(197135, 'ruRU', 'This is the line for application to begin testing. If you have no magical ability, you\'re wasting everyone\'s time.', '', 38134),
(197134, 'ruRU', '', 'О, ты опять тут. Не ожидала.', 38134),
(197130, 'ruRU', 'Мы спасены! Спасибочки тебе!', 'Мы спасены! Спасибочки тебе!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197129, 'ruRU', 'Оно движется!', 'Оно движется!', 38134),
(197128, 'ruRU', 'Мне его ударить?', 'Мне его ударить?', 38134),
(197127, 'ruRU', 'Сделай что-нибудь! Сделай что-нибудь!', 'Сделай что-нибудь! Сделай что-нибудь!', 38134),
(197121, 'ruRU', 'Интересный у тебя вид... это настоящие уши?', 'Интересный у тебя вид... это настоящие уши?', 38134),
(197114, 'ruRU', '', 'К счастью, на нашей стороне пилигрим Утробы. Вот уж кто не боится рисковать.', 38134),
(197113, 'ruRU', 'Твой план может сработать, но это будет опасно.', '', 38134),
(197100, 'ruRU', '', 'Тебе негде спрятаться. Мы обыщем весь Ревендрет, если придется.', 38134),
(197099, 'ruRU', '', 'Давай, беги. Сокрушители надежды все равно тебя найдут.', 38134),
(197098, 'ruRU', '', 'Встретимся на месте, пилигрим. Я предупрежу наших союзников о надвигающейся угрозе.', 38134),
(197095, 'ruRU', '', 'Превосходно. А теперь покажи ему, что бывает за неповиновение.', 38134),
(197093, 'ruRU', 'Колосс еще не до конца зарядился. Мне нужно время!', '', 38134),
(197092, 'ruRU', 'Мы все попадем... в Утробу...', 'Мы все попадем... в Утробу...', 38134),
(197091, 'ruRU', 'Только не сейчас...', 'Только не сейчас...', 38134),
(197090, 'ruRU', 'Я не знаю, что на них нашло... но вентирам больше нельзя верить...', 'Я не знаю, что на них нашло... но вентирам больше нельзя верить...', 38134),
(197089, 'ruRU', 'Нет! Ты лжешь!', 'Нет! Ты лжешь!', 38134),
(197087, 'ruRU', 'Вентиры обещали, что освободят нас от всех грехов.\n\nИ после столетий боли я думал, что наконец-то очистился...\n\nНо теперь вентиры оскорбляют меня... дразнят... меня бросили...\n\nЭто была ложь с самого начала?', 'Вентиры обещали, что освободят нас от всех грехов.\n\nИ после столетий боли я думала, что наконец-то очистилась...\n\nНо теперь вентиры оскорбляют меня... дразнят... меня бросили...\n\nЭто была ложь с самого начала?', 38134),
(197086, 'ruRU', 'Не забудь использовать резонатор, иначе они снова восстанут против нас.', '', 38134),
(197082, 'ruRU', 'Хорошо! А я... как-нибудь... сдвину колосса с места...', '', 38134),
(197067, 'ruRU', 'Еще один. Не расслабляйся.', '', 38134),
(197065, 'ruRU', '', 'Это значит: не вздумай меня предать.', 38134),
(197064, 'ruRU', '', 'Да, так и есть, $p. Мы партнеры. Отныне и пока наш огонь не угаснет.', 38134),
(197063, 'ruRU', '', 'Как только они узнают, что мы с тобой деловые партнеры, они постараются убить тебя, чтобы навредить мне.', 38134),
(197062, 'ruRU', '', 'Если встретишь в Темных Землях кого-то еще из моего картеля, относись к ним с опаской.', 38134),
(197061, 'ruRU', '', 'А зависть – одна из слабостей моего картеля. Те, кто когда-то отправил меня в Утробу, начали посылать за мной убийц.', 38134),
(197060, 'ruRU', '', 'Теперь мы знаем, что некоторые смертные могут покинуть это место. Это дает мне... завидное преимущество.', 38134),
(197057, 'ruRU', '', 'Мой картель уже давно посылает агентов в Утробу. Как бы здесь ни было опасно, мой народ не может устоять перед ценной информацией.', 38134),
(197056, 'ruRU', '', 'Правило седьмое: тебя предадут даже близкие союзники. Особенно близкие союзники.', 38134),
(197055, 'ruRU', '', 'Узнай в их загонах как можно больше. Продолжим исследования.', 38134),
(197053, 'ruRU', '', 'Этот шифр скроет твой запах, пока ты будешь в загонах. Принеси мне руны, с помощью которых создавали этих монстров. Я хочу их изучить.', 38134),
(197051, 'ruRU', '', 'Правило шестое: регулярно меняй маскировку. Верные Утробе постоянно пытаются нас отыскать.', 38134),
(197050, 'ruRU', '', 'Должна признаться, я сомневалась, что ты вернешься. И это был не комплимент.', 38134),
(197049, 'ruRU', '', 'Возьми этот экспериментальный шифр. Если он работает как надо, ты сможешь войти в цитадель, не погибнув ужасной смертью. Скорее всего...', 38134),
(197048, 'ruRU', '', 'В цитадели Вечных Мук что-то происходит. Хотелось бы узнать, что.', 38134),
(197047, 'ruRU', '', 'Правило пятое: не бойся рисковать. Обычно Верные Утробе этого не ждут.', 38134),
(197045, 'ruRU', 'Вслед за этим храмом падут остальные. Скоро весь Бастион сгорит.', '', 38134),
(197044, 'ruRU', '', 'А вот первый пункт – имя одного инквизитора. Он заслужил это место недавно. Я буду очень щедра, если благодаря тебе его не станет.', 38134),
(197043, 'ruRU', '', 'Правило четвертое: держи список тех, кто однажды захочет прервать твое существование. Не обольщайся, ты в моем списке где-то среди рядовых слуг Утробы.', 38134),
(197042, 'ruRU', '', 'Будь осторожнее, пока за тобой следит Око Тюремщика. Мне не хотелось бы потерять такого многообещающего партнера!', 38134),
(197039, 'ruRU', 'Залитая анимой арена приветствует знаменитого бойца!', '', 38134),
(197038, 'ruRU', 'Легендарный гладиатор, обладательница рунического труподела, Сабриэль Косторуб!', '', 38134),
(197037, 'ruRU', '', 'Правило третье: доверие надо заслужить. За сведения надо платить. Шифр у тебя? Все верно: ты у меня в долгу.', 38134),
(197029, 'ruRU', '', 'Тирна Скитт не забудет вас. Никогда.', 38134),
(197028, 'ruRU', '', 'Лакали спит. Знания остались при нем. Время излечит его.', 38134),
(197000, 'ruRU', '', 'Какие... вкусные... знания...', 38134),
(196984, 'ruRU', '', 'Идите сквозь туман, сквозь опасность. А затем мы встретимся вновь.', 38134),
(196976, 'ruRU', 'Смотрите! Там, наверху! Неужели это...', '', 38134),
(196975, 'ruRU', '', 'Сгиньте! Сгиньте в пучине проклятых душ!', 38134),
(196974, 'ruRU', '', 'Ты заплатишь за это оскорбление! Крылья Верных Утробе затмят небеса вашего мира!', 38134),
(196973, 'ruRU', '', '\"Жалкие\"? Вроде тех, которые тебя недавно разгромили, ведьма?', 38134),
(196972, 'ruRU', '', 'Ваши жалкие души насытят тьму!', 38134),
(196971, 'ruRU', 'Стойте. Кажется, я что-то слышу...', '', 38134),
(196968, 'ruRU', '', 'Готовь \"ритуал\". Все должно выглядеть убедительно, чтобы Мор\'Битан нам поверил.', 38134),
(196920, 'ruRU', 'Может, и так, Келтеш. Многие забывают, что даже в клетках души видят все, что происходит вокруг.', 'Может, и так, Келтеш. Многие забывают, что даже в клетках души видят все, что происходит вокруг.', 38134),
(196918, 'ruRU', '', 'Все, довольно. Собери для меня стигию и возвращайся скорее. Твой шифр ждет!', 38134),
(196917, 'ruRU', '', 'Понимаю, звучит иронично, учитывая, что сама я отсюда выбраться сейчас не в состоянии. Посмейся, я не обижусь. Только не слишком долго.', 38134),
(196916, 'ruRU', '', 'Правило первое: не допускай безвыходных ситуаций. Чтобы выжить в Утробе достаточно долго, нужно всегда иметь путь к отступлению.', 38134),
(196915, 'ruRU', 'Как мне понять, что подозрительно? Самю и Илка поссорились из-за податей и засухи. Это считается подозрительным?', '', 38134),
(196891, 'ruRU', '', 'Славься, Зоваал! Эти души – дар тебе!', 38134),
(196885, 'ruRU', 'Да, твои грехи придавили тебя к земле. Я попрошу Обвинительницу рассудить тебя.', 'Да, твои грехи придавили тебя к земле. Я попрошу Обвинительницу рассудить тебя.', 38134),
(196880, 'ruRU', '', 'Однако старания того стоят. Нет ничего лучше, чем дружба с уважающим тебя ларионом.', 38134),
(196879, 'ruRU', 'А-а-а! Я... я теперь... легче? Я не боюсь.', '', 38134),
(196878, 'ruRU', '', 'Заслужить доверие даже одного лариона – дело непростое. Это очень привередливые, гордые и упрямые существа.', 38134),
(196874, 'ruRU', '', 'Добро пожаловать в наш прайд, $n. Сердце укажет тебе путь.', 38134),
(196873, 'ruRU', 'Анимапард – идеальный компаньон, созданный специально для тебя. Его нужно лишь иногда чинить и калибровать.', '', 38134),
(196872, 'ruRU', 'Анимапарды понимают своего компаньона с полуслова. Они хорошо знают команды, выполняют их и никогда не упрямятся.', '', 38134),
(196868, 'ruRU', 'Я уверен, что ты по достоинству оценишь моих анимапардов.', '', 38134),
(196865, 'ruRU', 'Гиблотопь ширится с каждым днем, пожирая все вокруг. Может, ты ее остановишь?', '', 38134),
(196864, 'ruRU', 'Раньше тут было полно пиратов, благородных воров и нищих королей. Провались эта засуха в Утробу.', '', 38134),
(196863, 'ruRU', 'А ты, я смотрю, нынче знаменитость.', '', 38134),
(196862, 'ruRU', 'Между нами, я сюда поехал только потому, что у них не хватает крылатых, чтобы тут патрулировать.', '', 38134),
(196861, 'ruRU', 'Мой братец работал над этими хатами. Но вместо благодарности его заземлили.', '', 38134),
(196860, 'ruRU', 'Не высовывайся. Не хочу, чтоб клыкастые увидели нас вместе.', '', 38134),
(196859, 'ruRU', 'Говорят, в этих лесах живут разбойники и мятежники.', '', 38134),
(196858, 'ruRU', 'Поехали отсюда. Тут водятся жуткие нетопыри.', '', 38134),
(196857, 'ruRU', '$n, иди сюда.', '', 38134),
(196856, 'ruRU', 'Нет времени болтать. Мне надо следить за рабочими.', '', 38134),
(196854, 'ruRU', 'Ура! Ты поможешь!', 'Ура! Ты поможешь!', 38134),
(196846, 'ruRU', 'Эта служба для меня унизительна. Надеюсь, Графиня запомнит, на какие жертвы мне приходится идти.', 'Эта служба в Старых воротах для меня унизительна. Надеюсь, Графиня запомнит, на какие жертвы мне приходится идти.', 38134),
(196845, 'ruRU', '', 'Не говори глупостей, Пелодис. $n теперь часть нашего прайда. Это великая честь.', 38134),
(196844, 'ruRU', 'Мои анимапарды намного полезнее, и я думаю, что $n предпочтет их.', '', 38134),
(196840, 'ruRU', 'После стольких веков – такой нелепый финал...', '', 38134),
(196839, 'ruRU', 'Это одно из величайших изобретений Миканикоса. Оно превращает аниму в заряды энергии.', '', 38134),
(196838, 'ruRU', 'Чужаков ждет пытка Светом!', '', 38134),
(196836, 'ruRU', 'Если Налтор смог пробить эту броню, его магия очень сильна. Его будет нелегко одолеть.', '', 38134),
(196835, 'ruRU', 'Анима питает в Бастионе все, даже могучих колоссов. Это поможет сохранить заряд.', '', 38134),
(196834, 'ruRU', '', 'Скажи, когда можно начинать, $n.', 38134),
(196830, 'ruRU', 'Я ужасно нагрешил. Теперь я понимаю.', '', 38134),
(196828, 'ruRU', 'Рад встрече, пилигрим.', '', 38134),
(196815, 'ruRU', '', 'Я вернусь к воеводе Бреке. Можешь пойти со мной в облике животного.', 38134),
(196814, 'ruRU', '', 'Твоя связь с природой укрепилась. Защищай ее и поддерживай равновесие.', 38134),
(196813, 'ruRU', '', 'Слушай их шепот... запоминай их уроки.', 38134),
(196812, 'ruRU', '', 'Духи отвечают!', 38134),
(196811, 'ruRU', '', 'Духи природы, заклинаем вас. Наделите этого друида своим даром.', 38134),
(196810, 'ruRU', '', 'Неподалеку есть древний камень друидов. С его помощью мы обратимся к дикой природе.', 38134),
(196808, 'ruRU', 'Будем драться, пока не покажешь новые умения!', '', 38134),
(196807, 'ruRU', 'Неплохо! Твое мастерство заметно возросло. Вернемся в лагерь.', '', 38134),
(196806, 'ruRU', 'Покажи, чему тебе удалось научиться!', '', 38134),
(196805, 'ruRU', 'Думаю, мы закончили. Как ты себя чувствуешь?', '', 38134),
(196804, 'ruRU', 'Позволь миру раствориться...', '', 38134),
(196803, 'ruRU', 'Закрой глаза. Сосредоточься.', '', 38134),
(196802, 'ruRU', 'Идем. Помедитируем и раскроем наш внутренний потенциал.', '', 38134),
(196801, 'ruRU', 'Я не очень... люблю магию Скверны. Надеюсь, все что ты делаешь – ради Орды.', '', 38134),
(196800, 'ruRU', 'Потому что я учил не тебя.', '', 38134),
(196799, 'ruRU', 'Мне кажется, я ничему не научился.', '', 38134),
(196798, 'ruRU', 'И ударь разок Трога, чтобы снять заклинание.', '', 38134),
(196797, 'ruRU', 'Смотри! Превращенный враг на время выходит из строя. Используй это, чтобы разобраться с остальными.', '', 38134),
(196795, 'ruRU', 'Да ты совсем без головы. Так держать! Давай вернемся в лагерь.', '', 38134),
(196794, 'ruRU', 'Сейчас тебя атакуют двое. Уравняй шансы с помощью \"Превращения\"!', '', 38134),
(196793, 'ruRU', 'Урон снимает эффект \"Превращения\". Верни Герберту его привычный облик.', '', 38134),
(196792, 'ruRU', 'Я слышал об этой тактике. Превращаешь одного врага и разбираешься со вторым.', '', 38134),
(196790, 'ruRU', 'Соберись, тряпка! Это нужно для учебы!', '', 38134),
(196789, 'ruRU', 'Что-то не хочу я... в овцу...', '', 38134),
(196788, 'ruRU', 'Трог, иди сюда. Мы нападем на рекрута и научим применять \"Превращение\".', '', 38134),
(196787, 'ruRU', 'Противник будет бродить вокруг, пока ты не нападешь. Вот так.', '', 38134),
(196786, 'ruRU', 'Я просто показываю, как \"Превращение\" может пригодиться в бою.', '', 38134),
(196785, 'ruRU', '', 'Герберт, что это значит?!', 38134),
(196784, 'ruRU', 'Вот так работает \"Превращение\"!', '', 38134),
(196783, 'ruRU', 'Я бы мог достать книгу, но маг не должен все делать сам! Так что иди.', '', 38134),
(196782, 'ruRU', '', 'Помню, как Шуджа впервые заговорила с духами. Из тебя получится славный шаман, $p.', 38134),
(196781, 'ruRU', '', 'Вижу, духи дали тебе поручение. Они не ошиблись с выбором.', 38134),
(196780, 'ruRU', '', '$p? Ты говоришь с духами?', 38134),
(196777, 'ruRU', '', 'Да, полемарх! Да хранит нас Архонт.', 38134),
(196773, 'ruRU', '', 'Он переносит очищение более... болезненно, чем другие. Когда претендент сбивается с пути, это трагедия. Возможно, сейчас произойдет нечто очень неприятное.\n\nНо ты... Почему-то мне кажется, что тебе к таким вещам не привыкать.', 38134),
(196772, 'ruRU', '', 'Утер? Знакомое имя...\n\nМожет быть, это тот претендент, что стал протеже Девии? Любопытно, что ты его знаешь. Это наверняка важно, ведь вы из одного мира.', 38134),
(196771, 'ruRU', 'Обрушьте на них смерть! Они не должны нам помешать.', '', 38134),
(196770, 'ruRU', 'Эридия! Обереги! Они...', '', 38134),
(196769, 'ruRU', '', 'Ничего, я...', 38134),
(196768, 'ruRU', 'Артемеда сообщит обо всем Архонту. А мы вернемся в Орибос и расскажем всем об измене Малдраксуса!', '', 38134),
(196767, 'ruRU', '', 'Идеал продержится, пока вы не приведете подкрепление.', 38134),
(196764, 'ruRU', 'Конечно, Келтеш. День твоего суда все ближе, и мы должны очистить тебя от грехов.', 'Конечно, Келтеш. День твоего суда все ближе, и мы должны очистить тебя от грехов.', 38134),
(196762, 'ruRU', '', 'Постарайся почаще применять \"Удар бури\".', 38134),
(196761, 'ruRU', 'Пилигрим, нужно уходить отсюда!', '', 38134),
(196759, 'ruRU', 'Постарайся почаще применять \"Удар бури\".', '', 38134),
(196758, 'ruRU', 'Я боюсь, Бела.', 'Я боюсь, Бела.', 38134),
(196755, 'ruRU', 'Догони меня, если не боишься.', '', 38134),
(196754, 'ruRU', '', 'Ты заплатишь за это, некромант!', 38134),
(196753, 'ruRU', 'Нет, ты падешь следующей.', '', 38134),
(196752, 'ruRU', '', 'Я остановлю тебя, Амарт.', 38134),
(196750, 'ruRU', 'Кирии поддерживают порочный круг. Бастион только зря тратит аниму. Мы заберем ее всю.', '', 38134),
(196745, 'ruRU', '', 'Чудовище! Зачем ты это делаешь?!', 38134),
(196740, 'ruRU', '', 'Пожалуйста, найди Николона и убедись, что он цел. Он редко опаздывает.', 38134),
(196723, 'ruRU', '', '\"Удар бури\" заряжает оружие могучим электрическим зарядом и обрушивает на врага двойной удар. Кроме того, твои способности могут активировать \"Вестник шторма\", который дает усиленный заряд \"Удара бури\".', 38134),
(196722, 'ruRU', '', 'Твоя самая мощная атака оружием – \"Удар бури\".', 38134),
(196721, 'ruRU', 'Теперь моему духу можно уйти на Ту Сторону. Славный из тебя выйдет жрец.', '', 38134),
(196720, 'ruRU', 'Используй \"Воскрешение\", чтобы вырвать их из лап смерти!', '', 38134),
(196719, 'ruRU', 'Ты из наших жрецов? Иди сюда! В этих новобранцах еще теплится жизнь!', '', 38134),
(196718, 'ruRU', 'Если она еще дышит, мы должны помочь ей.', '', 38134),
(196717, 'ruRU', 'В момент нападения идеал Отваги находилась в глубине храма.', '', 38134),
(196716, 'ruRU', 'Спасибо, $p. Похоронить их, как подобает, не удастся, но ты можешь прочитать им молитву.', '', 38134),
(196715, 'ruRU', '\"Удар бури\" заряжает оружие могучим электрическим зарядом и обрушивает на врага двойной удар. Кроме того, твои способности могут активировать \"Вестник шторма\", который дает усиленный заряд \"Удара бури\".', '', 38134),
(196714, 'ruRU', 'Он идет к идеалу!', '', 38134),
(196713, 'ruRU', '', 'Доверься Свету, храбрый паладин.', 38134),
(196712, 'ruRU', '', '\"Божественный щит\" укроет тебя от темной магии алтаря.', 38134),
(196711, 'ruRU', '', 'Подойди, паладин. Свет придет на помощь в трудную минуту.', 38134),
(196710, 'ruRU', 'Смотрите и учитесь!', '', 38134),
(196709, 'ruRU', 'Огонь!!!', '', 38134),
(196708, 'ruRU', 'Тебе удалось восстановить еще немного моих воспоминаний?$b$bЧем больше ты находишь, тем сильнее становимся мы оба.', '', 38134),
(196706, 'ruRU', 'Не знаю ничего про Свет – но когда дух зовет, надо отвечать.', '', 38134),
(196705, 'ruRU', 'Колосс готов! Вперед! Вперед!', '', 38134),
(196704, 'ruRU', 'Раз вы такие храбрые, идите навстречу своей судьбе.', '', 38134),
(196703, 'ruRU', 'Хватит. Детенышу Костеклыка пора кушать.', '', 38134),
(196702, 'ruRU', 'Заряд почти восстановлен! Еще немного!', '', 38134),
(196701, 'ruRU', 'Дух древнего воина может многому тебя научить. Будь внимательнее.', '', 38134),
(196700, 'ruRU', 'Нам нет конца. Рано или поздно вы падете.', '', 38134),
(196699, 'ruRU', 'И это все? Я ожидал большего!', '', 38134),
(196698, 'ruRU', '', 'Скорее, $n! Прогони эти воспоминания!', 38134),
(196697, 'ruRU', 'Не могу! А-а-а-а!', '', 38134),
(196696, 'ruRU', '', 'Контролируй их, аколит!', 38134),
(196695, 'ruRU', '', 'Еще немного... Ах!', 38134),
(196694, 'ruRU', 'Брось \"Замораживающую ловушку\" в огрских руинах. Зверь наверняка придет на шум.', '', 38134),
(196693, 'ruRU', 'Частица льда? Бери, конечно. После того... случая у меня их полно.', '', 38134),
(196692, 'ruRU', 'Собери припасы поскорее. Чем раньше ты научишься делать ловушку, тем лучше.', '', 38134),
(196690, 'ruRU', 'Глупец. Вы окружены со всех сторон.', '', 38134),
(196689, 'ruRU', 'Приструни своих тварей. Им всем тоже придет конец.', '', 38134),
(196688, 'ruRU', 'Ваша армия уже отступает. Вы проиграли.', '', 38134),
(196685, 'ruRU', 'Странно... почему преторы деактивированы?', '', 38134),
(196683, 'ruRU', 'Пока что этого хватит. Найди зверя и приручи его.', '', 38134),
(196682, 'ruRU', 'Я постараюсь побыстрее изложить основы. Слушай внимательно.', '', 38134),
(196681, 'ruRU', 'Бал\'а даш, маланоре. Буду рад обучить еще одного эльфа крови искусству Странника.', '', 38134),
(196680, 'ruRU', 'Надо скорее предупредить полемарха!', '', 38134),
(196679, 'ruRU', 'Нет... Он будет недоволен...', '', 38134),
(196677, 'ruRU', 'Как ты смеешь задерживать груз для Казначея?!', '', 38134),
(196676, 'ruRU', 'Глупцы! Раскольники свергнут Архонта!', '', 38134),
(196675, 'ruRU', '', 'Оберег храма Отваги в порядке. Отрадно это видеть.', 38134),
(196674, 'ruRU', 'Что... опять?..', '', 38134),
(196673, 'ruRU', 'Меня... надо... заштопать...', '', 38134),
(196672, 'ruRU', 'Ремонту... не подлежит...', '', 38134),
(196671, 'ruRU', 'Сохраните... мой труп...', '', 38134),
(196670, 'ruRU', 'Слишком... много... дефектов...', '', 38134),
(196669, 'ruRU', 'Твоя самая мощная атака оружием – \"Удар бури\".', '', 38134),
(196668, 'ruRU', '', 'Сперва поешь. А то еще умрешь с голоду.', 38134),
(196667, 'ruRU', '', 'Свинобразы... устроили засаду...', 38134),
(196666, 'ruRU', 'Мясо и кости! Кожа и жилы!', '', 38134),
(196665, 'ruRU', 'Умрите поскорее, у меня много работы!', '', 38134),
(196664, 'ruRU', 'Разберу и переделаю!', '', 38134),
(196663, 'ruRU', 'Свежие органы для опытов!', '', 38134),
(196662, 'ruRU', 'О, свежие органы!', '', 38134),
(196661, 'ruRU', '', 'Тогда вы мне больше не нужны.', 38134),
(196660, 'ruRU', 'Генерал, вы требуете больше, чем мы можем отдать. У нас больше ничего не осталось.', 'Генерал, вы требуете больше, чем мы можем отдать. У нас больше ничего не осталось.', 38134),
(196659, 'ruRU', '', 'Прыгай на виверну и лети в Оргриммар. Нас встретят как героев!', 38134),
(196657, 'ruRU', 'Возможно, этих претендентов надо просто подбодрить.', '', 38134),
(196656, 'ruRU', 'Падших вернут в храм Верности, и через какое-то время они вернутся на путь.', '', 38134),
(196655, 'ruRU', 'Да, перерожденная.', '', 38134),
(196653, 'ruRU', 'Мы все чаще теряем претендентов. В храме Верности их стараются снова наставить на путь.', '', 38134),
(196651, 'ruRU', 'Я помню свою семью. Они теперь вряд ли меня узнают! Надеюсь, когда я забуду свою жизнь, все будет... не так.', '', 38134),
(196649, 'ruRU', 'Неплохо. Уж получше, чем мое последнее очищение.', '', 38134),
(196648, 'ruRU', '', '\"Вскипание лавы\" действует на оружие в левой руке, поэтому не забудь также применить \"Язык пламени\".', 38134),
(196647, 'ruRU', '\"Вскипание лавы\" действует на оружие в левой руке, поэтому не забудь также применить \"Язык пламени\".', '', 38134),
(196646, 'ruRU', '', 'Потом используй \"Вскипание лавы\" и обрушь на врага силу земных недр.', 38134),
(196645, 'ruRU', 'Потом используй \"Вскипание лавы\" и обрушь на врага силу земных недр.', '', 38134),
(196644, 'ruRU', 'Я тебя знаю? Твое лицо мне незнакомо...', '', 38134),
(196643, 'ruRU', 'Начнем?', '', 38134),
(196639, 'ruRU', '', 'Благодарю тебя, и мне очень жаль.', 38134),
(196638, 'ruRU', '', 'Я должна сообщить идеалу. Потерять аколита, да еще и в самом конце испытаний...', 38134),
(196637, 'ruRU', '', 'Я не могу в это поверить. Иди. Верность ждет тебя, аколит.', 38134),
(196636, 'ruRU', '', 'Аколит! Нет!', 38134),
(196635, 'ruRU', '', 'Довольно. Я больше не буду следовать пути!', 38134),
(196634, 'ruRU', '', 'Я столько трудилась в этом храме... И ради чего?', 38134),
(196633, 'ruRU', '', 'Нет! Ты должна бороться!', 38134),
(196632, 'ruRU', 'Ты знаешь, что делать. Впусти меня.', '', 38134),
(196631, 'ruRU', '', 'Я... бессильна...', 38134),
(196630, 'ruRU', 'Ты была бессильна предо мной раньше... Ты бессильна и сейчас.', '', 38134),
(196629, 'ruRU', '', 'Я готова.', 38134),
(196628, 'ruRU', 'Вредный старик Мве\'зала оградил своих лоа, чтобы те набирались сил, питаясь от диких семян.', '', 38134),
(196627, 'ruRU', 'Он хочет иссушить семена, оживить Хаккара и наполнить его темным моджо.', '', 38134),
(196626, 'ruRU', 'Следы ведут вперед, в другую рощу. Ну, чего ждем?', '', 38134),
(196625, 'ruRU', 'Тот, кто оставил эти следы, куда-то сильно спешил...', '', 38134),
(196624, 'ruRU', 'Как же мне не нравится все это...', '', 38134),
(196622, 'ruRU', 'Хаккара тоже нет? Ой-ей... как плохо. Это очень-очень плохо!', '', 38134),
(196621, 'ruRU', 'Кровь и гром, бойцы! Мы никак не могли пробиться через этот шторм.', '', 38134),
(196620, 'ruRU', 'Дамбалы нет... Осталась одна оболочка! Надо поскорее найти Хаккара!', '', 38134),
(196619, 'ruRU', '', 'Орда! Я знала, что они нас найдут.', 38134),
(196618, 'ruRU', 'Драккари поклонялись Тарон\'дже... а потом предали его. Нелегко в наше время найти хороших последователей!', '', 38134),
(196617, 'ruRU', '', 'Выше голову – тебе есть чем гордиться, $n. Мы убили дракона и прервали этот безумный ритуал!\n\nЯ позабочусь, чтобы старшие по званию узнали о наших сегодняшних подвигах!\n\nА теперь пора придумать, как выбраться с этого острова.', 38134),
(196616, 'ruRU', '', 'Погрузи корни в землю.', 38134),
(196615, 'ruRU', 'Мам\'тот заслужил немного покоя. Ох и не повезло ему тогда в Зул\'Драке!', '', 38134),
(196614, 'ruRU', 'Как минимум, на мой картель.\n\nЯ один из руководителей картеля Ба. Мы собираем ценные сведения и знания. Применяя их, мы кроме всего прочего становимся богаче и влиятельнее. \n\nБрокеры придают большое значение успеху и не боятся демонстрировать результаты своих начинаний. Благодаря чему я и заслужил это звание среди товарищей.\n\nТак что я влияю на свой горячо любимый картель, чтобы они достигли тех же успехов. Я лишь добиваюсь процветания для всех нас.', '', 38134),
(196612, 'ruRU', 'Главный? Нет, не совсем.\n\nЯ лишь один из многих. Но можно сказать, что ко мне прислушиваются. \n\nВлияние часто значит больше власти, не находишь?\n\nПоэтому \"влиятельность\" лучше подойдет. Я... влиятелен.', '', 38134),
(196610, 'ruRU', '', '$p, дай знать, когда решишь отправиться в путь. Нам нужно вернуться в Оргриммар.', 38134),
(196609, 'ruRU', '', 'Ха! Ограм и их дракону пришел конец. Мы победили, и над нами снова чистое небо.', 38134),
(196608, 'ruRU', 'Добро пожаловать. Чего ты ищешь у брокеров?\n\nМы всегда к твоим услугам.', '', 38134),
(196607, 'ruRU', '', 'В атаку! За Орду!', 38134),
(196606, 'ruRU', '', 'Лок\'тар огар! Победа или смерть!', 38134),
(196605, 'ruRU', '', 'У нас... получилось?..', 38134),
(196604, 'ruRU', '', 'Он завершает ритуал!', 38134),
(196603, 'ruRU', 'Все, что нужно ремесленнику!\n\nБрокеры путешествуют по вселенной и учатся у мастеров множества реальностей. Поэтому мы умеем подбирать товары для любых твоих нужд. \n\nНаши члены начали осваивать профессии, которые считает полезными твой народ. Мы готовы помочь тебе овладеть мастерством.\n\nМы ищем новые рецепты не покладая рук. Взгляни на наш ассортимент, он тебя не разочарует.', '', 38134),
(196601, 'ruRU', '', 'Гор\'грот! Ты умрешь, как и твой драгоценный дракон!', 38134),
(196600, 'ruRU', '', 'Вперед! Гор\'грот уже близко.', 38134),
(196599, 'ruRU', '', 'Слушаюсь! Лок\'тар!', 38134),
(196598, 'ruRU', '', 'Я отвлеку его. Шуджа, исцеляй нас!', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196597, 'ruRU', '', '$n, сначала ударишь ты. Затем я вступлю в бой. Нас никто не остановит!', 38134),
(196596, 'ruRU', '', 'Это один из телохранителей Гор\'грота. Что будем делать?', 38134),
(196595, 'ruRU', '', 'Вперед. Я отвлеку внимание огров на себя, а вы разберитесь с ними!', 38134),
(196594, 'ruRU', '', 'Некротическая энергия... Кажется, ритуал почти завершен!', 38134),
(196593, 'ruRU', '', 'Почти всех перебили! Поднажмем!', 38134),
(196592, 'ruRU', 'Спасибо... теперь надо прервать ритуал огров!', '', 38134),
(196591, 'ruRU', '', 'Мве\'зала дорого заплатит за то, что натворил. Эти духи были под моей опекой.', 38134),
(196590, 'ruRU', '', 'Волшебно! Королева будет рада узнать, что мы прогнали Мве\'залу из Арденвельда.', 38134),
(196588, 'ruRU', '', 'Сначала надели оружие силой ветра и огня.', 38134),
(196587, 'ruRU', 'Сначала надели оружие силой ветра и огня.', '', 38134),
(196586, 'ruRU', '', 'В храме Мудрости хранятся все воспоминания кирий. Отрадно знать, что его оберег еще действует.', 38134),
(196585, 'ruRU', 'Оберег храма Смирения цел. Хотя раскольникам не помешало бы вернуться туда и повторить урок.', '', 38134),
(196584, 'ruRU', 'Как увлекательно! Прекрасная демонстрация твоих навыков. Блестяще.', '', 38134),
(196583, 'ruRU', 'Оберег храма Верности работает. Медитирующим кириям, верным Бастиону и Архонту, ничто не угрожает.', '', 38134),
(196580, 'ruRU', 'Даже расколотый, Шлем Господства обладает невероятной властью над душами умерших. \n\nНо не верь мне на слово. Спроси нашего друга.', '', 38134),
(196579, 'ruRU', '', 'Прислужники Мве\'залы основательно здесь укрепились. Помоги Дикой Охоте выбить их!', 38134),
(196578, 'ruRU', '', 'Примени к оружию \"Язык пламени\" и \"Неистовство ветра\".', 38134),
(196577, 'ruRU', 'Примени к оружию \"Язык пламени\" и \"Неистовство ветра\".', '', 38134),
(196576, 'ruRU', '', 'Обойди ближайшие рощи, а затем найди леди Лунную Ягоду в Тирна Нохе.', 38134),
(196575, 'ruRU', 'Он сможет подчинить себе армию безвольных обитателей Ревендрета. А если хватит анимы – даже преобразовать саму сущность Темных Земель.', '', 38134),
(196574, 'ruRU', 'Ремесло наемников – не совсем моя специализация. Этим и ценно наше сотрудничество.', '', 38134),
(196573, 'ruRU', 'Эта башня крайне важна для Малдраксуса. Захватить ее было бы выгодным предприятием для картеля.', '', 38134),
(196572, 'ruRU', 'Хотим справиться с Мве\'залой – надо просить о помощи Королеву Зимы.', '', 38134),
(196571, 'ruRU', '', 'Занятно... Оберег храма Чистоты не работает. Вот почему туда проникло столько раскольников.', 38134),
(196564, 'ruRU', 'Старый хитрый лоа зашел слишком далеко!', '', 38134),
(196562, 'ruRU', 'Какая маска! Здорово выглядишь! Прямо как... я!', '', 38134),
(196561, 'ruRU', 'С духами лоа в Арденвельде творится что-то странное. Проверь-ка, что с ними не так, ага?', '', 38134),
(196558, 'ruRU', 'Ну же! Бери камень смерти и отправляйся ко мне на Ту Сторону. Нечего тут трусить!', '', 38134),
(196535, 'ruRU', '', 'Силы тут недостаточно. Надо снять заклятье.', 38134),
(196534, 'ruRU', '', 'Мулгрин! Скорее, нужно уничтожить эти печати!', 38134),
(196532, 'ruRU', '', 'Я сказала \"притворись огром\", а не \"танцуй как огр\"!', 38134),
(196531, 'ruRU', '', 'Идем дальше... Нужно выведать что-нибудь об этом ритуале.', 38134),
(196530, 'ruRU', '', 'Ты только молчи. Просто маши и улыбайся. Чем глупее, тем лучше.', 38134),
(196529, 'ruRU', '', 'Хорошо. Теперь притворись огром и проведи нас внутрь.', 38134),
(196528, 'ruRU', 'Подопытный доброволец! Всегда бы так.', '', 38134),
(196527, 'ruRU', '', 'Рекрут, мы с тобой идем дальше! Остальные закрепятся здесь.', 38134),
(196526, 'ruRU', '', 'Хорошо. Совсем скоро мы раздавим этих жалких огров.', 38134),
(196525, 'ruRU', 'Выполнять следующий этап нашего плана, конечно же. Постарайся хоть кого-то забрать с собой.', '', 38134),
(196524, 'ruRU', '', 'Мне кажется, это вроде ключа к одному из залов Торгаста.\n\nПоищи в вестибюле устройство, к которому он подойдет.\n\nКуда оно ведет... можно только догадываться.', 38134),
(196523, 'ruRU', 'Куда ты уходишь?!', '', 38134),
(196522, 'ruRU', 'Я так не думаю. Ты сыграл свою роль. Теперь моя очередь.', '', 38134),
(196520, 'ruRU', 'Бастион существует уже много эпох, и все это время почетный пост его правителя занимает Архонт.\n\nПока будете с ней говорить, не забывайте, что вы всего лишь смиренные просители.', '', 38134),
(196519, 'ruRU', 'Когда закончим, соберемся на Разрушенном мосту.', '', 38134),
(196518, 'ruRU', '', 'Я с тобой! Лок\'тар огар!', 38134),
(196517, 'ruRU', 'Я помогу. Прикончим их!', '', 38134),
(196516, 'ruRU', 'Ха-ха-ха... Свобода! Кхм... Ну что, не пора ли нам удалиться?', '', 38134),
(196513, 'ruRU', '', 'Ничего, вместе справимся. За Орду!', 38134),
(196512, 'ruRU', 'К нам приближается что-то большое!', '', 38134),
(196511, 'ruRU', 'Жаль прерывать такое интересное заклинание. Не подпускай ко мне гарпий!', '', 38134),
(196499, 'ruRU', 'Это было трудно еще до утечки анимы. Теперь это невозможно.\n\n<Претендент с тоской смотрит наверх.>\n\nНевозможно...', 'Это было трудно еще до утечки анимы. Теперь это невозможно.\n\n<Претендент с тоской смотрит наверх.>\n\nНевозможно...', 38134),
(196498, 'ruRU', 'Что бы я ни делал – все впустую. Нам не видать Элизийской цитадели. Архонт бездействует.\n\nБез анимы нам отсюда не выбраться.', 'Что бы я ни делала – все впустую. Нам не видать Элизийской цитадели. Архонт бездействует.\n\nБез анимы нам отсюда не выбраться.', 38134),
(196497, 'ruRU', 'Даже если я паду, никто не вернет меня в Орибос. Мы обречены навсегда остаться здесь.\n\nНадеяться больше не на что.', 'Даже если я паду, никто не вернет меня в Орибос. Мы обречены навсегда остаться здесь.\n\nНадеяться больше не на что.', 38134),
(196496, 'ruRU', 'Скоро для колоколов не останется анимы. Скоро нам уже ничто не поможет.\n\nНо мы не сдаемся.', 'Скоро для колоколов не останется анимы. Скоро нам уже ничто не поможет.\n\nНо мы не сдаемся.\n\n<Претендент тяжело и устало вздыхает.>', 38134),
(196495, 'ruRU', 'Даже если я пройду испытание, перерожденными нам уже не стать. Я обречен навечно оставаться в этом храме.', 'Даже если я пройду испытание, перерожденными нам уже не стать. Я обречена навечно оставаться в этом храме.', 38134),
(196494, 'ruRU', 'Я чувствую, что теряю себя. Я не хочу этого, но они требуют продолжать.\n\nРазве они не видят, что с нами делают эти обряды? Они ослепли?', 'Я чувствую, что теряю себя. Я не хочу этого, но они требуют продолжать.\n\nРазве они не видят, что с нами делают эти обряды? Они ослепли?', 38134),
(196493, 'ruRU', 'Где было мое прошлое, осталась лишь пустота. Неужели так и должно быть?', 'Где было мое прошлое, осталась лишь пустота. Неужели так и должно быть?', 38134),
(196492, 'ruRU', '', 'Лисония... Я не знала о тьме в ее душе...', 38134),
(196491, 'ruRU', '', 'Ее воспоминания ясны, Девия. Твоя Десница служила Утробе!', 38134),
(196490, 'ruRU', 'Я в деле!', '', 38134),
(196489, 'ruRU', '', 'Я этих гарпий мигом перережу.', 38134),
(196487, 'ruRU', '', 'Ты о Герберте? Он небось еще и заметки делает.', 38134),
(196486, 'ruRU', 'Они захватили в плен Отрекшегося для своего ритуала.', '', 38134),
(196484, 'ruRU', '', 'Я отправлюсь к Бо, на север, где живут гарпии. Встретимся там.', 38134),
(196482, 'ruRU', 'Ну и бардак... Можно не орать, пока я сплю? Разбудите меня, когда все утихнет.', '', 38134),
(196481, 'ruRU', 'Скоро я буду готов опять драться за Арденвельд! Вот только отдохну немного, переведу дух. А потом держитесь все, Ухач идет!', '', 38134),
(196480, 'ruRU', 'Ах! Как здорово вновь оказаться в относительно безопасном месте... Там, снаружи, такой кавардак!', '', 38134),
(196479, 'ruRU', '', 'Наконец-то... аудиенция у Архонта! Встретимся на месте, $n.', 38134),
(196478, 'ruRU', '', 'Малый ручей возвращается в озеро. Приветствую вас.', 38134),
(196477, 'ruRU', 'Мы здесь! Мы спаслись!', '', 38134),
(196475, 'ruRU', '', 'Видела бы ты наших пауков – тоже бы с ними целоваться не полезла.', 38134),
(196474, 'ruRU', '', 'Ты что, не любишь пауков, Вон\'са?', 38134),
(196473, 'ruRU', '', 'Ну, как бы тебе сказать...', 38134),
(196472, 'ruRU', '', 'Несчастные пауки остались без крова. Это несправедливо.', 38134),
(196467, 'ruRU', '', 'Спасибо тебе. Я унесу нас отсюда – запрыгивай!', 38134),
(196465, 'ruRU', 'Я высвободил часть их анимы! Она залечивает раны, если к ней подойти.', '', 38134),
(196464, 'ruRU', 'Спаси всех, кого сможешь!', '', 38134),
(196462, 'ruRU', 'Чем быстрее ты найдешь детали, тем быстрее колосс разберется с летунами!', '', 38134),
(196461, 'ruRU', 'Будь осторожнее. Нашему врагу неведома честь.', '', 38134),
(196460, 'ruRU', 'Друзья! Я иду на помощь!', '', 38134),
(196459, 'ruRU', 'Кощунство! Пилигрим, вперед! Освободи храм!', '', 38134),
(196458, 'ruRU', 'Вы будете первыми. Но не последними.', '', 38134),
(196457, 'ruRU', 'Какую клятву? Мой господин желает уничтожить Бастион и пополнить свое войско трупами кирий.', '', 38134),
(196452, 'ruRU', 'Ты нарушил древнюю клятву Малдраксусу, лич! Тебя ждет расплата!', '', 38134),
(196449, 'ruRU', 'По крайней мере, я не один. Малдраксийцы напали внезапно!', '', 38134),
(196444, 'ruRU', 'Если нет, тогда... Архонт милостивый... Клейя, смотри!', '', 38134),
(196442, 'ruRU', '', 'Похоже, от него скрыли сделку с Утробой. Интересно... другие раскольники тоже ничего о ней не знают?', 38134),
(196441, 'ruRU', 'Раскольник, разбивший оберег... он был в воспоминаниях Лисонии...', '', 38134),
(196440, 'ruRU', 'Кассий даст вам лариона. Поторопитесь!', '', 38134),
(196439, 'ruRU', '', 'Это так. Но сейчас нужно вернуть посох Примаса в обитель... и отпраздновать победу. Память о твоих деяниях будет вечной, пилигрим Утробы,', 38134),
(196438, 'ruRU', 'Нам еще многое нужно сделать. Вираз должен ответить за измену, и Кел\'Тузад тоже.', '', 38134),
(196437, 'ruRU', '', 'Признаюсь, поначалу я сомневалась в тебе, пилигрим. Но теперь я знаю, что наш ковенант может на тебя положиться.', 38134),
(196436, 'ruRU', '', 'Пусть Кел\'Тузад избежал правосудия, мы изгнали его из Малдраксуса и заключили хотя бы временный союз с маркграфиней Син\'дейн.', 38134),
(196435, 'ruRU', 'Сильные выживают, слабые выбывают!', '', 38134),
(196434, 'ruRU', 'Один из участников явно сильнее других!', '', 38134),
(196433, 'ruRU', 'Прекрасный удар!', '', 38134),
(196430, 'ruRU', '', 'Я чувствую, как потоки магии тянутся от руны к башне. Она доставит нас туда.', 38134),
(196429, 'ruRU', '', 'Помощь пришла! Мать-Земля, помоги!', 38134),
(196423, 'ruRU', '', 'Сюда!', 38134),
(196422, 'ruRU', '', 'В последний раз я видела Андуина, когда его уводили наверх в башню. Нужно найти путь к ней.', 38134),
(196421, 'ruRU', 'Это Тремакулюм. Здесь истязают души, пока они не превратятся в жалкие тени.', '', 38134),
(196420, 'ruRU', '', 'И ритуал прервать не забудь!', 38134),
(196419, 'ruRU', '', 'Ты – последняя надежда этих выживших. Это так, к слову.', 38134),
(196418, 'ruRU', 'Каждый раз, заглядывая в Торгаст, я чувствую на себе чей-то ненавидящий взгляд. Мы рискуем все больше, но отступать нельзя.', '', 38134),
(196417, 'ruRU', 'То, о чем ты просишь... грозит опасностью... всем нам.', '', 38134),
(196416, 'ruRU', 'Верховный лорд... Ты сумел заглянуть в Торгаст и указал пилигриму Утробы путь.', '', 38134),
(196415, 'ruRU', '', 'Не думай об этом. Сейчас не время.', 38134),
(196414, 'ruRU', 'Сколько всего я там видел... сколько мучений перенес...', '', 38134),
(196413, 'ruRU', '', 'Я боялась худшего. Мы с Бейном знаем, на какие ужасы способен Тюремщик.', 38134),
(196412, 'ruRU', 'Тралл. Хвала Матери-Земле! Я рад, что ты вернулся, брат мой.', '', 38134),
(196410, 'ruRU', '', 'Темная пещера, черная магия... Ну да, мы у цели.', 38134),
(196409, 'ruRU', '', 'Нужно найти, где они проводят ритуалы.', 38134),
(196404, 'ruRU', '', 'Прежде чем напасть, нанеси яд на оружие. Убей... Гатгрука...', 38134),
(196403, 'ruRU', 'Чтобы получить такую силу, понадобятся выгоревшие останки душ, затерянных в этой башне. Да. Взойди на нее, пилигрим.', '', 38134),
(196401, 'ruRU', '', 'Из этих корней можно сделать смертельный яд. Собери их, пока медведь не смотрит.', 38134),
(196397, 'ruRU', 'Маскировка помогала Дризе избегать опасностей. Попробуй тоже красться незаметно.', '', 38134),
(196390, 'ruRU', '', 'Шуджа, истинная сила Орды – в единстве. Лучше расскажи мне, что здесь происходит.', 38134),
(196389, 'ruRU', '', 'Мама... я пыталась остановить огров... но не смогла. Мне не хватило сил.', 38134),
(196386, 'ruRU', 'Я поступаю так, как мне велит Предназначение.\n\nБрокеры – гости в Орибосе, как и ты. Разве что отнимают у нас больше времени.', '', 38134),
(196383, 'ruRU', 'Здорово. Кульбуль готов помочь!', '', 38134),
(196382, 'ruRU', 'Свобода... для начала неплохо.', '', 38134),
(196377, 'ruRU', 'Пилигрим... Приходишь... И уходишь... Освободишь меня? Возможно. Поможешь? Это точно. А я помогу тебе. Так велит судьба.', '', 38134),
(196376, 'ruRU', 'Он использовал меня в своих целях... А затем бросил здесь. Я лишен памяти вот уже... Неважно. Ведь время не подвластно Смерти.', '', 38134),
(196375, 'ruRU', 'Он меня использовал. Тот, кого зовут Тюремщиком. Чтобы... создать... Хм-м... Мои воспоминания. Ты... Нет... он. Он их забрал.', '', 38134),
(196374, 'ruRU', 'Смертная душа? Нет. Да... Смертные в Утробе, в моей тюрьме? Хочешь спасти меня? Хм... Или использовать?', '', 38134),
(196373, 'ruRU', 'Кто-то из его слуг? Нет, нет. Ты... нечто другое.', '', 38134),
(196372, 'ruRU', '', 'Вентиры! Пришло время свергнуть угнетателей!', 38134),
(196371, 'ruRU', 'Стража! Убить баронессу!', '', 38134),
(196370, 'ruRU', '', 'Мерзкий огр. Посмотрим, как там Шуджа.', 38134),
(196369, 'ruRU', '', 'Мама... Я подвела... Орду...', 38134),
(196368, 'ruRU', '', 'Держись, Шуджа! Рекрут, за мной! Лок\'тар огар!', 38134),
(196348, 'ruRU', 'Эй, это... Помнишь, что я говорил о риске? Кажется, эта установка сейчас... взорвется.', '', 38134),
(196342, 'ruRU', 'Когда будешь взрывать зомби, помни, что эту блистательную технологию изобрел я!', '', 38134),
(196341, 'ruRU', 'Вот это мне нравится! Давай вернемся к воеводе.', '', 38134),
(196340, 'ruRU', 'Ого! Это даже круче огромного кабана! Попробуй еще раз!', '', 38134),
(196337, 'ruRU', '', 'Защищать эту рощу, даже ценой своей жизни – мой священный долг. Что привело тебя в Тирна Ваал?', 38134),
(196336, 'ruRU', '', 'Агенты дома Глаз заметили лича в горах среди гнезд костекрылов. Выследи его.', 38134),
(196334, 'ruRU', '', 'Нам нужно больше ездовых животных для новобранцев. А дикого костекрыла могут поймать только самые храбрые... или самые глупые.', 38134),
(196333, 'ruRU', '', 'Прекрасно. Одним назойливым личом меньше.', 38134),
(196332, 'ruRU', 'Я не подведу тебя, маркграфиня.', '', 38134),
(196331, 'ruRU', '', 'Безусловно. Посему достойным наказанием для тебя будет... титул барона. Отныне ты будешь моим послом у баронессы Дреки.', 38134),
(196330, 'ruRU', 'Я лишь хотел разоблачить Кел\'Тузада и вернуть тебя к власти.', '', 38134),
(196329, 'ruRU', 'Хм, что-то не то. Так... осталось лишь... готово! Попробуй-ка.', '', 38134),
(196328, 'ruRU', 'Итак, я обещал огромного кабана. Слово надо держать!', '', 38134),
(196327, 'ruRU', '', 'Балмедар. Ты связался с чужаками, предал свой дом... и свою маркграфиню.', 38134),
(196322, 'ruRU', '', 'Кел\'Тузад сбежал, но твои войска еще здесь. Уж не хочешь ли ты свергнуть меня и сама стать маркграфиней?', 38134),
(196321, 'ruRU', '', 'Мицелляры разделяют нашу ненависть к дому Ритуалов. И она может стать мощным оружием.', 38134),
(196314, 'ruRU', 'Чтобы до него добраться, нужно уничтожить стабилизирующие кристаллы.', '', 38134),
(196312, 'ruRU', 'Вперед! За Малдраксус! За Бастион! За Темные Земли!', '', 38134),
(196311, 'ruRU', '', 'У меня есть другие дела. Маяк для тебя починит распорядитель.', 38134),
(196306, 'ruRU', '', 'Действуй, и побыстрее. Речь о жизни моей дочери.', 38134),
(196305, 'ruRU', 'Придумывать – это ко мне! Как насчет... передавить их огромным кабаном?', '', 38134),
(196304, 'ruRU', '', 'Мы не бросаем своих! Придумай, как нам пройти мимо этой нежити.', 38134),
(196303, 'ruRU', 'Они что, воскрешают дракона?! А мы еще можем сбежать?', '', 38134),
(196302, 'ruRU', 'Так значит, вы из храма Чистоты... Хорошо.', '', 38134),
(196301, 'ruRU', '', 'Полемарх, у нас срочное послание к Архонту.', 38134),
(196300, 'ruRU', 'Зачем вы зажгли маяк, претенденты?', '', 38134),
(196296, 'ruRU', '', 'Тихо! Я что-то вижу!', 38134),
(196295, 'ruRU', 'А? Связь еще включена? Эй, слышишь? Пользование устройством означает согласие на риск!', '', 38134),
(196293, 'ruRU', 'На всякий пожарный. Вдруг твой рекрут оттуда свалится.', '', 38134),
(196292, 'ruRU', '', 'Если у него есть камера, зачем посылать кого-то наверх?', 38134),
(196290, 'ruRU', 'Ты слышишь меня, $n? Кажется, камера работает как часы!', '', 38134),
(196287, 'ruRU', 'Вот так! Выглядит неказисто, но работает.', '', 38134),
(196286, 'ruRU', 'Так, кажется, это уже слишком. Погоди...', '', 38134),
(196285, 'ruRU', 'Вот мини-версия \"Механорубаки-V\". Остается только его увеличить...', '', 38134),
(196277, 'ruRU', 'На помощь! Спригганы. Забрали аниму! Схватили друзей!', '', 38134),
(196272, 'ruRU', 'Не знаю... Но у меня есть одна идея!', '', 38134),
(196271, 'ruRU', '', 'О спасении говорить рано. Где остальные?', 38134),
(196270, 'ruRU', 'Ты – воевода Брека? Мать Шуджи! Она все повторяла, что ты спасешь наши шкуры.', '', 38134),
(196266, 'ruRU', 'Не все потеряно.', '', 38134),
(196265, 'ruRU', 'Вот почему я всегда ставлю на Орду! Валим отсюда!', '', 38134),
(196264, 'ruRU', 'Ну, сейчас ты получишь!', '', 38134),
(196263, 'ruRU', '', 'Надеюсь, что так. Она бывает... неосмотрительной.', 38134),
(196262, 'ruRU', 'Шуджа? Я ее не видел. Может, она не попала в плен?', '', 38134),
(196261, 'ruRU', '', 'Надо идти. У этих чудовищ моя дочь.', 38134),
(196260, 'ruRU', 'Ты падешь, и я пополню свою коллекцию новым оружием.', '', 38134),
(196258, 'ruRU', 'Вы здесь... хорошо. Свинобразы проводят какой-то мерзкий ритуал...', '', 38134),
(196257, 'ruRU', '', 'Оставь свои заботы у моих ног.', 38134),
(196253, 'ruRU', '', 'Орда не бросает своих солдат. И я уж точно не брошу ее.', 38134),
(196252, 'ruRU', '', 'Хорошо. До скорого! Твоя дочь не зря в тебя верит.', 38134),
(196250, 'ruRU', '', 'Маркграф поглотит вас!', 38134),
(196249, 'ruRU', 'Хм-м, странное создание. Что это? А, не важно. На что-нибудь сгодится.', '', 38134),
(196246, 'ruRU', '', 'Смотрите! Андуин!', 38134),
(196235, 'ruRU', '', 'Мы пойдем на север – посмотрим, что нашли другие. Вон\'са, отдохни перед тем, как вернуться к нам.', 38134),
(196233, 'ruRU', 'Швы не должны быть тугими, чтобы душа могла легко покидать кадавра и возвращаться в него.\n\nКадавр – лишь сосуд, и важно то, как душа им пользуется.', '', 38134),
(196232, 'ruRU', 'Победа и слава ваши! Сражайтесь вечно, пилигримы Утробы!', '', 38134),
(196231, 'ruRU', 'Да! Мордрета побеждена! Не могу поверить! Да здравствуют новые чемпионы арены!', '', 38134),
(196230, 'ruRU', 'Наши восходящие звезды встретятся с абсолютной чемпионкой Театра Боли: Мордретой, Вечной императрицей!', '', 38134),
(196229, 'ruRU', 'Я готов посмотреть на мир глазами нового кадавра.', '', 38134),
(196223, 'ruRU', 'Остаток души частично поглощен каким-то оружием.', 'Остаток души частично поглощен каким-то оружием.', 38134),
(196220, 'ruRU', 'На тебя идет охота. Берегись.', '', 38134),
(196218, 'ruRU', 'Солдаты Малдраксуса! Вы готовы к кровавой битве?!', '', 38134),
(196215, 'ruRU', 'Возвращайтесь в центральный зал! Пора начинать главное событие!', '', 38134),
(196208, 'ruRU', 'Ищи, не останавливайся!', '', 38134),
(196206, 'ruRU', 'Идеал вон там, в дальней части храма... с теми, кто ждет своей очереди.', '', 38134),
(196205, 'ruRU', '', 'Заверши максимальную серию приемов \"Потрошением\"!', 38134),
(196204, 'ruRU', '', 'Прежде чем использовать \"Потрошение\", убедись, что у тебя есть пять приемов.', 38134),
(196203, 'ruRU', '', 'Неплохо, но урон от \"Потрошения\" зависит от длины серии приемов. Еще раз!', 38134),
(196202, 'ruRU', '', 'Вот так. На \"Потрошение\" тратится вся серия приемов. Накопи пять приемов и попробуй еще раз!', 38134),
(196201, 'ruRU', '', 'Серия из четырех приемов! Пора применить \"Потрошение\"!', 38134),
(196200, 'ruRU', '', 'Обычных атак недостаточно. Используй \"Коварный удар\"!', 38134),
(196199, 'ruRU', '', 'У тебя три приема! Примени \"Потрошение\"!', 38134),
(196198, 'ruRU', '', 'Вот так! Чем длиннее серия, тем мощнее будет \"Потрошение\". Давай снова, но теперь после четырех приемов!', 38134),
(196197, 'ruRU', '', 'Используй \"Коварный удар\" почаще, чтобы продлевать серию приемов!', 38134),
(196196, 'ruRU', '', '\"Коварный удар\" продлевает серию приемов. Накопив три приема в серии, примени \"Потрошение\"!', 38134),
(196194, 'ruRU', 'Чем могу помочь?', '', 38134),
(196193, 'ruRU', '', 'Гиблое дело, видное бело.\n\nЯрость в клетке, мудрость века.\n\n<Сибилла Потерянная показывает на вас и истерически смеется.>', 38134),
(196192, 'ruRU', 'Невероятно! Претенденты прошли все испытания и вышли в финальный раунд!', '', 38134),
(196191, 'ruRU', '', 'Думаете, от меня так легко скрыться? Теперь вам отсюда не уйти!', 38134),
(196182, 'ruRU', 'Помогать – смысл моей жизни!', 'Помогать – смысл моей жизни!', 38134),
(196180, 'ruRU', 'Ты просто нечто. Не знаю, что, но нечто. Что тебе нужно?', '', 38134),
(196179, 'ruRU', 'Кто проник в мои владения?', '', 38134),
(196178, 'ruRU', 'Пилигрим? Ты знаешь, куда идти? Тьма... все ближе...', '', 38134),
(196174, 'ruRU', 'Я видел это место, когда искал Торгаст. Леди Праудмур рядом.', '', 38134),
(196173, 'ruRU', 'Эти души истязают огнем. Их плавят, превращая в материал для жутких созданий Тюремщика.', '', 38134),
(196169, 'ruRU', 'Перед тем как нас опять поймали, я... видел, что тебе удалось сбежать. Ты сможешь забрать нас из Утробы?', '', 38134),
(196168, 'ruRU', 'Мне снился дом... но проснулся я в преисподней.', '', 38134),
(196167, 'ruRU', 'Хочешь перекусить? Или, может, тебе помочь?', 'Хочешь перекусить? Или, может, тебе помочь?', 38134),
(196166, 'ruRU', 'Все что хочешь. Только попроси!', 'Все что хочешь. Только попроси!', 38134),
(196165, 'ruRU', 'Нужна помощь?', 'Нужна помощь?', 38134),
(196161, 'ruRU', '', '\"Звездный поток\" расходует астральную мощь. Накопи ее с помощью \"Гнева\".', 38134),
(196160, 'ruRU', '\"Звездный поток\" расходует астральную мощь. Накопи ее с помощью \"Гнева\".', '', 38134),
(196159, 'ruRU', '', 'Атакуй манекен \"Гневом\", пока не накопишь астральной мощи на \"Звездный поток\".', 38134),
(196158, 'ruRU', 'Атакуй манекен \"Гневом\", пока не накопишь астральной мощи на \"Звездный поток\".', '', 38134),
(196146, 'ruRU', 'А вот и твой пропавший барон, $n.', '', 38134),
(196140, 'ruRU', 'Как долго я пробыл в Утробе? Кажется, будто целую вечность.', '', 38134),
(196139, 'ruRU', 'Благодарю, $p. Теперь я смогу наконец прийти в себя.', '', 38134),
(196138, 'ruRU', '', '\"Огненный взрыв\" срабатывает мгновенно, и потому хорош в ближнем бою.', 38134),
(196137, 'ruRU', '', 'Из тебя выйдет толк. Попробуем еще разок, и можешь отправляться к свинобразам.', 38134),
(196136, 'ruRU', '', 'Да! Мгновенные заклинания вроде \"Огненного взрыва\" полезны в ближнем бою. Давай еще раз!', 38134),
(196134, 'ruRU', '', '\"Огненный взрыв\" срабатывает мгновенно. Используй его, если враг уже близко.', 38134),
(196133, 'ruRU', '', 'Держись на расстоянии и выпусти во врага \"Ледяную стрелу\"!', 38134),
(196132, 'ruRU', '', 'Продолжай применять \"Ледяную стрелу\", чтобы не подпускать к себе врага!', 38134),
(196131, 'ruRU', '', '\"Ледяная стрела\" замедляет врага. Используй ее, пока он не приблизится.', 38134),
(196130, 'ruRU', '', 'Используй \"Стихийный удар\" вместо \"Молнии\", когда враг подойдет к тебе!', 38134),
(196128, 'ruRU', '', 'Потренируем атаки дальнего боя еще раз. Они эффективны против тех, кто сражается вблизи.', 38134),
(196127, 'ruRU', '', 'Сокрушай врага атаками ближнего боя. Ты быстро учишься!', 38134),
(196126, 'ruRU', '', 'Еще раз! \"Стихийный удар\" весьма полезен в ближнем бою.', 38134),
(196125, 'ruRU', '', 'Расстояние дает тебе преимущество. Порази врага \"Молнией\"!', 38134),
(196124, 'ruRU', '', 'Помни – пока враг бежит к тебе, ему можно нанести немало урона!', 38134),
(196122, 'ruRU', '', 'Хорошо! А теперь примени \"Молнию\" еще раз. Приготовься!', 38134),
(196121, 'ruRU', '', 'Призвать полемарха Адреста тебе помогут распорядители. Выбери одного себе в спутники.', 38134),
(196120, 'ruRU', '', 'Замечательно. Используй \"Стихийный удар\", когда враг подойдет близко!', 38134),
(196119, 'ruRU', '', 'Хорошо. Используй \"Молнию\", чтобы поражать врагов на расстоянии.', 38134),
(196118, 'ruRU', 'Хуже, чем в последний раз, стать не могло...', '', 38134),
(196115, 'ruRU', 'Не дайте сомнениям овладеть вами. Дело и так плохо.', '', 38134),
(196114, 'ruRU', 'Еще одно испытание пройдено! А наши претенденты не из слабаков!', '', 38134),
(196113, 'ruRU', 'Нюхом чую, она где-то рядом!', '', 38134),
(196110, 'ruRU', 'Она точно где-то здесь!', '', 38134),
(196109, 'ruRU', 'Удачка, а ну покажись!', '', 38134),
(196106, 'ruRU', '', 'Вот так. Давай еще раз. С помощью \"Лунного огня\" и \"Гнева\" ты разберешься со свинобразами.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196105, 'ruRU', '', 'Слишком рано. \"Лунный огонь\" нанесет больше урона, если обновить его, когда действие почти истекло.', 38134),
(196104, 'ruRU', '', '\"Лунный огонь\" слабеет, обнови его!', 38134),
(196102, 'ruRU', '', '\"Лунный огонь\" наносит периодический урон. Используй \"Гнев\", пока враг поражен им.', 38134),
(196101, 'ruRU', '', 'Хорошо. Продолжай использовать \"Гнев\", пока \"Лунный огонь\" наносит урон. Я скажу, когда повторить \"Лунный огонь\".', 38134),
(196099, 'ruRU', '', 'Применяй \"Лунный огонь\" в начале боя. Он наносит периодический урон.', 38134),
(196097, 'ruRU', 'Видишь ли, среди нас точно есть предатель. Кто-то знал, когда именно напасть на экипаж.', 'Видишь ли, среди нас точно есть предатель. Кто-то знал, когда именно напасть на экипаж.', 38134),
(196096, 'ruRU', 'Что-то здесь не так...', 'Что-то здесь не так...', 38134),
(196095, 'ruRU', '', 'Хорошо, давай еще раз. Надо убедиться, что ты выстоишь против свинобразов.', 38134),
(196094, 'ruRU', '', 'Слишком рано. Чтобы нанести больше урона, обновляй действие \"Порчи\", когда оно почти закончилось.', 38134),
(196093, 'ruRU', '', 'Действие \"Порчи\" истекает, повтори!', 38134),
(196091, 'ruRU', '', '\"Порча\" наносит периодический урон.', 38134),
(196090, 'ruRU', '', 'Продолжай! С помощью \"Порчи\" чернокнижник может нанести сокрушительный урон.', 38134),
(196088, 'ruRU', 'Претенденты прошли первое испытание! Но до победы еще далеко!', '', 38134),
(196086, 'ruRU', 'Нет... надежды! Нет... спасения!', '', 38134),
(196085, 'ruRU', '', 'Используй \"Порчу\" в начале боя. Она наносит врагу периодический урон.', 38134),
(196084, 'ruRU', 'Говори!', '', 38134),
(196083, 'ruRU', 'Тогда мы знаем, куда идти.', '', 38134),
(196082, 'ruRU', '', 'Котел, который он упомянул, находится за рекой душ. Там же, должно быть, держат Бейна.', 38134),
(196081, 'ruRU', 'Лорд-адмирал, вы понимаете, о чем он говорит?', '', 38134),
(196080, 'ruRU', 'Из Утробы... нет выхода...', '', 38134),
(196079, 'ruRU', 'Нет!.. Молчит... сломан!', '', 38134),
(196078, 'ruRU', 'Путевой камень... у Котла... наверное...', '', 38134),
(196077, 'ruRU', 'Так путь все-таки существует? Как его найти?', '', 38134),
(196076, 'ruRU', 'Все бесполезно! Путь... давно... закрыт...', '', 38134),
(196075, 'ruRU', 'Как отсюда выбраться? Отвечай!', '', 38134),
(196074, 'ruRU', 'Наш... друг хочет что-то нам сказать?', '', 38134),
(196073, 'ruRU', 'Ты как раз вовремя.', '', 38134),
(196072, 'ruRU', '', 'Все будут... служить... Тюремщику...', 38134),
(196071, 'ruRU', '', 'Я сломлю вас!', 38134),
(196070, 'ruRU', 'Отсюда – точно смогу. Ведите нас.', '', 38134),
(196069, 'ruRU', 'Спасибо, $n. Я уже боялся, что так и не освобожусь от оков.', '', 38134),
(196068, 'ruRU', 'Если устанешь – не страшно. Все смертные хлипкие.', '', 38134),
(196061, 'ruRU', '', 'У тебя есть заклинания дальнего боя. Начинай с них, чтобы наносить первый удар!', 38134),
(196060, 'ruRU', '', 'Вот так, теперь повтори! \"Слово Тьмы: Боль\" продолжит наносить урон врагу, пока ты атакуешь его с помощью \"Кары\".', 38134),
(196059, 'ruRU', '', 'Слишком рано: \"Слово Тьмы: Боль\" нанесет больше урона, если обновить его, когда действие почти истекло.', 38134),
(196058, 'ruRU', '', 'Пора обновить \"Слово Тьмы: Боль\" на цели!', 38134),
(196052, 'ruRU', '', 'Браво, еще разок! Искусный жрец может легко изматывать врагов \"Словом Тьмы: Боль\".', 38134),
(196051, 'ruRU', '', 'Хорошо! Применяй \"Кару\", пока \"Слово Тьмы: Боль\" наносит урон. Я скажу, когда обновить его.', 38134),
(196050, 'ruRU', '', 'Используй \"Слово Тьмы: Боль\" в начале боя. Оно наносит периодический урон.', 38134),
(196032, 'ruRU', '', 'У тебя достаточно энергии Света! Примени \"Щит праведника\"!', 38134),
(196031, 'ruRU', '', 'Неплохо, но энергии Света больше нет. Применяй \"Удар воина Света\" и копи ее!', 38134),
(196030, 'ruRU', '', 'Неплохо. Продолжай применять \"Удар воина Света\" и копить энергию.', 38134),
(196029, 'ruRU', '', 'У тебя достаточно энергии. Примени \"Щит праведника\"!', 38134),
(196028, 'ruRU', '', 'Хорошо! Умелый паладин быстро накапливает энергию Света и тратит ее, чтобы нанести урон.', 38134),
(196027, 'ruRU', '', 'Еще раз! Применяй \"Удар воина Света\" и копи энергию!', 38134),
(196026, 'ruRU', '', '\"Удар воина Света\" даст тебе энергию. Ее можно потратить на более мощные атаки.', 38134),
(196025, 'ruRU', '', 'Тебя переполняет сила Света! Примени \"Щит праведника\"!', 38134),
(196024, 'ruRU', '', 'Обычных атак недостаточно. Попробуй \"Удар воина Света\"!', 38134),
(196022, 'ruRU', '', 'Еще раз. \"Рывок\"!', 38134),
(196021, 'ruRU', '', 'Повтори \"Рывок\", чтобы вступить в ближний бой. Всегда начинай схватку с \"Рывка\".', 38134),
(196020, 'ruRU', '', 'Применяй \"Рывок\" в начале боя. Покажи врагу ярость Орды!', 38134),
(196015, 'ruRU', 'Я пригожусь!', 'Я пригожусь!', 38134),
(196003, 'ruRU', 'У меня есть еда, напитки и особые предметы, которые могут тебе понадобиться.\n\nЧто я могу для тебя сделать?', '', 38134),
(195999, 'ruRU', '', 'Ну, все! Со свинобразами ты справишься. Пора спасать наших друзей!', 38134),
(195997, 'ruRU', '', 'Так держать! \"Рывок\" дает тебе ярость и наносит урон врагам. Всегда начинай бой с него.', 38134),
(195994, 'ruRU', '', '\"Мощный удар\" расходует ярость, нанося урон. Используй его, пока хватает ярости!', 38134),
(195993, 'ruRU', '', 'Бой надо всегда начинать с \"Рывка\". Это дает тебе ярость, нужную для многих способностей.', 38134),
(195992, 'ruRU', '', 'Вместо обычных атак примени \"Мощный удар\"!', 38134),
(195990, 'ruRU', 'Твой путь нужно согласовать с Предназначением. Ты с этим справишься?', '', 38134),
(195989, 'ruRU', '', 'Атакуй меня \"Рывком\"!', 38134),
(195988, 'ruRU', '', 'Понимаю, Клейя. Нужно обратиться к полемарху. Он представит вас Архонту.', 38134),
(195985, 'ruRU', '', 'Начнем новый урок!', 38134),
(195981, 'ruRU', '', 'Свинобразы гораздо опаснее мурлоков, рекрут. Тебе нужно подготовиться к битве с ними.', 38134),
(195980, 'ruRU', '', 'А еще она – солдат Орды. И знает, что мы никого не бросаем.', 38134),
(195979, 'ruRU', '', 'Она моя дочь. Как я могла не прийти?', 38134),
(195978, 'ruRU', '', 'Шуджа... сказала... ты... придешь...', 38134),
(195977, 'ruRU', 'За Старыми воротами может быть небезопасно.', 'За Старыми воротами может быть небезопасно.', 38134),
(195975, 'ruRU', '', 'Спасибо, Тирнакс. И тебе, Кадарин. Вам повезло, что вы есть друг у друга.', 38134),
(195972, 'ruRU', '', 'Надеюсь, она согласится это отдать.', 38134),
(195971, 'ruRU', '', 'Алекстраза наверняка сохранила что-то на память о Кориалстразе. Знак утраченной любви.', 38134),
(195969, 'ruRU', '', 'Вон\'са!', 38134),
(195968, 'ruRU', '', 'Хочу... есть...', 38134),
(195959, 'ruRU', 'Я подготовлю центурионов к атаке!', '', 38134),
(195958, 'ruRU', '', '$n, мы пойдем вперед. А вы трое – поищите других выживших.', 38134),
(195957, 'ruRU', '', 'Нужно сообщить об этом Архонту. Встретимся в Обители Героев, $n!', 38134),
(195953, 'ruRU', 'Бегите, смертные! Я ее задержу!', '', 38134),
(195952, 'ruRU', 'Не сегодня, Кааль!', '', 38134),
(195949, 'ruRU', '', 'Довольно! Ваш пример покажет, чего стоит гнев Владыки.', 38134),
(195948, 'ruRU', '', 'Упрямые бунтовщики. Ваш конец близок!', 38134),
(195947, 'ruRU', '', 'Эти чертоги станут вашей могилой!', 38134),
(195946, 'ruRU', '', 'Это пленница Владыки. Вам ее не увести!', 38134),
(195945, 'ruRU', '', 'Стража! Убейте их!', 38134),
(195943, 'ruRU', '', 'Так вот за кем вы пришли. Она не покинет эту тюрьму!', 38134),
(195942, 'ruRU', 'Скорей! Забирайте Зе\'рали и бегите!', '', 38134),
(195940, 'ruRU', 'Денатрий использует ее Свет против своих врагов. Мы направим этот Свет на него самого.', '', 38134),
(195939, 'ruRU', 'Пленницу держат внизу. Зе\'рали – наару, спустившаяся в Пепельный Предел. Ее спутники думали, что она погибла.', '', 38134),
(195937, 'ruRU', 'Самый крупный – это вожак. Убейте его, и остальные разбегутся.', '', 38134),
(195932, 'ruRU', 'Этот шторм случился не сам по себе...', '', 38134),
(195931, 'ruRU', '', 'Осторожнее, пилигрим Утробы. Они снуют среди нас.', 38134),
(195930, 'ruRU', 'Спасибо. Мы еще найдем экспедицию.', '', 38134),
(195924, 'ruRU', '', 'Ну и шторм. Думаю, первому кораблю тоже досталось.', 38134),
(195920, 'ruRU', '', 'Мурлоки опасны, когда их много. Верни наши припасы, но избегай больших групп.', 38134),
(195918, 'ruRU', '', '$n! Хорошо, что ты с нами. Надо перегруппироваться.', 38134),
(195917, 'ruRU', '', 'Я уничтожу вас!', 38134),
(195914, 'ruRU', '', 'Вы поплатитесь кровью.', 38134),
(195913, 'ruRU', '', 'Смерть предателям.', 38134),
(195910, 'ruRU', 'Это ориентир для тех душ, которые потеряли связь со своим миром.\n\n<Писарь судьбы некоторое время задумчиво на вас смотрит.>\n\nЯ чувствую, что к тебе прикоснулась величайшая сила, $p. Хотя твое существование не раз прерывалось, эта сила удерживала тебя в мире смертных.\n\nВозможно, такие устройства, как это, позволят тебе оставаться в Темных Землях, даже если тебя постигнет недобрая участь.\n\nНа все воля Предназначения. Он и укажет нам путь.', '', 38134),
(195905, 'ruRU', 'Здесь брокеры устроили место, где ты можешь хранить свои личные вещи. Как же они это назвали... кажется, банк.\n\nЧестно говоря, мы плохо понимаем, что такое владеть имуществом, но брокеры всегда знали в этом толк.', '', 38134),
(195903, 'ruRU', '', 'Это существо гораздо меньше, чем я думала.', 38134),
(195902, 'ruRU', '', 'Это пилигрим Утробы.', 38134),
(195901, 'ruRU', 'Здесь мы устроили зал ремесленников. Все для тебя, конечно же.\n\nМы, брокеры, повсюду путешествуем и узнаем об обитателях разных миров. Благодаря этому наши товары всегда соответствуют потребностям наших клиентов. \n\nЧлены нашего картеля научились различным профессиям, которые ценятся среди обитателей твоего мира. Они с радостью помогут тебе в обучении разным ремеслам.', '', 38134),
(195899, 'ruRU', 'Приветствую. Мы к твоим услугам.', '', 38134),
(195898, 'ruRU', 'Здесь можно приобрести более оригинальные товары. \n\nМы с коллегами специализируемся на поиске и поимке экзотических животных. Мы также можем предоставить доступ в ваше так называемое Хранилище Бездны и возможность применить магию трансмогрификации. \n\nИ наконец, мой друг Та\'визаж поможет тебе изменить внешность. Спасать миры можно и в шикарном виде, так ведь?', '', 38134),
(195896, 'ruRU', 'Добро пожаловать на базар, друг мой. Проси нас о чем пожелаешь.', '', 38134),
(195893, 'ruRU', '', 'Солдаты, приготовьтесь!', 38134),
(195892, 'ruRU', 'Воевода! Такой шторм мы не выдержим!', '', 38134),
(195891, 'ruRU', '', 'Мы будем сражаться в небе. Пусть пламя Отваги направит твой клинок.', 38134),
(195888, 'ruRU', 'Отомсти... им...', 'Отомсти... им...', 38134),
(195887, 'ruRU', 'Все могло быть... иначе...', 'Все могло быть... иначе...', 38134),
(195886, 'ruRU', 'Я жалею... лишь об одном...', 'Я жалею... лишь об одном...', 38134),
(195885, 'ruRU', 'Я это... заслужил...', 'Я это... заслужила...', 38134),
(195884, 'ruRU', 'Спаси... остальных...', 'Спаси... остальных...', 38134),
(195883, 'ruRU', 'Я... исчезаю...', 'Я... исчезаю...', 38134),
(195882, 'ruRU', 'Я ожидал... большего...', 'Я ожидала... большего...', 38134),
(195881, 'ruRU', 'Прости... меня...', 'Прости... меня...', 38134),
(195880, 'ruRU', '', 'Мне нужно быстрое и выносливое тело, с которым я смогу охотиться во всех Темных Землях!\n\nСделай мне тело собаки.', 38134),
(195879, 'ruRU', '', 'Я хочу быть наравне с другими местными душами. Ты поможешь раскрыть мой потенциал?', 38134),
(195878, 'ruRU', 'Нет-нет! Каждый должен внести свой вклад. На моем месте ты поступишь так же.', 'Нет-нет! Каждый должен внести свой вклад. На моем месте ты поступишь так же.', 38134),
(195877, 'ruRU', 'Нет, Вероника! Тебе тоже не хватает анимы. Нужно сказать госпоже Михаэле, что это слишком.', 'Нет, Вероника! Тебе тоже не хватает анимы. Нужно сказать госпоже Михаэле, что это слишком.', 38134),
(195876, 'ruRU', 'Не бойся. Я отдам за нас обоих.', 'Не бойся. Я отдам за нас обоих.', 38134),
(195875, 'ruRU', 'Снова подати? Но у меня не осталось душ, а анимы мне самому не хватает. Разве госпожа Михаэла не попросила исключить меня из списка?..', 'Снова подати? Но у меня не осталось душ, а анимы мне самой не хватает. Разве госпожа Михаэла не попросила исключить меня из списка?..', 38134),
(195873, 'ruRU', '', 'Пока! Заходи еще!', 38134),
(195872, 'ruRU', 'Превосходно.', '', 38134),
(195871, 'ruRU', '', 'Чтобы \"Пагубный восторг\" сработал лучше, поражай как можно больше врагов заклинаниями периодического урона!', 38134),
(195870, 'ruRU', '', 'Примени \"Пагубный восторг\".', 38134),
(195869, 'ruRU', '', '\"Пагубный восторг\" – мощное заклинание, требующее осколка души. Оно терзает врагов, усиливая твои эффекты периодического урона.', 38134),
(195868, 'ruRU', '', '\"Стрела Тьмы\" поражает противника силой Тьмы, не тратя осколки души.', 38134),
(195867, 'ruRU', '', 'Превосходно.', 38134),
(195866, 'ruRU', 'Чтобы \"Пагубный восторг\" сработал лучше, поражай как можно больше врагов заклинаниями периодического урона!', '', 38134),
(195865, 'ruRU', 'Примени \"Пагубный восторг\".', '', 38134),
(195864, 'ruRU', '\"Пагубный восторг\" – мощное заклинание, требующее осколка души. Оно терзает врагов, усиливая твои эффекты периодического урона.', '', 38134),
(195863, 'ruRU', '\"Стрела Тьмы\" поражает противника силой Тьмы, не тратя осколки души.', '', 38134),
(195862, 'ruRU', '', 'Вот так.', 38134),
(195859, 'ruRU', '', 'Приберись пока, а я разожгу огонь в горне.', 38134),
(195858, 'ruRU', '', 'Мы помогаем.', 38134),
(195857, 'ruRU', 'Сдаюсь! Твоя сила поможет нам на острове.', '', 38134),
(195856, 'ruRU', '', 'Распорядители созданы, чтобы нам помогать. Если они не могут выполнять поручения, то впадают в уныние.', 38134),
(195855, 'ruRU', '', 'Починить! Починить!', 38134),
(195854, 'ruRU', 'Не стой к врагу спиной!', '', 38134),
(195853, 'ruRU', 'Никогда не беги от врагов! Победа или смерть!', '', 38134),
(195852, 'ruRU', 'Я буду защищаться. Атакуй меня – в полную силу!', '', 38134),
(195851, 'ruRU', '', 'Это честь для меня, что вы двое сейчас рядом. Наконец-то...', 38134),
(195844, 'ruRU', '', 'Хорошо. А теперь... дождь? Наш шаман не предсказывал дождя.', 38134),
(195835, 'ruRU', 'Ты пойдешь с нами!', '', 38134),
(195834, 'ruRU', '', 'Мы должны быть готовы ко всему. Покажи мне, чего ты стоишь в бою.', 38134),
(195833, 'ruRU', '', 'Ай! На помощь! Холодно!', 38134),
(195832, 'ruRU', '', 'Наконец-то...', 38134),
(195831, 'ruRU', '', '<Глаза Софоны закрыты – она наслаждается моментом.>', 38134),
(195827, 'ruRU', 'Давай! Подчини его!', '', 38134),
(195825, 'ruRU', '...повинуюсь...', '', 38134),
(195824, 'ruRU', 'Я тебе не подчинюсь! Я...', '', 38134),
(195823, 'ruRU', 'Рыцари Черного Клинка! Рубите это чудовище!', '', 38134),
(195822, 'ruRU', 'Эта цель очень опасна. Я подам сигнал рыцарям Черного Клинка, они помогут нам, когда мы вступим в бой.', '', 38134),
(195821, 'ruRU', 'Эти души слишком повреждены. Должен быть другой способ...', '', 38134),
(195820, 'ruRU', 'Используй шлем, пока она слаба!', '', 38134),
(195819, 'ruRU', '', 'Претендентка Клейя.', 38134),
(195818, 'ruRU', '', 'Меня исцеляет земля под вашими ногами!', 38134),
(195817, 'ruRU', '', 'Родная земля, дай мне сил!', 38134),
(195816, 'ruRU', '', 'Прости... я не смогла спасти тебя...', 38134),
(195815, 'ruRU', '', 'Мерзкие создания!', 38134),
(195814, 'ruRU', '', 'Распускаются новые цветы. Они изгонят вас!', 38134),
(195813, 'ruRU', '', 'Вам не уйти от моей хватки!', 38134),
(195812, 'ruRU', '', 'Вас оплетут корни!', 38134),
(195811, 'ruRU', '', 'Кто... здесь?', 38134),
(195810, 'ruRU', '', 'Глаза застилает мрак... но мои корни сильны, и я не дам вам отобрать мою рощу!', 38134),
(195795, 'ruRU', '', 'Удивительно, что можно найти брошенным в Утробе. Скажу без ложной скромности: я сразу узнаю что-то ценное.', 38134),
(195790, 'ruRU', 'Тебе всегда было плевать на свой народ, ты жаждал силы!', '', 38134),
(195789, 'ruRU', '', 'До встречи, пилигрим Утробы.', 38134),
(195788, 'ruRU', '', 'Надеюсь, ты не станешь злоупотреблять моим гостеприимством. У тебя много дел.', 38134),
(195787, 'ruRU', 'Не расслабляйся, Отец Снов.', '', 38134),
(195785, 'ruRU', 'Тебе меня не удержать!', '', 38134),
(195784, 'ruRU', 'Рано меня списывать, старый лоа!', '', 38134),
(195783, 'ruRU', 'Поспешите! Цена теперь не важна.', '', 38134),
(195780, 'ruRU', 'Приветствую, $n. Лорд Болвар попросил нас тебе помочь.', '', 38134),
(195778, 'ruRU', 'А вы молодцы, но смотреть на то, что с Мве\'залой теперь будет, вам не надо. Хватайте побрякушки – и брысь отсюда!', '', 38134),
(195777, 'ruRU', 'Ты в моих владениях, смертная душа. Прячься где угодно... я все равно тебя найду.', '', 38134),
(195776, 'ruRU', '', 'Нам нельзя останавливаться. Пора сразиться с тираном!', 38134),
(195775, 'ruRU', 'Подданные мои, мы одержали небольшую победу. Можете забрать свою аниму.', '', 38134),
(195774, 'ruRU', 'Такие мелкие, такие слабые!', '', 38134),
(195773, 'ruRU', 'Тени заберут вас!', '', 38134),
(195772, 'ruRU', 'Я заберу ваши жизни!', '', 38134),
(195770, 'ruRU', 'Глупые смертные! Я – настоящий бог Смерти!', '', 38134),
(195769, 'ruRU', 'От смерти не уйти!', '', 38134),
(195768, 'ruRU', 'Я повелеваю тьмой!', '', 38134),
(195764, 'ruRU', 'Смотрите – вот кто защитил вас! Браво, пилигрим Утробы.', '', 38134),
(195762, 'ruRU', 'Главное – доспехи и мускулы! С ними я могу окунуться в гущу сражения и смеяться оттого, как отскакивают жалкие удары противника!', '', 38134),
(195760, 'ruRU', 'Твоим новобранцам нужно поразмяться, они совсем заскучали без боя!', '', 38134),
(195759, 'ruRU', 'Цыц, малявка! Здесь Бвонсамди теперь главный.', '', 38134),
(195758, 'ruRU', '', 'Смертокрыл чуть не уничтожил весь Азерот. Кто бы мог подумать, с таким-то именем!', 38134),
(195757, 'ruRU', '', 'Если найдешь мощное оружие – убедись, что его не осквернил Пылающий Легион или Древние боги.', 38134),
(195756, 'ruRU', 'Ха-ха, сколько же вас... только чтобы одолеть... меня одного...', '', 38134),
(195755, 'ruRU', 'Любишь ты похвастаться, Бвонсамди... Но перед Тюремщиком ты ничто!', '', 38134),
(195753, 'ruRU', 'Во мне течет сила Малдраксуса! Я – лик смерти!', '', 38134),
(195752, 'ruRU', 'Но ты не переживай, обдумаешь еще все. Времени у тебя будет предостаточно.', '', 38134),
(195751, 'ruRU', 'Кости зовут!', '', 38134),
(195750, 'ruRU', 'Времена меняются, дружище. Зря ты думал, что смертные слабы. У них всегда был... потенциал.', '', 38134),
(195749, 'ruRU', 'Черепа павших молят о мести!', '', 38134),
(195747, 'ruRU', 'Узрите мое новое вместилище, достойное повелителя смерти!', '', 38134),
(195746, 'ruRU', 'Ритуал завершен, и больше никто не в силах ему помешать.', '', 38134),
(195745, 'ruRU', 'Это я тоже предвидел!', '', 38134),
(195744, 'ruRU', 'Их кости усилят мой сосуд!', '', 38134),
(195743, 'ruRU', 'Добровольная жертва для дома Ритуалов!', '', 38134),
(195742, 'ruRU', 'Ритуал будет завершен! Кости павших, поприветствуйте наших гостей!', '', 38134),
(195739, 'ruRU', 'Нет... спасения...', '', 38134),
(195737, 'ruRU', 'Отчаяние... вечные муки...', '', 38134),
(195736, 'ruRU', 'Вот оно... избавление...', '', 38134),
(195734, 'ruRU', 'Боль... страдания... забвение...', '', 38134),
(195724, 'ruRU', 'Не терпится помочь тебе, пилигрим.', '', 38134),
(195721, 'ruRU', 'Ловкие пальцы, быстрая реакция и острое зрение – вот что мне нравится. Я помогу тебе достать припасы и продать ненужные вещи.', '', 38134),
(195720, 'ruRU', 'Если бы моя кожа выделяла регенерирующие споры, я бы могла исцелять нас всех во время битвы.', '', 38134),
(195718, 'ruRU', 'Когда меня снова соберут, из нас выйдет отличная команда!', '', 38134),
(195717, 'ruRU', 'Претенденты тренируются долгие века, закаляя тело и дух. Они очищаются от прошлого и смотрят только в будущее.\n\nПосвящают всю свою жизнь служению не только Архонту, но и всем душам. Друг другу.\n\nПерерождение, ранее лишь возвышенная мечта, наконец становится для этих претендентов реальностью.', '', 38134),
(195716, 'ruRU', 'Скоро я буду сражаться вместе с тобой!', '', 38134),
(195715, 'ruRU', 'Работать в команде очень важно. Мне нужно достаточно силы, чтобы нести в бой других.', '', 38134),
(195713, 'ruRU', 'Ах да! Если хочешь быстрее перемещаться между утесами, возьми шелкострел. Он поможет тебе перескакивать с места на место!', '', 38134),
(195705, 'ruRU', 'Еще одно искусное творение!', '', 38134),
(195703, 'ruRU', 'Обидно. Тошногнил! За работу! Надеюсь, ты лучше своего предшественника.', '', 38134),
(195702, 'ruRU', 'Растяпы! Кровомес! Не повреди руки и ноги, когда будешь их отрывать.', '', 38134),
(195701, 'ruRU', '', 'Тебе нужен арбалет. Кинжал бесполезен, если не можешь подойти близко!', 38134),
(195700, 'ruRU', 'Думаю, длинный кинжал под ребро – это в моем стиле.', '', 38134),
(195699, 'ruRU', 'Что ты предпочитаешь, смертная душа, кинжал, меч или топор?', '', 38134),
(195689, 'ruRU', '', 'Эй! Смотри! Еще личи! Давай с ними поиграем!', 38134),
(195688, 'ruRU', 'Замаскировать тебя под огра, чтобы проникнуть в их крепость и остановить ритуал по оживлению мертвого дракона. Это безумие.\n\nДавай скорее начнем!', '', 38134),
(195687, 'ruRU', '', 'Ты возглавишь наступление, $p. Избавь мою рощу от этих чужаков!', 38134),
(195686, 'ruRU', '', 'Трубите сигнал! Я созываю весь Арденвельд на Охоту!', 38134),
(195674, 'ruRU', '', 'Кирии... служащие Утробе? Какой кошмар...', 38134),
(195672, 'ruRU', '', 'Есть лишь один способ заслужить встречу с Архонтом: следовать пути и заслужить право на перерождение.', 38134),
(195671, 'ruRU', '', 'Они не узники, они служат Утробе добровольно. Я прошу об аудиенции!', 38134),
(195670, 'ruRU', '', 'Это печально. Но если Арбитр приговорила их к такой судьбе, нас это не касается.', 38134),
(195669, 'ruRU', '', 'Новая душа принесла тревожные новости о кириях в Утробе. Нужно сообщить об этом Архонту!', 38134),
(195668, 'ruRU', '', 'До свидания.', 38134),
(195666, 'ruRU', '', 'Почему \"Мнемида\" работает? Мы не можем тратить столько анимы.', 38134),
(195665, 'ruRU', '', 'Наверное, они заметили, что я включила привратницу. Я разберусь, $n.', 38134),
(195664, 'ruRU', '', 'Что все это значит?', 38134),
(195662, 'ruRU', 'Эта анима принадлежит Казначею. Уходите или умрите!', '', 38134),
(195661, 'ruRU', '', 'Вон он! Мы его нагоняем!', 38134),
(195660, 'ruRU', '', 'Смертозуб! Беги за экипажем!', 38134),
(195659, 'ruRU', 'Все можно починить. И это тоже.', '', 38134),
(195656, 'ruRU', '', 'Ему не уйти! Заставим этого пса осознать свои ошибки.', 38134),
(195655, 'ruRU', 'А-а-а! Нет! Хватит... А-а-агх!', 'А-а-а! Нет! Хватит... А-а-агх!', 38134),
(195654, 'ruRU', 'Чудесный вид! Только посмотри на этого шелкоброда! Бедняжка, наверное, скучает здесь совсем один. Развесели его!', '', 38134),
(195653, 'ruRU', 'Мы все порой чем-то жертвуем. Владыке нужна анима.', 'Мы все порой чем-то жертвуем. Владыке нужна анима.', 38134),
(195652, 'ruRU', 'Нет! Только не это. Я не могу...', 'Нет! Только не это. Я не могу...', 38134),
(195650, 'ruRU', 'Рыцари Черного Клинка знают, как заставить мертвых говорить. Если нам удастся поймать одну из теней, мы выведаем ее тайны.', '', 38134),
(195648, 'ruRU', 'Сюда прибывают бесчисленные души. Но некоторые пробыли здесь уже много тысяч лет. Наверняка кто-то из них знает путь наружу.', '', 38134),
(195647, 'ruRU', '', 'Трудно представить, какие ужасы пришлось испытать этим духам. Боюсь, их уже не спасти.', 38134),
(195643, 'ruRU', '', 'Я видела Бейна и Андуина каждый раз, когда сбегала. Они живы... И это все, что я могу сказать точно.', 38134),
(195640, 'ruRU', '', 'Что бы Тюремщик с банши ни задумали, для нас это кончится плохо.', 38134),
(195639, 'ruRU', 'Мы пережили нескончаемые муки. Временами казалось, что он испытывает нас... Хотя не знаю, зачем.', '', 38134),
(195638, 'ruRU', '', 'Он лично наблюдал за нашим... заключением.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195637, 'ruRU', '', 'Тюремщик правит этим местом. О нем мало известно, но мы явно заинтересовали его.', 38134),
(195635, 'ruRU', '', 'Знаю, в этом... месте... непросто освоиться. \n\nПодожди немного и соберись с силами. Потом я расскажу тебе все, что знаю.', 38134),
(195634, 'ruRU', '', 'Верно. И так же, как и нам, боль им причиняет Сильвана Ветрокрылая.', 38134),
(195633, 'ruRU', 'Что-то изменилось. Теперь здесь заточены и чистые, благородные души. И они кричат от боли.', '', 38134),
(195632, 'ruRU', '', 'Обитатели этого мира называют его Утробой. Как я поняла, он служит тюрьмой для самых нечестивых душ во всем мироздании.', 38134),
(195631, 'ruRU', '', 'Так... С чего начать?', 38134),
(195622, 'ruRU', 'Нет ничего приятнее, чем видеть, как душа обретает новое тело. \n\nС твоей помощью мы изготовим новую армию боевых кадавров, которые приведут Малдраксус к победе!', '', 38134),
(195615, 'ruRU', 'Довольно тайн!', 'Довольно тайн!', 38134),
(195614, 'ruRU', 'Впустите нас!', 'Впустите нас!', 38134),
(195613, 'ruRU', 'Что Архонт скрывает от нас?!', 'Что Архонт скрывает от нас?!', 38134),
(195612, 'ruRU', '', 'Рекомендую разложить свитки по местам. Только раскольникам разрешено просматривать их содержимое.', 38134),
(195611, 'ruRU', '', 'И ты здравствуй, хранительница.', 38134),
(195610, 'ruRU', '', 'Здравствуй, служительница.', 38134),
(195607, 'ruRU', 'Легко сказать. Трудно сделать.', '', 38134),
(195606, 'ruRU', '', 'Нам нужно поговорить с Архонтом.', 38134),
(195605, 'ruRU', '', 'Сюда.', 38134),
(195603, 'ruRU', '', 'Так это правда... Раскольники служат Утробе!', 38134),
(195602, 'ruRU', '', 'Ясно. Я готова.', 38134),
(195601, 'ruRU', '', 'Если займем еще один...', 38134),
(195600, 'ruRU', '', 'Мы вступим в игру, когда ты займешь храм Чистоты. Вместе мы добьемся величия...', 38134),
(195599, 'ruRU', '', 'Появилась новая сила. Теперь не только мы сражаемся за Бастион.', 38134),
(195598, 'ruRU', '', 'Он слишком узко мыслит. Выслушай меня.', 38134),
(195597, 'ruRU', '', 'Почему ты приказала ему уйти?', 38134),
(195594, 'ruRU', '', 'Чтобы просмотреть воспоминания, поднесите их к соответствующему глазу.', 38134),
(195591, 'ruRU', '', 'Сколько же ты их таких изготовил?', 38134),
(195590, 'ruRU', '', 'Добро пожаловать в хранилище Архонта. Пожалуйста, назовите цель визита.', 38134),
(195589, 'ruRU', 'Готово! Идите и делайте то, зачем пришли!', '', 38134),
(195588, 'ruRU', '', 'Эй! Мне нужна твоя помощь!', 38134),
(195587, 'ruRU', 'И что же ты раньше не сказала? Пф! Ладно. Я разберусь.', '', 38134),
(195586, 'ruRU', '', 'А, так вы хотели попасть внутрь хранилища? Боюсь, это уже совсем другой протокол.', 38134),
(195585, 'ruRU', 'Что? Еще одна преграда?', '', 38134),
(195584, 'ruRU', '', 'Сосредоточься. Мы здесь не просто так.', 38134),
(195583, 'ruRU', 'Признаюсь... Я хотел попасть сюда, просто чтобы доказать, что смогу. И смог!', '', 38134),
(195580, 'ruRU', '', 'Разместите подношения перед стражами, и путь откроется.', 38134),
(195579, 'ruRU', '', 'Я буду ждать у входа в хранилище Архонта. Оно находится к югу от вас.', 38134),
(195577, 'ruRU', 'Вон там! Защищайте меня!', '', 38134),
(195576, 'ruRU', 'Ну, да. \"Мнемида\" так и должна себя вести.', '', 38134),
(195575, 'ruRU', '', 'Какая она обходительная!', 38134),
(195574, 'ruRU', '', 'А теперь снова двигайтесь на юг.', 38134),
(195572, 'ruRU', '', 'Принеси три иссохших болиголова. \n\nЯ научу тебя, как сделать из них сильный яд.\n\nТолько смотри, нет ли рядом медведя, когда будешь их собирать. Почему-то они при этом шумят.', 38134),
(195571, 'ruRU', '', 'Ты, кажется, $gразбойник:разбойница;. Недалеко отсюда живет могучий огр, он взял в плен кое-кого из первой экспедиции.\n\nНаверняка и твоих друзей тоже хочет поймать! Он силен – ты не поверишь, до чего силен. Но его можно ослабить ядом.\n\nЯ как раз его готовила, когда меня настигла смерть. Добудешь припасы – научу тебя, как сделать яд для оружия.\n\nИдет?', 38134),
(195570, 'ruRU', 'Да, это точно.', '', 38134),
(195569, 'ruRU', 'Я не знаю, можно ли избежать судьбы Ночной Воительницы. Но я сделаю все, чтобы помочь вам.', '', 38134),
(195567, 'ruRU', 'Кадарин! Хвала Элуне! Сработало...', '', 38134),
(195565, 'ruRU', 'Я... Что... случилось?', '', 38134),
(195563, 'ruRU', '', 'Итак, внимание! Снимаем проклятие!', 38134),
(195562, 'ruRU', '', 'Старушка Герти... Надеюсь, с тобой и другими шелкобродами все в порядке. Роза, Дейзи... Колючая Лилия - младшая...', 38134),
(195560, 'ruRU', '', 'Мне... надо об этом поразмыслить. Встретимся в цитадели.', 38134),
(195558, 'ruRU', 'Когда же закончится эта засуха?', 'Когда же закончится эта засуха?', 38134),
(195557, 'ruRU', 'Мы больше других знаем о муках. Мы все вынесем.', 'Мы больше других знаем о муках. Мы все вынесем.', 38134),
(195556, 'ruRU', 'Сир Денатрий восстановит порядок.', 'Сир Денатрий восстановит порядок.', 38134),
(195555, 'ruRU', 'Каждый вносит свой вклад.', 'Каждый вносит свой вклад.', 38134),
(195552, 'ruRU', '', 'Например: идите на юг.', 38134),
(195551, 'ruRU', '', 'Мне запрещено указывать точное местонахождение подношений. Только примерное направление.', 38134),
(195548, 'ruRU', 'Ну? Теперь ты скажешь нам, где подношения?', '', 38134),
(195546, 'ruRU', 'Владыка говорит, что все должны чем-то жертвовать.', 'Владыка говорит, что все должны чем-то жертвовать.', 38134),
(195545, 'ruRU', 'Отличный день для гонки.', '', 38134),
(195542, 'ruRU', '', 'А! Верховный автоматург. Рада вас видеть.', 38134),
(195541, 'ruRU', '', 'Отключаю сознание. Ожидайте.', 38134),
(195540, 'ruRU', '', 'В моей системе возникла ошибка.', 38134),
(195539, 'ruRU', '', 'Пожалуйста, покиньте Локус, или мне придется применить силу.', 38134),
(195537, 'ruRU', 'Отвлекайте \"Мнемиду\"! Я починю ее!', '', 38134),
(195536, 'ruRU', '', 'При дальнейшем вмешательстве я буду вынуждена извлечь вас из Локуса.', 38134),
(195535, 'ruRU', '', 'Хм... Кажется, вам не разрешено просматривать это воспоминание.', 38134),
(195534, 'ruRU', '', 'Лисония, я должна сказать тебе, что...', 38134),
(195532, 'ruRU', '', 'Утер, возглавь раскольников и обрушь нашу месть на Бастион.', 38134),
(195527, 'ruRU', '', 'Мы захватим храм Чистоты и уничтожим любого, кто встанет у нас на пути.', 38134),
(195525, 'ruRU', '', 'Слишком долго нас лишали того, что делало нас теми, кто мы есть. Того, что дает нам силу!', 38134),
(195524, 'ruRU', '', 'Слушайте, раскольники!', 38134),
(195522, 'ruRU', '', 'Присоединяйся ко мне. Вместе мы откроем кириям новый путь. Вместе мы исправим вред, причиненный Архонтом.', 38134),
(195521, 'ruRU', '', 'Что же мне теперь делать?', 38134),
(195520, 'ruRU', '', 'Я пыталась предупредить всех. Слепое следование пути любой ценой... оно погубит нас.', 38134),
(195517, 'ruRU', '', 'Достаточно недавно, чтобы помнить боль, но достаточно давно, чтобы ее не чувствовать. Это тоже пройдет.', 38134),
(195516, 'ruRU', '', 'Я чувствую, как с каждой секундой теряю частицу себя. Как давно ты прошла через это?', 38134),
(195515, 'ruRU', '', 'Доверься процессу, претендентка. Со временем ты поймешь.', 38134),
(195513, 'ruRU', '', 'Иди вперед, $n. Я буду защищать верховного автоматурга.', 38134),
(195512, 'ruRU', 'Я ее исправлю, даже если это будет последнее, что я сделаю!', '', 38134),
(195510, 'ruRU', '', 'Тьма затуманила даже разум Арбитра, и та погрузилась в бесконечный сон.', 38134),
(195509, 'ruRU', 'Думаю, не взорвется...', '', 38134),
(195508, 'ruRU', '', 'Давай ты, $n. Тебе доводилось иметь дело с Лисонией.', 38134),
(195506, 'ruRU', 'Фрагмент готов! Отнесите его к зеркалам души.', '', 38134),
(195504, 'ruRU', 'Собрать их будет несложно. Остальные могут отправиться в архивы.', '', 38134),
(195503, 'ruRU', '', 'Воспоминания Лисонии распределились среди раскольников.', 38134),
(195501, 'ruRU', 'Прекрасно, Локус не взорвался.', '', 38134),
(195500, 'ruRU', '', 'Я позабочусь об этом маленьком шелкоброде. От этого зависит будущее Арденвельда. Будешь нас навещать время от времени?', 38134),
(195499, 'ruRU', '', 'Наверняка этот портал – не дорога к спасению, а очередной путь вглубь этой проклятой башни. Но я попытаюсь. Что мне остается?', 38134),
(195498, 'ruRU', '', 'Образ союзника, который якобы пришел вызволить меня... жестокая пытка.', 38134),
(195497, 'ruRU', '', 'Что это? Снова твои уловки?!', 38134),
(195493, 'ruRU', '', 'Я помогу тебе, чем смогу. Некротическую энергию на этом острове надо очистить.', 38134),
(195490, 'ruRU', 'О! Локус снова работает.', '', 38134),
(195489, 'ruRU', 'За ними.', '', 38134),
(195488, 'ruRU', 'Любопытно...', '', 38134),
(195487, 'ruRU', '', 'Фрагмент сущности одной из раскольниц. Возможно, он подскажет нам, с чего все началось.', 38134),
(195486, 'ruRU', 'А что это?', '', 38134),
(195485, 'ruRU', '', 'Миканикос, а отсюда Локус сможет извлечь воспоминания?', 38134),
(195482, 'ruRU', 'Проводники исправны. Заправь их анимой, которую я тебе дал.', '', 38134),
(195480, 'ruRU', '', 'Будем драться, пока не покажешь новые умения!', 38134),
(195478, 'ruRU', '', 'Что? Они увезли с собой последние остатки анимы.', 38134),
(195475, 'ruRU', '', 'Вот они. Но куда... Они все отправляются в Утробу! Бен!', 38134),
(195473, 'ruRU', '', 'Постой... а где души? Они должны лететь из Орибоса в свои новые дома...', 38134),
(195471, 'ruRU', '', 'Это была... \"Мнемида\"?', 38134),
(195470, 'ruRU', 'Не хочу об этом говорить... но да.', '', 38134),
(195469, 'ruRU', '', 'Теперь Бен предстанет перед Арбитром, и та отправит его душу в подобающий ей мир.', 38134),
(195467, 'ruRU', '', 'О нет... кажется, мы опоздали.', 38134),
(195465, 'ruRU', 'Я тебя создал! Я верховный автоматург!', '', 38134),
(195464, 'ruRU', '', 'Доступ к локусу временно ограничен.', 38134),
(195462, 'ruRU', 'Терпение преодолеет любое препятствие.', '', 38134),
(195461, 'ruRU', 'До новых встреч, $p.', '', 38134),
(195453, 'ruRU', 'Со временем я понял, что это не та безмозглая нежить, к которой я привык, что в Малдраксусе есть место и для чести... Или когда-то было...', '', 38134),
(195450, 'ruRU', '<Миканикос полностью поглощен изучением находящихся перед ним рун.>', '', 38134),
(195449, 'ruRU', 'Даже в тени меня настигла смерть...', '', 38134),
(195448, 'ruRU', 'Ох уж эти смертные, вечно лезут не в свои дела!', '', 38134),
(195442, 'ruRU', 'Восстаньте, вентиры! Мы отберем у тирана аниму, которая принадлежит нам по праву!', '', 38134),
(195441, 'ruRU', 'Маркграф! Я с тобой!', 'Маркграф! Я с тобой!', 38134),
(195440, 'ruRU', 'Но звон Колокола покаяния должен был стать для тебя последним предупреждением.', 'Но звон Колокола покаяния должен был стать для тебя последним предупреждением.', 38134),
(195439, 'ruRU', 'Можешь спросить пилигрима Утробы, какие мучения ждут тебя там, если ты не справишься.', 'Можешь спросить пилигрима Утробы, какие мучения ждут тебя там, если ты не справишься.', 38134),
(195438, 'ruRU', 'Мы поможем тебе всеми силами, но еще одна ошибка, и тебя навеки сошлют в Утробу.', 'Мы поможем тебе всеми силами, но еще одна ошибка, и тебя навеки сошлют в Утробу.', 38134),
(195436, 'ruRU', 'За это вы должны винить только себя. Свои ошибки. Свои грехи.', 'За это вы должны винить только себя. Свои ошибки. Свои грехи.', 38134),
(195435, 'ruRU', 'Надеюсь, что пример Садики воодушевляет вас. Этот путь ни для кого не бывает легким.', 'Надеюсь, что пример Садики воодушевляет вас. Этот путь ни для кого не бывает легким.', 38134),
(195434, 'ruRU', 'Спасибо за исповедь.', 'Спасибо за исповедь.', 38134),
(195433, 'ruRU', 'Возвращайся на место, Садика.', 'Возвращайся на место, Садика.', 38134),
(195432, 'ruRU', 'Вентиры помогут тебе найти и преодолеть все грехи, камнем лежащие на душе.', 'Вентиры помогут тебе найти и преодолеть все грехи, камнем лежащие на душе.', 38134),
(195430, 'ruRU', 'Мы здесь, чтобы поддержать тебя, Садика.', 'Мы здесь, чтобы поддержать тебя, Садика.', 38134),
(195429, 'ruRU', 'Мне больно... вспоминать...', 'Мне больно... вспоминать...', 38134),
(195426, 'ruRU', 'Чтобы очиститься от грехов, сначала нужно признать их.', 'Чтобы очиститься от грехов, сначала нужно признать их.', 38134),
(195422, 'ruRU', 'Я рад выслушать твои признания, Садика.', 'Я рада выслушать твои признания, Садика.', 38134),
(195421, 'ruRU', '<Не обращая внимания на мертвеца, вы изучаете главный трофей – его гримуар! \n\nВ нем описан ритуал, который заставит могущественного демона Бездны служить вам. Очевидно, этому чернокнижнику он не удался.\n\nНо у вас может получиться то, что не удалось ему. Возьмите лежащий рядом с трупом гримуар и завершите ритуал, чтобы подчинить себе демона Бездны!>', '', 38134),
(195418, 'ruRU', '', 'Предназначение привело тебя к нам. Я знаю.', 38134),
(195415, 'ruRU', 'Ему не сбежать.', '', 38134),
(195412, 'ruRU', 'И все же ты прячешься в некрополе! Я заткну тебе глотку одним ударом!', '', 38134),
(195411, 'ruRU', '', 'Несетесь к смерти, не разбирая дороги! Я разорву вас на части!', 38134),
(195409, 'ruRU', 'Антолита. Она ни разу не побеждала меня на арене. Не победит и здесь.', '', 38134),
(195408, 'ruRU', '', 'Защитник, для меня было честью сражаться рядом с тобой.', 38134),
(195406, 'ruRU', '', 'Гармалу не уйти от расплаты.\n\nТы с нами?', 38134),
(195398, 'ruRU', 'Смертная душа в Орибосе? Предназначение привело к нам смертную душу?', 'Смертная душа в Орибосе? Предназначение привело к нам смертную душу?', 38134),
(195382, 'ruRU', '', 'По твоему сигналу мы бросимся к ближней пещере. Пора?', 38134),
(195381, 'ruRU', 'У нас еще много важных дел, пилигрим.', '', 38134),
(195370, 'ruRU', 'Вот и зверь. Приручи его, как и предыдущего.', '', 38134),
(195369, 'ruRU', 'Вот и зверь. Приручи его, как и предыдущего.', '', 38134),
(195368, 'ruRU', 'Еще один зверь укрощен. Теперь научись ставить его в стойло.', '', 38134),
(195367, 'ruRU', 'У тебя слишком много питомцев. Одного надо отправить в стойла.', '', 38134),
(195366, 'ruRU', 'Дай мне знать, когда захочешь приручить зверя.', '', 38134),
(195365, 'ruRU', 'Чтобы приручить нового питомца, отпусти нынешнего. Потом призовешь обратно.', '', 38134),
(195364, 'ruRU', 'Замечательно! А теперь научись ставить питомцев в стойла.', '', 38134),
(195363, 'ruRU', 'У тебя слишком много питомцев. Поговори со мной, чтобы отправить одного в стойла.', '', 38134),
(195362, 'ruRU', 'Скажи, когда захочешь приручить зверя.', '', 38134),
(195358, 'ruRU', 'По крайней мере, мы падем с оружием в руках.', '', 38134),
(195351, 'ruRU', 'Наши силы собрались у Разрушенного моста. Встретимся там.', '', 38134),
(195340, 'ruRU', 'Будем надеяться. Наши похитители не обрадуются, что снова упустили свою добычу.', '', 38134),
(195331, 'ruRU', 'Матриарх гормов! Она со своим выводком выедает все вокруг сети. Убей ее и не забудь уничтожить яйца!', '', 38134),
(195328, 'ruRU', 'Кто же ты?', 'Кто же ты?', 38134),
(195327, 'ruRU', 'Эшелон! Приведи новую душу для жатвы...', '', 38134),
(195326, 'ruRU', 'Выследите все души, скрывающиеся в этих чертогах!', '', 38134),
(195325, 'ruRU', 'Мы осуждаем ложь Обвинительницы и ее сообщников!', '', 38134),
(195324, 'ruRU', 'Эти души спасти невозможно. Заберите у них аниму!', '', 38134),
(195303, 'ruRU', '', 'Я почти потеряла надежду вновь увидеть кого-то из нашего мира.', 38134),
(195301, 'ruRU', '', 'Признаюсь, сперва я не поняла, настоящие вы... или очередная иллюзия, которыми нас здесь терзают.', 38134),
(195299, 'ruRU', '', 'Здесь мы вроде бы в безопасности... пока что.', 38134),
(195297, 'ruRU', '', 'Сюда!', 38134),
(195296, 'ruRU', 'А тебя ждет погибель!', '', 38134),
(195295, 'ruRU', 'Союзники тебе ничем не помогут. Вас всех ждут оковы!', '', 38134),
(195294, 'ruRU', 'Клетка ждет, чародейка! У Тюремщика на тебя планы!', '', 38134),
(195293, 'ruRU', '', 'Мне надоели твои клетки, монстр!', 38134),
(195292, 'ruRU', 'Вы от нас не скроетесь, смертные!', '', 38134),
(195289, 'ruRU', 'Лорд-адмирал, не видели ли вы здесь Ночную Воительницу?', '', 38134),
(195287, 'ruRU', 'Снова засада! Перебьем их!', '', 38134),
(195285, 'ruRU', 'Что значит \"снова упустили свою добычу\"? Вы уже от них сбегали?', '', 38134),
(195284, 'ruRU', '', 'Со скрытностью ничего не вышло. Поспешим!', 38134),
(195282, 'ruRU', 'Займите пещеру, рыцари! Мы сдержим этих тварей!', '', 38134),
(195279, 'ruRU', '', 'Сразу за поворотом есть пещера. Если повезет, они не успеют нас заметить.', 38134),
(195278, 'ruRU', '', 'Но долго они не продержатся. Идем!', 38134),
(195277, 'ruRU', '', 'Мои зеркальные отражения отвлекут их.', 38134),
(195276, 'ruRU', '', 'Они нашли нас! Приготовьтесь!', 38134),
(195275, 'ruRU', 'Что-то приближается к нам с неба!', '', 38134),
(195274, 'ruRU', 'Спасибо, что пришла на помощь, Джайна.', '', 38134),
(195273, 'ruRU', 'Я... постараюсь.', '', 38134),
(195272, 'ruRU', '', 'Надо уходить. Тралл, ты сможешь идти?', 38134),
(195270, 'ruRU', 'Будьте внимательны: ищите следы, которые приведут к нашим союзникам.', '', 38134),
(195269, 'ruRU', 'Нас слишком мало. Лучше не привлекать к себе лишнего внимания.', '', 38134),
(195266, 'ruRU', 'Пора уходить... Пока не прибыло подкрепление.', '', 38134),
(195265, 'ruRU', 'Мы сделали все, что могли.', '', 38134),
(195264, 'ruRU', 'Соберемся впереди. Скорее!', '', 38134),
(195263, 'ruRU', 'Выхода... нет...', '', 38134),
(195262, 'ruRU', 'Умрите в оковах!', '', 38134),
(195259, 'ruRU', '', 'Встретимся у Ренатала.', 38134),
(195256, 'ruRU', 'Мы занимаем передовую позицию. Помоги нам ее удержать.', '', 38134),
(195255, 'ruRU', 'Найди других рыцарей. Мы пойдем следом.', '', 38134),
(195254, 'ruRU', 'Расчисти себе путь. Встретимся впереди.', '', 38134),
(195253, 'ruRU', '', 'Позорище. В Малдраксусе ее бы разве что в рядовые взяли!', 38134),
(195252, 'ruRU', '', 'Настал час расплаты!', 38134),
(195251, 'ruRU', 'К нам присоединилось несколько новых душ, пилигрим Утробы.', '', 38134),
(195222, 'ruRU', '', 'Раскольники рядом. Будь начеку.', 38134),
(195220, 'ruRU', 'Стойте! Где-то рядом оберег.', '', 38134),
(195217, 'ruRU', '', 'Вы почти рядом с целью.', 38134),
(195205, 'ruRU', '', 'Указывай мне цели, и я обрушу на них свой гнев.', 38134),
(195204, 'ruRU', 'Будет весело. Как в старые времена. Так ведь, $p?', '', 38134),
(195203, 'ruRU', 'Сила меня не интересует. Мне нужен твой талант стратега.', '', 38134),
(195202, 'ruRU', '', 'Казначей – серьезный противник. Чтобы свергнуть его, нужна огромная сила.', 38134),
(195201, 'ruRU', 'Я стараюсь не судить тех, кто служит Владыке.\n\nМеня сослали в Ревендрет за то, что я тоже отказывался видеть правду у себя под носом.\n\nНикогда не поздно сделать правильный выбор.', '', 38134),
(195196, 'ruRU', 'Тьма окружает меня!', '', 38134),
(195195, 'ruRU', 'Архивариус Фейн объяснит остальное.', '', 38134),
(195194, 'ruRU', 'Я восстановлю Темеля, как только ты принесешь все его части.', '', 38134),
(195193, 'ruRU', '', 'Я готова! А ты?', 38134),
(195173, 'ruRU', '', 'Привет, $n!\n\nТы нужен в Ледяной Короне. Без твоей помощи Азерот обречен!', 38134),
(195172, 'ruRU', '', 'Только ни в коем случае не вставай между пандареном и его хмелем. Не то тебе так достанется!', 38134),
(195166, 'ruRU', 'Мы используем один из проводников душ как врата в Бастион. Этим путем давно уже не пользовались. Твое появление может их удивить.', '', 38134),
(195165, 'ruRU', 'Успокой шелкобродов, играя на арфе. Тогда они поделятся с тобой шелком. Они не послушаются, если будут напуганы!', '', 38134),
(195164, 'ruRU', 'Мы бережем аниму как только можем. Но раз тебя поддержала Высокочтимый Голос, придется ее немного потратить.', '', 38134),
(195163, 'ruRU', 'Иди за мной. Я покажу тебе путь.', '', 38134),
(195162, 'ruRU', '', 'Выполняй приказ, Ка-Шер. Такова воля Арбитра.', 38134),
(195161, 'ruRU', 'Но Высокочтимый Голос, наши запасы анимы совсем истощились! Нам лучше...', '', 38134),
(195160, 'ruRU', '', 'Согласна. Смотритель Ка-Шер, отведи смертную душу в Кольцо Перенаправления. Потрать немного анимы и открой путь в Бастион.', 38134),
(195159, 'ruRU', '', 'Да, но это единственная зацепка для пришельца из мира смертных. Мы должны открыть для него путь.', 38134),
(195158, 'ruRU', '', 'Крылатые кирии Бастиона приносят души смертных в Темные Земли, но не в цепях. И они чисты, не тронуты Утробой.', 38134),
(195156, 'ruRU', 'Сильвана Ветрокрылая приказала похитить их. Темные крылатые существа сковали их цепями и унесли в разлом.', '', 38134),
(195155, 'ruRU', '', 'Как твои союзники попали из вашего мира в Утробу?', 38134),
(195154, 'ruRU', '', 'Если влияние Утробы распространится на ваш мир, равновесие жизни и смерти будет нарушено. Все, что вам дорого, поглотит тьма.', 38134),
(195153, 'ruRU', '...и когда она разорвала шлем, в небе над цитаделью открылся разлом. Шпиль огромной башни проник сквозь завесу.', '', 38134),
(195151, 'ruRU', '', 'Если страшный орк предложит что-то выпить, лучше откажись.', 38134),
(195150, 'ruRU', 'Кажется, я начинаю понимать.\n\nНо нас так мало... Как мы можем им противостоять?\n\n<Гресит делает глубокий вдох и вздергивает подбородок.>\n\nМы сделаем то, что должны. Мы восстановим Ревендрет.', '', 38134),
(195148, 'ruRU', 'Я думал, что смерть – конец всего. А тут оказалось, что смерть – это сила, дающая новое начало.', '', 38134),
(195147, 'ruRU', 'При жизни я открыл множество порталов. Но ни один из них не соединял две разных реальности.', '', 38134),
(195141, 'ruRU', 'Как пожелаешь. $n, укажи нашим послушникам, где создать порталы.', '', 38134),
(195140, 'ruRU', '', 'Ка-Шер, прояви гостеприимство. Я предлагаю разместить порталы на месте твоего прибытия... на краю города.', 38134),
(195139, 'ruRU', 'Ты хочешь открыть путь обратно в мир живых? Нет, только не это. В Орибос и так уже течет нескончаемый поток брокеров.', '', 38134),
(195134, 'ruRU', '', 'Возвращайся на нижние уровни. Смотрители расскажут тебе все, что нужно.', 38134),
(195133, 'ruRU', '', 'Говорят, его переполняет ненависть к Арбитру. А ее гибель будет означать конец всему, что создали Предвечные.', 38134),
(195132, 'ruRU', '', 'Я боюсь, что Тюремщик, пожрав тысячи душ, наберет силу и вырвется из Утробы.', 38134),
(195131, 'ruRU', '', 'Без анимы Темные Земли погибают от засухи. Миры отдалились друг от друга. Страх и неизвестность одолевают нас.', 38134),
(195130, 'ruRU', '', 'Души, полные анимы, попадают сразу в Утробу. Мы как могли старались прекратить это, пробудить Арбитра... но бесполезно.', 38134),
(195129, 'ruRU', '', 'Наше былое величие не описать словами. Подойди, и я покажу тебе.', 38134),
(195128, 'ruRU', '', 'Твое прибытие заставило Арбитра шевельнуться впервые за много циклов. Теперь у меня есть надежда, что она очнется и вновь станет служить Предназначению.', 38134),
(195127, 'ruRU', '', 'Она всегда одинакова. Безмолвна. Неподвижна.', 38134),
(195126, 'ruRU', '', 'Я молюсь, чтобы твое присутствие пробудило Арбитра.', 38134),
(195125, 'ruRU', '', 'Ее слово – закон.', 38134),
(195124, 'ruRU', 'Ее слово – закон.', '', 38134),
(195123, 'ruRU', '', 'Ка-Шер, не забывай – души судит Арбитр, а не мы. Я отведу эту смертную душу к ней. Такова воля Арбитра.', 38134),
(195122, 'ruRU', 'Разве смертные достойны встречи с Арбитром? Что, если это кто-то из слуг Тюремщика?', '', 38134),
(195121, 'ruRU', '', 'Живая смертная душа! Несомненно, это послужит Предназначению. Арбитр почувствовала ее прибытие – быть может, она даже пробудится.', 38134),
(195120, 'ruRU', '', 'Прекрасные вести! Только что на границе Утробы произошло нечто необычное. А потом появилась эта смертная душа и рассказала, что вырвалась оттуда.', 38134),
(195116, 'ruRU', 'Смотрители! Мы нашли на окраине чужака. Кажется, это... живая смертная душа.', '', 38134),
(195115, 'ruRU', 'Это дурной знак. Но что он предвещает?', '', 38134),
(195113, 'ruRU', '', 'Все провидцы говорили об одном. В один миг Утроба содрогнулась, будто отзываясь на перемены.', 38134),
(195111, 'ruRU', 'Говори что хочешь про Отрекшихся, но мы хотя бы не оживляем драконов.\n\nНу, то есть как... большинство из нас таким не занимается.', '', 38134),
(195108, 'ruRU', '', 'Ты пойдешь с нами.', 38134),
(195107, 'ruRU', 'Нужно немедленно доложить смотрителям!', '', 38134),
(195106, 'ruRU', '', 'Стойте. Утроба не имеет власти над этой душой, но я чувствую... это невозможно!', 38134),
(195105, 'ruRU', 'Прислужники Утробы атакуют Орибос! Защищайте Арбитра!', '', 38134),
(195102, 'ruRU', 'Только скажи, и я сразу начну.', '', 38134),
(195101, 'ruRU', 'Привет, лучший друг!\n\nМоя очередь нести вкусняшки!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(195057, 'ruRU', '', 'Кроме Твигина у меня больше никого нет. Постарайся спасти его.', 38134),
(195056, 'ruRU', 'Нет, ну какая ирония! Гарпии только что решили использовать меня как приманку, чтобы наловить больше ордынцев.\n\nИ ты сразу тут как тут. Кстати, они нападут в любой момент.', '', 38134),
(195048, 'ruRU', '', 'Какое жалкое, слабое тело.', 38134),
(195047, 'ruRU', '', 'Готовься к поражению!', 38134),
(195046, 'ruRU', '', 'Защищайся!', 38134),
(195040, 'ruRU', '', 'Наши жертвы не будут напрасными!', 38134),
(195039, 'ruRU', '', 'Но теперь пришло время побороться за то, что нам дорого.', 38134),
(195038, 'ruRU', 'Ночной народец в смятении.', 'Ночной народец в смятении.', 38134),
(195037, 'ruRU', '', 'Ара\'лон служил верно и умел принять нелегкий выбор. Пойти на жертву.', 38134),
(195035, 'ruRU', 'Что нам предпринять?', '', 38134),
(195033, 'ruRU', 'Теперь враг движется к роще Пробуждения, собирая армию из твоих подданных.', '', 38134),
(195031, 'ruRU', '', 'Королева, друсты поработили храброе сердце Ара\'лона, не оставив нам выбора. Его... больше нет.', 38134),
(195029, 'ruRU', '', 'Эта проклятая армия найдет только вечный сон!', 38134),
(195028, 'ruRU', 'Ха! Падают, как листья!', '', 38134),
(195025, 'ruRU', '', '<Глаза Королевы Зимы сияют от сдерживаемой силы.>\n\nРасскажи мне все, что знаешь.', 38134),
(195024, 'ruRU', '', '<Глаза королевы горят яростью.>\n\nЭтих тварей надо истребить.', 38134),
(195023, 'ruRU', '', 'Это лишь начало, Бен. Мне уже не терпится узнать, в какой мир тебя отправят. За храбрость и самоотверженность тебя вознаградят!', 38134),
(195022, 'ruRU', '', 'Как только прозвучит рог, перерожденные отнесут вас в Орибос. Там Бену вынесут приговор.', 38134),
(195021, 'ruRU', '', 'Он не заслужил такой жуткой смерти... Его храбрость безгранична. Обещаю, ты обретешь покой, Бен.', 38134),
(195020, 'ruRU', '', 'Плеть? Нет... они же все здесь умрут!', 38134),
(195011, 'ruRU', 'Почему звонят колокола?', 'Почему звонят колокола?', 38134),
(195010, 'ruRU', 'Я хочу к маме.', 'Я хочу к маме.', 38134),
(195009, 'ruRU', 'Что происходит?', 'Что происходит?', 38134),
(195008, 'ruRU', 'Мне страшно!', 'Мне страшно!', 38134),
(195007, 'ruRU', '', 'Кажется, его любит и семья, и все жители города. Самая обычная жизнь... почему же он попал к нам?', 38134),
(194998, 'ruRU', 'Моя семья... я защищу вас.', '', 38134),
(194996, 'ruRU', '', 'Эй, $n! Скажи, куда тебе нужно, и я помогу тебе туда добраться!\n\nЗаконы времени таинственны, но, кажется, тебе к загадкам не привыкать.', 38134),
(194995, 'ruRU', 'В нем твоя кровь, Дрека. Я даже не сомневаюсь, что он выжил.', '', 38134),
(194994, 'ruRU', '', 'Гордись им. У меня тоже есть сын. Он был совсем дитя, когда меня убили. Я дралась до последнего, пыталась спасти его... но я не знаю, что с ним стало.', 38134),
(194993, 'ruRU', 'Недавно я видел его в Утробе. Он тоже стал рыцарем смерти. И он тоже сражается за наш мир.', '', 38134),
(194992, 'ruRU', 'Двое сыновей. Один предал меня ради Плети. Другой спас меня ценой своей жизни.', '', 38134),
(194991, 'ruRU', '', 'У тебя есть дети, Александрос?', 38134),
(194989, 'ruRU', 'Пусть победа будет за тобой!', '', 38134),
(194987, 'ruRU', 'Понятия не имею, что случится, если попробовать извлечь воспоминания из такого фрагмента.\n\nГлавное, запомни: нам нужны воспоминания раскольников. Ищи все фиолетовое и с крыльями. И центурионов тоже...\n\nЯ точно не знаю, что именно нам нужно, но если воспоминания там, то экстрактор их вытащит!\n\nСкажи мне, когда его запустить.', '', 38134),
(194986, 'ruRU', 'Поглядите, что они сделали с моими центурионами! С Локусом!\n\n<Миканикос в гневе указывает на... все подряд.>', '', 38134),
(194985, 'ruRU', '', 'Тогда, возможно, ты еще продолжишь дело своей жизни.', 38134),
(194984, 'ruRU', 'Маркграф Крексус показал мне, что служить Малдраксусу почетно. Но действия наших врагов напоминают мне о злодеяниях Плети.', '', 38134),
(194983, 'ruRU', 'Борьба с нежитью была делом всей моей жизни. Когда я оказался окружен ею после смерти... Это был кошмар.', '', 38134),
(194982, 'ruRU', '', 'Когда-то ты воевал против нежити, Могрейн. Наверное, Малдраксус тебе... неприятен.', 38134),
(194979, 'ruRU', 'Здравствуй, пилигрим.', 'Здравствуй, пилигрим.', 38134),
(194978, 'ruRU', 'Привет, $p.', 'Привет, $p.', 38134),
(194977, 'ruRU', '', 'Не сдерживай себя. Пусть Плеть познает твою истинную мощь!', 38134),
(194975, 'ruRU', '', 'Идеал! Сюда!', 38134),
(194974, 'ruRU', 'Сражайтесь до последнего! Мы не уйдем безропотно во тьму!', '', 38134),
(194973, 'ruRU', '', 'В бой, братья и сестры! Маркграф за все заплатит!', 38134),
(194972, 'ruRU', '', 'Исполины маркграфа атакуют позицию Могрейна внизу и не дают ему пройти!', 38134),
(194961, 'ruRU', 'Нет, не хватит! Никогда!', '', 38134),
(194957, 'ruRU', 'Думаю, я справлюсь!', 'Думаю, я справлюсь!', 38134),
(194956, 'ruRU', 'Ксандрия полетела к храму. Возможно, ей нужна помощь.', 'Ксандрия полетела к храму. Возможно, ей нужна помощь.', 38134),
(194955, 'ruRU', 'Хвала Архонту, ты вовремя.', 'Хвала Архонту! Еще немного, и меня было бы не спасти.', 38134),
(194954, 'ruRU', 'Сюда! Скорее!', 'Сюда! Скорее!', 38134),
(194953, 'ruRU', 'Спаси... остальных...', 'Спаси... остальных...', 38134),
(194942, 'ruRU', 'У нас достаточно тел, Трупошов. Готовь наступление на следующий храм.', '', 38134),
(194941, 'ruRU', 'Одну минуточку. Кажется, прибыли запчасти для моего нового творения...', '', 38134),
(194940, 'ruRU', '', 'Я возглавлю войска здесь, а мои послушники отведут вас в некрополь. Покончим с этими чудовищами.', 38134),
(194939, 'ruRU', '', 'Вы как раз вовремя, бой в самом разгаре. Мы не можем останавливаться, пока не очистим небо от захватчиков.', 38134),
(194938, 'ruRU', 'Наивная. Тебе нас не остановить, ты бессильна!', '', 38134),
(194937, 'ruRU', '', 'Ты ответишь за все!', 38134),
(194936, 'ruRU', '', 'Чудовище!', 38134),
(194935, 'ruRU', 'Твой храм разрушен. Твои собратья перебиты. Ты уже проиграла!', '', 38134),
(194921, 'ruRU', 'Хочешь разделить судьбу своего слуги? Твоя хваленая отвага тебя и погубит!', '', 38134),
(194916, 'ruRU', 'Трупошов не обрадуется, что вы уничтожили его шедевр. Хотя компенсация в виде ваших органов его утешит.', '', 38134),
(194915, 'ruRU', 'Это место может стать для нас не просто мастерской, а новым домом.', '', 38134),
(194913, 'ruRU', 'Иметь дело с какими-то свинобразами – это ниже нашего достоинства. Скоро они увидят всю мощь Орды.', '', 38134),
(194912, 'ruRU', 'УДАРИМ САМОГО ЗДОРОВОГО, ХА-ХА!', '', 38134),
(194910, 'ruRU', '', 'При жизни я была царственной особой. Можешь поклониться, это нормально.', 38134),
(194908, 'ruRU', '', 'Свет озарит... даже это место!', 38134),
(194907, 'ruRU', 'Все подчинится нам!', 'Все подчинится нам!', 38134),
(194906, 'ruRU', 'Эти кольца откроют нам свои тайны!', 'Эти кольца откроют нам свои тайны!', 38134),
(194905, 'ruRU', 'Давай найдем удачку!', '', 38134),
(194902, 'ruRU', 'Будь начеку, оказавшись в Утробе. Иначе можешь навсегда потерять свою душу.', '', 38134),
(194901, 'ruRU', 'Живым не место в Темных Землях.\n\nЗдесь нельзя верить своим глазам и ушам. Все время будь начеку.\n\nКаждую секунду.', '', 38134),
(194897, 'ruRU', 'Сделай Корди... э-э... быстрым, как журавль-прыгун!\n\nИ пришей ему шесть... семь... нет, две руки с сильными пальцами, чтобы тяжести таскать.', '', 38134),
(194895, 'ruRU', 'Корди не терпится снова получить тело!', '', 38134),
(194883, 'ruRU', 'Это Плеть. Мы должны укрыться в ратуше.', 'Это Плеть. Мы должны укрыться в ратуше.', 38134),
(194880, 'ruRU', 'Командуй, Бен.', 'Командуй, Бен.', 38134),
(194875, 'ruRU', 'Я слышал что-то про Плеть... она уже здесь?', 'Я слышала что-то про Плеть... она уже здесь?', 38134),
(194874, 'ruRU', 'Это конец света!', 'Это конец света!', 38134),
(194873, 'ruRU', 'Что мне делать? Куда прятаться?', 'Что мне делать? Куда прятаться?', 38134),
(194872, 'ruRU', 'Бен! Что происходит?', 'Бен! Что происходит?', 38134),
(194856, 'ruRU', 'В ратуше безопаснее всего... Я организую оборону.', '', 38134),
(194845, 'ruRU', 'Нужно спросить Обвинительницу... Она всегда знает, что делать...', '', 38134),
(194844, 'ruRU', '', 'Мое терпение иссякает!', 38134),
(194837, 'ruRU', '', 'Должна тебя поблагодарить, Зе\'рали. О лучшем подопытном и мечтать нельзя.', 38134),
(194836, 'ruRU', '', 'Значит, боль ты чувствуешь. Любопытно.', 38134),
(194835, 'ruRU', 'Незваные гости наконец явились в мои владения. Начнем же!', '', 38134),
(194833, 'ruRU', '', 'Хвала Свету, вы нас нашли!', 38134),
(194832, 'ruRU', 'Похоже, мы нашли лорд-адмирала. Я подам рыцарям сигнал к наступлению.', '', 38134),
(194831, 'ruRU', 'Там, впереди! Это же леди Праудмур!', '', 38134),
(194830, 'ruRU', 'Продолжай поиски.', '', 38134),
(194829, 'ruRU', 'Прости, Соломон, не могу. У меня теперь простая жизнь – дрова да огород.', '', 38134),
(194828, 'ruRU', 'Если кто-то из наших оказался внутри, мы вряд ли сумеем их вызволить.', '', 38134),
(194827, 'ruRU', 'Идем дальше.', '', 38134),
(194826, 'ruRU', 'Не знаю, что здесь происходит, но лучше нам не вмешиваться.', '', 38134),
(194825, 'ruRU', 'О, Бен! Мы как раз обсуждали, как реорганизовать городское ополчение. Не хочешь снова его возглавить?', '', 38134),
(194823, 'ruRU', 'Давай постараемся не забрести в эту пасть!', '', 38134),
(194822, 'ruRU', 'Надеюсь, что эта тварь не сожрала никого из наших.', '', 38134),
(194821, 'ruRU', 'Лучше не злить это чудище. Вряд ли наше оружие пробьет такую броню.', '', 38134),
(194814, 'ruRU', '', 'Вы, наару, завораживаете меня... столько нераскрытых секретов...', 38134),
(194799, 'ruRU', '', 'Лок\'тар! Теперь весь Малдраксус знает, что с тобой лучше не шутить.', 38134),
(194795, 'ruRU', '', 'Что это за чудовища?! Хвала Архонту, они пока бездействуют.', 38134),
(194794, 'ruRU', '', 'Перерожденные, в небо! За Бастион!', 38134),
(194773, 'ruRU', '', 'Мой план обязательно сработает, если ты достанешь мне все необходимое.', 38134),
(194772, 'ruRU', '', 'Матьяш больше никогда не обратит против меня мой камень грехов.', 38134),
(194769, 'ruRU', 'А ты заходи, Зайхед! Видит Королева, уж ты получше этих всех.', '', 38134),
(194768, 'ruRU', '', 'Землю должно возделывать. Расти, Чесночник.', 38134),
(194767, 'ruRU', 'Хм... Что ж, мне нравится здесь у тебя. И Двор, кажется, тоже трудится не покладая рук.', '', 38134),
(194766, 'ruRU', '', 'Пилигрим помогает нам всем. С тобой Сердце леса расцветет как никогда.', 38134),
(194765, 'ruRU', 'Ах вот что тебе надо? Пока только пилигрим относится ко мне как подобает.', '', 38134),
(194764, 'ruRU', '', 'Так может, теперь мы снова сможем пользоваться кольцами?', 38134),
(194763, 'ruRU', 'Зайхед, старая колода! При чем тут ветер? Я здесь благодаря нашему другу.', '', 38134),
(194762, 'ruRU', '', 'Чесночник, каким ветром тебя принесло?', 38134),
(194760, 'ruRU', '', 'Привет, малыш. Что это у тебя?', 38134),
(194759, 'ruRU', '', 'Росток становится стволом. Ствол подпирает небеса. Небеса полны ростков.', 38134),
(194758, 'ruRU', '', 'Всему свое время.', 38134),
(194757, 'ruRU', '', 'То, что ты видишь вокруг, создано за много жизней.$b$bКрасиво, не правда ли?', 38134),
(194749, 'ruRU', 'Ха! Ну как, нравится? Вон из моего леса!', '', 38134),
(194748, 'ruRU', 'Поучитесь уважению!', '', 38134),
(194747, 'ruRU', 'Так им и надо!', '', 38134),
(194746, 'ruRU', 'Я тоже тебя люблю.', '', 38134),
(194744, 'ruRU', 'Напоминает, как я по ошибке зашел в зеркало примерочной в доме...', '', 38134),
(194743, 'ruRU', 'Рад тебя видеть, Сибилла. Я должен отдать тебе зеркало.', 'Рад тебя видеть, Сибилла. Я должен отдать тебе зеркало.', 38134),
(194742, 'ruRU', 'Ошибаешься, мой дорогой Таббинс. Леди Сибилла всегда приходит тогда, когда планирует.', '', 38134),
(194741, 'ruRU', '', 'Раз и два, главное сперва.', 38134),
(194740, 'ruRU', 'Конечно! Сейчас приду.', '', 38134),
(194735, 'ruRU', '', 'Зачем тебе разум, безумие – радость.', 38134),
(194734, 'ruRU', 'А, снова верные слуги кирий. Вам тоже не терпится поскорее умереть?', '', 38134),
(194732, 'ruRU', 'Скажи, когда подготовишься, дитя. Новичкам от этого всегда... не по себе!', '', 38134),
(194731, 'ruRU', '', 'Не нападай, а защищай.', 38134),
(194730, 'ruRU', 'Ха! Так этим негодяям и надо! Из нас троих получилась неплохая команда.', '', 38134),
(194729, 'ruRU', 'С радостью, дорогая.', '', 38134),
(194728, 'ruRU', '', 'Когда закончишь, скорей возвращайся! Надо доставить дрова мировому судье.', 38134),
(194727, 'ruRU', '', 'Сегодня правосудие свершится. Мы отомстим за братьев и сестер и покончим с кознями врагов раз и навсегда!', 38134),
(194726, 'ruRU', '', 'Рвать, хватать, отрывать!', 38134),
(194725, 'ruRU', '', 'Нету-нету защиты от Света!', 38134),
(194711, 'ruRU', 'Без тени я истлею...', '', 38134),
(194709, 'ruRU', 'Не хочешь перегреться – умей вертеться.', '', 38134),
(194708, 'ruRU', 'Я потом принесу тебе досок. Бесплатно!', '', 38134),
(194707, 'ruRU', 'Это еще мягко сказано, Бен. Камень пробил днище насквозь!', '', 38134),
(194706, 'ruRU', 'Привет, Вернон! Опять лодка дала течь?', '', 38134),
(194677, 'ruRU', '', 'Приготовься, $n! Мы почти прибыли.', 38134),
(194676, 'ruRU', 'Подавай, пока горячий! Хуже остывшего чая ничего нет!', '', 38134),
(194672, 'ruRU', 'Это дело оказалось весьма прибыльным, $p. Надеюсь, в будущем мы снова поработаем вместе.', '', 38134),
(194671, 'ruRU', 'Нет, так не пойдет – они мне все нужны! Неизвестно, появится ли снова такая возможность.', '', 38134),
(194670, 'ruRU', 'Не могу выразить, как я благодарен тебе за помощь в поисках этих дополнительных томов.', '', 38134),
(194669, 'ruRU', 'Не важно. Нам нет конца. Я убью вас сам.', '', 38134),
(194668, 'ruRU', 'Чесночник! Он нам помогает!', '', 38134),
(194666, 'ruRU', 'Убейте их.', '', 38134),
(194665, 'ruRU', 'Как глупо. Привести нас прямо к цели.', '', 38134),
(194664, 'ruRU', '', 'Скоро ты все поймешь. Чтобы понять эту душу, нужно увидеть минуты покоя.', 38134),
(194663, 'ruRU', '', 'Невероятно... но разве что-то не так? Это точно его последние мгновения?', 38134),
(194661, 'ruRU', 'Так вы все упрямитесь? Прекрасно. Вашу сущность пожрут падальщики.', '', 38134),
(194657, 'ruRU', '', 'Уверена, вам с Изерой есть что обсудить.', 38134),
(194637, 'ruRU', '', 'Теперь ты понял, Александрос?', 38134),
(194636, 'ruRU', 'Еще одна война? Ну и дела...', '', 38134),
(194632, 'ruRU', 'О, аромат напоминает мне о долгих годах в Гибельном лесу...', 'О, аромат напоминает мне о долгих годах в Гибельном лесу...', 38134),
(194631, 'ruRU', 'Таббинс и Теотар снова превзошли самих себя! Такой тонкий и необычный букет...', 'Таббинс и Теотар снова превзошли самих себя! Такой тонкий и необычный букет...', 38134),
(194630, 'ruRU', 'Я ценю возможность просто насладиться моментом.', 'Я ценю возможность просто насладиться моментом.', 38134),
(194629, 'ruRU', 'Приговор – сожжение!', '', 38134),
(194625, 'ruRU', 'Мой... последний... приговор...', '', 38134),
(194624, 'ruRU', 'Предатели будут наказаны!', '', 38134),
(194618, 'ruRU', '', 'Портал открывается! Мы все войдем в одни и те же врата, но выйдем в разных мирах.', 38134),
(194617, 'ruRU', '', 'Приступим.', 38134),
(194616, 'ruRU', '', 'Мы живем не ради себя, но ради служения всем душам во вселенной, что ищут нашей помощи.', 38134),
(194615, 'ruRU', '', 'Твердо держась своего пути, мы учимся и совершенствуемся.', 38134),
(194614, 'ruRU', '', 'Мы не сдаемся, несмотря ни на что. Беды и преграды не пошатнут нашу стойкость.', 38134),
(194613, 'ruRU', 'Прекрасно. Мы близки к перерождению как никогда.', '', 38134),
(194612, 'ruRU', '', 'Я нашла ритуальный свиток. Эти слова должны открыть врата.', 38134),
(194611, 'ruRU', 'Преданность Архонту будет наказана!', '', 38134),
(194610, 'ruRU', 'Ты знаешь правду.', '', 38134),
(194609, 'ruRU', 'Докажи свою верность!', '', 38134),
(194608, 'ruRU', 'Оставь меня, $p. Мне... нужно подумать.', '', 38134),
(194607, 'ruRU', 'Теперь я понимаю. Быть может, честь и покинула Малдраксус... но вместе мы сможем ее вернуть!', '', 38134),
(194606, 'ruRU', '', 'Арбитр увидела, что воспоминания не тяготят тебя, а придают тебе сил. Вот почему твое место в Малдраксусе.', 38134),
(194605, 'ruRU', 'Думаю... теперь я все понял. Давай вернемся к идеалу.', '', 38134),
(194604, 'ruRU', 'Мой дорогой сын... я так тобой горжусь... И эта гордость всегда будет со мной.', '', 38134),
(194603, 'ruRU', 'Отец?..', '', 38134),
(194602, 'ruRU', 'Я не забуду твоей жертвы... Никогда!', '', 38134),
(194601, 'ruRU', 'Дарион... ты погиб, спасая меня от Плети... И продолжаешь защищать Азерот!', '', 38134),
(194600, 'ruRU', 'Идем... прошу, идем.', '', 38134),
(194599, 'ruRU', '', 'Эта кузня, может, и древняя, но оружие получается не хуже обычного. Спасибо тебе.', 38134),
(194598, 'ruRU', 'Его предательство и сейчас ранит меня. Не лучше ли забыть эту боль навсегда?', '', 38134),
(194597, 'ruRU', 'Рено. Мой сын... и моя погибель.', '', 38134),
(194596, 'ruRU', 'Прости, $p. Давай продолжим.', '', 38134),
(194595, 'ruRU', 'Ее сияющая красота ничуть не изменилась... А ведь я забыл бы ее, если бы...', '', 38134),
(194594, 'ruRU', 'Это... Елена? Любовь моя... Свет мой...', '', 38134),
(194593, 'ruRU', 'Отвагу мне всегда давал Свет. Но... посмотрим, что скажут свитки.', '', 38134),
(194592, 'ruRU', '', 'Изучи эти свитки, Александрос. Осознай источник своей отваги... И суть наших различий.', 38134),
(194591, 'ruRU', '', 'Ваш путь приведет всех к погибели! Я дарую нашему народу новую судьбу!', 38134),
(194590, 'ruRU', 'Это случилось не по нашей вине! Если мы не будем сходить с пути, то справимся с этой бедой!', '', 38134),
(194589, 'ruRU', '', 'И к чему это привело? Претенденты не перерождаются, центурионы ржавеют...', 38134),
(194588, 'ruRU', '', 'Мы стали большим, чем когда-то были! Как ты не понимаешь?', 38134),
(194587, 'ruRU', '', 'Все утерянные воспоминания, забытые жизни... Будут отомщены!', 38134),
(194586, 'ruRU', '', 'Позже мы обсудим твою участь. До тех пор ты останешься здесь под стражей.', 38134),
(194585, 'ruRU', '', 'Пока что я пощажу тебя.', 38134),
(194584, 'ruRU', 'Архонт, смилуйся надо мной. Я... не понимал...', '', 38134),
(194583, 'ruRU', '', 'Что до тебя, Утер...', 38134),
(194581, 'ruRU', 'Смотрите! Воин Лисонии напал на нее!', '', 38134),
(194580, 'ruRU', 'Ты лишилась рассудка, Лисония?', '', 38134),
(194579, 'ruRU', 'Правосудие свершится.', '', 38134),
(194578, 'ruRU', 'Союз с подобной тьмой предает все, во что верят кирии!', '', 38134),
(194577, 'ruRU', '', 'Расколотая душа Утера открыла для меня силу Утробы. И я покажу ее вам!', 38134),
(194576, 'ruRU', '', 'Вы все – ничто перед мощью Утробы!', 38134),
(194575, 'ruRU', '', 'Твое служение окончено.', 38134),
(194574, 'ruRU', '', 'Невозможно... я видела...', 38134),
(194573, 'ruRU', '', 'Сейчас!', 38134),
(194572, 'ruRU', '', 'Ты так сильна, и при этом так слепа. Позволь открыть тебе глаза, Архонт.', 38134),
(194571, 'ruRU', '', 'Твой господин лгал тебе все это время.', 38134),
(194570, 'ruRU', '', 'Пустые угрозы.', 38134),
(194569, 'ruRU', '', 'Кирестия, твоему правлению конец! У кирий теперь новый владыка!', 38134),
(194566, 'ruRU', '', 'Идем, пилигрим. Ты увидишь гибель Архонта своими глазами!', 38134),
(194565, 'ruRU', 'Но сразить Лисонию придется тебе.', '', 38134),
(194564, 'ruRU', 'Клянусь, мы не позволим никому из ее армии добраться до Архонта.', '', 38134),
(194563, 'ruRU', 'Бастион достанется Лисонии.', '', 38134),
(194562, 'ruRU', 'Надеюсь, еще не слишком поздно. Если Архонт падет...', '', 38134),
(194561, 'ruRU', 'Другие наши войска тоже отступают в цитадель.', '', 38134),
(194560, 'ruRU', 'Раскольники подходят к Элизийской цитадели! Нам нужно вернуться!', '', 38134),
(194559, 'ruRU', '', 'Я исполню свое предназначение. Остался последний шаг...', 38134),
(194558, 'ruRU', '', 'Когда Архонт падет, даже ваши драгоценные идеалы прозреют.', 38134),
(194557, 'ruRU', '', 'Вот как? Тогда почему из-за вашей жалкой вылазки Архонт впервые осталась без защиты?', 38134),
(194556, 'ruRU', '', 'Я – не ты! Я не брошу своих братьев и сестер!', 38134),
(194554, 'ruRU', '', 'Я покажу ваше прошлое. Этот мир еще можно спасти. Не верьте Архонту, слушайте меня!', 38134),
(194553, 'ruRU', '', 'И это все? Покажите мне силу Архонта! Победите меня, если вы так этого хотите!', 38134),
(194552, 'ruRU', 'Даю тебе последний шанс. Твои воины терпят поражение, ты уже проиграла!', '', 38134),
(194551, 'ruRU', '', 'Глупый претендент. Проиграла? Думаешь, что справишься со мной в одиночку?', 38134),
(194550, 'ruRU', '', 'Свет, Бездна... Эти силы здесь ничего не значат. Арбитр не просто так отправила тебя в Малдраксус. Нужно попытаться узнать, почему.', 38134),
(194549, 'ruRU', 'Почему меня не выбрали? Разве я недостаточно предан Свету? Или моя душа была проклята, как только я стал нежитью?', '', 38134),
(194547, 'ruRU', '', 'Воспоминания терзают тебя даже после смерти. У нас, в Бастионе, их посчитали бы лишней ношей.', 38134),
(194545, 'ruRU', 'Сначала я увидел в малдраксийцах только Плеть, которая разорила мой мир. Чудовищ, которых я поклялся уничтожить.', '', 38134),
(194544, 'ruRU', '', 'Претенденты оставляют воспоминания в храме Чистоты, но мы пойдем по другому пути. Малдраксус тебе не подходит?', 38134),
(194543, 'ruRU', '', 'Идите со мной. Вы оба.', 38134),
(194542, 'ruRU', 'Статус цели: жи-жи-жи-ва. Устраняю...', '', 38134),
(194541, 'ruRU', 'Он отключается! Продолжайте сражаться!', '', 38134),
(194540, 'ruRU', '', 'Я приказала убить их. Почему они еще живы?', 38134),
(194539, 'ruRU', 'Я помню... унижение. Твоя... очередь.', '', 38134),
(194538, 'ruRU', '', 'Мы стольких обучили вместе. Ты совсем ничего не помнишь?', 38134),
(194537, 'ruRU', 'Недопустимый тон. Снимаю ограничение на смертельный урон.', '', 38134),
(194536, 'ruRU', '', 'Только не он...', 38134),
(194535, 'ruRU', 'Что? \"Атанос\"?..', '', 38134),
(194534, 'ruRU', '', 'Путь на юг свободен. Надо спешить!', 38134),
(194533, 'ruRU', '', 'Вот в чем лицемерие Архонта.', 38134),
(194532, 'ruRU', '', 'Вы считаете себя праведниками – и с легкостью убиваете своих сородичей.', 38134),
(194531, 'ruRU', '', 'Я продолжу ее дело. От этого зависят наши судьбы.', 38134),
(194530, 'ruRU', '', 'Как ты смеешь произносить ее имя?! Мой идеал осуществила лишь часть задуманного.', 38134),
(194529, 'ruRU', '', 'Ты знаешь, что тебя ждет. То же, что и Девию.', 38134),
(194528, 'ruRU', '', 'Молчи, претендент. Ты ничего не знаешь о кошмарах.', 38134),
(194527, 'ruRU', '', 'Меня лишили воспоминаний... прошлого... всей моей жизни!', 38134),
(194526, 'ruRU', 'Лисония, еще не поздно прекратить этот кошмар! Мы все кирии!', '', 38134),
(194524, 'ruRU', '', 'Мы с тобой до конца!', 38134),
(194523, 'ruRU', 'Сегодня великий день! А теперь, если позволишь, я почти закончил новую, улучшенную версию \"Брона\"!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194521, 'ruRU', 'Начинаем?', '', 38134),
(194517, 'ruRU', 'То же было и с Лисонией. Я не знал, что они обе служили силам тьмы...', '', 38134),
(194516, 'ruRU', 'Девия убедила меня в том, что я добьюсь правосудия.', '', 38134),
(194515, 'ruRU', 'Во время обучения мне пришлось нелегко. Я совсем забыл о прошлой жизни.', '', 38134),
(194514, 'ruRU', 'Спасибо за милосердие. Я не забуду твоей доброты.', '', 38134),
(194513, 'ruRU', 'Хотел бы я сам знать... Пожалуйста, веди меня.', '', 38134),
(194512, 'ruRU', '', 'Все нужно делать самой!', 38134),
(194511, 'ruRU', '', 'Еще не время. Вставайте и сражайтесь!', 38134),
(194510, 'ruRU', '', 'Они были слабы. Я даже рада избавлению от этих ничтожеств.', 38134),
(194509, 'ruRU', '', 'Все это неважно.', 38134),
(194508, 'ruRU', '', 'Если ты не собираешься сражаться честно, то и я не буду. Сюда, пилигрим!', 38134),
(194506, 'ruRU', 'Ты, как всегда, глупа, Наджия. Мои стражи в два счета с тобой расправятся.', '', 38134),
(194504, 'ruRU', '', 'Иди сюда. Разочаруй меня так же, как Утер.', 38134),
(194502, 'ruRU', '', 'Наконец-то! Пока я сидела взаперти, успела хорошенько обдумать нашу дуэль.', 38134),
(194500, 'ruRU', '', 'Кирестия права, в Темных Землях разруха. Но чтобы все исправить, ей не хватает... силы воли.', 38134),
(194498, 'ruRU', '', 'Я завершу дело Девии. Мы не будем служить Архонту и слепо идти по какому-то \"пути\".', 38134),
(194495, 'ruRU', '', 'Но мы забрали аниму.', 38134),
(194494, 'ruRU', '', 'Победа за нами. Пускай мы потеряли Шпили...', 38134),
(194493, 'ruRU', 'Мы почти победили... нанеси последний удар... ради нас всех.', '', 38134),
(194491, 'ruRU', 'Я не смогу их долго сдерживать! Скорее, к воротам!', '', 38134),
(194490, 'ruRU', '', 'Да хранит нас всех Архонт.', 38134),
(194489, 'ruRU', '', 'Я расчищу арену. Храмовые врата находятся к северу.', 38134),
(194487, 'ruRU', '', 'Скорее сюда!', 38134),
(194485, 'ruRU', '', 'ОТВЕДАЙТЕ СТАЛИ!', 38134),
(194483, 'ruRU', '', 'Ты ведь помнишь тренировки в Горниле? Мы легко обратили его на нашу сторону.', 38134),
(194482, 'ruRU', 'Д-да... мой идеал...', '', 38134),
(194481, 'ruRU', '', 'Остановите их любой ценой! \"Атанос\", отдай всю свою аниму до капли, если нужно!', 38134),
(194480, 'ruRU', '', 'Отвлеките их! Глашатай Истины почти зарядился.', 38134),
(194479, 'ruRU', '', 'Какая настойчивость!', 38134),
(194478, 'ruRU', 'Мы оттесним их подкрепление. Вперед!', '', 38134),
(194477, 'ruRU', '', 'Они еще держатся. Стреляйте!', 38134),
(194476, 'ruRU', '', 'Так заряди его! Если придется, отключи центурионов!', 38134),
(194475, 'ruRU', 'У колосса мало анимы. Нужна перезарядка.', '', 38134),
(194474, 'ruRU', '', 'Обрушьте на них смерть!', 38134),
(194473, 'ruRU', 'Я необычайно рад знакомству с тобой!', '', 38134),
(194469, 'ruRU', '', 'Моих лучших воинов так легко не победить.', 38134),
(194468, 'ruRU', '', 'Вы убили всего лишь новобранцев. Их место займут другие.', 38134),
(194465, 'ruRU', '', 'Узрите всю мощь раскольников!', 38134),
(194464, 'ruRU', '', 'Кирестия всегда была прямолинейна.', 38134),
(194463, 'ruRU', '', 'Бездумно врываемся в бой?', 38134),
(194462, 'ruRU', 'Слава Архонту! Слава Бастиону!', '', 38134),
(194460, 'ruRU', 'Ждем твоего сигнала.', '', 38134),
(194459, 'ruRU', 'Ты, пилигрим, вместе с основными силами пойдешь по центру.', '', 38134),
(194458, 'ruRU', 'Наши идеалы ударят с флангов.', '', 38134),
(194456, 'ruRU', 'Перерожденные, наше время пришло. Сегодня раскольники падут раз и навсегда.', '', 38134),
(194444, 'ruRU', 'Я тоже рад тебя видеть, Габбинс.', 'Я тоже рада тебя видеть, Габбинс.', 38134),
(194443, 'ruRU', 'Габбинс рад!', '', 38134),
(194442, 'ruRU', 'Я не забываю! И чайник при мне!', '', 38134),
(194440, 'ruRU', 'Габбинс и Таббинс, друзья мои! Я боялся, что вы забыли, который сейчас час.', '', 38134),
(194437, 'ruRU', '', 'Правосудие свершится.', 38134),
(194428, 'ruRU', '', 'Хорошо. Я нашла тебе подмогу.', 38134),
(194427, 'ruRU', '', 'Да! Вот он!', 38134),
(194426, 'ruRU', '', 'Ты составила список, Сика?', 38134),
(194425, 'ruRU', '', 'О! Ух-ху! Привет-привет!', 38134),
(194423, 'ruRU', 'Животным Бастиона не хватает кирийской дисциплины. Может, тебе удастся это исправить.', '', 38134),
(194421, 'ruRU', 'У-ух! Помочь? Могу что-нибудь сделать или составить компанию!', 'У-ух! Помочь? Могу что-нибудь сделать или составить компанию!', 38134),
(194414, 'ruRU', 'Великолепно. Это устройство должно нам помочь.', '', 38134),
(194413, 'ruRU', 'Брокеры наткнулись на склад любопытного оружия из других миров. Посмотрим, пригодится ли оно нам.', '', 38134),
(194402, 'ruRU', '', 'Не знаю. Пусть сперва все увидит...', 38134),
(194401, 'ruRU', 'Думаешь, эта душа правда сможет нам помочь?', '', 38134),
(194391, 'ruRU', '', 'Я же говорила! Еще не все потеряно.', 38134),
(194388, 'ruRU', 'Открой свой разум, смертная душа. Сейчас мы увидим, что за воспоминания в нем таятся.', 'Открой свой разум, смертная душа. Сейчас мы увидим, что за воспоминания в нем таятся.', 38134),
(194385, 'ruRU', 'Да-да. Спасибо, Габбинс. Я самый умный!', '', 38134),
(194384, 'ruRU', 'Таббинс знал! Ура Таббинсу!', '', 38134),
(194378, 'ruRU', '<свистит>', '<свистит>', 38134),
(194377, 'ruRU', 'Даже у смерти есть светлая сторона...', 'Даже у смерти есть светлая сторона...', 38134),
(194375, 'ruRU', 'Представь, как я удивился! Простой брокер, как я, работает с такой уникальной личностью, как ты!', '', 38134),
(194363, 'ruRU', 'Им откроется правда... и тогда они падут...', '', 38134),
(194358, 'ruRU', '', 'Когда-нибудь... они поймут...', 38134),
(194336, 'ruRU', '', 'Считай это частью обучения.', 38134),
(194335, 'ruRU', 'Зачем пешком? Меня и так утомило испытание.', '', 38134),
(194334, 'ruRU', '', 'Продолжай путь. Мы с Пелагием пойдем пешком.', 38134),
(194332, 'ruRU', 'Кажется, все мои инструменты на месте. Я должен кому-то зеркало.', 'Кажется, все мои инструменты на месте. Я должна кому-то зеркало.', 38134),
(194331, 'ruRU', 'Что ж, по дороге на чаепитие можно и повеселиться.', 'Что ж, по дороге на чаепитие можно и повеселиться.', 38134),
(194330, 'ruRU', 'Хм, интересное совпадение... Как моя сумка оказалась здесь?', 'Хм, интересное совпадение... Как моя сумка оказалась здесь?', 38134),
(194328, 'ruRU', '', 'Они хотят помешать нам переродиться. Надо найти остальных!', 38134),
(194327, 'ruRU', 'Если бы только я мог сказать то же самое! Ты просто не поверишь, что мне пришлось пережить этим утром!', '', 38134),
(194326, 'ruRU', 'Не жалуюсь...', 'Не жалуюсь...', 38134),
(194324, 'ruRU', 'Здравствуй, дорогая Вулка! Надеюсь, дорога сюда была приятной.', '', 38134),
(194319, 'ruRU', 'Дай во что-нибудь ударить!', 'Дай во что-нибудь ударить!', 38134),
(194318, 'ruRU', 'У меня плохое предчувствие...', 'У меня плохое предчувствие...', 38134),
(194317, 'ruRU', 'Я не дам себя сломить!', 'Я не дам себя сломить!', 38134),
(194316, 'ruRU', 'Нельзя спать... до... чаепития...', 'Нельзя спать... до... чаепития...', 38134),
(194315, 'ruRU', 'Наверное, можно отдохнуть... но только минуточку!', 'Наверное, можно отдохнуть... но только минуточку!', 38134),
(194314, 'ruRU', 'Нельзя останавливаться. Мне нужно идти дальше.', 'Нельзя останавливаться. Мне нужно идти дальше.', 38134),
(194297, 'ruRU', 'Лучше начну заваривать...', '', 38134),
(194296, 'ruRU', 'Кажется, я пришел первым, несмотря на неожиданные утренние события.', '', 38134),
(194293, 'ruRU', '', 'Могрейн рассказывал о твоей отваге и способностях командира, Дрека. Я рада наконец встретить тебя.', 38134),
(194292, 'ruRU', '', 'Ты как раз вовремя, идеал. Сейчас у нас общий враг.', 38134),
(194291, 'ruRU', 'Быть может, это шанс восполнить наши потери.', '', 38134),
(194290, 'ruRU', 'Это существо может принести нам прибыль.', '', 38134),
(194289, 'ruRU', 'Сибилла права. Хватит разговоров. Передайте чай и паштет!', 'Сибилла права. Хватит разговоров. Передайте чай и паштет!', 38134),
(194288, 'ruRU', '', 'Хватит рассказов. Лучше намазать!', 38134),
(194287, 'ruRU', 'Ура! Где чай, там и истории!', '', 38134),
(194286, 'ruRU', 'Я самый умный!', '', 38134),
(194285, 'ruRU', 'Нет, дорогой Таббинс, я настаиваю на том, что пережил сегодня более увлекательное приключение!', '', 38134),
(194284, 'ruRU', 'Тебе удалось убить столько кровобивней в одиночку?', 'Тебе удалось убить столько кровобивней в одиночку?', 38134),
(194281, 'ruRU', 'Первым делом нужно, чтобы тебя заметили!', '', 38134),
(194279, 'ruRU', 'Мало быть хорошим бойцом. Нужны связи!', '', 38134),
(194278, 'ruRU', 'Прочь, прочь, прочь! Это мое! Я его нашел! Я забираю себе это сокровище. Оно мое.\n\nУходи! Убирайся, ползи назад в свою поганую нору! Хоть прыгни со стены, мне все равно, только оставь меня с моим сокровищем.\n\nОно мне нужно. Такие как ты, в нарядной броне и дорогих одеждах, никогда меня не поймут. Это мое!', 'Прочь, прочь, ПРОЧЬ! Это МОЕ! Я его нашла! Я забираю себе это сокровище. Оно мое.\n\nУходи! Убирайся, ползи назад в свою поганую нору! Хоть прыгни со стены, мне все равно, только оставь меня с этим сокровищем.\n\nОно мне нужно. Такие как ты, в нарядной броне и дорогих одеждах, никогда меня не поймут. ОНО МОЕ!', 38134),
(194271, 'ruRU', 'Нет, нет, нет! Это мое сокровище!', '', 38134),
(194266, 'ruRU', 'Следующий поединок, |3-6($r)?', '', 38134),
(194265, 'ruRU', 'Я рассчитываю на не менее успешные сделки в будущем.', '', 38134),
(194250, 'ruRU', 'Прежде чем допустить тебя на арену, мне нужно оценить прибыльность нашего сотрудничества. По мере продолжительности нашего знакомства я решу, стоит ли тратить на тебя время.', '', 38134),
(194249, 'ruRU', '', 'Выходи, некромант! Я отомщу за Таникоса!', 38134),
(194247, 'ruRU', 'Пора научить этих грубиянов манерам!', '', 38134),
(194246, 'ruRU', 'Ах, желанная передышка от лучей Света...', '', 38134),
(194245, 'ruRU', 'Именно поэтому я не одобряю пробежек с зонтиками!', '', 38134),
(194229, 'ruRU', 'Неожиданный результат – лучший результат! Благодарю за помощь!', '', 38134),
(194228, 'ruRU', 'Следующий костюм!', '', 38134),
(194227, 'ruRU', 'А теперь – твой выход!', '', 38134),
(194224, 'ruRU', 'Да они тут везде кучами лежат, и их охраняют камнерожденные... Им надсмотрщик сказал. Мы все его терпеть не можем. Достань камни и помоги Наджии!', '', 38134),
(194223, 'ruRU', 'Это ж камни грехов. Если у надсмотрщика есть камень другого клыкастого, то тот будет плясать под его дудку. Так ты поможешь Наджии?', '', 38134),
(194219, 'ruRU', 'Ты – друг Наджии, а, значит, и мой друг. Нам велели отыскать камни с каракулями. Они разбросаны по всей чаще.', '', 38134),
(194211, 'ruRU', 'Как раз там, где я и думал!', '', 38134),
(194210, 'ruRU', '', 'Я поделюсь с тобой своей мудростью.', 38134),
(194202, 'ruRU', 'Надо найти счастливый чайник! Скорее!', '', 38134),
(194201, 'ruRU', 'Только не надо на меня сваливать...', '', 38134),
(194200, 'ruRU', 'Чайник всегда у Таббинса.', '', 38134),
(194199, 'ruRU', 'Я думал, чайник у тебя!', '', 38134),
(194197, 'ruRU', 'Мы маленько опоздали на чаепитие...', '', 38134),
(194196, 'ruRU', 'Я ничего не могу поделать без своих инструментов...', 'Я ничего не могу поделать без своих инструментов...', 38134),
(194195, 'ruRU', 'Не могу поверить, что меня поймали за кражей зеркал!', 'Не могу поверить, что меня поймали за кражей зеркал!', 38134),
(194194, 'ruRU', 'Все началось с простого недопонимания...', 'Все началось с простого недопонимания...', 38134),
(194193, 'ruRU', 'Без зонтика мне придется держаться теней... Проклятый Свет!', '', 38134),
(194192, 'ruRU', 'Ох... Верните мой зонтик, грубияны!', '', 38134),
(194191, 'ruRU', 'Сядь, налей себе чаю и послушай историю о моих славных приключениях...', '', 38134),
(194181, 'ruRU', 'Сибилла опоздала! Таббинс пришел рано!', '', 38134),
(194179, 'ruRU', 'Мои кости... соберут заново...', 'Мои кости... соберут заново...', 38134),
(194178, 'ruRU', '', 'Какая беспечность!', 38134),
(194177, 'ruRU', '', 'Голая нечисть.', 38134),
(194174, 'ruRU', '', 'Роща поет, красота возвращается. Королева и Двор благодарят тебя, друг и хранитель диких семян.', 38134),
(194173, 'ruRU', 'Я пробудился. Начинаю испытание.', '', 38134),
(194170, 'ruRU', '', 'Корни укрепляются, их жажда утолилась. Благодаря тебе многие духи переродятся.', 38134),
(194164, 'ruRU', '', 'Анима расцветает в саду. Пусть ты пока все лишь росток, но ты уже поддерживаешь и защищаешь нас.', 38134),
(194161, 'ruRU', '', 'Роща наполняется силой. То же будет и с дикими семенами. Впереди много работы.', 38134),
(194160, 'ruRU', 'Тебе еще расти и расти. Станешь сильнее – возвращайся.', '', 38134),
(194159, 'ruRU', 'Результат удовлетворительный. Я сдаюсь.', '', 38134),
(194158, 'ruRU', 'Тебя нужно оценить. Тебя нужно взвесить.', '', 38134),
(194140, 'ruRU', '', 'Трижды хватит, никто не заплатит.', 38134),
(194137, 'ruRU', 'У-и-и!', '', 38134),
(194135, 'ruRU', 'Я смог! Ого!', '', 38134),
(194127, 'ruRU', '', 'Весь мой отряд подкараулили пожиратели. Разлом в башне застал нас врасплох, и в следующую секунду мы уже были окружены. Мне едва удалось отбиться.', 38134),
(194124, 'ruRU', '', 'Вскоре семя прорастет, и ты сможешь его пробудить.', 38134),
(194122, 'ruRU', '', 'Помести дух в пустое дикое семя, чтобы начать.', 38134),
(194120, 'ruRU', 'Быстрее, помоги им подготовить сцену!', '', 38134),
(194117, 'ruRU', 'Начинаем следующий акт!', '', 38134),
(194106, 'ruRU', '', 'Сначала катализатор, только потом – семя. Сажай его первым, иначе он не подействует.', 38134),
(194105, 'ruRU', '', 'Каждый катализатор дарует что-то новое. Учись, пробуй, познавай.', 38134),
(194104, 'ruRU', '', 'Семена набираются сил от посаженного рядом катализатора.', 38134),
(194096, 'ruRU', 'Все по местам!', '', 38134),
(194094, 'ruRU', 'О, вот и ты! Мы как раз начинаем!', '', 38134),
(194093, 'ruRU', '', 'Можешь принять их дары. Таковы обычаи смертных.', 38134),
(194091, 'ruRU', '', 'Переродившись, духи благодарят нас.', 38134),
(194088, 'ruRU', '', 'Здесь растут особые семена. Твои навыки окрепнут вместе с ними. А я вам помогу.', 38134),
(194087, 'ruRU', '', 'Заботливый уход направит их навстречу судьбе. Наша задача – придать им сил.', 38134),
(194085, 'ruRU', 'Нет. Нет! Я должен бороться!', '', 38134),
(194082, 'ruRU', 'Нас всегда ждут новые открытия!', '', 38134),
(194081, 'ruRU', 'Резан оказался умнее, чем я думал! Теперь старина Бвонсамди сможет заключить сделку с новым лоа. Хе-хе.', '', 38134),
(194080, 'ruRU', 'Я... слаб... я... не справлюсь...', '', 38134),
(194078, 'ruRU', 'Это большая честь для меня, ваше величество. Спасибо, $p, за всю твою помощь... при жизни и здесь.', '', 38134),
(194077, 'ruRU', '', 'Я не знаю, какая судьба тебе уготована, Вол\'джин из племени Черного Копья. Но я дам тебе шанс возродиться.', 38134),
(194074, 'ruRU', '', 'И это необычное создание. Смертную душу, вобравшую в себя сущность лоа.', 38134),
(194073, 'ruRU', '', 'Я возьму этих троих духов под опеку, Бвонсамди.', 38134),
(194070, 'ruRU', 'Привет, твое величество! Старина Бвонсамди помог этой душе найти пропавших лоа.', '', 38134),
(194067, 'ruRU', 'Прошу! Прошу!!!', '', 38134),
(194066, 'ruRU', 'Все... закончилось...', '', 38134),
(194065, 'ruRU', 'Твой провал... пятно... на твоей душе...', '', 38134),
(194064, 'ruRU', 'Какая слабость. Тебе не видать перерождения.', '', 38134),
(194063, 'ruRU', 'П... помоги... мне...', '', 38134),
(194062, 'ruRU', 'Мне так жаль...', '', 38134),
(194061, 'ruRU', 'Один... вечно один...', '', 38134),
(194060, 'ruRU', 'Столько... испытаний...', '', 38134),
(194059, 'ruRU', 'Я не стану перерожденным.', '', 38134),
(194058, 'ruRU', 'Клейя... помоги!', '', 38134),
(194057, 'ruRU', 'Это невозможно!', '', 38134),
(194056, 'ruRU', 'А-а...', '', 38134),
(194054, 'ruRU', 'Я все... прощаю...', '', 38134),
(194053, 'ruRU', 'Наконец-то... тишина...', '', 38134),
(194052, 'ruRU', 'Нет!', '', 38134),
(194051, 'ruRU', 'За что? Почему?!', '', 38134),
(194050, 'ruRU', 'Нет! Так нельзя!', '', 38134),
(194049, 'ruRU', 'Я... не смог?', '', 38134),
(194048, 'ruRU', 'Прекрати... эти мучения...', '', 38134),
(194047, 'ruRU', 'Я недостоин!', '', 38134),
(194046, 'ruRU', 'Боль!', '', 38134),
(194042, 'ruRU', 'Я же просто старый темный охотник... Ты нужен нашему народу!', '', 38134),
(194040, 'ruRU', 'Пусть часть меня... переродится... с тобой.', '', 38134),
(194039, 'ruRU', 'Возьми остатки... моей сущности... отправляйся в Арденвельд...', '', 38134),
(194035, 'ruRU', 'Ой! Ой, нет, так не пойдет. Давай еще раз!', '', 38134),
(194031, 'ruRU', '', 'Время неумолимо, словно бурная река. Одной мне не преодолеть его течение.', 38134),
(194030, 'ruRU', '', 'Духи, спящие здесь, ослабли. Они беззащитны, словно капли дождя.', 38134),
(194029, 'ruRU', '', 'Это особенное место. Здесь заблудшие души получают новый шанс.', 38134),
(194028, 'ruRU', '', 'Шепотом моих ветвей роща приветствует тебя.', 38134),
(194026, 'ruRU', '', 'Следовать пути. Будто у нас... Ладно, идем, нам пора.', 38134),
(194025, 'ruRU', '', 'Вам многому предстоит научиться, но не бойтесь! Это ваш собственный путь, так что можно не спешить.', 38134),
(194024, 'ruRU', '', 'Вы очиститесь от недостатков, достигнете совершенства и сможете исполнять наш непростой и священный долг.', 38134),
(194023, 'ruRU', '', 'Ступив на наш путь, вы познаете все добродетели: Чистоту, Отвагу, Смирение, Мудрость и Верность.', 38134),
(194022, 'ruRU', 'Вол\'джин... Могучий у тебя дух. Ты жаждешь... правосудия.', '', 38134),
(194019, 'ruRU', 'Нет! Резан, мы тебя вытащим. Соберись с силами.', '', 38134),
(194018, 'ruRU', 'Вы... опоздали...', '', 38134),
(194017, 'ruRU', 'Есть... выход...', '', 38134),
(194016, 'ruRU', 'Эту душу не спасти! От нее ничего не осталось!', '', 38134),
(194015, 'ruRU', 'Беги... отсюда...', '', 38134),
(194014, 'ruRU', 'Ты разделишь страдания этого жалкого лоа!', '', 38134),
(194011, 'ruRU', 'Дело за тобой... Возьми мою аниму и спаси Резана!', '', 38134),
(194010, 'ruRU', 'Но я посвятил жизнь служению Свету. Разве за это не положено место в Бастионе?', '', 38134),
(194007, 'ruRU', '', 'Испепелитель. Вердикт Арбитра неоспорим. Должно быть, она увидела в тебе что-то, что роднит тебя с Малдраксусом.', 38134),
(194006, 'ruRU', 'Надо спасти Резана от этого чудовища!', '', 38134),
(194005, 'ruRU', 'Найти едкую жижу и соединяемую слизь бывает весьма непросто.', '', 38134),
(194003, 'ruRU', 'Возвращайся попозже. Сделаем вместе еще одно зелье.', '', 38134),
(193997, 'ruRU', 'Эти маски... мои друзья... их как будто не было.', '', 38134),
(193996, 'ruRU', 'В нашей деревне случилось нечто ужасное... Нельзя, чтобы это повторилось!', '', 38134),
(193995, 'ruRU', 'Я благодарен хранителям этой рощи за то, что они приняли меня. Обещаю отблагодарить их за гостеприимство.', '', 38134),
(193994, 'ruRU', 'Получилось! Сеть снова работает.', '', 38134),
(193993, 'ruRU', 'Кто-то или что-то мешает работе сети зеркал. Постарайся выяснить, что вызвало этот сбой, и восстанови сеть.', '', 38134),
(193991, 'ruRU', 'Землерои швыряют в лужу жижи все подряд. Это необходимо для создания землероев.\n\nК сожалению, если пропорции совсем неправильные, получаются эти мерзкие существа. Однажды из лужи вылезло огромное слияние... Ух, сколько грязи было!', '', 38134),
(193990, 'ruRU', 'Чудесно! Заходи на чай когда захочешь!', '', 38134),
(193985, 'ruRU', '', 'За тобой никто не следил? Костоплав Гейрмир требует строжайшей секретности.', 38134),
(193982, 'ruRU', 'Заходи, присоединяйся! Ты как раз к чаю.', '', 38134),
(193981, 'ruRU', '', 'Держи крепко. Нельзя потерять ни капельки!', 38134),
(193980, 'ruRU', '', 'Сика, у нас есть анима для привратницы?', 38134),
(193978, 'ruRU', 'Кричи, пинайся, кусайся, дерись оружием... победи любым путем!', '', 38134),
(193970, 'ruRU', 'Ты все делаешь правильно, пилигрим.', '', 38134),
(193960, 'ruRU', 'У тебя большой потенциал, пилигрим Утробы. Посмотрим, как ты справишься, помогая бойцам на арене.', '', 38134),
(193940, 'ruRU', 'Поскорее бы все это проанализировать! Блестяще, подмастерье.', '', 38134),
(193938, 'ruRU', 'Похоже, местность вокруг некрополя подверглась влиянию едких субстанций. Время собирать образцы!', '', 38134),
(193933, 'ruRU', 'Иди дальше. Я... догоню.', '', 38134),
(193930, 'ruRU', 'Преклонить колени? Мне? Перед тобой? Ты, как всегда, несешь чушь, Наджия. В следующий раз я дарую тебе истинную смерть.', '', 38134),
(193929, 'ruRU', '', 'Матьяш. Ты оскорбил меня, и я требую сатисфакции. Извинись, преклонив колени, или сражайся. Моим секундантом будет пилигрим Утробы.', 38134),
(193928, 'ruRU', 'Уф... Что это... что происходит?', '', 38134),
(193919, 'ruRU', 'Она знает?', 'Она знает?', 38134),
(193899, 'ruRU', 'Я буду стараться. Ради картеля, конечно.', 'Я буду стараться. Ради картеля, конечно.', 38134),
(193894, 'ruRU', 'Конечно. Смотрители тоже умны, но я верю тебе, Та\'мари. Пусть твои помощники продолжают работу.', 'Конечно. Смотрители тоже умны, но я верю тебе, Та\'мари. Пусть твои помощники продолжают работу.', 38134),
(193893, 'ruRU', 'Не надо их недооценивать. Ты смышленый брокер, но будь начеку.', 'Не надо их недооценивать. Ты смышленый брокер, но будь начеку.', 38134),
(193892, 'ruRU', 'Докладываю, ваше роскошество. Служители задают вопросы про сеть порталов, но у меня пока все под контролем.', 'Докладываю, ваше роскошество. Служители задают вопросы про сеть порталов, но у меня пока все под контролем.', 38134),
(193891, 'ruRU', 'Превосходно. Так держать.', 'Превосходно. Так держать.', 38134),
(193890, 'ruRU', 'Мне поручили небольшое задание.', 'Мне поручили небольшое задание.', 38134),
(193889, 'ruRU', 'Хотя, наверное, тебя тоже надо поблагодарить, Та\'мари?', 'Хотя, наверное, тебя тоже надо поблагодарить, Та\'мари?', 38134),
(193888, 'ruRU', 'Прекрасные новости. Передай мое уважение своим помощникам.', 'Прекрасные новости. Передай мое уважение своим помощникам.', 38134),
(193887, 'ruRU', 'Докладываю, ваше роскошество. У нас снова получилось увеличить пропускную способность порталов.', 'Докладываю, ваше роскошество. У нас снова получилось увеличить пропускную способность порталов.', 38134),
(193886, 'ruRU', '', 'Жалкие слабаки! Вам не остановить дом Кадавров!', 38134),
(193867, 'ruRU', 'Будет сделано, не сомневайтесь.', 'Будет сделано, не сомневайтесь.', 38134),
(193866, 'ruRU', 'Тогда пусть работает как раньше. Она может нам вскоре понадобиться.', 'Тогда пусть работает как раньше. Она может нам вскоре понадобиться.', 38134),
(193865, 'ruRU', 'Конечно. Ни у одного картеля нет такой надежной сети. Очень полезно.', 'Конечно. Ни у одного картеля нет такой надежной сети. Очень полезно.', 38134),
(193864, 'ruRU', 'Твой помощник еще поддерживает портал?', 'Твой помощник еще поддерживает портал?', 38134),
(193863, 'ruRU', 'Здравствуй. Полагаю, все идет по графику?', 'Здравствуй. Полагаю, все идет по графику?', 38134),
(193862, 'ruRU', 'Лоа где-то там, за мостом. Пошли, время не ждет.', '', 38134),
(193860, 'ruRU', 'Всегда пожалуйста.', 'Всегда пожалуйста.', 38134),
(193859, 'ruRU', 'Ты так думаешь? Ну что ж, спасибо.', 'Ты так думаешь? Ну что ж, спасибо.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193858, 'ruRU', 'Это сам по себе стиль. По-моему, тебе подходит.', 'Это сам по себе стиль. По-моему, тебе подходит.', 38134),
(193857, 'ruRU', 'На тебе они всегда смотрятся намного лучше. Я предпочитаю что-нибудь попроще.', 'На тебе они всегда смотрятся намного лучше. Я предпочитаю что-нибудь попроще.', 38134),
(193856, 'ruRU', 'Приветствую, друг мой. Ты уже знаешь, что у нас появились новые облики?', 'Приветствую, друг мой. Ты уже знаешь, что у нас появились новые облики?', 38134),
(193855, 'ruRU', 'Да. Очень экзотично!', 'Да. Очень экзотично!', 38134),
(193854, 'ruRU', 'Здравствуй, Визаж. Это что на твоей маске, новый блеск?', 'Здравствуй, Визаж. Это что на твоей маске, новый блеск?', 38134),
(193853, 'ruRU', 'До скорой встречи.', 'До скорой встречи.', 38134),
(193852, 'ruRU', 'Приятно слышать.', 'Приятно слышать.', 38134),
(193851, 'ruRU', 'Твой стиль цепляет глаз, Визаж.', 'Твой стиль цепляет глаз, Визаж.', 38134),
(193850, 'ruRU', 'Так заметно? Спасибо. Мне показалось, что разница едва видна.', 'Так заметно? Спасибо. Мне показалось, что разница едва видна.', 38134),
(193849, 'ruRU', 'Здравствуй, Визаж. Мне нравится твой новый наряд!', 'Здравствуй, Визаж. Мне нравится твой новый наряд!', 38134),
(193848, 'ruRU', 'Я знаю.', 'Я знаю.', 38134),
(193847, 'ruRU', 'Всегда к твоим услугам.', 'Всегда к твоим услугам.', 38134),
(193846, 'ruRU', 'Тебе идет. Может, я потом сам примерю.', 'Тебе идет. Может, я потом сама примерю.', 38134),
(193845, 'ruRU', 'Да. Вам нравится?', 'Да. Вам нравится?', 38134),
(193844, 'ruRU', 'Здравствуй, Та\'визаж. Опять щеголяешь в новом наряде?', 'Здравствуй, Та\'визаж. Опять щеголяешь в новом наряде?', 38134),
(193841, 'ruRU', '', 'Без нашей литейной мне приходится ковать оружие в полевых условиях. Охраняй меня, пока я работаю!', 38134),
(193839, 'ruRU', '', 'Я очищу эти земли!', 38134),
(193838, 'ruRU', '', 'Я сокрушу вас!', 38134),
(193837, 'ruRU', '', 'Вашей мерзости здесь не место!', 38134),
(193836, 'ruRU', '', 'Испытайте гнев идеала Отваги!', 38134),
(193835, 'ruRU', '', 'Я покараю вас!', 38134),
(193834, 'ruRU', '', 'Это место будет очищено!', 38134),
(193833, 'ruRU', 'Так и есть!', 'Так и есть!', 38134),
(193832, 'ruRU', 'Просто валите все на смертных. Они то и дело забывают где-нибудь своих змей. Верно я говорю?', 'Просто валите все на смертных. Они то и дело забывают где-нибудь своих змей. Верно я говорю?', 38134),
(193831, 'ruRU', 'Одно-два существа сбежали, но в остальном все в порядке. Служители же не заметят пару лишних змей, так ведь?', 'Одно-два существа сбежали, но в остальном все в порядке. Служители же не заметят пару лишних змей, так ведь?', 38134),
(193830, 'ruRU', 'Все идет хорошо?', 'Все идет хорошо?', 38134),
(193829, 'ruRU', '', 'А, Могрейн. Когда я с тобой покончу, ты будешь вспоминать Утробу с тоской.', 38134),
(193828, 'ruRU', 'Я постараюсь!', 'Я постараюсь!', 38134),
(193827, 'ruRU', 'Смертные отдают предпочтение так называемым \"милым\" существам. Не до конца понимаю, что они имеют в виду, но вроде бы вся соль в больших глазах. Так что больше животных с большими глазами!', 'Смертные отдают предпочтение так называемым \"милым\" существам. Не до конца понимаю, что они имеют в виду, но вроде бы вся соль в больших глазах. Так что больше животных с большими глазами!', 38134),
(193826, 'ruRU', 'Слушаюсь. Каких-то определенных?', 'Слушаюсь. Каких-то определенных?', 38134),
(193825, 'ruRU', 'Хорошо, но вели своим помощникам поставлять больше существ.', 'Хорошо, но вели своим помощникам поставлять больше существ.', 38134),
(193823, 'ruRU', 'Как и у нас всех. Но я ожидаю большой прибыли с нашего последнего предприятия.', 'Как и у нас всех. Но я ожидаю большой прибыли с нашего последнего предприятия.', 38134),
(193822, 'ruRU', 'Молодец. Работа на благо картеля отнимает много времени.', 'Молодец. Работа на благо картеля отнимает много времени.', 38134),
(193821, 'ruRU', 'Обязательно.', 'Обязательно.', 38134),
(193820, 'ruRU', 'Сеть порталов впечатляет. Передай мою похвалу.', 'Сеть порталов впечатляет. Передай мою похвалу.', 38134),
(193819, 'ruRU', 'Приятно посмотреть на создания, которых добывают мои помощники. Такая красота.', 'Приятно посмотреть на создания, которых добывают мои помощники. Такая красота.', 38134),
(193818, 'ruRU', 'Торговля идет уверенно. Вкусы смертных по части спутников очень разнообразны.', 'Торговля идет уверенно. Вкусы смертных по части спутников очень разнообразны.', 38134),
(193817, 'ruRU', 'Как твои дела?', 'Как твои дела?', 38134),
(193816, 'ruRU', 'Ты проиграешь, я знаю!', '', 38134),
(193815, 'ruRU', '', 'Скоро Пелагием овладеют страх и сомнения. Нужно спешить.', 38134),
(193814, 'ruRU', '', 'Что?! Зачем?.. У нас мало времени!', 38134),
(193813, 'ruRU', 'Клейя! Хвала Архонту... Пелагий начал ритуал без тебя!', '', 38134),
(193812, 'ruRU', '', 'Мы на месте. Пелагий будет рад с тобой познакомиться.', 38134),
(193811, 'ruRU', '', 'С нашим миром происходит страшная катастрофа, $n.', 38134),
(193810, 'ruRU', '', 'Постой... Я покажу.', 38134),
(193809, 'ruRU', '', 'Как только мы закрыли порталы, у нас снова появилась надежда.', 38134),
(193808, 'ruRU', '', 'С виду не скажешь, но мы столько потеряли... Трудно даже описать.', 38134),
(193807, 'ruRU', '', 'Твари Бездны появились из огромных порталов. Если бы мы их не уничтожили, победа стала бы невозможной.', 38134),
(193805, 'ruRU', '', 'Я почти прошла испытания, как вдруг началась засуха. С тех пор никто не получил крылья.', 38134),
(193802, 'ruRU', '', 'Ты знаешь путь лучше других, претендентка. Архонт следит за тобой.', 38134),
(193801, 'ruRU', '$p... Я чую здесь лоа!', '', 38134),
(193800, 'ruRU', '', 'Нам пора уходить. О мести... подумаем потом.', 38134),
(193799, 'ruRU', 'Хотя мы блюдем порядок в Орибосе, мы точно так же поражаемся деяниям Предназначения и стремимся понять его цели.\n\nПредназначение древнее Темных Земель. Ты даже не можешь представить себе, насколько. Эти технологии выше нашего понимания.', '', 38134),
(193797, 'ruRU', 'Нам не всегда дано постигнуть то, как работает Предназначение.\n\nЯ могу предположить, что оно увидит, как именно ты служишь Предназначению, и постарается удовлетворить твои нужды. \n\nТакова помощь Орибоса. Ты служишь Предназначению, и Предназначение найдет способ послужить тебе.\n\nПредназначение ведет нас.', '', 38134),
(193795, 'ruRU', 'Здравствуй, пилигрим.\n\nКогда твоя сила вырастет, ты сможешь получить в хранилище особый дар, который Орибос выберет для тебя в награду за служение Предназначению. Если не перестанешь помогать нам, будешь получать такие дары через определенные промежутки времени.\n\nЕсли не ошибаюсь, в твоем мире эти промежутки называют \"неделей\".', '', 38134),
(193789, 'ruRU', '', 'Они возомнили... себя охотниками? Я разорву их в клочья!', 38134),
(193787, 'ruRU', '', 'Ты... ты не мучитель. \n\nЯ была... в прекрасном лесу... а потом меня что-то схватило...\n\nМои похитители возвращаются... охраняй меня... пока силы не вернутся ко мне!..', 38134),
(193782, 'ruRU', 'Твоя душа отправится в лабиринт!', 'Твоя душа отправится в лабиринт!', 38134),
(193780, 'ruRU', 'Колокола Бастиона успокоят твою душу. Послушай их звон.', 'Колокола Бастиона успокоят твою душу. Послушай их звон.', 38134),
(193779, 'ruRU', '', 'Войди в воду, $n.', 38134),
(193778, 'ruRU', 'Присаживайся. Сейчас начнется презентация.', 'Присаживайся. Сейчас начнется презентация.', 38134),
(193777, 'ruRU', 'Добро пожаловать! Внеси свое имя в список благородных душ, для которых Бастион стал домом.', 'Добро пожаловать! Внеси свое имя в список благородных душ, для которых Бастион стал домом.', 38134),
(193772, 'ruRU', 'Привет, дружище. Чего тебе надо от Бвонсамди?', '', 38134),
(193770, 'ruRU', 'Привет, герой! Мы же хорошая команда, да?', '', 38134),
(193769, 'ruRU', 'Ну-ну, не удивляйся. От старины Бвонсамди не спрячешься!', '', 38134),
(193768, 'ruRU', 'Этого духа выследить непросто... даже темному охотнику. Идем.', '', 38134),
(193767, 'ruRU', 'Привет, герой. Есть разговор.', '', 38134),
(193765, 'ruRU', 'Хм-м... Здесь они мучили духа... а потом куда-то увели. Я напал на след.', '', 38134),
(193764, 'ruRU', 'Нас обнаружили!', '', 38134),
(193763, 'ruRU', 'Вас обоих ждут вечные муки!', 'Вас обоих ждут вечные муки!', 38134),
(193762, 'ruRU', 'Я выслежу духа, но не более. Иначе я могу застрять здесь навсегда.', '', 38134),
(193760, 'ruRU', 'Утроба ждет, $p. И душа, которую мы должны спасти.', '', 38134),
(193757, 'ruRU', 'Некогда источник чистого Света, теперь – существо Бездны, окутывающее все тьмой!', '', 38134),
(193756, 'ruRU', 'Это место... еще хуже, чем я думал. Я чувствую, что наш дух где-то рядом...', '', 38134),
(193747, 'ruRU', 'Что бы это ни было, думаю, оно должно помочь нам.', '', 38134),
(193746, 'ruRU', 'В лужах жижи можно создавать новых землероев, ну и не только...', '', 38134),
(193741, 'ruRU', '', 'Привет, |3-6($c)! Тебя подбросить?', 38134),
(193738, 'ruRU', '', 'Приветствую, душа из Азерота! Мы составили программу, чтобы помочь вам привыкнуть к новому этапу существования.', 38134),
(193737, 'ruRU', '', 'Архонт правит нашим миром. Она – самая стойкая и преданная долгу из Вечных.\n\nАрхонт определила цель существования Бастиона. Об этом древнем создании можно еще многое рассказать, но ее деяния столь велики, что претенденты недостойны даже слышать об этом.\n\nМожет, если вы когда-нибудь заслужите крылья, она пожелает поговорить с вами.', 38134),
(193736, 'ruRU', '', 'Архонт вверила мне очень важную задачу – помогать новоприбывшим освоиться. Я живу, чтобы рассказывать о кириях.\n\nДля меня честь познакомиться с вами.', 38134),
(193730, 'ruRU', '', 'Береги себя и не забывай, чему я тебя учила.', 38134),
(193729, 'ruRU', '', 'Я знаю, тебя ждет великое будущее, $n.', 38134),
(193727, 'ruRU', 'Я должен все исправить. Я не допущу, чтобы Мве\'зала добился своего.', '', 38134),
(193726, 'ruRU', 'Ты слышал, что сказал Мве\'зала. Твоя душа еще связана с Азеротом. Как и душа пилигрима.', '', 38134),
(193725, 'ruRU', 'Бвонсамди, из Утробы нет выхода, так ведь? Если я туда отправлюсь...', '', 38134),
(193724, 'ruRU', 'Постой, $n. Нам с тобой надо потолковать.', '', 38134),
(193723, 'ruRU', 'Марриаза!', '', 38134),
(193721, 'ruRU', 'Я отправлюсь в Утробу с тобой, $n. \n\nМве\'зала сделал из меня посмешище. Я больше не собираюсь ждать, пока все исправит кто-то другой.\n\nМы найдем пропавшую дикую богиню вместе.', '', 38134),
(193720, 'ruRU', 'Если Мве\'зала в чем-то замешан – жди беды.', '', 38134),
(193719, 'ruRU', 'Шон! Наконец-то!', '', 38134),
(193718, 'ruRU', '', 'Индиго! Какое счастье. Я думала, мне конец.', 38134),
(193714, 'ruRU', 'Был рад погеройствовать вместе с тобой, $n.', '', 38134),
(193681, 'ruRU', '', 'Каликс! Хвала Архонту!', 38134),
(193676, 'ruRU', 'Моему почетному гостю будет интересно выслушать Мве\'залу. Между ними... особая связь.', '', 38134),
(193675, 'ruRU', 'Бвонсамди! Что, возгордился, не можешь сам меня призвать?', '', 38134),
(193670, 'ruRU', 'Слабоватые у тебя слуги, Мве\'зала! Одни старые кости да мелкие духи.', '', 38134),
(193660, 'ruRU', 'Мой некрополь тут, рядом. Он, конечно... не в лучшем виде. Но ничего особо страшного! Ты же не боишься?', '', 38134),
(193655, 'ruRU', '', 'Этого хватит. Идем, пробудим привратницу.', 38134),
(193654, 'ruRU', 'У больших сто-о-лько анимы! Но вот этот... почти пустой.', 'У больших сто-о-лько анимы! Но вот этот... почти пустой.', 38134),
(193653, 'ruRU', 'Аниму... Бастиону...', '', 38134),
(193652, 'ruRU', 'Спасибо, друг.', 'Спасибо, друг.', 38134),
(193651, 'ruRU', 'Не бойся. Анима вернется, и он вернется.', 'Не бойся. Анима вернется, и он вернется.', 38134),
(193650, 'ruRU', '', 'Конечно! Бери мою бочку. Центурионы поделятся!', 38134),
(193645, 'ruRU', '', 'Камнерожденные, падший принц и его пилигрим уже здесь. Не дайте им дойти до Владыки.', 38134),
(193642, 'ruRU', 'Чем могу помочь?', 'Чем могу помочь?', 38134),
(193641, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только друидов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только друидов.', 38134),
(193638, 'ruRU', 'Это место просто смертельно классное!', '', 38134),
(193636, 'ruRU', 'Луисон сказал, что ты зайдешь.', '', 38134),
(193635, 'ruRU', 'Наверное, ты отличный боец! У тебя столько черепов!', '', 38134),
(193634, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только монахов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только монахов.', 38134),
(193633, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только паладинов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только паладинов.', 38134),
(193632, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только жрецов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только жрецов.', 38134),
(193631, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только магов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только магов.', 38134),
(193630, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только охотников.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только охотников.', 38134),
(193629, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только разбойников.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только разбойников.', 38134),
(193628, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только шаманов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только шаманов.', 38134),
(193627, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только чернокнижников.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только чернокнижников.', 38134),
(193626, 'ruRU', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только воинов.', 'Привет, |3-6($c)!\n\nЕсли хочешь потренироваться, найди наставника |3-8($c). Я обучаю только воинов.', 38134),
(193624, 'ruRU', 'Нет! Вы ведь... смертные! Это невозможно!', '', 38134),
(193623, 'ruRU', 'И это твои лучшие последователи, Бвонсамди? Жалкое зрелище.', '', 38134),
(193622, 'ruRU', 'Катитесь в Утробу! Никто не остановит Мве\'залу!', '', 38134),
(193621, 'ruRU', 'Надо было служить другому лоа.', '', 38134),
(193620, 'ruRU', 'Тюремщик заберет тебя!', '', 38134),
(193619, 'ruRU', 'Вам с Бвонсамди конец!', '', 38134),
(193617, 'ruRU', '', 'Разделаю под орех!', 38134),
(193616, 'ruRU', '', 'Лазерное шоу!', 38134),
(193615, 'ruRU', '', 'Ливень курятины!', 38134),
(193614, 'ruRU', '', 'Постойте! Забирайте моджо. Миллхаус мне дороже!', 38134),
(193613, 'ruRU', '', 'Миллхаус! А ну приберись, мне работать надо!', 38134),
(193609, 'ruRU', 'Невообразимая мощь!', '', 38134),
(193608, 'ruRU', 'Я испепелю вас... эм... Тьмой!', '', 38134),
(193607, 'ruRU', 'Теперь вы точно узрите мою истинную мощь!', '', 38134),
(193605, 'ruRU', 'Стойте! Лучше я потеряю моджо, чем ее.', '', 38134),
(193604, 'ruRU', 'Ха! Как вам моджо Манаштормов?', '', 38134),
(193602, 'ruRU', 'Я вам покажу, как соваться к гению!', '', 38134),
(193600, 'ruRU', 'Спасибо тебе, пилигрим Утробы. Да направит тебя Архон.', '', 38134),
(193599, 'ruRU', 'Аметия!', '', 38134),
(193596, 'ruRU', 'Спасибо, пилигрим. Прошу, помоги мне освободить Аметию – ее тоже держат здесь.', '', 38134),
(193593, 'ruRU', 'Пожалуйста, пилигрим Утробы, помоги мне. Они забрали Аметию! Я... не знаю, что буду делать без нее. Даже думать не хочу о том, что застряну здесь навсегда один, без нее.\n\nМы должны спасти ее немедленно!', '', 38134),
(193588, 'ruRU', '', 'Привет! Распорядительница Сика готова помочь!', 38134),
(193586, 'ruRU', 'Вот один из алтарей. Чтобы уничтожить его, нужен мой порошок.', '', 38134),
(193585, 'ruRU', '', 'Прочь с глаз моих.', 38134),
(193584, 'ruRU', '', 'Превосходно! Надеюсь, Кнопку еще можно починить.', 38134),
(193583, 'ruRU', 'Вот один из алтарей. Посыпь его порошком, и давай попробуем...', '', 38134),
(193581, 'ruRU', 'У нас теперь есть порошок! Надеюсь, его хватит, чтобы взорвать алтарь...', '', 38134),
(193580, 'ruRU', '', 'Спасибо, пилигрим. Непросто было собрать Кнопку из тех частей, что тут можно найти. Нам лучше уйти отсюда.', 38134),
(193575, 'ruRU', '', 'Это не просто сувениры.', 38134),
(193572, 'ruRU', '', 'Мои безделушки могут и убить.', 38134),
(193568, 'ruRU', 'Каменный легион не проникнет за эти стены.', 'Каменный легион не проникнет за эти стены.', 38134),
(193566, 'ruRU', '', 'КНОПКА!', 38134),
(193565, 'ruRU', '', 'Смотрите под ноги.', 38134),
(193563, 'ruRU', '', 'Используй \"Звездный огонь\", чтобы убить эту группу врагов.', 38134),
(193562, 'ruRU', '', 'Это будет... занимательно.', 38134),
(193561, 'ruRU', '', 'Ах! Ты же пилигрим Утробы, не так ли? Выглядишь аппетитно.\n\nЯ потеряла своего... спутника. Может, выпустишь меня, и пойдем поищем его вместе?', 38134),
(193559, 'ruRU', 'Используй \"Звездный огонь\", чтобы убить эту группу врагов.', '', 38134),
(193557, 'ruRU', '', '\"Звездный огонь\" наносит урон не только основной цели, но и противникам вокруг нее.', 38134),
(193556, 'ruRU', '\"Звездный огонь\" наносит урон не только основной цели, но и противникам вокруг нее.', '', 38134),
(193554, 'ruRU', '', 'Вы мечтали полетать?', 38134),
(193552, 'ruRU', 'Спасибо за помощь, пилигрим. Впредь мы постараемся быть осторожнее.', '', 38134),
(193551, 'ruRU', 'Вот и он. Руки-ноги вроде на месте.', '', 38134),
(193550, 'ruRU', 'Пилигрим Утробы! Перед тобой стоит, хм... парит надсмотрщик Матьяш из дома Брайрбейн.', '', 38134),
(193549, 'ruRU', '', 'Все... мои... планы...', 38134),
(193548, 'ruRU', '', 'Сделка... не удалась...', 38134),
(193546, 'ruRU', 'Спасибо, пилигрим Утробы. Пойдем искать Варрика, ему досталось не меньше.', '', 38134),
(193545, 'ruRU', '', 'Примени \"Гнев\" к манекену.', 38134),
(193544, 'ruRU', 'Примени \"Гнев\" к манекену.', '', 38134),
(193543, 'ruRU', '', '\"Гнев\" наносит урон на расстоянии и позволяет накапливать астральную мощь, которая нужна для более сильных атак.', 38134),
(193542, 'ruRU', '', 'Сделка завершена.', 38134),
(193541, 'ruRU', '\"Гнев\" наносит урон на расстоянии и позволяет накапливать астральную мощь, которая нужна для более сильных атак.', '', 38134),
(193540, 'ruRU', '', 'Жалкое зрелище.', 38134),
(193538, 'ruRU', '', 'Пока противник получает урон от других твоих атак, самое время применить \"Гнев\".', 38134),
(193537, 'ruRU', 'Пока противник получает урон от других твоих атак, самое время применить \"Гнев\".', '', 38134),
(193536, 'ruRU', 'Знаменитый пилигрим Утробы! Это не самый благоприятный день для встречи.\n\nМеня и моего помощника захватили в плен местные твари. Нам бы не помешала твоя помощь.', '', 38134),
(193535, 'ruRU', '', 'Ты себя переоцениваешь.', 38134),
(193534, 'ruRU', '', 'Вы дорого за это заплатите.', 38134),
(193532, 'ruRU', 'Атал\'аи! Служите Хаккару!', '', 38134),
(193527, 'ruRU', 'Ваша кровь послужит мне!', '', 38134),
(193526, 'ruRU', 'Узрите гнев Свежевателя Душ!', '', 38134),
(193525, 'ruRU', 'Умри, смертная тварь!', '', 38134),
(193524, 'ruRU', 'Сила... была моей...', '', 38134),
(193523, 'ruRU', 'Больше жертв Свежевателю Душ.', '', 38134),
(193522, 'ruRU', 'Я стану богом крови и смерти.', '', 38134),
(193521, 'ruRU', 'Лишь капля в море крови.', '', 38134),
(193520, 'ruRU', 'Мы должны найти Пентию, она где-то рядом. И конечно, спасибо за спасение!', '', 38134),
(193519, 'ruRU', 'Эй, $n! Чего копаемся?', '', 38134),
(193518, 'ruRU', 'Даже если так, мы все равно должны ступить на этот путь.', '', 38134),
(193517, 'ruRU', '', 'Я точно помню, что слышала о таком плане много лет назад. До раскрытия истинных планов Владыки.', 38134),
(193516, 'ruRU', 'Слабая, недостойная кровь.', '', 38134),
(193515, 'ruRU', 'Его сила моя! Я вернусь в мир живых!', '', 38134),
(193514, 'ruRU', '', 'Еще! Мне открываются новые знания.', 38134),
(193513, 'ruRU', '', 'Я ем... я... становлюсь лучше...', 38134),
(193512, 'ruRU', '', 'Вы слишком предсказуемы.', 38134),
(193511, 'ruRU', '', 'Я предвижу ваш каждый шаг!', 38134),
(193510, 'ruRU', '', 'От такого не увернетесь!', 38134),
(193509, 'ruRU', 'Этот дар останется с тобой, пока ты сохраняешь нам верность.', '', 38134),
(193508, 'ruRU', '', 'Вам не сбежать.', 38134),
(193506, 'ruRU', 'Кто ты? Почему бродишь тут на свободе? \n\nПожалуйста, помоги нам. Моего друга взяли в плен, мне нужно найти его.\n\nПоможешь?', '', 38134),
(193505, 'ruRU', '', 'Бум! Бу-у-ум!', 38134),
(193504, 'ruRU', '', 'Боль! Боль!', 38134),
(193503, 'ruRU', 'Мы спрячемся в безопасном месте. Спасибо тебе за помощь.', '', 38134),
(193502, 'ruRU', 'Вон она!', '', 38134),
(193500, 'ruRU', '', 'У тебя, наверное, куча вопросов! Не беспокойся! Со временем тебе все объяснят.\n\nИтак, вперед, в новую главу твоего существования!', 38134),
(193499, 'ruRU', 'В этом облике я истреблял орды нежити в Азероте. Но я понимаю, о чем ты.', '', 38134),
(193498, 'ruRU', '', 'Спрошу в ответ то же самое: зачем принимать форму слабого человека, когда есть тела посильнее?', 38134),
(193497, 'ruRU', '', 'Будучи калдорай, я оставалась простой служанкой королевы. Меня ценили только за красоту. В облике наги я добилась признания за ум и стойкость.', 38134),
(193489, 'ruRU', 'Мы должны добыть эти медальоны, чтобы положить начало новой эре и вернуть Ревендрету истинное предназначение.', '', 38134),
(193488, 'ruRU', 'В медальонах, которые дают ему власть над этим миром, заключена часть его силы. Он раздал их слугам, чтобы те беспрекословно подчинялись ему.', '', 38134),
(193485, 'ruRU', 'Баронесса... разве после смерти ты не вернула себе облик эльфийки? Почему ты осталась похожа на нагу?', '', 38134),
(193481, 'ruRU', '', 'Кромешная тьма из глубин разума.', 38134),
(193480, 'ruRU', '', 'Узрите ваше собственное невежество.', 38134),
(193477, 'ruRU', '', 'Тени!', 38134),
(193476, 'ruRU', '', 'Тьма!', 38134),
(193475, 'ruRU', '', 'Я столько еще не знаю.', 38134),
(193472, 'ruRU', '', 'Кое-кто из вас будет вкуснее.', 38134),
(193470, 'ruRU', '', 'Мои дети знают, кого съесть первым.', 38134),
(193469, 'ruRU', '', 'Лакомый кусочек!', 38134),
(193468, 'ruRU', '', 'Они чуют вас!', 38134),
(193462, 'ruRU', '', 'Мои дети пожрут вашу плоть.', 38134),
(193461, 'ruRU', '', 'Мое потомство утолит голод.', 38134),
(193460, 'ruRU', '', 'Пожрите врагов, дети мои!', 38134),
(193459, 'ruRU', '', 'Идите сюда, детки. Ужин ждет.', 38134),
(193458, 'ruRU', '', 'Здесь еда!', 38134),
(193457, 'ruRU', '', 'Каждый укус открывает целую вселенную.', 38134),
(193456, 'ruRU', '', 'Сюда! Ешьте!', 38134),
(193455, 'ruRU', '', 'Голод...', 38134),
(193454, 'ruRU', 'Не гони волну, Вол\'джин! Сейчас я покажу тебе кое-что интересное!', '', 38134),
(193452, 'ruRU', 'Мы с Гейрмир подготовили парочку сюрпризов! Передашь их нашим гостям, хорошо?', '', 38134),
(193451, 'ruRU', '', 'Это наш шанс! Идем за кулисы!', 38134),
(193450, 'ruRU', '', 'Вот так намного лучше! Перерыв!', 38134),
(193449, 'ruRU', '', 'Хм-м, нет, не то. Переставьте дерево в другую сторону!', 38134),
(193448, 'ruRU', '', 'Эй, ты, с деревом! Займи свое место!', 38134),
(193447, 'ruRU', '', 'Кхм... нам пора.', 38134),
(193446, 'ruRU', '', 'Переходим к сожжению Тельдрассила! Так, а дерево где?!', 38134),
(193445, 'ruRU', '', 'О! У нас еще остался волшебный порошок! Давай наведем иллюзию!', 38134),
(193444, 'ruRU', 'С нами пилигрим Утробы!', 'С нами пилигрим Утробы!', 38134),
(193443, 'ruRU', 'Обрушь на них хаос!', 'Обрушь на них хаос!', 38134),
(193441, 'ruRU', '', 'Мне тоже пора!', 38134),
(193420, 'ruRU', 'Вместе мы сильнее!', '', 38134),
(193324, 'ruRU', '', 'Хм-м. Твое имя было в списке? Впрочем, неважно.', 38134),
(193322, 'ruRU', '', 'Дружите с Королевой Резни? Плохой выбор!', 38134),
(193321, 'ruRU', 'Тени всегда укроют тебя.', 'Тени всегда укроют тебя.', 38134),
(193320, 'ruRU', '', 'Осталось убить еще одного врага. Скажи, когда подготовишься.', 38134),
(193319, 'ruRU', 'Я – Ханзабу, самый верный слуга Бвонсамди. Я служил ему даже тогда, когда весь Зулдазар от него отвернулся.\n\nМы остановим слуг Мве\'залы... во что бы то ни стало.', '', 38134),
(193318, 'ruRU', 'Мве\'зала... уже... победил...', '', 38134),
(193317, 'ruRU', 'Я освобожу Мве\'залу и буду вознагражден!', '', 38134),
(193313, 'ruRU', 'Это еще не конец.', 'Это еще не конец.', 38134),
(193312, 'ruRU', 'Живая смертная душа? Куда катится Ревендрет?', '', 38134),
(193311, 'ruRU', 'Все впустую. Сколько пострадало из-за нас?', 'Все впустую. Сколько пострадало из-за нас?', 38134),
(193307, 'ruRU', '', 'Полетели, пилигрим! Вместе мы очистим храм.', 38134),
(193304, 'ruRU', 'Смертельный холод защитит меня!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193303, 'ruRU', 'Никто не убежит от бури.', '', 38134),
(193302, 'ruRU', 'Вы мне надоели!', '', 38134),
(193301, 'ruRU', 'Временная победа... над малой частью наших сил...', '', 38134),
(193300, 'ruRU', 'Жаль, что вы не увидите, как падет этот мир.', '', 38134),
(193299, 'ruRU', 'Было глупо бросать мне вызов.', '', 38134),
(193298, 'ruRU', 'Не стоило и пытаться.', '', 38134),
(193297, 'ruRU', 'Мы разрушим Бастион до основания!', '', 38134),
(193296, 'ruRU', 'Ха-ха! Тело уже компактное. Кожа уже зеленая. Да! Меньше работы!', '', 38134),
(193295, 'ruRU', 'Тельце щуплое, зато сколько энергии! О-о-о, превосходно...', '', 38134),
(193294, 'ruRU', 'Какая регенерация! О-о-о, мне это нужно!', '', 38134),
(193293, 'ruRU', 'Рога на голове. Неплохо, неплохо.', '', 38134),
(193292, 'ruRU', 'Я смотрю, разложение уже началось. Очень удобно!', '', 38134),
(193291, 'ruRU', 'Такие плечи – и всего одна пара рук.', '', 38134),
(193290, 'ruRU', 'Запчасти, которые превращаются в другие запчасти? Гениально!', '', 38134),
(193289, 'ruRU', 'Хвост и щупальца. Я знаю, что я с тобой сделаю.', '', 38134),
(193288, 'ruRU', 'Маленькие ручки, большие глаза. Да-да-да! Как раз для тонкой работы.', '', 38134),
(193287, 'ruRU', 'Какие чудесные уши. Такие здесь нелегко найти!', '', 38134),
(193286, 'ruRU', 'Какое крепкое тело. Для пехоты самое то.', '', 38134),
(193285, 'ruRU', 'Простенькая модель, зато с большим потенциалом!', '', 38134),
(193284, 'ruRU', 'Отойди, пока клыкастые не видят, что мы болтаем. Не буди лихо.', '', 38134),
(193282, 'ruRU', 'Я вижу в вас потенциал!', '', 38134),
(193281, 'ruRU', 'Мои творения разберутся с вами.', '', 38134),
(193280, 'ruRU', 'Идиот! Я твой создатель!', '', 38134),
(193279, 'ruRU', 'Он вам ужасно понравится.', '', 38134),
(193278, 'ruRU', 'Стежок за стежком!', '', 38134),
(193277, 'ruRU', '', 'Когда-нибудь мой бывший дом признает нас союзниками. До тех пор мы будем помогать им по мере сил.', 38134),
(193276, 'ruRU', 'Так ваши органы не испортятся!', '', 38134),
(193275, 'ruRU', 'Никакого уважения к искусству!', '', 38134),
(193274, 'ruRU', 'Это был мой любимчик!', '', 38134),
(193273, 'ruRU', '', 'Дозорные дома Ритуалов выискивают здесь наши тайны... но найдут только смерть. Позаботься об этом.', 38134),
(193272, 'ruRU', 'Из вас... получился бы... шедевр...', '', 38134),
(193271, 'ruRU', 'У меня годами столько свежих запчастей не было.', '', 38134),
(193270, 'ruRU', 'Сколько превосходных запчастей!', '', 38134),
(193269, 'ruRU', 'Скоро я пущу твой труп в дело!', '', 38134),
(193267, 'ruRU', 'Нет! Теперь придется заново их собирать! Видимо, все надо делать самому.', '', 38134),
(193266, 'ruRU', 'Эй, ты что, живчик? У тебя уже есть камень грехов или ты его пока только заполняешь?', '', 38134),
(193265, 'ruRU', 'Много еще вас, живчиков, из ворот придет? Клыкастые хотят больше душ, но они предпочитают мертвых.', '', 38134),
(193264, 'ruRU', 'Если кто-то увидит, что я болтаю вместо работы, меня сожрут или отправят в Топь. Не подставляй меня.', '', 38134),
(193263, 'ruRU', 'Не беси клыкастых. Они нервные, потому что главный босс до них докапывается.', '', 38134),
(193262, 'ruRU', 'У меня хорошая работа. Не мешай.', '', 38134),
(193261, 'ruRU', 'Боссы не должны видеть, что мы треплемся. Не хочу в Топь!', '', 38134),
(193260, 'ruRU', 'Эти души так близки к окончательному приговору, что от них все время отделяются все более и более опасные воплощения. \n\nЧтобы собрать аниму для Владыки и Казначея, нам нужно больше душ. Свежих душ.', 'Эти души так близки к окончательному приговору, что от них все время отделяются все более и более опасные воплощения. \n\nЧтобы собрать аниму для Владыки и Казначея, нам нужно больше душ. Свежих душ.', 38134),
(193258, 'ruRU', 'По приказу Казначея все вентиры этого предела должны платить подать анимой. Неплательщиков ждут наказания вплоть до ссылки в Пепельный Предел. \n\nНе препятствуйте сборщикам податей.', '', 38134),
(193256, 'ruRU', '', 'Трус! Я вышвырну твой труп прочь из моего храма!', 38134),
(193255, 'ruRU', 'Помощники! Сырье прибыло – пора обрабатывать!', '', 38134),
(193254, 'ruRU', '', 'Довольно прелюдий! Сразитесь со мной, и я вас наконец-то сокрушу!', 38134),
(193253, 'ruRU', '', 'Твое появление дарит нам надежду.\n\nНам почти нечего тебе предложить, но бери все, что захочешь. Мы восстановим Арденвельд.', 38134),
(193252, 'ruRU', '', 'Что за отребье? Где настоящие враги?!', 38134),
(193251, 'ruRU', '', 'Резня продолжается!', 38134),
(193250, 'ruRU', '', 'Все, кто встанет у меня на пути, погибнут!', 38134),
(193249, 'ruRU', '', 'Жаль, мы могли бы стать друзьями.', 38134),
(193248, 'ruRU', '', 'Вычеркиваю еще одно имя.', 38134),
(193246, 'ruRU', '', 'Вот список и стал короче.', 38134),
(193245, 'ruRU', '', 'Скажи, когда приготовишься. Решайся быстрее, не трать зря мое время.', 38134),
(193243, 'ruRU', 'Принц Ренатал открыл Пепельный двор для гостей, чтобы произвести впечатление на влиятельных обитателей Темных Земель.\n\nЕсли наши гости уйдут недовольными, значит, мы не справились с поставленной задачей... А это не подобает глашатаю вечеринки.\n\nК тому же перед уходом каждый гость оставит свой дар.\n\nЧем больше гостям у нас понравится, тем больше даров они оставят!', '', 38134),
(193240, 'ruRU', 'Разные гости предпочитают разную атмосферу приема. Кому-то нравятся изысканные и церемониальные мероприятия, а кто-то лучше себя чувствует в неформальной или даже напряженной обстановке.\n\nКак организаторы приема, мы должны устроить все так, чтобы создать атмосферу, которая придется по вкусу нашим гостям. Это можно сделать, предложив особую программу, например чаепитие или развлечение с музыкой, а также выполняя разные действия во время приема.\n\nПопробуй что-нибудь изменить и посмотри, что произойдет. Если тебе будет неясно, какая обстановка нравится приглашенному, проверь в списке гостей!', '', 38134),
(193239, 'ruRU', 'Вентиры часто устраивают изысканные приемы и экстравагантные вечеринки, чтобы произвести впечатление на возможных союзников и повысить свое влияние в Ревендрете.\n\nПоэтому принц Ренатал и собирается провести прием при Пепельном Дворе на вершине Грехопада: он хочет расширить влияние нашего ковенанта.\n\nСклонив на свою сторону важных персон из Ревендрета и всех Темных Земель, мы станем самой влиятельной силой.\n\nКонечно же, гости у нас будут самые разные, и всех задобрить будет непросто.', '', 38134),
(193233, 'ruRU', 'Не мешайте актерам репетировать!', 'Не мешайте актерам репетировать!', 38134),
(193220, 'ruRU', 'Находиться где-нибудь под крышей гораздо приятнее.', '', 38134),
(193216, 'ruRU', '', 'Шпили повинуются мне!', 38134),
(193215, 'ruRU', '', 'Смерть ждет вас среди облаков!', 38134),
(193214, 'ruRU', '', 'Ветра завывают от ярости!', 38134),
(193213, 'ruRU', '', 'Прочь отсюда!', 38134),
(193212, 'ruRU', '', 'Нет! Мы должны... сойти... с пути...', 38134),
(193210, 'ruRU', '', 'С бездарным правлением Архонта покончено.', 38134),
(193209, 'ruRU', '', 'Все, кто служит кириям, падут!', 38134),
(193208, 'ruRU', '', 'Твоя судьба решена.', 38134),
(193207, 'ruRU', '', 'Близится эпоха раскольников!', 38134),
(193202, 'ruRU', 'Цыц, глупый лоа. Бвонсамди знает, что делать с этими ничтожествами.', '', 38134),
(193201, 'ruRU', 'Надо же, кто вернулся в свой некрополь. Небось боишься, что мои слуги его разнесут?', '', 38134),
(193178, 'ruRU', '', 'Маркграфу несдобровать!', 38134),
(193164, 'ruRU', '', 'Поверить не могу, что Эмени снова с нами!', 38134),
(193154, 'ruRU', '', 'Туда тебе и дорога. С такими лучше не связываться.', 38134),
(193151, 'ruRU', 'А я пришел сюда вовсе не к Королеве...', '', 38134),
(193150, 'ruRU', '', 'Королеву не интересуют твои \"сделки\", Бвонсамди.', 38134),
(193147, 'ruRU', 'Дом Кадавров так просто не отступит. Но мы изгоним их из Бастиона. Клянусь Светом.', '', 38134),
(193146, 'ruRU', '', 'Наконец-то тьма в моем храме рассеивается... Но боюсь, что отдыхать я буду недолго.', 38134),
(193144, 'ruRU', 'К чему все это насилие?', 'К чему все это насилие?', 38134),
(193143, 'ruRU', '', 'Небеса чисты! Присоединяйся ко мне на западной площадке. Пора воздать им по заслугам.', 38134),
(193140, 'ruRU', 'Кирии падут!', '', 38134),
(193138, 'ruRU', '', 'Воздух наполнится ядом... а твои легкие – кровью!', 38134),
(193136, 'ruRU', '', 'Мы отравим небеса!', 38134),
(193131, 'ruRU', 'Костеклык сотрет вас в порошок!', '', 38134),
(193128, 'ruRU', 'Кажется, они создают новое тело.', 'Кажется, они создают новое тело.', 38134),
(193126, 'ruRU', 'Послужите мне еще раз!', '', 38134),
(193125, 'ruRU', 'Восстаньте и служите мне снова!', '', 38134),
(193121, 'ruRU', 'Нас всех... используют...', '', 38134),
(193120, 'ruRU', 'Еще больше свежих трупов. Налтор будет доволен.', '', 38134),
(193119, 'ruRU', 'Да, ты мне пригодишься.', '', 38134),
(193115, 'ruRU', 'Пополнение для моей армии.', '', 38134),
(193111, 'ruRU', 'Ваши усилия тщетны. Моим слугам нет числа!', '', 38134),
(193110, 'ruRU', 'Что-то... будет...', '', 38134),
(193109, 'ruRU', 'Чумокост... не смог...', '', 38134),
(193102, 'ruRU', '', 'Очисти небеса. Как закончишь, встретимся на западной площадке.', 38134),
(193101, 'ruRU', 'Хозяин гордится Чумокостом.', '', 38134),
(193100, 'ruRU', 'Слабая мелюзга!', '', 38134),
(193099, 'ruRU', 'Разбить! Сломать!', '', 38134),
(193098, 'ruRU', 'Чумокост порвет вас!', '', 38134),
(193094, 'ruRU', 'А сейчас – экзотические грибы со всех Темных Земель!', '', 38134),
(193093, 'ruRU', 'Звучит не очень...', '', 38134),
(193092, 'ruRU', 'Это правда, Бастион – самое изысканное из всех царств.', '', 38134),
(193091, 'ruRU', '', 'Устроить беспорядок? Я за!', 38134),
(193090, 'ruRU', 'Возьмите у землероев салфетки. Иначе можно ненароком устроить беспорядок.', 'Возьмите у землероев салфетки. Иначе можно ненароком устроить беспорядок.', 38134),
(193089, 'ruRU', 'Эти чудесные лакомства покорят вас своим деликатным нежным вкусом.', '', 38134),
(193088, 'ruRU', 'Божественные десерты самой изысканной кухни Бастиона!', '', 38134),
(193087, 'ruRU', '', 'Она получит новое тело кадавра!', 38134),
(193086, 'ruRU', 'Вам меня не одолеть!', '', 38134),
(193081, 'ruRU', '', 'Этих чудовищ не одолеть в одиночку. Очистите небеса... и предоставьте их судьбу мне.', 38134),
(193080, 'ruRU', 'Анимы у меня предостаточно!', '', 38134),
(193079, 'ruRU', 'Мы не подведем, идеал.', '', 38134),
(193078, 'ruRU', 'Анима переполняет меня!', '', 38134),
(193077, 'ruRU', '', 'Ковенант высоко о тебе отзывался. Не подведи меня.', 38134),
(193075, 'ruRU', '', 'Мое терпение кончилось. Хватит терять время.', 38134),
(193074, 'ruRU', '', 'Ваши некроманты окружили внутреннее святилище ядовитым облаком. Я не смогу войти внутрь.', 38134),
(193073, 'ruRU', 'Познайте бремя своих грехов.', '', 38134),
(193072, 'ruRU', 'Тебе нечем гордиться.', '', 38134),
(193071, 'ruRU', 'Тебя погубит гордыня!', '', 38134),
(193069, 'ruRU', 'Узрите силу вентиров!', '', 38134),
(193068, 'ruRU', 'Ничтожества!', '', 38134),
(193064, 'ruRU', 'У меня кончается терпение!', '', 38134),
(193063, 'ruRU', 'Презренная чернь!', '', 38134),
(193062, 'ruRU', 'Прочь с дороги.', '', 38134),
(193061, 'ruRU', 'Никчемные... слуги...', '', 38134),
(193060, 'ruRU', 'Никогда бы не подумал, что опущусь до такого.', '', 38134),
(193056, 'ruRU', 'И что в тебе нашел Владыка?', '', 38134),
(193055, 'ruRU', 'Как скучно.', '', 38134),
(193054, 'ruRU', 'Знай свое место!', '', 38134),
(193053, 'ruRU', 'Прочь, отребье!', '', 38134),
(193052, 'ruRU', 'Эта чернь мне надоела. Придется разобраться с ними самому.', '', 38134),
(193051, 'ruRU', '', '\"Божественный щит\" укроет тебя от темной магии алтаря.', 38134),
(193048, 'ruRU', 'Пошли-ка со мной в некрополь... а потом достанем кое-что для Королевы Зимы.', '', 38134),
(193021, 'ruRU', 'И у них будет такой шанс. Но мы не можем просто так напасть на дом Кадавров. Нужно подготовить войска.', '', 38134),
(193020, 'ruRU', '', 'Малдраксийцы разбиты, и теперь храм Отваги вернет свою силу. Мои сородичи уже рвутся отомстить дому Кадавров.', 38134),
(193018, 'ruRU', 'Я стану... еще сильнее...', '', 38134),
(193017, 'ruRU', 'Я сниму с тебя кожу!', '', 38134),
(193016, 'ruRU', 'Было время, когда я устраивал в этой башне замечательные вечера. Даже Владыка тогда улыбался и наслаждался каждым мгновением. На него было приятно смотреть, он был истинным правителем вентиров.\n\nИнтересно, как давно он строит козни вместе с Тюремщиком. Быть может, и та улыбка скрывала за собой ненависть ко всему бытию.', '', 38134),
(193015, 'ruRU', '', 'Что ж. Посмотрим, что скажет Ренатал. По крайней мере, я прекращу эту бессмысленную вражду, из-за которой мои дети дерутся друг с другом.', 38134),
(193014, 'ruRU', 'Мать камня, я служу Ревендрету. В такое время только Темный принц знает, как ему помочь. Владыка служит Утробе и Тюремщику – но пилигрим поможет нам остановить его.', '', 38134),
(193013, 'ruRU', '', 'Итак, Дрейвен, ты все еще служишь принцу Ренаталу, несмотря на все его ошибки.', 38134),
(193012, 'ruRU', '', 'Сдерживая свой гнев, тебе удалось пересечь мост и одолеть всех, кто преграждал путь. Это успех.\n\nНо больше я не позволю тебе войти в мои владения. Объясни мне причину своего вторжения.', 38134),
(193011, 'ruRU', 'Смотри по сторонам. Может, мы и прошли испытание, но камнерожденные при первом же случае попробуют снять с нас головы.', '', 38134),
(193005, 'ruRU', 'Я не хочу убивать тебя, сестра. Не вынуждай меня.', '', 38134),
(193004, 'ruRU', '', 'Если бы не Кааль, я бы давно тебя убила, предатель!', 38134),
(192996, 'ruRU', 'Если кому-то нужен особый транспорт, чтобы вернуться домой, мы его предоставим!', '', 38134),
(192984, 'ruRU', 'Я считаю, что дары, собранные в этот раз, должны достаться пилигриму. Пилигрим заслуживает награды за свой труд.', '', 38134),
(192983, 'ruRU', '', 'Прими свое наказание.', 38134),
(192982, 'ruRU', '', 'Их боль послужит вам уроком.', 38134),
(192980, 'ruRU', 'Со временем эти приемы помогут нам завоевать умы и сердца гостей со всех уголков Темных Земель!', '', 38134),
(192979, 'ruRU', '', 'Я нужна... своей пастве...', 38134),
(192978, 'ruRU', 'Благодарю всех вас за вклад в успех этого знакового события!', '', 38134),
(192975, 'ruRU', 'Вместе мы поведем Ревендрет и все Темные Земли в более справедливое, стабильное и благополучное будущее.', '', 38134),
(192974, 'ruRU', 'Знай, что в Грехопаде всегда будут рады тебе и твоим друзьям.', '', 38134),
(192972, 'ruRU', 'Этой неординарной атмосферой мы обязаны нашему зеркальных дел мастеру Лорану.', '', 38134),
(192971, 'ruRU', 'Погрузитесь в атмосферу воспоминаний. Хочу поблагодарить хранителя Та\'сарана и картель Та за гостеприимство!', '', 38134),
(192970, 'ruRU', 'Этой прекрасной традиционной обстановкой мы обязаны Темелю, Калошу и другим слугам. Благодарю вас!', '', 38134),
(192969, 'ruRU', 'Битва с оскверненными душами напомнила нам об истинном предназначении Ревендрета.', '', 38134),
(192968, 'ruRU', 'Лично мне не терпится переодеться после беспорядка, учиненного армией землероев!', '', 38134),
(192967, 'ruRU', 'И как можно забыть, как мы вместе отбились от армии камнерожденных Владыки?', '', 38134),
(192966, 'ruRU', 'И конечно, силы Владыки сегодня получили важный урок: Грехопад силен как никогда!', '', 38134),
(192964, 'ruRU', 'Завораживающий танец, вдохновленный музыкой труппы \"Потерянный Грааль\".', '', 38134),
(192963, 'ruRU', 'Изящные звери и забавно копошащиеся гормлинги из Арденвельда.', '', 38134),
(192962, 'ruRU', 'Воодушевляющие ритуалы от нашей неумолимой поборницы справедливости, Обвинительницы!', '', 38134),
(192961, 'ruRU', 'TBD-REfreshment4', '', 38134),
(192960, 'ruRU', 'Острые и необычайно освежающие грибы из Малдраксуса!', '', 38134),
(192959, 'ruRU', 'Веселое и спонтанное бросание едой за столом.', '', 38134),
(192958, 'ruRU', 'Успокаивающий чай от наших дорогих друзей Теотара и Таббинса!', '', 38134),
(192957, 'ruRU', '', 'Пусть ливень возмездия очистит эти земли!', 38134),
(192956, 'ruRU', 'Я наслаждался каждой минутой в твоем обществе.', '', 38134),
(192955, 'ruRU', 'Мои дорогие друзья, с грустью сообщаю вам, что наш прием подходит к концу.', '', 38134),
(192953, 'ruRU', 'Не болтай. Ни к чему раскрывать наши доходы. Все понятно?', 'Не болтай. Ни к чему раскрывать наши доходы. Все понятно?', 38134),
(192951, 'ruRU', 'Я поговорю с Та\'сараном. Его картель может помочь.', 'Я поговорю с Та\'сараном. Его картель может помочь.', 38134),
(192950, 'ruRU', 'Ба\'соло, мы должны расширить сеть.', 'Ба\'соло, мы должны расширить сеть.', 38134),
(192949, 'ruRU', 'Вынужден сообщить, что наш портал едва выдерживает нужды смертных.', 'Вынуждена сообщить, что наш портал едва выдерживает нужды смертных.', 38134),
(192944, 'ruRU', 'Это ваша проницательность превращает их желания в доходы.', 'Это ваша проницательность превращает их желания в доходы.', 38134),
(192943, 'ruRU', 'Мы должны поблагодарить их за такие ненасытные аппетиты.', 'Мы должны поблагодарить их за такие ненасытные аппетиты.', 38134),
(192942, 'ruRU', 'Все идет хорошо, ваше роскошество. Смертные продолжают потреблять.', 'Все идет хорошо, ваше роскошество. Смертные продолжают потреблять.', 38134),
(192941, 'ruRU', 'Как идут наши дела в кольце наверху?', 'Как идут наши дела в кольце наверху?', 38134),
(192940, 'ruRU', '', 'Гор\'грот говорил о защитных камнях. Найди их, и мы освободим капитана.', 38134),
(192934, 'ruRU', 'Надо каким-то образом их утихомирить. Ничто не должно помешать нашему плану.', 'Надо каким-то образом их утихомирить. Ничто не должно помешать нашему плану.', 38134),
(192931, 'ruRU', 'Ваше роскошество. Вынужден доложить, что некоторых смотрителей начинает беспокоить наша деятельность.', 'Ваше роскошество. Вынуждена доложить, что некоторых смотрителей начинает беспокоить наша деятельность.', 38134),
(192930, 'ruRU', '', 'Теперь вы – часть моей общины.', 38134),
(192929, 'ruRU', 'Конечно, ваше роскошество.', 'Конечно, ваше роскошество.', 38134),
(192928, 'ruRU', 'Моя прибыль – прибыль для всех. Каждому согласно его положению.', 'Моя прибыль – прибыль для всех. Каждому согласно его положению.', 38134),
(192927, 'ruRU', 'Мы еще сможем извлечь отсюда выгоду.', 'Мы еще сможем извлечь отсюда выгоду.', 38134),
(192926, 'ruRU', 'Долгожданные новости, хотя меньшего я и не ожидал.', 'Долгожданные новости, хотя меньшего я и не ожидала.', 38134),
(192925, 'ruRU', 'Ваше роскошество, я счастлив доложить, что все идет по плану.', 'Ваше роскошество, я счастлива доложить, что все идет по плану.', 38134),
(192924, 'ruRU', '', 'Тебе нет прощения.', 38134),
(192923, 'ruRU', '', 'Ты тоже очистишься!', 38134),
(192922, 'ruRU', 'Глашатай вечеринки снова за свое!', '', 38134),
(192921, 'ruRU', '', 'Ваше покаяние близко!', 38134),
(192919, 'ruRU', 'Кажется, теперь все спокойно. Давай вернемся к идеалу.', '', 38134),
(192918, 'ruRU', 'Отпусти своего питомца, прежде чем приручать другого. Потом ты сможешь призвать его обратно.', '', 38134),
(192917, 'ruRU', 'Оплот рядом... и его охрана тоже. Разумнее будет позвать на помощь идеала.', '', 38134),
(192916, 'ruRU', 'Не... может... быть...', '', 38134),
(192908, 'ruRU', 'Его никто не охраняет? Будь начеку...', '', 38134),
(192906, 'ruRU', 'Засада! Перебей их!', '', 38134),
(192902, 'ruRU', '', 'Кричи, если надо будет тебя спасать.', 38134),
(192898, 'ruRU', 'Земля кровоточит!', '', 38134),
(192897, 'ruRU', 'Кровь поглотит вас!', '', 38134),
(192896, 'ruRU', 'Тоните в крови!', '', 38134),
(192895, 'ruRU', 'Замри, смертное существо!', '', 38134),
(192894, 'ruRU', 'Плоть слаба!', '', 38134),
(192893, 'ruRU', 'Ваша судьба высечена в камне!', '', 38134),
(192892, 'ruRU', 'Повинуйтесь, мои прислужники!', '', 38134),
(192889, 'ruRU', 'О боги! Эти спригганы устраивают такой разгром!', 'О боги! Эти спригганы устраивают такой разгром!', 38134),
(192887, 'ruRU', '', 'Я чувствую твою зависть.', 38134),
(192885, 'ruRU', '', 'Ты говоришь с принцессой!', 38134),
(192884, 'ruRU', 'Изумительно! У тебя природный талант. Надо только над прочностью поработать...', '', 38134),
(192877, 'ruRU', 'Мы же партнеры по картелю. Или, как сказали бы смертные... одна семья.', 'Мы же партнеры по картелю. Или, как сказали бы смертные... одна семья.', 38134),
(192876, 'ruRU', 'Напомните мне. Почему мы его вечно прикрываем?', 'Напомните мне. Почему мы его вечно прикрываем?', 38134),
(192875, 'ruRU', 'Конечно, матриарх.', 'Конечно, матриарх.', 38134),
(192874, 'ruRU', 'В таком случае мне пора. Дайте мне знать, когда он объявится.', 'В таком случае мне пора. Дайте мне знать, когда он объявится.', 38134),
(192873, 'ruRU', 'Никогда.', 'Никогда.', 38134),
(192872, 'ruRU', 'Мы бы так никогда не поступили, матриарх.', 'Мы бы так никогда не поступили, матриарх.', 38134),
(192871, 'ruRU', 'Надеюсь, вы не пытаетесь его выгородить!', 'Надеюсь, вы не пытаетесь его выгородить!', 38134),
(192870, 'ruRU', 'И не я, матриарх.', 'И не я, матриарх.', 38134),
(192869, 'ruRU', 'Нет, матриарх. Не я.', 'Нет, матриарх. Не я.', 38134),
(192868, 'ruRU', 'Приберегите свои любезности. Кто-нибудь из вас видел Та\'вини?', 'Приберегите свои любезности. Кто-нибудь из вас видел Та\'вини?', 38134),
(192867, 'ruRU', 'Матриарх Та\'долла. Добро пожаловать!', 'Матриарх Та\'долла. Добро пожаловать!', 38134),
(192854, 'ruRU', '', 'Ты всегда была занозой у меня в пятке. Но мне это даже нравилось.', 38134),
(192853, 'ruRU', '', 'Леди Лунная Ягода, вы меня с кем-то спутали!', 38134),
(192851, 'ruRU', '', 'Призови меня, если не сможешь одолеть малдраксийскую армию своими силами. Я покончу с ними сама.', 38134),
(192850, 'ruRU', 'Такой прием мне нравится!', '', 38134),
(192849, 'ruRU', '', 'Да, отпразднуем, выполнив свой священный долг – беспристрастно судить вверенные нам заблудшие души.', 38134),
(192848, 'ruRU', 'О... Ну, что тут скажешь? Хорошего понемногу, я всегда это говорил.', '', 38134),
(192847, 'ruRU', 'Да, насчет этого... Пилигрим Утробы решил не устраивать развлечения на этом приеме.', '', 38134),
(192846, 'ruRU', 'Отведи меня к сцене, давай еще немного развлечемся!', '', 38134),
(192845, 'ruRU', 'За победу Грехопада! За Темные Земли!', '', 38134),
(192844, 'ruRU', 'Враги отступают по всему фронту! Грехопад победил!', '', 38134),
(192842, 'ruRU', 'Наслаждайтесь этой импровизированной демонстрацией нашей боевой мощи.', '', 38134),
(192839, 'ruRU', 'Не бойтесь, дорогие гости. Мы разберемся с этими врагами.', '', 38134),
(192838, 'ruRU', 'Думаешь... я могу попросить у него автограф? Может быть, он научит меня своим охотничьим хитростям?', '', 38134),
(192837, 'ruRU', 'Это он! Вот он, Рогатый Охотник! Прямо там! Я буду молодцом, честно. Ничто не скроется от моего бдительного ока! Или от его! Его глаза тоже очень бдительные...', '', 38134),
(192836, 'ruRU', 'Так, Релдорн, держи себя в руках... Он совсем рядом... Только не оплошай...$b$bОй, я тебя не увидел! Что тебе нужно от Дикого Охотника? То есть Рогатого... Дикаря... Что тебе нужно?', '', 38134),
(192835, 'ruRU', 'Враг близко! Защищайте гостей любой ценой!', '', 38134),
(192833, 'ruRU', 'Пилигрим Утробы, ко мне! Вместе мы покончим с этой угрозой!', '', 38134),
(192832, 'ruRU', 'Ваш жалкий двор склонится перед Владыкой!', '', 38134),
(192829, 'ruRU', 'Там, за порослью... Нам нужно туда попасть. Семена помогут расчистить путь!', '', 38134),
(192828, 'ruRU', '', 'Я с удовольствием попробую этот ваш \"огненный\" чай. Звучит так... авангардно!', 38134),
(192827, 'ruRU', 'Пейте, друзья мои! Наш дражайший Теотар готовит лучший чай в Темных Землях!', '', 38134),
(192826, 'ruRU', 'Наш лучший рецепт. Чай с могучей анимой, которая защитит вас от грубого воздействия Света!', '', 38134),
(192825, 'ruRU', 'Я приготовил хитрый чай. На вкус – огонь!', '', 38134),
(192824, 'ruRU', 'Угощения не будет? Как необычно... Но, думаю, пилигрим знает, что делает.', '', 38134),
(192823, 'ruRU', 'И мы тоже.', '', 38134),
(192822, 'ruRU', 'Э-э-э... Вообще-то, мой принц... пилигрим Утробы решил, что сегодня угощений не будет.', '', 38134),
(192821, 'ruRU', '', 'Мы почти отбили храм, когда твои собратья привели подкрепление. Я не успокоюсь, пока не покончу с ними.', 38134),
(192820, 'ruRU', 'Дорогие гости, пожалуйте за мной к яствам. Сегодня мы приготовили для вас нечто особенное!', '', 38134),
(192819, 'ruRU', '', 'Как здорово! Давай покидаем еще!', 38134),
(192818, 'ruRU', 'Я тут... переделал свой чудесный зонтик, чтобы он поглощал аниму. Ты не хочешь его опробовать?', '', 38134),
(192817, 'ruRU', 'Скажи, |3-6($c), тебе не кажется, что этот разведчик немного... странный? Он не сказал ни слова с тех пор, как я сюда пришел.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192816, 'ruRU', 'Итак, Темный принц готовится узурпировать престол? Полагаю, какой-нибудь герой должен его остановить.', '', 38134),
(192815, 'ruRU', 'Что это?', '', 38134),
(192812, 'ruRU', 'Никогда. И все-таки он один из нас.', 'Никогда. И все-таки он один из нас.', 38134),
(192811, 'ruRU', 'Он тебя когда-нибудь слушал?', 'Он тебя когда-нибудь слушал?', 38134),
(192809, 'ruRU', 'И не лезь в мои порталы!', 'И не лезь в мои порталы!', 38134),
(192808, 'ruRU', 'Не тот портал! Отправь меня обратно. Если матриарх спросит – вы меня не видели!', 'Не тот портал! Отправь меня обратно. Если матриарх спросит – вы меня не видели!', 38134),
(192807, 'ruRU', 'Опять, Та\'вини? Мы же договаривались.', 'Опять, Та\'вини? Мы же договаривались.', 38134),
(192806, 'ruRU', 'Та\'вини?', 'Та\'вини?', 38134),
(192804, 'ruRU', '', 'С этим порошком мы различим их иллюзии!', 38134),
(192803, 'ruRU', 'Ты и представить не можешь, что они оставляют в хранилище Бездны.', 'Ты и представить не можешь, что они оставляют в хранилище Бездны.', 38134),
(192802, 'ruRU', 'Эти смертные – поистине странные существа.', 'Эти смертные – поистине странные существа.', 38134),
(192801, 'ruRU', 'Смертным бывают нужны самые разные вещи. Вдруг они захотят себе такого питомца?', 'Смертным бывают нужны самые разные вещи. Вдруг они захотят себе такого питомца?', 38134),
(192800, 'ruRU', 'Разве эта тварь не из Малдраксуса? Зачем переносить ее сюда?', 'Разве эта тварь не из Малдраксуса? Зачем переносить ее сюда?', 38134),
(192799, 'ruRU', 'Тебе виднее, босс.', 'Тебе виднее, босс.', 38134),
(192798, 'ruRU', 'Не думаю. По сравнению с тем местом у нас они будут жить в роскоши.', 'Не думаю. По сравнению с тем местом у нас они будут жить в роскоши.', 38134),
(192797, 'ruRU', 'А вдруг они начнут скучать по плоти и слизи?', 'А вдруг они начнут скучать по плоти и слизи?', 38134),
(192796, 'ruRU', 'Мы оказываем этим существам услугу, забирая их оттуда.', 'Мы оказываем этим существам услугу, забирая их оттуда.', 38134),
(192795, 'ruRU', 'Это существо тоже из Малдраксуса. Никаких сомнений.', 'Это существо тоже из Малдраксуса. Никаких сомнений.', 38134),
(192794, 'ruRU', 'X', '', 38134),
(192792, 'ruRU', '', 'Тебе нет равных в Малдраксусе, пилигрим.', 38134),
(192791, 'ruRU', '', 'Мы дадим отпор! Малдраксус выстоит!', 38134),
(192790, 'ruRU', 'Бастион благодарит вас, ваша милость.', '', 38134),
(192787, 'ruRU', '', 'Я медитировала, вспоминая свою жизнь, и вспомнила нечто важное. Пусть эта запись служит мне напоминанием в грядущей вечности.', 38134),
(192786, 'ruRU', '', 'Я не была творцом, пока не попала в Бастион. Я не умела ничего, кроме как воевать. Да, я служила своему народу, но какой ценой?', 38134),
(192785, 'ruRU', '', 'Под конец своего жизненного пути я поняла, что ухожу, не дав ничего своему миру. Я несла только смерть.', 38134),
(192784, 'ruRU', '', 'Моя душа пробудилась в Бастионе. Я научилась работать в кузне. То, что двигало мной при жизни, обрело новый смысл. Я смогла создавать, а не разрушать.', 38134),
(192783, 'ruRU', 'Такая у меня работа. Я хочу, чтобы им было повеселее!', 'Такая у меня работа. Я хочу, чтобы им было повеселее!', 38134),
(192782, 'ruRU', 'А тебе не надоедает говорить одно и то же существам, которых ты рассовываешь по ящикам?', 'А тебе не надоедает говорить одно и то же существам, которых ты рассовываешь по ящикам?', 38134),
(192781, 'ruRU', 'Это уж точно. Доставь это в хранилище.', 'Это уж точно. Доставь это в хранилище.', 38134),
(192780, 'ruRU', 'И поесть они тоже любят.', 'И поесть они тоже любят.', 38134),
(192779, 'ruRU', 'Они во многом полагаются на ресурсы, добытые у различных существ.', 'Они во многом полагаются на ресурсы, добытые у различных существ.', 38134),
(192778, 'ruRU', 'Поразительно, со сколькими существами эти смертные имеют дело.', 'Поразительно, со сколькими существами эти смертные имеют дело.', 38134),
(192777, 'ruRU', 'Как интересно. Заполучить такую запись в архивы – большая удача. Спасибо.', '', 38134),
(192776, 'ruRU', 'Раскольники почти уничтожили наследие этого места. Найди записи, чтобы мы смогли их сохранить.', '', 38134),
(192775, 'ruRU', 'Найди меня в храме Отваги. Мы свершим правосудие.', '', 38134),
(192773, 'ruRU', 'Дом Кадавров направил в Бастион подкрепление. Мы должны помочь кириям отразить эту атаку.', '', 38134),
(192750, 'ruRU', '', 'Говорят, Владыка намеренно не дает Тихому Пределу выбраться из нищеты. Чтобы бывшие богачи томились при виде чужих богатств, не в силах получить их.\n\nОмерзительное место, хотя там можно найти много достойных противников.', 38134),
(192720, 'ruRU', 'Назад! Бежим!', 'Назад! Бежим!', 38134),
(192719, 'ruRU', 'Как скажешь, босс.', 'Как скажешь, босс.', 38134),
(192718, 'ruRU', 'Давай больше не будем ничего призывать из того мира!', 'Давай больше не будем ничего призывать из того мира!', 38134),
(192717, 'ruRU', 'Вот и все!', 'Вот и все!', 38134),
(192716, 'ruRU', 'СКОРЕЕ!', 'СКОРЕЕ!', 38134),
(192715, 'ruRU', 'Перенаправляю поток.', 'Перенаправляю поток.', 38134),
(192714, 'ruRU', 'ОТПРАВЬ ЕГО ОБРАТНО!', 'ОТПРАВЬ ЕГО ОБРАТНО!', 38134),
(192713, 'ruRU', 'Что это? Оно, кажется, опасное!', 'Что это? Оно, кажется, опасное!', 38134),
(192710, 'ruRU', 'Но думаю, и такой вариант подойдет. Упакуй его.', 'Но думаю, и такой вариант подойдет. Упакуй его.', 38134),
(192709, 'ruRU', 'Да, но не такого цвета. Похоже, у них бывают разные расцветки и количество ног!', 'Да, но не такого цвета. Похоже, у них бывают разные расцветки и количество ног!', 38134),
(192708, 'ruRU', 'Мне казалось, такое существо у нас уже было.', 'Мне казалось, такое существо у нас уже было.', 38134),
(192707, 'ruRU', 'Еще одно с восемью ногами! Правда, такого сочетания я еще не видел.', 'Еще одно с восемью ногами! Правда, такого сочетания я еще не видела.', 38134),
(192702, 'ruRU', 'Уничтожить наглецов!', '', 38134),
(192701, 'ruRU', 'Ко мне!', '', 38134),
(192700, 'ruRU', 'Прах к праху!', '', 38134),
(192699, 'ruRU', 'Всех раздавлю!', '', 38134),
(192698, 'ruRU', '', 'Прекрасно, давай еще раз. Я должна убедиться, что ты выстоишь против свинобразов.', 38134),
(192695, 'ruRU', '', 'Прекрасно. Давай еще раз. С помощью \"Лунного огня\" и \"Гнева\" ты разберешься со свинобразами.', 38134),
(192692, 'ruRU', 'Будет сделано.', 'Будет сделано.', 38134),
(192691, 'ruRU', 'Это отличный образец. Позаботься о нем хорошенько.', 'Это отличный образец. Позаботься о нем хорошенько.', 38134),
(192690, 'ruRU', '', 'Хорошо, давай еще раз! \"Слово Тьмы: Боль\" продолжит наносить урон врагу, пока ты атакуешь его с помощью \"Кары\".', 38134),
(192688, 'ruRU', 'Полезай в ящик, дорогуша. Не волнуйся, внутри он гораздо больше.', 'Полезай в ящик, дорогуша. Не волнуйся, внутри он гораздо больше.', 38134),
(192686, 'ruRU', 'А оно очень милое!', 'А оно очень милое!', 38134),
(192685, 'ruRU', '', 'Его гордыне и высокомерию просто нет равных. Идем. Лишим Денатрия его добычи.', 38134),
(192680, 'ruRU', 'Смерть с небес!', '', 38134),
(192679, 'ruRU', 'Ничтожества.', '', 38134),
(192678, 'ruRU', 'Ожидаемый итог.', '', 38134),
(192677, 'ruRU', 'У тебя не было шансов.', '', 38134),
(192676, 'ruRU', 'Наконец-то помолчишь.', '', 38134),
(192675, 'ruRU', 'Прочь, презренные мятежники!', '', 38134),
(192673, 'ruRU', '', 'Здравствуй. Меня зовут Сорча.$b$bПо тебе видно, что ты часто сражаешься. Это хорошо. Нам предстоит еще много сражений.', 38134),
(192672, 'ruRU', 'Ой...', '', 38134),
(192671, 'ruRU', 'То, что случилось в Бастионе, позорит нашу честь. Мы должны исправить положение.', '', 38134),
(192670, 'ruRU', '', 'Я пришла сюда, чтобы попросить у Владычицы Водопадов новое оружие. Почти все наши инструменты, оружие и припасы приходят из Тирна Эаса. Ресурсов здесь явно предостаточно.$b$bБыть может, тебе удастся чего-то от нее добиться.', 38134),
(192669, 'ruRU', 'Войска дома Кадавров продолжают прибывать в храм Отваги. Прошу, помоги кириям истребить их.', '', 38134),
(192667, 'ruRU', '', 'Помоги мне достучаться до дромана. Она бездействует, пока другие страдают!', 38134),
(192619, 'ruRU', '', 'Надо помнить, что это истинное предназначение Ревендрета!', 38134),
(192618, 'ruRU', '', 'Вместе мы поможем всем желающим душам искупить грехи.', 38134),
(192617, 'ruRU', '', 'Спасибо всем, что вы были свидетелями обвинения!', 38134),
(192616, 'ruRU', 'Многим в лесу приходится несладко. Но я должен присматривать за этими малышами.$b$bЕсли тебе нужно купить что-то еще, обратись к Дже\'ни и ребятишкам на другой стороне рощи.', '', 38134),
(192615, 'ruRU', 'Добро пожаловать! Меня зовут Кавина, я лучший гормастер во всем Арденвельде.$b$bВозможно, я еще и последний гормастер, но, клянусь королевой, все равно лучший!', '', 38134),
(192614, 'ruRU', 'С гормами очень приятно работать. Из них получаются хорошие материалы, верные спутники, отличная еда... Они годятся просто для всего!$b$bЛучшие доспехи в Арденвельде? Все из панцирей гормов!$b$bОстрые и прочные клинки? Тут пригодятся их когти или жала!$b$bПрости, я немного увлекся. Просто я очень люблю свою работу.', '', 38134),
(192613, 'ruRU', '', 'Когда я освою азы магии, я хочу научиться управлять льдом! Вжух! Вжух!', 38134),
(192612, 'ruRU', '', '<Марастар переходит на громкий шепот.>Иногда мне кажется, что мои игрушки оживают, когда думают, что я их не вижу. Думаешь, это магия?', 38134),
(192611, 'ruRU', '', 'Иногда я мечтаю о жизни на морском дне. Но где там можно устроиться? И как там дышать?', 38134),
(192610, 'ruRU', '', '<Морская Арфа громко шепчет вам.> Эй! Не хочешь познакомиться с моим другом?$b$b<Морская Арфа показывает вам небольшую губку.>$b$bОн живет на дне океана. Его лучший друг – морская звезда!', 38134),
(192608, 'ruRU', '', 'Давай выясним, какое бремя этой души самое тяжкое... и избавим ее от него.', 38134),
(192607, 'ruRU', '', 'Пришли мне еще одну душу для обвинения!', 38134),
(192599, 'ruRU', 'Она говорила о невосполнимой утрате. О жизни, которую помнила теперь лишь по записям и книгам. Путь, говорила она, уничтожил ее суть!', '', 38134),
(192598, 'ruRU', 'Она уже почти прошла весь путь к перерождению. Оставался только мой храм, но не гордыня сразила ее.', '', 38134),
(192597, 'ruRU', 'Сегодня я потерял претендента. Я не тешу свое самолюбие, воображая, будто изменить его судьбу было в моей власти, но все же его потеря стала для меня неожиданной... и горькой.', '', 38134),
(192596, 'ruRU', 'Мне требуется о многом поразмышлять, и мой добрый друг Тениос пригласил меня сюда. Поможешь ли ты, милый пергамент, облегчить мою тяжелую ношу?', '', 38134),
(192590, 'ruRU', '', 'О, привет! Я говорила с Тирна Эасом.$b$bНичего, продолжай. Ты нам не мешаешь. Наш разговор – он на пару тысяч лет, не меньше. Мы уже давно не беседовали.', 38134),
(192589, 'ruRU', '', 'Один лес, другой лес. Какие странные условности. Мы все листья в одном великом лесу.', 38134),
(192588, 'ruRU', '', 'Мы снова встретились, пилигрим Утробы. И надеюсь, не в последний раз.', 38134),
(192586, 'ruRU', '', 'Как же хорошо снова взяться за работу. Без всех этих спригганов, гогочущих за дверью, и зловещих воплощений по углам...', 38134),
(192585, 'ruRU', '', 'Это не моя мастерская, но кое-что я тут сделать могу. Сети душ сами себя не изготовят, знаешь ли!', 38134),
(192584, 'ruRU', '', 'Еще раз спасибо за помощь в Лесной мастерской. Если бы не ты, мы бы до сих пор там торчали!', 38134),
(192581, 'ruRU', '', 'Начнем?', 38134),
(192580, 'ruRU', 'Должен сказать, тут куда лучше, чем в моей мастерской. Эти мерзкие спригганы жутко мешали!$b$bДа, тут тихо и спокойно. Наконец-то я слышу удары моего счастливого молота!', '', 38134),
(192579, 'ruRU', 'После того как тебе удалось найти мой молот, все пошло на лад. Это большая удача, согласись!', '', 38134),
(192578, 'ruRU', 'О, посетитель... Это опять ты. Мне повезло!', '', 38134),
(192577, 'ruRU', '', 'Здравствуй, дорогуша. Как идут дела за пределами моей рощи?', 38134),
(192576, 'ruRU', '', 'Я понимаю, что для всех в Арденвельде настали тяжелые времена. Но мне было велено охранять эту рощу, и я обеспечу безопасность всем ее обитателям.', 38134),
(192575, 'ruRU', '', 'Добро пожаловать в низину Мерцающих Каскадов.$b$bОтдыхай спокойно. Здесь тебе ничто не угрожает.', 38134),
(192574, 'ruRU', '', 'Смотри, как он сияет! Пусть его голос поразит монстра, созданного оскверненным колоколом!', 38134),
(192573, 'ruRU', '', 'Для тех, кто стремится к очищению, звуки колокола – благословение. Но тех, кто сбился с пути, его голос повергает в страх. Мы хорошо их проучили.', 38134),
(192572, 'ruRU', '', 'Раскольники снова осквернили самый могучий из моих колоколов. Я дарую тебе силу колокола поменьше. Дай им отпор. Пусть наши враги узрят истинную чистоту!', 38134),
(192567, 'ruRU', 'Вы видите мыло леди Лунной Ягоды на столе.', 'Вы видите мыло леди Лунной Ягоды на столе.', 38134),
(192566, 'ruRU', 'Не мешать!', '', 38134),
(192565, 'ruRU', 'Прочь!', '', 38134),
(192564, 'ruRU', 'Заражение!', '', 38134),
(192563, 'ruRU', 'Зараза расползается!', '', 38134),
(192562, 'ruRU', '', 'Вам... никогда... не победить...', 38134),
(192561, 'ruRU', '', 'Бастион станет нашим!', 38134),
(192548, 'ruRU', '', 'Приглашение от принца Ренатала? Чудно. Я ни за что этого не пропущу.', 38134),
(192545, 'ruRU', '', 'Вас поглотит кровавая бездна!', 38134),
(192544, 'ruRU', '', 'Плоть слабее камня!', 38134),
(192543, 'ruRU', '', 'Из Кровавых катакомб никто не сбегал. Никогда.', 38134),
(192542, 'ruRU', '', 'Восстание провалилось.', 38134),
(192541, 'ruRU', '', 'Верните пленницу!', 38134),
(192538, 'ruRU', 'Извини, смертное существо. Мы заняты – следим за потоком душ.', '', 38134),
(192537, 'ruRU', 'А теперь сделай, как надо.', '', 38134),
(192536, 'ruRU', 'Надо выполнить план!', '', 38134),
(192534, 'ruRU', 'Эй, ты! Ну-ка, поднажми!', '', 38134),
(192533, 'ruRU', 'Мы должны не думать, а вкалывать!', '', 38134),
(192532, 'ruRU', 'Не вопрос! Землерои умеют веселиться!', '', 38134),
(192531, 'ruRU', '', 'Ее гримерка вон там.', 38134),
(192530, 'ruRU', '', 'Давай разыграем Лунную Ягоду. Не все же ей подшучивать над другими!', 38134),
(192529, 'ruRU', 'Кто-нибудь! Несите ведра с водой и потушите огонь!', '', 38134),
(192527, 'ruRU', 'Темель! Скорее неси ведра с водой и потуши огонь!', '', 38134),
(192526, 'ruRU', 'Ой... От свечек пожар!', '', 38134),
(192525, 'ruRU', '', 'Слишком рано. \"Лунный огонь\" нанесет больше урона, если обновить его, когда действие почти истекло.', 38134),
(192524, 'ruRU', '', '\"Лунный огонь\" слабеет, обнови его!', 38134),
(192521, 'ruRU', '', '\"Лунный огонь\" наносит периодический урон. Используй \"Гнев\", пока враг поражен \"Лунным огнем\".', 38134),
(192520, 'ruRU', '', 'Вот так! Продолжай использовать \"Гнев\", пока \"Лунный огонь\" наносит урон. В нужный момент снова примени \"Лунный огонь\".', 38134),
(192518, 'ruRU', '', 'Применяй \"Лунный огонь\" в начале боя. Он наносит периодический урон.', 38134),
(192517, 'ruRU', '', 'Слишком рано. \"Слово Тьмы: Боль\" нанесет больше урона, если обновить его, когда действие почти истекло.', 38134),
(192516, 'ruRU', '', 'Пора обновить \"Слово Тьмы: Боль\" на цели!', 38134),
(192514, 'ruRU', '', 'Браво! Давай еще разок! Искусный жрец может легко изматывать врагов с помощью \"Слова Тьмы: Боль\".', 38134),
(192512, 'ruRU', '', 'Хорошо! Продолжай использовать \"Кару\", пока \"Слово Тьмы: Боль\" наносит урон. В нужный момент снова примени \"Слово Тьмы: Боль\".', 38134),
(192511, 'ruRU', '', 'Используй \"Слово Тьмы: Боль\" в начале боя. Оно наносит периодический урон.', 38134),
(192464, 'ruRU', 'Эти кретины не служат Ревендрету. Спасибо тебе, $n.', '', 38134),
(192463, 'ruRU', 'Без Жуткой Охотницы здесь появились новые хозяева. Разберись с ними!', '', 38134),
(192446, 'ruRU', '', 'Мои... исследования...', 38134),
(192445, 'ruRU', '', 'Наконец-то. Итак, Зе\'рали... на чем мы остановились?', 38134),
(192444, 'ruRU', '', 'Ты мне точно не пригодишься.', 38134),
(192443, 'ruRU', '', 'Назойливая мошка.', 38134),
(192441, 'ruRU', '', 'Не смейте прерывать мои эксперименты!', 38134),
(192440, 'ruRU', '', 'Сначала поешь. Восстанови силы.', 38134),
(192430, 'ruRU', '', 'Нет... Жуткая Охотница верила мне...', 38134),
(192429, 'ruRU', '', 'Пойдешь на корм!', 38134),
