(192079, 'ruRU', 'Поймаю что-то вкусное!', '', 38134),
(192078, 'ruRU', 'Развалю на куски!', '', 38134),
(192073, 'ruRU', '', 'От меня никто не уходит.', 38134),
(192070, 'ruRU', '', 'Хватит одной царапины...', 38134),
(192068, 'ruRU', '', 'Я убью вас на месте!', 38134),
(192066, 'ruRU', '', 'Я разрублю вашу плоть!', 38134),
