INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Olerán a sus parientes cuando entres en sus pasillos, $n. ¡Aplastarlos con su propio miedo!', 0),
