INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Has estado en la Mina Dentefrío, $n? ¿Tienes los suministros que te pedí?', 0),
(@ID, 'esMX', '¿Has estado en la Mina Dentefrío, $n? ¿Tienes los suministros que te pedí?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ah, los tienes. ¡Buen trabajo, $n! Estos suministros están algo húmedos de estar guardados en esa oscura mina tanto tiempo pero les sacaremos partido igualmente.$B$BGracias, $n. Tus acciones son vitales para el mantenimiento de nuestras tropas.', 0),
(@ID, 'esMX', 'Ah, los tienes. ¡Buen trabajo, $n! Estos suministros están algo húmedos de estar guardados en esa oscura mina tanto tiempo pero les sacaremos partido igualmente.$B$BGracias, $n. Tus acciones son vitales para el mantenimiento de nuestras tropas.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(6983, 'esES', '¡$n, nuestro Festival de Invierno será un desastre sin esos regalitos que hay que reponer! El propio Graccu no podrá compartir sus deliciosas empanadas de carne con todos los niños y niñas esta temporada...', 0),
(7043, 'esES', '¡$n, nuestro Festival de Invierno será un desastre sin esos regalitos que hay que reponer! El propio Graccu no podrá compartir sus deliciosas empanadas de carne con todos los niños y niñas esta temporada...', 0),
(6983, 'esMX', '¡$n, nuestro Festival de Invierno será un desastre sin esos regalitos que hay que reponer! El propio Graccu no podrá compartir sus deliciosas empanadas de carne con todos los niños y niñas esta temporada...', 0),
(7043, 'esMX', '¡$n, nuestro Festival de Invierno será un desastre sin esos regalitos que hay que reponer! El propio Graccu no podrá compartir sus deliciosas empanadas de carne con todos los niños y niñas esta temporada...', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(6983, 'esES', '¡Lo encontraste! ¡Has salvado el Festival de Invierno de los Pastos de Bosquehumeante!$B$BPobre Sacks... nos aseguraremos de enviar un destacamento a por él y encontrar la forma de descongelarlo. Con suerte, volverá a tiempo para el día del Festival aunque tendrá que compensar el tiempo que no haya podido trabajar. Sin embargo, ¡esto no sería posible si no fuera por ti!', 0),
(7043, 'esES', '¡Lo encontraste! ¡Has salvado el Festival de Invierno de los Pastos de Bosquehumeante!$B$BPobre Sacks... nos aseguraremos de enviar un destacamento a por él y encontrar la forma de descongelarlo. Con suerte, volverá a tiempo para el día del Festival aunque tendrá que compensar el tiempo que no haya podido trabajar. Sin embargo, ¡esto no sería posible si no fuera por ti!', 0),
(6983, 'esMX', '¡Lo encontraste! ¡Has salvado el Festival de Invierno de los Pastos de Bosquehumeante!$B$BPobre Sacks... nos aseguraremos de enviar un destacamento a por él y encontrar la forma de descongelarlo. Con suerte, volverá a tiempo para el día del Festival aunque tendrá que compensar el tiempo que no haya podido trabajar. Sin embargo, ¡esto no sería posible si no fuera por ti!', 0),
(7043, 'esMX', '¡Lo encontraste! ¡Has salvado el Festival de Invierno de los Pastos de Bosquehumeante!$B$BPobre Sacks... nos aseguraremos de enviar un destacamento a por él y encontrar la forma de descongelarlo. Con suerte, volverá a tiempo para el día del Festival aunque tendrá que compensar el tiempo que no haya podido trabajar. Sin embargo, ¡esto no sería posible si no fuera por ti!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(6984, 'esES', 'Jo jo... ¡Saludos, $gaventurero:aventurera;!$B$BLos goblins me han dicho que te has ganado un regalito especial. Aquí tienes. ¡Feliz Festival de Invierno!', 0),
(7045, 'esES', 'Jo jo... ¡Saludos, $gaventurero:aventurera;!$B$BLos goblins me han dicho que te has ganado un regalito especial. Aquí tienes. ¡Feliz Festival de Invierno!', 0),
(6984, 'esMX', 'Jo jo... ¡Saludos, $gaventurero:aventurera;!$B$BLos goblins me han dicho que te has ganado un regalito especial. Aquí tienes. ¡Feliz Festival de Invierno!', 0),
(7045, 'esMX', 'Jo jo... ¡Saludos, $gaventurero:aventurera;!$B$BLos goblins me han dicho que te has ganado un regalito especial. Aquí tienes. ¡Feliz Festival de Invierno!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Entraste la Mina Ferrohondo, $n? ¿Saqueastes sus suministros?', 0),
(@ID, 'esMX', '¿Entraste la Mina Ferrohondo, $n? ¿Saqueastes sus suministros?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Los tienes! ¡Bien hecho, $n! ¡Y espero que hayas visto a algunos enanos durante tu incursión y derrames su sangre por los Lobo Gélido!', 0),
(@ID, 'esMX', '¡Los tienes! ¡Bien hecho, $n! ¡Y espero que hayas visto a algunos enanos durante tu incursión y derrames su sangre por los Lobo Gélido!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Sin tu ayuda, los Jinetes de Lobos Lobo Gélido no tendrían monturas para la batalla. ¡Nos haces un gran servicio!', 0),
(@ID, 'esMX', 'Sin tu ayuda, los Jinetes de Lobos Lobo Gélido no tendrían monturas para la batalla. ¡Nos haces un gran servicio!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Solo espero que todos los que deben reunir pieles de carnero sean tan aplicados como tú, $n.$B$BA este paso, tendremos el Establo lleno ¡en cuestión de horas!', 0),
(@ID, 'esMX', 'Solo espero que todos los que deben reunir pieles de carnero sean tan aplicados como tú, $n.$B$BA este paso, tendremos el Establo lleno ¡en cuestión de horas!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Mientras algunos de nuestros soldados están ocupados en la captura de lobos para el maestro de establos, otros deben encargarse de las necesidades básicas de la monta. Me refiero a los arneses para montar, claro.$B$BDebes atacar a los carneros locales de la región. ¡Los mismísimos carneros que la caballería Pico Tormenta usa de montura!$B$BAcaba con ellos y tráeme sus pieles. Cuando tengamos suficientes pieles, confeccionaremos los arneses para los jinetes. ¡Los Caballeros Lobo de los Lobo Gélido volverán por sus fueros!', 0),
(@ID, 'esMX', 'Mientras algunos de nuestros soldados están ocupados en la captura de lobos para el maestro de establos, otros deben encargarse de las necesidades básicas de la monta. Me refiero a los arneses para montar, claro.$B$BDebes atacar a los carneros locales de la región. ¡Los mismísimos carneros que la caballería Pico Tormenta usa de montura!$B$BAcaba con ellos y tráeme sus pieles. Cuando tengamos suficientes pieles, confeccionaremos los arneses para los jinetes. ¡Los Caballeros Lobo de los Lobo Gélido volverán por sus fueros!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Muy bien, soldado. Te lo haré saber en cuanto tenga suficientes pieles acumuladas.$B$B¡Sigue así!', 0),
(@ID, 'esMX', 'Muy bien, soldado. Te lo haré saber en cuanto tenga suficientes pieles acumuladas.$B$B¡Sigue así!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡La Brigada Pico Tormenta no puede lanzarse a la batalla a pelo! Necesitamos arneses para nuestras monturas, después de todo, no somos salvajes.$B$BSi bien podríamos matar fácilmente a los carneros que merodean cerca de nuestra base por el cuero necesario para fabricar un arnés, hacerlo sería una idiotez. Necesitamos esos carneros como monturas.$B$BDebes atacar la base de nuestro enemigo, matando a los lobos de hielo que usan como monturas y tomando sus pieles. Devuélveme sus pieles para que le hagan arneses a la caballería. ¡Vamos!', 0),
(@ID, 'esMX', '¡La Brigada Pico Tormenta no puede lanzarse a la batalla a pelo! Necesitamos arneses para nuestras monturas, después de todo, no somos salvajes.$B$BSi bien podríamos matar fácilmente a los carneros que merodean cerca de nuestra base por el cuero necesario para fabricar un arnés, hacerlo sería una idiotez. Necesitamos esos carneros como monturas.$B$BDebes atacar la base de nuestro enemigo, matando a los lobos de hielo que usan como monturas y tomando sus pieles. Devuélveme sus pieles para que le hagan arneses a la caballería. ¡Vamos!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Bien hecho, soldado. Te avisaré cuando haya almacenado suficientes pieles.', 0),
(@ID, 'esMX', 'Bien hecho, soldado. Te avisaré cuando haya almacenado suficientes pieles.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'El residuo de miniaturización es una materia valiosísima en ingeniería, te lo aseguro. Y lo es porque sirve para asegurarse de que el controlador no hace algo tan decepcionante como explotar en la cara del usuario.$B$BOh, no te preocupes, me refería a otros controladores, ¡no al que te di a ti, claro está!', 0),
(@ID, 'esMX', 'El residuo de miniaturización es una materia valiosísima en ingeniería, te lo aseguro. Y lo es porque sirve para asegurarse de que el controlador no hace algo tan decepcionante como explotar en la cara del usuario.$B$BOh, no te preocupes, me refería a otros controladores, ¡no al que te di a ti, claro está!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Espléndidamente espléndido! Me alegra ver que has vuelto con el residuo, ¡y con todos tus apéndices intactos, cabeza y todo! Eso está muy bien... no es que el Ultra-reductor fuera peligroso, pero, oye...$B$BAh, y aprecio mucho tu ayuda, $n. De verdad, eres $gun buen amigo:una buena amiga; de los goblins de todo el mundo. Acepta esto como muestra de mi agradecimiento.', 0),
(@ID, 'esMX', '¡Espléndidamente espléndido! Me alegra ver que has vuelto con el residuo, ¡y con todos tus apéndices intactos, cabeza y todo! Eso está muy bien... no es que el Ultra-reductor fuera peligroso, pero, oye...$B$BAh, y aprecio mucho tu ayuda, $n. De verdad, eres $gun buen amigo:una buena amiga; de los goblins de todo el mundo. Acepta esto como muestra de mi agradecimiento.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Sin tu ayuda, los Jinetes de Carnero de los Pico Tormenta no tendrían monturas para la batalla. ¡Nos haces un gran servicio!', 0),
(@ID, 'esMX', 'Sin tu ayuda, los Jinetes de Carnero de los Pico Tormenta no tendrían monturas para la batalla. ¡Nos haces un gran servicio!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'El Establo estará lleno muy pronto. ¡Sigue con tu buen trabajo!', 0),
(@ID, 'esMX', 'El Establo estará lleno muy pronto. ¡Sigue con tu buen trabajo!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'SI consigues curar a la planta, volverá a crecer. Tenemos que hacer todo lo que podamos, $n...', 0),
(@ID, 'esMX', 'SI consigues curar a la planta, volverá a crecer. Tenemos que hacer todo lo que podamos, $n...', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Gracias, $n; seguiré investigando con los restos de líquido.$B$BAunque queda mucho por hacer, has contribuido a limpiar parte de la corrupción de Lenguavil en Maraudon. Toma esto como muestra de mi gratitud.', 0),
(@ID, 'esMX', 'Gracias, $n; seguiré investigando con los restos de líquido.$B$BAunque queda mucho por hacer, has contribuido a limpiar parte de la corrupción de Lenguavil en Maraudon. Toma esto como muestra de mi gratitud.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'He despertado de una pesadilla interminable...', 0),
(@ID, 'esMX', 'He despertado de una pesadilla interminable...', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ahora que me has traído las piezas del cetro, podremos limpiar Maraudon de la corrupcíón de Lenguavil.', 0),
(@ID, 'esMX', 'Ahora que me has traído las piezas del cetro, podremos limpiar Maraudon de la corrupcíón de Lenguavil.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Si deseas reiniciar el ritual, abandona nuestro primer intento y luego vuelve a hablarme.', 0),
(@ID, 'esMX', 'Si deseas reiniciar el ritual, abandona nuestro primer intento y luego vuelve a hablarme.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ten; debes usar el poder que te ha sido concedido siempre para hacer el bien. Sigue explorando las cavernas, a ver si encuentras los restos de mi tío. Te esperan grandes peligros, $n.$B$BVuelve a hablar conmigo si lo necesitas.', 0),
(@ID, 'esMX', 'Ten; debes usar el poder que te ha sido concedido siempre para hacer el bien. Sigue explorando las cavernas, a ver si encuentras los restos de mi tío. Te esperan grandes peligros, $n.$B$BVuelve a hablar conmigo si lo necesitas.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Te doy la bienvenida a Mulgore ya nuestra humilde comunidad de Cima del Trueno, $gamigo:amiga;. Me agrada la gente que se interesa por el reino, sus gentes y sus tradiciones. ¿Es esa la razón por la que estás aquí?', 0),
(@ID, 'esMX', 'Te doy la bienvenida a Mulgore ya nuestra humilde comunidad de Cima del Trueno, $gamigo:amiga;. Me agrada la gente que se interesa por el reino, sus gentes y sus tradiciones. ¿Es esa la razón por la que estás aquí?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Las leyendas y la tradición del Festival de Invierno se han ido borrando de la memoria de nuestra gente con el paso de los años. Sí, sé que los goblins ayudan a revivir el Festival en su propio beneficio, pero eso al menos ayuda a que no caiga en el olvido.$B$BAdmiro tu interés en nuestra historia es un hecho admirable, joven $c. Me impresiona tu sabiduría.', 0),
(@ID, 'esMX', 'Las leyendas y la tradición del Festival de Invierno se han ido borrando de la memoria de nuestra gente con el paso de los años. Sí, sé que los goblins ayudan a revivir el Festival en su propio beneficio, pero eso al menos ayuda a que no caiga en el olvido.$B$BAdmiro tu interés en nuestra historia es un hecho admirable, joven $c. Me impresiona tu sabiduría.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Te doy la bienvenida al Reino de Forjaz, $gamigo:amiga;. Me agrada la gente que se interesa por el reino, sus gentes y sus tradiciones. ¿Es esa la razón por la que estás aquí?', 0),
(@ID, 'esMX', 'Te doy la bienvenida al Reino de Forjaz, $gamigo:amiga;. Me agrada la gente que se interesa por el reino, sus gentes y sus tradiciones. ¿Es esa la razón por la que estás aquí?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Las leyendas y la tradición del Festival de Invierno se han ido borrando de la memoria de nuestra gente con el paso de los años. Sí, sé que los goblins ayudan a revivir el Festival en su propio beneficio, pero eso al menos ayuda a que no caiga en el olvido.$B$BEl hecho de que vayas más allá de lo material y te intereses por nuestra historia es algo admirable, joven $c. Me impresiona tu sabiduría.', 0),
(@ID, 'esMX', 'Las leyendas y la tradición del Festival de Invierno se han ido borrando de la memoria de nuestra gente con el paso de los años. Sí, sé que los goblins ayudan a revivir el Festival en su propio beneficio, pero eso al menos ayuda a que no caiga en el olvido.$B$BEl hecho de que vayas más allá de lo material y te intereses por nuestra historia es algo admirable, joven $c. Me impresiona tu sabiduría.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Preferiría pelear esta batalla por nuestra cuenta, pero no nos quedan otras opciones: necesitamos a los más poderosos, y las razas de Azeroth han demostrado sin lugar a dudas que pueden superar esas cosas cuando trabajan juntas. Así que es a ti a quien acudimos en busca de ayuda.$B$BSolo espero que sea suficiente.$B$BZaetar, hermano de Remulos, trajo muchos dolores a este mundo y finalmente causó su propia muerte. Solo espero no haber causado más muertes al pedirte que nos ayudes.', 0),
(@ID, 'esMX', 'Preferiría pelear esta batalla por nuestra cuenta, pero no nos quedan otras opciones: necesitamos a los más poderosos, y las razas de Azeroth han demostrado sin lugar a dudas que pueden superar esas cosas cuando trabajan juntas. Así que es a ti a quien acudimos en busca de ayuda.$B$BSolo espero que sea suficiente.$B$BZaetar, hermano de Remulos, trajo muchos dolores a este mundo y finalmente causó su propia muerte. Solo espero no haber causado más muertes al pedirte que nos ayudes.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Estupendo, $n! ¡Gracias!$B$BLe preguntaré a Marandis por los restos de Zaetar, pero al menos has hecho lo más difícil.$B$BElige uno de estos objetos como prueba de nuestra gratitud.', 0),
(@ID, 'esMX', '¡Estupendo, $n! ¡Gracias!$B$BLe preguntaré a Marandis por los restos de Zaetar, pero al menos has hecho lo más difícil.$B$BElige uno de estos objetos como prueba de nuestra gratitud.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Dejas un rastro de corrupción, $n, como muchos aventureros. Pero tú tienes algo distinto. ¿Has venido a hablar conmigo? Me resultas familiar, pero no consigo ubicarte.$B$BDime, ¿por qué has venido a verme?', 0),
(@ID, 'esMX', 'Dejas un rastro de corrupción, $n, como muchos aventureros. Pero tú tienes algo distinto. ¿Has venido a hablar conmigo? Me resultas familiar, pero no consigo ubicarte.$B$BDime, ¿por qué has venido a verme?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿El espíritu de mi hermano ha encontrado al fin la paz? Me alegro de saberlo. Lo creí perdido para siempre, pero esto... Este símbolo de su sacrificio me demuestra que no está perdido sino que ha renacido. Ha hecho lo que debía hacer.$B$BGracias, $n. Me has devuelto la esperanza; presiento que esta semilla será muy importante para las gentes de esta tierra.', 0),
(@ID, 'esMX', '¿El espíritu de mi hermano ha encontrado al fin la paz? Me alegro de saberlo. Lo creí perdido para siempre, pero esto... Este símbolo de su sacrificio me demuestra que no está perdido sino que ha renacido. Ha hecho lo que debía hacer.$B$BGracias, $n. Me has devuelto la esperanza; presiento que esta semilla será muy importante para las gentes de esta tierra.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Qué me dices, $n? ¿Te atreves a pisar suelo sagrado para ayudarme a unificar a los centauros?', 0),
(@ID, 'esMX', '¿Qué me dices, $n? ¿Te atreves a pisar suelo sagrado para ayudarme a unificar a los centauros?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Al fin puedo iniciar la unificación de las tribus, $r! Gracias.$B$BAcepta esto en prueba de mi gratitud. Espero que cuando volvamos a vernos sea en tiempo de paz.', 0),
(@ID, 'esMX', '¡Al fin puedo iniciar la unificación de las tribus, $r! Gracias.$B$BAcepta esto en prueba de mi gratitud. Espero que cuando volvamos a vernos sea en tiempo de paz.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Traes los cristales? Seguro que serán muy útiles en todo tipo de magia arcana, rituales de poder y grandes hechizos.', 0),
(@ID, 'esMX', '¿Traes los cristales? Seguro que serán muy útiles en todo tipo de magia arcana, rituales de poder y grandes hechizos.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Perfecto... Bueno, toma uno de estos abalorios y lárgate, tengo mucho que hacer.', 0),
(@ID, 'esMX', 'Perfecto... Bueno, toma uno de estos abalorios y lárgate, tengo mucho que hacer.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '$n, todavía no has asaltado un cementerio. Regresa a mí cuando esta tarea esté completa.', 0),
(@ID, 'esMX', '$n, todavía no has asaltado un cementerio. Regresa a mí cuando esta tarea esté completa.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Muy bien! Los cementerios son clave para el control del Valle de Alterac. Cuando capturas uno, contribuyes a asegurar la soberanía de la Alianza en ese lugar.$B$BVete ahora, $n. Vuelve a la batalla y si ves un cementerio bajo control de la Horda... ¡ya sabes qué hacer!', 0),
(@ID, 'esMX', '¡Muy bien! Los cementerios son clave para el control del Valle de Alterac. Cuando capturas uno, contribuyes a asegurar la soberanía de la Alianza en ese lugar.$B$BVete ahora, $n. Vuelve a la batalla y si ves un cementerio bajo control de la Horda... ¡ya sabes qué hacer!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Ataca un cementerio de la Alianza!', 0),
(@ID, 'esMX', '¡Ataca un cementerio de la Alianza!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Muy bien, $n! Con cada cementerio que controlemos, el espíritu de nuestros enemigos vagará perdido y sin rumbo.', 0),
(@ID, 'esMX', '¡Muy bien, $n! Con cada cementerio que controlemos, el espíritu de nuestros enemigos vagará perdido y sin rumbo.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Aún no has asaltado un búnker, $n! ¿Estás esperando que otros reclamen tu gloria?', 0),
(@ID, 'esMX', '¡Aún no has asaltado un búnker, $n! ¿Estás esperando que otros reclamen tu gloria?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡He oído hablar de tu éxito en la misión! Me complace tal noticia...$B$BNuestros soldados podrán ahora cargar con impunidad ¡y demostrar a la Alianza que el Valle de Alterac no es lugar para ellos!', 0),
(@ID, 'esMX', '¡He oído hablar de tu éxito en la misión! Me complace tal noticia...$B$BNuestros soldados podrán ahora cargar con impunidad ¡y demostrar a la Alianza que el Valle de Alterac no es lugar para ellos!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Aún no has asaltado una torre, $n! ¿Que estas esperando?', 0),
(@ID, 'esMX', '¡Aún no has asaltado una torre, $n! ¿Que estas esperando?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'He recibido un informe de campo según el cual has contribuido a recuperar un estandarte enemigo. ¡Muy bien, $n! Con sus líneas descolocadas ¡podemos adelantar más el frente!', 0),
(@ID, 'esMX', 'He recibido un informe de campo según el cual has contribuido a recuperar un estandarte enemigo. ¡Muy bien, $n! Con sus líneas descolocadas ¡podemos adelantar más el frente!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Es bueno ver a nuevos soldados aquí, $n, pero con un poco de suerte entrarás pronto en combate ¡y no serás $gun novato:una novata; por mucho tiempo!$B$BVuelve a hablar conmigo. Tengo tareas que tratar contigo...', 0),
(@ID, 'esMX', 'Es bueno ver a nuevos soldados aquí, $n, pero con un poco de suerte entrarás pronto en combate ¡y no serás $gun novato:una novata; por mucho tiempo!$B$BVuelve a hablar conmigo. Tengo tareas que tratar contigo...', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Aún no has capturado la mina, $n!$B$BUna vez que controlemos una mina, será mucho más fácil recolectar minerales y suministros de ella.', 0),
(@ID, 'esMX', '¡Aún no has capturado la mina, $n!$B$BUna vez que controlemos una mina, será mucho más fácil recolectar minerales y suministros de ella.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Lo has conseguido! ¡Muy bien, $n!$B$BCuando nos hacemos con el control de una mina y tenemos a soldados estacionados allí, es mucho más fácil recolectar el mineral y los suministros.', 0),
(@ID, 'esMX', '¡Lo has conseguido! ¡Muy bien, $n!$B$BCuando nos hacemos con el control de una mina y tenemos a soldados estacionados allí, es mucho más fácil recolectar el mineral y los suministros.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Hola, $n! Me alegra ver que $gun:una; $c como tú está impaciente por aportar su granito de arena a la Horda. Vuelve a hablar conmigo, tengo tareas vitales para ti...', 0),
(@ID, 'esMX', '¡Hola, $n! Me alegra ver que $gun:una; $c como tú está impaciente por aportar su granito de arena a la Horda. Vuelve a hablar conmigo, tengo tareas vitales para ti...', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Aún no has capturado una mina, $n! ¡Encuentra una que no controlemos y derrota a su líder!', 0),
(@ID, 'esMX', '¡Aún no has capturado una mina, $n! ¡Encuentra una que no controlemos y derrota a su líder!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Has capturado una mina! Con nuestros propios guerreros protegiéndola, será mucho más sencillo extraer minerales de sus profundidades y recolectar los suministros que allí hubieran escondido.$B$BMuy bien, $n. Esta acción ha sido una gran contribución a nuestros esfuerzos en la batalla.', 0),
(@ID, 'esMX', '¡Has capturado una mina! Con nuestros propios guerreros protegiéndola, será mucho más sencillo extraer minerales de sus profundidades y recolectar los suministros que allí hubieran escondido.$B$BMuy bien, $n. Esta acción ha sido una gran contribución a nuestros esfuerzos en la batalla.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Ve, $n! ¡Ve al Valle de Alterac y derrota al general orco!', 0),
(@ID, 'esMX', '¡Ve, $n! ¡Ve al Valle de Alterac y derrota al general orco!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Muy bien, $n! Con la nariz de la Horda ensangrentada tras la muerte de su general, ¡tendremos margen para explorar!', 0),
(@ID, 'esMX', '¡Muy bien, $n! Con la nariz de la Horda ensangrentada tras la muerte de su general, ¡tendremos margen para explorar!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Muy bien, $n! Los Pico Tormenta no tienen ninguna posibilidad en esta insensata invasión.$B$B¡Ja! Hasta ahora, ¡solo han conseguido que maten a su general!', 0),
(@ID, 'esMX', '¡Muy bien, $n! Los Pico Tormenta no tienen ninguna posibilidad en esta insensata invasión.$B$B¡Ja! Hasta ahora, ¡solo han conseguido que maten a su general!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Despierta! ¡Despierta, $n! Esto no es un sueño. ¡Estás realmente $gatrincherado:atrincherada; en medio de una cruda batalla!', 0),
(@ID, 'esMX', '¡Despierta! ¡Despierta, $n! Esto no es un sueño. ¡Estás realmente $gatrincherado:atrincherada; en medio de una cruda batalla!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ese ardor que sientes es tu alma que se despierta. Tu espíritu ha aceptado la llamada a las armas.$B$BAhora formas parte de los Lobo Gélido, $n. Enhorabuena.$B$BLleva siempre contigo esta insignia. A medida que subes de rango entre los Lobo Gélido, sustituiré tu insignia por otras, otras más poderosas.$B$BQuizás algún día se exalten tus hazañas entre el clan Lobo Gélido. Si tal día llega, se te concederá el Ojo del Orden.', 0),
(@ID, 'esMX', 'Ese ardor que sientes es tu alma que se despierta. Tu espíritu ha aceptado la llamada a las armas.$B$BAhora formas parte de los Lobo Gélido, $n. Enhorabuena.$B$BLleva siempre contigo esta insignia. A medida que subes de rango entre los Lobo Gélido, sustituiré tu insignia por otras, otras más poderosas.$B$BQuizás algún día se exalten tus hazañas entre el clan Lobo Gélido. Si tal día llega, se te concederá el Ojo del Orden.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Cuidado con las arpías de la región, $c. ¡No se lo pensarán dos veces antes de cortarte el cuello!', 0),
(@ID, 'esMX', 'Cuidado con las arpías de la región, $c. ¡No se lo pensarán dos veces antes de cortarte el cuello!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Toma esta insignia y llévala con orgullo, $n. A medida que subes de rango entre los Pico Tormenta, recibirás la insignia correspondiente. Vuelve aquí conforme adquieres más honores con los Pico Tormenta y sustituiré tu insignia por otras, otras más poderosas.$B$BQuizás algún día llegues a crecer tanto como para convertirte en una leyenda en el campo de batalla. Si tal día llega, se te concederá el Ojo del Orden.', 0),
(@ID, 'esMX', 'Toma esta insignia y llévala con orgullo, $n. A medida que subes de rango entre los Pico Tormenta, recibirás la insignia correspondiente. Vuelve aquí conforme adquieres más honores con los Pico Tormenta y sustituiré tu insignia por otras, otras más poderosas.$B$BQuizás algún día llegues a crecer tanto como para convertirte en una leyenda en el campo de batalla. Si tal día llega, se te concederá el Ojo del Orden.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '<El maestro de guerra Laggrond te hace el saludo.>$B$BHa llegado tu hora, $n. Has hecho mucho por el clan Lobo Gélido y con ello, te has ganado un lugar no solo como $gun:una; soldado Lobo Gélido sino también como $gamigo:amiga;.$B$B¡Muy bien! ¡Presenta tu insignia de Iniciado!', 0),
(@ID, 'esMX', '<El maestro de guerra Laggrond te hace el saludo.>$B$BHa llegado tu hora, $n. Has hecho mucho por el clan Lobo Gélido y con ello, te has ganado un lugar no solo como $gun:una; soldado Lobo Gélido sino también como $gamigo:amiga;.$B$B¡Muy bien! ¡Presenta tu insignia de Iniciado!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Esta nueva insignia refleja tu rango entre los Lobo Gélido. Llévala siempre contigo.$B$BY $c... Muere con honor.', 0),
(@ID, 'esMX', 'Esta nueva insignia refleja tu rango entre los Lobo Gélido. Llévala siempre contigo.$B$BY $c... Muere con honor.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Cómo me alegra verte de nuevo, $c! No esperaba que regresaras. No obstante, has demostrado ser $gun:una; valiente y honorable soldado Lobo Gélido.$B$BHa llegado de nuevo tu hora, $n. Te has ganado un nuevo rango y con ello, una nueva insignia que refleje este aumento de importancia.$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', '¡Cómo me alegra verte de nuevo, $c! No esperaba que regresaras. No obstante, has demostrado ser $gun:una; valiente y honorable soldado Lobo Gélido.$B$BHa llegado de nuevo tu hora, $n. Te has ganado un nuevo rango y con ello, una nueva insignia que refleje este aumento de importancia.$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '<El maestro de guerra Laggrond hace el saludo.>$B$B¡Vete, $n! Sigue con tu defensa de los Lobo Gélido. ¡Hay que ganar esta guerra! ¡Por Drek\'Thar! ¡Por el Jefe de Guerra!', 0),
(@ID, 'esMX', '<El maestro de guerra Laggrond hace el saludo.>$B$B¡Vete, $n! Sigue con tu defensa de los Lobo Gélido. ¡Hay que ganar esta guerra! ¡Por Drek\'Thar! ¡Por el Jefe de Guerra!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Tantos muertos. La sangre empapa el campo. Y a pesar de ello... estás $gvivo:viva;... Has perseverado. Has conducido a nuestros ejércitos a muchas victorias. Por ello, soldado, te has ganado un nuevo rango.$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', 'Tantos muertos. La sangre empapa el campo. Y a pesar de ello... estás $gvivo:viva;... Has perseverado. Has conducido a nuestros ejércitos a muchas victorias. Por ello, soldado, te has ganado un nuevo rango.$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '<El maestro de guerra Laggrond emite un rugido.>$B$BYa empieza a correrse la voz sobre tus hazañas, soldado. Los Pico Tormenta se estremecen de miedo con solo oír tu nombre. ¡Sigue así!', 0),
(@ID, 'esMX', '<El maestro de guerra Laggrond emite un rugido.>$B$BYa empieza a correrse la voz sobre tus hazañas, soldado. Los Pico Tormenta se estremecen de miedo con solo oír tu nombre. ¡Sigue así!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Irradias mando y poder, $c. $gExaltado:Exaltada; a los ojos de los Lobo Gélido, el enemigo se encoge ante la simple mención de tu nombre.$B$BEn pie, $ghéroe:heroína; de los Lobo Gélido. ¡Levántate y recibe los honores!$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', 'Irradias mando y poder, $c. $gExaltado:Exaltada; a los ojos de los Lobo Gélido, el enemigo se encoge ante la simple mención de tu nombre.$B$BEn pie, $ghéroe:heroína; de los Lobo Gélido. ¡Levántate y recibe los honores!$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Pronto, todos los que estén en el campo de batalla estarán bajo tu ojo vigilante. ¡Tendrás al valle y a sus soldados a tus órdenes!', 0),
(@ID, 'esMX', 'Pronto, todos los que estén en el campo de batalla estarán bajo tu ojo vigilante. ¡Tendrás al valle y a sus soldados a tus órdenes!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Drek\'Thar no tiene más que alabanzas para ti. Por todo Kalimdor se cuentan tus hazañas. El Jefe de Guerra está radiante, tu inquebrantable defensa de nuestro clan le ha complacido sobremanera.$B$BTe has ganado el Ojo del Orden.$B$BPresenta tu insignia, comandante $n.', 0),
(@ID, 'esMX', 'Drek\'Thar no tiene más que alabanzas para ti. Por todo Kalimdor se cuentan tus hazañas. El Jefe de Guerra está radiante, tu inquebrantable defensa de nuestro clan le ha complacido sobremanera.$B$BTe has ganado el Ojo del Orden.$B$BPresenta tu insignia, comandante $n.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Canaliza el poder de la insignia. Deja que los enemigos oigan tus palabras. El poder de la Horda es incuestionable!', 0),
(@ID, 'esMX', 'Canaliza el poder de la insignia. Deja que los enemigos oigan tus palabras. El poder de la Horda es incuestionable!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'La mayoría no vive lo suficiente para superar su propia mediocridad. Has demostrado ser $gun:una; soldado ejemplar, $c. Ha llegado la hora.$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', 'La mayoría no vive lo suficiente para superar su propia mediocridad. Has demostrado ser $gun:una; soldado ejemplar, $c. Ha llegado la hora.$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Que nuestros enemigos sepan que $gun:una; soldado de rango los ha atacado! ¡Lucha por la gloria de los Pico Tormenta!', 0),
(@ID, 'esMX', '¡Que nuestros enemigos sepan que $gun:una; soldado de rango los ha atacado! ¡Lucha por la gloria de los Pico Tormenta!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Por toda la base se habla de tus hazañas en la Tierra en Disputa! Le has asestado golpes contundentes a nuestro enemigo, ¡aplastándoles la moral! Por eso, te has ganado un rango de honor entre los Pico Tormenta.$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', '¡Por toda la base se habla de tus hazañas en la Tierra en Disputa! Le has asestado golpes contundentes a nuestro enemigo, ¡aplastándoles la moral! Por eso, te has ganado un rango de honor entre los Pico Tormenta.$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'El Rey está al corriente de tus heroicos actos en el frente, $c. ¡Sigue con tu inquebrantable defensa de la Guardia de Pico Tormenta!', 0),
(@ID, 'esMX', 'El Rey está al corriente de tus heroicos actos en el frente, $c. ¡Sigue con tu inquebrantable defensa de la Guardia de Pico Tormenta!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Debo saberlo, $n. Cuando miras directamente a los ojos del enemigo, ¿ves miedo? ¿Se acobardan ahora en tu presencia? ¡Deben darse cuenta de que están derrotados!$B$BTe has ganado la reverencia de la Guardia. ¡Presenta tu insignia!', 0),
(@ID, 'esMX', 'Debo saberlo, $n. Cuando miras directamente a los ojos del enemigo, ¿ves miedo? ¿Se acobardan ahora en tu presencia? ¡Deben darse cuenta de que están derrotados!$B$BTe has ganado la reverencia de la Guardia. ¡Presenta tu insignia!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Cuando nos conocimos, nunca hubiera imaginado que se estaba gestando $gun héroe:una heroína;. ¡Continúa, soldado!', 0),
(@ID, 'esMX', 'Cuando nos conocimos, nunca hubiera imaginado que se estaba gestando $gun héroe:una heroína;. ¡Continúa, soldado!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ante mí se encuentra $gun exaltado héroe:una exaltada heroína; de la Alianza.$B$B<El teniente Haggerdin saluda.>$B$BSon pocos los que se han ganado ese rango entre los Pico Tormenta. He visto caer al enemigo ante ti. He visto su resolución desmoronarse en tu presencia. Cuando entras en la refriega, ¡te conviertes en el faro de esperanza para nuestras fuerzas!$B$BPresenta tu insignia.', 0),
(@ID, 'esMX', 'Ante mí se encuentra $gun exaltado héroe:una exaltada heroína; de la Alianza.$B$B<El teniente Haggerdin saluda.>$B$BSon pocos los que se han ganado ese rango entre los Pico Tormenta. He visto caer al enemigo ante ti. He visto su resolución desmoronarse en tu presencia. Cuando entras en la refriega, ¡te conviertes en el faro de esperanza para nuestras fuerzas!$B$BPresenta tu insignia.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Seguramente serás tú quien lleve el Ojo del Orden!', 0),
(@ID, 'esMX', '¡Seguramente serás tú quien lleve el Ojo del Orden!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Deja que escuchen tu voz, comandante $n! Hazles conocer el miedo. ¡Muéstrales el poder que tiene Pico Tormenta en tu rango!', 0),
(@ID, 'esMX', '¡Deja que escuchen tu voz, comandante $n! Hazles conocer el miedo. ¡Muéstrales el poder que tiene Pico Tormenta en tu rango!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Eres tu quien debe llevar a nuestras tropas a la victoria, comandante! Los soldados están bajo tu mando. Seguirán tus órdenes directas. Guíalos... Aplasta al Lobo Gélido.', 0),
(@ID, 'esMX', '¡Eres tu quien debe llevar a nuestras tropas a la victoria, comandante! Los soldados están bajo tu mando. Seguirán tus órdenes directas. Guíalos... Aplasta al Lobo Gélido.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Me has traído algún resto de armadura, $n?', 0),
(@ID, 'esMX', '¿Me has traído algún resto de armadura, $n?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Estas piezas son muy buenas, $n. Puedo usarlas para confeccionar nuevos equipos para nuestros soldados.$B$BSi mi nivel de suministro llega a ser suficientemente alto, ¡a lo mejor nuestras avezadas unidades de combate irán al campo de batalla!', 0),
(@ID, 'esMX', 'Estas piezas son muy buenas, $n. Puedo usarlas para confeccionar nuevos equipos para nuestros soldados.$B$BSi mi nivel de suministro llega a ser suficientemente alto, ¡a lo mejor nuestras avezadas unidades de combate irán al campo de batalla!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Cómo va la caza, $n? ¿Tienes algún resto de armadura para mí?', 0),
(@ID, 'esMX', '¿Cómo va la caza, $n? ¿Tienes algún resto de armadura para mí?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Estos restos nos servirán. Los fundiré y los remendaré ¡y nadie se enterará de que proceden de esos cobardes Pico Tormenta!$B$BGracias, $n. Y permíteme pulir tu armadura...', 0),
(@ID, 'esMX', 'Estos restos nos servirán. Los fundiré y los remendaré ¡y nadie se enterará de que proceden de esos cobardes Pico Tormenta!$B$BGracias, $n. Y permíteme pulir tu armadura...', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Para convertirte en $gun:una; soldado Lobo Gélido, hay que ponerte a prueba...', 0),
(@ID, 'esMX', 'Para convertirte en $gun:una; soldado Lobo Gélido, hay que ponerte a prueba...', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Cómo debe de dolerle permanecer como uno de los vivos mientras su amado hermano ha ascendido a la perfección.', 0),
(@ID, 'esMX', 'Cómo debe de dolerle permanecer como uno de los vivos mientras su amado hermano ha ascendido a la perfección.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Tus proezas no pasarán desapercibidos, $n.$B$BNo debo perder el tiempo si hay que salvarlo. ¡Hay que ponerse en contacto con Sylvanas!', 0),
(@ID, 'esMX', 'Tus proezas no pasarán desapercibidos, $n.$B$BNo debo perder el tiempo si hay que salvarlo. ¡Hay que ponerse en contacto con Sylvanas!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Con gran pesar doy la orden, soldado.', 0),
(@ID, 'esMX', 'Con gran pesar doy la orden, soldado.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Te acordaste de incinerar el cuerpo, ¿verdad?', 0),
(@ID, 'esMX', 'Te acordaste de incinerar el cuerpo, ¿verdad?', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Algo que me gustaría escuchar?', 0),
(@ID, 'esMX', '¿Algo que me gustaría escuchar?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Increíble! Los has liberado, ¿verdad? ¿¿¿No??? Bueno, al menos sabemos dónde están retenidos', 0),
(@ID, 'esMX', '¡Increíble! Los has liberado, ¿verdad? ¿¿¿No??? Bueno, al menos sabemos dónde están retenidos', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Estado de la caza, soldado?', 0),
(@ID, 'esMX', '¿Estado de la caza, soldado?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Muy bien! La información que has conseguido es muy valiosa para nuestra causa.', 0),
(@ID, 'esMX', '¡Muy bien! La información que has conseguido es muy valiosa para nuestra causa.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Has encontrado los ingredientes? Debo advertirte de que los gusanos de la Alianza son muy numerosos en esta zona... En mi opinión, creo que ya están demasiado cerca...', 0),
(@ID, 'esMX', '¿Has encontrado los ingredientes? Debo advertirte de que los gusanos de la Alianza son muy numerosos en esta zona... En mi opinión, creo que ya están demasiado cerca...', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Mi sopa de tortuga reconfortante te mantendrá caliente en una noche fría... por si te interesa saberlo.', 0),
(@ID, 'esMX', 'Mi sopa de tortuga reconfortante te mantendrá caliente en una noche fría... por si te interesa saberlo.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Saludos, $r. Yo soy Kolark, un cazador de tesoros de Cima del Trueno. He viajado mucho por el mundo y he visto también mucho.$B$BMis viajes por los Mares del Sur me permitieron descubrir la existencia de estas flechas con cabeza de torio. Tengo un sinfín de ellas, pero ya no tengo balas. Y bueno, ningún tauren que se precie andaría por ahí con un endeble arco. ¡Las flechas no sirven para nada!$B$BTe voy a ofrecer un trato: si me traes proyectiles de torio te las cambio por mis cabezales de flecha de torio. ¿Hecho?', 0),
(@ID, 'esMX', 'Saludos, $r. Yo soy Kolark, un cazador de tesoros de Cima del Trueno. He viajado mucho por el mundo y he visto también mucho.$B$BMis viajes por los Mares del Sur me permitieron descubrir la existencia de estas flechas con cabeza de torio. Tengo un sinfín de ellas, pero ya no tengo balas. Y bueno, ningún tauren que se precie andaría por ahí con un endeble arco. ¡Las flechas no sirven para nada!$B$BTe voy a ofrecer un trato: si me traes proyectiles de torio te las cambio por mis cabezales de flecha de torio. ¿Hecho?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Excelente! Soy un tauren de palabra, $r; aquí tienes las flechas.', 0),
(@ID, 'esMX', '¡Excelente! Soy un tauren de palabra, $r; aquí tienes las flechas.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Puedes creer esta basura? ¡El último envío de municiones de Kharanos fue un fracaso! ¡Todo lo que tenemos como munición son estas endebles flechas que no sirven para nada! ¿Qué diablos voy a hacer con 500.000 flechas con puntas de torio?$B$BTe diré una cosa, $gchico:chica;. Si puedes traerme proyectiles de torio, te cambio flechas con puntas de torio, ¡Es un buen negocio! ¿Hay trato?', 0),
(@ID, 'esMX', '¿Puedes creer esta basura? ¡El último envío de municiones de Kharanos fue un fracaso! ¡Todo lo que tenemos como munición son estas endebles flechas que no sirven para nada! ¿Qué diablos voy a hacer con 500.000 flechas con puntas de torio?$B$BTe diré una cosa, $gchico:chica;. Si puedes traerme proyectiles de torio, te cambio flechas con puntas de torio, ¡Es un buen negocio! ¿Hay trato?', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Un trato hecho si alguna vez vi uno! Las flechas son tuyas.', 0),
(@ID, 'esMX', '¡Un trato hecho si alguna vez vi uno! Las flechas son tuyas.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Buscan exterminarnos? Son ellos los que serán exterminados.', 0),
(@ID, 'esMX', '¿Buscan exterminarnos? Son ellos los que serán exterminados.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '$n, es solo uno entre miles. No obstante, un trabajo bien hecho.', 0),
(@ID, 'esMX', '$n, es solo uno entre miles. No obstante, un trabajo bien hecho.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'No me importa de dónde venga el cabello, $r.', 0),
(@ID, 'esMX', 'No me importa de dónde venga el cabello, $r.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Sí, definitivamente esto es un gnomo. Bien hecho, $r, pero hay más trabajo por hacer.', 0),
(@ID, 'esMX', 'Sí, definitivamente esto es un gnomo. Bien hecho, $r, pero hay más trabajo por hacer.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Si, es cierto. Mi hermano es un humano. Vergonzoso... Una mancha en mi historial casi inmaculado.', 0),
(@ID, 'esMX', 'Si, es cierto. Mi hermano es un humano. Vergonzoso... Una mancha en mi historial casi inmaculado.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Sería más fácil si aceptaran su enfermedad y se entregaran a tu toque sanador. Probablemente también sería menos divertido. ¡Sigue adelante! Los curaremos, de una forma u otra.', 0),
(@ID, 'esMX', 'Sería más fácil si aceptaran su enfermedad y se entregaran a tu toque sanador. Probablemente también sería menos divertido. ¡Sigue adelante! Los curaremos, de una forma u otra.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Benedictus! No soy más que una mota a la sombra de su grandeza. ¡Alabado sea!', 0),
(@ID, 'esMX', 'Benedictus! No soy más que una mota a la sombra de su grandeza. ¡Alabado sea!', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Alabado sea Benedictus! ¡Otra alma perdida, que pronto será salvada por las llamas candentes de la Luz!$B$B¡Vete, oh soldado de la Luz! ¡Hay que salvar más!', 0),
(@ID, 'esMX', '¡Alabado sea Benedictus! ¡Otra alma perdida, que pronto será salvada por las llamas candentes de la Luz!$B$B¡Vete, oh soldado de la Luz! ¡Hay que salvar más!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Tienes la posibilidad de ofrecer cantidades mayores de sangre de nuestros enemigos. Será un placer aceptar ofrendas de mayor tamaño, $n.', 0),
(@ID, 'esMX', 'Tienes la posibilidad de ofrecer cantidades mayores de sangre de nuestros enemigos. Será un placer aceptar ofrendas de mayor tamaño, $n.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Se ha derramado sangre enemiga. Mezclaré esta sangre con el resto de elementos que hemos recolectado. Cuando la ofrenda esté completa, el Señor del Hielo enseñará a esos perros Pico Tormenta ¡toda la ira del clan Lobo Gélido!', 0),
(@ID, 'esMX', 'Se ha derramado sangre enemiga. Mezclaré esta sangre con el resto de elementos que hemos recolectado. Cuando la ofrenda esté completa, el Señor del Hielo enseñará a esos perros Pico Tormenta ¡toda la ira del clan Lobo Gélido!', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'A veces te puedes encontrar $gatrincherado:atrincherada; en la batalla durante varios días o incluso varias semanas. En esos períodos más largos de actividad, puedes acabar recogiendo grandes bloques de cristales de tormenta de los Lobo Gélido.$B$BEl Círculo acepta tales ofrendas, $n.', 0),
(@ID, 'esMX', 'A veces te puedes encontrar $gatrincherado:atrincherada; en la batalla durante varios días o incluso varias semanas. En esos períodos más largos de actividad, puedes acabar recogiendo grandes bloques de cristales de tormenta de los Lobo Gélido.$B$BEl Círculo acepta tales ofrendas, $n.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Muy bien, $n. Usaré el poder de estos talismanes para aumentar la magia que ya se ha reunido. Cuando hayas acumulado suficiente poder, concentraré las energías reunidas e invocaré al Señor del Bosque para que nos ayude en nuestra causa.', 0),
(@ID, 'esMX', 'Muy bien, $n. Usaré el poder de estos talismanes para aumentar la magia que ya se ha reunido. Cuando hayas acumulado suficiente poder, concentraré las energías reunidas e invocaré al Señor del Bosque para que nos ayude en nuestra causa.', 0);
INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Trato con la ironía al por mayor, soldado.', 0),
(@ID, 'esMX', 'Trato con la ironía al por mayor, soldado.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¡Ahora tienes licencia para diezmar a los enanos por docenas, $n!$B$B<Yazra mira a lo lejos por un momento.>$B$BNunca olvidas tu primer desespinado...', 0),
(@ID, 'esMX', '¡Ahora tienes licencia para diezmar a los enanos por docenas, $n!$B$B<Yazra mira a lo lejos por un momento.>$B$BNunca olvidas tu primer desespinado...', 0);
