(@ID, 'esMX', 'Qué gran victoria para la Horda, $n. Has honrado muchas veces a tu Jefe de Guerra, ahora él debe honrarte a ti.', 0);
INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
