INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', 'Ah, unu de nuestros mejores compendios. Este ha estado desaparecido durante años.', 0),
