INSERT INTO `quest_offer_reward_locale` (`id`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '<Lydros murmura algo ininteligible.>$B$BComo prometí, $n, la espada de Quel\'Serrar.', 0),
