INSERT INTO `quest_request_items_locale` (`id`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES
(@ID, 'esES', '¿Estás de regreso tan pronto? El tiempo ya no es el mismo para mí, quizás haya sido un largo viaje para ti...', 0),
