UPDATE `quest_template_locale` SET `LogTitle`='Dalgrun ruft', `LogDescription`='Sprecht mit Dalgrun Stahlkiefer in Sturmwind.', `QuestDescription`='Dalgrun Stahlkiefer beaufsichtigt die Ausbildung und Koordination der Jäger im Dienst von Sturmwind. Er hat Euch zu einem persönlichen Gespräch gebeten. Reist nach Sturmwind, sobald es Euch möglich ist, und trefft Euch mit Dalgrun in der Übungshalle der Altstadt.$B$BIch weiß nicht, was er vorhat, aber ich glaube, er ist von Eurem raschen Fortschritt beeindruckt.', `VerifiedBuild`=38134 WHERE (`ID`=27266 AND `locale`='deDE');
