DELETE FROM `quest_offer_reward` WHERE `ID` IN (31255 /*-Unknown-*/, 30015 /*-Unknown-*/, 29966 /*-Unknown-*/, 29967 /*-Unknown-*/, 29968 /*-Unknown-*/, 29943 /*-Unknown-*/, 29824 /*-Unknown-*/, 29823 /*-Unknown-*/, 29731 /*-Unknown-*/, 29730 /*-Unknown-*/, 31239 /*-Unknown-*/, 29939 /*-Unknown-*/, 29942 /*-Unknown-*/, 29937 /*-Unknown-*/);
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(31255, 2, 1, 0, 0, 0, 500, 0, 0, 'Oh, you are a friend of Cho\'s? Welcome! Welcome!$b$bI would serve you a banquet, but as you can see, we are in dire straights at the moment.$b$bSay, would you be willing to lend us a hand?', 38134), -- -Unknown-
(30015, 1, 404, 1, 0, 0, 500, 500, 0, 'Greetings, $n!$b$bWelcome to Dawn\'s Blossom, a home of my people.$b$bSadly I must depart. Alas, a Lorewalker\'s work is never done.$b$bMake yourself at home. Our people could surely make use of one with your talents.', 38134), -- -Unknown-
(29966, 396, 0, 0, 0, 0, 0, 0, 0, 'The Alliance must be purged from this land, $n!$b$bNo half-measures will do!', 38134), -- -Unknown-
(29967, 396, 0, 0, 0, 0, 0, 0, 0, 'I\'d say repelling the attack is an understatement!', 38134), -- -Unknown-
(29968, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, the smell of progress, and methane. ', 38134), -- -Unknown-
(29943, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes!$b$bBy Thrall\'s bloodied fist, I\'ve missed hunting Alliance!', 38134), -- -Unknown-
(29824, 1, 0, 0, 0, 0, 0, 0, 0, 'The most important thing we learned was that... not only have the Alliance befriended the jinyu, but they\'re giving them weapons, and teaching them how to use them.$b$bThe Alliance now control a force that outnumbers us thirty-to-one. And we - the HORDE - have befriended monkeys.', 38134), -- -Unknown-
(29823, 1, 0, 0, 0, 0, 0, 0, 0, 'Yeah, I ran away. So blikkin\' what? If it weren\'t for me, Kiryn would be dead. Oh, wait... she\'s already dead... already...$b$bGo away! Riko\'s head hurts.', 38134), -- -Unknown-
(29731, 396, 0, 0, 0, 0, 0, 0, 0, 'And that\'s how we found our Alliance captive.$b$bBut that\'s not all that happened. Not by a long shot...', 38134), -- -Unknown-
(29730, 1, 0, 0, 0, 0, 0, 0, 0, 'So, as you can see, none of this is Riko\'s fault.', 38134), -- -Unknown-
(31239, 71, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! Wikket Grooked ookin\' Jinyu!', 38134), -- -Unknown-
