INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(60013, 669, 0, 0, 0, 0, 0, 0, 0, 'Debemos apresurarnos. La Arconte debe saber lo que ocurrió aquí.', 38134), -- -Unknown-
(60180, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que haya sido una experiencia satisfactoria.', 38134), -- -Unknown-
(58843, 1, 0, 0, 0, 0, 0, 0, 0, 'Es un placer conocerte. Hace tiempo que La Bóveda no recibe visitantes.', 38134), -- -Unknown-
(58980, 1, 0, 0, 0, 0, 0, 0, 0, 'Tu búsqueda fue fructífera. Estoy encantada de ver que estás $gintacto:intacta;.', 38134), -- -Unknown-
(58979, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero sepas disculpar mi comportamiento. Sentía que no estaba en mis cabales.', 38134), -- -Unknown-
(58978, 0, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos resopla suavemente.>', 38134), -- -Unknown-
(58977, 669, 0, 0, 0, 0, 0, 0, 0, 'Lo logramos. Con un poco de suerte, tendremos sus recuerdos a nuestro alcance.', 38134), -- -Unknown-
(58800, 1, 0, 0, 0, 0, 0, 0, 0, 'Me encanta el sonido que hace el ánima. Hace mucho tiempo que no lo escucho en abundancia.', 38134), -- -Unknown-
(58799, 1, 0, 0, 0, 0, 0, 0, 0, 'Excelente. Haré un buen uso de su ánima.', 38134), -- -Unknown-
(58771, 1, 0, 0, 0, 0, 0, 0, 0, '¡Lo encontraste! Y también traes otros tomos interesantes. Cuando terminemos, me encargaré de registrarlos de forma adecuada.', 38134), -- -Unknown-
(58976, 1, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos gesticula, irritado, por los Rechazados que tiene atrás.>\n\n¿Estás aquí para solucionar ESTO?', 38134), -- -Unknown-
(57447, 669, 0, 0, 0, 0, 0, 0, 0, 'No tendremos ni un segundo de descanso hoy, ¿verdad?\n\n<Kleia mira el fragmento con recelo.>', 38134), -- -Unknown-
(57269, 669, 669, 0, 0, 0, 0, 0, 0, 'Tu aparición en este momento, al borde de esta traición, me resulta curiosa.\n\n<La Dechada de la pureza te estudia con una mirada inquisitiva.>\n\nHiciste un buen trabajo. La Arconte se enterará de tu contribución.', 38134), -- -Unknown-
(57446, 669, 0, 0, 0, 0, 0, 0, 0, 'Nuestra lucha aún no ha terminado, $n.', 38134), -- -Unknown-
(57719, 669, 0, 0, 0, 0, 0, 0, 0, 'Estas palabras… Esta voz…\n\n¿De dónde conozco esta voz?', 38134), -- -Unknown-
(59147, 669, 0, 0, 0, 0, 0, 0, 0, 'Algunas manchas se deben eliminar.', 38134), -- -Unknown-
(57037, 669, 0, 0, 0, 0, 0, 0, 0, 'Su canción volvió a sonar. Es una alegría para mis oídos, pero aún se oye la voz más fuerte. Debemos silenciarla antes de que destruya todo lo que amamos.', 38134), -- -Unknown-
(57444, 669, 0, 0, 0, 0, 0, 0, 0, 'Los vi morir. Se merecían algo peor.', 38134), -- -Unknown-
(57717, 669, 0, 0, 0, 0, 0, 0, 0, '<Eridia sujeta su martillo con más fuerza, tensa.>\n\n¿Cómo…? ¿Por qué son tantos?', 38134), -- -Unknown-
(57716, 669, 0, 0, 0, 0, 0, 0, 0, 'Gracias por traerlo aquí, $n. Está en un peor estado del que cualquiera de nosotros podría haber imaginado.', 38134), -- -Unknown-
(57264, 669, 0, 0, 0, 0, 0, 0, 0, 'Apreciamos tu ayuda. Gracias.', 38134), -- -Unknown-
(57977, 669, 0, 0, 0, 0, 0, 0, 0, 'Has demostrado ser competente en esta tarea. Tienes mi gratitud.', 38134), -- -Unknown-
(57270, 669, 0, 0, 0, 0, 0, 0, 0, 'Gracias por ofrecerte.', 38134), -- -Unknown-
(58174, 669, 0, 0, 0, 0, 0, 0, 0, '<Kleia te sonríe con cariño.>\n\n¿Eso quiere decir que te quedarás?', 38134), -- -Unknown-
(57712, 1, 0, 0, 0, 0, 0, 0, 0, 'Me aseguraré de que vuelvan al lugar apropiado para que todos puedan disfrutarlos.\n\nTe doy las gracias por recuperarlos, y espero que te hayan resultado útiles.', 38134), -- -Unknown-
(60222, 669, 0, 0, 0, 0, 0, 0, 0, 'Mereces recibir la gracia de Bastión, $n. Que te sirva bien.', 38134), -- -Unknown-
(57266, 669, 669, 0, 0, 0, 0, 0, 0, 'Este es un momento digno de celebrar, $n.\n\n<Kleia te sonríe de oreja a oreja.>\n\nLo hiciste. Vinieron.', 38134), -- -Unknown-
(57291, 669, 0, 0, 0, 0, 0, 0, 0, 'Concéntrate. Esta es la manera en que conseguirás hablar con la Arconte. Estoy convencida.', 38134), -- -Unknown-
(57714, 669, 0, 0, 0, 0, 0, 0, 0, 'Esta lección siempre es dolorosa. Esto no cambia la forma en la que te vemos.', 38134), -- -Unknown-
(57909, 1, 0, 0, 0, 0, 0, 0, 0, 'Enfrentarse a tantos en tan poco tiempo es una demostración indiscutible de aguante.\n\nMe cansé de solo mirarte.\n\n<Pelagos se ríe.>', 38134), -- -Unknown-
(57288, 669, 0, 0, 0, 0, 0, 0, 0, 'Impresionante. Veo que no me queda mucho por enseñarte.', 38134), -- -Unknown-
(57908, 669, 0, 0, 0, 0, 0, 0, 0, 'La gratitud de la forjalita es más profunda de lo que crees.\n\nBueno. Ahora debes volver a concentrarte en nuestro objetivo.', 38134), -- -Unknown-
(57713, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que te guste. Está lejos de ser mi mejor obra, pero te servirá mientras estés aquí.', 38134), -- -Unknown-
(59920, 1, 0, 0, 0, 0, 0, 0, 0, 'Estoy muy agradecida por tu ayuda. ¡Ahora puedo practicar mi oficio otra vez!', 38134), -- -Unknown-
(57267, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah. ¡Huelen bien! Y brillan mucho.\n\nEsto está bien, sí.', 38134), -- -Unknown-
(57263, 1, 0, 0, 0, 0, 0, 0, 0, '<Sika toca la sustancia blanda con el dedo.>\n\nBrilla. Eso quiere decir que es ánima. La forjalita estará complacida.', 38134), -- -Unknown-
(57265, 1, 0, 0, 0, 0, 0, 0, 0, 'De algún modo... esto es más y menos de lo que esperaba. Debemos conformarnos.', 38134), -- -Unknown-
(57711, 1, 0, 0, 0, 0, 0, 0, 0, '¡Hola otra vez! ¡Qué bueno verte!', 38134), -- -Unknown-
(57710, 669, 0, 0, 0, 0, 0, 0, 0, 'Confieso que… estaba esperando algo de espectáculo para levantar el ánimo de los aspirantes que tengo a cargo.\n\nPero no esperaba que me animaras a mí también. Gracias por eso.', 38134), -- -Unknown-
(57709, 669, 0, 0, 0, 0, 0, 0, 0, '<La forjalita se toma unos segundos para examinarte y luego asiente.>\n\n$gBienvenido:Bienvenida;. No eres lo que esperaba, pero de todas formas nos alegra que estés aquí.', 38134), -- -Unknown-
(57676, 669, 0, 0, 0, 0, 0, 0, 0, 'Puede que las cosas que nos atan sean violentas. Por más que nos enseñen mucho, no es motivo para dejar que corran libres por ahí.', 38134), -- -Unknown-
(57677, 669, 0, 0, 0, 0, 0, 0, 0, 'Estoy en gran deuda contigo, $n.', 38134), -- -Unknown-
(57261, 669, 0, 0, 0, 0, 0, 0, 0, 'Tu viaje apenas ha comenzado, $n. Espero que podamos ayudarnos mutuamente', 38134), -- -Unknown-
(60735, 669, 0, 0, 0, 0, 0, 0, 0, '<Kalisthene te observa atentamente.>\n\nTe hemos escuchado, mortal.', 38134), -- -Unknown-
(57584, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que eso fuera informativo y relajante.', 38134), -- -Unknown-
(57102, 273, 0, 0, 0, 0, 0, 0, 0, '<El centurión emite una suave campanada.>', 38134); -- -Unknown-


