UPDATE `broadcast_text_locale` SET `Text_lang`='Ich habe Neuigkeiten für Euch, $n: Alles, was Ihr bisher über Euren Beruf wusstet, war Kinderkram. Keine Herausforderungen, kein Stil, keine Raffinesse oder Eleganz!$b$bIch bin nicht einfach nur ein Modehistoriker, $gjunger:junge:r; $R, ich bin auch ein Philosoph und – wie ich durchaus zugebe – eine Art Stilikone hier.', `VerifiedBuild`=26365 WHERE (`ID`=129253 AND `locale`='deDE');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ihr habt mit Meister Bu Geduld bewiesen, bei den Grummeln Ehre, bei den Shado-Pan Pflichtbewusstsein und bei den Jinyu Vertrauen.', `VerifiedBuild`=26365 WHERE (`ID`=128500 AND `locale`='deDE');
UPDATE `broadcast_text_locale` SET `Text_lang`='Eine faszinierende Verwendung von Seelenenergie. Mit dem nötigen Ehrgeiz hätten sie so viel erreichen können.', `VerifiedBuild`=26365 WHERE (`ID`=128075 AND `locale`='deDE');
