UPDATE `broadcast_text_locale` SET `Text1_lang`='Tor\'gash glaubt, er kann die Zukunft aus den Innereien von Vögeln lesen, oder durch das Werfen der Knochen seiner Vorfahren. Ehrlich, alles, was man tun muss, ist die Geister zu fragen. Aber versucht nicht, ihm das zu erklären, $n, er ist sehr dickköpfig.', `VerifiedBuild`=26365 WHERE (`ID`=14947 AND `locale`='deDE');
