INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'deDE', '', 'Willkommen. Habt Ihr Interesse an Schlüsselsteinen?', 38134),
(209048, 'deDE', 'Aggressor Zo\'dash in Oribos kann Euch versorgen. Alternativ könnt Ihr Euch an den Gegenstandsaufwerter in Eurem Paktsanktum wenden.', 'Aggressor Zo\'dash in Oribos kann Euch versorgen. Alternativ könnt Ihr Euch an den Gegenstandsaufwerter in Eurem Paktsanktum wenden.', 38134),
(208581, 'deDE', 'Grüße, BENUTZERNAME. Ich hoffe, Ihr habt einen ZUFALLSGENERIERTES POSITIVES ADJEKTIV Tag.', '', 38134),
(208211, 'deDE', 'Mein früherer Meister, Grufthüter Kassir, möchte Euch in den Hallen der Sühne treffen, $p.', '', 38134),
(208209, 'deDE', 'Tubbins hat im Glutdistrikt nach Euch gefragt, $p.', '', 38134),
(208205, 'deDE', 'Gresit will Euch draußen an den Hallen der Sühne sehen, $p.', '', 38134),
(208120, 'deDE', 'Manche Tage ganz besonders! Meine goldene Tru-huhe hier, Ihr seht sie, ja? Ganz besonderer Tag! Grund für Besonderheit? Wir feiern einen Provosten! \n\nIch sag Euch Namen und Ihr bringt dann reife Pu-hurianfrucht hin. Kommt wieder, öffnet Truhe! Huh-rra!', '', 38134),
(208019, 'deDE', '$gEiner:Eine; von uns. $gEiner:Eine; von uns...', '', 38134),
(208018, 'deDE', 'Polemarch Adrestes wippt mit den Füßen, während er eine Melodie vor sich hin summt.', '', 38134),
(208016, 'deDE', '', 'Wohin Ihr auch geht, ich werde Euch beobachten.', 38134),
(208015, 'deDE', 'Bester Kumpel schleppt mir immer die besten Schreiben an!', '', 38134),
(208014, 'deDE', '', 'Viele Freunde. Viele Freuden. Sika glücklich.', 38134),
(208013, 'deDE', 'Hmm... $gEr ist mein bester Freund:Sie ist meine beste Freundin; unter den Lehrlingen... Ein möglicher Titel wäre dann wohl Freundling? Oder $gLehrfreund:Lehrfreundin;? Nein, nein, nein...', '', 38134),
(208012, 'deDE', 'Gemeinsam stärker, auf dass wir niemals getrennt werden mögen.', '', 38134),
(208011, 'deDE', '', 'Wenn der Tod uns zusammengeführt hat, welche Macht soll uns dann schon auseinanderreißen?', 38134),
(208010, 'deDE', 'Haltet alle die Klappe und hört Euch an, was $p zu sagen hat. $gMein bester Freund:Meine beste Freundin; versteht mehr vom Kämpfen als Ihr alle zusammen!', '', 38134),
(208009, 'deDE', '', 'Dieser Baum gedeiht im Wald, $p. Kehrt bald in meinen Hain zurück, auf dass wir beide gedeihen mögen.', 38134),
(208008, 'deDE', 'Choofa fängt langsam an, leise zu singen.', '', 38134),
(208007, 'deDE', 'Und für uns ist klar, dass $p $gder beste Freund:die beste Freundin; von allen Gräbern ist!', '', 38134),
(208006, 'deDE', 'Wir haben jetzt viele Freunde, aber bloß einer kann der beste sein...', '', 38134),
(208005, 'deDE', 'Tatsächlich freue ich mich schon sehr auf den nächsten Gluthof. Vielleicht nehme ich sogar das Angebot dieser Adligen an und wage ein Tänzchen...', '', 38134),
(208004, 'deDE', '', 'Es wird immer wieder eine Jagd geben, aber jemanden wie $p gibt es nur einmal.', 38134),
(208003, 'deDE', 'Wann immer Ihr mich braucht, wird meine Klinge für Euch bereitstehen, $gBruder:Schwester;. Ich werde Eurem Ruf stets folgen.', '', 38134),
(208002, 'deDE', '', 'Herzlichen Glückwunsch, $gmein wunderschöner:meine wunderschöne; $p. Euer Hof ist großartig, genau wie Ihr.', 38134),
(208001, 'deDE', 'Hinsetzen. Entspannen. Keine Eile. Hab immer Zeit für $gmeinen besten Freund:meine beste Freundin; $p.', '', 38134),
(208000, 'deDE', '', 'Was sollen wir als Nächstes zusammen anstellen, $p? Ich platze förmlich vor Ungeduld!', 38134),
(207999, 'deDE', '', 'Lasst Euch von mir nicht aufhalten, $gmein kleiner:meine kleine; $p.', 38134),
(207996, 'deDE', 'Passt auf Euch auf, $p. Es gibt immer Seelen, die man noch retten könnte, doch vergesst nicht, dass manche von ihnen bereits verloren sind.', '', 38134),
(207951, 'deDE', 'Fehler. Anwender hat keinen Zugriff, bis genügend Erfahrung angesammelt wurde.', '', 38134),
(207923, 'deDE', 'Für den Ardenwald muss noch viel getan werden, $n.', 'Für den Ardenwald muss noch viel getan werden, $n.', 38134),
(207903, 'deDE', '', 'Möchtet Ihr eine Geschichte hören?', 38134),
(207841, 'deDE', '$n! Hier drüben, Mann!', '', 38134),
(207504, 'deDE', 'Der Wind steht günstig heute! Wo darf ich Euch hinschicken?', '', 38134),
(207499, 'deDE', '', '<Lady Ilinca lächelt.>\n\nGute Frage. Es ist klug, Eure Gästeliste wöchentlich zu überarbeiten, um zu gewährleisten, dass die Gäste zueinander passen.\n\nIhr könnt die Einladung eines jeden Gasts zurücknehmen. Sprecht dazu mit Lord Garridan, damit er Euch die Gästeliste einsehen lässt.\n\nMöglicherweise müsst Ihr ein paar durchflutete Rubine aufbringen, um bei ausgeladenen Gästen den dadurch entstandenen emotionalen Schmerz zu lindern. Ihr könnt aber auch Botschafter rekrutieren oder bei mir Blankoeinladungen erwerben, um diese Kosten zu decken.\n\nAch ja, und vermeidet es unbedingt, Gäste mit gegensätzlichen Vorlieben bezüglich der Atmosphäre einzuladen. Sonst wird es ungemein schwierig, sie zufriedenzustellen.', 38134),
(207446, 'deDE', 'Einigkeit ist ein besonderes Konstrukt. Ihr unablässiges Pflichtbewusstsein bindet sie an die Schattenlande. Außerdem ist sie in der Lage, Rüstung zu entdecken, die unsere Aufgabe in den Schattenlande symbolisiert, während sie im Dienst der unvergänglichen Armee in Maldraxxus agiert.', 'Einigkeit ist ein besonderes Konstrukt. Ihr unablässiges Pflichtbewusstsein bindet sie an die Schattenlande. Außerdem ist sie in der Lage, Rüstung zu entdecken, die unsere Aufgabe in den Schattenlande symbolisiert, während sie im Dienst der unvergänglichen Armee in Maldraxxus agiert.', 38134),
(207442, 'deDE', '', 'Ja, und ich verrate es Euch, nachdem Ihr Euren Freund nach Oribos gebracht habt.\n\nKommt zu mir zurück, sobald er in Sicherheit ist.', 38134),
(207434, 'deDE', 'Seid Ihr das da unten, Theotar?', '', 38134),
(207419, 'deDE', 'So ein Pech, $gSchlundgänger:Schlundgängerin;. Zündet Eure Fackel an einer brennenden Kohlenpfanne wieder an, um weiterzumachen. Gebt nicht auf!', 'So ein Pech, $gSchlundgänger:Schlundgängerin;. Zündet Eure Fackel an einer brennenden Kohlenpfanne wieder an, um weiterzumachen. Gebt nicht auf!', 38134),
(207418, 'deDE', '', 'Entzündet Eure Fackel hier! Beeilt Euch! Hütet Euch vor Schleimen!', 38134),
(207388, 'deDE', '', 'Irgendwelche Wünsche?', 38134),
(207376, 'deDE', 'Ein Jammer, dass Ihr die Förmlichkeit der Stärke vorgezogen habt, $gSchlundgänger:Schlundgängerin;.', 'Ein Jammer, dass Ihr die Förmlichkeit der Stärke vorgezogen habt, $gSchlundgänger:Schlundgängerin;.', 38134),
(207372, 'deDE', 'Die Nachtfae können sich glücklich schätzen, eine so furchtlose Person wie Euch in ihren Diensten zu haben, $gSchlundgänger:Schlundgängerin;.', 'Die Nachtfae können sich glücklich schätzen, eine so furchtlose Person wie Euch in ihren Diensten zu haben, $gSchlundgänger:Schlundgängerin;.', 38134),
(207371, 'deDE', 'Die Wildnis scheint Euch in ihren Dienst gerufen zu haben, $n. Eine edle Berufung.', 'Die Wildnis scheint Euch in ihren Dienst gerufen zu haben, $n. Eine edle Berufung.', 38134),
(207370, 'deDE', 'Obwohl Ihr Euch nicht unserem Pakt angeschlossen habt, dient Ihr einem großen Zyklus, $gSchlundgänger:Schlundgängerin;. Eine wahrlich edle Pflicht.', 'Obwohl Ihr Euch nicht unserem Pakt angeschlossen habt, dient Ihr einem großen Zyklus, $gSchlundgänger:Schlundgängerin;. Eine wahrlich edle Pflicht.', 38134),
(207369, 'deDE', 'Richtet der Winterkönigin unsere Grüße aus. Sie ist eine wahre Verbündete der Archon.', 'Richtet der Winterkönigin unsere Grüße aus. Sie ist eine wahre Verbündete der Archon.', 38134),
(207368, 'deDE', 'Möget Ihr in Eurer... Ernte... Erfüllung finden, und möge Euer Dienst Euch Sinn geben, $n.', 'Möget Ihr in Eurer... Ernte... Erfüllung finden, und möge Euer Dienst Euch Sinn geben, $n.', 38134),
(207367, 'deDE', '$gSchlundgänger:Schlundgängerin;. Mögen sich die fehlgeleiteten Seelen unter Eurer Führung von ihren Bürden befreien.', '$gSchlundgänger:Schlundgängerin;. Mögen sich die fehlgeleiteten Seelen unter Eurer Führung von ihren Bürden befreien.', 38134),
(207366, 'deDE', 'Die Venthyr verstehen, dass die Vergangenheit eine Bürde ist. Unsere Pakte haben viel gemeinsam, $n.', 'Die Venthyr verstehen, dass die Vergangenheit eine Bürde ist. Unsere Pakte haben viel gemeinsam, $n.', 38134),
(207365, 'deDE', 'Ihr habt Euch entschlossen, den Venthyr zu dienen? Seelen zur Erlösung zu führen, ist eine äußerst würdige Aufgabe.', 'Ihr habt Euch entschlossen, den Venthyr zu dienen? Seelen zur Erlösung zu führen, ist eine äußerst würdige Aufgabe.', 38134),
(207364, 'deDE', 'Geht und dient... wie auch immer dieser Dienst aussehen mag.', 'Geht und dient... wie auch immer dieser Dienst aussehen mag.', 38134),
(207363, 'deDE', 'Die Gunst der Archon bleibt Euch erhalten, ungeachtet Eures Pakts.', 'Die Gunst der Archon bleibt Euch erhalten, ungeachtet Eures Pakts.', 38134),
(207362, 'deDE', 'Möget Ihr dem Reich der Zwietracht Disziplin beibringen, $n.', 'Möget Ihr dem Reich der Zwietracht Disziplin beibringen, $n.', 38134),
(207361, 'deDE', 'Ich dachte eigentlich, Ihr wärt eher für den Dienst als für den Konflikt geeignet, $gSchlundgänger:Schlundgängerin;. Aber vielleicht habe ich mich geirrt.', 'Ich dachte eigentlich, Ihr wärt eher für den Dienst als für den Konflikt geeignet, $gSchlundgänger:Schlundgängerin;. Aber vielleicht habe ich mich geirrt.', 38134),
(207360, 'deDE', 'Durch Euren Dienst ehrt Ihr unseren Pakt, $gSchlundgänger:Schlundgängerin;.', 'Durch Euren Dienst ehrt Ihr unseren Pakt, $gSchlundgänger:Schlundgängerin;.', 38134),
(207359, 'deDE', 'Es freut mich, dass sich unsere Wege kreuzen, $n. Eure Hingabe wird sehr geschätzt.', 'Es freut mich, dass sich unsere Wege kreuzen, $n. Eure Hingabe wird sehr geschätzt.', 38134),
(207358, 'deDE', 'Ah, $gSchlundgänger:Schlundgängerin;! Ihr seid bestimmt für die Archon unterwegs. Geht und dient.', 'Ah, $gSchlundgänger:Schlundgängerin;! Ihr seid bestimmt für die Archon unterwegs. Geht und dient.', 38134),
(207357, 'deDE', 'Wir können uns glücklich schätzen, Euch bei uns zu haben, $n. Möge die Gnade der Archon Euch leiten.', 'Wir können uns glücklich schätzen, Euch bei uns zu haben, $n. Möge die Gnade der Archon Euch leiten.', 38134),
(207356, 'deDE', 'Wenn Ihr genug mit den Fae herumgetollt habt, kommt doch mal wieder vorbei und amüsiert Euch mit uns.', 'Wenn Ihr genug mit den Fae herumgetollt habt, kommt doch mal wieder vorbei und amüsiert Euch mit uns.', 38134),
(207355, 'deDE', 'Richtet der Winterkönigin bitte unsere Grüße aus, $gSchlundgänger:Schlundgängerin;.', 'Richtet der Winterkönigin bitte unsere Grüße aus, $gSchlundgänger:Schlundgängerin;.', 38134),
(207354, 'deDE', 'Unsere Wälder mögen nicht so majestätisch wie die des Ardenwalds sein, aber auch wir kämpfen, um unser Reich zu bewahren.', 'Unsere Wälder mögen nicht so majestätisch wie die des Ardenwalds sein, aber auch wir kämpfen, um unser Reich zu bewahren.', 38134),
(207353, 'deDE', 'Die Nachtfae eifern nicht um Macht? Was machen sie denn dann in diesen verwilderten Hainen?', 'Die Nachtfae eifern nicht um Macht? Was machen sie denn dann in diesen verwilderten Hainen?', 38134),
(207352, 'deDE', 'Es ist gut, dass Ihr Euch unserem Pakt angeschlossen habt, $n. Ich glaube, Samt wird Euch hervorragend stehen.', 'Es ist gut, dass Ihr Euch unserem Pakt angeschlossen habt, $n. Ich glaube, Samt wird Euch hervorragend stehen.', 38134),
(207351, 'deDE', 'Meine Treue gilt dem Hof der Ernter. Und Euch, $gSchlundgänger:Schlundgängerin;.', 'Meine Treue gilt dem Hof der Ernter. Und Euch, $gSchlundgänger:Schlundgängerin;.', 38134),
(207350, 'deDE', 'Welch wundervolle Fehltritte Ihr für Euren Sündenstein sammeln müsst!', 'Welch wundervolle Fehltritte Ihr für Euren Sündenstein sammeln müsst!', 38134),
(207349, 'deDE', 'Mit Eurer Hilfe, $n,  werden wir zu unserer edlen Bestimmung zurückfinden.', 'Mit Eurer Hilfe, $n,  werden wir zu unserer edlen Bestimmung zurückfinden.', 38134),
(207348, 'deDE', 'Ich möchte wetten, von allen Pakten haben die Kyrianer die langweiligsten Sündensteine.', 'Ich möchte wetten, von allen Pakten haben die Kyrianer die langweiligsten Sündensteine.', 38134),
(207347, 'deDE', 'Revendreth mag ein Ort voller Schatten sein, $gKyrianer:Kyrianerin;, aber unsere Pflichten sind deshalb nicht minder edel als die Euren.', 'Revendreth mag ein Ort voller Schatten sein, $gKyrianer:Kyrianerin;, aber unsere Pflichten sind deshalb nicht minder edel als die Euren.', 38134),
(207346, 'deDE', '', 'Danke... Es freut mich, dass meine letzten Anstrengungen nicht vergebens waren.', 38134),
(207345, 'deDE', 'Eure Provosten scheinen sehr ordnungsliebend zu sein, $n. Wisst Ihr, wir haben da ein paar Gruften, die dringend geschrubbt werden müssten...', 'Eure Provosten scheinen sehr ordnungsliebend zu sein, $n. Wisst Ihr, wir haben da ein paar Gruften, die dringend geschrubbt werden müssten...', 38134),
(207343, 'deDE', 'Obwohl sich unsere Methoden unterscheiden, wollen unser beider Pakte die Seelen auf einen edlen Pfad führen.', 'Obwohl sich unsere Methoden unterscheiden, wollen unser beider Pakte die Seelen auf einen edlen Pfad führen.', 38134),
(207342, 'deDE', 'Ihr habt Euch für einen brutalen Pfad entschieden, $gSterblicher:Sterbliche;. Sollte Euch nach etwas mehr Finesse sein, so wisst Ihr ja, wo Ihr uns findet.', 'Ihr habt Euch für einen brutalen Pfad entschieden, $gSterblicher:Sterbliche;. Sollte Euch nach etwas mehr Finesse sein, so wisst Ihr ja, wo Ihr uns findet.', 38134),
(207341, 'deDE', '', 'Der Markgraf mag fort sein, aber deswegen geben wir noch lange nicht auf. Wir werden unsere Rache noch bekommen.\n\nMoment... Ihr... seid aus Azeroth. Sagt, haben die Ungekrönten meine Nachricht erhalten?', 38134),
(207340, 'deDE', 'Wisst Ihr, der Gluthof ist gefährlicher, als er sich anhört. Ich glaube, Ihr werdet ihn lieben.', 'Wisst Ihr, der Gluthof ist gefährlicher, als er sich anhört. Ich glaube, Ihr werdet ihn lieben.', 38134),
(207339, 'deDE', 'Es ist gut, dass Euer Pakt für die Verteidigung unseres Reiches kämpft, $gSchlundgänger:Schlundgängerin;.', 'Es ist gut, dass Euer Pakt für die Verteidigung unseres Reiches kämpft, $gSchlundgänger:Schlundgängerin;.', 38134),
(207338, 'deDE', '', 'Der Markgraf mag fort sein, aber deswegen geben wir noch lange nicht auf. Wir werden unsere Rache noch bekommen.', 38134),
(207337, 'deDE', 'Steckt Euer Skalpell und Eure Nadeln weg, Nekrolord. Ich würde meine Haut gern behalten.', 'Steckt Euer Skalpell und Eure Nadeln weg, Nekrolord. Ich würde meine Haut gern behalten.', 38134),
(207335, 'deDE', 'Ihr hinterlasst da irgendeine Spur, $n. Ist das etwa... Faeglitter?', 'Ihr hinterlasst da irgendeine Spur, $n. Ist das etwa... Faeglitter?', 38134),
(207334, 'deDE', '$gSchlundgänger:Schlundgängerin;! Habt Ihr es schon satt, Haine zu hüten? Seid Ihr bereit für eine richtige Rauferei?', '$gSchlundgänger:Schlundgängerin;! Habt Ihr es schon satt, Haine zu hüten? Seid Ihr bereit für eine richtige Rauferei?', 38134),
(207333, 'deDE', 'Die Nekrolords setzen nicht auf Tricks, sondern auf Taktik, Nachtfae.', 'Die Nekrolords setzen nicht auf Tricks, sondern auf Taktik, Nachtfae.', 38134),
(207332, 'deDE', 'Nein. Ich werde Eurer Aufführung nicht beiwohnen.', 'Nein. Ich werde Eurer Aufführung nicht beiwohnen.', 38134),
(207331, 'deDE', 'Um einer Seele den Stolz auszutreiben, prügelt man ihn am besten aus ihr heraus.', 'Um einer Seele den Stolz auszutreiben, prügelt man ihn am besten aus ihr heraus.', 38134),
(207330, 'deDE', 'Passt auf, wo Ihr hintretet, Venthyr. Sonst bekommt Ihr noch Schleim an Eure Schuhe.', 'Passt auf, wo Ihr hintretet, Venthyr. Sonst bekommt Ihr noch Schleim an Eure Schuhe.', 38134),
(207329, 'deDE', 'Feiern. Intrigen. Warum fechten die Venthyr ihre Differenzen nicht einfach aus?', 'Feiern. Intrigen. Warum fechten die Venthyr ihre Differenzen nicht einfach aus?', 38134),
(207328, 'deDE', 'Meine Tage sind in der Tat vom Krieg beherrscht... doch meine Erinnerungen gehören mir.', 'Meine Tage sind in der Tat vom Krieg beherrscht... doch meine Erinnerungen gehören mir.', 38134),
(207327, 'deDE', 'Ihr werdet hier keine Meditationsbecken finden, $gKyrianer:Kyrianerin;.', 'Ihr werdet hier keine Meditationsbecken finden, $gKyrianer:Kyrianerin;.', 38134),
(207326, 'deDE', 'Kommt. Zeigt mir die eleganten Kampftechniken, die Ihr in der Bastion gelernt habt.', 'Kommt. Zeigt mir die eleganten Kampftechniken, die Ihr in der Bastion gelernt habt.', 38134),
(207324, 'deDE', 'Ich würde Euch ja ins Theater der Schmerzen einladen, aber Eure Gewänder könnten dabei schmutzig werden.', 'Ich würde Euch ja ins Theater der Schmerzen einladen, aber Eure Gewänder könnten dabei schmutzig werden.', 38134),
(207323, 'deDE', 'Wenn Euer sterblicher Rahmen verschlissen ist, können wir Euch einen neuen anfertigen.', 'Wenn Euer sterblicher Rahmen verschlissen ist, können wir Euch einen neuen anfertigen.', 38134),
(207321, 'deDE', 'Ihr verkörpert das Vermächtnis des Primus, $n.', 'Ihr verkörpert das Vermächtnis des Primus, $n.', 38134),
(207320, 'deDE', 'Es ist gut, dass Ihr Euch für uns entschieden habt, $n. Die anderen Pakte werden Euch um Eure Macht beneiden.', 'Es ist gut, dass Ihr Euch für uns entschieden habt, $n. Die anderen Pakte werden Euch um Eure Macht beneiden.', 38134),
(207319, 'deDE', 'Welches Schicksal auch kommen mag, ich werde es mir im Kampf verdient haben.', 'Welches Schicksal auch kommen mag, ich werde es mir im Kampf verdient haben.', 38134),
(207318, 'deDE', 'So \'nen Ort hat der alte Gaunah im Leben noch nich\' geseh\'n!\n\n<Gaunah winkt Euch mit einer Hand herüber und lehnt sich in Eure Richtung, um Euch etwas zuzuflüstern.>\n\nPsst! Keinen Mucks!\n\nDiese Mittler biet\'n allen möglich\'n Kram für uns \"Sterbliche\" an, un\' ich will rausfind\'n, was genau dieser Kram is\'.\n\nVielleicht findet der alte Gaunah ja \'n paar neue Amulette zum Verkauf\'n. Hehe.', '', 38134),
(207316, 'deDE', 'Oh. Noch eine Seele aus Azeroth.\n\nNein, keine Seele. Das Licht von An\'she wärmt noch immer Euer Fleisch.\n\nAch, könnte ich doch nur einen letzten Blick auf den Hochberg werfen. Wenn es die Erdenmutter so will, lebt meine Familie immer noch dort.\n\nViele hier brennen ungeduldig auf den Aufstieg und blicken voller Reue auf ihre Vergangenheit. Ich aber erfreue mich an den Erinnerungen an meine Liebsten. Hoffentlich halten sie auch mich in guter Erinnerung, auch wenn ich sie eines Tages loslassen muss.\n\nAber nicht heute. Nein. Nicht heute.', '', 38134),
(207315, 'deDE', 'Ich kenne Euch. Wir haben Seite an Seite auf dem Hochberg gekämpft. Ihr scheint gut in Schuss zu sein.\n\nIch hoffe, dass Kura und die Jungs nicht zu lange getrauert haben, und dass Kura sich geliebt fühlt. Doch einige Erinnerungen würde ich schon gerne behalten. Kura ans Herz zu drücken. Junior seinen Namen zu geben. Mycah beizubringen, sich wie ein Geist mit dem Messer zu bewegen. Wie ein Geist...\n\nIch weiß nicht mehr, welches Schicksal mich ereilt hat. Nur, dass es schnell ging. Was ist aus Dargrul geworden?\n\nNein. Nein, sagt es mir nicht. Das gehört in ein vergangenes Leben. Diese Wut, dieser Schmerz... Darum befreien uns die Kyrianer von unseren Erinnerungen.', '', 38134),
(207314, 'deDE', 'Genießt Ihr das ewige Zwielicht, Venthyr?', 'Genießt Ihr das ewige Zwielicht, Venthyr?', 38134),
(207313, 'deDE', 'Ich werde die Faszination der Venthyr für Stein nie verstehen.', 'Ich werde die Faszination der Venthyr für Stein nie verstehen.', 38134),
(207312, 'deDE', 'Wir zählen darauf, dass Ihr wachsam bleibt, Nekrolord.', 'Wir zählen darauf, dass Ihr wachsam bleibt, Nekrolord.', 38134),
(207311, 'deDE', 'Verbündete des Primus sind Verbündete des Waldes.', 'Verbündete des Primus sind Verbündete des Waldes.', 38134),
(207310, 'deDE', 'Wir dienen beide unseren Pakten und erfüllen unsere Pflichten.', 'Wir dienen beide unseren Pakten und erfüllen unsere Pflichten.', 38134),
(207309, 'deDE', 'Möge unser Wind Eure Schwingen heben, $gKyrianer:Kyrianerin;.', 'Möge unser Wind Eure Schwingen heben, $gKyrianer:Kyrianerin;.', 38134),
(207308, 'deDE', 'Der Hof der Nacht kann sich glücklich schätzen, Euch zu haben, $n.', 'Der Hof der Nacht kann sich glücklich schätzen, Euch zu haben, $n.', 38134),
(207307, 'deDE', 'Wir sind dankbar, dass Ihr auf der Seite unseres Paktes steht, $gSchlundgänger:Schlundgängerin;. Für Königin und Hain.', 'Wir sind dankbar, dass Ihr auf der Seite unseres Paktes steht, $gSchlundgänger:Schlundgängerin;. Für Königin und Hain.', 38134),
(207306, 'deDE', 'Ihr werdet hier keine stolzen Seelen finden, Venthyr.', 'Ihr werdet hier keine stolzen Seelen finden, Venthyr.', 38134),
(207305, 'deDE', 'Die Wilde Jagd steht Euch bei, Nekrolord.', 'Die Wilde Jagd steht Euch bei, Nekrolord.', 38134),
(207304, 'deDE', 'Geht mit Mut, $gKyrianer:Kyrianerin;.', 'Geht mit Mut, $gKyrianer:Kyrianerin;.', 38134),
(207302, 'deDE', 'Die Nacht umarme Euch, Venthyr. Auch wenn sie das sicher schon tut.', 'Die Nacht umarme Euch, Venthyr. Auch wenn sie das sicher schon tut.', 38134),
(207301, 'deDE', 'Wollt Ihr länger im Wald bleiben, Nekrolord?', 'Wollt Ihr länger im Wald bleiben, Nekrolord?', 38134),
(207300, 'deDE', 'Das Glück sei Euch hold, $gKyrianer:Kyrianerin;. Ich hoffe, die Bastion hat die Dürre gut überstanden.', 'Das Glück sei Euch hold, $gKyrianer:Kyrianerin;. Ich hoffe, die Bastion hat die Dürre gut überstanden.', 38134),
(207299, 'deDE', 'Die Haine stehen in Eurer Schuld, $n!', 'Die Haine stehen in Eurer Schuld, $n!', 38134),
(207298, 'deDE', 'Was für ein Zufall, Euch hier zu treffen, Venthyr. Vielleicht sogar ein zu großer Zufall.', 'Was für ein Zufall, Euch hier zu treffen, Venthyr. Vielleicht sogar ein zu großer Zufall.', 38134),
(207297, 'deDE', 'Was stinkt hier so? Oh, ein Nekrolord. War ja klar.', 'Was stinkt hier so? Oh, ein Nekrolord. War ja klar.', 38134),
(207295, 'deDE', 'Oh, Ihr gehört zu den Kyrianern! Aber Ihr habt keine Flügel, ganz im Gegensatz zu mir!', 'Oh, Ihr gehört zu den Kyrianern! Aber Ihr habt keine Flügel, ganz im Gegensatz zu mir!', 38134),
(207294, 'deDE', 'Oh, Ihr seid es, $n! Eure Aufführung war fantastisch!', 'Oh, Ihr seid es, $n! Eure Aufführung war fantastisch!', 38134),
(207289, 'deDE', 'Wenn ich daran denke, dass ich nur ein Jahr davon entfernt war, mir meinen Helm zu verdienen...\n\n<Admiral Taylor seufzt, obwohl er nicht mehr zu atmen braucht.>', '', 38134),
(207288, 'deDE', 'Gern geschehen.', '', 38134),
(207287, 'deDE', 'Ich weiß noch, dass Ihr mich getötet habt.', '', 38134),
(207286, 'deDE', 'Und was ist mit Euch, Koltira? Was wisst Ihr noch über Euren Tod?', '', 38134),
(207285, 'deDE', 'Es bringt nichts, darüber zu spekulieren. Wir können das Geschehene nicht mehr ändern. Wir können nur vorwärts gehen, so unaufhaltsam wie der Tod selbst.', '', 38134),
(207284, 'deDE', 'Verstehe. Was meint Ihr, welches Schicksal die Richterin wohl für uns gewählt hätte?', '', 38134),
(207283, 'deDE', 'Falls ja, dann kann ich mich nicht mehr daran erinnern.', '', 38134),
(207282, 'deDE', 'Erinnert Ihr Euch noch, was zum Zeitpunkt Eures Todes geschah, Thassarian? Haben die Kyrianer Eure Seele geholt?', '', 38134),
(207281, 'deDE', 'Stimmt auch wieder.', '', 38134),
(207280, 'deDE', 'Oder Sylvanas Windläufer.', '', 38134),
(207279, 'deDE', 'Wie zum Beispiel Genn Graumähne?', '', 38134),
(207278, 'deDE', 'Bolvar Fordragon ist nicht mehr der Lichkönig. Wenn wir schon von jemandem Befehle entgegennehmen müssen, kann ich mir deutlich schlimmere Anführer als ihn vorstellen.', '', 38134),
(207277, 'deDE', 'Und nun führen wir unsere Klingen im Namen desjenigen, der ihn trug.', '', 38134),
(207276, 'deDE', 'Der Helm der Herrschaft wurde zerschmettert. Ein merkwürdiger Gedanke. Er war so lange ein Symbol unseres Fluchs und unserer Pflicht.', '', 38134),
(207272, 'deDE', 'Wisst Ihr, wie ich gestorben bin, $n? Bei der Verteidigung von Quel\'Thalas, unter dem Kommando von Waldläufergeneralin Sylvanas Windläufer.\n\nSie gab ihr Leben, um ihr Königreich und ihr Volk zu retten... und wurde dafür mit dem Untod belohnt. Genau wie ich. Doch damit enden die Gemeinsamkeiten.\n\nSylvanas hat mehr Tod und Leid verursacht, als wir beide zu Lebzeiten ertragen mussten. Sie sperrte mich jahrelang in den Tiefen von Unterstadt ein, nachdem ich es gewagt hatte, einen vorübergehenden Waffenstillstand auszuhandeln.\n\nSelbst die Schwarze Klinge konnte meine Freilassung nicht bewirken. Sagt über die Bansheekönigin, was Ihr wollt... aber sie hat sowohl im Leben als auch im Tod nie nachgegeben.\n\nSylvanas war schon immer schwer zu Fall zu bringen.', '', 38134),
(207271, 'deDE', 'Ich griff erstmals zum Schwert, als die Seuche des Untodes sich in Lordaeron auszubreiten begann. Danach diente ich im Tod deutlich länger als Soldat, als ich es zu Lebzeiten getan hatte.\n\nDie Kriege der Lebenden wirken belanglos angesichts dessen, was ich jenseits des Schleiers gesehen habe. Ich habe der Allianz die Treue geschworen, und ich werde für ihre Bürger kämpfen, bis ich ein letztes Mal falle. Doch es gibt für sie weitaus größere existenzielle Bedrohungen als die Horde.\n\nVielleicht erkennen sie alle das nun, da der Himmel über Eiskrone aufgerissen ist und ihnen der Tod im Nacken sitzt. Das ist der Fluch der Lebenden.\n\nSie wissen erst, was wirklich zählt, wenn ihre Zeit gekommen ist.', '', 38134),
(207270, 'deDE', 'Ich hätte gern ein Konstrukt mit ordentlich Gewicht! Wenn ich springe, sollte der Boden unter der Wucht meines Aufpralls aufbrechen.', '', 38134),
(207269, 'deDE', 'Ich wünsche mir einen starken Rahmen mit vielen Haken, um Dinge daran aufzuhängen. Ich werde Euch und meine gesamten Forschungsmaterialien auf unseren vielen gemeinsamen Abenteuern tragen!', '', 38134),
(207268, 'deDE', '', '<Philia flüstert Euch zu.>\n\nBitte erzählt Emma nichts vom Schlund.\n\nIch... Ich glaube, das würde sie nicht verkraften.', 38134),
(207266, 'deDE', '', 'Oh, hallo. Ihr seht so aus, als würdet Ihr Euch hier auskennen.\n\nHabt Ihr meine Söhne gesehen, Jo, Schorsch und Jokkel? Sie müssten irgendwo hier sein.\n\nIch werde nicht gehen, bevor ich meine Jungs gefunden habe. Ich werde sie nicht noch einmal aus den Augen lassen.', 38134),
(207251, 'deDE', 'Ich erweise allen Ehre, die unserer Sache dienen.', 'Ich erweise allen Ehre, die unserer Sache dienen.', 38134),
(207250, 'deDE', '<Stanikos unterbricht.>\n\nNicht BENUTZEN. Sind nur... geliehen!', '<Stanikos unterbricht.>\n\nNicht BENUTZEN. Sind nur... geliehen!', 38134),
(207248, 'deDE', '<Stanikos starrt Euch an.>\n\nEure Dinge. Dinge, die Ihr angefasst habt. Ja.', '<Stanikos starrt Euch an.>\n\nEure Dinge. Dinge, die Ihr angefasst habt. Ja.', 38134),
(207247, 'deDE', 'Ihr meint... meine Sammlung? Sie ist schön, nicht?', 'Ihr meint... meine Sammlung? Sie ist schön, nicht?', 38134),
(207241, 'deDE', 'Nein! Ihr könnt Euch nicht verstecken, Ve\'nari! Ihr... AHH!', '', 38134),
(207231, 'deDE', '', 'Wat?', 38134),
(207230, 'deDE', 'Ich habe gelernt, dass Taten mehr als Worte sagen, $uSchlundgänger:Schlundgängerin;. Hier, wir beide müssen unsere Absichten belegen.', '', 38134),
(207229, 'deDE', 'Wenn die Anführer fallen, sind auch ihre Anhänger verloren... Die Inquisitoren bilden da keine Ausnahme.', '', 38134),
(207228, 'deDE', '', 'Gahijis Pfad führt in viele dunkle Tiefen... Was werdet Ihr dort finden?', 38134),
(207227, 'deDE', '', 'Eure Königreiche und Stämme haben hier keine Macht. Schwört Eurer alten Gefolgschaft ab und befreit Euch von ihr!', 38134),
(207226, 'deDE', '', 'Eure sterbliche Kraft ist hier bedeutungslos. Läutet die Glocke und seht, wie schwach Ihr tatsächlich seid.', 38134),
(207225, 'deDE', '', 'Gesteht Euch Eure Schande ein. Durchlauft jede schreckliche Erinnerung, die Euch bedrückt. Haltet sie aus, damit Ihr sie überwinden könnt!', 38134),
(207224, 'deDE', '', 'Ob Ihr wollt oder nicht, für Eure Ungerechtigkeiten werdet Ihr hier zurechtgewiesen. Ihr habt die Wahl, ob Ihr von dieser Erfahrung etwas lernen wollt oder nicht.', 38134),
(207223, 'deDE', '', 'WAT?!', 38134),
(207222, 'deDE', '', 'Wat?', 38134),
(207221, 'deDE', 'Ja, ich vermute, dass es Euch überrascht, eine Person wie mich unter den Tyrannen und Adligen zu finden, die so oft die Käfige von Revendreth füllen.\n\nEine ruhige Seele, die man leicht übersehen kann.\n\nUnd in der Tat sind meine Taten kaum der Rede wert.\n\nWas meine Worte betrifft, sieht es allerdings ganz anders aus.\n\nMithilfe von Einflüsterungen habe ich zahllose andere dazu bewogen, in meinem Namen, zu meinem Vorteil und zu ihrem eigenen Nachteil schreckliche Dinge zu begehen.\n\nOffenbar haben die Venthyr von solchen Machenschaften keine hohe Meinung.', '', 38134),
(207220, 'deDE', 'Was möchte denn wohl $uder:die; legendäre $uSchlundgänger:Schlundgängerin; von so einer schlichten Seele wie mir wissen?', '', 38134),
(207219, 'deDE', '<Werimu lacht.>\n\nUngefähr alles, was man sich vorstellen kann.\n\nIch bin immer noch nicht davon überzeugt, dass alles, was diese Venthyr eine Sünde nenne, wirklich falsch war, aber selbst die Sachen, über die wir uns einig sind, reichen aus, um meinen Sündenstein vollständig aufzufüllen.\n\n<Werimu versinkt für einen Moment in Gedanken.>\n\nIch... Ich bin Verwalterin Pancha für ihre Geduld mit mir sehr dankbar. Ich habe gesehen, wie die anderen die Seelen in ihrer Obhut behandeln.\n\nIch versuche WIRKLICH, mich zu bessern.', '', 38134),
(207218, 'deDE', 'Seid Ihr allein in das Land der Toten gereist? Ihr müsst besser auf Euch aufpassen...\n\nIch habe auch einst geglaubt, es sei unnötig, Freunde an meiner Seite zu haben. Ich hielt sie für ein Zeichen der Schwäche, das gegen mich verwendet werde könnte.\n\nIn diesen Landen habe ich gelernt, wie falsch ich damit lag.', '', 38134),
(207213, 'deDE', '', 'Gahiji hat Dinge gestohlen... verlorene Dinge... vergessene Dinge...\n\nNiemand hat sie vermisst, bis...\n\nGahiji ist zu weit gegangen... hat Regeln vergessen, die nicht vergessen werden dürfen...\n\nGahiji hat Fehler gemacht...', 38134),
(207212, 'deDE', '', 'Gahiji möchte frei sein, wie Sterbliche...\n\nWeglaufen von hier... Weglaufen vor der Vergangenheit...', 38134),
(207210, 'deDE', 'Manchmal bereue ich, diese Steinscheusale erschaffen zu haben...', '', 38134),
(207209, 'deDE', 'Und wie ich schon gesagt habe: Es gibt keinen Titel namens \"Sündensteinherold\".', '', 38134),
(207208, 'deDE', 'Das ist kein Wettbewerb, Lemet...', '', 38134),
(207207, 'deDE', 'Das werden wir noch sehen... Der Sündensteinherold siegt erneut!', '', 38134),
(207206, 'deDE', 'Würd gern mal sehen, wie der alte Temel das macht. Darum bin ich der Sündensteinherold!', '', 38134),
(207205, 'deDE', 'Ich habe diese Woche jede Menge Sündensteinfragmente für Euch gefunden, Meister!\n\nÖffnet die Truhe und seht selbst.', '', 38134),
(207203, 'deDE', '', 'Eine Ritualglocke zu läuten, bedeutet, ein Opfer zu bringen.\n\nJede Glocke verlangt etwas Bestimmtes von der Seele, die sie läutet. Wird sie nicht korrekt verwendet, wie es bei den Verkommenen in diesen Hallen der Fall ist, führt das Opfer zu zwecklosem Leiden.\n\nDie Weisheit der Eingeschworenen liegt in der richtigen Einschätzung, was jede Seele opfern muss, um auf dem langen Pfad der Sühne voranzukommen. Dann versuchen sie, die betreffende Seele zu überzeugen, das notwendige Opfer im eigenen Interesse zu leisten.\n\nDas ist nicht immer einfach, denn einige der Seelen sind zu stur oder einfach nicht klug genug, um überhaupt je eine Glocke zu läuten.\n\nAber Ihr könnt die Wahrheit dahinter sehen: Ein Opfer zu leisten, um voranzukommen, ist gar kein Opfer.', 38134),
(207202, 'deDE', '', 'Ohne eine für das Ritual vorbereitete Seele haben wir zurzeit keine Verwendung für eine Ritualglocke.\n\nFane und die anderen Archivare der Eingeschworenen verstecken die Glocken, wenn sie nicht gebraucht werden, damit die verkommenen Ritualisten in diesen Hallen keinen Zugriff darauf bekommen.\n\nWenn eine Seele bereit ist, lasse ich die Gräber die benötigte Glocke herausholen. Ich glaube, es wird bald wieder so weit sein.', 38134),
(207201, 'deDE', '', '<Verwalterin Pancha grinst.>\n\nIch dachte mir schon, dass Ihr Euch für diese Glocke interessieren würdet, $gSchlundgänger:Schlundgängerin;. Sie dürfte sich für Euch... als nützlich erweisen, falls Ihr eines Tages nach Revendreth zurückkehrt und meiner Obhut unterstellt werdet.\n\nDie Glocke der Abkehr hilft jenen, denen es schwerfällt, sich von den Pflichten und Treueschwüren ihrer sterblichen Leben zu befreien. Eure Allianzen, Eure Königreiche, Eure Horden... sie existieren hier nicht und können Eurem Leben keinen Sinn mehr geben.\n\nLäutet die Glocke und befreit Euch von den Bindungen, die Euch fesseln.', 38134),
(207200, 'deDE', '', 'Ah, meine persönliche Lieblingsglocke, die Glocke der Unterwerfung.\n\nSie verlangt nur, dass man einen Teil seiner Macht abgibt.\n\nDas ist eigentlich ganz einfach, aber für stolze Seelen, die mittels Gewalt und Kampf zu Erfolg gelangt sind, ist dies eine schreckliche Vorstellung.\n\nWenn eine solche Seele ihre Macht abgegeben hat, kann sie wie zuvor weiter in den Kampf ziehen.\n\nAber in ihrem geschwächten Zustand verliert sie Kampf um Kampf, bis sie gebrochen und willens ist, sich den Niederstehenden zu ergeben.\n\nErst dann ist das Ritual abgeschlossen.', 38134),
(207199, 'deDE', '', 'Seid Ihr empfänglich für Mitgefühl mit anderen?\n\nDas ist nicht bei allen so. Die Glocke der Schande hilft jenen, die nicht gewillt oder in der Lage sind, genug Empathie aufzubringen, um die Folgen ihrer Missetaten in der Vergangenheit zu verstehen. Die Glocke offenbart diese Taten und konfrontiert sie mit ihnen.\n\nDie schändlichen Taten werden für alle sichtbar ans Licht gebracht. Dann werden die rachesüchtigen Seelen in unserer Gruft für eine Weile auf die zu Läuternden losgelassen, damit diese Gelegenheit bekommen, ihre Schandtaten einzusehen.\n\nFür jene, die der Empathie fähig sind, ist dies schwer zu ertragen, aber das gilt eben nicht für jene, die diese Prozedur durchlaufen.', 38134),
(207198, 'deDE', '', 'Die Glocke der Zurechtweisung zählt zu unseren strengsten Werkzeugen und ist jenen widerspenstigen Seelen vorbehalten, die ständig daran erinnert werden müssen, dass sie hier nicht mehr das Sagen haben.\n\nWenn man die Glocke läutet, weist man sich zunächst selbst zurecht, aber das ist eher selten der Hauptzweck. Für die Selbstreflexion haben wir bessere Werkzeuge.\n\nWichtiger ist, dass man sich so empfänglicher gegenüber den Zurechtweisungen der anderen macht, sodass alle Venthyr mehr Macht über einen erlangen.\n\nBei dem erhöhten Maß an Verkommenheit der Venthyr in diesen Hallen ist dies natürlich viel gefährlicher. Ich behalte meine Seelen gut im Auge, solange sie unter dem Einfluss dieser Glocke stehen.', 38134),
(207197, 'deDE', '', 'Die Glocke der Beichte ist ein einfaches, aber mächtiges Werkzeug, um Seelen zu helfen, die ihre Schuld überwinden wollen.\n\nNur jene, die bereit sind, eine Sünde einzugestehen, können sie läuten. Dadurch wird die Beichte dieser Seelen allen mitgeteilt, die sich in den Hallen aufhalten.\n\nDas öffentliche Eingeständnis ihrer Schuld hilft den Seelen, ihr Versagen zu überwinden, aber dadurch werden auch die verkommenen Venthyr dieser Hallen auf ihre Anwesenheit aufmerksam gemacht.', 38134),
(207196, 'deDE', '', 'Die meisten sind vorerst in den Sühnengruften untergebracht, wo sie sich auf ihr nächstes Ritual vorbereiten.\n\nKeine Sorge, ich habe sie weit von den verkommenen Anhängern des Oberster Kämmerers untergebracht. Die Seelen sind in Sicherheit, während sie mit der Schuld ihrer Missetaten zu kämpfen haben.\n\nDie Anklägerin selbst hat die Gruften vor jeglicher Manipulation geschützt.', 38134),
(207195, 'deDE', '', 'Nur ganz langsam, sofern die Vergangenheit als Ausblick auf die Zukunft dienen sollte.\n\nIcks Neigung zur Gewalt und ihr unkontrollierter Hass auf Verwahrstätten allgemeinen Wissens waren die Ursache für die vielen Sünden, für die sie nun büßen muss. Und es gibt nur wenige Werkzeuge, mit denen sie Sühne erlangen kann.\n\nVorerst habe ich ihr ein strenges Regiment aus Einzelunterbringung und einfach zu kommunizierenden Aufgaben auferlegt, damit sie den Wert dessen, was sie im Leben allzu schnell ablehnte, zu schätzen lernt.\n\nHeute wird sie dabei helfen, verlorene Sündensteinfragmente einzusammeln und damit den Archivaren zu helfen.', 38134),
(207194, 'deDE', '', 'Warum fragt Ihr ihn nicht selbst?\n\nKhongordzolo ist schon seit langer Zeit in meiner Obhut, aber er hat jetzt endlich Kontrolle über seine Zunge erlangt und kann Worte mit Wahrheitsgehalt aussprechen.\n\nErst nachdem ich seine unablässigen Lügen und Halbwahrheiten eindämmen konnte, war ich in der Lage, ihn mit den Konsequenzen seiner Taten in den sterblichen Reichen zu konfrontieren.\n\nJetzt muss er sein Ansehen mithilfe rechter Worte und Taten wiederherstellen. So wie wir alle, nicht wahr?', 38134),
(207193, 'deDE', '', 'Werimu ist sich seiner prekären Lage überhaupt noch nicht bewusst!\n\nEr hat schon viel Buße geleistet, aber die Last auf seiner Seele war riesig, als er hier ankam. So etwas hatte ich zuvor noch nie gesehen.\n\nEr wird vor den hungrigen Tentakeln des Schlunds nicht sicher sein, bis er nicht für seine letzte Sünde gebüßt hat.\n\nZurzeit versuche ich, seine Konzentration hochzuhalten, indem ich ihm eine vertraute Aufgabe gegeben habe: die Zerschlagung einer rivalisierenden Organisation.', 38134),
(207192, 'deDE', '', 'Gahiji hat sich als schwierige Herausforderung erwiesen.\n\nSie hat ihre Fehler schnell eingesehen, aber sie tut sich schwer damit, Reue zu empfinden.\n\nIch fürchte, dass sie noch eine ganze Weile bei uns bleiben wird.\n\nAber immerhin ist sie bemüht, sich hier nützlich zu machen. So habe ich sie damit beauftragt, die Sühnengruften von dem Dreck der verkommenen Venthyr zu säubern.', 38134),
(207191, 'deDE', '', 'Yevkek hat auf dem Pfad der Sühne bereits ein weiteres Stück zurückgelegt als die anderen Seelen in meiner Obhut.\n\nNachdem er zu Lebzeiten seine Stärke und Gerissenheit genutzt hat, um die Schwächsten der Gesellschaft auszubeuten, lernt er nun, diese Fähigkeiten einzusetzen, um andere zu beschützen und zu retten.\n\nWenn eine meiner anderen Seelen verloren geht oder vor der Gerechtigkeit zu fliehen versucht, überlasse ich Yevkek die Verantwortung, sie auf den rechten Pfad zurückzubringen.', 38134),
(207190, 'deDE', '', 'Selbst die Gräber sind Opfer der Verkommenen, die durch diese Hallen streifen.\n\nSie werden gezwungen, unter den verkommenen Anhängern des Obersten Kämmerers zu schuften, und müssen ihre eigentlichen Pflichten vernachlässigen.\n\nDiese Ritualglocke liegt beschädigt auf dem Podest, während sie dringend benötigt wird, den Seelen zur Sühne zu verhelfen!', 38134),
(207189, 'deDE', '', 'Mithilfe der reparierten Glocke können wir dieser Seele auf ihrem Pfad zur Sühne weiterhelfen.\n\nMöchtet Ihr die Glocke läuten und mir helfen, dieser Seele zu zeigen, dass ein Opfer den wahren Pfad zur Sühne darstellt?\n\nWenn wir selbst keine Demut zeigen und unsere Fehler eingestehen, wie können wir es dann von ihnen verlangen?', 38134),
(207175, 'deDE', '', 'Die Pfade zur Sühne der Seelen unterscheiden sich, aber eines haben sie alle gemein.\n\nEin würdiges Ende lässt sich nur durch Opfer und Leid erlangen.\n\nWenn die Alternative eine Ewigkeit im Schlund bedeutet, welchen Preis wäre man dann nicht bereit zu zahlen?', 38134),
(207168, 'deDE', 'Mein Pfad ist, mich den Schwachen zuzuwenden und jene zu retten, die ich ausgebeutet habe. Geht in Gnade, $uSterblicher:Sterbliche;.', '', 38134),
(207167, 'deDE', '', 'Wenn die Gräber die Glocke der Beichte repariert haben, WERDET Ihr sie läuten.', 38134),
(207163, 'deDE', 'Ich war so vieles...\n\nDer Wolf, der die Schafherde bedroht.\nDer Hai, der um das Floß kreist.\nDer Falke auf der Jagd nach dem Hasen.\n\nDie Verlorenen waren nie in Sicherheit vor mir.', '', 38134),
(207161, 'deDE', 'Ihr habt immer noch Zeit, $uSterblicher:Sterbliche;.\n\nÄndert Euren Weg. Meidet mein Schicksal.\n\nDer Pfad zur Sühne dieses Venthyr möge Erlösung sein, aber er ist...\n\n<Die Seele erschaudert und verstummt.>', '', 38134),
(207154, 'deDE', 'Was treibt Ihr hier? Könnt Ihr nich\' seh\'n, dass mir nichts mehr geblieb\'n ist?\n\nDie Elfen haben das Land meiner Vorväter gestohl\'n. Un\' dann mein Auge... und mein Leb\'n.\n\nJeder nimmt sich nur von uns. Aber wir geb\'n nich\' auf und vergess\'n nie.\n\nEines Tages werdet Ihr verlier\'n... un\' zwar alles!', '', 38134),
(207152, 'deDE', '', 'Von den anderen hat keiner überlebt. Nur ich allein.\n\nIhr habt ja selbst gemerkt, was für ein gefährlicher Ort der Schlund ist. Das Reisen in einer großen Gruppe kann zwar die Illusion von Sicherheit vermitteln, in der Realität aber gibt man dadurch nur ein noch verführerischeres Ziel ab.\n\nWir haben das auf die harte Tour gelernt. Wir wurden immer weiter dezimiert, bis wir so wenige waren, dass wir uns effizient verstecken konnten. Das hat die Jagd auf uns etwas verlangsamt... aber sie hörte nie auf.\n\nAls nur noch ich übrig war, habe ich mich mit meiner Lage als einzige Wahrheit abgefunden. Die Einsamkeit bot die beste Chance auf ein Überleben.\n\nZumindest, bis Ihr hier eintraft.', 38134),
(207149, 'deDE', '', 'Es ist bedauernswert, dass sie ein solches Schicksal erleiden. Aber Ihr müsst bedenken, dass sie ebenso unsere Feinde sind wie die Schlundgebundenen.\n\nDiese Mittler waren vom ersten Moment ihrer Ankunft an verloren. Und selbst wenn Ihr sie retten könntet, würdet Ihr uns beiden damit nur den Untergang bringen.\n\nIch gebe zu, dass dies recht unbarmherzig ist. Aber nur so kann man im Schlund überleben.\n\nIhr solltet Euch das gut merken.', 38134),
(207143, 'deDE', '', 'Wächterin Casad erwartet Euch im Konservatorium der Königin! Sie kann es kaum abwarten, mit Euch zu beginnen.\n\nTretet neben mir in den Pilzkreis, um zum Konservatorium zu reisen.', 38134),
(207142, 'deDE', '', 'Wenn in dieser endlosen Dunkelheit nichts vor Euch wartet außer die Zeit, dann werden Geschichten wertvoller als Gold.', 38134),
(207119, 'deDE', '', 'Oh, ich bin mir sicher, dass Ihr sie mittlerweile ganz gut kennt. Es ist ausgeschlossen, dass $uein Sterblicher:eine Sterbliche; wie Ihr ohne ihre Hilfe einen Ausflug in den Schlund überleben könnte.\n\nErzählt mir alles über Eure Zusammenarbeit mit Ve\'nari.\n\nSollte sie sich entschlossen haben, nicht mit Euch nach Oribos zurückzukehren, wird auch sie ein paar Fragen beantworten müssen.', 38134),
(207118, 'deDE', '', 'Natürlich, natürlich. Das führt mich also zur letzten Frage.\n\nHat sich Ve\'nari als gute Verbündete erwiesen, während Ihr Euch im Schlund bewährt habt?\n\n<Bevor Ihr antwortet, erinnert Ihr Euch daran, dass Ve\'nari Euch dringlich aufgetragen hat, Eure Zusammenarbeit niemandem gegenüber zu erwähnen.>', 38134),
(207113, 'deDE', '', 'Ah, dann bleibt es ein gefährlicher Ort, um Geschäfte zu betreiben. Das führt mich zu meinem nächsten Punkt.\n\nFrage zwei!\n\nFindet Ihr es herausfordernd, im beständigen und beklemmenden Schrecken des Schlunds zu überleben?', 38134),
(207112, 'deDE', '', 'Danke, dass Ihr bereit seid, Euer Wissen über den Schlund mit uns zu teilen.\n\nFrage eins:\n\nStimmt es, dass die Streitkräfte des Kerkermeisters größer und stärker geworden sind?', 38134),
(207111, 'deDE', '', 'Hallo, $uSchlundgänger:Schlundgängerin;, es ist mir ein Vergnügen, Eure Bekanntschaft zu machen!', 38134),
(207110, 'deDE', '', 'Wie aufregend!', 38134),
(207109, 'deDE', '', 'Ich werde sie nicht prüfen oder neu abstimmen. Auf diese Weise wird es eine Überraschung, was passiert, wenn sie aktiviert werden.', 38134),
(207108, 'deDE', '', 'Ich kann weitere Reparatursätze für Euch herstellen, wenn Ihr die notwendigen durchfluteten Rubine habt.', 38134),
(207107, 'deDE', 'In Revendreth gibt es keinen Mangel an Spiegeln, die repariert werden wollen, $uSchlundgänger:Schlundgängerin;.', '', 38134),
(207105, 'deDE', '', 'Ihr könnt gerne den Spiegelreparatursatz verwenden, den ich aus dem Glutdistrikt mitgebracht habe.', 38134),
(207104, 'deDE', 'In der ganzen Halle sind zerbrochene Spiegel, mit denen Ihr üben könnt.', '', 38134),
(207103, 'deDE', '', 'Oh, da gibt es vieles, was ich Euch lieber nicht erzählen möchte. Und sicherlich gibt es vieles, was Ihr mir lieber nicht erzählen möchtet.\n\nVertrauen erfordert Diskretion. Ohne kann man keine Geschäfte machen.\n\nUnd ich habe Euch bereits mein wertvollstes Geheimnis verraten: den Standort dieser Zuflucht. Im Gegenzug möchte ich also um Euer Vertrauen bitten.', 38134),
(207101, 'deDE', '', 'Mit Eurer Hilfe wird der Ardenwald den Zyklus bewahren.\n\nIch übertrage Euch mein Konservatorium.\n\nSprecht mit Wächterin Casad. Sie wird Euch weitere Anweisungen geben.', 38134),
(207100, 'deDE', '', 'Alle Dinge enden.\n\nAber am Ende wartet ein neuer Beginn. Das ist der Kreislauf der Natur.\n\nDie Geister in meinem Hain sind für den Zyklus wichtig. Ohne sie stirbt die Natur selbst im sterblichen Reich. Jeder Verlust ist eine Tragödie, jeder gerettete Geist ein Triumph.\n\nDieser Geist gehört einer, die Ihr aus dem sterbenden Hain und vor dem Wahnsinn der Drust gerettet habt. Heute erwacht sie aus langem Schlaf und kehrt in ihre Welt zurück. Mit Eurer Hilfe haben wir unsere heilige Pflicht erfüllt.', 38134),
(207097, 'deDE', '', 'Dass die Winterkönigin Euch persönlich in der Handhabung der Wildsamen unterrichtet, ist in der Tat eine Ehre.', 38134),
(207087, 'deDE', '', 'Das hat recht viel Stygia gekostet... Aber das ist wohl der Preis für das Geschäft.', 38134),
(207065, 'deDE', 'Mehr als Eurem? Aber natürlich. Ihr habt Euren Vertrag gebrochen und das Kartell verraten. Und jetzt werdet Ihr den Preis dafür zahlen!', '', 38134),
(207063, 'deDE', '', 'Ihre Schreie sind nicht so zufriedenstellend wie die der Stacheleber, aber das passt schon.', 38134),
(207012, 'deDE', '$n? Ich bin froh, Euch nach all der Zeit wiederzusehen. Ganz besonders an diesem seltsamen Ort.\n\nAls ich gehört habe, dass der Schleier zu den Schattenlanden zerrissen worden war, wusste ich, dass ich den Übergang wagen musste. Ungeachtet des Risikos.\n\nWie Ihr wisst, starb meine Frau im Kindbett... und unser Sohn kurz danach. Ich muss wissen, ob sie in der Obhut unserer Vorfahren in Sicherheit sind. Ich könnte die Vorstellung, dass ihre Geister in Gefahr sind, nicht ertragen.\n\nIch bitte Euch... Wenn Ihr Lezas Seele begegnet, dann sagt Ihr, dass unser Sohn Kor sich prächtig entwickelt. Und dass ich sie liebe. Auf ewig.\n\nUnd dass ich sie finden werde, um jeden Preis.', '', 38134),
(207011, 'deDE', 'Es ist eine Erleichterung, hier an diesem seltsamen Ort ein Gesicht aus Azeroth zu sehen.\n\nAls ich gehört habe, dass der Schleier zu den Schattenlanden zerrissen worden war, wusste ich, dass ich den Übergang wagen musste. Ungeachtet des Risikos.\n\nIhr müsst verstehen, dass ich... zwei Personen verloren habe, denen meine Liebe galt. Ich muss wissen, ob sie in der Obhut unserer Vorfahren in Sicherheit oder ihre Geister in Gefahr sind.\n\nIch bitte Euch... Wenn Ihr der Seele meiner Frau, Leza Fernschreiter, begegnet, dann sagt Ihr, dass unser Sohn Kor sich prächtig entwickelt. Und dass ich sie liebe. Auf ewig.\n\nUnd dass ich sie finden werde, um jeden Preis.', '', 38134),
(207009, 'deDE', '', 'Ich fürchte, dass sich die Verwendung eines meiner interessanteren Geräte als recht schmerzvoll für Euch erweisen wird.', 38134),
(207008, 'deDE', '', 'Kommt jetzt, $uSchlundgänger:Schlundgängerin;. Ich glaube, wir beiden haben vieles zu besprechen.', 38134),
(207007, 'deDE', '', 'Nein, Ve\'brax. Ihr werdet vielleicht den Schlund verlassen... aber Ihr werdet nie mehr zum Kartell zurückkehren.', 38134),
(207006, 'deDE', 'AGH! Was... tut Ihr... da...?!', '', 38134),
(207005, 'deDE', '', 'Wohl kaum. $uSchlundgänger:Schlundgängerin;, lasst uns einfach dieses Ärgernis aus dem Weg räumen.', 38134),
(207003, 'deDE', '', 'Habt Ihr geglaubt, sie würden dem Wort eines Assassinen glauben?', 38134),
(207002, 'deDE', 'Ich habe versucht, Euch zu warnen, $uSchlundgänger:Schlundgängerin;, aber Ihr wolltet ja nicht hören.', '', 38134),
(207000, 'deDE', 'Sie hat ihre früheren Geschäftspartner verraten und wird dasselbe mit Euch machen, wenn sie erfährt, dass wir beide gesprochen haben. Glaubt ihren Lügen nicht.', '', 38134),
(206999, 'deDE', 'Bevor ich mich in meinen sicheren Hafen zurückziehe, muss ich Euch vor Ve\'nari warnen. Sie ist nicht die, die sie vorgibt zu sein.', '', 38134),
(206995, 'deDE', 'Wir sind hergekommen, um unsere Verluste durch Ve\'naris Verrat wiedergutzumachen. Oh, was waren wir nur für Narren...', '', 38134),
(206988, 'deDE', 'Gefangen... gefoltert... gebrochen...\n\nXeros... bringt andere... ins Bestienlabyrinth...\n\nHaben... bei unserer Aufgabe... versagt...', '', 38134),
(206984, 'deDE', 'Ich habe ihm gesagt, ich komme wieder! Ich würde ihn nur ungern enttäuschen.', '', 38134),
(206983, 'deDE', 'Ich bin von einem üblen Zauberer in Ketten gelegt worden!\n\nBefreit mich, damit ich ihm beibringe, was das Beste im Leben ist! Und im Tod, nehme ich an.', '', 38134),
(206970, 'deDE', 'Diese Fledermaus scheint Euer Ausweg aus den Hallen der Sühne zu sein.', 'Diese Fledermaus scheint Euer Ausweg aus den Hallen der Sühne zu sein.', 38134),
(206969, 'deDE', 'Geistgeschmiedete Aegis, Sicherheitsschild mit Wumms!\n\nFeinde lauern? Bereiten großen Angriff vor?\n\nMit Aegis ist alles sicher! Absorbiert Schaden und feuert dann einen Teil auf Feinde zurück! Huu-huu!', '', 38134),
(206968, 'deDE', 'Ring des Schutzes, bringt Sicherheit!\n\nIhr müsst bleiben, wo Ihr seid, aber Feinde fernhalten?\n\nGanz leicht! Aktiviert Ring und die Feinde bleiben weg! Ihr seid sicher, könnt durchschnaufen.', '', 38134),
(206967, 'deDE', 'Phiole des Gleichmuts, leckere Erfrischung!\n\nFühlt Ihr Euch schwach? Braucht Ihr Stärkung?\n\nPhiole heilt Euch und Ihr kämpft weiter! Mit Provostengarantie!', '', 38134),
(206966, 'deDE', 'Phiole voll Lichfrost, sichert Euch Abstand!\n\nVon Gegnern überrannt? Müsst abhauen?\n\nKeine Sorge! Zerbrecht die Phiole auf dem Boden und friert die Feinde ein. Dann lauft Ihr!', '', 38134),
(206965, 'deDE', 'Verzeiht, $n! Ausrüstung nicht für Euch!\n\nNur für Seelenbande. Hilft ihnen bei Prüfungen!', '', 38134),
(206959, 'deDE', 'Huu-huu! Kann mit großer Sache helfen, aber nur einmal. Braucht Zeit, bis ich wieder helfen kann. Ein paar Stunden. Sehr, sehr beschäftigt!', 'Huu-huu! Kann mit großer Sache helfen, aber nur einmal. Braucht Zeit, bis ich wieder helfen kann. Ein paar Stunden. Sehr, sehr beschäftigt!', 38134),
(206958, 'deDE', 'Was kann ich für Euch tuu-huun? Kann nur mit einer Sache helfen, also wählt gut! Braucht Zeit, bis ich wieder helfen kann. Eine Stunde. Sehr beschäftigt!', 'Was kann ich für Euch tuu-huun? Kann nur mit einer Sache helfen, also wählt gut! Braucht Zeit, bis ich wieder helfen kann. Eine Stunde. Sehr beschäftigt!', 38134),
(206954, 'deDE', 'Jetzt, wo Ihr es sagt: Die haben so einen großen grünen Typen hier vorbeigeschleppt.\n\nDer war schon schwer zu übersehen. Hat immer \"Lothar\" gesagt oder so was.\n\nWenn Ihr mich freilasst, sage ich Euch alles, was ich weiß.', '', 38134),
(206949, 'deDE', 'Was das denn? Sterblich? Hier? Und am Leben? Ich schein wohl langsam durchzudrehen!\n\nIhr könnt nicht echt sein.', '', 38134),
(206948, 'deDE', 'Da ist sie... Die Schrift des Sieges! Wollt Ihr die Ehre wahrnehmen, $n? Für den Fall, dass dies eine tödliche Falle ist oder so.', '', 38134),
(206944, 'deDE', 'Verzweigt nochmal! Ich habe mich lange Zeit um Marasmius gekümmert. Ich kenne sogar ein paar der alten Routen, die er vergessen hat!$b$bIrgendetwas sagt mir, dass auch Ihr etwas über diese verborgenen Plätze wisst. Wenn Ihr einmal dorthin zurückwollt, kann ich Euch helfen dort hinzukommen, wenn es Euch interessiert.', 'Verzweigt nochmal! Ich habe mich lange Zeit um Marasmius gekümmert. Ich kenne sogar ein paar der alten Routen, die er vergessen hat!$b$bIrgendetwas sagt mir, dass auch Ihr etwas über diese verborgenen Plätze wisst. Wenn Ihr einmal dorthin zurückwollt, kann ich Euch helfen dort hinzukommen, wenn es Euch interessiert.', 38134),
(206941, 'deDE', 'Ihr... Ich habe Euch nicht erkannt. Wie seid Ihr den ganzen Weg bis hierher gekommen? Diese Junglinge heutzutage....$b$bIch weiß nicht, wie Ihr das gemacht habt, aber Ihr habt Euch ganz schön verstrickt, oder? Ich werde Euch dieses Mal verlässlich zurück zum Ardenwald bringen. Nur weil ich alt bin und sich niemand mehr um mich schert, kann ich ja trotzdem jemandem helfen, der Probleme hat.', '', 38134),
(206933, 'deDE', '', 'Euer Geist ist überfüllt mit Stolz. Soll ich Euch helfen zu büßen?', 38134),
(206931, 'deDE', 'Dort sollte sie weniger Schmerzen erleiden.', '', 38134),
(206930, 'deDE', 'Prinz Renathal hat die Autorität der Naaru auf den Turm von Morgenfels übertragen. Das ist der Ort, an dem sie erstmals aufgetaucht sind und der am meisten vom Licht durchtränkt ist.', '', 38134),
(206929, 'deDE', 'Z\'rali ist zu schwach, um nach Hause zurückzukehren. Und ihre bloße Anwesenheit ist eine Gefahr für jeden Venthyr in ihrer Nähe.', '', 38134),
(206912, 'deDE', '\'Natürlicher Schwung\' ist eine passive Fertigkeit, die es Euch ermöglicht, in Seelengestalt \'Flackern\' in Eure Bewegungsrichtung anzuwenden, statt in Eure Blickrichtung.', '\'Natürlicher Schwung\' ist eine passive Fertigkeit, die es Euch ermöglicht, in Seelengestalt \'Flackern\' in Eure Bewegungsrichtung anzuwenden, statt in Eure Blickrichtung.', 38134),
(206908, 'deDE', 'Ihr findet den Meister des Konflikts und seine verschiedenen Händlerfreunde an der Seite der Enklave.', 'Ihr findet den Meister des Konflikts und seine verschiedenen Händlerfreunde an der Seite der Enklave.', 38134),
(206897, 'deDE', 'Ich bin nur ein Echo des Wissens des gefallenen Paragons, aber ich sehe, dass Ihr nach Wissen strebt.\n\nLest meine Geschichte. Dann können wir darüber sprechen, was Ihr obendrein erlernen möchtet.', 'Ich bin nur ein Echo des Wissens des gefallenen Paragons, aber ich sehe, dass Ihr nach Wissen strebt.\n\nLest meine Geschichte. Dann können wir darüber sprechen, was Ihr obendrein erlernen möchtet.', 38134),
(206896, 'deDE', 'Ihr habt Kobri gefunden! Danke!', 'Ihr habt Kobri gefunden! Danke!', 38134),
(206895, 'deDE', 'Kobri ist Wein holen. Kobri schon lange weg. Ohne Wein fangen wir nicht an!\n\nIhr findet Kobri, ja?', 'Kobri ist Wein holen. Kobri schon lange weg. Ohne Wein fangen wir nicht an!\n\nIhr findet Kobri, ja?', 38134),
(206889, 'deDE', 'Für die Erdenmutter!', '', 38134),
(206886, 'deDE', 'Möge die Vorsehung Euch leiten.\n\nIch bewahre viele unserer Aufzeichnungen. Wir erhalten die Visionen, die uns die verwobenen Stränge des Schicksals im Dienste der Vorsehung offenbaren.\n\nWas kann ich Euch offenbaren?', 'Möge die Vorsehung Euch leiten.\n\nIch bewahre viele unserer Aufzeichnungen. Wir erhalten die Visionen, die uns die verwobenen Stränge des Schicksals im Dienste der Vorsehung offenbaren.\n\nWas kann ich Euch offenbaren?', 38134),
(206878, 'deDE', 'Willkommen. Auf welchem Pfad kann ich Euch helfen?', '', 38134),
(206833, 'deDE', '', 'Ihr werdet niemandem von meiner Existenz erzählen. Was Ihr hier erreichen wollt, müsst Ihr selbst erledigen.\n\nIch werde Euch einen Seelenbewahrer überlassen, ein Artefakt, in dem Ihr die im Schlund gefundenen Seelen aufbewahren könnt.\n\nWenn Ihr den Seelenbewahrer und die Bedingungen annehmt, haben wir beide eine Abmachung.\n\nIhr werdet mir jegliche Informationen, die Ihr im Schlund findet, anvertrauen.\n\nIm Gegenzug erlaube ich Euch, Werkzeuge zu erwerben, mit deren Hilfe Ihr dieses Reich überleben und die Seelen, die Ihr sucht, retten könnt.\n\nHaben wir also eine Abmachung?', 38134),
(206827, 'deDE', 'Ihr macht Musik? Spielt Ihr das hier?', 'Ihr macht Musik? Spielt Ihr das hier?', 38134),
(206726, 'deDE', '<Auswählen>', '', 38134),
(206725, 'deDE', '<Aktivieren und deaktivieren>', '', 38134),
(206719, 'deDE', 'Die Buße für obszöne Sprache und Änderung der Verhaltensweise muss immer mit Kosten einhergehen. Aber um die Verhaltensweise der Obszönen wahrhaft zu ändern, muss ihre widerwärtige Wortwahl anderen bewusst gemacht werden, damit sie besser zur Verantwortung gezogen werden können.', '', 38134),
(206713, 'deDE', 'Die Sündenmähre Graumähne wiehert gequält und verblasst schließlich.', '', 38134),
(206708, 'deDE', 'In Abwesenheit von König Anduin wurde mir der Schutz der Allianzbürger anvertraut.\n\nEs fühlt sich an, als wäre es eine Ewigkeit her, dass ich dem jungen Varian Wrynn geholfen habe, den Thron für ihn zu sichern. Und nun bin ich damit beauftragt, ihn für seinen Sohn zu sichern.\n\nDer Vierte Krieg hat der Allianz fürchterlich zugesetzt. Unsere militärische Macht ist erschöpft. Die Adligen von Sturmwind verlangen, dass unsere Verteidigung wiederaufgebaut wird.\n\nDie Rückeroberung von Burg Stromgarde war ein guter erster Schritt. Vielleicht gibt es noch andere Stellungen der Allianz, die gesichert werden können.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206707, 'deDE', 'Auf die Horde wart\'n \'ne Menge Prüfungen un\' Gefahren.\n\nIch bin stolz, \'ne Stimme des Rats zu sein, der die Dinge vorantreibt.\n\nVol\'jin war der beste Kriegshäuptling, den wir hatt\'n... und \'n guter Freund. Ich kann nur hoff\'n, sein Andenken zu ehr\'n, indem ich die Horde niemals vergess\'n lass, welche Opfer er für uns gebracht hat.', '', 38134),
(206706, 'deDE', '', 'Wir haben Monate damit verbracht, jeden Winkel von Azeroth nach einem Zeichen von Sylvanas abzusuchen, und dabei erfahren, dass meine Schwester in das Reich der Toten übergewechselt ist.\n\n<Allerias Augen verengen sich.>\n\nWie passend.\n\nIch würde mich zwar am liebsten der Jagd auf sie anschließen, aber zurzeit ist mein Platz hier. Die Allianz muss ihre Stärke wiederfinden, und ich werde Turalyon dabei helfen.\n\nAber wenn die Zeit gekommen ist, werde ich dafür sorgen, dass sich Sylvanas für ihre Taten verantworten muss.', 38134),
(206705, 'deDE', 'Der Weg steht offen. Ihr könnt jetzt nach Maldraxxus reisen.', '', 38134),
(206704, 'deDE', 'Die Wege öffnen sich wieder. Ich frage mich, ob die Vorsehung Euch deswegen zu uns geschickt hat?', '', 38134),
(206703, 'deDE', 'Der Weg steht offen. Ihr könnt jetzt zum Ardenwald reisen.', '', 38134),
(206701, 'deDE', 'Der Weg steht offen. Ihr könnt jetzt nach Revendreth reisen.', '', 38134),
(206692, 'deDE', 'Willkommen, Kah-Sher. Ist es wahr? Sollen wir das Tor erneut öffnen?', '', 38134),
(206686, 'deDE', '', 'Dank seiner instabilen Natur verursacht er enormen Schaden, kann aber immer nur ein Ziel gleichzeitig befallen.', 38134),
(206685, 'deDE', 'Dank seiner instabilen Natur verursacht er enormen Schaden, kann aber immer nur ein Ziel gleichzeitig befallen.', '', 38134),
(206684, 'deDE', '\'Instabiles Gebrechen\' erzeugt einen Seelensplitter, wenn der Gegner stirbt. Achtet also darauf, das Ziel, das Ihr zuerst tötet, damit zu belegen.', '', 38134),
(206683, 'deDE', '', '\'Instabiles Gebrechen\' erzeugt einen Seelensplitter, wenn der Gegner stirbt. Achtet also darauf, das Ziel, das Ihr zuerst tötet, damit zu belegen.', 38134),
(206682, 'deDE', '', '\'Instabiles Gebrechen\' ist ein mächtiger Zauber, der regelmäßigen Schaden verursacht.', 38134),
(206681, 'deDE', '\'Instabiles Gebrechen\' ist ein mächtiger Zauber, der regelmäßigen Schaden verursacht.', '', 38134),
(206679, 'deDE', '<Wenn dieses Gerät mit einem abgestimmten Splitter versehen wird, kann es verborgene Pfade im Turm öffnen.>', '', 38134),
(206677, 'deDE', 'So soll es geschehen, $uSchlundgänger:Schlundgängerin;.', '', 38134),
(206668, 'deDE', '', 'Diese Ewige Stadt leuchtet wunderschön. Und doch ist die Berührung des Todes überall zu spüren.\n\nUnd auch ich verspüre diese Macht. Vielleicht gibt es hier in Oribos Geheimnisse, die mir helfen zu verstehen, was dies bedeutet.\n\nDie Diener sprechen mit solcher Gewissheit von der \"Vorsehung\". Vielleicht können sie mir auch helfen, meine eigene zu finden.', 38134),
(206667, 'deDE', '', 'Die Schattenlande. Sie sind bemerkenswert... und wenn ich ehrlich bin, ein wenig furchterregend.\n\nAber ich werde nicht zulassen, dass mich die Furcht davon abhält, meinen Freunden zu helfen.\n\nOder davon, den Vater kennenzulernen, den ich für verloren hielt.', 38134),
(206666, 'deDE', 'Wir wissen, dass der Kerkermeister uns beobachtet. Und, dass er Anduin hat.\n\nUnsere einzige Hoffnung ist, dass Ihr und die anderen Helden die Pakte stärkt, $n.\n\nEs wird der Tag kommen, an dem der Kerkermeister und Sylvanas die nächsten Schritte ihres Plans offenlegen.\n\nUnd wenn sie das tun, braucht es all unsere gemeinsame Stärke, um sie zu überwältigen.', '', 38134),
(206629, 'deDE', 'Ja, das ist wirklich so. Scheint bei den Flügellosen in Mode zu sein.', 'Ja, das ist wirklich so. Scheint bei den Flügellosen in Mode zu sein.', 38134),
(206628, 'deDE', 'Ihr springt so viel ohne Flügel herum. Das kann doch nicht gut für die Gelenke sein.', 'Ihr springt so viel ohne Flügel herum. Das kann doch nicht gut für die Gelenke sein.', 38134),
(206627, 'deDE', 'Ja, bestimmt, aber ich federe das einfach ab...', 'Ja, bestimmt, aber ich federe das einfach ab...', 38134),
(206626, 'deDE', 'Ihr wisst, dass Ihr ganz schön viel herumspringt?', 'Ihr wisst, dass Ihr ganz schön viel herumspringt?', 38134),
(206625, 'deDE', 'Und darum... Warum nicht auf morgen verschieben?!', 'Und darum... Warum nicht auf morgen verschieben?!', 38134),
(206624, 'deDE', 'Hilfe? Lasst uns etwas Schönes für Euch plündern.', 'Hilfe? Lasst uns etwas Schönes für Euch plündern.', 38134),
(206623, 'deDE', 'Dieser... Stein! Er wird Euer Untergang sein!', 'Dieser... Stein! Er wird Euer Untergang sein!', 38134),
(206622, 'deDE', 'Lasst nicht zu, dass er Euch in Versuchung führt!', 'Lasst nicht zu, dass er Euch in Versuchung führt!', 38134),
(206621, 'deDE', 'Es ist nie zu spät, auf den Pfad der Rechtschaffenheit einzuschwenken!', 'Es ist nie zu spät, auf den Pfad der Rechtschaffenheit einzuschwenken!', 38134),
(206620, 'deDE', 'Wem werdet Ihr heute helfen?', 'Wem werdet Ihr heute helfen?', 38134),
(206619, 'deDE', 'Hört nicht auf ihn. Wenn Ihr zusammenarbeitet, kann nichts Eure Gruppe aufhalten!', 'Hört nicht auf ihn. Wenn Ihr zusammenarbeitet, kann nichts Eure Gruppe aufhalten!', 38134),
(206618, 'deDE', 'Dank Eures Schutzes werden es Eure Verbündeten weit bringen!', 'Dank Eures Schutzes werden es Eure Verbündeten weit bringen!', 38134),
(206617, 'deDE', 'Möge Eure Waffe nur jene heimsuchen, die wahrhaft Gerechtigkeit verdienen!', 'Möge Eure Waffe nur jene heimsuchen, die wahrhaft Gerechtigkeit verdienen!', 38134),
(206616, 'deDE', 'Ein rechtschaffener Kampf. Möge Euer Gegner Frieden in einem ehrenhaften Tod finden!', 'Ein rechtschaffener Kampf. Möge Euer Gegner Frieden in einem ehrenhaften Tod finden!', 38134),
(206615, 'deDE', 'Es war vermutlich ein Missverständnis...', 'Es war vermutlich ein Missverständnis...', 38134),
(206614, 'deDE', 'Ein Konflikt ist keine Lösung. Habt Ihr mal versucht, das über ein Gespräch zu klären?', 'Ein Konflikt ist keine Lösung. Habt Ihr mal versucht, das über ein Gespräch zu klären?', 38134),
(206613, 'deDE', 'Was macht schon eine Sünde mehr aus? Kommt schon!', 'Was macht schon eine Sünde mehr aus? Kommt schon!', 38134),
(206612, 'deDE', 'Zu welchen Schandtaten sind wir heute bereit?', 'Zu welchen Schandtaten sind wir heute bereit?', 38134),
(206611, 'deDE', 'Denkt nur an all die Zauber, die Ihr auf Eure Feinde hättet niederregnen lassen können, wenn Ihr diesen nicht verschwendet hättet!', 'Denkt nur an all die Zauber, die Ihr auf Eure Feinde hättet niederregnen lassen können, wenn Ihr diesen nicht verschwendet hättet!', 38134),
(206610, 'deDE', 'Ihr wisst doch, dass das nicht auf Gegenseitigkeit beruht hätte...', 'Ihr wisst doch, dass das nicht auf Gegenseitigkeit beruht hätte...', 38134),
(206609, 'deDE', 'So muss es sein, hah! Den habt Ihr zerstampft!', 'So muss es sein, hah! Den habt Ihr zerstampft!', 38134),
(206608, 'deDE', 'Ja! Streckt ihn nieder! Und jetzt den da drüben!', 'Ja! Streckt ihn nieder! Und jetzt den da drüben!', 38134),
(206607, 'deDE', 'Sprecht! Sprecht mit Eurer Waffe!', 'Sprecht! Sprecht mit Eurer Waffe!', 38134),
(206606, 'deDE', 'Das lasst Ihr Euch doch wohl nicht gefallen! Zeit für Rache!', 'Das lasst Ihr Euch doch wohl nicht gefallen! Zeit für Rache!', 38134),
(206605, 'deDE', 'Adonas verzerrt das Gesicht vor Schmerz und presst die Hand auf seine Seite.', '', 38134),
(206604, 'deDE', 'Bitte, $uSchlundgänger:Schlundgängerin;... ich brauche... Heilung...', '', 38134),
(206603, 'deDE', 'Das wird eine Weile schmerzen, aber ich denke, ich kann wieder fliegen... Ich danke Euch.', '', 38134),
(206590, 'deDE', 'Schriftrollen, Schriftrollen, Schriftrollen.\n\nOh, huu, hallo!', 'Schriftrollen, Schriftrollen, Schriftrollen.\n\nOh, huu, hallo!', 38134),
(206588, 'deDE', 'Viele Schriftrollen fehlen in Regalen! Ihr findet fehlende Schriftrolle? Ja?', 'Viele Schriftrollen fehlen in Regalen! Ihr findet fehlende Schriftrolle? Ja?', 38134),
(206579, 'deDE', 'Die Zeit wird knapp und wir müssen Denathrius bald angreifen, bevor er merkt, dass ich hier bin.\n\nAber jetzt wäre es töricht. Ich bin noch vom Schlund verwundet, und Ihr seid noch nicht stark genug, um zu überleben, was ich geplant habe.\n\nGeht, $uSchlundgänger:Schlundgängerin;, und werdet stärker. Erfahrt mehr über die Schattenlande. Tut, was Ihr tun müsst, um Euch hier besser auszukennen. Kehrt zu mir zurück, wenn Ihr bereit seid, einem der Herren des Todes gegenüberzutreten.', '', 38134),
(206576, 'deDE', 'Ihr findet die Portale auf der Plattform außerhalb der Stadt.', 'Ihr findet die Portale auf der Plattform außerhalb der Stadt.', 38134),
(206575, 'deDE', 'Ihr findet alle Mittler, die Handwerkerdienste anbieten, in der Halle der Formen.', 'Ihr findet alle Mittler, die Handwerkerdienste anbieten, in der Halle der Formen.', 38134),
(206571, 'deDE', 'Der Wald nährt.', 'Der Wald nährt.', 38134),
(206564, 'deDE', 'Schicksal und Zeit sind miteinander verflochten. Ihre Wege können neu geformt werden, wenn die Vorsehung es so will.\n\nEure Seele wird von einer großen Macht berührt, $uSterblicher:Sterbliche;. Durch ihren Einfluss kann ich Euer Schicksal verändern... und Euch einen Pfad eröffnen.\n\nSolltet Ihr ihn beschreiten, werden Euch die Bewohner der Schattenlande genauso sehen wie die anderen, die vor Euch durch ihre Reiche gereist sind.\n\nJeder der Pakte wird Euch willkommen heißen und bereitwillig aufnehmen. Für welchen von ihnen Ihr Euch auch entscheidet, Ihr müsst ihn stärker machen.\n\nWenn Ihr Euch nun für diesen Weg entscheidet, gibt es kein Zurück.\n\nDie Entscheidung liegt bei Euch.', '', 38134),
(206562, 'deDE', 'Natürlich habe ich einem Seelenband mit ihr zugestimmt. Sie ist meine teuerste Begleiterin, und dazu noch jemand, mit dem ich liebend gerne die Ewigkeit verbringen würde.', 'Natürlich habe ich einem Seelenband mit ihr zugestimmt. Sie ist meine teuerste Begleiterin, und dazu noch jemand, mit dem ich liebend gerne die Ewigkeit verbringen würde.', 38134),
(206561, 'deDE', 'Nochmals danke für Eure Hilfe.\n\n<Sie lächelt sanft.>\n\nSie hat ja gesagt.', 'Nochmals danke für Eure Hilfe.\n\n<Sie lächelt sanft.>\n\nSie hat ja gesagt.', 38134),
(206560, 'deDE', 'Ich antworte ihr persönlich. Habt Dank dafür. Ihr wisst sicher zu schätzen, wie bedeutsam dieser Moment ist.', 'Ich antworte ihr persönlich. Habt Dank dafür. Ihr wisst sicher zu schätzen, wie bedeutsam dieser Moment ist.', 38134),
(206559, 'deDE', 'Sie möchte ein Seelenband mit mir eingehen, $n.', 'Sie möchte ein Seelenband mit mir eingehen, $n.', 38134),
(206558, 'deDE', 'Schenkt Euch jemand, den Ihr mögt, einen Kranz, ist das ein symbolisches Geschenk.', 'Schenkt Euch jemand, den Ihr mögt, einen Kranz, ist das ein symbolisches Geschenk.', 38134),
(206551, 'deDE', 'Vor Euch liegen zwei Pfade, $n.\n\nBeginnt Eure Reise, so wie andere Sterbliche es vor Euch taten. Niemand wird Euer Schicksal oder Eure Rolle in der Vorsehung kennen.\n\nOder lasst Euch von den Fäden der Zeit und des Schicksals vorwärtsziehen. Die Pakte werden Euch kennen und Ihr könnt entscheiden, wem Eure Loyalität gelten soll.\n\nDie Reiche des Todes werden sich vor Euch auftun, und Euer Schicksal wird seinen Lauf nehmen.\n\nTrefft Eure Entscheidung.', 'Ihr habt zwei Optionen, $n.\n\nIch kann dafür sorgen, dass die Dinge ihren normalen Lauf nehmen. Jeder wird überrascht sein, dass Ihr hier seid, und Ihr lernt Eure neuen Freunde erneut kennen.\n\nOder ich lasse Euch in der Zeit vorwärtsspringen.\n\nIhr könnt dann sofort einem Pakt beitreten und überall in den Schattenlanden zu Abenteuern aufbrechen!\n\nSeid Ihr bereit, Eure Auswahl zu treffen?', 38134),
(206550, 'deDE', 'Das Schicksal ruft. Die Vorsehung braucht Euch.', '', 38134),
(206539, 'deDE', 'Die Mittler von Oribos haben unseren Verbündeten aus der Schwarzen Festung erlaubt, ein Portal zwischen ihrem Reich und unserem offenzuhalten. Ihr findet es im Portalraum, in der Spitze des Magierturms.', 'Die Mittler von Oribos haben unseren Verbündeten aus der Schwarzen Festung erlaubt, ein Portal zwischen ihrem Reich und unserem offenzuhalten. Ihr findet es im Portalraum, in der Spitze des Magierturms.', 38134),
(206534, 'deDE', '', 'Im Zweifelsfall einfache alle töten.', 38134),
(206532, 'deDE', '', 'Wisst Ihr, wie viele Rivalen ich getötet habe?', 38134),
(206530, 'deDE', 'Es war keine einfache Aufgabe, aber einige unserer Verbündeten aus der Schwarzen Festung haben mit den Mittlern von Oribos ein Tor zwischen unseren Reichen geschaffen. Ihr findet es auf der Hauptebene unserer Portalkammer.', 'Es war keine einfache Aufgabe, aber einige unserer Verbündeten aus der Schwarzen Festung haben mit den Mittlern von Oribos ein Tor zwischen unseren Reichen geschaffen. Ihr findet es auf der Hauptebene unserer Portalkammer.', 38134),
(206528, 'deDE', '', 'Mich schaudert es bei diesem Ort. Ich bekomme Gänsehaut und werde nervös. Ich will bei jedem seltsamen Geräusch zusammenzucken.\n\nIch bin entschlossen. Ich bin eine Aufgestiegene der Bastion. Es ist meine Pflicht, mich um die zu kümmern, die nicht nur unsere Wege bedrohen, sondern auch die ganzen Schattenlande.', 38134),
(206527, 'deDE', 'Aspiranten werden normalerweise nicht auf solch wichtige Missionen entsandt.\n\nIch muss zugeben, dass es mich nervös macht, so früh schon die Bastion zu verlassen. Aber mit Euch und Kleia an meiner Seite werde ich viel von dieser Reise lernen.', '', 38134),
(206526, 'deDE', '', 'Ich gelobe, eine würdige Vertreterin der Bastion und unserer Wege zu sein.\n\nDie Venthyr und die Kyrianer trennt vieles, aber ich bin mir sicher, dass wir eine gemeinsame Basis finden können.', 38134),
(206525, 'deDE', '', 'Die Gräfin zapft jeden Adeligen von Revendreth an, damals wie heute.\n\nFür sie bedeuten Informationen Macht. Und die teilt sie nicht einfach.', 38134),
(206524, 'deDE', '', 'Hallo $n!\n\nReisen durch die Zeit sind ziemlich gefährlich. Wenn Ihr ein bisschen erfahrener seid, könnt Ihr Euch an diese Abenteuer wagen!', 38134),
(206523, 'deDE', '', 'Wir dienen nicht nur der Archon, sondern auch den Bewohnern zahlloser Welten der Realität.', 38134),
(206522, 'deDE', '', 'Hallo, $n!\n\nFall Ihr hier seid, um mehr über Zeitwanderungen zu erfahren, fürchte ich, dass Ihr zuerst Euer Verständnis der Gegenwart vertiefen müsst, bevor Ihr die Vergangenheit besuchen könnt.\n\nVielleicht kann ich Euch zurückschicken, nachdem Ihr die Stadt besichtigt und den Kriegshäuptling getroffen habt!', 38134),
(206521, 'deDE', '', '<Kleias Augen sind geschlossen, und Sorge steht ihr ins Gesicht geschrieben.\n\nSie atmet tief ein und aus, während sie mit dem ringt, was sie erfahren hat.>', 38134),
(206520, 'deDE', '', 'Azeroth ist wunderschön. Ein harscher Kontrast zur Bastion. Ich bin gespannt zu sehen, wie die Bewohner dieser Welt leben.', 38134),
(206519, 'deDE', '', 'Für diesen Tag habe ich Äonen lang geübt.\n\nNur noch eine Aufgabe. Noch eine Prüfung.\n\nDann ist die Zeit für meinen Aufstieg gekommen.', 38134),
(206512, 'deDE', '<Archivarin Janeera würdigt Euch keines Blickes und studiert weiter die Schriftrolle vor ihr.>\n\nJa?', '<Archivarin Janeera würdigt Euch keines Blickes und studiert weiter die Schriftrolle vor ihr.>\n\nJa?', 38134),
(206511, 'deDE', 'Nun, bringt mir jederzeit Sündensteinfragmente, wenn Ihr noch einen jagen wollt, $p.', 'Nun, bringt mir jederzeit Sündensteinfragmente, wenn Ihr noch einen jagen wollt, $p.', 38134),
(206510, 'deDE', 'Seid versichert, dass ich Euch äußerst dankbar für Eure Anstrengungen bin, die Inquisitoren des Obersten Kämmerers loszuwerden.', 'Seid versichert, dass ich Euch äußerst dankbar für Eure Anstrengungen bin, die Inquisitoren des Obersten Kämmerers loszuwerden.', 38134),
(206509, 'deDE', 'Verzeihung. Mir wird oft gesagt, ich würde kalt und undankbar wirken.', 'Verzeihung. Mir wird oft gesagt, ich würde kalt und undankbar wirken.', 38134),
(206508, 'deDE', 'Archivar Fane hustet lautstark.', 'Archivar Fane hustet lautstark.', 38134),
(206507, 'deDE', 'Archivar Fane in den Hallen der Sühne kann immer einen Sündenstein eines Inquisitors für Euch erschaffen... wenn Ihr Fragmente habt.', '', 38134),
(206505, 'deDE', 'Ich weiß es nicht genau.\n\nHabt Ihr bei den Bäumen am Ortsrand nachgesehen? Dort haben Aspiranten gelesen, bevor die Ätherwyrm aufgetaucht sind.', 'Ich weiß es nicht genau.\n\nHabt Ihr bei den Bäumen am Ortsrand nachgesehen? Dort haben Aspiranten gelesen, bevor die Ätherwyrm aufgetaucht sind.', 38134),
(206496, 'deDE', 'Tubbins bekommt immer den schweren Teil. Wasser holen ist leicht!', '', 38134),
(206493, 'deDE', 'Das Wasser, das Ihr sucht, ist da vorn...\n\nWenn Ihr Probleme habt, es zu erreichen, sucht dort nach mir.\n\nIch bin nie fern... und oft gewillt zu helfen...', '', 38134),
(206492, 'deDE', 'Auf Wiedersehen... Vergesst nicht, hart zu arbeiten... und Ältere zu respektieren...', '', 38134),
(206491, 'deDE', 'Haltet die Luft an... Passt auf Euren Magen auf... \n\nUnd los...', '', 38134),
(206490, 'deDE', 'Ich kann Euch wieder zum Festland bringen, wenn Ihr es gesammelt habt.', '', 38134),
(206489, 'deDE', 'Das Wasser, das Ihr sucht, ist da vorn.', '', 38134),
(206488, 'deDE', 'Macht Euch bereit... Reisen mit meinem Myzelnetzwerk ist... am Anfang etwas verwirrend.', '', 38134),
(206487, 'deDE', 'Gebt mir einen Einblick in Euren Charakter... Arbeitet Ihr hart?', '', 38134),
(206486, 'deDE', 'Langsam, langsam... Fremde dürfen nicht einfach so durch mein Myzelnetzwerk reisen.', '', 38134),
(206483, 'deDE', 'Ich habe Eure schwere Arbeit durch mein Myzelnetzwerk gesehen.\n\nWo ich gesehen werde, sehe ich... Und Ihr seht mich überall.\n\nIhr arbeitet wahrlich hart.', '', 38134),
(206482, 'deDE', 'Ihr jungen Wesen, immer unterwegs, macht immer Radau...\n\nWas soll der Lärm dieses Mal...', '', 38134),
(206479, 'deDE', 'Ruht Euch aus. Bald beginnt die Jagd von Neuem.', '', 38134),
(206478, 'deDE', 'In der Tat.', '', 38134),
(206477, 'deDE', '', 'Und niemand ist stärker als die Wilde Jagd!', 38134),
(206476, 'deDE', '', 'Gemeinsam wachsen Wurzeln stärker.', 38134),
(206475, 'deDE', '', 'Ich habe viel auf dieser Patrouille gelernt. Es war mir eine Ehre, an Eurer Seite zu jagen.', 38134),
(206474, 'deDE', '', 'Das war eine würdige Jagd. Eure Fertigkeiten werden besser, Lumera.', 38134),
(206473, 'deDE', 'Verschlinger!', '', 38134),
(206472, 'deDE', '', 'Genau das macht mir Sorgen.', 38134),
(206471, 'deDE', '', 'Nein, gar nichts...', 38134),
(206470, 'deDE', '', 'Wartet. Hört Ihr das?', 38134),
(206469, 'deDE', 'Und jetzt begegnen wir ihnen überall im Wald. Finstere Zeiten.', '', 38134),
(206468, 'deDE', '', 'Die Geschichten sind wahr. Auch, wenn es lange her ist, dass die Wilde Jagd mit ihnen gekämpft hat.', 38134),
(206467, 'deDE', '', 'Ich dachte einst, die Drust wären Fabelwesen... Geschichten, um junge Fae zu erschrecken.', 38134),
(206466, 'deDE', '', 'Das habt Ihr verdient, Sika. Viel Spaß!', 38134),
(206465, 'deDE', '', 'Sie werden es noch bereuen, den Ardenwald jemals betreten zu haben!', 38134),
(206464, 'deDE', 'Die Drust! Wilde Jagd, zu den Waffen!', '', 38134),
(206463, 'deDE', '', 'Sika... hat gütige Freunde. Sika... geht feiern!', 38134),
(206462, 'deDE', '', 'Sika sieht kurz etwas unsicher aus, dann taucht ein breites Lächeln auf ihrem Gesicht auf.', 38134),
(206461, 'deDE', '', 'Genießt den Gluthof mit $p. Wir kümmern uns um alles, während Ihr fort seid.', 38134),
(206460, 'deDE', 'Ihr tut so viel für uns. Diesmal möchten wir Euch helfen.', '', 38134),
(206459, 'deDE', 'Ja, Sika. Wir hatten keine Ahnung, dass Euch das so überwältigt.', '', 38134),
(206458, 'deDE', 'Die Worte einer wahren Jägerin.', '', 38134),
(206457, 'deDE', '', 'Das lassen wir nicht zu. Die Wilde Jagd wird den Wald beschützen, ob in Zeiten des Überflusses oder in Hungersnöten.', 38134),
(206456, 'deDE', 'Wenn diese Dürre nicht bald endet, wird den Ardenwald Tirna Scithes Schicksal ereilen.', '', 38134),
(206454, 'deDE', '<Der Zenturio scheint jeglicher Anima beraubt.>', '', 38134),
(206453, 'deDE', '', 'Und hier sind noch mehr von ihnen!', 38134),
(206452, 'deDE', '', 'Tränen aus Regen für Tirna Scithe. So wenig Anima, so viele Gorm.', 38134),
(206445, 'deDE', 'Benötigt Ihr etwas?\n\nIch habe noch etwas Zeit, bevor ich mich um diese Seelen kümmere.', 'Benötigt Ihr etwas?\n\nIch habe noch etwas Zeit, bevor ich mich um diese Seelen kümmere.', 38134),
(206444, 'deDE', 'Leithfor nickt ernst.', '', 38134),
(206443, 'deDE', '', 'In Tirna Scithe gibt es keine Flüchtlinge. Das haben wir auf die harte Tour gelernt.', 38134),
(206442, 'deDE', '', 'Eine Täuschung... und ich bin darauf reingefallen.', 38134),
(206441, 'deDE', 'Lumera, nein! Bleibt zurück!', '', 38134),
(206440, 'deDE', '', 'Seht, Flüchtlinge! Sie brauchen unsere Hilfe!', 38134),
(206439, 'deDE', '', 'Ich freue mich auf seinen Hof.', 38134),
(206438, 'deDE', '', 'Ihr hättet den Schwächsten bekämpfen können. Aber Ihr habt den Stärksten ausgewählt.', 38134),
(206437, 'deDE', '', 'Um einen Verbündeten wie Euch anzulocken, muss Prinz Renathal einiges zu bieten haben.', 38134),
(206436, 'deDE', 'Wir sind jetzt beste Freunde, $p. Oder?\n\nWie oft muss ich Euch also noch anbetteln, einige Gräber Eures Paktes zu mir nach Maldraxxus zu schmuggeln?\n\nJa, ich habe ein paar bei dem Gluthof verflüssigt, als Ihr nicht hingesehen habt. Aber denkt mal an all die Experimente, die ich an ihnen durchführen könnte, wenn mir mein ganzes Labor zur Verfügung steht.\n\nEndlich könnte ich die brennende Frage beantworten: Bestehen Gräber aus Schleim?\n\nWäre möglich!', '<Sika legt die Papiere zur Seite, die sie geordnet hat, während sie mit Euch redet.>\n\n$p! Eine schöne Überraschung.\n\nOft sind Überraschungen nicht gut. Stören Pläne. Machen mehr Arbeit.\n\nSika denkt über den nächsten Gluthof nach. Hoffentlich wird Sika eingeladen.\n\nViel zu tun, aber bester Freund hilft Sika immer.\n\nSika hat guten besten Freund.', 38134),
(206435, 'deDE', 'Da ist ja mein früherer Lieblingslehrling, der jetzt Höfe veranstaltet!\n\nIch wollte unbedingt ein paar Ideen mit Euch teilen, die ich für den nächsten Gluthof habe.\n\nEine Warnung vorweg: Alle erfordern riesige Mengen Schleim.\n\nManche der Schleime sind allerdings auch köstlich!', '', 38134),
(206434, 'deDE', 'Eure Karrierelaufbahn macht mir Sorgen, $p.\n\nNoch nie hat einer meiner einstigen Lehrlinge Gluthöfe veranstaltet.\n\nSehr außergewöhnlich...\n\nKevin scheint die Vorstellung allerdings zu mögen, also will ich dem Fortschritt nicht im Weg stehen.\n\nWann findet übrigens der nächste Gluthof statt?', '', 38134),
(206433, 'deDE', 'Es ist $p! Kevin, nehmt den experimentellen Trank weg... für den Augenblick.', '', 38134),
(206432, 'deDE', 'Tretet nicht auf mein neuestes Experiment, $p. Ich würde Euch ungern verlieren.', '', 38134),
(206431, 'deDE', 'Höre ich da einen naiven Lehrling, der sich nähert?', '', 38134),
(206430, 'deDE', '$p, ich habe gerade einigen der anderen von Euch und dem ZWEITBESTEN Unterhaltungsprogramm der Schattenlande erzählt.\n\nWann findet übrigens der nächste Gluthof statt? Ich habe ein paar der aufsteigenden Talente versprochen, dass sie mitkommen dürfen.', '', 38134),
(206429, 'deDE', '$gMein lieber:Meine liebe; $p! Bleibt und schaut Euch die Kämpfe mit mir an.\n\nWeswegen Ihr auch hier seid, ich kann es Euch bringen lassen.\n\nIhr müsst nicht durch ganz Maldraxxus zockeln, wenn Ihr einen Freund wie Vole habt.', '', 38134),
(206428, 'deDE', 'Wie sollte es Euer Gluthof mit so einem Spektakel aufnehmen können?\n\nKann Euer nächster Hof Azmogal oder Riesenurtz hier beim Tortenstampfen bieten?\n\n<Vole zeigt auf den geschmolzenen Riesen unten.>\n\nIch würde es gar nicht erst versuchen. Ich wage zu bezweifeln, dass die fadenscheinigen Venthyr mit ihm umgehen könnten.', '', 38134),
(206427, 'deDE', 'Kommt her und seht Euch das Spektakel an, $p. Ich habe Euch einen Platz neben mir reserviert.', '', 38134),
(206426, 'deDE', 'Macht Platz, hier kommt ein Champion. $p!', '', 38134),
(206425, 'deDE', 'Wer geht als Nächstes? Wie wäre es mit Euch, $gSchlundgänger:Schlundgängerin;?', '', 38134),
(206424, 'deDE', 'Es ist keine Schande, draufzuzahlen. Nicht jeder taugt für die Arena.', '', 38134),
(206423, 'deDE', '$p. Wann immer wir uns treffen, werde ich an mein altes Leben in Azeroth erinnert.\n\nWenn all das hier vorbei ist und wir unsere Siege an der Festtafel feiern, habe ich ein paar Fragen an Euch.\n\nAber jetzt müssen wir uns auf diese Aufgabe konzentrieren.\n\nZu viel steht auf dem Spiel, um unseren Blick auch nur einen Augenblick vom Feind abzuwenden.', '', 38134),
(206422, 'deDE', 'Ist diese Erfahrung in einer führenden Position in den Schattenlanden Eure erste, $p?\n\nAngesichts Eures Verhaltens glaube ich das kaum. Führung ist eine Last, die die Wenigsten beim ersten Mal richtig schultern.\n\nIch habe die anderen Gäste beim Hof reden hören; sie waren wohl mit einer Eurer Entscheidungen unzufrieden. Als ob sich die ganzen Schattenlande um ihre Launen drehen würden.\n\nLasst Euch von ihren Worten nicht entmutigen.\n\nArbeitet einen Weg zum Sieg aus und führt uns dorthin. Sie werden es irgendwann verstehen.', '', 38134),
(206421, 'deDE', 'Seht Euch die Soldaten an, die uns unterstehen.\n\nStark, standhaft, loyal.\n\nAber das reicht nicht. Eine Streitkraft muss auch weise, schlau und beflügelnd agieren.\n\nIch möchte unbedingt erfahren, ob Prinz Renathal diese Wesenszüge besitzt und wie er sie einsetzt.\n\nWann findet Euer nächster Gluthof statt? Ich möchte unbedingt daran teilnehmen, um mich weiter mit dem Prinzen zu unterhalten.', '', 38134),
(206420, 'deDE', 'Ah, $p. Wie geht es meinem $gmeinem Lieblingsschlundgänger:meiner Lieblingsschlundgängerin; heute?', '', 38134),
(206419, 'deDE', 'Gibt es Neuigkeiten von den Schlachten in den Schattenlanden?', '', 38134),
(206418, 'deDE', 'Kommt gern näher, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(206417, 'deDE', 'Ich werde von Secutor Mevix und zwei unserer besten Soldaten begleitet. Ich bin mir sicher, Ihr könnt sie auch unterbringen.', '', 38134),
(206413, 'deDE', '', 'Immer in Eile, $p.\n\nIch beschwere mich nicht. Dieser Aspekt unserer Beziehung gefällt mir sogar.\n\nIhr verschwendet meine Zeit nicht, und ich nicht Eure Anstrengungen.\n\nUm was möchtet Ihr mich also bei dieser Gelegenheit bitten?\n\nAufklärung? Ein Attentat? Einen Ratschlag zur Hofplanung?\n\nDie Herrin der Spione steht Euch zur Verfügung.', 38134),
(206411, 'deDE', '', 'Ich habe viele Prinzen über lange Jahre dabei beobachtet, wie sie Hof gespielt haben.\n\nAuch unter idealen Bedingungen geht es eher schief als gut.\n\nEuer Renathal sollte lieber den Kritikern als den Schmeichlern zuhören.\n\nAn einem Hof gibt es immer beides. Fragt Ihr Euch, zu welchen ich gehöre?\n\n<Baronin Vashj schmunzelt.>', 38134),
(206410, 'deDE', 'Rührt Euch, Wachposten. $p ist $gein persönlicher Freund:eine persönliche Freundin; von mir.', '$p... Euch sehe ich dieser Tage gerne in unser Lager schlendern.', 38134),
(206409, 'deDE', 'Alles steht gut in Heldenrast, $p. Willkommen zurück.', 'Ah, der Meister der Höfe kehrt in unseren Winkel von Maldraxxus zurück.', 38134),
(206408, 'deDE', 'Seid gegrüßt, $gSchlundgänger:Schlundgängerin;.', 'Eure Augen haben Euch nicht getäuscht, Khaliiq. $uDer Schlundgänger:Die Schlundgängerin; nähert sich.', 38134),
(206407, 'deDE', 'Ich diene, wo ich gebraucht werde. Und ich werde hier gebraucht.', 'Seid vorsichtig, wen Ihr umwerbt, $p... Ihr wollt Euch nicht mit einem Monster einlassen.', 38134),
(206406, 'deDE', '', 'Der Wald verpflichtet.', 38134),
(206405, 'deDE', 'In der Tat. Die Frage ist, wer das sein könnte.', '', 38134),
(206404, 'deDE', '', 'Spriggans greifen normalerweise nicht so koordiniert an. Jemand muss sie leiten.', 38134),
(206403, 'deDE', 'Warum bringt Ihr dieses Schreiben nicht zu Polemarch Adrestes?', '', 38134),
(206402, 'deDE', 'Vorsicht!', '', 38134),
(206401, 'deDE', '', 'Spriggans! Perfekt für Zielübungen! Schade, dass es nicht mehr sind...', 38134),
(206400, 'deDE', '', 'Kümmert Euch nicht um Zarkin, Lumera. Taten sind ihm wichtiger als Worte.', 38134),
(206399, 'deDE', '', 'Genau. Ihr müsst einfach nur pures Chaos auf unsere Gegner regnen lassen!', 38134),
(206398, 'deDE', 'Leithfor nickt wortlos.', '', 38134),
(206396, 'deDE', 'Das werden wir sehen. Leithfor, Ihr geht vor.', '', 38134),
(206395, 'deDE', '', 'Ich bin der Aufgabe gewachsen, Sir. Keine Herausforderung kann mich abschrecken.', 38134),
(206394, 'deDE', 'Seid auf der Hut, Lumera. Dies ist eine gefährliche Patrouille für eine Jägernovizin.', '', 38134),
(206393, 'deDE', 'Ich hatte ja keine Ahnung, dass Höfe so fesselnd sein können, $p. Der Gluthof ist wahrlich eine unglaubliche Bestrebung Eurerseits.\n\nDie Schattenlande vereinigen - mit Tee trinken!\n\nEs ist natürlich viel mehr als das. Dennoch... Wer hätte das gedacht?', '', 38134),
(206392, 'deDE', 'Braucht Ihr einen Ort, an dem Ihr Euch für eine Weile ausruhen könnt?\n\nDie Erschöpften können die Schattenlande nicht retten.', '', 38134),
(206391, 'deDE', 'Habt Ihr einen Moment, $p? Ich wollte etwas sagen.\n\n<Pelagos holt tief Luft.>\n\nEs verletzte mich, als Ihr die Seite Revendreths in unserem gemeinsamen Kampf für die Schattenlande gewählt habt. Ich dachte, dass Ihr an meiner Seite und der des kyrianischen Paktes kämpfen wolltet.\n\nAber jetzt verstehe ich, warum Ihr die Venthyr gewählt habt. Euer Gluthof vereint die ganzen Schattenlande, wie es früher der Fall war... und wie es sein sollte.\n\nIch wollte nur sagen, dass ich mich freue, dass wir dadurch Freunde geworden sind. Wir teilen keine Seelenbande, aber ich spüre, dass wir anders verbunden sind.', '', 38134),
(206390, 'deDE', '', 'Ich freue mich, dass Ihr Zeit für ein Gespräch habt.\n\nWie lange ist das her? Einerlei, zu lang.\n\nErzählt mir alles, was Ihr getan und gesehen habt, seit wir uns das letzte Mal trafen. Braucht Ihr mich oder Pelagos?\n\n<Kleia macht eine Pause und lächelt.>\n\nEs tut gut, Euch zu sehen, $p.', 38134),
(206389, 'deDE', '', 'Es tut gut, Euch wieder unter den Seelen der Bastion zu sehen, $p.\n\nEure Gluthöfe hatten mich glauben lassen, dass Ihr den Schattenlanden dient, auch wenn Ihr die Venthyr als Hauptwerkzeug dafür ausgesucht hattet.', 38134),
(206388, 'deDE', '', 'Brauchen mich die Schattenlande?\n\nIch bin bereit zu dienen.', 38134),
(206387, 'deDE', 'Und viel wichtiger: Wollt Ihr Begleiter?', '', 38134),
(206386, 'deDE', '', '$p! Wohin reist Ihr?', 38134),
(206384, 'deDE', '', 'Die Macht des Kerkermeisters wächst, und die Schattenlande stehen großer Gefahr gegenüber. Seid bereit, $p. Eure Stärke wird in den nächsten Tagen benötigt.', 38134),
(206383, 'deDE', '', 'Wo immer $p ist, ist der sicherste Ort der Schattenlande.', 38134),
(206382, 'deDE', 'Passt auf Euch auf da draußen, $p.', '', 38134),
(206381, 'deDE', '', '$gEr:Sie; wird es uns sagen, wenn $ger:sie; will, dass wir es wissen.', 38134),
(206380, 'deDE', 'Ich frage mich, wohin $uder Schlundgänger:die Schlundgängerin; unterwegs ist...', '', 38134),
(206378, 'deDE', 'Ich habe das bisher nie gesagt, aber ich möchte, dass Ihr wisst, dass ich Euch dankbar bin, $p.\n\nIhr habt mich viel über das Wesen des Dienens und des Opferns gelehrt, und...\n\n<Polemarch Adrestes vergewissert sich, dass keine Aspiranten in Hörweite sind.>\n\nWie wichtig es ist, mitunter Spaß zu haben.\n\nIch habe anfangs gezögert, an Euren Gluthöfen teilzunehmen, aber jetzt freue ich mich darauf.\n\nFür die Archon selbstverständlich!', '', 38134),
(206377, 'deDE', 'Es ist schön, Euch wieder in der Bastion zu sehen, $p.\n\nIch frage mich oft, wie die Dinge gelaufen wären, wenn Ihr die Archon ganz angenommen hättet, so wie ich.\n\nUnd doch ist klar, dass die ganzen Schattenlande von Euren Diensten für Sündensturz profitieren.\n\nIch bin mir sicher, dass dort alles zum Besten steht.', '', 38134),
(206376, 'deDE', 'Was bringt Euch nach Heldenrast?\n\nSolltet Ihr militärische Unterstützung benötigen, stehen die Wachposten und ich bereit.\n\nOder bringt Ihr Kunde aus Sündensturz? Ich kann sie die Befehlskette hochreichen.', '', 38134),
(206374, 'deDE', 'Rührt Euch, Wachposten. $p ist $gein persönlicher Freund:eine persönliche Freundin; von mir.', '', 38134),
(206373, 'deDE', 'Alles steht gut in Heldenrast, $p. Willkommen zurück.', '', 38134),
(206372, 'deDE', 'Seid gegrüßt, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(206371, 'deDE', 'Ich diene, wo ich gebraucht werde. Und ich werde hier gebraucht.', '', 38134),
(206368, 'deDE', '', '<Sika ordnet weiter einen Papierstapel, während sie spricht.>\n\nWie kann Sika helfen? Die Bastion ist immer beschäftigt, aber $gder Schlundgänger:die Schlundgängerin; verdient Hilfe.\n\n$gSchlundgänger:Schlundgängerin; hilft der Bastion und den ganzen Schattenlanden.\n\nWas kann Sika tun?', 38134),
(206367, 'deDE', '', '$p benötigt etwas? Kommt ganz oben auf meine Liste!', 38134),
(206366, 'deDE', '', 'Sika mag es, wenn $p zu Besuch kommt!', 38134),
(206365, 'deDE', '', '$gSchlundgänger:Schlundgängerin; benötigt auch etwas? Füge ich meiner Liste hinzu.', 38134),
(206364, 'deDE', '', 'Sehr traurig. Gluthof macht Spaß. Vielleicht beim nächsten Mal...', 38134),
(206363, 'deDE', '<Mikanikos inspiziert vorsichtig Teile Eurer Rüstung, während er vor sich hinmurmelt.>\n\nSehr schlau... Sehr neu... Sehr faszinierend...\n\nEure Besuche sind immer ein Ereignis.', '', 38134),
(206362, 'deDE', 'Immer Platz für $p in meiner Werkstatt.', '', 38134),
(206361, 'deDE', '<Mikanikos wendet sich von seinem neuesten Projekt ab und schenkt Euch seine volle Aufmerksamkeit.>\n\n$p, mein liebster Werkstattbesuch.\n\nImmer neue Ideen. Immer ein guter Freund.\n\nArbeit kann warten. Was bringt Euch zur Ewigen Schmiede?\n\nBesuch? Ihr braucht etwas? Bald neuer Gluthof? Hoffentlich!', '', 38134),
(206360, 'deDE', '$p! Welche neue Inspiration bringt Ihr heute?\n\nHabt Ihr neue Gegner bekämpft? Neue Technologie entdeckt? Neue Magie gemeistert?\n\nErzählt mir alles.\n\nKleinigkeiten führen oft zu großen Entdeckungen!', '', 38134),
(206359, 'deDE', 'Jedes Teil ist perfekt für etwas, aber dieses Mal habe ich etwas Besonderes!', '', 38134),
(206358, 'deDE', '$gFreund:Freundin;! Gluthof ist sehr beeindruckend... brauche bald mehr!', '', 38134),
(206357, 'deDE', 'Viel zu lernen... Viel auszuprobieren...', 'Ihr seid in meinem Hain immer willkommen.', 38134),
(206355, 'deDE', '', 'Ich weiß.', 38134),
(206354, 'deDE', 'Wenn Ihr das sagt. Ich glaube Euch. Wirklich. Aber es geht nicht schnell genug.', '', 38134),
(206353, 'deDE', '', 'Es fühlt sich vielleicht nicht so an, aber es fällt mir auf, dass Ihr diesen Erinnerungen leichter begegnet. Ihr schlagt Euch gut, Pelagos.', 38134),
(206352, 'deDE', 'Ich wünschte, ich würde besser darin werden, Kleia.', '', 38134),
(206346, 'deDE', 'Nochmals danke, dass Ihr geholfen habt, diese Geister zurückzubringen. Eine schöne Bereicherung für das Konservatorium, findet Ihr nicht?', 'Nochmals danke, dass Ihr geholfen habt, diese Geister zurückzubringen. Eine schöne Bereicherung für das Konservatorium, findet Ihr nicht?', 38134),
(206345, 'deDE', 'Dieser Geist wird das Wasser vorziehen. Ihr werdet ihn wohl in der Nähe des Sees finden.', 'Dieser Geist wird das Wasser vorziehen. Ihr werdet ihn wohl in der Nähe des Sees finden.', 38134),
(206344, 'deDE', 'Wenn Ihr Plakate mit neuen Haarschnitten findet, kann ich mein Haar anders wachsen lassen. Oder ich kann ein Käppchen tragen, wenn Ihr ein passendes findet!', '', 38134),
(206343, 'deDE', 'Ihr findet Farbstoffe, die wir benutzen können, in der Nähe von Schlammlachen oder auf dem Nachtmarkt.', '', 38134),
(206338, 'deDE', 'Unsere Konstrukte geben auf dem Schlachtfeld gern etwas an. Ihr werdet sehen, dass Ihr für diese Konstrukte viele Modeaccessoires erhalten könnt, um ihre Erscheinung auf dem Schlachtfeld zu verbessern.\n\nIhr werdet allerdings an den verschiedensten Orten suchen müssen, um die Zutaten für einige dieser Modeaccessoires zu finden.', '', 38134),
(206337, 'deDE', 'Hochwertige Teile sind eine Schlüsselkomponente zur Erschaffung mächtiger Kampfkonstrukte und -gegenstände.\n\nDiese Teile sind schwer zu finden, doch die Konstrukte hier werden Euch helfen, hochwertige Teile zu erhalten, wenn Ihr ihnen bei ihren Problemen weiterhelft.', '', 38134),
(206336, 'deDE', 'Formbares Fleisch ist die Grundzutat der meisten Konstrukte und Gegenstände, die am Tisch für Monstrositätenflickerei hergestellt werden.\n\nBei fast allen Kreaturen, die Ihr in den Schattenlanden besiegt, besteht die Chance, ein gutes Stück formbares Fleisch zu finden.\n\nUnd Chordy hat eine gute Nase für formbares Fleisch, also fragt ihn ruhig, wenn Ihr Hilfe bei der Suche nach dieser Zutat braucht.', '', 38134),
(206325, 'deDE', 'Die Kunst der Monstrositätenflickerei erfordert eine ruhige Hand, hochwertige Zutaten und natürlich den Tisch für Monstrositätenflickerei dort drüben.\n\nIch habe Euch für alle Konstrukte und Gegenstände, die wir am Tisch für Monstrositätenflickerei bauen können, die benötigten Zutaten aufgeschrieben.', '', 38134),
(206322, 'deDE', 'Mann, werden die sauer sein, wenn sie das Fest verpassen, weil sie nicht dort waren!', '', 38134),
(206321, 'deDE', 'Hey, ich weiß ja nicht, ob ich Euch das schon mal gesagt habe, aber danke, dass Ihr meine Freunde in den Gluthof gebracht habt.\n\nWir sind wie eine Familie. Wir halten zusammen.\n\nAußerdem wäre Slanknen stinksauer, wenn ich ohne ihn auf diese bombastischen Feste gehen würde!\n\n<Choofa lacht.>\n\nAber ich könnte ihn ja einfach durch Euch ersetzen, stimmt\'s? Ihr gehört ja nun zu uns.', '', 38134),
(206320, 'deDE', 'Große Höfe, große Feste. Wir zwei wissen, wie man lebt! ...Und Rury, Awool und Slanknen natürlich auch.\n\nAch übrigens, wann ist denn der nächste Gluthof? Ich hätte nämlich gerade Zeit. Sollen wir schon mal los, ein paar Flaschen köpfen und ein bisschen vorfeiern?\n\nAch, ich sehe ja, dass Ihr zu tun habt. Schon gut.\n\nSorgt einfach dafür, dass ich beim nächsten Fest dabei bin, in Ordnung?', '', 38134),
(206319, 'deDE', 'Kumpel, Kumpel! Habt Ihr vielleicht Feuerwerkreste vom Hof dabei?', '', 38134),
(206318, 'deDE', 'Hey, $p! Wann ist das nächste Fest?', '', 38134),
(206317, 'deDE', 'Wohin ist Rury denn schon wieder verschwunden...?', '', 38134),
(206316, 'deDE', '', 'Ich vertraue den Mittlern nicht immer... aber ich kann auch nicht immer ihren Waren widerstehen.', 38134),
(206315, 'deDE', '', 'Schmerz und Verlust sind mächtige Lehrer, und ich war zu oft ihr Schüler.\n\nDoch dadurch weiß ich Eure Einladungen zum Gluthof umso mehr zu schätzen.\n\nMeine Reisen waren in vielerlei Hinsicht... erhellend. Euer Prinz ist beeindruckend. Ich glaube wirklich, dass er das Beste für uns und die anderen Reiche im Sinn hat.\n\nHabt Dank, $p. Ich freue mich auf unseren nächsten gemeinsamen Hof.', 38134),
(206314, 'deDE', '', 'Ich habe unsere bisherigen Reisen genossen, $p. Ich spüre, dass sie nur die ersten Schritte in einer langen Freundschaft sind.\n\nIhr habt mich jetzt schon dazu gebracht, viele neue Ideen und Möglichkeiten zu bedenken. Hoffentlich konnte ich Eure Augen ebenfalls für neue Ideen öffnen.\n\nIch weiß, Ihr könnt nicht lange bleiben. Das ist der Weg des Schlundgängers.\n\nAber bleibt auch nicht zu lange weg. Mein Hain wäre nicht derselbe ohne Euch.', 38134),
(206313, 'deDE', '', 'Es ist schön, Euch wiederzusehen, $p. Habt Ihr es eilig?', 38134),
(206312, 'deDE', '', 'Was führt Euch zu meinem Hain?', 38134),
(206311, 'deDE', '', 'Ihr seid in meinem Hain immer willkommen.', 38134),
(206310, 'deDE', '', 'Ah, $p.\n\nDie Jagd kann warten, wenn Ihr hier seid.\n\nWelche Neuigkeiten bringt Ihr aus Revendreth? Hier im Ardenwald bedrohen uns weiterhin von allen Seiten Drust, Gorm und andere Schmeißfliegen.\n\nAber ich will mich nicht beschweren. Immer ein guter Grund, auf die Jagd zu gehen.\n\nHoffentlich jagt Ihr bald wieder mit mir. Ich habe mich an Euren Kampfstil gewöhnt.\n\nWer braucht schon einen ganzen Jagdtrupp, wenn wir beide reichen, was?', 38134),
(206309, 'deDE', '', 'Wie steht es am Hof, $p?\n\nHabt Ihr Jagden für den nächsten Anlass arrangiert? Hoffentlich etwas Neues, falls ich kommen sollte.\n\nSo sehr mir unsere Jagden auf dem Hofland auch gefallen haben, ist es immer aufregender, auf der Fährte neuer Beute zu sein. Meint Ihr nicht auch?\n\nSo oder so können wir hoffentlich bald wieder zusammen jagen.', 38134),
(206308, 'deDE', '', 'Ich nehme an, Eure Jagd verläuft weiterhin gut, $p.\n\nSagt mir, welche Beute habt Ihr kürzlich aufgespürt?', 38134),
(206307, 'deDE', '', '$p! Ruht Euch aus und erzählt mir von Euren Jagden.', 38134),
(206306, 'deDE', '', 'Wie verläuft die Jagd, $gSchlundgänger:Schlundgängerin;?', 38134),
(206305, 'deDE', '', 'Die Jagd ruft. Wer antwortet auf ihren Ruf?', 38134),
(206304, 'deDE', '', 'Gute Jagd, $gSchlundgänger:Schlundgängerin;.', 38134),
(206303, 'deDE', '', 'Glaubt mir, die Pheromone der Gorm riechen viel besser, als man denkt.', 38134),
(206302, 'deDE', '', '<Lady Mondbeere sieht sich vorsichtig um, bevor sie sich zu Euch hinüberneigt und flüstert.>\n\nWir sind nun beste Freunde, $p. Das heißt, dass wir gemeinsam durch dick und dünn gehen.\n\nDa fällt mir der nächste Streich ein, den ich für uns geplant habe...\n\n<Lady Mondbeere bemerkt eine Fee in der Nähe, welche die Unterhaltung belauscht.>\n\nWir unterhalten uns beim nächsten Gluthof weiter darüber. Ich kann es kaum erwarten!', 38134),
(206301, 'deDE', '', '<Lady Mondbeere unterdrückt ein Lachen.>\n\n$p! Ich bin so froh, dass Ihr vorbeigekommen seid.\n\nIhr habt mich an den Streich erinnert, den ich Theotar beim letzten Gluthof gespielt habe. Ich hatte so viel Spaß!\n\nWird Theotar am nächsten Hof teilnehmen? Er hat geschworen, es mir heimzuzahlen, und ich warte immer noch...', 38134),
(206300, 'deDE', '', 'Hallo mal wieder! Nach welchem Schalk steht Euch heute der Sinn?\n\nUnd, viel wichtiger, kann ich mitmachen?', 38134),
(206299, 'deDE', '', '$p! Ihr würdet doch nicht einfach weiterziehen, ohne Eure Lieblingsfee zu besuchen, oder?', 38134),
(206298, 'deDE', '', 'Keine Sorge, $p. Heute habe ich keine Streiche auf Lager für Euch.', 38134),
(206297, 'deDE', '', 'Das dürfte interessant werden...', 38134),
(206296, 'deDE', 'Es gibt viel zu tun. Wenn Ihr sofortige Hilfe braucht, dann wendet Euch an die Hand der Reinheit. Danke.', 'Es gibt viel zu tun... Wenn Ihr sofortige Hilfe braucht, dann wendet Euch an die Hand der Reinheit. Danke.', 38134),
(206295, 'deDE', '<Er mustert Euch.>\n\nWelche Last tragt Ihr?', '<Sie mustert Euch.>\n\nWelche Last tragt Ihr?', 38134),
(206294, 'deDE', 'Ich werde Euch helfen, sobald ich kann, $uAspirant:Aspirantin;. Habt noch ein wenig Geduld.', 'Ich werde Euch helfen, sobald ich kann, $uAspirant:Aspirantin;. Habt noch ein wenig Geduld.', 38134),
(206293, 'deDE', 'Wie kann ich zu Diensten sein?', 'Wie kann ich zu Diensten sein?', 38134),
(206292, 'deDE', 'Was beunruhigt Euch, $uAspirant:Aspirantin;?', 'Was beunruhigt Euch, $uAspirant:Aspirantin;?', 38134),
(206291, 'deDE', 'Wollt Ihr ebenfalls geläutert werden? Ihr müsst leider warten. Meine Hilfe wird im Moment von vielen gebraucht.', 'Wollt Ihr ebenfalls geläutert werden? Ihr müsst leider warten. Meine Hilfe wird im Moment von vielen gebraucht.', 38134),
(206290, 'deDE', 'So etwas habe ich noch nie gesehen. Es ist nicht ungewöhnlich, dass in Krisenzeiten Aspiranten zu uns kommen, aber so viele zur gleichen Zeit... Ich habe das Gefühl, dass sich jenseits der Tore unseres Tempels etwas zusammenbraut.\n\nEntschuldigt mich. Ich muss zurück zur Arbeit.', 'So etwas habe ich noch nie gesehen. Es ist nicht ungewöhnlich, dass in Krisenzeiten Aspiranten zu uns kommen, aber so viele zur gleichen Zeit... Ich habe das Gefühl, dass sich jenseits der Tore unseres Tempels etwas zusammenbraut.\n\nEntschuldigt mich. Ich muss zurück zur Arbeit.', 38134),
(206289, 'deDE', 'Willkommen im Tempel der Reinheit, $uAspirant:Aspirantin;. Nehmt Euch einen Moment, den Hof zu bewundern... aber werdet nicht müßig. Es warten noch viele andere auf ihre Läuterung.', 'Willkommen im Tempel der Reinheit, $uAspirant:Aspirantin;. Nehmt Euch einen Moment, den Hof zu bewundern... aber werdet nicht müßig. Es warten noch viele andere auf ihre Läuterung.', 38134),
(206288, 'deDE', '<Der Aufgestiegene neigt seinen Kopf zu Euch.>\n\nMöge der Blick der Archon auf Euch ruhen.', '<Die Aufgestiegene neigt ihren Kopf zu Euch.>\n\nMöge der Blick der Archon auf Euch ruhen.', 38134),
(206287, 'deDE', 'Eure Ankunft ist bedeutsamer, als Ihr wissen könnt. Es ist ein Privileg, das mitzuerleben.', 'Eure Ankunft ist bedeutsamer, als Ihr wissen könnt. Es ist ein Privileg, das mitzuerleben.', 38134),
(206286, 'deDE', 'Ihr erregt großes Aufsehen in der Elysischen Feste, sterbliches Wesen.', 'Ihr erregt großes Aufsehen in der Elysischen Feste, sterbliches Wesen.', 38134),
(206285, 'deDE', '<Der Aufgestiegene sieht Euch neugierig an.>\n\nMeinen Glückwunsch und willkommen in den Reihen der Aspiranten.', '<Die Aufgestiegene sieht Euch neugierig an.>\n\nMeinen Glückwunsch und willkommen in den Reihen der Aspiranten.', 38134),
(206284, 'deDE', 'Ich bin nicht GESTORBEN und habe den... wie nennen sie das, den Schleier?... durchquert, um ewig hier zu WARTEN!\n\nWartet... Ihr seid neu! Hat das Warten endlich ein Ende?!', 'Ich bin nicht GESTORBEN und habe den... wie nennen sie das, den Schleier?... durchquert, um ewig hier zu WARTEN!\n\nWartet... Ihr seid neu! Hat das Warten endlich ein Ende?!', 38134),
(206283, 'deDE', '<Er seufzt schwer auf.>\n\nZumindest ist es ein schöner Platz zum Warten.', '<Sie seufzt schwer auf.>\n\nZumindest ist es ein schöner Platz zum Warten.', 38134),
(206282, 'deDE', 'Vor mir liegt ein langer Weg, aber ich habe nicht einmal die Chance, ihn zu gehen. Ich frage mich, ob ich versehentlich hierhergeschickt wurde.', 'Vor mir liegt ein langer Weg, aber ich habe nicht einmal die Chance, ihn zu gehen. Ich frage mich, ob ich versehentlich hierhergeschickt wurde.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206281, 'deDE', 'Geduld, sagen sie. Also ich sehe hier kein Paragon der Geduld herumsitzen, Ihr etwa?', 'Geduld, sagen sie. Also ich sehe hier kein Paragon der Geduld herumsitzen, Ihr etwa?', 38134),
(206280, 'deDE', 'Habt Ihr schon Neuigkeiten von der Archon? Ist die Dürre endlich vorüber?', 'Habt Ihr schon Neuigkeiten von der Archon? Ist die Dürre endlich vorüber?', 38134),
(206279, 'deDE', 'Sie haben gesagt, dass wir hier warten sollen, bis die Animakrise vorbei ist... Ich weiß nicht, wie lange das schon her ist. Ich glaube langsam, dass die Dürre niemals enden wird.\n\nDie Kuratoren sind immerhin sehr nett, und ich kann so viel essen, wie ich möchte, ohne dass ich zunehme! Haha!', 'Sie haben gesagt, dass wir hier warten sollen, bis die Animakrise vorbei ist... Ich weiß nicht, wie lange das schon her ist. Ich glaube langsam, dass die Dürre niemals enden wird.\n\nDie Kuratoren sind immerhin sehr nett, und ich kann so viel essen, wie ich möchte, ohne dass ich zunehme! Haha!', 38134),
(206278, 'deDE', 'Ich will doch nur mit meiner Reise beginnen. Ist das zu viel verlangt?', 'Ich will doch nur mit meiner Reise beginnen. Ist das zu viel verlangt?', 38134),
(206277, 'deDE', 'Das ist doch absurd. Ich warte schon seit... Moment mal, welcher Tag ist heute?\n\nSeit einer Ewigkeit!', 'Das ist doch absurd. Ich warte schon seit... Moment mal, welcher Tag ist heute?\n\nSeit einer Ewigkeit!', 38134),
(206276, 'deDE', 'Ein Neuankömmling? ENDLICH!', 'Ein Neuankömmling? ENDLICH!', 38134),
(206275, 'deDE', 'Oh! Ein neues Gesicht! Gibt es Neuigkeiten aus dem Jenseits?', 'Oh! Ein neues Gesicht! Gibt es Neuigkeiten aus dem Jenseits?', 38134),
(206274, 'deDE', 'Ich habe mein ganzes Leben geopfert, um hierherzugelangen, und es war wundervoll. Voller Hoffnung. Es ist immer noch wundervoll, aber man hat mir versprochen, dass ich wachsen und zu einer besseren Version meiner selbst werden würde. Stattdessen stecke ich hier fest und werde immer verbitterter. Das kann doch so nicht geplant gewesen sein, oder?', 'Ich habe mein ganzes Leben geopfert, um hierherzugelangen, und es war wundervoll. Voller Hoffnung. Es ist immer noch wundervoll, aber man hat mir versprochen, dass ich wachsen und zu einer besseren Version meiner selbst werden würde. Stattdessen stecke ich hier fest und werde immer verbitterter. Das kann doch so nicht geplant gewesen sein, oder?', 38134),
(206273, 'deDE', 'Der Tod war zumindest interessant. Aber dieses Warten... und auf was?', 'Der Tod war zumindest interessant. Aber dieses Warten... und auf was?', 38134),
(206272, 'deDE', 'Ich vermisse meinen Körper.', 'Ich vermisse meinen Körper.', 38134),
(206271, 'deDE', 'Ich hoffe, Euer Erscheinen ist ein Zeichen für eine bessere Zukunft, $umein Freund:meine Liebe;. Es sieht nicht gut aus in der Bastion.', 'Ich hoffe, Euer Erscheinen ist ein Zeichen für eine bessere Zukunft, $umein Freund:meine Liebe;. Es sieht nicht gut aus in der Bastion.', 38134),
(206270, 'deDE', 'Das Schlimmste am Warten ist, dass man keine Hände hat. Oder Füße. Oder Augen.\n\nEhrlich gesagt bin ich mir nicht sicher, wie das hier funktioniert. Sie haben mir gesagt, dass ich eine neue Gestalt erhalten würde, sobald ich die erste Läuterung abgeschlossen hätte. Aber seit die Dürre ausgebrochen ist, haben nur ganz wenige das Ritual abgeschlossen.', 'Das Schlimmste am Warten ist, dass man keine Hände hat. Oder Füße. Oder Augen.\n\nEhrlich gesagt bin ich mir nicht sicher, wie das hier funktioniert. Sie haben mir gesagt, dass ich eine neue Gestalt erhalten würde, sobald ich die erste Läuterung abgeschlossen hätte. Aber seit die Dürre ausgebrochen ist, haben nur ganz wenige das Ritual abgeschlossen.', 38134),
(206268, 'deDE', 'Sie haben mir gesagt, dass dieser Ort der Lohn für ein gutes Leben sein wird. War das eine Lüge? Werde ich jetzt doch bestraft?', 'Sie haben mir gesagt, dass dieser Ort der Lohn für ein gutes Leben sein wird. War das eine Lüge? Werde ich jetzt doch bestraft?', 38134),
(206267, 'deDE', 'Man sagt, dass die Sammler handwerklich begabt seien. Vielleicht sollte ich anfangen zu schnitzen.', 'Man sagt, dass die Sammler handwerklich begabt seien. Vielleicht sollte ich anfangen zu schnitzen.', 38134),
(206266, 'deDE', 'Ein Beobachter zu werden, scheint so schwierig... Wenn ich aber Seelen eine zweite Chance im Leben geben könnte... das wäre doch eine lobenswerte Aufgabe!', 'Eine Beobachterin zu werden, scheint so schwierig... Wenn ich aber Seelen eine zweite Chance im Leben geben könnte... das wäre doch eine lobenswerte Aufgabe!', 38134),
(206265, 'deDE', 'Wenn ich aufsteige, möchte ich ein Bringer werden. Ich möchte alle Länder jenseits der Schattenlande sehen. Ich möchte wissen, was da draußen ist!', 'Wenn ich aufsteige, möchte ich eine Bringerin werden. Ich möchte alle Länder jenseits der Schattenlande sehen. Ich möchte wissen, was da draußen ist!', 38134),
(206264, 'deDE', 'Die Bastion ist solch ein wundervoller Ort. Sie haben mir erzählt, dass der Himmel mit Anima erfüllt war, bevor das alles passiert ist. Ich würde es so gerne so sehen, wie es sein sollte.', 'Die Bastion ist solch ein wundervoller Ort. Sie haben mir erzählt, dass der Himmel mit Anima erfüllt war, bevor das alles passiert ist. Ich würde es so gerne so sehen, wie es sein sollte.', 38134),
(206263, 'deDE', 'Oh, eines Tages Schwingen zu haben...\n\n<Er seufzt wehmütig.>', 'Oh, eines Tages Schwingen zu haben...\n\n<Sie seufzt wehmütig.>', 38134),
(206262, 'deDE', 'Der Hain des Erwachens ist der letzte Schritt auf dem Pfad der Wiedergeburt. Es ist einer der heiligsten Orte im Ardenwald.\n\nWenn ein Geist bereit ist, in seine Welt zurückzukehren, bringen wir ihn hierher. Er geht durch dieses Portal zurück und beginnt seinen Kreislauf von neuem.', 'Der Hain des Erwachens ist der letzte Schritt auf dem Pfad der Wiedergeburt. Es ist einer der heiligsten Orte im Ardenwald.\n\nWenn ein Geist bereit ist, in seine Welt zurückzukehren, bringen wir ihn hierher. Er geht durch dieses Portal zurück und beginnt seinen Kreislauf von neuem.', 38134),
(206261, 'deDE', 'Ich habe die erste Läuterung schon drei Mal versucht. Meine Bürden überfordern mich jedes Mal. Und das ist ja erst der erste Schritt! Was, wenn ich für immer hier gefangen bin?!', 'Ich habe die erste Läuterung schon drei Mal versucht. Meine Bürden überfordern mich jedes Mal. Und das ist ja erst der erste Schritt! Was, wenn ich für immer hier gefangen bin?!', 38134),
(206260, 'deDE', 'Ich muss noch so viel lernen... aber zumindest hetzt mich keiner. Im Moment kann sowieso keiner aufsteigen.', 'Ich muss noch so viel lernen... aber zumindest hetzt mich keiner. Im Moment kann sowieso keiner aufsteigen.', 38134),
(206259, 'deDE', 'Zuerst der Ritus der Reinheit, dann der Ritus des Muts und dann der Ritus...\n\nMoment, jetzt hab ich es. Der Loyalität? Nein, das kann nicht stimmen...', 'Zuerst der Ritus der Reinheit, dann der Ritus des Muts und dann der Ritus...\n\nMoment, jetzt hab ich es. Der Loyalität? Nein, das kann nicht stimmen...', 38134),
(206258, 'deDE', 'Wie lange wartet IHR denn schon?', 'Wie lange wartet IHR denn schon?', 38134),
(206257, 'deDE', 'Ich muss noch so viel lernen und das hier verzögert meine Reise enorm. Ich fürchte, ich werde niemals aufsteigen.', 'Ich muss noch so viel lernen und das hier verzögert meine Reise enorm. Ich fürchte, ich werde niemals aufsteigen.', 38134),
(206256, 'deDE', 'Wie lange ist es schon her, dass alles noch normal war? Die Zeit vergeht hier so langsam.', 'Wie lange ist es schon her, dass alles noch normal war? Die Zeit vergeht hier so langsam.', 38134),
(206255, 'deDE', 'Ich will einfach nur mit den Fünf Riten beginnen.', 'Ich will einfach nur mit den Fünf Riten beginnen.', 38134),
(206254, 'deDE', 'Wo wurde Euer Training unterbrochen? Ich habe war kurz vor dem Abschluss des Ritus der Loyalität, als das alles begann. Ich war so kurz vor dem Aufstieg... so kurz.', 'Wo wurde Euer Training unterbrochen? Ich habe war kurz vor dem Abschluss des Ritus der Loyalität, als das alles begann. Ich war so kurz vor dem Aufstieg... so kurz.', 38134),
(206253, 'deDE', 'Wir können nichts tun. Keine Meditation oder Training wird mich dem Aufstieg näherbringen. Zumindest nicht im Moment.\n\n<Er seufzt schwer.>', 'Wir können nichts tun. Keine Meditation oder Training wird mich dem Aufstieg näherbringen. Zumindest nicht im Moment.\n\n<Sie seufzt schwer.>', 38134),
(206252, 'deDE', 'Obwohl die Bastion nur noch ein Schatten ihres ehemaligen Ruhms ist, ist das immer noch der schönste Ort, den ich je gesehen habe. Wenn ich einen Ort zum Warten auf die Ewigkeit auswählen müsste, wäre es dieser.', 'Obwohl die Bastion nur noch ein Schatten ihres ehemaligen Ruhms ist, ist das immer noch der schönste Ort, den ich je gesehen habe. Wenn ich einen Ort zum Warten auf die Ewigkeit auswählen müsste, wäre es dieser.', 38134),
(206251, 'deDE', 'Wie verbringt Ihr Eure Zeit, $umein Freund:meine Liebe;?', 'Wie verbringt Ihr Eure Zeit, $umein Freund:meine Liebe;?', 38134),
(206250, 'deDE', 'Bei der Archon, ich bin so erschöpft.', 'Bei der Archon, ich bin so erschöpft.', 38134),
(206249, 'deDE', 'Dient dem Tempel, haben sie gesagt. Verkörpert Eure Tugend, haben sie gesagt. Nun, ich...\n\n<Er verstummt schnell, als er Eure Präsenz bemerkt.>', 'Dient dem Tempel, haben sie gesagt. Verkörpert Eure Tugend, haben sie gesagt. Nun, ich...\n\n<Sie verstummt schnell, als sie Eure Präsenz bemerkt.>', 38134),
(206248, 'deDE', '<Er wird schnell wieder ernst, bevor er spricht.>\n\nWie kann ich behilflich sein, $uAspirant:Aspirantin;?', '<Sie wird schnell wieder ernst, bevor sie spricht.>\n\nWie kann ich behilflich sein, $uAspirant:Aspirantin;?', 38134),
(206247, 'deDE', 'Ich bin so schnell es geht bei Euch, $uAspirant:Aspirantin;.', 'Ich bin so schnell es geht bei Euch, $uAspirant:Aspirantin;.', 38134),
(206246, 'deDE', 'Benötigt Ihr ebenfalls eine Läuterung?', 'Benötigt Ihr ebenfalls eine Läuterung?', 38134),
(206245, 'deDE', 'Sie haben mir gesagt, ich kann ihnen helfen, nachdem ich meinen eigenen Ritus der Reinheit durchgeführt habe. Stattdessen habe ich... Angst. Es sind so VIELE.', 'Sie haben mir gesagt, ich kann ihnen helfen, nachdem ich meinen eigenen Ritus der Reinheit durchgeführt habe. Stattdessen habe ich... Angst. Es sind so VIELE.', 38134),
(206244, 'deDE', '<Er atmet lange und tief ein, bevor er Euch bemerkt.>\n\nFehlt irgendetwas?', '<Sie atmet lange und tief ein, bevor sie Euch bemerkt.>\n\nFehlt irgendetwas?', 38134),
(206243, 'deDE', 'Was braucht Ihr?', 'Was braucht Ihr?', 38134),
(206242, 'deDE', 'Eure Geduld ehrt Euch. Wisset, dass wir alles in unserer Macht Stehende tun.', 'Eure Geduld ehrt Euch. Wisset, dass wir alles in unserer Macht Stehende tun.', 38134),
(206241, 'deDE', 'Kann ich Euch irgendwie behilflich sein?', 'Kann ich Euch irgendwie behilflich sein?', 38134),
(206240, 'deDE', 'Wie kann ich zu Diensten sein?', 'Wie kann ich zu Diensten sein?', 38134),
(206239, 'deDE', 'Möge der Blick der Archon auf Euch ruhen, $umein Freund:meine Liebe;.', 'Möge der Blick der Archon auf Euch ruhen, $umein Freund:meine Liebe;.', 38134),
(206238, 'deDE', 'Was kann dieser Provost tun, huu?', 'Was kann diese Provostin tun, huu?', 38134),
(206236, 'deDE', 'Viel Wahnsinn. Viele Tote.\n\n<Seine Augen wirken müde.>', 'Viel Wahnsinn. Viele Tote.\n\n<Ihre Augen wirken müde.>', 38134),
(206235, 'deDE', 'Traurige Aspiranten machen Provosten traurig.', 'Traurige Aspiranten machen Provosten traurig.', 38134),
(206234, 'deDE', 'Nicht traurig sein. Ihr mögt Lied? Oder Saft?', 'Nicht traurig sein. Ihr mögt Lied? Oder Saft?', 38134),
(206233, 'deDE', 'Viele traurige Aspiranten... Wie können wir ändern?', 'Viele traurige Aspiranten... Wie können wir ändern?', 38134),
(206232, 'deDE', 'Saft gut für Denken. Gut für Sparring. Soll ich Saft holen?', 'Saft gut für Denken. Gut für Sparring. Soll ich Saft holen?', 38134),
(206231, 'deDE', 'Gutes Training heute? Müde? Vielleicht setzen. Vielleicht dehnen.', 'Gutes Training heute? Müde? Vielleicht setzen. Vielleicht dehnen.', 38134),
(206230, 'deDE', '$uGuter Aspirant:Gute Aspirantin;. Was brauchen?', '$uGuter Aspirant:Gute Aspirantin;. Was brauchen?', 38134),
(206229, 'deDE', 'Wie kann dieser hier Freude verbreiten?', 'Wie kann diese hier Freude verbreiten?', 38134),
(206228, 'deDE', 'Sauber machen. Schön machen. Bastion schön, oder?', 'Sauber machen. Schön machen. Bastion schön, oder?', 38134),
(206227, 'deDE', 'AHH! $uNeuer Aspirant:Neue Aspirantin;! Läutet Glocken! Blast Hörner! Bester Tag!', 'AHH! $uNeuer Aspirant:Neue Aspirantin;! Läutet Glocken! Blast Hörner! Bester Tag!', 38134),
(206226, 'deDE', 'Hallo! Ihr neu! Sehr gut! Ja!', 'Hallo! Ihr neu! Sehr gut! Ja!', 38134),
(206225, 'deDE', 'Huu! Hallo! $uNeuer Aspirant:Neue Aspirantin;! Juhu!', 'Huu! Hallo! $uNeuer Aspirant:Neue Aspirantin;! Juhu!', 38134),
(206223, 'deDE', 'Kann ich Euch etwas holen?', 'Kann ich Euch etwas holen?', 38134),
(206222, 'deDE', 'Wir helfen! Was braucht Ihr?', 'Wir helfen! Was braucht Ihr?', 38134),
(206221, 'deDE', '<Der Provost summt eine fröhliche Melodie vor sich hin.>', '<Die Provostin summt eine fröhliche Melodie vor sich hin.>', 38134),
(206220, 'deDE', 'Ihr wollt, ich hole?', 'Ihr wollt, ich hole?', 38134),
(206219, 'deDE', '', 'Wir sind gerade alle ein wenig müde. Die Dürre hat viele Aspekte unserer täglichen Existenz verändert.\n\nAber es muss weitergehen. Wie kann ich helfen?', 38134),
(206218, 'deDE', '<Achillon murmelt etwas, während er seinen Trainingsablauf durchgeht.>', '', 38134),
(206217, 'deDE', '<Phillios scheint in eine Unterhaltung mit Tymon vertieft zu sein.>\n\nIhr könnt Euer ganzes Dasein nicht auf Erinnerungen fokussieren. Holt sie hervor und lasst sie gehen. Ich weiß, das ist leichter gesagt als getan, aber Ihr habt es bald geschafft. Bald ist es vorbei. Da bin ich mir sicher.', '<Phillios scheint in eine Unterhaltung mit Tymon vertieft zu sein.>\n\nIhr könnt Euer ganzes Dasein nicht auf Erinnerungen fokussieren. Holt sie hervor und lasst sie gehen. Ich weiß, das ist leichter gesagt als getan, aber Ihr habt es bald geschafft. Bald ist es vorbei. Da bin ich mir sicher.', 38134),
(206216, 'deDE', '<Tymon scheint in eine Unterhaltung mit Phillios vertieft zu sein.>\n\nIhr seid bei Euren Prüfungen immer so stoisch! Ich wünschte, ich könnte das von Euch übernehmen. Es ist fast so, als ob mein Geist fliehen würde, sobald in mir eine schwierige Erinnerung hochkommt.', '<Tymon scheint in eine Unterhaltung mit Phillios vertieft zu sein.>\n\nIhr seid bei Euren Prüfungen immer so stoisch! Ich wünschte, ich könnte das von Euch übernehmen. Es ist fast so, als ob mein Geist fliehen würde, sobald in mir eine schwierige Erinnerung hochkommt.', 38134),
(206214, 'deDE', '', 'Mein Seelenband scheint Euch zu mögen. Ich sollte Euch wohl danken. Ohne ihre Schmiede hat sie allmählich ihre Begeisterung verloren.\n\nSchaut mal vorbei, falls Ihr in die Nähe des Tempels des Mutes kommt.', 38134),
(206213, 'deDE', '', 'Bitte sorgt Euch nicht um mich. Ich fühle mich... nicht gut, aber mein Paragon wird sich um mich kümmern, sobald sie kann. Bis dahin stützt mich mein Training.\n\n<Eridia atmet zitternd ein.>\n\nIch darf nicht in Kummer versinken, nicht in Wut und nicht in... Lysonia.', 38134),
(206211, 'deDE', 'Wir halten den Zyklus aufrecht.', 'Wir halten den Zyklus aufrecht.', 38134),
(206210, 'deDE', '', 'Diese ganzen verwundbaren Aspiranten... all die Aufgestiegenen, die wir zum Tempel der Loyalität geschickt haben... Ist das aus ihnen geworden? Sie müssen ihren Verstand verloren haben, diesen Tempel anzugreifen! Wir sind nicht ihre Feinde! Sondern nur...\n\n<Eridia verstummt, als sie zum Tempel starrt. Eine einzige Träne läuft über ihre Wange.>', 38134),
(206209, 'deDE', '', 'Ihr werdet $uZeuge:Zeugin; höchst ungewöhnlicher Umstände, sterbliches Wesen. Lasst Euch davon nicht verstören. Wir brauchen in diesen dunklen Zeiten alle bei klarem Verstand.', 38134),
(206208, 'deDE', 'Willkommen im Hain des Erwachens.', 'Willkommen im Hain des Erwachens.', 38134),
(206207, 'deDE', '', 'Ich bin die Hand des Paragons, ihr Wille ist der meinige.', 38134),
(206206, 'deDE', '', 'Braucht Ihr unsere Jünger?', 38134),
(206205, 'deDE', '', 'Wie mein Paragon es befiehlt, so wird es geschehen.', 38134),
(206204, 'deDE', '', 'Beim Willen von Vesiphone.', 38134),
(206202, 'deDE', 'Sie werden nicht zuhören. Sie können uns nicht hören! Diese Gewalt muss aufhören, bevor wir alle verloren sind!', '', 38134),
(206201, 'deDE', 'Ich danke Euch für Eure Hilfe. Wir sind hier schrecklich unterbesetzt.', '', 38134),
(206200, 'deDE', 'Ich habe mich dem Dienst im Tempel der Reinheit verschrieben. Ihr könnt mich immer um Hilfe bitten, solange Ihr auf unserem Land wandelt.', '', 38134),
(206199, 'deDE', '', 'Zu lange habe ich nicht mehr gesehen, dass diese Zeremonie durchgeführt wurde.\n\nHerzlichen Glückwunsch und vielen Dank für die Hoffnung, die Ihr uns gebracht habt.', 38134),
(206198, 'deDE', '', 'Ja, ich bin immer noch hier.\n\n<Schmiedin Sophone kichert.>\n\nEs scheint, als ob meine Schmiede das perfekte Lehrwerkzeug für neue Aspiranten ist. Ich habe die Archon gefragt, ob ich hier im Tiegel bleiben darf, und sie war einverstanden! Wenn das meine Ewigkeit ist, dann bin ich bis in alle Äonen glücklich.', 38134),
(206196, 'deDE', 'Die Nacht segne Euch.', 'Die Nacht segne Euch.', 38134),
(206195, 'deDE', '', 'Eines Tages werde ich aufsteigen. Und bis dahin bin ich sowieso in der Schmiede beschäftigt. Natürlich nur, solange ich die Anima dafür bekomme.', 38134),
(206194, 'deDE', '', 'Es muss seltsam sein, mit einem anderen Aspiranten über Euer Training zu sprechen. Ich habe hier nur das Sagen, weil ich am längsten dabei bin, ganz einfach.\n\n<Schmiedin Sophone kichert.>', 38134),
(206193, 'deDE', '', 'Meine Schmiede ist mein ganzer Stolz. Ich bin nicht ohne Grund die einzige Schmiedeaspirantin!', 38134),
(206192, 'deDE', '', 'Meister Mikanikos ist sehr beschäftigt. Sika stellt sicher, dass Schmiedin bekommt, was sie braucht, ja!', 38134),
(206191, 'deDE', '', '<Sika studiert ihre Schriftrolle.>\n\nAnima? Ja.\nZange? Ja.\nMaterialien? Ja.\nSchmiedin? Ja.\nSika? Ja!\n\nWir haben alles! Juhuu!', 38134),
(206190, 'deDE', '', 'Oh nein! Ist Sika in Schwierigkeiten?', 38134),
(206189, 'deDE', '', 'Sika hat den Begrüßer verpasst. Mnemis weiß alles!\n\nZumindest fast alles.', 38134),
(206188, 'deDE', '', 'Meister Mikanikos, der Oberste Schmied, hat Sika gebeten, sich um die Zenturionen zu kümmern. Mit so wenig Anima entscheiden wir, wer welche kriegt! Jetzt Ihr hier! Anima für Begrüßer gebraucht!\n\nGut, dass Sika Zenturionen zum Teilen hat!', 38134),
(206186, 'deDE', 'Kleia wird mich bei unserer Flucht beschützen. Tut, was Ihr tun müsst, $n. Ich glaube an Euch.', '', 38134),
(206185, 'deDE', 'Oh nein! Wie schrecklich... Ich hoffe, Kleia ist in Sicherheit!', '', 38134),
(206184, 'deDE', 'Wie schlimm. All diese Aspiranten... Vielleicht hat Eridia recht. Ich sollte hierbleiben.', '', 38134),
(206183, 'deDE', 'Etwas wird schmerzlich vermisst, $n...', '', 38134),
(206182, 'deDE', 'Ich freue mich, dass die Hand der Reinheit uns gebeten hat, zusammenzubleiben. Es sieht so aus, als ob Ihr die Situation gut im Griff hättet. Ich bin hier, um Euch falls nötig zu unterstützen. Ich komme schon zurecht.\n\n<Pelagos sieht sich unruhig um.>', '', 38134),
(206181, 'deDE', 'Oh je. Es muss schlimm gewesen sein, wenn ein Jünger seinen Tempel so hinterlässt.', '', 38134),
(206180, 'deDE', 'Ich wusste, dass Ihr es schaffen würdet, $n. Wir werden diese Krise gemeinsam meistern!', '', 38134),
(206179, 'deDE', 'Gebt einfach Euer Bestes! Athanos wurde zur Prüfung sogar der fähigsten Aspiranten entworfen.', '', 38134),
(206178, 'deDE', '<Pelagos scheint in ein Gespräch mit Kleia vertieft zu sein.>', '', 38134),
(206177, 'deDE', 'Euer Leben war so interessant! Ich kann es kaum erwarten, mehr über Euch zu erfahren. Aber jetzt lauscht zuerst der Schmiedin. Sie kennt diesen Ort besser als ich.', '', 38134),
(206176, 'deDE', 'Ich bin sehr neugierig auf Eure Taten, $n. Da Ihr hier seid, müssen sie großartig gewesen sein. Genau wie Eure Hilfe für mich, so kurz nach Eurer Ankunft! Ihr müsst wahrlich für diesen Ort geboren sein!', '', 38134),
(206175, 'deDE', 'Wir können nur versuchen, weiter voranzukommen.', '', 38134),
(206174, 'deDE', '', 'Ich komme mir wie ein ungebetener Gast vor. Dieser Ort ist so alt wie die Bastion selbst.\n\nUnter besseren Umständen würde ich mich glücklich schätzen, diesen Ort zu erblicken.', 38134),
(206173, 'deDE', '', 'Ich habe noch nie gesehen, dass sich eine Mnemiseinheit so benimmt. Das ist so... verstörend.', 38134),
(206172, 'deDE', '', 'Ich werde den Obersten Schmied beschützen. Tut, was Ihr tun müsst, $n.', 38134),
(206171, 'deDE', '', 'Die Verschmähten haben auch diesen Ort übernommen... Ist die komplette Bastion schon unter ihrer Herrschaft?\n\nIch hoffe, Pelagos hat rechtzeitig davon erfahren, aber wir müssen uns auf unsere Aufgabe hier konzentrieren. Wir müssen herausfinden, wer hinter diesen Angriffen steckt!', 38134),
(206170, 'deDE', '', 'Wir müssen herausfinden, warum die Verschmähten angegriffen haben und wer dahintersteckt!', 38134),
(206169, 'deDE', '', 'Ich danke Euch, dass Ihr Pelagos beschützt habt. Ich bin froh, dass Ihr noch in einem Stück seid!\n\nIch muss wissen, dass er in Sicherheit ist. Ich hoffe Ihr versteht das, aber ich kann nicht mit Euch kommen.\n\nIch weiß, Ihr könnt uns helfen und ich glaube fest daran, dass wir uns wiedersehen werden.\n\nGeht und dient, $n.', 38134),
(206168, 'deDE', '', '<Kleia konzentriert sich voll auf das Ritual.>', 38134),
(206167, 'deDE', '', 'Wir haben offenbar keine Zeit, Euren Sieg zu feiern. Wir werden gebraucht.', 38134),
(206166, 'deDE', '', 'Gut gemacht, $n. Ich hoffe die Archon beachtet endlich Eure Bemühungen.', 38134),
(206165, 'deDE', '', '<Kleia ist in ein Gespräch mit Pelagos vertieft.>', 38134),
(206164, 'deDE', '', 'Eines Tages werde ich aufsteigen. Bis dahin helfe ich Euch, Pelagos und allen anderen Aspiranten, wo ich kann.', 38134),
(206163, 'deDE', '', 'Niemand wird versehentlich zur Bastion geschickt, $n. Vergesst das nicht.\n\n<Kleia lächelt Euch an.>', 38134),
(206162, 'deDE', '', 'Ich freue mich darauf, zu sehen, welche Taten Euch vor unsere Tore gebracht haben, $n.', 38134),
(206161, 'deDE', '', 'Kalisthene überwacht alle Aspiranten, die sich derzeit nicht in einem Tempel aufhalten. Ich hätte wissen müssen, dass die Aktivierung des Begrüßers sie aufmerksam macht.\n\nKeine Sorge. Ich kümmere mich darum!', 38134),
(206160, 'deDE', '', 'Tretet in das Becken, $n. Es wird Euch helfen, Euch nach der schwierigen Reise durch den Schleier wieder zu erholen.', 38134),
(206159, 'deDE', '', 'Diese Zenturionen sollten Euch ein wenig Anima bieten können. Wir haben wirklich nicht viel zur Verfügung, aber wir werden das Beste daraus machen.', 38134),
(206157, 'deDE', '', 'Eindringlingsalarm! Aktuelle Bedrohungsstufe: Hoch. Vollstreckereinsatz steht unmittelbar bevor.', 38134),
(206156, 'deDE', '', 'Eindringlingsalarm! Aktuelle Bedrohungsstufe: Moderat.', 38134),
(206155, 'deDE', '', 'Eindringlingsalarm! Aktuelle Bedrohungsstufe: Gering.', 38134),
(206154, 'deDE', 'Singe das Glücklichlied... Glüüückliiichliiiiied...', '', 38134),
(206153, 'deDE', 'Vermisse kleinen besten Kumpel...', '', 38134),
(206152, 'deDE', 'Hoffentlich habt Ihr wieder was zum Naschen gebracht!', '', 38134),
(206151, 'deDE', 'Bester Kumpel!\n\nHabe Frage. Bin ich gut bei Festen?\n\nManchmal lacht Kleinzahn, aber versuche das Beste.\n\nMache besten Kumpel stolz!', '', 38134),
(206150, 'deDE', '<Steinkopf wischt sich mit der freien Hand über das Auge.>\n\nBester Kumpel!!! So froh, dass Ihr hier seid!\n\nWill überall hin mit bestem Kumpel...\n\nKönnte auch versuchen, in Schlünde zu gehen?', '', 38134),
(206149, 'deDE', '', 'Ich habe Pläne bekommen und sie eingehalten.\n\nPläne wurden geschmiedet und ich habe Hoffnung gegeben.\n\nAber alles brennt wie Tränen aus Feuer.\n\nNirgendwo hier kann man sich verstecken. Sie sehen Euch. Und wenn sie es nicht tun, werden es die anderen.', 38134),
(206148, 'deDE', '', 'Hallo Licht, mein alter Freund.\n\nWillst du mich wieder verbrennen?\n\nDas Licht scheint mir ins Gesicht, doch bringt statt Wärme nur die Gicht.\n\nEs heilt voller Schmerzen und brennt voller Leben.', 38134),
(206147, 'deDE', '', 'Wo sind die ganzen Planer hin?\nBrennen, Schmerzen, Sorgen.\nSie schreien lang und ohne Sinn,\nihr Hirn hält nicht bis morgen!\n\nWird Rhythmus oder Reim mich plagen?\nSollte ich\'s wohl noch mal sagen?\nDenn ich hab wieder nichts im Magen.\nWar, was ich tat, ein Grund zum Klagen?', 38134),
(206146, 'deDE', '', 'Sie haben gesagt, es wäre fertig! Fertig! Oder war das vor Ewigkeiten? Oder waren es Ewigkeiten, bis sie fertig sind? \n\nDie Zeit ist kein Jo-jo!\n\nMoment. Weiß ich überhaupt, was ein Jo-jo ist? Wann ist jetzt?', 38134),
(206145, 'deDE', 'Hey, $gSchlundgänger:Schlundgängerin;. Frage für Euch. Die Jungs und ich halten Revendreth blitzblank und bringen hier alles auf Vordermann.\n\nUnd... wer macht das im Schlund? Ich mein, davon ausgegangen, dass Ihr schon mal da wart.\n\nAuf was ich raus will, ist... die, die im Schlund arbeiten, bekommen die Pausen?', '', 38134),
(206144, 'deDE', '', 'Dann könnt Ihr hierher zurückkommen und wir feiern.', 38134),
(206143, 'deDE', '', 'Ich werde meinen Stallmeister senden, um das Maskottchen am Treffpunkt abzuholen.', 38134),
(206142, 'deDE', '', 'Höre ich da, wie sich die Schritte $gmeines wunderschönen:meiner wunderschönen; $p nähern?', 38134),
(206141, 'deDE', '', 'Seht Ihr, Alfryd? Ich wusste, heute wird ein guter Tag. $p ist hier.', 38134),
(206140, 'deDE', '', 'Ah, $uSchlundgänger:Schlundgängerin;. Welchem Umstand verdanke ich diesen Besuch?', 38134),
(206139, 'deDE', '', 'Oh, werter Alfryd, könntet Ihr mir einen neuen...', 38134),
(206138, 'deDE', '', '<Die Gräfin umarmt Euch herzlich.>\n\n$p! Was für Neuigkeiten bringt Ihr aus Sündensturz? Ich hoffe, alle sind wohlauf.\n\nÜberbringt Theotar und dem teuren Prinzen herzliche Grüße, wenn Ihr sie das nächste Mal seht.\n\nOder ich überbringe sie ihnen selber, sollte ich in Bälde dem nächsten Gluthof beiwohnen.\n\nAber genug von Ihnen. Was ist mit Euch? Ich habe Euch seit Eurem letzten Besuch vermisst.', 38134),
(206137, 'deDE', '', 'Ich habe über Euch nachgedacht.\n\nSagt mir, was begehrt Ihr in diesem Moment am meisten?\n\nNicht die Ziele im Leben oder Kindheitsträume. Träume verblassen, Ziele ändern sich.\n\nAber unter der Oberfläche lauert stets der Instinkt. Er zieht Euch von dannen, wenn Ihr unvorsichtig seid.\n\n<Die Gräfin lächelt.>\n\nGut. Sagt es mir nicht. Behaltet es für Euch. Nur so seid Ihr sicher.', 38134),
(206136, 'deDE', '', 'Sagt mir, was führt $uden mächtigen Schlundgänger:die mächtige Schlundgängerin; heute in meinen Turm?\n\nEin weiterer Gefallen vielleicht?\n\n<Die Gräfin lächelt.>\n\nSeid vorsichtig, wen Ihr um Gefallen bittet.\n\nViele arme Seelen waren von Verlangen geblendet und standen schon bald in der Schuld der widerwärtigsten Meister.', 38134),
(206133, 'deDE', 'Ich werd zwar nicht so rührselig wie Ihr Sterblichen, aber ich musste grad an den Tag denken, an dem wir uns getroffen haben.\n\nNoch eine öde Schicht voller Nichtstuerei in Stolzsturz... und plötzlich kommt $uder Schlundgänger:die Schlundgängerin; um die Ecke, um die ganzen Schattenlande mal so richtig wachzurütteln!\n\nHätte nie gedacht, dass wir mal beste Freunde werden. Schon ein Kracher...', '', 38134),
(206132, 'deDE', 'He, Kumpel.\n\nLasst es mich wissen, wenn was für Euch erledigt werden soll. Es stehen ein paar Sachen an, aber ich kann Zeit freischaufeln, wenn Ihr was habt.\n\nFreue mich übrigens auf den nächsten Gluthof. Theotar hat mir ein paar neue Sachen beigebracht, die ich zu den feinen Leuten sagen soll.', '', 38134),
(206130, 'deDE', 'Ich habe etwas Zeit, falls es was zu erledigen gibt.', '', 38134),
(206129, 'deDE', 'Hey, wie geht\'s Euch?\n\nIch und Knüppelfratze sind neuerdings gesellschaftlich ganz schön eingebunden dank Euch und unseren neuen Freunden vom Gluthof.\n\nAber für $uunseren BESTEN Freund:unsere BESTE Freundin; haben wir immer Zeit, $p.\n\nIhr müsst es nur was sagen, schon sind wir da.', '', 38134),
(206127, 'deDE', 'He, Kumpel. Habt Ihr die feine Gesellschaft über mich und Knüppelfratze reden hören?\n\nWir schaffen es nicht zu jedem Gluthof, aber ich will sicher sein, dass sie uns nicht vergessen haben.\n\nEine Pause von der Schlammarbeit für einen Besuch am Hof war schön.\n\nIhr und der Prinz behandelt die Gräber gut. Respekt!', '', 38134),
(206125, 'deDE', 'Ich mag aber meinen schmutzigen alten Hut...', '', 38134),
(206124, 'deDE', 'Mit uns geht\'s aufwärts, Knüppelfratze. Wir müssen anfang\'n, uns wie diese Drecksadligen anzuzieh\'n.', '', 38134),
(206122, 'deDE', 'Verstehe immer noch nicht, warum es so ein schicker Hut sein muss, Rendel...', '', 38134),
(206120, 'deDE', '$p ist beschäftigt, Knüppelfratze. Die Schattenlande retten und so. Außerdem ist $uer:sie; $uMEIN bester Freund:MEINE beste Freundin;!', '', 38134),
(206119, 'deDE', 'He, $gmein Lieblingsschlundgänger:meine Lieblingsschlundgängerin;! Hab Euch auf meiner letzten Kutschenrunde vermisst.', '', 38134),
(206118, 'deDE', 'Natürlich brauch ich die! Ich merk schon, keinerlei Verständnis für Mode.', '', 38134),
(206117, 'deDE', 'Ich sag\'s doch, Rendel. Ihr braucht nicht noch mehr Hüte...', '', 38134),
(206116, 'deDE', 'Nicht glotzen. Das ist unhöflich!', '', 38134),
(206115, 'deDE', 'He, schaut, es ist $uder ach so tolle Schlundgänger:die ach so tolle Schlundgängerin;!', '', 38134),
(206114, 'deDE', 'Diese Halunken haben keinen Respekt vor der Heiligkeit dieser Hallen!', '', 38134),
(206113, 'deDE', '$p! Was für eine schöne Überraschung, Euch zu sehen!', '', 38134),
(206112, 'deDE', 'Ich hoffe, dass Ihr das seid, $p...', '', 38134),
(206111, 'deDE', 'Wer ist da? Zeigt Euch!', '', 38134),
(206110, 'deDE', 'Ah, Ihr seid es, $umein Guter:meine Gute;. Ich hatte gehofft, dass Ihr bald vorbeikommt.\n\nEs fühlte sich wie eine Ewigkeit seit unserem letzten gemeinsamen Gluthof an. Ich gehe davon aus, dass die Dinge in Sündensturz unter Eurem wachsamen Blick gut stehen?\n\nDank Eurer Hilfe haben die Eingeschworenen und ich große Fortschritte bei der Rettung der verlorenen Seelen hier gemacht.\n\nIhr seid $uein guter Freund:eine gute Freundin;, $p. Und, viel wichtiger, eine gute Person.\n\nOder zumindest gut darin, wie eine zu wirken!\n\n<Grufthüter Kassir lacht.>\n\nAber ich glaube kaum, dass Ihr hier seid, um Euch meine alten Witze anzuhören.\n\nWas bringt Euch heute in meinen Winkel von Revendreth?', '', 38134),
(206109, 'deDE', 'Ah, $p.\n\n<Der Grufthüter seufzt erleichtert und entspannt sich.>\n\nEin willkommenes Gesicht unter all den Verrätern und Entweihern.\n\nIch mache hier weiter Fortschritte, muss aber zugeben, dass ich abgelenkt war.\n\nMeine Gedanken wandern oft zum nächsten Gluthof...\n\nIch bin immer noch überrascht, dass Ihr und Prinz Renathal mich für würdig haltet, wiederholt eingeladen zu werden. Ich fühle mich natürlich geehrt.\n\nAber genug von meinem Geschwätz. Was kann ich heute für Euch tun?', '', 38134),
(206108, 'deDE', 'Das wird lustig!', '', 38134),
(206107, 'deDE', 'Ein bisschen reisen, ein bisschen Gormschmieden, ein kleines Abenteuer...', '', 38134),
(206106, 'deDE', 'Es ist sehr schön hier unten, nicht wahr? Ruhig und friedlich. Nur hart arbeitende Pilzomanten wie Ihr und Cort da drüben. Und natürlich ich. Alle arbeiten hart und halten den Ardenwald am Laufen.$b$BSchön, dass Ihr mich besucht. Zu mir kommen in letzter Zeit nicht viele und mit Euch kann man sich immer wunderbar unterhalten. Ich wollte Euch nur wissen lassen, dass ich das sehr schätze.$b$BDas heißt aber nicht, dass Ihr jetzt in Watte gepackt werdet! Es gibt noch viel zu tun!', '', 38134),
(206105, 'deDE', '', 'Ein Venthyrhof klingt... interessant.', 38134),
(206104, 'deDE', '', 'Wenn ich offen sprechen darf... Ich bin froh, dass der Polemarch Euch zu mir geschickt hat.', 38134),
(206103, 'deDE', 'Ich bin schon seit ewigen Zeiten hier unten. Ich weiß noch genau, wie es war, als nur die Königin und ich hier waren. Wir waren so beschäftigt damals. Sie arbeitet wirklich hart....$b$bNicht wie diese Faulpelze von heute. Sie wollen immer nur die Ringe benutzen. Niemand fragt die Ringe, wie es ihnen dabei geht! Ich sollte der Königin wirklich sagen, dass sie ihre Untertanen zur Ordnung rufen muss!', '', 38134),
(206102, 'deDE', 'Willkommen beim Ring, Jungblut.$b$BHier unten ist nicht jeder willkommen, müsst Ihr wissen. Nur meine vertrauenswürdigsten Pilzomanten dürfen mein Heim betreten, das Zentrum der Faeringe.$B$BDas bedeutet, dass Ihr nun einer meiner Pilzomanten seid! Macht so weiter und Eure Zukunft wird großartig werden. Ich weiß es einfach.', '', 38134),
(206101, 'deDE', 'Hoffentlich isses nicht zu heiß...', '', 38134),
(206100, 'deDE', 'Hab gehört, es soll ziemlich hell sein im Glutdistrikt, ja?', '', 38134),
(206099, 'deDE', '', 'Beim Gluthof, versteht sich...', 38134),
(206098, 'deDE', '', 'Nun gut, dann sehen wir uns beim Hof.', 38134),
(206096, 'deDE', '', 'Ihr seid doch nicht verfolgt worden, oder?', 38134),
(206095, 'deDE', '', 'Bitte sagt, dass Ihr etwas Interessantes zu besprechen habt, $uSchlundgänger:Schlundgängerin;.', 38134),
(206091, 'deDE', 'Oh, der Wurzelkeller! Schön, dass es ihn noch gibt. Ihr habt in letzter Zeit wirklich viel getan. Macht mal eine Pause und kommt zurück, wenn Ihr Euch wieder erholt habt.', '', 38134),
(206088, 'deDE', 'Ist Euer Ehrgeiz größer als Eure Fähigkeiten, $uSchlundgänger:Schlundgängerin;?', 'Ist Euer Ehrgeiz größer als Eure Fähigkeiten, $uSchlundgänger:Schlundgängerin;?', 38134),
(206085, 'deDE', 'Welche Demonstration Eures Kampfgeschicks, $uSchlundgänger:Schlundgängerin;. Ist Eure Blutlust noch nicht gestillt?', '', 38134),
(206077, 'deDE', 'Bolvar leidet. Dunkle Mächte wenden sich gegen uns.', '', 38134),
(206075, 'deDE', '', 'Der Primus hat sie komplett vernichtet. Doch obwohl es keine Überlebenden gab, brennen ihre Feuer an diesem Ort nach wie vor. Ehrt jene, die hier Maldraxxus verteidigt haben.', 38134),
(206073, 'deDE', 'Nach so vielen harten Schlachten und Kriegen um des Lebens willen ist es... interessant... so viele Antworten darauf zu erhalten, was danach geschieht.', '', 38134),
(206070, 'deDE', '', 'Zenturionen. Diese durch Anima erweckten Konstrukte dienen vielen Zwecken in der Bastion.\n\nSie helfen den Aspiranten beim Training, verteidigen unser Land und übernehmen bei Bedarf niedere Tätigkeiten. Einige, wie die Mnemiseinheiten wie ich, sammeln das Wissen für die Archive.', 38134),
(206069, 'deDE', '', '<Begrüßer Mnemis lässt ein Läuten ertönen.>\n\nAh. Eulen. In Eurem Reich sind das nachtaktive Vögel, nicht wahr? \n\n<Begrüßer Mnemis zeigt auf Sika.>\n\nDas ist eine Provostin. Sie manifestieren sich aus der Anima der Bastion und helfen den Kyrianern bei ihren täglichen Aufgaben. Sie lieben es, zu Diensten zu sein.', 38134),
(206060, 'deDE', 'Ihr tragt viele Sachen. Werdet müde vom Rumrennen in diesem ganzen Zeug?', 'Ihr tragt viele Sachen. Werdet müde vom Rumrennen in diesem ganzen Zeug?', 38134),
(206055, 'deDE', 'Könnt meine Schriftrolle signieren?!', 'Könnt meine Schriftrolle signieren?!', 38134),
(206054, 'deDE', 'Ich bin Euer größter Fan, $n!', 'Ich bin Euer größter Fan, $n!', 38134),
(206052, 'deDE', 'Man sollte meinen, dass Ihr interessantere Dinge zu tun habt, als mit mir zu reden, nicht wahr?', '', 38134),
(206050, 'deDE', 'Was verschwindet, sobald Ihr seinen Namen nennt?', '', 38134),
(206049, 'deDE', '<Fürst Renard grinst. Das sind eine Menge Zähne.>', '', 38134),
(206048, 'deDE', 'Hmmmmm. Ja. Mit Euch dürfte das funktionieren.', '', 38134),
(206047, 'deDE', 'Sieh einer an... Was haben wir denn da? $GEin:Eine:c; $C. Wie interessant.', '', 38134),
(206042, 'deDE', '', 'Seid Ihr das, $uSchlundgänger:Schlundgängerin;? Ja, Balmedar hat mich schon über Eure kleine List informiert.\n\nDennoch sind Eure Leistungen für das Haus der Rituale nicht zu übersehen. Ich betrachte Euch als $uVerbündeten:Verbündete;, also werden meine Diener nichts von Eurer Verkleidung erfahren.\n\nGenießt Eure Zeit hier, \"$uAkolyth:Akolythin;\".', 38134),
(206041, 'deDE', '', 'Eine Markgräfin direkt anzusprechen steht Euch nicht zu, $uAkolyth:Akolythin;.\n\nEs wäre wohl besser, wenn Ihr jetzt geht.', 38134),
(206039, 'deDE', 'Eine äußerst interessante Waffe habt Ihr da, $uAkolyth:Akolythin;.\n\nIhr denkt doch nicht etwa über einen Berufungswechsel nach?', '', 38134),
(206037, 'deDE', 'Ich hoffe, dass wir endlich Frieden finden.', 'Ich hoffe, dass wir endlich Frieden finden.', 38134),
(206036, 'deDE', 'Ich muss immerzu an all unsere Gefallenen denken. Warum musste das nur geschehen?', 'Ich muss immerzu an all unsere Gefallenen denken. Warum musste das nur geschehen?', 38134),
(206035, 'deDE', 'Unser neues Heim... Es ist so wunderschön.', 'Unser neues Heim... Es ist so wunderschön.', 38134),
(206034, 'deDE', 'Es tut so gut, wieder im Wald zu sein. Ich danke Euch.', 'Es tut so gut, wieder im Wald zu sein. Ich danke Euch.', 38134),
(206033, 'deDE', 'Fühlt sich gut an, wieder Erfolg zu haben!', '', 38134),
(206016, 'deDE', '', 'Noch nie habe ich von einem so schlimmen Konflikt gehört. Die Lehren meiner Matrone haben mich zwar vorbereitet, dennoch fühle ich mich mitunter überwältigt.\n\nAber das sage ich Euch im Vertrauen. Die Baronin würde mich für meine Schüchternheit maßregeln.', 38134),
(206014, 'deDE', 'Ihr habt Euch als nützlich erwiesen, $n. \n\nSolltet Ihr jemals auf konventionellem Wege nach Maldraxxus zurückkehren, wärt Ihr im Haus der Rituale sehr willkommen.', '', 38134),
(206013, 'deDE', '', 'Bei meinem letzten Aufenthalt hier war der Primus noch da.\n\nDa wir nun wieder hier sind, kann ich seine Schmiede nicht verwaisen lassen... Das würde er mir ständig unter die Nase reiben.', 38134),
(206008, 'deDE', '', 'Als zusätzliche Belohnung für Eure Unterstützung könnt Ihr gerne mein Mannequin benutzen.\n\nEs erlaubt Euch, Eure Kleidung weiter anzupassen, aber selbstverständlich müsst Ihr dazu erst noch weitere Accessoires erwerben.', 38134),
(206007, 'deDE', '', 'Was wollt Ihr von mir, $uAkolyth:Akolythin;?', 38134),
(205988, 'deDE', 'Vergesst nicht, diskret zu sein, $n.\n\nNiemand darf von unserer... Abmachung erfahren.', '', 38134),
(205975, 'deDE', 'Tja, ich versuche, mich für Marasmius um diese Pilze zu kümmern. Die Dürre hat den armen Wurzeln wirklich böse zugesetzt.', 'Tja, ich versuche, mich für Marasmius um diese Pilze zu kümmern. Die Dürre hat den armen Wurzeln wirklich böse zugesetzt.', 38134),
(205973, 'deDE', 'Hallo! \n\nIch hab leider keine Zeit zum Quasseln! Diese Pilze hüten sich nicht von allein.\n\n<Er kichert.>\n\nKann ich Euch helfen?', 'Hallo! \n\nIch hab leider keine Zeit zum Quasseln! Diese Pilze hüten sich nicht von allein.\n\n<Zacki kichert.>\n\nKann ich Euch helfen?', 38134),
(205972, 'deDE', 'Verzeihung, aber Ihr habt die Gastlichkeit meines Sanktums verweigert.', '', 38134),
(205971, 'deDE', '', 'Wir kamen, um Gerechtigkeit zu finden. Vergeltung. Als Antwort auf eine Beleidigung des Lichtes selbst.\n\nWir waren zahlreich. Unermesslich viele. Und in unser aller Herzen brannte die rechtschaffene Wut.\n\nViele sind gefallen. Doch zuvor haben wir ein Feuer entfesselt, dem unsere Feinde nicht standhalten konnten. Die schwelende Narbe unserer Rache hängt noch immer am Firmament.\n\nIch lag gebrochen auf dem Schlachtfeld. Bis die Ungesehenen mich fanden. Mich bewahrten. Um als Gefäß für die Wut ihres Herrschers zu dienen.\n\nDoch ich hielt durch. Ich wusste, dass das Licht mir ein Schicksal vorbestimmt hatte. Genau wie Eurer Art auch, $uSterblicher:Sterbliche;.', 38134),
(205969, 'deDE', '', 'Die Heimat. Sie ruft mich, $uSterblicher:Sterbliche;. Und doch werde ich ihre Wärme nie wieder erfahren.\n\nMein Volk würde mich nicht mehr wie früher wahrnehmen, nur noch als Entartung. Zerbrochen. Von der Berührung des dunklen Meisters dieses Reichs befleckt.\n\nFür mich gibt es nur noch einen Weg: Ich muss sicherstellen, dass das Licht hier gespürt wird. Dass Gerechtigkeit geübt wird. Und dass der Tod in seine Schranken verwiesen wird.\n\nDarum bleibe ich hier. Pflichtgebunden, bis auch das letzte Flackern meines Daseins erlischt.', 38134),
(205968, 'deDE', 'Es wird Zeit brauchen. Zeit, die Euresgleichen nicht messen kann. \n\nAber in dieser Zeit wird mein ältester Freund, mein engster Vertrauter und mein größter Meister darüber hinauswachsen und sich wieder seiner wahren Aufgabe zuwenden.', '', 38134),
(205950, 'deDE', 'Ihr seid so entschlossen und tapfer, Awool. Ich bin stolz auf Euch.', '', 38134),
(205949, 'deDE', 'Ja! Das ist ja so aufregend. Eine Pause ist jetzt genau das Richtige. Danach bin ich dann wieder bereit, den Ardenwald zu verteidigen.', '', 38134),
(205948, 'deDE', 'Awool! Habt Ihr das gehört? Wir sind zu einem Fest eingeladen!', '', 38134),
(205946, 'deDE', '2...', '2...', 38134),
(205945, 'deDE', 'Unser Kodex verlangt die Wiederholung dieser Prüfungen, bis sie gemeistert wurden.', 'Unser Kodex verlangt die Wiederholung dieser Prüfungen, bis sie gemeistert wurden.', 38134),
(205944, 'deDE', 'Wir werden ihre Jagd aufhalten! Haus Sündzahn steht an der Seite von Haus Zornmeer!', 'Wir werden ihre Jagd aufhalten! Haus Sündzahn steht an der Seite von Haus Zornmeer!', 38134),
(205942, 'deDE', 'ICH WAR NOCH NICHT FERTIG, DIEB!', 'ICH WAR NOCH NICHT FERTIG, DIEB!', 38134),
(205941, 'deDE', '1...', '1...', 38134),
(205940, 'deDE', 'Ich zähle bis fünf.', 'Ich zähle bis fünf.', 38134),
(205939, 'deDE', 'Was macht Ihr da? Steigt sofort von Balthazar ab!', 'Was macht Ihr da? Steigt sofort von Balthazar ab!', 38134),
(205938, 'deDE', 'Kevin wird zu Euch und unseren neuen Freunden stoßen, nachdem er seine eigene Abkürzung genommen hat. Treibt sie einfach zu ihm, und er erledigt den Rest!', '', 38134),
(205925, 'deDE', '', 'Alexstrasza sieht Euer Reittier und runzelt enttäuscht die Stirn.', 38134),
(205919, 'deDE', '', 'Auch wenn ich mir sicher bin, dass Theotar von solch einem Streich angetan wäre. Hmm.', 38134),
(205918, 'deDE', '', 'Nein, zu direkt.', 38134),
(205913, 'deDE', 'Welche Währung soll es heute sein, $p?\n\nMünzen oder Tod?', '', 38134),
(205876, 'deDE', 'Kommt zu mir, verwirrte Seele. Ich möchte mit Euch reden.', 'Kommt zu mir, verwirrte Seele. Ich möchte mit Euch reden.', 38134),
(205862, 'deDE', 'In diesem Bereich habt Ihr Zugang zu Dienstleistungen, die sich um Euren materiellen Besitz drehen. Da dieses Konzept noch relativ neu für uns ist, waren die Mittler so nett, diese Dienste für Euch bereitzustellen.', '', 38134),
(205856, 'deDE', 'Ooh, ich nehm gern noch einen. Was Süßes <hicks!>... was Süffiges. Schon mal \'nen Animai Tai probiert? Keiner maxt... müxt... mixt den besser als Mak.', '', 38134),
(205855, 'deDE', 'Ich könnt definitiv noch was vertragen bei der ganz\'n Mes... Müs... Misere hier. Findet mir was mit ordentlich Umdrehungen. Ich brauch \'n Stützgetränk, um wieder zu Sinnen zu komm\'n un\' mich zu erinnern.', '', 38134),
(205854, 'deDE', 'Echt? Ihr wollt mir \'nen Drink spendier\'n? Ihr seid $gmein neuer bester Freund:meine neue beste Freundin;!$b$BIch hätt gern \'nen Spriggan Sour. Der hat \'nen Kick, da krai... krau... <hicks!> kräuseln sich Euch die Lippen, bis Euch die Wangen schmerz\'n! Ha!', '', 38134),
(205853, 'deDE', 'Nein, danke. Hab schon viel zu viel intus. Muss wieder <hicks!> nüchtern werden!', '', 38134),
(205850, 'deDE', 'Hallo... <hicks!> Könnt Ihr mir mal aushelf\'n?$b$BIch heiß Schimmerschlürf, und ich bin \'n Meister der Illusionen! Glaub ich zumindest. Also, ich hab vielleicht \'n bisschen viel intus und äh... <hicks!>...$b$bIch weiß nich\' mehr, wer von mir der Echte is\'. So fühlt sich wohl \'ne <hicks!> existent... existens... existenzielle Krise an. Mannomann.', '', 38134),
(205849, 'deDE', '<Großmeister Vole mustert Euch von oben bis unten.>\n\nIhr werdet genügen.\n\n<Großmeister Vole erhebt seine Stimme zu den Umstehenden.>\n\nFrischfleisch für das Theater der Schmerzen!', '', 38134),
(205848, 'deDE', 'Das war eine exzellente Vorstellung, würde ich meinen. Ich habe bereits mehrere Komplimente erhalten.$B$BHabt Dank, $gmein Freund:meine Liebe;. Ich stoße nicht oft auf ein musikalisches Talent, das es mit meinem eigenen aufnehmen kann.', '', 38134),
(205846, 'deDE', 'Ihr einigen Nachtfae, Ihr einigen Nachtfae, ohhh!', '', 38134),
(205845, 'deDE', 'Reichet Anima Eurem Jäger,', '', 38134),
(205844, 'deDE', 'Vor seiner nächsten Quest!', '', 38134),
(205843, 'deDE', 'Drum schenkt ihm Euren Jubel...', '', 38134),
(205842, 'deDE', 'Bestand der Held den Test.', '', 38134),
(205841, 'deDE', 'Trotz allem Zank und Trubel...', '', 38134),
(205840, 'deDE', 'Das Scheusal zu bezwingen.', '', 38134),
(205839, 'deDE', 'Um mit nur einem Stoß...', '', 38134),
(205838, 'deDE', 'Der Wolf zog seine Klingen...', '', 38134),
(205837, 'deDE', 'Die Bestie war groß...', '', 38134),
(205836, 'deDE', 'Drei Mäuler ohne Knebel!', '', 38134),
(205835, 'deDE', 'Da war, wonach wir suchten...', '', 38134),
(205834, 'deDE', 'Und drangen durch den Nebel...', '', 38134),
(205833, 'deDE', 'Wir stapften durch die Schluchten...', '', 38134),
(205832, 'deDE', 'Bei der Königin, das ist gut! Seid Ihr sicher, in Eurem vorherigen Leben $gkein Barde:keine Bardin; gewesen zu sein?$b$B<Narzisse braucht einen Augenblick, um sich wieder zu sammeln.> Nun, dann wäre das Lied wohl fertig. Möchtet Ihr es hören?', '', 38134),
(205831, 'deDE', 'Hmm, an sich ist das nicht verkehrt. Die Händchen der Anwesenden hier sollten uns lieber Bares zustecken, anstatt untätig zu sein. Auf der anderen Seite wäre es vielleicht effektiver, ihnen etwas zu schmeicheln, anstatt sich über die Größe ihrer Hände lustig zu machen, findet Ihr nicht?', '', 38134),
(205830, 'deDE', 'Menschen? Was sind denn Menschen? Seid Ihr ein Mensch? In dem Lied hier geht es nicht um Euch, wisst Ihr? Es soll die Nachtfae des Ardenwalds, besonders die hier anwesenden, dazu inspirieren, dass wir Helden sind, die Anerkennung, Bewunderung und Bezahlung verdient haben!', '', 38134),
(205829, 'deDE', 'Das gefällt mir! Es bringt einen gewissen Tatendrang mit sich und erinnert alle daran, dass er hier auch weiterhin für Frieden sorgen wird. Probieren wir das. Und jetzt zum großen Finale!$B$BReichet Anima Eurem Jäger, Ihr... Ihr...', '', 38134),
(205828, 'deDE', 'Das wäre natürlich etwas, das ich mir nicht entgehen lassen würde, aber wir sind nicht nur zum Spaß hier. Und sobald alle ihren Rausch ausgeschlafen haben, sind unsere Taschen vermutlich leerer als zuvor. Da spreche ich aus bester Erfahrung.', '', 38134),
(205826, 'deDE', 'Hui, das ist aber ziemlich starker Tobak. Wir wollen doch nicht etwa suggerieren, dass unser Held nicht mit einer kleinen Seuche klarkommen würde, oder? Das würde vermutlich wenig Vertrauen in seine Verlässlichkeit wecken.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205825, 'deDE', 'Ich habe seit einer Ewigkeit jungen, ehrgeizigen Elfen geholfen, ihren Weg zu finden.\n\nJetzt stehe ich vor meinem eigenen großen Abenteuer. Ich frage mich, ob auch sie sich so gefühlt haben, als sie das erste Mal einen neuen Hain betreten haben.', '', 38134),
(205823, 'deDE', 'Ortims Brut ist anfällig für die anderen Arten von Schleim. Nutzt diese Schwäche zu Eurem Vorteil.', '', 38134),
(205822, 'deDE', '', 'Ich weiß, dass Ihr mich aus diesem schrecklichen dunklen Ort gerettet habt, und bin dafür sehr dankbar.\n\nAber ich kann niemals das Leid vergessen, das die Horde über mein Volk gebracht hat.\n\nDer Gerechtigkeit muss Genüge getan werden.', 38134),
(205821, 'deDE', '', 'Der Krieg hat unser Volk mehr gekostet als alle andere.\n\nDas darf nicht ungesühnt bleiben.\n\nIch hoffe, Ihr steht uns zur Seite, wenn die Stunde unserer Rache endlich gekommen ist.', 38134),
(205818, 'deDE', 'Athanos hält nun nichts mehr zurück. Hütet Euch vor seinen mächtigen Schlägen... und haltet Euch nicht zu nahe am Rand der Arena auf.', '', 38134),
(205817, 'deDE', 'Oooh, gar nicht schlecht. Hebt sehr schön hervor, dass es sich hier um eine wirklich wilde Bestie gehandelt hat und nicht um ein abgerichtetes Schoßtier!$b$BMoment, woher wusstet Ihr eigentlich, dass es drei Mäuler waren?$b$BWie dem auch sei, hier ist die nächste Strophe:\nTrotz allem Zank und Trubel\nBestand der Held den Test\nDrum schenkt ihm Euren Jubel\n... Hmm, schenkt Ihm Euren Jubel und...?', '', 38134),
(205816, 'deDE', 'Also, das ist zwar nicht ganz verkehrt... das Ding war schon ziemlich eklig... aber es ist auch irgendwie nicht besonders beeindruckend, oder? Außerdem lässt der Reim etwas zu wünschen übrig. Hmm...', '', 38134),
(205815, 'deDE', 'Was? Wir haben gegen einen Boggart gekämpft, was soll das mit einem Hebel zu tun haben? Und warum ist der \"lang gefurcht\"? Das ergibt doch überhaupt keinen Sinn... aber immerhin: Der Reim ist fett... der Reim ist fett.$b$B<Narzisse zwinkert Euch zu.>', '', 38134),
(205814, 'deDE', 'Super! Hier ist die erste Strophe:\nWir stapften durch die Schluchten\nUnd drangen durch den Nebel\nDa war, wonach wir suchten\n... und jetzt fällt mir nichts mehr ein. Ideen?', '', 38134),
(205813, 'deDE', 'Behaltet Korinth immer im Auge. Er wird verschwinden, um Anima zu verzehren, um sich zu stärken.', '', 38134),
(205812, 'deDE', 'Danke, dass Ihr mich zurückgebracht habt.\n\nKönnt Ihr die Augen nach jemandem aufhalten, falls Ihr wieder in den Schlund zurückkehrt?\n\nIhr Name ist Delaryn.', '', 38134),
(205811, 'deDE', 'Thran\'tiok bezieht ihre Macht aus ihren Phylakterien. Solange sie intakt sind, ist sie unverwundbar, aber jede Zerstörung eines Phylakteriums verleiht ihr eine mächtige Fähigkeit.', '', 38134),
(205810, 'deDE', 'Drustblütenn können Euch vor Angriffen dieser Kreatur schützen, aber nur für kurze Zeit.', '', 38134),
(205809, 'deDE', 'Oh, seid erneut gegrüßt! Ich könnte schwören, dass wir uns schon einmal begegnet sind...$B$BWie dem auch sei, Ihr kommt genau zur rechten Zeit. Ich habe hier gerade eine kleine Schreibblockade und frage mich, ob Ihr mir bei einem Liedtext aushelfen könntet. Es ist ein Lied über meinen edlen Freund dort drüben und unseren Kampf gegen eine wahrlich furchteinflößende Kreatur.', '', 38134),
(205808, 'deDE', '', '<Mak gießt die Flüssigkeit in einen Mixbecher und beginnt, ihn energisch zu schütteln.>\nEuch umbringen? Warum? Seid Ihr lebendig oder wie? HA!$b$BOh, Ihr meint das ernst. Nee, Gormsaft schadet Euch nicht. Gibt dem Ganzen einen milden, süßlichen Abgang.\n<Mak reicht Euch das Glas.>\nWohl bekomm\'s!', 38134),
(205807, 'deDE', '', '<Mak mustert Euch von oben bis unten.>$b$bJa, ich hab genau das Richtige für Euch. Dazu brauch ich aber Gormsaft, und der ist mir gerade ausgegangen. Macht es Euch was aus, etwas mehr von Pressi da drüben zu besorgen? Macht Euch keine Sorgen darüber, ihm wehzutun... er mag die Aufmerksamkeit.', 38134),
(205806, 'deDE', '', 'Elune hat uns nicht vergessen. Dass Ihr uns gerettet habt, ist Beweis dafür.\n\nIch frage mich, ob wir noch eine Rolle zu spielen haben... ob es noch eine Pflicht zu erfüllen gibt.\n\nWir müssen geduldig sein.', 38134),
(205805, 'deDE', '', 'Die ehrgeizigen Bestrebungen der Nekrolords stehen einer existenziellen Bedrohung gegenüber, die von unseren eigenen Reihen ausgeht. Solltet Ihr uns erneut Eure Kraft schenken, werden wir gemeinsam ungleiche Fragmente zu einer Armee schmieden, die es mit dem Schicksal selbst aufnehmen kann.', 38134),
(205804, 'deDE', 'Der Weg zur Erlösung Revendreths ist ein weiter, doch der dunkle Prinz von Sündensturz ist bereit, ihn zu gehen. Ihr habt uns bereits bei den ersten Schritten geholfen. Nun müssen wir dem Meister und seinen Loyalisten die Herrschaft über Revendreth entreißen.', '', 38134),
(205803, 'deDE', '', 'Hass war der Anfang hiervon.\n\nAber ich hoffe, dass es mit Gerechtigkeit enden wird.', 38134),
(205801, 'deDE', '', 'Als ich meine Augen schloss, dachte ich, dass Elunes Gesicht das nächste sein würde, das ich erblicken werde.\n\nDanke, dass Ihr uns nicht aufgegeben habt.', 38134),
(205800, 'deDE', '', 'Ihr habt mit eigenen Augen gesehen, dass die Haine des Ardenwalds sterben. Mit jedem fallenden Himmelsbaum wächst die Verzweiflung unserer Königin. Die Königin und ihr Hof der Nacht kennen Eure Kraft. Helft uns, das Gleichgewicht im Ardenwald wiederherzustellen.', 38134),
(205798, 'deDE', 'Unsere edlen Kyrianer müssen die schlimmen Konsequenzen des Verrats bewältigen, den Ihr in der Bastion bezeugt habt. Die Streitkräfte der Verschmähten drohen, unser Reich gänzlich aus den Fugen zu heben. Eure Hilfe wird unabdingbar sein, wenn wir die Bastion zurückerobern und die Schattenlande retten wollen.', '', 38134),
(205797, 'deDE', 'Ihr müsst leichtfüßig bleiben, um diese Herausforderung zu bewältigen! Achtet auch auf Alderyns Vortex. Im Auge des Sturms ist es am sichersten.', '', 38134),
(205796, 'deDE', 'Diese Kriecher sind gefährlich. Haltet Abstand und sorgt dafür, dass sie Euch nicht in den widerwärtigen Abfall treiben!', '', 38134),
(205795, 'deDE', 'Achtet auf die Speere. Drei von ihnen werden Euch zum Stillstand bringen, also sind alle Gegenstände und Fähigkeiten wichtig, die Euch befreien können!', '', 38134),
(205794, 'deDE', 'Glänzend! Mit dem Hut ist man auf jeden Fall der Prinz unter uns Gräbern.', '', 38134),
(205793, 'deDE', 'Na, wenn das mal nicht \'n schicker Hut ist. Schau mal einer an, wie der abgeht.', '', 38134),
(205791, 'deDE', 'Habt Ihr jemals einen so fantastischen Wald gesehen? Diese Bäume!\n\nDanke, dass Ihr mich aus diesem schrecklichen Ort befreit habt, damit ich all das mit eigenen Augen erblicken konnte.', '', 38134),
(205790, 'deDE', 'Na, sieht das mal nich\' lässig aus?', '', 38134),
(205789, 'deDE', 'Ihr wollt den Hut wirklich am Hof tragen? Okay. Macht, was Ihr wollt, Rendel. Solang\' wir dann jetzt geh\'n können.', '', 38134),
(205788, 'deDE', 'Ha! Schaut mal mein\' Hut an. Hübsch, pink und spitz.', '', 38134),
(205785, 'deDE', '', 'Tja, sieh einer an!\n\nNun, vielleicht kann ich Euch mit einer Schriftrolle aushelfen, die Euch zurück zum Hauptteil der Insel bringt. Wir sind hier immerhin etwas ab vom Schuss.', 38134),
(205783, 'deDE', '', 'Sieh an! Ihr müsst $gein:eine:r; $R sein, nicht wahr? Jemanden wie Euch habe ich schon lang nicht mehr gesehen.\n\nSeid Ihr gekommen, um Euch vor all der wilden Nekromantie zu verstecken? Ich weiß nicht, wie gut Eure Art unter Wasser atmet.\n\nWir warten alle darauf, dass sich der Staub wieder legt, bevor wir weiterziehen. Sollte sich in fünf bis zehn Jahren erledigt haben. Gar nicht so lang für mich.\n\nFür Euch allerdings schon, vermute ich...', 38134),
(205781, 'deDE', '', 'Natürlich kenn ich ihn. Gwynceirw höchstpersönlich! Und wer könnte schon seinen treuen Gefährten Narzisse vergessen?$b$bMacht einfach nur keinen Ärger, okay? Letztes Mal, als ein Haufen Spriggans hier reinkam und Stress machen wollte, haben sie sich mit ihm angelegt. Hat ewig gedauert, bis ich hier wieder alles sauber hatte. Wie haben sie die Eingeweide an die Decke bekommen?! Und wo kam all das Blut her?!', 38134),
(205779, 'deDE', '', 'Klar, aber macht sie nicht kaputt. Marasmius beschwert sich immer, dass sich die Harfen an ihm verhaken. Fragt mich nicht, wie DAS funktionieren soll.', 38134),
(205778, 'deDE', '', 'Oh, Schimmerschlürf? Ja, der ist okay. Steht total auf Illusionen, aber manchmal ist er so betrunken, dass er sich nicht mehr erinnern kann, welche Version von ihm die echte ist.$b$bDer echte Schimmernass stürzt allerdings gut was runter. Sein Lieblingsgetränk ist der Animai Tai. Er behauptet, dass es der süffigste und süßeste Drink im ganzen Ardenwald ist.$b$bWenn man berücksichtigt, wie viel er trinkt, sehe ich das als Kompliment. Wollt Ihr einen probieren?', 38134),
(205777, 'deDE', 'Jaja, ist \'ne Verkleidung. Keiner wird erkennen, dass Ihr es seid, Rendel.', '', 38134),
(205776, 'deDE', 'Oooh! Ui! Schaut Euch mein\' Hut an, Knüppelfratze. Er brennt... lichterloh.', '', 38134),
(205771, 'deDE', 'All unsere Bemühungen haben zu diesem Moment geführt.', '', 38134),
(205757, 'deDE', 'Nachdem sich Provosten hier etwas entspannt haben, fühlen sie sich wieder FEDERleicht!', '', 38134),
(205756, 'deDE', 'Warum bekommen Provosten immer ihr Werkzeug zurück? Weil sie KAUZion verlangen!', '', 38134),
(205750, 'deDE', 'Wisst Ihr, warum Provosten als Helfer so beliebt sind? Weil sie nicht groß rumhEULEN!', '', 38134),
(205748, 'deDE', '', 'Provosten widmen ihr gesamtes Leben der Aufgabe, sich nicht nur um die Kyrianer zu kümmern, sondern auch um die Bastion als Ganzes.\n\nJeder und jede einzelne von ihnen verdient es, sich etwas zurücklehnen zu dürfen. Wenn sie hierherkommen, hoffen wir, dass sie einige Momente der Ruhe und Entspannung finden.', 38134),
(205746, 'deDE', 'Sagt, wenn Ihr bereit seid, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(205743, 'deDE', 'Ihr braucht Trainingspartner?', 'Ihr braucht Trainingspartnerin?', 38134),
(205742, 'deDE', 'Schwindelig. Wohl zu hart trainiert...', 'Schwindelig. Wohl zu hart trainiert...', 38134),
(205741, 'deDE', 'Pause tut gut. Lässt Muskeln erholen!', 'Pause tut gut. Lässt Muskeln erholen!', 38134),
(205738, 'deDE', 'Braucht Ihr Hilfe in magischen Angelegenheiten?', '', 38134),
(205737, 'deDE', 'Ihr habt sehr hübsche Haut. Kann ich sie haben, wenn Ihr sie nicht mehr braucht?', '', 38134),
(205734, 'deDE', 'Gibt es Wunden zu heilen?', '', 38134),
(205733, 'deDE', 'Ich habe die Hoffnung niemals aufgegeben.', 'Ich habe die Hoffnung niemals aufgegeben.', 38134),
(205732, 'deDE', 'Findet diese Qual je ein Ende?', 'Findet diese Qual je ein Ende?', 38134),
(205731, 'deDE', 'Ich habe gebetet, dass die Göttin uns erhören würde...', 'Ich habe gebetet, dass die Göttin uns erhören würde...', 38134),
(205730, 'deDE', 'Ist es vorbei?', 'Ist es vorbei?', 38134),
(205726, 'deDE', '', 'Ich werde nie gebrochen werden.', 38134),
(205725, 'deDE', 'Ich vermisse das Laubschattental.', '', 38134),
(205722, 'deDE', 'Wo ist Delaryn?', '', 38134),
(205720, 'deDE', 'Alles hat gebrannt... selbst die Setzlinge...', '', 38134),
(205717, 'deDE', '$gEin Ehrenpilzomant:Eine Ehrenpilzomantin;, was? Marasmius scheint Euch ja richtig ins Herz zu schließen. Willkommen im Team!$B$BÄhm, nun, das Team bestand im Grunde nur aus mir. Aber ich bin froh, dass Ihr jetzt hier seid! Eure Pilzomantenpflichten bringen den Zugang zu den Pfaden von Marasmius mit sich. Außerdem erhaltet Ihr Zugriff auf spannende Boni und Ausrüstung, die allein Pilzomanten vorbehalten sind. Wenn Ihr mir und Marasmius helft, erhaltet Ihr Marken, die Ihr bei mir eintauschen könnt.', '', 38134),
(205716, 'deDE', 'Der Ring war früher eine Art Zentralstation, von der aus man in den gesamten Ardenwald reisen konnte.$b$BIm Laufe der Zeit und durch die kürzliche Dürre hat selbst Marasmius einige seiner Pfade verloren. Vielleicht kann der Schaden mit genug Anima rückgängig gemacht werden...$b$bIm Augenblick ist es die Pflicht der Pilzomanten, sich um Marasmius zu kümmern. Wir werden die Wurzeln des Ardenwalds stärker als je zuvor machen und so sein Überleben sichern.', '', 38134),
(205715, 'deDE', 'Willkommen im Ring. Manche sagen, er sei das Zentrum des Ardenwalds oder der Ort, an dem Marasmius entsprang. Nur er selbst weiß es wirklich.', '', 38134),
(205714, 'deDE', '', 'Solche Dunkelheit...\n\nIch habe alles getan, was ich konnte, um sie zu retten, oder?', 38134),
(205711, 'deDE', 'Wir haben Prinzessin Talanji von den Zandalari aus dem Verlies in Sturmwind gerettet. \n\nIhr Schiff ist auf dem Weg nach Zandalar, doch die Hunde der Allianz sind ihr auf den Fersen.\n\nWir brauchen die Marine der Zandalari, um diesen Krieg zu gewinnen. Wir dürfen nicht zulassen, dass die Allianz die Prinzessin erneut gefangen nimmt.\n\nWir werden zu ihnen fliegen, um sie zu unterstützen, und dann dafür sorgen, dass sich die Zandalari der Horde anschließen.\n\nKommt.', '', 38134),
(205709, 'deDE', '', 'Da war Feuer... und dann...\n\n... habe ich gesungen.\n\nGöttin, was geschieht?', 38134),
(205704, 'deDE', 'So lässt es sich leben. Daran könnte man sich glatt gewöhnen.', '', 38134),
(205699, 'deDE', 'Du NICHT NEHMEN KERZE! Neeeein.', '', 38134),
(205698, 'deDE', 'Eine Schande, dass uns die Gegner zum Töten ausgegangen sind.\n\nWenn sie zurückkehren, bin ich bereit.', '', 38134),
(205696, 'deDE', 'Ha! Dummer Gräber. Macht für mich ganzes Schloss leer!', '', 38134),
(205691, 'deDE', 'Zuerst machen wir Schlickfaust wütend! Dann lassen wir ihn geradewegs vor die Steine laufen!\n\nStellt Euch die Kopfschmerzen vor...\n\n<Dreckflapp kichert wie ein Wahnsinniger.>', '', 38134),
(205690, 'deDE', 'Schlickfaust hätte mich auf dem Weg hierher beinahe zerquetscht!\n\nSchwachkopf.\n\n<Dreckflapps Augen leuchten schelmisch.>\n\nHey, wollt Ihr etwas Spaß haben?', '', 38134),
(205687, 'deDE', 'Wer hat Hunger?', '', 38134),
(205674, 'deDE', 'Wir trauern um das, was wir verloren haben... egal ob es etwas ist, das wir einst besaßen, oder eine Zukunft, die wir nie erleben werden. Lasst von den Erwartungen ab, die dieser Besitz in Euch hervorrief, und findet Frieden.', '', 38134),
(205673, 'deDE', 'Wenn wir uns von unseren Erwartungen befreien und die Furcht und den Schmerz des Verlustes loslassen, akzeptieren wir die Dinge, wie sie sind.', '', 38134),
(205672, 'deDE', 'Es ist schmerzhaft, etwas nicht mehr existieren zu sehen. Wir erinnern uns an die Freude, die es uns gebracht hat, an das Erstaunen und die Verbundenheit.', '', 38134),
(205671, 'deDE', 'Wir fürchten uns davor, etwas nicht mehr zu besitzen, das einst unser war, oder nicht das zu erleben, was wir uns einst ausgemalt hatten.', '', 38134),
(205639, 'deDE', '', 'Hey, willkommen im Wurzelkeller!$b$BWas darf\'s denn sein?', 38134),
(205627, 'deDE', '', 'Wir werden bereit sein, den Gluthof zu verteidigen. Wir sehen uns dort, $n.', 38134),
(205591, 'deDE', 'Am Rande der Existenz ist es leicht, von Verlust überwältigt zu werden. Hm. Vielleicht sollte ich über die Bedeutung des Verlustes meditieren. Könntet Ihr meine Fokusse finden? Sie sollten hier irgendwo sein...', '', 38134),
(205590, 'deDE', 'Wenn jemand nach Ruhe und Frieden sucht, ist dies der richtige Ort dafür! Vielleicht könnte diese Seele dort ein wenig Hilfe brauchen.', '', 38134),
(205587, 'deDE', '', 'Das war mit die schlechteste Kampfkunst, die ich je gesehen habe. Ihr seid durchgefallen. Geht üben.', 38134),
(205586, 'deDE', '', 'Gut gemacht. $gRekrut:Rekrutin;, Ihr habt bestanden.', 38134),
(205579, 'deDE', 'Ihr seht aus wie $uein... Handwerker:eine... Handwerkerin;?\n\nWenn Ihr die Fertigkeiten besitzt, kann ich Euch mein Wissen über die Herstellung der Gefäße vermitteln, in denen die Runenkräfte aufbewahrt werden.', '', 38134),
(205568, 'deDE', 'Nach welchem der Berufe sucht Ihr?', 'Nach welchem der Berufe sucht Ihr?', 38134),
(205561, 'deDE', '', 'Ich traue ihm nicht, aber es könnte unsere einzige Möglichkeit sein, ihre Machenschaften aufzudecken.', 38134),
(205560, 'deDE', '', '$uSchlundgänger:Schlundgängerin;! Dieser Lich hat einen Vorschlag, um uns Zugang zum Haus der Rituale zu verschaffen.', 38134),
(205559, 'deDE', 'Und doch habt Ihr keine andere Wahl, als zu akzeptieren, nicht wahr? Es sei denn, Ihr zieht einen selbstmörderischen Frontalangriff vor?', '', 38134),
(205558, 'deDE', '', '...Ich heiße Euren Plan nicht gut, Lich.', 38134),
(205552, 'deDE', 'Seleniamotten! Sie müssen gerade geschlüpft sein. Schaut, ob Ihr ein paar fangen könnt und bringt sie mit.', '', 38134),
(205550, 'deDE', 'Ich muss Euch warnen: Ich bin in der Kunst des Duellierens gut ausgebildet.', 'Ich muss Euch warnen: Ich bin in der Kunst des Duellierens gut ausgebildet.', 38134),
(205534, 'deDE', 'Eine neue Aufgabe? Welche Aufgabe kann es ohne meine Welt geben?\n\nDoch kann ich nicht einfach müßig bleiben. Das ist nicht meine Art.\n\nAber ich muss diese Existenz verstehen. Diese... Schattenlande. Ich kann viele Reiche spüren, aber hier gibt es vier, die eine besondere Rolle spielen.\n\nBringt mir etwas aus diesen vier Reichen. Etwas Organisches. Mein Volk kann viel nur durch Berührung erkennen.', '', 38134),
(205532, 'deDE', 'Ich würde es wissen, wenn sie noch da wäre. Ich kann ihr Fehlen spüren. \n\nWie überwältigend traurig. Was ist ein Gott ohne eine Welt? \n\nWo soll ich hingehen? Was soll ich tun?', '', 38134),
(205530, 'deDE', 'Ardenwald? Dann sind das die Schattenlande? Schaut nicht so überrascht, dass ich die Schattenlande kenne. Ich war eine Gottheit, wisst Ihr.\n\nIch kenne die Antworten auf viele Fragen. Und doch weiß ich nicht, wie ich hierher kam und warum meine Welt nicht mehr existiert.\n\nIch vermute, ich habe zur gleichen Zeit aufgehört zu existieren. Jetzt bin ich wiederhergestellt, aber meine Welt ist es nicht.', '', 38134),
(205528, 'deDE', 'Also, was habt Ihr zu sagen? Wie bin ich hierher gekommen?', '', 38134),
(205522, 'deDE', 'Ah, ah, nur ein Hauch hier. Sachte.', 'Ah, ah, nur ein Hauch hier. Sachte.', 38134),
(205521, 'deDE', 'Ohhhh. Was für ein lebhafter Farbton. Seht Ihr das?', 'Ohhhh. Was für ein lebhafter Farbton. Seht Ihr das?', 38134),
(205520, 'deDE', 'Seht Euch an, wie geschmeidig es sich auf der Oberfläche verteilt. Wie ein Blatt im Wind.', 'Seht Euch an, wie geschmeidig es sich auf der Oberfläche verteilt. Wie ein Blatt im Wind.', 38134),
(205519, 'deDE', 'Ich bin fertig. Möge die Freude, die Ihr an diesem Werk findet, der meinen ebenbürtig sein, als ich es erschuf.', 'Ich bin fertig. Möge die Freude, die Ihr an diesem Werk findet, der meinen ebenbürtig sein, als ich es erschuf.', 38134),
(205516, 'deDE', 'Gemeinsam... als Familie.', 'Gemeinsam... als Familie.', 38134),
(205505, 'deDE', 'Polemarch Adrestes nickt.', '', 38134),
(205498, 'deDE', '', 'Unser Werk hat erst begonnen.', 38134),
(205497, 'deDE', '', 'Willkommen im letzten Bollwerk der Ehre in Maldraxxus. Eure Ankunft hier war beinahe so chaotisch wie meine eigene.', 38134),
(205496, 'deDE', 'Das muss es sein! Bringt mir die Klinge!', '', 38134),
(205495, 'deDE', 'Arrgh. Spriggans. Lausige, lärmende, herumhüpfende kleine Dummköpfe. Aber diese Blumen pflücken sich nicht von selbst. Holt sie und lasst diese Racker verrotten.', '', 38134),
(205481, 'deDE', '', 'Wenn Ihr bereit seid, $uSchlundgänger:Schlundgängerin;, beginnen wir mit dem Angriff!', 38134),
(205472, 'deDE', 'Es ist lange her, dass Qadarin und ich zusammen mit anderen Champions an unserer Seite zur Verteidigung unserer Heimat gekämpft haben. Ich habe den Krieg nicht vermisst, aber ich habe es genossen, Seite an Seite mit Freunden und Verbündeten zu stehen.\n\n<Thiernax lacht grunzend.>\n\nUnd zu sehen, wie solche üblen Verderber so vollkommen vernichtet werden.', '', 38134),
(205471, 'deDE', '', 'Ich will mir gar nicht vorstellen, was passiert wäre, wenn die Drust hier die Kontrolle an sich gerissen hätten.\n\nMir ist klar, dass hier im Ardenwald noch viel zu tun ist, aber zumindest diese Bedrohung wurde vereitelt.\n\nIch habe meine Bedenken und werde Euch auch weiterhin im Auge behalten, aber zumindest können wir uns darauf einigen, diesem Ort zu helfen.', 38134),
(205470, 'deDE', 'Welch wunderbare Pigmente. Derartiges habe ich noch nie gesehen. Ich muss mich ans Werk machen.', 'Welch wunderbare Pigmente. Derartiges habe ich noch nie gesehen. Ich muss mich ans Werk machen.', 38134),
(205468, 'deDE', '', 'Ich will mir gar nicht vorstellen, was passiert wäre, wenn die Drust hier die Kontrolle an sich gerissen hätten.\n\nMir ist klar, dass hier im Ardenwald noch viel zu tun ist, aber zumindest diese Bedrohung wurde vereitelt.\n\nTyrande ist immer noch allein in Torghast. Die Suche nach anderen Nachtkriegern geht weiter, und Ysera und Thiernax scheinen Vertrauen in ihren Plan zu haben, aber... ich sorge mich um sie.', 38134),
(205467, 'deDE', '', 'Die Drust mögen besiegt sein, aber der Ardenwald braucht immer noch Beschützer. Ich werde für die Wilde Jagd kämpfen, wie Ara\'lon es zuvor getan hat, und den Schutz aller Haine zu meiner Aufgabe machen.\n\nDas gibt mir wirklich Hoffnung, dass der Ardenwald eines Tages vollständig wiederhergestellt sein wird.', 38134),
(205466, 'deDE', 'Nun, das war aufregend! Ich bin sehr froh, dass die Drust fort sind. Sie hatten einen schrecklichen Einfluss auf unsere Haine und die Wildsamen. Ich hoffe, dass die Königin hier wieder für Normalität sorgen kann. Oder zumindest etwas mehr Normalität. Vielen Dank, $n. Ihr habt mehr für uns getan, als Ihr wisst.', '', 38134),
(205465, 'deDE', '', 'Als Kind einer vom Krieg zerrissenen Welt kamt Ihr zu uns... als $gFremder:Fremde;. Nun haben wir im Kampf Seite an Seite gestanden. Ich verneige mich vor Euren Fertigkeiten als $gJäger:Jägerin;. Mein Speer steht Euch stets zur Verfügung.', 38134),
(205463, 'deDE', 'Selbst die mächtigsten Jäger fallen.\n\nWir trauern um Ara\'lon und die anderen, die wir verloren haben, aber wir feiern ihre Leben. Und unseren Sieg!\n\nIhr habt den Kampfeseifer des Ardenwalds unter Beweis gestellt. Lieder Eurer Taten werden im ganzen Wald besungen werden, $n.\n\nGut gekämpft. Wirklich gut gekämpft!', '', 38134),
(205459, 'deDE', '', 'Von dem Moment Eurer Ankunft an wusste ich, dass Ihr etwas Besonderes seid, $n. Und ich hatte recht! Ihr... seid interessant.\n\n<Mondbeere wird ernst.>\n\nUnd wir werden hier noch viel Interessantes brauchen können. Die Drust haben wir fortgejagt, aber sie haben nur die bereits bestehenden Probleme verschlimmert. Davon gibt es hier noch mehr als genug. Und ich weiß, dass wir sie alle lösen werden!', 38134),
(205458, 'deDE', '', 'Vieles wurde geopfert, damit ich fortbestehen kann. Es freut mich, dass ich zum Schutze dieses Ortes beitragen und so beginnen konnte, die mir gezeigte Gnade zurückzuerweisen.\n\nDer Macht der Drust wurde hier Einhalt geboten, aber wir müssen auf der Hut bleiben. Die Schattenlande... vieles stimmt mit ihnen nicht. Wir haben einen Sieg errungen, aber es gibt noch viel zu tun.\n\nIch sehe voraus, dass Eure Hilfe in den kommenden Herausforderungen vonnöten sein wird. Haltet Euch bereit.', 38134),
(205457, 'deDE', 'Gut gemacht. Weggetreten!', 'Gut gemacht. Weggetreten!', 38134),
(205442, 'deDE', 'Diese Pilze sind durch den Einfluss des Schlunds verrückt geworden! Wir müssen etwas unternehmen, schnell!', '', 38134),
(205441, 'deDE', 'Oh, tut mir leid. Habe ich vergessen zu erwähnen, dass dazu körperliche Arbeit nötig ist?', '', 38134),
(205440, 'deDE', 'Ihr müsst diesen Leichnam zum Ritualkreis zurückbringen.', '', 38134),
(205437, 'deDE', 'Diese Gestalt ist äußerst... interessant.', 'Diese Gestalt ist äußerst... interessant.', 38134),
(205436, 'deDE', 'Wie werdet Ihr dem Ardenwald dienen?', 'Wie werdet Ihr dem Ardenwald dienen?', 38134),
(205435, 'deDE', 'Welche Gestalt werdet Ihr annehmen?', 'Welche Gestalt werdet Ihr annehmen?', 38134),
(205434, 'deDE', 'Der Segen der Nacht sei mit Euch.', 'Der Segen der Nacht sei mit Euch.', 38134),
(205427, 'deDE', '', 'Aber wenn Ihr bei Eurer Aufgabe scheitert, wird EUER Name auf die Liste gesetzt!', 38134),
(205426, 'deDE', '', 'Gemeinsam streichen wir ein paar Namen von meiner Liste...', 38134),
(205425, 'deDE', '', 'Projiziert mit diesem Kristall mein Abbild für kurze Zeit in die Ebene der Lebenden.', 38134),
(205424, 'deDE', '', 'Einige der anderen Kämpfer werden mir folgen. Nicht, dass Ihr sie brauchen würdet, solange ich in der Nähe bin!', 38134),
(205423, 'deDE', 'Draven nennt MICH arrogant! Hätte dieser steingeborene Trottel meiner Warnung vor Kel\'Thuzad Gehör geschenkt, hätten wir den Lich vernichtet! Jetzt steht unser Feind an der Seite des Kerkermeisters.\n\n<Der Zorn in den Augen des Prinzen schwindet.>\n\nIch sehe ein, dass ich Fehler gemacht habe. Vielleicht kann ich irgendwann dafür büßen und Euer Vertrauen zurückgewinnen.\n\nUnd doch würde ich meine Erlösung gegen Rache für unser Volk eintauschen. Für die, die gestorben sind, weil Kel\'Thuzad...\n\n<Kael\'thas seufzt.>\n\nWeil ich sie nicht beschützen konnte.\n\nLasst mich allein, $p. Es gibt viel, über das ich nachdenken muss.', '', 38134),
(205422, 'deDE', 'Draven nennt MICH arrogant! Hätte dieser steingeborene Trottel meiner Warnung vor Kel\'Thuzad Gehör geschenkt, hätten wir den Lich vernichtet! Jetzt steht unser Feind an der Seite des Kerkermeisters.\n\n<Der Zorn in den Augen des Blutelfs schwindet.>\n\nIch sehe ein, dass ich Fehler gemacht habe. Vielleicht kann ich irgendwann dafür büßen.\n\nUnd doch würde ich meine Erlösung gegen Rache für mein Volk eintauschen. Für die, die gestorben sind, weil Kel\'Thuzad...\n\n<Kael\'thas seufzt.>\n\nWeil ich sie nicht beschützen konnte.', '', 38134),
(205421, 'deDE', 'Während ich, Euer Prinz, hier in Revendreth gefangen bin und wegen einiger... fragwürdiger Entscheidungen leiden muss, ist Kel\'Thuzad frei. Dieses Monster hat ganz Maldraxxus in seiner Hand!\n\n<Kael\'thas blickt finster drein, dann senkt er den Blick.>\n\nNeun von jeden zehn Elfen, $p. Alle von Arthas Menethil getötet, um diesen Lich großzuziehen. Unser nobles Königreich, nahezu ausgelöscht.\n\nÜberleben fühlte sich oft mehr als Last denn als Segen an. Ich habe alles getan, um die Stärke unseres Volks zu bewahren.\n\nAber vielleicht war der Weg, den ich wählte...\n\n<Kael\'thas verstummt.>', '', 38134),
(205420, 'deDE', 'Während ich hier in Revendreth gefangen bin und wegen einiger... fragwürdiger Entscheidungen leiden muss, ist Kel\'Thuzad frei. Dieses Monster hat ganz Maldraxxus in seiner Hand!\n\n<Kael\'thas blickt finster drein, dann senkt er den Blick.>\n\nNeun von jeden zehn Elfen, $p. Alle von Arthas Menethil getötet, um diesen Lich großzuziehen. Ein nobles Königreich, nahezu ausgelöscht.\n\nIhr blutet noch, nicht wahr? Schneidet Euch ins Fleisch und zählt die Blutstropfen. Lasst neun herabfallen und die Erde durchtränken für jeden, den Ihr auffangt.\n\nWas macht das mit Eurem Herzen, $p? Ich weiß genau, was es mit meinem gemacht hat.', '', 38134),
(205419, 'deDE', 'Diese Behandlung ist unerhört! Ich bin Euer Prinz, Euer rechtmäßiger Herrscher, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!', '', 38134),
(205418, 'deDE', 'Es macht Euch wohl Freude, mich so zu sehen! Ihr habt mich vielleicht im Kampf bezwungen, aber Ihr werdet nie auf einer Stufe mit mir stehen.\n\nIch bin ein Prinz edler Herkunft, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!', '', 38134),
(205417, 'deDE', 'Diese Behandlung ist unerhört! Ich bin ein Prinz edler Herkunft, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!', '', 38134),
(205416, 'deDE', 'Ihr! Kommt her, Untertan! Ich befehle Euch, mich sofort loszumachen!\n\n<Kael\'thas wehrt sich gegen seine Fesseln.>\n\nDiese Behandlung ist unerhört! Ich bin Euer Prinz, Euer rechtmäßiger Herrscher, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!\n\nAnklägerin! Lasst mich frei! Ich muss ein Königreich rächen. Hört Ihr mich?! ANKLÄGERIN!\n\n<Kael\'thas sträubt sich trotzig weiter.>', '', 38134),
(205415, 'deDE', 'Ihr! Ihr da! Macht mich sofort los!\n\n<Kael\'thas wehrt sich gegen seine Fesseln.>\n\nEs macht Euch wohl Freude, mich so zu sehen! Ihr habt mich vielleicht im Kampf bezwungen, aber Ihr werdet nie auf einer Stufe mit mir stehen!\n\nIch bin ein Prinz edler Herkunft, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!\n\nAnklägerin! Lasst mich frei! Ich muss ein Königreich rächen. Hört Ihr mich?! ANKLÄGERIN!\n\n<Kael\'thas sträubt sich trotzig weiter.>', '', 38134),
(205414, 'deDE', 'Ihr! Ihr da! Macht mich sofort los!\n\n<Kael\'thas wehrt sich gegen seine Fesseln.>\n\nDiese Behandlung ist unerhört! Ich bin ein Prinz edler Herkunft, aber diese unnahbare Richterin hat es trotzdem gewagt, mich in dieses triste und engstirnige Reich voll grauem Stein zu schicken.\n\nKeiner dieser Venthyr ist in der Lage, meine Entscheidungen im Leben zu beurteilen! Und auch Ihr nicht!\n\nAnklägerin! Lasst mich frei! Ich muss ein Königreich rächen. Hört Ihr mich?! ANKLÄGERIN!\n\n<Kael\'thas sträubt sich trotzig weiter.>', '', 38134),
(205412, 'deDE', 'Mit mir wollt Ihr jetzt wirklich nicht sprechen.\n\nIch bin nicht blind. Ich sehe, dass Ihr bereits daran arbeitet, anderen Eure Treue zu beweisen.', '', 38134),
(205411, 'deDE', 'Ich habe gesehen, wie Ihr versucht, andere zu beeindrucken.\n\nWir haben derzeit nichts zu besprechen.', '', 38134),
(205410, 'deDE', '', 'Ihr schenkt Eure Stärke derzeit anderen.\n\nGlaubt nicht, dass mir so etwas entgeht. Unsere Augen sind überall.', 38134),
(205409, 'deDE', '', 'Ihr arbeitet doch bereits daran, die Gunst eines anderen Pakts zu gewinnen.\n\nIhr vergeudet bei mir nur Eure Zeit.', 38134),
(205408, 'deDE', 'Habe ich Euch etwa bei Lady Mondbeere gesehen?\n\nIhr habt Euch hoffentlich nicht zu viel von diesem skurrilen Unfug über die Wiedergeburt angehört.', '', 38134),
(205407, 'deDE', 'Warum könnt Ihr es nicht lassen, Euch mit den Kyrianern zu umgeben?\n\nIm Dienst der Venthyr seid Ihr viel besser gestellt.', '', 38134),
(205406, 'deDE', 'Warum sprecht Ihr so oft mit Draka?\n\nIn dieser Zeit solltet Ihr eher den Venthyr helfen, wie Ihr versprochen habt.', '', 38134),
(205405, 'deDE', '', 'Seid vorsichtig, wenn Ihr mit General Draven sprecht.\n\nDen Venthyr kann man nicht trauen. Das wisst Ihr sicher bereits, aber Ihr scheint dennoch eine Menge Zeit mit ihm zu verbringen.', 38134),
(205404, 'deDE', '', 'Ich sehe, dass Ihr Euch weiterhin viel mit Draka beschäftigt.\n\nIch weiß, dass sie aus Eurer Welt kommt, aber Ihr habt jetzt neue Freunde. Uns.', 38134),
(205401, 'deDE', '', 'Ihr und die Kyrianer scheint in letzter Zeit gute Freunde zu sein.\n\nSeid Ihr es satt, unserer Königin zu dienen? Wir haben noch viel zusammen zu erledigen.', 38134),
(205400, 'deDE', '', 'Was habt Ihr mit Draven besprochen?\n\nIch wüsste nicht, dass wir derzeit mit den Venthyr zusammenarbeiten. Ich misstraue ihnen.', 38134),
(205398, 'deDE', '', 'Ich habe Euch nicht für $ueinen Naturfreund:eine Naturfreundin; gehalten, aber Ihr scheint einige Zeit mit den Nachtfae zu verbringen.', 38134),
(205397, 'deDE', '', 'Ihr scheint Euch in letzter Zeit gut mit den Kyrianern zu verstehen.\n\nIch darf annehmen, dass Ihr den Nekrolords immer noch treu seid, $n? Wir haben noch viel zu tun.\n\nEs wäre schade, wenn ein Verrat unser Verhältnis zunichtemachen würde.', 38134),
(205394, 'deDE', 'Wie ich gehört habe, stammt Ihr und Draka aus derselben Welt.\n\nHabe ich Euch deshalb kürzlich mit ihr sprechen sehen?', '', 38134),
(205390, 'deDE', 'Ich habe Euch vorhin mit General Draven sprechen sehen.\n\nIch darf annehmen, dass Ihr uns alle bedeutenden Informationen weitergebt, die Ihr von ihm erhalten habt?', '', 38134),
(205389, 'deDE', 'Habe ich da etwa gesehen, wie Ihr Angelegenheiten mit Lady Mondbeere besprecht?\n\nMeines Wissens nach gibt es bei uns derzeit keine Aufgaben, die mit den Nachtfae zu tun haben.', '', 38134),
(205388, 'deDE', '', 'Eins muss ich Euch lassen, $n. Ihr seid mutig.\n\nGedenkt Ihr, mich zu reizen? Mich mit Eurer mangelnden Treue zu provozieren?\n\nIhr solltet gehen. So bald möchte ich nichts mit jemandem wie Euch besprechen.', 38134),
(205387, 'deDE', '', 'Ihr habt uns erneut Eure Stärke gezeigt.\n\nStärke ist nur eine der Eigenschaften, die ein Nekrolord aufweisen muss. Ihr müsst uns außerdem dienen, und darin habt ihr zuvor versagt.\n\nGeht, wir werden die Frage Eurer Wiederaufnahme in den Pakt besprechen.\n\nKehrt später zu mir zurück, vielleicht haben wir dann eine Entscheidung getroffen.', 38134),
(205380, 'deDE', 'Ihr könnt von Glück sagen, dass ich Euch nicht in zwei Stücke reiße!\n\nWären wir nicht auf neutralem Boden, wäre das sehr verlockend.\n\nIhr solltet mich zufriedenlassen, bis meine Wut verraucht ist.', '', 38134),
(205378, 'deDE', 'Ihr habt die ersten Schritte getan, um unsere Gunst zurückzugewinnen.\n\nWir müssen Euren Fall weiter untersuchen. Sprecht zu einem späteren Zeitpunkt wieder mit mir.\n\nVielleicht haben wir bis dahin geurteilt, dass Ihr würdig seid, zu den Venthyr zurückzukehren.', '', 38134),
(205370, 'deDE', 'Wie möchtet Ihr Euren Mantel und Eure Gugel ändern?', '', 38134),
(205368, 'deDE', 'Welches Accessoire möchtet Ihr ändern?', '', 38134),
(205366, 'deDE', 'Zu welcher Farbe möchtet Ihr Eure Verkleidung ändern?', '', 38134),
(205365, 'deDE', 'Welchen Teil Eurer Verkleidung möchtet Ihr ändern?', '', 38134),
(205364, 'deDE', '', 'Ihr wagt es, wiederzukommen und mich anzusprechen?\n\nIhr habt Eure mangelnde Treue bereits bewiesen, $n.\n\nVielleicht verzeihe ich Euch später. Vielleicht.', 38134),
(205363, 'deDE', 'Dieses Mannequin wird von Moret der Modischen verwendet, um das neue Ornat für die Akolythen des Hauses der Rituale zu gestalten.\n\nIhr dürft es verwenden, um das Aussehen Eurer Verkleidung als $unekrotischer Akolyth:nekrotische Akolythin; zu verändern.', '', 38134),
(205362, 'deDE', 'Im Ernst, $n?\n\nIhr seid mutig, hier Euer Gesicht zu zeigen. Jemandem wie Euch habe ich gerade gar nichts zu sagen.', '', 38134),
(205359, 'deDE', '', 'Ihr habt die ersten Schritte zurück zu unserer Königin getan.\n\nWir besprechen, ob Eure Rückkehr Teil des Kreislaufs ist oder nicht.\n\nBitte kehrt später zu mir zurück, vielleicht sind wir dann zu einem Schluss gekommen. Ich würde um Entschuldigung für das Warten bitten, aber Euer vorheriger Verrat hat uns vorsichtig gemacht.', 38134),
(205356, 'deDE', 'Seid gegrüßt, $n.\n\nWir besprechen gerade, ob wir schon erlauben möchten, dass Ihr zu uns zurückkehrt.\n\nBitte kommt ein andermal zurück. Vielleicht haben die Winde sich dann zu Euren Gunsten gedreht.', '', 38134),
(205345, 'deDE', 'Tharon\'ja sieht alles! Sterbliche werden der ewigen Dynastie niemals ein Ende bereiten!', '', 38134),
(205343, 'deDE', 'Das ist nicht richtig! Diese Kerle dürfen nicht hier sein! Ihnen muss eine Lektion erteilt werden.', '', 38134),
(205341, 'deDE', 'Die Geheimnisse der Tiefe werden verborgen bleiben!', '', 38134),
(205335, 'deDE', 'Flickwerk spielen möchte!', '', 38134),
(205334, 'deDE', 'Es war töricht, herzukommen.', '', 38134),
(205330, 'deDE', 'Meh\' Tod! Meh\' Blut! Meh\' Mampf!', '', 38134),
(205328, 'deDE', 'Was ihr spürt, ist Euer Ende, das naht.', '', 38134),
(205323, 'deDE', 'Ihre Zeit war gekommen. Leider war die einzige Ewigkeit, die sie erwartete, der Schlund. Solche Entscheidungen obliegen nicht uns, sondern der Richterin.', 'Ihre Zeit war gekommen. Leider war die einzige Ewigkeit, die sie erwartete, der Schlund. Solche Entscheidungen obliegen nicht uns, sondern der Richterin.', 38134),
(205322, 'deDE', 'Es war ein verstörender Tod, aber es ist nicht unsere Pflicht, die Moralität ihres Schicksals zu beurteilen.', 'Es war ein verstörender Tod, aber es ist nicht unsere Pflicht, die Moralität ihres Schicksals zu beurteilen.', 38134),
(205321, 'deDE', '', 'Der Baum brennt! Ich... kann nicht... atmen...', 38134),
(205320, 'deDE', 'Egal, wie tragisch es auch sein mag... ihre Zeit war gekommen, nach Oribos zu gehen und ihre Ewigkeit anzutreten.', 'Egal, wie tragisch es auch sein mag... ihre Zeit war gekommen, nach Oribos zu gehen und ihre Ewigkeit anzutreten.', 38134),
(205319, 'deDE', 'Sich selbst für die eigene Familie zu opfern ist nobel, aber kein Grund für eine Rückkehr ins Reich der Lebenden.', 'Sich selbst für die eigene Familie zu opfern ist nobel, aber kein Grund für eine Rückkehr ins Reich der Lebenden.', 38134),
(205318, 'deDE', 'Ich duellierte mich mit meinem hitzköpfigen Bruder und ich... ich konnte es nicht übers Herz bringen, den lausigen Burschen zu verlieren! Ich habe mein Schwert langsam gezogen und den Preis dafür bezahlt.', 'Ich duellierte mich mit meinem hitzköpfigen Bruder und ich... ich konnte es nicht übers Herz bringen, den lausigen Burschen zu verlieren! Ich habe mein Schwert langsam gezogen und den Preis dafür bezahlt.', 38134),
(205317, 'deDE', 'Als Naturgeist wurde sie in den Ardenwald gesandt, um den Bedürftigen weiter zu helfen. Das war ihr Schicksal.', 'Als Naturgeist wurde sie in den Ardenwald gesandt, um den Bedürftigen weiter zu helfen. Das war ihr Schicksal.', 38134),
(205316, 'deDE', 'Auch wenn manche Seelen, die eng mit der Natur verbunden sind, nach einer Rast im Ardenwald zurückkehren, verbringen andere ihre Ewigkeit in seinem Geäst.', 'Auch wenn manche Seelen, die eng mit der Natur verbunden sind, nach einer Rast im Ardenwald zurückkehren, verbringen andere ihre Ewigkeit in seinem Geäst.', 38134),
(205315, 'deDE', '', 'Der Hain ist in Gefahr! Die Kleinen werden es ohne mich nicht in Sicherheit schaffen!', 38134),
(205314, 'deDE', 'Für Dämonen ist kein Platz in den Schattenlanden, abgesehen von dem ein oder anderen Gladiator in Maldraxxus.', 'Für Dämonen ist kein Platz in den Schattenlanden, abgesehen von dem ein oder anderen Gladiator in Maldraxxus.', 38134),
(205313, 'deDE', 'Oh, ist das ein Versehen? Diese Einladung ist an Polemarch Adrestes gerichtet.', '', 38134),
(205311, 'deDE', 'Korrekt. Dämonen müssen in den Wirbelnden Nether zurückkehren, aus dem sie kamen.', 'Korrekt. Dämonen müssen in den Wirbelnden Nether zurückkehren, aus dem sie kamen.', 38134),
(205309, 'deDE', '', 'Mmm! Den Stich einer solchen Klinge habe ich schon lang nicht mehr verspürt.', 38134),
(205303, 'deDE', 'Obwohl die Val\'kyr nicht unsere wahren Verwandten sind, müssen wir ihren Ruf zurück ins Reich der Sterblichen respektieren.', 'Obwohl die Val\'kyr nicht unsere wahren Verwandten sind, müssen wir ihren Ruf zurück ins Reich der Sterblichen respektieren.', 38134),
(205302, 'deDE', 'Gut gemacht. Diese Seele ist rechtzeitig zu uns gekommen. Nicht einmal die Untoten können dem Tod auf ewig entwischen.', 'Gut gemacht. Diese Seele ist rechtzeitig zu uns gekommen. Nicht einmal die Untoten können dem Tod auf ewig entwischen.', 38134),
(205301, 'deDE', 'Ich höre den gefürchteten Ruf der Val\'kyr. Bitte! Lasst mich in Frieden ruhen! Ich flehe Euch an!', '', 38134),
(205298, 'deDE', 'Vielen Dank. Ich werde über diese Elemente der Güte nachdenken, wie Ihr sie mir heute entgegengebracht habt. Gehabt Euch wohl.', '', 38134),
(205296, 'deDE', 'Alles geschieht zu seiner eigenen Zeit, ob wir es im jeweiligen Moment wünschen oder nicht. Diesen Umstand zu verstehen, bedeutet, Geduld zu haben.', '', 38134),
(205295, 'deDE', 'Wenn wir von Erwartungen und Missgunst denjenigen gegenüber ablassen, die uns Unrecht getan haben, dann ist das ein Schritt auf dem Weg zur Vergebung.', '', 38134),
(205294, 'deDE', 'Habt Ihr durchflutete Rubine? Die Sündenmähre da wird Euch bis nach Dunkelhafen bringen.\n\nAber nicht loslassen, wenn Ihr unterwegs nicht abgeworfen werden wollt.', '', 38134),
(205293, 'deDE', 'Unseren eigenen Blickpunkt aufzugeben und so das Leiden anderer wahrhaftig zu verstehen... das macht wahres Mitgefühl aus.', '', 38134),
(205292, 'deDE', 'Ihr seid gütig, mich hier am zerfallenden Rand der Realität zu besuchen. Hm. Vielleicht sollten wir über die Natur der Güte meditieren. Könntet Ihr meine Fokusse finden? Die sollten hier irgendwo sein...', '', 38134),
(205291, 'deDE', 'Ihr solltet wissen, dass der Polemarch einer Abweichung von seiner Routine niemals mit Freuden entgegensehen wird. Allerdings würde es ihm guttun. Lasst mich das regeln.', '', 38134),
(205286, 'deDE', 'Das hier nenne ich mal \"abgelegen\". Und es sieht so aus, als säße eine arme Seele hier draußen fest! Seht nach, ob sie Hilfe braucht.', '', 38134),
(205285, 'deDE', 'Oh, ich nehme alles, wirklich.\n\nIch nehme danach meine eigenen Modifikationen vor.', '', 38134),
(205284, 'deDE', 'Für ein paar durchflutete Rubine trägt Euch diese Sündenmähre die ganzen nördlichen Festungsmauern entlang.\n\nTolle Aussicht, aber voller Fieslinge und Grobiane. Lasst bloß nicht los, außer Ihr seid sicher, dass Ihr da wirklich landen wollt.', '', 38134),
(205282, 'deDE', 'Korrekt. Auch wenn das Schicksal ihres Dorfes hätte geändert werden können, wenn sie zurückgekehrt wäre, war ihre Zeit gekommen.', 'Korrekt. Auch wenn das Schicksal ihres Dorfes hätte geändert werden können, wenn sie zurückgekehrt wäre, war ihre Zeit gekommen.', 38134),
(205281, 'deDE', 'Auch wenn die folgende Flut tatsächlich viele Opfer fordern würde, ist es nicht unsere Pflicht, uns in die Angelegenheiten der Sterblichen einzumischen.', 'Auch wenn die folgende Flut tatsächlich viele Opfer fordern würde, ist es nicht unsere Pflicht, uns in die Angelegenheiten der Sterblichen einzumischen.', 38134),
(205280, 'deDE', 'Mein Explodinator 5000 hat gerade den Damm über meinem Labor... äh... explodiniert! All meine Experimente... ich meine natürlich: meine armen kleinen Lieblinge... sind in großer Gefahr!', 'Mein Explodinator 5000 hat gerade den Damm über meinem Labor... äh... explodiniert! All meine Experimente... ich meine natürlich: meine armen kleinen Lieblinge... sind in großer Gefahr!', 38134),
(205279, 'deDE', 'Ganz richtig. Egal, worum eine Seele fleht... wir dürfen ihr Schicksal nicht ändern.', 'Ganz richtig. Egal, worum eine Seele fleht... wir dürfen ihr Schicksal nicht ändern.', 38134),
(205278, 'deDE', 'So lange das Phylakterium des Lichs intakt ist, müssen wir ihn zurückschicken.', 'So lange das Phylakterium des Lichs intakt ist, müssen wir ihn zurückschicken.', 38134),
(205275, 'deDE', 'Ich war unzählige Male an diesem Scheideweg. Ich möchte nicht länger gebunden sein! Gewährt mir die Ruhe des Todes!', 'Ich war unzählige Male an diesem Scheideweg. Ich möchte nicht länger gebunden sein! Gewährt mir die Ruhe des Todes!', 38134),
(205272, 'deDE', 'Wenn Ihr ein paar durchflutete Rubine loswerden wollt, bringt Euch die Sündenmähre da bis zum Loch in der Wand im westlichen Schloss.\n\nAber nicht loslassen, wenn Ihr unterwegs nicht abgeworfen werden wollt.', '', 38134),
(205268, 'deDE', '$gSchlundgänger:Schlundgängerin;, sammelt doch ein wenig Anima für unsere Gäste, damit sie ein Andenken an das gute Omen mit nach Hause nehmen können.', '', 38134),
(205267, 'deDE', 'Ein gutes Omen für unseren Hof: Anima verdichtet sich über unseren Köpfen!', '', 38134),
(205251, 'deDE', 'Da dieses Wesen aus der Magie der Feuerlande geformt wurde, kann in Oribos kein Urteil darüber gefällt werden.', 'Da dieses Wesen aus der Magie der Feuerlande geformt wurde, kann in Oribos kein Urteil darüber gefällt werden.', 38134),
(205250, 'deDE', 'Gut! Kreaturen einer anderen Ebene müssen nach ihrem Tod dorthin zurückkehren. Die Schattenlande sind nicht ihr Schicksal.', 'Gut! Kreaturen einer anderen Ebene müssen nach ihrem Tod dorthin zurückkehren. Die Schattenlande sind nicht ihr Schicksal.', 38134),
(205249, 'deDE', 'Eindringlinge bedrohen die Feuerlande! Ich muss umgehend zu meinem Volk zurückkehren!', '', 38134),
(205247, 'deDE', 'Obwohl die Magie der Schattenlande sie umgibt, entspringt diese Kraft der Welt der Lebenden. Sie musste zurückkehren.', 'Obwohl die Magie der Schattenlande sie umgibt, entspringt diese Kraft der Welt der Lebenden. Sie musste zurückkehren.', 38134),
(205246, 'deDE', 'So muss es sein. Obwohl sie bereits einmal gestorben war, musste sie dem Ruf ihres Meisters folgen, bevor sie zu uns zurückkehren konnte.', 'So muss es sein. Obwohl sie bereits einmal gestorben war, musste sie dem Ruf ihres Meisters folgen, bevor sie zu uns zurückkehren konnte.', 38134),
(205245, 'deDE', 'Der Lichkönig ruft mich. Ich muss ihm erneut dienen!', 'Der Lichkönig ruft mich. Ich muss ihm erneut dienen!', 38134),
(205243, 'deDE', 'Pah, Staubelementare! Wenn sie so weitermachen, scheuern sie hier alles weg und die Wildsamen sterben!', '', 38134),
(205241, 'deDE', 'Das ist richtig. Diese Kreatur starb einen edlen Tod und hat ihren Platz in den Schattenlanden verdient.', 'Das ist richtig. Diese Kreatur starb einen edlen Tod und hat ihren Platz in den Schattenlanden verdient.', 38134),
(205240, 'deDE', 'Auch wenn es schmerzhaft sein mag, dürfen wir diese Kreatur nicht von ihrer Ewigkeit fernhalten.', 'Auch wenn es schmerzhaft sein mag, dürfen wir diese Kreatur nicht von ihrer Ewigkeit fernhalten.', 38134),
(205239, 'deDE', 'Erlaubt mir, zu übersetzen. Diese Kreatur fiel, während sie ihren Meister vor einem Wolfsrudel beschützte. Sie möchte zurückkehren, um sich zu vergewissern, dass es ihm gut geht.', 'Erlaubt mir, zu übersetzen. Diese Kreatur fiel, während sie ihren Meister vor einem Wolfsrudel beschützte. Sie möchte zurückkehren, um sich zu vergewissern, dass es ihm gut geht.', 38134),
(205238, 'deDE', 'Wuff! Bläff! Bläff! Bläff! Grrr...', '', 38134),
(205237, 'deDE', 'Das ist richtig. Er hat sich seinen Kriegertod verdient. Er ist ehrenhaft gestorben und in Maldraxxus zu großem Ruhm gekommen.', 'Das ist richtig. Er hat sich seinen Kriegertod verdient. Er ist ehrenhaft gestorben und in Maldraxxus zu großem Ruhm gekommen.', 38134),
(205236, 'deDE', 'Leider nein. Auch wenn andere ohne ihn tatsächlich sterben würden, hat er sein sterbliches Schicksal erfüllt.', 'Leider nein. Auch wenn andere ohne ihn tatsächlich sterben würden, hat er sein sterbliches Schicksal erfüllt.', 38134),
(205235, 'deDE', 'Die Schlacht tobt in meiner Stadt. Ich muss zurückkehren und die Kleinen beschützen, bevor die Bestien sie erreichen!', 'Die Schlacht tobt in meiner Stadt. Ich muss zurückkehren und die Kleinen beschützen, bevor die Bestien sie erreichen!', 38134),
(205234, 'deDE', 'Das ist inkorrekt. Wir dürfen keine Seelen überbringen, die mit anderen Kräften verbunden sind.', 'Das ist inkorrekt. Wir dürfen keine Seelen überbringen, die mit anderen Kräften verbunden sind.', 38134),
(205233, 'deDE', 'Korrekt. Seelen mit ausreichend starken Verbindungen zu anderen kosmischen Kräften muss die Rückkehr gewährt werden.', 'Korrekt. Seelen mit ausreichend starken Verbindungen zu anderen kosmischen Kräften muss die Rückkehr gewährt werden.', 38134),
(205232, 'deDE', '<Das Wesen gibt eine Kakofonie ängstlicher Glockentöne von sich.>', '', 38134),
(205230, 'deDE', 'Ein paar durchflutete Rubine, und die Sündenmähre da wird Euch bis zum Alten Tor bringen. Aber nicht loslassen, wenn Ihr unterwegs nicht abgeworfen werden wollt.', '', 38134),
(205229, 'deDE', 'Der Bass muss mehr wummern. Das muss mehr reinhauen.\n\nIhr nennt das hier einen Veranstaltungsort? Das ist eine Höhle! Hier gibt es keine Wärme und die Akustik ist bestenfalls schlecht.\n\nSeuchensau kann erst auftreten, wenn alles perfekt klingt. Nur weil ich untot bin, heißt das nicht, dass ich schlechter hören kann!', '', 38134),
(205222, 'deDE', 'Oh, hallo! Neue Rezepte gelernt?', '', 38134),
(205221, 'deDE', 'Ist gut, Euch zu sehen. Wir reden jetzt über Essen?', '', 38134),
(205220, 'deDE', 'Supi mein Name, weil ich Suppe koche. Ist witzig, ja?', '', 38134),
(205219, 'deDE', 'Hallo. Hallo! Ihr seid hier, um Rezept zu teilen?', '', 38134),
(205214, 'deDE', '', 'Meuchelschurken bilde ich nicht aus.', 38134),
(205212, 'deDE', '', 'Die Gier des Zehntherrn ist grenzenlos.\n\nIn Maldraxxus wäre so etwas undenkbar. Sollten sich die Venthyr nicht darauf konzentrieren, Seelen dabei zu helfen, für ihre Sünden zu büßen? Es macht mich außerdem krank, dass er ein Bündnis mit meinem Reich geschlossen hat.\n\nWir müssen diesem Wahnsinn Einhalt gebieten.', 38134),
(205205, 'deDE', '', 'Lasst... lasst mich in Ruhe.', 38134),
(205202, 'deDE', '', 'Seit Eurem ersten Sparringskampf ist Eure Stärke gewachsen. Sorgen wir für eine etwas angemessenere Herausforderung.', 38134),
(205201, 'deDE', '', 'Wer die Augen verraten hat, wird sterben!', 38134),
(205197, 'deDE', '<Arkelios kichert.>\n\nNein. Wenn sich alles unter mir auflöst...\n\nEs ist einfach an der Zeit für ein neues Abenteuer.', '', 38134),
(205194, 'deDE', 'Nun... ich bin gelaufen! \n\nIch habe dieses wunderbare Fleckchen unter einem Baum gefunden und angehalten, um zu meditieren.\n\nIrgendwie bin ich länger in mich gegangen, als ich gedacht hatte, denn ich scheine mich vom Rest des Ardenwalds entfernt zu haben.', '', 38134),
(205192, 'deDE', 'Ich bin ein Wanderer. Ein Reisender.\n\nIch bin äonenlang durch die Reiche des Lebens und nun des Todes gewandert und habe sie erkundet. Während die Zeit immer weiter voranschreitet, blicke ich zurück auf meine Existenz und denke über vieles nach.', '', 38134),
(205190, 'deDE', 'Seid gegrüßt, $gWeggefährte:Weggefährtin;.', '', 38134),
(205189, 'deDE', 'Ich habe... ich meine, wir haben die Macht! Endlich muss ich mir Euer ewiges Gekreische, dass Ihr diese Kammer bewachen müsst, nicht mehr anhören.', '', 38134),
(205188, 'deDE', 'Nein! Geht weg. Aufhören! Niemand darf hier rein.', '', 38134),
(205187, 'deDE', 'Na, wie fühlt sich das an? Na? Na? Macht Ihr jetzt auf, oder was?', '', 38134),
(205186, 'deDE', 'Ahhhh. Das habe ich <rülps> so vermisst. Mehr! Wir brauchen mehr.', '', 38134),
(205185, 'deDE', 'Seid Ihr auf der Suche nach einer... Verbesserung? \n\nEin bisschen mehr Durchschlagskraft, ein wenig mehr magische Finesse?\n\nSie gehört Euch, wenn Ihr das Gold dafür habt.', '', 38134),
(205184, 'deDE', 'Neeeein! Der Meister wird rasend sein vor Wut. Dafür bezahlt Ihr!', '', 38134),
(205182, 'deDE', 'Der mächtige Phalynx wird niemals wanken.', '', 38134),
(205180, 'deDE', 'Redet er mit Euch? Er ist ein Verräter. Hört nicht auf ihn.', '', 38134),
(205179, 'deDE', 'Pfft! Ignoriert diesen quasselnden Narren. Wir werden in diese Kammer gelangen. Lasst uns etwas zum Auslaugen suchen!', '', 38134),
(205178, 'deDE', 'Oooh ja. Das ist genau das Richtige! Ich... äh, wir werden noch viel mehr brauchen.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205173, 'deDE', '$uDer Schlundgänger:Die Schlundgängerin; kommt uns zu Hilfe! Ich zähle auf Euren Erfolg in dieser Angelegenheit.', '', 38134),
(205171, 'deDE', '', 'Einer meiner Späher hat geheime Informationen über Lysonias Bewegungen geliefert.', 38134),
(205155, 'deDE', '<Er hat sich tapfer gewehrt, scheint aber nicht auf einen Kampf vorbereitet gewesen zu sein.>', '', 38134),
(205153, 'deDE', '<Dieser Mittler scheint im Kampf gestorben zu sein.>', '', 38134),
(205148, 'deDE', 'Prima! Das sollte ausreichen. Jetzt können wir an diesem Großmaul vorbeikommen. Gehen wir zurück!', '', 38134),
(205147, 'deDE', '<Die Schneewehe hat diesem Mittler kaum Schutz bieten können.>', '', 38134),
(205146, 'deDE', 'Ihr findet nichts.', 'Ihr findet nichts.', 38134),
(205145, 'deDE', '<Dieser verletzte Mittler hätte das Portal vor seinem Tod fast erreicht.\n\nEs gibt keinen Hinweis darauf, dass er vor seinem Tod etwas von Wert gefunden hat.>', '', 38134),
(205144, 'deDE', 'Ich informiere die Archon.', '', 38134),
(205135, 'deDE', 'Hey! Lasst die Finger davon. Legt das wieder hin. Dieb!', '', 38134),
(205134, 'deDE', 'Bleibt zurück! Ihr dürft hier nicht drin sein.', '', 38134),
(205132, 'deDE', 'Das scheint ein Stapel zurückgelassener Rüstung zu sein.', '', 38134),
(205129, 'deDE', 'Ähem!\n\nGlaubt bloß nicht, dass Ihr an mir vorbeikommt. Ich bin unempfänglich für Euer erbärmliches Flehen.', '', 38134),
(205099, 'deDE', 'Mir wurde gesagt, jemand aus Sündensturz sei hier. Eure Anwesenheit ehrt uns.\n\nVersucht, den Hütern aus dem Weg zu gehen. Sie arbeiten daran, die hier angerichtete Zerstörung rückgängig zu machen.', 'Mir wurde gesagt, jemand aus Sündensturz sei hier. Eure Anwesenheit ehrt uns.\n\nVersucht, den Hütern aus dem Weg zu gehen. Sie arbeiten daran, die hier angerichtete Zerstörung rückgängig zu machen.', 38134),
(205098, 'deDE', 'Der Gruftdistrikt war einst ein Beispiel der Ordnung in Revendreth.\n\nJetzt müssen wir daran arbeiten, ihm wieder zu seiner früheren Pracht zu verhelfen. Wir haben hier mit dem Restaurierungsprozess begonnen. Leider ist der Rest des Distrikts im Augenblick viel zu gefährlich für uns.\n\nEntschuldigt mich, meine Pflichten rufen.', 'Der Gruftdistrikt war einst ein Beispiel der Ordnung in Revendreth.\n\nJetzt müssen wir daran arbeiten, ihm wieder zu seiner früheren Pracht zu verhelfen. Wir haben hier mit dem Restaurierungsprozess begonnen. Leider ist der Rest des Distrikts im Augenblick viel zu gefährlich für uns.\n\nEntschuldigt mich, meine Pflichten rufen.', 38134),
(205097, 'deDE', '', 'Sein Glaube war so stark. Er hatte keinerlei Zweifel.\n\nIch habe selbst mitangesehen, wie seine ganze Welt zzerschmettert wurde.\n\nIch...\n\nIch weiß nicht mehr, ob das alles noch einen Sinn ergibt.', 38134),
(205095, 'deDE', '', 'Wir wussten, dass einige Seelen versehentlich im Schlund gelandet sind.\n\nAchillon war immer so stark. Er hat mir Selbstsicherheit gegeben. Er war immer loyal. Er war immer voller Hoffnung.\n\nAber bei der Ankunft in Oribos... beim Anblick all dieser Seelen...\n\nIch habe ihn vor meinen Augen fallen sehen.', 38134),
(205093, 'deDE', '', '<Pallessa starrt ohne zu blinzeln ins Wasser.>', 38134),
(205091, 'deDE', 'Ihr und Eure Schar Verbündeter könnt Euch meiner ewigen Wertschätzung für Eure Taten sicher sein, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(205088, 'deDE', '', 'Halte... es nicht... mehr aus...', 38134),
(205087, 'deDE', '', 'Unggh...', 38134),
(205086, 'deDE', '', 'Die Macht des Kerkermeisters wächst, und die Schattenlande stehen großer Gefahr gegenüber. Seid bereit, $p. Eure Stärke wird in den nächsten Tagen benötigt.', 38134),
(205079, 'deDE', '', 'Bis dann, $uFreund:Freundin; der Kyrianer! Viel Glück!', 38134),
(205078, 'deDE', '', 'Möget Ihr und Eure Seelenbande die Lektionen der Geduld, Einigkeit und Zusammenarbeit, die wir verkünden, vergegenwärtigen.', 38134),
(205077, 'deDE', '', 'Wann immer Ihr wünscht, $n. Meine Wurzeln reichen tief.', 38134),
(205073, 'deDE', '', 'Die besessenen Fae haben mich in die Enge getrieben! Ich wusste nicht, was ich tun sollte!', 38134),
(205069, 'deDE', 'Was sollte das denn? Ich hatte gerade Spaß bei meinem Ausflug!', '', 38134),
(205063, 'deDE', '', 'Mein armer Gummi ist ganz aufgebracht! Nichts kann ihn beruhigen.', 38134),
(205062, 'deDE', '', 'Helft meinen Verbündeten mit den Problemen, die sie fesseln. Versichert ihnen, dass sie bei ihrer Rückkehr die Verjüngung erhalten, die sie benötigen.', 38134),
(205059, 'deDE', '', 'Thran\'tiok hat ihr Phylakterium außerhalb des Hauses der Rituale versteckt, nachdem es entdeckt wurde. Ihr müsst es finden... für uns beide.', 38134),
(205056, 'deDE', 'Was plagt Euch, $gSterblicher:Sterbliche;?', '', 38134),
(205052, 'deDE', '', 'Dieses Mal nicht, Thran\'tiok!', 38134),
(205051, 'deDE', '', 'Ihr werdet mir durch schmerzvolles Leiden dienen, wenn ich Euch breche und herausfinde, welcher meiner Feinde Euer Meister ist...', 38134),
(205050, 'deDE', '', 'Ein weiterer närrischer Verräter landet in meiner Falle!', 38134),
(204961, 'deDE', 'Sündensturz ist standhaft.', 'Sündensturz ist standhaft.', 38134),
(204960, 'deDE', 'Ja, $gSchlundgänger:Schlundgängerin;?', 'Ja, $gSchlundgänger:Schlundgängerin;?', 38134),
(204959, 'deDE', 'Neue Befehle, $gmein Herr:meine Dame;?', 'Neue Befehle, $gmein Herr:meine Dame;?', 38134),
(204958, 'deDE', 'Der General lässt Euch grüßen.', 'Der General lässt Euch grüßen.', 38134),
(204957, 'deDE', 'Für Sündensturz.', 'Für Sündensturz.', 38134),
(204956, 'deDE', 'Ich bin bereit.', 'Ich bin bereit.', 38134),
(204955, 'deDE', 'Wie kann ich dienen?', 'Wie kann ich dienen?', 38134),
(204954, 'deDE', 'Was braucht Ihr?', 'Was braucht Ihr?', 38134),
(204889, 'deDE', 'Die Gewundenen Korridore', 'Die Gewundenen Korridore', 38134),
(204888, 'deDE', 'Die Oberen Ebenen', 'Die Oberen Ebenen', 38134),
(204883, 'deDE', 'Kaltherzinterstitia', 'Kaltherzinterstitia', 38134),
(204882, 'deDE', 'Die Seelenschmieden', 'Die Seelenschmieden', 38134),
(204879, 'deDE', 'Ich kann alles erledigen, was Ihr benötigt.\n\nWie kann ich behilflich sein, $gder Herr:die Dame;?', '', 38134),
(204876, 'deDE', 'Gute Farbe. Ne ordentliche Butlerfarbe, oder?', '', 38134),
(204875, 'deDE', 'Was für ein Farbpigment sollen wir denn für Euren Gräberbutler in die Schlammlache werfen?\n\nWir können die Farbe später ändern, aber das ist unangenehm für den Gräber, also überlegt gut.', '', 38134),
(204874, 'deDE', 'Ihr wollt einen neuen Gräber machen, was?\n\nDann macht ihn wie mich. Ich bin der Beste!', '', 38134),
(204873, 'deDE', 'Weiß nicht warum, aber heute ist mir irgendwie nach Blaumachen...', '', 38134),
(204872, 'deDE', 'Diese neuen Gräber machen sich nicht von alleine...', '', 38134),
(204870, 'deDE', 'Ein Samen ohne jegliche Anima.', 'Ein Samen ohne jegliche Anima.', 38134),
(204868, 'deDE', 'Mir fehlt die Steinwachtwarte. \n\nOder das, was sie mal war... Nicht das, was unser neuer \"Ernter\" daraus gemacht hat.', '', 38134),
(204867, 'deDE', 'Wir wurden hierher verbannt, weil wir Anima in der Steinwachtwarte \"gehortet\" haben.\n\nWenn Ihr mich fragt, ist es kein Horten, wenn man nur so viel Anima hat, wie man braucht. Das nennt sich Überleben.\n\nDieser Zehntherr ist genauso korrupt wie Denathrius.', '', 38134),
(204865, 'deDE', 'Der Glutdistrikt ist ein unerbittlicher Ort. Ich wünschte, wir könnten ihn verlassen.\n\nAber wir müssen hier in diesem Versteck bleiben, denn das Licht verbrennt unsere Haut. Der Schmerz ist unerträglich.', '', 38134),
(204863, 'deDE', 'Endlich ein Hoffnungsschimmer. Habt Dank, $R.', '', 38134),
(204862, 'deDE', 'Wir haben es geschafft! Das Licht steht nicht mehr zwischen uns und unserem Überleben.', '', 38134),
(204852, 'deDE', 'Vielleicht können wir ihn ausbilden!', '', 38134),
(204851, 'deDE', 'Wir aber müssen uns mit einem Gräber zufriedengeben.', '', 38134),
(204850, 'deDE', 'Denkt daran: Die besten Butler sind weise, charmant und verfügen über außergewöhnlich gute soziale Fertigkeiten.', '', 38134),
(204840, 'deDE', 'Zwei Dutzend Jahrtausende lang wurde ich vom Boss angekaut. Seit Äonen das Futter der Blutsauger.\n\nJo, man kann die Bissspuren nicht sehen, aber die wissen schon, wie sie\'s verstecken müssen, damit andere\'s nicht sehen. Die hochnäsigen Venthyr finden das würdelos.\n\nDann kommt Ihr eines Tages daher und macht meinen Boss fertig. Ich glaub zwar nicht, dass Ihr wusstet, wen Ihr getötet oder befreit habt, aber ich bin Euch trotzdem dankbar. Und jetzt gibt\'s ein bisschen Rache.', '', 38134),
(204839, 'deDE', 'Fratzi hat auf Euch gesetzt und \'n paar Rubinchen gewonnen.', '', 38134),
(204837, 'deDE', 'Sterblicher.', '', 38134),
(204835, 'deDE', 'Euer Meister ist ziemlich anspruchsvoll. Es wird einige Zeit dauern, die von ihm erbetenen Reagenzien zu sammeln.\n\nIch kann den Prozess jedoch womöglich beschleunigen, wenn Ihr mir dabei helft, einige der schwierigeren Zutaten zu sammeln.', '', 38134),
(204834, 'deDE', 'Was kann ich für Euch beschaffen, $uAkolyth:Akolythin;?', '', 38134),
(204827, 'deDE', '', 'Treffen wir uns in Revendreth. Wir werden die Wurzel dieses Verrats finden... und sie beseitigen.', 38134),
(204823, 'deDE', 'All dieses Gerede lässt diese alten Knochen oft ziemlich austrocknen, und man muss geschmiert bleiben, wenn man mit all diesen Kämpfern ringt.', '', 38134),
(204814, 'deDE', 'Nun, zumindest erkennt Ihr Eure Torheit.\n\nIch kann Euch nicht einfach zurückkehren lassen. Meine Ehre hat höhere Ansprüche.\n\nIhr müsst Euch als würdig erweisen. Zufällig habe ich eine zu Euren Fähigkeiten passende Aufgabe.\n\nSeid Ihr bereit, diese Buße zu tun, um wieder zu den Venthyr zu gehören?', '', 38134),
(204813, 'deDE', 'Ihr wagt es, vor mich zu treten und mich das zu fragen?\n\nWenn wir nicht auf neutralem Boden stünden, würde ich Euch womöglich einfach zermalmen.\n\nWarum sollten wir Euch zurückhaben wollen?', '', 38134),
(204812, 'deDE', 'Eure Entschlossenheit ist beruhigend, $uSchlundgänger:Schlundgängerin;.\n\nWir kennen Eure Stärken bereits.\n\nNun, da Ihr Euch sicher seid, schwört den Venthyr die Treue.', '', 38134),
(204811, 'deDE', 'Das ist keine Kleinigkeit, $n.\n\nIhr müsst Euch sicher sein, dass Ihr Euch unseren Reihen anschließen wollt.\n\nSeid Ihr wirklich ganz sicher, dass Ihr das besprechen wollt?', '', 38134),
(204810, 'deDE', 'Irre ich mich? Seid Ihr nicht bereits Mitglied eines anderen Pakts?\n\nIhr mögt auf uns Venthyr neidisch sein, aber seid Ihr wirklich bereit, Eure derzeitigen Verbündeten zu verraten?', '', 38134),
(204809, 'deDE', 'Wie kann ich Euch helfen, $uSchlundgänger:Schlundgängerin;?', '', 38134),
(204802, 'deDE', 'Ihr seid $uAkolyth:Akolythin; des Barons, nicht von mir, $n.\n\nVergesst das nicht und beschränkt unseren Austausch auf das Nötigste.', '', 38134),
(204799, 'deDE', 'Der Portalstein! Unser Weg hier raus.', '', 38134),
(204798, 'deDE', 'Ihr seid dem Kerkermeister entkommen... Ja. Es wird deutlicher.', '', 38134),
(204795, 'deDE', 'Der Schlund... Ich erinnere mich. Als wäre es schon lange her.', '', 38134),
(204793, 'deDE', 'Ich werde das nach Bedarf unter unseren Truppen verteilen. Lasst uns über die Sicherheitsauswahl sprechen.', '', 38134),
(204784, 'deDE', '', 'Ihr wünscht jetzt also in unsere Reihen zurückzukehren?\n\nIch kann Euch leider nicht einfach zurück ins Herz des Waldes spazieren lassen.\n\nIhr müsst uns beweisen, dass Ihr würdig seid, uns wieder zu dienen.\n\nZum Glück habe ich eine Aufgabe, die eine ideale Prüfung abgeben sollte.\n\nSeid Ihr bereit, Euch zu beweisen?', 38134),
(204783, 'deDE', '', 'Ah, $uder, der:die, die; uns enttäuscht hat.\n\nIch hätte nicht gedacht, Euch hier noch einmal zu sehen, $n.\n\nGlaubt nicht, dass wir Euch vergeben haben.', 38134),
(204782, 'deDE', '', 'Ihr scheint fest entschlossen zu sein.\n\nWarum sollte ich das anzweifeln? Ihr habt unserer Königin bereits treu gedient. Ihr könnt es wieder tun.\n\nVerschreibt Ihr Eure ganze Seele dem Dienst an den Nachtfae und unserer geliebten Königin?', 38134),
(204781, 'deDE', 'Das wäre nicht meine erste Wahl... aber ich kann etwas damit anfangen.', 'Das wäre nicht meine erste Wahl... aber ich kann etwas damit anfangen.', 38134),
(204780, 'deDE', '', 'Seid Ihr Euch sicher, $n?\n\nEine derartige Entscheidung kann großes Ungleichgewicht in den Kreislauf des Lebens bringen. Ihr müsst absolut sicher sein, dass Ihr das wünscht, bevor wir mehr besprechen.', 38134),
(204778, 'deDE', '', 'Verstehe.\n\nNun, wir sind dankbar für die Hilfe, die Ihr uns bisher geleistet habt, doch Ihr vertretet bereits einen anderen Pakt.\n\nMöchtet Ihr wirklich mit mir darüber sprechen, diesen für uns zu verraten?', 38134),
(204777, 'deDE', 'So! Alles fertig. Seid, äh, vorsichtig und verhaltet Euch ruhig. Und geht auf keinen Fall zu nahe an die Verschlinger ran. Gute Jagd!', '', 38134),
(204776, 'deDE', '', 'Wie kann ich Euch helfen, $uSchlundgänger:Schlundgängerin;?', 38134),
(204775, 'deDE', '', 'Ihr habt Euch bereits zuvor von unserer Stärke abgewandt.\n\nWarum sollten wir das vergessen? Habt Ihr dazu etwas zu sagen?', 38134),
(204773, 'deDE', 'Na gut! Hier is\' mein zweitbester Striegel. Die Tiere finden es toll, wie er gegen Juckreiz hilft.', '', 38134),
(204772, 'deDE', 'Eure Magie kann Euch nicht vor mir verstecken.', '', 38134),
(204771, 'deDE', 'Ich sehe Euch...', '', 38134),
(204770, 'deDE', 'Was ist das für ein Trick?', '', 38134),
(204767, 'deDE', '', 'Seid Ihr bereit, Euch uns gegenüber wieder als würdig zu erweisen?\n\nWir werden Euch nicht mit offenen Armen wieder aufnehmen. Nein, dieses Mal nicht. Dieses Mal brauchen wir einen Beweis, dass Ihr zu uns steht.\n\nSeid Ihr bereit, Euch als würdig zu erweisen?', 38134),
(204765, 'deDE', 'Euer Wille ist stark.\n\nIhr werdet zweifelsohne einen guten Nekrolord abgeben. Jetzt müsst Ihr Eure Stärke und Macht in unseren Dienst schwören.\n\nSchwört es!', 'Euer Wille ist stark.\n\nIhr werdet zweifelsohne einen guten Nekrolord abgeben. Jetzt müsst Ihr Eure Stärke und Macht in unseren Dienst stellen.\n\nSchwört es!', 38134),
(204764, 'deDE', 'Diesen Pfad zu beschreiten, bedeutet, Euren gewählten Pakt zu verlassen.\n\nEs ist verständlich, dass Ihr stärkere Verbündete sucht, doch Eure aktuellen werden Euch diese Abkehr übel nehmen.\n\nWollt Ihr wirklich fortfahren?', 'Diesen Pfad zu beschreiten, bedeutet, Eure derzeitigen Verbündeten im Stich zu lassen.\n\nEs ist verständlich, dass Ihr stärkere Freunde sucht, doch Eure aktuellen werden Euch diesen Verrat übel nehmen.\n\nWollt Ihr das wirklich besprechen?', 38134),
(204750, 'deDE', 'Ja, sollte reichen. Die Unterhaltung kann starten.', '', 38134),
(204746, 'deDE', 'Damit mache ich schönes Zeug. Dekorationen sind fertig!', '', 38134),
(204745, 'deDE', '', 'Wir erfüllen hier in Revendreth einen Zweck, auch wenn das viele Venthyr vergessen haben.', 38134),
(204744, 'deDE', '', 'Fragt nicht, wie ich daran gekommen bin. Verbucht es einfach als Gefallen, den Ihr zurückzahlen müsst. Ich freue mich auf zukünftige Geschäfte mit Euch.', 38134),
(204742, 'deDE', '', 'Da ich Eure Bitte kommen sah, habe ich ein Portalgerät erworben, das in den Turm führt.', 38134),
(204741, 'deDE', '', 'Ich spare mir die üblichen Warnungen, da Eure einzigartige Verbindung von Mut und Tollkühnheit das ohnehin überflüssig macht.', 38134),
(204740, 'deDE', 'Ich muss die Schriftrolle einfach behalten, um meine Nachforschungen abzuschließen.\n\nSagt der Baronin einfach, dass Ihr sie nicht finden konntet.', 'Ich muss die Schriftrolle einfach behalten, um meine Nachforschungen abzuschließen.\n\nSagt der Baronin einfach, dass Ihr sie nicht finden konntet.', 38134),
(204739, 'deDE', 'Ja, ich habe die Schriftrolle noch.\n\nWas genau gedenkt Ihr dagegen zu tun?', 'Ja, ich habe die Schriftrolle noch.\n\nWas genau gedenkt Ihr dagegen zu tun?', 38134),
(204738, 'deDE', 'Oh, die? Ich bin fast fertig.\n\nSagt Ninadar einfach, dass ich sie noch etwas länger brauche.', 'Oh, die? Ich bin fast fertig.\n\nSagt Ninadar einfach, dass ich sie noch etwas länger brauche.', 38134),
(204737, 'deDE', '', 'Torghast, ja? Sobald ich Euch sagte, dass Eure Freunde dort festgehalten werden, wusste ich, dass Ihr mich bald um Hilfe bitten würdet.', 38134),
(204735, 'deDE', 'Seht Ihr nicht, dass ich beschäftigt bin, $uAkolyth:Akolythin;?', 'Seht Ihr nicht, dass ich beschäftigt bin, $uAkolyth:Akolythin;?', 38134),
(204734, 'deDE', 'Müsst Ihr nicht irgendein Ritual studieren?', 'Müsst Ihr nicht irgendein Ritual studieren?', 38134),
(204733, 'deDE', 'Was wollt Ihr, $uAkolyth:Akolythin;?', 'Was wollt Ihr, $uAkolyth:Akolythin;?', 38134),
(204717, 'deDE', '<Ihr seht eine kleine Steinfledermaus an diesem Schrein hängen.\n\nIhr stellt fest, dass man mit einer bestimmten Menge durchfluteter Rubine die Fledermaus aufwecken könnte.\n\nVielleicht wird sie Euch dann sogar gehorchen.>', '<Ihr seht eine kleine Steinfledermaus an diesem Schrein hängen.\n\nIhr stellt fest, dass man mit einer bestimmten Menge durchfluteter Rubine die Fledermaus aufwecken könnte.\n\nVielleicht wird sie Euch dann sogar gehorchen.>', 38134),
(204716, 'deDE', 'Wir müssen wachsam bleiben.', '', 38134),
(204714, 'deDE', 'Auf Euren Befehl, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(204707, 'deDE', '', 'Ah ja, ich erinnere mich, dass der Baron eine Foliantensammlung aus meiner Bibliothek angefordert hat, eine seltsame Auswahl an Themen, wenn ich mich recht entsinne.\n\nWie Ihr seht, bin ich eine sehr beschäftigte Lich. Solltet Ihr mir aber die Arbeit erleichtern, sehe ich mich vielleicht in der Lage, Euch die Folianten zu liefern, die Ihr sucht.', 38134),
(204706, 'deDE', '', 'Ich hoffe, Ihr habt einen guten Grund, mich zu unterbrechen, $uAkolyth:Akolythin;.\n\nAlso, worum geht es?', 38134),
(204705, 'deDE', '', 'Oh, Rogana, ich habe unzählige Komplimente für das Kleid erhalten, das Ihr nur für mich genäht habt.', 38134),
(204704, 'deDE', 'Und stets eine meiner allerliebsten Sünden, liebe Rogana.', '', 38134),
(204702, 'deDE', '', 'Ihr solltet das zurück zum Sanktum bringen. Vielleicht können Baronin Draka oder einer der anderen dessen Bedeutung erkennen.', 38134),
(204701, 'deDE', '<Tulpennase kichert.>', '', 38134),
(204700, 'deDE', 'Seid vorsichtig damit, $gSchlundgänger:Schlundgängerin;... Ich würde nur ungern sehen, dass Eure Kleidung ruiniert wird.', '', 38134),
(204699, 'deDE', '', 'Ah ja, das ist es. Eine Rüstung von unvergleichlicher Qualität. Ihresgleichen habe ich nicht einmal in den Schmieden der Auserwählten gesehen.', 38134),
(204695, 'deDE', 'Ach, kommt schon, Temel! Die Fae wissen, wie man feiert!', '', 38134),
(204694, 'deDE', 'Ich weiß nicht genau, was mich nervöser macht: das Ungeziefer oder die Fae...', '', 38134),
(204692, 'deDE', 'Begleitet mich, $uAkolyth:Akolythin;. Gestattet Euch, die Früchte Eurer Arbeit zu genießen.', '', 38134),
(204691, 'deDE', 'Bereitet Euch gut vor, Nelesis, denn die Stunde unseres Triumphs wird bald schlagen.', '', 38134),
(204690, 'deDE', 'Wie kann ich Euch helfen, $uSchlundgänger:Schlundgängerin;?', '', 38134),
(204689, 'deDE', 'Ihr habt bereits Eure Macht und Findigkeit bewiesen, $umein Freund:meine Liebe;.\n\nAuch wenn ich Euch gern auf Seiten der Nekrolords im Kampf um Maldraxxus begrüßen würde, gehört Ihr doch bereits einem anderen Pakt an.\n\nWollt Ihr den wirklich verlassen?', '', 38134),
(204684, 'deDE', 'Ihr werdet verstehen müssen, dass wir Euch nicht beim Wort nehmen.\n\nSeid Ihr bereit, dafür zu arbeiten, den Kyrianern wieder beitreten zu dürfen?', '', 38134),
(204681, 'deDE', 'Da seid Ihr ja wieder, $n.\n\nSehr mutig von Euch, mir erneut gegenüberzutreten.\n\nWir haben nicht vergessen, dass Ihr unseren Pakt verraten habt.', '', 38134),
(204677, 'deDE', 'Seid auf der Hut! Er spürt uns!', '', 38134),
(204676, 'deDE', '', 'Lasst die Gäste in Ruhe, Podd. $p hat offensichtlich große Pläne für diesen Hof.', 38134),
(204675, 'deDE', 'Ihr seid hübscher als ein ganzer Schlammhaufen!', '', 38134),
(204674, 'deDE', '', 'Wenn Ihr von dieser Kleidung genug habt, habe ich nach dem Fest weitere Optionen für Euch.', 38134),
(204667, 'deDE', '', 'Mögen Eure Waffen ihr Ziel treffen.', 38134),
(204666, 'deDE', 'Es ist vollbracht. Es war mir eine Ehre, mit Euch zu jagen.', '', 38134),
(204665, 'deDE', '', 'Wir sind bereit, $n. Sagt, wenn Ihr es seid.', 38134),
(204664, 'deDE', 'Wie kann ich Euch helfen?', '', 38134),
(204658, 'deDE', 'Ich glaube Euch.\n\nJetzt muss ich Euch bitten, den Kyrianern die Treue zu schwören und ein Mitglied des Pakts der Kyrianer zu werden.\n\nSprecht mir nach...\n\n\"Ich werde den Kyrianern mutig und mit all meinem Willen dienen.\"', '', 38134),
(204657, 'deDE', 'Ich muss ein letztes Mal nachfragen, $umein Freund:meine Liebe;.\n\nSeid Ihr sicher, dass Ihr den Kyrianern beitreten möchtet? Das bedeutet, Eure derzeitigen Verbündeten zu verraten.\n\nDas werden sie nicht so schnell vergessen.\n\nSeid Ihr wirklich sicher?', '', 38134),
(204656, 'deDE', 'Ihr habt Euch bereits einem anderen Pakt verpflichtet, $n. Wollt Ihr diesem Pfad wirklich folgen?\n\nWir wissen noch genau, wie Ihr uns in der Bastion geholfen habt. Eure Stärke wäre uns willkommen.\n\nSeid Ihr wirklich bereit, Eure jetzigen Verbündeten zu verraten?', '', 38134),
(204653, 'deDE', 'Oh pscht, Ihr alte Schlammlache... Ich glaube kaum, dass sie sich überhaupt noch an mich erinnert.', 'Oh pscht, Ihr alte Schlammlache... Ich glaube kaum, dass sie sich überhaupt noch an mich erinnert.', 38134),
(204652, 'deDE', 'Ich habe gesehen, dass auch die Kuratorin hier ist, Fredrik...', 'Ich habe gesehen, dass auch die Kuratorin hier ist, Fredrik...', 38134),
(204651, 'deDE', 'Seid kein Fangzahnbrecher, in dieser Gruft gibt es nur gebrochene Herzen.', 'Seid kein Fangzahnbrecher, in dieser Gruft gibt es nur gebrochene Herzen.', 38134),
(204650, 'deDE', 'Habt Ihr vorhin die Hörner dieser Sylvar gesehen? Wie reizend...', 'Habt Ihr vorhin die Hörner dieser Sylvar gesehen? Wie reizend...', 38134),
(204649, 'deDE', 'Harlowe, Ihr alter Sündenstein! Das ist vergebliche Liebesmüh...', 'Harlowe, Ihr alter Sündenstein! Das ist vergebliche Liebesmüh...', 38134),
(204648, 'deDE', 'Ich glaube, ich habe vorhin die Anklägerin auf der Bühne gesehen. Ich mochte sie schon immer...', 'Ich glaube, ich habe vorhin die Anklägerin auf der Bühne gesehen. Ich mochte sie schon immer...', 38134),
(204647, 'deDE', 'Oh, unbedingt.', 'Oh, unbedingt.', 38134),
(204646, 'deDE', 'Sollen wir an Theotar und Lady Ilinca vorbeistolzieren und sie neidisch machen?', 'Sollen wir an Theotar und Lady Ilinca vorbeistolzieren und sie neidisch machen?', 38134),
(204645, 'deDE', 'Habt Dank, Fredrik. Mein Stil steht Euch auch exzellent.', 'Habt Dank, Fredrik. Mein Stil steht Euch auch exzellent.', 38134),
(204644, 'deDE', 'Und, Harlowe, mein Stil steht Euch exzellent.', 'Und, Harlowe, mein Stil steht Euch exzellent.', 38134),
(204643, 'deDE', '', 'Unmöglich!', 38134),
(204641, 'deDE', 'Oh, ich liebe Dramen am Hof!', '', 38134),
(204640, 'deDE', 'Ich würde mich nicht mal TOT mit Euch hier blicken lassen, Frederik!', 'Ich würde mich nicht mal TOT mit Euch hier blicken lassen, Frederik!', 38134),
(204639, 'deDE', 'Heute werden wir alle miterleben, wie ein fester Bestandteil von Sündensturz geschaffen wird!', '', 38134),
(204638, 'deDE', 'Ah, gut. Der Bildhauer ist eingetroffen. Ein besonderes Erlebnis für unsere Gäste.', '', 38134),
(204632, 'deDE', 'Ich habe ihn zuerst entdeckt, Fredrik! Ihr seid der Modedieb!', 'Ich habe ihn zuerst entdeckt, Fredrik! Ihr seid der Modedieb!', 38134),
(204626, 'deDE', 'Was für eine Statue soll ich Euch machen?', '', 38134),
(204623, 'deDE', 'Die Verschmähten trachten unerlässlich nach unserem Wissen. In diesem Moment suchen sie nach den uralten Schriftrollen mit der gesammelten Weisheit des Paragons.\n\nIch weiß nicht, warum, aber gewiss nicht, um daraus zu lernen.\n\nBitte holt diese Schriftrollen zurück. Mein Jünger wird Euch zu ihnen tragen.', 'Die Verschmähten trachten unerlässlich nach unserem Wissen. In diesem Moment suchen sie nach den uralten Schriftrollen mit der gesammelten Weisheit des Paragons.\n\nIch weiß nicht, warum, aber gewiss nicht, um daraus zu lernen.\n\nBitte holt diese Schriftrollen zurück. Mein Jünger wird Euch zu ihnen tragen.', 38134),
(204621, 'deDE', 'Ohne unsere Schwingen würden die Schattenlande im Chaos versinken. Möge Euch unsere ewige Pflicht leiten.', 'Ohne unsere Schwingen würden die Schattenlande im Chaos versinken. Möge Euch unsere ewige Pflicht leiten.', 38134),
(204620, 'deDE', 'B... Bleibt zurück!\n\nOh, Ihr seid nicht hier, um mich zu diesen fürchterlichen Verbrechen auf Pergament zu bringen? \n\nDiese Venthyr haben mir meine Tinte weggenommen, weil ich ihre \"Forschung\" nicht niederschreiben wollte. Wenn man das so nennen kann.\n\nKönnt Ihr mir helfen? Ohne meine Tinte bin ich machtlos!', 'B... Bleibt zurück!\n\nOh, Ihr seid nicht hier, um mich zu diesen fürchterlichen Verbrechen auf Pergament zu bringen? \n\nDiese Venthyr haben mir meine Tinte weggenommen, weil ich ihre \"Forschung\" nicht niederschreiben wollte. Wenn man das so nennen kann.\n\nKönnt Ihr mir helfen? Ohne meine Tinte bin ich machtlos!', 38134),
(204618, 'deDE', 'Erinnert Euch an Eure Ausbildung. Vertraut auf den Pfad, die Richterin und ihre unendliche Weisheit.', 'Erinnert Euch an Eure Ausbildung. Vertraut auf den Pfad, die Richterin und ihre unendliche Weisheit.', 38134),
(204617, 'deDE', 'Ihr werdet viele in den Armen tragen. Einige werden schwerer wiegen als andere.', 'Ihr werdet viele in den Armen tragen. Einige werden schwerer wiegen als andere.', 38134),
(204616, 'deDE', 'Alles andere überlasst der Richterin und dem Ewigen Pfad. Dies fällt in unseren Aufgabenbereich.', 'Alles andere überlasst der Richterin und dem Ewigen Pfad. Dies fällt in unseren Aufgabenbereich.', 38134),
(204615, 'deDE', 'Die letzten Momente im Leben anderer dürft Ihr nur verwenden, um zu entscheiden, ob ihre Zeit im Reich der Sterblichen beendet ist.', 'Die letzten Momente im Leben anderer dürft Ihr nur verwenden, um zu entscheiden, ob ihre Zeit im Reich der Sterblichen beendet ist.', 38134),
(204614, 'deDE', 'Erliegt nicht der Versuchung, sie zu verurteilen. Alle Seelen treten vor die Richterin und alle werden ernten, was sie gesät haben.', 'Erliegt nicht der Versuchung, sie zu verurteilen. Alle Seelen treten vor die Richterin und alle werden ernten, was sie gesät haben.', 38134),
(204613, 'deDE', 'Tragt nichts von Euch selbst in ihre Leben, denn ihre Existenz gehört ihnen selbst.', 'Tragt nichts von Euch selbst in ihre Leben, denn ihre Existenz gehört ihnen selbst.', 38134),
(204612, 'deDE', 'Wer einen Blick in das Leben eines anderen wirft, erkennt, wie diese Person wirklich ist. Und das ohne Vorbehalte oder sie zu verurteilen.', 'Wer einen Blick in das Leben eines anderen wirft, erkennt, wie diese Person wirklich ist. Und das ohne Vorbehalte oder sie zu verurteilen.', 38134),
(204611, 'deDE', 'Nur, wenn Ihr Abstand zu den Erfahrungen anderer gewinnt, könnt Ihr deren Wahrheit erkennen.', 'Nur, wenn Ihr Abstand zu den Erfahrungen anderer gewinnt, könnt Ihr deren Wahrheit erkennen.', 38134),
(204610, 'deDE', 'Eine gute Jagd, $n. Und nun zu Eurer Belohnung.', '', 38134),
(204609, 'deDE', '', 'Endlich sind unsere geschätzten Wälder vor diesem Gorm sicher.', 38134),
(204608, 'deDE', '', 'Ein neuer Zauber! Ich kann kaum abwarten, ihn auszuprobieren!', 38134),
(204604, 'deDE', '', 'Wir bringen des Stab zum Sitz des Primus zurück. Möge er zur Erinnerung an unseren Sieg dienen.', 38134),
(204603, 'deDE', 'Mord! Es hat einen Mord gegeben!', 'Mord! Es hat einen Mord gegeben!', 38134),
(204602, 'deDE', 'War das ein Schmerzens- oder Glücksschrei? Das ist immer so schwer zu unterscheiden...', '', 38134),
(204601, 'deDE', '', 'Es heißt, als der Primus verschwand, wurden die Teile seiner Rüstung in alle Winde verstreut. Wenn es noch mehr gibt, beanspruchen wir sie für unseren Pakt!', 38134),
(204580, 'deDE', '', 'Still. Den Gorm in diese Höhle zu treiben, war nicht gerade leicht.', 38134),
(204579, 'deDE', '', 'Alderyn, da seid Ihr ja. Gut. Die Bestie ist verwundet, aber solange die Panzerung noch intakt ist, kann ich nicht viel mehr ausrichten.', 38134),
(204578, 'deDE', 'Ah, hier muss es sein.', '', 38134),
(204567, 'deDE', 'Das war nicht... mein Schicksal...', 'Das war nicht... mein Schicksal...', 38134),
(204566, 'deDE', 'Maldraxxus... wird unser sein...', 'Maldraxxus... wird unser sein...', 38134),
(204565, 'deDE', 'Das Haus... wird nicht fallen...', 'Das Haus... wird nicht fallen...', 38134),
(204564, 'deDE', 'Wer beendet... meine Forschung?', 'Wer beendet... meine Forschung?', 38134),
(204563, 'deDE', 'Eindringlinge! Streckt sie nieder!', 'Eindringlinge! Streckt sie nieder!', 38134),
(204562, 'deDE', 'Ihr könnt Euch vor unserer Magie nicht verbergen!', 'Unserer Magie entkommt Ihr nicht!', 38134),
(204561, 'deDE', 'Diesen Übertritt bereut Ihr!', 'Diesen Übertritt bereut Ihr!', 38134),
(204560, 'deDE', 'Eure Leiche wird mir gut dienen!', 'Eure Leiche wird mir gut dienen!', 38134),
(204559, 'deDE', '', 'Mein Tod... ändert gar nichts...', 38134),
(204558, 'deDE', '', 'Ihr werdet es erleben!', 38134),
(204557, 'deDE', '', 'Die Werke des Meisters formen die Schattenlande neu!', 38134),
(204556, 'deDE', '', 'Ihr wagt es, mein Ritual zu stören!', 38134),
(204554, 'deDE', 'Willensschwache Wesen sollten nicht meine Zeit vergeuden, doch wenn Ihr darauf besteht, dann zeige ich Euch, was wahre Schmerzen sind.', '', 38134),
(204553, 'deDE', 'Lasst die Kreaturen des Himmels die Verteidigung des Ardenwalds anführen.', '', 38134),
(204552, 'deDE', 'Die Kreaturen des Ardenwalds wirken vielleicht friedlich, aber sie verteidigen ihr Revier mit der Kraft und dem Mut der mächtigsten Champions aller Zeiten. Seid Ihr stark genug für diese Aufgabe?', '', 38134),
(204547, 'deDE', '', 'Qualitätskatalysatoren bescheren Euch vielfältigere und bessere Belohnungen von wiederhergestellten Geistern.\n\nMit genug Platz könnt Ihr mehrere Qualitätskatalysatoren einsetzen.', 38134),
(204543, 'deDE', 'Unsere Rolle in der Vorsehung kann man derzeit nicht sicher bestimmen.\n\nIhr seid diesen Zustand womöglich gewohnt, $uSchlundgänger:Schlundgängerin;, doch für uns ist das ein sehr unvertrautes Gefühl.', '', 38134),
(204542, 'deDE', 'Welch eine wunderschöne Aussicht.\n\nAuch wenn der Schlund der Vorsehung trotzt, ist er doch ein fester Bestandteil dieses Reiches.', '', 38134),
(204541, 'deDE', 'Der Schlund ist voll unbändiger Macht, doch nicht im Dienste der Vorsehung.\n\nSo stellt die Vorsehung uns auf die Probe.', '', 38134),
(204540, 'deDE', 'Wir alle müssen eine Rolle spielen.\n\nDas sagt uns die Vorsehung.\n\nAls wir die Bedeutung unserer Rollen kannten, war das leicht zu glauben. Aber jetzt? Jetzt bin ich mir nicht mehr so sicher.\n\nWir müssen trotzdem weiter auf die Vorsehung vertrauen.\n\nUnser Weg ist die Vorsehung.', '', 38134),
(204539, 'deDE', 'Die Vorsehung kann hin und wieder eine wunderbar besinnliche Perspektive gewähren, nicht wahr?', '', 38134),
(204538, 'deDE', 'Ich frage mich, wo diese Veränderungen hinführen. Die Vorsehung hat uns immer geleitet.\n\nDas legt nahe, dass sie das auch immer noch tut. Ich weiß nur nicht, wie alles ausgehen wird.\n\nAuch ein kleines Rädchen im Getriebe möchte seinen Zweck verstehen.', '', 38134),
(204537, 'deDE', 'Willkommen, $uSchlundgänger:Schlundgängerin;.\n\nNicht alles, was Ihr seht, ist verkäuflich. Wir sammeln auch Dinge.\n\nWir haben so seltsame Kreaturen in Eurer Welt gefunden. Ich habe Freude daran, meine Sammlung zu erweitern.', '', 38134),
(204536, 'deDE', 'Willkommen, $uSchlundgänger:Schlundgängerin;.\n\nDieses Gerät ermöglicht Euch, auf Splitterlager in entfernten Dimensionen zuzugreifen, die diese Welt von Eurer trennen. Anders als unsere bediensteten Freunde da drüben mit ihrer heiligen Vorsehung weiß ich sogar, wie das Ding funktioniert.\n\nAber ich werde es Euch nicht verraten. Seht es als Geschäftsgeheimnis an.\n\nIhr könnt es jedoch benutzen. Mit den besten Wünschen der Mittler.', '', 38134),
(204535, 'deDE', '', 'Bei den fünf Häusern... die Schulterstücke des Primus!', 38134),
(204534, 'deDE', '', 'Bringt die Rüstung des Primus in unser Sanktum, Schlundgänger. Dort bekommt sie den Ehrenplatz, der ihr gebührt.', 38134),
(204533, 'deDE', '', 'Der Lich bedroht unser beider Reiche. Sobald unsere Truppen bereit sind, rufen wir Revendreth, uns in die Schlacht zu folgen.', 38134),
(204532, 'deDE', '', 'Stellt keine Forderungen an mich, gefallener Prinz! Wir müssen mehr über diese Bedrohung erfahren, anstatt blindlings loszustürmen.', 38134),
(204531, 'deDE', 'Kel\'Thuzad wird für seine Taten büßen! Baronin Draka! Ihr müsst mich sofort nach Maldraxxus begleiten!', '', 38134),
(204530, 'deDE', 'Einerlei. Das Haus der Rituale gehorcht mir allein. Dort erwarte ich Euch!', '', 38134),
(204529, 'deDE', 'Anmaßendes Pack! Ihr wagt es, meine Dienerin niederzustrecken?!', '', 38134),
(204528, 'deDE', '', 'Der Lich hat sich einer Quelle großer Macht bedient...', 38134),
(204527, 'deDE', 'Hmpf. Wie es Euch beschämen muss, Euren glorreichen Monarchen in Ketten zu sehen.', '', 38134),
(204526, 'deDE', 'Was macht Ihr hier, bester Kumpel?', '', 38134),
(204516, 'deDE', 'Ich? Natürlich nicht. Aber $uder Schlundgänger:die Schlundgängerin; vielleicht... Das könnte funktionieren.', '', 38134),
(204515, 'deDE', '', 'Die Gräfin? Glaubt Ihr wirklich, dass Ihr sie überzeugen könnt, zu kommen, Lord Garridan?', 38134),
(204514, 'deDE', 'Ich hoffe, er bringt den Aschenbringer mit. Das wäre ein echtes Spektakel!', '', 38134),
(204513, 'deDE', '', 'Oh, Ich kann kaum erwarten, diesen Alexandros Mograine aus Maldraxxus zu treffen...', 38134),
(204506, 'deDE', 'Iiihgor kampfbereit!', '', 38134),
(204503, 'deDE', '', 'Hier an diesem grässlichen Ort sollte man keinen Kampf erwarten, der es wert ist, und doch stehen wir nun hier. Vergeudet nicht meine Zeit.', 38134),
(204500, 'deDE', '', 'Wir folgen den Worten des Primus! Gemeinsam werden wir kämpfen und die Schattenlande retten!', 38134),
(204499, 'deDE', '', 'Ihr habt den wahren Feind von Maldraxxus enttarnt, Schlundgänger. Und nun bringt Ihr diese Schulterstücke wieder an ihren Platz.', 38134),
(204497, 'deDE', 'Habt Dank, $gSchlundgänger:Schlundgängerin;. Es wird mir eine Ehre sein, Prinz Renathals Hof beizuwohnen.', '', 38134),
(204489, 'deDE', 'Skoldushalle', 'Skoldushalle', 38134),
(204484, 'deDE', 'Was möchtet Ihr präsentieren, $n?', 'Was möchtet Ihr präsentieren, $n?', 38134),
(204474, 'deDE', '', 'Ich bewache lediglich den Abgesandten des Hofes, aber von Zeit zu Zeit höre ich von Bedrohungen, die es abzuwenden gilt.', 38134),
(204472, 'deDE', 'Ihr denkt jetzt vielleicht: \"Da steckt ein Gnom in einem seltsamen Schlammklumpen fest. Vielleicht braucht er Hilfe!\" Nee! Ob Ihr\'s glaubt oder nicht, ich bin absichtlich in diesem \"Ding\". Ich bin inkognito, getarnt, verkleidet... Wie auch immer Ihr das nennen wollt. Dieser Sack Schlamm ist mein einziger Schutz an diesem furchtbaren Ort. Zum Glück habe ich ein paar wirklich tolle Tricks für das Training neuer Begleiter hier draußen gefunden, und mein Team besteht jetzt aus echten Champions. Ich erkämpfe mir den Sieg und den Respekt, den ich verdiene! Der einzige Nachteil ist, dass jetzt alles, was ich besitze, klebrig ist. Alles.', '', 38134),
(204471, 'deDE', 'Faulwanst. Reste. Mehr Teile. Ihr kämpft.', '', 38134),
(204470, 'deDE', 'Dutzende von Jahren habe ich auf würdige Gegner gewartet. Dem Sieger die Beute!', '', 38134),
(204469, 'deDE', 'Auch die kleinste Schlacht sollte mit Bedacht und Ehre geschlagen werden. Präsentiert Euer Team, wenn Ihr bereit seid.', '', 38134),
(204466, 'deDE', '', 'Wir nehmen unsere Verteidigung sehr ernst. Ob groß oder klein, wir investieren viel in die Ausbildung und Instandhaltung der Ressourcen, die die Bastion stark machen.', 38134),
(204457, 'deDE', 'Wie überlebt Ihr? Ihr tragt bei gefährlichen Aktivitäten nicht einmal einfache Schutzausrüstung. Hier, nehmt diese.', '', 38134),
(204455, 'deDE', '', 'Unsere Verbündeten warten oben in der Nekropole.', 38134),
(204442, 'deDE', '', 'Die Schwarze Klinge ist bereit.', 38134),
(204441, 'deDE', 'Die Schwarze Klinge ist bereit.', '', 38134),
(204439, 'deDE', 'In Eurer Abwesenheit ist viel geschehen, $C.\n\nIch bringe Euch auf den neuesten Stand.', '', 38134),
(204438, 'deDE', '', 'In Eurer Abwesenheit ist viel geschehen, $C.\n\nIch bringe Euch auf den neuesten Stand.', 38134),
(204435, 'deDE', '', 'Es scheint, dass Eure und unsere Vorsehung miteinander verflochten sind.', 38134),
(204431, 'deDE', '', 'Welch prachtvolle weite Ebene. Einfach wunderbar für einen Kampf. Seid Ihr bereit?', 38134),
(204356, 'deDE', '', 'Die Bestie ist ungeschützt! Schlundgänger, schlagt zu! Wir geben Euch Deckung!', 38134),
(204355, 'deDE', '', 'Ihr quält mich mit der Vision dieses Hordechampions. Um mich zu retten? Oder wolltet Ihr einen Kampf sehen?', 38134),
(204344, 'deDE', '', 'Das dürfte interessant werden...', 38134),
(204338, 'deDE', 'Also gut, wie wäre es denn mit einem von diesen...', '', 38134),
(204320, 'deDE', 'Hier ist vielleicht etwas für mich dabei...', '', 38134),
(204317, 'deDE', 'Bereit zur Rückkehr nach Oribos?', '', 38134),
(204316, 'deDE', 'Ihr habt vielleicht Nerven, Harlowe! Taucht im selben Aufzug wie ich auf.', 'Ihr habt vielleicht Nerven, Harlowe! Taucht im selben Aufzug wie ich auf.', 38134),
(204311, 'deDE', 'Und wenn nicht, sind sie keine echten Fans!', '', 38134),
(204310, 'deDE', 'Aber natürlich, Chiu. Die sind super!', '', 38134),
(204309, 'deDE', '', 'Ich hoffe, sie gefallen unseren Fans...', 38134),
(204308, 'deDE', 'Unsere neuen Poster sind in der Gruft aufgehängt. Chiu hat sie gezeichnet.', '', 38134),
(204307, 'deDE', 'Wir können nur zu Euren Verbündeten zurück und uns neu sammeln.', '', 38134),
(204306, 'deDE', 'Kel\'Thuzad hat sich in die Nekropole zurückgezogen. Wir beide haben jetzt keine Chance gegen ihn.', '', 38134),
(204305, 'deDE', 'Rasch! Vielleicht sind wir zu spät!', '', 38134),
(204304, 'deDE', 'Seht... Kampfspuren, die in die Halle der Markgräfin führen.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204303, 'deDE', 'Ihr habt es geschafft! Ich habe natürlich nie an Euch gezweifelt.', '', 38134),
(204302, 'deDE', 'Ihr müsst vielleicht Köpfchen beweisen, um ihn zu erreichen.', '', 38134),
(204301, 'deDE', 'Klettert auf diese Regale, um zum Schalter zu gelangen.', '', 38134),
(204300, 'deDE', 'Der Schalter ist am Ende des Korridors.', '', 38134),
(204299, 'deDE', 'Eine Hand? Ja... ja! Beim Primus, das ist genial!', '', 38134),
(204298, 'deDE', 'Eine Fackel? Auch wenn viele Missetäter auf diese Art Schalter verbergen, bezweifle ich das in einer Zelle doch sehr.', '', 38134),
(204297, 'deDE', 'Wollt Ihr mit jemandem sprechen, der auf Eurem Niveau ist? Ansonsten hilft uns ein Schädel nicht weiter.', '', 38134),
(204296, 'deDE', 'Meine Magie kann leider keine Ketten manipulieren. Seht, was Ihr sonst noch auftreiben könnt.', '', 38134),
(204295, 'deDE', 'Wachen! Werft diesen Wicht in eine Zelle! Und bringt mir den verräterischen Lich!', '', 38134),
(204294, 'deDE', 'Also steckt Ihr dahinter! Und was haben wir hier... Balmedars Oculus!', '', 38134),
(204293, 'deDE', 'Unmöglich! Wartet... was ist das? Verrat!', '', 38134),
(204292, 'deDE', 'Was... was geschieht hier? Neeein!', '', 38134),
(204291, 'deDE', 'Das Blut des Trägers wird den Weg öffnen!', '', 38134),
(204290, 'deDE', 'Die Barriere wird schwächer! Längst versiegelte Pfade öffnen sich!', '', 38134),
(204289, 'deDE', 'Ja! Ich sehe den Weg! Die Finsternis des Schlunds ruft nach mir!', '', 38134),
(204288, 'deDE', 'Das Ritual beginnt! Öffnet den Animazufluss!', '', 38134),
(204287, 'deDE', 'Hoffen wir um Euretwillen, dass sie nicht tödlich sind.', '', 38134),
(204286, 'deDE', 'Auch wenn das Ritual gewiss fehlschlägt, könnte es einige... Kollateralschäden geben.', '', 38134),
(204285, 'deDE', 'Es war eigentlich nicht vorgesehen, dass Ihr am Ritual teilnehmt. Aber wir werden improvisieren.', '', 38134),
(204284, 'deDE', 'Wie Ihr befehlt, Meister. Es wird sofort geschehen!', '', 38134),
(204283, 'deDE', 'Dann haben wir einen Verräter unter uns. Wir müssen unsere Pläne beschleunigen.', '', 38134),
(204282, 'deDE', 'Es war nirgendwo zu sehen, Meister.', '', 38134),
(204281, 'deDE', 'Verschwendet meine Zeit nicht mit Lakaien! Was ist mit dem Reagenz?', '', 38134),
(204280, 'deDE', '...die Karawane wurde vollständig geplündert, die Mittlereskorte brutal getötet.', '', 38134),
(204279, 'deDE', 'Kommt mit den übrigen Reagenzien zu mir. Ich habe einen Plan.', '', 38134),
(204278, 'deDE', 'Eine mysteriöse Lieferung der Mittler? Sehr faszinierend. Ich muss davon wissen!', '', 38134),
(204277, 'deDE', 'Was der Baron auch anfordert, bringt es erst zu mir. Ich möchte sehen, was er benutzt.', '', 38134),
(204276, 'deDE', 'Vielleicht helfen uns einige dieser Reagenzien, auf den Typ des Rituals zu schließen.', '', 38134),
(204275, 'deDE', 'Haltet an Eurer List fest. Mal sehen, was wir sonst noch aufdecken.', '', 38134),
(204274, 'deDE', 'Trotz unserer Mühen haben wir nichts über die Pläne des Barons herausgefunden.', '', 38134),
(204273, 'deDE', 'Was könnte Kel\'Thuzad damit nur vorhaben?', '', 38134),
(204272, 'deDE', '\"Die Bindung des Verbannten\"? Das ist nun wirklich kaum mehr als ein Geschichtsbuch.', '', 38134),
(204270, 'deDE', 'Was für eine Barriere möchte der Baron wohl errichten... oder umgehen? Seltsam.', '', 38134),
(204268, 'deDE', '\"Größere Schutze und Barrieren, Band IV: Gegenzauber\". Keine leichte Lektüre.', '', 38134),
(204266, 'deDE', 'Hat sein Ritual etwas mit den Pfaden zu tun, die die Reiche des Todes verbinden?', '', 38134),
(204265, 'deDE', '\"Pfade der Ersten\"? Eine seltsame Wahl voller Spekulationen und Halbwahrheiten.', '', 38134),
(204264, 'deDE', 'Was erwartet er in einem solchen Standardwerk zu finden?', '', 38134),
(204263, 'deDE', '\"Geflügeltes Arkanum\". Eine logische Wahl für Forschung zu kyrianischer Mystik.', '', 38134),
(204261, 'deDE', 'Ich verstehe, was den Verschmähten entgeht.\n\nPerfektion ist für den Erfolg unerlässlich! Ihr müsst Eure Werkzeuge und Ressourcen pflegen.', '', 38134),
(204259, 'deDE', 'Allerdings kaum ein Beweis für Verrat. Werfen wir einen Blick in die Folianten, die er sucht.', '', 38134),
(204258, 'deDE', 'Der Baron erbittet also Hilfe von den Venthyr? Nicht unverzeihlich, wenn er wenigstens seine Beute teilt.', '', 38134),
(204257, 'deDE', 'Genug der Grübelei. Es wird Zeit, das Ritual vorzubereiten.', '', 38134),
(204256, 'deDE', 'Und dank der Anima, die unsere Verbündeten in Revendreth sichergestellt haben, werde ich den Weg zum Sieg öffnen.', '', 38134),
(204255, 'deDE', 'Dieser Kyrianer trägt den Schlüssel zu mächtigen Orten in sich.', '', 38134),
(204254, 'deDE', 'Dafür bezahlt Ihr, Lich! Ich bin niemandes Belohnung!', '', 38134),
(204253, 'deDE', 'Darunter ist auch meine Belohnung, ein Kyrianer, der bei ihnen \"Träger\" genannt wird.', '', 38134),
(204252, 'deDE', 'Die Konstrukte sind mit der übrigen Beute unseres Überfalls auf die Bastion zurück.', '', 38134),
(204251, 'deDE', 'Aber daran würdet Ihr nicht einmal denken... nicht wahr?', '', 38134),
(204250, 'deDE', 'Falls Ihr vorhaben solltet, mich zu hintergehen, werde ich dafür sorgen, dass die Markgräfin die Identität des Mörders erfährt.', '', 38134),
(204249, 'deDE', 'Eine kleine Anmerkung: Der Akolyth, den Ihr so mutwillig erschlagen habt, war auch der Lieblingsgefährte von Markgräfin Sin\'dane.', '', 38134),
(204248, 'deDE', 'Zufriedenstellend. Kommt in die Halle der Klingen, dort überprüfe ich die Qualität Eurer Verkleidung.', '', 38134),
(204247, 'deDE', 'Die nächste Aufgabe wird schwerer. Ihr mögt doch Herausforderungen?', '', 38134),
(204246, 'deDE', 'Gut, Ihr habt mein kleines \"Hilfspaket\" gefunden. Dann seid Ihr nicht völlig nutzlos.', '', 38134),
(204245, 'deDE', 'Erzählt niemandem von dieser Absprache.', '', 38134),
(204244, 'deDE', 'Der Turm steht nahe dem Skapliagrat. Dort findet Ihr alles Nötige, um weiterzukommen.', '', 38134),
(204242, 'deDE', 'Ich frage mich, für wen wir spielen werden?', '', 38134),
(204241, 'deDE', '', 'Mit diesen Instrumenten fühle ich mich wieder wie ich selbst...', 38134),
(204240, 'deDE', 'Endlich!', '', 38134),
(204239, 'deDE', 'Genug der Besinnung. Die Proben können beginnen!', '', 38134),
(204238, 'deDE', 'Gute Neuigkeiten, Leute! Ich habe unser nächstes Konzert gebucht... und das wird GROSS!', '', 38134),
(204237, 'deDE', '', 'In meinem sterblichen Leben konnte ich nie lange an einem Ort bleiben.\n\nJeden Tag wartete auf mich ein neues Abenteuer, das ich erleben wollte.\n\nDie gleiche Reiselust spüre ich bei Euch.\n\nAlso sagt mir ehrlich... Glaubt Ihr an die Theorie von Verwalter Tedo? Kann Musik die Seele retten?\n\nIch bin mir da noch nicht sicher.', 38134),
(204236, 'deDE', 'Ansel macht sich zu viele Sorgen. Es wird alles gut.\n\nWie meine Asha zu sagen pflegte: \"Hinter jeder überwundenen Dünenkuppe bietet sich die Möglichkeit auf Wasser\".\n\nDie Musik wird uns retten. Das war schon immer so.', '', 38134),
(204235, 'deDE', 'Habt Ihr schon mal davon gehört, dass Musik Seelennahrung sein soll?\n\nDas ist natürlich eine Metapher...\n\n<Verwalter Tedo schaut Euch fest an.>\n\nAber was, wenn es mehr als das wäre?\n\nWas, wenn es für einige, die sich in ihrem sterblichen Leben zur Musik hingezogen fühlten, eine Erlösung sein könnte?\n\nWas, wenn Musik wirklich die Seele heilen kann?\n\nIch glaube fest daran, dass das wahr ist.', '', 38134),
(204233, 'deDE', '', 'Jetzt, wo das Konservatorium etwas weiter wiederhergestellt wurde, habt Ihr Zugriff auf weitere Wildsamen und die Katalysatorfelder.\n\nMithilfe der Katalysatorfelder könnt Ihr die Wildsamen noch weiter stärken, bevor Ihr darin einen Geist platziert.\n\nWenn Ihr Katalysatoren pflanzt, seht Ihr, mit welchen Wildsamen sie verbunden sind.', 38134),
(204232, 'deDE', 'Wie sollen wir unsere Betreuer mit Musik beeindrucken, wenn wir immer unsere Instrumente verlieren?\n\n<Glückspilz Ansel schaut auf die Sündensteine draußen.>\n\nWir kommen hier nie wieder raus.', '', 38134),
(204231, 'deDE', '', 'Zurzeit gibt es nur zwei Wildsamen, die wiederhergestellt wurden. Alles, was ihr momentan also tun könnt, ist, sie zu verwenden, um die Geister wieder mit Stärke zu versehen.\n\nJe weiter Ihr dabei vorankommt, diesem Garten zu einstiger Pracht zu verhelfen, desto mehr Potential wird aktiviert.\n\nFragt mich noch einmal, wenn Ihr das Konservatorium weiter wiederhergestellt habt.', 38134),
(204229, 'deDE', '', 'Willkommen, $n. Wie kann ich Euch heute behilflich sein?', 38134),
(204228, 'deDE', '', 'Willkommen, $n. Wenn Ihr es wiederhergestellt habt, könnt Ihr das Konservatorium der Königin durch dieses Pilzportal betreten.\n\nSprecht mit Zayhad, um alle Dinge zu sehen, die wir für die Wiederherstellung benötigen.', 38134),
(204227, 'deDE', 'Weißmähne wird Euch von dem finsteren Schicksal unserer Verbündeten erzählen.', '', 38134),
(204226, 'deDE', 'Nazgrim wird Euch von dem finsteren Schicksal unserer Verbündeten erzählen.', '', 38134),
(204225, 'deDE', 'Hochlord Bolvar Fordragon hat sich von seinem Kampf mit Sylvanas Windläufer erholt. Er und seine Verbündeten erwarten uns.', '', 38134),
(204224, 'deDE', 'Wir haben angeordnet, dass Acherus vor die Eiskronenzitadelle verlegt wird. Lasst mich ein Schwarzes Tor für uns öffnen.', '', 38134),
(204223, 'deDE', 'Die Vorbereitungen wurden getroffen.', '', 38134),
(204222, 'deDE', 'Die Schwarze Klinge steht bereit. Ich erwarte Euch in Orgrimmar. Kommt, bevor die eisige Hand des Todes ganz Azeroth umklammert.', '', 38134),
(204221, 'deDE', 'Der Schleier zwischen unserer Welt und den Schattenlanden wurde zerrissen.', '', 38134),
(204220, 'deDE', 'Die Schwarze Klinge steht bereit. Ich erwarte Euch in Sturmwind. Kommt, bevor die eisige Hand des Todes ganz Azeroth umklammert.', '', 38134),
(204219, 'deDE', 'Der Schleier zwischen unserer Welt und den Schattenlanden wurde zerrissen.', '', 38134),
(204217, 'deDE', 'Schwächt ihn, damit ich sie mit dem Stab befreien kann!', '', 38134),
(204216, 'deDE', 'Kel\'Thuzad entzieht Markgräfin Sin\'dane ihre Macht!', '', 38134),
(204215, 'deDE', 'Diener! Vernichtet die Eindringlinge!', '', 38134),
(204211, 'deDE', '<Die Truhe schwingt knarrend auf und enthüllt verschiedene Ausstattungen und Accessoires.>', '', 38134),
(204210, 'deDE', '<Näherin Rogana inspiziert Euch eingehend.>\n\nWas für... abenteuerliche Festkleidung.\n\nIch bin mir sicher, dass Ihr etwas Passenderes in meiner Truhe findet.', '<Näherin Rogana inspiziert Euch eingehend.>\n\nWas für... abenteuerliche Festkleidung.\n\nIch bin mir sicher, dass Ihr etwas Passenderes in meiner Truhe findet.', 38134),
(204200, 'deDE', 'Für $geinen Sterblichen:eine Sterbliche; seid Ihr sehr weise, $p.', 'Für $geinen Sterblichen:eine Sterbliche; seid Ihr sehr weise, $p.', 38134),
(204199, 'deDE', 'Verkneift es Euch!', 'Verkneift es Euch!', 38134),
(204198, 'deDE', 'Manchmal glaube ich, Harlowe redet nur, um seine eigene Stimme zu hören!', 'Manchmal glaube ich, Harlowe redet nur, um seine eigene Stimme zu hören!', 38134),
(204197, 'deDE', 'Da habt Ihr wohl recht. Fredrik kann sein unglückliches Aussehen nicht leugnen.', 'Da habt Ihr wohl recht. Fredrik kann sein unglückliches Aussehen nicht leugnen.', 38134),
(204196, 'deDE', 'Fredrik ist nur neidisch, dass ich besser in dem Anzug aussehe als er. Das Leben nach dem Tod tut seiner Bauchgegend nicht gut.', 'Fredrik ist nur neidisch, dass ich besser in dem Anzug aussehe als er. Das Leben nach dem Tod tut seiner Bauchgegend nicht gut.', 38134),
(204194, 'deDE', 'Habt Ihr gesehen, was Harlowe trägt? Wie unoriginell. Ich sollte Ihm diese Klamotten glatt runterreißen!', 'Habt Ihr gesehen, was Harlowe trägt? Wie unoriginell. Ich sollte Ihm diese Klamotten glatt runterreißen!', 38134),
(204192, 'deDE', 'N\'Abend. Der neue Boss sagt mir, ich soll herkommen und rausfinden, was wir mit neu ankommender Anima machen können.\n\nKriegen wir genug davon, können wir die meisten Mauern, die von der Seite von Revendreth dauernd wegbrechen, wieder aufbauen.', '', 38134),
(204187, 'deDE', 'Was Ihr auch tut, Ihr zögert das Unvermeidliche nur hinaus.', '', 38134),
(204186, 'deDE', 'Ihr haltet Euch für besonders talentiert. Euch werde ich noch Demut beibringen.', '', 38134),
(204185, 'deDE', 'Vyraz hat mich im Schlund in die Falle gelockt. Und zweifellos war es seine Hand, durch die unser Markgraf gefallen ist.', '', 38134),
(204182, 'deDE', 'So erschöpfend vorhersehbar. Andererseits dürfte Scheitern für Euresgleichen zweite Natur sein.', '', 38134),
(204176, 'deDE', 'Ihr wollt fliegen? Ja?', 'Ihr wollt fliegen? Ja?', 38134),
(204175, 'deDE', 'Einigermaßen beeindruckend. Dennoch, ich freue ich mich darauf, Euch auszulöschen.', '', 38134),
(204174, 'deDE', '', 'Es wäre Wahnsinn, ihnen jetzt zu folgen. Konzentriert Euch. Für Theatralik ist später Zeit.', 38134),
(204169, 'deDE', 'Ah ja, Ihr habt die Steinziegel. Was soll ich machen?', '', 38134),
(204164, 'deDE', 'Das Zerschmettern des Helms hat Bolvar Fordragon von der Bürde, Lichkönig zu sein, befreit. Jetzt genießt er dieselbe Freiheit wie wir.', '', 38134),
(204160, 'deDE', 'Ich glaube, unsere vorderen Tore brauchen eine neue Statue.\n\nWas meint Ihr, was für eine?', '', 38134),
(204155, 'deDE', 'Bitte! Benutzt die Flöte in der Nähe des Gormlingbaus, dann kommen sie aus ihrem Versteck!', '', 38134),
(204138, 'deDE', 'Mögen die Geister meiner Ahnen sicher in Frieden ruhen.', '', 38134),
(204137, 'deDE', 'Ich kenne nur die dunkelsten Tiefen der Schattenlande. Gut zu wissen, dass es hier auch hoffnungsvolle Orte gibt.', '', 38134),
(204133, 'deDE', '', 'Thrall, wisst Ihr, wohin sie Anduin gebracht haben?', 38134),
(204132, 'deDE', 'Meine Freunde!', '', 38134),
(204125, 'deDE', 'Man sagt, ein Boss is\' ein Boss wie jeder andere Boss, aber kein Boss is\' ein Boss, der weniger auf Peitschenhiebe aus ist als der letzte Boss.\n\nDas gilt aber nicht für den Prinzen. Der is\' schon irgendwie in Ordnung, auch wenn er ein Blutsauger ist und so. Steht mit beiden Beinen auf\'m Boden.', '', 38134),
(204124, 'deDE', 'Ich habe im Laufe der Äonen auf vielen Schlachtfeldern in vielen Reichen gestanden.\n\nIn all diesen Konflikten hat sich nichts ehrenhafter angefühlt, als an der Seite von Prinz Renathal, General Draven und Euch zu dienen.\n\nWir kämpfen für die Integrität unserer Realität gegen unvorstellbare Kräfte. Dafür wurde ich wahrhaftig geschaffen.', '', 38134),
(204123, 'deDE', 'Wenn es Prinz Renathal ernst damit ist, so viel verlorenes Territorium zurückzugewinnen, dann brauchen wir mehr Steingeborene, die sich für seine Sache einsetzen.\n\nEs wäre so viel einfacher gewesen, wenn er einfach den Steinmetz überzeugt hätte, sich uns anzuschließen. So, wie es jetzt aussieht, müssen wir nur bestimmte Steingeborene animieren, damit wir nicht noch mehr Kräfte wecken, die unsere Feinde gegen uns einsetzen können.\n\nGlücklicherweise habt Ihr mich, um Euch zu sagen, welche von ihnen uns gegenüber loyal sind.', 'Wenn es Prinz Renathal ernst damit ist, so viel verlorenes Territorium zurückzugewinnen, dann brauchen wir mehr Steingeborene, die sich für seine Sache einsetzen.\n\nEs wäre so viel einfacher gewesen, wenn er einfach den Steinmetz überzeugt hätte, sich uns anzuschließen. So, wie es jetzt aussieht, müssen wir nur bestimmte Steingeborene animieren, damit wir nicht noch mehr Kräfte wecken, die unsere Feinde gegen uns einsetzen können.\n\nGlücklicherweise habt Ihr mich, um Euch zu sagen, welche von ihnen uns gegenüber loyal sind.', 38134),
(204122, 'deDE', 'Demzufolge werde ich alle potenziellen Vergeltungspläne aus dem Protokoll streichen.', 'Demzufolge werde ich alle potenziellen Vergeltungspläne aus dem Protokoll streichen.', 38134),
(204114, 'deDE', 'Meinem Protokoll zufolge habt Ihr alle gestohlenen Andenken geborgen.', 'Meinem Protokoll zufolge habt Ihr alle gestohlenen Andenken geborgen.', 38134),
(204110, 'deDE', '<Das war kein Unfall!\n\nOberst Morts Verletzungen legen nahe, dass sein Tod schnell und schmerzhaft kam.\n\nKeine Anzeichen eines Kampfs, und keiner der Zeugen hat einen Attentäter gesehen.\n\nWer auch immer hinter diesem grausamen Mord steckt, befindet sich noch unter den Gästen.>', '', 38134),
(204108, 'deDE', 'Markgraf Gharmal wird heute sterben.', '', 38134),
(204107, 'deDE', 'Viele Aspiranten, wenig Saft! Kommt später wieder, ja?', 'Viele Aspiranten, wenig Saft! Kommt später wieder, ja?', 38134),
(204104, 'deDE', 'Huh! Getränk für Euch! Nehmt! Nehmt!', 'Huh! Getränk für Euch! Nehmt! Nehmt!', 38134),
(204103, 'deDE', '<Der Provost bietet Euch sein Tablett mit einem sanften \"Huu\" an.>', '<Die Provostin bietet Euch ihr Tablet mit einem sanften \"Huu\" an.>', 38134),
(204100, 'deDE', 'Aspirant durstig? Provost zur Stelle!', 'Aspirant durstig? Provost zur Stelle!', 38134),
(204099, 'deDE', 'Trinken! Trinken! Trinken?', 'Trinken! Trinken! Trinken?', 38134),
(204097, 'deDE', 'Einen Schluck? Sehr lecker!', 'Einen Schluck? Sehr lecker!', 38134),
(204081, 'deDE', '', 'Guten Tag, $gmein Freund:meine Liebe;!\n\nEs gibt noch so viel zu tun.\n\nWenn Ihr etwas Zeit habt, wäre Eure Unterstützung sehr willkommen.', 38134),
(204080, 'deDE', 'Wenn wir damit zu Markgräfin Sin\'dane zurückkehren, halten wir Kel\'Thuzad vielleicht noch auf.', '', 38134),
(204079, 'deDE', 'Moment... Der Stab des Primus! Er ist noch intakt!', '', 38134),
(204078, 'deDE', '', 'Geht zum Ring der Übertragung und öffnet ein Portal zum Ardenwald. Möge die Vorsehung Euch leiten, sterbliche Seele.', 38134),
(204077, 'deDE', '', 'Die Haine des Ardenwalds benötigen Anima, um die schlummernden Naturgeister zu nähren. Ich hoffe, dass die Vorräte der Königin die Dürre überstanden haben.', 38134),
(204076, 'deDE', '', 'Die Winterkönigin war eine enge Verbündete des Primus. Sie wird diese Warnung sicher beherzigen.', 38134),
(204075, 'deDE', 'Gemeinsam können wir tief ins Reich des Kerkermeisters blicken. Doch Vorsicht. Verliert Euch nicht im Dunkel.', '', 38134),
(204074, 'deDE', 'Im zerbrochenen Helm wohnt noch immer die Macht des Lichkönigs. Die Macht... des Schlunds.', '', 38134),
(204073, 'deDE', '', 'Graf Denathrius herrscht über Revendreth. Ich bete, dass er die Animadürre gut überstanden hat. Wir brauchen seine Hilfe dringend.', 38134),
(204072, 'deDE', '', 'Es ist das Ziel hochmütiger, arroganter Seelen, in denen die Richterin einen Funken Potenzial sieht. Die Venthyr führen diese Seelen auf den Pfad zur Erlösung.', 38134),
(204071, 'deDE', '', 'Revendreth ist ein unheimliches Reich voll düsterer Türme und kaltem Stein. Aber Land und Bewohner dienen einem edlen Zweck.', 38134),
(204070, 'deDE', '', 'Ohne die Macht von Maldraxxus besteht für uns leider keine große Hoffnung, den Verbannten aufzuhalten.', 38134),
(204069, 'deDE', '', 'Ihr müsst ihre Gründe für den Angriff auf die Kyrianer erfahren. Verhindert um jeden Preis, dass dieser Konflikt weiter eskaliert.', 38134),
(204068, 'deDE', 'Sie hat nicht überlebt...', '', 38134),
(204067, 'deDE', '', 'Gweyir! Nein...', 38134),
(204066, 'deDE', '', 'Bleibt bei uns, Gweyir! Der Fluch ist fast gebrochen!', 38134),
(204065, 'deDE', '', 'Weiter so! Die Umklammerung der Drust lässt nach!', 38134),
(204064, 'deDE', '', 'Zerstört diese Dinger, schnell!', 38134),
(204063, 'deDE', '', 'Macht Euch bereit. Gorak Zhars Magie lässt sich nicht einfach bereinigen.', 38134),
(204062, 'deDE', '', 'Ich brauche ein kleines, schnelles und leises Konstrukt.\n\nMeine Feinde sehen mich selten, aber sie fürchten stets meine Präsenz.', 38134),
(204060, 'deDE', 'Ich wusste, Ihr würdet meine Hilfe brauchen.', '', 38134),
(204059, 'deDE', 'Ich werde ein starkes Konstrukt mit Eisenhaut benötigen.\n\nDamit kann ich jedes Problem lösen, das Euch stört.', '', 38134),
(204058, 'deDE', 'Lord Garridan hat nicht übertrieben. Ihr veranstaltet einen würdigen Hof!', 'Lord Garridan hat nicht übertrieben. Ihr veranstaltet einen würdigen Hof!', 38134),
(204057, 'deDE', 'Wo ist Stefan der Wählerische? Er hat mir eine Privatverkostung versprochen.', 'Wo ist Stefan der Wählerische? Er hat mir eine Privatverkostung versprochen.', 38134),
(204056, 'deDE', 'Ich bin froh, dass Theotar aus dem Exil zurückgekehrt ist. Er ist so geistreich!', 'Ich bin froh, dass Theotar aus dem Exil zurückgekehrt ist. Er ist so geistreich!', 38134),
(204055, 'deDE', 'Habt Ihr irgendwo ein extragroßes Getränk herumstehen sehen?', 'Habt Ihr irgendwo ein extragroßes Getränk herumstehen sehen?', 38134),
(204054, 'deDE', 'Ich hoffe dieser Hof endet niemals!', 'Ich hoffe dieser Hof endet niemals!', 38134),
(204053, 'deDE', 'Ich hab mich an Eure Berühmtheit da drüben herangemacht... Gern geschehen!', 'Ich hab mich an Eure Berühmtheit da drüben herangemacht... Gern geschehen!', 38134),
(204052, 'deDE', 'Ich amüsiere mich prächtig!', 'Ich amüsiere mich prächtig!', 38134),
(204051, 'deDE', 'Ich muss zugeben, Ihr veranstaltet hier einen faszinierenden Hof.', 'Ich muss zugeben, Ihr veranstaltet hier einen faszinierenden Hof.', 38134),
(204014, 'deDE', 'Ich kann es kaum erwarten, herauszufinden, ob die Gerüchte über den Gluthof wahr sind!', 'Ich kann es kaum erwarten, herauszufinden, ob die Gerüchte über den Gluthof wahr sind!', 38134),
(204013, 'deDE', 'In Dunkelhafen werden ja alle so neidisch sein!', 'In Dunkelhafen werden ja alle so neidisch sein!', 38134),
(204012, 'deDE', 'Wie viele berühmte Leute ich heute wohl treffen werde?', 'Wie viele berühmte Leute ich heute wohl treffen werde?', 38134),
(204011, 'deDE', 'Ganz unter uns... Ich kann es kaum glauben, dass ich eine Einladung ergattern konnte... Ich bin ja so aufgeregt!', 'Ganz unter uns... Ich kann es kaum glauben, dass ich eine Einladung ergattern konnte... Ich bin ja so aufgeregt!', 38134),
(204010, 'deDE', 'Von diesem Hof wird die ganze Stadt sprechen!', 'Von diesem Hof wird die ganze Stadt sprechen!', 38134),
(204009, 'deDE', 'Was haltet Ihr von meinen Stiefeln? Ich habe für diesen Anlass extra lange Dornen anbringen lassen!', 'Was haltet Ihr von meinem Kleid? Ich habe es mir extra für diesen Anlass aufgehoben.', 38134),
(204008, 'deDE', 'Ich kann kaum erwarten, dass das Tor sich öffnet!', 'Ich kann kaum erwarten, dass das Tor sich öffnet!', 38134),
(204007, 'deDE', 'Ich bitte Euch um einen kleinen Tipp, $gSchlundgänger:Schlundgängerin;! Welche neuen, spannenden Dinge erwarten uns heute beim Hof?', 'Ich bitte Euch um einen kleinen Tipp, $gSchlundgänger:Schlundgängerin;! Welche neuen, spannenden Dinge erwarten uns heute beim Hof?', 38134),
(204005, 'deDE', 'Ara\'lon... ich werde sofort einen Suchtrupp losschicken. Wir überlassen ihn nicht den Drust.', '', 38134),
(204003, 'deDE', '', 'Ara\'lon ist noch nicht mit dem Beutel zurück. Ich fürchte, er ist in Gefahr, oder Schlimmeres.', 38134),
(204002, 'deDE', '', 'Trefft mich am Herz des Waldes, um das Ritual abzuschließen. Gute Jagd, Euch beiden.', 38134),
(204000, 'deDE', '', 'Er bevorzugt förmliche Höfe, also solltet Ihr Eure gesamte Aufmerksamkeit auf Förmlichkeiten richten.', 38134),
(203999, 'deDE', 'Wir haben Grufthüter Kassirs Rückmeldung erhalten, und er wird teilnehmen.', '', 38134),
(203998, 'deDE', 'Ohne ihn komme ich nicht zurück. Ihr habt mein Wort!', '', 38134),
(203997, 'deDE', '', '$p, Ihr sammelt den Nachtwurz. Ara\'lon, wir brauchen einen Hexenbeutel. Den Drust einen stehlen wird gefährlich sein.', 38134),
(203996, 'deDE', 'Sprecht mit dem Personal bezüglich der Verträge und Rückmeldungen und ich kümmere mich um den Rest.', '', 38134),
(203995, 'deDE', 'Ich kenne unseren ersten Gast sehr, sehr gut, also weiß ich genau, welche Art Hof ihm gefallen würde!', '', 38134),
(203993, 'deDE', 'Wir werden mit seinen Gräbern sprechen müssen. Sie scheinen immer einen weiteren Kessel auf dem Feuer zu haben.', '', 38134),
(203992, 'deDE', 'Ich fürchte, dass Theotar während der Proben unseren gesamten Teevorrat ausgetrunken hat.', '', 38134),
(203990, 'deDE', 'Gresit macht das für uns klar. Ich markier seine Position auf Eurer Karte.', '', 38134),
(203989, 'deDE', 'Wenn Ihr nicht wisst, wo Ihr anfangen sollt, kann Euch meine Unterhaltungsliste verraten, wo Ihr hingehen müsst.', '', 38134),
(203988, 'deDE', 'Ihr helft der Anklägerin und ihren Ritualisten mit allem, was sie brauchen, und die helfen dann uns. Alles klar?', '', 38134),
(203987, 'deDE', 'Niemand hält bessere Rituale ab als die Eingeschworenen in den Hallen der Sühne.', '', 38134),
(203984, 'deDE', 'Ein klarer Kopf gewährleistet, dass nur reife Seelen nach Oribos kommen. Gut gemacht.', '', 38134),
(203983, 'deDE', 'Ein Beobachter muss entscheiden, ob eine Seele für die Schattenlande bereit ist. Schärft Eure Fähigkeiten mit dieser Prüfung.', '', 38134),
(203981, 'deDE', '', 'Gerade noch rechtzeitig! Fast wärt Ihr beiden zu spät gewesen...', 38134),
(203980, 'deDE', 'Wenn Ihr nach Kivarr sucht, die haben sie gerade weggeschleppt!', '', 38134),
(203979, 'deDE', '', 'Mein Käfig hat drei separate Schlösser mit je einem Schlüssel. Ich glaube, die Drust fürchten mich.', 38134),
(203974, 'deDE', 'Sucht den Hain ab. Die Drust können nicht weit mit ihr gekommen sein.', '', 38134),
(203973, 'deDE', 'Kivarr ist nicht hier... aber Spuren eines Kampfes. Ihr muss etwas zugestoßen sein!', '', 38134),
(203970, 'deDE', 'Ich habe nicht vor, viel zu kämpfen, aber mit leerem Magen kann man keinen Krieg führen.\n\nIch will ein Konstrukt mit einem guten Kopf, starken Händen und vielen Mündern, um meine Kochkünste zu testen.', '', 38134),
(203969, 'deDE', '', 'Ich will zwei riesige Klingen als Hände. Dann rutscht dem Gegner sofort das Herz in die Hose!\n\nDas wird fantastisch... stimmt\'s?', 38134),
(203968, 'deDE', '', 'Ich bin es leid, ein Fleischschild zu sein und Kopf voran in zwanzig Gegner auf einmal zu rammen!\n\nIch wollte schon immer Zauber schleudern und mit Magie durch Gegner pflügen.\n\nBaut mir ein Konstrukt, das das kann!', 38134),
(203966, 'deDE', 'Auserwählte! Der Feind nähert sich! Eine Schlacht steht uns bevor, und der Sieg wird unser sein!', '', 38134),
(203965, 'deDE', 'Einverstanden. Prüft sie. Dann trefft mich in der Nekropole. Es gilt, einen Krieg zu führen.', '', 38134),
(203964, 'deDE', 'Seid gegrüßt, $uReisender:Reisende;.\n\nIch erforsche uralte Relikte hier in den Schattenlanden.\n\nMöchtet Ihr Euch mir anschließen?', 'Seid gegrüßt, $uReisender:Reisende;.\n\nIch erforsche uralte Relikte hier in den Schattenlanden.\n\nMöchtet Ihr Euch mir anschließen?', 38134),
(203961, 'deDE', 'Archäologie? Was ist denn das?\n\n<Er lauscht Eurer Erklärung zu Abläufen und Sinn der Archäologie.>\n\nFaszinierend, aber ich denke nicht, dass wir hier so etwas haben.\n\nEuch Sterblichen mag die Zeit Geheimnisse entreißen, doch hier funktioniert das etwas anders.\n\nSollte sich daran jedoch etwas ändern, sage ich Euch Bescheid.', 'Archäologie? Was ist denn das?\n\n<Er lauscht Eurer Erklärung zu Abläufen und Sinn der Archäologie.>\n\nFaszinierend, aber ich denke nicht, dass wir hier so etwas haben.\n\nEuch Sterblichen mag die Zeit Geheimnisse entreißen, doch hier funktioniert das etwas anders.\n\nSollte sich daran jedoch etwas ändern, sage ich Euch Bescheid.', 38134),
(203960, 'deDE', '', 'Wir haben beide viel von ihm gelernt. Und gemeinsam werden wir ihn rächen.', 38134),
(203959, 'deDE', 'Ich bin erfreut, dass Ihr Krexus so gut dient. Selbst als Ihr noch Akarek gedient habt, hat er Euren Mut bewundert.', '', 38134),
(203958, 'deDE', 'Er hat meine Kraft in unmögliche Siege verwandelt. Für diesen Verrat reiße ich Vyraz sein schwarzes Herz heraus.', '', 38134),
(203957, 'deDE', '', 'Krexus hat viel Lob für Euch übrig, Aschenbringer. Und das ist selten.', 38134),
(203956, 'deDE', '', 'Überleben ist die Tugend der Schwachen. Entweder Ihr siegt... oder sterbt.', 38134),
(203955, 'deDE', '', 'Der Markgraf hat mir eine neue Aufgabe anvertraut, Vashj. Wir überleben nur, wenn wir die Vergangenheit ruhen lassen.', 38134),
(203948, 'deDE', '', 'Habt Ihr Euch deshalb hinter dem Banner der Auserwählten versteckt als die anderen Augen niedergemetzelt wurden?', 38134),
(203947, 'deDE', '', 'Ich habe Markgraf Akarek nicht verraten. Ich wurde auf eine Mission geschickt, bevor unser Haus fiel. So wie Ihr.', 38134),
(203944, 'deDE', '', 'Na, was haben wir denn hier? Eins von Kel\'Thuzads Haustierchen...', 38134),
(203943, 'deDE', 'Nein? Schade. Doch erlaubt mir, Euch ein... Abschiedsgeschenk zu geben.', '', 38134),
(203942, 'deDE', 'Ihr seid also entkommen? Wollt Ihr meinen Sieg feiern?', '', 38134),
(203941, 'deDE', 'Mag sein. Doch das ist eben der Preis der Macht.', '', 38134),
(203940, 'deDE', '', 'Das werdet Ihr büßen!', 38134),
(203939, 'deDE', '', 'Eure Schwäche ist des Hauses der Rituale unwürdig, Sin\'dane. Es wird Zeit, einen neuen Markgrafen zu salben.', 38134),
(203938, 'deDE', '', 'Baronin?! Ihr wagt es?!', 38134),
(203937, 'deDE', 'Sieht aus wie \'ne neue Jagd für Vol\'jin... sobald ich ausgeruht bin.', '', 38134),
(203936, 'deDE', 'Allein? Nein.', '', 38134),
(203935, 'deDE', '', 'Was soll dieser Verrat?! Denkt Ihr etwa, Ihr wärt der Macht einer Markgräfin gewachsen?', 38134),
(203934, 'deDE', '', 'Ich habe von Eurer eitlen und herzlosen Königin gehört. Ihr zwei scheint Euch sehr ähnlich zu sein... in Gestalt und Wesen.', 38134),
(203933, 'deDE', '', 'Königin Azsharas Pläne brachten uns ungeahnte Macht. Die Zerschlagung war ein geringer Preis für unseren Aufstieg.', 38134),
(203931, 'deDE', '', 'Ihr wisst viel über gefallene Welten, Vashj. Und über Pakte mit Dämonenabschaum.', 38134),
(203930, 'deDE', '', 'Ich habe Eure Welt mit eigenen Augen gesehen, Draka. Oder zumindest ihre zerschmetterten Überreste.', 38134),
(203929, 'deDE', '', 'Sie haben Widerstandkraft und Stärke bewiesen. Genau die brauchen wir jetzt.', 38134),
(203928, 'deDE', 'Eine waghalsige Flucht!', '', 38134),
(203927, 'deDE', 'Man erntet, was man sät.', '', 38134),
(203926, 'deDE', 'Lasst Euch davon nicht aufhalten.', '', 38134),
(203924, 'deDE', 'Da steckt jemand fest!', '', 38134),
(203922, 'deDE', 'Gut, dass Ihr die sterbliche Seele herausgeholt habt. Unsere Feinde dürfen sie nicht haben.', '', 38134),
(203921, 'deDE', 'Erliegt niemals dem Übermut. Bleibt stets konzentriert!', '', 38134),
(203920, 'deDE', 'An der Seite der Arena geht es tief nach unten!', '', 38134),
(203919, 'deDE', 'Der Fluch der Drust wird Gweyirs Willen bald brechen. Es ist zu riskant, wenn sie hier bleibt!', '', 38134),
(203918, 'deDE', '', 'Ich werde Baronin Vashj berichten. Es ist mir eine Ehre, mit Euch zu dienen.', 38134),
(203914, 'deDE', 'Gweyir! Sie atmet noch! Aber... was haben sie ihr angetan?', '', 38134),
(203911, 'deDE', 'Nera war die wildeste von uns. Sie hätte sich den Drust nie ergeben.', '', 38134),
(203910, 'deDE', 'Taeras... Nacht umarme Euch, alter Freund.', '', 38134),
(203909, 'deDE', '', 'Das erste Portal von vielen. Ein würdiger Sieg.', 38134),
(203908, 'deDE', 'Azelor... das habe ich befürchtet. Er hatte Besseres verdient...', '', 38134),
(203903, 'deDE', 'Der ganze Hain ist besessen! Seht zu, dass Ihr nicht auffliegt.', '', 38134),
(203902, 'deDE', 'Hier drüben! Ich brauche Hilfe...', '', 38134),
(203901, 'deDE', 'Zzz... Zurückkehren... Treshala... Darnassus... Zzzz...', '', 38134),
(203899, 'deDE', 'Wir müssen alle noch viel lernen, Uther. Reden wir in meinem Tempel weiter.', '', 38134),
(203896, 'deDE', 'Wir müssen verstehen, was ihn so schmerzt. Vermutlich wissen wir vieles noch nicht.', '', 38134),
(203895, 'deDE', 'Ich bitte Euch, unterstellt Uther meiner Obhut.', '', 38134),
(203894, 'deDE', '', 'Jetzt, da wir die Nekropole kontrollieren, können wir ein Portal zum Sitz des Primus öffnen.', 38134),
(203893, 'deDE', 'Wir brauchen ihn auf dem Schlachtfeld. Ihr müsst ihm helfen, das zu überwinden!', '', 38134),
(203892, 'deDE', 'Pelagos wollte Euch die Sammler als Unterstützung organisieren, aber die bevorstehende Schlacht hat ihn zweifeln lassen.', '', 38134),
(203891, 'deDE', '', 'In diesem Zustand hilft sie uns nicht. Stellt die Vesperglocken wieder her und stabilisiert sie!', 38134),
(203889, 'deDE', 'Ich verleihe Euch nun diese Macht. Verteidiger von Maldraxxus!', '', 38134),
(203885, 'deDE', '', 'Ihre Pein ist einfach köstlich!', 38134),
(203884, 'deDE', '', 'Aalen wir uns in ihrer Qual!', 38134),
(203883, 'deDE', 'Meine Herrschaft währt ewig!', '', 38134),
(203882, 'deDE', 'Ihr werdet alle zu meinen Füßen sterben!', '', 38134),
(203881, 'deDE', 'Niemand wird überleben!', '', 38134),
(203865, 'deDE', '', 'Jetzt!', 38134),
(203864, 'deDE', '', 'Fühlt unseren Stich! Bleibt nur noch eine Aufgabe.', 38134),
(203863, 'deDE', '', 'Bleibt wachsam! Spione sind unter uns!', 38134),
(203858, 'deDE', 'Unser Feind hält an seiner Überzeugung fest. Wenn wir ihn mit Worten nicht erreichen, müssen wir Taten sprechen lassen. Diese unglückliche Aufgabe überlasse ich Euch.', '', 38134),
(203857, 'deDE', 'Mit der Zeit werden sie ihren Irrtum einsehen. Ich hoffe, sie haben heute eine wertvolle Lektion gelernt.', '', 38134),
(203852, 'deDE', '', 'Unsere Taten machen uns zu Vorbildern für die gesamten Schattenlande. Gut gemacht.', 38134),
(203851, 'deDE', '', 'Wir rufen Euch an. Ihr müsst lediglich einen Dienst erfüllen. Werdet Ihr antworten?', 38134),
(203848, 'deDE', '', 'Die Dankbarkeit des einen ist die Motivation des anderen. Eure Taten machen die Bastion stärker.', 38134),
(203847, 'deDE', '', 'Die Rivalität zwischen Nemea und Pelodis dauert schon äonenlang an. Sie finden für Euch sicher Verwendung... in aller Freundschaft, versteht sich.', 38134),
(203846, 'deDE', '', 'Ihr habt Euch gegen diese fremden Bestien behauptet. Vielleicht schlagen wir sie doch noch zurück. Danke.', 38134),
(203845, 'deDE', '', 'Eure Tapferkeit ist gefragt, sterbliche Seele. Diese Monster verschlingen alles auf ihrem Weg. Setzt dieser Bedrohung ein Ende!', 38134),
(203844, 'deDE', '', 'Ihr wart für den wahren Pfad immer zu schwach. Devos hätte Euch auf diesem Feld verrotten lassen sollen!', 38134),
(203843, 'deDE', 'Wir wollten unser Volk retten, nicht an solche Finsternis binden!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203841, 'deDE', 'Lysonia... warum...', '', 38134),
(203840, 'deDE', '', 'Nun wird alles klar. Ihr wart der Schlüssel zu unserem Schicksal! Zu meinem Schicksal!', 38134),
(203839, 'deDE', 'Meine Seele ist von diesem Ort gezeichnet?', '', 38134),
(203838, 'deDE', 'Ich sehe nur Finsternis...', '', 38134),
(203837, 'deDE', '', 'Hinterfragt mich nicht. Tut es!', 38134),
(203836, 'deDE', 'Lysonia, ich...', '', 38134),
(203835, 'deDE', '', 'Mehr Energie! Tötet ihn, falls nötig!', 38134),
(203834, 'deDE', 'Zu Befehl.', '', 38134),
(203833, 'deDE', '', 'Er bricht nicht! Strengt Euch mehr an.', 38134),
(203832, 'deDE', '', 'Ich habe die Zahl der Patrouillen erhöht, Polemarch. Die Aktivitäten der Verschmähten haben zugenommen... drastisch.', 38134),
(203831, 'deDE', '', 'Geht, meine Verschmähten. Bereitet alles vor. Der neue Morgen muss bald anbrechen.', 38134),
(203830, 'deDE', 'Der originalgetreue Nachbau des Emblems des Aufstiegs durch die Archon, unterstützt durch die anderen Pakte.', '', 38134),
(203829, 'deDE', '', 'Polemarch. Schlundgänger. Ich habe etwas, das Ihr sehen müsst.', 38134),
(203828, 'deDE', '', 'Und auch Uther, wie ich befürchte.', 38134),
(203827, 'deDE', '', 'Wir brauchen sie vielleicht. Laut meinen Beobachtern hatte Thenios recht. Lysonia führt die Verschmähten an.', 38134),
(203826, 'deDE', '', 'Dieses neue Emblem macht die Aufgestiegenen stärker als zuvor!', 38134),
(203819, 'deDE', 'Ohne Devos wird Lysonia die Führung der Verschmähten übernehmen. Ich bete, dass wir Frieden finden, bevor sie erneut in den Krieg ziehen.', '', 38134),
(203813, 'deDE', 'Ihre Hand, Lysonia, hat sich mit Uther schnell angeschlossen. Während unsere Anima abnahm, wurden sie zahlreicher.', '', 38134),
(203812, 'deDE', 'Als Verkörperung der Loyalität vom Pfad abzukommen, hat Devos auf einen Irrweg voller Zweifel und Gewalt gebracht.', '', 38134),
(203805, 'deDE', 'Devos wollte... ein vermeintliches Unrecht tilgen... Doch Uther war nicht für die Prüfungen des Aufstiegs bereit.', '', 38134),
(203799, 'deDE', 'Es wurde Zeit, dass Ihr seht, was nur wenige gesehen haben: Uthers Ankunft in der Bastion.', '', 38134),
(203798, 'deDE', 'Der... der enthält...', '', 38134),
(203797, 'deDE', 'Die Archon hat mir meine Gerechtigkeit verwehrt. Dank Devos leidet dieses Monster nun dort, wo es hingehört!', '', 38134),
(203796, 'deDE', '', 'Wir werden ihre glorreiche Vision verwirklichen: Kyrianer, und zwar alle, frei von den Fesseln Eurer Rituale!', 38134),
(203795, 'deDE', '', 'Mit diesen Scherben kann ich neue Spiegel für den nächsten Gluthof zusammensetzen.', 38134),
(203791, 'deDE', 'Ihr hattet Eure Gelegenheit, aufzugeben. Jetzt rächen wir Krexus mit Eurem Blut!', '', 38134),
(203789, 'deDE', '', 'Euer Verrat hat Euer Schicksal besiegelt. Zeit für Gerechtigkeit!', 38134),
(203788, 'deDE', 'Schluss mit diesem Irrsinn! Der Primus würde das nicht dulden!', '', 38134),
(203787, 'deDE', 'Seid gegrüßt.\n\nIch erschaffe Musik aus den Sünden der Seelen, die von den Taten ihres Untergangs in den gefürchteten Tiefen dieser heiligsten aller Hallen singen.\n\nVielleicht werdet Ihr, wenn Eure Zeit gekommen ist, meinem Chor beitreten...', 'Seid gegrüßt.\n\nIch erschaffe Musik aus den Sünden der Seelen, die von den Taten ihres Untergangs in den gefürchteten Tiefen dieser heiligsten aller Hallen singen.\n\nVielleicht werdet Ihr, wenn Eure Zeit gekommen ist, meinem Chor beitreten...', 38134),
(203785, 'deDE', 'Seht nur, wie sie laufen. Bald haben wir die Drust aus unserem Wald vertrieben!', '', 38134),
(203784, 'deDE', 'Die Drust bedrängen unsere Verteidigung am Hain des Erwachens. Zeigt ihnen die Macht der Wilden Jagd!', '', 38134),
(203781, 'deDE', 'Treibt den Sturmangriff voran! Schaltet ihren Anführer aus.', '', 38134),
(203772, 'deDE', '', 'Alles begann, als die Horde Teldrassil zerstörte, die Heimat der Kaldorei. \n\nDie Allianz konnte keine Soldaten entbehren, um zurückzuholen, was von Teldrassil übrig blieb, und so hatte meine Minn\'do keine Wahl.\n\nSie flehte Elune an, ihr die Macht der Nachtkriegerin zu verleihen, eine Form von Elunes Zorn.\n\nTyrande trägt jetzt den Zorn der Elune in sich, aber solche Macht tötet jeden, der sie zu lange trägt.', 38134),
(203771, 'deDE', '', 'Wisst Ihr es nicht? Ich dachte, dass sich die Kunde des Zorns meiner Minn\'do in der Horde verbreitet hätte.\n\nEure Leute haben Teldrassil zerstört, die Heimat der Kaldorei. \n\nDie Allianz konnte keine Soldaten entbehren, um zurückzuholen, was von Teldrassil übrig blieb, und so hatte meine Minn\'do keine Wahl.\n\nSie flehte Elune an, ihr die Macht der Nachtkriegerin zu verleihen, eine Form von Elunes Zorn.\n\nTyrande trägt jetzt den Zorn der Elune in sich, aber solche Macht tötet jeden, der sie zu lange trägt.', 38134),
(203770, 'deDE', '', 'Wir teleportieren uns mit der verbleibenden Anima zur Gießerei. Trefft uns dort.', 38134),
(203765, 'deDE', '', 'Lord Garridans Gästeliste müsste ein paar grundlegende Informationen darüber enthalten, wo man die potenziellen Teilnehmer finden kann.\n\nWenn Ihr mit Lord Garridan gesprochen habt, erhaltet Ihr eine Kopie der Gästeliste, die Ihr dann jederzeit einsehen könnt.\n\nWir markieren zudem die Standorte aller Gäste, für die es noch keine Rückmeldung gibt, auf der Karte.\n\nUm die Rückmeldung eines Gastes zu erhalten, müsst Ihr einfach nur zum jeweiligen Standort auf der Karte reisen und tun, was von Euch verlangt wird.\n\nWenn Ihr noch keinen potenziellen Gast getroffen habt, gibt es auch keine Rückmeldung, bis Ihr sie antrefft, normalerweise auf Abenteuerreisen in ihren Reichen.\n\nNoch einmal: Lord Garridans Gästeliste enthält alle Details über die jeweiligen Aufgaben, die erledigt werden müssen, um eine Rückmeldung zu erhalten.', 38134),
(203764, 'deDE', '', 'Solltet Ihr weitere Informationen benötigen, stelle ich sie Euch gerne bereit. Ihr müsst nur fragen.', 38134),
(203763, 'deDE', '', 'Laut meinen Aufzeichnungen trifft man ihn meistens in der Nähe der Gruften bei den Hallen der Sühne an.', 38134),
(203762, 'deDE', 'Würdet Ihr ihn liebenswürdigerweise besuchen und das Problem lösen, das seine Teilnahme verhindert?', '', 38134),
(203761, 'deDE', 'Wir haben von Grufthüter Kassir noch keine Rückmeldung erhalten.', '', 38134),
(203758, 'deDE', '', 'Sehr gut! Vielleicht habt Ihr die Ehre, den Mantel an seinen rechtmäßigen Ort zu bringen.', 38134),
(203753, 'deDE', 'Wartet, das könnte etwas sein. Was denkt Ihr, Schlundgänger?', '', 38134),
(203752, 'deDE', 'Gharmal gierte unaufhörlich nach Macht. Er hätte damit lieber Maldraxxus als seine eigenen Interessen verteidigen sollen.', '', 38134),
(203751, 'deDE', 'Hmm. Nichts, das für uns von Wert wäre.', '', 38134),
(203750, 'deDE', 'Kommt. Durchsuchen wir die Vorräte des gefallenen Markgrafen.', '', 38134),
(203749, 'deDE', '', 'Richtig. Der Schlundgänger und ich bereiten unsere Reise dorthin vor. Seht in der Zwischenzeit nach, ob Gharmal sonst etwas von Wert für uns besessen hat.', 38134),
(203748, 'deDE', 'Gharmal hat sich mit Sin\'dane verschworen. Wenn Revendreth hinter ihnen steht, ist das schlecht für uns. Forschen wir nach.', '', 38134),
(203747, 'deDE', '', 'Sie wirkt nicht wie die aus Maldraxxus geplünderte Anima. Diese war ein Geschenk an einen Verbündeten.', 38134),
(203746, 'deDE', 'Anima... aber nicht aus Maldraxxus. Das riecht nach Venthyrhexerei.', '', 38134),
(203745, 'deDE', '', 'Ich hielt es für weise, den Turm nach Beweisen für Gharmals Verrat zu durchsuchen. Das hier hatte ich nicht erwartet.', 38134),
(203744, 'deDE', 'Geht voran, Baronin.', '', 38134),
(203743, 'deDE', '', 'Mograine! Schlundgänger! In Gharmals Hochburg gab es etwas Besorgniserregendes. Das müsst Ihr sehen.', 38134),
(203741, 'deDE', '', 'Da ich so viele Namen von meiner Liste streichen konnte, habe ich wohl etwas Zeit, um Euren Hof zu verteidigen.', 38134),
(203740, 'deDE', 'Stellt Euch, Draka! Oder bettelt Ihr uns um Aufnahme an, wie auch bei den Auserwählten?', 'Stellt Euch, Draka! Oder bettelt Ihr uns um Aufnahme an, wie auch bei den Auserwählten?', 38134),
(203739, 'deDE', 'Krexus war ein Narr! Er hat für seinen Trotz bezahlt, das werdet Ihr auch!', 'Krexus war ein Narr! Er hat für seinen Trotz bezahlt, das werdet Ihr auch!', 38134),
(203738, 'deDE', 'Eine Baronie für jeden, der uns die Hülle des Paragons bringt!', 'Eine Baronie für jeden, der uns die Hülle des Paragons bringt!', 38134),
(203736, 'deDE', 'Wir hätten die Bastion schon längst plündern sollen! Der Primus hat uns zurückgehalten!', 'Wir hätten die Bastion schon längst plündern sollen! Der Primus hat uns zurückgehalten!', 38134),
(203734, 'deDE', '', 'Ich werde dies mit Adrestes besprechen. Geht und dient, Schlundgänger.', 38134),
(203733, 'deDE', '', 'Wir sollten weitere Verstärkungen in den Reichen anderer Pakte suchen.', 38134),
(203732, 'deDE', '', 'Die Macht des Emblems wächst bereits! Die Aufgestiegenen werden wichtig gegen Lysonia und Uther sein.', 38134),
(203731, 'deDE', '', 'Die Ehre gebührt Euch, als Dank für Eure Rolle im Sieg gegen Gharmal.', 38134),
(203730, 'deDE', '', 'Natürlich hat Mikanikos das Emblem bereits für solche Erweiterungen vorbereitet.', 38134),
(203729, 'deDE', '', 'Oh, das hat geklappt? Und der Schlundgänger lebt! Fantastisch. Äh... aber lieber den Schlund nicht nochmal so verlassen. Es könnte Euer Ende sein.', 38134),
(203728, 'deDE', 'Schlagt jetzt zu!', '', 38134),
(203727, 'deDE', 'Schlundgänger! Ich werde die Bestie auf Euren Befehl schwächen!', '', 38134),
(203710, 'deDE', '', 'Trefft mich in der Elysischen Feste. Wir platzieren das Herz gemeinsam.', 38134),
(203709, 'deDE', '', 'Ich... danke Euch. So soll es geschehen.', 38134),
(203708, 'deDE', 'Eine makabre Aufgabe, aber es trägt die Gabe des Fleischformens. Seine Macht würde Euer Emblem des Aufstiegs verstärken.', '', 38134),
(203707, 'deDE', 'Xandria, ich flehe Euch an, Gharmals Herz zu nehmen.', '', 38134),
(203706, 'deDE', '', 'Lebt wohl. Erzählt bitte niemandem von mir. Und wenn Ihr noch mal in diesen Mahlstrom der Finsternis springt, bringt doch etwas Interessantes mit. Ich suche ständig neue Ware.', 38134),
(203705, 'deDE', '', 'Ich glaube, damit ist unser Handel beendet. Der Portalstein ist gleich da vorn.', 38134),
(203704, 'deDE', 'Ihr verwechselt Torheit mit Mut, Kyrianer. Ihr seid auf unserem Boden!', 'Ihr verwechselt Torheit mit Mut, Kyrianer. Ihr seid auf unserem Boden!', 38134),
(203703, 'deDE', 'Craven Korinth hat im Glutdistrikt vor langer Zeit den Verstand verloren. Das macht ihn unberechenbar und damit gefährlich.', '', 38134),
(203702, 'deDE', '', 'Schwadroniert ruhig. Zeit, für Eure Sünden zu zahlen.', 38134),
(203699, 'deDE', 'Ist das alles? Ich hatte auf eine anständige Mahlzeit gehofft!', 'Ist das alles? Ich hatte auf eine anständige Mahlzeit gehofft!', 38134),
(203698, 'deDE', 'Die Feen in Nuuminuurus Obhut sind trickreich. Behaltet sie gut im Auge!', '', 38134),
(203697, 'deDE', 'Bringen wir es hinter uns.', '', 38134),
(203696, 'deDE', '', 'Das bin ich, seit sie meinen Tempel geplündert haben.', 38134),
(203695, 'deDE', 'Xandria! Seid Ihr bereit für den gerechten Kampf?', '', 38134),
(203694, 'deDE', 'Hört mich an, Auserwählte! Hört mich an, Kyrianer! Das letzte Gefecht ist nah!', '', 38134),
(203693, 'deDE', 'Entfesselt die Macht der Runenklinge und zerstört Gharmals Kolosse!', '', 38134),
(203692, 'deDE', 'Zeigen wir ihnen die wahre Macht des Hauses der Auserwählten.', '', 38134),
(203691, 'deDE', 'Unsere kyrianischen Kolosse vernichten alle, die sich dem Haus der Konstrukte entgegenstellen!', 'Unsere kyrianischen Kolosse vernichten alle, die sich dem Haus der Konstrukte entgegenstellen!', 38134),
(203690, 'deDE', 'Unsere neuen Verbündeten haben uns unvorstellbare Macht gegeben. Sin\'danes neuer Baron hatte recht!', 'Unsere neuen Verbündeten haben uns unvorstellbare Macht gegeben. Sin\'danes neuer Baron hatte recht!', 38134),
(203689, 'deDE', 'Die Bastion war nur der Anfang. Bald sind alle an unser Fleisch gebunden!', 'Die Bastion war nur der Anfang. Bald sind alle an unser Fleisch gebunden!', 38134),
(203687, 'deDE', 'Ein Sieg auf dem Pfad!', '', 38134),
(203686, 'deDE', 'Ein Kampf für die Annalen!', '', 38134),
(203684, 'deDE', 'Ihr besitzt große innere Stärke, Pelagos. Vergesst diesen Sieg nie!', '', 38134),
(203680, 'deDE', 'Eine Prüfung der Weisheit für das Anwenden des Gelernten auf diese Herausforderung.', '', 38134),
(203679, 'deDE', 'Eine Prüfung der Loyalität für das Wandeln in den Fußstapfen Eurer Vorgänger.', '', 38134),
(203676, 'deDE', 'Eine Prüfung des Mutes für Eure ersten Schritte auf dem Pfad.', '', 38134),
(203674, 'deDE', 'Vor Euch gab es Herausforderer des Pfades... und es wird auch nach Euch weitere geben. Denkt daran: Wir sind alle hier, um Euch zu unterstützen.', '', 38134),
(203671, 'deDE', '', 'Folgt mir nach Torghast, $p. Wir müssen uns beeilen.', 38134),
(203670, 'deDE', 'Ihr seid überaus talentiert, Kleia. Mögen Eure Vorgänger Euch in dieser Prüfung leiten.', '', 38134),
(203669, 'deDE', 'Dieser hölzerne Koloss ist ein Alptraum. Vorsicht vor seiner splitternden Rüstung!', '', 38134),
(203668, 'deDE', 'Alderyn und Myn\'ir sind ein tödliches Duo, dessen Pfeile und Magie gefährlich sind. Geht vorsichtig vor.', '', 38134),
(203667, 'deDE', 'Echthra ist eine begabte Spionin. Unterschätzt ihr Händchen für Gift nicht.', '', 38134),
(203666, 'deDE', 'Bringt den Mut auf, Euch dieser Prüfung erneut zu stellen.', '', 38134),
(203665, 'deDE', 'Denkt über diese Niederlage nach und nehmt die Lektionen mit Euch.', '', 38134),
(203664, 'deDE', 'Wir alle müssen Lektionen auf dem Pfad lernen.', '', 38134),
(203660, 'deDE', 'Mit jeder Prüfung lernen wir mehr über uns selbst und unser Volk. Der Pfad des Aufstiegs ist bereit!', '', 38134),
(203659, 'deDE', 'Werden die Herausforderer den Triumph kosten oder aus einer Niederlage lernen? Wir werden sehen.', '', 38134),
(203658, 'deDE', 'Heute haben wir uns für eine weitere Prüfung versammelt. Der Pfad wartet!', '', 38134),
(203657, 'deDE', 'Was für ein Empfang. So viele tapfere Champions. Ich fühle mich wirklich geehrt.', '', 38134),
(203655, 'deDE', 'Wählt ein Ziel aus.', 'Wählt ein Ziel aus.', 38134),
(203654, 'deDE', 'Baronin Drakas Angriff hat begonnen. Auf Euren Befehl bringe ich Euch zur Schlacht.', '', 38134),
(203653, 'deDE', 'Ein guter Kampf. Und jetzt zu Kel\'Thuzad.', '', 38134),
(203652, 'deDE', 'Der Lich hat einen Schild! Waffen sind gegen diese Barriere machtlos!', '', 38134),
(203649, 'deDE', 'Die Explosion hat sie komplett vernichtet!', '', 38134),
(203648, 'deDE', '', 'Ich... will... mehr!', 38134),
(203647, 'deDE', '', 'Nein! Ich will, was mir versprochen wurde! Was ich verdiene!', 38134),
(203646, 'deDE', '', 'Endlich! All die Macht, die ich verdiene!', 38134),
(203645, 'deDE', '', 'Euresgleichen wird mich nicht besiegen!', 38134),
(203640, 'deDE', 'Wir müssen lernen, zusammenzuarbeiten, wenn die Bastion diese Krise überstehen soll. Bis dahin sind wir Euch dankbar für Eure Hilfe.', '', 38134),
(203639, 'deDE', 'Der Einfluss der Verschmähten hat Zweifel über die Weisheit meines Tempels aufkommen lassen. Ich werde mit meinen Akolythen sprechen, aber erst müssen wir die Ordnung wiederherstellen.', '', 38134),
(203638, 'deDE', 'Spielt Ihr immer noch mit den Eindringlingen, Ninadar? Vernichtet sie!', '', 38134),
(203637, 'deDE', '', 'Ja, Meister. Ich existiere, um zu dienen.', 38134),
(203635, 'deDE', '', 'Es freut mich sehr, Euch zu sehen. Ich habe wichtige Neuigkeiten.', 38134),
(203633, 'deDE', '', 'Ich gehe davon aus, dass Ihr bereit seid. Reißt die Lichs in Stücke!', 38134),
(203632, 'deDE', 'Ja, ja. Aber wenn Euch dieser alte Loa mal wieder helf\'n kann, kommt zur Andren Seite, hm? Heh.', '', 38134),
(203631, 'deDE', '', 'Geht.', 38134),
(203630, 'deDE', 'Klaaar, Majestät. Der alte Bwonsamdi gibt Euch gern \'ne Seele, für die er seinen Hals riskiert hat. Und im Gegenzug hätt\' er bloß gern...', '', 38134),
(203626, 'deDE', '', 'Die Nekropole vibriert vor Kraft. Es ist Zeit, abzulegen.', 38134),
(203625, 'deDE', 'Wenn die Zeit reif ist, finden wir Anduin... und befreien ihn.', '', 38134),
(203624, 'deDE', 'Dann müssen wir Geduld bewahren. Schlundgänger, Ihr durchkreuzt weiter die Pläne des Kerkermeisters.', '', 38134),
(203623, 'deDE', 'Die Bosheit des Kerkermeisters ist allgegenwärtig in diesem Turm. Wenn er seinen Preis in Gefahr wähnt, könnte er... ihn zerstören.', '', 38134),
(203622, 'deDE', 'Das weiß ich nicht. Aber wir müssen vorsichtig sein.', '', 38134),
(203621, 'deDE', '', 'Sylvanas hat mit ihm gespielt, hat gesagt, sie will alles zerstören. Das kann sie nicht... oder?', 38134),
(203620, 'deDE', '', 'Zumindest lebt er noch und ist unverletzt. Was auch immer die Banshee will, er wird sich weigern.', 38134),
(203619, 'deDE', 'Mehr... kann ich nicht sehen.', '', 38134),
(203618, 'deDE', '', 'Beschützt das Herz des Waldes, Kind der Träume. Kein Feind darf unsere Hallen betreten.', 38134),
(203616, 'deDE', '', 'Ihr habt Euch selbst für meinen Geist geopfert. Ich beschütze den Ardenwald bis zum bitteren Ende.', 38134),
(203612, 'deDE', '', 'Der Himmel ruft nach uns! Beeilt Euch!', 38134),
(203611, 'deDE', 'Lasst keinen Drust am Leben!', '', 38134),
(203610, 'deDE', 'Das muss Anduin sein.', '', 38134),
(203609, 'deDE', 'Meine Wächter haben von einem wertvollen Gefangenen tief im Turm des Kerkermeisters gesprochen.', '', 38134),
(203608, 'deDE', 'Ich bitte Euch, blickt tiefer. Findet den König, den ihr als Kind beschützt habt.', '', 38134),
(203607, 'deDE', '', 'Ich stehe in Eurer Schuld, $n. Wir mögen auf anderen Seiten gestanden haben, aber jetzt eint uns ein gemeinsamer Feind.', 38134),
(203606, 'deDE', '', 'Gut, wir sind alle hier. Wir müssen schnell unseren Zug machen, bevor der Feind reagieren kann.', 38134),
(203605, 'deDE', '', 'Wir greifen die Ostflanke an und sie die Westflanke. Dann nehmen wir Gharmal in die Zange.', 38134),
(203604, 'deDE', '', 'Mograines Leute sind wertvolle Verbündete und kennen den Feind gut.', 38134),
(203603, 'deDE', '', 'Wir kämpfen bis zum Ende für Euch, Paragon.', 38134),
(203602, 'deDE', 'Das werden wir bald sehen.', '', 38134),
(203600, 'deDE', '', 'Ich halte mein Wort. Gemeinsam bringen wir Gharmal zu Fall.', 38134),
(203590, 'deDE', '', 'Anscheinend ist Dambala auch hier. Es wird nicht einfach, einen Loa zu bekämpfen, aber ich habe einen Trick!', 38134),
(203565, 'deDE', '', 'Ich fürchte, sie wurden nach Torghast gebracht. Jener Turm am Horizont. Der Kerkermeister verwahrt dort seinen wertvollsten Besitz.', 38134),
(203564, 'deDE', 'Dann zeigt uns, wie man in diesen Turm kommt!', '', 38134),
(203563, 'deDE', '', 'Ah, Ihr meint die anderen Sterblichen, die bei der Flucht zurückgeblieben sind.', 38134),
(203562, 'deDE', 'Immer langsam, Mittlerin. Wir müssen noch mehr retten als nur den Baron der Maldraxxi.', '', 38134),
(203554, 'deDE', 'Nur die Kleidung richten... Und wenn Ihr nach einer anderen Farbe sucht, findet Ihr sie in der Nähe von Schlammlachen, oder der Mittler auf dem Nachtmarkt hat vielleicht eine.', '', 38134),
(203553, 'deDE', 'Neue Kleidung. Für mich...', '', 38134),
(203552, 'deDE', 'Was stimmt mit meinen Haaren nicht? Oder habt Ihr ein neues Plakat für mich gefunden, das ich lernen kann?', '', 38134),
(203548, 'deDE', 'Ja? Wie kann ich dienen? Oder habt Ihr was Neues für mich gefunden?', '', 38134),
(203547, 'deDE', '', 'Hoffentlich mag jeder äußerst lebendige Pilze. Hee hee hee.', 38134),
(203540, 'deDE', '', 'Wie ich sehe, habt Ihr den Schlundgänger mitgebracht. Ich vertraue Eurer Allianz und somit auch Euch.', 38134),
(203538, 'deDE', 'Aber wenn Ihr mit der Winterkönigin fertig seid, kommt doch mal zur Andren Seite und besucht Euren alt\'n Freund, hm?', '', 38134),
(203537, 'deDE', 'Mueh\'zala ist stark und echt gemein, aber keine Sorge um den alten Bwonsamdi. Ich alter Loa hab noch jede Menge Tricks auf Lager!', '', 38134),
(203536, 'deDE', 'Sieht aus, als muss ich mit dem Boss abrechn\'n... diesmal endgültig.', '', 38134),
(203527, 'deDE', '', 'Schnell! Wirkt den Zauber!', 38134),
(203513, 'deDE', '', 'Was ist? Könnt Ihr nicht sehen, dass ich lese?', 38134),
(203512, 'deDE', '', 'Markgraf Gharmals Herrschaft findet heute ein Ende! Wir sammeln die Truppen und bereiten die Zerekriss auf einen Angriff vor.', 38134),
(203500, 'deDE', '', 'Twigin kann Euch zur Nekropole fliegen, falls nötig.', 38134),
(203481, 'deDE', '', 'Das Licht ist stark in Anduin. Ich weiß, sein Herz wird ihn beschützen.', 38134),
(203480, 'deDE', '', 'Ich bin froh, dass Ihr wieder da seid, Thrall. Aggra und der Lordregent werden es sofort erfahren.', 38134),
(203478, 'deDE', 'Bitte helft, Geehrte Stimme, während ich mit den anderen spreche. Wir müssen über die Konsequenzen dieser Vision beraten.', '', 38134),
(203477, 'deDE', '', 'Hochlord, dieser Champion hat eine dringende Warnung von dem Primus aus Maldraxxus. Die anderen Reiche müssen sie unbedingt erhalten.', 38134),
(203476, 'deDE', 'Noch nicht, Calia. Solche Visionen haben ihren Preis. Ich wage es so schnell noch nicht wieder.', '', 38134),
(203475, 'deDE', '', 'Ich konnte weder Anduin noch Baine Bluthuf hören, Hochlord. Geht es wirklich nicht noch einmal?', 38134),
(203474, 'deDE', '', 'Ich konnte die Lordadmiralin hören und wohl auch Thralls Stimme. Ihre Schreie... mir ist das Blut in den Adern gefroren!', 38134),
(203473, 'deDE', 'Mehr... sehe ich nicht. Es ist... zu gefährlich, weiter vorzudringen.', '', 38134),
(203467, 'deDE', '', 'Beste Pilze! Holt Euch beste Pilze hier.', 38134),
(203450, 'deDE', '', 'Alle Dinge wachsen, um zu überleben. Bäume, Wälder, Leute: Alle brauchen Nahrung, aber nicht dieselbe.$b$BMit mir zu teilen, beweist Herzensgüte. Wenn Ihr mit mir teilt, teile ich meinen Segen mit Euch.', 38134),
(203448, 'deDE', 'Vorräte aus den Werkstätten, die ich verteilen darf? Oh, das ist wirklich mein Glückstag!$b$bBei so viel Glück könnte ich sogar etwas davon abgeben. Ja, ja. Gebt mir die Vorräte und etwas von meinem Glück wird Euch sicher folgen!', '', 38134),
(203447, 'deDE', '', 'Ihr solltet jetzt gehen. Der Kerkermeister lässt sich sein Spielzeug nur ungern rauben... auch nicht das ausgemusterte. Los jetzt. Zurück nach Oribos mit Euch!', 38134),
(203443, 'deDE', '', 'Wir schlagen gleichzeitig zu. Der Plan der Baronin wird gelingen.', 38134),
(203442, 'deDE', '', 'Endlich zeigt Ihr Euer elendes Gesicht!', 38134),
(203437, 'deDE', 'Ich fürchte, etwas erhebt sich in Torghast. Etwas Grauenhaftes.', '', 38134),
(203436, 'deDE', 'Kommt sofort nach Oribos, Schlundgänger. Eine Dunkelheit... ruft nach mir.', '', 38134),
(203435, 'deDE', 'Schlundgänger, macht weiter so. Erkundet die Tiefen von Torghast. Kämpft für die Schattenlande. Und wenn die Zeit kommt, ist der Sieg unser!', '', 38134),
(203434, 'deDE', '', 'Wir werden mit unseren Verbündeten in Oribos die Pakte zusammenrufen. Nur mit ihrer Hilfe besiegen wir den Kerkermeister... und retten Anduin.', 38134),
(203433, 'deDE', 'Ihr habt die Armeen des Kerkermeisters gesehen. Wir sind noch nicht bereit. Wenn wir überstürzt unseren Herzen folgen, könnten wir alles verlieren.', '', 38134),
(203432, 'deDE', 'Wir sollten unsere Truppen sammeln und Torghast angreifen! Anduin befreien!', '', 38134),
(203431, 'deDE', 'Habt Dank. Ihr alle. Jaina hat recht. Der Kerkermeister weiß, was wir gesehen haben. Und wird es gegen uns verwenden.', '', 38134),
(203430, 'deDE', '', 'Den Angriff haben wir aufgehalten, aber die Gefahr ist noch nicht vorbei.', 38134),
(203429, 'deDE', 'Gut gemacht, Jaina. Fast hätten wir ihn verloren.', '', 38134),
(203428, 'deDE', '', 'Vater!', 38134),
(203427, 'deDE', '', 'Durchhalten, Bolvar! Noch ein wenig!', 38134),
(203426, 'deDE', 'Ich nehme mir, was mir zusteht! Euch holt alle der Tod!', '', 38134),
(203425, 'deDE', 'Kämpft, Hochlord! Wir stehen Euch bei!', '', 38134),
(203424, 'deDE', '', 'Vater, bleib stark! Er darf nicht gewinnen!', 38134),
(203423, 'deDE', 'RAAAaaaarrGGHHHH!', '', 38134),
(203422, 'deDE', 'Jämmerlich versagt! Wie alle vor Euch!', '', 38134),
(203421, 'deDE', 'Ihr solltet meine Ankunft verkünden. Stattdessen trotzt Ihr mir!', '', 38134),
(203420, 'deDE', '', 'Eine... Gramklinge? Oh, nein...', 38134),
(203419, 'deDE', '', 'Der Kerkermeister dringt in Bolvars Geist ein! Wir müssen die Verbindung trennen!', 38134),
(203418, 'deDE', 'ARGH! Nie... werde ich... Euch dienen!', '', 38134),
(203417, 'deDE', 'Der Kerkermeister! Wie ist das möglich?!', '', 38134),
(203416, 'deDE', 'Ich... sehe... Euch!', '', 38134),
(203415, 'deDE', '', 'Bolvar! Was ist das?!', 38134),
(203414, 'deDE', 'Die Verbindung ist gebrochen. Mehr kann ich nicht... Wartet... etwas...', '', 38134),
(203412, 'deDE', 'Es gibt immer einen Preis, wenn man sich mit der Dunkelheit einlässt. Aber ich muss es wissen. Wir alle!', '', 38134),
(203411, 'deDE', '', 'Auch ich sorge mich um Anduin, aber sollte der Hochlord wirklich dort hinein sehen? Der Preis ist so hoch...', 38134),
(203410, 'deDE', '', 'Anduin wird dort immer noch gefangengehalten! Ist er wohlauf, Vater?', 38134),
(203408, 'deDE', 'Was ist, Hochlord? Was spürt Ihr?', '', 38134),
(203407, 'deDE', 'Nein! Nicht noch einmal!', '', 38134),
(203406, 'deDE', 'Bosheit... wächst... Herrschaft... erhebt sich...', '', 38134),
(203403, 'deDE', 'Darion! Nach all dieser Zeit...', '', 38134),
(203402, 'deDE', 'Diese Stimme! Ist das möglich! Der Baron ist... mein Vater?!', '', 38134),
(203401, 'deDE', '', 'Ich kann uns lange genug tarnen, um Euren verlorenen Baron zu finden. Kommt!', 38134),
(203397, 'deDE', 'Vorsicht. Die Augen des Kerkermeisters sind überall.', '', 38134),
(203396, 'deDE', 'Seid vorsichtig, Champion. Findet Darion und er wird Euch mit dem Baron helfen.', '', 38134),
(203395, 'deDE', '', 'Aber seid gewarnt. Auch wenn Ihr dem Schlund einmal entkommen seid, ein zweites Mal gelingt es Euch vielleicht nicht.', 38134),
(203394, 'deDE', 'Die Geehrte Stimme hat gesprochen. Ich werde Euch zum Ring der Übertragung führen, $uSterblicher:Sterbliche;.', '', 38134),
(203363, 'deDE', '', 'Wir dürfen ihm Anduin nicht überlassen.', 38134),
(203362, 'deDE', '', 'Arthas und Bolvar waren nicht allein von dieser dunklen Macht berührt. Die Klinge hat viele Opfer gefordert. Viele Seelen.', 38134),
(203361, 'deDE', '', 'Die Magie der Runen auf Frostgram war vor dem Lichkönig auf Azeroth gänzlich unbekannt. Aber diese Macht spürt man auch überall im Schlund... und in Torghast.', 38134),
(203360, 'deDE', '', 'Wie auch beim Schicksal meines Vaters. Der Helm... das Schwert... waren sie von Anfang an sein Werk?', 38134),
(203359, 'deDE', '', 'Der Geist meines Bruders hängt über uns allen wie ein Leichentuch. Er hat seine Entscheidungen selbst getroffen, aber es scheint, der Kerkermeister hatte seine Hand im Spiel.', 38134),
(203358, 'deDE', '', 'Danke, Taelia. Der Schlund ist... ein Ort ohne Hoffnung. Solch eine Leere habe ich erst einmal erlebt. Gegen Arthas in Eiskrone.', 38134),
(203357, 'deDE', '', 'Schön, dass es Euch gut geht, Lordadmiralin. Was Ihr und die anderen nicht alles ausgestanden habt.', 38134),
(203353, 'deDE', 'Geht hinter dem Eis in Deckung!', '', 38134),
(203352, 'deDE', '', 'Geht hinter den Eisblöcken in Deckung!', 38134),
(203347, 'deDE', '', 'Kehrt nach Oribos zurück. Sagt ihnen, dass Maldraxxus die anderen Reiche nicht mehr behelligt. Unser Krieg tobt hier.', 38134),
(203346, 'deDE', '', 'Die Ewigen... der Kerkermeister. Das ist eine schlimme Nachricht, $n.', 38134),
(203336, 'deDE', 'Ist das noch ein Test? Was soll ich hier tun?!', 'Ist das noch ein Test? Was soll ich hier tun?!', 38134),
(203335, 'deDE', 'Keiner meiner Lehrer hat DIESE Prüfung erwähnt!', 'Keiner meiner Lehrer hat DIESE Prüfung erwähnt!', 38134),
(203334, 'deDE', '<Der Aspirant schreit auf und zeigt auf die nächste Kreatur.>\n\nSEHT Ihr das?!', '<Die Aspirantin schreit auf und zeigt auf die nächste Kreatur.>\n\nSEHT Ihr das?!', 38134),
(203333, 'deDE', 'Fünf Ideale. Fünf Runen auf der Klinge. Dieselben Runen wie auch im Sitz des Primus.', '', 38134),
(203332, 'deDE', 'Das unermüdliche Streben nach Sieg... selbst im Angesicht des sicheren Todes.', '', 38134),
(203331, 'deDE', 'Der Verstand, ihre Pläne zu durchschauen und sie zu überlisten.', '', 38134),
(203330, 'deDE', 'Der Ambition, sich über seine Feinde zu erheben und sie zu vernichten.', '', 38134),
(203329, 'deDE', 'Unser wunderschöner Obstgarten wurde ins Chaos gestürzt! Wer räumt das alles wieder auf?', 'Unser wunderschöner Obstgarten wurde ins Chaos gestürzt! Wer räumt das alles wieder auf?', 38134),
(203328, 'deDE', 'Warum passiert das?! Das hier ist ein FRIEDVOLLER Ort!', 'Warum passiert das?! Das hier ist ein FRIEDVOLLER Ort!', 38134),
(203327, 'deDE', 'Sie sind aus dem Nichts aufgetaucht! So viele! Überall!', 'Sie sind aus dem Nichts aufgetaucht! So viele! Überall!', 38134),
(203326, 'deDE', 'Der ganze Obstgarten ist verrückt geworden!', 'Der ganze Obstgarten ist verrückt geworden!', 38134),
(203325, 'deDE', 'Was ist nur in diese Kreaturen gefahren?!', 'Was ist nur in diese Kreaturen gefahren?!', 38134),
(203322, 'deDE', '', 'Wir werden viel voneinander lernen.', 38134),
(203318, 'deDE', 'Wendet Euch an Dactylis, wenn Ihr Schwierigkeiten habt, Materialien zu finden. In jedem Bauplan sind die erforderlichen Materialien aufgelistet, und diese Listen enthalten Informationen, die Euch helfen, sie aufzustöbern.\n\nVergesst nicht, dass diese Materialien über die Reiche der Schattenlande verstreut sind. Ihr müsst eventuell die Bastion verlassen, um sie zu finden.', '', 38134),
(203316, 'deDE', '', 'So gut wie erledigt.', 38134),
(203315, 'deDE', '', 'Ich bin schon weg.', 38134),
(203314, 'deDE', '', 'Habt Ihr was Krummes vor?', 38134),
(203313, 'deDE', '', 'Feuerball!', 38134),
(203312, 'deDE', '', 'Nekroblitz!', 38134),
(203311, 'deDE', '', 'Hah... Bzzzt!', 38134),
(203310, 'deDE', '', 'Stets zu Diensten.', 38134),
(203308, 'deDE', '', 'Lasst uns zaubern!', 38134),
(203298, 'deDE', 'Diese Art von Treffen muss wirklich aufhören.', '', 38134),
(203293, 'deDE', 'Alles an Bord!', '', 38134),
(203292, 'deDE', 'Ich muss sofort unsere Abenteuer festhalten!', '', 38134),
(203291, 'deDE', '', 'Lange haben wir mit den Kreaturen der Bastion in Frieden gelebt. Es schmerzt uns alle, sie so leiden zu sehen. Habt Dank für Eure Hilfe.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203290, 'deDE', '', 'Ich habe Berichte über Wildtiere erhalten, die in der Nähe Chaos stiften. Die Animadürre muss sie wahnsinnig gemacht haben. Bitte stellt wieder Ruhe her.', 38134),
(203289, 'deDE', 'Wunderbar, noch ein Kapitel für mein Buch!', '', 38134),
(203285, 'deDE', 'Hier! Alles für Euch!', '', 38134),
(203284, 'deDE', 'Hab was für Euch dagelassen.', '', 38134),
(203282, 'deDE', 'Ich riech so gut.', '', 38134),
(203279, 'deDE', '', 'Ist es hier irgendwo?', 38134),
(203277, 'deDE', '', 'Ich geh und suche mir Ärger!', 38134),
(203271, 'deDE', 'Existenz ist nur Eure Sichtweise des Universums.', 'Existenz ist nur Eure Sichtweise des Universums.', 38134),
(203270, 'deDE', 'Das könnte glatt Eure Seele verändern.', 'Das könnte glatt Eure Seele verändern.', 38134),
(203269, 'deDE', 'Möge Eure Seele sicher sein.', 'Möge Eure Seele sicher sein.', 38134),
(203268, 'deDE', 'Ich freue mich schon auf unsere Abenteuer.', 'Ich freue mich schon auf unsere Abenteuer.', 38134),
(203267, 'deDE', '', 'Suchen wir uns mal Ärger!', 38134),
(203266, 'deDE', 'Der Primus ist ein Muster der fünf Soldatenideale von Maldraxxus.', '', 38134),
(203265, 'deDE', '', 'Trefft uns bei Ansids Käfig, wenn Ihr den Schlüssel habt. Rasch.', 38134),
(203263, 'deDE', 'Großer Ausverkauf!', '', 38134),
(203262, 'deDE', 'Wir haben geöffnet!', '', 38134),
(203260, 'deDE', '', 'Wir kriegen Euch wieder hin!', 38134),
(203259, 'deDE', '', 'Ich mach das wieder heile!', 38134),
(203258, 'deDE', '', 'Ihr seid zäh... das wird schon.', 38134),
(203257, 'deDE', '', 'Habt Ihr ein Aua-Aua?', 38134),
(203255, 'deDE', '', 'Schlundgänger! Sie haben Ansid. Wir müssen ihn befreien, bevor wir fortfahren.', 38134),
(203253, 'deDE', 'Tiefenechodreizack, gut für fiese Zauber!\n\nGegner wirkt Zauber, der wehtut? Macht alles schwerer für Euch?\n\nNicht mehr! Speer werfen, Zauber unterbrechen! Und auch ein klein bisschen körperlichen Schaden, ja.', '', 38134),
(203252, 'deDE', 'Nein, nix mit Tanzschritten. Das gibt nur Ärger, verstanden?', '', 38134),
(203251, 'deDE', 'Jau, ich werde ihnen meine Tanzschritte zeig\'n!', '', 38134),
(203250, 'deDE', 'Knüppelfratze und ich werd\'n richtige Hofadlige.', '', 38134),
(203249, 'deDE', 'Ihr habt den Mut, die Macht und die Gerissenheit, Eure Rivalen zu schlagen.', '', 38134),
(203248, 'deDE', '', 'Es wäre mir eine Ehre, an Eurer Seite zu kämpfen, $p.', 38134),
(203247, 'deDE', 'Jetzt werde ich wohl sehen, wie hart Euer Prinz wirklich ist.', '', 38134),
(203246, 'deDE', 'Jaina, etwas stimmt nicht. Der Portalstein lädt sich nicht mehr auf!', '', 38134),
(203245, 'deDE', '', 'Markgräfin Sin\'dane bittet um Eure Hilfe im Kampf gegen Kel\'Thuzads Verbündete. Sie sollen bezahlen!', 38134),
(203244, 'deDE', '', 'Ich warte am Treffpunkt auf Euch. Offen wir, die anderen sind bereits dort...', 38134),
(203240, 'deDE', '', 'Gut gemacht! Schnelligkeit, Geschick und Gerissenheit sind alle wertvolle Eigenschaften.', 38134),
(203239, 'deDE', '', 'Es ist eine Ehre, die Fackel der Herausforderung zu tragen. Entzündet die Kohlenpfannen, um die großen Häuser zur Arena zu rufen... doch Ihr müsst schnell sein!', 38134),
(203233, 'deDE', 'Ihr denkt, Ihr habt am meisten, doch was könnt Ihr Euch leisten?\n\nKönnt mich nicht bestechen, tut mir leid. Wenn Ihr denn kein Schreiber seid... hab ich nichts für Euch. Ohhh, die Augen werden feucht.', 'Ihr denkt, Ihr habt am meisten, doch was könnt Ihr Euch leisten?\n\nKönnt mich nicht bestechen, tut mir leid. Wenn Ihr denn kein Schreiber seid... hab ich nichts für Euch. Ohhh, die Augen werden feucht.', 38134),
(203231, 'deDE', '', 'Meine Schwestern zeigen es vielleicht nicht, aber wir sind dankbar dafür und begierig darauf, wieder fliegen zu können.', 38134),
(203229, 'deDE', '', 'Was ist Euer Begehr?', 38134),
(203226, 'deDE', '', 'Sie haben... uns erwartet. Ich fürchte, Khaliiq und Ansid schweben in Gefahr.', 38134),
(203225, 'deDE', '', 'Hier lang! Schnell!', 38134),
(203224, 'deDE', '', 'Passt auf!', 38134),
(203223, 'deDE', '', 'Seid vorsichtig... sie haben uns noch nicht gesehen.', 38134),
(203222, 'deDE', 'Keine Sorge. Uns fällt schon das Richtige für diesen Hof ein. Die Festherolde!', '', 38134),
(203221, 'deDE', '', 'Bleibt dicht bei mir ,$p. Verstohlenheit ist der Schlüssel.', 38134),
(203205, 'deDE', 'Das... werde ich. Habt Dank für Euren Rat, Niya.', '', 38134),
(203204, 'deDE', '', 'Kümmert Euch gut um sie. Ihr Wert übertrifft ihr Aussehen bei Weitem.', 38134),
(203203, 'deDE', '', 'Ihr haltet ganze Universen voller Möglichkeiten in Eurer Hand, Renathal.', 38134),
(203202, 'deDE', 'Vielleicht eine schmackhafte.', '', 38134),
(203201, 'deDE', 'Welche Pflanze versteckt sich wohl in dieser kleinen Hülle?', '', 38134),
(203200, 'deDE', 'Prinz Renathal hält einen Samen zwischen den Fingern.', '', 38134),
(203199, 'deDE', 'Eine kleine Menge ist nie genug.', '', 38134),
(203198, 'deDE', 'Danke, Seuchenerfinder, doch ich glaube, dass diese kleine Menge für meine Zwecke genügt.', '', 38134),
(203197, 'deDE', 'Ich habe die perfekten Mischungen gefunden. Trefft mich nach Ende des Hofes hinter dem Turm, dann versorge ich Euch damit.', '', 38134),
(203196, 'deDE', 'Möchtet Ihr Scherzglibber?', '', 38134),
(203193, 'deDE', 'Danke. Damit kann ich Theotar später einen wunderbaren Streich spielen...', '', 38134),
(203192, 'deDE', 'Prinz Renathal steckt den Schleim in eine seiner Manteltaschen.', '', 38134),
(203191, 'deDE', '', 'Gerüchte sind eine Währung. Das ist alles.', 38134),
(203190, 'deDE', '', 'Ihr verkompliziert die Angelegenheit unnötig, Prinz Renathal.', 38134),
(203189, 'deDE', 'Gerüchte sind wie Gräberbecken. Sie breiten sich zu schnell aus und alle fühlen sich danach schmutzig.', '', 38134),
(203188, 'deDE', 'Prinz Renathal zerreißt das Papier, ohne zu zögern.', '', 38134),
(203184, 'deDE', '', 'Diese Zenturionen sind Wissensträger, die um jeden Preis bewahrt werden müssen. Danke für Euren Einsatz.', 38134),
(203183, 'deDE', '', 'Der Oberste Schmied hat uns informiert, dass eine unserer Mnemiseinheiten nicht mehr reagiert. Seht zu, dass sie alles Nötige hat, um ihren Dienst fortzusetzen.', 38134),
(203182, 'deDE', '', 'Rekruten, sobald Ihr Euch mit $umeinem Freund:meiner Freundin; hier duelliert habt, könnt Ihr Euch zurückziehen. Schlagt Ihr Euch gut, werdet Ihr gerufen, wenn Ihr gebraucht werdet.', 38134),
(203181, 'deDE', '', 'Diese Plakate funktionieren hervorragend. Es sind schon Rekruten aufgetaucht.', 38134),
(203180, 'deDE', '', 'Diese Angelegenheit ist... heikel. Alles Weitere erfahrt Ihr, wenn Ihr dort seid. Und beeilt Euch bitte.', 38134),
(203173, 'deDE', 'Und nun hinfort! Ich habe Eure Anwesenheit lange genug geduldet.', 'Und nun hinfort! Ich habe Eure Anwesenheit lange genug geduldet.', 38134),
(203172, 'deDE', 'Macht es kurz.', 'Macht es kurz.', 38134),
(203152, 'deDE', 'Das wird ein Spektakel! Ich hoffe, einige finden hier bei den Prüfungen ihren wahren Tod.', 'Das wird ein Spektakel! Ich hoffe, einige finden hier bei den Prüfungen ihren wahren Tod.', 38134),
(203150, 'deDE', 'Oh, ich hoffe, ich trete bei den Prüfungen gegen ein Mitglied des Hauses Dunkelader an. Da sind noch einige Rechnungen offen!', 'Oh, ich hoffe, ich trete bei den Prüfungen gegen ein Mitglied des Hauses Dunkelader an. Da sind noch einige Rechnungen offen!', 38134),
(203149, 'deDE', 'Niemand schlägt mich. Meine Fertigkeiten mit dem Rapier sind legendär.', 'Niemand schlägt mich. Meine Fertigkeiten mit dem Rapier sind legendär.', 38134),
(203148, 'deDE', 'Haus Sauerwein wird für seine Beleidigungen bezahlen, sobald das Training beginnt!', 'Haus Sauerwein wird für seine Beleidigungen bezahlen, sobald das Training beginnt!', 38134),
(203147, 'deDE', '', 'Ziehen wir uns zurück. Ich bringe uns zu einem kleinen Alkoven am Portalstein, der Euch bereits zur Flucht verholfen hat.', 38134),
(203146, 'deDE', 'Ich will meinem Haus Ruhm bringen, nicht mir selbst.', 'Ich will meinem Haus Ruhm bringen, nicht mir selbst.', 38134),
(203145, 'deDE', 'Halt. Mein Schwert... Schicksalsbringer. Ich werde es bald brauchen.', '', 38134),
(203143, 'deDE', 'Schone deine Kräfte, Vater. Wir können uns später unterhalten.', '', 38134),
(203142, 'deDE', 'Eine Prüfung für einen Platz in einer Elitetruppe ist die perfekte Gelegenheit, um es einem Narren vom Haus Primula so richtig zu zeigen. Ich bin dabei.', 'Eine Prüfung für einen Platz in einer Elitetruppe ist die perfekte Gelegenheit, um es einem Narren vom Haus Primula so richtig zu zeigen. Ich bin dabei.', 38134),
(203141, 'deDE', 'Mein Sohn...', '', 38134),
(203137, 'deDE', 'Ich würd das kostenlose Unterhaltung nennen.', '', 38134),
(203136, 'deDE', 'Keine Sorge, Gubbins. Wir werden Euch vor diesem grausamen Scharlatan beschützen!', '', 38134),
(203133, 'deDE', 'Kein Freund. Feuerwerk macht Gubbins Angst.', '', 38134),
(203131, 'deDE', 'Schickt ihn fort und hört nicht auf das, was er sagt!', '', 38134),
(203130, 'deDE', 'Ganz sicher nicht! Ich würde mich nie dabei erwischen lassen, mit so einem Tunichtgut zu plaudern!', '', 38134),
(203129, 'deDE', '', 'Der Animageruch verrät Eure Anwesenheit in der Verdammnisfestung. Kehrt um!', 38134),
(203128, 'deDE', '', 'Für das Bestienlabyrinth seid Ihr nicht gewappnet. Kehrt um!', 38134),
(203126, 'deDE', 'Schafft diesen Unrat hier raus, bevor er unsere ganzen wundervollen Dekorationen ruiniert!', '', 38134),
(203125, 'deDE', 'Nicht schlecht. Nicht schlecht.', '', 38134),
(203124, 'deDE', '', 'Schicksalsbringer, hm? Ich habe es für ihn gefertigt, als er zum Baron ernannt wurde. Er wollte etwas, das dem ähnlich war, das er zu Lebzeiten benutzt hatte. Eurer Reaktion nach zu urteilen, erkennt Ihr die Form ansatzweise wieder. Ich nehme an, wenn ich mir einen Titel mit einem Schwert teilen würde, würde ich ziemlich an ihm hängen.\n\nEs ist eine meiner erlesensten Kreationen. Alexandros hat hart um einige der Materialien gekämpft, die darin enthalten sind. Ich bin nicht sicher, woher der Schädel stammt, aber er ist sicherlich nicht aus Maldraxxus.', 38134),
(203122, 'deDE', '', 'Es ist schön, Euch wiederzusehen, $gmein Freund:meine Liebe;. Aufregende Zeiten, was?', 38134),
(203120, 'deDE', '', 'Habt Dank, Schlundgänger. Man wird sich um die Seelen kümmern.', 38134),
(203119, 'deDE', '', 'Die Hoffnungsbrecher der Furchthetzerin lassen diese Seelen verrotten. Bitte helft ihnen.', 38134),
(203112, 'deDE', '', 'He da, $C. Ich biete erfahrenen Reisenden die Möglichkeit, anderen zu helfen, die sie unterwegs treffen.', 38134),
(203109, 'deDE', '', 'Seltsam... Das klingt nicht nach dem Gorak Zhar, den ich kenne...', 38134),
(203108, 'deDE', '', 'Narren! Für Gorak Zhar seid Ihr nur Insekten!', 38134),
(203107, 'deDE', 'Ich bringe Naschdinge zum Gluthof. Lecker für Gäste!', '', 38134),
(203106, 'deDE', 'He da, $C. Ich biete erfahrenen Reisenden die Möglichkeit, anderen zu helfen, die sie unterwegs treffen.', 'He da, $C. Ich biete erfahrenen Reisenden die Möglichkeit, anderen zu helfen, die sie unterwegs treffen.', 38134),
(203100, 'deDE', '', 'Alles ist bereit. Lasst Twigin wissen, wenn Ihr aufbrechen wollt.', 38134),
(203098, 'deDE', 'Ein paar Explosionen sollten schon ausreichen!', '', 38134),
(203097, 'deDE', 'Das ist ein respektierter Hof, nicht der Endmorast!', '', 38134),
(203096, 'deDE', 'Ja zu den Laternen, nein zu den Fackeln.', '', 38134),
(203095, 'deDE', '', 'Wir infiltrieren die Nekropole, während Ansid die Konstrukte ablenkt.', 38134),
(203094, 'deDE', '', 'Behaltet das Gebiet von oben im Blick, während Twigin hierherfliegt.', 38134),
(203084, 'deDE', 'Euch gebührt mein Dank, Schlundgänger.', '', 38134),
(203083, 'deDE', '', 'Ich wünsche Euch viel Spaß.', 38134),
(203082, 'deDE', '', 'Die Anklägerin schmunzelt.', 38134),
(203081, 'deDE', 'Ihr könnt auf mich zählen, $gSchlundgänger:Schlundgängerin;! Ich bin immer der Beste, in dem, was ich tue!', '', 38134),
(203080, 'deDE', 'Der Festherold meldet sich zum Dienst!', '', 38134),
(203079, 'deDE', 'Temel schaut einen Augenblick lang verwirrt, zieht dann aber einen festlichen Hut von seinem Gürtel und setzt ihn auf.', '', 38134),
(203078, 'deDE', '', 'Eure Dienste als Sündenherold werden hier nicht benötigt. Ihr werdet $gdem Schlundgänger:der Schlundgängerin; helfen, einen neuen Hof zu koordinieren.', 38134),
(203077, 'deDE', 'Der einzig wahre Sündenherold steht zu Diensten, Anklägerin!', '', 38134),
(203076, 'deDE', '', 'Temel!', 38134),
(203075, 'deDE', '', 'Aber wenn der Prinz wünscht, dass diese Pflichten erfüllt werden...', 38134),
(203074, 'deDE', '', 'Kommt nicht in Frage. Die gepeinigten Seelen brauchen meine volle Aufmerksamkeit.', 38134),
(203073, 'deDE', 'Ich muss unbedingt Lady Lerae davon erzählen!', 'Ich muss unbedingt Lady Lerae davon erzählen!', 38134),
(203071, 'deDE', 'Ah, $gSchlundgänger:Schlundgängerin;. Wir haben gerade von Euch gesprochen.\n\nWas verschafft uns die Ehre?', '', 38134),
(203070, 'deDE', 'Oh, das wird der aufregendste Hof von ganz Revendreth.', 'Oh, das wird der aufregendste Hof von ganz Revendreth.', 38134),
(203069, 'deDE', 'Und natürlich werde ich sicherstellen, dass Ihr alle auf der Gästeliste vermerkt werdet.', '', 38134),
(203068, 'deDE', 'Mein ausgedehntes Netzwerk an Kontakten und mein bescheidenes diplomatisches Geschick stehen Euch zu Diensten.', '', 38134),
(203067, 'deDE', 'Es wäre mir eine Ehre, Euch und Prinz Renathal behilflich zu sein.', '', 38134),
(203066, 'deDE', 'Der alte Blutsauger hat ein Auge für Talent. Ich mach das schon.', '', 38134),
(203065, 'deDE', 'Hat Euch wohl Theotar gesagt, oder?', '', 38134),
(203064, 'deDE', 'Es überrascht mich, dass Ihr einem Gräber so eine Aufgabe anvertraut.', '', 38134),
(203062, 'deDE', 'Habt Ihr mal den Verschütteten Kelch spielen hören?\n\nDa wird einem das Auge nass. Echt unvergessliche Musik.\n\nHab angefangen, ihre Biografie zu lesen. Endlich mal ein Buch, für das es sich lohnt, Lesen zu lernen!', '', 38134),
(203061, 'deDE', 'Schlundgänger, auf ein Wort?', '', 38134),
(203060, 'deDE', 'Ich werde unser härtester Kritiker und größter Fürsprecher sein!', '', 38134),
(203059, 'deDE', 'Frisches Fleisch! Importiertes Gemüse! Nur die saubersten Gräberkellner!', '', 38134),
(203058, 'deDE', 'Natürlich werden nur die feinsten Zutaten verwendet...', '', 38134),
(203056, 'deDE', '<Stefan der Wählerische unterbricht sein Diktat an die in der Nähe schwebende Schriftrolle und schaut Euch an.>\n\nJa? Möchtet Ihr auch etwas zu dem komplexen Aroma dieses Steaks sagen?', '', 38134),
(203055, 'deDE', 'Zunächst einmal benötigen wir mehr Tee. Den gesamten Tee!', '', 38134),
(203054, 'deDE', 'Meine Existenz vor diesem Moment war nur eine Probe. Ich werde Euch alles beibringen, was Ihr wissen müsst!', '', 38134),
(203053, 'deDE', 'Gleichwohl freue auch ich mich. Und ich verlasse mich darauf, dass Ihr meine Kenntnisse der höfischen Etikette ein wenig auffrischen werdet.', '', 38134),
(203052, 'deDE', 'Ich glaube, die meisten dieser Gerüchte habt Ihr selbst in die Welt gesetzt, Theotar.', '', 38134),
(203051, 'deDE', 'Mein lieber Renathal, die Gerüchte sind wahr. Wir werden einen Hof hier in Sündensturz veranstalten!', '', 38134),
(203050, 'deDE', 'Ich zittere vor Aufregung! Diese Neuigkeiten können nicht warten!', '', 38134),
(203049, 'deDE', '', 'Wir erstatten dem Gluthof wie befohlen Bericht.', 38134),
(203047, 'deDE', '', 'Es liegt noch viel Arbeit vor uns.', 38134),
(203038, 'deDE', 'Kommt. Ich gebe Euch eine Chance zu erklären, warum Ihr bei mir eingedrungen seid.', 'Kommt. Ich gebe Euch eine Chance zu erklären, warum Ihr bei mir eingedrungen seid.', 38134),
(203037, 'deDE', 'Wartet... nicht feuern! Ich kenne $uden:die; hier.', 'Wartet... nicht feuern! Ich kenne $uden:die; hier.', 38134),
(203036, 'deDE', 'Wer wagt es, mein Reich zu betreten?', 'Wer wagt es, mein Reich zu betreten?', 38134),
(203035, 'deDE', 'Keinen Schritt weiter!', 'Keinen Schritt weiter!', 38134),
(203029, 'deDE', '', 'In der Falle. Hmpf. Dann los! Beweist uns Eure vielgerühmte Macht.', 38134),
(203028, 'deDE', 'Wer... ist da? Muss... den Markgrafen warnen...', '', 38134),
(203027, 'deDE', '', 'Länger als Ihr, Sterblicher. Mehr müsst Ihr nicht wissen.', 38134),
(203026, 'deDE', 'Wie lange seid Ihr schon hier?', '', 38134),
(203022, 'deDE', '', 'Dürre und Zwist schaden dem Hain. Wenn er erblüht, dann auch die Geister.', 38134),
(203015, 'deDE', 'Lysonia ist geschwächt, aber ich bin mir trotzdem nicht sicher, ob wir ihre furchterregende Stärke überwinden können.\n\nUnd doch müssen wir es versuchen. Steht Ihr mir bei?', '', 38134),
(203014, 'deDE', 'So viele Gefallene... und die Aufmerksamkeit des Paragons liegt ganz bei seiner Hand.\n\nWir müssen handeln, $gSterblicher:Sterbliche;.', '', 38134),
(203009, 'deDE', 'Wir kennen das Risiko. Aber wir müssen es versuchen.', '', 38134),
(203008, 'deDE', '', 'Es gibt nur einen Weg in das dunkle Reich. Denselben Weg, den alle verdammten Seelen beschreiten müssen.', 38134),
(203007, 'deDE', 'Geehrte Stimme, wir erbitten Einlass in den Schlund.', '', 38134),
(203006, 'deDE', 'Ich weiß nicht, wie wir den verschwundenen Baron oder unsere lebenden Verbündeten finden. Aber bei Darion Mograine kann ich Euch helfen. Immerhin ein Anfang.', '', 38134),
(203005, 'deDE', 'Es scheint, Ihr müsst Euch erneut den Schrecken im Schlund stellen, $n.', '', 38134),
(203002, 'deDE', '', 'Ein wirklich außergewöhnlicher Fund. Wenn man Verschlinger mit diesen Endmorastblüten füttert, werden sie ganz ruhig und lassen sich zähmen.\n\nAllerdings hält die Wirkung nur vorübergehend an. Damit das wilde Tier zahm bleibt, muss man ihm weiter Blumen geben.\n\nSeid Ihr bereit?', 38134),
(203000, 'deDE', '', 'Was habt Ihr für mich?', 38134),
(202999, 'deDE', '', 'Verschlinger sind faszinierende Kreaturen.', 38134),
(202998, 'deDE', 'Ich war in meinem Leben schon bei vielen Höfen.\n\nUnd das niemals freiwillig.', '', 38134),
(202997, 'deDE', 'Es gibt nur wenige vergnüglichere Aktivitäten, als mit Freunden einen guten Tee bei Hof zu verkosten.\n\nWenn Ihr einmal nicht wisst, was zu tun ist, dann gießt einfach Tee nach. Das löst jedes Problem!', '', 38134),
(202996, 'deDE', 'Ich besuche Höfe nicht ganz so gern wie Theotar und bin auch nicht so versessen darauf, sie zu organisieren, wie Temel.\n\nAber ich bin mit meinen eigenen Fähigkeiten ganz zufrieden. Ich habe seit Anbeginn dieses Reiches mit großem Einsatz Verbündete für uns gewonnen.\n\nDer Gluthof wird uns als mächtiges Werkzeug dienen, um den Ruhm und Einfluss unseres Paktes zu steigern.\n\nIch freue mich sehr, Euch bei diesem nächsten großen Vorhaben an meiner Seite zu wissen.', '', 38134),
(202992, 'deDE', 'Endlich Essen, das die Haare auf der Brust sprießen lässt!', '', 38134),
(202991, 'deDE', '', 'Ich erstatte in Nurakkir Bericht. Trefft mich dort.', 38134),
(202990, 'deDE', 'Argh! Schlundgänger... bringt es zu Ende!', '', 38134),
(202989, 'deDE', 'Sucht Ihr was?', '', 38134),
(202988, 'deDE', 'Ey! Was glotzt Ihr so?', '', 38134),
(202987, 'deDE', 'Was bringt Euch an Nourmans Tür?', '', 38134),
(202985, 'deDE', '', 'Unsere Kunsthandwerker sind schwer beschäftigt. Ich verspreche Euch einen herrlichen Anblick für den Gluthof!', 38134),
(202984, 'deDE', 'Der Gluthof ist hiermit offiziell eröffnet!', '', 38134),
(202983, 'deDE', 'Es ist alles so sauber! Der Bereich wird im Nu bereit sein!', '', 38134),
(202982, 'deDE', 'Wir müssen noch jede Menge saubermachen, bevor unser erster echter Hof starten kann!', '', 38134),
(202981, 'deDE', 'Nun, das war kein Hof, wie ich ihn gewohnt bin, aber wenn andere ihn genossen haben...', '', 38134),
(202980, 'deDE', 'Ein wundervoller förmlicher Hof! Gubbins und Tubbins müssen unbedingt zum nächsten kommen!', '', 38134),
(202979, 'deDE', 'Meine Pflicht ist getan.', '', 38134),
(202978, 'deDE', 'Ihr habt das Unmögliche möglich gemacht, $gSchlundgänger:Schlundgängerin;: einen Hof, der mir sehr gefallen hat. Ich bin beeindruckt.', '', 38134),
(202977, 'deDE', 'Natürlich, Temel. Beginnt mit der Koordination der Einladungen für unsere ersten Gäste von außerhalb.', '', 38134),
(202976, 'deDE', 'Ich bereite alles für unsere Rituale am Gluthof vor.', '', 38134),
(202975, 'deDE', 'Matschklatsch grummelt vor sich hin, während er sich mit der Schubkarre davonmacht.', '', 38134),
(202974, 'deDE', 'Deshalb seid Ihr auch nicht eingeladen, Matschklatsch! Und jetzt fort mit Euch!', '', 38134),
(202973, 'deDE', 'Schmuddelig gefällt es mir besser!', '', 38134),
(202972, 'deDE', 'Ja, ja, aber versucht weiterhin, sauberzumachen. Die Gräber haben schon wieder alles verschmuddelt!', '', 38134),
(202971, 'deDE', 'Ich werde Euch alle in wenigen Minuten zur Tributbühne rufen, wo die Abschlusszeremonie stattfindet.', '', 38134),
(202970, 'deDE', 'Genug der Vorträge, Temel. Lasst $gden Schlundgänger:die Schlundgängerin; den Probehof genießen!', '', 38134),
(202969, 'deDE', 'Gibt es einen direkten Konflikt, wie bei Theotar und Wachhauptmann Boromod, so müsst Ihr entscheiden, welcher Gast Euch wichtiger ist.', '', 38134),
(202968, 'deDE', 'Aktivitäten, die den einen erfreuen, sind dem anderen unangenehm.', '', 38134),
(202967, 'deDE', 'Ihr seht also, $gSchlundgänger:Schlundgängerin;, dass jeder Gast eine andere Art von Hof bevorzugt.', '', 38134),
(202966, 'deDE', 'Mein lieber Wachhauptmann, da muss ich Euch leider widersprechen. Ich habe den besten Höfen in ganz Revendreth beigewohnt und...', '', 38134),
(202965, 'deDE', 'Na, DAS war ein Spaß! Es sollte bei jedem Hof weniger Tee und mehr Kämpfe geben!', '', 38134),
(202934, 'deDE', '', 'Es freut mich, Euch diesen Segen zu erteilen. Wenn Ihr mir bitte folgen wollt.', 38134),
(202933, 'deDE', '', 'Und so gebe ich den Segen der Königin weiter, $p. Erhebt Euch.', 38134),
(202932, 'deDE', '', '\"Empfangt diesen Segen. Werdet eins mit den wilden Kreaturen. Welche Form Ihr auch braucht, Ihr sollt sie haben.\"', 38134),
(202931, 'deDE', '', '\"Wie, meine Königin?\", fragte die Seele. \"Ich habe keinen Körper mehr.\"', 38134),
(202930, 'deDE', '', '\"Kümmert Euch ewig um diese Geister. Der Kreislauf muss weitergehen, sonst ist alles aus.\"', 38134),
(202929, 'deDE', '', 'Die Seele kniete. \"Wie kann ich zu Diensten sein, o Königin?\"', 38134),
(202928, 'deDE', '', '\"Dann seid willkommen in meinem Reich\", antwortete die Königin.', 38134),
(202927, 'deDE', '', '\"Ich wurde zu Euch geschickt! Mein Leben war der Natur verschrieben.\"', 38134),
(202926, 'deDE', '', 'Unsere Königin traf sie am Tor. \"Ihr seid weder Geist noch Fae. Was führt Euch in meinen Wald?\"', 38134),
(202925, 'deDE', '', 'Vor langer Zeit, nach Bäumen und Nachtfae, erschien die erste sterbliche Seele im Ardenwald.', 38134),
(202923, 'deDE', 'Stellt neue Gegenstände bei Dactylis her, die Ihr noch nie hergestellt habt. Vielleicht müsst Ihr noch mehr Erinnerungen einfangen oder neue Ausrüstung ausprobieren!\n\nHabt Ihr eine bestimmte Erinnerung oder einen bestimmten Schwierigkeitsgrad eines Bosses noch nicht bezwungen, dann stellt Euch dieser Prüfung, um eine Belohnung zu erhalten. Wenn Ihr Glücksbringer herstellt und benutzt, kann das Euren Seelenbanden den benötigten Vorteil verschaffen.\n\nSucht immer nach neuen Herausforderungen. In der Nähe gibt es viele Händler mit tollen Belohnungen, die Ihr nur erhaltet, wenn Ihr bestimmte Herausforderungen auf dem Pfad des Aufstiegs überwindet. Versucht, sie alle zu sammeln!', '', 38134),
(202921, 'deDE', 'Manche Seelenbande haben über eine bestimmte Erinnerung eher einen Vorteil als andere. Eine Option besteht also darin, ein anderes Seelenband für den Kampf zu wählen.\n\nVersucht, Euer Seelenband mit Ausrüstung und Amuletten auszustatten. Ausrüstung gibt Eurem Seelenband eine aktive Fähigkeit, Amulette hingegen passive Stärkungen. Beide können den Ausgang eines Kampfes entscheiden. Ich kann Euch mehr darüber verraten, wenn Ihr wollt!\n\nUnd außerdem werden Eure Seelenbande passiv gestärkt, wenn Euer Ruhm innerhalb Eures Pakts steigt. Habt Ihr Schwierigkeiten mit einer Erinnerung, dann versucht, Euch Zeit zu lassen, Euren Ruhm zu steigern und Euer Seelenband zu stärken, bevor Ihr es erneut versucht.', '', 38134),
(202919, 'deDE', '', 'Wir werden mächtige Verbündete sein. Aber keine Freunde. Nicht, dass ich welche hätte.', 38134),
(202914, 'deDE', 'Anders als aktive Ausrüstung stärken Amulette Eure Seelenbande passiv. Sie können nur einmal benutzt werden, dann ist die Macht darin aufgebraucht. Stellt also viele davon her!\n\nIhr könnt zwar nur ein Amulett pro Sorte auf einmal ausrüsten, dafür aber so viele Sorten, wie Ihr wollt. Stellt sie einfach nach Euren Vorlieben zusammen.', '', 38134),
(202912, 'deDE', 'Manchmal begegnet Euch eine Herausforderung, die Euer Seelenband nicht allein bewältigen kann... Und da kommt Ausrüstung ins Spiel. Ausrüstung kann Euren Seelenbanden eine weitere Kampffertigkeit wie etwa einen Spurt verleihen.\n\nHabt Ihr ein Stück Ausrüstung hergestellt, könnt Ihr es auf ewig einsetzen. Sprecht einfach mit Paolone im Kolosseum, um Euer Seelenband auszustatten. Ihr könnt immer nur einen Gegenstand auf einmal ausrüsten, wählt also den, der Eurem Seelenband in der jeweiligen Situation am besten hilft.', '', 38134),
(202910, 'deDE', 'Der Pfad des Aufstiegs hält Ruhm und Ehre bereit, aber das müssen wir mit Handlungen der Demut ausbalancieren. Medaillons des Dienstes sind ein Beweis dafür und werden eingesetzt, um den Pfad in Angriff zu nehmen.\n\nIhr könnt diese Medaillons verdienen, indem Ihr Eure Pflicht gegenüber der Bastion erfüllt. Sie lassen sich am besten sammeln, indem Ihr Berufungen folgt, auf Schatzjagden geht oder seltene Gegner bezwingt. Benötigt Ihr mehr, könnt Ihr natürlich auch immer Materialien bei Dactylis spenden.', '', 38134),
(202909, 'deDE', 'Ich bin kein Freund vieler Worte, $gmein Herr:meine Dame;. Wie kann ich helfen?', 'Ich bin keine Freundin vieler Worte, $gmein Herr:meine Dame;. Wie kann ich helfen?', 38134),
(202908, 'deDE', 'Ich nehme zum ersten Mal an einem Hof teil. Ich werde mein Bestes geben.', 'Ich nehme zum ersten Mal an einem Hof teil. Ich werde mein Bestes geben.', 38134),
(202906, 'deDE', 'Glaubt Ihr, es wird Essen übrig bleiben?', 'Glaubt Ihr, es wird Essen übrig bleiben?', 38134),
(202905, 'deDE', 'Neue Baupläne findet Ihr durch das Besiegen von Erinnerungen im Kolosseum. Wenn Ihr sie zu Dactylis zurückbringt, könnt Ihr damit Köder, Amulette und Ausrüstung herstellen. Versucht, sie alle zu sammeln und mit den Möglichkeiten zu experimentieren.', '', 38134),
(202903, 'deDE', 'Ich bin bereit zu dienen.', 'Ich bin bereit zu dienen.', 38134),
(202902, 'deDE', 'Als ersten Schritt müsst Ihr dazu den Seelenspiegel ermächtigen, stärkere Erinnerungen hervorlocken zu können. Das erreicht Ihr, indem Ihr Euer Paktsanktum aufwertet.\n\nZweitens sind manche Prüfungen schwerer als andere. Ihr müsst Euch gegen genug Bosse beweisen, um eine härtere Herausforderung zu erschließen.\n\nUnd natürlich könnt Ihr nicht den höheren Schwierigkeitsgrad einer Erinnerung herausfordern, wenn Ihr nicht erst deren schwächere Versionen besiegt habt.', '', 38134),
(202900, 'deDE', 'Dactylis wird Euch wissen lassen, wenn Ihr neue Erinnerungen einfangen könnt. Für jede Erinnerung müsst Ihr einen speziellen Köder und einen Seelenspiegel herstellen. Dabei kann sie Euch auch helfen. Sobald Ihr sie Dactylis übergeben habt, gibt sie Euch weitere Anweisungen, wie Ihr die Erinnerung zurück ins Kolosseum bringt, um Eure Seelenbande trainieren zu können.', '', 38134),
(202899, 'deDE', 'Alle Störenfriede müssen zuerst an mir vorbei.', 'Alle Störenfriede müssen zuerst an mir vorbei.', 38134),
(202897, 'deDE', 'Ich erwarte Eure Befehle.', 'Ich erwarte Eure Befehle.', 38134),
(202896, 'deDE', 'Wie kann ich zu Diensten sein?', 'Wie kann ich zu Diensten sein?', 38134),
(202895, 'deDE', 'Für Sündensturz.', 'Für Sündensturz.', 38134),
(202893, 'deDE', '', 'Der Rest der Wildtiere wird gesammelt. Wir stehen bereit, wenn Ihr uns zum Gluthof ruft.', 38134),
(202892, 'deDE', 'Kalisthene hält viele Lektionen bereit. Es ist an Euch, sie zu lernen.', '', 38134),
(202889, 'deDE', 'Eine Geschichte für die Ewigkeit!', '', 38134),
(202888, 'deDE', 'Erinnert Euch an alles Gelernte.', '', 38134),
(202883, 'deDE', 'Verschlinger sind ausgehungert, wild, unersättlich. Tut, was Ihr tun müsst, um Euch in dieser Herausforderung zu schützen.', '', 38134),
(202881, 'deDE', 'Die Zeit ist reif für eine weitere Herausforderung auf dem Pfad. Wer tritt vor?', '', 38134),
(202880, 'deDE', 'Ein weiterer Kyrianer nimmt an dieser heiligen Tradition teil. Zeit, sich dem Pfad des Aufstiegs zu stellen!', '', 38134),
(202868, 'deDE', 'Nicht alle mögen dieselben Dinge wie Ihr, Theotar. Wir müssen unseren Hof nach den Vorlieben jedes einzelnen Gastes ausrichten.', '', 38134),
(202867, 'deDE', 'Ein paar Kämpfe wären eine angenehme Abwechslung von dieser förmlichen Angelegenheit.', '', 38134),
(202866, 'deDE', 'Wenn Ihr meine Laune verbessern wollt, dann duelliert Euch mit einem meiner neuen Rekruten.', '', 38134),
(202864, 'deDE', 'Pah! Ich habe kein Interesse an den albernen Förmlichkeiten des Hofes.', '', 38134),
(202863, 'deDE', 'Ich weiß ja nicht, Theotar... Boromod scheint kein großer Freund von Förmlichkeit zu sein...', '', 38134),
(202862, 'deDE', 'Beginnt mit unserem mürrischen Wachhauptmann. Er wird sich gewiss freuen, Teil der Förmlichkeiten des Hofes zu sein.', '', 38134),
(202861, 'deDE', 'Alle sollten sich ein Tässchen Tee gönnen!', '', 38134),
(202859, 'deDE', '', 'Pelagos gibt sich nie geschlagen. Niemals!', 38134),
(202856, 'deDE', '', 'Ein guter Kampf!', 38134),
(202854, 'deDE', 'Habt Dank, $gSchlundgänger:Schlundgängerin;. Die richtigen Umgangsformen sind bei einem Hof äußerst wichtig, ganz egal, wo er stattfindet.', '', 38134),
(202852, 'deDE', '', 'Habt Mut im Angesicht aller Widrigkeiten.', 38134),
(202851, 'deDE', '', 'Hier ist ein Rat für Euch, Pelagos. Haltet Eure Gegner immer auf Distanz.', 38134),
(202849, 'deDE', '', 'Konzentration, Kleia. Zeigt Euch der Herausforderung gewachsen!', 38134),
(202848, 'deDE', '', 'Ah, Athanos. Einer meiner Favoriten. Hoffen wir, dass Ihr besser seid als Eure Vorgänger!', 38134),
(202847, 'deDE', '', 'Durch die Geißel des Lichts in den Wahnsinn getrieben. Das wird ein harter Kampf!', 38134),
(202846, 'deDE', '', 'Ein Lich ist ein sehr gefährlicher Gegner. Manche sagen, das liegt nur an ihren Phylakterien... Ich weiß es besser.', 38134),
(202844, 'deDE', '', 'Nicht einer, nicht zwei, sondern drei Gegner in einer Herausforderung! Lasst Euch nicht überrennen.', 38134),
(202843, 'deDE', '', 'Tirnenn sind sanfte Geschöpfe, aber ihre Naturverbundenheit macht sie zu gefährlichen Gegnern.', 38134),
(202842, 'deDE', '', 'Die Soldaten der Wilden Jagd schrecken vor keiner Herausforderung zurück. Zeigt uns, ob Ihr ihnen gewachsen seid.', 38134),
(202841, 'deDE', '', 'Die Dame des Hasses... Ich will gar nicht wissen, wie sie sich diesen Titel verdient hat.', 38134),
(202837, 'deDE', '', 'Das sieht nicht gut aus. Ganz und gar nicht.', 38134),
(202834, 'deDE', '', 'Manche Dinge sind unwiderstehlich!', 38134),
(202833, 'deDE', '', 'Auf die Beine und macht weiter!', 38134),
(202832, 'deDE', '', 'Es wird wohl Zeit für eine Pause!', 38134),
(202831, 'deDE', '', 'So leicht geben sich wahre Kämpfer nicht geschlagen!', 38134),
(202830, 'deDE', '', 'Bleibt stark! Es ist fast geschafft!', 38134),
(202822, 'deDE', '', 'Mit jeder Niederlage wird der Sieg am Ende umso süßer!', 38134),
(202821, 'deDE', '', 'Ah, eine Lektion in Demut.', 38134),
(202820, 'deDE', '', 'Staub abklopfen und auf ein Neues.', 38134),
(202817, 'deDE', '', 'Darauf habe ich mich den ganzen Tag gefreut. Zeit für eine neue Prüfung!', 38134),
(202816, 'deDE', '', 'Auf eine neue Prüfung!', 38134),
(202815, 'deDE', 'Ich bereite für alle Fälle noch eine Kanne zu.', '', 38134),
(202814, 'deDE', 'Serviert den Tee, solange er noch heiß ist.', '', 38134),
(202813, 'deDE', '', 'Was für eine herrliche Vorstellung! Schon bei unserem ersten Treffen wusste ich, dass Ihr für Großes bestimmt seid.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202812, 'deDE', '', 'Aspiranten, seht $p, $uder:die; dem mächtigen Athanos die Stirn geboten hat!', 38134),
(202811, 'deDE', '', 'Der Speer ist fürchterlich schwer. Ihr könnt ihn nur kurz anheben. Der richtige Zeitpunkt ist der Schlüssel!', 38134),
(202810, 'deDE', '', 'Kalisthene behütet und unterweist die Aspiranten seit Äonen. Mal sehen, wie Ihr Euch schlagt.', 38134),
(202809, 'deDE', 'Ihr habt Euch verbessert, $p. Meine Erinnerung gehört Euch.', '', 38134),
(202808, 'deDE', '', 'Ihr müsst wohl noch Einiges lernen.', 38134),
(202807, 'deDE', 'Mein lieber Temel, woher wusstet Ihr, dass \"der teuerste\" mein Lieblingsgeschmack ist?', '', 38134),
(202806, 'deDE', '$gSchlundgänger:Schlundgängerin;, bitte serviert Theotar eine Tasse unseres teuersten Tees.', '', 38134),
(202804, 'deDE', 'Was meint Ihr? Sind wir bereit, Gäste von außerhalb zum Gluthof zu laden?', '', 38134),
(202803, 'deDE', 'Ihr wollt förmlich? Dann bieten wir Euch förmlich!', '', 38134),
(202802, 'deDE', 'Ich muss jedoch zugeben, dass jene aus den einflussreichsten Kreisen... förmlichere Anlässe gewohnt sind.', '', 38134),
(202801, 'deDE', 'Nun, das ist auf jeden Fall ein Anfang, mein lieber Prinz.', '', 38134),
(202800, 'deDE', 'Theotar, Ihr habt die Heiterkeit solcher sozialer Anlässe immer genossen.', '', 38134),
(202799, 'deDE', 'Ich diene, wo ich gebraucht werde.', '', 38134),
(202798, 'deDE', 'Prinz Renathal zwinkert Wachhauptmann Boromod zu.', '', 38134),
(202797, 'deDE', 'Die feine Gesellschaft und der Adel scheinen sich auch zu amüsieren, nur unser Wachhauptmann ist nicht in Stimmung.', '', 38134),
(202796, 'deDE', 'Ich bin beeindruckt, Temel. Ihr und $gder Schlundgänger:die Schlundgängerin; habt bereits einiges geschafft!', '', 38134),
(202795, 'deDE', 'Hier kommt Prinz Renathal mit unserem Ehrengast!', '', 38134),
(202794, 'deDE', 'Sehr gut. Und jetzt das Gleiche mit dem Bottich mit Enzymen.', '', 38134),
(202793, 'deDE', '', 'Majestätische Kreaturen, nicht? Sie wissen das auch! So stolz.', 38134),
(202792, 'deDE', '', 'Vor langer Zeit haben wir mit seiner Magie jegliche Flucht aus dem Schlund verhindert. Auf dass der Verbannte ewig gefesselt sein würde.', 38134),
(202790, 'deDE', 'Alle Gräber arbeiten hart daran, für Prinz Renathal alles perfekt herzurichten.', '', 38134),
(202789, 'deDE', 'Ich könnte bei dieser Gluthofprobe Eure Hilfe brauchen.', '', 38134),
(202786, 'deDE', 'Mein Augenstern. Ich..i Ich werde es tun.', '', 38134),
(202785, 'deDE', '', 'Aber bitte ich dich: Sag uns, ob sie noch leben.', 38134),
(202784, 'deDE', '', 'Ich habe dich nie um etwas gebeten, seit ich ein Kind war. Das Kind, das du weggeschickt hast.', 38134),
(202783, 'deDE', '', 'Ich bin ins Reich des Todes gekommen, um den Vater zu suchen, den ich verloren glaubte. Bolvar Fordragon, einer der größten Helden von Azeroth.', 38134),
(202782, 'deDE', 'Du verstehst nicht, wie groß das Risiko ist, Taelia. In den Schlund zu sehen, wäre...', '', 38134),
(202781, 'deDE', '', 'Bitte, Vater. Wir müssen wissen, ob unsere Freunde noch zu retten sind!', 38134),
(202780, 'deDE', 'Ich genieße jetzt mein neues Leben! Neue Leben? Halten wir uns an eines, ja? Wie auch immer, lebt wohl!', '', 38134),
(202779, 'deDE', 'Viel Glück mit Eurer Arena oder wovon Ihr da auch immer gefaselt habt.', '', 38134),
(202778, 'deDE', 'Hervorragend! Das hätte keiner von uns erwartet, aber so ist das eben mit der Wissenschaft!', '', 38134),
(202775, 'deDE', 'Gut, gut. Das wird ihm gefallen.', '', 38134),
(202774, 'deDE', 'Ein Mann mit Geschmack? Ich werde eine Kanne meines besten Tees zubereiten.', '', 38134),
(202773, 'deDE', 'Wenn Ihr in dieser Hütte Tee habt, Stefan, wird unser Gast das sicher zu schätzen wissen.', '', 38134),
(202772, 'deDE', 'Prinz Renathal bringt einen Gast mit? Du liebe Zeit, ich werde eine besondere Erfrischung vorbereiten.', '', 38134),
(202771, 'deDE', 'Den Rest machen wir später sauber. Prinz Renathal sollte bald mit dem Ehrengast hier sein.', '', 38134),
(202770, 'deDE', 'Seht Euch $gdiesen Meisterschrubber:diese Meisterschrubberin; an! Ihr Gräber könnt Euch das ein oder andere $gvom Schlundgänger:von der Schlundgängerin; abschauen.', '', 38134),
(202769, 'deDE', 'Ihr täuscht keine Müdigkeit vor, $gSchlundgänger:Schlundgängerin;! Macht auch den Rest sauber, bevor unsere Gäste eintreffen.', '', 38134),
(202768, 'deDE', 'Das sieht schon besser aus! Ich liebe saubere Höfe.', '', 38134),
(202767, 'deDE', 'Ihr zwei übernehmt die Südpatrouille. Ich kümmere mich um die Zivilisten.', '', 38134),
(202766, 'deDE', '', 'Auf Nimmerwiedersehen. Traut ihm nicht, Jägerin.', 38134),
(202765, 'deDE', 'Alles klar, Boss.', '', 38134),
(202763, 'deDE', 'Ich hätte ein besseres Gefühl bei diesem Probehof, wenn der Erfrischungsbereich etwas sauberer wäre.', '', 38134),
(202762, 'deDE', 'Kümmert Euch nicht um die feine Gesellschaft. Wir müssen uns nur darauf konzentrieren, auf jeden Fall die wichtigsten Gäste zu beeindrucken.', '', 38134),
(202761, 'deDE', 'Oh, das hätte ich fast vergessen: Ich habe zur Probe ein paar der Paktmitglieder eingeladen.', '', 38134),
(202760, 'deDE', 'Aber es ist das reinste Chaos! Wir müssen alles aufräumen, bevor wir hier Hof halten können.', '', 38134),
(202759, 'deDE', 'Da seid Ihr ja, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(202758, 'deDE', 'Wenn Ihr je verwirrt seid oder Rat benötigt, bin ich zur Stelle. Mögt Ihr und Eure Seelenbande diesen Pfad stolz beschreiten.', '', 38134),
(202757, 'deDE', '', 'Dactylis kann Euch dabei helfen, neue Erinnerungen zu erhalten, gegen die Ihr trainieren könnt. Aber auch Zaubersprüche und Ausrüstung, die Euren Seelenbanden einen Vorteil verschaffen können.', 38134),
(202756, 'deDE', '', 'Die nächsten Schritte obliegen Euch und Euren Seelenbanden, $n. Ruhm erwartet Euch, aber auch Lektionen.', 38134),
(202755, 'deDE', '', 'Ab jetzt erfordert jeder Versuch auf dem Pfad ein Medaillon des Dienstes, den Beweis einer erfüllten Pflicht gegenüber der Bastion. Der Pfad birgt Ruhm, aber wir müssen am Boden bleiben. Hier sind einige für den Anfang.', 38134),
(202752, 'deDE', 'Ausrüstung sicher bei mir. Benutzen? Kommt zu mir! Im Kolosseum!', '', 38134),
(202741, 'deDE', 'Hier, dieser Teleporter wird Euch zum Ring der Übertragung zurückbringen.', '', 38134),
(202735, 'deDE', 'Jetzt oder nie, $p. Benutzt Euren Spiegel und bringen wir es hinter uns!', '', 38134),
(202734, 'deDE', 'Ich bin... wir sind... alle ich? Wir sind alle ich!', '', 38134),
(202733, 'deDE', 'Was... was ist passiert? Wo bin ich?', '', 38134),
(202727, 'deDE', 'Kerzen in den Hof! Stühle zu den Erfrischungen! Laternen zur Bühne!', '', 38134),
(202709, 'deDE', '', 'Wie Ihr wünscht.', 38134),
(202706, 'deDE', 'Erst müsst Ihr an mir vorbei!', '', 38134),
(202701, 'deDE', '', 'Jetzt lernt Ihr, wie dumm es ist, dem Haus der Rituale zu trotzen!', 38134),
(202698, 'deDE', 'Sehr gut, arbeitet hart...', '', 38134),
(202697, 'deDE', 'Tubbins macht jetzt Tee. Bester Tee! Alle sagen, ich bin großartig!', '', 38134),
(202696, 'deDE', 'Neeeeeiin!!!', '', 38134),
(202694, 'deDE', 'Gut, es sieht schlimm aus. Ich gebe Euch recht.', '', 38134),
(202693, 'deDE', 'Ooh, vielleicht drei Spritzer... lassen wir es krachen!', '', 38134),
(202692, 'deDE', 'Und zu guter Letzt noch Euer Katalysator! Vorsichtig umrühren und...', '', 38134),
(202691, 'deDE', 'Nur eine Prise Knochenpulver... ein Spritzer Schleim...', '', 38134),
(202690, 'deDE', 'Seht die Wunder von Äonen alchemistischer Forschung und verschlissener Gehilfen!', '', 38134),
(202688, 'deDE', 'Vielleicht steige ich nicht so schnell wie andere auf, aber ich bin wirklich glücklich mit meinem gewählten Pfad, $p.\n\nNur wenige Kyrianer sprechen über ihre Vergangenheit oder erinnern sich an sie! Als Volk haben wir in dieser Hinsicht noch eine Menge zu lernen.\n\nAls ich es Kleia erzählt habe, hörte sie einfach zu. Genau wie Ihr. Und ihre Freundschaft blieb unerschüttert. Genau wie Eure.\n\nHabt Dank. Ich bin froh, dass ich meine Geschichte mit Euch teilen durfte.', '', 38134),
(202686, 'deDE', 'Aber als ich in der Bastion als Aspirant ankam, der aussah wie...\n\n<Pelagos deutet auf sich selbst.>\n\nWie das! Ein männlicher Körper!\n\nDa fühlte ich mich... wohl! Begeistert! Als würde mein Äußeres zum ersten Mal zu meinem Inneren passen!\n\nDiese ganzen Gefühle ergaben auf einmal Sinn.', '', 38134),
(202684, 'deDE', 'Danke, dass Ihr mir zuhört. Wirklich. Durch unser Seelenband weiß ich, dass ich Euch vertrauen kann.\n\n<Pelagos atmet tief ein und dann langsam wieder aus.>\n\nIch war im Leben weiblich. Ich erinnere mich nicht an meinen alten Namen oder mein Volk, aber nie... habe ich gefühlt, dass mein Körper mein Inneres widerspiegelt.\n\nDas war mir noch nie so klar wie jetzt. Mit dieser Identität habe ich mein ganzes Leben gerungen...', '', 38134),
(202682, 'deDE', 'Bis auf eine.\n\nEs fällt schwer, daran zu denken. Ich sollte sie ziehen lassen, aber... ich will... ich muss... mich daran erinnern.\n\nSie ist zu sehr ein Teil von mir, um sie zu vergessen.', '', 38134),
(202680, 'deDE', 'Nur noch wenig in letzter Zeit.\n\nDie Erinnerungen schwinden, je weiter mein früheres Leben in die Ferne rückt. Und viele andere habe ich willentlich ziehen lassen, um meine Pflichten hier zu erfüllen.\n\nBis auf...', '', 38134),
(202677, 'deDE', 'Sie haben gesagt, ich wäre wirr im Kopf! Und sie sagen es immer noch!\n\nDas ändert sich ab heute!', '', 38134),
(202667, 'deDE', 'Durch harte Arbeit wachsen und dehnen sich sogar die Mächtigen...', '', 38134),
(202665, 'deDE', 'Langsam, langsam, Jungblut.', '', 38134),
(202663, 'deDE', '', 'Der Spiegel hinter Euch enthält alle von Euch gesammelten Erinnerungen. Wählt damit Euren Gegner.', 38134),
(202662, 'deDE', 'Ihr müsst durch die Pilzringe gehen, um die Inseln zu erreichen. Der Zugang ist beschränkt, aber versucht es.', 'Ihr müsst durch die Pilzringe gehen, um die Inseln zu erreichen. Der Zugang ist beschränkt, aber versucht es.', 38134),
(202661, 'deDE', 'Heute geht Pelagos seine ersten Schritte auf dem Pfad des Aufstiegs. Möge er den zahllosen Kyrianern vor ihm folgen!', '', 38134),
(202659, 'deDE', '', 'Sagt Bescheid, wenn Ihr so weit seid und ich aktiviere das Gerät. Es wird uns nicht lange schützen.', 38134),
(202658, 'deDE', '', 'Ertrinkt in Schrecken!', 38134),
(202654, 'deDE', '', 'Aus Träumen erwächst Hoffnung. Nährt die Eichel mit Liedern und Gelächter.', 38134),
(202653, 'deDE', 'Ihr und Eure Seelenbande wandelt in den Fußspuren vieler anderer. Es gibt keine größere Ehre oder Tradition, als den Pfad des Aufstiegs zu beschreiten!', '', 38134),
(202627, 'deDE', '', 'So soll es sein.', 38134),
(202626, 'deDE', '', 'Ich spüre Geister der Kaldorei in Eurem Seelenbewahrer, $n. Lasst sie im Ardenwald frei.', 38134),
(202625, 'deDE', 'GLÜCKSkanne...', '', 38134),
(202624, 'deDE', 'Nie kalt! Nie kaputt! Nie leer!', '', 38134),
(202623, 'deDE', 'Vorsicht! Das is meine Glückskanne.', '', 38134),
(202621, 'deDE', '', '<In den Augen der Winterkönigin blitzt eine kaum gebändigte Macht auf. Sie blickt wohlwollend auf Euch herab.>\n\nGut gekämpft, $gSterblicher:Sterbliche;.\n\nKommt. Überlassen wir ihre Knochen den Gorm.', 38134),
(202619, 'deDE', '', 'Raaargh! Elende Königin! Die Drust sind unbesiegbar!', 38134),
(202618, 'deDE', '', 'Kommt, Sterblicher. Beseitigen wir die Kreatur.', 38134),
(202616, 'deDE', '', 'Ich darf ihre Spur nicht verlieren. Wir treffen uns später.', 38134),
(202615, 'deDE', '', 'Seht! Die Magie meiner Haine stellt sich gegen Euch!', 38134),
(202614, 'deDE', '', 'Seid gewarnt. Eine Macht will sie verschlingen. Ich bete, dass Ihr und Shandris sie davon befreien könnt.', 38134),
(202613, 'deDE', 'Shandris, das ist unsere einzige Hoffnung, Tyrande zu retten.', '', 38134),
(202611, 'deDE', '', 'Euer Geist verströmt Freude.\n\nGebt diese glücklichen Erinnerungen weiter, dann haben wir bald ein neues Lied für den Herzholzhain.', 38134),
(202602, 'deDE', 'Auf Euren Wunsch werden wir dem Gluthof mit einigen faszinierenden Gegenständen der Sterblichen beiwohnen. Vielleicht erblicken Eure Gäste etwas, das sie kaufen möchten.', 'Auf Euren Wunsch werden wir dem Gluthof mit einigen faszinierenden Gegenständen der Sterblichen beiwohnen. Vielleicht erblicken Eure Gäste etwas, das sie kaufen möchten.', 38134),
(202600, 'deDE', '', 'Ihr kommt gerade recht, Schlundgänger.', 38134),
(202599, 'deDE', '', 'Der Feind kommt näher. Wir können die Stellung kaum halten.', 38134),
(202588, 'deDE', 'Ich bin jederzeit bereit. Oh, das ist so aufregend!', '', 38134),
(202582, 'deDE', '', 'Marileth lehrt Euch, was es heißt, zwei Seelen aneinander zu binden.', 38134),
(202581, 'deDE', 'Der Fluss ist kein Ort für Lebende. Wir sollten ihn um jeden Preis meiden.', '', 38134),
(202580, 'deDE', 'Seht, wie sie sich abmühen! Schon bald dürfen wir das Fleisch der auserwählten Exilanten und ihrer Kyrianer formen.', 'Seht, wie sie sich abmühen! Schon bald dürfen wir das Fleisch der auserwählten Exilanten und ihrer Kyrianer formen.', 38134),
(202576, 'deDE', '', 'Vergeudet diese Gelegenheit nicht, Schlundgänger.', 38134),
(202575, 'deDE', 'Es ist eine große Ehre, die Macht des Primus zu führen.', '', 38134),
(202568, 'deDE', '', 'Dieser Hain braucht ein neues Herz. Aber solch ein Lied zu singen, dauert viele Jahreszeiten.\n\nEin neues Herz beginnt mit vielen frohen Erinnerungen. Ich fühle sie in den Wurzeln dieses Hains.\n\nNehmt diese Eichel. Sie verleiht Euch ein Auge für diese Erinnerungen.\n\nFindet sie und bringt sie mir.', 38134),
(202567, 'deDE', '', 'Wie schön, dass Ihr hier seid.', 38134),
(202565, 'deDE', 'Sylvanas! Was hat sie bloß mit ihm vor?', '', 38134),
(202561, 'deDE', 'Möchtet Ihr etwas Bestimmtes kaufen?', 'Möchtet Ihr etwas Bestimmtes kaufen?', 38134),
(202559, 'deDE', 'Diese mechanische Kreatur sieht aus, als könnte sie einige Reparaturen brauchen.', '', 38134),
(202558, 'deDE', 'Das Brandmal... hat den... Schlüssel...', '', 38134),
(202557, 'deDE', '', 'Überlasst das mir.', 38134),
(202556, 'deDE', 'Die Drust versperren uns den Weg!', '', 38134),
(202555, 'deDE', '', 'Ich habe gelernt, hier zu überleben. Der Kerkermeister bemerkt Euch, wenn Ihr seine Truppen angreift, also solltet Ihr sie umgehen, wann immer Ihr könnt.', 38134),
(202554, 'deDE', '', 'Eure Anwesenheit zieht Aufmerksamkeit auf sich, die für meine Geschäfte nicht gut ist, ohne Euch zu nahe treten zu wollen.', 38134),
(202553, 'deDE', 'Warum helft Ihr uns, Mittlerin?', '', 38134),
(202552, 'deDE', '', 'Ich bin Ve\'nari, eine einfache Mittlerin in diesem Land der Qual. Ich habe einen Vorschlag für Euch. Kommt!', 38134),
(202551, 'deDE', '', 'Ah, ich habe mich schon gefragt, wonach die Schlundgebundenen gesucht haben. Ihr habt doch das Fest des Kerkermeisters ruiniert...', 38134),
(202549, 'deDE', 'Die Ablenkung war effektiv, das muss man ihnen lassen. Ich werde ihre Niederlage nun umso mehr genießen.\n\n<Herne grinst breit.>\n\nSollen wir beginnen?', '', 38134),
(202548, 'deDE', '', 'Nach der Dürre können wir aus dieser Eichel ein neues Herz des Hains ziehen. Habt Dank.', 38134),
(202547, 'deDE', 'Diese elenden Kreaturen werden meine Klinge zu spüren bekommen, bevor es vorbei ist.', '', 38134),
(202546, 'deDE', '', 'Der Herzholzhain ist verkümmert, aber die Träume seiner Fae bleiben. Könnt Ihr Zayhad helfen, sie zu sammeln?', 38134),
(202536, 'deDE', 'Fügt dem Kessel den Katalysator hinzu, damit wir die Macht meiner Schöpfung bestaunen können!', '', 38134),
(202535, 'deDE', 'Es überrascht mich nicht, dass sich die Kunde meines Genies bis zur Bastion verbreitet hat. Diese neue Schöpfung wird meine bisher stärkste und könnte Eure arglosen Kyrianer viel über Brutalität und Chaos lehren!', '', 38134),
(202534, 'deDE', 'Wir werden die Schattenlande vor äußeren und inneren Bedrohungen schützen!', '', 38134),
(202529, 'deDE', 'Ja mein Prinz. Leicht verletzt, aber ungebrochen.', '', 38134),
(202528, 'deDE', '', 'Regel 2: Bleibt unauffällig. Der Schlund ist kein Ort für Theater. Geht rein, holt, was Ihr wollt, und dann raus... Und versucht, weder Glieder noch sonst was zu verlieren.', 38134),
(202527, 'deDE', 'Freunde! Schützt Euch!', '', 38134),
(202525, 'deDE', 'Verbündete, bleibt stark!', '', 38134),
(202524, 'deDE', 'Lebt Ihr noch, alter Freund?', '', 38134),
(202522, 'deDE', '', 'Habt Dank. Es erstaunt mich immer wieder, was sie anrichten!', 38134),
(202521, 'deDE', '', 'Die Hüter der Gormlinge brauchen Hilfe. Könntet Ihr mithelfen?', 38134),
(202520, 'deDE', '', 'Doch keine Angst, sterbliche Seele. Bleibt seinem Blick einen Tag fern und der Kerkermeister wendet sich von Euch ab. Dann könnt Ihr Euch wieder hinauswagen. Um Stygia zu sammeln. Für mich.', 38134),
(202519, 'deDE', '', 'Die gute Nachricht: Ihr seid hier in meiner Zuflucht sicher. Da draußen nicht. Sollte Euch etwas zustoßen, könnte Euch ein bisschen wertvolle Stygia durch die Lappen gehen.', 38134),
(202518, 'deDE', '', 'Zuerst hetzt er die Seelensucher auf Euch. Dann die Türme. Dann die Todesschwadronen. Und dann... Das wollt Ihr gar nicht wissen. Methodisch und akribisch vorgehen... so bleibt man am Leben.', 38134),
(202517, 'deDE', '', 'Da Euch der Kerkermeister bemerkt hat, seid ihr verdammt. Wenn Ihr etwas Bedeutungsvolles im Schlund tut, zieht das seine Aufmerksamkeit auf Euch. Was wir nicht wollen.', 38134),
(202516, 'deDE', '', 'Ihr dürft nicht passieren.', 38134),
(202515, 'deDE', '', 'Wir bewachen das Herz des Waldes. Zutritt haben nur die Wilde Jagd, der Hof des Winters und jene in offiziellem Auftrag.', 38134),
(202514, 'deDE', '', 'Seid gegrüßt.$b$BNur wer mit der Wilden Jagd reitet, ist im Herz des Waldes willkommen.', 38134),
(202513, 'deDE', '', 'Ihr seid willkommen im Herz des Waldes. Bitte tretet ein.', 38134),
(202512, 'deDE', '', 'Wir halten Wacht, für Königin und Hain.', 38134),
(202511, 'deDE', '', 'Willkommen zurück. Wie läuft Eure Jagd?', 38134),
(202509, 'deDE', '', 'Keine Zeit für Trauer. Wir müssen uns beeilen!', 38134),
(202508, 'deDE', '', 'Lady Mondbeere zwinkert.', 38134),
(202507, 'deDE', '', 'Der Gluthof wird sicherlich ein netter und würdiger Anlass.', 38134),
(202499, 'deDE', '', 'Wie kann ich Euch behilflich sein?', 38134),
(202498, 'deDE', '', 'Meine Baronin hatte recht mit Euch, $p. Wir sammeln uns in Nurakkir.', 38134),
(202497, 'deDE', 'Die Drust haben sich eingegraben. Wir müssen entschieden zuschlagen.', '', 38134),
(202496, 'deDE', '', 'Eure Taten zeichnen Euch aus. Ihr seid der Gunst der Bastion würdig. Heraclor steht!', 38134),
(202495, 'deDE', '', 'Die Verschmähten wollen einen unserer stärksten Verteidiger ausschalten. Findet den Zenturiokoloss namens Heraclor und verteidigt ihn um jeden Preis!', 38134),
(202493, 'deDE', '', 'Lichs sind schlau und arrogant. Rechnet mit Widerstand.', 38134),
(202492, 'deDE', '', 'Ingra Drif... lasst ihn ordentlich leiden.', 38134),
(202491, 'deDE', 'Ihr habt mich vor einem schrecklichen Schicksal bewahrt! Gut, dass ich auf Eurer Seite bin!', '', 38134),
(202490, 'deDE', 'Devos\' Nachfolgerin Lysonia ist eine große Gefahr. Ich werde sofort Bringer senden, um ihre Pläne zu entlarven.', '', 38134),
(202489, 'deDE', 'Ruhm der Archon. Ruhm der Bastion!', '', 38134),
(202488, 'deDE', 'Damit können unsere Aspiranten wieder aufsteigen und das Reich von den Verschmähten befreien!', '', 38134),
(202487, 'deDE', 'Unser neu geschmiedetes Emblem ist stärker denn je!', '', 38134),
(202486, 'deDE', 'Aber dank des Schlundgängers waren wir standhaft.', '', 38134),
(202485, 'deDE', 'Als Devos das Emblem zerstört hat, wollte sie damit unsere Bande zertrennen.', '', 38134),
(202484, 'deDE', 'Ihr habt unserem Volk einen großen Dienst erwiesen. Jetzt müssen wir die Macht des wiedergeborenen Emblems gegen künftige Angriffe versiegeln.', '', 38134),
(202472, 'deDE', '', 'Seid Ihr bereit, Euch den Nekrolords anzuschließen?', 38134),
(202469, 'deDE', '', 'Baronin! Ihr seid zurückgekehrt!', 38134),
(202460, 'deDE', 'Wahrscheinlich nur einer der Gräber, der einen neuen Sündenstein auf den Haufen geworfen hat.', '', 38134),
(202457, 'deDE', 'Hat sich dieser Berg Sündensteine gerade bewegt?', '', 38134),
(202447, 'deDE', '', 'Wie viel müssen wir noch verlieren?', 38134),
(202420, 'deDE', '', 'Alle schauen... Hallo! Komme in Freundschaft!', 38134),
(202419, 'deDE', 'Ich bitte um Eure Aufmerksamkeit! Ich erhebe mein Glas auf die hilfreichste Person, die ich seit der Erschaffung von Gubbins getroffen habe. Sie ist extra aus der Bastion angereist... Sika!', '', 38134),
(202413, 'deDE', 'Ehrfurchtgebietende Stärke, beispielloser Mut und eine furchterregende Opferquote. Niemand kann es mit ihm aufnehmen. Einen Applaus für... GROSSMEISTER VOOOOOLE!', '', 38134),
(202412, 'deDE', 'Danke, ich übernehme mal, Theotar.', '', 38134),
(202411, 'deDE', 'Ich bitte um Eure Aufmerksamkeit! Ich erhebe mein Glas auf die Hauptfigur des Theaters der Schmerzen, den einzigartigen...', '', 38134),
(202410, 'deDE', '', 'Im Ardenwald ist alles groß. Kommt uns mal besuchen.', 38134),
(202409, 'deDE', 'Ich bitte um Eure Aufmerksamkeit! Ich erhebe mein Glas auf die unbeugsame Beschützerin des Ardenwaldes und den größten Bären, den ich je gesehen habe, Dromanin Aliothe!', '', 38134),
(202380, 'deDE', '', 'Zumindest hoffe ich das...', 38134),
(202379, 'deDE', '', 'Nichts ist für immer verloren, Prinz Renathal.', 38134),
(202378, 'deDE', 'Dieser hier zählt zu den ersten... Ich dachte, dieser Sündenstein wäre für immer verloren...', '', 38134),
(202377, 'deDE', 'Prinz Renathal untersucht den Sündenstein mit großem Interesse.', '', 38134),
(202375, 'deDE', 'Diese Fledermaus scheint Euer Ausweg aus den Blutigen Tiefen zu sein.', 'Diese Fledermaus scheint Euer Ausweg aus den Blutigen Tiefen zu sein.', 38134),
(202373, 'deDE', 'Bolvar glaubt zu wissen, wohin die Banshee ihre Opfer entführt hat.', '', 38134),
(202368, 'deDE', '', 'Wir stellen Seelennetze für einen Zweck her, wie auch alles sonst im Ardenwald. Die meisten bewahren Anima auf oder setzen sie frei, so wie die hier in der Gegend. Aber das hier... Das hier ist anders.$b$bDas hier fängt Träume.$b$bLeider kann ich nur seine Form wiederherstellen, aber nicht seinen Zweck. Ihr müsst jemanden finden, der dem Seelennetz die Macht der Träume verleihen kann.', 38134),
(202367, 'deDE', '', '<Gwenna untersucht die Fragmente zögerlich.>$b$bOh, du armes Ding. Was ist passiert?$b$BAh, Tirna Scithe. Dessen Schmerz ist wohlbekannt. Ja, ja, in einem Flügelschlag bist du wieder wie neu!$b$BHabt Dank, dass Ihr mir dies gebracht habt. Zu oft werfen wir Dinge weg, die noch nützlich sein können. Wenn Ihr mir zehn Stück lichtlose Seide bringt, kann ich es reparieren.', 38134),
(202366, 'deDE', 'Mit der Macht der Archive haben wir stets ein wachsames Auge auf die gesamte Bastion.', 'Mit der Macht der Archive haben wir stets ein wachsames Auge auf die gesamte Bastion.', 38134),
(202365, 'deDE', 'Mit der Macht der Archive haben wir stets ein wachsames Auge auf die gesamte Bastion.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Wache der Weisen]|h|r: Wenn Eure Gesundheit sehr niedrig ist, heilt ein Aufgestiegener Euch und stößt Gegner in der Nähe zurück. Ihr erleidet weniger Schaden und dieser Effekt nimmt rapide ab. Kann nur einmal alle paar Minuten auftreten.', 'Mit der Macht der Archive haben wir stets ein wachsames Auge auf die gesamte Bastion.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[Wache der Weisen]|h|r: Wenn Eure Gesundheit sehr niedrig ist, heilt ein Aufgestiegener Euch und stößt Gegner in der Nähe zurück. Ihr erleidet weniger Schaden und dieser Effekt nimmt rapide ab. Kann nur einmal alle paar Minuten auftreten.', 38134),
(202364, 'deDE', 'Es ist an der Zeit, dass die Vesperglocken wieder den Ruf nach Frieden und Klarheit erschallen lassen.', 'Es ist an der Zeit, dass die Vesperglocken wieder den Ruf nach Frieden und Klarheit erschallen lassen.', 38134),
(202363, 'deDE', 'Es ist an der Zeit, dass die Vesperglocken wieder den Ruf nach Frieden und Klarheit erschallen lassen.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Vesperglocke der Klarheit]|h|r: Wenn Ihr viele Gegner versammelt, haben Eure Schadenszauber und -fähigkeiten eine Chance, kurz die Vesperglocke der Klarheit zu beschwören. Die Vesperglocke der Klarheit betäubt Gegner in der Nähe, wenn sie erscheint, und erhöht Euren verursachten Schaden, solange Ihr in ihrer Nähe seid.', 'Es ist an der Zeit, dass die Vesperglocken wieder den Ruf nach Frieden und Klarheit erschallen lassen.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[Vesperglocke der Klarheit]|h|r: Wenn Ihr viele Gegner versammelt, haben Eure Schadenszauber und -fähigkeiten eine Chance, kurz die Vesperglocke der Klarheit zu beschwören. Die Vesperglocke der Klarheit betäubt Gegner in der Nähe, wenn sie erscheint, und erhöht Euren verursachten Schaden, solange Ihr in ihrer Nähe seid.', 38134),
(202362, 'deDE', '$gKyrianer:Kyrianerin;! Willkommen im Äonischen Archiv, wo die Anima unsere Fähigkeiten stärken kann.', '$gKyrianer:Kyrianerin;! Willkommen im Äonischen Archiv, wo die Anima unsere Fähigkeiten stärken kann.', 38134),
(202360, 'deDE', 'Welch ein Ereignis das sein wird!', '', 38134),
(202359, 'deDE', 'Meinem beschwingten Schritt gleicht einzig die Leichtigkeit meiner Seele.', '', 38134),
(202358, 'deDE', 'Habt Ihr bitte einen kleinen Moment?', '', 38134),
(202357, 'deDE', 'Erzlich! Benutzt das Medaillon, das Euch gegeben wurde, und wir zerquetschen gemeinsam diesen Schlundgänger.', '', 38134),
(202356, 'deDE', 'Schnell, tötet alles auf Eurem Weg zur nächsten Quelle. Ich spüre die Macht meines Medaillons in der Nähe.', '', 38134),
(202355, 'deDE', 'Und so beginnt Eure Sühne!', '', 38134),
(202354, 'deDE', 'Ihr werdet für Eure Arroganz bezahlen!', '', 38134),
(202353, 'deDE', '', 'Bwonsamdis Schicksal interessiert mich nicht. Helft ihm oder auch nicht. Ich muss mich um den Ardenwald kümmern.', 38134),
(202352, 'deDE', '', 'Mein Bruder wird sicher nicht am Zeremoniellen sparen.', 38134),
(202349, 'deDE', 'Unser Gast will sich uns anschließen. Wer den Eid geleistet hat, ist bei unserer Rebellion willkommen.', '', 38134),
(202347, 'deDE', 'Nehmt Euren Platz an meiner Seite ein.', '', 38134),
(202331, 'deDE', 'Ja. Zusammen mit diesen Sterblichen werden wir uns den Sieg holen.', '', 38134),
(202330, 'deDE', 'Ein passendes Ende für so viel Stolz. Hilflos wird er mitansehen, wie Revendreth neu erblüht und der Kerkermeister fällt.', '', 38134),
(202329, 'deDE', 'Zu lange schon setzt Denathrius das Licht als Waffe gegen unser Volk im Glutdistrikt ein. Jetzt werden wir ihn damit binden.', '', 38134),
(202328, 'deDE', '', 'Ich würde mich freuen, meine Königin beim Gluthof zu repräsentieren.', 38134),
(202327, 'deDE', '', '$n, wir sehen Euch bald am Gluthof wieder.', 38134),
(202326, 'deDE', 'Wir bringen die Klinge nach Sündensturz. Z\'rali, die von den Sterblichen gerettet wurde, will uns unterstützen.', '', 38134),
(202325, 'deDE', 'Stimmt. Aber ich weiß eine Lösung.', '', 38134),
(202324, 'deDE', 'Wenn dieses Schwert wirklich die Essenz des Grafen enthält, fürchte ich, dass Eure Zauber allein seine Macht nicht halten können.', '', 38134),
(202323, 'deDE', 'Das reicht jetzt aber wirklich. Ruhe!', '', 38134),
(202322, 'deDE', '', 'Verräterischer Prinz! Ich werde Euch aufspießen! Aufschlitzen! Ausweiden! Euch in Fetzen...', 38134),
(202321, 'deDE', 'Nein. Denathrius wird sich für seinen Verrat verantworten.', '', 38134),
(202320, 'deDE', '', 'Zovaal wird Euch zurückholen, Meister! Die Rache ist unser!', 38134),
(202319, 'deDE', 'Die Klinge hat seine Essenz absorbiert!', '', 38134),
(202317, 'deDE', '', 'Das ist nicht das Ende, Meister! Lasst mich Euch retten!', 38134),
(202314, 'deDE', 'Immer mit der Ruhe, Remornia. Eile ist so... unschicklich.', '', 38134),
(202309, 'deDE', '', 'Allein durch die harte Arbeit aller wird die Reinheit unseres Reichs bewahrt. Gut gemacht.', 38134),
(202308, 'deDE', '', 'In dieser Gegend herrscht Chaos. Glücklicherweise haben wir Provosten, die sich um alle unserer Probleme kümmern. Stellt bitte die Ordnung wieder her.', 38134),
(202307, 'deDE', 'Seht nur, Remornia! Die tapferen Sterblichen trotzen allem, was wir Ihnen entgegenwerfen. Uns bleibt wohl keine Wahl. Wir müssen uns ergeben.', '', 38134),
(202306, 'deDE', 'Ihr seid der Schlüssel zum Sieg, Sterbliche! Geht und vernichtet den elenden Verräter!', '', 38134),
(202305, 'deDE', 'Mehr Loyalisten! Wir müssen sie zurücktreiben, mein Prinz!', '', 38134),
(202302, 'deDE', 'Bald werden noch mehr Drust angreifen. Beeilt Euch!', '', 38134),
(202299, 'deDE', '', 'Nun, das lief in etwa so gut, wie ich es erwartet hatte.', 38134),
(202298, 'deDE', '', 'Ich habe mein Bestes getan, um es für alle interessant zu gestalten, aber das war wohl nicht genug.', 38134),
(202297, 'deDE', 'Ich muss wieder an die Arbeit. Zu viel Zeit verloren.', '', 38134),
(202296, 'deDE', '', 'Ich fürchte, Ihr habt noch einiges darüber zu lernen, wie man Hof hält, mein lieber Prinz.', 38134),
(202295, 'deDE', 'Ich habe genug Zeit fern des Schlachtfelds verschwendet.', '', 38134),
(202294, 'deDE', '', 'Höfe sind nichts für mich. Ich kehre zur Jagd zurück.', 38134),
(202293, 'deDE', 'Ich verstehe nun, dass die Archon mich hierhergesandt hat, um mich Bescheidenheit zu lehren.', '', 38134),
(202292, 'deDE', 'Tja... Wettrennen zur Kutsche?', '', 38134),
(202291, 'deDE', 'Die feinen Schnösel hatte ich mir anders vorgestellt.', '', 38134),
(202290, 'deDE', 'Slanknen, trinkt schon aus! Wir können endlich abhauen!', '', 38134),
(202289, 'deDE', 'Es ist stets eine Ehre, eingeladen zu werden, Prinz Renathal... Vielleicht solltet Ihr diese Ehre das nächste Mal jemand anderem zuteilwerden lassen.', '', 38134),
(202288, 'deDE', '', 'Ich hatte gehofft, Ihr würdet überzeugender sein, Prinz Renathal. Ich möchte an Eure Sache glauben.', 38134),
(202287, 'deDE', 'Schmiede, die ihren Stahl schärfen, sind interessanter als Euer Hof.', '', 38134),
(202286, 'deDE', 'Nicht alles war schlecht, Kleia. Dieser eine Gräber, Gubbins, war ganz nett.', '', 38134),
(202285, 'deDE', '', 'Der Archon sei Dank, es ist vorbei.', 38134),
(202284, 'deDE', 'Immerhin hatte Kevin Spaß. Ihr werdet diese Gräber bestimmt nicht vermissen.', '', 38134),
(202283, 'deDE', '', 'Hätte in der Bastion bleiben sollen. Mehr putzen.', 38134),
(202282, 'deDE', 'Party braucht mehr zu beißen.', '', 38134),
(202281, 'deDE', 'Natürlich waren das zwei separate, parallele Experimente... aber sie sind beide gut gelaufen!', '', 38134),
(202280, 'deDE', '', '<Lady Mondbeere zwinkert Euch zu.>', 38134),
(202279, 'deDE', 'Es ist Zeit, unseren früheren Meister zu entthronen. Was wir jetzt tun, tun wir zum Wohle der Schattenlande!', '', 38134),
(202278, 'deDE', 'Nicht nur die Steingeborenen wurden manipuliert und betrogen, mein Freund. Ich wage zu behaupten, auch die Richterin wurde zur Spielfigur.', '', 38134),
(202277, 'deDE', 'Meine Art wird zur Verteidigung Revendreths aus Stein erschaffen. Widerlich, dass unser einstiger Meister unsere Loyalität missbraucht.', '', 38134),
(202275, 'deDE', 'Ich mochte, als ich Witz erzählt hab und guter Kumpel gelacht hat.', '', 38134),
(202274, 'deDE', '', 'Toller Hof! Die Reise wert! Werde allen Freunden davon erzählen!', 38134),
(202273, 'deDE', 'Ein erfolgreiches Experiment mit Freundschaft und Glibber!', '', 38134),
(202272, 'deDE', 'Wenn wir Seite an Seite stehen, stellt sich uns niemand entgegen.', '', 38134),
(202271, 'deDE', '', 'Ich bewundere Eure Hingabe dafür, den Schattenlanden auch über Revendreth hinaus zu dienen.', 38134),
(202270, 'deDE', 'Mir gefällt Euer Stil, Renathal. Ein Prinz, der keine Angst hat, sich die Hände schmutzig zu machen... Großes Potenzial.', '', 38134),
(202269, 'deDE', '', 'Die heutige Veranstaltung hat mich beeindruckt. Ich werde der Winterkönigin von Euren Worten und Taten berichten.', 38134),
(202268, 'deDE', 'Natürlich is unser Geschenk das besteste. Wir haben\'s selbst gemacht!', '', 38134),
(202267, 'deDE', 'Hoffentlich ist mein Geschenk das beste! Dann ladet Ihr mich wieder ein, ja?', '', 38134),
(202266, 'deDE', '', 'Ich freue mich darauf, in Zukunft an Eurer Seite zu jagen.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202265, 'deDE', 'Habt Dank für die angenehme Pause. Ich kehre erfrischt in den Kampf zurück!', '', 38134),
(202263, 'deDE', 'Und wir haben uns zusammengetan, um den gemeinsamen Feind all unserer Reiche zurückzuschlagen: die Verschlinger!', '', 38134),
(202262, 'deDE', 'Seid gewarnt. Denathrius\' ergebenste Wachen stehen zwischen uns und unserer Beute.', '', 38134),
(202261, 'deDE', 'Wir müssen unsere Jagd fortsetzen, Sterbliche. Zum Glück scheint dieser Spiegel noch reparabel zu sein.', '', 38134),
(202260, 'deDE', 'Nein. Sorgt dafür.', '', 38134),
(202259, 'deDE', 'Wohl wahr, Ihr habt wirklich eine Spur der Zerstörung hinterlassen. Aber wenn Ihr so etwas mögt, wird Euch mein Freund hier gefallen.', '', 38134),
(202258, 'deDE', 'Aber Ihr habt mich gut genug gelehrt, so viele Eurer Schergen zu beseitigen, \"Meister\".', '', 38134),
(202257, 'deDE', 'Nach allem, was ich Euch gelehrt habe, überschätzt Ihr Euch noch immer, Renathal. Tz. Sehr enttäuschend.', '', 38134),
(202256, 'deDE', 'Ihr habt Euch bisher gut geschlagen, Sterbliche, und Denathrius\' Pläne wanken. Aber unterschätzt ihn nicht. Seine Macht ist... beachtlich.', '', 38134),
(202255, 'deDE', 'Hier entlang ist noch ein Gang zurück in die Vorkammer.', '', 38134),
(202253, 'deDE', 'Seht, wie sie rennen! Traurig, wie tief einige meiner Leute gefallen sind.', '', 38134),
(202252, 'deDE', 'Kommt, Sterbliche. Es ist Zeit, Revendreth von einer anmaßenden Gruppe Adeliger zu befreien, die zwischen uns und Denathrius stehen.', '', 38134),
(202251, 'deDE', 'Nehmt diese Seele und helft ihm, Anklägerin.', '', 38134),
(202250, 'deDE', '', 'Ihr habt so viel zu lernen.', 38134),
(202249, 'deDE', 'Ich bin es leid, dass andere mich ausnutzen. Kann Eure \"Hilfe\" mir meine Rache bringen?', '', 38134),
(202248, 'deDE', '', 'Kael\'thas Sonnenwanderer! Euch trifft keine Schuld für des Grafen Tyrannei, aber für Eure Taten. Kommt mit mir und ich helfe Euch, für sie zu sühnen.', 38134),
(202246, 'deDE', '', 'Die Macht der Relikte ist nichts im Vergleich zu den Waffen, die vor uns auf uns warten.', 38134),
(202244, 'deDE', 'Gier. Sie ist wahrlich die Übelste aller Sünden.', '', 38134),
(202243, 'deDE', 'Abscheulich! Unser Volk hungert vor den Schlossmauern und diese Loyalisten frönen ihrer Gier.', '', 38134),
(202242, 'deDE', 'Die Mittler sind ein gieriges Pack. Sorgt dafür, dass sie diese Waffen nicht bekommen!', '', 38134),
(202241, 'deDE', 'Viele wertvolle Waffen zieren diese Hallen. Zu wertvoll für diese Adeligen. Lasst sie uns befreien.', '', 38134),
(202240, 'deDE', '', 'Während sie tanzen und sich laben, droht die Armee des Kerkermeisters, die Schattenlande zu vernichten. Narren! Sie haben es nicht besser verdient!', 38134),
(202239, 'deDE', 'Mit diesem Spiegel könnt Ihr Euch leichter durch das Schloss bewegen, Sterbliche.', '', 38134),
(202238, 'deDE', 'Die Steingeborenen haben auf diesen Wegen die Wünsche unseres Meisters erfüllt. Welche Perversionen ich gesehen habe...', '', 38134),
(202237, 'deDE', 'Hier, Sterbliche. Ich kenne einen Weg zurück zur Vorkammer.', '', 38134),
(202235, 'deDE', '', 'Wir haben keine Zeit für Reue. Denathrius\' Herrschaft muss enden.', 38134),
(202234, 'deDE', '', 'Wir haben den Animastrom eingedämmt, aber ich fürchte, der Schaden ist bereits angerichtet. Wir müssen retten, was wir können. Für Revendreth.', 38134),
(202233, 'deDE', '', 'Freunde helfen Sika. Sika bringt Freunde.\n\nFreunde räumen Schmuddeliges auf. $gSchlundgänger:Schlundgängerin; ist $gFreund:Freundin;.\n\n$gSchlundgänger:Schlundgängerin;... macht sauber?\n\n<Sika wird von jemand anderem abgelenkt.>\n\nHabt Spaß! Hofspaß!', 38134),
(202232, 'deDE', 'Gut gemacht, Sterbliche! Dort ist der Animavorrat!', '', 38134),
(202231, 'deDE', '', 'Vernichtet diese Kreatur, bevor sie noch stärker wird!', 38134),
(202230, 'deDE', '', 'Die Bestie will ihren endlosen Hunger an dem Animavorrat unter uns stillen.', 38134),
(202229, 'deDE', 'Ein Verschlinger? Hier? Einen so großen habe ich noch nie gesehen!', '', 38134),
(202228, 'deDE', 'Was für ein wundervoller Tag für einen Hof!\n\nIch habe natürlich Kevin mitgebracht. Und dazu noch zwei weitere schmackhafte Leckerbissen, die ich eigens für diesen Anlass vorbereitet habe!\n\nOh, keine Sorge. Sie sind immer noch Schleime. Alles ist Schleim.\n\n<Marileth schaut sich um.>\n\nDa fällt mir ein... Ich habe gehört, dass Eure Gräber aus Dreck gemacht werden.\n\nDreck... Dreck...\n\nMeint Ihr, dass es da Gemeinsamkeiten mit Schleimen gibt?\n\nIch sollte das herausfinden!', '', 38134),
(202225, 'deDE', 'Es freut mich, hier zu sein und Eurem Prinzen die besten Kämpfer in Maldraxxus zu präsentieren.\n\n<Großmeister Vole lässt in Richtung der feinen Gesellschaft die Muskeln spielen.>\n\nDa fällt mir ein... Ich nehme Wetten an, ob ich und meine Jungs es schaffen, hier einen kleinen Kampfclub zu eröffnen, bevor alles vorbei ist.\n\nDieser Hof ist kein Theater der Schmerzen, aber die Stimmung hier ist ausgeprägt... \"offen\".\n\nDie Chancen stehen gut. Lasst mich wissen, ob Ihr dabei seid.', '', 38134),
(202224, 'deDE', '', 'Ich bin neugierig, $gSchlundgänger:Schlundgängerin;. Betrachtet Ihr Revendreth als Euer wahres Zuhause?\n\nIst Prinz Renathal EUER Prinz?\n\n<Die Dromanin schaut an Euch vorbei.>\n\nMein sterbliches Zuhause existiert nicht mehr. Zerstört.\n\nAber im Ardenwald unter der Winterkönigin fühle ich mich zu Hause.\n\n<Die Dromanin seufzt lang und tief.>\n\nDie Überlegungen, die Euer Prinz uns angetragen hat, sind nicht leicht zu berechnen.', 38134),
(202223, 'deDE', '', 'Ich... kann... nicht mehr...', 38134),
(202222, 'deDE', 'Ich muss schon sagen, ist zur Abwechslung mal ganz nett, dass die Gräber mal MICH bedienen.\n\n<Rendel lächelt mit stolz geschwellter Brust.>\n\nOh, ja, der alte Rendel ist auf den Weg nach oben in dieser dreckigen Welt.', '', 38134),
(202219, 'deDE', 'So oft zum Hof gefahren und die feinen, aufgebrezelten Herrschaften da abgeladen.\n\nUnd jetzt schaut mich an. Selber aufgebrezelt. Ha!', '', 38134),
(202218, 'deDE', '', 'Sagt, $gSchlundgänger:Schlundgängerin;, welche Arten von Monstern durchstreifen die Lande jenseits dieses Hofes?\n\nJenseits dieser Ebene sehe ich Verwüstung in allen Richtungen.\n\nVerheerte Lande bringen Monster hervor. Etwas Ähnliches habe ich in meinem Reich erlebt.\n\nHaltet die Sinne geschärft.', 38134),
(202217, 'deDE', '', 'Der Weg vor Euch ist voller Gefahren. Aber wir müssen den Animavorrat erreichen!', 38134),
(202216, 'deDE', 'Hier ist der Gang nach unten, Sterbliche.', '', 38134),
(202215, 'deDE', '', 'Das passende Ende für ein Scheusal.', 38134),
(202214, 'deDE', 'Setzt der Grausamkeit des Hundemeisters ein Ende. Dann können wir tiefer in das Schloss vordringen.', '', 38134),
(202213, 'deDE', 'Der Zwinger ist für die Jagdmeute des Meisters. Er hält sie hungrig nach Fleisch.', '', 38134),
(202212, 'deDE', '', 'Wenn wir überhaupt eine Chance gegen Denathrius haben wollen, müssen wir ihm seinen wertvollsten Besitz rauben. Ich weiß, wo wir ihn finden.', 38134),
(202211, 'deDE', 'Jenseits der Zwinger führt ein Gang in die tiefsten Schlossgewölbe. Von dort erreichen wir den Animavorrat und können den Strom in den Schlund eindämmen.', '', 38134),
(202210, 'deDE', 'Die Vorkammer gehört uns! Aber vermutlich hat sich Denathrius in sein Sanktum zurückgezogen.', '', 38134),
(202208, 'deDE', 'Wollen wir Eure Sühne mit einer richtigen Herausforderung beginnen?', '', 38134),
(202207, 'deDE', 'Ihr habt Eure Lektion wohl doch nicht gelernt. Ich war vielleicht zu nachsichtig. Mitgefühl ist nun mal meine Schwäche.', '', 38134),
(202206, 'deDE', 'Ich erinnere mich gut an Eure letzte große Ansprache, Renathal. Wir wissen beide, wie das ausging.', '', 38134),
(202205, 'deDE', 'Schluss mit dem dümmlichen Geschwätz, Denathrius! Kämpft!', '', 38134),
(202204, 'deDE', 'Unser verwirrter Prinz, natürlich. Die entthronte Anklägerin, ernst wie eh und je. Der rebellische General darf auch nicht fehlen. Und ein Haufen Sterbliche voller Todessehnsucht.', '', 38134),
(202203, 'deDE', 'Willkommen! Ich habe mich so auf Euch gefreut. Schauen wir mal, wen wir hier haben.', '', 38134),
(202202, 'deDE', 'Interessant. Interessant.\n\nDer Hof ist sehr interessant.\n\nViele neue Ideen. Viele neue Leute.\n\nViel zu lernen.', '', 38134),
(202199, 'deDE', 'Seid auf der Hut, Sterbliche. Denathrius ist hier im Vorteil. Er wird all seine List gegen uns einsetzen.', '', 38134),
(202198, 'deDE', '', 'Er weiß, die Zeit läuft uns davon. Mit jedem Moment wächst die Macht des Kerkermeisters. Der Animastrom in den Schlund muss versiegen.', 38134),
(202196, 'deDE', 'Hmm.$b$b<Er ist eindeutig sehr alarmiert. Und er liefert einen Beweis für die Vielseitigkeit dieses Worts.>', '', 38134),
(202195, 'deDE', 'Die Schlosstore waren zu leicht erstürmt. Unser alter Meister will uns tiefer in seine Festung locken.', '', 38134),
(202194, 'deDE', 'Hmm.$b$b<Er klingt fast nachdenklich.>', '', 38134),
(202193, 'deDE', 'Hmm.$b$b<Er klingt, als würde er sehr, sehr angestrengt versuchen, die Geräusche der Umgebung auszublenden. Und es klingt, als würde das nicht funktionieren.>', '', 38134),
(202192, 'deDE', 'Hmm.$b$b<Er klingt eindeutig so, als hättet Ihr ihn verärgert.>', '', 38134),
(202190, 'deDE', 'Die Meditation bringt mich in Einklang mit dem Wald und hilft mir, die Dinge zu finden, die nicht hierher gehören.$b$B<Der Vorkai öffnet ein Auge und schaut Euch an.>$b$bAblenkungen sind abträglich. Geht und sprecht mit Narzisse.', '', 38134),
(202189, 'deDE', 'Hmm.$b$B<Ihr seid Euch nicht sicher, ob der Vorkai besonders konzentriert meditiert oder Ihr ihn verärgert habt.>', '', 38134),
(202188, 'deDE', '$p, der Königin sei Dank, Ihr seid da!', '', 38134),
(202185, 'deDE', 'Es ist vorbei, Gorak Zhar!', '', 38134),
(202184, 'deDE', '', 'Nein! Diesen Frevel werdet Ihr teuer bezahlen!', 38134),
(202182, 'deDE', 'Ich muss mich auf die Reise vorbereiten. Ich brenne darauf, mich mit unseren Verbündeten zu besprechen.', '', 38134),
(202181, 'deDE', '', 'Die Augen sind jetzt Eure Meister. Wartet auf Befehle.', 38134),
(202179, 'deDE', 'Fußpolster des Herolds sehr hilfreich bei Bewegung!\n\nGegner machen Euch langsam? Könnt Euch nicht mehr bewegen?\n\nNicht mit diesen Stiefeln! Einfach wieder losspurten, kein Problem!', '', 38134),
(202178, 'deDE', '', 'Genug! Ich gebe auf. Nennt Eure Bedingungen.', 38134),
(202177, 'deDE', 'Kümmere mich gut um Ausrüstung, keine Sorge! Alles bereit, wenn Ihr Pfad beschreitet.\n\nIhr wollt Information zu verfügbarer Ausrüstung? Welche?', '', 38134),
(202175, 'deDE', '', 'Lasst die Funken fliegen!', 38134),
(202174, 'deDE', '', 'Spione?! Das werdet Ihr büßen, Abschaum!', 38134),
(202173, 'deDE', '', 'Wo bin ich?! Wer wagt es, mich zu beschwören?!', 38134),
(202172, 'deDE', 'Verflucht, Narzisse....', '', 38134),
(202171, 'deDE', '', 'Das hat gesessen! Magie ist einfach die Antwort auf alles.', 38134),
(202170, 'deDE', '', 'Was sagt Ihr zu noch ein bisschen mehr Glitzerchaos?', 38134),
(202169, 'deDE', 'Bitte nehmt Euren Platz ein, wenn Ihr bereit seid.', '', 38134),
(202168, 'deDE', 'Trefft uns unten in den Kammern, um anzufangen.', '', 38134),
(202167, 'deDE', '', 'Notizen keine neue Arbeit... Notizen helfen?', 38134),
(202153, 'deDE', '', 'Ein Verschlinger könnte sie schon gefressen haben. Das wäre zu schade.', 38134),
(202152, 'deDE', '', 'Sie ist bald zurück. Wir haben kaum Zeit. Ich zeige Euch die Wahrheit.', 38134),
(202151, 'deDE', '', 'Schlundgänger, Denathrius benutzt Euch als Spielfigur.', 38134),
(202150, 'deDE', '', 'Geht! Ich bewache die Brücke.', 38134),
(202149, 'deDE', 'Der Preis für Elunes dunkle Kraft war mein Leben. \n\nSterbliche sollten eine solche Macht nicht führen. So oder so ging ich daran zugrunde. Ich brachte dieses Opfer gerne für meine Welt, mein Volk, meine Familie.\n\nQadarin wollte es nicht zulassen. Der Versuch, die Bürde gemeinsam zu schultern, tötete uns beide. Das bereue ich noch heute.\n\n<Thiernax seufzt.>\n\nIch sollte die Vergangenheit ruhen lassen. Unsere Welt wurde gerettet und wir sind immer noch zusammen. Nur das zählt.', '', 38134),
(202146, 'deDE', 'Es gibt viel zu tun, bevor die Königin eintrifft. Die Zeit drängt!', '', 38134),
(202144, 'deDE', '', 'Lasst mich Euch die Wahrheit zeigen, $gSchlundgänger:Schlundgängerin;.', 38134),
(202143, 'deDE', '', 'Eure Loyalität wurde gesehen und anerkannt. Steht an unserer Seite.', 38134),
(202142, 'deDE', 'Im Dickicht können Eure Augen Euch trügen. Was nah scheint, ist hinter einem Nebelschleier verborgen. \n\nVersucht es nur, aber das Ergebnis ist dasselbe. Wer alleine herumstreift, geht oft in der Zeit verloren. \n\nHoffnung liegt nur im Bewusstsein. Das Dickicht verbirgt viele Geheimnisse für den Scharfsinnigen. \n\nLasst Euch vom Licht führen. Der Gang durch den Schleier hat bereits begonnen.', '', 38134),
(202141, 'deDE', '', 'Ich akzeptiere Euren Treueschwur. Ihr dürft Euch erheben.', 38134),
(202140, 'deDE', '', 'Sika ist so liebenswürdig und arbeitet so hart. Ich sollte die Schmiede sauber halten, um ihre Sorgen zu schmälern.', 38134),
(202139, 'deDE', 'Habt Dank, $uSchlundgänger:Schlundgängerin;.\n\nIch habe diese Ätherwyrms in Schach gehalten, während die Aspiranten geflohen sind... aber es waren zu viele.\n\n<Adonas berührt sehr vorsichtig seine Seite.>\n\nIch bin froh, dass Ihr in diesem Moment dazugestoßen seid.', '', 38134),
(202138, 'deDE', '', 'Den Blutigen Tiefen kann niemand entkommen, auch Ihr nicht!', 38134),
(202137, 'deDE', '', 'Ihr seid doch nur Dravens Lakaien!', 38134),
(202136, 'deDE', '', 'Verfluchte Sterbliche!', 38134),
(202135, 'deDE', '', 'Wir treffen uns bei der Säule. Es gibt Arbeit für uns.', 38134),
(202134, 'deDE', 'Oh. Ich habe mit Sika über die Umstrukturierung der Bibliothek gesprochen, und sie schien es so gern zu übernehmen. Ich hatte keine Ahnung, dass sie so beschäftigt ist. Ich kümmere mich darum!', 'Oh. Ich habe mit Sika über die Umstrukturierung der Bibliothek gesprochen, und sie schien es so gern zu übernehmen. Ich hatte keine Ahnung, dass sie so beschäftigt ist. Ich kümmere mich darum!', 38134),
(202133, 'deDE', 'Mehr habt Ihr nicht drauf?', '', 38134),
(202132, 'deDE', 'Klar, ich trage Euch.', '', 38134),
(202131, 'deDE', 'Und das war alles?', '', 38134),
(202130, 'deDE', 'Gibt\'s Probleme?', '', 38134),
(202129, 'deDE', 'Bin zur Stelle, wenn Ihr mich braucht.', '', 38134),
(202128, 'deDE', 'Und wen töten wir heute?', '', 38134),
(202127, 'deDE', 'Könnte schlimmer sein.', '', 38134),
(202123, 'deDE', '', 'Das waren die Klingenhände, oder?', 38134),
(202122, 'deDE', '', 'Handschlag gefällig?', 38134),
(202121, 'deDE', '', 'Knuddeln!', 38134),
(202120, 'deDE', '', 'Vergesst mich nicht.', 38134),
(202117, 'deDE', 'Ihr helft auch Hephaesius?', '', 38134),
(202116, 'deDE', '', 'Freunde müssen zusammenhalten.', 38134),
(202115, 'deDE', '', 'Wollt Ihr mit?', 38134),
(202113, 'deDE', 'Möge das Läuten dieser Glocke uns erneut inspirieren.', '', 38134),
(202109, 'deDE', 'Ja, Mann?', '', 38134),
(202107, 'deDE', 'Um ein großes Böse zu bekämpfen, das viele niedergestreckt hatte.\n\nEs hatte sich eine Koalition aus Champions gebildet, die den dunklen Gott töten wollte, der in unserer Welt gelandet war. Um das zu bewältigen, brauchten wir die Kraft, die die letzte Sprecherin von Mutter Mond erbeten hatte.\n\n<Thiernax lacht traurig.>\n\nWir stritten darüber. Seine Mutter war bereits bei dem Versuch gestorben. Er wollte nicht auch noch seinen Ehemann verlieren. Doch Elunes Kraft war eine der größten Waffen gegen die Monster. Also vollzog ich das Ritual. Überlebte es. Er stand an meiner Seite. Mit Augen so dunkel wie Neumonde kämpften wir gegen die Monster.\n\nDen Preis dafür ahnte ich nicht.', '', 38134),
(202104, 'deDE', 'Letzten Endes. \n\nThiernax wurde erfolgreich zum Nachtkrieger ernannt. Ich kämpfte bis zum Ende an seiner und der Seite der anderen Champions von Fyzandi. Wir siegten, doch der Preis dafür war hoch. \n\nDie Armeen des Wahnsinns töteten viele, während wir fort waren, und verstreuten sich, als der Dunkle starb. Einige unseres Volkes fielen dem Geflüster anheim. Sie verschwanden oder wurden verrückt. Wir haben Freunde und Familie verloren. \n\nDer Gott blutete. Es wird viele Generationen dauern, bis diese Krankheit vollständig geläutert wird.\n\nFalls es überhaupt möglich ist.', '', 38134),
(202103, 'deDE', 'Monster.\n\nKreaturen mit zu vielen Augen, zu vielen Gliedern und zu vielen hungrigen Mäulern. Sie fegten über die Welt, als meine Urgroßeltern noch Kinder waren. Und so begann der Endlose Krieg. Ganze Generationen mussten ihr Leben lang gegen die Anderen und ihren Wahnsinn standhalten.\n\nMeine Mutter fand heraus, was im Zentrum des Kraters lebte. Um das Wesen aus einer anderen Welt niederzustrecken, wollte sie Elunes dunkles Gesicht anrufen. Sie starb.', '', 38134),
(202100, 'deDE', 'Ja. Ein paar Generationen vor meiner Geburt landete der Dunkle Gott in unserer Welt. \n\nDer Aufprall vernichtete ein ganzes Königreich und störte das Gleichgewicht der Natur. Niemand, der sich in das Gebiet wagte, kehrte je aus der Einöde zurück. \n\nWir Druiden behielten die Grenzen im Auge, doch wir konnten nicht ahnen, was noch kommen sollte.', '', 38134),
(202098, 'deDE', 'Meine Welt hieß Fyzandi. Sie war wunderschön - voller dichter Wälder, imposanter Berge und friedlicher Ebenen. \n\nFür dieses wilde Land haben wir den Dunklen Gott bekämpft.', '', 38134),
(202097, 'deDE', 'Ja, holen wir unsere Belohnung! Wie wäre es mit einem Lied zur Feier? Reichet Samen Eurem Jäger...', '', 38134),
(202096, 'deDE', 'He, ich habe auch geholfen! Ihr erinnert Euch an den grässlichen Gesang?', '', 38134),
(202095, 'deDE', 'Ha, ich habe... Wartet, was?', '', 38134),
(202094, 'deDE', 'Vielen Dank für die Hilfe. Das war heftig, aber Ihr habt gut gekämpft.', '', 38134),
(202093, 'deDE', 'Argh.', '', 38134),
(202092, 'deDE', 'Hmm. Was zählt, ist, dass es tot ist. Machen wir uns auf den Weg.', '', 38134),
(202091, 'deDE', 'Gute Arbeit. Boggarts hassen grässliche Geräusche.', '', 38134),
(202087, 'deDE', 'Moment!', '', 38134),
(202086, 'deDE', 'OH NEIN!', '', 38134),
(202085, 'deDE', 'Warum? Das sieht nicht so übel aus. Sicherlich nicht die Kreatur, nach der wir suchen. Ich erledige sie schnell. Seht her!', '', 38134),
(202084, 'deDE', 'Habt keine Furcht, Ihr Witzfigur... hm? Ich, äh... Gwynceirw? Ich glaube, ich habe etwas gefunden!', '', 38134),
(202083, 'deDE', 'Wo ist das Tier? Wo könnte es sein? Wenn Ihr nicht helft, dann ist es wohl mein!', '', 38134),
(202082, 'deDE', 'Überlasst es Narzisse, dem brillanten Dichter. Bei dem Tölpel gibt es hinter der Stirn keine Lichter!', '', 38134),
(202081, 'deDE', 'Oh Narzisse, oh Narzisse, stets am Rennen! Nicht wie sein Partner, der ist nur am PENNEN!', '', 38134),
(202080, 'deDE', 'Oh, ich sage Euch, was ich tun werde! Ich finde das Ding alleine, wenn ich muss. Und ich werde dabei ein Lied schreiben! Hier, wie wäre es damit...', '', 38134),
(202079, 'deDE', 'Der? Beachtet ihn gar nicht. Er ist nur beleidigt, weil er seine Beute noch nicht erlegt hat. Im Grunde ist er ein erstaunlicher Kämpfer, aber seine Methoden sind etwas... seltsam. Aber das wisst Ihr nicht von mir.$b$bUnd er kann mich ja auch nicht hören, WENN ER EIN NICKERCHEN HÄLT.$B$B<Gwynceirw scheint nicht zu reagieren.>', '', 38134),
(202078, 'deDE', 'Habt Ihr endlich das Nickerchen beendet und wollt nun mitmachen?$b$BOh, Ihr seid ja gar nicht er. Aber vielleicht wollt Ihr Euch nützlich machen. Habt Ihr hier in letzter Zeit einen Pflanzenschrecken gesehen?', '', 38134),
(202077, 'deDE', 'Ich. Meditiere. Gerade. Oder zumindest versuche ich es.$b$bWarum geht ihr nicht zu Narzisse? Er plaudert immer gern.', '', 38134),
(202076, 'deDE', 'Anderswo in der Bastion findet sich eine besondere Glocke. Eine, die lange nicht geläutet wurde.\n\nEinst diente sie als wichtiger Brauch für alle Kyrianer. Allerdings sind die meisten Kristallhämmer, mit denen man solche Glocken läutete, zerbrochen.\n\nWenn jemand die Splitter finden würde, könnte ich einen neuen schmieden.', '', 38134),
(202075, 'deDE', 'Gut, dass Ihr da seid.', 'Gut, dass Ihr da seid.', 38134),
(202074, 'deDE', 'Gut, dass Ihr da seid. Ich habe mir etwas überschüssige Anima besorgt und einen meisterlichen Plan ersonnen, wie wir all diese Asche um uns herum wiederverwenden können.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Aschene Urne]|h|r: Nachdem Ihr mehrere Gegner besiegt habt, ruft Ihr einen Aschekriecher herbei, der an Eurer Seite kämpft. Er kann nicht geheilt werden, aber jeder Gegner, den Ihr gemeinsam besiegt, erhöht den Schaden des Kriechers und heilt ihn.', 'Gut, dass Ihr da seid. Ich habe mir etwas überschüssige Anima besorgt und einen meisterlichen Plan ersonnen, wie wir all diese Asche um uns herum wiederverwenden können.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[Aschene Urne]|h|r: Nachdem Ihr mehrere Gegner besiegt habt, ruft Ihr einen Aschekriecher herbei, der an Eurer Seite kämpft. Er kann nicht geheilt werden, aber jeder Gegner, den Ihr gemeinsam besiegt, erhöht den Schaden des Kriechers und heilt ihn.', 38134),
(202073, 'deDE', 'Seid leise.', 'Seid leise.', 38134),
(202072, 'deDE', 'Seid leise, dann kann ich Euch Hilfe gegen diese elenden Verbrecher anbieten. Ich habe sie um einige ihrer verfluchten Spiegel erleichtert, die Euch gute Dienste leisten können.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Mobiles Spiegelset]|h|r: Eure Schaden verursachenden Zauber und Fähigkeiten haben eine Chance, Spiegelscherben zu beschwören, die das Licht zu einem Strahl vor Euch bündeln. Dieser fügt Euren Gegnern Feuerschaden zu und lässt sie brennen, wodurch sie zusätzlich regelmäßigen Schaden erleiden.', 'Seid leise, dann kann ich Euch Hilfe gegen diese elenden Verbrecher anbieten. Ich habe sie um einige ihrer verfluchten Spiegel erleichtert, die Euch gute Dienste leisten können.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[Mobiles Spiegelset]|h|r: Eure Schaden verursachenden Zauber und Fähigkeiten haben eine Chance, Spiegelscherben zu beschwören, die das Licht zu einem Strahl vor Euch bündeln. Dieser fügt Euren Gegnern Feuerschaden zu und lässt sie brennen, wodurch sie zusätzlich regelmäßigen Schaden erleiden.', 38134),
(202071, 'deDE', 'Venthyr! Wir haben uns in der Hoffnung unter den Verhüllten verborgen, dass Ihr uns helfen könnt. Ich habe Euch dafür Anima besorgt.', 'Venthyr! Wir haben uns in der Hoffnung unter den Verhüllten verborgen, dass Ihr uns helfen könnt. Ich habe Euch dafür Anima besorgt.', 38134),
(202070, 'deDE', 'Venthyr! Wir haben uns in der Hoffnung unter den Verhüllten verborgen, dass Ihr uns helfen könnt. Ich habe Euch dafür Anima besorgt.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Durst nach Anima]|h|r: Wenn Eure Gesundheit auf einen niedrigen Wert fällt, haben Eure Schaden verursachenden Zauber und Fähigkeiten eine hohe Chance, Euren Gegner für kurze Zeit zu betäuben. In diesem Zeitraum werdet Ihr geheilt und der Gegner erleidet Schaden.\n\nDieser Effekt kann nicht sehr oft auftreten. Wappnet Euch.', 'Venthyr! Wir haben uns in der Hoffnung unter den Verhüllten verborgen, dass Ihr uns helfen könnt. Ich habe Euch dafür Anima besorgt.\n\nWenn Ihr meine Verstärkung annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[Durst nach Anima]|h|r: Wenn Eure Gesundheit auf einen niedrigen Wert fällt, haben Eure Schaden verursachenden Zauber und Fähigkeiten eine hohe Chance, Euren Gegner für kurze Zeit zu betäuben. In diesem Zeitraum werdet Ihr geheilt und der Gegner erleidet Schaden.\n\nDieser Effekt kann nicht sehr oft auftreten. Wappnet Euch.', 38134),
(202066, 'deDE', 'Ich kann nich\' länger hierbleib\'n. Bringt die Geister heim in den Ardenwald...', '', 38134),
(202056, 'deDE', 'Rezan, ich verschwende Euer Geschenk nich\'. Euer Name is\' unvergess\'n... auf immer.', '', 38134),
(202054, 'deDE', 'Ihr seid... der... den sie brauchen...', '', 38134),
(202040, 'deDE', 'Los, reißt die Barriere nieder!', '', 38134),
(202039, 'deDE', 'Wir werden die Besessenen hier und jetzt rächen! Für den Ardenwald!', '', 38134),
(202038, 'deDE', 'Was ist das für Hexenwerk?! Tötet ihre Diener! Ich sichere die Wildsamen!', '', 38134),
(202037, 'deDE', 'Rezan! Der Loa der Könige! Wir müss\'n die Barriere durchbrech\'n!', '', 38134),
(202035, 'deDE', 'Ein Loa is\' noch hier... fast verschwund\'n...', '', 38134),
(202034, 'deDE', 'Shadra! Bwonsamdi hat gesagt, sie wurde von ihrer eig\'nen Priesterin verschlung\'n...', '', 38134),
(202033, 'deDE', 'Hier sind mehr Loa. Schnell!', '', 38134),
(202032, 'deDE', 'Hir\'eek! Ich hab seinen Todesschrei in Nazmir gehört...', '', 38134),
(202029, 'deDE', 'Habt Ihr mir etwas mitgebracht?', '', 38134),
(202027, 'deDE', '', 'Halt!', 38134),
(202024, 'deDE', '', 'So viel ist während meiner Abwesenheit passiert.\n\nEs wird Zeit brauchen, bis ich wieder ich selbst bin, falls das überhaupt möglich ist.\n\nHabt Dank, $n, dass Ihr mich gerettet habt.', 38134),
(202021, 'deDE', 'Es wäre mir eine Ehre, Sika behilflich zu sein. Ich kann dafür sorgen, dass ihre anstehenden Vorratslieferungen intakt und pünktlich eintreffen.', '', 38134),
(202020, 'deDE', '', 'Ungeziefer! Wir nehmen uns Eure Teile.', 38134),
(201999, 'deDE', '', 'Thros wird dieses Reich umfangen!', 38134),
(201996, 'deDE', '', 'Armselige kleine Kreaturen! Ihr müht Euch vergebens!', 38134),
(201995, 'deDE', '', 'Ich spüre die riesige Angst der Beute.', 38134),
(201994, 'deDE', '', 'Manifestationen ihrer verbleibenden Hoffnung. Wir sind nah dran.', 38134),
(201993, 'deDE', '', 'Ihre verlorene Hoffnung weist den Weg.', 38134),
(201992, 'deDE', 'Aber gewiss! Natürlich helfe ich. Eine hervorragende Gelegenheit, um der Schmiedin Sophone zu helfen und dabei etwas von ihr zu lernen.', 'Aber gewiss! Natürlich helfe ich. Eine hervorragende Gelegenheit, um der Schmiedin Sophone zu helfen und dabei etwas von ihr zu lernen.', 38134),
(201983, 'deDE', 'Hier lang! Wir müssen die Überlebenden befreien!', '', 38134),
(201980, 'deDE', 'Xavius der gefallene Beinah-Sylvar! Azsharas Berater eignete sich die Verderbnis des Smaragdgrünen Alptraums an und brachte seinem einstigen Volk den Ruin! Seht die Macht seiner furchterregenden Nachtschrecken!', '', 38134),
(201979, 'deDE', 'N\'Zoth der Verderber! Seine Augen - und Fische! - waren überall und infizierten die ganze Welt von Azeroth! Wem konnte man trauen? Welche Fische gefahrlos essen? Bis heute kennt niemand das volle Ausmaß seines Bösen!', '', 38134),
(201978, 'deDE', 'Jaina Prachtmeer, Lordadmiralin und Eiskönigin! Ihre Eiswinde waren wie Diamantenstaub und froren Ihre Gegner ein, bis sich das Blut in ihren Adern nicht mehr bewegte! Und so endeten Ihre Feinde als Galionsfiguren an ihrem sagenumwobenen Schlachtschiff!', '', 38134),
(201977, 'deDE', 'Gul\'dan, Vorstand des Schattenrats! \"Aber wie kehrte er zurück? Wo kam er her?\", fragten sie! Aber die wahre Frage war: Von WANN kam er her? Und was war mit seinem Schädel?', '', 38134),
(201976, 'deDE', 'Kil\'jaeden der Betrüger! Niemand erinnert sich, wer Jaeden ist, aber vielleicht gehört das zu seiner Täuschung? Die Schlacht fand im Wirbelnden Nether statt, und das ist, wie wir alle wissen, die trügerischste Dimension von allen!', '', 38134),
(201975, 'deDE', 'Argus, der Zerrütter! Sie wollten einen Todestitan erschaffen und wussten genau, dass der Tod in den Bereich der Schattenlande und unserer erhabenen Königin fällt! Das war nicht hinnehmbar, und so wollten die Abenteurer das Unmögliche möglich machen: Eine Welt töten!', '', 38134),
(201974, 'deDE', 'Vertraut auf den Rest meiner Kyrianer! Sie bringen uns in Sicherheit!', '', 38134),
(201971, 'deDE', 'Kann Konstruktion beenden! Gut. Gluthof als Nächstes.', '', 38134),
(201970, 'deDE', 'Ich ebenfalls. Ich danke Euch.', '', 38134),
(201969, 'deDE', '', 'Reflexion ist wichtig... Das verstehe ich gut. Ich hoffe, Ihr findet, was Ihr sucht.', 38134),
(201968, 'deDE', 'Nein, mein Kind. Die Verschmähten sind nicht, was ich einst dachte... Vielleicht waren sie es nie. Ich muss Ruhe und Isolation finden und über vieles nachdenken.', '', 38134),
(201967, 'deDE', '', 'Wohin jetzt, Uther? Doch bestimmt nicht zurück zu den Verschmähten.', 38134),
(201966, 'deDE', 'Erledigt diese Verräter!', '', 38134),
(201965, 'deDE', 'Wir müssen da durch. Thales?', '', 38134),
(201964, 'deDE', 'Sterbliche zu schützen war nicht Teil der Stellenbeschreibung.', 'Sterbliche zu schützen war nicht Teil der Stellenbeschreibung.', 38134),
(201963, 'deDE', 'Habt Dank. Sie hatten Besseres verdient als das.', '', 38134),
(201962, 'deDE', 'Ich werde dafür bezahlt, alle innerhalb des Alten Tores zu beschützen, sogar Leute wie Euch. Vielleicht sollte ich um eine Lohnerhöhung bitten.', 'Ich werde dafür bezahlt, alle innerhalb des Alten Tores zu beschützen, sogar Leute wie Euch. Vielleicht sollte ich um eine Lohnerhöhung bitten.', 38134),
(201960, 'deDE', 'Wenn ich diesen Posten und die dazugehörige Bezahlung nicht bräuchte, würde ich meine Waffe nicht erheben, um Sterbliche zu verteidigen.', 'Wenn ich diesen Posten und die dazugehörige Bezahlung nicht bräuchte, würde ich meine Waffe nicht erheben, um Sterbliche zu verteidigen.', 38134),
(201959, 'deDE', 'Mograine hat mich gebeten, zurückzubleiben, falls Ihr zu weit vom Kurs abkommt.\n\nDieses schwarze Tor wird Euch zu ihm bringen. Ich empfehle Euch, es zu nutzen.', '', 38134),
(201958, 'deDE', '', 'Nach Euch, Schlundgänger.', 38134),
(201950, 'deDE', '', 'Mograine hat mich gebeten, zurückzubleiben, falls Ihr zu weit vom Kurs abkommt.\n\nDieses schwarze Tor wird Euch zu ihm bringen. Ich empfehle Euch, es zu nutzen.', 38134),
(201949, 'deDE', 'Die Verantwortlichen müssen für diese Schrecken büßen.', '', 38134),
(201948, 'deDE', '', 'Vielleicht können wir das eines Tages vertiefen. Jetzt lasst uns diesem üblen Ort entfliehen.', 38134),
(201947, 'deDE', 'Ich denke, wir ähneln uns sehr, Kleia.', '', 38134),
(201946, 'deDE', '', 'Wir mögen Feinde sein, aber es wäre nicht richtig gewesen, Euch leiden zu lassen.', 38134),
(201945, 'deDE', 'Ich habe Schmerzen, aber sie sind mir nicht neu. Meine Wunden werden heilen.', '', 38134),
(201944, 'deDE', '', 'Wie geht es Euch, Uther?', 38134),
(201943, 'deDE', 'Ich muss Euch danken, dass Ihr mich vor Lysonias Missbrauch gerettet habt. Ich fürchte, dass ohne Euer Einschreiten von mir nichts mehr übrig wäre.', '', 38134),
(201942, 'deDE', '', 'Die Schlundgebundenen sind ganz wild auf Euch. Wir beeilen uns besser.', 38134),
(201940, 'deDE', 'In dieser unglaublichen Darbietung wird sich einer unserer Darsteller vor Euren Augen in einen mächtigen Feind aus einem anderen Reich verwandeln und ist bereit, es mit jedem aufzunehmen, der es wagt, ihn herauszufordern!', '', 38134),
(201939, 'deDE', '', 'Wartet, bis die Bestie vorbei ist.', 38134),
(201938, 'deDE', 'Vergesst nicht, dass wir nur durch die Unterstützung unserer Schirmherren und Zuschauern wie Euch genug Anima haben, um dieser Kunst zu frönen! Und jetzt, ohne weitere Umschweife...', '', 38134),
(201937, 'deDE', 'Die einst wunderschöne Königin Azshara, die in den Tiefen jenseits der Wellen herrschte, griff ihre Feinde an! Nachdem sie sie in ihren Palast gelockt hatte, nutzte sie höfische List und viele, viele Tentakel, um sie zu schlagen! Ja, Königin!', '', 38134),
(201936, 'deDE', 'Seid vorsichtig! Sie ist fast verloren...', '', 38134),
(201935, 'deDE', 'Aufgehorcht, Kunstliebhaber! Dank der großzügigen Schirmherrschaft von $n darf ich eine neue Vorstellung ankündigen, die bald im Sternseeamphitheater aufgeführt wird!', '', 38134),
(201933, 'deDE', '', 'Bleibt dicht bei mir. Ich will keine Aufmerksamkeit durch einen Kampf.', 38134),
(201932, 'deDE', '', 'Bringt sie schnell zur Strecke! Oder wir verlieren sie!', 38134),
(201931, 'deDE', '', 'Wir müssen Yseras Wildsamen zur Königin bringen, das ist ihre einzige Hoffnung!', 38134),
(201930, 'deDE', '', 'Ich kehre zu den anderen Augen zurück. Geht schnell zu Werk.', 38134),
(201928, 'deDE', 'Ah, welch herrlicher Tag für Inspiration! Mein Herz singt das Lied der Schöpfung! Meine Schwingen zittern vor Aufregung, wenn all die künstlerischen Möglichkeiten mir durch den Geist gehen! Es ist ein wahres Wunder, dass wir genug Anima haben, um mir zu gestatten, wieder Kunst zu schaffen.$b$bIch habe lange gearbeitet und weiß, dass ich eine Darbietung erschaffen habe, die den Ardenwald bis in die Wurzeln erschüttern wird. Möchtet Ihr sie sehen? Die Macht meiner Leidenschaft?', '', 38134),
(201926, 'deDE', 'Oh, weh mir!$b$BAh, $umein Freund:meine Freundin;, mich dürstet. Nicht nach Speis und Trank, jedenfalls nicht nach etwas, das Euch bekannt wäre. Mich dürstet nach der Nahrung des Geistes! Nach den bildenden Künsten, die uns zu Höhenflügen inspirieren, höher, als die stärksten aller Feen fliegen könnten!$b$BOh, aber die Dürre hat unsere Kreativität versiegen lassen. Schließlich müssen wir ums nackte Überleben kämpfen.$B$BDoch sagt mir, ist ein Leben ohne Kunst wahrhaft lebenswert?', '', 38134),
(201925, 'deDE', 'Die Uhr tickt! Los jetzt!', '', 38134),
(201924, 'deDE', '', 'Eile ist jetzt eher geboten als Tarnung. Wir sollten zum Portalstein fliegen und den Schlund schnell hinter uns lassen.', 38134),
(201923, 'deDE', 'Gebt mir Deckung!', '', 38134),
(201922, 'deDE', 'Sprengladung ist fertig! Zurück!', '', 38134),
(201921, 'deDE', 'Zeit für ein Blutbad!', '', 38134),
(201920, 'deDE', 'Das sollte reichlich Schaden anrichten!', '', 38134),
(201905, 'deDE', '', 'Wir müssen alle bedenken, dass Erlösung der wahre Zweck von Revendreth ist.', 38134),
(201904, 'deDE', 'Sichert die verbleibenden Seelen. Wir werden ihnen nach Möglichkeit helfen.', '', 38134),
(201903, 'deDE', 'Zieht Euch mit allen Seelen zurück, die Ihr beherrschen könnt!', 'Zieht Euch mit allen Seelen zurück, die Ihr beherrschen könnt!', 38134),
(201902, 'deDE', '', 'Verschont die Seelen, wenn es geht. Wir können sie vielleicht noch retten!', 38134),
(201901, 'deDE', '', 'Die Verderbtheit des Meisters kennt keine Grenzen!', 38134),
(201900, 'deDE', 'Diese Seelen wurden vom Meister missbraucht und in Waffen verwandelt!', '', 38134),
(201892, 'deDE', 'Sehr gut! Ich stelle sicher, dass diese Schleime unverzüglich rekonstituiert werden.', '', 38134),
(201891, 'deDE', 'Ah, Lehrling! Wir haben gerade Schleimmangel. Würdet Ihr ein wenig für unsere Zwecke bereitstellen?', '', 38134),
(201890, 'deDE', 'Hallo! Seuchenerfinder Marileth hat mich hierhergeschickt, um diese Ausrüstung zu testen. Wir sammeln die lebenden Schleime in dieser Gegend. Oder zumindest Proben davon. Wollt Ihr helfen?', '', 38134),
(201883, 'deDE', '', 'Beeindruckende Auswahl an Feinden, $p...', 38134),
(201875, 'deDE', 'Dieses Individuum hat die Macht, Maldrazit mit Anima zu erfüllen. Außerdem hat es Angst. Seht Ihr den Ausdruck in seinen Augen?', 'Dieses Individuum hat die Macht, Maldrazit mit Anima zu erfüllen. Außerdem hat es Angst. Seht Ihr den Ausdruck in seinen Augen?', 38134),
(201874, 'deDE', 'Dieses Individuum hat die Macht, Maldrazit mit Anima zu erfüllen.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Präparierter Maldrazit]|h|r: Ihr führt einen Splitter Maldrazit mit Euch, der die Seelen der von Euch besiegten Gegner absorbiert. Wenn Ihr unter einen Gesundheitsschwellenwert fallt, zerspringt der Splitter, stößt Gegner in der Nähe zurück und gewährt Euch für kurze Zeit einen Absorptionsschild.\n\nDieser Effekt kann nicht sehr oft auftreten, aber bei jedem Todesstoß verkürzt sich die Abklingzeit.', 'Dieses Individuum hat die Macht, Maldrazit mit Anima zu erfüllen.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[Präparierter Maldrazit]|h|r: Ihr führt einen Splitter Maldrazit mit Euch, der die Seelen der von Euch besiegten Gegner absorbiert. Wenn Ihr unter einen Gesundheitsschwellenwert fallt, zerspringt der Splitter, stößt Gegner in der Nähe zurück und gewährt Euch für kurze Zeit einen Absorptionsschild.\n\nDieser Effekt kann nicht sehr oft auftreten, aber bei jedem Todesstoß verkürzt sich die Abklingzeit.', 38134),
(201873, 'deDE', 'Passt auf Eure Hände auf! Dieses Individuum ist geschickt im Umgang mit Eis und Frost.', 'Passt auf Eure Hände auf! Dieses Individuum ist geschickt im Umgang mit Eis und Frost.', 38134),
(201872, 'deDE', 'Passt auf Eure Hände auf! Dieses Individuum ist geschickt im Umgang mit Eis und Frost.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Arktisches Fleisch]|h|r: Wenn Ihr Schaden erleidet, besteht eine Chance, dass Ihr einen Stapel von \'Kristallisierende Anima\' erhaltet, der Euren erlittenen Schaden verringert. Wenn Ihr bei der maximalen Anzahl Stapel Schaden erleidet, werden Gegner in der Nähe für 5 Sek. eingefroren und erleiden währenddessen mehr Schaden.', 'Passt auf Eure Hände auf! Dieses Individuum ist geschickt im Umgang mit Eis und Frost.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[Arktisches Fleisch]|h|r: Wenn Ihr Schaden erleidet, besteht eine Chance, dass Ihr einen Stapel von \'Kristallisierende Anima\' erhaltet, der Euren erlittenen Schaden verringert. Wenn Ihr bei der maximalen Anzahl Stapel Schaden erleidet, werden Gegner in der Nähe für 5 Sek. eingefroren und erleiden währenddessen mehr Schaden.', 38134),
(201870, 'deDE', 'Dieses Individuum beherrscht die Beschwörung klappriger Kreaturen. Ziemlich ekelhaft.', 'Dieses Individuum beherrscht die Beschwörung klappriger Kreaturen. Ziemlich ekelhaft.', 38134),
(201869, 'deDE', 'Dieses Individuum beherrscht die Beschwörung klappriger Kreaturen.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Skelettbefehligung]|h|r: Wenn der passive Effekt von \'Fleischformung\' die Essenz einer Leiche verschlingt, wird dadurch oft ein Skelett beschworen, das an Eurer Seite kämpft.', 'Dieses Individuum beherrscht die Beschwörung klappriger Kreaturen.\n\nWenn Ihr die Verstärkung aus ihm extrahiert, erhaltet Ihr Folgendes:\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[Skelettbefehligung]|h|r: Wenn der passive Effekt von \'Fleischformung\' die Essenz einer Leiche verschlingt, wird dadurch oft ein Skelett beschworen, das an Eurer Seite kämpft.', 38134),
(201868, 'deDE', '', 'Kommt, Uther. Ich bringe Euch in Sicherheit.', 38134),
(201865, 'deDE', 'Seid gegrüßt, $umein Freund:meine Liebe;.', 'Seid gegrüßt, $umein Freund:meine Liebe;.', 38134),
(201864, 'deDE', 'Wie kann ich zu Diensten sein?', 'Wie kann ich zu Diensten sein?', 38134),
(201863, 'deDE', 'Was kann ich für Euch tun?', 'Was kann ich für Euch tun?', 38134),
(201861, 'deDE', '', 'Selbstverständlich, Herrin. Diese Diener der Archon sind bedeutungslos.', 38134),
(201860, 'deDE', '', 'Enttäuscht mich nicht, Lysonia. Beweist, dass Ihr Eurer neuen Macht würdig seid.', 38134),
(201859, 'deDE', '', 'Diese Drust gehen mir auf die Schwingen! Wir müssen den Hain und Yseras Wildsamen verteidigen, sonst war alles umsonst!', 38134),
(201858, 'deDE', '', 'Euch bedrückt etwas, Thrall. Was ist es?', 38134),
(201857, 'deDE', '', 'Wenn es einen Weg aus dem Schlund gibt, gibt es auch einen hinein. Wir werden zurückkehren.', 38134),
(201855, 'deDE', '', 'Ja, aber wir wissen nicht, wie wir sie finden sollen. Wir versuchen es, aber dieser Portalstein könnte der einzige Weg hier raus sein.', 38134),
(201853, 'deDE', 'Immer noch keine Spur von Tyrande. Wir dürfen sie nicht in diesem Alptraum alleinlassen.', '', 38134),
(201851, 'deDE', 'Sie ist vor uns in den Riss geeilt. Sie wollte Sylvanas Windläufer finden... um jeden Preis.', '', 38134),
(201850, 'deDE', 'Aber weiter kein Zeichen von der Hohepriesterin. Vielleicht ist sie vorausgeeilt.', '', 38134),
(201849, 'deDE', 'Gut, dass Ihr es durch den Riss geschafft habt. Ohne Euch würde diese Mission scheitern.', '', 38134),
(201848, 'deDE', '', 'Große Gefahr zieht herauf. Kommt zu mir, damit wir ihr gemeinsam trotzen.', 38134),
(201844, 'deDE', '', 'Ja, bringt mich zu diesem Ardenwald. Ich brauche meine Stärke, um meinen Zorn auf Mueh\'zala loszulassen!', 38134),
(201842, 'deDE', '', 'Ich erinnere mich daran, einem Wald entrissen worden zu sein. Ich weigerte mich, diesem elenden Mueh\'zala zu helfen... und bin hier gelandet.\n\nWäre ich bei Kräften, würde ich ihn in Stücke reißen und sie über alle Welten verteilen!\n\nIch muss in diesen Wald zurück. Wie könnt Ihr mir helfen?', 38134),
(201841, 'deDE', '', 'Gesegnet sei ihre Stimme.', 38134),
(201840, 'deDE', '', 'Kah-Delen, bringt die Repräsentanten des Pakts zur Enklave. Wenn sie sterbliche Seele und ich hier fertig sind, treffen wir Euch dort. Mit ihrer Stimme habe ich gesprochen.', 38134),
(201839, 'deDE', '', 'Dann muss ich dem Ruf folgen. Kommt, Schlundgänger, vielleicht können sie uns den Weg weisen.', 38134),
(201838, 'deDE', 'Geehrte Stimme! Die Ewigen haben Euch zur Kammer der Richterin einbestellt!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201837, 'deDE', 'Der Kerkermeister scheint auch die Schicksalsfäden unserer Welt zu ziehen.', '', 38134),
(201836, 'deDE', '', 'Möge die Vorsehung uns retten! Es ist noch schlimmer als erwartet!', 38134),
(201835, 'deDE', 'Denathrius ist schuld an der Animadürre und hat seine riesigen Vorräte in den Schlund geleitet. Er ist mit dem Kerkermeister im Bunde.', '', 38134),
(201834, 'deDE', '', 'Ich spüre eine vertraute Energie, die mich nach Westen zieht. Wir müssen die Pläne von Lysonia und ihrem Meister vereiteln.', 38134),
(201833, 'deDE', '', 'Ich will das nicht bereuen.', 38134),
(201832, 'deDE', '', 'Trefft mich beim Haus der Augen. Eine Mission dieser Art erfordert... Raffinesse.', 38134),
(201831, 'deDE', '', 'Wir können den Sitz ohne Nekropole nicht halten. Also übernehmen wir eine von unserem Feind.', 38134),
(201830, 'deDE', '', 'Nein. Es reicht!', 38134),
(201829, 'deDE', '', 'Stattdessen stehlt und zerstört Ihr. Eine Schande. Ihr tut mir fast leid.', 38134),
(201828, 'deDE', '', 'Die Drust hätten Beschützer der Natur sein können. Hüter des Kreislaufs.', 38134),
(201826, 'deDE', '', 'Wir holen uns jeden Hain. Jede Fae. Jede Seele.', 38134),
(201825, 'deDE', '', 'Und doch werden wir stärker, während Eure Untertanen verkümmern!', 38134),
(201824, 'deDE', '', 'Selbstsüchtige Kreaturen. Ihr seid nichts als Euer eigenes Verlangen.', 38134),
(201823, 'deDE', '', 'Seht zu, wie ich Euren Thron einnehme und das Herz des Waldes zerquetsche!', 38134),
(201822, 'deDE', '', 'Spart Euch Euer Mitleid, schwindende Königin.', 38134),
(201820, 'deDE', 'Wilde Jagd, wir reiten! Für den Ardenwald!', '', 38134),
(201813, 'deDE', 'Das is\' Ashamane! \'Ne wilde Göttin aus dem Krieg der Ahnen!', '', 38134),
(201812, 'deDE', 'Wir haben unsere Befehle. Kämpft tapfer, Kyrianer der Bastion.', '', 38134),
(201811, 'deDE', 'Seid gegrüßt, $C. Ich möchte Euch die Hilfe der Wilden Jagd gegen unsere Feinde anbieten.\n\nNehmt Ihr meine Hilfe an, gewährt Euch das:\n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Vorkaiansturm]|h|r: Wenn Ihr in der Unterzahl seid, haben Eure Zauber und Fähigkeiten eine Chance, Vorkai herbeizurufen. Diese stürmen auf Euer Ziel zu, verursachen Naturschaden und betäuben Gegner in ihrer Bahn.', 'Seid gegrüßt, $C. Ich möchte Euch die Hilfe der Wilden Jagd gegen unsere Feinde anbieten.\n\nNehmt Ihr meine Hilfe an, gewährt Euch das:\n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[Vorkaiansturm]|h|r: Wenn Ihr in der Unterzahl seid, haben Eure Zauber und Fähigkeiten eine Chance, Vorkai herbeizurufen. Diese stürmen auf Euer Ziel zu, verursachen Naturschaden und betäuben Gegner in ihrer Bahn.', 38134),
(201810, 'deDE', 'Mit meiner Verstärkung vernichtet die Erde selbst die Feinde des Ardenwalds.\n\nWenn Ihr meine Hilfe annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Tirnennzorn]|h|r: Eure Zauber und Fähigkeiten haben eine hohe Chance, Erdsplitter herbeizurufen, die dem Gegner pro Sekunde Blutungsschaden als Naturschaden zufügen, der stapelbar ist. Gegner, die sterben, während sie mit der maximalen Anzahl von Stapeln belegt sind, explodieren und fügen Gegnern in der Nähe Naturschaden zu.', 'Mit meiner Verstärkung vernichtet die Erde selbst die Feinde des Ardenwalds.\n\nWenn Ihr meine Hilfe annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[Tirnennzorn]|h|r: Eure Zauber und Fähigkeiten haben eine hohe Chance, Erdsplitter herbeizurufen, die dem Gegner pro Sekunde Blutungsschaden als Naturschaden zufügen, der stapelbar ist. Gegner, die sterben, während sie mit der maximalen Anzahl von Stapeln belegt sind, explodieren und fügen Gegnern in der Nähe Naturschaden zu.', 38134),
(201809, 'deDE', 'Ich helfe allen, die gehen, rennen oder traben, stets leichtfüßig zu bleiben, $R.\n\nWenn Ihr meine Hilfe annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Schwindlermanöver]|h|r: Bei Einsatz außerhalb des Kampfes verhindert \'Seelengestalt\', dass Gegner Euch bemerken.', 'Ich helfe allen, die gehen, rennen oder traben, stets leichtfüßig zu bleiben, $R.\n\nWenn Ihr meine Hilfe annehmt, gewährt Euch das:\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[Schwindlermanöver]|h|r: Bei Einsatz außerhalb des Kampfes verhindert \'Seelengestalt\', dass Gegner Euch bemerken.', 38134),
(201802, 'deDE', 'Haltet die Bande fest un\' manifestiert Euren Geist nich\' voll an dem dunklen Ort. Sonst macht Eure Seele vielleicht PUFF!', '', 38134),
(201789, 'deDE', 'Benachrichtigt die Winterkönigin. Sie muss davon erfahren.', '', 38134),
(201788, 'deDE', '', 'Leider sind sie verloren. Gorak Zhar wird für den Schaden bezahlen, den sie meinem Reich zugefügt hat.', 38134),
(201787, 'deDE', '', 'Besorgniserregende Neuigkeiten. Ich hatte gehofft, wir könnten die von den Drust Besessenen heilen.', 38134),
(201779, 'deDE', 'Mit meiner Verstärkung vernichtet die Erde selbst alle Feinde des Ardenwalds.', 'Mit meiner Verstärkung vernichtet die Erde selbst alle Feinde des Ardenwalds.', 38134),
(201774, 'deDE', 'Ich helfe allen, die gehen, rennen oder traben, stets leichtfüßig zu bleiben, $R.', 'Ich helfe allen, die gehen, rennen oder traben, stets leichtfüßig zu bleiben, $R.', 38134),
(201773, 'deDE', 'Ein guter Kampf! Jetzt zur Sache.', '', 38134),
(201772, 'deDE', 'Zwei gegen einen? Dass Ihr Euch auch nicht übernehmt!', '', 38134),
(201771, 'deDE', 'Ich halte hier die Stellung. Geht!', '', 38134),
(201770, 'deDE', '', 'Es ist vollbracht. Ich werde diesen Boden halten.', 38134),
(201766, 'deDE', '', 'Ich übernehme ab hier. Los!', 38134),
(201765, 'deDE', '', 'Hellfunkel? Gerwyn? Nicht auch noch Ihr...', 38134),
(201764, 'deDE', '', 'Schnell, helft mir, mit dem Anker zu kommunizieren!', 38134),
(201763, 'deDE', '', 'Helft mir, den Anker zurückzuholen, $p.', 38134),
(201762, 'deDE', '', 'Ich reiße Euch in Stücke!', 38134),
(201747, 'deDE', 'Ich freue mich so. Ein Forschungsausflug ist genau das, was Kevin braucht. Ich bin mir sicher, unsere Kollegen werden es zu schätzen wissen, dass wir daran gedacht haben, ihnen einige Schleime mitzubringen!', '', 38134),
(201744, 'deDE', '', 'Verzeiht mir, ich muss einen Moment ruhen.', 38134),
(201741, 'deDE', '', 'Ich habe Lysonia hinter diesen Toren aufgespürt. Meine erste Erkundung war... beunruhigend.', 38134),
(201737, 'deDE', 'Herrjemine. Hilfe! Kann mich jemand hören?', '', 38134),
(201730, 'deDE', 'Mit Freunden feiern gehen... Was Schöneres gibt es nicht!', '', 38134),
(201727, 'deDE', 'Ohne die Unterstützung der Steingeborenen können wir nicht fortfahren.', '', 38134),
(201726, 'deDE', 'Kel\'Thuzads Lichs haben sie zu Stein verwandelt.', '', 38134),
(201724, 'deDE', 'Eure Knochen werden Mueh\'zalas Thron zier\'n!', '', 38134),
(201722, 'deDE', 'Vernichtet alle, die sich Mueh\'zala entgegenstell\'n!', '', 38134),
(201721, 'deDE', 'Schluss jetz\'! Kommt her, Tote aus\'m Schreckenssumpf!', '', 38134),
(201720, 'deDE', 'Seid gegrüßt, $C. Ich möchte Euch die Hilfe der Wilden Jagd gegen unsere Feinde anbieten.', 'Seid gegrüßt, $C. Ich möchte Euch die Hilfe der Wilden Jagd gegen unsere Feinde anbieten.', 38134),
(201718, 'deDE', 'Mueh\'zala... gewinnt... immer...', '', 38134),
(201717, 'deDE', 'Möge Mueh\'zala Euch verfluchen...', '', 38134),
(201716, 'deDE', 'Für Euch... besteht keine Hoffnung...', '', 38134),
(201715, 'deDE', 'Mueh\'zala... wird sich... erheben...', '', 38134),
(201713, 'deDE', 'Nicht... schon wieder...', '', 38134),
(201708, 'deDE', 'Ihr seid unbedeutend für Mueh\'zala!', '', 38134),
(201705, 'deDE', 'Ihr werdet mit Bwonsamdi fallen!', '', 38134),
(201704, 'deDE', 'Niemand verrät Mueh\'zala!', '', 38134),
(201703, 'deDE', 'Mueh\'zala wird wieder herrschen!', '', 38134),
(201701, 'deDE', 'Wir werden den Meister befreien!', '', 38134),
(201695, 'deDE', 'Es hat funktioniert! Was für ein... beeindruckender Ort!', '', 38134),
(201694, 'deDE', 'Ich bin dicht bei Euch. Möge die Erdenmutter uns führen.', '', 38134),
(201693, 'deDE', 'Der Portalstein! Hoffentlich kann ich ihn dank Eurer Anwesenheit auch benutzen.', '', 38134),
(201690, 'deDE', 'Aber ich habe offensichtlich noch viel zu lernen, bevor ich Zena auffordern kann.', '', 38134),
(201689, 'deDE', 'Oh danke, danke, dass Ihr mich in Sicherheit gebracht habt. Diese verflixten Spriggans machen nur Ärger!', '', 38134),
(201637, 'deDE', 'Bwonsamdi bet\'n alle Zandalari an! Ohne den Kerkermeister wärt Ihr nur \'n vergessener Loa. Armer Mueh\'zala!', '', 38134),
(201636, 'deDE', '', 'Trefft mich in meiner Höhle. Ich helfe Euch, so gut ich kann.', 38134),
(201635, 'deDE', 'Meine Truppen hol\'n mich raus... Un\' dann wird es Euch leidtun.', '', 38134),
(201634, 'deDE', '', 'Xandria erwartet unsere Ankunft auf der Zerekriss. Gehen wir.', 38134),
(201631, 'deDE', 'Herrje. Oje. Etwas Seltsames passiert mit dem Wasser.', '', 38134),
(201630, 'deDE', 'Schön, Euch wiederzuseh\'n, $n. Bwonsamdi weiß, dass er auf Euch zähl\'n kann.\n\nEs wird viel Arbeit, die Streitkräfte von Mueh\'zala aufzuhalt\'n.', '', 38134),
(201629, 'deDE', 'Ich schwinge mich in die Lüfte und kundschafte das Land unten aus.', '', 38134),
(201628, 'deDE', 'Niemand hält sich hier ohne mein Wissen auf. Und es gibt kein Entkommen!', '', 38134),
(201627, 'deDE', 'Ihr haltet Euch für schlau? Baut einen Spiegel, um in mein Reich einzubrechen?', '', 38134),
(201626, 'deDE', '', 'Hallo! Ich bin Weidenblüte. Ist es nicht schrecklich, was mit dem Ardenwald passiert?\n\n<Sie kommt näher und flüstert.>\n\nSagt es ihr bitte nicht, aber ich überlege gerade, wie ich es Lady Mondbeere heimzahlen kann. Wir spielen uns immer gegenseitig Streiche und dieses Mal werde ich sie ordentlich reinlegen!\n\nIch habe eine Idee, aber ich muss im Amphitheater irgendwie hinter die Bühne kommen, damit es klappt. Vielleicht könnt Ihr helfen.', 38134),
(201622, 'deDE', 'Diese Fanfare lässt die Schwarze Klinge gegen Euren Feind vorrücken. Setzt sie klug ein.', '', 38134),
(201620, 'deDE', 'Ich werde den Schlundgänger eskortieren, mein Prinz. Kommt, $n.', '', 38134),
(201619, 'deDE', 'Diese Falle sorgt bei Stürzen aus jeglicher Höhe für Sicherheit. Falls das für Euch von Belang sein sollte.', '', 38134),
(201618, 'deDE', 'Werft sie aus dem Fest!', '', 38134),
(201617, 'deDE', 'Diese Falle macht Diebe bewegungsunfähig, damit man sie an einen... angemesseneren Ort befördern kann.', '', 38134),
(201615, 'deDE', 'Mich überrascht so gut wie nichts. Schon gar keine Diebe, die unsere exotischen Waren zu stehlen versuchen.\n\nKartell Ta hat für solche Situationen eine sehr effektive Diebstahlsicherung entwickelt.', '', 38134),
(201613, 'deDE', '$gSchlundgänger:Schlundgängerin;! Brauchen Euch hier unten!', '', 38134),
(201612, 'deDE', 'Und warum macht sich dann der Gräber an dem Käfigschloss hinter Euch zu schaffen?', '', 38134),
(201611, 'deDE', 'Kein Bedarf. Keine Diebe hier!', '', 38134),
(201610, 'deDE', '', 'Beißende Winde werden zur lauen Brise. Schwebt zu den anderen. Helft dem Wald.', 38134),
(201609, 'deDE', 'Ich habe genug von diesem Ort. Gehen wir.', '', 38134),
(201608, 'deDE', 'Endlich... die Qualen sind vorüber.', '', 38134),
(201604, 'deDE', 'Bleibt zurück! Sie werden Euch auch binden!', '', 38134),
(201603, 'deDE', 'Oh! Ein Fest ist genau, was ich brauche, um meine Stärke zurückzuerlangen. Dann stürze ich mich wieder in den Kampf!', '', 38134),
(201602, 'deDE', 'Für eine kleine Gebühr zeige ich Euch, wie Kartell Ta mit Dieben umgeht.', '', 38134),
(201600, 'deDE', 'Ja. Wenn Choofa geht, gehe ich auch. Nach meinem Nickerchen.', '', 38134),
(201599, 'deDE', 'Sündensturz, zu mir! Gemeinsam marschieren wir gegen Maldraxxus!', '', 38134),
(201598, 'deDE', 'Unser Volk wurde massakriert, unser Sonnenbrunnen entweiht, um einen Lich zu erwecken.', '', 38134),
(201595, 'deDE', 'Habt Dank! Ohne Euch wäre all das nicht möglich gewesen.', '', 38134),
(201594, 'deDE', 'Es tröstet mich, dass Weidenblüte weiterleben und Teil aller neuen Aufgestiegenen sein wird.', '', 38134),
(201593, 'deDE', 'Stellt das Gefäß hier ab, $n.', '', 38134),
(201591, 'deDE', 'Kommt. Pelagos erwartet uns am Emblem.', '', 38134),
(201590, 'deDE', 'Ich fühle mich geehrt, welch großes Opfer der Ardenwald für uns bringt.', '', 38134),
(201589, 'deDE', 'Halt! Was ist das da vorn?', '', 38134),
(201588, 'deDE', 'Die Lordadmiralin muss wirklich mächtig sein, wenn sie eine dieser Monstrositäten bezwingen konnte.', '', 38134),
(201587, 'deDE', 'Es wäre unklug, die Verzauberung zu brechen. Lasst uns weitergehen.', '', 38134),
(201586, 'deDE', 'Und wieder Lady Prachtmeers Handschrift.', '', 38134),
(201585, 'deDE', 'Der Konflikt scheint sich in diese Richtung zu bewegen. Wir sollten der Spur folgen, bevor sie... erkaltet.', '', 38134),
(201584, 'deDE', 'Frostmagie. Sieht nach der Arbeit der Lordadmiralin aus.', '', 38134),
(201582, 'deDE', '', 'Behaltet Ara\'lon gut im Auge, $n. Seine Schuld lastet schwer auf ihm...', 38134),
(201581, 'deDE', 'Kivarr lebt in einer Höhle in der Nähe des Abendhains. Ich treffe Euch dort.', '', 38134),
(201580, 'deDE', 'Wir finden einen Weg, Euch zu retten, Gweyir. Ich schwöre es...', '', 38134),
(201579, 'deDE', '', 'Sie schläft jetzt. Ihre Träume werden sie trösten... eine Zeit lang.', 38134),
(201578, 'deDE', 'Gorak Zar und ihre Schergen müssen aufgehalten werden. Findet Kivarr in den Schattenlanden und bereitet dem Fluch ein Ende.', '', 38134),
(201577, 'deDE', '', 'Gol Inath wird Euer Grab!', 38134),
(201576, 'deDE', '', 'Der geistlose Dornsprecher hat Euch mir in die Arme getrieben.', 38134),
(201575, 'deDE', 'Gorak Tul mag tot sein, aber seine dunkle Präsenz hält sich noch innerhalb der Mauern auf.', '', 38134),
(201574, 'deDE', 'Platziert diesen Fetisch im Kessel in Gol Inath, aber seid vorsichtig...', '', 38134),
(201573, 'deDE', '', 'Wenn man den Fluch der Drust brechen kann, weiß Ulfar, wie. Vertraut auf seine Weisheit.', 38134),
(201572, 'deDE', '', 'Vielleicht ist die junge Jägerin noch nicht verloren...', 38134),
(201571, 'deDE', 'Berichtet Lord Herne. Ich bringe Gweyir in Sicherheit.', '', 38134),
(201570, 'deDE', 'Setzt Eure Maske auf, wenn Ihr soweit seid. Wir müssen die anderen finden.', '', 38134),
(201569, 'deDE', 'Wir brauchen Verkleidungen. Sucht die Umgebung nach etwas tauglichem ab.', '', 38134),
(201567, 'deDE', 'Wir dürfen keine weiteren Jäger und Jägerinnen verlieren. Findet sie und bringt sie heim.', '', 38134),
(201564, 'deDE', '', 'Haltet das Gebiet. Ruft, wenn Ihr Verstärkung benötigt!', 38134),
(201563, 'deDE', 'Verdoppelt die Patrouillen! Lasst niemanden entkommen!', '', 38134),
(201562, 'deDE', '', 'Zeigt ihnen, wie Maldraxxus mit Verrätern verfährt!', 38134),
(201561, 'deDE', 'Rache... für den Meister!', 'Rache... für den Meister!', 38134),
(201560, 'deDE', 'Alarm für alle Wachen! Achtet auf entkommene Gefangene!', '', 38134),
(201559, 'deDE', 'Die Spiegelgefängnisse geben nach!', '', 38134),
(201558, 'deDE', 'Bitte richtet Prinz Renathal meinen Dank für die Einladung aus. Ich werde dem Gluthof gewiss beiwohnen.', '', 38134),
(201557, 'deDE', 'Wie Ihr meint, Paragon. Ich werde mein Unwohlsein überwinden.', '', 38134),
(201556, 'deDE', 'Vermutlich. Umso mehr freut mich diese Gelegenheit für Euch, neue Fähigkeiten dazuzulernen, um dem Willen der Archon zu dienen.', '', 38134),
(201555, 'deDE', 'Nein, natürlich nicht. Aber es gibt sicherlich andere, die für solche Frivolitäten besser geeignet sind?', '', 38134),
(201554, 'deDE', 'Die Richterin ist der letzte Schlüssel. Schützt sie, oder alles ist verloren.', '', 38134),
(201553, 'deDE', 'Warnt die Archon, die Winterkönigin und den Grafen. Sie müssen uns verteidigen. Zovaal darf das Grabmal nicht erreichen.', '', 38134),
(201552, 'deDE', 'Es gibt nur eine Hoffnung für die Schattenlande. Die Ewigen müssen erneut zusammenstehen, bevor der Kerkermeister dem Schlund entkommt.', '', 38134),
(201551, 'deDE', 'Dass Ihr nun diese Botschaft hört, bestätigt meinen Verdacht. Zovaal hat seine Ketten zu einer Waffe geschmiedet... und mich damit zu Fall gebracht.', '', 38134),
(201549, 'deDE', 'Wir sind auserwählt. Kampf ist unsere Stärke. Wir müssen unsere mächtigsten Soldaten versammeln... sogar unseren vermissten Baron.', '', 38134),
(201548, 'deDE', 'Jetzt fürchte ich, dass Zovaal nicht allein gehandelt hat. Er muss uralte Verbündete haben... und versucht, neue zu finden.', '', 38134),
(201547, 'deDE', 'Vor Äonen bestraften die Ewigen unseren Bruder Zovaal für seinen Verrat. Er wurde für immer an den Schlund gefesselt, um dort als Kerkermeister zu dienen.', '', 38134),
(201545, 'deDE', 'Ich bin der Primus. Eure Anwesenheit in meinem Sanktum zeigt, dass Dunkelheit über Maldraxxus... und allen Reichen des Todes liegt.', '', 38134),
(201544, 'deDE', 'Ah, Adrestes. Ich hörte, Ihr seid zu wichtig, um die Einladung von Prinz Renathal anzunehmen? Oder habe ich Eure Bedenken missverstanden?', '', 38134),
(201542, 'deDE', 'Die nötige Geduld, um zu lernen.\n\nDas nötige Wissen, um Perspektive zu gewinnen.\n\nDie nötige Einsicht, um zu wählen.\n\nDas nötige Urteilsvermögen, um tiefere Wahrheit zu verstehen.\n\nDas ist der Pfad der Weisheit.', 'Die nötige Geduld, um zu lernen.\nDas nötige Wissen, um Perspektive zu gewinnen.\nDie nötige Einsicht, um zu wählen.\nDas nötige Urteilsvermögen, um tiefere Wahrheit zu verstehen.\n\nDas ist der Pfad der Weisheit.', 38134),
(201538, 'deDE', 'Es wäre mir eine Ehre, teilzunehmen. Doch die Einladung ist für Polemarch Adrestes.', '', 38134),
(201536, 'deDE', 'Es ist keine Schande, andere um Hilfe zu bitten.', '', 38134),
(201535, 'deDE', 'Ich danke Euch, $p! Ich habe nicht viel anzubieten, aber bitte nehmt diese Mondsichtgabe als Dankeschön für alles, was Ihr getan habt. Mit den Werkzeugen kann ich jetzt wieder meine Arbeit im Garten aufnehmen.', 'Ich danke Euch, $p! Ich habe nicht viel anzubieten, aber bitte nehmt diese Mondsichtgabe als Dankeschön für alles, was Ihr getan habt. Mit den Werkzeugen kann ich jetzt wieder meine Arbeit im Garten aufnehmen.', 38134),
(201534, 'deDE', '', 'Das Gleichgewicht muss wiederhergestellt werden, auch zu diesem hohen Preis. Habt Dank.', 38134),
(201531, 'deDE', 'Ich scheine meinen Korb, die Flöte, den Hammer und den Zauberstab verloren zu haben. Oh, und mein Notizbuch! Die Historie des Gartens geht verloren, wenn die Spriggans es vernichten...\n\nIch weiß nicht, wie ich mich für Eure Freundlichkeit revanchieren kann, aber wenn Ihr meine Sachen findet, werde ich Euch auf ewig dankbar sein.', 'Ich scheine meinen Korb, die Flöte, den Hammer und den Zauberstab verloren zu haben. Oh, und mein Notizbuch! Die Historie des Gartens geht verloren, wenn die Spriggans es vernichten...\n\nIch weiß nicht, wie ich mich für Eure Freundlichkeit revanchieren kann, aber wenn Ihr meine Sachen findet, werde ich Euch auf ewig dankbar sein.', 38134),
(201530, 'deDE', 'Wirklich?! Ihr seht zwar wie $gein mächtiger:eine mächtige:c; $C aus, aber es könnte gefährlich sein. Und ich möchte auf keinen Fall, dass Euch etwas passiert.', 'Wirklich?! Ihr seht zwar wie $gein mächtiger:eine mächtige:c; $C aus, aber es könnte gefährlich sein. Und ich möchte auf keinen Fall, dass Euch etwas passiert.', 38134),
(201529, 'deDE', 'Auf bald!', '', 38134),
(201526, 'deDE', 'Es ist meine Schuld, dass die Drust Gweyir gefangen genommen und die Kontrolle über ihn übernommen haben...', '', 38134),
(201525, 'deDE', '\"Das nötige Urteilsvermögen, um tiefere Wahrheit zu verstehen.\"\n\nIch habe den Pfad zwar bewältigt, aber etwas stimmt hier nicht. Ich muss die wahre Antwort übersehen haben.', '\"Das nötige Urteilsvermögen, um tiefere Wahrheit zu verstehen.\"\n\nIch habe den Pfad zwar bewältigt, aber etwas stimmt hier nicht. Ich muss die wahre Antwort übersehen haben.', 38134),
(201524, 'deDE', '\"Die nötige Einsicht, um zu wählen.\"\n\nDarüber muss ich länger meditieren.', '\"Die nötige Einsicht, um zu wählen.\"\n\nDarüber muss ich länger meditieren.', 38134),
(201523, 'deDE', '\"Das nötige Wissen, um Perspektive zu gewinnen.\"\n\nWas soll ich bloß erkennen?', '\"Das nötige Wissen, um Perspektive zu gewinnen.\"\n\nWas soll ich bloß erkennen?', 38134),
(201522, 'deDE', '\"Die nötige Geduld, um zu lernen.\"\n\nIch muss innehalten und nachdenken. Hast führt nirgendwohin.', '\"Die nötige Geduld, um zu lernen.\"\n\nIch muss innehalten und nachdenken. Hast führt nirgendwohin.', 38134),
(201521, 'deDE', 'AAAARRRRRRGH!', '', 38134),
(201520, 'deDE', 'Elemente... hört mich. Gebt mir die Kraft, um...', '', 38134),
(201519, 'deDE', '', 'Wie oft muss ich Euch denn noch besiegen?', 38134),
(201518, 'deDE', '', 'Genug davon!', 38134),
(201513, 'deDE', 'Unsere Augen sind an das Mondlicht gewöhnt und so entdecken wir viele Geheimnisse in der Welt.\n\nMondsicht ermöglicht Euch, zu sehen, was nur Feen sehen können.', 'Unsere Augen sind an das Mondlicht gewöhnt und so entdecken wir viele Geheimnisse in der Welt.\n\nMondsicht ermöglicht Euch, zu sehen, was nur Feen sehen können.', 38134),
(201512, 'deDE', 'Oh, Lehrling! Da seid Ihr ja!', '', 38134),
(201511, 'deDE', '', 'Diese maskierten Fae haben plötzlich den Hain angegriffen. Bitte, wir müssen sie zurückschlagen!', 38134),
(201510, 'deDE', '', 'Ich verstehe die Vorteile eines koordinierten Angriffs, also werde ich dem Gluthof beiwohnen und die Wilde Jagd bei Diskussionen vertreten.', 38134),
(201509, 'deDE', '', 'Er weiß, dass Ihr hier seid...', 38134),
(201508, 'deDE', '', '<Schimmerflieges Ausdruck wird ernst.>\n\nEs ist eine fürchterliche Tragödie, einen Baum zu verlieren. Wenn es nicht ausreichend Anima für den ganzen Wald gibt, kann es vorkommen, dass die Königin einen Baum sterben lässt, um die anderen zu retten.\n\nEine furchtbare, aber notwendige Entscheidung.\n\nWenn ein Baum stirbt, können die Wildsamen unter seinen Zweigen nicht mehr genährt werden. Einige werden vielleicht in einen anderen Hain gebracht, aber der Rest...\n\nUnd noch schlimmer ist, dass einige behaupten, in den toten Hainen sei die Barriere zwischen dem Ardenwald und den anderen Ebenen am schwächsten.\n\nSie sagen, dass wir an diesen Stellen am verwundbarsten für Angriffe von außen sind.', 38134),
(201507, 'deDE', '', 'Oh ja! Die Tirnenn sind wundervolle, große, laufende und sprechende Baumleute.\n\nSie stehen in irgendeiner Beziehung zu den Bäumen vom Ardenwald, aber sie sind so alt und fremdartig, dass wir nicht genau wissen, wie das zusammenhängt.\n\nEinige von ihnen können sogar die Bäume sprechen hören! Sie sollen aber mehr über Lieder und Melodien kommunizieren als über Worte, sagt man.\n\nDas ist faszinierend!', 38134),
(201505, 'deDE', '', 'Ja! Jeder Baum hat einen Namen!\n\nUnser Baum im Winterschlafbecken heißt Tirna Glayn. Im Norden hat der Hain von Tirna Vaal seinen Namen durch den dortigen Baum erhalten.\n\nTirna Noch, der gefallene Baum, ging schon früher in der Dürre verloren und Tirna Rowen oben im Herzholzhain ist erst kürzlich verschieden.\n\nZu den noch lebenden Bäumen zählt auch Tirna Kaithe im Hain namens Klauenkamm.\n\nTirna Eas ist der Baum im Schimmerkaskadenbecken. Und der Baum meiner Heimat, Tirna Fenn, befindet sich in Traumsang Fenn!\n\nAber es gingen noch andere Bäume verloren, zum Beispiel Tirna Marwol im Dunkelvorstoß.\n\nUnd natürlich ist da noch Tirna Scithe, aber da würde ich mich nicht hintrauen, das ist viel zu gefährlich!', 38134),
(201500, 'deDE', '', 'Grüße. Was möchtet Ihr über unsere Bäume erfahren?', 38134),
(201490, 'deDE', '', 'Vielen Dank, $n. Ich verdanke Euch... alles.', 38134),
(201489, 'deDE', '', 'Weise Worte, Hochlord. Ich traue Eurem Urteil.', 38134),
(201488, 'deDE', 'Bei allem Respekt, Lady Prachtmeer, Ihr müsst erst wieder zu Kräften kommen. Lasst den Schlundgänger nach den anderen suchen.', '', 38134),
(201487, 'deDE', '', 'Ich... verstehe. Gibt es Nachricht von Thrall und Anduin? Wir müssen zurück nach Torghast und...', 38134),
(201486, 'deDE', 'Schön, dass Ihr in Sicherheit seid, Jaina. Meine Wunden heilen, doch... die Schrecken des Schlunds quälen mich weiterhin.', '', 38134),
(201485, 'deDE', '', 'Baine! Dem Licht sei Dank, Ihr lebt!', 38134),
(201477, 'deDE', '', 'Wir müssen fokussiert sein und die Vorsehung umsetzen.', 38134),
(201476, 'deDE', 'Vyraz, Draka. Bringt den Sterblichen zum Sitz des Primus. Sehen wir, was geschieht.', '', 38134),
(201475, 'deDE', 'Erst bringt Ihr Krieg, jetzt die Klinge des Primus. Ihr werdet immer mehr zur Legende!', '', 38134),
(201474, 'deDE', 'Ich hoffe, dass wir Erfolg haben. Es gibt immer noch etwas Anima.', '', 38134),
(201473, 'deDE', 'Anduin... eine Waffe? Was hat sie nur vor?!', '', 38134),
(201464, 'deDE', 'Ich habe mich in Sicherheit bringen können, aber in all dieser Verwirrung ein paar meiner Sachen verloren.\n\nIch weiß, dass die Spriggans meine Werkzeuge zerstören werden!\n\nIch fürchte, ich kann nicht einfach zurückgehen und mir meine Werkzeuge holen. Und dann kann der Garten nicht bestellt werden...', 'Ich habe mich in Sicherheit bringen können, aber in all dieser Verwirrung ein paar meiner Sachen verloren.\n\nIch weiß, dass die Spriggans meine Werkzeuge zerstören werden!\n\nIch fürchte, ich kann nicht einfach zurückgehen und mir meine Werkzeuge holen. Und dann kann der Garten nicht bestellt werden...', 38134),
(201462, 'deDE', 'Ich war im Garten der Nacht, als die Spriggans angriffen. Wellen von ihnen schwärmten durch den Garten und begannen, uns alle zu terrorisieren...', 'Ich war im Garten der Nacht, als die Spriggans angriffen. Wellen von ihnen schwärmten durch den Garten und begannen, uns alle zu terrorisieren...', 38134),
(201454, 'deDE', 'Und meine gefallenen Freunde rächen.', '', 38134),
(201443, 'deDE', 'Diese Krone soll alles verkörpern, wofür Sündensturz steht. Revendreth wird sich vereint von Denathrius\' Sünden befreien.', '', 38134),
(201427, 'deDE', '', 'Der Angriff hat sich herumgesprochen. Ich schließe mich Euch an.', 38134),
(201424, 'deDE', 'Kah-Delen, eine überaus ernste Aufgabe wartet auf uns.', '', 38134),
(201422, 'deDE', 'Sofort, geehrte Stimme.', '', 38134),
(201421, 'deDE', '', 'Der Weg nach Maldraxxus muss geöffnet werden. Holt Kah-Delen und kümmert Euch darum.', 38134),
(201409, 'deDE', '', 'Es sei denn... das ist nur der grausamste Trick von allen.', 38134),
(201408, 'deDE', '', 'Wenn ich meinen Teleportationszauber an Eure Fähigkeit binde, den Schlund zu verlassen, bin ich endlich frei!', 38134),
(201407, 'deDE', '', 'Moment. Ihr seid es wirklich, oder? Ja... Ihr seid mit dem Portalstein geflohen, $n. Es kommt mir so lange vor...', 38134),
(201406, 'deDE', 'Heute ehren wir Giannakis!\n\nWeiß viele Dinge. Liest immer Schriftrollen.\n\nIm Tempel der Demut der finden.\n\nIhr bringt ihm reife Purianfrucht? Bitte?', '', 38134),
(201405, 'deDE', 'Heute ehren wir Ioanna!\n\nManchmal traurig. Aber Weiseste von allen.\n\nAn Provostenrast zu finden.\n\nIhr bringt ihr reife Purianfrucht? Bitte?', '', 38134),
(201396, 'deDE', '', 'Die Königin verlangt Taten, und wir gehorchen.', 38134),
(201395, 'deDE', '', '$p, ihr seid der Sturm, der allen Staub wegfegt.', 38134),
(201394, 'deDE', '', 'Die Äste toben zum Geheul des Waldes!', 38134),
(201392, 'deDE', 'Mein Paragon... Ich habe versagt...', '', 38134),
(201387, 'deDE', '', 'Wir müssen zusammenhalten, wenn wir überleben wollen. Bleibt standhaft!', 38134),
(201383, 'deDE', '', 'Da ist das Paragon! Er steht Lysonia allein gegenüber. Wo sind seine Jünger?', 38134),
(201366, 'deDE', 'Ich habe Kleia auf der Suche nach Lysonia vorausgeschickt. Schnell, trefft Euch mit ihr am Tempel der Demut.', '', 38134),
(201360, 'deDE', '', 'Ja, die Nekrolords sind Meister der Kriegsführung, doch sie haben die Schattenlande mit ihrer Macht immer verteidigt, nicht angegriffen.', 38134),
(201359, 'deDE', 'Das sind schlimme Neuigkeiten. Wir dürfen nicht zulassen, dass Krieg dieses Reich verheert und alles zunichte macht.', '', 38134),
(201347, 'deDE', 'Wie Ihr befehlt, Baronin.', '', 38134),
(201345, 'deDE', 'Hipokos schüttelt den Kopf und deutet dann an die Stelle, an der seine Zunge sein sollte.', '', 38134),
(201343, 'deDE', 'Hipokos! Was... haben sie Euch angetan? Könnt Ihr sprechen?', '', 38134),
(201337, 'deDE', '', 'Wir erstatten Krexus Bericht. Haltet die Stellung, bis wir zurückkommen.', 38134),
(201333, 'deDE', '', 'Schleimer ist in Sicherheit. Er wartet auf Euch in Tirna Vaal.', 38134),
(201332, 'deDE', 'Kota, habt Ihr Schleimer gefunden? Ich habe ihn verloren!', '', 38134),
(201331, 'deDE', '', '<Baronin Vashj schnaubt verächtlich.>', 38134),
(201330, 'deDE', '', 'Sorgt Euch nicht zu sehr, Kind. Vielleicht kann jemand anderes uns helfen, Tyrande Wisperwind zu retten.', 38134),
(201329, 'deDE', '', 'Ysera, ich... konnte Tyrande nicht zurückbringen. Sie wandelt immer noch im Schlund, getrieben von der Macht der Nachtkriegerin. Und Zorn.', 38134),
(201328, 'deDE', '', 'Sie werden hier Frieden finden. Einige werden vielleicht neue Gestalt annehmen und mit den Hainen helfen.', 38134),
(201327, 'deDE', '', 'Was geschieht mit diesen Seelen?', 38134),
(201325, 'deDE', '', 'Mein Geist war fast verloren. Die Winterkönigin hat mich erweckt. Jetzt bin ich an dieses Reich gebunden.', 38134),
(201324, 'deDE', '', 'Das Scheusal ist verwundbar! Tötet es!', 38134),
(201323, 'deDE', '', 'Bei Elune! Ihr könnt diese Seelen retten!', 38134),
(201322, 'deDE', '', 'Bitte, Schlundgänger! Setzt den Kristall ein, um die Geister der Kaldorei zu retten!', 38134),
(201321, 'deDE', '', 'Wenigstens wurden die Seelen dieser Kaldorei befreit. Zum ersten Mal seit Langem spüre ich... Hoffnung.', 38134),
(201299, 'deDE', 'Die steingeborenen Reservisten sind für den heutigen Hof aktiviert.\n\nKeine Sorge. Sie werden sich um Eindringlinge kümmern.', '', 38134),
(201295, 'deDE', 'Haltet Ihr Euch selbst für tapfer, $gAspirant:Aspirantin;?\n\nDann beweist mir Euren Mut.', 'Haltet Ihr Euch selbst für tapfer, $gAspirant:Aspirantin;?\n\nDann beweist mir Euren Mut.', 38134),
(201292, 'deDE', 'Ich bin... ewig.', '', 38134),
(201291, 'deDE', 'Eure Überheblichkeit kennt keine Grenzen, Sterblicher!', '', 38134),
(201289, 'deDE', 'Wir dürfen Euch nicht... an die Finsternis verlieren.', '', 38134),
(201287, 'deDE', '', 'Sha\'lor! Bei der Gnade der Königin, es ist schön, Euch zu sehen.', 38134),
(201286, 'deDE', 'Uff! Nun, eine Art Ausgang war es zumindest.', '', 38134),
(201284, 'deDE', 'Wir haben nichts davon, wenn unser Haus als erstes im Kampf fällt. Wir dürfen uns nicht vom Krieg ablenken lassen.', '', 38134),
(201279, 'deDE', 'Meldet Euch bei der Speerspitze. Wir müssen diese Schlacht gewinnen!', '', 38134),
(201278, 'deDE', 'Seht! Die Barriere um den Tempel ist weg. Gute Arbeit, $n.', '', 38134),
(201277, 'deDE', 'Ich habe vielleicht einen Ausgang gefunden! Aber ich gehe da nicht lang! Hilfe!', '', 38134),
(201276, 'deDE', 'Ich gehe als Kundschafter vorraus. Zaudert nicht, Soldat!', '', 38134),
(201272, 'deDE', 'Was, wenn sie recht haben? Was dann... was dann...', 'Was, wenn sie recht haben? Was dann... was dann...', 38134),
(201271, 'deDE', '', 'Und ich räche mich währenddessen an denen, die sie hierzu verdammt haben!', 38134),
(201270, 'deDE', '', 'Wenn das stimmt, vertraue ich Euch diese heilige Pflicht an ,$n. Rettet die Seelen meines Volkes.', 38134),
(201269, 'deDE', '', 'Wenn das stimmt, vertraue ich Euch diese heilige Pflicht an, Kind Teldrassils. Rettet die Seelen unseres Volkes.', 38134),
(201268, 'deDE', '', 'Dieser Schlundgänger kann unsere Brüder und Schwestern von ihrem Leid befreien.', 38134),
(201267, 'deDE', '', 'Nein! Mein Jagd endet erst, wenn jede Seele gerettet ist... und die Banshee zur Rechenschaft gezogen ist!', 38134),
(201266, 'deDE', '', 'Und ich räche währenddessen die Kaldorei.', 38134),
(201265, 'deDE', '', 'So sei es. Bringt diesen Champion der Horde zur Buße für die Verbrechen seines Volks.', 38134),
(201264, 'deDE', '', 'Ihr müsst ihm nicht vertrauen... aber bitte vertraut mir.', 38134),
(201263, 'deDE', '', 'Ich teile Eure Zweifel... aber er hat schon andere Seelen gerettet. Er könnte uns helfen.', 38134),
(201262, 'deDE', '', 'Ab jetzt müsst Ihr die Materialien selbst finden. Konsultiert den Bauplan, falls Ihr Hinweise benötigt.', 38134),
(201261, 'deDE', 'Schmerz... Leid... keine Hoffnung...', '', 38134),
(201260, 'deDE', 'Geht, ich kümmere mich um die anderen hier.', '', 38134),
(201259, 'deDE', '', 'Ich muss das Gelände nach den entkommenen Tieren absuchen.', 38134),
(201258, 'deDE', 'Einen anderen Kyrianer zu bitten, ein Gegner auf dem Pfad zu sein, ist eine sehr persönliche Angelegenheit. Ich hoffe, dass Ihr beide das zeremonielle Wesen unserer Methode versteht.', '', 38134),
(201257, 'deDE', '', 'Seid gegrüßt. Ich helfe Euch gern bei der Herstellung Eures Köders und Spiegels.', 38134),
(201256, 'deDE', 'Dactylis wird Euch bei allem helfen, was Ihr für den Pfad herstellen müsst.', '', 38134),
(201254, 'deDE', 'Ihr werdet alle nötigen Komponenten in der Bastion finden. In Zukunft müsst Ihr allerdings vielleicht andere Reiche der Schattenlande bereisen, um Material zu sammeln.', '', 38134),
(201253, 'deDE', '', 'Ich habe getan, was ich konnte. Das muss genügen.', 38134),
(201250, 'deDE', 'Zu Euren Diensten, Erster Schmied.', '', 38134),
(201249, 'deDE', 'Wir sitzen im gleichen Boot. Denkt daran, wenn die Bastion Hilfe braucht.', '', 38134),
(201248, 'deDE', 'Wir danken Euch, Oberster Schmied. Ohne Eure Hilfe und Euer Vertrauen hätten wir es nicht geschafft.', '', 38134),
(201247, 'deDE', 'Krone ist bereit für Euren Anführer. Deathrius\' Einfluss ist weg.', '', 38134),
(201246, 'deDE', '', 'Das war jetzt aber genug Ärger.', 38134),
(201245, 'deDE', '', 'Schwimm los, Kleiner.', 38134),
(201235, 'deDE', 'Meine Leute werden sicher unsere Armeen für den Gegenschlag vorbereiten, sobald Artemede Bescheid gibt.', '', 38134),
(201234, 'deDE', 'Wir sehen uns dort. Passt auf Euch auf.', '', 38134),
(201233, 'deDE', 'Wir müssen sofort nach Oribos reisen.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201231, 'deDE', 'Haltet ein. Ihre Loyalität muss allein stehen.', '', 38134),
(201230, 'deDE', 'Denathrius! Schlundgänger, wir geben Euch Deckung!', '', 38134),
(201220, 'deDE', 'Genug von Eurer Anmaßung! JETZT. IST. SCHLUSS!', '', 38134),
(201219, 'deDE', 'Ihr wagt es, MEINER Herrschaft zu trotzen?', '', 38134),
(201218, 'deDE', '', 'Aber ich werde diesen Käfig verzaubern, damit ihm kein Gorm mehr entkommt.', 38134),
(201217, 'deDE', '', 'Ich weiß ja nicht, wie Ihr das hinbekommen habt, $gSchlundgänger:Schlundgängerin;...', 38134),
(201216, 'deDE', '', 'Ihr kommt von weit her und habt noch einiges vor Euch.\nRuht Eure müden Glieder jetzt aus.', 38134),
(201215, 'deDE', 'Wir haben keine Zeit! Ihr müsst die Krone präsentieren. Wer treu zu den Schattenlanden steht, wird hier anerkannt.', '', 38134),
(201210, 'deDE', 'Seid Ihr bereit für den Tempel?', '', 38134),
(201199, 'deDE', 'Wer ist da? Seid Ihr hier, um zu helfen?', '', 38134),
(201196, 'deDE', 'Der Verstand, die Pläne Eurer Feinde zu durchschauen.', '', 38134),
(201195, 'deDE', 'Die Macht, Eure Feinde zu zerschmettern.', '', 38134),
(201194, 'deDE', 'Dann müssen wir jetzt handeln. Der Tempel ist die einzige Möglichkeit, die Krone von Denathrius\' Einfluss zu befreien.', '', 38134),
(201193, 'deDE', 'Die Gerissenheit, Eure Feinde zu überlisten.', '', 38134),
(201192, 'deDE', 'Die Barriere um den Tempel ist nicht mehr.', '', 38134),
(201191, 'deDE', 'Die Ambition, über alle hinauszuwachsen.', '', 38134),
(201189, 'deDE', 'Besiegt.', '', 38134),
(201188, 'deDE', 'Euer Ende ist gekommen.', '', 38134),
(201187, 'deDE', 'Stellt Euch Eurer Niederlage.', '', 38134),
(201186, 'deDE', 'Ihr habt vergessen, nach oben zu schauen.', '', 38134),
(201183, 'deDE', 'Oh, Gubbins, habt Ihr Eure Freunde eingeladen? Wie nett.', '', 38134),
(201181, 'deDE', 'Gut gekämpft! Keine Beute kann sich vor der Wilden Jagd verstecken oder ihr entkommen!', '', 38134),
(201180, 'deDE', 'Hört zu, ein paar meiner Jäger sind auf Patrouille im Nebelschleierdickicht. Es wäre gut, wenn Ihr sie begleiten könntet.', '', 38134),
(201171, 'deDE', 'Theotar... ist das ein Freund von Euch?', '', 38134),
(201170, 'deDE', 'Das darf doch nicht wahr sein!', '', 38134),
(201168, 'deDE', 'Und jetzt wünsche ich allen viel Spaß... bei was auch immer das hier ist...', '', 38134),
(201160, 'deDE', '', 'Vielleicht sollte ich Balthazar zum Gluthof reiten...', 38134),
(201156, 'deDE', 'Habe Fußpolster! Müsst herkommen und anlegen!', '', 38134),
(201155, 'deDE', 'Heute ehren wir Angeliki!\n\nErinnert sich an alles. Sehr weise.\n\nIm Äonischen Archiv zu finden.\n\nIhr bringt ihr reife Purianfrucht? Bitte?', '', 38134),
(201141, 'deDE', '<Das Echo des gefallenen Paragons mustert Euch.>\n\nHabt Ihr das fehlende Kapitel meiner Geschichte gefunden, $gSterblicher:Sterbliche;?', '<Das Echo des gefallenen Paragons mustert Euch.>\n\nHabt Ihr das fehlende Kapitel meiner Geschichte gefunden, $gSterblicher:Sterbliche;?', 38134),
(201140, 'deDE', 'Wollt Ihr mehr erfahren, dann findet den Folianten und bringt ihn mir.', 'Wollt Ihr mehr erfahren, dann findet den Folianten und bringt ihn mir.', 38134),
(201137, 'deDE', 'Ich bin nur ein Echo des Wissens, das das gefallene Paragon einst besaß, doch wie ich sehe, dürstet Ihr nach Wissen.\n\nMeine Geschichte ist unvollständig. Meine Jünger haben das letzte Kapitel versteckt. Die Verschmähten wollen mein Schicksal für ihre Zwecke verdrehen, doch wenn die Geschichte vollständig ist, wird ihnen das nicht gelingen. Sollten sie das Kapitel finden, werden sie es zerstören.\n\nIch schenke Euch die Gabe, den heiligen Folianten sehen zu können. Bringt ihn mir und Ihr erfahrt, wie diese Geschichte wirklich endet.\n\nWas sagt Ihr?', 'Ich bin nur ein Echo des Wissens, das das gefallene Paragon einst besaß, doch wie ich sehe, dürstet Ihr nach Wissen.\n\nMeine Geschichte ist unvollständig. Meine Jünger haben das letzte Kapitel versteckt. Die Verschmähten wollen mein Schicksal für ihre Zwecke verdrehen, doch wenn die Geschichte vollständig ist, wird ihnen das nicht gelingen. Sollten sie das Kapitel finden, werden sie es zerstören.\n\nIch schenke Euch die Gabe, den heiligen Folianten sehen zu können. Bringt ihn mir und Ihr erfahrt, wie diese Geschichte wirklich endet.\n\nWas sagt Ihr?', 38134),
(201135, 'deDE', 'Komm, Schleimer. Zeit, Mut zu beweisen!', '', 38134),
(201128, 'deDE', '', 'Weidenblüte nickt.', 38134),
(201124, 'deDE', 'Ihr solltet für die Aufgabe ausreichen, General. Ich kundschafte unten aus.', '', 38134),
(201123, 'deDE', 'Noch verspottet uns Kel\'Thuzad aus der Ferne. Seine Nekromanten werden sein Versteck preisgeben... irgendwann.', '', 38134),
(201117, 'deDE', '', 'Ihr seid weit gekommen. Gut gekämpft.', 38134),
(201115, 'deDE', '', 'Weidenblüte fragt sich, ob Pelagos die richtige Information von ihr erhalten hat, und zuckt mit den Schultern.', 38134),
(201114, 'deDE', '', 'Erlaubt mir, Eurer Kampfgeschick zu prüfen.', 38134),
(201113, 'deDE', '', 'Mal sehen, was Ihr gelernt habt.', 38134),
(201112, 'deDE', 'Ich kann auch etwas helfen. Dieser Schutz wird unsere Überlebenschancen erhöhen.', '', 38134),
(201111, 'deDE', 'Ich werde Euch aus der Luft unterstützen.', '', 38134),
(201104, 'deDE', 'Eine letzte Prozedur fehlt noch!', '', 38134),
(201098, 'deDE', '', 'Ihr müsst Lysonias Ritual unterbinden. Sie darf das Paragon nicht überwältigen.', 38134),
(201097, 'deDE', '', 'Die Herbstanhöhe liegt im Norden. Hoffentlich sind wir nicht zu spät.', 38134),
(201096, 'deDE', '', 'Das sollte uns etwas Zeit verschaffen... Ich werde hier auf Eure Berichte warten, während $n die Verschmähten untersucht.', 38134),
(201095, 'deDE', '', 'Chyrus ist stark. Er hat es übernommen, ihre Aufmerksamkeit von den Jüngern abzulenken.', 38134),
(201094, 'deDE', '', 'Das Paragon ist oben am Tempel und stellt sich Lysonia ganz allein!', 38134),
(201093, 'deDE', '', 'Diese Blutspur sollte uns zu ihr führen.', 38134),
(201091, 'deDE', '', 'Das war meine Minn\'do. Genau wie in meinem Traum.', 38134),
(201085, 'deDE', '', 'Seit diesem Tag wurde keine Seele mehr verurteilt. Alle gingen an die Schrecken des Schlunds verloren.', 38134),
(201075, 'deDE', 'Acherus, die Hochburg der Schwarzen Klinge, ist jetzt bei der Eiskronenzitadelle. Mein Schwarzes Tor wird uns hinbringen.', '', 38134),
(201074, 'deDE', 'Die Vorbereitungen wurden getroffen.', '', 38134),
(201073, 'deDE', 'Meine liebe Zena, diese Bewegung ist meine Spezialität.', '', 38134),
(201072, 'deDE', '', 'Zena zwinkert Iven zu.', 38134),
(201071, 'deDE', '', 'Die verrückte Drehung sah besonders aufregend aus.', 38134),
(201070, 'deDE', '', 'Ihr müsst mir vielleicht ein oder zwei Schritte beibringen, die Ihr vorhin getanzt habt.', 38134),
(201069, 'deDE', 'Natürlich, Lady Zena. Es wäre mir eine besondere Ehre, heute Abend einen Tanz mit Euch genießen zu dürfen.', '', 38134),
(201068, 'deDE', 'Iven errötet und stammelt eine Antwort.', '', 38134),
(201067, 'deDE', '', 'Dürfte ich den Herrn um einen Tanz bitten?', 38134),
(201066, 'deDE', '', 'Verzeiht meine Aufdringlichkeit, aber ich habe Eure Tanzkünste aus der Ferne bewundert.', 38134),
(201065, 'deDE', 'Wenn ich diesen Tanz beherrsche, werde ich damit Zenas Herz gewinnen!', '', 38134),
(201064, 'deDE', 'Danke, dass Ihr mir das Tanzen beigebracht habt, $gSchlundgänger:Schlundgängerin;.', '', 38134),
(201063, 'deDE', 'Ihr seid eine Legende, die ich selbst sehen will.', '', 38134),
(201062, 'deDE', 'Ich werde Vyraz Bericht erstatten und dann sehen wir uns am Sitz des Primus.', '', 38134),
(201060, 'deDE', '', 'Welche Gräuel auch im Turm lauern, wir können ihnen Tyrande nicht ausgeliefert lassen.', 38134),
(201059, 'deDE', '', 'Ich reiße den Turm in Stücke und die Banshee gleich mit!', 38134),
(201058, 'deDE', '', 'Nein! Unsere Leute in Torghast leiden!', 38134),
(201057, 'deDE', '', 'Minn\'do! Kommt mit uns, bevor die Macht der Nachtkriegerin Euch überwältigt!', 38134),
(201056, 'deDE', 'Wir müssen jetzt zuschlagen! Wir müssen Kel\'Thuzad angreifen.', '', 38134),
(201055, 'deDE', 'Genug dieser Ablenkungen! Wir lassen uns nicht abschrecken!', '', 38134),
(201054, 'deDE', 'Wir treffen uns dort, $n.', '', 38134),
(201053, 'deDE', 'Muss zum Beheben die Zitadelle der Loyalität erreichen.', '', 38134),
(201052, 'deDE', 'Krone ist Denathrius\' Willen durch seinen andauernden Einfluss unterworfen.', '', 38134),
(201045, 'deDE', 'Mir geht es gut. Wir brauchen Zugang zum Tempel, um die Krone in Ordnung zu bringen. Müssen Barriere deaktivieren.', '', 38134),
(201044, 'deDE', 'Verzeiht die Verzögerung, $n. Unseren neuen Verbündeten von meiner Flugfähigkeit zu überzeugen, war schwierig.', '', 38134),
(201034, 'deDE', 'Seid in unserem Hain willkommen.', 'Seid in unserem Hain willkommen.', 38134),
(201019, 'deDE', 'Wenn Ihr das für weise haltet, gerne.', '', 38134),
(201015, 'deDE', 'Mueh\'zala hat Helya an die Seite vom Kerkermeister gebracht... \'n Handel für\'s alte Odynauge geschlag\'n... und die Banshee zum Kriegshäuptling gemacht!', '', 38134),
(201014, 'deDE', 'Los jetzt, Helden! Bringt mir mein Mojo zurück un\' helft mir, diesen alten Loa zurechtzustutz\'n!', '', 38134),
(201013, 'deDE', 'Glaubt, Ihr habt gewonn\', Bossmann? Pah. Wart wohl zu lang\' in den Schatten.', '', 38134),
(201012, 'deDE', 'Eure kleinen Freunde denk\'n, sie könn\' Euch helfen, Bwonsamdi? Welche Chance habt Ihr\'n gegen mich?', '', 38134),
(201011, 'deDE', 'Der alte Hakkar is\' \'n fieser. Aber Ihr werdet ihn zahl\'n lass\'n. Früher oder später kommt er zurück. Üble Loas geb\'n nie auf!', '', 38134),
(201010, 'deDE', 'Was guckt Ihr so? Klar, Hakkar is\' \'n Loa des Bluts. Aber Geschäft is\' Geschäft, Mann! Tsk! Holt mir einfach mein Mojo zurück.', '', 38134),
(201009, 'deDE', 'So \'n Loa vergisst man nich\'! Hakkar hat immer Mojo für seine Rituale gebraucht... un\' Bwonsamdi war immer bereit für \'n Geschäft.', '', 38134),
(201008, 'deDE', 'Die Mittler ham was vor. Behaltet sie besser im Auge! NNNGGGG! Un\' jetzt bringt mir mein Mojo zurück!', '', 38134),
(201007, 'deDE', 'Die da will mir mein Mojo nich\' einfach so zurückgeb\'n. Gut aufpass\'n... die is\' listig.', '', 38134),
(201006, 'deDE', 'Un\' jetzt macht der alte Bwonsamdi Geschäfte nich\' nur auf Azeroth. Die Mittler woll\'n Macht... jeglicher Art.', '', 38134),
(201005, 'deDE', 'Ich brauch keine Jünger! Der Kerkermeister gibt mir schon alle Macht, die ich brauche... und Bwonsamdi wird man vergessen!', '', 38134),
(201004, 'deDE', 'Is\' lange her, aber ich erinner\' mich... wie die Trolle sich von Euch abgewandt ham. All die Opfer... all das Blut... sie wollt\'n... NNNGGGHHHH!', '', 38134),
(201003, 'deDE', 'Vergesst ma\' nich\', wer Euch zum Loa gemacht hat, Bwonsamdi! Und jetzt her mit den Seel\'n und kniet nieder!', '', 38134),
(201002, 'deDE', 'Die einzige Macht, die zählt, is\' der Tod! Und der Tod holt alle ein!', '', 38134),
(201001, 'deDE', 'Tsk... da habt Ihr einfach so vergess\'n, wie man \'n Loa is\'! Euer Band mit diesen Leuten... die Macht... NNNNGGGG!', '', 38134),
(201000, 'deDE', 'Ich will die Seel\'n, Bwonsamdi! Ich will Rastakhan und all die andren Zandalari! Mit Mueh\'zala sollte man besser nich\' spaßen!', '', 38134),
(200999, 'deDE', 'Ich lenk\' den alten Loa ab. Los jetzt. Folgt den Wegen. Un\' kommt nich\' auf die Idee, zu kneif\'n!', '', 38134),
(200998, 'deDE', 'Mueh\'zala hat mich... überrumpelt. Aber wenn Ihr \'n bisschen Mojo von einigen meiner... Geschäftspartner einsammeln könnt, werd\'n wir ihn gemeinsam besieg\'n!', '', 38134),
(200997, 'deDE', 'Wartet... wer...? Helden! Öh... Ihr seid doch nich\' mehr sauer wegen der Sache in Zuldazar, oder?', '', 38134),
(200996, 'deDE', 'Wartet... wer...? Helden! Wusste ich doch, dass Ihr Euerm Bwonsamdi helfen kommt.', '', 38134),
(200995, 'deDE', 'Ihr wart immer weich, Bwonsamdi. Schwach. Früher oder später zerbrecht Ihr. Dann is\' Zeit für den Kerkermeister!', '', 38134),
(200994, 'deDE', 'Damit Ihr sie in den Schlund schickt? Neeneenee... das sin\' meine Leute, Mueh\'zala. Ich geb die nich\' auf!', '', 38134),
(200993, 'deDE', 'Wo versteckt\'n Ihr all die Zandalariseel\'n, Bwonsamdi? Gebt sie jetzt Euerm Bossmann oder Ihr werdet leid\'n. Seeehr leid\'n!', '', 38134),
(200992, 'deDE', 'Wahre Liebe... is\' das nich\' süß? Der alte Bwonsamdi wusste früher auch was von Romanz\'n. Hehe... \'ne ganze Menge sogar!', '', 38134),
(200987, 'deDE', 'Lernt aus der Vergangenheit, aber lebt nicht in ihr.', '', 38134),
(200977, 'deDE', '', 'Dient mit Mut.', 38134),
(200970, 'deDE', '', 'Keine Bürde währt ewig.', 38134),
(200963, 'deDE', 'Ich hoffe doch, dass Eure Anwesenheit hier meinen Profit nicht schmälert.', '', 38134),
(200962, 'deDE', '', 'Haltet Euch von den Drust fern, während Ihr Pollen von den Schreckensrosen einsammelt. Verstreut ihn über den entweihten Sylvari, um sie wütend zu lassen. Das reicht hoffentlich, um aufzuhalten, was die Drust vorhaben.', 38134),
(200947, 'deDE', 'Wenn Ihr Euch an Euer neuer Aussehen gewöhnt habt, können wir tanzen.', '', 38134),
(200946, 'deDE', 'Darf ich bitten?', 'Darf ich bitten?', 38134),
(200945, 'deDE', 'Ich habe Euch eine andere Haarfarbe als Zena gegeben, damit niemand Verdacht schöpft!', 'Ich habe Euch eine andere Haarfarbe als Zena gegeben, damit niemand Verdacht schöpft!', 38134),
(200944, 'deDE', 'Es ist sehr großzügig von Euch, mir so behilflich zu sein.', '', 38134),
(200943, 'deDE', '<Ivens Augen weiten sich, als er durch Euch hindurch auf Bilder seiner Vorstellungskraft blickt.>\n\nOh, Zena... Die wunderschönste Venthyr in ganz Revendreth...\n\nIhre Schönheit ist Euch hier am Hof sicher auch schon aufgefallen.\n\nIch kann nicht mit ihr am gleichen Ort und nicht von ihr hingerissen sein!\n\n<Iven zeigt auf die tanzende Venthyr.>\n\nDa ist sie, meine zukünftige Liebe. Sie wartet darauf, dass ich den Mut aufbringe, sie um einen Tanz zu bitten!\n\nLeider habe ich kaum Erfahrung mit den Tänzen der Venthyr... und ich will sie nicht enttäuschen.', '', 38134),
(200937, 'deDE', 'Wirklich? Oh, das ist ja fabelhaft.\n\nEin wenig Übung mit Euch wird mir sicher etwas mehr Selbstsicherheit verschaffen.\n\n<Iven scharrt nervös mit den Füßen.>\n\nMir fällt gerade ein, dass es noch hilfreicher wäre, wenn ich mit jemanden tanzen könnte, der aussieht wie Zena...\n\nIhre Bewegungen unterscheiden sich schon sehr von denen Eures Volkes.\n\nVielleicht... also, wenn Ihr nichts dagegen habt...\n\n<Iven schaut Euch hoffnungsvoll an.>\n\nAlso, ich möchte Euch fragen, ob ich Euch vorübergehend in das Ebenbild von Zena verwandeln darf?', '', 38134),
(200935, 'deDE', 'Oh, entschuldigt bitte, $gSchlundgänger:Schlundgängerin;.\n\nIch war Euch wohl im Wege. Verzeiht...\n\n<Iven beobachtet sehnsüchtig die grazilen Tänzer.>\n\nIch befürchte, Ihr begegnet mir in einem Moment der Enttäuschung.\n\nIch bin nämlich erst kürzlich ein Venthyr geworden und weiß wirklich überhaupt nichts von ihren... unseren... Tänzen.\n\nUnd auch wenn ich ungeübt bin, will ich es doch lernen, um direkt in das Herz meiner zukünftigen Liebe zu tanzen, Zena!', '', 38134),
(200933, 'deDE', '', 'He! Hier drüben!', 38134),
(200932, 'deDE', 'Bitte genießt die beruhigende Musik dieser drei bescheidenen Seelen.', '', 38134),
(200931, 'deDE', 'Ich danke Euch, Kuhl. Wir sind ganz aufgeregt, dass Ihr uns hierher zum Spielen eingeladen habt.', '', 38134),
(200930, 'deDE', 'Der Verschüttete Kelch ist bereit für die Darbietung!', '', 38134),
(200929, 'deDE', 'Öffnet Eure Herzen und Seelen der bewegendsten Musik der ganzen Schattenlande!', '', 38134),
(200928, 'deDE', '', 'Bleibt außer Sicht! Die Drust sind überall, $n! Wirklich überall! Sie planen etwas Großes, und wir müssen es aufhalten!\n\nGut, hier ist der Plan. Die Drust haben einige Sylvar mit diesen seltsamen Masken entweiht. Die armen Dinger stehen komplett unter ihrer Kontrolle. Wir können sie nicht retten, aber sie sind trotzdem noch nützlich.\n\nSeht Ihr diese Blume da? Das ist eine Schreckensrose. Nein, nicht anfassen! Ihr Pollen ist gefährlich. Er macht die Sylvar verrückt und gewalttätig, bis sie ohnmächtig werden. Ich setze meine Magie ein, um Euch als gewöhnliche Ardenmotte zu tarnen. Holt etwas von dem Pollen, verstreut ihn über den Entweihten und sorgt für Chaos.\n\nDas kann gar nicht schiefgehen!\n\nSeid Ihr bereit?', 38134),
(200927, 'deDE', 'Dieser Sterbliche sagt, dem Schlund schon einmal entkommen zu sein! Stellen wir diese Behauptung auf die Probe.', '', 38134),
(200925, 'deDE', '', 'Ist er nicht im Schlund verloren?', 38134),
(200924, 'deDE', 'Und was, wenn diese Halle leer ist? Nein. Der Sitz ist ein sinnloses Unterfangen.', '', 38134),
(200922, 'deDE', '', 'Ohne Krexus sind wir so gut wie verloren. Wir müssen den Sitz des Primus öffnen!', 38134),
(200921, 'deDE', '', '$p, zu mir! Sofort!', 38134),
(200920, 'deDE', 'Sie... haben die Nekropole zerstört. Krexus, er...', '', 38134),
(200919, 'deDE', 'Die Konstrukte... Gegenangriff... Es waren zu viele...', '', 38134),
(200918, 'deDE', '', 'Vyraz! Was ist hier geschehen?', 38134),
(200916, 'deDE', 'Hahahahahahahaha!', 'Hahahahahahahaha!', 38134),
(200915, 'deDE', 'Dummer Flatterkäfer! Mich wirst du nicht los!', 'Dummer Flatterkäfer! Mich wirst du nicht los!', 38134),
(200914, 'deDE', 'Mach schneller, dummes Ding!', 'Mach schneller, dummes Ding!', 38134),
(200913, 'deDE', 'Reitet auf ihnen!', 'Reitet auf ihnen!', 38134),
(200912, 'deDE', '', 'Ein glorreicher Sieg!', 38134),
(200910, 'deDE', '', 'Ich bin nervös, $p. Musik entblößt vor aller Augen meine Seele.\n\nUnd obwohl dieser Sündenstein alle an meine dunkle Seite erinnert, hoffe ich, dass die Musik auch mein inneres Licht enthüllt.\n\nLord Garridan in Eurer Botschaft sieht es ebenso. Bei meiner Ankunft war er ausgesprochen nett und voll des Lobes für meine Musik.\n\nIch habe meine Laute bei ihm vergessen. Holt sie mir bitte, während ich mich auf meinen Auftritt vorbereite.', 38134),
(200909, 'deDE', '', 'Ihr seid vielleicht gescheitert, aber dennoch auf dem Weg zum Sieg.', 38134),
(200907, 'deDE', '<Glückspilz Ansel wendet widerwillig seinen Blick von dem Sündenstein weg.>\n\nMeine Bürde ist noch viel zu schwer.\n\nSolange auch nur eine Sünde auf diesem Stein lastet, bin ich nicht frei von den Gefahren des Schlunds.\n\nZum Glück gefällt meine außergewöhnliche Musik meinen Venthyraufsehern. Und Prinz Renathals Hof zu dienen, wird sich gewiss für mich lohnen.\n\nHolt meine Geige aus den Bandrequisiten bei den Tänzern und ich kann mit meiner öffentlichen Buße beginnen.', '', 38134),
(200905, 'deDE', '', 'Sie können nicht mehr gerettet werden. Gut gekämpft.', 38134),
(200904, 'deDE', '', 'Ich dulde dieses Pack nicht mehr in meinem Tempel. Kümmert Euch um sie.', 38134),
(200902, 'deDE', '<Noko atmet langsam ein.>\n\nDer Bühnenmanager kann meine Flöte nirgendwo finden.\n\nBeim letzten Mal hatte sie sich einer der ungehobelten Roadies unter den Nagel gerissen.\n\nDiese elenden Gräber machen nichts als Ärger. Und das überall auf dem Fest.\n\nNehmt ihnen meine Flöte ab und ich werde für Eure Gäste spielen.', '', 38134),
(200900, 'deDE', 'Der Geist hat sich wieder beruhigt... Ich danke Euch.', '', 38134),
(200895, 'deDE', '', 'Das erste Lied werde ich Euch widmen.', 38134),
(200894, 'deDE', '', 'Meine Leute wurden von den Drust in geistlose Monster verwandelt! Verteidigt den Ardenwald zusammen mit meiner Verbündeten Bernfuß.', 38134),
(200893, 'deDE', 'Ich kann mir vorstellen, dass Eure Reise nicht angenehm sein wird. Tja, nun. Viel Glück!', 'Ich kann mir vorstellen, dass Eure Reise nicht angenehm sein wird. Tja, nun. Viel Glück!', 38134),
(200892, 'deDE', 'Von dort aus könnt Ihr in die Seelensäule springen. Das ist der Seelenstrom, der in den Schlund mündet.', 'Von dort aus könnt Ihr in die Seelensäule springen. Das ist der Seelenstrom, der in den Schlund mündet.', 38134),
(200891, 'deDE', 'Die Teleportationsplattform bringt Euch zum Ring der Übertragung.', 'Die Teleportationsplattform bringt Euch zum Ring der Übertragung.', 38134),
(200889, 'deDE', 'Achtung, $p. Bitte folgt mir.', 'Achtung, $p. Bitte folgt mir.', 38134),
(200888, 'deDE', 'Das wird meine beste Darbietung überhaupt.', '', 38134),
(200886, 'deDE', 'Diese Klinge... Sie wurde vom Primus geschmiedet!', '', 38134),
(200885, 'deDE', 'Ich spüre... eine Macht... in der Luft...', '', 38134),
(200883, 'deDE', '', 'Ihr habt zwar keine Schwingen, aber den Mut der Aufgestiegenen!', 38134),
(200882, 'deDE', 'Möge unsere Musik auch Eure Seele leiten.', '', 38134),
(200881, 'deDE', '', 'Maldraxxus wagt sich erneut in unser Reich? Zeigt ihnen, dass wir die Lüfte beherrschen!', 38134),
(200880, 'deDE', 'Er hat Baine!', '', 38134),
(200879, 'deDE', '', 'Der Kerkermeister.', 38134),
(200876, 'deDE', 'Pelagos, Zeit für Eure erste Herausforderung. Schlagt die Vesperglocke, wenn Ihr bereit seid.', '', 38134),
(200875, 'deDE', '', 'Der Pfad des Aufstiegs ist nah. Macht Euch bereit, Kämpfer!', 38134),
(200871, 'deDE', 'Wer ich bin? Ich bin \'ne Seele!\n\nAls meine Zeit kam, wurde ich in den Ardenwald gerufen. Ich hab\' mein Leben lang mit Geistern gearbeitet, also war das nur... na ja, natürlich.\n\nWie gefällt Euch meine Seelengestaltform? Ich wollte schon IMMER nur als Wolf rumrennen!', '', 38134),
(200867, 'deDE', '', 'Die Nachricht ist magisch versiegelt? Spielverderber...', 38134),
(200865, 'deDE', 'Jetzt feiern! Große Happen für große Kerle!', '', 38134),
(200863, 'deDE', '', 'Wir sollten schnell weiter. Geht es Euch gut genug?', 38134),
(200856, 'deDE', 'Unsere Verbündeten sind dieser Monstrosität nicht allein gewachsen. Wir müssen ihnen helfen!', '', 38134),
(200850, 'deDE', '', 'Oh, bitte lasst mich frei, Wanderer! Es ist viel zu lange her, dass ich mich um die Haine des Ardenwalds kümmern konnte.', 38134),
(200847, 'deDE', 'Ja, lasst mich das notieren! Oh Moment, ich brauche meine Notizen.', '', 38134),
(200846, 'deDE', 'So geht das nicht. Ich hatte Notizen, ich bin sicher, ich hatte Notizen. Forschung, ja. Erforschung von Anima. \n\nIch scheine meine Notizen verloren zu haben.', '', 38134),
(200842, 'deDE', 'Tolle Vorstellung! Lasst uns meinen Säbel suchen und diese Dinger ordentlich verprügeln.', '', 38134),
(200841, 'deDE', 'Ja, Ihr habt mich erwischt. Seid Ihr aus Schadenfreude hier? Oh, Ihr gehört nicht zu denen.\n\nIch wurde gefangen genommen und meiner Waffen beraubt. Glaubt Ihr, Ihr könntet mir bei einer gewagten Flucht helfen?', '', 38134),
(200838, 'deDE', '', 'Suchen wir nach... Ich kann mich nicht erinnern.', 38134),
(200837, 'deDE', '', 'Sie haben mich hier bewegungsunfähig gehalten und meinen Geist vergiftet. Ich glaube, ich habe vorher meine Erinnerungen gerettet, aber ich bin mir nicht sicher.', 38134),
(200834, 'deDE', '', 'Also schön! Suchen wir diesen Grabpirscher.', 38134),
(200833, 'deDE', '', 'Dieses Ding hat mich hier festgesetzt! Hat mich mit den Toten überwältigt. Ich weiß nicht, wie lange das her ist, aber mir reicht es! Befreit mich und helft mir, es zur Strecke zu bringen!', 38134),
(200830, 'deDE', 'Ich danke Euch! Gehen wir Enothia suchen.', '', 38134),
(200829, 'deDE', 'Das läuft nicht wie erwartet. Ich habe versucht, Enothia zu retten, und wurde selbst gefangen genommen. Bitte helft mir! Und ihr!', '', 38134),
(200826, 'deDE', '', 'Sie sind in der Nähe, zeigt keine Reue.', 38134),
(200825, 'deDE', '', '$uSchlundgänger:Schlundgängerin;, ich habe gesehen, wie Ihr durch diese Hallen gepirscht seid.\n\nWenngleich mein übermäßiges Selbstvertrauen zu meiner Gefangennahme geführt hat, habe ich immer noch eine Mission zu erfüllen. Ein hochrangiger Diener des Kerkermeisters hat einige wertvolle Informationen, die ich an mich bringen muss.', 38134),
(200821, 'deDE', 'Geht weg.\n\nIch wurde besiegt.\n\nOhne meine Rüstung werde ich bald tot sein.', '', 38134),
(200818, 'deDE', 'Er ist immer noch in der Nähe. Eine gute Zerstückelung wird ihm eine Lektion erteilen.', '', 38134),
(200817, 'deDE', 'Seid gegrüßt! Oder gesegnet, je nachdem, welche flauschigen Gefälligkeiten Ihr erwartet.\n\nIch bin meinem... nennen wir es einfach Experiment... nachgejagt. Es erwies sich als klüger als geplant, und jetzt stecke ich in diesem Dilemma.\n\nWas haltet Ihr davon, mich zu befreien, und dann holen wir das zurück, was mir gehört? Wenigstens die nützlichen Teile.', '', 38134),
(200814, 'deDE', 'Ich muss meine Klinge finden. Nur dann kann ich zurückkehren.', '', 38134),
(200813, 'deDE', 'Noch jemand, der gekommen ist, um mich zu verspotten. Oder auch nicht? Ihr gehört nicht zu denen?\n\nIch habe geglaubt, ich sei dazu verdammt, die Ewigkeit gefangen in diesen Ketten und innerhalb dieser Mauern zu verbringen.\n\nBitte helft mir, meine Klinge zurückzuholen, ich möchte wenigstens das bisschen Ehre zurückgewinnen.', '', 38134),
(200810, 'deDE', 'Jetzt lasst uns meinen Zauberstab suchen! Nun, er gehört mir noch nicht. Wir müssen vielleicht ein wenig Gewalt anwenden, um ihn zu bekommen.', '', 38134),
(200809, 'deDE', 'Ahh, $uder:die; sagenumwobene $uSchlundgänger:Schlundgängerin;. Mein Machthunger scheint zu meiner derzeitigen misslichen Lage geführt zu haben.\n\nKönntet Ihr $uein guter:eine gute;... was auch immer Ihr seid sein und mich freilassen?\n\nOh, und ich habe vielleicht auch noch eine kleine Rechnung zu begleichen.', '', 38134),
(200803, 'deDE', 'Gefangen!\n\nBefreit mich, Kleinling!\n\nMuss zum Meister zurückkehren.', '', 38134),
(200799, 'deDE', 'Dort liegt ein Stapel Teile. Es sieht aus, als wären sie von etwas Wichtigem entfernt worden.', '', 38134),
(200796, 'deDE', 'Habt Dank! Diese letzten Ketten werden den Schlüssel von der Wache benötigen, die mich eingesperrt hat.', '', 38134),
(200795, 'deDE', 'Diese Kreaturen haben mich gefesselt und hier oben angekettet. Befreit mich, bitte!', '', 38134),
(200792, 'deDE', '', 'Ich danke Euch! Gehen wir mein Schwert suchen.', 38134),
(200791, 'deDE', '', 'Seid Ihr $uder Schlundgänger:die Schlundgängerin;? Mein Familienschwert wurde mir genommen. Bitte helft mir, es wiederzubekommen.', 38134),
(200780, 'deDE', 'Ich schaffe das.', '', 38134),
(200778, 'deDE', 'Ich nehme die Herausforderung an!', '', 38134),
(200776, 'deDE', 'Haha! DAS macht mal Spaß!', '', 38134),
(200773, 'deDE', 'Äh... War ich das etwa?', '', 38134),
(200770, 'deDE', 'Danke, die Monstrosität muss hier irgendwo sein.', '', 38134),
(200769, 'deDE', 'Derjenige, dem ich diente, wurde hier unten getötet. Ich bin alleine und beschämt zurückgeblieben. Bitte helft mir, die Aufgabe zu erledigen, für die er hergekommen ist.', '', 38134),
(200766, 'deDE', '', 'Holen wir uns die Monstrosität!', 38134),
(200765, 'deDE', '', 'Mein Speer zerbrach im Kampf mit einer der Monstrositäten hier. Helft mir, sie zu töten, damit ich mir ihre Waffe aneignen kann.', 38134),
(200764, 'deDE', 'Früher war ich auf einer Augenhöhe mit den Besten, aber so wie es aussieht, fehlt mir nun das Wissen, das ich einst hatte. Ich kann nur annehmen, dass mir meine Erinnerungen gestohlen wurden. Bitte helft mir, sie wiederzuerlangen.', '', 38134),
(200763, 'deDE', 'Dann wollen wir mal nach einem Gedächtnisstein suchen.', '', 38134),
(200760, 'deDE', 'Ayeleth möchte Anima! Tötet meinen Peiniger, er hat meinen Vorrat gestohlen und ich möchte ihn zurück.', '', 38134),
(200759, 'deDE', 'Holen wir uns meine Anima zurück.', '', 38134),
(200756, 'deDE', '', 'Bitte befreit mich! Ich weiß nicht, wie lange ich hier schon angekettet bin.', 38134),
(200752, 'deDE', 'Ich muss meinen Sündenstein zurückholen. Es gibt Seelen, die zertrümmert werden müssen!', '', 38134),
(200751, 'deDE', 'Nun, wo is mein Sündenstein?', '', 38134),
(200746, 'deDE', 'Also schön! Suchen wir diesen Gargoyle und zahlen es ihm heim.', '', 38134),
(200745, 'deDE', 'Ich wurde entführt! Ein schurkischer Gargoyle hat mich hier reingezogen. Befreit mich, damit ich mich rächen kann!', '', 38134),
(200742, 'deDE', 'Der Kampf um den Erhalt des Ardenwalds ist noch nicht vorbei.', '', 38134),
(200741, 'deDE', 'Gehen wir meine Schaufel aufspür\'n!', '', 38134),
(200740, 'deDE', 'Sie hab\'n mir meine Schaufel gestohlen! Ich könnt Hilfe gebrauch\'n, sie zurückzuholen.', '', 38134),
(200737, 'deDE', '', 'Lasst uns gehen und meine Erinnerungen suchen. Sie sollten hier irgendwo in einem Gedächtnisstein gespeichert sein.', 38134),
(200735, 'deDE', '', 'Wer seid Ihr? Wartet, wer bin ich? Meine Erinnerung ist lückenhaft. Ich muss meinen Gedächtnisstein finden, um sie wiederherzustellen.', 38134),
(200733, 'deDE', '<Wählt eine Schlussformel.>', '', 38134),
(200731, 'deDE', 'Lasst die Lehren, die Ihr aus dieser Herausforderung gezogen habt, in Eure zukünftigen Versuche einfließen.', '', 38134),
(200728, 'deDE', 'Anwesenheit am Gluthof erbeten, da _______', '', 38134),
(200726, 'deDE', 'Bitte befreit ________ von seiner Pflicht am Tor.', '', 38134),
(200695, 'deDE', 'Ihr seid also die Person, die der Bastionsgesandte erwähnt hat.', '', 38134),
(200694, 'deDE', 'Bereite noch Ausrüstung vor. Kommt später vorbei, nach dem Herstellen!', '', 38134),
(200693, 'deDE', 'Alle Ausrüstung hier, alles da!\n\nWas wollt Ihr anlegen?', '', 38134),
(200692, 'deDE', 'Ich habe Eure Armee aufgespürt, Draven.', '', 38134),
(200691, 'deDE', 'Schlundgänger, nehmt den Blutelf und zerstört die Kristalle, die ihnen die Anima entziehen. Ich patrouilliere weiterhin am Himmel.', '', 38134),
(200690, 'deDE', 'Kael\'thas gibt von unten das Signal. Wir müssen landen!', '', 38134),
(200689, 'deDE', '', 'Ich... bedaure Euren Verlust und die Rolle, die ich dabei spielen musste.\n\nIch hoffe, dass dies nicht das Ende ist.', 38134),
(200688, 'deDE', 'Meine Liebe hatte die richtige Wahl getroffen, als sie Euch retten wollte. Ich bedaure zutiefst, wie es geendet ist.\n\nDer Traum wird niemals mehr derselbe sein.', '', 38134),
(200687, 'deDE', '', 'Mutter, wärst du doch nur hier. Noch haben wir Hoffnung, aber ohne dich wanken wir.\n\nIch brauche deine Weisung.', 38134),
(200686, 'deDE', 'Du warst die Mutter, die den Traumpfad mit mir gegangen ist. Du hast mir so viel beigebracht.\n\nDie Welt braucht dich.', '', 38134),
(200684, 'deDE', '<Ihr habt alles, um Steinkopfs Berechtigungsschein zu schreiben.\n\nÜberlegt Euch, was Ihr schreiben möchtet.>', '', 38134),
(200681, 'deDE', 'Trampel?! Der mächtige Millhaus Manasturm hat noch nie im Leben getrampelt!', '', 38134),
(200680, 'deDE', '', 'Hey! Das hat wehgetan, du Trampel!', 38134),
(200679, 'deDE', '', 'Dann steh mir nicht im Weg, Millhaus!', 38134),
(200678, 'deDE', 'Autsch! Pass doch auf, was du machst, Millificent!', '', 38134),
(200677, 'deDE', 'Du bist die Beste, Milly! Lass uns nie wieder streiten! Zumindest vorerst...', '', 38134),
(200675, 'deDE', '', 'Ihr schafft es stets, im Zentrum des Trubels zu sein, nicht wahr?', 38134),
(200674, 'deDE', 'Ich werde durchhalten... für Euch...', '', 38134),
(200671, 'deDE', '', 'Der Sturm ruht... vorerst.', 38134),
(200669, 'deDE', '', 'Wir sind nicht die Einzigen, die diesen Alptraum erlebt haben. Die Drust sind im Anmarsch!', 38134),
(200667, 'deDE', '', 'He! Hört alle zu!', 38134),
(200658, 'deDE', '', 'Hallo, $n!\n\nFall Ihr hier seid, um mehr über Zeitwanderungen zu erfahren, fürchte ich, dass Ihr zuerst Euer Verständnis der Gegenwart vertiefen müsst, bevor Ihr die Vergangenheit besuchen könnt.\n\nVielleicht kann ich Euch zurückschicken, nachdem Ihr die Stadt besichtigt und den König getroffen habt!', 38134),
(200657, 'deDE', '', 'Ahhh, mein Schatz! Du hast ja DOCH ein Herz!', 38134),
(200656, 'deDE', 'Nichts scheint die Alpträume zu beruhigen! Könntet Ihr es... direkter versuchen?', '', 38134),
(200647, 'deDE', '', 'Niete!', 38134),
(200646, 'deDE', 'Nun, ich, äh... Hey, Moment mal! Du hast eben zugegeben, dass du das auch gemacht hast! Heuchlerin!', '', 38134),
(200645, 'deDE', 'Aber jetz\', äh... brauch ich mein Mojo zurück!', '', 38134),
(200644, 'deDE', 'Auch die zwei irren kleinen Gnome da. Die war\'n beide machthungrig. Da hab ich gerne ausgeholf\'n.', '', 38134),
(200643, 'deDE', 'Nich\' nur die Trolle bitt\'n den alt\'n Bwonsamdi um Hilfe. Alle woll\'n Geschäfte mit dem Loa des Todes mach\'n!', '', 38134),
(200641, 'deDE', 'Mein Einfluss ist unwiderlegbar! Ihr werdet MIR dienen!', '', 38134),
(200640, 'deDE', 'Bei der Archon! Sie kommt von dem Medaillon in der Krone. Schnell, beeilt Euch!', '', 38134),
(200639, 'deDE', 'Gut gemacht, treuer Diener. Ich werde zurückkehren.', '', 38134),
(200638, 'deDE', 'Diese Stimme? Von woher...?', '', 38134),
(200637, 'deDE', 'Ja... ermächtigt die Krone.', '', 38134),
(200621, 'deDE', 'Wir sollten es der Dromanin sagen. Wildsamen binden sich selten an ihre Pfleger. So eine Freude!', '', 38134),
(200620, 'deDE', 'Der hier ist anders. Unser wahrer Feind... die Drust.', '', 38134),
(200618, 'deDE', '', 'Vyraz ist ungestüm, hat aber recht. Wir brauchen Verstärkung.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200616, 'deDE', 'Welche Erinnerung fordert Ihr heraus?', '', 38134),
(200615, 'deDE', '', 'Nicht übel, zumindest für jemanden ohne Flügel.', 38134),
(200614, 'deDE', '', 'Die Aspiranten testen ihre Präzision im Flug. Könnt Ihr mit ihnen mithalten?', 38134),
(200611, 'deDE', 'Unsere Ausbildung ist nie vorbei. Wir müssen unsere Standards halten.', '', 38134),
(200610, 'deDE', '', 'Ein Aufruhr der Geister, gestaltgewordener Alptraum. Traumweber ruft um Hilfe.', 38134),
(200608, 'deDE', 'Ich weiß, wo wir mehr bekommen.', '', 38134),
(200607, 'deDE', 'Nein... die Alpträume bleiben. Wir brauchen mehr Anima.', '', 38134),
(200606, 'deDE', '', 'Hat es geklappt?', 38134),
(200605, 'deDE', 'Hoffentlich ist das genug...', '', 38134),
(200603, 'deDE', '', 'Der Sitz des Primus wird belagert! Wir müssen ihn verteidigen!', 38134),
(200601, 'deDE', '', 'Aschenbringer! Schlundgänger! Mir nach!', 38134),
(200599, 'deDE', 'Aber gewiss! Proglo hat diese Anima für genau einen solchen Fall aufbewahrt.\n\nBitte. Gehabt Euch wohl, und solltet Ihr etwas brauchen, vergesst nicht, dass Proglo eine der tapfersten Seelen im Ardenwald ist!', '', 38134),
(200597, 'deDE', 'AH! Was seid Ihr? Vier Spriggans in so einer Art Kostüm?\n\nEine dieser verseuchten Monstrositäten?\n\nWas immer Ihr seid... Geht einfach weg! Bitte?', '', 38134),
(200596, 'deDE', 'Seid gegrüßt! Seid Ihr wegen der Anima hier? Proglo hat sie gut verwahrt, falls sie für Wildsamen gebraucht wird.\n\nDiese verfluchten Spriggans und diese verseuchten Kreaturen sollen sie nicht bekommen!\n\nProglo ist eine der tapfersten Seelen im Ardenwald. Ihm entgeht nichts!', '', 38134),
(200593, 'deDE', 'Wir bereiten das Ritual vor!', '', 38134),
(200592, 'deDE', 'General Draven nähert sich!', '', 38134),
(200591, 'deDE', 'Fasst Euch kurz. Wir haben viel zu tun.', '', 38134),
(200590, 'deDE', '', 'Krexus hat recht. Wir besitzen den Schlüssel zum Sitz des Primus!', 38134),
(200587, 'deDE', 'Dann locken wir ihn hervor. Zuerst müssen wir aber den Luftraum von Gegnern säubern.', '', 38134),
(200586, 'deDE', 'Dieser Feigling versteckt sich im Untergrund.', '', 38134),
(200584, 'deDE', '', 'Nehmt diesen Teil meiner Macht. Damit könnt Ihr Eurer Seele eine Gestalt verleihen, die die Seele da unten... einladender finden wird.', 38134),
(200577, 'deDE', 'Ich hoffe sehr, dass sich unsere Wege wieder kreuzen werden. Folgt bis dahin dem Pfad.', '', 38134),
(200576, 'deDE', 'Der Speer der Kalliope ist gerettet, und das verdanken wir Euch, $uHeld:Heldin;.', '', 38134),
(200575, 'deDE', '', 'Das hat gesessen! Ich behalte die Drust im Auge! Ihr fliegt zurück zu Aliothe!', 38134),
(200574, 'deDE', 'Gut gemacht, $n, gut gemacht.', '', 38134),
(200573, 'deDE', '', 'Obwohl unsere Ressourcen begrenzt sind, tue ich, was ich kann.', 38134),
(200572, 'deDE', '', 'Der Pfad des Aufstiegs erwartet Euch.\n\nSeid Ihr bereit, ihn in Angriff zu nehmen?', 38134),
(200571, 'deDE', 'Bitte, Aliothe. Helft uns, diese Alpträume zu beenden!', '', 38134),
(200538, 'deDE', 'LANGBARTS EINTOPF<\n\nNotiz von Supi: Rezept von Aspirant in der Bastion. Aspirant früher großer Zwergenkoch. Hat mir Rezept nach bestandener Prüfung geschenkt. Vergangenheit ruht jetzt. Sein Lieblingsrezept.\n\nZutaten:\n2 Pfund Rinderkamm\n150 Gramm Speck, gewürfelt\n500 Milliliter malziges Zwergenstarkbier\n250 Milliliter Rinderbrühe\n50 Gramm Tomatenmark\n2 Esslöffel Soße der Familie Worcester\n3 Esslöffel Weizenmehl\n2 Lauchstangen, gehackt\n3 Karotten, gehackt\n1 Pastinake, gehackt\n1 Teelöffel Thymian\n1 Teelöffel Rosmarin\n1,5 Teelöffel Salz\n1/4 Teelöffel schwarzer Pfeffer\n2 Baelorbeerblätter\n\nZubereitung:\n<Haben die Gräber noch nicht aufgeschrieben.>', '', 38134),
(200533, 'deDE', 'SUPIS GESAMMELTE REZEPTE\n\n(Aufgeschrieben von einer Bande halbgebildeter Gräber.)\n\nInhaltsverzeichnis:', '', 38134),
(200527, 'deDE', '', 'Die anderen Häuser werden bald von dem Schwert hören. Haltet Euch kampfbereit.', 38134),
(200526, 'deDE', '', 'Der Primus war ein legendärer Waffenschmied. Mit dem Schlüssel zu seinem Gewölbe gehört sein Arsenal uns.', 38134),
(200525, 'deDE', '', 'Ein Tor scheint uns im Weg zu sein. Gebt mir Bescheid, dann trage ich Euch darüber hinweg.', 38134),
(200508, 'deDE', '', 'Der Sitz des Primus ist seit seinem Verschwinden verschlossen. Viele haben versucht, ihn zu öffnen.', 38134),
(200506, 'deDE', 'Lasst uns nicht zu lange hierbleiben. Müsst Ihr nicht den Wildsamen zum Winterschlafbecken bringen?', '', 38134),
(200505, 'deDE', 'Wir dürfen nicht zulassen, dass diese Monster Soryns Aue einnehmen.', '', 38134),
(200491, 'deDE', '', 'Ihr seid zurück! Der Wildsamen wurde schon unruhig.', 38134),
(200469, 'deDE', 'Nehmt unseren Hirsch und reitet mit uns, $p.', '', 38134),
(200466, 'deDE', 'Die Gäste sind glücklich! Ich bin ein echter Kellner!', '', 38134),
(200465, 'deDE', 'Keine Angst, Vogelmann. Ich kümmer mich um alle Bestellungen!', '', 38134),
(200464, 'deDE', 'Wohin sind alle Kellner?', 'Wohin sind alle Kellner?', 38134),
(200463, 'deDE', 'Bestellung fertig!', '', 38134),
(200458, 'deDE', '$n, Ihr habt den Speer errungen und die verbleibenden Gelehrten sind in Sicherheit. Lasst uns das Angebot annehmen und ohne weiteren Streit abziehen. Ich treffe Euch dann an der Heldenrast.', '', 38134),
(200455, 'deDE', 'Geleidet Euch wohl, $p.', '', 38134),
(200451, 'deDE', '', 'Gut gemacht! Ich glaube, die hiesigen Motten könnten Einiges von Euch lernen.', 38134),
(200450, 'deDE', '', 'In der Nähe gibt es wertvolle Animazapfen, aber hier sind überall Drust. Etwas Magie sollte Euch helfen, an ihnen vorbeizukommen.', 38134),
(200449, 'deDE', '', 'Wie ich höre, gebt Ihr einen sehr attraktiven Talkreischer ab.\n\n<Mondbeere lacht.>\n\nIch behalte Euren Namen im Hinterkopf, falls ich wieder einmal $geinen Balzexperten:eine Balzexpertin; brauche.\n\nDoch genug davon, ich bringe gute Neuigkeiten! Die Königin kommt nach Tirna Vaal! Dieser Hain wird offenbleiben, sich um Wildsamen kümmern und Seelen aufnehmen. Sie wird bald hier sein, um das Herz zu segnen und allem neue Kraft zu schenken.\n\nIhr solltet dann auch die Gelegenheit haben, mit ihr zu sprechen.', 38134),
(200448, 'deDE', '', 'Ihr habt Korenth also dazu gebracht, zu tanzen?\n\n<Mondbeere lacht.>\n\nIch wusste, dass ich Euch mag, $n. Ich behalte Euren Namen im Hinterkopf, falls ich sonst noch Aufgaben oder Gefallen für Euch habe.\n\nIch bringe gute Neuigkeiten! Die Königin kommt nach Tirna Vaal! Dieser Hain wird offenbleiben, sich um Wildsamen kümmern und Seelen aufnehmen. Sie wird bald hier sein, um das Herz zu segnen und allem neue Kraft zu schenken.\n\nIhr solltet dann auch die Gelegenheit haben, mit ihr zu sprechen.', 38134),
(200446, 'deDE', 'Prestige und Macht. Renathal wird zufrieden sein, $n. Kümmert Euch um die Bedürfnisse unseres Freundes.', '', 38134),
(200445, 'deDE', 'Perfektes Werkzeug... Krone für den Anführer. Kanalisiert die Macht der Medaillons, von denen Ihr erzählt habt.', '', 38134),
(200442, 'deDE', '', 'Glaubt nicht, Ihr habt gewonnen! Das nächste Mal ist Euer Ende.', 38134),
(200441, 'deDE', '', 'Angriff!', 38134),
(200440, 'deDE', '', 'Ihr steht weit unter mit, Kyrianer. Ich bin das Paragon des Zweifels und verbitte mir Eure fortwährende Einmischung!', 38134),
(200439, 'deDE', '', 'Mir scheint, Ihr habt Gäste, Paragon. Also kümmert Euch um sie.', 38134),
(200438, 'deDE', '', 'Die Zukunft steht fest. Die Archon stirbt durch meine Klinge und die Bastion wird mein sein!', 38134),
(200437, 'deDE', 'Ahhh!', '', 38134),
(200436, 'deDE', '', 'Ihr wart nützlich, Uther. Devos mag Euch geschätzt haben... aber für mich seid Ihr nutzlos.', 38134),
(200435, 'deDE', '', 'Nehmt seine Kraft. Lasst ihn Eure Transformation stärken, Lysonia.', 38134),
(200431, 'deDE', 'Ich sehe, wie Ihr am Rand verweilt, $gSterblicher:Sterbliche;. Fühlt Ihr, wie der Schlund nach Eurer Seele greift?\n\nIch bewundere Euren Mut und Eure Beharrlichkeit. Doch wie stark sind sie wirklich?\n\nWenn Ihr seid, wer Ihr vorgebt, dann fordere ich Euch heraus: Besiegt Ihr mich, kehrt Ihr in die Bastion zurück. Fallt Ihr, werde ich Euch Eure Anima wieder nehmen.\n\nWas sagt Ihr?', 'Ich sehe, wie Ihr am Rand verweilt, $gSterblicher:Sterbliche;. Fühlt Ihr, wie der Schlund nach Eurer Seele greift?\n\nIch bewundere Euren Mut und Eure Beharrlichkeit. Doch wie stark sind sie wirklich?\n\nWenn Ihr seid, wer Ihr vorgebt, dann fordere ich Euch heraus: Besiegt Ihr mich, kehrt Ihr in die Bastion zurück. Fallt Ihr, werde ich Euch Eure Anima wieder nehmen.\n\nWas sagt Ihr?', 38134),
(200429, 'deDE', 'Das war nur eine Schlacht. Ich fürchte, uns erwarten noch viele mehr.', '', 38134),
(200428, 'deDE', 'Gut gemacht! Rückzug und neu formieren!', '', 38134),
(200427, 'deDE', 'Wilde Jagd! Jetzt!', '', 38134),
(200422, 'deDE', '', 'Da seid Ihr ja wieder! Ich habe gute Nachrichten!', 38134),
(200421, 'deDE', 'Beeilt Euch! Alles muss für die Königin perfekt sein!', '', 38134),
(200420, 'deDE', '', 'Hilfe! Kann mir denn niemand helfen?', 38134),
(200418, 'deDE', '', '<Nelwyn schluchzt.>', 38134),
(200416, 'deDE', 'Folgt der Straße nach Tirna Vaal. Dort treffen wir uns!', '', 38134),
(200396, 'deDE', 'Was für ein Blutbad! Applaus für die Überlebenden!', '', 38134),
(200395, 'deDE', 'Hah! Nicht schlecht für einen Haufen Neophyten!', '', 38134),
(200391, 'deDE', 'Eine wundervolle Vorstellung! Ihr werdet schnell bereit sein.', '', 38134),
(200390, 'deDE', 'Man sollte die Kunst des Fliegens üben, bevor man sich den Aufgestiegenen anschließt. Sehen wir, wie Ihr Euch schlagt!', '', 38134),
(200385, 'deDE', 'Vergeuden wir keine Zeit, in Ordnung?', '', 38134),
(200384, 'deDE', 'Wir ziehen los, wenn Ihr bereit seid.', '', 38134),
(200367, 'deDE', '', 'Passt auf, dass Euer Name sich nicht in diese Liste einreiht.', 38134),
(200366, 'deDE', '', 'Es gab so viele Enttäuschungen auf der Suche nach dem Speer.', 38134),
(200363, 'deDE', '<Das Ross wiehert Euch herausfordernd an!>', '<Das Ross wiehert Euch herausfordernd an!>', 38134),
(200362, 'deDE', '<Das Ross starrt Euch mit angelegten Ohren an und stampft bedrohlich auf.>', '<Das Ross starrt Euch mit angelegten Ohren an und stampft bedrohlich auf.>', 38134),
(200356, 'deDE', 'Hier entlang! Wir wurden von hungrigen Tieren angegriffen. Seelen sind in Gefahr!', '', 38134),
(200353, 'deDE', 'Die nächste Prozedur ist eine echte Bombe!', '', 38134),
(200345, 'deDE', '', 'Schattenpirscher sind eigentlich keine Fressfeinde des Gorms, aber Ihr habt es geschafft!', 38134),
(200344, 'deDE', '', 'Wir müssen den Gormbefall eindämmen. Vielleicht kann Euch meine Magie dabei helfen!', 38134),
(200343, 'deDE', 'Legionen der Verschmähten besudeln meine Schmiede. Schafft mir Platz, und ich helfe Euch.', '', 38134),
(200342, 'deDE', '', 'Ich stehe Renathal bei dem, was er zu erreichen hofft, zur Seite.', 38134),
(200341, 'deDE', '', 'Laurent hat seine Fehler, aber ich vertraue darauf, dass er den Spiegel repariert.', 38134),
(200340, 'deDE', '', 'Diese Umgebung ist viel besser geeignet.', 38134),
(200336, 'deDE', 'Ihr vertretet Lady Mondbeere? Tja, hoffentlich könnt Ihr gut mit ausgehungerten Tieren umgehen!', '', 38134),
(200334, 'deDE', 'Weiter!', '', 38134),
(200333, 'deDE', 'Federleicht! Wir brauchen Hilfe!', '', 38134),
(200331, 'deDE', 'Dieser Lärm... ist unerträglich!', '', 38134),
(200330, 'deDE', '', 'Was ist das für ein Wahnsinn?', 38134),
(200329, 'deDE', 'Es ist viel einfacher, meinen Fokus zu behalten, ohne mich um die Steinlegion sorgen zu müssen, die über meinem Kopf lauert.', '', 38134),
(200328, 'deDE', 'Eine... grausige Aufgabe, die notwendig ist, solange die Dürre anhält.', '', 38134),
(200326, 'deDE', 'Die Wildsamen bergen Naturgeister, die eines Tages erwachen und in ihre Welten zurückkehren.', '', 38134),
(200323, 'deDE', 'Folgt mir. Der Herzholzhain ist nicht weit.', '', 38134),
(200322, 'deDE', 'Die Königin muss entscheiden, wer überlebt... und wer verdorrt. Dann ist der Geist darin verloren.', '', 38134),
(200287, 'deDE', 'Stoßt ins Horn, $p!', '', 38134),
(200286, 'deDE', 'Schnell, Ihr müsst das Horn aufheben!', '', 38134),
(200275, 'deDE', 'Versucht, den Vogel vom Nest wegzulocken!', '', 38134),
(200272, 'deDE', '$p, nehmt das Horn!', '', 38134),
(200271, 'deDE', 'Nein! Das Horn!', '', 38134),
(200270, 'deDE', 'Für Königin und Hain!', '', 38134),
(200267, 'deDE', 'Danke, dass Ihr den Wildsamen gerettet habt! Ich bringe ihn in Sicherheit.', '', 38134),
(200266, 'deDE', 'Ihr habt Talent! Die Aspiranten könnten etwas von Euch lernen.', '', 38134),
(200265, 'deDE', 'Wer fliegen will, muss fallen lernen. Schließt Euch den Aspiranten an und versucht es!', '', 38134),
(200262, 'deDE', 'Ta\'saran von den Mittlern bietet Stallungen in der Halle der Kuriositäten an.', 'Ta\'saran von den Mittlern bietet Stallungen in der Halle der Kuriositäten an.', 38134),
(200259, 'deDE', 'Ihr findet einen Briefkasten außerhalb des Gasthauses am östlichen Ende des Rings der Schicksale.', 'Ihr findet einen Briefkasten außerhalb des Gasthauses am östlichen Ende des Rings der Schicksale.', 38134),
(200257, 'deDE', 'Ihr findet das Gasthaus am östlichen Ende des Rings der Schicksale.', 'Ihr findet das Gasthaus am östlichen Ende des Rings der Schicksale.', 38134),
(200255, 'deDE', 'Ihr findet den Flugmeister oben auf dem Ring der Übertragung. Nehmt das Portal vom Ring der Schicksale nach oben.', 'Ihr findet den Flugmeister oben auf dem Ring der Übertragung. Nehmt das Portal vom Ring der Schicksale nach oben.', 38134),
(200253, 'deDE', 'Ta\'visage von den Mittlern bietet Euch die Möglichkeit, Euer Aussehen zu verändern. Ihr findet ihn in der Halle der Kuriositäten.', 'Ta\'visage von den Mittlern bietet Euch die Möglichkeit, Euer Aussehen zu verändern. Ihr findet ihn in der Halle der Kuriositäten.', 38134),
(200252, 'deDE', '$p und ich greifen zuerst an. Der Rest, wenn ich in das Horn stoße!', '', 38134),
(200251, 'deDE', 'Wir werden es wiedergutmachen, Oberster Schmied. Ich schwöre es.', '', 38134),
(200250, 'deDE', 'Die Mittler stellen Euch in der Halle der Verwahrung Einrichtungen für Bankgeschäfte zur Verfügung.', 'Die Mittler stellen Euch in der Halle der Verwahrung Einrichtungen für Bankgeschäfte zur Verfügung.', 38134),
(200248, 'deDE', 'Die Mittler bieten in der Halle der Kuriositäten Transmogrifikationsdienste an.', 'Die Mittler bieten in der Halle der Kuriositäten Transmogrifikationsdienste an.', 38134),
(200246, 'deDE', 'Ihr findet die Aufseher und andere wichtige Besucher im Versammlungsraum der Enklave.', 'Ihr findet die Aufseher und andere wichtige Besucher im Versammlungsraum der Enklave.', 38134),
(200244, 'deDE', '', 'Tja, immerhin habe ich meine Schwingen, falls es schiefgeht. Ich vertraue Euch.', 38134),
(200243, 'deDE', '', 'Wir müssen da rein...? In den Brunnen der Seelen auf dem Weg zur ewigen Verdammnis ohne jede Hoffnung auf Freiheit und Erlösung?', 38134),
(200239, 'deDE', 'Sie haben eine riesige Bestie. Ihr sind viele von uns zum Opfer gefallen.', '', 38134),
(200238, 'deDE', 'Heute ehren wir Chaddius!\n\nMag es gern sauber. Badet dauernd.\n\nNahe Tempel der Reinheit zu finden.\n\nIhr bringt ihm reife Purianfrucht? Bitte?', '', 38134),
(200237, 'deDE', 'Heute ehren wir Alexandros!\n\nMag Aspiranten. Bringt süßen Saft.\n\nNahe Prüfstein des Aspiranten zu finden.\n\nIhr bringt ihm reife Purianfrucht? Bitte?', '', 38134),
(200236, 'deDE', 'Heute ehren wir Pagius!\n\nFindet alte Dinge. Repariert sie manchmal.\n\nArbeitet bei Agthias Ruhestätte.\n\nIhr bringt ihr reife Purianfrucht? Bitte?', '', 38134),
(200233, 'deDE', 'Lauft, Korenth!', '', 38134),
(200232, 'deDE', 'Öh... also... hallo?', '', 38134),
(200231, 'deDE', 'Macht weiter! Ich schleiche mich vorbei...', '', 38134),
(200230, 'deDE', 'Wir werden sie erlegen.', '', 38134),
(200227, 'deDE', 'Wisst Ihr, ich glaube, ich habe genau das Richtige.\n\nHaltet still. Tut vermutlich nicht weh.', '', 38134),
(200225, 'deDE', 'Ihr habt meinen Dank!', 'Ihr habt meinen Dank!', 38134),
(200224, 'deDE', 'Keine Zeit zum Ausruhen!', 'Keine Zeit zum Ausruhen!', 38134),
(200223, 'deDE', 'Die haben mich zu einem von ihnen gemacht...', 'Die wollten mich zu einer von ihnen machen...', 38134),
(200222, 'deDE', 'Es werden mit jedem Kampf mehr...', 'Es werden mit jedem Kampf mehr...', 38134),
(200221, 'deDE', 'Der Kampf geht weiter! Für den Ardenwald!', 'Der Kampf geht weiter! Für den Ardenwald!', 38134),
(200220, 'deDE', 'Wir werden den Wald nicht enttäuschen!', 'Wir werden den Wald nicht enttäuschen!', 38134),
(200219, 'deDE', '', 'Meine Aufgabe ist heilig.', 38134),
(200215, 'deDE', '', 'Ich nehme Mitgefühl mit und treffe Euch in Oribos. Ich muss mich vorbereiten. Diese Reise unternehmen nur sehr wenige Kyrianer...', 38134),
(200207, 'deDE', 'Danke, dass Ihr alle zum Gluthof gekommen seid!', '', 38134),
(200206, 'deDE', '', 'AHHH! Kommt, Uther. Unsere Vorsehung ist größer als diese Narren!', 38134),
(200204, 'deDE', '', 'Schluss mit den Spielchen. Es gibt Arbeit zu erledigen.', 38134),
(200203, 'deDE', 'Ihr zwingt Eure Ansichten anderen auf... Euer eigenes Versagen... Ich arbeite seit Äonen daran, meine Prinzipien zu verkörpern...', '', 38134),
(200202, 'deDE', 'Meisterin... nichts hat sich manifestiert.', '', 38134),
(200201, 'deDE', '', 'Zeigt uns Euren Stolz, Paragon!', 38134),
(200200, 'deDE', '', 'Ihr seid zu spät. Bald wird sich Euer geliebtes Paragon dem in Innern eingesperrten Stolz ergeben müssen.', 38134),
(200199, 'deDE', 'Spannungen? Wo zieht Ihr mich diesmal wieder rein, Draven?', '', 38134),
(200198, 'deDE', 'Ich weiß mit Grobianen wie Euch umzugehen!', '', 38134),
(200197, 'deDE', 'Wie unhöflich!', '', 38134),
(200196, 'deDE', 'Vielleicht wird Euch das Manieren beibringen!', '', 38134),
(200195, 'deDE', 'Wir treffen uns an der Ewigen Schmiede. Wenn Ihr dabei seid, solltet es bei der Ankunft keine Spannungen geben.', '', 38134),
(200193, 'deDE', 'Macht bitte schnell. Ich habe Termine einzuhalten.', '', 38134),
(200189, 'deDE', 'Kommt! Viele brauchen Hilfe!', '', 38134),
(200188, 'deDE', '', 'Keine Sorge, Uther. Wir haben alles, was wir brauchen.', 38134),
(200187, 'deDE', 'Die Sammler wurden angehalten. Wir verlieren Energie!', '', 38134),
(200185, 'deDE', 'Einer der Drust... und er ist groß.', '', 38134),
(200184, 'deDE', '', 'Was ist das?!', 38134),
(200183, 'deDE', '', 'Oh, ich liebe kreative Lösungen! In Zukunft überlegen sie es sich vielleicht zweimal.', 38134),
(200182, 'deDE', '', 'Diese Spriggans sind außer Kontrolle! Zeigen wir ihnen doch mal, wie sich das anfühlt.', 38134),
(200176, 'deDE', '', 'Gut gemacht, $n. Ich kenne keine Gnade für jene, die die Heiligkeit des Ardenwalds beschmutzen.', 38134),
(200175, 'deDE', '', 'Der Garten der Nacht ist von Spriggans überlaufen. Werdet zu meinem Avatar und säubert den Garten ohne Gnade!', 38134),
(200171, 'deDE', '', 'Moment, du hast AUCH ein Geschäft mit Bwonsamdi gemacht? Ich wusste ja, dass du pfuschst, Millhaus, aber das ist einfach enttäuschend!', 38134),
(200170, 'deDE', 'Was, der alte Loa schickt schon Schuldeneintreiber? Aber ich hatte sein Mojo doch noch gar nicht lang genug, um mein Meisterstück zu erstellen!', '', 38134),
(200169, 'deDE', 'Natürlich! Nur noch ein paar schnelle Nachbesserungen...', '', 38134),
(200168, 'deDE', 'Die Gäste treffen bald ein. Ich gehe davon aus, dass Ihr und Temel alles unter Kontrolle habt?', '', 38134),
(200167, 'deDE', 'Wie immer trefft Ihr genau dann ein, wenn ich Euch brauche, $p.', '', 38134),
(200165, 'deDE', 'Der neueste... und heißeste... Hof in Revendreth!', '', 38134),
(200163, 'deDE', '', 'Wir halten die Stellung! Öffnet die Tür!', 38134),
(200157, 'deDE', 'Halt! Die Straße ist nicht sicher!', '', 38134),
(200156, 'deDE', 'Lady Mondbeere... ist die beste Fee... Ist das wirklich nötig?', '', 38134),
(200155, 'deDE', 'Nun... wenn Ihr so wollt...', '', 38134),
(200151, 'deDE', 'Ahhh!', '', 38134),
(200150, 'deDE', '', 'Genug der närrischen Spielchen. Uther, bringt es zu Ende!', 38134),
(200148, 'deDE', 'Einst wart Ihr unseren Wegen und unserer Archon so treu. Ihr müsst die dunklen Gedanken verbannen. Ihr könnt Eure Anhänger und Euch selbst noch retten!', '', 38134),
(200147, 'deDE', '', 'Ich muss das Paragon finden. Mitgefühl sollte Euch am Boden Hilfe genug sein.', 38134),
(200145, 'deDE', '', 'Idealistisch wie immer, Paragon. Mein Wille wird Euch brechen.', 38134),
(200144, 'deDE', 'Lysonia, Eure Selbstsucht wird Euer Ende sein. Kehrt auf den Weg zurück!', '', 38134),
(200143, 'deDE', 'Ah! Wie können die Kyrianer nur diese Helligkeit aushalten?', '', 38134),
(200142, 'deDE', 'Die Bastion. Selbst ich kann meinen Sünden nicht entkommen.', '', 38134),
(200133, 'deDE', '', 'Bei der Gnade der Archon, Hilfe ist da. Meine Soldaten sind verwundet und ich kann sie kaum noch stabilisieren.', 38134),
(200131, 'deDE', '', 'Kommt, wir suchen mein Seelenband Pelagos an der Aspirantenrast auf.', 38134),
(200130, 'deDE', '', 'Da! Die Jünger der Demut sind in Bedrängnis, aber Voitha hält noch stand!', 38134),
(200129, 'deDE', '', 'Die armen Aspiranten... Die Verschmähten zwingen sie, ihrem Stolz zu erliegen, um ihre Armeen zu stärken.', 38134),
(200127, 'deDE', 'Eure Ketten werden ewig sein!', '', 38134),
(200126, 'deDE', 'Wie sehr Ihr Euch auch wehrt, Sterbliche. Euer Schicksal ist besiegelt!', '', 38134),
(200124, 'deDE', 'Moment... Ihr habt uns bei den Schimmerkaskadenhöhen geholfen. Vielleicht könnt Ihr das wieder.', '', 38134),
(200114, 'deDE', 'Hipokos, vielleicht könnt Ihr einen dieser Prätoren reparieren!', '', 38134),
(200113, 'deDE', 'Ihr seid frei! Schnell, bevor die Wachen zurückkehren!', '', 38134),
(200111, 'deDE', '', 'Die Wilde Jagd beschützt den Ardenwald im Namen der Königin. Jede helfende Hand erleichtert ihre Last!', 38134),
(200110, 'deDE', '', 'Die Schwarze Klinge ist in der Nähe. Sie wird Euch verteidigen!', 38134),
(200109, 'deDE', 'Findet die Höhle auf der anderen Seite der Kluft! Unsere Verbündeten schützen Euch!', '', 38134),
(200108, 'deDE', '', 'Wenn die Winterkönigin sie nur alle retten könnte...', 38134),
(200107, 'deDE', '', 'Jeder Wildsamen ist kostbar. So viele sind verloren.', 38134),
(200105, 'deDE', '', 'Mehr Alpträume! Schützt mich, während ich den Wildsamen beruhige!', 38134),
(200104, 'deDE', '', 'Das Lied muss sie anlocken...', 38134),
(200100, 'deDE', '', 'Warum seid Ihr hier, $n?', 38134),
(200098, 'deDE', '', 'Ich werde alles tun, Euch zu helfen, sie zu treffen. So, wie Ihr mir geholfen habt.', 38134),
(200097, 'deDE', '', 'Danke fürs Mitnehmen, $p. Tut mir leid, dass Ihr nicht mit der Königin sprechen konntet.', 38134),
(200096, 'deDE', 'Hier entlang, Lehrling!', '', 38134),
(200095, 'deDE', '', 'Spriggans! Vorsicht!', 38134),
(200094, 'deDE', '', 'Der Alptraum ist zurück... Ich muss die Harfe spielen...', 38134),
(200093, 'deDE', '', 'Ihr findet das erste Versteck in dieser Richtung. Viel Glück!', 38134),
(200092, 'deDE', 'Nein! Die Macht der Kuratorin war fast mein.', '', 38134),
(200087, 'deDE', 'Ich erwarte Euch im Winterschlafbecken. Gute Reise, Ihr drei.', '', 38134),
(200085, 'deDE', 'Niya, nehmt meine Harfe. Ihre Musik besänftigt die Wildsamen.', '', 38134),
(200084, 'deDE', 'Das Winterschlafbecken wird dem Wildsamen guttun. Vielleicht möchte Niya Euch begleiten.', '', 38134),
(200083, 'deDE', 'Korenth gerät immer in Schwierigkeiten. Schauen wir, was es dieses Mal ist...', '', 38134),
(200082, 'deDE', 'Wenn Ihr mich wollt, kommt und holt mich.', '', 38134),
(200081, 'deDE', 'Grashaal, sie haben Euch geweckt, um mich abzulösen?', '', 38134),
(200080, 'deDE', 'Widerwärtige Selbstsucht! Wir geben niemals auf. Wir folgen Befehlen!', '', 38134),
(200079, 'deDE', 'Kaal, wenn Euch mein Urteil je etwas wert war, dann vertraut mir und gebt auf.', '', 38134),
(200078, 'deDE', '', 'Draven! Zwingt mich nicht, Euch zu töten.', 38134),
(200076, 'deDE', 'Könnt Ihr es fühlen? Eine Verschiebung in der natürlichen Ordnung? Etwas geht hier vor sich. Wir müssen rasch handeln.\n\nDrei Ernter und $uein Schlundgänger:eine Schlundgängerin;, durchflutet von der ungezähmten Macht der Anima aus den vielen Reichen der Schattenlande, gegen einen Herrn des Todes, und wir stehen am Abgrund zu seinem Herrschaftsgebiet.\n\nLasst uns diesen Schrecken gemeinsam bekämpfen!', '', 38134),
(200075, 'deDE', 'Meiner Art gebührt zwar sonst nicht die Ehre des Seelenbands, aber ich bin kein normaler Provost! Also?', '', 38134),
(200074, 'deDE', 'Ich hatte schon seit Ewigkeiten nicht mehr so großartige Abenteuer wie mit Euch.', '', 38134),
(200073, 'deDE', '', 'Nie hätte ich geglaubt, dass mich das Schicksal hierher verschlägt, und doch stehen wir hier.\n\nAlso... Wie gelangen wir zum Schlund?', 38134),
(200071, 'deDE', 'Was wir ihm angetan haben, war keine Gerechtigkeit. Es war Rache.', '', 38134),
(200070, 'deDE', 'Immer wenn ich meine Augen schließe, sehe ich meinen Tod. Er steht vor mir... mit dieser verfluchten Klinge...', '', 38134),
(200069, 'deDE', 'Bringt ihn zur Ewigen Wacht, bis die Archon über sein Schicksal entschieden hat.', '', 38134),
(200066, 'deDE', '', 'Eure heutigen Taten waren sehr lehrreich.', 38134),
(200065, 'deDE', 'Ich schwöre den Verschmähten ab! Lieber vergesse ich meine Vergangenheit, als dass dies unsere Zukunft wird!', '', 38134),
(200063, 'deDE', 'Ehrloser Feigling!', '', 38134),
(200062, 'deDE', 'Genug der Spiele! Wachen! Schlachtet die Schwächlinge ab!', '', 38134),
(200059, 'deDE', '', 'Die Gorm vermehren sich rasend. Zerstört ihre Larven, um die Ausbreitung einzudämmen.', 38134),
(200057, 'deDE', 'Ihr wollt Hilfe?', 'Ihr wollt Hilfe?', 38134),
(200055, 'deDE', 'Hilfe gefällig?', 'Hilfe gefällig?', 38134),
(200054, 'deDE', 'Ihr mögt süßen Saft? Kann ich holen.', 'Ihr mögt süßen Saft? Kann ich holen.', 38134),
(200052, 'deDE', 'Kann viel helfen. Einfach fragen.', 'Kann viel helfen. Einfach fragen.', 38134),
(200046, 'deDE', 'Purianfrüchte machen glücklich! Juu-huu!', 'Purianfrüchte machen glücklich! Juu-huu!', 38134),
(200045, 'deDE', 'Bin glücklich! Danke Eu-heuch!', 'Bin glücklich! Danke Eu-heuch!', 38134),
(200044, 'deDE', 'Huh, Ihr ehrt mich!', 'Huh, Ihr ehrt mich!', 38134),
(200043, 'deDE', 'Ihr ehrt MICH? Wow!', 'Ihr ehrt MICH? Wow!', 38134),
(200042, 'deDE', '', 'Vorerst müssen wir Nachtfae an ihrer Stelle handeln.', 38134),
(200037, 'deDE', 'Frohen Provostentag!\n\nWir reparieren. Wir tragen. Wir dienen.\n\nSind Provosten!', '', 38134),
(200035, 'deDE', 'In letzter Zeit sind sehr viele seltsame Dinge geschehen. Ich kann mich nicht erinnern, je solch einen Aufruhr im Ardenwald gesehen zu haben.', '', 38134),
(200032, 'deDE', '', 'Wie seltsam... Fahrt fort mit dem Lernen. Wir treffen uns beim Begrüßer.', 38134),
(200031, 'deDE', '', 'Was ist das? Sieht kyrianisch aus, aber irgendwie... entstellt.', 38134),
(200028, 'deDE', 'Narr! Alle Häuser von Maldraxxus stehen gegen Euch!', '', 38134),
(200027, 'deDE', 'Ihr könnt nicht gewinnen, Vyraz! Ihr seid in der Unterzahl!', '', 38134),
(200026, 'deDE', 'Anscheinend wurde dieser Mittler unlängst getötet.', 'Anscheinend wurde dieser Mittler unlängst getötet.', 38134),
(200025, 'deDE', 'Danke, Anduin. Es wird guttun, wieder bei Kräften zu sein.', '', 38134),
(200024, 'deDE', 'Diese Wunden sehen schlimm aus, Thrall. Lasst sie mich versorgen.', '', 38134),
(200014, 'deDE', '', 'Kala, du warst heute wirklich ungezogen. Entschuldige dich.', 38134),
(200013, 'deDE', '', 'In den kommenden Jahren werde ich Euch in den Gebrauch des Stabs einweisen, um den Garten und seine Kreaturen wiederherzustellen, Zosime.', 38134),
(200012, 'deDE', 'Ein Soldat der Allianz hat viele Eurer Kameraden getötet. Mir scheint, das verlangt nach einer Machtdemonstration.', '', 38134),
(200011, 'deDE', 'Ein Soldat der Horde hat Eure Verbündeten abgeschlachtet. Ruft ihm ins Gedächtnis, wie überlegen Ihr seid.', '', 38134),
(200010, 'deDE', 'Ihr habt kurzen Prozess mit den Dieben gemacht. Ich werde das vermerken.', '', 38134),
(200009, 'deDE', 'Die Nachtfae scheinen von Dieben heimgesucht worden zu sein. Eure Hilfe würden sie sicher zu schätzen wissen.', '', 38134),
(200008, 'deDE', 'Erlesene Schwertkunst, Sterblicher. Ich habe in Zukunft... vielleicht ein paar Aufträge für Euch.', '', 38134),
(200007, 'deDE', 'Bei Venthyrduellen geht es um Stolz und Prestige. Für das Kartell bedeuten sie aber auch Profit.', '', 38134),
(199996, 'deDE', '', 'Wenn Ihr Erfolg in Eurem Innern findet, kehre ich zurück.', 38134),
(199995, 'deDE', '', 'Kanalisiert all Eure Anima in den Stab, Kind. Euer Mut und Eure Hoffnung werden hier auf die Probe gestellt.', 38134),
(199994, 'deDE', '', 'Der Mut ruft. Ah, Zosime. Ihr kommt, um den Stab zu holen.', 38134),
(199993, 'deDE', '', 'Geht voran. Ich muss Kala beruhigen. Jetzt ist sie so aufgeregt, weil sie ihr Spielzeug HAT.', 38134),
(199992, 'deDE', '', 'Wenn Ihr eintretet, dann läutet die Vesperglocke. Hoffentlich erscheint Korinna und sagt uns, was zu tun ist.', 38134),
(199984, 'deDE', 'Und nun, Keltesh, habt Ihr etwas Verdächtiges bemerkt?', 'Und nun, Keltesh, habt Ihr etwas Verdächtiges bemerkt?', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(199951, 'deDE', '', 'Der Garten der Erholung ist ein friedlicher Ort, um zu meditieren und über die eigenen Herausforderungen zu sinnieren. Oder zumindest war er das. Ich hoffe, das wird er wieder sein.\n\nDie große Korinna erschuf diesen Ort für alle. Sie war eine der ersten Aufgestiegenen. Am Anfang war es Korinna, die die Kreaturen der Bastion zähmte und ihnen inneren Frieden brachte. Aber dieser Frieden wurde durch den Mangel an Anima zerstört, und niemand findet hier mehr Ruhe.', 38134),
(199950, 'deDE', '', 'Ich habe keinen Weg aus dem Schlund gefunden. Ich kann nur über kurze Distanzen teleportieren. Dieser Alptraum scheint... ausbruchsicher.', 38134),
(199948, 'deDE', '', 'Es tut gut, Euch zu sehen. Als Teil meiner Aspirantenprüfungen muss ich mich um diesen Garten hier kümmern. Und es könnte besser laufen.\n\nDie Animadürre macht viele der Tiere hier rasend. Ich muss irgendwie das Gleichgewicht wiederherstellen.\n\nWerdet Ihr mir helfen?', 38134),
(199905, 'deDE', 'Eine solch erlauchte Audienz ist eine seltene Ehre. Zeigt Euch ihrer würdig.', '', 38134),
(199904, 'deDE', 'Die Paragons haben sich versammelt und erwarten die Ankunft der Archon. Ihr solltet besser schweigen, bis Ihr gefragt werdet.', '', 38134),
(199887, 'deDE', '', '<Z\'rali schreit vor Schmerzen.>', 38134),
(199886, 'deDE', '', 'Ihr... wisst nicht... was Ihr tut...', 38134),
(199885, 'deDE', '', 'Das Licht... wird... siegen...', 38134),
(199879, 'deDE', '', 'Was möchtet Ihr wissen?', 38134),
(199877, 'deDE', 'Seid Ihr jetzt der Boss? Dann braucht Ihr wohl den Schlüssel hier.', '', 38134),
(199876, 'deDE', 'Unser Ziel liegt tief im Gefängnis. Lasst Euch von niemandem aufhalten.', '', 38134),
(199875, 'deDE', 'Wenn Ihr was zu sagen habt, dann zum Boss, Edgar.', '', 38134),
(199873, 'deDE', '', 'Ich brauche etwas... Zeit, um mich von den Vorfällen in Torghast zu erholen. Ich möchte jetzt nicht weiter darüber sprechen.', 38134),
(199869, 'deDE', '', '<Korayn starrt zum Horizont.>', 38134),
(199867, 'deDE', 'Wenn die Gäste mir bitte folgen würden. $uDer Schlundgänger:Die Schlundgängerin; möchte uns etwas zeigen.', '', 38134),
(199866, 'deDE', 'Natürlich. Das ist nicht allzu weit.', '', 38134),
(199859, 'deDE', 'Wohin soll ich unsere Gäste führen?', '', 38134),
(199858, 'deDE', '', 'Ich gebe mir Mühe. Vielleicht könnt Ihr dem Wald bald wieder zu Wachstum verhelfen!', 38134),
(199856, 'deDE', '', 'Der Wald seufzt vor Lachen. Ihr bringt Freude, Lady Mondbeere.', 38134),
(199855, 'deDE', '', 'Ein Bart, ein Hut... Geschenke der Zeit und... Spaß der Fae.', 38134),
(199853, 'deDE', 'Entspannt Euch, $p. Nehmt Euch etwas von den übrig gebliebenen Erfrischungen.\n\nNach so einem erfolgreichen Hof habt Ihr etwas Ruhe und eine Belohnung verdient.', '', 38134),
(199850, 'deDE', 'Konntet Ihr die benötigte Lilie sammeln?', '', 38134),
(199849, 'deDE', 'Ich sage es ungern, aber wir müssen den Zeitplan einhalten.', '', 38134),
(199848, 'deDE', '', 'Gut gemacht, $n. Die Seuche ist unter Kontrolle, zumindest für jetzt.', 38134),
(199847, 'deDE', '', 'Hier wimmelt es von Gorm. Rottet die Seuche aus, bevor sie sich ausbreitet!', 38134),
(199832, 'deDE', '', 'Ich begleite Euch gern. Ich mache mich für die Reise bereit.', 38134),
(199828, 'deDE', '', 'Das wird ein wundervoller Streich! Macht einfach... Oh, hallo!', 38134),
(199826, 'deDE', 'Das gnadenlose Streben nach Sieg.', '', 38134),
(199823, 'deDE', '', 'Sie manifestieren sich als böse, umherirrende Schemen. Ich bezweifle, dass sie uns helfen wollen.', 38134),
(199821, 'deDE', '', 'Seid gegrüßt, Bewohner von Argus! Wir haben einige Aktivitäten vorbereitet, damit Ihr Euch an Eure neue Existenz gewöhnen könnt.', 38134),
(199820, 'deDE', '', 'Seid gegrüßt, Bewohner von Draenor! Wir haben einige Aktivitäten vorbereitet, damit Ihr Euch an Eure neue Existenz gewöhnen könnt.', 38134),
(199818, 'deDE', '', 'Gleiche Siegel, ein Weg ins Nirgendwo. Nur ein Weg, nur ein Siegel seiner Art.', 38134),
(199816, 'deDE', '', 'Sie hat wohl einen mächtigen Verbündeten. Die Schatten hier flüstern von einem... Kerkermeister.', 38134),
(199815, 'deDE', '', 'Seht den Weg. Durchdringt den Nebel.', 38134),
(199812, 'deDE', '', 'Stein und Siegel zeigen den Weg. Eins davon ist falsch. Eins davon ist der Weg.', 38134),
(199811, 'deDE', '', 'Ich muss meine Station vorbereiten. Wir sehen uns dort!', 38134),
(199809, 'deDE', '', 'Bitte besucht die Stationen, um mehr zu erfahren.', 38134),
(199800, 'deDE', 'Wir werden ihn hier und jetzt rächen!', '', 38134),
(199647, 'deDE', '', 'Eine unmögliche Entscheidung. Und doch muss sie für den Kreislauf getroffen werden.', 38134),
(199646, 'deDE', '', 'Das Herz ist durch die Dürre verkümmert. Seine Kraft muss gestützt werden... obwohl kaum noch Anima bleibt, um unsere Haine zu retten.', 38134),
(199645, 'deDE', '', 'Das Herz des Waldes ist kritisch für den Ardenwald... und den Schutz der anderen Reiche.', 38134),
(199643, 'deDE', '', 'Mein Wissensschatz ist immens. Was möchtet Ihr erfahren?', 38134),
(199639, 'deDE', '', 'Ich verstehe nicht.\n\nWelche Welt, welches System, welche Entität, welches Reich oder welche Daseinsform hat Euch hervorgebracht?', 38134),
(199638, 'deDE', '', 'Wir sind uns bewusst, dass sich das Ende einer sterblichen Existenz abrupt anfühlen kann und manchmal nur schwer zu akzeptieren ist. Ihr seid nicht das erste und auch nicht das letzte Individuum, das die Natur seines neuen Daseins infrage stellt.\n\nNutzt diese Gelegenheit, um diese Tatsachen zu verarbeiten und sie zu akzeptieren. Danach können wir mit Eurer Orientierung fortfahren.', 38134),
(199615, 'deDE', 'Ich werde ihn mit der Hitze von tausend Sonnen in Flammen aufgehen lassen!', '', 38134),
(199613, 'deDE', 'Befreit die Gefangene und bringt sie schnell in Sicherheit. Wir werden siegen.', '', 38134),
(199611, 'deDE', '', 'Es fühlt sich an, als ob der Ardenwald auseinanderbricht. Zuerst die Besessenen, jetzt greifen seltsame Monster Tirna Vaal an...\n\nBraucht Ihr etwas, $n?', 38134),
(199609, 'deDE', 'Kommt, Sünder! Dient Eurem Meister!', '', 38134),
(199601, 'deDE', '', 'Lord Herne, wir erbitten eine Audienz bei der Winterkönigin.', 38134),
(199600, 'deDE', '', 'Leider ernste Angelegenheiten.', 38134),
(199599, 'deDE', 'Mondbeere. Hier für neue Streiche? Oder gibt es ernste Angelegenheiten?', '', 38134),
(199598, 'deDE', '', 'Boggarts im Publikum? Wäre nicht mein erstes heimgesuchtes Stück. Die Show muss weitergehen!', 38134),
(199597, 'deDE', '', 'Das Publikum ist ungewöhnlich laut. Ich vermute... Unlauteres. Könntet Ihr der Sache nachgehen?', 38134),
(199596, 'deDE', 'Ich sehe Euch, Schlundgänger. Verschwindet aus meinem Turm!', '', 38134),
(199595, 'deDE', '', 'Kommt, $p. Kommt zu mir an meinem Hof und seht das Herz des Waldes. Dann sprechen wir weiter.', 38134),
(199594, 'deDE', '', 'Wenn der Verbannte sich regt, gibt es womöglich eine noch größere Gefahr als diese Dürre.', 38134),
(199593, 'deDE', '', 'Mein alter Freund. Nach Äonen sind unsere schlimmsten Ängste wahr geworden.', 38134),
(199592, 'deDE', '', 'Die Winterkönigin sieht sich die Botschaft des Primus an.', 38134),
(199590, 'deDE', '', 'Sylvanas befehligt die Streitmacht des Kerkermeisters. Sie könnte sie herbeirufen und uns überwältigen.\n\nEs hilft Anduin nicht, wenn wir alle gefangen genommen werden. Wir sollten eine Konfrontation vermeiden, bis wir wissen, worauf sie aus ist.', 38134),
(199585, 'deDE', 'In der Tat. Jede Menge Möglichkeiten.', 'In der Tat. Jede Menge Möglichkeiten.', 38134),
(199584, 'deDE', 'Das ist dein Schicksal. Akzeptiere es!', '', 38134),
(199576, 'deDE', '', 'Dann seid Ihr kein Jäger mehr.', 38134),
(199573, 'deDE', 'Ich diene jetzt einem neuen Meister! Einem, der den Ardenwald verdorren sehen wird.', '', 38134),
(199559, 'deDE', '', 'Das ist besorgniserregend. Ein uraltes Ritual hat ihr unglaubliche Macht verliehen. Ich fürchte, dass sie sie nicht kontrollieren kann.', 38134),
(199557, 'deDE', '', 'Tyrande? Hier? Nein, habe ich nicht.', 38134),
(199555, 'deDE', '', 'Diese Höhle hat mir schon vorher als Versteck gedient. Für eine kurze Weile bietet sie uns Schutz.', 38134),
(199554, 'deDE', '', 'Ja, ich bin unzählige Male ausgebrochen, lande aber immer wieder in einem Käfig.', 38134),
(199533, 'deDE', 'Gut gemacht! Ihr habt Eure tiefsten Zweifel besiegt.', '', 38134),
(199532, 'deDE', 'Nicht ohne Kampf!', '', 38134),
(199531, 'deDE', 'Du wirst am Tempel der Loyalität fallen und zu den Verschmähten wechseln!', '', 38134),
(199530, 'deDE', 'Du irrst dich. Vereint sind wir stärker. Alle drei!', '', 38134),
(199529, 'deDE', 'Du wirst nie aufsteigen! Du wirst nur Kleias Lakai sein!', '', 38134),
(199528, 'deDE', 'Das wird dein letzter Fehlschlag.', '', 38134),
(199527, 'deDE', 'So oft habe ich bei dieser Prüfung versagt... aber nicht heute.', '', 38134),
(199526, 'deDE', 'Vereint.', '', 38134),
(199524, 'deDE', 'Ich... nein... Wir schaffen das! Ich bin NICHT allein.', '', 38134),
(199523, 'deDE', 'ICH... SCHAFFE... DAS... NICHT!', '', 38134),
(199522, 'deDE', 'Nicht stark genug.... nicht... gut genug...', '', 38134),
(199520, 'deDE', '', 'Es wird Zeit, Eure neue Rüstung auf die Probe zu stellen.', 38134),
(199515, 'deDE', 'Widerstand ist... sinnlos...', '', 38134),
(199514, 'deDE', '', 'Wir müssen Lysonia unbedingt verfolgen. Sie darf kein weiteres Mal zuschlagen!', 38134),
(199510, 'deDE', '', 'So viel zu unserem Glück!', 38134),
(199508, 'deDE', 'Ich... weiß nicht, ob ich das schaffe...', '', 38134),
(199507, 'deDE', 'Und jetzt vernichten wir ihr Lieblingsprojekt!', '', 38134),
(199485, 'deDE', '', 'Gor\'groth hat Wärtersteine erwähnt. Findet sie und befreit damit den Kriegsherrn.', 38134),
(199484, 'deDE', 'Das Haus der Seuchen... vernichtet! Wo ist Stradama? Ich muss den Trank herstellen... I-Ich muss...', '', 38134),
(199483, 'deDE', 'Niemand kann den Lichs öffentlich Verrat vorwerfen, also habe ich einen neuen Trank ersonnen - ein letztes Mittel, um Stradama zu schützen, falls nötig. Ich bete, so weit kommt es nicht.', '', 38134),
(199482, 'deDE', 'Das Haus der Rituale will uns mit Anima versorgen. Stradama und ich vermuten ein doppeltes Spiel... doch unser Haus kann nicht ablehnen. Wir müssen wachsam bleiben.', '', 38134),
(199481, 'deDE', 'Ihr könnt nirgendwo hin. Nichts entkommt dem Schlund!', '', 38134),
(199480, 'deDE', 'Mein Genie... muss... fortbestehen...', '', 38134),
(199477, 'deDE', 'Während der Primus fort ist, bahnt sich ein Bürgerkrieg an. Markgräfin Stradama vertraut mir ihre Ängste an. Ohne Anima stagniert unsere Arbeit - und somit auch unsere Macht.', '', 38134),
(199471, 'deDE', 'Wartet! Tut ihm nichts!', '', 38134),
(199468, 'deDE', 'Eure Zeit is\' vorbei. Eure Macht gleich null. Un\' jetzt kassiert der alte Mueh\'zala ab.', '', 38134),
(199467, 'deDE', 'Was seid Ihr für \'n Loa des Todes, Bwonsamdi. Nehmt Euch doch einfach, was Euer is\'!', '', 38134),
(199466, 'deDE', 'Rennt Ihr jetz\' mit Euren Problemen zur Winterkönigin?', '', 38134),
(199465, 'deDE', 'Nein, nein, nein. Ihr versteht nich\'! Der Boss klaut die Loa im Schlaf und...', '', 38134),
(199464, 'deDE', '', 'Streit unter den Loa ist nicht meine Angelegenheit. Zieht den Ardenwald nicht in Eure kleinlichen Zankereien hinein.', 38134),
(199463, 'deDE', 'He, Majestät. Ich komm\' in Frieden. Wir müss\'n zusammenarbeit\'n, um den alt\'n Mueh\'zala zu besieg\'n.', '', 38134),
(199462, 'deDE', '', 'Was hat diese Störung zu bedeuten, $n?', 38134),
(199461, 'deDE', 'He da, Eiskönigin! Lang nich\' geseh\'n, hm? Schöner Baum, übrigens. Habt\'s ja richtig hübsch hier!', '', 38134),
(199459, 'deDE', 'Da will wohl jemand die Geheimnisse meines Genies stehlen!', '', 38134),
(199458, 'deDE', '', 'Ah-HAH! Also HABT Ihr beim Prozess gepfuscht!', 38134),
(199457, 'deDE', 'Nun, äh, erfolgreiche Fleischvorbereitung hängt von vielen komplizierten Faktoren ab. Vielleicht hätte ich etwas mehr...', '', 38134),
(199456, 'deDE', '', 'Äh... Es hat sich gerade bewegt! Die Kyrianerteile kämpfen um die Kontrolle über das Konstrukt!', 38134),
(199455, 'deDE', 'Unfug! Sobald wir eine Maldraxxiseele einsetzen, bringt sie die letzten Echos der Gehirnwäsche der Bastion zum Schweigen!', '', 38134),
(199454, 'deDE', '', 'Er wird außerdem merken, dass Ihr das Fleisch nicht von Erinnerungen gereinigt habt!', 38134),
(199453, 'deDE', 'Wenn Fleischnaht zurückkehrt, wird er erfahren, dass ich, Malkrex, seine Forschung abgeschlossen habe. Kyrianisches Fleisch wird zum ultimativen Konstrukt!', '', 38134),
(199452, 'deDE', '', 'Da seid Ihr ja! Die Königin ist bald hier!', 38134),
(199450, 'deDE', 'Hab gehört, Derawyn und Korenth hatten\'s mit Streichen. Stimmt das?', '', 38134),
(199449, 'deDE', '', 'Danke.', 38134),
(199448, 'deDE', 'Na ja... ich bleibe besser bei Euch. Zum Schutz.', '', 38134),
(199447, 'deDE', '', 'Schon gut, Te\'zan. Geht ruhig.', 38134),
(199446, 'deDE', 'Ist es wirklich ungefährlich? Hier sind noch eine Menge maskierte Fae.', '', 38134),
(199445, 'deDE', '', 'Wir treffen uns in Tirna Vaal. Ich... muss mich nur noch verabschieden.', 38134),
(199442, 'deDE', '', 'Das Herz des Hains! Nein!', 38134),
(199441, 'deDE', '', 'Ohne die Anima des Herzens werden so viele Wildsamen darben...', 38134),
(199440, 'deDE', 'Niya... es... tut mir leid...', '', 38134),
(199439, 'deDE', '', 'Ihr bekommt gar nichts mehr!', 38134),
(199438, 'deDE', 'Mir gefällt nicht, wie Ihr mich anseht.\n\nIhr wisst schon, dass Ihr im Leben nach dem Tod ewigen Frieden finden sollt, ja?', '', 38134),
(199436, 'deDE', '', 'Fremde Magie hat von unseren Leuten Besitz ergriffen, Animadürren suchen das Land heim und das Herz eines Hains wurde gestohlen.\n\nDas geht mir eindeutig zu schnell.', 38134),
(199427, 'deDE', 'Noch stehe ich, Prinz. Auch wenn mir morgen früh die Flügel schmerzen werden.', '', 38134),
(199426, 'deDE', 'Draven! Geht es Euch gut?', '', 38134),
(199424, 'deDE', 'Es gibt keinen Stein, den ich nicht brechen kann, Grashaal!', '', 38134),
(199423, 'deDE', 'Ihr könnt meinem Zorn nicht entkommen, Kaal!', '', 38134),
(199422, 'deDE', '', 'Unsere Wege trennen sich im Dämmerwald. Tirna Vaal ruft Euch heim.', 38134),
(199421, 'deDE', 'Ich brauche mehr Anima!', '', 38134),
(199420, 'deDE', 'Bringt mir ihre Anima, Helden!', '', 38134),
(199419, 'deDE', '', 'Hoffnung erwächst aus Asche. Der Zyklus geht weiter. Immer.', 38134),
(199417, 'deDE', 'Ich werde den Luftraum säubern!', '', 38134),
(199414, 'deDE', 'Alle werden sich beugen! Alle werden fallen!', '', 38134),
(199413, 'deDE', 'Die Meister werden den Ardenwald beherrschen!', '', 38134),
(199412, 'deDE', 'Na schön. Aber ich will später was zurückhaben!', '', 38134),
(199411, 'deDE', '', 'Gegebene Anima kehrt wieder zurück.', 38134),
(199410, 'deDE', '', 'Dunkelheit kommt. Das Licht der Sterne ist unser Schild.', 38134),
(199399, 'deDE', 'Ich versichere Euch, dass ich nicht ganz so hilflos bin.\n\nMein lieber Freund Theotar kommt regelmäßig vorbei, um dafür zu sorgen, dass ich vor den harschen Grausamkeiten des Lichts geschützt bin.\n\nEr ist ein wahrer Gentleman.', '', 38134),
(199397, 'deDE', 'Natürlich nicht. Mir gefällt es hier oben recht gut.\n\nFriedlich und ruhig. Niemand stört mich... Normalerweise.\n\n<Lord Rauborsten setzt einen zu seinem Namen passenden Gesichtsausdruck auf.>\n\nIch hole endlich all die Lektüre nach, die ich seit Jahren lesen wollte.\n\nIch wünschte nur, ich hätte mehr Bücher.', '', 38134),
(199396, 'deDE', 'Ja, was wollt Ihr?\n\nWenn Ihr meine Lesezeit so unhöflich unterbrecht, hoffe ich, dass Ihr einen sehr guten Grund dafür habt.', '', 38134),
(199395, 'deDE', 'So viele sind verloren. So viele sind gestorben...\n\n<Ara\'lon verfällt in Schweigen.>', '', 38134),
(199384, 'deDE', 'Viel Glück da drin. Der neue Boss ist ziemlich temperamentvoll.', '', 38134),
(199383, 'deDE', 'Ich mach Euch mal schnell die Tür da auf.', '', 38134),
(199382, 'deDE', '', 'Ihr seid eine Schande für uns, Draven! Wenn Ihr nicht dient, müsst Ihr fallen!', 38134),
(199380, 'deDE', 'Kaal! Grashaal! Lasst Euch nicht durch fehlgeleitete Loyalität blenden! Denathrius\' Handel wird Revendreth vernichten!', '', 38134),
(199379, 'deDE', '', 'Steingeborene! Tötet diese Thronräuber!', 38134),
(199378, 'deDE', 'Brauche... all meine Kraft... um ihren... Angriff aufzuhalten!', '', 38134),
(199377, 'deDE', 'Angriff! In Deckung!', '', 38134),
(199372, 'deDE', '', 'Diese scheußlichen Kreaturen lernen den Zorn der Wilden Jagd kennen!', 38134),
(199370, 'deDE', 'Der zerrissene Himmel macht uns alle nervös, einschließlich der Geißel. Lasst uns die Zeit nutzen und Eure Kampffertigkeiten aufpolieren.', '', 38134),
(199369, 'deDE', 'Willkommen an Bord! König Wrynn erwartet, dass jeder Held der Allianz in Höchstform gegen die Schrecken von Eiskrone antritt!', '', 38134),
(199368, 'deDE', '', 'Die Geißel ist unruhig, seit der Himmel aufgerissen wurde. Es ist unsere Aufgabe, sie im Auge zu behalten. Wir sollten daher sicherstellen, dass Ihr jederzeit kampfbereit seid.', 38134),
(199367, 'deDE', '', 'Willkommen bei der Eiskronenpatrouille! Ich hoffe, Ihr seid bereit!', 38134),
(199365, 'deDE', '', 'Wie kann ich behilflich sein?', 38134),
(199363, 'deDE', '', 'Derawyn... nein! Das werden diese Monster büßen!', 38134),
(199362, 'deDE', '', 'Der Zyklus... geht weiter... und Ihr... auch...', 38134),
(199361, 'deDE', '', 'Korenth ist fort. Euch kann ich nicht auch noch verlieren!', 38134),
(199360, 'deDE', '', 'Nein... die Anima... ist für die Wildsamen...', 38134),
(199357, 'deDE', 'Hipokos hat unter den gefallenen Kyrianern einen Prätor gefunden und ihn repariert. Da dieser keine Seele besitzt, sollte er die Barriere durchqueren können.\n\nUnter Eurer Führung könnte er die Verteidigung ausschalten und uns so den Weg freimachen.', '', 38134),
(199356, 'deDE', 'Für Azeroth!', '', 38134),
(199353, 'deDE', 'Wir müssen fliehen und der Archon berichten, was hier geschieht.', '', 38134),
(199352, 'deDE', 'Den habe ich früher in meinem alten Hain für Wildsamen benutzt. Möge er Euch gute Dienste leisten.', '', 38134),
(199350, 'deDE', 'Danke, dass Ihr uns gefunden habt. Der Ardenwald könnte mehr Leute wie Euch gebrauchen.', '', 38134),
(199349, 'deDE', 'Ich habe Krexus besiegt! Ihr zwei seid Nichts!', '', 38134),
(199348, 'deDE', 'Meine Klinge wird Euren Schädel spalten, Verräter! Für Krexus!', '', 38134),
(199345, 'deDE', 'Dummer Mograine, nur Loyalität statt Macht im Sinn. Aber Sieg hängt von den richtigen Verbündeten ab. Und mit ihrer Hilfe beanspruche ich den Markgraftitel!', '', 38134),
(199344, 'deDE', 'Eure eigenen Pläne sind Euer Verderben. Jetzt löschen wir Euch aus!', '', 38134),
(199343, 'deDE', 'Der Sterbliche kehrt also zurück, zusammen mit unserem verirrten Baron. Die Konstrukte und der Schlund konnten Euch also leider nicht töten.', '', 38134),
(199342, 'deDE', '', 'Genug, Lorkron! Wenn Ihr mich wirklich kennt, dann solltet Ihr Angst haben.', 38134),
(199341, 'deDE', 'Ihr habt uns auf eine aussichtslose Jagd geschickt. Nun seid Ihr die Beute. Und ich kenne Euch gut.', '', 38134),
(199340, 'deDE', '', 'Die maskierten Fae... Das ist das Schicksal, das sie erwartet?!', 38134),
(199339, 'deDE', 'Erkennt Ihr Eure Beute nicht? Die Klingen? Die Gesichter hinter den Masken?', '', 38134),
(199337, 'deDE', '', 'Ich kenne die Stimme. Aber er ist in der Schlacht gefallen...', 38134),
(199336, 'deDE', 'Korayn... wie immer die verschlagene Jägerin.', '', 38134),
(199335, 'deDE', 'Kennt Eure Beute. Das habt Ihr mich als Erstes gelehrt. Und selbst vergessen.', '', 38134),
(199334, 'deDE', '', 'Lorkron... was ist mit Euch geschehen?', 38134),
(199332, 'deDE', '', 'Kehrt zum Schimmerkaskadenbecken zurück. Meine Jagd hat erst begonnen.', 38134),
(199331, 'deDE', '', 'Ein lieber Freund... jetzt ein erbitterter Feind. Er muss sterben.', 38134),
(199330, 'deDE', 'Nein! Meine Beute schlägt mich nicht wieder. Hütet Euch vor den Schatten, Korayn...', '', 38134),
(199329, 'deDE', 'Eure Jagd endet hier, Korayn!', '', 38134),
(199325, 'deDE', '', 'Wartet.', 38134),
(199323, 'deDE', '', 'Vorsicht. Diese Augen können meinen Zauber durchschauen.', 38134),
(199322, 'deDE', '', 'Vorwärts zum Sieg!', 38134),
(199320, 'deDE', '', 'Euer Handeln hat diese Monster im Zaum gehalten. Möge Euch die Königin segnen.', 38134),
(199319, 'deDE', '', 'Die Drust wagen es, Anspruch auf diesen Hain zu erheben! Zeigt keine Gnade.', 38134),
(199318, 'deDE', 'Und ich werde meine verlässlichsten Kontakte in Maldraxxus kontaktieren.', '', 38134),
(199317, 'deDE', '', 'Ich stehe in Eurer Schuld. Ich war fast verloren.', 38134),
(199316, 'deDE', '', 'Mein Seelenband... der Schlund...', 38134),
(199310, 'deDE', 'Mithilfe des Rituals der Absolution können diese Seelen die Lasten ihres sterblichen Lebens loslassen.', 'Mithilfe des Rituals der Absolution können diese Seelen die Lasten ihres sterblichen Lebens loslassen.', 38134),
(199304, 'deDE', 'Gut gemacht! Als Nächstes werden wir... Wartet, ist das etwa...? Zu den Waffen! Die Geißel greift an!', '', 38134),
(199303, 'deDE', '', 'Beeindruckend! Als Nächstes werden wir... Wartet, ist das etwa...? Zu den Waffen! Die Geißel greift an!', 38134),
(199302, 'deDE', 'Die Mutter der Seuche... kommt... Euch holen...', '', 38134),
(199301, 'deDE', 'Wächter der Verwesung, hört meinen Ruf!', '', 38134),
(199298, 'deDE', 'Essenszeit, meine Diener!', '', 38134),
(199296, 'deDE', 'Erhebt euch, Diener der Seuche!', '', 38134),
(199291, 'deDE', 'Willkommen. Wir haben Euch schon erwartet!', '', 38134),
(199288, 'deDE', '', 'Das Wissen, das... wir hätten haben können!', 38134),
(199287, 'deDE', '', 'Wir nehmen uns die Artefakte im Namen des Meisters!', 38134),
(199286, 'deDE', 'Habt Ihr eine Möglichkeit gefunden, mich zu befreien?', '', 38134),
(199285, 'deDE', 'Boss... wird wütend sein...', '', 38134),
(199284, 'deDE', 'Maschine kaputt!', '', 38134),
(199283, 'deDE', 'Kriegt nicht... Kristall...', '', 38134),
(199282, 'deDE', 'Funkelschmetter!', '', 38134),
(199281, 'deDE', 'Ich hab\'s... versucht...', '', 38134),
(199280, 'deDE', 'Knochenleute nerven!', '', 38134),
(199279, 'deDE', 'Oder... ich Bösewicht?', '', 38134),
(199278, 'deDE', 'Ihr Bösewichte!', '', 38134),
(199275, 'deDE', 'Sind alle Maldraxxi so blutrünstig?', '', 38134),
(199274, 'deDE', 'Das ist Fleischnahts Handschrift. Schafft eine Ablenkung, während ich ihn aufspüre!', '', 38134),
(199273, 'deDE', '', 'Ohne Mateos Spiegelsplitter werden wir das Medaillon nicht rechtzeitig erreichen.', 38134),
(199271, 'deDE', 'Ich werde Eure Geheimnisse erfahren!', 'Ich werde Eure Geheimnisse erfahren!', 38134),
(199266, 'deDE', 'Maldraxxus wird unser sein!', 'Maldraxxus wird unser sein!', 38134),
(199265, 'deDE', 'Ey! Boss sagt, ohne die richtigen Fangzähne kommt hier keiner rein.', '', 38134),
(199259, 'deDE', 'Um zu Waffen für den Kerkermeister geschmiedet zu werden.', '', 38134),
(199258, 'deDE', 'Mit \'Sühne\' kann man auch Verbündete heilen. Ein sehr vielseitiger Zauber.', '', 38134),
(199257, 'deDE', '', 'Mit \'Sühne\' kann man auch Verbündete heilen. Ein sehr vielseitiger Zauber.', 38134),
(199246, 'deDE', '', 'Für mich... ist es... zu spät...', 38134),
(199245, 'deDE', '', 'So... dunkel...', 38134),
(199244, 'deDE', 'Danke, ich geh vor! Ich denk ma\', Ihr könnt etwas Hilfe brauchen.', '', 38134),
(199243, 'deDE', 'Das sind die fiesest\'n Fae, die ich je erlebt hab!', '', 38134),
(199242, 'deDE', 'Trotz allem, was Sylvanas getan hat, hätte ich nie gedacht, dass sie sich an so etwas beteiligen würde.', '', 38134),
(199241, 'deDE', '', 'Dankbarkeit erblüht im Sturm. Unsere Pfade treffen sich im Sumpf.', 38134),
(199240, 'deDE', 'So viele Seelen strömen in den Schlund. Die Toten von Azeroth... Draenor... aus Welten jenseits unserer Vorstellungskraft. Alle hierher verdammt.', '', 38134),
(199239, 'deDE', '', 'Üble Magie... ein vertrauter Feind...', 38134),
(199238, 'deDE', 'Die Armee des Kerkermeisters reicht an die der Legion heran... und wird noch größer.', '', 38134),
(199237, 'deDE', '', 'Bleibt bei mir. Sie dürfen uns nicht bemerken.', 38134),
(199229, 'deDE', 'Mit der Rückeroberung unserer heiligen Stätten nehmen die Aufgestiegenen ihr Training wieder auf. Sie hoffen, auch unseren letzten Tempel zurückzuerlangen.', '', 38134),
(199228, 'deDE', '[DEPRECATED] Ya best be turnin\' around, Bwonsamdi. Dis be none of ya concern.', '', 38134),
(199227, 'deDE', 'Das ist das Zeichen von Mueh\'zala. Hmmmmm... Oh nein, nein, nein! Der Boss steckt dahinter?!', '', 38134),
(199226, 'deDE', 'Dank Eurer Bemühungen wird Sündensturz sicher bleiben.', '', 38134),
(199225, 'deDE', 'Der Feind kommt näher! Bemannt die Spiegel! Wer unsere Mauern durchbrechen will, bekommt das Licht zu spüren!', '', 38134),
(199223, 'deDE', 'Steingeborene Schläger sind stark, aber ihre Präzision lässt zu wünschen übrig. Helft ihnen, unsere Verbündeten unten auf den Zinnen zu unterstützen.', '', 38134),
(199222, 'deDE', 'Das Haus Primula steht in Eurer Schuld.', '', 38134),
(199221, 'deDE', 'Jemand hat dem Haus Primula ein wertvolles Steinscheusal gestohlen. Dieser dreiste Diebstahl muss bestraft werden! Findet das Scheusal... und den Täter.', '', 38134),
(199220, 'deDE', 'Eure Leistungen sind nicht unbemerkt geblieben.', '', 38134),
(199219, 'deDE', 'Interessant. In diesem Gebiet wurde ein zerbrochener Spiegel gefunden, der nicht mit unserem Netzwerk verbunden ist. Repariert ihn und seht, wohin er führt.', '', 38134),
(199218, 'deDE', '', 'Dank Eurer Taten wird Revendreth zu seinem wahren Ziel zurückfinden.', 38134),
(199217, 'deDE', '', 'Seelen schwärmen in nie da gewesener Zahl durch die Menagerie. Wir dürfen ihre Anima nicht ungenutzt lassen. Handelt schnell, bevor sie in den Schlund gezogen werden!', 38134),
(199214, 'deDE', 'Das hat noch besser funktioniert als erwartet.', '', 38134),
(199213, 'deDE', 'Verschlinger sind unersättliche Kreaturen, doch wir können sie uns zunutze machen. Zähmt einen und lasst ihn unsere Feinde im Endmorast vernichten.', '', 38134),
(199211, 'deDE', '', 'Diese Zauber müssen nur dann erneuert werden, wenn ihre Effekte auf dem Ziel fast abgelaufen sind.', 38134),
(199210, 'deDE', '', 'Setzt \'Agonie\' und \'Verderbnis\' gegen die Zielattrappe ein.', 38134),
(199209, 'deDE', '', '\'Agonie\' und \'Verderbnis\' quälen Euer Ziel mit regelmäßigem Schattenschaden, während Ihr zusätzliche Zauber wirkt.', 38134),
(199208, 'deDE', '', 'Als nächstes fügt Ihr denen Leid zu, die so dumm sind, Euch anzugreifen.', 38134),
(199207, 'deDE', 'Diese Zauber müssen nur dann erneuert werden, wenn ihre Effekte auf dem Ziel fast abgelaufen sind.', '', 38134),
(199206, 'deDE', 'Setzt \'Agonie\' und \'Verderbnis\' gegen die Zielattrappe ein.', '', 38134),
(199205, 'deDE', '\'Agonie\' und \'Verderbnis\' quälen Euer Ziel mit regelmäßigem Schattenschaden, während Ihr zusätzliche Zauber wirkt.', '', 38134),
(199204, 'deDE', 'Als nächstes fügt Ihr denen Leid zu, die so dumm sind, Euch anzugreifen.', '', 38134),
(199183, 'deDE', 'Die Anklägerin muss uns erlauben, ihn zur Strecke zu bringen!', '', 38134),
(199182, 'deDE', 'Diese Venthyr kollaborieren mit einem Lich?!', '', 38134),
(199181, 'deDE', '', 'Wir brechen sofort nach Maldraxxus auf, $n. Vyraz wird für seine Verbrechen bezahlen.', 38134),
(199180, 'deDE', 'Wir werden später sprechen, mein Sohn. Ich schwöre es.', '', 38134),
(199179, 'deDE', 'Draka! Wir müssen sofort nach Maldraxxus zurück. Markgraf Krexus...', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(199178, 'deDE', 'Nein... dann kommt meine Warnung zu spät! Wir wurden von einem der unseren verraten!', '', 38134),
(199177, 'deDE', '', 'Unser Markgraf wurde getötet. Die Auserwählten brauchen Eure Klinge, jetzt mehr denn je.', 38134),
(199174, 'deDE', 'Ich muss nach Azeroth zurück und unseren Verbündeten davon erzählen, Vater. Wenn es meine Pflicht erlaubt, werde ich wieder an Eurer Seite stehen.', '', 38134),
(199173, 'deDE', 'Hochlord Fordragon! Wir sind dem Schlund entkommen. Danke für Eure Hilfe.', '', 38134),
(199172, 'deDE', '', 'Bringen wir es nun zu Ende.', 38134),
(199171, 'deDE', '', 'Eure Stunde wird noch schlagen. Das hier hat erst begonnen!', 38134),
(199170, 'deDE', '', 'Ich werde Eure Wahrheit kennen!', 38134),
(199169, 'deDE', '', 'Ihr wart dumm, die Macht eines Paragons herauszufordern.', 38134),
(199168, 'deDE', '', 'Unser Reich liegt in Trümmern. Die Verschmähten werden uns befreien!', 38134),
(199167, 'deDE', '', 'Ihr könnt sie nicht mehr retten. Sie hat die Wahrheit erkannt!', 38134),
(199154, 'deDE', '', 'Unser Weg hier heraus... hoffen wir.', 38134),
(199153, 'deDE', 'Er schien unbedeutend. Die Truppen des Kerkermeisters haben ihn ignoriert. Was meint Ihr, ist er?', '', 38134),
(199152, 'deDE', 'Ich habe auf dem hohen Grad einen uralten Steinobelisken gesehen.', '', 38134),
(199151, 'deDE', 'Wir suchen einen Portalstein jenseits des Kessels. Habt Ihr ihn gesehen?', '', 38134),
(199150, 'deDE', 'Verstanden. Geht voran.', '', 38134),
(199149, 'deDE', '', 'Ich wünschte, Ihr könntet Euch länger erholen, aber wir müssen los.', 38134),
(199148, 'deDE', 'Dann gilt Euch mein ewiger Dank, $n.', '', 38134),
(199147, 'deDE', 'Wäre es fast gewesen, wäre nicht dieser Champion.', '', 38134),
(199146, 'deDE', 'Anduin? Ich dachte, das ist mein Ende...', '', 38134),
(199145, 'deDE', 'Baine, hört Ihr mich?', '', 38134),
(199144, 'deDE', 'Ich glaube, er kommt zu sich.', '', 38134),
(199143, 'deDE', '', 'Haltet durch, Baine! Lasst nicht zu, dass der Kerkermeister Euch holt!', 38134),
(199142, 'deDE', 'Ihr müsst es besiegen, schnell!', '', 38134),
(199141, 'deDE', 'Dieser Fluch zerreißt seine Seele!', '', 38134),
(199140, 'deDE', 'Macht Euch auf alles gefasst, wenn ich die Klinge zerbreche.', '', 38134),
(199139, 'deDE', '', 'Aber... Das kann doch nicht sein.', 38134),
(199138, 'deDE', '', 'Was denn?! Ich bin beschäftigt!', 38134),
(199137, 'deDE', 'Aber... der Uralte... hat versprochen...', '', 38134),
(199136, 'deDE', 'Ich nehme all Eure Anima!', '', 38134),
(199135, 'deDE', 'Ich, ein Verräter? An der Anwesenheit $udes Schlundgängers:der Schlundgängerin; kann ich erkennen, dass Ihr der wahre Verräter seid. Kümmert Euch um diese beiden und bringt die Spiegelscherbe zu mir in Archivam.', '', 38134),
(199134, 'deDE', 'Denathrius? Wie konntet Ihr Euch auf seine Seite schlagen, nachdem er die Kuratorin in den Schlund geschleudert hat? Verräter.', '', 38134),
(199133, 'deDE', 'Emil! Wir haben nach Euch gesucht... Der Graf benötigt die Spiegelscherbe, die Euch die Kuratorin gegeben hat.', 'Emil! Wir haben nach Euch gesucht... Der Graf benötigt die Spiegelscherbe, die Euch die Kuratorin gegeben hat.', 38134),
(199130, 'deDE', '', 'Korenth... Wir holen das Herz des Hains zurück. Das schwöre ich.', 38134),
(199127, 'deDE', 'Geht die Stufen hoch, wenn Ihr bereit seid.', '', 38134),
(199126, 'deDE', '', 'Korenth! Wo seid Ihr?', 38134),
(199125, 'deDE', '', 'Ich suche nach Überlebenden. Trefft mich tiefer im Hain.', 38134),
(199121, 'deDE', 'Ich bin Ara\'lon. Ich kämpfe für die Wilde Jagd.\n\nEinst diente ich in einem wunderschönen Hain namens Tirna Noch und nährte die Geister während ihres langen Winters. Doch dieser Hain fiel und ich fand einen neuen Daseinszweck.\n\nIch diene dem Ardenwald.', '', 38134),
(199119, 'deDE', 'Wir kehren so schnell wie möglich zum Hain zurück. Habt vielen Dank.', '', 38134),
(199118, 'deDE', 'Die Fälle wurden überrannt. Ich glaube... wir werden gejagt.', '', 38134),
(199116, 'deDE', 'Gebt Acht. Hier wimmelt es von schrecklichen Kreaturen aller Größenordnungen.', '', 38134),
(199115, 'deDE', 'Hier drüben! Bevor sie Euch sehen!', '', 38134),
(199113, 'deDE', 'Das muss er sein. Baines Blut befleckt die Klinge noch immer.', '', 38134),
(199111, 'deDE', 'Er hat... das Herz...', '', 38134),
(199110, 'deDE', 'Krelnor... maskiert... Schleiermoor...', '', 38134),
(199109, 'deDE', 'Wenn der Kerkermeister kommt, wird er meinen Stahl kosten!', '', 38134),
(199108, 'deDE', 'Endlich, eine echte Kriegerwaffe!', '', 38134),
(199107, 'deDE', 'Besser als nichts, schätze ich. Sehen wir uns weiter um.', '', 38134),
(199106, 'deDE', 'Hmpf. Viel zu unhandlich.', '', 38134),
(199105, 'deDE', 'Zu viel Klinge, zu wenig Heft.', '', 38134),
(199104, 'deDE', 'Eine taugliche Waffe. Allerdings habe ich nie verstanden, was die Allianz an Schwertern findet.', '', 38134),
(199103, 'deDE', 'Suchen wir weiter.', '', 38134),
(199102, 'deDE', 'Stäbe waren nie mein Ding, aber immerhin bricht dieser nicht im Nahkampf.', '', 38134),
(199101, 'deDE', 'Sie hat ein ordentliches Gewicht, aber schauen wir, was wir sonst noch finden.', '', 38134),
(199100, 'deDE', 'Eine Armbrust? Ich sehe meinem Gegner lieber in die Augen.', '', 38134),
(199099, 'deDE', 'Aber Ihr provoziert mich weiter. Nun gut. Dann spürt meinen Zorn!', '', 38134),
(199097, 'deDE', 'Genug! Ich habe mich geduldig zurückgehalten. Ich war sogar höflich.', '', 38134),
(199094, 'deDE', '', 'Ihre Schreie werden durch die Reiche des Todes hallen!', 38134),
(199093, 'deDE', '', 'Ich habe Euch eine unvergleichliche Waffe geschmiedet. Zerbrecht Ihr die Klinge, zerbreche ich Euch.', 38134),
(199092, 'deDE', '', 'Ja! Ja! Ich hacke sie in Stücke!', 38134),
(199088, 'deDE', '', 'Ich besudele Eure Mauern mit ihnen!', 38134),
(199086, 'deDE', '', 'Ich hacke sie in winzigkleine Stücke!', 38134),
(199079, 'deDE', 'Gute Arbeit, Soldat. Sagt Draka, dass wir kampfbereit sind.', '', 38134),
(199076, 'deDE', 'Vertraut mir oder auch nicht. Dafür stehen die Maldraxxi nicht.', '', 38134),
(199072, 'deDE', '', 'Ich höre gute Dinge über Euch, $p. Ruht Euch ruhig hier aus.', 38134),
(199058, 'deDE', '', 'Ich werde weiter üben, während Ihr fort seid.\n\nBis zum nächsten Mal, $gSchlundgänger:Schlundgängerin;.', 38134),
(199053, 'deDE', '', 'Wer seid... Halt, Ihr habt Derawyn und Te\'zan geholfen...', 38134),
(199051, 'deDE', '', 'Kommt. Markgraf Krexus wartet.', 38134),
(199050, 'deDE', '', '<Sie sieht friedlich aus.>', 38134),
(199049, 'deDE', '', 'Dieselbe Arroganz, die Euer Leben ruiniert hat...', 38134),
(199048, 'deDE', '', 'Wir haben Glück. Wir dürfen einen weiteren Tag jagen.', 38134),
(199045, 'deDE', '', '<Eine verletzte Kyrianerin ist hierher gekrochen, um außer Sichtweite zu sterben.$B$BIhre Hände umklammern eine kleine Schachtel.>', 38134),
(199044, 'deDE', 'Ein... unerwartetes... Resultat...', '', 38134),
(199043, 'deDE', 'Willige neue Versuchsobjekte!', '', 38134),
(199040, 'deDE', '', 'Throg wird mit Euch üben. Ich muss mit der Besatzung über den Regen sprechen.', 38134),
(199031, 'deDE', 'Sie... experimentieren mit uns...', '', 38134),
(199030, 'deDE', 'Der Tabulator zählt Leichen wie Münzen.', '', 38134),
(199028, 'deDE', '<Das Gesicht des Sylvar ist ausdruckslos und starrt ins Leere. Er ist einem Hinterhalt zum Opfer gefallen.>', '', 38134),
(199027, 'deDE', 'Diese Magie... Was machen sie mit meinen Geschwistern?!', '', 38134),
(199026, 'deDE', '', '<Das Gesicht der Vorkai ist wutverzerrt. Sie wurde von einer Überzahl überwältigt und fand den Tod.>', 38134),
(199022, 'deDE', 'Euer Tod wird nicht so schnell sein!', '', 38134),
(199020, 'deDE', 'Ihr habt meinen Liebling getötet!', '', 38134),
(199019, 'deDE', 'Mein Schatz! Nein!', '', 38134),
(199018, 'deDE', 'Hecutis, hier! Jetzt!', '', 38134),
(199017, 'deDE', 'Margore, zu mir!', '', 38134),
(199016, 'deDE', 'Bargast, komm!', '', 38134),
(199015, 'deDE', '<Das Gesicht des Vorkai wurde durch dunkle Magie entstellt. Er wurde in eine Falle gelockt.>', '', 38134),
(199013, 'deDE', '', 'Ha! Ihr habt uns geradewegs zu Emil geführt! Seine Spiegelscherbe wird unser sein!', 38134),
(199012, 'deDE', 'Hoffentlich ist dieser wundersame Gegenstand so tödlich, wie er aussieht!', '', 38134),
(199011, 'deDE', 'Ihr da! Hat die Kuratorin Euch geschickt? Kommt, es gibt... Was?!', '', 38134),
(199010, 'deDE', 'Die Vorfreude auf dieses Relikt bringt mich fast um! Aber wahrscheinlich tötet es eher Euch.', '', 38134),
(199009, 'deDE', 'Sehen wir, ob die Legenden über dieses Relikt stimmen!', '', 38134),
(199008, 'deDE', '', 'Meine Jäger und Jägerinnen erstatten immer pünktlich Bericht. Findet sie.', 38134),
(199007, 'deDE', '', 'Ich bin Jagdhauptmann Korayn von der Wilden Jagd. Ich diene der Winterkönigin und beschütze den Ardenwald.\n\nWisset, dass meine Klinge all jenen ein rasches Ende bereitet, die den Wald bedrohen. \n\nOhne Wenn und Aber.', 38134),
(199005, 'deDE', '', 'Niemand entkommt der Wilden Jagd.', 38134),
(199004, 'deDE', 'Diese Seele lässt sich nicht zum Schweigen bringen, ganz gleich, wie fest ich die Ketten ziehe.', '', 38134),
(199003, 'deDE', 'Diese Seele versucht, mit allem, was sie hat, sich meiner Kontrolle zu entwinden.', '', 38134),
(199001, 'deDE', 'Ich diene der Anklägerin.\n\nSprecht mit ihr, wenn Ihr etwas benötigt.', '', 38134),
(198998, 'deDE', '', 'Das ist vergeblich...', 38134),
(198997, 'deDE', '', 'Ihr werdet unsere Armeen nähren!', 38134),
(198993, 'deDE', '', 'In diesem Zustand ist Kael\'thas Sonnenwanderer eine Gefahr für alle.', 38134),
(198991, 'deDE', '', 'Wir lassen nicht zu, dass dieser Hain fällt!\n\nDie Königin hat mich mit der Sicherheit des Winterschlafbeckens und den Geistern darin beauftragt. Wir dürfen nicht versagen.', 38134),
(198990, 'deDE', '', 'Kommt. Das ist der einzige Weg zum Haus der Auserwählten.', 38134),
(198989, 'deDE', 'Wir müssen sie retten! Beeilt Euch!', '', 38134),
(198988, 'deDE', '', 'Markgraf Krexus ist der Stärkste von uns. Zeigt ihm keine Furcht. Nur Respekt.', 38134),
(198985, 'deDE', '', 'Die Auserwählten kämpfen mit Kraft und Ehre. Wir vernichten die Verräter und beenden den Krieg.', 38134),
(198984, 'deDE', 'Eintrag IV:\n\nIch bin mir ziemlich sicher, dass sich Emil in der kleinen Katakombe am Fuße des zerstörten Wachturms befindet. Wenn ich sie nur erreichen könnte...\n\nDer Fortschritt durch die Katakomben ist zum Stillstand gekommen. Die Sündensteinkonstrukte sind eine Gefahr, mit denen es selbst unsere Stärksten kaum aufnehmen können.\n\nIch dachte, es wäre die Erlösung, als wir die verhüllten Späher aus Archivam gesehen haben... aber sie haben ihre Klingen gegen uns gezogen, gegen ihresgleichen. Die Gründe dieses Verrats kenne ich nicht. Ich kann nur hoffen, dass sie der Jagd überdrüssig werden, bevor sie mich finden.', '', 38134),
(198983, 'deDE', '', 'Seid gegrüßt. Lady Mondbeere hat nur Lob für Euch.', 38134),
(198982, 'deDE', '', 'Niemals! Eure Meister werden für ihre Taten büßen!', 38134),
(198981, 'deDE', 'Folgt uns, Niya. Tragt die Maske, dient den Meistern!', '', 38134),
(198978, 'deDE', '', 'Moment... dieser Trank... Marileth?', 38134),
(198958, 'deDE', 'Ich diene nicht Euch, ich werde nur dem Ruf der Gräfin Beachtung schenken.', 'Ich diene nicht Euch, ich werde nur dem Ruf der Gräfin Beachtung schenken.', 38134),
(198957, 'deDE', 'Belastet Euch nicht mit Dingen, die Ihr nicht ansatzweise begreifen könnt.', 'Belastet Euch nicht mit Dingen, die Ihr nicht ansatzweise begreifen könnt.', 38134),
(198952, 'deDE', 'Als ich in Revendreth ankam, bestand meine Buße darin, der Gräfin und den Adligen ihres Hofs zu dienen. Im Laufe der Jahrtausende verwandelte sich meine Buße in ein Verlangen. Nun bleibe ich als persönlicher Diener und Vertrauter der Gräfin freiwillig hier. Ich kümmere mich um alles, was sie braucht, und schließe ihre dunkelsten Geheimnisse weg.\n\nUm Euretwillen hoffe ich, dass ich kein weiteres Schloss benötige.', 'Als ich in Revendreth ankam, bestand meine Buße darin, der Gräfin und den Adligen ihres Hofs zu dienen. Im Laufe der Jahrtausende verwandelte sich meine Buße in ein Verlangen. Nun bleibe ich als persönlicher Diener und Vertrauter der Gräfin freiwillig hier. Ich kümmere mich um alles, was sie braucht, und schließe ihre dunkelsten Geheimnisse weg.\n\nUm Euretwillen hoffe ich, dass ich kein weiteres Schloss benötige.', 38134),
(198951, 'deDE', 'Ich sichere draußen eine Stellung!', '', 38134),
(198950, 'deDE', '', 'Jetzt stehen die fünf Häuser im Krieg. Zwei sind gefallen. Die Auserwählten dürfen nicht folgen.', 38134),
(198949, 'deDE', '', 'Oh gut, Verstärkung. Moment, Ihr seid kein Venthyr.', 38134),
(198948, 'deDE', '', 'Wir haben geschworen, die Schattenlande zu verteidigen. Doch unser Anführer, der Primus, ist verschwunden.', 38134),
(198947, 'deDE', '', 'Wenn Ihr tiefer in die Katakomben vorstoßt, könnten wir Eure Hilfe brauchen.', 38134),
(198946, 'deDE', 'Ich vielleicht schon. Weit weg von hier...', '', 38134),
(198945, 'deDE', '', 'Der Kontakt zu Emil ist abgebrochen, als das Chaos ausbrach. Wir haben Gardist Dorin vorausgeschickt, um nach ihm zu suchen. Er ist noch nicht zurück.', 38134),
(198944, 'deDE', 'Seine Wunden heilen, aber sein Geist bleibt geschwächt. So etwas habe ich noch nie gesehen.', '', 38134),
(198935, 'deDE', '', 'Hmm. Was auch immer seine Absicht war, ohne Heft taugt sie nichts. Überlasst das mir.', 38134),
(198933, 'deDE', 'Seid Ihr die Verstärkung, die wir angefordert haben? Wir haben nichts mehr von unseren Spähern gehört.', 'Seid Ihr die Verstärkung, die wir angefordert haben? Wir haben nichts mehr von unseren Spähern gehört.', 38134),
(198932, 'deDE', '', 'Das war also der Grund für diesen Aufruhr. Eine Klinge und eine Nachricht vom Primus persönlich...', 38134),
(198931, 'deDE', '', 'Reitet den Hirsch. Eilt zum Herzholzhain. Ein Sturm zieht auf.', 38134),
(198912, 'deDE', '', 'Klagende Winde dringen aus dem Herzholzhain... der Wald erschauert...', 38134),
(198911, 'deDE', 'Ich muss sagen, ich bewundere die unerschütterliche Überzeugung der Gräfin, Ihr nicht auch?', 'Ich muss sagen, ich bewundere die unerschütterliche Überzeugung der Gräfin, Ihr nicht auch?', 38134),
(198910, 'deDE', '$gSchlundgänger:Schlundgängerin;? Ihr seid wahrlich eigenartig.', '$gSchlundgänger:Schlundgängerin;? Ihr seid wahrlich eigenartig.', 38134),
(198908, 'deDE', '<Mustert Euch ohne ein Wort des Grußes von Kopf bis Fuß.>', '<Mustert Euch ohne ein Wort des Grußes von Kopf bis Fuß.>', 38134),
(198907, 'deDE', 'Ich an Eurer Stelle wäre vorsichtig, $gSchlundgänger:Schlundgängerin;. Die Gräfin kennt Eure tiefsten Sehnsüchte und ist immer auf der Suche nach der nächsten Ernte.', 'Ich an Eurer Stelle wäre vorsichtig, $gSchlundgänger:Schlundgängerin;. Die Gräfin kennt Eure tiefsten Sehnsüchte und ist immer auf der Suche nach der nächsten Ernte.', 38134),
(198904, 'deDE', 'Wen man hier nicht so alles trifft, $pr, der Gräfin war wohl nach Wohltätigkeit.', 'Wen man hier nicht so alles trifft, $pr, der Gräfin war wohl nach Wohltätigkeit.', 38134),
(198903, 'deDE', 'Jetzt ist nicht der richtige Zeitpunkt, mich mit Euren belanglosen Problemen zu belästigen, $C.', 'Jetzt ist nicht der richtige Zeitpunkt, mich mit Euren belanglosen Problemen zu belästigen, $C.', 38134),
(198902, 'deDE', 'Stimmt es, was man sich über Schlundgänger erzählt?', 'Stimmt es, was man sich über Schlundgänger erzählt?', 38134),
(198901, 'deDE', '', 'Eure Augen verraten mir, dass etwas nicht stimmt. Wie kann ich Euch helfen?', 38134),
(198899, 'deDE', 'Die Gräfin kann in ihr Reich natürlich einladen, wen sie möchte, aber jemanden aus diesem Volk, wirklich?', 'Die Gräfin kann in ihr Reich natürlich einladen, wen sie möchte, aber jemanden aus diesem Volk, wirklich?', 38134),
(198893, 'deDE', 'Generalin Kaal, in Deckung! Meine Soldaten schlagen aus dem Himmel zu!', '', 38134),
(198892, 'deDE', '', 'Grashaal, schützt Euch! Gleich regnet es Schrecken von oben!', 38134),
(198891, 'deDE', '', 'Maldraxxus ist die Heimat derjenigen, die für den Kampf gelebt haben. Hier wird Stärke geschätzt und belohnt.', 38134),
(198890, 'deDE', '', 'Wir werden überrannt! Ich bringe uns hier weg!', 38134),
(198881, 'deDE', 'Ich hoffe, Ihr habt Euch an diesem Austausch erfreut.', '', 38134),
(198876, 'deDE', 'Eure Treffsicherheit ist beeindruckend. Das wird sie lehren, sich vom Ardenwald fernzuhalten.', '', 38134),
(198875, 'deDE', 'Verschlinger dringen weiter in den Wald ein. Tötet sie und verwendet ihre Anima, um die Risse zu versiegeln, bevor sie uns überrennen!', '', 38134),
(198874, 'deDE', 'Wollt Ihr üben?', '', 38134),
(198871, 'deDE', 'Die Wirbel drehen und verändern sich rasend schnell, sodass Ihr nur einen kurzen Blick auf wiedererkennbare Orte in den Schattenlanden erhaschen könnt. Indem Ihr das Portal betretet, könnt Ihr sofort an einen Punkt einer gewünschten Gegend reisen, wobei dies auch Gefahren bergen könnte.', 'Die Wirbel drehen und verändern sich rasend schnell, sodass Ihr nur einen kurzen Blick auf wiedererkennbare Orte in den Schattenlanden erhaschen könnt. Indem Ihr das Portal betretet, könnt Ihr sofort an einen Punkt einer gewünschten Gegend reisen, wobei dies auch Gefahren bergen könnte.', 38134),
(198868, 'deDE', '', 'Ich habe die Hälften meinen Untergebenen anvertraut. Wir müssen zu ihnen, bevor Denathrius\' Truppen sie finden.', 38134),
(198867, 'deDE', '', 'Ich habe mein Medaillon durch diesen Spiegel versteckt, bevor Denathrius mich in den Schlund warf. Ich habe das Glas zerbrochen, um es ihm zu verwehren.', 38134),
(198865, 'deDE', '', 'Die Katakomben der Reue wurden ins Chaos gestürzt und Denathrius\' Streitkräfte haben Archivam besetzt.\n\nGlücklicherweise konnten wir die Gruft der Vergessenen mit der Hilfe einiger Venthyr aus der Gruft der Absolution etwas stabilisieren. Aber die Überbleibsel der Zerstörung verbleiben.\n\nPrinz Renathal hat mir die Aufgabe übertragen, das Medaillon der Habsucht zurückzuholen. Doch ich bin für feindliche Auseinandersetzungen nicht gewappnet. Ich brauche Eure Hilfe, $gSchlundgänger:Schlundgängerin;.', 38134),
(198864, 'deDE', '', 'Es ist schlimmer als befürchtet, Schlundgänger. Meine Abwesenheit hat den Gruftdistrikt ins Chaos gestürzt. Es gibt viel zu tun.', 38134),
(198863, 'deDE', 'Ich vermisse dich so.\n\nIm Traum werden wir zusammen sein... eines Tages.', '', 38134),
(198861, 'deDE', '', 'Wieder muss ich kämpfen... und sterben. Wann endet dieser Alptraum endlich?', 38134),
(198860, 'deDE', 'Gut gekämpft, Mevix!', '', 38134),
(198859, 'deDE', 'Ha! Dieser Fremdling ist hundert Soldaten wert! Geht und holt uns den Sieg!', '', 38134),
(198858, 'deDE', 'Ein direkter Angriff wird Gharmal kalt erwischen und seine Armee mit einem Schlag vernichten!', '', 38134),
(198857, 'deDE', 'Aber wir brauchen Verstärkung!', '', 38134),
(198856, 'deDE', 'Das ist unsere Chance, einen Schlag gegen das Haus der Konstrukte zu führen!', '', 38134),
(198854, 'deDE', 'Die Vorsehung leitet sie genauso wie uns. Ihr müsst Vertrauen haben, mein Freund.', 'Die Vorsehung leitet sie genauso wie uns. Ihr müsst Vertrauen haben, mein Freund.', 38134),
(198853, 'deDE', 'Ich hoffe, das ist keine trügerische Hoffnung. Können wir diesen Fremden vertrauen?', 'Ich hoffe, das ist keine trügerische Hoffnung. Können wir diesen Fremden vertrauen?', 38134),
(198852, 'deDE', 'Ich glaube, es zeigt die grenzenlose Kreativität der Ersten, die alles geschaffen und uns mit der Vorsehung gesegnet haben.', 'Ich glaube, es zeigt die grenzenlose Kreativität der Ersten, die alles geschaffen und uns mit der Vorsehung gesegnet haben.', 38134),
(198851, 'deDE', 'Die unzähligen Formen, die diese Fremden annehmen, faszinieren mich.', 'Die unzähligen Formen, die diese Fremden annehmen, faszinieren mich.', 38134),
(198850, 'deDE', 'Wir müssen nur darauf vertrauen, dass die Vorsehung diejenigen von uns leiten wird, die mit ihnen zusammenarbeiten müssen.', 'Wir müssen nur darauf vertrauen, dass die Vorsehung diejenigen von uns leiten wird, die mit ihnen zusammenarbeiten müssen.', 38134),
(198849, 'deDE', 'Ich bin nur dankbar, dass ich mich nicht direkt mit ihnen befassen muss.', 'Ich bin nur dankbar, dass ich mich nicht direkt mit ihnen befassen muss.', 38134),
(198848, 'deDE', 'Ganz meine Meinung!', 'Ganz meine Meinung!', 38134),
(198847, 'deDE', 'Ich wünschte, die Aufseher würden strenger mit den Mittlern umgehen. Das Chaos, das sie mit sich bringen, irritiert mich.', 'Ich wünschte, die Aufseher würden strenger mit den Mittlern umgehen. Das Chaos, das sie mit sich bringen, irritiert mich.', 38134),
(198846, 'deDE', 'Keine Sorge, mein Freund. Die Vorsehung wird uns durch dieses Chaos führen.', 'Keine Sorge, mein Freund. Die Vorsehung wird uns durch dieses Chaos führen.', 38134),
(198845, 'deDE', 'Dann habt Ihr mehr Vertrauen als ich. Ich kann meine Zweifel eingestehen, die, ehrlich gesagt, auch nach der Ankunft dieser Fremden immer noch bestehen.', 'Dann habt Ihr mehr Vertrauen als ich. Ich kann meine Zweifel eingestehen, die, ehrlich gesagt, auch nach der Ankunft dieser Fremden immer noch bestehen.', 38134),
(198844, 'deDE', 'Ich habe den Glauben nie verloren. Ich will ja nicht behaupten, dass meine Ergebenheit stärker ist als Eure, aber ich habe nie an der Vorsehung gezweifelt.', 'Ich habe den Glauben nie verloren. Ich will ja nicht behaupten, dass meine Ergebenheit stärker ist als Eure, aber ich habe nie an der Vorsehung gezweifelt.', 38134),
(198843, 'deDE', 'Ich muss zugeben, die jüngsten Ereignisse haben meinen Glauben wiederhergestellt. Die Vorsehung schützt wirklich.', 'Ich muss zugeben, die jüngsten Ereignisse haben meinen Glauben wiederhergestellt. Die Vorsehung schützt wirklich.', 38134),
(198842, 'deDE', 'In der Tat. Die Vorsehung leitet die Schöpfung mit dem Blick eines Handwerksmeisters. Die reine Funktion ist eine unzureichende Darstellung ihrer Handwerkskunst.', 'In der Tat. Die Vorsehung leitet die Schöpfung mit dem Blick eines Handwerksmeisters. Die reine Funktion ist eine unzureichende Darstellung ihrer Handwerkskunst.', 38134),
(198841, 'deDE', 'Solche Schönheit kann kein reiner Zufall sein. Sicherlich ist dies das Werk der Ersten.', 'Solche Schönheit kann kein reiner Zufall sein. Sicherlich ist dies das Werk der Ersten.', 38134),
(198840, 'deDE', 'Wahre Worte, mein Freund. Die Vorsehung ist an der gesamten Schöpfung beteiligt.', 'Wahre Worte, mein Freund. Die Vorsehung ist an der gesamten Schöpfung beteiligt.', 38134),
(198839, 'deDE', 'Dieser Anblick erinnert mich immer wieder an die Herrlichkeit der Vorsehung.', 'Dieser Anblick erinnert mich immer wieder an die Herrlichkeit der Vorsehung.', 38134),
(198838, 'deDE', 'Das habe ich auch gehört. Die Vorsehung leitet uns auch jetzt noch. Ein Licht in der gegenwärtigen Dunkelheit.', 'Das habe ich auch gehört. Die Vorsehung leitet uns auch jetzt noch. Ein Licht in der gegenwärtigen Dunkelheit.', 38134),
(198837, 'deDE', 'Wir machen sie nieder!', '', 38134),
(198836, 'deDE', 'Es heißt, die Richterin habe sich geregt!', 'Es heißt, die Richterin habe sich geregt!', 38134),
(198835, 'deDE', 'Die Vorsehung leitet sie genauso wie uns, dessen bin ich mir sicher.', 'Die Vorsehung leitet sie genauso wie uns, dessen bin ich mir sicher.', 38134),
(198834, 'deDE', 'Die Tore öffnen sich wieder. Das sind endlich gute Nachrichten. Ich hoffe, dass man diesen Fremden vertrauen kann.', 'Die Tore öffnen sich wieder. Das sind endlich gute Nachrichten. Ich hoffe, dass man diesen Fremden vertrauen kann.', 38134),
(198833, 'deDE', 'Ich für meinen Teil zweifle nicht an der Vorsehung, auch wenn ihre Wege unergründlich sind.', 'Ich für meinen Teil zweifle nicht an der Vorsehung, auch wenn ihre Wege unergründlich sind.', 38134),
(198832, 'deDE', 'Dieses Chaos kann doch gewiss nicht der wahre Wille der Vorsehung sein?', 'Dieses Chaos kann doch gewiss nicht der wahre Wille der Vorsehung sein?', 38134),
(198830, 'deDE', '', 'Keine Zeit für Worte. Wir müssen los!', 38134),
(198829, 'deDE', '', 'Ich bin Baronin Draka. Das Haus der Auserwählten steht Euch bei.', 38134),
(198828, 'deDE', 'Der Schlüssel ehrt die Würdigen.', '', 38134),
(198827, 'deDE', 'Verrat stiftet Chaos. Brüchige Bande müssen gestärkt werden.', '', 38134),
(198826, 'deDE', 'Die Reiche des Todes ertrinken in Dunkelheit.', '', 38134),
(198825, 'deDE', 'Lasst keinen am Leben!', '', 38134),
(198824, 'deDE', '', 'Tashmur, fangt!', 38134),
(198823, 'deDE', '', 'Ich alarmiere die Wilde Jagd. Wir brauchen scheinbar erneut Eure Hilfe!', 38134),
(198822, 'deDE', '', 'Seht! All diese Fae fliehen aus dem Herzholzhain... Etwas stimmt nicht!', 38134),
(198821, 'deDE', '', 'So! Nachdem die Schuld beglichen- Was ist das?', 38134),
(198820, 'deDE', '', 'Nehmt stattdessen dieses Geschenk. Ein Teil der Magie aus dem Ardenwald, ganz zu Euren Diensten.', 38134),
(198818, 'deDE', '', 'Wir brauchen Eure Jagdfähigkeiten. Alles hängt davon ab, eine schwer fassbare Beute aufzuspüren.', 38134),
(198817, 'deDE', 'Wir werden eine Schneise durch die Verteidigung des Meisters schlagen.\n\nLasst mich wissen, wenn Ihr für das Training bereit seid.', '', 38134),
(198816, 'deDE', 'Er erwartet Euch am Frostthron. Die Anführer der Allianz und der Horde sind dort mit ihm versammelt.', '', 38134),
(198815, 'deDE', 'Doch den Helm zu zerschmettern, hat auch Bolvar Fordragon von der Last befreit, der Lichkönig zu sein.', '', 38134),
(198814, 'deDE', 'Blickt zum Himmel. Dort seht Ihr das Ergebnis ihrer Taten. Ich... habe so etwas noch nie gesehen.', '', 38134),
(198813, 'deDE', 'Bevor ihre Diener die Anführer entführt haben, griff die Bansheekönigin Eiskrone an. Sie hat den Frostthron eingenommen und den Helm der Herrschaft an sich gerissen.', '', 38134),
(198811, 'deDE', 'Gefügig gemacht!', '', 38134),
(198810, 'deDE', 'Ihr seid gebrochen!', '', 38134),
(198809, 'deDE', 'Ihr wart mir nie gewachsen!', '', 38134),
(198808, 'deDE', 'Dachtet Ihr, Ihr könnt siegen?', '', 38134),
(198807, 'deDE', 'Remornia! Das Gewürm muss sterben.', '', 38134),
(198806, 'deDE', 'Eure Eitelkeit kostet Euch alles.', '', 38134),
(198805, 'deDE', 'Widersetzt Euch weiter, und Ihr leidet bis in alle Ewigkeit!', '', 38134),
(198804, 'deDE', 'Kabale! Verschlingt diese Narren.', '', 38134),
(198798, 'deDE', '', 'Huln Hochberg! Es ist lange her, dass ich Euch im Kampf erlebt habe.', 38134),
(198789, 'deDE', '', 'Die Vasallen des Meisters werden gar nicht wissen, wie ihnen geschieht.\n\nLasst mich wissen, wenn Ihr für das Training bereit seid.', 38134),
(198786, 'deDE', '', 'Eine Fee erwidert einen Gefallen immer. Ihr habt viel für den Wald getan, aber ich konnte Eure Nachricht nicht überbringen.', 38134),
(198778, 'deDE', 'Ihr werdet mich nicht auffliegen lassen!', 'Ihr werdet mich nicht auffliegen lassen!', 38134),
(198775, 'deDE', 'Willkommen zurück! Habt Ihr gefunden, was Ihr sucht?', '', 38134),
(198774, 'deDE', 'Ah, Ihr seid gerade rechtzeitig angekommen.', '', 38134),
(198773, 'deDE', 'Ich habe den perfekten Zauber vorbereitet, mit dem Ihr unentdeckt bleibt. Haltet nur kurz still...', '', 38134),
(198772, 'deDE', 'Nehmt Euch von den Gorm, was Ihr braucht. Dann setzen wir dem Weltenfresser ein Ende.', '', 38134),
(198770, 'deDE', '<Nelwyn grinst Euch an.>', '<Nelwyn grinst Euch an.>', 38134),
(198769, 'deDE', '', 'Oh, bitte helft uns, $gtapferer Held:tapfere Heldin;!', 38134),
(198768, 'deDE', 'Es ist und war mir immer eine Ehre, Revendreth zu dienen.', '', 38134),
(198752, 'deDE', 'Ich bitte um Eure Aufmerksamkeit! Ich erhebe mein Glas auf einen der Unsrigen, den getreuen Grufthüter Kassir!', '', 38134),
(198728, 'deDE', 'Mevix! Stiftet Chaos unter diesen Feiglingen!', '', 38134),
(198727, 'deDE', 'Ich danke Euch. Als Zeichen meiner Dankbarkeit werde ich mein Glas auf einen Eurer Gäste erheben.', '', 38134),
(198726, 'deDE', 'Schnell! Fangt alle fünf Teile meines Teeservice, bevor das Steinscheusal alle zerbricht!', '', 38134),
(198724, 'deDE', '', 'Jetzt spielen wir ein echtes Spiel!', 38134),
(198722, 'deDE', 'Hecutis, zerschmettere die Knochen!', '', 38134),
(198721, 'deDE', 'Bargast, zerreiße ihre Seelen!', '', 38134),
(198720, 'deDE', 'Margore, lass sie bluten!', '', 38134),
(198717, 'deDE', '', 'Ihr hättet auf dem Weg bleiben sollen!', 38134),
(198716, 'deDE', 'Lasst die Hunde los!', '', 38134),
(198715, 'deDE', 'Ich flehe Euch an, seid vorsichtig! Ihr lasst überall Teile fallen!', '', 38134),
(198714, 'deDE', 'Nein! Das ist mein bestes Teeservice!', '', 38134),
(198713, 'deDE', 'Auf diesem Bolzen steht Euer Name.', '', 38134),
(198712, 'deDE', 'Ich habe genug Bolzen für alle!', '', 38134),
(198711, 'deDE', 'Nichts entgeht mir.', '', 38134),
(198710, 'deDE', 'Ich kann Eure Angst riechen.', '', 38134),
(198709, 'deDE', 'Aber... das wird verheerende Verluste mit sich bringen.', '', 38134),
(198708, 'deDE', 'Das ist der Preis des Sieges!', '', 38134),
(198707, 'deDE', 'Vorwärts! Durchbrecht ihre Reihen mit einem Frontalangriff!', '', 38134),
(198705, 'deDE', 'Narren! Ich bin Revendreth!', '', 38134),
(198698, 'deDE', 'Remornia! Schnitzt Schreie aus ihrem Fleisch!', '', 38134),
(198697, 'deDE', 'Mit Eurer Unterstützung werden die Truppen von Revendreth zunehmend stärker und zahlreicher.', '', 38134),
(198696, 'deDE', 'Remornia! Massakriert diese Welpen!', '', 38134),
(198695, 'deDE', 'Dunkelschwingentruppen wurden für Übungsmanöver in dieses Gebiet entsandt. Tut, was Ihr könnt, um unsere Truppen kampfbereit zu machen!', '', 38134),
(198694, 'deDE', 'Mögen die Schatten Euch verschlingen!', '', 38134),
(198693, 'deDE', 'Ihr werdet gemeinsam sterben!', '', 38134),
(198692, 'deDE', 'Schreit für mich!', '', 38134),
(198691, 'deDE', 'Ich werde Euch niederstrecken!', '', 38134),
(198689, 'deDE', 'Treue Apostel! Beseitigt den Abschaum.', '', 38134),
(198687, 'deDE', 'Diener! Schafft mir den Unrat aus den Augen.', '', 38134),
(198684, 'deDE', 'Kriecht auf Knien zu mir. Ergebt Euch, und Euer Tod wird gnädig sein.', '', 38134),
(198683, 'deDE', 'Ihr müsst lernen, wo Euer Platz ist, Sterbliche.', '', 38134),
(198682, 'deDE', 'Remornia! Weg mit dieser elenden Plage.', '', 38134),
(198677, 'deDE', 'Eure Überheblichkeit kommt Euch teuer zu stehen!', '', 38134),
(198676, 'deDE', 'Hütet Euch, Jägerin! Dieser Weg ist nicht mehr sicher.', '', 38134),
(198675, 'deDE', '', 'Nichts entgeht der Wilden Jagd. Einer von ihnen muss uns bei unserer Suche helfen können.', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198673, 'deDE', 'Diese feigen Verräter fordern töricht die Macht der Auserwählten heraus!', '', 38134),
(198670, 'deDE', 'Sprecht lauter, Rekrut!', '', 38134),
(198669, 'deDE', 'Schmerz reinigt die Seele.', '', 38134),
(198668, 'deDE', 'Gesteht Eure Sünden, bevor sie Euch verschlingen!', '', 38134),
(198665, 'deDE', '', 'Lasst Vyraz nicht warten. Seine Geduld ist sehr begrenzt.', 38134),
(198664, 'deDE', '', 'Schnell, spielt die Harfe, um die Seidenschreiter in Sicherheit zu bringen!', 38134),
(198653, 'deDE', '<Diese Sylvar ist unnatürlich blass und atmet kaum... Vielleicht ist sie bereits tot.\n\nIhr blinzelt und ein weit aufgerissenes Auge starrt Euch an.\n\nDer Mund öffnet sich zu einem klaffenden Maul.>', '<Diese Sylvar ist unnatürlich blass und atmet kaum... Vielleicht ist sie bereits tot.\n\nIhr blinzelt und ein weit aufgerissenes Auge starrt Euch an.\n\nDer Mund öffnet sich zu einem klaffenden Maul.>', 38134),
(198651, 'deDE', '', 'Ich danke Euch! Es ist schwer, jemanden zu finden, dem man vertrauen kann.', 38134),
(198649, 'deDE', 'Meine Großmutter ist so krank!\n\nWir haben versucht, nach Tirna Vaal zu kommen. Die Dromanin könnte uns vielleicht helfen, aber sie ist so schwach und der Wald so gefährlich!\n\nBitte, helft Ihr mir? Meine Großmutter versteckt sich in einer Höhle in der Nähe. Sie ist so krank...', 'Meine Großmutter ist so krank!\n\nWir haben versucht, nach Tirna Vaal zu kommen. Die Dromanin könnte uns vielleicht helfen, aber sie ist so schwach und der Wald so gefährlich!\n\nBitte, helft Ihr mir? Meine Großmutter versteckt sich in einer Höhle in der Nähe. Sie ist so krank...', 38134),
(198648, 'deDE', '', 'Habt vielen Dank! Ihr wart mir und den Seelen in meiner Obhut eine große Hilfe.\n\nDenkt daran, auf der Straße zu bleiben. Der Wald wimmelt von gefährlichen Kreaturen!\n\nHabt erneut vielen Dank!', 38134),
(198645, 'deDE', '', 'Die Nacht umarme Euch. Wir stehen in Eurer Schuld!', 38134),
(198642, 'deDE', '', 'Bitte rettet die armen Seelen!', 38134),
(198616, 'deDE', 'Meine Teile... müssen... gezählt werden...', '', 38134),
(198615, 'deDE', 'Noch eine Leiche zum Einsammeln!', '', 38134),
(198614, 'deDE', '', 'Gut. Die Treiber werden dafür sorgen, dass die Gormlinge friedlich bleiben.', 38134),
(198613, 'deDE', '', 'Ein Haufen Gormlinge hat die Metamorphose zu Matriarchinnen begonnen. Bringt sie zu unseren Treibern, bevor es zu spät ist.', 38134),
(198612, 'deDE', '', 'Matyas\' Tagebuch war sehr informativ. Wir müssen listig sein, um ihm meinen Sündenstein zu entreißen. Ich weiß, wie wir ihn außer Gefecht setzen... Wir brauchen nur die richtigen Zutaten.', 38134),
(198607, 'deDE', 'Ich will Euch nicht zu nahe treten, aber Ihr wirkt wie jemand, dem oft Merkwürdiges passiert.', '', 38134),
(198606, 'deDE', 'Sie sind die Vorsehung des Ardenwalds! Wildsamen enthalten schlummernde Naturgeister, wie die Älteste Dunmira. Sie ist unter diesem Vogel. Wenn Ihr ihn für mich vom Nest weglocken könntet, könnte ich mich reinschleichen und mir sie schnappen!\n\nIch bin froh, dass Mondbeere jemanden schicken konnte, wenn sie schon selbst nicht kommen konnte. Ihr wisst wohl, dass sie immer einen großen Aufstand macht, wenn man sie um Hilfe bittet?\n\n<Korenth kichert.>\n\nIhr werdet mir doch helfen, den Vogel aus dem Nest zu locken?\n\n<Korenth lächelt hoffnungsvoll.>', '', 38134),
(198590, 'deDE', 'Hm, das ist nicht gut... Der Geist hat weiter Alpträume. Was ist nur mit ihm geschehen?', '', 38134),
(198589, 'deDE', 'Moment... der Geist... bindet sich an Euch! Das ist das erste Mal seit... seit ich mich erinnern kann!', '', 38134),
(198573, 'deDE', 'Ein wenig Anima sollte ihn beruhigen. Sobald er ruhiger träumt, können wir...', '', 38134),
(198569, 'deDE', 'Oh, oh... Der Geist in dem Wildsamen hat einen Alptraum. Das arme Ding muss ein gewaltsames Ende erlebt haben...', '', 38134),
(198567, 'deDE', 'Ihr sollt Loksey für die Furchthetzerin zum Schwundgrufthügel bringen. Sie ist schon aufgebrochen.', '', 38134),
(198565, 'deDE', 'Wartet, ich erkenne es... es war mit uns an Bord! Es muss den Schiffbruch überlebt haben.', '', 38134),
(198564, 'deDE', 'Erwischt!', '', 38134),
(198563, 'deDE', 'Seht, da kommt es!', '', 38134),
(198561, 'deDE', 'Das war eine außergewöhnliche Ehre, Prinz Renathal. Habt Dank.', '', 38134),
(198560, 'deDE', 'Nein! Wir wollen noch nicht gehen! Es is toll hier!', '', 38134),
(198559, 'deDE', 'Mein Bericht an die Archon wird höchst zufriedenstellend ausfallen.', '', 38134),
(198558, 'deDE', '', 'Der Hof hat dem Tratsch alle Ehre gemacht. Ich freue mich schon auf den nächsten.', 38134),
(198557, 'deDE', 'Viel gelernt. Werde über heute nachdenken. Kluge Leute...', '', 38134),
(198556, 'deDE', '', 'Ich habe mich wirklich prächtig amüsiert!', 38134),
(198555, 'deDE', '', 'Ihr habt beeindruckend Hof gehalten, Prinz. Vielleicht seid Ihr doch kein hoffnungsloser Fall.', 38134),
(198551, 'deDE', 'Ist das nicht ein toller kleiner Wildsamen? Es wird noch viele Jahre dauern, bis er zu seiner Ebene zurückkehrt.\n\nAlso, nur zu, nehmt die gefundenen Animazapfen und verseht ihn mit Anima.', '', 38134),
(198550, 'deDE', 'Diese Spriggans bedeuten nichts als Ärger!', 'Diese Spriggans bedeuten nichts als Ärger!', 38134),
(198549, 'deDE', 'Welkt und verfallt!', '', 38134),
(198546, 'deDE', 'Der Sieg verlangt Opfer!', '', 38134),
(198545, 'deDE', 'Meine Soldaten erheben sich erneut!', '', 38134),
(198542, 'deDE', '', 'Ihr verwehrt mir... den Himmel?! Dann... antworte ich genauso!', 38134),
(198534, 'deDE', 'Alles dank Eurer wertvollen Hilfe!', '', 38134),
(198533, 'deDE', '', 'Der Wind hebt Eure Schwingen aufs Neue, Traumweber.', 38134),
(198532, 'deDE', 'Nadjia, Ihr seid wieder bei Euch! Wir wollten herausfinden, warum der Zuchtmeister $udiesen Sterblichen:diese Sterbliche; gerufen hat. Wir haben sein persönliches Tagebuch gefunden.', '', 38134),
(198531, 'deDE', '', 'Argh. Meine Augen fühlen sich an, als wären sie durchbohrt worden. Seid Ihr das, $gSchlundgänger:Schlundgängerin;?', 38134),
(198529, 'deDE', '', 'Tut mir leid, ich musste unterwegs Halt machen.', 38134),
(198527, 'deDE', 'Fangen wir mit einem jüngeren an. Er ist kurz vor der Dürre eingetroffen und braucht etwas mehr Aufmerksamkeit.', '', 38134),
(198520, 'deDE', 'Ihr habt den Baron gehört! Vorwärts!', '', 38134),
(198519, 'deDE', 'Kommt und lernt, wie man Wildsamen pflegt und ihre Geister träumen sieht.', '', 38134),
(198517, 'deDE', 'Wir alle wurden schon mal gefressen. Kann passieren.', '', 38134),
(198510, 'deDE', '', 'Seht her, $n. Lähmt eine Seele durch Angst, damit ihre Anima frei fließt.', 38134),
(198509, 'deDE', 'Wir greifen an, wenn Ihr ins Horn stoßt. Für die Auserwählten!', '', 38134),
(198505, 'deDE', '', '<Nadjia kann kaum sprechen.>', 38134),
(198500, 'deDE', 'Wir werden Denathrius\' gestohlene Anima gegen ihn verwenden.', '', 38134),
(198499, 'deDE', 'Schlundgänger! Kommt, sammelt möglichst viel von dieser Anima ein. Diese Art Bindung währt nicht ewig, sollte Euch aber für die kommende Schlacht stärken.', '', 38134),
(198496, 'deDE', '', 'Argh. Spriggans.', 38134),
(198495, 'deDE', '', 'Ich erkenne nur den Beginn einer neuen Zukunft. Eine Zukunft, die keinen Platz für Verräter wie Euch hat!', 38134),
(198494, 'deDE', '', 'Denathrius hat Revendreth verraten, indem er diese Anima in den Schlund entfesselt hat. Das erkennt sicher auch Ihr Speichellecker!', 38134),
(198493, 'deDE', '', 'Anklägerin. Ihr konntet mir entwischen, und nun kehrt Ihr dreist mit dieser sterblichen Lumpenbande in diese Hallen zurück. Armselig.', 38134),
(198492, 'deDE', '', 'Die Jungtiere wurden alle gefunden, und die Spriggans fliehen. Danke für Eure Hilfe, $n!', 38134),
(198490, 'deDE', '', 'Spriggans terrorisieren unsere Seidenschreiter! Bitte macht diesem Unsinn ein Ende!', 38134),
(198489, 'deDE', 'Meine Wut brennt wie tausend Sonnen!', '', 38134),
(198488, 'deDE', 'Ich bin noch nicht fertig! Lasst mich frei sein!', '', 38134),
(198487, 'deDE', '', 'Tor ilisar\'thera\'nal!', 38134),
(198486, 'deDE', 'Tyrande! Tut das nicht!', '', 38134),
(198485, 'deDE', '', 'Rettet die anderen! Die Banshee gehört mir!', 38134),
(198484, 'deDE', 'Findet die Entführten und bringt sie nach Hause!', '', 38134),
(198483, 'deDE', 'Euer Mut wird nie vergessen, Champion. Al diel shala.', '', 38134),
(198482, 'deDE', 'Der Pfad öffnet sich!', '', 38134),
(198481, 'deDE', 'Die Zeit ist gekommen! Nehmt den zerbrochenen Helm, und wir öffnen den Riss.', '', 38134),
(198480, 'deDE', 'Aber es gibt keinen anderen Weg. Der Tod will Azeroth holen. Es ist an Euch, sie zu verteidigen.', '', 38134),
(198479, 'deDE', 'Lebendige Sterbliche sollten den Schleier nie durchqueren. Von dort, wo Ihr hingeht, könnte es keine Rückkehr geben.', '', 38134),
(198478, 'deDE', 'Die Schwarze Klinge fürchtet den Tod nicht. Weder hier noch im Jenseits.', '', 38134),
(198477, 'deDE', 'Die Ritter der Schwarzen Klinge werden Euch unterstützen. Darion Mograine hat freiwillig angeboten, an Eurer Seite zu kämpfen.', '', 38134),
(198476, 'deDE', 'Euch werden beträchtliche Gefahren begegnen. Allein könntet Ihr unterlegen sein.', '', 38134),
(198475, 'deDE', 'Doch diese Bedrohung muss eingedämmt werden. Ihr müsst dorthin gehen, wo ich nicht hinkann.', '', 38134),
(198474, 'deDE', 'Ich wage nicht, mit Euch zu reisen. Ich fürchte, dass ich auch ohne Helm an den Willen seines Meisters gebunden werden könnte.', '', 38134),
(198473, 'deDE', 'Es ist eben diese Präsenz, die jenseits des zerschmetterten Himmels lauert.', '', 38134),
(198472, 'deDE', 'Ich spürte eine dunkle Präsenz am Rande meines Bewusstseins. Nicht, Arthas... nicht Ner\'zhul. Etwas... anderes.', '', 38134),
(198471, 'deDE', 'Als ich den Helm trug, brauchte es all meinen Willen, um seine hinterhältige Macht im Zaum zu halten. Oft wäre ich fast gescheitert.', '', 38134),
(198470, 'deDE', 'Platziert die Splitter der Herrschaft um den Runenkreis. Dann kann das Ritual beginnen.', '', 38134),
(198469, 'deDE', 'Dort müsst Ihr hin, wenn Ihr die von Sylvanas Entführten retten wollt. Gemeinsam können wir den Weg öffnen.', '', 38134),
(198468, 'deDE', 'Während meiner Wacht auf dem Frostthron erhaschte ich flüchtige Blicke in die Reiche des Todes. Darunter war auch ein Ort unentrinnbarer Dunkelheit.', '', 38134),
(198467, 'deDE', 'Als die Banshee den Helm zerriss, öffnete sie einen tiefe Wunde im Schleier zwischen unserer Welt und den Schattenlanden.', '', 38134),
(198466, 'deDE', 'Eine weit größere Bedrohung steht bevor. Wir müssen uns darauf vorbereiten.', '', 38134),
(198465, 'deDE', 'Genug! Der Krieg ist vorbei. Noch einen können wir uns nicht leisten.', '', 38134),
(198464, 'deDE', '', 'Ihr wagt es, nach den Gräueltaten Eures Volkes von Frieden zu sprechen?!', 38134),
(198463, 'deDE', '', 'Die Kaldorei brauchen Euch, wenn sie Hoffnung auf Frieden haben sollen, Hohepriesterin.', 38134),
(198462, 'deDE', 'Auch ich will Sylvanas zur Rechenschaft ziehen. Aber Lor\'themar hat recht. Anduin würde nicht wollen, dass wir das Wohl der Allianz seinetwegen riskieren.', '', 38134),
(198461, 'deDE', '', 'Feigling! Ich würde alles geben, um die Banshee auf meiner Gleve aufzuspießen!', 38134),
(198460, 'deDE', 'Wir können Azeroths Anführer nicht auf ein mögliches Selbstmordkommando schicken. Unsere entführten Freunde würden uns denselben Rat geben.', '', 38134),
(198459, 'deDE', '...der Frieden bleibt brüchig. Dies sind sorgenvolle Zeiten. Unsere Völker brauchen Stabilität... Allianz wie Horde.', '', 38134),
(198456, 'deDE', 'Es bleibt noch viel zu tun, um für unser Werk in den Schattenlanden bereit zu sein.', '', 38134),
(198452, 'deDE', 'Manchmal sehe ich keinen Sinn mehr darin, diese Seelen vorzubereiten. Wenn es keine neuen Seelen gibt, nimmt die Ausbeute an Anima ab.', 'Manchmal sehe ich keinen Sinn mehr darin, diese Seelen vorzubereiten. Wenn es keine neuen Seelen gibt, nimmt die Ausbeute an Anima ab.', 38134),
(198451, 'deDE', 'Wie ich höre, seid Ihr auf der Suche nach Anima. Wenn es doch nur genug für alle gäbe.', 'Wie ich höre, seid Ihr auf der Suche nach Anima. Wenn es doch nur genug für alle gäbe.', 38134),
(198450, 'deDE', 'Seht nach oben. Wir können nicht einmal mehr unsere eigene Schöpfung kontrollieren!', 'Seht nach oben. Wir können nicht einmal mehr unsere eigene Schöpfung kontrollieren!', 38134),
(198449, 'deDE', 'Sonst sind diese Käfige immer randvoll... verfluchte Animadürre.', 'Sonst sind diese Käfige immer randvoll... verfluchte Animadürre.', 38134),
(198431, 'deDE', '', 'Schlagt härter zu! Keine Gnade!', 38134),
(198429, 'deDE', '', 'Habt meinen Dank. Sobald der Kerkermeister besiegt ist... gibt es für die Kyrianer viel zu besprechen.', 38134),
(198428, 'deDE', '', 'Lang haben wir unsere sterblichen Leben als Bürde gesehen, die es für den Dienst abzulegen gilt. Doch am Ende haben uns Sterbliche gerettet.', 38134),
(198427, 'deDE', '', 'Die Intrigen des Kerkermeisters haben die Kyrianer gespalten. Wir müssen sie wieder versöhnen.', 38134),
(198426, 'deDE', '', 'Devos...', 38134),
(198425, 'deDE', '', 'Der Kerkermeister hat Euren Verstand vergiftet, Devos. Ihr müsst aufgehalten werden... um jeden Preis.', 38134),
(198424, 'deDE', '', 'Mit Eurem Speer werde ich die Spitzen übernehmen und neue Paragons schmieden, die die Kyrianer anführen!', 38134),
(198423, 'deDE', '', 'Fügt Euch, Archon. Ich diene jetzt einer viel größeren Macht.', 38134),
(198422, 'deDE', '', 'Argh!', 38134),
(198421, 'deDE', '', 'Und ich betrauere Euren Verrat.', 38134),
(198420, 'deDE', '', 'Er holt uns aus den Gefängnissen, die wir uns selbst errichtet haben. Und dafür hat er sich meine Loyalität verdient.', 38134),
(198419, 'deDE', '', 'Und so habe ich dem Kerkermeister zugehört. Ich habe von dem großen Unrecht erfahren, das Ihr ihm... und uns allen angetan habt.', 38134),
(198418, 'deDE', '', 'Aber Ihr verschließt die Augen vor der Wahrheit. Dass der Pfad eine Lüge ist.', 38134),
(198417, 'deDE', '', 'Was ich in Uthers Erinnerungen sah, erschütterte mich bis ins Mark.', 38134),
(198416, 'deDE', '', 'Nein. Ein Sterblicher war es, der den Wahnsinn Eurer Edikte entlarvt hat.', 38134),
(198415, 'deDE', 'Da die Verbündeten des Kerkermeisters uns umgeben, müssen wir ihre Ressourcen voll ausschöpfen.', '', 38134),
(198414, 'deDE', '', 'Wir opfern, weil wir müssen. Bürden Sterblicher behindern nur unsere heilige Pflicht.', 38134),
(198413, 'deDE', '', 'Aber wem dienen wir? Einer unfähigen Richterin, tyrannischen Gesetzen! Unsere Opfer waren vergebens!', 38134),
(198412, 'deDE', 'Die Elysische Feste wurde von der Archon erbaut, unserem Bündnis als Basis zu dienen.', '', 38134),
(198411, 'deDE', '', 'Äonenlang hören wir, wir müssen uns \"läutern\". Unsere Erinnerungen, unsere Identitäten und Liebsten aufgeben. All das im Namen des Dienstes.', 38134),
(198410, 'deDE', '', 'Nicht, solange Ihr sie befehligt! Die Verschmähten werden für die Bastion einen neuen Pfad ebnen!', 38134),
(198409, 'deDE', '', 'Kommt zu Vernunft, Devos. Die Kyrianer müssen zusammenhalten.', 38134),
(198408, 'deDE', '', 'Verschmähte, stellt sicher, dass sich die Sterblichen nicht einmischen!', 38134),
(198407, 'deDE', '', 'Dass Ihr dem Pfad so blind gefolgt seid, hat uns fast vernichtet. Ich musste mich Euch in den Weg stellen!', 38134),
(198406, 'deDE', '', 'Ihr wart das Paragon der Loyalität, Devos. Dabei habt Ihr die ganze Zeit die Verschmähten angeführt!', 38134),
(198405, 'deDE', '', 'Bezwingt ihre Nachhut, während ich Devos konfrontiere. Geht und dient.', 38134),
(198404, 'deDE', '', 'Wir sind weit in der Unterzahl. Die Verschmähten haben eine Schneise durch unsere Truppen geschlagen.', 38134),
(198403, 'deDE', 'Sonnt Euch im Glanz der Elysischen Feste. Überwältigend, nicht wahr?', '', 38134),
(198402, 'deDE', 'Diese mutigen Seelen sind jetzt Eure Familie, Schlundgänger. Willkommen!', '', 38134),
(198401, 'deDE', 'Jetzt gemeinsam! Schlagt zu!', '', 38134),
(198400, 'deDE', 'Ich werde es beenden!', '', 38134),
(198399, 'deDE', 'Meine Aufgabe ist erfüllt!', '', 38134),
(198398, 'deDE', 'Mehr... Ich will mehr...', '', 38134),
(198397, 'deDE', 'Mit diesem Gegner bin ich fertig.', '', 38134),
(198396, 'deDE', 'Der Schlundgänger hat sich uns angeschlossen, auf dass wir unser Reich zurückerobern.', '', 38134),
(198395, 'deDE', 'Eine neue Kerbe an meinem Heft...', '', 38134),
(198390, 'deDE', 'Späht das Gebiet aus und findet Generalin Alver. Wenn Ihr bereit seid, blast mit diesem Horn zum Angriff.', '', 38134),
(198389, 'deDE', 'Lange her, seit Vergangenheit erlebt wurde.\n\nAufregend zu beobachten!', '', 38134),
(198388, 'deDE', 'Am Pfad teilzuhaben... Sehr ungewöhnlich für eine Provostin.\n\nBron und ich nehmen die Herausforderung an!', '', 38134),
(198387, 'deDE', 'Gute Gelegenheit, Brons Kampfmanöver zu testen!', '', 38134),
(198386, 'deDE', 'Schickt mich in Richtung Eurer Feinde, um den Rest kümmere ich mich.', '', 38134),
(198385, 'deDE', 'Da Ihr mich befreit habt, werde ich Euch helfen, Eure Feinde zu töten... zumindest für eine Weile.', '', 38134),
(198384, 'deDE', 'Lasst mich die Führung übernehmen und Eure Feinde werden fallen.', '', 38134),
(198383, 'deDE', 'Gut gekämpft, $p! Gebt den Befehl, und wir beginnen den nächsten Angriff!', '', 38134),
(198382, 'deDE', 'Oh, wie sehr habe ich mich danach gesehnt, wieder kämpfen zu dürfen...', '', 38134),
(198381, 'deDE', 'Wir schlagen zielgenau zu!', '', 38134),
(198380, 'deDE', '', 'Ich bin bereit für eine Herausforderung.', 38134),
(198379, 'deDE', '', 'Erlaubt mir, die Flügel zu strecken.', 38134),
(198378, 'deDE', '', 'Wenn ich Erfolg habe, gewinnt Pelagos vielleicht Selbstvertrauen.', 38134),
(198371, 'deDE', 'Nur eine weitere Prüfung. Selbst wenn ich diesmal scheitere... macht mich das für das nächste Mal stärker.', '', 38134),
(198370, 'deDE', 'Diesmal habe ich ein gutes Gefühl, $n.', '', 38134),
(198369, 'deDE', 'Bin ich dran?\n\nIch werde mein Bestes geben.', '', 38134),
(198368, 'deDE', '', '\"Mortanis beschwören\"... bei dieser Dürre?!', 38134),
(198365, 'deDE', 'Jawohl!', '', 38134),
(198363, 'deDE', 'Ist es wirklich Verrat, wenn man sich seinen Meister überhaupt nicht aussuchen konnte?', '', 38134),
(198362, 'deDE', 'Oh, und danke noch einmal! Woher Ihr auch kommt, die Leute dort sind gesegnet.', '', 38134),
(198361, 'deDE', 'Öffnet mit meinem Dolch die Pflanzen und befreit die anderen. Wir sehen uns in Tirna Vaal.', '', 38134),
(198341, 'deDE', 'Die Pflanzen haben sie gefressen? Nicht schon wieder...', '', 38134),
(198340, 'deDE', 'Stimmt schon! Der ist ein richtig fieser Lulatsch, wenn er alkohilosir... äh... alkiholersier... äh... einen in der Krone hat. Hicks! Da hilft bloß nachgießen, damit er schlappmacht.', '', 38134),
(198339, 'deDE', 'Ah, viel besser! Heutzutage kümmert sich nicht mehr jeder um die Alten.', '', 38134),
(198338, 'deDE', 'Oh, gut, Pilzomanten kann ich immer brauchen. Könntet Ihr Euch um meine Stiele und diese widerlichen Eindringlinge kümmern?', '', 38134),
(198337, 'deDE', 'Solange uns nicht einer verpfeift, ist alles in Butter. Aber Matschblick ist für uns ein echtes Problem.', '', 38134),
(198336, 'deDE', 'Hilfe! Helft mir!', '', 38134),
(198335, 'deDE', 'Was wir hier machen, ist echt gefährlich. Wenn uns der Blutsauger beim Faulenzen erwischt, geht es wieder ab in die Pampe.', '', 38134),
(198334, 'deDE', '', 'Das wird helfen. Hoffentlich habt Ihr nicht zu viele Sporen eingeatmet.', 38134),
(198333, 'deDE', '', 'Der oberste Pilzomant braucht Hilfe! Helft ihm und schützt die Stiele.', 38134),
(198332, 'deDE', '', 'Hier entlang. Kommt.', 38134),
(198329, 'deDE', 'Wenn Ihr bereit seid, greifen wir an. Die Gräber hier bewegen den Spiegel, um die Verstärkung von Schloss Nathria fernzuhalten. Währenddessen wagen Ihr, ich und unsere wenigen verbliebenen Verbündeten einen direkten Angriff auf Denathrius.\n\nEs hängt jetzt alles von $ueinem Schlundgänger:einer Schlundgängerin;, drei Erntern, dem ehemaligen General der Steinlegion und den wenigen Venthyr ab, die noch auf unserer Seite sind. Der Kampf wird das Wesen erreichen, das all das hier erschaffen hat, so weit das Auge reicht.\n\nLasst mich wissen, wenn Ihr bereit seid, Euch dem Tode zu stellen.', '', 38134),
(198316, 'deDE', 'Sterblicher! Ihr habt keine Chance gegen uns!', '', 38134),
(198315, 'deDE', 'Die Schlacht wartet, Soldaten. Macht die Verteidigung bereit!', '', 38134),
(198313, 'deDE', '', '<Der Zenturio bewegt sich fahrig.>\n\nFehler aufgetreten.\n\nFehler aufgetreten.\n\nFehler aufgetreten.', 38134),
(198312, 'deDE', 'FANGT! FANGT! AHHH!\n\nFANGT DIE SCHRIFTROLLE!', 'FANGT! FANGT! AHHH!\n\nFANGT DIE SCHRIFTROLLE!', 38134),
(198311, 'deDE', '', 'Geht und dient, $n.', 38134),
(198310, 'deDE', '', 'Ein Kämpfer auf dem Pfad des Aufstiegs zu sein, ist eine große Ehre, auch wenn Eure Seelenbande nur eine Vision von mir bekämpfen werden... vielleicht zeige ich mich sogar selbst.', 38134),
(198309, 'deDE', '', 'Es ist vollbracht. Ein Teil meiner selbst wohnt nun diesem Spiegel inne, mit Euch.', 38134),
(198308, 'deDE', 'FLEISCHKLOPS KAMPFBEREIT!', '', 38134),
(198307, 'deDE', 'Ich fühle mich ausgeglichen nach meiner morgendlichen Meditation, aber ich könnte etwas Abwechslung vertragen!\n\nVielleicht ein gutes Buch...', 'Ich fühle mich ausgeglichen nach meiner morgendlichen Meditation, aber ich könnte etwas Abwechslung vertragen!\n\nVielleicht ein gutes Buch...', 38134),
(198306, 'deDE', 'Hm... ich glaube, ich hätte gerne etwas Obst. Könntet Ihr wohl einen Provosten für mich finden?', 'Hm... ich glaube, ich hätte gerne etwas Obst. Könntet Ihr wohl einen Provosten für mich finden?', 38134),
(198305, 'deDE', 'PEINIGER STIEHLT ESSEN! IMMER NEHMEN ALLES ESSEN! BRAUCHE RACHE! UND ESSEN!', '', 38134),
(198304, 'deDE', '', 'Tretet vor. Präsentiert Euch den Kyrianern, damit sie Euch unter sich willkommen heißen.', 38134),
(198303, 'deDE', 'Erblickt die glorreiche Archon! Sie, die sie schon alt war, als dieses Reich geschaffen wurde.', '', 38134),
(198302, 'deDE', 'Der Schlundgänger hat sich als Verbündeter nicht nur der Bastion, sondern aller Schattenlande bewiesen.', '', 38134),
(198301, 'deDE', 'Dieses Ritual soll ruhelosen Geistern helfen, die Schwierigkeiten zu überwinden, durch die sie ihre Kräfte nicht wiedererlangen können.\n\nIch habe das Gefühl, dass dieser Geist, der so an Euch hängt, in seinem Leben eine harte Prüfung durchlaufen hat. Ich hoffe, dass wir ihm zu einem friedlichen Schlummer verhelfen können.', '', 38134),
(198300, 'deDE', 'Brüder und Schwestern, hört her! Wir haben uns heute für ein bedeutsames Ereignis eingefunden.', '', 38134),
(198299, 'deDE', 'Nun wollen wir dem Pfad gemeinsam folgen. Kommt.', '', 38134),
(198298, 'deDE', '', 'Der Stein! Was war das?!', 38134),
(198297, 'deDE', '', 'Auch wenn der Pfad mir viel abverlangt, kann ich so noch mehr für die Bastion tun. Ihr werde Euch erlauben, mit dem Seelenspiegel meine Erinnerungen einzufangen.', 38134),
(198296, 'deDE', '', 'Der Pfad des Aufstieg? Es ehrt mich, als jemand zu gelten, der viel zu lehren hat.', 38134),
(198292, 'deDE', '', 'Der Primus hat den Stein als Herausforderung hinterlassen. Erreicht ihn und zeigt Eure Macht.', 38134),
(198288, 'deDE', 'Ich würde gerne etwas trinken. Wo ist dieser Provost bloß?', 'Ich würde gerne etwas trinken. Wo ist dieser Provost bloß?', 38134),
(198287, 'deDE', 'Aber... Ihr seid... minderwertig...', '', 38134),
(198286, 'deDE', '', 'Es ist meine Pflicht, unsere Ressourcen und meine Aspiranten zu schützen.\n\nIch erinnere mich an unsere erste Begegnung... an die auf unnötige Zenturionen verschwendete Anima. Ich weiß, Ihr habt uns durch diese Dürre geholfen, aber wir dürfen immer noch nichts verschwenden.\n\nWas wollt Ihr von mir?', 38134),
(198271, 'deDE', 'In Sicherheit, in Sicherheit! Hier sind wir in Sicherheit.\n\nDanke, danke, dass Ihr uns gerettet habt!', '', 38134),
(198268, 'deDE', '', 'Ich hatte Euch gewarnt, nicht zurückzukehren!', 38134),
(198267, 'deDE', '', 'Zayhad! Aufwachen!', 38134),
(198265, 'deDE', 'Wir sind fast da! Die Ställe sind nur noch den Hügel da hoch.', '', 38134),
(198264, 'deDE', 'Wenn es nach der Allianz ginge, hätten wir nicht mal das.', '', 38134),
(198263, 'deDE', '', 'Seid willkommen, junger Sprössling. Euer Pfad enthüllt sich mit jedem weiteren Schritt.', 38134),
(198262, 'deDE', 'In Orgrimmar haben wir zumindest alle ein Zuhause gefunden. Und das bedeutet den Leuten hier etwas.', '', 38134),
(198261, 'deDE', 'Ist zwar staubig und heiß und voller grimmiger Orcs – nichts für ungut – aber trotzdem ganz charmant hier, oder?', '', 38134),
(198260, 'deDE', 'Ist zwar staubig und heiß und voller grimmiger Orcs, aber trotzdem ganz charmant hier, oder?', '', 38134),
(198259, 'deDE', 'Willkommen in der großen Stadt, $n. Das hier ist Orgrimmar. Alle Leute von Bedeutung machen hier Geschäfte.', '', 38134),
(198257, 'deDE', '', 'Zumindest ein paar von Matyas\' Anhängern sind gezwungen, ihm zu dienen... so wie ich. Befreit sie, wenn Ihr es könnt.', 38134),
(198256, 'deDE', 'Ein weiterer geht zu Boden...', '', 38134),
(198253, 'deDE', 'Ich spüre... alles. Bei der Archon, was für ein Leben Ihr gelebt habt!', '', 38134),
(198252, 'deDE', 'Purianfrüchte machen glücklich! Wir machen Aspiranten glücklich?', 'Purianfrüchte machen glücklich! Wir machen Aspiranten glücklich?', 38134),
(198251, 'deDE', 'Wer bekommt Schriftrolle? Huuuuh?', 'Wer bekommt Schriftrolle? Huuuuh?', 38134),
(198250, 'deDE', 'Wo hat wer Durst?', 'Wo hat wer Durst?', 38134),
(198249, 'deDE', 'Was saubermachen?', 'Was saubermachen?', 38134),
(198248, 'deDE', 'Soll ich halten? Tragen? Fangen?!', 'Soll ich halten? Tragen? Fangen?!', 38134),
(198247, 'deDE', 'Ihr mögt lecker Purianfrucht? \n\nKönnen Aspiranten haben, ja! Arbeit schwer, Frucht verdient!', 'Ihr mögt lecker Purianfrucht? \n\nKönnen Aspiranten haben, ja! Arbeit schwer, Frucht verdient!', 38134),
(198246, 'deDE', 'Schriftrolle, Schriftrolle, Schriftrolle...\n\nWo soll Schriftrolle hin?', 'Schriftrolle, Schriftrolle, Schriftrolle...\n\nWo soll Schriftrolle hin?', 38134),
(198245, 'deDE', 'Durstig? Ihr wollt süßen Saft?', 'Durstig? Ihr wollt süßen Saft?', 38134),
(198244, 'deDE', 'Saubermachen! Saubermachen!\n\n<Der Provost summt vor sich hin.>', 'Saubermachen! Saubermachen!\n\n<Die Provostin summt vor sich hin.>', 38134),
(198243, 'deDE', 'Soll ich halten?', 'Soll ich halten?', 38134),
(198239, 'deDE', 'Es ist mir eine Freude, ein... was auch immer Ihr seid hier im Ardenwald zu erblicken.', '', 38134),
(198232, 'deDE', 'Es wäre eine Ehre, Seelenführerin. Ich hoffe nur... dass mein Band mit Kleia bestehen bleibt.', '', 38134),
(198230, 'deDE', 'Gut, bringt das hier zu Ende. Ich suche die vermissten Pfleger.', '', 38134),
(198229, 'deDE', 'Seit Beginn der Dürre gibt es nicht genug Anima für alle. Wir mussten einige... schwere Entscheidungen treffen.', '', 38134),
(198228, 'deDE', 'Ohne Pfleger erhalten die Wildsamen keine Anima und die Geister in ihnen werden nicht wiedergeboren.', '', 38134),
(198227, 'deDE', 'Ich danke Euch! Diese Teiche sind gerade nicht sehr ruhig...', '', 38134),
(198226, 'deDE', 'Nein, nein... Das wird der Königin nicht gefallen...', '', 38134),
(198224, 'deDE', 'Zersplittert!', '', 38134),
(198223, 'deDE', 'Zerbrecht!', '', 38134),
(198222, 'deDE', 'Euch ramme ich und Grund und Boden!', '', 38134),
(198221, 'deDE', 'Ich werde Euch brechen!', '', 38134),
(198220, 'deDE', 'Wetten, die Wache da weiß den Weg zu den Ställen?', '', 38134),
(198219, 'deDE', 'Und? Was haben sie gesagt?', '', 38134),
(198214, 'deDE', 'Ich bin Eurer Anmaßungen überdrüssig!', '', 38134),
(198213, 'deDE', 'Ich bin unzerstörbar!', '', 38134),
(198212, 'deDE', 'Eure Klingen werden an meiner Haut zerbersten!', '', 38134),
(198210, 'deDE', 'Sterbt durch meine Hand!', '', 38134),
(198208, 'deDE', 'Zu dienen... war meine... größte Ehre...', '', 38134),
(198205, 'deDE', 'Ihr wart unwürdig, das Schlachtfeld mit mir zu teilen.', '', 38134),
(198203, 'deDE', 'Ihr solltet wissen, wo Euer Platz ist!', '', 38134),
(198202, 'deDE', 'Ehrloser Abschaum!', '', 38134),
(198201, 'deDE', 'Verräter müssen sterben!', '', 38134),
(198200, 'deDE', '', 'Meine Klinge wird Euch finden!', 38134),
(198199, 'deDE', '', 'Ich reiße Euch in Stücke!', 38134),
(198198, 'deDE', '', 'Blutet!', 38134),
(198197, 'deDE', '', 'Kein Entkommen!', 38134),
(198196, 'deDE', '', 'Ihr könnt nicht fliehen!', 38134),
(198194, 'deDE', '', 'Euer Ende naht!', 38134),
(198193, 'deDE', '', 'Die Freude über unseren Sieg auf der Insel des Verbannten wird nicht lange währen. Wir müssen uns jetzt wieder auf den Krieg konzentrieren.', 38134),
(198192, 'deDE', '', 'So leicht bröckele ich nicht!', 38134),
(198191, 'deDE', '', 'Fleisch zu Stein!', 38134),
(198190, 'deDE', '', 'Ich werde Euch selbst vernichten!', 38134),
(198189, 'deDE', '', 'Hier oben, $gSchlundgänger:Schlundgängerin;. Ich habe Matyas im oberen Teil des Turms gefunden. Kommt zu mir, wenn Ihr bereit seid.', 38134),
(198188, 'deDE', '', 'Kämpft ehrenhaft und vergesst nie Eure Ausbildung. Lok-tar ogar!', 38134),
(198187, 'deDE', '', 'Erzittert in meinem Schatten!', 38134),
(198186, 'deDE', '', 'Mein Blut... für... den Meister...', 38134),
(198185, 'deDE', '', 'Euer Verrat ist vorbei.', 38134),
(198184, 'deDE', '', 'Großer Ruhm wartet auf dem Schlachtfeld, $p.', 38134),
(198183, 'deDE', 'Kostümwechsel! Hinter die Bühne!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(198180, 'deDE', '', 'Verräterabschaum!', 38134),
(198179, 'deDE', '', 'Die Tiefen sollen Euch holen!', 38134),
(198175, 'deDE', 'Es sind einfach zu viele!', 'Es sind einfach zu viele!', 38134),
(198173, 'deDE', '', 'Euer Aufstand endet hier!', 38134),
(198170, 'deDE', '', 'Kontrolliert die störrische Seele mit den Ketten. Eine in Eisen gelegte Seele gehorcht Euch.', 38134),
(198166, 'deDE', '', 'Federleicht, platziert Ihr die Lilie. Das blöde Siegel geht nicht auf!', 38134),
(198163, 'deDE', 'Hier herrscht Chaos! Wir sollten uns nicht gegenseitig bekämpfen!', 'Hier herrscht Chaos! Wir sollten uns nicht gegenseitig bekämpfen!', 38134),
(198156, 'deDE', 'Kann viele Dinge reparieren! Müsst nur fragen.', 'Kann viele Dinge reparieren! Müsst nur fragen.', 38134),
(198155, 'deDE', 'Hallo! Ja? Kann Ding reparieren.', 'Hallo! Ja? Kann Ding reparieren.', 38134),
(198142, 'deDE', 'Woher bekommen sie ihre Anima?', 'Woher bekommen sie ihre Anima?', 38134),
(198141, 'deDE', 'Die Konstrukte waren unsere Verbündeten! Warum wenden sie sich gegen uns?', 'Die Konstrukte waren unsere Verbündeten! Warum wenden sie sich gegen uns?', 38134),
(198140, 'deDE', 'Gute Arbeit. Wir müssen die Gorm in Schach halten, während wir unsere Verteidigung verstärken.', '', 38134),
(198139, 'deDE', 'Drei Gormmatriarchinnen kämpfen um die Vorherrschaft über den Schwarm. Keine von ihnen darf Königin werden. Tötet sie alle.', '', 38134),
(198137, 'deDE', 'Die Dunkle Fürstin hat uns für eine wichtige Mission zusammengerufen. Wir dürfen auf keinen Fall versagen.', '', 38134),
(198136, 'deDE', 'Ihre Armee wächst mit jedem Soldaten, den wir verlieren.', 'Ihre Armee wächst mit jedem Soldaten, den wir verlieren.', 38134),
(198135, 'deDE', 'Wir sind weit in der Unterzahl!', 'Wir sind weit in der Unterzahl!', 38134),
(198129, 'deDE', '', 'Danke, Aliothe. Danke für Euren Rat und Eure Freundschaft.', 38134),
(198127, 'deDE', '', 'Ja. Aber mit Eurer Hilfe drängen wir sie zurück. Da habe ich keine Zweifel.', 38134),
(198125, 'deDE', '', 'Aber auch der Ardenwald ist bedroht. Ich sehe die Schatten an seinen Rändern. Sie dringen in ihn ein.', 38134),
(198124, 'deDE', '', 'Jetzt ist dieser Wald meine Heimat. So soll es sein. Wir dienen dem Zyklus und die Königin leitet uns.', 38134),
(198121, 'deDE', '', 'Meine Welt wurde von der Brennenden Legion zerschmettert. Es ist nichts als Asche von ihr übrig.', 38134),
(198119, 'deDE', '', 'Die Sehnsucht in Eurer Stimme spricht von Verlust.', 38134),
(198118, 'deDE', '', 'Ihr seid wie ich an die Königin und den Ardenwald gebunden. Doch beneide ich Euch um Eure Träume.', 38134),
(198116, 'deDE', '', 'Ich fühle mich diesem Reich tief verbunden. Und doch steige ich in meinen Träumen über Azeroth auf.', 38134),
(198115, 'deDE', 'Was gibt es, $gRekrut:Rekrutin;?', '', 38134),
(198112, 'deDE', '', 'Der Speer! Bringt sie mit ihm zu Fall!', 38134),
(198111, 'deDE', '', 'Sterbliche! Nehmt meinen Speer!', 38134),
(198108, 'deDE', '', 'Ich bin ein Tirnenn. Ich bin Teil des Waldes und verwandt mit den Bäumen.\n\nAls die Winterkönigin ihre Hand ausstreckte und den Ardenwald erschuf, gehörten wir zu den ersten, die wuchsen. Wir schlugen tiefe Wurzeln und errichteten hohe Mauern. Wir erbauten die Haine.\n\nDie Tirnenn dienen, bis die unermessliche Zeit abgelaufen ist und wir in den Zyklus zurückkehren.', 38134),
(198105, 'deDE', '', 'Ich werde Euch niederstrecken!', 38134),
(198103, 'deDE', '', 'Warum belästigt Ihr mich? Seht Ihr nicht, dass ich beschäftigt bin?!', 38134),
(198099, 'deDE', '', 'Soll dieser Speer Euer Herz durchbohren!', 38134),
(198095, 'deDE', '', 'Kriegshäuptling. Hier ist der erwähnte Rekrut.', 38134),
(198094, 'deDE', 'Gewiss, meine Königin. Wir werden Euch nicht enttäuschen.', '', 38134),
(198093, 'deDE', '', 'Ganz sicher nicht.', 38134),
(198092, 'deDE', '', 'Nathanos, klärt den Neuankömmling über die Mission auf.', 38134),
(198089, 'deDE', '', 'Wenn wir die Allianz besiegen wollen, braucht die Horde weitere mächtige Verbündete.', 38134),
(198088, 'deDE', '', 'Eure Talente sprechen für sich, $n. Ich werde sie gut nutzen.', 38134),
(198087, 'deDE', '', 'Mir scheint, Ihr habt ein Auge für Talent, Kriegsherrin Grimmaxt.', 38134),
(198085, 'deDE', '', 'Ich brauche einen Moment... um meine müden Knochen auszuruhen...', 38134),
(198084, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nLiebes Tagebuch,\n\nunglaublich, dass wir bald erreichen könnten, wovon wir seit Ewigkeiten träumen.\n\nLady Sybille und der Prinz haben Vertrauen in $uden Schlundgänger:die Schlundgängerin; und - noch wichtiger - Gubbins ebenfalls.\n\nUnd ich auch.\n\nDaher habe ich mich entschieden, ein Seelenband mit $uihm:ihr; zu schließen. Wie der Prinz sagte, sind wir zusammen stärker, und ich will $udem Schlundgänger:der Schlundgängerin; meine Stärke nicht vorenthalten.\n\nIch glaube, dass Revendreth wiederhergestellt, die Schattenlande gerettet und der Tee gänzlich ausgetrunken wird!\n\nDein untertänigster Diener (und jetzt auch $useiner:ihrer;)\nTheotar\n\nPS: Die Seelenbindung war ein exquisites Erlebnis. Ich berichte später von allen Details!', '', 38134),
(198083, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nLiebes Tagebuch,\n\nmir ist langweilig. Bitte schick Hilfe.\n\nDein untertänigster Diener,\nTheotar', '', 38134),
(198082, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nLiebes Tagebuch,\n\nich bin bestürzt über meine eigene Unschicklichkeit. Wie habe ich heute Abend nur diesen schwerwiegenden Lapsus begehen können?\n\nIch fürchte, dass sich meine Reputation durch das schreckliche Gerede in Folge meiner Taten nie wieder erholen wird. Die Gräfin muss es wohl schon wissen und wird ohne Zweifel anderen davon erzählen.\n\nIch... muss die Verantwortung für meine Fehler übernehmen. Ich werde einen Weg finden, die Dinge wieder in Ordnung zu bringen.\n\nUnd, liebes Tagebuch, ich schwöre, meinen Tee nie wieder gegen die Uhrzeigerrichtung zu rühren. Ich habe meine Lektion gelernt!', '', 38134),
(198081, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nGästeliste für Teekränzchen:\nLady Sybille\nSir Gubbins\nSir Tubbins\nDie Ehrenvolle Vulca\nSteinkopf <ausgestrichen und mit $p ersetzt>', '', 38134),
(198080, 'deDE', '<Theotar hat einen Titel auf der ersten Seite in wundervoller Kalligraphie geschrieben.>\n\nDas sehr offizielle und sehr geheime Tagebuch von Theotar und $p.\n\nAuf viele gemeinsame aufregende Abenteuer!', '', 38134),
(198079, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nSchöne Sonnenschirme sorgen für sorglose Spaziergänge im Sonnenschein.\nPerfekte Planung mindert Pein.\nMissstände mehren sich, bis man Tee trinkt, denn Tee ist das Allerbeste!', '', 38134),
(198078, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nWas für einen vergnüglichen Spaziergang ich heute mit Lady Sybille unternehmen durfte!\n\nWir wurden von Gubbins und Tubbins begleitet und der gute Pustelrücken trabte hinter uns her, um uns vor unerwünschten Unterbrechungen zu schützen.\n\nNachdem wir auf dem Hügel Spiegelscherben gesammelt hatten, machten wir Halt an der Wohnstatt einer Bekanntschaft, die uns in gewisser Weise etwas schuldete.\n\nDiese Schuld wurde vollständig beglichen.', '', 38134),
(198076, 'deDE', '', 'Noch mehr dieser Narren wollen uns aufhalten. Lasst das nicht zu.', 38134),
(198075, 'deDE', '', 'Noch eine Unterbrechung durch die Schlosswache. Beseitigt sie.', 38134),
(198074, 'deDE', '', 'Weitere Wachen sind auf dem Weg. Gewährt ihnen einen schnellen Tod.', 38134),
(198072, 'deDE', 'Nein! Ich lasse mich nicht wieder einsperren!', '', 38134),
(198071, 'deDE', 'Bei der Macht der Sonne!', '', 38134),
(198070, 'deDE', 'Erhebt Euch aus der Asche!', '', 38134),
(198069, 'deDE', 'Ihr sollt alle von Flammen umhüllt sein!', '', 38134),
(198068, 'deDE', 'Felomin ashal!', '', 38134),
(198067, 'deDE', 'Feuer verschlinge Euch!', '', 38134),
(198066, 'deDE', 'Alle sollen meinen Zorn spüren!', '', 38134),
(198065, 'deDE', 'Sicher... könnt Ihr das... noch besser...', '', 38134),
(198062, 'deDE', '', 'Tirna Vaal ist einer der Haine, die mehr Glück hatten. Er hat die Chance, zu überleben. Und die Königin wird alles in ihrer Macht Stehende dafür tun.', 38134),
(198061, 'deDE', 'Ihr macht... wohl Witze...', '', 38134),
(198057, 'deDE', 'Schluss damit! Die Welt soll brennen!', '', 38134),
(198056, 'deDE', 'Ich kann dieser Qual nicht standhalten!', '', 38134),
(198055, 'deDE', 'Ja... Meine Macht... kehrt zurück!', '', 38134),
(198053, 'deDE', 'Ich spüre... wie meine Kraft... zurückkehrt...', '', 38134),
(198052, 'deDE', 'Meine Mörder... sind hier, um mich zu retten?', '', 38134),
(198051, 'deDE', '$uMein lieber:Meine liebe; $p, ich bin ja so froh, dass wir endlich auf einer Wellenlänge liegen...\n\n<Theotar zwinkert.>\n\nWir müssen bald wieder zusammen Tee trinken.\n\nLady Sybille hat sich nach Eurem Wohlbefinden erkundigt.', '', 38134),
(198050, 'deDE', 'Ich freue mich auf unsere gemeinsame Zeit.', '', 38134),
(198049, 'deDE', 'Eure Qual erwartet Euch.', '', 38134),
(198048, 'deDE', 'Sonnenwanderer... gehört... uns...', '', 38134),
(198047, 'deDE', 'Renathals Verräter?! Wachten, haltet sie auf!', '', 38134),
(198045, 'deDE', 'Zorn ist Eure größte Sünde, Sonnenwanderer. Sie wird uns gute Dienste leisten.', '', 38134),
(198044, 'deDE', 'Sobald ich diese Ketten sprenge, werdet Ihr als Erste brennen!', '', 38134),
(198043, 'deDE', '', 'Bin ich hier denn als Einzige fürs Retten zuständig?', 38134),
(198039, 'deDE', '', 'Denathrius bewahrt in dieser Kammer seine mächtigsten Seelen auf. Er nährt ihre dunkelsten Instinkte und richtet sie als Waffen gegen seine Feinde.', 38134),
(198033, 'deDE', '', 'Wir sollten zusammenbleiben. Allein könnten unsere Feinde hier jeden von uns überwältigen.', 38134),
(198031, 'deDE', '', 'Bleibt bei mir, $n. Mein Zauber reicht nicht weit.', 38134),
(198025, 'deDE', '', 'Ihr nehmt den Fluss. Ich biete Euch vom Ufer aus Deckung. Hoffen wir, dass uns am Ende die echte Tyrande erwartet.', 38134),
(198018, 'deDE', 'Ich bringe Euch zur Elysischen Feste, wenn Ihr bereit seid.', '', 38134),
(198004, 'deDE', '', 'Nichts ist geschenkt. Alles will verdient sein. Auch die Materialien für Euer Heft.', 38134),
(198003, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIhr seid immer so ernsthaft, $umein lieber:meine liebe; $p... Ihr müsst doch auch mal etwas Spaß genießen!\n\nSchnappt Euch einen meiner Sonnenschirme und eine Tasse meines neuesten Tees!\n\nUnternehmt einen Spaziergang und berichtet mir von Euren Abenteuern da draußen!\n\nIch bestehe darauf.', '', 38134),
(198002, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIhr habt wie immer recht, $umein lieber:meine liebe; $p.\n\nWir werden ertragen, was wir ertragen müssen, um den Sieg zu erringen ... und wir werden es mit Stil ertragen!', '', 38134),
(198001, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nEure Worte bewegen mich immer wieder zutiefst, $p.\n\nIch verstehe jetzt, warum Ihr so eine bedeutende Persönlichkeit in Eurer sterblichen Welt wart.\n\nWer würde es wagen, Euch nicht in den Abgrund zu folgen?', '', 38134),
(198000, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch respektiere die hinter Euren Worten stehende Philosophie, aber ich selbst habe mich dieser nie verschrieben, muss ich gestehen.\n\nTatsächlich engen die strengen Konzepte der meisten Philosophien die komplexen und interessanten Aspekte meines Lebens viel zu sehr ein.\n\nAber wenn mir jemand eine Philosophie der Sonnenschirme und Salons vorstellen würde, wäre ich Feuer und Flamme!', '', 38134),
(197999, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nNun, Ihr seid ohne Umschweife zur Sache gekommen...\n\nIch muss gestehen, dass ich nach einem harten Tag auf eine etwas weniger ernsthafte Korrespondenz gehofft hatte.\n\nAber natürlich weiß ich Eure Gedanken zu allen möglichen bedeutsamen Unterfangen zu schätzen.', '', 38134),
(197998, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch kann nicht behaupten, dass ich dem, was Ihr geschrieben habt, zustimme, aber ich muss zugeben, dass Eure Gedankengänge schlüssig sind.\n\nEs ist Euch wie immer gelungen, meine intellektuelle Seite anzuregen.', '', 38134),
(197997, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch muss gestehen, dass ich den Zweck unseres Schicksals noch nie aus diesem Blickwinkel betrachtet habe, $p.\n\nEure Gedanken dazu sind... faszinierend.', '', 38134),
(197996, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIhr habt meinen Verstand angeregt und mein Herz berührt!\n\nJetzt glaube ich, mehr denn je, dass wir weise wählen sollten, worauf wir uns einlassen. Aber wenn wir uns auf etwas einlassen, sollten wir daran festhalten und es nie wieder loslassen.\n\nKomme, was da wolle.\n\nIch danke Euch, $p. Ihr seid mir eine Inspiration.', '', 38134),
(197979, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch habe Euren wundervollen Witz jedem Sonnenschirm in meinem Salon erzählt, aber keiner von ihnen hat so herzlich gelacht wie ich.\n\nKein Sinn für Humor, diese Sonnenschirme. Vielleicht sind die Teekannen eher dafür empfänglich...', '', 38134),
(197978, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nOh, ich liebe Rätsel! Hier ist eines für Euch:\n\nWarum ist ein Steingeborener wie ein Sündenstein?', '', 38134),
(197977, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nUnd nun bin ich an der Reihe, einen Witz zu erzählen!\n\nWie lautet die Lieblingsnachspeise eines Gräbers?\n\nMatschkuchen natürlich!', '', 38134),
(197976, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nDas ist das Lustigste, was ich gehört habe, seit dem einen Mal, zwanzig Minuten, nachdem Steinkopf einen Lichtsplitter verschluckt hatte!', '', 38134),
(197975, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch bitte Euch, $p. Erzählt mir noch einen Witz!', '', 38134),
(197974, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch muss gestehen, dass ich Euer letztes humoristisches Werk nicht verstanden habe.\n\nVielleicht muss man dabei gewesen sein.', '', 38134),
(197973, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch hoffe, Ihr könnt mir vergeben, aber ich musste diesen Scherz Gubbins und Tubbins vorlesen!\n\nZehn Tees später lachen sie immer noch!', '', 38134),
(197972, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\n$uMein lieber Schlundgänger:Meine liebe Schlundgängerin;, ich hatte ja keine Vorstellung davon, wie lustig Ihr sein könnt!\n\nWart Ihr womöglich in Eurem sterblichen Leben $uein Hofnarr:eine Hofnärrin;?\n\nOh, was für eine törichte Frage. Es ist ja immer noch Euer sterbliches Leben, nicht wahr? An diese Eigenart von Euch werde ich mich wohl nie gewöhnen.', '', 38134),
(197971, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nUnd IHR, $umein lieber Schlundgänger:meine liebe Schlundgängerin;, erinnert mich in jeder Hinsicht an einen eleganten Sonnenschirm!\n\nIch weiß gar nicht, wie viele Hinsichten auf einen Sonnenschirm zutreffen... aber ich liebe Sonnenschirme, also gilt dies mit Sicherheit als Kompliment!', '', 38134),
(197970, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nUnd Ihr, $ulieber Schlundgänger:liebe Schlundgängerin;, habt mit Eurer Annahme natürlich recht.\n\nIch werde den nächsten Tee Euch zu Ehren benennen.\n\nWas haltet Ihr von \"Tee so heiß wie $p\"?', '', 38134),
(197969, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch stimme vollkommen mit Euch überein, $uSchlundgänger:Schlundgängerin;.\n\nIch habe schon viele Leute am Hof der Gräfin für genau dieses Konzept zu erwärmen versucht, aber es konnte sich niemand für diese Idee begeistern lassen.\n\nVielleicht können wir sie gemeinsam überzeugen.', '', 38134),
(197968, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch vermute, dass Eure vorherige Nachricht an Tubbins gerichtet war.\n\nAber seid versichert, dass ich sie ihm habe zukommen lassen.\n\nAlles Gute für Eure weiteren Unternehmungen,\nTheotar', '', 38134),
(197967, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nEuer Angebot klingt verlockend, $p.\n\nWir sollten diese Sache während eines Spaziergangs durch die Austrocknung unter meinem Sonnenschirm weiter ausführen.\n\nIch bringe Tee mit, wenn Ihr Gebäck bereithaltet.', '', 38134),
(197966, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nUnd dann sollten wir eine Tasse Tee zusammen genießen... oder zwanzig.\n\nManchmal überkommt mich ein rasender Durst.', '', 38134),
(197965, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nIch muss gestehen, dass mein Gesicht nach der Lektüre Eures Briefes röter ist als Denathrius\' Lendenschurz.', '', 38134),
(197964, 'deDE', '<Theotar hat auf Eure Nachricht in wundervoller Kalligraphie geantwortet.>\n\nOh, was seid Ihr doch für $uein ungezogener kleiner Sterblicher:eine ungezogene kleine Sterbliche;!\n\nIch habe mich so sehr über Eure Randnotizen hier gefreut.\n\nUnd, damit das auch dokumentiert wird, ich teile Eure Einschätzung.', '', 38134),
(197963, 'deDE', '<Ihr schlagt eine zufällige Seite auf, die in Theotars wundervoller Kalligraphie beschrieben ist.>\n\nEs ist schon lange her, dass ich Hoffnung in den Augen des Prinzen gesehen habe.\n\nIch bin mir nicht ganz sicher, ob es wirklich Hoffnung ist, die hinter seinen feurigroten Augen schimmert, aber soweit ich mich erinnern kann, ist dies ist immerhin das erste Mal, dass ich diese Möglichkeit einräume.', '', 38134),
(197957, 'deDE', '', 'Ruht Euch aus, dieser Kampf wird noch eine Weile dauern.', 38134),
(197951, 'deDE', 'Ich freue mich sehr, Euch wiederzusehen, $glieber:liebe; $p. Wir müssen bald wieder zusammen Tee trinken.\n\nLady Sybille hat sich nach Eurer Gesundheit erkundigt.', '', 38134),
(197945, 'deDE', '<Dieser Teleporter gewährt Zugang zu den anderen Ebenen des Turms.>', '', 38134),
(197942, 'deDE', 'Euch auch einen schönen Tag, Marquis. Wir müssen leider weiter!', '', 38134),
(197941, 'deDE', 'Sie ist WIRKLICH hübsch, nicht? Schön, dass Euch das aufgefallen ist, Marquis...', '', 38134),
(197940, 'deDE', 'Wir sollten nicht trödeln. Zur Entspannung sprinte ich auch schon mal.', '', 38134),
(197917, 'deDE', 'Es heißt, die Blutsauger da oben im Schloss werden immer mehr... na ja... \"sie selbst\". Seid vorsichtig. Man is\' bei denen nich\' sicher, wenn die... \"sie selbst\" sind.', '', 38134),
(197916, 'deDE', 'Irgendwer hat gesagt, Ihr habt andere Welten gesehen. Gibt\'s da irgendwo Gräber?\n\nDie Jungs hier sind ganz nett, aber es juckt einen schon, nach tausend Jahren oder so mal wen Neues kennenzulernen.', '', 38134),
(197915, 'deDE', 'Die Blutsauger hier sin\' gar nich so übel wie anderswo. Aber durch die Dürre sind die alle noch hinterhältiger.', '', 38134),
(197912, 'deDE', 'Ey! Ich hab\' gehört, Ihr habt Rendel und Knüppelfratze an einem Stück aus dem Endmorast gefischt, ohne gefressene Gliedmaßen un\' so.\n\nIhr könnt immer herkommen, egal, was die Blutsauger sagen. Wir Gräber mögen alle, die sich für uns einsetzen.', '', 38134),
(197911, 'deDE', '\'Tschuldigung, aber wie wichtig seid Ihr? Also, auf \'ner Skala von \"dreckgeboren\" bis \"Gottheit\"?\n\nIch muss ja rauszufinden, wie geschwollen ich quatschen muss, wenn ich mit Euch rede.', '', 38134),
(197910, 'deDE', 'Ich hatte ein Gerücht gehört, dass Ihr hier rumlauft. Tratsch geht hier schneller durch die Stadt, als eine gescheiterte Seele im Schlund landet.', '', 38134),
(197909, 'deDE', 'Willkommen in Dunkelhafen. Heimat derer, von deren Mitleid und Neid sich ernährt wird.\n\nAchtet darauf, wie Ihr hier um Hilfe bittet.', '', 38134),
(197908, 'deDE', '', 'Dieses Reich will uns täuschen. Weiter. Ich sehe eine weitere Spur aus Leichen vor uns.', 38134),
(197907, 'deDE', 'Ihr werdet die Nachtkriegerin niemals finden!', '', 38134),
(197906, 'deDE', '', 'Für immer.', 38134),
(197905, 'deDE', '', 'Törichtes Kind. Niemand kann hier fort. Aber bleibt doch hier bei mir...', 38134),
(197903, 'deDE', '', 'Meine Träume haben mich hergeführt. Wir wollen Euch retten.', 38134),
(197902, 'deDE', '', 'Shandris? Warum seid Ihr hier?', 38134),
(197901, 'deDE', '', 'Minn\'do!', 38134),
(197879, 'deDE', '', 'Eine weitere Beute erlegt. Ich habe genug von diesen... Kreaturen. Meine Jäger kümmern sich um den Rest.', 38134),
(197877, 'deDE', '', 'Dieses Ungeziefer ist aus der Nähe noch widerlicher.', 38134),
(197870, 'deDE', 'Euch seziere ich persönlich!', '', 38134),
(197869, 'deDE', 'Wunderbar, noch mehr Versuchsobjekte! Lasst die Seuchenbohrer frei!', '', 38134),
(197855, 'deDE', 'Seid Ihr bereit, mit der Zeremonie zu beginnen?', '', 38134),
(197854, 'deDE', '', 'Ihr habt vielleicht bemerkt, dass ich ein schwebendes Schwert bin? Ich komme mit Leichtigkeit durch das Gitter.', 38134),
(197852, 'deDE', '', 'Sieh sich mal einer die ganzen Vorräte an! Damit könnte ich für einige Zeit Seelennetze fertigen.$b$bIm Austausch könnte ich Euch etwas gegen die Feindseligkeit des Waldes anbieten. Damit könnt Ihr Euch zurückziehen oder an den Feind anschleichen, was auch immer sich gerade anbietet.$b$bHaben wir eine Abmachung?', 38134),
(197850, 'deDE', 'Gut, dass Ihr hier seid. Mir gehen langsam die Gorm zum Schmieden aus, wenn Ihr versteht.$b$BVorräte? Na, da sage ich nicht nein. Wenn Ihr sie mir gebt, könnte ich etwas für Euch machen, das Euch zeitweise ein paar Freunde verschafft. Was sagt Ihr?', '', 38134),
(197848, 'deDE', '', 'Vorräte? Für mich? Aber die Lady...$b$bIch nehme dieses Geschenk dankbar an. Ich habe unsere Kriegsfarben mitgebracht und würde sie Euch gerne im Austausch anbieten. Sie werden Euch mit der Stärke und der Schnelligkeit der Jagd segnen.', 38134),
(197846, 'deDE', '', 'Ah, die Vorräte aus den Wachstumswerken! Da ich dieses Gebiet beaufsichtige, ist es gut, dass Ihr sie mir zur Aufbewahrung bringt.$b$bIch habe genau das Richtige, um Eure Lebensgeister zu wecken und Euren Gang zu beflügeln, wenn Ihr sie mir gebt.', 38134),
(197843, 'deDE', 'Der Wyvernschweif wartet. Ihr könnt mir einen ausgeben!', '', 38134),
(197839, 'deDE', 'Lasst uns einen Ausbilder finden, um Eure Fähigkeiten zu spezialisieren!', '', 38134),
(197834, 'deDE', '', 'Ich werde die Missstände dieser Ewigkeit geraderücken.', 38134),
(197833, 'deDE', 'Das sollte Euch Manieren lehren, Grobian!', '', 38134),
(197832, 'deDE', 'Bei der Königin, das hoffe ich.', 'Bei der Königin, das hoffe ich.', 38134),
(197831, 'deDE', 'Ist das... eine neue Seele? Kehren endlich Seelen in den Ardenwald zurück?!', 'Ist das... eine neue Seele? Kehren endlich Seelen in den Ardenwald zurück?!', 38134),
(197830, 'deDE', '', 'Die Westflanke ist steckt fest. Mograine braucht Verstärkung!', 38134),
(197829, 'deDE', '', 'Meine Teleportationsfähigkeit ist hier eingeschränkt, aber ich sollte ein Portal zurück zu meinem Stab öffnen können.', 38134),
(197827, 'deDE', '', 'Den Fehler mache ich nicht noch einmal.', 38134),
(197826, 'deDE', '', 'Ich bin verletzt... Einen Moment!', 38134),
(197820, 'deDE', '', 'Geht... mit Mut im Herzen...', 38134),
(197819, 'deDE', '', 'Schmerz... Leid...!', 38134),
(197817, 'deDE', '', 'Das ist Tyrandes Werk. In meinen Träumen sah ich, wie sie sich durch die Monster kämpfte.\n\nEine Spur aus Leichen. Sehen frisch aus... als ob sie gerade erst hier war!', 38134),
(197815, 'deDE', '', 'Der Ort aus meiner Vision liegt vor uns. Kommt. Lasst uns sehen, ob es dort Antworten gibt.', 38134),
(197814, 'deDE', 'Tja, der Oberste Kämmerer hat wohl \'nen Job für mich, aber nix drüber gesagt, wie lange der dauern soll.\n\nIch darf in der Kutsche hier rumdüsen, bis mir \'n Blutsauger oder \'ne Schwinge sagt, dass jetzt gut ist. Behaltet mich im Auge.\n\nIhr habt uns Gräbern echt \'nen großen Gefallen getan, als Ihr mich vor diesem Verschlinger gerettet habt. Egal, was passiert, auf uns könnt Ihr Euch verlassen.', '', 38134),
(197812, 'deDE', '', 'Alles bereit. Auf geht\'s.', 38134),
(197811, 'deDE', '', 'Sobald wir Anduin haben, wird mein Stab zum Anker, um uns wieder in Sicherheit zu teleportieren.', 38134),
(197810, 'deDE', '', 'Ich fühle die Angst der Verräterin. Sie ist im Verfallenen Lager.', 38134),
(197809, 'deDE', 'Die restlichen Truppen halten hier die Stellung. Gebt Bescheid, wenn Ihr diesen Portalstein findet.', '', 38134),
(197808, 'deDE', 'Wir können diese Seelen nicht ihrem Schicksal überlassen.', '', 38134),
(197807, 'deDE', 'Passt auf Euch auf. Und legt beim Kriegshäuptling ein gutes Wort für mich ein!', '', 38134),
(197806, 'deDE', 'Ich wusste immer, dass Ihr berühmt werdet, $n.', '', 38134),
(197805, 'deDE', '', 'Der Kriegshäuptling erwartet Euch in der Feste Grommash.', 38134),
(197804, 'deDE', '', 'Man erwartet Euch in der Feste Grommash. Lasst den Kriegshäuptling besser nicht warten...', 38134),
(197803, 'deDE', '', '$n, ich habe Nachricht vom Kriegshäuptling!', 38134),
(197799, 'deDE', 'Was macht Ihr hier? Es ist viel zu gefährlich für Euch, hier allein herumzulaufen.', 'Was macht Ihr hier? Es ist viel zu gefährlich für Euch, hier allein herumzulaufen.', 38134),
(197798, 'deDE', '', '$n! Es wärmt mein Herz, hier einen Verbündeten zu sehen. Um Tyrandes willen, bitte helft mir.', 38134),
(197797, 'deDE', '', 'Also gut, ich will meinen Schmerz verdrängen... vorerst. Aber vergessen wird er nicht.', 38134),
(197796, 'deDE', 'Shandris, Kriegsnarben sind mir nicht fremd. Aber dieser Schlundgänger könnte unsere einzige Hoffnung sein, Tyrande zu retten.', '', 38134),
(197795, 'deDE', '', 'Einem Soldaten der Horde vertraue ich kein Leben an, das mir so am Herzen liegt.', 38134),
(197794, 'deDE', '', 'Niemand ist je dem Schlund entkommen, bis gewisse Sterbliche hier gezeigt haben, dass sie zurückkehren können.', 38134),
(197789, 'deDE', 'Lasst Euch Zeit. Ich hab jede Menge zu tun und bin beschäftigt.', '', 38134),
(197788, 'deDE', 'Die anderen Gräber sind so liederlich... lassen ihre Werkzeuge überall rumliegen.', '', 38134),
(197787, 'deDE', 'Mehr kriegt Ihr aber nicht. Den Rest müsst Ihr selbst finden!', '', 38134),
(197786, 'deDE', 'Für den Anfang kriegt Ihr meinen Ersatzhammer.', '', 38134),
(197782, 'deDE', 'Niemand Meister stören!', '', 38134),
(197781, 'deDE', '', 'Die Jagd ruft, $n.', 38134),
(197779, 'deDE', '', 'Azeroths Zukunft braucht Euren Schutz, $n!', 38134),
(197778, 'deDE', '', 'Ich hoffe, Eure Zeit in der Vergangenheit war schön, aber wir brauchen Euch wieder in der Gegenwart, $n!', 38134),
(197777, 'deDE', '', 'Ich wollte diese Seelen an einen Ort bringen, an dem sie etwas Anima haben können, doch alles ging schief!\n\nDie Fauna des Ardenwalds war vor der Dürre nie so gefährlich. Ich muss sie sicher ins Geistertal bringen.\n\nBitte helft mir!', 38134),
(197776, 'deDE', '', 'Te\'zan! Ihr habt Hilfe gefunden!', 38134),
(197775, 'deDE', 'Sicher, sicher! Hier auf Eurem Kopf.', '', 38134),
(197772, 'deDE', 'Was seid Ihr denn für ein Wesen.', '', 38134),
(197770, 'deDE', '', 'Also dann, blast auf dem ausgehöhlten Zahn zur Jagd.', 38134),
(197768, 'deDE', '', 'Entzieht Hundemeister Loksey die Anima, Schlundgänger.', 38134),
(197766, 'deDE', 'Dieser Austausch ist nicht länger von Wert. Betrachtet unser Geschäft als abgeschlossen, vorerst.', '', 38134),
(197763, 'deDE', '', 'Champion, der Lordregent erwartet Euch in Orgrimmar. Es ist dringend.', 38134),
(197762, 'deDE', 'Champion, Ihr werdet in Sturmwind für eine dringende und heikle Angelegenheit gebraucht... Kommt sofort, und zwar allein.', '', 38134),
(197760, 'deDE', 'Gute Wahl, $n!', '', 38134),
(197758, 'deDE', '', 'Ich mache uns wieder unsichtbar. Einen Moment.', 38134),
(197754, 'deDE', 'Jaina, könnt Ihr uns vor ihnen verbergen?', '', 38134),
(197752, 'deDE', '', 'Der Stein reagiert auf Euch, $n! Versucht, ihn zu aktivieren. Wir halten die Armee des Kerkermeisters zurück!', 38134),
(197748, 'deDE', 'Die Seele Eurer Welt ist des Todes! Alles, was Ihr seht und kennt, wird zunichtegemacht!', '', 38134),
(197747, 'deDE', '', 'Er ist fast aufgeladen! Nicht nachlassen!', 38134),
(197746, 'deDE', 'Wir haben bereits endlose Armeen aufgehalten. Wir geben nie auf!', '', 38134),
(197745, 'deDE', 'Meine Truppen sind endlos!', '', 38134),
(197744, 'deDE', '', 'Haltet die Stellung! Der Portalstein muss voll aufgeladen sein!', 38134),
(197743, 'deDE', 'Schlundgebundene! Ergreift sie!', '', 38134),
(197742, 'deDE', 'Ein Relikt der Ersten... reagiert auf Sterbliche?! Unmöglich!', '', 38134),
(197740, 'deDE', '', 'Mein Zauber wird uns an den meisten seiner Wachen vorbeibringen, aber einige werden ihn durchschauen können.', 38134),
(197739, 'deDE', 'Die Truppen des Kerkermeisters sind endlos. Wir können sie nicht alle besiegen.', '', 38134),
(197738, 'deDE', 'Hier! Diese Barriere schützt uns!', '', 38134),
(197736, 'deDE', 'Wenn ich gewusst hätte, dass es mich in den Endmorast bringt, einen Blutsauger an den anderen zu verpetzen, hätte ich die Klappe gehalten. Habt Ihr Rubine, mit denen ich meine Fackel anbekomme?', '', 38134),
(197734, 'deDE', '', 'Eure Sünden werden Eure Verbündeten einholen.', 38134),
(197733, 'deDE', '', 'Sterbt gemeinsam!', 38134),
(197732, 'deDE', '', 'Eure Schicksale sind verbunden!', 38134),
(197731, 'deDE', '', 'Für Eure Sünden gibt es keine Erlösung!', 38134),
(197730, 'deDE', '', 'Die Qual Eurer Sünde wird ewig sein.', 38134),
(197729, 'deDE', '', 'Es wird mir Freude bereiten, Euch die Sünde herauszureißen.', 38134),
(197728, 'deDE', '', 'Katastrophal!', 38134),
(197727, 'deDE', '', 'Die Boden zerbricht!', 38134),
(197726, 'deDE', '', 'Zerstörung überall!', 38134),
(197725, 'deDE', '', 'Herzen von Dunkelheit gefesselt!', 38134),
(197724, 'deDE', '', 'Die Sünde soll Euer Herz verschlingen!', 38134),
(197723, 'deDE', '', 'Ich werde Euch das Herz zermalmen!', 38134),
(197722, 'deDE', '', 'Es muss die Vorsehung sein. Sie leitet uns immer.', 38134),
(197721, 'deDE', '', 'Dieses Wesen stinkt nach dem Schlund!', 38134),
(197720, 'deDE', '', 'Die Anima schwillt an und verkündet Euer Ende!', 38134),
(197718, 'deDE', '', 'Vielen Dank, Champion.', 38134),
(197717, 'deDE', '', 'Aus dem Schlund? Ich weigere mich, das zu glauben!', 38134),
(197716, 'deDE', '', 'Es geht alles so schnell! Mein Hain liegt im Sterben!', 38134),
(197713, 'deDE', 'Und so zerbröckeln die Steine unter Eurer Macht. Das wird nicht vergessen.', '', 38134),
(197712, 'deDE', 'Denathrius\' Elitetruppen halten diesen Distrikt. Sie werden uns angreifen, wenn wir nichts tun.', '', 38134),
(197710, 'deDE', '', 'Wird unser Glaube an die Vorsehung auf die Probe gestellt?', 38134),
(197709, 'deDE', '', 'Ein Eindringling? Wie ist das möglich?', 38134),
(197708, 'deDE', '', 'Ist $uer:sie; gerade dem Schlund entronnen? Unmöglich!', 38134),
(197705, 'deDE', 'Das Haus der Rituale hält hier ein Ritual der Ermächtigung ab. Könnt Ihr es nicht aufhalten, zerstört, was daraus hervorgeht.', '', 38134),
(197704, 'deDE', 'Trotz all ihrer widerlichen Zauberkünste, brechen die Knochen dieser Ritualisten genau wie alle anderen. Ausgezeichnete Arbeit.', '', 38134),
(197701, 'deDE', 'Aber wie prüfe ich ihre Echtheit?', '', 38134),
(197699, 'deDE', '', 'Ihr habt enormen Mut bewiesen, Euch dieser Herausforderung zu stellen, und kehrt siegreich zu uns zurück.', 38134),
(197698, 'deDE', '', 'Valinor war einst eine Prüfung für unsere tapfersten Champions. Jetzt ist er ein Agent des Chaos und eine Prüfung für Euch.', 38134),
(197693, 'deDE', '', 'Mein ewiger Dank! Eines Tages wird dieser Hain wieder hell und fröhlich sein.', 38134),
(197692, 'deDE', '', 'Finstere Mächte bedrohen Tirna Scithe. Wenn wir sie jetzt nicht aufhalten, werden sie den Ardenwald übernehmen!', 38134),
(197691, 'deDE', 'Sammelt den Zehnt wo möglich von den Dorfbewohnern ein und sucht die Gegend nach jedem übersehenen Tropfen ab.', 'Sammelt den Zehnt wo möglich von den Dorfbewohnern ein und sucht die Gegend nach jedem übersehenen Tropfen ab.', 38134),
(197689, 'deDE', 'Was nützen uns all die Rituale, wenn wir unsere Leute nicht vor dem Schlund retten?', 'Was nützen uns all die Rituale, wenn wir unsere Leute nicht vor dem Schlund retten?', 38134),
(197688, 'deDE', 'Bitte helft uns, das Ritual der Sühne abzuschließen, danach können wir mit Euch sprechen.', 'Bitte helft uns, das Ritual der Sühne abzuschließen, danach können wir mit Euch sprechen.', 38134),
(197687, 'deDE', 'Ich habe nicht damit gerechnet, dass sie sich gegenseitig umbringen, aber für das übergeordnete Wohl tun wir, was nötig ist. Denathrius muss gehen. Lebt wohl.', 'Ich habe nicht damit gerechnet, dass sie sich gegenseitig umbringen, aber für das übergeordnete Wohl tun wir, was nötig ist. Denathrius muss gehen. Lebt wohl.', 38134),
(197672, 'deDE', 'Nun, also... Ich hoffe, hier mögen alle Feuer!', '', 38134),
(197671, 'deDE', 'Ich fürchte... öh... Wir haben keine Wassereimer...', '', 38134),
(197669, 'deDE', '', 'Ich weiß, wo Ihr einen weiteren Seelenspiegel findet. Geht zur Höhle im Norden. Wir treffen Euch dort.', 38134),
(197668, 'deDE', '', 'Solange Ihr entschlossen bleibt, kann es keine wahre Niederlage geben.', 38134),
(197665, 'deDE', '', 'Eure Zeit ist noch nicht gekommen, Kind von Azeroth.', 38134),
(197664, 'deDE', '', 'Eine Niederlage bringt auch Weisheit.', 38134),
(197663, 'deDE', '', 'Auch die Besten unter uns werden ab und überwältigt.', 38134),
(197662, 'deDE', '', 'Ich bin sicher, nächstes Mal schafft Ihr es. Wozu hat man Verbündete?', 38134),
(197661, 'deDE', '', 'Ihr werdet Euch besser schlagen. Nächstes Mal.', 38134),
(197660, 'deDE', '', 'Niemand hätte das voraussehen können.', 38134),
(197659, 'deDE', 'Ich wusste, ich hätte meinen guten Mantel mitbringen sollen.', '', 38134),
(197658, 'deDE', 'Einige Traditionen gehören gebrochen.', '', 38134),
(197657, 'deDE', 'Das ist alles so aufregend!', '', 38134),
(197656, 'deDE', 'Entspannt Euch. Wir brauchen alle mal eine Pause.', '', 38134),
(197655, 'deDE', 'Welch Dekadenz!', '', 38134),
(197654, 'deDE', 'Die Schlichtheit birgt großen Wert.', '', 38134),
(197653, 'deDE', 'Ein bisschen Gefahr macht alles interessanter.', '', 38134),
(197652, 'deDE', 'Zusammen sind wir stark!', '', 38134),
(197651, 'deDE', 'Guter, sauberer Spaß!', '', 38134),
(197650, 'deDE', 'Passt besser auf! Wir haben ein Fiasko angerichtet.', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(197635, 'deDE', 'Ein entscheidender Sieg. Wir treffen Euch vor der Schauerkuppe, $n.', '', 38134),
(197633, 'deDE', 'Ich bedanke mich noch einmal für die Ehre Eurer Einladung.\n\nSchön zu sehen, dass es auf dem neuen Weg des Prinzen in die Zukunft noch Raum für Tradition und Schicklichkeit gibt.', '', 38134),
(197632, 'deDE', 'Na also, sieht für mich ganz fantastisch aus! Und jetzt rettet den Ardenwald!', '', 38134),
(197631, 'deDE', 'Unsere Handwerker können wahre Wunder vollbringen. Jemand muss ihnen nur Vorräte bringen. Jemand wie Ihr!', '', 38134),
(197630, 'deDE', 'Wie Ihr befehlt!', '', 38134),
(197629, 'deDE', '', 'Ihr da! Sagt dem Baron, er soll Anima besorgen, wenn er seine Knochenwache will!', 38134),
(197627, 'deDE', '', 'Seid Ihr bereit?', 38134),
(197626, 'deDE', 'Durch die Gnade der Archon wurde mir der Feierlichkeitendienst in diesem entfernten Bezirk zugeteilt.\n\nIhr könnt Euch sicher sein, dass heute kein Feind der Anführerin der Bastion einen Fuß in Euren Hof setzen wird!\n\n<Polemarch Adrestes nimmt Haltung an, während er die anderen Gäste misstrauisch beäugt.>', '', 38134),
(197625, 'deDE', '', 'Als frühere Hofdame von Königin Azshara habe ich zahlreiche extravagante Höfe gesehen.\n\nAls Baronin von Maldraxxus habe ich unzählige brillante Strategien und Manöver aufgrund winziger Fehler scheitern sehen.\n\nKurzum: Ich bin nicht so leicht zu beeindrucken.', 38134),
(197624, 'deDE', '', 'Mehr Befehle? Was jetzt?', 38134),
(197623, 'deDE', '', 'Ich würde lieber auf dem Rücken eines Terrorwolfs reisen als in seinem Bauch.', 38134),
(197622, 'deDE', '', 'Sie will die Anima entziehen! Benutzt die Phiole, $p!', 38134),
(197621, 'deDE', '', 'Hergehört! Wer verliert, schleppt Erz zur Schmiede! Und jetzt kämpft!', 38134),
(197620, 'deDE', 'Es gibt so wenig Anima und ich musste mich von so vielen schlummernden Geistern verabschieden, Freunden, um die ich mich jahrelang gekümmert habe.\n\nDoch Älteste Dunmira ist ihrem Erwachen so nah, ich kann ihr nicht einfach Lebwohl sagen! \n\nIch habe ihren Wildsamen an einen Ort gebracht, wo sie ein bisschen mehr Anima bekommen konnte, und dann hat diese schreckliche Kreatur ihn gestohlen! Ich glaube, dieser Vogel will ihn ausbrüten wie ein Ei.', '', 38134),
(197614, 'deDE', '', 'So viel Aufwand... und ich stehe noch immer. Bringen wir es zu Ende.', 38134),
(197611, 'deDE', 'Neue Befehle! Es geht nach Westen! Ausrücken!', '', 38134),
(197603, 'deDE', 'Die Zeit ist gekommen. Truppen, Stellung halten.', '', 38134),
(197602, 'deDE', 'Graf Denathrius\' Privatsammlung. Über Äonen aus den Schattenlanden zusammengetragen.', '', 38134),
(197601, 'deDE', 'Interessant, die Kämpfe haben die Wachen fortgelockt.', '', 38134),
(197598, 'deDE', 'Unerschöpfliche Armeen gehorchen meinem Befehl!', '', 38134),
(197597, 'deDE', 'Diese Macht lässt die Realität bröckeln!', '', 38134),
(197595, 'deDE', 'Die Macht dieses Relikts lässt Götter erzittern!', '', 38134),
(197594, 'deDE', '', 'Ich habe diese Farce satt. Zeigt Euch, und ich gewähre Euch die Gnade eines schnellen Endes.', 38134),
(197593, 'deDE', 'Arkane Macht durchströmt mich!', '', 38134),
(197590, 'deDE', 'Sterbt gemeinsam!', '', 38134),
