(199285, 'esES', 'El jefe... se enfadar...', '', 38134),
(199284, 'esES', 'Mquina no funciona!', '', 38134),
(199283, 'esES', 'No cojas... el cristal...', '', 38134),
(199282, 'esES', 'Machaque brilla!', '', 38134),
(199281, 'esES', 'Hice... lo que pude...', '', 38134),
(199280, 'esES', 'Esqueletos mandones!', '', 38134),
(199279, 'esES', 'Soy... mala... gente?', '', 38134),
(199278, 'esES', 'Mala gente!', '', 38134),
(199275, 'esES', 'Todos los maldraxxi son as de crueles?', '', 38134),
(199274, 'esES', 'Esto lleva el sello de Cosecarne. Crea una distraccin mientras yo voy a por sus discpulos!', '', 38134),
(199273, 'esES', '', 'Sin el fragmento de espejo que tiene Mateo, no llegaremos al medalln a tiempo.', 38134),
(199271, 'esES', 'Conocer tus secretos!', 'Conocer tus secretos!', 38134),
(199266, 'esES', 'Maldraxxus ser nuestro!', 'Maldraxxus ser nuestro!', 38134),
