(199258, 'esES', 'Penitencia tambin puede sanar a un aliado; es bastante verstil.', '', 38134),
(199257, 'esES', '', 'Penitencia tambin puede sanar a un aliado, es bastante verstil.', 38134),
(199246, 'esES', '', 'Es... tarde... para m...', 38134),
(199245, 'esES', '', 'Qu... oscuridad...', 38134),
