(193823, 'esMX', 'Igual que todos. Sospecho que tendremos buenas ganancias gracias a esta última empresa.', 'Igual que todos. Sospecho que tendremos buenas ganancias gracias a esta última empresa.', 38134),
(193822, 'esMX', 'Bastante bien. Trabajar para el cártel me mantiene ocupado.', 'Bastante bien. Trabajar para el cártel me mantiene ocupada.', 38134),
(193821, 'esMX', 'Me encargaré de ello.', 'Me encargaré de ello.', 38134),
(193820, 'esMX', 'Es una red de portales realmente impresionante. Por favor, que mis felicitaciones lleguen a quien corresponda.', 'Es una red de portales realmente impresionante. Por favor, que mis felicitaciones lleguen a quien corresponda.', 38134),
