INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(189397, 'esMX', '¡Mejor colega del mundo!', '', 38134),
(189396, 'esMX', '¡Más bocadillos! ¡Más bocadillos!', '', 38134),
(189395, 'esMX', '¿Qué haces aquí, $umal colega:mala colega;?', '', 38134),
(189394, 'esMX', '¡Rocas muy inquietas hoy!', '', 38134),
(189390, 'esMX', '$UBienvenido:Bienvenida;, Surcafauces. Este portal te llevará hacia el Anillo de Transferencia, el nivel de transporte de Oribos.', '', 38134),
(189380, 'esMX', 'Entonces, seguiremos en silencio. Por ahora.', 'Entonces, seguiremos en silencio. Por ahora.', 38134),
(189379, 'esMX', 'Por favor, no. No lo hagas por mí.', 'Por favor, no. No lo hagas por mí.', 38134),
(189378, 'esMX', 'Podríamos buscar algo para hacer alboroto, si insistes.', 'Podríamos buscar algo para hacer alboroto, si insistes.', 38134),
(189377, 'esMX', '¡Tus palabras me ofenden! A veces, el silencio no representa más de lo que es.', '¡Tus palabras me ofenden! A veces, el silencio no representa más de lo que es.', 38134),
(189374, 'esMX', 'Ba\'silk, hace mucho tiempo que no presentas una solicitud. Tu silencio me resulta... preocupante.', 'Ba\'silk, hace mucho tiempo que no presentas una solicitud. Tu silencio me resulta... preocupante.', 38134),
(189372, 'esMX', 'Ah... Bien. Supongo que tendremos que confiar en los mortales. Sus costumbres deben ser el Propósito. Con huesos y todo.', 'Ah... Bien. Supongo que tendremos que confiar en los mortales. Sus costumbres deben ser el Propósito. Con huesos y todo.', 38134),
(189370, 'esMX', 'Dicen que están relacionados con rituales de algún tipo. Intentamos no inmiscuirnos en los asuntos personales de nuestros clientes.', 'Dicen que están relacionados con rituales de algún tipo. Intentamos no inmiscuirnos en los asuntos personales de nuestros clientes.', 38134),
(189368, 'esMX', 'Son suministros necesarios que llegaron a pedido de los mortales.', 'Son suministros necesarios que llegaron a pedido de los mortales.', 38134),
(189367, 'esMX', 'Ba\'silk, he recibido quejas sobre unas cajas de huesos amontonadas en los niveles inferiores.', 'Ba\'silk, he recibido quejas sobre unas cajas de huesos amontonadas en los niveles inferiores.', 38134),
(189363, 'esMX', '¿Para qué necesitan tantos huesos?', '¿Para qué necesitan tantos huesos?', 38134),
(189359, 'esMX', '¡¿Cómo se te ocurre?!', '¡¿Cómo se te ocurre?!', 38134),
(189358, 'esMX', 'Dentro de lo razonable, claro. Pero empiezo a preguntarme si en realidad te estás aprovechando de nuestra naturaleza complaciente.', 'Dentro de lo razonable, claro. Pero empiezo a preguntarme si en realidad te estás aprovechando de nuestra naturaleza complaciente.', 38134),
(189357, 'esMX', 'No quise ofenderte. Solo quiero decir que compartimos los mismos intereses. Los mortales necesitan suministros si han de ayudar a tu Árbitra. Y comodidades.', 'No quise ofenderte. Solo quiero decir que compartimos los mismos intereses. Los mortales necesitan suministros si han de ayudar a tu Árbitra. Y comodidades.', 38134),
(189356, 'esMX', 'No te atrevas a invocar el Propósito tan a la ligera, Ba\'silk. El Propósito es nuestra vida, pero no la tuya.', 'No te atrevas a invocar el Propósito tan a la ligera, Ba\'Silk. El Propósito es nuestra vida, pero no la tuya.', 38134),
(189355, 'esMX', 'Pero quieres que los mortales se sientan a gusto, ¿verdad? ¿No es ese el Propósito?', 'Pero quieres que los mortales se sientan a gusto, ¿verdad? ¿No es ese el Propósito?', 38134),
(189353, 'esMX', 'Parece un poco excesivo. No obstante, el Propósito es nuestra vida. Haz lo que debas hacer.', 'Parece un poco excesivo. No obstante, el Propósito es nuestra vida. Haz lo que debas hacer.', 38134),
(189352, 'esMX', 'Lo siento mucho. Solo intentamos cumplir con las necesidades de nuestros invitados mortales. Seguramente sabrás entender.', 'Lo siento mucho. Solo intentamos cumplir con las necesidades de nuestros invitados mortales. Seguramente sabrás entender.', 38134),
(189351, 'esMX', '¿En serio, Ba\'silk? ¿De verdad necesitas importar todavía más criaturas a Oribos?', '¿En serio, Ba\'silk? ¿De verdad necesitas importar todavía más criaturas a Oribos?', 38134),
(189350, 'esMX', 'Me temo que no comprendes.\n\nEl Propósito es nuestra vida. ¿Quién soy yo para cuestionarlo?\n\nSí, yo soy el encargado de responder las preguntas incesantes de los negociantes.\n\nEs solo que sus preguntas se vuelven, ocasionalmente, bastante cansinas.', 'Me temo que no comprendes.\n\nEl Propósito es nuestra vida. ¿Quién soy yo para cuestionarlo?\n\nSí, yo soy la encargada de responder las preguntas incesantes de los negociantes.\n\nEs solo que sus preguntas se vuelven, ocasionalmente, bastante cansinas.', 38134),
(189345, 'esMX', 'Dragadores... ¡siempre causando problemas!', '', 38134),
(189344, 'esMX', 'Qué velas más curiosas...', '', 38134),
(189343, 'esMX', '¡Cuidadores holgazanes! ¡Ese monumento está a punto de caerse!', '', 38134),
(189342, 'esMX', 'Me informaron que irías al lugar del accidente. Te diriges al lugar más peligroso de Maldraxxus. Ten cuidado, usa una máscara y lávate las manos.', '', 38134),
(189340, 'esMX', 'Mi pueblo, al igual que el tuyo, es un visitante en este motor antiguo.\n\nClaro que hemos estado aquí por un período más largo. Por ende, tenemos más afinidad con los asistentes.\n\nCon gusto actuaremos como nexo si fuera de ayuda para su causa.', 'Mi pueblo, al igual que el tuyo, es un visitante en este motor antiguo.\n\nClaro que hemos estado aquí por un período más largo. Por ende, tenemos más afinidad con los asistentes.\n\nCon gusto actuaremos como nexo si fuera de ayuda para su causa.', 38134),
(189338, 'esMX', 'Surcafauces.\n\n¿Qué puedo hacer para ayudar a un ser de tu especie? Confío en que mis hermanos han encontrado formas de volver su estadía aquí más confortable.\n\nEste es un hogar temporal para nosotros, al igual que para ustedes. Parece que nuestros propósitos se intersecan aquí.', 'Surcafauces.\n\n¿Qué puedo hacer para ayudar a un ser de tu especie? Confío en que mis hermanos han encontrado formas de volver su estadía aquí más confortable.\n\nEste es un hogar temporal para nosotros, al igual que para ustedes. Parece que nuestros propósitos se intersecan aquí.', 38134),
(189337, 'esMX', 'Sí, pero no.\n\nEspero sepas disculpar la naturaleza enigmática de mi respuesta. \n\nLlevamos un registro, pero puede que no lo utilicemos para ubicar a esas almas. Solo el Propósito es capaz de revelar si los hilos de un alma vuelven a conformar el entramado de una existencia individual.', '', 38134),
(189335, 'esMX', '$UBienvenido:Bienvenida;, Surcafauces. ¿Te interesa conocer lo que registra el sinoescriba?', '', 38134),
(189328, 'esMX', '', 'Vas muy bien, $p. La Casa de los Ojos observa tus acciones… y las aprueba.', 38134),
(189318, 'esMX', '¡Asistentes, listos para el lanzamiento!', '', 38134),
(189317, 'esMX', '', 'Una última vez. ¡Carga!', 38134),
(189315, 'esMX', '', 'Buen trabajo. Cargar aumentará tu ira y dañará a tus enemigos. ¡Úsala siempre para iniciar una pelea!', 38134),
(189314, 'esMX', '', 'Vuelve a cargar contra mí para acortar la distancia. ¡Siempre empieza tu ataque con Cargar!', 38134),
(189313, 'esMX', '', 'Los ataques normales no son muy útiles solos. ¡Usa Embate!', 38134),
(189312, 'esMX', '', '¡Carga hacia mí!', 38134),
(189311, 'esMX', '', 'Embate consumirá parte de tu ira para infligir daño. ¡Puedes usar Embate siempre que tengas suficiente ira!', 38134),
(189310, 'esMX', '', 'Me carcome el remordimiento cuando veo el estado de mi hogar.\n\nPero no ganaré nada con lamentarme. Solo puedo vengarme para remediar esta situación.', 38134),
(189308, 'esMX', '', '¡Bien! Un guerrero siempre carga hacia el combate. Te da la ira que necesitas para usar otras habilidades.', 38134),
(189306, 'esMX', '', 'Usa Cargar para comenzar la batalla. ¡No tengas piedad con el enemigo!', 38134),
(189305, 'esMX', '', 'Mefiles. Qué gusto tenerte con nosotros.', 38134),
(189298, 'esMX', '', 'Tienes cinco puntos de combo. ¡Usa Eviscerar para terminar con esto!', 38134),
(189297, 'esMX', '', 'Concéntrate en obtener cinco puntos de combo antes de usar Eviscerar.', 38134),
(189296, 'esMX', '', 'Nada mal, pero Eviscerar es mucho más poderoso con más puntos de combo. Inténtalo de nuevo.', 38134),
(189295, 'esMX', 'Recuperar Festus es lo más importante. No pierdas tiempo.', '', 38134),
(189294, 'esMX', '¡Vamos, $n! Quién sabe lo que hacen los Rechazados en mi forja. ¡No deben mancillar a mi Festus también!', '', 38134),
(189292, 'esMX', 'Puedes llevarte la lanza. Vive para servir.', '', 38134),
(189291, 'esMX', '', '¡Excelente! Eviscerar consume todos tus puntos de combo. ¡Usa cinco puntos de combo con tu último Eviscerar!', 38134),
(189290, 'esMX', '', '¡Tienes cuatro puntos de combo! ¡Hora de usar Eviscerar!', 38134),
(189289, 'esMX', '', '¡Usa Golpe siniestro tan seguido como puedas para obtener puntos de combo!', 38134),
(189288, 'esMX', '', 'El cazador se convierte... en presa.', 38134),
(189287, 'esMX', '', '¡Bien! Eviscerar inflige más daño según tus puntos de combo. Inténtalo de nuevo, pero esta vez con cuatro puntos de combo.', 38134),
(189286, 'esMX', '', 'No dejaremos rastro de esta resistencia. ¡En el nombre del amo!', 38134),
(189285, 'esMX', '', '¡Tienes tres puntos de combo! ¡Usa Eviscerar ahora!', 38134),
(189283, 'esMX', '', 'Los ensamblajes revuelven el cadáver de mi casa en desgracia. Pero un cadáver siempre puede levantarse.', 38134),
(189281, 'esMX', '', 'Golpe siniestro aumenta tus puntos de combo. ¡Usa Eviscerar cuando tengas al menos tres puntos de combo!', 38134),
(189277, 'esMX', 'Ven, Bron. ¡Disfrutemos los frutos del trabajo!', '', 38134),
(189276, 'esMX', 'Informaré a la Arconte. Descansa hasta que decidamos los próximos pasos.', '', 38134),
(189274, 'esMX', 'Este día, damos un paso fundamental para restaurar las costumbres de los kyrian.', '', 38134),
(189273, 'esMX', '', 'Nos vengaremos de todos los que han traicionado a Maldraxxus.', 38134),
(189266, 'esMX', '', 'Bien. Ya puedes enfrentar a los jabaespines. Recuperemos a nuestra gente.', 38134),
(189265, 'esMX', '', '¡Tienes suficiente poder sagrado para terminar con esto! ¡Usa Escudo del honrado!', 38134),
(189264, 'esMX', '', 'Ya empiezas a entender. Sigue usando Golpe de cruzado para obtener poder sagrado.', 38134),
(189262, 'esMX', '', 'Han capturado a tu amigo Haetio y me entregaste la Lanza de Calíope. Debo darte las gracias.', 38134),
(189261, 'esMX', '', 'Ya no necesitas ese disfraz. Sé quien eres, Surcafauces.', 38134),
(189257, 'esMX', 'Así parece. Debemos confiar en que ese es el Propósito.', 'Así parece. Debemos confiar en que ese es el Propósito.', 38134),
(189256, 'esMX', 'Sinoescriba, ¿es cierto que las Fauces se apoderan de cada alma nueva que llega?', 'Sinoescriba, ¿es cierto que las Fauces se apoderan de cada alma nueva que llega?', 38134),
(189255, 'esMX', '¿Preocuparnos? No. Ellos también deben cumplir con el Propósito, al igual que nosotros.', '¿Preocuparnos? No. Ellos también deben cumplir con el Propósito, al igual que nosotros.', 38134),
(189254, 'esMX', 'Sinoescriba. Los mortales llegan en números cada vez mayores. ¿Deberíamos preocuparnos?', 'Sinoescriba. Los mortales llegan en números cada vez mayores. ¿Deberíamos preocuparnos?', 38134),
(189253, 'esMX', 'El Propósito lo gobierna todo. Aunque en ocasiones parezca oscuro, debemos confiar en su naturaleza infalible.', 'El Propósito lo gobierna todo. Aunque en ocasiones parezca oscuro, debemos confiar en su naturaleza infalible.', 38134),
(189252, 'esMX', 'Sinoescriba, ¿cómo sabemos si esto cumple con el Propósito?', 'Sinoescriba, ¿cómo sabemos si esto cumple con el Propósito?', 38134),
(189251, 'esMX', 'Me temo que no hay nada que ninguno de nosotros pueda hacer por el momento. Debemos confiar en el Propósito.', 'Me temo que no hay nada que ninguno de nosotros pueda hacer por el momento. Debemos confiar en el Propósito.', 38134),
(189250, 'esMX', '¿Cómo puedo ayudarte, sinoescriba?', '¿Cómo puedo ayudarte, sinoescriba?', 38134),
(189248, 'esMX', 'El Propósito es nuestra vida.', 'El Propósito es nuestra vida.', 38134),
(189247, 'esMX', 'Por desgracia, no. Las almas siguen fluyendo hacia las Fauces. Aunque escapa a nuestra comprensión, debe ser el Propósito.', 'Por desgracia, no. Las almas siguen fluyendo hacia las Fauces. Aunque escapa a nuestra comprensión, debe ser el Propósito.', 38134),
(189246, 'esMX', 'Sinoescriba. Los Sobrestantes quieren saber si ha habido algún cambio.', 'Sinoescriba. Los Sobrestantes quieren saber si ha habido algún cambio.', 38134),
(189244, 'esMX', 'Un solo golpe. ¡Usa todo el poder que tienes!', '', 38134),
(189242, 'esMX', '', '¡Bien! ¡Ya no tienes poder sagrado, así que usa Golpe de cruzado para generar más!', 38134),
(189241, 'esMX', '', '¡Tienes suficiente poder sagrado para volver a usar Escudo del honrado!', 38134),
(189240, 'esMX', '', '¡Bien! ¡Sigue usando Golpe de cruzado para acumular poder sagrado!', 38134),
(189238, 'esMX', 'En definitiva, cada criatura tiene un uso.', '', 38134),
(189237, 'esMX', '', 'Buen trabajo. Un Paladín hábil acumula poder sagrado rápidamente y lo usa para dañar a sus enemigos.', 38134),
(189236, 'esMX', '', '¡Rebosas de poder sagrado! ¡Ahora usa Escudo del honrado!', 38134),
(189235, 'esMX', 'Puede que aprendas algunas cosas trabajando para mí... o no.', '', 38134),
(189234, 'esMX', '', 'Golpe de cruzado aumenta tu poder sagrado. ¡Lo necesitarás para tus habilidades más potentes!', 38134),
(189231, 'esMX', '', 'Los ataques normales no son muy útiles solos. ¡Usa Golpe de cruzado!', 38134),
(189230, 'esMX', 'Un enemigo... digno...', 'Un enemigo... digno...', 38134),
(189229, 'esMX', 'Nada... mal...', 'Nada... mal...', 38134),
(189228, 'esMX', '¿Eso... es todo... lo que tienes...?', '¿Esto... es todo... lo que tienes...?', 38134),
(189227, 'esMX', 'Im... posible...', 'Im... posible...', 38134),
(189226, 'esMX', 'No... puedo... perder...', 'Pero... No... puedo... perder...', 38134),
(189225, 'esMX', 'Eso es... trampa...', 'Eso es... trampa...', 38134),
(189224, 'esMX', 'Pero... cómo...', 'Pero... cómo...', 38134),
(189223, 'esMX', '¡No quedará nada de ti!', '¡No quedará nada de ti!', 38134),
(189222, 'esMX', '¡Inclínate ante mi poder!', '¡Inclínate ante mi poder!', 38134),
(189221, 'esMX', '¡Caerás en el olvido!', '¡Caerás en el olvido!', 38134),
(189220, 'esMX', '¡Te mostraré la verdadera fuerza!', '¡Te mostraré la verdadera fuerza!', 38134),
(189218, 'esMX', '', 'Bueno, ¡veamos qué sabes hacer!', 38134),
(189215, 'esMX', 'Mata a todos los que quieras. No te diré nada.', '', 38134),
(189209, 'esMX', 'Los fragmentos nunca deben desecharse. Sirven para reparar casi cualquier espejo roto.', '', 38134),
(189208, 'esMX', 'No siempre se sabe con certeza a dónde te llevará el espejo, pero cualquier lugar es mejor que este.', '', 38134),
(189207, 'esMX', '¡Cada parte de tu cuerpo está en peligro!', '¡Cada parte de tu cuerpo está en peligro!', 38134),
(189206, 'esMX', '¡Prepárate para perder!', '¡Prepárate para perder!', 38134),
(189205, 'esMX', '¡Que empiece la batalla!', '¡Que comience la batalla!', 38134),
(189204, 'esMX', '', '¡Lo intentaré!', 38134),
(189203, 'esMX', 'La Luz... se va...', 'La Luz... se va...', 38134),
(189202, 'esMX', 'Tus huesos arderán hasta ser ceniza.', 'Tus huesos arderán hasta ser ceniza.', 38134),
(189201, 'esMX', '', 'No dejará que nada obstaculice su camino. ¡Debemos luchar hasta llegar a ella!', 38134),
(189200, 'esMX', '¡Los Rechazados fueron expulsados por completo! Regresemos.', '', 38134),
(189195, 'esMX', '', 'Alguna vez fuiste amo y señor del terror. ¿Y ahora? Tus dientes han perdido su terror.', 38134),
(189158, 'esMX', '¿Yo desafío a Revendreth? ¡Yo SOY Revendreth!', '', 38134),
(189154, 'esMX', '¿Ah, sí? Lo veremos. ¡Acechamiedos! Prepara a tus cazadores.', '', 38134),
(189149, 'esMX', '', 'Incluso tus castigos revelan tu complacencia. Muy bien, ocúltame tal como haces con tus errores.', 38134),
(189148, 'esMX', 'Quizás un par de siglos ardiendo en el Distrito de Brasas te recuerden quién gobierna este lugar.', '', 38134),
(189146, 'esMX', '', 'Yo te acuso a TI, Denathrius, de traicionar tu cargo. TÚ eres el que desafía la voluntad de Revendreth.', 38134),
(189145, 'esMX', 'Una mala decisión. Se te acusa de desafiar la voluntad de Revendreth.', '', 38134),
(189144, 'esMX', '¡Amo! ¡Amo! ¡La criatura ha llegado!', '', 38134),
