INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(209968, 'zhTW', '', '歡迎，朋友。你對鑰石有興趣嗎？', 38134),
(209048, 'zhTW', '在奧睿博司找到侵略者佐達許，他能滿足你的需求。你的誓盟聖所內也會有物品升級專家。', '在奧睿博司找到侵略者佐達許，他能滿足你的需求。你的誓盟聖所內也會有物品升級專家。', 38134),
(208581, 'zhTW', '「使用者名稱」你好。希望你今天過得「隨機產生正面形容詞」。', '', 38134),
(208300, 'zhTW', '我非常樂意能幫助這些年輕的靈魂。波賓很快就能痊癒了！', '', 38134),
(208299, 'zhTW', '所謂近朱者赤，近墨者黑。只要遵循嚴格的禮儀，還有名字有個「賓」字，任何無賴都能變得彬彬有禮！', '', 38134),
(208298, 'zhTW', '控罪者才剛把我派給希奧塔。他正在教我怎麼改掉過去的錯誤行為！', '', 38134),
(208297, 'zhTW', '這杯茶加入了一些新的食材，全都是波賓的點子。希望你喜歡！', '', 38134),
(208292, 'zhTW', '親愛的$p，你好。\n\n<波賓看著寫在手背上的草稿。>\n\n很高興認識你。我聽希奧塔說了很多關於你的事。\n\n需要來杯茶嗎？還是你比較想要一把好用的遮陽傘？\n\n<波賓在等你回覆，努力保持自然的笑容。>', '', 38134),
(208211, 'zhTW', '$p，我之前的主人，墓穴守衛者凱希爾在贖罪之殿等你。', '', 38134),
(208209, 'zhTW', '$p，塔賓斯在餘燼區找你。', '', 38134),
(208205, 'zhTW', '$p，葛雷希特在贖罪之殿找你。', '', 38134),
(208120, 'zhTW', '有些日子非常特別！看到我這個黃金寶箱，就代表是特別的日子！在特別的日子裡，我們會向一位侍者致敬。\n\n問我是哪位侍者，送他一個成熟的普恩果。回來就可以打開寶箱！喔耶！', '', 38134),
(208019, 'zhTW', '我們的一份子。我們的一份子。我們的一份子…', '', 38134),
(208018, 'zhTW', '軍政官阿德雷特斯邊哼著音樂，邊踏著舞步。', '', 38134),
(208016, 'zhTW', '', '不管你在哪裡，我都會盯著你。', 38134),
(208015, 'zhTW', '好朋友會給我好紙張！', '', 38134),
(208014, 'zhTW', '', '好多朋友，好開心。西卡好幸運。', 38134),
(208013, 'zhTW', '嗯…$g他:她;是我的好朋友也是學徒…好友徒？好學友？不對，不對…', '', 38134),
(208012, 'zhTW', '團結力量大，願我們永不分離。', '', 38134),
(208011, 'zhTW', '', '如果死亡能讓我們相遇，又有什麼力量能拆散我們？', 38134),
(208010, 'zhTW', '大家都閉嘴，聽$p的。你們所有人加起來都沒比我最好的朋友更了解戰鬥！', '', 38134),
(208009, 'zhTW', '', '$p，這棵樹在森林裡能生長。快點回到我的林地，這樣我們兩個才能茁壯。', 38134),
(208008, 'zhTW', '丘發慢慢地開始輕聲吟唱。', '', 38134),
(208007, 'zhTW', '大家都說，$p是泥掘者最好的朋友！', '', 38134),
(208006, 'zhTW', '我們現在有很多朋友了，但最好的朋友只有一個…', '', 38134),
(208005, 'zhTW', '我其實蠻期待下一場餘燼宴會的。我甚至還可能會接受貴族的跳舞邀請…', '', 38134),
(208004, 'zhTW', '', '總會有下一場狩獵，但絕對不會有下一個$p。', 38134),
(208002, 'zhTW', '', '恭喜啊，美麗的$p。你的宴會跟你一樣令人驚豔。', 38134),
(208000, 'zhTW', '', '$p，我們接下來會一起經歷什麼樣的冒險？我等不及了！', 38134),
(207999, 'zhTW', '', '我可愛的$p，你怎麼還在這裡。', 38134),
(207996, 'zhTW', '$p，保重。總會有拯救不完的靈魂，但是也別忘了已經逝去的那些。', '', 38134),
(207951, 'zhTW', '錯誤。使用者需取得更多經驗值才能解鎖此功能。', '', 38134),
(207923, 'zhTW', '$n，亞登曠野還有很多事等著我們去完成。', '$n，亞登曠野還有很多事等著我們去完成。', 38134),
(207903, 'zhTW', '', '你想聽個故事嗎？', 38134),
(207841, 'zhTW', '$n！老兄，這裡啦！', '', 38134),
(207504, 'zhTW', '今天順風，很適合飛行！你想去哪裡？', '', 38134),
(207499, 'zhTW', '', '<伊琳卡女士露出微笑。>\n\n你問了很重要的問題。每週調整賓客名單是明智之舉。如此才能確保賓客彼此相處融洽。\n\n與加瑞登勛爵交談查看賓客名單，就能取消對任何一位賓客的邀請。\n\n我們可能會需要花費一些灌能紅寶石，安撫因名單更動受傷的情緒。但你可以招募大使或從我手中購買空白邀請函來填補這些開支。\n\n還有啊，盡量不要同時邀請喜歡相反氛圍的賓客。那樣會很難讓他們兩個都感覺滿意。', 38134),
(207446, 'zhTW', '結團是很獨特的傀儡。她對暗影之境有無可動搖的責任心。她在瑪卓薩斯為不死軍團完成工作時可以發現特殊的護甲。那些護甲象徵我們在暗影之境的使命。', '結團是很獨特的傀儡。她對暗影之境有無可動搖的責任心。她在瑪卓薩斯為不死軍團完成工作時可以發現特殊的護甲。那些護甲象徵我們在暗影之境的使命。', 38134),
(207442, 'zhTW', '', '我是知道，但等你把你的朋友送回奧睿博司我再告訴你。\n\n等確保他安全了再回來找我。', 38134),
(207434, 'zhTW', '希奧塔，你在那邊嗎？', '', 38134),
(207388, 'zhTW', '', '你想要什麼嗎？', 38134),
(207376, 'zhTW', '可惜你認為繁文縟節比力量還重要，淵喉行者。', '可惜你認為繁文縟節比力量還重要，淵喉行者。', 38134),
(207372, 'zhTW', '淵喉行者，暗夜妖精能有像你如此無畏的勇士為他們效勞，應該感到很榮幸。', '淵喉行者，暗夜妖精能有像你如此無畏的勇士為他們效勞，應該感到很榮幸。', 38134),
(207371, 'zhTW', '看來曠野需要你的效勞，$n。這是崇高的使命。', '看來曠野需要你的效勞，$n。這是崇高的使命。', 38134),
(207370, 'zhTW', '雖然你並未加入我們的誓盟，但是你為偉大的生命循環而效力，這的確是崇高的職責。', '雖然你並未加入我們的誓盟，但是你為偉大的生命循環而效力，這的確是崇高的職責。', 38134),
(207369, 'zhTW', '請為我們向御靈者最誠摯的盟友，凜冬女王獻上崇敬的問候。', '請為我們向御靈者最誠摯的盟友，凜冬女王獻上崇敬的問候。', 38134),
(207368, 'zhTW', '願你在你所選擇的道路上，能從…收割之中，獲得滿足，並且履行你的使命，$n。', '願你在你所選擇的道路上，能從…收割之中，獲得滿足，並且履行你的使命，$n。', 38134),
(207367, 'zhTW', '淵喉行者。願你能導引頑強的靈魂解脫束縛。', '淵喉行者。願你能導引頑強的靈魂解脫束縛。', 38134),
(207366, 'zhTW', '汎希爾族瞭解靈魂背負的過往是沉重的負擔。我們的誓盟也抱持相同的看法，$n。', '汎希爾族瞭解靈魂背負的過往是沉重的負擔。我們的誓盟也抱持相同的看法，$n。', 38134),
(207365, 'zhTW', '你選擇為汎希爾族效力？引導靈魂踏上贖罪之路是一項崇敬的任務。', '你選擇為汎希爾族效力？引導靈魂踏上贖罪之路是一項崇敬的任務。', 38134),
(207364, 'zhTW', '無論你如今的義務為何，請切記…莫忘職責。', '無論你如今的義務為何，請切記…莫忘職責。', 38134),
(207363, 'zhTW', '無論加入那個誓盟，御靈者都會眷顧著你。', '無論加入那個誓盟，御靈者都會眷顧著你。', 38134),
(207362, 'zhTW', '希望你能為戰亂不休的界域帶來紀律，$n。', '希望你能為戰亂不休的界域帶來紀律，$n。', 38134),
(207361, 'zhTW', '我認為比起征戰，你更適合為世間效勞，淵喉行者。或許我的看法錯了。', '我認為比起征戰，你更適合為世間效勞，淵喉行者。或許我的看法錯了。', 38134),
(207360, 'zhTW', '很榮幸能有你為我們的誓盟效勞，淵喉行者。', '很榮幸能有你為我們的誓盟效勞，淵喉行者。', 38134),
(207359, 'zhTW', '我很高興見到我們的道路交會了，$n。我們很感激你的付出。', '我很高興見到我們的道路交會了，$n。我們很感激你的付出。', 38134),
(207358, 'zhTW', '喔，淵喉行者！想必你正在為御靈者效力。莫忘職責。', '喔，淵喉行者！想必你正在為御靈者效力。莫忘職責。', 38134),
(207357, 'zhTW', '我們很幸運有你的加入，$n。願御靈者的恩典指引你的道路。', '我們很幸運有你的加入，$n。願御靈者的恩典指引你的道路。', 38134),
(207356, 'zhTW', '等你陪那些妖精玩膩了之後，歡迎你隨時回來看看。', '等你陪那些妖精玩膩了之後，歡迎你隨時回來看看。', 38134),
(207355, 'zhTW', '淵喉行者，請幫忙向凜冬女王傳達我們的祝福。', '淵喉行者，請幫忙向凜冬女王傳達我們的祝福。', 38134),
(207354, 'zhTW', '我們的森林或許沒有亞登曠野的林地那麼壯觀，但是我們也會為了維護我們界域而戰。', '我們的森林或許沒有亞登曠野的林地那麼壯觀，但是我們也會為了維護我們界域而戰。', 38134),
(207353, 'zhTW', '暗夜妖精不會彼此競爭力量嗎？那他們在那些粗野的林地都做些什麼呢？', '暗夜妖精不會彼此競爭力量嗎？那他們在那些粗野的林地都做些什麼呢？', 38134),
(207352, 'zhTW', '幸好你加入了我們的誓盟，$n。我認為天鵝絨的衣飾穿在你身上一定會很好看。', '幸好你加入了我們的誓盟，$n。我認為天鵝絨的衣飾穿在你身上一定會很好看。', 38134),
(207351, 'zhTW', '我與收割者廷衛同在，與你也是，淵喉行者。', '我與收割者廷衛同在，與你也是，淵喉行者。', 38134),
(207350, 'zhTW', '你的罪孽石上肯定刻了不平凡的行徑！', '你的罪孽石上肯定刻了不平凡的行徑！', 38134),
(207349, 'zhTW', '$n，有了你的協助，就能重振我們崇高的使命。', '$n，有了你的協助，就能重振我們崇高的使命。', 38134),
(207348, 'zhTW', '我敢打賭在所有的誓盟當中，琪瑞安族的罪孽石一定最平凡無趣。', '我敢打賭在所有的誓盟當中，琪瑞安族的罪孽石一定最平凡無趣。', 38134),
(207347, 'zhTW', '琪瑞安，瑞文崔斯或許是個幽暗的地方，但是我們的職責就跟你們的一樣崇高。', '琪瑞安，瑞文崔斯或許是個幽暗的地方，但是我們的職責就跟你們的一樣崇高。', 38134),
(207346, 'zhTW', '', '謝謝你…很高興知道我最後的努力並沒有白費。', 38134),
(207345, 'zhTW', '你那些侍者看來受過良好的訓練，$n。你也知道，我們有些墓穴需要好好打掃一番…', '你那些侍者看來受過良好的訓練，$n。你也知道，我們有些墓穴需要好好打掃一番…', 38134),
(207343, 'zhTW', '雖然手段不同，但是我們雙方誓盟都在試圖引導靈魂踏上崇高的道路。', '雖然手段不同，但是我們雙方誓盟都在試圖引導靈魂踏上崇高的道路。', 38134),
(207342, 'zhTW', '你選擇踏上了殘暴的道路，凡人。要是你想學習更高明的手段，你知道哪裡可以找到我們。', '你選擇踏上了殘暴的道路，凡人。要是你想學習更高明的手段，你知道哪裡可以找到我們。', 38134),
(207341, 'zhTW', '', '藩侯或許已經不在了，但這並不表示我們的努力都白費了。等適合的時機來臨時，我們一定會復仇的。\n\n等等…你…你來自艾澤拉斯。告訴我，我的訊息有傳達給未冕者了嗎？', 38134),
(207340, 'zhTW', '餘燼宴會其實比你想的還要危險。不過，我想你一定會喜歡它的。', '餘燼宴會其實比你想的還要危險。不過，我想你一定會喜歡它的。', 38134),
(207339, 'zhTW', '幸好有你的誓盟為守護我們的界域而戰，淵喉行者。', '幸好有你的誓盟為守護我們的界域而戰，淵喉行者。', 38134),
(207338, 'zhTW', '', '藩侯或許已經不在了，但這並不表示我們的努力都白費了。等適合的時機來臨時，我們一定會復仇的。', 38134),
(207337, 'zhTW', '把你的解剖刀和針線拿開，死靈領主。我可不喜歡皮肉分離的模樣。', '把你的解剖刀和針線拿開，死靈領主。我可不喜歡皮肉分離的模樣。', 38134),
(207335, 'zhTW', '你走過的地方會留下一道痕跡，$n。那是…妖精的光輝嗎？', '你走過的地方會留下一道痕跡，$n。那是…妖精的光輝嗎？', 38134),
(207334, 'zhTW', '淵喉行者！你對照顧林地感到厭倦了嗎？想來場真正的戰鬥嗎？', '淵喉行者！你對照顧林地感到厭倦了嗎？想來場真正的戰鬥嗎？', 38134),
(207333, 'zhTW', '比起策略，死靈領主更喜歡使用詭計呢，暗夜妖精。', '比起策略，死靈領主更喜歡使用詭計呢，暗夜妖精。', 38134),
(207332, 'zhTW', '不。我不想跟你一起表演。', '不。我不想跟你一起表演。', 38134),
(207331, 'zhTW', '要讓靈魂捨棄傲慢的最好方法，就是狠狠揍他們一頓。', '要讓靈魂捨棄傲慢的最好方法，就是狠狠揍他們一頓。', 38134),
(207330, 'zhTW', '汎希爾人，小心你的腳步。你的鞋子會踩到軟泥的。', '汎希爾人，小心你的腳步。你的鞋子會踩到軟泥的。', 38134),
(207329, 'zhTW', '派對…陰謀。那些汎希爾人為什麼不乾脆打一架算了？', '派對…陰謀。那些汎希爾人為什麼不乾脆打一架算了？', 38134),
(207328, 'zhTW', '沒錯，我的日子裡充滿了戰爭…但這是屬於我自己的回憶。', '沒錯，我的日子裡充滿了戰爭…但這是屬於我自己的回憶。', 38134),
(207327, 'zhTW', '琪瑞安，你在這裡可找不到冥思池。', '琪瑞安，你在這裡可找不到冥思池。', 38134),
(207326, 'zhTW', '來吧。讓我看看你在昇靈堡學到的優雅戰鬥招式。', '來吧。讓我看看你在昇靈堡學到的優雅戰鬥招式。', 38134),
(207324, 'zhTW', '我是想邀請你到苦痛劇場，但是你的衣服可能會弄髒。', '我是想邀請你到苦痛劇場，但是你的衣服可能會弄髒。', 38134),
(207323, 'zhTW', '等你凡人的軀體用壞了之後，我們可以為你打造新的。', '等你凡人的軀體用壞了之後，我們可以為你打造新的。', 38134),
(207321, 'zhTW', '你傳承了統御者的精神，$n。', '你傳承了統御者的精神，$n。', 38134),
(207320, 'zhTW', '幸好你選擇加入了我們，$n。其他的誓盟都會羨慕你的力量的。', '幸好你選擇加入了我們，$n。其他的誓盟都會羨慕你的力量的。', 38134),
(207319, 'zhTW', '無論前方的命運是什麼，都會透過戰鬥而來。', '無論前方的命運是什麼，都會透過戰鬥而來。', 38134),
(207318, 'zhTW', '老格利夫塔從來沒見過這種地方！\n\n<格利夫塔對你揮了揮手，靠向你耳邊低語。>\n\n噓！小聲點！\n\n這些仲介者會向我們這些「凡人」提出各種好東西來交易，而我一直想知道到底有哪些好東西。\n\n也許老格利夫塔可以找到一些新的護符來賣，對嗎？嘿。', '', 38134),
(207316, 'zhTW', '喔，另一位來自艾澤拉斯的靈魂。\n\n不，不是靈魂。安希的光芒依然照耀著你的軀體。\n\n希望我能親眼再見高嶺最後一次。要是大地之母允許，說不定我的家人還在那裡。\n\n這裡許多人都一心渴望晉升，並且放不下過往的遺憾，但我只要緬懷那些我所愛之人的回憶，就能感到心滿意足。我希望他們也惦記著我，雖然有一天我必須放下他們。\n\n但不會是今天。不，絕不會是今天。', '', 38134),
(207315, 'zhTW', '我認得你。我們曾在高嶺並肩而戰。你看起來風采依舊。\n\n我希望庫菈和孩子們並未哀傷太久，而且庫菈能受到妥善的照顧。不過，我還是希望能保有部分的回憶。與庫菈鼻碰鼻、為二世取名字、教麥卡怎麼帶著刀子像鬼魂般行動。像鬼魂般…\n\n我不記得當時的命運是如何結束的，只記得一切發生的太快了。達古爾後來還好嗎？\n\n不。不，別告訴我。那是上輩子的牽掛。這種憤怒，這種痛苦…就是琪瑞安想讓我們卸下的回憶重擔。', '', 38134),
(207314, 'zhTW', '汎希爾人，你喜歡永恆的暮光嗎？', '汎希爾人，你喜歡永恆的暮光嗎？', 38134),
(207313, 'zhTW', '我永遠都無法瞭解汎希爾人為什麼對石頭那麼感興趣。', '我永遠都無法瞭解汎希爾人為什麼對石頭那麼感興趣。', 38134),
(207312, 'zhTW', '死靈領主，你得隨時保持警戒。', '死靈領主，你得隨時保持警戒。', 38134),
(207311, 'zhTW', '統御者的盟友就是森林的盟友。', '統御者的盟友就是森林的盟友。', 38134),
(207310, 'zhTW', '侍奉誓盟是我們的職責。', '侍奉誓盟是我們的職責。', 38134),
(207309, 'zhTW', '琪瑞安，願我們的風能承載你的翅膀。', '琪瑞安，願我們的風能承載你的翅膀。', 38134),
(207308, 'zhTW', '暗夜之廷很榮幸能有你的加入，$n。', '暗夜之廷很榮幸能有你的加入，$n。', 38134),
(207307, 'zhTW', '我們很感激你與我們誓盟同在，淵喉行者。為了女王和林地。', '我們很感激你與我們誓盟同在，淵喉行者。為了女王和林地。', 38134),
(207306, 'zhTW', '你在這裡找不到高傲的靈魂，汎希爾人。', '你在這裡找不到高傲的靈魂，汎希爾人。', 38134),
(207305, 'zhTW', '曠野獵者與你同在，死靈領主。', '曠野獵者與你同在，死靈領主。', 38134),
(207304, 'zhTW', '勇往直前，琪瑞安。', '勇往直前，琪瑞安。', 38134),
(207302, 'zhTW', '暗夜擁抱你，汎希爾人。不過，我想早就如此了。', '暗夜擁抱你，汎希爾人。不過，我想早就如此了。', 38134),
(207301, 'zhTW', '死靈領主，你會在森林中待很久嗎？', '死靈領主，你會在森林中待很久嗎？', 38134),
(207300, 'zhTW', '祝你好運，琪瑞安。我希望昇靈堡能安然度過這場乾旱。', '祝你好運，琪瑞安。我希望昇靈堡能安然度過這場乾旱。', 38134),
(207299, 'zhTW', '林地欠你一份情，$n！', '林地欠你一份情，$n！', 38134),
(207298, 'zhTW', '汎希爾人，想不到會在這裡遇見你。真的是太意外了。', '汎希爾人，想不到會在這裡遇見你。真的是太意外了。', 38134),
(207297, 'zhTW', '那是什麼怪味？喔，是死靈領主。難怪。', '那是什麼怪味？喔，是死靈領主。難怪。', 38134),
(207295, 'zhTW', '喔，你是琪瑞安的一員！雖然沒有翅膀…不過，我可不一樣！', '喔，你是琪瑞安的一員！雖然沒有翅膀…不過，我可不一樣！', 38134),
(207294, 'zhTW', '喔，是$n！我超愛你的表演！', '喔，是$n！我超愛你的表演！', 38134),
(207289, 'zhTW', '想當初我再一年就能拿到我的頭盔了…\n\n<泰勒上將嘆了口氣，雖然他已經不需要呼吸。>', '', 38134),
(207288, 'zhTW', '你不用客氣。', '', 38134),
(207287, 'zhTW', '我記得是你殺死我的。', '', 38134),
(207286, 'zhTW', '那你呢，寇爾提拉？你還記得你死亡時發生的事嗎？', '', 38134),
(207285, 'zhTW', '多想無益。已經發生的事情是不能改變的。我們只能不斷前進，就像死亡是無可避免的。', '', 38134),
(207284, 'zhTW', '瞭解。你覺得審斷者會為我們選擇哪一種命運？', '', 38134),
(207283, 'zhTW', '就算有，我也不記得了。', '', 38134),
(207282, 'zhTW', '薩沙理安，你還記得你死亡的那一刻嗎？琪瑞安有來引導你的靈魂嗎？', '', 38134),
(207281, 'zhTW', '你說的沒錯。', '', 38134),
(207280, 'zhTW', '或是希瓦娜斯‧風行者。', '', 38134),
(207279, 'zhTW', '例如，吉恩‧葛雷邁恩？', '', 38134),
(207278, 'zhTW', '伯瓦爾‧弗塔根已經不再是巫妖王了。要是我們得聽從別人的號令，結果可能會更糟糕。', '', 38134),
(207277, 'zhTW', '而如今我們的刀刃卻為了戴著它的人而揮舞。', '', 38134),
(207276, 'zhTW', '統御之盔已經粉碎了。真是難以想像，長久以來，它一直是我們的詛咒和職責的象徵。', '', 38134),
(207272, 'zhTW', '你知道我是怎麼死的嗎，$n？當時我正在防守奎爾薩拉斯，聽命於遊俠將軍希瓦娜斯‧風行者。\n\n她為了拯救王國和人民而付出了她的生命，也因此而復生為不死生物。我也是如此，不過我們卻從此踏上了不同的道路。\n\n希瓦娜斯造成的苦難和折磨，遠比我們生平所見的還要多。在我為了減少傷亡而達成暫時休戰的協議後，她把我關在幽暗城的深處許多年。\n\n就連黯刃騎士團也無法說服她放了我。不管別人對女妖之王的看法如何…但是無論生前或死後，她都是個冷酷無情的人。\n\n希瓦娜斯從來就不是能輕易擊敗的對手。', '', 38134),
(207271, 'zhTW', '我首次執起刀劍是在不死瘟疫散播到羅德隆的時候。在之後的歲月中，我擔任士兵的時間，在死後遠比生前還要多。\n\n比起我在死亡帷幕後見到的力量，生者的戰爭根本微不足道。我發誓要效忠聯盟，在我最終滅亡之前，我都會為聯盟的人民而戰。但是足以影響聯盟存亡的，還有遠比部落更危險的威脅。\n\n或許他們也意識到了，在寒冰皇冠上方被撕裂的天空，以及步步逼進的死亡，這些都是生者的詛咒。\n\n直到大限將至的那一刻，他們才能瞭解真正重要的事物。', '', 38134),
(207270, 'zhTW', '我想要重一點的傀儡！當我跳起來落地的時候，我的重量要能踩裂地面。', '', 38134),
(207269, 'zhTW', '我想要有堅固的架構，還有很多可以掛東西的鉤子。我會帶著你，還有我所有的研究資料，一起展開許多精彩的冒險！', '', 38134),
(207268, 'zhTW', '', '<費莉亞對你說悄悄話。>\n\n請不要告訴艾瑪淵喉的事。\n\n我…我擔心她的心臟會受不了。', 38134),
(207266, 'zhTW', '', '喔，你好。你看起來對這附近很熟悉。\n\n你有看到我的兒子，傑姆、傑克和傑鉻嗎？他們應該就在這附近。\n\n我在找到他們之前不會離開這裡。我不能再搞丟他們了！', 38134),
(207251, 'zhTW', '我榮耀那些與我們同在之人。', '我榮耀那些與我們同在之人。', 38134),
(207250, 'zhTW', '<史坦尼寇斯急著打斷你。>\n\n你不能使用它們。我才剛…借來的！', '<史坦尼寇斯急著打斷你。>\n\n你不能使用它們。我才剛…借來的！', 38134),
(207248, 'zhTW', '<史坦尼寇斯看著你。>\n\n你的東西。你碰過的東西。是的。', '<史坦尼寇斯看著你。>\n\n你的東西。你碰過的東西。是的。', 38134),
(207247, 'zhTW', '你是說…我的收藏嗎？看起來很棒，對吧？', '你是說…我的收藏嗎？看起來很棒，對吧？', 38134),
(207241, 'zhTW', '不！你是逃不了的，維娜里！你…啊！', '', 38134),
(207231, 'zhTW', '', '什麼？', 38134),
(207230, 'zhTW', '淵喉行者，我學會了坐而言不如起而行。在這裡，我們都必須證明我們的意圖。', '', 38134),
(207229, 'zhTW', '當首領倒下，追隨者也會跟著倒下…就算審判官也不例外。', '', 38134),
(207228, 'zhTW', '', '加希吉的贖罪之路通往許多黑暗的深淵…你在那邊會發現什麼？', 38134),
(207227, 'zhTW', '', '你的王國和部族在這裡沒有一席之地。捨棄你對舊權力的忠誠，獲得解脫吧！', 38134),
(207226, 'zhTW', '', '你的凡人力量在這裡毫無意義。敲響鐘聲，看看你實際上有多脆弱。', 38134),
(207225, 'zhTW', '', '擁抱你羞恥的過往。走過你所經歷的每一段沉重、可怕的回憶。坦然接受，你才能克服它！', 38134),
(207224, 'zhTW', '', '無論你是否願意，你的不義之舉都會受到責難。你只能選擇是否能從中學到經驗。', 38134),
(207223, 'zhTW', '', '什麼？', 38134),
(207222, 'zhTW', '', '什麼？', 38134),
(207221, 'zhTW', '是的，我想你一定很意外會在瑞文崔斯裝滿暴君或王室成員的牢籠裡找到像我這樣的人。\n\n我只是個沉默、不起眼的靈魂。\n\n事實上，我做的事根本不值一提。\n\n不過，我說的話卻是另一回事。\n\n我的低語曾經說服過無數的人，犯下許多對他們有害，卻有利於我的暴行。\n\n看來汎希爾對這種煽動他人的行為並不寬容。', '', 38134),
(207220, 'zhTW', '而傳說的淵喉行者，想要我這個卑微的靈魂做什麼呢？', '', 38134),
(207219, 'zhTW', '<衛瑞穆笑了。>\n\n我少做了什麼嗎，凡人？\n\n我還沒徹底相信這些汎希爾所聲稱的罪行都真的錯了，但光是我們都同意的罪行，就刻滿了我罪孽石的前後兩面。\n\n<衛瑞穆沉思了一會。>\n\n我…我很感激管理員潘洽耐心地照護我。我見過其他管理員是怎麼對待他們負責的靈魂。\n\n我真的在改進我自己。', '', 38134),
(207218, 'zhTW', '凡人，你是獨自來到這塊死亡之地嗎？你必須更加小心…\n\n我曾經以為我的身邊不需要朋友。我以為朋友會成為我的弱點，被有心人所利用。\n\n我在這裡體會了這個想法有多麼的荒謬。', '', 38134),
(207213, 'zhTW', '', '加希吉竊取東西…失去東西…遺忘東西…\n\n沒有人會想起…直到…\n\n加希吉走得太遠…忘記了不該遺忘的規則…\n\n加希吉犯下許多錯誤…', 38134),
(207212, 'zhTW', '', '加希吉希望獲得自由，就像凡人一樣…\n\n逃離這裡…逃離過往…', 38134),
(207210, 'zhTW', '我有時候會後悔做出了那隻石惡魔…', '', 38134),
(207209, 'zhTW', '而且就像我跟你說的：根本沒有『罪孽石使者』這種東西。', '', 38134),
(207208, 'zhTW', '這又不是比賽，雷梅特…', '', 38134),
(207207, 'zhTW', '我們走著瞧…罪孽石使者再一次獲勝了！', '', 38134),
(207206, 'zhTW', '我想看老特梅爾這麼做。這就是我當罪孽石使者的原因！', '', 38134),
(207205, 'zhTW', '主人，我這禮拜幫你找到了一大堆的罪孽石碎片！\n\n請打開箱子瞧一瞧。', '', 38134),
(207203, 'zhTW', '', '要敲響儀式之鐘就必須做出犧牲。\n\n每個儀式之鐘都會要求敲響的靈魂付出某種代價。要是使用的方法錯誤，就像這座殿堂中的墮落者一樣，只會遭受無謂的折磨。\n\n誓復者的明智之處在於，他們會確認每個靈魂在漫長的贖罪之路上需要犧牲什麼，然後說服他們為了自己著想，而做出必要的犧牲。\n\n這並非一向都那麼順利，有些太頑固、或是眼光短淺的靈魂總是不願敲響儀式之鐘。\n\n但是你，能看見其中的真理：為了進展而犧牲，並不算真正的犧牲。', 38134),
(207202, 'zhTW', '', '現在沒有靈魂準備要進行儀式，因此我們目前還不需要儀式之鐘。\n\n費恩和誓復者的其他文獻管理員在用不到儀式之鐘的時候會把它們藏好，以免被殿堂中墮落的祭儀師給破壞了。\n\n當靈魂準備就緒時，我會叫泥掘者把我們需要的儀式之鐘拿出來。應該不久後就會用上了。', 38134),
(207200, 'zhTW', '', '喔，這是我最喜歡的：順服之鐘。\n\n它只會要求借用你部分的力量。\n\n沒錯，這的確很簡單。但對於生前透過暴力和戰鬥而取得成功的高傲靈魂來說，這卻是個可怕的代價。\n\n一旦這些靈魂捨棄了自身的力量，他們就能像以往一樣，隨心所欲地戰鬥。\n\n但是，在虛弱的狀態下，他們會不斷在戰鬥中落敗，直到意志崩潰，最終願意屈服於原本比他們還弱的對手。\n\n此時，儀式才算圓滿完成。', 38134),
(207199, 'zhTW', '', '淵喉行者，你會以同理心來看待他人嗎？\n\n並非所有人都是如此。羞恥之鐘能夠幫助那些不願意，或者無法以同理心來看待他們過往行為對其他人帶來的後果，讓他們能以更具體的方式來面對。\n\n羞恥之鐘會把你最羞恥的行為展現在所有人面前。接著我們會釋放墓穴中抱著懷恨之心的靈魂，讓他們責問這些羞恥的靈魂，逼他們面對以往犯下的過錯。\n\n要經歷這個過程是很困難的，但是卻能幫助到需要的人。', 38134),
(207198, 'zhTW', '', '責難之鐘是我們最重要的懲罰工具之一，專門適用於那些最頑固的靈魂，這能夠時常提醒他們該遵循這裡的規範。\n\n敲響鐘聲之後，首先你會責難自己，但這並不是它主要目的。我們有其他更適合自我反省的工具。\n\n更重要的是，鐘聲會使你在面對其他人的責難時更加脆弱，讓所有的汎希爾對你的教誨更有影響力。\n\n顯然，由於目前殿堂中的墮落汎希爾，這也會相對比較危險。因此當我照護的靈魂受到鐘聲的影響時，我都會密切地監督他們。', 38134),
(207197, 'zhTW', '', '認罪之鐘是簡單但強大的工具，能夠協助那些接受他們所犯下罪行的靈魂。\n\n只有願意認罪的靈魂能夠敲響它，而這麼做會讓其認罪的鐘聲響徹殿堂。\n\n公開承認罪行能夠幫助靈魂面對他們犯下的錯誤，同時警告那些墮落的汎希爾，這座殿堂存在的意義。', 38134),
(207196, 'zhTW', '', '大多數人目前都被監禁在贖罪墓穴，為他們下一個儀式進行準備。\n\n別擔心，我把他們安置在距離宮務大臣墮落的追隨者很遠的地方。那些靈魂可以安全地在那裡反省他們所犯下的罪行。\n\n控罪者則負責看守那些墓穴不受外界的影響。', 38134),
(207195, 'zhTW', '', '從以往的進展來看，她的贖罪之路恐怕會很漫長。\n\n伊克的暴力傾向，以及她對集體知識的痛恨，使她犯下了許多需要懺悔的罪行，同時也很難找到能夠協助她進行懺悔的工具。\n\n目前我暫時讓她定期接受單獨監禁，以及能夠簡單溝通的任務，讓她學習在世時太快就放棄的事物。\n\n今天，她會協助文獻管理員收集遺失的罪孽石碎片。', 38134),
(207194, 'zhTW', '', '你為何不自己問他呢？\n\n翁葛祖羅在我的照護之下已經有很長的一段時間了，但是他終於能控制好他的言行，所說的話也值得信賴。\n\n當我突破他漫天的謊言，以及半真半假的話語後，才能成功強迫他面對自己在凡間的惡行所帶來的後果。\n\n如今，他必須透過誠善的言行，來重建他的聲望。我們不也都應當如此嗎？', 38134),
(207193, 'zhTW', '', '衛瑞穆還沒能理解他目前的危險處境！\n\n雖然他已經懺悔了許多罪行，但是他剛來到這裡的時候，背負的重擔實在太多了。我以前從來沒見過這種情況。\n\n在懺悔完最後一條罪行之前，他都無法安全擺脫淵喉的魔爪。\n\n為了讓他集中心思進行懺悔，我正在試圖讓他進行一項他熟悉的任務：摧毀對手的組織。', 38134),
(207192, 'zhTW', '', '加希吉的贖罪之路並不簡單。\n\n雖然她很快就承認犯下的錯誤，但是懺悔的進展卻十分緩慢。\n\n恐怕她會在這裡待上很長一段時間。\n\n不過，她很熱心地想為這裡盡一份心力。我經常派她協助清理那些墮落汎希爾在贖罪墓穴所留下的汙穢之物。', 38134),
(207191, 'zhTW', '', '在我的照護之下，伊夫柯克在贖罪的進展上表現得比大多數的靈魂都要好。\n\n在他一生運用其力量與智慧來欺壓弱勢的人們之後，他正在學習如何使用這些天賦來保護和拯救他人。\n\n當我照護的其他靈魂失去方向或背離正義的時候，我相信伊夫柯克會找到他們，並且指引他們回到正確的道路上。', 38134),
(207190, 'zhTW', '', '這座殿堂中的墮落汎希爾讓泥掘者也深受其害。\n\n他們在宮務大臣墮落的追隨者命令之下，被迫放下他們真正的職責，只能做些不重要的粗活。\n\n即便這個儀式之鐘是靈魂懺悔的重要工具，他們還是對壞掉的它置之不理。', 38134),
(207189, 'zhTW', '', '現在鐘修好了，我們可以繼續引導這個靈魂走上贖罪之路。\n\n你能幫忙敲響鐘聲，讓這個靈魂明白犧牲是通往贖罪最真實的道路嗎？\n\n要是我們不願意以謙卑的態度來面對犯下的錯誤，要如何才能克服我們的過失呢？', 38134),
(207175, 'zhTW', '', '每個靈魂的贖罪之路都不同，但其中也有共同之處。\n\n犧牲和受苦是達成目標的必須手段。\n\n當另一個選擇是永遠留在淵喉時，還有什麼代價會讓人卻步嗎？', 38134),
(207168, 'zhTW', '我的道路就是尋找弱者，拯救那些曾經被我危害的人。與仁慈同行，凡人。', '', 38134),
(207167, 'zhTW', '', '等泥掘者修好認罪之鐘，就輪到你去敲鐘了。', 38134),
(207163, 'zhTW', '我曾是許多事物…\n\n跟蹤牧羊人羊群的狼。\n在木筏周圍巡游的鯊魚。\n獵殺野兔的老鷹。\n\n許多生命都難逃我的毒手。', '', 38134),
(207161, 'zhTW', '你還有時間，凡人。\n\n改變你的做法，避免跟我一樣的命運。\n\n汎希爾的贖罪之路或許能找到救贖，但這實在…\n\n<這個靈魂一邊顫抖一邊沉默了下來。>', '', 38134),
(207152, 'zhTW', '', '除了我以外，其他人都死了。\n\n如你所見，淵喉是一個危險的地方。雖然結伴同行能提供安全的假象，但事實上只會讓你們成為更容易下手的目標。\n\n這就是我們慘痛的教訓。我們的人數不斷銳減，直到剩下少數幾個人，才能勉強找地方躲了起來。雖然這樣延緩了他們的獵殺…但仍不足以阻止他們。\n\n當最後只剩下我一個人的時候，我接受了殘酷的真相。只有獨自一人躲藏，才有最高的倖存機會。\n\n至少直到遇見你為止。', 38134),
(207149, 'zhTW', '', '他們要遭受這種命運的確很不幸，但是別搞錯了。他們跟淵誓者一樣，都是我們的敵人。\n\n那些仲介者從來到這裡的那一刻起，就註定了他們悲慘的下場。即使你救出了他們，也只會危害到我們兩人。\n\n我承認，這樣的確很殘酷。但這卻是能在淵喉生存的金科玉律。\n\n謹記這點，你就能無往不利。', 38134),
(207143, 'zhTW', '', '看守者卡莎蒂正在女王的栽培園等你！他們早就想讓你開始進行了。\n\n只要踏入在我旁邊的蘑菇環，就能前往栽培園了。', 38134),
(207142, 'zhTW', '', '當你在這個無盡黑暗之中，除了時間之外一無所有，故事會比黃金更有價值。', 38134),
(207119, 'zhTW', '', '喔，我相信你現在應該跟她很熟才對。要不是她從中協助你，像你這樣的凡人怎麼可能在淵喉中存活到現在。\n\n把你跟維娜里的協議全都告訴我。\n\n要是她選擇不跟你回到奧睿博司，她也必須要面對這一切。', 38134),
(207118, 'zhTW', '', '當然，當然。我還有最後一個問題。\n\n當你在淵喉的時候，維娜里是不是成為了你寶貴的盟友呢？\n\n<在回答之前，你想起維娜里特別警告你不能把你們的協議告訴任何人。>', 38134),
(207113, 'zhTW', '', '喔，所以那裡對進行商業活動來說，還是很危險的地方。這正好帶到我的下一個問題。\n\n問題二！ \n\n你覺得要在淵喉如此殘酷、充滿恐怖事物的地方存活，是否很困難呢？', 38134),
(207112, 'zhTW', '', '感謝你願意為我們提供你對淵喉的瞭解。\n\n問題一：\n\n閻獄之主的部隊不論在力量或是數量上，真的都變得更強大了嗎？', 38134),
(207111, 'zhTW', '', '你好,淵喉行者。很高興認識你！', 38134),
(207110, 'zhTW', '', '真是刺激！', 38134),
(207109, 'zhTW', '', '我不會先測試或重新同調，這樣我們就不知道啟動時會有什麼東西跑出來。', 38134),
(207108, 'zhTW', '', '等你有足夠的灌能紅寶石，我就能幫你製作更多修復工具組。', 38134),
(207107, 'zhTW', '淵喉行者，瑞文崔斯永遠不乏需要修補的鏡子。', '', 38134),
(207105, 'zhTW', '', '你可以使用我從餘燼區帶來的鏡子修復工具組。', 38134),
(207104, 'zhTW', '對面有一個破碎的鏡子，你可以用來練習。', '', 38134),
(207103, 'zhTW', '', '喔，有很多事情我都沒告訴你。就像我肯定你也有很多事情沒有告訴我一樣。\n\n信任必須建立在謹慎之上，淵喉行者，否則任何交易都無法進行。\n\n總而言之，我已經把我最有價值的秘密告訴了你：我僅有的藏身之處。我只要求你也同樣的信任我。', 38134),
(207101, 'zhTW', '', '有了你的幫助，亞登曠野就能繼續維持循環。\n\n我要將栽培園交付給你。\n\n去找看守者卡莎蒂。他會繼續指導你。', 38134),
(207100, 'zhTW', '', '萬事萬物都有終結。\n\n最後，我們卻會找到新的開始。這就是大自然的循環。\n\n我的各個林地中的靈魂對於循環至關重要。少了他們，凡人世界的大自然也會漸漸死亡。每損失一個靈魂都是莫大的悲劇。每拯救一個，都是一場勝利。\n\n你從凋亡的林地和瘋狂的佐司特手中救了這個靈魂。今天，她要從長久的睡夢中甦醒，回到她的世界。你幫助我們完成了神聖的使命。', 38134),
(207097, 'zhTW', '', '凜冬女王親自指導你野性種子的知識，確實是一項殊榮。', 38134),
(207087, 'zhTW', '', '剛才用掉了不少的冥魄…但是我想那就是做生意的代價。', 38134),
(207065, 'zhTW', '跟你相比嗎？那當然。你違背了你的合約，而且背叛了集團！現在你得付出代價！', '', 38134),
(207063, 'zhTW', '', '他們的尖叫聲沒有野豬人的動聽，但也只能將就了。', 38134),
(207012, 'zhTW', '$n？過了這麼久了，能再見到你真好。尤其是在這個奇怪的地方。\n\n當我得知暗影之境的帷幕被打破時，我就知道我一定要跨越過來。無論要承擔什麼樣的風險。\n\n你或許記得，我的妻子…因為難產而亡。不久後我也失去了其中一個兒子。我必須知道他們的靈魂有沒有平安與先祖團聚，又或是遭遇危險。\n\n求求你，如果你遇到我妻子，麗紗的靈魂，告訴她我們的孩子柯爾正日益茁壯。告訴她我愛她。永遠。\n\n告訴她無論有多艱難，我一定會找到她。', '', 38134),
(207011, 'zhTW', '能在這個奇異的地方見到艾澤拉斯的面孔，真讓人鬆一口氣。\n\n當我得知暗影之境的帷幕被打破時，我就知道我一定要跨越過來。無論要承擔什麼樣的風險。\n\n原因是…我失去了兩個最重要的人。我必須知道他們的靈魂有沒有平安與先祖團聚，又或是遭遇危險。\n\n求求你，如果你遇到我妻子，麗紗‧遠遊者的靈魂，告訴她我們的孩子柯爾正日益茁壯。告訴她我愛她。永遠。\n\n告訴她無論有多艱難，我一定會找到她。', '', 38134),
(207009, 'zhTW', '', '使用我其中一個更有趣的裝置。恐怕這會讓你相當痛苦。', 38134),
(207008, 'zhTW', '', '來吧，淵喉行者。我們還有很多事情要討論。', 38134),
(207007, 'zhTW', '', '不，維布拉克斯。你會離開淵喉的…但是你永遠也回不去集團。', 38134),
(207006, 'zhTW', '啊！你…你在…做什麼…？！', '', 38134),
(207005, 'zhTW', '', '你想得美。淵喉行者，我們除掉這個麻煩的傢伙吧。', 38134),
(207003, 'zhTW', '', '你以為他們會相信刺客所說的話嗎？', 38134),
(207002, 'zhTW', '我試著警告過你，淵喉行者。但是你聽不進去。', '', 38134),
(207000, 'zhTW', '她背叛了之前的夥伴，要是她知道我們的談話，將來也會背叛你。別相信她的謊言。', '', 38134),
(206999, 'zhTW', '在回到我的安全處所之前，我必須警告你維娜里這個人。她並不像外表所見的那麼單純。', '', 38134),
(206995, 'zhTW', '我們來到這裡希望能彌補維娜里的背叛對我們造成的損失。喔，我們真是太愚蠢了…', '', 38134),
(206988, 'zhTW', '俘虜…折磨…屈服…\n\n賽洛斯…把其他人帶去…獸穴…\n\n我們的任務…失敗了…', '', 38134),
(206984, 'zhTW', '我說過我會回來的！食言就不好了。', '', 38134),
(206983, 'zhTW', '我被可惡的巫術師囚禁了！\n\n釋放我，我來教訓他活著的價值！呃，還有死後的價值。', '', 38134),
(206981, 'zhTW', '希奧塔用上許多深夜市集的人脈才做到這樣…', '', 38134),
(206970, 'zhTW', '看來這隻蝙蝠能帶你離開贖罪之殿。', '看來這隻蝙蝠能帶你離開贖罪之殿。', 38134),
(206969, 'zhTW', '靈鑄禦盾，可攻可守的盾牌！\n\n敵人步步進逼嗎？準備絕地大反攻嗎？\n\n讓這面禦盾保障你的安全！吸收傷害，並將一些傷害反彈給你的對手！呼呼！', '', 38134),
(206968, 'zhTW', '結界之環，保障你的安全！\n\n需要留在原地，又不想讓敵人近身嗎？\n\n那簡單！啟動結界之環，敵人就無法近身！躲起來，喘口氣吧。', '', 38134),
(206967, 'zhTW', '寧靜之瓶，讓人食指大動的餐點！\n\n覺得力不從心嗎？想要提神一下嗎？\n\n寧靜之瓶會治療你，讓你繼續奮戰！這點侍者可以掛保證！', '', 38134),
(206966, 'zhTW', '妖霜之瓶，有助於保持距離！\n\n被對手逼到絕路？需要快點逃跑嗎？\n\n別害怕！摔破妖霜之瓶，把敵人凍結在原地。然後快跑！', '', 38134),
(206965, 'zhTW', '抱歉，$n！裝備不是給你的！\n\n是給你的靈魂之絆的，以便在試煉中助他們一臂之力！', '', 38134),
(206959, 'zhTW', '呵呵呵！可以幫你做一件大事，只有一件事！要等幾個小時才能再幫一次，忙忙忙！', '呵呵呵！可以幫你做一件大事，只有一件事！要等幾個小時才能再幫一次，忙忙忙！', 38134),
(206958, 'zhTW', '我能幫你忙嗎？只能幫一件事，小心選擇！要等一小時才能再幫一次，我很忙！', '我能幫你忙嗎？只能幫一件事，小心選擇！要等一小時才能再幫一次，我很忙！', 38134),
(206954, 'zhTW', '經你這樣一說，我確實看到一個壯碩的綠色傢伙被拖著經過這裡。\n\n那傢伙讓人印象深刻。他一直喊著「洛克塔」什麼什麼的。\n\n你先放了我，我會把知道的都告訴你。', '', 38134),
(206949, 'zhTW', '怎麼回事？一個活生生的凡人，跑來這裡？我一定是發瘋了！\n\n你不是真的吧。', '', 38134),
(206948, 'zhTW', '找到了，勝利之籍！$n，你不先上嗎？說不定會有死亡陷阱之類的東西。', '', 38134),
(206944, 'zhTW', '喔，太瞭解了！我曾照顧瑪拉茲莫斯好一段時間。我甚至知道一些連他都已經忘記的古老路線！$b$b我的直覺告訴我，你也知道那些隱密的地點。如果你有興趣，想回去某個地點，我可以助你一臂之力。', '喔，太瞭解了！我曾照顧瑪拉茲莫斯好一段時間。我甚至知道一些連他都已經忘記的古老路線！$b$b我的直覺告訴我，你也知道那些隱密的地點。如果你有興趣，想回去某個地點，我可以助你一臂之力。', 38134),
(206933, 'zhTW', '', '你的靈魂充斥著傲慢，凡人。需要我幫你贖罪嗎？', 38134),
(206931, 'zhTW', '她待在那裡應該比較好。', '', 38134),
(206930, 'zhTW', '雷納瑟王子將曙光要塞交給那魯管理。這裡是她的同類首次現身的地方，而且聖光盈滿各個角落。', '', 38134),
(206929, 'zhTW', '吉雷麗虛弱到無法回家，而且她天生就對汎希爾人相當危險。', '', 38134),
(206912, 'zhTW', '自然動力是被動技能，讓你在靈魂幻體形態使用閃爍時，會往你正在移動的方向傳送，而不是你面對的方向。', '自然動力是被動技能，讓你在靈魂幻體形態使用閃爍時，會往你正在移動的方向傳送，而不是你面對的方向。', 38134),
(206908, 'zhTW', '衝突大師和他那群商人朋友就在政要區那一邊。', '衝突大師和他那群商人朋友就在政要區那一邊。', 38134),
(206897, 'zhTW', '我只是過往神聖楷模深奧知識的殘影，但我看得出來你對知識的渴望。\n\n閱讀我的故事，然後我們再來討論你還能學習什麼東西。', '我只是過往神聖楷模深奧知識的殘影，但我看得出來你對知識的渴望。\n\n閱讀我的故事，然後我們再來討論你還能學習什麼東西。', 38134),
(206896, 'zhTW', '你找到柯柏利了！謝謝你！', '你找到柯柏利了！謝謝你！', 38134),
(206895, 'zhTW', '柯柏利跑去拿酒。柯柏利去了太久。沒有酒就沒辦法開始！\n\n找到柯柏利，好嗎？', '柯柏利跑去拿酒。柯柏利去了太久。沒有酒就沒辦法開始！\n\n找到柯柏利，好嗎？', 38134),
(206889, 'zhTW', '為了大地之母！', '', 38134),
(206886, 'zhTW', '願使命指引你。\n\n我保留了不少我們的紀錄。在貫徹使命的過程中，我們會保存曲折的命運之絲讓我們看到的幻象。\n\n你想看到哪個幻象？', '願使命指引你。\n\n我保留了不少我們的紀錄。在貫徹使命的過程中，我們會保存曲折的命運之絲讓我們看到的幻象。\n\n你想看到哪個幻象？', 38134),
(206878, 'zhTW', '歡迎。你想踏上哪條道路呢？', '', 38134),
(206833, 'zhTW', '', '你不能向其他人透露我的存在。你在這裡所做的事，都是出自你個人的意願。\n\n我會交給你靈魂保管水晶，它能夠存放你在淵喉找到的靈魂。\n\n要是你接受靈魂保管水晶和這些規定，那就意味著我們彼此達成了協議。\n\n要是在淵喉中發現重要情報，你必須無條件跟我分享。\n\n作為交換，我會讓你購買工具，幫助你在這裡生存，並拯救你想尋找的靈魂。\n\n我們達成協議了嗎？', 38134),
(206827, 'zhTW', '你擅長音樂嗎？會彈奏這些樂器嗎？', '你擅長音樂嗎？會彈奏這些樂器嗎？', 38134),
(206726, 'zhTW', '<選擇一個。>', '', 38134),
(206725, 'zhTW', '<關閉再開啟。>', '', 38134),
(206719, 'zhTW', '彌補粗俗和改變作風，向來都需要付出代價。不過，真正改變自己嘴賤傷人的行為，一定比透過公審讓他們負起責任來得好。', '', 38134),
(206713, 'zhTW', '罪奔者布蘭契痛苦地嘶吼，形體隨之消散。', '', 38134),
(206708, 'zhTW', '安杜因國王不在的時候，我受託保護聯盟市民。\n\n幫助年輕的瓦里安‧烏瑞恩守住他的王位，感覺像是很久以前的事了。現在我又臨危受命，要幫他兒子鎮守這裡。\n\n第四次大戰讓聯盟付出慘痛的代價。我們的軍事實力大不如前。暴風城的貴族希望能快點重建整個防禦體制。\n\n奪回激流堡是很好的第一步。也許舊聯盟還有其他失地值得收復。', '', 38134),
(206707, 'zhTW', '部落正面臨許多挑戰，許多危險。\n\n但我們有個願意帶領我們前進的議會，我很榮幸能參與其中。\n\n沃金是有始以來最好的大酋長…也是一個好朋友。我只希望讓部落永遠記得他為我們所做的犧牲，讓他的事蹟永遠流傳下去。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206706, 'zhTW', '', '我們花了好幾個月，找遍艾澤拉斯每一個角落，尋找希瓦娜斯的蹤影，最後才發現我的姐妹早已踏足死者的國度。\n\n<艾蘭里亞瞇起眼睛。>\n\n真適合她。\n\n儘管我很渴望一起去追捕她，不過這裡現在需要我。聯盟必須再次強大起來，我會幫助圖拉揚達成這個目標。\n\n不過，等到時機到來，我會要希瓦娜斯為她的所作所為負責。', 38134),
(206705, 'zhTW', '路已經開啟了。你現在可以進入瑪卓薩斯了。', '', 38134),
(206704, 'zhTW', '道路重新開啟了。你的使命就是為我們帶來這個奇蹟嗎？', '', 38134),
(206703, 'zhTW', '路已經開啟了。你現在可以進入亞登曠野了。', '', 38134),
(206701, 'zhTW', '路已經開啟了。你現在可以進入瑞文崔斯了。', '', 38134),
(206692, 'zhTW', '歡迎，卡薛爾。那是真的嗎？我們要再次打開大門嗎？', '', 38134),
(206686, 'zhTW', '', '動盪的特性能造成極大傷害，但一次只能作用於一名敵人。', 38134),
(206685, 'zhTW', '動盪的特性能造成極大傷害，但一次只能作用於一名敵人。', '', 38134),
(206684, 'zhTW', '痛苦動盪會從死亡的敵人產生靈魂裂片，所以務必施放在你會首先殺死的敵人身上。', '', 38134),
(206683, 'zhTW', '', '痛苦動盪會從死亡的敵人產生靈魂裂片，所以務必施放在你會首先殺死的敵人身上。', 38134),
(206682, 'zhTW', '', '痛苦動盪是強大的法術，能造成持續性傷害。', 38134),
(206681, 'zhTW', '痛苦動盪是強大的法術，能造成持續性傷害。', '', 38134),
(206679, 'zhTW', '<使用同調裂片灌注時，這個裝置可以為你開啟塔內隱藏的道路。>', '', 38134),
(206677, 'zhTW', '沒問題，淵喉行者。', '', 38134),
(206668, 'zhTW', '', '這座永恆之城璀璨耀眼、無比美麗。然而死亡的影響卻無所不在。\n\n我也受到那股力量的影響。也許奧睿博司這裡的秘密能幫助我瞭解那代表什麼意義。\n\n侍從用深信不疑的口吻談論著他們的「使命」。也許他們也可以幫我找到我的使命。', 38134),
(206667, 'zhTW', '', '暗影之境，令人驚豔…但是說真的，也有點嚇人。\n\n不過我會克服恐懼，繼續幫助我的朋友。\n\n我也會繼續瞭解我原以為早已失去的父親。', 38134),
(206666, 'zhTW', '我們都知道，閻獄之主正在盯著我們，也知道安杜因在他的手上。\n\n我們的唯一希望就是靠你和其他英雄讓誓盟變得強大，$n。\n\n閻獄之主和希瓦娜斯一定很快就會揭露他們下一階段的計畫。\n\n到時候，我們需要集眾人之力，才能對抗他們。', '', 38134),
(206629, 'zhTW', '他真的很常這樣。大概是沒翅膀的關係吧。', '他真的很常這樣。大概是沒翅膀的關係吧。', 38134),
(206628, 'zhTW', '凡人，你常常跳上跳下的。沒有翅膀應該很傷關節。', '凡人，你常常跳上跳下的。沒有翅膀應該很傷關節。', 38134),
(206627, 'zhTW', '確實沒錯，但我跟著搖擺就好了…', '確實沒錯，但我跟著搖擺就好了…', 38134),
(206626, 'zhTW', '你知道…你很常跳來跳去嗎？', '你知道…你很常跳來跳去嗎？', 38134),
(206625, 'zhTW', '既然如此，幹嘛不等到明天再說？', '既然如此，幹嘛不等到明天再說？', 38134),
(206624, 'zhTW', '幫忙？凡人，我們去搶奪一些對你有用的東西吧。', '幫忙？凡人，我們去搶奪一些對你有用的東西吧。', 38134),
(206623, 'zhTW', '這…這顆石頭！它會帶你走向毀滅，凡人！', '這…這顆石頭！它會帶你走向毀滅，凡人！', 38134),
(206622, 'zhTW', '別讓他誘惑你！', '別讓他誘惑你！', 38134),
(206621, 'zhTW', '踏上正義之路永遠不嫌晚！', '踏上正義之路永遠不嫌晚！', 38134),
(206620, 'zhTW', '凡人，你今天打算幫誰的忙？', '凡人，你今天打算幫誰的忙？', 38134),
(206619, 'zhTW', '別聽他亂說。只要團結一致，任何事物都阻止不了你的隊伍！', '別聽他亂說。只要團結一致，任何事物都阻止不了你的隊伍！', 38134),
(206618, 'zhTW', '有你保護，你的盟友就能走得更遠！', '有你保護，你的盟友就能走得更遠！', 38134),
(206617, 'zhTW', '願你的武器只制裁那些罪有應得的人！', '願你的武器只制裁那些罪有應得的人！', 38134),
(206616, 'zhTW', '很公平的一戰。願他們能夠死得其所！', '很公平的一戰。願他們能夠死得其所！', 38134),
(206615, 'zhTW', '這很可能是誤解…', '這很可能是誤解…', 38134),
(206614, 'zhTW', '衝突不是最佳的解決方法。你有先跟他們講道理嗎？', '衝突不是最佳的解決方法。你有先跟他們講道理嗎？', 38134),
(206613, 'zhTW', '多一樁罪又會怎樣？走嘛！', '多一樁罪又會怎樣？走嘛！', 38134),
(206612, 'zhTW', '我們今天能造成什麼麻煩呢？', '我們今天能造成什麼麻煩呢？', 38134),
(206611, 'zhTW', '想一下，如果你沒浪費掉那個法術，就可以對你的敵人施放大規模毀滅法術了！', '想一下，如果你沒浪費掉那個法術，就可以對你的敵人施放大規模毀滅法術了！', 38134),
(206610, 'zhTW', '你知道，他很可能不會為你做一樣的事…', '你知道，他很可能不會為你做一樣的事…', 38134),
(206609, 'zhTW', '公平，哈！你把他踩在腳下！', '公平，哈！你把他踩在腳下！', 38134),
(206608, 'zhTW', '沒錯！幹掉他。現在換那邊那個！', '沒錯！幹掉他。現在換那邊那個！', 38134),
(206607, 'zhTW', '講道理！用你的武器跟他講道理！', '講道理！用你的武器跟他講道理！', 38134),
(206606, 'zhTW', '以牙還牙啊。解決他！', '以牙還牙啊。解決他！', 38134),
(206605, 'zhTW', '亞當納斯一邊按住側腰，一邊露出痛苦的奇怪表情。', '', 38134),
(206604, 'zhTW', '淵喉行者，拜託你，我需要…治療…', '', 38134),
(206603, 'zhTW', '雖然會痠痛一陣子，但我應該還能飛起來…謝謝你。', '', 38134),
(206590, 'zhTW', '卷軸、卷軸、卷軸。\n\n噢、呼、你好啊！', '卷軸、卷軸、卷軸。\n\n噢、呼、你好啊！', 38134),
(206588, 'zhTW', '架子上少了很多卷軸！你有找到不見的卷軸嗎？有嗎？', '架子上少了很多卷軸！你有找到不見的卷軸嗎？有嗎？', 38134),
(206579, 'zhTW', '時間苦短，我們必須盡快進攻，不然戴納瑟斯會發現我在這裡。\n\n但是此刻馬上採取行動是件愚蠢的行為。我在淵喉受的傷尚未痊癒，你又還不夠強，無法勝任我的計畫。\n\n淵喉行者，去讓自己變強吧，在暗影之境學習成長。做你該做的，吸取更多此地的相關經驗。等你準備好的時候，回來找我，然後面對治理死亡的其中一名統治者。', '', 38134),
(206576, 'zhTW', '你可以在城市外圍的平台找到傳送門。', '你可以在城市外圍的平台找到傳送門。', 38134),
(206575, 'zhTW', '你會在塑物大廳找到所有提供工匠服務的仲介者。', '你會在塑物大廳找到所有提供工匠服務的仲介者。', 38134),
(206571, 'zhTW', '森林滋養萬物。', '森林滋養萬物。', 38134),
(206564, 'zhTW', '命運與時間彼此交會。在使命的牽動下，它們的道路可能會偏折、重塑。\n\n凡人，有股偉大的力量影響著你的靈魂。在其影響之下，我可能會改變你的命運…指出前進的道路。\n\n一旦你選擇踏上道路，暗影之境的居民就會一視同仁地看待你，就像過去行遍他們國度的其他人一樣。\n\n每個誓盟都會歡迎你，期盼你加入他們的行列。無論你選擇哪個誓盟，你都必須幫助他們變得強大。\n\n但是，只要你選擇了道路，就沒有後悔的餘地。\n\n一切操之在你。', '', 38134),
(206562, 'zhTW', '我當然願意當她的靈魂之絆。她是我最親愛的夥伴，也是我願意共度這永恆餘生的人。', '我當然願意當她的靈魂之絆。她是我最親愛的夥伴，也是我願意共度這永恆餘生的人。', 38134),
(206561, 'zhTW', '我再次感謝你的協助。\n\n<她向右邊輕輕微笑。>\n\n她說好。', '我再次感謝你的協助。\n\n<她向右邊輕輕微笑。>\n\n她說好。', 38134),
(206560, 'zhTW', '我會直接回應她。謝謝你，我很肯定你明白這一刻有多重要。', '我會直接回應她。謝謝你，我很肯定你明白這一刻有多重要。', 38134),
(206559, 'zhTW', '$n，她問我能不能當她的靈魂之絆。', '$n，她問我能不能當她的靈魂之絆。', 38134),
(206558, 'zhTW', '如果從珍重的人那裡得到花環，象徵的意義非凡。', '如果從珍重的人那裡得到花環，象徵的意義非凡。', 38134),
(206551, 'zhTW', '$n，眼前有兩條路可以走。\n\n像你先前的其他凡人一樣，展開你的旅程。沒有人知道你的命運，或你在使命扮演的角色。\n\n或是讓時間與天意構成的經緯引領你前進。誓盟會認識你，你可以選擇自己效忠的對象。\n\n死亡領域將會向你開放，你的命運也將掌握在自己手中。\n\n做出抉擇吧。', '$n，你有兩個選擇。\n\n我可以讓事情保持原狀。大家看到你在這裡會很驚訝，你會重新認識新朋友。\n\n或者，我可以讓你的時間往前進。\n\n你可以立刻加入誓盟，然後開始在暗影之境四處冒險！\n\n你決定好了嗎？', 38134),
(206550, 'zhTW', '天意已現。使命需要你的貢獻。', '', 38134),
(206539, 'zhTW', '奧睿博司的仲介者允許我們黯黑堡的盟友建立通連兩端的傳送門。你可以在法師塔最上層的傳送室找到傳送門。', '奧睿博司的仲介者允許我們黯黑堡的盟友建立通連兩端的傳送門。你可以在法師塔最上層的傳送室找到傳送門。', 38134),
(206534, 'zhTW', '', '不確定的時候，殺光就對了。', 38134),
(206532, 'zhTW', '', '你知道我殺過多少對手嗎？', 38134),
(206530, 'zhTW', '儘管費盡千辛萬苦，不過我們一些黯黑堡的盟友與奧睿博司的仲介者合作，成功建立連通兩端的傳送門。你可以在傳送室的主樓層找到傳送門。', '儘管費盡千辛萬苦，不過我們一些黯黑堡的盟友與奧睿博司的仲介者合作，成功建立連通兩端的傳送門。你可以在傳送室的主樓層找到傳送門。', 38134),
(206528, 'zhTW', '', '這個地方讓我有非常不祥的預感，我渾身都在打顫，而且不時有詭異的聲響激發我想要迅速離開的本能反應。\n\n但是我必須堅定意志。我是昇靈堡的晉升者，我的職責就是要找出潛在的威脅。這不僅是為了捍衛我們的理念，更是為了守護整個暗影之境。', 38134),
(206527, 'zhTW', '一般來說，不會派志士來執行這樣的任務。\n\n我必須承認，這麼快就離開昇靈堡，讓我有點緊張。不過有你和克蕾雅在身邊，相信我一定能從這趟旅程學到不少東西。', '', 38134),
(206526, 'zhTW', '', '我發誓會貫徹昇靈堡的使命，遵循我們的道路。\n\n汎希爾和琪瑞安可以說是南轅北轍，不過我相信我們能找到共同點的。', 38134),
(206525, 'zhTW', '', '女伯爵掐住瑞文崔斯每位貴族的脈搏，不論過去或現在。\n\n對她而言，情報就是力量。她可不會輕易拱手讓出。', 38134),
(206524, 'zhTW', '', '嗨唷，$n！\n\n時空旅行是很危險的事。等你再累積一點點經驗，再展開新的冒險吧！', 38134),
(206523, 'zhTW', '', '我們所做的一切不只是為了侍奉御靈者，更是為了服務無數現實世界的人民。', 38134),
(206522, 'zhTW', '', '嘿，$n！\n\n關於時光漫遊，你必須先了解現在的世界才能回到過去。\n\n等你熟悉這座城市並跟大酋長會面後，或許我可以送你回去！', 38134),
(206521, 'zhTW', '', '<克蕾雅閉上雙眼，露出擔憂的神情。\n\n她的呼吸變得綿長，努力攫取她所希望知道的真相。>', 38134),
(206520, 'zhTW', '', '艾澤拉斯真的很美。與昇靈堡截然不同。我很期待親眼見識這個世界的人如何生活。', 38134),
(206519, 'zhTW', '', '無數紀元來，我訓練不輟，就是為了這一天。\n\n只要再完成一個任務，再通過一場試煉。\n\n我就能實現晉升的心願。', 38134),
(206512, 'zhTW', '<文獻管理員賈尼拉完全沒抬頭看你，專心閱讀她面前的卷軸。>\n\n有事嗎？', '<文獻管理員賈尼拉完全沒抬頭看你，專心閱讀她面前的卷軸。>\n\n有事嗎？', 38134),
(206511, 'zhTW', '沒錯，$p，每次你想獵殺另一個審判官的時候，就把罪孽石碎片交給我。', '沒錯，$p，每次你想獵殺另一個審判官的時候，就把罪孽石碎片交給我。', 38134),
(206510, 'zhTW', '我向你保證，我真的對你感激不盡，謝謝你幫我們除掉宮務大臣的審判官。', '我向你保證，我真的對你感激不盡，謝謝你幫我們除掉宮務大臣的審判官。', 38134),
(206509, 'zhTW', '抱歉。經常有人說我給人一種冷淡、不知感恩的感覺。', '抱歉。經常有人說我給人一種冷淡、不知感恩的感覺。', 38134),
(206508, 'zhTW', '文獻管理員費恩大聲咳嗽。', '文獻管理員費恩大聲咳嗽。', 38134),
(206507, 'zhTW', '贖罪之殿的文獻管理員費恩隨時都能幫你製造審判官罪孽石…只要你有碎片的話。', '', 38134),
(206505, 'zhTW', '當然不是百分百確定。\n\n你查過村莊外圍的樹林了嗎？在翼靈蛇入侵之前，我曾看到志士在那裡看書。', '當然不是百分百確定。\n\n你查過村莊外圍的樹林了嗎？在翼靈蛇入侵之前，我曾看到志士在那裡看書。', 38134),
(206496, 'zhTW', '塔賓斯一直在做粗活。取水很簡單！', '', 38134),
(206493, 'zhTW', '你在找的水就在那上面…\n\n如果你沒辦法取到水，就去找在那裡的我。\n\n我隨時在附近…而且通常很樂意幫忙…', '', 38134),
(206492, 'zhTW', '再見了，年輕人…記得好好努力…還有敬老尊賢…', '', 38134),
(206491, 'zhTW', '屏住呼吸…縮好小腹…\n\n出發…', '', 38134),
(206490, 'zhTW', '在你取完水後，我可以送你回大陸。', '', 38134),
(206489, 'zhTW', '你在找的水就在那上面。', '', 38134),
(206488, 'zhTW', '做好準備…第一次使用我的菌絲網旅行的人…都會頭暈。', '', 38134),
(206487, 'zhTW', '讓我見識一下你的真面目…你很努力工作嗎？', '', 38134),
(206486, 'zhTW', '年輕人，別衝動…我不會隨隨便便就讓陌生人穿梭於我的菌絲網。', '', 38134),
(206483, 'zhTW', '我透過我的菌絲網看到了你的努力。\n\n只要有我在的地方，我都看得見…你在哪裡都能看到我。\n\n你的確很努力工作。', '', 38134),
(206482, 'zhTW', '你們這些年輕人，老是活蹦亂跳，老是吵吵鬧鬧…\n\n你們這次又在吵什麼啊…', '', 38134),
(206479, 'zhTW', '大家，好好休息一下。明天又要展開新的狩獵。', '', 38134),
(206478, 'zhTW', '沒錯。', '', 38134),
(206477, 'zhTW', '', '世上沒有比曠野獵者更強大的東西！', 38134),
(206476, 'zhTW', '', '糾纏的樹根會更為強大。', 38134),
(206475, 'zhTW', '', '這次巡邏讓我學了不少。很榮幸能跟你們一起狩獵。', 38134),
(206474, 'zhTW', '', '那是一場很有收穫的狩獵。你的技術越來越好了，盧美拉。', 38134),
(206473, 'zhTW', '吞噬者！', '', 38134),
(206472, 'zhTW', '', '所以才讓人覺得奇怪。', 38134),
(206471, 'zhTW', '', '沒…我什麼也…', 38134),
(206470, 'zhTW', '', '等等。你聽到了嗎？', 38134),
(206469, 'zhTW', '現在森林裡卻到處都有他們的蹤影。真是棘手。', '', 38134),
(206468, 'zhTW', '', '結果居然是真的。不過曠野獵者已經很久沒有跟他們交戰了。', 38134),
(206467, 'zhTW', '', '我過去曾以為佐司特是神話生物…只是用來嚇年輕妖精的。', 38134),
(206466, 'zhTW', '', '這是應該的，西卡。祝你玩得開心。', 38134),
(206465, 'zhTW', '', '他們一定會後悔踏上亞登曠野的土地！', 38134),
(206464, 'zhTW', '佐司特來了！曠野獵者，準備迎戰！', '', 38134),
(206463, 'zhTW', '', '西卡…有很多好朋友。西卡…會去派對！', 38134),
(206462, 'zhTW', '', '西卡躊躇了一下，隨即露出大大的笑容。', 38134),
(206461, 'zhTW', '', '去$p那裡渡個假，享受餘燼宴會吧。你不在的時候，一切都交給我們會吧。', 38134),
(206460, 'zhTW', '你幫我們做了很多事，這次換我們幫你了。', '', 38134),
(206459, 'zhTW', '沒錯，西卡。我們完全不知道你的感受如此強烈。', '', 38134),
(206458, 'zhTW', '很有真正獵人的氣魄。', '', 38134),
(206457, 'zhTW', '', '我們不會讓那種事發生。無論盛衰，曠野獵者都會保護森林。', 38134),
(206456, 'zhTW', '如果這場乾旱不快點結束，整個亞登曠野都會變得跟特那希一樣。', '', 38134),
(206454, 'zhTW', '<這個百衛的靈魄似乎耗盡了。>', '', 38134),
(206453, 'zhTW', '', '而且還有更多出現了！', 38134),
(206452, 'zhTW', '', '為了特那希淚如雨下。靈魄少得可憐，果姆卻多到不行。', 38134),
(206445, 'zhTW', '淵喉行者，你需要什麼協助嗎？\n\n在輪到我去處理這些靈魂之前，我有一點時間。', '淵喉行者，你需要什麼協助嗎？\n\n在輪到我去處理這些靈魂之前，我有一點時間。', 38134),
(206444, 'zhTW', '列斯弗嚴肅地點點頭。', '', 38134),
(206443, 'zhTW', '', '特那希沒有難民。這個知識我們都得來不易。', 38134),
(206442, 'zhTW', '', '是幻象…我竟然被騙了。', 38134),
(206441, 'zhTW', '盧美拉，不！退後！', '', 38134),
(206440, 'zhTW', '', '快看，有難民！他們需要我們的幫助！', 38134),
(206439, 'zhTW', '', '我很期待出席他的宴會。', 38134),
(206438, 'zhTW', '', '你原本可以選擇挑戰他們當中最弱的人。但你卻選了他們當中最強的人。', 38134),
(206437, 'zhTW', '', '想要吸引像你這樣的盟友，雷納瑟王子得提供更多好處。', 38134),
(206436, 'zhTW', '$p，我們已經是最好的朋友了，對吧？\n\n我到底要求你幾次，你才願意從你的誓盟幫我走私一些泥掘者到瑪卓薩斯？\n\n對，我是趁你不注意的時候，把宴會的一些泥掘者液化了，不過，想像一下，要是我有實驗室的全套設備，一定能進行更多實驗。\n\n或許最終還能回答每個人心中的一大問題：泥掘者是軟泥構成的嗎？\n\n這絕對是辦得到的！', '<西卡在和你說話把原本手上在整理的文件放了下來。>\n\n$p！你是好的驚喜。\n\n大多數的時候，驚喜不是什麼好事。打斷計劃，造成更多工作。\n\n西卡一直在想下一場餘燼宴會，希望西卡能受邀。\n\n有好多事要做，但是好朋友永遠會幫西卡的忙。\n\n西卡有很好的好朋友。', 38134),
(206435, 'zhTW', '原來是我後來跑去辦宴會的前任學徒啊！\n\n我一直很想對下一場餘燼宴會提出一些建議。\n\n醜話先說在前頭，我的建議都需要用到大量的軟泥。\n\n不過，有些軟泥的味道真的很不錯！', '', 38134),
(206434, 'zhTW', '$p，你跳槽這件事情讓我很困擾。\n\n我從來沒有學徒最後居然跑去籌辦宴會。\n\n太離經叛道了…\n\n凱文似乎充滿想法，不過，我猜我不會妨礙他的進步。\n\n對了，下一場餘燼宴會是什麼時候？', '', 38134),
(206433, 'zhTW', '是$p！凱文，先暫時…把那瓶實驗性藥水收起來。', '', 38134),
(206432, 'zhTW', '$p，小心點，不要踩到我最新的實驗。我可不想失去沒有你。', '', 38134),
(206431, 'zhTW', '我是不是聽見某個蠢萌學徒走過來的腳步聲？', '', 38134),
(206430, 'zhTW', '$p，我正好跟其他人提到你，還有你那個暗影之境「第二好」的娛樂活動。\n\n對了，下一場餘燼宴會是什麼時候？我已經答應一些有潛力的新參賽者，下次會帶他們一起去。', '', 38134),
(206429, 'zhTW', '$p，小傢伙。留下來跟我一起看比賽吧。\n\n不管你為什麼而來，我都能滿足你的心願。\n\n當你有個像沃爾這樣的朋友，就沒必要走遍整個瑪卓薩斯。', '', 38134),
(206428, 'zhTW', '淵喉行者，你的餘燼宴會能比得上這種壯觀的場面嗎？\n\n你會在下一場宴會叫阿茲莫格或那個大傢伙厄茲過來表演踩爛蛋糕嗎？\n\n<沃爾指了指下面龐大的熔火巨人。>\n\n我不建議這麼做。我覺得弱不禁風的汎希爾應該無法應付他。', '', 38134),
(206427, 'zhTW', '快來看表演，$p。我幫你留了位置，就在我旁邊。', '', 38134),
(206426, 'zhTW', '讓出一條路來，我們的勇士來了。$p！', '', 38134),
(206425, 'zhTW', '接下來換誰上場？淵喉行者，該輪到你了吧？', '', 38134),
(206424, 'zhTW', '付錢沒什麼好丟臉的。不是每個人都適合競技場。', '', 38134),
(206422, 'zhTW', '$p，你是到了暗影之境才第一次學習領導別人嗎？\n\n我是因為你的一些舉動，才有這樣的懷疑。很少有人一下子就能扮演好領導者的角色。\n\n我在宴會上聽到其他賓客在說長道短，認為你的一些決策並不高明。搞得像是整個暗影之境都繞著他們轉一樣。\n\n別被他們的閒言閒語擊倒了。\n\n做好規劃，帶我們走向勝利。讓他們心服口服。', '', 38134),
(206421, 'zhTW', '淵喉行者，看看下面那些士兵。\n\n他們強壯、堅定又忠心。\n\n但是還不夠。無論規模大或小，每支軍隊都必須聰明、狡猾又驍勇。\n\n我很想知道雷納瑟王子到底有什麼長處，如果他有，又會如何運用這些長處。\n\n下一場餘燼宴會是什麼時候？我向你保證，我一定會抽空參加，這樣我才能跟王子多聊一點。', '', 38134),
(206420, 'zhTW', '啊，$p。我親愛的淵喉行者今天過得如何啊？', '', 38134),
(206419, 'zhTW', '朋友，暗影之境各地的戰況如何？', '', 38134),
(206418, 'zhTW', '淵喉行者，隨時歡迎你光臨。', '', 38134),
(206417, 'zhTW', '我會帶著武鬥士梅維斯，以及我底下最優秀的兩名士兵一起去。我相信你那邊一定能夠容納這麼多人。', '', 38134),
(206413, 'zhTW', '', '小$p，老是匆匆忙忙的。\n\n這不是抱怨。我其實相當享受這種互動模式。\n\n你不會浪費我的時間，我也不會辜負你的努力。\n\n這次你又需要我幫什麼忙？\n\n偵察？暗殺？宴會企劃建議？\n\n間諜總管隨時為你效勞。', 38134),
(206411, 'zhTW', '', '經歷了無數歲月，我已經看過很多王子舉行宴會，淵喉行者。\n\n就算在最好的條件下，結果往往也是不盡理想。\n\n你的雷納瑟如果能多聽一點忠言，少聽一點讒言，一定能做得更好。\n\n一場宴會總少不了這兩種聲音。你覺得我會是那一邊呢？\n\n<瓦許女爵露出狡猾的笑容。>', 38134),
(206410, 'zhTW', '稍息。$p是我自己的朋友。', '$p…是最近少數我想在營地看到的人。', 38134),
(206409, 'zhTW', '$p，英雄之陵這裡一切安好。歡迎回來。', '啊，宴會的主人終於回來我們瑪卓薩斯這個小角落了。', 38134),
(206408, 'zhTW', '淵喉行者，你好。', '卡莉克，看來你的觀察蠻正確的，淵喉行者過來了。', 38134),
(206407, 'zhTW', '有人需要我就提供服務，而這裡有人需要我。', '$p，好好挑選獻殷勤的對象…你絕不希望最後居然跟一隻怪物結盟。', 38134),
(206406, 'zhTW', '', '森林會幫忙。', 38134),
(206405, 'zhTW', '確實。問題在於那個人是誰。', '', 38134),
(206404, 'zhTW', '', '木魈的攻擊通常不會這麼有條理。一定有人在指揮他們。', 38134),
(206403, 'zhTW', '你為什麼沒把這封信交給軍政官阿德雷特斯？', '', 38134),
(206402, 'zhTW', '小心！', '', 38134),
(206401, 'zhTW', '', '是木魈！正好拿來當作靶子！數量這麼少，真不過癮…', 38134),
(206400, 'zhTW', '', '別在意札金，盧美拉。比起言語，他更重視行動。', 38134),
(206399, 'zhTW', '', '就是那樣。你只需要把我們的敵人打得落花流水！', 38134),
(206398, 'zhTW', '列斯弗沉默地點點頭。', '', 38134),
(206396, 'zhTW', '我們會安排一下。列斯弗，你當前鋒。', '', 38134),
(206395, 'zhTW', '', '長官，我做好準備了。一點挑戰都沒有的事物未免太無趣。', 38134),
(206394, 'zhTW', '盧美拉，提高警覺，對於新手獵人來說，這次巡邏非常危險。', '', 38134),
(206393, 'zhTW', '$p，我完全沒想過餘燼宴會可以這麼吸引人。說真的，這場宴會可是你的一大壯舉。\n\n喝著一杯接一杯的茶，暗影之境隨之團結在一起了。\n\n當然了，這背後的工夫不是這麼簡單，但還是…有誰想得到呢？', '', 38134),
(206392, 'zhTW', '淵喉行者，你需要一個可以休息一陣子的地方嗎？\n\n精疲力盡的話可沒辦法拯救暗影之境喔。', '', 38134),
(206391, 'zhTW', '$p，如果你有空的話，我想和你談一些事。\n\n<佩拉苟斯吸了一口氣。>\n\n當你選擇瑞文崔斯的勇士一起拯救暗影之境的時候，我很受傷。我原本以為你會選擇我以及琪瑞安誓盟一同並肩作戰。\n\n但是我現在知道你為什麼會選擇汎希爾了。你的餘燼宴會讓整個暗影之境團結在一起。就像以前那樣…也是現在應該要的樣子。\n\n也就是說，我很高興在經歷這一切之後，我們成為了朋友。或許我們不會成為靈魂之絆，不過我老是覺得我們已經以另一種方式連結在一起了。', '', 38134),
(206390, 'zhTW', '', '我的朋友！很高興你有時間聊聊。\n\n已經過了多久了？不管怎樣，都已經太久了。\n\n和我聊聊我們上次見面以後發生的所有事吧。需要我或佩拉苟斯幫你什麼忙嗎？\n\n<克蕾雅吸了一口氣後露出了微笑。>\n\n$p，很高興再次見到你。', 38134),
(206389, 'zhTW', '', '$p，很高興在昇靈堡的眾多靈魂之中再見到你。\n\n你的餘燼宴會讓我知道你對暗影之境的付出有多少，即使你選擇把汎西爾當作首選來做出實際行動。', 38134),
(206388, 'zhTW', '', '淵喉行者，暗影之境需要我嗎？\n\n我隨時能夠效命。', 38134),
(206387, 'zhTW', '更重要的是，你一路上需要夥伴同行嗎？', '', 38134),
(206386, 'zhTW', '', '$p！朋友，你要去哪裡？', 38134),
(206384, 'zhTW', '', '閻獄之主的力量持續增長，而暗影之境正面臨著接踵而來的危機。做好準備，$p。在未來的日子裡，我們需要你的力量。', 38134),
(206383, 'zhTW', '', '不管$p在哪裡，那裡就是暗影之境最安全的地方。', 38134),
(206382, 'zhTW', '$p，在外要注意安全。', '', 38134),
(206381, 'zhTW', '', '$g他:她;想讓我們知道的話，會告訴我們的。', 38134),
(206380, 'zhTW', '我在想淵喉行者要去哪裡…', '', 38134),
(206378, 'zhTW', '要是我之前沒說過的話，我想讓你知道我非常感謝你，$p。\n\n你教會了我很多關於奉獻、犧牲，還有…\n\n<軍政官阿德雷特斯確認附近沒有志士會聽見以後，才繼續說下去。>\n\n…還有適時放鬆一下的重要性。\n\n我一開始不太想參加你的餘燼宴會，不過我現在覺得很期待。\n\n當然！這是為了御靈者！', '', 38134),
(206377, 'zhTW', '$p，很高興又在昇靈堡見到你。\n\n我常常在想，如果你跟我一樣選擇全心奉獻給御靈者的話，事情會有哪些不同。\n\n不過，很顯然地你對罪殞城的奉獻依然對整個暗影之境功不可沒。\n\n我相信在你的照料之下一切都會很好。', '', 38134),
(206376, 'zhTW', '淵喉行者，是什麼風把你吹來英雄之陵的？\n\n如果你需要軍事方面的協助，我跟哨兵隨時可以動身。\n\n還是你帶來了罪殞城的最新消息？我可以將這消息沿著指揮鍊傳上去。', '', 38134),
(206374, 'zhTW', '稍息。$p是我自己的朋友。', '', 38134),
(206373, 'zhTW', '$p，英雄之陵這裡一切安好。歡迎回來。。', '', 38134),
(206372, 'zhTW', '淵喉行者，你好。', '', 38134),
(206371, 'zhTW', '有人需要我就提供服務，而這裡有人需要我。', '', 38134),
(206369, 'zhTW', '', '<西卡在說話的同時暫時停下了她原本在整理文件的工作。>\n\n上一次的餘燼宴會好好玩哦，\n\n或許下次會需要更多清掃員。西卡能幫忙哦，幫忙把一切都清乾淨！\n\n那些小泥掘者需要好好洗個澡，因為非常臭。這可不是西卡的工作。', 38134),
(206367, 'zhTW', '', '$p需要什麼嗎？我會列為第一優先！', 38134),
(206366, 'zhTW', '', '西卡最喜歡看到$p來訪！', 38134),
(206365, 'zhTW', '', '淵喉行者也需要什麼嗎？加到我的清單上。', 38134),
(206364, 'zhTW', '', '非常悲傷。餘燼宴會好玩。下次看看吧…', 38134),
(206363, 'zhTW', '<米卡尼寇斯仔細地觀察著你護甲上的部件，嘴裡嘟噥著。>\n\n太聰明了…非常新穎…非常有趣…\n\n淵喉行者，你每次來訪都會帶來新鮮有趣的事。', '', 38134),
(206362, 'zhTW', '隨時歡迎$p光臨我的工坊。', '', 38134),
(206360, 'zhTW', '$p！你今天有什麼新的驚喜嗎？\n\n和新的敵人戰鬥？發現新的科技？學會新的魔法？\n\n什麼事都可以告訴我。\n\n從小細節常常可以得到大啟示！', '', 38134),
(206359, 'zhTW', '每一個部分都有完美契合的對應狀況，但這次我有個特別的東西！', '', 38134),
(206358, 'zhTW', '朋友！餘燼宴會真是太有意思了…需要更多這種宴會！', '', 38134),
(206357, 'zhTW', '有好多東西要學習…有好多東西要嘗試…', '淵喉行者，永遠歡迎你光臨我的林地。', 38134),
(206355, 'zhTW', '', '我知道。', 38134),
(206354, 'zhTW', '如果你這麼認為的話。我相信你。我真的相信你。但我就是覺得還不夠快。', '', 38134),
(206353, 'zhTW', '', '我知道你可能還沒有什麼感覺，但我看得出來你在面對這些回憶時，越來越得心應手了。佩拉苟斯，你做得很好。', 38134),
(206352, 'zhTW', '克蕾雅，真希望我能有越來越熟練的感覺。', '', 38134),
(206346, 'zhTW', '再次感謝你讓這些靈魂得以復原。他們把栽培園打理得更好了，你不覺得嗎？', '再次感謝你讓這些靈魂得以復原。他們把栽培園打理得更好了，你不覺得嗎？', 38134),
(206345, 'zhTW', '那個靈魂可能更喜歡水。我想你可以在湖邊找到它們。', '那個靈魂可能更喜歡水。我想你可以在湖邊找到它們。', 38134),
(206344, 'zhTW', '要是你找到新髮型海報，我可以當造型參考，找到我能戴的帽子的話我就戴！', '', 38134),
(206343, 'zhTW', '你去爛泥地附近或深夜市集找找我們能用的染料。', '', 38134),
(206338, 'zhTW', '我們的傀儡最喜歡在戰場上炫耀了。你會發現有很多不同的外觀配件可以取得，讓傀儡在戰場上更加英姿勃發。\n\n你得要到處去尋找材料，才能打造出這些外觀配件。', '', 38134),
(206337, 'zhTW', '優良肢體是建造強大的戰鬥傀儡和物品所必備的關鍵材料。\n\n這些部位很難找到，但如果你能幫忙這裡的傀儡解決他們的問題，他們就會幫助你取得優良肢體。', '', 38134),
(206336, 'zhTW', '憎惡縫合術製造大多數的傀儡和物品時，會需要延展血肉這種基本材料。\n\n只要在暗影之境打倒任何生物，你都有機會從他們身上找到適合的延展血肉。\n\n寇迪也非常擅長尋找延展血肉，所以請他幫忙去尋找這種材料時，請別客氣。', '', 38134),
(206325, 'zhTW', '憎惡縫合術這門技藝需要穩定的手、高品質的材料以及當然要有的憎惡縫合桌，就在那邊。\n\n我已經分別把在憎惡縫合桌可以建造的傀儡和物品需要用到的材料寫下來了。', '', 38134),
(206322, 'zhTW', '噢，如果他們沒來而錯過這場派對，那他們一定會超級生氣的！', '', 38134),
(206321, 'zhTW', '嘿，不知道我有沒有直接跟你說過，很感謝你總是讓我的朋友也來參加餘燼宴會。\n\n我們是絕佳拍檔。我們緊緊相隨。\n\n而且，要是我不帶他去參加那些超棒的派對，史蘭克能絕對不會讓我聽到最後的！\n\n<丘發笑著。>\n\n嘿，我可以直接把他換成你，對吧？你也是我們的一份子了。', '', 38134),
(206320, 'zhTW', '大宴會，大派對。我們過得還真是充實啊，我說我們兩個！…還有盧里，還有阿嗚，還有史蘭克能。\n\n說到這個，下次餘燼宴會什麼時候舉辦？我現在有空，你想要早點到場，打開幾瓶酒，然後來一場事前派對嗎？\n\n算了，你看起來好像很忙。你還有事情要做，沒關係。\n\n但一定要確保我下次也會受邀喔，好嗎？', '', 38134),
(206319, 'zhTW', '兄弟，兄弟！你身上還有宴會剩下來的煙火嗎？', '', 38134),
(206318, 'zhTW', '嘿，$p！下次宴會是什麼時候啊？', '', 38134),
(206317, 'zhTW', '盧里到底跑哪去了…？', '', 38134),
(206316, 'zhTW', '', '我不怎麼信任仲介者…不過我也無法抗拒他們帶來的商品。', 38134),
(206315, 'zhTW', '', '痛苦和失去是高明的老師，我一天到晚在上他們的課。\n\n也因為這樣，讓我更珍惜你邀請我參加餘燼宴會的機會。\n\n我的旅程變得…充滿啟發，在各個方面。你的王子很有說服力。我相信他會祝福我們和其他界域的。\n\n$p，謝謝你邀請我來。我很期待下次宴會。', 38134),
(206314, 'zhTW', '', '到目前為止我很享受這段旅程，$p，而我感覺這只是我們長遠的友誼之路最開始的幾步而已。\n\n你已經教我思考很多新的想法和可能性了。我希望我也開拓了你的對新想法的眼界。\n\n我知道你沒辦法陪我坐太久。這就是淵喉行者的使命。\n\n但也別離開太久。要不是你，我的林地也不可能復原。', 38134),
(206313, 'zhTW', '', '$p，很高興再見到你。趕時間嗎？', 38134),
(206312, 'zhTW', '', '朋友，是什麼事讓你跑來我的林地？', 38134),
(206311, 'zhTW', '', '淵喉行者，我的林地永遠歡迎你。', 38134),
(206310, 'zhTW', '', '啊，$p。\n\n等你回來我們再去打獵，朋友。\n\n你從瑞文崔斯帶來什麼消息了嗎？在亞登曠野，佐司特、果姆和其他害蟲一直從各個地方威脅著我們。\n\n我不是要抱怨。還有更多去狩獵的理由。\n\n我很希望你能盡早再跟我一起。我已經習慣你的戰鬥風格了。\n\n有我們兩個，哪裡還需要一整個狩獵團啊，對吧？', 38134),
(206309, 'zhTW', '', '$p，宴會怎麼樣了？\n\n你為下個環節安排狩獵活動了嗎？如果我要參加的話，希望會是新玩意。\n\n到目前為止我很喜歡一起在宴會場地打獵，但能嘗試新的事物總是更令人興奮。你不覺得嗎？\n\n不管怎樣，我都希望我們能早點再一起去打獵。', 38134),
(206308, 'zhTW', '', '$p，我想你的狩獵應該一直都很順利。\n\n和我說說你最近追蹤了什麼獵物吧。', 38134),
(206307, 'zhTW', '', '$p！暫且休息一下，然後和我說說你的狩獵發生了什麼事。', 38134),
(206306, 'zhTW', '', '淵喉行者，狩獵進行得如何？', 38134),
(206305, 'zhTW', '', '狩獵在呼喚，誰會回應呼喚呢？', 38134),
(206304, 'zhTW', '', '淵喉行者，祝你狩獵順利。', 38134),
(206303, 'zhTW', '', '相信我，果姆的費洛蒙比你想像中還要好聞。', 38134),
(206302, 'zhTW', '', '<月莓女士在靠近你用耳語說話之前，往兩側看了看。>\n\n$p，我們現在是好朋友了。這代表我們兩個做什麼都要在一起！\n\n…說到這個，我已經想到下個惡作劇的計劃了，我想要…\n\n<月莓女士發現到附近有個飛靈在偷聽這場對話。>\n\n我們下次餘燼宴會時再詳談吧。我等不及了。', 38134),
(206301, 'zhTW', '', '<月莓女士暗自笑了一下。>\n\n$p！看到你過來我好高興。\n\n看到你就讓我想起，上次餘燼宴會時我對希奧塔開的玩笑。真是段美好的時光啊！\n\n希奧塔也會參加下一場宴會嗎？他答應我說他會回覆我，但我一直等到現在…', 38134),
(206300, 'zhTW', '', '又見面了。你今天想怎樣搗蛋啊？\n\n還有，最重要的是，會不會算我一份？', 38134),
(206299, 'zhTW', '', '$p！你經過你最喜歡的飛靈身邊，卻不打個招呼嗎？', 38134),
(206298, 'zhTW', '', '別擔心，$p。我今天完全沒有要整你。', 38134),
(206297, 'zhTW', '', '這下可有意思了…', 38134),
(206296, 'zhTW', '還有很多事要做…如果你有緊急的特殊需求，請去找純潔之手。謝謝你。', '還有很多事要做…如果你有緊急的特殊需求，請去找純潔之手。謝謝你。', 38134),
(206295, 'zhTW', '<他仔細觀察著你。>\n\n你背負了什麼樣的重擔？', '<她仔細觀察著你。>\n\n你背負了什麼樣的重擔？', 38134),
(206294, 'zhTW', '志士，如果我一有機會我會盡快來幫助你。請再稍等一下。', '志士，如果我一有機會我會盡快來幫助你。請再稍等一下。', 38134),
(206293, 'zhTW', '我能為你效勞嗎？', '我能為你效勞嗎？', 38134),
(206292, 'zhTW', '志士，什麼事讓你困擾？', '志士，什麼事讓你困擾？', 38134),
(206291, 'zhTW', '你也需要淨化嗎？恐怕我必須要求你先暫時等一下了。還有很多人也需要我的救助。', '你也需要淨化嗎？恐怕我必須要求你先暫時等一下了。還有很多人也需要我的救助。', 38134),
(206290, 'zhTW', '我從沒見過這種事。志士在我們遭逢危機時來到此處不算罕見的事，但同時間一次這麼多來到這裡…我不禁覺得我們神殿大門的另一端一定發生了什麼大事。\n\n請見諒，我必須回去工作了。', '我從沒見過這種事。志士在我們遭逢危機時來到此處不算罕見的事，但同時間一次這麼多來到這裡…我不禁覺得我們神殿大門的另一端一定發生了什麼大事。\n\n請見諒，我必須回去工作了。', 38134),
(206289, 'zhTW', '志士，歡迎來到純潔神殿。請花點時間參觀這個地方…但請多加小心。這裡也有許多人在等待淨化。', '志士，歡迎來到純潔神殿。請花點時間參觀這個地方…但請多加小心。這裡也有許多人在等待淨化。', 38134),
(206288, 'zhTW', '<晉升者低下他的頭向你鞠躬。>\n\n凡人，御靈者注視著你。', '<晉升者低下她的頭向你鞠躬。>\n\n凡人，御靈者注視著你。', 38134),
(206287, 'zhTW', '你的到來遠比你想的還要重要，能見證此刻是我的榮幸。', '你的到來遠比你想的還要重要，能見證此刻是我的榮幸。', 38134),
(206286, 'zhTW', '凡人，你在樂土堡造成了很大的騷動。', '凡人，你在樂土堡造成了很大的騷動。', 38134),
(206285, 'zhTW', '<晉升者好奇地看著你。>\n\n恭喜，我很榮幸能邀請你加入志士的行列。', '<晉升者好奇地看著你。>\n\n恭喜，我很榮幸能邀請你加入志士的行列。', 38134),
(206284, 'zhTW', '我沒死，還跨過了這個…他們稱呼那個為什麼？帷幕？…我來這裡不是為了永恆的「等待」！\n\n等等…你是新來的！那代表等待終於結束了？！', '我沒死，還跨過了這個…他們稱呼那個為什麼？帷幕？…我來這裡不是為了永恆的「等待」！\n\n等等…你是新來的！那代表等待終於結束了？！', 38134),
(206283, 'zhTW', '<他重重地嘆了一口氣。>\n\n至少在這個地方等還算舒適。', '<她重重地嘆了一口氣。>\n\n至少在這個地方等還算舒適。', 38134),
(206282, 'zhTW', '前方的路很漫長，但就連踏上去的機會都遭受拒絕…我開始思考我被送來這裡會不會根本是錯的。', '前方的路很漫長，但就連踏上去的機會都遭受拒絕…我開始思考我被送來這裡會不會根本是錯的。', 38134),
(206281, 'zhTW', '他們說要有耐心！哼，我可沒看見這附近有什麼耐心楷模，你有看見嗎？', '他們說要有耐心！哼，我可沒看見這附近有什麼耐心楷模，你有看見嗎？', 38134),
(206280, 'zhTW', '你有帶來御靈者的最新消息嗎？乾旱終於停了嗎？', '你有帶來御靈者的最新消息嗎？乾旱終於停了嗎？', 38134),
(206279, 'zhTW', '他們和我們說在靈魄危機解決以前在這裡等著…我連那是多久以前的事都忘了。我開始覺得這場乾旱永遠不會結束了。\n\n至少這裡的侍者很友善，而且不管我怎麼吃都不會變胖。哈哈！', '他們和我們說在靈魄危機解決以前在這裡等著…我連那是多久以前的事都忘了。我開始覺得這場乾旱永遠不會結束了。\n\n至少這裡的侍者很友善，而且不管我怎麼吃都不會變胖。哈哈！', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(206278, 'zhTW', '我只想要開始我的旅程。這樣的要求會太多嗎？', '我只想要開始我的旅程。這樣的要求會太多嗎？', 38134),
(206277, 'zhTW', '真是太荒唐了。我已經在這裡等了…等等，今天的日期是？\n\n真的過了好久！', '真是太荒唐了。我已經在這裡等了…等等，今天的日期是？\n\n真的過了好久！', 38134),
(206276, 'zhTW', '有新來的？終於啊！', '有新來的？終於啊！', 38134),
(206275, 'zhTW', '喔！新來的！你從彼界帶來了什麼消息？', '喔！新來的！你從彼界帶來了什麼消息？', 38134),
(206274, 'zhTW', '我犧牲了自己的一生才能來到這裡，這裡真是美極了。充滿希望。這裡還是很美，但他們向我保證我能在這裡成長，成為最好的自己。然後我卻被困在這，一天比一天苦澀。事情原本應該不是這個樣子吧？', '我犧牲了自己的一生才能來到這裡，這裡真是美極了。充滿希望。這裡還是很美，但他們向我保證我能在這裡成長，成為最好的自己。然後我卻被困在這，一天比一天苦澀。事情原本應該不是這個樣子吧？', 38134),
(206273, 'zhTW', '至少死亡還滿有趣的。但我等了這麼久…到底是為了什麼？', '至少死亡還滿有趣的。但我等了這麼久…到底是為了什麼？', 38134),
(206272, 'zhTW', '真想念我的身體。', '真想念我的身體。', 38134),
(206271, 'zhTW', '朋友，我希望你是事情開始好轉的徵兆。昇靈堡的狀況很不好。', '朋友，我希望你是事情開始好轉的徵兆。昇靈堡的狀況很不好。', 38134),
(206270, 'zhTW', '等待時最糟糕的部分不是沒有手，或腳，或眼睛。\n\n老實說，我也不太確定這些要怎麼用。他們跟我說，只要我能完成第一次淨化我就能獲得一個新的型態。但在乾旱開始之後，只有極少數的挑戰者能完成這個儀式。', '等待時最糟糕的部分不是沒有手，或腳，或眼睛。\n\n老實說，我也不太確定這些要怎麼用。他們跟我說，只要我能完成第一次淨化我就能獲得一個新的型態。但在乾旱開始之後，只有極少數的挑戰者能完成這個儀式。', 38134),
(206268, 'zhTW', '他們跟我說，我能來這裡是要獎勵我有個美好的人生。難道那是謊言嗎？還是說，我其實被懲罰了？', '他們跟我說，我能來這裡是要獎勵我有個美好的人生。難道那是謊言嗎？還是說，我其實被懲罰了？', 38134),
(206267, 'zhTW', '他們說收集者的手藝很好。或許我該去學點木工…', '他們說收集者的手藝很好。或許我該去學點木工…', 38134),
(206266, 'zhTW', '要成為一名看守者聽起來似乎挺困難的…但如果我能給那些靈魂死而復生的機會…這個使命會有價值嗎？', '要成為一名看守者聽起來似乎挺困難的…但如果我能給那些靈魂死而復生的機會…這個使命會有價值嗎？', 38134),
(206265, 'zhTW', '當我晉升之後，我會想要成為一名引渡者。我想要看看暗影之境以外的所有世界。我想知道外頭有什麼！', '當我晉升之後，我會想要成為一名引渡者。我想要看看暗影之境以外的所有世界。我想知道外頭有什麼！', 38134),
(206264, 'zhTW', '昇靈堡真是個美妙的地方。他們跟我說在這些事情之前天空總是充滿著靈魄。我非常期盼能看到它過往的榮光。', '昇靈堡真是個美妙的地方。他們跟我說在這些事情之前天空總是充滿著靈魄。我非常期盼能看到它過往的榮光。', 38134),
(206263, 'zhTW', '噢，如果有一天我也有了翅膀…\n\n<他渴望地嘆了口氣。>', '噢，如果有一天我也有了翅膀…\n\n<她渴望地嘆了口氣。>', 38134),
(206262, 'zhTW', '醒覺林地是重生之路的最後一站。這裡是亞登曠野最神聖的地方之一。\n\n當靈魂準備好回到他們的世界時就會被帶到這裡。穿過這個傳送門後，他們的循環會再次展開。', '醒覺林地是重生之路的最後一站。這裡是亞登曠野最神聖的地方之一。\n\n當靈魂準備好回到他們的世界時就會被帶到這裡。穿過這個傳送門後，他們的循環會再次展開。', 38134),
(206261, 'zhTW', '我嘗試過初次淨化多達三次，我的重擔每一次都會把我壓垮！而這還只是第一步而已！如果我永遠都卡在這一步怎麼辦？', '我嘗試過初次淨化多達三次，我的重擔每一次都會把我壓垮！而這還只是第一步而已！如果我永遠都卡在這一步怎麼辦？', 38134),
(206260, 'zhTW', '我還有好多事情要學習…但至少還不急。反正…現在也不可能晉升。', '我還有好多事情要學習…但至少還不急。反正…現在也不可能晉升。', 38134),
(206259, 'zhTW', '首先是純淨試煉，接著是勇氣試煉，再來是…\n\n等等，我想到了。忠誠試煉？不對，應該不是這樣…', '首先是純淨試煉，接著是勇氣試煉，再來是…\n\n等等，我想到了。忠誠試煉？不對，應該不是這樣…', 38134),
(206258, 'zhTW', '「你」已經等多久了？', '「你」已經等多久了？', 38134),
(206257, 'zhTW', '我還有好多事要學習，而這樣延宕下去只會拖延這趟旅途。我擔心我永遠都無法晉升。', '我還有好多事要學習，而這樣延宕下去只會拖延這趟旅途。我擔心我永遠都無法晉升。', 38134),
(206256, 'zhTW', '這種不正常的狀況已經持續了多久？這裡的時間流動得太奇怪了。', '這種不正常的狀況已經持續了多久？這裡的時間流動得太奇怪了。', 38134),
(206255, 'zhTW', '我只想要開始進行五大試煉。', '我只想要開始進行五大試煉。', 38134),
(206254, 'zhTW', '你上次訓練到哪？我先前在忠誠試煉時差一點就成功了，但事情卻突然變得一發不可收拾。我差一點就能晉升了…就差一點。', '你上次訓練到哪？我先前在忠誠試煉時差一點就成功了，但事情卻突然變得一發不可收拾。我差一點就能晉升了…就差一點。', 38134),
(206253, 'zhTW', '我在這裡沒有別的事可以做了。再多的冥想或訓練都無法讓我更接近晉升一步。至少現在不行。\n\n<他深深嘆了一口氣。>', '我在這裡沒有別的事可以做了。再多的冥想或訓練都無法讓我更接近晉升一步。至少現在不行。\n\n<她深深嘆了一口氣。>', 38134),
(206252, 'zhTW', '雖然現在的昇靈堡和往日榮光相比黯淡不少，但這裡還是我見過最美麗的地方。如果要我選擇一個等待永恆，那非這個地方莫屬了。', '雖然現在的昇靈堡和往日榮光相比黯淡不少，但這裡還是我見過最美麗的地方。如果要我選擇一個等待永恆，那非這個地方莫屬了。', 38134),
(206251, 'zhTW', '朋友，你是如何消磨時光的？', '朋友，你是如何消磨時光的？', 38134),
(206250, 'zhTW', '御靈者在上啊，我真的好累。', '御靈者在上啊，我真的好累。', 38134),
(206249, 'zhTW', '他們說，要效忠神殿。他們說，展現你的美德。可是我…\n\n<他發現你的存在後，很快就安靜了下來。>', '他們說，要效忠神殿。他們說，展現你的美德。可是我…\n\n<她發現你的存在後，很快就安靜了下來。>', 38134),
(206248, 'zhTW', '<他在說話前很迅速地把心定了下來。>\n\n志士，我能為你效勞嗎？', '<她在說話前很迅速地把心定了下來。>\n\n志士，我能為你效勞嗎？', 38134),
(206247, 'zhTW', '志士，我會盡快來幫你的。', '志士，我會盡快來幫你的。', 38134),
(206246, 'zhTW', '你也需要淨化嗎？', '你也需要淨化嗎？', 38134),
(206245, 'zhTW', '在我完成自己的純淨試煉後，他們說我可以幫他們的忙。但我卻…我好害怕。他們數量太多了。', '在我完成自己的純淨試煉後，他們說我可以幫他們的忙。但我卻…我好害怕。他們數量太多了。', 38134),
(206244, 'zhTW', '<在注意到你之前，他吸了一口又深又長的氣。>\n\n有哪裡不對勁嗎？', '<在注意到你之前，她吸了一口又深又長的氣。>\n\n有哪裡不對勁嗎？', 38134),
(206243, 'zhTW', '有什麼事嗎？', '有什麼事嗎？', 38134),
(206242, 'zhTW', '我很感激你的耐心。請你了解，我們已經竭盡所能了。', '我很感激你的耐心。請你了解，我們已經竭盡所能了。', 38134),
(206241, 'zhTW', '我該做什麼來服侍你呢？', '我該做什麼來服侍你呢？', 38134),
(206240, 'zhTW', '我能為你效勞嗎？', '我能為你效勞嗎？', 38134),
(206239, 'zhTW', '朋友，御靈者注視著你。', '朋友，御靈者注視著你。', 38134),
(206238, 'zhTW', '這名侍者能幫上什麼忙，唷呼？', '這名侍者能幫上什麼忙，唷呼？', 38134),
(206236, 'zhTW', '真是悲傷。好多人殞落了。\n\n<他的眼角下垂。>', '真是悲傷。好多人殞落了。\n\n<她的眼角下垂。>', 38134),
(206235, 'zhTW', '傷心的志士讓侍者也傷心。', '傷心的志士讓侍者也傷心。', 38134),
(206234, 'zhTW', '不難過。你想唱歌？來點果汁？', '不難過。你想唱歌？來點果汁？', 38134),
(206233, 'zhTW', '好多傷心的志士…拜託你，讓我們做得更好？', '好多傷心的志士…拜託你，讓我們做得更好？', 38134),
(206232, 'zhTW', '果汁對思考很好。對比劃很好。你想要我去拿果汁嗎？', '果汁對思考很好。對比劃很好。你想要我去拿果汁嗎？', 38134),
(206231, 'zhTW', '訓練的好日子？酸痛？坐下試試。伸展試試。', '訓練的好日子？酸痛？坐下試試。伸展試試。', 38134),
(206230, 'zhTW', '好志士，需要什麼？', '好志士，需要什麼？', 38134),
(206229, 'zhTW', '這東西要怎如何變開心？', '這東西要怎如何變開心？', 38134),
(206228, 'zhTW', '清乾淨。打理好。昇靈堡很美，對吧？', '清乾淨。打理好。昇靈堡很美，對吧？', 38134),
(206227, 'zhTW', '啊！新志士！搖鈴鐺！吹號角！好日子！', '啊！新志士！搖鈴鐺！吹號角！好日子！', 38134),
(206226, 'zhTW', '嗨！你新來的！非常好！耶！', '嗨！你新來的！非常好！耶！', 38134),
(206225, 'zhTW', '唷呼！哈囉！新志士！好耶！', '唷呼！哈囉！新志士！好耶！', 38134),
(206223, 'zhTW', '我幫你拿什麼嗎？', '我幫你拿什麼嗎？', 38134),
(206222, 'zhTW', '我們會幫忙！有什麼事嗎？', '我們會幫忙！有什麼事嗎？', 38134),
(206221, 'zhTW', '<侍者自己哼著歡樂的曲調。>', '<侍者自己哼著歡樂的曲調。>', 38134),
(206220, 'zhTW', '你想要，我去拿？', '你想要，我去拿？', 38134),
(206219, 'zhTW', '', '我們現在都還有點疲倦。這場乾旱已經完全改變了我們每天存在的意義。\n\n但我們必須堅持下去。我能為你效勞嗎？', 38134),
(206218, 'zhTW', '<艾奇隆在進行依次訓練的時候喃喃抱怨。>', '', 38134),
(206217, 'zhTW', '<菲力歐斯似乎在與泰蒙深談。>\n\n你無法全心投注在記憶之中。召喚它前來，然後任它流逝。我知道事情不像我說的這麼容易，但你已經快完成了。你很快就會克服的。我很確定。', '<菲力歐斯似乎在與泰蒙深談。>\n\n你無法全心投注在記憶之中。召喚它前來，然後任它流逝。我知道事情不像我說的這麼容易，但你已經快完成了。你很快就會克服的。我很確定。', 38134),
(206216, 'zhTW', '<泰蒙似乎在跟菲力歐斯深談。>\n\n你在試煉的時候總是那麼堅忍！真希望我也能做到那樣。感覺我只要一想到一段令人不安的記憶，我的腦袋就會開始不受控制。', '<泰蒙似乎在跟菲力歐斯深談。>\n\n你在試煉的時候總是那麼堅忍！真希望我也能做到那樣。感覺我只要一想到一段令人不安的記憶，我的腦袋就會開始不受控制。', 38134),
(206214, 'zhTW', '', '我的靈魂之絆似乎很喜歡你。我覺得我應該好好感謝你…少了她的熔爐後，她開始變得有點消沉。\n\n如果你路過勇氣神殿的話就來打聲招呼吧。', 38134),
(206213, 'zhTW', '', '拜託，不要為我煩惱。我…覺得怪怪的，但我的神聖楷模有空的時候會照顧我的。在此同時，我還可以用訓練激勵自己。\n\n<伊芮迪雅顫抖著吸了一口氣。>\n\n我不能沉溺於悲傷，或憤怒，或…萊索妮雅。', 38134),
(206211, 'zhTW', '我們維護著循環。', '我們維護著循環。', 38134),
(206210, 'zhTW', '', '所有脆弱的志士…所有我們送去忠誠神殿的晉升者…他們就是變成這個樣子嗎？他們一定是神智混亂了，才會攻擊神殿！我們不是他們的敵人！我們只是…\n\n<伊芮迪雅遙望著神殿時陷入了沉默。一滴眼淚滑過她的臉頰。>', 38134),
(206209, 'zhTW', '', '你見證過最不尋常的情況，凡人。希望你不會因此感到困擾。我們在這黑暗的時刻需要所有心靈健全的人。', 38134),
(206208, 'zhTW', '歡迎來到醒覺林地。', '歡迎來到醒覺林地。', 38134),
(206207, 'zhTW', '', '我是楷模之手，她的意念就是我的意念。', 38134),
(206206, 'zhTW', '', '需要我的侍徒幫你什麼忙嗎？', 38134),
(206205, 'zhTW', '', '我的楷模希望如此，那就該如此。', 38134),
(206204, 'zhTW', '', '遵循維希芬妮的意念。', 38134),
(206202, 'zhTW', '他們不肯傾聽，他們根本不理我們！這場暴力衝突必須停止，不然我們會全盤皆輸！', '', 38134),
(206200, 'zhTW', '我已經全心奉獻給純潔神殿。當你在我們的領地時，有任何需求都可以呼喚我。', '', 38134),
(206199, 'zhTW', '', '距離上次看到儀式進行，已經過了太久太久了。\n\n恭喜，也感謝你帶給我們的希望。', 38134),
(206198, 'zhTW', '', '對，我還在這裡。\n\n<熔爐士索芬妮咯咯笑著。>\n\n事實證明，我的熔爐是用來教導新進志士的絕佳用具。我問御靈者我能不能留在試煉場這裡，她同意了！如果這就是我的永生，那我在未來的漫長時光裡會很高興的。', 38134),
(206196, 'zhTW', '願暗夜的祝福伴隨著你。', '願暗夜的祝福伴隨著你。', 38134),
(206195, 'zhTW', '', '我總有一天會晉升。在那之前，我會把時間全部投入在熔爐上。當然是在我還能拿到靈魄的時候啦。', 38134),
(206194, 'zhTW', '', '和另一名志士討論訓練的事感覺一定很奇怪。他們要我掌管這裡的一切，因為我是待最久的，就只是這樣而已。\n\n<熔爐士索芬妮咯咯笑著。>', 38134),
(206193, 'zhTW', '', '我的熔爐是我的驕傲及喜悅。我是唯一的熔爐士志士是有原因的！', 38134),
(206192, 'zhTW', '', '米卡尼寇斯非常忙碌。西卡會確保熔爐士能得到她需要的東西，沒錯！', 38134),
(206191, 'zhTW', '', '<西卡參考她的卷軸。>\n\n靈魄呢？好。\n鉗子呢？好。\n材料呢？好。\n熔爐士呢？好。\n西卡呢？耶！\n\n我們什麼都有了！呼呼！', 38134),
(206190, 'zhTW', '', '噢不！西卡惹上麻煩了嗎？', 38134),
(206189, 'zhTW', '', '西卡想念接待員。助憶士什麼事情都知道！\n\n嗯，幾乎所有事啦。', 38134),
(206188, 'zhTW', '', '首席熔爐士米卡尼寇斯大師要求西卡負責照護百衛。靈魄的量很少，我們必須小心分配！既然你來了！接待員需要靈魄！\n\n好險西卡讓百衛能分享一些靈魄出來！', 38134),
(206186, 'zhTW', '在我們逃脫時，克蕾雅會保護我。做你該做的事吧，$n。我相信你。', '', 38134),
(206185, 'zhTW', '噢不！太可怕了…希望克蕾雅平安無事！', '', 38134),
(206184, 'zhTW', '真可怕。那所有的志士…或許伊芮迪雅是對的。我應該留在這裡。', '', 38134),
(206182, 'zhTW', '我很高興純潔之手要求我們一同行動。你看起來對這種狀況游刃有餘，如果你有需要的話，我會在這裡激勵你。我不會有事的。\n\n<佩拉苟斯不安地看著四周。>', '', 38134),
(206181, 'zhTW', '噢，天啊。情況一定很危急，才會讓侍徒就這樣離開神殿。', '', 38134),
(206180, 'zhTW', '我就知道你可以，$n。我們會堅持下去，度過難關！', '', 38134),
(206179, 'zhTW', '只要盡力就好！亞薩諾斯專門測試那些能力最強的志士。', '', 38134),
(206178, 'zhTW', '<看來佩拉苟斯和克蕾雅很專注的在討論。>', '', 38134),
(206177, 'zhTW', '你的人生閱歷太有趣了！我等不及要瞭解更多你的事蹟。但你現在最好去聽聽熔爐士要說些什麼，她比我還要更瞭解這個地方是如何運作的。', '', 38134),
(206176, 'zhTW', '$n，我對你的事蹟非常感興趣。一定是很偉大的行為才會把你帶來這裡，而且讓你在抵達這裡沒多久就幫我一個大忙！我很肯定你一定是為這個地方而生的！', '', 38134),
(206175, 'zhTW', '我們只能不斷向前推進。', '', 38134),
(206174, 'zhTW', '', '我感覺很像在非法入侵。總覺得這裡就跟昇靈堡一樣古老。\n\n要不是因為我們的情況如此惡劣，我會覺得看到它是件幸運的事。', 38134),
(206173, 'zhTW', '', '我從來沒看過助憶士有這種行為。真是…讓人擔憂。', 38134),
(206172, 'zhTW', '', '我會保護首席熔爐士。該做什麼就去做吧，$n。', 38134),
(206171, 'zhTW', '', '棄誓者也攻佔這個地方了…他們控制整個昇靈堡了嗎？\n\n我希望佩拉苟斯能及時把消息傳出去，但我們要先專心進行這裡的任務。我們一定要找出是誰在進行這些攻擊行為！', 38134),
(206170, 'zhTW', '', '我們必須找出為什麼棄誓者要發動攻擊。我們必須找出是誰在背後操控！', 38134),
(206169, 'zhTW', '', '謝謝你讓佩拉苟斯平安無事。我也很高興看到你安然無恙！\n\n我得保證他的安全。我希望你能理解，但我不能跟你一起去。\n\n我知道你可以幫助我們，但你也知道我們一定會再見面的。\n\n莫忘職責，$n。', 38134),
(206168, 'zhTW', '', '<看來克蕾雅非常專注在儀式上。>', 38134),
(206167, 'zhTW', '', '看來沒空慶祝你的勝利了。我們有事要先處理。', 38134),
(206166, 'zhTW', '', '$n，做得好。我希望你的努力最終能獲得御靈者的注意。', 38134),
(206165, 'zhTW', '', '<克蕾雅和佩拉苟斯討論得非常專注。>', 38134),
(206164, 'zhTW', '', '總有一天，我會晉升的。到那時候，我很樂意幫助你，佩拉苟斯，還有其他所有志士，什麼忙都可以幫。', 38134),
(206163, 'zhTW', '', '$n，從來沒有一個靈魂是因為錯誤而被送到昇靈堡的。記住這點。\n\n<克蕾雅對你微笑。>', 38134),
(206162, 'zhTW', '', '$n，我很期待知道是什麼樣的事蹟，能讓你來到我們的大門底下。', 38134),
(206161, 'zhTW', '', '卡莉賽恩監管著所有現在不住在任何一間神殿裡的志士。我早該知道啟動接待員會引起她的注意。\n\n不用擔心，我來處理！', 38134),
(206160, 'zhTW', '', '走進池子裡，$n。這樣可以讓你從穿越帷幕的旅程中恢復過來。', 38134),
(206159, 'zhTW', '', '你應該可以從那些百衛身上拿到一些靈魄。現在只剩珍貴的一點點可以用了，但我相信手上這些夠用的。', 38134),
(206157, 'zhTW', '', '這是有人入侵的警告。目前威脅程度：重度。即將部署執行者。', 38134),
(206156, 'zhTW', '', '這是有人入侵的警告。目前威脅程度：中度。', 38134),
(206155, 'zhTW', '', '這是有人入侵的警告。目前威脅程度：輕度。', 38134),
(206154, 'zhTW', '我唱快樂的歌…快…樂…的…歌…', '', 38134),
(206153, 'zhTW', '我想念我的小不點好朋友…', '', 38134),
(206152, 'zhTW', '希望你會再帶點心來！', '', 38134),
(206150, 'zhTW', '<岩石頭用空著的那隻手抹抹眼睛。>\n\n最好的朋友！！！真高興你在這裡！\n\n我真希望走到哪裡都跟最好的朋友一起去…\n\n我也能到淵喉走走嗎？', '', 38134),
(206149, 'zhTW', '', '幫我安排了時間表，我也照表操課。\n\n擬定了計劃，賦予了希望。\n\n但全都在火焰的淚水中燃燒殆盡。\n\n在這個地方無所遁形。他們看著你。就算他們沒有，那其他人也會如此。', 38134),
(206148, 'zhTW', '', '你好，聖光，我的老朋友。\n\n又來灼燒我了嗎？\n\n在這道聖光中燒光我的時光，誤解了我見解的崩解。\n\n在治癒中受傷，在生命中灼燒。', 38134),
(206147, 'zhTW', '', '這些安排的人跑哪兒去？\n燃燒、疼痛、憂慮，\n他們的尖叫聲不曾離去，\n連腦袋都變成一條死魚！\n\n等等，剛才是旋律還是押韻而已？\n或許我不應該懷疑，\n真希望我有隻螞蟻，\n難道懲罰真有意義？', 38134),
(206146, 'zhTW', '', '他們說那早就結束了！結束了！那是說好久以前的事了？還是說還要好久才會結束？還是說好久以前就不幹了？\n\n時間可不是溜溜球！\n\n等等？我現在應該知道什麼是溜溜球了嗎？現在是何時？', 38134),
(206145, 'zhTW', '呃…淵喉行者，問你一個問題。瑞文崔斯是靠我和那些小伙子在維持，而且狀況越來越好。\n\n那…在淵喉負責這些事的是誰？我是說，你去過那裡，所以叫做淵喉行者。\n\n更重要的是，那些創造淵喉的人，有時間休息嗎？', '', 38134),
(206144, 'zhTW', '', '然後你就可以回來這裡，我們好好慶祝一下。', 38134),
(206143, 'zhTW', '', '我應該派獸欄管理員到集合位置，從你那裡帶吉祥物回來。', 38134),
(206142, 'zhTW', '', '我聽到的是那位好看的$p往這裡走過來的腳步聲嗎？', 38134),
(206141, 'zhTW', '', '艾弗里德，看吧？我就跟你說今天會是美好的一天。$p來了。', 38134),
(206140, 'zhTW', '', '啊，淵喉行者，我有什麼榮幸能幫你？', 38134),
(206139, 'zhTW', '', '噢，親愛的艾弗里德，你可以幫我拿個新的…', 38134),
(206138, 'zhTW', '', '<女伯爵滿懷暖意地擁抱了你。>\n\n我美麗的$p啊，你從罪殞城帶來了什麼樣的消息？我相信大家一定都過得很好。\n\n你下次見到希奧塔和親愛的王子時，請為我向他們獻上我的愛。\n\n或者，如果我還有幸能參加下一場餘燼宴會的話，我可以親自和他們說。\n\n但別再談他們了，你自己呢？自從你上次來訪後我就一直很想念你。', 38134),
(206137, 'zhTW', '', '親愛的，我一直在想著你。\n\n告訴我，你現在最想要的是什麼？\n\n不是你的人生目標或是孩提時的夢想。夢想會淡去，目標會改變。\n\n直覺不會休息，會在情感表面的底下靜靜待著。如果你放下了戒心，這個直覺就會拉著你走。\n\n<女伯爵微笑著。>\n\n很好。可別跟我說囉…留在你自己的心裡就好。這是確保安全的唯一方法。', 38134),
(206136, 'zhTW', '', '請告訴我，是什麼風把偉大的淵喉行者吹到我塔上的？\n\n再幫我一個忙，可以嗎？\n\n<女伯爵微笑著。>\n\n注意一下你要求幫忙的對象。\n\n許多可憐的靈魂，受到慾望的蒙蔽，才發現自己欠了殘酷的主人一堆債。', 38134),
(206133, 'zhTW', '凡人，我沒辦法向你一樣讓眼睛流淚，但我承認我一直在回想我們第一次碰面的那一天。\n\n那天又是一個在尊殞的無聊又沒事做的值班…然後不知從哪裡跑來了一個淵喉行者本人，而且要撼動整個暗影之境！\n\n那時沒想過我們最後會變成最好的朋友。真是和淤泥一樣笨…', '', 38134),
(206132, 'zhTW', '嗨，朋友。\n\n如果你有需要幫忙的地方，只要說一聲就好了。我還有些事要處理，但只要你有需求我就會空出時間。\n\n喔對了，我很期待下一場餘燼宴會。希奧塔教了我一些新東西，用來和他那些高貴的朋友們說上幾句。', '', 38134),
(206130, 'zhTW', '如果你有什麼事的話，我現在有空。', '', 38134),
(206129, 'zhTW', '老兄，你好啊！\n\n我和棍臉最近都被社交生活忙死了，全都感謝你和我們在餘燼宴會交到的那些新朋友們。\n\n$p，但我們永遠會為最好的朋友空出時間。\n\n你只要說一聲，我們隨傳隨到。', '', 38134),
(206128, 'zhTW', '', '對我們的領域精心計畫的侵略，就必須以絕對的武力制伏。在此向那些保護我們秘密的人致敬。', 38134),
(206127, 'zhTW', '嘿，兄弟。你最近有沒有聽說社交名流在討論我和我的棍臉？\n\n我沒辦法每一場宴會都參加，但我想確保他們不會忘了我們。你懂吧？\n\n能從這些淤泥勞動之中抽空出來參加宴會其實滿不錯的。\n\n你和王子都對泥掘者很好。為此我尊敬你們。', '', 38134),
(206126, 'zhTW', '', '我們對戰爭的知識，以及破壞力強大的機具是非常重要的資產。絕對不能讓那些想對暗影之境不利的人奪走。', 38134),
(206125, 'zhTW', '可是我喜歡髒髒的舊帽子…', '', 38134),
(206124, 'zhTW', '棍臉，走吧，該打扮得像那些高貴得不得了的貴族了。', '', 38134),
(206122, 'zhTW', '倫德，我還是搞不懂，你為什麼想要找頂時髦的帽子…', '', 38134),
(206120, 'zhTW', '棍臉，$p很忙的，忙著拯救暗影之境和大家。而且，$g他:她;還是我最好的朋友！', '', 38134),
(206119, 'zhTW', '嗨，我最喜歡的淵喉行者！上次坐馬車的路上沒看到你。', '', 38134),
(206118, 'zhTW', '我當然需要！你根本不知道高級時尚是怎麼回事。', '', 38134),
(206117, 'zhTW', '我在跟你說話，倫德。你不需要那麼多帽子…', '', 38134),
(206116, 'zhTW', '別盯著看，很沒禮貌。', '', 38134),
(206115, 'zhTW', '嘿，你看。是那個時髦的行者！', '', 38134),
(206114, 'zhTW', '那些無賴對這些大廳的聖潔毫無尊敬之意！', '', 38134),
(206113, 'zhTW', '$p！能看到你真是個天大的驚喜啊！', '', 38134),
(206112, 'zhTW', '$p，我希望那是你…', '', 38134),
(206111, 'zhTW', '誰在那裡？給我現身！', '', 38134),
(206110, 'zhTW', '啊，我親愛的老朋友。我原本還希望你最近能來拜訪一下呢。\n\n自從我們在上次的餘燼宴會碰面後，好像過了一個永恆那麼久。我相信罪殞城在你嚴厲的看管之下安然羔恙吧？\n\n誓奴和我在拯救失落的靈魂上大有進展，全都感謝你的幫忙。\n\n$p，你真是個好朋友。更重要的是，你有一個善良的心。\n\n或至少你很努力假裝如此！\n\n<墓穴守衛者凱希爾大笑。>\n\n但我想你應該不是來聽我講老笑話的吧。\n\n老朋友，你今天來我在瑞文崔斯的小容身之處有何貴幹啊？', '', 38134),
(206109, 'zhTW', '啊，$p。\n\n<墓穴守衛者發出了解脫且放鬆的一口氣。>\n\n在這充滿叛徒和褻瀆者的地方能看到你還真是高興。\n\n我在這裡一直有進展，但我得承認我有點分心。\n\n我發現我的思緒一直飄向下一場餘燼宴會…\n\n我還是很驚豔你和雷納瑟王子認為我有資格再次受邀，我真是感到光榮無比。\n\n閒話就說到此吧。朋友，我能幫你什麼忙嗎？', '', 38134),
(206108, 'zhTW', '這一點會很好玩！', '', 38134),
(206107, 'zhTW', '一點點的旅行、一點點的果姆工鑄、一點點的冒險…', '', 38134),
(206106, 'zhTW', '這裡很棒，對吧？寧靜又祥和。只有像你和那邊的寇特這樣努力工作的蘑菇法師。當然，還有我。努力工作都是為了讓亞登曠野順利運作。$b$B最近沒什麼人會想到我，還好有你來陪我聊聊天，只是想讓你知道我很珍惜你的陪伴。$b$B不過別以為我會因此而對你比較好！還有好多工作要做！', '', 38134),
(206105, 'zhTW', '', '汎希爾的宴會聽起來…很有趣。', 38134),
(206104, 'zhTW', '', '請容我發言，我很高興軍政官是派你來找我。', 38134),
(206103, 'zhTW', '我已經在這裡很久很久了。我還記得一開始的時候，就只有我和女王而已。我們那時候好忙碌。她真的非常勤奮…$b$b才不像現在的這些蠢蛋！整天就想利用那些妖精之環。根本沒人問過那些環的感受！看來，我得請女王管好她的臣民了！', '', 38134),
(206102, 'zhTW', '歡迎來到蘑菇環，年輕人。$b$B要知道，這裡不是誰都可以來的。只有我最信任的蘑菇法師才能到我家來，也就是妖精之環的中心點。$B$B你就是我最信任的！現在你也是我的蘑菇法師了！繼續努力，在未來會有更好的發展，我相信你。', '', 38134),
(206101, 'zhTW', '希望不會太熱…', '', 38134),
(206100, 'zhTW', '聽說餘燼區那一帶很明亮，對吧？', '', 38134),
(206096, 'zhTW', '', '你沒被跟蹤吧，對嗎？', 38134),
(206095, 'zhTW', '', '最好告訴我你要討論的是有趣的事，淵喉行者。', 38134),
(206091, 'zhTW', '噢，深根地窖！很高興看到它還在。哇，你最近做了很多事。去吧，好好休息，等你放鬆一下再回來！', '', 38134),
(206088, 'zhTW', '你的野心是不是超出你的能力了，淵喉行者？', '你的野心是不是超出你的能力了，淵喉行者？', 38134),
(206085, 'zhTW', '淵喉行者，真是英勇的表現啊！你的嗜血慾望滿足了嗎？', '', 38134),
(206077, 'zhTW', '伯瓦爾在受苦。黑暗之力正在前來對抗我們。', '', 38134),
(206075, 'zhTW', '', '統御者徹底毀滅了他們。即使無人倖存，他們的火焰仍在這裡燃燒。表揚那些曾守禦過瑪卓薩斯的人。', 38134),
(206073, 'zhTW', '在經歷過無數次生死交關的艱難戰鬥和戰爭後，竟然在死後還能夠得到這麼多問題的答案，還真是…有意思…', '', 38134),
(206070, 'zhTW', '', '百衛。這些傀儡透過靈魄獲得生命，在昇靈堡有著眾多使命。\n\n它們協助我們為志士訓練、防衛我們的家園並在需要時能執行見不得光的任務。其中有一些，例如像我一樣的助憶士能代表文庫呈現豐富的知識。', 38134),
(206069, 'zhTW', '', '<接待員助憶士發出了一連串的嗚響聲。>\n\n啊。貓頭鷹。在你的國度中，貓頭鷹是夜行性鳥類，對吧？\n\n<接待員助憶士向西卡的方向比了一個手勢。>\n\n這位是侍者。他們從昇靈堡的靈魄之中現身，幫助琪瑞安處理日常事務。他們最喜歡的就是服務。', 38134),
(206060, 'zhTW', '你身上穿的東西還真多。穿成這樣跑來跑去都不會累嗎？', '你身上穿的東西還真多。穿成這樣跑來跑去都不會累嗎？', 38134),
(206055, 'zhTW', '可以在我的卷軸上簽名嗎？', '可以在我的卷軸上簽名嗎？', 38134),
(206054, 'zhTW', '$n，我是你最狂熱的粉絲！', '$n，我是你最狂熱的粉絲！', 38134),
(206052, 'zhTW', '我想你應該會有比來找我說話更有趣的事情可以做，嗯？', '', 38134),
(206050, 'zhTW', '什麼東西你一說他的名字就會消失？', '', 38134),
(206049, 'zhTW', '<狐王雷納德露齒而笑。牙齒很多。>', '', 38134),
(206048, 'zhTW', '嗯嗯嗯。好。我覺得你可以。', '', 38134),
(206047, 'zhTW', '哇，哇，哇！看看我遇到什麼了？一個$c。真有意思。', '', 38134),
(206042, 'zhTW', '', '淵喉行者，是你嗎？很好，巴爾米達先前已經告知我你的小小詭計了。\n\n儘管如此，你對祭儀家族做出的奉獻還是不容忽視。我把你視為盟友，所以我不會向我的臣民揭發你的偽裝。\n\n好好享受你在這裡的時光吧，「侍僧」。', 38134),
(206041, 'zhTW', '', '直接對藩侯說話已經超出你的職權了，侍僧。\n\n我建議你盡速離開。', 38134),
(206039, 'zhTW', '侍僧，你身上那把武裝看起來還真不錯。\n\n你該不會是想要轉換職業吧？', '', 38134),
(206037, 'zhTW', '真希望我們有朝一日能尋得平靜。', '真希望我們有朝一日能尋得平靜。', 38134),
(206036, 'zhTW', '我無法停止想那些殞落的靈魂。為什麼會發生這種事？', '我無法停止想那些殞落的靈魂。為什麼會發生這種事？', 38134),
(206035, 'zhTW', '我們的新家…真是太美了。', '我們的新家…真是太美了。', 38134),
(206034, 'zhTW', '再次回到森林的感覺真好。謝謝你。', '再次回到森林的感覺真好。謝謝你。', 38134),
(206033, 'zhTW', '能再度高飛真是太好了！', '', 38134),
(206016, 'zhTW', '', '我從來沒有經歷過這麼嚴重的衝突。我的族長曾教導過我，但不管現在還是往後我仍然覺得難以克服。\n\n請你理解我是很有自信地坦漏我的煩惱，不然女爵會怪我太害羞。', 38134),
(206014, 'zhTW', '$n，你證明了你自己還算有用處。\n\n如果你有機會用比較正常的方式回來瑪卓薩斯，那祭儀家族會很歡迎你的加入。', '', 38134),
(206013, 'zhTW', '', '我上次來這裡的時候，統御者還在。\n\n既然我們已經回來了，我不能放著他的熔爐不管。要是以前，他肯定會唸個沒完沒了。', 38134),
(206008, 'zhTW', '', '做為回報你協助我們的額外獎勵，歡迎你任意使用我們的造型人體模型。\n\n這能讓你進行更多變的服裝設計，雖然你要先自己想辦法拿到額外的配件才行。', 38134),
(206007, 'zhTW', '', '侍僧，你想要從我這裡得到什麼？', 38134),
(205988, 'zhTW', '$n，記得要小心謹慎。\n\n不能讓任何人知道我們的…交易。', '', 38134),
(205975, 'zhTW', '嗯，我想試著為瑪拉茲莫斯照顧這些蘑菇。這場乾旱對他的根部造了非常大的傷害。', '嗯，我想試著為瑪拉茲莫斯照顧這些蘑菇。這場乾旱對他的根部造了非常大的傷害。', 38134),
(205973, 'zhTW', '哈囉！\n\n抱歉，我現在沒空閒聊！這些蘑菇沒辦法好好照顧自己的。\n\n<他咯咯笑著。>\n\n需要什麼嗎？', '哈囉！\n\n抱歉，我現在沒空閒聊！這些蘑菇沒辦法好好照顧自己的。\n\n<札其咯咯笑著。>\n\n需要什麼嗎？', 38134),
(205972, 'zhTW', '我很抱歉，淵喉行者，但你已經拒絕了我們聖所的款待。', '', 38134),
(205971, 'zhTW', '', '我們前來伸張正義，實施懲戒。回應公然侮辱聖光的惡行。\n\n我們人數眾多，不計其數。每顆心裡都燃燒著正義的怒火。\n\n無數人殞落，但在殞落之前，我們也朝敵人釋放了無法抵禦的大火。我們的復仇留下的灼熱傷痕還在天空久久不散。\n\n我支離破碎地躺在戰場上，直到看不見的人找到了我，並且保存下來，作為讓他們的王發洩怒火的容器。\n\n但我逆來順受，因為我知道這都是聖光的安排，我欣然接受，而你們也該如此，凡人。', 38134),
(205969, 'zhTW', '', '凡人，家園在呼喚著我。然而，我再也無法感受它的溫暖了。\n\n我的族人不會再像以前那樣接納我，他們認為我離經叛道、殘破不堪，被這個國度的黑暗主宰腐化。\n\n現在，我眼前只剩下一條路，那就是確保聖光照耀此地。使得正義得以伸張，使得死亡適得其所。\n\n因此我留在這裡，履行我的職責，直到我的最後一絲光芒消失殆盡。', 38134),
(205968, 'zhTW', '這需要一點時間。像你這樣的人無法估計的時間。\n\n但在那時候，我看到我認識最久的朋友、最親密的知己，還有最尊貴的主宰從最激烈的指責中更進一步，他真正的意圖也再次得到證實。', '', 38134),
(205965, 'zhTW', '', '我想回顧我們漫長歷史中最重要的一天。', 38134),
(205964, 'zhTW', '', '那是個再尋常不過的日子。志士展開他們的旅程。侍者負責照料他們。晉升者一如往常地從奧睿博司來來去去。', 38134),
(205963, 'zhTW', '', '我很清楚地記得…從奧睿博司穩定流入的靈魂，就這麼…停止了。上一秒還在迎接新來的靈魂，下一秒就完全沒了。', 38134),
(205962, 'zhTW', '', '一開始我們以為只是暫時停止而已。畢竟，有資格進入昇靈堡的高貴靈魂沒那麼多！然後一天過去了。然後兩天。然後十天。', 38134),
(205960, 'zhTW', '在先前那些首席熔爐士的既有傳統下，我創造了新的東西！', '', 38134),
(205959, 'zhTW', '我們有很多百衛，各有不同的用途。有又大又壯的。有動作快又會飛的。也有可以騎乘、跑得快的。', '', 38134),
(205958, 'zhTW', '我想想…百衛不會做什麼？他們會說話，但不是很聰明。他們很多事都不知道。為什麼不會呢？', '', 38134),
(205957, 'zhTW', '我是首席熔爐士，所以我做了一個。我拿了一個軍教官，裝上心智核心。雖然它會一直重複同樣的事，但它也能回答問題！非常…非常簡單的問題。', '', 38134),
(205956, 'zhTW', '想想可能的情況！我們能保存的所有知識！我要叫它…助憶士。好。太棒了！', '', 38134),
(205955, 'zhTW', '', '我最近遇到了一個非常迷人的志士。她的名字是萊索妮雅，我們因為對音樂的共同喜好而結緣。', 38134),
(205954, 'zhTW', '', '她彈豎琴，我吹笛子。我們在分開之前互相演奏了好幾個小時。我們也說好下次有機會的時候要一起訓練。', 38134),
(205953, 'zhTW', '', '當我們在競技場相遇時，那感覺就像是在跳舞一樣。我們的動作是如此地一致。我差點忘了我們是去訓練的！', 38134),
(205952, 'zhTW', '', '之後，我們找到一個安靜的地方朝樂土堡窺探，我們還談論對於未來的夢想。她想到忠誠神殿加入德沃絲，而我覺得純潔神殿才是我的歸屬。', 38134),
(205951, 'zhTW', '', '我感覺好像已經認識她很久很久了。你覺得…她會同意成為我的靈魂之絆嗎？', 38134),
(205950, 'zhTW', '你真是厲害又勇敢，阿嗚。我以你為傲。', '', 38134),
(205949, 'zhTW', '耶！真是太讓人興奮了。我想我需要休息一下，這樣我才能準備好再度回到捍衛亞登曠野的戰場。', '', 38134),
(205948, 'zhTW', '阿嗚！你聽到了嗎？我們受邀參加派對了！', '', 38134),
(205946, 'zhTW', '2…', '2…', 38134),
(205944, 'zhTW', '我們要阻擋他們的追趕！罪牙家族和艾爾摩家族站在同一陣線！', '我們要阻擋他們的追趕！罪牙家族和艾爾摩家族站在同一陣線！', 38134),
(205942, 'zhTW', '這個小偷，我不會善罷干休的！', '這個小偷，我不會善罷干休的！', 38134),
(205941, 'zhTW', '1…', '1…', 38134),
(205940, 'zhTW', '我只會給你數到5。', '我只會數到5。', 38134),
(205939, 'zhTW', '你在做什麼？立刻從巴爾薩札身上下來！', '你在做什麼？立刻從巴爾薩札身上下來！', 38134),
(205938, 'zhTW', '凱文在走完他專屬的捷徑後，會在我們的新朋友那裡和你碰面。只要把它們趕到他那邊就好，剩下的他會處理！', '', 38134),
(205919, 'zhTW', '', '雖然我很確定希奧塔一定會被這種搞笑的行為給逗樂。嗯。', 38134),
(205918, 'zhTW', '', '不行，太直接了。', 38134),
(205913, 'zhTW', '你今天用的貨幣是什麼，$p？\n\n硬幣還是你的命？', '', 38134),
(205876, 'zhTW', '遊蕩的靈魂，過來我這邊。我有話要跟你說。', '遊蕩的靈魂，過來我這邊。我有話要跟你說。', 38134),
(205862, 'zhTW', '你可以在這個地方使用與個人物品相關的服務。我們對這些概念相對陌生，所以仲介者很好心地幫忙為您安排了這些服務。', '', 38134),
(205856, 'zhTW', '噢，我從來不會拒絕再來一杯的。甜甜的，順口的飲料。你喝過靈魄邁泰嗎？瑪克調的飲料是這一帶最好喝的。', '', 38134),
(205855, 'zhTW', '我絕對可以再來一杯，狀況這麼糟糕，此時不喝更待何時。看看你能不能給我來點烈酒。或許該來杯蛾毛酒。濃烈的飲料可能可以幫我清醒一點，看看能不能想起點什麼。', '', 38134),
(205854, 'zhTW', '真的嗎？你要請我喝？你是我新的最好的好朋友。我只是突然想大聲說出來。$b$B我想來杯木魈沙瓦。那飲料的獨特美妙風味，酸澀味還讓你的嘴唇揪起來，揪到臉頰都痛了！哈！', '', 38134),
(205853, 'zhTW', '不，謝了。我已經喝太多了。我需要<嗝！>清醒一下！', '', 38134),
(205850, 'zhTW', '嘿…<嗝！>可以幫我一下嗎？$b$B我的名字是爍爍迪，我可是幻象大師。至少，我記得我應該是。嗯，我承認我有點喝太多了，呃…<嗝！>…$b$b我分不出哪一個我才是真的我了。我想這應該就是<嗝>存在主義危機吧。唉唷。', '', 38134),
(205849, 'zhTW', '<沃爾大師上下打量著你。>\n\n就你吧。\n\n<沃爾大師對著他身邊的其他人大喊。>\n\n苦痛劇場的新血來了！', '', 38134),
(205848, 'zhTW', '真是精彩的表演，這可不是我在吹牛。已經有好幾個人稱讚我。$B$B謝謝你，朋友。遇到跟我一樣愛好音樂的人不是常有的事。', '', 38134),
(205846, 'zhTW', '噢，暗夜妖精團結一心，噢！暗夜妖精團結一心，噢！', '', 38134),
(205845, 'zhTW', '賞點靈魄給獵人吧！', '', 38134),
(205844, 'zhTW', '讓他再出發去冒險！', '', 38134),
(205843, 'zhTW', '為他大聲歡呼，', '', 38134),
(205842, 'zhTW', '英雄通過了考驗。', '', 38134),
(205841, 'zhTW', '結果顯而易見，', '', 38134),
(205840, 'zhTW', '斬聲響徹了林地。', '', 38134),
(205839, 'zhTW', '只需致勝的一擊…', '', 38134),
(205838, 'zhTW', '冬狼拔出了武器！', '', 38134),
(205837, 'zhTW', '受到魔影的挑釁，', '', 38134),
(205836, 'zhTW', '三個嘴巴的仇敵！', '', 38134),
(205835, 'zhTW', '我們的獵物就在那裡，', '', 38134),
(205834, 'zhTW', '穿過濃密的霧氣…', '', 38134),
(205833, 'zhTW', '越過泥濘的沼地…', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205832, 'zhTW', '女王在上，這很不錯！你確定在你先前的世界中你不是吟遊詩人嗎？$b$B<水仙花了點時間整理自己的心情。>好了，我準備好開始了。準備好要聆聽了嗎？', '', 38134),
(205831, 'zhTW', '艾澤拉斯是什麼？是你嗎？你就是一個艾澤拉斯？這首歌不是寫給你的，你懂吧。這是要用來激勵整個亞登曠野的暗夜妖精，尤其是現在在這裡的暗夜妖精。要告訴大家我們是英雄，值得受到稱讚、吹捧和打賞的英雄！', '', 38134),
(205830, 'zhTW', '朗朗上口，但沒什麼趣味。況且，亞登曠野其實也沒有那麼多峽谷。給你的小知識！', '', 38134),
(205829, 'zhTW', '我喜歡！這樣有響應號召的感覺，而且能提醒大家他是多麼致力於維持和平。我們來試試看吧。好了，現在輪到大結局了！$B$B賞點靈魄給獵人吧！噢…噢…', '', 38134),
(205828, 'zhTW', '雖然說這個畫面會看起來很有趣啦，但這樣大家的手指頭可能會不保。我看還是別開這種玩笑了吧。', '', 38134),
(205826, 'zhTW', '是說，大家開心再見是很棒啦，但我希望歌詞能更加…充實一些。你知道嗎，當英雄也是挺累的，而且努力工作就會需要獲得回報，你懂我在說什麼吧。', '', 38134),
(205825, 'zhTW', '長久以來，我都在引導年輕且有野心的精靈尋找他們的道路。\n\n現在我自己的旅程才正要開始。我在想他們第一次踏入新林地時也是這種感覺嗎？', '', 38134),
(205824, 'zhTW', '小心你的四周！因為恐懼而跑到散射範圍裡絕對會慘不忍睹。', '', 38134),
(205823, 'zhTW', '歐提姆的生成物對其他類型的軟泥怪來說很脆弱。試著將它們的弱點化為你的優勢。', '', 38134),
(205822, 'zhTW', '', '我知道是你把我從黑暗又可怕的地方救出來。我很感激。\n\n但我無法忘記部落讓我的人民受到怎樣的苦難。\n\n正義一定會得到伸張。', 38134),
(205821, 'zhTW', '', '戰爭讓我們的人民傷亡慘重。比任何人都慘重。\n\n絕對不能就這樣罷休。\n\n希望在我們復仇的時刻，你會站在我們這一邊。', 38134),
(205820, 'zhTW', '吞噬裂隙撕裂我們的現實，通到吞噬者的家園。牠們毫無道理地傷害除了阿札魯克斯以外的一切。', '', 38134),
(205818, 'zhTW', '亞薩諾斯不再有所保留了。做好準備躲過他強力的揮擊…然後想辦法別太靠近競技場的邊緣。', '', 38134),
(205817, 'zhTW', '噢噢…這樣很有氣勢。吟遊詩人就是要給聽眾聽覺與幻想的綜合饗宴！$b$B等等，你是怎麼知道牠有三張嘴巴的？$b$B不管怎樣，下一段是：\n結果顯而易見，\n英雄通過了考驗。\n為他大聲歡呼，\n…嗯，為他大聲歡呼，然後呢…？', '', 38134),
(205816, 'zhTW', '嗯，這樣太沒戲劇效果了吧？勇者水仙，在野獸睡覺的時候戳了牠一刀！不，不，那樣不行…嗯…', '', 38134),
(205815, 'zhTW', '我很佩服你對押韻的堅持，但是我們是在跟邪靈戰鬥。雖然我常把事情的真相在歌詞裡稍微誇飾一下，我覺得這樣有點太跳躍了，如果你懂我意思的話。$b$B<水仙朝你用力地眨眨眼睛。>', '', 38134),
(205814, 'zhTW', '太好了！這是第一段詞：\n越過泥濘的沼地…\n穿過濃密的霧氣…\n我們的獵物就在那裡，\n…最後一句我想不出來了。有什麼想法嗎？', '', 38134),
(205813, 'zhTW', '請千萬不要讓科林斯離開你的視線，他會突然消失不見跑去吸取靈魄，恢復他的精力。', '', 38134),
(205812, 'zhTW', '謝謝你帶我回來。\n\n如果你打算回去淵喉的話，你可以順便幫我找個人嗎？\n\n她的名字叫做德拉茵。', '', 38134),
(205811, 'zhTW', '突蘭提亞克能從她的眾多骨匣抽取能量。假使她的骨匣全都完整無損，就無法對她造成傷害，但每當有骨匣被摧毀就會賦予她強大的能力。', '', 38134),
(205810, 'zhTW', '佐司特花能保護你不受到那頭怪物的攻擊，但時間很短暫。', '', 38134),
(205809, 'zhTW', '嗨，又見面了！我敢發誓我們之前見過面…$B$B算了，你來得剛好。恐怕身為作詞者的我在創作時有點碰壁了，我在想你有沒有意願幫我一起寫歌詞。這首歌是關於我那邊那位高貴的朋友，以及我們最近一場和最恐怖的怪物的戰鬥。', '', 38134),
(205808, 'zhTW', '', '<瑪克攪動飲料倒進搖酒器裡，開始用力搖晃。>\n會死？說什麼？要不然你是還活著嗎？哈哈！$b$B噢，你是認真在問。不，果姆汁沒什麼危險。只會讓飲料尾韻多點輕柔的甜味。\n<瑪克給你一杯飲料。>\n好好享受！', 38134),
(205807, 'zhTW', '', '<瑪克上下打量著你。>$b$b嗯，看來我找到適合你的東西了。我需要一點果姆汁，可是不巧用完了。你可以到捏捏那邊去幫我拿一點回來嗎？別擔心會傷害到牠，牠很喜歡得到關注。', 38134),
(205806, 'zhTW', '', '伊露恩沒有遺忘我們。你救了我們，這就是證明。\n\n我在想是不是還有事情等著我們去做，還有責任要盡。\n\n我們一定要有耐心。', 38134),
(205805, 'zhTW', '', '死靈領主的野心是否能存續，就掌握在我們手裡了。如果你能再次加入我們的行列，我們就能一起奮戰，讓不同的勢力攜手合作，組成足以逆轉命運的大軍。', 38134),
(205804, 'zhTW', '尋求瑞文崔斯的救贖是一條漫漫長路，但罪殞城的黑暗王子已經準備好踏上旅途了。你已經幫助我們邁出這趟旅程的第一步了。現在我們需要齊心努力才能從君主和他的忠誠者手中把瑞文崔斯奪回來。', '', 38134),
(205803, 'zhTW', '', '是仇恨導致這一切。\n\n可是我希望正義能為一切畫下句點。', 38134),
(205802, 'zhTW', '如果你注射了夠多劑歐提姆的刺激物，你就會獲得無比的力量！小心那些冰冰的東西，那會抵消你的刺激能量。', '', 38134),
(205801, 'zhTW', '', '當我閉上雙眼時，我以為我接下來會看到伊露恩的臉孔。\n\n謝謝你沒有放棄我們。', 38134),
(205800, 'zhTW', '', '就和你親眼看過的一樣，亞登曠野的林地正在死去。隨著巨大的星界之樹一棵棵倒下，我們的女王也變得愈來愈絕望。女王和她的暗夜之廷知道你擁有的力量。幫助我們重建亞登曠野的平衡。', 38134),
(205799, 'zhTW', '飛靈需要他們的朋友前來支援。如果他們落單，他們會逐漸衰弱而且很容易受到攻擊。', '', 38134),
(205798, 'zhTW', '你也在昇靈堡見證到那場背叛了吧，我們高貴的琪瑞安正在面對那場背叛的恐怖後果。棄誓者部隊威脅著要將我們的國度整個瓦解。如果我們要奪回昇靈堡並拯救暗影之境，那你的協助必不可缺。', '', 38134),
(205797, 'zhTW', '你速度要夠快，才能克服這個挑戰！還要注意奧德林的漩渦，風暴的中心就是最安全的地方。', '', 38134),
(205796, 'zhTW', '那些爬行者很危險。記得務必保持距離，別讓牠們把你逼到穢物裡去！', '', 38134),
(205795, 'zhTW', '小心那些長矛。三支就能把你擋在原地，能讓你恢復自由行動的物品或技能就是關鍵！', '', 38134),
(205794, 'zhTW', '閃閃發光。你戴上那頂帽子就會變成我們泥掘者的王子了。', '', 38134),
(205793, 'zhTW', '這頂帽子就很時髦。看看那些石頭。', '', 38134),
(205791, 'zhTW', '你看過這麼漂亮的森林嗎？看看那些樹！\n\n謝謝你把我從可怕的地方救出來，這樣我才能親眼看到它。', '', 38134),
(205790, 'zhTW', '這是不是挺時髦的？', '', 38134),
(205789, 'zhTW', '倫德，你一定要戴那頂帽子去宴會嗎？好吧，隨便啦，你想怎樣就怎樣吧，只要能馬上出發就好。', '', 38134),
(205788, 'zhTW', '哈呃！看看我，又美又粉又尖。', '', 38134),
(205781, 'zhTW', '', '我當然認識他，他是葛溫希爾！而且有誰能忘得掉他那可靠的夥伴水仙呢？$b$b聽好了，不要惹事生非就對了。上次有一群木魈跑來找麻煩，結果和他打了起來。花了我好久才把這裡清乾淨。內臟是怎麼噴到天花板的？！那麼多血又是打哪來的？！', 38134),
(205779, 'zhTW', '', '好啊，但別弄壞了。瑪拉茲莫斯總是在抱怨豎琴勾到他。不要問我他是怎麼辦到的。', 38134),
(205778, 'zhTW', '', '噢，你說爍爍迪嗎？嗯他人不錯，很喜歡幻象。有時候他會醉到連哪個是真的自己都記不得。$b$b但真的那傢伙很愛喝。他最喜歡的飲料是靈魄邁泰，他說那是他在整個亞登曠野找到的最順口最甜美的飲料。$b$b知道他有多愛喝，讓我覺得那是滿大的稱讚。想喝一杯試試嗎？', 38134),
(205777, 'zhTW', '對啊，倫德，你變裝了一下，沒人會認出你啦。', '', 38134),
(205776, 'zhTW', '噢噢！喂！你看我的帽子，棍臉。它在發亮…著火了。', '', 38134),
(205771, 'zhTW', '我們所有的努力都是為了這一刻。', '', 38134),
(205757, 'zhTW', '侍者是來這裡紓壓的…這裡就像他們的鳥園。', '', 38134),
(205756, 'zhTW', '侍者的烹飪建議通常是對的。他們最會做貓頭鷹料理。', '', 38134),
(205750, 'zhTW', '你知道為什麼侍者的肌肉這麼發達嗎？因為他們一直在幫忙琪瑞安打理裝備。', '', 38134),
(205748, 'zhTW', '', '侍者們奉獻他們的一生，不只為了琪瑞安，也是為了整個昇靈堡。\n\n他們當中的每一個都值得悉心照料。當他們來休息的時候，我們希望他們也能有歇息和放鬆的時刻。', 38134),
(205746, 'zhTW', '淵喉行者，就等你準備好。', '', 38134),
(205743, 'zhTW', '哪裡需要加強嗎？', '哪裡需要加強嗎？', 38134),
(205742, 'zhTW', '有點頭暈嗎，或許力道太強了…', '有點頭暈嗎，或許力道太強了…', 38134),
(205741, 'zhTW', '休息一下對你很好。讓肌肉恢復一下！', '休息一下對你很好。讓肌肉恢復一下！', 38134),
(205738, 'zhTW', '需要幫忙處理任何魔法的事嗎？', '', 38134),
(205737, 'zhTW', '你的皮膚看起來非常不錯，如果你不需要了可以給我嗎？', '', 38134),
(205734, 'zhTW', '有誰需要治療嗎？', '', 38134),
(205733, 'zhTW', '我從未放棄希望。', '我從未放棄希望。', 38134),
(205732, 'zhTW', '這種折磨何時才會結束？', '這種折磨何時才會結束？', 38134),
(205731, 'zhTW', '我祈禱女神能聽見我們的願望…', '我祈禱女神能聽見我們的願望…', 38134),
(205730, 'zhTW', '結束了嗎？', '結束了嗎？', 38134),
(205726, 'zhTW', '', '我不會被擊垮的。', 38134),
(205725, 'zhTW', '我想念幽影谷。', '', 38134),
(205722, 'zhTW', '德拉茵在哪裡？', '', 38134),
(205720, 'zhTW', '一切都在燃燒…就連幼苗也是…', '', 38134),
(205717, 'zhTW', '榮譽蘑菇法師是吧？瑪拉茲莫斯好像真的很喜歡你。歡迎加入我們！$B$B呃，之前是只有我啦，但很高興你來了！既然你要負起蘑菇法師的責任，你將得以進入瑪拉茲莫斯的通道，也可以得到一些很棒的特殊能力，以及蘑菇法師的專屬裝備。幫助我和瑪拉茲莫斯也能獲得代幣，可以來跟我交換。', '', 38134),
(205716, 'zhTW', '蘑菇環以前像是個中繼站，可以前往亞登曠野幾乎任何地方的交通樞紐。$b$B隨著時間流逝和最近的乾旱，即使是瑪拉茲莫斯也失去了某些通道。或許只要有足夠的靈魄，就能修復這些傷害…$b$b但目前來說，我們蘑菇法師的責任是照護瑪拉茲莫斯。我們會讓亞登曠野的根比以往更加強健，也會致力讓這裡永存。', '', 38134),
(205715, 'zhTW', '歡迎來到蘑菇環，朋友。有些人說這裡是亞登曠野的中央，也是瑪拉茲莫斯最初生長的地方。只有他才知道。', '', 38134),
(205714, 'zhTW', '', '如此黑暗…\n\n我已經盡我所能拯救他們了，不是嗎？', 38134),
(205711, 'zhTW', '我們從暴風城的監獄裡救出了贊達拉的塔蘭姬公主。\n\n公主的船正載著她回到贊達拉，但是聯盟狗還在緊追不捨。\n\n如果我們想打贏這場仗，就需要贊達拉海軍的力量。絕不能讓聯盟再囚禁公主。\n\n我們會從空路提供協助，並且確保贊達拉會加入部落。\n\n來吧。', '', 38134),
(205709, 'zhTW', '', '有一場大火…然後…\n\n我在歌唱。\n\n天啊，發生什麼事了？', 38134),
(205704, 'zhTW', '這是輕鬆的生活。小伙子會習慣的。', '', 38134),
(205699, 'zhTW', '你不能拿蠟燭！不能能能能。', '', 38134),
(205698, 'zhTW', '真可惜我們已經沒有敵人可以殺了。\n\n等他們回來的時候，我會做好準備。', '', 38134),
(205696, 'zhTW', '哈！愚蠢的泥掘者。空蕩蕩的城堡都是我的啦！', '', 38134),
(205691, 'zhTW', '首先，要把泥拳惹毛！再讓他一頭衝進石頭堆裡！\n\n想想頭會有多痛…\n\n<泥拍瘋狂地咯咯笑。>', '', 38134),
(205690, 'zhTW', '泥拳在這一路上差點把我壓碎！\n\n真是蠢貨。\n\n<泥拍眼中流露出頑皮的神色。>\n\n嘿，你想找點樂子嗎？', '', 38134),
(205687, 'zhTW', '有誰餓了？', '', 38134),
(205686, 'zhTW', '', '謝謝你。我現在可以工作了！…希望不會有更多木魈跑來攪局。', 38134),
(205685, 'zhTW', '又是這些小怪物！嘿，那裡有個妖精工匠。看看她是否需要有人幫忙搞定麻煩的木魈。', '', 38134),
(205681, 'zhTW', '', '<揮手隨便指著每樣東西。>', 38134),
(205679, 'zhTW', '', '木魈偷了我的工具和材料，我要把那些東西找回來！我本來還以為一個人在這裡終於可以完成工作…我錯了。', 38134),
(205677, 'zhTW', '', '等到亞登曠野的這個地方崩裂，我才終於能有清靜的空間好好做自己的工作吧。', 38134),
(205674, 'zhTW', '我們為失去的一切哀悼，無論是我們曾擁有的，還是我們原本可以擁有的未來。放棄那些關於佔有的無謂期待，感受平靜吧。', '', 38134),
(205673, 'zhTW', '如果我們不再懷抱期望，放下對失去的恐懼和痛苦。才能真正接受這些事而感到釋懷。', '', 38134),
(205672, 'zhTW', '看到景物已非的樣子真是痛苦。我們記得曾有的快樂、驚奇和羈絆。', '', 38134),
(205671, 'zhTW', '我們害怕的是失去曾經擁有的，或者再也無法體會我們曾以為理所當然的一切。', '', 38134),
(205639, 'zhTW', '', '你好，歡迎來到深根地窖！$b$B想喝點什麼嗎？', 38134),
(205627, 'zhTW', '', '我們會維護餘燼宴會的安全。$n，到時候見。', 38134),
(205591, 'zhTW', '在一切存在的邊際，很容易被失去的悲傷擊潰。嗯。或許我該好好思索一下失去的意義。你可以幫我找我用來冥想的核心嗎？它們應該就在這裡附近…', '', 38134),
(205590, 'zhTW', '要是有人想追求「平靜祥和」的話，這裡倒是很適合！或許那邊那個個靈魂需要幫助。', '', 38134),
(205587, 'zhTW', '', '這真是我看過最糟糕的打鬥，新兵，你失敗了。去練習吧。', 38134),
(205586, 'zhTW', '', '做得很好。新兵，你通過了。', 38134),
(205579, 'zhTW', '你有…工匠的裝扮嗎？\n\n如果你學會了技能，我就可以教你如何製作可容納符文之力的容器。', '', 38134),
(205568, 'zhTW', '你想學習哪種專業技能？', '你想學習哪種專業技能？', 38134),
(205561, 'zhTW', '', '我不相信他，但這可能是我們揭穿他們陰謀的唯一方法。', 38134),
(205560, 'zhTW', '', '淵喉行者！這個巫妖有個提議，可以讓我們混進祭儀家族。', 38134),
(205559, 'zhTW', '你除了接受之外沒有任何選擇，不是嗎？難道你想玉石俱焚？', '', 38134),
(205558, 'zhTW', '', '…我可沒有同意你的計畫，巫妖。', 38134),
(205552, 'zhTW', '賽納莉亞蛾！看來剛有一群孵化出來。看看你能不能抓幾隻回來。', '', 38134),
(205550, 'zhTW', '我得警告你：我受過決鬥的嚴格訓練。', '我得警告你：我受過決鬥的嚴格訓練。', 38134),
(205534, 'zhTW', '新的目標？我的世界都消失了，怎麼還能有什麼目標呢？\n\n不過，我也不能一直無所事事，那不是我的風格。\n\n我必須了解這個世界，這個…暗影之境。我能感覺到很多界域，不過有四個應該是地位最特殊的。\n\n幫我從這四個界域各帶點東西回來，最好是有機質。只要碰一下，我就能弄清楚很多事。', '', 38134),
(205532, 'zhTW', '我能感覺到它是否還存在。我感覺得它消失了。\n\n真是令人傷心絕望。失去了世界，還算什麼神？\n\n我該去哪裡？我該做什麼？', '', 38134),
(205530, 'zhTW', '亞登曠野？這裡是暗影之境嗎？不要因為我知道暗影之境而感到驚訝。我以前可是一個神靈。\n\n我知道很多問題的答案。但我卻不知道我為什麼會在這裡，也不知道我的世界為何消失了。\n\n我猜想我跟世界一起消滅了。但我現在恢復了，我的世界卻沒有。', '', 38134),
(205528, 'zhTW', '嗯，你想說什麼？我怎麼會出現在這種地方？', '', 38134),
(205522, 'zhTW', '啊，啊，那裡再多一點。輕輕地。', '啊，啊，那裡再多一點。輕輕地。', 38134),
(205521, 'zhTW', '哦哦哦。真是鮮明的色調。你看到了嗎？朋友。', '哦哦哦。真是鮮明的色調。你看到了嗎？朋友。', 38134),
(205520, 'zhTW', '看看顏料鋪展的樣子有多滑順。就像一片風中的落葉。', '看看顏料鋪展的樣子有多滑順。就像一片風中的落葉。', 38134),
(205519, 'zhTW', '我完成了。我創作這副作品時很快樂，希望這幅作品也能帶給你一樣的樂趣。', '我完成了。我創作這副作品時很快樂，希望這幅作品也能帶給你一樣的樂趣。', 38134),
(205516, 'zhTW', '像一家人一樣…團聚。', '像一家人一樣…團聚。', 38134),
(205505, 'zhTW', '軍政官阿德雷特斯點點頭。', '', 38134),
(205498, 'zhTW', '', '我們的任務才剛開始。', 38134),
(205497, 'zhTW', '', '歡迎來到瑪卓薩斯最後的榮耀堡壘。你的到來就和我自己同樣一片混亂。', 38134),
(205496, 'zhTW', '一定就是那個！把它拿來給我！', '', 38134),
(205481, 'zhTW', '', '等你準備好，淵喉行者，我們就開始攻擊！', 38134),
(205472, 'zhTW', '我和可達林與其他勇士一起捍衛家園已經是陳年往事了。我並不想念戰爭，但我喜歡跟盟友並肩作戰的感覺。\n\n<塞爾納斯輕笑一聲。>\n\n也喜歡看到邪惡的腐化者被徹底毀滅的樣子。', '', 38134),
(205471, 'zhTW', '', '我不願意再去想要是佐司特掌控這裡的話究竟會發生什麼事。\n\n我知道在亞登曠野這裡還有該做的事尚未完成，但至少威脅已經解除了。\n\n我對你還有戒心，也一直在盯著你，不過至少我們對於幫助這個地方還有共識。', 38134),
(205470, 'zhTW', '好棒的顏料！從來沒看過這樣的。我得去做事了。', '好棒的顏料！從來沒看過這樣的。我得去做事了。', 38134),
(205468, 'zhTW', '', '我不願意再去想要是佐司特掌控這裡的話究竟會發生什麼事。\n\n我知道在亞登曠野這裡還有該做的事尚未完成，但至少威脅已經解除了。\n\n泰蘭妲依然孤身留在托迦司。尋找其他黑夜戰士的行動仍在繼續，伊瑟拉和塞爾納斯對他們的計畫很有自信，但…我很擔心她。', 38134),
(205467, 'zhTW', '', '雖然佐司特已被擊敗了，但亞登曠野依然需要被保護。我會為曠野獵者而戰，就像亞拉隆一樣，也會保護林地的一切。\n\n這給了我希望，或許有一天亞登曠野能真的恢復原貌。', 38134),
(205466, 'zhTW', '哇，真令人興奮！我很高興佐司特都走了。他們對林地和野性種子造成了很惡劣的影響。我希望女王能讓事情都恢復正常。至少要比現在正常。謝謝你，$n。你為我們做的比你想像的還要多。', '', 38134),
(205465, 'zhTW', '', '烽火戰地之子啊，你以外來者的身份來到這裡。現在我們卻攜手並肩，以盟友的身份一起作戰。我向你身為獵人的技藝致敬。我的長矛聽候你的命令。', 38134),
(205463, 'zhTW', '就連最厲害的獵人都會倒下。\n\n我們為亞拉隆和其他人的逝去而悲痛，但也歌頌他們的生命。慶祝我們的勝利！\n\n你在戰鬥中展現出亞登曠野的堅毅。你的英勇事蹟會傳遍整座森林，$n。\n\n打得好。真是打得好！', '', 38134),
(205459, 'zhTW', '', '我從你一抵達的時候就知道你很不尋常，$n。我果然沒錯！你…還真有意思。\n\n<月莓變得嚴肅。>\n\n我們現在非常需要更多有趣的事物。佐司特被趕走了，但他們只是讓已經存在的問題更嚴重了。這裡還是有很多不好的事情。我們一定會阻止壞事繼續！', 38134),
(205458, 'zhTW', '', '付出了很多，我才得以存活。我很高興能幫忙守護這個地方，還要回報這裡帶給我的所有善意。\n\n佐司特在這裡已經失去力量了，但我們還是要小心。暗影之境…有點不對勁。我們贏得了一次勝利，但還有很多事要解決。\n\n我在即將到來的挑戰中需要你的幫助。做好準備。', 38134),
(205457, 'zhTW', '做得好。解散！', '做得好。解散！', 38134),
(205442, 'zhTW', '那些蘑菇在淵喉的影響之下發瘋了！我們得做點什麼，快啊！', '', 38134),
(205441, 'zhTW', '噢，很抱歉。我是不是忘了說要動手做事？', '', 38134),
(205440, 'zhTW', '你得把這副軀體帶回到儀式法陣去。', '', 38134),
(205437, 'zhTW', '那形狀真的是…很奇特。', '那形狀真的是…很奇特。', 38134),
(205436, 'zhTW', '你要怎麼為亞登曠野奉獻。', '你要怎麼為亞登曠野奉獻。', 38134),
(205435, 'zhTW', '你想要哪種形態？', '你想要哪種形態？', 38134),
(205434, 'zhTW', '暗夜給予你祝福。', '暗夜給予你祝福。', 38134),
(205427, 'zhTW', '', '但你要是任務失敗，你的名字就會出現在名單上！', 38134),
(205426, 'zhTW', '', '我們一起把名單上的幾個名字刪掉吧…', 38134),
(205425, 'zhTW', '', '用這個水晶可以把我的影像投射到生者界域一小段時間。', 38134),
(205424, 'zhTW', '', '其他鬥士會跟隨著我，不是你要他們陪在我旁邊！', 38134),
(205423, 'zhTW', '德瑞文竟然說我傲慢！要是那個蠢蛋石源魔把我那些關於科爾蘇加德的警告聽進去，我們就能殺掉他了！結果，我們的敵人和閻獄之主合作了。\n\n<這位王子的眼中湧現怒意。>\n\n我現在知道我做錯了。或許有一天，我能好好補償他們，並且證明我值得你再相信我一次。\n\n我會欣然地用我的救贖去換取為我的人民復仇的機會。至於那些因科爾蘇加德而死的人…\n\n<凱爾薩斯嘆了一口氣。>\n\n是因為我沒能保護他們。\n\n$p，讓我靜一靜。我還有很多要思考的事。', '', 38134),
(205422, 'zhTW', '德瑞文竟然說我傲慢！要是那個蠢蛋石源魔把我那些關於科爾蘇加德的警告聽進去，我們就能殺掉他了！結果，我們的敵人和閻獄之主合作了。\n\n<這名血精靈的雙眼中湧現了怒氣。>\n\n我現在知道我做錯了。或許有一天，我能補償他們。\n\n我會欣然地用自己的救贖去換取為我的人民復仇的機會。至於那些因科爾蘇加德而死的人…\n\n<凱爾薩斯嘆了一口氣。>\n\n是因為我沒能保護他們。', '', 38134),
(205420, 'zhTW', '當我因為幾個…有待商榷的選擇而受困在瑞文崔斯的時候，科爾蘇加德卻逍遙法外。那個禽獸別想再控制瑪卓薩斯！\n\n<凱爾薩斯怒目瞪視了一會，隨即垂下眼睛。>\n\n$p，我們中有九成的人是被阿薩斯‧米奈希爾殺掉，用以喚醒那個巫妖。尊貴的王國，已不復存在。\n\n你還在流血，是不是？切開你的肉，數數有幾滴血。就讓那九個滋養大地，為了你拯救的那一個犧牲吧。\n\n$p，告訴我那東西對你的心做了什麼事，。我就可以仔細地告訴你它對我做了什麼。', '', 38134),
(205419, 'zhTW', '這種待遇簡直無法無天！我是你們的王子，正統的王儲，冷酷無情的審斷者竟敢把我丟到這個充滿亂石和暴民的荒蕪之地。\n\n這些汎希爾根本無權評判我所做出的任何選擇。你也一樣。', '', 38134),
(205418, 'zhTW', '我想你應該很喜歡看到我這個樣子。就算你能在戰鬥中佔上風，也絕對不是我的對手。\n\n我是血統高貴的王子，冷漠無情的仲裁者竟敢把我送到這個充滿岩石和偏執之人的荒涼之地。\n\n這些汎希爾根本無權評判我所做出的任何選擇。你也一樣。', '', 38134),
(205417, 'zhTW', '這種待遇簡直無法無天！我是血統高貴的王子，冷漠無情的仲裁者竟敢把我送到這個充滿岩石和偏執之人的荒涼之地。\n\n這些汎希爾根本無權評判我所做出的任何選擇。你也一樣。', '', 38134),
(205416, 'zhTW', '喂！過來啊，目標！我命令你立刻解開我的束縛！\n\n<凱爾薩斯試圖掙脫束縛。>\n\n這種待遇簡直無法無天！我是你們的王子，正統的王儲，冷酷無情的審斷者竟敢把我丟到這個充滿亂石和暴民的荒蕪之地。\n\n這些汎希爾沒有資格評判我的選擇！你也一樣！\n\n控罪者！快放了我！我要為我的王國復仇。控罪者！有聽見嗎？\n\n<凱爾薩斯繼續反抗掙扎。>', '', 38134),
(205415, 'zhTW', '喂！對，就是你！我要你現在就釋放我！\n\n<凱爾薩斯試圖掙脫束縛。>\n\n我想你應該很高興能見到我！就算你在戰鬥中佔了上風，但你永遠不是我的對手！\n\n我是血統高貴的王子，冷漠無情的仲裁者竟敢把我送到這個充滿岩石和偏執之人的荒涼之地。\n\n這些汎希爾沒有資格評判我的選擇！你也一樣！\n\n控罪者！快放了我！我要為我的王國復仇。控罪者！有聽見嗎？\n\n<凱爾薩斯繼續反抗掙扎。>', '', 38134),
(205414, 'zhTW', '喂！對，就是你！我要你現在就釋放我！\n\n<凱爾薩斯試圖掙脫束縛。>\n\n這種待遇簡直無法無天！我是血統高貴的王子，冷漠無情的仲裁者竟敢把我送到這個充滿岩石和偏執之人的荒涼之地。\n\n這些汎希爾沒有資格評判我的選擇！你也一樣！\n\n控罪者！快放了我！我要為我的王國復仇。控罪者！有聽見嗎？\n\n<凱爾薩斯繼續反抗掙扎。>', '', 38134),
(205412, 'zhTW', '你現在最好不要跟我說話。\n\n我可沒瞎。我看得出來你已經在對其他誓盟表示忠心了。', '', 38134),
(205411, 'zhTW', '我看過你想博取別人好感的樣子。\n\n我們現在沒有什麼好討論的。', '', 38134),
(205410, 'zhTW', '', '你已經在別的地方出力了。\n\n別以為我喜歡這種事情。我們到處都有眼線。', 38134),
(205409, 'zhTW', '', '我敢說你已經開始想辦法討其他誓盟的歡心了。\n\n你不用在我這裡浪費時間了。', 38134),
(205408, 'zhTW', '我好像看到你和月莓女士走在一起？\n\n希望你沒把太多莫名其妙的重生鬼話給聽進去。', '', 38134),
(205407, 'zhTW', '你為什麼一定要琪瑞安結夥？\n\n你不為汎希爾效命的時候真是好多了。', '', 38134),
(205406, 'zhTW', '你為什麼老是跟德拉卡說話？\n\n你要是把時間拿來遵守承諾幫助汎希爾的話不是很好嗎。', '', 38134),
(205405, 'zhTW', '', '跟德瑞文將軍交手的時候要小心。\n\n千萬不能相信汎希爾。我相信你已經知道了，但我還是忍不住去注意你跟他最近相處的時間。', 38134),
(205404, 'zhTW', '', '我看到你還在對德拉卡獻殷勤。\n\n我知道你們從同樣的地方來，但是，你現在有新朋友了。我們。', 38134),
(205401, 'zhTW', '', '你和琪瑞安最近好像走得很近。\n\n你對侍奉女王感到厭煩了嗎？我們還有很多要一起做的事。', 38134),
(205400, 'zhTW', '', '那就是你跟德瑞文在討論的事嗎？\n\n我沒想到我們那時候是在跟汎希爾合作。我不相信他們。', 38134),
(205398, 'zhTW', '', '我本來沒把你當作愛好自然的人，不過你最近似乎常常跟暗夜妖精在一起。', 38134),
(205397, 'zhTW', '', '看來你最近成了琪瑞安的得力盟友。\n\n$n，你應該還效忠於死靈領主吧？我們還有很多事要做。\n\n我不希望我們之間的關係受到背叛的影響。', 38134),
(205394, 'zhTW', '我知道你和德拉卡是從同一個地方來的。\n\n這就是我看到你最近在跟她聊天的原因嗎？', '', 38134),
(205390, 'zhTW', '我稍早看到你和德瑞文將軍在談話。\n\n我相信你會把他告訴你的相關訊息如實轉達。', '', 38134),
(205389, 'zhTW', '我好像看到你跟月莓女士在那邊做什麼事？\n\n我不知道我們有什麼任務是要跟暗夜妖精一起執行的。', '', 38134),
(205388, 'zhTW', '', '$n，我敬重你，你很勇敢。\n\n你是故意想惹我生氣的吧？想看看我對你的背叛有什麼反應是嗎？\n\n你該離開了。我還需要一點時間才有辦法跟你這種人討論事情。', 38134),
(205387, 'zhTW', '', '你必須再次展現你的力量\n\n但力量不是我們在死靈領主身上唯一看重的事。我們也要求你要全心奉獻，但你很明顯地在這方面有所欠缺。\n\n走吧，我們會好好評估你想重新加入誓盟的事。\n\n晚點再回來，或許那時候我們就做出決定了。', 38134),
(205380, 'zhTW', '我沒把你劈成兩半是你走運！\n\n要不是這裡是中立區，我可不會這麼冷靜。\n\n你最好等我氣消了以後再回來。', '', 38134),
(205378, 'zhTW', '你讓我們越來越討厭你了。\n\n我們晚點再來評估你的事，之後再來找我談吧。\n\n或許那時候我們已經判斷出你是否有資格重新加入汎希爾。', '', 38134),
(205370, 'zhTW', '你想怎麼調整披肩和風帽？', '', 38134),
(205368, 'zhTW', '你想換掉哪個飾品配件？', '', 38134),
(205366, 'zhTW', '你想把偽裝改成什麼顏色？', '', 38134),
(205365, 'zhTW', '你想改變偽裝的哪個部分？', '', 38134),
(205364, 'zhTW', '', '你還敢回來跟我說話？\n\n$n，你證明自己缺乏忠誠。\n\n或許晚一點我就會原諒你了。或許。', 38134),
(205363, 'zhTW', '這個人體模型是時尚的莫瑞特用來展示新款禮服給祭儀家族的侍僧看的。\n\n你可以使用它來更改你的死靈侍僧偽裝外觀。', '', 38134),
(205362, 'zhTW', '是$n本人？\n\n你敢在我面前露臉，還真勇敢。我現在沒有什麼話要跟你這種人說。', '', 38134),
(205359, 'zhTW', '', '你已經踏出重返女王身邊的第一步了。\n\n我們還在討論你的歸來是不是循環的一部分。\n\n你之後再回來吧，或許那時候我們就有結論了。很抱歉讓你等候，請理解你先前的背叛讓我們必須十分謹慎。', 38134),
(205356, 'zhTW', '$n，你好。\n\n我們還在討論是否讓你重新加入我們。\n\n請你過一陣子再回來看看，願你此行一路順風。', '', 38134),
(205345, 'zhTW', '薩隆杰全都知道！凡人所做的一切無法結束永恆的王朝！', '', 38134),
(205343, 'zhTW', '這樣不對！這些傢伙不應該出現在這裡！應該給他們一點教訓。', '', 38134),
(205341, 'zhTW', '深埋的秘密就應該繼續隱藏。', '', 38134),
(205335, 'zhTW', '縫補者要跟你玩！', '', 38134),
(205334, 'zhTW', '你敢來表示你很蠢。', '', 38134),
(205330, 'zhTW', '更多內臟！更多鮮血！更多食物！', '', 38134),
(205328, 'zhTW', '你感覺到的寒氣就是厄運的前兆！', '', 38134),
(205323, 'zhTW', '她的時候到了。不幸的是，她即將面臨的永恆只會是淵喉。這種不是我們能決定的，只有審斷者可以。', '她的時候到了。不幸的是，她即將面臨的永恆只會是淵喉。這種不是我們能決定的，只有審斷者可以。', 38134),
(205322, 'zhTW', '雖然她死得很慘烈，但決定她的命運是否人道不是我們的責任。', '雖然她死得很慘烈，但決定她的命運是否人道不是我們的責任。', 38134),
(205321, 'zhTW', '', '樹燒起來了！我…無法呼吸…', 38134),
(205320, 'zhTW', '不管有多悲慘，這都是她進入奧睿博司與永恆的時機。', '不管有多悲慘，這都是她進入奧睿博司與永恆的時機。', 38134),
(205319, 'zhTW', '為家庭自我犧牲是高貴之舉，但沒有必要回到生靈之地。', '為家庭自我犧牲是高貴之舉，但沒有必要回到生靈之地。', 38134),
(205318, 'zhTW', '我跟一個急性子的兄弟決鬥，我…我無法接受我居然輸給了卑鄙小人！我揮劍太慢，因此付出了代價。', '我跟一個急性子的兄弟決鬥，我…我無法接受我居然輸給了卑鄙小人！我揮劍太慢，因此付出了代價。', 38134),
(205317, 'zhTW', '身為自然之靈，她被送到亞登曠野去幫助有需要的人。這就是她的宿命。', '身為自然之靈，她被送到亞登曠野去幫助有需要的人。這就是她的宿命。', 38134),
(205316, 'zhTW', '雖然有些和自然緊緊相依的靈魂在亞登曠野休息之後確實回來了，有些卻在亞登曠野的枝葉間永存。', '雖然有些和自然緊緊相依的靈魂在亞登曠野休息之後確實回來了，有些卻在亞登曠野的枝葉間永存。', 38134),
(205315, 'zhTW', '', '林地現在有危險！沒有我的話，那些小傢伙到不了安全的地方！', 38134),
(205314, 'zhTW', '惡魔不屬於暗影之境，把那些戰鬥者留給瑪卓薩斯吧。', '惡魔不屬於暗影之境，把那些戰鬥者留給瑪卓薩斯吧。', 38134),
(205313, 'zhTW', '噢，出錯了嗎？這是要給軍政官阿德雷特斯的？', '', 38134),
(205311, 'zhTW', '正確。從扭曲虛空來的惡魔就該到他們該回的地方去。', '正確。從扭曲虛空來的惡魔就該到他們該回的地方去。', 38134),
(205309, 'zhTW', '', '嗯嗯！我已經很久沒有像這樣感受過一把劍了。', 38134),
(205303, 'zhTW', '雖然華爾琪不是我們真正的同胞，他們願意回到凡人的國度還是值得嘉勉。', '雖然華爾琪不是我們真正的同胞，他們願意回到凡人的國度還是值得嘉勉。', 38134),
(205302, 'zhTW', '做得好。這個靈魂來得正好。就連不死生物都無法永遠逃離死亡。', '做得好。這個靈魂來得正好。就連不死生物都無法永遠逃離死亡。', 38134),
(205301, 'zhTW', '我聽到可怕的華爾琪在呼喚了。拜託了！讓我好好安息吧！我求求你！', '', 38134),
(205294, 'zhTW', '你有灌能紅寶石嗎？罪奔者會一路帶你到暗隱村的。\n\n千萬別鬆手，除非你想被丟在半路上。', '', 38134),
(205291, 'zhTW', '你必須瞭解，軍政官絕對不會喜歡既定行程受到干擾的。但沒差，這對他來說也是好事。讓我想想辦法。', '', 38134),
(205285, 'zhTW', '噢，不管什麼都可以。\n\n我之後會再自己調整的。', '', 38134),
(205284, 'zhTW', '只要一把灌能紅寶石，罪奔者就會一路帶你到北方城垛去。\n\n風景很美，但到處都有可能出現怪東西和惡棍。不要隨便放手，除非你很確定那是你要去的地方。', '', 38134),
(205282, 'zhTW', '沒錯。雖然她如果有回去的話，也許城鎮的命運會出現轉機，但她死期已到。', '沒錯。雖然她如果有回去的話，也許城鎮的命運會出現轉機，但她死期已到。', 38134),
(205281, 'zhTW', '雖然等一下爆發的洪水會害死很多條生命，但介入凡人的事務並非我們的職責。', '雖然等一下爆發的洪水會害死很多條生命，但介入凡人的事務並非我們的職責。', 38134),
(205280, 'zhTW', '我的爆炸終結者5000型剛剛…呃…炸掉了我實驗室上方的水壩！我的所有實驗體…我是說我可愛的寵物們，都陷入了前所未有的危機！', '我的爆炸終結者5000型剛剛…呃…炸掉了我實驗室上方的水壩！我的所有實驗體…我是說我可愛的寵物們，都陷入了前所未有的危機！', 38134),
(205279, 'zhTW', '說得沒錯。不論靈魂再怎麼懇求，我們都不能改變他們的命運。', '說得沒錯。不論靈魂再怎麼懇求，我們都不能改變他們的命運。', 38134),
(205278, 'zhTW', '只要巫妖的骨匣還是完整的，我們就必須送他回去。', '只要巫妖的骨匣還是完整的，我們就必須送他回去。', 38134),
(205275, 'zhTW', '我來過這個十字路口好幾次了。我不想要再被束縛住了。賜予我死亡的平靜吧！', '我來過這個十字路口好幾次了。我不想要再被束縛住了。賜予我死亡的平靜吧！', 38134),
(205272, 'zhTW', '如果你能拿一把灌能紅寶石來換，那這個罪奔者就會一路帶著你到西邊城堡的城牆破洞。\n\n千萬別鬆手，除非你想被丟在半路上。', '', 38134),
(205268, 'zhTW', '淵喉行者，去收集一些讓我們的賓客帶回家，讓他們能記得這個好兆頭。', '', 38134),
(205267, 'zhTW', '有個好預兆降臨在我們的宮廷，靈魄在我們上頭結合了！', '', 38134),
(205251, 'zhTW', '這個生物是源自火源之界的魔法，不能送去奧睿博司進行審判。', '這個生物是源自火源之界的魔法，不能送去奧睿博司進行審判。', 38134),
(205250, 'zhTW', '很好！其他界域的生物在死亡時都必須回歸那裡。暗影之境不是他們的命運依歸。', '很好！其他界域的生物在死亡時都必須回歸那裡。暗影之境不是他們的命運依歸。', 38134),
(205249, 'zhTW', '入侵者在危害火源之界！我必須馬上回到同類身邊！', '', 38134),
(205247, 'zhTW', '雖然她身懷暗影之境的魔法，但這股力量實則源自於生者的世界。她必須回去。', '雖然她身懷暗影之境的魔法，但這股力量實則源自於生者的世界。她必須回去。', 38134),
(205246, 'zhTW', '一定是這樣。雖然她死過一次了，但是她在回到我們身邊前必須回應主人的召喚。', '一定是這樣。雖然她死過一次了，但是她在回到我們身邊前必須回應主人的召喚。', 38134),
(205245, 'zhTW', '巫妖王呼喚我。我必須再次服侍他！', '巫妖王呼喚我。我必須再次服侍他！', 38134),
(205243, 'zhTW', '呿，灰塵元素！再這樣下去，他們就會把整個地方削乾抹淨，那野性種子會全部死去！', '', 38134),
(205241, 'zhTW', '說得沒錯。這頭怪物高貴地死去，並且在暗影之境贏得了一席之地。', '說得沒錯。這頭怪物高貴地死去，並且在暗影之境贏得了一席之地。', 38134),
(205240, 'zhTW', '雖然這樣做可能會有點痛苦，但我們絕對不能讓這個怪物獲得永生。', '雖然這樣做可能會有點痛苦，但我們絕對不能讓這個怪物獲得永生。', 38134),
(205239, 'zhTW', '請讓我來翻譯。這隻生物的主人遭到一群野狼攻擊，但它沒能保護對方，因此想要回到人世間，確認主人是否平安無恙。', '請讓我來翻譯。這隻生物的主人遭到一群野狼攻擊，但它沒能保護對方，因此想要回到人世間，確認主人是否平安無恙。', 38134),
(205238, 'zhTW', '汪！汪！汪！汪！呃…', '', 38134),
(205237, 'zhTW', '沒錯，他死得很有戰士的尊嚴。他死得光榮，之後也在瑪卓薩斯締造了更多的豐功偉業。', '沒錯，他死得很有戰士的尊嚴。他死得光榮，之後也在瑪卓薩斯締造了更多的豐功偉業。', 38134),
(205236, 'zhTW', '可惜並不是。雖然的確其他人因為少了他而殞命，但是他在凡人的世界有完成自己的使命。', '可惜並不是。雖然的確其他人因為少了他而殞命，但是他在凡人的世界有完成自己的使命。', 38134),
(205235, 'zhTW', '戰火在我的城市肆虐。我必須在那些怪獸荼毒我的小傢伙之前回去保護他們！', '戰火在我的城市肆虐。我必須在那些怪獸荼毒我的小傢伙之前回去保護他們！', 38134),
(205234, 'zhTW', '不正確。我們不能擺渡那些和其他力量有所相連的靈魂。', '不正確。我們不能擺渡那些和其他力量有所相連的靈魂。', 38134),
(205233, 'zhTW', '正確。必須允許那些和宇宙力量緊密相連的靈魂回來。', '正確。必須允許那些和宇宙力量緊密相連的靈魂回來。', 38134),
(205232, 'zhTW', '<此生物發出令人焦慮的不和諧鳴響聲。>', '', 38134),
(205230, 'zhTW', '有了這把灌能紅寶石，罪奔者就會一路帶你到舊城門去。千萬別弄丟，除非你想被丟在半路上。', '', 38134),
(205229, 'zhTW', '電貝斯要再更重。大鼓要再更大力。\n\n這也敢叫做舞臺啊？根本就是個洞穴！一點溫暖的感覺都沒有，而且音響設備可以再爛一點沒關係。\n\n在調整到完美之前荒疫野豬不會上台表演。我雖然死過一次但不代表我的耳朵死了！', '', 38134),
(205222, 'zhTW', '噢，你好！有學到新的食譜嗎？', '', 38134),
(205221, 'zhTW', '很高興見到你。要來聊聊食物嗎？', '', 38134),
(205220, 'zhTW', '我的名字是史燉華，我超愛燉肉燉菜這種料理，應該看得出來吧？', '', 38134),
(205219, 'zhTW', '哈囉。哈囉！你是來分享食譜的嗎？', '', 38134),
(205214, 'zhTW', '', '我不負責訓練刺殺盜賊。', 38134),
(205212, 'zhTW', '', '稅務官的貪婪無邊無際。\n\n在瑪卓薩斯，這些行為簡直不可思議。汎希爾不是應該專心幫助靈魂為他們的罪行贖罪嗎？更讓我作嘔的是，他曾經和我的國度的人宣示效忠。\n\n我們必須阻止這場瘋狂行徑。', 38134),
(205205, 'zhTW', '', '別…離開我。', 38134),
(205202, 'zhTW', '', '你的力量在我們第一次過招後增強了不少。我們想提供你更適合的挑戰。', 38134),
(205201, 'zhTW', '', '背叛蛛眼家族的人都得死！', 38134),
(205197, 'zhTW', '<阿克利歐斯咯咯地笑。>\n\n不。如果一切都在我腳下崩塌殆盡…\n\n…那就是該踏上全新旅程的時候了。', '', 38134),
(205194, 'zhTW', '嗯…我走路來的！\n\n我在樹蔭底下發現這個舒服的地方，於是便決定停下來冥想沉思。\n\n不過，我好像一個不小心入定太久，跟亞登曠野的其他部分脫節了。', '', 38134),
(205192, 'zhTW', '我是一名漫遊者。一名旅行者。\n\n我在生者的世界探索了好幾個紀元，而現在來到了死者的界域。過了這麼長的時間了，我常回顧我自己的存在並思考許多事物。', '', 38134),
(205190, 'zhTW', '你好啊，旅行者。', '', 38134),
(205189, 'zhTW', '我有…我是說我們威力無窮！我終於再也不用聽你不停抱怨防守密室的事了。', '', 38134),
(205188, 'zhTW', '不！走開。住手！誰都不能進去這裡。', '', 38134),
(205187, 'zhTW', '感覺如何啊？嗯？嗯？你一定要把門打開就是了？', '', 38134),
(205186, 'zhTW', '啊啊啊。我真是太<打嗝>想念這個了。再來！我們還要更多。', '', 38134),
(205185, 'zhTW', '你想要來一點…強化嗎？\n\n讓你的拳頭更有力，或是施放更強的法術。\n\n只要付點錢，就全是你的了。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(205184, 'zhTW', '不！主人一定會氣死的。你會付出代價！', '', 38134),
(205182, 'zhTW', '偉大的芬連克斯絕不會退縮。', '', 38134),
(205180, 'zhTW', '他在和你說話嗎？他可是個叛徒。別聽他說的。', '', 38134),
(205179, 'zhTW', '呿！別管那個喋喋不休的蠢蛋。我們絕對要進去那個密室。我們去找東西來吸取吧！', '', 38134),
(205178, 'zhTW', '喔耶。這樣才像話嘛！我…呃…這樣的數量還遠遠不夠。', '', 38134),
(205173, 'zhTW', '淵喉行者救了我！我賭這件事你一定會成功。', '', 38134),
(205171, 'zhTW', '', '我的斥候回傳了萊索妮雅動向的情報。', 38134),
(205170, 'zhTW', '他的下場能夠帶給我平靜嗎？我能有擺脫他的一天嗎？或許…有朝一日可以吧。或許吧。', '', 38134),
(205169, 'zhTW', '', '為了佩拉苟斯去做吧，克蕾雅…為他行動吧。', 38134),
(205168, 'zhTW', '等等。我不是一個人在戰鬥。畢竟，我還有克蕾雅陪我！誠如她所說，我每次都會越變越強。終有一天，我會克服難關！終有一天…', '', 38134),
(205167, 'zhTW', '', '我明白了，我一直在走的那條路。我不會忘記的。', 38134),
(205166, 'zhTW', '我本來要她回想自己獲得的一切，但結果…我也開始自問，這是唯一的道路嗎？我親愛的卷軸，我心中滿是疑問卻沒有任何答案。', '', 38134),
(205155, 'zhTW', '<他們雖然奮勇抵抗，但看起來是突然遭到襲擊的。>', '', 38134),
(205153, 'zhTW', '<這個仲介者似乎是在打鬥中死亡的。>', '', 38134),
(205148, 'zhTW', '很好！這樣應該夠了。現在我們能通過那個大嘴巴了。我們回去那裡吧！', '', 38134),
(205147, 'zhTW', '<這些雪堆為仲介者提供了些許的保護。>', '', 38134),
(205146, 'zhTW', '你什麼也沒找到。', '你什麼也沒找到。', 38134),
(205145, 'zhTW', '<這個傷重而亡的仲介者差一點點就能抵達傳送門了。\n\n看不出他有企圖尋找任何珍貴物品的跡象。>', '', 38134),
(205144, 'zhTW', '我必須向御靈者彙報。', '', 38134),
(205135, 'zhTW', '嘿！別碰那個。把它放回去。小偷！', '', 38134),
(205134, 'zhTW', '退後！這裡不允許你進入。', '', 38134),
(205132, 'zhTW', '這看起來像是一堆廢棄的護甲。', '', 38134),
(205129, 'zhTW', '咳咳！\n\n你可別妄想能通過我這關啊。不管你怎麼懇求我都對我沒用。', '', 38134),
(205099, 'zhTW', '我聽說罪殞城的某個人可能會來這裡。你的到來讓我們備感榮耀。\n\n請儘量不要防礙到守衛者。他們正在努力修補這裡遭到的破壞。', '我聽說罪殞城的某個人可能會來這裡。你的到來讓我們備感榮耀。\n\n請儘量不要防礙到守衛者。他們正在努力修補這裡遭到的破壞。', 38134),
(205098, 'zhTW', '地穴結界曾經是瑞文崔斯的秩序象徵。但再也不是了。\n\n現在，我們並必須重建它往日的榮光。我們已經從這裡開始重建作業了。很不幸的，結界的其餘部分現在對我們來說太危險了。\n\n很抱歉，可是我得回工作崗位了。', '地穴結界曾經是瑞文崔斯的秩序象徵。但再也不是了。\n\n現在，我們並必須重建它往日的榮光。我們已經從這裡開始重建作業了。很不幸的，結界的其餘部分現在對我們來說太危險了。\n\n很抱歉，可是我得回工作崗位了。', 38134),
(205091, 'zhTW', '淵喉行者，你和盟友為我們盡心盡力，這份恩情我沒齒難忘。', '', 38134),
(205088, 'zhTW', '', '撐…不住了…', 38134),
(205087, 'zhTW', '', '嗯呃…', 38134),
(205086, 'zhTW', '', '閻獄之主的力量持續增長，而暗影之境正面臨著接踵而來的危機。做好準備，$p。在未來的日子裡，我們需要你的力量。', 38134),
(205079, 'zhTW', '', '再會了，琪瑞安的朋友！祝你好運！', 38134),
(205078, 'zhTW', '', '我們傳授了耐心、團結與合作的道理，願你和靈魂之絆都能夠有所領悟。', 38134),
(205077, 'zhTW', '', '我紮根於此，$n，隨時都可以回來找我。', 38134),
(205073, 'zhTW', '', '著魔的妖精把我給包圍住了！該怎麼辦才好！', 38134),
(205069, 'zhTW', '為什麼？虧我本來還玩得滿開心的！', '', 38134),
(205063, 'zhTW', '', '我可憐的軟糖發狂了！沒有東西能安撫他。', 38134),
(205062, 'zhTW', '', '協助我的盟友，幫他們從麻煩中脫身。向他們再次保證，他們回來的時候能好好恢復。', 38134),
(205059, 'zhTW', '', '突蘭提亞克的骨匣被發現之後，把它藏在祭儀家族之外。你必須找出來…我們彼此都需要。', 38134),
(205056, 'zhTW', '你怎麼了，凡人？', '', 38134),
(205052, 'zhTW', '', '這次你別想，突蘭提亞克！', 38134),
(205051, 'zhTW', '', '你將遭受痛苦的折磨，直到我破壞你的心智、揭露指使你的主人是誰…', 38134),
(205050, 'zhTW', '', '又有愚蠢的叛徒掉到我的陷阱中！', 38134),
(205045, 'zhTW', '屍體要埋在哪裡？', '屍體要埋在哪裡？', 38134),
(205043, 'zhTW', '讓他們放馬過來，我做好戰鬥準備了。', '讓他們放馬過來，我做好戰鬥準備了。', 38134),
(205042, 'zhTW', '我等得不耐煩了。敵人在哪裡？', '我等得不耐煩了。敵人在哪裡？', 38134),
(205041, 'zhTW', '找到了一塊蛋糕。還把它吃了。味道很噁心。', '找到了一塊蛋糕。還把它吃了。味道很噁心。\n\n可能是個泥掘者。', 38134),
(205040, 'zhTW', '幫我找個能打的過來。', '幫我找個能打的過來。', 38134),
(205039, 'zhTW', '他們答應我能血濺大地。', '他們答應我能血濺大地。', 38134),
(205028, 'zhTW', '你們的王子沒我想像中高大。', '你們的王子沒我想像中高大。', 38134),
(205027, 'zhTW', '不管面對什麼挑戰，我的法術都能化解。', '不管面對什麼挑戰，我的法術都能化解。', 38134),
(205026, 'zhTW', '我聽到有些客人在小聲討論附近有個什麼「禁忌圖書館」。\n\n我能問問裡面有什麼嗎？', '我聽到有些客人在小聲討論附近有個什麼「禁忌圖書館」。\n\n我能問問裡面有什麼嗎？', 38134),
(205025, 'zhTW', '有我在的地方敵人就別想活命。', '有我在的地方敵人就別想活命。', 38134),
(205024, 'zhTW', '我們還有很多可以互相了解的地方。', '我們還有很多可以互相了解的地方。', 38134),
(205023, 'zhTW', '我對你的世界很有興趣。', '我對你的世界很有興趣。', 38134),
(205022, 'zhTW', '你的石惡魔話可真多啊。', '你的石惡魔話可真多啊。', 38134),
(205021, 'zhTW', '我是來侍奉的。', '我是來侍奉的。', 38134),
(205004, 'zhTW', '能回到瑞文崔斯真好。我向來敬重汎希爾人和他們的石源魔。', '能回到瑞文崔斯真好。我向來敬重汎希爾人和他們的石源魔。', 38134),
(205003, 'zhTW', '我敢說我能解決掉兩個…或三個大的泥掘者。', '我敢說我能解決掉兩個…或三個大的泥掘者。', 38134),
(205002, 'zhTW', '格鬥俱樂部在哪？你有個格鬥俱樂部，對吧？', '格鬥俱樂部在哪？你有個格鬥俱樂部，對吧？', 38134),
(204999, 'zhTW', '廢話太多，殺敵太少。', '廢話太多，殺敵太少。', 38134),
(204998, 'zhTW', '遇到麻煩的話，趕快來找我。', '遇到麻煩的話，趕快來找我。', 38134),
(204997, 'zhTW', '開打的時候，我一定到。', '開打的時候，我一定到。', 38134),
(204996, 'zhTW', '你要殺什麼嗎？', '你要殺什麼嗎？', 38134),
(204995, 'zhTW', '我還不習慣聽汎希爾對我發號施令。', '我還不習慣聽汎希爾對我發號施令。', 38134),
(204961, 'zhTW', '罪殞城屹立不搖。', '罪殞城屹立不搖。', 38134),
(204960, 'zhTW', '淵喉行者，有什麼事嗎？', '淵喉行者，有什麼事嗎？', 38134),
(204959, 'zhTW', '$g先生:女士;，有新的命令嗎？', '$g先生:女士;，有新的命令嗎？', 38134),
(204958, 'zhTW', '將軍向你問候。', '將軍向你問候。', 38134),
(204957, 'zhTW', '為了罪殞城。', '為了罪殞城。', 38134),
(204956, 'zhTW', '我準備好了。', '我準備好了。', 38134),
(204955, 'zhTW', '我該做什麼呢？', '我該做什麼呢？', 38134),
(204954, 'zhTW', '有什麼事嗎？', '有什麼事嗎？', 38134),
(204889, 'zhTW', '扭曲迴廊', '扭曲迴廊', 38134),
(204888, 'zhTW', '牢塔上層', '牢塔上層', 38134),
(204883, 'zhTW', '寒心之間', '寒心之間', 38134),
(204882, 'zhTW', '熔魂工坊', '熔魂工坊', 38134),
(204881, 'zhTW', '碎骨之牢', '碎骨之牢', 38134),
(204879, 'zhTW', '我能安排任何您囑咐我的事務。\n\n$g先生:女士;，今天我可以幫您做什麼呢？', '', 38134),
(204876, 'zhTW', '這個顏色選得好。這個顏色很適合管家，你說是吧？', '', 38134),
(204875, 'zhTW', '我們要丟什麼顏色的染料進爛泥池裡面幫泥掘者管家染色呢？\n\n顏色之後還是可以換，只不過，這樣子泥掘者會不開心，所以請謹慎選擇。', '', 38134),
(204874, 'zhTW', '你是不是想要製造一個新的泥掘者啊？\n\n把它們做得像我。我是最棒的！', '', 38134),
(204873, 'zhTW', '我也不知道為什麼，今天有點憂鬱…', '', 38134),
(204872, 'zhTW', '那些新來的泥掘者是沒有辦法自食其力的…', '', 38134),
(204870, 'zhTW', '這顆種子的靈魄已經耗盡。', '這顆種子的靈魄已經耗盡。', 38134),
(204868, 'zhTW', '我好想念石崗哨瞰臺。\n\n嗯，我想念它以前的樣子…不是我們的全新「收割者」摧殘這裡後的樣子。', '', 38134),
(204867, 'zhTW', '我們被放逐的原因是在石崗哨瞰臺「囤積」靈魄。\n\n你問我的話，儲備剛好夠用的靈魄根本算不上囤積。這是為了生存。\n\n稅務官就跟戴納瑟斯一樣腐敗。', '', 38134),
(204865, 'zhTW', '餘燼區是個可怕的地方。真希望我們能離開。\n\n但我們必須待在這個藏身處，因為聖光會燒灼我們的皮膚。無法忍受的痛苦。', '', 38134),
(204863, 'zhTW', '情況總算出現了一點轉機。$r，謝謝你。', '', 38134),
(204862, 'zhTW', '我們辦到了，親愛的！聖光不再阻礙我們生存了。', '', 38134),
(204852, 'zhTW', '或許我們可以訓練它們！', '', 38134),
(204851, 'zhTW', '但我們卻只能用泥掘者來取代。', '', 38134),
(204850, 'zhTW', '請記住：頂尖的管家必須集聰穎、魅力以及非凡的社交技巧於一身。', '', 38134),
(204840, 'zhTW', '老大這兩萬多年來一直在咬我。吸血鬼則是好幾個紀元。\n\n沒錯，你找不到任何咬痕，因為他們知道怎麼隱藏才不會被發現。這些冠冕堂皇的汎希爾從沒考慮過別人的自尊。\n\n然後某一天你突然跑來，老大就不見了。我不知道你殺了誰或放了誰，但我都同樣感激。現在我要給你點回報。', '', 38134),
(204839, 'zhTW', '瑪格西靠你的比賽中贏了幾枚紅寶石。', '', 38134),
(204837, 'zhTW', '凡人。', '', 38134),
(204835, 'zhTW', '你的主人要求還真多。要收集他要求的材料需要一些時間。\n\n但是呢，如果你能幫我收集一些比較難取得的材料，我的進度可能會加快一些。', '', 38134),
(204834, 'zhTW', '我能為你做什麼，侍僧？', '', 38134),
(204827, 'zhTW', '', '淵喉行者，我們到瑞文崔斯會合吧。我們會找出這個陰謀的源頭…並終結它。', 38134),
(204823, 'zhTW', '整天光說不練，害老骨頭都生鏽了。得要和這些戰鬥者過過招，才能讓筋骨活絡起來。', '', 38134),
(204814, 'zhTW', '嗯。至少你認清自己是個蠢貨。\n\n我不能就這樣讓你回來。我的榮譽感認為不能這麼輕易就妥協。\n\n你必須向我們證明你的價值。我剛好有個和你身手相符的任務。\n\n你準備好完成這向贖罪任務，並且再次成為汎希爾的一員了嗎？', '', 38134),
(204813, 'zhTW', '你竟然敢站在我面前問這種問題？\n\n如果這不是中立地區的話，我或許早就把你砍死了。\n\n你憑什麼認為我們會想要你回來？', '', 38134),
(204812, 'zhTW', '淵喉行者，看來你是認真的。\n\n我們早就認可你的實力了。\n\n現在，既然你如此篤定，那就宣示效忠汎希爾吧。', '', 38134),
(204811, 'zhTW', '$n，這不是隨隨便便就能決定的事。\n\n你必須非常確定你想要加入我們的行列。\n\n你完全確定你真的想要繼續談論這件事嗎？', '', 38134),
(204810, 'zhTW', '是我搞錯了嗎？你不是其他誓盟的成員嗎？\n\n你也許真的這麼忌妒我們汎希爾人，不過你真的已經準備好要背棄自己目前的盟友了嗎？', '', 38134),
(204809, 'zhTW', '淵喉行者，我能幫上什麼忙？', '', 38134),
(204802, 'zhTW', '你是男爵的侍僧，不是我的，$n。\n\n你要記住，不要一直跟我頻繁接觸。', '', 38134),
(204799, 'zhTW', '甬道石！我們逃脫的關鍵。', '', 38134),
(204798, 'zhTW', '你逃離了閻獄之主…沒錯。記憶變得更清楚了。', '', 38134),
(204795, 'zhTW', '淵喉…我還記得…感覺好像已經是很久以前的事。', '', 38134),
(204793, 'zhTW', '我會按照需求，把這些東西分配給部隊的人。我們來討論一下守衛選項吧。', '', 38134),
(204784, 'zhTW', '', '你現在還想回到我們的行列？\n\n恐怕我沒有辦法就這樣讓你若無其事地回到森林之心。\n\n你必須證明自己能再次忠心奉獻。\n\n幸好我這裡有個任務要執行，正好可以當作試煉。\n\n你準備好要證明自己的價值了嗎？', 38134),
(204783, 'zhTW', '', '啊，那個讓我們失望透頂的人。\n\n$n，你根本就不應該回到這裡露臉。\n\n別以為我們會原諒你棄我們而去。', 38134),
(204782, 'zhTW', '', '你似乎已經有所決斷了。\n\n我又有什麼立場質疑你？你已經無比忠誠地侍奉女王了。你當然可以再這麼做。\n\n你願意全心全意地將自己奉獻給暗夜妖精，並侍奉我們敬愛的女王嗎？', 38134),
(204781, 'zhTW', '這不會是我的首選…但是我會試試看的。', '這不會是我的首選…但是我會試試看的。', 38134),
(204780, 'zhTW', '', '你確定嗎，$n？\n\n這樣的決定會讓生命的循環失去平衡。在我們進一步討論之前，你要確定這真的是你想做的事才行。', 38134),
(204778, 'zhTW', '', '原來如此。\n\n嗯，我們很感謝你先前對我們的幫助，但你已經是其他誓盟的代表了。\n\n你確定要討論離開他們加入我們的事嗎？', 38134),
(204777, 'zhTW', '好了！大功告成。嗯，不過，小心不要做太劇烈的動作。還有無論如何，一定要跟吞噬者保持距離。狩獵愉快！', '', 38134),
(204776, 'zhTW', '', '淵喉行者，我能幫上什麼忙？', 38134),
(204775, 'zhTW', '', '你已經忘卻我們的力量過了。\n\n我們怎能如此輕易忘記？你有什麼要為自己辯駁的嗎？', 38134),
(204773, 'zhTW', '對啦！這是我第二好的毛刷。動物們最喜歡我用毛刷幫它們抓癢。', '', 38134),
(204772, 'zhTW', '你的魔法躲不過我的視線！', '', 38134),
(204771, 'zhTW', '我看到你了…', '', 38134),
(204770, 'zhTW', '這是什麼把戲？', '', 38134),
(204767, 'zhTW', '', '你準備好在我們的見證下重新證明自己了嗎？\n\n我們可不會就這樣張開手臂讓你回來。不會，這次可不會。這次，我們需要你證明你會為我們的理想犧牲奉獻。\n\n你準備好對我們證明你的價值了嗎？', 38134),
(204765, 'zhTW', '你的意志很堅強。\n\n我毫不懷疑你能在死靈領主大展身上。現在，你必須承諾會將你的力量和能量用來服侍我們。\n\n發誓吧！', '你的意志很堅強。\n\n我毫不懷疑你能在死靈領主大展身上。現在，你必須承諾會將你的力量和能量用來服侍我們。\n\n發誓吧！', 38134),
(204764, 'zhTW', '走上這條路，意味著拋棄原本選擇的誓盟。\n\n我不能責怪你想要尋求更為強大的盟友，不過我不認為你目前的盟友會輕易放你走。\n\n你確定要這樣做嗎？', '走向這條道路代表你要捨棄目前的盟友。\n\n我不能責怪你想要尋求更為強大的朋友，但我不認為你目前的朋友會就這麼放過你的背叛。\n\n你確定要背叛他們嗎？', 38134),
(204750, 'zhTW', '好，這樣應該行得通。表演準備就緒了。', '', 38134),
(204746, 'zhTW', '我使用這個製作漂亮東西。裝飾準備好！', '', 38134),
(204745, 'zhTW', '', '即便許多汎希爾族都已經被世人遺忘，我們在瑞文崔斯還是有各自的職責。', 38134),
(204744, 'zhTW', '', '就別問我是怎麼拿到這個東西了。就把這想成是一種善意的表現吧，但你現在欠我一筆了。淵喉行者，我很期待我們未來的交易。', 38134),
(204742, 'zhTW', '', '因為我早就預料到你會需要什麼。而我就這麼剛好拿到了一個能前往那座塔的傳送裝置。', 38134),
(204741, 'zhTW', '', '我就不再提那些老生常談的警告跟免責聲明了，因為在你的愚勇之前這些都毫無意義。', 38134),
(204740, 'zhTW', '我必須要有那個卷軸，才有辦法完成我的研究。\n\n你就去跟女爵說，你找不到卷軸吧。', '我必須要有那個卷軸，才有辦法完成我的研究。\n\n就去跟女爵說，你沒找到卷軸吧。', 38134),
(204739, 'zhTW', '嗯，卷軸還在我這裡。\n\n你到底打算拿它做什麼啊？', '嗯，卷軸還在我這裡。\n\n你到底打算拿它做什麼啊？', 38134),
(204738, 'zhTW', '噢，這個啊？我這邊就快好了。\n\n幫我告訴妮奈妲，再給我一點點時間。', '噢，這個啊？我這邊就快好了。\n\n幫我告訴妮奈妲，再給我一點點時間。', 38134),
(204737, 'zhTW', '', '托迦司啊？當我跟你說你的朋友被關在裡面時，我就知道你遲早會來尋求我的協助。', 38134),
(204735, 'zhTW', '侍僧，你看不出來我在忙嗎？', '侍僧，你看不出來我在忙嗎？', 38134),
(204734, 'zhTW', '你不是還有儀式需要去研究嗎？', '你不是還有儀式需要去研究嗎？', 38134),
(204733, 'zhTW', '侍僧，怎麼了？', '侍僧，怎麼了？', 38134),
(204717, 'zhTW', '<你看到一隻小小的石蝙蝠從神殿上懸吊著。\n\n你很確定只要用特定數量的灌能紅寶石，就能喚醒那隻蝙蝠。\n\n這樣做的話，說不定就能讓那隻蝙蝠聽你差遣。>', '<你看到一隻小小的石蝙蝠從神殿上懸吊著。\n\n你很確定只要用特定數量的灌能紅寶石，就能喚醒那隻蝙蝠。\n\n這樣做的話，說不定就能讓那隻蝙蝠聽你差遣。>', 38134),
(204716, 'zhTW', '我們必須時刻保持警戒。', '', 38134),
(204714, 'zhTW', '就等你一聲令下了，淵喉行者。', '', 38134),
(204707, 'zhTW', '', '啊，沒錯，我還記得男爵想要借閱幾本我圖書館的書藏。如果我沒有記錯的話，應該都是一些枯燥乏味的書。\n\n誠如你所見，我這位巫妖忙得很。不過，如果你能減輕我的負擔，我說不定能把你要的那些書籍給你。', 38134),
(204706, 'zhTW', '', '侍僧，你居然敢打擾我，最好不要是什麼雞毛蒜皮的小事。\n\n說吧，怎麼了？', 38134),
(204705, 'zhTW', '', '噢，洛嘉娜，你親手為我縫紉的那身服裝大受好評。', 38134),
(204704, 'zhTW', '而且向來是我最喜歡的罪孽，親愛的洛嘉娜。', '', 38134),
(204703, 'zhTW', '祝你好運。不要被自己的網子給纏住了！', '', 38134),
(204702, 'zhTW', '', '淵喉行者，你應該把它帶回去聖所。也許德拉卡女爵或其他人會知道這個東西的來歷。', 38134),
(204701, 'zhTW', '鬱金香鼻略為輕視地笑道。', '', 38134),
(204700, 'zhTW', '淵喉行者，小心一點啊…我可不想要毀了你的衣服。', '', 38134),
(204699, 'zhTW', '', '啊，沒錯，就是它。這件護甲工藝精湛，無人能出其右。我從來沒見過這種東西，就連天選者的熔爐也製作不出來。', 38134),
(204695, 'zhTW', '特梅爾，來！這些妖精很懂得怎麼大鬧一場！', '', 38134),
(204694, 'zhTW', '不知道是哪個讓我更緊張，蟲子還是妖精…', '', 38134),
(204692, 'zhTW', '跟我來，侍僧。盡情享受你辛勤工作的果實吧。', '', 38134),
(204691, 'zhTW', '奈勒希斯，做好準備。我們凱旋的時刻就快實現了。', '', 38134),
(204690, 'zhTW', '淵喉行者，我能幫上什麼忙？', '', 38134),
(204689, 'zhTW', '我的朋友，你展現出絕倫的力量與狡詐。\n\n雖然我很歡迎你來跟死靈領主並肩作戰、一同奪回瑪卓薩斯，不過你目前是另一個誓盟的盟友。\n\n你確定要拋棄他們嗎？', '', 38134),
(204684, 'zhTW', '嗯，請你務必瞭解我們不能光聽你所說的。\n\n你願意為了重新加入琪瑞安把握這個機會好好努力嗎？', '', 38134),
(204681, 'zhTW', '嗨$n，又見面了。\n\n你還真有自信像這樣再次出現在我面前啊。\n\n我們還沒有忘了你是如何背棄我們遠大的目標。', '', 38134),
(204677, 'zhTW', '提高警覺！他感應到我們了！', '', 38134),
(204676, 'zhTW', '', '帕德，別打擾客人。$p顯然對這場宴會有所盤算。', 38134),
(204675, 'zhTW', '你看起來比一團泥巴漂亮！', '', 38134),
(204674, 'zhTW', '', '如果你厭倦這種裝扮，我在會後派對上還有更多不同的選擇給你參考。', 38134),
(204673, 'zhTW', '', '有利的選擇。每個零件都會正好符合特定人士的喜好。', 38134),
(204672, 'zhTW', '我選的也是那個！', '', 38134),
(204667, 'zhTW', '', '願你的武器克敵制勝。', 38134),
(204666, 'zhTW', '完成了。能跟你一起狩獵是一種榮幸。', '', 38134),
(204665, 'zhTW', '', '$n，只要你準備好，我們隨時可以開始。', 38134),
(204664, 'zhTW', '需要幫忙嗎？', '', 38134),
(204658, 'zhTW', '我相信你。\n\n現在我必須要求你宣誓對琪瑞安效忠，並成為琪瑞安誓盟的成員。\n\n跟著我複誦…\n\n「我會全心全意並且英勇地服侍琪瑞安。」', '', 38134),
(204657, 'zhTW', '我的朋友，我必須再詢問一次。\n\n你真的很確定你想要加入琪瑞安嗎？這代表你要背叛目前的盟友。\n\n他們短時間內是不會放下這件事的。\n\n所以，你確定嗎？', '', 38134),
(204656, 'zhTW', '你之前決定效忠另一個誓盟「$n」。你確定要踏上這條道路嗎？\n\n你對昇靈堡伸出援手，我們仍牢記在心，所以當然歡迎你加入我們。\n\n你真的已經準備好要背棄自己目前的盟友了嗎？', '', 38134),
(204653, 'zhTW', '別吵，你這個髒東西…我懷疑她根本不記得我是誰。', '別吵，你這個髒東西…我懷疑她根本不記得我是誰。', 38134),
(204652, 'zhTW', '費德里克，我看到監管者也出席了…', '費德里克，我看到監管者也出席了…', 38134),
(204651, 'zhTW', '那個墓穴裡只有心碎，千萬別讓他人牙碎了。', '那個墓穴裡只有心碎，千萬別讓他人牙碎了。', 38134),
(204650, 'zhTW', '你剛才有沒有看到那些希瓦爾的角？還真誘人…', '你剛才有沒有看到那些希瓦爾的角？還真誘人…', 38134),
(204649, 'zhTW', '哈羅威，你這個老罪孽石！那位已經沒救了…', '哈羅威，你這個老罪孽石！那位已經沒救了…', 38134),
(204648, 'zhTW', '我確定我剛才看到控罪者上台了。我一直都很仰望她…', '我確定我剛才看到控罪者上台了。我一直都很仰望她…', 38134),
(204647, 'zhTW', '噢，那是當然的。', '噢，那是當然的。', 38134),
(204646, 'zhTW', '我們應該大方地經過希奧塔和伊琳卡女士，讓他們羨慕嗎？', '我們應該大方地經過希奧塔和伊琳卡女士，讓他們羨慕嗎？', 38134),
(204645, 'zhTW', '謝謝你，費德里克，我的風格穿在你身上也很好看。', '謝謝你，費德里克，我的風格穿在你身上也很好看。', 38134),
(204644, 'zhTW', '哈羅威，我的穿著風格套在你身上挺不賴的嘛。', '哈羅威，我的穿著風格套在你身上挺不賴的嘛。', 38134),
(204643, 'zhTW', '', '這怎麼可能！', 38134),
(204641, 'zhTW', '噢，我愛死宮廷劇了！', '', 38134),
(204640, 'zhTW', '費德里克，這樣說吧，我才不想和你一起死在這裡！', '費德里克，這樣說吧，我才不想和你一起死在這裡！', 38134),
(204639, 'zhTW', '今天，我們將會見證永不陷落的罪殞城的誕生！', '', 38134),
(204638, 'zhTW', '啊，太好了。雕刻師已經到了。給賓客的特別獻禮。', '', 38134),
(204632, 'zhTW', '費德里克，這套服裝是我先找到的！你這時尚小偷！', '費德里克，這套服裝是我先找到的！你這時尚小偷！', 38134),
(204626, 'zhTW', '我該幫你打造什麼樣的雕像？', '', 38134),
(204623, 'zhTW', '棄誓者鍥而不捨地追尋著我們的知識。就在我們說話的同時，他們也正在尋找那些集結所有神聖楷模智慧的古代卷軸。\n\n我不知道目的是為了什麼，但我認為他們絕對不是要用來學習的。\n\n拜託，請拿回那些卷軸。我的侍徒會帶你去他們那邊。', '棄誓者鍥而不捨地追尋著我們的知識。就在我們說話的同時，他們也正在尋找那些集結所有神聖楷模智慧的古代卷軸。\n\n我不知道目的是為了什麼，但我認為他們絕對不是要用來學習的。\n\n拜託，請拿回那些卷軸。我的侍徒會帶你去他們那邊。', 38134),
(204621, 'zhTW', '沒有我們的雙翼，暗影之境就會陷入混亂。願我們永恆的使命指引你。', '沒有我們的雙翼，暗影之境就會陷入混亂。願我們永恆的使命指引你。', 38134),
(204620, 'zhTW', '退…退後！\n\n噢，你該不會是來強迫我遵守羊皮紙上那些糟糕透頂的法令的吧？\n\n那些汎希爾因為我拒絕抄寫他們的「研究」，就拿走了我的墨水。那算哪門子的研究。\n\n你能幫助我嗎？沒有墨水的話我根本一無是處！', '退…退後！\n\n噢，你該不會是要強迫我遵守羊皮紙上面那些糟糕透頂的法令吧？\n\n那些汎希爾因為我拒絕抄寫他們的「研究」，就拿走了我的墨水。那算哪門子的研究嘛。\n\n你能幫助我嗎？沒有墨水的話我根本一無是處！', 38134),
(204618, 'zhTW', '別忘記你的訓練。請相信這條道路，相信審斷者和她無窮的智慧。', '別忘記你的訓練。請相信這條道路，相信審斷者和她無窮的智慧。', 38134),
(204617, 'zhTW', '你會承擔很多重擔。有些重擔往往沉重無比。', '你會承擔很多重擔。有些重擔往往沉重無比。', 38134),
(204616, 'zhTW', '其他的一切是審斷者和永恆之路的工作。這才是我們的責任。', '其他的一切是審斷者和永恆之路的工作。這才是我們的責任。', 38134),
(204615, 'zhTW', '其他人生前的最後一刻，只是讓你們判斷他們是否陽壽已盡。', '其他人生前的最後一刻，只是讓你們判斷他們是否陽壽已盡。', 38134),
(204614, 'zhTW', '努力不要去評斷他人。所有靈魂都會來到審斷者面前，而且他們種下什麼因，就會得到什麼果。', '努力不要去評斷他人。所有靈魂都會來到審斷者面前，而且他們種下什麼因，就會得到什麼果。', 38134),
(204613, 'zhTW', '不要把自己帶入他們的人生，他們的人生是他們自己的。', '不要把自己帶入他們的人生，他們的人生是他們自己的。', 38134),
(204612, 'zhTW', '請牢記，窺探其他人的一生是為了瞭解這個人的一切，以客觀而直接的角度看見他們真實的樣貌。', '請牢記，窺探其他人的一生是為了瞭解這個人的一切，以客觀而直接的角度看見他們真實的樣貌。', 38134),
(204611, 'zhTW', '你必須拋開對其他人的成見，才能看見真相。', '你必須拋開對其他人的成見，才能看見真相。', 38134),
(204610, 'zhTW', '不錯的狩獵，$n。來吧，我們來談談你的報酬。', '', 38134),
(204609, 'zhTW', '', '那個果姆無法再吞食我們寶貴的森林。', 38134),
(204608, 'zhTW', '', '是新法術！我等不及想用用看了！', 38134),
(204604, 'zhTW', '', '我們會將法杖送回統御者之座。這個法杖會成為我們勝利的象徵。', 38134),
(204603, 'zhTW', '殺人犯！有個殺人犯！', '殺人犯！有個殺人犯！', 38134),
(204602, 'zhTW', '那是到底是在哀嚎還是在歡呼？我老是分不太出來…', '', 38134),
(204601, 'zhTW', '', '據說統御者消失的時候，他的護甲散落在各處。如果你能找到更多部件，我們的誓盟就會更加強大！', 38134),
(204588, 'zhTW', '就像大家說的，從淤泥到淤泥…', '', 38134),
(204587, 'zhTW', '你總有一天也會進到爛泥池。我們都一樣。', '', 38134),
(204580, 'zhTW', '', '別出聲。把這個洞穴的果姆逼到角落，可不是簡單的事。', 38134),
(204579, 'zhTW', '', '奧德林，你在這裡。很好。這頭野獸受傷了，但是那個外殼完好無損，我沒什麼進展。', 38134),
(204578, 'zhTW', '啊，這一定就是那個地方。', '', 38134),
(204567, 'zhTW', '我的命運…不該如此…', '我的命運…不該如此…', 38134),
(204566, 'zhTW', '瑪卓薩斯…會是我們的…', '瑪卓薩斯…會是我們的…', 38134),
(204565, 'zhTW', '祭儀家族…永不陷落…', '祭儀家族…永不陷落…', 38134),
(204564, 'zhTW', '誰能完成…我的研究？', '誰能完成…我的研究？', 38134),
(204563, 'zhTW', '入侵者！殺光他們！', '入侵者！殺光他們！', 38134),
(204562, 'zhTW', '你躲不過我們的魔法！', '你躲不過我們的魔法！', 38134),
(204561, 'zhTW', '你會後悔闖進來的！', '你會後悔闖進來的！', 38134),
(204560, 'zhTW', '你的屍體很適合當奴隸！', '你的屍體很適合當奴隸！', 38134),
(204559, 'zhTW', '', '我的死…無足輕重…', 38134),
(204558, 'zhTW', '', '你會親眼見證的！', 38134),
(204557, 'zhTW', '', '主人的偉業會改變整個暗影之境！', 38134),
(204556, 'zhTW', '', '你竟敢打斷我的儀式！', 38134),
(204554, 'zhTW', '意志軟弱的傢伙不該浪費我的時間，不過，要是你堅持的話，我會讓你見識真正的痛苦。', '', 38134),
(204553, 'zhTW', '讓天空中的生物來帶領亞登曠野的守軍吧。', '', 38134),
(204552, 'zhTW', '亞登曠野的生物或許看起來很溫馴，不過為了守護家園，他們願意拿出無與倫比的力量和勇氣。你也有這種覺悟嗎？', '', 38134),
(204548, 'zhTW', '', '產量催化物質能增加靈魂復原後給你的資源獎勵數量。\n\n如果你有足夠的空間，可以同時種下好幾個產量催化物質。', 38134),
(204547, 'zhTW', '', '品質催化物質會加強靈魂復原後給你的獎勵的種類和品質。\n\n如果你有足夠的空間，可以同時種下好幾個品質催化物質。', 38134),
(204546, 'zhTW', '', '加速催化物質能縮短野性種子孵化及重新生長的時間。\n\n如果你有足夠的空間，可以同時種下好幾個加速催化物質，加速效果會疊加上去。', 38134),
(204545, 'zhTW', '', '你能透過催化物質影響野性種子的成長方式。\n\n你現在可以在花園的催化區種下催化物質。種下後，你就能從催化物質的靈魄流動方向看到它會影響哪些野性種子。\n\n如果野性種子已經在成長中，則無法受益於後來才種下的催化物質。\n\n催化物質有很多不同的類型。每種對野性種子都有不同的效果。', 38134),
(204543, 'zhTW', '我們在使命中所擔任的角色，目前還無法下結論。\n\n淵喉行者，你可能已經熟悉這種狀況了，但這對我們來說是前所未有的事情。', '', 38134),
(204542, 'zhTW', '多麼壯觀美麗的景象啊。\n\n雖然淵喉違抗著使命，但它仍然是這個國度不可分割的一部份。', '', 38134),
(204541, 'zhTW', '淵喉蘊藏著如此強大的力量，但卻與使命背道而馳。\n\n這是使命考驗我們的方式。', '', 38134),
(204540, 'zhTW', '我們都有份內的工作。\n\n使命是這樣告訴我們的。\n\n當我們還知道自己的意義與價值時，我們對此深信不疑。但現在呢？我迷失了方向。\n\n我們絕對不能失去信念。要相信使命。\n\n使命是我們的生存之道。', '', 38134),
(204539, 'zhTW', '使命總是能一次又一次發人深省，不是嗎？', '', 38134),
(204538, 'zhTW', '真不知道這些變化會帶領我們走向何方。使命向來都會指引我們前進的方向。\n\n也就是說，這仍然是使命。但我不知道結局會是如何。\n\n就算是小齒輪，也會想要知道自己扮演著什麼樣的角色。', '', 38134),
(204537, 'zhTW', '淵喉行者，歡迎你。\n\n你看到的東西不一定全是商品。我們也會收購東西。\n\n我們在你的國度找到這麼奇特的生物。我很高興能增加我的收藏。', '', 38134),
(204536, 'zhTW', '淵喉行者，歡迎你。\n\n這個裝置能讓你從各個次元使用裂片倉庫，就是那些把這個世界和你的世界分隔開來的次元。我跟那些擁有神聖使命的侍者不一樣，我很清楚這個東西怎麼運作。\n\n不過，我並不會把實情告訴你。就想成是商業機密吧。\n\n不過你隨時可以使用它。仲介者請客。', '', 38134),
(204535, 'zhTW', '', '以五大家族之名…是統御者的肩鎧！', 38134),
(204534, 'zhTW', '', '淵喉行者，把統御者的護甲帶去聖所，它屬於那個榮耀的地方。', 38134),
(204533, 'zhTW', '', '這個巫妖危及我們雙方的國度。當我們的部隊準備出擊時，我們會送出瑞文崔斯參戰的呼喚。', 38134),
(204532, 'zhTW', '', '墮落王子，別對我頤指氣使！我們必須先深入瞭解這場威脅，不可冒進。', 38134),
(204531, 'zhTW', '我會讓科爾蘇加德付出代價！德拉卡女爵！我要你馬上護送我回瑪卓薩斯！', '', 38134),
(204530, 'zhTW', '算了。祭儀家族聽命於我。你真的有種，就在那邊找我吧！', '', 38134),
(204529, 'zhTW', '愛管閒事的傢伙！你居然敢打倒我的下屬？', '', 38134),
(204528, 'zhTW', '', '那個巫妖正在抽取一股強大的力量…', 38134),
(204527, 'zhTW', '看到你們偉大的統治者被鎖鏈銬住，一定感到無地自容吧。', '', 38134),
(204526, 'zhTW', '好朋友，你在這裡做什麼？', '', 38134),
(204516, 'zhTW', '我？當然不行。但換個角度想，淵喉行者…我欣賞我們的運氣。', '', 38134),
(204515, 'zhTW', '', '你說女伯爵？加瑞登勛爵，你真以為你能說服她參加？', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204514, 'zhTW', '我希望他帶著灰燼使者。那一定很有看頭！', '', 38134),
(204513, 'zhTW', '', '噢，我簡直等不及想和來自瑪卓薩斯的亞歷山卓斯‧莫格萊尼見面了…', 38134),
(204506, 'zhTW', '埃格準備好戰鬥了！', '', 38134),
(204503, 'zhTW', '', '誰也想不到居然能在這種糟糕的地方展開這麼特別的戰鬥，但人算不如天算。不要浪費我的時間。', 38134),
(204500, 'zhTW', '', '我們服從統御者的號令！我們會齊心奮戰，拯救暗影之境！', 38134),
(204499, 'zhTW', '', '你找出了瑪卓薩斯真正的敵人，淵喉行者。現在你可以把那些肩鎧放回它們該去的地方了。', 38134),
(204497, 'zhTW', '淵喉行者，謝謝你邀請我，能夠出席雷納瑟王子的宴會，是我的榮幸。', '', 38134),
(204484, 'zhTW', '你想要展示什麼，$n？', '你想要展示什麼，$n？', 38134),
(204474, 'zhTW', '', '我的職責是守護宮廷的特使，但我偶爾會聽到一些需要解決的威脅。', 38134),
(204472, 'zhTW', '你現在可能在想：「那個地精困在一團奇怪的黏液裡，可能需要幫忙！」  錯了！信不信由你，我是自願變成這樣子的。這是變裝、偽裝、化妝…你喜歡怎麼叫，就怎麼叫。這團黏液是我在這個糟糕的地方的唯一保護。幸好，我在這裡發現了不少值得訓練的新寵物，組建了一支實力高強的隊伍。我會一路過關斬將，直到獲得我應得的尊敬！唯一的壞處是我的個人物品都變得黏黏的，無一倖免。', '', 38134),
(204471, 'zhTW', '腐腸、殘渣。多出來的。你要打。', '', 38134),
(204470, 'zhTW', '我等了這麼多年，就是想要一個好對手。贏家擁有所有戰利品！', '', 38134),
(204469, 'zhTW', '就算是規模再小的戰爭，也要慎重以對。等你準備好了，就展示你的隊伍。', '', 38134),
(204466, 'zhTW', '', '我們非常嚴肅看待我們的防禦。無論大小，我們都致力於善用每一分資源，保護昇靈堡。', 38134),
(204457, 'zhTW', '你是怎麼活下來的？進行這麼危險的行為，竟然連能提供基本防護的裝備都沒有穿。拿去吧，這是多出來的。', '', 38134),
(204455, 'zhTW', '', '盟友在上方的亡者之城等候。', 38134),
(204442, 'zhTW', '', '黯刃騎士準備就緒。', 38134),
(204441, 'zhTW', '黯刃騎士準備就緒。', '', 38134),
(204439, 'zhTW', '$c，你不在的時候發生了很多事。\n\n讓我幫你統整一下最新狀況。', '', 38134),
(204438, 'zhTW', '', '$c，你不在的時候發生了很多事。\n\n讓我幫你統整一下最新狀況。', 38134),
(204435, 'zhTW', '', '你的使命似乎和我們的交纏在一起。', 38134),
(204431, 'zhTW', '', '這裡有如此廣大的開放平原。正好適合拿來戰鬥。你準備好了嗎？', 38134),
(204357, 'zhTW', '以凡人的標準而言…你的衣著還不賴。', '以凡人的標準而言…你的衣著還不賴。', 38134),
(204356, 'zhTW', '', '他失去防護了！淵喉行者，快擊倒他！我們會掩護你的！', 38134),
(204355, 'zhTW', '', '用這位部落勇士的幻象來折磨我。是來幫我的？還是你想看我們打起來？', 38134),
(204349, 'zhTW', '呸，你太無趣了。', '', 38134),
(204346, 'zhTW', '淵喉行者！想來玩一場抓泥掘者的遊戲嗎？\n\n我把自己全身上下都塗滿油，但沒人想來追我！\n\n說不定潛入那個舊爛泥池，可以活絡你的筋骨？', '', 38134),
(204344, 'zhTW', '', '這一定很有意思…', 38134),
(204338, 'zhTW', '好的，看看這些…', '', 38134),
(204320, 'zhTW', '其中一個可能是我的名字…', '', 38134),
(204317, 'zhTW', '準備好回到奧睿博司了嗎？', '', 38134),
(204316, 'zhTW', '哈羅威，你還真有種！居然敢穿著和我一樣的服裝現身。', '哈羅威，你還真有種！居然敢穿著和我一樣的服裝現身。', 38134),
(204311, 'zhTW', '如果他們不喜歡，他們就不是我的粉絲！', '', 38134),
(204310, 'zhTW', '他們當然會喜歡，丘。這是一幅傑作！', '', 38134),
(204309, 'zhTW', '', '希望我們的粉絲會喜歡…', 38134),
(204308, 'zhTW', '我們的新海報就在地穴裡面。是丘幫他們畫的。', '', 38134),
(204307, 'zhTW', '看來也只能先回去找你的盟友，然後從長計議了。', '', 38134),
(204306, 'zhTW', '科爾蘇加德撤退到亡城了。我們兩個現在沒辦法對付他。', '', 38134),
(204305, 'zhTW', '快點！我們快趕不上了！', '', 38134),
(204304, 'zhTW', '看那邊…有打鬥的痕跡，一路延伸到藩侯的大廳。', '', 38134),
(204303, 'zhTW', '嗯，你辦到了！我就知道你一定可以的。', '', 38134),
(204302, 'zhTW', '你需要花點心思才能找到通往那裡的路。', '', 38134),
(204301, 'zhTW', '試著爬上架子去找拉桿。', '', 38134),
(204300, 'zhTW', '拉桿就在大廳的另一側。門的右邊。', '', 38134),
(204299, 'zhTW', '一隻手？沒錯…沒錯！我的統御者呀，真是太天才了！', '', 38134),
(204298, 'zhTW', '火炬？雖然很多壞人可能會把隱藏開關藏在裡面，但是這種情況通常不會出現在監牢裡。', '', 38134),
(204297, 'zhTW', '除非你想找個跟你一樣蠢的傢伙說話，不然這個頭骨對我們沒什麼用處。', '', 38134),
(204296, 'zhTW', '恐怕我的魔法對鎖鍊起不了什麼作用。看你能不能找到其他有用的東西。', '', 38134),
(204295, 'zhTW', '守衛！把這個傢伙扔進監牢！然後把那個背叛的巫妖給我帶過來！', '', 38134),
(204294, 'zhTW', '原來背後的主謀就是你！看看誰也來了…巴爾米達之眼！', '', 38134),
(204293, 'zhTW', '這不可能！等等…這是叛徒做的！', '', 38134),
(204292, 'zhTW', '什麼…發生了什麼事？不！', '', 38134),
(204291, 'zhTW', '引渡者的鮮血會打開這條道路！', '', 38134),
(204290, 'zhTW', '屏障的力量被削弱了！被封印了無數歲月的通道很快就會開啟了！', '', 38134),
(204289, 'zhTW', '很好！我看見了！淵喉的黑暗從遠處呼喚著我！', '', 38134),
(204288, 'zhTW', '儀式開始！開啟靈魄之流！', '', 38134),
(204287, 'zhTW', '希望那不會對你造成致命的傷害。', '', 38134),
(204286, 'zhTW', '我很確定儀式一定會失敗，而且或許會造成…額外的損害。', '', 38134),
(204285, 'zhTW', '原本的計畫本來是不會讓你參加儀式的，但看來我們得見機行事。', '', 38134),
(204284, 'zhTW', '遵命，主人。我們會照辦！', '', 38134),
(204283, 'zhTW', '看來有個叛徒混在我們之中。我們必須加緊行動。', '', 38134),
(204282, 'zhTW', '到處都找不到，主人。', '', 38134),
(204281, 'zhTW', '別浪費我的時間淨說些廢話！我的材料呢？', '', 38134),
(204280, 'zhTW', '…商隊被徹底洗劫一空。負責護送的仲介者也慘遭殺害。', '', 38134),
(204279, 'zhTW', '帶著剩下的材料來找我。我有個計畫。', '', 38134),
(204278, 'zhTW', '來自仲介者的神秘貨物？真有意思，我要看看裡面是什麼！', '', 38134),
(204277, 'zhTW', '不管男爵要求什麼，都先帶來給我。我要看看他用的是什麼材料。', '', 38134),
(204276, 'zhTW', '或許我們可以從這些材料中推測出儀式的本質。', '', 38134),
(204275, 'zhTW', '繼續你的行動，看看還能找到什麼線索。', '', 38134),
(204274, 'zhTW', '忙了老半天，結果對男爵的計畫還是毫無頭緒。', '', 38134),
(204273, 'zhTW', '科爾蘇加德到底想從這本書中找到什麼？', '', 38134),
(204272, 'zhTW', '「監禁者之縛」？這本書不就是歷史記載而已嗎？', '', 38134),
(204271, 'zhTW', '', '多虧了你，栽培園現在完全恢復了。\n\n你現在能使用最多支援四個催化物質連結的野性種子。這樣一來你就能最大程度地善用花園的完整潛力。', 38134),
(204270, 'zhTW', '男爵究竟想建立…或是避開什麼屏障呢？真奇怪。', '', 38134),
(204269, 'zhTW', '', '你在恢復栽培園的路上往前邁了一大步。\n\n你現在能使用最多支援三個催化物質連結的野性種子。這能讓你更深入地探索在這裡讓神靈恢復的力量。', 38134),
(204268, 'zhTW', '「強力結界和屏障，第四部之反制法術」。確實是本艱澀費解的典籍。', '', 38134),
(204266, 'zhTW', '難道他的儀式跟連接死亡領域的通道有某種關聯？', '', 38134),
(204265, 'zhTW', '「首創者之道」？這就怪了，裡面記載的都是臆測和半真半假的訊息。', '', 38134),
(204264, 'zhTW', '他究竟想從如此稀鬆平常的內容中找到什麼呢？', '', 38134),
(204263, 'zhTW', '「靈翼秘典」。研究這本琪瑞安的思想典籍並不讓人意外。', '', 38134),
(204261, 'zhTW', '我明白的事情比棄誓者要多。\n\n必須追求完美才能成功！一定要照顧工具與資源。', '', 38134),
(204259, 'zhTW', '不過，這還不能算是背叛的證據。來看看他找的這些典籍藏有什麼秘密。', '', 38134),
(204258, 'zhTW', '男爵在尋求汎希爾的幫助？這也不是說無法原諒，如果他願意分享他的戰利品的話。', '', 38134),
(204257, 'zhTW', '話就說到這裡。差不多該準備進行儀式了。', '', 38134),
(204256, 'zhTW', '只要利用我們在瑞文崔斯的盟友取得的靈魄貯存，就能開啟邁向勝利的大門了。', '', 38134),
(204255, 'zhTW', '這個可悲的琪瑞安體內，擁有通往強大力量之所在的鑰匙。', '', 38134),
(204254, 'zhTW', '你會付出代價的，巫妖！我才不是任何人的獎賞！', '', 38134),
(204253, 'zhTW', '其中還包含了我個人的獎賞，琪瑞安族稱之為「引渡者」。', '', 38134),
(204252, 'zhTW', '傀儡們帶著我們襲擊昇靈堡的剩餘戰利品回來了。', '', 38134),
(204251, 'zhTW', '但你是絕對不會背叛我的…對吧？', '', 38134),
(204250, 'zhTW', '要是你哪天敢背叛我，那我保證一定會讓藩侯知道真正的兇手是誰。', '', 38134),
(204249, 'zhTW', '噢，我差點忘了告訴你，剛才那名被你殘忍殺害的侍僧，碰巧是藩侯辛丹最喜歡的手下。', '', 38134),
(204248, 'zhTW', '太好了。來刀刃大廳找我，我要鑑定一下你的偽裝品質如何。', '', 38134),
(204247, 'zhTW', '你的下一個任務可就沒這麼簡單。希望你已經準備好接受挑戰了。', '', 38134),
(204246, 'zhTW', '太好了，你找到了我的小「關懷包」。看來你也不是完全沒用嘛。', '', 38134),
(204245, 'zhTW', '這件事別告訴別人。', '', 38134),
(204244, 'zhTW', '那座塔在斯卡里安山脊附近。到了那裡，你就會知道該怎麼做了。', '', 38134),
(204242, 'zhTW', '我在想，我們要為誰演奏？', '', 38134),
(204241, 'zhTW', '', '有了這些樂器，我覺得過去的自己又回來了…', 38134),
(204240, 'zhTW', '終於！', '', 38134),
(204239, 'zhTW', '反省時間結束。可以開始排練了！', '', 38134),
(204238, 'zhTW', '各位，有好消息！我預訂好下一場表演了…而且規模很大喔！', '', 38134),
(204237, 'zhTW', '', '在我還是個凡人的時候，我從來都沒有辦法在同一個地方待很久。\n\n每一天對我來說都是嶄新的冒險，我下定決心一定要找到它。\n\n我感覺你的體內也有同樣的浪者之魂。\n\n告訴我吧，旅行者…你相信管理員泰多的那套理論嗎？音樂有辦法拯救靈魂嗎？\n\n我不是很確定。', 38134),
(204236, 'zhTW', '安塞爾太杞人憂天了。不會出什麼事的。\n\n誠如艾薩常說的，每翻越一座沙丘，都更有機會找到水源。\n\n音樂會拯救我們的。向來如此。', '', 38134),
(204235, 'zhTW', '你聽說過音樂是靈魂的糧食嗎？\n\n當然了，這只是個譬喻…\n\n<管理員泰德的眼睛直直盯著你。>\n\n但如果其實不只如此呢？\n\n如果，對少數幾個在生前受到音樂吸引的人來說，音樂是一種救贖的工具呢？\n\n如果音樂真的能治癒靈魂呢？\n\n我相信這是千真萬確的。', '', 38134),
(204233, 'zhTW', '', '栽培園現在恢復地更完整了。你可以使用更多的野性種子以及催化區。\n\n在你將靈魂種進野性種子之前，催化區能提供額外的祝福。\n\n當你種下催化物質時，你能看到它和哪些野性種子相連結。', 38134),
(204232, 'zhTW', '如果我們一直把樂器弄丟，那我們要怎麼用音樂讓管理員對我們留下好印象？\n\n<好運安塞爾看了一眼外頭的罪孽石。>\n\n我們永遠都別想離開這了。', '', 38134),
(204231, 'zhTW', '', '目前只有兩個野性種子恢復了。你現在能做的就是，用它們幫助靈魂恢復力量。\n\n隨著你繼續將這片林地恢復至往日榮光，花園的潛在力量也會進一步綻放。\n\n當你更進一步恢復栽培園後再來問我吧。', 38134),
(204229, 'zhTW', '', '歡迎，$n。我今天能幫你什麼忙？', 38134),
(204228, 'zhTW', '', '歡迎，$n。等你把女王的栽培園復原後，就能從這個蘑菇傳送們進入。\n\n潔哈德會和你說所有我們需要你幫忙修復的東西。', 38134),
(204227, 'zhTW', '懷特邁恩會告訴你，我們的盟友遭遇到了什麼樣的殘酷厄運。', '', 38134),
(204226, 'zhTW', '納茲格寧姆會告訴你，我們的盟友遭遇到了什麼樣的殘酷厄運。', '', 38134),
(204225, 'zhTW', '在敗給希瓦娜斯之後，大領主伯瓦爾的身體已經康復。他跟盟友們正在等我們過去。', '', 38134),
(204224, 'zhTW', '我們下令把亞榭洛移動到冰冠城塞附近。請容我開啟死亡之門帶大家過去。', '', 38134),
(204223, 'zhTW', '死亡領主！我們準備好了。', '', 38134),
(204222, 'zhTW', '黯刃騎士準備就緒。我在奧格瑪等你。來吧，趁寒冷的死亡還沒把魔掌伸到艾澤拉斯的每個角落之前。', '', 38134),
(204221, 'zhTW', '死亡領主，分隔我們這個世界和暗影之境的帷幕已經被撕裂了。', '', 38134),
(204220, 'zhTW', '黯刃騎士準備就緒，我在暴風城等你。來吧，趁寒冷的死亡還沒把魔掌伸到艾澤拉斯的每個角落之前。', '', 38134),
(204219, 'zhTW', '勇士，分隔我們這個世界和暗影之境的帷幕已經被撕裂了。', '', 38134),
(204217, 'zhTW', '去削弱他，我或許有辦法用法杖來救她！', '', 38134),
(204216, 'zhTW', '科爾蘇加德正在吸取藩侯辛丹的力量！', '', 38134),
(204215, 'zhTW', '僕從們！消滅這些多事的傢伙！', '', 38134),
(204211, 'zhTW', '<行李箱吱吱作響地打開，裡面有各種服裝和配飾。>', '', 38134),
(204210, 'zhTW', '<裁縫師蘿嘉娜上下打量著你。>\n\n真是…令人震驚的派對服飾品味啊。\n\n我很確定你可以在我的箱子裡找到更適合的衣服。', '<裁縫師蘿嘉娜上下打量著你。>\n\n真是…令人震驚的派對服飾品味啊。\n\n我很確定你可以在我的箱子裡找到更適合的衣服。', 38134),
(204201, 'zhTW', '有派對的精采日子。', '有派對的精采日子。', 38134),
(204200, 'zhTW', '$p，以凡人來說，你相當有智慧。', '$p，以凡人來說，你相當有智慧。', 38134),
(204199, 'zhTW', '忍住別說！', '忍住別說！', 38134),
(204198, 'zhTW', '有時我覺得哈羅威之所以說話只是想聽到自己的聲音！', '有時我覺得哈羅威之所以說話只是想聽到自己的聲音！', 38134),
(204197, 'zhTW', '我想你是對的。費德里克的糟糕美學品味簡直沒救了。', '我想你是對的。費德里克的糟糕美學品味簡直沒救了。', 38134),
(204196, 'zhTW', '費德里克只是忌妒我比他更適合這件衣服。死後的生活在他的肚子上留下了痕跡。', '費德里克只是忌妒我比他更適合這件衣服。死後的生活在他的肚子上留下了痕跡。', 38134),
(204194, 'zhTW', '你看到哈羅威穿著什麼嗎？真沒創意。我應該把那些衣服從他的背上撕下來！', '你看到哈羅威穿著什麼嗎？真沒創意。我應該把那些衣服從他的背上撕下來！', 38134),
(204192, 'zhTW', '長官，晚安。新老大要我來這裡看看我們能用即將到來的靈魄做些什麼。\n\n有足夠的靈魄我們就可以重建大部分的牆，防止瑞文崔斯淪陷。', '', 38134),
(204187, 'zhTW', '凡人，儘管瞎攪和吧。你們無法避免必然的結果。', '', 38134),
(204186, 'zhTW', '果然不出我所料。不過，你們對失敗大概習以為常了吧。', '', 38134),
(204185, 'zhTW', '是維拉斯把我困在淵喉裡面的。我們的藩侯一定是維拉斯害死的。', '', 38134),
(204182, 'zhTW', '你們自以為與眾不同、天賦異稟。但你們遲早會懂得謙卑的。', '', 38134),
(204176, 'zhTW', '你想飛？是嗎？', '你想飛？是嗎？', 38134),
(204175, 'zhTW', '表現的還不錯嘛。不過，我還是很期待終結你們的生命。', '', 38134),
(204174, 'zhTW', '', '現在就追過去是不智之舉。不要分心，以後有的是大鬧一場的機會。', 38134),
(204169, 'zhTW', '我看到你拿到石磚了。你要我製作什麼？', '', 38134),
(204164, 'zhTW', '但是頭盔毀掉後，伯瓦爾也卸下了巫妖王的重擔。現在他與我們享有同等的自由了，死亡領主。', '', 38134),
(204163, 'zhTW', '', '我已經花了一千年的時間在練習打擊樂的禮儀藝術了。\n\n我還曾經獲邀替凜冬女王本人演奏曲目！現在我居然還有機會能夠在這麼盛大的派對上表演。謝謝你，淵喉行者。', 38134),
(204160, 'zhTW', '我在想我們應該在前門放座新雕像。\n\n你覺得誰的雕像比較好？', '', 38134),
(204155, 'zhTW', '拿去！在小果姆的地穴附近使用這支笛子，它們就會從地下爬出來！', '', 38134),
(204138, 'zhTW', '願我所有先祖的靈魂，都能在和平舒適的國度中安息沉眠。', '', 38134),
(204137, 'zhTW', '我原本以為暗影之境這裡到處都充滿著黑暗。還有地方存有希望，真是太好了。', '', 38134),
(204133, 'zhTW', '', '索爾，你知道他們把安杜因帶去哪裡了嗎？', 38134),
(204132, 'zhTW', '我的朋友啊！', '', 38134),
(204125, 'zhTW', '他們說老大和其他老大一樣是個老大，但是每一個老大都比上一個老大還喜歡當個愛鞭打的老大。\n\n但王子不是這種老大。他還不賴，對一個有利牙的吸血鬼來說。是很好相處的那一型。', '', 38134),
(204124, 'zhTW', '多個紀元以來，我曾經在好幾個國度中踏上無數個戰場。\n\n在這些大大小小的征戰之中，我從來沒有像服侍雷納瑟王子、德瑞文將軍和你時這樣深感榮耀。\n\n我們是為了我們國度的完整而戰，面對無窮無盡的大軍。我就是為此而生的。', '', 38134),
(204123, 'zhTW', '如果雷納瑟王子是認真的要收復失去的領土，那我們會需要集結更多的石源魔來完成他的使命。\n\n如果他能直接說服石鑄宗匠加入我們的話，那整件事會輕鬆許多。照目前的狀況來看，我們必須只活化特定的石源魔才行，以免我們喚醒的部隊反被對手掌控並用來對付我們。\n\n好險有我在你身邊告訴你哪些石源魔效忠我們。', '如果雷納瑟王子是認真的要收復失去的領土，那我們會需要集結更多的石源魔來完成他的使命。\n\n如果他能直接說服石鑄宗匠加入我們的話，那整件事會輕鬆許多。照目前的狀況來看，我們必須只活化特定的石源魔才行，以免我們喚醒的部隊反被對手掌控並用來對付我們。\n\n好險有我在你身邊告訴你哪些石源魔效忠我們。', 38134),
(204122, 'zhTW', '我也應該據此清除紀錄中的所有潛在報復計畫。', '我也應該據此清除紀錄中的所有潛在報復計畫。', 38134),
(204114, 'zhTW', '根據我的紀錄來看，你已經找回所有被偷的紀念物。', '根據我的紀錄來看，你已經找回所有被偷的紀念物。', 38134),
(204110, 'zhTW', '<這不是場意外！\n\n摩特上校身上的傷意味著他的死不只很痛苦也很迅速。\n\n現場沒有打鬥的痕跡，也沒有任何證人目睹這起暗殺事件。\n\n不管這個可怕的兇手是誰，他很有可能還藏身在人群裡。>', '', 38134),
(204108, 'zhTW', '今天就是藩侯珈爾瑪的死期。', '', 38134),
(204107, 'zhTW', '好多志士，果汁太少。晚點再來，好嗎？', '好多志士，果汁太少。晚點再來，好嗎？', 38134),
(204104, 'zhTW', '唷呼！這裡還有飲料。快拿！快拿！', '唷呼！這裡還有飲料。快拿！快拿！', 38134),
(204103, 'zhTW', '<侍者把手中的托盤遞了過來，發出一聲輕響。>', '<侍者把手中的托盤遞了過來，發出一聲輕響。>', 38134),
(204100, 'zhTW', '志士口渴嗎？侍者有飲料！', '志士口渴嗎？侍者有飲料！', 38134),
(204099, 'zhTW', '喝吧！喝吧！喝？', '喝吧！喝吧！喝？', 38134),
(204097, 'zhTW', '來一杯？很美味哦！', '來一杯？很美味哦！', 38134),
(204081, 'zhTW', '', '你好啊，朋友！\n\n還有很多工作尚未完成。\n\n如果你有空的話，來幫個忙吧，我們會很感激你的。', 38134),
(204080, 'zhTW', '如果我們把它帶回去給藩侯辛丹，說不定還有機會對抗科爾蘇加德。', '', 38134),
(204079, 'zhTW', '等等…是統御者之杖！而且沒有受損！', '', 38134),
(204078, 'zhTW', '', '前往樞運之環並打開通往亞登曠野的門。願使命引導你，凡人。', 38134),
(204077, 'zhTW', '', '亞登曠野的林地需要靈魄，才能餵食沉睡於那裡的自然之靈。希望女王有足夠的儲量抵禦這場可怕的乾旱。', 38134),
(204076, 'zhTW', '', '凜冬女王是長久以來受到統御者信任的盟友。我相信她一定會聽從他的警告。', 38134),
(204075, 'zhTW', '只要同心協力，我們就能一探閻獄之主的國度。不過要小心，別讓自己迷失在黑暗之中。', '', 38134),
(204074, 'zhTW', '破損的頭盔依然與巫妖王的力量，也就是…與淵喉的力量產生共鳴。', '', 38134),
(204073, 'zhTW', '', '統治瑞文崔斯的是戴納瑟斯王。我祈禱他在這場靈魄乾旱中能撐得比其他國度更久。我們非常需要他出手協助。', 38134),
(204072, 'zhTW', '', '那些被審斷者判斷可能會引發衝突的傲慢靈魂會以此地為歸屬，而汎希爾人會引領這些靈魂走向救贖之路。', 38134),
(204071, 'zhTW', '', '瑞文崔斯是個不祥國度，到處都是黑暗尖塔和冰冷石牆。不過這片土地和居民背負著高貴的使命。', 38134),
(204070, 'zhTW', '', '若是少了瑪卓薩斯之力，恐怕我們阻止監禁者的希望…渺茫。', 38134),
(204069, 'zhTW', '', '凡人，你必須了解他們攻擊琪瑞安的動機。在衝突升級之前，盡全力阻止這場衝突。', 38134),
(204068, 'zhTW', '她沒能倖存下來…', '', 38134),
(204067, 'zhTW', '', '葛薇亞！不…', 38134),
(204066, 'zhTW', '', '堅持住啊，葛薇亞！詛咒快要破除了！', 38134),
(204065, 'zhTW', '', '繼續加油！佐司特對她的影響消退了！', 38134),
(204064, 'zhTW', '', '動作快！摧毀那些東西！', 38134),
(204063, 'zhTW', '', '做好準備，淵喉行者。要淨化勾拉札爾的魔法沒那麼容易。', 38134),
(204062, 'zhTW', '', '我需要一個小型、快速且能輕裝行進的傀儡。\n\n我的敵人很少看見我，但他們總是害怕我的存在。', 38134),
(204060, 'zhTW', '我就知道你需要我的幫忙。', '', 38134),
(204059, 'zhTW', '我需要一具有強韌鋼鐵外殼的傀儡。\n\n這樣的話，你碰到了什麼麻煩我都能幫你解決。', '', 38134),
(204058, 'zhTW', '加瑞登勛爵並沒有誇大。你辦的宴會非常棒！', '加瑞登勛爵並沒有誇大。你辦的宴會非常棒！', 38134),
(204057, 'zhTW', '挑剔的史帝芬人呢？他說好要給我一個專屬樣品的。', '挑剔的史帝芬人呢？他說好要給我一個專屬樣品的。', 38134),
(204056, 'zhTW', '我很高興希奧塔從流放中歸來了。真是厲害！', '我很高興希奧塔從流放中歸來了。真是厲害！', 38134),
(204055, 'zhTW', '你有在別的地方看過超大飲料嗎？', '你有在別的地方看過超大飲料嗎？', 38134),
(204054, 'zhTW', '我希望這場宴會不要結束！', '我希望這場宴會不要結束！', 38134),
(204053, 'zhTW', '我剛剛在那裡陪你的貴賓聊天…不用謝！', '我剛剛和你的貴賓在那裡聊天…不客氣！', 38134),
(204052, 'zhTW', '這是我一生中最美好的時刻！', '這是我一生中最美好的時刻！', 38134),
(204051, 'zhTW', '我必須承認，你辦了一場很棒的宴會。', '我必須承認，你辦了一場很棒的宴會。', 38134),
(204050, 'zhTW', '安可派對才是最棒的派對！', '安可派對才是最棒的派對！', 38134),
(204049, 'zhTW', '你之後要如何辦個比這次更棒的宴會啊？', '還有宴會比這次更好的嗎？！', 38134),
(204048, 'zhTW', '我必須承認我更喜歡…另一種風格的宴會。', '我必須承認我更喜歡…另一種風格的宴會。', 38134),
(204047, 'zhTW', '這是這輩子最開心的時候！', '這是這輩子最開心的時候！', 38134),
(204046, 'zhTW', '我不敢相信那…喔，你好啊，淵喉行者。', '我不敢相信那…喔，你好啊，淵喉行者。', 38134),
(204045, 'zhTW', '下次記得要邀請我！', '下次記得要邀請我！', 38134),
(204044, 'zhTW', '我等不及告訴我的朋友了。', '我等不及告訴我的朋友了。', 38134),
(204043, 'zhTW', '真是美妙的宴會！', '真是美妙的宴會！', 38134),
(204014, 'zhTW', '餘燼宴會是不是一如傳言？我等不及要一探究竟了！', '餘燼宴會是不是一如傳言？我等不及要一探究竟了！', 38134),
(204013, 'zhTW', '暗隱村的每個人都會嫉妒到不行！', '暗隱村的每個人都會嫉妒到不行！', 38134),
(204012, 'zhTW', '你覺得我今天會見到多少名人？', '你覺得我今天會見到多少名人？', 38134),
(204011, 'zhTW', '這當做我們之間的小秘密，我還是不敢相信我居然會拿到邀請函…我好興奮哦！', '這當做我們之間的小秘密，我還是不敢相信我居然會拿到邀請函…我好興奮哦！', 38134),
(204010, 'zhTW', '這場宴會將成為全城的話題！', '這場宴會將成為全城的話題！', 38134),
(204009, 'zhTW', '你覺得我的靴子如何？為了這場盛會，我裝上了超長的尖刺！', '你覺得我的服裝如何？我一直留到特殊場合才穿。', 38134),
(204008, 'zhTW', '我等不及要看到他們打開大門了！', '我等不及要看到他們打開大門了！', 38134),
(204007, 'zhTW', '給我一點提示，淵喉行者！今天的宴會上，有什麼令人與奮的新玩意？', '給我一點提示，淵喉行者！今天的宴會上，有什麼令人與奮的新玩意？', 38134),
(204005, 'zhTW', '亞拉隆…我會馬上派出搜索隊。不能讓佐司特奪走他。', '', 38134),
(204003, 'zhTW', '', '亞拉隆沒有帶著背袋回來。我擔心他遇到了麻煩，或是遭遇不測。', 38134),
(204002, 'zhTW', '', '到森林之心和我會合，準備完成儀式。祝二位狩獵順利。', 38134),
(204000, 'zhTW', '', '他很在意形式，所以記得注意你的禮節。', 38134),
(203999, 'zhTW', '我們已經收到墓穴守衛者凱希爾的聯絡了，他會出席。', '', 38134),
(203998, 'zhTW', '我一定會帶著它回來。我向你保證！', '', 38134),
(203997, 'zhTW', '', '$p，你去採集納斯根。亞拉隆，我們還需要女巫背袋。要從佐司特身上偷來相當危險。', 38134),
(203996, 'zhTW', '跟我們的工作人員合作，獲得契約和回函，剩下的就交給我處理！', '', 38134),
(203995, 'zhTW', '我非常熟悉我們的第一個客人，所以完全知道他會喜歡什麼樣的宴會！', '', 38134),
(203993, 'zhTW', '我們應該跟他的泥掘者談談，他似乎會一直燒水。', '', 38134),
(203992, 'zhTW', '我擔心希奧塔會在預演的時候，喝光我們準備的茶。', '', 38134),
(203990, 'zhTW', '葛雷希特會把我們鉤上去。我會在地圖上標示他的地點。', '', 38134),
(203989, 'zhTW', '如果你不確定從什麼地方著手，我的娛樂名單會告訴你該往哪邊去。', '', 38134),
(203988, 'zhTW', '你幫控罪者和她的祭儀師提供任何需要的事物，他們就會協助我們做為報答，懂了嗎？', '', 38134),
(203987, 'zhTW', '沒有人比贖罪之殿的誓復者更擅長舉行儀式。', '', 38134),
(203984, 'zhTW', '冷靜思考，就能確保引渡者只把準備就緒的靈魂帶進奧睿博司。做得好。', '', 38134),
(203983, 'zhTW', '看守者必須判斷一個靈魂是否準備好前往暗影之境。在這場試煉中，好好磨練你的技巧。', '', 38134),
(203981, 'zhTW', '', '你們兩個來得剛剛好！時機算得真是驚險…', 38134),
(203980, 'zhTW', '你在找琦娃嗎？他們剛把她拖走！', '', 38134),
(203979, 'zhTW', '', '我的籠子有三個鎖，分別有自己的鑰匙。總覺得佐司特好像很怕我。', 38134),
(203974, 'zhTW', '搜查林地。佐司特應該不會走太遠。', '', 38134),
(203973, 'zhTW', '琦娃不在這裡…但有打鬥的痕跡。她一定是出了什麼事！', '', 38134),
(203970, 'zhTW', '我不是很喜歡打架啦，但你總不能空著肚子上戰場吧。\n\n我想要個有一堆嘴巴、手臂有力又頭腦聰明的傀儡來幫我試試廚藝。', '', 38134),
(203969, 'zhTW', '', '我想要兩把超巨大的利刃當做手。想像一下，當敵人看到我的雙手時會有多驚嚇！\n\n這一定會很棒的…對吧？', 38134),
(203968, 'zhTW', '', '', 38134),
(203966, 'zhTW', '天選者！敵人朝我們攻過來了！做好戰爭的準備，勝利是我們的！', '', 38134),
(203965, 'zhTW', '同意。考驗一下那個凡人的實力。在那之後，到亡城來找我。我們有場仗要打。', '', 38134),
(203964, 'zhTW', '旅行者，你好。\n\n我在暗影之境尋找古老的聖物。\n\n要一起來嗎？', '旅行者，你好。\n\n我在暗影之境尋找古老的聖物。\n\n要一起來嗎？', 38134),
(203961, 'zhTW', '考古學？那是什麼？\n\n<他聽你解釋考古學的過程和原理。>\n\n真有意思，但我們這裡好像沒有人在研究這種學問。\n\n凡人會因為時間的關係而遺忘過去的事情，但這個地方的運行法則不同。\n\n不過，如果有一天狀況改變，我一定會讓你知道的。', '考古學？那是什麼？\n\n<他聽你解釋考古學的過程和原理。>\n\n真有意思，但我們這裡好像沒有人在研究這種學問。\n\n凡人會因為時間的關係而遺忘過去的事情，但這個地方的運行法則不同。\n\n不過，如果有一天狀況改變，我一定會讓你知道的。', 38134),
(203960, 'zhTW', '', '我們兩個都從他身上學到了很多。我們一起幫他報仇。', 38134),
(203959, 'zhTW', '很高興你對克利瑟斯盡忠職守。他在你為阿卡雷克效力的時候，就一直很欣賞你。', '', 38134),
(203958, 'zhTW', '他教會我使用力量戰勝強敵。維拉斯竟敢背叛他，我要把他的心臟給挖出來。', '', 38134),
(203957, 'zhTW', '', '克利瑟斯對你評價很高呢，灰燼使者。他可不會隨便誇獎別人。', 38134),
(203956, 'zhTW', '', '膽小的弱者才會求生存。沒有勝利…就是死亡。', 38134),
(203955, 'zhTW', '', '瓦許，藩侯託付了新的目標給我，要生存就要拋下所有過去。', 38134),
(203948, 'zhTW', '', '這就是你躲在天選者家族，放任蛛眼家族被屠殺的理由嗎？', 38134),
(203947, 'zhTW', '', '我沒有拋下藩侯阿卡瑞克。我在家族潰敗之前就去執行任務了，就像你一樣。', 38134),
(203944, 'zhTW', '', '哎呀，看看是誰來了？科爾蘇加德的小寵物。', 38134),
(203943, 'zhTW', '不是？真可惜。不過，請讓我送你一份…臨別禮物。', '', 38134),
(203942, 'zhTW', '凡人，你成功逃出來了。你是來慶祝我的勝利嗎？', '', 38134),
(203941, 'zhTW', '或許吧。不過，權力總是有其代價的。', '', 38134),
(203940, 'zhTW', '', '你會為你的詭計付出代價！', 38134),
(203939, 'zhTW', '', '辛丹，你太軟弱了，根本不配成為祭儀家族的一員。是時候選出一位新藩侯了。', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203938, 'zhTW', '', '妮奈妲？你好大的膽子！', 38134),
(203937, 'zhTW', '看來老沃金又能展開一場新的狩獵…但要先休息一下。', '', 38134),
(203936, 'zhTW', '你說我嗎？錯了。', '', 38134),
(203935, 'zhTW', '', '大膽叛徒！你以為你的力量能與藩侯相比嗎？', 38134),
(203934, 'zhTW', '', '我是聽說過妳那虛榮又無情的女王。看來妳跟她有許多相似之處…不論內在或外在都很像。', 38134),
(203933, 'zhTW', '', '艾薩拉女王的野心為我們帶來強大的力量。相較於這股力量，大崩裂只是個小小的代價。', 38134),
(203931, 'zhTW', '', '瓦許，妳似乎很了解破碎的世界，以及污穢的惡魔力量。', 38134),
(203930, 'zhTW', '', '德拉卡，我也親眼看過你的世界。更精確地說，那個支離破碎的世界。', 38134),
(203929, 'zhTW', '', '他展現了毅力與力量。這是艱難時刻所需要的特質。', 38134),
(203928, 'zhTW', '大膽的逃脫！', '', 38134),
(203927, 'zhTW', '如何播種、如何收割。', '', 38134),
(203926, 'zhTW', '可別被壓垮了。', '', 38134),
(203924, 'zhTW', '困在原地！', '', 38134),
(203922, 'zhTW', '幹得好，你把凡人帶離了競技場。不能讓我們的敵人奪走那個凡人。', '', 38134),
(203921, 'zhTW', '不要讓自負蒙蔽了你。專心戰鬥！', '', 38134),
(203920, 'zhTW', '從競技場這一側掉下去可是很高的。', '', 38134),
(203919, 'zhTW', '佐司特的詛咒很快就會控制葛薇亞，讓她繼續待在這裡太冒險了！', '', 38134),
(203918, 'zhTW', '', '淵喉行者，我會向瓦許女爵回報。與你共事是我的榮幸。', 38134),
(203916, 'zhTW', '是真的！原來真的可以從淵喉的無盡地城中安全逃脫。\n\n如果你有機會回去那裡，你可以幫忙找一些藥草回來嗎？我一直都對異國風味很有興趣。', '', 38134),
(203914, 'zhTW', '葛薇亞！她還有呼吸！但…他們對她做了什麼？', '', 38134),
(203911, 'zhTW', '奈拉是我們之中最兇悍的戰士。她絕對不會臣服於佐司特。', '', 38134),
(203910, 'zhTW', '泰拉斯…黑夜會擁你入懷的，老朋友。', '', 38134),
(203909, 'zhTW', '', '傳送門不只這一個，不過我們打了場勝仗。', 38134),
(203908, 'zhTW', '阿玆洛爾…我的恐懼成真了。他不應該落得如此下場…', '', 38134),
(203903, 'zhTW', '整個林地都被附身了！小心別被識破。', '', 38134),
(203902, 'zhTW', '$n，這邊！我需要協助…', '', 38134),
(203901, 'zhTW', '呼嚕嚕…回去…塔莎拉…達納蘇斯…呼嚕嚕…', '', 38134),
(203899, 'zhTW', '我們都有很多事情要學習，烏瑟。來，我們到我的神殿進一步長談。', '', 38134),
(203896, 'zhTW', '我們必須瞭解他的痛苦源自何處。我懷疑還有很多我們不知道的事。', '', 38134),
(203895, 'zhTW', '我希望你可以讓我來照顧烏瑟。', '', 38134),
(203894, 'zhTW', '', '我們控制住亡城了，現在可以開啟通往統御者之座的傳送門。', 38134),
(203893, 'zhTW', '我們的戰鬥需要他的能力。你必須幫助他克服心理障礙。', '', 38134),
(203892, 'zhTW', '佩拉苟斯起初是要召集收集者來協助你，但是他卻陷入對戰鬥的疑惑。', '', 38134),
(203891, 'zhTW', '', '在這種狀態下，她不可能幫助我們。修好禱鐘，穩定她的傷勢！', 38134),
(203889, 'zhTW', '我親自賜予你這股力量。瑪卓薩斯的防衛者！', '', 38134),
(203885, 'zhTW', '', '他們的苦痛如此甜美！', 38134),
(203884, 'zhTW', '', '我要沉浸在他們的苦難中！', 38134),
(203883, 'zhTW', '我是永遠的王！', '', 38134),
(203882, 'zhTW', '你們都會死在我的腳下！', '', 38134),
(203881, 'zhTW', '全都得死！', '', 38134),
(203865, 'zhTW', '', '就是現在！', 38134),
(203864, 'zhTW', '', '要你們好看！只剩下最後一件事了。', 38134),
(203863, 'zhTW', '', '保持警覺！有間諜混入了我們之中！', 38134),
(203858, 'zhTW', '我們的敵人堅持己見，如果話語不能打動他們，那就必須訴諸行動了。這個艱辛的任務就交給你了。', '', 38134),
(203857, 'zhTW', '最後他們會明白自己是錯誤的。希望他們今天學到了寶貴的一課。', '', 38134),
(203852, 'zhTW', '', '你的行動就是我們為整個暗影之境樹立的典範。做得好。', 38134),
(203851, 'zhTW', '', '我們向你求援，凡人。現在需要你貢獻一份力量。你願意嗎？', 38134),
(203848, 'zhTW', '', '一方的感激之情，會轉化為另一方的動力。在你的努力之下，昇靈堡更加強大。', 38134),
(203847, 'zhTW', '', '內梅雅和佩羅迪斯的競爭持續了很久。我很確定他們會需要你幫忙…當然是參與他們的競爭囉。', 38134),
(203846, 'zhTW', '', '你挺身對抗這些來自另一個國度的野獸。或許我們有辦法抵禦牠們。謝謝你。', 38134),
(203845, 'zhTW', '', '凡人，我們需要你的勇氣。這些怪物會吞噬眼前的一切事物。快解決這個危機！', 38134),
(203844, 'zhTW', '', '你就是太軟弱，才無法追隨正確的道路。德沃絲應該把你留在那裡死去！', 38134),
(203843, 'zhTW', '我們應該保護大家，而不是讓他們陷入黑暗！', '', 38134),
(203841, 'zhTW', '萊索妮雅…為什麼…', '', 38134),
(203840, 'zhTW', '', '我現在了解你的職責了。你是我們命運的關鍵。我的命運！', 38134),
(203839, 'zhTW', '我的靈魂有這個地方的印記嗎？', '', 38134),
(203838, 'zhTW', '我只看見黑暗…', '', 38134),
(203837, 'zhTW', '', '不要質疑我。動手。', 38134),
(203836, 'zhTW', '萊索妮雅，我…', '', 38134),
(203835, 'zhTW', '', '我要更多力量！你必須殺了他！', 38134),
(203834, 'zhTW', '遵命。', '', 38134),
(203833, 'zhTW', '', '他還沒有破綻！給我認真一點！', 38134),
(203832, 'zhTW', '', '軍政官，我已經加派巡邏人手了。淵誓者最近的活動…變得十分活躍。', 38134),
(203831, 'zhTW', '', '去吧，我的棄誓者。完成準備工作。嶄新的黎明即將到來。', 38134),
(203830, 'zhTW', '御靈者正努力重建晉升紋章，其他誓盟也付出了力量。', '', 38134),
(203829, 'zhTW', '', '軍事執政官、淵喉行者，我有東西要給你們看看。', 38134),
(203828, 'zhTW', '', '恐怕烏瑟也包括在內。', 38134),
(203827, 'zhTW', '', '我們或許需要他們。看守者說薩尼歐斯的猜疑是對的，是萊索妮雅在領導棄誓者。', 38134),
(203826, 'zhTW', '', '這個全新的紋章會讓晉升者變得無比強大！', 38134),
(203819, 'zhTW', '德沃絲不在了，萊索妮雅一定會接替她的位置，繼續領導棄誓者。希望我們能在他們死灰復燃之前得到和平。', '', 38134),
(203813, 'zhTW', '她的副手萊索妮雅也跟著烏瑟一起追隨她。我們的靈魄貯存越來越少，但他們的勢力越來越大。', '', 38134),
(203812, 'zhTW', '德沃絲偏離身為忠誠化身的道路後，一步步踏上了通往猜疑、暴力的不歸路。', '', 38134),
(203805, 'zhTW', '德沃絲本意是好的…她想矯正既有的錯誤…但烏瑟還沒準備好接受晉升試煉。', '', 38134),
(203799, 'zhTW', '淵喉行者，該讓你看看鮮少人知道的過去了…烏瑟來到昇靈堡的故事。', '', 38134),
(203798, 'zhTW', '這…這裡面…', '', 38134),
(203797, 'zhTW', '御靈者拒絕給予我最後的正義。德沃絲會讓那個怪物得到該有的制裁！', '', 38134),
(203796, 'zhTW', '', '我們會親眼見證她的榮耀幻象成真。琪瑞安！所有琪瑞安，掙脫儀式的束縛吧！', 38134),
(203795, 'zhTW', '', '有了這些裂片，我就能為下次的餘燼宴會準備新的鏡子。', 38134),
(203791, 'zhTW', '你來不及投降了。我們要用你的鮮血替克利瑟斯復仇！', '', 38134),
(203789, 'zhTW', '', '你的背叛決定了你的命運。正義必將伸張！', 38134),
(203788, 'zhTW', '住手！統御者不會容許這些瘋狂行徑！', '', 38134),
(203787, 'zhTW', '你好，凡人。\n\n我是個罪孽作曲家，專門為那些高唱自己死亡事蹟的靈魂編撰能唱進這個神聖大廳可怕深處的曲子。\n\n或許，當你的時刻來臨時，你也會加入我的唱詩班…', '你好，凡人。\n\n我是個罪孽作曲家，專門為那些高唱自己死亡事蹟的靈魂編撰能唱進這個神聖大廳可怕深處的曲子。\n\n或許，當你的時刻來臨時，你也會加入我的唱詩班…', 38134),
(203785, 'zhTW', '看，他們潰不成軍了。總有一天，我們會把佐司特趕出我們的森林！', '', 38134),
(203784, 'zhTW', '佐司特軍隊在測試醒覺林地的防禦設施。讓他們瞧瞧曠野獵者的力量！', '', 38134),
(203781, 'zhTW', '淵喉行者，快攻擊！去擊敗他們的首領。', '', 38134),
(203772, 'zhTW', '', '事情的起因是部落毀掉了卡多雷的家園，泰達希爾。\n\n聯盟無法派遣多餘的士兵協助奪回已成廢墟的泰達希爾，於是我的尊師沒有別的選擇。\n\n她懇求伊露恩賜予她黑夜戰士的力量，化身為伊露恩的怒火。\n\n泰蘭妲的體內如今流淌著伊露恩的怒火，但是這股力量的擁有者只要時間一久就會因它而死。', 38134),
(203771, 'zhTW', '', '你沒聽說嗎？我以為尊師的事情早已傳遍了整個部落。\n\n你的同胞毀掉了卡多雷的家園，泰達希爾。\n\n聯盟無法派遣多餘的士兵協助奪回已成廢墟的泰達希爾，於是我的尊師沒有別的選擇。\n\n她懇求伊露恩賜予她黑夜戰士的力量，化身為伊露恩的怒火。\n\n泰蘭妲的體內如今流淌著伊露恩的怒火，但是這股力量的擁有者只要時間一久就會因它而死。', 38134),
(203770, 'zhTW', '', '我們會用剩餘的靈魄傳送到鑄造廠。到那裡會合。', 38134),
(203765, 'zhTW', '', '你可以在加瑞登勛爵的賓客名單上找到賓客的基本資訊，用這些資訊應該就能找到這些可能會出席的客人。\n\n跟加瑞登勛爵對話之後，你就會收到賓客名單的複本，這樣你就可以隨時查閱名單。\n\n如果在已被發現的賓客之中，有人尚未回覆是否出席，我們也會在你的地圖上標出這些賓客的位置。\n\n只要前往地圖上的標示地點並完成賓客交代的任務，就可以讓他們回覆是否出席。\n\n如果你還沒遇到受邀的賓客，你就沒辦法得到他們的回覆；一般而言，你必須先在他們的國度內探索一番並發現他們之後，才能夠得到他們的回覆。\n\n同樣地，如果你需要先完成某些特定事項才能獲得賓客回覆的話，加瑞登勛爵的賓客名單上會列出詳細的事項。', 38134),
(203764, 'zhTW', '', '如果你需要，我很樂意提供更多資訊。儘管問。', 38134),
(203763, 'zhTW', '', '根據我的紀錄，他通常在贖罪之殿的地穴旁。', 38134),
(203762, 'zhTW', '你方便去看看是什麼事讓他耽擱了嗎？', '', 38134),
(203761, 'zhTW', '我們還沒收到墓穴守衛者凱希爾的聯絡。', '', 38134),
(203758, 'zhTW', '', '做得好！把披肩物歸原位的榮耀就交給你了，我的朋友。', 38134),
(203753, 'zhTW', '等等，這個東西可能有用。你覺得呢，淵喉行者？', '', 38134),
(203752, 'zhTW', '珈爾瑪對力量有強烈的渴望。他應該用來保衛瑪卓薩斯，而不是謀取自己的利益。', '', 38134),
(203751, 'zhTW', '嗯。沒什麼對我們有用的東西。', '', 38134),
(203750, 'zhTW', '來吧，淵喉行者。看看墮落藩侯藏了什麼東西。', '', 38134),
(203749, 'zhTW', '', '同意。我會準備跟淵喉行者一起去那邊一趟。同時，你們看看珈爾瑪這裡還有沒有什麼我們用得到的東西。', 38134),
(203748, 'zhTW', '我們知道珈爾瑪和辛丹是同一夥的。如果他們有瑞文崔斯撐腰，情況會更不利。我們必須著手調查。', '', 38134),
(203747, 'zhTW', '', '這東西和瑪卓薩斯失竊的靈魄不太一樣，這是提供給盟友的物資。', 38134),
(203746, 'zhTW', '靈魄…但並非來自瑪卓薩斯。這個東西蘊藏了汎希爾的巫術。', '', 38134),
(203745, 'zhTW', '', '我搜索這座高塔只是想找珈爾瑪謀反的罪證。沒想到居然會找到這種東西。', 38134),
(203744, 'zhTW', '請帶路吧，女爵。', '', 38134),
(203743, 'zhTW', '', '莫格萊尼！淵喉行者！我在珈爾瑪的城塞裡面找到某些東西，你們最好看看。', 38134),
(203741, 'zhTW', '', '既然名單上的人越來越少，我想我可以花點時間幫你防禦。', 38134),
(203740, 'zhTW', '出來啊，德拉卡！還是你想求我們收留你，就像你拜託天選者一樣？', '出來啊，德拉卡！還是你想求我們收留你，就像你拜託天選者一樣？', 38134),
(203739, 'zhTW', '克利瑟斯太愚蠢了！他違抗了我們，才落得如此下場，你們也是！', '克利瑟斯太愚蠢了！他違抗了我們，才落得如此下場，你們也是！', 38134),
(203738, 'zhTW', '誰能拿到楷模的屍體，我就封他爵位！', '誰能拿到楷模的屍體，我就封他爵位！', 38134),
(203736, 'zhTW', '我們早該血洗昇靈堡了！是統御者阻止了我們！', '我們早該血洗昇靈堡了！是統御者阻止了我們！', 38134),
(203734, 'zhTW', '', '我會找阿德雷特斯討論此事。莫忘職責，淵喉行者。', 38134),
(203733, 'zhTW', '', '我們應該前往其他誓盟的國度，更進一步強化紋章。', 38134),
(203732, 'zhTW', '', '我能感受到紋章的力量正在增長！新的晉升者會是對抗萊索妮雅和烏瑟的關鍵。', 38134),
(203731, 'zhTW', '', '淵喉行者，這份榮耀是屬於你的，謝謝你的貢獻，我們才能打敗珈爾瑪。', 38134),
(203730, 'zhTW', '', '就如大家所料，米卡尼寇斯已經準備好用這個贈禮來強化紋章。', 38134),
(203729, 'zhTW', '', '哦，居然成功了？而且淵喉行者還沒死！太厲害了。嗯…不過最好別再用這種方式離開淵喉了。說不定會害你灰飛煙滅。', 38134),
(203728, 'zhTW', '發動攻擊！', '', 38134),
(203727, 'zhTW', '淵喉行者！等你準備好，我就會削弱那隻野獸！', '', 38134),
(203710, 'zhTW', '', '到樂土堡找我。我們一起去放那顆心臟。', 38134),
(203709, 'zhTW', '', '我…感謝你。交給我吧。', 38134),
(203708, 'zhTW', '這是個艱困的任務，但它蘊藏著他形塑血肉的力量。這股力量會強化你新打造的晉升紋章。', '', 38134),
(203707, 'zhTW', '珊卓亞，我懇請你拿起珈爾瑪的心臟。', '', 38134),
(203706, 'zhTW', '', '再會了。請千萬不要告訴任何人你見過我。要是改天你又跑進來這個暗不見天日的漩渦之中，希望你能帶點好東西來跟我交易。', 38134),
(203705, 'zhTW', '', '這樣子，我們之間的交易就算圓滿結束了。甬道石就在那個方向的不遠處。', 38134),
(203704, 'zhTW', '琪瑞安，你錯把愚蠢當作勇敢了。竟敢擅闖我們的地盤！', '琪瑞安，你錯把愚蠢當作勇敢了。竟敢擅闖我們的地盤！', 38134),
(203703, 'zhTW', '很久以前，膽小鬼科林斯就在餘燼區喪失心智。由於他難以預測，絕對不容小覷。', '', 38134),
(203702, 'zhTW', '', '儘管虛張聲勢吧，現在你該為罪行付出代價了。', 38134),
(203700, 'zhTW', '突蘭提亞克是強大的死靈魔法使用者。她的話語對於死亡有非凡的控制力。', '', 38134),
(203699, 'zhTW', '你們就這點軍力嗎？還不夠我塞牙縫！', '你們就這點軍力嗎？還不夠我塞牙縫！', 38134),
(203698, 'zhTW', '努密努魯的飛靈非常難纏。你得隨時提防他們！', '', 38134),
(203697, 'zhTW', '我們來完成這件事吧。', '', 38134),
(203696, 'zhTW', '', '從他們襲擊神殿的時候，我就嚴陣以待了。', 38134),
(203695, 'zhTW', '珊卓亞！準備好迎接真正的戰鬥了嗎？', '', 38134),
(203694, 'zhTW', '天選者、琪瑞安，聽我號令！現在，我們要發動最後的總攻擊！', '', 38134),
(203693, 'zhTW', '釋放符文刃的力量，一舉摧毀珈爾瑪的重戰士！', '', 38134),
(203692, 'zhTW', '就讓這些惡魔領教一下，天選者家族的真正威力。', '', 38134),
(203691, 'zhTW', '我們琪瑞安大軍會消滅所有敢跟傀儡家族作對的敵人！', '我們琪瑞安大軍會消滅所有敢跟傀儡家族作對的敵人！', 38134),
(203690, 'zhTW', '我們新盟友賦予的力量超乎想像。辛丹的新男爵說得沒錯！', '我們新盟友賦予的力量超乎想像。辛丹的新男爵說得沒錯！', 38134),
(203689, 'zhTW', '昇靈堡只是個開始。所有人都會成為我們的血肉！', '昇靈堡只是個開始。所有人都會成為我們的血肉！', 38134),
(203688, 'zhTW', '真精彩！', '', 38134),
(203687, 'zhTW', '你在晉升之路獲勝了！', '', 38134),
(203686, 'zhTW', '這場戰鬥會流傳千古！', '', 38134),
(203684, 'zhTW', '你的心靈很強大，佩拉苟斯。記住這場勝利！', '', 38134),
(203680, 'zhTW', '智慧試煉，在挑戰中學以致用。', '', 38134),
(203679, 'zhTW', '忠誠試煉，跟隨著前人堅定的腳步。', '', 38134),
(203677, 'zhTW', '謙遜試煉，接受失敗並堅持不懈。', '', 38134),
(203676, 'zhTW', '勇氣試煉，踏上晉升之路的第一步。', '', 38134),
(203674, 'zhTW', '你不是第一個挑戰晉升之路的志士，也不會是最後一個。記住，我們都會支持你。', '', 38134),
(203671, 'zhTW', '', '跟我一起進入托迦司，$p。我們必須動作快點。', 38134),
(203670, 'zhTW', '你真是游刃有餘啊，克蕾雅。願前人的腳步在試煉中引導你。', '', 38134),
(203668, 'zhTW', '奧德林與米伊兒這個雙人組，能用弓箭與魔法形成致命的場地。千萬小心腳下。', '', 38134),
(203667, 'zhTW', '伊契瑟拉是擁有眾多才能的間諜。別小看她對毒物的精通。', '', 38134),
(203666, 'zhTW', '鼓起勇氣再一次面對這場試煉。', '', 38134),
(203665, 'zhTW', '反思挫敗，繼續前行。', '', 38134),
(203664, 'zhTW', '晉升之路上總會學到教訓。', '', 38134),
(203662, 'zhTW', '記取這個教訓，強化所有百衛。', '', 38134),
(203660, 'zhTW', '透過每次試煉，我們更加了解自己，以及我們的使命。晉升之路為你開啟！', '', 38134),
(203659, 'zhTW', '挑戰者會抱得勝利與榮耀歸來，還是在失敗中學得教訓呢？我們拭目以待。', '', 38134),
(203658, 'zhTW', '今日我們齊聚一堂，見證晉升之路的另一場試煉！', '', 38134),
(203657, 'zhTW', '真是盛大的場面啊。這麼多無畏的勇士，讓我感到十分的光榮。', '', 38134),
(203655, 'zhTW', '選擇目的地。', '選擇目的地。', 38134),
(203654, 'zhTW', '德拉卡女爵開始發動攻勢了。我隨時都能帶你去戰場。', '', 38134),
(203653, 'zhTW', '打得漂亮，淵喉行者。接下來換科爾蘇加德了。', '', 38134),
(203652, 'zhTW', '巫妖有屏障的保護！武器是打不穿的！', '', 38134),
(203649, 'zhTW', '那一下讓她粉身碎骨了！', '', 38134),
(203648, 'zhTW', '', '我要…更多的…力量！', 38134),
(203647, 'zhTW', '', '不！這是我應得的力量！是我辛苦爭取來的！', 38134),
(203646, 'zhTW', '', '終於！我應得的所有力量！', 38134),
(203645, 'zhTW', '', '我才不會被你這種人擊敗！', 38134),
(203640, 'zhTW', '如果昇靈堡要克服這場危機，我們必須學著互相合作。在那之前，我們需要你的協助。', '', 38134),
(203639, 'zhTW', '在棄誓者的影響之下，我神殿裡流傳的智慧受到懷疑。我會和侍僧談談，但首先我們必須恢復秩序。', '', 38134),
(203638, 'zhTW', '妮奈妲，你跟這些入侵者玩夠了嗎？快殺了他們！', '', 38134),
(203637, 'zhTW', '', '遵命。我的存在就是為了服侍主人。', 38134),
(203635, 'zhTW', '', '很高興見到你，$p。我有消息要告訴你。', 38134),
(203633, 'zhTW', '', '相信你準備好了，淵喉行者。殺光那些巫妖！', 38134),
(203632, 'zhTW', '好啦。如果你改變心意，需要老羅亞幫忙的話，就來彼界境地找我囉。', '', 38134),
(203631, 'zhTW', '', '退下。', 38134),
(203630, 'zhTW', '當。然。沒問題，陛下。老伯昂撒姆第很樂意把冒著危險搶救的靈魂交給你。作為報酬，我只想要…', '', 38134),
(203626, 'zhTW', '', '亡城充滿了能量。我們該起飛了。', 38134),
(203625, 'zhTW', '等到時機來臨，我們就會找到安杜因…帶他回家。', '', 38134),
(203624, 'zhTW', '那我們更必須耐心等待。淵喉行者，繼續利用各種手段削弱閻獄之主。', '', 38134),
(203623, 'zhTW', '那座塔的一切事物都充滿了閻獄之主的惡意。只要他認為自己的獎品受到威脅，就可能會決定…拋棄它。', '', 38134),
(203622, 'zhTW', '我不知道，還是要小心行事，隨時戒備。', '', 38134),
(203621, 'zhTW', '', '希瓦娜斯只是在玩弄他。她說過要毀了一切。她應該不是認真的…對吧？', 38134),
(203620, 'zhTW', '', '至少他還活著，看似毫髮無傷。不管那個女妖有什麼目的，他都不會投降。', 38134),
(203619, 'zhTW', '我只能…看到這些。', '', 38134),
(203618, 'zhTW', '', '夢境之子啊，請你保護森林之心。不要讓敵人攻入我們的神聖大廳。', 38134),
(203616, 'zhTW', '', '你付出了自己的精華來保全我的靈魂。我也會用盡每一分力量守護亞登曠野。', 38134),
(203612, 'zhTW', '', '我們從空中過去！動作快！', 38134),
(203611, 'zhTW', '別放過任何佐司特！', '', 38134),
(203610, 'zhTW', '那一定是安杜因。', '', 38134),
(203609, 'zhTW', '我聽說有個非常重要的俘虜關在閻獄之塔深處。', '', 38134),
(203608, 'zhTW', '我請求你繼續尋找安杜因，找到你曾經誓言保護的那個男孩。', '', 38134),
(203607, 'zhTW', '', '$n，我欠你一份情。雖然我們曾經互相敵對，但我們現在面對的是共同的敵人。', 38134),
(203606, 'zhTW', '', '好，都到齊了。趁敵人還沒有建立起防線，我們必須趕快行動。', 38134),
(203605, 'zhTW', '', '我們會從東側進攻，他們則會從西側。然後我們一同殺到珈爾瑪面前。', 38134),
(203604, 'zhTW', '', '莫格萊尼的同胞是強大的盟友，而且也很了解我們的敵人。', 38134),
(203603, 'zhTW', '', '楷模大人，我會陪你戰鬥到最後。', 38134),
(203602, 'zhTW', '我們很快就會知道了。', '', 38134),
(203600, 'zhTW', '', '我會遵守約定。結合我們的力量，珈爾瑪必敗無疑。', 38134),
(203590, 'zhTW', '', '丹巴拉似乎也在這裡。對付羅亞可不容易，不過我有好辦法！', 38134),
(203565, 'zhTW', '', '他們恐怕被帶到托迦司去了，就是遠方的那座高塔。閻獄之主把最珍貴的東西都收藏在那裡。', 38134),
(203564, 'zhTW', '告訴我們要怎麼進入那座塔！', '', 38134),
(203563, 'zhTW', '', '啊，你是說這位朋友逃走時丟下的那些凡人。', 38134),
(203562, 'zhTW', '別催我們，仲介者。除了瑪卓薩斯男爵之外，我們還有其他非救不可的人。', '', 38134),
(203553, 'zhTW', '你給我新衣服…', '', 38134),
(203552, 'zhTW', '我的頭髮怎麼了？還是你找到新的海報讓我參考？', '', 38134),
(203548, 'zhTW', '請說，需要我服務嗎？還是你有什麼新東西要給我？', '', 38134),
(203547, 'zhTW', '', '我希望每個人都喜歡有生命力的蘑菇。嘻嘻嘻。', 38134),
(203540, 'zhTW', '', '我看到淵喉行者跟你一起。我相信他們與你結盟的選擇，所以我也願意跟你結盟。', 38134),
(203538, 'zhTW', '話雖這樣，等你幫完凜冬女王的忙，或許可以過來彼界境地一趟。探望一下你的朋友，如何？', '', 38134),
(203537, 'zhTW', '繆薩拉強大無比，充滿惡意，不過完全不用擔心老伯昂撒姆第。我這個老羅亞還是有兩把刷子的！', '', 38134),
(203536, 'zhTW', '看來我得跟老大好好算一下舊帳了…一次算個清楚。', '', 38134),
(203527, 'zhTW', '', '動作快！施放法術！', 38134),
(203513, 'zhTW', '', '什麼事？你沒看到我在讀書嗎。', 38134),
(203512, 'zhTW', '', '藩侯珈爾瑪的統治到今天為止！我們會集結部隊，在澤瑞奎斯做好進攻準備。', 38134),
(203500, 'zhTW', '', '有需要的話，推金可以載你到亡城。', 38134),
(203481, 'zhTW', '', '安杜因擁有強大的聖光，他一定可以撐過去的。', 38134),
(203480, 'zhTW', '', '看到你平安回來真是太好了，索爾。我立刻傳訊給阿格拉和攝政王。', 38134),
(203478, 'zhTW', '榮耀之聲，在我告訴其他人時，請協助他們。我們必須考慮這件事帶來的影響。', '', 38134),
(203477, 'zhTW', '', '大領主，這個凡人帶來了瑪卓薩斯統御者的迫切警告，務必通報其他地方。', 38134),
(203476, 'zhTW', '還不行，卡莉雅。要看見這些畫面是要付出代價的。短期之內，我不敢輕易嘗試。', '', 38134),
(203475, 'zhTW', '', '我沒有聽見安杜因或者貝恩‧血蹄的聲音。大領主，你真的不能再試一次嗎？', 38134),
(203474, 'zhTW', '', '我能認出海軍總帥的聲音，然後另一個肯定是索爾。他們的哀號聲…令我不寒而慄！', 38134),
(203473, 'zhTW', '我只能…看到這麼多。再繼續下去…恐怕會有危險。', '', 38134),
(203467, 'zhTW', '', '上好的蘑菇！這裡有上好的蘑菇。', 38134),
(203450, 'zhTW', '', '萬物都是為了生存而成長。樹木、森林、人們，都需要食物，不過需要的食物各有不同。$b$B跟我分享是一個善舉。如果你跟我分享，那麼我也該有所回報。', 38134),
(203448, 'zhTW', '來自工坊的補給品，交給我去發？喔，我今天真幸運！$b$b既然運氣這麼好，我說不定還能將好運分享出去。來吧，把補給品給我，我就分一點好運給你！', '', 38134),
(203447, 'zhTW', '', '你真的不該留在這裡。閻獄之主不喜歡有人拿走他的東西…即使是他不要的。去吧，該回奧睿博司了。', 38134),
(203443, 'zhTW', '', '我們合力出擊。女爵的計畫會成功的。', 38134),
(203442, 'zhTW', '', '你總算露出真面目了！', 38134),
(203437, 'zhTW', '我擔心在托迦司內部發生什麼事情。恐怖的事情。', '', 38134),
(203436, 'zhTW', '淵喉行者，快回奧睿博司。我感受到…黑暗在呼喚我。', '', 38134),
(203435, 'zhTW', '淵喉行者，繼續努力。探索托迦司的黑暗之地，為暗影之境而戰。等到時機成熟，我們就會取得勝利！', '', 38134),
(203434, 'zhTW', '', '我們會和奧睿博司的盟友合作，集結誓盟。在他們的協助下，我們才有可能阻止閻獄之主…還有救回安杜因。', 38134),
(203433, 'zhTW', '你也見過閻獄之主的大軍。我們還沒準備好要發動全面攻擊。如果貿然行事、全軍出動的話，可能會全盤皆輸。', '', 38134),
(203432, 'zhTW', '我們應該集結兵力，攻擊托迦司！一定要釋放安杜因！', '', 38134),
(203431, 'zhTW', '我要感謝…所有人。珍娜說得對，閻獄之主知道我們看到了什麼，他會利用這點來對付我們。', '', 38134),
(203430, 'zhTW', '', '雖然我們成功擋住了對方的攻勢，但是危機還沒解除。', 38134),
(203429, 'zhTW', '做得好，珍娜，差點就來不及了。', '', 38134),
(203428, 'zhTW', '', '父親！', 38134),
(203427, 'zhTW', '', '撐住，伯瓦爾！再撐一下就好！', 38134),
(203426, 'zhTW', '我會奪走我的東西！而你們死期將近！', '', 38134),
(203425, 'zhTW', '別認輸！我們與你同在！', '', 38134),
(203424, 'zhTW', '', '父親，抵抗他！不要就此認輸！', 38134),
(203423, 'zhTW', '啊啊啊啊！', '', 38134),
(203422, 'zhTW', '你是個敗筆。就跟前任那幾個一樣！', '', 38134),
(203421, 'zhTW', '你本應成為我的先鋒。但你卻忤逆我！', '', 38134),
(203420, 'zhTW', '', '一把…哀傷之刃？噢，不…', 38134),
(203419, 'zhTW', '', '閻獄之主正在入侵伯瓦爾的心智！我們必須切斷他們之間的連結！', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203418, 'zhTW', '啊啊！我…絕對不會…屈服！', '', 38134),
(203417, 'zhTW', '閻獄之主的聲音！這怎麼可能？！', '', 38134),
(203416, 'zhTW', '看見…你了！', '', 38134),
(203415, 'zhTW', '', '伯瓦爾！怎麼了？！', 38134),
(203414, 'zhTW', '切斷連結了。我已經盡力…等等…不太對勁啊…', '', 38134),
(203412, 'zhTW', '再度跟那股黑暗力量連結必須付出代價。不過我別無選擇，我們得知道他們的下落！', '', 38134),
(203411, 'zhTW', '', '我也很擔心安杜因。但是大領主窺探托迦司安全嗎？這樣是否會造成傷害…', 38134),
(203410, 'zhTW', '', '安杜因還被關在那裡！父親，他的情況還好嗎？', 38134),
(203408, 'zhTW', '大領主，怎麼了？你感應到什麼？', '', 38134),
(203407, 'zhTW', '不！不可以！', '', 38134),
(203406, 'zhTW', '惡意…不斷增長…支配…崛起…', '', 38134),
(203403, 'zhTW', '達瑞安！好久不見…', '', 38134),
(203402, 'zhTW', '我認得那個聲音！這是真的嗎？男爵就是…我父親？！', '', 38134),
(203401, 'zhTW', '', '我可以隱藏形跡，而且時間夠你找到那位任性的男爵。跟我來！', 38134),
(203397, 'zhTW', '小心點。閻獄之主的耳目無所不在。', '', 38134),
(203396, 'zhTW', '勇士，路上小心。去找達瑞安，他會協助你尋找那位失蹤的男爵。', '', 38134),
(203395, 'zhTW', '', '但你可要小心了，凡人…雖然你曾經逃出過淵喉，但下一次不見得會這麼幸運。', 38134),
(203394, 'zhTW', '榮耀之聲開口了。我會引導你前往樞運之環，凡人。', '', 38134),
(203363, 'zhTW', '', '絕對不能讓它奪走安杜因的性命。', 38134),
(203362, 'zhTW', '', '這股黑暗的影響對象不只是阿薩斯和伯瓦爾，這柄武器已經奪走了許多生命、許多靈魂。', 38134),
(203361, 'zhTW', '', '在巫妖王現身之前，全艾澤拉斯沒人看過霜之哀傷上頭符文的魔法，可是同樣的力量…構築出淵喉和托迦司的每一個角落。', 38134),
(203360, 'zhTW', '', '我父親的命運也是。那頂頭盔…跟那把劍…一開始就是閻獄之主的手段？', 38134),
(203359, 'zhTW', '', '我兄弟的亡靈就像陰霾一樣籠罩著我們大家。他自己做出了選擇，不過我確實覺得閻獄之主操弄了他的命運。', 38134),
(203358, 'zhTW', '', '泰莉亞，謝謝你。淵喉…是剝奪希望之地。過去我只經歷一次這種空虛感，那就是我在寒冰皇冠面對阿薩斯的時候。', 38134),
(203357, 'zhTW', '', '海軍總帥，看到你沒事真是太好了，我無法想像你和其他人經歷怎樣的恐怖。', 38134),
(203353, 'zhTW', '躲在寒冰後面！', '', 38134),
(203352, 'zhTW', '', '在寒冰屏障後面找掩護！', 38134),
(203347, 'zhTW', '', '回奧睿博司吧。告訴他們，瑪卓薩斯不會再對其他國度發動攻擊了。我們要先整肅內部。', 38134),
(203346, 'zhTW', '', '永恆者…閻獄之主。$n，這個訊息非常危急。', 38134),
(203333, 'zhTW', '五大特質，對應刀刃上的五個符文。就和統御者之座上面的符文一模一樣。', '', 38134),
(203332, 'zhTW', '即使面對必然戰敗的處境…也依舊努力追求勝利。', '', 38134),
(203331, 'zhTW', '足以洞悉敵人的眼界，以及智取敵人的狡詐。', '', 38134),
(203330, 'zhTW', '想要超越敵人的野心，以及能夠粉碎敵人的力量。', '', 38134),
(203322, 'zhTW', '', '我們可以從其他人身上學到好多。', 38134),
(203318, 'zhTW', '如果你無法順利找到材料，可以向達提莉絲請教。每張藍圖都會列出所需材料，材料清單的資訊能幫助你找到收集地點。\n\n請記住，這些材料散落在暗影之境各個區域。你可能得離開昇靈堡才能找到材料！', '', 38134),
(203316, 'zhTW', '', '交給我吧。', 38134),
(203315, 'zhTW', '', '我要閃囉。', 38134),
(203314, 'zhTW', '', '有什麼鬼鬼祟祟的事情要做嗎？', 38134),
(203313, 'zhTW', '', '火球術！', 38134),
(203312, 'zhTW', '', '死靈之箭！', 38134),
(203311, 'zhTW', '', '哈哈哈…砰！', 38134),
(203310, 'zhTW', '', '有空再聯絡。', 38134),
(203308, 'zhTW', '', '看我的魔法！', 38134),
(203298, 'zhTW', '我們真的不能再這樣見面了！', '', 38134),
(203293, 'zhTW', '都上來了！', '', 38134),
(203292, 'zhTW', '我要馬上把我們的冒險記下來！', '', 38134),
(203289, 'zhTW', '太棒了，我的書有新章節了！', '', 38134),
(203285, 'zhTW', '好好享受吧！', '', 38134),
(203284, 'zhTW', '我有留東西給你。', '', 38134),
(203282, 'zhTW', '我聞起來怎麼樣？', '', 38134),
(203279, 'zhTW', '', '在這附近嗎？', 38134),
(203277, 'zhTW', '', '我去別的地方找麻煩！', 38134),
(203271, 'zhTW', '存在只是個人對宇宙的感知。', '存在只是個人對宇宙的感知。', 38134),
(203270, 'zhTW', '這是個，交換靈魂的體驗。', '這是個，交換靈魂的體驗。', 38134),
(203269, 'zhTW', '願你的靈魂平安。', '願你的靈魂平安。', 38134),
(203268, 'zhTW', '我很期待我們未來的冒險旅程。', '我很期待我們未來的冒險旅程。', 38134),
(203267, 'zhTW', '', '我們來大鬧一場！', 38134),
(203266, 'zhTW', '統御者具備瑪卓薩斯士兵該有的五大特質。', '', 38134),
(203265, 'zhTW', '', '你拿到鑰匙後，就到安西德的牢籠跟我們會合。快。', 38134),
(203263, 'zhTW', '關門歇業！', '', 38134),
(203262, 'zhTW', '開張營業啦！', '', 38134),
(203260, 'zhTW', '', '我來幫你治療一下！', 38134),
(203259, 'zhTW', '', '我會讓傷勢好轉！', 38134),
(203258, 'zhTW', '', '你很強壯…不會有事的。', 38134),
(203257, 'zhTW', '', '你是不是受傷傷？', 38134),
(203255, 'zhTW', '', '淵喉行者！安西德被抓走了。我們要先救他才能繼續。', 38134),
(203253, 'zhTW', '深湧回音三叉戟，很適合用來對付法術！\n\n對手會施展法術重創你？讓你傷透腦筋？\n\n再也不會了！投擲長矛、打斷法術！還能造成一點物理傷害，讚。', '', 38134),
(203252, 'zhTW', '你才不是咧！你想害死我們啊？', '', 38134),
(203251, 'zhTW', '耶，我來讓他們看看我的舞步！', '', 38134),
(203250, 'zhTW', '我和棍臉最適合當宴會上的貴族。', '', 38134),
(203249, 'zhTW', '你的心中有勇氣、力量和謀略，足以戰勝你的對手。', '', 38134),
(203248, 'zhTW', '', '$p，能跟你並肩作戰是我的榮幸。', 38134),
(203247, 'zhTW', '看來我要見識一下你的王子有多強悍了。', '', 38134),
(203246, 'zhTW', '珍娜，事情不太對勁。甬道石似乎停止充能了！', '', 38134),
(203245, 'zhTW', '', '藩侯辛丹要我們處理科爾蘇加德的同盟。讓他們付出代價！', 38134),
(203244, 'zhTW', '', '我會在會合地點等妳。希望其他人已經在那裡了…', 38134),
(203233, 'zhTW', '就算你這麼厲害，又有什麼可以買？\n\n收買我沒甜頭吃，除非你是雕銘師…我沒有東西能賣你。唉唷，別難過。', '就算你這麼厲害，又有什麼可以買？\n\n收買我沒甜頭吃，除非你是雕銘師…我沒有東西能賣你。唉唷，別難過。', 38134),
(203231, 'zhTW', '', '我的姊妹們或許沒有顯露出來，但能夠再次飛翔，我們都十分感激，畢竟這是我們的渴望。', 38134),
(203229, 'zhTW', '', '你在找什麼？', 38134),
(203226, 'zhTW', '', '他們…知道我們會來。我擔心卡莉克和安西德可能遇到了危險。', 38134),
(203225, 'zhTW', '', '這邊！快！', 38134),
(203224, 'zhTW', '', '小心！', 38134),
(203223, 'zhTW', '', '小心行事…目前沒人發現我們。', 38134),
(203222, 'zhTW', '別擔心。合作之下，我們會想出如何舉辦這場宴會。派對使者！', '', 38134),
(203221, 'zhTW', '', '$p，跟緊一點，我們不能暴露行蹤。', 38134),
(203205, 'zhTW', '我…我會的。妮亞，謝謝你加以指引。', '', 38134),
(203204, 'zhTW', '', '好好照顧，別看外表這樣，其中蘊含的價值可不只如此。', 38134),
(203203, 'zhTW', '', '雷納瑟，你握著全宇宙的可能性。', 38134),
(203202, 'zhTW', '說不定很美味。', '', 38134),
(203201, 'zhTW', '在小小的外殼裡，潛藏著什麼樣的未來？', '', 38134),
(203200, 'zhTW', '雷納瑟王子雙手捧著一枚種子。', '', 38134),
(203199, 'zhTW', '一點點絕對不夠用。', '', 38134),
(203198, 'zhTW', '多謝你，瘟疫設計者，但我相信這一點就夠我用了。', '', 38134),
(203197, 'zhTW', '我調合出了最完美的樣品。宴會結束以後到塔後面找我，我把東西交給你。', '', 38134),
(203196, 'zhTW', '你在找惡作劇黏液嗎？', '', 38134),
(203193, 'zhTW', '謝謝你。這招惡作劇會讓希奧塔大吃一驚…', '', 38134),
(203192, 'zhTW', '雷納瑟王子把那隻泥漿怪收進他外套的其中一個口袋。', '', 38134),
(203191, 'zhTW', '', '謠言總是傳來傳去，僅此而已。', 38134),
(203190, 'zhTW', '', '雷納瑟王子，沒必要將事情複雜化。', 38134),
(203189, 'zhTW', '謠言就像是泥掘者水池一樣，迅速傳播，讓每個人都灰頭土臉。', '', 38134),
(203188, 'zhTW', '雷納瑟王子毫不猶豫地撕碎那張紙。', '', 38134),
(203184, 'zhTW', '', '這些百衛儲存著知識，必須不惜一切去妥善保存。感謝你的服務。', 38134),
(203183, 'zhTW', '', '首席熔爐士通知我們，我們有個助憶士失聯。希望你能幫她返回工作崗位。', 38134),
(203182, 'zhTW', '', '新兵，在你和我朋友決鬥之後你就可以退下了。表現好一點，有需要的時候會再叫你。', 38134),
(203181, 'zhTW', '', '那些海報效果很好。我們已經招到新兵了。', 38134),
(203173, 'zhTW', '給我滾！我忍耐你在這裡夠久了。', '給我滾！我忍耐你在這裡夠久了。', 38134),
(203172, 'zhTW', '快點行動。', '快點行動。', 38134),
(203152, 'zhTW', '這應該很有看頭！我希望在今天的測驗看見真的有人死亡。', '這應該很有看頭！我希望在今天的測驗看見真的有人死亡。', 38134),
(203150, 'zhTW', '噢，我希望我能在測驗中和暗脈家族的成員比一場。我要提出一些尚未解決的事情！', '噢，我希望我能在測驗中和暗脈家族的成員比一場。我要提出一些尚未解決的事情！', 38134),
(203149, 'zhTW', '沒人比我強。我在細劍上的技巧幾近傳說。', '沒人比我強。我在細劍上的技巧幾近傳說。', 38134),
(203148, 'zhTW', '等我們動手的那一刻，就要讓酸酒家族為他們的攻擊付出代價！', '等我們動手的那一刻，就要讓酸酒家族為他們的攻擊付出代價！', 38134),
(203147, 'zhTW', '', '我建議暫時撤退。我會帶大家去一個地方，就在上次幫助你逃脫的那個甬道石附近。', 38134),
(203146, 'zhTW', '我追尋榮耀是為了我的家族，不是為了自己。', '我追尋榮耀是為了我的家族，不是為了自己。', 38134),
(203145, 'zhTW', '等等。我的劍…天命使者。我很快就用得上了。', '', 38134),
(203143, 'zhTW', '父親，你先別急。我們晚點可以慢慢聊。', '', 38134),
(203142, 'zhTW', '精英部隊的測驗是個暗算櫻草家族笨蛋的絕佳良機。算我一份。', '精英部隊的測驗是個暗算櫻草家族笨蛋的絕佳良機。算我一份。', 38134),
(203141, 'zhTW', '兒子啊…', '', 38134),
(203137, 'zhTW', '我稱之為免費娛樂。', '', 38134),
(203136, 'zhTW', '不用怕，佳賓斯。我們會從這個殘忍的騙子手中保護你！', '', 38134),
(203133, 'zhTW', '不是朋友。煙火嚇到佳賓斯了。', '', 38134),
(203131, 'zhTW', '叫他滾蛋而且不要聽信他吐出來的每一個字！', '', 38134),
(203130, 'zhTW', '當然不會！絕對不容許任何八卦把我和這種惡棍扯在一塊！', '', 38134),
(203129, 'zhTW', '', '凡人，你的靈魄氣息，暴露了你在墮滅堡的行蹤。趕快離開！', 38134),
(203128, 'zhTW', '', '你還沒做好進入獸穴的準備，凡人。快回來！', 38134),
(203126, 'zhTW', '在它毀掉我們美妙的裝飾之前，把那團髒亂丟出去！', '', 38134),
(203125, 'zhTW', '不錯嘛、不錯嘛。', '', 38134),
(203124, 'zhTW', '', '天命使者，是嗎？那是他得到男爵頭銜時我為他打造的。他想要一把和生前使用的東西類似的武器。從你的反應來看，我猜你大概認得這東西的形狀。如果我和一把劍共享一個稱號的話，我一定會對它愛不釋手。\n\n這是我最好的作品之一。亞歷山卓斯花了很多工夫才取得了這把劍的某些材料。我不太確定這個頭顱是哪來的，但一定不是瑪卓薩斯。', 38134),
(203122, 'zhTW', '', '很高興再次見到你，我的朋友。讓人興奮不已，不是嗎？', 38134),
(203120, 'zhTW', '', '淵喉行者，謝謝你。這些靈魂會獲得良好照料。', 38134),
(203119, 'zhTW', '', '懼獵者的希望破滅者任由這些靈魂腐爛。請幫幫忙。', 38134),
(203112, 'zhTW', '', '你好，$c。我為老練的旅行者提供各種方式，協助他們在路上會面。', 38134),
(203109, 'zhTW', '', '奇怪…他聽起來跟我記憶中的勾拉札爾相去甚遠…', 38134),
(203108, 'zhTW', '', '蠢貨！在勾拉札爾面前，你只不過是螻蟻！', 38134),
(203107, 'zhTW', '我帶甜食給餘燼宴會的賓客享用！', '', 38134),
(203106, 'zhTW', '你好，$c。我為老練的旅行者提供各種方式，協助他們在路上會面。', '你好，$c。我為老練的旅行者提供各種方式，協助他們在路上會面。', 38134),
(203100, 'zhTW', '', '全部都就緒了。準備要離開的時候就跟推金說一聲。', 38134),
(203098, 'zhTW', '來點爆炸應該會有用！', '', 38134),
(203097, 'zhTW', '這是受人尊敬的宴會，不是歿路沼澤！', '', 38134),
(203096, 'zhTW', '燈籠可以，火炬不行。', '', 38134),
(203095, 'zhTW', '', '趁安西德引開傀儡，我們潛入亡城。', 38134),
(203094, 'zhTW', '', '在推金載你飛過來的路上，好好偵察這個區域。', 38134),
(203084, 'zhTW', '十分感謝你，淵喉行者。', '', 38134),
(203083, 'zhTW', '', '玩得愉快。', 38134),
(203082, 'zhTW', '', '控罪者嘻嘻作笑。', 38134),
(203081, 'zhTW', '你可以信賴我，淵喉行者！我會全力以赴！', '', 38134),
(203080, 'zhTW', '派對使者前來效力！', '', 38134),
(203079, 'zhTW', '特梅爾看來迷惑了一陣子，然後從腰間飛快拉出派對帽從頭上一戴。', '', 38134),
(203078, 'zhTW', '', '你在此地不需要以罪孽使者的身份效力。你將協助淵喉行者，安排一場全新宴會。', 38134),
(203077, 'zhTW', '唯一的罪孽使者，控罪者，任你差遣！', '', 38134),
(203076, 'zhTW', '', '特梅爾！', 38134),
(203075, 'zhTW', '', '不過，如果王子需要有人完成這些工作…', 38134),
(203074, 'zhTW', '', '我做不出這種事。這個不安的靈魂需要我全神貫注。', 38134),
(203073, 'zhTW', '我等不及把這個消息告訴蕾瑞女士了。', '我等不及把這個消息告訴蕾瑞女士了。', 38134),
(203071, 'zhTW', '啊，淵喉行者。我們剛好在談論你呢。\n\n是什麼風把你給吹來了？', '', 38134),
(203070, 'zhTW', '噢噢！這會變成瑞文崔斯上最熱門的宴會！', '噢噢！這會變成瑞文崔斯上最熱門的宴會！', 38134),
(203069, 'zhTW', '而且，當然了，我會確保你們都在賓客名單上。', '', 38134),
(203068, 'zhTW', '我的廣大連絡網和謙和的外交技巧供你差使。', '', 38134),
(203067, 'zhTW', '協助你和雷納瑟王子是無上榮幸。', '', 38134),
(203066, 'zhTW', '那個尖牙鬼看得出所有人的天份。交給我吧。', '', 38134),
(203065, 'zhTW', '一定是希奧塔告訴你的，對嗎？', '', 38134),
(203064, 'zhTW', '我吃驚的是，你把這樣的工作交給一個泥掘者。', '', 38134),
(203062, 'zhTW', '你可曾聽過失落聖杯樂團演奏音樂？\n\n感人肺腑。相當震懾人心的旋律。\n\n我開始閱讀他們的自傳。總算有一本內容值得我去學習的書了！', '', 38134),
(203061, 'zhTW', '有空談談嗎？', '', 38134),
(203060, 'zhTW', '我會擔任我們最嚴苛的評論家和最強大的支持者！', '', 38134),
(203059, 'zhTW', '新鮮的肉！舶來品！只用最乾淨的泥掘者服務生！', '', 38134),
(203058, 'zhTW', '當然了，只能用上好的材料…', '', 38134),
(203056, 'zhTW', '<原本正看著身旁那個飄浮卷軸的挑剔的史帝芬轉頭看向你。>\n\n有事嗎？你對於這個牛排的迷人香氣也有意見要發表嗎？', '', 38134),
(203055, 'zhTW', '首先，我們需要更多茶。所有的茶！', '', 38134),
(203054, 'zhTW', '我在這方面的經驗僅限於排練。我會把你需要知道的都告訴你！', '', 38134),
(203053, 'zhTW', '但是，我也覺得很開心。不過，我會仰仗你來提醒我在宮廷禮儀的知識。', '', 38134),
(203052, 'zhTW', '我相信大多數謠傳都是你在起頭，希奧塔。', '', 38134),
(203051, 'zhTW', '我親愛的雷納瑟，謠傳是真的。我們會在罪殞城這邊舉辦宴會！', '', 38134),
(203050, 'zhTW', '我興奮到臉都紅了。這個消息不逕而走！', '', 38134),
(203049, 'zhTW', '', '我們會依你的命令，去餘燼宴會報到。', 38134),
(203047, 'zhTW', '', '還有很多工作要做。', 38134),
(203038, 'zhTW', '過來，我給你機會，讓你解釋為什麼要闖入我的巢穴。', '過來，我給你機會，讓你解釋為什麼要闖入我的巢穴。', 38134),
(203037, 'zhTW', '等等…先別攻擊！我認得這個人。', '等等…先別攻擊！我認得這個人。', 38134),
(203036, 'zhTW', '誰膽敢深入我的地盤？', '誰膽敢深入我的地盤？', 38134),
(203035, 'zhTW', '你太過份了！', '你太過份了！', 38134),
(203029, 'zhTW', '', '被困住了。嗯哼…繼續啊！凡人，讓我們看看你的神力。', 38134),
(203028, 'zhTW', '誰…在那裡？我要…警告藩侯…', '', 38134),
(203027, 'zhTW', '', '比你還久，凡人。知道這些就夠了。', 38134),
(203026, 'zhTW', '你被困在這裡多久了？', '', 38134),
(203022, 'zhTW', '', '乾旱和紛爭讓林地元氣大傷。若林地能再次繁盛，裡頭的靈魂也會。', 38134),
(203015, 'zhTW', '即使萊索妮雅已被弱化，要靠你我打敗這個可怕勁敵，我還是沒有把握。\n\n無論如何，我們還是必須試一試。你願意幫我嗎？', '', 38134),
(203014, 'zhTW', '這麼多人犧牲…楷模卻只注意到她的左右手。\n\n我們必須行動，凡人。', '', 38134),
(203009, 'zhTW', '我們了解這麼做的風險。我們沒有別的選擇。', '', 38134),
(203008, 'zhTW', '', '通往那個黑暗領域的通道只有一條。所有註定遭受磨難的靈魂，都得經過那條路。', 38134),
(203007, 'zhTW', '榮耀之聲，我們想找出前往淵喉的道路。', '', 38134),
(203006, 'zhTW', '雖然我不知道該怎麼找到這位瑪卓薩斯男爵，或者我們還活著的盟友。不過，我應該能幫你找到達瑞安‧莫格萊尼。至少是個開始。', '', 38134),
(203005, 'zhTW', '看來你必須再次勇闖淵喉了，$n。', '', 38134),
(202998, 'zhTW', '我這輩子參加過很多次宴會。\n\n沒有一次是自願的。', '', 38134),
(202997, 'zhTW', '天下沒有什麼比跟朋友一起在宴會上品嚐好茶更迷人的事了。\n\n不知道要做什麼的時候，就再倒點茶。那一切問題就都解決了！', '', 38134),
(202996, 'zhTW', '我不像希奧塔那麼愛參加宴會，也不像特梅爾那麼享受舉辦宴會。\n\n但是我對自己很有信心。自從這個界域創生以來，我便不斷在結交盟友。\n\n餘燼宴會是個強大的手段，能夠提高我們誓盟的名望與影響力。\n\n很高興有你陪在身邊，一起開創偉大的事業。', '', 38134),
(202995, 'zhTW', '為了科學研究，狂人摩塔莫爾不惜把自己一分為三。這段回憶會對我們的訓練很有幫助！', '', 38134),
(202991, 'zhTW', '', '我會到努拉齊爾回報。那邊見。', 38134),
(202990, 'zhTW', '啊！淵喉行者…結束這一切！', '', 38134),
(202989, 'zhTW', '你在找什麼嗎？', '', 38134),
(202988, 'zhTW', '喂！你在看什麼？', '', 38134),
(202987, 'zhTW', '什麼風把你吹來諾門的門前？', '', 38134),
(202985, 'zhTW', '', '我們的工匠都很努力。我可以向你保證餘燼宴會表現得燦爛輝煌！', 38134),
(202984, 'zhTW', '餘燼宴會正式開始！', '', 38134),
(202983, 'zhTW', '每個地方都一塵不染！地面馬上就會準備好！', '', 38134),
(202982, 'zhTW', '在第一場正式宴會之前，我們還有很多清掃工作待辦！', '', 38134),
(202981, 'zhTW', '嗯，這並不像我習慣的宴會，但我想如果大家都開心的話…', '', 38134),
(202980, 'zhTW', '如此神奇、正式的宴會！接下來一定要請佳賓斯與塔賓斯光臨！', '', 38134),
(202979, 'zhTW', '我的職責已盡。', '', 38134),
(202978, 'zhTW', '你達成了不可能的任務，淵喉行者：你舉行的宴會完全讓我樂在其中。太精采了。', '', 38134),
(202977, 'zhTW', '當然了，特梅爾。開始安排邀請函，送往我們第一批外來賓客。', '', 38134),
(202976, 'zhTW', '我會將餘燼宴會的儀式所需的一切都備好。', '', 38134),
(202975, 'zhTW', '泥頸一邊碎唸，一邊推著手推車離開。', '', 38134),
(202974, 'zhTW', '泥頸，那就是我不邀請你的理由！別停下來！', '', 38134),
(202973, 'zhTW', '我覺得越混亂越好！', '', 38134),
(202972, 'zhTW', '沒錯，沒錯，不過走之前一定要弄乾淨。泥掘者又製造出新的混亂！', '', 38134),
(202971, 'zhTW', '我會在幾分鐘內請你們所有人登上這個放著禮物的舞台，舉行閉幕儀式。', '', 38134),
(202970, 'zhTW', '別再說教了，特梅爾。讓淵喉行者享受這次預演吧！', '', 38134),
(202969, 'zhTW', '如果出現直接衝突，像是希奧塔和守衛隊長波羅莫的情況，你就必須選出想集中火力取悅的賓客。', '', 38134),
(202968, 'zhTW', '讓某個賓客開心的活動，可能會讓另一個賓客討厭。', '', 38134),
(202967, 'zhTW', '淵喉行者，重點是，每個人喜歡的宴會類型都不一樣。', '', 38134),
(202966, 'zhTW', '我親愛的守衛隊長，我必須說你錯得太離譜了。我參加過全瑞文崔斯最完美的宴會，而且…', '', 38134),
(202965, 'zhTW', '就是要這樣才有意思！宴會就是應該少喝點茶，多點打鬥！', '', 38134),
(202934, 'zhTW', '', '我很樂意賜予你祝福。跟我來，好嗎？', 38134),
(202933, 'zhTW', '', '$p，我在此將女王的祝福，延伸給你。起身吧。', 38134),
(202932, 'zhTW', '', '「我賜予你祝福，讓你化為野生動物。任何你要的形體，都能為你所有。」', 38134),
(202931, 'zhTW', '', '「女王陛下，我該怎麼做？」靈魂問道。「我的軀體已不復存在。」', 38134),
(202930, 'zhTW', '', '「永遠照護這裡的靈魂。循環絕不能中斷，否則一切將崩毀。」', 38134),
(202929, 'zhTW', '', '靈魂單膝下跪。「女王陛下，我該如何為你效勞？」', 38134),
(202928, 'zhTW', '', '「那麼，歡迎你來到我的國度。」女王回應道。', 38134),
(202927, 'zhTW', '', '「我被派來此地！是因為我生前，全心全意為自然奉獻。」', 38134),
(202926, 'zhTW', '', '女王陛下在通道口會見他。她說：「你不是神靈也不是妖精。你為何來到我的森林？」', 38134),
(202925, 'zhTW', '', '很久以前，在樹木和暗夜妖精出現之後，第一個凡人的靈魂來到了亞登曠野。', 38134),
(202923, 'zhTW', '繼續和達提莉絲一起製作全新物品。也許你可以捕捉更多記憶、或者嘗試新裝備！\n\n如果你從未打敗特定記憶或某個難度的首領，那就努力克服試煉、獲取獎勵。製造並好好利用符咒，給靈魂之絆額外的力量。\n\n最後，繼續挑戰下一個難關。附近許多商人都會提供超讚獎勵，只有克服晉升之路的挑戰，才能獲取這些獎勵。全部收集吧！', '', 38134),
(202921, 'zhTW', '有些靈魂之絆也許會對特定記憶具有特別優勢。你可以選擇其他靈魂之絆進行戰鬥。\n\n試試看幫靈魂之絆添增裝備與符咒。裝備會讓靈魂之絆獲得主動技能，符咒會讓靈魂之絆獲得被動強化效果。兩者都能扭轉戰局。如果你想學習，我可以告訴你更多細節！\n\n最後，當你藉由我們誓盟提高名望等級，你的靈魂之絆也會獲得強化。如果你對抗記憶時陷入苦戰，可以在下次戰鬥前花些時間提高名望等級、強化靈魂之絆。', '', 38134),
(202919, 'zhTW', '', '淵喉行者，我們能成為強大的盟友。但不是朋友。我可沒有什麼「朋友」。', 38134),
(202914, 'zhTW', '裝備讓你獲得主動技能，符咒則是會被動強化你的靈魂之絆。符咒只能使用一次，能量就會耗盡，所以請務必製作多份符咒！\n\n雖然不同類別的符咒同時間只能裝備一種，但是符咒種類不限。你可以盡情混合搭配。', '', 38134),
(202912, 'zhTW', '有時候你可能會遇到靈魂之絆還無法應付的挑戰，這時候裝備就能派上用場。裝備可以讓你的靈魂之絆在戰鬥中獲得額外主動技能，例如突進。\n\n裝備製作後就可以永久使用。只要在大競技場與帕歐隆交談，就能為靈魂之絆穿戴裝備。你一次只能使用一個裝備，所以請務必選擇能幫助靈魂之絆解決當前問題的最佳裝備。', '', 38134),
(202910, 'zhTW', '雖然晉升之路讓人備感榮耀，但我們必須以謙遜之舉調適自我心態。功績勳章既可證明你的貢獻，也能用來挑戰晉升之路。\n\n你若在昇靈堡善盡職責，便可獲得這些勳章。收集勳章的最佳方式就是參加使命任務、尋寶任務、殺死稀有敵人。當然，如果你需要更多勳章，隨時都能透過達提莉絲捐獻材料並獲得勳章。', '', 38134),
(202909, 'zhTW', '無須多說，$g先生:女士;。有可以效勞的地方嗎？', '無須多說，$g先生:女士;。有可以效勞的地方嗎？', 38134),
(202908, 'zhTW', '這是我的第一個宴會任務。我會盡我所能。', '這是我的第一個宴會任務。我會盡我所能。', 38134),
(202906, 'zhTW', '你覺得會有食物剩下嗎？', '你覺得會有食物剩下嗎？', 38134),
(202905, 'zhTW', '在大競技場打敗記憶，就能獲得新藍圖。等你把藍圖帶回來給達提莉絲，你就能製作新誘餌、符咒和裝備。收集所有藍圖，嘗試各種選擇。', '', 38134),
(202903, 'zhTW', '我隨時聽令。', '我隨時聽令。', 38134),
(202902, 'zhTW', '挑戰高難度記憶的第一步：強化靈魂之鏡，引出更強記憶。強化方法就是升級誓盟聖所。\n\n第二步：有些試煉會更加困難，你打敗的首領必須達一定數量，才能進行更難的挑戰。\n\n當然，如果你還沒打敗難度較低的記憶，就不能挑戰難度較高的記憶。', '', 38134),
(202900, 'zhTW', '等你需要捕捉新記憶，達提莉絲會通知你。為了每個記憶，你必須製作專屬的誘餌和靈魂之鏡，他也會協助你製作。一旦你把這些交給達提莉絲，她會給你進一步的指示，教你如何將記憶帶回大競技場，跟你的靈魂之絆訓練。', '', 38134),
(202899, 'zhTW', '任何潛在的麻煩人物都必須經過我這關。', '任何潛在的麻煩人物都必須經過我這關。', 38134),
(202897, 'zhTW', '聽候你的指揮。', '聽候你的指揮。', 38134),
(202896, 'zhTW', '我能為你效勞嗎？', '我能為你效勞嗎？', 38134),
(202895, 'zhTW', '為了罪殞城。', '為了罪殞城。', 38134),
(202893, 'zhTW', '', '其他的野生動物正在集合。在你呼叫我們參加餘燼宴會時，我們會準備萬全。', 38134),
(202889, 'zhTW', '你會流傳千古！', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202888, 'zhTW', '記住你所學的一切。', '', 38134),
(202883, 'zhTW', '吞噬者飢腸轆轆、兇殘無比，而且來者不拒。你得在這個挑戰中想辦法保護自己。', '', 38134),
(202881, 'zhTW', '該來徵求另一名挑戰者了，下個上場的會是誰呢？', '', 38134),
(202880, 'zhTW', '另一名琪瑞安加入了這項神聖的傳統，英勇克服晉升之路的考驗吧！', '', 38134),
(202868, 'zhTW', '不是每個人的喜好都和你一樣，希奧塔。我們需要為宴會的每一位來賓量身訂製。', '', 38134),
(202867, 'zhTW', '在這種正式活動中，幾場精彩戰鬥可以改善氣氛。', '', 38134),
(202866, 'zhTW', '如果你想讓我心情好點，去找我的新兵決鬥一場。', '', 38134),
(202864, 'zhTW', '呸！我對宴會的愚蠢形式沒興趣。', '', 38134),
(202863, 'zhTW', '我不知道，希奧塔…波羅莫不像是個重視禮節的人…', '', 38134),
(202862, 'zhTW', '先從我們情緒低落的守衛隊長開始吧。當然，能夠納入宴會禮節的一環，他一定會很感激。', '', 38134),
(202861, 'zhTW', '每個人都應該拿到一杯茶！', '', 38134),
(202859, 'zhTW', '', '佩拉苟斯再次成功克服難關！', 38134),
(202858, 'zhTW', '', '這次我可親眼見識到克蕾雅為何能贏得雙翼。', 38134),
(202856, 'zhTW', '', '打得不錯！', 38134),
(202854, 'zhTW', '淵喉行者，謝謝你，無論在哪裡舉辦宴會，都有必要維持一定的禮節。', '', 38134),
(202853, 'zhTW', '', '看他們倒下的樣子！', 38134),
(202852, 'zhTW', '', '面對逆境，挺身奮戰。', 38134),
(202851, 'zhTW', '', '佩拉苟斯，給你一個的小小建議：別讓對手靠近你。', 38134),
(202850, 'zhTW', '', '我很期待見識布隆的戰鬥技巧，不知道我有沒有機會和他比試一下？', 38134),
(202849, 'zhTW', '', '克蕾雅，冷靜面對這項挑戰！', 38134),
(202848, 'zhTW', '', '啊，那可是我最喜歡的亞薩諾斯，希望你不會跟上一位挑戰者一樣！', 38134),
(202846, 'zhTW', '', '大家都知道巫妖很難對付，有人覺得是因為骨匣的關係…不過我很清楚巫妖有多恐怖。', 38134),
(202845, 'zhTW', '', '佐司特是亞登曠野的毒瘤，雖然眼前只有一個，可是千萬別小看。', 38134),
(202844, 'zhTW', '', '一個嫌太少，兩個不算多，三個恰恰好，希望這次挑戰者不會被打得落荒而逃。', 38134),
(202843, 'zhTW', '', '木靈看似溫和，不過他們和大自然之間的連結，會讓他們變成可怕的對手。', 38134),
(202841, 'zhTW', '', '憎恨夫人登場…我可不太想知道她是如何獲得這個稱號的。', 38134),
(202840, 'zhTW', '', '在晉升之路上可不是每天都能看到吞噬者，這非看不可！', 38134),
(202839, 'zhTW', '', '不，他們動不了了！', 38134),
(202838, 'zhTW', '', '快點掙脫！', 38134),
(202837, 'zhTW', '', '挑戰者的狀態看起來不太妙。', 38134),
(202836, 'zhTW', '', '當心腳下啊！', 38134),
(202835, 'zhTW', '', '可沒人說不能用毒喔！', 38134),
(202834, 'zhTW', '', '看來有人被吸過去了！', 38134),
(202832, 'zhTW', '', '看來他們需要喘口氣！', 38134),
(202831, 'zhTW', '', '要攔阻這名戰鬥者可不容易！', 38134),
(202830, 'zhTW', '', '再加把勁！就差一點點！', 38134),
(202828, 'zhTW', '', '看來你該幫巨人全身上下維修一番了。', 38134),
(202822, 'zhTW', '', '每次失敗，都會讓勝利更加甜美！', 38134),
(202821, 'zhTW', '', '啊，謙遜的教訓。', 38134),
(202820, 'zhTW', '', '振作起來，再挑戰一次。', 38134),
(202817, 'zhTW', '', '我期待好久好久了，再次進行試煉吧！', 38134),
(202816, 'zhTW', '', '接受另一次試煉！', 38134),
(202815, 'zhTW', '我再準備另一壺，說不定用得上。', '', 38134),
(202814, 'zhTW', '趁茶還熱的時候奉上。', '', 38134),
(202813, 'zhTW', '', '精采的表現！從我第一眼看到你，就知道你必將成就不凡。', 38134),
(202812, 'zhTW', '', '志士夥伴們，見證$p挺身對抗強大的亞薩諾斯！', 38134),
(202811, 'zhTW', '', '這把長矛沉重無比，你只能舉起來一下。你得抓好時機！', 38134),
(202810, 'zhTW', '', '卡莉賽恩訓練志士的資歷非常久遠，我們來看看你有多少實力吧。', 38134),
(202809, 'zhTW', '你進步了，$p。我的記憶歸你了。', '', 38134),
(202808, 'zhTW', '', '看來你還有很多要學。', 38134),
(202807, 'zhTW', '我親愛的特梅爾，你怎麼知道那個「最昂貴」是我喜歡的香味？', '', 38134),
(202806, 'zhTW', '淵喉行者，請為希奧塔送上一杯我們最昂貴的茶。', '', 38134),
(202804, 'zhTW', '你覺得如何？我們準備好開放外人參加餘燼宴會了嗎？', '', 38134),
(202803, 'zhTW', '你想要正式？我們就正式！', '', 38134),
(202802, 'zhTW', '不過，我必須承認，那些最有影響力的人比較習慣更…正式的場合。', '', 38134),
(202801, 'zhTW', '是的，這只是開始而已，我親愛的王子。', '', 38134),
(202800, 'zhTW', '希奧塔，你一向很喜歡這種歡樂的社交場合。', '', 38134),
(202799, 'zhTW', '我隨時為你效勞。', '', 38134),
(202798, 'zhTW', '雷納瑟王子朝守衛隊長波羅莫眨眨眼。', '', 38134),
(202797, 'zhTW', '那些名人和貴族似乎都玩得很開心，不過我們自己的守衛隊長不怎麼開心就是了。', '', 38134),
(202796, 'zhTW', '我很佩服，特梅爾。你和淵喉行者已經做了大幅改善！', '', 38134),
(202795, 'zhTW', '雷納瑟王子帶著我們的貴賓過來了！', '', 38134),
(202794, 'zhTW', '太好了，接下來，也把酵素桶接上去。', '', 38134),
(202793, 'zhTW', '', '牠們真是美麗，對不對？而且牠們自己也知道！散發一股傲氣。', 38134),
(202792, 'zhTW', '', '很久以前，我們汲取它的魔法，用來確保沒有東西能逃出淵喉。確保監禁者永世不得脫身。', 38134),
(202790, 'zhTW', '為了雷納瑟王子，我已經動員每一個泥掘者讓這個地方完美無缺。', '', 38134),
(202789, 'zhTW', '我需要你幫忙主導餘燼宴會的排練活動。', '', 38134),
(202786, 'zhTW', '我的小星星，我會…答應你的請求。', '', 38134),
(202785, 'zhTW', '', '但是現在我要請你告訴我，他們是否還活著。', 38134),
(202784, 'zhTW', '', '我從小時候，從你把我送走的那時候，就從沒要求過任何事。', 38134),
(202783, 'zhTW', '', '我長途跋涉來到死亡領域，為了見我以為再也見不到的父親，艾澤拉斯最偉大的英雄，伯瓦爾‧弗塔根。', 38134),
(202782, 'zhTW', '泰莉亞，你不瞭解這麼做的風險。要是我窺探淵喉…', '', 38134),
(202781, 'zhTW', '', '父親，拜託。我得知道那些朋友是否還有希望！', 38134),
(202780, 'zhTW', '我要展開這一趟全新人生之旅！還是好幾趟？先一趟就好了。總之，再會了！', '', 38134),
(202779, 'zhTW', '祝你在競技場一切順利，不管你要幹什麼，都祝你好運。', '', 38134),
(202778, 'zhTW', '太好了！這跟我們的預期不盡相同，但你的技術真高竿！', '', 38134),
(202775, 'zhTW', '很好，很好。他一定會很滿意。', '', 38134),
(202774, 'zhTW', '有品味的紳士？我應該泡一壺我最好的茶。', '', 38134),
(202773, 'zhTW', '史帝芬，如果你那裡有什麼好茶，我想一定可以讓賓客感到滿意。', '', 38134),
(202772, 'zhTW', '雷納瑟王子有貴賓？喔，天啊，我應該準備特別的餐點。', '', 38134),
(202771, 'zhTW', '我們之後會將剩下的都整理好。雷納瑟王子應該很快就會帶著貴賓過來。', '', 38134),
(202770, 'zhTW', '看看擦洗得多麼乾淨！你的泥掘者可以從淵喉行者身上學到一點東西。', '', 38134),
(202769, 'zhTW', '很有效率，淵喉行者！在賓客抵達之前，將剩下的也清理乾淨。', '', 38134),
(202768, 'zhTW', '看起來已經好多了！我喜歡整潔的宴會。', '', 38134),
(202767, 'zhTW', '你們二個負責南邊的巡邏。我來處理市民。', '', 38134),
(202766, 'zhTW', '', '走得好。獵手，你最好別太信任他。', 38134),
(202765, 'zhTW', '遵命，老大。', '', 38134),
(202763, 'zhTW', '如果餐點區域再乾淨點，我覺得這場宴會排練會更加出色。', '', 38134),
(202762, 'zhTW', '不用管社交名流。我們應該專心讓最重要的賓客留下好印象。', '', 38134),
(202761, 'zhTW', '噢，我差點忘了講：我邀請了一些誓盟成員來看看反應。', '', 38134),
(202760, 'zhTW', '可是這裡真是有夠亂！在正式舉行宴會之前，我們得清理一下。', '', 38134),
(202759, 'zhTW', '淵喉行者，你在這裡啊！', '', 38134),
(202758, 'zhTW', '如果你感到困惑、需要人指點迷津，我會提供大方向指引。願你與靈魂之絆滿懷自豪踏上晉升之路。', '', 38134),
(202757, 'zhTW', '', '達提莉絲可以幫你取得全新記憶讓你進行訓練，還有符咒與裝備，讓你的靈魂之絆實力提升。', 38134),
(202756, 'zhTW', '', '你的下一步，就由你和靈魂之絆決定，$n。你會獲得榮耀，但也會學到教訓。', 38134),
(202755, 'zhTW', '', '從現在起，每次挑戰晉升之路都需要職責勳章，證明你已對昇靈堡善盡職責。雖然晉升之路是榮耀的象徵，但我們還是得腳踏實地。我已經給你一些勳章，讓你可以開始嘗試。', 38134),
(202752, 'zhTW', '我會幫你保管這些裝備。如果你想使用這些裝備，就到大競技場來找我！', '', 38134),
(202741, 'zhTW', '來，凡人。這個傳送器將會把你送到樞運之環。', '', 38134),
(202735, 'zhTW', '現在正是最好時機，$p。使用你的靈魂之鏡，快點了結這件事。！', '', 38134),
(202734, 'zhTW', '我在…我們在…？我們在哪裡？', '', 38134),
(202733, 'zhTW', '這是…怎麼回事？我在哪裡？', '', 38134),
(202727, 'zhTW', '在天井點蠟燭！餐點用的椅子！舞台上的燈籠！', '', 38134),
(202709, 'zhTW', '', '如你所願。', 38134),
(202706, 'zhTW', '你得先過我這關！', '', 38134),
(202701, 'zhTW', '', '我現在就讓你知道反抗祭儀家族的下場！', 38134),
(202698, 'zhTW', '做得好，認真工作…', '', 38134),
(202697, 'zhTW', '現在塔賓斯泡茶。最好的茶！每個人都說我有多棒！', '', 38134),
(202696, 'zhTW', '不！！！', '', 38134),
(202694, 'zhTW', '好吧，這傷勢真嚴重。沒死算你厲害。', '', 38134),
(202693, 'zhTW', '喔，或許灑三次，享受人生嘛！', '', 38134),
(202692, 'zhTW', '最後一個材料也很重要，那就是你提供的催化劑！用小攪拌器攪拌…', '', 38134),
(202691, 'zhTW', '只要一點骨粉…再撒上少許軟泥…', '', 38134),
(202690, 'zhTW', '看啊，無數年鑽研煉金術和犧牲學徒所產生的奇蹟！', '', 38134),
(202688, 'zhTW', '我的晉升速度可能不如某些人，$p，但是我真的很高興能踏上這條路。\n\n願意討論過去的琪瑞安族很少，擁有生前記憶的人更少！我相信在那方面，我的族人還有太多要學習。\n\n我告訴克蕾雅的時候，她認真聆聽，就跟你一樣。她是個忠誠的好友，我一直都希望能有這種朋友，就跟你一樣。\n\n謝謝你。我很高興能跟你分享我的故事。', '', 38134),
(202686, 'zhTW', '可是等我抵達昇靈堡後，我變成一位志士，看起來就像，嗯…\n\n<佩拉苟斯指著自己。>\n\n這樣！男兒身！\n\n我覺得…舒服自在！熱血沸騰！別人眼中的我、內心深處的我，第一次完全吻合！\n\n我終於明白自己生前的種種感受。', '', 38134),
(202684, 'zhTW', '我真的很感激你願意聆聽。身為靈魂之絆，我知道自己可以信任你。\n\n<佩拉苟斯深吸一口氣，然後緩緩吐氣。>\n\n我生前曾是女兒身。我不記得自己當時的名字，也不記得種族，但是我記得…那時總是覺得身體外表與內心格格不入。\n\n我從未像現在看得如此透徹。我活著的時候，一輩子都為了身分所苦…', '', 38134),
(202682, 'zhTW', '除了一件事。\n\n很想回想起來到底是什麼。我知道，我應該放下過去，但是…我想…也許必須…想起來才對。\n\n這是我想要遺忘的一大段記憶。', '', 38134),
(202680, 'zhTW', '我最近記性極差。\n\n隨著時間推移，生前那段時光越來越遙遠，記憶也逐漸模糊。為了善盡職責，我自願放下許多記憶。\n\n除了…', '', 38134),
(202677, 'zhTW', '他們說我瘋了！就算那是事實，他們還是叫個不停！\n\n今天一切都會改變！', '', 38134),
(202667, 'zhTW', '就算是強大的人，也要努力工作才能伸展和成長…', '', 38134),
(202665, 'zhTW', '慢下來，年輕人。', '', 38134),
(202663, 'zhTW', '', '你收集到的記憶都存放在身後那面鏡子裡面，用它來選擇你的對手。', 38134),
(202662, 'zhTW', '你一定要穿過蘑菇環才能抵達那些島嶼。次數有限，但你可以試試。', '你一定要穿過蘑菇環才能抵達那些島嶼。次數有限，但你可以試試。', 38134),
(202661, 'zhTW', '佩拉苟斯今天踏上了晉升之路的第一步。願他今後能夠跟隨無數琪瑞安前輩的堅定步伐！', '', 38134),
(202659, 'zhTW', '', '準備好了就告訴我一聲。我會啟動裝置，但是它支撐不了多久。', 38134),
(202658, 'zhTW', '', '恐懼會淹沒你！', 38134),
(202653, 'zhTW', '你和你的靈魂之絆走著許多先人走過的道路。踏上晉升之路是我們無上的榮耀與傳統。', '', 38134),
(202632, 'zhTW', '<狗骨頭挖掘地面，並且聞來聞去。>', '', 38134),
(202627, 'zhTW', '', '依你指示。', 38134),
(202626, 'zhTW', '', '我在你的靈魂保管水晶中感受到卡多雷的靈魂，$n。把他們釋放到亞登曠野的荒野中吧。', 38134),
(202621, 'zhTW', '', '<凜冬女王的雙眼炯炯有神，裡頭蘊藏著深厚的力量。她以充滿肯定的神情看著你。>\n\n做得漂亮，凡人。\n\n來吧。就讓他們的屍骸被果姆啃咬殆盡吧。', 38134),
(202619, 'zhTW', '', '啊啊啊！卑劣的女王！佐司特還會東山再起的！', 38134),
(202618, 'zhTW', '', '來吧，凡人。我們除掉這卑鄙的東西。', 38134),
(202616, 'zhTW', '', '我絕對不能跟丟她的蹤跡。我會在前面等你。', 38134),
(202615, 'zhTW', '', '看呀！我的林地的魔法全部都會起來反抗你！', 38134),
(202614, 'zhTW', '', '要小心。強大的力量想吞噬她。我祈禱你跟珊蒂斯能幫助她掙脫控制。', 38134),
(202613, 'zhTW', '珊蒂斯，這可能是你拯救泰蘭妲的唯一希望。', '', 38134),
(202602, 'zhTW', '應你的要求，我們會帶著各種有趣的凡人物品出席餘燼宴會。也許你的賓客會看到中意的東西。', '應你的要求，我們會帶著各種有趣的凡人物品出席餘燼宴會。也許你的賓客會看到中意的東西。', 38134),
(202600, 'zhTW', '', '淵喉行者，你來得正好。', 38134),
(202599, 'zhTW', '', '敵人進軍了。我們沒辦法堅守太久。', 38134),
(202588, 'zhTW', '就等你準備好。噢，感覺會很好玩！', '', 38134),
(202582, 'zhTW', '', '瑪利雷斯會教導你將兩個靈魂連結在一起的意義。', 38134),
(202581, 'zhTW', '那條河生者不宜靠近。務必要避開它。', '', 38134),
(202580, 'zhTW', '盡情掙扎吧！那些流亡的天選者和他們琪瑞安寵物的屍體，很快就能利用了。', '盡情掙扎吧！那些流亡的天選者和他們琪瑞安寵物的屍體，很快就能利用了。', 38134),
(202576, 'zhTW', '', '不要浪費這個機會，淵喉行者。', 38134),
(202575, 'zhTW', '能夠駕馭統御者之力，是至高無上的榮耀。', '', 38134),
(202567, 'zhTW', '', '真高興看到你加入。', 38134),
(202565, 'zhTW', '希瓦娜斯！她想對安杜因做什麼？', '', 38134),
(202561, 'zhTW', '想要買點特別的東西嗎？', '想要買點特別的東西嗎？', 38134),
(202559, 'zhTW', '這個機械生物看起來需要修理了。', '', 38134),
(202558, 'zhTW', '鑰匙…在烙印者…手上…', '', 38134),
(202557, 'zhTW', '', '我會想辦法。', 38134),
(202556, 'zhTW', '佐司特擋住了前進的路！', '', 38134),
(202555, 'zhTW', '', '我有不少能在這邊活下來的訣竅。但要是你跟閻獄之主的手下開戰了，那一定會驚動他。所以，最好盡可能偷偷繞過他們。', 38134),
(202554, 'zhTW', '', '你的出現會吸引到很多目光。這樣只會讓我的生意更難做。我沒有惡意，只是陳述事實。', 38134),
(202553, 'zhTW', '仲介者，你為什麼要幫助我們？', '', 38134),
(202552, 'zhTW', '', '我是維娜里，被困在這個苦難之境的小小仲介者。我想跟你說個提議。來吧！', 38134),
(202551, 'zhTW', '', '我倒是很好奇淵誓者究竟在找什麼。你就是那個意外現身，把閻獄之主搞得灰頭土臉的凡人吧。', 38134),
(202549, 'zhTW', '我得承認，聲東擊西還真有效。這樣一來擊敗他們的滋味就更美好了。\n\n<赫恩露齒而笑。>\n\n可以開始了嗎？', '', 38134),
(202547, 'zhTW', '這些醜惡的怪物將在死前嚐嚐我的刀刃。', '', 38134),
(202536, 'zhTW', '把催化劑加進大鍋，讓我們見識這個驚世傑作！', '', 38134),
(202535, 'zhTW', '昇靈堡已經聽說我有多聰明絕頂，這也難怪啦。這個全新作品將是我有史以來的最強傑作，給單純的琪瑞安好好學習殘暴與混亂！', '', 38134),
(202534, 'zhTW', '我們會保護暗影之境，排除外來和內部的威脅！', '', 38134),
(202529, 'zhTW', '王子，我還在。我受傷了，但是還撐得住。', '', 38134),
(202528, 'zhTW', '', '守則二：保持低調。淵喉不是鬧著玩的地方。進來，拿到你要的東西，然後就離開…盡量保持四肢健全，完整的走出去。', 38134),
(202527, 'zhTW', '朋友！架好護盾！', '', 38134),
(202525, 'zhTW', '各位，找好掩護！', '', 38134),
(202524, 'zhTW', '老朋友，你還活著嗎？', '', 38134),
(202522, 'zhTW', '', '謝謝你。他們闖的禍總是讓人嘆為觀止！', 38134),
(202521, 'zhTW', '', '照顧小果姆的人需要一點幫忙。你能去幫幫他們嗎？', 38134),
(202520, 'zhTW', '', '別擔心，凡人。只要避開他的凝視一天，閻獄之主的注意力就會轉到別的地方。到時候你就能再次外出冒險了。為我尋找更多的冥魄。', 38134),
(202519, 'zhTW', '', '好消息是，你在我的藏身處是安全的。但是出去就危險了。記住，要是你出事了，你先前收集的寶貴冥魄可能會不小心流失一些。', 38134),
(202518, 'zhTW', '', '首先，他會派狩魂者來找你，然後是哨塔，接著是他的殺手部隊。在那之後…你不會想知道的。總之你的行動必須精準、謹慎，才不會喪命。', 38134),
(202517, 'zhTW', '', '既然閻獄之主盯上了你，你差不多已經完了。你在淵喉做的任何事都會引起他的注意。而我們最不希望的，就是引起他的注意。', 38134),
(202516, 'zhTW', '', '我不能讓你通過。', 38134),
(202515, 'zhTW', '', '我們負責保護森林之心的安全。除非有公務在身，否則只有曠野獵者和凜冬之廷的成員可以入內。', 38134),
(202514, 'zhTW', '', '你好。$b$B只有曠野獵者的成員才可以進入森林之心。', 38134),
(202513, 'zhTW', '', '森林之心歡迎你光臨。你可以通過。', 38134),
(202512, 'zhTW', '', '為了女王和林地，我們堅守此地。', 38134),
(202511, 'zhTW', '', '歡迎回來。狩獵成果如何？', 38134),
(202509, 'zhTW', '', '晚點才能哀悼。我們必須快點！', 38134),
(202508, 'zhTW', '', '月莓女士眨眨眼。', 38134),
(202507, 'zhTW', '', '我很確定餘燼宴會將是一個美妙又威嚴的場合。', 38134),
(202499, 'zhTW', '', '我需要什麼嗎？', 38134),
(202498, 'zhTW', '', '$p，女爵大人並沒有看走眼。我們在努拉齊爾會合。', 38134),
(202497, 'zhTW', '佐司特嚴密駐守。我們必須發動最終攻勢。', '', 38134),
(202496, 'zhTW', '', '凡人，你的努力不同凡響。你有資格獲得昇靈堡的恩典。赫拉克勒屹立不倒！', 38134),
(202495, 'zhTW', '', '棄誓者想要消滅我們最強的防衛者。找到名為赫拉克勒的百衛機兵巨像，不計一切代價保護他！', 38134),
(202493, 'zhTW', '', '巫妖狡詐又自大，對方勢必會頑強抵抗。', 38134),
(202492, 'zhTW', '', '英拉錐夫…淵喉行者，讓他受苦吧。', 38134),
(202491, 'zhTW', '你救了我一命！還好我和你同陣線！', '', 38134),
(202490, 'zhTW', '德沃絲的繼任者萊索妮雅是很大的威脅。我會立刻派人查明她的計畫。', '', 38134),
(202489, 'zhTW', '御靈者萬歲！昇靈堡萬歲！', '', 38134),
(202488, 'zhTW', '我們可以再次晉升通過考驗的志士，並且從棄誓者手中奪回我們的國度！', '', 38134),
(202487, 'zhTW', '我們重鑄的晉升紋章比以往還要強大！', '', 38134),
(202486, 'zhTW', '在淵喉行者的協助下，我們撐過去了。', '', 38134),
(202485, 'zhTW', '當德沃絲摧毀晉升紋章時，她想破壞我們之間的連結。', '', 38134),
(202484, 'zhTW', '你們都對我們的族人貢獻良多。現在我們必須封印晉升紋章重生的力量，保護它不受未來敵人的威脅。', '', 38134),
(202472, 'zhTW', '', '你準備加入死靈領主的行列了嗎？', 38134),
(202469, 'zhTW', '', '女爵大人！你回來了！', 38134),
(202460, 'zhTW', '很可能是某個泥掘者把新的罪孽石丟進石堆。', '', 38134),
(202457, 'zhTW', '剛剛那座罪孽石山脈是不是動了一下？', '', 38134),
(202447, 'zhTW', '', '我們還要失去多少才夠？', 38134),
(202420, 'zhTW', '', '每個人都在看我…哈囉！我是大家的朋友！', 38134),
(202419, 'zhTW', '大家請注意一下這邊！我想提議向從我創造佳賓斯以來、最樂於助人的人士舉杯致敬，那就是來自昇靈堡的西卡！', '', 38134),
(202418, 'zhTW', '而且我等不及想讓你看第十六號了，希奧塔！那只會痛一下下。', '', 38134),
(202417, 'zhTW', '大家請注意一下這邊！我想提議向教我軟泥十五種驚奇用途的天才人士舉杯致敬，那就是瘟疫設計者瑪利雷斯！', '', 38134),
(202410, 'zhTW', '', '亞登曠野的生物體型都很大。你應該找機會來看看。', 38134),
(202409, 'zhTW', '大家請注意一下這邊！我想請大家向艾利歐德樹林長敬酒，他是亞登曠野的堅毅守護者，也是我生平所見體型最大的熊！', '', 38134),
(202405, 'zhTW', '', '我比較喜歡你說我「致命」又「無情」，不過必要時，我的確也是相當驍勇善戰。', 38134),
(202404, 'zhTW', '大家請注意一下這邊！我想請大家向驍勇善戰的亞登曠野狩獵隊長克芮恩敬酒！', '', 38134),
(202403, 'zhTW', '願我們的敵人在我們面前灰飛煙滅。', '', 38134),
(202401, 'zhTW', '大家請注意一下這邊！我想請大家向瑪卓薩斯的強者，亞歷山卓斯‧莫格萊尼敬酒！', '', 38134),
(202396, 'zhTW', '', '等你看到我安排的最後驚喜再來敬酒吧！', 38134),
(202395, 'zhTW', '大家請注意一下這邊！我想請大家向這裡最聰明狡黠的月莓女士敬酒！', '', 38134),
(202380, 'zhTW', '', '至少，我希望如此…', 38134),
(202379, 'zhTW', '', '雷納瑟王子，沒有東西是永遠找不到的。', 38134),
(202378, 'zhTW', '這就是傳說中的…我以為再也找不到這顆罪孽石了…', '', 38134),
(202377, 'zhTW', '雷納瑟王子興致勃勃地檢視罪孽石。', '', 38134),
(202375, 'zhTW', '看來這隻蝙蝠能帶你離開血紅深淵。', '看來這隻蝙蝠能帶你離開血紅深淵。', 38134),
(202373, 'zhTW', '伯瓦爾也許知道女妖把那些領袖帶去了哪裡。', '', 38134),
(202368, 'zhTW', '', '我們為使命製作捕魂網，就像亞登曠野的任何其他工作一樣。大多數都是用來儲存或是釋放靈魄，這附近的那幾個也是。但這個…這個不一樣。$b$b這個是用來捕捉夢境的。$b$b可惜的是，我的能力只能修復它的外形，但無法恢復它的作用。我得找到某位具備夢境之力的人，才能為強化這面捕魂網。', 38134),
(202367, 'zhTW', '', '<關娜極小心地檢視著碎片。>$b$b喔，你這可憐的東西。怎麼回事呢？$b$B啊，特那希。它的痛苦人人皆知。對，對，我們一下就能修好你！$b$B謝謝你把這帶來給我。我們太習慣丟棄還能用的事物。如果你能帶十片無光絲布給我，我就可以修好它。', 38134),
(202366, 'zhTW', '藉由文庫的力量，我們就能隨時隨地守望昇靈堡全境。', '藉由文庫的力量，我們就能隨時隨地守望昇靈堡全境。', 38134),
(202365, 'zhTW', '藉由文庫的力量，我們就能隨時隨地守望昇靈堡全境。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[智者守望]|h|r：降至低生命力時，晉升者會恢復你的生命力，並擊退附近的敵人。使你承受的傷害降低，此效果會在短時間內快速衰退，並且只能每幾分鐘發動一次。', '藉由文庫的力量，我們就能隨時隨地守望昇靈堡全境。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Icon_wing06a.BLP:20|t|cFFFF0000|Hspell:335577|h[智者守望]|h|r：降至低生命力時，晉升者會恢復你的生命力，並擊退附近的敵人。使你承受的傷害降低，此效果會在短時間內快速衰退，並且只能每幾分鐘發動一次。', 38134),
(202364, 'zhTW', '是時候讓這些禱鐘再次響起代表和平與澄明的鐘聲了。', '是時候讓這些禱鐘再次響起代表和平與澄明的鐘聲了。', 38134),
(202363, 'zhTW', '是時候讓這些禱鐘再次敲響和平與明晰的鐘聲了。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[明晰禱鐘]|h|r：把許多敵人聚集在一起時，你的傷害法術與技能有機率召喚出明晰禱鐘，持續一小段時間。明晰禱鐘會在出現時擊昏附近的敵人，待在禱鐘附近時，你造成的傷害會增加。', '是時候讓這些禱鐘再次敲響和平與明晰的鐘聲了。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Misc_Bell_01.blp:20|t|cFFFF0000|Hspell:335578|h[明晰禱鐘]|h|r：把許多敵人聚集在一起時，你的傷害法術與技能有機率召喚出明晰禱鐘，持續一小段時間。明晰禱鐘會在出現時擊昏附近的敵人，待在禱鐘附近時，你造成的傷害會增加。', 38134),
(202362, 'zhTW', '琪瑞安！歡迎來到永世文庫，透過靈魄的匯聚，我們得以強化你的能力。', '琪瑞安！歡迎來到永世文庫，透過靈魄的匯聚，我們得以強化你的能力。', 38134),
(202360, 'zhTW', '真是太讓人期待了！', '', 38134),
(202359, 'zhTW', '輕快的腳步藏不住我心中的雀躍。', '', 38134),
(202358, 'zhTW', '朋友，能佔用你一點時間嗎？', '', 38134),
(202357, 'zhTW', '大巫妖！跟我合作！使用我給你的勳章，我們就能連手殺掉淵喉行者。', '', 38134),
(202356, 'zhTW', '淵喉行者，快點殺掉阻礙你前往下一個水泉的敵人。我感應到勳章就在附近。', '', 38134),
(202355, 'zhTW', '開始懺悔吧！', '', 38134),
(202354, 'zhTW', '你會為自己的傲慢付出代價！', '', 38134),
(202353, 'zhTW', '', '至於伯昂撒姆第，我對他的命運毫無興趣。幫他或不幫，隨你決定。我必須照料亞登曠野。', 38134),
(202352, 'zhTW', '', '我的兄弟對儀祭不會馬虎。', 38134),
(202349, 'zhTW', '淵喉行者已經同意為我們的理想效力。我們會聽到誓約，並歡迎對方加入反抗軍。', '', 38134),
(202347, 'zhTW', '現在，和我同一陣線。', '', 38134),
(202331, 'zhTW', '沒錯，我的朋友。和這些凡人一起，我們會獲得勝利。', '', 38134),
(202330, 'zhTW', '這個下場很適合這些傲慢之人。我們會拯救瑞文崔斯、破壞閻獄之主的計畫，到時他只能無助地看著。', '', 38134),
(202329, 'zhTW', '長久以來，戴納瑟斯一直把聖光當成武器來對付餘燼區的居民。現在，我們要用聖光的力量來束縛他。', '', 38134),
(202328, 'zhTW', '', '我很榮幸能代表女王陛下出席餘燼宴會。', 38134),
(202327, 'zhTW', '', '$n，我們很快就會在餘燼宴會相逢。', 38134),
(202326, 'zhTW', '我們會把那把刀刃帶回罪殞城。有個被凡人拯救的那魯，叫吉雷利，他曾誓言要效忠我們。', '', 38134),
(202325, 'zhTW', '沒錯。但是我知道有個東西可以辦到。', '', 38134),
(202324, 'zhTW', '如果那把劍真的吸取了戴納瑟斯的精華，光靠你的法術恐怕沒辦法鎮壓他的力量。', '', 38134),
(202323, 'zhTW', '你的話我們已經聽膩了！', '', 38134),
(202322, 'zhTW', '', '王子，你這個叛徒！我會把你串起來！切碎！砍斷！解剖！再切成一條一條的…', 38134),
(202321, 'zhTW', '不。戴納瑟斯會為他的背叛付出代價。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(202320, 'zhTW', '', '佐瓦爾會恢復您的力量，主人！我們會復仇的！', 38134),
(202319, 'zhTW', '那把刀刃吸收了他的精華！', '', 38134),
(202317, 'zhTW', '', '一切還沒結束，主人！我來保護您！', 38134),
(202314, 'zhTW', '冷靜一點，雷莫尼亞。這種事是急不得的…這樣非常失禮。', '', 38134),
(202309, 'zhTW', '', '因為大家的努力，我們才能保存我們的原始自然環境。做得好。', 38134),
(202308, 'zhTW', '', '這地方真是混亂不堪。幸好，我們有許多侍者聽命於我們，能幫助我們處理任何問題。請讓這裡恢復和平。', 38134),
(202307, 'zhTW', '雷莫尼亞，聽好了！這些大膽的凡人克服了所有擋在他們面前的阻礙。現在看來我們別無選擇，只能投降了。', '', 38134),
(202306, 'zhTW', '凡人，你是勝利的關鍵！去吧，打倒那個背叛我們大家的人！', '', 38134),
(202305, 'zhTW', '更多忠誠派來了！王子，我們必須擊退他們！', '', 38134),
(202302, 'zhTW', '馬上就有更多佐司特要打過來了，動作快！', '', 38134),
(202299, 'zhTW', '', '嗯，一切的發展完全如我所料。', 38134),
(202298, 'zhTW', '', '我已經盡最大努力提起每個人的興致，但這樣恐怕還不夠。', 38134),
(202297, 'zhTW', '我必須回去工作了。浪費太多時間了。', '', 38134),
(202296, 'zhTW', '', '親愛的王子，這場宴會恐怕有很多值得改進的地方。', 38134),
(202295, 'zhTW', '我已經浪費夠多時間，該回到戰場上了。', '', 38134),
(202294, 'zhTW', '', '宴會不適合我。我一定要回到狩獵中。', 38134),
(202293, 'zhTW', '我現在瞭解御靈者把我送來此處，只是想要讓我威信掃地。', '', 38134),
(202292, 'zhTW', '那麼…比誰先跑到馬車那邊？', '', 38134),
(202291, 'zhTW', '上流社會和我想像中的不太一樣。', '', 38134),
(202290, 'zhTW', '史蘭克能，快點把剩下的喝完！我們差不多該走了！', '', 38134),
(202289, 'zhTW', '能夠受邀一向都是我的榮幸，雷納瑟王子。或許，這樣的榮耀下次可以贈與他人。', '', 38134),
(202288, 'zhTW', '', '雷納瑟王子，我原本期待你會說出更具說服力的話語。我想要相信你的理想。', 38134),
(202287, 'zhTW', '看鐵匠把武器磨利，我還覺得比較有趣。', '', 38134),
(202286, 'zhTW', '克蕾雅，也沒那麼糟啦。有個叫做佳賓斯的泥掘者對我們很好。', '', 38134),
(202285, 'zhTW', '', '感謝御靈者，一切都結束了。', 38134),
(202284, 'zhTW', '好吧，至少凱文玩得很開心。我相信你絕對不會想念那些泥掘者。', '', 38134),
(202283, 'zhTW', '', '早知道應該待在昇靈堡的，那裡乾淨多了。', 38134),
(202282, 'zhTW', '這個派對需要更多點心。', '', 38134),
(202281, 'zhTW', '當然了，這原本是兩項獨立、平行的實驗…但兩邊的結果都很成功！', '', 38134),
(202280, 'zhTW', '', '月莓女士朝你眨眨眼。', 38134),
(202279, 'zhTW', '該是推翻前任主宰的時候了。我們接下來所做的事，都是為了整個暗影之境的福祉！', '', 38134),
(202278, 'zhTW', '我的朋友，被擺佈、欺瞞的不只有石源魔。我敢說就連審斷者也曾被當成棋子。', '', 38134),
(202277, 'zhTW', '我們從石頭召喚而來，只為保衛瑞文崔斯。看到我們效忠的一切被昔日的主人變得如此不堪，實在令人難過。', '', 38134),
(202275, 'zhTW', '我喜歡在我說笑話的時候，大家都很捧場。', '', 38134),
(202274, 'zhTW', '', '很有趣的宴會！很值得的旅行！一定會向所有朋友宣傳！', 38134),
(202273, 'zhTW', '結合友誼和軟泥的成功實驗！', '', 38134),
(202272, 'zhTW', '只要我們同心協力，沒有無法克服的難關。', '', 38134),
(202271, 'zhTW', '', '我景仰你致力於為暗影之境效命，而且不只將目光放在瑞文崔斯。', 38134),
(202270, 'zhTW', '我喜歡你的風格，雷納瑟。一個不怕弄髒自己雙手的王子…真是前途無量。', '', 38134),
(202269, 'zhTW', '', '今日所見一切令我印象深刻。你的一言一行，我會如實向凜冬女王稟報。', 38134),
(202268, 'zhTW', '當然，我們的禮物一定是最好的。這是我們親手做的！', '', 38134),
(202267, 'zhTW', '希望我的禮物是最棒的！然後你就會再次邀請我，對吧？', '', 38134),
(202266, 'zhTW', '', '我很期待有朝一日能與你一起狩獵。', 38134),
(202265, 'zhTW', '感謝你讓我休息一下。我又能活力充沛地回到戰場上了！', '', 38134),
(202263, 'zhTW', '而且我們聯合起來驅離所有國度的共同敵人：吞噬者！', '', 38134),
(202262, 'zhTW', '要小心，對上戴納瑟斯之前，還得先解決他最忠誠的衛兵。', '', 38134),
(202261, 'zhTW', '凡人，我們絕不能放棄。幸好，這面鏡子看起來還有修復的可能。', '', 38134),
(202260, 'zhTW', '對，接下來就交給你了。', '', 38134),
(202259, 'zhTW', '確實，你所到之處，死傷無數。如果你這麼喜歡搞破壞，我這個大塊頭朋友倒是可以陪你玩玩。', '', 38134),
(202258, 'zhTW', '但你也不算白教，畢竟我可是殺掉了你很多的爪牙呢，「老師」。', '', 38134),
(202257, 'zhTW', '雷納瑟，我教了你這麼多，結果你卻不明白自己的地位。嘖、嘖、嘖，真叫人失望。', '', 38134),
(202256, 'zhTW', '凡人，你表現得很好，你成功重挫了戴納瑟斯的計畫。但千萬不要小看他的力量。他的威力…非同小可。', '', 38134),
(202255, 'zhTW', '這裡有另一條通道，可以通往前廳。', '', 38134),
(202253, 'zhTW', '看看他們！我的人民居然墮落成這樣，實在令人難過。', '', 38134),
(202252, 'zhTW', '來吧，凡人。瑞文崔斯那些傲慢的貴族擋在我們和戴納瑟斯之間，是時候除掉他們了。', '', 38134),
(202251, 'zhTW', '控罪者，帶走這個靈魂並協助他。', '', 38134),
(202250, 'zhTW', '', '你還有很多要學習的地方。', 38134),
(202249, 'zhTW', '我受夠了被其他人當成棋子來利用。你的「協助」能讓我報仇嗎？', '', 38134),
(202248, 'zhTW', '', '凱爾薩斯‧逐日者！你雖然脫離了國王的暴政，但你別想擺脫自己過去的行為。跟我來吧，我會幫助你贖罪的。', 38134),
(202246, 'zhTW', '', '和前方那把武器相比，這些聖物的力量不值一提。', 38134),
(202244, 'zhTW', '貪婪，的確是所有罪惡中最邪惡的一項。', '', 38134),
(202243, 'zhTW', '真是令人震驚！我們的人民在城堡的牆外挨餓，而這些忠誠者卻活在酒池肉林中。', '', 38134),
(202242, 'zhTW', '仲介者是一群貪得無厭的傢伙，別讓威力如此強大的武器落入他們的手中！', '', 38134),
(202241, 'zhTW', '大廳內展示著許多珍貴的武器。我們去奪走那些貴族的武器。', '', 38134),
(202240, 'zhTW', '', '在他們紙醉金迷的時候，閻獄之主召集了一支能摧毀暗影之境的軍隊。這些相信戴納瑟斯謊言的蠢蛋真是活該！', 38134),
(202239, 'zhTW', '這個鏡子應該能幫助你前往城堡，凡人。', '', 38134),
(202238, 'zhTW', '石源魔可以使用這些道路來達成主宰的要求。我親眼見過那些糜爛的畫面…', '', 38134),
(202237, 'zhTW', '凡人，往這邊走。有一條通道可以通往前廳。', '', 38134),
(202236, 'zhTW', '你喜歡帽子？\n\n新朋友布特讓我漂漂亮亮去派對。\n\n不要擔心，你還是最好的朋友！', '', 38134),
(202235, 'zhTW', '', '沒有時間後悔了。戴納瑟斯的時代必須結束。', 38134),
(202234, 'zhTW', '', '我們阻止了靈魄之流，但恐怕傷害已經造成。為了瑞文崔斯，我們要回收剩下的東西。', 38134),
(202233, 'zhTW', '', '朋友會幫忙西卡。西卡會帶朋友來。\n\n朋友會幫忙清理。淵喉行者是朋友。\n\n淵喉行者…會幫忙清理？\n\n<西卡被附近的人轉移了注意力。>\n\n玩得開心！享受宴會！', 38134),
(202232, 'zhTW', '凡人，做得好！靈魄貯存處就在前方了。', '', 38134),
(202231, 'zhTW', '', '殺掉這隻生物，別讓牠變得更強！', 38134),
(202230, 'zhTW', '', '那隻野獸想用下方貯存的靈魄來滿足自己無止盡的食慾。', 38134),
(202229, 'zhTW', '居然有吞噬者？！我從來沒看過這麼大的！', '', 38134),
(202228, 'zhTW', '今天真適合舉行宴會！\n\n我會帶凱文去，當然囉。還有其他兩樣我特別為宴會精心準備的美味點心！\n\n噢，別擔心。那兩樣還是泥漿怪。全部都是泥漿怪。\n\n<馬里雷斯環顧四周。>\n\n說到這個…我聽說你的泥掘者是淤泥做的。\n\n淤泥…淤泥…你覺得那跟泥漿怪差不多嗎？\n\n我一定要弄清楚！', '', 38134),
(202227, 'zhTW', '淵喉行者，就算王子殿下無法達成目的，這場宴會仍是盛大的成功。\n\n你看看周圍。\n\n不同界域的居民都來到這裡，彼此協商溝通、尋找共同的目標。\n\n這讓人充滿希望。\n\n願希望能帶來團結，而團結帶來和平。', '', 38134),
(202226, 'zhTW', '', '希望你不介意我和佩拉苟斯邀請了另外兩位志士跟我們一起來。\n\n他們展現出絕佳的潛力，御靈者也同意這段旅程的經歷能帶來更多啟發。\n\n我們都還有很多要向彼此學習的。', 38134),
(202225, 'zhTW', '很榮幸有這個機會，能向你們的王子介紹瑪卓薩斯最優秀的戰士。\n\n<沃爾大師向一旁的社交名流展現結實的身材。>\n\n順帶一提，等一下我和這些小夥子說不定還有機會直接打起架來呢，各位想不想要來開個賭盤啊？\n\n雖然這場宴會並不是苦痛劇場，但是它還滿有一種…「怎麼樣都可以」的感覺。\n\n賠率很高唷。有興趣的人，下好離手。', '', 38134),
(202224, 'zhTW', '', '淵喉行者，我很好奇一件事。你真的有把瑞文崔斯當成自己的家來看待嗎？\n\n你有把雷納瑟王子當成王子看待嗎？\n\n<樹林長眼神呆滯地看向你身後。>\n\n我的凡人家園已經不見了。被摧毀了。\n\n但我在亞登曠野、在凜冬女王身邊，找回了家的感覺。\n\n<樹林長發出一聲長嘆。>\n\n你那個王子要我們思考的事情，是個很艱難的抉擇。', 38134),
(202223, 'zhTW', '', '我…無法…繼續下去…', 38134),
(202222, 'zhTW', '我必須說，能夠反過來讓泥掘者等我，感覺真棒。\n\n<倫德挺起胸膛並露出驕傲的笑容。>\n\n好，倫德我終於開始在這個位於社會底層的不毛之地慢慢往上爬了。', '', 38134),
(202219, 'zhTW', '載著那些為了宴會打扮得花枝招展的傢伙參加宴會那麼多次。\n\n現在你看看我。我才是漂亮又迷人。哈！', '', 38134),
(202218, 'zhTW', '', '淵喉行者，告訴我，在宴會場之外究竟有哪些怪物和野獸？\n\n在這片高原之外，我能看到的都是一片荒蕪。\n\n荒蕪的大地孕育出怪物和野獸。我在故鄉看過類似的情景。\n\n千萬小心。', 38134),
(202217, 'zhTW', '', '前面的路上充滿危險。但我們必須找到那些靈魄貯存！', 38134),
(202216, 'zhTW', '凡人，往下的通道在這裡。', '', 38134),
(202215, 'zhTW', '', '罪有應得的下場。', 38134),
(202214, 'zhTW', '阻止馴犬者的惡行，我們才能繼續往城堡的下層前進。', '', 38134),
(202213, 'zhTW', '主宰的獵狗群住在養狗場，他會故意讓那些野獸挨餓。', '', 38134),
(202212, 'zhTW', '', '如果我們想對抗戴納瑟斯，唯一的希望就是奪走他最有價值的靈魂。我知道有一條路可以找到他們。', 38134),
(202211, 'zhTW', '養狗場後面有一條通道，可以通往城堡的底層。我們可以從那邊抵達靈魄貯存處並阻止靈魄流進淵喉。', '', 38134),
(202210, 'zhTW', '我們佔領前廳了！但我想戴納瑟斯應該已經撤回他的聖所了。', '', 38134),
(202209, 'zhTW', '我既不習慣繁文縟節的禮儀，也不習慣皇室的富麗堂皇。\n\n<亞歷山卓斯鬆開拿著武器的手，表情也變得溫和起來。>\n\n但我還是很感謝能有短暫的休閒時光。就算是最強悍的戰士，也沒辦法不眠不休地作戰。\n\n我會利用這個機會好好養精蓄銳，然後精力充沛地迎接日後的戰鬥。\n\n我一定會收拾掉克利瑟斯。', '', 38134),
(202208, 'zhTW', '我們就用適合的挑戰開始為你贖罪吧，如何？', '', 38134),
(202207, 'zhTW', '看來你學不會教訓。或許我太仁慈了。畢竟，我可是出了名的憐憫包容。', '', 38134),
(202206, 'zhTW', '雷納瑟，我還記得不久之前你也是像這樣大吼大叫，我們都知道當時下場如何。', '', 38134),
(202205, 'zhTW', '廢話少說，戴納瑟斯！出來面對我們！', '', 38134),
(202204, 'zhTW', '果然是我們桀驁不馴的王子啊，還有遭到罷黜的熱血控罪者。再加上叛變的前任將軍，後面跟著一群急著來送死的凡人小嘍囉。', '', 38134),
(202203, 'zhTW', '歡迎！我一直都很期待你們的到來。我們來看看是誰來了？', '', 38134),
(202202, 'zhTW', '有意思。有意思。\n\n宴會真是太有趣了。\n\n這麼多新的點子。這麼多新的面孔。\n\n這麼多值得去學習的事。', '', 38134),
(202199, 'zhTW', '提高警覺，凡人。戴納瑟斯在這裡很有優勢。他會用盡一切詭計阻撓我們。', '', 38134),
(202198, 'zhTW', '', '他知道我們的時間不多了。閻獄之主的力量每分每秒都在增長。我們必須阻止靈魄繼續流入淵喉。', 38134),
(202196, 'zhTW', '嗯。$b$b<他明顯處於高度警戒。他徹底展現了「嗯」這個字有多少變化。>', '', 38134),
(202195, 'zhTW', '城堡的大門輕輕鬆鬆就攻破了。我們的前任主人在挑釁我們，想引誘我們去城塞的深處。', '', 38134),
(202194, 'zhTW', '嗯。$b$b<他連聽起來都有沉思的感覺。>', '', 38134),
(202193, 'zhTW', '嗯。$b$b<聽起來他非常、非常努力想要蓋過附近聲音的干擾。聽起來不怎麼管用。>', '', 38134),
(202192, 'zhTW', '嗯。$b$b<這次聽起來很明顯是覺得你很煩。>', '', 38134),
(202191, 'zhTW', '', '我不知道汎希爾居然這麼地…容易受騙。我惡搞他們惡搞得不亦樂乎。\n\n<月莓女士露出笑容，並看向側邊。>\n\n噢，我得走了，我的其中一個分身就快要 - \n\n算了，這種事情你還是不要知道比較好。', 38134),
(202190, 'zhTW', '冥想可以讓我與森林同調。協助我找出不屬於此地的事物。$b$B<沃爾凱睜開一眼看著你。>$b$b此事不容打擾。去和水仙談談。', '', 38134),
(202189, 'zhTW', '嗯。$b$B<你無法確定這位沃爾凱是特別用力冥想，還是只是覺得你很煩。>', '', 38134),
(202188, 'zhTW', '$p！感謝女王，你來了！', '', 38134),
(202185, 'zhTW', '勾拉札爾，結束了！', '', 38134),
(202184, 'zhTW', '', '不！你要為無禮付出鮮血的代價！', 38134),
(202182, 'zhTW', '我必須為接下來的旅程作準備。我很想向我們的盟友請教。', '', 38134),
(202181, 'zhTW', '', '你現在聽令於獄主之眼。等我們的命令。', 38134),
(202179, 'zhTW', '信使薄靴讓你行動自如！\n\n對手讓你緩速？無法移動？\n\n有了信使薄靴，誰也別想困住你！讓你擺脫阻礙，向前突進！', '', 38134),
(202178, 'zhTW', '', '夠了！我認輸。說吧，你想怎麼樣？', 38134),
(202177, 'zhTW', '我會顧好你的裝備，別擔心！等你要挑戰晉升之路時，裝備都會準備妥當。\n\n想知道有哪些可用裝備嗎？哪一個？', '', 38134),
(202175, 'zhTW', '', '閃閃發光吧！', 38134),
(202174, 'zhTW', '', '間諜？你這隻害蟲會後悔的！', 38134),
(202173, 'zhTW', '', '我在哪裡？是誰膽敢召喚我出來？', 38134),
(202172, 'zhTW', '該死的，水仙…', '', 38134),
(202171, 'zhTW', '', '這下子他們知道厲害了吧！誰說魔法不是萬靈丹？', 38134),
(202170, 'zhTW', '', '再多來點閃亮亮的混亂場面，你覺得如何？', 38134),
(202169, 'zhTW', '準備要開始的時候，請站到定位。', '', 38134),
(202168, 'zhTW', '準備好就到下面的房間找我們。', '', 38134),
(202167, 'zhTW', '', '不是寫說有新工作…是寫說要幫忙？', 38134),
(202166, 'zhTW', '我身邊的晉升者都在說道路的事。他們說每個靈魂在歷經充滿苦難的俗世之後，都必須在試煉中找到平靜和永恆。', '', 38134),
(202165, 'zhTW', '我試著忘卻。我試著原諒。但…我清醒時的每一個思緒，他都陰魂不散。不論踏上哪一條道路都一樣。', '', 38134),
(202164, 'zhTW', '我對這股憤怒感到陌生。我認不出來。我必須淨化它。那是我的道路。我的神聖楷模說事情應該要是如此，而它也必須要是如此。', '', 38134),
(202163, 'zhTW', '阿薩斯，聖光唾棄你的靈魂。願你的靈魂在淵喉裡腐爛！', '', 38134),
(202162, 'zhTW', '', '我今天又再問了一次，我何時才能進行最終儀式。又一次的，沒有任何答覆。這個地方確實教會了我耐心，但我仍覺得耐心備受考驗。', 38134),
(202161, 'zhTW', '', '我能在志士同胞的臉上看到挫折。我自己的感受也很強烈，但我絕對不能顯露出來。他們都景仰我！佩拉苟斯也景仰我…', 38134),
(202160, 'zhTW', '', '這場乾旱何時才會結束？到底是什麼造成的？晉升者知道嗎？還是他們也和我們一樣困惑？真希望我能知道答案。', 38134),
(202159, 'zhTW', '', '我必須回想我的訓練。集中精神。從我能掌控的東西尋求平靜。', 38134),
(202158, 'zhTW', '我聽說把思緒用文字記錄下來，能夠幫助人反躬自省並跳脫自身、看清問題的全貌。我想想…', '', 38134),
(202157, 'zhTW', '今天我又再次挑戰純潔儀式。我的靈魂之絆說我已經準備好了。我一開始感覺渾身充滿力量！但緊接著，我又退縮了，每次都這樣。', '', 38134),
(202156, 'zhTW', '我到底為什麼會這麼弱啊？克蕾雅解釋起來是那麼有道理，但等到實際上場的時候，我又會把學習到的內容忘得一乾二淨。', '', 38134),
(202155, 'zhTW', '我有時候會擔心自己永遠也無法晉升。擔心被送回奧睿博司，而審斷者會凝視我的靈魂，發現…', '', 38134),
(202153, 'zhTW', '', '吞噬者可能已經吃掉她了。真可惜。', 38134),
(202152, 'zhTW', '', '她很快就會回來。時間不多，讓我向你揭露真相吧。', 38134),
(202151, 'zhTW', '', '淵喉行者，戴納瑟斯把你當成他的棋子。', 38134),
(202150, 'zhTW', '', '繼續！我會守住橋樑。', 38134),
(202149, 'zhTW', '獲得伊露恩黑暗力量的代價就是失去自己的性命。\n\n凡人本來就不該得到這些。無論如何，都會要了我的命。為了我的家園、族人和親人，我願意犧牲自己。\n\n可達林並不樂見事態這樣發展。我後悔讓他跟我共患難，這讓我們兩個都葬送了性命。\n\n<塞爾納斯嘆了口氣。>\n\n我應該放下過去。重要的是我們拯救了家園，而且依然在一起。', '', 38134),
(202146, 'zhTW', '在女王到來之前，我們有很多事得張羅，別浪費時間！', '', 38134),
(202144, 'zhTW', '', '淵喉行者，讓我向你揭露真相吧。', 38134),
(202143, 'zhTW', '', '你的忠誠已經為人所見並獲得認可。和我們團結一心。', 38134),
(202142, 'zhTW', '在混亂中，你的雙眼會欺騙你。看似近在咫尺的，卻隱匿在迷障之後。\n\n努力嘗試也無法改變結果。敢於孤身犯險的卻往往迷失於時間的洪流。\n\n能覺察才會有希望。只有最敏銳的雙眼能找出混亂所隱藏的秘密。\n\n讓光明引導你的道路。穿越迷障的道路已經開啟。', '', 38134),
(202141, 'zhTW', '', '我接受你那兼具誠實和真理的誓言。你可以起身了。', 38134),
(202140, 'zhTW', '', '西卡待人親切，工作又超認真。我可以更努力打掃熔爐，幫她分擔煩憂。', 38134),
(202139, 'zhTW', '淵喉行者，謝謝你。\n\n志士逃跑的時候，我努力抵擋這些翼靈蛇…不過牠們的數量實在太多了。\n\n<亞當納斯輕輕摸了摸腰側。>\n\n幸好你有及時趕上。', '', 38134),
(202138, 'zhTW', '', '過去沒人能成功逃出血紅深淵，你當然也不例外！', 38134),
(202137, 'zhTW', '', '你只不過是德瑞文的走狗！', 38134),
(202136, 'zhTW', '', '可悲的凡人！', 38134),
(202135, 'zhTW', '', '到石柱那裡跟我碰頭。有工作要做。', 38134),
(202134, 'zhTW', '喔。我剛剛和西卡談過重新編列圖書館的事，而且她似乎很樂意接下這份工作。我完全不知道她過去那麼忙。我會搞定的！', '喔。我剛剛和西卡談過重新編列圖書館的事，而且她似乎很樂意接下這份工作。我完全不知道她過去那麼忙。我會搞定的！', 38134),
(202133, 'zhTW', '你就這點本事嗎？', '', 38134),
(202132, 'zhTW', '沒問題，我揹你。', '', 38134),
(202131, 'zhTW', '呃…就這樣？', '', 38134),
(202130, 'zhTW', '有問題要解決嗎？', '', 38134),
(202129, 'zhTW', '如果你需要最棒的人選，找我就對了。', '', 38134),
(202128, 'zhTW', '我們今天要殺誰？', '', 38134),
(202127, 'zhTW', '我遇過更危險的情況。', '', 38134),
(202123, 'zhTW', '', '像刀子一樣的手有嚇到你嗎？', 38134),
(202122, 'zhTW', '', '你需要幫手嗎？', 38134),
(202121, 'zhTW', '', '來抱一個！', 38134),
(202120, 'zhTW', '', '我離開以後要想我。', 38134),
(202117, 'zhTW', '你也幫助了西菲希爾斯？', '', 38134),
(202116, 'zhTW', '', '朋友就要在一起。', 38134),
(202115, 'zhTW', '', '要載你嗎？', 38134),
(202113, 'zhTW', '願此鐘再度鳴響，振奮人心。', '', 38134),
(202109, 'zhTW', '老兄，怎了？', '', 38134),
(202107, 'zhTW', '為了對抗殘害生靈的邪惡勢力。\n\n一群勇士結盟合作，想消滅踏足我們家園的黑暗之神。為了達成目標，我們需要使用最後的代言者向月神乞求的力量。\n\n<塞爾納斯苦笑了一下。>\n\n我們為此爭執過。他的母親已經為此喪命，他自然不希望丈夫也因此犧牲。但伊露恩的力量是對抗那些怪物的最佳武器，所以我進行了儀式而且活下來了。他也支持我。我們的雙眼像新月一樣黯淡，並開始與怪物生死相搏。\n\n我沒預料到會有這樣的代價。', '', 38134),
(202104, 'zhTW', '最後。\n\n塞爾納斯成功變成了黑夜戰士。我和他以及菲贊迪的勇士們一起並肩作戰到最後。我們獲勝了，但卻付出慘痛的代價。\n\n瘋狂大軍在我們離開時殺了我們許多同胞，卻也在黑暗主宰死去時四散而逃。我們也有一些同胞屈服在低語之中。他們不是消失不見，就是發狂。我們失去了朋友、家人。\n\n連神也在流血。要花上好幾個世代的時間才能淨化留在我們世界上的汙穢。\n\n如果真的能淨化的話。', '', 38134),
(202103, 'zhTW', '怪物。\n\n這些生物有數不清的眼睛、肢體以及飢餓的嘴巴。他們在我曾祖父母還是小孩的時候就橫掃了整個世界。無盡戰爭也就此展開。整個世代都賭上自己的生命去防守這些怪物和他們的瘋狂之舉。\n\n而是我的母親發現了那些住在火山口中心的怪物的本質。為了要殺死另一個世界的東西，她試圖激起伊露恩黑暗的一面。她卻因此死去。', '', 38134),
(202100, 'zhTW', '太好了。在我出生的好幾個世代之前，黑暗之神在我們的世界降臨了。\n\n這起事件造成了一個王國的覆滅，而自然的平衡也受到了破壞。所有深入那片廢墟的勇士，從來沒有人回來過。\n\n我們德魯伊密切觀察邊境的情況，但是卻出現了令人意想不到的敵人。', '', 38134),
(202098, 'zhTW', '我的世界名叫菲贊迪。那是個美麗的世界，有著廣袤的樹林、高大的山巒和寧靜的平野。\n\n為了保護大自然，我們才挺身對抗黑暗之神。', '', 38134),
(202097, 'zhTW', '沒錯，來去收下我們的獎勵吧！唱首慶祝的歌如何？賞你的獵人一點種子…', '', 38134),
(202096, 'zhTW', '喂，我也有幫忙！還記得那可怕的歌聲嗎？', '', 38134),
(202095, 'zhTW', '嗯，我真的…等等，什麼？', '', 38134),
(202094, 'zhTW', '感謝你在那裡的幫助。雖然情勢危急，但你表現得很不錯。', '', 38134),
(202093, 'zhTW', '呃。', '', 38134),
(202092, 'zhTW', '嗯。重要的是它死了。我們繼續前進吧。', '', 38134),
(202091, 'zhTW', '做得好。邪靈討厭可怕的聲音。', '', 38134),
(202087, 'zhTW', '等等，住手！', '', 38134),
(202086, 'zhTW', '喔，不！', '', 38134),
(202085, 'zhTW', '為什麼？這看起來沒有很危險阿。絕對不是我們在找的獵物。我隨手就能解決掉。看我厲害！', '', 38134),
(202084, 'zhTW', '不用害怕，你這個沒用的小丑…呃？我，呃…葛溫希爾？我好像找到了什麼東西！', '', 38134),
(202083, 'zhTW', '野獸野獸在哪裡？到底跑到哪裡去？你不幫忙沒關係，我只需要我自己！', '', 38134),
(202082, 'zhTW', '天才詩人一把抓。真材實料人人誇！', '', 38134),
(202081, 'zhTW', '喔，水仙水仙天下無敵！同伴根本不值一提！', '', 38134),
(202080, 'zhTW', '喔，我來告訴你我有什麼打算！有必要的話，我會自己一個人找到那傢伙。在這過程中，我還要寫一首歌！你聽聽看…', '', 38134),
(202079, 'zhTW', '你說誰，他嗎？不用管他，他只是因為還沒獵到目標而暴躁。其實他是個很出色的戰士，但他的手段很…奇特。但你別告訴別人你是從我這裡聽到的。$b$b他在打盹應該…聽 不 到 我 講 話 吧。$B$B<葛溫希爾似乎沒反應。>', '', 38134),
(202078, 'zhTW', '終於決定不再打盹並加入了，嗯？$b$B噢，是你不是他。好吧，或許你很管用。你最近有沒有看到可怕植物在附近嚇人？', '', 38134),
(202077, 'zhTW', '我‧正‧在‧冥‧想。至少，我想要冥想。$b$b你去找水仙聊聊好嗎？他超愛講話。', '', 38134),
(202076, 'zhTW', '昇靈堡某處有個特別的禱鐘。許久不曾敲響。\n\n這些禱鐘會在琪瑞安族舉行重大儀祭時派上用場。不過，我們用來敲響這些禱鐘的水晶槌大多都已經毀壞。\n\n如果有人能找回碎片，我就能鑄造全新水晶槌。', '', 38134),
(202075, 'zhTW', '謝天謝地，你來了。', '謝天謝地，你來了。', 38134),
(202074, 'zhTW', '謝天謝地，你來了。我取走了一部分多餘的靈魄，並想出了一個絕妙的計策，可以把我們周遭的這些灰燼回收再利用。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[灰燼罈]|h|r：擊敗數個敵人後，你會召來灰燼爬行者為你而戰。他無法被治療，但你每擊敗一個敵人，就能提高他的傷害並治療他。', '謝天謝地，你來了。我取走了一部分多餘的靈魄，並想出了一個絕妙的計策，可以把我們周遭的這些灰燼回收再利用。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Misc_Food_Legion_gooBloodGel_Bottle.blp:20|t|cFFFF0000|Hspell:335238|h[灰燼罈]|h|r：擊敗數個敵人後，你會召來灰燼爬行者為你而戰。他無法被治療，但你每擊敗一個敵人，就能提高他的傷害並治療他。', 38134),
(202073, 'zhTW', '別出聲。', '別出聲。', 38134),
(202072, 'zhTW', '不要吵鬧，我就可以幫助你消滅這些惡徒。我替他們除去了一部分悲慘的鏡子，你可以利用這點來幫助我們。\n\n使用我的強化，你將會獲得：\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[可攜式鏡面組]|h|r：你的傷害性法術和技能有一定機率召出鏡子裂片，在你面前聚焦光線，形成光束，對敵人造成火焰傷害，並使他們起火燃燒，額外造成持續性傷害。', '不要吵鬧，我就可以幫助你消滅這些惡徒。我替他們除去了一部分悲慘的鏡子，你可以利用這點來幫助我們。\n\n使用我的強化，你將會獲得：\n\n|TINTERFACE\\ICONS\\TRADE_ARCHAEOLOGY_HIGHBORNESOULMIRROR.BLP:20|t|cFFFF0000|Hspell:335238|h[可攜式鏡面組]|h|r：你的傷害性法術和技能有一定機率召出鏡子裂片，在你面前聚焦光線，形成光束，對敵人造成火焰傷害，並使他們起火燃燒，額外造成持續性傷害。', 38134),
(202071, 'zhTW', '汎希爾！我們一直藏身在隱蔽者之中，抱持著你能幫我們的希望。我為你偷了不少靈魄。', '汎希爾！我們一直藏身在隱蔽者之中，抱持著你能幫我們的希望。我為你偷了不少靈魄。', 38134),
(202070, 'zhTW', '汎希爾！我們一直藏身在隱蔽者之中，抱持著你能幫我們的希望。我偷來了一些靈魄讓你使用。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[渴求靈魄]|h|r：在低生命力時，你的傷害性法術和技能有很高機率在短時間內使敵人昏迷，並在持續時間內恢復自身生命力，同時對敵人造成傷害。\n\n此效果每分鐘只能觸發一次。', '汎希爾！我們一直藏身在隱蔽者之中，抱持著你能幫我們的希望。我偷來了一些靈魄讓你使用。\n\n使用我的強化，你將會獲得：\n\n|TInterface\\ICONS\\Spell_AnimaRevendreth_Beam.blp:20|t|cFFFF0000|Hspell:335240|h[渴求靈魄]|h|r：在低生命力時，你的傷害性法術和技能有很高機率在短時間內使敵人昏迷，並在持續時間內恢復自身生命力，同時對敵人造成傷害。\n\n此效果每分鐘只能觸發一次。', 38134),
(202066, 'zhTW', '我再也無法…待在淵喉了。把靈魂都帶回亞登曠野…', '', 38134),
(202056, 'zhTW', '瑞贊，我不會白費你的禮物。你的名號將傳頌下去…直到永遠。', '', 38134),
(202054, 'zhTW', '你才是…他們需要的…領袖…', '', 38134),
(202040, 'zhTW', '淵喉行者，打破屏障！', '', 38134),
(202039, 'zhTW', '我們現在就要為被控制的同胞復仇！為了亞登曠野！', '', 38134),
(202038, 'zhTW', '這是什麼巫術？！殺掉她的爪牙！我會保護野性種子！', '', 38134),
(202037, 'zhTW', '瑞贊！羅亞之王！我們需要破壞屏障！', '', 38134),
(202035, 'zhTW', '還剩下一位羅亞…幾乎消失了…', '', 38134),
(202034, 'zhTW', '沙德拉！伯昂撒姆第告訴我，她被自己的女祭司吞噬了…', '', 38134),
(202033, 'zhTW', '這邊還有更多羅亞。快點！', '', 38134),
(202032, 'zhTW', '希爾雷克！我聽過他在納茲米爾死去的尖嘯。', '', 38134),
(202029, 'zhTW', '你幫我帶了什麼來嗎？', '', 38134),
(202027, 'zhTW', '', '站住！', 38134),
(202024, 'zhTW', '', '我不在的這段時間發生了好多事。\n\n我需要時間才有可能再次恢復自我，或許永遠都沒辦法。\n\n$n，謝謝你救了我。', 38134),
(202021, 'zhTW', '很榮幸能幫助西卡。我會確保她接下來的補給會準時抵達、完好無缺。', '', 38134),
(202020, 'zhTW', '', '害蟲！你的屍體會很好用。', 38134),
(201999, 'zhTW', '', '瑟洛斯會吞噬這個世界！', 38134),
(201996, 'zhTW', '', '可悲的小東西！你的掙扎徒勞無功！', 38134),
(201995, 'zhTW', '', '我感受到獵物散發的莫大恐懼。', 38134),
(201994, 'zhTW', '', '他們殘留希望的化身。我們很接近了。', 38134),
(201993, 'zhTW', '', '他們破碎的希望暴露了行蹤。', 38134),
(201992, 'zhTW', '噢，對！我當然會幫忙。這可是協助熔爐士索芬妮的大好機會，說不定還能向她學習。', '噢，對！我當然會幫忙。這可是協助熔爐士索芬妮的大好機會，說不定還能向她學習。', 38134),
(201983, 'zhTW', '跟我來！必須救出倖存者！', '', 38134),
(201980, 'zhTW', '薩維斯，墮落的希瓦爾！作為艾薩拉議會的一員，他接受了翡翠夢魘的腐化，並為他的人民帶來毀滅。小心恐怖的暗夜懼獸的力量。', '', 38134),
(201979, 'zhTW', '『腐化者』恩若司！它派出的眼睛和魚無所不在，襲捲整個艾澤拉斯！你可以相信誰？哪條魚可以安心食用？直到今天，還是沒人知道它到底有多麼邪惡！', '', 38134),
(201978, 'zhTW', '珍娜‧普勞德摩爾，海軍總帥，冰之女王！她的冰風如同鑽石粉塵，凍結敵人，直到他們血管中的血液不再流動！她的敵人就此死去，成為她秘法戰艦上的船首像！', '', 38134),
(201977, 'zhTW', '古爾丹，暗影議會的領導者！「但他是如何回來的？他從哪裡回來？」他們問！不過真正的問題是：他何時回來的？還有他的顱骨到底怎麼了？', '', 38134),
(201976, 'zhTW', '欺詐者基爾加丹！沒有人記得加丹是誰，不過說不定這也是他的詭計？戰鬥發生在扭曲虛空之中，我們所知最為虛幻的維度！', '', 38134),
(201975, 'zhTW', '『滅界者』阿古斯！他們想創造死亡泰坦，但死亡是由暗影之境與我們偉大的女王掌管。這顯然不可行，所以冒險者們採取了極端的方法：毀滅世界本身。', '', 38134),
(201974, 'zhTW', '相信我這些殘存的弟兄！他們會保護我們的安全！', '', 38134),
(201971, 'zhTW', '可以順利完工！很好。接著是餘燼宴會。', '', 38134),
(201970, 'zhTW', '希望如此，謝謝你。', '', 38134),
(201969, 'zhTW', '', '我相當明白…自我省思的重要性。祝你能找到想要的事物。', 38134),
(201968, 'zhTW', '嗯，孩子，我原本以為棄誓者…說不定一開始我就錯了。我要找個平靜的地方，有太多事情要思考了。', '', 38134),
(201967, 'zhTW', '', '烏瑟，你接下來要去哪？想必不會回到棄誓者那邊。', 38134),
(201966, 'zhTW', '解決那些叛徒！', '', 38134),
(201965, 'zhTW', '我們要想辦法過去，索雷斯？', '', 38134),
(201964, 'zhTW', '保護凡人不應該是工作的一部分。', '保護凡人不應該是工作的一部分。', 38134),
(201963, 'zhTW', '謝謝你，他們不該受這種苦。', '', 38134),
(201962, 'zhTW', '我的工作是保護舊城門內的所有居民，甚至像你這樣的$r。或許我應該要求加薪。', '我的工作是保護舊城門內的所有居民，甚至像你這樣的$r。或許我應該要求加薪。', 38134),
(201960, 'zhTW', '如果我不需要這個職位以及隨之而來的報酬，我不會舉起武器捍衛凡人。', '如果我不需要這個職位以及隨之而來的報酬，我不會舉起武器捍衛凡人。', 38134),
(201959, 'zhTW', '莫格萊尼要我留守後方，免得你偏離路途太遠。\n\n這道死亡之門會帶你到他那裡。建議你使用它。', '', 38134),
(201958, 'zhTW', '', '淵喉行者，你先請。', 38134),
(201950, 'zhTW', '', '莫格萊尼要我留守後方，免得你偏離路途太遠。\n\n這道死亡之門會帶你到他那裡。建議你使用它。', 38134),
(201949, 'zhTW', '犯下這些恐怖罪行的人必須受到制裁。', '', 38134),
(201948, 'zhTW', '', '也許有一天我們能好好聊聊，現在，先逃離這個可怕的地方吧。', 38134),
(201947, 'zhTW', '克蕾雅，看來我們蠻像的。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201946, 'zhTW', '', '雖然我們是敵人，可是我不能眼睜睜看你受苦。', 38134),
(201945, 'zhTW', '很痛苦，不過我以前也經歷過，傷痛終究會痊癒的。', '', 38134),
(201944, 'zhTW', '', '烏瑟，感覺如何？', 38134),
(201943, 'zhTW', '我要感謝你們從萊索妮雅手上救我出來，要不是你們出手，我現在可能只剩一具空殼了。', '', 38134),
(201942, 'zhTW', '', '淵誓者一定急著要找到你。我們要速戰速決。', 38134),
(201940, 'zhTW', '在這場精彩的表演中，我們其中一位演員將在你們眼前化身為異世界的強大敵人。他隨時樂意與膽敢為敵的人作戰！', '', 38134),
(201939, 'zhTW', '', '等那頭野獸走遠。', 38134),
(201938, 'zhTW', '請記住，因為有像你這樣的贊助者和觀眾的支持，我們才有足夠的靈魄來追求藝術創作！就不浪費時間了…', '', 38134),
(201937, 'zhTW', '從深淵之中、浪潮之下，曾經美麗無比的艾薩拉女王攻擊著她的敵人。先引誘他們進入宮殿，然後用陷阱與無數的觸手擊潰他們。高招，女王！', '', 38134),
(201936, 'zhTW', '輕一點！她現在命懸一線…', '', 38134),
(201935, 'zhTW', '請注意，熱愛藝術的同好們。我很高興的宣布，多虧$n的慷慨贊助，新的表演很快會在星湖劇場上演。', '', 38134),
(201933, 'zhTW', '', '跟緊一點。我可不想和他們開戰，引起注意。', 38134),
(201932, 'zhTW', '', '快點解決它！否則我們會失去她！', 38134),
(201931, 'zhTW', '', '我們必須把伊瑟拉的野性種子交給女王，這是她唯一的希望。', 38134),
(201930, 'zhTW', '', '我要重新集結蛛眼家族。趕緊行動。', 38134),
(201928, 'zhTW', '喔，真是啟迪人心的一天！我的心中響起創作之歌！我腦中充滿靈感，整個人雀躍不已！這真是個奇蹟，我們居然有了足夠的靈魄，能讓我有空再次進行創作。$b$b我已經辛勤創作許久，我知道這個表演一定會在亞登曠野造成轟動。你想看嗎？我嘔心瀝血的成果？', '', 38134),
(201926, 'zhTW', '唉呀，哎喲！$b$B啊，我的朋友。我餓了。不是想吃東西或喝東西，至少不是你習以為常的那種。我想要的是精神糧食！像是很有創意的藝術品，能讓我們昇華到連最勇猛的飛靈都飛不到的高度！$b$B喔，不過靈魄乾旱扼殺了我們的創意。我們反而還需要為了生存而汲汲營營。$B$B不過，告訴我，沒有藝術相伴的人生還有什麼樂趣可言？', '', 38134),
(201925, 'zhTW', '要爆了！動作快！', '', 38134),
(201924, 'zhTW', '', '此刻，速度比隱密更加重要，我們用飛的前往甬道石，早點逃離淵喉。', 38134),
(201923, 'zhTW', '掩護我！', '', 38134),
(201922, 'zhTW', '炸彈設好了！快退後！', '', 38134),
(201921, 'zhTW', '該來製造混亂了！', '', 38134),
(201920, 'zhTW', '這肯定威力驚人！', '', 38134),
(201909, 'zhTW', '朋友，你相信我了嗎？只要我們團結一心，就會變得所向無敵！', '', 38134),
(201908, 'zhTW', '啊啊！', '', 38134),
(201907, 'zhTW', '我們先一起從這裡擊退它們吧。然後將它們逐出所有國度！', '', 38134),
(201906, 'zhTW', '吞噬者！我們共同的敵人，不斷啃食所有國度的邊界！', '', 38134),
(201905, 'zhTW', '', '我們都必須牢記，救贖才是瑞文崔斯真正的使命。', 38134),
(201904, 'zhTW', '保住剩下的靈魂。可能的話，我們會幫助它們。', '', 38134),
(201903, 'zhTW', '帶著你能掌控的靈魂一起撤退！', '帶著你能掌控的靈魂一起撤退！', 38134),
(201902, 'zhTW', '', '如果可以的話，放過這些靈魂吧。我們或許有辦法讓它們恢復原狀！', 38134),
(201901, 'zhTW', '', '主宰的墮邪之力真是無邊無界！', 38134),
(201900, 'zhTW', '這些靈魂被主宰腐化了，並轉化為武器！', '', 38134),
(201899, 'zhTW', '說真的，我在挑選裝飾時沒選泥土香味…', '', 38134),
(201898, 'zhTW', '他們到底在做什麼？這個地方髒透了！', '', 38134),
(201897, 'zhTW', '夠亂了！每個泥掘者只顧自己！', '', 38134),
(201896, 'zhTW', '淵喉行者，把他們弄到外面去，還有誰遞個拖把給我！', '', 38134),
(201895, 'zhTW', '這些泥掘者身上都是泥巴！他們把裝飾都毀了！', '', 38134),
(201893, 'zhTW', '追到附近就停步。窮寇勿追！', '', 38134),
(201892, 'zhTW', '太專業了！我會確保這些軟泥能夠準時重組。', '', 38134),
(201891, 'zhTW', '啊，學徒！我們的軟泥怪不夠用了。你能不能去回收幾個過來？', '', 38134),
(201890, 'zhTW', '哈囉！瘟疫設計者瑪利雷斯派我來這裡測試這件裝備。我們正在收集這個區域裡的活軟泥怪。或至少是收集它們的樣本。想要幫忙嗎？', '', 38134),
(201888, 'zhTW', '當然不是我。但有些人是！', '', 38134),
(201887, 'zhTW', '退回要塞重整！', '', 38134),
(201886, 'zhTW', '淵喉行者真有先見之明，已經事先啟動我們的石源魔預備隊。飛上天吧！', '', 38134),
(201885, 'zhTW', '石源魔軍團！主宰派了他最強大的部隊來我們的宴會？', '', 38134),
(201884, 'zhTW', '你知道，有些人認為能得到石源魔軍團的注意是天大的榮耀。', '', 38134),
(201883, 'zhTW', '', '真是出色的敵對選擇，$p…', 38134),
(201875, 'zhTW', '這傢伙有辦法把靈魄注入瑪卓薩岩。但他也很害怕，有看到他的眼神嗎？', '這傢伙有辦法把靈魄注入瑪卓薩岩。但他也很害怕，有看到他的眼神嗎？', 38134),
(201874, 'zhTW', '這傢伙有辦法將靈魄注入瑪卓薩岩。\n\n從他身上取得強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[注魂的瑪卓薩岩]|h|r：你保留著一枚瑪卓薩岩裂片，吸收你所擊敗的敵人的靈魂。生命力降低至一定程度時，它會碎裂並擊退附近敵人，且賦予你護盾，持續一小段時間。\n\n此效果無法經常觸發，但每次你做出最後一擊時，會降低它的冷卻時間。', '這傢伙有辦法將靈魄注入瑪卓薩岩。\n\n從他身上取得強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_Staff_2H_MaldraxxusQuest_B_01.blp:20|t|cFFFF0000|Hspell:335041|h[注魂的瑪卓薩岩]|h|r：你保留著一枚瑪卓薩岩裂片，吸收你所擊敗的敵人的靈魂。生命力降低至一定程度時，它會碎裂並擊退附近敵人，且賦予你護盾，持續一小段時間。\n\n此效果無法經常觸發，但每次你做出最後一擊時，會降低它的冷卻時間。', 38134),
(201873, 'zhTW', '別亂碰！這傢伙有使用冰霜的能力。', '別亂碰！這傢伙有使用冰霜的能力。', 38134),
(201872, 'zhTW', '別亂碰！這傢伙具有冰霜之力。\n\n從他身上取得強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[極寒血肉]|h|r：當你受到傷害時，有一定機率獲得一層晶化靈魄，使你受到的傷害降低。堆疊至最高層數時受到傷害會凍結附近敵人，持續5秒，並使其受到的傷害提高。', '別亂碰！這傢伙具有冰霜之力。\n\n從他身上取得強化，你將會獲得：\n\n|TInterface\\ICONS\\INV_misc_permafrostshard.blp:20|t|cFFFF0000|Hspell:335040|h[極寒血肉]|h|r：當你受到傷害時，有一定機率獲得一層晶化靈魄，使你受到的傷害降低。堆疊至最高層數時受到傷害會凍結附近敵人，持續5秒，並使其受到的傷害提高。', 38134),
(201870, 'zhTW', '這傢伙有辦法召喚衰老的怪物。超噁心！', '這傢伙有辦法召喚衰老的怪物。超噁心！', 38134),
(201869, 'zhTW', '這傢伙有能力召喚出腐朽的生物。\n\n從他身上取得強化，你將會獲得：\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[指揮骷髏]|h|r：血肉工藝的被動效果消耗屍體精華時，不時會召喚骷髏到你身邊一同奮戰。', '這傢伙有能力召喚出腐朽的生物。\n\n從他身上取得強化，你將會獲得：\n\n|TINTERFACE\\ICONS\\INV_MISC_BONE_HUMANSKULL_01.BLP:20|t|cFFFF0000|Hspell:335042|h[指揮骷髏]|h|r：血肉工藝的被動效果消耗屍體精華時，不時會召喚骷髏到你身邊一同奮戰。', 38134),
(201868, 'zhTW', '', '烏瑟，我帶你去安全的地方。', 38134),
(201865, 'zhTW', '你好，朋友。', '你好，朋友。', 38134),
(201864, 'zhTW', '我該如何為你效勞？', '我該如何為你效勞？', 38134),
(201863, 'zhTW', '我能幫你什麼忙嗎？', '我能幫你什麼忙嗎？', 38134),
(201861, 'zhTW', '', '當然了，主人。這些御靈者的僕從根本不算什麼。', 38134),
(201860, 'zhTW', '', '別讓我失望，萊索妮雅。證明你有資格驅使剛剛獲得的力量。', 38134),
(201859, 'zhTW', '', '這些佐司特真是煩死人了！我們必須捍衛樹林和伊瑟拉的野性種子，否則就白費工夫了！', 38134),
(201858, 'zhTW', '', '索爾，你看起來不太對勁。', 38134),
(201857, 'zhTW', '', '要是有辦法離開淵喉，那就一定也有回來的方法。到時候我們再回來救她。', 38134),
(201855, 'zhTW', '', '我同意，但是我們目前束手無策。我們會盡力尋找她，但這個甬道石很有可能是大家唯一的出路。', 38134),
(201853, 'zhTW', '泰蘭妲還是行蹤不明。我們不能把她丟在這麼可怕的地方。', '', 38134),
(201851, 'zhTW', '她搶先一步跳進了裂隙。她發誓不計一切代價…也要找到希瓦娜斯‧風行者。', '', 38134),
(201850, 'zhTW', '還沒發現高階祭司的蹤跡。或許她領先了我們好幾步。', '', 38134),
(201849, 'zhTW', '你穿過裂隙了。很好，我們需要你才能完成這個任務。', '', 38134),
(201848, 'zhTW', '', '有個危機迫在眉睫。過來我這，我們一起面對。', 38134),
(201844, 'zhTW', '', '繆薩拉會嚐到我的怒火。我相信你，凡人…', 38134),
(201842, 'zhTW', '', '我還記得自己從森林裡面被撕扯出來。我拒絕協助那個卑劣的繆薩拉…然後就被丟到這裡面來了。\n\n要是我沒有失去力量的話，我早就把他的身體給撕裂開來、讓他死無全屍了！\n\n我必須回去那片森林。你能夠協助我嗎？', 38134),
(201841, 'zhTW', '', '祝福她的聲音。', 38134),
(201840, 'zhTW', '', '卡德倫，帶著諸位誓盟代表前往政要區。我和凡人在事情結束後，會在那裡與你會合。我代表她的話語。', 38134),
(201839, 'zhTW', '', '那我必須回應他們的召喚。淵喉行者，跟我來，或許他們能指引我們的道路。', 38134),
(201838, 'zhTW', '榮耀之聲！永恆者召喚你前去審斷者殿堂！', '', 38134),
(201837, 'zhTW', '看來閻獄之主…一直以來都在牽動著我們世界的命運。', '', 38134),
(201836, 'zhTW', '', '使命請救救我們！情況比我們以為的還要糟。', 38134),
(201835, 'zhTW', '戴納瑟斯造成這場靈魄乾旱，還將他豐沛的庫存提供給淵喉。他和閻獄之主互相勾結。', '', 38134),
(201834, 'zhTW', '', '有股熟悉的能量將我拉向西方，不管萊索妮雅和她的主人想做什麼，我們都要制止他們。', 38134),
(201833, 'zhTW', '', '你可別讓我後悔。', 38134),
(201832, 'zhTW', '', '到蛛眼家族附近和我碰面。這類型的任務需要一點…技巧。', 38134),
(201831, 'zhTW', '', '沒有亡城，我們就別想守住統御者之座。我們要從敵人那裡奪過來。', 38134),
(201830, 'zhTW', '', '不，到此為止了！', 38134),
(201829, 'zhTW', '', '你們卻選擇了竊取和踐踏生命。真是糟蹋。我為你感到可憐。', 38134),
(201828, 'zhTW', '', '佐司特本來能成為自然的守護者。生命循環的管理人。', 38134),
(201826, 'zhTW', '', '一個接一個，我們會奪走每座林地、每個妖精、每個靈魂。', 38134),
(201825, 'zhTW', '', '但我們變得更強了，而你的僕從卻只能逐漸凋零！', 38134),
(201824, 'zhTW', '', '真是自私的生物。除了自己的慾望之外，什麼也看不見。', 38134),
(201823, 'zhTW', '', '你會眼睜睜地看著我奪取你的王座，然後親手把森林之心捏成碎片！', 38134),
(201822, 'zhTW', '', '我不需要力量衰退的女王來憐憫我。', 38134),
(201820, 'zhTW', '曠野獵者，出發！為了亞登曠野！', '', 38134),
(201813, 'zhTW', '是亞夏曼！是來自先祖之戰的荒野神靈。', '', 38134),
(201812, 'zhTW', '我們有命令在身。好好作戰，昇靈堡的琪瑞安。', '', 38134),
(201811, 'zhTW', '你好，$c。我代表曠野獵者來這裡助陣，共同對抗我們的敵人。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[沃爾凱衝鋒]|h|r：敵人數量比你多的時候，你的法術和技能有一定機率召喚出沃爾凱朝你的目標衝鋒，造成自然傷害並使路徑上的敵人昏迷。', '你好，$c。我代表曠野獵者來這裡助陣，共同對抗我們的敵人。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\Spell_unused2.blp:20|t|cFFFF0000|Hspell:334539|h[沃爾凱衝鋒]|h|r：敵人數量比你多的時候，你的法術和技能有一定機率召喚出沃爾凱朝你的目標衝鋒，造成自然傷害並使路徑上的敵人昏迷。', 38134),
(201810, 'zhTW', '有了我的加持，大地都會崛起來消滅亞登曠野的敵人。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[木靈之怒]|h|r：你的法術和技能有很高的機率喚來大地裂片，使你的敵人因流血每秒受到自然傷害，效果可堆疊。堆疊最高層數的敵人死後會爆炸，對附近敵人造成自然傷害。', '有了我的加持，大地都會崛起來消滅亞登曠野的敵人。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\Achievement_BG_win_AV_X_times.blp:20|t|cFFFF0000|Hspell:334536|h[木靈之怒]|h|r：你的法術和技能有很高的機率喚來大地裂片，使你的敵人因流血每秒受到自然傷害，效果可堆疊。堆疊最高層數的敵人死後會爆炸，對附近敵人造成自然傷害。', 38134),
(201809, 'zhTW', '我能幫助在地面上走，或跑，或跳的生物學習如何讓自己步伐輕盈，$r。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[欺詐者的動作]|h|r：在離開戰鬥後，使用靈魂幻體會讓你避開敵人的注意。', '我能幫助在地面上走，或跑，或跳的生物學習如何讓自己步伐輕盈，$r。\n\n接受我的幫助，你將會獲得：\n\n|TInterface\\ICONS\\ACHIEVEMENT_GUILDPERK_QUICK AND DEAD.BLP:20|t|cFFFF0000|Hspell:334540|h[欺詐者的動作]|h|r：在離開戰鬥後，使用靈魂幻體會讓你避開敵人的注意。', 38134),
(201802, 'zhTW', '記得要緊抓著與艾澤拉斯連結的線，不要在黑暗之中顯露靈魂。要不然你的靈魂可能噗一聲就不見了！', '', 38134),
(201789, 'zhTW', '立刻通知凜冬女王。要讓她知道這件事。', '', 38134),
(201788, 'zhTW', '', '但那些被奪走的人已經無法挽回了。勾拉札爾要為她對我的世界造成的傷害付出高昂的代價。', 38134),
(201787, 'zhTW', '', '$n，真是可怕的消息。我原本希望能找到方法解救被佐司特附身的人。', 38134),
(201779, 'zhTW', '有了我的加持，大地都會崛起來消滅亞登曠野的敵人。', '有了我的加持，大地都會崛起來消滅亞登曠野的敵人。', 38134),
(201774, 'zhTW', '我能幫助在地面上走，或跑，或跳的生物學習如何讓自己步伐輕盈，$r。', '我能幫助在地面上走，或跑，或跳的生物學習如何讓自己步伐輕盈，$r。', 38134),
(201773, 'zhTW', '精彩的打鬥！該辦正事了。', '', 38134),
(201772, 'zhTW', '二對一？對你們來說…還真不公平！', '', 38134),
(201771, 'zhTW', '我會守住這裡。去吧！', '', 38134),
(201770, 'zhTW', '', '完成了。我會堅守這裡。', 38134),
(201766, 'zhTW', '', '這裡我來就行了。快走！', 38134),
(201765, 'zhTW', '', '亮火花？葛文？怎麼連你們也…', 38134),
(201764, 'zhTW', '', '快點，幫我控制錨點！', 38134),
(201763, 'zhTW', '', '$p，幫我奪回錨點。', 38134),
(201762, 'zhTW', '', '我會把你撕成碎片！', 38134),
(201747, 'zhTW', '我真是太高興了。凱文最需要的就是外出研究，而且同事們一定會感激我們這麼體貼，幫他們帶軟泥怪回來！', '', 38134),
(201744, 'zhTW', '', '抱歉，我必須休息一下。', 38134),
(201741, 'zhTW', '', '我追蹤萊索妮雅來到這座大門後方。我當時看到的…並不樂觀。', 38134),
(201737, 'zhTW', '噢，真是煩上加煩。救命啊！有什麼人聽得到我嗎？', '', 38134),
(201730, 'zhTW', '跟朋友一起參加派對！這是最棒的。', '', 38134),
(201727, 'zhTW', '我們需要石源魔的支援才能進行下一步。', '', 38134),
(201726, 'zhTW', '那些巫妖把他們變成石頭了。', '', 38134),
(201724, 'zhTW', '你的骨頭會用來裝飾繆薩拉的王座！', '', 38134),
(201722, 'zhTW', '碾碎所有反抗繆薩拉的人！', '', 38134),
(201721, 'zhTW', '夠了！我在此召喚恐怖泥沼的死者！', '', 38134),
(201720, 'zhTW', '你好，$c。我代表曠野獵者來這裡助陣，共同對抗我們的敵人。', '你好，$c。我代表曠野獵者來這裡助陣，共同對抗我們的敵人。', 38134),
(201718, 'zhTW', '繆薩拉…一定會贏的…', '', 38134),
(201717, 'zhTW', '繆薩拉詛咒你…', '', 38134),
(201716, 'zhTW', '你們…不會有希望的…', '', 38134),
(201715, 'zhTW', '繆薩拉…會…再次崛起…', '', 38134),
(201713, 'zhTW', '別…再一次…', '', 38134),
(201708, 'zhTW', '在繆薩拉的眼裡，你根本不算什麼！', '', 38134),
(201705, 'zhTW', '你會跟伯昂撒姆第一起消失！', '', 38134),
(201704, 'zhTW', '沒有人敢背叛繆薩拉！', '', 38134),
(201703, 'zhTW', '繆薩拉會再次統治一切！', '', 38134),
(201701, 'zhTW', '我們會釋放主人的！', '', 38134),
(201695, 'zhTW', '甬道石成功了！這裡實在…太壯觀了！', '', 38134),
(201694, 'zhTW', '我會待在你身邊。願大地之母指引我們的道路。', '', 38134),
(201693, 'zhTW', '甬道石！希望有你的協助，我也能透過它離開這裡。', '', 38134),
(201690, 'zhTW', '不過，就像你看到的，我還有很多東西要學，不然我沒有勇氣接近珊納。', '', 38134),
(201689, 'zhTW', '噢，謝謝你，感謝你讓我重回安全處境。這些狡猾的木魈很會惹麻須！', '', 38134),
(201637, 'zhTW', '整個贊達拉都在讚美伯昂撒姆第！沒有閻獄之主，你就只是個被遺忘的羅亞。可憐哪，繆薩拉！', '', 38134),
(201636, 'zhTW', '', '回到我的巢穴找我吧，我會盡可能協助你們。', 38134),
(201635, 'zhTW', '我的部隊會來釋放我…到時候你們都會完蛋。', '', 38134),
(201634, 'zhTW', '', '珊卓亞在澤瑞奎斯等我們。出發吧。', 38134),
(201631, 'zhTW', '喔，天啊，喔，我的天，水裡發生某種怪現象。', '', 38134),
(201630, 'zhTW', '$n，能再次見到你真是太好了。伯昂撒姆第知道他能仰賴你。\n\n若要徹底阻止繆薩拉的部隊，我們還有很多工作要做。', '', 38134),
(201629, 'zhTW', '我會在空中偵察地面的戰況。', '', 38134),
(201628, 'zhTW', '沒有人能進入牆內不被我發現。你休想離開！', '', 38134),
(201627, 'zhTW', '你以為自己很聰明？打造一面鏡子來入侵我的領域？', '', 38134),
(201626, 'zhTW', '', '你好，朋友。我是柳絮。亞登曠野現在的情況很糟糕對吧？\n\n<她壓低了聲音靠近你。>\n\n我正準備對月莓女士報一箭之仇，不過別告訴她。我們一直互相捉弄，這次我一定要讓她好看！\n\n我有個計劃，不過我必須潛入劇場的後台，才有辦法實行。或許你能幫個忙。', 38134),
(201622, 'zhTW', '這個號角可以通知黯刃騎士集中攻擊你的對手。好好使用它。', '', 38134),
(201620, 'zhTW', '王子，我來護送淵喉行者。$n，我們走吧。', '', 38134),
(201619, 'zhTW', '不管從多高的地方丟下去，這個陷阱都會保證他們的安全。免得你心裡過不去。', '', 38134),
(201618, 'zhTW', '把他們丟出派對！', '', 38134),
(201617, 'zhTW', '這個陷阱會讓小偷動彈不得，讓你把他們移到比較…合適的位置上。', '', 38134),
(201615, 'zhTW', '我並不怎麼感到訝異，我早就料到那些竊賊會想要偷走我們的舶來品。\n\n塔商集團為了因應這種狀況，研發出了非常有效的防竊技術。', '', 38134),
(201613, 'zhTW', '淵喉行者！下面需要你！', '', 38134),
(201612, 'zhTW', '然後，為什麼那位泥掘者要在你背後撬那個籠子上的鎖？', '', 38134),
(201611, 'zhTW', '不需要。這裡沒有小偷。', '', 38134),
(201610, 'zhTW', '', '刺骨寒風變成舒和微風。去吹拂其他人吧。協助這片曠野。', 38134),
(201609, 'zhTW', '我受夠這個地方了。我們走吧。', '', 38134),
(201608, 'zhTW', '這場折磨…總算結束了。', '', 38134),
(201604, 'zhTW', '退後！它們也會將你束縛！', '', 38134),
(201603, 'zhTW', '喔！我正好需要一場派對來恢復我的力量。然後我就會回到戰場上！', '', 38134),
(201602, 'zhTW', '付筆小錢，我就帶你見識一下塔商集團對付小偷的手段。', '', 38134),
(201600, 'zhTW', '好。丘發離開的時候我會一起走。我先打個盹。', '', 38134),
(201599, 'zhTW', '罪殞城的弟兄，跟我來！攻下瑪卓薩斯！', '', 38134),
(201598, 'zhTW', '同胞被屠殺，太陽之井被玷汙，只為了讓一個不死巫妖重生。', '', 38134),
(201595, 'zhTW', '謝謝你，朋友，多虧有你，這一切才能順利進行。', '', 38134),
(201594, 'zhTW', '我很欣慰柳絮的生命能夠延續下去，成為每位新晉升者的一部分。', '', 38134),
(201593, 'zhTW', '把容器放在這裡，$n。', '', 38134),
(201591, 'zhTW', '來吧，我們去晉升紋章那兒找佩拉苟斯。', '', 38134),
(201590, 'zhTW', '亞登曠野的人為我們付出了崇高的犧牲，我衷心慚愧。', '', 38134),
(201589, 'zhTW', '等等！前面那是什麼？', '', 38134),
(201588, 'zhTW', '海軍總帥居然能擊敗這種怪物，真令人佩服。', '', 38134),
(201587, 'zhTW', '最好不要解除這個魔法。我們繼續前進吧。', '', 38134),
(201586, 'zhTW', '又有普勞德摩爾女士的「足跡」。', '', 38134),
(201585, 'zhTW', '這場衝突似乎朝那邊去了。最好在這些軌跡…融化前跟上去。', '', 38134),
(201584, 'zhTW', '冰魔法。看來這是我們的海軍總帥留下的。', '', 38134),
(201582, 'zhTW', '', '亞拉隆認為這件事情是自己的錯。我擔心他會被自責壓垮。', 38134),
(201581, 'zhTW', '琦娃住在日暮林地附近的小窩裡。我們去那裡碰頭。', '', 38134),
(201580, 'zhTW', '我們會想辦法救妳的，葛薇亞。我保證…', '', 38134),
(201579, 'zhTW', '', '她目前睡著了…但睡夢中仍不得安寧。', 38134),
(201578, 'zhTW', '一定要阻止勾拉札爾和她的追隨者。在暗影之境找到琦娃，並結束這道詛咒。', '', 38134),
(201577, 'zhTW', '', '戈爾伊納是你的葬身之地！', 38134),
(201576, 'zhTW', '', '可悲的凡人。那個愚蠢的棘語者把你引來我的手掌心了。', 38134),
(201575, 'zhTW', '就算勾拉圖爾已經死了，他的黑暗存在依舊滯留在那裡。', '', 38134),
(201574, 'zhTW', '把這個塑像放在戈爾伊納的大鍋裡，但是要小心…', '', 38134),
(201573, 'zhTW', '', '如果真有方法能夠破除佐司特的詛咒，烏爾法一定會知道。去請教他吧。', 38134),
(201572, 'zhTW', '', '年輕的女獵手可能還有希望…', 38134),
(201571, 'zhTW', '向赫恩勛爵回報。我會帶葛薇亞到安全的地方。', '', 38134),
(201570, 'zhTW', '準備好就戴上面具吧。我們必須找到其他人。', '', 38134),
(201569, 'zhTW', '我們需要偽裝。在這個區域搜尋可以利用的東西。', '', 38134),
(201567, 'zhTW', '我們不能再失去更多獵手了，去找他們、帶他們回家。', '', 38134),
(201564, 'zhTW', '', '守住陣地。大聲呼叫支援！', 38134),
(201563, 'zhTW', '巡邏人員增加一倍！誰也別想逃！', '', 38134),
(201562, 'zhTW', '', '讓他們看看瑪卓薩斯怎麼處理叛徒！', 38134),
(201561, 'zhTW', '為主宰…復仇！', '為主宰…復仇！', 38134),
(201560, 'zhTW', '所有守衛全力追緝逃脫的囚犯！', '', 38134),
(201559, 'zhTW', '鏡牢失效了！', '', 38134),
(201558, 'zhTW', '請代我感謝雷納瑟王子的邀請。我一定會出席餘燼宴會。', '', 38134),
(201557, 'zhTW', '是的，楷模。我會克服心中不安。', '', 38134),
(201556, 'zhTW', '很有可能。我很高興你有這個機會提升自我，同時為御靈者效力。', '', 38134),
(201555, 'zhTW', '絕對沒這回事。不過當然有人比我更適合這種愉快的活動吧？', '', 38134),
(201554, 'zhTW', '審斷者是最後的鑰匙。保護好她，不然一切都完了。', '', 38134),
(201553, 'zhTW', '請把我的警告帶給御靈者、凜冬女王和國王。他們一定要守住防線。不要讓佐瓦爾抵達瑟伯切爾。', '', 38134),
(201552, 'zhTW', '要拯救暗影之境，只剩下最後一個希望。你必須趕在閻獄之主逃離淵喉之前，讓永恆者們再次集結起來。', '', 38134),
(201551, 'zhTW', '如果你收到這則訊息，就代表我的懷疑是正確的。佐瓦爾把他的鎖鏈鑄成了武器…並擊敗了我。', '', 38134),
(201549, 'zhTW', '我們是天選者。我們依靠軍事的力量。我們必須集結最驍勇善戰的士兵…包括失蹤的男爵。', '', 38134),
(201548, 'zhTW', '但我擔心，佐瓦爾並非單獨行動。我懷疑他有古老盟友的協助…而且還會繼續尋找盟友加入他。', '', 38134),
(201547, 'zhTW', '許久以前，我們的兄弟佐瓦爾因為背叛，受到了永恆者的懲罰。他被束縛在淵喉裡無法逃脫，成為永遠的閻獄之主。', '', 38134),
(201545, 'zhTW', '我是統御者。你來到了我的聖所，這就表示黑暗降臨在瑪卓薩斯…還有整個死亡領域。', '', 38134),
(201544, 'zhTW', '啊，阿德雷特斯。聽說你是連雷納瑟王子都敢拒絕的超級大人物。還是我誤會你了？', '', 38134),
(201542, 'zhTW', '耐心，需要學習省思。\n\n知識，需要全新觀點。\n\n洞見，需要三思慎選。\n\n判斷，需要深究真相。\n\n這就是智慧之路。', '耐心，需要學習省思。\n知識，需要全新觀點。\n洞見，需要三思慎選。\n判斷，需要深究真相。\n\n這就是智慧之路。', 38134),
(201538, 'zhTW', '能參加是我的榮幸。雖然，本來要邀請的客人是軍政官阿德雷特斯。', '', 38134),
(201536, 'zhTW', '向人求助並不可恥。', '', 38134),
(201535, 'zhTW', '謝謝你，$p！  我沒什麼能給你的，請收下月視的能力吧，就當作是回報你所做的一切。拿回我的工具後，又能繼續花園的工作了。', '謝謝你，$p！  我沒什麼能給你的，請收下月視的能力吧，就當作是回報你所做的一切。拿回我的工具後，又能繼續花園的工作了。', 38134),
(201534, 'zhTW', '', '就算要付出慘痛的代價，也一定要恢復平衡。謝謝你。', 38134),
(201531, 'zhTW', '我的籃子、笛子、錘子和魔杖好像都不見了。噢，還有我的日記也是！要是被木魈得逞，花園的歷史將會被抹殺掉…\n\n如果你能夠幫忙找到我的東西的話，我雖然不知道有沒有辦法報答這份恩情，但我會永遠感謝你的。', '我的籃子、笛子、錘子和魔杖好像都不見了。噢，還有我的日記也是！要是被木魈得逞，花園的歷史將會被抹殺掉…\n\n如果你能夠幫忙找到我的東西的話，我雖然不知道有沒有辦法報答這份恩情，但我會永遠感謝你的。', 38134),
(201530, 'zhTW', '真的嗎？你的確看起來很強，$pc，不過這很危險，我不希望你出任何事。', '真的嗎？你的確看起來很強，$pc，不過這很危險，我不希望你出任何事。', 38134),
(201529, 'zhTW', '待會見！', '', 38134),
(201526, 'zhTW', '都是因為我，葛薇亞才會被佐司特抓走…', '', 38134),
(201525, 'zhTW', '「判斷，需要深究真相。」\n\n就算通曉此道，我還是老覺得不太對勁。我一定是遺漏了真正的答案。', '「判斷，需要深究真相。」\n\n就算通曉此道，我還是老覺得不太對勁。我一定是遺漏了真正的答案。', 38134),
(201524, 'zhTW', '「洞見，需要三思慎選。」\n\n我必須繼續冥想，才能參透這一點。', '「洞見，需要三思慎選。」\n\n我必須繼續冥想，才能參透這一點。', 38134),
(201523, 'zhTW', '「知識，需要全新觀點。」\n\n那我該看些什麼？', '「知識，需要全新觀點。」\n\n那我該看些什麼？', 38134),
(201522, 'zhTW', '「耐心，需要學習省思。」\n\n我需要停下來思考。急躁無濟於事。', '「耐心，需要學習省思。」\n\n我需要停下來思考。急躁無濟於事。', 38134),
(201521, 'zhTW', '嚇啊啊啊啊！', '', 38134),
(201520, 'zhTW', '元素啊…聽我號令。賜予我力量…', '', 38134),
(201519, 'zhTW', '', '我到底得打敗你幾次才夠？', 38134),
(201518, 'zhTW', '', '夠了！', 38134),
(201513, 'zhTW', '我們的雙眼已經習慣了月光，這使我們能夠發現許多被隱藏起來的秘密。\n\n月視能夠讓你看見只有飛靈才能看見的東西。', '我們的雙眼已經習慣了月光，這使我們能夠發現許多被隱藏起來的秘密。\n\n月視能夠讓你看見只有飛靈才能看見的東西。', 38134),
(201512, 'zhTW', '喔，學徒！你在這裡呀！', '', 38134),
(201511, 'zhTW', '', '這些戴面具的妖精突襲了林地。拜託，我們得把他們趕出去！', 38134),
(201510, 'zhTW', '', '我明白聯手襲擊的價值，所以我會參加這場餘燼宴會，並在討論中代表曠野獵者。', 38134),
(201509, 'zhTW', '', '他知道你在這…', 38134),
(201508, 'zhTW', '', '<幻飛的表情變得苦澀。>\n\n失去神木是個可怕的悲劇。當靈魄不夠供應給整座森林使用時，女王會選擇讓林木死去，好讓其他神木能夠繼續存活。\n\n雖然殘酷，但我們別無選擇。\n\n神木枯死時，受到它庇護的所有野性種子也會陷入危機。有些種子會被送去其他林地，不過剩下的就…\n\n更糟的是，有人說在死亡的林地，亞登曠野與其他界域間的屏障會特別薄弱。\n\n他們說，外敵最喜歡攻擊那些地方。', 38134),
(201507, 'zhTW', '', '說得沒錯！木靈是又高又大、會走路會說話的樹人。\n\n他們似乎跟亞登曠野的林木有關聯，不過他們非常古老，也非常奇特，所以詳細情況我們也不清楚。\n\n某些木靈甚至能聽到樹木跟他們說話！據我瞭解，他們溝通時，不像是在說話，比較像是在唱歌。\n\n太有趣了！', 38134),
(201505, 'zhTW', '', '沒錯！每棵神木都有名字！\n\n我們在冬日窪地的神木名叫特那葛倫。北方的林地「特那瓦勒」的名字也是來自那裡的神木。\n\n「傾倒之樹」特那諾不久前因為靈魄乾旱而乾枯，心木林地的特那羅文最近才剛剛枯死。\n\n其他目前還存活的神木包括利爪之緣林地的特那凱司。\n\n還有爍瀑盆地的神木「特那伊斯」。以及我家鄉夢歌沼地的神木「特那芬」！\n\n不過其他的神木都已經枯死，像是黑暗之境的「特那馬沃」。\n\n當然還有特那希，不過最好不要靠近那裡，那裡實在太危險了！', 38134),
(201500, 'zhTW', '', '你好。對於我們的樹木，你有什麼想知道的？', 38134),
(201490, 'zhTW', '', '$n，謝謝你。我真的…欠你太多了。', 38134),
(201489, 'zhTW', '', '你說得對，大領主。我相信你的判斷。', 38134),
(201488, 'zhTW', '恕我直言，普勞德摩爾女士，妳需要好好休養。讓淵喉行者去繼續尋找其他人吧。', '', 38134),
(201487, 'zhTW', '', '…嗯，我懂你的意思。有索爾和安杜因的消息嗎？我們得回到托迦司，然後…', 38134),
(201486, 'zhTW', '珍娜，很高興見到你平安無事。雖然我在復原中，但我得承認，淵喉的恐怖記憶揮之不去。', '', 38134),
(201485, 'zhTW', '', '貝恩！感謝聖光，你還活著！', 38134),
(201477, 'zhTW', '', '我們必須集中精神。使命必將完成。', 38134),
(201476, 'zhTW', '維拉斯，德拉卡！把凡人帶到統御者之座。來看看能發現什麼。', '', 38134),
(201475, 'zhTW', '首先你帶來戰爭，現在還加上統御者之刃。凡人，你真是個傳奇！', '', 38134),
(201474, 'zhTW', '希望努力能得到回報。還有一些靈魄能用。', '', 38134),
(201473, 'zhTW', '安杜因…是武器？這是什麼意思？', '', 38134),
(201464, 'zhTW', '我安全逃出來了，但是在一陣混亂之中，我丟失了一些東西。\n\n木魈一定會把我的工具砸個稀巴爛的，我很確定！\n\n我可能沒辦法自己回去把工具拿回來，這樣的話，花園就沒人照料了…', '我安全逃出來了，但是在一陣混亂之中，我丟失了一些東西。\n\n木魈一定會把我的工具砸個稀巴爛的，我很確定！\n\n我可能沒辦法自己回去把工具拿回來，這樣的話，花園就沒人照料了…', 38134),
(201462, 'zhTW', '木魈發動攻擊時，我人正在黑夜花園。  他們源源不絕地湧進花園，開始脅迫所有人…', '木魈發動攻擊時，我人正在黑夜花園。  他們源源不絕地湧進花園，開始脅迫所有人…', 38134),
(201454, 'zhTW', '並為死去的同胞伸張正義。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201443, 'zhTW', '讓這頂王冠體現罪殞城的一切。瑞文崔斯會團結起來，然後從戴納瑟斯的罪孽中解放。', '', 38134),
(201427, 'zhTW', '', '襲擊的消息傳遍四方，我跟你一起行動。', 38134),
(201424, 'zhTW', '卡德倫，有件極度神聖的任務交付給我們倆個。', '', 38134),
(201422, 'zhTW', '遵命，榮耀之聲。', '', 38134),
(201421, 'zhTW', '', '必須開啟通往瑪卓薩斯的道路。召集卡德倫並辦好這件事。', 38134),
(201409, 'zhTW', '', '除非…除非這一切都是殘酷的詭計。', 38134),
(201408, 'zhTW', '', '要是我用傳送術配合你的能力來離開淵喉，我就可以徹底擺脫這個地方！', 38134),
(201407, 'zhTW', '', '真的是你？！沒錯…$n，我看到你用甬道石逃走了。彷彿已經是好久以前的事了…', 38134),
(201396, 'zhTW', '', '女王有令，我們聽命。', 38134),
(201395, 'zhTW', '', '$p，你就像是洗去一切塵埃的風暴。', 38134),
(201394, 'zhTW', '', '森林發出怒吼，樹枝隨之亂舞！', 38134),
(201392, 'zhTW', '我的楷模…我…讓你失望了…', '', 38134),
(201387, 'zhTW', '', '我們必須並肩奮戰才能活下來。堅守這裡！', 38134),
(201383, 'zhTW', '', '看見神聖楷模了！他隻身對抗萊索妮雅，他的侍徒怎麼不在？', 38134),
(201366, 'zhTW', '我先派克蕾雅去追萊索妮雅了，到謙遜神殿和她會合，動作要快。', '', 38134),
(201360, 'zhTW', '', '的確，死靈領主是戰爭的專家，但他們向來都用力量來保衛暗影之境，而不是破壞它。', 38134),
(201359, 'zhTW', '這真是噩耗。絕對不能讓世界陷入戰火、不能讓我們的努力付之一炬。', '', 38134),
(201347, 'zhTW', '聽從你的命令，女爵。', '', 38134),
(201345, 'zhTW', '希伯寇斯搖搖頭，伸手指向他原本長著舌頭的地方。', '', 38134),
(201343, 'zhTW', '希伯寇斯！他們對你…做了什麼？你能說話嗎？', '', 38134),
(201337, 'zhTW', '', '我們去向克利瑟斯回報。守住這裡，等我們回來。', 38134),
(201333, 'zhTW', '', '咕嚕寶安全了。他在特那瓦勒等你。', 38134),
(201332, 'zhTW', '寇達，你有看到咕嚕寶嗎？他走丟了！', '', 38134),
(201331, 'zhTW', '', '瓦許女爵譏笑。', 38134),
(201330, 'zhTW', '', '年輕人，別讓你的心受到影響了。或許還有別人能幫助我們拯救泰蘭妲‧語風。', 38134),
(201329, 'zhTW', '', '伊瑟拉，我…沒能把泰蘭妲帶回來。她還在淵喉徘徊，受到黑夜戰士之力、還有無盡的怒火所驅使。', 38134),
(201328, 'zhTW', '', '他們能在這裡休養生息。有些甚至能選擇轉化形態，協助照料林地。', 38134),
(201327, 'zhTW', '', '這些靈魂會怎麼樣？', 38134),
(201325, 'zhTW', '', '我差點失去了我的靈魂。但凜冬女王喚醒了我。我現在和這個世界緊密相連。', 38134),
(201324, 'zhTW', '', '怪物露出破綻了！攻下他！', 38134),
(201323, 'zhTW', '', '以伊露恩之名！帶這些靈魂前往安全的地方！', 38134),
(201322, 'zhTW', '', '拜託，淵喉行者！使用你身上的水晶拯救這些卡多雷的靈魂！', 38134),
(201321, 'zhTW', '', '至少這些卡多雷的靈魂自由了。經過這麼久，我終於又感受到…希望了。', 38134),
(201305, 'zhTW', '', '我們之中有些同胞訴說著很久以前爭奪這片土地的故事。\n\n但我沒經歷過那些光榮往事。\n\n但能在這裡也讓我感到與有榮焉…而且，如果我們沒有受到這些干擾的話，或許我會有時間享受這些佳餚。\n\n<艾曼妮舔了舔嘴唇。>\n\n我在暗影之境還沒看過這樣的盛宴呢。', 38134),
(201295, 'zhTW', '你覺得自己勇敢嗎，志士？\n\n我要你證明自己。', '你覺得自己勇敢嗎，志士？\n\n我要你證明自己。', 38134),
(201292, 'zhTW', '我是…永恆不死的。', '', 38134),
(201291, 'zhTW', '凡人，你太傲慢了！', '', 38134),
(201289, 'zhTW', '我們不能讓你…葬送在黑暗之中。', '', 38134),
(201287, 'zhTW', '', '沙洛！女王保佑，真高興見到你。', 38134),
(201286, 'zhTW', '噢嗚！至少是逃出來了。', '', 38134),
(201284, 'zhTW', '如果我們的家族在戰鬥中第一個敗亡，我們也沒有好處。我們必須全力作戰。', '', 38134),
(201279, 'zhTW', '去長矛之尖報到，新兵。我們有場仗要贏！', '', 38134),
(201278, 'zhTW', '你看！神殿周圍的屏障消失了。$n，幹得好。', '', 38134),
(201277, 'zhTW', '我想我找到出口了！但我可不要往那邊去！救救我！', '', 38134),
(201276, 'zhTW', '我先去前面偵察。你們別拖拖拉拉！', '', 38134),
(201272, 'zhTW', '如果他們是對的呢？如果…如果…', '如果他們是對的呢？如果…如果…', 38134),
(201271, 'zhTW', '', '而我會向送他們來到這裡的罪人復仇！', 38134),
(201270, 'zhTW', '', '如果是真的，那我將這神聖使命託付給你，$n。救救我同胞的靈魂。', 38134),
(201269, 'zhTW', '', '如果是真的，那我將這神聖使命託付給你，泰達希爾的孩子。救救我們同胞的靈魂。', 38134),
(201268, 'zhTW', '', '這位淵喉行者能夠拯救我們的兄弟姊妹脫離折磨。', 38134),
(201267, 'zhTW', '', '不！在拯救所有靈魂以前，我的狩獵不會結束。女妖要為罪行付出代價！', 38134),
(201266, 'zhTW', '', '而我要去為卡多雷復仇。', 38134),
(201265, 'zhTW', '', '隨便你。就讓這名部落勇士為他的族人贖罪吧。', 38134),
(201264, 'zhTW', '', '你不必信任他…但請你相信我。', 38134),
(201263, 'zhTW', '', '我原本也懷疑…但我看見他拯救了許多失落的靈魂。我相信他能幫助我們。', 38134),
(201262, 'zhTW', '', '從現在起，你必須自行尋找材料。如果需要指示就參考藍圖。', 38134),
(201261, 'zhTW', '痛苦…折磨…絕望…', '', 38134),
(201260, 'zhTW', '去吧。這裡的交給我照顧。', '', 38134),
(201259, 'zhTW', '', '我必須展開地毯式的搜索，尋找逃跑的動物。', 38134),
(201258, 'zhTW', '指定另一位琪瑞安族當晉升之路的對手，完全是個人選擇。希望你們不會違背我們的儀祭本質。', '', 38134),
(201257, 'zhTW', '', '你好，朋友。我很榮幸能協助你製作誘餌和靈魂之鏡。', 38134),
(201256, 'zhTW', '達提莉絲將會協助你製作晉升之路所需的所有物品。', '', 38134),
(201254, 'zhTW', '你可以在昇靈堡找到所有必備材料。然而，將來你可能需要前往暗影之境其他區域收集材料。', '', 38134),
(201253, 'zhTW', '', '我已經盡力拯救了。希望這樣足夠。', 38134),
(201250, 'zhTW', '為你效勞，首席熔爐士。', '', 38134),
(201249, 'zhTW', '我們有難同當。但是當昇靈堡需要幫忙時也請記得我們。', '', 38134),
(201248, 'zhTW', '首席熔爐士，真的感激不盡。沒有你的協助和信任，我們是不可能成功的。', '', 38134),
(201247, 'zhTW', '可以把王冠交給你們的主人了。戴納瑟斯的影響消失了。', '', 38134),
(201246, 'zhTW', '', '你惹的麻煩已經夠多了。', 38134),
(201245, 'zhTW', '', '自個游走，小東西。', 38134),
(201235, 'zhTW', '等到阿特米德報信之後，我的族人一定會召集軍隊，準備反擊。', '', 38134),
(201234, 'zhTW', '我會去那裡等你。請多保重。', '', 38134),
(201233, 'zhTW', '我們必須馬上前往奧睿博司。', '', 38134),
(201231, 'zhTW', '退後。必須孤立那些忠誠者。', '', 38134),
(201230, 'zhTW', '戴納瑟斯！淵喉行者，我們掩護你！', '', 38134),
(201220, 'zhTW', '你太放肆了！準備受死！', '', 38134),
(201219, 'zhTW', '你竟敢質疑我的統治權？', '', 38134),
(201218, 'zhTW', '', '不過我會對這個籠子施法，確保果姆不會再次逃跑。', 38134),
(201217, 'zhTW', '', '我不知道你是怎麼辦到的，淵喉行者…', 38134),
(201216, 'zhTW', '', '貴賓，你遠道而來，這裡有許多美好的事物在等待著你。\n身體放鬆，好好休息吧。', 38134),
(201215, 'zhTW', '沒時間了！你快點呈上王冠，暗影之境的忠誠者已經發現這裡了。', '', 38134),
(201210, 'zhTW', '準備好去神殿了嗎？', '', 38134),
(201199, 'zhTW', '是誰？你是來幫忙的嗎？', '', 38134),
(201196, 'zhTW', '足以洞察對手計畫的見識。', '', 38134),
(201195, 'zhTW', '足以擊潰對手的力量。', '', 38134),
(201194, 'zhTW', '那我們動作要快。神殿是解除戴納瑟斯控制的唯一管道。', '', 38134),
(201193, 'zhTW', '足以智取對手的計謀。', '', 38134),
(201192, 'zhTW', '神殿的屏障消失了。', '', 38134),
(201191, 'zhTW', '想要超越對手的野心。', '', 38134),
(201189, 'zhTW', '解決了。', '', 38134),
(201188, 'zhTW', '你的死期到了。', '', 38134),
(201187, 'zhTW', '面對你的失敗。', '', 38134),
(201186, 'zhTW', '你忘了往上看。', '', 38134),
(201183, 'zhTW', '佳賓斯啊，這些是你邀請的朋友嗎？真可愛。', '', 38134),
(201181, 'zhTW', '打得好！不管獵物躲在哪裡、跑得有多快，通通逃不出曠野獵者的手掌心！', '', 38134),
(201180, 'zhTW', '淵喉行者，我手下幾名獵人去霧紗密林巡邏了。如果你願意協助他們，一定能提振士氣。', '', 38134),
(201171, 'zhTW', '希奧塔…這位是你朋友嗎？', '', 38134),
(201170, 'zhTW', '不會吧！', '', 38134),
(201168, 'zhTW', '現在，請各位享用…這個到底是什麼東西…', '', 38134),
(201160, 'zhTW', '', '或許我應該騎乘巴爾薩札去餘燼宴會…', 38134),
(201157, 'zhTW', '今天我們要向克文科斯致敬！\n\n他努力不懈，勤於鍛造。\n\n他的工坊在永恆熔爐附近。\n\n請送個成熟的普恩果。', '', 38134),
(201156, 'zhTW', '我有薄靴！記得要裝備上去！', '', 38134),
(201141, 'zhTW', '<殞落楷模的殘影凝視著你。>\n\n你找到我故事中遺失的章節了嗎，凡人？', '<殞落楷模的殘影凝視著你。>\n\n你找到我故事中遺失的章節了嗎，凡人？', 38134),
(201140, 'zhTW', '如果你想學習更多東西，你必須幫我找回秘典。', '如果你想學習更多東西，你必須幫我找回秘典。', 38134),
(201137, 'zhTW', '我只是過往神聖楷模深奧知識的殘影，但我看得出來你對知識的渴望。\n\n我的故事並不完整。最後一章被我的侍徒藏起來了。棄誓者為了自身的利益想扭曲我的命運，但他們無法透過完整的故事做到這一點。要是他們找到的話，一定會摧毀的。\n\n我會准許你閱讀神聖秘典。還給我以後，你就會知道故事真正的結局。\n\n你覺得呢？', '我只是過往神聖楷模深奧知識的殘影，但我看得出來你對知識的渴望。\n\n我的故事並不完整。最後一章被我的侍徒藏起來了。棄誓者為了自身的利益想扭曲我的命運，但他們無法透過完整的故事做到這一點。要是他們找到的話，一定會摧毀的。\n\n我會准許你閱讀神聖秘典。還給我以後，你就會知道故事真正的結局。\n\n你覺得呢？', 38134),
(201135, 'zhTW', '來吧，咕嚕寶！該輪到我們拿出勇氣了！', '', 38134),
(201128, 'zhTW', '', '柳絮點點頭。', 38134),
(201124, 'zhTW', '將軍，空中有你就夠了。地面交給我。', '', 38134),
(201123, 'zhTW', '科爾蘇加德在遠處對我們叫囂，過不了多久，他的死靈法師就會…暴露他的藏身之處。', '', 38134),
(201117, 'zhTW', '', '你通過了重重難關，打得漂亮。', 38134),
(201115, 'zhTW', '', '柳絮在想她有沒有給佩拉苟斯正確的情報，然後聳聳肩。', 38134),
(201114, 'zhTW', '', '我要試試你的戰鬥技巧。', 38134),
(201113, 'zhTW', '', '我們來看看你學到多少。', 38134),
(201112, 'zhTW', '我也會幫忙，這個結界能提高你的生存機率。', '', 38134),
(201111, 'zhTW', '我會飛到天上提供支援。', '', 38134),
(201104, 'zhTW', '只剩最後一道程序了！', '', 38134),
(201098, 'zhTW', '', '你必須阻止萊索妮雅的儀式。我們不能讓她擊敗楷模。', 38134),
(201097, 'zhTW', '', '秋意高地就在北邊。我希望我們還來得及。', 38134),
(201096, 'zhTW', '', '這樣應該能爭取到一點時間…$n去調查棄誓者時，我會留在這裡聽你報告現況。', 38134),
(201095, 'zhTW', '', '力量強大的凱拉斯選擇以身犯險，把她從侍徒身邊引開。', 38134),
(201094, 'zhTW', '', '楷模就在神殿附近，孤身一人對抗萊索妮雅！', 38134),
(201093, 'zhTW', '', '跟著這條血腥路走，應該就能找到她。', 38134),
(201091, 'zhTW', '', '這都是我的尊師一手造成的。跟我夢見的一樣。', 38134),
(201085, 'zhTW', '', '從那天開始，就沒有靈魂得到審判。反之，淵喉的恐怖吞噬了一切。', 38134),
(201075, 'zhTW', '黯刃騎士的亞榭洛要塞移動到了冰冠城塞。我的死亡之門會帶領大家過去。', '', 38134),
(201074, 'zhTW', '勇士！我們準備好了。', '', 38134),
(201073, 'zhTW', '我親愛的珊納，那正好是我最擅長的動作。', '', 38134),
(201072, 'zhTW', '', '珊納朝伊文眨眨眼。', 38134),
(201071, 'zhTW', '', '那個失控般的旋轉看起來特別有趣。', 38134),
(201070, 'zhTW', '', '我很喜歡你先前做出的一、兩個動作，請務必教教我。', 38134),
(201069, 'zhTW', '當然可以，珊納女士。能與你共舞將會是今夜最大的光榮。', '', 38134),
(201068, 'zhTW', '伊文不禁臉紅，說話結結巴巴。', '', 38134),
(201067, 'zhTW', '', '我能與這位紳士共舞嗎？', 38134),
(201066, 'zhTW', '', '恕我冒昧，但我在遠處就注意到你的出色舞技。', 38134),
(201065, 'zhTW', '如果我能跳好這個舞，就能用它贏得珊納的心！', '', 38134),
(201064, 'zhTW', '謝謝你讓我學會跳舞，淵喉行者。', '', 38134),
(201063, 'zhTW', '我希望能親眼見證你的傳說。', '', 38134),
(201062, 'zhTW', '我會向維拉斯回報，然後跟你在統御者之座會合。', '', 38134),
(201060, 'zhTW', '', '無論泰蘭妲在塔內追獵何種邪惡怪物，我們都不該讓她獨自面對。', 38134),
(201059, 'zhTW', '', '我要毀掉那座塔，還有那該死的女妖！', 38134),
(201058, 'zhTW', '', '不行！我們的同胞還在托迦司裡受苦！', 38134),
(201057, 'zhTW', '', '尊師！跟我們一起離開淵喉…免得黑夜戰士的力量將你吞噬！', 38134),
(201056, 'zhTW', '現在是進攻的好時機！拿下科爾蘇加德。', '', 38134),
(201055, 'zhTW', '不要再玩聲東擊西的把戲了！你嚇阻不了我們的。', '', 38134),
(201054, 'zhTW', '$n，在那邊碰面。', '', 38134),
(201053, 'zhTW', '要解決問題，就得去忠誠者要塞。', '', 38134),
(201052, 'zhTW', '王冠屈服於戴納瑟斯殘留的意志影響。', '', 38134),
(201045, 'zhTW', '我沒事。我們要進入神殿才能修復王冠。必須關閉，屏障防護。', '', 38134),
(201044, 'zhTW', '$n，抱歉耽誤了一下，我花了些時間說服新盟友信任我的飛行能力。', '', 38134),
(201034, 'zhTW', '歡迎來到我們的林地。', '歡迎來到我們的林地。', 38134),
(201019, 'zhTW', '如果你認為這是明智的做法，那就照你說的吧。', '', 38134),
(201015, 'zhTW', '都是因為繆薩拉，黑爾雅才會加入閻獄之主…才能換得老歐丁的眼睛…還用低語把女妖推上大酋長的位置！', '', 38134),
(201014, 'zhTW', '來吧，英雄們！帶回我的魔精，幫我解決這個老羅亞！', '', 38134),
(201013, 'zhTW', '你以為自己贏了嗎？老大？嘿嘿嘿，也許你潛伏在陰影裡面太久了。', '', 38134),
(201012, 'zhTW', '伯昂撒姆第，你的這些小傢伙認為他們能幫你？你對上我會有勝算嗎？', '', 38134),
(201011, 'zhTW', '老哈卡，他是難纏的傢伙。你讓他付出了代價。但是他早晚都會回來。壞羅亞是不會就此罷休的！', '', 38134),
(201010, 'zhTW', '你為什麼要那樣看我？沒錯，哈卡是血之羅亞。但交易歸交易！快把魔精帶回來給我。', '', 38134),
(201009, 'zhTW', '有個羅亞你可能還記得！哈卡的儀式需要很多魔精…然後伯昂撒姆第很樂意進行交易。', '', 38134),
(201008, 'zhTW', '那些仲介者好像有什麼陰謀。你最好盯著他們！啊！快點帶著我的魔精回來！', '', 38134),
(201007, 'zhTW', '這傢伙不會想把魔精還我。小心…她會耍花招…', '', 38134),
(201006, 'zhTW', '老伯昂撒姆第不只在艾澤拉斯上做交易。仲介者總是在尋找…各式各樣的力量。', '', 38134),
(201005, 'zhTW', '我不需要追隨者！只要有閻獄之主給我的力量就夠了…會被遺忘的人是你，伯昂撒姆第！', '', 38134),
(201004, 'zhTW', '那是很久以前的事了，但是我還記得。我記得族人是怎麼離你而去。那些獻祭…那些鮮血…他們打算…啊啊啊啊！', '', 38134),
(201003, 'zhTW', '別忘了是誰讓你成為羅亞，伯昂撒姆第！快把那些靈魂交出來，臣服於你的主人！', '', 38134),
(201002, 'zhTW', '唯一的力量就是死亡！而死亡會降臨在他們頭上！', '', 38134),
(201001, 'zhTW', '你忘記了身為羅亞的職責！背棄了與族人的連結…共享的力量…啊！', '', 38134),
(201000, 'zhTW', '我要那些靈魂，伯昂撒姆第！拉斯塔哈和所有贊達拉人都是我的！跟繆薩拉作對可不是明智的決定！', '', 38134),
(200999, 'zhTW', '我會繼續擾亂這個老羅亞。去吧，照我說的。你沒有拒絕的選擇！', '', 38134),
(200998, 'zhTW', '繆薩拉讓我…處於下風。如果你能從幾個…我的生意夥伴身上收集魔精…然後我們同心協力，就能反敗為勝！', '', 38134),
(200997, 'zhTW', '等等…是誰…？英雄！呃…別太介意上次我們在祖達薩的小爭執，好嗎？', '', 38134),
(200996, 'zhTW', '等等…是誰…？英雄！我就知道，你們會來幫忙老朋友伯昂撒姆第的。', '', 38134),
(200995, 'zhTW', '伯昂撒姆第，你總是太心軟。你遲早會說的。然後，閻獄之主就能得償所望！', '', 38134),
(200994, 'zhTW', '讓你把他們送進淵喉嗎？不不不…繆薩拉，他們是我的族人。我絕不會拋棄他們！', '', 38134),
(200993, 'zhTW', '你把贊達拉的靈魂藏到哪裡去了，伯昂撒姆第？把他們交出來，不然你會後悔的，非常後悔！', '', 38134),
(200992, 'zhTW', '這就是真愛嗎…真是太甜蜜了。老伯昂撒姆第的羅曼史可不輸給他。嘿嘿…比他的還精彩！', '', 38134),
(200987, 'zhTW', '從過去中學習，但不要佇足不前。', '', 38134),
(200977, 'zhTW', '', '心懷勇氣。', 38134),
(200970, 'zhTW', '', '困難只是一時。', 38134),
(200963, 'zhTW', '我希望你出現在這裡不會對我的生意造成任何影響。', '', 38134),
(200962, 'zhTW', '', '你在取得兇蠻玫瑰的花粉時，要和佐司特保持距離。朝著受控制的希瓦爾灑花粉來讓她們暴怒。希望這樣做能阻擾佐司特的行動。', 38134),
(200948, 'zhTW', '我很喜歡你的宴會，淵喉行者。我就知道你不會讓我失望。\n\n請代我向王子表達恭喜之意。\n\n失陪了，這首美妙的音樂讓我不禁想要過去舞池那裡一趟。', '我很喜歡你的宴會，淵喉行者。我就知道你不會讓我失望。\n\n請代我向王子表達恭喜之意。\n\n失陪了，這首美妙的音樂讓我不禁想要過去舞池那裡一趟。', 38134),
(200947, 'zhTW', '只要你修改了你的新外觀，我們就該跳個舞。', '', 38134),
(200946, 'zhTW', '能和你跳支舞嗎？', '能和你跳支舞嗎？', 38134),
(200945, 'zhTW', '我已經將你的髮色染成和珊納一樣，所以不會招惹任何猜疑！', '我已經將你的髮色染成和珊納一樣，所以不會招惹任何猜疑！', 38134),
(200944, 'zhTW', '你這麼親切實在太好心了。', '', 38134),
(200937, 'zhTW', '你願意嗎？噢，那真是太好了。\n\n我相信只要跟你練習幾次，我就會對自己有信心了。\n\n<伊文緊張地來回走著。>\n\n我突然想到，如果跟我跳舞的對象長得很像珊納的話，應該會更有幫助…\n\n你知道的，她移動身體的方式跟一般的$pr很不一樣。\n\n不知道…你會不會介意…\n\n<伊文抬起頭來，滿心期待地望著你。>\n\n我想說的是，如果我暫時把你變成珊納的樣子，你能夠接受嗎？', '', 38134),
(200935, 'zhTW', '噢，抱歉，淵喉行者。\n\n我擋到你的路了。真抱歉…\n\n<伊文雙眼發亮地看著舞姿曼妙的舞者。>\n\n現在的時機好像有點不湊巧。\n\n是這樣的，我最近才剛剛成為汎希爾，完全不知道要怎麼跳他們的…我是說我們的這種舞步。\n\n雖然我是個菜鳥，但我有滿腔熱血、樂意學習，希望我有一天能夠藉由舞步，走入我未來的愛人珊納心中。', '', 38134),
(200933, 'zhTW', '', '喂！這邊！', 38134),
(200932, 'zhTW', '請享受三位謙卑靈魂帶來的療癒音樂。', '', 38134),
(200931, 'zhTW', '謝謝你，阿嘻。我們能受邀來此表演也覺得十分開心。', '', 38134),
(200930, 'zhTW', '失落聖杯樂團準備開始表演了！', '', 38134),
(200929, 'zhTW', '對暗影之境最感動的音樂放開你的內心和靈魂！', '', 38134),
(200928, 'zhTW', '', '躲起來，別被發現！這裡到處都是佐司特，$n！完全沒有蓋你！他們打算做很可怕的事情，我們必須阻止他們。\n\n計畫是這樣的。佐司特使用那種詭異的面具，控制了許多希瓦爾。可憐的希瓦爾，現在完全任憑佐司特擺布。我們救不了他們，但我們可以利用他們。\n\n你有看到我身旁的花朵嗎？這是兇蠻玫瑰，小心別碰到！兇蠻玫瑰的花粉很危險，會讓希瓦爾發狂並攻擊旁人，直到他們昏厥為止。我會使用魔法把你偽裝成一般的亞登蛾。去採集兇蠻玫瑰的花粉，然後把花粉灑在受到控制的希瓦爾身上，製造些混亂。\n\n絕對不會出任何差池的，放心吧！\n你準備好了嗎？', 38134),
(200927, 'zhTW', '這個凡人聲稱自己曾經從淵喉逃出來過！我們來看看是不是真的。', '', 38134),
(200925, 'zhTW', '', '他不是消失在淵喉裡了嗎？', 38134),
(200924, 'zhTW', '要是那裡根本空無一物呢？不，統御者之座根本只是徒勞。', '', 38134),
(200922, 'zhTW', '', '沒有克利瑟斯，就沒有勝算。我們一定要開啟統御者之座！', 38134),
(200921, 'zhTW', '', '$p，跟我來！快！', 38134),
(200920, 'zhTW', '他們…摧毀了亡者之城。克利瑟斯，他…', '', 38134),
(200919, 'zhTW', '傀儡家族…反擊了…他們的數量太多了…', '', 38134),
(200918, 'zhTW', '', '維拉斯！發生什麼事了？', 38134),
(200916, 'zhTW', '哈哈哈哈！', '哈哈哈哈！', 38134),
(200915, 'zhTW', '愚蠢的蟲子！別想擺脫我！', '愚蠢的蟲子！別想擺脫我！', 38134),
(200914, 'zhTW', '跑快一點，蠢東西！', '跑快一點，蠢東西！', 38134),
(200913, 'zhTW', '駕駕！', '駕駕！', 38134),
(200912, 'zhTW', '', '光榮獲勝！', 38134),
(200910, 'zhTW', '', '$p，我很緊張。音樂會讓我的靈魂赤裸裸地暴露在眾人面前。\n\n儘管這顆罪孽石讓我回想起內心的黑暗，我還是希望我的音樂能讓其中的光明熠熠生輝。\n\n大使館中的加瑞登勛爵認同我。他非常善解人意，並且非常喜歡我第一次來的時候為他表演的曲子。\n\n我一定是把我的魯特琴留在他那裡了。請幫我取回來，我會這裡繼續進行表演前的冥想。', 38134),
(200909, 'zhTW', '', '雖然你輸了，不過你還是在通往勝利的道路上。', 38134),
(200907, 'zhTW', '<好運安塞爾不情願地將視線從他的罪孽石移開。>\n\n我的重擔還是沉重無比。\n\n只要這顆石頭中還有尚未滌去的罪孽，我就永遠有墮入淵喉的危險。\n\n幸運的是，我出色的音樂得到汎希爾看管者的賞識，而且我很確定，在雷納瑟王子的宴會上表演，絕對是有利無害。\n\n從舞者附近的那堆樂團用具中取回我的小提琴，然後我就能開始公開懺悟了。', '', 38134),
(200902, 'zhTW', '<諾可緩緩吸了口氣。>\n\n舞台經理說他到處都找不到我的長笛\n\n上一次發生這種事，是因為有個吵鬧的設備管理員拿著它跑掉。\n\n你可以發現那些討厭的泥掘者在派對上惹事生非。\n\n從他們手中拿回我的長笛，我就會為你的賓客表演。', '', 38134),
(200900, 'zhTW', '看來靈魂恢復平靜了…謝謝你，朋友。', '', 38134),
(200895, 'zhTW', '', '我會把第一條歌獻給你。', 38134),
(200894, 'zhTW', '', '佐司特把我的族人變成了無法思考的野獸！跟我的盟友琥珀足合作，守護亞登曠野吧。', 38134),
(200893, 'zhTW', '看來你的旅程將會充滿坎坷。算了。祝你好運！', '看來你的旅程將會充滿坎坷。算了。祝你好運！', 38134),
(200892, 'zhTW', '你可以從那裡跳入靈魂柱。那是靈魂流入淵喉的通道。', '你可以從那裡跳入靈魂柱。那是靈魂流入淵喉的通道。', 38134),
(200891, 'zhTW', '傳送器會帶你前往樞運之環。', '傳送器會帶你前往樞運之環。', 38134),
(200889, 'zhTW', '注意，$p。請跟我來。', '注意，$p。請跟我來。', 38134),
(200888, 'zhTW', '保證這是我有史以來最精彩的演出。', '', 38134),
(200886, 'zhTW', '那把刀刃…是統御者親手打造的！', '', 38134),
(200885, 'zhTW', '我感覺到…力量…在周遭迴盪…', '', 38134),
(200883, 'zhTW', '', '或許你沒有翅膀，但你具備了晉升者的勇氣！', 38134),
(200882, 'zhTW', '願我的音樂也能引導你的靈魂，朋友。', '', 38134),
(200881, 'zhTW', '', '瑪卓薩斯居然敢在我們的領域出現？讓他們看看是誰在掌管天空。', 38134),
(200880, 'zhTW', '他抓住了貝恩！', '', 38134),
(200879, 'zhTW', '', '閻獄之主。', 38134),
(200876, 'zhTW', '佩拉苟斯，第一道挑戰的時間已經到來。準備好了就敲響禱鐘。', '', 38134),
(200875, 'zhTW', '', '前面就是晉升之路，全體戰鬥人員各就各位！', 38134),
(200871, 'zhTW', '我是誰？我是靈魂！\n\n我臨終時受到亞登曠野的呼喚。我一輩子都跟靈魂共事，所以覺得…再正常不過。\n\n你覺得我的靈魂形態好看嗎？變成一頭狼恣意奔跑，是我長久以來的夢想！', '', 38134),
(200867, 'zhTW', '', '訊息被魔法封住了？嗯，一點都不有趣…', 38134),
(200865, 'zhTW', '我去派對！大塊頭吃大點心！', '', 38134),
(200863, 'zhTW', '', '我們得快點離開。你走得動嗎？', 38134),
(200856, 'zhTW', '我們的盟友無法獨自對抗那隻巨怪。我們必須幫忙！', '', 38134),
(200850, 'zhTW', '', '噢，漫遊者，拜託放了我！我已經很久沒有機會能照顧亞登曠野的林地了', 38134),
(200847, 'zhTW', '好耶，讓我寫在筆記！等等，我需要我的筆記。', '', 38134),
(200846, 'zhTW', '這遠遠不夠。我有做筆記，我明明有做筆記的。對了，是研究。研究靈魄。\n\n我好像弄丟我的筆記了。', '', 38134),
(200842, 'zhTW', '打得漂亮！我們去找回我的軍刀，然後好好教訓這些東西吧。', '', 38134),
(200841, 'zhTW', '沒錯，被你抓到了，你是來幸災樂禍的嗎？噢，你跟他們不是一夥的。\n\n我被抓了，武器也被搶走了。你能幫我想辦法從這裡逃出去嗎？', '', 38134),
(200838, 'zhTW', '', '我們去找…什麼來著？我記不得了。', 38134),
(200837, 'zhTW', '', '他們把我困在這裡，還禁錮了我的心智。我應該有在事情發生之前儲存了我的記憶，但我不太確定。', 38134),
(200834, 'zhTW', '', '好！我們去找那個墓巡者吧。', 38134),
(200833, 'zhTW', '', '那個怪物把我困在這裡！用一堆死者壓制我。我不知道已經過了多久，但我受夠了！釋放我，然後幫我擊潰它！', 38134),
(200830, 'zhTW', '謝謝你！我們出發去找伊諾席雅。', '', 38134),
(200829, 'zhTW', '這和預期的不一樣。我原本要來救伊諾席雅，結果卻變成了階下囚。請幫幫我！還有她！', '', 38134),
(200826, 'zhTW', '', '他們就在附近，別手下留情。', 38134),
(200825, 'zhTW', '', '淵喉行者。我最近一直看到你在這邊出沒。\n\n我因為過於自負而被抓了起來。我還有個任務要完成，閻獄之主底下一名高階爪牙身上，有我必須取得的寶貴資訊。', 38134),
(200821, 'zhTW', '走開。\n\n我被打敗了。\n\n沒有了護甲，我很快就會死掉。', '', 38134),
(200818, 'zhTW', '它還在這附近。把它大卸八塊，它應該就會學到教訓了。', '', 38134),
(200817, 'zhTW', '你好！或是哈囉，或是任何你偏好的招呼。\n\n我剛才在追我的…呃，就叫那東西為實驗品吧。結果它比我計畫的還要聰明，害我陷入了這個困境。\n\n你能不能把我放了，然後一起去拿回我的東西？至少，要把還有用的部分回收起來。', '', 38134),
(200814, 'zhTW', '必須找回我的武器，我才可以回去。', '', 38134),
(200813, 'zhTW', '又一個來羞辱我的。不對…你跟他們好像不是一夥的？\n\n我原本以為我完了，永生都要被鎖鍊困在這裡。\n\n請幫助我拿回我的武器，我希望我至少能重拾這一點榮耀。', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(200810, 'zhTW', '走，出發去找我的魔杖吧！嗯，其實它還不算是我的。我們或許要施加點小小的暴力才能取得它。', '', 38134),
(200809, 'zhTW', '啊，是傳說中的淵喉行者。對力量的渴望害我落入了現在這個困境。\n\n你可以當個好心的…不管你是什麼種族，然後放了我嗎？\n\n噢，我或許還有一件小事情需要解決。', '', 38134),
(200803, 'zhTW', '被困住了。\n\n矮冬瓜，快救我出去！\n\n我必須回去主人那裡。', '', 38134),
(200799, 'zhTW', '這裡有一堆零件。看樣子是從某個重要的東西上面拆下來的。', '', 38134),
(200796, 'zhTW', '謝謝你。要用把我關起來的守衛身上的鑰匙，才能打開這個鎖鍊。', '', 38134),
(200795, 'zhTW', '這些怪物用鎖鏈把我困在這裡。拜託，放了我！', '', 38134),
(200792, 'zhTW', '', '謝謝你。我們去把我的劍找回來。', 38134),
(200791, 'zhTW', '', '你是淵喉行者嗎？我的家傳寶劍被奪走了。拜託幫我拿回來。', 38134),
(200780, 'zhTW', '交給我。', '', 38134),
(200778, 'zhTW', '我準備好接受挑戰了！', '', 38134),
(200776, 'zhTW', '哈哈！這個好玩！', '', 38134),
(200773, 'zhTW', '呃…是我的錯嗎？', '', 38134),
(200770, 'zhTW', '謝謝你，那個憎惡體應該就在這附近。', '', 38134),
(200769, 'zhTW', '我所服侍的對象在此處遇害，留我自己一個人愧對於世。他們被派來這裡，本來是有一件工作要做，請協助我完成他們未能完成的事。', '', 38134),
(200766, 'zhTW', '', '我們去解決那個怪物。', 38134),
(200765, 'zhTW', '', '我的長矛在我跟這裡的怪物對戰時折斷了。幫我殺掉它，我才能拿它的武器來用！', 38134),
(200764, 'zhTW', '我以前還能跟最厲害的高手平分秋色，但如今的我已經失去了相關的知識。一定是我的記憶被竊取走了。請幫助我把記憶找回來。', '', 38134),
(200763, 'zhTW', '我們來找回憶石吧。', '', 38134),
(200760, 'zhTW', '艾樂思想要靈魄！殺掉折磨我的傢伙。他偷走了我的裝備，我想全部拿回來。', '', 38134),
(200759, 'zhTW', '我們去奪回我的靈魄。', '', 38134),
(200756, 'zhTW', '', '拜託，救救我！我已經被銬在這裡不知道多久了。', 38134),
(200752, 'zhTW', '我得找回我的罪孽石！有些靈魂必須被摧毀！', '', 38134),
(200751, 'zhTW', '我的罪孽石在哪裡？', '', 38134),
(200746, 'zhTW', '非常好。我們去找那個石像鬼報仇吧。', '', 38134),
(200745, 'zhTW', '我遭到綁架！被某個粗暴的石像鬼拖到這裡。快放了我！放我走，我要去報仇！', '', 38134),
(200742, 'zhTW', '拯救亞登曠野的戰鬥尚未結束。', '', 38134),
(200741, 'zhTW', '我們去把鏟子找回來！', '', 38134),
(200740, 'zhTW', '他們偷走了我的鏟子！我需要幫忙把它拿回來。', '', 38134),
(200737, 'zhTW', '', '我們去尋找我的記憶吧。那些記憶應該就儲存在附近某處的回憶石裡面。', 38134),
(200735, 'zhTW', '', '你是誰？等等，我是誰？我的記憶破碎了，我必須找到我的回憶石才能恢復。', 38134),
(200733, 'zhTW', '<選擇告別詞。>', '', 38134),
(200731, 'zhTW', '記取本次挑戰的教訓，未來不要重蹈覆轍，我的朋友。', '', 38134),
(200728, 'zhTW', '他們受邀出席餘燼宴會，因為_______', '', 38134),
(200726, 'zhTW', '請允許________放下自身在城門的職務。', '', 38134),
(200701, 'zhTW', '希望我們的賓客不會像我這麼挑嘴吧。', '', 38134),
(200700, 'zhTW', '至少，這些應該比你從瑪卓薩斯帶回來的還要安全。', '', 38134),
(200699, 'zhTW', '沒問題，老大。', '', 38134),
(200698, 'zhTW', '服務生，把這些…道地美食…送上餐桌，馬上去。', '', 38134),
(200697, 'zhTW', '太噁心、不能吃、有臭味…', '', 38134),
(200696, 'zhTW', '挑剔的史帝芬不停地在你交給他的蘑菇裡面翻找著。', '', 38134),
(200695, 'zhTW', '你一定就是那個昇靈堡特使提到的人。', '', 38134),
(200694, 'zhTW', '我還在調整裝備。等你製作好物品再回來吧！', '', 38134),
(200693, 'zhTW', '你的所有裝備就在這裡！\n\n你想配戴哪些裝備？', '', 38134),
(200692, 'zhTW', '德瑞文，我找到你的軍隊了。', '', 38134),
(200691, 'zhTW', '淵喉行者，你跟血精靈去摧毀吸取軍隊靈魄的水晶。我繼續在空中巡邏。', '', 38134),
(200690, 'zhTW', '凱爾薩斯從下面發出信號，我們趕快降落！', '', 38134),
(200689, 'zhTW', '', '我的朋友…我對失去你的事實，還有我非扮演不可的角色感到悲傷。\n\n我希望還有挽救餘地。', 38134),
(200688, 'zhTW', '我的摯愛試圖拯救你，做出了正確的選擇。我對這樣的結局感到悲痛。\n\n夢境再也不會一樣了。', '', 38134),
(200687, 'zhTW', '', '母親，要是你能與我同在就好了。我們的族人尚有希望，但少了你我們只能勉力掙扎。\n\n我需要你的指引。', 38134),
(200686, 'zhTW', '在夢境的道路上，你是與我同行之母。你教了我無數知識。\n\n這個世界需要你。', '', 38134),
(200684, 'zhTW', '<所有的工具都已備齊，你可以替岩石頭撰寫許可單了。\n\n接下來，你必須決定要寫些什麼。>', '', 38134),
(200681, 'zhTW', '粗魯？偉大的米歐浩斯‧曼納斯頓這輩子從來沒有粗魯過！', '', 38134),
(200680, 'zhTW', '', '喂！很痛耶，你這個粗魯的笨蛋！', 38134),
(200679, 'zhTW', '', '那就別來礙事，米歐浩斯！', 38134),
(200678, 'zhTW', '噢！瞄準一點，米歐菲瑟！', '', 38134),
(200677, 'zhTW', '你是最棒的，小米米！我們以後別再吵了！至少，暫時先這樣…', '', 38134),
(200675, 'zhTW', '', '你還真喜歡湊熱鬧。', 38134),
(200674, 'zhTW', '我會忍耐的…為了…你…', '', 38134),
(200671, 'zhTW', '', '暴風雨…暫時減弱了。', 38134),
(200669, 'zhTW', '', '見證那場夢魘的人，不只有我們。佐司特的部隊正在逼近！', 38134),
(200667, 'zhTW', '', '喂！大家聽我說！', 38134),
(200658, 'zhTW', '', '嘿，$n！\n\n關於時光漫遊，你必須先了解現在的世界才能回到過去。\n\n等你熟悉這座城市並跟國王會面後，或許我可以送你回去！', 38134),
(200657, 'zhTW', '', '噢，親愛的！你果然很愛我！', 38134),
(200656, 'zhTW', '我沒辦法緩解夢魘！你能試試看…更直接的方法嗎？', '', 38134),
(200647, 'zhTW', '', '過氣的傢伙！', 38134),
(200646, 'zhTW', '這個嘛，我…呃…嘿，等等！你剛剛承認跟我做了一樣的事情！虛偽的傢伙！', '', 38134),
(200645, 'zhTW', '但是現在，呃…我得把魔精拿回來！', '', 38134),
(200644, 'zhTW', '比方說，那兩個瘋狂的地精。他們倆都渴望力量，我也很樂意幫他們忙。', '', 38134),
(200643, 'zhTW', '需要老伯昂撒姆第幫忙的人可不只有食人妖而已。所有人都想跟死亡羅亞做交易！', '', 38134),
(200641, 'zhTW', '我的影響力無人能敵！你會為我效力！', '', 38134),
(200640, 'zhTW', '御靈者在上！聲音來自王冠的徽章，淵喉行者，快！', '', 38134),
(200639, 'zhTW', '做得好，我忠誠的僕人，我會再次復甦。', '', 38134),
(200638, 'zhTW', '這聲音？哪裡來的？', '', 38134),
(200637, 'zhTW', '就是這樣…把皇冠交給我。', '', 38134),
(200621, 'zhTW', '我們應該跟樹林長說。野性種子和照料者有所連結，這可是罕見的美談！', '', 38134),
(200620, 'zhTW', '這一個跟其他的不一樣。真正的敵人…是佐司特。', '', 38134),
(200618, 'zhTW', '', '雖然維拉斯很直接，但他說的沒錯。我們需要支援。', 38134),
(200616, 'zhTW', '你想挑戰哪個記憶？', '', 38134),
(200615, 'zhTW', '', '不錯嘛！以沒有翅膀的人來說，已經很好了。', 38134),
(200614, 'zhTW', '', '所有志士都來這裡測試在空中的精準度，你應該沒問題吧？', 38134),
(200611, 'zhTW', '我們的訓練絕對還不算完成。我們必須維持標準。', '', 38134),
(200610, 'zhTW', '', '夢魘幻化成形，靈魂騷動不安。織夢者需要協助。', 38134),
(200608, 'zhTW', '我知道哪裡還有。', '', 38134),
(200607, 'zhTW', '不…夢魘還在糾纏。我們需要更多的靈魄。', '', 38134),
(200606, 'zhTW', '', '成功了嗎？', 38134),
(200605, 'zhTW', '希望這樣就夠了…', '', 38134),
(200603, 'zhTW', '', '統御者之座遭到圍攻！我們要守住它！', 38134),
(200601, 'zhTW', '', '灰燼使者！淵喉行者！跟我來！', 38134),
(200599, 'zhTW', '當然沒問題！波葛洛特別把靈魄保存好，就是為了用在這樣的場合。\n\n拿去吧。保重。記住，波葛洛是亞登曠野最勇敢的靈魂之一，如果你有困難，找我就對了！', '', 38134),
(200597, 'zhTW', '啊！你是什麼生物？是四個木魈穿著怪偽裝？\n\n荒疫怪物的其中一種？\n\n不管你是什麼生物…可以走開嗎？拜託？', '', 38134),
(200596, 'zhTW', '你好！你是為靈魄而來？波葛洛一直努力保存靈魄，提供給需要的野性種子使用。\n\n不能讓那些可惡的木魈和染疫生物的魔掌碰到這些！\n\n波葛洛是亞登曠野最勇敢的靈魂之一。誰也瞞不過我！', '', 38134),
(200593, 'zhTW', '我們去準備儀式！', '', 38134),
(200592, 'zhTW', '德瑞文將軍來了！', '', 38134),
(200591, 'zhTW', '有話快說。我們有很多事要做。', '', 38134),
(200590, 'zhTW', '', '克利瑟斯是對的。我們拿到統御者之座的鑰匙了！', 38134),
(200587, 'zhTW', '那我們就把他引出來，但是要先清掉空中的敵人。', '', 38134),
(200586, 'zhTW', '他就像個懦夫一樣躲在地底下。', '', 38134),
(200584, 'zhTW', '', '收下我部分的力量吧，凡人。使用它就能改變你的形態，讓下面那個靈魂…願意接納你。', 38134),
(200577, 'zhTW', '真希望我們的道路能在日後交會。在那之前，沿著道路前進吧。', '', 38134),
(200576, 'zhTW', '卡莉歐普之矛已經回來了，英雄，這都是你的功勞。', '', 38134),
(200575, 'zhTW', '', '這樣應該行了！我先監視佐司特，你回去找艾利歐德吧！', 38134),
(200574, 'zhTW', '做得好，$n，做得很好。', '', 38134),
(200573, 'zhTW', '', '雖然我們資源短缺，但我還是會盡力而為。', 38134),
(200572, 'zhTW', '', '晉升之路正等著你。\n\n準備好開始挑戰了嗎？', 38134),
(200571, 'zhTW', '拜託，艾利歐德，幫我們驅散這個靈魂的夢魘！', '', 38134),
(200527, 'zhTW', '', '其他家族很快就會得知刀刃的事情。準備戰鬥吧。', 38134),
(200526, 'zhTW', '', '統御者是傳奇武器鍛造師。要是我們有這把鑰匙，就能得到他的武器了。', 38134),
(200525, 'zhTW', '', '看來有道門擋在路上，你準備好就跟我說，我會帶你越過障礙。', 38134),
(200508, 'zhTW', '', '統御者之座在他失蹤以後就封鎖了。有許多人試圖想打開。', 38134),
(200506, 'zhTW', '我們不要在此地久留。你得把那枚野性種子帶去冬日窪地，對嗎？', '', 38134),
(200505, 'zhTW', '我們不能任由這些怪物佔據索令的牧原。', '', 38134),
(200491, 'zhTW', '', '你回來啦！野性種子很不安。', 38134),
(200469, 'zhTW', '騎上雄鹿跟我一起來吧，$p。', '', 38134),
(200466, 'zhTW', '賓客都很開心！我是真正的服務生！', '', 38134),
(200465, 'zhTW', '不用害怕，鳥人。全部都交給我就行了！', '', 38134),
(200464, 'zhTW', '服務生都跑到哪裡去了？', '服務生都跑到哪裡去了？', 38134),
(200463, 'zhTW', '點單了！', '', 38134),
(200458, 'zhTW', '$n，你已經找到長矛，其他的學者也都安全了。我們就接受這個提案，安靜離開，別再製造事端。我會在英雄之陵等你。', '', 38134),
(200455, 'zhTW', '$p，苦難與你同在。', '', 38134),
(200451, 'zhTW', '', '做得好！這裡的飛蛾說不定可以跟你學學幾招。', 38134),
(200450, 'zhTW', '', '我們要去採收幾個靈魄果，可是這裡到處都是佐司特！利用一點魔法，應該就能瞞過他們。', 38134),
(200449, 'zhTW', '', '我聽說你扮成峽谷尖嘯者的樣子非常迷人。\n\n<月莓笑了笑。>\n\n下次我需要觀摩一些性感鳥類舞步的時候，我會跟你聯絡的。\n\n寒暄時間結束了，我有好消息要跟你說！女王要大駕光臨特那瓦勒了！這片林地會繼續開放，照料野性種子並保護棲身於此的靈魂。她很快就會來到這裡，祝福林地之心，並再次賜予萬物活力。\n\n到時候，你應該有機會可以跟她談談。', 38134),
(200448, 'zhTW', '', '所以說，你成功讓寇任斯跳了舞嗎？\n\n<月莓笑了笑。>\n\n我就知道你這個人不錯，$n。如果我有其他零散的工作或是請託找上門來，我會再通知你。\n\n我有好消息！女王要大駕光臨特那瓦勒了！這片林地會繼續開放，照料野性種子並保護棲身於此的靈魂。她很快就會來到這裡，祝福林地之心，並再次賜予萬物活力。\n\n到時候，你應該有機會可以跟她談談。', 38134),
(200446, 'zhTW', '尊貴又強大。雷納瑟會很滿意的。$n，好好獎勵這位朋友。', '', 38134),
(200445, 'zhTW', '完美的工具…這就是你們主人的王冠，能將那些勳章的力量化為一體。', '', 38134),
(200442, 'zhTW', '', '別以為這樣就贏了！下次見面，一定會決一勝負。', 38134),
(200441, 'zhTW', '', '攻擊！', 38134),
(200440, 'zhTW', '', '琪瑞安，你哪裡比得上我，我可是猜疑楷模，再說，你接連干擾我，我已經不耐煩了！', 38134),
(200439, 'zhTW', '', '看來你似乎有訪客，楷模。好好照顧他們吧。', 38134),
(200438, 'zhTW', '', '我清楚看見未來，御靈者會死在我手中，然後昇靈堡就是我的了！', 38134),
(200437, 'zhTW', '啊啊！', '', 38134),
(200436, 'zhTW', '', '烏瑟，你曾經是顆有用的棋子，德沃絲很重視你…不過你對我來說沒用了。', 38134),
(200435, 'zhTW', '', '萊索妮雅，拿走他的力量。用他來加強你的轉化。', 38134),
(200431, 'zhTW', '我看到你在邊緣徘迴不去，凡人。你是否感到淵喉在拉扯你的靈魂？\n\n你的勇氣與毅力讓人敬佩。我會考驗你。\n\n如果你真如他們所說的勇敢，那就接受挑戰打敗我，再次進入昇靈堡。若是屈服，我就會將你的靈魄再次抽離。\n\n你覺得呢？', '我看到你在邊緣徘迴不去，凡人。你是否感到淵喉在拉扯你的靈魂？\n\n你的勇氣與毅力讓人敬佩。我會考驗你。\n\n如果你真如他們所說的勇敢，那就接受挑戰打敗我，再次進入昇靈堡。若是屈服，我就會將你的靈魄再次抽離。\n\n你覺得呢？', 38134),
(200429, 'zhTW', '這不過是第一場戰鬥。恐怕還有很多場等著我們。', '', 38134),
(200428, 'zhTW', '打得漂亮！回來重新整備！', '', 38134),
(200427, 'zhTW', '曠野獵者！就是現在！', '', 38134),
