INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(196085, 'zhTW', '', '在戰鬥開始時使用腐蝕術，它會持續耗損對手的生命力。', 38134),
(196084, 'zhTW', '乖乖說出來！', '', 38134),
(196083, 'zhTW', '看來我們的目標很清楚了。', '', 38134),
(196082, 'zhTW', '', '它說的那個大釜就在靈魂之河附近。我認為貝恩就被關在那個地方。', 38134),
(196081, 'zhTW', '你知道他說的地方嗎，海軍總帥？', '', 38134),
(196080, 'zhTW', '沒有人…能逃出…淵喉…', '', 38134),
(196079, 'zhTW', '不！它已經…失效了！', '', 38134),
(196078, 'zhTW', '在大釜的…甬道石…或許能…', '', 38134),
(196077, 'zhTW', '果然有路可以離開！快告訴我們在哪？', '', 38134),
(196076, 'zhTW', '沒用的！那條路…早就…封死了…', '', 38134),
(196075, 'zhTW', '快說！我們要怎麼離開這裡？', '', 38134),
(196074, 'zhTW', '這位朋友似乎有話要說。', '', 38134),
(196073, 'zhTW', '你來的正好。', '', 38134),
(196072, 'zhTW', '', '所有人…都得效忠…閻獄之主…', 38134),
(196071, 'zhTW', '', '你會屈服的！', 38134),
(196070, 'zhTW', '離開這裡我就會沒事的。請帶路吧。', '', 38134),
(196069, 'zhTW', '謝謝你。我還以為我永遠都無法獲得自由了。', '', 38134),
(196068, 'zhTW', '休息一下吧，不必感到羞愧。你畢竟是個凡人。', '', 38134),
(196061, 'zhTW', '', '你的法術可以進行遠程攻擊。對著你的敵人施放，就能搶先造成傷害！', 38134),
(196060, 'zhTW', '', '很好，再來一次！當你以懲擊攻擊對手時，暗言術：痛，會持續造成傷害。', 38134),
(196059, 'zhTW', '', '你太早使用暗言術：痛了。等持續時間快結束時再使用可以造成更多傷害。', 38134),
(196058, 'zhTW', '', '暗言術：痛快消失了！再施放一次！', 38134),
(196052, 'zhTW', '', '就是這樣，再來一次！牧師只要能善用暗言術：痛，就能持續削弱敵人。', 38134),
(196051, 'zhTW', '', '很好！在暗言術：痛造成傷害的同時繼續施放懲擊。我會告訴你施放的時機。', 38134),
(196050, 'zhTW', '', '在戰鬥開始時使用暗言術：痛，它會持續耗損對手的生命力。', 38134),
(196032, 'zhTW', '', '你有足夠的聖能了！快使用公正之盾！', 38134),
