INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(193833, 'zhTW', '沒錯！', '沒錯！', 38134),
(193832, 'zhTW', '把錯怪在那些凡人頭上就好。畢竟，他們常常不記得把自己的蛇放在哪裡。對不對？', '把錯怪在那些凡人頭上就好。畢竟，他們常常不記得把自己的蛇放在哪裡。對不對？', 38134),
(193831, 'zhTW', '除了不小心讓一些生物跑掉外，一切大致順利。現場的人不會注意到有幾隻蛇到處爬來爬去，對吧？', '除了不小心讓一些生物跑掉外，一切大致順利。現場的人不會注意到有幾隻蛇到處爬來爬去，對吧？', 38134),
(193830, 'zhTW', '情況似乎相當不錯？', '情況似乎相當不錯？', 38134),
(193829, 'zhTW', '', '莫格萊尼啊，處理掉你以後，淵喉會有一段美好回憶的。', 38134),
(193828, 'zhTW', '我們會朝這個方向努力！', '我們會朝這個方向努力！', 38134),
(193827, 'zhTW', '那些凡人似乎特別喜歡他們稱為「可愛」的生物。不確定那是什麼意思，不過大眼睛似乎是重點。就送更多大眼睛的生物過來吧！', '那些凡人似乎特別喜歡他們稱為「可愛」的生物。不確定那是什麼意思，不過大眼睛似乎是重點。就送更多大眼睛的生物過來吧！', 38134),
(193826, 'zhTW', '收到，有特定偏好嗎？', '收到，有特定偏好嗎？', 38134),
(193825, 'zhTW', '很好，不過記得請你的同事繼續送來更多生物。', '很好，不過記得請你的同事繼續送來更多生物。', 38134),
(193823, 'zhTW', '大家的情況都一樣。我猜，最近這筆生意能讓我們大賺一筆。', '大家的情況都一樣。我猜，最近這筆生意能讓我們大賺一筆。', 38134),
(193822, 'zhTW', '非常好，集團的生意讓我忙得暈頭轉向。', '非常好，集團的生意讓我忙得暈頭轉向。', 38134),
(193821, 'zhTW', '放心，我會的。', '放心，我會的。', 38134),
(193820, 'zhTW', '無與倫比的傳送門網路。請轉達我的祝賀之意。', '無與倫比的傳送門網路。請轉達我的祝賀之意。', 38134),
(193819, 'zhTW', '我的同事找來了各式各樣的生物，令人嘆為觀止。真是太有趣了。', '我的同事找來了各式各樣的生物，令人嘆為觀止。真是太有趣了。', 38134),
(193818, 'zhTW', '生意還不錯。那些凡人中意的夥伴各有不同。', '生意還不錯。那些凡人中意的夥伴各有不同。', 38134),
(193817, 'zhTW', '生意如何？', '生意如何？', 38134),
(193816, 'zhTW', '你會一敗塗地，我就是知道！', '', 38134),
(193815, 'zhTW', '', '佩拉苟斯可能會被猜疑與恐懼吞噬。動作要快。', 38134),
(193814, 'zhTW', '', '什麼？！那個傻瓜…我們得趕快！', 38134),
(193813, 'zhTW', '克蕾雅！感謝御靈者…佩拉苟斯自己開始了儀式！', '', 38134),
(193812, 'zhTW', '', '我們到了。佩拉苟斯見到你會很開心。', 38134),
(193811, 'zhTW', '', '$n，我們正在經歷難以言喻的浩劫。', 38134),
(193810, 'zhTW', '', '等等…你來看看這個。', 38134),
(193809, 'zhTW', '', '關上了傳送門，我們的部隊才出現一線生機。', 38134),
(193808, 'zhTW', '', '或許我們的世界看起來很完好。言語無法表達我們失去的東西。', 38134),
(193807, 'zhTW', '', '虛無在這裡開啟了龐大的傳送門運送部隊。如果不摧毀掉，我們就沒有獲勝的希望。', 38134),
(193805, 'zhTW', '', '我差點就要完成最後的試煉，乾旱就開始了。自那時起就沒有人晉升過。', 38134),
(193802, 'zhTW', '', '志士，你比多數人都理解我們遵循的道路。願御靈者看顧著你。', 38134),
(193801, 'zhTW', '$p…我感應到…羅亞就在附近！', '', 38134),
(193800, 'zhTW', '', '打得好…跟我來吧。復仇…還不急。', 38134),
(193799, 'zhTW', '我們維持著奧睿博司的穩定，使命的運行之法是神聖且無可窺探的。\n\n使命相當古老。有可能遠遠超乎我們所能理解的範圍。這是項神聖的科技。', '', 38134),
(193797, 'zhTW', '使命的運行之法，並非總是顯而易見。\n\n根據我的了解，它會根據你為使命犧牲奉獻了哪些事情，來評估你的需求。\n\n這是奧睿博司幫助你的方式。你服侍使命，使命也會以獨有的方式報答你。\n\n使命是我們的道路。', '', 38134),
(193795, 'zhTW', '你好，淵喉行者。\n\n這個寶庫會依照你對於使命的犧牲奉獻，提供你相應的奧睿博司恩澤。只要你有持續幫助我們，就能定期獲得這份恩澤。\n\n你的同類似乎把這種定期的概念稱作「每週一次」。', '', 38134),
(193789, 'zhTW', '', '他們以為…能狩獵我？我會把他們…撕成碎片！', 38134),
(193787, 'zhTW', '', '你…你不是來拷問我的。\n\n我原本身在…美麗的森林裡…但有人強行把我抓走了…\n\n抓住我的人回來了…保護我…直到我的力量恢復！', 38134),
(193782, 'zhTW', '你的魂魄會永遠留在這裡！', '你的魂魄會永遠留在這裡！', 38134),
(193780, 'zhTW', '昇靈堡的鐘聲會安撫你的靈魂。聆聽，然後感受平靜。', '昇靈堡的鐘聲會安撫你的靈魂。聆聽，然後感受平靜。', 38134),
(193779, 'zhTW', '', '$n，進入池子。', 38134),
(193778, 'zhTW', '請找位子坐。講演即將開始。', '請找位子坐。講演即將開始。', 38134),
(193777, 'zhTW', '歡迎。請把你的名字加到現在以昇靈堡為家的高貴靈魂名單裡。', '歡迎。請把你的名字加到現在以昇靈堡為家的高貴靈魂名單裡。', 38134),
(193772, 'zhTW', '老兄，怎了？你想從伯昂撒姆第手中獲得什麼？', '', 38134),
(193770, 'zhTW', '你好啊，英雄！我們這支隊伍表現不錯，不是嗎？', '', 38134),
(193769, 'zhTW', '你還不知道嗎？誰都別想躲過伯昂撒姆第！', '', 38134),
(193768, 'zhTW', '就算是暗影獵手…也很難跟上這個蹤跡。走吧。', '', 38134),
(193767, 'zhTW', '你好啊，英雄。我們得談談。', '', 38134),
(193765, 'zhTW', '嗯。他們折磨了那個靈魂…然後移到其他地方。我找到蹤跡了。', '', 38134),
(193764, 'zhTW', '我們被發現了！', '', 38134),
(193763, 'zhTW', '一起受到永恆的折磨吧！', '一起受到永恆的折磨吧！', 38134),
(193762, 'zhTW', '我能幫你追蹤那個靈魂，不能再多了。否則我可能會永遠的被困在這深淵。', '', 38134),
(193760, 'zhTW', '$p，淵喉等著我們，我們要拯救的靈魂也是。', '', 38134),
(193757, 'zhTW', '曾經導引著聖光，如今會用無盡黑暗吞噬一切的虛空之靈！', '', 38134),
(193756, 'zhTW', '這個地方…比我想像的還糟。我感應到附近有靈魂的痕跡…', '', 38134),
(193747, 'zhTW', '我很肯定，不管出現什麼，都會成為我們的助力。', '', 38134),
(193746, 'zhTW', '跟其他地方比起來，爛泥池是絕佳的泥掘者誕生地…', '', 38134),
(193741, 'zhTW', '', '嘿，$c！要搭便車吧？', 38134),
(193738, 'zhTW', '', '艾澤拉斯的居民，歡迎你。我們準備了幾項活動幫助你適應新的存在方式。', 38134),
(193737, 'zhTW', '', '御靈者統治著我們的國度。她是最堅定，也是最為全心全意的永恆者。\n\n在她的主導下，昇靈堡才能發揮在暗影之境的作用。關於這位古老之人的事蹟一時說不完，但志士還沒有資格聽聞她的榮耀。\n\n也許在你獲得自己的羽翼時，有機會見到她的尊容。', 38134),
(193736, 'zhTW', '', '御靈者賜予我的功用在於確保所有來訪的新人都能適應新身分。我的存在就是為了教導其他人認識琪瑞安。\n\n能夠碰到你，是我的榮耀和權利。', 38134),
(193730, 'zhTW', '', '注意安全，不要忘記你所受的訓練。', 38134),
(193729, 'zhTW', '', '我相信你的前途不可限量，$n。', 38134),
(193727, 'zhTW', '那就讓我來做個了結吧。我絕對不會讓繆薩拉一直得意下去。', '', 38134),
(193726, 'zhTW', '你有聽到老繆薩拉說的吧。你的靈魂與艾澤拉斯還相連著。就像我們這位朋友。', '', 38134),
(193725, 'zhTW', '伯昂撒姆第，沒有人能逃出淵喉，是嗎？如果我進去…', '', 38134),
(193724, 'zhTW', '等等， $n。我有些事要講。', '', 38134),
(193723, 'zhTW', '莫瑞茲！', '', 38134),
(193721, 'zhTW', '$n，我要跟你一起去淵喉。\n\n繆薩拉當我是傻子。我可不會袖手旁觀，坐等別人幫忙收拾善後。\n\n我們會一起找到失蹤的荒野神靈。', '', 38134),
(193720, 'zhTW', '我不喜歡和繆薩拉有關的任何事情。', '', 38134),
(193719, 'zhTW', '索恩！你終於來了！', '', 38134),
(193718, 'zhTW', '', '印迪寇！太好了。我以為我完蛋了。', 38134),
(193714, 'zhTW', '能跟你一起冒險是我的榮幸，$n。', '', 38134),
(193681, 'zhTW', '', '卡利克斯！感謝御靈者！', 38134),
(193676, 'zhTW', '我的特別貴賓一定很想聽聽繆薩拉會說些什麼。他們之間有點…特殊的關係。', '', 38134),
(193675, 'zhTW', '伯昂撒姆第！你太尊貴，不屑自己召喚我嗎？', '', 38134),
(193673, 'zhTW', '', '戰鬥結束後，守方受到指示進行淨化。畢竟他們奪走了被附身的同胞性命，這樣的回憶太過沉重。', 38134),
(193670, 'zhTW', '繆薩拉，你的信徒太弱了！不過是一群老骨頭和嚇人的小鬼魂。', '', 38134),
(193660, 'zhTW', '我的亡城在這個方向。情況或許會有點…混亂。不過，沒有勇敢的大英雄處理不了的事，對吧？', '', 38134),
(193655, 'zhTW', '', '夠了。能喚醒接待員了！', 38134),
(193654, 'zhTW', '大傢伙的靈魂真真真多！不過，這個…幾乎耗盡了。', '大傢伙的靈魂真真真多！不過，這個…幾乎耗盡了。', 38134),
(193653, 'zhTW', '我的靈魄…獻給昇靈堡…', '', 38134),
(193652, 'zhTW', '謝謝你，朋友。', '謝謝你，朋友。', 38134),
(193651, 'zhTW', '不用擔心。等到靈魄的供給恢復，他就會恢復正常。', '不用擔心。等到靈魄的供給恢復，他就會恢復正常。', 38134),
(193650, 'zhTW', '', '好啊！用我的靈魄桶吧。百衛機兵樂意分享！', 38134),
(193645, 'zhTW', '', '石源魔，墮落王子和他的淵喉行者來了。別讓他們靠近主宰。', 38134),
(193642, 'zhTW', '我要怎麼協助你？', '我要怎麼協助你？', 38134),
(193641, 'zhTW', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導德魯伊。', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導德魯伊。', 38134),
(193638, 'zhTW', '必須誓死捍衛這個地方！', '', 38134),
(193636, 'zhTW', '路易森說過你會來這裡。', '', 38134),
(193635, 'zhTW', '能收集這麼多頭骨，想必你是個身經百戰的戰士！', '', 38134),
(193634, 'zhTW', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導武僧。', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導武僧。', 38134),
(193633, 'zhTW', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導聖騎士。', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導聖騎士。', 38134),
(193632, 'zhTW', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導牧師。', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導牧師。', 38134),
(193631, 'zhTW', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導法師。', '你好啊，$c。\n\n如果你想接受訓練，那你得去找$c訓練師。我只能指導法師。', 38134),
