INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'koKR', '거대한 혁신', '', '', '', '', '', '', '', '', 26365),
(50374, 'koKR', '아제로스의 피', '마그니 브론즈비어드의 전갈을 전달해야 합니다.', '얼라이언스와 호드의 지도자들이 지금 무엇을 두고 싸우는지 알기나 하는 겐가?\n\n상처에서 솟아오르는 이 물질은 아제로스의 피란 말일세!\n\n우린 상처를 치료해야 하지, 힘을 얻기 위해 이를 이용해서는 안 되네! 아제로스는 우리에게 유일한 고향이네. 이런 식으로... 아제로스를 죽게 둘 수는 없네!\n\n난 자네를 믿네, $n. 얼라이언스는 자네의 의견을 중히 여긴다는 것도 알아. 그들이 이 광기를 멈추도록 설득해야 하네!\n\n이게 우리 행성이 가진 유일한 희망일세...', '', '', '', '', '', '', 26365),
(50373, 'koKR', '최근 방문객', '대마법사 카드가와 대화해야 합니다.', '실리더스의 상황이 세간의 집중을 받고 있는 것 같소.\n\n카드가 대마법사님이 당신이 자리를 비운 동안 막 도착하셨소. $n 당신과 이야기를 나누러 오신 것 같소이다.\n\n급해 보이시더군. 나라면 가능한 한 빨리 그분과 대화를 나눌 것 같소.', '', '', '', '', '', '', 26365),
(50372, 'koKR', '사막 연구', '테미 팅크스피너와 대화해야 합니다.', '살게라스의 암흑의 검이 이 땅에 파괴의 그림자를 드리웠소.\n\n공격을 받았을 때 실리시드 둥지는 파괴된 줄로 알았는데, 실리시드가 돌아오고 있다는 보고를 받았소.\n\n어떻게 실리시드가 파괴 공격을 피했는지, 그 검이 실리시드 둥지에 어떤 영향을 끼치는지 알아낼 필요가 있겠소.\n\n국왕께서 살아남은 실리시드를 해부해서 연구하라고 하셨소.\n\n테미 팅크스피너가 연구를 수행하고 있는데 \"수집가\"가 필요하다고 하더군.', '', '', '', '', '', '', 26365),
(50371, 'koKR', '스톰윈드로부터의 소환', '스톰윈드에서 마티아스 쇼를 만나야 합니다.', '안두인 린 국왕께서 당신에게 가능한 한 빨리 스톰윈드로 돌아와 달라고 부탁하셨습니다. 당신이 도와줘야 할 급한 일이 생겼습니다.\n\n도착하시는 대로 자세한 내용을 알려드리겠습니다.\n\n사자의 안식처에서 기다리겠습니다. 부디 서두르십시오... 기회가 언제 사라질지 모릅니다!', '', '', '', '', '', '', 26365),
(50364, 'koKR', '아제로스의 피', '마그니 브론즈비어드의 전갈을 전달해야 합니다.', '얼라이언스와 호드의 지도자들이 지금 무엇을 두고 싸우는지 알기나 하는 겐가?\n\n상처에서 솟아오르는 이 물질은 아제로스의 피란 말일세!\n\n우린 상처를 치료해야 하지, 힘을 얻기 위해 이를 이용해서는 안 되네! 아제로스는 우리에게 유일한 고향이네. 이런 식으로... 아제로스를 죽게 둘 수는 없네!\n\n난 자네를 믿네, $n. 호드는 자네의 의견을 중히 여긴다는 것도 알아. 그들이 이 광기를 멈추도록 설득해야 하네!\n\n이게 우리 행성이 가진 유일한 희망일세...', '', '', '', '', '', '', 26365),
(50360, 'koKR', '카드가의 부탁', '대마법사 카드가와 대화해야 합니다.', '자네가 없는 동안 대마법사 카드가가 왔네. $n 자네와 얘기를 나누려고 온 모양이더군.\n\n어서 그가 무슨 얘기를 하는지 들어보게. 놈이 가능한 한 빨리 사라졌으면 좋겠거든.\n\n여기서 제일 쓸모없는 게 바로 호드 일에 참견하고 다니는 인간 한 명일세!', '', '', '', '', '', '', 26365),
(50358, 'koKR', '사막 연구', '픽스니 러스트밤과 대화해야 합니다.', '살게라스의 암흑의 검이 이 땅에 파괴의 그림자를 드리웠네.\n\n공격을 받았을 때 실리시드 둥지는 파괴된 줄로 알았는데, 실리시드가 돌아오고 있다는 보고를 받았네.\n\n내가 이 일을 맡았다면 그 흉측한 둥지를 영원히 태워버렸을 걸세! 그런데 내 일이 아니니, 뭐.\n\n어둠의 여왕께서 살아남은 실리시드를 해부해서 연구하라고 하셨네.\n\n픽스니 러스트밤이 연구를 수행하고 있는데 \"수집가\"가 필요하다고 하더군.', '', '', '', '', '', '', 26365),
(50341, 'koKR', '최근의 발견', '나타노스 블라이트콜러와 대화해야 합니다.', '오늘 아침, 군단을 상대로 거둔 승리를 축하하기 위한 연회에 호드의 지도자들이 모두 모였었네.\n\n모두가 연회를 즐기는 와중, 갤리윅스가 어둠의 여왕님께만 은밀하게 무언가를 말씀드리더군.\n\n그자가 실리더스에서 수수께끼의 물질을 발견한 모양이네... 지금껏 알려진 광물과는 전혀 다른 마력을 가진 광물일세.\n\n대족장께서 자네에게 자세한 얘기를 전하라고 명령하셨네. 특수 임무를 준비하는 과정으로 말일세.', '', '', '', '', '', '', 26365),
(50338, 'koKR', 'G-특공대', '방사성 폐기물을 처치해야 합니다.', '대체 여긴 어떻게 들어왔습니까, $n 님?\n\n저는 G-특공대의 멀독입니다. 당신 같은 $r의 도움이라면 언제든 환영이지요.\n\n놈리건을 자기들 고향이라 부르는 오염된 생물체를 처리하도록 도와주십시오. 그러면 당신을 명예 G-특공대원으로 임명하겠습니다!\n\n이 낙하산을 받으시고, 톱니바퀴의 전당으로 뛰어내려서 방사성 폐기물을 제거해 주십시오. 이번 임무를 마치시면 서쪽 전당에 있는 특공대원 B.E 버랙허스에게 보고하시면 됩니다. 둘이 아주 잘 어울릴 것 같군요.', '', '', '', '', '', '', 26365),
(50337, 'koKR', 'G-특공대', '기계화 문지기 6000을 처치해야 합니다.', '멀독은 완전히 정신 나간 놈입니다, $c님. 절대로, 절대 그 노움이 조종하는 비행기는 타지 마십시오.\n\n저는 잠행을 별로 좋아하지 않습니다. 하긴, 당신이라고 다를 것 같지는 않군요. 이 복도를 따라 출격실까지 가셔서 기계화 문지기 6000을 박살내 주십시오.\n\n기계화 문지기는 비행기를 지키고 있습니다...\n\n... 전 비행기가 정말 싫습니다. 비행기 따위에는 절대로 타지 않겠습니다!\n\n일을 마친 후에 이 낙하산을 타고 뛰어내리십시오. 그리고 특공대원 멋쟁이에게 다음 임무를 받으십시오.', '', '', '', '', '', '', 26365),
(50336, 'koKR', 'G-특공대', '기계박사 텔마플러그를 처치해야 합니다.', '저라면 B.E 버랙허스나 멀독은 가까이하지 않겠습니다. 둘 다 그다지... 안정적이지 않아서 말이지요.\n\n그래도 당신은 괜찮아 보이네요. 이제 일을 시작해 볼까요? 이 터널을 따라서 땜장이 왕실까지 가세요. 기계박사 텔마플러그를 만나면 어떻게 해야 할지 자연히 아시게 될 겁니다.\n\n거기서 저희 특공대장 한이발도 만나실 수 있을 겁니다. 저희를 위해 노력하신 데 대해 두둑한 보상을 해 주실 겁니다.\n\n아마도요...', '', '텔마플러그의 폭탄을 조심하세요!', '기계박사 텔마플러그', '', '', '', 26365),
(50335, 'koKR', '복수는 나의 것', '란돌프 몰로크, 군주 열지옥, 들창코를 처치해야 합니다.', '당신이 어느 쪽에 충성을 맹세했건 상관 없습니다, $r. 들창코만 없어지면 됩니다!\n\n놈과 이 빛을 저버린 곳의 다른 쓸모없는 녀석들이 내 부하들의 생명을 앗아갔습니다. 얼라이언스와 호드는 직접 보지 못했겠지만, 내 부하들을 싸늘한 시신으로 만든 건 견딜 수 없는 일이란 걸 알아주십시오.', '', '', '', '', '', '', 26365),
(50319, 'koKR', '낯선 땅의 낯선 자', '오그리마에서 대족장의 명령 게시판을 발견해야 합니다.', '반갑소. 나는 대사 블랙가드요.\n\n<손에 쥔 종이 조각을 내려다보는 블랙가드>\n\n당신을 오그리마에서 맞게 되어 영광이오, $r.\n\n<당신을 흘끗 보더니 의아하다는 듯 미소지으며 다시 종이를 내려다보는 블랙가드>\n\n당신 일평생의 여행이 눈 앞에 펼쳐지니, 반드시... 음...\n\n그거 아시오? 난 이 환영 인사를 온종일 읽어 주고 있다오. 이제 그만할 때도 됐지.\n\n당신네 사절과 얘기하고 모험을 할 수 있는 곳이 어딘지 찾아보시오. 서두르시오.', '', '', '', '', '', '', 26365),
(50318, 'koKR', '노스렌드에서 때맞춰 온 호출', '수정노래 숲 상공의 달라란에 있는 보르무와 만나야 합니다.', '불타는 별이 거대한 천공의 그림자 속에서 빛을 잃어가 현자들에게 두려움을 심어 주고 있습니다. 달라란에 있는 현명한 보르무를 찾아가 그녀를 도와주십시오.\n\n아, 이 달라란 말고 다른 달라란입니다. 저도 압니다. 굉장히 헷갈리죠.', '', '', '', '', '', '', 26365),
(50316, 'koKR', '이상 상황 감지: 울두아르', '울두아르 탐험을 다시 체험해야 합니다.', '늦었네요. 지난번에 요그사론을 울두아르 깊은 곳의 감옥에서 탈출시키려 했던 힘에 맞섰을 때 기억 안 나요? 그때도 늦었잖아요. 당신 정말로 시간을 엄수하도록 신경쓰는 게 좋겠어요.\n\n뭐, 가시죠! 고대 신들! 감시자! 티탄 같은 거! 목록에서 하나씩 지워가며 나아가 봐요. 우리에겐 시간도 많고 갈 곳도 많다고요.\n\n<급히 떠나라는 몸짓을 하는 보르무>', '', '', '', '', '', '', 26365),
(50313, 'koKR', '낯선 땅의 낯선 자', '스톰윈드에서 영웅의 부름 게시판을 발견해야 합니다.', '스톰윈드에 어서 오십시오, $r|1이여;여;! 안두인 린 국왕께서 이 멋진 도시를 둘러보라고 초대하셨습니다. 오, 당신은 독실해 보이시는군요. 분명 마음에 들어하실 멋진 대성당이 있습니다!\n\n아, 하지만 그건 잠시 미뤄 둬야겠군요. 빛벼림 사절 한 분이 당신께 도시 벽 너머에서 기다리고 있는 모험에 대해 얘기해 드리려고 기다리고 계십니다.\n\n좋은 하루 보내세요, $n 님. 아, 그리고 가끔씩 소동이 일어나더라도 이해해 주십시오. 빛의 군대 병사들 특유의 덕목인 절제를 갖추지 못한 시민들이 많거든요!', '', '', '', '', '', '', 26365),
(50305, 'koKR', '낯선 땅의 낯선 자', '스톰윈드에서 영웅의 부름 게시판을 발견해야 합니다.', '스톰윈드에 어서 오십시오, $r|1이여;여;! 안두인 린 국왕께서 이 멋진 도시를 둘러보라고 초대하셨습니다. 오, 당신은 밤문화를 잘 즐길 수 있는 분인 것 같군요. 유명한 술집과 경쾌한 음악이 흘러나오는 곳이 많답니다!\n\n아, 그건 잠시 미뤄 둬야겠군요. 렌도레이 사절 한 분이 당신께 도시 벽 너머에서 기다리고 있는 모험에 대해 얘기해 드리려고 기다리고 계십니다.\n\n좋은 하루 보내세요, $n 님. 아, 그리고 걱정이 되어 드리는 말씀입니다만... 당신이 휘두르는 암흑의 힘을 잘 제어하십시오. 스톰윈드에 있는 모두가 저처럼 깨어 있는 건 아니거든요!', '', '', '', '', '', '', 26365),
(50303, 'koKR', '낯선 땅의 낯선 자', '오그리마에서 대족장의 명령 게시판을 발견해야 합니다.', '반갑소. 나는 대사 블랙가드요.\n\n<손에 쥔 종이 조각을 내려다보는 블랙가드>\n\n당신을 오그리마에서 맞게 되어 영광이오, $r.\n\n<당신을 흘끗 보더니 의아하다는 듯 미소지으며 다시 종이를 내려다보는 블랙가드>\n\n당신과 평생을 함께할 여행이 눈 앞에... 으으!\n\n못 해먹겠네! 해 뜰 때부터 질 때까지 이 똑같은 환영 인사를 읽는 것도 지긋지긋하오.\n\n가서 어디서 싸우면 좋을지 찾아보고 당신네 사절단에게 얘기하시오.\n\n제발 나를 혼자 내버려 두시오.', '', '', '', '', '', '', 26365),
(50254, 'koKR', '대의를 위한 두 번째 동맹', '두 번째로 동맹을 맺을 종족을 선택해야 합니다.', '새로운 동맹 하나는 우리에게 힘이 되어 주고, 두 번째 동맹은 우릴 더 강하게 만들어 줄 것이다.\n\n지금 이 순간에도 얼라이언스는 우릴 공격할 책략을 세우고 있다. 서두르지 않으면 공격에 대비할 준비도 하기 전에 놈들이 쳐들어올 것이다.\n\n다음 종족과 동맹을 맺을 준비가 되면 말해라. 한 치의 망설임도 있어선 안 된다.', '', '', '', '', '', '', 26365),
(50248, 'koKR', '대의를 위한 두 번째 동맹', '두 번째로 동맹을 맺을 종족을 선택해야 합니다.', '얼라이언스를 강화할 중요한 첫 발을 떼셨습니다, $n 님. 잘 하셨어요.\n\n하나, 새로운 동맹 하나로는 호드를 막을 수 없을 것 같습니다. 대족장은 오그리마의 병력이 스톰윈드의 병력을 압도할 때까지 쉬지 않을 거예요.\n\n당신께서 다음 동맹을 찾아 주셨으면 합니다. 더 이상 지체하면 안 돼요.', '', '', '', '', '', '', 26365),
(50247, 'koKR', '우편은 계속 배달되어야 한다', '우체국장을 도와 제한 시간 안에 편지를 15개 이상 분류해야 합니다.', '최근 일어난 말썽들 때문에 우편을 분류할 우편의 정령들이 점점 부족해지고 있소.\n\n정말 단순한 일이오. 우편물을 알맞은 차원문으로 던져 넣기만 하면 되오. 아제로스 구석구석과 그 너머의 세계로 우편을 날려 보내는 거요.\n\n이걸로 게임을 해 봐도 재미있겠군. 1분 안에 우편을 몇 개나 분류할 수 있겠소?', '', '', '', '', '', '', 26365),
(50242, 'koKR', '동맹 선택', '바인, 리아드린과 대화하여 잠재적 동맹에 대해 더 알아봐야 합니다.', '바인 블러드후프와 여군주 리아드린이 우리 병력을 강화할 잠재적 동맹을 제안하러 이 자리에 왔다. 저들은 모두 호드를 강하게 만들기 위해 무엇이 필요한지 알고 있지.\n\n너는 강력한 용사임을 증명했다, $n. 어떤 동맹과 먼저 손을 잡아야 할지 네게 결정권을 맡기겠다.\n\n저들의 이야기를 듣고 결정을 내려라. 어느 쪽을 따를지 내게 얘기해 주면 호드를 강화할 작전을 개시할 것이다.', '', '', '', '', '', '', 26365),
(50239, 'koKR', '동맹 선택', '투랄리온, 알레리아와 대화하여 잠재적 동맹에 대해 더 알아봐야 합니다.', '대총독 투랄리온 님과 알레리아 윈드러너 님께서 우리와 함께할 잠재적 동맹을 제안하러 오셨어요. 두 분은 목숨을 걸고 우리 세계를 지키신 영웅이기에, 저는 이분들의 조언을 깊게 신뢰하죠.\n\n$n 님, 당신은 얼라이언스의 충직한 용사이십니다. 당신이 어떤 길을 먼저 따라야 할지 선택해 주세요.\n\n두 분께 선택에 도움이 될 이야기를 들어 보신 뒤, 어떤 동맹을 먼저 맺을지 결정하시고 제게 알려 주세요.', '', '', '', '', '', '', 26365),
(50232, 'koKR', '황혼 생존자', '미힐 보이드스트라이더의 머리카락을 가져와야 합니다.', '이 땅은 한때 황혼의 망치단 일원으로 넘쳐났었네.\n\n이교도는 모두 제거된 줄 알았는데, 어둠은 늘 그림자 속에서 살아남곤 하지.\n\n여기 있던 이교도 대부분은 살게라스의 공격으로 죽어 버렸지만, 그중 몇은 아직 살아 있네. 놈들의 지도자 미힐 보이드스트라이더는 실리더스가 붕괴되자 더 대담해졌더군.\n\n이 광신도가 생존자들을 모아 활동하지 못 하게 해야 하네. 놈의 머리카락을 가져다 주게!', '', '', '', '', '', '', 26365),
(50231, 'koKR', '애벌레 한가득', '실리시드 새끼 벌레 $1oa마리를 모아야 합니다.', '어떻게 실리시드들이 살게라스의 검이 꽂혔을 때 살아남을 수 있었을까요?!\n\n실리시드만큼 환경의 변화에 빠르게 적응하는 생물은 태어나 처음 봐요!\n\n이놈들을 길들일 방법이 있을 거예요! 이 진화 생물학을 이용해서 우리는 이득을 취하는 거죠!\n\n벌레들을 어릴 때부터 훈련시켜야겠어요! 늙고 뻣뻣한 성체는 잊어요. 우린 어린 놈들이 필요해요!\n\n실리시드 새끼 벌레를 가능한 한 많이 훔쳐다 주세요. 그러면 저는 녀석들을 훈련할 방법을 찾을게요!', '', '', '', '', '', '', 26365),
(50230, 'koKR', '힘의 근원', '실리시드 뇌 $1oa개를 모아야 합니다.', '실리시드들이 모두 잔뜩 화가 나서 그 어느 때보다도 강해졌어요!\n\n제가 생각하기에 녀석들은 땅 밑의 어떤 힘을 먹고 있는 것 같아요. 아마 살게라스의 검 근처인 것 같아요!\n\n저는 이 불가사의한 힘이 실리시드에게 어떤 영향을 끼치는지 연구하라는 지시를 받았답니다.\n\n온전한 실리시드 뇌를 최대한 많이 가져다 주시면 저는 연구를 시작할게요!', '', '', '', '', '', '', 26365),
(50229, 'koKR', '천 조각 조금', '황혼 비단 조각 $1oa개를 모아야 합니다.', '밖에서 부스럭거리는 소리가 난다 했더니, $n 님 당신이었나요? 접니다, 앵거스 스톰브루!\n\n사방에 이교도들이 깔려 있으니 목소리를 낮추는 게 좋겠습니다. 이쪽으로 가까이 기대시고, 얘기하는 동안에는 코를 막아주세요!\n\n실리더스에 남은 화장실이라곤 여기 뿐입니다! 제가 밤새 둘러보고 다녔으니 믿으셔도 됩니다. 이교도들이 눈을 뜨기 전에 살짝 볼일을 보러 몰래 들어왔는데, 음... 문제가 하나 생겼습니다...\n\n그... 뒤처리할 물건이... 없네요...\n\n제게 가져다 줄 천 조각이 좀 있는지 찾아보시고, 있다면 문틈으로 슬쩍 넣어주세요!', '', '', '', '', '', '', 26365),
(50228, 'koKR', '황혼 생존자', '미힐 보이드스트라이더의 머리카락을 가져와야 합니다.', '실리더스는 한때 황혼의 망치단 일원으로 넘쳐났었소.\n\n이교도는 모두 제거되는 듯했으나, 늘 그랬던 것처럼 어둠 속에서 살아남았소. 놈들의 수는 줄어도 교단은 그 전성기 못지않게 광신적이오.\n\n여기 있던 이교도 대부분은 살게라스의 공격으로 죽어 버렸지만, 그중 몇은 살아남았소. 놈들의 지도자 미힐 보이드스트라이더는 실리더스가 붕괴되자 더 대담해졌더군.\n\n미힐이 생존자들을 모아 활동하지 못 하게 해야 하오. 그를 반드시 막아야 하오!', '', '', '', '', '', '', 26365),
(50227, 'koKR', '애벌레 한가득', '실리시드 새끼 벌레 $1oa마리를 모아야 합니다.', '어떻게 실리시드들이 살게라스의 검이 꽂혔을 때 살아남을 수 있었을까요? 정말 혼란스러워요!\n\n실리시드만큼 환경의 변화에 빠르게 적응하는 생물은 태어나 처음 봐요.\n\n애벌레, 또는 새끼 벌레라고도 불리는 이 생물들은 둥지의 필요에 따라 다양한 실리시드 생물로 진화할 수 있어요.\n\n둥지 의식이 애벌레의 진화 발달에 신호를 보내는 걸까요? 아니면 환경 요소가 작용하는 걸까요?\n\n과학은 답을 찾아야 해요!', '', '', '', '', '', '', 26365),
(50226, 'koKR', '힘의 근원', '실리시드 뇌 $1oa개를 모아야 합니다.', '실리더스가 파괴되자 실리시드가 잠시 사라졌지만, 최근들어 다시 땅 밑에서 살아 돌아오고 있어요.\n\n이 새로운 실리시드는 전보다 더 강해진 것 같아요!\n\n제 가설은 실리시드가 살게라스의 검 밑에 있는 어떤 힘의 근원을 먹고 있다는 거예요.\n\n저는 이 수수께끼의 힘이 실리시드 둥지에 끼치는 영향을 연구하라는 과제를 받았죠.\n\n온전한 실리시드 뇌를 최대한 많이 가져다 주시면 저는 연구를 시작할게요!', '', '', '', '', '', '', 26365),
(50131, 'koKR', '외부의 관점', '스포어가르와 중립적 평판이 되어야 합니다.', '우리 부족을 위해 해주신 모든 일에 감사드려요. 하지만 주민들이 여전히 외부인을 두려워하고 있어요.\n\n부화의 골짜기에 있는 수렁군주와 싸워 포자 주머니를 구해주세요. 우리 부족의 미래가 여기에 달렸어요!', '', '', '', '', '', '', 26365),
(50130, 'koKR', '우호적인 몸짓', '스포어가르와 약간 우호적 평판이 되어야 합니다.', '습지대의 삶은 평온했던 적이 없소, $n. 여길 고향이라 부르는 이들과 조화를 이루어 살고 싶지만 이곳의 적들이 우리의 존재를 위협하고 있소이다.\n\n우리와 함께 움직이겠소? 스포어가르 부족을 도와주시오.', '', '', '', '', '', '', 26365),
(50071, 'koKR', '빛을 위하여!', '스톰윈드로 가서 국왕 안두인 린과 대화해야 합니다.', '빛벼림 드레나이들도 얼라이언스에 합류하게 된 걸 기쁘게 생각할 겁니다.\n\n이제 저희와 당신의 목표는 같습니다, $n 님.\n\n스톰윈드로 돌아갑시다. 파리야 대장이 저희 병사들과 이야기를 나눌 겁니다.', '', '', '', '', '', '', 26365),
(50055, 'koKR', '대변자의 관점', '실리더스에 있는 마그니 브론즈비어드에게 카드가의 편지를 가져가야 합니다.', '살게라스가 우리 세계에 상처를 남겼소, $n. 오래도록 치유되지 않을 상처를 말이오...\n\n하나 얼라이언스와 호드 사이의 상처 또한 치유되지 않는 것 같소.\n\n아제로스의 파괴를 막기 위해 싸우다 수많은 생명을 잃었소. 더 이상의 희생을 가만히 보고만 있지 않을 것이오.\n\n가기 전에 부탁할 것이 하나 있소. 이 편지를 마그니 브론즈비어드에게 가져다 주겠소? 마그니는 실리더스에서 그 상처를 치유할 방법을 찾고 있소.\n\n이 편지에 그가 필요로 하는 정보가 들어 있을 것이오.', '', '', '', '', '', '', 26365),
(50054, 'koKR', '들끓는 해안', '들끓는 해안 전장을 완료해야 합니다.', '실리더스 앞바다의 한 섬에 아제라이트 자원이 풍부하다는 보고를 받았네.\n\n얼라이언스가 이 자원의 존재를 눈치채기 전에 그 섬을 차지하는 게 중요하네.\n\n나타노스 블라이트콜러 님께서 직접 원정대를 지휘하고 계신데, 자네의 합류를 요청하셨네. 명예롭게 나아가게!', '', '', '', '', '', '', 26365),
(50053, 'koKR', '게으른 발굴조사단원!', '게으른 발굴조사단원 $1oa명에게 동기를 부여해야 합니다.', '보아하니 자네는 오크의 일을 할 때 고블린을 절대 믿지 않는 것 같군.\n\n갤리윅스는 대족장을 섬길지 모르나, 일을 제대로 처리하려면 놈들을 잘 감시해야 하네!\n\n난 진작에 고블린 일꾼 중에 근무시간에 빈둥거리고 잠을 자는 놈이 많다는 걸 알고 있었네. 호드는 게으름을 용서하지 않네!\n\n작업장으로 가서 저 게으른 발굴조사단원들을 일터로 돌려보내게!', '', '', '', '', '', '', 26365),
(50052, 'koKR', '첩자는 용납할 수 없다', '실리더스에서 SI:7 첩보원 $1oa명을 처치해야 합니다.', '아, $n. 와 줘서 고마워.\n\n정말 감당이 안 돼! 살인 벌레에 수상한 물질에... 땅에 꼿꼿이 꽂혀 있는 거대한 칼이라니... 영 기분 나쁜 곳이야!\n\n설상가상으로 얼라이언스 첩자들이 기지 주변을 기웃거린다는 보고를 받고 있어!\n\n잘 들으라고. 어둠의 여왕님은 이 수수께끼의 광물에 대한 계획을 갖고 계셔. 그리고 나는 여왕님을 실망시키지 않을 거야. 무슨 뜻인지 알겠지?\n\n채광 기지로 가서 SI:7 첩자들을 제거해 줘. 놈들이 작전을 다 망쳐 버리기 전에 말야!', '', '', '', '', '', '', 26365),
(50049, 'koKR', '대변자의 관점', '실리더스에 있는 마그니 브론즈비어드에게 카드가의 편지를 가져가야 합니다.', '살게라스가 우리 세계에 상처를 남겼소, $n. 오래도록 치유되지 않을 상처를 말이오...\n\n하나 얼라이언스와 호드 사이의 상처 또한 치유되지 않는 것 같소.\n\n아제로스의 파괴를 막기 위해 싸우다 수많은 생명을 잃었소. 더 이상의 희생을 가만히 보고만 있지 않을 것이오.\n\n가기 전에 부탁할 것이 하나 있소. 이 편지를 마그니 브론즈비어드에게 가져다 주겠소? 마그니는 실리더스에서 그 상처를 치유할 방법을 찾고 있소.\n\n이 편지에 그가 필요로 하는 정보가 들어 있을 것이오.', '', '', '', '', '', '', 26365),
(50048, 'koKR', '들끓는 해안', '들끓는 해안 전장을 완료해야 합니다.', '실리더스 앞바다의 한 섬에 아제라이트 자원이 풍부하다는 보고를 받았소.\n\n호드가 이 자원의 존재를 눈치채기 전에 그 섬을 차지해야 하오.\n\n단장 마티아스 쇼가 직접 원정대를 지휘하고 있는데, 당신의 합류를 요청했소. 행운을 비오!', '', '', '', '', '', '', 26365),
(50047, 'koKR', '무료 표본', '수수께끼의 광물 표본 $1oa개를 모아야 합니다.', '국왕께서 SI:7에게 고블린 채광 작전에 잠입해 이 수수께끼의 물질에 대해 알게 되는 모든 정보를 모아오라고 하셨소.\n\n내 요원이 기지로 숨어들긴 했지만, 채굴된 광물 표본을 손에 넣기엔 시간이 촉박하오.\n\n다시 말하면 놈들과 직접 맞닥뜨릴 자가 필요하다는 얘기요. 쏟아지는 역경에 맞서 싸움을 지휘하고 살아 돌아올 자 말이오.\n\n돌아오면 광물 표본을 카르거스 스톤맨틀에게 가져다 주시오. 연구를 해봐야겠소.', '', '', '', '', '', '', 26365),
(50046, 'koKR', '파괴 공작', '다이너마이트 묶음 $1oa개를 설치해야 합니다.', '호드 놈들이 상처의 땅에서 뻔뻔스럽게 수수께끼의 광물을 채굴하고 있어요.\n\n다행인 것은 고블린의 기계공학이 놈리건의 기계공학만큼 정교하지 않다는 점이죠. 조금 구식인 다이너마이트만 있어도 고블린 분쇄기를 완전히 망가뜨릴 수 있어요.\n\n그 조잡한 고블린 기계 말이에요!\n\n이 다이너마이트 묶음을 받고 채광 기지 곳곳에 있는 비활성화된 분쇄기에 심어 주세요.\n\n저 탐욕스러운 고블린 녀석들, 분쇄기를 고치려면 몇 주는 걸릴 거예요!', '', '', '', '', '', '', 26365),
(50035, 'koKR', '브렛 테스트 - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'koKR', '상처의 감시자', '실리더스에 있는 그롤 워블레이드와 대화해야 합니다.', '빌지워터 무역회사가 벌써 실리더스에서 채광 작업을 시작했네. 이 수수께끼의 물질을 추출하는 게지.\n\n대족장께서 자네가 실리더스에서 직접 채광 작업을 감독하면서 얼라이언스의 방해공작을 막아달라고 부탁하셨네.\n\n갤리윅스는 자네가 임명되어 기분이 언짢은 모양이지만, 아무도 실바나스 님을 거스를 순 없지...\n\n지금 바로 실리더스로 가게. 기지 지휘관이 자네를 기다리고 있다네.', '', '', '', '', '', '', 26365),
(49981, 'koKR', '상처의 감시자', '실리더스에서 SI:7 지휘관을 만나야 합니다.', '이미 실리더스에 기지를 세울 요원들을 보내 놓았습니다.\n\n호드의 움직임을 감시하고 이 수수께끼의 물질에 대해 알아낼 수 있는 정보는 모두 모으는 것이 그들의 임무입니다.\n\n저희 SI:7 지휘관 하나가 거기서 당신을 기다리고 있습니다. 서둘러 가 보십시오.', '', '', '', '', '', '', 26365),
(49977, 'koKR', '오그리마로부터의 소환', '나타노스 블라이트콜러와 대화해야 합니다.', '대족장께서 자네에게 가능한 한 빨리 오그리마로 돌아올 것을 부탁하셨네. 자네가 도와줘야 할 급한 일이 생겼어.\n\n도착하는 대로 자세한 내용을 알려주겠네.\n\n그롬마쉬 요새 입구 근처에서 기다리겠네. 어둠의 여왕님께서 자네에게 부탁할 계획을 짜 놓으셨네, $n. 나 같으면 그분을 기다리게 하지 않을 걸세...', '', '', '', '', '', '', 26365),
(49976, 'koKR', '몰락한 자가 남긴 것', '마티아스 쇼와 대화해야 합니다.', '오늘 아침, 사자의 안식처에서 안두인 린 국왕님이 스톰윈드 시민들에게 전쟁이 끝났다는 사실을 공식적으로 알리셨습니다.\n\n시민들이 떠난 뒤 저는 국왕님과 그분의 조언자를 따로 뵈어 실리더스에서 발견된 수수께끼의 물질에 대해 말씀드렸지요. \n\n이 반짝이는 광물에는 엄청난 힘이 있어 보입니다. 게다가 호드는 이미 거대한 상처에서 이를 채굴하고 있습니다.\n\n국왕께서 중요한 임무를 맡기기에 앞서, 당신에게 자세한 얘기를 들려 드리라고 명하시더군요.', '', '', '', '', '', '', 26365),
(49973, 'koKR', '탈리스라의 영지', '수라마르의 첫 번째 비전술사의 영지에서 여군주 리아드린을 만나야 합니다.', '테론 섭정께서 나이트본을 실버문으로 초대했습니다.\n\n우선 이 사실을 그들에게 전해야 합니다. 저는 이런 일은 얼굴을 보고 직접 얘기하는 게 좋다고 생각해요.\n\n저와 함께 수라마르로 가서 여군주 탈리스라를 만나 주시겠습니까?', '', '', '', '', '', '', 26365),
(49933, 'koKR', '호드를 위하여', '오그리마 대사관에서 대사 블랙가드를 만나야 합니다.', '홀로 섰던 수많은 세월을 지나, 우리는 다시 위대한 존재의 일부가 될 태세를 갖추고 있어요. 호드와 맺은 동맹으로 새로운 시작을 할 수 있게 된 거죠. 낭비해선 안 되는 특별한 기회예요.\n\n대족장님의 대사가 오그리마에서 $n 님을 기다리고 있어요. 거기서 당신은 아제로스를 가로지르는 여정을 시작하게 될 거예요. 이 땅을 지키기 위해, 샬도레이가 설 곳을 손에 넣기 위해 싸우세요.\n\n우리가 누구인지 세상에 보여주세요, $c|1이여;여;. 호드의 명예를 지키면, 수라마르의 명예 또한 지키게 될 겁니다.', '', '', '', '', '', '오그리마 대사관에서 대사 블랙가드를 만나야 합니다.', 26365),
(49930, 'koKR', '동맹 소집', '오그리마 대사관에서 대족장 실바나스 윈드러너를 만나야 합니다.', '군단과의 싸움에서 모든 것을 쏟은 탓에, 호드의 병력은 많이 약해져 있다. 적들은 우리의 것을 뺏으려는 생각에 침까지 흘릴 테지. 용납할 수 없다.\n\n오그리마에 대사관을 만들어 두었다. 거기서 새로운 세력을 결집할 것이다. 거기서 만나지.', '', '', '', '', '', '', 26365),
(49929, 'koKR', '동맹 소집', '스톰윈드 대사관에서 국왕 안두인 린을 만나야 합니다.', '호드는 아제로스의 피를 차지하기 위해서 뭐든 할 거예요. 군단과의 전투 이후, 우리 병력은 호드에 맞서기엔 너무 약해졌어요.\n\n이제 얼라이언스에 새 세력을 편입시킬 때입니다. 이 모든 걸 통제하려고 스톰윈드에 대사관을 세웠어요. 거기서 만나죠.', '', '', '', '', '', '', 26365),
(49928, 'koKR', '공허의 유산', '텔로그러스 균열에서 알레리아 윈드러너를 만나야 합니다.', '당신은 아제로스를 모험하며, 자신을 끊임없이 삼키려는 힘을 완벽히 통제하는 법을 배웠어요.\n\n용사여, 당신은 얼라이언스의 강력한 무기가 되었군요. 공허의 균열로 돌아오세요. 당신의 업적을 기리도록 하죠.', '', '', '', '', '', '', 26365),
(49866, 'koKR', '판다리아로!', '스톰윈드에 있는 렐 나이트윈드에게 보고해야 합니다.', '바리안 린 국왕 폐하와의 작전 회의가 있을 예정이니, 지금 즉시 스톰윈드 왕궁으로 와주십시오.$b$b남쪽 해상에서 충돌이 있었다는 소식이 들어왔습니다. 자세한 상황을 조사하는 데 경험이 많은 $c의 도움이 필요합니다.', '', '', '', '', '', '', 26365),
(49865, 'koKR', '대격변 최초 응답자', '스톰윈드에 있는 대지예언자 나라트에게 보고해야 합니다.', '용족 죽음의 위상 데스윙이 하수인들을 보내 생명이 살아 숨쉬는 아제로스를 쓸어버리려 합니다.$b$b대지예언자 나라트라면 하이잘 산, 바쉬르, 그리고 데스윙이 노리는 다른 곳들에 대해 자세히 얘기해 줄 수 있을 것입니다.', '', '', '', '', '', '', 26365),
(49864, 'koKR', '두 곳의 최전방 전투', '특별 수행 임무에서 다음 모험 장소를 정해야 합니다.', '$n 님! 당신은 다시 한 번 중요한 사안으로 얼라이언스의 지원 요청을 받았습니다.\n\n얼라이언스의 군대를 도와 데스윙과 황혼의 망치단에 맞서겠습니까?\n\n아니면 남쪽의 판다리아로 날아가 호드보다 먼저 그 땅의 자원을 차지하겠습니까?\n\n마음을 정하고 전장에 뛰어드십시오!', '', '', '', '', '', '', 26365),
(49863, 'koKR', '노스렌드로!', '배를 타고 노스렌드로 가야 합니다.$b$b북풍의 땅으로 가는 배는 스톰윈드 항구에서, 울부짖는 협만으로 가는 배는 저습지에서 탈 수 있습니다.', '리치 왕이 북쪽 땅을 휘젓고 다닙니다.$b$b언데드 군단이 그의 명령에 따라 노스렌드를 종횡무진 행군하며 발을 딛는 곳마다 역병을 퍼뜨리고 있습니다.$b$b그들을 막으려면 당신이 필요합니다, $n 님.', '', '', '', '', '', '', 26365),
(49862, 'koKR', '아웃랜드로!', '저주받은 땅에 있는 어둠의 문에 들어가야 합니다.', '셀 수 없이 많은 악마의 군대, 불타는 군단이 드레나이들의 황폐한 고향 땅 아웃랜드를 공격했습니다. 악마 침공으로 아웃랜드의 모든 것이 철저히 전멸했지만, 그 공격을 견디고 그나마 남아 있는 것이라도 지키려면 얼라이언스 모험가의 힘이 필요합니다.$b$b저주받은 땅으로 가서 어둠의 문을 통과하십시오.', '', '', '', '', '', '', 26365),
(49860, 'koKR', '모험이 부른다', '특별 수행 임무에서 다음 모험 장소를 정해야 합니다.', '당신의 영웅적인 모험담이 아제로스와... 그 너머의 세계까지 퍼졌습니다. 세계 곳곳에서 당신의 능력을 필요로 하고 있습니다.\n\n어둠의 문으로 들어가 아웃랜드의 조각난 세계에서 불타는 군단과 맞서 싸우시겠습니까?\n\n아니면 북쪽의 노스렌드로 날아가 리치 왕과의 전투에 합류하시겠습니까?\n\n선택은 당신의 몫입니다, 모험가여.', '', '', '', '', '', '', 26365),
(49855, 'koKR', '하이잘 산의 재앙', '세나리온 사절 블랙후프와 대화하고 달숲으로 이동해야 합니다.', '세나리온 의회에 있는 우리 형제들에 따르면 데스윙의 하수인들이 세계수에 대대적인 공격을 감행해 그 나무를 완전히 불태우려 한다고 하오!$B$B세나리온 사절 블랙후프와 이야기를 나누고 달숲으로 이동하시오. 블랙후프는 오그리마 지혜의 골짜기에 있는 타우렌 지구에 있소.', '달숲으로 가는 이동수단 확보', '', '', '', '', '달숲의 밤의 안식처에 있는 사절 윈드송과 대화해야 합니다.', 26365),
(49852, 'koKR', '판다리아로!', '오그리마의 그롬마쉬 요새에 보고해야 합니다.', '지금 즉시 그롬마쉬 요새에 보고하십시오.$b$b남쪽에 자원이 풍부한 대륙이 새로 발견되었다는 소식을 받았습니다. 당신에게 소규모 파견대를 붙여 줄 테니 호드를 위해 그 땅을 차지하십시오.', '', '', '', '', '', '', 26365),
(49851, 'koKR', '대격변 대응 선발대', '오그리마에 있는 선견자 크로가에게 보고해야 합니다.', '용족 죽음의 위상 데스윙이 하수인들을 보내 생명이 살아 숨쉬는 아제로스를 쓸어버리려 합니다.$b$b오그리마에 있는 선견자 크로가라면 하이잘 산, 바쉬르, 그리고 데스윙이 노리는 다른 곳들에 대해 자세히 얘기해 줄 수 있을 것입니다.', '', '', '', '', '', '', 26365),
(49846, 'koKR', '두 곳의 최전방 전투', '특별 수행 임무에서 다음 모험 장소를 정해야 합니다.', '$n 님! 당신은 다시 한 번 중요한 사안으로 호드의 지원 요청을 받았습니다.\n\n호드의 군대를 도와 데스윙과 황혼의 망치단에 맞서겠습니까?\n\n아니면 남쪽의 판다리아로 날아가 얼라이언스가 도착하기 전에 그곳의 자원을 차지하겠습니까?\n\n마음을 정하고 전장에 뛰어드십시오!', '', '', '', '', '', '', 26365),
(49817, 'koKR', '노스렌드로!', '비행선을 타고 노스렌드로 가야 합니다.$b$b울부짓는 협만으로 가는 비행선은 티리스팔 숲에서, 북풍의 땅으로 가는 비행선은 오그리마에서 탈 수 있습니다.', '리치 왕이 북쪽 땅을 휘젓고 다닙니다.$b$b언데드 군단이 그의 명령에 따라 노스렌드를 종횡무진 행군하며 발을 딛는 곳마다 역병을 퍼뜨리고 있습니다.$b$b그들을 막으려면 당신이 필요합니다, $n 님.', '', '', '', '', '', '', 26365),
(49816, 'koKR', '아웃랜드로!', '저주받은 땅에 있는 어둠의 문에 들어가야 합니다.', '셀 수 없이 많은 악마의 군대, 불타는 군단이 오크들의 황폐한 고향땅 아웃랜드를 공격했습니다. 악마 침공으로 아웃랜드의 모든 것이 철저히 전멸했지만, 그 공격을 견디고 그나마 남아 있는 것이라도 지키려면 호드 모험가의 힘이 필요합니다.$b$b저주받은 땅으로 가서 어둠의 문을 통과하십시오.', '', '', '', '', '', '', 26365),
(49813, 'koKR', '모험이 부른다', '특별 수행 임무에서 다음 모험 장소를 정해야 합니다.', '당신의 영웅적인 모험담이 아제로스와... 그 너머의 세계까지 퍼졌습니다. 세계 곳곳에서 당신의 능력을 필요로 하고 있습니다.\n\n어둠의 문으로 들어가 아웃랜드의 조각난 세계에서 불타는 군단과 맞서 싸우시겠습니까?\n\n아니면 북쪽의 노스렌드로 날아가 리치 왕과의 전투에 합류하시겠습니까?\n\n선택은 당신의 몫입니다, 모험가여.', '', '', '', '', '', '', 26365),
(49812, 'koKR', '달라란의 델라스', '달라란의 안토니다스 기념지에서 델라스 문팽을 찾아야 합니다.', '델라스 문팽이 도움을 요청했습니다. 델라스를 먼저 도와주면 그녀도 지휘의 고서 해독을 도와줄 겁니다.$B$B그대가 가능한 한 빨리 델라스를 찾아 주십시오. 위대한 키린 토의 지도자 안토니다스의 기념비를 자주 찾는 걸로 알고 있습니다.', '', '', '', '', '', '달라란의 안토니다스 기념지에서 델라스 문팽을 찾아야 합니다.', 26365),
(49788, 'koKR', '얼라이언스를 위하여', '스톰윈드 대사관에서 대사 무어가드를 만나야 합니다.', '속삭임을 지배하는 건 꾸준한 집중과 수련을 요하는 일이에요. 당신이 보여준 헌신은 렌도레이라는 이름을 지닌 모두에게 귀감이 되었어요. 이제 우리는 공허의 아이들이 되었으니까요.\n\n여기서 많은 것을 배웠겠지만, 이제 텔로그러스 균열 너머의 세계를 모험할 때가 왔어요.\n\n얼라이언스의 중심 도시, 스톰윈드로 가세요. 국왕의 대사가 그대를 맞아주고 그대의 여행길을 인도해 줄 거예요.\n\n아제로스를 지켜야만 해요. 이 부름에 답할 적임자로 그대만 한 이가 없어요, $p|1이여;여;.', '', '', '', '', '', '스톰윈드 대사관에서 대사 무어가드를 만나야 합니다.', 26365),
(49787, 'koKR', '유령의 땅', '유령의 땅에 있는 엄브릭의 집을 찾아야 합니다.', '엄브릭이라고 하는 블러드 엘프와 그의 추종자들이 공허 마법을 연구했다는 이유로 실버문에서 쫓겨났다는군요.\n\n금지된 연구를 했다고는 하지만 너무 극단적인 조치였죠.\n\n하지만 얼라이언스에게는 새로운 가능성이 열린 거예요. 이 추방자들을 찾으면 우리의 대의에 함께하게 만들 수 있을 겁니다. 그리고 더 중요한 건, 공허가 그들을 잠식하기 전에 그 힘을 이해하는 법을 알려줄 수 있다는 거죠.', '', '', '', '', '', '', 26365),
(49784, 'koKR', '나이트본의 유산', '밤의 요새에 있는 첫 번째 비전술사 탈리스라를 만나야 합니다.', '당신은 처음으로 아제로스를 향해 발을 내딛는 샬도레이들 중 하나예요. 하지만 지금은 고향에 돌아올 시간이에요.\n\n수라마르로 돌아와요. 이 세상에서 우리 자리를 되찾는 걸 도와 준 것에 대해 감사를 표하고 싶어요.', '', '', '', '', '', '', 26365),
(49783, 'koKR', '높은산의 유산', '천둥 토템에서 마일라 하이마운틴을 만나야 합니다.', '그대는 오랜 시간 아제로스에 머물렀습니다. 그리고, 그 시간 동안 높은산의 명예를 드높여 주었죠.\n\n이제 천둥토템이 그대를 부릅니다. 우릴 위해 해 주신 모든 일들에 진심으로 감사드립니다.', '', '', '', '', '', '', 26365),
(49782, 'koKR', '빛벼림의 유산', '구원호에서 대장 파리야를 만나야 합니다.', '용사님은 얼라이언스를 위해 엄청난 일을 해 주셨습니다. 제가 하더라도 그렇게 할 수 있을지 의문이군요. 뭐, 할 수야... 있겠지만요.\n\n용사님, 구원호에서 만나도록 하죠. 그대의 업적을 기릴 시간이 왔습니다.', '', '', '', '', '', '', 26365),
(49773, 'koKR', '호드를 위하여', '오그리마 대사관에서 대사 블랙가드를 만나야 합니다.', '높은산에 새로운 시대가 도래했습니다, $n 님.\n\n몇 세기를 지나 칼림도어에 있는 우리 동족과의 유대감이 다시 돈독해졌습니다. 그리고 우리의 땅을 지키기 위한 싸움을 통해, 호드 안에서 새로운 우정을 찾았습니다.\n\n동맹에게 우리가 가져다줄 힘을 보여주기 위해서라도, 그대 같은 용감한 타우렌이라면 모험의 부름에 귀를 기울이고 계시겠죠.\n\n그대는 오그리마에 초대받았습니다. 대족장님의 대사관에서 대사를 만나보고 아제로스를 가로지르는 여정을 시작하십시오.\n\n대지모신이 $n 님과 함께하기를.', '', '', '', '', '', '오그리마 대사관에서 대사 블랙가드를 만나야 합니다.', 26365),
(49772, 'koKR', '얼라이언스를 위하여', '스톰윈드 대사관에서 대사 무어가드를 만나야 합니다.', '빛의 군대는 하나의 목적 아래 창설되었습니다. 군단의 격퇴 말입니다. 비록 그 목적은 이루었지만, 새로운 임무가 우리 앞에 놓여 있습니다.\n\n아제로스는 적으로 둘러싸인 세계입니다. 그간 했던 훈련을 바탕으로 어둠에 맞서 싸우고 얼라이언스의 이상을 지켜 주십시오.\n\n스톰윈드로 가십시오. 대사관에서 국왕님의 대사를 찾아 온 땅에 빛의 정의를 퍼뜨릴 준비를 하십시오.\n\n페타 소네스 가메라, $n 님.', '', '', '', '', '', '스톰윈드 대사관에서 대사 무어가드를 만나야 합니다.', 26365),
(49756, 'koKR', '어둠의 세력', '울기네스의 전령 키텔레크를 처치해야 합니다.', '고대 신들이 썬더 블러프에 얼굴 없는 자 하나를 몰래 보냈네. 그 악마 같은 놈이 내 부족민들의 도시를 더럽히고 있네!\n\n함께 힘을 합쳐 이 추찹한 놈으로부터 우리 도시를 지켜내야 하네.\n\n전령을 처치하면 멀고어에 있는 다른 적들도 모두 몰아낼 수 있을 걸세. $n, 자네의 무기가 빗나가지 않기를!', '', '울기네스의 전령', '키텔레크', '', '', '', 26365),
(49698, 'koKR', '빛으로 벼려지다', '투랄리온과 함께 구원호로 가야 합니다.', '$n 님, 당신은 제 동료들에게 어떤 힘이 있는지 직접 보셨습니다. 저희는 수천 년이 넘는 시간 동안 군단에 맞서며 전문가로 벼려져 왔습니다.\n\n이제 얼라이언스의 형제자매들과 힘을 합쳐 무기를 들어야 할 때가 왔습니다.\n\n구원호로 오십시오. 파리야 대장과 나눌 이야기가 많을 것 같군요.', '', '', '', '', '', '구원호에서 대장 파리야를 만나야 합니다.', 26365),
(49614, 'koKR', '나이트본', '오그리마에 있는 대족장에게 가야 합니다.', '대족장님이 우리가 성공한 걸 알면 기뻐하실 겁니다.\n\n탈리스라와 나이트본은 강력한 동맹군이 되어 줄 겁니다. 확실합니다.', '', '', '', '', '', '', 26365),
(49613, 'koKR', '실버문', '실버문에서 여군주 리아드린을 만나야 합니다.', '나이트본은 수천 년간 세상과 단절돼 있었습니다. 그들이 다시 세상에 모습을 드러냈으니, 우리가 옳은 길로 이끌어야 합니다.\n\n테론 섭정께서 우리가 나이트본을 만날 수 있도록 그들을 실버문으로 초대했습니다.\n\n탈리스라와 함께 실버문으로 가시죠, $n 님.', '', '', '', '', '', '', 26365),
(49567, 'koKR', '영웅의 부름: 나그란드!', '드레노어의 나그란드에 있는 부관 발포르와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 드레노어의 나그란드에 있는 부관 발포르에게 보고하십시오.\n\n이렐이 전쟁노래 부족과의 전투를 돕기 위해 나그란드에 전초기지를 세울 것을 권했습니다.\n\n비행 탈것을 타고 나그란드로 가서 부관 발포르와 보급 짐마차를 찾으십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '드레노어의 나그란드에 있는 부관 발포르와 대화해야 합니다.', 26365),
(49566, 'koKR', '영웅의 부름: 아라크 첨탑!', '아라크 첨탑에 있는 아지크와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 아라크 첨탑에 있는 아지크에게 보고하십시오.\n\n날개가 없는 아라코아가 아라크 첨탑을 버리고 도망치는 모습이 목격되고 있습니다. 소문에 따르면 강철 호드의 초강력 무기를 피해 달아나는 것이라고 합니다.\n\n아라코아를 본거지에서 떠나게 한 원인이 무엇인지, 그들의 날개가 메마른 이유가 무엇인지 알아내야 합니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '아라크 첨탑에 있는 아지크와 대화해야 합니다.', 26365),
(49565, 'koKR', '영웅의 부름: 탈라도르!', '탈라도르에 있는 현장감독 엑소스와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 탈라도르에 있는 현장감독 엑소스에게 보고하십시오.\n\n강철 호드가 탈라도르를 지배하려 합니다. 이곳은 아무런 저항 없이 드레노어 곳곳으로 빠르게 병력을 보내기 좋은 완벽한 요충지입니다.\n\n현장감독 엑소스가 지금 탈라도르에 전초기지를 건설하고 있습니다. 그곳으로 가서 강철 호드를 막으십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '탈라도르에 있는 현장감독 엑소스와 대화해야 합니다.', 26365),
(49564, 'koKR', '영웅의 부름: 고르그론드!', '고르그론드에 있는 이렐과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 고르그론드에 있는 이렐에게 보고하십시오.\n\n이렐과 마라아드가 고르그론드의 밀림을 간단히 조사해 줄 것을 부탁했습니다.\n\n지형을 잘 파악해서 강철 호드의 약점을 이용할 방법을 알아내려는 것입니다.\n\n그들의 조사를 도와주십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '고르그론드에 있는 이렐과 대화해야 합니다.', 26365),
(49562, 'koKR', '영웅의 부름: 어둠달 골짜기!', '드레노어의 어둠달 골짜기에 있는 예언자 벨렌과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 드레노어의 어둠달 골짜기에 있는 예언자 벨렌 님께 보고하십시오.\n\n어둠의 문을 파괴해 아제로스를 침략하려는 강철 호드를 막아냈지만 이제는 놈들과 싸워야 합니다.\n\n예언자 벨렌 님이 축복해 주신다면, 드레노어에서 아제로스의 적과 대치하기 좋은 전략적인 위치를 잡을 수 있을 겁니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '드레노어의 어둠달 골짜기에 있는 예언자 벨렌과 대화해야 합니다.', 26365),
(49561, 'koKR', '영웅의 부름: 공포의 황무지!', '용의 척추에 있는 명궁 리와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 영원꽃 골짜기의 용의 척추에 있는 명궁 리에게 보고하십시오.\n\n음영파가 판다리아 전역을 침공하려는 사마귀 군단을 막으려고 합니다.\n\n명궁 리를 만나려면 연을 타고 용의 척추까지 날아가십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '용의 척추에 있는 명궁 리와 대화해야 합니다.', 26365),
(49560, 'koKR', '영웅의 부름: 탕랑 평원!', '탕랑 평원에 있는 타란 주와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 탕랑 평원에 있는 타란 주에게 보고하십시오.\n\n음영파가 야운골의 손아귀에서 자신들의 땅을 되찾으려 하지만 전투를 벌일 인원이 부족합니다.\n\n야운골을 공격하려는 음영파에게 힘을 실어 주십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '탕랑 평원에 있는 타란 주와 대화해야 합니다.', 26365),
(49559, 'koKR', '영웅의 부름: 쿤라이 봉우리!', '네 바람의 계곡에 있는 그루멀 전령과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 쿤라이 봉우리에 있는 전승지기 초를 보조하십시오.\n\n네 바람의 계곡에 있는 그루멀 전령과 대화하십시오. 쿤라이 봉우리로 가는 길을 알려줄 것입니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '네 바람의 계곡에 있는 그루멀 전령과 대화해야 합니다.', 26365),
(49558, 'koKR', '영웅의 부름: 크라사랑 밀림!', '크라사랑 밀림에 있는 컨컨과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 크라사랑 밀림에 있는 쥬의 감시초소에 보고하십시오.\n\n크라사랑 밀림에 사는 판다렌이 평소와는 다른 모습이라는 소문이 돌고 있습니다.\n\n쥬의 감시초소에 있는 컨컨과 대화해 보십시오. 그라면 판다렌이 무기력해진 원인이 무엇인지 잘 알고 있을지도 모릅니다.', '', '', '', '', '', '크라사랑 밀림에 있는 컨컨과 대화해야 합니다.', 26365),
(49557, 'koKR', '영웅의 부름: 네 바람의 계곡!', '네 바람의 계곡에 있는 첸 스톰스타우트와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 네 바람의 계곡에 있는 첸 스톰스타우트에게 보고하십시오.\n\n모험심 많은 스톰스타우트의 정신을 얼라이언스도 본받아야 합니다.\n\n스톰스타우트와 함께 여행하며 판다리아가 품은 비밀을 알아내십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '네 바람의 계곡에 있는 첸 스톰스타우트와 대화해야 합니다.', 26365),
(49556, 'koKR', '영웅의 부름: 비취 숲!', '스톰윈드 왕궁에 있는 대제독 제스테레스에게 보고해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 스톰윈드에 있는 스톰윈드 왕궁에 보고하십시오.\n\n제스테레스 대제독께서 매우 중요한 임무를 수행할 특수 부대를 꾸리고 있습니다.\n\n즉시 스톰윈드 왕궁으로 와서 작전을 전달받으십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '', 26365),
(49555, 'koKR', '영웅의 부름: 얼음왕관!', '수정노래 숲 상공의 달라란에 있는 장교 반 로셈과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 수정노래 숲 상공의 달라란에 있는 장교 반 로셈에게 보고하십시오.\n\n용맹의 원정대가 얼음왕관 성채와 아서스를 공격할 병력을 모으기 위해 모험가들의 힘을 필요로 하고 있습니다.\n\n리치 왕이 저지른 짓에 대한 대가를 치르게 할 때입니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '수정노래 숲 상공의 달라란에 있는 장교 반 로셈과 대화해야 합니다.', 26365),
(49554, 'koKR', '영웅의 부름: 폭풍우 봉우리!', '수정노래 숲 상공의 달라란에 있는 린 두옥테인과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 수정노래 숲 상공의 달라란에 있는 린 두옥테인에게 보고하십시오.\n\nK3의 고블린들이 폭풍우 봉우리에 도시를 짓고, 모험가들을 소집하기 위해 대사를 보내왔습니다.\n\n린과 대화하여 K3의 시민들이 필요한 게 무엇인지 알아보십시오. 그들은 리치 왕과 맞서 싸울 때 강력한 동맹이 되어 줄 겁니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '수정노래 숲 상공의 달라란에 있는 린 두옥테인과 대화해야 합니다.', 26365),
(49553, 'koKR', '영웅의 부름: 숄라자르 분지!', '수정노래 숲 상공의 달라란에 있는 대마법사 펜타루스와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 수정노래 숲 상공의 달라란에 있는 대마법사 펜타루스에게 보고하십시오.\n\n헤멧 네싱워리가 이끄는 원정대가 숄라자르 분지로 답사를 떠났는데 실종되고 말았습니다.\n\n저희는 풀이 우거진 숄라자르의 지형을 잘 모르지만, 그 분지에 리치 왕과 싸울 때 유리한 점이 될 수 있는 비밀이 숨어 있을지도 모릅니다.\n\n얼라이언스에 영광을!', '', '', '', '', '', '수정노래 숲 상공의 달라란에 있는 대마법사 펜타루스와 대화해야 합니다.', 26365),
(49552, 'koKR', '영웅의 부름: 줄드락!', '줄드락에 있는 성전사 란틴가 경과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 줄드락에 있는 성전사 란틴가 경에게 보고하십시오.\n\n스컬지가 줄드락 시민을 닥치는 대로 학살하면서 그들을 스컬지 군대의 일원으로 탈바꿈시키고 있습니다.\n\n은빛십자군과 칠흑의 기사단을 도와 이 위협을 막아 주십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '줄드락에 있는 성전사 란틴가 경과 대화해야 합니다.', 26365),
(49551, 'koKR', '영웅의 부름: 울부짖는 협만!', '울부짖는 협만에 있는 맥칼로이와 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 울부짖는 협만에 있는 맥칼로이에게 보고하십시오.\n\n항구도시 발가드는 울부짖는 협만의 중심부에 있습니다. 얼라이언스가 스컬지를 공격하기 유리한 위치이지요.\n\n거기에 주둔하는 병사들을 도우십시오.\n\n얼라이언스에 영광을!', '', '', '', '', '', '울부짖는 협만에 있는 맥칼로이와 대화해야 합니다.', 26365),
(49550, 'koKR', '영웅의 부름: 어둠달 골짜기!', '아웃랜드의 어둠달 골짜기에 있는 편대사령관 누아인과 대화해야 합니다.', '얼라이언스의 모든 신체 건강한 자들은 아웃랜드의 어둠달 골짜기에 있는 편대사령관 누아인에게 보고하십시오.\n\n불타는 군단이 와일드해머 성채를 포위하고 우리 시민들을 위험에 빠뜨려 목숨을 위협하고 있습니다.\n\n성채의 병력을 강화해 불타는 군단의 무자비한 공격을 막아내야 합니다!\n\n얼라이언스에 영광을!', '', '', '', '', '', '아웃랜드의 어둠달 골짜기에 있는 편대사령관 누아인과 대화해야 합니다.', 26365),
(49549, 'koKR', '대족장의 명령: 나그란드!', '드레노어의 나그란드에 있는 바위경비병 브록스와 대화해야 합니다.', '신체 건강한 호드 병사는 즉시 드레노어의 나그란드에 있는 바위경비병 브록스에게 보고하십시오.\n\n스랄이 호드 전초기지를 건설하는 중입니다. 전쟁노래 부족을 막기 위해서는 도움이 필요하다고 합니다.\n\n브론 스카이혼과 대화해 나그란드까지 비행한 후 보급 짐마차와 합류하십시오.\n\n호드를 위하여!', '', '', '', '', '', '드레노어의 나그란드에 있는 바위경비병 브록스와 대화해야 합니다.', 26365),
(49548, 'koKR', '대족장의 명령: 아라크 첨탑!', '아라크 첨탑에 있는 아지크와 대화해야 합니다.', '신체 건강한 호드 병사는 즉시 아라크 첨탑에 있는 아지크에게 보고하십시오.\n\n날개가 없는 아라코아가 아라크 첨탑을 버리고 도망치는 모습이 목격되고 있습니다. 소문에 따르면 강철 호드의 초강력 무기를 피해 달아나는 것이라고 합니다.\n\n아라코아를 본거지에서 떠나게 한 원인이 무엇인지, 그들의 날개가 메마른 이유가 무엇인지 알아내야 합니다.\n\n호드를 위하여!', '', '', '', '', '', '아라크 첨탑에 있는 아지크와 대화해야 합니다.', 26365),
(49547, 'koKR', '대족장의 명령: 탈라도르!', '탈라도르에 있는 현장감독 그로바쉬와 대화해야 합니다.', '신체 건강한 호드 병사는 즉시 탈라도르에 있는 현장감독 그로바쉬에게 보고하십시오.\n\n강철 호드가 탈라도르를 지배하려 합니다. 이곳은 아무런 저항 없이 드레노어 곳곳으로 빠르게 병력을 보내기 좋은 완벽한 요충지입니다.\n\n현장감독 그로바쉬가 지금 탈라도르에 전초기지를 건설하고 있습니다. 그곳으로 가서 강철 호드를 막으십시오.\n\n호드를 위하여!', '', '', '', '', '', '탈라도르에 있는 현장감독 그로바쉬와 대화해야 합니다.', 26365),
(49546, 'koKR', '대족장의 명령: 고르그론드!', '고르그론드에 있는 듀로탄과 대화해야 합니다.', '신체 건강한 호드 병사는 즉시 고르그론드에 있는 듀로탄에게 보고하십시오.\n\n듀로탄이 강철 호드를 무찌를 고대의 힘을 찾기 위해 고르그론드의 황량한 땅을 함께 조사해 줄 동맹군을 찾고 있습니다.\n\n듀로탄과 대화해 그 땅에 어떤 비밀이 있는지 알아보십시오.\n\n호드를 위하여!', '', '', '', '', '', '고르그론드에 있는 듀로탄과 대화해야 합니다.', 26365),
