INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'zhTW', '泰坦級的創新', '', '', '', '', '', '', '', '', 26365),
(50374, 'zhTW', '艾澤拉斯之血', '傳遞麥格尼‧銅鬚的訊息。', '聯盟和部落的領袖，真的知道他們在爭奪什麼嗎？\n\n從世界創口湧出的物質，其實是艾澤拉斯的生命之源！\n\n我們該做的是治療傷口，而不是爭奪力量！艾澤拉斯是我們唯一的家園，我們不能讓她就這樣死去！\n\n$n，我信任你，而且我知道你的意見對聯盟的人來說很有分量。你必須勸阻他們，停止這種瘋狂的行為！\n\n只有這樣，世界才會有希望…', '', '', '', '', '', '', 26365),
(50373, 'zhTW', '突來訪客', '和大法師卡德加交談。', '希利蘇斯的情況似乎越來越引人注意。\n\n大法師卡德加在你離開的時候來到這裡。他似乎是專程來這裡找你的，$n。\n\n看來這件事很緊急，建議你快點去找他談談。', '', '', '', '', '', '', 26365),
(50372, 'zhTW', '沙漠研究', '和譚美‧技旋交談。', '薩格拉斯的黑暗劍刃使這片大地籠罩著毀滅的陰影。\n\n我們原本以為異種蟲巢在攻擊之下都已遭到摧毀，但我收到的回報表示，異種蟲又出現了。\n\n我們需要找出異種蟲如何躲過這場浩劫，以及劍刃對蟲巢造成了何種影響。\n\n國王想要解剖存活的異種蟲進行研究。\n\n譚美‧技旋會負責這個研究，她需要一個「抓蟲人」。', '', '', '', '', '', '', 26365),
(50371, 'zhTW', '暴風城的召喚', '在暴風城和馬迪亞斯·肖爾見面。', '烏瑞恩國王要你盡快返回暴風城。有件急事需要你去辦。\n\n等你到了我會再詳細說明。\n\n我在雄獅陵園等你。請快一點…機會稍縱即逝！', '', '', '', '', '', '', 26365),
(50364, 'zhTW', '艾澤拉斯之血', '傳遞麥格尼‧銅鬚的訊息。', '聯盟和部落的領袖，真的知道他們在爭奪什麼嗎？\n\n從世界創口湧出的物質，其實是艾澤拉斯的生命之源！\n\n我們該做的是治療傷口，而不是爭奪力量！艾澤拉斯是我們唯一的家園，我們不能讓她就這樣死去！\n\n$n，我信任你，而且我知道你的意見對部落的人來說很有分量。你必須勸阻他們，停止這種瘋狂的行為！\n\n只有這樣，世界才會有希望…', '', '', '', '', '', '', 26365),
(50360, 'zhTW', '卡德加的請求', '和大法師卡德加交談。', '大法師卡德加在你不在的期間來到這裡。他似乎是專程來這裡找你的，$n。\n\n快去找他，問清楚他的目的。我希望他愈早離開愈好。\n\n我可不想讓個人類在部落這裡探頭探腦！', '', '', '', '', '', '', 26365),
(50358, 'zhTW', '沙漠研究', '和碧克西妮‧鏽彈交談。', '薩格拉斯的黑暗劍刃使這片大地染上了毀滅的陰影。\n\n我們原本以為異種蟲巢在攻擊之下都已遭到摧毀，但我收到回報，異種蟲又出現了。\n\n要是讓我做主，我一定會把那些該死的蟲巢全部燒個精光！但做主的不是我。\n\n黑暗女王命令我們解剖存活下來的異種蟲，進行研究。\n\n碧克西妮‧鏽彈會負責這個研究，她需要一個「樣本採集專家」。', '', '', '', '', '', '', 26365),
(50341, 'zhTW', '最新的發現', '與納薩諾斯‧凋零者交談。', '今天稍早時，部落的領導者聚在一起共享盛宴，慶祝我們打敗了燃燒軍團。\n\n加里維克斯在大家享用餐點時，要求和黑暗女王私下談話。\n\n他似乎在希利蘇斯發現神秘物質…一種前所未見的神奇晶礦。\n\n國王命我向你報告細節，好讓你為一項特別的任務做好準備。', '', '', '', '', '', '', 26365),
(50338, 'zhTW', '地龍特攻隊', '殺死黏性輻射塵。', '$n，你是怎麼進來的？\n\n我是地龍特攻隊的哮·狼，我們正好需要像你這樣的$r提供協助。\n\n幫我們擊敗佔據諾姆瑞根的輻射生物，我就讓你成為地龍特攻隊的榮譽特種兵！\n\n帶著這個降落傘，用它降落在齒輪大廳，殺死黏性輻射塵。完成之後，通過西邊的廊道去向特種兵怪怪頭回報。我相信你們兩個會處得很好的。', '', '', '', '', '', '', 26365),
(50337, 'zhTW', '地龍特攻隊', '摧毀電刑器6000型。', '$c，哮·狼是個瘋子。千萬，千萬不要和那個地精待在同一架飛行器上。\n\n我向來不喜歡偷偷摸摸的，想必你也是。走下這條通道，抵達發射台，然後摧毀電刑器6000型。\n\n那個電刑器負責控制飛行器…\n\n…而我痛恨飛行器。我絕對不搭飛行器，哪一種都一樣！\n\n完成之後，用這個降落傘降落，然後去找特種兵小白臉，取得下一個任務。', '', '', '', '', '', '', 26365),
(50336, 'zhTW', '地龍特攻隊', '殺死機電師瑟瑪普拉格', '要是我，我會和怪怪頭與哮‧狼保持距離，這兩個傢伙都很不穩定。\n\n先來幫你找點事情做吧！順著這條隧道往下走，找到技工議會。等你見到機電師瑟瑪普拉格，應該就會知道你該做什麼。\n\n我們的領導者，特種兵漢·泥巴，也在那裡的某處等著。他會根據你工作的成果給予優厚的獎賞。\n\n大概啦…', '', '小心他的炸彈！', '機電師瑟瑪普拉格', '', '', '', 26365),
(50335, 'zhTW', '報仇雪恨', '擊敗藍道夫·摩洛克、歐玻西特王和霍格。', '$r，我才不管你效忠誰、為誰而戰。我只知道霍格必須死！\n\n他，還有其他惡棍，在這片該死的土地上殺了我的手下。聯盟和部落或許意見不合，但是你一定明白，我絕對不能允許他們冷血殺死我的手下。', '', '', '', '', '', '', 26365),
(50319, 'zhTW', '陌生人在陌生地', '在奧格瑪找到大酋長的命令告示板。', '嗨。我是黑衛大使。\n\n<黑衛朝下看著手中的那張紙。>\n\n我僅代表大酋長，很榮幸歡迎你來到奧格瑪，$r。\n\n<他朝你瞥了幾眼，露出不自然的微笑，然後眼光再回到紙上。>\n\n你即將踏上一生中最精彩的冒險旅程，我向你保證…呃…\n\n搞什麼啊？我一整天都在用相同的台詞打招呼。我受夠了。\n\n你去找你的特使聊聊，問問看你該去哪裡冒險。快去吧。', '', '', '', '', '', '', 26365),
(50318, 'zhTW', '適時送達的北裂境邀請函', '在水晶之歌森林上方的達拉然與沃爾慕碰面。', '炙焰在星體的影子下逐漸息滅，卻點燃了先知心裡的恐懼。去達拉然找賢者沃爾慕，助她一臂之力。\n\n不，不是這個達拉然，是另一個達拉然。我知道很容易搞混。', '', '', '', '', '', '', 26365),
(50316, 'zhTW', '發現亂象：奧杜亞', '再次經歷奧杜亞遠征。', '你遲到了。你不記得上次有人動手腳、我們必須阻止尤格薩倫從奧杜亞地底監牢中脫逃的時候嗎？你那次也是晚到。你真該改一改遲到的壞習慣。\n\n等什麼，快去啊！上古諸神！守衛者！跟泰坦有關的東西！快去解決該解決的事，我們才能繼續下一步。還有很多時空要去呢。\n\n<沃爾慕不耐煩地揮手要你離開。>', '', '', '', '', '', '', 26365),
(50313, 'zhTW', '陌生人在陌生地', '在暴風城找到英雄召喚告示板。', '$r，歡迎來到暴風城。安杜因·烏瑞恩國王邀你盡情探索我們這座美麗的城市。話說，你看起來像是信仰虔誠的人。我們有座宏偉的教堂，你一定會感興趣的！\n\n啊，不過這件事還不急。你應該先去找光鑄德萊尼特使，他會告訴你出了這座城市，有哪些冒險在等著你。\n\n祝你有個愉快的一天，$n。喔，請不要介意環境有時候比較混亂。並不是每個市民都像你們聖光軍團一樣嚴守紀律！', '', '', '', '', '', '', 26365),
(50305, 'zhTW', '陌生人在陌生地', '在暴風城找到英雄召喚告示板。', '$r，歡迎來到暴風城。安杜因·烏瑞恩國王邀你盡情探索這座美麗的城市。話說，你看起來像是享受夜生活的人。這裡有不少受歡迎的酒館和熱鬧的音樂會！\n\n啊，不過這些都不急。你應該先去找刃多雷特使，他會告訴你出了這座城市，有哪些冒險在等著你。\n\n祝你有個愉快的一天，$n。喔，容我提醒你…最好控制好你體內的暗影之力。在暴風城裡，並不是每個人都跟我一樣思想開明。', '', '', '', '', '', '', 26365),
(50303, 'zhTW', '陌生人在陌生地', '在奧格瑪找到大酋長的命令告示板。', '嗨。我是黑衛大使。\n\n<黑衛低頭看著手中的紙張。>\n\n我僅代表大酋長，很榮幸歡迎你來到奧格瑪，$r。\n\n<他朝你瞥了幾眼，露出不自然的微笑，然後眼光再回到紙上。>\n\n你即將踏上一生中最精彩的冒險旅程…呃！\n\n我受夠了！我再也受不了一整天都照這個稿子一直念。\n\n你自己去找東西殺一殺，然後去找你的特使交談。\n\n不要再來煩我了。', '', '', '', '', '', '', 26365),
(50254, 'zhTW', '第二個抗敵盟友', '選擇下一個要尋求的盟友。', '結交盟友會為我們帶來助力。更多盟友，助力就會更大。\n\n聯盟一直計畫要擊垮我們。我們必須快速採取行動，以免來不及做好準備，反被他們打得措手不及。\n\n等你準備好啟程尋求下一個盟友的時候，跟我說一聲。這件事刻不容緩。', '', '', '', '', '', '', 26365),
(50248, 'zhTW', '第二個抗敵盟友', '選擇下一個要尋求的盟友。', '$n，在強化聯盟的這件任務上，你已經跨出了重要的第一步。做得好。\n\n但我擔心只有一個新盟友恐怕還不足以對抗部落。他們的大酋長一定會大舉募集兵力，直到數量遠勝暴風城守軍才會罷手。\n\n我必須請你再去尋求下一個盟友。這件事情刻不容緩，務必立刻進行。', '', '', '', '', '', '', 26365),
(50247, 'zhTW', '克服萬難，郵件必達', '協助郵務大師，在時限內分類至少15封郵件。', '最近發生了不少亂七八糟的事，導致分類信件的郵件元素大幅短缺。\n\n說真的，這份工作很簡單。只要把信扔進正確的傳送門，它們就會自動前往艾澤拉斯的各個角落，甚至是艾澤拉斯以外的地點。\n\n我喜歡把這件事變成遊戲。你能在一分鐘之內分類幾封郵件？', '', '', '', '', '', '', 26365),
(50242, 'zhTW', '選擇盟友', '與貝恩和莉雅德倫談談，了解他們對潛在盟友的看法。', '貝恩·血蹄和莉雅德倫女士特別來到這裡，為我們推薦可望結交的盟友，以提升部落的實力。他們非常明白能讓部落變強的要件有哪些。\n\n$n，你的實力我們都有目共睹。應該先尋求和哪一方結盟，我相信你一定能做出最好的判斷。\n\n先聽他們怎麼說，再做出決定。做好決定後跟我說，我們要馬上開始執行強化部落的計畫。', '', '', '', '', '', '', 26365),
(50239, 'zhTW', '選擇盟友', '與圖拉揚和艾蘭里亞談談，了解他們對潛在盟友的看法。', '大主教圖拉揚和艾蘭里亞‧風行者特別來到這裡，為我們推薦可望結交的盟友。他們都是奉獻生命保護世界的英雄，我認為他們的意見非常值得參考。\n\n你向來都是聯盟最可靠的勇士，$n。我信賴你一定能為我們做出最好的選擇。\n\n先聽他們怎麼說，再做出決定。讓我知道你認為誰才是我們第一步應該要尋求的盟友。', '', '', '', '', '', '', 26365),
(50232, 'zhTW', '暮光倖存者', '取回米希爾‧虛無行者的頭。', '在希利蘇斯這裡，曾經到處都看得到暮光之錘教徒。\n\n我們以為這個邪教已經遭到殲滅了，但是邪惡之徒總是能在暗處找到生存空間。\n\n雖然絕大多數的教徒都已經在薩格拉斯的攻擊下喪生，但還是有少數教徒活了下來。而且，他們的領導者米希爾‧虛無行者在希利蘇斯遭遇這場災難之後，反而變得更為大膽。\n\n不能放任這個瘋狂的傢伙召集倖存的教徒！把他的頭帶回來給我！', '', '', '', '', '', '', 26365),
(50231, 'zhTW', '幼蟲一籮筐', '收集$1oa個小異種蟲。', '異種蟲是怎麼在薩格拉斯劍下存活下來的？！\n\n我從來沒遇過像異種蟲這樣，這麼快就能順應環境演化的物種！\n\n一定有能夠馴服這些怪物的辦法！只要揭開牠們演化的奧秘，我們就可以從中獲得好處！\n\n我們必須從牠們還小的時候就開始馴化！已經變硬的老蟲我們用不到，我們要的是幼蟲！\n\n幫我抓一些異種蟲幼體回來，越多越好，我一定會找出馴化牠們的方法！', '', '', '', '', '', '', 26365),
(50230, 'zhTW', '力量之源', '收集$1oa個異種蟲腦。', '異種蟲變得焦躁狂暴，而且力量比之前更強了！\n\n我猜牠們從地底吸收了某種力量，很可能就在薩格拉斯劍刃的附近！\n\n我接到命令，要研究這股神秘力量對異種蟲造成的影響。\n\n幫我取回完整的異種蟲大腦，越多越好，我會立刻開始研究！', '', '', '', '', '', '', 26365),
(50229, 'zhTW', '一小塊布片', '收集$1oa塊暮光絲綢。', '外面的聲音是誰？$n，是你嗎？是我啊，安格斯‧烈酒！\n\n外面到處都是邪教徒，你最好小聲一點。把頭靠過來，說話的時候把鼻子也捂起來！\n\n整個希利蘇斯就只有這一間廁所！相信我，我花了好幾個小時找了一整晚，最後決定趁那些邪教徒還在睡，偷偷進來「留點東西」，可是我碰到一個問題…\n\n我找不到布來…呃…你知道我的意思…\n\n你看你能不能幫我找到一小塊布片，然後從門口偷偷遞過來給我！', '', '', '', '', '', '', 26365),
(50228, 'zhTW', '暮光倖存者', '取回米希爾‧虛無行者的頭。', '在希利蘇斯這裡，曾經到處都看得到暮光之錘教徒。\n\n我們以為這個邪教已經遭到殲滅了，但其實他們一直在暗影中存續。教徒的數量雖然變少了，可是他們的行徑還是跟全盛時期一樣瘋狂。\n\n雖然絕大多數的教徒都在薩格拉斯的攻擊下喪生，但還是有少數教徒活了下來。而且，他們的領導者米希爾‧虛無行者在希利蘇斯遭遇這場災難之後，行動反而變得更為大膽。\n\n不能放任米希爾召集倖存的教徒。一定要阻止他！', '', '', '', '', '', '', 26365),
(50227, 'zhTW', '幼蟲一籮筐', '收集$1oa個小異種蟲。', '異種蟲是怎麼在薩格拉斯劍下存活下來的？！我不懂！\n\n我從來沒遇過像異種蟲這樣，這麼迅速就能順應環境演化的物種。\n\n異種蟲幼體，或是一般俗稱的小異種蟲，能夠根據蟲巢的需求演化成不同類型的異種蟲生物。\n\n控制幼體演化發展的是蟲群的意志嗎？還是某種環境因素造成的？\n\n科學一定會找出答案的！', '', '', '', '', '', '', 26365),
(50226, 'zhTW', '力量之源', '收集$1oa個異種蟲腦。', '雖然異種蟲在希利蘇斯遭到破壞時短暫消失了一陣子，但最近牠們又從地底冒出來了。\n\n新出沒的異種蟲似乎比之前更強！\n\n我的假設是，異種蟲攝取了出現在薩格拉斯劍下的某種強大物質。\n\n我的任務，是研究這股神秘力量會對異種蟲巢造成什麼影響。\n\n幫我取回完整的異種蟲大腦，越多越好，我會立刻開始研究！', '', '', '', '', '', '', 26365),
(50131, 'zhTW', '外力相助', '斯博格爾聲望達到中立。', '謝謝你為我們所做的一切，不過他們還是很害怕外來者。\n\n到重生峽谷去對抗泥沼領主，救回孢子囊。我們的未來就靠你了！', '', '', '', '', '', '', 26365),
(50130, 'zhTW', '友好的表示', '斯博格爾聲望達到友好。', '$n，在沼澤生活不容易。我們想和住在這裡的生物和平共存，但是敵人威脅了我們的生存空間。\n\n協助我們吧，讓斯博格爾一族在這裡茁壯。', '', '', '', '', '', '', 26365),
(50071, 'zhTW', '為了聖光！', '回暴風城和安杜因·烏瑞恩國王交談。', '光鑄德萊尼很高興能加入聯盟。\n\n我們現在有共同的目標了，$n。\n\n我們回暴風城去，法芮雅隊長要跟我們的同胞談談。', '', '', '', '', '', '', 26365),
(50055, 'zhTW', '代言者的看法', '把卡德加的信帶去希利蘇斯交給麥格尼‧銅鬚。', '薩格拉斯給這個世界留下一道傷痕，$n。一道難以癒合的傷痕…\n\n而且，看來在聯盟與部落之間，也有一道難以癒合的傷痕。\n\n為了拯救艾澤拉斯免於遭到毀滅，已經有太多的生命犧牲。我不能袖手旁觀，坐視更多的人喪生。\n\n在我離開之前，有件事要請你幫忙。請把這封信帶去給麥格尼‧銅鬚，他在希利蘇斯尋找治療世界創口的方法。\n\n這封信的內容可能會對他有用。', '', '', '', '', '', '', 26365),
(50054, 'zhTW', '騰湧海岸', '完成騰湧海岸戰場。', '我們收到情報，希利蘇斯的岸邊有座島嶼蘊含豐富的艾澤萊礦藏。\n\n我們必須在聯盟發現之前，搶先一步佔領那座海島。\n\n納薩諾斯‧凋零者會親自帶領遠征部隊，他要你過去向他報到。祝你好運！', '', '', '', '', '', '', 26365),
(50053, 'zhTW', '怠惰的勘查員！', '激勵$1oa名怠惰的勘查員。', '我在這裡的時間很久，知道獸人的事絕對不能交給哥布林。\n\n加里維克斯和污水企業雖然效忠大酋長，但還是得緊緊盯住他們，才能確保他們有把事情做好！\n\n我親眼看過許多工人無所事事，甚至在工作的時間睡懶覺。部落絕不能容忍偷懶的行徑。\n\n到外面去教訓那些怠惰的勘查員，要他們好好地認真工作！', '', '', '', '', '', '', 26365),
(50052, 'zhTW', '間諜殺無赦', '在希利蘇斯殺死$1oa名軍情七處探員。', '嗨，$n，真高興你來了。\n\n我快忙不過來了！出現致命的蟲子，奇怪的物質…地上還插著那把巨劍…我對這個地方有非常不好的預感！\n\n更糟的是，我不斷收到回報，說聯盟的間諜在這個營地的周圍打探情報！\n\n仔細聽好，黑暗女王對這神秘的晶礦有許多計畫，我可不想讓女王失望，你明白我的意思嗎？\n\n快到採礦營地去解決那些軍情七處的間諜，別讓他們破壞採礦作業！', '', '', '', '', '', '', 26365),
(50049, 'zhTW', '代言者的看法', '把卡德加的信帶去希利蘇斯交給麥格尼‧銅鬚。', '薩格拉斯給這個世界留下一道傷痕，$n。一道難以癒合的傷痕…\n\n而且，看來在聯盟與部落之間，也有一道難以癒合的傷痕。\n\n為了拯救艾澤拉斯免於遭到毀滅，已經有太多的生命犧牲。我不能袖手旁觀，坐視更多的人喪生。\n\n在我離開之前，有件事要請你幫忙。請把這封信帶去給麥格尼‧銅鬚，他在希利蘇斯尋找治療世界創口的方法。\n\n這封信的內容可能會對他有用。', '', '', '', '', '', '', 26365),
(50048, 'zhTW', '騰湧海岸', '完成騰湧海岸戰場。', '我們收到情報，希利蘇斯的岸邊有座島嶼蘊含豐富的艾澤萊礦藏。\n\n我們必須在部落發現之前，搶先一步佔領那座海島。\n\n馬迪亞斯‧肖爾會親自帶領遠征部隊，他要你過去向他報到。祝你好運！', '', '', '', '', '', '', 26365),
(50047, 'zhTW', '免費樣本', '收集$1oa個神秘的礦石樣本。', '國王命令軍情七處潛入哥布林的採礦作業場地，盡可能收集這種神秘新物質的相關情報。\n\n我的探員已經成功潛入了，但是時間緊迫，我們需要快點拿到礦石樣本。\n\n也就是說，我需要有人能採取更直接的行動。要有辦法應付強敵，而且還要活著回來。\n\n如果你成功回來，就把礦石樣本帶去給卡古斯‧石衣，讓他進行分析。', '', '', '', '', '', '', 26365),
(50046, 'zhTW', '大搞破壞', '設置$1oa捆炸藥。', '部落迫不及待地開始在世界創口採掘這種神秘礦石，實在厚顏無恥。\n\n幸好，跟我們在諾姆瑞根創造的科技相比，哥布林工程系統實在簡單多了！要徹底破壞哥布林的伐木機，只要用上最簡單的炸藥就行了。\n\n這種機器實在是太原始了！\n\n帶上這幾捆炸藥，然後到採礦營地去把它們設置在未啟動的伐木機上。\n\n那群貪婪的哥布林得花上好幾個星期才能修好這些機器！', '', '', '', '', '', '', 26365),
(50035, 'zhTW', 'Bret Test - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'zhTW', '目睹世界創口', '在希利蘇斯與戈洛爾‧戰刃交談。', '污水企業已經開始在希利蘇斯進行採礦作業，開採這種神秘物質。\n\n大酋長堅持要你親自監督在希利蘇斯的採礦作業，並且阻止聯盟前來干擾。\n\n加里維克斯似乎對你的這項指派感到非常不滿，但是沒有人能違逆希瓦娜斯。\n\n你必須立刻前往希利蘇斯。基地指揮官已經在那裡等你了。', '', '', '', '', '', '', 26365),
(49981, 'zhTW', '目睹世界創口', '在希利蘇斯與軍情七處指揮官碰面。', '我派特務先去希利蘇斯建立了基地。\n\n他們受命監視部落的行動，並且蒐集跟這個神秘物質有關的一切資訊。\n\n我的軍情七處指揮官已經在那裡等你了。快出發吧。', '', '', '', '', '', '', 26365),
(49977, 'zhTW', '奧格瑪的召喚', '與納薩諾斯‧凋零者交談。', '大酋長要你盡快返回奧格瑪。有件急事要你去做。\n\n等你到了我會再詳細說明。\n\n我在葛羅瑪許堡的入口附近等你。黑暗女王有任務要交給你，$n。別讓她等太久…', '', '', '', '', '', '', 26365),
(49976, 'zhTW', '墮落泰坦的贈禮', '與馬迪亞斯·肖爾交談。', '今天稍早時，烏瑞恩國王在雄獅陵園對暴風城的民眾發表演說，正式宣布戰爭已經結束。\n\n人群散去以後，我私下與國王和他的參謀會面，討論我們在希利蘇斯發現的某種神秘物質。\n\n這種發光的晶礦似乎蘊含著極大的力量。部落已經取得先機，在世界創口動手開採了。\n\n國王命我向你報告細節，你必須準備執行重要的任務。', '', '', '', '', '', '', 26365),
(49973, 'zhTW', '薩莉瑟拉的住所', '到蘇拉瑪爾前往首席秘法師的莊園與莉雅德倫女士見面。', '攝政王洛索瑪·塞隆邀請夜裔精靈到銀月城去見他。\n\n首先，我們必須向他們傳達攝政王的邀請，而且這件事我希望親口告知。\n\n請你跟我一起去蘇拉瑪爾見薩莉瑟拉女士。', '', '', '', '', '', '', 26365),
(49933, 'zhTW', '為了部落', '在駐奧格瑪大使館與黑衛大使見面。', '經過了漫長的隔絕與孤立，我們終於準備好要再度崛起。與部落結盟讓我們得以重新開始，我們一定要好好把握這個絕無僅有的機會。\n\n$n，大酋長的大使在奧格瑪等著你。你在艾澤拉斯的冒險要從那裡展開。奮戰吧，保護這片土地，並且為夏多雷爭取一席之地。\n\n讓世人見識我們的能耐，$c。為部落帶來榮耀，同時，這也會為蘇拉瑪爾帶來榮耀。', '', '', '', '', '', '在駐奧格瑪大使館與黑衛大使見面。', 26365),
(49930, 'zhTW', '召集盟友', '在駐奧格瑪大使館和大酋長希瓦娜斯·風行者見面。', '部落當初無私地對抗燃燒軍團，還因此元氣大傷。敵人現在卻想趁機奪走屬於我們的東西。絕對不能讓他們稱心如意。\n\n我已經下令在奧格瑪建造大使館，我們要在那裡招募盟友。立刻去那裡跟我會合。', '', '', '', '', '', '', 26365),
(49929, 'zhTW', '召集盟友', '到駐暴風城大使館去見烏瑞恩國王。', '部落會不擇手段搶奪艾澤拉斯的生命之源。我擔心擊敗燃燒軍團之後，我們的兵力不足以阻止他們。\n\n聯盟需要結交新的盟友。因此，我們還在暴風城建立了大使館。到那裡去跟我會合吧。', '', '', '', '', '', '', 26365),
(49928, 'zhTW', '虛無精靈的傳承', '在特洛古斯裂隙與艾蘭里亞‧風行者碰面。', '你在艾澤拉斯的這段旅程中，完全學會駕馭一直想控制你的這股力量。\n\n你已經成為聯盟不可或缺的一大助力，$p。回到虛無裂隙吧，我們想表揚你的成就。', '', '', '', '', '', '', 26365),
(49866, 'zhTW', '前往潘達利亞！', '到暴風城向瑞爾‧夜風報到。', '請立刻趕到暴風要塞，聆聽瓦里安‧烏瑞恩國王的任務簡報。$b$b我們收到消息，南方的海上發生了衝突，需要經驗豐富的$c去幫我們查明狀況。', '', '', '', '', '', '', 26365),
(49865, 'zhTW', '大災變緊急應變人員', '到暴風城向語地者納拉亞特報到。', '死亡之翼，是帶來死亡的巨龍，他派出爪牙到處殺害艾澤拉斯的生靈。$b$b如果想進一步了解遭死亡之翼鎖定的海加爾山、瓦許伊爾及其他地區情況如何，語地者納拉亞特能為你提供情報。', '', '', '', '', '', '', 26365),
(49864, 'zhTW', '兩道戰線', '使用特殊任務，選擇你下一個要冒險的地點。', '$n！我們要再一次請你協助聯盟處理重要的事務。\n\n你能幫助聯盟對抗死亡之翼和暮光之錘嗎？\n\n還是你願意往南飛去潘達利亞，取得資源，別讓部落捷足先登？\n\n決定好了就前往戰場吧！', '', '', '', '', '', '', 26365),
(49863, 'zhTW', '前往北裂境！', '快登船前往北裂境。$b$b你可以從暴風港搭船前往北風凍原，或是從濕地搭船前往凜風峽灣。', '巫妖王在北方開始蠢動。$b$b不死大軍聽從他的指揮，橫越北裂境，在所到之處一路散播疫病。$b$b$n，我們需要你去阻止它們。', '', '', '', '', '', '', 26365),
(49862, 'zhTW', '前往外域！', '到詛咒之地通過黑暗之門。', '燃燒軍團這支無窮無盡的惡魔大軍，大舉進攻外域這個滿目瘡痍的德萊尼家園。我們需要聯盟的冒險者去保護殘存的外域，別讓惡魔完全毀滅這個地方。$b$b先到詛咒之地，然後再通過黑暗之門。', '', '', '', '', '', '', 26365),
(49860, 'zhTW', '冒險的呼喚', '使用特殊任務，選擇你下一個要冒險的地點。', '你的英勇事蹟傳遍了整個艾澤拉斯…甚至傳進了別的世界。有其他地方需要借助你的力量。\n\n你是否願意進入黑暗之門並前往殘破的外域對抗燃燒軍團呢？\n\n還是要飛往北裂境，參戰討伐巫妖王？\n\n你自己決定吧，冒險者。', '', '', '', '', '', '', 26365),
(49855, 'zhTW', '海加爾山的災難', '與塞納里奧使者黑蹄交談，以傳送進入月光林地。', '我們收到塞納里奧議會的兄弟回報，死亡之翼的爪牙正發動攻擊，企圖破壞世界之樹，威脅將它整個燒掉！$B$B去找塞納里奧使者黑蹄交談，他會讓你進入月光林地。黑蹄就在奧格瑪智慧谷的牛頭人區。', '傳送進入月光林地', '', '', '', '', '到月光林地的永夜港與使者風歌交談。', 26365),
(49852, 'zhTW', '前往潘達利亞！', '到奧格瑪的葛羅瑪許堡報到。', '你受命立刻趕往葛羅瑪許堡報到。$b$b我們接獲消息，在南邊發現一處蘊含豐富資源的大陸，我們要派你和一隊士兵到那裡去為部落奪回資源。', '', '', '', '', '', '', 26365),
(49851, 'zhTW', '大災變緊急應變人員', '到奧格瑪向先知克洛加爾報到。', '死亡之翼，是帶來死亡的巨龍，他派出爪牙到處殺害艾澤拉斯的生靈。$b$b如果想進一步了解遭死亡之翼鎖定的海加爾山、瓦許伊爾及其他地區情況如何，奧格瑪的先知克洛加爾能為你提供情報。', '', '', '', '', '', '', 26365),
(49846, 'zhTW', '兩道戰線', '使用特殊任務，選擇你下一個要冒險的地點。', '$n！我們要再一次請你協助部落處理重要的事務。\n\n你能幫助部落對抗死亡之翼和暮光之錘嗎？\n\n還是你願意往南飛去潘達利亞，取得資源，別讓聯盟捷足先登？\n\n決定好了就前往戰場吧！', '', '', '', '', '', '', 26365),
(49817, 'zhTW', '前往北裂境！', '搭乘飛艇開往北裂境。$b$b你可以在提里斯法林地登艇飛向凜風峽灣，或是在奧格瑪登艇前往北風凍原。', '巫妖王在北方開始蠢動。$b$b不死大軍聽從他的指揮，橫越北裂境，在所到之處一路散播疫病。$b$b$n，我們需要你去阻止它們。', '', '', '', '', '', '', 26365),
(49816, 'zhTW', '前往外域！', '到詛咒之地通過黑暗之門。', '燃燒軍團這支無窮無盡的惡魔大軍，大舉進攻外域這個滿目瘡痍的獸人家園。我們需要部落的冒險者去保護殘存的外域，別讓惡魔完全毀滅這個地方。$b$b先到詛咒之地，然後再通過黑暗之門。', '', '', '', '', '', '', 26365),
(49813, 'zhTW', '冒險的呼喚', '使用特殊任務，選擇你下一個要冒險的地點。', '你的英勇事蹟傳遍了整個艾澤拉斯…甚至傳進了別的世界。有其他地方需要借助你的力量。\n\n你是否願意進入黑暗之門並前往殘破的外域對抗燃燒軍團呢？\n\n還是要飛往北裂境，參戰討伐巫妖王？\n\n你自己決定吧，冒險者。', '', '', '', '', '', '', 26365),
(49812, 'zhTW', '達拉然的德菈絲', '去達拉然的安東尼達斯紀念碑找德菈絲‧月牙。', '德菈絲‧月牙需要我們幫忙。她會協助我們解讀命令寶典作為回報。$B$B請你盡快去找她。她經常去祈倫托過去偉大的領導者安東尼達斯的紀念碑附近。', '', '', '', '', '', '去達拉然的安東尼達斯紀念碑找德菈絲‧月牙。', 26365),
(49788, 'zhTW', '為了聯盟', '在駐暴風城大使館與穆爾加德大使見面。', '專心和自制才能克服低語。你所付出的努力，啟迪了所有刃多雷。那是我們的新名字，現在我們是虛無之子。\n\n你在這裡學到了不少，但是離開特洛古斯裂隙的時候到了。\n\n到暴風城去吧，前往聯盟的核心。國王的大使會在那裡迎接你，為你即將踏上的旅程指引方向。\n\n一定要保護艾澤拉斯。$p，除了你，我想不到更適合擔負這項使命的人了。', '', '', '', '', '', '在駐暴風城大使館與穆爾加德大使見面。', 26365),
(49787, 'zhTW', '鬼魂之地', '在鬼魂之地找到奧姆里克的住所。', '有個血精靈名叫奧姆里克，他和一群追隨者因為研究虛無魔法而被逐出銀月城。\n\n雖然他們的研究觸犯禁忌，但是這種處置方式還是太極端了。\n\n不過這倒是給了聯盟一個機會。要是我們能找到這些流亡者，或許可以說服他們加入聯盟。更重要的是，說不定能協助他們理解虛無，使他們免於遭到虛無腐化的命運。', '', '', '', '', '', '', 26365),
(49784, 'zhTW', '夜裔精靈的傳承', '在暗夜堡與首席秘法師薩莉瑟拉會面。', '你是第一批勇敢離開蘇拉瑪爾、探索艾澤拉斯世界各地荒野的夏多雷。但現在，該是你返回家鄉的時候了。\n\n回到蘇拉瑪爾，讓我們為你致上謝意，感謝你幫助我們同胞在這個世界取得一席之地。', '', '', '', '', '', '', 26365),
(49783, 'zhTW', '高嶺牛頭人的傳承', '在雷霆圖騰與梅拉‧高嶺見面。', '$p，你在艾澤拉斯經歷了漫長的旅程。在這段日子裡，你為高嶺帶來了榮耀。\n\n現在雷霆圖騰要召喚你回到家鄉，我們要感謝你為高嶺所做的一切。', '', '', '', '', '', '', 26365),
(49782, 'zhTW', '光鑄德萊尼的傳承', '在梵迪卡爾上與法芮雅隊長碰面。', '你對聯盟的貢獻可說是有目共睹，$p。我自己都未必能做得這麼好。好吧，應該可以…但應該無法再超越了。\n\n來吧，到梵迪卡爾上跟我碰面。我們該好好獎勵你一直以來的努力。', '', '', '', '', '', '', 26365),
(49773, 'zhTW', '為了部落', '在駐奧格瑪大使館與黑衛大使見面。', '高嶺的新世紀來臨了，$n。\n\n經過了幾百年的分裂，我們終於能夠重建和卡林多親族之間的情誼。在我們奮力拯救這片大地的過程裡，也在部落陣營中找到全新的友誼。\n\n為了向盟友展現我們的力量，像你這樣的英勇牛頭人必須回應冒險的呼喚。\n\n你已獲邀前往奧格瑪。到大使館與大酋長的大使會面吧。然後你就能開始在艾澤拉斯的旅程了。\n\n願大地之母與你同在，$n。', '', '', '', '', '', '在駐奧格瑪大使館與黑衛大使見面。', 26365),
(49772, 'zhTW', '為了聯盟', '在駐暴風城大使館與穆爾加德大使見面。', '當初成立聖光軍團只有一個目的，那就是擊敗燃燒軍團。我們已經完成這個目標，但眼前又出現了新的使命。\n\n艾澤拉斯處處都是敵人。用你所受過的訓練對抗黑暗，維護聯盟所秉持的理念。\n\n到暴風城去吧。到大使館找安杜因國王的大使，讓聖光的正義照耀整個艾澤拉斯。\n\n聖光指引我們的道路，$n。', '', '', '', '', '', '在駐暴風城大使館與穆爾加德大使見面。', 26365),
(49756, 'zhTW', '黑暗之力', '擊敗基特列克，烏蓋涅斯的使者', '上古諸神對雷霆崖派出了一名無面者。這個魔物褻瀆了我們族人的土地！\n\n我們必須同心協力，才能把這個怪物趕出這裡。\n\n我認為只要消滅這名使者，就能把敵人趕出莫高雷。願我們的武器招招致命，$n！', '', '烏蓋涅斯的使者', '基特列克', '', '', '', 26365),
(49698, 'zhTW', '光鑄儀式', '和圖拉揚一起前往梵迪卡爾。', '$n，你親眼見過我同胞的能耐。我們經過千年的鍛鍊，專門為了對抗燃燒軍團而不斷精進強化。\n\n現在，我相信我們拿起武器跟聯盟的兄弟姊妹一起並肩奮戰的時刻到了。\n\n到梵迪卡爾加入我們吧。我們還有很多事要跟法芮雅隊長討論。', '', '', '', '', '', '到梵迪卡爾與法芮雅隊長碰面', 26365),
(49614, 'zhTW', '夜裔精靈', '回奧格瑪找大酋長。', '大酋長一定會對我們的成果感到滿意。\n\n薩莉瑟拉和夜裔精靈絕對會成為得力的盟友，我敢肯定。', '', '', '', '', '', '', 26365),
(49613, 'zhTW', '銀月城', '在銀月城和莉雅德倫見面。', '數千年來，夜裔一直與世隔絕。他們現在重新振作，我們必須引導他們走上正確的道路。\n\n攝政王塞隆也向夜裔發出了邀請，要在銀月城跟我們見面。\n\n來吧，$n，我們跟薩莉瑟拉一起前往銀月城。', '', '', '', '', '', '', 26365),
(49567, 'zhTW', '英雄的召喚：納葛蘭！', '到德拉諾的納葛蘭與巴爾佛中尉交談。', '所有身強體壯的聯盟公民都必須前往德拉諾的納葛蘭向巴爾佛中尉報到。\n\n伊芮爾建議聯盟在納葛蘭建立對抗戰歌氏族的前哨站。\n\n飛往納葛蘭和巴爾佛中尉與補給商隊會合。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到德拉諾的納葛蘭與巴爾佛中尉交談。', 26365),
(49566, 'zhTW', '英雄的召喚：阿拉卡山！', '到阿拉卡山與阿茲卡交談。', '所有身強體壯的聯盟公民都必須前往阿拉卡山向阿茲卡報到。\n\n有人看見沒了翅膀的阿拉卡從阿拉卡山落荒而逃。據說威脅他們的是鋼鐵部落的超級武器。\n\n我們需要找出他們逃離家園的原因，還有他們為什麼會沒了翅膀。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到阿拉卡山與阿茲卡交談。', 26365),
(49565, 'zhTW', '英雄的召喚：塔拉多爾！', '到塔拉多爾和工頭艾克索斯交談。', '所有身強體壯的聯盟公民都必須前往塔拉多爾向工頭艾克索斯報到。\n\n鋼鐵部落想要控制塔拉多爾。那裡是重要的戰略位置，可迅速將部隊派往德拉諾各地。\n\n工頭艾克索斯目前正在塔拉多爾建立哨站。快去那裡阻止鋼鐵部落。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到塔拉多爾和工頭艾克索斯交談。', 26365),
(49564, 'zhTW', '英雄的召喚：格古隆德！', '到格古隆德與伊芮爾交談。', '所有身強體壯的聯盟公民都必須前往格古隆德向伊芮爾報到。\n\n伊芮爾和瑪銳德請求派人支援，組織一支小型隊伍前往格古隆德的荒野偵查。\n\n我們希望能更加瞭解那塊區域，發掘可用來對付鋼鐵部落的弱點。\n\n去協助他們的偵察任務。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到格古隆德與伊芮爾交談。', 26365),
(49562, 'zhTW', '英雄的召喚：影月谷！', '到德拉諾的影月谷與預言者費倫交談。', '所有身強體壯的聯盟公民都必須前往德拉諾的影月谷向預言者費倫報到。\n\n黑暗之門的崩毀阻止了鋼鐵部落入侵艾澤拉斯的計畫，但是現在我們必須化被動為主動。\n\n有了預言者費倫的協助，我們將可在德拉諾建立一座戰略據點，對抗艾澤拉斯的敵人。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到德拉諾的影月谷與預言者費倫交談。', 26365),
(49561, 'zhTW', '英雄的召喚：悚然荒野！', '到蟠龍脊與女弓手李交談。', '所有身強體壯的聯盟公民都必須前往恆春谷的蟠龍脊向女弓手李報到。\n\n影潘正在努力阻止螳螂人大軍侵略整個潘達利亞。\n\n你可以搭乘風箏飛往蟠龍脊找女弓手李。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到蟠龍脊與女弓手李交談。', 26365),
(49560, 'zhTW', '英雄的召喚：螳螂荒原！', '到螳螂荒原與塔蘭‧祝交談。', '所有身強體壯的聯盟公民都必須前往螳螂荒原向塔蘭‧祝報到。\n\n影潘希望能奪回被揚古佔據的土地，但是缺乏足夠的人手與戰力。\n\n去協助他們對抗揚古。\n\n為了聯盟的榮耀！', '', '', '', '', '', '在螳螂荒原與塔蘭‧祝交談。', 26365),
(49559, 'zhTW', '英雄的召喚：崑萊峰！', '到四風峽與咕嚕摩傳令員交談。', '所有身強體壯的聯盟公民都必須前往崑萊峰協助博學行者阿洲。\n\n去四風峽找咕嚕摩傳令員，他們會協助你前往崑萊峰。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到四風峽與咕嚕摩傳令員交談。', 26365),
(49558, 'zhTW', '英雄的召喚：喀撒朗蠻荒！', '到喀撒朗蠻荒與肯‧肯交談。', '所有身強體壯的聯盟公民都必須前往喀撒朗蠻荒的祝家守望站報到。\n\n有傳聞指出，喀撒朗蠻荒的熊貓人出現了異常的行為。\n\n去和祝家守望站的肯‧肯談一談。也許他會比較清楚該地熊貓人為何變得無精打采的原因。', '', '', '', '', '', '到喀撒朗蠻荒與肯‧肯交談。', 26365),
(49557, 'zhTW', '英雄的召喚：四風峽！', '到四風峽與老陳·風暴烈酒交談。', '所有身強體壯的聯盟公民都必須前往潘達利亞，到四風峽向老陳‧風暴烈酒報到。\n\n聯盟需要向風暴烈酒族人的冒險精神看齊。\n\n與風暴烈酒族人同行，找出隱藏在潘達利亞的秘密。\n\n為了聯盟的榮耀！', '', '', '', '', '', '到四風峽與老陳‧風暴烈酒交談。', 26365),
(49556, 'zhTW', '英雄的召喚：翠玉林！', '到暴風要塞向傑塔瑞斯上將報到。', '所有身強體壯的聯盟公民，都必須前往暴風城裡的暴風要塞報到。\n\n傑塔瑞斯上將正在編組一支隊伍，準備出發執行極度重要的任務。\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '', 26365),
(49555, 'zhTW', '英雄的召喚：寒冰皇冠！', '到水晶之歌森林上方的達拉然與軍官凡‧羅森交談。', '所有身強體壯的聯盟公民都必須前往水晶之歌森林上方的達拉然向軍官凡‧羅森報到。\n\n驍勇遠征軍需要更多冒險者的協助，以強化對冰冠城塞和阿薩斯的攻勢。\n\n時候到了，該讓巫妖王付出代價。\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與軍官凡‧羅森交談。', 26365),
(49554, 'zhTW', '英雄的召喚：風暴群山！', '對水晶之歌森林上方的達拉然與倫‧杜克坦交談。', '所有身強體壯的聯盟公民都必須前往水晶之歌森林上方的達拉然向倫‧杜克坦報到。\n\nK3的哥布林在風暴群山建造了一座城市，並且派出使者徵召冒險者。\n\n去和倫‧杜克坦詢問K3的人民需要什麼。他們或許能夠成為一同對抗巫妖王的盟友。\n\n為了聯盟的光輝榮耀！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與倫‧杜克坦交談。', 26365),
(49553, 'zhTW', '英雄的召喚：休拉薩盆地！', '到水晶之歌森林上方的達拉然與大法師潘塔魯斯交談。', '所有身強體壯的聯盟公民都必須前往水晶之歌森林上方的達拉然向大法師潘塔魯斯報到。\n\n赫米特‧奈辛瓦里，還有他的遠征隊，先前已被派去探索休拉薩盆地，但是他們卻失蹤了。\n\n我們對林木蓊鬱的休拉薩了解不多，但是這座盆地藏有的秘密，或許能讓我們在對抗巫妖王時占到優勢。\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與大法師潘塔魯斯交談。', 26365),
(49552, 'zhTW', '英雄的召喚：祖爾德拉克！', '到祖爾德拉克與十字軍領主蘭迪加交談。', '所有身強體壯的聯盟公民都必須前往北裂境的祖爾德拉克，向十字軍領主蘭迪加報到。\n\n天譴軍正在祖爾德拉克殘殺當地的居民，並且把他們轉化成天譴軍麾下的不死怪物。\n\n協助銀白十字軍和黯刃騎士團平息這場威脅。\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '到祖爾德拉克與十字軍領主蘭迪加交談。', 26365),
(49551, 'zhTW', '英雄的召喚：凜風峽灣！', '到凜風峽灣與麥凱諾交談。', '所有身強體壯的聯盟公民都必須前往凜風峽灣向麥凱諾報到。\n\n瓦爾加德的港口就在凜風峽灣的中央，那裡是聯盟攻擊天譴軍團的重要戰略位置。\n\n協助派駐在那裡的士兵。\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '到凜風峽灣與麥凱諾交談。', 26365),
(49550, 'zhTW', '英雄的召喚：影月谷！', '到外域的影月谷與空軍指揮官奴艾恩交談。', '所有身強體壯的聯盟公民都必須前往外域的影月谷向空軍指揮官奴艾恩報到。\n\n燃燒軍團包圍了蠻錘要塞，我們的人民陷入危險，他們的生命遭到威脅。\n\n我們必須派人支援要塞，阻止燃燒軍團殘忍無情的進攻！\n\n為了聯盟的光輝與榮耀！', '', '', '', '', '', '到外域的影月谷與空軍指揮官奴艾恩交談。', 26365),
(49549, 'zhTW', '大酋長的命令：納葛蘭！', '到德拉諾的納葛蘭與石衛士布洛克斯交談。', '所有身強體壯的部落成員都必須前往德拉諾的納葛蘭，向石衛士布洛克斯報到。\n\n索爾在建立部落哨站，而且他需要有人幫忙阻止戰歌氏族。\n\n去找布隆‧天角，然後飛往納葛蘭和補給商隊會合。\n\n為了部落！', '', '', '', '', '', '到德拉諾的納葛蘭與石衛士布洛克斯交談。', 26365),
(49548, 'zhTW', '大酋長的命令：阿拉卡山！', '到阿拉卡山與阿茲卡交談。', '所有身強體壯的部落成員都必須前往阿拉卡山，向阿茲卡報到。\n\n有人看到沒有翅膀的阿拉卡逃離阿拉卡山。傳言說他們是為了逃離鋼鐵部落的超級武器。\n\n我們要想辦法查明他們為什麼逃離家園，他們的翅膀又為什麼萎縮損毀。\n\n為了部落！', '', '', '', '', '', '到阿拉卡山與阿茲卡交談。', 26365),
(49547, 'zhTW', '大酋長的命令：塔拉多爾！', '到塔拉多爾與工頭葛羅巴什交談。', '所有身強體壯的部落成員都必須前往塔拉多爾，向工頭葛羅巴什報到。\n\n鋼鐵部落想要控制塔拉多爾。那裡是重要的戰略位置，可迅速將部隊派往德拉諾各地。\n\n工頭葛羅巴什正在塔拉多爾建立哨站。 到那裡去，想辦法阻止鋼鐵部落。\n\n為了部落！', '', '', '', '', '', '到塔拉多爾與工頭葛羅巴什交談。', 26365),
(49546, 'zhTW', '大酋長的命令：格古隆德！', '到格古隆德與杜洛坦交談。', '所有身強體壯的部落成員都必須前往格古隆德，向杜洛坦報到。\n\n杜洛坦正在召集盟友協助我們探查蠻荒原始的格古隆德，尋找能讓我們粉碎鋼鐵部落的遠古力量。\n\n去找他談談，看看那裏藏有什麼秘密。\n\n為了部落！', '', '', '', '', '', '到格古隆德與杜洛坦交談。', 26365),
(49543, 'zhTW', '大酋長的命令：悚然荒野！', '到蟠龍脊與女弓手李交談。', '所有身強體壯的部落成員都必須前往恆春谷的蟠龍脊向女弓手李報到。\n\n影潘正在努力阻止螳螂人大軍侵略整個潘達利亞。\n\n你可以搭乘風箏飛往蟠龍脊找女弓手李。\n\n命運在召喚你！', '', '', '', '', '', '到蟠龍脊與女弓手李交談。', 26365),
(49542, 'zhTW', '大酋長的命令：螳螂荒原！', '到螳螂荒原與塔蘭‧祝交談。', '所有身強體壯的部落成員都必須前往螳螂荒原向塔蘭·祝回報。\n\n影潘希望能奪回被揚古佔據的土地，但是缺乏足夠的人手與戰力，難以如願。\n\n協助他們對揚古展開攻擊。\n\n為了部落！', '', '', '', '', '', '到螳螂荒原與塔蘭‧祝交談。', 26365),
(49541, 'zhTW', '大酋長的命令：崑萊峰！', '到四風峽與咕嚕摩傳令員交談。', '所有身強體壯的部落成員都必須前往崑萊峰，協助博學行者阿洲。\n\n去四風峽找咕嚕摩傳令員，他們會協助你前往崑萊峰。\n\n為了部落！', '', '', '', '', '', '到四風峽與咕嚕摩傳令員交談。', 26365),
(49540, 'zhTW', '大酋長的命令：喀撒朗蠻荒！', '到喀撒朗蠻荒與肯‧肯交談。', '所有身強體壯的部落成員都必須前往喀撒朗蠻荒，向祝家守望站報到。\n\n有傳聞指出，喀撒朗蠻荒的熊貓人出現了異常的行為。\n\n到祝家守望站與肯·肯交談。或許他會知道是什麼原因讓這些熊貓人變得無精打采。', '', '', '', '', '', '到喀撒朗蠻荒與肯‧肯交談。', 26365),
(49539, 'zhTW', '大酋長的命令：四風峽！', '到四風峽與老陳·風暴烈酒交談。', '所有身強體壯的部落成員都必須前往四風峽向老陳·風暴烈酒報到。\n\n風暴烈酒家的冒險精神，是值得部落借鑑的地方。\n\n和風暴烈酒家的人同行，探索潘達利亞的秘密。\n\n為了部落！', '', '', '', '', '', '到四風峽與老陳·風暴烈酒交談。', 26365),
(49538, 'zhTW', '大酋長的命令：翠玉林！', '到葛羅瑪許堡向納茲格寧姆將軍報到。', '所有身強體壯的部落成員都必須前往奧格瑪，向納茲格寧姆將軍報到。\n\n我們對近對聯盟的海戰捷報連連，讓大家意志更為堅定。\n\n到葛羅瑪許堡報到，助部落的入侵艦隊一臂之力。\n\n為了部落！', '', '', '', '', '', '', 26365),
(49537, 'zhTW', '大酋長的命令：寒冰皇冠！', '到水晶之歌森林上方的達拉然與空奪者克朗交談。', '所有身強體壯的部落成員都必須前往水晶之歌森林上方的達拉然，向空奪者克朗報到。\n\n戰歌進攻部隊需要更多冒險者，才能一鼓作氣對冰冠城塞發動猛攻，直取阿薩斯。\n\n時候到了，我們要讓巫妖王付出代價！\n\n為了部落！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與空奪者克朗交談。', 26365),
(49536, 'zhTW', '大酋長的命令：風暴群山！', '對水晶之歌森林上方的達拉然與倫‧杜克坦交談。', '所有身強體壯的部落成員都必須前往水晶之歌森林上方的達拉然向倫‧杜克坦報到。\n\nK3的哥布林在風暴群山建了一座城市，還派出他們的大使四處招募冒險者。\n\n找倫‧杜克坦談談，看看K3的人有什麼需要。他們會是我們對付巫妖王的寶貴盟友。\n\n為了部落！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與倫‧杜克坦交談。', 26365),
(49535, 'zhTW', '大酋長的命令：休拉薩盆地！', '到水晶之歌森林上方的達拉然與大法師潘塔魯斯交談。', '所有身強體壯的部落成員都必須前往水晶之歌森林上方的達拉然，向大法師潘塔魯斯報到。\n\n赫米特‧奈辛瓦里，還有他的遠征隊，先前已被派去探索休拉薩盆地，但是他們卻失蹤了。\n\n我們對林木蓊鬱的休拉薩了解不多，但是這座盆地藏有的秘密，或許能讓我們在對抗巫妖王時占到優勢。\n\n為了部落！', '', '', '', '', '', '到水晶之歌森林上方的達拉然與大法師潘塔魯斯交談。', 26365),
(49534, 'zhTW', '大酋長的命令：祖爾德拉克！', '到祖爾德拉克與十字軍領主蘭迪加交談。', '所有身強體壯的部落成員都必須前往祖爾德拉克，向十字軍領主蘭迪加報到。\n\n天譴軍團正在祖爾德拉克殘殺當地的居民，並且把他們轉化成天譴軍麾下的不死怪物。\n\n協助銀白十字軍和黯刃騎士團平息這場威脅。\n\n為了部落！', '', '', '', '', '', '到祖爾德拉克與十字軍領主蘭迪加交談。', 26365),
(49533, 'zhTW', '大酋長的命令：凜風峽灣！', '到凜風峽灣與藥劑師黎珊達交談。', '所有身強體壯的部落成員都必須前往凜風峽灣，向藥劑師黎珊達報到。\n\n被遺忘者在北裂境東岸建立了要塞。\n\n現在正是向天譴軍團進攻的時機，我們除了展現兵力，還有皇家藥劑師學會新研發的病毒。\n\n為了部落！', '', '', '', '', '', '到凜風峽灣與藥劑師黎珊達交談。', 26365),
(49532, 'zhTW', '大酋長的命令：影月谷！', '到外域的影月谷與血衛士高莫克交談。', '所有身強體壯的部落成員都必須前往外域的影月谷，向血衛士高莫克報到。\n\n燃燒軍團想包圍影月村，不過我們的柯爾克隆精英部隊會讓它們瞧瞧膽敢跟部落作對會是什麼下場。\n\n擊潰燃燒軍團。\n\n為了部落！', '', '', '', '', '', '到外域的影月谷與血衛士高莫克交談。', 26365),
(49354, 'zhTW', '勿忘太陽之井', '完成「勿忘太陽之井」事件。', '攝政王想讓首席秘法師薩莉瑟拉看看我們的太陽之井。\n\n不知道他是否願意讓艾蘭里亞‧風行者同行，但是我們有收到邀請。', '', '', '', '', '', '', 26365),
(49266, 'zhTW', '萬世熔爐', '見證光鑄儀式的過程。', '光鑄戰士一直都是對抗燃燒軍團的希望明燈。\n\n不過很少人知道，要成為聖光之怒的化身，需要付出多大的代價。\n\n聖光軍團的這幾位勇士，已經準備好要接受他們最終的試煉。\n\n是否有這個榮幸請你見證這場儀式？', '', '', '', '', '', '', 26365),
(49199, 'zhTW', '大型侵略點：鬼母佛努娜', '', '', '', '', '', '', '', '', 26365),
(49197, 'zhTW', '大型侵略點：梭塔納索', '', '', '', '', '', '', '', '', 26365),
