INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(59931, 'esMX', 'En busca de la expedición perdida', 'Busca sobrevivientes de la expedición en el campamento cercano.', 'Vi fuego hacia el norte. Veremos si es obra de alguien de la expedición.\n\nComo tú eres $gel recluta más nuevo:la recluta más nueva;, deberás quedarte cerca de mí.\n\nVen. Vamos al norte.', '', '', '', '', '', 'Habla con Won\'sa en el Campamento Abandonado.', 38134),
(59929, 'esMX', 'Murlocmanía', 'Recupera $1oa equipos de primeros auxilios de los múrlocs.', 'La costa está llena de naufragios. Aquí debe ser donde chocó la expedición perdida.\n\nNo podemos dar inicio a nuestra búsqueda hasta que nuestros soldados hayan sanado. ¡No hay honor en dejar a los caídos detrás!\n\nLos múrlocs robaron nuestros suministros médicos. Asesina a esas alimañas y recupera nuestros equipos de primeros auxilios.', '', '¡Los múrlocs han robado suministros de primeros auxilios valiosos!', 'Múrlocs', '', '', '', 38134),
(59928, 'esMX', 'Prepárate para el impacto', 'Encuentra a la señora de la guerra Breka.', 'Bien luchado, $n. ¡Eres sinónimo de honor y fuerza para la Horda!\n\nAhora solo tenemos que... Un momento...\n\n¿Oyes eso?', '', '', '', '', '', 'Encuentra a la señora de la guerra Breka.', 38134),
(59927, 'esMX', 'Mantén tu posición', 'Entrena con el bruto Throg.', '¡Lok-tar! No hay nada mejor que una buena lucha bajo la lluvia.\n\n¡Entrenaremos juntos y tomaremos dimensión de tu fuerza!', '', '', '', '', '', '', 38134),
(59926, 'esMX', 'Entrada en calor', 'Entrena con un muñeco de combate para entrar en calor.', 'Nos acercamos a la isla donde se perdió la última expedición de la Horda. \n\nDebes prepararte para lo que acecha allá afuera. Entra en calor con estos muñecos de combate.\n\n¡Muéstrame tu fuerza!', '', '', '', '', '', '', 38134),
(59937, 'esMX', 'Domar lo salvaje', 'Aprende Domesticar bestia de Mithdran Rastrealba. Luego usa Domesticar bestia con cualquier criatura salvaje cercana.', '$gUn cazador:Una cazadora; como tú tiene que tener una buena mascota a su lado. ¿No trajiste una contigo?\n\nMmm. Supongo que tendré que ayudarte con tu falta de entrenamiento.\n\nEscucha con atención y aprende rápido. Te enseñaré a domesticar una bestia y convertirla en tu mascota.\n\nNecesitarás una mascota si vas a luchar contra los jabaespines.', '', '', '', '', '', '', 38134),
(59984, 'esMX', 'Calabozo: Ciudadela Mazoscuro', 'Completa el calabozo \"Ciudadela Mazoscuro\".', 'El objetivo de Gor\'groth es revivir a un poderoso dragón llamado Ravnyr.\n\nHa recogido la cantidad suficiente de esencia de vida para intentar terminar el ritual... aunque es probable que Ravnyr no pueda recuperar toda su fuerza.\n\n¡Gor\'groth solo se detendrá cuando alguien lo asesine!\n\n¡Debes atacar la ciudadela y matar a Gor\'groth antes de que complete el ritual!\n\nSi no estuviera recuperándome de su magia oscura, arremetería contigo en la batalla.', '', '', '', '', '', '', 38134),
(59941, 'esMX', 'Redimensionando la situación', 'Usa el redimensionador sobre $1oa jabalíes merodeadores.', 'A este dispositivo lo bauticé “redimensionador”. Es lo que usé para desencoger el helicóptero propulsor Mk. 5.\n\nMira, voy a serte honesto: apenas entiendo cómo funciona esta cosa.\n\nAsí que voy a apretar muchos botones para ver qué pasa.\n\nSin dudas, descubriremos cómo hacer gigante a uno de estos jabalíes. ¡Y luego lo usaremos para aplastar a esos matones no-muertos!\n\nSostén el redimensionador mientras pruebo. Después presiona el botón y... reza para que ocurra algo bueno.', '', '', '', '', '', 'Habla con la líder bélica Breka', 38134),
(59930, 'esMX', 'Cuidados de emergencia', 'Usa un equipo de primeros auxilios para sanar a Bo, Mithran Rastrealba y Lana Jordan.', 'Tenemos que tratar a los heridos antes de poder comenzar la búsqueda de mi... de la expedición perdida.\n\nToma este equipo de primeros auxilios y ayuda a los sobrevivientes. ¡La Horda no deja a nadie atrás!', '', '', '', '', '', '', 38134);

