DELETE FROM `quest_offer_reward` WHERE `ID` IN (56156 /*-Unknown-*/, 55363 /*-Unknown-*/, 55362 /*-Unknown-*/, 55361 /*-Unknown-*/, 57004 /*-Unknown-*/, 56350 /*-Unknown-*/, 56162 /*-Unknown-*/, 57010 /*-Unknown-*/, 55618 /*-Unknown-*/, 55497 /*-Unknown-*/, 55425 /*-Unknown-*/, 55407 /*-Unknown-*/, 55400 /*-Unknown-*/, 55374 /*-Unknown-*/, 55533 /*-Unknown-*/, 55851 /*-Unknown-*/, 54972 /*-Unknown-*/, 55175 /*-Unknown-*/, 56644 /*-Unknown-*/, 56643 /*-Unknown-*/, 56642 /*-Unknown-*/, 56641 /*-Unknown-*/, 56640 /*-Unknown-*/, 54969 /*-Unknown-*/, 55095 /*-Unknown-*/, 56043 /*-Unknown-*/, 54363 /*-Unknown-*/, 52154 /*-Unknown-*/);
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(56156, 0, 0, 0, 0, 0, 0, 0, 0, 'Fue un honor pelear a tu lado, $n. Si alguna vez me necesitas, solo tienes que pedírmelo.', 38134), -- -Unknown-
(55363, 0, 0, 0, 0, 0, 0, 0, 0, 'Tiemblo al pensar en el destino que me esperaba si no me hubieras rescatado.', 38134), -- -Unknown-
(55362, 0, 0, 0, 0, 0, 0, 0, 0, 'Has actuado bien. Puedo percibir que el equilibrio elemental está cerca de volver a su estado natural.', 38134), -- -Unknown-
(55361, 0, 0, 0, 0, 0, 0, 0, 0, 'Qué bueno verte. Debemos hablar rápidamente. Temo por la seguridad de mi maestro…', 38134), -- -Unknown-
(57004, 1, 0, 0, 0, 0, 0, 0, 0, 'Hay un vínculo entre el océano y lo que descansa bajo las olas.\n\nEl hombre resplandeciente podrá usar las perlas de maná prismáticas para mejorar los objetos bentónicos.\n\nEsto será muy provechoso.', 38134), -- -Unknown-
(56350, 1, 0, 0, 0, 0, 0, 0, 0, '¡La Maretista! Por supuesto...', 38134), -- -Unknown-
(56162, 1, 0, 0, 0, 0, 0, 0, 0, 'Gracias por volver a ayudarnos. Aún queda mucho por hacer.', 38134), -- -Unknown-
(57010, 1, 0, 0, 0, 0, 0, 0, 0, 'Bueno, bueno... Parece que el corazón de Azeroth tiene capacidades nuevas, ¡tal como dijo MADRE!\n\nMantente $gatento:atenta; para encontrar esencias nuevas. Tienen que estar dispersas por todo Azeroth.', 38134), -- -Unknown-
(55618, 1, 0, 0, 0, 0, 0, 0, 0, 'Fuente de energía aceptada. Activación de forja completada.', 38134), -- -Unknown-
(55497, 1, 6, 0, 0, 0, 300, 0, 0, 'Me gusta este amigo tauren que tienes. ¡Esperemos que se mantenga alejado de la corrupción!\n\n¡Bien, hora de ver lo que MADRE puede hacer con esa esencia!', 38134), -- -Unknown-
(55425, 1, 0, 0, 0, 0, 0, 0, 0, 'Ahora que tenemos la esencia, ruego que la tierra encuentre la paz una vez más.', 38134), -- -Unknown-
(55407, 1, 0, 0, 0, 0, 0, 0, 0, '¿Han sanado las fisuras? Bien. Podemos proseguir.', 38134), -- -Unknown-
(55400, 1, 0, 0, 0, 0, 0, 0, 0, 'Esperaba que el viaje no fuese tan… duro para ti como para mí.', 38134), -- -Unknown-
(55374, 1, 0, 0, 0, 0, 0, 0, 0, 'Debemos apresurarnos. Siento que la esencia de los míos me está llamando.', 38134), -- -Unknown-
(55533, 1, 0, 0, 0, 0, 0, 0, 0, 'Análisis completo. Para impedir la aniquilación planetaria, el Corazón de Azeroth debe ser mejorado.\n\nObjetivo: Buscar esencias compatibles. Prioridad: Crítica.', 38134), -- -Unknown-
(55851, 1, 0, 0, 0, 0, 0, 0, 0, '¡Te doy la bienvenida nuevamente! Tenemos mucho que hacer ahora que MADRE terminó sus cálculos.\n\nEmpecemos de inmediato.', 38134), -- -Unknown-
(54972, 1, 0, 0, 0, 0, 0, 0, 0, 'Tenemos muchísima suerte de haber conocido a los ankoanos. Quizás la situación no sea tan terrible como parece.', 38134), -- -Unknown-
(55175, 1, 0, 0, 0, 0, 0, 0, 0, 'Hasta ahora, los Ankoanos han demostrado ser grandes aliados.', 38134), -- -Unknown-
(56644, 1, 0, 0, 0, 0, 0, 0, 0, 'Hemos estado observándolos. Tu gente parece muy capaz.', 38134), -- -Unknown-
(56643, 1, 0, 0, 0, 0, 0, 0, 0, 'Ya me cansé de este lugar.', 38134), -- -Unknown-
(56642, 1, 0, 0, 0, 0, 0, 0, 0, 'El ritual terminó en cuanto matamos al ama de las mareas.', 38134), -- -Unknown-
(56641, 1, 0, 0, 0, 0, 0, 0, 0, 'A veces es muy satisfactorio romper juguetes ajenos.', 38134), -- -Unknown-
(56640, 1, 0, 0, 0, 0, 0, 0, 0, 'Nuestro ejército quedó devastado, pero volveremos a levantarlo.', 38134), -- -Unknown-
(54969, 1, 0, 0, 0, 0, 0, 0, 0, 'Gracias por confiar en mí.', 38134), -- -Unknown-
(55095, 1, 0, 0, 0, 0, 0, 0, 0, 'Por fin, buenas noticias. $GNuestro:Nuestra; adalid se levanta en medio del caos.', 38134), -- -Unknown-
(56043, 1, 1, 0, 0, 0, 0, 0, 0, 'Jamás me habría esperado esto...', 38134), -- -Unknown-
(54363, 1, 0, 0, 0, 0, 0, 0, 0, 'Gracias por tu aporte.', 38134), -- -Unknown-
(52154, 1, 0, 0, 0, 0, 0, 0, 0, 'Estoy agradecido por el conocimiento de Alleria y la predisposición de nuestros nuevos aliados para tomar la delantera. Esta nueva estrategia de dos puntas nos presenta nuevas opciones estratégicas que debemos evaluar con cuidado.\n\nDebo pedirle a Alleria que se quede conmigo para coordinar nuestros esfuerzos en ambos frentes. Necesitaré que acompañes al magíster Umbric para asegurar el éxito de la misión. Buena suerte.', 38134); -- -Unknown-


