INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(60013, 669, 0, 0, 0, 0, 0, 0, 0, 'Debemos apresurarnos. La Arconte debe saber lo que ocurri aqu.', 38134), -- -Unknown-
(60180, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que haya sido una experiencia satisfactoria.', 38134), -- -Unknown-
(58843, 1, 0, 0, 0, 0, 0, 0, 0, 'Es un placer conocerte. Hace tiempo que La Bveda no recibe visitantes.', 38134), -- -Unknown-
(58980, 1, 0, 0, 0, 0, 0, 0, 0, 'Tu bsqueda fue fructfera. Estoy encantada de ver que ests $gintacto:intacta;.', 38134), -- -Unknown-
(58979, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero sepas disculpar mi comportamiento. Senta que no estaba en mis cabales.', 38134), -- -Unknown-
(58978, 0, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos resopla suavemente.>', 38134), -- -Unknown-
(58977, 669, 0, 0, 0, 0, 0, 0, 0, 'Lo logramos. Con un poco de suerte, tendremos sus recuerdos a nuestro alcance.', 38134), -- -Unknown-
(58800, 1, 0, 0, 0, 0, 0, 0, 0, 'Me encanta el sonido que hace el nima. Hace mucho tiempo que no lo escucho en abundancia.', 38134), -- -Unknown-
(58799, 1, 0, 0, 0, 0, 0, 0, 0, 'Excelente. Har un buen uso de su nima.', 38134), -- -Unknown-
(58771, 1, 0, 0, 0, 0, 0, 0, 0, 'Lo encontraste! Y tambin traes otros tomos interesantes. Cuando terminemos, me encargar de registrarlos de forma adecuada.', 38134), -- -Unknown-
(58976, 1, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos gesticula, irritado, por los Rechazados que tiene atrs.>\n\nEsts aqu para solucionar ESTO?', 38134), -- -Unknown-
(57447, 669, 0, 0, 0, 0, 0, 0, 0, 'No tendremos ni un segundo de descanso hoy, verdad?\n\n<Kleia mira el fragmento con recelo.>', 38134), -- -Unknown-
(57269, 669, 669, 0, 0, 0, 0, 0, 0, 'Tu aparicin en este momento, al borde de esta traicin, me resulta curiosa.\n\n<La Dechada de la pureza te estudia con una mirada inquisitiva.>\n\nHiciste un buen trabajo. La Arconte se enterar de tu contribucin.', 38134), -- -Unknown-
(57446, 669, 0, 0, 0, 0, 0, 0, 0, 'Nuestra lucha an no ha terminado, $n.', 38134), -- -Unknown-
(57719, 669, 0, 0, 0, 0, 0, 0, 0, 'Estas palabras Esta voz\n\nDe dnde conozco esta voz?', 38134), -- -Unknown-
(59147, 669, 0, 0, 0, 0, 0, 0, 0, 'Algunas manchas se deben eliminar.', 38134), -- -Unknown-
(57037, 669, 0, 0, 0, 0, 0, 0, 0, 'Su cancin volvi a sonar. Es una alegra para mis odos, pero an se oye la voz ms fuerte. Debemos silenciarla antes de que destruya todo lo que amamos.', 38134), -- -Unknown-
(57444, 669, 0, 0, 0, 0, 0, 0, 0, 'Los vi morir. Se merecan algo peor.', 38134), -- -Unknown-
(57717, 669, 0, 0, 0, 0, 0, 0, 0, '<Eridia sujeta su martillo con ms fuerza, tensa.>\n\nCmo? Por qu son tantos?', 38134), -- -Unknown-
(57716, 669, 0, 0, 0, 0, 0, 0, 0, 'Gracias por traerlo aqu, $n. Est en un peor estado del que cualquiera de nosotros podra haber imaginado.', 38134), -- -Unknown-
(57264, 669, 0, 0, 0, 0, 0, 0, 0, 'Apreciamos tu ayuda. Gracias.', 38134), -- -Unknown-
(57977, 669, 0, 0, 0, 0, 0, 0, 0, 'Has demostrado ser competente en esta tarea. Tienes mi gratitud.', 38134), -- -Unknown-
(57270, 669, 0, 0, 0, 0, 0, 0, 0, 'Gracias por ofrecerte.', 38134), -- -Unknown-
(58174, 669, 0, 0, 0, 0, 0, 0, 0, '<Kleia te sonre con cario.>\n\nEso quiere decir que te quedars?', 38134), -- -Unknown-
(57712, 1, 0, 0, 0, 0, 0, 0, 0, 'Me asegurar de que vuelvan al lugar apropiado para que todos puedan disfrutarlos.\n\nTe doy las gracias por recuperarlos, y espero que te hayan resultado tiles.', 38134), -- -Unknown-
(60222, 669, 0, 0, 0, 0, 0, 0, 0, 'Mereces recibir la gracia de Bastin, $n. Que te sirva bien.', 38134), -- -Unknown-
(57266, 669, 669, 0, 0, 0, 0, 0, 0, 'Este es un momento digno de celebrar, $n.\n\n<Kleia te sonre de oreja a oreja.>\n\nLo hiciste. Vinieron.', 38134), -- -Unknown-
(57291, 669, 0, 0, 0, 0, 0, 0, 0, 'Concntrate. Esta es la manera en que conseguirs hablar con la Arconte. Estoy convencida.', 38134), -- -Unknown-
(57714, 669, 0, 0, 0, 0, 0, 0, 0, 'Esta leccin siempre es dolorosa. Esto no cambia la forma en la que te vemos.', 38134), -- -Unknown-
(57909, 1, 0, 0, 0, 0, 0, 0, 0, 'Enfrentarse a tantos en tan poco tiempo es una demostracin indiscutible de aguante.\n\nMe cans de solo mirarte.\n\n<Pelagos se re.>', 38134), -- -Unknown-
(57288, 669, 0, 0, 0, 0, 0, 0, 0, 'Impresionante. Veo que no me queda mucho por ensearte.', 38134), -- -Unknown-
(57908, 669, 0, 0, 0, 0, 0, 0, 0, 'La gratitud de la forjalita es ms profunda de lo que crees.\n\nBueno. Ahora debes volver a concentrarte en nuestro objetivo.', 38134), -- -Unknown-
(57713, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que te guste. Est lejos de ser mi mejor obra, pero te servir mientras ests aqu.', 38134), -- -Unknown-
(59920, 1, 0, 0, 0, 0, 0, 0, 0, 'Estoy muy agradecida por tu ayuda. Ahora puedo practicar mi oficio otra vez!', 38134), -- -Unknown-
(57267, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah. Huelen bien! Y brillan mucho.\n\nEsto est bien, s.', 38134), -- -Unknown-
(57263, 1, 0, 0, 0, 0, 0, 0, 0, '<Sika toca la sustancia blanda con el dedo.>\n\nBrilla. Eso quiere decir que es nima. La forjalita estar complacida.', 38134), -- -Unknown-
(57265, 1, 0, 0, 0, 0, 0, 0, 0, 'De algn modo... esto es ms y menos de lo que esperaba. Debemos conformarnos.', 38134), -- -Unknown-
(57711, 1, 0, 0, 0, 0, 0, 0, 0, 'Hola otra vez! Qu bueno verte!', 38134), -- -Unknown-
(57710, 669, 0, 0, 0, 0, 0, 0, 0, 'Confieso que estaba esperando algo de espectculo para levantar el nimo de los aspirantes que tengo a cargo.\n\nPero no esperaba que me animaras a m tambin. Gracias por eso.', 38134), -- -Unknown-
(57709, 669, 0, 0, 0, 0, 0, 0, 0, '<La forjalita se toma unos segundos para examinarte y luego asiente.>\n\n$gBienvenido:Bienvenida;. No eres lo que esperaba, pero de todas formas nos alegra que ests aqu.', 38134), -- -Unknown-
(57676, 669, 0, 0, 0, 0, 0, 0, 0, 'Puede que las cosas que nos atan sean violentas. Por ms que nos enseen mucho, no es motivo para dejar que corran libres por ah.', 38134), -- -Unknown-
(57677, 669, 0, 0, 0, 0, 0, 0, 0, 'Estoy en gran deuda contigo, $n.', 38134), -- -Unknown-
(57261, 669, 0, 0, 0, 0, 0, 0, 0, 'Tu viaje apenas ha comenzado, $n. Espero que podamos ayudarnos mutuamente', 38134), -- -Unknown-
(60735, 669, 0, 0, 0, 0, 0, 0, 0, '<Kalisthene te observa atentamente.>\n\nTe hemos escuchado, mortal.', 38134), -- -Unknown-
(57584, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que eso fuera informativo y relajante.', 38134), -- -Unknown-
(57102, 273, 0, 0, 0, 0, 0, 0, 0, '<El centurin emite una suave campanada.>', 38134); -- -Unknown-


