INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('262', '570', 'zhCN', '敏锐', '选择', '军团中最近有一名艾瑞达刺客非常出名——阿卡丽·影血。据说她的战刃能扭曲持有者周围的空气，并将他们隐藏在暗影之中。\n\n我们有情报揭示了她目前的位置。是时候出击了。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('262', '569', 'zhCN', '狂徒', '选择', '亡灵舰长伊丽扎人称南海灾星。她的诅咒之刃曾让无数的敌人心惊胆颤。\n\n我们追踪到她的舰队在破碎群岛的阿苏纳海岸下了锚。你要是早点赶过去，或许能把恐惧之刃弄到手。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('262', '568', 'zhCN', '奇袭', '选择', '弑君者是迦罗娜在为古尔丹做刺客时使用的匕首。这对匕首上浸染着无迹可寻的剧毒，执行残酷的任务时不会留下任何证据。\n\n很久之前，迦罗娜就在痛苦之中将这对利刃藏了起来，但现在，为了对抗危险的燃烧军团信徒，她又一次将它们拣起。\n\n我们必须找到她。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('317', '782', 'zhCN', '', '激活', '激活矩阵核心，选择维迪卡尔的主武器圣光裁决者作为你在阿古斯的能力。', '', '27602');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('317', '776', 'zhCN', '回响遮罩', '选择', '选择后可以在阿古斯使用奥术回响遮罩的隐形能力。', '', '27602');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('317', '774', 'zhCN', '战争机甲', '选择', '选择后可以在阿古斯使用光铸战争机甲。', '', '27602');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('317', '775', 'zhCN', '邪能之心', '选择', '选择阿古斯邪能之心可以让你在阿古斯受到致命伤害后复活，并在非战斗状态下持续为你恢复生命值。', '', '27602');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('317', '771', 'zhCN', '圣光裁决者', '选择', '选择后可以在阿古斯使用维迪卡尔的主武器圣光裁决者瞄准并轰炸目标。', '', '27602');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('235', '490', 'zhCN', '惩戒', '选择', '破碎海滩的失利让我们失去了大领主提里奥·弗丁，还有我们对抗军团最强大的武器……灰烬使者。\n\n我们必须找到灰烬使者，不惜一切代价将它夺回来。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('235', '489', 'zhCN', '防护', '选择', '一个古代维库传说提到了一面由提尔亲自铸造的盾牌。在蔑冬之战中，这面盾牌被用来向维库一族揭露洛肯的谎言，使许多维库加入了提尔的阵营。\n\n这面盾牌至今仍下落不明，但有传言说有人或许知道这个故事的结局。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('235', '488', 'zhCN', '神圣', '选择', '一支秘密的圣骑士组织几百年来都在看守着泰坦监护者提尔的墓穴。据说提尔有一柄强大的战锤就埋在他的墓穴里。\n\n最近的一些事件威胁到了这个秘密组织的安全，他们开始寻求全艾泽拉斯的圣骑士们的帮助。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('236', '493', 'zhCN', '狂怒', '选择', '我亲自挑选的瓦拉加尔，最强大的勇士的战剑，被女巫海拉给偷走了。 她和她那些愚蠢的奴仆滥用神剑的力量，四处窃取伟大英雄们的灵魂，使他们无法升至英灵殿。\n\n这对强大的武器能让你化身剑与火的风暴，是一个不错的选择。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('236', '492', 'zhCN', '防护', '选择', '一名维库古王曾将大地守护者奈萨里奥的一块鳞片铸造为一面强大的盾牌。如今这盾牌和它的主人一起沉睡在了一座被遗忘的墓穴中。\n\n找回这面盾牌不但能使你得到坚不可摧的守护，还能让你获得这头强大的守护巨龙的一部分力量。\n', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('236', '491', 'zhCN', '武器', '选择', '索拉丁是首位人类国王，是强大的统治者和战士。他手中的大剑由维库人铸造，经过了精灵的强化，还在无数的战场上啜饮过鲜血。\n\n斯多姆卡，灭战者与索拉丁一同消失了。若有人能找回这把剑，将可以成就数千年未见的伟业。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('265', '586', 'zhCN', '冰霜', '选择', '这柄传说之杖曾为提瑞斯法最初的守护者阿洛迪所有。在他身为守护者的一百年中，他曾无数次带着这柄法杖深入战场对抗燃烧军团的部队。\n\n在阿洛迪从守护者的位置上退下来后，他和这柄法杖一同消失了。没有人知道这一人一杖的下落——除了他自己。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('265', '585', 'zhCN', '火焰', '选择', '此剑在精灵语中被称为Felo\'melorn，也就是“烈焰之击”的意思。在上古之战、巨魔战争以及对抗死亡骑士阿尔萨斯·米奈希尔时，都为逐日者家族带来了无数的荣誉。\n\n最终，这把剑遗失在了诺森德的冰荒废土中。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('265', '584', 'zhCN', '奥术', '选择', '艾格文是唯一一位女性的提瑞斯法守护者，艾露尼斯正因为被她使用过而出名。不过据说这把武器的年代比艾格文古老得多。\n\n在艾格文的晚年，她将这柄法杖托付给了蓝龙军团。蓝龙们认为此杖过于危险，将它藏在了一处秘密的宝库里，并存放至今。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('253', '543', 'zhCN', '邪恶', '选择', '天启是一把由纳斯雷兹姆所铸造的古剑，它曾散播过无数的瘟疫、死亡和恐慌，乃是究极的毁灭利器。\n\n这把剑曾由提瑞斯秘法会所使用，但后来被黑暗骑士夺走。若想要把它找回来，我们必须前往黑暗骑士的秘密巢穴。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('253', '542', 'zhCN', '冰霜', '选择', '传奇武器霜之哀伤于四年前在冰冠堡被毁，即便如此，它依然还是艾泽拉斯最恶名昭彰的武器之一。\n\n只需要用上一点死灵法术，它的碎片便可被重铸为一对符文剑，其潜力绝不会逊色于霜之哀伤，甚至可能比霜之哀伤更加强大。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('253', '541', 'zhCN', '鲜血', '选择', '裂肉者高瑞里克斯是一名凶残而庞大的恶魔战士。他挥舞着他的巨斧诅咒之喉，为军团屠戮了许多个世界。\n\n要想从高瑞里克斯那里夺取这把强大的武器，你需要只身前往入侵我们世界的军团部队的核心地带。但诅咒之喉中蕴含的力量会让你的冒险得到回报。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('255', '547', 'zhCN', '复仇', '选择', '强大的奥达奇一族是少有的几个能与燃烧军团对抗的种族之一。最后萨格拉斯本人亲手杀死了他们最伟大的战士，并将他的战刃据为己有。\n\n如今，伊利达雷的叛徒凯丽娅投靠了基尔加丹，并成为了这对战刃的新主人。她吸取的灵魂越多，她的力量就越强大。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('255', '546', 'zhCN', '浩劫', '选择', '这对战刃属于曾经的恶魔猎手瓦雷迪斯·邪魂。瓦雷迪斯在许多年前牺牲于黑暗神殿的保卫战中。\n\n燃烧军团之主，欺诈者基尔加丹在扭曲虚空中复活了瓦雷迪斯，并将自己的一部分混乱力量灌输进了他的战刃中。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('247', '545', 'zhCN', '恢复', '选择', '数千年前，艾维娜将母亲之树加尼尔的一根枝条赐给了最初的德鲁伊们。\n\n据说这把法杖可以让持有者与自然达到完美的和谐，能够使用最强大的恢复法术。\n\n这跟枝条被保管在莎拉达希尔下方的祭坛中，但最近有情报显示它和那里的德鲁伊一块失踪了……', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('247', '531', 'zhCN', '守护', '选择', '强大的巨熊之神乌索克在上古之战中为了对抗燃烧军团、保卫艾泽拉斯献出了生命。\n\n他在战斗中使用的令人生畏的巨爪被安放在了翡翠梦境之中。在那里，他的灵魂仍然守卫着自己的利爪。\n\n如果你能够取得他的利爪，就能够让它们在与军团的战斗中发挥最高的价值。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('247', '530', 'zhCN', '野性', '选择', '阿莎曼，这只古老而强大的夜刃豹，曾在上古之战中战斗。很久之前，她便陨落在了瓦尔莎拉的森林深处。\n\n灰烬德鲁伊尊崇着她的记忆，守卫着保存她利齿的神殿。\n\n然而她的利齿在对抗军团的战斗中将会是无比强大的武器。我们需要说服他们，允许让我们在战斗中使用他们宝贵的神器。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('247', '529', 'zhCN', '平衡', '选择', '月神镰刀涉及到德鲁伊教的一段黑暗历史，它一直由艾露恩的高阶女祭司泰兰德所保管。\n\n它与艾泽拉斯的狼人起源直接相关，据说其中蕴含了强大的月光能量，只有深谙平衡之道的德鲁伊才能控制住它。\n\n我们需要说服泰兰德的人民，证明你能够在即将到来的战争中保护它的安全。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('242', '513', 'zhCN', '踏风', '选择', '云游四海的冒险者们回到岛上后流传着一个有趣的故事。他们提到一对古老的拳刃，据说能赐给持有者风暴之力。\n\n我们对这对神秘的兵器知之甚少，但假如它们真有那样的力量，那我们应该试着去寻找一下。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('242', '512', 'zhCN', '织雾', '选择', '很久以前，熊猫人的末代皇帝使用他的法杖“神龙”将潘达利亚笼罩在了迷雾之中。\n\n法杖在永春台保留了数个世代。当军团攻打潘达利亚时，影踪派的守军们立刻前去保护法杖。但他们一去就没了音讯，事态恐怕不妙了——我们绝不能让法杖落入坏人手里！', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('242', '511', 'zhCN', '酒仙', '选择', '我们从传说中了解到，很久之前曾经有一把法杖，是用潘达利亚第一棵树木的枝条制成的。\n\n据说持有这把法杖的人不会被任何武器伤害到。\n\n传说中还说，最后一个拥有这把法杖的人是美猴王。要想找到它，我们应该从美猴王开始求索。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('245', '525', 'zhCN', '毁灭', '选择', '由数百名艾瑞达术士和黑暗泰坦本人铸就，用于执行最可怕的毁灭任务。它能够撕裂真实世界与其他世界之间的间隙，是入侵艾泽拉斯的关键所在。\n\n随后使用它的人是耐奥祖，他不假思索地将这法杖的力量倾泄到了德拉诺，险些将那个世界毁灭。虽然很久以来没人知道它的下落，但现在它又回到了军团的手中。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('245', '524', 'zhCN', '恶魔学识', '选择', '巫师萨奇尔是第一个尝试召唤和控制恶魔的艾瑞达。\n在他被击杀后，他的头骨被公开展示，作为学习黑暗法术的教训。\n然而，他们没吸取这个教训。\n\n在艾瑞达堕落以后，这颗头骨为燃烧军团的将领们所得，并被用来强化他们对自己的军队的控制力。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('245', '523', 'zhCN', '痛苦', '选择', '逆风收割者是最初的通灵师萨泰尔的武器，它抽干了逆风小径所有的生命，并以此得名。\n\n在失踪了数个世纪后，我们最终查明，乌萨勒斯落入了黑暗骑士的手中。你若想找到这件神器，就必须去追踪那些黑暗骑士了。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('248', '534', 'zhCN', '戒律', '选择', '血色十字军对消灭艾泽拉斯亡灵的执着近乎狂热，他们曾试图以法杖的形式打造出第二件灰烬使者级别的神器。 然而，仪式遭到了叛徒的破坏，还引发了剧烈的爆炸。这把受损的法杖释放出的力量几乎无法驾驭。\n\n最后，肯瑞托的精英法师将此杖藏在了安全的地方，以免引发更多的灾难。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('248', '533', 'zhCN', '暗影', '选择', '一把上古之神的匕首最近重现人间，暮光之锤意图利用它来东山再起。他们正用那匕首执行一个邪恶的仪式，我们必须阻止它。\n\n黑暗帝国之刃曾在已被忘却的时代被用于执行和上古之神有关的邪恶仪式。这把仪祭刀能与虚空共鸣，并能使用强大的暗影能量来强化它的主人。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('248', '532', 'zhCN', '神圣', '选择', '为了躲避军团的追捕，德莱尼曾在多个星球上辗转逃难。在其中一颗星球上，一群勇士带着治愈水晶图雷勇敢地对抗燃烧军团，使他们的同胞得以逃脱。\n\n最近有传言说，达拉然中的一名士兵曾在军团手中目击到了这颗水晶。如果消息是真的，我们必须把它夺回来！', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('240', '506', 'zhCN', '射击', '选择', '许多年前，游侠奥蕾莉亚·风行者便是使用风行者家族的家传弓“萨斯多拉”与燃烧军团展开了对抗。如今奥蕾莉亚已下落不明。\n\n假如我们能找到奥蕾莉亚，她的武器便会成为我们对抗恶魔的无价之宝。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('240', '505', 'zhCN', '生存', '选择', '很久以前，一只拥有魔法力量的堕落怪物从高岭部族手里抢走了他们最重要的武器：胡恩·高岭的长矛。\n\n他们希望你能前往风暴峡湾杀死他们的宿敌，夺回那支长矛，并用以对抗燃烧军团。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('240', '504', 'zhCN', '野兽控制', '选择', '在远古时代，曾有一名维库督军偷得了一把灌注有风暴之力的步枪。他使用这件神器的力量征服了破碎群岛上的敌人。\n\n进入这名督军的墓穴，取得这件强大的武器。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('266', '589', 'zhCN', '恢复', '选择', '据说艾萨拉女王曾经拥有一把威力巨大的权杖，能够控制海洋与生命之水。\n\n埃鲁纳克一直在寻找这件神器的下落，他手下的一名萨满在刚找到相关线索后就失踪了。她找到这件神器了吗？这是否意味着传说是真的？若是这件神器能落入一位本领高强的萨满的手里，岂不美哉？', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('266', '588', 'zhCN', '元素', '选择', '角斗士雷加尔知道一把强大武器，它或许能帮助我们对抗军团。\n\n在熊猫人战役后，人们在雷电王座下找到了风暴之神莱登的武器。它们随后由白虎亲自保管，等待有资格继承它的人出现。雷加尔会帮助你证明你有这个资格。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
INSERT INTO `world`.`playerchoice_response_locale` (`ChoiceId`, `ResponseId`, `locale`, `Header`, `Answer`, `Description`, `Confirmation`, `VerifiedBuild`) VALUES ('266', '587', 'zhCN', '增强', '选择', '毁灭之锤落入了大漩涡中。若是让敌人拿走了可就不妙了。\n\n我丈夫古伊尔，也就是你口中的“萨尔”，已进入深岩之洲寻找这件失落的神器。小心——与毁灭之锤一同落入大漩涡的恶魔格斯逊可能还活着。', 'CONFIRM_ARTIFACT_CHOICE', '27843');
